INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 1, 'Alguses lõi Jumal taevad ja maa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 2, 'Ja maa oli tühi ja paljas ja pimedus oli sügavuse peal ja Jumala Vaim hõljus vete kohal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 3, 'Ja Jumal ütles: „Saagu valgus!\" Ja valgus sai.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 4, 'Ja Jumal nägi, et valgus oli hea, ja Jumal lahutas valguse pimedusest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 5, 'Ja Jumal nimetas valguse päevaks ja pimeduse ta nimetas ööks. Siis sai õhtu ja sai hommik, - esimene päev.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 6, 'Ja Jumal ütles: „Saagu laotus vete vahele ja see lahutagu veed vetest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 7, 'Ja nõnda sündis: Jumal tegi laotuse ja lahutas veed, mis laotuse all, vetest, mis laotuse peal olid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 8, 'Ja Jumal nimetas laotuse taevaks. Siis sai õhtu ja sai hommik, - teine päev.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 9, 'Ja Jumal ütles: „Veed kogunegu taeva all ühte paika, et kuiva näha oleks!\" Ja nõnda sündis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 10, 'Ja Jumal nimetas kuiva pinna maaks ja veekogu Ta nimetas mereks. Ja Jumal nägi, et see hea oli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 11, 'Ja Jumal ütles: „Maast tärgaku haljas rohi, seemet kandvad taimed, viljapuud, mille viljas nende seeme on, nende liikide järgi maa peale!\" Ja nõnda sündis:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 12, 'maa laskis võrsuda haljast rohtu, seemet kandvaid taimi nende liikide järgi, ja viljapuid, mille viljas nende seeme on, nende liikide järgi. Ja Jumal nägi, et see hea oli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 13, 'Siis sai õhtu ja sai hommik, - kolmas päev.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 14, 'Ja Jumal ütles: „Saagu valgused taevalaotusse eraldama päeva ööst! Tähistagu need seatud aegu, päevi ja aastaid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 15, 'olgu nad valgusteks taevalaotuses, valgustuseks maale!\" Ja nõnda sündis:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 16, 'Jumal tegi kaks suurt valgust: suurema valguse valitsema päeval ja väiksema valguse valitsema öösel, ning tähed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 17, 'Ja Jumal pani need taevalaotusse, et nad valgustaksid maad');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 18, 'ja valitseksid päeval ja öösel ja eraldaksid valguse pimedusest. Ja Jumal nägi, et see hea oli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 19, 'Siis sai õhtu ja sai hommik, - neljas päev.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 20, 'Ja Jumal ütles: „Vesi kihagu elavaist olendeist, ja maa peal lennaku linnud taevalaotuse poole!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 21, 'Ja Jumal lõi suured mereloomad ja kõiksugu elavad olendid, kellest vesi kihab, nende liikide järgi, ja kõiksugu tiibadega linnud nende liikide järgi. Ja Jumal nägi, et see hea oli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 22, 'Ja Jumal õnnistas neid ja ütles: „Olge viljakad ja teid saagu palju, täitke mere vesi, ja lindusid saagu palju maa peale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 23, 'Siis sai õhtu ja sai hommik, - viies päev.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 24, 'Ja Jumal ütles: „Maa toogu esile elavad olendid nende liikide järgi, lojused ja roomajad ja metselajad nende liikide järgi!\" Ja nõnda sündis:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 25, 'Jumal tegi metselajad nende liikide järgi, ja lojused nende liikide järgi, ja kõik roomajad maa peal nende liikide järgi. Ja Jumal nägi, et see hea oli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 26, 'Ja Jumal ütles: „Tehkem inimesed Oma näo järgi, meie sarnaseks, et nad valitseksid kalade üle meres, lindude üle taeva all, loomade üle ja kogu maa üle ja kõigi roomajate üle, kes maa peal roomavad!\",');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 27, 'Ja Jumal lõi inimese Oma näo järgi, Jumala näo järgi lõi Ta tema, Ta lõi tema meheks ja naiseks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 28, 'Ja Jumal õnnistas neid, ja Jumal ütles neile: „Olge viljakad ja teid saagu palju, täitke maa ja alistage see enestele; ja valitsege kalade üle meres, lindude üle taeva all ja kõigi loomade üle, kes maa peal liiguvad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 29, 'Ja Jumal ütles: „Vaata, Mina annan teile kõik seemet kandvad taimed kogu maal, ja kõik puud, mis kannavad vilja, milles nende seeme on; need olgu teile roaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 30, 'Ja kõigile loomadele maa peal ja kõikidele lindudele taeva all ja kõigile roomajaile maa peal, kelles on elav hing, annan Ma kõiksugu haljast rohtu toiduks!\" Ja nõnda sündis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 31, 'Ja Jumal vaatas kõike, mis Ta oli teinud, ja vaata, see oli väga hea! Siis sai õhtu ja sai hommik, - kuues päev.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 1, 'Nõnda on taevas ja maa ning kõik nende väed valmis saanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 2, 'Ja Jumal lõpetas seitsmendal päeval Oma töö, mis Ta tegi, ja hingas seitsmendal päeval kõigist Oma tegudest, mis Ta oli teinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 3, 'Ja Jumal õnnistas seitsmendat päeva ja pühitses seda, sest Ta oli siis hinganud kõigist Oma tegudest, mis Jumal luues oli teinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 4, 'See on lugu taevaste ja maa sündimisest, kui need loodi. Sel ajal, kui Issand Jumal tegi maa ja taevad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 5, 'kui ainustki väljapõõsast ei olnud veel maa peal ja ainustki väljarohtu ei olnud veel tärganud, sest Issand Jumal ei olnud lasknud vihma sadada maa peale, ja inimest ei olnud põldu harimas,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 6, 'tõusis udu maast ja kastis kogu mullapinda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 7, 'Ja Issand Jumal valmistas inimese, kes põrm on, mullast, ja puhus tema ninasse elavat õhku: nõnda sai inimene elavaks hingeks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 8, 'Ja Issand Jumal istutas Eedeni rohuaia päevatõusu poole ja pani sinna inimese, kelle Ta oli valmistanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 9, 'Ja Issand Jumal laskis maast tõusta kõiksugu puid, mis armsad olid pealtnäha ja millest hea oli süüa, ja elupuu keset aeda, ning hea ja kurja tundmise puu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 10, 'Ja Eedenist väljus jõgi rohuaeda kastma, sealtpeale jagunedes neljaks haruks:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 11, 'esimese nimi on Piison, see voolab ümber kogu Havilamaa, kus kulda on;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 12, 'selle maa kuld on hea, seal on bedolavaiku ja karneoolikive.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 13, 'Ja teise jõe nimi on Giihon, see voolab ümber kogu Kuusimaa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 14, 'Ja kolmanda jõe nimi on Hiddekel, see voolab hommiku pool Assurit; ja neljas jõgi on Frat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 15, 'Ja Issand Jumal võttis inimese ja pani ta Eedeni aeda harima ja hoidma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 16, 'Ja Issand Jumal keelas inimest ja ütles: „Kõigist aia puudest sa võid küll süüa,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 17, 'aga hea ja kurja tundmise puust sa ei tohi süüa, sest päeval, mil sa sellest sööd, pead sa surma surema!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 18, 'Ja Issand Jumal ütles: „Inimesel ei ole hea üksi olla; Ma tahan teha temale abi, kes tema kohane on!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 19, 'Ja Issand Jumal valmistas mullast kõik loomad väljal ja kõik linnud taeva all ning tõi inimese juurde, et näha, kuidas tema neid nimetab. Ja kuidas inimene igat elavat olendit nimetas, nõnda pidi selle nimi olema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 20, 'Ja inimene pani nimed kõigile lojustele ja lindudele taeva all ja kõigile loomadele väljal, aga inimesele ei leidunud abi, kes tema kohane oleks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 21, 'Siis Issand Jumal laskis tulla raske une inimese peale ja see uinus magama; siis Ta võttis ühe tema küljeluudest ning sulges selle aseme taas lihaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 22, 'Ja Issand Jumal ehitas küljeluu, mille Ta inimesest oli võtnud, naiseks ja tõi tema Aadama juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 23, 'Ja Aadam ütles: „See on nüüd luu minu luust ja liha minu lihast! Teda peab hüütama mehe naiseks, sest ta on mehest võetud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 24, 'Seepärast mees jätab maha oma isa ja ema ning hoiab oma naise poole, ja nemad on üks liha!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 25, 'Ja nad olid mõlemad alasti, Aadam ja tema naine, ega häbenenud mitte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 1, 'Aga madu oli kavalam kõigist loomadest väljal, kelle Issand Jumal oli teinud, ja ta ütles naisele: „Kas Jumal on tõesti öelnud, et te ei tohi süüa mitte ühestki rohuaia puust?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 2, 'Ja naine vastas maole: „Me sööme küll rohuaia puude vilja,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 3, 'aga selle puu viljast, mis keset aeda, on Jumal öelnud: Te ei tohi sellest süüa ega selle külge puutuda, et te ei sureks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 4, 'Ja madu ütles naisele: „Te ei sure kummatigi mitte,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 5, 'vaid Jumal teab, et mil päeval te sellest sööte, lähevad teie silmad lahti ja te saate Jumala sarnaseks, tundes head ja kurja!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 6, 'Ja naine nägi, et puust oli hea süüa, ja see tegi ta silmadele himu, ja et puu oli ihaldusväärne, sest see pidi targaks tegema. Siis ta võttis selle viljast ja sõi ning andis ühtlasi ka oma mehele, ja tema sõi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 7, 'Siis nende mõlemate silmad läksid lahti ja nad tundsid endid alasti olevat, ja nad õmblesid viigilehti kokku ning tegid enestele põlled.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 8, 'Ja nad kuulsid Issanda Jumala häält, kes rohuaias sinna ja tänna käis, kui päev viluks läks, ja Aadam ja tema naine peitsid endid Issanda Jumala palge eest rohuaia puude keskele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 9, 'Ja Issand Jumal hüüdis Aadamat ning ütles temale: „Kus sa oled?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 10, 'Ja tema vastas: „Ma kuulsin Su häält rohuaias ja kartsin, sest ma olen alasti. Sellepärast ma peitsin enese ära!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 11, 'Siis Ta küsis: „Kes on sulle teada andnud, et sa alasti oled? Või oled sa söönud puust, millest Ma sind keelasin söömast?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 12, 'Ja Aadam vastas: „Naine, kelle Sa mulle kaasaks andsid, tema andis mulle puust ja ma sõin!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 13, 'Ja Issand Jumal küsis naiselt: „Miks sa seda tegid?\" Ja naine vastas: „Madu pettis mind, ja ma sõin!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 14, 'Siis Issand Jumal ütles maole: „Et sa seda tegid, siis ole sa neetud kõigi lojuste ja kõigi väljaloomade seas! Sa pead roomama oma kõhu peal ja põrmu sööma kogu eluaja!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 15, 'Ja Ma tõstan vihavaenu sinu ja naise vahele, sinu seemne ja tema seemne vahele, kes purustab su pea, aga kelle kanda sa salvad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 16, 'Naisele Ta ütles: „Sinule Ma saadan väga palju valu, kui sa käima peal oled: sa pead valuga lapsi ilmale tooma! Sa himustad küll oma meest, aga tema valitseb su üle!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 17, 'Aga Aadamale Ta ütles: „Et sa kuulsid oma naise sõna ja sõid puust, millest Mina sind keelasin, öeldes, et sa ei tohi sellest süüa, siis olgu maapind neetud sinu üleastumise pärast! Vaevaga pead sa sellest sööma kogu eluaja!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 18, 'Ta peab sulle kasvatama kibuvitsu ja ohakaid, ja põllutaimed olgu sulle toiduks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 19, 'Oma palge higis pead sa leiba sööma, kuni sa jälle mullaks saad, sest sellest sa oled võetud! Tõesti, sa oled põrm ja pead jälle põrmuks saama!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 20, 'Ja Aadam pani oma naisele nimeks Eeva, sest ta sai kõigi elavate emaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 21, 'Ja Issand Jumal tegi Aadamale ja ta naisele nahkriided ning pani neile selga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 22, 'Ja Issand Jumal ütles: „Vaata, inimene on saanud nagu üheks meie hulgast, tundes head ja kurja! Aga nüüd, et ta oma kätt ei sirutaks ega võtaks ka elupuust ja ei sööks ega elaks igavesti\", -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 23, 'siis saatis Issand Jumal tema Eedeni rohuaiast välja, et ta hariks maad, millest ta oli võetud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 24, 'Ja Ta ajas Aadama välja ja pani hommiku poole Eedeni rohuaeda keerubid ja tuleleegina sähviva mõõga, et need valvaksid elupuu teed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 1, 'Ja Aadam sai ühte oma naise Eevaga, kes sai käima peale ja tõi Kaini ilmale ning ütles: „Ma olen Issanda abiga mehe ilmale toonud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 2, 'Ja tema sünnitas taas: ta venna Aabeli. Ja Aabel oli pudulojuste karjane, Kain aga oli põllumees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 3, 'Ja mõne aja pärast juhtus, et Kain tõi Issandale roaohvri maaviljast,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 4, 'ja ka Aabel tõi oma pudulojuste esimesest soost ning nende rasvast, ja Issand vaatas Aabeli ja tema roaohvri peale,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 5, 'aga Kaini ja tema roaohvri peale ta ei vaadanud. Siis Kain vihastus väga ja lõi pilgu maha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 6, 'Ja Issand küsis Kainilt: „Mispärast sa vihastud? Ja mispärast sa pilgu maha lööd?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 7, 'Eks ole: kui sa head teed, siis on su pilk tõstetud üles? Aga kui sa head ei tee, siis luurab patt ukse ees ja himustab sind! Kuid sina pead tema üle valitsema!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 8, 'Ja Kain kõneles oma venna Aabeliga. Ja sündis, kui nad väljal olid, et Kain kippus oma venna Aabeli kallale ja tappis tema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 9, 'Aga Issand küsis Kainilt: „Kus on su vend Aabel?\" Ja tema vastas: „Ei mina tea. Kas ma olen oma venna hoidja?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 10, 'Ja Tema ütles: „Mis sa oled teinud? Sinu venna vere hääl kisendab maa pealt Minu poole!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 11, 'Aga nüüd ole sa neetud siit maa pealt, mis oma suu on avanud, su venna verd sinu käest vastu võttes!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 12, 'Kui sa harid maad, siis see ei anna sulle enam oma rammu! Sa pead maa peal olema hulkur ja põgenik!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 13, 'Ja Kain ütles Issandale: „Mu karistus on suurem kui ma suudan kanda!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 14, 'Vaata Sa oled mind täna ära ajanud siit maalt ja ma pean varjule minema Su palge eest ning maa peal olema hulkur ja põgenik! Ja igaüks, kes mind leiab, tapab mu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 15, 'Ja Issand ütles temale: „Sellepärast peab igaühele, kes Kaini tapab, seitsmekordselt kätte makstama!\" Ja Issand pani Kainile märgi, et leidja teda maha ei lööks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 16, 'Ja Kain läks ära Issanda palge eest ning elas Noodimaal, hommiku pool Eedenit.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 17, 'Ja Kain sai ühte oma naisega, kes sai käima peale ja tõi ilmale Hanoki; ja ta ehitas ühe linna ning nimetas selle linna oma poja Hanoki nime järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 18, 'Ja Hanokile sündis Iirad, Iiradile sündis Mehuujael, Mehuujaelile sündis Metuusael, Metuusaelile sündis Lemek.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 19, 'Ja Lemek võttis enesele kaks naist: ühe nimi oli Aada ja teise nimi oli Silla.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 20, 'Ja Aada tõi ilmale Jaabali, kes sai nende isaks, kes elasid telkides ja kasvatasid karja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 21, 'Ja tema venna nimi oli Juubal, kes sai kõigi kandlelööjate ja vilepuhujate isaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 22, 'Ja Silla tõi ilmale Tuubal-Kaini, kes õpetas sepist taguma kõiki, kes tegid vask- ja raudsepa tööd; ja Tuubal-Kaini õde oli Naama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 23, 'Ja Lemek ütles oma naistele: „Aada ja Silla, kuulge mu häält, te Lemeki naised, pange tähele mu kõnet! Jah, haava pärast ma tapan mehe ja verme pärast nooruki!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 24, 'Kui Kaini pärast makstakse kätte seitse korda, siis Lemeki pärast seitsekümmend seitse korda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 25, 'Ja Aadam sai taas ühte oma naisega, kes tõi ilmale poja ja pani temale nimeks Sett, öeldes: „Jumal andis mulle teise järeltulija Aabeli asemele, kuna Kain ta tappis.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 26, 'Ja Setile sündis ka poeg, ja ta pani temale nimeks Enos. Sel ajal hakati Issand nime appi hüüdma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 1, 'See on Aadama sünniraamat. Sel päeval, mil Jumal inimese lõi, tegi Ta tema Jumala sarnaseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 2, 'Ta lõi tema meheks ja naiseks, ja Ta õnnistas neid ning andis neile loomisepäeval nime: inimene!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 3, 'Kui Aadam oli elanud sada kolmkümmend aastat, siis sündis temale poeg, kes oli tema sarnane, tema nägu, ja ta pani sellele nimeks Sett.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 4, 'Ja Aadama elupäevi oli pärast Seti sündimist kaheksasada aastat, ja temale sündis poegi ja tütreid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 5, 'Nõnda oli kõiki Aadama elupäevi, mis ta elas, üheksasada kolmkümmend aastat; siis ta suri.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 6, 'Kui Sett oli elanud sada viis aastat, siis sündis temale Enos.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 7, 'Ja Sett elas pärast Enosi sündimist kaheksasada seitse aastat, ja temale sündis poegi ja tütreid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 8, 'Nõnda oli kõiki Seti elupäevi üheksasada kaksteist aastat; siis ta suri.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 9, 'Kui Enos oli elanud üheksakümmend aastat, siis sündis temale Keenan.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 10, 'Ja Enos elas pärast Keenani sündimist kaheksasada viisteist aastat, ja temale sündis poegi ja tütreid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 11, 'Nõnda oli kõiki Enosi elupäevi üheksasada viis aastat; siis ta suri.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 12, 'Kui Keenan oli elanud seitsekümmend aastat, siis sündis temale Mahalalel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 13, 'Ja Keenan elas pärast Mahalaleli sündimist kaheksasada nelikümmend aastat, ja temale sündis poegi ja tütreid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 14, 'Nõnda oli kõiki Keenani elupäevi üheksasada kümme aastat; siis ta suri.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 15, 'Kui Mahalalel oli elanud kuuskümmend viis aastat, siis sündis temale Jered.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 16, 'Ja Mahalalel elas pärast Jeredi sündimist kaheksasada kolmkümmend aastat, ja temale sündis poegi ja tütreid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 17, 'Nõnda oli kõiki Mahalaleli elupäevi kaheksasada üheksakümmend viis aastat; siis ta suri.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 18, 'Kui Jered oli elanud sada kuuskümmend kaks aastat, siis sündis temale Eenok.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 19, 'Ja Jered elas pärast Eenoki sündimist kaheksasada aastat, ja temale sündis poegi ja tütreid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 20, 'Nõnda oli kõiki Jeredi elupäevi üheksasada kuuskümmend kaks aastat; siis ta suri.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 21, 'Kui Eenok oli elanud kuuskümmend viis aastat, siis sündis temale Metuusala.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 22, 'Ja Eenok kõndis pärast Metuusala sündimist ühes Jumalaga kolmsada aastat, ja temale sündis poegi ja tütreid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 23, 'Nõnda oli kõiki Eenoki elupäevi kolmsada kuuskümmend viis aastat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 24, 'Eenok kõndis ühes Jumalaga, ja siis ei olnud teda enam, sest Jumal võttis tema ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 25, 'Kui Metuusala oli elanud sada kaheksakümmend seitse aastat, siis sündis temale Lemek.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 26, 'Ja Metuusala elas pärast Lemeki sündimist seitsesada kaheksakümmend kaks aastat, ja temale sündis poegi ja tütreid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 27, 'Nõnda oli kõiki Metuusala elupäevi üheksasada kuuskümmend üheksa aastat; siis ta suri.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 28, 'Kui Lemek oli elanud sada kaheksakümmend kaks aastat, siis sündis temale poeg');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 29, 'ja ta pani sellele nimeks Noa ning ütles: „See trööstib meid meie töös ja käte vaevas maa pärast, mille Issand on neednud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 30, 'Ja Lemek elas pärast Noa sündimist viissada üheksakümmend viis aastat, ja temale sündis poegi ja tütreid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 31, 'Nõnda oli kõiki Lemeki elupäevi seitsesada seitsekümmend seitse aastat; siis ta suri.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 32, 'Ja kui Noa oli viissada aastat vana, siis sündisid Noale Seem, Haam ja Jaafet.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 1, 'Ja kui inimesi hakkas maa peal palju saama ja neile sündis tütreid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 2, '.siis nägid Jumala pojad, et inimeste tütred olid ilusad, ja nad võtsid enestele naisi kõigist neist, keda nad välja valisid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 3, '.Aga Issand ütles: „Minu Vaim ei pea igavesti jääma inimesse, sest ta on ikkagi ainult liha. Olgu ta elupäevi sada kakskümmend aastat!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 4, 'Sel ajal, ja veel pärastpoolegi, kui Jumala pojad heitsid inimeste tütarde juurde ja need neile lapsi ilmale tõid, olid hiiglased maa peal: needsamad vägimehed, kes muistsest ajast on kuulsad mehed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 5, '.Kui Issand nägi, et inimese kurjus maa peal oli suur ja kõik Ta südame mõtlemised iga päev üksnes kurjad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 6, 'siis Issand kahetses, et ta inimese oli teinud maa peale, ja Ta süda valutas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 7, 'Ja Issand ütles: „Ma tahan inimese, kelle Ma olen loonud, maa pealt kaotada, niihästi inimesed kui loomad ja roomajad ja taeva linnud, sest Ma kahetsen, et Ma nad olen teinud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 8, 'Aga Noa leidis armu Issanda silmis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 9, 'See on jutustus Noa soost: Noa oli üks õige mees, täiesti vaga oma rahvapõlve seas; Noa kõndis ühes Jumalaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 10, 'Ja Noale sündis kolm poega - Seem, Haam ja Jaafet.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 11, 'Aga maa oli Jumala palge ees raisku läinud ja vägivald täitis maad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 12, 'Ja Jumal vaatas maad, ja näe, see oli raisku läinud, sest kõik liha maa peal oli oma eluviisides raiskunud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 13, 'Ja Jumal ütles Noale: „Ma olen otsustanud teha lõpu kõigele lihale, sest maa on täis nende vägivalda, ja seepärast, vaata, Ma hävitan nad ühes maaga!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 14, 'Tee enesele laev goferipuust; laev tee kambritega ja pigita seda seest ning väljast maapigiga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 15, 'Ja tee see nõndaviisi: laeva pikkus olgu kolmsada küünart, laius viiskümmend küünart ja kõrgus kolmkümmend küünart;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 16, 'valmista see küünramõõdu järgi; tee laevale katus peale ja tee uks laeva küljesse; tee sellele alumine, keskmine ja ülemine lagi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 17, 'Sest vaata, Ma saadan veeuputuse maa peale ja hävitan taeva alt kõik liha, kus eluvaim sees on; kõik mis maa peal on, peab hinge heitma!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 18, 'Aga sinuga Ma teen lepingu ja sina pead minema laeva, sina ja su pojad, su naine ja su poegade naised sinuga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 19, 'Ja sa pead viima laeva kõigist elavaist olendeist, kõigest lihast, igast liigist kaks, et nad koos sinuga jääksid elama: need olgu isane ja emane.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 20, 'Lindudest nende liikide järgi ja loomadest nende liikide järgi, kõigist roomajaist maa peal nende liikide järgi, kõigist peavad kaks tulema sinu juurde, et nad võiksid jääda elama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 21, 'Ja sina võta enesele kõigest roast, mida süüakse, ja kogu enese juurde, et see oleks toiduks sinule ja neile.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 22, 'Ja Noa tegi kõik. Nõnda nagu Jumal teda käskis, nõnda ta tegi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 1, 'Ja Issand ütles Noale: „Mine sina ja kogu su pere laeva, sest Ma olen näinud, et sa selle rahvapõlve seas Minu ees õige oled.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 2, 'Võta enesele kõigist puhtaist loomadest seitse paari, isane ja emane; ja loomadest, kes puhtad ei ole, kaks - isane ja emane.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 3, 'Nõndasamuti lindudest taeva all seitse paari, isane ja emane, et nende sugu jääks elama kogu maa peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 4, 'Sest juba seitsme päeva pärast Ma lasen vihma sadada maa peale nelikümmend päeva ja nelikümmend ööd, ja Ma kaotan maapinnalt kõik olendid, keda Ma olen teinud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 5, 'Ja Noa tegi kõik nõnda, nagu Issand teda käskis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 6, 'Ja Noa oli kuussada aastat vana, kui uputusevesi maa peale tuli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 7, 'Ja Noa läks laeva, ja ta pojad ja ta naine ja ta poegade naised temaga, veeuputuse eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 8, 'Puhtaist loomadest ja loomadest, kes puhtad ei ole, ja lindudest ja kõigist, kes maa peal roomavad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 9, 'tulid kahekesi Noa juurde laeva isane ja emane, nõnda nagu Jumal Noale oli käsu andnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 10, 'Ja seitsme päeva pärast tuli veeuputus maa peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 11, 'Sel aastal, mil Noa kuussada aastat vanaks sai, teise kuu seitsmeteistkümnendal päeval, otse selsamal päeval puhkesid kõik suure sügavuse allikad ja taeva luugid tehti lahti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 12, 'Ja sadu tuli maa peale nelikümmend päeva ja nelikümmend ööd.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 13, 'Otse selsamal päeval läksid Noa ja Noa pojad Seem ja Haam ja Jaafet ning Noa naine ja kolm ta poegade naist üheskoos laeva,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 14, 'nemad ja kõik loomad oma liikide järgi, ja kõiksugu lojused oma liikide järgi, ja kõiksugu roomajad, kes maa peal roomavad, oma liikide järgi, ja kõiksugu lendajad oma liikide järgi, kõik linnud, kõik tiivulised.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 15, 'Ja need tulid Noa juurde laeva kahekaupa kõigest lihast, kus eluvaim sees on.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 16, 'Ja need, kes sisse läksid, olid isane ja emane kõigest lihast, nõnda nagu Jumal temale oli käsu andnud. Ja Issand sulges tema tagant.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 17, 'Siis tuli nelikümmend päeva veeuputust maa peale; vesi tõusis ja tõstis laeva, nõnda et see kerkis kõrgele maast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 18, 'Ja vesi võttis võimust ning seda sai maa peal väga palju, ja laev liikus veepinnal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 19, 'Ja vesi võttis maa peal üpris väga võimust ja kõik kõrged mäed kogu taeva all kaeti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 20, 'Vesi tõusis neist viisteist küünart kõrgemale, nõnda et mäed olid kaetud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 21, 'Siis heitis hinge kõik liha, mis maa peal liikus, niihästi linnud kui lojused ja metselajad ja kõik roomajad, kes maa peal roomasid, ja kõik inimesed ka.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 22, 'Kõik, kellel eluvaimu hingus ninas oli, kõik kes olid kuival maal, need surid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 23, 'Nõnda hävitati kõik olendid, kes maa peal olid; niihästi inimesed kui loomad ja roomajad ja linnud taeva all hävitati maa pealt, ja jäid järele ainult Noa ja need, kes temaga laevas olid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 24, 'Ja vesi võimutses maa peal sada viiskümmend päeva.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 1, 'Siis Jumal mõtles Noale ja kõigile metselajaile ja kõigile lojustele, kes temaga laevas olid; ja Jumal laskis tuult puhuda üle maa ja vesi alanes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 2, 'Ja sügavuse allikad ja taevaluugid suleti, ja sadu taevast keelati.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 3, 'Ja vesi taganes maa pealt, taganes üha, ja saja viiekümne päeva pärast oli vesi vähenenud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 4, 'Ja seitsmenda kuu seitsmeteistkümnendal päeval peatus laev Ararati mägede kohal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 5, 'Ja vesi vähenes üha kümnenda kuuni; kümnenda kuu esimesel päeval paistsid mägede tipud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 6, 'Ja kui nelikümmend päeva oli möödunud, siis Noa avas laeva akna, mille ta oli teinud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 7, 'ja laskis välja ühe kaarna; see lendas sinna ja tänna, kuni vesi maa pealt oli kuivanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 8, 'Siis ta laskis enese juurest välja ühe tuvi, et näha, kas vesi on maa pealt kahanenud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 9, 'Aga tuvi ei leidnud oma jalavarvastele puhkepaika ja tuli tagasi tema juurde laeva, sest vesi oli veel kogu maa peal; siis ta pistis oma käe välja ja võttis tema ning pani enese juurde laeva.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 10, 'Ja ta ootas veel teist seitse päeva ning laskis taas ühe tuvi laevast välja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 11, 'Ja õhtul tuli tuvi tema juurde, ja vaata, tal oli nokas haljas õlipuu leht! Siis Noa mõistis, et vesi oli maa pealt kahanenud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 12, 'Ja ta ootas veel teist seitse päeva ning laskis ühe tuvi välja, aga see ei tulnud enam tagasi tema juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 13, 'Ja kuuesaja esimesel aastal, esimese kuu esimesel päeval, oli vesi maa pealt kuivanud. Ja Noa võttis ära laeva katuse ja vaatas, ja ennäe, maapind oli tahenenud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 14, 'Ja teise kuu kahekümne seitsmendal päeval oli maa täiesti kuiv.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 15, 'Ja Jumal kõneles Noaga ning ütles:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 16, '„Mine laevast välja, sina ja su naine ja su pojad ja su poegade naised ühes sinuga!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 17, 'Kõik loomad, kes su juures on, kõik liha, niihästi linnud kui lojused, ja kõik roomajad, kes maa peal roomavad, vii enesega ühes välja, et nad sigineksid maa peal, oleksid viljakad ja et neid maa peale saaks palju!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 18, 'Ja Noa läks välja ja ta pojad ja ta naine ja ta poegade naised ühes temaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 19, 'Kõik loomad, kõik roomajad ja kõik linnud, kes liiguvad maa peal, läksid laevast välja sugukondade kaupa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 20, 'Ja Noa ehitas Issandale altari ja võttis kõigist puhtaist loomadest ja kõigist puhtaist lindudest ning ohverdas altaril põletusohvreid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 21, 'Ja Issand tundis meeldivat lõhna ja Issand mõtles Oma südames: „Ma ei nea enam maad inimese pärast, sest inimese südame mõtlemised on kurjad ta lapsepõlvest peale; Ma ei hävita ka enam kõike, mis elab, nõnda nagu Ma olen teinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 22, 'Niikaua kui püsib maa, ei lõpe külv ega lõikus, külm ega kuum, suvi ega talv, päev ega öö!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 1, 'Ja Jumal õnnistas Noad ja tema poegi ning ütles neile: „Olge viljakad, teid saagu palju ja täitke maa!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 2, 'Teid peavad kartma ja pelgama kõik maa loomad ja kõik taeva linnud, kõik, kes maa peal liiguvad, ja kõik mere kalad; need on teie kätte antud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 3, 'Kõik, mis liigub ja elab, olgu teile roaks; kõik selle annan Ma teile nagu halja rohugi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 4, 'Kummatigi ei tohi te liha süüa ta hingega, see on: ta verega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 5, 'Tõepoolest, teie eneste verd Ma nõuan taga: Ma nõuan seda kõigilt loomadelt, ja Ma nõuan inimestelt vastastikku inimese hinge!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 6, 'Kes valab inimese vere, selle vere valab inimene, sest inimene on tehtud Jumala näo järgi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 7, 'Ja teie olge viljakad, teid saagu palju, siginege maa peal ja paljunege seal!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 8, 'Ja Jumal rääkis Noa ja tema poegadega, kes ta juures olid, ning ütles:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 9, '„Mina, vaata, teen lepingu teiega ja teie järglastega pärast teid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 10, 'ja iga elava hingega, kes teie juures on: lindudega, lojustega ja kõigi metselajatega, kes teie juures on, kõigiga, kes laevast välja tulid, kõigi maa loomadega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 11, 'Ma teen teiega lepingu, et enam ei hävitata kõike liha uputuse veega ja veeuputus ei tule enam maad rikkuma!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 12, 'Ja Jumal ütles: „Lepingu tähis, mille Ma teen Enese ja teie ja iga teie juures oleva elava hinge vahel igavesteks põlvedeks, on see:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 13, 'Ma panen pilvedesse Oma vikerkaare ja see on lepingu tähiseks Minu ja maa vahel!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 14, 'Kui Ma kogun maa kohale pilvi ja pilvedes nähakse vikerkaart,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 15, 'siis Ma mõtlen Oma lepingule, mis on Minu ja teie ja iga elava hinge vahel kõiges lihas, ja vesi ei saa enam kõike liha hävitavaks uputuseks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 16, 'Kui pilvedes on vikerkaar, siis Ma vaatan seda ja mõtlen igavesele lepingule Jumala ja iga elava hinge vahel kõiges maa peal olevas lihas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 17, 'Ja Jumal ütles Noale: „See on selle lepingu tähis, mille Ma olen teinud enese ja kõige liha vahel, mis maa peal on.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 18, 'Ja Noa pojad, kes laevast välja tulid, olid Seem, Haam ja Jaafet; ja Haam oli Kaanani isa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 19, 'Need kolm olid Noa pojad ja neist rahvastus kogu maa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 20, 'Noa hakkas põllumeheks ja istutas viinamäe.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 21, 'Ta jõi viina, jäi joobnuks ja ajas oma telgis enese paljaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 22, 'Aga Haam, Kaanani isa, nägi oma isa paljast ihu ja rääkis sellest oma kahele vennale õues.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 23, 'Siis Seem ja Jaafet võtsid vaiba ja panid enestele õlgadele, läksid tagurpidi ja katsid kinni oma isa palja ihu; nende näod olid ära pööratud, nõnda et nad oma isa paljast ihu ei näinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 24, 'Kui Noa ärkas oma viinauimast ja sai teada, mis ta noorem poeg oli teinud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 25, 'siis ta ütles: „Neetud olgu Kaanan, saagu ta oma vendadele sulaste sulaseks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 26, 'Ta ütles veel: „Kiidetud olgu Issand, Seemi Jumal, ja Kaanan olgu tema sulane!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 27, 'Jumal andku avarust Jaafetile, ta elagu Seemi telkides ja Kaanan olgu tema sulane!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 28, 'Ja Noa elas pärast veeuputust kolmsada viiskümmend aastat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 29, 'Ja kõiki Noa elupäevi oli üheksasada viiskümmend aastat; siis ta suri.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 1, 'Ja need on Noa poegade Seemi, Haami ja Jaafeti järeltulijad; pärast veeuputust sündisid neile pojad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 2, 'Jaafeti pojad olid Gomer, Maagoog, Maadai, Jaavan, Tubal, Mesek ja Tiiras.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 3, 'Ja Gomeri pojad olid Askenas, Riifat ja Toogarma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 4, 'Ja Jaavani pojad olid Eliisa ja Tarsis, kitiidid ja rodanlased;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 5, 'neist eraldusid saarte rahvad. Need olid Jaafeti järeltulijad nende maade järgi, igaühel oma keel, nende suguvõsade kaupa, vastavalt neile rahvastele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 6, 'Ja Haami pojad olid Kuus, Mitsraim, Puut ja Kaanan.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 7, 'Ja Kuusi pojad olid Seba, Havila, Sabta, Raema ja Sabteka; ja Raema pojad olid Seeba ja Dedan.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 8, 'Ja Kuusile sündis Nimrod, kes oli esimene vägev mees maa peal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 9, 'Tema oli vägev kütt Issand ees, seepärast öeldakse: „Vägev kütt Issanda ees nagu Nimrod!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 10, 'Ja tema kuningriigi alguseks olid Paabel, Erek, Akkad ja Kalne Sinearimaal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 11, 'Sellelt maalt ta väljus Assurisse ja ehitas Niinive, Rehobot-Iiri ja Kelahi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 12, 'ja Reseni Niinive ja Kelahi vahele - see on see suur linn.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 13, 'Ja Mitsraimile sündisid luudlased, anamlased, lehablased, naftuhlased,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 14, 'patruuslased ja kasluhlased, kellest vilistid ja kaftoorlased on lähtunud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 15, 'Ja Kaananile sündisid Siidon, tema esmasündinu, ja Heet,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 16, 'jebuuslased, emorlased, girgaaslased,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 17, 'hiivlased, arklased, siinlased,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 18, 'arvadlased, semarlased ja hamatlased; hiljem harunesid kaananlaste suguvõsad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 19, 'Ja kaananlaste maa-ala oli Siidonist Gerari suunas Assani, Soodoma, Gomorra, Adma ja Seboimi suunas Lesani.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 20, 'Need olid Haami järeltulijad nende suguvõsade, keelte, maade ja rahvaste järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 21, 'Ja Seemile sündisid ka pojad; tema oli kõigi Eeberi poegade esiisa, Jaafeti vanem vend.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 22, 'Seemi pojad olid Eelam, Assur, Arpaksad, Luud ja Aram.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 23, 'Ja Arami pojad olid Uuts, Huul, Geter ja Maas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 24, 'Ja Arpaksadile sündis Selah, Selahile sündis Eeber.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 25, 'Ja Eeberile sündis kaks poega: ühe nimi oli Peleg, sest tema päevil jagunes maa; ja tema venna nimi oli Joktan.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 26, 'Ja Joktanile sündisid Almodad, Selef, Hasarmavet, Jaarah,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 27, 'Hadoram, Uusal, Dikla,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 28, 'Oobal, Abimael, Seeba,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 29, 'Oofir, Havila ja Joobab; need kõik olid Joktani pojad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 30, 'Nende elukohad olid Meesast Sefaara suunas idapoolses mäestikus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 31, 'Need olid Seemi järeltulijad nende suguvõsade, keelte, maade ja rahvaste järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 32, 'Need olid Noa poegade suguvõsad nende põlvnemiste ja rahvaste järgi; neist harunesid rahvad maa peal pärast veeuputust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 1, 'Kogu maailmas aga oli üks keel ja ühesugused sõnad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 2, 'Ja sündis, kui nad hommiku poolt teele läksid, et nad Sinearimaal leidsid oru ja jäid sinna elama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 3, 'Nad ütlesid üksteisele: „Tehkem nüüd telliskive ja põletagem neid hästi.\" Siis olid telliskivid neile ehituskivideks ja maapigi oli saviks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 4, 'Ja nad ütlesid: „Tulge, ehitagem enestele linn ja torn, mille tipp oleks taevas, ja tehkem enestele nimi, et me ei hajuks üle kogu maailma!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 5, 'Aga Issand tuli alla vaatama linna ja torni, mida inimlapsed ehitasid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 6, 'Ja Issand ütles: „Vaata, rahvas on üks ja neil kõigil on üks keel, ja see on alles nende tegude algus. Nüüd ei ole neil võimatu ükski asi, mida nad kavatsevad teha!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 7, 'Mingem nüüd alla ja segagem seal nende keel, et nad üksteise keelt ei mõistaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 8, 'Ja Issand pillutas nad sealt üle kogu maailma ja nad jätsid linna ehitamata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 9, 'Seepärast pandi sellele nimeks Paabel, sest seal Issand segas ära kogu maailma keele ja sealt pillutas Issand nad üle kogu maailma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 10, 'Need olid Seemi järeltulijad: kui Seem oli sada aastat vana, siis sündis temale Arpaksad kaks aastat pärast veeuputust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 11, 'Ja Seem elas pärast Arpaksadi sündimist viissada aastat, ja temale sündis poegi ja tütreid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 12, 'Kui Arpaksad oli elanud kolmkümmend viis aastat, siis sündis temale Selah.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 13, 'Ja Arpaksad elas pärast Selahi sündimist nelisada kolm aastat, ja temale sündis poegi ja tütreid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 14, 'Kui Selah oli elanud kolmkümmend aastat, siis sündis temale Eeber.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 15, 'Ja Selah elas pärast Eeberi sündimist nelisada kolm aastat, ja temale sündis poegi ja tütreid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 16, 'Kui Eeber oli elanud kolmkümmend neli aastat, siis sündis temale Peleg.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 17, 'Ja Eeber elas pärast Pelegi sündimist nelisada kolmkümmend aastat, ja temale sündis poegi ja tütreid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 18, 'Kui Peleg oli elanud kolmkümmend aastat, siis sündis temale Reu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 19, 'Ja Peleg elas pärast Reu sündimist kakssada üheksa aastat, ja temale sündis poegi ja tütreid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 20, 'Kui Reu oli elanud kolmkümmend kaks aastat, siis sündis temale Serug.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 21, 'Ja Reu elas pärast Serugi sündimist kakssada seitse aastat, ja temale sündis poegi ja tütreid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 22, 'Kui Serug oli elanud kolmkümmend aastat, siis sündis temale Naahor.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 23, 'Ja Serug elas pärast Naahori sündimist kakssada aastat, ja temale sündis poegi ja tütreid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 24, 'Kui Naahor oli elanud kakskümmend üheksa aastat, siis sündis temale Terah.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 25, 'Ja Naahor elas pärast Terahi sündimist sada üheksateist aastat, ja temale sündis poegi ja tütreid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 26, 'Kui Terah oli elanud seitsekümmend aastat, siis sündisid temale Aabram, Naahor ja Haaran.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 27, 'Ja need olid Terahi järeltulijad: Terahile sündisid Aabram, Naahor ja Haaran; ja Haaranile sündis Lott.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 28, 'Aga Haaran suri enne kui ta isa Terah, oma sünnimaal Kaldea Uuris.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 29, 'Ja Aabram ja Naahor võtsid enestele naised; Aabrami naise nimi oli Saarai ja Naahori naise nimi oli Milka, Haarani tütar, kes oli Milka ja Jiska isa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 30, 'Aga Saarai oli viljatu, temal ei olnud last.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 31, 'Ja Terah võttis oma poja Aabrami ja Haarani poja Loti, oma pojapoja, ja Saarai, oma minia, oma poja Aabrami naise, ja lahkus ühes nendega Kaldea Uurist, et minna Kaananimaale; ja nad jõudsid Haaranini ning jäid sinna elama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 32, 'Ja Terahi elupäevi oli kakssada viis aastat, ja Terah suri Haaranis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 1, 'Ja Issand ütles Aabramile: „Mine omalt maalt, omast sugukonnast ja isakojast maale, mille Ma sulle näitan!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 2, 'Ma teen sind suureks rahvaks ja õnnistan sind, Ma teen su nime suureks, et sa oleksid õnnistuseks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 3, 'Siis Ma õnnistan neid, kes sind õnnistavad, panen vande alla need, kes sind neavad, ja sinu nimel õnnistavad endid kõik suguvõsad maa peal!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 4, 'Ja Aabram läks, nagu Issand teda käskis, ja Lott läks ühes temaga; Aabram oli seitsekümmend viis aastat vana, kui ta Haaranist väljus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 5, 'Ja Aabram võttis oma naise Saarai ja oma vennapoja Loti ja kogu nende varanduse, mis nad olid soetanud, ja Haaranis hangitud hingelised, ja nad läksid teele Kaananimaa poole. Ja nad jõudsid Kaananimaale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 6, 'Ja Aabram käis maa läbi Sekemi püha paigani, Moore tammeni; kaananlased olid siis veel sellel maal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 7, 'Ja Issand ilmutas Ennast Aabramile ning ütles: „Sinu soole Ma annan selle maa!\" Siis ta ehitas sinna altari Issandale, kes oli Ennast temale ilmutanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 8, 'Sealt ta liikus edasi mäestikku Peetelist hommiku poole ja lõi oma telgi üles, nõnda et Peetel jäi õhtu ja Ai hommiku poole; ja ta ehitas sinna altari Issandale ning hüüdis appi Issanda nime.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 9, 'Ja Aabram läks teele, rändas üha ja siirdus Lõunamaale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 10, 'Aga maal oli nälg. Siis Aabram läks alla Egiptusesse, et seal võõrana elada, sest maal oli suur nälg.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 11, 'Ja kui ta minnes Egiptusele ligines, ütles ta oma naisele Saaraile: „Vaata, ma tean, et sa oled ilusa välimusega naine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 12, 'Aga kui egiptlased sind näevad, ütlevad nad: „See on tema naine!\" Siis nad tapavad mind, aga jätavad sind elama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 13, 'Ütle siis, et oled minu õde, et mu käsi sinu tõttu võiks hästi käia ja mu hing sinu pärast ellu jääks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 14, 'Kui Aabram jõudis Egiptusesse, siis nägid egiptlased, et naine oli väga ilus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 15, 'Ja kui vaarao vürstid teda nägid, siis nad ülistasid teda vaaraole ja naine võeti vaarao kotta,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 16, 'kes tegi Aabramile tema pärast head: ta sai pudulojuseid, veiseid ja eesleid, sulaseid ja ümmardajaid, emaeesleid ja kaameleid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 17, 'Aga Issand nuhtles vaaraot ja tema koda suurte nuhtlustega Saarai, Aabrami naise pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 18, 'Siis vaarao kutsus Aabrami ning ütles: „Miks sa mulle seda tegid? Miks sa ei teatanud mulle, et ta on sinu naine?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 19, 'Miks sa ütlesid: ta on mu õde, nõnda et ma tema enesele naiseks võtsin? Aga nüüd, vaata, seal on su naine, võta tema ja mine!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 20, 'Ja vaarao andis tema pärast meestele käsu, et nad saadaksid minema tema ja ta naise ja kõik, mis tal oli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 1, 'Ja Aabram läks Egiptusest üles Lõunamaale, tema ja ta naine ja kõik, mis tal oli; ja Lott oli ühes temaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 2, 'Ja Aabram oli väga rikas karja, hõbeda ja kulla poolest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 3, 'Ja ta rändas peatuspaigast teise, Lõunamaalt Peeteli poole, sinna paika, kus ta telk enne oli olnud Peeteli ja Ai vahel,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 4, 'altari paika, mille ta varem sinna oli teinud; ja Aabram hüüdis seal appi Issanda nime.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 5, 'Aga ka Lotil, kes rändas ühes Aabramiga, oli pudulojuseid, veiseid ja telke.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 6, 'Kuid maa ei suutnud neid toita, et üheskoos elada, sest nende varandus oli nii suur, et neil oli võimatu üheskoos elada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 7, 'Ja Aabrami lojuste karjaste ja Loti lojuste karjaste vahel tekkis riid; kaananlased ja perislased elasid siis veel sellel maal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 8, 'Siis Aabram ütles Lotile: „Ärgu olgu riidu minu ja sinu vahel, minu karjaste ja sinu karjaste vahel. Meie, mehed, oleme ju vennad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 9, 'Eks ole kogu maa su ees lahti? Mine nüüd minu juurest ära, lähed sina vasakut kätt, lähen mina paremat kätt; lähed sina paremat kätt, lähen mina vasakut kätt.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 10, 'Siis Lott tõstis oma silmad üles ja nägi, et kogu Jordani piirkond oli kõikjal veerikas; enne kui Issand Soodoma ja Gomorra hävitas, oli see kuni Soarini otsekui Issand rohuaed, samasugune nagu Egiptusemaa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 11, 'Ja Lott valis enesele kogu Jordani piirkonna; Lott läks teele hommiku poole ja nad lahkusid teineteisest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 12, 'Aabram jäi Kaananimaale ja Lott asus piirkonna linnadesse ning lõi oma telgid üles Soodomast saadik.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 13, 'Aga Soodoma mehed olid väga pahad ja patused Issanda ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 14, 'Ja Issand ütles Aabramile, pärast seda kui Lott tema juurest oli lahkunud: „Tõsta nüüd oma silmad üles ja vaata paigast, kus sa oled, põhja ja lõuna ja hommiku ja õhtu poole,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 15, 'sest kogu maa, mida sa näed, Ma annan sinule ja su soole igaveseks ajaks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 16, 'Ja Ma teen su soo maapõrmu sarnaseks: kui keegi suudab maapõrmu ära lugeda, siis on sinugi sugu äraloetav!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 17, 'Võta kätte, käi maa läbi pikuti ja põiki, sest Ma annan selle sinule!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 18, 'Ja Aabram võttis telgid ja tuli ning elas Mamre tammikus, mis on Hebroni juures; ja ta ehitas sinna altari Issandale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 1, 'Ja Sineari kuninga Amrafeli, Ellasari kuninga Arjoki, Eelami kuninga Kedorlaomeri ja Goojimi kuninga Tideali päevil sündis,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 2, 'et nad alustasid sõda Soodoma kuninga Bera, Gomorra kuninga Birsa, Adma kuninga Sineabi, Seboimi kuninga Semeeberi ja Bela, see on Soari kuninga vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 3, 'Need kõik kogunesid Siddimi orgu, kus nüüd on Soolameri.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 4, 'Kaksteist aastat olid nad Kedorlaomerit orjanud, aga kolmeteistkümnendal aastal nad tõstsid mässu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 5, 'Ja neljateistkümnendal aastal tulid Kedorlaomer ja need kuningad, kes olid ühes temaga, ja lõid refalasi Astarot-Karnaimis, susiite Haamis, emiite Kirjataimi tasandikul');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 6, 'ja horiite Seiri mäestikus kuni Eel-Paaranini, mis on kõrbe ääres.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 7, 'Siis nad pöördusid tagasi ja tulid Een-Mispatti, see on Kaadesisse, ja vallutasid kogu amalekkide väljade ala, samuti võitsid nad emorlasi, kes elasid Haseson-Taamaris.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 8, 'Aga Soodoma kuningas, Gomorra kuningas, Adma kuningas, Seboimi kuningas ja Bela, see on Soari kuningas, läksid välja ja valmistusid tapluseks nende vastu Siddimi orus:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 9, 'Eelami kuninga Kedorlaomeri, Goojimi kuninga Tideali, Sineari kuninga Amrafeli ja Ellasari kuninga Arjoki vastu - neli kuningat viie vastu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 10, 'Aga Siddimi org oli täis maapigi auke. Kui Soodoma ja Gomorra kuningad põgenesid, siis nad langesid neisse, kuna ülejäänud põgenesid mäestikku.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 11, 'Ja nad võtsid kogu Soodoma ja Gomorra varanduse ja kogu nende toiduse ning läksid ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 12, 'Ja ära minnes nad võtsid ühes ka Loti, Aabrami vennapoja, ja tema varanduse; ta elas ju Soodomas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 13, 'Aga üks põgenik tuli ja teatas Aabramile, heebrealasele, kes elas emorlase Mamre, Eskoli ja Aaneri venna tammikus; ja need olid Aabrami liitlased.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 14, 'Kui Aabram kuulis, et ta vennapoeg oli vangi viidud, siis ta vaatas üle oma kodakondsed, kes tema peres olid sündinud, arvult kolmsada kaheksateist, ja ajas vaenlasi taga kuni Daanini.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 15, 'Ta jaotas öösel oma sulased nende vastu, lõi neid ja jälitas neid kuni Hoobani, mis on Damaskusest vasakut kätt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 16, 'Ja ta tõi tagasi kogu varanduse; ka Loti, oma vennapoja, ja tema varanduse ta tõi tagasi, samuti naised ja rahva.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 17, 'Ja kui ta Kedorlaomerit ja ühes temaga olevaid kuningaid löömast tagasi tuli, läks Soodoma kuningas temale vastu Saave orgu, see on Kuningaorgu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 18, 'Ja Melkisedek, Saalemi kuningas, tõi leiba ja viina, sest tema oli kõige kõrgema Jumala preester,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 19, 'ja õnnistas teda ning ütles: „Olgu õnnistatud Aabram, kõige kõrgema Jumala, taeva ja maa looja poolt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 20, 'Olgu kiidetud kõige kõrgem Jumal, kes sinu vaenlased su kätte andis!\" Ja Aabram andis temale kümnist kõigest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 21, 'Ja Soodoma kuningas ütles Aabramile: „Anna hingelised mulle, aga varandus võta enesele.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 22, 'Kuid Aabram ütles Soodoma kuningale: „Ma tõstan oma käe üles Issanda, kõige kõrgema Jumala poole, Kes on taeva ja maa Looja,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 23, 'et ma ei võta lõngaotsa ega kingapaelagi kõigest sellest, mis sinu oma on, et sa ei saaks öelda: mina olen Aabrami rikkaks teinud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 24, 'Mul pole midagi vaja - ainult, mis poisid sõid, ja mehed, kes ühes minuga käisid - Aaner, Eskol ja Mamre - need võtku oma osa!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 1, 'Pärast neid lugusid tuli Aabramile nägemuses Issanda sõna, Kes ütles: „Ära karda, Aabram! Mina olen sulle kilbiks. Sinu tasu on väga suur!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 2, 'Aga Aabram ütles: „Issand Issand! Mida Sa mulle saad anda? Mina ju lähen ära lastetuna ja mu koja valitsejaks on Elieser Damaskusest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 3, 'Ja Aabram ütles: „Vaata, mulle sa ei ole ihuvilja andnud, aga näe, minu kojas sündinu saab mu pärijaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 4, 'Ja vaata, temale tuli Issanda sõna, Kes ütles: „Tema ei ole sinu pärija, vaid see, kes väljub su oma ihust, on su pärija.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 5, 'Ja Ta viis teda õue ning ütles: „Vaata nüüd taeva poole ja loe tähti, kui sa suudad neid lugeda!\" Ja Ta ütles temale: „Nõnda saab olema sinu sugu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 6, 'Ja ta uskus Issandat ning see arvati temale õiguseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 7, 'Ja Ta ütles temale: „Mina olen Issand, Kes sind tõi Kaldea Uurist, et anda sulle päranduseks see maa!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 8, 'Aga ta küsis: „Issand Issand, millest ma ära tunnen, et ma selle pärin?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 9, 'Siis Ta vastas temale: „Too mulle kolmeaastane õhvake, kolmeaastane kits, kolmeaastane jäär ja turteltuvi ühes lennuvõimelise pojaga.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 10, 'Ja ta tõi Temale kõik need, lõikas need keskelt lõhki ja pani vastavad pooled vastakuti; aga lindusid ta ei lõiganud lõhki.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 11, 'Kui röövlinnud laskusid korjuste peale, siis peletas Aabram need minema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 12, 'Aga kui päike loojus, vajus Aabram sügavasse unne, ja vaata, hirm ning suur pimedus haarasid teda!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 13, 'Ja Issand ütles Aabramile: „Sa pead teadma, et su järglased on võõrastena maal, mis ei ole nende oma; nad tehakse orjadeks ja neid vaevatakse nelisada aastat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 14, 'Aga ka rahvast, keda nad orjavad, Ma karistan, ja selle järel nad tulevad ära suure varandusega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 15, 'Sina ise aga lähed rahuga oma vanemate juurde, sind maetakse heas vanuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 16, 'Alles neljas põlv tuleb siia tagasi, sest emorlaste süü ei ole tänini veel küllaldane!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 17, 'Ja kui päike oli loojunud ja kui oli pime, siis nähti suitsevat küpsetusahju ja tuleleeki, mis nende lõigatud tükkide vahelt läbi käis!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 18, 'Selsamal päeval Issand tegi Aabramiga lepingu ja ütles: „Sinu soole Ma annan selle maa Egiptuseojast suure jõeni, Frati jõeni,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 19, 'keenlased, kenislased, kadmonlased,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 20, 'hetiidid, perislased, refalased,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 21, 'emorlased, kaananlased, girgaaslased ja jebuuslased!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 1, 'Ja Saarai, Aabrami naine, ei toonud temale last ilmale; aga tal oli ümmardajaks egiptlanna, nimega Haagar.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 2, 'Ja Saarai ütles Aabramile: „Vaata, Issand on mind keelanud last saamast. Heida nüüd mu ümmardaja juurde, vahest saan järeltulija temalt!\" Ja Aabram kuulis Saarai sõna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 3, 'Ja Saarai, Aabrami naine, võttis egiptlanna Haagari, oma ümmardaja, pärast seda kui Aabram oli kümme aastat elanud Kaananimaal, ja andis ta oma mehele Aabramile naiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 4, 'Ja tema heitis Haagari juurde ja see sai käima peale; aga kui see nägi, et ta oli käima peal, siis oli ta emand tema silmis nagu alam.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 5, 'Ja Saarai ütles Aabramile: „Mulle sündinud ülekohus tulgu sinu peale! Mina andsin oma ümmardaja sinu sülle, aga kui ta nüüd näeb, et ta on käima peal, siis olen mina tema silmis nagu alam. Issand mõistku õigust minu ja sinu vahel!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 6, 'Aga Aabram ütles Saaraile: „Vaata, su ümmardaja on sinu käes! Talita temaga, nagu sa heaks arvad.\" Siis Saarai alandas teda, aga seejärel ta põgenes tema juurest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 7, 'Ja Issanda Ingel leidis tema veeallika juurest kõrbes, Suuri tee ääres oleva allika juurest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 8, 'Ja Ta ütles: „Haagar, Saarai ümmardaja! Kust sa tuled ja kuhu sa lähed?\" Ja tema vastas: „Ma põgenen oma emanda Saarai eest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 9, 'Siis ütles Issanda Ingel temale: „Mine tagasi oma emanda juurde ja alanda ennast tema käte alla!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 10, 'Ja Issanda Ingel ütles temale: „Ma teen sinu soo nõnda arvurikkaks, et see paljuse pärast pole loetav!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 11, 'Ja Issanda Ingel ütles temale: „Vaata, sa oled käima peal ja tood poja ilmale! Pane temale nimeks Ismael, sest Issand on kuulnud su alandusest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 12, 'Temast tuleb mees nagu metseesel - tema käsi on igaühe vastu ja igaühe käsi on tema vastu - ta elab vaenus kõigi oma vendadega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 13, 'Siis ta nimetas Issandat, Kes temaga oli rääkinud, nimega „Sina oled nähtav Jumal!\" Sest ta ütles: „Eks ole minagi siin viimaks näinud Teda, Kes mind näeb?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 14, 'Seepärast nimetatakse kaevu Lahhai-Roi kaevuks; vaata, see on Kaadesi ja Baaradi vahel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 15, 'Ja Haagar tõi Aabramile poja ilmale; ja Aabram pani oma pojale, kelle Haagar oli sünnitanud, Ismael nimeks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 16, 'Ja Aabram oli kaheksakümmend kuus aastat vana, kui Haagar Aabramile Ismaeli ilmale tõi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 1, 'Kui Aabram oli üheksakümmend üheksa aastat vana, siis Issand ilmutas Ennast Aabramile ja ütles temale: „Mina olen kõigeväeline Jumal, käi Minu palge ees ja ole vaga!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 2, 'Ma teen lepingu Enese ja sinu vahel ja sind üpris väga paljuks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 3, 'Siis Aabram heitis silmili maha ja Jumal rääkis temaga, öeldes:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 4, '„See olen Mina! Vaata, Mu leping sinuga on, et sina saad paljude rahvaste isaks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 5, 'Sinu nime ei hüüta siis enam Aabramiks, vaid su nimi olgu Aabraham, sest Ma teen sind paljude rahvaste isaks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 6, 'Ma teen sind üpris väga viljakaks ja lasen sind rahvaiks saada, kuningadki põlvnevad sinust!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 7, 'Ma teen lepingu Enese ja sinu vahel, ja sinu soo vahel pärast sind, igaveseks lepinguks sugupõlvedele, et Ma olen Jumalaks sinule ja su soole pärast sind.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 8, 'Ja Ma annan sinule ja su soole pärast sind selle maa, kus sa võõrana elad, kogu Kaananimaa, igaveseks omandiks. Ja Mina olen neile Jumalaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 9, 'Ja Jumal ütles Aabrahamile: „Ja sina pead Mu lepingut pidama, sina ja su sugu pärast sind põlvest põlve.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 10, 'See on Minu leping Minu ja teie ning sinu soo vahel pärast sind, mida te peate pidama: kõik meesterahvad tuleb teil ümber lõigata!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 11, 'Te peate oma eesnaha liha ümber lõikama ja see olgu Minu ja teie vahelise lepingu märgiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 12, 'Kaheksapäevastena tuleb teil ümber lõigata kõik teie meesterahvad põlvkondade viisi, olgu peres sündinud, olgu raha eest ostetud ükskõik missuguselt võõralt, kes sinu soost ei ole,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 13, 'kindlasti tuleb ümber lõigata niihästi su peres sündinu kui su raha eest ostetu. Minu leping peab teie ihu küljes olema igavese lepinguna!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 14, 'Aga eesnahaga meesterahvas, kelle eesnaha liha ei ole ümber lõigatud, tuleb hävitada oma rahva seast: ta on tühistanud Minu lepingu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 15, 'Ja Jumal ütles Aabrahamile: „Saaraid, oma naist, ära hüüa enam Saaraiks, vaid tema nimi olgu Saara!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 16, 'Ma õnnistan teda ja annan ka temalt sulle poja! Ma õnnistan teda nõnda, et ta saab rahvaiks, rahvaste kuningadki põlvnevad temast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 17, 'Siis Aabraham heitis silmili maha, naeris ja ütles oma südames: „Kas peaks saja-aastasele poeg sündima? Või peaks üheksakümneaastane Saara sünnitama?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 18, 'Ja Aabraham ütles Jumalale: „Kui ainult Ismaelgi sinu ees jääks elama!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 19, 'Siis ütles Jumal: „Siiski, su naine Saara sünnitab sulle poja ja sa pead panema temale nimeks Iisak. Ja Ma teen temaga lepingu, igaveseks lepinguks tema soole pärast teda!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 20, 'Aga ka Ismaeli pärast Ma olen sind kuulnud. Vaata, Ma õnnistan teda ja teen ta viljakaks ning üpris väga arvurikkaks. Temast sünnib kaksteist vürsti ja Ma teen ta suureks rahvaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 21, 'Kuid Oma lepingu Ma teen Iisakiga, kelle Saara sulle sünnitab tuleval aastal selsamal ajal!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 22, 'Kui Jumal oli lõpetanud kõneluse Aabrahamiga, siis Ta läks tema juurest üles.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 23, 'Ja Aabraham võttis oma poja Ismaeli ja kõik oma peres sündinud, ja kõik raha eest ostetud, kõik meesterahvad Aabrahami pere inimeste hulgast, ja lõikas ümber nende eesnaha liha, otse selsamal päeval, nõnda nagu Jumal teda oli käskinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 24, 'Aabraham oli üheksakümmend üheksa aastat vana, kui ta eesnaha liha ümber lõigati.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 25, 'Ja Ismael, tema poeg, oli kolmteist aastat vana, kui ta eesnaha liha ümber lõigati.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 26, 'Otse selsamal päeval lõigati ümber Aabraham ja tema poeg Ismael;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 27, 'ja kõik ta pere mehed, peres sündinud ja võõrastelt raha eest ostetud, lõigati ümber koos temaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 1, 'Ja Issand ilmutas Ennast temale Mamre tammikus, kui ta istus telgi ukse ees kõige palavamal päevaajal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 2, 'Ta tõstis oma silmad üles ja vaatas, ja ennäe, kolm meest seisid ta ees! Ja nähes neid, tõttas ta telgi ukse juurest neile vastu ja kummardas maani');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 3, 'ning ütles: „Issand, kui ma Sinu silmis armu leian, siis ära mine Oma sulasest mööda!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 4, 'Toodagu nüüd pisut vett, peske jalgu ja nõjatuge puu alla.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 5, 'Ma toon palukese leiba, kinnitage südant enne kui edasi lähete, kui juba kord olete Oma sulase kaudu käimas!\" Ja nad vastasid: „Tee nõnda, nagu sa oled rääkinud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 6, 'Ja Aabraham tõttas telki Saara juurde ning ütles: „Võta ruttu kolm mõõtu nisujahu, sõtku ja tee kooke!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 7, 'Ja Aabraham jooksis karja juurde, võttis ühe noore ja ilusa vasika, andis poisi kätte ja see tõttas seda valmistama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 8, 'Ja ta tõi võid, piima ja vasika, mis oli valmistatud, ja pani nende ette; ta ise seisis nende juures puu all, kui nad sõid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 9, 'Siis nad küsisid temalt: „Kus su naine Saara on?\" Ja ta vastas: „Seal telgis!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 10, 'Siis ta ütles: „Ma tulen sinu juurde kindlasti tagasi aasta pärast samal ajal, ja vaata, su naisel Saaral saab olema poeg!\" Ja Saara kuulis seda tema selja taga oleva telgi ukse juures.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 11, 'Aga Aabraham ja Saara olid vanad ja elatanud; Saaral oli lakanud olemast ka see, mis muidu naistele on omane.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 12, 'Ja Saara naeris iseeneses ja mõtles: „Nüüd, kui ma olen vanaks jäänud, peaks mul veel himu olema! Ja ka mu isand on vana.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 13, 'Aga Issand ütles Aabrahamile: „Miks Saara naerab ja ütleb: kas ma tõesti peaksin sünnitama, kuna ma ju olen vana?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 14, 'Kas peaks Issandal midagi olema võimatu? Ma tulen su juurde tagasi aasta pärast samal ajal, ja Saaral saab olema poeg!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 15, 'Kuid Saara salgas, öeldes: „Mina ei naernud!\" Sest ta kartis. Tema aga ütles: „Sa naersid küll!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 16, 'Siis mehed tõusid sealt üles ja vaatasid alla Soodoma poole; ja Aabraham läks ühes nendega, neid saatma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 17, 'Ja Issand ütles: „Kas peaksin varjama Aabrahami eest, mida tahan teha?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 18, 'Aabraham saab ometi suureks ja vägevaks rahvaks ja tema nimel õnnistavad endid kõik maailma rahvad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 19, 'Sest Ma tean temast, et ta käsib oma poegi ja järeltulevat sugu hoida Issand teed ning teha, mis õige ja kohus, et Issand võiks anda Aabrahamile, mis Ta temale on tõotanud.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 20, 'Siis ütles Issand: „Hädakisa Soodoma ja Gomorra pärast on suur ja nende patud on väga rasked!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 21, 'Seepärast Ma lähen alla ja vaatan, kas nad Minuni jõudnud hädakisa kohaselt on teinud kõike seda või mitte. Ma tahan seda teada!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 22, 'Ja mehed pöördusid sealt ära ja läksid Soodomasse, aga Aabraham jäi veel seisma Issanda ette.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 23, 'Ja Aabraham astus ligi ning ütles: „Kas tahad tõesti hävitada õige ühes õelaga?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 24, 'Vahest on linnas viiskümmend õiget? Kas tahad siis need hävitada ega taha paigale andeks anda nende viiekümne õige pärast, kes seal on?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 25, 'Jäägu Sinust kaugele see tegu, et tapad õige ühes õelaga, et õigel käib käsi nagu õelalgi! Jäägu see Sinu poolt tegemata! Kas kõige maailma kohtumõistja ei peaks tegema õigust?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 26, 'Ja Issand ütles: „Kui Ma Soodoma linnast leian viiskümmend õiget, siis annan nende pärast andeks kogu paigale.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 27, 'Aga Aabraham kostis ning ütles: „Vaata, ma olen nõuks võtnud siiski Issandaga rääkida, kuigi olen põrm ja tuhk!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 28, 'Vahest puudub viiekümnest õigest viis? Kas tahad siis nende viie pärast hävitada kogu linna?\" Ja Tema vastas: „Ma ei hävita, kui sealt leian nelikümmend viis!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 29, 'Ja ta jätkas veelgi kõnelust Temaga ning ütles: „Vahest leidub seal nelikümmend?\" Ja Tema vastas: „Ma ei tee seda neljakümne pärast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 30, 'Aga ta ütles: „Ärgu süttigu põlema Issanda viha, et ma veel räägin! Vahest leidub seal kolmkümmend?\" Ja Tema vastas: „Ma ei tee seda, kui sealt leian kolmkümmend!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 31, 'Siis ta ütles: „Vaata, ma olen nõuks võtnud siiski Issandaga rääkida. Vahest leidub seal kakskümmend?\" Ja Tema vastas: „Ma ei hävita kahekümne pärast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 32, 'Aga ta ütles: „Ärgu süttigu põlema Issanda viha, et ma veel üksainus kord räägin! Vahest leidub seal kümme?\" Ja Tema vastas: „Ma ei hävita kümne pärast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 33, 'Ja Issand läks ära, kui oli lõpetanud kõneluse Aabrahamiga; ja Aabraham läks koju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 1, 'Ja need kaks Inglit jõudsid õhtul Soodomasse. Lott istus parajasti Soodoma väravas. Kui Lott neid nägi, siis ta tõusis neile vastu, kummardas silmili maha');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 2, 'ja ütles: „Ennäe mu isandaid! Astuge ometi oma sulase kotta, jääge öömajale ja peske oma jalad. Hommikul võite vara üles tõusta ja oma teekonda jätkata!\" Kuid nad vastasid: „Ei, me jääme ööseks välja!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 3, 'Aga ta käis neile väga peale. Siis nad põikasid tema juurde ning tulid ta kotta. Ja ta valmistas neile söömaaja, küpsetas hapnemata leibu ja nad sõid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 4, 'Nad ei olnud veel magama heitnud, kui linna mehed, Soodoma mehed, piirasid koda, niihästi noored kui vanad, kogu rahvas viimseni,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 5, 'ja hüüdsid Lotti ning ütlesid temale: „Kus on mehed, kes öösel tulid sinu juurde? Too nad välja meie kätte, et saaksime neid katsuda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 6, 'Siis Lott läks välja nende juurde ukse ette, sulges ukse enese järel');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 7, 'ja ütles: „Mu vennad, ärge ometi tehke kurja!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 8, 'Vaadake, mul on kaks tütart, kes mehest veel midagi ei tea. Ma toon need välja teie kätte ja talitage nendega, nagu teie silmis hea on. Neile meestele ainult ärge tehke midagi, sest nad on tulnud varjule minu katuse alla!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 9, 'Kuid nad vastasid: „Käi minema!\" Ja nad ütlesid: „Ise on tulnud võõrana elama, aga tahab olla kohtumõistjaks! Nüüd me kohtleme sind kurjemini kui neid!\" Ja nad kippusid väga mehe, Loti, kallale ning asusid ust maha murdma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 10, 'Siis mehed sirutasid käed välja, tõmbasid Loti eneste juurde kotta ja sulgesid ukse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 11, 'Aga mehi, kes koja ukse taga olid, lõid nad pimestusega, niihästi väikesi kui suuri, nõnda et need väsisid ust otsimast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 12, 'Ja mehed ütlesid Lotile: „Kas sul veel keegi siin on? Oma väimehed, pojad ja tütred ja kõik, kes sul linnas on, vii siit paigast ära,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 13, 'sest me hävitame selle paiga, kuna hädakisa nende pärast on Issanda ees suur; ja Issand on meid läkitanud seda hävitama!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 14, 'Siis Lott läks välja ja kõneles oma väimeestega, kes pidid võtma ta tütred, ning ütles: „Tõuske, minge siit paigast ära, sest Issand hävitab selle linna!\" Aga oma väimeeste silmis ta heitis nalja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 15, 'Ja kui hakkas koitma, kiirustasid Inglid Lotti, öeldes: „Tõuse, võta oma naine ja kaks tütart, kes siin on, et sa ei hukkuks linna süü pärast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 16, 'Ja kui ta veel kõhkles, siis haarasid mehed kinni ta käest, ja ta naise käest ja ta mõlemate tütarde käest, sest Issand tahtis teda säästa; ja nad viisid ta ära ning jätsid väljapoole linna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 17, 'Ja neid välja viies nad ütlesid: „Päästa oma hing! Ära vaata selja taha ja ära peatu mitte kuskil ümbruskonnas! Põgene mäestikku, et sa ei hukkuks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 18, 'Ja Lott vastas neile: „Muidugi mitte, mu Issand!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 19, 'Vaata, Su sulane on küll Sinu silmis armu leidnud ja suur on Su heldus, mida mulle oled osutanud, jättes mu hinge elama! Aga ma ei suuda põgeneda mäestikku, et õnnetus mind ei tabaks ja ma ei sureks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 20, 'Vaata, see linn taamal on nii ligidal, et sinna võiks põgeneda ja on pisitilluke! Kui ma ometi sinna pääseksin - eks ole, see on ju pisitilluke? - siis võiks mu hing jääda elama!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 21, 'Ja Ta vastas temale: „Vaata, Ma võtan sind kuulda ka selles asjas ega paiska segi linna, millest sa räägid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 22, 'Tõtta, põgene sinna, sest Ma ei või midagi teha enne, kui sa sinna oled jõudnud!\" Seepärast pandi sellele linnale nimeks Soar.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 23, 'Päike oli just tõusnud maa kohale, kui Lott jõudis Soari.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 24, 'Ja Issand laskis sadada Soodoma ja Gomorra peale väävlit ja tuld Issanda juurest taevast');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 25, 'ning hävitas need linnad ja kogu ümbruskonna, kõik linnade elanikud ja maa taimestiku.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 26, 'Aga Loti naine, kes ta järel käis, vaatas tagasi ja muutus soolasambaks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 27, 'Ja Aabraham läks hommikul vara paika, kus ta Issanda ees oli seisnud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 28, 'heitis pilgu alla Soodoma ja Gomorra poole ja kogu ümbruskonna maapinnale, vaatas, ja ennäe, maast tõusis suits otsekui sulatusahju suits!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 29, 'Kui Jumal oli segi paisanud ümbruskonna linnad, mõtles Jumal Aabrahamile ja saatis Loti ära selle hävituse keskelt, millega ta segi paiskas need linnad, kus Lott oli elanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 30, 'Ja Lott läks Soarist üles ning elas mäestikus ühes oma kahe tütrega, sest ta kartis elada Soaris; ta elas ühes koopas, tema ise ja ta mõlemad tütred.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 31, 'Kord ütles vanem nooremale: „Meie isa on vana ja pole ühtki meest maal, kes tuleks meie juurde kogu maailma kombe järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 32, 'Tule, joodame oma isa viinaga ja magame temaga, et saaksime oma isalt järeltulija!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 33, 'Ja nad jootsid oma isa sel ööl viinaga ja vanem läks ning magas oma isaga; see ei märganudki, millal ta magama heitis või üles tõusis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 34, 'Ja järgmisel päeval ütles vanem nooremale: „Vaata, ma magasin eile oma isaga. Joodame ka sel ööl teda viinaga, siis mine maga sina temaga, et saaksime oma isalt järeltulija!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 35, 'Ja nad jootsid ka sel ööl oma isa viinaga ja noorem läks ning magas temaga; see ei märganudki, millal ta magama heitis või üles tõusis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 36, 'Ja Loti mõlemad tütred said oma isast käima peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 37, 'Ja vanem sünnitas poja ning pani temale nimeks Moab; see on moabiitide isa tänapäevani.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 38, 'Ja noorem sünnitas ka poja ning pani temale nimeks Ben-Ammi; see on ammonlaste isa tänapäevani.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 1, 'Ja Aabraham siirdus sealt Lõunamaale ning asus elama Kaadesi ja Suuri vahele; ta elas Geraris võõrana.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 2, 'Ja Aabraham ütles oma naise Saara kohta: „Ta on minu õde.\" Siis Gerari kuningas Abimelek läkitas järele ja võttis Saara.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 3, 'Aga Jumal tuli Abimeleki juurde öösel unes ja ütles temale: „Vaata, sa pead surema naise pärast, kelle sa enesele võtsid, sest ta on abielunaine!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 4, 'Abimelek aga ei olnud temasse puutunud ja vastas: „Issand, kas tahad surmata ka õiget rahvast?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 5, 'Eks ta öelnud mulle: ta on minu õde? Ja tema ütles ka ise: ta on mu vend! Ma tegin seda vaga südame ja süütute kätega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 6, 'Siis ütles Jumal temale unes: „Minagi tean, et sa tegid seda vaga südamega ja ma hoidsin sind ka Minu vastu pattu tegemast: sellepärast Ma ei lasknud sind temasse puutuda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 7, 'Ja nüüd anna mehele naine tagasi, sest ta on prohvet ja ta palub sinu pärast, et sa jääksid elama. Aga kui sa tagasi ei anna, siis tea, et peate surema sina ja kõik, kes sul on!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 8, 'Ja Abimelek tõusis hommikul vara, kutsus kõik oma sulased ning rääkis kõik need sõnad nende kuuldes; ja mehed kartsid väga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 9, 'Siis Abimelek kutsus Aabrahami ja ütles temale: „Mis sa meile tegid! Millega ma sinu vastu pattu tegin, et sa tõid suure süü minu ja mu kuningriigi peale? Sa oled minuga teinud sündmatuid tegusid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 10, 'Ja Abimelek küsis Aabrahamilt: „Mida sa mõtlesid seda asja tehes?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 11, 'Ja Aabraham vastas: „Mina mõtlesin ainult, et selles paigas ei ole jumalakartust ja mind tapetakse mu naise pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 12, 'Ja tema ongi tõepoolest mu õde: mu isa tütar, kuigi mitte mu ema tütar; seetõttu ta sai mu naiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 13, 'Aga kui Jumal saatis mind isamajast rändama, ütlesin ma temale: osuta mulle seda armastust, et sa kõigis paigus, kuhu tuleme, minu kohta ütled: ta on mu vend!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 14, 'Siis Abimelek võttis pudulojuseid ja veiseid, sulaseid ja ümmardajaid ja andis Aabrahamile; ja ta andis temale tagasi Saara, ta naise.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 15, 'Ja Abimelek ütles: „Vaata, mu maa on lahti sinu ees, ela, kus sulle meeldib!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 16, 'Ja Saarale ta ütles: „Näe, ma annan su vennale tuhat hõbetükki. Vaata, see olgu sulle hüvituseks kõigi ees, kes su juures on. Sa oled kõiges õigeks osutunud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 17, 'Ja Aabraham palus Jumalat ja Jumal tegi terveks Abimeleki, ta naise ja ta ümmardajad, nõnda et nad said lapsi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 18, 'sest Issand oli sulgenud kõvasti kõik emaihud Abimeleki kojas Saara, Aabrahami naise pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 1, 'Ja Issand hoolitses Saara eest, nõnda nagu Ta oli lubanud. Issand toimis Saaraga, nõnda nagu Ta oli öelnud:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 2, 'Saara sai käima peale ja tõi Aabrahamile poja ilmale ta vanas eas, määratud ajal, millest Jumal temaga oli rääkinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 3, 'Ja Aabraham pani oma pojale, kes temale sündis, kelle Saara temale ilmale tõi, Iisak nimeks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 4, 'Ja Aabraham lõikas ümber oma poja Iisaki, kui see kaheksapäevane oli, nõnda nagu Jumal teda oli käskinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 5, 'Ja Aabraham oli sada aastat vana, kui ta poeg Iisak temale sündis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 6, 'Aga Saara ütles: „Jumal pani mind naerma! Igaüks, kes sellest kuuleb, naerab mind!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 7, 'Ja ta ütles: „Kes oleks võinud Aabrahamile kuulutada, et Saara imetab veel lapsi? Ometi tõin ma temale poja ilmale ta vanas eas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 8, 'Laps kasvas ja võõrutati; ja Aabraham tegi suured joodud Iisaki võõrutamispäeval.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 9, 'Kui Saara nägi egiptlanna Haagari poega, kelle see Aabrahamile oli ilmale toonud, naervat,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 10, 'siis ta ütles Aabrahamile: „Kihuta minema see ümmardaja ja tema poeg, sest selle ümmardaja poeg ei või pärida ühes minu poja Iisakiga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 11, 'See kõne aga oli Aabrahami silmis ta poja kohta väga paha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 12, 'Aga Jumal ütles Aabrahamile: „Ärgu olgu see su silmis paha ei poisi ega su ümmardaja kohta. Kõiges, mis Saara sulle ütleb, kuule ta sõna, sest Iisakist loetakse sinu sugu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 13, 'Aga ka ümmardaja poja Ma teen rahvaks, sest ta on ju sinu järeltulija!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 14, 'Ja Aabraham tõusis hommikul vara, võttis leiva ja veelähkri ning andis Haagarile, pannes need temale selga, samuti lapse ja saatis ta minema. Ja tema läks ning eksles Beer-Seba kõrbes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 15, 'Kui vesi lähkrist lõppes, siis ta heitis lapse ühe põõsa alla');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 16, 'ja läks ning istus temaga kohastikku, ammulaske kauguses, sest ta ütles: „Ma ei või näha lapse surma!\" Nõnda ta istus temaga kohastikku, tõstis häält ja nuttis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 17, 'Aga Jumal kuulis poisi häält ja Jumala Ingel hüüdis taevast Haagarit ning ütles temale: „Mis sul viga on, Haagar? Ära karda, sest Jumal on kuulnud poisi häält seal, kus ta on.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 18, 'Tõuse, tõsta poiss üles ja võta ta käekõrvale, sest Ma tahan temast teha suure rahva!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 19, 'Ja Jumal tegi ta silmad lahti, nõnda et ta nägi üht veekaevu; ta läks ning täitis lähkri veega ja andis poisile juua.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 20, 'Ja Jumal oli poisiga; ta kasvas ja elas kõrbes, ja temast sai ammukütt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 21, 'Ta elas Paarani kõrbes; ja ta ema võttis temale Egiptusemaalt naise.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 22, 'Sel ajal Abimelek ja tema väepealik Piikol rääkisid Aabrahamiga, öeldes: „Jumal on sinuga kõiges, mis sa teed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 23, 'Ja nüüd vannu mulle siin Jumala juures, et sa ei peta mind ega mu lapsi ja lapselapsi! Heateo pärast, mis ma sulle tegin, tee seda mulle ja maale, kus sa võõrana elad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 24, 'Ja Aabraham ütles: „Ma vannun!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 25, 'Aabraham aga noomis Abimelekit veekaevu pärast, mille Abimeleki sulased väevõimuga olid ära võtnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 26, 'Kuid Abimelek vastas: „Mina ei tea, kes seda tegi. Sina pole mulle sellest teatanud ja mina ise pole ka midagi kuulnud enne kui täna!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 27, 'Siis Aabraham võttis pudulojuseid ja veiseid ja andis Abimelekile; ja nad mõlemad sõlmisid lepingu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 28, 'Kui Aabraham pani seitse lambatalle eraldi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 29, 'küsis Abimelek Aabrahamilt: „Milleks on siin need seitse talle, keda sa oled pannud eraldi?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 30, 'Ja tema vastas: „Sa pead need seitse talle minu käest võtma tõendina minu poolt, et mina olen kaevanud selle kaevu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 31, 'Seepärast hüütakse seda paika Beer-Sebaks, sest nad mõlemad andsid seal vande.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 32, 'Nõnda sõlmisid nad Beer-Sebas lepingu. Siis Abimelek ja tema väepealik Piikol tõusid ja läksid tagasi vilistide maale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 33, 'Aabraham aga istutas Beer-Sebasse ühe tamariskipuu ja hüüdis seal appi Issanda, igavese Jumala nime.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 34, 'Ja Aabraham elas võõrana vilistide maal kaua aega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 1, 'Ja pärast neid sündmusi kiusas Jumal Aabrahami ning ütles temale: „Aabraham!\" Ja ta vastas: „Siin ma olen!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 2, 'Ja Tema ütles: „Võta nüüd Iisak, oma ainus poeg, keda sa armastad, ja mine Morija maale ning ohverda ta seal põletusohvriks ühel neist mägedest, mis Ma sulle nimetan!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 3, 'Ja Aabraham tõusis hommikul vara, saduldas oma eesli, võttis enesega ühes oma kaks noort meest ja oma poja Iisaki, lõhkus põletusohvri puud, seadis minekule ja läks paika, millest Jumal temale oli rääkinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 4, 'Kolmandal päeval Aabraham tõstis oma silmad üles ja nägi seda paika kaugelt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 5, 'Ja Aabraham ütles oma noortele meestele: „Jääge teie eesliga siia. Mina ja poiss läheme sinna, kummardame ja tuleme siis tagasi teie juurde.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 6, 'Ja Aabraham võttis põletusohvri puud, pani need oma pojale Iisakile õlale, võttis oma kätte tule ja noa ning mõlemad läksid üheskoos.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 7, 'Ja Iisak rääkis oma isa Aabrahamiga ning ütles: „Isa!\" Ja tema vastas: „Siin ma olen, mu poeg!\" Siis ta ütles: „Näe, siin on tuli ja puud, aga kus on ohvritall?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 8, 'Ja Aabraham vastas: „Küllap Jumal vaatab Enesele ohvritalle, mu poeg!\" Nõnda läksid mõlemad üheskoos.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 9, 'Ja kui nad jõudsid paika, millest Jumal temale oli rääkinud, ehitas Aabraham sinna altari, ladus puud, sidus kinni oma poja Iisaki ja pani ta altarile puude peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 10, 'Ja Aabraham sirutas käe ja võttis noa, et oma poega tappa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 11, 'Aga Issanda Ingel hüüdis teda taevast ja ütles: „Aabraham, Aabraham!\" Ja tema vastas: „Siin ma olen!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 12, 'Siis Ta ütles: „Ära pane kätt poisi külge ja ära tee temale midagi, sest nüüd Ma tean, et sa kardad Jumalat ega keela Mulle oma ainust poega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 13, 'Ja Aabraham tõstis oma silmad üles, vaatas, ja ennäe, üks jäär oli rägastikus sarvipidi kinni! Ja Aabraham läks ning võttis jäära ja ohverdas selle põletusohvriks oma poja asemel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 14, 'Ja Aabraham pani sellele paigale nimeks: „Issand näeb!\" Seepärast öeldakse tänapäevalgi: „Mäel, kus Issand Ennast ilmutab!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 15, 'Ja Issanda Ingel hüüdis Aabrahami teist korda taevast');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 16, 'ning ütles temale: „Ma vannun Iseenese juures, ütleb Issand: sellepärast, et sa seda tegid ega keelanud Mulle oma ainust poega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 17, 'Ma õnnistan sind tõesti ja teen su soo väga paljuks nagu tähti taevas ja nagu liiva mere ääres, ja su sugu vallutab oma vaenlaste väravad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 18, 'Ja sinu soo nimel õnnistavad endid kõik maailma rahvad, sellepärast et sa võtsid kuulda Mu häält!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 19, 'Siis Aabraham läks tagasi oma noorte meeste juurde, ja nad tõusid ning läksid üheskoos Beer-Sebasse. Ja Aabraham jäi elama Beer-Sebasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 20, 'Ja pärast neid sündmusi teatati Aabrahamile ja öeldi: „Vaata, Milka on ka su vennale Naahorile poegi ilmale toonud:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 21, 'Uusi, tema esmasündinu Buusi, selle venna, Kemueli, Arami isa,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 22, 'Kesedi, Haso, Pildase, Jidlafi ja Betueli.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 23, 'Ja Betuelile sündis Rebeka. Need kaheksa tõi Milka ilmale Naahorile, Aabrahami vennale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 24, 'Ja tema liignaine, Reuma nimi, sünnitas ka - Teba, Gahami, Tahase ja Maaka.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 1, 'Ja Saara elas sada kakskümmend seitse aastat; nii palju oli Saaral eluaastaid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 2, 'Saara suri Kirjat-Arbas, see on Hebronis, Kaananimaal; ja Aabraham tuli Saarat leinama ja taga nutma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 3, 'Siis Aabraham tõusis üles oma surnu juurest ja rääkis hetiitidega, öeldes:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 4, '„Mina olen teie juures võõras ja majaline. Andke mulle eneste juurde pärandhaud, et saaksin matta surnu oma silma eest.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 5, 'Ja hetiidid vastasid Aabrahamile, öeldes temale:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 6, '„Kuule meid, isand! Sina oled Jumala vürst meie keskel! Mata oma surnu meie kõige paremasse hauda. Ükski meist ei keela sulle oma hauda su surnu matmiseks.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 7, 'Aga Aabraham tõusis ning kummardas maa rahva, hetiitide ees');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 8, 'ja rääkis nendega, öeldes: „Kui see on teile meeltmööda, et ma oma surnu oma silma eest ära matan, siis kuulge mind ja mõjustage minu kasuks Efronit, Sohari poega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 9, 'et ta mulle annaks temale kuuluva Makpela koopa, mis on ta välja servas. Ta andku see mulle täie hinna eest pärandhauaks teie keskel.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 10, 'Aga Efron istus hetiitide hulgas; ja hetiit Efron vastas Aabrahamile hetiitide kuuldes, kõigi juuresolekul, kes ta linna väravast läbi käisid, ja ütles:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 11, '„Ei, mu isand! Kuule mind! Ma annan sulle välja, samuti koopa, mis selles on, ma annan sulle: ma annan selle sulle oma rahvuskaaslaste silma all. Mata oma surnu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 12, 'Kuid Aabraham kummardas maa rahva ees,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 13, 'rääkis Efroniga maa rahva kuuldes ja ütles: „Kui sa ometi ise mind kuulda võtaksid! Mina maksan välja eest raha, võta see minult, et saaksin oma surnu sinna matta!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 14, 'Siis Efron vastas Aabrahamile, öeldes temale:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 15, '„Mu isand, kuule mind! Maa väärtus on nelisada hõbeseeklit, - mis tähtsus sellel ongi minu ja sinu vahekorra juures! Mata aga oma surnu.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 16, 'Ja Aabraham kuulas Efronit; Aabraham vaagis Efronile raha, mida see hetiitide kuuldes oli nimetanud, nelisada hõbeseeklit, kaubanduses käibel olevaid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 17, 'Nõnda said Efroni väli Makpelas Mamre kohal, väli ja selles olev koobas ja kõik väljal olevad puud, mis olid ümber kogu selle maa-ala,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 18, 'Aabrahami omandiks hetiitide nähes, kõigi juuresolekul, kes ta linna väravast läbi käisid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 19, 'Ja seejärel Aabraham mattis oma naise Saara Makpela välja koopasse Mamre kohal, see on Hebronis Kaananimaal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 20, 'Nõnda said väli ja selles olev koobas hetiitidelt Aabrahamile pärandhauaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 1, 'Kui Aabraham oli vana ja elatanud ning Issand oli Aabrahami kõigiti õnnistanud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 2, 'siis ütles Aabraham vanimale sulasele oma peres, kes valitses kõige üle, mis tal oli: „Pane nüüd oma käsi mu puusa alla!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 3, 'Mina vannutan sind Issanda, taeva Jumala ja maa Jumala juures, et sa mu pojale ei võta naist kaananlaste tütreist, kelle keskel ma elan,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 4, 'vaid et sa lähed minu maale ja mu sugulaste juurde ning võtad sealt naise mu pojale Iisakile!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 5, 'Aga sulane ütles temale: „Võib-olla naine ei taha mulle järgneda siia maale. Kas ma siis tõesti pean su poja viima tagasi maale, kust sa oled ära tulnud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 6, 'Siis ütles Aabraham temale: „Hoia, et sa ei vii mu poega sinna tagasi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 7, 'Issand, taeva Jumal, Kes mind võttis mu isakojast ja mu sünnimaalt ja Kes mulle rääkis ning vandus, öeldes: sinu soole ma annan selle maa! - Tema Ise läkitab Oma Ingli sinu ees, et saaksid sealt mu pojale naise võtta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 8, 'Aga kui naine ei taha sulle järgneda, siis oled sa sellest mu vandest vaba. Ainult ära vii mu poega sinna tagasi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 9, 'Siis sulane pani käe oma isanda Aabrahami puusa alla ja vandus temale selle kõne kohaselt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 10, 'Ja sulane võttis oma isanda kaamelitest kümme kaamelit ning läks, ja tal oli oma isandalt kaasas kõiksugu kalleid asju; ta võttis kätte ja läks Mesopotaamiasse Naahori linna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 11, 'Seal laskis ta õhtul kaamelid põlvili heita väljaspool linna veekaevu juures sel ajal, kui veeviijad välja tulid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 12, 'ning ütles: „Issand, minu isanda Aabrahami Jumal, lase ometi see mul täna korda minna ja tee head mu isandale Aabrahamile!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 13, 'Vaata, ma seisan veeallika juures ja linnaelanike tütred tulevad vett viima.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 14, 'Sündigu siis, et tütarlaps, kellele ma ütlen: kalluta oma kruusi, et ma saaksin juua! ja kes vastab: joo, ja ma joodan ka su kaameleid! - on see, kelle sa oled määranud oma sulasele Iisakile. Sellest ma siis tean, et sa mu isandale oled head teinud.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 15, 'Ja veel enne kui ta rääkimise oli lõpetanud, vaata, siis tuli välja Rebeka, kes oli sündinud Betuelile, Aabrahami venna Naahori naise Milka pojale; ja tal oli kruus õlal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 16, 'Ja tütarlaps oli väga ilusa välimusega, alles neitsi ja mehe poolt puutumata. Ta läks alla allika juurde, täitis kruusi ja tuli üles.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 17, 'Siis jooksis sulane temale vastu ning ütles: „Anna mulle oma kruusist pisut vett rüübata!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 18, 'Tema vastas: „Joo, mu isand!\" Ja ta tõstis kähku kruusi alla oma käele ning andis temale juua.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 19, 'Ja olles temale juua andnud, ütles ta: „Ma ammutan ka su kaamelitele, kuni needki on joonud.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 20, 'Ja ta tühjendas kähku oma kruusi künasse ning jooksis jälle kaevule vett ammutama ja ammutas kõigile ta kaamelitele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 21, 'Mees aga silmitses teda vaikides, et mõista, kas Issand oli ta teekonna lasknud korda minna või mitte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 22, 'Ja kui kaamelid olid joonud, võttis mees kuldrõnga, pooleseeklilise, ja kaks käevõru ta käte jaoks, kümme kuldseeklit väärt,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 23, 'ning küsis: „Kelle tütar sa oled? Ütle ometi mulle! Ons su isa kojas meile ööbimispaika?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 24, 'Ja ta vastas temale: „Mina olen Betueli, Naahori ja Milka poja tütar.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 25, 'Ta ütles temale veel: „Niihästi õlgi kui muud loomasööta on meil küllalt, ööbimispaikki on olemas.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 26, 'Siis mees kummardas ja heitis Issanda ette');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 27, 'ning ütles: „Kiidetud olgu Issand, mu isanda Aabrahami Jumal, Kes mu isandale ei ole keelanud Oma heldust ja tõde! Issand on mind teekonnal juhtinud mu isanda vendade kotta!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 28, 'Aga tütarlaps jooksis ning teatas oma ema perele, mis oli sündinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 29, 'Ja Rebekal oli vend, Laaban nimi; ja Laaban jooksis välja, mehe juurde allikale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 30, 'Sest kui ta nägi rõngast ja käevõrusid oma õe kätel, ja kui ta oli kuulnud oma õe Rebeka sõnu, kes ütles: „Nõnda rääkis mulle see mees!\" - siis ta läks selle mehe juurde, ja ennäe, see seisis kaamelite juures allikal!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 31, 'Ja ta ütles: „Tule sisse, Issanda õnnistatu! Miks sa väljas seisad? Ma olen korda seadnud koja ja kaamelite paiga.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 32, 'Mees tuli siis kotta ja Laaban päästis kaamelid lahti, andis õlgi ja toitu kaamelitele ning tõi vett tema ja ühes temaga olevate meeste jalgade pesemiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 33, 'Siis pandi temale rooga ette. Aga ta ütles: „Ma ei söö enne kui ma oma asja olen rääkinud!\" Ja talle vastati: „Räägi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 34, 'Siis ta ütles: „Mina olen Aabrahami sulane.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 35, 'Issand on mu isandat väga õnnistanud, nõnda et ta on jõukaks saanud: Ta on temale andnud pudulojuseid ja veiseid, hõbedat ja kulda, sulaseid ja ümmardajaid, kaameleid ja eesleid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 36, 'Ja Saara, mu isanda naine, on vanas eas mu isandale poja ilmale toonud, ja sellele on ta andnud kõik, mis tal on.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 37, 'Ja mu isand vannutas mind, öeldes: sa ei tohi võtta mu pojale naist kaananlaste tütarde seast, kelle maal ma elan,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 38, 'vaid sa pead minema mu isakotta ja mu suguvõsa juurde ning sealt võtma naise mu pojale!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 39, 'Aga mina ütlesin oma isandale: võib-olla naine ei tule minuga?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 40, 'Siis ta vastas mulle: Issand, Kelle palge ees ma olen käinud, läkitab Oma Ingli sinuga ja laseb su teekonna korda minna, et saad mu pojale naise võtta minu suguvõsast ja minu isakojast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 41, 'Mu vandest sa vabaned sel juhul, kui sa tuled mu suguvõsa juurde, aga nemad ei anna sulle, siis oled mu vandest vaba.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 42, 'Ma jõudsin täna allika juurde ja ütlesin: Issand, mu isanda Aabrahami Jumal, kui Sa nüüd tahad korda saata mu teekonna, mida ma käin,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 43, 'siis vaata, ma seisan veeallika juures. Kui üks neitsi tuleb vett viima ja ma temale ütlen: anna mulle oma kruusist pisut vett juua!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 44, 'ja kui tema mulle vastab: joo ise, ja ma ammutan ka su kaamelitele! siis on tema see naine, kelle Issand on määranud mu isanda pojale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 45, 'Veel enne kui olin kõneluse iseenesega lõpetanud, vaata, siis tuli Rebeka, kruus õlal, ja läks alla allika juurde ning ammutas vett. Ja ma ütlesin temale: anna mulle juua!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 46, 'Siis ta tõstis kähku kruusi õlalt alla ning ütles: joo, ja ma joodan ka su kaameleid. Ja mina jõin ning tema jootis mu kaameleid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 47, 'Ja ma küsisin temalt ning ütlesin: kelle tütar sa oled? Ja tema vastas: Betueli, Naahori ja Milka poja tütar. Siis ma panin temale rõnga ninasse ja käevõrud kätele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 48, 'kummardasin ning heitsin Issanda ette ja kiitsin Issandat, oma isanda Aabrahami Jumalat, Kes mind oli juhatanud õigele teele, et saaksin võtta oma isanda vennatütre ta pojale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 49, 'Ja kui te nüüd tahate osutada heldust ja truudust mu isandale, siis öelge mulle. Aga kui mitte, öelge sedagi mulle, ja ma pöördun siis kas paremat või vasakut kätt!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 50, 'Seepeale vastasid Laaban ja Betuel ning ütlesid: „Issandalt on see asi alguse saanud! Me ei või sulle sõnagi lausuda, ei halba ega head.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 51, 'Vaata, Rebeka on su ees, võta tema ja mine! Saagu ta naiseks su isanda pojale, nõnda nagu Issand on öelnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 52, 'Kui Aabrahami sulane kuulis nende sõnu, siis ta kummardas maani Issanda ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 53, 'Ja sulane võttis välja hõbe- ja kuldriistad ja riided ning andis Rebekale; ka tema vennale ja emale andis ta kalleid asju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 54, 'Siis nad sõid ja jõid, tema ja mehed, kes ühes temaga olid, ja nad ööbisid seal. Aga hommikul, kui nad olid tõusnud, ütles ta: „Saatke mind nüüd mu isanda juurde!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 55, 'Aga tütarlapse vend ja ema vastasid: „Jäägu tütarlaps veel mõneks ajaks, kas või kümneks päevakski meie juurde. Siis sa võid minna.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 56, 'Kuid tema ütles neile: „Ärge mind viivitage, sest Issand on lasknud mu teekonna korda minna. Saatke mind teele, et saaksin minna oma isanda juurde!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 57, 'Siis nad ütlesid: „Me kutsume tütarlapse ja küsime tema suust!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 58, 'Ja nad kutsusid Rebeka ning küsisid temalt: „Kas tahad minna ühes selle mehega?\" Ja ta vastas: „Ma lähen!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 59, 'Siis nad saatsid ära oma õe Rebeka ja tema imetaja, ja Aabrahami sulase ja tema mehed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 60, 'Ja nad õnnistasid Rebekat ning ütlesid temale: „Õeke, sinust tulgu tuhat korda kümme tuhat, ja sinu sugu vallutagu oma vihameeste väravad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 61, 'Siis Rebeka ja tema tüdrukud tõusid, istusid kaamelite selga ning järgnesid mehele. Nõnda võttis sulane Rebeka ja läks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 62, 'Iisak aga oli tulemas Lahhai-Roi kaevu poolt, sest ta elas Lõunamaal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 63, 'Ja Iisak oli vastu õhtut läinud väljale mõtisklema; ja kui ta oma silmad üles tõstis ja vaatas, ennäe, siis tulid kaamelid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 64, 'Kui Rebeka oma silmad üles tõstis ja nägi Iisakit, siis ta laskus kaameli seljast');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 65, 'ning küsis sulaselt: „Kes on see mees, kes meile väljal vastu tuleb?\" Ja sulane vastas: „See on mu isand!\" Siis Rebeka võttis loori ja kattis ennast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 66, 'Ja sulane jutustas Iisakile kõigest, mis ta oli teinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 67, 'Ja Iisak viis Rebeka oma ema Saara telki; ja ta võttis Rebeka, see sai tema naiseks ja ta armastas teda. Nõnda leidis Iisak troosti pärast oma ema surma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 1, 'Ja Aabraham võttis taas naise, nimega Ketuura.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 2, 'Ja see tõi temale ilmale Simrani, Joksani, Medani, Midjani, Jisbaki ja Suua.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 3, 'Ja Joksanile sündisid Seeba ja Dedan; ja Dedani järeltulijad olid assüürlased, letuuslased ja leumlased.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 4, 'Ja Midjani pojad olid Eefa, Eefer, Hanok, Abiida ja Eldaa. Need kõik olid Ketuura järeltulijad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 5, 'Ja Aabraham andis kõik, mis tal oli, Iisakile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 6, 'Aga liignaiste poegadele, kes Aabrahamil olid, Aabraham andis ande ja saatis nad veel oma eluajal oma poja Iisaki juurest ära hommiku poole, Hommikumaale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 7, 'Ja Aabrahami eluea aastaid, mis ta elas, oli sada seitsekümmend viis aastat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 8, 'Ja Aabraham heitis hinge ning suri heas vanuses, vana ning elatanud, ja ta koristati oma rahva juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 9, 'Ja ta pojad Iisak ja Ismael matsid tema Makpela koopasse, hetiit Sohari poja Efroni väljal, mis on Mamre kohal,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 10, 'väljale, mille Aabraham hetiitidelt oli ostnud, maeti Aabraham ja tema naine Saara.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 11, 'Ja pärast Aabrahami surma Jumal õnnistas ta poega Iisakit. Ja Iisak elas Lahhai-Roi kaevu juures.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 12, 'Ja need olid Ismaeli, Aabrahami poja järeltulijad, kelle egiptlanna Haagar, Saara ümmardaja, Aabrahamile ilmale tõi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 13, 'Ismaeli poegade nimed, nimetatud nende sündimise järjekorras, olid need: Nebajot, Ismaeli esmasündinu, siis Keedar, Adbeel, Mibsam,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 14, 'Misma, Duuma, Massa,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 15, 'Hadad, Teema, Jetuur, Naafis ja Keedma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 16, 'Need olid Ismaeli pojad ja need olid nende nimed vastavalt nende asulatele ja leeridele: nende suguharude kaksteist vürsti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 17, 'Ja need olid Ismaeli eluaastad: sada kolmkümmend seitse aastat; siis ta heitis hinge ja suri, ja ta koristati oma rahva juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 18, 'Ja nad asusid Havilast kuni vastu Egiptust oleva Suurini Assuri suunas, tungides kallale kõigile oma vendadele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 19, 'Ja need olid Iisaki, Aabrahami poja järeltulijad: Aabrahamile sündis Iisak.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 20, 'Ja Iisak oli neljakümneaastane, kui ta võttis enesele naiseks Rebeka, süürlase Betueli tütre Mesopotaamiast, süürlase Laabani õe.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 21, 'Ja Iisak palus Issandat oma naise pärast, sest see oli viljatu; ja Issand kuulis ta palvet ja ta naine Rebeka sai käima peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 22, 'Aga kui lapsed ta ihus tõuklesid, siis ta ütles: „Kui nõnda, mispärast siis just mina pean see olema?\" Ja ta läks Issandalt küsima.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 23, 'Ja Issand vastas temale: „Su ihus on kaks rahvast, kaks erinevat hõimu su üsast alates: üks rahvas on vägevam teisest - vanem orjab nooremat!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 24, 'Ja kui tema sünnitamise aeg saabus, vaata, siis olid ta ihus kaksikud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 25, 'See, kes väljus esimesena, oli punakas, täiesti nagu karune kuub; ja temale pandi nimeks Eesav.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 26, 'Seejärele väljus ta vend, kelle käsi hoidis kinni Eesavi kannast; ja temale pandi nimeks Jaakob. Iisak oli kuuskümmend aastat vana, kui nad sündisid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 27, 'Ja poisid kasvasid suureks. Eesavist sai osav kütt, väljal uitaja, kuna Jaakobist vagane mees, kes elas telkides.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 28, 'Ja Iisak armastas Eesavit, sest jahisaak oli temale suurepärane; aga Rebeka armastas Jaakobit.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 29, 'Kord Jaakob keetis leent, Eesav aga tuli väljalt ning oli väsinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 30, 'Ja Eesav ütles Jaakobile: „Anna mulle ometi süüa seda punast, seda punast leent, sest ma olen väsinud!\" Sellepärast hakati teda kutsuma Edomiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 31, 'Aga Jaakob ütles: „Enne müü mulle oma esmasünniõigus!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 32, 'Ja Eesav vastas: „Vaata, mina ju suren niikuinii, milleks mulle siis veel esmasünniõigus!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 33, 'Siis ütles Jaakob: „Vannu mulle enne!\" Ja ta vandus temale ning müüs oma esmasünniõiguse Jaakobile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 34, 'Ja Jaakob andis Eesavile leiba ja läätseleent; ja tema sõi ja jõi, tõusis üles ja läks ära. Nii vähe hoolis Eesav esmasünniõigusest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 1, 'Aga maal oli nälg, pärast seda eelmist nälga, mis Aabrahami päevil oli olnud. Ja Iisak läks vilistide kuninga Abimeleki juurde Gerarisse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 2, 'Ja Issand ilmutas Ennast temale ning ütles: „Ära mine alla Egiptusesse! Jää maale, kuhu Ma sind käsin!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 3, 'Ela võõrana siin maal, ja Ma olen sinuga ning õnnistan sind, sest sinule ja sinu soole Ma annan kõik need maad ning pean vannet, mille Ma olen vandunud su isale Aabrahamile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 4, 'Ja Ma teen su soo paljuks nagu taevatähed ja annan su soole kõik need maad, ja sinu soo nimel õnnistavad endid kõik maailma rahvad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 5, 'sellepärast et Aabraham kuulis Mu sõna ja pidas, mis Ma käskisin pidada - Mu käske, seadlusi ja õpetusi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 6, 'Ja Iisak jäi elama Gerarisse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 7, 'Kui kohalikud mehed küsisid tema naise kohta, siis ta ütles: „See on mu õde!\" Sest ta kartis öelda: „See on mu naine!\", mõeldes ise: „Muidu kohalikud mehed tapavad mind Rebeka pärast, kuna ta on ilusa välimusega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 8, 'Aga kui ta seal pikemat aega oli viibinud, vaatas Abimelek, vilistide kuningas, kord aknast välja ja nägi, et vaata, Iisak kallistas oma naist Rebekat!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 9, 'Siis Abimelek kutsus Iisaki ja ütles: „Vaata, ta on tõepoolest su naine! Kuidas sa siis võisid öelda: ta on mu õde?\" Ja Iisak vastas temale: „Ma mõtlesin, et muidu ma ehk pean tema pärast surema.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 10, 'Aga Abimelek ütles: „Miks sa meile seda tegid? Kui kergesti oleks võinud keegi rahva hulgast magada su naisega ja sa oleksid meie peale toonud süü!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 11, 'Ja Abimelek andis käsu kogu rahvale, öeldes: „Kes puudutab seda meest ja tema naist, peab surema!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 12, 'Ja Iisak külvas seal maal ja sai sel aastal sajakordselt, sest Issand õnnistas teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 13, 'Ja mees läks rikkaks, läks üha rikkamaks, kuni ta oli läinud väga rikkaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 14, 'Ja temal oli pudulojuste karju ja veiste karju ja palju peret, nõnda et vilistid teda kadestasid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 15, 'Ja vilistid matsid kinni ja täitsid mullaga kõik kaevud, mis tema isa sulased olid kaevanud tema isa Aabrahami päevil.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 16, 'Ja Abimelek ütles Iisakile: „Mine ära meie juurest, sest sa oled saanud meist palju vägevamaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 17, 'Siis Iisak läks sealt ära ja lõi oma telgid üles Gerari orgu ning elas seal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 18, 'Ja Iisak kaevas uuesti need veekaevud, mis tema isa Aabrahami päevil olid kaevatud ja mis vilistid pärast Aabrahami surma olid kinni matnud; ja ta pani neile needsamad nimed, mis tema isa neile oli pannud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 19, 'Aga kui Iisaki sulased kaevasid orus ja leidsid seal voolava veega kaevu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 20, 'siis Gerari karjased riidlesid Iisaki karjastega, öeldes: „Vesi on meie oma!\" Ta pani siis kaevule nimeks Eesek, sellepärast et nad temaga olid tülitsenud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 21, 'Siis nad kaevasid teise kaevu, ja selle pärast riidlesid nad ka; ja sellele ta pani nimeks Sitna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 22, 'Sealt ta siirdus edasi ja kaevas veel ühe kaevu, aga selle pärast nad ei riielnud; ja sellele ta pani nimeks Rehobot ning ütles: „Nüüd on Issand andnud meile avarust, et võiksime siin maal olla viljakad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 23, 'Ja sealt ta läks üles Beer-Sebasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 24, 'Ja Issand ilmutas Ennast temale selsamal ööl ning ütles: „Mina olen su isa Aabrahami Jumal! Ära karda, sest Ma olen sinuga ja õnnistan sind! Ma teen su soo paljuks Oma sulase Aabrahami pärast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 25, 'Siis ta ehitas sinna altari, hüüdis appi Issanda nime ja lõi sinna oma telgi üles; ja Iisaki sulased kaevasid sinna kaevu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 26, 'Ja Abimelek tuli Gerarist tema juurde ühes oma sõbra Ahusati ja väepealiku Piikoliga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 27, 'Aga Iisak ütles neile: „Miks tulete minu juurde? Te ju vihkate mind ja olete mind eneste juurest ära saatnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 28, 'Ja nemad vastasid: „Me näeme selgesti, et Issand on sinuga! Seepärast me ütleme: olgu meie vahel vanne, meie ja sinu vahel, ja me teeme sinuga lepingu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 29, 'et sa meile kurja ei tee, nõnda nagu me sinusse ei ole puutunud, vaid oleme sulle ainult head teinud ja sind rahuga ära saatnud. Sina oled ju nüüd Issanda õnnistatu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 30, 'Siis ta tegi neile võõruspeo ning nad sõid ja jõid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 31, 'Ja nad tõusid hommikul vara ning andsid üksteisele vande. Siis Iisak saatis nad ära ja nad läksid ta juurest rahuga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 32, 'Ja selsamal päeval tulid Iisaki sulased ning teatasid temale kaevust, mille nad olid kaevanud, ja ütlesid temale: „Me leidsime vett!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 33, 'Siis ta nimetas selle Sibaks; seepärast on linna nimeks tänapäevani Beer-Seba.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 34, 'Kui Eesav oli nelikümmend aastat vana, siis ta võttis naiseks Juuditi, hetiit Beeri tütre, ja Baasmati, hetiit Eeloni tütre.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 35, 'Aga need olid meelehärmiks Iisakile ja Rebekale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 1, 'Kui Iisak oli vanaks jäänud ja ta silmanägemine oli tuhmunud, siis ta kutsus Eesavi, oma vanema poja, ja ütles temale: „Mu poeg!\" Ja see vastas temale: „Siin ma olen!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 2, 'Ja ta ütles: „Vaata, ma olen vanaks jäänud ega tea oma surmapäeva.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 3, 'Võta nüüd oma jahiriistad, nooletupp ja amb, mine väljale ja küti mulle mõni jahiloom.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 4, 'Valmista siis mulle maitsvat rooga, mida ma armastan, ja too mulle süüa, et mu hing sind õnnistaks enne kui ma suren.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 5, 'Aga Rebeka kuulis, kui Iisak rääkis oma poja Eesaviga. Ja kui Eesav oli läinud väljale küttima ja jahisaaki tooma,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 6, 'siis rääkis Rebeka oma poja Jaakobiga, öeldes: „Vaata, ma kuulsin su isa rääkivat su venna Eesaviga ja ütlevat:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 7, 'too mulle jahisaaki ja valmista mulle maitsvat rooga, et ma söön ja sind Issand ees õnnistan enne kui ma suren.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 8, 'Ja nüüd, mu poeg, kuule mu sõna ja tee, mida ma sind käsin:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 9, 'mine pudulojuste juurde ja võta mulle sealt kaks head sikutalle ja ma valmistan need su isale maitsvaks roaks, mida ta armastab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 10, 'Sina vii need siis oma isale, et ta sööks ja sind õnnistaks enne kui ta sureb!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 11, 'Aga Jaakob ütles oma emale Rebekale: „Vaata, mu vend Eesav on karune, aga mina olen sile!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 12, 'Võib-olla katsub isa mind käega, siis oleksin tema silmis nagu petis ja tooksin enesele needuse, mitte õnnistuse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 13, 'Aga ta ema ütles talle: „Sinu needmine tulgu minu peale, mu poeg! Kuule ainult mu sõna ja mine too mulle!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 14, 'Siis ta läks ja võttis need ning tõi oma emale; ja ta ema valmistas maitsva roa, mida ta isa armastas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 15, 'Ja Rebeka võttis oma vanema poja Eesavi parimad riided, mis ta juures kodus olid, ja pani need selga oma nooremale pojale Jaakobile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 16, 'Tema käte ja sileda kaela ümber aga pani ta sikutallede nahad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 17, 'Siis ta andis maitsva roa ja leiva, mille ta oli valmistanud, oma poja Jaakobi kätte,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 18, 'ja see läks oma isa juurde ning ütles: „Mu isa!\" Ja tema vastas: „Siin ma olen! Kumb sa oled, mu poeg?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 19, 'Ja Jaakob ütles oma isale: „Mina olen Eesav, sinu esmasündinu! Ma tegin, nagu sa mind käskisid. Tõuse, istu ja söö mu jahisaaki, et su hing mind õnnistaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 20, 'Aga Iisak küsis oma pojalt: „Kuidas sa nii kähku leidsid, mu poeg?\" Ja tema vastas: „Issand, sinu Jumal, saatis mulle ette!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 21, 'Siis Iisak ütles Jaakobile: „Tule ometi ligemale, et ma sind käega katsun, mu poeg, kas sa oled mu poeg Eesav või mitte?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 22, 'Ja Jaakob astus oma isa Iisaki juurde, ja tema katsus teda käega ning ütles: „Hääl on Jaakobi hääl, aga käed on Eesavi käed!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 23, 'Ja ta ei tundnud teda ära, sest tema käed olid karused nagu ta venna Eesavi käed; ja ta õnnistas teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 24, 'Ta küsis veel kord: „Kas sa oled tõesti mu poeg Eesav?\" Ja ta vastas: „Olen!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 25, 'Siis ta ütles: „Ulata mulle ja ma söön oma poja jahisaaki, et mu hing sind õnnistaks!\" Ja ta ulatas temale selle, ja ta sõi; ja ta tõi temale viina, ja ta jõi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 26, 'Seejärel ütles ta isa Iisak temale: „Tule nüüd ligemale ja anna mulle suud, mu poeg!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 27, 'Ja ta astus ligi ning andis temale suud; siis ta tundis tema riiete lõhna ja ta õnnistas teda ning ütles: „Näe, mu poja lõhn - otsekui välja lõhn, mida Issand on õnnistanud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 28, 'Jumal andku sulle taeva kastet ja maa rammu, ning külluses vilja ja viina!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 29, 'Rahvad orjaku sind, rahvahõimud kummardagu sind! Ole oma vendade isand, su ema pojad kummardagu sind! Neetud olgu, kes sind neab, õnnistatud, kes sind õnnistab!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 30, 'Ja kui Iisak oli Jaakobit õnnistanud ja kui Jaakob just oli väljunud oma isa Iisaki juurest, siis tuli tema vend Eesav küttimast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 31, 'Ja temagi valmistas maitsva roa ja viis oma isa juurde ning ütles isale: „Tõuse, mu isa, ja söö oma poja jahisaaki, et su hing mind õnnistaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 32, 'Aga tema isa Iisak küsis temalt: „Kes sa oled?\" Ja ta vastas: „Mina olen su poeg Eesav, su esmasündinu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 33, 'Siis Iisak värises üpris väga suurest ärritusest ja ütles: „Kes oli siis see, kes jahilooma küttis ja mulle tõi? Ja mina sõin kõike, enne kui sa tulid, ning õnnistasin teda! Õnnistatuks ta jääbki!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 34, 'Kui Eesav kuulis oma isa sõnu, siis ta kisendas üpris väga valjusti ja kibedasti ning ütles oma isale: „Õnnista ka mind, mu isa!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 35, 'Aga ta vastas: „Su vend tuli kavalusega ja võttis su õnnistuse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 36, 'Siis ta ütles: „Eks ole temale nimeks pandud Jaakob? Juba teist korda on ta mind petnud: ta võttis mu esmasünniõiguse, ja vaata, nüüd ta võttis ka mu õnnistuse!\" Ja ta küsis: „Kas sul pole hoitud õnnistust eraldi minu jaoks?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 37, 'Aga Iisak vastas ning ütles Eesavile: „Vaata, ma olen pannud ta sinu isandaks ja olen andnud kõik ta vennad temale sulaseiks, ja ma olen teda varustanud vilja ja viinaga. Mida võiksin siis nüüd teha sinu heaks, mu poeg?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 38, 'Ja Eesav ütles oma isale: „Ons see sul ainus õnnistus, mu isa? Õnnista ka mind, mu isa!\" Ja Eesav tõstis häält ning nuttis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 39, 'Siis vastas tema isa Iisak ning ütles temale: „Vaata, su eluase on eemal rammusast maast ja ilma taeva kasteta ülalt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 40, 'Sa elad oma mõõga varal ja pead oma venda orjama! Ometi sünnib, kui end raputad, et rebid tema ikke oma kaelast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 41, 'Ja Eesav hakkas Jaakobit vihkama õnnistuse pärast, millega ta isa teda oli õnnistanud; ja Eesav mõtles iseeneses: „Küllap tulevad mu isa leinamise päevad, siis ma tapan oma venna Jaakobi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 42, 'Kui Rebekale tehti teatavaks ta vanema poja Eesavi mõtted, siis ta laskis kutsuda oma noorema poja Jaakobi ja ütles temale: „Vaata, sinu vend Eesav trööstib ennast sellega, et ta su tapab!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 43, 'Aga nüüd, mu poeg, kuule mu sõna! Võta kätte ja põgene mu venna Laabani juurde Haaranisse');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 44, 'ja jää tema juurde mõneks ajaks, kuni su venna raev on raugenud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 45, 'kuni su venna viha sinu pärast on möödunud ja ta unustab, mis sa temale oled teinud. Siis ma läkitan sulle järele ja lasen sind sealt ära tuua. Miks peaksin teid mõlemaid kaotama ühel ja samal päeval?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 46, 'Ja Rebeka ütles Iisakile: „Ma olen elust tüdinud hetiiditaride pärast. Kui Jaakob võtab naise hetiiditaride hulgast, selle maa tütreist, niisuguse nagu need, mis elu mul siis on?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 1, 'Siis Iisak kutsus Jaakobi ja õnnistas teda; ja ta keelas teda ning ütles temale: „Ära võta naist kaananlaste tütreist!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 2, 'Võta kätte, mine Mesopotaamiasse oma emaisa Betueli kotta ja võta sealt enesele naine oma ema venna Laabani tütreist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 3, 'Kõigeväeline Jumal õnnistagu sind, tehku sind viljakaks ja paljuks, et sinust tuleks hulk rahvaid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 4, 'Ta andku sulle Aabrahami õnnistust, sinule ja su soole ühes sinuga, et sa päriksid maa, kus sa võõrana elad, mille Jumal on andnud Aabrahamile!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 5, 'Ja Iisak saatis Jaakobi teele ning see läks Mesopotaamiasse süürlase Betueli poja Laabani juurde, kes oli Jaakobi ja Eesavi ema Rebeka vend.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 6, 'Kui Eesav nägi, et Iisak oli õnnistanud Jaakobit ja oli saatnud ta Mesopotaamiasse sealt enesele naist võtma, olles teda õnnistanud ja keelanud, öeldes: „Ära võta naist kaananlaste tütreist!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 7, 'ja et Jaakob oli kuulanud oma isa ja ema ja oli läinud Mesopotaamiasse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 8, 'siis Eesav mõistis, et kaananlaste tütred olid pahad ta isa Iisaki silmis,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 9, 'ja Eesav läks Ismaeli juurde ning võttis oma naiste kõrvale enesele naiseks Mahalati, Aabrahami poja Ismaeli tütre, Nebajoti õe.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 10, 'Jaakob aga väljus Beer-Sebast ja läks Haarani poole.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 11, 'Ta sattus ühte paika ja ööbis seal, sest päike oli loojunud; ta võttis selle paiga kividest ühe, pani enesele peaaluseks ja heitis sinna paika magama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 12, 'Ja ta nägi und, ja vaata, maa peal seisis redel, mille ots ulatus taevasse, ja ennäe, Jumala Inglid astusid seda mööda üles ja alla!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 13, 'Ja vaata, Issand seisis tema ees ning ütles: „Mina olen Issand, su isa Aabrahami Jumal ja Iisaki Jumal. Maa, mille peal sa magad, Ma annan sinule ja su soole!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 14, 'Ja sinu sugu saab maapõrmu sarnaseks ja sa levid õhtu ja hommiku, põhja ja lõuna poole, ja sinu ja su soo nimel õnnistavad endid kõik maailma suguvõsad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 15, 'Ja vaata, Mina olen sinuga ja hoian sind kõikjal, kuhu sa lähed ning toon sind taas sellele pinnale, sest Ma ei jäta sind maha, kuni olen teinud, mis Ma sulle olen öelnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 16, 'Siis Jaakob ärkas unest ja ütles: „Issand on tõesti selles paigas, mina aga ei teadnud seda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 17, 'Ja ta kartis ning ütles: „Küll on see paik kardetav! See pole muud midagi kui Jumala koda ja taeva värav!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 18, 'Ja Jaakob tõusis hommikul vara ning võttis kivi, mille ta oli pannud enesele peaaluseks, ja pani selle sambaks püsti ning valas selle otsa peale õli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 19, 'Ja ta pani sellele paigale nimeks Peetel; enne aga oli selle linna nimi Luus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 20, 'Ja Jaakob andis tõotuse, öeldes: „Kui Jumal on minuga ja hoiab mind teel, mida käin, ja annab mulle leiba süüa ja riided selga,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 21, 'ja mina võin rahuga pöörduda oma isakotta, siis on Issand mulle Jumalaks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 22, 'ja see kivi, mille ma panin sambaks, saab Jumala kojaks! Ja kõigest, mis Sa mulle annad, ma annan Sulle täpselt kümnist!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 1, 'Ja Jaakob läks teele ning jõudis hommikumaa poegade maale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 2, 'Ta vaatas, ja ennäe, väljal oli kaev! Ja vaata, sealsamas, selle kõrval, lebas kolm pudulojuste karja, sest sellest kaevust joodeti karju; kaevu suul aga oli suur kivi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 3, 'Kui kõik karjad olid kogunenud sinna, siis veeretati kivi kaevu suult, joodeti pudulojuseid ja seati kivi tagasi paika kaevu suule.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 4, 'Ja Jaakob küsis neilt: „Vennad, kust te olete?\" Ja nad vastasid: „Me oleme Haaranist.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 5, 'Siis ta küsis neilt: „Kas tunnete Laabanit, Naahori poega?\" Ja nad vastasid: „Tunneme küll!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 6, 'Ta küsis neilt: „Kuidas ta käsi käib?\" Ja nad vastasid: „Hästi! Ja näe, sealt tuleb tema tütar Raahel pudulojustega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 7, 'Ta ütles: „Vaata, päike on alles kõrgel, pole veel aeg karja kokku ajada. Jootke pudulojuseid ja minge söötke neid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 8, 'Aga nad vastasid: „Me ei saa enne kui kõik karjad on koos. Siis veeretatakse kivi kaevu suult ja me saame joota pudulojuseid.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 9, 'Kui ta alles nendega rääkis, tuli Raahel oma isa pudulojustega, sest ta oli neid hoidmas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 10, 'Ja kui Jaakob nägi Raahelit, oma ema venna Laabani tütart ja oma ema venna Laabani pudulojuseid, siis Jaakob astus ligi ja veeretas kivi kaevu suult ning jootis oma ema venna Laabani pudulojuseid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 11, 'Siis Jaakob suudles Raahelit, tõstis häält ja nuttis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 12, 'Ja Jaakob andis Raahelile teada, et ta on tema isa sugulane ja Rebeka poeg; ja Raahel jooksis ning teatas oma isale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 13, 'Ja kui Laaban kuulis sõnumit oma õepojast Jaakobist, siis ta jooksis temale vastu, kaelustas ja suudles teda ning viis ta oma kotta; ja ta jutustas Laabanile kõik, mis oli sündinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 14, 'Siis ütles Laaban temale: „Sa oled tõesti minu luu ja liha!\" Ja ta jäi tema juurde kuuks ajaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 15, 'Ja Laaban ütles Jaakobile: „Kas sa sellepärast, et oled mu sugulane, peaksid mind teenima ilma palgata? Nimeta mulle oma palk!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 16, 'Laabanil aga oli kaks tütart; vanema nimi oli Lea ja noorema nimi oli Raahel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 17, 'Leal olid läiketa silmad, aga Raahel oli jumekas ja ilusa välimusega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 18, 'Jaakob armastas Raahelit, seepärast ta ütles: „Ma teenin sind seitse aastat su noorema tütre Raaheli pärast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 19, 'Laaban vastas: „Ma annan ta parem sinule kui mõnele teisele mehele. Jää minu juurde.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 20, 'Ja Jaakob teenis Raaheli pärast seitse aastat, ja need olid tema silmis nagu üksikud päevad, sellepärast et ta teda armastas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 21, 'Siis Jaakob ütles Laabanile: „Anna mu naine mulle kätte, sest aeg on täis, et ma võin minna tema juurde!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 22, 'Laaban koguski kokku kõik selle paiga mehed ja tegi peo.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 23, 'Aga õhtul ta võttis oma tütre Lea ja viis selle tema juurde; ja Jaakob heitis ta juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 24, 'Ja Laaban andis oma ümmardaja Silpa oma tütrele Leale ümmardajaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 25, 'Jõudis hommik, ja vaata, see oli Lea! Siis ütles Jaakob Laabanile: „Mis sa mulle oled teinud! Eks ma ole Raaheli pärast sind teeninud? Mispärast sa mind petsid?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 26, 'Aga Laaban vastas: „Ei ole meie pool kombeks anda noorem enne vanemat!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 27, 'Pea sellega pulmanädal ära, siis me anname ka teise sulle teenistuse eest, kui sa mind veel teist seitse aastat teenid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 28, 'Ja Jaakob tegi nõnda ning pidas sellega pulmanädala ära, siis ta andis oma tütre Raaheli temale naiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 29, 'Ja Laaban andis oma ümmardaja Billa oma tütrele Raahelile ümmardajaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 30, 'Siis Jaakob heitis ka Raaheli juurde, ta armastas ju Raahelit ikkagi rohkem kui Leat; ja ta teenis Laabanit veel teist seitse aastat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 31, 'Ent kui Issand nägi, et Lea hüljati, siis ta avas tema üsa; Raahel aga oli viljatu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 32, 'Ja Lea sai käima peale ja tõi poja ilmale ning pani temale nimeks Ruuben, sest ta ütles: „Issand on mu alandust näinud! Küllap mu mees hakkab nüüd mind armastama!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 33, 'Ja ta sai taas käima peale ja tõi poja ilmale ning ütles: „Issand on kuulnud, et mind hüljati! Seepärast on ta mulle ka selle andnud!\" Ja ta pani temale nimeks Siimeon.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 34, 'Ja tema sai taas käima peale ja tõi poja ilmale ning ütles: „Nüüd viimaks mu mees kiindub minusse, sest ma olen temale kolm poega ilmale toonud!\" Seepärast pandi sellele nimeks Leevi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 35, 'Ja tema sai taas käima peale ja tõi poja ilmale ning ütles: „Nüüd ma kiidan Issandat!\" Seepärast ta pani temale nimeks Juuda. Siis ta lakkas sünnitamast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 1, 'Kui Raahel nägi, et ta ei toonud Jaakobile lapsi ilmale, siis Raahel kadestas oma õde ja ütles Jaakobile: „Muretse mulle lapsi, muidu ma suren!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 2, 'Aga Jaakobi viha süttis põlema Raaheli vastu ja ta küsis: „Kas mina olen Jumala asemik, kes sulle ihuvilja keelab?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 3, 'Ja Raahel vastas: „Vaata, seal on mu orjatar Billa. Heida tema juurde, et ta sünnitaks lapsi mu põlvede peale ja minagi saaksin nõnda temalt järglasi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 4, 'Ja ta andis temale naiseks oma ümmardaja Billa ning Jaakob heitis selle juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 5, 'Ja Billa sai käima peale ning tõi Jaakobile poja ilmale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 6, 'Siis ütles Raahel: „Jumal tegi mulle õigust! Ta kuulis ka mu häält ja andis mulle poja!\" Seepärast ta pani temale nimeks Daan.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 7, 'Ja Billa, Raaheli ümmardaja, sai taas käima peale ning tõi Jaakobile teise poja ilmale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 8, 'Siis ütles Raahel: „Ma olen oma õega võidelnud Jumala võitlust ja olen võitnud!\" Ja ta pani temale nimeks Naftali.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 9, 'Kui Lea nägi, et ta oli lakanud sünnitamast, siis ta võttis oma ümmardaja Silpa ja andis selle Jaakobile naiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 10, 'Ja Silpa, Lea ümmardaja, tõi Jaakobile poja ilmale');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 11, 'ning Lea ütles: „Õnneks!\" Ja ta pani temale nimeks Gaad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 12, 'Ja Silpa, Lea ümmardaja, tõi Jaakobile teise poja ilmale');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 13, 'ning Lea ütles: „Ma olen õnnelik! Tõesti, naised kiidavad mind õnnelikuks!\" Ja ta pani temale nimeks Aaser.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 14, 'Kord läks Ruuben nisulõikuse ajal ja leidis väljalt lemmemarju ja tõi neid oma emale Leale. Ja Raahel ütles Leale: „Anna ka minule oma poja lemmemarju!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 15, 'Aga ta vastas temale: „Kas on veel vähe, et sa võtsid mu mehe? Nüüd tahad sa ka mu poja lemmemarjad ära võtta!\" Siis ütles Raahel: „Vastutasuks magagu ta täna öösel sinu juures su poja lemmemarjade eest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 16, 'Kui Jaakob tuli õhtul väljalt, siis läks Lea temale vastu ja ütles: „Sa pead minu juurde heitma, sest ma olen sind tinginud tasu eest, oma poja lemmemarjade eest!\" Ja tema magas sel ööl ta juures.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 17, 'Ja Jumal kuulis Lead, ja Lea sai käima peale ja tõi Jaakobile viienda poja ilmale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 18, 'Ja Lea ütles: „Jumal tasus mulle, et ma andsin oma ümmardaja oma mehele!\" Ja ta pani temale nimeks Issaskar.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 19, 'Ja Lea sai taas käima peale ja tõi Jaakobile kuuenda poja ilmale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 20, 'Ja Lea ütles: „Jumal valmistas mulle ilusa kingituse! Nüüd mu mees hakkab mind sallima, sest ma olen temale kuus poega ilmale toonud!\" Ja ta pani temale nimeks Sebulon.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 21, 'Ja pärastpoole ta tõi tütre ilmale ning pani temale nimeks Diina.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 22, 'Aga Jumal mõtles Raahelile, ja Jumal kuulis teda ning avas tema üsa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 23, 'Ja ta sai käima peale ja tõi poja ilmale ning ütles: „Jumal võttis ära mu teotuse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 24, 'Ja ta pani temale nimeks Joosep, öeldes: „Annaks Issand mulle lisaks veel teisegi poja!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 25, 'Ja kui Raahel oli Joosepi ilmale toonud, siis Jaakob ütles Laabanile: „Lase mind, et saaksin minna koju ja oma kodumaale!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 26, 'Anna mu naised ja lapsed, kelle pärast ma sind olen teeninud, ja ma lähen, sest sa tead ju ise, kuidas ma sind olen teeninud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 27, 'Ja Laaban vastas temale: „Kui ma nüüd sinu silmis armu leiaksin! Märgid näitavad mulle, et Issand on mind sinu pärast õnnistanud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 28, 'Ja ta ütles: „Nimeta mulle oma palk ja ma annan selle!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 29, 'Siis ta vastas temale: „Sina tead ise, kuidas ma sind olen teeninud ja mis on saanud su karjast minu juures.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 30, 'Sest pisut oli seda, mis sul oli enne mind. See on aga ohtrasti kasvanud ja Issand on sind õnnistanud minu sammude läbi. Millal ma siis nüüd saan hoolitseda ka oma pere eest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 31, 'Siis ta küsis: „Mis ma sulle pean andma?\" Ja Jaakob vastas: „Ära anna mulle midagi. Kui sa lubad mulle seda, siis ma karjatan ja hoian veelgi su pudulojuseid:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 32, 'ma käin täna läbi kõik su pudulojused, lahutades neist kõik tähnilised ja kirjud uted ja kõik indlevad noored jäärad, samuti kirjud ja tähnilised kitsede hulgast. Need olgu siis mulle palgaks');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 33, 'ja mu õigus kostku minu eest tulevikus, kui sa tuled mu palka vaatama: kõik, kes ei ole tähnilised ja kirjud kitsede hulgas ja indlevad noorte jäärade hulgas, loetagu minu poolt varastatuiks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 34, 'Ja Laaban vastas: „Hästi, sündigu tõesti su sõna järgi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 35, 'Ja ta lahutas selsamal päeval keerdsabalised ja kirjud sikud ja kõik tähnilised ja kirjud kitsed, kõik, kellel oli valget küljes, ja kõik indlevad noored jäärad, andis need oma poegade hooleks');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 36, 'ning jättis kolme päeva tee enese ja Jaakobi vahele; Jaakob aga jäi karjatama Laabani ülejäänud pudulojuseid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 37, 'Ja Jaakob võttis enesele papli-, mandli- ja plataanipuu tooreid keppe ja kooris neile valged vöödid, paljastades keppide valge värvi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 38, 'Siis ta pani kooritud kepid pudulojuste ette rennidesse ja veekünadesse, kuhu pudulojused tulid jooma; ja joomas olles nad paaritusid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 39, 'Ja kui pudulojused keppide juures paaritusid, siis pudulojused sünnitasid tallesid: keerdsabalisi, tähnilisi ja kirjusid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 40, 'Ja Jaakob eraldas noored jäärad: ta pööras isaloomade pead keerdsabaliste poole ja kõigi indlevate poole Laabani pudulojuste hulgas. Nõnda tegi ta enesele eraldi karjad ega pannud neid Laabani pudulojuste sekka.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 41, 'Ja iga kord, kui tugevamad pudulojused paaritusid, pani Jaakob kepid künadesse pudulojuste silme ette, et nad paarituksid keppide juures.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 42, 'Aga kui pudulojused olid väetimad, siis ta ei pannud. Nõnda said väetid Laabanile ja tugevamad Jaakobile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 43, 'Ja mees kosus üpris väga ja tal oli palju pudulojuseid, ümmardajaid ja sulaseid, kaameleid ja eesleid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 1, 'Aga ta kuulis Laabani poegade kõnelusi, kes ütlesid: „Jaakob on ära võtnud kõik, mis oli meie isa päralt. Sellest, mis oli meie isa päralt, on ta enesele soetanud kõik selle rikkuse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 2, 'Ja Jaakob nägi Laabani palet, ja vaata, see ei olnud enam ta vastu nagu enne!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 3, 'Siis Issand ütles Jaakobile: „Mine tagasi oma isade maale ja oma sugulaste seltsi. Mina olen sinuga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 4, 'Ja Jaakob läkitas sõna ning käskis kutsuda oma pudulojuste juurde väljale Raaheli ja Lea');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 5, 'ning ütles neile: „Ma näen teie isa palgest, et ta ei ole enam mu vastu nagu enne. Aga mu isa Jumal oli mu juures!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 6, 'Te ju teate, et ma olen teeninud teie isa kõigest väest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 7, 'Kuid teie isa narritas mind ja muutis mu palka kümme korda. Jumal aga ei ole lubanud teda mulle kurja teha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 8, 'Kui ta ütles nõnda: tähnilised saagu sinule palgaks, siis kõik pudulojused poegisid tähnilisi. Ja kui ta ütles nõnda: keerdsabalised saagu sinule palgaks, siis kõik pudulojused poegisid keerdsabalisi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 9, 'Nõnda võttis Jumal teie isa karja ja andis mulle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 10, 'Pudulojuste innaajal ma tõstsin oma silmad üles ja nägin unes, vaata, et jäärad, kes kargasid pudulojuseid, olid keerdsabalised, tähnilised ja nastlikud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 11, 'Ja Jumala Ingel ütles mulle unes: Jaakob! Ja ma vastasin: siin ma olen!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 12, 'Siis Ta ütles: tõsta ometi oma silmad üles ja vaata: kõik jäärad, kes kargavad pudulojuseid, on keerdsabalised, tähnilised ja nastlikud, sest Ma olen näinud kõike, mis Laaban sulle teeb!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 13, 'Mina olen Peeteli Jumal, kus sa võidsid samba, kus sa andsid Mulle tõotuse. Võta nüüd kätte, lahku siit maalt ja mine tagasi oma sünnimaale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 14, 'Siis Raahel ja Lea vastasid ning ütlesid temale: „Kas meil ongi enam osa või omandit meie isakojas?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 15, 'Eks ta ole pidanud meid võõraks, kuna ta meid müüs ja ise muidugi ka meie hinna ära sõi?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 16, 'Jah, kõik see rikkus, mille Jumal meie isalt ära võttis, on meie ja meie laste oma. Ja nüüd tee kõik, mis Jumal sulle on öelnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 17, 'Ja Jaakob võttis kätte, tõstis oma lapsed ja naised kaamelite selga');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 18, 'ja saatis teele kogu oma karja ja kõik oma varanduse, mis ta oli kogunud, oma karjavaranduse, mis ta Mesopotaamias oli soetanud, et minna oma isa Iisaki juurde Kaananimaale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 19, 'Aga Laaban oli läinud pudulojuseid niitma. Ja Raahel varastas oma isa teeravikujud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 20, 'Jaakob kasutas süürlase Laabani teadmatust ega andnud temale märku, et ta põgeneb.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 21, 'Nõnda ta siis põgenes ühes kõigega, mis tal oli, võttis kätte ja läks üle jõe ning siirdus Gileadi mäestiku poole.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 22, 'Aga kolmandal päeval anti Laabanile teada, et Jaakob oli põgenenud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 23, 'Tema võttis siis enesega ühes oma suguvennad ja ajas teda taga seitse päevateekonda ning jõudis Gileadi mäestikus temale järele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 24, 'Kuid Jumal tuli süürlase Laabani juurde öösel unes ja ütles temale: „Hoia, et sa Jaakobile ei ütle head ega halba!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 25, 'Kui Laaban Jaakobile järele jõudis, oli Jaakob mäestikus telgi üles löönud, ja Laabangi suguvendadega lõi telgi üles Gileadi mäestikku.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 26, 'Ja Laaban ütles Jaakobile: „Mis sa oled teinud? Sa kasutasid mu teadmatust ja viisid ära mu tütred, nagu oleksid nad olnud mõõga abil vangistatud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 27, 'Miks sa põgenesid salaja ja vargsil viisil ega teatanud mulle, et oleksin saanud sind rõõmsasti ära saata laulude, trummi ja kandlega?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 28, 'Sa ei lasknud mind suudelda oma poegi ja tütreid! Sa oled nüüd talitanud mõistmatult.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 29, 'Mul oleks meelevald teha teile kurja! Aga teie isa Jumal rääkis minuga eile öösel, öeldes: hoia, et sa Jaakobile ei ütle head ega halba!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 30, 'Nüüd oled sa küll läinud oma teed, sellepärast et sa igatsesid nii väga oma isakoja järele. Aga mispärast sa varastasid mu jumalad?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 31, 'Ja Jaakob vastas ning ütles Laabanile: „Sellepärast et ma kartsin! Sest ma mõtlesin, et sa röövid minult oma tütred.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 32, 'See, kelle juurest sa leiad oma jumalad, ärgu jäägu elama! Meie suguvendade ees otsi läbi, mis mul kaasas on, ja võta ära, mis on sinu!\" Aga Jaakob ei teadnud, et Raahel oli need varastanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 33, 'Ja Laaban läks Jaakobi telki ja Lea telki ja mõlemate ümmardajate telki, aga ei leidnud midagi; ja Lea telgist väljudes ta läks Raaheli telki.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 34, 'Kuid Raahel oli võtnud teeravid ja oli pannud need kaameli sadula tasku ning istus ise nende peal. Ja Laaban kompas läbi kogu telgi, aga ei leidnud midagi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 35, 'Ja Raahel ütles oma isale: „Ärgu süttigu viha mu isanda silmis, et ma ei saa su ees üles tõusta, sest mul on naiste asjad!\" Nõnda ta otsis läbi, aga teeraveid ta ei leidnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 36, 'Siis Jaakob vihastus ja riidles Laabaniga. Ja Jaakob kostis ning ütles Laabanile: „Milles seisneb mu üleastumine? Mis on mu patt, et oled mind nii tulisi jalu taga ajanud?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 37, 'Kuna sa oled läbi otsinud kogu mu kraami, siis missuguse oma koja riista sa oled leidnud? Pane siia minu suguvendade ja oma suguvendade ette, et nad võiksid õigust mõista meie mõlemate vahel!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 38, 'Ma olin sinu juures kakskümmend aastat! Su emalambad ja kitsed ei heitnud loodet ja jääri su karjast ma ei söönud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 39, 'Murtut ma sulle ei toonud, ma pidin selle hüvitama. Sa nõudsid minult niihästi päeval kui öösel varastatut!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 40, 'Päeval piinas mind palavus ja öösel külm, ja uni põgenes mu silmist!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 41, 'Nüüd ma olen olnud su kojas kakskümmend aastat. Neliteist aastat ma teenisin sind su kahe tütre pärast ja kuus aastat pudulojuste pärast, ja sa muutsid mu palka kümme korda!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 42, 'Kui minuga ei oleks olnud mu isa Jumal, Aabrahami Jumal, Iisaki Kartus, siis oleksid sa mind nüüd tühje käsi ära saatnud. Jumal on näinud mu häda ja mu kätevaeva ja on eile öösel teinud otsuse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 43, 'Siis Laaban kostis ja ütles Jaakobile: „Tütred on minu tütred ja pojad on minu pojad ja pudulojused on minu pudulojused, ja kõik, mis sa näed, on minu! Aga mida ma saaksin praegu teha oma tütarde heaks või nende poegade heaks, keda nad on ilmale toonud?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 44, 'Aga tule nüüd, tehkem leping, mina ja sina, ja see olgu tunnistajaks minu ja sinu vahel!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 45, 'Siis Jaakob võttis ühe kivi ja pani sambaks püsti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 46, 'Ja Jaakob ütles oma suguvendadele: „Korjake kive!\" Ja need võtsid kive ning kuhjasid kivikangru; ja nad sõid seal kivikangru peal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 47, 'Ja Laaban pani sellele nimeks Jegar-Sahaduuta; Jaakob aga nimetas selle Galeediks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 48, 'Ja Laaban ütles: „See kivikangur olgu täna tunnistajaks minu ja sinu vahel!\" Seepärast ta pani sellele nimeks Galeed');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 49, 'ja Mispa, sest ta ütles: „Issand valvab minu ja sinu vahel, kui me üksteist enam ei näe!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 50, 'Kui sa kohtled mu tütreid halvasti või võtad mu tütarde kõrvale teisi naisi, ilma et ükski inimene oleks meie juures, vaata, siis on Jumal ometi tunnistajaks minu ja sinu vahel!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 51, 'Siis ütles Laaban Jaakobile: „Vaata, see kivikangur, ja vaata, see sammas, mille ma püstitasin enese ja sinu vahele, -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 52, 'see kivikangur olgu tunnistajaks, samuti olgu see sammas tunnistajaks, et mina ei tohi tulla sellest kivikangrust mööda sinu juurde ja et sina ei tohi tulla sellest kivikangrust ja sambast mööda minu juurde kurja tegema!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 53, 'Aabrahami Jumal ja Naahori Jumal, nende vanemate Jumal, mõistku kohut meie vahel!\" Ja Jaakob vandus oma isa Iisaki Kartuse juures.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 54, 'Ja Jaakob ohverdas mäe peal tapaohvri ning kutsus oma suguvennad leiba võtma. Ja nad võtsid leiba ning jäid ööseks mäele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 55, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 1, 'Aga Laaban tõusis hommikul vara, suudles oma poegi ja tütreid ning õnnistas neid; siis Laaban läks teele ja pöördus tagasi koju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 2, 'Ka Jaakob läks oma teed. Aga temale tulid vastu Jumala Inglid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 3, 'Ja Jaakob ütles, kui ta neid nägi: „See on Jumala leer!\" Ja ta pani sellele paigale nimeks Mahanaim.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 4, 'Siis Jaakob läkitas käskjalad enese eel oma venna Eesavi juurde Seirimaale Edomi');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 5, 'ja andis neile käsu, öeldes nõnda: „Öelge mu isandale Eesavile: nõnda ütleb su sulane Jaakob: ma olen tänini Laabani juures võõrana elanud ja viibinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 6, 'Mul on härgi, eesleid, pudulojuseid, sulaseid ja ümmardajaid, ja ma läkitan seda teatama oma isandale, et su silmis armu leida!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 7, 'Käskjalad tulid tagasi Jaakobi juurde ja ütlesid: „Me jõudsime su venna Eesavi juurde. Ta juba tulebki sulle vastu ühes neljasaja mehega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 8, 'Siis Jaakob kartis väga ja tal oli kitsas käes; ja ta jaotas rahva, kes ühes temaga oli, samuti pudulojused, veised ja kaamelid, kahte leeri,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 9, 'sest ta mõtles: kui Eesav tuleb ühe leeri kallale ja lööb selle maha, siis teine leer pääseb!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 10, 'Ja Jaakob ütles: „Mu isa Aabrahami Jumal ja mu isa Iisaki Jumal, Issand, Kes mulle ütlesid: mine tagasi oma maale ja oma sugulaste seltsi, siis Ma teen sulle head!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 11, 'Mina pole väärt kõiki neid heategusid ja kõike seda truudust, mida sa oma sulasele oled osutanud! Sest kepp käes ma läksin üle selle Jordani, aga nüüd on mul kaks leeri!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 12, 'Päästa mind ometi mu venna Eesavi käest, sest ma kardan, et ta tuleb ja lööb mind maha ühes emade ja lastega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 13, 'Sina Ise aga oled öelnud: Ma teen sulle tõesti head ja lasen su soo saada mereliiva sarnaseks, mida ei saa ära lugeda selle rohkuse pärast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 14, 'Ja ta jäi selleks ööks sinna ning võttis sellest, mis oli saanud tema omaks, oma vennale Eesavile kingituseks');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 15, 'kakssada kitse ja kakskümmend sikku, kakssada emalammast ja kakskümmend jäära,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 16, 'kolmkümmend imetajat kaamelit ja nende varssa, nelikümmend lehma ja kümme härjavärssi, kakskümmend emaeeslit ja kümme eeslitäkku.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 17, 'Ja ta andis need oma sulaste kätte, iga karja eraldi, ja ütles oma sulastele: „Minge minu eel ja jätke vahemaa iga karja vahele!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 18, 'Ja ta käskis esimest, öeldes: „Kui mu vend Eesav tuleb sulle vastu, küsib sinult ja ütleb: kelle oma sa oled ja kuhu sa lähed, ja kelle omad on need, kes su ees on?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 19, 'Siis vasta: need on su sulase Jaakobi omad, kingitus, mis läkitatakse mu isandale Eesavile, ja vaata, ka tema ise tuleb meie taga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 20, 'Ja ta käskis ka teist ja kolmandat ja kõiki muid, kes karjade järel käisid, öeldes: „Te peate Eesavile ütlema sedasama, kui te teda kohtate!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 21, 'Ja öelge ka: vaata, su sulane Jaakob tuleb meie taga!\" Sest ta mõtles: ma lepitan teda kingitusega, mis mu eel läheb. Alles pärast seda ilmun ma ise tema ette, vahest võtab ta mind siis armulikult vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 22, 'Nõnda läks kingitus tema eel, aga ta ise jäi selleks ööks leeri.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 23, 'Kuid veel selsamal ööl ta tõusis üles ja võttis oma mõlemad naised ja mõlemad ümmardajad ja oma üksteist poega ja läks läbi Jabboki koolme.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 24, 'Ta võttis nad ja viis üle jõe, samuti viis ta üle kõik, mis tal oli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 25, 'Aga Jaakob ise jäi üksinda maha. Siis heitles üks Mees temaga, kuni hakkas koitma!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 26, 'Aga kui See nägi, et Ta ei saanud võimust tema üle, siis Ta lõi tema puusaliigest; ja Jaakobi puusaliiges nihkus paigast, kui ta heitles Temaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 27, 'Ja Mees ütles: „Lase mind lahti, sest juba koidab!\" Aga tema vastas: „Ei ma lase sind mitte, kui Sa mind ei õnnista!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 28, 'Siis Ta küsis temalt: „Mis su nimi on?\" Ja ta vastas: „Jaakob!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 29, 'Seepeale ütles Tema: „Su nimi ärgu olgu enam Jaakob, vaid olgu Iisrael, sest sa oled võidelnud Jumala ja inimestega ja oled võitnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 30, 'Siis küsis Jaakob ja ütles: „Ütle nüüd mulle Oma nimi!\" Aga Ta vastas: „Miks sa Mu nime küsid?\" Ja Ta õnnistas teda seal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 31, 'Ja Jaakob pani sellele paigale nimeks Penuel, sest ta ütles: „Kuigi ma nägin Jumalat palgest palgesse, pääses siiski mu hing!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 32, 'Päike tõusis, kui ta puusast longates Penuelist edasi läks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 1, 'Kui Jaakob oma silmad üles tõstis ja vaatas, ennäe, siis tuli Eesav ja ühes temaga nelisada meest! Ta jaotas nüüd lapsed Lea ja Raaheli ja mõlemate ümmardajate vahel,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 2, 'seadis ümmardajad ja nende lapsed ette, Lea ja tema lapsed nende järele, Raaheli ja Joosepi viimseiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 3, 'Ta ise aga läks nende eel ja kummardas seitse korda maani, kuni ta jõudis oma venna juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 4, 'Aga Eesav jooksis temale vastu ja süleles teda, langes temale kaela ja suudles teda; ja nad nutsid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 5, 'Siis ta tõstis oma silmad üles ja nägi naisi ja lapsi, ja ta küsis: „Kes need sul on?\" Ja tema vastas: „Need on lapsed, keda Jumal su sulasele armulikult on andnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 6, 'Ka ümmardajad astusid ligi, nemad ise ja nende lapsed, ja nad kummardasid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 7, 'Siis astus ligi ka Lea ühes oma lastega ja nad kummardasid; lõppeks astusid ligi Joosep ja Raahel ja kummardasid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 8, 'Siis ta küsis: „Mida sa kavatsed kogu selle leeriga, keda ma kohtasin?\" Ja tema vastas: „Oma isanda silmis armu leida!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 9, 'Aga Eesav ütles: „Mul eneselgi on küllalt, mu vend! Jäägu sulle, mis sul on!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 10, 'Kuid Jaakob vastas: „Sugugi mitte! Kui ma nüüd su silmis olen armu leidnud, siis võta mu kingitus minult vastu! Sest ma olen ju tohtinud näha su palet, otsekui näeks Jumala palet, ja sa oled olnud mu vastu lahke!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 11, 'Võta nüüd minu tervituskink, mis sulle toodi, sest Jumal on olnud mu vastu armuline ja mul on kõike küllalt!\" Ja ta käis temale peale, kuni ta võttis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 12, 'Siis ütles Eesav: „Hakkame liikuma ja lähme, ja mina käin sinuga rinnu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 13, 'Aga Jaakob vastas temale: „Mu isand näeb ju, et lapsed on väetid ja minu hooleks on imetajad pudulojused ja lehmad; kui neid liiga kiiresti aetakse ühegi päeva, siis sureb kogu kari.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 14, 'Mingu aga mu isand oma sulase eel ja mina liigun pikkamisi oma ees käiva karja kannul ja laste kannul, kuni ma jõuan oma isanda juurde Seiri.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 15, 'Eesav ütles: „Ma jätan siis sinu juurde osa rahvast, kes ühes minuga on!\" Aga tema vastas: „Mispärast nõnda? Kui ma ainult oma isanda silmis armu leiaksin!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 16, 'Ja Eesav läks selsamal päeval oma teed tagasi Seiri.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 17, 'Aga Jaakob liikus Sukkotti, ehitas enesele koja ja tegi oma karjadele lehtkatused; seepärast pandi sellele paigale nimeks Sukkot.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 18, 'Ja Jaakob jõudis Mesopotaamiast tulles õnnelikult Sekemi linna, mis on Kaananimaal, ja lõi linna ees leeri üles.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 19, 'Ta ostis selle väljaosa, kuhu ta oma telgi oli üles löönud, Sekemi isa Hamori lastelt saja rahatüki eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 20, 'Ja ta püstitas sinna altari ning pani sellele nimeks: „Jumal, Iisraeli Jumal!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 1, 'Kord läks Diina, Lea tütar, kelle Lea oli Jaakobile ilmale toonud, maa tütreid vaatama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 2, 'Aga maa vürsti hiivlase Hamori poeg Sekem nägi teda, võttis tema, magas ta juures ja naeris ta ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 3, 'Kuid ta hing kiindus Diinasse, Jaakobi tütresse, ja ta armastas tütarlast ning rääkis tütarlapsele meelitusi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 4, 'Ja Sekem rääkis oma isa Hamoriga, öeldes: „Võta see tüdruk mulle naiseks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 5, 'Jaakob aga sai kuulda, et ta tütar Diina oli ära teotatud. Aga et ta pojad olid tema karjaga väljal, siis Jaakob vaikis, kuni nad koju tulid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 6, 'Ja Hamor, Sekemi isa, läks Jaakobi juurde, et temaga rääkida.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 7, 'Jaakobi pojad tulid väljalt, kui nad sellest olid kuulda saanud; mehed olid nördinud ja nende viha süttis väga põlema, et ta Jaakobi tütre juures magades oli Iisraelis teinud häbiteo; sest nõnda ei tohtinud teha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 8, 'Ja Hamor rääkis nendega, öeldes: „Mu poja Sekemi hing on kiindunud teie tütresse. Andke ta ometi temale naiseks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 9, 'Saage meiega langudeks, andke meile oma tütreid ja võtke enestele meie tütreid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 10, 'ja jääge meie juurde elama. Maa on lahti teie ees, elage, rännake ja kodunege siin!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 11, 'Ja Sekem ütles tema isale ja vendadele: „Kui ma teie silmis armu leian, siis annan teile, mida te minult nõuate.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 12, 'Pange mulle peale ükskõik kui palju mõrsjahinda ja kingitusi: ma annan, mida te minult nõuate. Andke ainult tütarlaps mulle naiseks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 13, 'Siis kostsid Jaakobi pojad Sekemile ja tema isale Hamorile, rääkides aga kavalasti, sellepärast et ta nende õe Diina oli ära teotanud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 14, 'ja ütlesid neile: „Me ei või teha niisugust asja, et anname oma õe mehele, kellel on eesnahk, sest see oleks meile häbiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 15, 'Ainult sel tingimusel oleme teiega nõus, kui saate meie sarnaseiks, lastes kõik oma meesterahvad ümber lõigata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 16, 'Siis me anname oma tütreid teile ja võtame teie tütreid enestele, elame üheskoos teiega ja saame üheks rahvaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 17, 'Aga kui te ei võta meid kuulda ega lase endid ümber lõigata, siis võtame oma tütre ja läheme ära!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 18, 'Ja nende sõnad olid head Hamori ja Hamori poja Sekemi silmis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 19, 'Ja noor mees ei kõhelnud nõnda tegemast, sest ta ihaldas Jaakobi tütart; ja tema oli lugupeetum kui kõik teised ta isakojas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 20, 'Siis Hamor ja ta poeg Sekem läksid oma linna väravasse ja rääkisid oma linna meestega, öeldes:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 21, '„Need mehed on rahuarmastajad. Jäägu nad meie juurde, elagu ja rännaku nad siin maal. Sest maa, vaata, laiub ju nendegi ees igat kätt. Siis võtame enestele naisteks nende tütreid ja anname oma tütreid neile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 22, 'Aga ainult sel tingimusel on mehed meiega nõus meie juures elama ja saama meiega üheks rahvaks, kui meil kõik meesterahvad ümber lõigatakse, nõnda nagu nemad ise on ümber lõigatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 23, 'Nende karjad ja varandus ja kõik nende lojused, eks need saa siis meile! Olgem ainult nendega nõus, siis nad asuvad meie juurde elama.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 24, 'Ja nad võtsid kuulda Hamorit ja tema poega Sekemit, kõik, kes ta linna väravast läbi käisid; ja kõik meesterahvad lõigati ümber, kõik, kes ta linna väravast läbi käisid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 25, 'Aga kolmandal päeval, kui nad olid valudes, võtsid kaks Jaakobi poega, Siimeon ja Leevi, Diina vennad, kumbki oma mõõga ja läksid takistamatult linna ja tapsid ära kõik meesterahvad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 26, 'Nad tapsid mõõgateraga ka Hamori ja tema poja Sekemi, võtsid Sekemi kojast Diina ja läksid ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 27, 'Jaakobi pojad tulid haigetele kallale ja riisusid linna, sellepärast et nad nende õe olid ära teotanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 28, 'Nad võtsid ära nende pudulojused, veised ja eeslid ja mis iganes oli linnas või väljal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 29, 'Nad viisid ära kõik nende varanduse ja kõik nende lapsed ja naised, ja riisusid kõik, mis kodades oli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 30, 'Aga Jaakob ütles Siimeonile ja Leevile: „Te saadate mind õnnetusse, sellepärast et te mind olete teinud vihatavaks maa elanike, kaananlaste ja perislaste hulgas! Mul on vähe mehi: kui nad kogunevad mu vastu, siis nad löövad mind ja me hukkume, niihästi mina kui mu pere!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 31, 'Kuid nemad vastasid: „Kas ta siis tohtis talitada meie õega nagu hooraga?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 1, 'Ja Jumal ütles Jaakobile: „Võta kätte, mine üles Peetelisse, ela seal ja tee sinna altar Jumalale, Kes sulle ennast ilmutas, kui sa põgenesid oma venna Eesavi eest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 2, 'Ja Jaakob ütles oma perele ja kõigile, kes olid ühes temaga: „Kõrvaldage võõrad jumalad, kes teie keskel on, ja puhastage endid ning vahetage riided!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 3, 'Ja me võtame kätte ning läheme üles Peetelisse ja teeme sinna altari Jumalale, Kes mind kuulis mu ahastuse ajal ja oli minuga teel, mida käisin!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 4, 'Siis nad andsid Jaakobile kõik nende käes olevad võõrad jumalad ja kõrvarõngad, mis neil kõrvus olid, ja Jaakob mattis need maha ühe Sekemi ligidal oleva tamme alla.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 5, 'Ja nad läksid teele; aga hirm Jumala ees lasus ümberkaudseil linnadel ja need ei ajanud taga Jaakobi poegi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 6, 'Ja Jaakob jõudis Luusi, see on Peetelisse Kaananimaal, tema ja kogu rahvas, kes oli ühes temaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 7, 'Ja ta ehitas sinna altari ning pani sellele paigale nimeks Eel-Peetel, sest seal oli Jumal ennast temale ilmutanud, kui ta põgenes oma venna eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 8, 'Aga Deboora, Rebeka imetaja, suri, ja ta maeti ühe tamme alla allpool Peetelit, ja sellele pandi nimeks Nututamm.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 9, 'Ja Jumal ilmutas Ennast taas Jaakobile, kui see Mesopotaamiast tuli, ja õnnistas teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 10, 'Ja Jumal ütles temale: „Jaakob on su nimi! Ärgu hüütagu su nime enam Jaakobiks, vaid su nimi olgu Iisrael!\" Ja Ta pani temale nimeks Iisrael.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 11, 'Ja Jumal ütles temale: „Mina olen kõigeväeline Jumal! Ole viljakas ja paljune! Sinust saab rahvas, jah, rahvaste hulk, ja sinu niudeist väljuvad kuningad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 12, 'Ja maa, mille Ma andsin Aabrahamile ja Iisakile, Ma annan sinule; ka sinu soole pärast sind Ma annan selle maa!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 13, 'Ja Jumal läks tema juurest üles paigast, kus Ta temaga oli rääkinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 14, 'Ja Jaakob püstitas samba sinna paika, kus Ta temaga oli rääkinud, kivisamba, ja kallas selle peale joomaohvri ning valas õli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 15, 'Ja Jaakob nimetas paiga, kus Jumal temaga oli rääkinud, Peeteliks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 16, 'Siis nad läksid Peetelist teele. Aga kui veel tükk maad oli minna Efratani, pidi Raahel sünnitama, ja tal oli raske sünnitus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 17, 'Tema raske sünnituse ajal ütles aitajanaine temale: „Ära karda, sest ka seekord on sul poeg!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 18, 'Ja kui ta hing oli välja minemas, sest varsti ta surigi, pani ta temale nimeks Ben-Ooni; aga selle isa kutsus teda Benjaminiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 19, 'Ja Raahel suri ning ta maeti Efrata tee äärde, see on Petlemma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 20, 'Ja Jaakob püstitas tema hauale samba; see Raaheli hauasammas on alles tänapäevani.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 21, 'Ja Iisrael läks teele ning lõi oma telgi üles teispoole Karjatorni.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 22, 'Ja kui Iisrael elas seal maal, juhtus, et Ruuben läks ja magas oma isa liignaise Billa juures. Ja Iisrael sai sellest kuulda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 23, 'Jaakobil oli kaksteist poega; Lea pojad: Ruuben, Jaakobi esmasündinu, Siimeon, Leevi, Juuda, Issaskar ja Sebulon;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 24, 'Raaheli pojad: Joosep ja Benjamin;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 25, 'Billa, Raaheli ümmardaja pojad: Daan ja Naftali;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 26, 'Silpa, Lea ümmardaja pojad: Gaad ja Aaser. Need olid Jaakobi pojad, kes sündisid temale Mesopotaamias.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 27, 'Ja Jaakob jõudis oma isa Iisaki juurde Mamresse, Kirjat-Arbasse, see on Hebronisse, kus Aabraham ja Iisak olid võõrastena elanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 28, 'Ja Iisaki elupäevi oli sada kaheksakümmend aastat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 29, 'Siis Iisak heitis hinge ja suri; ta koristati oma rahva juurde, vana ja elatanud; ta pojad Eesav ja Jaakob matsid tema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 1, 'Ja need olid Eesavi, see on Edomi järeltulijad:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 2, 'Eesav võttis oma naised Kaanani tütreist: Aada, hetiit Eeloni tütre, ja Oholibama, hiivlase Sibeoni poja Ana tütre,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 3, 'ja Baasmati, Ismaeli tütre, Nebajoti õe.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 4, 'Aada tõi Eesavile ilmale Eliifase, ja Baasmat tõi ilmale Reueli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 5, 'Ja Oholibama tõi ilmale Jeusi, Jalami ja Korahi; need olid Eesavi pojad, kes temale sündisid Kaananimaal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 6, 'Ja Eesav võttis oma naised, pojad ja tütred ja kõik oma pere hingelised, karja, kõik lojused ja kogu varanduse, mis ta Kaananimaal oli soetanud, ja läks teisele maale, ära oma venna Jaakobi juurest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 7, 'Sest nende varandus oli liiga suur üheskoos elamiseks, ja maa, kus nad võõrastena elasid, ei suutnud neid toita nende karjade pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 8, 'Ja Eesav asus elama Seiri mäestikku; Eesav on Edom.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 9, 'Ja need olid Eesavi, edomlaste isa järeltulijad Seiri mäestikus:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 10, 'need olid Eesavi poegade nimed: Eliifas, Eesavi naise Aada poeg, Reuel, Eesavi naise Baasmati poeg.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 11, 'Ja Eliifase pojad olid: Teeman, Oomar, Sefo, Gatam ja Kenas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 12, 'Ja Timna oli Eesavi poja Eliifase liignaine ja tema tõi Eliifasele ilmale Amaleki; need olid Eesavi naise Aada järeltulijad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 13, 'Ja need olid Reueli pojad: Nahat, Serah, Samma ja Missa; need olid Eesavi naise Baasmati järeltulijad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 14, 'Ja need olid Eesavi naise Oholibama, Sibeoni poja Ana tütre pojad, kes tõi Eesavile ilmale Jeusi, Jalami ja Korahi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 15, 'Need olid Eesavi poegade vürstid: Eliifase, Eesavi esmasündinu pojad - vürst Teeman, vürst Oomar, vürst Sefo, vürst Kenas,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 16, 'vürst Korah, vürst Gatam, vürst Amalek. Need olid Eliifasest põlvnevad vürstid Edomimaal, need olid Aada järeltulijad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 17, 'Ja need olid Eesavi poja Reueli pojad: vürst Nahat, vürst Serah, vürst Samma, vürst Missa. Need olid Reuelist põlvnevad vürstid Edomimaal, need olid Eesavi naise Baasmati järeltulijad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 18, 'Ja need olid Eesavi naise Oholibama pojad: vürst Jeus, vürst Jalam, vürst Korah. Need olid Eesavi naisest, Ana tütrest Oholibamast põlvnevad vürstid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 19, 'Need olid Eesavi, see on Edomi järeltulijad, ja need olid nende vürstid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 20, 'Need olid horiit Seiri pojad, selle maa elanikud: Lootan, Soobal, Sibeon, Ana,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 21, 'Diison, Eeser ja Diisan; need olid horiitide vürstid, Seiri pojad Edomimaal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 22, 'Ja Lootani pojad olid Hori ja Heemam; ja Lootani õde oli Timna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 23, 'Ja need olid Soobali pojad: Alvan, Maanahat, Eebal, Sefo ja Oonam.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 24, 'Ja need olid Sibeoni pojad: Ajja ja Ana; Ana oli see, kes kõrbes kohtas mürkmadusid, kui ta karjatas oma isa Sibeoni eesleid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 25, 'Ja need olid Ana lapsed: Diison ja Oholibama, Ana tütar.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 26, 'Ja need olid Diisoni pojad: Hemdan, Esban, Jitran ja Keran.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 27, 'Need olid Eeseri pojad: Bilhan, Saavan ja Akan.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 28, 'Need olid Diisani pojad: Uuts ja Aran.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 29, 'Need olid horiitide vürstid: vürst Lootan, vürst Soobal, vürst Sibeon, vürst Ana,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 30, 'vürst Diison, vürst Eeser, vürst Diisan. Need olid horiitide vürstid nende vürstkondade kaupa Seirimaal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 31, 'Ja need olid kuningad, kes valitsesid Edomimaal, enne kui ükski kuningas valitses Iisraeli laste üle:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 32, 'Bela, Beori poeg, oli kuningaks Edomis, ja tema linna nimi oli Dinhaba.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 33, 'Kui Bela suri, sai tema asemel kuningaks Joobab, Serahi poeg Bosrast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 34, 'Kui Joobab suri, sai tema asemel kuningaks Huusam teemanlaste maalt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 35, 'Kui Huusam suri, sai tema asemel kuningaks Hadad, Bedadi poeg, kes lõi midjanlasi Moabi väljadel; ja tema linna nimi oli Aviit.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 36, 'Kui Hadad suri, sai tema asemel kuningaks Samla Masreekast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 37, 'Kui Samla suri, sai tema asemel kuningaks Saul jõeäärsest Rehobotist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 38, 'Kui Saul suri, sai tema asemel kuningaks Baal-Haanan, Akbori poeg.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 39, 'Kui Baal-Haanan, Akbori poeg, suri, sai tema asemel kuningaks Hadar; tema linna nimi oli Pau; ja tema naise nimi oli Mehetabel, Mee-Sahabi tütre Matredi tütar.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 40, 'Ja need olid Eesavi vürstide nimed nende suguvõsade kaupa, nimeliselt nende asupaikade järgi: vürst Timna, vürst Alva, vürst Jetet,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 41, 'vürst Oholibama, vürst Eela, vürst Piinon,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 42, 'vürst Kenas, vürst Teeman, vürst Mibsar,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 43, 'vürst Magdiel, vürst Iiram. Need olid Edomi, see on Eesavi, edomlaste isa vürstid nende elukohtade järgi nende pärismaal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 1, 'Aga Jaakob elas maal, kus ta isa oli võõrana elanud, Kaananimaal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 2, 'Need on Jaakobi suguvõsa lood: Kui Joosep oli seitsmeteistkümneaastane, siis oli ta ühes oma vendadega pudulojuste karjane; tema oli abilisena oma isa naiste Billa ja Silpa poegade juures. Ja Joosep kandis nende isale ette nende halva kuulsuse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 3, 'Iisrael armastas Joosepit enam kui kõiki oma poegi, sest ta oli tema vana ea poeg, ja ta tegi temale kirju kuue.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 4, 'Kui ta vennad nägid, et nende isa armastas teda enam kui kõiki tema vendi, siis nad vihkasid teda ega rääkinud temaga avameelselt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 5, 'Kord nägi Joosep unenäo ja jutustas selle oma vendadele; seejärel hakkasid need teda veel enam vihkama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 6, 'Ta nimelt ütles neile: „Kuulge ometi seda unenägu, mis ma unes nägin!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 7, 'Jah, vaadake, me olime väljal vihke sidumas, ja ennäe, minu vihk tõusis üles ning jäigi püsti seisma! Aga vaata, teie vihud ümbritsesid seda ja kummardasid minu vihu ees!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 8, 'Siis ta vennad ütlesid temale: „Kas sina tahad saada meile kuningaks ja hakata meie üle valitsema?\" Ja nad vihkasid teda veelgi enam tema unenägude ja kõnede pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 9, 'Ja ta nägi veel teise unenäo, jutustas selle oma vendadele ja ütles: „Vaata, ma nägin veel ühe unenäo, ja ennäe, päike, kuu ja üksteist tähte kummardasid minu ees!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 10, 'Aga kui ta seda jutustas oma isale ja vendadele, siis ta isa sõitles teda ning ütles temale: „Mis unenägu see küll on, mis sa nägid! Kas mina ja su ema ja vennad tõesti peame tulema ja sinu ees maani kummardama?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 11, 'Ta vennad said temale kadedaks, aga ta isa pidas meeles selle loo.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 12, 'Kord olid ta vennad läinud Sekemisse oma isa karja hoidma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 13, 'Ja Iisrael ütles Joosepile: „Eks ole su vennad Sekemis karja hoidmas? Tule, ma läkitan sind nende juurde!\" Ja tema vastas: „Siin ma olen!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 14, 'Siis ta ütles temale: „Mine ometi vaatama, kas su vendade käsi hästi käib ja kas kari on korras, ja too mulle sõna!\" Ta läkitas teda Hebroni orust ja ta tuli Sekemisse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 15, 'Ja üks mees kohtas teda, sest vaata, ta oli väljal eksinud! Ja mees küsis temalt, öeldes: „Mida sa otsid?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 16, 'Ja tema vastas: „Ma otsin oma vendi. Ütle mulle ometi, kus nad karja hoiavad?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 17, 'Ja mees ütles: „Nad on siit edasi läinud, sest ma kuulsin neid ütlevat: läki Dotanisse!\" Ja Joosep läks järele oma vendadele ning leidis nad Dotanis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 18, 'Aga nad nägid teda kaugelt ja enne kui ta jõudis nende juurde, võtsid nad õelalt nõuks teda tappa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 19, 'Nad ütlesid üksteisele: „Näe, sealt tuleb see unenägude sepitseja!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 20, 'Tulgem nüüd, tapkem tema, visakem ta mõnesse kaevu ja öelgem, et kuri elajas sõi tema ära! Siis saame näha, mis ta unenäod tähendavad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 21, 'Kui Ruuben seda kuulis, siis ta tahtis teda nende käest päästa ja ütles: „Ärgem võtkem temalt hinge!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 22, 'Ja Ruuben ütles neile: „Ärge valage verd, visake ta siia kõrbes olevasse kaevu, aga ärge pange oma kätt tema külge!\" Sest ta tahtis teda päästa nende käest ja saata tagasi isa juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 23, 'Ja kui Joosep tuli oma vendade juurde, siis kiskusid need Joosepil kuue seljast, kirju kuue, mis tal seljas oli,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 24, 'ja võtsid tema ning viskasid kaevu; aga kaev oli tühi, selles ei olnud vett.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 25, 'Seejärel nad istusid leiba võtma. Ja kui nad oma silmad üles tõstsid ja vaatasid, ennäe, siis tuli ismaeliitide killavoor Gileadist! Nende kaamelid kandsid mitmesugust vaiku, palsamit ja lõhnaainest, ja nad olid sellega teel alla Egiptusesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 26, 'Ja Juuda ütles oma vendadele: „Mis kasu sellest on, kui me tapame oma venna ja katame kinni tema vere?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 27, 'Tulge, müüme tema ismaeliitidele, aga meie käed ärgu puudutagu teda, sest ta on meie lihane vend!\" Ja ta vennad kuulasid teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 28, 'Kui siis Midjani mehed, kaupmehed, mööda läksid, tõmbasid ja võtsid nad Joosepi kaevust välja ja müüsid Joosepi kahekümne hõbetüki eest ismaeliitidele; ja need viisid Joosepi Egiptusesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 29, 'Kui Ruuben tuli tagasi kaevu juurde, vaata, siis ei olnud Joosepit enam kaevus! Siis ta käristas oma riided lõhki');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 30, 'ja läks tagasi oma vendade juurde ning ütles: „Poissi ei ole enam! Ja mina, kuhu ma nüüd lähen?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 31, 'Siis nad võtsid Joosepi kuue ja tapsid ühe siku ning kastsid kuue verre.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 32, 'Ja nad saatsid kirju kuue, tulid oma isa juurde ning ütlesid: „Selle me leidsime! Tunnista nüüd, kas see on su poja kuub või mitte?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 33, 'Ja ta tundis selle ära ning ütles: „See on mu poja kuub! Kuri elajas on ta ära söönud, Joosep on tõesti maha murtud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 34, 'Ja Jaakob käristas oma riided lõhki, kinnitas kotiriide niuete ümber ja leinas oma poega kaua aega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 35, 'Kõik ta pojad ja tütred püüdsid teda trööstida, kuid ta ei lasknud ennast trööstida, vaid ütles: „Ma lähen tõesti leinates oma poja juurde hauda!\" Ja tema isa nuttis teda taga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 36, 'Aga midjanlased müüsid tema Egiptuses Pootifarile, vaarao hoovkondlasele, ta ihukaitse pealikule.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 1, 'Sel ajal Juuda läks ära oma vendade juurest ja siirdus ühe Adullami mehe juurde, kelle nimi oli Hiira.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 2, 'Ja Juuda nägi seal kaananlase, Suua nimelise mehe tütart, võttis selle ning heitis ta juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 3, 'Ja see sai käima peale ning tõi poja ilmale; ja ta pani sellele nimeks Eer.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 4, 'Ja tema sai taas käima peale ning tõi poja ilmale; ja ta pani sellele nimeks Oonan.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 5, 'Ja tema tõi veelgi ühe poja ilmale ja pani sellele nimeks Seela; ta oli Kesibis, kui ta selle ilmale tõi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 6, 'Ja Juuda võttis Eerile, oma esmasündinule, naise nimega Taamar.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 7, 'Aga Eer, Juuda esmasündinu, oli Issand silmis paha ja Issand laskis tema surra.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 8, 'Siis Juuda ütles Oonanile: „Heida oma venna naise juurde, ole temale mehe eest ja soeta oma vennale sugu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 9, 'Kuna aga Oonan teadis, et sugu ei pidanud saama temale, siis heites oma venna naise juurde ta hävitas oma seemne maha pillates, et mitte anda sugu oma vennale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 10, 'Aga see, mis ta tegi, oli Issand silmis paha ja Ta laskis surra ka tema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 11, 'Siis Juuda ütles oma miniale Taamarile: „Jää lesena oma isakotta elama, kuni mu poeg Seela on kasvanud suuremaks!\" Sest ta mõtles: „Muidu sureb seegi nagu ta vennad!\" Ja Taamar läks ning elas oma isakojas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 12, 'Mõne aja pärast suri Suua tütar, Juuda naine. Kui Juuda leinaaeg oli möödunud, siis ta läks üles Timnasse oma pudulojuste niitjate juurde, tema ja ta sõber Hiira, Adullami mees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 13, 'Ja Taamarile anti teada ning öeldi: „Vaata, su äi läheb üles Timnasse pudulojuseid niitma!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 14, 'Siis ta võttis lesepõlve riided seljast ära, varjas ennast looriga ja kattis enese kinni ning istus Eenaimi väravasse, kust tee viib Timnasse, sest ta oli näinud, et Seela oli kasvanud suureks, teda aga ei olnud antud temale naiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 15, 'Kui Juuda teda nägi, siis ta pidas teda hooraks, sest ta oli oma näo kinni katnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 16, 'Ja ta pöördus teelt tema poole ning ütles: „Lase ma heidan su juurde!\" Sest ta ei teadnud, et see oli tema minia. Aga too vastas: „Mis sa mulle annad, kui sa heidad mu juurde?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 17, 'Ta ütles: „Ma läkitan sulle karjast ühe sikutalle!\" Ja naine vastas: „Jah, kui sa annad mulle pandi, seniks kui sa läkitad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 18, 'Siis ta küsis: „Mis võiks olla pandiks, mille ma pean sulle andma?\" Ja tema vastas: „Su pitsat ja vöö ja kepp, mis sul käes on!\" Ja ta andis need temale ning heitis ta juurde; ja naine sai temast käima peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 19, 'Siis ta tõusis ja läks ära ning võttis eneselt loori ja pani lesepõlve riided selga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 20, 'Ja Juuda läkitas oma sõbra Adullami mehega sikutalle, et võtta pant naise käest; aga see ei leidnud teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 21, 'Siis ta küsis meestelt seal paigas, öeldes: „Kus on see pordunaine, kes oli Eenaimi tee ääres?\" Aga need vastasid: „Siin pole pordunaist olnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 22, 'Ja ta tuli tagasi Juuda juurde ning ütles: „Ma ei leidnud teda. Ja mehedki seal paigas ütlesid: siin pole pordunaist olnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 23, 'Siis ütles Juuda: „Pidagu siis enesele, et me ei satuks pilke alla! Vaata, ma läkitasin selle siku, aga sina ei leidnud teda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 24, 'Aga kolme kuu pärast teatati Juudale ja öeldi: „Su minia Taamar on hooranud, ja vaata, ta on hooratööst saanud ka käima peale!\" Siis ütles Juuda: „Tooge ta välja, et ta põletataks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 25, 'Kui ta välja toodi, siis ta läkitas sõna oma äiale: „Sellest mehest, kelle omad need on, olen ma käima peal!\" Ja ta ütles: „Tunnista nüüd, kelle see pitsat ja vöö ja kepp on!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 26, 'Ja Juuda tundis need ära ning ütles: „Tema on õigem minust! Sest ma ei ole teda andnud oma pojale Seelale!\" Ja ta ei ühtinud enam temaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 27, 'Aga sünnitamise ajal, vaata, olid ta ihus kaksikud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 28, 'Ja kui ta sünnitas, sirutus üks käsi välja; ja aitajanaine võttis ning sidus käe ümber helepunase lõnga, öeldes: „See tuleb esmalt välja!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 29, 'Aga kui ta oma käe tagasi tõmbas, vaata, siis tuli välja ta vend! Ja aitajanaine ütles: „Missuguse lõhe sa küll enesele oled rebestanud!\" Ja temale pandi nimeks Perets.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 30, 'Ja pärast tuli välja tema vend, kelle käe ümber oli helepunane lõng; ja temale pandi nimeks Serah.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 1, 'Ja Joosep viidi alla Egiptusesse; ja egiptlane Pootifar, vaarao hoovkondlane, ihukaitsepealik, ostis tema ismaeliitidelt, kes olid ta sinna toonud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 2, 'Aga Issand oli Joosepiga ja tema oli mees, kellel kõik asjad korda läksid; ja ta elas oma isanda, egiptlase kojas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 3, 'Ka tema isand nägi, et Issand oli temaga ja et kõik, mis ta tegi, Issand laskis tema käes korda minna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 4, 'Ja Joosep leidis armu tema silmis ning teenis teda; ja ta pani tema oma koja üle, ja kõik, mis tal oli, ta andis tema kätte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 5, 'Ja sellest ajast peale, kui ta oli pannud tema oma koja üle, ja kõige üle, mis tal oli, Issand õnnistas egiptlase koda Joosepi pärast, ja Issand õnnistus oli kõigega, mis tal oli kojas ja väljal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 6, 'Ja ta jättis kõik, mis tal oli, Joosepi kätte ega teadnud millestki, kui ainult leivast, mida ta sõi; Joosep oli jumekas ja ilusa välimusega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 7, 'Nende asjaolude tõttu sündis, et ta isanda naine Joosepile silma heitis ja ütles: „Maga mu juures!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 8, 'Aga ta keeldus ja ütles oma isanda naisele: „Vaata, mu isand ise ei tea, mis kojas on, ja kõik, mis tal on, ta on andnud minu kätte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 9, 'Ei ole ükski selles kojas suurem minust ja tema ei ole mulle keelanud midagi muud kui sind, sest sa oled tema naine. Kuidas tohiksin siis teha seda suurt kurja ja pattu oma Jumala vastu?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 10, 'Ja kuigi naine rääkis Joosepile iga päev, ei kuulanud see teda, et ta magaks ta kõrval ja oleks ta juures.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 11, 'Aga ühel päeval, kui Joosep tuli koju oma toimetusi tegema ja ühtki inimest pererahvast ei olnud kodus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 12, 'ta haaras kinni tema kuuest, öeldes: „Maga mu juures!\" Aga Joosep jättis oma kuue tema kätte, põgenes ja läks õue.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 13, 'Ja kui naine nägi, et ta oma kuue oli jätnud tema kätte ja oli põgenenud õue,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 14, 'siis ta hüüdis pererahvast ja rääkis neile, öeldes: „Näete, ta on toonud meie juurde heebrea mehe meid ära naerma! Ta tuli mu juurde, et minuga magada, aga ma hüüdsin suure häälega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 15, 'ja kui ta kuulis, et ma häält tõstsin ja hüüdsin, siis ta jättis oma kuue mu juurde, põgenes ja läks õue!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 16, 'Ja ta pani tema kuue enese kõrvale seniks, kui ta isand koju tuli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 17, 'Ja ta rääkis temale needsamad sõnad, öeldes: „Heebrea sulane, kelle sa oled meie juurde toonud, tuli mu juurde mind ära naerma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 18, 'Aga kui ma häält tõstsin ja hüüdsin, siis ta jättis oma kuue mu juurde ja põgenes õue!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 19, 'Ja kui ta isand kuulis oma naise jutustust, mida see temale rääkis, öeldes: „Nõndaviisi on su sulane minuga teinud!\" siis ta viha süttis põlema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 20, 'Ja Joosepi isand võttis tema ja pani vangihoonesse, sinna paika, kus kuninga vange kinni peeti. Siis oli ta seal vangihoones.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 21, 'Aga Issand oli Joosepiga ja pööras tema poole Oma helduse ning tegi, et ta sai vangihoone ülema soosikuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 22, 'Ja vangihoone ülem andis Joosepi kätte kõik vangid, kes olid vangihoones, ja kõik, mis seal pidi tehtama, seadis tema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 23, 'Vangihoone ülem ei vaadanud millegi järele, mis oli tema käes, sest Issand oli temaga ja mis ta tegi, Issand laskis korda minna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 1, 'Ja pärast seda lugu sündis, et Egiptuse kuninga joogikallaja ja pagar eksisid oma isanda Egiptuse kuninga vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 2, 'Ja vaarao sai väga kurjaks oma kahe hoovkondlase peale, joogikallajate ülema ja pagarite ülema peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 3, 'Ja ta andis nad vahi alla ihukaitsepealiku kotta, vangihoonesse, samasse paika, kus Joosep kinni oli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 4, 'Ja ihukaitsepealik pani Joosepi nende juurde, et ta neid teeniks; ja nad olid vahi all kaua aega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 5, 'Ja need mõlemad nägid ühel ööl unenäo, kumbki oma unenäo, kumbki oma tähendusega unenäo, Egiptuse kuninga joogikallaja ja pagar, kes vangihoones kinni olid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 6, 'Kui Joosep tuli hommikul nende juurde ja nägi neid, vaata, siis olid nad nukra näoga!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 7, 'Ja ta küsis vaarao hoovkondlastelt, kes olid ühes temaga vahi all ta isanda kojas, öeldes: „Mispärast on teil täna nii pahurad näod?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 8, 'Ja nad vastasid temale: „Me nägime unenägusid, aga ei ole kedagi, kes need seletaks!\" Ja Joosep ütles neile: „Eks seletused ole Jumala käes? Siiski jutustage mulle!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 9, 'Ja joogikallajate ülem jutustas oma unenäo Joosepile ning ütles temale: „Mu unenäos oli nõnda: vaata, mu ees oli viinapuu');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 10, 'ja viinapuul oli kolm oksa; see lehistus, õitses ja marjakobarad valmisid;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 11, 'mul oli käes vaarao karikas ja ma võtsin viinamarju ja pigistasin neid vaarao karikasse ja andsin karika vaarao kätte.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 12, 'Ja Joosep ütles temale: „Selle seletus on niisugune: kolm oksa on kolm päeva.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 13, 'Enne kui kolm päeva on möödunud, vaarao tõstab su pea üles ja paneb sind tagasi su ametisse ja sa annad vaaraole karikat kätte endist viisi, nagu siis, kui olid ta joogikallaja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 14, 'Aga pea mind meeles, kui su käsi hästi käib ja tee mulle siis head ning tuleta mind vaaraole meelde ja vii mind siit hoonest välja,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 15, 'sest mind on vargsil viisil varastatud heebrealaste maalt ja ma pole siingi teinud midagi, et mind vangiurkasse pandaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 16, 'Kui pagarite ülem nägi, et ta oli hästi seletanud, siis ta ütles Joosepile: „Ka mina nägin und, ja vaata, mu pea kohal oli kolm punutud korvi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 17, 'Kõige ülemises korvis oli kõiksugust vaaraole valmistatud pagarirooga, aga linnud sõid selle mu peapealsest korvist.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 18, 'Ja Joosep vastas ning ütles: „Selle seletus on niisugune: kolm korvi on kolm päeva.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 19, 'Enne kui kolm päeva on möödunud, vaarao võtab sul pea otsast ja poob sind puusse ning linnud söövad liha su pealt.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 20, 'Ja kolmandal päeval, vaarao sünnipäeval, kui ta tegi kõigile oma sulastele joomapeo, sündiski, et ta tõstis üles joogikallajate ülema pea, samuti pagarite ülema pea oma sulaste seast,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 21, 'ja pani joogikallajate ülema tema joogikallajaametisse vaaraole karikat kätte ulatama,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 22, 'aga pagarite ülema ta laskis puua, nõnda nagu Joosep neile oli seletanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 23, 'Ent joogikallajate ülem ei pidanud Joosepit meeles, vaid unustas tema ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 1, 'Ja kahe aasta pärast sündis, et vaarao nägi und, ja vaata, ta seisis Niiluse jõe ääres!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 2, 'Ja näe, jõest tõusis seitse lehma, ilusad näha ja lihavad liha poolest, ja sõid roostikus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 3, 'Ja vaata, teist seitse lehma tõusis nende järel jõest, pahad näha ja lahjad liha poolest, ja need seisid teiste lehmade kõrval jõe kaldal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 4, 'Ja lehmad, kes olid pahad näha ja lahjad liha poolest, sõid ära need seitse lehma, kes olid ilusad näha ja lihavad. Siis vaarao ärkas üles.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 5, 'Aga ta uinus magama ja nägi teist puhku und, ja vaata, seitse viljapead tõusis ühest kõrrest, jämedad ja head.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 6, 'Ja vaata, seitse peenikest ja hommikutuulest kõrvetatud viljapead võrsus nende järel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 7, 'Ja peenikesed viljapead neelasid ära need seitse jämedat ja täit viljapead. Ja vaarao ärkas üles, ja vaata, see oli olnud unenägu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 8, 'Aga hommikul oli ta vaim rahutu ja ta läkitas järele ning kutsus kõik Egiptuse ennustajad ja targad, ja vaarao jutustas neile oma unenäo, aga ükski ei suutnud seda vaaraole seletada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 9, 'Siis rääkis joogikallajate ülem vaaraoga, öeldes: „Täna meenub mulle mu patt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 10, 'Vaarao sai väga kurjaks oma sulaste peale ja pani mind vahi alla ihukaitsepealiku kotta, minu ja pagarite ülema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 11, 'Ja me nägime ühel ööl unenägusid, mina ja tema, kumbki nägime unenäo, millel oli oma tähendus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 12, 'Ja sealsamas oli meiega heebrea noormees, ihukaitsepealiku sulane, ja me jutustasime temale ja tema seletas meile meie unenäod, kummalegi ta unenäo tähenduse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 13, 'Ja nõnda nagu ta meile seletas, nõnda sündis: mina sain tagasi oma ametisse ja teine poodi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 14, 'Siis vaarao läkitas järele ja laskis kutsuda Joosepi; ja Joosep toodi kiiresti vangiurkast välja; ta ajas habeme, vahetas riided ja tuli vaarao juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 15, 'Ja vaarao ütles Joosepile: „Ma nägin unenäo, aga keegi ei seleta seda. Aga ma olen kuulnud sinust räägitavat, et kui sina unenägu kuuled, siis sa seletad selle.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 16, 'Ja Joosep vastas vaaraole, öeldes: „Mina mitte! Küllap Jumal annab vaaraole õige vastuse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 17, 'Ja vaarao jutustas Joosepile: „Mu unenäos oli nõnda: vaata, ma seisin Niiluse jõe kaldal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 18, 'Ja näe, jõest tõusis seitse lehma, lihavad liha poolest ja ilusad näha, ja sõid roostikus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 19, 'Ja vaata, teist seitse lehma tõusis nende järel, väetid ja väga pahad näha ning lahjad liha poolest. Ei ole ma kogu Egiptusemaal näinud selliseid pahu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 20, 'Ja lahjad ning pahad lehmad sõid ära need seitse esimest lihavat lehma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 21, 'Ja need läksid nende kõhtu, aga ei olnud tundagi, et nad olid läinud nende kõhtu ja nende välimus oli paha nagu ennegi. Ja ma ärkasin üles.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 22, 'Siis ma nägin und, ja vaata, seitse viljapead tõusis ühest kõrrest, täis ja head.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 23, 'Ja vaata, seitse kuiva, peenikest, hommikutuulest kõrvetatud viljapead võrsus nende järel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 24, 'Ja peenikesed viljapead neelasid ära need seitse head viljapead. Ma olen seda rääkinud oma ennustajaile, aga ükski ei oska mulle seletada.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 25, 'Ja Joosep ütles vaaraole: „Vaarao unenäod tähendavad mõlemad üht ja sedasama. Jumal on vaaraole teada andnud, mida Ta kavatseb teha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 26, 'Seitse head lehma on seitse aastat ja seitse head viljapead on seitse aastat; unenägudel on sama tähendus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 27, 'Ja seitse lahjat ja paha lehma, kes tõusid nende järel, on seitse aastat, ja seitse tühja, hommikutuulest kõrvetatud viljapead on seitse nälja-aastat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 28, 'See ongi see asi, mis ma vaaraole rääkisin: mida Jumal kavatseb teha, seda on Ta vaaraole näidanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 29, 'Vaata, tuleb seitse aastat, millal on suur küllus kogu Egiptusemaal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 30, 'Aga neile järgneb seitse nälja-aastat, siis ununeb Egiptusemaal kõik küllus ja nälg toob maale hävituse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 31, 'Ei ole enam küllust tundagi siin maal nälja tõttu, mis tuleb selle järel, sest see on väga kange.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 32, 'Ja kuna vaaraol on unenägu kaks korda kordunud, siis on see asi Jumala poolt kindel ja Jumal laseb selle varsti sündida.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 33, 'Ja nüüd vaarao vaadaku välja üks mõistlik ja tark mees ja seadku Egiptusemaa üle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 34, 'Vaarao tehku nõnda: pangu ülevaatajad maale ja lasku Egiptusemaad maksta viiendikku neil seitsmel külluseaastal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 35, 'Ja kogutagu kõik nende tulevaste heade aastate toidus ja varutagu vilja linnadesse vaarao käe alla toiduseks ning säilitatagu seda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 36, 'See vili olgu maale tagavaraks seitsmeks nälja-aastaks, mis tulevad Egiptusemaale, et nälg ei laastaks maad.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 37, 'See kõne oli hea vaarao silmis ja kõigi ta sulaste silmis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 38, 'Ja vaarao ütles oma sulastele: „Kas leiame niisuguse mehe, kelles on Jumala Vaim?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 39, 'Ja vaarao ütles Joosepile: „Et Jumal on sulle kõike seda teada andnud, siis ei ole keegi nii mõistlik ja tark kui sina!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 40, 'Sina pead olema mu koja üle ja kogu mu rahvas kuulaku sinu sõna. Ainult aujärje poolest ma tahan olla suurem sinust!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 41, 'Ja vaarao ütles Joosepile: „Vaata, ma olen pannud su kogu Egiptusemaa üle!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 42, 'Ja vaarao võttis sõrmest pitserisõrmuse ning pani Joosepi sõrme; ja ta pani temale kallid linased riided selga ja riputas kuldkee kaela.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 43, 'Ja ta laskis teda sõita oma teises tõllas, mis tal oli, ja hüüda tema ees: „Põlvili!\" Ja ta pani tema kogu Egiptusemaa üle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 44, 'Ja vaarao ütles Joosepile: „Mina olen vaarao, aga ilma sinu tahtmata ei tohi keegi tõsta kätt ega jalga kogu Egiptusemaal!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 45, 'Ja vaarao pani Joosepile nimeks Sofnat Paneah ja andis temale naiseks Asnati, Ooni preestri Pooti-Fera tütre; ja Joosep läks välja Egiptusemaale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 46, 'Joosep oli kolmekümneaastane, kui ta astus vaarao, Egiptuse kuninga ette; ja Joosep läks ära vaarao juurest ning käis läbi kogu Egiptusemaa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 47, 'Ja maa kandis seitsmel külluseaastal ühest ivast peotäie.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 48, 'Ja ta kogus kõik põllusaagi seitsmel külluseaastal, mis Egiptusemaal olid, ja talletas selle linnadesse; igasse linna ta andis ümbruskonna põldude saagi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 49, 'Ja Joosep kuhjas väga palju vilja, nagu liiva mere ääres, kuni lakati seda mõõtmast, sest see ei olnud enam mõõdetav!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 50, 'Ja Joosepile sündis enne nälja-aasta tulekut kaks poega, keda temale tõi ilmale Asnat, Ooni preestri Pooti-Fera tütar.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 51, 'Ja Joosep pani esmasündinule nimeks Manasse, sest ta ütles: „Jumal on mind lasknud unustada kõik mu vaeva ja kogu mu isakoja!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 52, 'Ja teisele ta pani nimeks Efraim, sest ta ütles: „Jumal on mind teinud viljakaks mu viletsuse maal!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 53, 'Kui seitse külluseaastat, mis Egiptusemaale tulid, lõppesid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 54, 'siis hakkasid tulema seitse nälja-aastat, nõnda nagu Joosep oli öelnud; ja nälg oli kõigis maades, aga kogu Egiptusemaal oli leiba.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 55, 'Ja kui kogu Egiptusemaa tundis nälga, siis rahvas kisendas vaarao poole leiva pärast; ja vaarao ütles kõigile egiptlasile: „Minge Joosepi juurde. Mis tema teile ütleb, seda tehke!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 56, 'Ja nälg oli üle kogu maa ja Joosep avas kõik aidad ning müüs egiptlasile vilja; ja nälg võttis Egiptusemaal võimust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 57, 'Ja kõigist maadest tuldi Egiptusesse Joosepilt vilja ostma, sest nälg oli võtnud võimust kõigis maades.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 1, 'Kui Jaakob nägi, et Egiptuses oli vilja, siis Jaakob ütles oma poegadele: „Miks te üksteisele otsa vaatate?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 2, 'Ja ta ütles: „Vaata, ma olen kuulnud, et Egiptuses on vilja. Minge sinna ja ostke meile sealt, et jääksime elama ega sureks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 3, 'Siis läks kümme Joosepi venda Egiptusest vilja ostma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 4, 'Aga Benjamini, Joosepi venda, Jaakob ei läkitanud ühes vendadega, sest ta ütles: „Et temale õnnetust ei juhtuks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 5, 'Ja Iisraeli pojad tulid vilja ostma teiste tulijate seltsis, sest Kaananimaal oli nälg.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 6, 'Joosep oli maal valitsejaks; tema müüs vilja kogu maa rahvale. Ja Joosepi vennad tulid ning kummardasid tema ette silmili maha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 7, 'Kui Joosep nägi oma vendi, siis ta tundis nad ära, aga tegi ennast neile võõraks ja rääkis nendega karmilt ning küsis neilt: „Kust te tulete?\" Ja nad vastasid: „Kaananimaalt, et leiba osta!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 8, 'Joosep tundis oma vendi, aga nemad ei tundnud teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 9, 'Siis meenusid Joosepile unenäod, mis ta neist unes oli näinud, ja ta ütles neile: „Te olete maakuulajad. Olete tulnud vaatama, kust maa lahti on!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 10, 'Aga nemad ütlesid: „Ei, mu isand, su sulased on tulnud leiba ostma!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 11, 'Me kõik oleme ühe mehe pojad, oleme ausad mehed, su sulased ei ole maakuulajad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 12, 'Tema aga ütles neile: „Ei, küllap olete tulnud vaatama, kust maa lahti on!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 13, 'Siis nad vastasid: „Meie, su sulased, olime kaksteist venda, ühe mehe pojad Kaananimaalt. Ja vaata, noorim on praegu meie isa juures, aga üht ei ole enam olemas.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 14, 'Aga Joosep ütles neile: „See on nõnda, nagu ma teile olen rääkinud ja öelnud: te olete maakuulajad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 15, 'Selles asjas ma katsun teid läbi: nii tõesti kui vaarao elab, ei pääse te siit minema enne kui teie noorim vend on siia tulnud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 16, 'Läkitage eneste hulgast üks, et ta tooks teie venna. Teid aga jäetakse vangi ja teie kõnesid uuritakse, kas neis on tõtt. Muidu olete, nii tõesti kui vaarao elab, maakuulajad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 17, 'Ja ta pani nad üheskoos kolmeks päevaks vahi alla.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 18, 'Ja kolmandal päeval ütles Joosep neile: „Tehke seda, siis jääte elama! Mina olen jumalakartlik:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 19, 'kui olete ausad mehed, jäägu üks teie vendadest aheldatuna hoonesse, kus te olite vahi all, aga teised minge viige vilja näljahäda pärast teie kodudes');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 20, 'ja tooge mu juurde oma noorim vend, siis saavad teie kõned tõeks ja te ei pea surema!\" Ja nad tegid nõnda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 21, 'Ja nad ütlesid üksteisele: „Me oleme tõesti süüdi oma venna pärast, kelle hinge kitsikust me nägime, kui ta palus meilt armu ja meie ei võtnud teda kuulda. Sellepärast on see kitsikus meile tulnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 22, 'Ja Ruuben vastas neile, öeldes: „Eks ma rääkinud teile ja öelnud: ärge tehke pattu poisi vastu! Aga teie ei kuulanud. Vaata, nüüd nõutaksegi tema verd!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 23, 'Aga nad ei teadnud, et Joosep seda mõistis, sest ta rääkis nendega tõlgi kaudu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 24, 'Ja ta pöördus ära nende juurest ning nuttis; siis ta tuli tagasi nende juurde ja rääkis nendega ning võttis nende hulgast Siimeoni ja laskis tema nende silme ees vangistada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 25, 'Ja Joosep käskis täita nende kotid viljaga, panna igaühe raha tagasi ta kotti ja anda neile teemoona; ja neile tehti nõnda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 26, 'Siis nad tõstsid vilja eeslite selga ja läksid sealt ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 27, 'Kui üks oma koti avas, et öömajal eeslile toitu anda, siis ta nägi oma raha, ja vaata, see oli koti suus!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 28, 'Ja ta ütles oma vendadele: „Mu raha on tagasi antud, ja vaata, see on mu kotis!\" Siis kadus nende julgus ja nad värisesid, öeldes isekeskis: „Miks on Jumal meile seda teinud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 29, 'Ja nad tulid oma isa Jaakobi juurde Kaananimaale ning andsid temale teada kõik, mis neile oli juhtunud, öeldes:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 30, '„See mees, maa isand, rääkis meiega karmilt ja pidas meid salajasiks maakuulajaiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 31, 'Aga me ütlesime temale: me oleme ausad mehed, me ei ole maakuulajad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 32, 'Meid oli kaksteist venda, oma isa pojad. Üht ei ole enam ja noorim on praegu meie isa juures Kaananimaal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 33, 'Ja see mees, maa isand, ütles meile: sellest ma saan teada, kas te olete ausad: jätke üks oma vendadest minu juurde ja võtke vilja näljahäda pärast teie kodudes ja minge');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 34, 'ning tooge oma noorim vend minu juurde, et saaksin teada, et te ei ole maakuulajad, vaid olete ausad mehed. Siis ma annan teile venna tagasi ja te võite maal liikuda vabalt.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 35, 'Ja kui nad oma kotte tühjendasid, vaata, siis oli igaühe rahakukkur ta kotis! Ja nähes oma rahakukruid, nemad ja nende isa hakkasid kartma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 36, 'Ja nende isa Jaakob ütles neile: „Te teete mind lapsetuks! Joosepit ei ole enam ja Siimeoni ei ole enam ja Benjamini tahate ka võtta. See kõik on tulnud minu peale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 37, 'Aga Ruuben rääkis oma isale, öeldes: „Võid surmata mu mõlemad pojad, kui ma ei too teda sinu juurde! Anna ta minu hooleks ja ma toon ta tagasi sinu juurde!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 38, 'Kuid tema ütles: „Minu poeg ei lähe ühes teiega alla, sest ta vend on surnud ja tema üksi on üle jäänud. Kui temaga teel, mida te käite, õnnetus juhtub, siis saadate mu hallid juuksed murega hauda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 1, 'Aga maal oli kange nälg.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 2, 'Ja kui nad olid ära söönud vilja, mis nad Egiptusest olid toonud, siis nende isa ütles neile: „Minge ostke meile pisut leiba!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 3, 'Ja Juuda vastas temale, öeldes: „See mees kinnitas meile väga ja ütles: te ei saa näha mu nägu, kui teie vend ei ole ühes teiega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 4, 'Kui sa nüüd läkitad meie venna ühes meiega, siis läheme ja ostame sulle leiba.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 5, 'Aga kui sa teda ei läkita, siis me ei lähe, sest see mees ütles meile: te ei saa näha mu nägu, kui teie vend ei ole ühes teiega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 6, 'Ja Iisrael ütles: „Miks tegite mulle seda kurja ja andsite mehele teada, et teil on veel üks vend?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 7, 'Ja nad vastasid: „See mees päris väga meie ja meie suguseltsi järele, küsides: kas teie isa alles elab? Ons teil veel mõni vend? Ja me andsime temale teada, nagu asi on. Kas võisime teada, et ta ütleb: tooge oma vend siia?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 8, 'Ja Juuda ütles oma isale Iisraelile: „Saada poiss minuga ja me võtame kätte ning läheme, et jääksime elama ega sureks, ei meie ega sina ega meie väetid lapsed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 9, 'Mina olen temale käemeheks, nõua teda minult. Kui ma ei too teda tagasi sinu juurde ja ei sea sinu palge ette, siis jään su ees alatiseks süüdlaseks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 10, 'Tõesti, kui me ei oleks viivitanud, oleksime nüüd juba teist korda tagasi tulnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 11, 'Siis ütles neile Iisrael, nende isa: „Kui see nõnda on, siis tehke seda. Võtke maa parimast oma kottidesse ja viige sellele mehele meeleheaks pisut palsamit ja pisut mett, kalleid rohte ja mürri, pähkleid ja mandleid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 12, 'Ja võtke ühes kahekordne raha; ka see raha, mis teie kottide suus tagasi tuli, võtke jälle ühes - vahest oli see eksitus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 13, 'Võtke ka oma vend, asuge teele ja minge tagasi selle mehe juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 14, 'Kõigeväeline Jumal lasku teid leida halastust selle mehe ees, et ta teiega ära saadaks teie teise venna ja Benjamini. Aga kui jään lasteta, siis jään lasteta!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 15, 'Ja mehed võtsid selle kingituse, võtsid ühes kahekordse raha ja Benjamini, asusid teele ja läksid alla Egiptusesse ning astusid Joosepi ette.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 16, 'Kui Joosep nägi, et Benjamin oli nendega kaasas, siis ta ütles oma kojaülemale: „Vii need mehed mu kotta ja tapa tapaveis ning valmista see, sest need mehed söövad minuga lõunat!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 17, 'Ja mees tegi, nagu Joosep oli öelnud; ja mees viis mehed Joosepi kotta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 18, 'Aga mehed kartsid, kui neid Joosepi kotta viidi, ja ütlesid: „Meid viiakse raha pärast, mis eelmisel korral meie kottides tagasi tuli, et veeretada meie peale süüd ja kippuda meile kallale ning võtta orjadeks meid ja meie eeslid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 19, 'Ja nad astusid mehe juurde, kes oli Joosepi kojaülem, ja rääkisid temaga koja ukse ees');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 20, 'ning ütlesid: „Oh mu isand, me kord juba käisime siin leiba ostmas!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 21, 'Aga kui me seejärel jõudsime öömajale ja tegime oma kotid lahti, vaata, siis oli igamehe raha ta koti suus, meie raha selle täiskaalus! Me tõime selle nüüd tagasi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 22, 'Ja me tõime ka teise raha leiva ostmiseks enestega kaasa. Me ei tea, kes oli pannud meie raha meie kottidesse.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 23, 'Aga tema ütles: „Rahustuge, ärge kartke! Teie Jumal ja teie isa Jumal on pannud teile varanduse kottidesse! Teie raha ma olen saanud.\" Ja ta tõi Siimeoni välja nende juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 24, 'Siis mees viis mehed Joosepi kotta ja andis neile vett ning nad pesid oma jalgu; ja ta andis toitu nende eeslitele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 25, 'Ja nad seadsid kingitused valmis, kuni Joosep tuli lõunale, sest nad olid kuulnud, et nad seal pidid leiba võtma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 26, 'Kui Joosep koju tuli, siis nad viisid temale kotta kingituse, mis neil kaasas oli, ja kummardasid maha tema ette.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 27, 'Ja tema küsis neilt, kuidas nende käsi käib, ja ütles: „Kas teie vana isa, kellest te rääkisite, käsi hästi käib? Ons ta alles elus?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 28, 'Ja nad vastasid: „Su sulase, meie isa käsi käib hästi, ta elab alles.\" Ja nad kummardasid ning heitsid maha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 29, 'Kui ta oma silmad üles tõstis ja nägi oma venda Benjamini, oma ema poega, siis ta küsis: „Ons see teie noorim vend, kellest te mulle rääkisite?\" Ja ta ütles: „Jumal olgu sulle armuline, mu poeg!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 30, 'Aga Joosep vaikis äkki, sest ta oli südamest liigutatud oma venna pärast ja otsis võimalust nutmiseks. Ta läks ühte siseruumi ja nuttis seal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 31, 'Ja kui ta oma silmi oli pesnud, siis ta tuli välja, valitses enese üle ja ütles: „Pange toit lauale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 32, 'Ja temale pandi eraldi ja neile eraldi, ja egiptlastele, kes tema juures sõid, eraldi, sest egiptlased ei söö leiba üheskoos heebrealastega - see on egiptlastele jäledus!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 33, 'Ja nad pandi istuma temaga vastamisi, esmasündinu esimesena ja noorim viimsena, ja mehed panid seda isekeskis imeks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 34, 'Ja ta laskis tõsta rooga enda eest nende ette, aga Benjamini osa oli viis korda suurem kui kõigi teiste osad. Ja nad jõid ühes temaga ning jäid joobnuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 1, 'Siis ta käskis oma kojaülemat, öeldes: „Täida meeste kotid viljaga, nii palju kui nad jaksavad kanda, ja pane igamehe raha ta koti suhu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 2, 'Ja minu karikas, see hõbekarikas, pane noorima koti suhu ühes ta viljarahaga!\" Ja ta tegi, nagu Joosep käskis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 3, 'Hommikul, kui läks valgeks, lasti minema mehed ja nende eeslid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 4, 'Kui nad olid linnast väljunud ega olnud veel kaugele jõudnud, ütles Joosep oma kojaülemale: „Võta kätte, aja mehi taga, ja kui oled nad tabanud, siis ütle neile: miks tasute head kurjaga?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 5, 'Eks see ole see, millest mu isand joob ja millest ta endeid otsib? Olete käitunud halvasti, et tegite nõnda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 6, 'Ja kui ta nad tabas, siis ta rääkis neile needsamad sõnad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 7, 'Aga nad vastasid temale: „Miks räägib mu isand nõndaviisi? Su sulasele oleks teotuseks nõnda teha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 8, 'Vaata, raha, mis me leidsime oma kottide suust, tõime sulle tagasi Kaananimaalt. Kuidas võiksime siis varastada su isanda kojast hõbedat või kulda?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 9, 'Kelle juurest su sulaste hulgast see leitakse, surgu, ja me teised jääme su isandale orjadeks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 10, 'Siis ta ütles: „Olgu see nüüd ka nõnda teie sõnade järgi. Kelle juurest see leitakse, jäägu mulle sulaseks, ja te teised olete süüta!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 11, 'Ja nad tõttasid ning tõstsid igaüks oma koti maha ja iga mees tegi oma koti lahti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 12, 'Ja ta otsis nad läbi, alates vanemast ja lõpetades nooremaga - ja karikas leiti Benjamini kotist!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 13, 'Siis nad käristasid oma riided lõhki, iga mees pani oma eeslile koorma selga ja nad läksid tagasi linna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 14, 'Ja Juuda läks ühes oma vendadega Joosepi kotta, kes oli alles seal, ja nad heitsid maha tema ette.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 15, 'Ja Joosep ütles neile: „Mis tegu see on, mis te olete teinud? Eks te tea, et niisugune mees nagu mina, oskab endeid seletada?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 16, 'Siis vastas Juuda: „Mida me oma isandale ütleme? Kuidas peame rääkima ja kuidas endid õigustama? Jumal on avastanud su sulaste süü! Vaata, me jääme oma isandale sulaseiks, niihästi meie kui see, kelle käest karikas leiti!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 17, 'Aga ta ütles: „Ei tule mulle mõttessegi seda teha! Mees, kelle käest karikas leiti, peab jääma mulle sulaseks, te teised aga minge rahuga oma isa juurde!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 18, 'Siis astus Juuda tema juurde ja ütles: „Mu isand, luba ometi oma sulast rääkida üks sõna mu isanda kõrva ees ja su viha ärgu süttigu põlema oma sulase vastu, sest sina oled nagu vaarao ise!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 19, 'Mu isand küsis oma sulaseilt, öeldes: ons teil isa või mõni vend?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 20, 'Ja meie vastasime oma isandale: meil on vana isa ja üks nooruk, ta vanas eas sündinud, alles noor; aga selle vend on surnud ja ta on üksi oma emast järele jäänud, ja ta isa armastab teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 21, 'Ja sa ütlesid oma sulaseile: tooge ta minu juurde, et ma näeksin teda oma silmaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 22, 'Ja me vastasime oma isandale: nooruk ei või isast lahkuda. Kui ta lahkuks oma isast, siis see sureks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 23, 'Ja sa ütlesid oma sulaseile: kui teie noorim vend ei tule ühes teiega, siis te ei saa enam näha mu nägu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 24, 'Ja kui me läksime su sulase, meie isa juurde ja andsime temale teada oma isanda sõnad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 25, 'siis ütles meie isa: minge jälle tagasi, ostke meile pisut leiba.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 26, 'Aga me vastasime: me ei või minna. Kui meie noorim vend on ühes meiega, siis me läheme, sest me ei saa näha selle mehe nägu, kui meie noorim vend ei ole ühes meiega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 27, 'Ja su sulane, minu isa, ütles meile: te teate, et mu naine on mulle kaks poega ilmale toonud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 28, 'Aga üks läks mu juurest ära ja ma ütlesin: ta on tõesti maha murtud! Ja ma pole teda tänini enam näinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 29, 'Kui te nüüd võtate ka selle mu silma alt ja temaga juhtub õnnetus, siis saadate mu hallid juuksed kurvastusega hauda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 30, 'Ja kui ma nüüd tuleksin su sulase, oma isa juurde ja ühes meiega ei oleks poiss, kelle hingesse tema hing on kiindunud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 31, 'ja kui ta siis näeb, et poissi ei ole, ta sureb ja su sulased on sinu sulase, meie isa hallid juuksed saatnud murega hauda,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 32, 'sest su sulane on poisile käemeheks mu isa juures, kuna ma olen öelnud: kui ma ei too teda sinu juurde, siis ma jään oma isa ees alatiseks süüdlaseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 33, 'Ja nüüd siis jäägu su sulane poisi asemel sulaseks mu isandale ja poiss mingu ühes oma vendadega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 34, 'Sest kuidas võiksin minna oma isa juurde, kui poiss ei oleks ühes minuga? Et ma ei näeks seda õnnetust, mis juhtub mu isaga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 1, 'Siis Joosep ei suutnud enam enese üle valitseda kõigi nende ees, kes ta juures seisid, ja ta hüüdis: „Minge kõik mu juurest ära!\" Ja ükski ei seisnud tema juures, kui Joosep ennast vendadele tunda andis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 2, 'Ja ta puhkes valjusti nutma, nõnda et egiptlased seda kuulsid ja vaarao kojas saadi sellest teada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 3, 'Ja Joosep ütles oma vendadele: „Mina olen Joosep! Kas mu isa alles elab?\" Aga vennad ei suutnud temale vastata, nõnda hirmunud olid nad tema palge ees!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 4, 'Siis ütles Joosep, oma vendadele: „Astuge ligemale!\" Ja nad astusid ligemale ning ta ütles: „Mina olen Joosep teie vend, kelle te müüsite Egiptusesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 5, 'Aga nüüd ärge kurvastage ja ärgu süttigu teil isekeskis viha, et müüsite mind siia, sest elu säilitamiseks läkitas Jumal mind teie eele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 6, 'Sest kaks aastat on olnud nüüd nälg maal ja on veel viis aastat, mil ei ole kündi ega lõikust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 7, 'Seepärast Jumal läkitas mind teie eele kindlustama teile järeltulijaid maa peal ja hoidma teid elus, pääsemiseks paljudele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 8, 'Nüüd aga ei ole teie mind läkitanud siia, vaid Jumal, ja tema on mind pannud vaaraole isaks ja kogu ta kojale isandaks ning valitsejaks kogu Egiptusemaale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 9, 'Tõtake ja minge mu isa juurde ning öelge temale: nõnda ütleb su poeg Joosep: Jumal on mind pannud isandaks kogu Egiptusele. Tule minu juurde, ära viivita!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 10, 'Sa võid elada Gooseni maakonnas ja olla mu läheduses, sina ja pojad ja su poegade pojad, ja su pudulojused ja su veised ja kõik, mis sul on.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 11, 'Ma tahan seal hoolitseda sinu eest, sest veel viis aastat on näljahäda, et ei jääks vaeseks sina ega su pere ega kõik, kes sul on.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 12, 'Ja vaata, te näete oma silmaga, samuti näeb mu vend Benjamin oma silmaga, et ma tõepoolest ise teiega räägin!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 13, 'Jutustage mu isale kõigest minu aust Egiptuses ja kõigest, mida olete näinud, ja tõtake ning tooge mu isa siia!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 14, 'Siis ta langes oma vennale Benjaminile ümber kaela ja nuttis, ja Benjamin nuttis tema kaela ümber.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 15, 'Ja ta andis suud kõigile oma vendadele ning nuttis üheskoos nendega; ja seejärel ta vennad rääkisid temaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 16, 'Kuuldus kostis ka vaarao kotta, et Joosepi vennad olid tulnud, ja see oli hea vaarao silmis ja tema sulaste silmis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 17, 'Ja vaarao ütles Joosepile: „Ütle oma vendadele: tehke nõnda: koormake oma veoloomad ja minge Kaananimaale,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 18, 'võtke oma isa ja oma pered ning tulge minu juure, siis ma annan teile Egiptusemaa parimat ja te saate süüa maa rasva!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 19, 'Ja sul tuleb anda käsk: tehke nõnda: võtke endile Egiptusemaalt vankrid väetite laste ja naiste jaoks ja tooge oma isa ning tulge.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 20, 'Teie silm ärgu kurvastagu teie asjade pärast, sest parim kogu Egiptusemaal peab olema teie päralt!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 21, 'Ja Iisraeli pojad tegid nõnda ja Joosep andis neile vaarao käsu peale vankrid, samuti andis ta neile teemoona.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 22, 'Ta andis neile kõigile peoriided, aga Benjaminile andis ta kolmsada hõbeseeklit ja viied peoriided.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 23, 'Samuti läkitas ta oma isale kümme eeslit, kes olid koormatud Egiptuse parimate kaupadega, ja kümme emaeeslit, kes kandsid vilja, leiba ja moona ta isale teekonna tarvis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 24, 'Siis ta saatis oma vennad minema ja nad läksid. Ja ta ütles neile: „Teel ärge riielge!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 25, 'Ja nad läksid ära Egiptusest ning tulid Kaananimaale oma isa Jaakobi juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 26, 'Ja nad jutustasid temale ning ütlesid: „Joosep elab alles, ta on nimelt kogu Egiptusemaa valitseja!\" Aga ta süda jäi külmaks, sest ta ei uskunud neid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 27, 'Siis nad jutustasid temale kõigest, mis Joosep nendega oli rääkinud. Ja kui ta nägi vankreid, mis Joosep oli läkitanud teda ära tooma, siis nende isa Jaakobi vaim elustus taas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 28, 'Ja Iisrael ütles: „Küllalt! Mu poeg Joosep elab alles! Ma tahan minna ja teda näha, enne kui suren!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 1, 'Nõnda Iisrael läks teele ühes kõigega, mis tal oli, ja tuli Beer-Sebasse ning ohverdas tapaohvreid oma isa Iisaki Jumalale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 2, 'Ja Jumal rääkis Iisraeliga öösel nägemuses; ta ütles: „Jaakob, Jaakob!\" Ja see vastas: „Siin ma olen!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 3, 'Siis ta ütles: „Mina olen Jumal, sinu isa Jumal, ära karda minna Egiptusesse, sest ma teen sind seal suureks rahvaks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 4, 'Mina lähen ühes sinuga Egiptusesse ja ma toon sind sealt tagasi, ja Joosep suleb oma käega su silmad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 5, 'Ja Jaakob asus Beer-Sebast minekule; Iisraeli pojad panid oma isa Jaakobi ja oma väetid lapsed ja naised vankritesse, mis vaarao oli läkitanud teda tooma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 6, 'Ja nad võtsid oma karjad ja varanduse, mis nad Kaananimaal olid soetanud, ja tulid Egiptusesse, Jaakob ja kõik ta sugu ühes temaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 7, 'Oma pojad ja poegade pojad, tütred ja poegade tütred ja kogu oma soo viis ta ühes enesega Egiptusesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 8, 'Ja need on Iisraeli laste nimed, kes Egiptusesse tulid: Jaakob ja tema pojad. Jaakobi esmasündinu oli Ruuben.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 9, 'Ruubeni pojad olid Hanok, Pallu, Hesron ja Karmi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 10, 'Siimeoni pojad olid Jemuel, Jaamin, Ohad, Jaakin, Sohar ja Saul, kaananlanna poeg.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 11, 'Leevi pojad olid Geerson, Kehat ja Merari.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 12, 'Juuda pojad olid Eer, Oonan, Seela, Perets ja Serah; aga Eer ja Oonan surid Kaananimaal. Peretsi pojad olid Hesron ja Haamul.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 13, 'Issaskari pojad olid Toola, Puuva, Joob ja Simron.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 14, 'Sebuloni pojad olid Sered, Eelon ja Jahleel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 15, 'Need olid Lea pojad, keda ta Jaakobile ilmale tõi Mesopotaamias; peale nende oli tal tütar Diina. Kõiki ta poegi ja tütreid oli ühtekokku kolmkümmend kolm hinge.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 16, 'Gaadi pojad olid Sifjon, Haggi, Suuni, Esbon, Eeri, Arodi ja Areli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 17, 'Aaseri pojad olid Jimna, Jisva, Jisvi ja Beria; nende õde oli Serah. Beria pojad olid Heber ja Malkiel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 18, 'Need olid Silpa pojad, kelle Laaban andis oma tütrele Leale ja kes tõi need Jaakobile ilmale, kuusteist hinge.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 19, 'Jaakobi naise Raaheli pojad olid Joosep ja Benjamin.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 20, 'Joosepile sündisid Egiptusemaal pojad, keda temale ilmale tõi Asnat, Ooni preestri Pooti-Fera tütar: Manasse ja Efraim.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 21, 'Benjamini pojad olid Bela, Beker, Asbel, Geera, Naaman, Eehi, Roos, Muppim, Huppim ja Aard.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 22, 'Need olid Raaheli pojad, kes Jaakobile sündisid, ühtekokku neliteist hinge.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 23, 'Daani poeg oli Husim.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 24, 'Naftali pojad olid Jahsel, Guuni, Jeeser ja Sillem.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 25, 'Need olid Billa pojad, kelle Laaban andis oma tütrele Raahelile ja kes tõi need Jaakobile ilmale, ühtekokku seitse hinge.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 26, 'Kõiki hingi, kes Jaakobiga Egiptusesse tulid, kes tema niudeist olid väljunud, peale Jaakobi poegade naiste, oli ühtekokku kuuskümmend kuus hinge.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 27, 'Ja Joosepi poegi, kes temale Egiptuses olid sündinud, oli kaks hinge; kõiki Jaakobi soo hingi, kes Egiptusesse tulid, oli seitsekümmend.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 28, 'Ja ta läkitas Juuda enese eel Joosepi juurde, et see teda juhataks Goosenisse; nõnda nad tulid Gooseni maakonda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 29, 'Ja Joosep laskis hobused oma vankri ette rakendada ning läks Goosenisse vastu oma isale Iisraelile; kui ta teda nägi, siis ta langes temale kaela ja nuttis kaua tema kaelas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 30, 'Ja Iisrael ütles Joosepile: „Nüüd ma võin surra, sest olen näinud su nägu ja tean, et oled alles elus!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 31, 'Ja Joosep ütles oma vendadele ja oma isa perele: „Ma lähen ja teatan vaaraole ning ütlen temale: mu vennad ja mu isa pere, kes olid Kaananimaal, on minu juurde tulnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 32, 'Ja need mehed on pudulojuste karjased, sest nad olidki karjakasvatajad, ja nad on oma pudulojused ja veised ja kõik, mis neil oli, kaasa toonud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 33, 'Kui juhtub, et vaarao teid kutsub ja küsib: mis teie amet on?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 34, 'siis vastake: su sulased on olnud karjakasvatajad noorest põlvest kuni tänini, niihästi meie kui meie isad - et saaksite elada Gooseni maakonnas, sest egiptlased põlgavad kõiki pudulojuste karjaseid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 1, 'Ja Joosep läks ja teatas vaaraole ning ütles: „Mu isa ja vennad ja nende pudulojused ja veised ja kõik, mis neil oli, on tulnud Kaananimaalt, ja vaata, nad on Gooseni maakonnas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 2, 'Ja ta võttis oma vendade hulgast viis meest ja tõi need vaarao ette.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 3, 'Ja vaarao küsis tema vendadelt: „Mis teie amet on?\" Ja nad vastasid vaaraole: „Su sulased on pudulojuste karjased, niihästi meie ise kui meie isad.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 4, 'Ja nad ütlesid vaaraole: „Me oleme maale tulnud võõrastena elama, sest su sulaste karjale ei olnud sööta, kuna Kaananimaal on kange nälg. Luba siis nüüd oma sulaseid elada Gooseni maakonnas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 5, 'Ja vaarao rääkis Joosepiga, öeldes: „Sinu isa ja vennad on su juurde tulnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 6, 'Egiptusemaa on su ees lahti, pane oma isa ja vennad elama parimasse maakonda. Elagu nad Gooseni maakonnas, ja kui sa tunned nende hulgast tublisid mehi, siis pane need mu karja ülevaatajaiks.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 7, 'Siis Joosep tõi sisse oma isa Jaakobi ja pani seisma vaarao ette; ja Jaakob õnnistas vaaraot.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 8, 'Ja vaarao küsis Jaakobilt: „Kui palju sul eluaastaid on?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 9, 'Ja Jaakob vastas vaaraole: „Aastaid, mis ma võõrana olen elanud, on sada kolmkümmend aastat. Piskud ja kurjad on olnud mu eluaastad ja need ei ulatu mu isade eluaastateni nende võõrsiloleku ajal!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 10, 'Siis Jaakob õnnistas vaaraot ja läks ära vaarao juurest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 11, 'Ja Joosep paigutas oma isa ja vennad elama ja andis neile maaomandi Egiptusemaal kõige paremas maakonnas, Raamsese maakonnas, nagu vaarao oli käskinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 12, 'Ja Joosep hoolitses leivaga oma isa ja vendade ja kogu isa pere eest, vastavalt nende väetite laste suudele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 13, 'Aga kogu maal ei olnud leiba, sest näljahäda oli väga kange, ja Egiptusemaa ja Kaananimaa olid näljast nõrkemas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 14, 'Ja Joosep kogus kokku kõik Egiptusemaal ja Kaananimaal leiduva raha vilja eest, mida osteti; ja Joosep viis raha vaarao kotta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 15, 'Kui raha oli lõppenud Egiptusemaalt ja Kaananimaalt, siis tulid kõik egiptlased Joosepi juurde, öeldes: „Anna meile leiba! Kas peame su silma ees surema, sellepärast et raha on otsas?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 16, 'Ja Joosep vastas: „Andke oma lojused ja mina annan teile lojuste eest, kui raha on otsas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 17, 'Ja nad tõid oma lojused Joosepile ja Joosep andis neile leiba hobuste, pudulojuste karjade, veiste karjade ja eeslite eest; nõnda muretses ta neile sel aastal leiba kõigi nende lojuste eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 18, 'Kui see aasta lõppes, siis järgmisel aastal nad tulid tema juurde ja ütlesid temale: „Me ei saa oma isandale salata, et raha on otsas ja lojuste karjad on meie isanda käes. Meil ei ole isanda ees muud üle jäänud kui ainult meie ihud ja põllumaa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 19, 'Kas peame su silma ees surema, niihästi me ise kui meie põllud? Osta meid ja meie põllud leiva eest, et me oma põldudega saaksime vaaraole orjadeks. Anna meile seemet, et jääksime elama ega sureks ja et põllud ei jääks tühjaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 20, 'Siis Joosep ostis vaaraole kogu Egiptuse põllumaa, sest egiptlased müüsid igaüks oma põllu, sellepärast, et nälg ahistas neid. Nõnda sai maa vaarao omaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 21, 'Ja ta tegi rahva temale orjaks, ühest Egiptuse raja äärest teise ääreni.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 22, 'Ainult preestrite põldusid ta ei ostnud, sest preestritel oli sissetulek vaaraolt ja nemad elatusid sissetulekust, mida vaarao neile andis; seepärast nemad ei müünud oma põldusid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 23, 'Ja Joosep ütles rahvale: „Vaata, ma olen nüüd ostnud teid ja teie põllud vaaraole. Näe, siin on teile seemet, külvake see põldudele!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 24, 'Aga saagist peate andma viiendiku vaaraole, kuna neli osa jäägu teie kätte teile põlluseemneks, samuti toiduseks teile ja neile, kes teie peredes on, ja toiduseks teie väeteile lastele.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 25, 'Ja nad vastasid: „Sina oled meid elus hoidnud! Kui leiame armu oma isanda silmis, siis jääme vaaraole orjadeks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 26, 'Ja Joosep tegi selle seaduseks Egiptuse põllumaa kohta tänapäevani, et vaaraole saab viiendik; ainult preestrite põllud ei saanud vaarao omaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 27, 'Ja Iisrael jäi elama Egiptusemaale Gooseni maakonda; nad jäid sinna paigale, olid viljakad ja neid sai väga palju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 28, 'Ja Jaakob elas Egiptusemaal seitseteist aastat, ja Jaakobi päevi, tema eluaastaid, oli sada nelikümmend seitse aastat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 29, 'Kui Iisraeli surma päev ligines, siis ta kutsus oma poja Joosepi ning ütles temale: „Kui ma nüüd olen su silmis armu leidnud, siis pane oma käsi mu puusa alla ja osuta mulle heldust ja truudust: ära mata mind Egiptusesse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 30, 'sest ma tahan magada oma vanemate juures. Vii mind Egiptusest ära ja mata nende hauda!\" Ja ta vastas: „Ma teen su sõna järgi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 31, 'Ja tema ütles: „Vannu mulle!\" Ja ta vandus temale. Siis Iisrael kummardas voodi peatsi poole.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 1, 'Pärast seda sündmust öeldi Joosepile: „Vaata, su isa on haige!\" Siis ta võttis oma kaks poega enesega, Manasse ja Efraimi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 2, 'Ja Jaakobile anti teada ning öeldi: „Näe, su poeg Joosep tuleb sinu juurde!\" Iisrael võttis siis jõu kokku ning tõusis voodis istukile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 3, 'Ja Jaakob ütles Joosepile: „Kõigeväeline Jumal ilmutas ennast mulle Luusis Kaananimaal, ja õnnistas mind');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 4, 'ning ütles mulle: vaata, ma teen sind viljakaks ja paljuks ja teen sinust rahvaste hulga ja annan selle maa sinu soole pärast sind igaveseks omandiks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 5, 'Ja nüüd olgu su kaks poega, kes sulle Egiptusemaal on sündinud, enne kui ma tulin sinu juurde Egiptusesse, minu omad: Efraim ja Manasse olgu minu omad nagu Ruuben ja Siimeongi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 6, 'Aga su järeltulijad, kes sulle pärast neid sünnivad, olgu sinu päralt ja neid nimetatagu nende vendade nimede järgi nende pärisosades.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 7, 'Kui ma Mesopotaamiast tulin, suri mul Raahel Kaananimaal, tee peal, kui veel tükk maad oli minna Efratasse. Ja ma matsin tema Efrata tee äärde, see on Petlemma.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 8, 'Kui Iisrael nägi Joosepi poegi, siis ta küsis: „Kes need on?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 9, 'Ja Joosep vastas oma isale: „Need on mu pojad, keda Jumal mulle siin on andnud!\" Siis ta ütles: „Too nad minu juurde, et ma neid õnnistaksin!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 10, 'Aga Iisraeli silmad olid vanadusest tuhmid ja ta ei näinud enam. Siis Joosep viis nad tema juurde ja tema andis neile suud ning süleles neid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 11, 'Ja Iisrael ütles Joosepile: „Ei oleks uskunud, et saan näha su nägu, aga vaata, Jumal on mind lasknud näha ka su järglasi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 12, 'Siis Joosep võttis nad ära tema põlvilt ja kummardas silmili maha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 13, 'Ja Joosep võttis nad mõlemad, Efraimi oma paremale käele, Iisraelist vasakule poole, ja Manasse oma vasakule käele, Iisraelist paremale poole, ja viis nad tema juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 14, 'Aga Iisrael sirutas oma parema käe ja pani Efraimi pea peale, kes oli noorem, ja oma vasaku käe Manasse pea peale; ta pani oma käed ristamisi, sest Manasse oli esmasündinu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 15, 'Ja ta õnnistas Joosepit ning ütles: „Jumal, Kelle palge ees mu isad Aabraham ja Iisak on rännanud, Jumal, Kes on olnud mu karjane kogu mu elu kuni tänapäevani,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 16, 'Ingel, Kes mind on päästnud kõigest kurjast, õnnistagu neid poisse; neid nimetades nimetatagu minu nime ja mu isade Aabrahami ja Iisaki nime! Ja nad siginegu rohkesti keset maad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 17, 'Aga kui Joosep nägi, et ta isa oma parema käe asetas Efraimi pea peale, siis ta pani seda pahaks ja haaras kinni oma isa käest, et seda Efraimi pea pealt tõsta Manasse pea peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 18, 'Ja Joosep ütles oma isale: „Mitte nõnda, mu isa, sest see on mu esmasündinu! Pane oma parem käsi tema pea peale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 19, 'Aga ta isa keeldus ja ütles: „Ma tean, mu poeg, ma tean, ka tema peab saama rahvaks ja temagi peab olema suur! Ometi peab ta noorem vend saama temast suuremaks ja selle sugu olema rahvarohke!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 20, 'Ja ta õnnistas neid sel päeval, öeldes: „Sinu nimel õnnistatagu Iisraelis, öeldagu: Jumal tehku sind Efraimi ja Manasse sarnaseks!\" Nõnda seadis ta Efraimi Manassest ettepoole.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 21, 'Ja Iisrael ütles Joosepile: „Vaata, ma suren, aga Jumal on teiega ja viib teid tagasi teie isade maale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 22, 'Ja ma annan sulle ühe mäeseljandiku rohkem kui su vendadele, mille olen mõõga ja ammuga võtnud emorlastelt!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 1, 'Ja Jaakob kutsus oma pojad enese juurde ning ütles: „Tulge kokku, siis ma kuulutan teile, mis teiega sünnib tulevasil päevil!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 2, 'Kogunege ja kuulge, Jaakobi pojad, võtke kuulda Iisraeli, oma isa!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 3, 'Ruuben, sina oled mu esmasündinu, mu rammu ja sigitusjõu esikpoeg, väljapaistev väärikuselt ja väljapaistev võimult!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 4, 'Veena oled vooganud - esikohale sa ei jää, sest sa tõusid oma isa sängi! Sel korral sa rüvetasid mu voodi, kuhu sa olid tõusnud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 5, 'Siimeon ja Leevi, vennaksed, nende ümberlõikamisnoad on vägivalla riistad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 6, 'Nende nõusse ei astu mu hing, nende seltsiga ei liitu mu süda! Sest oma vihas nad tapsid mehi ja meelevallatuses halvasid härgi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 7, 'Olgu neetud nende kange viha ja nende range raev! Ma jaotan nad Jaakobis ja hajutan Iisraelis!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 8, 'Juuda, sind ülistavad su vennad! Sinu käsi on su vaenlaste turjal, sind kummardavad su isa pojad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 9, 'Juuda on lõukoera kutsikas - saagi kallalt, mu poeg, oled tõusnud! Ta on heitnud maha, ta lebab nagu kunagi lõukoer või lõvi - kes julgeks teda äratada?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 10, 'Ei lahku valitsuskepp Juudast ega korraldamissau tema jalgade vahelt, kuni tuleb tema valitseja, keda rahvad võtavad kuulda!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 11, 'Ta seob oma eesli viinapuu külge, hea viinapuu külge oma eeslivarsa; ta peseb oma kuube viinaga ja oma vammust viinamarjade verega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 12, 'Ta silmad on viinast hämused ja hambad piimast valged!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 13, 'Sebulon elab mererannas, laevade rannikul, ja tema selg on pööratud Siidoni poole!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 14, 'Issaskar on kondine eesel, kes lebab sadulakorvide vahel!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 15, 'Kui ta nägi head hingamispaika ja meeldivat maad, ta langetas oma turja koormat kandma ja sai tööorjaks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 16, 'Daan mõistab kohut oma rahvale, üks Iisraeli suguharu on temagi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 17, 'Daan on madu teel, rästik raja peal, kes salvab hobuse kandu, nõnda et ratsanik kukub selili!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 18, 'Ma ootan päästet sinult, Issand!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 19, 'Gaad - röövjõugud ründavad teda, aga ta ise ründab neid, olles neil kannul!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 20, 'Aaserilt tuleb rammus roog ja temal on anda kuninglikke maiuspalu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 21, 'Naftali on nobe emahirv, kes kannab haralisi sarvi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 22, 'Joosep on noor viljapuu, noor viljapuu allika ääres, mille oksad ulatuvad üle müüri!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 23, 'Ammukütid vaenavad teda, peavad ta peale ajujahti,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 24, 'aga tema amb jääb painduvaks ja ta käsivarred on nõtked Jaakobi Vägeva abiga, Karjase, Iisraeli Kalju nime abiga,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 25, 'su isa Jumala abiga, kes sind aidaku, Kõigeväelise abiga, Kes sind õnnistagu õnnistustega ülalt taevast, õnnistustega all asuvast põhjaveest, õnnistustega emarindadest ja üskadest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 26, 'Su isa õnnistused ületavad igaveste mägede õnnistused, ürgsete küngaste ihaldusväärsed annid! Need tulgu Joosepi pea peale, oma vendade vürsti pealaele!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 27, 'Benjamin on kiskjalik hunt! Hommikul ta sööb saaki ja õhtul jaotab röövitut!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 28, 'Need kõik olid Iisraeli suguharud, neid oli kaksteist, ja see oli, mis nende isa neile rääkis, kui ta neid õnnistas: ta õnnistas igaüht temale kohase õnnistusega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 29, 'Ja ta käskis neid ning ütles neile: „Mind koristatakse mu rahva juurde. Matke mind mu isade juurde koopasse, mis on hetiit Efroni väljal,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 30, 'sellesse koopasse, mis on Makpela väljal Mamre kohal Kaananimaal, mille Aabraham ostis koos väljaga hetiit Efronilt pärandhauaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 31, 'Sinna on maetud Aabraham ja tema naine Saara, sinna on maetud Iisak ja tema naine Rebeka, ja sinna ma olen matnud Lea.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 32, 'Väli ja seal olev koobas on hetiitidelt ostetud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 33, 'Kui Jaakob oli oma poegadele käsu andnud, siis ta sirutas voodis oma jalad välja ja heitis hinge; ja ta koristati oma rahva juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 1, 'Siis Joosep langes oma isa palge vastu, nuttis tema kohal ja suudles teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 2, 'Ja Joosep käskis oma teenistuses olevaid arste tema isa palsameerida; ja arstid palsameerisid Iisraeli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 3, 'Selleks kulus nelikümmend päeva, sest nii palju päevi kulub palsameerimiseks; ja egiptlased nutsid teda seitsekümmend päeva.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 4, 'Kui tema nutupäevad olid möödunud, siis Joosep rääkis vaarao hoovkonnaga, öeldes: „Kui ma nüüd teie silmis olen armu leidnud, siis rääkige vaarao kõrva ees ja öelge:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 5, 'Minu isa laskis mind vanduda, öeldes: vaata, ma suren. Mata mind mu hauda, mille ma enesele olen kaevanud Kaananimaal. Ma tahaksin nüüd minna, oma isa matta ja siis tagasi tulla!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 6, 'Ja vaarao ütles: „Mine ja mata oma isa, nõnda nagu ta sind on lasknud vanduda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 7, 'Ja Joosep läks oma isa matma; ja temaga ühes läksid kõik vaarao sulased, tema hoovkonna vanemad ja kõik Egiptusemaa vanemad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 8, 'ja kogu Joosepi pere ja tema vennad ja ta isa pere; ainult oma väetid lapsed ja pudulojused ja veised jätsid nad Gooseni maakonda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 9, 'Ja temaga ühes läksid niihästi vankrid kui ratsanikud, ja see oli väga suur killavoor.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 10, 'Kui nad jõudsid Atadi rehealuse juurde, mis on teispool Jordanit, siis nad panid seal toime väga suure ja mõjuva leinakaebuse, ja ta pidas oma isa peiesid seitse päeva.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 11, 'Kui maa elanikud, kaananlased, nägid neid peiesid Atadi rehealuse juures, siis nad ütlesid: „Need on egiptlastel suured peied!\" Seepärast pandi sellele paigale nimeks Aabel-Mitsraim; see on teispool Jordanit.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 12, 'Ja ta pojad tegid temaga nõnda, nagu ta neid oli käskinud:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 13, 'ta pojad viisid tema Kaananimaale ja matsid ta Makpela välja koopasse Mamre kohal, mille Aabraham ühes väljaga oli ostnud pärandhauaks hetiit Efronilt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 14, 'Ja Joosep läks tagasi Egiptusesse, tema ja ta vennad ja kõik, kes ühes temaga olid läinud ta isa matma, pärast seda kui ta oma isa oli matnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 15, 'Kui Joosepi vennad nägid, et nende isa oli surnud, siis nad ütlesid: „Kui Joosep meid nüüd vihkab ja tõesti tasub meile kätte kõige kurja eest, mis me temale oleme teinud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 16, 'Ja nad käskisid Joosepile öelda: „Su isa andis käsu, enne kui ta suri, ja ütles:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 17, 'öelge Joosepile nõnda: anna ometi andeks oma vendade üleastumine ja patt, et nad sulle on kurja teinud! Seepärast anna siis nüüd andeks oma isa Jumala sulaste üleastumine!\" Ja Joosep nuttis, kui temale seda räägiti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 18, 'Siis tulid ka Joosepi vennad ise, heitsid maha ta ette ja ütlesid: „Vaata, me jääme sulle orjadeks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 19, 'Aga Joosep vastas neile: „Ärge kartke! Kas mina olen Jumala asemik?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 20, 'Te mõtlesite küll mu vastu kurja, aga Jumal pööras selle heaks, et teha, mis tänapäeval ongi tehtud: hoida palju rahvast elus!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 21, 'Ja nüüd ärge kartke, mina toidan teid ja teie väeteid lapsi!\" Ja ta trööstis ning rahustas neid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 22, 'Ja Joosep jäi Egiptusesse, tema ja ta isa pere. Ja Joosep elas saja kümne aastaseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 23, 'Ja Joosep nägi Efraimi lapsi kolm põlve; ka Manasse pojast Maakirist sündis lapsi Joosepi põlvede peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 24, 'Ja Joosep ütles oma vendadele: „Mina suren, aga Jumal hoolitseb kindlasti teie eest ja viib teid siit maalt sellele maale, mille Ta vandega on tõotanud anda Aabrahamile, Iisakile ja Jaakobile!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 25, 'Ja Joosep vannutas Iisraeli poegi, öeldes: „Kui Jumal tõesti hoolitseb teie eest, siis viige ka minu luud siit ära!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 26, 'Ja Joosep suri, sada kümme aastat vana, ja ta palsameeriti ja pandi kirstu Egiptuses.');
