INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 1, 'Jeesuse Kristuse, Taaveti poja, Aabrahami poja, sünniraamat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 2, 'Aabrahamile sündis Iisak, Iisakile sündis Jaakob, Jaakobile sündisid Juuda ja Tema vennad;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 3, 'Juudale sündisid Taamarist Perets ja Serah, Peretsile sündis Hesrom, Hesromile sündis Aram;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 4, 'Aramile sündis Amminaadab, Amminaadabile sündis Nahson, Nahsonile sündis Salmon;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 5, 'Salmonile sündis Raahabist Boas, Boasele sündis Rutist Oobed, Oobedile sündis Jesse;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 6, 'Jessele sündis kuningas Taavet; Taavetile sündis Uurija naisest Saalomon;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 7, 'Saalomonile sündis Rehabeam, Rehabeamile sündis Abija, Abijale sündis Aasa;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 8, 'Aasale sündis Joosafat, Joosafatile sündis Jooram, Jooramile sündis Ussija;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 9, 'Ussijale sündis Jootam, Jootamile sündis Ahas, Ahasele sündis Hiskija;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 10, 'Hiskijale sündis Manasse, Manassele sündis Aamos, Aamosele sündis Joosija;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 11, 'Joosijale sündisid Jekonja ja tema vennad Baabüloni vangipõlve ajal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 12, 'Pärast Baabüloni vangipõlve sündis Jekonjale Sealtiel, Sealtielile sündis Serubbaabel;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 13, 'Serubbaabelile sündis Abihuud, Abihuudile sündis Eljakim, Eljakimile sündis Asur;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 14, 'Asurile sündis Saadok, Saadokile sündis Aahim, Aahimile sündis Elihuud;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 15, 'Elihuudile sündis Eleasar, Eleasarile sündis Mattan, Mattanile sündis Jaakob;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 16, 'Jaakobile sündis Joosep, Maarja mees; ja Maarjast sündis Jeesus, Keda nimetatakse Kristuseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 17, 'Kõiki põlvi Aabrahamist Taavetini on siis neliteistkümmend põlve; ja Taavetist Baabüloni vangipõlve ajani on neliteistkümmend põlve; ja Baabüloni vangipõlvest Kristuseni on neliteistkümmend põlve.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 18, 'Aga Jeesuse Kristuse sündimine oli nõnda: kui Ta Ema Maarja oli kihlatud Joosepiga, siis Ta leiti enne nende kokkusaamist käima peal olevat Pühast Vaimust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 19, 'Aga et Tema mees Joosep oli õiglane ega tahtnud Teda saata häbisse, siis Ta võttis nõuks salaja Tema hüljata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 20, 'Ent kui temal see mõte oli, vaata, siis ilmus talle Issanda ingel unes ja ütles: \"Joosep, Taaveti poeg, ära karda Maarjat, Oma naist, enese juure võtta, sest mis Temas on sündinud, on Pühast Vaimust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 21, 'Ta toob ilmale Poja ja Sa pead Temale nimeks panema Jeesus; sest Tema päästab Oma rahva nende pattudest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 22, 'Aga see kõik on sündinud, et läheks täide, mis Issand on rääkinud prohveti kaudu, kes ütleb:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 23, '\"Ennäe, Neitsi saab käima peale ning toob Poja ilmale, ja Temale peab pandama nimeks Immaanuel\", see on meie keeli: Jumal meiega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 24, 'Kui Joosep unest ärkas, siis Ta tegi nõnda, kuidas Issanda Ingel Teda oli käskinud ning võttis Oma naise enese juure');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 25, 'ega puutunud Temasse, enne kui Ta oli ilmale toonud Poja. Ja Ta pani Temale nimeks Jeesus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 1, 'Kui Jeesus oli sündinud Petlemas Juudamaal kuningas Heroodese ajal, vaata, siis tulid targad hommikumaalt Jeruusalemma');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 2, 'ja ütlesid: \"Kus on see sündinud Juutide Kuningas? Sest me oleme näinud Tema tähte hommikumaal ning oleme tulnud Teda kummardama.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 3, 'Kui kuningas Heroodes seda kuulis, ehmus ta väga ja kogu Jeruusalem ühes temaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 4, 'Ja ta kogus kokku kõik rahva ülempreestrid ja kirjatundjad ning kuulas neilt, kus Kristus pidi sündima.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 5, 'Nemad ütlesid temale: \"Petlemas Juudamaal; sest nõnda on kirjutatud prohveti kaudu:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 6, 'Ja Sina, Petlem Juudamaal, ei ole mingil kombel kõige vähem Juuda vürstide seast, sest sinust lähtub Valitseja, Kes Mu rahvast Iisraeli hoiab kui karjane!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 7, 'Siis Heroodes kutsus targad salaja ja uuris neilt hoolega aja, mil täht oli ilmunud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 8, 'Ja ta läkitas nad Petlemma ning ütles: \"Minge kuulake hoolega Lapsukese järele, ja kui te Tema leiate, siis andke minule teada, et minagi läheksin Teda kummardama!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 9, 'Kui nemad olid kuningat kuulnud, siis nad läksid teele. Ja vaata, täht, mida nad olid näinud hommikumaal, käis nende eel, kuni ta tuli ja seisatas ülal seal kohal, kus Lapsuke oli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 10, 'Aga tähte nähes nad said üliväga rõõmsaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 11, 'Ja nad läksid sinna kotta ning nägid Lapsukest ühes Maarjaga, Tema Emaga, ja heitsid maha ning kummardasid Teda, ja avasid oma varandused ning tõid Temale ande, kulda ja viirukit ja mürri.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 12, 'Ja kui nad unes olid Jumalalt saanud käsu mitte minna tagasi Heroodese juure, läksid nad teist teed tagasi omale maale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 13, 'Aga kui nad olid ära läinud, vaata, siis ilmus Issanda Ingel unes Joosepile ja ütles: \"Tõuse ning võta Lapsuke ja Tema Ema enesega ning põgene Egiptusesse ja ole seal, kuni Ma sinule ütlen; sest Heroodes hakkab otsima Last, et Teda hukata.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 14, 'siis ta tõusis ning võttis enesega Lapsukese ja Tema Ema öösel ja läks ära Egiptusesse');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 15, 'ja oli seal kuni Heroodese surmani, et läheks täide, mis Issand on rääkinud prohveti kaudu, kes ütleb: \"Egiptusest Ma kutsusin Oma Poja!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 16, 'Kui nüüd Heroodes nägi, et targad olid teda petnud, siis ta vihastus väga ja läkitas ning laskis tappa kõik poeglapsed Petlemas ja kogu selle ümbruskonnas, kaheaastased ja nooremad, vastavalt ajale, mida ta tarkadelt oli hoolega uurinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 17, 'Siis läks täide, mis on üteldud prohvet Jeremija kaudu:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 18, '\"Raamast on kuuldud häält, palju nuttu ja kaebust: Raahel nutab oma lapsi ega taha lasta ennast trööstida, sest neid ei ole enam!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 19, 'Aga kui Heroodes oli surnud, vaata, siis ilmus Issanda Ingel Joosepile unes Egiptuses');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 20, 'ning ütles: \"Tõuse ning võta Lapsuke ja Tema Ema enesega ja mine Iisraelimaale; sest need on surnud, kes püüdsid Lapsukese hinge!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 21, 'Siis ta tõusis ning võttis Lapsukese ja Ta Ema enesega ja tuli Iisraelimaale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 22, 'Aga kui ta kuulis, et Arhelaos valitseb Judead oma isa Heroodese asemel, siis ta kartis sinna minna. Kuid ta sai unes Jumalalt käsu ja siirdus Galilea aladele');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 23, 'ja tuli ning asus elama linna, mida kutsutakse Naatsaretiks, et läheks täide, mis on üteldud prohvetite kaudu: \"Teda peab hüütama Naatsaretlaseks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 1, 'Neil päevil tuli Ristija Johannes ja kuulutas Judea kõrves');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 2, 'ning ütles: \"Parandage meelt, sest Taevariik on lähedal!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 3, 'Tema on nimelt see, kellest prohvet Jesaja on rääkinud, üteldes: \"Hüüdja hääl on kõrves: Valmistage Issanda teed, õgvendage Tema teerajad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 4, 'Aga Johannesel oli riie kaameli karvust ja nahkvöö vööl; ja tema toit oli rohutirtsud ja metsmesi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 5, 'Siis läks välja tema juure Jeruusalem ja kogu Judea ja kõik Jordani ümberkaudne maa;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 6, 'ja ta ristis nad Jordani jões ning nad tunnistasid oma patud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 7, 'Aga nähes palju varisere ja sadusere tulevat ristimisele, ütles ta neile: \"Te rästikute sigitis, kes on teile märku annud põgeneda tulevase viha eest?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 8, 'Seepärast kandke õiget meeleparanduse vilja');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 9, 'ja ärge arvake võivat ütelda iseenestes: meil on Aabraham isaks; sest ma ütlen teile, et Jumal võib neist kividest äratada Aabrahamile lapsi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 10, 'Ent kirves on juba puude juure küljes; iga puu nüüd, mis ei kanna head vilja, raiutakse maha ja visatakse tulle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 11, 'Mina ristin teid küll veega meeleparanduseks, aga Kes tuleb pärast mind, on vägevam mind ja mina ei ole kõlvuline Temale jalatseidki kandma; Tema ristib teid Püha Vaimu ja tulega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 12, 'Ta visk-labidas on Tema käes ja Ta puhastab Oma rehealuse ja kogub Oma nisud aita, kuid aganad Ta põletab ära kustutamatu tulega.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 13, 'Siis tuli Jeesus Galileast Jordani ääre Johannese juure, et see Teda ristiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 14, 'Kuid Johannes peatas Teda, üteldes: \"Minule on vaja, et Sina mind ristiksid, ja Sina tuled minu juure!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 15, 'Jeesus vastas ning ütles temale: \"Olgu nüüd nii; sest nõnda on meie kohus täita kõike õigust.\" Siis Johannes andis Temale järele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 16, 'Kui nüüd Jeesus oli ristitud, tuli Ta sedamaid veest välja. Ja ennäe, taevad avanesid, ja Ta nägi Jumala Vaimu laskuvat alla kui tuvi ja tulevat Tema peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 17, 'Ja vaata, taevast kostis hääl, mis ütles: \"Seesinane on Minu armas Poeg, Kellest Mul on hea meel!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 1, 'Siis Vaim viis Jeesuse kõrbe kuradi kiusata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 2, 'Ja kui Ta oli paastunud nelikümmend päeva ja nelikümmend ööd, tuli Temale viimaks nälg kätte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 3, 'Ja kiusaja tuli Ta juure ning ütles Temale: \"Kui Sa oled Jumala Poeg, siis ütle, et need kivid leivuks saaksid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 4, 'Aga Tema vastas ning ütles: \"Kirjutatud on: Inimene ei ela ükspäinis leivast, vaid igaühest sõnast, mis lähtub Jumala suust!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 5, 'Siis kurat võttis Tema enesega pühasse linna ja asetas Ta pühakoja harjale');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 6, 'ja ütles Temale: \"Oled Sa Jumala Poeg, siis kukuta Ennast alla, sest kirjutatud on: Tema annab Oma Inglitele käsu Sinu pärast, ja Nemad kannavad Sind kätel, et Sa Oma jalga vastu kivi ei tõukaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 7, 'Jeesus ütles temale: \"Taas on kirjutatud: Ära kiusa Issandat, Oma Jumalat!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 8, 'Jällegi võttis kurat Ta Enesega väga kõrgele mäele ja näitas Talle kõik maailma kuningriigid ja nende hiilguse');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 9, 'ning ütles Temale: \"Selle kõik Ma annan Sinule, kui Sa maha langed ja mind kummardad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 10, 'Siis Jeesus ütles temale: \"Tagane Minust, saatan! Sest on kirjutatud: Sina pead Issandat, Oma Jumalat kummardama ja ükspäinis Teda teenima!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 11, 'Siis kurat jättis Ta rahule. Ja vaata, Ingleid tuli Tema juure, ja Need teenisid Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 12, 'Aga kui Jeesus kuulis, et Johannes oli pandud vangi, siis Ta läks ära Galileasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 13, 'Ja jättes maha Naatsareti tuli Ta ja asus Kapernauma, mis on mererannas Sebuloni ja Naftali aladel, 14 et läheks täide, mis on üteldud prohvet Jesaja kaudu:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 14, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 15, '\"Sebulonimaa ja Naftalimaa; mereäärne tee, maa sealpool Jordanit, paganate Galilea -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 16, 'rahvas, kes istub pimeduses, näeb suurt valgust, ja surma maal ja varjus istujaile, neile tõuseb valgus!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 17, 'Sellest ajast alates Jeesus hakkas kuulutama ning ütlema: \"Parandage meelt, sest Taevariik on lähedal!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 18, 'Aga Galilea mere rannal kõndides Ta nägi kaht venda, Siimonat, keda nimetatakse Peetruseks, ja Andreast, Tema venda, võrku merre heitvat. Sest nad olid kalamehed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 19, 'Ja Ta ütles neile: \"Tulge minu järele ja Ma teen teid inimeste püüdjaiks! 20 Nemad jätsid kohe võrgud maha ning järgisid Teda. 21 Ja kui Ta läks sealt eemale, nägi Ta teist kaht venda, Jakoobust, Sebedeuse poega, ja Johannest, ta venda, paadis ühes nende isa Sebedeusega parandamas oma võrke. Ja Ta kutsus neid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 20, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 21, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 22, 'Ja nad jätsid kohe maha paadi ja oma isa ning järgisid Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 23, 'ja vigadust rahva seas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 24, 'Ja jutt Temast levis üle kogu Süüria. Ja Tema juure toodi kõik vaevalised, mõnesuguse haiguse ja tõve põdejad, seestunud, kuutõbised ja halvatud. Ja Ta tegi nad terveks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 25, 'Ja palju rahvast järgis Teda Galileast ja Dekapolist ja Jeruusalemast ja Judeast ja sealtpoolt Jordanit.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 1, 'Nähes rahvahulki, läks Ta üles mäele. Ja kui Ta oli maha istunud, tulid Ta jüngrid Tema juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 2, 'Ja Ta avas Oma suu, õpetas neid ning ütles:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 3, '\"Õndsad on vaimust vaesed, sest nende päralt on Taevariik.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 4, 'Õndsad on kurvad, sest nemad trööstitakse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 5, 'Õndsad on tasased, sest nemad pärivad maa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 6, 'Õndsad on need, kelledel nälg ja janu on õiguse järele, sest nemad rahuldatakse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 7, 'Õndsad on armulised, sest nemad saavad armu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 8, 'Õndsad on puhtad südamelt, sest nemad saavad näha Jumalat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 9, 'Õndsad on rahunõudjad, sest neid hüütakse Jumala lapsiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 10, 'Õndsad on need, keda taga kiusatakse õiguse pärast, sest nende päralt on Taevariik.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 11, 'Õndsad olete teie, kui inimesed teid Minu pärast laimavad ja taga kiusavad ja valetades räägivad teist kõiksugust kurja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 12, 'Olge rõõmsad ja ilutsege, sest teie palk on suur taevas; samuti on nad taga kiusanud prohveteid enne teid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 13, 'Teie olete maa sool. Aga kui sool tuimub, millega saab teda teha soolaseks? Ta ei kõlba enam millekski kui välja visata ja inimeste tallata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 14, 'Teie olete maailma valgus. Ei saa jääda varjule linn, mis asetseb mäe otsas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 15, 'Ei süüdata ka küünalt ega panda seda vaka alla, vaid küünlajalale, ja see paistab kõikidele, kes majas on.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 16, 'Nõnda paistku teie valgus inimeste ees, et nad näeksid teie häid tegusid ja annaksid au teie Isale, kes on taevas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 17, 'Ärge arvake, et Ma olen tulnud tühistama käsuõpetust või prohveteid; Ma ei ole tulnud neid tühistama, vaid täitma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 18, 'Sest tõesti Ma ütlen teile, kuni kaob taevas ja maa, ei kao käsuõpetusest mitte ühtki tähekest või ühtki märgikest, enne kui kõik on sündinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 19, 'Kes nüüd iganes tühistab ühe neist vähimaist käskudest ja nõnda õpetab inimesi, teda hüütakse vähimaks Taevariigis; aga kes seda mööda teeb ja nõnda õpetab, teda hüütakse suureks Taevariigis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 20, 'Sest Ma ütlen teile: Kui teie õigus pole palju parem kirjatundjate ja variseride omast, siis te ei saa Taevariiki.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 21, 'Te olete kuulnud, et muistsele põlvele on üteldud: Sa ei tohi tappa! ja kes iganes tapab, kuulub kohtu alla.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 22, 'Kuid Mina ütlen teile, et igaüks, kes on oma vennale vihane, kuulub kohtu alla; aga kes iganes oma vennale ütleb \"raka!\" kuulub Suurkohtu alla; aga kes ütleb \"Sa jõle!\" kuulub põrgutulle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 23, 'Sellepärast kui sa oma andi tood altarile ja seal meenub sulle, et su vennal on midagi sinu vastu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 24, 'siis jäta oma and sinna altari ette ja mine lepi enne oma vennaga, ja siis tule ja too oma and.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 25, 'Ole varsti järeleandlik oma vastasele, niikaua kui sa temaga teel oled, et vastane sind ei annaks kohtuniku kätte, ja kohtunik sind ei annaks sulase kätte, ja sind ei pandaks vangi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 26, 'Tõesti Ma ütlen sulle, sa ei pääse sealt, enne kui oled maksnud ära viimse veeringu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 27, 'Te olete kuulnud, et on üteldud: Sa ei tohi abielu rikkuda!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 28, 'Aga Mina ütlen teile, et igaüks, kes naise peale vaatab teda himustades, on juba abielu rikkunud temaga oma südames.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 29, 'Ent kui su parem silm sind pahandab, siis kisu ta välja ja heida enesest ära, sest sulle on parem, et üks sinu liikmeist hukkub kui et kogu su ihu heidetakse põrgusse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 30, 'Ja kui sinu parem käsi sind pahandab, siis raiu ta maha ja heida enesest ära, sest sulle on parem, et üks sinu liikmeist hukkub kiti et kogu su ihu läheb põrgusse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 31, 'Ka on üteldud, et kes iganes oma naise enesest lahutab, see andku temale lahutuskiri.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 32, 'Aga Mina ütlen teile, et igaüks, kes oma naise enesest lahutab muidu kui hooruse pärast, see teeb, et naisega abielu rikutaks, ja kes iganes lahutatud naisega abiellub, rikub abielu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 33, 'Taas te olete kuulnud, et muistsele põlvele on üteldud: Sa ei tohi valet vanduda! ja: Pea Issandale oma vanded!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 34, 'Aga Mina ütlen teile: Ärge üldse vanduge, ei taeva juures, sest see on Jumala aujärg;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 35, 'ega maa juures, sest see on Tema jalgealune järg; ega Jeruusalema juures, sest see on suure Kuninga linn.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 36, 'Ara vannu ka oma pea juures, sest sina ei või ühtki juuksekarva teha valgeks ega mustaks;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 37, 'vaid teie kõne olgu: Jah, jah, või: Ei, ei; aga mis üle selle on, see on kurjast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 38, 'Te olete kuulnud, et on üteldud: Silm silma vastu ja hammas hamba vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 39, 'Aga Mina ütlen teile: Ärge pange vastu kurjale, vaid kui keegi sind lööb vastu su paremat kõrva, siis kääna temale ka teine;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 40, 'ja sellele, kes tahab sinuga kohut käia ja võtta su vammuse, jäta ka kuub;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 41, 'ja kes sind sunnib kaasas käima ühe penikoorma, sellega mine kaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 42, 'Anna sellele, kes sult palub, ja ära käändu kõrvale sellest, kes sult tahab laenata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 43, 'Te olete kuulnud, et on üteldud: Armasta oma ligimest ja vihka oma vaenlast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 44, 'Aga Mina ütlen teile: Armastage oma vaenlasi ja palvetage nende eest, kes teid taga kiusavad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 45, 'et te saaksite oma Isa lapsiks, Kes on taevas, sest Tema laseb Oma päikest tõusta kurjade ja heade üle ja laseb vihma sadada õigete ja ülekohtuste peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 46, 'Sest kui te armastate neid, kes teid armastavad, mis palka te saate? Eks tölneridki tee sedasama?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 47, 'Ja kui te lahkesti tervitate ainult oma vendi, mida iseäralikku te siis teete? Eks paganadki tee sedasama?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 48, 'Teie olge siis täiuslikud, nõnda nagu teie taevane Isa on täiuslik.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 1, 'Hoidke, et te armastuseande ei jaga inimeste nähes selleks, et nemad teid vaatleksid; muidu ei ole teil palka Oma Isalt, Kes on taevas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 2, 'Seepärast, kui sa armastuseande annad, siis ära lase enese ees sarve puhuda, nagu silmakirjatsejad teevad kogudusekodades ja uulitsail, et inimesed neid ülistaksid. Tõesti Ma ütlen teile, et neil on oma palk käes!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 3, 'Vaid kui sina armastuseande annad, siis ärgu su vasak käsi teadku, mida su parem käsi teeb,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 4, 'et su armastuseannid oleksid salajas; ja su Isa, kes näeb salajasse, tasub sinule.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 5, 'Ja kui te palvetate, siis ärge olge nagu silmakirjatsejad; sest need armastavad palvetada kogudusekodades ja uulitsate nurkadel, et nad paistaksid inimestele silma. Tõesti Ma ütlen teile, et neil on oma paik käes!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 6, 'Ent sina, kui sa palvetad, siis mine oma kambrisse ja sule uks, ja palu oma Isa, Kes on salajas, ja su Isa, kes näeb salajasse, tasub sinule.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 7, 'Aga kui te palvetate, siis ärge palju lobisege, nõnda nagu paganad, sest nad arvavad, et neid kuuldakse nende paljude sõnade tõttu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 8, 'Ärge siis saage nende sarnaseks, sest Jumal, teie Isa, teab, mida te vajate, enne kui te Teda palute.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 9, 'Teie palvetage siis nõnda: Meie Isa, Kes oled taevas! Pühitsetud olgu Sinu nimi;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 10, 'sinu Riik tulgu; Sinu tahtmine sündigu nagu taevas, nõnda ka maa peal;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 11, 'meie igapäevane leib anna meile tänapäev;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 12, 'ja anna meile andeks meie võlad, nagu meiegi andeks anname oma võlglastele;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 13, 'ja ära saada meid kiusatusse, vaid päästa meid kurjast; sest Sinu on Riik ja vägi ja au igavesti! Aamen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 14, 'Sest kui te annate andeks inimestele nende eksimused, siis annab teie taevane Isa ka teile andeks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 15, 'Aga kui te inimestele nende eksimusi andeks ei anna, siis ei anna ka teie Isa teie eksimusi andeks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 16, 'Aga kui te paastute, siis ärge jääge kurvanäoliseks, nõnda nagu silmakirjatsejad; sest nad teevad oma palge näotumaks, et rahvas näeks neid paastuvat. Tõesti, Ma ütlen teile, neil on oma palk käes!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 17, 'Vaid kui sina paastud, siis võia oma pea ja pese oma pale,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 18, 'et su paastumine ei oleks nähtav inimestele, vaid su Isale, Kes on salajas. Ja su Isa, Kes näeb salajasse, tasub sinule.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 19, 'Ärge koguge endile varandusi maa peale, kus koi ja rooste rikuvad ja kus vargad sisse murravad ning varastavad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 20, 'Vaid koguge endile varandusi taevasse, kus koi ega rooste ei riku ja kus vargad sisse ei murra ega varasta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 21, 'Sest kus su varandus on, seal on ka su süda!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 22, 'Ihu küünal on silm; kui su silm on terve, siis on kõik su ihu valguses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 23, 'Aga kui su silm on rikkis, siis on kogu su ihu pimeduses. Kui nüüd su valgus, mis on sinus, on pimedus, kui suur on siis pimedus?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 24, 'Ukski ei või teenida kaht isandat, sest tema kas vihkab üht ja armastab teist, või hoiab ühe poole ega hooli teisest. Te ei või teenida Jumalat ja mammonat!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 25, 'Sellepärast Ma ütlen teile: Ärge olge mures oma elu pärast, mida süüa ja mida juua, ega oma ihu pärast, millega riietuda. Eks elu ole enam kui toidus ja ihu enam kui riided?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 26, 'Pange tähele taeva linde: nad ei külva ega lõika ega pane kokku aitadesse, ja teie taevane Isa toidab neid. Eks teie ole palju enam kui nemad?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 27, 'Aga kes teie seast võib muretsemisega oma pikkusele ühegi küünra jätkata?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 28, 'Ja miks te muretsete riietuse pärast? Pange tähele lilli väljal, kuidas nad kasvavad; nad ei tee tööd ega ketra.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 29, 'Ometi Ma ütlen teile, et Saalomongi kõiges oma hiilguses pole olnud nõnda ehitud kui üks nendest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 30, 'Kui nüüd Jumal rohtu väljal, mis täna on ja homme ahju visatakse, nõnda ehib, kas siis mitte palju enam teid, teie nõdrausulised?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 31, 'Ärge siis olge mures, küsides: Mida me sööme? või: Mida me joome? või: Millega me riietume?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 32, 'Sest kõike seda taotlevad paganad. Teie taevane Isa teab ju, et te seda kõike vajate.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 33, 'Ent otsige esiti Jumala Riiki ja Tema õigust, siis seda kõike antakse teile pealegi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 34, 'Ärge siis olge mures homse pärast, sest küll homne päev muretseb enese eest. Igale päevale saab küllalt omast vaevast!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 1, 'Ärge mõistke kohut, et teie üle ei mõistetaks kohut.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 2, 'Sest missuguse kohtuga te kohut mõistate, niisugusega mõistetakse teile kohut; ja missuguse mõõduga te mõõdate, niisugusega möödetakse ka teile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 3, 'Aga miks sa näed pindu oma venna silmas, kuid palki omas silmas sa ei pane tähele?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 4, 'Või kuidas sa ütled oma vennale: Lase ma tõmban pinnu sinu silmast!? Ja vaata, palk on su omas silmas!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 5, 'Sa silmakirjatseja! Tõmba esiti palk omast silmast, ja siis sa seletad tõmmata pindu oma venna silmast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 6, 'Ärge andke seda, mis on püha, koertele, ja ärge heitke oma pärle sigade ette, et nad neid ei sõtkuks oma jalgega ega käänduks ja teid ei kisuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 7, 'Paluge, siis antakse teile; otsige, siis te leiate; koputage, siis avatakse teile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 8, 'Sest igaüks, kes palub, see saab, ja kes otsib, see leiab, ja kes koputab, sellele avatakse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 9, 'Või missugune inimene on teie seast, kellelt tema poeg palub leiba, ja ta annaks temale kivi?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 10, 'Või kui ta palub kala, ja ta annaks temale mao?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 11, 'Kui nüüd teie, kes olete kurjad, mõistate anda häid ande oma lastele, eks palju enam teie Isa taevas anna häid ande neile, kes Teda paluvad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 12, 'Sellepärast kõik, mida te tahate, et inimesed teile teevad, tehke ka neile; sest see on käsuõpetus ja prohvetid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 13, 'Minge sisse kitsast väravast; sest avar on värav ja lai on tee, mis viib hukatusse, ja palju on neid, kes sealt sisse lähevad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 14, 'Ja kitsas on värav ja ahtake on tee, mis viib ellu, ja pisut on neid, kes selle leiavad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 15, 'Hoiduge valeprohveteist, kes tulevad teie juure lammaste riideis, aga seest-pidi on nad kiskjad hundid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 16, 'Nende viljast te tunnete nad. Ega viinamarju nopita kibuvitsust või viigimarju ohakaist?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 17, 'Nõnda iga hea puu kannab head vilja, aga halb puu kannab halba vilja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 18, 'Hea puu ei või kanda halba vilja ega halb puu kanda head vilja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 19, 'Iga puu, mis ei kanna head vilja, raiutakse maha ja visatakse tulle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 20, 'Nõnda siis nende viljast te tunnete nad ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 21, 'Mitte igaüks, kes Minule ütleb: Issand, Issand, ei saa Taevariiki, vaid kes teeb Mu Isa tahtmist, Kes on taevas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 22, 'Mitmed ütlevad Minule tol päeval: Issand, Issand, kas me ei ole Sinu nimel ennustanud ja Sinu nimel ajanud välja kurje vaime ja Sinu nimel teinud palju vägevaid tegusid?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 23, 'Ja siis Ma tunnistan neile: Ma ei ole elades teid tunnud, taganege Minust, te ülekohtutegijad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 24, 'Igaüks nüüd, kes neid Mu sõnu kuuleb ja teeb nende järele, on võrreldav mõistliku mehega, kes ehitas oma koja kaljule.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 25, 'Ja ränk sadu tuli ja tulid vetevoolud, ja tuuled puhusid ja sööstsid vastu seda koda; aga Ta ei langenud, sest Tema alus oli rajatud kaljule.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 26, 'Ja igaüks, kes neid Minu sõnu kuuleb, aga ei tee nende järele, on võrreldav jõleda mehega, kes oma koja ehitas liivale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 27, 'Ja ränk sadu tuli ja tulid vetevoolud, ja tuuled puhusid ja sööstsid vastu seda koda, ja ta langes, ja tema langemine oli suur.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 28, 'Ja sündis, kui Jeesus oli lõpetanud need kõned, et rahvahulgad hämmastusid Tema õpetusest;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 29, 'sest Ta õpetas neid nõnda nagu see, kellel on meelevald, ja mitte nõnda nagu nende kirjatundjad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 1, 'Aga kui Tema astus mäelt alla, järgis Teda palju rahvast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 2, 'Ja vaata, pidalitõbine tuli ja heitis maha Tema ette ning ütles: \"Issand, kui Sa tahad, võid Sa mind puhtaks teha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 3, 'Siis Jeesus sirutas Oma käe ja puudutas teda ning ütles: \"Ma tahan, saa puhtaks!\" Ja sedamaid sai ta oma pidalitõvest puhtaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 4, 'Ja Jeesus ütles temale: \"Katsu, et Sa seda ühelegi ei ütle, vaid mine näita ennast preestritele ja vii ohvriand, mille Mooses on seadnud, neile tunnistuseks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 5, 'Aga kui Jeesus saabus Kapernauma, tuli Tema juure sõjapealik, palus Teda');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 6, 'ning ütles: \"Issand, minu poiss on kodus halvatuna maas suures valus!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 7, 'Jeesus ütles temale: \"Ma tulen ja teen ta terveks.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 8, 'Aga sõjapealik kostis ja ütles: \"Issand, mina pole väärt, et Sa mu katuse alla tuled, vaid ütle ainult sõna, siis mu poiss paraneb.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 9, 'siis ta tuleb, ja oma orjale: Tee seda! siis ta teeb.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 10, 'Kui Jeesus seda kuulis, imestas Ta ja ütles neile, kes Teda järgisid: \"Tõesti Ma ütlen teile, mitte üheltki Iisraelis Ma ei ole leidnud nii suurt usku!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 11, 'Ent Ma ütlen teile, et paljud tulevad idast ja läänest ja istuvad lauas Aabrahami, Iisaki ja Jaakobiga Taevariigis,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 12, 'aga kuningriigi lapsed heidetakse välja kõige äärmisemasse pimedusse; seal on ulumine ja hammaste kiristamine!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 13, 'Ja Jeesus ütles sõjapealikule: \"Mine! Nagu sa oled uskunud, nõnda sündigu sulle!\" Ja tema poiss sai terveks selsamal tunnil.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 14, 'Ja Jeesus tuli Peetruse kotta ja nägi tema ämma olevat maas palavikus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 15, 'Siis Jeesus võttis ta käest kinni, ja palavik lahkus temast. Ja ämm tõusis üles ja ümmardas teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 16, 'Aga õhtu tulles toodi Tema juure palju seestunuid; ja Ta ajas vaimud välja sõnaga ja tegi haiged terveks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 17, 'et läheks täide, mis on üteldud prohvet Jesaja kaudu: \"Tema võttis Enese peale meie haigused ja kandis meie tõved!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 18, 'Aga kui Jeesus nägi rahvahulka enese ümber olevat, käskis Ta minna teisele poole järve.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 19, 'Ja Tema juure tuli üks kirjatundja ja ütles Temale: \"Õpetaja, ma tahan Sind järgida, kuhu Sa iganes lähed!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 20, 'Siis Jeesus ütles temale: \"Rebastel on augud ja taeva lindudel on pesad, kuid Inimese Pojal ei ole, kuhu Ta Oma pea võiks panna!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 21, 'Ka keegi teine jüngritest ütles Temale: \"Issand, luba mind enne minna ja matta oma isa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 22, 'Aga Jeesus ütles temale: \"Järgi mind ja lase surnuid oma surnud matta!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 23, 'Ja kui Tema astus paati, läksid Ta jüngrid Tema järele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 24, 'Ja vaata, suur maru tõusis merel, nõnda et paat lainetega kaeti. Aga Tema magas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 25, 'Siis jüngrid tulid Tema juure, äratasid Ta üles ja ütlesid: \"Issand aita! Me hukkume!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 26, 'Tema ütles neile: \"Miks te olete arad, te nõdrausulised?\" Siis Ta tõusis üles ja sõitles tuult ja merd. ja meri jäi täiesti vaikseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 27, 'Aga inimesed imestasid ja ütlesid: \"Mis Mees See küll on, et isegi tuuled ja meri kuulevad Tema sõna!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 28, 'Ja kui Ta jõudis teisele poole järve gadaralaste maale, kohtasid Teda kaks haudadest tulnud seestunut, kes olid väga hirmsad, nii et ükski ei saanud käia seda teed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 29, 'Ja vaata, nemad kisendasid ning ütlesid: \"Mis on Sul meiega tegemist, Sa Jumala Poeg? Oled Sa siia tulnud meid enneaegu vaevama?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 30, 'Aga kaugel neist oli suur seakari söömas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 31, 'Ja kurjad vaimud palusid Teda ning ütlesid: \"Kui Sa meid välja ajad, siis läheta meid seakarja sisse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 32, 'Ja Ta ütles neile: \"Minge!\" Siis nad läksid sigade sisse. Ja vaata, kogu kari kukutas enese ülepeakaela kaldalt merre ja suri vette. 33 Aga karjased põgenesid ja läksid linna ning andsid teada kõik ja mis oli sündinud seestunutega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 33, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 34, 'Ja vaata, kogu linn läks välja Jeesusele vastu. Ja kui nad Teda nägid, palusid nad Teda, et Ta nende rajadest ära läheks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 1, 'Ja Ta astus paati ja tuli teisele poole järve ning saabus Oma linna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 2, 'Ja vaata, nad kandsid Tema juure halvatu, kes oli voodis maas. Kui Jeesus nende usku nägi, ütles Ta halvatule: \"Ole julge, mu poeg, sinu patud antakse sulle andeks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 3, 'Ja vaata, mõned kirjatundjaist ütlesid isekeskis: \"Seesinane pilkab Jumalat.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 4, 'Aga kui Jeesus nende mõtteid nägi, ütles Ta: \"Mispärast te mõtlete kurja oma südames?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 5, 'Sest mis on kergem ütelda: Su patud antakse sulle andeks, või ütelda: Tõuse üles ja kõnni?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 6, 'Aga et te teaksite, et Inimese Pojal on meelevald maa peal anda patud andeks, siis Ta ütleb halvatule: Tõuse üles, võta oma voodi ja mine koju!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 7, 'Ja see tõusis üles ja läks koju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 8, 'Aga kui rahvahulgad seda nägid, panid nad seda imeks ja andsid Jumalale au, Kes annab inimestele niisuguse meelevalla.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 9, 'Ja sealt mööda minnes nägi Jeesus meest, Matteus nimi, tolli juures istuvat, ja ütles temale: \"Järgi mind!\" Ja see tõusis ja järgis Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 10, 'Ja kui Ta lauas istus tema kojas, vaata, siis tuli palju tölnereid ja patuseid ning istusid lauas ühes Jeesuse ja Ta jüngritega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 11, 'Seda nähes ütlesid variserid Ta jüngritele: \"Mispärast teie Õpetaja sööb ühes tölnerite ja patustega?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 12, 'Aga kui Tema seda kuulis, ütles Ta: \"Arsti ei vaja terved, vaid haiged.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 13, 'Ent minge ja õppige, mis see on: Ma tahan halastust ja mitte ohvrit. Sest Ma pole tulnud kutsuma õigeid, vaid patuseid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 14, 'Siis tulid Johannese jüngrid Tema juure ja küsisid: \"Mispärast meie ja variserid paastume, aga Sinu jüngrid ei paastu?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 15, 'Jeesus ütles neile: \"Ega peiupoisid või olla kurvad, niikaua kui peig on nende juures? Ent päevad tulevad, mil peig neilt võetakse, ja siis nad paastuvad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 16, 'Ükski ei pane vanutamata riidetükki paigaks vanale kuuele; sest niisugune augutäidis rebeneb küljest lahti ja lõhe läheb pahemaks. 17 Ei valata ka mitte värsket viina vanusse nahklähkreisse; muidu lähkrid lõhkevad ja viin voolab maha ning lähkrid saavad hukka; vaid värske viin valatakse uutesse lähkritesse, siis säilivad mõlemad.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 17, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 18, 'Kui Tema seda neile rääkis, vaata, siis tuli üks ülem ja langes maha Tema ette ning ütles: \"Minu tütar suri praegu, aga tule ja pane Oma käsi tema peale, siis ta saab elavaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 19, 'Ja Jeesus tõusis ja läks ta järel, samuti Ta jüngrid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 20, 'Ja vaata, naine, kes oli kaksteistkümmend aastat veritõbe põdenud, tuli Tema selja taha ja puudutas Tema kuue palistust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 21, 'Sest ta ütles iseeneses: \"Saaksin Ma vaid puudutada Tema kuube, siis ma pääseksin!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 22, 'Aga Jeesus pöördus, nägi teda ja ütles: \"Ole julge, tütar, sinu usk on sind päästnud!\" Ja naine sai terveks selsamal tunnil.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 23, 'Ja kui Jeesus ülema kotta jõudis ja nägi vilepuhujaid ja käratsevat rahvast,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 24, 'ütles Ta: \"Minge ära, sest tütarlaps pole surnud, vaid magab!\" Ja nad naersid Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 25, 'Aga kui rahvahulk oli välja aetud, läks Ta sisse ja võttis tema käest kinni, ja tütarlaps tõusis üles.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 26, 'Ja sõnum sellest levis üle kogu selle maa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 27, 'Kui Jeesus sealt edasi läks, järgis Teda kaks pimedat, karjudes ning üteldes: \"Taaveti Poeg, halasta meie peale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 28, 'Kui Ta tuppa astus, tulid need pimedad Tema juure, ja Jeesus ütles neile: \"Kas te usute, et Ma võin seda teha?\" Nemad ütlesid temale: \"Jah, Issand!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 29, 'Siis Ta puudutas nende silmi ning ütles: \"Teile sündigu teie usku mööda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 30, 'Ja nende silmad avanesid. Ja Jeesus hoiatas neid kõvasti ning ütles: \"Katsuge, et seda keegi ei saa teada!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 31, 'Aga nemad läksid välja ja tegid Tema kuulsaks kogu sellel maal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 32, 'Kui need olid välja läinud, vaata, siis toodi Ta juure keeletu mees, kes oli seestund.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 33, 'Ja kui kuri vaim oli välja aetud, rääkis keeletu. Ja rahvas imestas ja ütles: \"Seda pole veel iialgi nähtud Iisraelis!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 34, 'Kuid variserid ütlesid: \"Kurjade vaimude ülema abil ajab Ta kurje vaime välja.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 35, 'Ja Jeesus käis läbi kõik linnad ja külad, õpetas nende kogudusekodades ja kuulutas Jumala riigi Evangeeliumi, parandas kõike tõbe ja kõike viga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 36, 'Aga kui Ta rahvahulki nägi, oli Tal hale meel nende pärast, sest nad olid piinatud ja vintsutatud otsegu lambad kelledel ei ole karjast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 37, 'Siis Ta ütles Oma jüngritele: \"Lõikust on palju, aga vähe töötegijaid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 38, 'Paluge siis lõikuse Issandat, et Ta läkitaks töötegijaid välja Oma lõikusele.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 1, 'Ja Ta kutsus Oma kaksteistkümmend jüngrit enese juure ja andis meile meelevalla rüvedate vaimude üle, neid välja ajada ja parandada kõike tõbe ja kõike viga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 2, 'Nende kaheteistkümne apostli nimed on need: esimene Siimon, nimetatud Peetruseks, ja Andreas, tema vend; Jakoobus, Sebedeuse poeg, ja Johannes, tema vend;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 3, 'Filippus ja Bartolomeus, Toomas ja Matteus, tölner; Jakoobus, Alfeuse poeg, ja Taddeus;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 4, 'Siimon Kaanast ja Juudas Iskariot, kes Tema ka ära andis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 5, 'Need kaksteistkümmend läkitas Jeesus, käskis neid ning ütles: \"Ärge minge paganate teele ja ärge astuge samaarlaste linna,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 6, 'vaid minge ennemini Iisraeli koja kadunud lammaste juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 7, 'Ja minnes kuulutage ning ütelge: Taevariik on lähedal!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 8, 'Tehke haigeid terveks, äratage surnuid üles, puhastage pidalitõbiseid, ajage välja kurje vaime. Muidu olete saanud, muidu andke!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 9, 'Ärge varuge kulda ega hõbedat ega vaskraha oma vöö vahele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 10, 'ei pauna teekonnale ega kaht kuube ega jalatseid ega saua; sest töötegija on oma toiduse väärt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 11, 'Aga kui te kuhugi linna või külasse astute, siis kuulake, kes seal on vääriline, ja sinna jääge, seni kui sealt ära lähete.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 12, 'Aga sisse astudes majasse teretage seda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 13, 'Ja kui see maja on selle vääriline, siis tulgu teie rahu ta peale; aga kui see ei ole vääriline, pöördugu teie rahu tagasi teie juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 14, 'Ja kes iganes teid vastu ei võta ega kuule teie sõnu, sellest kojast või sellest linnast minge välja ja puistake tolm oma jalgadelt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 15, 'Tõesti Mina ütlen teile, Soodoma- ja Gomorramaal on kohtupäeval hõlpsam põli kui niisugusel linnal!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 16, 'Vaata, Mina läkitan teid nagu lambaid huntide keskele! Olge siis arukad nagu maod ja vagurad nagu tuvid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 17, 'Aga hoiduge inimestest; sest nad annavad teid ära kohtute kätte ja piitsutavad teid kogudusekodades.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 18, 'Ja teid viiakse minu pärast ka maavalitsejate ja kuningate ette, neile ja paganaile tunnistuseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 19, 'Ja kui nad teid annavad kohtu kätte, siis ärge muretsege, kuidas või mida rääkida, sest teile antakse sel tunnil see, mida teil tuleb rääkida.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 20, 'Sest teie ei ole need, kes kõnelevad, vaid see on teie Isa Vaim, Kes kõneleb teie sees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 21, 'Siis vend annab venna surma ja isa oma lapse, ja lapsed hakkavad vastu vanemaile ja tapavad nad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 22, 'Ja te saate kõikide vihaalusteks Minu nime pärast; aga kes jääb püsima otsani, see päästetakse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 23, 'Aga kui nad teid taga kiusavad ühes linnas, siis põgenege teise. Sest tõesti Ma ütlen teile, te ei jõua Iisraeli linnu õpetades läbi käia, enne kui Inimese Poeg tuleb!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 24, 'Jünger ei ole ülem oma õpetajast ega ori ülem oma isandast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 25, 'Jünger olgu rahul sellega, et tema käsi käib nagu ta õpetajal ja orjal nagu ta isandal. Kui nad pereisandat on hüüdnud Peeltsebuliks, kui palju enam tema peret!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 26, 'Ärge siis neid kartke! Sest midagi ei ole peidetud, mis ei tuleks ilmsiks, ja midagi pole salajas, mida ei saada teada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 27, 'Mis Ma teile ütlen pimedas, seda rääkige valges; ja mida te kuulete kõrva sisse räägitavat, seda kuulutage katustelt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 28, 'Ja ärge kartke neid, kes ihu tapavad, aga hinge ei või tappa, vaid kartke ennemini Teda, kes hinge ja ihu võib hukutada põrgus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 29, 'Eks kaks varblast müüda veeringu eest? Ent ükski neist ei lange maha ilma teie Isata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 30, 'Aga teie juuksekarvadki on kõik ära loetud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 31, 'Ärge siis kartke; teie olete kallihinnalisemad kui palju varblasi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 32, 'Igaüks nüüd, kes Mind tunnistab inimeste ees, teda tunnistan Minagi Oma Isa ees, Kes on taevas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 33, 'Aga kes iganes Mind ära salgab inimeste ees, teda salgan Minagi Oma Isa ees, Kes on taevas!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 34, 'Ärge arvake, et Ma olen tulnud tooma rahu maa peale; Ma ei ole tulnud tooma rahu, vaid mõõka!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 35, 'Sest Ma olen tulnud ajama inimest riidu tema isaga ja tütart tema emaga ja miniat tema ämmaga;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 36, 'ja inimese vaenlasiks saavad ta omad kodakondsed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 37, 'Kes isa või ema enam armastab kui Mind, see ei ole Mind väärt; ja kes poega või tütart enam armastab kui Mind, see ei ole Mind väärt;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 38, 'ja kes ei võta oma risti enese peale ega järgi Mind, ei ole Mind väärt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 39, 'Kes oma elu leiab, kaotab selle, ja kes oma elu kaotab Minu pärast, leiab selle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 40, 'Kes teid vastu võtab, võtab Mind vastu; ja kes Mind vastu võtab, võtab vastu Selle, Kes Mind on läkitanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 41, 'Kes prohveti vastu võtab ta prohvetinime tõttu, saab prohveti palga; ja kes õige vastu võtab, õige inimese nime tõttu, see saab õige inimese palga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 42, 'Ja kes iganes üht neist vähemaist joodab karikatäie külma veega ta jüngri nime tõttu, tõesti Ma ütlen teile, see ei jää ilma oma palgast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 1, 'Ja sündis, kui Jeesus oli lõpetanud käskude andmise Oma kaheteistkümnele jüngrile, et Ta läks sealt eemale õpetama ja jutlustama nende linnades.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 2, 'Aga kui Johannes vangihoones olles kuulis Kristuse tegudest, läkitas ta oma jüngrite kaudu');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 3, 'Temale ütlema: \"Kas oled Sina See, Kes tuleb, või peame teist ootama?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 4, 'Ja Jeesus vastas ning ütles neile: \"Minge ja teatage Johannesele, mida te kuulete ja näete:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 5, 'pimedad saavad nägijaiks ja jalutumad käivad, pidalitõbised tehakse puhtaks ja kurdid kuulevad, ja surnud äratatakse üles ja vaestele kuulutatakse Evangeeliumi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 6, 'ja õnnis on, kes iganes Minust ei pahandu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 7, 'Aga kui need ära läksid, hakkas Jeesus rahvale rääkima Johannesest: \"Mida te olete läinud välja kõrbe vaatama? Kas pilliroogu, mida tuul kõigutab?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 8, 'Või mida te olete välja läinud vaatama? Kas inimest, kes on riietatud peente riietega? Vaata, kes kannavad peeni riideid, need on kuningate kodades.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 9, 'Või mida te olete välja läinud vaatama? Kas prohvetit? Tõesti, Ma ütlen teile, et ta on enamgi kui prohvet.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 10, 'Tema on see, kellest on kirjutatud: Vaata, Mina läkitan Sinu palge eele Oma Ingli, kes Sulle tee valmistab Sinu ees!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 11, 'Tõesti, Ma ütlen teile, ei ole naistest sündinute seast tõusnud suuremat kui Ristija Johannes, aga väiksem Taevariigis on suurem temast!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 12, 'Ent Ristija Johannese päevist siiamaani rünnatakse Taevariiki, ja kes seda ründavad, kisuvad ta endile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 13, 'Ja kõik prohvetid ja käsuõpetus on ennustanud Johanneseni,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 14, 'ja kui te tahate seda tõeks võtta: tema on Eelija, kes pidi tulema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 15, 'Kellel kõrvad on, see kuulgu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 16, 'Aga kellega Ma võrdleksin sedasinast sugupõlve? See on laste sarnane, kes turgudel istuvad ja hüüavad teistele');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 17, 'ning ütlevad: Me oleme teile vilet ajanud, ja te pole tantsinud; me oleme teile nutulugu laulnud, ja te pole nutnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 18, 'Sest Johannes tuli, ei söönud ega joonud; ja nad ütlevad: Temas on kuri vaim!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 19, 'Inimese Poeg tuli, sööb ja joob; ja nad ütlevad: Vaata, see inimene on söödik ja viinajoodik, tölnerite ja patuste sõber! Ometi mõistetakse tarkus õigeks ta tegudest.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 20, 'Siis Ta hakkas sõitlema neid linnu, kus kõige rohkem oli sündinud Tema vägevaid tegusid, sellepärast et nad ei olnud meelt parandanud:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 21, '\"Häda sulle, Korasin! Häda sulle, Betsaida! Sest kui Tüüroses ja Siidonis oleksid sündinud need vägevad teod, mis on sündinud teie juures, küll nemad ammugi oleksid kotis ja tuhas parandanud meelt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 22, 'Aga Ma ütlen teile: Tüürosel ja Siidonil on hõlpsam põli kohtupäeval kui teil!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 23, 'Ja sina, Kapernaum, kas sa ei olnud ülendatud taevani? Kuni põrguni sa langed alla! Sest kui Soodomas oleksid sündinud need vägevad teod, mis on sündinud sinus, seisaks see veel tänapäeval!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 24, 'Aga Ma ütlen teile: Soodomamaal on hõlpsam põli kohtupäeval kui sinul!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 25, 'Sel ajal Jeesus hakkas rääkima ja ütles: \"Ma ülistan sind, Isa, taeva ja maa Issand, et Sa selle oled peitnud tarkade ja mõistlike eest ja oled selle ilmutanud väetimaile!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 26, 'Tõesti, Isa, see on nõnda olnud Su meele järele!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 27, 'Kõik on Mu Isa annud Minu kätte, ja ükski muu ei tunne Poega kui ainult Isa, ega ükski tunne Isa kui ainult Poeg ja see, kellele iganes Poeg tahab Seda ilmutada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 28, 'Tulge Minu juure kõik, kes olete vaevatud ja koormarud, ja Mina annan teile hingamise!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 29, 'Võtke endi peale Minu ike ja õppige Minust, et Mina olen tasane ja südamelt alandlik; ja te leiate hingamise oma hingedele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 30, 'Sest Minu ike on hea ja Minu koorem on kerge!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 1, 'Sel ajal läks Jeesus viljast läbi hingamispäeval. Aga Tema jüngritel oli nälg ja nad hakkasid päid katkuma ja sööma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 2, 'Kui variserid seda nägid, ütlesid nad Temale: \"Vaata, Sinu jüngrid teevad, mida ei sünni teha hingamispäeval!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 3, 'Aga Tema ütles neile: \"Eks te ole lugenud, mis Taavet tegi, kui temal ja ta kaaslastel nälg oli,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 4, 'kuidas ta läks Jumala kotta ja nad sõid ära vaateleivad, mida süüa ei olnud luba temal ega ta kaaslastel, vaid ainult preestritel?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 5, 'Või eks te ole käsuõpetusest lugenud, et preestrid rikuvad hingamispäeval pühakojas hingamispäeva ja on ometigi süüta?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 6, 'Kuid Ma ütlen teile, et siin on See, Kes on suurem kui pühakoda!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 7, 'Aga kui te teaksite, mis see on: Ma tahan halastust, aga mitte ohvrit! siis te ei oleks hukka mõistnud süütumaid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 8, 'Sest Inimese Poeg on hingamispäeva isand!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 9, 'Ja kui Ta läks sealt eemale, tuli Ta nende kogudusekotta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 10, 'Ja vaata, seal oli inimene kuivanud käega. Ja nad küsisid Temalt ning ütlesid: \"Kas sünnib terveks teha hingamispäeval?\" Nad mõtlesid Ta peale kaevata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 11, 'Aga Tema ütles neile: \"Kes on teie seast inimene, kellel on üksainus lammas, ja kui see kukub auku hingamispäeval, et ta temast ei haara kinni ega tõmba teda välja?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 12, 'Kui suur vahe on nüüd inimese ja lamba vahel! Sellepärast sünnib küll teha head hingamispäeval!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 13, 'Siis Ta ütles sellele inimesele: \"Siruta oma käsi!\" Ja ta sirutas. Ja see sai jälle terveks otsegu teine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 14, 'Siis variserid läksid välja ja pidasid nõu Tema vastu, kuidas Teda hukka saata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 15, 'Aga kui Jeesus seda sai teada, läks Ta sealt ära. Ja paljud järgisid Teda, ja Ta tegi nad kõik terveks;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 16, 'ja Ta hoiatas neid kõvasti, et nad Temast avalikult ei räägiks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 17, 'et läheks täide, mis on üteldud prohvet Jesaja kaudu:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 18, '\"Vaata, See on Mu Sulane, Kelle Ma olen valinud, Mu armas, kellest Mu hingel on hea meel! Ma panen Oma Vaimu Tema peale ja Ta kuulutab paganaile kohut.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 19, 'Ta ei riidle ega kisenda, ja Ta häält ei kuulda uulitsail.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 20, 'Rudjutud pilliroogu Ta ei murra katki ja suitsvat tahti Ta ei kustuta ära, kuni Ta on õigluse jalule seadnud;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 21, 'ja paganad panevad oma lootuse Tema nime peale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 22, 'Siis toodi Tema juure seestunu, kes oli pime ja keeletu. Ja Ta tegi tema terveks, nõnda et keeletu rääkis ja nägi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 23, 'Ja kõik rahvas hämmastus ning ütles: \"Kas see vahest ei ole Taaveti poeg?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 24, 'Aga kui variserid seda kuulsid, ütlesid nad: \"Seesinane ei aja kurje vaime välja muidu kui Peeltsebuli, kurjade vaimude peamehe abil!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 25, 'Et aga Jeesus nende mõtteid mõistis, ütles Ta neile: \"Iga kuningriik, mis on isekeskis riius, hävib; ja ükski linn või koda, mis on isekeskis riius, ei jää püsima.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 26, 'Kui nüüd saatan välja ajab saatana, siis on ta riius iseenesega; kuidas siis võib tema riik püsida?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 27, 'Ja kui Mina Peeltsebuli abil kurje vaime välja ajan, kelle abil ajavad teie pojad need välja? Sellepärast peavad nemad olema teile kohtumõistjaiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 28, 'Aga kui Mina Jumala Vaimuga kurjad vaimud välja ajan, siis on juba Jumala Riik teie juure tulnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 29, 'Või kuidas võib keegi minna vägeva kotta ja riisuda tema riistu, kui ta enne ei seo seda vägevat? Alles siis ta saab riisuda tema maja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 30, 'Kes ei ole Minuga, see on Minu vastu; ja kes Minuga ei kogu, see pillab laiali.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 31, 'Seepärast Ma ütlen teile: Iga patt ja jumalapilge antakse inimestele andeks; aga Püha Vaimu pilget ei anta inimestele andeks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 32, 'Ja kes iganes räägib sõna Inimese Poja vastu, sellele antakse see andeks, aga kes iganes midagi räägib Püha Vaimu vastu, sellele ei anta seda andeks, ei selles maailmas ega tulevases.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 33, 'Kas tunnistage puu heaks ja ta vili heaks, või tunnistage puu halvaks ja ta vili halvaks. Sest puud tuntakse tema viljast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 34, 'Te rästikute sigitis, kuidas te võite rääkida head, kui te olete kurjad? Sellest, mida süda on täis, sellest räägib suu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 35, 'Hea inimene toob südame heast tagavarast esile head, ja halb inimene toob halvast tagavarast esile halba.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 36, 'Ent Ma ütlen teile, et inimesed peavad kohtupäeval aru andma igaühest tühjast sõnast, mis nad on rääkinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 37, 'Sest su sõnust arvatakse sind õigeks ja su sõnust mõistetakse sind hukka.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 38, 'Siis vastasid Temale mõningad kirjatundjad ja variserid ning ütlesid: \"Õpetaja, me tahame Sinult näha tunnustähte.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 39, 'Aga Tema kostis ja ütles neile: \"See kuri ja abielurikkuja tõug otsib tunnustähte, aga talle ei anta muud tunnustähte kui prohvet Joona tunnustäht.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 40, 'Sest otsegu Joona oli valaskala kõhus kolm päeva ja kolm ööd, nõnda peab Inimese Poeg olema maapõues kolm päeva ja kolm ööd.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 41, 'Niinive mehed astuvad kohtus esile ühes sellesinase sugupõlvega ja süüdistavad teda, sest nad parandasid meelt Joona jutluse tõttu. Ja vaata, siin on enam kui Joona!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 42, 'Lõunamaa kuninganna tõuseb üles kohtus ühes sellesinase sugupõlvega ja süüdistab Teda, sest Ta tuli ilmamaa otsast kuulama Saalomoni tarkust. Ja vaata, siin on enam kui Saalomon!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 43, 'Kui rüve vaim on inimesest väljunud, käib ta kuivi paiku mööda ja otsib hingamist, aga ei leia.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 44, 'Siis ta ütleb: Ma lähen tagasi oma kotta, kust ma väljusin! Ja kui ta tuleb, leiab ta selle tühja olevat, puhitud ja ehitud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 45, 'Siis ta läheb ja võtab enesega kaasa teist seitse vaimu, kes on temast kurjemad; ja kui nad sisse tulevad, elavad nad seal. Ja selle inimese viimne lugu läheb pahemaks kui esimene. Nõnda käib ka selle kurja sugupõlve käsi.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 46, 'Kui Ta rahvale veel rääkis, vaata, siis seisid Ta Ema ja Ta vennad õues ja tahtsid Temaga rääkida.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 47, 'Siis ütles keegi Temale: \"Vaata, Su Ema ja Su vennad seisavad õues ja tahavad Sinuga rääkida!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 48, 'Tema aga vastas ja ütles sellele, kes seda Temale teatas: \"Kes on Mu Ema ja kes on Mu vennad?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 49, 'Ja Ta sirutas Oma käe jüngrite poole ning ütles: \"Vaata, siin on Mu ema ja Mu vennad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 50, 'Sest kes iganes teeb Mu Isa tahtmist, Kes on taevas, see on Mu vend ja õde ja ema!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 1, 'Sel päeval väljus Jeesus sealt kojast ja istus maha mere ääre.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 2, 'Ja palju rahvast kogunes Tema juure, nii et Ta pidi astuma paati ja maha istuma. Ja kõik rahvas seisis rannal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 3, 'Ja Ta rääkis neile palju tähendamissõnadega ning ütles: \"Vaata, külvaja läks välja külvama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 4, 'Ja kui ta külvas, kukkus muist tee ääre, ja linnud tulid ja sõid selle ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 5, 'Muist kukkus kaljuse maa peale, kus tal ei olnud palju mulda. Ja see tärkas ruttu, sest tal ei olnud sügavat maad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 6, 'Aga kui päike tõusis, kõrbes ta, ja et tal ei olnud juurt, kuivas ta ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 7, 'Muist kukkus ohakate sekka ja ohakad tõusid ja lämmatasid selle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 8, 'Aga muist kukkus hea maa peale ja kandis vilja, mõni iva sada seemet, mõni kuuskümmend ja mõni kolmkümmend.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 9, 'Kellel kõrvad on, see kuulgu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 10, 'Siis jüngrid astusid Ta juure ja ütlesid Temale: \"Mispärast Sa räägid neile tähendamissõnadega?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 11, 'Tema kostis ning ütles: \"Teile on antud mõista Taevariigi saladusi, neile aga ei ole antud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 12, 'Sest kellel on, sellele antakse, ja tal on küllalt; aga kellel ei ole, sellelt võetakse ka see, mis tal on!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 13, 'Sellepärast Ma räägin neile tähendamissõnadega, et nad nähes ei näeks ja kuuldes ei kuuleks ega mõistaks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 14, 'Ja nende kohta läheb täide prohvet Jesaja ennustus, mis ütleb: Kuuldes te kuulete ega mõista, ja nähes te näete ega taipa!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 15, 'Sest sellesinase rahva süda on tuimaks läinud ja nad kuulevad raskesti oma kõrvadega ja sulevad oma silmad, et nad silmadega ei näeks ja kõrvadega ei kuuleks ja südamega ei mõistaks ega pöörduks, et Mina neid parandaksin!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 16, 'Aga õndsad on teie silmad, et nad näevad, ja teie kõrvad, et nad kuulevad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 17, 'Sest tõesti, Ma ütlen teile, paljud prohvetid ja õiged on igatsenud näha, mida teie näete, ega ole näinud, ja kuulda, mida teie kuulete, ega ole kuulnud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 18, 'Kuulge siis teie nüüd tähendamissõna külvajast:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 19, 'Kui keegi Kuningriigi sõna kuuleb ega saa aru, siis tuleb tige ja kisub ära selle, mis oli külvatud ta südamesse. Seda tähendab see, mis külvati tee ääre.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 20, 'Mis kaljuse maa peale külvati tähendab seda, kes sõna kuuleb ja võtab selle kohe rõõmuga vastu;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 21, 'temal aga ei ole juurt eneses, vaid ta on ainult üürikeseks: kui viletsus ning kiusatus tuleb sõna pärast, taganeb ta varsti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 22, 'Aga mis ohakate sekka külvati, tähendab seda, kes küll sõna kuuleb, maailma mure ja rikkuse pettus aga lämmatab sõna ära ja see jääb viljatumaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 23, 'Ent mis külvati hea maa peale, tähendab seda, kes sõna kuuleb ning saab sellest aru ja kannab ka vilja; ja mõni annab sada seemet, mõni kuuskümmend, mõni kolmkümmend!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 24, 'Teise tähendamissõna esitas Ta neile ning ütles: \"Taevariik on mehe sarnane, kes oma põllule külvas hea seemne.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 25, 'Aga inimeste magades tuli ta vaenlane ja külvas lustet nisu sekka ja läks ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 26, 'Kui nüüd oras kasvas ja vili hakkas looma, siis tuli ka luste nähtavale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 27, 'Aga majaisanda orjad tulid tema juure ning ütlesid temale: Isand, eks sa külvanud head seemet oma põllule, kust tuleb sellele nüüd lustet?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 28, 'Tema ütles neile: Seda on teinud vaenuline inimene. Siis ütlesid orjad temale: Kas sa nüüd tahad, et me läheme ja selle kokku kogume?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 29, 'Aga tema ütles: Ei, et te lustet katkudes ühes sellega ei kisuks välja ka nisu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 30, 'Laske mõlemad ühtlasi kasvada lõikuseks, ja lõikuseajal Ma ütlen lõikajaile: Koguge enne lusted ja siduge vihku ärapõletamiseks, nisu aga koguge kokku mu aita!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 31, 'Teise tähendamissõna Ta esitas neile ning ütles: \"Taevariik on sinepiivakese sarnane, mille mees võttis ning külvas oma põllule.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 32, 'See on küll kõige väiksem kõigist seemneist, aga kui see on kasvanud, on see suurim aiataimedest ja saab puuks, nii et taeva linnud tulevad ja teevad pesad ta okstele!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 33, 'Teise tähendamissõna Ta rääkis neile: \"Taevariik on haputaigna sarnane, mille naine võttis ja segas kolme vaka jahu sekka, kuni kõik läks hapnema!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 34, 'Seda kõike rääkis Jeesus rahvale tähendamissõnadega ja ilma tähendamissõnata ei rääkinud Ta neile midagi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 35, 'et läheks täide, mis on üteldud prohveti kaudu: \"Ma avan Oma suu tähendamissõnades ja kuulutan, mis on olnud salajas maailma algusest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 36, 'Siis Jeesus laskis rahvahulgad ära minna ja tuli koju. Ja Ta jüngrid astusid Tema juure ning ütlesid: \"Seleta meile tähendamissõna põllulustest.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 37, 'Tema kostis ja ütles: \"Kes head seemet külvab, on Inimese Poeg.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 38, 'Põld on maailm; hea seeme on Kuningriigi lapsed, lusted aga on tigeda lapsed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 39, 'Vaenlane, kes neid külvab, on kurat. Aga lõikus on maailma-ajastu lõpp; lõikajad on Inglid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 40, 'Otsegu nüüd umbrohi kogutakse ja tulega ära põletatakse, nõnda peab ka sündima selle maailma-ajastu lõpul.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 41, 'Inimese Poeg läkitab Oma Inglid ja Nemad koguvad Tema Kuningriigist kõik pahandused ja kõik need, kes teevad, mis on käsu vastu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 42, 'ja heidavad nad tuleahju; seal on ulumine ja hammaste kiristamine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 43, 'Siis paistavad õiged nagu päike Oma Isa Kuningriigis. Kel kõrvad on, see kuulgu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 44, 'Taevariik on põllusse peidetud varanduse sarnane, mille mees leidis ning kinni kattis, ja läks sealt ära rõõmsana selle üle ja müüs ära kõik, mis tal oli, ja ostis selle põllu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 45, 'Taas on Taevariik kaupmehe sarnane, kes otsis ilusaid pärle,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 46, 'ja kui ta oli leidnud ühe kalli pärli, läks ta ja müüs kõik, mis tal oli, ning ostis selle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 47, 'Taas on Taevariik nooda sarnane, mis merre heideti ja kokku vedas kõiksugu kalu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 48, 'Ja kui see täis sai, veeti ta randa, ja istuti maha ning koguti head astjatesse, aga halvad visati ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 49, 'Nõnda sünnib maailma-ajastu lõpul: Inglid väljuvad ja eraldavad kurjad õigete hulgast');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 50, 'ja heidavad nad tuleahju; seal on ulumine ja hammaste kiristamine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 51, 'Kas te olete aru saanud kõigest sellest?\" Nad ütlesid Temale: \"Jah!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 52, 'Siis Ta ütles neile: \"Seepärast on iga kirjatundja, kes on õpetatud Taevariigi jaoks, majaisandmehe sarnane, kes toob esile oma tagavarast uut ja vana!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 53, 'Ja sündis, kui Jeesus oli lõpetanud need tähendamissõnad, et Ta läks sealt ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 54, 'Ja Ta tuli Oma kodukohta ja õpetas neid nende kogudusekojas, nii et nad hämmastusid ning ütlesid: \"Kust on Sellel see tarkus ja need vägevad teod?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 55, 'Eks Tema ole see puusepa poeg? Eks Ta Ema nimetata Maarjaks ja Ta vendi Jakoobuseks ja Jooseseks ja Siimonaks ja Juudaks?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 56, 'Ja eks Ta õed kõik ole meie juures? Kust siis Temale see kõik on tulnud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 57, 'Ja nad pahandusid Temast. Aga Jeesus ütles neile: \"Ei peeta prohvetist kuskil nii vähe lugu kui kodukohas ja ta omas majas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 58, 'Ja Ta ei teinud seal mitte palju vägevaid tegusid nende uskmatuse pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 1, 'Sel ajal kuulis nelivürst Heroodes Jeesusest räägitavat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 2, 'Ja ta ütles oma teenijaile: \"See on Ristija Johannes; tema on surnuist üles tõusnud ja sellepärast on imelised väed tegevad tema sees.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 3, 'Oli ju Heroodes Johannese kinni võtnud, sidunud ja pannud ta vangi Heroodiase, oma venna Filippuse naise pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 4, 'Sest Johannes oli temale ütelnud: \"Sul ei ole luba teda pidada.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 5, 'Ja Heroodes oleks tahtnud Johannese tappa, aga kartis rahvast, sest nad pidasid teda prohvetiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 6, 'Aga kui Heroodese sünnipäev tuli, tantsis Heroodiase tütar nende keskel ja meeldis Heroodesele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 7, 'Sellepärast tõotas ta vandega temale anda, mida ta iganes peaks paluma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 8, 'Siis ütles tütar oma ema õhutusel: \"Anna mulle siia vaagnal Ristija Johannese pea!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 9, 'Ja kuningas sai kurvaks, kuid vande ja lauasistujate pärast ta käskis temale selle anda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 10, 'Ta läkitas siis ja laskis vangihoones Johannese pea otsast raiuda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 11, 'Ja tema pea toodi vaagnal ning anti neitsile; ja neitsi viis selle oma emale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 12, 'Ja tema jüngrid tulid ning võtsid tema keha ja matsid ta maha ja tulid ning teatasid sellest Jeesusele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 13, 'Kui Jeesus seda kuulis, läks Ta sealt paadiga ära tühja paika üksipäini. Ja kui rahvahulgad seda kuulsid, käisid nad linnadest jala Tema järel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 14, 'Ja maale astudes Ta nägi palju rahvast. Ja Tal hakkas nendest hale meel, ja Ta tegi terveks nende põdejad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 15, 'Ent õhtu tulles astusid jüngrid Tema juure ning ütlesid: \"See on tühi paik ja aeg on juba möödas; lase rahvas minna, et nad läheksid küladesse ja ostaksid enestele toidupoolist.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 16, 'Aga Jeesus ütles neile: \"Nendel pole tarvis ära minna; andke teie neile süüa.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 17, 'Nemad ütlesid Temale: \"Meil ei ole siin rohkem kui viis leiba ja kaks kala!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 18, 'Kuid Tema ütles: \"Tooge mulle need siia!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 19, 'Ja Ta käskis rahvahulgad istuda murule, võttis need viis leiba ja kaks kala, vaatas üles taeva poole ja õnnistas ning murdis ja andis leivad jüngrite kätte, aga jüngrid andsid rahvale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 20, 'Ja nad kõik sõid ja nende kõhud said täis. Ja nad korjasid kokku ülejäänud palukesi kaksteistkümmend korvitäit.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 21, 'Ent neid, kes olid söönud, oli ligi viis tuhat meest peale naiste ja laste.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 22, 'Ja sedamaid sundis Jeesus Oma jüngreid astuma paati ja sõitma Tema eele teisele poole, kuni Ta laseb rahva minema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 23, 'Kui Ta siis rahva oli lasknud minema, läks Ta Ise üles mäele üksipäini palvetama. Ja õhtu tulles oli Ta üksinda sealsamas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 24, 'Aga paat oli juba palju vagusid maad kaldast eemal ja oli hädas lainetega, sest tuul oli vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 25, 'Aga neljandal öövahikorral tuli Jeesus nende juure kõndides merel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 26, 'Ja kui jüngrid Teda nägid merel kõndivat, kohkusid nad ja ütlesid: \"See on tont!\" Ja nad kisendasid hirmu pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 27, 'Aga sedamaid rääkis Jeesus nendega ning ütles: \"Olge julged, Mina olen see, ärge kartke!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 28, 'Peetrus kostis Temale ning ütles: \"Issand, kui Sina oled, siis käsi mind tulla Enese juure vee peale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 29, 'Tema ütles: \"Tule!\" Ja Peetrus astus paadist välja ning kõndis vee peal ja tuli Jeesuse juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 30, 'Aga nähes tuult lõi ta kartma ja hakkas vajuma, kisendas ning ütles: \"Issand, päästa mind!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 31, 'Ja Jeesus sirutas sedamaid Oma käe, haaras temast kinni ja ütles talle: \"Sa nõdrausuline, miks sa kahtlesid?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 32, 'Ja nad astusid paati, ja tuul rauges.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 33, 'Siis paadis olijad tulid ja kummardasid Teda ning ütlesid: \"Tõesti, Sa oled Jumala Poeg!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 34, 'Ja kui nad olid jõudnud teisele kaldale, tulid nad Gennesareti maile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 35, 'Ja kui selle koha mehed Ta ära tundsid, läkitasid nad sõna kogu ümberkaudsele maale. Ja Tema juure toodi kõik haiged;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 36, 'ja need palusid Teda, et nad saaksid vaid puudutada Tema kuue palistust. Ja nii paljud, kui puudutasid, said terveks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 1, 'Siis tuli varisere ja kirjatundjaid Jeruusalemast Jeesuse juure ja need ütlesid:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 2, '\"Miks Sinu jüngrid rikuvad vanemate pärimust? Sest nad ei pese käsi, kui nad hakkavad leiba võtma.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 3, 'Aga Tema kostis ja ütles neile: \"Miks te ise rikute Jumala käsku oma pärimuse pärast?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 4, 'Sest Jumal on ütelnud: Sa pead oma isa ja ema austama, ja kes isa või ema neab, peab surma surema!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 5, 'Kuid teie ütlete: Kes iganes isale või emale ütleb: Ohvrianniks läheb see, mis sul iganes on õigus minult saada!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 6, 'see ei tarvitse oma isa või ema austada. Sellega te olete Jumala sõna tühjaks teinud oma pärimuse pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 7, 'Te silmakirjatsejad, Jesaja on teist õigesti ennustanud, üteldes:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 8, 'See rahvas austab Mind oma huultega, aga nende süda on Minust kaugel;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 9, 'ilmaaegu teenivad nad Mind, õpetades õpetusi, mis on inimeste käskimised!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 10, 'Ja Ta kutsus rahva Enese juure ning ütles neile: \"Kuulge ja mõistke!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 11, 'Mitte see, mis suust sisse läheb, ei rüveta inimest, vaid see, mis suust väljub, rüvetab inimest.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 12, 'Siis astusid Tema jüngrid Ta juure ja ütlesid Temale: \"Kas Sa tead, et variserid pahandusid kuuldes seda sõna?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 13, 'Aga Tema kostis ning ütles: \"Iga taim, mida Mu taevane Isa pole istutanud, kistakse välja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 14, 'Jätke nad! Nad on pimedate pimedad teejuhid. Aga kui pime pimedat juhib, langevad mõlemad auku!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 15, 'Siis kostis Peetrus ja ütles Temale: \"Seleta meile see võrdum!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 16, 'Jeesus ütles: \"Kas teiegi veel ei saa aru?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 17, 'Eks te mõista, et kõik, mis suust sisse läheb, läheb kõhtu ja väljub eri paika.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 18, 'Aga mis suust väljub, see lähtub südamest ja see rüvetab inimese.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 19, 'Sest südamest lähtub kurje mõtteid, mõrvu, abielurikkumisi, hoorust, vargusi, valetunnistusi, jumalapilget.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 20, 'Need on, mis rüvetavad inimese; kuid pesemata kätega söömine ei rüveta inimest.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 21, 'Ja Jeesus tuli sealt ära ning läks Tüürose ja Siidoni rajadesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 22, 'Ja vaata, neist paigust väljus Kaananea naine; see kisendas ning ütles Talle: \"Issand, Taaveti poeg, halasta mu peale! Mu tütart vaevab kuri vaim hirmsasti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 23, 'Aga Ta ei vastanud temale sõnagi. Siis Tema jüngrid astusid Ta juure, palusid Teda ning ütlesid: \"Saada ta ära, sest ta kisendab meie taga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 24, 'Ent Tema kostis ning ütles: \"Mind ei ole läkitatud muude kui Iisraeli soo kadunud lammaste juure!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 25, 'Kuid naine tuli, kummardas Teda ning ütles: \"Issand, aita mind!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 26, 'Aga Tema kostis ning ütles: \"Ei ole ilus võtta laste leib ja heita koerukeste ette!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 27, 'siis ütles naine: \"Jah, Issand, ometigi söövad koerukesed raasukesi, mis nende isandate laualt langevad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 28, 'Siis kostis Jeesus ning ütles temale: \"Oh naine, su usk on suur! Sulle sündigu nõnda, kuidas sa tahad!\" Ja Tema tütar sai terveks sestsamast tunnist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 29, 'Ja Jeesus läks sealt ära ja tuli Galilea mere ääre; ja Ta läks mäele ja istus sinna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 30, 'Ja Tema juure tuli palju rahvast, tuues enesega ühes jalutumaid, vigaseid, pimedaid, keeletumaid ja palju teisi; ja nad heitsid need Tema jalge ette. Ja Ta tegi nad terveks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 31, 'nii et rahvas pani imeks, nähes keeletumaid rääkivat, vigaseid olevat terved, jalutumaid käivat ja pimedaid nägevat. Ja nad andsid au Iisraeli Jumalale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 32, 'Ja Jeesus kutsus Oma jüngrid Enese juure ja ütles: \"Mul on meel hale rahvast, sest nad on juba kolm päeva olnud Minu juures, ja neil ei ole midagi süüa; Ma ei taha lasta neid ära minna ilma söömata, et nad teel ei nõrkeks.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 33, 'Siis ütlesid Ta jüngrid Temale: \"Kust me võtame kõrves nii palju leibu, et nii hulga rahva kõhud täita?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 34, 'Ja Jeesus ütles neile: \"Mitu leiba teil on?\" Nad vastasid: \"Seitse, ja pisut kalukesi.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 35, 'Siis Ta käskis rahva maha istuda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 36, 'Ja Ta võttis need seitse leiba ja kalad, tänas, murdis ning andis jüngrite kätte, ja jüngrid andsid rahvale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 37, 'Ja kõik sõid, ja nende kõhud said täis; ja nad korjasid kokku ülejäänud palukesi seitse korvitäit.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 38, 'Ent neid, kes olid söönud, oli neli tuhat meest peale naiste ja laste.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 39, 'Ja kui Ta oli lasknud rahva ära minna, läks Ta paati ja tuli Magdala maa-alale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 1, 'Siis tulid variserid ja saduserid Ta juure Teda kiusama ja nõudsid, et Ta neile näitaks mõne tunnustähe taevast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 2, 'Aga Ta kostis ning ütles neile: \"Õhtu tulles te ütlete: Head ilma tuleb, sest taevas punab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 3, 'Ja hommikul te ütlete: Täna tuleb rajuilm, sest taevas punab ja on pilves. Taeva nägu te oskate küll mõista, kuid aegade tunnustähti te ei suuda mõista.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 4, 'See kuri ja abielurikkuja tõug otsib tunnustähte, ja temale ei anta muud tähte kui prohvet Joona täht!\" Ja Ta jättis nad maha ning läks ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 5, 'Ja kui Tema jüngrid olid jõudnud teispoolsele kaldale, olid nad unustanud leiba kaasa võtta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 6, 'Aga Jeesus ütles neile: \"Olge ettevaatlikud ja hoiduge variseride ja saduseride haputaignast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 7, 'Siis nad arutasid isekeskis ja ütlesid: \"See käib selle kohta, et me ei võtnud leiba kaasa.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 8, 'Jeesus märkas seda ja ütles: \"Te nõdrausulised, miks te arutate isekeskis, et te ei ole leiba kaasa võtnud?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 9, 'Kas te veel ei saa aru? Eks te mäleta neid viit leiba viiele tuhandele ja mitu korvitäit te saite,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 10, 'ja neid seitset leiba neljale tuhandele ja mitu korvitäit te saite?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 11, 'Kuidas te siis ei mõista, et Ma teile ei rääkinud leivast, kui Ma käskisin teid hoiduda variseride ja saduseride haputaignast?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 12, 'Siis nad mõistsid, et Ta neid ei käskinud hoiduda leiva haputaignast, vaid variseride ja saduseride õpetusest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 13, 'Aga kui Jeesus tuli Filippuse Kaisarea rajale, küsis Ta Oma jüngritelt ning ütles: \"Keda ütlevad inimesed Mind, Inimese Poja, olevat?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 14, 'Nemad ütlesid: \"Mõned ütlevad Sind olevat Ristija Johannese, teised Eelija, aga teised Jeremija või ühe prohveteist.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 15, 'Tema ütles neile: \"Aga teie, keda teie Mind ütlete olevat?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 16, 'Siimon Peetrus vastas ning ütles: \"Sina oled Kristus, elava Jumala Poeg!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 17, 'Jeesus vastas ning ütles temale: \"Õnnis oled Sa, Siimon, Joona poeg, sest liha ja veri ei ole sulle seda ilmutanud, vaid Mu Isa, Kes on taevas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 18, 'Ja Mina ütlen sulle ka: Sina oled Peetrus, ja sellele kaljule Ma ehitan Oma Koguduse, ja surmavalla väravad ei võida seda!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 19, 'Ma annan sinule Taevariigi võtmed, ja mis sa maa peal seod, see on taevas seotud, ja mis sa maa peal lahti päästad, on ka taevas lahti päästetud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 20, 'Siis Ta keelas Oma jüngreid, et nad kellelegi ei ütleks, et Tema on Kristus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 21, 'Sellest ajast hakkas Jeesus Kristus teatama Oma jüngritele, et Ta peab minema Jeruusalemma ja palju kannatama vanemate ja ülempreestrite ja kirjatundjate poolt, ja et Ta tapetakse ja kolmandal päeval äratatakse üles.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 22, 'Siis Peetrus võttis Tema isepäinis, hakkas Teda noomima ja ütles: \"Jumal hoidku, Issand! Ärgu seda Sulle sündigu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 23, 'Aga Ta pöördus ja ütles Peetrusele: \"Tagane Minust, saatan! Sa oled Mulle pahanduseks; sest sa ei mõtle sellele, mis on Jumala, vaid mis on inimeste meelt mööda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 24, 'Siis Jeesus ütles Oma jüngritele: \"Kui keegi tahab Minu järele tulla, siis ta salaku end ning võtku oma rist enese peale ja järgigu Mind.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 25, 'Sest kes iganes oma hinge tahab päästa, see kaotab selle; aga kes oma hinge kaotab Minu pärast, see leiab selle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 26, 'Sest mis kasu on inimesel sellest, kui ta kogu maailma kasuks saaks, kuid oma hingele kahju teeks? Või mis lunastushinda võib inimene anda oma hinge eest?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 27, 'Sest Inimese Poeg tuleb Oma Isa auhiilguses Oma Inglitega, ja siis Ta tasub igaühele Tema tööd mööda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 28, 'Tõesti, Mina ütlen teile, neist, kes siin seisavad, on mõned, kes ei maitse surma, enne kui nad näevad Inimese Poja tulevat Tema Kuningriigis!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 1, 'Ja kuue päeva pärast Jeesus võttis enesega Peetruse, Jakoobuse ja Johannese, Tema venna, ja viis nad üles kõrgele mäele isepäinis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 2, 'Ja Ta muudeti nende ees; ja Ta pale paistis otsegu päike ja Ta riided läksid valgeks otsegu valgus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 3, 'Ja vaata, Mooses ja Eelija näitasid endid neile ja kõnelesid Temaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 4, 'Aga Peetrus hakkas rääkima ja ütles Jeesusele: \"Issand, siin on hea olla; kui Sa tahad, siis ma teen siia kolm telki, ühe Sinule, uhe Moosesele ja ühe Eelijale?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 5, 'Kui ta alles rääkis, vaata, siis varjas neid hele pilv. Ja vaata, hääl pilvest ütles: \"See on Mu armas Poeg, Kellest Mul on hea meel; Teda kuulake!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 6, 'Ja kui jüngrid seda kuulsid, langesid nad silmili maha ning kartsid väga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 7, 'Ja Jeesus tuli nende juure, puudutas neid ja ütles: \"Tõuske üles ja ärge kartke!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 8, 'Aga kui nad oma silmad üles tõstsid, ei näinud nad kedagi kui Jeesust üksi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 9, 'Ja mäelt alla tulles keelas Jeesus neid ning ütles: \"Ärge rääkige kellelegi sellest nägemusest, kuni Inimese Poeg surnuist üles tõuseb!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 10, 'Ja jüngrid küsisid Temalt ning ütlesid: \"Miks siis kirjatundjad ütlevad, et Eelija peab tulema enne?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 11, 'Tema kostis ja ütles: \"Eelija tuleb küll enne ja seab kõik korda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 12, 'Aga Ma ütlen teile, et Eelija on juba tulnud, ja nad ei ole teda ära tunnud, vaid on temale teinud, mida nad tahtsid. Nõnda peab ka Inimese Poeg kannatama nende poolt!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 13, 'Siis jüngrid said aru, et Ta neile rääkis Ristijast Johannesest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 14, 'Ja kui nad tulid rahva juure, astus Tema ette inimene ja heitis põlvili Ta ette');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 15, 'ning ütles: \"Issand, halasta mu poja peale, sest ta on kuutõbine ja suures vaevas; sagedasti langeb Ta tulle ja sagedasti vette.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 16, 'Ja ma tõin ta Sinu jüngrite juure, kuid nad ei suutnud teda terveks teha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 17, 'Aga Jeesus kostis ning ütles: \"Oh sina uskmatu ja pöörane tõug! Kui kaua pean Ma olema teie juures? Kui kaua pean Ma teiega kannatama? Tooge ta siia mu juure!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 18, 'Ja Jeesus sõitles teda, ja kuri vaim väljus temast, ja poiss sai terveks samast tunnist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 19, 'Siis tulid jüngrid Jeesuse juure isepäinis ja küsisid: \"Miks meie ei võinud teda välja ajada?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 20, 'Aga Tema ütles neile: \"Teie nõdra usu pärast. Sest tõesti Ma ütlen teile, kui teil oleks usku nagu sinepiivake, siis te võiksite ütelda sellele mäele: Siirdu siit sinna! Ja ta siirduks sinna, ja miski ei oleks teile võimatu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 21, 'Seesinane sugu ei lähe muidu välja kui aga palve ja paastumisega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 22, 'Aga kui nad üheskoos viibisid Galileas, ütles Jeesus neile: \"Inimese Poeg antakse ära inimeste kätte;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 23, 'ja nad tapavad Tema, ja kolmandal päeval Ta äratatakse üles!\" Ja nad said väga kurvaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 24, 'Aga kui nad Kapernauma jõudsid, tulid Peetruse juure maksuraha korjajad ja ütlesid: \"Kas teie Õpetaja maksuraha ei maksa?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 25, 'Tema ütles: \"Jah, maksab!\" Ja kui Ta koju tuli, jõudis Jeesus talle küsimusega ette ning ütles: \"Mis sa arvad, Siimon? Kellelt võtavad kuningad maa peal tolli või maksu, kas omilt lastelt või võõrastelt?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 26, 'Peetrus vastas Temale: \"Võõrastelt.\" Jeesus ütles temale: \"Siis on lapsed sellest vabad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 27, 'Kuid et neid mitte pahandada, mine merele, heida õng sisse ja võta esimene kala, mis üles tuleb; ja kui sa tema suu avad, sa leiad hõberaha. Võta see ja anna neile minu ja enese eest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 1, 'Sel ajal tulid jüngrid Jeesuse juure ja ütlesid: \"Kes on küll suurim Taevariigis?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 2, 'Ja Jeesus kutsus lapsukese Enese juure, asetas tema nende keskele');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 3, 'ja ütles: \"Tõesti Ma ütlen teile, kui te ei pöördu ega saa kui lapsukesed, ei saa te mitte Taevariiki!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 4, 'Kes nüüd iseennast alandab nagu see lapsuke, on suurem Taevariigis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 5, 'Ja kes iganes ühe niisuguse lapsukese vastu võtab Minu nimel, see võtab Mind vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 6, 'Aga kes pahandab ühe neist pisukesist, kes Minusse usuvad, sellele oleks parem, et veskikivi tema kaela poodaks ja ta uputataks mere sügavusse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 7, 'Häda maailmale pahanduste pärast! Sest pahandused peavad tulema, kuid häda sellele inimesele, kelle läbi pahandus tuleb!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 8, 'Aga kui su käsi või su jalg sind pahandab, siis raiu ta maha ja heida enesest ära. Parem on sulle, et sa vigasena või jalutuna lähed elusse, kui et sul on kaks kätt ja kaks jalga ja sind heidetakse igavesse tulle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 9, 'Ja kui su silm sind pahandab, kisu ta välja ja viska enesest ära. Parem on sulle, et sa ühe silmaga lähed elusse kui et sul on kaks silma ja sind heidetakse põrgutulle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 10, 'Vaadake ette, et te ühtki neist pisukesist ei põlga; sest Ma ütlen teile, et nende Inglid taevas alati näevad Mu Isa palet, Kes on taevas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 11, 'Sest Inimese Poeg on tulnud päästma seda, mis on kadunud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 12, 'Mis te arvate? Kui kellelgi inimesel juhtub olema sada lammast ja üks neist eksib ära, eks Ta jäta need üheksakümmend üheksa mägedele ja lähe otsima seda, kes on ära eksinud?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 13, 'Ja kui juhtub, et ta selle leiab, tõesti Ma ütlen teile, et ta sellest tunneb rohkem rõõmu kui üheksakümne üheksast, kes ei olnud ära eksinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 14, 'Nõnda ei ole ka teie Isa tahtmine, Kes on taevas, et üks neist pisukesist hukka läheks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 15, 'Aga kui su vend eksib sinu vastu, siis mine ja noomi teda nelja silma all. Kui ta sind kuulab, siis sa oled oma venna võitnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 16, 'Aga kui ta sind ei kuula, siis võta enesega veel üks või kaks, et iga asi kinnitataks kahe või kolme tunnistaja sõnaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 17, 'Aga kui ta neid ei kuula, siis ütle kogudusele; aga kui ta kogudustki ei kuula, siis Ta olgu sinu meelest nagu pagan ja tölner.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 18, 'Tõesti Ma ütlen teile, et mis te iganes maa peal seote, on ka taevas seotud, ja mis te iganes maa peal lahti päästate, on ka taevas lahti päästetud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 19, 'Taas ütlen Ma tõesti teile, et kui kaks teie seast ühel nõul on maa peal mingi asja pärast, mida nad iganes paluvad, siis see saab neile Minu Isalt, Kes on taevas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 20, 'Sest kus kaks või kolm koos on Minu nimel, seal olen Mina nende keskel.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 21, 'Siis astus Peetrus Tema juure ja ütles: \"Issand, mitu korda ma pean, kui mu vend mu vastu eksib, temale andeks andma? Ons küllalt seitsmest korrast?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 22, 'Jeesus ütles temale: \"Ma ei ütle sulle mitte: Seitse korda! vaid: Seitsekümmend korda seitse korda!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 23, 'Sellepärast on Taevariik kuninga sarnane, kes oma sulastega tahtis aru teha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 24, 'Aga kui ta hakkas aru tegema, toodi üks ta ette, kes oli talle kümme tuhat talenti võlgu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 25, 'Aga kui tal ei olnud maksta, käskis isand müüa tema ja ta naise ja ta lapsed ja kõik, mis tal oli, ja maksta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 26, 'Siis sulane heitis maha, kummardas teda ja ütles: Kannata minuga, ja ma maksan sulle kõik!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 27, 'Siis oli isandal hale meel sellest sulasest, ta laskis tema lahti ja kinkis talle võla.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 28, 'Aga seesama sulane läks välja ja leidis ühe oma kaassulase, kes temale oli võlgu sada teenarit. Ja ta võttis tema kinni, kägistas teda ja ütles: Maksa, mis sa oled võlgu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 29, 'siis tema kaassulane heitis maha tema jalge ette, palus teda ning ütles: Kannata minuga, ja ma maksan sulle kõik!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 30, 'Aga tema ei tahtnud, vaid läks ära ja heitis ta vangi, kuni ta maksab oma võla.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 31, 'Aga kui tema kaassulased nägid, mis sündis, said nad väga kurvaks ja tulid ning kaebasid oma isandale kõik, mis oli sündinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 32, 'siis kutsus ta isand tema enese ette ja ütles temale: Sa tige sulane! Kõik selle võla ma kinkisin sulle, sellepärast et sa mind palusid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 33, 'eks siis sinagi pidanud halastama oma kaassulase peale, nõnda nagu mina sinu peale halastasin?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 34, 'Ja ta isand sai vihaseks ja andis tema piinajate kätte, kuni ta maksab kõik, mis tal temaga oli võlgu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 35, 'Nõnda teeb ka Minu taevane Isa teile, kui te igaüks omast südamest andeks ei anna oma vennale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 1, 'Ja sündis, kui Jeesus need kõned oli lõpetanud, et Ta läks ära Galileamaalt ja tuli Juudamaa alale teisele poole Jordanit.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 2, 'Ja palju rahvast järgis Teda, ja Ta tegi neid seal terveks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 3, 'Siis tuli Tema juure varisere Teda kiusama, ja need ütlesid: \"Kas on luba oma naist enesest lahutada igasugusel põhjusel?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 4, 'Tema kostis ja ütles: \"Kas te ei ole lugenud, et Looja nad algusest lõi meheks ja naiseks, 5 ja ütles: Seepärast inimene jätab maha oma isa ja ema ning hoiab oma naise poole, ja need kaks on üks liha!?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 5, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 6, 'Nõnda ei ole nad siis enam kaks, vaid on üks liha. Mis nüüd Jumal on ühte pannud, seda inimene ärgu lahutagu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 7, 'Nad ütlesid Temale: \"Mispärast siis Mooses käskis anda lahutuskirja ja lahutada?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 8, 'Ta ütles neile: \"Mooses on teile teie südame kanguse pärast annud loa oma naisi enestest lahutada; algusest ei ole see mitte nõnda olnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 9, 'Aga Ma ütlen teile: Kes oma naise enesest lahutab muidu kui hooruse pärast ja võtab teise, see rikub abielu, ja kes võtab lahutatud naise, see rikub abielu.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 10, 'Siis ütlesid jüngrid Temale: \"Kui inimese lugu naisega on nõnda, siis ei ole hea abielluda.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 11, 'Aga Tema ütles neile: \"Kõik ei saa aru sellest sõnast, vaid ainult need, kelledele see on antud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 12, 'Sest on kohitsetuid, kes emaihust nõnda on sündinud, ja on kohitsetuid, kes inimeste poolt on kohitsetud, ja on kohitsetuid, kes ise on ennast kohitsenud Taevariigi pärast. Kes suudab aru saada, saagu aru!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 13, 'Siis toodi Tema juure lapsukesi, et Ta Oma käed nende peale paneks ja palvetaks. Aga jüngrid sõitlesid toojaid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 14, 'Ent Jeesus ütles: \"Jätke lapsukesed rahule ja ärge keelake neid Minu juure tulemast, sest niisuguste päralt on Taevariik!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 15, 'Ja Ta pani Oma käed nende peale ja läks sealt ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 16, 'Ja vaata, üks mees astus Tema juure ja ütles Temale: \"Õpetaja, mis head Ma pean tegema, et saaksin igavese elu?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 17, 'Aga Ta ütles talle: \"Miks sa mult küsid, mis on hea? Üksainus on, Kes on hea. Ent kui sa tahad minna elu sisse, siis pea käsud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 18, 'Tema küsis Temalt: \"Missugused?\" Jeesus ütles: \"Sa ei tohi tappa; Sa ei tohi abielu rikkuda; Sa ei tohi varastada; Sa ei tohi valet tunnistada;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 19, 'Sa pead oma isa ja ema austama; ja armasta oma ligimest nagu iseennast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 20, 'Noor mees ütles Temale: \"Seda kõike ma olen pidanud. Mis puudub mul veel?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 21, 'Jeesus ütles talle: \"Kui tahad olla täiuslik, siis mine müü oma varandus ja anna vaestele, siis on sul varandus taevas, ja tule ning järgi Mind!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 22, 'Kui noor mees seda sõna kuulis, läks ta ära kurva meelega, sest tal oli palju vara.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 23, 'Siis Jeesus ütles Oma jüngritele: \"Tõesti, Ma ütlen teile, rikkal on raske pääseda Taevariiki.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 24, 'Ja taas Ma ütlen teile: Hõlpsam on kaamelil minna läbi nõelasilma kui rikkal pääseda Jumala Riiki!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 25, 'Kui jüngrid seda kuulsid, hämmastusid nad väga ning ütlesid: \"Kes siis võib õndsaks saada?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 26, 'Aga Jeesus vaatas neile otsa ja ütles neile: \"Inimestel on see võimatu, kuid Jumalal on kõik võimalik!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 27, 'Siis kostis Peetrus ning ütles Temale: \"Vaata, meie oleme jätnud maha kõik ja oleme järginud Sind. Mis me sellest nüüd saame?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 28, 'Aga Jeesus ütles neile: \"Tõesti, Ma ütlen teile, et teie, kes olete Mind järginud, uuestisündimises, kui Inimese Poeg istub Oma aujärjele, istute ka ise kaheteistkümnele aujärjele ja mõistate kohut kaheteistkümne Iisraeli suguharu üle!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 29, 'Ja igaüks, kes on jätnud maha majad või vennad või õed või isa või ema või lapsed või põllud Minu nime pärast, saab mitu korda rohkem ja pärib igavese elu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 30, 'Aga paljud esimesed jäävad viimseiks, ja viimsed saavad esimesteks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 1, 'Sest Taevariik on majaisanda sarnane, kes vara hommikul välja läks töötegijaid palkama oma viinamäele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 2, 'Kui ta töötegijatega oli kokku leppinud ühe teenari peale päevapalgaks, läkitas ta nad oma viinamäele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 3, 'Ja ta läks välja kolmandal tunnil ja nägi teisi turul tööta seisvat');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 4, 'ja ütles neile: Minge ka teie viinamäele ja mis iganes õige on, annan ma teile!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 5, 'Ja nad läksid ära. Taas läks ta välja kuuendal ja üheksandal tunnil ja tegi nõndasamuti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 6, 'Aga üheteistkümnendal tunnil ta läks välja ja leidis teisi seisvat ja ütles neile: Mis te siin kogu päeva tööta seisate?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 7, 'Nad ütlesid temale: Meid ei ole keegi palganud! Tema ütles neile: Minge teiegi viinamäele!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 8, 'Kui siis õhtu tuli, ütles viinamäe isand oma ülevaatajale: Kutsu töötegijad ja anna neile palk alates viimseist kuni esimesteni.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 9, 'Kui nüüd need tulid, kes üheteistkümnendal tunnil olid palgatud, said nad igaüks ühe teenari.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 10, 'Aga kui esimesed tulid, arvasid nemad, et nad saavad rohkem; ja nemadki said igaüks ühe teenari.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 11, 'Kui nad olid saanud, nurisesid nad majaisanda vastu');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 12, 'ning ütlesid: Need viimsed aga on ühe tunni tööd teinud ja sa oled nad teinud meie väärilisteks, kes me päeva koormat ja palavust oleme kannud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 13, 'Aga ta kostis ning ütles ühele nende seast: Sõber, ma ei tee sulle ülekohut; eks sa leppinud minuga kokku ühe teenari peale?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 14, 'Võta, mis ainule kuulub, ja mine oma teed; aga ma tahan sellele viimsele anda nõnda nagu sinulegi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 15, 'Kas ei ole mul luba enese omaga teha, mida ma tahan? Või on sinu silm kade, et mina hea olen?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 16, 'Nõnda saavad viimsed esimesteks ja esimesed viimseiks. Sest paljud on kutsutud, kuid vähesed on valitud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 17, 'Ja tahtes minna üles Jeruusalemma, võttis Jeesus need kaksteistkümmend isepäinis ja ütles teel olles neile:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 18, '\"Vaata, me läheme üles Jeruusalemma ja Inimese Poeg antakse ülempreestrite ja kirjatundjate kätte, ja nad mõistavad Tema surma');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 19, 'ja annavad Tema paganate kätte pilgata ja piitsutada ja risti lüüa; ja kolmandal päeval Ta äratatakse üles!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 20, 'Siis astus Sebedeuse poegade ema Tema juure ühes oma poegadega, kummardas ja palus Talt midagi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 21, 'Aga Ta ütles naisele: \"Mida sa tahad?\" Tema ütles Temale: \"Ütle, et need mu kaks poega istuksid üks Su paremale ja teine Su vasakule käele Sinu Kuningriigis!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 22, 'Aga Jeesus vastas ning ütles: \"Te ei tea, mida te palute! Kas te võite juua seda karikat, mis Minul tuleb juua?\" Nad ütlesid Talle: \"Jah, võime!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 23, 'Tema ütles neile: \"Minu karika te küll joote, aga Mu paremal ja vasakul käel istuda ei ole Minu käes teile anda, vaid kellele Mu Isa selle on valmistanud.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 24, 'Ja kui need kümme seda kuulsid, sai nende meel pahaseks nende kahe venna peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 25, 'Aga Jeesus kutsus nad Enese juure ja ütles: \"Te teate, et paganate ülemad valitsevad nende üle ja suured isandad tarvitavad vägivalda nende kallal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 26, 'Teie seas aga ei tohi nõnda olla; vaid kes iganes teie seast tahab suureks saada, see olgu teie teenija;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 27, 'ja kes teie seast tahab olla kõige esimene, see olgu teie ori;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 28, 'otsegu Inimese Poeg ei ole tulnud, et Teda teenitaks, vaid teenima ja Oma hinge andma lunaks paljude eest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 29, 'Ja kui nad Jeerikost välja läksid, järgis Teda palju rahvast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 30, 'Ja vaata, kaks pimedat istus tee ääres, ja kui nad kuulsid, et Jeesus läheb mööda, kisendasid nad: \"Issand, Taaveti Poeg, halasta meie peale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 31, 'Aga rahvas sõitles neid, et nad vait jääksid. Kuid nad kisendasid veel enam: \"Issand, Taaveti Poeg, halasta meie peale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 32, 'Siis Jeesus jäi seisma, hüüdis neid ja ütles: \"Mida te tahate, et Ma teile teeksin?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 33, 'Nad ütlesid Temale: \"Issand, et meie silmad läheksid lahti!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 34, 'siis Jeesusel hakkas hale meel ja Ta puudutas nende silmi. Ja kohe nad nägid jälle ja järgisid Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 1, 'Ja kui nad Jeruusalema ligi said ja Betfage poole Õlimäele tulid, siis läkitas Jeesus kaks jüngrit ning ütles neile:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 2, '\"Minge sinna alevisse, mis on teie ees, ja varsti te leiate kinniseotud emaeesli ja sälu ta juures; päästke need lahti ja tooge Mu juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 3, 'Ja kui teile keegi midagi ütleb, siis ütelge: Issandal on neid tarvis! Siis Ta lähetab nad sedamaid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 4, 'Aga see on kõik sündinud, et läheks täide, mis on üteldud prohveti kaudu:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 5, 'Ütelge Siioni tütrele: Vaata, sinu Kuningas tuleb sulle tasane ja istub emaeesli seljas ja sälu seljas, kes on ikkealuse looma varss!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 6, 'Siis jüngrid läksid ja tegid nõnda, nagu Jeesus neid oli käskinud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 7, 'ning tõid emaeesli ja sälu ja panid riided nende peale ja panid Ta riiete peale istuma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 8, 'Aga suurem hulk laotas oma riided tee peale, aga teised raiusid oksi puudest ja puistasid tee peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 9, 'Aga rahvahulgad, kes ees ja taga käisid, hüüdsid: \"Hoosianna Taaveti Pojale! Õnnistatud olgu, Kes tuleb Issanda nimel! Hoosianna kõrges!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 10, 'Ja kui Ta Jeruusalemma sisse läks, tõusis kogu linn liikvele ja ütles: \"Kes See on?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 11, 'Aga rahvas ütles: \"Tema on See Prohvet Jeesus Galilea Naatsaretist!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 12, 'Ja Jeesus läks pühakotta ja ajas välja kõik, kes müüsid ja ostsid pühakojas, ja viskas kummuli rahavahetajate lauad ja tuvimüüjate istmed,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 13, 'ja ütles neile: \"Kirjutatud on: Minu koda peab hüütama palvekojaks, aga teie teete Ta röövliauguks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 14, 'Ja pühakojas tuli Tema juure pimedaid ja jalutumaid, ja Ta tegi nad terveks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 15, 'Aga kui ülempreestrid ja kirjatundjad nägid neid imesid, mida Ta tegi, ja poisse, kes pühakojas hüüdsid: \"Hoosianna Taaveti Pojale!\" sai nende meel pahaseks');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 16, 'ja nad ütlesid Temale: \"Kas Sa kuuled, mida need ütlevad?\" Jeesus ütles neile: \"Jah, kuulen! Kas te iganes pole lugenud: Laste ja imikute suust oled Sa Enesele valmistanud kiituse?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 17, 'Ja Ta jättis nad maha ja läks linnast välja Betaaniasse ja jäi Sinna ööseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 18, 'Aga kui Ta vara hommikul jälle linna läks, hakkas Tal nälg.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 19, 'iialgi enam kasvagu sinust vilja!\" Ja kohe kuivas viigipuu ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 20, 'Kui jüngrid seda nägid, panid nad imeks ning ütlesid: \"Kuidas see viigipuu nii kohe ära kuivas?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 21, 'Aga Jeesus kostis ning ütles neile: \"Tõesti Ma ütlen teile, kui teil oleks usku ja te ei mõtleks kaksipidi, siis te ei teeks mitte ainult seda, mis viigipuule on sündinud, vaid kui te ka ütleksite sellele mäele: Tõuse paigast ja lange merre! siis see sünniks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 22, 'Ja kõik, mida te iganes palves palute uskudes, seda te saate!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 23, 'Ja kui nad tulid pühakotta, astusid ülempreestrid ja rahva vanemad Tema juure, kui Ta õpetas, ja ütlesid: \"Missuguse meelevallaga Sa teed neid asju? Ja kes on Sulle selle meelevalla annud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 24, 'Aga Jeesus kostis ning ütles neile: \"Ma tahan ka teilt küsida ühe asja; kui te Mulle selle ütlete, siis Ma ütlen ka teile, missuguse meelevallaga Ma neid asju teen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 25, 'Kust oli Johannese ristimine, kas taevast või inimestest?\" Aga nemad arutasid asja isekeskis ja ütlesid: \"Kui me ütleme: Taevast! siis Ta ütleb meile: Mispärast te siis ei uskunud teda?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 26, 'Aga kui me ütleme: Inimestest! siis meil tuleb karta rahvast, sest nad kõik peavad Johannest prohvetiks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 27, 'Ja nad kostsid Jeesusele ning ütlesid: \"Me ei tea!\" Siis ütles ka Tema neile: \"Ega Minagi teile ütle, missuguse meelevallaga Ma neid asju teen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 28, 'Aga mis te arvate? Ühel inimesel oli kaks poega. Ja ta läks esimese juure ning ütles: Poeg, mine täna tööle mu viinamäele!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 29, 'Aga tema kostis ning ütles: Küll ma lähen, isand! Ja ta ei läinud mitte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 30, 'Siis isa läks ka teise juure ja ütles nõndasamuti. See kostis ning ütles: Ei mina taha! Pärast ta kahetses ja läks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 31, 'Kumb neist tegi isa tahtmist?\" Nad ütlesid: \"Viimane.\" Jeesus ütles neile: \"Tõesti Ma ütlen teile, et tölnerid ja hoorad saavad enne teid Jumala riiki.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 32, 'Sest Johannes tuli teie juure õiguse teed ja te ei uskunud teda; ent tölnerid ja hoorad uskusid teda. Aga teie, ehk te küll seda nägite, ei kahetsenud pärastki, et teda uskuda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 33, 'Kuulge teist tähendamissõna: Oli majaisand, kes istutas viinamäe ja tegi selle ümber aia ja kaevas sinna surutõrre ja ehitas torni ja andis selle rendile aednike kätte ja läks võõrale maale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 34, 'Kui siis viinamarja korjamise aeg lähenes, läkitas ta oma sulased aednike juure oma vilja vastu võtma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 35, 'Aga aednikud võtsid tema sulased kinni; mõnd nad peksid, mõne nad tapsid, mõne nad viskasid kividega surnuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 36, 'Taas ta läkitas teised sulased, rohkem kui esimesi; ja nad tegid neile nõndasamuti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 37, 'Viimaks ta läkitas oma poja nende juure, mõteldes: Küllap nad mu poega häbenevad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 38, 'Aga kui aednikud nägid poega, ütlesid nad isekeskis: See on see pärija, tulge, tapame ta ära, siis saame tema pärandi enestele!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 39, 'Ja nad võtsid ja tõukasid ta välja viinamäest ning tapsid ta ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 40, 'Kui nüüd viinamäe isand tuleb, mis ta teeb nende aednikega?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 41, 'Nad ütlesid Temale: \"Need kurjad ta hukkab ära kurjasti ja annab viinamäe teiste aednike kätte, kes annavad temale vilja omal ajal.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 42, 'Jeesus ütles neile: \"Kas te pole iialgi Kirjast lugenud: Kivi, mille hooneehitajad kõrvale heitsid, on saanud nurgakiviks; Issandalt on see tulnud ja on imeasi meie silmis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 43, 'Seepärast Ma ütlen teile: Jumala Riik võetakse teilt ära ja antakse sellele rahvale, kes selle vilja kannab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 44, 'Ja kes selle kivi peale langeb, läheb rusuks; aga kelle peale iganes see langeb, selle ta teeb pihuks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 45, 'Ja kui ülempreestrid ja variserid Tema tähendamissõnu kuulsid, mõistsid nad, et Ta neist rääkis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 46, 'Ja nad oleksid Ta hea meelega kinni võtnud, aga kartsid rahvast, sest see pidas Teda prohvetiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 1, 'Ja Jeesus algas kõnet ja rääkis neile jälle tähendamissõnadega ning ütles:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 2, '\"Taevariik on kuninga sarnane, kes oma pojale pulmad tegi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 3, 'Ja ta läkitas oma sulased kutsutuid pulma kutsuma. Ja need ei tahtnud tulla.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 4, 'ja kõik on valmis, tulge pulma!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 5, 'Aga nad ei hoolinud sellest, vaid läksid ära, kes oma põllule, kes oma kaubale;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 6, 'veel teised võtsid tema sulased kinni, kohtlesid neid ülbesti ja tapsid nad ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 7, 'Aga kuningas vihastus ja läkitas oma sõjaväed välja, hukkas need tapjad ja süütas nende linna põlema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 8, 'Siis ta ütles oma sulastele: Pulmad on küll valmis, aga need, kes olid kutsutud, ei olnud seda väärt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 9, 'Minge nüüd teelahkmetele ja kutsuge pulma, keda te iganes leiate!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 10, 'Ja need sulased läksid välja teedele ja kogusid kokku kõik, keda nad leidsid, kurjad ja head. Ja pulmakoda sai täis lauasistujaid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 11, 'Siis kuningas läks sisse lauavõõraid vaatama ja nägi seal inimest, kellel ei olnud pulmariiet seljas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 12, 'Ja ta ütles temale: Sõber, kuidas sa siia oled sisse tulnud, ilma et sul pulmariiet oleks? Aga too ei saanud sõnagi suust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 13, 'Siis kuningas ütles teenijaile: Siduge tema jalad ja käed ja heitke ta kõige äärmisemasse pimedusse, seal on ulumine ja hammastekiristamine!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 14, 'Sest paljud on kutsutud, kuid vähesed on valitud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 15, 'Siis läksid variserid ja pidasid nõu, kuidas nad Teda Ta kõnest saaksid võrgutada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 16, 'Ja nad läkitavad Tema juure oma jüngrid ühes Heroodese seltsiga ja lasevad ütelda: \"Õpetaja, me teame, et Sa oled tõemeelne ja õpetad Jumala teed tões ega hooli ühestki, sest Sa ei vaata inimese isikule.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 17, 'Ütle nüüd meile, mida Sa arvad: Kas on tarvis anda maksu keisrile või mitte?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 18, 'Aga Jeesus mõistis nende tigedust ja ütles: \"Te silmakirjatsejad, miks te Mind kiusate?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 19, 'Näidake Mulle maksuraha!\" Ja nad tõid Tema kätte ühe teenari.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 20, 'Tema ütles neile: \"Kelle kuju ja pealkiri see on?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 21, 'Nad vastasid Temale: \"Keisri.\" Siis Ta ütles neile: \"Andke siis keisrile, mis kuulub keisrile, ja Jumalale, mis kutsub Jumalale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 22, 'Kui nad seda kuulsid, imestasid nad ja jätsid Tema ning läksid ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 23, 'Sel päeval tuli Tema juure sadusere, kes ütlevad, et ülestõusmist ei olegi, ja küsisid Talt:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 24, '\"Õpetaja, Mooses on ütelnud: Kui keegi sureb lasteta, peab ta vend abielluma ta naisega ja soetama oma vennale järglase.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 25, 'Meie juures oli seitse venda. Esimene abiellus ja suri, ja et tal ei olnud järglast, jättis ta oma naise oma vennale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 26, 'Nõndasamuti ka teine ja kolmas kuni seitsmendani.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 27, 'Kõige viimaks suri ka naine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 28, 'Ent ülestõusmises, kelle naiseks ta jääb nende seitsme seast, sest ta on kõikidel olnud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 29, 'Aga Jeesus kostis ning ütles neile: \"Eksite, sest te ei mõista Kirja ega Jumala väge!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 30, 'Sest ülestõusmises ei võeta naisi ega minda mehele, vaid ollakse kui Jumala Inglid taevas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 31, 'Aga kas te ei ole surnute ülestõusmisest lugenud, mis Jumal teile on ütelnud:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 32, 'Mina olen Aabrahami Jumal ja Iisaki Jumal ja Jaakobi Jumal. Jumal ei ole mitte surnute, vaid on elavate Jumal!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 33, 'Ja kui rahvas seda kuulis, hämmastus ta Tema õpetusest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 34, 'Aga kui variserid said kuulda, et Ta saduseride suu oli sulgenud, tulid nad ühel meelel kokku;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 35, 'ja üks käsutundja nende seast küsis Talt Teda kiusates:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 36, '\"Õpetaja, missugune käsk on suur käsuõpetuses?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 37, 'Aga Jeesus ütles temale: \"Armasta Issandat, oma Jumalat, kõigest oma südamest ja kõigest oma hingest ja kõigest oma meelest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 38, 'See on suur ja esimene käsk.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 39, 'Aga teine on selle sarnane: Armasta oma ligimest nagu iseennast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 40, 'Neis kahes käsus on kogu käsuõpetus ja prohvetid koos!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 41, 'Aga kui variserid üheskoos olid, küsis neilt Jeesus');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 42, 'ning ütles: \"Mis teie arvate Kristusest, kelle Poeg Ta on?\" Nad ütlesid Temale: \"Taaveti!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 43, 'Tema ütles neile: \"Kuidas siis Taavet hüüab Teda vaimus Issandaks, kui ta ütleb:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 44, 'Issand on ütelnud minu Issandale: Istu Mu paremale käele, kuni Ma Sinu vaenlased panen Su jalge alla?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 45, 'Kui nüüd Taavet Teda nimetab Issandaks, kuidas Ta siis on tema poeg?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 46, 'Ja ükski ei võinud Temale sõnagi vastata, ja ükski ei julgenud sellest päevast peale Temalt midagi enam küsida.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 1, 'Siis rääkis Jeesus rahvale ja Oma jüngritele');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 2, 'ning ütles: \"Moosese istmel istuvad kirjatundjad ja variserid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 3, 'Kõike nüüd, mis nad iganes teile ütlevad, seda tehke ja pidage, aga nende tegude järele ärge tehke, sest nad ütlevad küll, aga ei tee;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 4, 'nad seovad kokku raskeid ja ränki koormaid ja panevad neid inimeste õlgadele, aga ise nad ei taha sõrmegagi neid liigutada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 5, 'Kõik oma teod nad teevad selleks, et inimesed neid näeksid; sest nad teevad oma palvekaukad laiad ja oma rüüde tupsud suured.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 6, 'Nad armastavad ülemat paika lauas võõruspidudel ja esimesi istmeid kogudusekodades,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 7, 'ja teretusi turgudel ja et inimesed neid kutsuksid \"Rabi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 8, 'Aga teie ärge laske endid hüüda rabiks, sest üks on teie õpetaja; aga teie kõik olete vennad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 9, 'Ja ärge te kutsuge kedagi maa peal oma isaks, sest üks on teie Isa, Kes on taevas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 10, 'Ja ärge te laske ka endid kutsuda juhatajaks, sest üks on teie Juhataja - Kristus!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 11, 'Aga kes on ülem teie seast, see olgu teie teenija.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 12, 'Sest kes ennast ise ülendab, seda alandatakse, ja kes ennast ise alandab, seda ülendatakse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 13, 'Aga häda teile, kirjatundjad ja variserid, te silmakirjatsejad, et te sulete Taevariigi inimeste eest! Ise te ei lähe sisse ega lase sisse minna neid, kes tahavad sisse minna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 14, 'Häda teile, kirjatundjad ja variserid, te silmakirjatsejad, et te sööte ära leskede hooned ja loete silmakirjaks pikki palveid! Sellepärast te langete seda raskema kohtu alla.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 15, 'Häda teile, kirjatundjad ja variserid, te silmakirjatsejad, et te käite läbi mered ja maad, et saavutada ühegi, kes heidab teie usku; ja kui ta on heitnud, siis te teete tast põrgulapse, kaks korda hullema kui te ise olete!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 16, 'Häda teile, te sõgedad teejuhid, kes ütlete: Kes iganes vannub templi juures, sellel ei ole sest midagi, aga kes vannub templi kulla juures, sellel on kohustus!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 17, 'Te jõledad ja sõgedad! Sest kumb on suurem, kuld või tempel, mis kulda pühitseb?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 18, 'Ja: Kes iganes vannub altari juures, sellel ei ole sest midagi; aga kes vannub annetuse juures, mis on seal peal, sellel on kohustus!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 19, 'Te jõledad ja sõgedad! Sest kumb on suurem, ohver või altar, mis ohvrit pühitseb?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 20, 'Seepärast, kes vannub altari juures, vannub tema juures ja kõige selle juures, mis tema peal on.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 21, 'Ja kes vannub templi juures, see vannub tema ja Selle juures, Kes seal elab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 22, 'Ja kes vannub taeva juures, see vannub Jumala aujärje juures ja Tema juures, Kes sellel istub.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 23, 'Häda teile, kirjatundjad ja variserid, te silmakirjatsejad, et te maksate kümnist mündist ja tillist ja köömnest ja jätate kõrvale, mis on tähtsaim käsuõpetuses, õigluse ja halastuse ja ustavuse! Seda tuleks teha, aga teist mitte jätta tegemata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 24, 'Te sõgedad teejuhid, kes kurnate sääski, aga neelate alla kaameleid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 25, 'Häda teile, kirjatundjad ja variserid, te silmakirjatsejad, et te karika ja vaagna teete puhtaks väljastpoolt, aga seestpoolt on need täis röövi ja aplust!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 26, 'Sa sõge variser! Tee esmalt karikas puhtaks seestpoolt, et see ka väljastpoolt saaks puhtaks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 27, 'Häda teile, kirjatundjad ja variserid, te silmakirjatsejad, et te olete lubjatud haudade sarnased, mis kull väljastpoolt on nägusad, aga seestpoolt on täis surnute luid ja kõike räpasust!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 28, 'Nõnda olete ka teie küll väljastpoolt näha õiged inimeste ees, kuid seestpoolt te olete täis salalikku meelt ja ülekohut.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 29, 'Häda teile, kirjatundjad ja variserid, te silmakirjatsejad, et te ehitate prohvetite haudu ja kaunistate õigete hauasambaid');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 30, 'ja ütlete: Kui me oleksime elanud oma esiisade ajal, ei meie küll nende osalised oleks olnud prohvetite verd valamas!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 31, 'Nõnda te siis tunnistate iseeneste vastu, et te olete nende lapsed, kes tapsid prohveteid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 32, 'Et täitke siis ka oma vanemate mõõt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 33, 'Te maod, te rästikute sigitis, kuidas te põgenete põrgu hukatuse eest?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 34, 'Seepärast, vaata, Ma läkitan teie juure prohveteid ja tarku ja kirjatundjaid! Ja muist neist te tapate ja lööte risti, ja muist neist te piitsutate oma kogudusekodades ja kiusate neid taga ühest linnast teise,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 35, 'et teie peale tuleks kõik vaga veri, mis on valatud maa peal alates õige Aabeli verest Sakarja, Berekja poja vereni, kelle te tapsite templi ja altari vahel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 36, 'Tõesti Ma ütlen teile, see kõik tuleb sellesinase sugupõlve peale!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 37, 'Jeruusalem, Jeruusalem, kes tapad prohvetid ja viskad kividega surnuks need, kes sinu juure on läkitatud! Kui mitu korda Ma olen tahtnud su lapsi koguda, otsegu kana kogub oma pojukesi tiibade alla, ja teie ei ole tahtnud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 38, 'Vaata, teie koda jäetakse teil maha!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 39, 'Sest Mina ütlen teile: Nüüdsest peale ei saa te Mind näha, seni kui te ütlete: Õnnistatud olgu, kes tuleb Issanda nimel!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 1, 'Ja Jeesus tuli välja pühakojast ja läks edasi. Ja Tema jüngrid astusid Ta juure Temale näitama pühakoja hooneid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 2, 'Aga Jeesus ütles neile: \"Eks te näe seda kõike? Tõesti Ma ütlen teile, siia ei jäeta kivi kivi peale, mida maha ei kistaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 3, 'Ja kui Ta Õlimäel istus, astusid jüngrid Tema juure isepäinis ja ütlesid: \"Ütle meile, millal see kõik sünnib ja mis on Su tulemise ja maailma-ajastu lõpetuse tunnus?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 4, 'Aga Jeesus kostis ning ütles neile: \"Katsuge, et keegi teid ei eksita!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 5, 'Sest paljud tulevad Minu nime all ja ütlevad: Mina olen Kristus! ja eksitavad paljusid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 6, 'Aga te saate kuulda sõdadest ja sõnumeid sõjast; katsuge, et te ei ehmuks! Sest see peab sündima, aga ots ei ole veel käes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 7, 'Sest rahvas tõuseb rahva vastu ja kuningriik kuningriigi vastu, ja nälga ja maavärisemisi on paiguti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 8, 'Aga see kõik on sünnivalude hakatus!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 9, 'Siis antakse teid viletsusse ja teid tapetakse ja te olete kõigi rahvaste all Minu nime pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 10, 'Ja siis taganevad paljud ja annavad üksteist ära ja vihkavad üksteist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 11, 'Ja palju valeprohveteid tõuseb, ja need eksitavad paljusid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 12, 'Ja et ülekohus läheb väga võimsaks, jaheneb paljude armastus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 13, 'Aga kes otsani vastu peab, see pääseb!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 14, 'Ja seda Kuningriigi Evangeeliumi peab kuulutatama kogu maailmas tunnistuseks kõigile rahvaile, ja siis tuleb ots.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 15, 'Kui te siis näete pühas paigas seisvat hävituse koletist, millest on rääkinud prohvet Taaniel, - kes seda loeb, see pangu tähele -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 16, 'siis põgenegu need, kes on Juudamaal, mägedele;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 17, 'kes on katusel, ärgu tulgu maha midagi oma majast võtma;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 18, 'ja kes on väljal, ärgu mingu tagasi võtma oma kuube!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 19, 'Aga häda neile, kes on käima peal, ja neile, kes imetavad neil päevil!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 20, 'Ent paluge, et teie põgenemine ei juhtuks talvel ega hingamispäeval.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 21, 'Sest siis tuleb suur viletsus, mille sarnast ei ole olnud maailma algusest kuni praeguse ajani ega tulegi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 22, 'Ja kui neid päevi ei lühendataks, ei pääseks mitte ükski liha; aga äravalitute pärast lühendatakse need päevad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 23, 'Kui siis keegi teile ütleb: Vaata, siin on Kristus! või: Vaata seal! ärge uskuge.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 24, 'Sest valekristusi ja valeprohveteid tõuseb ja need teevad suuri tunnustähti ja imesid, et eksitada, kui võimalik, ka äravalituid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 25, 'Vaata, Ma olen teile seda ette ütelnud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 26, 'Kui teile siis üteldakse: Vaata, Ta on kõrves! ärge minge välja; Vaata, Ta on kambrites! ärge uskuge.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 27, 'Sest otsegu välk sähvab ida poolt ja paistab läände, nõnda peab olema Inimese Poja tulemine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 28, 'Sest kus on raibe, sinna kogunevad kotkad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 29, 'Aga varsti pärast nende päevade viletsust läheb päike pimedaks ja kuu ei anna oma valget, ja tähed langevad maha taevast ja taeva vägesid kõigutatakse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 30, 'Ja siis ilmub Inimese Poja tunnustäht taevas, ja siis hakkavad kõik rahva suguvõsad maa peal halisema ja näevad Inimese Poja tulevat taeva pilvede peal suure väe ja auhiilgusega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 31, 'Ja Ta läkitab Oma Inglid suure pasunahäälega, ja nad koguvad kokku ta äravalitud neljast tuulest, ühest taeva otsast teise.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 32, 'Ent viigipuust õppige võrdumit: kui ta oksad juba on pungas ja ajavad lehti, siis te tunnete, et suvi on ligi. 33 Nõnda ka teie: kui te näete seda kõike, siis teadke, et see on ligi ukse ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 33, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 34, 'Tõesti Ma ütlen teile, et selle põlve rahvas ei lõpe ära, enne kui see kõik sünnib!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 35, 'Taevas ja maa hävivad, aga Minu sõnad ei hävi mitte!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 36, 'Aga sellest päevast ja tunnist ei tea ükski, ei Inglidki taevas ega ka Poeg, muud kui Isa üksi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 37, 'Sest nõnda nagu Noa päevad olid, nõnda peab olema Inimese Poja tulemine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 38, 'Sest nõnda nagu inimesed olid noil päevil enne veeuputust: sõid ja jõid, võtsid naisi ja läksid mehele selle päevani, mil Noi läks laeva,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 39, 'ega saanud aru, enne kui tuli veeuputus ja võttis nad puha ära: nõnda on ka Inimese Poja tulemine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 40, 'Siis on käks põllul: üks võetakse vastu ja teine jäetakse maha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 41, 'Kaks naist on jahvatamas veskil: üks võetakse vastu ja teine jäetakse maha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 42, 'Siis valvake, sest te ei tea, mil päeval teie Issand tuleb!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 43, 'Aga seda teadke, et kui peremees teaks, mil öövahi-ajal varas tuleb, küll ta siis valvaks ega laseks oma majasse sisse murda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 44, 'Sellepärast olge teiegi valmis, sest Inimese Poeg tuleb tunnil, mil te ei arva!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 45, 'Kes on nüüd ustav ja mõistlik sulane, kelle ta isand on pannud oma pere üle neile rooga andma õigel ajal?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 46, 'Õnnis on see sulane, keda ta isand tulles leiab nõnda tegevat!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 47, 'Tõesti Ma ütlen teile, et ta paneb tema üle kogu oma vara!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 48, 'Aga kui see halb sulane mõtleb oma südames: Mu isand viibib tulles!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 49, 'ja hakkab lööma oma kaassulaseid, joomaritega sööma ja jooma,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 50, 'siis selle sulase isand tuleb päeval, mil ta teda ei oota, ja tunnil, mil ta ei tea arvata,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 51, 'ja raiub ta pooleks ja annab temale osa silmakirjatsejatega; seal on ulumine ja sammaste kiristamine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 1, 'on Taevariik kümne neitsi samane, kes võtsid oma lambid ja läksid välja peigmehele vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 2, 'Viis neist olid rumalad ja viis mõistlikud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 3, 'Rumalad võtsid küll lambid, aga ei võtnud õli enesega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 4, 'Aga mõistlikud võtsid õli astjatesse ühes oma lampidega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 5, 'Kui peigmees viibis, jäid nad kõik uniseks ja uinusid magama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 6, 'Aga keskööl kuuldi häält: Ennäe, peigmees! Minge vastu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 7, 'Siis tõusid kõik need neitsid üles ja valmistasid oma lambid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 8, 'Aga rumalad ütlesid mõistlikele: Andke meile oma õlist, sest meie lambid kustuvad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 9, 'Kuid mõistlikud kostsid ja ütlesid: Ei ilmaski, sellest ei jätku meile ja teile; vaid minge pigemini kaupmeeste juure ja ostke endile!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 10, 'Aga kui nad läksid ostma, tuli peigmees, ja kes olid valmis, läksid temaga pulma. Ja uks suleti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 11, 'Pärast tulid ka teised neitsid ja ütlesid: Issand, Issand, ava meile!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 12, 'Aga tema vastas ning ütles: Tõesti ma ütlen teile, ma ei tunne teid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 13, 'Seepärast valvake, sest te ei tea päeva ega tundi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 14, 'Sest see on nõnda, nagu üks inimene võõrale maale minnes kutsus oma sulased ja usaldas nende kätte oma vara.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 15, 'Ühele ta andis viis talenti, teisele kaks ja kolmandale ühe, igaühele tema jõudu mööda, ja läks võõrale maale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 16, 'Kes viis talenti oli saanud, läks kohe ja kauples nendega ning saavutas teist viis lisaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 17, 'Nõndasamuti, kes oli saanud kaks, saavutas teist käks lisaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 18, 'Aga see, kes oli saanud ühe, läks ja kaevas selle maa sisse ja peitis ära oma isanda raha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 19, 'Pika aja pärast tuli nende sulaste isand tagasi ja hakkas nendega aru pidama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 20, 'Siis astus esile see, kes oli saanud viis talenti, ja lisas teist viis talenti juure ja ütles: Isand, viis talenti sa usaldasid mu kätte; vaata, ma olen nendega saavutanud teist viis!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 21, 'Tema isand ütles talle: See on hea, sa hea ja ustav sulane! Sa oled pisku üle ustav olnud, ma panen sind palju üle; mine oma Issanda rõõmusse!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 22, 'Esile astus ka see, kes oli saanud kaks talenti, ja ütles: Isand, sa usaldasid mu kätte kaks talenti; vaata, ma olen nendega saavutanud teist kaks talenti!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 23, 'Tema isand ütles talle: See on hea, sa hea ja ustav sulane! Sa oled pisku üle ustav olnud, ma panen sind palju üle; mine oma Issanda rõõmusse!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 24, 'Siis astus esile ka see, kes oli saanud ühe talendi, a ütles: Isand, ma teadsin, et sa oled kalk inimene. Sa lõikad, kuhu sa ei ole külvanud, ja kogud sealt, kuhu sa ei ole puistanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 25, 'Ja ma kartsin ning läksin ja matsin su talendi maha. Vaata, siin on su oma!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 26, 'Aga tema isand kostis ning ütles temale: Sa paha ja laisk sulane! Sa teadsid, et ma lõikan, kuhu ma ei ole külvanud, ja kogun sealt, kuhu ma ei ole puistanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 27, 'Sa oleksid siis pidanud mu raha andma rahavahetajate kätte, ja tulles ma oleksin saanud enese oma kasuga tagasi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 28, 'Sellepärast võtke talent ära tema käest ja andke see sellele, kellel on kümme talenti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 29, 'Sest igaühele, kellel on, antakse, ja temal peab olema küllalt, aga kellel ei ole, selle käest võetakse ära ka see, mis tal on.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 30, 'Ja kõlvatu sulane heitke välja kõige äärmisemasse pimedusse; seal on ulumine ja hammaste kiristamine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 31, 'Aga kui Inimese Poeg tuleb Oma auhiilguses ja kõik Inglid Temaga, siis Ta istub Oma aujärjele;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 32, 'ja siis kogutakse Tema ette kõik rahvad, ja Ta eraldab nad üksteisest, nagu karjane eraldab lambad sikkudest. 33 Ja Ta asetab lambad Oma paremale käele, aga sikud vasakule käele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 33, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 34, 'Siis Kuningas ütleb neile, kes on Ta paremal käel: Tulge siia, Minu Isa õnnistatud, pärige kuningriik, mis teile on valmistatud maailma asutamisest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 35, 'Sest Mul oli nälg, ja te andsite Mulle süüa; Mul oli janu, ja te jootsite Mind; Ma olin võõras, ja te võtsite Mind vastu;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 36, 'Ma olin alasti, ja te riietasite Mind; Ma olin haige, ja te tulite Mind vaatama; Ma olin vangis, ja te tulite Mu juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 37, 'Siis vastavad õiged Temale: Issand, millal me nägime Sind näljasena ja söötsime Sind, või millal janusena ja jootsime Sind?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 38, 'Millal me nägime Sind võõrana ja võtsime Sind vastu, või alasti ja riietasime Sind?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 39, 'Millal me nägime Sind haigena või vangis ja tulime Su juure?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 40, 'Siis vastab Kuningas ja ütleb neile: Tõesti Ma ütlen teile, et mida te iganes olete teinud ühele nende Mu vähemate vendade seast, seda te olete Minule teinud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 41, 'siis Ta ütleb ka neile, kes on vasakul käel: Minge ära Minu juurest, te neetud, igavesse tulle, mis on valmistatud kuradile ja tema inglitele!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 42, 'Sest Mul oli nälg, ja te ei annud Mulle süüa; Mul oli janu, ja te ei jootnud Mind;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 43, 'Ma olin võõras, ja te ei võtnud Mind vastu; Ma olin alasti, a te ei riietanud Mind; Ma olin haige ja vang, ja te ei tulnud Mind vaatama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 44, 'Siis vastavad ka need Temale: Issand, millal me nägime Sind näljas või janus või võõrana või alasti või haigena või vangis, ja ei ole Sind teeninud?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 45, 'Siis Ta vastab neile nõnda: Tõesti Ma ütlen teile, et mida te iganes ei ole teinud ühele nende vähemate seast, seda te ei ole Minulegi teinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 46, 'Ja need lähevad igavesse karistusse, aga õiged igavesse elusse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 1, 'Kui Jeesus kõik need kõned oli lõpetanud, ütles Ta Oma jüngritele:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 2, '\"Te teate, et kahe päeva pärast on paasapüha; siis antakse Inimese Poeg risti lüüa!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 3, 'Siis tulid kokku ülempreestrid ja rahva vanemad ülempreestri kotta, kelle nimi oli Kaifas,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 4, 'ja pidasid isekeskis nõu, Jeesus kavalusega kinni võtta ja ära tappa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 5, 'Aga nad ütlesid: \"Mitte pühade ajal, et mässu ei tõuseks rahva seas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 6, 'Aga kui Jeesus oli Betaanias pidalitõbise Siimona kojas,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 7, 'tuli Ta juure naine, kel oli ühes alabasterriist väga kalli salviga, ja valas selle Tema pea peale, kui Ta lauas istus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 8, 'Aga kui jüngrid seda nägid, sai nende meel pahaseks ja nad ütlesid: \"Mistarvis on see raiskamine?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 9, 'Sest selle oleks võinud ära müüa hulga raha eest ja anda vaestele.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 10, 'Aga kui Jeesus seda märkas, ütles Ta neile: \"Miks teete vaeva sellele naisele? Ta on ju Mulle heateo teinud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 11, 'Sest vaeseid on alati teie juures, Mind aga ei ole teil mitte alati.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 12, 'Sest salvi Minu ihu peale valades tegi Ta seda Minu matmiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 13, 'Tõesti Ma ütlen teile, et kus iganes seda Evangeeliumi kuulutatakse kogu maailmas, seal räägitakse tema mälestuseks ka sellest, mis ta on teinud.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 14, 'Siis läks üks neist kaheteistkümnest, nimega Juudas Iskariot, ülempreestrite juure');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 15, 'ning ütles: \"Mis te mulle tahate anda, kui ma Ta annan teie kätte?\" Nad pakkusid temale kolmkümmend hõbetükki.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 16, 'Ja sellest ajast ta otsis parajat aega Teda ära anda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 17, 'Aga esimesel hapnemata leibade päeval tulid jüngrid Jeesuse juure ja ütlesid Temale: \"Kus Sa tahad, et me Sulle valmistame paasasöömaaja?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 18, 'Tema ütles: \"Minge linna ühe mehe juure ja ütelge talle: Õpetaja ütleb: Mu aeg on ligi, sinu juures Ma pean paasasöömaaja Oma jüngritega.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 19, 'Ja jüngrid tegid nõnda, kuidas Jeesus neid oli käskinud, ja valmistasid paasatalle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 20, 'Aga kui õhtu tuli, istus Ta lauda Oma kaheteistkümne jüngriga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 21, 'Ja kui nemad sõid, ütles Ta: \"Tõesti Ma ütlen teile, üks teie seast annab Mind ära!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 22, 'Ja nad said väga kurvaks ning hakkasid üksteise järele Temale ütlema: \"Ega ometi mina see ole, Issand?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 23, 'Tema vastas ning ütles: \"Kes Minuga oma käe vaagnasse pistab, see annab Mind ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 24, 'Inimese Poeg läheb küll ära, nõnda nagu Temast on kirjutatud; aga häda sellele inimesele, kelle läbi Inimese Poeg ära antakse! Hea oleks sellele inimesele, kui ta ei oleks sündinud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 25, 'Siis kostis Juudas, Ta äraandja, ning ütles: \"Ega ometi mina see ole, Rabi?\" Tema ütles talle: \"Sina jah!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 26, 'Ja kui nad sõid, võttis Jeesus leiva, õnnistas ja murdis ja andis Oma jüngritele ning ütles: \"Võtke, sööge, see on Minu Ihu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 27, 'Ja Ta võttis karika, tänas ja andis neile ning ütles: \"Jooge kõik selle seest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 28, 'Sest see on Minu Veri, uue lepingu Veri, mis paljude eest valatakse pattude andeksandmiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 29, 'Aga Ma ütlen teile: Nüüdsest peale Ma ei joo enam viinapuu viljast kuni selle päevani, mil Ma ühes teiega joon uut Oma Isa Riigis!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 30, 'Ja kui nad kiituslaulu olid laulnud, läksid nad välja Õlimäele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 31, 'Siis ütleb Jeesus neile: \"Sel ööl te kõik taganete minust; sest kirjutatud on: Ma löön karjast ja karja lambad pillutatakse!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 32, 'Aga pärast Oma ülestõusmist Ma lähen teie eele Galileasse.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 33, 'Siis Peetrus kostis ning ütles Temale: \"Kui ka kõik Sinust taganevad, ei tagane mina mitte ilmaski!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 34, 'Jeesus ütles talle: \"Tõesti Ma ütlen sulle, täna öösel, enne kui kukk on laulnud, salgad sa Mind kolm korda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 35, 'Peetrus ütleb Temale: \"Kui ma Sinuga peaksin ka surema, ei salga ma Sind mitte!\" Samuti ütlesid ka kõik jüngrid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 36, 'Siis tuli Jeesus nendega paika, mida nimetatakse Ketsemaniks, ja ütles Oma jüngritele: \"Istuge siin niikaua, kui Ma lähen sinna ja palvetan!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 37, 'Ja Ta võttis Enese juure Peetruse ja kaks Sebedeuse poega, ja hakkas kurvaks minema ning ahastust tundma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 38, 'Siis Ta ütles neile: \"Minu hing on väga kurb surmani; jääge siia ja valvake Minuga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 39, 'Ja Ta läks pisut eemale, heitis silmili maha, palvetas ning ütles: \"Minu Isa, kui on võimalik, siis mingu see karikas Minust mööda! Ometi mitte nõnda, kuidas Mina tahan, vaid kuidas Sina tahad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 40, 'Ja Ta tuli Oma jüngrite juure ja leidis nad magamast ja ütles Peetrusele: \"Nii te siia ei suutnud ühtki tundi minuga valvata?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 41, 'Valvake ja paluge, et te kiusatusse ei satuks! Vaim on küll valmis, aga liha on nõder!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 42, 'Taas läks Ta teist korda ära, palvetas ning ütles: \"Minu Isa, kui see ei või muidu mööda minna kui et Ma selle joon, siis sündigu Sinu tahtmine!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 43, 'Ja Ta tuli jälle ja leidis nad magamast, sest nende silmad olid rasked unest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 44, 'Ja Ta jättis nad ja läks ära ja palvetas kolmat korda ja ütles jälle needsamad sõnad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 45, 'Siis tuleb Ta Oma jüngrite juure ja ütleb neile: \"Te ikka veel magate ja puhkate! Vaata, tund on ligi ja Inimese Poeg antakse patuste kätte!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 46, 'Tõuske üles, lähme! Vaata, see on ligidal, kes Mind ära annab!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 47, 'Ja kui Ta alles rääkis, vaata, siis tuli Juudas, üks neist kaheteistkümnest, ja temaga ühes suur jõuk mõõkade ja nuiadega ülempreestrite ja rahva vanemate poolt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 48, 'Aga Ta äraandja oli neile annud märgu ja ütelnud: \"Keda ma suudlen, see Ta on, Tema võtke kinni!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 49, 'Ja kohe ta astus Jeesuse juure ja ütles: \"Tere, Rabi!\" ja andis Temale suud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 50, 'Aga Jeesus ütles talle: \"Sõber, mispärast oled sa siin?\" Siis nad tulid Tema juure, pistsid oma käed Tema külge ja võtsid Ta kinni.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 51, 'Ja vaata, üks neist, kes olid Jeesusega, sirutas oma käe ja tõmbas oma mõõga välja ja lõi ülempreestri sulast ning raius ta kõrva ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 52, 'Siis ütles Jeesus temale: \"Pista oma mõõk tuppe tagasi, sest kõik, kes mõõga tõmbavad, saavad mõõga läbi hukka!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 53, 'Või arvad sa, et Ma ei või Oma Isa paluda, ja Ta läkitab Mulle kohe enam kui kaksteistkümmend leegioni Ingleid?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 54, 'Kuidas siis läheksid kirjad täide, et see nõnda peab sündima?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 55, 'Sel tunnil ütles Jeesus hulgale: \"Otsegu röövli peale olete te välja tulnud mõõkade ja nuiadega Mind kinni võtma! Ma olen iga päev istunud teie juures pühakojas ja õpetanud, ja te ei ole Mind kinni võtnud.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 56, 'Aga see kõik on sündinud, et läheksid täide prohvetite kirjad. Siis jätsid kõik jüngrid Tema maha ja põgenesid ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 57, 'Aga need, kes Jeesuse olid kinni võtnud, viisid Ta ülempreester Kaifas juure, kuhu kirjatundjad ja vanemad olid kokku tulnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 58, 'Aga Peetrus järgis Teda eemalt ülempreestri kojani, ja ta läks sisse ja istus maha ühes sulastega, et näha, kuidas asi lõpeb.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 59, 'Aga ülempreestrid ja kõik Suurkohus otsisid valetunnistust Jeesuse vastu, et Teda surmata,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 60, 'ega leidnud ühtki, ehk küll palju valetunnistajaid esile tuli. Viimaks ometi astus esile kaks');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 61, 'ning need ütlesid: \"Tema on ütelnud: Ma võin Jumala templi lammutada ja kolme päevaga üles ehitada!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 62, 'Siis tõusis ülempreester üles ja ütles Temale: \"Kas Sa midagi ei vasta selle peale, mida need tunnistavad Sinu kohta?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 63, 'Sa meile ütled, kas Sina oled Kristus, Jumala Poeg?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 64, 'Jeesus ütleb temale \"Jah, olen! Ometi Ma ütlen teile: Sellest ajast te näete Inimese Poega istuvat Jumala väe paremal pool ja tulevat taeva pilvede peal!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 65, 'Siis ülempreester käristas oma riided lõhki ja ütles: \"Tema on Jumalat pilganud! Milleks meile veel on tunnistajaid vaja? Vaata, nüüd te olete Tema jumalapilget kuulnud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 66, 'Mis te arvate?\" Ent nemad vastasid ning ütlesid: \"Tema on surma väärt!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 67, 'Siis nad sülitasid temale silmi ja lõid Teda rusikatega, aga mõningad peksid Teda kepiga');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 68, 'ja ütlesid: \"Ütle, Kristus, meile kui prohvet, kes see on, kes Sind lõi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 69, 'Aga Peetrus istus väljas sisemises õues. Ja tema juure tuli üks tüdruk ja ütles: \"Ka sina olid ühes selle galilealase Jeesusega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 70, 'Kuid Tema salgas kõikide ees ning ütles: \"Ei mina saa aru, mida sa räägid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 71, 'Ja kui ta läks värava poole, nägi teda teine tüdruk ja ütles neile, kes seal olid: \"Ka see oli naatsaretlase Jeesusega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 72, 'Tema salgas jälle vandega: \"Mina ei tunne Seda Inimest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 73, 'Aga natukese aja pärast lähenesid need, kes seal seisid, ja ütlesid Peetrusele: \"Tõesti, sinagi oled üks nende seast, sest su keelemurregi näitab, kust sa oled!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 74, 'Siis ta hakkas väga needma ja vanduma: \"Ei mina tunne Seda Inimest!\" Ja sedamaid laulis kukk.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 75, 'Siis tuli Peetrusele meele Jeesuse sõna, kes talle oli ütelnud: \"Enne kui kukk laulab, salgad sa Mind kolm korda!\" Ja ta läks välja ja nuttis kibedasti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 1, 'Aga kui valgeks läks, tegid kõik ülempreestrid ja rahvavanemad Jeesuse kohta otsuseks Tema ära tappa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 2, 'Nad sidusid Ta kinni, viisid Ta ära ja andsid Ta maavalitseja Pilaatuse kätte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 3, 'Siis Juudas, kes oli Ta ära annud, nähes, et Ta oli hukka mõistetud, kahetses ning tõi need kolmkümmend hõbetükki tagasi ülempreestrite ja vanemate kätte');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 4, 'ning ütles: \"Ma olen pattu teinud, et ma ära andsin süütuma vere!\" Aga nemad ütlesid: \"Mis see meisse puutub? Vaata ise!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 5, 'Ja ta viskas hõbetükid maha templisse ja läks kõrvale ja poos enese.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 6, 'Aga ülempreestrid võtsid hõbetükid ja ütlesid: \"Neid ei sünni panna ohvrikirstu, sest see on vere hind!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 7, 'Kuld nad pidasid nõu ning ostsid nende eest potissepa põllu matmispaigaks muulastele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 8, 'Selle tõttu hüütakse seda põldu verepõlluks tänapäevani.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 9, 'Siis läks täide, mis on üteldud prohvet Jeremija kaudu: \"Ja ma võtsin need kolmkümmend hõbetükki Iisraeli lastelt, selle hinnatud Mehe hinna, Kelle nad olid hinnanud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 10, 'ja andsin need potissepa põllu eest, nõnda nagu Issand mind oli käskinud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 11, 'Aga Jeesus seati maavalitseja ette. Ja maavalitseja küsis Temalt ning ütles: \"Oled Sa Juutide Kuningas?\" Aga Jeesus ütles: \"Jah, olen!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 12, 'Kui siis ülempreestrid ja vanemad Tema peale kaebasid, ei vastanud Ta midagi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 13, 'Siis ütles Pilaatus Temale: \"Eks Sa kuule, mida kõike nad tunnistavad Sinu vastu?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 14, 'Ent Tema ei vastanud talle ühtainustki sõna, nõnda et maavalitseja pani seda väga imeks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 15, 'Aga tavaliselt laskis maavalitseja pühiks rahvale vabaks ühe vangi, keda nad tahtsid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 16, 'Ja neil oli siis kuulus vang, nimega Barabas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 17, 'Kui nad nüüd koos olid, ütles Pilaatus neile: \"Kumma te tahate, et ma teile vabaks lasen, Barabase või Jeesuse, keda hüütakse Kristuseks?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 18, 'Sest ta teadis, et nad olid Ta annud kadeduse pärast tema kätte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 19, 'Aga kui maavalitseja istus kohtujärjel, läkitas tema naine ta juure ütlema: \"Ärgu sul olgu ühtki tegemist Selle Õigega; sest Ma olen täna unes palju kannatanud Tema pärast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 20, 'Aga ülempreestrid ja vanemad meelitasid rahvahulki lahti paluma Barabast, aga hukka mõistma Jeesust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 21, 'Ja maavalitseja kostis ning ütles neile: \"Kumma neist kahest te tahate, et ma teile vabaks lasen?\" Nemad ütlesid: \"Barabase!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 22, 'Pilaatus ütles neile: \"Mis ma siis pean tegema Jeesusega, Keda hüütakse Kristuseks?\" Nemad ütlesid kõik: \"Löödagu Ta risti!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 23, 'Maavalitseja küsis: \"Mis kurja Ta siis on teinud?\" Aga nad karjusid veel enam ja ütlesid: \"Löödagu Ta risti!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 24, 'Kui Pilaatus nägi, et ta midagi ei võinud parata, vaid et kära läks veel suuremaks, võttis ta vett ja pesi oma käsi rahva nähes ning ütles: \"Ma olen süüta selle verest! Küll te näete!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 25, 'Siis vastas kõik rahvas ning ütles: \"Tema veri tulgu meie peale ja meie laste peale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 26, 'Siis ta vabastas neile Barabase, aga Jeesust ta laskis rooskadega peksta ja andis Ta risti lüüa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 27, 'Siis võtsid maavalitseja sõjamehed Jeesuse enestega kohtukotta ja kogusid Tema juure kokku kõik oma jõugu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 28, 'Nad võtsid Ta riidest lahti ja panid Temale selga purpurmantli,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 29, 'ja punusid kibuvitsust krooni ning panid selle Temale pähe, andsid Ta paremasse kätte pilliroo, heitsid Ta ette põlvili maha, naersid Teda ja ütlesid: \"Tere, Juutide Kuningas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 30, 'Ja nad sülitasid Tema peale ja võtsid roo ning lõid sellega Temale pähe.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 31, 'Ja kui nemad Teda olid naernud, võtsid nad mantli Ta seljast ära ja panid Ta omad riided Temale selga ja viisid Ta ära risti lüüa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 32, 'Välja minnes leidsid nad Küreene mehe, nimega Siimon. Seda nad sundisid kandma Tema risti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 33, 'Ja kui nad jõudsid paika, mida hüütakse Kolgataks - see tähendab Pealae asemeks -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 34, 'andsid nad Temale juua sapiga segatud viina. Kui Ta seda oli maitsnud, ei tahtnud Ta juua.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 35, 'Aga kui nad Tema olid risti löönud, jaotasid nad Tema riided isekeskis ja heitsid liisku nende pärast');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 36, 'ning istusid maha ja valvasid Teda seal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 37, 'Ja Tema pea kohale nad panid pealkirja, millele oli kirjutatud Ta süü: \"See on Jeesus, Juutide Kuningas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 38, 'Siis löödi Temaga ühes risti käks röövlit, üks paremale ja teine vasakule poole.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 39, 'Aga möödaminejad pilkasid Teda, vangutasid pead');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 40, 'ning ütlesid: \"Sina, Kes templi lammutad ja kolme päevaga üles ehitad, aita Iseennast! Kui Sa oled Jumala Poeg, siis astu ristilt maha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 41, 'Nõndasamuti naersid Teda ka ülempreestrid ühes kirjatundjate ja vanematega ning ütlesid:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 42, '\"Teisi Ta on aidanud, Iseennast Ta ei või aidata; kui Ta on Iisraeli Kuningas, astugu Ta nüüd ristilt maha, siis me usume Temasse!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 43, 'Ta on lootnud Jumala peale, see päästku nüüd Tema, kui Ta tahab; sest Ta on ütelnud: Ma olen Jumala Poeg!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 44, 'Samuti teotasid Teda röövlidki, kes ühes Temaga olid risti löödud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 45, 'Aga kuuendal tunnil tuli pimedus üle kogu maa üheksanda tunnini.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 46, 'Üheksandal tunnil kisendas Jeesus suure häälega ning ütles: \"Elii! Elii! Lemaa sabahtaani?\" see on: \"Mu Jumal! Mu Jumal! Miks Sa Mind oled maha jätnud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 47, 'Aga kui mõningad neist, kes seal seisid, seda kuulsid, ütlesid nad: \"Tema kutsub Eelijat!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 48, 'Ja varsti jooksis üks nende seast, võttis käsna, täitis selle äädikaga, pistis pilliroo otsa ja jootis Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 49, 'Aga teised ütlesid: \"Oota, saab näha, kas Eelija tuleb Teda aitama?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 50, 'Aga Jeesus kisendas jälle suure häälega ja heitis hinge.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 51, 'Ja vaata, templi eesriie kärises lõhki kaheks, ülemast otsast alamast saadik! Ja maa värises ja kaljud lõhkesid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 52, 'ja hauad läksid lahti, ja tõusis üles palju pühade ihusid, kes olid maganud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 53, 'ja tulid välja haudadest ja läksid pärast Tema ülestõusmist pühasse linna ja ilmusid paljudele!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 54, 'Aga kui pealik ja need, kes temaga ühes Jeesust valvasid, nägid maa värisevat ja seda, mis sündis, lõid nad väga kartma ja ütlesid: \"Tõesti, See oli Jumala Poeg!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 55, 'Ja seal oli palju naisi kaugelt vaatamas, kes Jeesust järgisid Galileast, Teda teenides;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 56, 'nende seas oli Maarja Magdaleena ja Maarja, Jakoobuse ja Joosese ema, ja Sebedeuse poegade ema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 57, 'Aga kui õhtu kätte jõudis, tuli rikas mees Arimaatiast, Joosep niini, kes ka oli Jeesuse jünger.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 58, 'See läks Pilaatuse juure ja palus Jeesuse ihu. Siis Pilaatus käskis selle anda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 59, 'Ja Joosep võttis ihu ja mähkis selle puhtasse lõuendisse');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 60, 'ja pani Tema oma uude hauda, mille ta oli lasknud kalju sisse raiuda; ja ta veeretas suure kivi haua uksele ja läks ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 61, 'Aga Maarja Magdaleena ja teine Maarja olid seal istumas haua kohal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 62, 'Teisel päeval, mis järgneb valmistuspäevale, tulid ülempreestrid ja variserid kokku Pilaatuse juure');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 63, 'ning ütlesid: \"Isand, meile meenub, et see petis veel elus olles ütles: Ma tõusen kolme päeva pärast üles!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 64, 'Käsi nüüd hauda valve all pidada kolmanda päevani, et jüngrid ei tuleks ja Teda ära ei varastaks ega ütleks rahvale: Ta on surnuist üles tõusnud! Nii osutuks viimne pettus pahemaks kui esimene!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 65, 'Pilaatus ütles neile: \"Siin on teil valvesalk! Minge ja pidage valvet nii hästi kui oskate!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 66, 'Nemad läksid ja võtsid haua valve alla, pannes kivi pitseriga kinni ja seades valvurid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 1, 'Kui hingamispäev oli möödunud ja hakkas koitma nädala esimese päeva hommikul, tulid Maarja Magdaleena ja teine Maarja hauda vaatama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 2, 'Ja vaata, suur maavärisemine sündis, sest Issanda Ingel astus taevast alla, tuli ja veeretas kivi kõrvale ja istus selle peale!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 3, 'Ta nägu oli otsegu välk ja ta riided valged nagu lumi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 4, 'Aga hirmu pärast tema ees värisesid valvajad ja jäid otsegu surnuks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 5, 'Ent Ingel hakkas kõnelema ning ütles naistele: \"Ärge kartke, sest ma tean, et te otsite Jeesust, Kes oli risti löödud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 6, 'Teda ei ole siin, sest Ta on üles tõusnud, nõnda nagu Ta ütles. Tulge siia, vaadake aset, kus Ta on maganud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 7, 'ja minge usinasti ütlema Tema jüngritele, et Ta on surnuist üles tõusnud. Ja vaata, Ta läheb teie eele Galileasse; seal te saate Teda näha. Vaata, ma olen teile seda ütelnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 8, 'Ja nad läksid rutusti haua juurest minema kartuse ja suure rõõmuga ning jooksid seda kuulutama Tema jüngritele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 9, 'Ja vaata, Jeesus tuli neile vastu ja ütles: \"Tere!\" Ja nad tulid Ta juure ja hakkasid Tema jalgade ümbert kinni ja kummardasid Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 10, 'Siis Jeesus ütles neile: \"Ärge kartke! Minge ning teatage Mu vendadele, et nad läheksid Galileasse, seal nad saavad Mind näha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 11, 'Aga kui nad olid minemas, vaata, siis tulid mõned valvureist linna ja teatasid ülempreestritele kõik, mis oli sündinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 12, 'Ja need tulid kokku ühes vanematega, pidasid isekeskis nõu ja andsid sõjameestele rohkesti raha');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 13, 'ning ütlesid: \"Ütelge, et Ta jüngrid tulid öösel ja varastasid Ta ära, kui me magasime.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 14, 'Ja kui maavalitseja seda saab kuulda, küll me teda meelitame ja teeme, et te saate olla mureta!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 15, 'Need aga võtsid raha ja tegid nõnda, kuidas neid õpetati. Ja see jutt on juutide keskel levinud tänase päevani.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 16, 'Ja need üksteistkümmend jüngrit läksid Galileasse sellele mäele, kuhu Jeesus neid oli käskinud minna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 17, 'Ja kui nad Teda nägid, kummardasid nad Teda. Aga mõned olid kahevahel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 18, 'Ja Jeesus tuli nende juure ja rääkis nendega ning ütles: \"Minule on antud kõik meelevald taevas ja maa peal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 19, 'Minge siis ja tehke jüngriteks kõik rahvad, neid ristides Isa ja Poja ja Püha Vaimu nimesse');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 20, 'ja neid õpetades pidama kõike, mida Mina teid olen käskinud. Ja vaata, Mina olen iga päev teie juures maailma-ajastu otsani!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 1, 'Jeesuse Kristuse, Jumala Poja Evangeeliumi algus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 2, 'Nõnda nagu on kirjutatud prohvet Jesaja raamatus: \"Vaata, Ma läkitan Su palge eele Oma Ingli, kes valmistab Su teed,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 3, 'hüüdja hääl on kõrves: Valmistage Issanda teed, õgvendage Tema teerajad!\",');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 4, 'nõnda oli Ristija Johannes kõrves kuulutamas meeleparanduse ristimist pattude andekssaamiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 5, 'Ja tema juure rändasid kogu Juudamaa ja kõik Jeruusalema elanikud ja said temalt ristimise Jordani jões, tunnistades oma patud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 6, 'Ja Johannes oli riietatud kaameli karvusse ja tal oli nahkvöö vööl, ja ta sõi rohutirtse ja metsmett.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 7, 'Ja ta kuulutas ning ütles: \"Pärast mind tuleb See, Kes on vägevam mind, Kellele ma ei kõlba kummardades lahti päästma Tema jalatsite paelu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 8, 'Mina olen teid veega ristinud, aga Tema ristib teid Püha Vaimuga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 9, 'Neil päevil sündis, et Jeesus tuli Galilea Naatsaretist ja Johannes ristis Teda Jordanis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 10, 'Ja sedamaid, kui Ta veest välja tuli, nägi ta taevad avanevat ja Vaimu nagu tuvi laskuvat Tema peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 11, 'Ja hääl kostis taevast: \"Sina oled Mu armas Poeg, Kellest Mul on hea meel!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 12, 'Ja kohe ajas Püha Vaim tema kõrbe.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 13, 'Ja Ta oli kõrves nelikümmend päeva saatana kiusata; ja ta oli metselajate seas, ja Inglid teenisid Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 14, 'Aga pärast seda, kui Johannes oli pandud vangi, tuli Jeesus Galileasse ja kuulutas Jumala Evangeeliumi');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 15, 'ja ütles: \"Aeg on täis saanud ja Jumala Riik on lähedal, parandage meelt ja uskuge Evangeeliumisse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 16, 'Ja kui Ta Galilea mere ääres kõndis, nägi Ta Siimonat ja Andreast võrku merre heitvat; sest nad olid kalurid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 17, 'Ja Jeesus ütles neile: \"Tulge Minu järele ja Ma teen teist inimesepüüdjad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 18, 'Ja sedamaid jätsid nad võrgud maha ja järgisid Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 19, 'Ja pisut eemale minnes nägi Ta Jakoobust, Sebedeuse poega, ja Johannest, ta venda, neidki paadis võrke parandamas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 20, 'Ja kohe kutsus Ta nad. Ja nad jätsid oma isa Sebedeuse paati ühes palgalistega ning läksid ära Tema järele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 21, 'Ja nad läksid Kapernauma, ja Ta läks hingamispäeval kohe kogudusekotta ja õpetas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 22, 'Ja nad hämmastusid Tema õpetusest, sest Ta õpetas neid kui see, kellel on meelevald, ja mitte nõnda nagu kirjatundjad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 23, 'Ja nende kogudusekojas oli parajasti inimene, kelles oli rüve vaim, ja see kisendas');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 24, 'ning ütles: \"Mis meil on tegemist Sinuga, Jeesus Naatsaretlane? Oled Sa tulnud meid hävitama? Ma tunnen sind, kes Sa oled: Jumala Püha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 25, 'Siis Jeesus sõitles teda ning ütles: \"Ole vait ja mine temast välja!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 26, 'Ja rüve vaim raputas teda ja läks suure häälega kisendades temast välja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 27, 'Ja nad kõik kohkusid väga, nii et nad üksteiselt küsisid: \"Mis see on? Uus võimas õpetus! Ka rüvedaile vaimudele Ta annab käsku ja nad kuulevad Tema sõna!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 28, 'Ja kuuldus Temast levis varsti igale poole Galilea ümbrusesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 29, 'Ja kogudusekojast väljudes tulid nad kohe Siimona ja Andrease majasse ühes Jakoobuse ja Johannesega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 30, 'Aga Siimona ämm lamas maas palavikus, ja varsti üteldi Temale seda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 31, 'Ja Ta läks tema juure ja aitas ta üles, kinni hakates ta käest. Ja palavik lahkus temast, ja ta ümmardas neid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 32, 'Aga õhtu tulles, kui päike looja läks, toodi Tema juure kõik haiged ja seestunud;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 33, 'ja kogu linn oli tulnud kokku ukse ette.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 34, 'Ja Ta tegi terveks paljud, kes põdesid mõnesugust haigust, ja ajas välja mitu kurja vaimu ega lasknud kurje vaime rääkida, sest et nad Teda tundsid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 35, 'Ja vara hommikul enne valget tõusis Ta üles ja väljus ning läks ära tühja paika ja palvetas seal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 36, 'Ja Siimon ning tema kaaslased tõttasid Ta järele');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 37, 'ja leidsid Tema ning ,ütlesid Talle: \"Kõik otsivad Sind!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 38, 'ütles neile: \"Lähme teisale, lähemaisse aleveisse, et Ma sealgi kuulutaksin, sest selleks Ma olen välja tulnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 39, 'Ja Ta läks ning kuulutas nende kogudusekodades kogu Galileas ja ajas kurjad vaimud välja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 40, 'Ja Tema juure tuleb pidalitõbine, palub Teda ja heidab ta ette põlvili ning ütleb Temale: \"Kui Sa tahad, võid Sa mind teha puhtaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 41, 'Ja Jeesusel hakkas temast hale meel, Ta sirutas Oma käe, puudutas teda ja ütles talle: \"Ma tahan, saa puhtaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 42, 'Ja kohe lahkus pidalitõbi temast ja ta sai puhtaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 43, 'Ja Ta hoiatas teda kõvasti ja saatis ta kohe ära');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 44, 'ning ütles talle: \"Katsu, et sa kellelegi midagi ei ütle, vaid mine näita ennast preestrile ja ohverda oma puhastamise eest, mis Mooses on käskinud, neile tunnistuseks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 45, 'Aga kui see oli välja läinud, hakkas ta palju kuulutama ja levitama teateid sellest asjast, nõnda et Jeesus enam ei võinud avalikult linna minna, vaid viibis väljas tühjes paigus. Ja Tema juure tuldi kõikjalt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 1, 'Mõne päeva pärast läks Ta jälle Kapernauma, ja saadi kuulda, et Ta on kodus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 2, 'Siis kogunes sinna palju rahvast, nii et neil väljas ukse eeski ei olnud enam maad. Ja Ta rääkis neile Jumala sõna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 3, 'Ja Ta juure tuldi ning toodi halvatut, keda kandsid neli meest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 4, 'Ja kui nad rahva pärast ei pääsnud teda tooma Tema , ligi võtsid nad sealt kohalt, kus Ta oli, katuse lahti, tegid augu ja lasksid alla sängi, millel halvatu lamas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 5, 'Kui Jeesus nende usku nägi, ütles Ta halvatule: \"Poeg, su patud antakse andeks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 6, 'Aga seal istusid mõned kirjatundjad, ja need mõtlesid oma südames:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 7, '\"Mida Seesinane räägib? Ta pilkab Jumalat. Kes muu võib patte andeks anda kui ainuüksi Jumal?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 8, 'Ja kohe tundis Jeesus Oma vaimus, et nad iseeneses nõnda mõtlevad, ja ütles neile: \"Miks te mõtlete seda oma südamees?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 9, 'Kumb on kergem, kas ütelda halvatule: Sulle antakse patud andeks! või: Tõuse üles ja võta oma säng ning kõnni?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 10, 'Aga et te teaksite, et Inimese Pojal on meelevald patte andeks anda, siis - ütleb Ta halvatule');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 11, 'Ma ütlen sulle: Tõuse üles, võta oma säng ja mine koju!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 12, 'Ja sedamaid tõusis ta üles ja võttis oma sängi ja läks välja kõigi nähes, nõnda et kõik ehmusid ja andsid au Jumalale ning ütlesid: \"Niisugust asja me pole ilmaski näinud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 13, 'Ja Ta läks jälle välja mere ääre, ja kõik rahvas tuli Tema juure, ja Ta õpetas neid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 14, 'Ja mööda minnes nägi Ta Leevi, Alfeuse poja, tollihoone juures istuvat; ja Ta ütleb temale: \"Järgi Mind!\" Ja tema tõusis ja järgis Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 15, 'Ja sündis, kui Ta lauas istus tema majas, et istus ka palju tölnereid ja patuseid lauas ühes Jeesuse ja Ta jüngritega; sest palju oli neid, kes Teda järgisid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 16, 'Ja kui variseride kirjatundjad nägid, et Ta sööb tölnerite ja patustega, ütlesid nad Tema jüngritele: \"Miks Ta sööb ja joob tölnerite ja patustega?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 17, 'Ja kui Jeesus seda kuulis, ütles Ta neile: \"Ei terved vaja arsti, vaid haiged. Ma ei ole tulnud kutsuma õigeid, vaid patuseid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 18, 'Ja Johannese ja variseride jüngrid pidasid paastu. Siis tuldi ja üteldi Temale: \"Miks Johannese ja variseride jüngrid paastuvad, aga Sinu jüngrid ei paastu?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 19, 'Jeesus ütles neile: \"Ega peiupoisid või paastuda sel ajal, kui peigmees on nende juures? Niikaua kui peigmees on nende juures, ei või nad paastuda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 20, 'Aga päevi tuleb, mil peigmees neilt ära võetakse, ja siis, sel päeval, nad paastuvad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 21, 'Ükski ei pane vanutamata riidetükki paigaks vanale kuuele, muidu augutäidis rebeneb selle küljest lahti, uus vana küljest, ja auk läheb pahemaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 22, 'Ka ei pane ükski värsket viina vanadesse nahklähkritesse; muidu värske viin ratkub länkrid ja viin ning lähkrid lähevad raisku; vaid värske viin valatakse uutesse lähkritesse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 23, 'Ja juhtus, et Ta ühel hingamispäeval kõndis läbi viljapõldude ja Ta jüngrid hakkasid teed käies viljapäid katkuma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 24, 'Ja variserid ütlesid Temale: \"Vaata, miks nad hingamispäeval teevad, mida ei tohi?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 25, 'Tema ütles neile: \"Kas te ei ole lugenud, mis tegi Taavet, kui tal puudus oli ning nälg temal ja ta kaaslastel,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 26, 'kuidas ta läks Jumala kotta ülempreester Ebjatari ajal ja sõi ära vaateleivad, mida ühelgi ei olnud luba süüa kui vaid preestritel, ja andis ka neile, kes olid temaga?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 27, 'Ja ta ütles neile: \"Hingamispäev on tehtud inimese pärast aga mitte inimene hingamispäeva pärast!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 28, 'Nii on Inimese Poeg ka hingamispäevaisand!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 1, 'Ja Ta läks jälle kogudusekotta. Ja seal oli mees, kellel oli kuivanud käsi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 2, 'Ja nad varitsesid Teda, kas Ta hingamispäeval peaks terveks tegema, et kaevata Tema peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 3, 'Siis Ta ütles mehele, kellel oli kuivanud käsi: \"Tõuse ja astu esile!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 4, 'Ja Ta küsis neilt: \"Kas tohib hingamispäeval teha head või kurja, et elu päästa või hukata?\" Aga nad jäid vait.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 5, 'Ja Ta vaatas ümber nende peale vihaga ja oli ühtlasi kurb nende südamekanguse pärast ja ütles mehele: \"Siruta käsi!\" Ja ta sirutas; ja ta käsi sai jälle terveks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 6, 'Ja variserid läksid välja ja pidasid kohe nõu Heroodese seltsiga Jeesuse vastu, et Teda hukata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 7, 'Aga Jeesus läks ära ühes Oma jüngritega mere poole, ja suurrahvahulk järgis Teda Galileast ja Judeast,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 8, 'ja Jeerusalemast ja Idumeast ja teiselt poolt Jordanit ja Tüürose ja Siidoni ümbrusest, suur rahvakogu, kes kuuldes, mis suuri asju Ta tegi, tuli Tema juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 9, 'Ja Ta ütles Oma jüngritele, et nad hoiaksid lootsiku Temale valmis rahva pärast, et see ei tungiks Ta peale,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 10, 'sest Ta tegi terveks paljusid, mistõttu Temale peale rõhusid kõik, kelledel oli häda, et nad saaksid teda puudutada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 11, 'Ja rüvedad vaimud, kui nad Teda nägid, heitsid maha Tema ette, kisendasid ning ütlesid: \"Sina oled Jumala Poeg!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 12, 'Ja Ta hoiatas neid kangesti, et nad ei teeks Teda avalikuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 13, 'Ja Ta läks üles mäele ja kutsus Enese juure, keda Ta tähtis, ja nad tulid Ta juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 14, 'Ja Ta määras kaksteistkümmend, et need oleksid Tema juures ja Ta võiks neid läkitada kuulutama');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 15, 'ja et neil oleks meelevald välja ajada kurje vaime.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 16, 'Ta määras need kaksteistkümmend: Siimona, ja pani temale nimeks Peetrus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 17, 'ja Jakoobuse, Sebedeuse poja, ja Johannese, Jakoobuse venna, ja pani neile nimeks Boanerges, see on Kõuepojad;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 18, 'ja Andrease ja Filippuse ja Bartolomeuse ja Matteuse ja Tooma ja Jakoobuse, Alfeuse poja, ja Taddeuse ja Siimona Kaanast,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 19, 'ja Juudas Iskarioti, kes Ta ära andis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 20, 'Ja Ta tuli koju. Ja rahvas tuli jälle kokku, nõnda et nad ei saanud leibagi võtta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 21, 'Ja kui Tema juures olijad seda kuulsid, läksid nad välja Teda kinni võtma, sest nad ütlesid: \"Ta on arust ära!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 22, 'Ja kirjatundjad, kes olid tulnud Jeruusalemast, ütlesid: \"Temas on Peeltsebul, ja kurjade vaimude ülema abil ajab Ta välja kurje vaime!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 23, 'Tema kutsus need Enese juure ja ütles neile võrdumitega: \"Kuidas võib saatan välja ajada saatanat?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 24, 'Ja kui kuningriik on isekeskis riius, siis ei või see kuningriik püsida.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 25, 'Ja kui koda on, isekeskis riius, siis ei või see koda püsida.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 26, 'Ja kui saatan paneb iseenesele vastu, siis ei või ta püsida, vaid tal on ots käes!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 27, 'Ükski ei või minna vägeva majasse ja risuda ta riistu, kui ta enne seda vägevat ei seo. Ja siis ta võib rüüstata tema maja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 28, 'Tõesti Ma ütlen teile, et kõik patud antakse inimlastele andeks, ja Jumala pilkamised, niipalju kui nad iganes on Jumalat pilganud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 29, 'kuid kes iganes on pilganud Püha Vaimu, sellel ei ole andekssaamist iialgi, vaid ta on süüdlane igaveses patus!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 30, 'Sest nad ütlesid: \"Temas on rüve vaim!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 31, 'Siis tulid Tema Ema ja vennad ja seisid õues ja läkitasid Tema juure Teda kutsuma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 32, 'Ja rahvas istus Ta ümber ja Temale üteldi: \"Vaata, Su Ema ja su vennad õues otsivad Sind!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 33, 'Ta vastas neile ning ütles: \"Kes on Mu ema või mu vennad?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 34, 'Ja ta vaatas Enese ümber nende peale, kes istusid sõõris Ta ümber, ning ütles: \"Vaata, siin on Mu ema ja Mu vennad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 35, 'Sest kes iganes teeb Jumala tahtmist, see on Mu vend ja õde ja ema!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 1, 'Ja Ta hakkas jälle õpetama mere ääres. Ja üsna palju rahvast kogunes Ta juure, nõnda et Ta pidi minema paati ja istuma merel. Ja kõik rahvas oli maal mererannas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 2, 'Ja Ta õpetas neid palju tähendamissõnadega, ja Oma õpetuses Ta ütles neile:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 3, '\"Kuulge! Vaata, külvaja läks välja külvama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 4, 'Ja sündis, kui Ta külvas, et muist kukkus tee ääre, ja linnud tulid ja sõid selle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 5, 'Ja muist kukkus kaljusele maale, kus tal ei olnud palju mulda; ja see tärkas varsti, sest tal ei olnud sügavat maad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 6, 'Aga kui päike tõusis, kõrbes see, ja et tal ei olnud juurt, kuivas ta ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 7, 'Ja muist kukkus ohakate sekka, ja ohakad tõusid ning lämmatasid selle, ja ta ei annud vilja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 8, 'Ja muist kukkus heale maale, ja kui see tärkas ja kasvas, andis see vilja, ja mõni kandis kolmkümmend seemet ja mõni kuuskümmend ja mõni sada.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 9, 'Ta ütles: \"Kel kõrvad on kuulda, see kuulgu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 10, 'Aga kui Ta üksi oli, küsisid Temalt need, kes ühes nende kaheteistkümnega olid Tema ümber, nende tähendamissõnade mõtet.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 11, 'Ja Ta ütles neile: \"Teile on antud mõista Jumala Riigi saladust, aga neile, kes on väljas, sünnib see kõik tähendamissõnade läbi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 12, 'et nad nähes näeksid ega taipaks, ja kuuldes kuuleksid ega mõistaks, et nad ei pöörduks ja neile ei antaks andeks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 13, 'Ja Ta ütles neile: \"Te ei taipa seda tähendamissõna, kuidas te siis tahate mõista kõiki teisi tähendamissõnu?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 14, 'Külvaja külvab sõna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 15, 'Need on teeäärsed, kuhu sõna külvatakse, ja kui nad on kuulnud, tuleb kohe saatan ja võtab sõna ära, mis nendesse oli külvatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 16, 'nõndasamuti, mis külvati kaljusele maale, on need, kes kui nad sõna kuulevad, seda kohe rõõmuga vastu võtavad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 17, 'aga neil ei ole juurt enestes, vaid nad kestavad ainult üürikese aja; pärast, kui tõuseb viletsus või tagakiusamine sõna pärast, nad taganevad varsti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 18, 'Ja teised on ohakate sekka külvatud. Need on, kes sõna kuulevad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 19, 'aga selle maailma mured ja rikkuse petlikkus ja muude asjade himud saavad võimule ja lämmatavad sõna ära, ja see jääb viljatumaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 20, 'Ja kes on heale maale külvatud, on need, kes sõna kuulevad ja vastu võtavad ja kannavad vilja, mõni kolmekümnekordselt ja mõni kuuekümnekordselt ja mõni sajakordselt.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 21, 'Ja Ta ütles neile: \"Kas küünal tuuakse selleks, et panna teda vaka alla või voodi alla? Kas mitte selleks, et teda panna küünlajalale?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 22, 'Sest midagi ei ole varjul muu jaoks, kui et ta saaks avalikuks, ja midagi ei ole peidetud muu jaoks, kui et ta tuleks ilmsiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 23, 'Kui kellelgi kõrvad on kuulda, see kuulgu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 24, 'Ja Ta ütles neile: \"Pange tähele, mida te kuulete. Mis mõõduga te mõõdate, sellega mõõdetakse teile ja teile lisatakse veel juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 25, 'Sest kellel on, sellele antakse; ja kellel ei ole, sellelt võetakse see, mis tal on!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 26, 'Ja Ta ütles: \"Nõnda on Jumala Riik, otsegu inimene viskab seemet maa peale,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 27, 'ja heidab magama ja tõuseb üles öösel ja päeval, ja seeme tärkab ja sirgub, nõnda et ta isegi ei tea kuidas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 28, 'Sest maa kannab vilja iseenesest, esmalt orast, pärast päid, siis täit nisu pea sees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 29, 'Aga kui vili on valminud, saadab ta sedamaid sirbi sinna, sest lõikus on käes.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 30, 'Ja Ta ütles: \"Millega võiksime võrrelda Jumala Riiki? Või missuguse tähendamissõnaga me seda tähendame?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 31, 'Sinepiivakesega, mis on, kui see maha külvatakse, väiksem kui kõik muud seemned maa peal,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 32, 'aga kui see on külvatud, tõuseb ta ning saab suuremaks kõigist taimedest ja kasvatab suured oksad, nõnda et taeva linnud võivad pesitada ta varju all.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 33, 'Ja mitme niisugune tähendamissõnaga rääkis Ta neile sõnu sedamööda, kuidas nad suutsid kuulda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 34, 'Aga ilma tähendamissõnata ei rääkinud Ta neile midagi; ja isepäinis Ta seletas jüngritele kõik ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 35, 'Ja samal päeval, kui õhtu käes oli, ütles Ta neile: \"Lähme üle teisele poole.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 36, 'Ja kui nad rahva olid laskunud ära minna, võtavad nad Tema paati; nõnda nagu Ta oli; ja ka teisi paate oli Temaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 37, 'Siis tõusis kange tuulispea ja lõi laineid paati, nõnda et paat juba täitus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 38, 'Ja Tema Ise oli paadi päras magamas, toetudes peaalusele; ja nad äratasid Ta üles ja ütlesid Talle: \"Õpetaja, kas Sa ei hooli sellest, et me hukkume?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 39, 'Tema tõusis üles ja sõitles tuult ning ütles merele: \"Ole vait ja väga!\" Ja tuul rauges, ja meri jäi täiesti vaikseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 40, 'Ja Ta ütles neile: \"Miks te olete nii arad? Kuidas teil ei ole usku?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 41, 'Ja nad lõid väga kartma ning ütlesid üksteisele: \"Kes See siis õieti on, et ka tuul ja meri kuulevad Tema sõna?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 1, 'Ja nad tulid teisele poole merd gerasalaste maale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 2, 'Ja kui Ta paadist väljus, kohtas Teda varsti haudadest tulnud mees, kellel oli rüve vaim.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 3, 'Mehe eluase oli surnuhaudades ja keegi ei suutnud teda ahelatega kinni panna,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 4, 'sest ta oli mitu korda olnud kinni jalgrau\'us ja ahelais, aga oli ahelad puruks kiskunud ja jalgrauad katki murdnud, nii et ükski ei suutnud teda taltsutada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 5, 'Ööd ja päevad läbi oli ta aina mägedel ja surnuhaudades ning karjus ja peksis ennast kividega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 6, 'Aga kui ta Jeesust kaugelt nägi, jooksis ta ja kummardas Teda');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 7, 'ja ütles suure häälega kisendades: \"Mis Sinul on minuga tegemist, Jeesus, kõige kõrgema Jumala Poeg? Ma vannutan Sind Jumala juures, et Sa mind ei piinaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 8, 'Sest Ta oli ütelnud temale: \"Mine välja inimesest, sa rüve vaim!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 9, 'Ja Ta küsis temalt: \"Mis su nimi on?\" Ta ütles Talle: \"Mu nimi on Leegion, sest meid on palju!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 10, 'Ja ta anus Teda väga, et Ta neid välja ei saadaks sealt maalt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 11, 'Ent seal oli mäe all suur seakari söömas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 12, 'Ja rüvedad vaimud anuksid Teda ning ütlesid: \"Läheta meid sigade sisse, et me läheksime neisse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 13, 'Ja Tema andis neile loa. Siis rüvedad vaimud väljusid ja läksid sigade sisse. Ja seakari kukutas enese ülepeakaela kaldalt merre, arvult ligi kaks tuhat, ja uppus merre.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 14, 'Aga nende karjatsejad põgenesid ja teatasid sellest linnas ja maal. Ja inimesi tuli välja vaatama, mis on sündinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 15, 'Ja nad tulid Jeesuse juure ja nägid seestunut, kelles oli olnud Leegion, istuvat, riietatud ja selge aruga olevat. Ja nad kartsid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 16, 'Ja need, kes seda olid näinud, jutustasid neile, mis seestunuga oli sündinud, ja sigadest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 17, 'Ja inimesed hakkasid Teda paluma, et ta nende maa-alalt ära läheks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 18, 'Kui Ta paati astus, palus Talt see, kes oli olnud seestunud, et Ta tohiks jääda Tema juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 19, 'Aga Tema ei lubanud seda talle mitte, vaid ütles talle: \"Mine koju omaste juure ja teata neile, mis suuri asju Issand sulle on teinud ja kuidas Ta sinu peale on halastanud.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 20, 'Ja ta läks ära ja hakkas Dekapolis kuulutama, mis suuri asju Jeesus temale oli teinud. Ja kõik panid seda imeks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 21, 'Ja kui Jeesus paadiga jälle siiapoole kaldale oli jõudnud, kogunes palju rahvast Tema juure, ja Ta oli mererannas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 22, 'Siis tuleb üks kogudusekoja ülemaid, Jairus nimi, ja langeb Teda nähes Tema jalge ette maha');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 23, 'ja palub Teda väga ning ütleb: \"Mu tütreke on hinge vaakumas; ma palun, et Sa tuleksid ja paneksid Oma käed tema peale, et ta saaks terveks ning jääks ellu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 24, 'Ja Ta läks temaga. Ja palju rahvast käis Tema järel ja rõhus Ta peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 25, 'Ja seal oli naine, kes oli kaksteistkümmend aastat olnud veritõves');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 26, 'ja oli saanud palju kannatada mitme arsti all ning oli kulutanud kogu oma vara, ilma et ta oleks mingit abi saanud, kuna haigus oli läinud veel pahemaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 27, 'See oli kuulnud Jeesusest ja tuli rahva seas Tema selja taha ja puudutas Ta kuube.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 28, 'Sest ta mõtles: Kui ma aga puudutaksin Tema riideid, saaksin ma terveks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 29, 'Ja kohe kuivas tema vereläte, ja ta tundis oma ihu vaevast terveks saanud olevat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 30, 'Ka Jeesus tundis kohe Iseeneses, et vägi Temast oli väljunud, pöördus ümber rahva seas ja küsis: \"Kes puudutas Mu riideid?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 31, 'Tema jüngrid ütlesid Temale: \"Sa näed, et rahvas rõhub Su peale, ja küsid: Kes puudutas Mind?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 32, 'Ent Ta vaatas ümber, et näha, kes seda oli teinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 33, 'Siis lõi naine kartma ning värises, sest et ta teadis, mis temale oli sündinud, ja tuli ning heitis maha Tema ette ja ütles Temale kogu tõe.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 34, 'Aga Ta ütles naisele: \"Tütar, sinu usk on sind aidanud; mine rahuga ja ole terve oma vaevast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 35, 'Kui Ta alles rääkis, tulid mõned kogudusekoja ülema perest ja ütlesid: \"Su tütar on surnud, mis sa õpetajat veel tülitad?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 36, 'Aga Jeesus jättis tähele panemata, mida räägiti, ja ütles kogudusekoja ülemale: \"Ara karda, usu vaid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 37, 'Ja ta ei lasknud kedagi tulla ühes Enesega kui vaid Peetruse, Jakoobuse ja Johannese, Jakoobuse venna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 38, 'Ja nad tulevad kogudusekoja ülema majasse, ja Ta näeb käratsemist ja nutjaid ja ulujaid;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 39, 'ja sisse minnes ütleb Ta neile: \"Mis te käratsete ja nutate? Laps ei ole surnud, vaid magab!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 40, 'Ja nad naersid Teda. Aga kui Ta kõik on välja ajanud, võtab Ta Enesega lapse isa ja ema ja Enese juures olijad ja läheb sisse sinna, kus laps maas oli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 41, 'Ja lapse käest kinni hakates ütleb Ta temale: \"Talitaa kuum!\" see on tõlgitult: Neitsike, Ma ütlen sulle, tõuse üles!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 42, 'Ja sedamaid tõusis neitsike üles ja kõndis; ta oli juba kaksteistkümmend aastat vana. Ja nad ehmusid üliväga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 43, 'Ja Ta keelas neid kangesti, et keegi seda ei saaks teada, ja käskis tütarlapsele süüa anda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 1, 'Ja Ta läks sealt ära ja tuli Oma kodukohta, ja Ta jüngrid järgisid Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 2, 'Ja kui hingamispäev tuli, hakkas Ta kogudusekojas õpetama. Ja paljud, kes Teda kuulsid, hämmastusid väga ja ütlesid: \"Kust Sellele see kõik on tulnud ja mis tarkus see on, mis Temale on antud? Ja kuidas niisugused vägevad teod sünnivad Ta käte läbi?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 3, 'Eks Tema ole see puusepp, Maarja poeg, Jakoobuse ja Joosese ja Juuda ja Siimona vend? Ja eks ka Tema õed ole siin meie juures?\" Ja nad pahandusid Temast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 4, 'Aga Jeesus ütles neile: \"Prohvet ei ole mujal autu kui oma kodukohas ja oma sugulaste seas ning omas majas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 5, 'Ja Ta ei saanud seal teha ühtki muud vägevat tegu kui ainult panna Oma käed väheste haigete peale ja nad terveks teha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 6, 'Ja Ta pani imeks nende uskmatust. Ja Tema käis läbi ümberkaudsed külad ning õpetas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 7, 'Ja Ta kutsus need kaksteistkümmend Enese juure ja hakkas neid läkitama kahekaupa ja andis neile meelevalla rüvedate vaimude üle,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 8, 'ja käskis neid, et nad midagi muud ei võtaks teele kaasa kui ainult kepi, ei pauna ega leiba ega raha vöö vahele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 9, 'vaid sidugu paeltega kingad jalga ja ärgu pangu kaht kuube selga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 10, 'Ja Ta ütles neile: \"Kuhu kotta te iganes sisse lähete, sinna jääge, seni kui te sealt väljute.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 11, 'Ja kus paigas iganes teid vastu ei võeta ega kuulata, sealt väljuge ja puistake tolm oma jalgadelt neile tunnistuseks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 12, 'Ja nad läksid välja ja kuulutasid, et tuleb meelt parandada,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 13, 'ja ajasid palju kurje vaime välja ja võidsid palju tõbiseid õliga ja tegid nad terveks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 14, 'Ja kuningas Heroodes sai kuulda Jeesusest, sest Ta nimi oli juba tuttav, ja ütles: \"Ristija Johannes on surnuist üles tõusnud ja sellepärast on imelised väed tema sees tegevad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 15, 'Aga teised ütlesid: \"Tema on Eelija!\" Teised aga: \"Tema on prohvet nagu üks prohveteid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 16, 'Aga kui Heroodes seda kuulis, ütles ta: \"Johannes, kelle pea ma otsast raiusin, on üles tõusnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 17, 'Sest Heroodes ise oli läkitanud ja Johannese kinni võtnud ja tema sidunud ning vangitorni pannud Heroodiase, oma venna Filippuse naise pärast. Sest ta oli tema enesele naiseks võtnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 18, 'Oli ju Johannes ütelnud Heroodesele: \"Sul ei ole luba oma venna naist pidada!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 19, 'Aga Heroodias kandis ta peale viha ja tahtis teda tappa, ja ei saanud mitte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 20, 'Sest Heroodes kartis Johannest; ta teadis teda õige ja püha mehe olevat ning kaitses teda, ja kui ta teda kuulis, jäi ta mõneski asjas kahevahele ja kuulas teda siiski hea meelega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 21, 'Kui nüüd paras aeg oli tulnud, mil Heroodes oma sünnipäeval tegi söömaaja oma suurtele isandatele ja pealikuile ning Galilea ülemaile,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 22, 'siis tuli sama Heroodiase tütar sisse ja tantsis. See oli meelt mööda Heroodesele ja neile, kes lauas istusid. siis ütles kuningas neitsile: \"Palu mult, mida sa iganes tahad, ja ma annan sulle!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 23, 'Ja ta vandus temale: \"Ma annan sulle, mis sa iganes minult palud, olgu kas või pool minu kuningriiki!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 24, 'Aga neitsi läks välja ja küsis oma emalt: \"Mis ma pean paluma?\" Ent ema ütles: \"Ristija Johannese pea!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 25, 'Ja neitsi läks kohe rutuga kuninga juure, palus ning ütles: \"Ma tahan, et sa mulle sedamaid annad vaagnal Ristija Johannese pea!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 26, 'Siis sai kuningas väga kurvaks, kuid vande ja lauasistujate pärast ta ei tahtnud temale seda keelata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 27, 'Ja kuningas läkitas kohe valvuri ja käskis tuua tema pea.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 28, 'See läks ja raius vangitornis ta pea otsast ära ja tõi ta pea vaagnal ning andis selle neitsile, ja neitsi andis selle oma emale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 29, 'Kui tema jüngrid seda kuulsid, tulid nad ja võtsid ta keha ja panid selle hauda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 30, 'Ja Apostlid tulid kokku Jeesuse juure ja kuulutasid Temale kõik, mida nad olid teinud ja mida õpetanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 31, 'Siis Ta ütles neile: \"Tulge teie kõrvale üksikusse paika ja puhake pisut!\" Sest tulijaid ja minejaid oli palju ja nad ei saanud mahti süüagi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 32, 'Ja nad sõudsid paadiga kõrvaie üksikusse paika.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 33, 'ja paljud nägid neid ära sõudvat ning tundsid Tema ära ja jooksid jala sinna kokku kõigist linnadest ja jõudsid neist ette.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 34, 'Ja kui Ta paadist välja astus ja nägi palju rahvast, hakkas Tal neist hale meel, et nad olid otsegu lambad, kelledel pole karjast. Ja Ta hakkas neid pikalt õpetama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 35, 'Aga kui aeg oli läinud hiliseks, tulid Ta jüngrid Ta juure ja ütlesid: \"Paik on tühi ja aeg jääb juba hiliseks;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 36, 'lase nad minema, et nad läheksid ümberkaudu asulaisse ja küladesse ja ostaksid endile leiba, sest neil ei ole midagi süüa!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 37, 'Aga Tema kostis ja ütles neile: \"Andke teie neile süüa!\" Nemad ütlesid Temale: \"Kas me peaksime minema kahesaja teenari eest leiba ostma ja neile süüa andma?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 38, 'Aga Tema küsis neilt: \"Mitu leiba teil on? Minge vaadake.\" Kui nad olid teada saanud, ütlesid nad: \"Viis leiba ja kaks kala.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 39, 'Ja Ta käskis neid asetada kõik istuma, salkkond salkkonna kõrvale haljale murule.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 40, 'Ja nad istusid maha ridamisi, sajakaupa ja viiekümnekaupa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 41, 'Ja Ta võttis need viis leiba ja kaks kala, vaatas üles taeva poole ja õnnistas ja murdis leivad ja andis need Oma jüngrite kätte rahva ette panemiseks. Ja ka need kaks kala jagas Ta kõikidele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 42, 'Ja kõik sõid ja nende kõhud said täis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 43, 'Ja korjati kokku kaksteistkümmend korvitäit palukesist ja kalust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 44, 'Ja neid, kes leivust olid söönud, oli viis tuhat meest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 45, 'Ja sedamaid sundis Ta Oma jüngreid astuma paati ja sõitma Tema eele teisele rannale Betsaidasse, kuni Tema laseb rahva minema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 46, 'Ja kui Ta lahkumistervituse oli ütelnud, läks Ta mäele palvetama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 47, 'Kui siis õhtu jõudis, oli paat keset merd ja Tema üksi maal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 48, 'Ja Ta nägi neil sõudes püsti häda käes olevat, sest tuul oli neile vastu. Ja neljandal öövahi-korral tuli Ta mere peal kõndides nende juure ja tahtis neist mööda minna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 49, 'Aga kui nad Teda nägid mere peal kõndivat, arvasid nad, et see on tont, ja hakkasid karjuma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 50, 'Sest nad kõik nägid Teda ja ehmusid. Aga Tema rääkis kohe nendega ja ütles neile: \"Olge julged, Mina olen, ärge kartke!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 51, 'Ja Ta läks nende juure paati, ja tuul rauges. Ja nad kohkusid üpris väga iseenestes,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 52, 'sest nad ei olnud veel aru saanud leibade loost, vaid nende süda oli läinud kõvaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 53, 'Ja kui nad üle mere olid maale sõudnud, jõudsid nad Gennesaretti ja astusid rannale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 54, 'Ja kui nad paadist olid väljunud, tunti Ta sedamaid ära');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 55, 'ja joosti kogu sealne ümberkaudne maa läbi ning hakati haigeid vooditega kandma igale poole, kus Teda kuuldi olevat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 56, 'Ja kuhu Ta iganes läks küladesse või linnadesse või asulaisse, asetati põdejaid turgudele ja paluti Teda, et nad saaksid vaid puudutada Tema kuue palistust. Ja kõik, kes Teda puudutasid, said terveks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 1, 'Ja variserid ja mõned kirjatundjaist, kes olid tulnud Jeruusalemast, kogunesid Tema juure,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 2, 'ja nähes mõningaid Ta jüngritest ebapühade, see on pesemata kätega leiba võtvat -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 3, 'sest variserid ja ükski juut ei söö enne, kui nad on hoolsasti käsi pesnud, nii pidades esivanemate pärimust;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 4, 'ka turult tulles ei söö nad muidu kui et nad endid enne pesevad; ja palju muid asju on, mida nad on võtnud pidada, nagu karikate ja kiviriistade ja vasknõude pesemised -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 5, 'küsisid Temalt variserid ja kirjatundjad: \"Miks Sinu jüngrid ei ela vanemate pärimuse järele, vaid söövad leiba ebapühade kätega?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 6, 'Aga Ta ütles neile: \"Jesaja on hästi teist silmakirjatsejaist ennustanud, nõnda nagu on kirjutatud: See rahvas austab Mind huultega, kuid nende süda on Minust kaugel;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 7, 'ilmaaegu nad teenivad Mind, õpetades õpetusi, mis on inimeste käskimised.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 8, 'Sest hüljates Jumala käsu, te peate inimeste pärimust!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 9, 'Ja Ta ütles neile: \"Ilus küll! Te heidate Jumala käsu kõrvale, et pidada oma pärimust!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 10, 'Sest Mooses ütles: Sa pead oma isa ja ema austama, ja kes isa või ema neab, peab surma surema!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 11, 'Teie aga ütlete: Kui inimene ütleb oma isale või emale: Korbaan - see tähendab ohvriand - on see, mis sa iganes minu käest võiksid saada!,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 12, 'siis te ei lase teda enam ühtki head teha oma isale ega emale');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 13, 'ja teete nõnda Jumala sõna tühjaks oma pärimusega, mille te oiete pärinud, ja teete palju muud seesugust.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 14, 'Ja Ta kutsus jälle kõik rahva Enese juure ja ütles neile: \"Kuulge Mind kõik ja mõistke!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 15, 'Miski, mis läheb väljastpoolt inimest tema sisse ei või teda rüvetada; aga mis läheb inimesest välja, see rüvetab inimest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 16, 'Kui kellelgi kõrvad on kuulda, see kuulgu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 17, 'Ja kui Ta rahva juurest oli läinud ühte majasse, küsisid Ta jüngrid Temalt selle tähendamissõna mõtet.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 18, 'Ja ta ütles neile: \"Kas teiegi olete mõistmatumad? Eks te saa aru, et kõik, mis väljastpoolt läheb inimese sisse, ei või teda rüvetada?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 19, 'Sest see ei lähe mitte tema südamesse, vaid kõhtu, ja väljub eri paika.\" Nii Ta kuulutas kõik toidused puhtaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 20, 'Ja Ta ütles: \"Mis inimesest välja läheb, see rüvetab inimest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 21, 'Sest seestpoolt, inimeste südamest, lähtuvad kurjad mõtted, hoorus, vargused, tapmised,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 22, 'abielurikkumised, ahnus, kurjused, kavalus, kiimalus, kade silm, Jumala pilkamine, kõrkus, rumalus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 23, 'Kõik need pahad asjad lähtuvad seestpoolt ja rüvetavad inimest.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 24, 'Ja Ta tõusis ning läks sealt ära Tüürose ja Siidoni alale. Ja Ta läks ühte majasse ega tahtnud, et keegi seda teada saaks. Kuid Ta ei võinud jääda varjule.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 25, 'Sest üks naine, kelle tütrekesel oli rüve vaim, oli Temast kohe kuulda saanud ja tuli ning heitis ta jalge ette maha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 26, 'See naine oli kreeklane, sündimise poolest Sürofoiniikia inimene. Ja see palus Teda, et Ta ajaks kurja vaimu välja ta tütrest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 27, 'Siis ütles Jeesus temale: \"Lase esmalt laste kõhud täis saada; sest ei ole mitte hea võtta laste leib ja heita koerukeste ette!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 28, 'Ent naine kostis ja ütles Temale: \"Jah, Issand, aga koerukesed laua all söövad laste raasukesi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 29, 'Siis Ta ütles naisele: \"Selle sõna pärast mine; kuri vaim on sinu tütrest lahkunud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 30, 'Ja naine läks koju ja leidis tütre voodis magavat ning kurja vaimu lahkunud olevat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 31, 'Ja kui Ta jälle välja läks Tüürose aladelt, tuli Ta Siidoni kaudu Galilea mere ääre Dekapoli alade keskkohale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 32, 'Ja Ta juure toodi kurt, kellel oli kidakeel, ja paluti Teda, et Ta paneks Oma käe tema peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 33, 'Siis Ta võttis tema rahva seast kõrvale ja pistis Oma sõrmed tema kõrvu ja sülitas ning puudutas ta keelt,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 34, 'vaatas üles taevasse, ohkas ja ütles talle: \"Effataa!\", see on: Mine lahti!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 35, 'Ja tema kõrvad läksid lahti ja ta keeleköidik pääsis kohe valla, ja ta rääkis selgesti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 36, 'Ja Ta keelas neid seda ühelegi rääkimast. Aga mida enam Ta neid keelas, seda enam nad kuulutasid seda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 37, 'Ja inimesed hämmastusid üliväga ja ütlesid: \"Kõik on Ta hästi teinud; kurdid teeb ta kuulma ja keeletumad rääkima!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 1, 'Neil päevil, kui jälle väga palju rahvast koos oli ja neil ei olnud midagi süüa, kutsus Jeesus jüngrid Enese juure ja ütles neile:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 2, '\"Mul on väga hale meel rahvast, sest nad on juba kolm päeva olnud Minu juures, ja neil ei ole midagi süüa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 3, 'Ja kui Ma nad lasen koju minna söömata, nõrkevad nad teel!\" Sest mõned neist olid kaugelt tulnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 4, 'Ja Ta jüngrid vastasid Temale: \"Kust võib keegi siin kõrves neid leivaga täita?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 5, 'Ja Ta küsis neilt: \"Mitu leiba teil on?\" Nad ütlesid: \"Seitse.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 6, 'Ja Ta käskis rahva istuda maa peale. Siis Ta võttis need seitse leiba, tänas, murdis ja andis Oma jüngrite kätte, et nad paneksid ette. Ja nad panid rahvale ette.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 7, 'Ja neil oli pisut kalukesi; ja Ta õnnistas ja käskis ka need ette panna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 8, 'Siis nad sõid ja nende kõhud said täis, ja ülejäänud palukesi korjati kokku seitse korvitäit.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 9, 'Aga sööjaid oli olnud arvata neli tuhat. Ja Ta laskis nad minna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 10, 'Ja varsti Ta astus ühes oma jüngritega paati ja jõudis Dalmanuuta paikadesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 11, 'Ja variserid tulid välja ja hakkasid Temaga vaidlema ning nõudsid Temalt tunnustähte taevast, et Teda kiusata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 12, 'Siis Ta ohkas Oma vaimus ja ütles: \"Miks see sugu otsib tähte? Tõesti, Ma ütlen teile, sellele soole ei anta tähte!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 13, 'Ja Ta jättis nad maha ja astus jälle paati ja läks ära teisele poole.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 14, 'Ja nad olid unustanud leiba kaasa võtta ja neil ei olnud rohkem kui üksainus leib paadis kaasas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 15, 'Ja Ta käskis neid ning ütles: \"Vaadake ette, hoiduge variseride haputaignast ja Heroodese haputaignast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 16, 'Ja nad arutlesid isekeskis seda, et neil ei ole leiba.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 17, 'Tema märkas seda ning ütles neile: \"Mis te arutate, et teil ei ole leiba? Kas te veel ei mõista ega saa aru? Kas teil on ikka alles kõva süda?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 18, 'Silmad teil on, aga te ei näe; kõrvad teil on, aga te ei kuule ega mäleta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 19, 'Kui Ma viis leiba murdsin viiele tuhandele, mitu korvitäit palukesi te siis korjasite?\" Nad ütlesid: \"Kaksteistkümmend!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 20, '\"Kui Ma seitse murdsin neljale tuhandele, mitu korvitäit palukesi te siis korjasite?\" Nad ütlesid: \"Seitse.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 21, 'Siis Ta ütles neile: \"Kuidas te siis ei mõista?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 22, 'Ja nad tulevad Betsaidasse. Ja Ta juure tuuakse pime, ja nad paluvad Teda, et Ta teda puudutaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 23, 'Siis Ta haaras pimeda käest kinni ja viis ta alevist välja, sülitas temale silmi ning pani Oma käed ta peale ja küsis talt: \"Kas sa näed midagi?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 24, 'Tema vaatas üles ning ütles: \"Ma näen inimesi, ma näen neid nagu puid kõndimas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 25, 'siis Ta pani taas Oma käed ta silmadele; ja nüüd ta vaatas teravasti ja oli jälle terve; ja ta nägi kõike selgesti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 26, 'Ja Ta saatis tema koju ning ütles: \"Ära minegi külasse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 27, 'Ja Jeesus ja Ta jüngrid läksid Filippuse Kaisarea küladesse. Ja teel küsis Ta Oma jüngritelt ning ütles neile: \"Kelle Mind inimesed ütlevad olevat?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 28, 'Aga nemad vastasid Temale ning ütlesid: \"Ristija Johannese, ja teised Eelija, ja veel teised ühe prohveteist!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 29, 'Siis Ta küsis neilt: \"Aga teie, kelle teie ütlete Mind olevat?\" Peetrus vastas ning ütles Temale: \"Sina oled Kristus!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 30, 'Ja Ta hoiatas neid kõvasti, et nad Temast ühelegi ei räägiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 31, 'Ja Ta hakkas neid õpetama: \"Inimese Poeg peab palju kannatama ja hüljatama vanemate ja ülempreestrite poolt ning tapetama ja kolme päeva pärast üles tõusma.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 32, 'Ja Ta rääkis seda üsna avalikult. Ja Peetrus võttis ta isepäinis ja hakkas Teda noomima.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 33, 'Aga tema pöördus, vaatas Oma jüngritele ja sõitles Peetrust ning ütles: \"Tagane Minust, saatan, sest sa ei mõtle seda, mis on Jumala, vaid mis on inimeste kohane!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 34, 'Ja Ta kutsus rahva Enese juure ühes Oma jüngritega ning ütles neile: \"Kui keegi tahab Minu järele tulla, siis ta salaku end ära ja võtku oma rist enese peale ja järgigu Mind.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 35, 'Sest kes iganes oma hinge tahab päästa, see kaotab selle; aga kes iganes oma hinge kaotab Minu ja armuõpetuse pärast, see päästab selle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 36, 'Sest mis kasu on inimesel sellest, kui ta kogu maailma kasuks saaks, aga oma hingele kahju teeks?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 37, 'Või mis võib inimene anda oma hinge vahetushinnaks?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 38, 'Sest kes häbeneb Minu ja Mu sõnade pärast selles abielurikkujas ja patuses tõus, selle pärast häbeneb ka Inimese Poeg, kui Ta tuleb Oma Isa auhiilguses ühes pühade Inglitega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 1, 'Ja tema ütles neile: \"Tõesti, Ma ütlen teile, neist, kes siin seisavad, on mõned, kes ei maitse surma, kuni nad näevad Jumala Riigi tulnud olevat väega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 2, 'Ja kuue päeva pärast võttis Jeesus enesega Peetruse, Jakoobuse ja Johannese ning viis nad üles kõrgele mäele isepäinis, ja Tema muudeti nende ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 3, 'Tema riided läksid hiilgavaks, helevalgeks otsegu lumi, nagu neid ükski vanutaja maa peal ei suuda teha nii heledaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 4, 'Ja Eelija ühes Moosesega ilmus neile, ja nad kõnelesid Jeesusega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 5, 'Ja Peetrus kostis ning ütles Jeesusele: \"Rabi, siin on hea olla! Teeme nüüd kolm telki: Sinule ühe ja Moosesele ühe ja Eeiijale ühe!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 6, 'Aga ta ei teadnud, mida ütelda, sest nad olid täis kartust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 7, 'Siis tuli pilv ja heitis varju nende üle; ja hääl kostis pilvest: \"See on Mu armas Poeg, Teda kuulake!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 8, 'Ja kui nad äkitselt ümber vaatasid, ei näinud nad enam kedagi muud kui üksnes Jeesust eneste juures.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 9, 'Ja kui nad mäelt alla läksid, keelas Ta neid, et nad ühelegi ei jutustaks, mis nad olid näinud, enne kui Inimese Poeg on surnuist üles tõusnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 10, 'Ja nad pidasid meeles selle sõna ja küsisid isekeskis: \"Mis tähendab surnuist üles tõusta?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 11, 'Siis nad küsisid Temalt ning ütlesid: \"Miks kirjatundjad ütlevad, et Eelija peab enne tulema?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 12, 'Aga Tema ütles neile: \"Eelija tuleb küll enne korda seadma kõike. Ent kuidas on kirjutatud Inimese Pojast, et Ta peab palju kannatama ja Teda peetakse halvaks?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 13, 'Aga Ma ütlen teile, et ka Eelija on tulnud ja et nad tegid temale, mida nad tahtsid, nõnda nagu temast on kirjutatud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 14, 'Ja kui nad tulid jüngrite juure, nägi Ta palju rahvast nende ümber ja kirjatundjaid vaidlevat nendega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 15, 'Ja kohe, kui rahvahulk Teda nägi, kohkusid kõik ja jooksid Temale vastu ning teretasid Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 16, 'Ja Ta küsis nendelt: \"Miks te vaidlete nendega?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 17, 'Siis vastas keegi rahva seast: \"Õpetaja, ma tõin Su juure oma poja, kellel on keeletu vaim;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 18, 'ja kus ta iganes tuleb tema kallale, kisub ta teda, ja tema ajab vahtu ja kiristab hambaid ja kuivetub. Ja ma ütlesin Su jüngritele, et nad ajaksid ta välja, kuid nad ei suutnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 19, 'Aga Tema vastas neile ja ütles: \"Oh sina uskmatu tõug! Kui kaua Ma pean olema teie juures? Kui kaua Ma pean teiega kannatama? Tooge ta Minu juure!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 20, 'Ja nad tõid ta Tema juure. Ja kohe, kui ta Jeesust nägi, raputas vaim Teda ja ta kukkus maa peale ja aeles ning ajas vahtu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 21, 'Ja Ta küsis tema isalt: \"Kui kaua aega see on temal olnud?\" Tema vastas: \"Lapsest saadik!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 22, 'See on teda mitu puhku küll tulle, küll vette visanud, et teda hukata; aga kui Sa kuidagi võid, siis olgu Sul meist hale meel ning aita meid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 23, 'Aga Jeesus ütles temale: \"Sa ütled: Kui Sa võid! Kõik on võimalik sellele, kes usub!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 24, 'Ja poisi isa hüüdis sedamaid ning ütles: \"Ma usun, aita Mu uskmatust!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 25, 'Aga nähes, et rahvast ikka rohkem kokku jooksis, sõitles Jeesus rüvedat vaimu ning ütles temale: \"Sina, keeletu ja kurt vaim, Ma käsin sind: mine temast välja ja ära tule enam tema sisse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 26, 'Siis see kisendas ja raputas teda väga ja väljus. Ja poiss jäi otsegu surnuks, nõnda et mitmed ütlesid: \"Ta on surnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 27, 'Kuid Jeesus hakkas ta käest kinni ja tõstis ta üles; ja ta tõusis püsti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 28, 'Ja kui ta koju tuli, küsisid Ta jüngrid Temalt isepäinis: \"Miks meie ei võinud teda välja ajada?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 29, 'Tema ütles neile: \"See sugu ei või teisiti välja minna kui vaid palvega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 30, 'Ja nad läksid sealt ära ja rändasid läbi Galilea. Ja Ta ei tahtnud, et keegi saaks teada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 31, 'Sest Ta õpetas Oma jüngreid ja ütles neile: \"Inimese Poeg antakse inimeste kätte ja nad tapavad Tema, ja kui Ta on tapetud, tõuseb Ta kolmandal päeval üles!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 32, 'Aga nemad ei saanud aru sellest sõnast ja kartsid Temalt küsida.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 33, 'Ja nad tulid Kapernauma. Ja koju jõudes Ta küsis neilt: \"Mille üle te teel vaidlesite?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 34, 'Aga nad jäid vait; sest nad olid teel isekeskis vaielnud selle üle, kes on suurem.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 35, 'Ja Tema istus maha ja kutsus need kaksteistkümmend ning ütles neile: \"Kui keegi tahab olla esimene, siis olgu ta kõikidest viimne ja kõikide teenija!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 36, 'Ja Ta võttis lapse ja pani selle nende keskele, ja kaisutas teda ja ütles neile:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 37, '\"Kes iganes ühe niisuguseist lapsist vastu võtab Minu nimel, see võtab Mind vastu, ja kes iganes Mind vastu võtab, see ei võta Mind vastu, vaid Teda, Kes Mind on läkitanud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 38, 'Johannes ütles Temale: \"Õpetaja, me nägime Sinu nimel kurje vaime välja ajavat meest, kes meid ei järgi, ja me keelasime teda, sest ta ei järgi meid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 39, 'Aga Jeesus ütles: \"Ärge keelake teda! Sest kedagi ei ole, kes teeb vägeva teo Minu nimel ja suudab sedamaid rääkida Minust kurja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 40, 'Sest kes ei ole meie vastu, see on meie poolt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 41, 'Sest kes teid joodab karikatäie veega Minu nimel, sellepärast et te olete Kristuse Omad, tõesti, Ma ütlen teile, see ei jää ilma oma palgast!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 42, 'Ja kes pahandab üht neist pisukesist, kes Minusse usuvad, sellele oleks parem, et veskikivi temale kaela pandaks ja ta merre visataks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 43, 'Ja kui su käsi sind pahandab, raiu ta maha; parem on sul vigasena minna ellu kui et sul on kaks kätt ja pead minema põrgusse, kustumatusse tulle,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 44, 'kus nende uss ei sure ja tuli ei kustu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 45, 'Ja kui su jalg sind pahandab, raiu ta maha; parem on sul jalutuna minna ellu kui et sul on kaks jalga ja sind heidetakse põrgusse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 46, 'kus nende uss ei sure ja tuli ei kustu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 47, 'Ja kui su silm sind pahandab, kisu ta välja; parem on sul ühe silmaga minna Jumala riiki kui et sul on kaks silma ja sind heidetakse põrgusse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 48, 'kus nende uss ei sure ja tuli ei kustu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 49, 'Sest igaüht peab tulega soolatama!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 50, 'Sool on hea; aga kui sool tuimaks läheb, millega te teete ta maitsekaks? Olgu teil enestes soola ja pidage rahu isekeskis!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 1, 'Ja Tema läks sealt teele ja tuli Judea aladele ja maale sealpool Jordanit. Ja rahvast voolas jälle kokku Tema juure, ja Ta õpetas neid jälle nagu tavaliselt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 2, 'Ja varisere tuli Tema juure, ja Teda kiusates küsisid nad Temalt: \"Kas on mehel luba oma naist enesest lahutada?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 3, 'Tema vastas ja ütles neile: \"Mida Mooses teid käskis?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 4, 'Nemad ütlesid: \"Mooses andis loa kirjutada lahutuskiri ja lahutada!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 5, 'Aga Jeesus kostis ja ütles: \"Teie südame kanguse pärast ta kirjutas teile selle käsu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 6, 'ent loomise algusest Jumal lõi nad meheks ja naiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 7, 'Sellepärast loobub inimene oma isast ja emast,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 8, 'ja need kaks, mees ja naine, saavad üheks lihaks. Nii ei ole nad enam kaks, vaid üks liha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 9, 'Mis nüüd Jumal on ühte pannud, seda inimene ärgu lahutagu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 10, 'Ja kodus küsisid Tema jüngrid jälle Temalt sama asja pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 11, 'Ja Ta ütles neile: \"Kes iganes oma naise enesest lahutab ja võtab teise, rikub abielu tema vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 12, 'Ja kui naine enese lahutab oma mehest ja läheb teisele, siis ta rikub abielu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 13, 'Ja nad tõid Tema juure lapsukesi, et Ta neid puudutaks. Aga jüngrid sõitlesid toojaid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 14, 'Kui Jeesus seda nägi, läks Ta tusaseks ja ütles neile: \"Laske lapsukesed Minu juure tulla, ärge keelake neid, sest niisuguste päralt on Jumala Riik!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 15, 'Tõesti, Ma ütlen teile, kes Jumala Riiki vastu ei võta nagu lapsuke, see ei pääse sinna sisse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 16, 'Ja Ta kaisutas neid ja pani Oma käed nende peale ning õnnistas neid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 17, 'Ja kui Ta ära läks teele, jooksis keegi Ta juure, langes põlvili Ta ette ning küsis Temalt: \"Hea Õpetaja, mis ma pean tegema, et ma igavese elu päriksin?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 18, 'Aga Jeesus ütles talle: \"Miks sa Mind nimetad heaks? Keegi muu ei ole hea kui ainult Jumal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 19, 'Käsud sa tead: Sa ei tohi tappa; sa ei tohi abielu rikkuda; sa ei tohi varastada; sa ei tohi valet tunnistada; sa ei tohi kedagi petta; sa pead oma isa ja ema austama!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 20, 'Aga ta kostis ja ütles Temale: \"Õpetaja, seda kõike ma olen pidanud oma noorestpõlvest alates!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 21, 'Ja Jeesus vaatas tema peale ja tundis armastust ta vastu ning ütles talle: \"Üht asja on sulle vaja: mine ja müü ära kõik, mis sul on, ja anna vaestele, ja sul on siis varandus taevas; ja tule ning järgi Mind!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 22, 'Tema aga jäi murelikuks selle sõna pärast ja läks ära kurva meelega, sest tal oli palju vara.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 23, 'Siis Jeesus vaatas ümber ja ütles Oma jüngritele: \"Kui raske on neil, kelledel on palju vara, sisse minna Jumala Riiki!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 24, 'Aga jüngrid kohkusid Tema sõnade pärast. Ent Jeesus kostis jälle ja ütles neile: \"Lapsed, kui raske on sisse minna Jumala Riiki!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 25, 'Hõlpsam on kaamelil minna läbi nõelasilma kui rikkal pääseda Jumala Riiki!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 26, 'Aga nemad hämmastusid veel enam ja ütlesid isekeskis: \"Kes siis võib õndsaks saada?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 27, 'Jeesus vaatas nendele ja ütles: \"Inimestel on see võimatu, aga mitte Jumalal; sest Jumalal on kõik võimalik!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 28, 'Peetrus hakkas Temale ütlema: \"Vaata, meie oleme jätnud maha kõik ja oleme järginud Sind!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 29, 'Jeesus ütles: \"Tõesti, Ma ütlen teile, ei ole kedagi, kes maja või vennad või õed või ema või isa või naise või põllud on maha jätnud Minu ja Armuõpetuse pärast');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 30, 'ega saaks sajakordselt nüüd selsinasel ajal maju ja vendi ja õdesid ja emasid ja lapsi ja põlde keset tagakiusamisi ja tulevases maailma-ajastus igavest elu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 31, 'Aga paljud esimesed saavad viimseiks ja viimsed esimesiks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 32, 'Aga nad olid teel minemas Jeruusalemma; ja Jeesus käis nende ees, ja nad kohkusid, aga järelkäijad kartsid. Ja Ta võttis jälle need kaksteistkümmend Enese juure ja hakkas neile rääkima, mis Temale peab sündima:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 33, '\"Vaata, me läheme üles Jeruusalemma; ja Inimese Poeg antakse ülempreestrite ja kirjatundjate kätte, ja nad mõistavad Ta surma ja annavad Ta paganate kätte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 34, 'Ja need pilkavad Teda ja sülitavad Ta peale ja peksavad Teda rooskadega ja tapavad Tema, ja kolme päeva pärast tõuseb ta üles!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 35, 'Siis tulid Tema juure Jakoobus ja Johannes, mõlemad Sebedeuse pojad, ja ütlesid: \"Õpetaja, me tahame, et Sa meile teeksid, mis me Sinult palume!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 36, 'Aga Tema ütles neile: \"Mida te tahate, et ma teile teeksin?\" 37 Nemad ütlesid Talle: \"Anna meile, et me üks Sinu paremal ja teine Sinu vasakui pool saaksime istuda Sinu auhiilguses!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 37, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 38, 'Aga Jeesus ütles neile: \"Te ei tea, mida te palute. Kas te võite juua seda karikat, mida Mina joon, või endid lasta ristida selle ristimisega, millega Mind ristitakse?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 39, 'Aga nad ütlesid Talle: \"Võime küll!\" Jeesus ütles neile: \"Seda karikat, mida Mina joon, peate te jooma, ja selle ristimisega, millega Mind ristitakse, peab teid ristitama,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 40, 'kuid istuda Mu paremal ja vasakul pool ei ole Minu anda, vaid see antakse neile, kellele see on valmistatud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 41, 'Kui need kümme seda kuulsid, said nad pahaseks Jakoobuse ja Johannese peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 42, 'Ja Jeesus kutsus nad Enese juure ja ütles neile: \"Te teate, et need, keda arvatakse rahvaste ülemaiks, valitsevad nende üle ja rahvaste suured tarvitavad võimust nende vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 43, 'Aga nõnda ärgu olgu teie seas, vaid kes teie seast tahab saada suureks, olgu teie teenija,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 44, 'ja kes teie seast tahab olla kõige ülem, olgu kõikide ori!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 45, 'Sest Inimese Poeg ei ole tulnud ennast laskma teenida, vaid ise teenima ja andma oma hinge lunaks paljude eest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 46, 'Ja nad tulid Jeerikosse. Ja kui Ta Jeerikost välja läks ja Tema jüngrid ja suur hulk rahvast, siis istus Timeuse poeg, pime Bartimeus, tee ääres ja kerjas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 47, 'Ja kui ta kuulis, et Jeesus Naatsaretlane on seal, hakkas ta kisendama ja ütlema: \"Jeesus, Taaveti poeg, halasta minu peale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 48, 'Ja paljud sõitlesid teda, et ta jääks vait; kuid ta kisendas veel enam: \"Taaveti poeg, halasta minu peale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 49, 'Ja Jeesus jäi seisma ja ütles: \"Kutsuge ta siia!\" Ja nad kutsusid pimedat ja ütlesid temale: \"Ole julge, tõuse üles, Tema kutsub sind!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 50, 'Ent tema viskas kuue seljast, tõusis püsti ja tuli Jeesuse juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 51, 'Ja Jeesus hakkas temaga rääkima ning ütles: \"Mis sa tahad, et Ma sulle teeksin?\" Ent pime ütles talle: \"Rabunii, et ma jälle näeksin!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 52, 'Jeesus ütles talle: \"Mine, su usk on sind aidanud!\" Ja sedamaid nägi ta jälle ja järgis Teda teekonnal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 1, 'Ja kui nad Jeruusalema ligi said, Betfage ja Betaania poole Õlimäe juure, läkitab Tema kaks Oma jüngreist');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 2, 'ja ütleb neile: \"Minge alevisse, mis teie ees on, ja kohe, kui te sinna saate, leiate kinniseotud sälu, kelle seljas ükski inimene pole istunud; päästke see lahti ja tooge siia!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 3, 'Ja kui keegi teile ütleb: Miks te seda teete? siis ütelge: Issandal on teda tarvis! Siis ta lähetab selle sedamaid siia.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 4, 'Ja nad läksid ja leidsid sälu ukse ääre kinniseotuna väljas teeharul ja päästsid ta lahti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 5, 'Ja mõned neist, kes seal seisid, ütlesid neile: \"Mis te teete, et te sälu lahti päästate?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 6, 'Aga nemad ütlesid neile nõnda, kuidas Jeesus oli käskinud. Siis lasti nad tulema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 7, 'Ja nad tõid sälu Jeesuse juure ja panid oma riided selle peale, ja Tema istus ta selga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 8, 'Ja paljud laotasid oma riided tee peale, aga teised oksi, mida nad raiusid väljadelt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 9, 'Ja need, kes käisid ees ja taga, hüüdsid: \"Hoosianna! Õnnistatud olgu, Kes tuleb Issanda nimel!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 10, 'Õnnistatud olgu meie isa Taaveti kuningriik! Hoosianna kõrges!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 11, 'Ja Ta tuli Jeruusalemma pühakotta. Ja kui Ta kõike oli vaadelnud ja hiline aeg juba käes oli, läks Ta välja Betaaniasse nende kaheteistkümnega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 12, 'Ja kui nad teisel päeval Betaaniast väljusid, oli Tal nälg.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 13, 'Ja nähes kaugelt viigipuud lehis, läks Ta vaatama, kas Ta sellelt midagi leiab. Aga sinna juure jõudes ei leidnud Ta midagi muud kui lehti; sest ei olnud veel viigimarja-aeg.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 14, 'Ja Jeesus hakkas kõnelema ning ütles: \"Ärgu iialgi enam sinust ükski vilja söögu!\" Ja Tema jüngrid kuulsid seda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 15, 'Ja nad tulid Jeruusalemma. Ja kui Jeesus oli pühakotta sisse läinud, hakkas Ta välja ajama neid, kes müüsid ja ostsid pühakojas; ja rahavahetajate lauad ja tuvimüüjate istmed ta lükkas kummuli');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 16, 'ega sallinud, et keegi kandis astjaid pühakojast läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 17, 'Ja Ta õpetas ja ütles neile: \"Eks ole kirjutatud: Minu koda peab hüütama palvekojaks kõigile rahvaile? Kuid teie olete selle teinud röövliauguks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 18, 'Ja kirjatundjad ja ülempreestrid kuulsid seda ja otsisid nõu, kuidas Teda hukata. Kuid nad kartsid Teda, sest kõik rahvas oli hämmastuses Tema õpetuse pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 19, 'Ja kui õhtu tuli, läks Ta linnast välja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 20, 'Ja vara hommikul nägid nad mööda minnes viigipuu juureni ära kuivanud olevat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 21, 'Siis Peetrusele tuli meele Issanda sõna ning ta ütles Temale: \"Rabi, vaata, viigipuu, mille Sa needsid, on ära kuivanud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 22, 'Ja Jeesus vastas ning ütles neile: \"Olgu teil usku Jumalasse!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 23, 'Tõesti, Ma ütlen teile, kui keegi ütleb sellele mäele: Tõuse paigast ja lange merre! ja ei mõtle kaksipidi oma südames, vaid usub, et see, mis ta ütleb, sünnib, siis see saab temale!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 24, 'Sellepärast Ma ütlen teile: Kõik, mida te palute ja anute, uskuge, et te seda saate, siis see saab teile!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 25, 'Ja kui te seisate ja palvetate, siis andke andeks, kui teil midagi on kellegi vastu, et ka teie Isa, Kes on taevas, annaks teile andeks teie üleastumised.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 26, 'Aga kui te andeks ei anna, siis ei anna ka teie Isa, Kes on taevas, teie üleastumisi andeks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 27, 'Ja nad tulid jälle Jeruusalemma. Ja kui Ta pühakojas kõndis, tulid Ta juure ülempreestrid ja kirjatundjad ja vanemad');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 28, 'ja ütlesid Talle: \"Missuguse meelevallaga Sa seda teed? Ja kes on Sulle annud selle meelevalla?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 29, 'Aga Jeesus ütles neile: \"Ma tahan ka teilt küsida üht asja; vastake Mulle ja Mina ütlen teile, missuguse meelevallaga Ma seda teen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 30, 'Kas Johannese ristimine oli taevast või inimestest? Vastake Mulle!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 31, 'Nad arutasid isekeskis ja ütlesid: \"Kui me ütleme taevast, siis Ta ütleb: Mispärast te siis teda ei uskunud?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 32, 'Või ütleme: Inimestest?\" Siis oli neil hirm rahva ees, sest kõik pidasid Johannest tõesti prohvetiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 33, 'Ja nad vastasid Jeesusele ning ütlesid: \"Me ei tea!\" Siis ütles Jeesus neile: \"Ega Minagi teile ütle, missuguse meelevallaga Ma neid asju teen!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 1, 'Ja ta hakkas neile rääkima tähendamissõnadega: \"Inimene istutas viinamäe ja tegi aia ümber ja kaevas sinna surutõrre ja ehitas torni ja andis selle rendile aednike kätte, ja läks ise võõrale maale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 2, 'Ja ta läkitas omal ajal aednike juure sulase aednikelt vastu võtma viinamäe vilja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 3, 'Aga nad võtsid ja peksid teda ja läkitasid ta minema tühje käsi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 4, 'Ja ta läkitas jälle teise sulase nende juure. Seda nad lõid pähe ja teotasid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 5, 'Siis ta läkitas teise. Ja selle nad tapsid. Ja veel muid; ühtesid nad peksid, teised nad tapsid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 6, 'Tal oli veel üks, ta armas poeg. Selle ta läkitas viimseks nende juure, üteldes: Ehk nad häbenevad mu poega?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 7, 'Aga aednikud ütlesid üksteisele: See ongi pärija. Tulge, tapkem ta ära, siis jääb pärand meile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 8, 'Ja nad võtsid ja tapsid tema ja viskasid ta viinamäest välja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 9, 'Mis teeb nüüd viinamäe isand? Ta tuleb ja hukkab aednikud ja annab viinamäe teiste kätte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 10, 'Kas te pole lugenud seda kirja: Kivi, mille hooneehitajad kõrvale heitsid, on saanud nurgakiviks?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 11, 'See tuli Issandalt ja on imeasi meie silmis!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 12, 'Ja nad püüdsid Teda kinni võtta, kuid kartsid rahvast; sest nad mõistsid, et ta selle tähendamissõna oli ütelnud nende kohta. Ja nad jätsid Ta rahule ja läksid ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 13, 'Ja nad läkitavad Tema juure mõningaid varisere ja Heroodese käsilasi, et need tabaksid Teda sõnast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 14, 'Ja need tulid ning ütlesid Talle: \"Õpetaja, me teame, et Sa oled tõearmastaja ega hooli kellestki, sest Sa ei vaata inimese isikule, vaid õpetad Jumala teed tões. Kas on vaja anda keisrile maksu või mitte? Kas anname või ei anna?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 15, 'Tema aga, nähes nende silmakirjalikkust, ütles neile: \"Miks te Mind kiusate? Tooge Mulle näha teenariraha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 16, 'Nemad tõid. Siis Ta ütleb neile: \"Kelle kuju ja peakiiri see on?\" Nemad ütlesid Temale: \"Keisri!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 17, 'Jeesus aga ütles neile: \"Andke keisrile, mis kuulub keisrile, ja Jumalale, mis kuulub Jumalale!\" ja nemad panid Teda imeks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 18, 'Ja Tema juure tuleb sadusere, kes ütlevad, et ei ole ülestõusmist, ja nad küsivad Temalt, üteldes:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 19, '\"Õpetaja, Mooses on meile kirjutanud, et kui kellegi vend sureb ja jätab naise järele, aga ei jäta lapsi, siis võtku ta vend see naine ja soetagu oma vennale järglane.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 20, 'Oli seitse venda; ja esimene võttis naise, ja kui ta suri, ei jätnud ta järglast järele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 21, 'Ja teine võttis naise ja suri, ja temagi ei jätnud järglast, ja samuti kolmas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 22, 'Ja seitsmest ei jätnud keegi järglast järele. Kõige viimaks suri ka naine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 23, 'Kui nad nüüd ülestõusmises üles tõusevad, nende seast kelle naine ta siis on? Sest ta on olnud naiseks seitsmele!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 24, 'Jeesus ütles neile: \"Kas te mitte ei eksi, sellepärast et te ei tunne pühi kirju ega Jumala väge?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 25, 'Sest kui surnuist üles tõustakse, ei võeta naisi ega minda mehele, vaid ollakse nagu Inglid, kes on taevas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 26, 'Aga surnute kohta, et need üles äratatakse, eks te ole lugenud Moosese raamatust kibuvitsapõõsa loos, kuidas Jumal temaga kõneles ning ütles: Mina olen Aabrahami Jumal ja Iisaki Jumal ja Jaakobi Jumal!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 27, 'Ta ei ole mitte surnute Jumal, vaid elavate Jumal! Te eksite küll väga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 28, 'Siis tuli Ta juure üks kirjatundjaist, kes oli kuulnud neid vaidlevat ja märkas, et Ta neile oli hästi kostnud, ja küsis Temalt: \"Missugune käsk on kõige esimene?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 29, 'Jeesus vastas Talle: \"Esimene on see: Kuule Iisrael, Issand sinu Jumal, on ainus Issand!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 30, 'Ja sina armasta Issandat, oma Jumalat, kõigest oma südamest ja kõigest oma hingest ja kõigest oma meelest ja kõigest oma väest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 31, 'Teine on see: Armasta oma ligimest nagu iseennast. Neist suuremat muud käsku ei ole!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 32, 'Ja kirjatundja ütles talle: \"Õige küll, õpetaja, Sina oled tõtt mööda ütelnud, sest Üksainus on olemas ja ei ole muud peale Tema!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 33, 'Ja Teda armastada kõigest südamest ja kõigest meelest ja kõigest väest ja ligimest armastada nagu iseennast, see on rohkem kui kõik põletusohvrid ja muud ohvrid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 34, 'Ja kui Jeesus nägi, et ta vastas mõistlikult, ütles Ta temale: \"Sina ei ole kaugel Jumala Riigist!\" Ja ükski ei julgenud Temalt enam küsida.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 35, 'Ja Jeesus kõneles edasi ning ütles pühakojas õpetades: \"Kuidas kirjatundjad ütlevad, et Kristus on Taaveti poeg?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 36, 'Ütleb ju Taavet ise Püha Vaimu läbi: Issand ütles mu Issandale: Istu Mu paremale käele, kuni Ma Su vaenlased panen Su jalgade alla!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 37, 'Taavet ise nimetab Teda Issandaks. Kuidas ta siis on Tema poeg?\" Ja palju rahvast kuulas Teda hea meelega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 38, 'Ja Ta ütles oma õpetuses: \"Hoiduge kirjatundjaist, kes tahavad käia pikis rüüdes ja armastavad teretust turgudel');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 39, 'ja esimesi istmeid kogudusekodades ja ülemat paika lauas õhtusöömaaegadel, nende eest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 40, 'kes söövad leskede hooned ja loevad silmakirjaks pikki palveid! Need saavad seda raskema hukatuse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 41, 'Ja kui Jeesus ohvrirahakirstu kohal istus, vaatas Ta, kuidas rahvas ohvriraha kirstu pani. Ja paljud rikkad panid sinna palju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 42, 'Siis tuli üks vaene lesknaine ja pani sisse kaks leptonit, see on üks veering.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 43, 'Ja Ta kutsus Oma jüngrid Enese juure ning ütles neile: \"Tõesti, Ma ütlen teile, see vaene lesk pani rohkem ohvrikirstu kui kõik, kes panid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 44, 'Sest need kõik panid oma küllusest, ent tema pani oma vaesusest kõik, mis tal oli, kogu oma elatise!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 1, 'Ja kui Tema pühakojast väljus, ütles üks Tema jüngreist Temale: \"Õpetaja, näe, millised kivid ja millised hooned need on!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 2, 'Siis Jeesus vastas ja ütles talle: \"Kas sa vaatad neid suuri hooneid? Ei jäeta siia mitte kivi kivi peale, mida maha ei kistaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 3, 'Ja kui Ta istus Õlimäel pühakoja vastas, küsisid Temalt Peetrus ja Jakoobus ja Johannes ja Andreas isepäinis:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 4, '\"Ütle meile, millal see sünnib ja mis on tunnuseks, kui see kõik hakkab täide minema?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 5, 'Aga Jeesus hakkas neile rääkima: \"Katsuge, et keegi teid ei eksitaks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 6, 'Paljud tulevad Minu nimel ning ütlevad: \"Mina olen see!\" ja eksitavad paljusid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 7, 'Aga kui te kuulete sõdadest ja sõjasõnumeid, siis ärge ehmuge; see peab sündima, aga ots ei ole veel käes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 8, 'Sest rahvas tõuseb rahva vastu ja riik riigi vastu ja maavärisemisi on mõnes paigas ja on näljahädad. Need on valude algus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 9, 'Aga pidage silmas iseendid: teid antakse ära suurkohtute kätte ja kogudusekodades teid peksetakse, ja teid seatakse maavalitsejate ja kuningate ette Minu pärast neile tunnistuseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 10, 'Ja kõigile rahvaile peab enne kuulutatama Evangeeliumi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 11, 'Aga kui nad teid viivad ja annavad kohtu kätte, ärge muretsege enneaegu, mida teil tuleb rääkida, vaid mis teile antakse sel tunnil, seda rääkige. Sest teie pole rääkijad, vaid Püha Vaim.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 12, 'Ja vend annab surma venna ja isa lapse, ja lapsed hakkavad vastu vanemaile ja saadavad nad surma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 13, 'Ja teid vihatakse kõigilt poolt Minu nime pärast Aga kes otsani jääb püsima, see pääseb.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 14, 'Kui te siis näete hävituse koletisi seisvat seal, kus see ei peaks seisma - kes seda loeb, see pangu tähele - siis põgenegu need, kes on Juudamaal mägedele;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 15, 'kes on katusel, ärgu tulgu maha ja ärgu mingu sisse midagi võtma oma majast,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 16, 'ja kes on väljal, ärgu mingu tagasi võtma oma kuube.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 17, 'Aga häda neile, kes on käima peal, ja neile, kes imetavad neil päevil!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 18, 'Aga paluge, et teie põgenemine ei juhtuks talvel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 19, 'Sest neil päevil on niisugune viletsus, millist ei ole olnud loodu algusest, mille Jumal on loonud, kuni siiamaani, ega tulegi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 20, 'Ja kui Issand ei lühendaks neid päevi, ei pääseks ükski liha; aga äravalitute pärast, kelled Tema on ära valinud, on Ta need päevad lühendanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 21, 'Ja kui siis keegi teile ütleb: \"Vaata, siin on Kristus, vaata seal!\" ärge uskuge.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 22, 'Sest valekristusi ja valeprohveteid tõuseb, ja need teevad tunnustähti ja imesid, et eksitada, kui võimalik, ka äravalituid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 23, 'Aga teie vaadake ette! Mina olen teile kõik ette ütelnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 24, 'Aga neil päevil pärast seda viletsuseaega pimeneb päike ja kuu ei anna oma valgust;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 25, 'ja taeva tähed peavad taevast maha langema ja vägesid, mis on taevas, kõigutatakse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 26, 'Ja siis nähakse Inimese Poega tulevat pilvedes suure väe ja auhiilgusega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 27, 'Ja siis Ta läkitab Inglid ja kogub kokku Oma äravalitud neljast tuulest ilmamaa otsast taeva otsani.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 28, 'Aga viigipuust õppige võrduit: kui ta oksad on juba pungas ja ajavad lehti, siis te tunnete, et suvi on lähedal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 29, 'Nõnda ka teie, kui te näete seda sündivat, teadke, et see on lähedal ukse ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 30, 'Tõesti, Ma ütlen teile, selle põlve rahvas ei lõpe mitte ära, enne kui see kõik sünnib!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 31, 'Taevas ja maa hävivad, kuid Minu sõnad ei hävi mitte!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 32, 'Aga sellest päevast või tunnist ei tea ükski, ei Inglidki taevas ega Poeg, muud kui Isa üksi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 33, 'Vaadake ette, olge valvel, sest te ei tea, millal see aeg on käes!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 34, 'Otsegu inimene, kes läks võõrale maale, jättis maha oma koja ja andis oma sulastele meelevalla, igaühele ta töö, ja käskis uksehoidjat valvata,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 35, 'nõnda valvake; sest te ei tea, millal kojaisand tuleb, kas õhtul või kesköö ajal, või kukelaulu ajal või vara hommikul,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 36, 'et ta ei tuleks äkitselt ja ei leiaks teid magavat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 37, 'Aga mida Ma ütlen teile, ütlen kõigile: Valvake!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 1, 'Aga kahe päeva pärast olid paasaja hapnemata leibade pühad, ja ülempreestrid ja kirjatundjad otsisid, kuidas kavalusega Teda kinni võtta ja ära tappa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 2, 'Sest nad ütlesid: \"Mitte pühade ajal, et ei tõuseks rahva mässu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 3, 'Ja kui Ta istus lauas Betaanias, olles pidalitõbise Siimona kojas, tuli üks naine, kellel oli alabasterriist selge ning kalli nardisalviga. Ja ta murdis alabasterriista katki ja valas salvi Tema pea peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 4, 'Aga seal olid mõned, kes pahameelega ütlesid üksteisele: \"Mistarvis on see salvi raiskamine?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 5, 'Sest selle oleks võinud ära müüa enam kui kolmesaja teenari eest ja raha vaestele anda.\" Ja nad sõitlesid naist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 6, 'Aga Jeesus ütles: \"Jätke ta rahule! Miks te teete talle südamevalu? Ta on teinud Mulle heateo.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 7, 'Sest vaeseid on ikka teie juures ja, kui te tahate, võite neile head teha; aga Mind ei ole teil mitte alati.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 8, 'Ta on teinud, mis ta võis. Ta on ennakult Mu ihu võidnud matmiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 9, 'Tõesti, Ma ütlen teile, kus iganes Evangeeliumi kuulutatakse kõiges maailmas, peab ka räägitama tema mälestuseks, mis tema on teinud.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 10, 'Ja Juudas Iskariot, üks neist kaheteistkümnest, läks ülempreestrite juure, et Teda neile ära anda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 11, 'Kui nad seda kuulsid, said nad rõõmsaks ja lubasid anda talle raha. Ja ta hakkas otsima, kuidas ta saaks teda ära anda parajal ajal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 12, 'Ja hapnemata leibade esimesel päeval, kui paasatalle veristati, ütlesid Tema jüngrid Talle: \"Kuhu Sa tahad, et me läheme ja teeme ettevalmistusi Sulle paasatalle söömiseks?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 13, 'Ja Tema läkitas kaks Oma jüngreist ning ütles neile: \"Minge linna, ja teile tuleb vastu inimene, kes kannab veekruusi; järgige teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 14, 'Ja seal, kuhu ta sisse läheb, ütelge majaisandale: \"Õpetaja küsib: Kus on võõrastetuba, milles Ma võin Oma jüngritega süüa paasatalle?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 15, 'Ja tema näitab teile suure ülemise toa, mis on valmis korraldatud. Seal valmistage meile.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 16, 'Ja Tema jüngrid läksid ära ja tulid linna ning leidsid nõnda, nagu Ta neile oli ütelnud. Ja nad valmistasid paasatalle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 17, 'Aga kui õhtu jõudis, tuleb Tema nende kaheteistkümnega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 18, 'Ja kui nad lauas istusid ja sõid, ütles Jeesus: \"Tõesti Ma ütlen teile, üks teie seast annab Mu ära, üks, kes Minuga ühes sööb!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 19, 'Siis nad hakkasid kurvaks minema ja üksteise järele Temale ütlema: \"Ega ometi mina?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 20, 'Tema ütles neile: \"Üks neist kaheteistkümnest, kes ühes Minuga pistab käe vaagnasse, annab Mind ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 21, 'Inimese Poeg läheb küll ära, nõnda nagu Temast on kirjutatud, kuid häda sellele inimesele, kelle läbi Inimese Poeg ära antakse. Hea oleks niisugusele inimesele, kui ta ei oleks sündinud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 22, 'Ja kui nad sõid, võttis Jeesus leiva, õnnistas, murdis ja andis neile ning ütles: \"Võtke! See on Minu ihu.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 23, 'Ja Ta võttis karika, tänas ja andis neile; ja nad kõik jõid selle seest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 24, 'Ja Ta ütles neile: \"See on Minu veri, lepingu veri, mis ära valatakse paljude eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 25, 'Tõesti Ma ütlen teile: Mina ei joo enam viinapuu viljast kuni selle päevani, mil Ma joon uut Jumala Riigis!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 26, 'Ja kui nad kiituslaulu olid laulnud, läksid nad välja Õlimäele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 27, 'Ja Jeesus ütleb neile: \"Te kõik taganete Minust, sest kirjutatud on: Ma löön karjast ja lambad pillutatakse!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 28, 'Aga pärast Oma ülestõusmist Ma lähen teie eele Galileasse.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 29, 'Siis Peetrus ütleb Temale: \"Kui ka kõik Sinust taganevad, siis mina mitte!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 30, 'Jeesus ütles talle: \"Tõesti Ma ütlen sulle, täna, sellel ööl, enne kui kukk on kaks korda laulnud, salgad sina Mind kolm korda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 31, 'Aga tema ütles veel eriti: \"Kui ma Sinuga peaksin ka surema, ei salga ma Sind mitte!\" Ja samuti ütlesid ka kõik.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 32, 'Ja nad tulid paika, mille nimi on Ketsemani, ja Tema ütles Oma jüngritele: \"Istuge siin niikaua kui Ma palvetan!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 33, 'Ja Ta võttis Enesega ühes Peetruse, Jakoobuse ja Johannese ja hakkas vabisema ning suurt õudsust tundma,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 34, 'ja Ta ütles neile: \"Minu hing on väga kurb surmani, jääge siia ja valvake!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 35, 'Ja Ta läks pisut eemale ja heitis maha maa peale ning palus, et see tund, kui on võimalik, läheks Temast mööda,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 36, 'ja Ta ütles: \"Abba Isa! Sinul on kõik võimalik; võta see karikas Minult ära! Siiski mitte, mida Mina tahan, vaid mida Sina tahad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 37, 'Ja Ta tuleb ja leiab nad magamast, ja ütleb Peetrusele: \"Siimon, kas sa magad? Kas sa ei suuda ühtki tundi valvata?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 38, 'Valvake ja paluge, et te ei satuks kiusatusse! Vaim on küll valmis, kuid liha on nõder!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 39, 'Ja Ta läks jälle ära ja palvetas sama palvet.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 40, 'Ja Ta tuli tagasi ja leidis nad taas magamast, sest nende silmad olid rasked unest, ja nad ei teadnud, mida Temale vastata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 41, 'Ja Ta tuli kolmat puhku ja ütles neile: \"Te ikka veel magate ja puhkate; aeg on möödas, tund on tulnud! Vaata, Inimese Poeg antakse patuste kätte!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 42, 'Tõuske üles, lähme! Vaata, see, kes Mu ära annab, on lähedal!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 43, 'Ja sedamaid, kui Tema alles rääkis, saabus Juudas, üks neist kaheteistkümnest, ja ühes temaga jõuk mõõkade ja nuiadega ülempreestrite ja kirjatundjate ja vanemate poolt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 44, 'Aga see, kes Tema ära andis, oli neile annud tähe ning ütelnud: \"Keda ma suudlen, See Ta on; Tema võtke kinni ja viige Ta kindla valve all minema!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 45, 'Ja kui ta sinna tuli, astus ta kohe Tema juure ja ütles: \"Rabi!\" ja andis Temale suud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 46, 'Aga nemad pistsid oma käed Tema külge ja võtsid Ta kinni.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 47, 'Siis keegi juuresseisjaist tõmbas mõõga ja lõi ülempreestri sulast ning raius ta kõrva ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 48, 'Ja Jeesus kostis ja ütles neile: \"Otsegu röövli vastu te olete väljunud mõõkade ja nuiadega Mind kinni võtma!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 49, 'Ma olen olnud iga päev teie juures pühakojas õpetamas, ja te ei ole Mind mitte kinni võtnud; kuid see sünnib, et kirjad täide läheksid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 50, 'Siis kõik jätsid Ta maha ja põgenesid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 51, 'Ja üks noor mees järgis Teda; sel oli linane riie seljas paljal ihul; ja nad võtsid ta kinni.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 52, 'Aga tema jättis linase riide maha ja põgenes ära alasti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 53, 'Ja nad viisid Jeesuse ülempreestri juure, ja sinna tulid kokku kõik ülempreestrid ja vanemad ja kirjatundjad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 54, 'Ja Peetrus järgis Teda kaugelt kuni ülempreestri õue. Ja ta istus ühes sulastega ja soojendas ennast tule ääres.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 55, 'Aga ülempreestrid ja kogu Suurkohus otsisid Jeesuse vastu tunnistust, et saata Teda surma, kuid ei leidnud mitte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 56, 'Sest paljud tunnistasid valet Tema peale, aga tunnistused ei läinud ühte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 57, 'Seal tõusid mõned ning tunnistasid valet Tema peale, üteldes:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 58, '\"Me oleme kuulnud Teda ütlevat: Mina kisun selle kätega tehtud templi maha ja ehitan kolme päevaga teise, mis ei ole kätega tehtud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 59, 'Ja nõndagi ei läinud nende tunnistus ühte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 60, 'Siis ülempreester astus keskele ja küsis Jeesuselt ning ütles: \"Eks Sa kosta midagi selle vastu, mida need tunnistavad Sinu kohta?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 61, 'Aga Tema jäi vait ega vastanud midagi. Taas küsis Talt ülempreester ja ütles Temale: \"Kas Sina oled Kristus, Kiidetava Poeg?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 62, 'Ja Jeesus ütles: \"Mina olen See, ja te peate nägema Inimese Poja istuvat Jumala väe paremal poolel ja tulevat taeva pilvedega.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 63, 'Siis rebestas ülempreester oma kuue ja ütles: \"Mis meil veel on vaja tunnistajaid?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 64, 'Te olete kuulnud jumalapilget! Mis te arvate?\" Aga nad kõik otsustasid, et Ta on surma väärt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 65, 'Ja mõned hakkasid Ta peale sülitama ja Ta nägu kinni katma ja Teda rusikatega lööma vastu kõrvu ja Temale ütlema: \"Kuuluta kui prohvet!\" Ja sulased lõid teda keppidega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 66, 'Ja kui Peetrus oli all õues, tuli üks ülempreestri tüdrukuist,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 67, 'ja nähes Peetrust end soojendamas, vaatas ta temale otsa ja ütles: \"Ka sina olid ühes Selle Naatsareti Jeesusega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 68, 'Aga tema salgas, üteldes: \"Ei ma tea ega saa aru, mida sa räägid!\" Ja ta läks välja eesõue. Ja kukk laulis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 69, 'Ja nähes teda seal, hakkas tüdruk jälle ütlema neile, kes seal seisid: \"See on üks nende seast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 70, 'Aga ta salgas taas. Ja vähe aja pärast ütlesid juuresseisjad jälle Peetrusele: \"Tõesti sa oled nende seast; sest sa oled galilealane!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 71, 'Aga tema hakkas needma ja vanduma: \"Ei ma tunne Seda Inimest, kellest te räägite!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 72, 'Ja kohe laulis kukk teist korda. Siis Peetrusele tuli meele sõna, mis Jeesus temale oli ütelnud: \"Enne kui kukk on kaks korda laulnud, salgad sa Mind kolm korda!\" Ja ta ruttas välja ja nuttis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 1, 'Ja kohe vara hommikul pidasid ülempreestrid ühes vanemate ja kirjatundjatega ja kõik Suurkohus nõu ja sidusid Jeesuse kinni ning viisid Tema ära ja andsid Ta Pilaatuse kätte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 2, 'Ja Pilaatus küsis Temalt: \"Oled Sa Juutide Kuningas?\" Tema vastas ning ütles Temale: \"Jah, olen!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 3, 'Ja ülempreestrid kaebasid palju Tema peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 4, 'Aga Pilaatus küsis Talt jälle ning ütles: \"Kas Sa midagi ei vasta? Vaata, kui raskesti nad Sind süüdistavad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 5, 'Kuid Jeesus ei vastanud enam midagi, nõnda et Pilaatus imestas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 6, 'Aga igaks pühaks laskis ta neile vabaks ühe vangi, keda nad talt palusid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 7, 'Ja seal oli mees nimega Barabas, vangi pandud ühes mässulistega, kes olid mässus toime pannud mõrva.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 8, 'Ja rahvas läks üles ja hakkas paluma teha nii, nagu ta ikka neile oli teinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 9, 'Aga Pilaatus vastas neile ning ütles: \"Kas tahate, et ma teile lasen vabaks Juutide Kuninga?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 10, 'Sest ta teadis, et ülempreestrid Tema olid annud ta kätte kadedusest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 11, 'Aga ülempreestrid kihutasid rahvast, et ta neile laseks vabaks pigemini Barabase.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 12, 'Siis vastas Pilaatus jälle ning ütles neile: \"Mis ma siis teen Temaga, keda te nimetate Juutide Kuningaks?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 13, 'Aga nemad kisendasid taas \"Löö Ta risti!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 14, 'Pilaatus ütles neile: \"Mis kurja Ta siis on teinud?\" Aga nad kisendasid veel enam: \"Löö Ta risti!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 15, 'Ent Pilaatus, tahtes teha rahva meele järele, andis neile vabaks Barabase, aga Jeesust ta laskis rooskadega peksta, et Teda risti lüüa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 16, 'Siis viisid sõjamehed Ta siseõue, see on kohtukotta, ja kutsusid kokku terve sõdurite salga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 17, 'Ja nad riietasid Ta purpurkuuega ja punusid kibuvitsakrooni ning panid Temale pähe');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 18, 'ja hakkasid Teda teretama: \"Tere, Juutide Kuningas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 19, 'Ja nad lõid Teda pähe pillirooga ja sülitasid Tema peale, heitsid põlvili maha ja kummardasid Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 20, 'Ja kui nad Teda olid naernud, võtsid nad purpurkuue Tema seljast ära ja panid Tema Omad riided Temale selga ja viisid Ta välja risti löödavaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 21, 'Ja nad sundisid üht möödaminevat meest, Siimonat Küreenest, kes tuli väljalt, Aleksandrose ja Ruufuse isa, kandma Tema risti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 22, 'Ja nad viisid Ta Kolgata paika, see on meie keeli: Pealae ase.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 23, 'Ja nad pakkusid Temale juua mürriga segatud viina. Kuid Tema ei võtnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 24, 'Ja nad löövad Ta risti ja jagavad Ta riided, heites liisku selle kohta, mis keegi saab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 25, 'Ent see oli kolmas tund, kui nad Ta risti lõid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 26, 'Ja pealkirjaks oli kirjutatud Tema süü: \"Juutide Kuningas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 27, 'Ka lõid nad ühes Temaga risti kaks röövlit, ühe paremale ja teise vasakule poolele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 28, 'Siis läks täide Kiri, mis ütleb: \"Ja Teda arvati üleastujate hulka!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 29, 'Ja möödaminejad pilkasid Teda, vangutasid pead ja ütlesid: \"Oh Sind, Kes Sa maha kisud templi ja ehitad üles kolme päevaga,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 30, 'aita Iseennast ja astu ristilt maha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 31, 'Samuti naersid Teda ka ülempreestrid isekeskis ühes kirjatundjatega ja ütlesid: \"Muid Ta on aidanud, Iseennast ta ei saa aidata!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 32, 'Kristus, Iisraeli Kuningas, astugu nüüd ristilt maha, et me näeksime ja usuksime!\" Ja need, kes olid ühes Temaga risti löödud, teotasid Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 33, 'Ja kuuendal tunnil tekkis pimedus üle kogu maa ja kestis üheksanda tunnini.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 34, 'Ja üheksandal tunnil kisendas Jeesus suure häälega ning ütles: \"Eloii, Eloii, lamaa sabahtaani!\" see on meie keeli: Mu Jumal, Mu Jumal, miks Sa Mind maha jätsid?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 35, 'Kui mõned juuresseisjaist seda kuulsid, ütlesid nad: \"Vaata, Ta kutsub Eelijat!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 36, 'Siis jooksis keegi ja täitis käsna äädikaga, pistis selle pilliroo otsa ja jootis Teda ning ütles: \"Oodake, saame näha, kas Eelija tuleb Teda maha võtma!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 37, 'Aga Jeesus kisendas suure häälega ja heitis hinge.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 38, 'Ja templi eesriie kärises lõhki kahest servast, ülemast alamani!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 39, 'Aga kui pealik, kes Tema ees seisis, nägi, et Ta nõnda kisendades hinge heitis, ütles ta: \"Tõesti, See Inimene oli Jumala Poeg!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 40, 'Seal oli ka naisi kaugelt vaatamas, nende seas ka Maarja Magdaleena ja Maarja, noorema Jakoobuse ja Joosese ema, ja Saloome,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 41, 'kes olid Teda järginud ja Teda teeninud, kui Ta Galileamaal oli, ja palju muid naisi, kes ühes Temaga olid läinud üles Jeruusalemma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 42, 'Aga kui päev juba oli jõudmas õhtule, kuna käes oli pühade valmistuspäev, see on hingamispäeva eelpäev,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 43, 'siis tuli Joosep Arimaatiast, lugupeetud kohtunõunik, kes ka ise ootas Jumala Riiki, ja julges minna Pilaatuse juure ja paluda enesele Jeesuse ihu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 44, 'Siis Pilaatus imestas, et ta juba oli surnud, ja kutsus enese juure pealiku ning küsis temalt, kas Ta ammu suri.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 45, 'Ja kui ta pealikult oli saanud teate, andis ta surnukeha Joosepile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 46, 'Tema ostis lõuendit, võttis Ta keha ning mähkis Ta lõuendisse ja asetas Ta hauda, mis oli raiutud kaljusse, ja veeretas kivi uksele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 47, 'Aga Maarja Magdaleena ja Maarja, Joosese ema, vaatasid, kuhu Ta asetati.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 1, 'Ja kui hingamispäev oli möödunud, ostsid Maarja Magdaleena ja Maarja, Jakoobuse ema, ja Saloome lõhnaaineid, et minna Teda võidma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 2, 'Ja nädala esimesel päeval tulid nad väga vara päeva tõustes hauale');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 3, 'ja ütlesid isekeskis: \"Kes veeretab meile ära kivi haua ukselt?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 4, 'Ja silmi tõstes nad nägid, et kivi oli ära veeretatud; aga see oli väga suur.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 5, 'Ja nad läksid hauakoopasse ja nägid paremal poolel noore mehe istuvat, pikk valge rüü seljas. Ja nad kohkusid väga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 6, 'Aga tema ütles neile: \"Ärge kohkuge! Te otsite Jeesust Naatsaretlast, Kes oli risti löödud; Ta on üles tõusnud, Teda ei ole siin. Ennäe aset, kuhu nad Ta panid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 7, 'Ent minge ütelge Tema jüngritele, ka Peetrusele, et Ta läheb teie eele Galileasse; seal te saate Teda näha, nõnda nagu Ta teile ütles.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 8, 'Nad tulid välja, jooksid haua juurest ära, sest neid oli vallanud väristus ja hämmastus; ja nad ei ütelnud kellelegi midagi, sest nad kartsid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 9, 'Aga kui Tema nädala esimesel päeval vara oli üles tõusnud, ilmus Ta esiti Maarja Magdaleenale, kellest Tema oli välja ajanud seitse kurja vaimu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 10, 'Tema läks ja teatas neile, kes Jeesusega olid olnud, ja leinasid ning nutsid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 11, 'Ja kui need kuulsid, et Ta elab ja Maarja Magdaleena olevat Teda näinud, ei uskunud nad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 12, 'Selle järele Ta ilmus teisel näol kahele nende seast, kui need olid teel maale minemas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 13, 'Ja ka need läksid ja kuulutasid seda teistele, kuid neidki nad ei uskunud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 14, 'Pärast Ta ilmus neile üheteistkümnele, kui nad lauas istusid, ja sõitles nende uskmatust ning kõva südant, sest nad ei olnud uskunud neid, kes olid näinud Teda üles tõusnud olevat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 15, 'Ja Ta ütles neile: \"Minge kõike maailma ja kuulutage Evangeeliumi kõigele loodule.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 16, 'Kes usub ja keda ristitakse, see saab õndsaks; aga kes ei usu, see mõistetakse hukka.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 17, 'Aga tunnustähed järgivad neid, kes usuvad. Minu nimel nad ajavad kurje vaime välja, räägivad uusi keeli,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 18, 'võtavad üles madusid, ja kui nad mõnd surmajooki joovad, ei tee see neile mitte kahju; haigete peale nad panevad käed, ja need saavad terveks.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 19, 'Ja Issand Jeesus, kui Ta oli nendega rääkinud, võeti üles taevasse ja Ta istus Isa paremale poolele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 20, 'Aga nemad läksid välja ja jutlustasid igal pool. Ja Issand töötas ühes nendega ning kinnitas sõna tunnustähtedega, mis pärast seda sündisid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 1, 'Et juba mitmed on võtnud kätte kirjutada loo neist asjust, mis meie seas tõsikindlalt on sündinud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 2, 'nõnda nagu meile on jutustanud need, kes ise on seda näinud algusest ja on olnud sõna sulased,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 3, 'siis olen minagi arvanud heaks algusest peale kõike hoolega uurida ja järgemööda kirja panna sinule, kõrgeauline Teofllos,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 4, 'et sa õpiksid tundma nende asjade kindla tõe, mida sulle on õpetatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 5, 'Heroodese, Judea kuninga ajal oli preester, nimega Sakarias, Abija järjekorrast, ja temal oli naine Aaroni tütardest, ja selle nimi oli Eliisabet.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 6, 'Nad olid mõlemad õiged Jumala ees ja elasid kõigis käskudes ja Issanda seadustes laitmatult.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 7, 'Ja neil ei olnud last, sest Eliisabet oli sigimatu ja nad mõlemad olid väga elatanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 8, 'Aga sündis, kui ta oma teenistuse järjekorras oli preestritalitusi toimetamas Jumala ees,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 9, 'et preestriameti kombe järele langes liisk temale minna suitsutama, ja ta läks Issanda templisse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 10, 'Ja kõik rahvahulk oli väljas palvetamas suitsutuse tunnil.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 11, 'Seal ilmus temale Issanda Ingel, seistes suitsutusaltari paremal pool.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 12, 'Teda nähes Sakarias ehmus ja hirm valdas teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 13, 'Aga Ingel ütles talle: \"Ära karda, Sakarias, sest su palvet on kuuldud ja su naine Eliisabet toob sulle ilmale poja, ja sa paned temale nimeks Johannes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 14, 'Ja temast on sul rõõm ning hea meel, ja paljud rõõmustuvad tema sündimisest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 15, 'Sest ta peab suur olema Issanda ees; ja viina ega vägijooki ei joo ta mitte, ja ta täitub Püha Vaimuga juba oma ema ihus;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 16, 'ja ta pöörab palju Iisraeli lapsi Issanda, nende Jumala poole;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 17, 'ja ta käib Tema eel Eeiija vaimus ja väes, et käänata isade südamed laste poole ja sõnakuulmatud õigete meelsuse poole, et soetada Issandale valmistatud rahvast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 18, 'Ja Sakarias ütles Inglile: \"Millest ma seda võin ära tunda? Mina olen ju vana ja mu naine on oma päevade poolest väga elatanud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 19, 'Ingel vastas ning ütles temale: \"Mina olen Gabriel, kes seisab Jumala ees, ja ma olen läkitatud rääkima sinuga ja kuulutama sulle seda rõõmusõnumit!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 20, 'Ja vaata, sa jääd keeletumaks ega saa rääkida päevani, mii see sünnib, sellepärast et sa ei ole uskunud mu sõnu, mis lähevad täide omal ajal!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 21, 'Ja rahvas oli ootamas Sakariast ja pani imeks, et ta nii kaua viibis templis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 22, 'Aga väljudes ta ei saanud nendega rääkida. Siis nad said aru, et ta templis oli näinud nägemust. Ja tema andis neile käega märku ning jäi keeletumaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 23, 'Ja kui tema teenistuskorra päevad täis said, siis ta läks koju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 24, 'Aga pärast neid päevi sai tema naine Eliisabet käima peale ja pidas ennast varjul viis kuud, üteldes:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 25, '\"Nõnda on Issand mulle teinud neil päevil, mil Tema on vaadanud minu peale, et minult ära võtta mu teotus inimeste seas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 26, 'Aga kuuendas kuus läkitas Jumal Ingel Gabrieli Galilea linna, mille nimi on Naatsaret,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 27, 'Neitsi juure, Kes oli kihlatud Joosepi-nimelise mehega Taaveti soost. Ja neitsi nimi oli Maarja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 28, 'Ja tulles sisse Tema juure, ütles Ingel: \"Tere, Sa armuleidnu, Issand olgu Sinuga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 29, 'Aga Tema ehmus väga sellest sõnast ja mõtles, mis see teretus võiks tähendada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 30, 'Siis Ingel ütles temale: \"Ära karda, Maarja, sest Sa oled leidnud armu Jumala juures!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 31, 'Ja vaata, Sa saad käima peale ning tood ilmale Poja ja paned Temale nimeks Jeesus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 32, 'Tema saab suur olema ja Teda peab hüütama Kõigekõrgema Pojaks, ja Issand Jumal annab Temale Ta isa Taaveti aujärje,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 33, 'ja Ta valitseb igavesti Jaakobi soo üle ning Tema Kuningriigil ei ole otsa!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 34, 'Aga Maarja ütles Inglile: \"Kuidas see võib sündida, kuna Ma mehest ei tea?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 35, 'ka Püha, Kes Sinust sünnib, nimetatama Jumala Pojaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 36, 'Ja vaata, Su sugulane Eliisabet, temagi on pojaga käima peal oma vanas eas, ja see on kuues kuu temal, keda üteldi olevat sigimatu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 37, 'sest Jumalal ei ole ükski asi võimatu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 38, 'Aga Maarja ütles: \"Vaata, siin on Issanda Ümmardaja; Mulle sündigu su sõna järele!\" Ja Ingel läks ära Tema juurest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 39, 'Neil päevil asus Maarja teele ja läks rutuga mägestikku Juuda linna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 40, 'Ja Ta tuli Sakariase kotta ja teretas Eliisabetti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 41, 'Ja sündis, kui Eliisabet kuulis Maarja teretust, et laps hüppas tema ihus. Ja Eliisabet sai täis Püha Vaimu');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 42, 'ja hüüdis suure häälega ning ütles: \"Õnnistatud oled Sina naiste seas ja õnnistatud on Sinu ihu sugu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 43, 'Kust see tuleb, et minu Issanda Ema on tulnud minu juure?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 44, 'Sest vaata, kui Su teretuse hääl kostis mu kõrvu, siis hüppas lapsuke suurest rõõmust mu ihus!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 45, 'Ja õnnis see, kes on uskunud, et läheb täide, mis Issand Temale on ütelnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 46, 'Siis Maarja ütles: \"Mu hing ülistab väga Issandat');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 47, 'ja Mu vaim rõõmutseb Jumalas, Minu Õnnistegijas;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 48, 'sest Ta on vaadanud oma Ümmardaja alanduse peale! Vaata, nüüdsest peake kiidavad Mind õndsaks kõik sugupõlved,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 49, 'sest Minule on teinud suuri asju Vägev, ja Tema nimi on püha,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 50, 'ja Tema halastus kestab põlvest põlve neile, kes Teda kardavad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 51, 'Ta on Oma käsivarrega teinud võimsa teo, Ta on pillutanud need, kes on suurelised oma südame meele poolest;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 52, 'Ta on tõuganud maha vägevad aujärgedelt ja ülendanud alandlikud;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 53, 'näljased on Ta täitnud heade andidega, aga rikkad tühjalt minema saatnud;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 54, 'Ta on vastu võtnud Oma sulase Iisraeli, meele tuletades Oma halastust,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 55, 'nõnda nagu Ta on rääkinud meie isadele, Aabrahamile ja tema soole kuni igavikuni!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 56, 'Ja Maarja jäi tema juure umbes kolm kuud ja läks siis tagasi koju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 57, 'Aga Eliisabeti mahasaamise aeg tuli kätte, ja ta tõi poja ilmale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 58, 'Kui tema üleaedsed ja sugulased kuulsid, et Issand oli temale osutanud suurt halastust, olid nad rõõmsad ühes temaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 59, 'Ja nad tulid kaheksandal päeval lapsukest ümber lõikama ja nimetasid tema ta isa järele Sakariaseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 60, 'Aga tema ema kostis ning ütles: \"Ei sugugi, vaid tema nimi peab olema Johannes!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 61, 'Kuid nemad ütlesid temale: \"Su suguvõsas pole ühtki, keda hüütakse selle nimega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 62, 'Ja viibates isale küsisid nad, kuidas tema tahaks teda nimetada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 63, 'Tema nõudis lauakese ja kirjutas selle peale nõnda: \"Johannes on tema nimi.\" Ja nad kõik panid seda imeks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 64, 'Aga sedamaid läks tema suu ning keel lahti, ja ta hakkas rääkima, kiites Jumalat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 65, 'Ja kartus tuli kõikidele, kes elasid nende ümbruses, ja kõigist neist asjust kõneldi Juuda mägestikus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 66, 'Ja kõik, kes seda kuulsid, võtsid seda oma südamesse ja ütlesid: \"Mis saab küll sellest lapsest?\" Sest Issanda käsi oli temaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 67, 'Ja Sakarias, tema isa, sai täis Püha Vaimu ja hakkas ennustama, üteldes:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 68, '\"Kiidetud olgu Issand, Iisraeli Jumal, et Ta on tulnud katsuma Oma rahvast ja on saatnud Oma rahvale lunastuse');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 69, 'ning on meile äratanud päästesarve Taaveti, Oma sulase kojast');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 70, '- nõnda nagu Ta maailma ajastu algusest on rääkinud Oma pühade prohvetite suu kaudu -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 71, 'päästu meie vaenlasist ja kõigi nende käest, kes meid vihkavad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 72, 'et osutada halastust meie esiisadele ja tuletada meele oma püha lepingut,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 73, 'seda vannet, mille Ta vandus meie isale Aabrahamile,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 74, 'et ta laseb meid päästetuina meie vaenlaste käest kartmatult');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 75, 'Teda teenida pühaduses ja õiguses Tema ees kogu meie eluaja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 76, 'Ja sina, lapsuke, sind peab hüütama Kõigekõrgema prohvetiks, sest sina lähed Issanda palge eel valmistama Tema teid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 77, 'et anda Tema rahvale pääste tunnetus nende pattude andeksandmises');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 78, 'meie Jumala südamliku halastuse läbi, millega meid on tulnud katsuma päevatõus kõrgest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 79, 'paistma meile, kes istume pimeduses ja surma varjus, ja juhtima meie jalgu rahuteele!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 80, 'Aga lapsuke kasvas ja sai tugevaks vaimus. Ja ta oli kõrves selle päevani, mil ta esines Iisraelile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 1, 'Neil päevil sündis, et keiser Augustus andis käsu üles kirjutada kogu maailm.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 2, 'See üleskirjutus oli esimene ja toimus, kui Küreenius oli Süüria maavalitseja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 3, 'Ja kõik läksid endid laskma kirja panna, igaüks oma linna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 4, 'Siis läks ka Joosep Galileast Naatsareti linnast üles Juudamaale Taaveti linna, mida hüütakse Petlemaks, sest ta oli pärit Taaveti kojast ning soost,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 5, 'ennast laskma kirja panna ühes Maarja, oma kihlatud naisega, kes oli käima peal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 6, 'Aga nende seal olles sai aeg täis, et Maarja pidi maha saama,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 7, 'ja Ta tõi ilmale Oma esimese Poja ja mähkis Ta mähkmetesse ja asetas Ta sõime, sest neil polnud muud aset majas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 8, 'Ja seal paigus oli karjaseid väljal öitsis pidamas valvet öösel oma karja juures.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 9, 'Ja Issanda Ingel astus nende ette ja Issanda auhiilgus paistis nende ümber, ja nemad kartsid üliväga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 10, 'Ja Ingel ütles neile: \"Ärge kartke! Sest vaata, ma kuulutan teile suurt rõõmu, mis saab osaks kõigele rahvale:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 11, 'sest teile on täna Taaveti linnas sündinud Õnnistegija, Kes on Issand Kristus!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 12, 'Ja see olgu teile tunnuseks: te leiate lapse mähitud ja sõimes magavat!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 13, 'Ja äkitselt oli Ingliga ühes taeva sõjaväe hulk; need kiitsid Jumalat ning ütlesid:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 14, '\"Au olgu Jumalale kõrges ja maa peal rahu inimeste seas, kelledest Temal on hea meel!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 15, 'Kui siis Inglid olid läinud nende juurest ära taevasse, ütlesid karjased üksteisele: \"Läki nüüd Petlemma ja vaadakem seda asja, mis on sündinud, mis Issand on meile teada annud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 16, 'Ja nad tulid tõtates ning leidsid Maarja ja Joosepi ja lapsukese, kes magas sõimes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 17, 'Aga kui nad seda nägid, teatasid nad asjast, mis neile oli üteldud selle lapsukese kohta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 18, 'Ja kõik, kes seda kuulsid, panid imeks, mida karjased neile rääkisid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 19, 'Ent Maarja pidas kõik need sõnad meeles ning mõtles nendele Oma südames.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 20, 'Ja karjased läksid tagasi Jumalale au andes ja Teda kiites kõige eest, mis nad olid kuulnud ja näinud, nõnda nagu neile oli üteldud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 21, 'Ja kui kaheksa päeva täis sai ja Laps pidi ümber lõigatama, pandi Temale nimeks Jeesus, mille Ingel oli pannud, enne kui Laps sai Ema ihusse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 22, 'Ja kui nende puhastuspäevad täis said Moosese käsuõpetuse järele, viisid nad Lapse Jeruusalemma, et Teda seada Issanda ette -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 23, 'nagu on kirjutatud Issanda käsuõpetuses: \"Iga poeglaps, kes on ema esmasündinu, loetagu pühitsetuks Issandale\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 24, '- ja tuua ohver, nagu Issanda käsuõpetuses on üteldud: paar turteltuvi või kaks muud tuvi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 25, 'Ja vaata, Jeruusalemas oli mees, Siimeon nimi. See mees oli õige ja jumalakartlik ning ootas Iisraeli troosti; ja Püha Vaim oli tema peal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 26, 'Temale oli Püha Vaim ilmutanud, et ta ei näe surma, enne kui ta on näinud Issanda Võitut.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 27, 'Ja ta tuli Vaimu sunnil pühakotta. Ja kui vanemad Lapsukese Jeesuse sinna tõid, et Tema pärast teha käsuõpetuse kombe järele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 28, 'siis ta võttis Tema sülle, kiitis Jumalat ning ütles:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 29, '\"Issand, nüüd Sa lased Oma sulase rahus minna Oma sõna järele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 30, 'sest mu silmad on näinud Sinu päästet,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 31, 'mille Sa oled valmistanud kõigi rahvaste nähes,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 32, 'valguseks, mis peab ilmuma paganaile ja auhiilguseks Oma rahvale Iisraelile!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 33, 'Ja Ta isa ja Ema panid imeks, mida Temast räägiti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 34, 'Ja Siimeon õnnistas neid ja ütles Maarjale, Tema Emale: \"Vaata, Seesinane on seatud langemiseks ja tõusmiseks paljudele Iisraelis ja tähiseks, mille vastu räägitakse -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 35, 'ent Sinu Omastki hingest peab mõõk läbi tungima - et saaksid avalikuks paljude südamete mõtlemised!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 36, 'Ja seal oli naisprohvet Anna, Fanueli tütar, Aaseri suguharust. See oli väga elatanud ja oli elanud oma mehega seitse aastat pärast oma neitsipõlve,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 37, 'ja tema oli lesk, ligi kaheksakümmend neli aastat vana. See ei läinud ära pühakojast, vaid teenis Jumalat paastumiste ja palvetamistega ööd ja päevad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 38, 'Tema tuli sinna samal tunnil ja ülistas Jumalat ning kõneles Temast kõikidele, kes ootasid Jeruusalema lunastust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 39, 'Ja kui nad olid lõpetanud kõik, mis tuli teha Issanda käsuõpetuse järele, läksid nad tagasi Galileamaale oma Naatsareti linna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 40, 'Aga Lapsuke kasvas ja sai tugevaks ning täitus tarkusega. Ja Jumala arm oli Tema peal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 41, 'Ja Tema vanemad käisid iga aasta Jeruusalemas paasapühil.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 42, 'Ja kui Ta sai kaheteistkümneaastaseks, läksid nad üles Jeruusalemma pühadeaja kommet mööda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 43, 'Ja kui pärast nende päevade möödumist nad olid kodu poole minemas, jäi Laps Jeesus Jeruusalemma; ja Tema vanemad ei pannud seda tähele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 44, 'Nad arvasid Tema olevat kaasteekäijate seas, ja käisid ühe ühe päeva tee ning otsisid Teda sugulaste ja tuttavate juurest;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 45, 'ja kui nad Teda ei leidnud, läksid nad tagasi Jeruusalemma ja otsisid Teda sealt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 46, 'Ja kolme päeva pärast sündis, et nad Ta leidsid pühakojas istumast õpetajate keskel ja neid kuulamast ja neilt küsimast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 47, 'Aga kõik, kes Teda kuulsid, panid imeks tema mõistust ja tema kostuseid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 48, 'Ja Teda nähes ehmusid Tema vanemad väga, ja Ta Ema ütles Temale: \"Poeg, miks Sa oled meile nõnda teinud? Vaata, Su isa ja Mina oleme valuga Sind otsinud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 49, 'siis Ta ütles neile: \"Miks te Mind otsite? Eks te teadnud, et Ma pean olema selles, mis on Mu Isa Oma?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 50, 'Aga nemad ei mõistnud seda sõna, mis Ta neile rääkis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 51, 'Ja ta läks ühes nendega ja tuli Naatsaretti ja oli neile allaheitlik. Ja Tema Ema pidas kõik need sõnad Oma südames.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 52, 'Ja Jeesus edenes tarkuses ja pikkuses ja armus Jumala ja inimeste juures.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 1, 'Keiser Tibeeriuse valitsuse viieteistkümnendal aastal, kui Pontius Pilaatus oli Judea maavalitseja ja Heroodes Galilea nelivürst ja Filippus, tema vend, Iturea ja Trahhoniitisemaa nelivürst ja Lüsaanias Abileene nelivürst,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 2, 'kui ülempreestreiks olid Annas ja Kaifas, sai Jumala sõna Sakariase poja Johannese kätte kõrves.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 3, 'Ja ta tuli kogu Jordani ümberkaudsele maale ja kuulutas meeleparandusristimist pattude andekssaamiseks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 4, 'nagu on kirjutatud prohvet Jesaja sõnade raamatus: \"Hüüdja hääl on kõrves: Valmistage Issanda teed, õgvendage tema teerajad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 5, 'Kõik kuristikud täidetagu ja kõik mäed ja mäekünkad tehtagu madalaks; mis on kõver, saagu sirgeks, ja mis on mätlik, siledaks teeks;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 6, 'siis kõik liha näeb Jumala päästet!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 7, 'Siis ta ütles rahvale, kes tuli välja ennast laskma tema poolt ristida: \"Te rästikute sigitis, kes on teile annud märku põgeneda tulevase viha eest?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 8, 'Seepärast kandke õiget meeleparanduse vilja ja ärge hakake iseenestes ütlema: Meil on isaks Aabraham! Sest ma ütlen teile, et Jumal võib neist kividest äratada Aabrahamile lapsi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 9, 'Kuid kirves on ka juba puude juure küljes! Iga puu nüüd, mis head vilja ei kanna, raiutakse maha ja visatakse tulle!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 10, 'Ja rahvas küsis temalt ning ütles: \"Mis meil siis tuleb teha?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 11, 'Tema vastas ning ütles neile: \"Kellel on kaks vammust, see andku sellele, kellel ei ole, ja kellel on rooga, see tehku nõndasamuti!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 12, 'Ent ka tölnereid tuli endid laskma ristida, ja need ütlesid temale: \"Õpetaja, mis meil tuleb teha?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 13, 'Aga ta ütles neile: \"Ärge nõudke rohkem kui teile on seatud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 14, 'Ka küsisid temalt sõjamehed ning ütlesid: \"Ja meie, mis siis meil tuleb teha?\" Ja ta ütles neile: \"Ärge tehke kellelegi liiga ja ärge survake kedagi, vaid olge rahul oma palgaga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 15, 'Aga kui rahvas ootas ja kõik oma südames mõtlesid Johannesest, kas tema vahest ei ole Kristus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 16, 'siis kostis Johannes ja ütles kõikidele: \"Mina ristin teid veega; aga on tulemas Vägevam minust, Kelle jalatsite paela ma ei ole kõlvuline lahti päästma; Tema ristib teid Püha Vaimu ja tulega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 17, 'Tema visklabidas on Ta käes, et teha puhtaks Oma rehealune ja koguda nisud Oma aita, aga aganad põletada ära kustutamatu tulega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 18, 'Ka veel muude asjade pärast manitsedes kuulutas ta armuõpetust rahvale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 19, 'Aga kui nelivürst Heroodes sai talt noomida Heroodiase, oma venna Filippuse naise pärast ja kõigi pahategude pärast, mis Heroodes oli teinud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 20, 'sai ta peale kõige muu veel toime sellega, et ta Johannese pani kinni vanglasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 21, 'Ent sündis, kui kõike rahvast ristiti ja ka Jeesus oli ristitud ja palvetas, et taevas avati');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 22, 'ja Püha Vaim tuli ihulikul kujul alla nagu tuvi Tema peale, ja hääl kostis taevast: \"Sina oled Mu armas Poeg, Sinust on Mul hea meel!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 23, 'Ja Jeesus oli Oma tegevust alates umbes kolmkümmend aastat vana ja oli, nagu arvati, Joosepi Poeg, kes oli Eeli poeg.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 24, 'Eeli oli Mattati, see oli Leevi, see oli Melki, see oli Jannai, see oli Joosepi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 25, 'see oli Matatia, see oli Aamose, see oli Nahumi, see oli Esli, see oli Nangai,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 26, 'see oli Mahati, see oli Matatia, see oli Simei, see oli Jooseki, see oli Jooda,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 27, 'see oli Joohanani, see oli Reesa, see oli Serubaabeli, see oli Sealtieli, see oli Neeri,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 28, 'see oli Melki, see oli Adi, see oli Koosami, see oli Elmadami, see oli Eeri,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 29, 'see oli Jeesuse, see oli Elieseri, see oli Joorimi, see oli Mattati, see oli Leevi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 30, 'see oli Siimeoni, see oli Juuda, see oli Joosepi, see oli Joonami, see oli Eljakimi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 31, 'see oli Melea, see oli Menna, see oli Matata, see oli Naatani, see oli Taaveti,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 32, 'see oli Jesse, see oli Oobedi, see oli Boase, see oli Salma, see oli Nahsoni,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 33, 'see oli Amminaadabi, see oli Admini, see oli Arni, see oli Hesromi, see oli Peretsi, see oli Juuda,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 34, 'see oli Jaakobi, see oli Iisaki, see oli Aabrahami, see oli Taara, see oli Naahori,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 35, 'see oli Serugi, see oli Reu, see oli Pelegi, see oli Eeberi, see oli Sela,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 36, 'see oli Keenani, see oli Arpaksadi, see oli Seemi, see oli Noa, see oli Lenseki,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 37, 'see oli Metuusala, see oli Eenoki, see oli Jeredi, see oli Mahalaleli, see oli Keenani,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 38, 'see oli Eenose, see oli Seti, see oli Aadama, see oli Jumala poeg.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 1, 'Siis tuli Jeesus, täis Püha Vaimu, tagasi Jordani äärest, ja Ta aeti Vaimu läbi kõrbe;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 2, 'ja nelikümmend päeva kurat kiusas Teda. Ja Ta ei söönud midagi neil päevil. Ja kui need möödusid, tuli Temale nälg.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 3, 'Siis ütles kurat Temale: \"Kui Sa oled Jumala Poeg, siis ütle sellele kivile, et ta leivaks muutuks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 4, 'Jeesus vastas temale: \"Kirjutatud on, et inimene ei ela ükspäinis leivast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 5, 'Siis kurat viis Tema kõrgele ning näitas Talle kõik maailma kuningriigid viivu ajaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 6, 'Ja kurat ütles Temale: \"Ma annan Sulle meelevalla kõigi nende üle ja nende auhiilguse, sest see on antud minule ja mina annan selle, kellele ma iganes tahan.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 7, 'Kui Sa nüüd kummardad minu ette, on kõik Sinu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 8, 'Jeesus vastas ning ütles temale: \"Kirjutatud on: Sina pead Issandat, oma Jumalat kummardama ja ükspäinis Teda teenima!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 9, 'Ja ta viis Tema Jeruusalemma ja asetas ta pühakoja harjale ning ütles Talle: \"Kui Sa oled Jumala Poeg, siis kukuta Ennast siit alla;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 10, 'sest on kirjutatud: Ta annab Oma Inglitele käsu Sinu pärast, et nad Sind hoiaksid');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 11, 'ja et nad Sind kätel kannaksid, et Sa Oma jalga vastu kivi ei tõukaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 12, 'Jeesus vastas ning ütles temale: \"Üteldud on: Sa ei tohi Issandat, oma Jumalat kiusata!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 13, 'Ja kui kurat kõik kiusamise oli lõpetanud, läks ta Temast eemale mõneks ajaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 14, 'Ja Jeesus läks tagasi Vaimu väes Galileasse; ja kuuldus Temast levis kogu ümberkaudsele maale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 15, 'Ja Ta õpetas nende kogudusekodades, ja kõik andsid Temale au.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 16, 'üles lugema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 17, 'Ja Tema kätte anti prohvet Jesaja raamat. Ja kui Ta raamatu avas, leidis Ta koha, kuhu oli kirjutatud:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 18, '\"Issanda Vaim on Minu peal; seepärast on Ta Mind võidnud kuulutama Evangeeliumi vaestele; Ta on Mind läkitanud kuulutama vabakssaamist seotuile ja nägemist pimedaile, laskma rõhutuid vabadusse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 19, 'kuulutama Issanda meelepärast aastat!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 20, 'Ja kui Ta raamatu oli kokku keeranud, andis Ta selle kojasulase kätte ja istus maha; ja kõikide silmad kogudusekojas olid sihitud Tema peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 21, 'Ta hakkas neile kõnelema: \"Täna on see kiri täide läinud teie kuuldes!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 22, 'Ja kõik tunnustasid Teda ja panid imeks neid armu sõnu, mis lähtusid Ta suust, ning ütlesid: \"Eks Seesinane ole Joosepi poeg?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 23, 'Siis Ta ütles neile: \"Küllap te Mulle ütlete selle vanasõna: Arst, aita Iseennast! Neid suuri asju, mida me oleme kuulnud Kapernaumas sündinud olevat, tee ka siin Oma kodukohas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 24, 'Aga Ta ütles: \"Tõesti Ma ütlen teile, ükski prohvet ei ole meelepärane oma kodumaal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 25, 'Tõepoolest Ma ütlen teile: Palju lesknaisi oli Iisraelis Eelija ajal, mil taevas oli kinni pandud kolm aastat ja kuus kuud, kui suur nälg tuli üle kogu maa;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 26, 'aga ei ühegi juure nende seast ei läkitatud Eelijat kui vaid Sareptasse Siidonimaale lesknaise juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 27, 'Ja palju pidalitõbiseid oli Iisraelis prohvet Eliisa ajal, aga ükski neist ei saanud puhtaks kui vaid Naaman, see süürlane!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 28, 'Seda kuuldes said kõik kogudusekojas täis viha,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 29, 'ja tõusid ning tõukasid Tema linnast välja ja viisid Ta üles mäerünkale, millele nende linn oli ehitatud, et Teda kukutada ülepeakaela alla.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 30, 'Aga Tema sammus nende keskelt läbi ja läks Oma teed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 31, 'Ja Ta tuli Kapernauma, Galilea linna, ja õpetas neid hingamispäevil.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 32, 'Ja nemad hämmastusid Tema õpetusest, sest Ta sõnal oli meelevald.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 33, 'Ja kogudusekojas oli mees, kellel oli rüveda pahareti vaim, see kisendas suure häälega:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 34, '\"Oeh, mis on meil Sinuga tegemist, Jeesus Naatsaretlane? Oled Sa tulnud meid hävitama? Ma tunnen Sind, kes Sa oled, Jumala Püha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 35, 'Siis Jeesus sõitles teda ning ütles: \"Ole vait ja mine temast välja!\" Ja kuri vaim viskas ta maha nende keskele ja väljus temast ega teinud talle ühtki kahju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 36, 'Ja kohkumine valdas kõiki, ja nad rääkisid isekeskis ning ütlesid: \"Mis lugu see on, et Ta meelevalla ja väega käsib rüvedaid vaime, ja need väljuvad?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 37, 'Ja kuuldus Temast levis kõigisse ümberkaudse maa paikadesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 38, 'Aga ta tõusis üles kogudusekojast ja läks Siimona kotta. Ja Siimona ämm oli haige raskes palavikus, ja nad palusid Teda tema pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 39, 'Ja Ta astus tema juure ja ähvardas palavikku, ja see lahkus temast. Ja sedamaid tõusis naine üles ja ümmardas neid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 40, 'Aga kui päike looja läks, tõid kõik, kelledel oli haigeid mõnesugustes tõbedes, need Tema juure, ja Ta pani igaühe peale Oma käed ning tegi nad terveks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 41, 'Aga ka kurjad vaimud läksid paljudest välja kisendades ning üteldes: \"Sina oled Jumala Poeg!\" Ja Tema sõitles neid ega lubanud neid rääkida, sest et nad teadsid Tema olevat Kristuse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 42, 'Ja päeva tulles väljus Ta ja läks tühja paika; kuid rahvahulgad otsisid Teda ning tulid Tema juure. Ja nad pidasid Teda kinni, et Ta ei läheks ära nende juurest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 43, 'Aga Tema ütles neile: \"Ma pean ka muudele linnadele kuulutama Jumala Riigi Evangeeliumi, sest selleks Ma olen läkitatud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 44, 'Ja Ta jutlustas Galilea kogudusekodades.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 1, 'Aga sündis, kui rahvas tema juure tungis Jumala sõna kuulama ja Ta seisis Gennesareti järve ääres,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 2, 'et Ta nägi kaht paati järve rannas seisvat; aga kalamehed olid neist väljunud ja loputasid võrke.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 3, 'Tema astus ühte paati, mis oli Siimona oma, ja palus teda natuke maad rannast eemale sõuda. Siis Ta istus maha ja õpetas rahvast paadist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 4, 'Aga kui Ta lakkas kõnelemast, ütles Ta Siimonale: \"Sõua sügavale kohale ja heitke oma võrgud välja loomuse katseks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 5, 'Siimon vastas ning ütles Temale: \"Õpetaja, me oleme kogu öö rühelnud ega ole ühtki saanud, aga Sinu sõna peale ma heidan võrgud välja!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 6, 'Ja kui nad seda tegid, said nad suure hulga kalu; ja nende võrgud rebenesid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 7, 'Ja nad viipasid oma kaaslastele teises paadis, et nad tuleksid ja neid aitaksid. Ja nad tulid ja täitsid mõlemad paadid nõnda, et need olid vajumas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 8, 'Seda nähes heitis Siimon Peetrus maha Jeesuse põlvede ette ning ütles: \"Issand, mine minu juurest ära, sest ma olen patune inimene!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 9, 'Sest kohkumine oli vallanud teda ja kõiki, kes temaga olid, kalasaagi pärast, mis nad olid saanud;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 10, 'nõndasamuti ka Jakoobust ja Johannest, Sebedeuse poegi, kes olid Siimona kaaslased. Ja Jeesus ütles Siimonale: \"Ära karda, nüüdsest alates pead sa inimesi püüdma!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 11, 'Ja nad vedasid paadid maale ja jätsid kõik maha ja järgisid Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 12, 'Ja kui Ta viibis ühes linnas, vaata, siis oli seal mees täis pidalitõbe. Kui ta Jeesust nägi, heitis ta silmili maha, palus Teda ning ütles: \"Issand, kui Sa tahad, võid Sa mind puhtaks teha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 13, 'Ja Ta sirutas oma käe ja puudutas teda ning ütles: \"Ma tahan, saa puhtaks!\" Ja sedamaid lahkus pidalitõbi temast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 14, 'Ja Tema keelas teda seda kellelegi ütlemast \"Mine vaid\" - ütles Ta - \"ja näita ennast preestrile ja ohverda oma puhastuse eest, nõnda nagu Mooses on seadnud neile tunnistuseks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 15, 'Aga sõnum temast levis laiemale, ja palju rahvast tuli kokku teda kuulama ja temalt tervist saama tõbedest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 16, 'Aga Tema läks kõrvale tühjadesse paikadesse ja palvetas seal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 17, 'Ja ühel päeval, kui Ta õpetas, oli seal istumas varisere ja käsutundjaid, kes olid tulnud kõigist Galilea ja Judea küladest ja Jeruusalemast; ja Issanda vägi oli seal selleks, et Jeesus terveks teeks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 18, 'Ja vaata, mehed tõid voodiga inimese, kes oli halvatud; ja nad püüdsid teda sisse viia ja Tema ette panna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 19, 'Ja kui nad rahva pärast ei leidnud, kust nad ta oleksid saanud sisse viia, läksid nad üles katusele ja lasksid ta katuse kivide vahelt alla kõige voodiga otse Jeesuse ette maha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 20, 'Kui Tema nende usku nägi, ütles Ta: \"Inimene, sinu patud on sulle andeks antud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 21, 'Siis kirjatundjad ja variserid hakkasid mõtlema ning ütlesid: \"Kes see on, kes nõnda Jumalat pilgates räägib? Kes muu võib patte andeks anda kui üksnes Jumal?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 22, 'Aga Jeesus tundis ära nende mõtlemised ja kostust andes Ta ütles neile: \"Mida te mõtlete oma südames?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 23, 'Kumb on hõlpsam ütelda: Su patud on sulle andeks antud, või ütelda: Tõuse üles ja kõnni?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 24, 'Aga et te teaksite, et Inimese Pojal on meelevald patte andeks anda,\" - siis ütles Ta halvatule: \"Ma ütlen sulle, tõuse üles ja võta oma voodi ning mine koju!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 25, 'Ja sedamaid tõusis ta nende nähes üles, võttis oma voodi, millel ta oli lamanud, ja läks koju Jumalale au andes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 26, 'Ja hämmastus valdas kõiki, ja nad andsid Jumalale au ja said täis kartust ning ütlesid: \"Me oleme täna näinud imelisi asju!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 27, 'Pärast seda Ta läks välja ja nägi tölnerit, Leevi nimi, tollihoones istuvat ja ütles temale: \"Järgi Mind!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 28, 'Ja ta jättis kõik maha, tõusis ja järgis Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 29, 'Ja Leevi tegi Temale suure võõruspidu oma majas, ja suur hulk tölnereid ja teisi oli ühes nendega lauas istumas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 30, 'Siis variserid ja nende kirjatundjad nurisesid tema jüngrite vastu ning ütlesid: \"Miks te sööte ja joote ühes tölnerite ja patustega?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 31, 'Ja Jeesus vastas neile ning ütles: \"Ei ole arsti vaja tervetele, ainult haigetele!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 32, 'Ma ei ole tulnud õigeid kutsuma meeleparandusele, vaid patuseid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 33, 'Aga nemad ütlesid Temale: \"Johannese jüngrid paastuvad sagedasti ja peavad palveid, samuti variseridegi omad, aga Sinu jüngrid söövad ja joovad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 34, 'Jeesus ütles neile: \"Kas te võite peiupoisse panna paastuma sel ajal, kui peigmees on nende juures?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 35, 'Ent päevad tulevad, mil peigmees võetakse neilt ära; siis nad paastuvad neil päevil!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 36, 'Aga Ta ütles neile ka tähendamissõna: \"Ükski ei lõika paika uue kuue küljest ega pane seda vana kuue peale, muidu ta lõikab katki ka uue kuue, ja pealegi ei sobi vanale kuuele paik uue küljest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 37, 'Ka ei pane keegi värsket viina vanusse nahklähkreisse, muidu värske viin ratkub lähkrid ja ta jookseb maha ning lähkrid lähevad rikki; 38 vaid värske viin tuleb panna uutesse nahklähkritesse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 38, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 39, 'siis keegi, kes joob vana, ei himusta värsket, vaid ütleb: Vana on maitsev!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 1, 'Ja hingamispäeval Ta juhtus minema läbi viljapõldude; ja Tema jüngrid katkusid päid, hõõrusid neid käte vahel ja sõid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 2, 'Ent mõningad variseridest ütlesid: \"Miks te teete seda, mida hingamispäeval ei ole luba teha?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 3, 'Aga Jeesus vastas ning ütles neile: \"Eks te ole seda lugenud, mida Taavet tegi, kui temal ja ta kaaslastel oli nälg,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 4, 'kuidas ta läks Jumala kotta ja võttis vaateleivad, sõi ning andis ka oma kaaslasile, ehk küll neid ei olnud luba süüa muil kui üksnes preestritel!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 5, 'Ja Ta ütles neile: \"Inimese Poeg on hingamispäeva isand!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 6, 'Aga juhtus ühel teisel hingamispäeval, et ta läks kogudusekotta ja õpetas. Ja seal oli inimene, kelle parem käsi oli kuivanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 7, 'Aga kirjatundjad ja variserid varitsesid Teda, kas Ta hingamispäeval peaks terveks tegema, et nad leiaksid kaebust Tema peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 8, 'Tema aga teadis nende mõtted ja ütles mehele, kellel oli kuivanud käsi: \"Tõuse üles ja astu keskele!\" Ja ta tõusis ja seisis seal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 9, 'Siis ütles Jeesus neile: \"Ma küsin teilt: Kas hingamispäeval on luba teha head või kurja, hinge päästa või hukka saata?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 10, 'Ja Ta vaatas ümber nende kõikide peale ja ütles mehele: \"Siruta oma käsi!\" Ja see tegi nõnda. Ja tema käsi sai jälle terveks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 11, 'Nemad aga sattusid meeletumasse vimma ja rääkisid üksteisega sellest, mida nad küll võiksid teha Jeesusele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 12, 'Neil päevil sündis ka, et Ta läks välja mäele palvetama ja viibis kogu öö Jumala palumises.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 13, 'Ja kui valgeks läks, kutsus Ta Oma jüngrid Enese juure ja valis nende seast kaksteistkümmend, kelled Ta nimetas ka apostliteks:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 14, 'Siimona, keda Ta nimetas ka Peetruseks, ja tema venna Andrease, Jakoobuse ja Johannese, Filippuse ja Bartolomeuse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 15, 'Matteuse ja Tooma, Jakoobuse, Alfeuse poja, ja Siimona, keda hüütakse Selooteseks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 16, 'ja Juuda, Jakoobuse poja, ja Juudas Iskarioti, kes sai äraandjaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 17, 'Nendega Ta astus alla ja jäi peatuma lagedale paigale; ja seal oli suur hulk Tema jüngreid ja kogu Juudamaalt ja Jeruusalemast ning Tüürose ja Siidoni rannamaalt suur rahvakogu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 18, 'kes oli tulnud Teda kuulama ja ennast laskma parandada oma haigustest. Ja ka need, keda rüvedad vaimud vaevasid, said terveks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 19, 'Ja kõik rahvas püüdis Teda puudutada, sest vägi lähtus Temast ja parandas kõik.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 20, 'Ja Tema tõstis silmad Oma jüngrite poole ja ütles: \"Õndsad olete, vaesed, sest teie päralt on Jumala Riik.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 21, 'Õndsad teie, kelledel nüüd on nälg, sest teid peab toidetama. Õndsad teie, kes nüüd nutate, sest siis te naerate.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 22, 'Õndsad olete, kui inimesed teid vihkavad ja kui nad teid kogudusest välja heidavad ning laimavad ja teie nime kui paha nime hülgavad Inimese Poja pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 23, 'Rõõmutsege sel päeval ja hüpake, sest vaata, teie paik on suur taevas! Tegid ju nõndasamuti prohvetitega nende isad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 24, 'Kuid häda teile rikastele! Sest teil on troost käes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 25, 'Häda teile, kes nüüd olete täidetud, sest teile tuleb nälg! Häda teile, kes nüüd naerate, sest te saate veel leinata ja nutta!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 26, 'Häda teile, kui kõik inimesed teist räägivad head, sest nõndasamuti tegid nende isad valeprohvetitele!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 27, 'Aga teile, kes kuulete, ütlen Ma: Armastage oma vaenlasi, tehke head neile, kes teid vihkavad;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 28, 'õnnistage neid, kes teid neavad; paluge nende eest, kes teid siunavad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 29, 'Kes sind lööb vastu kõrva, sellele paku ka teine; ja kes sult võtab kuue, sellele ära keela vammust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 30, 'Anna igaühele, kes sind palub, ja sellelt, kes võtab sinu oma, ära nõua tagasi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 31, 'Ja nõnda nagu te tahate, et inimesed teile teeksid, nõnda tehke neile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 32, 'Ja kui te armastate neid, kes teid armastavad, mis tänu on teil sellest? Sest patusedki armastavad neid, kes neid armastavad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 33, 'Ja kui te teete head oma heategijaile, mis tänu on teil sellest? Sest patusedki teevad sedasama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 34, 'Ja kui te laenate neile, kelledelt te loodate tagasi saada, mis tänu on teil sellest? Patusedki laenavad patustele, et nad samavõrra saaksid tagasi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 35, 'Aga armastage oma vihamehi ja tehke head ja laenake ilma midagi selle vastu lootmata; siis teie palk on suur ja te olete Kõigekõrgema lapsed, sest Tema on lahke ka tänamatute ja kurjade vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 36, 'Olge armulised, nõnda nagu teie Isa on armuline.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 37, 'Ja ärge mõistke kohut, siis ei mõisteta ka teie üle kohut. Ärge mõistke hukka, siis ei mõisteta ka teid hukka. Andke andeks, siis antakse ka teile andeks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 38, 'Andke, siis antakse ka teile; hea, tuubitud ja raputatud ja kuhjaga mõõt antakse teie rüppe; sest sama mõõduga, millega teie mõõdate, mõõdetakse ka teile.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 39, 'Aga Ta ütles neile võrdumi: \"Egas pime või pimedat talutada? Eks nad mõlemad kuku auku?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 40, 'Ei ole õpilane ülem oma õpetajast, ent iga täiesti õpetatu on samasugune kui ta õpetaja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 41, 'Aga miks sa näed pindu oma venna silmas, kuid palki su omas silmas sa ei pane tähele?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 42, 'Ehk kuidas sa võid ütelda oma vennale: Vend, lase ma tõmban pinnu sinu silmast! ja sa ise ei näe palki omas silmas? Sa silmakirjatseja! Tõmba esmalt palk omast silmast ja siis sa seletad pindu välja tõmmata oma venna silmast!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 43, 'Sest ei ole head puud, mis kannaks halba vilja, ega jälle halba puud, mis kannaks head vilja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 44, 'Sest igat puud tuntakse tema viljast. Ei nopita ju viigimarju kibuvitsust ega korjata viinakobaraid ohakaist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 45, 'Hea inimene toob esile head oma südame heast tagavarast ja halb inimene toob esile halba oma südame halvast tagavarast. Sest mida süda täis, sellest suu räägib!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 46, 'Aga miks te Mind hüüate: Issand, Issand! ega tee, mida Ma ütlen?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 47, 'Igaüks, kes tuleb Minu juure ja kuuleb Mu sõnu ning teeb nende järele - Ma näitan teile, kelle sarnane ta on.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 48, 'Ta on inimese sarnane, kes maja ehitades kaevas ja võttis sügavasse ning rajas tema aluse kaljule. Kui siis suurvesi tuli, murdus vool vastu seda maja ja ei suutnud seda kõigutada, sest see oli hästi ehitatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 49, 'Aga kes kuuleb ega tee, see on inimese sarnane, kes maja rajas maa peale ilma aluseta; ja voolav vesi rõhus selle vastu ja see langes kohe kokku; ja selle maja varisemine oli suur!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 1, 'Kui Ta kõik Oma kõned oli lõpetanud kuulavale rahvale, läks Ta Kapernauma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 2, 'Ja ühe sõjapealiku sulane oli haige ning suremisel; ja ta oli kallis tema meelest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 3, 'Aga kui ta Jeesusest kuulis, läkitas ta juutide vanemad Tema juure ja palus Teda, et Ta tuleks ja teeks tema sulase terveks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 4, 'Kui need saabusid Jeesuse juure, palusid nad Teda üliväga ning ütlesid: \"Ta on seda väärt, et Sa temale seda teed;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 5, 'sest ta armastab meie rahvast ja tema on meile ehitanud kogudusekoja.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 6, 'Jeesus läks nendega. Aga kui ta enam ei olnud majast kaugel, läkitas pealik oma sõbrad Talle ütlema: \"Issand, ära tee Enesele vaeva, sest mina ei ole väärt, et Sina mu katuse alla tuled.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 7, 'Sellepärast ei ole mina ennast arvanud väärt tulema Sinu juure, vaid ütle aga sõna, siis mu sulane paraneb.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 8, 'Sest minagi olen inimene, kes on pandud valitsuse alla, ja minu all on sõjamehi, ja kui ma ütlen ühele: Mine! siis ta läheb, ja teisele: Tule! siis ta tuleb, ja oma sulasele: Tee seda! siis ta teeb.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 9, 'Kui Jeesus seda kuulis imetles Ta teda ja pöördudes rahva poole, kes järel käis, ütles Ta: \"Ma ütlen teile, ei ole ma Iisraelistki leidnud nii suurt usku!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 10, 'Ja kui need, kes olid läkitatud, jälle koju said, leidsid nad sulase terve olevat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 11, 'Järgmisel päeval läks Ta linna, mida hüütakse Nainiks, ja Temaga ühes läksid Tema jüngrid ja palju rahvast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 12, 'Kui Ta linna värava ligi jõudis, vaata, siis kanti välja surnut, oma ema ainust poega; ja ema oli lesk. Ja suur hulk linna rahvast oli temaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 13, 'Ja kui Issand teda nägi, oli tal väga hale meel temast ja ütles talle: \"Ära nuta!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 14, 'Ja ta astus ligi ja puudutas puusärki; aga kandjad jäid seisma. Ja Ta ütles: \"Noormees, ma ütlen sulle, tõuse üles!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 15, 'Ja surnu tõusis istukile ja hakkas rääkima. Ja Ta andis tema ta emale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 16, 'Aga neid kõiki valdas hirm, ja nad andsid au Jumalale ning ütlesid: \"Suur prohvet on meie seas tõusnud ja Jumal on tulnud Oma rahvast katsuma!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 17, 'Ja see jutt Temast levis kogu Juudamaale ja kõigisse ümberkaudseisse paikadesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 18, 'Ja seda kõike kuulutasid Johannesele tema jüngrid. Ja Johannes kutsus enese juure kaks oma jüngritest');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 19, 'ja läkitas nad Issanda juure küsima: \"Kas oled Sina See, Kes tuleb, või peame ootama teist?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 20, 'Aga kui mehed Tema juure jõudsid, ütlesid nad: \"Ristija Johannes läkitas meid Sinu juure küsima: Kas oled Sina See, Kes tuleb, või peame ootama teist?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 21, 'Ent samal tunnil Ta tegi paljud terveks tõbedest ja valudest ja kurjest vaimudest, ja paljudele pimedaile Ta kinkis nägemise.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 22, 'Ja Ta vastas ning ütles neile: \"Minge ja kuulutage Johannesele, mida te olete näinud ja kuulnud: pimedad näevad jälle, jalutumad käivad, pidalitõbised saavad puhtaks, kurdid kuulevad, surnud äratatakse üles, vaestele kuulutatakse Evangeeliumi;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 23, 'ja õnnis on see, kes Minust ei pahandu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 24, 'Aga kui Johannese käskjalad olid ära läinud, hakkas Ta rahvale rääkima Johannesest: \"Mida te olete läinud välja kõrbe vaatama? Pilliroogu, mida tuul kõigutab?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 25, 'Või mida te olete välja läinud vaatama? Inimest, pehmete riietega riietatud? Vaata, kes käivad toredas riietuses ja endid hellitavad, need on kuninglikes kodades!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 26, 'Või mida te olete välja läinud vaatama? Kas prohvetit? Tõesti, Ma ütlen teile, ta on veel rohkem kui prohvet!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 27, 'Tema on see, kellest on kirjutatud: Vaata, Mina läkitan Sinu palge eele Oma Ingli, kes Sulle tee valmistab Sinu ees!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 28, 'Mina ütlen teile, ei ole kedagi suuremat naisest sündinute seas kui Johannes, aga väikseim Jumala Riigis on suurem temast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 29, 'Ja kõik rahvas, kes seda kuulis, ja tölnerid tunnistasid Jumala õigeks, lastes endid ristida Johannese ristimisega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 30, 'Variserid ja käsutundjad aga tegid tühjaks Jumala tahte eneste kohta ega lasknud teda endid ristida.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 31, '\"Kellega Ma siis võrdlen selle sugupõlve inimesi ja kelle sarnased nad on?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 32, 'Nad on laste sarnased, kes turul istuvad ja üksteisele hüüavad nõnda: Me oleme teile vilet ajanud, ja te ei ole tantsinud; me oleme teile nutulaulu laulnud, ja te ei ole nutnud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 33, 'Sest Ristija Johannes on tulnud, ja ta ei söönud leiba ega joonud viina; ja te ütlete: Temal on kuri vaim!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 34, 'Inimese Poeg on tulnud, ja ta sööb ja joob, ja te ütlete: Vaata, see inimene on söödik ja viinajoodik, tölnerite ja patuste sõber!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 35, 'Ometi mõistetakse tarkus õigeks kõigi tema laste poolt!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 36, 'Aga keegi variseridest palus Teda enesega leiba võtma. Ja Ta läks variseri kotta ja istus lauda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 37, 'Ja vaata, selles linnas oli naine, kes oli patune. Kui see teada sai, et Ta istub lauas variseri kojas, tõi ta alabasterriistatäie kallist lõhnasalvi');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 38, 'ja astus nuttes Tema taha ta jalgade juure ja hakkas Tema jalgu kastma silmaveega ja kuivatas neid oma juustega, ja suudles Tema jalgu ning võidis neid salviga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 39, 'Aga kui variser, kes Teda oli kutsunud, seda nägi, mõtles ta iseeneses nõnda: Kui Seesinane oleks prohvet, küll Ta siis ära tunneks, kes ja missugune see naine on, kes Teda puudutab, et ta on patune.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 40, 'Ja Jeesus vastas ning ütles temale: \"Siimon, mul on sulle midagi ütelda!\" Aga tema kostis: \"Õpetaja, räägi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 41, '\"Ühel rahalaenajal oli kaks võlglast: üks oli võlgu viissada teenarit, teine viiskümmend.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 42, 'Aga kui neil ei olnud maksta, kinkis ta mõlemaile selle. Kumb neist nüüd teda rohkem armastab?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 43, 'Siimon vastas ning ütles: \"Ma arvan see, kellele ta rohkem kinkis.\" Tema ütles temale: \"Sa otsustasid õieti!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 44, 'Ja pöördudes naise poole, ütles Ta Siimonale: \"Kas sa näed seda naist? Ma tulin sinu majasse, sa ei annud vett Mu jalgade tarvis, tema aga on pisaratega Mu jalgu kastnud ja oma juustega kuivatanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 45, 'Sa ei annud Mulle suud; tema aga ei ole sellest ajast, kui ta sisse tuli, lakanud Minu jalgadele suud andmast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 46, 'Sa ei võidnud Mu pead õliga, tema aga on Mu jalgu võidnud salviga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 47, 'Sellepärast, ütlen Ma sulle, on tema rohked patud andeks antud, sest ta on palju armastanud. Aga kellele pisut andeks antakse, see armastab pisut!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 48, 'Ja Ta ütles naisele: \"Sinu patud on sulle andeks antud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 49, 'Siis need, kes Temaga lauas istusid, hakkasid iseeneses mõtlema: Kes see on, kes ka patud andeks annab?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 50, 'Aga Tema ütles naisele: \"Sinu usk on sind aidanud; mine rahuga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 1, 'Pärast seda aega rändas Tema linnast linna ja külast külla ning jutlustas ja kuulutas Evangeeliumi Jumala Riigist. Ja need kaksteistkümmend olid Temaga,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 2, 'ja mõned naised, kelled Tema oli terveks teinud kurjadest vaimudest ja tõbedest: Maarja, keda hüütakse Magdaleenaks, kellest seitse kurja vaimu oli välja läinud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 3, 'ja Johanna, Heroodese varahoidja Kuusa naine, ja Susanna ja palju muid, kes Teda toetasid oma varaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 4, 'Aga kui palju rahvast kogunes ja kõigist linnadest tuli Tema juure, rääkis Ta tähendamissõnaga:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 5, '\"Külvaja läks välja oma seemet külvama. Ja kui ta külvas, kukkus muist tee ääre ja tallati ära, ja taeva linnud sõid selle ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 6, 'Ja muist kukkus kaljule. Ja kui see tärkas, kuivas see ära, sest sellel ei olnud niiskust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 7, 'Ja muist kukkus ohakate keskele, ja ohakad kasvasid ühtlasi üles ja lämmatasid selle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 8, 'Ja muist kukkus heale maale, ja kui see tärkas, kandis see sajakordse vilja.\" Seda üteldes Ta hüüdis: \"Kellel kõrvad on kuulda, see kuulgu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 9, 'Siis küsisid Tema jüngrid Temalt, mida see tähendamissõna tähendab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 10, 'Tema ütles: \"Teile on antud mõista Jumala Riigi Saladusi, aga muile tähendamissõnades, et nad nähes ei näeks ja kuuldes ei mõistaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 11, 'Ent tähendamissõna on see: Seeme on Jumala sõna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 12, 'Teeäärsed on need, kes kuulevad; pärast tuleb kurat ja võtab sõna ära nende südamest, et nad ei usuks ega saaks õndsaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 13, 'Kaljupealsed on need, kes kuuldes sõna rõõmuga vastu võtavad, kuid neil ei ole juurt; ajutiseks nad usuvad ja kiusatuse ajal nad loobuvad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 14, 'Mis ohakate sekka kukkus, need on need, kes kuulevad ja lähevad ära ning lämbuvad muredest ja rikkusest ja elu lõbudest ega kanna valmis vilja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 15, 'Aga mis on heas maas, on need, kes sõna kuulevad, kaunis ja heas südames peavad ning kannavad vilja kannatlikkuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 16, 'Ükski, kes süütab küünla, ei kata seda astjaga kinni ega pane voodi alla, vaid asetab selle küünlajalale, et sissetulijad näeksid valgust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 17, 'Sest midagi ei ole varjul, mis ei saaks avalikuks, ei ole ka midagi salajas, mis ei saaks teatavaks ega tuleks ilmsiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 18, 'Katsuge siis, kuidas te kuulete; sest kellel iganes on, sellele antakse, ja kellel iganes ei ole, sellelt võetakse seegi, mis ta arvab enesel olevat.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 19, 'Aga Tema juure tulid Ta ema ja vennad ega võinud saada Tema ligi rahva pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 20, 'Ja Temale teatati: \"Sinu Ema ja Su vennad seisavad õues ja tahavad Sind näha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 21, 'Aga Ta kostis ning ütles neile: \"Minu Ema ja Mu vennad on need, kes Jumala sõna kuulevad ja teevad selle järele!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 22, 'Ja ühel päeval astusid Tema ja Ta jüngrid paati, ja Ta ütles neile: \"Lähme teisele poole järve!\" Ja nad sõudsid minema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 23, 'Ja kui nad paadiga purjetasid, uinus Ta magama. Siis tõusis järvel tuulispea ja paat uhtus vett täis, ja nad olid hädas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 24, 'Siis nad tulid Tema juure, äratasid Ta üles ja ütlesid: \"Õpetaja, õpetaja, me hukkume!\" Aga Tema tõusis üles, ähvardas tuult ning vee lainetust, ja need raugesid ning ilm muutus vaikseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 25, 'Aga Tema ütles neile: \"Kus on teie usk?\" Ent nad kartsid ja imestasid ning ütlesid üksteisele: \"Kes see õieti on, et Tema käsib tuuli ja vett, ja need kuulevad Ta sõna?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 26, 'Ja nad purjetasid gerasalaste maale, mis on Galilea vastas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 27, 'Aga kui Tema astus maale, tuli linnast Talle vastu mees, kelle sees oli kurje vaime; ja ta ei olnud ammust ajast kuube selga pannud ega asunud majas, vaid surnuhaudades.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 28, 'Kõige Kõrgema Jumala Poeg? Ma palun Sind, ära piina mind!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 29, 'Sest Ta käskis rüvedat vaimu väljuda inimesest. See oli teda juba kaua aega vaevanud, ja teda oli ahelatega seotud ja peetud jalgrau\'us, aga ta oli rauad katki kiskunud ja kuri vaim oli teda ajanud kõrbedesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 30, 'Jeesus küsis temalt: \"Mis su nimi on?\" Tema vastas: \"Leegion!\" Sest palju kurje vaime oli läinud tema sisse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 31, 'Ja need palusid Teda, et Ta neid ei käsiks minna ära põrguhauda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 32, 'Aga seal oli suur seakari mäel söömas. Ja nad palusid Teda, et Ta neid lubaks minna nende sisse. Ja Ta andis neile loa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 33, 'Aga kui kurjad vaimud olid inimesest väljunud, läksid nad sigade sisse, ja seakari kukutas enese ülepeakaela kaldalt järve ning uppus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 34, 'Kui karjatsejad nägid, mis sündis, põgenesid nad ja andsid seda teada linnas ja maal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 35, 'Siis tuldi välja vaatama, mis oli sündinud, ja mindi Jeesuse juure ja leiti inimene, kellest kurjad vaimud olid välja läinud, Jeesuse jalge ees istumas, riietatult ja selge aruga. Ja inimesed lõid kartma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 36, 'Siis jutustasid neile need, kes olid näinud, kuidas seestunu oli terveks saanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 37, 'Ja kogu rahvahulk gerasalaste maa ümbruskonnast palus Teda, et Ta läheks ära nende juurest. Sest suur hirm oli neid vallanud. Tema aga astus paati ja pöördus tagasi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 38, 'Aga mees, kellest kurjad vaimud olid välja läinud, palus Teda, et ta võiks jääda Tema juure. Kuid Jeesus laskis ta minema, üteldes:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 39, '\"Mine tagasi koju ja jutusta, mis suuri asju Jumal sulle on teinud!\" Ja ta läks ära ja kuulutas mööda kogu linna, kui suuri asju Jeesus temale oli teinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 40, 'Aga kui Jeesus tagasi tuli, võttis rahvas Teda vastu, sest nemad kõik olid Teda ootamas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 41, 'Ja vaata, siis tuli mees, Jairus nimi, ja see oli kogudusekoja ülem; ja ta heitis Jeesuse jalge ette ning palus Teda tulla enese kotta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 42, 'Sest tal oli ainus tütar, kaksteistkümmend aastat vana, ja see oli suremas. Aga Tema sinna minnes tungis rahvas Temale peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 43, 'Ja keegi naine, kes oli kaksteistkümmend aastat põdenud verejooksu ega olnud võinud ühegi käest saada tervist,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 44, 'tuli Tema selja taha ja puudutas Ta kuue palistust, ja sedamaid lakkas ta verejooks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 45, 'Ja Jeesus ütles: \"Kes puudutas Mind?\" Aga kui kõik salgasid, ütles Peetrus: \"Õpetaja, rahvahulgad tungivad Sulle peale ja rõhuvad Sind!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 46, 'Kuid Jeesus ütles: \"Keegi puudutas Mind, sest Ma tundsin väe Enesest välja minevat!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 47, 'Kui nüüd naine nägi, et ta ei olnud varjule jäänud, tuli ta värisedes ja heitis Tema ette maha ja teatas Temale kõige rahva kuuldes, mis asja pärast ta oli Teda puudutanud ja kuidas ta kohe oli terveks saanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 48, 'Siis Ta ütles naisele: \"Tütar, sinu usk on sind aidanud, mine rahuga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 49, 'Kui Tema alles rääkis, tuli kogudusekoja ülema perest keegi ning ütles: \"Su tütar on surnud, ära tülita enam Õpetajat!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 50, 'Aga seda kuuldes vastas Jeesus temale: \"Ara karda, usu vaid, siis ta saab abi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 51, 'Kui Ta siis jõudis majasse, ei lasknud Ta Enesega sisse tulla kedagi teist kui ainult Peetruse ja Johannese ja Jakoobuse ning lapse isa ja ema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 52, 'Ja kõik nutsid ja leinasid tütarlast. Aga Tema ütles: \"Ärge nutke, ta ei ole surnud, vaid ta magab!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 53, 'Ja nad naersid Teda, teades, et lapsuke on surnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 54, 'Ent Tema haaras lapse käest kinni ja ütles hüüdes: \"Tütarlaps, tõuse üles!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 55, 'Ja tema vaim tuli tagasi, ja ta tõusis kohe üles. Ja Ta käskis talle süüa anda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 56, 'Ja tütarlapse vanemad kohkusid; aga Tema keelas neid ühelegi rääkimast seda, mis oli sündinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 1, 'Aga Ta kutsus kokku need kaksteistkümmend ja andis neile väe ja meelevalla kõigi kurjade vaimude üle ja tõbesid parandada,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 2, 'ja läkitas nad välja kuulutama Jumala Riiki ja tegema haigeid terveks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 3, 'ja ütles neile: \"Ärge võtke midagi teele kaasa, ei keppi ega pauna, ei leiba ega raha; ärgu olgu ka kellelgi kaht kuube.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 4, 'Ja kuhu majasse te iganes sisse lähete, sinna jääge ja sealt minge teele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 5, 'Ja kes iganes teid vastu ei võta, sellest linnast minge välja ja puistake toim oma jalgadelt tunnistuseks nende vastu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 6, 'Nii nad läksid teele ja käisid mööda külasid, kuulutades Evangeeliumi ja tehes haigeid terveks igal pool.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 7, 'Aga nelivürst Heroodes sai kuulda kõik, mis sündis, ja oli kahevahel, sest mõned ütlesid: \"Johannes on surnuist üles tõusnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 8, 'aga teised: \"Eelija on ilmunud!\" aga mõned jälle: \"Keegi vanust prohveteist on üles tõusnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 9, 'Ent Heroodes ütles: \"Johannese pea ma lasksin maha raiuda; kes on siis See, Kellest ma kuulen niisuguseid asju?\" Ja ta püüdis Teda näha saada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 10, 'Ja kui Apostlid tagasi tulid, jutustasid nad Temale, mis nad olid teinud. Ja Ta võttis nad Enese juure ja läks ära üksindusse, linna poole, mida hüütakse Betsaidaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 11, 'Aga kui rahvahulgad seda teada said, läksid nad Temale järele. Ja Ta võttis nad vastu ja rääkis neile Jumala Riigist; ja kelledele oli vaja ravitsemist, need Ta tegi terveks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 12, 'Siis päev hakkas veerema. Ja need kaksteistkümmend tulid Ta juure ja ütlesid Talle: \"Lase rahvas minema, et nad läheksid ümberkaudseisse küladesse ja asulaisse puhkama ja otsima rooga; sest me oleme siin tühjas paigas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 13, 'Aga Ta ütles neile: \"Andke teie neile süüa!\" Nemad vastasid: \"Meil pole rohkem kui viis leiba ja kaks kala, või me peaksime minema ja ostma toidust kogu sellele rahvale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 14, 'Ent neid oli ligi viis tuhat meest. Siis Ta ütles Oma jüngritele: \"Seadke nad maha istuma salkadesse viiekümne kaupa!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 15, 'Ja nad tegid nõnda ja seadsid kõik maha istuma einele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 16, 'Ent Ta võttis need viis leiba ja kaks kala, vaatas üles taeva poole, õnnistas neid ning murdis nad ja andis Oma jüngritele ettepanemiseks rahvale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 17, 'Ja kõlk sõid ja kõikide kõhud said täis; ja neist ülejäänud palukesi korjati kokku kaksteistkümmend korvitäit.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 18, 'Ja sündis, kui Ta üksipäini oli palvetamas, et Ta jüngrid olid ühes Temaga. Ja Ta küsis neilt: \"Keda rahvas ütleb Mind olevat?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 19, 'Aga nemad vastasid ning ütlesid: \"Ristija Johannese, aga mõned Eelija, aga teised, et keegi vanust prohveteist on üles tõusnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 20, 'Siis Ta ütles neile: \"Ent teie, keda teie ütlete Mind olevat?\" Peetrus vastas ning ütles: \"Jumala Kristuse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 21, 'Ja Ta ähvardas neid kangesti ning keelas neid sellest ühelegi rääkimast,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 22, 'üteldes: \"Inimese Poeg peab palju kannatama ja kõlbmatuna hüljatama vanemate ja ülempreestrite ja kirjatundjate poolt ja tapetama ja kolmandal päeval jälle üles äratatama!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 23, 'Aga Ta ütles kõikidele: \"Kui keegi tahab Minu järele tulla, see salaku end ja võtku oma rist iga päev enese peale ja järgigu Mind.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 24, 'Sest kes iganes oma hinge tahab päästa, see kaotab selle, aga kes iganes oma hinge kaotab Minu pärast, see päästab selle!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 25, 'Sest mis kasu on inimesel sellest, kui ta kogu maailma kasuks saaks, aga kaotaks iseenese või läheks hukka?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 26, 'Sest kes Minu ja Mu sõnade pärast häbeneb, selle pärast häbeneb Inimese Poeg, kui Ta tuleb Omas ja Isa ja pühade Inglite auhiilguses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 27, 'Aga Ma ütlen teile tõesti: On mõningad siin seisjaist, kes ei maitse surma, kuni nad näevad Jumala Riiki!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 28, 'Ent arvata kaheksa päeva pärast neid kõnesid sündis, et Ta võttis Enesega Peetruse ja Johannese ja Jakoobuse ja läks mäele palvetama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 29, 'Ja kui Ta palvetas, muutus Tema näoilme teissuguseks ja Tema riietus läks valgeks ja hiilgavaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 30, 'Ja vaata, kaks meest kõnelesid Temaga; need olid Mooses ja Eelija.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 31, 'Nemad olid ilmunud auhiilguses ja kõnelesid Tema eluotsast, mis tal Jeruusalemas tuli täide saata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 32, 'Aga Peetrus ja ta kaaslased olid suikunud raskesse unne; ent üles ärgates nad nägid Tema auhiilgust ja neid kaht meest seisvat Tema juures.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 33, 'Kui siis need Temast olid lahkumas, ütles Peetrus Jeesusele: \"Õpetaja, siin on meil hea olla! Teeme kolm telki, ühe Sinule ja ühe Moosesele ja ühe Eelijale!\" Kuid ta ei teadnud, mida ta rääkis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 34, 'Aga kui ta seda rääkis, tekkis pilv ja varjas nad; ja nemad kartsid pilve sisse jõudes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 35, 'Ja hääl kostis pilvest ning ütles: \"See on Minu Äravalitud Poeg, Teda kuulake!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 36, 'Ja kui see hääl oli kostnud, leiti Jeesus üksi olevat. Ja nad olid vait ega kuulutanud neil päevil kellelegi midagi sellest, mida nad olid näinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 37, 'Järgmisel päeval, kui nad mäelt alla läksid, tuli palju rahvast Temale vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 38, 'Ja vaata, üks mees rahvahulga seast kisendas ning ütles: \"Õpetaja, ma palun Sind vaadata mu poega, sest ta on mu ainuke!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 39, 'Ja vaata, vaim haarab teda, ja ta karjatab äkki, ja vaim raputab teda, nõnda et ta ajab suust vahtu; ja see lahkub vaevalt teda piinamast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 40, 'Ja mina palusin Sinu jüngreid, et nad ajaksid ta välja, ja nad ei suutnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 41, 'Aga Jeesus vastas ning ütles: \"Oh sa uskmatu ja pöörane tõug! Kui kaua Ma pean olema teie juures ja kannatama teiega? Too siia oma poeg!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 42, 'Kui ta alles oli tulemas, kiskus teda kuri vaim ja raputas teda. Aga Jeesus sõitles rüvedat vaimu ja tegi poisi terveks ning andis ta tagasi tema isale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 43, 'Ent kõik hämmastusid Jumala suurest väest. Aga kui kõik panid imeks seda, mida Ta tegi, ütles Ta Oma jüngritele:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 44, '\"Pange teie oma kõrvu need sõnad. Sest Inimese Poeg antakse inimeste kätte!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 45, 'Ent nemad ei mõistnud seda sõna, ja see oli pandud varjule nende eest, et nad sellest aru ei saaks. Ja nad kartsid küsida temalt selle sõna kohta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 46, 'Aga nende seas tõusis vaidlus, kes küll nende seast peaks olema suurem.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 47, 'Jeesus, teades nende südame mõtlemisi, võttis lapsukese ja seadis selle enese kõrvale');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 48, 'ning ütles neile: \"Kes iganes selle lapsukese võtab vastu Minu nimel, võtab Mind vastu; ja kes iganes võtab vastu Mind, võtab vastu Selle, Kes Mind on läkitanud. Sest kes teist kõigist on vähem, see on suur!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 49, 'Siis Johannes hakkas rääkima ning ütles: \"Õpetaja, me nägime üht meest Sinu nimel kurje vaime välja ajavat; ja me keelasime teda, sellepärast et ta ühes meiega Sind ei järgi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 50, 'Jeesus ütles talle: \"Ärge keelake, sest kes ei ole meie vastu, see on meie poolt!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 51, 'Aga sündis, kui Tema ülesvõtmisepäevad olid täis saamas, et ta suundus kindlasti minema Jeruusalema poole,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 52, 'ja Ta läkitas käskjalad Enese eele. Ja need läksid ja tulid ühte samaarlaste külla, valmistama Temale öömaja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 53, 'Ent need ei võtnud Teda vastu, sest Ta oli minemas Jeruusalema poole.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 54, 'Kui Ta jüngrid Jakoobus ja Johannes seda nägid, ütlesid nad: \"Issand, kas tahad, et me käsime tulla tule taevast maha ja nad hävitada?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 55, 'Aga ta pöördus ümber ja sõitles neid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 56, 'Ja nad läksid teise alevisse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 57, 'Ja kui nad olid sinna minemas, ütles teel keegi Temale: \"Issand, ma tahan Sind järgida, kuhu Sa iganes lähed!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 58, 'Jeesus ütles talle: \"Rebastel on augud ja taeva lindudel on pesad, aga Inimese Pojal ei ole aset, kuhu Ta Oma pea paneks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 59, 'Kellelegi teisele Ta ütles: \"Järgi mind!\" Ent see ütles: \"Issand, luba mind enne minna ja oma isa maha matta!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 60, 'Aga Ta ütles temale: \"Lase surnuid oma surnud matta, sina aga mine ja kuuluta Jumala Riiki!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 61, 'Ja veel üks teine ütles: \"Issand, ma tahan Sind järgida! Aga luba mind enne jätta jumalaga oma kodakondsed!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 62, 'Aga Jeesus ütles temale: \"Ükski, kes paneb oma käe adra külge ja vaatab tagasi, ei kõlba Jumala Riigile!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 1, 'Pärast seda määras Issand teised seitsekümmend ja läkitas nad kahekaupa enese eele igasse linna ja paika, kuhu Ta ise mõtles minna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 2, 'Ja Ta ütles neile: \"Lõikust on palju, aga töötegijaid vähe; seepärast paluge lõikuse Issandat, et ta läkitaks välja töötegijaid Oma lõikusele!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 3, 'Minge! Vaata, ma läkitan teid kui tallesid huntide keskele!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 4, 'Ärge kandke kukrut ega pauna ega jalatseid ja teel ärge kedagi teretage.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 5, 'Kuhu majasse te aga iganes sisse lähete, seal ütelge esiti: \"Rahu olgu sellele kojale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 6, 'Ja kui iganes seal on rahulaps, siis hingab teie rahu tema peal; aga kui mitte, siis tuleb rahu tagasi teie peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 7, 'Aga sinna majasse jääge, sööge ja jooge, mis neil on, sest töötegija on oma palga väärt; ärge käige ühest majast teise.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 8, 'Ja kuhu linna te iganes lähete ja kus teid vastu võetakse, seal sööge, mida teile ette pannakse;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 9, 'ja tehke selle paiga haiged terveks ja ütlege neile: Jumala Riik on teie lähedal!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 10, 'Aga kuhu linna te iganes lähete ja kus teid vastu ei võeta, seal minge välja ta uulitsaile ning ütelge:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 11, 'Tolmugi, mis teie linnast on hakanud meie jalgade kulge, me pühime ära teile; ometi teadke, et Jumala Riik on lähedal!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 12, 'Ma ütlen teile, et Soodomal on sel päeval Hõlpsam põli kui tol linnal!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 13, 'Häda sulle, Korasin, häda sulle, Betsaida! Sest kui Tüüroses ja Siidonis oleksid sündinud need vägevad teod, mis teie juures on sündinud, küll nad oleksid ammu kotiriides ja tuhas istudes parandanud meelt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 14, 'Ometi peab Tüürosel ja Siidonil olema kohtus hõlpsam põli kui teil!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 15, 'Ja sina, Kapernaum, eks sa olnud ülendatud taevani? Sind tõugatakse põrguni alla!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 16, 'Kes teid kuuleb, see kuuleb Mind, ja kes teid hülgab, see hülgab Mind; aga kes hülgab Mind, see hülgab Selle, Kes Mind on läkitanud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 17, 'Siis need seitsekümmend tulid tagasi rõõmuga ja ütlesid: \"Issand, ka kurjad vaimud alistuvad meile Sinu nime tõttu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 18, 'Aga Ta ütles neile: \"Ma nägin saatana nagu välgu taevast maha langevat!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 19, 'Vaata, Ma olen annud teile meelevalla astuda madude ja skorpionide peale ja vaenlase kõige väe peale; ja miski ei tee teile kahju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 20, 'Ometi ärge rõõmutsege, et vaimud teile alistuvad, vaid rõõmutsege palju enam sellest, et teie nimed on taevasse kirja pandud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 21, 'Selsamal tunnil Ta rõõmustus Pühas Vaimus ja ütles: \"Mina tänan Sind, Isa, taeva ja maa Issand, et Sa oled tarkade ja mõistlike eest selle pannud varjule ja oled selle ilmutanud väetimaile! Jah, Isa, see on nõnda olnud Sulle meelepärast!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 22, 'Kõik on Mu Isa Mulle annud! Ja ükski ei tunne, kes on Poeg, kui vaid Isa, ja kes on Isa, kui vaid Poeg ja kellele iganes Poeg tahab ilmutada!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 23, 'Ja Ta pöördus jüngrite poole eriti ning ütles: \"Õndsad on silmad, kes näevad, mida teie näete!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 24, 'Sest Ma ütlen teile, et palju prohveteid ja kuningaid on tahtnud näha, mida teie näete, ja ei ole näinud, ja kuulda, mida teie kuulete, ja ei ole kuulnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 25, 'Ja vaata, keegi käsutundja astus esile, kiusas Teda ja ütles: \"Õpetaja, mis ma pean tegema, et ma päriksin igavese elu?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 26, 'Aga Tema ütles talle: \"Mis on käsuõpetuses kirjutatud? Kuidas sa loed?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 27, 'Tema vastas ning ütles: \"Armasta Issandat, oma Jumalat, kõigest oma südamest ja kõigest oma hingest ja kõigest oma jõust ja kõigest oma meelest, ja oma ligimest nagu iseennast.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 28, 'Aga Ta ütles temale: \"Sa oled õieti vastanud; tee seda, ja sa pead elama!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 29, 'Tema aga tahtis iseennast õigeks teha ja ütles Jeesusele: \"Kes siis on mu ligimene?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 30, 'Aga vastates Jeesus ütles: \"Üks inimene läks Jeruusalemast alla Jeerikosse ja sattus röövlite kätte. Kui need ta riided olid riisunud ja temale hoope annud, läksid nad ära, jättes ta poolsurnuna maha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 31, 'Juhtumisi tuli keegi preester sedasama teed mööda alla ja nägi teda ning läks mööda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 32, 'Nõndasamuti ka leviit. Kui ta tuli sinna paika ja nägi teda, läks ta mööda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 33, 'Aga üks samaarlane käis seda teed ja tuli tema juure; ja kui ta teda nägi, läks ta meel haledaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 34, 'Ja ta astus ligi, sidus ta haavad ning valas peale õli ja viina, tõstis ta oma looma selga ning viis ta öömajale ja kandis hoolt tema eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 35, 'Ja teisel hommikul ära minnes võttis ta välja kaks teenarit, andis need peremehele ja ütles: Kanna hoolt tema eest, ja mida sa veel peaksid kulutama, seda ma maksan sulle tagasi tulles.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 36, 'Kes neist kolmest oli sinu arvates ligimene sellele, kes oli sattunud röövlite kätte?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 37, 'Aga tema ütles: \"See, kes tema peale halastas!\" Siis ütles Jeesus temale: \"Mine ja tee sina nõndasamuti!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 38, 'Aga kui nad rändasid, läks Ta ühte alevisse. Ent üks naine, Marta nimi, võttis Tema vastu oma majasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 39, 'Ja temal oli õde, keda hüüti Maarjaks; seegi istus Issanda jalge ees ja kuulas Tema kõnet.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 40, 'Marta aga tegi tegemist mitmesuguse talitusega. Ta tuli sinna ja ütles: \"Issand, kas Sa ei hooli sellest, et mu õde laseb mind üksi talitada? Ütle ometi temale, et ta mind aitaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 41, 'Aga Issand vastas ning ütles talle: \"Marta, Marta, sa muretsed ja teed enesele tüli paljude asjadega;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 42, 'kuid tarvis on vähe, õigupoolest üht! Maarja on selle hea osa valinud ja seda ei võeta temalt ära!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 1, 'Ja kui Ta ühes paigas oli palvetamas ja oli lõpetanud, ütles üks Ta jüngreist Temale: \"Issand, õpeta meid palvetama nõnda nagu Johannes õpetas oma jüngreid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 2, 'Aga Ta ütles neile: \"Kui te palvetate, siis ütelge: Isa! Pühitsetud olgu Sinu nimi; Sinu riik tulgu;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 3, 'meie igapäevane leib anna meile iga päev;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 4, 'ja anna meile andeks meie patud, sest meiegi anname andeks igaühele, kellel on meiega võlgu; ja ära saada meid kiusatusse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 5, 'Ja Ta ütles neile: \"Kellel teie seast on sõber ja ta läheks südaöösel ta juure ja ütleks temale: Sõber, laena mulle kolm leiba,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 6, 'sest mu sõber on teelt tulnud mu juure ja mul ei ole, mida talle ette paneksin!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 7, 'Kas tema peaks toast vastama ning ütlema: Ära tee mulle tüli, uks on juba lukus ja mu lapsukesed on minuga voodis; ma ei või üles tõusta sulle andma!?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 8, 'Ma ütlen teile, kui ta üles ei tõuseks ja talle ei annaks, sellepärast et ta on tema sõber, siis tõuseks ta ometi üles tema pealekäimise pärast ning annaks talle, niipalju kui talle vaja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 9, 'Ka Mina ütlen teile: Paluge, siis antakse teile; otsige, siis te leiate; koputage, siis avatakse teile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 10, 'Sest igaüks, kes palub, see saab, ja kes otsib, see leiab, ja kes koputab, sellele avatakse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 11, 'Ent missugune isa on teie seas, kellelt poeg palub kala, et ta annaks sellele kala asemel mao?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 12, 'Või kui ta palub muna, ta annaks talle skorpioni?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 13, 'Kui nüüd teie, kes olete kurjad, märkate anda häid ande oma lastele, kui palju enam Isa taevast annab Püha Vaimu neile, kes Teda paluvad.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 14, 'Ja kord Ta ajas kurja vaimu välja; ja see oli keeletu. Aga kui kuri vaim oli väljunud, hakkas keeletu rääkima ja rahvahulgad panid seda imeks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 15, 'Aga mõningad nende seast ütlesid: \"Ta ajab kurje vaime välja Peeltsebuli, kurjade vaimude ülema abil!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 16, 'Aga teised kiusasid Teda ning nõudsid Temalt tunnustähte taevast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 17, 'Ent Tema mõistis ära nende mõtted ja ütles neile: \"Iga kuningriik, mis on isekeskis riius, hävib ja koda langeb koja peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 18, 'Kui saatangi on riius isekeskis, kuidas võib tema kuningriik püsida? Te ju ütlete, et Mina ajavat kurje vaime välja Peeltsebuli abil!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 19, 'Aga kui Mina kurje vaime välja ajan Peeltsebuli abil, kelle abil ajavad teie pojad neid välja? Sellepärast saavad nemad teie kohtumõistjaiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 20, 'Ent kui Mina kurje vaime välja ajan Jumala sõrmega, siis on Jumala Riik jõudnud teie juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 21, 'Kui vägev relvastatud mees valvab oma elamut, siis on ta vara rahus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 22, 'Aga kui temast vägevam peale tuleb ja võidab tema, siis ta võtab ära kõlk ta relvad, millede peale ta lootis, ja jagab temalt saadud saagi välja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 23, 'Kes Minu poolt ei ole, on Minu vastu; ja kes Minuga ei kogu, see hajutab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 24, 'Kui rüve vaim on inimesest välja läinud, käib ta mööda põudseid paiku ja otsib hingamist. Ja kui ta ei leia, ütleb ta: Ma pöördun tagasi oma kotta, kust ma väljusin!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 25, 'Ja kui ta tuleb, leiab ta selle pühitud ning ehitud olevat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 26, 'Siis ta läheb ja võtab kaasa teist seitse vaimu, kes on kurjemad temast, ja nad tulevad sisse ning elavad seal. Ja selle inimese viimne lugu läheb pahemaks kui eelmine!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 27, 'Kui Ta seda rääkis, sündis, et üks naine rahva seast tõstis oma häält ning ütles Temale: \"Õnnis on ihu, mis Sind on kannud, ja rinnad, mida Sina oled imenud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 28, 'Aga Tema ütles: \"Jah, õndsad on need, kes Jumala sõna kuulevad ja seda tallele panevad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 29, 'Kui rahvahulki murdu kokku tuli, hakkas Ta kõnelema: \"See sugu on paha sugu; ta otsib tunnustähte, ja talle ei anta muud tähte kui vaid prohvet Joona täht.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 30, 'Sest nõnda nagu Joona sai täheks Niinive elanikele, nõnda peab ka Inimese Poeg olema sellele soole.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 31, 'Lõunamaa kuninganna tõuseb üles kohtupäeval selle soo meeste kõrval ja mõistab nad hukka; sest ta tuli ilmamaa otsast kuulama Saalomoni tarkust. Ja vaata, siin on rohkem kui Saalomon!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 32, 'Niinive mehed tõusevad üles kohtupäeval selle soo kõrval ja mõistavad selle hukka; sest nad parandasid meelt Joona jutluse tõttu. Ja vaata, siin on rohkem kui Joona!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 33, 'Ükski ei süüta küünalt ega pane seda varjule ega vaka alla, vaid küünlajalale, et sissetulijad valgust näeksid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 34, 'Ihu küünal on su silm. Kui su silm on korras, siis on ka kõik su ihu valguses; aga kui ta on rikkis, siis on ka su ihu pime.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 35, 'Katsu siis, et valgus, mis on sinu sees, ei oleks pimedus!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 36, 'Kui nüüd kogu su ihu on valguses, nõnda et tal sugugi ei ole pimedust, siis on ta täiesti valgustatud, nõnda nagu küünal valgustaks sind oma välkuva leegiga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 37, 'Kui Tema alles nõnda rääkis, palus Teda keegi variser enese juure lõunat sööma. Ja Ta läks sisse ja istus lauda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 38, 'Aga variser pani imeks, kui ta nägi, et Ta enne söömist ennast ei pesnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 39, 'Siis Issand ütles temale: \"Teie, variserid, teete küll väljastpoolt puhtaks karika ja vaagna, aga seestpoolt te olete täis rüüstet ja kurjust!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 40, 'Te mõistmatumad! Eks see, kes tegi välispidise, ole teinud ka seespidise?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 41, 'Ent andke armuanniks, mis teie sees on, ja vaata, siis on teile kõik puhas!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 42, 'Aga häda teile, variseridele, et te maksate kümnist mündist ja ruudist ja kõigest aiarohust ja lähete mööda õiglusest ja Jumala armastusest! Seda peab tegema ja teist ei tohi tegemata jätta!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 43, 'Häda teile variseridele, et te armastate esimest istet kogudusekodades ja teretusi turgudel!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 44, 'Häda teile, et te olete nagu äravajunud hauad, ja inimesed käivad nende peal, ilma et nad teaksid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 45, 'Aga üks käsutundjaist kostis ja ütles Temale: \"Õpetaja, seda üteldes sa teotad ka meid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 46, 'Ent Tema ütles: \"Häda ka teile käsutundjaile, sest te panete inimestele peale rängad koormad ja ise ei puutu ühe oma sõrmegagi neisse koormaisse!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 47, 'Häda teile, et te ehitate prohvetitele hauamärke, aga teie isad on nad tapnud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 48, 'Nii te olete ise tunnistajad, et teil on hea meel oma isade tegudest; sest nemad tapsid nad, aga teie ehitate nende hauamärke!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 49, 'Sellepärast ütleb ka Jumala tarkus: Ma läkitan nende juure prohveteid ja apostleid, ja nad tapavad mõned neist ja kiusavad neid taga,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 50, 'et sellelt sugupõlvelt nõutaks kõigi prohvetite verd, mis on ära valatud alates maailma asutamisest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 51, 'Aabeli verest Sakarja verest saadik, kes sai hukka altari ja Jumala koja vahel. Tõesti, Ma ütlen teile, seda nõutakse sellelt sugupõlvelt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 52, 'Häda teile käsutundjaile, et te olete ära võtnud tunnetuse võtme! Ise te ei ole sisse läinud ja olete keelanud neid, kes sisse läheksid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 53, 'Ja kui Ta sealt väljus, hakkasid kirjatundjad ja variserid Teda kangesti kimbutama ja usutlema mitmeis asjus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 54, 'et kavaldamisega midagi Tema suust kätte saada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 1, 'Kui sel ajal rahvast oli tuhandeti kokku tulnud, nõnda et nad üksteist tallasid, hakkas Ta kõigepealt kõnelema Oma jüngritele: \"Hoiduge variseride haputaigna, see on silmakirjalikkuse eest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 2, 'Ei ole midagi varjatud, mis ei tuleks ilmsiks, ega ole midagi salajas, mida ei saadaks teada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 3, 'Sellepärast peab kõike, mida te ütlete pimedas, kuuldama valges; ja mida te üksteise kõrva räägite kambrites, seda peab kuulutatama katustelt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 4, 'Aga Ma ütlen teile, Oma sõpradele: Ärge kartke neid, kes tapavad ihu ja pärast ei saa rohkem midagi teha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 5, 'Ent Ma näitan teile, keda teil tuleb karta: kartke teda, kellel on meelevald pärast tapmist heita põrgusse. Tõesti, Ma ütlen teile, teda kartke!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 6, 'Eks viis varblast müüda kahe veeringu eest? Ja ükski neist ei ole unustatud Jumala ees!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 7, 'Aga teie juuksekarvadki on kõik ära loetud! Ärge kartke, teie olete hinnalisemad kui palju varblasi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 8, 'Ent Ma ütlen teile: Kes iganes Mind tunnistab inimeste ees, seda tunnistab ka Inimese Poeg Jumala Inglite ees!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 9, 'Aga kes Mind salgab inimeste ees, see salatakse Jumala Inglite ees!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 10, 'Ja igaüks, kes Inimese Poja vastu räägib sõna, sellele antakse andeks; aga sellele, kes pilkab Püha Vaimu, ei anta mitte andeks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 11, 'Aga kui teid viiakse kogudusekodadesse ja ülemate ja valitsejate ette, ärge olge mures, kuidas või mida te enese eest kostate või mida ütlete;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 12, 'sest Püha Vaim õpetab teile selsamal tunnil, mida tuleb ütelda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 13, 'Siis keegi rahva seast ütles Talle: \"Õpetaja ütle mu vennale, et ta minuga jagaks pärandi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 14, 'Aga Tema ütles talle: \"Inimene, kes on Mind seadnud teile kohtumõistjaks või jagajaks?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 15, 'Ja Ta ütles neile: \"Vaadake ette ja hoiduge ahnuse eest; sest külluseski ei olene kellegi elu sellest, mis tal on!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 16, 'Ja Ta rääkis neile ka tähendamissõna, üteldes: \"Ühe rikka mehe põllumaa oli hästi vilja kannud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 17, 'Ja ta mõtles iseeneses nõnda: Mis ma teen? Sest mul ei ole, kuhu ma koguksin oma vilja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 18, 'Ja ta ütles: Seda ma teen: ma kisun maha oma aidad ja ehitan suuremad, ja sinna ma kogun kõik oma vilja ja oma vara,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 19, 'ja ütlen oma hingele: Hing, sul on palju hüüt tagavaraks mitmeks aastaks; ole nüüd rahul, söö, joo ja ole rõõmus!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 20, 'Aga Jumal ütles temale: Sina meeletu! Selsamal ööl nõutakse sult sinu hing; kellele saab siis, mis sa oled soetanud?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 21, 'Nõnda on selle lugu, kes enesele kogub tagavara ja ei ole rikas Jumalas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 22, 'Siis Ta ütles oma jüngritele: \"Seepärast ma ütlen teile: Ärge olge mures oma hinge pärast, mida süüa, ega oma ihu pärast, millega riietuda!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 23, 'Sest hing on enam kui toidus ja ihu enam kui riided!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 24, 'Pange tähele kaarnaid! Nad ei külva ega lõika; neil ei ole varakambrit ega aita, ja Jumal toidab neid. Kui palju hinnalisemad olete teie kui on linnud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 25, 'Aga kes teie seast võib muretsemisega oma pikkusele ühegi küünra jätkata?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 26, 'Kui te nüüd kõige vähematki ei suuda, miks te siis olete mures muu pärast?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 27, 'Pange tähele lilli, kuidas nad ei ketra ega koo; aga ma ütlen teile: Saalomongi kõiges oma hiilguses pole olnud nõnda ehitud kui üks nendest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 28, 'Kui nüüd Jumal rohtu, mis täna kasvab ja homme visatakse ahju, nõnda ehib, kui palju enam teid, te nõdrausulised!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 29, 'Ka teie ärge küsige, mida süüa või mida juua, ja ärge olge kärsitumad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 30, 'Sest kõike seda nõuavad taga maailma rahvad; teie Isa teab ju, et te seda vajate.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 31, 'Otsige vaid Tema Riiki, ja see kõlk antakse teile pealegi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 32, 'Ära karda, sa pisuke karjuke, sest teie Isa on heaks arvanud anda teile Kuningriigi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 33, 'Müüge ära, mis teil on, ja andke armuande! Valmistage enestele kukrud, mis ei vanane, ja varandus taevas, mis ei vähene, kuhu varas ligi ei pääse ja mida koi ei riku!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 34, 'Sest kus teie varandus on, seal on ka teie süda!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 35, 'Teie niuded olgu vöötatud ja küünlad põlegu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 36, 'Ja teie olge inimeste sarnased, kes ootavad oma isandat, millal ta lahkub pulmast, et kui ta tuleb ja koputab, nad kohe temale avaksid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 37, 'Õndsad on need sulased, keda isand tulles leiab valvamast! Tõesti, Ma ütlen teile, tema vöötab enese ja paneb nad lauda istuma ja tuleb ja teenib neid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 38, 'Ja tulgu ta teisel või kolmandal öövahikorral, kui ta nad leiab nõnda - õndsad on need sulased!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 39, 'Aga seda teadke: Kui peremees teaks, mil tunnil varas tuleb, ta ei laseks oma kotta sisse murda!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 40, 'Olge siis ka teie valmis, sest Inimese Poeg tuleb tunnil, mil te ei arvagi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 41, 'Aga Peetrus küsis: \"Issand, kas ütled selle tähendamissõna meile või kõikidele?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 42, 'Ja Issand ütles: \"Kes on siis ustav ja mõistlik majapidaja, keda isand seab oma pere üle neile õigel ajal andma määratud moona?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 43, 'Õnnis see sulane, keda isand tulles leiab nõnda tegemast!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 44, 'Tõesti, Ma ütlen teile, ta paneb tema üle kõige oma vara!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 45, 'Aga kui see sulane ütleb oma südames: Mu isand viibib tulles! ja ta hakkab peksma poisse ja tüdrukuid, ja sööma ja jooma ja purjutama,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 46, 'siis selle sulase isand tuleb päeval, mil ta ei oota teda, ja tunnil, mida ta ei tea, ja raiub ta pooleks ja annab temale osa ühes uskmatutega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 47, 'Aga sulane, kes teadis oma isanda tahtmist, aga ei valmistanud ega teinud tema tahtmise järele, saab palju hoope.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 48, 'Aga kes ei teadnud, kuid tegi, mis on hoopide väärt, saab pisut hoope. Sest kellele on palju antud, sellelt nõutakse palju; ja kelle hoole on palju jäetud, sellelt päritakse veel rohkem!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 49, 'Tuld Ma tulin viskama maa peale; ja mis Ma muud tahaksin, kui et see juba oleks süüdatud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 50, 'Aga Mind peab ristitama ristimisega, ja kuidas on mul kitsas käes, kuni see on lõpetatud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 51, 'Kas mõtlete, et Ma olen tulnud andma rahu maa peale? Mitte sugugi, ütlen Ma teile, vaid lahkmeelt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 52, 'Sest nüüdsest alates peavad viis ühes majas olema lahkmeeles, kolm kahe vastu ja kaks kolme vastu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 53, 'isa poja vastu ja poeg isa vastu, ema tütre vastu ja tütar ema vastu, ämm oma minia vastu ja minia ämma vastu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 54, 'Aga Ta ütles ka rahvale: \"Kui te näete pilve tõusvat lääne poolt, siis te kohe ütlete: Raske sadu tuleb! Ja see sünnib nõnda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 55, 'Ja kui näete lõunatuult puhuvat, siis te ütlete: Kuum tuleb! Ja see sünnib nõnda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 56, 'Te silmakirjatsejad! Maa ja taeva nägu te oskate mõista, aga kuidas te ei mõista sedasinast aega?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 57, 'Ja miks te ka iseenestest ei otsusta, mis on õige?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 58, 'Sellepärast kui sa oma vastasega lähed ülema ette, siis püüa temaga asi lahendada teel, et ta sind ei veaks kohtuniku ette ja kohtunik sind ei annaks kohtusulase kätte ja kohtusulane sind ei heidaks vanglasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 59, 'Ma ütlen sulle, et sa ei pääse sealt, enne kui maksad viimse leptoni!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 1, 'Selsamal ajal olid saabunud mõned, kes jutustasid Temale galilealastest, kellede vere Pilaatus oli seganud nende ohvritega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 2, 'Ja Jeesus hakkas kõnelema ning ütles neile: \"Kas arvate, et need galilealased olid suuremad patused kui kõik muu Galilea rahvas, et nad seda said kannatada?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 3, 'Ma ütlen teile, et mitte sugugi, vaid kui te ei paranda meelt, lähete kõik samuti hukka!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 4, 'Või arvate, et need kaheksateistkümmend, kellede peale langes Siiloa torn ja tappis nad, olid suuremad süüalused kui kõik inimesed, kes elavad Jeruusalemas?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 5, 'Ma ütlen teile, et mitte sugugi, vaid kui te ei paranda meelt, siis lähete kõik samuti hukka!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 6, 'Ja Ta ütles selle tähendamissõna: \"Ühel inimesel oli viigipuu; see oli istutatud tema viinamäkke. Ja ta tuli ning otsis sellelt vilja, aga ei leidnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 7, 'Siis ta ütles viinamäe aednikule: Vaata, kolm aastat ma käin otsimas sellelt viigipuult vilja, aga ei leia. Raiu ta maha! Miks ta maad raiskab?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 8, 'Aga see vastas ning ütles talle: Isand, jäta ta veel sellekski aastaks, kuni ma ta ümber kaevan ja talle panen sõnnikut!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 9, 'Ehk ta hakkab edaspidi vilja kandma; aga kui mitte, siis raiu ta maha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 10, 'Ja Ta oli õpetamas ühes kogudusekojas hingamispäeval.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 11, 'Ja vaata, seal oli naine, kellel oli olnud haiguse vaim kaheksateistkümmend aastat; naine oli küürus ega saanud ennast sugugi sirgeks ajada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 12, 'Teda nähes kutsus Jeesus ta enese juure ja ütles talle: \"Naine, sa oled lahti oma haigusest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 13, 'Ja Ta pani oma käed tema peale; ja kohe ajas naine enese sirgeks ja andis Jumalale au.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 14, 'Aga kogudusekoja ülem, kes pahandus sellest, et Jeesus tegi terveks hingamispäeval, hakkas rääkima ning ütles rahvale: \"Kuus päeva on, mil tööd tehakse; tulge ometi neil päevil ja laske endid ravida, aga mitte hingamispäeval!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 15, 'Aga Issand vastas temale ning ütles: \"Te silmakirjatsejad! Eks igaüks teie seast päästa oma härja või eesli sõime küljest hingamispäeval ja vii teda jooma?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 16, 'Kas siis teda, kes on Aabrahami tütar, keda saatan, vaata, kaheksateistkümmend aastat on sidunud, ei pidanud päästetama sellest köidikust hingamispäeval?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 17, 'Ja kui Ta seda ütles, häbenesid kõik ta vastased, ja kogu rahvas rõõmustus kõigist aulistest tegudest, mida Tema tegi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 18, 'Siis Ta ütles: \"Mille sarnane on Jumala Riik ja millega Ma võrdlen seda?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 19, 'See on sinepiivakese sarnane, mille inimene võttis ja külvas oma aeda; ja see kasvas ja sai puuks, ja taeva linnud tegid pesad tema okstele!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 20, 'Ja Ta ütles veel: \"Millega Ma võrdleksin Jumala Riiki?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 21, 'See on haputaigna sarnane, mille üks naine võttis ja segas kolme vaka jahu hulka, kuni kõik läks hapnema!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 22, 'Ja Ta rändas mööda linnu ja külasid õpetades ja edasi minnes teed Jeruusalemma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 23, 'Siis küsis keegi Temalt: \"Issand, kas on pisut neid, kes saavad õndsaks?\" Aga Tema ütles neile:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 24, '\"Võidelge, et te läheksite sisse kitsast uksest, sest paljud, ütlen ma teile, püüavad sisse minna, aga ei jaksa mitte!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 25, 'Sellest ajast, kui peremees on üles tõusnud ja on pannud ukse lukku, hakkate te seisma õues ja koputama uksele, üteldes: Issand, ava meile! Ja Tema vastab teile, üteldes: Mina ei tunne teid, kust te olete!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 26, 'Siis te hakkate ütlema: Me oleme Sinu ees Söönud ja joonud ja meie uulitsais Sa õpetasid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 27, 'Ja Tema ütleb teile: Ma ei tunne teid, kust te olete! Jääge Minust eemale, kõik ülekohtutegijad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 28, 'Seal on ulumine ja hammaste kiristamine, kui te näete Aabrahami ja Iisakit ja Jaakobit ja kõiki prohveteid Jumala Riigis, kuid endid olevat välja tõugatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 29, 'Ja tuleb inimesi idast ja läänest, põhjast ja lõunast, ja nad istuvad lauas Jumala riigis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 30, 'Ja vaata, on viimseid, kes saavad esimesiks, ja esimesi, kes jäävad viimseiks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 31, 'Samal tunnil tulid mõned variserid ja ütlesid Temale: \"Välju ja mine siit ära, sest Heroodes tahab sind tappa!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 32, 'Tema ütles neile: \"Minge ja ütelge sellele rebasele: Vaata, Ma ajan kurje vaime välja ja teen terveks täna ja homme ja kolmandal päeval ma jõuan eesmärgile!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 33, 'Siiski Ma pean veel rändama täna ja homme ja tunahomme; sest see ei sobi, et prohvet saab hukka mujal kui Jeruusalemas!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 34, 'Jeruusalem, Jeruusalem, kes tapad prohvetid ja viskad kividega surnuks need, kes sinu juure on läkitatud! Kui mitu korda Ma olen tahmud koguda sinu lapsi otsegu kana oma poegi tiibade alla, aga teie ei ole tahtnud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 35, 'Vaata, teie koda jäetakse teil maha! Ja tõesti, Ma ütlen teile, et te ei näe Mind enne, kui tuleb aeg, mil te ütlete: Õnnistatud olgu, kes tuleb Issanda nimel!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 1, 'Ja sündis, kui Tema hingamispäeval tuli ühe variseride ülema kotta leiba võtma, et nad varitsesid Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 2, 'Ja vaata, üks vesitõbine inimene oli Tema ees!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 3, 'Ja Jeesus hakkas rääkima kirjatundjatele ja variseridele ning ütles: \"Kas on luba hingamispäeval terveks teha?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 4, 'Aga nad jäid vait. Ja puudutades Ta tegi tema terveks ja laskis ta minna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 5, 'Siis Ta ütles neile: \"Kes on teie seast, kelle poeg või härg kukub kaevu, et ta teda kohe välja ei tõmba hingamispäeval?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 6, 'Ja nad ei suutnud selle peale midagi vastata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 7, 'Aga tähele pannes, kuidas need, kes olid kutsutud, valisid ülemaid istekohti, rääkis Ta neile tähendamissõna ning ütles neile:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 8, '\"Kui sind keegi on pulma kutsunud, siis ära istu ülemasse paika, sest vahest on tema poolt kutsutud keegi sinust aulisem,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 9, 'ja et see, kes sind ja teda kutsus, ei ütleks sulle: Anna sellele aset! ja sa peaksid siis häbiga istuma alamasse paika.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 10, 'Aga kui sa oled kutsutud, siis mine ja istu alamasse paika, ja kui tuleb see, kes sind kutsus, ja ütleb sulle: Sõber, mine sinna ülemale! siis on sul au nende ees, kes sinuga istuvad ühes lauas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 11, 'Sest igaüht, kes ennast ülendab, alandatakse, ja kes ennast alandab, ülendatakse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 12, 'Aga Ta ütles ka sellele, kes Teda oli kutsunud: \"Kui sa teed lõuna- või õhtusöömaaja, siis ära kutsu oma sõpru ega oma vendi ega oma sugulasi ega rikkaid naabreid, et nad sind jälle ei kutsuks endi juure ja sina nõnda saaksid tasu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 13, 'vaid kui sa teed võõruspidu, siis kutsu vaeseid, vigaseid, jalutumaid, pimedaid;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 14, 'siis sa oled õnnis, sest neil ei ole midagi sulle tasuda, kuna see tasutakse sulle õigete ülestõusmises!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 15, 'Kui üks lauas kaasistujaist seda kuulis, ütles ta Temale: \"Õnnis on, kes leiba sööb Jumala Riigis!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 16, 'Aga Ta ütles temale: \"Üks inimene tegi suure õhtusöömaaja ja kutsus paljusid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 17, 'Ja õhtusöömaaja tunnil ta läkitas oma sulase kutsutuile ütlema: Tulge, sest kõik on juba valmis!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 18, 'Ja nad hakkasid endid kõik ühest suust vabandama. Esimene ütles talle: Ma olen ostnud põllu ja pean välja minema seda vaatama; ma palun sind, vabanda mind!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 19, 'Ja teine ütles: Ma olen ostnud viis paari härgi ja lähen neid katsuma; ma palun sind, vabanda mind!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 20, 'Ja veel teine ütles: Ma olen võtnud naise ega saa sellepärast tulla!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 21, 'Ja sulane tuli tagasi ja teatas seda oma isandale. Siis vihastus kojaisand ja ütles oma sulasele: Mine kohe välja linna laiadele uulitsatele ja kurudesse ja too siia sisse vaesed ja vigased ja jalutumad ja pimedad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 22, 'Ja sulane ütles: Isand, see on tehtud, nõnda nagu sa käskisid; aga veel on ruumi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 23, 'Ja isand ütles sulasele: Mine välja teede peale ja aedade ääre ja sunni sisse tulema, et minu koda täis saaks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 24, 'Sest Ma ütlen teile, et ükski neist kutsutud meestest ei saa maitsta Mu õhtusöömaaega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 25, 'Aga Temaga ühes käis palju rahvast ja Tema pöördus ning ütles neile:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 26, '\"Kui keegi tuleb Minu juure ja ei vihka oma isa ja ema ja naist ja lapsi ja vendi ja õdesid ja veel pealegi oma elu, see ei või olla Mu jünger.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 27, 'Ja kes ei kanna oma risti ega käi Minu järel, see ei või olla Mu jünger.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 28, 'Sest kes teie seast, kui ta tahab ehitada torni, ei istu enne maha ega arva kulu, kas temal on, mis vaja läheb teostamiseks?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 29, 'Et kui ta aluse on rajanud ega suuda lõpetada, kõik vaatajad teda ei hakkaks naerma,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 30, 'üteldes: See mees hakkas ehitama ega suuda lõpule viia!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 31, 'Või missugune kuningas, minnes sõtta võitlema teise kuningaga, ei istu enne maha ega pea nõu, kas ta võib minna kümne tuhandega selle vastu, kes kahekümne tuhandega ta peale tuleb?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 32, 'Ja kui mitte, eks ta läkita, kui teine alles on kaugel, ja palu rahu?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 33, 'Nõnda ka igaüks teie seast, kes mitte ei loobu kõigest, mis tal on, ei või olla Mu jünger.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 34, 'Sool on hea; aga kui sool tuimub, millega seda teha soolaseks?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 35, 'Ei see kõlba maasse ega sõnnikusse; see visatakse välja! Kellel kõrvad on kuulda, see kuulgu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 1, 'Aga kõik tölnerid ja patused lähenesid Temale, Teda kuulama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 2, 'Ja variserid ja kirjatundjad nurisesid ning ütlesid: \"See võtab patuseid vastu ja sööb ühes nendega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 3, 'Aga Ta rääkis neile selle tähendamissõna ning ütles:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 4, '\"Kes teie seast, kui tal on sada lammast ja ühe neist kaotab, ei jäta need üheksakümmend üheksa kõrbe ega lähe kadunule järele, kuni ta tema leiab?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 5, 'Ja kui ta on leidnud, võtab ta rõõmuga tema oma õlgadele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 6, 'ja kui ta koju tuleb, ta kutsub kokku sõbrad ja naabrid ning ütleb neile: Rõõmutsege ühes minuga, sest ma leidsin oma lamba, kes oli kadunud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 7, 'Ma ütlen teile, nõnda on taevas ühest patusest, kes meelt parandab, rohkem rõõmu kui üheksakümne üheksast õigest, kes meeleparandust ei vaja!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 8, 'Või kui ühel naisel on kümme drahmiraha ja ta kaotab ühe drahmi, eks ta süüta küünla ja pühi koda ja otsi hoolega, kuni ta selle leiab?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 9, 'Ja kui ta selle on leidnud, siis ta kutsub kokku sõbrad ja naabrid ja ütleb: Rõõmutsege ühes minuga, sest ma olen leidnud drahmiraha, mille ma kaotasin!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 10, 'Nõnda, ütlen Ma teile, tõuseb rõõm Jumala Inglite keskel ühest patusest, kes meelt parandab!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 11, 'Veel ütles ta: \"Ühel inimesel oli kaks poega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 12, 'Ja noorem neist ütles isale: Isa, anna mulle kätte see osa vara, mis saab minule! Ja isa jagas varanduse nende vahel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 13, 'Ja ei möödunud palju päevi, kui noorem poeg kogus kokku kõik ja siirdus kaugele maale, ja seal ta pillas ära oma vara, elades kõlvatumat elu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 14, 'Aga kui ta kõik oli ära raisanud, tuli sinna maale kange nälg, ja puudus hakkas talle kätte tulema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 15, 'Ja ta läks ja poetas enese ühe kodaniku juure seal maal, ja see saatis ta oma väljadele sigu hoidma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 16, 'Ja ta püüdis oma kõhtu täita kaunadega, mida sead sõid; ja ükski ei annud temale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 17, 'Aga kui ta keskenes endasse, ütles ta: Kui palju palgalisi on mu isal ja neil on leiba küllalt, mina aga suren siia nälja kätte!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 18, 'Ma asun teele ning lähen oma isa juure ja ütlen temale: Isa, ma olen pattu teinud taeva vastu ja sinu ees');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 19, 'ega ole enam väärt, et mind su pojaks hüütaks; pea mind kui üht oma palgalist!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 20, 'Ja ta läks teele ja tuli oma isa juure. Aga kui ta alles kaugel oli, nägi teda tema isa ja tal hakkas hale meel. Ja ta jooksis ja hakkas temale ümber kaela ja andis temale suud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 21, 'Aga poeg ütles talle: Isa, ma olen pattu teinud taeva vastu ja sinu ees ega ole enam väärt, et mind su pojaks hüütaks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 22, 'Ent isa ütles oma sulastele: Tooge kõige kallim rüü ja pange temale selga ja andke temale sõrmus sõrme ja kingad jalga,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 23, 'ja tooge, veristage nuumvasikas ja söögem ning olgem rõõmsad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 24, 'sest see mu poeg oli surnud ja on ellu virgunud, ta oli kadunud ja on leitud! Ja nemad hakkasid rõõmutsema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 25, 'Aga tema vanem poeg oli väljal; ja kui ta sealt tulles jõudis maja ligi, kuulis ta pilli ja ringmängu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 26, 'Siis ta kutsus ühe teenijaist enda juure ja kuulas, mis see peaks olema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 27, 'See ütles temale: Su vend on tulnud ja su isa on veristanud nuumvasika, et ta on saanud tema tervisega tagasi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 28, 'Aga ta vihastus ega tahtnud sisse minna. Siis tuli ta isa välja ja palus teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 29, 'Kuid tema vastas ning ütles isale: Vaata, nii mitu aastat orjan mina sind ja ei ole iialgi astunud üle sinu käsu, ja sina ei ole mulle elades annud üht sikkugi, et ma oleksin võinud rõõmus olla oma sõpradega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 30, 'Aga kui tuli see sinu poeg, kes su vara on ära raisanud hooradega, veristasid sina temale nuumvasika!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 31, 'Tema ütles temale: Laps, sina oled ikka minu juures ja kõik, mis on minu, on sinu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 32, 'Nüüd oli tarvis pidu teha ja rõõmutseda, et see sinu vend oli surnud ja on jälle saanud elavaks, ja et ta oli kadunud ja on leitud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 1, 'Aga Ta ütles ka Oma jüngritele: \"Oli keegi rikas mees, kellel oli majapidaja, ja seda oli tema ees süüdistatud, nagu pillaks see tema vara.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 2, 'Ta kutsus tema ja ütles talle: Kuidas ma seda kuulen sinust? Tee aru oma majapidamisest, sest sa ei või enam maja pidada!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 3, 'Aga majapidaja mõtles iseeneses: Mis ma teen? Minu isand võtab mu käest majapidamise ära; kaevata ma ei jaksa, kerjata on mul häbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 4, 'Ma tean, mis ma teen, et kui ma majapidamisest lahti saan, mind vastu võetaks nende kodadesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 5, 'Ja ta kutsus enda juure igaühe oma isanda võlglasist ja ütles esimesele: Palju sul on võlgu minu isandaga?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 6, 'See ütles: Sada vaati õli! Tema ütles talle: Võta oma arveraamat, istu maha ning kirjuta kohe viiskümmend!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 7, 'Pärast ta ütles teisele: Aga palju on sinul võlgu? Tema vastas: Sada vakka nisu! Ja ta ütles talle: Võta oma arveraamat ning kirjuta kaheksakümmend!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 8, 'Ja isand kiitis seda ülekohtust majapidajat, et ta arukalt oli teinud, sest selle maailma lapsed on oma sugupõlve suhtes arukamad kui valguse lapsed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 9, 'Ja Mina ütlen teile: Tehke endile sõpru ülekohtuse mammonaga, et kui see saab otsa, nemad võtaksid teid vastu igavestesse telkidesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 10, 'Kes ustav on kõige vähemas, see on ustav ka suures; ja kes ülekohtune on kõige vähemas, see on ülekohtune ka suures.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 11, 'Kui te nüüd ülekohtuses mammonas ei ole ustavad olnud, kes võib teie kätte tõelist usaldada?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 12, 'Ja kui te võõra omaga ei ole ustavad olnud, kes võib teile kätte anda meie oma?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 13, 'Ükski sulane ei või teenida kaht isandat; sest kas ta vihkab üht ja armastab teist, või hoiab ühe poole ja põlgab teise ära; te ei või teenida Jumalat ja mammonat!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 14, 'Aga seda kõike kuulsid ka variserid, kes olid rahaahned; ja nad irvitasid Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 15, 'Ja Ta ütles neile: \"Teie olete need, kes endid teevad õigeks inimeste ees; aga Jumal tunneb teie südant. Sest mis inimeste keskel on kõrge, see on Jumala ees jäledus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 16, 'Käsuõpetus ja prohvetid ulatavad Johanneseni; sellest ajast peale kuulutatakse Jumala Riiki ja igaüks tungib väevõimul sinna sisse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 17, 'Aga kergem on, et taevas ja maa hukka lähevad, kui et üks täheke käsuõpetusest hävib!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 18, 'Igauks, kes oma naise enesest lahutab ja võtab teise, see rikub abielu; ja igaüks, kes võtab lahutatud naise, see rikub abielu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 19, 'Oli üks rikas mees. See riietus purpuri ja kalli lõuendiga ja elas iga päev rõõmsasti ja suuresti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 20, 'Aga üks vaene, Laatsarus nimi, oli maas tema värava ees täis paiseid');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 21, 'ja püüdis oma kõhtu täita leivaraasukestega, mis rikka laualt kukkusid. Ent koeradki tulid ja lakkusid tema paiseid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 22, 'Siis sündis, et vaene suri, ja Inglid kandsid ta Aabrahami sülle. Aga ka rikas suri ja maeti maha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 23, 'Ja kui ta põrgus suures valus oli ja oma silmad üles tõstis, nägi ta Aabrahami kaugelt ja Laatsarust tema süles.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 24, 'Ja tema hüüdis ning ütles: Isa Aabraham, halasta mu peale ja läkita Laatsarus, et ta oma sõrme otsa kastaks vette ja jahutaks mu keelt, sest ma tunnen suurt piina selles tuleleegis!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 25, 'Aga Aabraham ütles: Laps, mõtle, et sa oled hea põlve oma elus kätte saanud ja Laatsarus nõndasamuti kurja. Nüüd rõõmustatakse teda siin ja sina oled piinas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 26, 'Ja kõige selle lisaks on meie ja teie vahele seatud suur kuristik, et need, kes siit tahavad minna teie juure, ei saa mitte, ega need, kes seal on, ei saa tulla meie juure!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 27, 'Aga ta ütles: Ma palun siis sind, isa, et sa läkitaksid tema mu isa majasse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 28, 'sest mul on viis venda - et ta kinnitaks neile, et nemadki ei satuks siia piinapaika!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 29, 'Kuid Aabraham ütles: Neil on Mooses ja prohvetid; kuulaku nad neid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 30, 'Aga tema ütles: Ei mitte, isa Aabraham, vaid kui keegi surnuist läheks nende juure, siis nad parandaksid meelt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 31, 'Siis Aabraham ütles talle: Kui nad ei kuula Moosest ja prohveteid, ei nad veenduks ka, kui keegi surnuist üles tõuseks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 1, 'Ja Ta ütles oma jüngritele: \"Võimatu on, et pahandusi ei tuleks; aga häda sellele, kelle läbi need tulevad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 2, 'Sellele oleks tulusam, et veskikivi pandaks temale kaela ja ta visataks merre, kui et ta pahandaks ühe neist pisukesist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 3, 'Pidage endid silmas! Kui su vend pattu teeb, siis noomi teda; ja kui ta kahetseb, anna talle andeks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 4, 'Ja kui ta seitse korda päevas eksib su vastu ja seitse korda päevas pöördub su poole ning ütleb: Ma kahetsen! siis anna temale andeks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 5, 'Ja Apostlid ütlesid Issandale: \"Kasvata meie usku!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 6, 'Aga Issand ütles: \"Kui teil usku oleks sinepiivakese võrra ja te ütleksite sellele metsviigipuule: Juuri end välja ja istuta end merre! siis see kuuleks teie sõna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 7, 'Aga kes teie seast, kellel sulane on kündmas või karja hoidmas, ütleks temale, kui ta põllult tuleb: Tule kohe siia ja istu lauda!?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 8, 'Eks ta pigemini ütle temale: Valmista, mida ma õhtuks söön, ja pane vöö vööle ja talita mind, kuni ma saan söönud ja joonud; ja pärast seda söö ja joo sina.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 9, 'Kas ta seda sulast tänab, et see tegi, mida kästi?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 10, 'Nõnda ka teie: kui te olete kõik teinud, mis teid on kästud, siis ütelge: Me oleme kõlvatumad sulased; me oleme teinud, mis meie kohus oli teha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 11, 'Ja sündis, kui Ta oli teel Jeruusalemma, et Ta läks Samaaria ja Galilea vahelt läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 12, 'Ja kui Ta saabus ühte alevisse, tulid Temale vastu kümme pidalitõbist meest ja jäid eemale seisma,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 13, 'ja nad tõstsid häält ning ütlesid: \"Jeesus, õpetaja, halasta meie peale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 14, 'Neid nähes ütles ta neile: \"Minge ja näidake endid preestritele!\" Ja kui nad läksid, said nad puhtaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 15, 'Aga üks nende seast, nähes enese terveks saanud olevat, läks tagasi ja andis Jumalale suure häälega au');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 16, 'ja heitis Tema jalge ette silmili maha ja tänas Teda. Ja see oli samaarlane.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 17, 'Aga Jeesus kostis ning ütles: \"Eks kümme ole saanud puhtaks? Ent kus on need üheksa?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 18, 'Kas muid ei ole leitud, kes oleksid tulnud tagasi Jumalale au andma, kui aga see muulane?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 19, 'Ja Ta ütles temale: \"Tõuse üles ja mine; su usk on sind aidanud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 20, 'Aga kui variserid Temalt küsisid, millal tuleb Jumala Riik, vastas Ta neile ning ütles: \"Jumala riik ei tule tähelepanu äratades;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 21, 'ei ütelda ka mitte: Vaata siin! või: Vaata seal! sest ennäe, Jumala Riik on seespidi teie sees!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 22, 'Ja Ta ütles jüngritele: \"Päevad tulevad, mil te himustate näha üht Inimese Poja päevist, ja te ei näe mitte!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 23, 'Siis üteldakse teile: Vaata siin! või: Vaata seal! Ärge minge ja ärge ajage seda taga!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 24, 'Sest otsegu välk, mis sähvatab teisel pool taeva serval ja paistab teisele poole taeva servale, nõnda on Inimese Poeg Omal päeval!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 25, 'Kuid enne Ta peab palju kannatama ja sellesinase sugupõlve poolt kõlbmatuna ära heidetama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 26, 'Ja nõnda nagu oli Noa päevil, nõnda on ka Inimese Poja päevil:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 27, 'nad sõid, jõid, võtsid naisi ja läksid mehele selle päevani, mil Noa läks laeva, ja tuli veeuputus ning hävitas nad kõik.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 28, 'Samuti ka, nagu oli Loti päevil: nad sõid, jõid, ostsid, müüsid, istutasid, ehitasid hooneid;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 29, 'aga sel päeval, mil Lott väljus Soodomast, sadas tuld ja väävlit taevast ning hävitas nad kõik.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 30, 'Otse nii peab olema sel päeval, mil Inimese Poeg ilmub!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 31, 'Kes sel päeval on katusel ja kelle riistad on toas, ärgu see tulgu maha neid võtma; ja nõndasamuti, kes on põllul, ärgu mingu koju tagasi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 32, 'Mõtelge Loti naisele!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 33, 'Kes oma hinge püüab säästa, see kaotab selle, ja kes selle kaotab, see hoiab selle elus!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 34, 'Ma ütlen teile, selsamal ööl on kaks ühes voodis: üks võetakse vastu ja teine jäetakse maha;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 35, 'kaks naist jahvatavad üheskoos: üks võetakse vastu ja teine jäetakse maha;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 36, 'kaks meest on väljal: üks võetakse vastu ja teine jäetakse maha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 37, 'Ja nad vastasid ning ütlesid Temale: \"Kus, Issand?\" Aga Ta ütles neile: \"Kus on korjus, sinna kogunevad ka kotkad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 1, 'Aga ta ütles neile tähendamissõna, selleks et alati tuleks palvetada ja mitte tüdida.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 2, 'Ta ütles: \"Ühes linnas oli kohtunik, kes ei kartnud Jumalat ega häbenenud inimesi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 3, 'Samas linnas oli ka lesknaine. See tuli ta juure ja ütles: Kaitse mu õigust mu vastase vastu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 4, 'Ja kaua aega ta ei tahtnud. Kuid pärast ta mõtles iseeneses: Ehk ma küll Jumalat ei karda ja inimesi ei häbene,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 5, 'siis ometigi, kuna see lesk mind tüütab, muretsen ma temale õiguse, et ta viimaks ei tuleks ja mulle näkku ei lööks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 6, 'Siis ütles Issand: \"Kuulge, mida see ülekohtune kohtunik ütleb!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 7, 'Kas siis Jumal ei peaks muretsema õigust oma äravalituile, kes Tema poole kisendavad ööd ja päevad, ja kas Ta peaks viivitama neid aidates?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 8, 'Ma ütlen teile: Küll ta peatselt muretseb neile õiguse! Siiski, kui Inimese Poeg tuleb, kas Ta leiab usku maa pealt?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 9, 'Aga ka mõnede vastu, kes iseeneste peale lootsid, et nad on õiged, ja põlgasid muid, ütles Ta selle tähendamissõna:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 10, '\"Kaks inimest läks üles pühakotta palvetama: üks oli variser ja teine tölner.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 11, 'Variser seisis ning palus iseeneses nõnda: Oh Jumal, ma tänan Sind, et mina ei ole nõnda nagu muud inimesed, röövijad, ülekohtused, abielurikkujad ega nõnda nagu see tölner!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 12, 'Ma paastun kaks korda nädalas; ma annan kümnist kõigest, mis saan!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 13, 'Aga tölner seisis eemal ega tahtnud oma silmigi tõsta taeva poole, vaid lõi enesele vastu rindu ning ütles: Oh Jumal, ole mulle patusele armuline!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 14, 'Ma ütlen teile, et see läks alla oma kotta paremini õigeks mõistetuna kui teine! Sest igaüht, kes ennast ise ülendab, alandatakse; aga kes ennast ise alandab, seda ülendatakse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 15, 'Siis toodi ka lapsukesi Tema juure, et Ta neid puudutaks. Aga seda nähes jüngrid sõitlesid toojaid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 16, 'Ent kuuljad ütlesid: \"Kes siis võib õndsaks saada?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 17, 'Tõesti, Ma ütlen teile, kes Jumala Riiki vastu ei võta nagu lapsuke, see ei pääse sinna sisse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 18, 'Ja üks ülem küsis Temalt ning ütles: \"Hea Õpetaja, mis ma pean tegema, et ma igavese elu päriksin?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 19, 'Aga Jeesus ütles temale: \"Miks sa Mind nimetad heaks? Keegi muu ei ole hea kui ainult Jumal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 20, 'Käsud sa tead: Sa ei tohi abielu rikkuda; sa ei tohi tappa; sa ei tohi varastada; sa ei tohi valet tunnistada; sa pead oma isa ja ema austama!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 21, 'Aga tema ütles: \"Seda kõike ma olen pidanud oma noorestpõlvest alates!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 22, 'Aga kui Jeesus seda kuulis, ütles Ta temale: \"Üht asja on sulle vaja: müü ära kõik, mis sul on, ja jaga vaestele, ja sul on siis varandus taevas; ja tule ning järgi Mind!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 23, 'Aga seda kuuldes sai ta koguni kurvaks; sest ta oli väga rikas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 24, 'Teda nõnda nähes, ütles Jeesus: \"Kui raske on jõukail sisse minna Jumala Riiki!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 25, 'Sest hõlpsam on, et kaamel läheb läbi nõelasilma, kui et rikas sisse läheb Jumala Riiki!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 26, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 27, 'Aga Tema ütles: \"Mis inimestel on võimatu, on Jumalal võimalik!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 28, 'Siis ütles Peetrus: \"Vaata, meie oleme kõigest loobunud ja oleme Sind järginud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 29, 'Aga Tema ütles neile: \"Tõesti, Ma ütlen teile, et ei ole kedagi, kes on loobunud majast või naisest või vendadest või vanemaist või lapsist Jumala Riigi pärast,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 30, 'kes mitte mitu korda rohkem tagasi ei saaks selsinasel ajal ja tulevases maailmas igavest elu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 31, 'Aga Ta võttis need kaksteistkümmend Enese juure ja ütles neile: \"Vaata, me läheme üles Jeruusalemma ja kõik viiakse lõpule, mis prohvetid on kirjutanud Inimese Pojast!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 32, 'Sest Ta antakse ära paganate kätte ja Teda naerdakse ja teotatakse ja Tema peale sülitatakse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 33, 'ja kui nad Teda on rooskadega peksnud, tapavad nad Ta, ja kolmandal päeval Ta tõuseb üles!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 34, 'Aga nemad ei saanud midagi aru sellest, ja see kõne oli varjul nende eest, ja nad ei mõistnud, mida üteldi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 35, 'Aga kui Ta Jeerikole lähenes, istus üks pime tee ääres ja kerjas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 36, 'Kuuldes rahvast mööda minevat, päris ta, mis see peaks olema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 37, 'Talle teatati: \"Jeesus Naatsaretlane läheb mööda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 38, 'Siis ta kisendas ning ütles: \"Jeesus, Taaveti Poeg, halasta mu peale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 39, 'Ja möödaminejad sõitlesid teda, et ta vait jääks. Tema aga karjus palju rohkem: \"Taaveti Poeg, halasta minu peale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 40, 'Jeesus jäi seisma ja käskis talutada teda Enese juure. Kui ta ligidale jõudis, küsis Ta temalt:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 41, '\"Mis sa tahad, et Ma sulle teeksin?\" Tema ütles: \"Issand, et ma jälle näeksin!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 42, 'Ja Jeesus ütles temale: \"Näe jälle! Sinu usk on sind aidanud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 43, 'Ja kohe ta nägi jälle ja järgis Teda ning andis Jumalale au! Ja seda nähes kõik rahvas kiitis Jumalat!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 1, 'Ja tema tuli Jeeriko linna ja läks sealt läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 2, 'Ja vaata, seal oli mees, Sakkeus nimi, ja see oli tölnerite ülem ja oli rikas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 3, 'Ja ta püüdis näha saada Jeesust, kes Ta on, aga ei saanud rahva pärast, sest ta oli väikese kasvuga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 4, 'Ja ta jooksis ettepoole ja ronis metsviigipuu otsa, et Teda näha; sest Jeesus pidi sealtkaudu minema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 5, 'Kui nüüd Jeesus sinna paika jõudis, vaatas Ta üles ja nägi teda ning ütles temale: \"Sakkeus, tule usinasti maha, sest täna Ma pean jääma sinu kotta!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 6, 'Ja ta tuli usinasti maha ja võttis Teda vastu rõõmuga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 7, 'Seda nähes nurisesid kõik ning ütlesid: \"Tema on ühe patuse mehe juure läinud jalgu puhkama!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 8, 'Aga Sakkeus astus Issanda ette ning ütles: \"Vaata, Issand, poole oma varandusest ma annan vaestele; ja kui ma kellelegi olen ülekohut teinud, annan ma neljakordselt tagasi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 9, 'Siis Jeesus ütles temale: \"Täna on sellele kojale õnnistus tulnud, sest ka tema on Aabrahami poeg;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 10, 'sest Inimese Poeg on tulnud otsima ja päästma, mis on kadunud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 11, 'Aga kui nad seda kuulsid, ütles Ta sinna juure veel tähendamissõna, sellepärast et Ta oli Jeruusalema lähedal ja et nemad arvasid Jumala Riigi varsti ilmuvat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 12, 'Sellepärast Ta ütles: \"Üks suursugune inimene läks teele kaugele maale, et omandada kuningriik ja tulla tagasi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 13, 'Aga ta kutsus oma kümme sulast ja andis neile kümme naela raha ning ütles neile: Kaubelge seni, kuni ma tagasi tulen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 14, 'Aga tema kodanikud vihkasid teda ja läkitasid käskjalad temale järele ja lasksid ütelda: Me ei taha, et ta on kuningaks meie üle!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 15, 'Ja sündis, kui tema tuli tagasi ja oli omandanud selle riigi, et ta käskis kutsuda enese juure need sulased, kelledele ta raha oli annud, et teada saada, missugust kasu igaüks kaubeldes oli saavutanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 16, 'Siis tuli sinna esimene ja ütles: Isand, sinu nael raha on tootnud kümme naela!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 17, 'Tema ütles talle: See on hea, sa hea sulane! Et sa kõige vähemas oled ustav olnud, siis olgu sul valitsus kümne linna üle!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 18, 'Ja teine tuli ja ütles: Isand, sinu nael on tootnud viis naela!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 19, 'Aga ta ütles sellelegi: Ja sina valitse viie linna üle!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 20, 'Veelgi tuli teine ja ütles: Isand, vaata, siin on su nael, mida ma olen hoidnud higirätikus;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 21, 'sest ma kartsin sind, et sa oled vali inimene; sa võtad, mida sa ei ole paigale pannud, ja lõikad, mida sa ei ole külvanud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 22, 'Ta ütles temale: Sinu suust ma mõistan sind hukka, sa paha sulane! Kui sa teadsid, et ma olen vali mees ja võtan, mida ma ei ole pannud paigale, ja lõikan, mida ma ei ole külvanud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 23, 'miks sa siis ei annud mu raha panka? Küll ma tulles oleksin selle tagasi nõudnud kasudega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 24, 'Ja ta ütles juuresseisjaile: Võtke nael ära ta käest ja andke sellele, kellel on kümme!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 25, 'Ja nad ütlesid talle: Isand, temal on kümme naela!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 26, 'Ma ütlen teile: Kellel on, sellele antakse, aga kellel ei ole, sellelt võetakse ka see, mis tal on!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 27, 'Aga need mu vaenlased, kes ei tahtnud mind kuningaks, tooge tänna ja tapke nad ära minu ees!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 28, 'Ja kui ta seda oli rääkinud, läks ta edasi Jeruusalema poole.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 29, 'Ja sündis, kui Ta Betfage ja Betaania ligi jõudis, mäe juure, mida kutsutakse Õlimäeks, et Ta läkitas kaks Oma jüngrit');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 30, 'ning ütles: \"Minge vastasolevasse alevisse, ja kui te sinna jõuate, leiate te kinniseotud sälu, kelle seljas ükski inimene ei ole istunud. Päästke ta lahti ja tooge siia.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 31, 'Ja kui keegi teilt küsib: Miks te ta lahti päästate? siis ütelge nii: Issandal on teda tarvis!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 32, 'Aga need, kes olid läkitatud, läksid ja leidsid kõik nõnda, nagu Ta neile oli ütelnud. 33 Kui nad sälgu lahti päästsid, ütlesid selle omanikud neile: \"Miks te sälu lahti päästate?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 33, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 34, 'Nemad vastasid: \"Issandal on teda tarvis!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 35, 'Ja nad tõid selle Jeesuse juure, heitsid oma riided sälu peale ja panid Jeesuse tema selga istuma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 36, 'Kui Ta nüüd edasi liikus, laotasid nad oma riided tee peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 37, 'Aga kui Ta lähenes Õlimäe kallakule, hakkas kogu jüngrite hulk rõõmutsedes suure häälega kiitma Jumalat kõigi vägevate tegude eest, mis nad olid näinud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 38, 'ning ütlesid: \"Kiidetud olgu Kuningas, Kes tuleb Issanda nimel, rahu taevas ja au kõrges!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 39, 'Ja mõningad variserid rahva seast ütlesid Temale: \"Õpetaja, sõitle Oma jüngreid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 40, 'Ta vastas ning ütles: \"Ma ütlen teile, kui need vait jääksid, hakkaksid kivid kisendama!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 41, 'Ja kui ta ligi jõudis ja linna nägi, nuttis Ta tema pärast');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 42, 'ning ütles: \"Kui sina teaksid sellel päeval, mis sinu rahule tarvis läheb! Ent nüüd see on pandud varjule sinu silmade eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 43, 'Sest päevad tulevad sinu peale, mil su vaenlased teevad sinu ümber valli ja piiravad sind ja vaevavad sind kõikepidi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 44, 'Ja lõhuvad sind maha maatasa ja su lapsed sinu sees ega jäta kivi kivi peale, sellepärast et sa ei ole tunnud oma armukatsumisaega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 45, 'Ja Ta läks pühakotta ja hakkas välja ajama neid, kes seal müüsid ja ostsid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 46, 'ning ütles neile: \"Kirjutatud on: Minu koda on palvekoda, aga teie olete ta teinud röövliauguks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 47, 'Ja Ta oli iga päev õpetamas pühakojas; aga ülempreestrid ja kirjatundjad ja rahvaülemad püüdsid Teda hukka saata,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 48, 'kuid ei leidnud, mis teha. Sest kõik rahvas rippus Ta küljes Teda kuulates.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 1, 'Ja ühel päeval, kui Ta pühakojas rahvast õpetas ja Evangeeliumi kuulutas, tulid ülempreestrid ja kirjatundjad vanematega Ta juure,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 2, 'kõnetasid Teda ning ütlesid: \"Ütle meile, missuguse meelevallaga Sa teed seda või kes on see, kes Sulle selle meelevalla on annud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 3, 'Tema kostis ning ütles neile: \"Mina küsin ka teilt ühe asja; ütelge Mulle:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 4, 'Kas oli Johannese ristimine taevast või inimestest?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 5, 'Nemad pidasid nõu isekeskis, üteldes: \"Kui me ütleme taevast, siis Ta ütleb: Mispärast te ei ole siis teda uskunud?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 6, 'Aga kui me ütleme inimestest, siis kõik rahvas viskab meid kividega, sest see on veendunud, et Johannes on prohvet!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 7, 'Nemad vastasid, et nad ei teadvat, kust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 8, 'Siis Jeesus ütles neile: \"Ega Minagi ütle teile, missuguse meelevallaga Ma seda teen!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 9, 'Aga Ta hakkas rääkima rahvale seda tähendamissõna: \"Üks inimene istutas viinamäe ja andis selle rendile aednike kätte ning läks välismaale kauaks ajaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 10, 'Ja parajal ajal ta läkitas sulase aednike juure, et nad temale annaksid viinamäe viljast. Aga aednikud peksid teda ja saatsid ta tühjalt minema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 11, 'Ja ta läkitas veel teise sulase. Aga nad peksid tedagi ja saatsid ta tühjalt minema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 12, 'Ja ta läkitas veel kolmanda. Aga nad haavasid ka seda ja tõukasid ta välja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 13, 'Siis ütles viinamäe isand: Mis ma pean tegema? Ma läkitan oma armsa poja, vahest nad häbenevad teda!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 14, 'Aga kui aednikud teda nägid, pidasid nad nõu isekeskis ning ütlesid: See on pärija. Tapame ta ära, et pärand saaks meile!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 15, 'Ja nad tõukasid ta viinamäest välja ja tapsid ta ära. Mis teeb nüüd viinamäe isand nendega?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 16, 'Ta tuleb ja hukkab need aednikud ja annab viinamäe teiste kätte!\" Seda kuuldes nad ütlesid: \"Ärgu seda sündigu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 17, 'Aga Tema vaatas neile otsa ja ütles: \"Mis see siis õigupoolest tähendab, mis on kirjutatud: See kivi, mille hooneehitajad kui kõlbmatu ära heitsid, see on saanud nurgakiviks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 18, 'Igaüks, kes selle kivi peale kukub, läheb rusuks; aga kelle peale see langeb, selle ta teeb pihuks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 19, 'Ja ülempreestrid ja kirjatundjad püüdsid selsamal tunnil pista käed Tema külge, kuid nad kartsid rahvast, sest nad said aru, et Ta nende kohta oli ütelnud selle tähendamissõna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 20, 'Ja nad varitsesid Teda ja läkitasid välja kavalad mehed, kes pidid teesklema õigeid, et Teda tabada kõnest ja Teda ära anda ülemusele ning maavalitseja meelevalda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 21, 'Ja nad küsisid Temalt ning ütlesid: \"Õpetaja, me teame, et Sa räägid ja õpetad õigesti ega arvesta ühegi isikut, vaid õpetad Jumala teed tõtt mööda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 22, 'Kas meil sünnib keisrile anda maksuraha või ei sünni?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 23, 'Aga Tema mõistis ära nende kurikavaluse ja ütles neile: \"Miks te Mind kiusate?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 24, 'Näidake Mulle teenariraha! Kelle kuju ja pealkiri sellel on?\" Nemad vastasid: \"Keisri!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 25, 'Tema ütles neile: \"Siis andke keisrile, mis kuulub keisrile, ja Jumalale, mis kuulub Jumalale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 26, 'Ja nad ei saanud Teda kinni võtta rahva ees; ja imestades Tema vastust, jäid nad vait.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 27, 'Ent Ta juure tuli mõningaid sadusere, kes ütlesid, et ei ole ülestõusmist, ja küsisid Temalt');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 28, 'nõnda: \"Õpetaja, Mooses on meile kirjutanud, et kui kellegi vend, kes oli naisemees, sureb ja tal ei olnud lapsi, siis võtku ta vend selle naine ja saatku oma vennale sugu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 29, 'Oli kord seitse venda; ja esimene võttis naise ja suri lapseta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 30, 'Ja teine samuti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 31, 'Ja kolmas võttis tema, ja nõndasamuti ka kõik seitse ei jätnud lapsi järele ja surid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 32, 'Kõige viimaks suri ka naine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 33, 'Kelle naiseks nende seast nüüd see naine peab olema ülestõusmises? Ta on ju olnud naiseks neile seitsmele?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 34, 'Jeesus kostis neile: \"Sellesinase maailm-asjastu lapsed võtavad naisi ja lähevad mehele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 35, 'aga kes on väärt arvatud jõudma teise maailma ja surnuist üles tõusma, ei need võta naist ega lähe mehele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 36, 'Sest nad ei või ka enam surra; nad on Inglite sarnased ja nad on Jumala pojad, olles ülestõusmise pojad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 37, 'Aga et surnud tõusevad üles, seda näitab ka Mooses kibuvitsapõõsa loos, kui ta nimetab Issandat Aabrahami Jumalaks ja Iisaki Jumalaks ja Jaakobi Jumalaks;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 38, 'Jumal aga ei ole mitte surnute, vaid on elavate Jumal; sest kõik elavad Temale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 39, 'Siis mõned kirjatundjaist vastasid ning ütlesid: \"Õpetaja, Sa oled hästi rääkinud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 40, 'Ei nad julgenud Temalt ka enam midagi küsida.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 41, 'Aga Ta ütles neile: \"Kuidas üteldakse Kristus olevat Taaveti Poeg,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 42, 'kuna Taavet ise ütleb Laulude raamatus: Issand on ütelnud minu Issandale: Istu Mu paremale poole,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 43, 'kuni Ma panen Su vaenlased Su jalgealuseks järiks?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 44, 'Taavet nimetab siis Teda Issandaks, ja kuidas Ta on tema Poeg?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 45, 'Aga Ta ütles kõige rahva kuuldes Oma jüngritele:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 46, '\"Hoiduge kirjatundjate eest, kes tahavad käia pikis rüüdes ja armastavad teretusi turgudel ja esimesi istmeid kogudusekodades ja ülemat kohta lauas söömaaegadel!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 47, 'Nemad söövad leskede hooned ja loevad silmakirjaks pikki palveid. Need saavad seda raskema hukatuse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 1, 'Aga Ta vaatas ja nägi rikkaid oma ande ohvrikirstu panevat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 2, 'Ent Ta nägi ka üht vaest lesknaist kaks leptonit sinna sisse panevat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 3, 'Ja Ta ütles: \"Tõesti Ma ütlen teile, see vaene lesknaine pani rohkem kui kõik muud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 4, 'Sest need kõik panid oma küllusest anni, kuid tema pani oma vaesusest kõik toiduse, mis tal oli!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 5, 'Ja kui mõningad rääkisid pühakojast, et see on ilusate kividega ja annetistega ehitud, ütles Ta:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 6, '\"Päevad tulevad, et sellest kõigest, mida te näete, ei jäeta kivi kivi peale, mida maha ei kistaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 7, 'Siis küsiti Temalt: \"Õpetaja, millal see siis sünnib? Ja mis on selle tunnuseks, et see hakkab sündima?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 8, 'Aga Tema ütles: \"Katsuge, et teid ei eksitataks; sest paljud tulevad Minu nime all ja ütlevad: Mina olen see! ja: Aeg on lähenenud! Ärge minge nende järele!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 9, 'Ja kui te kuulete sõdadest ja mässudest, siis ärge kohkuge; sest see peab enne sündima; aga ots ei ole veel niipea käes!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 10, 'Siis ta ütles neile: \"Rahvas tõuseb rahva vastu ja kuningriik kuningriigi vastu');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 11, 'ja suuri maavärisemisi on siis ja paiguti nälga ning katku; ja hirmsaid asju on näha ning suuri tunnustähti taeval!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 12, 'Aga enne kõike seda pistavad nad oma käed teie külge ja kiusavad teid taga, andes teid ära kogudusekodadesse ja vangi, viivad teid kuningate ja maavalitsejate ette Minu nime pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 13, 'Aga see annab teile juhust tunnistamiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 14, 'Seepärast võtke südamesse, et te enneaegu ei muretseks, mida vastata enda kaitseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 15, 'Sest Mina annan teile suu ja tarkuse, mille vastu ei saa panna ega rääkida ükski teie vastaseist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 16, 'Ka teie vanemad ja vennad ja sugulased ning sõbrad annavad teid ära ja surmavad mõned teie seast;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 17, 'ja teid vihatakse kõikide poolt Minu nime pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 18, 'Aga mitte juuksekarvgi ei saa hukka teie peast!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 19, 'Oma püsivusega kannatustes te päästate oma hinged.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 20, 'Aga kui te näete Jeruusalma olevat sõjaväe poolt ümber piiratud, siis tundke, et ta hävitus on ligidal!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 21, 'Siis põgenegu need, kes on Juudamaal, mägedele ja need, kes on linnas, mingu välja, ja kes on maal, ärgu mingu linna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 22, 'Sest need on kättemaksu päevad, et läheks täide, mis on kirjutatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 23, 'Häda käima peal olijaile ja imetajaile neil päevil, sest suur häda on maa peal ja viha selle rahva vastu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 24, 'Ja nad langevad mõõgatera läbi ja nad viiakse vangi kõigi rahvaste sekka, ja Jeruusalem jääb paganrahvaste tallata, kuni paganate ajad täis saavad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 25, 'Ja päikeses, kuus ja tähtedes on ennustusmärke ja maa peal on rahvastel ahastus ja nõutu olek mere kohina ja veevoogude pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 26, 'Ja inimesed lähevad rammetumaks kartuse ja sündmuste ootamise pärast, mis maailma peale on tulemas; sest taeva vägesid kõigutatakse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 27, 'Ja siis nad näevad Inimese Poja tulevat pilves suure väe ja auhiilgusega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 28, 'Aga kui see kõik hakkab sündima, siis vaadake üles ja tõstke oma pead, sest teie lunastus läheneb!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 29, 'Ja Ta ütles neile võrdumi: \"Vaadake viigipuud ja kõiki puid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 30, 'Kui nad juba pakatavad ja te näete seda, siis tunnete iseenesest, et suvi on juba ligidal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 31, 'Nõnda ka teie: kui te näete seda sündivat, siis tundke, et Jumala Riik on ligidal!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 32, 'Tõesti Ma ütlen teile, see sugupõlv ei lõpe ära, enne kui see kõik sünnib!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 33, 'Taevas ja maa hävivad, kuid Minu sõnad ei hävi mitte!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 34, 'Aga hoidke, et te oma südameid ei koormaks liigsöömise ega purjutamisega ega peatoiduse muredega ja et see päev ei tuleks teie peale äkitselt');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 35, 'otsegu linnupael! Sest ta tuleb kõikide peale, kes kogu maapinnal asuvad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 36, 'Siis valvake ja paluge igal ajal, et teid arvataks väärt põgenema kõige selle eest, mis tuleb, ja seisma Inimese Poja ees!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 37, 'Ja Ta õpetas päeva ajal pühakojas, aga ööseks Ta läks välja ning ööbis mäel, mida kutsutakse Õlimäeks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 38, 'Ja kõik rahvas tuli vara hommikul Tema juure pühakotta Teda kuulama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 1, 'Aga hapnemata leibade päev, mida kutsutakse paasapühaks, oli ligidal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 2, 'Ja ülempreestrid ja kirjatundjad pidasid aru, kuidas Teda surmata; sest nad kartsid rahvast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 3, 'Aga saatan oli läinud Juudase sisse, keda liignimega hüüti Iskariotiks, kes oli nende kaheteistkümne arvust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 4, 'Ja ta läks ära ja rääkis ülempreestrite ja sõjapealikutega, kuidas ta võiks Tema ära anda nende kätte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 5, 'Ja nad said rõõmsaks ja leppisid kokku, et nad annavad temale raha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 6, 'Tema nõustus sellega ja otsis parajat juhust Tema äraandmiseks neile ilma rahva teadmata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 7, 'Nii jõudis kätte hapnemata leibade päev, mil paasatall pidi tapetama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 8, 'Ja Ta läkitas Peetruse ja Johannese ning ütles: \"Minge ja valmistage meile paasatall, et me seda sööksime.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 9, 'Aga nad küsisid Temalt: \"Kus Sa tahad, et me selle valmistame?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 10, 'Tema vastas neile: \"Vaata, kui te linna sisse lähete, siis tuleb teile vastu inimene, kes kannab veekruusi; minge Tema järele sinna majasse, kuhu Ta sisse läheb,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 11, 'ja ütelge selle maja isandale: Õpetaja ütleb sulle: Kus on võõrastetuba, kus Ma võiksin süüa paasatalle Oma jüngritega?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 12, 'Siis ta näitab teile suure ülemise toa; seal valmistage.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 13, 'Ent kui nad ära läksid, leidsid nad nõnda, nagu Ta neile oli ütelnud. Ja nad valmistasid paasatalle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 14, 'Ja kui tund kätte jõudis, istus Ta maha ja Apostlid ühes Temaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 15, 'Ja Ta ütles neile: \"Ma olen südamest igatsenud seda paasatalle süüa ühes teiega, enne kui Ma kannatan.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 16, 'Sest Ma ütlen teile, et Ma ei söö enam sellest, kuni kõik on täide läinud Jumala Riigis!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 17, 'Ja Ta võttis karika, tänas ning ütles: \"Võtke see ja jagage eneste vahel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 18, 'Sest Ma ütlen teile, et Mina ei joo enam viinapuu viljast, enne kui tuleb Jumala Riik!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 19, 'Ja Ta võttis leiva, tänas ja murdis ja andis neile ning ütles: \"See on Minu ihu, mis teie eest antakse; seda tehke Minu mälestuseks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 20, 'Samuti ka karika pärast õhtusöömaaega ja ütles: \"See karikas on Uus Leping Minu veres, mis teie eest valatakse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 21, 'vaata, Mu äraandja käsi on Minuga lauas!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 22, 'Ja Inimese Poeg läheb küll ära, nõnda nagu see on määratud; ometi häda sellele inimesele, kes Ta ära annab!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 23, 'Ja nemad hakkasid küsima üksteiselt, kes see küll peaks nende seast olema, kes seda teeb.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 24, 'Aga nende seas tõusis vaidlus ka selle kohta, keda neist tuleks arvata suuremaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 25, 'Siis Tema ütles neile: \"Rahvaste kuningad valitsevad isandaina nende üle ja nende võimumehi hüütakse armulisiks isandaiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 26, 'Kuid teie ärge olge nõnda, vaid suurem teie seast olgu nagu noorem, ja ülem nõnda nagu see, kes teenib.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 27, 'Sest kumb on suurem, kas see, kes istub lauas, või see, kes teenib? Eks see, kes istub lauas? Ent Mina olen teie seas nõnda nagu see, kes teenib.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 28, 'Aga teie olete need, kes Minu juure on jäänud Mu kiusatustes;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 29, 'ja Mina sean teile Riigi, nõnda nagu Minu Isa selle Mulle on seadnud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 30, 'et te sööksite ja jooksite Minu lauas Minu Riigis ja istuksite aujärgedel ja mõistaksite kohut Iisraeli kaheteistkümne suguharu üle!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 31, '\"Siimon, Siimon, vaata, saatan on väga püüdnud sõeluda teid nagu nisu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 32, 'Aga Mina olen sinu eest palunud, et su usk ära ei lõpeks, ja kui sa pärast pöördud, siis kinnita oma vendi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 33, 'Aga tema ütles Talle: \"Issand, ma olen valmis Sinuga minema niihästi vangi kui surma!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 34, 'Kuid Ta ütles: \"Ma ütlen sulle, Peetrus: kukk ei laula täna mitte enne, kui sina oled kolm korda salanud, et sa Mind tunned!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 35, 'Ja Ta ütles neile: \"Kui Ma teid läkitasin kukruta ja paunata ja jalatseita, kas oli teil midagi puudu?\" Nemad vastasid: \"Ei midagi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 36, 'Siis ta ütles neile: \"Aga nüüd, kellel on kukkur, see võtku ta kaasa, nõndasamuti ka paun; ja kellel ei ole, see müügu ära oma kuub ja ostku mõõk!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 37, 'Sest Ma ütlen teile, et Minus peab veel täide minema, mis on kirjutatud: \"Ja Teda arvati üleastujate hulka!\" Sest mis Minusse puutub, jõuab lõpule!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 38, 'Aga nemad ütlesid: \"Issand, ennäe, siin on kaks mõõka!\" Tema ütles neile: \"Sellest jätkub!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 39, 'Ja Ta läks välja ja tuli Oma viisi järele Õlimäele. Ent ka Tema jüngrid järgisid Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 40, 'Ja kui ta jõudis kohale, ütles Ta neile: \"Palvetage, et te ei satuks kiusatusse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 41, 'Ta ise läks neist eemale, niikaugele kui kiviga jõuab visata, heitis põlvili maha ja palvetas,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 42, 'üteldes: \"Isa, kui Sa tahad, siis võta see karikas Minult ära; ometi ärgu sündigu Minu, vaid Sinu tahtmine!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 43, 'Siis ilmus Temale Ingel taevast ja kinnitas Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 44, 'Ja heideldes raskesti, palvetas Ta veel pinevamalt; ja Tema higi oli nagu verepisarad, mis langesid maa peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 45, 'Ja Ta tõusis üles palvetamast ning tuli Oma jüngrite juure ja leidis nad magamast kurbuse pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 46, 'Ja Ta ütles neile: \"Miks te magate? Tõuske üles ja palvetage, et te ei satuks kiusatusse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 47, 'Kui Tema alles rääkis, vaata, siis tuli rahvast ja see, keda hüüti Juudaseks, üks neist kaheteistkümnest, käis nende ees ja tuli Jeesuse ligi teda suudlema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 48, 'Aga Jeesus ütles temale: \"Juudas, annad sa Inimese Poja suudlusega ära?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 49, 'Kui nüüd kaaslased seda nägid, mis oli tulemas, ütlesid nad: \"Issand, kas me peame mõõgaga sekka lööma?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 50, 'Ja üks neist lõi ülempreestri sulast ning raius tema parema kõrva ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 51, 'Aga Jeesus kostis ning ütles: \"Jätke sellega!\" Ja Ta puudutas tema kõrva ja tegi ta terveks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 52, 'Siis Jeesus ütles ülempreestritele ja pühakoja pealikuile ja vanemaile, kes Tema vastu olid tulnud: \"Te olete tulnud välja otsegu röövli vastu mõõkade ja nuiadega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 53, 'Kui Ma iga päev teie juures olin pühakojas, ei ole te pistnud käsi Mu külge. Ent see on teie tund ja pimeduse võimus!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 54, 'Aga nad võtsid Ta kinni ja viisid ning tõid Tema ülempreestri kotta. Aga Peetrus järgis Teda kaugelt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 55, 'Ja kui nad keset õue süütasid tule ja üheskoos maha istusid, istus ka Peetrus nende keskele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 56, 'Siis nägi üks ümmardaja teda tule ääres istuvat ning jäi temale otsa vaatama ja ütles: \"Ka seesinane oli ühes Temaga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 57, 'Aga tema salgas ning ütles: \"Naine, mina Teda ei tunne!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 58, 'Üürikese aja pärast nägi teda teine, keegi mees, ning ütles: \"Sinagi oled nende seast!\" Kuid Peetrus ütles: \"Inimene, mina ei ole mitte!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 59, 'Ja umbes tund aega hiljemini kinnitas seda keegi teine ning ütles: \"Tõepoolest, seesinane oli Temaga, sest tema on ju galilealane!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 60, 'Aga Peetrus ütles: \"Inimene, ma ei mõista, mida sa räägid!\" Ja sedamaid laulis kukk!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 61, 'Ja Issand pöördus ja vaatas Peetrusele; ja Peetrusele tuli meele Issanda sõna, kuidas Ta temale oli ütelnud: \"Enne kui kukk täna laulab, salgad sa Mind kolm korda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 62, 'Ja ta läks välja ja nuttis kibedasti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 63, 'Aga mehed, kes Jeesust kinni hoidsid, naersid ja peksid Teda,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 64, 'ja nad katsid Ta silmad kinni ja küsisid Temalt ning ütlesid: \"Mõista kui prohvet, kes see on, kes Sind lõi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 65, 'Ja palju muid pilkesõnu nad rääkisid Tema vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 66, 'Ja kui valgeks läks, tulid rahvavanemad, ülempreestrid ja kirjatundjad kokku ja viisid Ta üles oma Suurkohtu ette');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 67, 'ning ütlesid: \"Kui Sina oled Kristus, siis ütle meile!\" Tema vastas neile: \"Kui Ma teile ütlen, ei usu te ju mitte,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 68, 'ja kui Ma küsin, ei vasta te mitte!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 69, 'Ent nüüdsest peale hakkab Inimese Poeg istuma Jumala väe paremal poolel!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 70, 'Aga nad kõik ütlesid: \"Kas siis Sina oled Jumala Poeg?\" Tema ütles neile: \"Jah, Ma olen!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 71, 'Siis nad ütlesid: \"Mis tunnistust me veel vajame? Oleme ju ise seda kuulnud Tema suust!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 1, 'Ja kogu nende hulk tõusis ning viis Tema Pilaatuse juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 2, 'Ja nad hakkasid kaebama Tema peale ning ütlesid: \"Me oleme leidnud, et Tema rahvast eksitab ja keelab andmast maksuraha keisrile ning ütleb Enese Kuningas Kristuse olevat!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 3, 'Siis Pilaatus küsis Temalt ning ütles: \"Kas Sina oled Juutide Kuningas?\" Ja Ta vastas temale, üteldes: \"Jah, olen!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 4, 'Pilaatus aga ütles ülempreestritele ja rahvale: \"Ma ei leia sellest inimesest ühtki süüd!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 5, 'Aga nemad ajasid peale ja ütlesid: \"Tema ässitab rahvast ja õpetab mööda kõike Juudamaad, alates Galileast kuni siiani!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 6, 'Aga kui Pilaatus seda kuulis, küsis ta, kas See Inimene ei ole mitte galilealane?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 7, 'Ja saades teada, et Ta on Heroodese valitsuse alt, saatis ta Tema Heroodese juure, kes ka viibis Jeruusalemas neil päevil.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 8, 'Kui siis Heroodes Jeesust nägi, sai ta väga rõõmsaks, sest ta oli hea meelega tahtnud Teda juba ammugi näha, kuna ta oli Temast palju kuulnud; ka lootis ta näha saada mõnd imetähte, mida Ta ehk teeb.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 9, 'Ta küsis Temalt palju asju, aga Tema ei vastanud talle midagi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 10, 'Aga ülempreestrid ja kirjatundjad seisid ja kaebasid väga valjusti Tema peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 11, 'Kui siis Heroodes oma sõjameestega teda oli halvaks pannud ja pilganud ning riietanud toreda riidega, saatis ta Tema tagasi Pilaatuse juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 12, 'Ja sel päeval said Pilaatus ja Heroodes üksteisega sõbraks, sest enne nad olid olnud üksteise vihamehed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 13, 'Siis Pilaatus kutsus kokku ülempreestrid ning rahvaülemad ja rahva');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 14, 'ning ütles neile: \"Te olete toonud selle inimese minu juure otsegu rahva eksitaja. Ja vaata, ma olen Teda üle kuulanud teie ees ega ole sellest inimesest leidnud ühtki süüd, milles te Teda süüdistate,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 15, 'ega Heroodeski, vaid ta on Tema saatnud tagasi meie juure. Ja vaata, Tema pole teinud midagi, mis oleks surma väärt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 16, 'Sellepärast ma tahan Teda karistada ja vabaks lasta!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 17, 'Aga temal oli kohustuseks neile pühiks vabaks lasta üks vang.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 18, 'Siis kogu hulk kisendas väga ning ütles: \"Hukka ära, lase meile Barabas vabaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 19, 'See oli mingi linnas sündinud mässu ja tapmise pärast vangi heidetud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 20, 'Siis Pilaatus tõstis jälle häält nende vastu, tahtes vabaks lasta Jeesuse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 21, 'Aga nemad karjusid: \"Löö risti, löö Ta risti!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 22, 'Siis ta ütles neile kolmat puhku: \"Mis Ta siis on kurja teinud? Ma pole Temast leidnud ühtki surmasüüd. Sellepärast ma karistan teda ja lasen ta vabaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 23, 'Aga nad käisid peale, suure kisendamisega nõudes, et Jeesus löödaks risti. Ja nende hüüded võtsid võimust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 24, 'Siis Pilaatus tegi otsuse, et sünniks nende palve järele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 25, 'Ta laskis neile vabaks selle, kes mässamise ja tapmise pärast oli vangitorni heidetud ja keda nemad vabaks palusid. Aga Jeesuse ta andis nende meelevalla kätte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 26, 'Ja Teda välja viies said nad kätte kellegi Siimona Küreenest, kes tuli väljalt; ja tema peale nad panid risti, et ta seda kannaks Jeesuse järele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 27, 'Aga Teda järgis suur huik rahvast ja ka naisi, kes kaebasid ja Teda nutsid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 28, 'Siis Jeesus pöördus nende poole ja ütles: \"Jeruusalema tütred, ärge nutke Mind, vaid nutke iseendid ja oma lapsi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 29, 'Sest vaata, päevad tulevad, mil üteldakse: Õndsad on sigimatud ja ihud, mis ei ole ilmale kannud, ja rinnad, mis ei ole imetanud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 30, 'Siis hakatakse ütlema mägedele: Langege meie peale! ja mäekinkudele: Katke meid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 31, 'Sest kui seda tehakse toore puuga, mis sünnib siis kuivaga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 32, 'Aga ka kaks muud kurjategijat viidi ühes Temaga välja hukkamiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 33, 'Ja kui nad jõudsid sinna paika, mida hüütakse Pealaeks, lõid nad Tema sinna risti ja need kurjategijad, ühe paremale, teise vasakule poole.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 34, 'Aga Jeesus ütles: \"Isa, anna neile andeks, sest nad ei tea, mida nad teevad!\" Ja nad jagasid Tema riided ning heitsid liisku nende pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 35, 'Ja rahvas seisis seal vaatamas, ja ka ülemad irvitasid ühes nendega ning ütlesid: \"Muid Ta on aidanud, aidaku Iseennast, kui Ta on Jumala Võitu, see äravalitu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 36, 'Ka sõjamehed naersid Teda, läksid Tema juure ja viisid Temale äädikat');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 37, 'ning ütlesid: \"Kui Sina oled Juutide Kuningas, siis aita Iseennast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 38, 'Aga oli ka pealkiri Tema kohal: \"See on Juutide Kuningas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 39, 'Siis teine poodud kurjategijaist pilkas Teda ning ütles: \"Eks Sa ole Kristus? Aita Iseennast ja meid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 40, 'Aga teine kostis ja sõitles teda ning ütles: \"Kas sinagi ei karda Jumalat, kuna sa ise oled ju sama karistuse all?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 41, 'Meie küll õiguse poolest, sest me saame kätte, mis meie teod on ära teeninud, aga Seesinane ei ole teinud midagi ebakohast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 42, 'Ja ta ütles: \"Jeesus, mõtle minule, kui Sa Oma Kuningriiki tuled!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 43, 'Ja Jeesus ütles temale: \"Tõesti Ma ütlen sulle, täna pead sa Minuga olema paradiisis!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 44, 'Ja oli juba umbes kuues tund, siis tekkis pimedus üle kogu maa üheksandast tunnist saadik,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 45, 'sest päike pimenes. Ja templi eesriie kärises keskelt lõhki!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 46, 'Ja Jeesus kisendas suure häälega ning ütles: \"Isa, Sinu kätte Ma annan Oma vaimu!\" Ja kui Ta seda oli ütelnud, heitis Ta hinge.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 47, 'Aga kui pealik nägi, mis sündis, andis ta Jumalale au ning ütles: \"See inimene oli tõesti õige!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 48, 'Ja kui kõik rahvahulgad, kes olid kokku tulnud seda vaatama, nägid, mis sündis, lõid nad enestele vastu rindu ja läksid tagasi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 49, 'Aga kõik Ta tuttavad ja ka naised, kes olid järginud Teda Galileamaalt, seisid eemal ning nägid seda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 50, 'Ja vaata, mees, nimega Joosep, Suurkohtu liige, hea ja õiglane mees,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 51, 'kes ei olnud ühes nõus nende otsusega ega teoga, Arimaatiast, Juuda rahva linnast, mees, kes ootas Jumala Riiki,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 52, 'see läks Pilaatuse juure ja palus enesele Jeesuse ihu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 53, 'Ja ta võttis selle maha ja mähkis ta kallisse lõuendisse. Ja ta pani ta kaljusse raiutud hauda, kuhu ei olnud veel iialgi kedagi pandud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 54, 'Ja see oli valmistuspäev, ja hingamispäev oli tulemas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 55, 'Aga naised, kes Galileast olid teda järginud, käisid kaasas ja vaatasid hauda ja kuidas Tema ihu sinna pandi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 56, 'Ja kui nad olid tagasi tulnud, valmistasid nad lõhnarohte ja salvi. Aga hingamispäeval nad seisid rahul käsu järele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 1, 'Aga nädala esimesel päeval puhteajal läksid naised hauale, viies ühes lõhnarohud, mis nad olid valmistanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 2, 'Ja nad leidsid kivi haualt ära veeretatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 3, 'Siis nad läksid sisse, kuid ei leidnud mitte Issanda Jeesuse ihu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 4, 'Ja kui nad selle pärast kahevahel olid, vaata, siis seisis kaks meest nende kõrval säravais riideis!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 5, 'Aga kui naised hakkasid kartma ja oma silmad maha lõid, ütlesid mehed neile: \"Miks te elavat otsite surnute juurest?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 6, 'Tema ei ole siin, vaid on üles tõusnud! Tuletage meele, mis Ta teile rääkis veel Galileas olles,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 7, 'kui Ta teile ütles, et Inimese Poeg antakse patuste inimeste kätte ja lüüakse risti ja kolmandal päeval Ta tõuseb jälle üles!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 8, 'Siis nad tuletasid meele Tema sõnad');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 9, 'ja läksid tagasi haua juurest ja kuulutasid seda kõike neile üheteistkümnele ja kõigile teistele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 10, 'Aga Maarja Magdaleena ja Johanna ja Maarja, Jakoobuse ema, ja teised nende kaaslased jutustasid seda Apostlitele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 11, 'Ent need kõned olid nende meelest nagu tühi jutt ja nad ei uskunud naisi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 12, 'Aga Peetrus võttis kätte ja jooksis hauale. Ja kui ta kummargile sisse vaatas, nägi ta üksnes surnulinad maas olevat. Ja ta läks ära ja pani seda imeks, mis oli sündinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 13, 'Ja vaata, kaks nende seast olid minemas selsamal päeval külasse, mis on ligi kuuskümmend vagu maad Jeruusalemast ja mille nimi on Emmaus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 14, 'Ja nad kõnelesid isekeskis kõigest, mis oli sündinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 15, 'Ja nende kõneldes ja vaieldes lähenes ka Jeesus Ise neile ja käis nendega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 16, 'Aga nende silmad peeti, nii et nad Teda ei tunnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 17, 'Ja Ta ütles neile: \"Mis kõned need on, mis te käies kõnelete isekeskis?\" Siis nad seisatasid kurvanäolistena.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 18, 'Aga teine, nimega Kleopas, vastas ning ütles Temale: \"Sinaks üksi elad kui võõras Jeruusalemas ega tea, mis neil päevil seal on sündinud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 19, 'Ta küsis neilt: \"Mis?\" Aga nad ütlesid Temale: \"See, mis sündis Jeesus Naatsaretlasega, Kes oli prohvet, vägev teolt ja sõnalt Jumala ja kõige rahva ees,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 20, 'kuidas meie ülempreestrid ja vanemad on annud Ta surma mõista ja on Ta risti löönud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 21, 'Ent meie lootsime Tema olevat Selle, Kes Iisraeli rahva lunastab; aga peale selle kõige on täna kolmas päev, kui see sündis!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 22, 'Ka mõned naised meie seast, kes puhteajal haual käisid, on meid ehmatanud:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 23, 'kui nad Tema ihu ei leidnud, tulid nad ja ütlesid endid näinud olevat ka Inglite nägemust, kes ütlevad Tema elavat!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 24, 'Ja mõningad neist, kes olid meiega, läksid hauale ja leidsid nõnda olevat, nagu naised olid ütelnud; kuid Teda Ennast nad ei näinud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 25, 'Tema ütles neile: \"Oh te mõistmatumad ja südamest pikaldased uskuma seda kõike, mis prohvetid on rääkinud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 26, 'Eks Kristus pidanud seda kannatama ja Oma auhiilgusesse minema?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 27, 'Ja Ta hakkas peale Moosesest ja kõigist prohveteist ja seletas neile, mis Temast kõigis kirjades oli üteldud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 28, 'Ja kui nad lähenesid külale, kuhu nad olid minemas, tegi Ta Enese eemale minema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 29, 'Ja nad käisid Temale peale ning ütlesid: \"Jää meie juure, sest õhtu jõuab ja päev veereb!\" Ja Tema läks sisse nende juure jääma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 30, 'Ja sündis, kui Ta nendega lauas istus, et Ta võttis leiva, õnnistas ja murdis ning andis neile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 31, 'Siis nende silmad läksid lahti ja nad tundsid Tema ära! Ja Tema kadus nende silmist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 32, 'Ja nad ütlesid üksteisele: \"Eks meie süda põlenud meie sees, kui Ta teel meiega rääkis ja meile kirju seletas?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 33, 'Ja nad tõusid samal tunnil ja läksid tagasi Jeruusalemma ja leidsid koos olevat need üksteistkümmend ja kaaslased,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 34, 'kes ütlesid: \"Issand on tõesti üles tõusnud ja Siimonale ilmunud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 35, 'Ja nemad ise jutustasid, mis teel oli sündinud ja kuidas nad Tema olid leiva murdmisest ära tunnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 36, 'Aga kui nad seda rääkisid, seisis Jeesus ise nende keskel ja ütles neile: \"Rahu olgu teile!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 37, 'Nemad kohkusid ja lõid kartma ning arvasid vaimu nägevat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 38, 'Ent Tema ütles neile: \"Miks te olete nii väga ehmunud? Ja mispärast tõuseb niisuguseid mõtteid teie südamest?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 39, 'Vaadake Mu käsi ja jalgu, et Mina see olen! Katsuge Mind kätega ja nähke, sest vaimul ei ole liha ega luid, nõnda nagu te näete Minul olevat!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 40, 'Ja seda üteldes näitas Ta neile Oma käsi ja jalgu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 41, 'Aga kui nad rõõmu pärast veel ei uskunud ja imeks panid, ütles Ta neile: \"Kas teil on siin midagi süüa?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 42, 'Ja nad panid Tema ette pala küpsetatud kala.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 43, 'Ja Tema võttis ja sõi nende ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 44, 'Siis Ta ütles neile: \"Need on kõned, mis Ma teile rääkisin veel teie juures olles; sest kõik peab täide minema, mis on kirjutatud Minu kohta Moosese käsuõpetuses ja Prohveteis ja Lauludes!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 45, 'Siis Ta avas nende mõistuse, nõnda et nad kirjadest aru said.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 46, 'Ja ta ütles neile: \"Nõnda on kirjutatud ja nõnda pidi Kristus kannatama ja surnuist üles tõusma kolmandal päeval,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 47, 'ja Tema nimel peab kuulutatama meeleparandust pattude andekssaamiseks kõigi rahvaste seas, alates Jeruusalemast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 48, 'Teie olete nende asjade tunnistajad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 49, 'Ja vaata, Mina läkitan teie peale Oma Isa tõotuse! Ent teie jääge sellesse linna seni, kuni teid ehitakse väega kõrgest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 50, 'Siis Ta viis nad välja Betaania lähedale ja tõstis Oma käed üles ja õnnistas neid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 51, 'Ja sündis, et Ta neid õnnistades lahkus neist ja võeti üles taevasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 52, 'Ja nemad kummardasid Teda ning läksid tagasi Jeruusalemma suure rõõmuga');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 53, 'ja olid alati pühakojas, tänades Jumalat!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 1, 'Alguses oli Sõna, ja Sõna oli Jumala juures, ja Sõna oli Jumal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 2, 'Seesama oli alguses Jumala juures.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 3, 'Kõik on tekkinud Tema läbi, ja ilma Temata ei ole tekkinud midagi, mis on tekkinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 4, 'Temas oli elu, ja elu oli inimeste valgus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 5, 'ja valgus paistab pimeduses, ja pimedus ei ole seda võtnud omaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 6, 'Oli mees, Jumala läkitatud; selle nimi oli Johannes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 7, 'See tuli tunnistuseks, tunnistama Valgusest, et kõlk usuksid tema kaudu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 8, 'Tema ei olnud valgus, vaid ta tuli tunnistama Valgusest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 9, 'See tõeline Valgus, Mis valgustab igat inimest, oli tulemas maailma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 10, 'Tema oli maailmas, ja maailm on tekkinud Tema läbi, ja maailm ei tunnud Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 11, 'Ta tuli sellesse, mis Tema oma, ja Tema omad ei võtnud Teda vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 12, 'Aga kõigile, kes Teda vastu võtsid, andis Ta meelevalla saada Jumala lapsiks, kes usuvad Tema nimesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 13, 'kes ei ole sündinud verest, ei liha tahtest ega mehe tahtest, vaid Jumalast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 14, 'Ja Sõna sai lihaks ja elas meie keskel, ja me nägime Tema au kui Isast Ainusündinud Poja au, täis armu ja tõde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 15, 'Johannes annab tunnistust Temast, ja ta hüüdis nõnda: \"Tema oli See, Kellest ma ütlesin: Kes tuleb pärast mind, See on olnud enne mind, sest Ta oli enne kui mina!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 16, 'Sest Tema täiusest me kõik oleme saanud, ja armu armu peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 17, 'Sest käsuõpetus on antud Moosese kaudu, arm ja tõde on tulnud Jeesuse Kristuse kaudu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 18, 'Ükski ei ole Jumalat iialgi näinud; ainusündinud Poeg, kes on Isa süles, on Temast kõnelnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 19, 'Ja see on Johannese tunnistus, kui juudid läkitasid Jeruusalemast preestreid ja leviite temalt küsima: \"Kes sa oled?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 20, 'Tema tunnistas ega salanud mitte, vaid tunnistas: \"Mina ei ole Kristus!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 21, 'Ja nad küsisid temale: \"Kes siis? Oled sa Eelija?\" Tema ütles: \"Ei ole!\" - \"Oled sina see prohvet?\" Tema vastas: \"Ei!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 22, 'Siis nad ütlesid talle: \"Kes sa oled? Et annaksime vastuse neile, kes meid läkitasid. Mis sa ütled enesest?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 23, 'Tema ütles: \"Mina olen hüüdja hääl kõrves: Tehke tasaseks Issanda tee, nõnda nagu prohvet Jesaja on ütelnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 24, 'Ja need, kes olid läkitatud, olid variseride seast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 25, 'Ja nad küsisid temalt ja ütlesid talle: \"Miks sa siis ristid, kui sa pole Kristus ega Eelija ega see prohvet?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 26, 'Johannes vastas neile ning ütles: \"Mina ristin veega; aga teie keskel seisab See, Keda te ei tunne,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 27, 'Kes tuleb pärast mind, Kelle jalatsi paela lahti päästma mina ei ole vääriline!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 28, 'See sündis Betaanias, sealpool Jordanit, kus Johannes ristis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 29, 'Järgmisel päeval näeb Johannes Jeesuse tulevat tema juure ja ta ütleb: \"Vaata, see on Jumala Tall, Kes võtab ära maailma patu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 30, 'See on, Kellest ma ütlesin: Pärast mind tuleb Mees, Kes on olnud enne mind, sest Ta oli enne kui mina!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 31, 'Ja mina ei tunnud Teda, kuid selleks, et Ta saaks ilmsiks Iisraelile, olen mina tulnud veega ristima!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 32, 'Ja Johannes tunnistas ning ütles: \"Ma nägin Vaimu taevast alla laskuvat nagu tuvi; ja Ta jäi Tema peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 33, 'Ja mina ei tunnud Teda mitte; aga Kes mind läkitas veega ristima, See ütles mulle: Kelle peale sa näed Vaimu alla laskuvat ja Tema peale jäävat, See on, Kes ristib Püha Vaimuga!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 34, 'Ja mina olen näinud ja tunnistanud, et Seesinane on Jumala Poeg.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 35, 'Järgmisel päeval seisis Johannes jälle seal, ja kaks tema jüngrit.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 36, 'Ja kui ta nägi Jeesust kõndivat, ütles ta: \"Ennäe, See on Jumala Tall!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 37, 'Ja need kaks jüngrit kuulsid tema ütlust ja järgisid Jeesust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 38, 'Aga Jeesus pöördus ümber, ja nähes neid Teda järgivat, ütles Ta neile: \"Mida te otsite?\" Aga nemad ütlesid Talle: \"Rabi! - see tähendab meie keeli õpetaja - kus Sa asud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 39, 'Ta ütles neile: \"Tulge ja vaadake!\" Nad tulidki ja nägid, kus Ta asus, ja nad jäid Tema juure selle päeva. See oli arvata kümnes tund.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 40, 'Andreas, Siimon Peetruse vend, oli üks neist kahest, kes oli kuulnud Johannese ütlust ja hakanud Jeesust järgima.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 41, 'Tema leiab esiteks oma venna Siimona ja ütleb temale: \"Me oleme leidnud Messia!\" - see on meie keeli Kristuse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 42, 'Ja ta viis tema Jeesuse juure. Jeesus vaatas temale otsa ja ütles: \"Sina oled Siimon, Joona poeg, sind peab hüütama Keefaseks!\" - see tähendab Peetruseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 43, 'Järgmisel päeval Jeesus tahtis minna Galileasse; ja Ta leiab Filippuse ning ütleb temale: \"Järgi mind!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 44, 'Aga Filippus oli pärit Betsaidast, Andrease ja Peetruse linnast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 45, 'Filippus leiab Naatanaeli ja ütleb temale: \"Kellest Mooses on kirjutanud käsuõpetuses, ja prohvetid, selle me oleme leidnud, Jeesuse, Joosepi poja, Naatsaretist!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 46, 'Naatanael ütles talle: \"Kas Naatsaretist võib tulla midagi head?\" Filippus ütles temale: \"Tule ja vaata!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 47, 'Jeesus nägi Naatanaeli tema juure tulevat ja ütles temast: \"Ennäe, tõeline iisraellane, kelles ei ole kavalust!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 48, 'Naatanael ütles talle: \"Kust Sa mind tunned?\" Jeesus vastas ja ütles temale: \"Enne kui Filippus sind kutsus, nägin ma sind, kui sa olid viigipuu all!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 49, 'Naatanael vastas Temale: \"Rabi, sina oled Jumala Poeg, Sina oled Iisraeli Kuningas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 50, 'Jeesus vastas ja ütles talle: \"Selleparast et Ma ütlesin: Ma nägin sind viigipuu all! usud sa. Sa saad näha suuremaid asju kui need!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 51, 'Ja Ta ütles talle: \"Tõesti, tõesti Ma ütlen teile, te näete ka taeva olevat avatud ja Jumala inglid astuvat üles ja alla Inimese Poja peale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 1, 'Ja kolmandal päeval olid pulmad Kaanas Galileamaal, ja Jeesuse Ema oli seal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 2, 'Ja ka Jeesus ja Tema jüngrid olid kutsutud pulma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 3, 'Ja kui tuli puudus viinast, ütles Jeesuse Ema Temale: \"Neil ei ole viina!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 4, 'Jeesus ütles Talle: \"Mis Sul minuga asja, Naine? Minu aeg ei ole veel tulnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 5, 'Tema Ema ütles teenijaile: \"Mis Tema teile iganes ütleb, seda tehke!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 6, 'Aga seal oli kuus kivist anumat seismas juutide puhastuskombe pärast ja igaühte mahtus kaks või kolm mõõtu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 7, 'Jeesus ütles neile: \"Täitke anumad veega!\" Ja nemad täitsid need ääretasa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 8, 'Ja Ta ütles neile: \"Ammutage nüüd neist ja viige pidukorraldajale!\" Ja nemad viisid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 9, 'Aga kui pidukorraldaja maitses vett, mis oli viinaks saanud, ega teadnud, kust see oli, kuna teenijad, kes vee olid toonud, teadsid, siis kutsus pidukorraldaja peigmehe.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 10, 'ja ütles talle: \"Igaüks paneb enne lauale hea viina, ja kui juba küllalt on joodud, lahjema. Sina oled hoidnud hea viina siitsaadik!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 11, 'Selle esimese tunnustähe tegi Jeesus Kaanas Galileamaal ja ilmutas Oma au. Ja Tema jüngrid uskusid Temasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 12, 'Pärast Ta läks alla Kapernauma, Tema ja Ta Ema ja Ta vennad ja Ta jüngrid; ja sinna nad ei jäänud kauaks ajaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 13, 'Ja juutide paasapühad olid ligidal. Ja Jeesus läks üles Jeruusalemma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 14, 'Seal Ta leidis pühakojas neid, kes müüsid härgi ja lambaid ja tuvisid, ja rahavahetajaid istumas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 15, 'Ja Ta tegi paelust piitsa ja ajas kõik pühakojast välja, niihästi lambad kui härjad, ja rahavahetajate raha Ta puistas laiali ning kummutas nende lauad');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 16, 'ja ütles tuvimüüjaile: \"Viige need siit ära! Ärge tehke Minu Isa koda kaubakojaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 17, 'Tema jüngritele meenus, et on kirjutatud: \"Püha viha Sinu koja pärast sööb Mind ära!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 18, 'Siis kostsid juudid ning ütlesid Temale: \"Mis tunnustähte Sa näitad meile, et Sa seda teed?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 19, 'Jeesus vastas ning ütles neile: \"Lammutage see tempel, ja Ma püstitan selle kolme päevaga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 20, 'Siis ütlesid juudid: \"Seda templit on ehitatud nelikümmend ja kuus aastat, ja Sina püstitad selle kolme päevaga?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 21, 'Ent Tema rääkis Oma ihu templist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 22, 'Aga kui Ta oli surnuist üles tõusnud, meenus Tema jüngritele, et Ta seda oli ütelnud. Ja nad uskusid Kirja ja sõna, mis Jeesus oli ütelnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 23, 'Aga kui Ta oli Jeruusalemas paasapühal, uskusid paljud Tema nimesse, nähes Tema imetegusid, mida Ta tegi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 24, 'Kuid Jeesus Ise ei usaldanud Ennast nende kätte, sest Ta tundis neid kõiki');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 25, 'ega olnud Tal tarvis, et keegi oleks annud tunnistust kellegi inimese kohta; sest Ta ise teadis, mis oli inimeses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 1, 'Oli variseride seas mees, Nikodeemus nimi, uks juutide ülem.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 2, 'See tuli Jeesuse juure öösel ja ütles Temale: \"Rabi, me teame, et Sa Jumalalt oled tulnud õpetajaks; sest keegi ei või teha neid imetähti, mida Sina teed, kui Jumal ei ole temaga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 3, 'Jeesus vastas ning ütles talle: \"Tõesti, tõesti Ma ütlen sulle, kui keegi ei sunni ülalt, ei või ta Jumala riiki näha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 4, 'Nikodeemus ütles Temale: \"Kuidas võib inimene sündida, kui ta on vana? Ega ta või teist korda minna oma ema ihusse ja sündida?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 5, 'Jeesus vastas: \"Tõesti, tõesti Ma ütlen sulle, kui keegi ei sünni veest ja Vaimust, ei või ta Jumala riiki pääseda!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 6, 'Mis lihast on sündinud, on liha, ja mis Vaimust on sündinud, on vaim!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 7, 'Ära pane imeks, et Ma ütlesin sulle: Te peate uuesti sündima!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 8, 'Tuul puhub, kus ta tahab, ja sa kuuled ta häält, aga sa ei tea, kust ta tuleb ja kuhu ta läheb; nõnda on igaüks, kes on sündinud Vaimust!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 9, 'Nikodeemus vastas ning ütles Talle: \"Kuidas see võib sündida?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 10, 'Jeesus vastas ning ütles talle: \"Sina oled Iisraeli õpetaja, ja ei tea seda?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 11, 'Tõesti, tõesti Ma ütlen sulle, Me räägime, mida Me teame, ja tunnistame, mis Me oleme näinud, ja te ei võta Meie tunnistust vastu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 12, 'Kui Ma teile räägin maisist asjust, ja te ei usu, kuidas te usuksite, kui Ma teile räägiksin taevasist asjust?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 13, 'Ja ometi ei ole ükski muu läinud taevasse kui aga See, Kes taevast on maha tulnud, Inimese Poeg!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 14, 'Ja nõnda nagu Mooses kõrves mao ülendas, nõnda ülendatakse Inimese Poeg,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 15, 'et igaühel, kes usub Temasse, oleks igavene elu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 16, 'Sest nõnda on Jumal maailma armastanud, et Ta Oma Ainusündinud Poja , on annud, et ükski, kes Temasse usub, ei saaks hukka, vaid et temal oleks igavene elu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 17, 'Sest Jumal ei ole Oma Poega läkitanud maailma, et Ta maailma üle kohut mõistaks, vaid et maailm Tema läbi õndsaks saaks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 18, 'Kes usub Temasse, selle üle ei mõisteta kohut; aga kes ei usu, selle üle on juba kohus mõistetud, sest Ta ei ole uskunud Jumala ainusündinud Poja nimesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 19, 'Aga see on kohus, et valgus on tulnud maailma ja inimesed armastasid pimedust rohkem kui valgust, sest nende teod olid kurjad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 20, 'Sest igaüks, kes teeb kurja, vihkab valgust ega tule valguse juure, et ta tegusid ei laidetaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 21, 'Aga kes teeb tõtt, see tuleb valguse juure, et ta teod saaksid avalikuks, sest need on Jumalas tehtud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 22, 'Pärast seda tulid Jeesus ja Tema jüngrid Juudamaale; ja seal Ta viibis nendega ja ristis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 23, 'Aga ka Johannes oli ristimas Ainonis Salimi lähedal, sest seal oli palju vett ja sinna tuli inimesi ja nad ristiti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 24, 'Sest Johannes ei olnud veel vangitorni heidetud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 25, 'Siis hakkasid Johannese jüngrid vaidlema ühe juudiga puhastuse pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 26, 'Ja nad tulid Johannese juure ja ütlesid temale: \"Rabi! See, Kes oli Sinuga sealpool Jordanit, Kellest sina tunnistasid, vaata, see ristib ja kõik lähevad Tema juure!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 27, 'Johannes vastas ning ütles: \"Inimene ei või midagi võtta, kui see temale ei ole antud taevast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 28, 'Te ise olete minu tunnistajad, et ma ütlesin: Mina ei ole Kristus, vaid mind on läkitatud Tema eele!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 29, 'Kellel on pruut, see on peigmees; aga peigmehe sõber seisab ja kuulab teda ja on väga rõõmus peigmehe häälest. See mu rõõm on nüüd saanud täielikuks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 30, 'Tema peab kasvama, aga mina pean kahanema!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 31, 'Kes tuleb ülalt, see on üle kõikide! Kes on maast, see on maast ja räägib maast; Kes tuleb taevast, on üle kõikide');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 32, 'ja tunnistab seda, mida Ta on näinud ja kuulnud, ja Tema tunnistust ei võta ükski vastu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 33, 'Kes võtab vastu Tema tunnistuse, see kinnitab, et Jumal on tõeline!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 34, 'Sest See, Kelle Jumal on läkitanud, räägib Jumala sõnu; sest Jumal ei anna Vaimu mõõdu järele!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 35, 'Isa armastab Poega ja on kõik annud Tema kätte!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 36, 'Kes usub Pojasse, sellel on igavene elu; aga kes ei kuule Poja sõna, see ei saa elu näha, vaid Jumala viha jääb tema peale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 1, 'Kui nüüd Issand sai teada, et variserid olid kuulnud, et Jeesus rohkem jüngreid tegevat ja ristivat kui Johannes -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 2, 'ehk küll Jeesus ise ei ristinud, vaid Tema jüngrid -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 3, 'siis Ta lahkus Judeast ja läks Galileasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 4, 'Aga Tal tuli Samaariast läbi minna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 5, 'Siis Ta tuli Samaaria linna, mida hüütakse Sühhariks, põllu lähedale, mille Jaakob oli annud oma pojale Joosepile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 6, 'Seal oli Jaakobi allikas. Väsinuna teekäimisest istus siis Jeesus maha allika ääre. Oli arvata kuues tund.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 7, 'Siis tuleb Samaariast naine vett ammutama. Jeesus ütleb talle: \"Anna Mulle juua!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 8, 'Ent Tema jüngrid olid läinud linna toidust ostma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 9, 'Siis ütleb Samaaria naine Temale: \"Kuidas Sina, olles juut, küsid juua minult, kes olen Samaaria naine?\" Sest juudid ei lepi kokku samaarlastega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 10, 'Jeesus vastas ning ütles talle: \"Kui sa teaksid Jumala andi ja Kes See on, Kes sinule ütleb: Anna Mulle juua! siis sa paluksid Teda ja Ta annaks sinule elavat vett!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 11, 'Naine ütleb temale: \"Isand, Sul ei ole ämbrit ja kaev on sügav; kust Sa siis saad selle elava vee?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 12, 'Kas Sina oled suurem kui meie isa Jaakob, kes meile andis selle kaevu ja jõi sealt ise, samuti ta pojad ja ta lojused?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 13, 'Jeesus vastas ning ütles temale: \"Igaüks, kes seda vett joob, see januneb jälle;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 14, 'aga kes iganes joob seda vett, mida Mina temale annan, see ei janune igavesti mitte, vaid see vesi, mida Mina temale annan, saab tema sees veeallikaks, mis voolab igavesse ellu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 15, 'Naine ütleb Temale: \"Isand, anna mulle seda vett, et ma ei januneks ega oleks mul vaja siia tulla vett ammutama!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 16, 'Jeesus ütleb talle: \"Mine kutsu oma mees ja tule siia!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 17, 'Naine vastas ning ütles: \"Minul ei ole meest!\" Jeesus ütleb temale: \"Sina ütled õigesti: Mul ei ole meest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 18, 'sest viis meest on sul olnud ja kes sul nüüd on, ei ole mitte sinu mees; seda sa oled õigesti ütelnud.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 19, 'Naine ütleb Temale: \"Issand, ma näen, et Sa oled prohvet!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 20, 'Meie esiisad kummardasid Jumalat sellel mäel ja teie ütlete, et Jeruusalemas olevat paik, kus tuleb kummardada!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 21, 'Jeesus ütleb talle: \"Naine, usu Mind, et tund tuleb, mil te ei kummarda Isa ei sellel mäel ega Jeruusalemas!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 22, 'Teie kummardate, mida te ei tea, meie kummardame, mida me teame, sest õndsus tuleb juutidelt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 23, 'Aga tund tuleb ja on nüüd, et tõelised kummardajad kummardavad Isa vaimus ja tões; sest Isa otsib neid, kes Teda nõnda kummardavad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 24, 'Jumal on Vaim, ja kes Teda kummardavad, need peavad vaimus ja tões teda kummardama!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 25, 'Naine ütles Temale: \"Ma tean, et Messias tuleb, keda nimetatakse Kristuseks, kui See tuleb, siis Ta kuulutab meile kõik!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 26, 'Jeesus ütleb talle: \"Mina, kes sinuga rägin, olen See!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 27, 'Ja seepeale tulid Tema jüngrid ja imestasid, et Ta naisega kõneles. Siiski keegi ei ütelnud: \"Mis Sa küsid või mis Sa räägid temaga?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 28, 'Siis naine jättis oma veekannu sinna ja läks linna ning ütles inimestele:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 29, '\"Tulge vaadake Inimest, Kes mulle on ütelnud kõik, mis ma olen teinud! Kas See ei ole Kristus? \"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 30, 'Nad väljusid linnast ja tulid Tema juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 31, 'Vaheajal palusid jüngrid Teda ning ütlesid: \"Rabi, söö!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 32, 'Aga Tema ütles neile: \"Minul on süüa rooga, millest teie ei tea!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 33, 'Siis ütlesid jüngrid isekeskis: \"Kas ehk keegi on Temale süüa toonud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 34, 'Jeesus ütleb neile: \"Minu roog on see, et Ma teen Selle tahtmist, Kes Mind on läkitanud, ja lõpetan Tema töö.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 35, 'Eks te ise ütle, et on veel neli kuud, ja siis tuleb lõikus? Vaata, Ma ütlen teile, tõstke oma silmad üles ja vaadake põlde, et need on valged lõikuseks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 36, 'Ja nüüd saab lõikaja palka ja kogub vilja igaveseks eluks, et niihästi külvaja kui lõikaja ühtlasi saaksid rõõmutseda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 37, 'Sest siin on sõna tõsi: Üks on, kes külvab, ja teine, kes lõikab!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 38, 'Mina olen teid läkitanud lõikama seda, mille kallal teie pole vaeva näinud, teised on vaeva näinud, ja teie olete tulnud nende vaevavilja lõikama!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 39, 'Aga palju samaarlasi sellest linnast uskus Temasse naise kõne tõttu, kes tunnistas: \"Tema ütles mulle kõik, mis ma olen teinud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 40, 'Kui nüüd samaarlased tulid Tema juure, palusid nad Teda jääda nende juure. Ja Ta viibis seal kaks päeva.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 41, 'Ja veel palju rohkem samaarlasi hakkas uskuma Tema sõna tõttu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 42, 'Nad ütlesid naisele: \"Me ei usu mitte enam sinu kõne pärast; sest me ise oleme kuulnud ja teame, et Tema on tõesti maailma Õnnistegija!\"43 Aga kahe päeva pärast tuli Ta sealt ära Galileasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 43, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 44, 'Sest Jeesus ise tunnistas, et prohvetist ei peeta lugu ta omal isamaal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 45, 'Kui Ta nüüd oli tulnud Galileasse, võtsid galilealased Tema vastu, sest nad olid näinud kõike seda, mis Ta oli teinud Jeruusalemas suurel pühal, sest ka nemad olid käinud neil pühil.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 46, 'Siis tuli Jeesus jälle Galilea Kaanasse, kus Ta oli teinud vee viinaks. Ja seal oli üks kuninga ametnik, kelle poeg oli haige Kapernaumas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 47, 'Kui see sai kuulda, et Jeesus on tulnud Judeast Galileasse, läks ta Tema juure ja palus Teda, et Ta tuleks ja teeks terveks tema poja, sest see oli suremas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 48, 'Siis Jeesus ütles talle: \"Kui te ei näe tunnustähti ega imetegusid, siis te ei usu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 49, 'Kuninga ametnik ütles Temale: \"Issand, tule alla, enne kui mu laps sureb!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 50, 'Jeesus ütles talle: \"Mine, su poeg elab!\" Ja inimene uskus sõna, mis Jeesus talle ütles, ja läks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 51, 'Aga kui ta alles oli minemas, tulid ta sulased talle vastu ja kuulutasid ning ütlesid, et ta poeg elab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 52, 'Siis ta kuulas neilt tundi, mil ta oli hakanud toibuma. Nad ütlesid siis talle: \"Eile seitsmendal tunnil lahkus temast palavik!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 53, 'Siis isa märkas, et see oli sündinud samal tunnil, mil Jeesus talle ütles: \"Su poeg elab!\" Ja tema uskus, samuti kõik ta pere.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 54, 'Selle teise tunnustähe tegi Jeesus, kui Ta Judeast oli tulnud Galileasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 1, 'Pärast seda oli juutide püha ja Jeesus läks Jeruusalemma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 2, 'Aga Jeruusalemas on Lambavärava ligi tiik, seda hüütakse heebrea keeli Betsataks. Sellel on viis võlvitud hoonet.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 3, 'Neis lamas hulk haigeid, pimedaid, jalutumaid, kõhetuid, [kes ootasid vee liikumist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 4, 'Sest aeg-ajalt tuli Ingel alla tiiki ja segas vett. Kes siis esimesena pärast veesegamist sisse astus, sai terveks, olgu mis tahes haiguses ta oli.]');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 5, 'Aga seal oli inimene, kes oli kolmkümmend kaheksa aastat haige olnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 6, 'Kui Jeesus teda nägi seal lamavat ja teada sai, et ta juba kaua aega oli maas olnud, ütles Ta talle: \"Kas sa tahad terveks saada?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 7, 'Haige vastas temale: \"Isand, mul ei ole seda inimest, kes mind aitaks tiiki, kui vett segatakse; seni, kui ma tulen, astub teine minu eel sisse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 8, 'Jeesus ütles temale: \"Tõuse üles, võta oma voodi ja kõnni!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 9, 'Ja inimene sai kohe terveks, võttis oma voodi ja kõndis! Aga see päev oli hingamispäev.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 10, 'Siis ütlesid juudid sellele, kes oli terveks saanud: \"Nüüd on hingamispäev, sul ei sobi voodit kanda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 11, 'Tema vastas neile: \"Kes mind terveks tegi, ütles mulle: Võta oma voodi ja kõnni!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 12, 'Nad küsisid temalt: \"Kes on See Inimene, kes sulle ütles: Võta oma voodi ja kõnni?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 13, 'Aga tervekssaanu ei teadnud, Kes Ta on, sest Jeesus oli läinud kõrvale, seal paigas oleva rahva hulka.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 14, 'Pärast seda leiab Jeesus tema pühakojast ja ütleb talle: \"Vaata, sa oled terveks saanud, ära tee enam pattu, et sulle ei juhtuks midagi halvemat!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 15, 'Inimene läks ära ja ütles juutidele, et see oli Jeesus, Kes ta terveks tegi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 16, 'Ja sellepärast juudid kiusasid Jeesust taga, et Ta seda oli teinud hingamispäeval.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 17, 'Aga Jeesus kostis neile: \"Minu Isa tegutseb tänini ja Mina tegutsen!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 18, 'Sellepärast püüdsid nüüd juudid veel enam Teda tappa, et Ta mitte ainult ei olnud pannud mikski hingamispäeva, vaid oli ka ütelnud Jumala Oma Isa olevat, tehes Ennast Jumala sarnaseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 19, 'Siis Jeesus vastas ning ütles neile: \"Tõesti, tõesti Ma ütlen teile, Poeg ei või Iseenesest teha midagi kui vaid seda, mida Ta näeb Isa tegevat! Sest mida Isa teeb, seda teeb Poeg nõndasamuti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 20, 'Sest Isa armastab Poega ja näitab Talle kõik, mida Ta Ise teeb, ja tahab Temale näidata suuremaid tegusid kui need on, nõnda et te seda panete imeks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 21, 'Sest otsegu Isa äratab üles surnuid ja teeb elavaks, nõnda teeb ka Poeg elavaks, keda tahab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 22, 'Sest Isa ei mõista kohut kellegi üle, vaid kõik kohtu on Ta annud Poja kätte,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 23, 'et kõik austaksid Poega, nagu nad austavad Isa. Kes ei austa Poega, see ei austa Isa, kes Tema on läkitanud. 24 Tõesti, tõesti ma ütlen teile, kes Minu sõna kuuleb ja usub seda, kes Mind on läkitanud, sellel on igavene elu ja see ei tule mitte kohtu alla, vaid on surmast läinud elusse!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 24, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 25, 'Tõesti, tõesti Ma ütlen teile, tund tuleb ja on juba käes, mil surnud peavad kuulma Jumala Poja häält, ja kes seda kuulevad, peavad elama!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 26, 'Sest otsegu Isal on elu iseeneses, nõnda on Ta annud ka Pojale, et elu on Temas Eneses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 27, 'Ja Ta on Temale annud meelevalla ka kohut pidada, sellepärast et Ta on Inimese Poeg.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 28, 'Ärge pange seda imeks, sest tuleb tund, mil kõik, kes on haudades, kuulevad Tema häält');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 29, 'ning tulevad välja need, kes on teinud head, elu ülestõusmiseks, aga kes on teinud halba, hukkamõistmise ülestõusmiseks.30 Mina ei või Iseenesest ühtki teha. Nagu Ma kuulen, nõnda Ma mõistan kohut ja Minu otsus on õige, sest Mina ei nõua Oma tahtmist, vaid Selle tahtmist, Kes Mind on läkitanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 30, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 31, 'Kui Mina tunnistan Iseenesest, siis ei ole Mu tunnistus mitte tõsi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 32, 'On Teine, Kes Minust tunnistab, ja Ma tean, et see tunnistus, mida Ta Minust tunnistab, on tõsi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 33, 'Teie läkitasite Johannese juure ja tema andis tõele tunnistust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 34, 'Aga Mina ei võta tunnistust inimeselt, vaid räägin seda, et teie saaksite õndsaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 35, 'Tema oli küünal, mis põles ja paistis; aga teie tahtsite natuke aega ilutseda tema valguses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 36, 'Aga Minul on suurem tunnistus kui Johannese oma, sest teod, mis Isa Minule on annud, et Ma needlõpetaksin, needsamad teod, mida Ma teen, tunnistavad Minust, et Isa Mind on läkitanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 37, 'Ja Isa, Kes Mind on läkitanud, see on tunnistanud Minust. Teie ei ole iialgi kuulnud Tema häält ega ole näinud Tema nägu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 38, 'ega ole teil Tema sõna püsivana teie sees, sest te ei usu Teda, Kelle Tema on läkitanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 39, 'Te uurite Pühi Kirju, sest te arvate enestel neis olevat igavese elu, ja need on, mis tunnistavad Minust,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 40, 'ja te ei taha tulla Minu juure, et te saaksite elu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 41, 'Mina ei võta vastu austust inimestelt,42 aga Mina tunnen teid, et teil ei ole Jumala armastust iseenestes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 42, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 43, 'Mina olen tulnud Oma Isa nimel, ja te ei võta Mind vastu, kui teine tuleb iseenese nimel, tema te võtate vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 44, 'Kuidas te võite uskuda, kui te võtate austust vastu üksteiselt ega otsi seda austust, mis tuleb Üksnes Jumalalt?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 45, 'Ärge mõtelge, et Mina kaebaksin teie peale Isa ees, on olemas, kes teie peale kaebab - Mooses, kelle peale te loodate.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 46, 'Sest kui te usuksite Moosest, usuksite te ka Mind; sest tema on kirjutanud Minust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 47, 'Aga kui te tema kirju ei usu, kuidas te siis usuksite Minu sõnu?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 1, 'Pärast seda läks Jeesus Tibeeria poolt Galilea mere teisele poole.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 2, 'ja palju rahvast järgis Teda, sest nad nägid Tema imetähti, mida Ta tegi haigete juures.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 3, 'Aga Jeesus läks üles mäele ja istus sinna Oma jüngritega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 4, 'Ja paasa, juutide püha, oli ligi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 5, 'Kui nüüd Jeesus Oma silmad üle tõstis ja nägi palju rahvast enese juure tulevat, ütles Ta Filippusele: \"Kust me ostame leiba, et need saaksid süüa?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 6, 'Ent seda Ta ütles teda kiusates, sest Ta teadis küll, mida Ta tahtis teha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 7, 'Filippus vastas Talle: \"Leibu kahesaja teenari eest ei jätku neile, et igaüks natukesegi võtaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 8, 'Üks Tema jüngritest, Andreas, Siimon Peetruse vend, ütleb Talle:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 9, '\"Siin on üks poisike, kellel on viis odraleiba ja kaks kalakest, aga mis saab sellest nii paljudele?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 10, 'Jeesus ütles: \"Asetage inimesed maha istuma!\" Ja seal paigas oli palju rohtu. Siis asetusid maha arvult umbes viis tuhat meest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 11, 'Jeesus võttis nüüd leivad, tänas ja, andis neile, kes maas istusid, samuti kai neist kalukesist, niipalju kui nad tahtsid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 12, 'Aga kui nende kõhud olid täis saanud, ütles Ta oma jüngritele: \"Koguge ülejäänud palukesed kokku, et midagi ei läheks raisku!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 13, 'Siis nad kogusid kokku kaksteistkümmend korvitäit palukesi, mis oli üle jäänud viiest odraleivas nendelt, kes olid söönud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 14, 'Kui nüüd rahvas nägi tunnustähte, mille Jeesus oli teinud, ütlesid nad: \"See on tõesti Prohvet, kes maailma pidi tulema!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 15, 'Siis Jeesus sai aru, et nad tahavad tulla ja Teda vägisi võtta ning teha kuningaks. Ja Ta läks jälle kõrvale mäe ainuüksi.16 Aga kui õhtu tuli, läksid Tema jüngrid mereranda');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 16, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 17, 'ja astusid paati ja tulid teisele poole merd Kapernauma. Aga pimedus oli juba käes ja Jeesus ei olnud veel tulnud nende juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 18, 'Ja meri hakkas lainetama, sest suur tuul puhus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 19, 'Kui nad nüüd olid sõudnud umbes kakskümmend viis või kolmkümmend vagu maad, nägid nad Jeesust merel kõndivat ja paadi lähedale jõudvat, ja nad lõid kartma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 20, 'Aga Tema ütles neile: \"Mina olen see, ärge kartke!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 21, 'Nüüd nad tahtsid Teda paati võtta, ja varsti sai paat sinna randa, kuhu nad sõitsid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 22, 'Järgmisel päeval nägi rahvas, kes viibis mere teisel rannal, et teist lootsikut ei olnud seal, vaid oli ainult üks, ja et Jeesus ei olnud astunud ühes Oma jüngritega paati, vaid et ainult Tema jüngrid olid ära läinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 23, 'Oli aga tulnud veel lootsikuid Tibeeriast selle paiga lähedale, kus nad olid leiba söönud, kui Issand oli Jumalat tänanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 24, 'Kui nüüd rahvas nägi, et seal ei olnud Jeesust ega Tema jüngreid, läksid nad lootsikusse ja tulid Kapernauma ning otsisid Jeesust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 25, 'Ja kui nad Ta leidsid mere teiselt rannalt, ütlesid nad Talle: \"Rabi, millal Sa siia said?\"26 Jeesus vastas neile ning ütles: \"Tõesti, tõesti Ma ütlen teile, et te ei otsi Mind, sellepärast et te nägite tunnustähti, vaid et te sõite leiba ja teie kõhud said täis!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 26, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 27, 'Ärge hankige rooga, mis hävib, vaid rooga, mis jääb igaveseks eluks, mida Inimese Poeg tahab teile anda, sest Teda on Jumal Isa pitseriga kinnitanud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 28, 'Siis ütlesid nad Temale: \"Mis me peame tegema, et võiksime teha Jumala tegusid?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 29, 'Jeesus vastas ning ütles neile: \"See on Jumala tegu, et te usute Temasse, Kelle Ta on läkitanud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 30, 'Siis nad ütlesid Temale: \"Mis tunnustähe sa Siis teed, et me näeksime ja usuksime Sind? Mis Sa teed?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 31, 'Meie esiisad sõid kõrves mannat, nagu on kirjutatud: Ta andis neile süüa leiba taevast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 32, 'Siis Jeesus ütles neile: \"Tõesti, tõesti Ma ütlen teile! Mooses ei annud teile leiba taevast, aga Minu Isa annab teile tõelise leiva taevast!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 33, 'Sest Jumala leib on see, kes tuleb taevast alla ja annab maailmale elu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 34, 'Siis nad ütlesid Talle: \"Issand, anna meile ikka seda leiba!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 35, '. Jeesus ütles neile: \"Mina olen eluleib. Kes tuleb Minu juure, see ei näe nälga, ja kes Minusse usub, sellele ei tule iialgi janu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 36, 'Aga Mina olen teile ütelnud, et te olete Mind näinud ega usu mitte!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 37, 'Kõik, mis Isa Minule annab, tuleb Minu juure, ja kes Minu juure tuleb, seda Ma ei lükka välja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 38, 'Sest Ma olen taevast alla tulnud mitte Oma tahtmist tegema, vaid Selle tahtmist, Kes Mind on läkitänud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 39, 'Aga see on Selle tahtmine, Kes Mind on läkitanud, et Ma ühtki ei kaota sellest kõigest, mis Ta Minule on annud, vaid et Ma selle üles äratan viimsel päeval.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 40, 'Sest see on Mu Isa tahtmine, et igaühel, kes Poega näeb ja Temasse usub, on igavene elu, ja Ma äratan tema üles viimsel päeval!\"41 Siis nurisesid juudid Tema üle, et Ta oli ütelnud: \"Mina olen leib, mis taevast on alla tulnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 41, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 42, 'Ja nad ütlesid: \"Eks see ole Jeesus, Joosepi poeg, Kelle isa ja Ema me tunneme? Kuidas Ta siis ütleb: Ma olen tulnud taevast?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 43, 'Jeesus vastas ning ütles neile: \"Ärge nurisege isekeskis!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 44, 'Ükski ei või tulla Minu juure, kui teda ei tõmba Isa, kes Mind on läkitanud; ja Mina äratan tema üles viimsel päeval.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 45, 'Prohvetite raamatuis on kirjutatud: Nad kõik peavad olema Jumala poolt õpetatud! Igaüks, kes on Isalt kuulnud ja on õppinud, tuleb Minu juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 46, 'Mitte, et keegi teine oleks näinud Isa kui See, Kes on Jumala juurest; See on näinud Isa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 47, 'Tõesti, tõesti Ma ütlen teile: Kes usub, sellel on igavene elu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 48, 'Mina olen Eluleib.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 49, 'Teie esiisad sõid kõrves mannat ja surid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 50, 'See on leib, mis taevast alla tuleb, et inimene seda sööks ega sureks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 51, 'Mina olen elav leib, mis taevast on alla tulnud. Kui keegi sööb seda leiba, siis ta elab igavesti; ja leib, mille Mina annan, on Minu Liha, mille annan maailma elu eest !\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 52, 'Siis riidlesid juudid isekeskis, üteldes: \"Kuidas see võib meile anda Oma Liha süüa?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 53, 'Siis Jeesus ütles neile: \"Tõesti, tõesti Ma ütlen teile, et kui te ei söö Inimese Poja Liha ega joo Tema Verd, siis ei ole elu teis enestes!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 54, 'Kes sööb Minu Liha ja joob Minu Verd, sel on igavene elu; ja Mina äratan tema üles viimsel päeval.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 55, 'Sest Mu Liha on tõeline roog ja Mu Veri on tõeline jook.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 56, 'Kes sööb Minu Liha ja joob Minu Verd, jääb Minusse ja Mina temasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 57, 'Nagu elav Isa Mind on läkitanud ja Mina elan Isa läbi, nõnda ka see, kes Mind sööb, elab Minu läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 58, 'See on leib, mis taevast on alla tulnud; see ei ole niisugune, mida sõid teie vanemad ja surid. Kes seda leiba sööb, elab igavesti!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 59, 'Seda Ta ütles kogudusekojas, õpetades Kapernaumas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 60, 'Siis paljud Tema jüngrid, kui nad seda kuulsid, ütlesid: \"See kõne on kõva, kes võib seda kuulda?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 61, 'Aga et Jeesus Iseenesest teadis, et Tema jüngrid selle üle nurisesid, ütles Ta neile: \"Kas see teid pahandab?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 62, 'Aga mis on siis, kui te näete Inimese Poja üles minevat sinna, kus Ta enne oli?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 63, 'Vaim on, kes teeb elavaks; lihast ei ole kasu millekski; sõnad, mis mina teile olen rääkinud, on vaim ja on elu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 64, 'Aga teie seas on mõned, kes ei usu!\" Sest Jeesus teadis algusest peale, kes need olid, kes ei uskunud, ja kes see on, kes Tema ära annab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 65, 'Ja Ta ütles: \"Sellepärast Ma olen teile ütelnud, et ükski ei või tulla Minu juure, kui temale seda ei ole annud Minu Isa!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 66, 'Sellest ajast läksid paljud Tema jüngrid ära Ta järelt ega kõndinud enam Temaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 67, 'Siis Jeesus ütles neile kaheteistkümnele: \"Kas ka teie tahate ära minna?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 68, 'Siimon Peetrus vastas Temale: \"Issand, kelle juure me läheme? Sinul on igavese elu sõnad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 69, 'ja me oleme uskunud ja tunnud, et Sina oled Jumala Püha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 70, 'Jeesus kostis neile: \"Eks ole Mina teid kaksteistkümmend ära valinud, ja üks teie seast on kurat?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 71, 'Aga Ta rääkis Juudast, Iskarioti Siimona pojast, sest see pidi Tema ära andma ja oli üks neist kaheteistkümnest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 1, 'Ja pärast seda käis Jeesus mööda Galileamaad, sest Ta ei tahtnud käia Juudamaal, kuna juudid püüdsid Teda tappa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 2, 'Aga juutide lehtmajade püha oli ligi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 3, 'Siis ütlesid ta vennad Temale \"Mine siit ära Judeasse, et ka Su jüngrid näeksid Su tegusid, mida Sa teed;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 4, 'Teised ütlesid: \"Tema on Kristus!\" Aga muud ütlesid: \"Ega siis Kristus Galileast tule?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 5, 'Sest Ta vennadki ei uskunud Temasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 6, 'Siis ütles Jeesus neile: \"Minu aeg ei ole veel tulnud, aga teie aeg on alati soodus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 7, 'Maailm ei või teid vihata, aga Mind ta vihkab, sest Mina tunnistan temast, et Tema teod on kurjad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 8, 'Minge teie üles pühiks; Mina veel ei lähe üles neiks pühiks, sest Minu aeg ei ole veel täis saanud.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 9, 'Kui Ta neile seda oli ütelnud, jäi Ta Galileasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 10, 'Ent kui Ta vennad olid üles läinud pühiks, läks Temagi üles, mitte avalikult, vaid otsegu salaja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 11, 'Siis otsisid juudid Teda pühil ja ütlesid: \"Kus Ta on?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 12, 'Ja nurisemine Tema pärast oli suur hulkade seas. Mõned ütlesid: \"Tema on hea!\" Teised ütlesid: \"Ei, vaid Ta eksitab rahvast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 13, 'Kuid ükski ei rääkinud Temast julgesti hirmu pärast juutide ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 14, 'Aga kui pühad juba olid poole peale jõudnud, läks Jeesus üles pühakotta ja õpetas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 15, 'Siis juudid panid seda imeks ning ütlesid: \"Kuidas see Kirja tunneb ilma õppimata?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 16, 'Jeesus aga vastas neile ning ütles: \"Minu õpetus ei ole Minu oma, vaid Selle, Kes Mind on läkitanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 17, 'Kui keegi tahab teha Tema tahtmist, see tunneb, kas see õpetus on Jumalast või kas Mina räägin Iseenesest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 18, 'Kes iseenesest räägib, see otsib iseenese austust; aga Kes otsib selle austust, Kes Teda on läkitanud, See on tõeline ja Temas ei ole ülekohut.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 19, 'Eks Mooses ole teile annud käsuõpetuse, ja ükski teie seast ei tee käsuõpetuse järele. Miks te püüate Mind tappa?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 20, 'Rahvas vastas: \"Sul on kuri vaim; kes püüab Sind tappa?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 21, 'Jeesus kostis ning ütles neile: \"Ühe teo Ma tegin ja te kõik imestate.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 22, 'Sellepärast on Mooses teile annud ümberlõikamise - mitte nii, et see on Moosesest, vaid esiisadest - ja te lõikate inimese ümber ka hingamispäeval.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 23, 'Kui inimene lõigatakse ümber hingamispäeval, et Moosese käsuõpetust ei rikutaks, miks te siis olete pahased Minule, et Ma olen kogu inimese terveks teinud hingamispäeval?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 24, 'Ärge mõistke kohut silmnäo järele, vaid mõistke õiget kohut!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 25, 'Siis ütlesid mõned Jeruusalema elanikest: \"Eks See ole See, Keda nad püüavad tappa?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 26, 'Ja ennäe, Tema räägib vabalt ja nad ei ütle Talle midagi. Kas on ehk meie ülemad tõesti ära tunnud, et Tema on Kristus?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 27, 'Ometi me teame, kust Ta on. Aga kui Kristus tuleb, siis ei tea ükski, kust Ta on!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 28, 'Siis Jeesus hüüdis pühakojas õpetades ning ütles: \"Küll te tunnete Mind ja teate, kust Ma olen, ja Ma ei ole tulnud iseenesest; aga tõeline on See, Kes Mind on läkitanud, Keda te ei tunne.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 29, 'Mina tunnen Teda, sest Ma olen Temast, ja Tema on Mind läkitanud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 30, 'Siis nad püüdsid Teda kinni võtta, aga ükski ei pistnud kätt Tema külge, sest Tema tund ei olnud veel tulnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 31, 'Aga paljud rahva seast uskusid Temasse ja ütlesid: \"Kui Kristus tuleb, kas Ta peaks tegema veel rohkem tunnustähti kui See on teinud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 32, 'Variserid kuulsid rahvast isekeskis seda Temast sosistavat. Ja variserid ja ülempreestrid läkitasid sulaseid Teda kinni võtma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 33, 'Siis ütles Jeesus neile: \"Ma olen veel üürikese aja teie juures ja siis Ma lähen Selle juure, Kes Mind on läkitanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 34, 'Siis te otsite Mind, aga ei leia, ja kus Ma olen, sinna Te ei või tulla!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 35, 'Siis ütlesid juudid isekeskis: \"Kuhu Ta tahab minna, et me Teda ei peaks leidma? Mõtleb Ta minna hajuvil asuvate kreeklaste juure ja õpetada kreeklasi?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 36, 'Mis sõna see on, mis Ta ütles: Te otsite Mind, aga ei leia, ja kus Ma olen, sinna te ei või tulla?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 37, 'Aga pühade viimsel, suurel päeval seisis Jeesus ja kõneles valju häälega ning ütles: \"Kui kellelgi on janu, see tulgu Minu juure ja joogu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 38, 'Kes usub Minusse, nagu Kiri ütleb, selle ihust peavad voolama elava vee jõed!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 39, 'Aga seda Ta ütles Vaimust, Kelle pidid saama need, kes Temasse usuvad; sest veel ei olnud Vaimu, ei olnud ju Jeesust veel mitte austatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 40, 'Siis ütlesid paljud rahva seast, kes seda kõnet kuulsid: \"Tema on tõesti see prohvet!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 41, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 42, 'Eks Kiri ütle, et Kristus tuleb Taaveti soost ja Petlemast, alevist, kust oli Taavet?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 43, 'Siis tekkis lahkmeel rahva sekka Tema pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 44, 'Aga mõned neist tahtsid Teda kinni võtta, kuid ükski ei pistnud kätt Tema külge.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 45, 'Nii tulid sulased ülempreestrite ja variseride juure tagasi. Ja need küsisid neilt: \"Mispärast te ei ole Teda toonud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 46, 'Sulased kostsid: \"Ükski inimene ei ole iialgi nõnda rääkinud nagu See Inimene!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 47, 'Siis vastasid variserid neile: \"Kas teiegi olete eksitatud?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 48, 'Kas ükski ülemaist või variseridest on uskunud Temasse?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 49, 'Aga see rahvahulk, kes ei tunne käsuõpetust, on neetud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 50, 'Nikodeemus, kes enne oli Tema juure tulnud ja oli üks nende seast, ütleb neile:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 51, '\"Kas meie seadus inimese hukka mõistab, enne kui ta tema üle kuulab ja teada saab, mis ta on teinud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 52, 'Nad vastasid ning ütlesid temale: \"Oled ka sina Galileast? Uuri ja vaata, ei Galileast ei tõuse prohvetit!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 53, 'Ja igaüks läks koju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 1, 'Aga Jeesus läks õlimäele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 2, 'Vara hommikul Ta tuli jälle pühakoita ja kõik rahvas tuli Tema juure; ja Ta istus maha ja õpetas neid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 3, 'Siis ütles Jeesus juutidele, kes Teda uskusid: \"Kui te jääte Minu sõnasse, siis te olete tõesti Minu jüngrid');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 4, 'Teie teete oma isa tegusid!\" Nad ütlesid Temale: \"Meie ei ole sündinud porduelust; meil on üks isaks, Jumal!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 5, 'Tõesti, tõesti Ma ütlen teile, et kui keegi Minu sõna peab, siis ta ei näe surma igavesti!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 6, 'Seda nad ütlesid Teda kiusates, et neil oleks kaebamist tema peale. - Jeesus kummardas ja kirjutas sõrmega maa peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 7, 'Kui nad siis küsides käisid Temale peale, ajas Ta enese sirgu ning ütles neile: \"Kes teie seast on patuta, see olgu esimene tema peale kivi viskama!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 8, 'Ja Ta kummardas jälle ning kirjutas maa peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 9, 'Kui nad seda kuulsid ja südametunnistus neid süüdistas, läksid nad välja üksteise järele, vanemad eesotsas. Ja Jeesus jäi sinna üksi ja naine, nagu ta seal seisis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 10, 'Ent Jeesus ajas Enese sirgu ega näinud kedagi näinud kui naist; ja Ta ütles naisele: \"Naine, kus on need sinu süüdistaiad? Ega keegi ole sind hukka mõistnud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 11, 'Naine vastas: \"Mitte keegi, Issand!\" Jeesus ütles: \"Ega Minagi sind hukka mõista; mine ja ära tee enam pattu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 12, 'Siis rääkis Jeesus jälle neile ning ütles: \"Mina olen maailma valgus. Kes Mind järgib, see ei käi pimeduses, vaid temal on elu valgus!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 13, 'Siis variserid ütlesid Talle: \"Sina tunnistad Iseenesest, Sinu tunnistus ei ole tõsi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 14, 'Jeesus vastas ning ütles neile: \"Ehk Mina küll Iseenesest tunnistan, on Mu tunnistus siiski tõsi, sest Ma tean, kust Ma olen tulnud ja kuhu Ma lähen. Aga teie ei tea, kust Ma tulen ja kuhu Ma lähen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 15, 'Teie mõistate kohut liha järele, Mina ei mõista kohut kellegi üle!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 16, 'Aga kui Mina ka kohut mõistan, siis on Minu otsus tõsi, sest Mina ei ole üksi, vaid Minuga on see, Kes Mind on läkitanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 17, 'Ja teie käsuõpetuseski on kirjutatud, et kahe inimese tunnistus on tõsi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 18, 'Mina annan Enesest Ise tunnistust, ja ka Isa, Kes Mind on läkitanud, tunnistab Minust!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 19, 'Siis nad ütlesid Temale: \"Kus on Sinu Isa?\" Jeesus vastas: \"Teie ei tunne Mind ega Mu Isa. Kui te Mind tunneksite, siis Te tunneksite ka Mu Isa!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 20, 'Need sõnad rääkis Jeesus ohvriraha kirstu juures, kui Ta pühakojas õpetas. Ja ükski ei võtnud Teda kinni, sest Tema tund ei olnud veel tulnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 21, 'Siis ütles Jeesus taas neile: \"Mina lähen ära ja te otsite Mind, ja te surete oma pattudesse. Kuhu Mina lähen, sinna teie ei või tulla!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 22, 'Siis ütlesid juudid: \"Kas Ta õige tahab ennast tappa, et Ta ütleb: Kuhu mina lähen, sinna teie ei või tulla?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 23, 'Ja Ta ütles neile: \"Teie olete alt, Mina olen ülalt, teie olete sellest maailmast, Mina ei ole sellest maailmast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 24, 'Sellepärast Ma ütlesin teile, et te surete oma pattudesse; sest kui te ei usu, et Mina see olen, siis te surete oma pattudesse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 25, 'Nemad aga ütlesid Temale: \"Kes Sa siis oled?\" Jeesus vastas neile: \"Kõigepealt See, mida Mina teile ütlengi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 26, 'Mul on teist palju ütelda ja teie kohta otsustada; aga See, Kes Mind on läkitanud, on tõeline, ja mis Ma Temalt olen kuulnud, seda Ma räägin maailmale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 27, 'Nad ei saanud aru, et Ta rääkis neile Isast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 28, 'Siis ütles Jeesus: \"Kui te Inimese Poja olete ülendanud, siis te mõistate, et Mina see olen ja et Ma ei tee midagi Iseenesest, vaid räägin seda selle järele, kuidas Mu Isa Mind on õpetanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 29, 'Ja See, kes Mind on läkitanud, on Minuga; Ta ei ole Mind üksi jätnud, sest Ma teen ikka, mis on Tema meelt mööda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 30, 'Kui Ta seda rääkis, uskusid paljud Temasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 31, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 32, 'ja tunnetate tõe, ja tõde teeb teid vabaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 33, 'Nemad kostsid Temale: \"Meie oleme Aabrahami sugu ega ole veel iialgi kedagi orjanud; kuidas Sa siis ütled: Te saate vabaks?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 34, 'Jeesus kostis neile: \"Tõesti, tõesti Ma ütlen teile, et igaüks, kes teeb pattu, on patu ori!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 35, 'Ent ori ei jää majasse igavesti, poeg jääb igavesti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 36, 'Kui nüüd Poeg teid vabaks teeb, siis te olete õieti vabad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 37, 'Ma tean, et te olete Aabrahami sugu; aga te püüate Mind tappa, sest Mu sõna ei mahu teisse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 38, 'Mina räägin, mida olen näinud Oma Isalt, ja teie teete, mida olete kuulnud oma isalt!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 39, 'Nad vastasid ning ütlesid Temale: \"Meie isa on Aabraham!\" Jeesus ütles neile: \"Kui te oleksite Aabrahami lapsed, siis te teeksite ka Aabrahami tegusid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 40, 'Aga nüüd te püüate tappa Mind, Inimest, Kes olen rääkinud teile tõtt, mida Ma olen kuulnud Jumalalt. Seda ei ole Aabraham teinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 41, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 42, 'Jeesus ütles neile: \"Oleks Jumal teie isa, siis te armastaksite Mind, sest Mina olen lähtunud Jumalast ja tulen Temast, sest Ma ei ole ju tulnud Iseenesest, vaid Tema on Mind läkitanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 43, 'Mispärast te ei mõista Minu kõnet? Sellepärast et te ei või kuulda Minu sõna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 44, 'Teie olete oma isast kuradist ja oma isa himude järele te tahate teha! Tema on inimese tapja olnud algusest peale ega ole jäänud püsima tõesse, sest tõde ei ole tema sees. Kui ta räägib valet, siis ta räägib omast, sest ta on valetaja ja vale isa!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 45, 'Aga et Mina tõtt räägin, siis te ei usu Mind!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 46, 'Kes teie seast võib Mind patus süüdistada? Kui Mina tõtt räägin, miks te siis ei usu Mind?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 47, 'Kes Jumalast on, see kuuleb Jumala sõnu. Teie ei kuule selle pärast, et teie ei ole Jumalasi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 48, 'Siis kostsid juudid ning ütlesid Talle: \"Eks me ütle õigesti, et Sina oled samaarlane ja et Sul on kuri vaim?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 49, 'Jeesus vastas: \"Mul ei ole kurja vaimu, aga Ma austan Oma Isa ja teie teotate Mind.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 50, 'Mina aga ei otsi Oma au; Üks on, Kes seda otsib ja kohut mõistab!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 51, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 52, 'Juudid ütlesid Temale: \"Nüüd me oleme ära tunnud, et Sul on kuri vaim! Aabraham on surnud ja prohvetid, ja Sina ütled: Kui keegi Minu sõna peab, siis ta ei maitse surma igavesti!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 53, 'Kas Sina oled suurem kui meie isa Aabraham, kes on surnud? Ja prohvetid on surnud. Kelleks Sina ennast pead?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 54, 'Jeesus vastas: \"Kui Mina Iseenesele annan austust, siis ei ole Mu austus midagi; Minu Isa on, Kes Mulle annab austuse, Keda te ütlete oma Jumala olevat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 55, 'Ja teie ei ole Teda tunnud, aga Mina tunnen Teda. Ja kui Ma ütleksin, et Ma Teda ei tunne, siis Ma oleksin teie sarnane valelik; aga Ma tunnen Teda ja pean Tema sõna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 56, 'Aabraham, teie isa, hakkas rõõmutsema, et tema saab näha Minu päeva. Ja Ta nägi seda ja oli rõõmus!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 57, 'Siis ütlesid juudid Temale: \"Sa ei ole veel viiskümmend aastat vana ja oled näinud Aabrahami?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 58, 'Jeesus ütles neile: \"Tõesti, tõesti Ma ütlen teile, et enne kui Aabraham sündis, olin Mina!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 59, 'Siis nad haarasid kive, ei Tema peale visata. Aga Jeesus peitis enese ära ja väljus pühakojast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 1, 'Ja mööda minnes Ta nägi inimest, kes sündimisest saadik oli olnud pime.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 2, 'Ja Tema jüngrid küsisid Temalt: \"Rabi, kes on pattu teinud, kas tema või ta vanemad, et ta on sündinud pimedana?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 3, 'Jeesus vastas: \"Ei ole tema pattu teinud ega ta vanemad, vaid et Jumala teod saaksid temas avalikuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 4, 'Meie peame tegema Selle tegusid, Kes Mind on läkitanud, niikaua kui päeva on; öö tuleb, mil ükski ei või midagi teha!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 5, 'Niikaua kui Ma olen maailmas, olen Ma maailma valgus!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 6, 'Kui Ta seda oli ütelnud, sülitas Ta maha ja tegi süljest muda ning võidis selle mudaga tema silmad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 7, 'ja ütles talle: \"Mine pese silmi Siiloa tiigis!\" - Siiloa tähendab \"Läkitatu\". - Siis ta läks sinna ja pesi, ja tuli nägijana tagasi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 8, 'Aga naabrid ja kes teda varemini olid näinud kerjuse olevat, ütlesid: \"Eks see ole sama, kes istus ja kerjas?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 9, 'Ühed ütlesid: \"See on sama!\" Aga teised: \"Ei ole, vaid ta on tema sarnane!\" Tema ise ütles: \"Mina olen see!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 10, 'Siis nad küsisid temalt: \"Kuidas siis sinu silmad avati?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 11, 'Tema vastas: \"Inimene, Keda kutsutakse Jeesuseks, tegi muda ja võidis sellega mu silmi ja ütles mulle: Mine Siiloa tiigi ääre ja pese silmi! Aga kui ma sinna läksin ja pesin, sain ma nägijaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 12, 'Siis ütlesid nad temale: \"Kus Ta on?\" Tema ütles: \"Ei mina tea!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 13, 'Siis nad viisid tema, kes enne oli pime olnud, variseride juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 14, 'Aga see oli hingamispäev, kui Jeesus tegi muda ja avas ta silmad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 15, 'Siis küsisid variseridki taas temalt, kuidas ta oli nägijaks saanud. Aga tema ütles neile: \"Ta pani muda mu silmadele ja, ma pesin näo ning sain nägijaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 16, 'Siis ütlesid mõned variseridest: \"See inimene ei ole Jumalast, sest ta ei pea hingamispäeva!\" Teised ütlesid: \"Kuidas võib patune inimene teha niisuguseid tunnustähti?\" Ja nende seas oli lahkmeel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 17, 'Nüüd nad ütlesid taas pimedale: \"Sina, mis sina ütled Temast, et Ta su silmad avas?\" Tema ütles: \"Ta on prohvet!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 18, 'Aga juudid ei uskunud, et ta oli olnud pime ja oli saanud nägijaks, kuni nad kutsusid nägijaks-saanu vanemad');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 19, 'ja küsisid neilt ning ütlesid: \"Kas see on teie poeg, kelle te ütlete pimedana sündinud olevat? Kuidas ta siis nüüd näeb?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 20, 'Ta vanemad kostsid neile ning ütlesid: \"Me teame, et see on meie poeg ja et ta sündis pimedana.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 21, 'Aga kuidas ta nüüd näeb, seda me ei tea; või kes ta silmad avas, seda me ei tea. Ta on küllalt vana, küsige temalt eneselt, küllap ta ise kostab enese eest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 22, 'Seda ütlesid ta vanemad, sest nad kartsid juute; sest juudid olid juba isekeskis võtnud nõuks, et kui keegi peaks Teda tunnistama Kristuseks, siis see lükatakse kogudusest välja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 23, 'Sellepärast ütlesid ta vanemad: \"Ta on küllalt vana, küsige temalt eneselt!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 24, 'Siis nad kutsusid teist korda inimese, kes oli pime olnud, ja ütlesid temale: \"Anna Jumalale au! Me teame, et See Inimene on patune!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 25, 'Tema vastas nüüd: \"Kas Ta on patune, seda ma ei tea; üht ma tean, et olin pime ja nüüd näen!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 26, 'Nad küsisid nüüd temalt: \"Mis Ta tegi sulle? Kuidas Ta su silmad avas?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 27, 'Ta vastas neile: \"Ma juba ütlesin teile, aga te ei võtnud kuulda. Miks te jälle tahate kuulda? Või tahate teiegi hakata Tema jüngreiks?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 28, 'Siis nad hakkasid teda sõimama ja ütlesid: \"Sina oled Tema jünger, meie aga oleme Moosese jüngrid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 29, 'Me teame, et Jumal on rääkinud Moosesega, aga kust See Inimene on, seda me ei tea!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 30, 'Mees kostis ning ütles neile: \"See ongi imelik, et te ei tea, kust Ta on, ja Tema avas mu silmad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 31, 'Me ju teame, et Jumal ei kuule patuseid, vaid kui keegi on jumalakartlik ja teeb Tema tahtmist, siis seda Ta kuuleb.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 32, 'Maailma algusest ei ole kuuldud, et keegi on avanud sündinud pimeda silmad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 33, 'Kui Ta ei oleks Jumalast, siis Ta ei võiks midagi teha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 34, 'Nad kostsid ning ütlesid talle: \"Sina oled koguni pattudes sündinud ja sina õpetad meid?\" Ja nad tõukasid ta välja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 35, 'Jeesus sai kuulda, et nad olid ta välja tõuganud; ja kui Ta tema leidis, ütles Ta: \"Kas sa usud Inimese Pojasse?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 36, 'Tema vastas ja ütles: \"Issand, Kes See on, et võiksin uskuda Temasse?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 37, 'Jeesus ütles temale: \"Sa oled ju Teda näinud ja See on Tema, Kes sinuga räägib!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 38, 'Aga tema ütles: \"Issand, ma usun!\" Ja ta kummardas Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 39, 'Ja Jeesus ütles: \"Mina olen tulnud kohtu mõistmiseks sellesse maailma, et need, kes ei näe, saaksid nägijaks, ja kes näevad, saaksid pimedaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 40, 'Ja mõned variseridest, kes olid Tema juures, kuulsid seda ja ütlesid: \"Kas meiegi oleme pimedad?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 41, 'Jeesus ütles neile: \"Kui te oleksite pimedad, ei oleks teil pattu; aga et te nüüd ütlete: Me näeme! siis jääb teie patt teile!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 1, 'Tõesti, tõesti Ma ütlen teile: kes ei lähe uksest sisse lambatarasse, vaid astub sisse mujalt, see on varas ja röövel!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 2, 'Teised ütlesid: \"Need ei ole seestunu sõnad; ega kuri vaim või avada pimedate silmi?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 3, 'sellele avab uksehoidja, ja lambad kuulevad ta häält, ja ta kutsub omi lambaid nimepidi ja viib nad välja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 4, 'Ja kui ta oma lambad on välja ajanud, käib ta nende ees ja lambad järgivad teda, sest nad tunnevad tema häält.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 5, 'Aga võõrast nad ei järgi, vaid põgenevad tema eest, sest nad ei tunne vööraste häält!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 6, 'Selle võrduim Jeesus ütles neile; nemad aga ei saanud aru, mis see on, mida Ta neile rääkis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 7, 'Sellepärast ütles Jeesus taas: \"Tõesti, tõesti Ma ütlen teile, et Mina olen lammaste üks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 8, 'Kõik, kes on tulnud enne Mind, on vargad ja röövlid; lambad aga ei ole neid kuulnud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 9, 'Mina olen uks. Kui keegi Minu kaudu sisse läheb, siis ta saab õndsaks ja käib sisse ja välja ja leiab karjamaad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 10, 'Varas ei tule muu pärast kui varastama ja tapma ning hukkama. Mina olen tulnud, et neil oleks elu ja kõike ülirohkesti!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 11, 'Mina olen hea karjane. Hea karjane jätab oma elu lammaste eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 12, 'Palgaline ja kes ei ole karjane, kelle omad lambad ei ole, näeb hundi tulevat ja jätab lambad maha ja põgeneb - ja hunt kisub neid ja ajab nad laiali.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 13, '- Ta põgeneb, sest ta on palgaline ega hooli lammastest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 14, 'Mina olen hea karjane ja tunnen Omi ja Minu Omad tunnevad Mind,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 15, 'nõnda nagu Isa tunneb Mind ja Mina tunnen Isa, ja Ma jätan Oma elu lammaste eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 16, 'Ja Mul on veel teisi lambaid, kes ei ole sellest tarast; needki ma pean tooma siia, ja nad kuulevad Minu häält, ja siis on üks kari ja üks Karjane.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 17, 'Sellepärast Isa armastab Mind, et Ma jätan Oma elu, et seda jälle võtta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 18, 'Ükski ei võta seda Minult, vaid Ma jätan selle Iseenesest. Mul on meelevald seda jätta ja Mul on meelevald seda jälle võtta. Selle käsusõna Ma olen saanud Oma Isalt!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 19, 'Siis tekkis taas vaidlus juutide keskel nende sõnade pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 20, 'Ja paljud nende seast ütlesid: \"Temas on kuri vaim ja Ta jampsib! Miks te Teda kuulate?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 21, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 22, 'Siis tulid templi uuendamise pühad Jeruusalemas. 0li talv.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 23, 'Ja Jeesus köndis pühakojas Saalomoni võlvitud hoones.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 24, 'Siis juudid asusid Ta ümber ja ütlesid Temale: \"Kaua sa pead meie hinge kahevahel? Oled Sa Kristus, siis ütle meile seda lausa!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 25, 'Jeesus kostis neile: \"Ma olen teile juba ütelnud aga te ei usu! Teod, mis Ma teen Oma Isa nimel, need tunnistavad Minust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 26, 'Teie aga ei usu, sest teie ei ole Minu lammaste hulgast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 27, 'Minu lambad kuulevad Minu häält ja Mina tunnen neid ja nemad järgivad Mind.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 28, 'Ja mina annan neile igavese elu ja nemad ei saa iialgi hukka, ja ükski ei kisu neid minu käest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 29, 'Minu Isa, Kes nad mulle on annud, on suurem kui kõik; ja ükski ei saa neid Minu Isa Käest ära kiskuda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 30, 'Mina ja Isa oleme üks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 31, 'Siis võtsid juudid jälle maast kive, et Teda nendega visata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 32, 'Jeesus kostis neile: \"Palju häid tegusid Ma olen teile näidanud Oma Isast; missuguse teo pärast te tahate Mind kividega visata?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 33, 'Juudid vastasid Temale: \"Hea teo pärast me ei viska Sind kividega, vaid Jumala pilkamise pärast ja et Sina, Kes oled inimene, pead Ennast Jumalaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 34, 'Jeesus vastas neile: \"Eks teie käsuõpetuses ole kirjutatud: Mina olen ütelnud: Teie olete jumalad!?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 35, 'Kui Ta nimetab jumalaiks neid, kellede kätte sai Jumala sõna - ja Kiri ei või jääda valeks -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 36, 'kuidas te siis ütlete Sellele, Keda Isa on pühitsenud ja läkitanud maailma: Sina pilkad Jumalat! sellepärast et Ma ütlesin: Mina olen Jumala Poeg?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 37, 'Kui Ma ei tee Oma Isa tegusid, siis ärge Mind uskuge;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 38, 'aga kui Ma teen, siis uskuge neid tegusid, kui te ka Mind ei usu, et te tunneksite ja saaksite aru, et Isa on Minus ja Mina olen Isas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 39, 'Siis nad püüdsid taas Teda kinni võtta aga Ta pääsis nende käest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 40, 'Ja Tema läks jälle teisele poole Jordanit, sinna paika, kus Johannes esmalt oli olnud ristimas; ja Ta viibis seal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 41, 'Ja paljud tulid Tema juure ja ütlesid: \"Johannes ei teinud küll ühtki tunnustähte; aga kõik, mis Johannes Sellest Mehest on ütelnud, on tõsi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 42, 'Ja seal uskusid paljud Temasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 1, 'Aga keegi Laatsarus Betaaniast, Maarja ja tema õe Marta alevist, oli haige.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 2, 'Maarja aga oli see, kes Issandat oli kalli salviga võidnud ja Tema jalgu oma juustega kuivatanud. Tema vend Laatsarus oligi haigestunud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 3, 'Siis läkitasid õed Tema juure ütlema: \"Issand, vaata, see, keda Sa armastad, on haige!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 4, 'Kui Jeesus seda kuulis, ütles Ta: \"See haigus ei ole surmaks, vaid Jumala austuseks, et Jumala Poega selle läbi austataks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 5, 'Aga Jeesus armastas Martat ja tema õde ja Laatsarust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 6, 'Kui ta nüüd kuulis tema haige olevat, siis Ta viibis veel kaks päeva seal paigas, kus Ta oli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 7, 'Pärast seda ütles Ta jüngritele: \"Lähme jälle Juudamaale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 8, 'Jüngrid ütlesid Temale: \"Rabi, äsja püüdsid juudid Sind kividega visata ja Sa lähed jälle sinna?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 9, 'Jeesus kostis: \"Eks kaksteistkümmend tundi ole päevas? Kui keegi kõnnib päeval, siis ta ei komista, sest ta näeb selle maailma valgust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 10, 'Aga kui keegi kõnnib öösel siis ta komistab, sest temas ei ole valjust!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 11, 'Seda Ta rääkis. Ja pärast seda Ta ütles neile: \"Laatsarus, meie sõber, magab aga Ma lähen teda unest äratama!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 12, 'Siis ütlesid Tema jüngrid: \"Issand, kui ta magab, siis ta saab terveks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 13, 'Aga Jeesus rääkis ta surmast; nemad aga mõtlesid, et Ta räägib une magamisest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 14, 'Siis Jeesus ütles neile lausa: \"Laatsarus on surnud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 15, 'ja Ma olen rõõmus teie pärast, et Ma ei olnud seal, et te usuksite. Aga läki tema juure!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 16, 'Siis ütles Toomas, keda hüütakse Kaksikuks, kaasjüngritele: \"Lähme ka meie, et ühes Temaga surra!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 17, 'Kui nüüd Jeesus tuli, leidis Ta, et Laatsarus oli juba neli päeva hauas olnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 18, 'Aga Betaania oli Jeruusalema lähedal, umbes viisteistkümmend vagu maad sellest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 19, 'Ja palju juute oli tulnud Marta ja Maarja juure neid trööstima nende venna pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 20, 'Kui nüüd Marta kuulis, et Jeesus on tulemas, läks ta Temale vastu; aga Maarja jäi koju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 21, 'Siis Marta ütles Jeesusele: \"Issand, kui Sa oleksid siin olnud, mu vend ei oleks surnud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 22, 'Aga nüüdki ma tean, et Jumal Sulle annab, mis Sa iganes Jumalalt palud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 23, 'Jeesus ütles talle: \"Sinu vend tõuseb üles!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 24, 'Marta ütles Talle: \"Ma tean, et ta üles tõuseb ülestõusmises viimsel päeval!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 25, 'Jeesus ütles temale: \"Mina olen ülestõusmine ja elu; kes Minusse usub, see elab, ehk ta küll sureb!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 26, 'Ja igaüks, kes elab ja Minusse usub, see ei sure igavesti. Kas sa usud seda?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 27, 'Ta ütleb Temale: \"Jah, Issand, mul on see usk, et Sina oled Kristus Jumala Poeg, Kes maailma on tulemas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 28, 'Ja kui ta seda oli ütelnud, läks ta ja kutsus oma õe Maarja salaja ning ütles: \"Õpetaja on siin ja kutsub sind!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 29, 'Kui õde seda kuulis, tõusis ta sedamaid ja läks Tema juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 30, 'Sest Jeesus ei olnud veel jõudnud alevisse, vaid oli veel seal paigas, kus Marta Teda oli kohanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 31, 'Kui nüüd juudid, kes olid Maarja juures toas teda trööstimas, nägid, et Maarja rutates tõusis ja väljus, järgisid nad teda mõteldes, et ta läheb haua juure nutma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 32, 'Kui siis Maarja jõudis sinna, kus Jeesus oli, ja Teda nägi, heitis ta maha Tema jalgade ette ning ütles Talle: \"Issand, oleksid Sa siin olnud, mu vend ei oleks surnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 33, 'Kui nüüd Jeesus nägi teda nutvat ja juute, kes temaga olid tulnud, ka nutvat, ärritus Ta vaimus ja võpatas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 34, 'Ja Ta ütles: \"Kuhu te olete ta pannud?\" Nad ütlesid Temale: \"Issand, tule ja vaata!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 35, 'Jeesus nuttis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 36, 'Siis ütlesid Juudid: \"Vaata, kuidas Ta teda armastas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 37, 'Aga mõned nende seast ütlesid: \"Kas Tema, kes avas pimeda silmad, ei võinud teha, et ka see ei oleks surnud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 38, 'Siis Jeesus ärritus taas Iseeneses ning tuli haua juure. See oli koobas ja selle avausel lasus kivi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 39, 'Jeesus ütleb: \"Tõstke kivi ära!\" Siis ütleb surnu õde Marta Temale: \"Issand, ta lehkab juba, sest on juba neljas päev!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 40, 'Jeesus ütleb temale: \"Eks Ma ütelnud sulle: kui sa usuksid, siis sa näha Jumala au?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 41, 'Siis nad tõstsid kivi ära. Aga Jeesus tõstis Oma silmad ütles: \"Ma tänan Sind, Isa, et sa oled kuulnud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 42, 'Mina ju teadsin, et Sa Mind ikka kuuled; kuid rahva pärast, kes siin ümber seisab, Ma räägin, et nad usuksid, et Sina Mind oled läkitanud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 43, 'Ja kui Ta seda oli ütelnud, hüüdis Ta suure häälega: \"Laatsarus, tule välja!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 44, 'Ja surnu tuli välja jalust ja käsist mähkmetega mähitud ja nägu higirätikusse mässitud. Jeesus ütleb neile \"Päästke ta lahti ja laske ta minna!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 45, 'Siis palju juute, kes olid tulnud Maarja juure ja olid näinud, mis Jeesus oli teinud, uskusid Temasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 46, 'Aga mõned neist läksid variseride juure ja ütlesid neile, mis Jeesus oli teinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 47, 'Siis ülempreestrid ja variserid kogusid kokku Suurkohtu ja ütlesid: \"Mida me peame tegema? Sest See Inimene teeb palju tunnustähti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 48, 'Kui me Ta nõnda jätame, usuvad kõik Temasse; siis tulevad roomlased ja võtavad ära meie paiga ja rahva!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 49, 'Aga üks nende seast, Kaifas, kes oli sel aastal ülempreester, ütles neile: \"Te ei tea midagi');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 50, 'ega sellele, et teile on parem, et üks inimene sureb rahva eest kui et kogu rahvas hukkub!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 51, 'Ent seda ta ei ütelnud iseenesest, vaid olles sel aastal ülempreester, kuulutas ta prohveti viisil, et Jeesus pidi surema rahva eest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 52, 'ja mitte ainult selle rahva eest, vaid et Ta ka hajuvil elavad Jumala lapsed koguks ühtekokku.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 53, 'Sellest päevast alates nad pidasid nüüd üheskoos nõu Teda ära tapma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 54, 'Siis ei käinud Jeesus enam avalikult juutide seas, vaid läks sealt ära kõrvelähedasse kohta, Efraimi-nimelisse linna; ja Ta viibis seal Oma jüngritega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 55, 'Aga juutide paasapühad olid ligi, ja paljud läksid sealt maalt enne paasapühi Jeruusalemma endid puhastama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 56, 'Siis nad otsisid Jeesust ja rääkisid üksteisega pühakojas seistes: \"Mis te arvate, kas Ta ei tule pühiks?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 57, 'Ülempreestrid ja variserid olid aga annud käsud, et kui keegi peaks teadma, kus Ta on, see annaks seda teada, et nad Ta kinni võtaksid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 1, 'Kuus päeva enne paasapüha tuli Jeesus Betaaniasse, kus elas Laatsarus, kelle Jeesus oli surnuis üles äratanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 2, 'Siis nad tegid seal Temale söömaaja ja Marta ümmardas. Ja Laatsarus oli üks neist, kes ühes Temaga lauas istusid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 3, 'Siis võttis Maarja naela selget, väga kallist nardisalvi ning võidis Jeesuse jalgu ja kuivatas Ta jalgu oma juustega. Ja maja sai täis salvi lõhna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 4, 'Aga üks Tema jüngritest, Juudas Iskariot, kes pärast Tema ära andis, ütles:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 5, '\"Mispärast ei ole see salv ära müüdud kolmesaja teenari eest ja raha antud vaestele?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 6, 'Aga seda ta ei ütelnud, et ta vaestest hoolis, vaid et ta oli varas ja ta käes oli kukkur, ja ta kõrvaldas, mis sisse pandi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 7, 'Siis ütles Jeesus: \"Jäta ta rahule, et ta seda saaks hoida Minu matuse päevaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 8, 'Sest vaeseid on ikka teie juures, aga Mind ei ole teil ikka!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 9, 'Siis sai suur hulk juute teada, et Tema on seal, ja nad ei tulnud sinna mitte ainult Jeesuse pärast, vaid et ka näha saada Laatsarust, kelle Ta oli surnuist üles äratanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 10, 'Aga ülempreestrid pidasid nõu Laatsarusegi ära tappa,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 11, 'sest tema pärast läks sinna palju juute ja uskus Jeesusesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 12, 'Järgmisel päeval, kui palju rahvast, kes oli tulnud pühi pidama, kuulis, et Jeesus tuleb Jeruusalemma,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 13, 'võtsid nad palmipuude oksi ja läksid välja Temale vastu ning hüüdsid: \"Hoosianna, õnnistatud olgu, Kes tuleb Issanda nimel, Iisraeli kuningas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 14, 'Aga Jeesus leidis noore eesli ja istus tema selga, nõnda nagu on kirjutatud:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 15, '\"Ära karda, Siioni tütar! Vaata, sinu kuningas tuleb ning istub eeslisälu seljas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 16, 'Sellest ei saanud jüngrid esialgu aru; aga kui Jeesus oli austatud, siis meenus neile, et see oli kirjutatud Tema kohta ja et nad Temale seda olid teinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 17, 'Nii andis Temast tunnistust rahvas, kes oli Tema juures, kui ta Laatsaruse välja hüüdis hauast ja tema üles äratas surnuist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 18, 'Sellepärast ka rahvas läks Temale vastu, sest nad kuulsid Teda selle tunnustähe teinud olevat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 19, 'Siis variserid rääkisid isekeskis: \"Eks te näe, et te ei saa midagi teha? Vaata, maailm jookseb Tema järele!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 20, 'Aga nende seas, kes olid tulnud pühiks palvetama, leidusid mõned kreeklased.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 21, 'Need tulid nüüd Filippuse juure, kes oli Betsaidast, Galilea linnast, ja palusid teda, üteldes: \"Isand, me tahame Jeesust näha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 22, 'Filippus tuleb ja ütleb seda Andreasele, ja Andreas ja Filippus tulevad ning ütlevad seda Jeesusele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 23, 'Aga Jeesus vastab neile ning ütleb: \"Tund on tulnud, et Inimese Poega austataks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 24, 'Tõesti, tõesti Ma ütlen teile: Kui nisuiva ei kuku mullasse ega sure, jääb ta üksi; aga kui ta sureb, siis ta kannab palju vilja!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 25, 'Kes oma elu armastab, see kaotab selle, ja kes oma elu vihkab siin maailmas, see hoiab selle igaveseks eluks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 26, 'ütlema? Isa, päästa Mind sellest tunnist! Aga sellepärast Ma olen tulnud sellesse tundi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 27, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 28, 'Isa, austa Oma nime!\" Siis tuli hääl taevast: \"Mina olen juba austanud ja austan veel!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 29, 'Siis rahvahulk, kes seal seisis ja seda kuulis, ütles pikse olevat müristanud. Aga teised ütlesid: \"Ingel rääkis Temaga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 30, 'Jeesus kostis ning ütles: \"See hääl ei sündinud Minu pärast, vaid teie pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 31, 'Nüüd käib kohus üle selle maailma; nüüd tõugatakse välja selle maailma vürst.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 32, 'Ja Mina tahan, kui Mind maast üles tõstetakse, kõik tõmmata Enese juure!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 33, 'Aga seda Ta ütles tähendades, mis surma Ta pidi surema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 34, 'Siis vastas rahvas Temale: \"Me oleme kuulnud käsuõpetusest, et Kristus jääb igavesti. Kuidas siis Sina ütled, et Inimese Poeg ülendatakse? Kes on See Inimese Poeg?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 35, 'Jeesus ütles nüüd neile: \"Valgus on veel üürikeseks ajaks teie keskel; käige niikaua kui teil valgust on, et pimedus teid ei tabaks; sest kes käib pimeduses, ei tea, kuhu ja läheb.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 36, 'Uskuge valgusesse, niikaua kui teil valgus on, et te saaksite valguse lapsiks!\" Seda rääkis Jeesus ja läks ning, peitis Enese ära nende eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 37, 'Ja ehk Ta küll palju tunnustähti oli teinud nende nähes, ei uskunud nad siiski Temasse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 38, 'et läheks täide prohvet Jesaja sõna, mis ta ütles: \"Issand, kes usub meie kuulutust ja kellele on ilmutatud Issanda käsivars?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 39, 'Sellepärast nad ei võinud uskuda, et Jesaja oli veel ütelnud:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 40, '\"Tema on teinud nende silmad pimedaks ja nende südamed kõvaks, et nad silmadega ei näeks ja südamega ei mõistaks ega pöörduks, et Mina neid parandaksin!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 41, 'Seda ütles Jesaja, kui ta nägi Jeesuse auhiilgust ja rääkis Temast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 42, 'Siiski uskus ka palju ülemaid Temasse; kuid variseride pärast nad ei tunnistatud seda, et neid ei lükataks kogudusest välja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 43, 'Sest nad armastasid austust inimeselt rohkem kui austust Jumalalt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 44, 'Aga Jeesus hüüdis ning ütles: \"Kes Minusse usub, see ei usu Minusse, vaid Sellesse, Kes Mind on läkitanud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 45, 'Ja kes näeb Mind, see näeb Seda, Kes Mind on läkitanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 46, 'Mina olen tulnud valguseks maailma, et ükski, kes usub Minusse, ei jääks pimedusse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 47, 'Ja kui keegi kuuleb Minu sõnu ega pea neid, selle üle ei mõista Mina kohut; sest Mina ei ole tulnud kohut mõistma maailma üle, vaid maailma õndsaks tegema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 48, 'Kes Mind põlgab ega võta vastu Minu sõna, sellel oma kohtumõistja. Sõna, mis Ma olen rääkinud, see mõistab tema üle kohut viimsel päeval.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 49, 'Sest Ma ei ole rääkinud Iseenesest, vaid Isa, Kes Mind on läkitanud, on Mulle annud kasu, mida Ma pean rääkima.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 50, 'Ja Ma tean, et Tema käsk on igavene elu. Mida Ma nüüd räägin, seda räägin nõnda, kuidas Isa mulle on ütelnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 1, 'Aga enne paasapühi, kui Jeesus teadis tunni olevat tulnud, et Ta siit maailmast läheb Isa juure, armastas Ta neid otsani, nõnda Ta oli armastanud Omi, kes olid maailmas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 2, 'Ja õhtusöömaajal olles, kui kurat juba oli Juuda, Siimona poja Iskarioti südamesse pannud, et ta Tema ära annaks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 3, 'ja kui Jeesus teadis et Isa kõik Tema kätte oli annud ja et Tema oli lähtunud Jumalast Ja läheb Jumala juure,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 4, 'tõuseb Ta üles õhtusöömaajalt ja võtab Oma kuue seljast ja võtab rätiku ja vöötub sellega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 5, 'Pärast seda Ta valab vett vaagnasse ja hakkab pesema jüngrite jalgu ja kuivatama rätikuga, millega Ta oli vöötatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 6, 'Siis Ta tuleb Siimon Peetruse juure, ja see ütleb Temale: \"Issand, kas Sina mu jalg pesed?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 7, 'Jeesus vastas ütles temale: \"Mida Mina teen, seda sina nüüd ei tea, aga pärast seda sa pead teada saama!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 8, 'Peetrus ütleb Temale: \"Eladeski ei või Sina minu jalgu pesta!\" Jeesus vastas temale: \"Kui Ma sind ei pese, siis ei ole sul osa Minuga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 9, 'Siimon Peetrus ütleb Temale: \"Issand, mitte üksnes minu jalgu, vaid ka käed ja pea!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 10, 'Jeesus ütleb temale: \"Kes on pestud, sellel ei ole vaja muud kui jalgu pesta, sest ta on üldse puhas; ja teie olete puhtad, kuid mitte kõik!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 11, 'Sest Ta teadis, kes Tema ära annab, sellepärast Ta ütles: \"Teie ei ole kõik puhtad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 12, 'Kui Ta nüüd nende jalad on pesnud ja Oma kuue oli võtnud, istus Ta jälle maha ja ütles neile: \"Kas teate, mis Ma teile olen teinud?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 13, 'Te hüüate Mind Õpetajaks ja Issandaks ja ütlete õigesti, sest Mina olen See.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 14, 'Kui nüüd Mina, Isaand ja Õpetaja, teie jalad olen pesnud, siis peate teiegi üksteise jalgu pesema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 15, 'Mina teile olen teinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 16, 'Tõesti, tõesti Ma ütlen teile: Ei ole ori suurem kui tema isand ega käskjalg suurem kui tema läkitaja!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 17, 'Kui te seda teate; õndsad olete, kui te seda teete!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 18, 'Ma ei ütle seda teie kõikide kohta; Ma tean, kelled Ma olen valinud. Aga Ma ütlen seda, et Kiri täide läheks: Kes Minu leiba sööb, on tõstnud oma kanna Minu vastu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 19, 'Nüüd ütlen Ma teile seda, enne kui see sünnib, et kui see sünnib, te usuksite, et Mina See olen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 20, 'Tõesti, tõesti Ma ütlen teile, et kes vastu võtab selle, kelle Ma läkitan, see võtab Mind vastu; aga kes Mind vastu võtab, see võtab vastu Tema, kes Mind on läkitanud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 21, 'Kui Jeesus seda oli ütelnud, võpatas Ta oma vaimus ja tunnistas ning ütles: \"Tõesti, tõesti Ma ütlen teile, et üks teie seast annab Mind ära!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 22, 'Siis vaatasid jüngrid üksteisele otsa ja olid kahevahel, kellest Ta räägib.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 23, 'Üks Tema jüngritest, see, keda Jeesus armastas, oli lauas istumas Jeesuse rinna najal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 24, 'Selle poole nüüd Siimon Peetrus noogutas peaga ning ütles temale: \"Ütle, kes see on, kellest Ta räägib?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 25, 'See laskus siis Jeesuse rinnale ja ütles Temale: Issand, kes see on?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 26, 'Jeesus vastas: \"See on see, kellele Mina kastutan ja annan selle palukese!\" Ja Tema kastis palukese ning võttis ja andis selle Juudale, Siimona pojale Iskariotile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 27, 'Ja palukese järele läks saatan temasse. Siis Jeesus ütles temale: \"Mis sa teed, seda tee kähku!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 28, 'Aga sellest ei saanud aru ükski neist, kes lauas istusid, miks Ta seda temale ütles.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 29, 'Sest mõned mõtlesid, et Jeesus, kuna Juuda käes seisis kukkur, temale ütleb: \"0sta, mis meile pühiks vaja on!\" Või et ta midagi vaestele annaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 30, 'Kui ta nüüd palukese oli võtnud, läks ta sedamaid välja. Aga oli öö.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 31, 'Kui ta nüüd oli väljunud, ütles Jeesus: \"Nüüd on Inimese Poeg austatud ja Jumal on austatud Temas!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 32, '0ns Jumal Temas austatud, siis austab Teda ka Jumal Eneses ja austab Teda varsti!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 33, 'Lapsukesed, Ma olen veel üürikese aja teie juures; te hakkate Mind otsima. Ja nagu Ma juutidele ütlesin: Kuhu Mina lähen, sinna teie ei või tulla! nõnda Ma ütlen nüüd ka teile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 34, 'Uue käsusõna Ma annan teile, et te üksteist peate armastama, nõnda nagu Mina teid olen armastanud; et teiegi üksteist armastaksite!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 35, 'Sellest tunnevad kõik, et teie olete Minu jüngrid, kui teil on armastus isekeskis!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 36, 'Siimon Peetrus ütleb Temale: \"Issand, kuhu sa lähed?\" Jeesus vastas: \"Kuhu Ma lähen, sinna sa ei võid Mind järgida, aga edaspidi sa järgid Mind!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 37, 'Peetrus ütleb Temale: \"Issand, miks ma nüüd ei või Sind järgida? Ma annan oma elu Sinu eest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 38, 'Jeesus vastab temale: \"Annad sina oma elu Minu eest? Tõesti, tõesti Ma ütlen sulle, et kukk ei laula mitte enne, kui sa Mind juba kolm korda oled salanud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 1, 'Teie süda ärgu ehmugu! Uskuge Jumalasse ja uskuge Minusse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 2, 'Minu Isa majas on palju eluasemeid. Kui see nii ei oleks, kas Ma oleksin teile ütelnud: Ma lähen teile aset valmistama?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 3, 'Ja kui Ma olen läinud ja teile aseme valmistanud, tulen Ma jälle tagasi ja võtan teid Enese juure, et teiegi oleksite, kus Mina olen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 4, 'Ja kuhu Mina lähen, seda teed te teate!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 5, 'Toomas ütleb Temale: \"Issand, me ei tea, kuhu Sa lähed! Kuidas võime teada teed?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 6, 'Jeesus ütleb temale: \"Mina olen tee ja tõde ja elu, ükski ei saa Isa juure muidu kui Minu kaudu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 7, 'Kui te Mind oleksite ära tunnud, siis te tunneksite ka Minu Isa, ja sellest ajast te tunnete Teda ja olete Teda näinud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 8, 'Filippus ütleb Temale: \"Issand, näita meile Isa, siis me jääme rahule!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 9, 'Jeesus ütleb temale: \"Niikaua aega olen Ma teie juures ja sa ei ole Mind tunnud, Filippus? Kes Mind on näinud, see on näinud Isa. Kuidas sa ütled: Näita meile Isa!?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 10, 'Kas sa ei usu, et Mina olen Isas ja Isa on Minus? Neid sõnu, mida Ma räägin, ei räägi Ma Iseenesest, vaid Isa, Kes asub Minus, teeb Oma tegusid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 11, 'Uskuge Mind, et Mina olen Isas ja et Isa on Minus; aga kui mitte, siis uskuge nende tegude pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 12, 'Tõesti, tõesti Ma ütlen teile, et kes usub Minusse, see teeb ka neid tegusid, mida Mina teen, ja teeb veel suuremaid kui need on, sest Mina lähen Isa juure!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 13, 'Ja mida te iganes palute Minu nimel, seda Ma teen, et Isa austaks Pojas!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 14, 'Kui te midagi Minult palute Minu nimel, siis Ma teen seda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 15, 'Kui te Mind armastate, siis pidage Minu käsusõnu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 16, 'Ja Mina palun Isa, ja Tema annab teile Trööstija, et see teie juure jääks igavesti,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 17, 'tõe Vaimu, Keda maailm ei või vastu võtta, sellepärast et ta Teda ei näe ega tunne, aga teie tunnete Teda, sest Tema jääb teie juure ja tahab olla teie sees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 18, 'Ma ei jäta teid vaestekslasteks; Ma tulen teie juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 19, 'Veel on pisut aega, siis maailm ei näe Mind enam, aga teie näete Mind; sest Mina elan ja teie saate elama!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 20, 'Sel päeval te saate tunda, et Mina olen Oma Isas ja teie Minus ja Mina teis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 21, 'Kellel on minu käsusõnad ja kes neid peab, see on, kes Mind armastab; kes aga Mind armastab, seda armastab Minu Isa ja Ma tahan teda armastada ja Iseennast temale ilmutada!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 22, 'Temale ütleb Juudas, mitte Iskariot: \"Issand, millest see oleneb, et Sa meile tahad Ennast ilmutada, aga mitte maailmale?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 23, 'Jeesus vastas ning ütles temale: \"Kui keegi Mind armastab, küll ta peab Minu sõna, ja Minu Isa armastab Teda, ja Me tuleme tema juure\" ja teeme eluaseme tema juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 24, 'Kes Mind ei armasta, see ei pea Minu sõnu, ja sõna, mida te kuulete, ei ole Minu, vaid on Isa sõna, kes Mind on läkitanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 25, 'Seda olen Mina teile rääkinud teie juures viibides.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 26, 'Aga Trööstija, Püha Vaim, kelle Minu Isa läkitab Minu nimel, see õpetab teile kõik ja tuletab teile meele kõik, mis Mina teile olen ütelnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 27, 'Rahu Ma jätan teile; Oma rahu Ma annan teile; Mina ei anna teile nõnda nagu maailm annab. Teie süda ärgu ehmugu ja ärgu mingu araks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 28, 'Te kuulsite Mind teile ütlevat: Ma lähen ära ja tulen taas teie juurde. Kui te Mind armastaksite, siis te rõõmutseksite, et Ma lähen Isa juure; sest Minu Isa on suurem kui Mind!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 29, 'Ja nüüd Ma olen teile seda ütelnud, enne kui see sünnib, et te usuksite, kui see sünnib.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 30, 'Ma ei räägi enam palju teiega, sest selle maailma vürst tuleb ja ta ei saa Minust midagi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 31, 'Aga et maailm aru, et Ma armastan Isa ja teen nõnda, kuidas Isa mind on käskinud. Tõuske, mingem siit ära!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 1, '\"Mina olen tõeline viinapuu ja Minu Isa on viinamäe aednik.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 2, 'Iga oksa Minu küljes, mis ei kanna vilja, Tema kõrvaldab ja igaüht, mis kannab vilja, Tema puhastab, et see kannaks rohkem vilja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 3, 'Teie olete juba puhtad selle sõna pärast, mis Mina teile rääkinud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 4, 'Jääge Minusse ja Mina jään teisse. Nagu oks ei või vilja kanda iseenesest, kui ta ei jää viinapuu külge, nõnda ka teie, kui te ei jää Minusse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 5, 'Mina olen viinapuu, teie olete oksad; kes jääb Minusse ja Mina temasse, see kannab palju vilja; sest ilma Minuta ei või te midagi teha!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 6, 'Kui keegi ei jää Minusse, siis ta heidetakse välja nagu viinapuu oks ning kuivab ära. Ja nad kogutakse kokku ja heidetakse tulle ning põletatakse ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 7, 'Kui te jääte Minusse ja Minu sõnad jäävad teisse, siis paluge, mis te iganes tahate, ja see sünnib teile!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 8, 'Selles on Minu Isa austatud, et te kannate palju vilja ja osutute Minu jüngriteks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 9, 'Otsegu Minu Isa on armastanud Mind, nõnda olen ka Mina teid armastanud; jääge Minu armastusse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 10, 'Kui te peate Minu käsusõnu, siis te jääte Minu armastusse, nõnda nagu Mina olen pidanud Oma Isa käsusõnu ja jään Tema armasusse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 11, 'Seda Ma olen teile rääkinud, et Minu rõõm oleks teie sees ja teie rõõm saaks täielikuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 12, 'See on Minu käsusõna, et te armastaksite üksteist nõnda nagu Mina teid olen armastanud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 13, 'Suuremat armastust ei ole kellelgi kui see, et ta jätab oma elu oma sõprade eest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 14, 'Teie olete Mu sõbrad, kui te teete, mida Mina teid käsin.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 15, 'Ma ei ütle teid enam orjad olevat, sest ori ei tea, mida ta isand teeb. Vaid Ma olen teid nimetanud sõpradeks, sest Ma olen teile teada annud kõik, mis Ma Oma Isalt olen kuulnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 16, 'Mitte teie ei ole Mind valinud, vaid Mina olen teid valinud ja olen teid seadnud, et te läheksite ja kannaksite vilja ja et teie vili jääks ja Isa teile annaks, mida te iganes palute Minu nimel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 17, 'Seda Ma käsin teid, et te armastaksite üksteist!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 18, 'Kui maailm teid vihkab, siis teadke, et ta Mind on enne teid vihanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 19, 'Kui te oleksite maailmast, siis maailm armastaks oma. Aga et te ei ole maailmast, vaid Mina olen teid ära valinud maailmast, sellepärast vihkab teid maailm!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 20, 'Pidage meeles seda sõna, mis Ma teile olen ütelnud: Ei ole ori suurem oma isandast! On nemad Mind taga kiusanud, siis nad kiusavad teidki taga. On nad pidanud Minu sõna, siis nad peavad teiegi sõna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 21, 'Aga seda kõike nad teevad teile Minu nime pärast, sest nad ei tunne Teda, Kes Mind on läkitanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 22, 'Kui Ma ei oleks tulnud ega oleks neile rääkinud, siis ei oleks neil pattu. Aga nüüd ei ole neil oma pattu millegagi vabandada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 23, 'Kes vihkab Mind, see vihkab Mu Isa!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 24, 'Kui Ma ei oleks nende keskel teinud neid tegusid, mida ükski muu ei ole teinud, ei oleks neil pattu, aga nüüd on nad näinud ja vihanud niihästi Mind kui Mu Isa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 25, 'Aga see on sündinud, et läheks täide sõna, mis on kirjutatud nende käsuõpetuses: Nad on Mind vihanud ilmaasjata!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 26, 'Aga kui Trööstija tuleb, kelle Mina teile läkitan Isalt, tõe Vaim, Kes lähtub Isast, See tunnistab Minust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 27, 'teiegi tunnistate, sest te olete algusest peale olnud ühes Minuga!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 1, 'Seda Ma olen teile rääkinud, et te ei taganeks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 2, 'Nad heidavad teid kogudusest välja ja tuleb aeg, mil igaüks, kes teid tapab, arvab au andvat Jumalale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 3, 'Ja seda nad teevad, sellepärast et nad ei tunne Isa ega Mind.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 4, 'Aga seda Ma olen teile rääkinud, et kui tuleb see aeg, te mõtleksite sellele, et Mina teile seda olen ütelnud. Ent seda Ma ei ole teile ütelnud algusest, sest Ma olin ühes teiega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 5, 'Aga nüüd Ma lähen Selle juure, Kes Mind on läkitanud, ja keegi teie seast ei küsi Minult: Kuhu Sa lähed?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 6, 'Ainult et Mina seda teile olen ütelnud, siis on kurbus täitnud teie südame.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 7, 'Aga Mina ütlen teile tõtt: See tuleb teile heaks, et Mina lähen ära. Sest kui Ma ei läheks, ei tuleks Trööstija teie juure. Aga kui Ma lähen, siis Ma läkitan Tema teie juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 8, 'Ja kui Ta tuleb, siis Ta toob maailmale selguse patu kohta ja õiguse kohta ja kohtu kohta:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 9, 'patu kohta, et nad ei usu Minusse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 10, 'õiguse kohta, et Ma lähen Isa juure ja te ei näe Mind enam;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 11, 'ja kohtu kohta, et selle maailma vürsti üle on kohus mõistetud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 12, 'Mul on teile veel palju ütlemist, aga te ei või nüüd seda kanda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 13, 'Aga kui Tema, tõe Vaim, tuleb, siis Ta juhatab teid kõigesse tõtte, sest Tema ei räägi Iseenesest, vaid mida Ta kuuleb, seda ta räägib ja tulevasi asju Ta kuulutab teile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 14, 'Tema austab Mind, sest Minu Omast Ta võtab ja kuulutab teile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 15, 'Kõik, mis Isal on, see on Minu päralt; sellepärast ma ütlesin, et Ta võtab Minu Omast ja kuulutab teile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 16, 'Üürikese aja pärast, siis te ei näe Mind, ja taas üürikese aja pärast, siis te näete Mind!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 17, 'Siis ütlesid mõned Tema jüngritest isekeskis: \"Mis see on, mis Ta ütleb meile: Üürikese aja pärast, siis te ei näe Mind, ja taas üürikese aja pärast, siis te näete Mind! ja: Mina lähen Isa juure!?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 18, 'Nimelt nad küsisid: \"Mis see on, mis Ta ütleb: üürikese aja pärast? Meie ei tea, mida Ta räägib.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 19, 'Siis Jeesus sai aru, et nad Temalt tahtsid küsida, ja ütles neile: \"Seda te küsitlete isekeskis, et Ma ütlesin: Üürikese aja pärast, siis te ei näe Mind, ja taas üürikese aja pärast te näete Mind!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 20, 'Tõesti, tõesti Ma ütlen teile, te nutate ja kaeblete, aga maailm on rõõmus! Teid kurvastatakse, aga teie kurvastus muutub rõõmuks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 21, 'Kui naine sünnitab, on ta murelik, sest tema hetk on tulnud; aga kui ta lapse on saanud, ei mõtle ta enam ahastusele rõõmu pärast, et inimene on sündinud maailma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 22, 'Ja teilgi on nüüd meel murelik, aga Ma tahan teid jälle näha ja teie süda rõõmustub, ja ükski ei võta teie rõõmu teilt ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 23, 'Ja sel päeval te ei küsi Minult midagi. Tõesti, tõesti Ma ütlen teile, et mida te iganes Isalt palute, seda Ta annab teile Minu nimel!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 24, 'Tänini ei ole te midagi palunud Minu nimel. Paluge, siis te saate, et teie rõõm võiks olla täielik.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 25, 'Seda Ma olen teile rääkinud võrdumitega; aga tuleb aeg, mil Ma teile enam ei räägi võrdumitega, vaid kuulutan Isast teile avalikult.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 26, 'Sel päeval te palute Minu nimel. Ja Ma ei ütle teile, et Ma palun Isa teie eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 27, 'Sest Isa Ise armastab teid, sellepärast et teie olete Mind armastanud ja uskunud, et Mina olen lähtunud Jumalast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 28, 'Ma olen lähtunud Isast ja tulnud maailma; taas Ma jätan maha maailma ja lähen Isa juure!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 29, 'Tema jüngrid ütlesid: \"Vaata, nüüd Sa ütled lausa ega räägi võrdumitega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 30, 'Nüüd me teame, et Sa tead kõik ja et Sul ei ole vaja, et keegi Sinult küsib. Sellest me usume, et Sa oled lähtunud Jumala juurest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 31, 'Jeesus vastas neile: \"Kas te nüüd usute?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 32, 'Vaata, tund tuleb ja on juba tulnud, et teid hajutatakse igaüks ise kohta ja te jätate Mind üksi! Ja Mina ei ole ometi mitte üksi, sest Isa on Minuga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 33, 'Seda Ma olen teil rääkinud, et teil oleks rahu Minus. Maailmas on teil ahastust, aga olge julged, Mina olen maailma ära võitnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 1, 'Seda rääkis Jeesus ja tõstis Oma silmad taeva poole ning ütles: \"Isa, tund on tulnud! Austa Oma Poega, et ka Poeg austaks Sind,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 2, 'nagu Sa oled Temale annud meelevalla kõige liha üle, et Ta annaks igavese elu kõigile, kelled Sina oled Temale annud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 3, 'Aga see on igavene elu, et nad tunneksid Sind, ainust tõelist Jumalat ja Jeesust Kristust, kelle Sina oled läkitanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 4, 'olen Sind austanud maa peal, Ma olen lõpetanud selle töö, mille Sa oled annud Mulle teha!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 5, 'Ja nüüd austa Mind Sina, Isa, Enese juures selle auga, mis Mul oli Sinu juures enne maailma olemasolu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 6, 'Mina olen Sinu nime ilmutanud inimestele, kelled Sa Mulle oled annud maailmast. Nemad olid Sinu Omad ja Sina andsid nad Mulle, ja nad on pidanud Sinu sõna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 7, 'Nüüd nad teavad, et kõik, mis Sina Mulle oled annud, on Sinult.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 8, 'Sest need sõnad, mis Sa andsid Mulle, olen Ma annud neile, ja nemad on vastu võtnud ja teavad, et Ma tõesti olen lähtunud Sinu juurest, ja nemad on uskunud, et Sina oled Mind läkitanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 9, 'Mina palun nende eest; maailma eest Ma ei palu, vaid nende eest, kelled Sina oled Mulle annud, sest nad on Sinu Omad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 10, 'Ja kõik, mis on Minu Oma, on Sinu, ja mis on Sinu Oma, on Minu, ja nendes Ma olen austatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 11, 'Ja Ma ei ole edaspidi enam maailmas, aga nemad on maailmaas, ja Mina tulen Sinu juure. Püha Isa, hoia neid Oma nimes, mille Sa oled mulle annud, et nad oleksid üks, nõnda nagu Meie!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 12, 'Kui Ma olin ühes nendega, hoidsin Ma neid Sinu nimes, mille Sa oled mulle annud; ja Ma hoidsin neid ning ükski neist ei ole kadunud, kui ainult kadumisepoeg, et Kiri täide läheks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 13, 'Aga nüüd Ma tulen Sinu juure ja räägin seda maailmas, et Minu rõõm neil oleks täieline neis enestes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 14, 'Mina olen neile annud Sinu sõna ja maailm vihkab neid; sest nad ei ole maailmast, nõnda nagu Mina ei ole maailmast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 15, 'Mina ei palu, et Sa võtaksid nad ära maailmast, vaid et Sa neid hoiaksid tigeda eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 16, 'Nad ei ole maailmast, nõnda nagu Minagi ei ole maailmast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 17, 'Pühitse neid tões: Sinu sõna on tõde!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 18, '0tsegu Sina oled Mind läkitanud maailma, nõnda Minagi olen nad läkitanud maailma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 19, 'Ja Mina pühitsen Iseennast nende eest, et nemadki oleksid pühitsetud tõe sees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 20, 'Aga Ma ei palu mitte üksnes nende eest, vaid ka nende eest, kes nende sõna kaudu usuvad Minusse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 21, 'et nemad kõik oleksid üks, nõnda nagu Sina, Isa, Minus ja Mina Sinus, et nemadki Meis oleksid ja maailm usuks, et Sa Mind oled läkitanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 22, 'Selle au, mille Sa andsid Mulle, olen Mina annud neile, et nad oleksid üks, nõnda nagu Meie oleme üks:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 23, 'Mina nendes ja Sina Minus, et nad täielikult saaksid üheks ja maailm tunneks, et Sina oled Mind läkitanud ja oled armastanud neid, nõnda nagu Sa Mind oled armastanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 24, 'Isa, Ma tahan, kus Mina olen, ka nemad oleksid Minu juures, kelled Sa Mulle oled annud; et nad näeksid Minu auhiilgust, mille Sa mulle oled annud, sest Sa oled Mind armastanud enne maailma rajamist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 25, 'Õige, Isa, maailm ei ole Sind tunnud; aga Mina tunnen Sind ja need on hakanud tundma, et Sina oled Mind läkitanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 26, 'Ja Ma olen neile annud teada Sinu nime ja annan seda teada selleks, et armastus, millega Sa Mind oled armastanud, oleks nendes ja Mina oleksin nendes!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 1, 'Kui Jeesus seda oli rääkinud, läks ta Oma jüngritega välja üle Kiidroni jõe. Seal oli aed, kuhu Ta läks Oma jüngritega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 2, 'Miks sa Minult küsid? Küsi nendelt, kes on kuulnud, mida Ma neile olen rääkinud. Vaata, need teavad, mis Ma olen rääkinud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 3, 'Juudas võttis nüüd enesega kaasa väesalga ning ülempreestritelt ja variseridelt sulaseid ja tuli sinna tulelontide, lampide ja relvadega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 4, 'Siis Jeesus, kuna Ta teadis kõik, mis Tema peale tuleb, läks välja ja ütles neile: \"Keda te otsite?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 5, 'Nad vastasid Temale: \"Jeesust Naatsaretlast!\" Tema ütles neile: \"Mina olen See!\" Ka Juudas, kes Tema ära andis, seisis nende juures.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 6, 'Kui Ta nüüd neile ütles: \"Mina olen See!\" taganesid nad ja langesid maha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 7, 'Siis Ta küsis taas neilt: \"Keda te otsite?\" Nemad ütlesid: \"Jeesust Naatsaretlast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 8, 'Jeesus vastas: \"Ma olen teile ütelnud, et Mina See olen! Kui te nüüd Mind otsite, siis laske need ära minna!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 9, 'et täide läheks sõna, mis Ta oli ütelnud: Mina ei ole kaotanud kedagi neist, kelled Sa Mulle oled, annud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 10, 'Siis Siimon Peetrus, kellel oli mõõk, tõmbas selle välja ja lõi ülempreestri sulast ning raius tema parema kõrva ära; sulase nimi aga oli Malkus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 11, 'Siis Jeesus ütles Peetrusele: \"Pista oma mõõk tuppe! Eks Mina pea jooma seda karikat, mille Isa Mulle on annud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 12, 'Aga väesalk ja ülempealik ja juutide sulased võtsid Jeesuse ja sidusid Ta kinni');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 13, 'ja viisid esiti Annase juure; sest see oli Kaifase äi, ja Kaifas oli tol aastal ülempreester.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 14, 'Kaifas aga oli see, kes juutidele nõu andes oli ütelnud: \"Parem on, et üks inimene sureb rahva eest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 15, 'Aga Siimon Peetrus järgis Jeesust ja üks teine jünger. See jünger oli tuttav ülempreestrile ja läks Jeesusega ühes ülempreestri õue.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 16, 'Ja Peetrus seisis väljas ukse ees. Siis läks teine jünger, kes oli tuttav ülempreestrile, välja ja rääkis uksehoidjaga ning viis Peetruse sisse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 17, 'Siis ütles uksehoidjatüdruk Peetrusele: \"Eks sinagi ole üks Selle Inimese jüngritest?\" Tema ütles: \"Mina ei ole!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 18, 'Aga teenrid ja sulased seisid ja olid teinud sütetule, sest oli külm, ja soojendasid endid. Ja Peetrus seisis nende juures ning soojendas ennast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 19, 'Ülempreester küsis nüüd Jeesuselt Tema jüngrite ja Tema õpetuse kohta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 20, 'Jeesus kostis temale: \"Mina olen avalikult rääkinud maailmale; Mina olen alati õpetanud koguduse- ja pühakojas, kuhu kõik juudid kokku tulevad, ja salaja Ma ei ole midagi rääkinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 21, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 22, 'Aga kui Ta seda oli ütelnud, lõi üks sulaseist, kes seisid seal juures, Jeesust kõrva ääre ning üttes: \"Kas Sa nõnda vastad ülempreestrile?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 23, 'Jeesus kostis temale: \"Kui Ma olen rääkinud pahasti, siis tõesta, et see on paha; aga kui Ma olen hästi rääkinud, miks sa Mind siis lööd?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 24, 'Siis Annas saatis Tema seotult ülempreester Kaifase juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 25, 'Aga Siimon Peetrus seisis ja soojendas ennast. Siis ütlesid nad temale: \"Eks sinagi ole Tema jüngrite seast?\" Tema salgas ja ütles: \"Mina ei ole!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 26, 'Üks ülempreestri teenreist, selle sugulane, kelle kõrva Peetrus oli ära raiunud, ütleb: \"Kas mina sind ei näinud ühes Temaga aias?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 27, 'Siis Peetrus salgas jälle, ja sedamaid laulis kukk.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 28, 'Siis nad viisid Jeesuse Kaifase juurest kohtukotta; aga see oli puhteajal. Ja nad ise ei läinud kohtukotta, et nad ei rüvetuks, vaid võiksid süüa paasatalle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 29, 'Siis läks Pilaatus välja nende juure ja ütles: \"Mis teil on kaebamist Selle Inimese peale?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 30, 'Nemad vastasid ning ütlesid temale: \"Kui see ei oleks kurjategija, me ei oleks annud Teda sinu kätte!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 31, 'Siis ütles Pilaatus neile \"Võtke teie Ta ja mõistke oma käsuõpetuse järele kohut Tema üle!\" Juudid ütlesid temale: \"Meil ei ole luba kedagi tappa!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 32, 'et täide läheks Jeesuse sõna, mis Ta oli ütelnud, kui Ta tähendas millist surma Ta sureb.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 33, 'Siis käis Pilaatus jälle kohtukotta ja kutsus Jeesuse ning ütles Temale: \"0led Sina Juutide Kuningas?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 34, 'Jeesus vastas: \"Räägid sa seda iseenesest või on sulle teised seda Minust ütelnud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 35, 'Pilaatus vastas: \"Egas ma juut ole! Sinu Oma rahvas ja ülempreestrid on Sind annud minu kätte. Mis Sa oled teinud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 36, 'Jeesus kostis: \"Minu Riik ei ole sellest maailmast; oleks Mu Riik sellest maailmast, küll Mu sulased oleksid võidelnud, et Mind ei oleks antud juutide kätte. Ent nüüd ei ole Mu Riik mitte siit!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 37, 'Siis ütles Pilaatus Temale: \"Siis oled Sina ometi Kuningas?\" Jeesus vastas: \"Jah, olen, sest Mina olen Kuningas!\" Ma olen selleks sündinud ja selleks tulnud maailma, et Ma tõele tunnistus annaksin. Kes tõe seest on, see kuuleb Minu häält!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 38, 'Pilaatus ütleb Temale: \"Mis on tõde?\" Kui ta seda oli ütelnud, läks ta uuesti välja juutide juure ja ütles neile: \"Mina ei leia Temast ühtki süüd!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 39, 'Aga teil on kombeks, et ma paasapühal lasen ühe teile vabaks. Kas tahate nüüd, et ma teile vabaks lasen Juutide Kuninga?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 40, 'Siis nad kisendasid jälle kõik ning ütlesid: \"Mitte Teda, vaid Barabas!\" Aga Barabas oli röövel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 1, 'Siis Pilaatus võttis Jeesuse ja laskis Teda rooskadega peksta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 2, 'Ja sõjamehed punusid kibuvitsust krooni ja panid selle Temale pähe ja riietasid Teda purpurkuuega');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 3, 'ja tulid Tema juure ja ütlesid: \"Tere, Juutide Kuningas!! Ja nad lõid Teda kõrva ääre.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 4, 'Siis Pilaatus läks taas välja ja ütles neile: \"Vaata, ma toon Ta teile välja, et te aru saaksite, et ma ei leia Temast ühtki süüd!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 5, 'Siis tuli Jeesus välja ja kandis kibuvitsakrooni ja purpurkuube. Ja Pilaatus ütles neile: \"Ennäe Inimest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 6, 'Kui nüüd ülempreestrid ja sulased Teda nägid, kisendasid nad ning ütlesid: \"Löö risti! Löö risti!\" Pilaatus ütles neile: \"Võtke teie Tema ja lööge Ta risti, sest mina ei leia Temast süüd!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 7, 'Juudid vastasid temale: \"Meil on käsuõpetus, ja kasu järele Ta peab surema, sest Ta on Enese teinud Jumala Pojaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 8, 'Kui nüüd Pilaatus seda sõna kuulis, kartis ta veel enam.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 9, 'Ja ta läks jälle kohtukotta ja ütleb Jeesusele: \"Kust Sa oled?\" Aga Jeesus ei annud temale vastust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 10, 'Siis ütleb Pilaatus Temale: \"Kas Sa ei räägi minuga? Eks Sa tea, et mul on meelevald Sind vabaks lasta ja meelevald Sind risti lüüa?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 11, 'Jeesus kostis: \"Sinul ei oleks mingit meelevalda Minu üle, kui see sulle ei oleks antud ülalt; sellepärast on sellel, kes Mind sinu kätte andis, suurem patt!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 12, 'Sellest alates püüdis Pilaatus Teda vabaks lasta. Aga juudid kisendasid ning ütlesid: \"Kui sa Selle vabaks lased, siis sa ei ole keisri sõber! Igaüks, kes iseenese teeb kuningaks, on keisri vastane!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 13, 'Kui nüüd Pilaatus neid sõnu kuulis, viis ta Jeesuse välja ja istus maha kohtujärjele sinna paika, mida hüütakse Kivipõrandaks, aga heebrea keeli Gabbataks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 14, 'Aga oli paasapüha valmistuspäev, arvata kuues tund. Ja tema ütles juutidele: \"Ennäe teie Kuningat!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 15, 'Aga nad kisendasid: \"Vii ära, vii ära, löö Ta risti!\" Pilaatus ütleb neile: \"Kas ma pean teie Kuninga risti lööma?\" Ülempreestrid vastasid: \"Meil ei ole kuningat, vaid on keiser!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 16, 'Siis ta andis Tema nende kätte risti lüüa. Ja nad võtsid Jeesuse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 17, 'Ja Tema kandis Ise Oma risti ning väljus nõndanimetatud Pealae asemele, mida heebrea keeli hüütakse Kolgataks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 18, 'Seal nad lõid Ta risti ja teised kaks Temaga, teise teisele poole, aga Jeesuse keskele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 19, 'Aga Pilaatus kirjutas ka pealkirja ja pani selle risti külge; ja sellele oli kirjutatud: \"Jeesus Naatsaretlane, Juutide Kuningas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 20, 'Seda pealkirja luges nüüd palju juute, sest paik, kus Jeesus risti löödi, oli linna ligi. Ja pealkiri oli kirjutatud heebrea, ladina ja kreeka keeli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 21, 'Siis ütlesid juutide ülempreestrid Pilaatusele: \"Ära kirjuta \"Juutide Kuningas\", vaid: \"Ta on ütelnud: Mina olen Juutide Kuningas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 22, 'Pilaatus kostis: \"Mis ma olen kirjutanud, olen ma kirjutanud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 23, 'Kui nüüd sõjamehed Jeesuse olid risti löönud, võtsid nad Tema riided ja tegid neli osa, igale sõjamehele ühe osa, ja kuue; kuub aga oli õmbluseta, ülemisest äärest alumiseni ühes tükis kootud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 24, 'Siis nad ütlesid üksteisele: \"Ärgem kiskugem seda lõhki, vaid heitkem liisku selle kohta, kellele see saab!\" - et läheks täide Kiri, mis ütleb: \"Nad on Mu riided isekeskis jaganud ja Minu kuue kohta liisku heitnud!\" Seda sõjamehed tegidki.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 25, 'Aga Jeesuse risti juures seisid Tema Ema ja Ta Ema õde Maarja, Kloopase naine, ja Maarja Magdaleena.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 26, 'Kui nüüd Jeesus nägi risti kõrval seisvat Oma Ema ja jüngrit, keda Ta armastas, ütleb Ta Emale: \"Naine, vaata, see on Su poeg!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 27, 'Pärast Ta ütleb jüngrile: \"Vaata, See on su Ema!\" Ja sestsamast tunnist võttis jünger Ta oma kotta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 28, 'Pärast seda ütleb Jeesus, teades, et kõik juba on lõpetatud, et Kiri täide läheks: \"Mul on janu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 29, 'Seal seisis astja täis äädikat. Siis nad pistsid äädikaga täidetud käsna iisopi otsa ja panid selle Tema suu ette.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 30, 'Kui nüüd Jeesus äädikat oli võtnud, ütles Ta: \"See on lõpetatud!\" Ja Ta nõrgutas pead ning heitis hinge.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 31, 'Aga et oli valmistuspäev ja et kehad ei jääks hingamispäevaks ristile - sest see hingamispäev oli suur - palusid juudid Pilaatust, et ristilöödute sääreluud murtaks ja nad maha võetaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 32, 'Siis tulid sõjamehed ja murdsid esimese sääreluud ja teise omad, kes ühes Temaga olid risti löödud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 33, 'Aga kui nad tulid Jeesuse juure ja nägid Ta juba surnud olevat, ei murdnud nad Tema sääreluid, 34, vaid üks sõjameestest pistis odaga Tema küljesse; ja kohe tuli välja verd ja vett.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 34, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 35, 'Ja see, kes seda nägi, on seda tunnistanud, ja tema tunnistus on tõsi, ja tema teab, et ta räägib tõtt, et teiegi usuksite.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 36, 'Sest see on sündinud, et Kiri täide läheks: \"Tema luid ärgu murtagu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 37, 'Ja taas ütleb teine Kiri: \"Nad saavad näha, Kellesse nad on pistnud!\" 38 Aga pärast seda Joosep Arimaatiast, kes oli Jeesuse jünger, kuid salaja, kartusest juutide eest, palus Pilaatuselt, ei ta tohiks maha võtta Jeesuse ihu. Ja Pilaatus andis temale loa. Siis ta tuli ja võttis Jeesuse ihu maha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 38, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 39, 'Nikodeemuski, kes varem oli öösel Jeesuse juure tulnud, tuli ja tõi segatud mürri ja aaloet ligi sada naela.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 40, 'Siis nad võtsid Jeesuse ihu ja mähkisid ta linastesse riietesse lõhnarohtudega, nõnda nagu on juutide matmisviis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 41, 'Aga seal paigas, kus Ta oli risti löödud, oli aed ja aias uus haud, kuhu veel iialgi ei olnud kedagi pandud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 42, 'Sinna nad panid siis Jeesuse juutide valmistuspäeva pärast; sest see haud oli lähedal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 1, 'Aga nädala esimesel päeval tuli Maarja Magdaleena vara, kui alles pime oli, hauale ja näeb, et kivi on haua eest ära võetud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 2, 'Siis ta jookseb ja tuleb Siimon Peetruse ja teise jüngri juure, keda Jeesus armastas, ja ütleb neile: \"Nad on Issanda hauast ära võtnud ja me ei tea, kuhu nad Ta on pannud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 3, 'Siis läksid välja Peetrus ja teine jünger ja tulid hauale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 4, 'Aga nad jooksid mõlemad üheskoos, ja teine jünger jooksis ees usinamini kui Peetrus ja jõudis enne hauale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 5, 'Ja kummargile sisse vaadates näeb ta surnulinad seal olevat, aga ta ei läinud sisse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 6, 'Siis tuli Siimon Peetrus, kes teda järgis, ja läks haua sisse. Ja ta näeb surnulinad maas olevat,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 7, 'ja higirätiku, mis oli Tema pea peal olnud et see ei ole surnulinadega maas, vaid on isepäinis kokkumähitud teises kohas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 8, 'Siis läks sisse ka teine jünger, kes esimesena oli tulnud hauale, ja nägi ja uskus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 9, 'Sest nad ei saanud veel aru Kirjast, et Tema pidi surnuist üles tõusma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 10, 'Siis need jüngrid läksid ja tagasi omaste juure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 11, 'Maarja aga seisis haua juures väljas ja nuttis. Kui ta nõnda nuttis, vaatas ta kummargile hauda');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 12, 'ja näeb kaks Inglit valgeis riideis istuvat, ühe peatsis ja teise jalutsis, seal kus Jeesuse ihu maganud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 13, 'Ja need ütlevad temale: \"Naine, miks sa nutad?\" Ta ütleb neile \"Nemad on mu Issanda ära viinud ja ei tea, kuhu nad Ta on pannud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 14, 'Kui ta seda oli ütelnud, pöördus ta ümber ja näeb Jeesust seisvat, ja ta ei teadnud, et see on Jeesus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 15, 'Jeesus ütleb temale: \"Naine, miks sa nutad? Keda sa otsid?\" Naine mõtleb Tema aedniku olevat ja ütleb Talle: \"Isand, kui sina Ta oled ära kannud, siis ütle mulle, kuhu sa Ta oled pannud, ja mina toon Ta ära!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 16, 'Jeesus ütleb temale: \"Maarja!\" See pöördub ümber ja ütleb Talle heebrea keeli: \"Rabuuni!\" See tähendab: õpetaja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 17, 'Jeesus ütleb talle: \"Ära puuduta Mind, sest Ma pole veel üles läinud Oma Isa juure! Kuid mine Mu vendade juure ja ütle neile: Mina lähen üles Oma Isa ja teie Isa juure ja Oma Jumala ja teie Jumala juure!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 18, 'Maarja Magdaleena tuleb ja teatab jüngritele, et ta on Issandat näinud ja et Ta temale seda on ütelnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 19, 'Kui nüüd õhtu aeg oli samal nädala esimesel päeval ja uks6d olid lukus seal, kus jüngrid olid kartuse pärast juutide eest, siis tuli Jeesus ja seisis keset nende seas ja ütles neile: \"Rahu olgu teile!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 20, 'Ja kui Ta seda oli ütelnud, näitas Ta neile Oma käsi ja külge. Siis jüngrid said rõõmsaks Issandat nähes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 21, 'Jeesus ütles nüüd taas neile: \"Rahu olgu teile! Nõnda nagu Minu Isa on Mind läkitanud, nõnda läkitan ka Mina teid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 22, 'Ja kui Ta seda oli ütelnud, puhus Ta nende peale ja ütles neile: \"Võtke vastu Püha Vaim!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 23, 'Kelledele te iganes patud andeks annate, neile on need andeks antud; kelledele te iganes patud kinnitate, neile i need kinnitatud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 24, 'Aga Toomas, üks neist kaheteistkümnest, keda nimetatakse Kaksikuks, ei olnud nendega, kui Jeesus tuli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 25, 'Siis ütlesid teised jüngrid temale: \"Me nägime Issandat!\" Aga tema ütles neile: \"Kui ma ei näe Tema kätes naelte jälgi ega pane oma sõrme naelte asemeisse ja oma kätt Tema külje sisse, siis ma ei usu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 26, 'Ja kaheksa päeva pärast olid Tema jüngrid jälle toas ja Toomas nendega. Siis tuli Jeesus uste lukus olles ja seisis nende keskel ja ütles: \"Rahu olgu teile!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 27, 'Selle järele Ta ütleb Toomale: \"Pane oma sõrm siia ja vaata Minu käsi, ja siruta oma käsi siia ja pista see Minu küljesse, ja ära ole uskmatu, vaid usklik!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 28, 'Toomas vastas ning ütles Temale: \"Minu Issand ja minu Jumal!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 29, 'Jeesus ütleb Temale: \"Et sa Mind oled näinud, siis sa usud. Õndsad on need, kes ei näe ja siiski usuvad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 30, 'Nii tegi Jeesus ka veel muid tunnustähti jüngrite ees, mida ei ole kirjutatud sellesse raamatusse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 31, 'Aga need on kirjutatud, et te usuksite, et Jeesus on Kristus, Jumala Poeg, ja et teil uskudes oleks elu Tema nime sees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 1, 'Pärast seda ilmus Jeesus jälle jüngritele Tibeeria mere ääres. Aga Ta ilmus nõnda:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 2, 'Siimon Peetrus ja Toomas, keda kutsutakse Kaksikuks, ja Naatanael Kaanast Galileamaalt, ja Sebedeuse pojad ja veel teised kaks Tema jüngritest olid seal koos.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 3, 'Siis ütleb Siimon Peetrus neile: \"Ma lähen kalale.\" Nad ütlevad temale: \"Me tuleme ka ühes sinuga.\" Nad läksid välja ja astusid varsti paati, aga ei saanud ühtki sel ööl.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 4, 'Aga kui juba oli hommik, seisis Jeesus rannal. Ent jüngrid ei teadnud, et see oli Jeesus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 5, 'Siis ütleb Jeesus neile: \"Lapsed, kas teil on midagi toidupoolist?\" Nemad vastasid Temale: \"Ei ole.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 6, 'Aga Tema ütles neile: \"Heitke võrk välja paremale poole paati, siis te leiate!\" Nad heitsid selle siis välja ega suutnud seda enam vedada kalade hulga pärast!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 7, 'Siis ütles see jünger, keda Jeesus armastas, Peetrusele: \"See on Issand!\" Kui nüüd Siimon Peetrus kuulis, et see on Issand, pani ta kuue selga, sest ta oli alasti, ja heitis enese merre.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 8, 'Aga teised jüngrid tulid paadiga - sest nad ei olnud kaldast kaugemal kui kakssada küünart maad - ja vedasid võrku kaladega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 9, 'Kui nad nüüd astusid maale, näevad nad sütetule maas olevat ja kalukesi seal peal ja leiba.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 10, 'Jeesus ütleb neile: \"Tooge neid kalukesi, mis te praegu püüdsite!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 11, 'Siimon Peetrus läks ja vedas võrgu mäele, mis oli täis suuri kalu, arvult sada ja viiskümmend kolm. Ja ehk küll kalu nii palju oli, võrk ei rebenenud mitte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 12, 'Jeesus ütleb neile: \"Tulge einestama!\" Aga ükski jüngritest ei julgenud Temalt küsida: \"Kes sa oled?\" Sest nad teadsid, et See oli Issand.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 13, 'Siis Jeesus tuleb ja võtab leiva ja annab neile, ja samuti kalukesi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 14, 'See oli juba kolmas kord, et Jeesus pärast Oma ülestõusmist surnuist näitas Ennast Oma jüngritele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 15, 'Aga kui nad olid einestanud, ütleb Jeesus Siimon Peetrusele: \"Siimon, Joona poeg, kas sa armastad Mind rohkem kui need?\" Ta ütleb Temale: \"Jah, Issand, Sina tead, et Sa oled mulle armas!\" Ta ütleb temale: \"Sööda Mu tallekesi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 16, 'Ta ütleb temale jälle teist korda: \"Siimon, Joona poeg, kas sa armastad Mind?\" Ta ütleb Temale: \"Jah, Issand, Sina tead, et Sa oled mulle armas!\" Ta ütleb temale: \"Hoia Mu lambaid kui karjane!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 17, 'Kolmat korda Ta ütleb temale: \"Siimon, Joona poeg, kas Ma olen sulle armas?\" Peetrus sai kurvaks, et Ta temale kolmat korda ütles: \"Kas Ma olen sulle armas?\" Ja ta ütles Temale \"Issand, Sina tead kõik, Sina tunned, et oled mulle armas!\" Jeesus ütleb temale \"Sööda Mu lambaid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 18, 'Tõesti, tõesti Ma ütlen sulle: Kui sa olid noorem, siis sa vöötasid ennast ise ja läksid, kuhu sa tahtsid. Aga kui sa saad vanaks, siis sa sirutad oma käed ja keegi teine vöötab sind ja viib sind, kuhu sa ei taha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 19, 'Aga seda Ta ütles tähendades, missuguse surmaga ta pidi Jumalat austama. Ja kui Ta seda oli rääkinud, ütleb Ta temale: \"Järgi mind!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 20, 'Aga Peetrus pöördus ja näeb jüngrit, keda Jeesus armastas, järel sammuvat, selle, kes õhtusöömaajal oli laskunud Jeesuse rinnale ja ütelnud \"Issand, kes see on, kes Sind ära annab?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 21, 'Kui nüüd Peetrus teda nägi, ütleb Jeesusele: \"Issand, aga kuidas jääb temaga?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 22, 'Jeesus ütleb talle: \"Kui Ma tahan, et ta jääb, kuni Ma tulen, mis see sinusse puutub? Järgi sina Mind!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 23, 'tõusis jutt vendade seas, et see jünger ei surevat. Ent Jeesus ei olnud temale mitte ütelnud, et ta ei sure, vaid: \"Kui Ma tahan, et ta jääb, kuni Ma tulen, mis see sinusse puutub?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 24, 'See on see jünger, kes neist asjust tunnistab ja on selle kirjutanud; ja me teame, et tema tunnistus on tõsi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 25, 'veel palju muudki, mida Jeesus tegi; kui seda kõike üksikult üles kirjutataks, siis ma arvan, et maailm ei mahutaks kirjutatavaid raamatuid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 1, 'Esimese raamatu, Teofilos, ma kirjutasin kõigest sellest, mida Jeesus algusest peale tegi ja õpetas');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 2, 'selle päevani, mil Ta võeti üles pärast seda kui Ta Püha Vaimu läbi oli andnud käsu Apostlitele, keda Ta oli valinud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 3, 'kellele Tema ka pärast Oma kannatamist oli mitme tõestusmärgiga Ennast näidanud elavana, neile ilmudes nelikümmend päeva ning neile rääkides Jumala riigist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 4, 'Ja kui Ta nendega koos oli, keelas Ta neid Jeruusalemmast ära minemast, käskis aga oodata Isa tõotust, \"mis teie,\" ütles Tema, \"olete kuulnud Minult.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 5, 'Sest Johannes ristis veega, aga teid peab ristitama Püha Vaimuga mitte kaua pärast neid päevi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 6, 'Siis küsisid Temalt need, kes olid kokku tulnud, ning ütlesid: \"Issand, kas sa sel ajal jälle ehitad Iisraelile kuningriigi?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 7, 'Tema ütles neile: \"Teile ei sünni teada aegu ega hetki, mis Isa Omas meelevallas on määranud;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 8, 'aga te saate Püha Vaimu väe, Kes tuleb teie peale, ja peate olema Minu tunnistajad Jeruusalemmas ja kõigel Juuda- ja Samaariamaal ja maailma otsani!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 9, 'Kui Ta seda oli öelnud, siis tõsteti Ta üles nende nähes ja pilv viis Ta nende silme eest ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 10, 'Ja kui nad vaatasid üksisilmi taeva poole, kuidas Ta ära läks, vaata, siis seisid nende juures kaks meest valgeis riideis,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 11, 'ja need ütlesid: \"Galilea mehed, miks te seisate ja vaatate üles taeva poole? See Jeesus, Kes teilt üles võeti taevasse, tuleb samal kombel kui te nägite Teda taevasse minevat!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 12, 'Siis nad läksid tagasi Jeruusalemma mäelt, mida kutsutakse Õlimäeks ja mis on Jeruusalemma lähedal ühe hingamispäeva teekonna maad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 13, 'Ja kui nad olid jõudnud linna, siis nad läksid ülemisse tuppa, kus nad olid asumas, Peetrus ja Jakoobus ja Johannes ja Andreas, Filippus ja Toomas, Bartolomeus ja Matteus, Jakoobus, Alfeuse poeg ja Siimon Selootes ja Juudas, Jakoobuse vend.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 14, 'Need kõik olid alati ühel meelel üheskoos palves naistega ja Maarjaga, Jeesuse Emaga, ja Jeesuse vendadega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 15, 'Neil päevil tõusis Peetrus vendade seas üles - rahvast aga oli koos umbes sada kakskümmend isikut - ning ütles:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 16, '\"Mehed, vennad, täide on pidanud minema Kirjasõna, mis Püha Vaim on ette kuulutanud Taaveti suu kaudu Juuda kohta, kes hakkas juhiks Jeesuse kinnivõtjaile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 17, 'Sest ta oli arvatud meie sekka ja oli saanud selle ameti osaliseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 18, 'Aga tema hankis enesele põllu ülekohtu palgaga, ja kui ta ülepeakaela alla kukkus, lõhkes ta keskpaigast ja kogu ta sisikond vajus välja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 19, 'Ja see on saanud teatavaks kõigile Jeruusalemma elanikele, nõnda et seda põldu nende oma keelemurdes hüütakse Akeldaamaks, see on Verepõlluks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 20, 'Sest Psalmide raamatus on kirjutatud: \"Tema elamu saagu tühjaks ja ärgu olgu selles elanikku, ja tema ülevaatajaamet saagu teisele!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 21, 'Sellepärast peab nüüd üks neist meestest, kes on käinud ühes meiega kõige selle aja, mil Issand Jeesus meie juures käis sisse ja välja,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 22, 'alates Johannese ristimisest kuni selle päevani, mil Ta meilt võeti üles, saama Tema ülestõusmise tunnistajaks ühes meiega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 23, 'Ja nad seadsid üles kaks meest: Joosepi, keda hüütakse Barsabaseks, lisanimega Justuseks, ja Mattiase.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 24, 'Ja nad palvetasid ning ütlesid: \"Sina, Issand, kõikide südametundja, näita meile, kumma neist kahest Sina oled valinud');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 25, 'saama selle tunnistuse ja Apostliameti koha, millest Juudas ära astus, et minna oma paika!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 26, 'Ja nad heitsid liisku nende kohta; ja liisk langes Mattiasele, ja tema arvati nende üheteistkümne Apostli juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 1, 'Ja kui nelipühapäev kätte tuli, olid kõik ühes paigas koos.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 2, 'Ja taevast sündis äkitselt kohin, otsekui oleks kange tuul puhunud, ja täitis kõik koja, kus nad istusid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 3, 'Ja neile ilmus nagu lõhestatud tulekeeli, ja need langesid üksikult igaühe peale nende seast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 4, 'Ja nad kõik said täis Püha Vaimu ja hakkasid rääkima teisi keeli, nõnda nagu Vaim neile andis rääkida.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 5, 'Aga Jeruusalemmas oli elamas juute, jumalakartlikke mehi kõigist rahvaist taeva all.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 6, 'Kui nüüd see hääl tekkis, tuli rahvahulk kokku ja kohkus ära, sest igaüks kuulis neid rääkivat oma keelemurret.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 7, 'Ja nad hämmastusid kõik ja panid imeks ning ütlesid üksteisele: \"Vaata, eks need kõik, kes räägivad, ole Galilea mehed?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 8, 'Kuidas me siis kuuleme igaüks oma keelemurret, mille sees me oleme sündinud?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 9, 'Meie, partlased ja meedlased ja eelamlased ja kes elame Mesopotaamias, Judeas ning Kapadookias, Pontoses ja Aasias,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 10, 'Früügias ja Pamfüülias, Egiptuses ja Küreene-poolses Liibüas, ja siin asuvad roomlased, juudid ühes nende usku pöördunutega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 11, 'kreetalased ja araablased, kuidas me kuuleme neid rääkivat meie omis keeltes Jumala suuri asju?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 12, 'Nad kõik olid hämmastunud ja kahevahel ning küsisid üksteiselt: \"Mis see küll peaks tähendama?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 13, 'Aga teised irvitasid ning ütlesid: \"Nad on täis magusat viina!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 14, 'Siis Peetrus astus esile ühes nende üheteistkümnega, tõstis oma häält ning seletas neile: \"Juuda mehed ja kõik teie, Jeruusalemma elanikud, see olgu teile teada ja pange tähele mu sõnu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 15, 'Ei ole need sugugi, nagu te arvate, joobnud, sest see on kolmas päevatund;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 16, 'vaid see on, mis on öeldud prohvet Joeli kaudu:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 17, '\"Ja viimseil päevil sünnib, ütleb Jumal, et Ma valan Oma Vaimu kõige liha peale! Ja teie pojad ja tütred hakkavad ennustama, teie noored mehed näevad nägemusi, ja teie vanemad uinuvad unenägusid nähes!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 18, 'Ja neil päevil Ma valan Oma Vaimu Oma sulaste ja ümmardajate peale ja nemad ennustavad;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 19, 'ja Ma annan näha imesid ülal taevas ja tunnustähti all maa peal, verd ja tuld ja suitsusambaid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 20, 'Päike muutub pimedaks ja kuu vereks, enne kui tuleb Issanda päev, suur ja auline!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 21, 'Ja sünnib, et igaüks, kes appi hüüab Issanda nime, pääseb!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 22, 'Iisraeli mehed, kuulge neid sõnu: Jeesuse Naatsaretlase, Mehe, Kellest Jumal andis tunnistuse väe ja imede ja tunnustähtedega, mis Jumal tegi Tema läbi teie seas, nõnda nagu isegi teate,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 23, 'Tema te olete, kui Ta Jumala määratud otsusel ja etteteadmist mööda oli loovutatud teie kätte, ülekohtuste käte läbi risti naelutanud ning tapnud;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 24, 'kuid Jumal on Tema üles äratanud, päästes Teda surma valudest, sest ei olnud ju võimalik, et surm oleks Teda kinni pidanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 25, 'Sest Taavet ütleb Tema kohta: \"Ma pean Issandat alati oma silma ees, sest Ta on mu paremal pool, et ma ei kõiguks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 26, 'Sellepärast rõõmutseb mu süda ja mu keel ilutseb, ja mu ihugi võib hingata lootuses!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 27, 'Sest Sina ei jäta mu hinge surmavalda ega lase Oma Püha näha kõdunemist!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 28, 'Sa annad mulle teada elutee, Sa täidad mind rõõmuga Oma palge ees!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 29, 'Mehed, vennad, olgu lubatud teie peavanemast Taavetist lausa öelda, et ta on surnud ja maha maetud ja tema haud on meie juures tänapäevani.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 30, 'Et ta nüüd oli prohvet ja teadis, et Jumal temale oli vandega tõotanud seada tema ihusoost järglase tema aujärjele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 31, 'siis ta rääkis ette nähes Kristuse ülestõusmisest, et Tema hinge ei jäeta surmavalda ja et Tema liha ei näe kõdunemist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 32, 'Selle Jeesuse on Jumal surnuist üles äratanud; selle tunnistajad oleme meie kõik.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 33, 'Et Ta nüüd Jumala parema käe läbi on ülendatud ja on Isalt saanud Püha Vaimu tõotuse, siis on Tema selle välja valanud, nõnda nagu te nüüd näete ning kuulete.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 34, 'Sest Taavet ei ole läinud taevasse, aga tema ütleb: \"Issand on öelnud minu Issandale: istu Minu paremale käele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 35, 'kuni Ma panen Su vaenlased Sinu jalajäriks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 36, 'Kindlasti teadku nüüd kogu Iisraeli kodakond, et Jumal on teinud Tema Issandaks ning Kristuseks, Selle Jeesuse, Kelle te risti lõite!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 37, 'Aga kui nad seda kuulsid, läks see neil südamest läbi ja nad ütlesid Peetrusele ja teistele Apostlitele: \"Mehed, vennad, mis me peame tegema?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 38, 'Aga Peetrus ütles neile: \"Parandage meelt ja igaüks teist lasku ennast ristida Jeesuse Kristuse nimesse pattude andekssaamiseks, ja siis te saate Püha Vaimu anni.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 39, 'Sest teie ja teie laste päralt on see tõotus ja kõikide päralt, kes on kaugel, keda iganes Issand, meie Jumal, kutsub Enese juurde.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 40, 'Ja veel paljude muude sõnadega tunnistas ja manitses ta ning ütles: \"Laske endid päästa sellest pöörasest soost!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 41, 'Kes nüüd vastu võtsid tema sõna, need ristiti, ja nõnda lisati sel päeval nende juurde ligi kolm tuhat hinge.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 42, 'Aga nemad jäid alati Apostlite õpetusse ja osadusse ja leivamurdmisse ja palvetesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 43, 'Aga igale hingele tuli kartus; ja palju imesid ja tunnustähti sündis Apostlite läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 44, 'Aga kõik need, kes uskusid, olid üheskoos ja kõik, mis neil oli, oli neil ühine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 45, 'Omandi ja vara nad müüsid ära ja jagasid, mis saadi, igaühele sedamööda, kuidas keegi vajas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 46, 'Ja nad viibisid iga päev ühel meelel pühakojas ja murdsid leiba kodasid mööda ja võtsid toidust ilutsemise ja siira südamega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 47, 'kiites Jumalat ja leides armastust kõige rahva juures. Aga Issand lisas iga päev nende ühendusele juurde neid, kes päästeti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 1, 'Aga Peetrus ja Johannes läksid pühakotta palvusele, mida peeti üheksandal tunnil.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 2, 'Ja üks mees, kes oli emaihust alates halvatu, toodi sinna. Ta pandi iga päev pühakoja ukse ette, mida hüütakse Ilusaks, ande paluma pühakojas käijailt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 3, 'Kui see nägi, et Peetrus ja Johannes tahavad minna pühakotta, palus ta neilt andi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 4, 'Siis Peetrus ühes Johannesega vaatas temale otsa ning ütles: \"Vaata meie poole!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 5, 'Tema jäi teraselt neile otsa vaatama, oodates, et ta neilt midagi saab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 6, 'Aga Peetrus ütles: \"Hõbedat ja kulda mul ei ole, aga mis mul on, seda ma annan sulle: Jeesuse Kristuse, Naatsaretlase nimel, tõuse üles ja kõnni!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 7, 'Ja ta haaras kinni tema paremast käest ja aitas ta üles. Ja sedamaid said tema jalapöiad ja luupeksed tugevaks;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 8, 'ja ta kargas üles, seisis ja kõndis, ja läks ühes nendega pühakotta sisse, kõndides ja hüpates ning Jumalat kiites.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 9, 'Ja kõik rahvas nägi teda kõndivat ja Jumalat kiitvat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 10, 'Nad tundsid tema, et ta on seesama, kes oli istunud kerjamas pühakoja Ilusa värava ees, ja nad panid seda väga imeks ja olid hämmastunud sellest, mis temale oli sündinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 11, 'Aga kui halvatu, kes oli terveks saanud, käis Peetruse ja Johannese kannul, jooksis kõik rahvas kokku nende juurde nõndanimetatud Saalomoni võlvitud hoonesse ja oli ärevil.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 12, 'Kui Peetrus seda nägi, hakkas ta kõnelema rahvale: \"Iisraeli mehed, miks te panete seda imeks või miks te üksisilmi vaatate meie peale, otsekui oleksime meie oma väe või vagadusega selle pannud kõndima?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 13, 'Aabrahami ja Iisaki ja Jaakobi Jumal, meie esiisade Jumal, on austanud Oma sulast Jeesust, Kelle te ära andsite ja salgasite Pilaatuse ees, kui see tegi otsuseks Teda vabaks lasta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 14, 'Te salgasite ära Püha ja Õige ja palusite, et teile mõrtsukas vabaks antaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 15, 'Te tapsite elu Ülima juhi, Kelle Jumal on surnuist üles äratanud; selle tunnistajad oleme meie.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 16, 'Ja usu läbi Tema nimesse on Tema nimi teinud tugevaks selle, keda te näete ja tunnete; ja usk, mis tuleb Temalt, on sellele andnud täie tervise teie kõikide nähes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 17, 'Ja nüüd, vennad, tean ma, et te olete seda teinud teadmatusest nagu teie ülemadki.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 18, 'Aga Jumal on nõnda lasknud täide minna, mis Tema kõigi Oma prohvetite suu kaudu on ette kuulutanud, et Tema võitu peab kannatama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 19, 'Sellepärast parandage meelt ja pöörduge, et teie patud kustutataks, et hingamiseajad tuleksid Issanda palgest');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 20, 'ja et Ta läkitaks teie jaoks määratud Kristuse Jeesuse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 21, 'Keda taevas peab pidama Enesele selle ajani, mil oma kohale asetatakse kõik, mis Jumal on rääkinud kõigi Oma pühade prohvetite suu kaudu maailma ajastust alates.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 22, 'Sest Mooses on öelnud: \"Ühe prohveti äratab teile Issand, teie Jumal, teie vendade hulgast, minu sarnase; Teda peate kuulama kõiges, mida Ta teile ütleb.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 23, 'Ja peab sündima, et iga hing, kes seda Prohvetit ei kuula, kaotatakse ära rahva seast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 24, 'Ja kõik prohvetid Saamueli ajast alates ja pärast seda, nii mitu kui neid on rääkinud, on ka ette kuulutanud neid päevi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 25, 'Teie olete prohvetite ja selle lepingu lapsed, mille Jumal tegi teie esiisadega, kui Ta Aabrahamile ütles: \"Ja sinu soos õnnistatakse kõiki suguvõsasid maa peal!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 26, 'Teile kõigepealt on Jumal üles äratanud Oma Sulase Jeesuse ja on Tema läkitanud teie juurde teid õnnistama, kui te pöördute igaüks oma tigedusest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 1, 'Aga kui nad rahvale rääkisid, astusid nende juurde preestrid ja pühakoja pealik ja saduserid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 2, 'ja nende meel oli paha, et nemad õpetasid rahvast ja kuulutasid surnuist ülestõusmist Jeesuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 3, 'Ja nad pistsid käed nende külge ja panid nad vangi järgmiseks päevaks, sest oli juba õhtu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 4, 'Aga paljud neist, kes Sõna kuulsid, uskusid; ja meeste arv tõusis ligi viie tuhandeni.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 5, 'Järgmisel päeval sündis, et rahva ülemad ja vanemad ja kirjatundjad tulid kokku Jeruusalemma,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 6, 'ka ülempreester Annas ja Kaifas ja Johannes ja Aleksandros ja niipalju kui neid oli ülempreestri soost.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 7, 'Ja nad lasksid nad ette tuua ning küsitlesid neid: \"Missuguse väega või kelle nimel te tegite seda?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 8, 'Siis Peetrus, täis Püha Vaimu, ütles neile: \"Rahva ülemad ja vanemad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 9, 'Kui me täna peame kohtus aru andma vigasele inimesele tehtud heateost ja Kelle läbi ta on terveks saanud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 10, 'siis olgu teile kõigile ja kogu Iisraeli rahvale teada, et Jeesuse Kristuse, Naatsaretlase nimel, Kelle teie risti lõite ja Kelle Jumal on surnuist üles äratanud, et Tema läbi seisab see siin teie ees tervena.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 11, 'Tema on see kivi, mille teie, kojaehitajad, olete ära põlanud ja mis on saanud nurgakiviks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 12, 'Ja ühegi muu sees ei ole päästet; sest ei ole antud taeva all inimestele ühtki muud nime, kelles meid päästetakse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 13, 'Aga kui nad Peetruse ja Johannese julgust nägid ja teada said, et nad on kirjatundmatud ja õppimatud mehed, panid nad seda imeks ja tundsid nad ära, et nad olid need, kes olid olnud Jeesusega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 14, 'Ja kui nad inimest, kes oli terveks saanud, nende juures nägid seisvat, ei olnud neil midagi öelda selle vastu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 15, 'vaid nad käskisid neid välja minna Suurkohtust, pidasid isekeskis nõu');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 16, 'ning ütlesid: \"Mis me peame tegema nende inimestega, sest avalik ime on sündinud nende läbi ja on teada kõigile, kes Jeruusalemmas elavad, nõnda et me ei või seda salata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 17, 'Aga et seda laiemale ei levitataks rahva sekka, siis ähvardagem neid kõvasti, et nad enam ühelegi inimesele ei räägiks sel nimel!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 18, 'Ja nad kutsusid nad sisse ja keelasid neid midagi rääkimast ja õpetamast Jeesuse nimel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 19, 'Aga Peetrus ja Johannes kostsid neile ning ütlesid: \"Kas on õige Jumala ees teid rohkem kuulata kui Jumalat? Otsustage ise.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 20, 'Sest me ei või jätta rääkimata seda, mida oleme näinud ja kuulnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 21, 'Aga nad ähvardasid neid ja lasksid nad vabaks, sest nad ei leidnud midagi, mille eest neid karistada, rahva pärast, sest kõik kiitsid Jumalat selle eest, mis oli sündinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 22, 'Sest juba rohkem kui nelikümmend aastat vana oli inimene, kellele see tervekssaamise tunnustäht oli sündinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 23, 'Kui nad olid vabaks saanud, tulid nad omade juurde ja kuulutasid, mis ülempreestrid ja vanemad neile olid öelnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 24, 'Aga kui need seda kuulsid, tõstsid nad ühel meelel häält Jumala poole ja ütlesid: \"Issand, Sina oled See Jumal, Kes on teinud taeva ja maa ja mere ja kõik, mis nende sees on,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 25, 'Kes oled Püha Vaimu läbi Oma sulase Taaveti suu kaudu öelnud: \"Miks paganad möllavad ja rahvad mõtlevad tühja?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 26, 'Ilmamaa kuningad on kokku astunud ja ülemad on kogunenud ühte Issanda vastu ning Tema Võitu vastu?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 27, 'Sest tõepoolest on selles linnas Sinu püha sulase Jeesuse vastu, Kelle Sa oled võidnud, kogunenud ühte paganatega ja Iisraeli rahvaga Heroodes ja Pontius Pilaatus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 28, 'et teha seda, mis Sinu käsi ja nõu oli enne määranud, et see pidi sündima.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 29, 'Ja nüüd vaata, Issand, nende ähvardamistele ja anna Oma sulastele kõige julgusega rääkida Sinu sõna,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 30, 'Oma kätt sirutades selleks, et tervekssaamised ning tunnustähed ja imed sünniksid Sinu püha sulase Jeesuse nime läbi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 31, 'Ja kui nad olid palvetanud, kõikus paik, kus nad koos olid, ning nad kõik said täis Püha Vaimu ja rääkisid Jumala sõna julgesti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 32, 'Ja usklike hulk oli üks süda ja üks hing; ja ükski ei öelnud oma varanduse kohta, et see on tema oma, vaid kõik oli neil ühine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 33, 'Ja Apostlid tunnistasid suure väega Issanda Jeesuse ülestõusmist, ja suur arm oli nende kõikide juures.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 34, 'Ei olnud ka ühtki vaest nende seas; sest kellel olid põllud või majad, need müüsid need ära ning tõid müüdud asjade hinna');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 35, 'ja panid selle Apostlite jalgade ette. Igaühele jagati siis sedamööda, kuidas ta vajas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 36, 'Aga Joosep, keda Apostlid lisanimega hüüdsid Barnabaseks - see on meie keeli Trööstija - leviit, Küprose saarelt pärit,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 37, 'kellel oli põld, müüs selle ära ja tõi hinna ja pani selle Apostlite jalgade ette.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 1, 'Aga keegi mees, Ananias nimi, oma naise Safiiraga müüs oma varanduse');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 2, 'ja mees toimetas oma naise teades muist hinda kõrvale ja muist ta tõi ning pani Apostlite jalgade ette.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 3, 'Aga Peetrus ütles: \"Ananias, mispärast on saatan täitnud su südame, et sa valet rääkisid Pühale Vaimule ja kõrvale toimetasid muist põllu hinda?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 4, 'Eks see su käes olles olnud sinu oma, ja kui see oli müüdud, eks see olnud ka siis sinu meelevallas? Mispärast oled sa seda ette võtnud oma südames? Sina ei ole valetanud inimestele, vaid Jumalale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 5, 'Kui nüüd Ananias kuulis neid sõnu, langes ta maha ja heitis hinge. Ja suur kartus tuli kõikide peale, kes seda kuulsid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 6, 'Aga noored mehed tõusid ning koristasid ta ära ja kandsid ta välja ning matsid maha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 7, 'Arvata kolme tunni pärast tuli tema naine ega teadnud, mis oli sündinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 8, 'Aga Peetrus küsis talt: \"Ütle mulle, kas te selle hinnaga müüsite põllu?\" Tema vastas: \"Selle hinnaga jah!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 9, 'Siis ütles Peetrus talle: \"Mispärast on teil üks nõu olnud kiusata Issanda Vaimu? Vaata, nende jalad, kes su mehe matsid, on ukse ees ja kannavad sindki välja!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 10, 'Siis langes naine kohe maha tema jalge ette ja heitis hinge. Ja kui noored mehed tulid sisse, leidsid nad ta surnud olevat ja kandsid ta välja ning matsid ta tema mehe kõrvale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 11, 'Ja suur kartus tuli kõige koguduse peale ja kõikide peale, kes seda kuulsid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 12, 'Aga palju tunnustähti ja imesid sündis rahva seas Apostlite käte läbi. Ja nad olid kõik koos ühel meelel Saalomoni võlvitud hoones.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 13, 'Muudest inimestest aga ei julgenud ükski liituda nende hulka, rahvas aga pidas neist suurt lugu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 14, 'Seda enam aga lisandus neid, kes uskusid Issandasse, nii mehi kui naisi suurel hulgal!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 15, 'Kandsid ju inimesed haigeid tänavailegi ja asetasid neid vooditesse ja raamidele, et kui Peetrus tuleb, tema vari langeks mõne peale nendest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 16, 'Ka tuli rahvast kokku Jeruusalemma ümberkaudseist linnadest, tuues haigeid ja rüvedaist vaimudest vaevatuid, ja need kõik said terveks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 17, 'Aga ülempreester tõusis ning kõik ta kaaslased, saduseride lahk, ning said täis viha,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 18, 'ja pistsid oma käed Apostlite külge ning panid nad üldisesse vanglasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 19, 'Aga Issanda Ingel tegi öösel vangihoone uksed lahti ja viis nad välja ning ütles:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 20, '\"Minge, esinege ning rääkige rahvale pühakojas kõik selle elu sõnad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 21, 'Kui nad seda olid kuulnud, läksid nad koidu ajal pühakotta ja õpetasid. Aga ülempreester ja tema kaaslased tulid ja kutsusid kokku Suurkohtu ja kõik Iisraeli laste vanematekogu ja läkitasid sulased vangitorni neid tooma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 22, 'Kui nüüd sulased kohale jõudsid, ei leidnud nad neid vangihoonest. Nad tulid tagasi ja andsid seda teada,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 23, 'öeldes: \"Vangla me leidsime küll hästi hoolsasti lukustatud olevat ja hoidjad väljas uste ees seisvat, aga kui me avasime, ei leidnud me kedagi seest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 24, 'Kui pühakoja pealik ja ülempreestrid neid sõnumeid kuulsid, jäid nad kahevahele, mis sellest arvata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 25, 'Siis tuli keegi ja jutustas neile: \"Vaata, mehed, keda te panite vanglasse, seisavad pühakojas ja õpetavad rahvast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 26, 'Siis läks pealik sulastega ja tõi nad ära; aga mitte vägisi, sest nad kartsid, et rahvas nad kividega surnuks viskab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 27, 'Ja nad tõid ning seadsid nad Suurkohtu ette. Ja ülempreester küsis neilt ning ütles:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 28, '\"Kas me teid ei ole kõvasti keelanud õpetamast selle nimel? Ja vaata, te olete Jeruusalemma täitnud oma õpetusega ja tahate Selle inimese vere saata meie peale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 29, 'Aga Peetrus ja teised Apostlid kostsid ning ütlesid: \"Jumala sõna tuleb rohkem kuulda kui inimeste sõna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 30, 'Meie esiisade Jumal on üles äratanud Jeesuse, Kelle te olete poonud puu külge ning surmanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 31, 'Tema on Jumal Oma parema käega tõstnud Juhiks ja Õnnistegijaks, andma Iisraelile meeleparandust ja pattude andeksandmist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 32, 'Ja meie oleme kõigi nende asjade tunnistajad ja samuti Püha Vaim, Keda Jumal on andnud neile, kes võtavad kuulda Tema sõna!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 33, 'Aga kui nad seda kuulsid, käis see nende südamest läbi, ja nad võtsid nõuks nemad ära tappa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 34, 'Kuid Suurkohtus tõusis üles üks variser, Gamaaliel nimi, käsuõpetaja, keda kõik rahvas austas, ja käskis mehed välja viia natukeseks ajaks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 35, 'ja ütles siis koosolijaile: \"Iisraeli mehed, olge ettevaatlikud sellega, mida te nende inimestega mõtlete teha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 36, 'Sest mitte ammu tagasi tõusis Teudas ning ütles enese midagi olevat, ja temaga liitus arvult ligi nelisada meest; tema tapeti ja kõik, kes nõustusid temaga, hajutati ja hävisid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 37, 'Pärast teda astus üles galilealane Juudas rahvalugemise päevil ja ahvatles rahva enese järele; ka tema sai hukka ja kõik, kes heitsid tema nõusse, hajutati.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 38, 'Ja nüüd ma ütlen teile: jätke need inimesed rahule ja laske nad minna! Sest kui see nõu ja töö on inimestest, siis läheb see tühja,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 39, 'aga kui see on Jumalast, siis te ei või seda tühjaks teha, et te kuidagi ei osutuks jumalavastasteks!\" Ja nad võtsid kuulda tema nõu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 40, 'Ja kui Apostlid olid sisse kutsutud, peksid nad neid ja keelasid neid rääkimast Jeesuse nimel ja lasksid nad minna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 41, 'Aga nemad läksid Suurkohtu eest minema, rõõmsad sellest, et neid oli väärt arvatud kannatama teotust Selle nime pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 42, 'Ja nad ei lakanud iga päev pühakojas ja kodasid mööda õpetamast Evangeeliumi Kristusest Jeesusest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 1, 'Aga neil päevil kui jüngrite arv suurenes, tõusis nurin kreekakeelsete juutide seas heebrealaste vastu, et igapäevases abiandmises õieti ei hoolitud nende leskedest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 2, 'Siis kutsusid need kaksteist eneste juurde jüngrite hulga ning ütlesid: \"See ei sobi, et me kõrvale jätame Jumala sõna ja hoolt kanname toidulaudade eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 3, 'Sellepärast, vennad, vaadake endi seast seitse meest, kellel on hea tunnistus ja kes on täis Vaimu ja tarkust, kelle me seaksime sellesse ametisse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 4, 'Meie aga tahame pidevalt olla palvetamises ja sõna teenistuses!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 5, 'See kõne oli kogu rahvahulga meelt mööda. Ja nad valisid Stefanose, mehe täis usku ja Püha Vaimu, ja Filippuse ja Prokorose ja Nikaanori ja Timoni ja Parmena ja Nikolaose, juudiusku pöördunud antiohlase.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 6, 'Need nad seadsid Apostlite ette ja palvetasid ning panid oma käed nende peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 7, 'Ja Jumala sõna kasvas; ja jüngrite arv sai väga suureks Jeruusalemmas; ja suur hulk preestreid sai sõnakuulelikuks usule.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 8, 'Aga Stefanos, täis armu ja väge, tegi imetegusid ja suuri tunnustähti rahva seas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 9, 'Siis tõusid nõndanimetatud libertiinide ja küreenlaste ja aleksandrialaste kogudustest ja Kiliikia ja Aasia poolt mõned, kes vaidlesid Stefanosega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 10, 'aga ei suutnud vastu seista tarkusele ja Vaimule, Kelle läbi tema rääkis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 11, 'Siis nad hankisid mehed, kes ütlesid: \"Me oleme teda kuulnud pilkesõnu rääkivat Moosese ja Jumala vastu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 12, 'Ja nemad õhutasid rahvast ja vanemaid ja kirjatundjaid ja langesid ta kallale ning võtsid ta kinni ja viisid ta Suurkohtu ette.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 13, 'Nad esitasid valetunnistajaid, kes ütlesid: \"See inimene ei lakka rääkimast sõnu selle püha paiga ja käsuõpetuse vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 14, 'Sest me oleme kuulnud teda ütlevat, et Jeesus Naatsaretlane hävitavat selle paiga ja muutvat kombed, mis Mooses meile on andnud.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 15, 'Ja kui kõik Suurkohtus istujad temale otsa vaatasid, nägid nad tema palge olevat otsekui Ingli palge.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 1, 'Siis ütles ülempreester: \"Kas see on nõnda?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 2, 'Aga tema ütles: \"Mehed, vennad ja isad, kuulge! Au Jumal ilmus meie esiisale Aabrahamile, kui ta oli Mesopotaamias enne oma siirdumist Haaranisse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 3, 'ning ütles temale: \"Mine välja omalt maalt ja omast sugukonnast ja tule sinna maale, mille Ma sulle näitan.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 4, 'Siis ta läks kaldealaste maalt ja asus elama Haaranisse. Ja kui tema isa suri, saatis Jumal tema sealt siia maale, kus teie nüüd elate,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 5, 'ega andnud siin temale pärandiks mitte jalatäitki, aga tõotas selle anda omandiks temale ja tema järglasile pärast teda ajal, mil ta oli alles lasteta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 6, 'Ent Jumal rääkis nõnda: tema järglased peavad olema võõrastena võõral maal ja seal nad tehakse orjadeks ja neid vaevatakse nelisada aastat,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 7, 'ja rahvast, keda nad orjavad, Mina karistan, ütles Jumal, ja selle järel nad lähevad välja ja teenivad Mind siin selles paigas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 8, 'Ja Ta andis temale ümberlõikamislepingu; ja nii sündis Aabrahamile Iisak ja ta lõikas tema ümber kaheksandal päeval. Ja Iisakile sündis Jaakob, ja Jaakobile need kaksteist peavanemat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 9, 'Ja peavanemad kadestasid Joosepit ning müüsid ta ära Egiptusesse; aga Jumal oli temaga');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 10, 'ja päästis tema kõigist ta viletsustest ja andis temale armu ja tarkust, kui ta oli Egiptuse kuninga, vaarao ees; ja see tõstis ta kogu Egiptuse ja oma koja ülemaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 11, 'Aga nälg tuli kogu Egiptusesse ja Kaananisse ja suur viletsus; ja meie esiisad ei leidnud toidust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 12, 'Aga kui Jaakob kuulis, et Egiptuses oli vilja, läkitas ta meie esiisad sinna esimest korda;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 13, 'ja teisel korral andis Joosep ennast tunda oma vendadele ja vaarao sai teada Joosepi päritolu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 14, 'Siis Joosep läkitas järele ja laskis kutsuda oma isa Jaakobi ja kogu oma suguvõsa, seitsekümmend viis hinge.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 15, 'Ja Jaakob läks alla Egiptusesse ja suri, tema ja meie esiisad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 16, 'ja nad viidi Sekemisse ja pandi hauda, mille Aabraham oli ostnud raha eest Emori, Sekemi isa lastelt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 17, 'Kui nüüd lähenes tõotuse aeg, mille Jumal Aabrahamile oli vandega tõotanud, kasvas rahvas ja sigis paljuks Egiptuses,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 18, 'seni kui tõusis teine kuningas, kes Joosepist midagi ei teadnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 19, 'See tarvitas kavalust meie soo kallal ja tegi paha meie esivanemaile neid sundides ära heitma oma lapsukesed, et need ei jääks ellu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 20, 'Sel ajal sündis Mooses ja ta meeldis Jumalale. Teda toideti kolm kuud tema isa majas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 21, 'Aga kui ta oli kõrvale heidetud, võttis vaarao tütar tema ja kasvatas ta enesele pojaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 22, 'Ja Moosesele õpetati kõike egiptlaste tarkust ja tema oli vägev sõnades ja tegudes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 23, 'Aga kui ta oli nelikümmend aastat vanaks saanud, tuli tema südamesse mõte oma vendi, Iisraeli lapsi, vaatama minna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 24, 'Ja nähes ühele ülekohut tehtavat, läks ta appi ja tasus kätte selle eest, kellele liiga tehti, ja lõi egiptlase maha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 25, 'Ent ta mõtles, et tema vennad saavad sellest aru, et Jumal tema käe läbi annab neile pääste; aga nad ei saanud aru.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 26, 'Ja järgmisel päeval ta juhtus nende juurde, kui nad riidlesid, ja sundis neid rahule ning ütles: \"Mehed, te olete vennad, miks teete liiga üksteisele?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 27, 'Aga see, kes tegi liiga oma ligimesele, lükkas ta enesest ära ja ütles: \"Kes on sind seadnud meie ülemaks ja kohtumõistjaks?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 28, 'Kas tahad tappa mind, nagu sa eile tapsid egiptlase?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 29, 'Selle kõne peale Mooses põgenes ja elas võõrana Midjanimaal; seal sündis temale kaks poega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 30, 'Ja kui nelikümmend aastat möödas oli, ilmus temale Siinai mäe kõrbes Issanda Ingel tuleleegis kibuvitsapõõsas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 31, 'Kui Mooses seda nägi, pani ta nägemust imeks. Ja kui ta ligi astus vaatama, kostis Issanda hääl:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 32, '\"Mina olen sinu vanemate Jumal, Aabrahami Jumal ja Iisaki Jumal ja Jaakobi Jumal!\" Aga Mooses hakkas värisema ega julgenud sinna vaadata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 33, 'Aga Issand ütles temale: \"Võta kingad jalast, sest paik, kus sa seisad, on püha maa!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 34, 'Mina olen küllalt näinud Oma rahva vaeva Egiptuses ja olen kuulnud nende ohkamist ning olen tulnud alla neid vabastama. Siis tule nüüd, Ma läkitan sind Egiptusesse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 35, 'Selle Moosese, kelle nemad ära salgasid, öeldes: \"Kes on sind seadnud ülemaks ja kohtumõistjaks?\", selle läkitas Jumal ülemaks ja lunastajaks selle ingli kaudu, kes temale ilmus kibuvitsapõõsas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 36, 'See viis nad välja ja tegi imetegusid ja tunnustähti Egiptuses ja Punases meres ja kõrbes nelikümmend aastat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 37, 'See on see Mooses, kes ütles Iisraeli lastele: \"Ühe prohveti äratab teile Jumal teie vendade hulgast, minu sarnase\",');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 38, 'tema on see, kes oli koguduse seas kõrbes ühes selle Ingliga, kes temaga kõneles Siinai mäel, ja oli ühes meie esiisadega ja sai elavaid sõnu meile edasiandmiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 39, 'Temale meie esivanemad ei tahtnud olla sõnakuulelikud, vaid tõukasid ta ära ja pöördusid oma südamega Egiptuse poole,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 40, 'öeldes Aaronile: \"Tee meile jumalaid, kes käiksid meie ees, sest me ei tea, mis on sündinud selle Moosesega, kes meid tõi Egiptusest välja!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 41, 'Ja nad tegid noil päevil vasika ja viisid sellele ebajumalale ohvreid ning rõõmustusid oma käte tegudest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 42, 'Kuid Jumal pöördus neist ära ja jättis nad teenima taeva vägesid, nõnda nagu on kirjutatud prohvetite raamatus: \"Kas teie, Iisraeli sugu, nelikümmend aastat kõrbes tõite Mulle tapaohvreid ja muid ohvreid?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 43, 'Ei, vaid te kandsite Mooloki telki ja oma jumala Romfa tähte, kujusid, mis te olite teinud, et neid kummardada: sellepärast Ma siirutan teid teispoole Baabüloni!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 44, 'Meie esiisadel oli tunnistustelk kõrbes, nõnda nagu oli määranud see, Kes Moosesega rääkis, et ta pidi selle valmistama eeskuju järgi, mida ta oli näinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 45, 'Selle meie esiisad ühes Joosuaga võtsid ning viisid paganate maa-alale, keda Jumal ajas minema meie esiisade eest kuni Taaveti päevini,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 46, 'kes leidis armu Jumala ees ja palus, et ta leiaks elamu Jaakobi kodakonnale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 47, 'Ent Saalomon ehitas Temale koja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 48, 'Kuid Kõigekõrgem ei ela kätega tehtud kodades, nõnda nagu ütleb prohvet:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 49, '\"Taevas on Minu aujärg ja maa Minu jalgealune järg! Mis koda te Mulle tahate ehitada\", ütleb Issand, \"või mis on Mu hingamise ase?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 50, 'Eks Minu käsi ole selle kõik teinud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 51, 'Te kangekaelsed ja ümberlõikamatud südamest ja kõrvust! Te panete ikka vastu Pühale Vaimule! Nõnda nagu teie esiisad, nõnda teete teiegi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 52, 'Keda prohvetitest teie esiisad ei ole taga kiusanud? Nad tapsid need, kes ennustasid selle Õige tulemist, Kelle äraandjaiks ja tapjaiks nüüd olete saanud teie,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 53, 'kes saite käsuõpetuse Inglite teenistuse kaudu ega pidanud seda mitte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 54, 'Aga kui nad seda kuulsid, lõikas see neile südamesse ja nad kiristasid hambaid ta peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 55, 'Tema aga täis Püha Vaimu vaatas üksisilmi taeva poole ja nägi Jumala auhiilgust ning Jeesust seisvat Jumala paremal poolel,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 56, 'ja ta ütles: \"Ennäe! Ma näen taevad lahti olevat ja Inimese Poja seisvat Jumala paremal poolel!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 57, 'Siis nad karjusid suure häälega ning pidasid oma kõrvad kinni ja kargasid kõik ühel meelel tema kallale');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 58, 'ning lükkasid ta linnast välja ja viskasid teda kividega. Ja tunnistajad panid oma riided maha ühe noore mehe jalgade ette, kelle nimi oli Saulus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 59, 'Ja nad viskasid Stefanost kividega, kuna tema palus Jumalat ja ütles: \"Issand Jeesus, võta minu vaim vastu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 60, 'Ja ta heitis põlvili ning hüüdis suure häälega: \"Issand, ära arva seda pattu neile süüks!\" Ja kui ta seda oli öelnud, uinus ta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 1, 'Aga Saulusel oli hea meel Stefanose surmast. Sel ajal algas suur Jeruusalemma koguduse tagakiusamine. Ja kõik peale Apostlite hajutati Juuda- ja Samaariamaale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 2, 'Aga mõned jumalakartlikud mehed matsid Stefanose maha ja leinasid väga tema pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 3, 'Ent Saulus rüüstas kogudust, käies mööda kodasid, ja tõi välja mehi ja naisi ja andis nad vangi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 4, 'Siis käisid hajaliolevad mööda maad ja kuulutasid Evangeeliumi sõna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 5, 'Aga Filippus tuli Samaaria linna ja kuulutas neile Kristust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 6, 'Ja rahvas pani ühel meelel seda tähele, mida Filippus ütles, kuuldes teda ning nähes imetähti, mida ta tegi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 7, 'Sest paljude seest, kellel olid rüvedad vaimud, läksid need välja suure häälega kisendades, ja palju halvatuid ja jalutuid sai terveks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 8, 'Ja suur rõõm oli selles linnas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 9, 'Aga üks mees, Siimon nimi, oli enne juba seal linnas ja nõidus ning eksitas Samaaria rahvast, öeldes enese midagi suurt olevat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 10, 'Teda panid tähele kõik, nii pisukesed kui suured, ning ütlesid: \"See on Jumala vägi, mida kutsutakse Suureks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 11, 'Ja nad hoidsid tema poole, sest et ta kaua aega oli neid nõidusega eksitanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 12, 'Aga kui nad Filippust uskusid, kes neile kuulutas Evangeeliumi Jumala riigist ja Jeesuse Kristuse nimest, siis ristiti nii mehi kui naisi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 13, 'Ka Siimon ise sai usklikuks, ja kui ta oli ristitud, jäi ta alati Filippuse juurde ja pani väga imeks, nähes sündivat imetähti ja suuri vägevaid tegusid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 14, 'Aga kui Jeruusalemmas olevad Apostlid said kuulda, et Samaaria linn oli Jumala sõna vastu võtnud, läkitasid nad nende juurde Peetruse ja Johannese.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 15, 'Kui need sinna said, palvetasid nad nende eest, et nad saaksid Püha Vaimu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 16, 'Sest Vaim ei olnud veel langenud ühegi peale nende seast, vaid nemad olid ainult ristitud Issanda Jeesuse nimesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 17, 'Siis panid nad oma käed nende peale, ja nad said Püha Vaimu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 18, 'Aga kui Siimon nägi, et Apostlite käte pealepanemise läbi anti Püha Vaimu, pakkus ta neile raha');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 19, 'ning ütles: \"Andke ka minule see meelevald, et see, kelle peale ma iganes oma käed panen, saaks Püha Vaimu.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 20, 'Aga Peetrus ütles talle: \"Kadugu su raha sinuga tükkis, et sa mõtled Jumala andi saada raha eest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 21, 'Sul ei ole jagu ega osa sellest sõnast, sest su süda ei ole avameelne Jumala ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 22, 'Sellepärast paranda meelt sellest oma kurjusest ja palu Issandat, et sinu südame mõte sulle vahest andeks antaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 23, 'Sest ma näen sind viha sapis ja ülekohtu sõlmes kinni olevat!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 24, 'Aga Siimon vastas ning ütles: \"Paluge teie Issandat minu pärast, et minu peale ei tuleks midagi sellest, mis te olete öelnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 25, 'Kui nad nüüd olid tunnistanud ja rääkinud Issanda sõna, pöördusid nad tagasi Jeruusalemma ja kuulutasid Evangeeliumi mitmes Samaariamaa alevis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 26, 'Aga Issanda Ingel rääkis Filippusega ja ütles: \"Tõuse ja mine lõuna poole seda teed, mis Jeruusalemmast läheb alla Gaasa poole, see on kõrbetee!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 27, 'Ja ta tõusis ning läks. Ja vaata, üks Etioopia mees, etiooplaste kuninganna Kandake suur kojaülem, kogu ta varanduse hooldaja, kes oli tulnud Jeruusalemma Jumalat kummardama,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 28, 'oli tagasi minemas ning istus oma tõllas ja luges prohvet Jesaja raamatut.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 29, 'Ja Vaim ütles Filippusele: \"Mine ja astu selle tõlla lähedale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 30, 'Aga kui Filippus jooksis tõlla juurde, kuulis ta teda lugevat prohvet Jesaja raamatut ja küsis: \"Kas sa ka mõistad, mida sa loed?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 31, 'Tema ütles: \"Kuidas ma võin mõista, kui keegi mind ei juhata?\" Ja ta palus Filippust astuda tõlda ja istuda tema kõrvale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 32, 'Ja kirjakoht, mida ta luges, oli see: \"Nagu lammas viidi Ta tappa, ja nagu tall oma niitja ees on vait, nõnda ei avanud Ta Oma suud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 33, 'Teda alandades võeti kohus Ta pealt! Kes kõneleb Tema päritolust? Sest Ta elu võeti ära maa pealt!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 34, 'Aga kojaülem hakkas rääkima Filippusega ning ütles: \"Ma palun sind, kellest räägib prohvet seda? Kas enesest või kellestki teisest?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 35, 'Aga Filippus avas oma suu ja lähtudes sellest kirjakohast ta kuulutas temale Evangeeliumi Jeesusest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 36, 'Ent kui nad teed edasi läksid, jõudsid nad vee juurde ja kojaülem ütles: \"Ennäe vett! Mis keelab, et mind ei peaks ristitama?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 37, 'Aga Filippus ütles: \"Kui sa usud kõigest südamest, siis võib see sündida!\" Tema vastas ning ütles: \"Mina usun, et Jeesus Kristus on Jumala Poeg!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 38, 'Ja ta käskis tõlla peatada, ja nad astusid mõlemad maha vette, Filippus ja kojaülem; ja ta ristis teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 39, 'Aga kui nad veest välja tulid, võttis Issanda Vaim Filippuse ära, ja kojaülem ei näinud teda enam. Aga ta läks oma teed rõõmuga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 40, 'Aga Filippus leiti Asdodist ja ta käis mööda maad ning kuulutas Evangeeliumi kõigile linnadele, kuni ta jõudis Kaisareasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 1, 'Aga Saulus turtsus ikka veel ähvardamise ja tapmisega Issanda jüngrite vastu ja läks ülempreestri juurde');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 2, 'ning palus temalt kirju Damaskuse kogudustele, et kui ta peaks leidma usuteel käijaid, olgu mehi või naisi, et ta võiks nad siduda ja tuua Jeruusalemma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 3, 'Aga kui ta oli sinna minemas ja Damaskuse lähedale jõudis, sündis, et äkitselt paistis tema ümber valgus taevast;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 4, 'ja ta kukkus maha maa peale ning kuulis üht häält temale ütlevat: \"Saul, Saul, miks sa Mind taga kiusad?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 5, 'Tema vastas: \"Issand, kes Sa oled?\" Ja Issand ütles: \"Mina olen Jeesus, Keda sa taga kiusad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 6, 'Aga tõuse ja mine linna, ja seal öeldakse sulle, mis sul tuleb teha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 7, 'Aga mehed, kes olid temaga teel, seisid ehmunult; nad kuulsid küll häält, aga ei näinud kedagi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 8, 'Aga Saulus tõusis maast üles ja kui ta oma silmad avas, ei näinud ta enam! Siis nad talutasid teda kättpidi ja viisid ta Damaskusesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 9, 'Ja kolm päeva ta ei näinud, ei söönud ega joonud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 10, 'Ent Damaskuses oli üks jünger, Ananias nimi. Sellele ütles Issand nägemuses: \"Ananias!\" Tema vastas: \"Issand, vaata, siin ma olen!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 11, 'Issand ütles temale: \"Tõuse ja mine sinna tänavasse, mida kutsutakse Õigeks, ja kuula Juuda majas Sauluse-nimelise Tarsose mehe järele, sest vaata, ta palvetab');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 12, 'ja on nägemuses näinud mehe, Ananias nimi, sisse tulevat ja käe ta peale panevat, et ta nägemise tagasi saaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 13, 'Ananias vastas: \"Issand, ma olen mitmelt kuulnud sellest mehest, kui palju ta on kurja teinud Sinu pühadele Jeruusalemmas;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 14, 'ja tal on ülempreestrilt luba siin siduda kõiki, kes Sinu nime appi hüüavad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 15, 'Aga Issand ütles temale: \"Mine, sest ta on Mulle valitud riist, et ta Minu nime kannaks paganate ja kuningate ning Iisraeli laste ette,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 16, 'sest Mina tahan temale näidata, kui palju ta peab Minu nime pärast kannatama!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 17, 'Siis Ananias läks ära ja astus sinna majja. Ja kui ta oma käe tema peale oli pannud, ütles ta: \"Saul, vend, Issand on mind läkitanud, Jeesus, Kes sulle ilmus teel, mida sa tulid, et sa nägemise tagasi saaksid ja täituksid Püha Vaimuga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 18, 'Ja sedamaid pudenes tema silmadelt otsekui soomuseid, ja ta nägi jälle ja tõusis ning laskis ennast ristida.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 19, 'Ja kui ta oli leiba võtnud, sai ta jälle tugevaks. Siis ta jäi mõneks päevaks jüngrite sekka Damaskusesse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 20, 'ja kuulutas varsti kogudusekodades Kristust, tunnistades, et Seesama on Jumala Poeg.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 21, 'Aga kõik, kes teda kuulsid, ehmusid ning ütlesid: \"Eks see ole seesama, kes Jeruusalemmas rüüstas selle nime appihüüdjaid ja on siia tulnud selleks, et neid kinni siduda ja viia ülempreestrite kätte?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 22, 'Aga Saulus läks järjest vägevamaks ja ajas juudid, kes Damaskuses elasid, kihama ning tõestas, et Jeesus on Kristus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 23, 'Mõne päeva pärast pidasid juudid nõu teda ära tappa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 24, 'Aga Saulus sai teada nende salanõu. Ent nad valvasid päeva ja öö väravais, et teda tappa;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 25, 'kuid jüngrid võtsid ta öösel ja aitasid ta üle müüri ning lasksid ta korviga alla.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 26, 'Kui siis Saulus jõudis Jeruusalemma, püüdis ta liituda jüngrite hulka, aga nemad kõik kartsid teda ega uskunud, et ta on jünger.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 27, 'Kuid Barnabas võttis ta vastu ja viis ta Apostlite juurde ja rääkis neile, kuidas tema teel olles oli Issandat näinud ning temaga kõnelnud ja kuidas ta Damaskuses oli avalikult kuulutanud Evangeeliumi Jeesuse nimel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 28, 'Nii ta jäi nende juurde sisse ja välja käima Jeruusalemmas ja kuulutas avalikult Evangeeliumi Issanda nimel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 29, 'Ta rääkis ka ja ajas juttu kreekakeelsete juutidega; aga need püüdsid teda tappa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 30, 'Aga kui vennad seda teada said, viisid nad ta Kaisareasse ja saatsid ta sealt Tarsosesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 31, 'Siis oli nüüd kogudusel rahu kogu Juuda- ja Galilea- ja Samaariamaal, ja see kasvas ja täienes ja edenes Issanda kartuses ning Püha Vaimu julgustusel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 32, 'Aga sündis, kui Peetrus kõigis paigus käimas oli, et ta tuli ka pühade juurde, kes elasid Lüddas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 33, 'Seal ta leidis ühe inimese, Aineas nimi, kes oli kaheksa aastat olnud voodis maas ja halvatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 34, 'Ja Peetrus ütles talle: \"Aineas! Jeesus Kristus teeb su terveks: tõuse üles ja sea oma ase korda!\" Ja sedamaid ta tõusis üles.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 35, 'Ja teda nägid kõik, kes elasid Lüddas ja Saaronis; ja need pöördusid Issanda poole.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 36, 'Aga Joppes oli keegi naisjünger, nimega Tabiita, mis meie keeli tähendab Hirv. See oli rikas häist tegudest ja armastusandidest, mida ta tegi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 37, 'Ja neil päevil sündis, et ta jäi haigeks ja suri. Ja nad pesid teda ja panid ta ülemisse tuppa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 38, 'Aga Lüdda oli Joppe lähedal. Kui jüngrid kuulsid, et Peetrus seal on, läkitasid nad kaks meest tema juurde ja palusid teda, et ta viibimata tuleks nende juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 39, 'Siis Peetrus tõusis ja läks nendega. Ja kui ta sinna jõudis, viisid nad ta ülemisse tuppa, ja kõik lesknaised astusid tema ümber, nutsid ja näitasid kuubi ja riideid, mis Hirv nende juures olles oli teinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 40, 'Aga Peetrus ajas kõik välja, heitis põlvili ja palus Jumalat ning pöördus surnu poole ja ütles: \"Tabiita, tõuse üles!\" Siis avas see oma silmad, ja kui ta Peetrust nägi, tõusis ta istuma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 41, 'Aga Peetrus andis temale käe ja tõstis ta püsti. Siis ta kutsus sisse pühad ja lesed ja esitas teda neile elavana.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 42, 'Aga see sai teatavaks kogu Joppes ja paljud uskusid Issandasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 43, 'Ja Peetrus jäi mõneks päevaks Joppesse ühe nahkru Siimona juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 1, 'Kaisarea linnas oli üks mees, Korneelius nimi, selle väesalga pealik, mida kutsuti Itaalia väesalgaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 2, 'Tema oli vaga ja jumalakartlik kogu oma perega ja jagas rahvale palju ande ning palus alati Jumalat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 3, 'See nägi nägemuses selgesti, arvata üheksandal päevatunnil, et Jumala Ingel tuli sisse tema juurde ning ütles talle: \"Korneelius!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 4, 'Tema aga vaatas Inglile otsa, lõi kartma ning ütles: \"Isand, mis on?\" See ütles talle: \"Sinu palved ja su annid on tõusnud Jumala ette sind meelde tuletama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 5, 'Läkita nüüd mehi Joppesse ja lase kutsuda Siimon, kelle lisanimi on Peetrus;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 6, 'tema asub nahkur Siimona juures, kelle maja on mererannas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 7, 'Kui siis Ingel oli ära läinud, kes Korneeliusega rääkis, kutsus ta kaks oma perest ja ühe vaga sõjamehe neist, kes alati ta juures olid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 8, 'rääkis neile kõik ära ning läkitas nad Joppesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 9, 'Aga järgmisel päeval, kui nad teel olles lähenesid linnale, läks Peetrus katusele palvetama kuuendal tunnil.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 10, 'Ja temale tuli nälg ning ta tahtis süüa. Aga kui talle rooga valmistati, jäi ta otsekui enesest ära');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 11, 'ning nägi taeva avatud olevat ja enese juurde alla tulevat anuma, otsekui suure linase riide, mida nelja nurka pidi alla lasti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 12, 'Selle sees oli kõiksugu neljajalgseid ja roomajaid maa elajaid ja taeva linde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 13, 'Ja temale ütles hääl: \"Tõuse, Peetrus, verista ja söö!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 14, 'Aga Peetrus ütles: \"Ei ilmaski, Issand, sest ma pole veel söönud seda, mis on keelatud ja roojane!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 15, 'Ja hääl ütles taas teist korda temale: \"Mis Jumal on puhastanud, seda sina ära arva keelatuks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 16, 'Ja see sündis kolm korda, ja astja võeti jälle üles taevasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 17, 'Aga kui Peetrus oli iseeneses kahevahel, mis tema nähtud nägemus küll võiks tähendada, vaata, siis olid mehed, keda Korneelius oli läkitanud, küsitledes leidnud Siimona maja ja seisid värava taga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 18, 'Nad hüüdsid ning küsisid, kas Siimon, kelle lisanimi on Peetrus, on seal võõrsil.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 19, 'Ent kui Peetrus alles oma meeles mõtles nägemusele, ütles Vaim temale: \"Vaata, kolm meest otsivad sind!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 20, 'Aga tõuse üles ja astu alla ning mine nendega ilma kaksipidi mõtlemata, sest Mina olen nad läkitanud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 21, 'Siis Peetrus tuli alla meeste juurde ning ütles: \"Vaata, mina olen see, keda te otsite! Mis asja pärast te olete tulnud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 22, 'Nemad vastasid: \"Pealik Korneelius, õige ja jumalakartlik mees, kellel on hea tunnistus kogu juuda rahvalt, on pühalt Inglilt saanud sõna, et ta sind peab kutsuma oma kotta ja kuulama sinu kõnet!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 23, 'Siis ta kutsus nad sisse ja pidas neid külalistena. Aga järgmisel päeval läks Peetrus nendega ära ja mõned vennad, kes Joppest olid, läksid ühes temaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 24, 'Järgmisel päeval nad saabusid Kaisareasse. Ja Korneelius ootas neid ja kutsus kokku oma sulased ja parimad sõbrad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 25, 'Ja kui Peetrus oli sisse astunud, läks Korneelius talle vastu ning heitis tema jalge ette maha ja kummardas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 26, 'Aga Peetrus tõstis ta üles ja ütles: \"Tõuse üles, ka mina ise olen inimene!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 27, 'Ja kui ta temaga oli kõnelnud, läks ta sisse ja leidis eest suure hulga inimesi');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 28, 'ning ütles neile: \"Te teate, et juuda mehel ei ole luba sõbrustada muulasega või minna tema juurde, aga Jumal on mulle näidanud, et ühtki inimest ei tohi pidada halvaks ega rüvedaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 29, 'Sellepärast olen ma ka tõrkumata tulnud, kui mind kutsuti. Siis küsin ma nüüd: mis asja pärast te olete mind kutsunud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 30, 'Korneelius ütles: \"Neli päeva tagasi ma olin otse selsamal üheksandal tunnil oma kojas palvetamas ja vaata, üks mees seisis minu ees hiilgavais riideis');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 31, 'ning ütles: Korneelius, sinu palvet on kuuldud ja su annid on Jumalale meelde tulnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 32, 'Siis läkita nüüd Joppesse ja kutsu Siimon, keda lisanimega hüütakse Peetruseks; tema on võõrsil nahkur Siimona majas mererannas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 33, 'Siis ma läkitasin sedamaid sinu juurde, ja sa oled hästi teinud, et tulid. Nüüd me oleme kõik siin üheskoos Jumala ees kuulamas kõike, mis Jumal sind on käskinud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 34, 'Siis Peetrus avas oma suu ning ütles: \"Ma mõistan tõesti, et Jumal ei tee vahet isikute vahel,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 35, 'vaid kõige rahva seast on see, kes teda kardab ja teeb õigust, tema meele järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 36, 'Selle sõna ta on läkitanud Iisraeli lastele, kuulutades Evangeeliumi rahust Jeesuse Kristuse läbi, Kes on kõikide Issand.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 37, 'Te teate seda asja, mis on sündinud tervel Juudamaal alates Galileast pärast ristimist, mida Johannes kuulutas,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 38, 'kuidas Jumal Jeesuse Naatsaretist oli võidnud Püha Vaimu ja väega ja kuidas Tema käis mööda maad ja tegi head ning parandas kõiki, kelle üle kurat oli saanud võimuse; sest Jumal oli Temaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 39, 'Ja meie oleme kõigi nende asjade tunnistajad, mis Ta on teinud juutide maal ja Jeruusalemmas; ja nad poosid Tema ristipuule ning surmasid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 40, 'Selle on Jumal üles äratanud kolmandal päeval ja on Teda lasknud saada ilmsiks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 41, 'mitte kõigele rahvale, vaid Jumala ennevalitud tunnistajaile, meile, kes Temaga oleme söönud ja joonud, pärast seda kui Ta oli surnuist üles tõusnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 42, 'Ja Tema on meid käskinud rahvale kuulutada ja tunnistada, et Tema on Jumala poolt seatud elavate ja surnute kohtumõistja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 43, 'Temast tunnistavad kõik prohvetid, et Tema nime läbi igaüks, kes usub Temasse, saab pattude andeksandmise!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 44, 'Kui Peetrus neid sõnu alles rääkis, langes Püha Vaim kõikide peale, kes seda sõna kuulsid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 45, 'Ja usklikud ümberlõigatute hulgast, niipalju kui neid ühes Peetrusega oli tulnud, ehmusid, et Püha Vaimu and valati ka paganate peale,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 46, 'sest nad kuulsid neid võõraid keeli rääkivat ja Jumalat väga ülistavat. Siis Peetrus ütles:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 47, '\"Kas keegi võib vett keelata, et ei ristitaks neid, kes on saanud Püha Vaimu nõnda nagu meiegi?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 48, 'Ja ta käskis neid ristida Issanda nimesse. Siis nad palusid teda, et ta jääks mõneks päevaks nende juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 1, 'Siis Apostlid ja vennad, kes Juudamaal siin ja seal elasid, said kuulda, et ka paganad olid vastu võtnud Jumala sõna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 2, 'Aga kui Peetrus tuli Jeruusalemma, vaidlesid temaga ümberlõigatud');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 3, 'ning ütlesid: \"Sina oled läinud sisse ümberlõikamatute juurde ja oled söönud nendega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 4, 'Aga Peetrus hakkas rääkima ning seletas neile asja selle järjekorras nõnda:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 5, '\"Mina olin Joppe linnas palvetamas ja nägin otsekui enesest ära olles nägemuse: seal tuli alla anum, nagu suur linane riie, mida nelja nurka pidi lasti alla taevast, ja see tuli aiva minu juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 6, 'Kui ma seda juureldes vaatasin, nägin ma maa neljajalgseid elajaid ja metsalisi ja roomajaid ja taeva linde,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 7, 'ja ma kuulsin ka häält mulle ütlevat: tõuse üles, Peetrus, verista ja söö!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 8, 'Aga ma ütlesin: ei ilmaski, Issand; sest minu suhu ei ole elades saanud midagi keelatut ehk roojast!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 9, 'Siis vastas mulle teist korda hääl taevast: mis Jumal on puhastanud, seda ära sina arva keelatuks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 10, 'Ja see sündis kolm korda. Ja kõik tõmmati jälle üles taevasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 11, 'Ja vaata, samal hetkel seisid maja ees, kus me olime, kolm meest, kes olid Kaisareast läkitatud minu juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 12, 'Ent Vaim ütles mulle, et ma läheksin ühes nendega ega mõtleks kaksipidi. Siis tulid ka need kuus venda minuga, ja me läksime selle mehe kotta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 13, 'Ja ta kuulutas meile, kuidas ta oli näinud Inglit oma majas seisvat ja ta talle ütlevat: läkita Joppesse ja kutsu Siimon, keda hüütakse lisanimega Peetruseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 14, 'Tema räägib sulle sõnu, mille läbi sina ja kõik su pere saate õndsaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 15, 'Aga kui ma hakkasin kõnelema, langes Püha Vaim nende peale, nagu meiegi peale alguses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 16, 'Siis meenus mulle Issanda sõna, kuidas Ta ütles: Johannes ristis küll veega, aga teid ristitakse Püha Vaimuga!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 17, 'Kui nüüd Jumal neile on andnud sama anni kui meilegi, kes usume Issandasse Jeesusesse Kristusesse, kes siis olin mina, et oleksin võinud Jumalat keelata?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 18, 'Aga kui nad seda kuulsid, jäid nad vait ja andsid Jumalale au, öeldes: \"Siis on Jumal ka paganaile andnud meeleparanduse eluks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 19, 'Aga need, kes olid hajutatud viletsuse läbi, mis oli tekkinud Stefanose pärast, käisid maad läbi Foiniikiani ja Küprose saareni ja Antiookiani ega kuulutanud sõna kellelegi muule kui ainult juutidele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 20, 'Aga nende hulgas olid mõned Küprose ja Küreene mehed; need läksid Antiookiasse ning rääkisid ka kreeklastele, jutlustades Issandat Jeesust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 21, 'Ja Issanda käsi oli nendega ning suur hulk uskus ja pöördus Issanda poole.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 22, 'Aga teade neist kostis Jeruusalemmas oleva koguduse kõrvu, ja nad läkitasid Barnabase Antiookiasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 23, 'Kui ta sinna jõudis ja nägi Jumala armu, sai ta rõõmsaks ja manitses kõiki jääma kindla südamega Issanda juurde;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 24, 'sest ta oli õige mees, täis Püha Vaimu ning usku, ja palju rahvast koguti Issandale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 25, 'Siis ta läks Tarsosesse Saulust otsima, ja kui ta tema leidis, tõi ta tema Antiookiasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 26, 'Ja nemad käisid terve aasta koos koguduses ja õpetasid suure hulga rahvast. Ja Antiookias hakati kõige enne nimetama jüngreid kristlasteks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 27, 'Noil päevil tuli Jeruusalemmast prohveteid Antiookiasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 28, 'Ja üks neist, Aagabus nimi, tõusis ja andis Vaimu läbi teada, et suur nälg oli tulemas üle kogu maailma. Ja see tuligi keiser Klaudiuse ajal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 29, 'Siis jüngrid võtsid nõuks igaüks sedamööda, kuidas kellelgi jõudu oli, saata abi vendadele, kes elasid Juudamaal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 30, 'Seda nad tegidki ja saatsid annid vanemate kätte Barnabase ja Sauluse käe läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 1, 'Aga sel ajal pistis kuningas Heroodes käe mõnede külge kogudusest, et neile paha teha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 2, 'Ja ta tappis mõõgaga Jakoobuse, Johannese venna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 3, 'Ja nähes, et see oli juutidele meelt mööda, võttis ta ette vangistada ka Peetruse. Sel ajal olid parajasti hapnemata leibade pühad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 4, 'Ta võttis tema kinni ja heitis ta vangitorni ning andis tema nelja neljamehelise sõjameeste salga kätte hoida, kavatsedes teda pärast paasapüha tuua rahva ette.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 5, 'Nii peeti siis Peetrust vangis, aga kogudus tegi väsimata palvet Jumala poole tema eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 6, 'Aga kui Heroodes tahtis teda tuua kohtu ette, magas Peetrus sel ööl kahe sõjamehe vahel kahe ahelaga seotult, ja vahid ukse ees valvasid vangihoonet.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 7, 'Ja vaata, Issanda Ingel seisis seal ja valgus paistis seal toas! Ja Ingel tõukas Peetruse külge, äratas ta üles ning ütles: \"Tõuse usinasti üles!\" Ja ta ahelad langesid käte ümbert maha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 8, 'Ja Ingel ütles talle: \"Pane vöö vööle ja kingad jalga!\" Tema tegi nõnda. Veel ta ütles talle: \"Pane kuub selga ja tule mu järel!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 9, 'Ja ta tuli välja ja läks ta järele ega teadnud, et on ilmsi see, mis sünnib ingli kaudu, vaid mõtles nägevat nägemust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 10, 'Aga kui nad jõudsid mööda esimesest ja teisest vahist, tulid nad raudvärava ette, mis viis linna. See läks iseenesest neile lahti ja nad tulid välja ja läksid edasi üht tänavat mööda. Ja Ingel lahkus äkitselt temast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 11, 'Kui siis Peetrus teadlikuks sai, ütles ta: \"Nüüd ma tean tõesti, et Issand on läkitanud Oma Ingli ja on mind päästnud Heroodese käest ja kõigest, mida juuda rahvas ootas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 12, 'Ja märgates, kus ta on, tuli ta Maarja, Johannese, teise nimega Markuse ema maja juurde, kuhu mitmed olid kokku tulnud palvetama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 13, 'Ent kui Peetrus koputas jalgväravale, tuli tüdruk, nimega Roode, välja kuulatama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 14, 'Kui ta Peetruse hääle ära tundis, ei teinud ta väravat lahti rõõmu pärast, vaid jooksis sisse ja teatas, et Peetrus värava taga seisvat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 15, 'Nemad ütlesid talle: \"Sa jampsid!\" Aga tema tunnistas kindlasti, et see on nõnda. Nad ütlesid: \"See on tema Ingel!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 16, 'Aga Peetrus jäi veel koputama. Ja kui nad avasid, nägid nad teda ja ehmusid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 17, 'Siis ta viitas käega neile, et nad jääksid vait, ja jutustas neile, kuidas Issand ta oli vangihoonest välja viinud, ning ütles: \"Kuulutage seda Jakoobusele ja vendadele!\" Ja ta väljus ning läks teise paika.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 18, 'Aga kui valgeks läks, ei olnud sõjameestel pisut muret sellest, mis Peetrusega oli sündinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 19, 'Ja kui Heroodes teda otsis ega leidnud, laskis ta valvuritele kohut mõista ja käskis nad ära hukata. Ise ta läks siis Juudamaalt Kaisareasse ja viibis seal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 20, 'Heroodes aga kandis viha tüüroslaste ja siidonlaste vastu. Need tulid ühel meelel tema juurde ja meelitasid Blastust, kuninga kambriülemat, ja palusid rahu. Sest nende maa sai oma toiduse kuninga maast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 21, 'Siis pani Heroodes määratud päeval kuninglikud riided selga ja istus kohtujärjele ja pidas neile kõnet;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 22, 'aga rahvas kisendas: \"See on Jumala, aga mitte inimese hääl!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 23, 'Aga sedamaid lõi teda Issanda Ingel, sest et ta ei andnud au Jumalale; ja ussid sõid ta ära ja ta heitis hinge.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 24, 'Aga Jumala sõna kasvas ja levis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 25, 'Ja Barnabas ja Saulus tulid tagasi Jeruusalemmast, kui nad olid täitnud oma ülesande, ning võtsid enestega ühes ka Johannese, keda hüüti lisanimega Markuseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 1, 'Aga Antiookias olevas koguduses oli prohveteid ja õpetajaid: Barnabas ja Siimon, keda hüüti Nigeriks, ja Luukius Küreenest ja Maanaen, kes nelivürst Heroodesega ühes oli üles kasvatatud, ja Saulus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 2, 'Kui need Issandat teenisid ja paastusid, ütles Püha Vaim: \"Eraldage Mulle Barnabas ja Saulus tööle, milleks Mina nad olen kutsunud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 3, 'Siis nad paastusid ja palusid Jumalat ja panid oma käed nende peale ja saatsid nad teele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 4, 'Need, Püha Vaimu poolt läkitatud, tulid nüüd Seleukiasse ja sõitsid sealt laevaga Küprosesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 5, 'Ja kui nad Salamisesse olid jõudnud, kuulutasid nad Jumala sõna juutidele kogudusekodades; neil oli ka Johannes käsiliseks ühes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 6, 'Aga kui nad olid saare läbi käinud Paafosest saadik, kohtasid nad üht juudi nõida ja valeprohvetit, Barjeesus nimi;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 7, 'see oli maavalitseja Sergius Pauluse juures, kes oli mõistlik mees. Tema kutsus Barnabase ja Sauluse enese juurde ja püüdis kuulda Jumala sõna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 8, 'Aga neile pani vastu Elümas, nõid - sest seda tähendab nimi tõlkes - püüdes maavalitsejat ära pöörata usust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 9, 'Kuid Saulus, keda hüütakse ka Pauluseks, täis Püha Vaimu, vaatas teravasti talle otsa');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 10, 'ning ütles: \"Oh sind, kes oled täis kõike kavalust ja kõike tigedust, sa kuradi laps, sa kõige õiguse vaenlane, kas sa ei lakka kõverdamast Issanda sirgeid teid?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 11, 'Ja nüüd, vaata, Issanda käsi tuleb sinu peale ja sa jääd pimedaks ega näe päikest tükil ajal!\" Ja sedamaid langes ta peale sõgedus ning pimedus, ja ta käis ümber ja otsis talutajaid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 12, 'Kui maavalitseja nägi, mis sündis, uskus ta ja ehmus üliväga Issanda õpetusest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 13, 'Ja kui need, kes olid kaasas Paulusega, ära läksid Paafosest, saabusid nad Pergesse, Pamfüüliamaale. Seal Johannes lahkus neist ja läks tagasi Jeruusalemma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 14, 'Aga nemad läksid edasi Pergest ja jõudsid Pisiidia Antiookiasse ning läksid kogudusekotta hingamispäeval ja istusid maha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 15, 'Ja pärast seda, kui olid loetud käsk ja prohvetid, läkitasid kogudusekoja ülemad neile ütlema: \"Mehed, vennad, kui teil on öelda mõni manitsussõna rahvale, siis öelge!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 16, 'Siis Paulus tõusis üles ja viitas käega neile ning ütles: \"Iisraeli mehed ja teie, kes kardate Jumalat, kuulge!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 17, 'Selle rahva, Iisraeli, Jumal valis ära meie esiisad ja ülendas rahva, kui nad võõrastena olid Egiptuses, ja tõi nad sealt välja kõrgele tõstetud käsivarrega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 18, 'ja sallis nende kombeid ligi nelikümmend aastat kõrbes');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 19, 'ja hävitas seitse rahvast Kaananimaal ja jagas nende maa neile pärandiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 20, 'See kõik sündis umbes neljasaja viiekümne aasta jooksul. Pärast seda Ta andis neile kohtumõistjaid prohvet Saamueli ajani.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 21, 'Ja selle järele nad palusid enestele kuningat. Ja Jumal andis neile Sauli, Kiisi poja, Benjamini suguharust mehe. Nii möödus nelikümmend aastat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 22, 'Ja kui Ta selle oli tagandanud, äratas Ta neile kuningaks Taaveti, kellest Tema ka tunnistades ütles: \"Ma olen leidnud Taaveti, Jesse poja, Oma südamekohase mehe, kes teeb kõik Mu tahtmist mööda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 23, 'Selle soost on Jumal äratanud Oma tõotuse järgi Jeesuse Iisraeli Päästjaks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 24, 'siis kui otse enne Tema ilmumist Johannes kuulutas meeleparanduse ristimist kõigele Iisraeli rahvale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 25, 'Ent kui Johannes oli oma elukäiku lõpetamas, ütles tema: \"Mina ei ole see, kelleks te mind peate; aga vaata, pärast mind tuleb See, Kelle kinga jalast päästma ma väärt ei ole!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 26, 'Mehed, vennad, Aabrahami soo lapsed ja need, kes teie seast kardavad Jumalat, meile on selle pääste sõna läkitatud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 27, 'Sest Jeruusalemma elanikud ja nende ülemad, Jeesust mitte tundes, saatsid hukkamõistmisega täide prohvetite sõnad, mida igal hingamispäeval loetakse;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 28, 'ja ehk nad küll ei leidnud ühtki surmasüüd, palusid nad Pilaatust, et Ta surmataks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 29, 'Ja kui nad olid täide saatnud kõik, mis Temast oli kirjutatud, võtsid nad Ta maha puu pealt ja panid hauda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 30, 'Aga Jumal äratas Tema surnuist üles!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 31, 'Ja Tema ilmus palju päevi neile, kes Temaga olid tulnud Galileast Jeruusalemma ja kes nüüd on Ta tunnistajad rahva ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 32, 'Ja me kuulutame teile selle hea sõnumi, tõotuse, mis anti meie esiisadele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 33, 'et Jumal selle on täitnud meile, nende lastele, äratades üles Jeesuse, nagu ka teises laulus on kirjutatud: \"Sina oled Mu Poeg, täna Ma sünnitasin Sind!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 34, 'Et Ta Tema on surnuist üles äratanud, nii et Tal iialgi ei ole tarvis kõduneda, sellest on Ta öelnud nõnda: \"Mina täidan teile need Taavetile antud pühad ja kindlad tõotused!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 35, 'Sellepärast Ta ütleb ka teises paigas: \"Sina ei lase Oma Püha näha kõdunemist!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 36, 'Sest kui Taavet oma rahvapõlve ajal oli teinud Jumala tahte järgi, uinus ta magama ja pandi oma esiisade juurde ja nägi kõdunemist;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 37, 'kuid See, Kelle Jumal üles äratas, ei näinud kõdunemist!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 38, 'Sellepärast olgu teil nüüd teada, mehed, vennad, et teile Selle läbi kuulutatakse pattude andeksandmist,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 39, 'ja et igaüks, kes usub, mõistetakse õigeks Tema sees kõigest sellest, millest te ei võinud õigeks saada Moosese käsuõpetuse kaudu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 40, 'Katsuge nüüd, et teid ei tabaks, mis on öeldud prohvetite kirjades:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 41, '\"Vaadake, te põlgajad, ja pange imeks ja minge hukka! Sest Ma teen teie päevil teo; teo, mida te ei usuks, kui keegi teile sellest jutustaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 42, 'Kui nad siis väljusid, paluti neid järgmisel hingamispäeval neile rääkida neid sõnu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 43, 'Aga kui kogudusekojast oli laiali mindud, järgis palju juute ja juudiusku pöördunud jumalakartlikke Paulust ja Barnabast, kes nendega kõnelesid ja neid õhutasid jääma Jumala armusse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 44, 'Aga järgmisel hingamispäeval kogunes peaaegu kõik linn kuulama Jumala sõna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 45, 'Kui siis juudid nägid rahvahulka, said nad täis viha ja vaidlesid vastu Pauluse kõnele ning pilkasid Jumalat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 46, 'Aga Paulus ja Barnabas kõnelesid julgesti ning ütlesid: \"Teile pidi Jumala sõna esiti räägitama; kuid et te selle enesest ära lükkate ega arva endid olevat igavest elu väärt, siis me pöördume paganate poole.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 47, 'Sest nõnda on meid Issand käskinud: Mina olen Sind pannud valguseks paganaile, et Sa oleksid päästeks ilmamaa otsani!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 48, 'Kui paganad seda kuulsid, said nad rõõmsaks ja ülistasid Issanda sõna ja uskusid, nii paljud kui olid määratud igaveseks eluks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 49, 'Ja Issanda sõna levis üle kogu maakonna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 50, 'Aga juudid ässitasid jumalakartlikke kõrgema seisuse naisi ja linna ülemaid ning äratasid vaenu Pauluse ja Barnabase vastu ja ajasid nad välja oma aladelt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 51, 'Aga nemad puistasid oma jalgade põrmu nende peale ja tulid Ikoonioni.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 52, 'Ja jüngrid täitusid rõõmu ja Püha Vaimuga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 1, 'Aga Ikoonionis sündis, et nad üheskoos läksid juutide kogudusekotta ja kõnelesid nõnda, et niihästi suur hulk juute kui kreeklasi sai usklikuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 2, 'Aga uskmatud juudid kihutasid üles ja ärritasid paganate meeled vihale vendade vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 3, 'Siis nad viibisid seal kauemat aega, kuulutades julgesti Issandat, Kes andis tunnistuse Oma armusõnale ja laskis sündida tunnustähti ja imetegusid nende käte läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 4, 'Aga linna rahvas jagunes kaheks: ühed olid juutide, teised Apostlite poolt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 5, 'Kui siis paganad ja juudid oma ülematega ühel nõul tahtsid tulla nende kallale, et neid teotada ja kividega visata,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 6, 'said nad seda teada ja põgenesid Lükaoonia linnadesse, Lüstrasse ja Derbesse ja nende ümbrusesse');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 7, 'ja kuulutasid seal Evangeeliumi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 8, 'Ja Lüstras istus mees, võimetu seisma, oma emaihust jalutu, kes ei olnud elades kõndinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 9, 'See kuulis Pauluse kõnet. Ja kui Paulus teda silmas ning nägi, et tal oli usku terveks saada,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 10, 'ütles ta suure häälega: \"Tõuse püsti oma jalgele!\" Ja ta kargas üles ja kõndis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 11, 'Aga kui rahvas nägi, mis Paulus oli teinud, siis nad tõstsid oma häält ning ütlesid lükaoonia keeli: \"Jumalad on inimeste sarnastena alla tulnud meie juurde!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 12, 'Ja nad nimetasid Barnabast Zeusiks ja Paulust Hermeseks, sest tema oli see, kes kõneles.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 13, 'Ja Zeusi preester linna aguli templist tõi härgi ja lillepärgi värava ette ja tahtis ohverdada ühes rahvaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 14, 'Aga kui Apostlid Barnabas ja Paulus seda kuulsid, rebestasid nad oma riided, kargasid hüüdes rahva sekka');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 15, 'ning ütlesid: \"Mehed, miks te seda teete? Meie oleme niisamasugused nõdrad inimesed nagu teiegi ja kuulutame teile Evangeeliumi, et te tühjest asjust pöörduksite elava Jumala poole, Kes on teinud taeva ja maa ja mere ning kõik, mis nende sees on,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 16, 'Kes endisel põlvel on lasknud kõik rahvad käia Omi teid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 17, 'ja siiski ei ole jätnud andmata enesest tunnistust, vaid on meile head teinud, ja on taevast meile andnud vihma ja head viljalist aega ja rooga ning on täitnud meie südame rõõmuga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 18, 'Nii kõneldes nad vaevalt vaigistasid rahva, et see neile ei ohverdaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 19, 'Aga Antiookiast ja Ikoonionist tuli sinna juute ja need meelitasid hulga eneste poole ja viskasid Paulust kividega ning vedasid ta linnast välja, arvates, et ta on surnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 20, 'Aga kui jüngrid olid tema ümber tulnud, tõusis ta üles ja läks linna. Ja järgmisel päeval ta läks ühes Barnabasega Derbesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 21, 'Ja kui nad selles linnas olid kuulutanud Evangeeliumi ja saanud mõned jüngreiks, läksid nad tagasi Lüstrasse, Ikoonioni ja Antiookiasse ning');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 22, 'kinnitasid jüngrite hingi, manitsedes neid jääma ususse ning seletades, et meil mitme viletsuse kaudu tuleb minna Jumala Riiki.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 23, 'Ja kui nad neile igas koguduses käte pealepanemisega olid seadnud vanemad, jätsid nad paastudes ja palvetades nemad Issanda hooleks, Kellesse need nüüd uskusid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 24, 'Siis nad käisid läbi Pisiidiamaa ja saabusid Pamfüüliasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 25, 'Ja kui nad Perges olid sõna kuulutanud, läksid nad Ataaliasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 26, 'Sealt nad purjetasid Antiookiasse, kust nad olid väljunud Jumala armu hooleks antuina sellele tööle, mille nad olid lõpetanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 27, 'Ja kui nad olid saabunud, kogusid nad koguduse kokku ja kuulutasid, mis suuri asju Jumal oli teinud, olles nendega, ja et ta oli avanud paganaile usu ukse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 28, 'Ja nad viibisid seal kauemat aega jüngrite juures.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 1, 'Ja mõned, kes olid tulnud Juudamaalt, õpetasid vendi nõnda: \"Kui te ei lase endid ümber lõigata Moosese kombe järgi, siis te ei või õndsaks saada!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 2, 'Kui sellest tõusis lahkmeel ja Paulusel ja Barnabasel ei olnud nendega mitte pisut vaidlemist, siis tehti otsuseks, et Paulus ja Barnabas ning mõned muud nende seast lähevad Jeruusalemma Apostlite ja vanemate juurde selle tüliküsimuse pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 3, 'Siis kogudus saatis nad teele, ja nemad läksid Foiniikiast ja Samaariast läbi ning jutustasid paganate pöördumisest ja tegid sellega suure rõõmu kõigile vendadele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 4, 'Kui nad saabusid Jeruusalemma, võtsid kogudus, Apostlid ja vanemad nad vastu. Ja nemad jutustasid, mis suuri asju Jumal oli teinud, olles nendega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 5, 'Aga mõned variseride usulahust, kes olid saanud usklikuks, astusid esile ning ütlesid: \"Neid tuleb ümber lõigata ja käskida pidada Moosese käsku!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 6, 'Siis Apostlid ja vanemad tulid kokku seda asja arutama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 7, 'Ja kui asjast tekkis palju vaidlust, tõusis Peetrus ning ütles neile: \"Mehed, vennad, teie teate, et Jumal juba ammusest ajast teie seast on ära valinud mind, et minu suu läbi paganad kuuleksid Evangeeliumi sõna ja usuksid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 8, 'Ja südametundja Jumal on neile andnud tunnistuse, andes neile Püha Vaimu otsekui meilegi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 9, 'ega ole teinud mingisugust vahet meie ja nende vahel, puhastades nende südamed usu läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 10, 'Miks te siis nüüd kiusate Jumalat, et panete jüngrite kaela ikke, mida meie esiisad ega meie ei ole suutnud kanda?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 11, 'Ent me usume Issanda Jeesuse Kristuse armu läbi saada õndsaks, otse nõnda kui nemadki!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 12, 'Aga kogu rahvahulk jäi vait ja nad kuulasid, kuidas Barnabas ja Paulus jutustasid, mis suuri tunnustähti ja imesid Jumal paganate seas oli teinud nende läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 13, 'Kui nad siis olid jutu lõpetanud, võttis Jakoobus sõna ning ütles: \"Mehed, vennad, kuulge mind!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 14, 'Siimon on seletanud, kuidas Jumal esiti võttis eesmärgiks võita Oma nimele rahvas paganate seast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 15, 'Sellega sobivad ühte prohvetite sõnad, nagu on kirjutatud:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 16, '\"Pärast seda Ma tulen jälle ja ehitan üles Taaveti lagunenud maja ning parandan ära tema varemed ja püstitan ta uuesti,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 17, 'et muudki inimesed otsiksid Issandat ja kõik rahvad, kelle üle on nimetatud Minu nimi, ütleb Issand, Kes seda teeb,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 18, 'mis on teada igavikust.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 19, 'Sellepärast arvan mina, et neile, kes paganate seast pöörduvad Jumala poole, ei peaks tehtama raskusi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 20, 'vaid neile kirjutatagu, et nad hoiduksid väärjumalate rüveduse, porduelu, lämbunu ja vere eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 21, 'Sest Moosesel on vanast ajast kõigis linnades küllalt neid, kes teda kuulutavad, ning teda loetakse igal hingamispäeval kogudusekodades.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 22, 'Siis Apostlid ja vanemad ühes kõige kogudusega arvasid heaks valida endi seast mehed ning läkitada Antiookiasse ühes Pauluse ja Barnabasega, nimelt Juuda, lisanimega Barsabas, ja Siilase, kes olid juhatajad vendade seas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 23, 'Nendega nad saatsid järgmise kirja: \"Meie, Apostlid ja vanemad ja vennad, saadame tervisi vendadele paganate seast, kes elavad Antiookia- ja Süüria- ja Kiliikiamaal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 24, 'Olles kuulnud, et mõned meie seast on välja läinud ja on teid teinud kõnedega rahutuks ja on saatnud teie hinged kitsikusse, ilma et meie neid oleksime käskinud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 25, 'arvasime üksmeelselt heaks valida mehed ja läkitada need teie juurde ühes meie armsate vendade Barnabase ja Paulusega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 26, 'meestega, kes oma elu on pannud kaalule meie Issanda Kristuse nime pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 27, 'Nii me oleme siis läkitanud Juuda ja Siilase, et nad teile suusõnal teataksid sedasama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 28, 'Sest Püha Vaim ja meie oleme arvanud heaks, et teie peale ei tohi panna enam ühtki koormat kui aga need väga tarvilised määrused:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 29, 'hoiduda ebajumalate ohvriliha ja vere ja lämbunu ja porduelu eest. Kui te nendest hoidute, teete hästi. Jääge Jumalaga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 30, 'Nii nad saadeti teele ja nad saabusid Antiookiasse. Seal nad kogusid rahvahulga kokku ja andsid kirja neile kätte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 31, 'Kui nad seda olid lugenud, said nad rõõmsaks selle troostist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 32, 'Aga Juudas ja Siilas, kes ka ise olid prohvetid, manitsesid vendi mitme sõnaga ja kinnitasid neid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 33, 'Ja kui nad seal mõne aja olid viibinud, saadeti nad rahuga vendade juurest nende juurde, kes nad olid läkitanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 34, 'Ja Siilas arvas heaks jääda nende juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 35, 'Aga Paulus ja Barnabas viibisid Antiookias ning õpetasid ja kuulutasid ühes paljude teistega Issanda armuõpetuse sõna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 36, 'Mõne päeva pärast ütles Paulus Barnabasele: \"Lähme jälle vaatama vendi kõiki linnu mööda, kus me oleme kuulutanud Issanda sõna, kuidas nende käsi käib!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 37, 'Ja Barnabas andis nõu võtta kaasa Johannes, keda hüütakse Markuseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 38, 'Aga Paulus arvas heaks mitte võtta enesega teda, kes neist oli lahkunud Pamfüülias ega olnud ühes nendega läinud tööle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 39, 'Sellest tekkis äge vaidlus, nii et nad üksteisest lahkusid ja et Barnabas võttis enese juurde Markuse ning sõitis laevaga Küprosesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 40, 'Aga Paulus valis Siilase ja läks teele, ja vennad andsid ta Jumala armu hooleks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 41, 'Ja ta käis läbi Süüria ja Kiliikia kinnitades kogudusi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 1, 'Ja ta saabus Derbesse ja Lüstrasse. Ja vaata, seal oli üks jünger, Timoteos nimi, uskliku juudi naise poeg; aga ta isa oli kreeklane.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 2, 'Sellele andsid Lüstras ja Ikoonionis olijad vennad hea tunnistuse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 3, 'Teda tahtis Paulus enesele saatjaks teele. Ja ta võttis ning lõikas ta ümber juutide pärast, kes olid neis paigus; sest nad teadsid kõik, et tema isa oli kreeklane.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 4, 'Aga kui nad käisid linnast linna, käskisid nad usklikke pidada neid seadmisi, mis Apostlid ja vanemad Jeruusalemmas olid otsustanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 5, 'Siis kinnitati kogudusi usus ja liikmete arv kasvas päev-päevalt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 6, 'Ja nemad käisid läbi Früügia- ja Galaatiamaa, sest Püha Vaim oli neid keelanud sõna rääkimast Aasias.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 7, 'Jõudes Müüsiani üritasid nad matkata Bitüüniasse, kuid Jeesuse Vaim ei lasknud neid mitte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 8, 'Siis nad läksid mööda Müüsiast ja tulid alla Troasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 9, 'Ja Paulus nägi öösel nägemuse: Makedoonia mees seisis ja palus teda ning ütles: \"Tule alla Makedooniasse ja aita meid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 10, 'Kui ta seda nägemust oli näinud, siis me püüdsime kohe välja minna Makedooniasse ja me olime julged selles, et Issand meid oli kutsunud neile Evangeeliumi kuulutama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 11, 'Me läksime Troast teele ja tulime otsekohe Samotraakesse ja järgmisel päeval Neapolisse');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 12, 'ja sealt Filipisse, mis on esimesi linnu tolles Makedoonia osas, asulinn. Sinna linna me jäime peatama mõneks päevaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 13, 'Ja hingamispäeval me läksime linnast välja jõe äärde, kus neil oli kombeks käia palvusel, ja istusime maha ning kõnelesime naistele, kes olid kokku tulnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 14, 'Ja ka üks jumalakartlik naine, nimega Lüüdia, purpurimüüja Tüatiira linnast, kuulas jutlust. Tema südame avas Issand, et ta pani tähele seda, mida Paulus rääkis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 15, 'Aga kui tema ja ta pere olid ristitud, palus ta meid ning ütles: \"Kui te arvate, et ma olen saanud usklikuks Issandasse, siis tulge mu kotta ja jääge sinna!\" Ja ta käis meile peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 16, 'Aga sündis, kui me läksime palvusele, et meile tuli vastu tüdruk, kellel oli lausuja vaim. See tõi oma isandaile palju kasu lausumisega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 17, 'Tema tuli Paulusele ja meile järele, kisendas ning ütles: \"Need inimesed on Kõigekõrgema Jumala sulased, kes teile kuulutavad õndsuse teed!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 18, 'Ja seda ta tegi mitu päeva. Aga Paulusel oli sellepärast meel haige, ja ta pöördus ümber ning ütles vaimule: \"Ma käsin sind Jeesuse Kristuse nimel välja minna tema seest!\" Ja vaim läks välja samal tunnil.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 19, 'Aga kui tüdruku isandad nägid, et nende kasu lootus oli lõppenud, võtsid nad Pauluse ja Siilase kinni ja vedasid nad turule ülemate juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 20, 'Ja nad viisid nad pealikute ette ning ütlesid: \"Need inimesed teevad meie linna rahutuks ja nad on juudid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 21, 'Nad kuulutavad kombeid, mida meil kui roomlasil ei sünni vastu võtta ega täita!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 22, 'Ja rahvas tõusis nende vastu. Ja pealikud käskisid kiskuda nende riided seljast ja neid vitstega peksta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 23, 'Ja kui nad neile olid palju hoope andnud, viskasid nad nemad vanglasse ja käskisid vangihoidjal neid hoolsasti valvata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 24, 'Niisuguse kange käsu saanud, heitis ta nad kõige kindlamasse vangihoonesse ja pani nende jalad pakku.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 25, 'Aga keskööl palvetasid Paulus ja Siilas ning kiitsid lauldes Jumalat. Ja vangid kuulsid neid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 26, 'Siis äkitselt sündis suur maavärisemine, nii et vangihoone alused vabisesid; ja sedamaid läksid kõik uksed lahti, ka kõikide köidikud pääsesid valla.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 27, 'Kui nüüd vangihoidja unest ärkas ja nägi vangitorni uksed avatud olevat, tõmbas ta mõõga ja tahtis enesele otsa teha, sest ta mõtles, et vangid on ära karanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 28, 'Aga Paulus hüüdis suure häälega ning ütles: \"Ära tee enesele kurja, sest me oleme kõik siin!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 29, 'Siis ta küsis tuld, kargas sisse, heitis värisedes Pauluse ja Siilase ette maha,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 30, 'tõi nad ka välja ning ütles: \"Isandad, mis ma pean tegema, et ma õndsaks saaksin?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 31, 'Aga nemad ütlesid: \"Usu Issandasse Jeesusesse, siis saad õndsaks sina ja su pere!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 32, 'Ja nad kuulutasid temale Issanda sõna ja kõigile, kes olid tema kojas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 33, 'Tema võttis nad sel öötunnil enese juurde ja pesi nende haavad. Ja ta ristiti sedamaid ja kõik tema pere.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 34, 'Siis ta viis nad oma kotta, tõi toitu lauale ja oli väga rõõmus, et ta kogu oma perega oli saanud usklikuks Jumalasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 35, 'Kui valgeks läks, läkitasid pealikud kohtusulased ütlema: \"Lase need inimesed lahti!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 36, 'Siis vangihoidja teatas need sõnad Paulusele, öeldes: \"Pealikud on siia läkitanud sõna, et teid tuleb lahti lasta; seepärast väljuge nüüd ja minge rahuga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 37, 'Aga Paulus ütles neile: \"Nad on meid avalikult peksnud ilma kohut mõistmata, ja me oleme Rooma kodanikud; nad on meid heitnud vangitorni ja nüüd nad tahavad meid salaja välja lükata. Nõnda ei tohi olla! Vaid tulgu nad ise ja saatku meid välja!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 38, 'Kohtusulased teatasid need sõnad pealikuile. Need lõid kartma, kui nad kuulsid neid Rooma kodanikud olevat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 39, 'Ja nad tulid ning rääkisid nendega lahkesti, tõid nad välja vangist ja palusid neid linnast ära minna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 40, 'Siis nad tulid vanglast välja ja läksid Lüüdia juurde. Ja kui nad olid vendi näinud, siis nad kinnitasid neid ja läksid ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 1, 'Ja nad läksid läbi Amfipolise ja Apolloonia ja tulid Tessaloonikasse, kus oli juutide kogudusekoda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 2, 'Ja Paulus läks oma kombe järgi sisse nende juurde ja kõneles neile kolmel hingamispäeval Kirjast,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 3, 'seletades ja tõestades, et Kristus pidi kannatama ja surnuist üles tõusma, ja öeldes: \"See Jeesus, Keda mina teile kuulutan, on Kristus!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 4, 'Ja mõned neist said usklikuks ja liitusid Pauluse ja Siilasega, nõndasamuti suur hulk jumalakartlikke kreeklasi ja mitte pisut suursuguseid naisi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 5, 'Aga juudid said kadedaks ja võtsid enestega mõningaid hulguseid, nurjatuid mehi, kihutasid rahva üles ning tõstsid linnas möllu ja ründasid Jaasoni maja ning katsusid neid välja vedada rahva ette.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 6, 'Aga kui nad neid ei leidnud, vedasid nad Jaasoni ja mõned vennad linna ülemate ette ning kisendasid: \"Need, kes kõiges maailmas tüli tõstavad, on ka siin!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 7, 'Neid on Jaason vastu võtnud ja nad kõik teevad keisri käskude vastu ja ütlevad ühe teise olevat kuninga, Jeesuse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 8, 'Seda kuuldes said rahvas ja linna ülemad rahutuks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 9, 'kuid saades Jaasonilt ja teistelt tagatise, lasksid nad nemad lahti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 10, 'Aga vennad saatsid sedamaid Pauluse ja Siilase läbi öö Beroiasse. Kui nemad olid saabunud, läksid nad juutide kogudusekotta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 11, 'Need olid üllamad kui Tessaloonika juudid; nemad võtsid sõna vastu kõige hea meelega ja uurisid iga päev Kirjast, kas see on nõnda, nagu kuulutati.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 12, 'Siis uskusid nüüd paljud neist, ja mitte pisut kreeka lugupeetud naisi ja mehi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 13, 'Aga kui Tessaloonika juudid said teada, et Paulus ka Beroias kuulutas Jumala sõna, tulid nad sinnagi rahvast ässitama ja rahutuks tegema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 14, 'Siis vennad saatsid Pauluse sedamaid minema mere äärde; aga Siilas ja Timoteos jäid sinna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 15, 'Ja Pauluse saatjad viisid ta Ateenani, ja kui nad olid saanud Siilase ja Timoteose jaoks käsu, et need nii ruttu kui võimalik tuleksid tema juurde, tulid nad sealt ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 16, 'Aga kui Paulus Ateenas neid oli ootamas, ärritus ta vaim temas, kui ta nägi linna ebajumalakujusid täis olevat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 17, 'Ent ta kõneles nüüd kogudusekojas juutidega ja jumalakartlike inimestega, ja turul iga päev nendega, keda ta kohtas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 18, 'Ja mõned epikuurlaste ja stoalaste mõttetargad vaidlesid temaga. Ühed ütlesid: \"Mida siis see lobasuu tahab öelda?\" Aga teised: \"Tema näib kuulutavat võõraid vaime!\" Sest ta jutlustas neile Jeesusest ja ülestõusmisest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 19, 'Ja nad võtsid ta kinni ja viisid ta Areopaagi ning ütlesid: \"Kas me võiksime teada saada, mis uus õpetus see on, mida sa õpetad?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 20, 'Sest sa tood meie kõrvade ette võõrastavaid asju; sellepärast tahamegi nüüd teada, mis need õige on!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 21, 'Sest kõik ateenlased ja muulased, kes nende juures elasid, ei olnud millelegi nii himukad kui jutustama ja kuulama uudiseid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 22, 'Aga Paulus seisis keset Areopaagi ning ütles: \"Ateena mehed, ma näen, et te olete igapidi väga jumalakartlikud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 23, 'Sest ma olen läbi minnes ja teie pühi paiku vaadeldes leidnud ka altari, mille peale oli kirjutatud: \"Tundmatule Jumalale!\" Keda te nüüd tundmata teenite, Teda kuulutan mina teile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 24, 'Jumal, kes on teinud maailma ja kõik, mis seal sees, Tema, Kes on taeva ja maa Issand, ei ela templis, mis on kätega tehtud;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 25, 'Teda ei teenita ka mitte inimeste kätega, otsekui oleks Temale midagi vaja; Tema annab Ise kõigile elu ja õhu ja kõik.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 26, 'Ja Ta on ühest ainsast teinud kogu inimkonna kõige maa peale elama ning neile seadnud ennemääratud ajad ja nende asukohtade rajad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 27, 'et nad otsiksid Jumalat, kas nad Teda ehk saaksid kätega kombata ja leida, ehk Tema küll ei ole kaugel mitte ühestki meist;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 28, 'sest Tema sees elame ja liigume ja oleme meie, nagu ka mõned teie luuletajaist on öelnud: \"Sest ka meie oleme Tema sugu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 29, 'Kui me nüüd oleme Jumala sugu, siis me ei tohi mõelda, et jumalik olend on kulla või hõbeda või kivi sarnane, niisugune nagu inimese oskuse ja väljamõeldise läbi tekitatud kuju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 30, 'Jumal on küll niisuguseid teadmatuse aegu sallinud, kuid nüüd Ta kuulutab kõigile inimestele kõigis paigus, et nad peavad meelt parandama,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 31, 'sest Ta on seadnud päeva, mil Ta tahab kohut mõista maailma üle õigluses Mehe läbi, Kelle Ta selleks on määranud, ja pakub usku kõigile pärast seda, kui Ta on Tema surnuist üles äratanud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 32, 'Aga kui nad surnute ülestõusmisest kuulsid, panid mõned seda naeruks, aga teised ütlesid: \"Me tahame sind selles asjas kuulda veel teinegi kord!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 33, 'Nõnda läks Paulus nende keskelt ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 34, 'Ent mõned mehed ühinesid temaga ja said usklikuks; nende hulgas oli ka Areopaagi liige Dionüüsios ja üks naine, nimega Daamaris, ja muid ühes nendega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 1, 'Pärast seda läks Paulus Ateenast ära ja tuli Korintosesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 2, 'Sealt ta leidis juudi, nimega Akvila; see oli Pontosest pärit ja oli hiljuti tulnud Itaaliast ühes oma naise Priskillaga, sest keiser Klaudius oli käskinud kõigil juutidel Roomast lahkuda. Nende juurde tuli tema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 3, 'Ja et neil oli ühesugune amet, siis ta jäi nende juurde ja nad tegid ühes tööd; sest nad olid ametilt telgitegijad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 4, 'Aga ta õpetas kogudusekojas igal hingamispäeval ning äratas juute ja kreeklasi usule.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 5, 'Kui siis Siilas ja Timoteos Makedooniast sinna jõudsid, oli Paulus andunud sõna kuulutamisele ning tunnistas juutidele, et Jeesus on Messias.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 6, 'Aga kui nad vastu panid ja pilkama hakkasid, puistas ta tolmu oma riietelt ning ütles neile: \"Teie veri tulgu teie pea peale! Mina olen puhas. Sest ajast ma lähen paganate juurde!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 7, 'Ja kui ta sealt ära läks, tuli ta ühe jumalakartliku mehe kotta, nimega Justus, kelle maja oli otse kogudusekoja kõrval.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 8, 'Aga kogudusekoja ülem Krispos sai usklikuks Issandasse kogu oma perega ja palju korintlasi sai kuulates usklikuks, ja nad ristiti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 9, 'Siis Issand ütles öösel nägemuses Paulusele: \"Ära karda, vaid kõnele ja ära ole vait!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 10, 'Sest Mina olen sinuga ja ükski ei saa su kallale tulla sulle kurja tegema, sest Mul on palju rahvast selles linnas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 11, 'Siis ta viibis seal ühe aasta ja kuus kuud ja õpetas nende seas Jumala sõna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 12, 'Aga kui Gallio oli Ahhaia maavalitseja, tõusid juudid ühel meelel Pauluse vastu ja viisid ta kohtujärje ette');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 13, 'ning ütlesid: \"See siin meelitab inimesi Jumalat teenima käsuvastaselt!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 14, 'Aga kui Paulus tahtis suu avada, ütles Gallio juutidele: \"Kui oleks tehtud mõni ülekohtune tegu või ilge kuritöö, oh juudid, siis ma oleksin küll kohustatud teid üle kuulama;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 15, 'aga kui on vaidlust sõnade ja nimede ja käsuõpetuse pärast teie seas, siis katsuge ise toime saada; neis asjus ei taha mina olla kohtumõistja!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 16, 'Ja ta ajas nad ära kohtujärje eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 17, 'Nemad aga läksid kõik kogudusekoja ülema Soostenese kallale ja peksid teda kohtujärje ees. Ja Gallio ei hoolinud sellest midagi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 18, 'Aga Paulus viibis seal veel mitu päeva. Siis ta jättis vennad jumalaga ja sõitis laevaga Süüriasse ja ühes temaga Priskilla ja Akvila, pärast seda kui ta Kenkreas oli lasknud oma pea pügada, sellepärast et ta oli andnud tõotuse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 19, 'Ja nad saabusid Efesosse ja siin ta jättis nad maha, tema ise aga läks kogudusekotta ja vaidles juutidega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 20, 'Aga kui nad teda palusid kauemaks eneste juurde jääda, ei olnud ta nõus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 21, 'vaid jättis nad jumalaga ning ütles: \"Ma tulen teinekord teie juurde tagasi, kui Jumal tahab!\" Ja ta läks Efesosest ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 22, 'Ja kui ta Kaisareas oli maabunud, läks ta üles Jeruusalemma, tervitas kogudust ja läks siis alla Antiookiasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 23, 'Kui ta seal oli viibinud mõnd aega, läks ta teele ja käis järgemööda läbi Galaatia maakonna ja Früügia, kinnitades kõiki jüngreid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 24, 'Aga keegi juut, Apollos nimi, Aleksandriast pärit, vahva kõnemees ja vägev kirjaseletaja, tuli Efesosse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 25, 'Teda oli õpetatud tundma Issanda teed ja ta oli tuline vaimult, rääkis ja õpetas õigesti Jeesusest, aga tundis ainult Johannese ristimist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 26, 'Tema hakkas julgesti õpetama kogudusekojas. Aga kui Priskilla ja Akvila teda kuulsid, võtsid nad ta eneste juurde ja seletasid talle selgemini ära Jumala tee.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 27, 'Aga kui ta tahtis minna Ahhaiasse, julgustasid vennad teda selleks ning kirjutasid jüngritele, et nad tema vastu võtaksid. Kui ta sinna oli saabunud, saatis ta palju kasu neile, kes armu läbi olid saanud usklikuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 28, 'Sest suure osavusega lükkas ta avalikult juutide väited ümber ja näitas Kirjast, et Jeesus on Messias.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 1, 'Kui Apollos oli Korintoses, sündis, et Paulus käis läbi ülamaakonnad ja tuli Efesosse ning leidis sealt jüngreid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 2, 'Ja ta küsis neilt: \"Kas te saite Püha Vaimu, kui saite usklikuks?\" Aga nemad ütlesid talle: \"Me ei olegi kuulnud, kas Püha Vaimu on!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 3, 'Ja tema ütles: \"Mis ristimisega te siis olete ristitud?\" Nad vastasid: \"Johannese ristimisega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 4, 'Aga Paulus ütles: \"Johannes ristis meeleparandusristimisega, öeldes rahvale, et nad usuksid Temasse, Kes pärast teda tuleb, see on Jeesusesse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 5, 'Kui nad seda kuulsid, lasksid nad endid ristida Issanda Jeesuse nimesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 6, 'Ja kui Paulus pani oma käed nende peale, tuli Püha Vaim nende peale ja nad rääkisid võõraid keeli ja ennustasid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 7, 'Neid oli üldse ligi kaksteist meest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 8, 'Ja ta läks kogudusekotta ning kõneles seal avalikult kolm kuud, õpetades ja äratades nõudma Jumala riiki.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 9, 'Aga kui mõned tegid oma südame kõvaks ega uskunud, vaid rääkisid halba õpetusest rahva ees, läks ta ära nende juurest ning eraldas neist jüngrid ja pidas iga päev arutlusi Türannose koolis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 10, 'See kestis kaks aastat, nii et kõik, kes Aasias elasid, niihästi juudid kui kreeklased, said kuulda Issanda Jeesuse sõna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 11, 'Ja Jumal tegi iseäralikke imetegusid Pauluse käte läbi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 12, 'nii et ka tema ihu pealt võetud higirätikuid ja põlli viidi haigete peale ja tõved lahkusid haigetest ning kurjad vaimud läksid välja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 13, 'Aga mõningad ümberrändajad juudi lausujad hakkasid Issanda Jeesuse nime nimetama nende peale, kelles oli kurje vaime, öeldes: \"Ma vannutan teid Jeesuse nimel, keda Paulus kuulutab!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 14, 'Ja need olid ühe juudi ülempreestri Skeua seitse poega, kes seda tegid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 15, 'Ent kuri vaim vastas ning ütles neile: \"Jeesust ma tunnen ja Paulust ma tean, aga kes teie olete?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 16, 'Ja inimene, kelles oli kuri vaim, kargas nende kallale, sai võimust nende üle ja võitis nad ära, nõnda et nad alasti ja haavatult põgenesid sellest kojast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 17, 'Ja see sai teatavaks kõigile juutidele ja kreeklastele, kes elasid Efesoses. Ja hirm tuli nende kõikide peale, ja Issanda Jeesuse nime ülistati väga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 18, 'Ja paljud neist, kes olid usklikuks saanud, tulid ning tunnistasid ja andsid üles oma teod.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 19, 'Aga mitmed neist, kes olid tegemist teinud nõiakunstiga, tõid kokku oma raamatud ja põletasid need ära kõikide nähes. Ja kui nende hind kokku arvati, leiti see olevat viiskümmend tuhat hõbetükki.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 20, 'Nii võimsasti kasvas Issanda sõna ja avaldas oma väge.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 21, 'Kui see kõik oli sündinud, võttis Paulus vaimus ette minna Makedoonia ja Ahhaia kaudu Jeruusalemma ning ütles: \"Kui ma seal olen ära käinud, pean ma näha saama ka Rooma linna!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 22, 'Ja ta läkitas kaks oma abilist, Timoteose ja Erastose, Makedooniasse ja jäi ise veel mõneks ajaks Aasiasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 23, 'Sel ajal tekkis suur tüli õpetuse pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 24, 'Sest üks hõbesepp, Demeetrios nimi, tegi hõbedast Artemise templeid ja saatis sellega ametimeestele palju kasu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 25, 'Need ta kogus kokku ja teisi, kes seda tööd tegid, ning ütles: \"Mehed, te teate, et meil sellest tööst on hea tulu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 26, 'ja te näete ning kuulete, et see Paulus mitte ainult Efesoses, vaid ka peaaegu kogu Aasias palju rahvast ära meelitab ja eksitab, öeldes, et need ei olevat jumalad, mis kätega tehakse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 27, 'Ent mitte ükspäinis seda meie tööstust ei ähvarda halvakspanu, vaid ka suure jumalanna Artemise templit, nii et seda mikski ei panda ja et kaob ka jumalanna suur au, keda kummardavad kogu Aasia ja kõik maailm!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 28, 'Kui nad seda kuulsid, said nad täis viha, kisendasid ning ütlesid: \"Suur on efeslaste Artemis!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 29, 'Ja kõik linn sai täis kära ja nad tormasid ühel meelel teatrisse ja vedasid ühes sinna Gaajuse ja Aristarhose, kaks makedoonlast, kes olid Pauluse matkakaaslased.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 30, 'Ent kui Paulus tahtis rahva sekka minna, ei lasknud jüngrid teda mitte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 31, 'Ka mõned Aasiamaa ülemad, kes olid tema sõbrad, läkitasid tema juurde ning palusid teda, et ta ei läheks teatriväljakule.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 32, 'Seal kisendasid ühed seda, teised teist; sest rahvakogu oli segaduses ja suurem hulk ei teadnud, misjaoks nad olid kokku tulnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 33, 'Siis toodi rahva seast esile Aleksandros, kelle juudid ette lükkasid. Aleksandros viitas käega, märku andes, et ta tahab pidada kaitsekõnet rahva ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 34, 'Aga kui nad märkasid, et ta on juut, tõstsid nad kisa ja karjusid kõik nagu ühest suust ligi kaks tundi aega: \"Suur on efeslaste Artemis!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 35, 'Viimaks linnakirjutaja vaigistas rahva ning ütles: \"Efesose mehed, kas on ühtki inimest, kes ei teaks, et efeslaste linn on suure Artemise ja tema taevast mahalangenud kuju hoidja?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 36, 'Et nüüd ükski sellele ei saa vastu rääkida, siis tuleb teil jääda vait ega tule midagi ette võtta uisapäisa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 37, 'Sest te olete toonud siia need mehed, kes ei ole templi rüüstajad ega teie jumalanna teotajad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 38, 'Kui nüüd Demeetriosel ja temaga ühes olevail ametimeestel on kellegi peale midagi kaebamist, siis on selleks kohtupäevi ja maavalitsejaid; kaevaku nad üksteist kohtusse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 39, 'Ja kui teil on veel mingit muud nõudmist, siis seletatagu asi korrapärase rahvakogu ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 40, 'Sest meil tuleb tänase kära pärast karta, et meie peale kaebus tõstetakse, ilma et meil oleks mingit põhjust, millega seda rahva kokkujooksu õigustada!\" Kui ta seda oli öelnud, saatis ta rahvakogu laiali.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 41, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 1, 'Kui kära oli vaigistatud, kutsus Paulus jüngrid enese juurde, jättis nad jumalaga ja läks teele Makedooniasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 2, 'Ja kui ta need maakohad oli läbi käinud ja seal palju manitsussõnu rääkinud, tuli ta Kreekamaale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 3, 'Seal ta viibis kolm kuud. Kui ta siis mõtles mereteed minna Süüriasse ja juudid salanõu pidasid ta vastu, võttis ta nõuks tagasi matkata Makedoonia kaudu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 4, 'Teda saatsid beroialane Soopatros, Pürrose poeg, tessalooniklastest Aristarhos ja Sekundus, ja derbelane Gaajus, ja Timoteos, ja aasialased Tühhikos ja Trofimos.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 5, 'Need läksid ära eele ja ootasid meid Troas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 6, 'Meie aga läksime laevaga pärast hapnemata leibade päevi Filipist teele ja saabusime nende juurde Troasse viiendal päeval, ja jäime sinna seitsmeks päevaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 7, 'Aga kui me esimesel nädalapäeval olime kokku tulnud leiba murdma, siis Paulus, kes tahtis teisel päeval ära minna, kõneles nendega ja jätkas oma kõnet keskööni.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 8, 'Ent mitu lampi põles ülemises toas, kus me koos olime.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 9, 'Aga üks noor mees, Eutühhos nimi, istus aknal; ja kui Paulus kauemini kõneles, tuli raske uni tema peale ja ta kukkus magades kolmandalt korralt alla ja tõsteti üles surnuna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 10, 'Aga Paulus läks alla ja heitis tema peale, haaras ta ümbert kinni ning ütles: \"Ärge hädaldage, sest tal on hing veel sees!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 11, 'Siis ta läks üles ja murdis leiba ja sõi ja kõneles kaua kuni koiduni ja läks siis teele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 12, 'Aga noor mees viidi minema elusana ja oldi suuresti trööstitud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 13, 'Me läksime eele laeva ja sõitsime Assosesse, kus mõtlesime oodata Paulust laeva, sest nõnda oli ta käskinud, ette võttes ise jala tulla.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 14, 'Kui ta nüüd Assoses meiega kokku sai, võtsime ta laeva ja tulime Mitüleenesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 15, 'Sealt me purjetasime edasi ja jõudsime järgmisel päeval Kiose saare kohta ja peatusime teisel päeval Saamoses ja saabusime järgmisel päeval Mileetosesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 16, 'Sest Paulusel oli nõu Efesosest mööda sõita, et mitte aega viita Aasias. Tõttas ta ju, et kui vähegi võimalik, Nelipühapäevaks jõuda Jeruusalemma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 17, 'Aga Mileetosest ta läkitas sõna Efesosse ja kutsus koguduse vanemad enese juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 18, 'Ja kui nad tema juurde tulid, ütles ta neile: \"Te teate, kuidas ma esimesest päevast, kui ma tulin Aasiasse, kõige selle aja olin teie juures,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 19, 'teenides Issandat kõige alanduse ja pisarate ja kiusatustega, mis mind tabasid juutide salanõu tõttu;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 20, 'kuidas ma teie eest ei pidanud salajas midagi, mis teile tarvis oli, et oleksin selle jätnud teile kuulutamata ega oleks teid õpetanud avalikult ja kodasid mööda');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 21, 'ja tunnistanud nii juutidele kui kreeklastele pöördumist Jumala poole ja usku meie Issandasse Jeesusesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 22, 'Ja nüüd, vaata, ma lähen vaimus seotuna Jeruusalemma ega tea, mis mind seal võib tabada;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 23, 'kuid seda ma tean, et Püha Vaim igas linnas mulle tunnistab ning ütleb, et köidikud ja viletsused ootavad mind.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 24, 'Ometi ma ei hooli sellest midagi ega pea ka oma elu kalliks, kui ma aga lõpetan oma elukorra ja ametikohused, mis ma olen saanud Issanda Jeesuse käest, et tunnistada Jumala armu Evangeeliumi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 25, 'Ja nüüd, vaata, ma tean, et te iialgi enam ei saa minu silmi näha, te kõik, kelle keskel ma olen käinud ja kuulutanud Jumala Riiki.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 26, 'Sellepärast tunnistan ma teile tänasel päeval, et ma olen puhas kõikide verest;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 27, 'sest ma pole midagi teie eest salanud Jumala tahtest, et ma teile seda tervelt ei oleks kuulutanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 28, 'Sellepärast pange tähele iseendid ja kõike karja, kellele Püha Vaim on pannud teid ülevaatajaiks karjastena hoidma Jumala kogudust, mille Ta on omandanud Iseenese vere läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 29, 'Ma tean seda, et pärast minu äraminekut tulevad teie sekka hirmsad hundid, kes karjale armu ei anna;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 30, 'ja teie eneste seast tõusevad mehed, kes kõnelevad pööraseid asju, et vedada jüngreid eneste järele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 31, 'Sellepärast valvake ja mõelge sellele, et ma kolm aastat ööd ja päevad ei ole lakanud silmaveega manitsemast igaüht teie seast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 32, 'Ja nüüd ma annan teid, vennad, Jumala ja Tema armusõna hooleks, Kes on vägev teid üles ehitama ja andma teile pärandi kõikide seas, Kes on pühitsetud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 33, 'Hõbedat ega kulda ega riietust ei ole ma ihaldanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 34, 'Te teate ise, et need mu käed on teinud tööd minu ja mu kaaslaste vajaduste rahuldamiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 35, 'Mina olen teile kõigiti näidanud, et nõnda tööd tehes tuleb hoolt kanda nõrkade eest ja meeles pidada Issanda Jeesuse sõnu, mis Ta on öelnud: õndsam on anda kui võtta!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 36, 'Ja kui ta seda oli öelnud, langes ta põlvili ja palvetas ühes nende kõikidega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 37, 'Siis puhkesid kõik valjusti nutma ja hakkasid Paulusel ümber kaela ja andsid temale suud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 38, 'Kõige rohkem kurvastas neid sõna, mis ta oli öelnud, et nad enam ei saavat näha tema palet. Siis nad saatsid ta laeva.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 1, 'Aga kui me neist olime lahkunud ja laevaga teele läinud, purjetasime otsekohe Koossi, aga teisel päeval Roodosesse ja sealt Patarasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 2, 'Seal me leidsime laeva, mis läks Foiniikiasse, astusime sellesse ja purjetasime minema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 3, 'Kui Küpros meile hakkas paistma, jätsime selle vasakut kätt ja purjetasime Süüriasse ning maabusime Tüüroses, sest seal pidi laev koormast tühjendatama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 4, 'Seal me kohtasime jüngreid ning jäime nende juurde seitsmeks päevaks. Nemad ütlesid Paulusele Vaimu mõjul, et ta ei läheks Jeruusalemma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 5, 'Aga kui meie aeg lõppes, läksime teele. Ja nad kõik naiste ja lastega saatsid meid linnast välja, ja me heitsime põlvili ranna äärde ning palvetasime.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 6, 'Ja kui me olime üksteise jätnud jumalaga, astusime laeva; aga nemad pöördusid tagasi koju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 7, 'Tüürosest me sõitsime Ptolemaisse ja lõpetasime seal oma laevasõidu; me teretasime vendi ja jäime nende juurde üheks päevaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 8, 'Aga järgmisel päeval läksime sealt välja ja tulime Kaisareasse; me läksime Evangelist Filippuse kotta, kes oli üks neist seitsmest, ja jäime tema juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 9, 'Temal oli neli tütart; need olid neitsid ja ennustasid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 10, 'Aga kui me mõned päevad seal viibisime, tuli sinna prohvet Judeast, Aagabus nimi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 11, 'Tema tuli meie juurde ja võttis Pauluse vöö, sidus oma käed ja jalad kinni ja ütles: \"Seda ütleb Püha Vaim: mehe, kelle oma on see vöö, seovad juudid Jeruusalemmas nõnda kinni ja annavad ta paganate kätte!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 12, 'Kui me seda kuulsime, palusime teda meie ja ka teised, kes seal elasid, et ta ei läheks Jeruusalemma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 13, 'Siis Paulus vastas: \"Mis te teete, et nutate ja rõhute minu südant? Sest ma pole mitte üksnes valmis laskma ennast siduda, vaid ka surema Jeruusalemmas Issanda Jeesuse nime pärast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 14, 'Aga kui ta ei võtnud meid kuulda, jäime rahule ja ütlesime: \"Issanda tahtmine sündigu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 15, 'Pärast neid päevi me valmistusime teele ja läksime Jeruusalemma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 16, 'Ja meiega ühes tulid mõned jüngrid Kaisareast, kes meid viisid ühe vana jüngri Mnaasoni, Küprose mehe juurde, kelle külaliseks me pidime jääma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 17, 'Ja kui me jõudsime Jeruusalemma, võtsid vennad meid heal meelel vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 18, 'Järgmisel päeval läks Paulus meiega Jakoobuse juurde. Ja kõik vanemad tulid sinna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 19, 'Ja kui ta neid oli teretanud, jutustas ta järgemööda kõik, mis Jumal oli teinud paganate seas tema ameti kaudu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 20, 'Seda kuuldes nad andsid Jumalale au ning ütlesid talle: \"Sa näed, vend, kui mitu tuhat juuti on, kes on saanud usklikuks, ja need kõik peavad kangesti käsu kombeid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 21, 'Aga nad on saanud kuulda sinust, et sina õpetavat juute, kes elavad paganate seas, taganema Moosesest öeldes, et ei ole vaja lapsi ümber lõigata ega elada usukommete järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 22, 'Mis nüüd teha? Igatahes nad saavad kuulda, et sa oled tulnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 23, 'Siis tee nüüd seda, mis me sulle ütleme: meil on neli meest, kes on võtnud eneste peale tõotuse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 24, 'Need võta enese juurde ning puhasta ennast ühes nendega ja kanna nende eest nende kulud, et nad oma pea pügaksid; siis märkavad kõik, et see on tühi, mis nad sinust on kuulnud, vaid et ka sina ise elad käsku pidades.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 25, 'Aga paganate kohta, kes on saanud usklikuks, oleme otsustanud ja läkitanud kirja, et neil on tarvis hoiduda ebajumala ohvrilihast ja verest ja lämbunu lihast ja hooraelust!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 26, 'Siis võttis Paulus mehed enese juurde ja järgmisel päeval ta laskis ennast ühes nendega puhastada, läks pühakotta ja teatas, et puhastuspäevad on lõppenud, ja jäi sinna, kuni nende igaühe eest oli ohver toodud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 27, 'Aga kui need seitse päeva hakkasid lõpule jõudma, nägid teda Aasiast olevad juudid pühakojas ja ajasid kõik rahva kihama ning pistsid oma käed tema külge');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 28, 'ja kisendasid: \"Iisraeli mehed, tulge appi! Siin on see mees, kes igal pool õpetab kõiki inimesi meie rahva ja käsu ja selle paiga vastu; pealegi on ta toonud kreeklasi pühakotta ja selle paiga rüvetanud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 29, 'Sest nad olid enne näinud temaga linnas olevat efeslase Trofimose ja arvasid temast, et Paulus oli ta toonud pühakotta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 30, 'Ja kogu linn tõusis liikvele ning rahvast jooksis kokku. Ja nad võtsid Pauluse kinni ning tõmbasid ta pühakojast välja, ja uksed pandi kohe lukku.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 31, 'Juba nad tahtsid teda tappa, kui ülempealikule teada anti, et kõik Jeruusalemm on kihamas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 32, 'See võttis sedamaid sõjamehi ja pealikuid enesega ja tuli joostes nende juurde. Kui nad ülempealikut ja sõjamehi nägid, lakkasid nad Paulust peksmast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 33, 'Siis astus ülempealik ette, võttis ta oma hoole alla, käskis ta kahe ahelaga kinni siduda ja küsis, kes ta on ja mis ta on teinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 34, 'Aga rahva seast kisendasid ühed seda, teised teist. Ja et ta ei võinud õieti asja teada saada kära pärast, käskis ta tema viia kindlusesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 35, 'Kui aga Paulus jõudis trepile, pidid sõjamehed teda kandma rahvatungi pärast,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 36, 'sest suur rahvahulk käis järel ja kisendas: \"Hukka ta ära!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 37, 'Ja kui Paulust juba oldi viimas kindlusesse, ütles ta ülempealikule: \"Kas mul on luba midagi sulle rääkida?\" Aga tema ütles: \"Kas sa oskad kreeka keelt?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 38, 'Kas sina ei olegi see egiptlane, kes mõni aeg tagasi mässu tõstis ja viis välja kõrbe neli tuhat mõrtsukat?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 39, 'Paulus ütles: \"Mina olen juut Tarsosest, kuulsa Kiliikia linna kodanik; aga ma palun sind, anna mulle luba rahvale kõnelda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 40, 'Kui ta andis loa, astus Paulus astmeile ning andis käega rahvale märku. Kui kõik vait jäid, hakkas ta kõnelema heebrea keeli ning ütles:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 1, '\"Mehed, vennad ja vanemad, kuulge nüüd, mida ma enese kaitseks teile räägin!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 2, 'Kui nad kuulsid, et ta neile räägib heebrea keeli, jäid nad veel vaiksemaks. Ja tema ütles:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 3, '\"Mina olen Juuda mees, sündinud Tarsoses Kiliikiamaal ja kasvatatud siin selles linnas Gamaalieli jalgade ees, õpetatud kõige rangemaks esiisade käskude täitmiseks; ja ma olin agar Jumala nõudja, nõnda nagu teie kõik tänapäeval olete.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 4, 'Ja sellisena ma kiusasin taga seda õpetust vereni, köites ja viies vangi niihästi mehi kui naisi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 5, 'nõnda nagu mulle võivad anda tunnistust ka ülempreester ja kogu vanematekogu, kellelt ma sain ka kirju viimiseks vendadele Damaskusesse; ja ma olin sinna minemas kinni siduma ka neid, kes seal olid, ja Jeruusalemma viima, et neid nuheldaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 6, 'Aga sündis, kui ma teel olin ja Damaskuse lähedale jõudsin lõunaajal, et äkitselt paistis mu ümber suur valgus taevast!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 7, 'Ma kukkusin maha ja kuulsin häält mulle ütlevat: Saul! Saul! Miks sa Mind taga kiusad?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 8, 'Mina aga vastasin: kes Sa oled, Issand? Ja Ta ütles mulle: Mina olen Jeesus Naatsaretlane, Keda sa taga kiusad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 9, 'Need aga, kes olid minuga, nägid küll valgust ja kartsid, kuid selle häält, Kes minuga rääkis, nad ei kuulnud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 10, 'Siis ma ütlesin: Issand, mida ma pean tegema? Aga Issand ütles mulle: tõuse üles, mine Damaskusesse ja seal sulle öeldakse kõik, mis sulle on määratud teha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 11, 'Ja kui ma selle heleda valguse pärast ei võinud näha, talutasid mind kättpidi mu kaasasolijad, ja ma jõudsin Damaskusesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 12, 'Siis keegi Ananias, käsuõpetuselt jumalakartlik mees, kellel oli hea tunnistus kõigi sealsete juutide poolt,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 13, 'tuli mu juurde, astus ligi ja ütles mulle: Saul, vend, näe jälle! Ja selsamal tunnil ma nägin jälle ning vaatasin talle otsa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 14, 'Tema aga ütles: meie esiisade Jumal on sind enne valinud tundma Tema tahtmist ja nägema seda Õiget ja kuulma häält Tema suust,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 15, 'sest sa pead olema Tema tunnistaja kõigi inimeste ees neis asjus, mida sa oled näinud ja kuulnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 16, 'Ja mida sa nüüd ootad? Tõuse üles ja lase ennast ristida ja oma patud ära pesta, appi hüüdes Tema nime.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 17, 'Kui ma siis jälle tulin Jeruusalemma ja pühakojas palvetasin, sündis minuga, et ma otsekui enesest ära olin');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 18, 'ja nägin Teda. Ja Tema ütles mulle: tõtta ja mine kohe ära Jeruusalemmast, sellepärast et nad sinu tunnistust Minu kohta vastu ei võta!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 19, 'Ja mina ütlesin: Issand, nad teavad seda ise, et ma viisin vangi ja peksin kogudusekodades neid, kes uskusid Sinusse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 20, 'Ja kui Stefanose, Su tunnistaja veri ära valati, siis minagi juures olles kiitsin seda heaks ja hoidsin nende riideid, kes teda tapsid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 21, 'Ja Tema ütles mulle: mine, sest Ma tahan sind läkitada kaugele paganate sekka!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 22, 'Sellest sõnast saadik nad kuulasid teda, ja siis nad tõstsid oma häält ning ütlesid: \"Hukka niisugune ära maa pealt, tema ei tohi ellu jääda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 23, 'Ja kui nad kisendasid ja riideid seljast kiskusid ning tolmu vastu taevast viskasid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 24, 'käskis ülempealik teda viia kindlusesse ning ütles, et teda tuleb üle kuulata ja piitsa abil kätte saada, mis süü pärast nad nõnda tema peale kisendasid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 25, 'Aga kui nad tema olid maha sirutanud, et teda rihmadega peksta, ütles Paulus pealikule, kes seal juures seisis: \"Kas teil on luba peksta Rooma kodanikku ja pealegi ilma kohtu otsuseta?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 26, 'Kui pealik seda kuulis, läks ta ja teatas seda ülempealikule ning ütles: \"Mis sa nüüd teed? See inimene on ju Rooma kodanik!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 27, 'Siis tuli ülempealik Pauluse juurde ning ütles: \"Ütle mulle, kas sa oled Rooma kodanik?\" Tema ütles: \"Olen küll!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 28, 'Ja ülempealik vastas: \"Ma olen suure rahaga nõutanud enesele selle Rooma kodanikuõiguse!\" Ent Paulus ütles: \"Aga mina olen sellena sündinud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 29, 'Siis läksid sedamaid tema juurest ära need, kes teda pidid piitsa abil üle kuulama. Ja ülempealik kartis, kui ta teada sai, et ta on Rooma kodanik, ja et ta tema oli kinni sidunud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 30, 'Aga järgmisel päeval, tahtes õieti teada saada, mis asja pärast juudid Paulust süüdistasid, päästis ta tema köidikuist lahti ja käskis ülempreestreid ja kogu nende Suurkohtu kokku tulla, viis Pauluse sinna ja seadis ta nende ette.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 1, 'Aga Paulus pööras oma silmad Suurkohtu poole ning ütles: \"Mehed, vennad, ma olen täiesti hea südametunnistusega Jumala ees elanud tänase päevani!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 2, 'Aga ülempreester Ananias käskis neid, kes ta lähedal seisid, lüüa temale vastu suud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 3, 'Siis Paulus ütles talle: \"Küll Jumal sind lööb, sa lubjatud sein! Sina istud ja mõistad kohut minu üle käsku mööda ja käsid mind lüüa käsu vastu?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 4, 'Lähedal seisjad aga ütlesid: \"Kas sa teotad Jumala ülempreestrit?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 5, 'Paulus ütles: \"Vennad, ma ei teadnud, et ta on ülempreester, sest on kirjutatud: oma rahva ülemat ära sajata!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 6, 'Kuna aga Paulus teadis, et üks osa neist oli sadusere ja teine varisere, siis ta hüüdis Suurkohtu ees: \"Mehed, vennad, ma olen variser ja variseri poeg, lootuse ja surnute ülestõusmise pärast olen ma kohtu ees!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 7, 'Kui ta seda oli rääkinud, tõusis kära variseride ja saduseride vahel ja nende hulk läks lahku.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 8, 'Sest saduserid ei ütle ülestõusmist olevat, samuti Ingleid ja vaime; aga variserid tunnustavad mõlemaid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 9, 'Siis tõusis suur karjumine, ja kirjatundjad variseride hulgast tõusid ja vaidlesid kangesti ning ütlesid: \"Me ei leia ühtki paha sellest inimesest! Ehk aga on vaim või Ingel temaga rääkinud?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 10, 'Aga kui kära läks suureks, kartis ülempealik, et nad Pauluse lõhki kisuvad, ja käskis sõjaväe alla minna ja ta ära kiskuda nende seast ja viia kindlusesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 11, 'Aga järgmisel ööl seisis Issand ta juures ning ütles: \"Ole julge, Paulus! Sest otsekui sa neid asju Minust oled tunnistanud Jeruusalemmas, nõnda pead sa tunnistama ka Roomas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 12, 'Kui nüüd valgeks läks, heitsid mõned juudid ühte nõusse ja andsid isekeskis vande, et nad ei söö ega joo, enne kui nad on Pauluse tapnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 13, 'Ja neid oli rohkem kui nelikümmend, kes nõnda olid heitnud vandeliitu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 14, 'Need läksid ülempreestrite ja vanemate juurde ning ütlesid: \"Me oleme needes vandunud mitte midagi suhu võtta, enne kui oleme tapnud Pauluse!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 15, 'Pange siis teie nüüd ühes Suurkohtuga ülempealikule ette, et ta homme tema tooks teie ette, otsekui tahaksite teie ta asja paremini üle kuulata; aga meie oleme valmis teda tapma, enne kui ta saab teie ette!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 16, 'Aga Pauluse õepoeg sai kuulda varitsemisnõust ja läks alla ning tuli kindlusesse ja teatas seda Paulusele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 17, 'Siis Paulus kutsus enese juurde ühe pealikuist ja ütles: \"Vii see noormees ülempealiku juurde, sest tal on midagi temale öelda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 18, 'See võttis siis tema ja saatis ta ülempealiku juurde ja ütles: \"Vang Paulus kutsus mind enese juurde ja palus noormehe tuua sinu juurde, sest tal on midagi sulle rääkida!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 19, 'Aga ülempealik võttis ta kättpidi ja viis ta kõrvale ning küsis temalt: \"Mis sul on mulle teatada?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 20, 'Tema ütles: \"Juudid on heitnud ühte nõusse sind paluda, et sa Pauluse homme laseksid viia Suurkohtu ette, otsekui sa mõtleksid paremini üle kuulata tema asja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 21, 'Aga sina ära usu neid, sest rohkem kui nelikümmend meest nende seast varitsevad teda; need on vandunud mitte enne süüa ja juua kui nad tema on tapnud; ja nüüd on nad valmis ning ootavad sinu luba!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 22, 'Siis laskis ülempealik noormehe ära minna ja keelas teda kellelegi rääkimast, et ta seda oli temale teada andnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 23, 'Ja ta kutsus enese juurde kaks pealikuist ning ütles: \"Seadke valmis kakssada sõjameest Kaisareasse minemiseks ja seitsekümmend ratsameest ja kakssada piigimeest kolmandal öötunnil,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 24, 'samuti pidage ratsaloomad valmis, et ühe selga panna Paulus ja tema tervelt viia maavalitseja Feeliksi juurde.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 25, 'Ka kirjutas ta kirja nende sõnadega:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 26, '\"Klaudius Lüüsias läkitab aulikule maavalitsejale Feeliksile palju tervist!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 27, 'Selle mehe olid juudid kinni võtnud ja tahtsid teda tappa; siis tulin mina sõjaväega vahele ja võtsin ta ära nende käest ja sain teada, et ta on Rooma kodanik.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 28, 'Ja et ma tahtsin teada saada, mis süü pärast nad ta peale kaebasid, viisin ma ta nende Suurkohtu ette');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 29, 'ja leidsin, et ta peale kaevati nende käsuõpetusse puutuvate vaidluste pärast; aga ükski kaebus ei olnud niisugune, mis oleks olnud surma või vangistust väärt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 30, 'Aga kui mulle teada anti, et juutidel on kaval nõu selle mehe vastu, saatsin ma tema sedamaid sinu juurde ja käskisin ka kaebajaid sinu ees kõnelda tema vastu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 31, 'Vastavalt saadud käsule võtsid siis sõjamehed Pauluse ja viisid ta ära läbi öö Antipatrisesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 32, 'Aga järgmisel päeval lasksid nad ratsamehed temaga edasi minna ja pöördusid ise tagasi kindlusesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 33, 'Kui need Kaisareasse jõudsid, andsid nad kirja ära maavalitseja kätte ja viisid ka Pauluse tema ette.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 34, 'Kui maavalitseja oli kirja lugenud, küsis ta, kust maakonnast see mees on, ja teada saades, et ta on Kiliikiast,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 35, 'ütles ta: \"Ma tahan sind üle kuulata, kui su kaebajad ka siia jõuavad!\" Ja ta käskis teda hoida Heroodese peavahis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 1, 'Aga viie päeva pärast tuli alla ülempreester Ananias mõnede vanematega ja ühe kõnemehe Tertullusega, ja nad tõstsid maavalitseja ees kaebust Pauluse peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 2, 'Kui Paulus ette kutsuti, algas Tertullus oma kaebekõnet ning ütles:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 3, '\"Me oleme sinu kaudu, auline Feeliks, saanud maitsta rahu ja sinu hoolekandel on palju parandusi osaks saanud sellele rahvale. Seda me võtame kõikepidi ja kõigis paigus vastu suure tänuga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 4, 'Aga et ma sind kauemini ei tülitaks, siis palun sind meid oma lahkust mööda lühidalt kuulata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 5, 'Me oleme leidnud selle mehe kahjuliku olevat ja naatsaretlaste usulahu eestvedajana juute mässule kihutavat igal pool kogu maailmas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 6, 'Ta on püüdnud rüvetada isegi pühakoda. Tema me võtsime kinni, et tema üle kohut mõista meie käsuõpetuse järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 7, 'Kuid ülempealik Lüüsias tuli ning võttis ta suure vägivallaga meie käest ära');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 8, 'ja käskis tema süüdistajaid tulla sinu juurde. Sa võid ise teda nüüd üle kuulata ja teada saada kõike, milles me teda süüdistame!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 9, 'Ja juudid ühinesid sellega, tõendades, et see nõnda on.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 10, 'Kui siis maavalitseja Paulusele käega märku andis, et ta räägiks, vastas ta: \"Et ma sind mitu aastat kui selle rahva kohtunikku tunnen, siis tahan seda julgemini kosta enese eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 11, 'Nagu sa võid teada saada, ei ole rohkem kui kaksteist päeva sellest, kui ma läksin üles Jeruusalemma Jumalat kummardama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 12, 'Ei nad ole leidnud mind ühegi inimesega kõnelemast ega rahva seas tüli tõstmast, ei pühakojas, ei kogudusekodades ega linnas;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 13, 'nad ei või ka tõeks teha seda, milles nad nüüd mind süüdistavad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 14, 'Aga seda ma tunnistan sulle, et ma seda õpetust mööda, mida nad kutsuvad usulahuks, nõnda teenin oma esiisade Jumalat, et ma usun kõike, mis käsuõpetuses ja prohvetite raamatutes on kirjutatud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 15, 'ja et mul on see lootus Jumala peale, mida ka nemad ise ootavad, et tuleb õigete ja ülekohtuste ülestõusmine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 16, 'Ja selles ma püüan alati hoida puhast südametunnistust Jumala ja inimeste ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 17, 'Aga nüüd ma tulin mitme aasta pärast tooma annetusi oma rahvale ja ohvriande.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 18, 'Seda tegemast leidsid mind mõned juudid Aasiamaalt, kui ma pühakojas ennast puhastasin ega andnud põhjust rahvamurruks või käratsemiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 19, 'Need peaksid siin sinu ees olema ja kaebama, kui neil midagi oleks mu vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 20, 'Või öelgu need siinolijad ise, kas nad on minust leidnud mingisugust süüd, kui ma seisin Suurkohtu ees,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 21, 'olgu siis selle ainsa sõna, et ma nende seas seistes hüüdsin: surnute ülestõusmise pärast te mõistate täna kohut minu üle!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 22, 'Aga Feeliks lükkas asja edasi, sest ta teadis väga hästi, kuidas selle õpetusega lugu on, ja ütles: \"Kui ülempealik Lüüsias tuleb siia, siis ma uurin teie asja!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 23, 'Ja ta käskis pealikut hoida teda vangis, aga kerge vahi all ja mitte takistada omi temale abiks olemast või tema juurde tulemast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 24, 'Mõne päeva pärast tuli Feeliks ühes oma naise Drusillaga, kes oli juut, ja kutsus Pauluse enese ette ning kuulas ta kõnet usust Kristusesse Jeesusesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 25, 'Aga kui Paulus kõneles õigusest ja kasinusest ja tulevasest kohtust, ehmus Feeliks ja kostis: \"Mine seks puhuks ära; kui mul aega on, kutsun ma sind jälle!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 26, 'Ühtlasi ta ka lootis Pauluselt raha saada, mistõttu ta teda ka sagedasti kutsus enese juurde ja kõneles temaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 27, 'Aga kui kaks aastat täis sai, tuli Porkius Festus Feeliksi asemele; ja et Feeliks tahtis teha juutidele meelehead, siis jättis ta Pauluse vangi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 1, 'Kui nüüd Festus oli astunud maavalitseja ametisse, läks ta kolme päeva pärast Kaisareast Jeruusalemma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 2, 'Ja ülempreestrid ja juutide peamehed esitasid temale kaebuse Pauluse vastu ning pöördusid tema poole,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 3, 'paludes Paulusele seda armu, et ta kutsuks tema Jeruusalemma, sest nad olid nõuks võtnud teda varitseda ja teel ära tappa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 4, 'Aga Festus vastas, et Paulust peetakse kinni Kaisareas ja et ta ise mõtleb peatselt sinna minna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 5, '\"Kes nüüd teie seast\", ütles ta, \"on mõjumehed, need tulgu ühes minuga alla, ja kui sellel mehel on süüd, kaevaku nad tema peale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 6, 'Ja kui ta mitte rohkem kui kaheksa või kümme päeva nende juures oli viibinud, läks ta alla Kaisareasse. Ja järgmisel päeval istus ta kohtujärjele ja käskis Pauluse ette tuua.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 7, 'Aga kui ta ette astus, asusid Jeruusalemmast tulnud juudid tema ümber ja tõstsid Pauluse vastu mitu rasket kaebust, mida nad ei suutnud tõeks teha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 8, 'Sest Paulus kaitses ennast, öeldes: \"Ei ma ole kuidagi viisi eksinud ei juutide käsu ega pühakoja vastu ega keisri vastu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 9, 'Aga Festus tahtis juutidele teha meelehead ning vastas Paulusele nõnda: \"Kas sa tahad minna Jeruusalemma ja seal minu ees lasta enese üle kohut mõista neis asjus?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 10, 'Paulus ütles: \"Ma seisan keisri kohtujärje ees, kus mu üle peab kohut mõistetama; juutidele ei ole ma teinud ühtki ülekohut, nõnda nagu sa väga hästi tead.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 11, 'Sest kui ma midagi ülekohut olen teinud ja seda, mis on surma väärt, siis ma ei tõrgu suremast; aga kui selles, milles need mind süüdistavad, ei ole mitte midagi, siis ei või ükski mind nende kätte anda; mina nõuan keisri kohut!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 12, 'Siis kõneles Festus Suurkohtu esindajatega ja vastas: \"Keisri kohut oled sa nõudnud, keisri ette sa lähed!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 13, 'Aga mõne päeva pärast tulid kuningas Agrippas ja Berniike Kaisareasse Festust tervitama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 14, 'Ja kui nad seal mitu päeva olid viibinud, jutustas Festus kuningale Pauluse asjast ning ütles: \"Feeliks on siia kinni jätnud ühe mehe,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 15, 'kelle pärast minu Jeruusalemmas olles ülempreestrid ja juutide vanemad tulid mu ette ja nõudsid tema hukkamõistmist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 16, 'Neile ma vastasin, et roomlastel ei ole kommet inimest anda hukata, enne kui süüalune on saanud olla suu suud vastu süüdistajatega ja leidnud võimaluse ennast kaitsta kaebuse vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 17, 'Kui nad nüüd siia kokku tulid, siis ma viivitamata istusin järgmisel päeval kohtujärjele ja käskisin mehe ette tuua.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 18, 'Aga kui kaebajad seisid ta ümber, ei suutnud nad leida ühtki kurja süüd, mida ma ootasin,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 19, 'vaid neil oli mõningaid vaidlusi temaga nende omist usuküsimustest ja kellegi surnud Jeesuse pärast, Keda Paulus ütles elavat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 20, 'Kui ma siis nõutu olin, kuidas seda asja lahendada, küsisin temalt, kas ta ei tahaks minna Jeruusalemma ja seal lasta enese üle kohut mõista neis asjus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 21, 'Aga kui Paulus nõudis, et teda niikaua kinni peetaks, kuni keisri majesteet teeb otsuse, siis ma käskisin teda kinni pidada seni, kui ma võin tema saata keisri juurde.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 22, 'Siis ütles Agrippas Festusele: \"Mina isegi tahaksin kuulda seda inimest!\" Ja tema ütles: \"Homme sa saad teda kuulda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 23, 'Kui nüüd Agrippas ja Berniike järgmisel päeval suure toredusega tulid ja kohtukotta läksid ühes ülempealikute ja meestega, kes linnas olid kõige ülemad, siis Festuse sõna peale toodi Paulus ette.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 24, 'Ja Festus ütles: \"Kuningas Agrippas ja kõik mehed, kes olete meiega! Te näete siin seda, kelle pärast kogu juutide hulk Jeruusalemmas ja siin on mulle peale käinud kisendades, et ta ei peaks enam ellu jääma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 25, 'Aga kui ma aru sain, et ta pole teinud midagi, mis on surma väärt, ja tema ka ise nõudis majesteedi kohut, tegin ma otsuseks saata tema sinna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 26, 'Midagi kindlat mul ei ole temast oma isandale kirjutada. Sellepärast ma olen lasknud tema tuua teie ette ja kõigepealt sinu ette, kuningas Agrippas, et mul pärast ülekuulamist oleks midagi kirjutada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 27, 'Sest minule näib mõttetuna lähetada vangi, ilma et tema vastu tõstetud süüdistust teada antaks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 1, 'Aga Agrippas ütles Paulusele: \"Sul on luba enese eest rääkida!\" Siis Paulus sirutas oma käe ja kostis enese eest nõnda:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 2, '\"Ma arvan enesele õnneks, kuningas Agrippas, et ma täna sinu ees saan kosta kõigi nende kaebuste pärast, mis juudid tõstavad minu vastu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 3, 'seda enam, et sa tunned kõiki juutide kombeid ja vaidlusküsimusi. Sellepärast ma palun sind mind kannatlikult kuulata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 4, 'Minu elukorda mu noorest east alates, kuidas see mul algusest on olnud oma rahva seas Jeruusalemmas, teavad kõik juudid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 5, 'sest nad tunnevad mind algusest peale, kui nad aga tahavad seda tunnistada, et mina kui variser olen elanud meie jumalateenistuse kõige rangemate kommete järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 6, 'Ja nüüd ma seisan kohtu ees lootuse pärast selle tõotuse peale, mille Jumal on andnud meie esiisadele');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 7, 'ja mille täitumist meie kaksteist suguharu ööd ja päevad alati Jumalat teenides loodavad näha. Selle lootuse pärast, kuningas, tõstavad juudid kaebust minu peale!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 8, 'Mis? Kas teil peetakse uskumatuks asjaks, et Jumal surnuid üles äratab?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 9, 'Ka mina ise olin arvamisel, et ma pean kõikepidi vastu panema Jeesuse Naatsaretlase nimele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 10, 'mida ma ka tegin Jeruusalemmas. Ma panin palju pühi inimesi vangi, kui ma ülemailt preestreilt olin saanud selleks loa. Ja kui need tapeti, andsin mina oma hääle selle poolt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 11, 'Ja ma nuhtlesin neid sageli kõigis kogudusekodades ja sundisin neid pilkama Jumalat ning olin päris hull nende vastu neid taga kiusates ka välismaa linnadeni.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 12, 'Ja kui ma ka neis asjus läksin Damaskusesse ülempreestrite volituse ja loaga,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 13, 'siis ma nägin, kuningas, keskpäevaajal tee peal valgust, mis oli heledam kui päike ja paistis taevast minu ja nende ümber, kes ühes minuga teel olid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 14, 'Aga kui me kõik maha kukkusime, kuulsin ma üht häält mulle heebrea keeli ütlevat: Saul! Saul! Miks sa Mind taga kiusad? Sulle läheb raskeks astla vastu takka üles lüüa!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 15, 'Mina aga ütlesin: kes Sa oled, Issand? Issand ütles: Mina olen Jeesus, Keda sa taga kiusad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 16, 'Tõuse siis nüüd üles ja seisa oma jalgel, sest Ma olen sulle ilmunud selleks, et Ma sind seaksin Oma sulaseks ja selle tunnistajaks, kuidas Ma veel sulle ilmun ning');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 17, 'päästan sind su rahva ja paganate käest, kelle juurde Ma sind nüüd läkitan');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 18, 'avama nende silmi, et nad pimedusest pöörduksid valguse poole ja saatana võimuse alt Jumala poole ning saaksid pattude andeksandmist ja osa nende seas, kes on pühitsetud usu läbi Minusse!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 19, 'Sellepärast, kuningas Agrippas, ei olnud ma kuulmatu taevase nägemuse vastu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 20, 'vaid jutlustasin esiti neile, kes elavad Damaskuses, ja siis neile, kes elavad Jeruusalemmas ja kogu Judeamaal, ja paganaile, et nad meelt parandaksid ja pöörduksid Jumala poole ning teeksid õigeid meeleparanduse tegusid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 21, 'Selle asja pärast on juudid mind kinni võtnud pühakojas ja on püüdnud mind tappa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 22, 'Aga et ma tänapäevani olen Jumalalt abi saanud, siis ma seisan veel siin ja tunnistan nii väikestele kui suurtele ega ütle muud midagi kui seda, mis prohvetid ja Mooses on rääkinud tulevasist asjust,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 23, 'et Kristus pidi kannatama ja esimesena surnuist üles tõusma ning kuulutama valgust niihästi juutidele kui paganaile!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 24, 'Aga kui ta nõnda enese eest kostis, ütles Festus suure häälega: \"Paulus, sa jampsid! Suur kirjatarkus paneb sind jampsima!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 25, 'Aga Paulus ütles: \"Auline Festus, ma ei jampsi mitte, vaid ma räägin tõelisi ja mõistlikke sõnu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 26, 'Sest kuningas teab neid asju küll ja temale mina räägin ka julgesti, sest ma ei arva midagi temale teadmata olevat neist asjust; ei ole ju need nurgas sündinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 27, 'Kas sina, kuningas Agrippas, usud prohveteid? Ma tean, et sa usud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 28, 'Aga Agrippas ütles Paulusele: \"Ei puudu palju, sa meelitad mind, et ma saaksin kristlaseks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 29, 'Aga Paulus ütles: \"Ma sooviksin küll Jumalalt, olgu piskus või paljus, et mitte ainult sina, vaid ka kõik, kes mind täna kuulete, saaksite niisuguseiks nagu mina olen, aga ilma nende köidikuteta!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 30, 'Siis tõusid üles kuningas ja maavalitseja ja Berniike ning need, kes nendega istusid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 31, 'ja läksid kõrvale ning rääkisid isekeskis ja ütlesid: \"See inimene ei tee midagi, mis oleks surma ja köidikute väärt!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 32, 'Siis Agrippas ütles Festusele: \"Selle inimese oleks võinud lahti lasta, kui ta mitte ei oleks nõudnud keisri kohut!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 1, 'Kui oli otsustatud, et me laevaga sõidame Itaaliasse, anti Paulus ja mõned teised vangid ühe pealiku hoolde, nimega Juulius, kes oli üks keiserliku väesalga ülemaid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 2, 'Ja me astusime Adramüti laeva, mille tee pidi minema Aasiamaa kohtade kaudu, ja purjetasime edasi. Meiega oli ka Aristarhos, makedoonlane Tessaloonikast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 3, 'Ja teisel päeval me saabusime Siidonisse. Ja Juulius kohtles Paulust lahkesti ning lubas teda minna oma sõprade juurde, et need hoolitseksid tema eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 4, 'Ja kui me sealt olime läinud merele, purjetasime Küprose varju, sest tuuled olid vastu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 5, 'ja kui olime purjetanud läbi Kiliikia ja Pamfüülia- äärse mere, saabusime Mürrasse Lüükiamaal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 6, 'Seal leidis pealik Aleksandria laeva, mis läks Itaaliasse, ja pani meid sellesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 7, 'Aga kui me mitu päeva olime vähehaaval edasi purjetanud ja vaevaga saanud Kniidose kohale, ei lasknud tuul meid randa ja me purjetasime Kreeta varju Salmoone juures');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 8, 'ja saime ainult vaevaga sealt mööda ning jõudsime ühte paika, mida nimetatakse Heaks Sadamaks ja mille lähedal oli Lasaia linn.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 9, 'Aga kui palju aega oli kulunud ja laevasõit juba läks kardetavaks ja ka paastuaeg oli möödunud, hoiatas Paulus neid');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 10, 'ning ütles: \"Mehed, ma näen, et laevasõit ähvardab tuua raskusi ja suurt hädaohtu mitte ainult koormale ja laevale, vaid ka meie elule!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 11, 'Aga pealik usaldas rohkem tüürimeest ja laevajuhti kui seda, mis Paulus ütles.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 12, 'Ja et sinna sadamasse ei olnud hea üle talve jääda, siis oli suurema hulga nõu sealt ära minna ja kui võimalik, jõuda Foiniksisse, ühte Kreeta sadamasse vastu edela- ja loodetuult, ja sinna ületalve jääda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 13, 'Aga kui lõunatuul hakkas puhuma ja nad arvasid oma nõu võivat täide viia, tõmbasid nad ankru üles ja liikusid edasi Kreeta ranna läheduses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 14, 'Ent varsti pärast seda tõusis saare poolt marutuul, mida hüütakse kirdemaruks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 15, 'Kui laev kisti sellega kaasa ega saanud tuulele vastu panna, andsime endid tuule ajada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 16, 'Sattudes siis ühe väikese saare varju, mida hüütakse Klaudaks, suutsime hädavaevalt toime saada paadiga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 17, 'Kui see oli üles tõmmatud, tarvitati veel teisi abinõusid ja laev seoti ümbert kinni. Ja et kardeti sattuda Sürti liivale, võeti purjed maha ja lasti ennast edasi ajada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 18, 'Aga kui rajuilm meid väga vintsutas, heitsid nad teisel päeval muist koormat välja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 19, 'Ja kolmandal päeval viskasid nad oma käega laeva riistad välja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 20, 'Aga kui mitu päeva ei paistnud ei päikest ega tähti ja maru läks väga kangeks, lõppes viimaks kõik lootus veel pääseda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 21, 'Kui nad siis kaua olid olnud söömata, astus Paulus nende keskele ja ütles: \"Mehed, te oleksite pidanud kuulama minu nõu ja Kreetast mitte ära tulema, siis te oleksite vältinud selle häda ja kahju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 22, 'Nüüd ma manitsen teid olla julged, sest ükski hing teie seast ei hukku, vaid üksnes laev.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 23, 'Sest sel ööl seisis minu juures selle Jumala Ingel, Kelle Oma ma olen ja Keda ma ka teenin,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 24, 'ning ütles: ära karda Paulus, sa pead saama keisri ette, ja vaata, Jumal on sulle kinkinud need kõik, kes on ühes sinuga laeval!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 25, 'Sellepärast, mehed, olge julges meeles, sest ma usun Jumalat, et nõnda sünnib, nagu mulle on öeldud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 26, 'Aga me peame ajama ühele saarele.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 27, 'Kui juba jõudis kätte neljateistkümnes öö, kui meid Aadria merel aeti sinna ja tänna, siis arvasid laevamehed kesköö ajal endid ühele maale lähenevat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 28, 'Ja kui nad loodi vette heitsid, leidsid nad kakskümmend sülda vett. Ja kui nad pisut edasi olid jõudnud ja jälle loodi heitsid, leidsid nad viisteist sülda vett.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 29, 'Siis nad kartsid sattuda karile ja heitsid laeva tagumisest otsast välja neli ankrut ja jäid ootama päevavalgust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 30, 'Aga kui laevamehed püüdsid laevast põgeneda ja paati merre lasksid, nagu tahaksid nad laeva ninast ankrut sisse lasta,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 31, 'ütles Paulus pealikule ja sõjameestele: \"Kui need ei jää laeva, siis te ei pääse eluga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 32, 'Siis raiusid sõjamehed paadi köied katki ja lasksid selle merre kukkuda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 33, 'Aga seni kui valgeks läks, manitses Paulus neid kõiki leiba võtta ja ütles: \"Täna on neljateistkümnes päev, kui te ootate ja olete söömata ega ole midagi suhu võtnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 34, 'Sellepärast ma manitsen teid leiba võtta, sest see on tarvilik teie pääsemiseks; ei tohi ju ühelgi teie seast juuksekarvgi peast kaotsi minna!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 35, 'Kui ta seda oli öelnud, võttis ta leiva, tänas Jumalat nende kõikide nähes, murdis ja hakkas sööma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 36, 'Siis nende kõikide meel läks heaks ja nad võtsid ka leiba.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 37, 'Aga neid kõiki oli laeval kakssada seitsekümmend kuus hinge.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 38, 'Ja kui nad olid leiba võtnud, kergendasid nad veel laeva, heites vilja merre.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 39, 'Kui valgeks läks, siis nad ei tundnud, mis maa see oli, aga nad nägid üht lahte, millel oli sobiv rand. Sinna nad otsustasid, kui võimalik, laeva ajada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 40, 'Nad raiusid ankrud katki ja lasksid need merre vajuda; siis nad päästsid tüüri köied lahti ja seadsid väikese purje tuule järele ning suunasid sõidu ranna poole.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 41, 'Ent nad sattusid neemele ja ajasid laeva sinna; laeva nina tungis sinna sisse ja jäi liikumata seisma, aga pära katkes kangete lainete käes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 42, 'Ent sõjameestel oli nõu vangid ära tappa, et ükski neist ujudes ei pääseks põgenema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 43, 'Kuid pealik, tahtes Paulust päästa, keelas neid seda nõu täitmast ja käskis neid, kes oskasid ujuda, esimestena hüpata vette ja väljuda maale,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 44, 'aga teisi ta käskis püüda randa jõuda laudadel ja muist laevatükkidel. Ja sel kombel pääsesid kõik tervelt maale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 1, 'Kui olime pääsenud, saime teada, et saart hüütakse Meliteks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 2, 'Aga umbkeelsed osutasid meile suurt lahkust; nad tegid tule maha ja võtsid meid kõiki selle äärde vihmasaju ja külma pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 3, 'Aga kui Paulus sületäie hagu kokku riisus ja tulle pani, tuli kuumuse tõttu välja rästik ja hakkas tema käest kinni!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 4, 'Kui umbkeelsed nägid seda looma tema käe küljes rippuvat, ütlesid nad isekeskis: \"Vist on see mees inimesetapja, et õiguse jumalanna teda ei lase elada, ehk ta küll on pääsenud merest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 5, 'Aga tema raputas eluka tulle ega tundnud mingit viga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 6, 'Nad ootasid küll, et ta üles paistetab või äkitselt surnuna maha langeb. Aga kui nad olid kaua oodanud ja nägid, et talle ei sündinud mingit viga, mõtlesid nad teisiti ja ütlesid tema jumala olevat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 7, 'Aga selle paiga läheduses oli saare tähtsamal mehel, nimega Puublius, mõis. Tema võttis meid vastu ja pidas meid kolm päeva lahkesti külalistena.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 8, 'Ja sündis, kui Puubliuse isa oli haige maas palavikus ja kõhutõves, et Paulus läks tema juurde, palvetas ja pani oma käed ta peale ning tegi tema terveks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 9, 'Kui see oli sündinud, tulid ka teised haiged saarelt ligi ja said terveks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 10, 'Ja nemad austasid meid mitmel viisil, ja kui me hakkasime ära minema, andsid nad teekonna jaoks, mis vaja oli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 11, 'Aga kolme kuu pärast me läksime sealt minema Aleksandria laevaga, mis oli ületalve olnud sellel saarel ja mille tunnuseks oli Dioskuuride märk.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 12, 'Me maabusime Sürakuusas ja olime seal kolm päeva paigal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 13, 'Sealt me sõitsime edasi ja jõudsime Reegioni, ja kui ühe päeva pärast tõusis lõunatuul, saabusime teisel päeval Puteolisse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 14, 'Sealt me leidsime vendi, ja nende kutsel jäime seitsmeks päevaks nende juurde. Siis me läksime Rooma linna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 15, 'Ja kui sealsed vennad meist kuulda said, tulid nad meile vastu Appiuse turuni ja Trestabernani. Neid nähes tänas Paulus Jumalat ja sai julgust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 16, 'Aga kui saabusime Rooma, anti Paulusele luba jääda omaette elama ühes sõduriga, kes teda valvas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 17, 'Aga kolme päeva pärast Paulus kutsus kokku juutide peamehed. Kui need kokku tulid, ütles ta neile: \"Mehed, vennad, mina pole teinud midagi meie rahva ja esiisade kommete vastu, ja ometi ma olen Jeruusalemmast vangina antud üle roomlaste kätte;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 18, 'ja kui need mind olid üle kuulanud, tahtsid nad mind vabaks lasta, sest minul ei olnud ühtki surmasüüd.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 19, 'Aga kui juudid selle vastu rääkisid, siis ma pidin häda pärast nõudma keisri kohut, aga mitte, nagu oleks mul midagi kaebamist oma rahva peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 20, 'Selle asja pärast ma teid nüüd kutsusin enese juurde, et teid näha ja teiega rääkida; sest Iisraeli lootuse pärast ma olen kinni selles ahelas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 21, 'Aga nemad ütlesid talle: \"Me ei ole saanud ühtki kirja Juudamaalt sinu kohta; ei ole ka tulnud siia ühtki venda, kes sinust oleks teatanud ehk rääkinud midagi paha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 22, 'Ent meie soovime siiski kuulda sinult, mida sa mõtled; sest sellest väärusust on meil teada, et selle vastu igas paigas räägitakse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 23, 'Ja nad määrasid temale päeva ja siis tuli tema juurde majasse veel rohkem inimesi. Neile ta seletas asja ja tunnistas Jumala Riiki ning meelitas hommikust õhtuni neid uskuma Moosese käsuõpetusest ja prohvetitest seda, mis Jeesusest on kirjutatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 24, 'Siis ühed veendusid tema sõnus, teised aga ei uskunud mitte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 25, 'Aga kui nad isekeskis ei olnud üksmeelsed, läksid nad ära, kui Paulus oli öelnud selle sõna: \"Püha Vaim on hästi rääkinud prohvet Jesaja kaudu teie esiisadele');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 26, 'ja on öelnud: mine selle rahva juurde ja ütle: kuuldes te kuulete ega mõista, ja nähes te näete ega taipa!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 27, 'Sest selle rahva süda on tuimaks läinud ja nad kuulevad raskesti oma kõrvadega ja sulevad oma silmad, et nad silmadega ei näeks ja kõrvadega ei kuuleks ja südamega ei mõistaks ega pöörduks, et Mina neid parandaksin!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 28, 'Siis olgu teile teada, et see Jumala pääste on läkitatud paganaile ja nemad võtavad seda kuulda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 29, 'Ja kui ta seda oli öelnud, läksid juudid minema ja neil oli isekeskis palju vaidlemist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 30, 'Aga Paulus jäi kaheks terveks aastaks oma üürielamusse ja võttis vastu kõiki, kes tulid tema juurde,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 31, 'ja kuulutas Jumala Riiki ning õpetas Issandast Jeesusest Kristusest kõige julgusega ilma takistamata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 1, 'Paulus, Jeesuse Kristuse sulane, kutsutud Apostliks, välja valitud kuulutama Jumala Evangeeliumi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 2, 'mille Jumal on enne tõotanud Oma prohvetite kaudu pühades kirjades');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 3, 'Oma Pojast - Kes liha poolest on sündinud Taaveti soost');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 4, 'ja pühaduse vaimu poolest on seatud surnuist ülestõusmise läbi Jumala Pojaks väes - Jeesusest Kristusest, meie Issandast,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 5, 'Kelle läbi me oleme saanud armu ja apostliameti, et äratada usu sõnakuulelikkust Tema nime heaks kõigi rahvaste seas,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 6, 'kelle seast ka teie olete Jeesuse Kristuse poolt kutsutud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 7, 'kõigile Roomas olevaile Jumala armastatuile, kutsutud pühadele: armu teile ja rahu Jumalalt, meie Isalt, ja Issandalt Jeesuselt Kristuselt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 8, 'Kõigepealt ma tänan oma Jumalat Jeesuse Kristuse läbi teie kõikide pärast, et teie usust kiitvalt kõneldakse kogu maailmas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 9, 'Sest Jumal, Keda ma oma vaimus teenin Tema Poja Evangeeliumi kuulutamisega, on mu tunnistaja, kuidas ma lakkamata pean teid meeles,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 10, 'aina oma palvetes anudes, kas ma juba kord Jumala tahtmisel saaksin tulla teie juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 11, 'Sest ma igatsen teid näha, et võiksin jagada teile pisut vaimulikku annet teie kinnituseks;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 12, 'see on, et me teie juures üheskoos olles vastastikku ergutaksime üksteist ühise usu, teie ja minu oma läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 13, 'Ja ma ei taha, vennad, et teil oleks teadmata, et ma sageli olen ette võtnud tulla teie juurde, et ma ka teie seas võiksin saata mõnd kasu, nõnda nagu muude paganate seas; aga ma olin tänini takistatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 14, 'Ma olen nii kreeklaste kui umbkeelsete, nii tarkade kui rumalate võlglane;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 15, 'siis olen ma omalt poolt valmis ka teile, Roomas asuvaile, Evangeeliumi kuulutama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 16, 'Sest ma ei häbene Evangeeliumi; sest see on Jumala vägi õndsakssaamiseks igaühele, kes usub, nii juudile esiti kui ka kreeklasele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 17, 'Sest temas saab ilmsiks Jumala õigus usust usku, nõnda nagu on kirjutatud: \"Õige elab usust!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 18, 'Sest Jumala viha saab ilmsiks taevast kõige inimeste jumalatuse ja ülekohtu vastu, nende vastu, kes tõde ülekohtuga kinni peavad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 19, 'sellepärast et see, mida teatakse Jumalast, on avalik nende seas; sest Jumal on seda neile avaldanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 20, 'Sest Tema nähtamatut olu, nii Tema igavest väge kui jumalikku olemist, nähakse, kui neid pannakse tähele, Tema tegudes maailma loomisest alates, nii et nad ei saa vabanduda,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 21, 'sellepärast et nad Jumalat tundes ei ole teda kui Jumalat austanud ega tänanud, vaid on oma mõtlemistes saanud tühiseks ja nende mõistmatu süda on läinud pimedaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 22, 'Kiites endid targaks, on nad saanud jõledaks');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 23, 'ja on kadumatu Jumala au vahetanud kaduva inimese ja lindude ja neljajalgsete ja roomajate sarnase kuju vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 24, 'Sellepärast on Jumal nad andnudki nende südamete himudes rüvedusse, oma ihusid ise pilastama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 25, 'Nemad on Jumala tõe vahetanud vale vastu ja on austanud ning teeninud loodut enam kui Loojat, Kes on ülistatud igavesti. Aamen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 26, 'Sellepärast on Jumal nad andnud häbematuisse ihadesse; sest nende naised on vahetanud loomuliku sugulise käitlemise loomuvastasega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 27, 'ja samuti on ka mehed loobunud loomulikust naise sugulisest käitlemisest ja on oma himus süttinud üksteisest ja teinud rõvedust mees mehega ja kätte saanud iseenestes oma eksimuse palga, mille pidid saama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 28, 'Ja nagu nad heaks ei arvanud kinni pidada Jumala tunnetamisest, nii on Jumal nad andnud hoolimatu meele sisse, tegema seda, mis ei kõlba;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 29, 'nad on täis kõike ülekohut, kurjust, ahnust, tigedust, täis kadedust, mõrva, riidu, kurikavalust; nad on keelekandjad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 30, 'laimajad, Jumala vihkajad, ülbed, suurelised, hooplejad, leidlikud kurjale, sõnakuulmatud vanemaile,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 31, 'mõistmatud, lepingu rikkujad, armuheitmatud, halastamatud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 32, 'kes, ehk nad küll tunnevad Jumala õiguse määrust ja teavad, et need, kes teevad niisuguseid asju, on surma väärt, ei tee mitte ainult sedasama, vaid tunnevad head meelt neist, kes nõnda teevad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 1, 'Sellepärast, oh inimene, kes kohut mõistad, ole sa kes tahes, ei ole sul mingit vabandust! Sest milles sa kohut mõistad teise üle, sessamas sa mõistad iseenese hukka; sest sina, kes teist hukka mõistad, teed ise sedasama!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 2, 'Aga me teame, et Jumala kohus tõepoolest tabab neid, kes sellesarnast teevad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 3, 'Või mõtled sa, oh inimene, kes kohut mõistad nende üle, kes sellesarnast teevad, ja teed ka sedasama, et sa pääsed Jumala kohtu eest?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 4, 'Või põlgad sa Tema helduse ja kannatlikkuse ja pika meele rohkust ega saa aru, et Jumala heldus sind ajab meelt parandama?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 5, 'Ent sa kogud enesele viha oma kangusega ja patustpöördumata südamega vihapäevaks ja Jumala õige kohtu ilmumiseks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 6, 'Kes igaühele maksab tema tegude järgi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 7, 'neile küll igavest elu, kes head tehes püsivusega otsivad au ja kiitust ja kadumatut põlve,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 8, 'aga neile, kes on riiakad ja tõele sõnakuulmatud, aga sõnakuulelikud ülekohtule, viha ja raevu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 9, 'Viletsus ning ahastus tuleb igale inimese hingele, kes kurja teeb, nii juudile esiti kui ka kreeklasele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 10, 'aga au ja kiitus ja rahu igaühele, kes head teeb, nii juudile esiti kui ka kreeklasele!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 11, 'Sest Jumal ei tee vahet isikute vahel!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 12, 'Sest kõik, kes ilma käsuta on pattu teinud, lähevad ka hukka ilma käsuta; ja kõik, kes käsu all on pattu teinud, nende üle mõistetakse ka kohut käsu järgi;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 13, 'sest käsu kuuljad ei ole õiged Jumala ees, vaid käsu tegijad mõistetakse õigeks -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 14, 'sest kui paganad, kellel ei ole käsku, loomu poolest täidavad käsu nõudeid, on nad, ehk küll neil ei ole käsku, iseenestele käsuks');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 15, 'ja näitavad, et käsutegu on kirjutatud nende südametesse, kuna nende südametunnistus ühtlasi tunnistab seda ja nende mõtted isekeskis kas kaebavad nende peale või ka kostavad nende eest');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 16, '- sel päeval, kui Jumal kohut mõistab inimeste salajaste asjade üle Kristuse Jeesuse läbi minu Evangeeliumi järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 17, 'Ent kui sa nimetad ennast juudiks ja loodad käsu peale ning kiitled Jumalast');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 18, 'ja tunned Tema tahtmist, ja käsust õpetust saades katsud läbi, mis on kõige parem,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 19, 'ja pead ennast sõgedate teejuhiks, nende valguseks, kes on pimeduses,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 20, 'mõistmatute juhatajaks, alaealiste õpetajaks, kellel on käes täieks kujunenud tunnetus ja tõde käsuõpetuses;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 21, 'kuidas sa siis, kes teist õpetad, iseennast ei õpeta; kes kuulutad, et ei tohi varastada, ise varastad;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 22, 'kes ütled, et ei tohi abielu rikkuda, ise rikud abielu; kes pead ebajumalaid jäledaks asjaks, ise riisud pühamut;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 23, 'kes kiitled käsust, ise teotad Jumalat käsust üleastumisega?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 24, 'Pilgatakse ju Jumala nime teie pärast paganate seas, nõnda nagu on kirjutatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 25, 'Sest ümberlõikamisest on küll kasu, kui sa teed käsu järgi; aga kui sa oled käsust üleastuja, siis on sinu ümberlõikamine saanud eesnahaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 26, 'Kui nüüd ümberlõikamatu peab käsu nõudmisi, eks siis tema eesnahka peeta ümberlõikamiseks?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 27, 'Ja eks siis see, kes loomu poolest on ümberlõikamatu ja peab täiesti käsku, mõista kohut sinu üle, kes kirjatähe ja ümberlõikamise kaudu oled ometi käsust üleastuja?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 28, 'Ei ole ju juut see, kes seda on välispidi; ega ole ümberlõikamine see, mis sünnib välispidi lihas,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 29, 'vaid juut on see, kes seda on seespidi, ja südame ümberlõikamine on vaimus, mitte kirjatähes. Niisugune saab kiituse mitte inimestelt, vaid Jumalalt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 1, 'Mis paremus on siis juudil? Või mis kasu on ümberlõikamisest?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 2, 'On paljugi kõikepidi! Kõigepealt see, et nende kätte on usaldatud, mida Jumal on rääkinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 3, 'Mis sellest, kui mõned olid uskmatud? Ega siis nende uskmatu meel tee tühjaks Jumala ustavust?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 4, 'Mitte sugugi! Kindel on, et Jumal on tõemeelne, olgugi, et iga inimene on valelik, nagu on kirjutatud: \"Et Sa oleksid õiglane Oma sõnades ja võit jääks Sinule, kui Sinuga kohtus käiakse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 5, 'Aga kui meie ülekohus teeb avalikuks Jumala õigluse, mis peame siis ütlema? Kas Jumal ei ole ülekohtune, kui Ta nuhtleb vihas? Ma räägin kui inimene.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 6, 'Mitte sugugi! Kuidas võiks siis Jumal kohut mõista maailma üle?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 7, 'Sest kui Jumala tõde minu vale läbi saab palju selgemaks Tema auks, miks siis veel minu kui patuse üle kohut mõistetakse?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 8, 'Ja miks me ei teeks nõnda, nagu meist pilgates öeldakse ja nagu mõned meist räägivad, et meie ütlevat: \"Tehkem kurja, et sellest tuleks head?\" Niisugune nuhtlus on õige.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 9, 'Kuidas siis on? Kas me oleme paremas seisukorras? Mitte sugugi! Oleme ju varemini tõestanud, et niihästi juudid kui kreeklased on kõik patu all,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 10, 'nõnda nagu on kirjutatud: \"Ei ole kedagi, kes oleks õige, ei ühtainustki;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 11, 'ei ole arusaajat; ei ole, kes otsiks Jumalat;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 12, 'nad kõik on läinud kõrvale, nad on puha saanud kõlvatuks, ei ole kedagi, kes teeb head, ei ühtainustki.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 13, 'Nende kurk on lahtine haud; oma keelega nad petavad; madude mürk on nende huulte taga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 14, 'Nende suu on täis sajatamist ja kibedaid sõnu;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 15, 'nende jalad on nobedad verd valama;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 16, 'hävitus ja õnnetus on nende teedel,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 17, 'ja rahu teed nad ei tunne;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 18, 'ei ole Jumala kartust nende silmade ees!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 19, 'Aga me teame, et mida iganes käsk ütleb, seda ta ütleb neile, kes on käsu all, et iga suu suletaks ja kogu maailm oleks süüalune Jumala ees;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 20, 'sellepärast et käsu tegudest ei mõisteta õigeks ühtki liha tema ees; sest käsu kaudu tuleb patu tundmine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 21, 'Aga nüüd on Jumala õigus, millest käsk ja prohvetid tunnistavad, ilmutatud ilma käsuta,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 22, 'see Jumala õigus, mis tuleb Jeesuse Kristuse usu kaudu kõikidele, kes usuvad; sest siin ei ole ühtki vahet;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 23, 'sest kõik on pattu teinud ja on Jumala aust ilma');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 24, 'ning mõistetakse õigeks täiesti muidu, Tema armust, lunastuse läbi, mis on Kristuses Jeesuses,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 25, 'Kelle Jumal on seadnud lepitusvahendiks usu kaudu Tema veres, Oma õiguse osutamiseks enne tehtud pattude arvestamata jätmise pärast');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 26, 'tol ajal, kui Jumal seda sallis, et osutada Oma õigust praegusel ajal ning näidata, et Tema on õige ja teeb õigeks selle, kes on Jeesuse usust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 27, 'Kus on nüüd kiitlemine? See on kõrvaldatud. Missuguse käsu läbi? Kas tegude käsu läbi? Ei mitte, vaid usu käsu läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 28, 'Sellepärast me arvame, et inimene mõistetakse õigeks usu läbi lahus käsu tegudest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 29, 'Või on Jumal ükspäinis juutide Jumal? Eks Ta ole ka paganate Jumal? Jah, tõesti ka paganate Jumal!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 30, 'Sest üks ja sama Jumal teeb usust õigeks ümberlõigatu ja usu läbi ümberlõikamatu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 31, 'Kas me siis teeme käsu tühjaks usu läbi? Mitte sugugi, vaid me kinnitame käsku!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 1, 'Mis me siis ütleme oma lihase esiisa Aabrahami saavutanud olevat?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 2, 'Sest kui Aabraham on õigeks mõistetud tegudest, on temal kiitlemist, kuid mitte Jumala ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 3, 'Sest mis ütleb Kiri? \"Aabraham uskus Jumalat ja see arvati temale õiguseks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 4, 'Aga tegude tegijale ei arvata palka mitte armust, vaid teenet mööda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 5, 'Aga kes tegusid ei tee, vaid usub Temasse, Kes mõistab õigeks jumalatu, sellele arvatakse tema usk õiguseks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 6, 'nõnda nagu Taavet kiidab õndsaks inimest, kellele Jumal arvab õiguse osaks ilma tegudeta:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 7, '\"Õndsad need, kelle ülekohus on andeks antud ja kelle patud on kinni kaetud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 8, 'Õnnis see, kellele Jumal pattu ei arvesta!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 9, 'Kas nüüd see õndsakskiitmine käib ainult ümberlõigatute kohta või ka ümberlõikamatute kohta? Me ju ütleme, et Aabrahamile usk arvati õiguseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 10, 'Kuidas see siis arvati? Kas siis, kui ta oli ümberlõigatu või kui ta oli ümberlõikamatu? Mitte kui ta oli ümberlõigatu, vaid kui ta oli ümberlõikamatu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 11, 'Ja ümberlõikamise märgi ta sai selle usu õiguse kinnituseks, mis tal oli ümberlõikamata põlves, et ta oleks kõikide isa, kes usuvad ümberlõikamatutena, et õigus arvatakse ka neile osaks');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 12, 'ja et ta oleks ka ümberlõigatute isa, nende isa, kes mitte ainult ei ole ümber lõigatud, vaid ka käivad meie isa Aabrahami usu jälgedes, mis tal oli juba ümberlõikamatuna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 13, 'Sest see tõotus, et Aabraham saab maailma pärijaks, ei antud temale ega tema soole mitte käsu läbi, vaid usu õiguse kaudu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 14, 'Sest kui need, kes on käsust, on pärijad, siis on usk tühistatud ja tõotus on tehtud mõjutuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 15, 'Sest käsk toob viha; aga kus ei ole käsku, seal ei ole ka üleastumist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 16, 'Sellepärast on see usust, et see oleks armust, et tõotus oleks kindel kogu soole, mitte üksnes sellele, mis on pärit käsust, vaid ka sellele, mis on pärit Aabrahami usust, kes on meie kõikide isa -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 17, 'nagu on kirjutatud: \"Ma olen sind pannud paljude rahvaste isaks!\" - selle Jumala ees, Keda ta uskus ja Kes teeb elavaks surnud ja kutsub esile olematuid asju otsekui olevaid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 18, 'Tema uskus üle lootuse lootuses, et ta saab paljude rahvaste isaks vastavalt sõnale: \"Nõnda peab olema sinu sugu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 19, 'Ja ta ei läinud usus nõdraks tähele pannes oma ihu, mis oli elatanud - oli ta ju umbes saja-aastane - ja et Saara lapsekoda oli surnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 20, 'Ent Jumala tõotusest ta ei mõtelnud uskmatuses kaksipidi, vaid sai vägevaks usus, andes Jumalale austust');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 21, 'ja olles täiesti julge selles, et Jumal on vägev tegema, mis Ta on tõotanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 22, 'Sellepärast see arvatigi temale õiguseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 23, 'Aga ei ole mitte üksnes tema pärast kirjutatud, et see arvati temale õiguseks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 24, 'vaid ka meie pärast, kellele see peab arvatama õiguseks, sest me usume Temasse, Kes surnuist üles äratas meie Issanda Jeesuse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 25, 'Kes loovutati meie üleastumiste pärast ja üles äratati meie õigekssaamise pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 1, 'Et me nüüd oleme usust õigeks saanud, siis on meil rahu Jumalaga meie Issanda Jeesuse Kristuse läbi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 2, 'Kelle läbi me oleme ka usus saanud ligipääsu sellele armule, milles me nüüd oleme, ja kiitleme Jumala au lootusest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 3, 'Aga mitte üksnes sellest, vaid me kiitleme ka viletsustest, teades, et viletsus saadab kannatlikkuse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 4, 'ja kannatlikkus saadab püsivuse ja püsivus lootuse;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 5, 'aga lootus ei jäta häbisse, sest Jumala armastus on välja valatud meie südameisse Püha Vaimu läbi, Kes meile on antud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 6, 'Sest Kristus on, kui me alles nõdrad olime, omal ajal surnud nende eest, kes alles olid jumalatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 7, 'Vaevalt ju keegi läheb surma õige eest; ehk mõni küll julgeks surra hea sõbra eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 8, 'Ent Jumal osutab Oma armastust meie vastu sellega, et Kristus on surnud meie eest, kui me alles patused olime;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 9, 'kui palju enam me nüüd, olles õigeks saanud Tema veres, pääseme Tema kaudu viha eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 10, 'Sest kui meid Jumalaga lepitati Tema Poja surma kaudu, kui me alles olime Jumala vaenlased, kui palju enam päästetakse meid Tema elu läbi nüüd, kus me juba oleme lepitatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 11, 'Aga mitte ainult seda, vaid me kiitleme ka Jumalast meie Issanda Jeesuse Kristuse läbi, Kelle kaudu me nüüd oleme saanud lepituse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 12, 'Sellepärast, otsekui ühe inimese kaudu patt tuli maailma ja patu läbi surm, nõnda on ka surm tunginud kõigisse inimestesse, sest nad kõik on pattu teinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 13, 'Sest patt oli maailmas ka enne käsku, kuid pattu ei arvata süüks seal, kus ei ole käsku.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 14, 'Ometi valitses surm Aadamast Mooseseni ka neid, kes ei olnud pattu teinud samasuguse üleastumisega nagu Aadam, kes oli selle eeltähendus, kes pidi tulema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 15, 'Aga ka armuanniga ei ole lugu nõnda nagu pattulangemisega; sest kui ühe inimese pattulangemise läbi paljud on surnud, siis on veel palju enam Jumala arm ja and selle ühe Inimese Jeesuse Kristuse armu läbi ülirohkesti saanud osaks neile paljudele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 16, 'Ja ka anniga ei ole nõnda nagu sellega, mis on tulnud ühe pattulangenu läbi; sest ühest ainsast pattulangemisest on kohus saanud hukkamõistmiseks, aga armuand mitmest pattulangemisest õigeksmõistmiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 17, 'Sest kui ühe inimese pattulangemise tõttu surm on valitsenud selle ühe läbi, kui palju enam peavad need, kes saavad armu ja õiguse anni täiuse, valitsema elus selle ühe, Jeesuse Kristuse läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 18, 'Nõnda siis, otsekui üks langemine on saanud hukkamõistmiseks kõigile inimestele, nõnda saab ka üks õiguse täitmine kõigile inimestele elu õigustuseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 19, 'Sest otsekui tolle ühe inimese sõnakuulmatuse läbi paljud on saanud patuseks, nõnda saavad ka selle ühe inimese sõnakuulelikkuse läbi paljud õigeks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 20, 'Aga käsk tuli kõrvalt sisse, et langemine saaks suuremaks. Ent kus patt on suurenenud, seal on ka arm saanud ülirohkeks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 21, 'et otsekui patt on valitsenud surmas, samuti ka arm valitseks õiguse kaudu igaveseks eluks Jeesuse Kristuse, meie Issanda läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 1, 'Mis me nüüd ütleme? Kas peame jääma patusse, et arm suureneks?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 2, 'Ei milgi kombel! Meie, kes oleme ära surnud patule, kuidas peaksime veel elama selles?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 3, 'Või te ei tea, et nii mitu, kui meid on ristitud Kristusesse Jeesusesse, oleme ristitud Tema surmasse?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 4, 'Me oleme siis surmasse ristimise kaudu ühes Temaga maha maetud, et otsekui Kristus on surnuist üles äratatud Isa au läbi, nõnda meiegi käiksime uues elus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 5, 'Sest kui me oleme kasvanud ühte Tema surma sarnasusega, siis saame üheks ka Tema ülestõusmise sarnasusega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 6, 'kuna me seda teame, et meie vana inimene on ühes temaga risti löödud, et patuihu kaotataks, nõnda et me enam ei orjaks pattu;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 7, 'sest kes on surnud, see on õigeks mõistetud patust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 8, 'Aga kui me ühes Kristusega oleme surnud, siis usume, et me ühes Temaga saame ka elama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 9, 'Sest me teame, et Kristus pärast seda, kui Ta surnuist üles äratati, enam ei sure; surm ei valitse enam Tema üle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 10, 'Sest mis Ta suri, seda Ta suri patule ühe korra; aga mis Ta elab, elab Ta Jumalale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 11, 'Nõnda arvake teiegi endid surnud olevat patule, aga elavat Jumalale Kristuses Jeesuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 12, 'Seepärast ärgu valitsegu siis patt teie surelikus ihus, et te kuulaksite teda tema himudes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 13, 'Ärge ka mitte andke oma liikmeid ülekohtu relviks patule, vaid andke iseendid Jumalale kui need, kes surnuist on saanud elavaks, ja oma liikmed õiguse relviks Jumalale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 14, 'Sest patt ei tohi teie üle valitseda, sellepärast et teie ei ole käsu all, vaid armu all.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 15, 'Kuidas siis? Kas hakkame pattu tegema, sest me ei ole käsu all, vaid armu all? Mitte sugugi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 16, 'Eks te tea, et kelle orjaks te hakkate sõnakuulmises, selle orjad te olete, kelle sõna te kuulete, kas patu orjad surmaks või sõnakuulmise orjad õiguseks?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 17, 'Aga tänu Jumalale, et te olite patu orjad, aga nüüd olete südamest saanud sõnakuulelikuks sellele õpetuse väljendusele, mille juurde teid on juhatatud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 18, 'ja et te patust vabanenuina olete saanud õiguse orjadeks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 19, 'Ma räägin inimese kombel teie liha nõtruse pärast. Sest otsekui te oma liikmed andsite orjadeks rüvedusele ja ülekohtule, selleks et käsust üle astuda, nõnda andke nüüd oma liikmed õiguse orjadeks pühitsusele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 20, 'Sest kui te olite patu orjad, olite vabad õigusest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 21, 'Mis vilja te kandsite siis? Niisugust, millest teil nüüd on häbi! Sest selle lõpp on surm!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 22, 'Aga nüüd, et te olete saanud patust vabaks ja Jumala orjadeks, on teie vili pühitsuseks ja selle lõpp on igavene elu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 23, 'Sest patu palk on surm, aga Jumala armuand on igavene elu Kristuses Jeesuses, meie Issandas!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 1, 'Või eks te tea, vennad - mina räägin käsutundjaile - et käsk valitseb inimese üle, niikaua kui inimene elab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 2, 'Nii on abielunaine käsuga seotud elusoleva mehe külge; aga kui mees sureb, on ta lahti mehe käsu alt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 3, 'Sellepärast, kui naine mehe elus olles läheb teisele mehele, nimetatakse teda abielurikkujaks; aga kui mees sureb, on ta sellest käsust vaba, nii et ta ei ole abielurikkuja, kui ta läheb teisele mehele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 4, 'Nõnda teiegi, mu vennad, olete surmatud käsule Kristuse ihu läbi, et saaksite teisele mehele, kes on surnuist üles äratatud, et me kannaksime vilja Jumalale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 5, 'Sest kui me olime lihas, siis patuhimud, mis tõusid käsu läbi, tegutsesid meie liikmetes, et kanda vilja surmale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 6, 'Aga nüüd me oleme käsust lahti ning oleme surnud sellele, mis meid kinni pidas, nii et me teenime Jumalat vaimu uues olemises ja mitte kirjatähe vanas olemises.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 7, 'Mis me siis ütleme? Ons käsk patt? Mitte sugugi! Ent pattu ma ei oleks tundnud muidu kui käsu kaudu; sest ma ei oleks kurjast himust midagi teadnud, kui käsk ei oleks öelnud: \"Sa ei tohi himustada!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 8, 'Aga patt, saades tõuget käsust, äratas minus kõiksugu himusid; sest ilma käsuta on patt surnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 9, 'Mina elasin enne ilma käsuta; aga kui käsusõna tuli, virgus patt ellu');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 10, 'ja mina surin ära, ja nõnda leiti käsusõna, mis mulle pidi olema eluks, mulle olevat surmaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 11, 'Sest kui patt oli saanud käsusõnast tõuke, pettis ta mind ja surmas mind selle abil.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 12, 'Nõnda on siis käsk püha ja käsusõna püha ja õige ja hea.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 13, 'Kas siis nüüd see, mis on hea, on saanud mulle surmaks? Mitte sugugi! Vaid patt, et ta ilmneks patuna, on selle hea kaudu mulle toonud surma, et patt ise saaks üliväga patuseks käsusõna läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 14, 'Sest me teame, et käsk on vaimne, aga mina olen lihalik ja müüdud patu alla.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 15, 'Ma ei tunne ju ära, mida ma teen; sest ma ei tee seda, mida ma tahan, vaid mida ma vihkan, seda ma teen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 16, 'Kui ma aga teen, mida ma ei taha, siis ma möönan, et käsk on hea.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 17, 'Nii ei tee seda enam mitte mina, vaid patt, mis minus elab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 18, 'Sest ma tean, et minus, see on minu lihas, ei ela head. Tahet mul on, aga head teha ma ei suuda;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 19, 'sest head, mida ma tahan, ma ei tee, vaid kurja, mida ma ei taha, ma teen!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 20, 'Kui ma nüüd teen seda, mida ma ei taha, siis ei tee seda enam mitte mina, vaid patt, mis minus elab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 21, 'Nii ma leian eneses käsu, et kuigi ma tahan teha head, on mulle omane teha kurja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 22, 'Sest seespidise inimese poolest on mul Jumala käsust hea meel,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 23, 'aga oma liikmetes ma tunnen teist käsku, mis paneb vastu mu mõistuse käsule ja võtab mind vangi patu käsu alla, mis on mu liikmetes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 24, 'Oh mind viletsat inimest! Kes päästab mind sellest surma ihust?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 25, 'Tänu olgu Jumalale Jeesuse Kristuse, meie Issanda läbi! Nii ma siis teenin oma mõistusega Jumala käsku, aga lihaga patu käsku.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 1, 'Nii ei ole siis nüüd mingisugust hukkamõistmist neile, kes on Kristuses Jeesuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 2, 'Sest elu vaimu käsk Kristuses Jeesuses on sind vabastanud patu ja surma käsust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 3, 'Sest mis oli võimatu käsule, sellepärast et ta oli jõuetu liha tõttu, seda tegi Jumal, kui Ta läkitas Oma Poja patuse liha sarnasuses ja patu pärast ning mõistis patu hukka liha sees,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 4, 'et käsu õigus täide viidaks meie sees, kes me ei käi liha järgi, vaid Vaimu järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 5, 'Sest kes elavad liha järgi, nende meel on suunatud lihalikele asjadele; aga kes elavad Vaimu järgi, neil vaimsetele asjadele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 6, 'Sest liha mõtteviis, see on surm, aga Vaimu mõtteviis, see on elu ja rahu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 7, 'sellepärast et liha mõtteviis on vaen Jumala vastu, sest ta ei alistu Jumala käsule ega võigi alistuda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 8, 'Kes siis lihameeles on, need ei või olla Jumalale meelepärased.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 9, 'Aga teie ei ole lihameeles, vaid vaimus, kui Jumala Vaim tõepoolest asub teie sees. Aga kellel ei ole Kristuse Vaimu, see ei ole Tema Oma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 10, 'Kui aga Kristus on teie sees, siis on küll ihu surnud patu pärast; aga vaim on elu õiguse pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 11, 'Aga kui selle Vaim, kes Jeesuse on surnuist üles äratanud, teis elab, siis Tema, Kes Kristuse Jeesuse surnuist üles äratas, teeb ka teie surelikud ihud elavaks Oma Vaimu läbi, Kes teis elab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 12, 'Sellepärast nüüd, vennad, oleme küll võlglased, aga mitte lihale, et elada liha järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 13, 'Sest kui te liha järgi elate, siis te surete; aga kui te Vaimu läbi ihu teod suretate, siis te elate.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 14, 'Sest kõik, keda iganes Jumala Vaim juhib, on Jumala lapsed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 15, 'Sest te ei ole saanud orjapõlve vaimu, et peaksite jälle kartma, vaid te olete saanud lapsepõlve Vaimu, kelles me hüüame: \"Abba! Isa!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 16, 'Seesama Vaim tunnistab ühes meie vaimuga, et me oleme Jumala lapsed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 17, 'Kui me aga oleme lapsed, siis oleme ka pärijad, nii Jumala pärijad kui ka Kristuse kaaspärijad, et kui me ühes Temaga kannatame, siis meid ühes Temaga ka austatakse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 18, 'Sest ma arvan, et selle aja kannatused ei ole midagi tulevase au vastu, mis meile peab ilmsiks saama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 19, 'Sest kogu loodu ootab pikisilmi Jumala laste ilmsikssaamist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 20, 'On ju kogu loodu heidetud kaduvuse alla - mitte vabatahtlikult, vaid allaheitja tahtest - ometi lootuse peale,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 21, 'sest ka kogu loodu ise päästetakse kord kaduvuse orjusest Jumala laste au vabadusse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 22, 'Sest me teame, et kõik loodu ühtlasi ägab ja on aina sünnitusvaevas tänini;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 23, 'aga mitte üksnes seda, vaid isegi need, kellel on Vaimu esmaand, ka meie ise ägame enestes ning ootame lapseseisust, oma ihu lunastust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 24, 'Sest me oleme päästetud lootuses. Ent lootus, mida nähakse, ei ole mingi lootus; sest mida keegi näeb, kuidas ta seda veel loodab?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 25, 'Aga kui me loodame seda, mida me ei näe, siis me ootame seda kannatlikkusega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 26, 'Aga samuti tuleb ka Vaim appi meie nõtrusele; sest me ei tea seda, mida paluda, nõnda nagu peaks, ent Vaim Ise palvetab meie eest sõnades väljendamatute ohkamistega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 27, 'Aga südamete uurija teab, mis Vaimul on mõttes, et Ta kooskõlas Jumalaga kostab pühade eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 28, 'Aga me teame, et neile, kes Jumalat armastavad, kõik ühtlasi heaks tuleb, neile, kes Tema kavatsuse järgi on kutsutud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 29, 'Sest keda Ta on ette ära tundnud, need on Ta ka ette ära määranud olema Tema Poja näo sarnased, et Tema oleks esmasündinu paljude vendade seas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 30, 'Aga keda Ta on ette ära määranud, need on Ta ka kutsunud, ja keda Ta on kutsunud, need on Ta ka õigeks teinud, ent keda Ta on õigeks teinud, neid on Ta ka austanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 31, 'Mis me siis ütleme selle kohta? Kui Jumal on meie poolt, kes võib olla meie vastu?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 32, 'Tema, Kes Oma Poegagi ei säästnud, vaid loovutas Tema meie kõikide eest, kuidas Ta ei peaks siis Temaga meile kõike muud annetama?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 33, 'Kes võib süüdistada Jumala valituid? Jumal on, Kes õigeks teeb.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 34, 'Kes on, kes võib hukka mõista? Kristus Jeesus on, Kes suri, ja mis veel enam, Kes üles äratati, Kes on Jumala paremal käel, Kes meie eest palub.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 35, 'Kes võib meid lahutada Kristuse armastusest? Kas viletsus, või ahastus, või tagakiusamine, või nälg, või alastiolek, või häda, või mõõk?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 36, 'Nõnda nagu on kirjutatud: \"Sinu pärast surmatakse meid kogu päev, meid arvatakse tapalambaiks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 37, 'Aga selles kõiges me saame täie võidu Tema läbi, Kes meid on armastanud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 38, 'Sest ma olen veendunud selles, et ei surm ega elu, ei Inglid, ei vürstid, ei käesolev ega tulev, ei vägevad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 39, 'ei kõrgus ega sügavus ega mingi muu loodu või meid lahutada Jumala armastusest, mis on Kristuses Jeesuses, meie Issandas!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 1, 'Ma räägin tõtt Kristuses, mina ei valeta mitte; seda tunnistab mulle mu südametunnistus Pühas Vaimus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 2, 'et mul on suur kurbus ja lõpmatu valu mu südames.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 3, 'Sest ma sooviksin ise saada äraneetavaks, Kristusest lahku oma vendade heaks, kes on mu sugulased liha poolest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 4, 'kes on iisraellased, kellele kuuluvad lapseõigus ja au, ja lepingud ja käsuandmine, ja jumalateenistus ja tõotused,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 5, 'kelle omad on esiisad ja kellest Kristus on pärit liha poolest, Tema, Kes on Jumal üle kõige, kiidetud igavesti! Aamen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 6, 'Aga ei või olla nõnda, et Jumala sõna oleks läinud tühja; sest mitte kõik need, kes põlvnevad Iisraelist, ei ole Iisrael;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 7, 'ka ei ole kõik lapsed selle tõttu, et nad on Aabrahami sugu, vaid on öeldud: \"Iisakist loetakse sinu sugu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 8, 'See on: lihased lapsed, mitte need ei ole Jumala lapsed, vaid tõotuse lapsed arvatakse sooks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 9, 'Sest tõotuse sõna on see: \"Ma tulen jälle aasta pärast samal ajal, ja Saaral saab olema poeg!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 10, 'Aga mitte ainult temaga, vaid ka Rebekaga oli samane lugu, kui ta ühest, see on meie esiisast Iisakist, sai käima peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 11, 'Sest enne kui kaksikud olid sündinud ega olnud teinud midagi head ega halba, siis - selleks, et valikule vastav Jumala otsustus jääks kindlaks mitte tegude pärast, vaid kutsuja tõttu -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 12, 'öeldi temale: \"Vanem orjab nooremat!\",');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 13, 'nagu ju on kirjutatud: \"Jaakobit Ma armastasin, aga Eesavit Ma vihkasin!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 14, 'Mida me nüüd ütleme? Kas Jumal teeb ülekohut? Mitte sugugi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 15, 'Sest Ta ütleb Moosesele: \"Ma olen armuline, kellele Ma olen armuline, ja halastan, kelle peale Ma halastan!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 16, 'Nõnda siis ei olene see sellest, kes tahab, ega sellest, kes jookseb, vaid Jumalast, kes armu annab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 17, 'Sest Kiri ütleb vaaraole: \"Just selleks Ma olen sind õhutanud, et sinus näidata Oma väge ja teha kuulsaks Oma nime kogu maailmas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 18, 'Nõnda Ta siis annab armu, kellele tahab, ja paadutab, keda tahab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 19, 'Sa ehk ütled nüüd mulle: \"Miks Ta siis veel sõitleb? Kes võib vastu panna Tema tahtele?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 20, 'Oh inimene, nii see on! Kes siis sina oled, et sa tahad vaielda Jumala vastu? Kas see, mis tehtud, ütleb oma tegijale: \"Miks sa mu nõnda tegid?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 21, 'Või ei ole potissepal meelevalda savi ühest ja samast segust teha ühe astja õilsaks, teise halvaks otstarbeks?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 22, 'Eks Jumal, tahtes osutada Oma viha ja teha teatavaks Oma väge, suure pikameelega sallinud vihaastjaid, mis olid valmistatud hukatuseks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 23, 'ja teinud seda selleks, et ilmutada Oma au rikkust armuastjate vastu, mis Ta ette oli valmistanud aulisteks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 24, 'nõnda nagu Tema on kutsunud ka meid, mitte ainult juutide, vaid ka paganate seast?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 25, 'Nõnda nagu Ta ütleb ka Hoosea raamatus: \"Ma tahan hüüda Omaks rahvaks neid, kes ei olnud Mu rahvas, ja Oma armsaks seda, kes ei olnud Mulle armas;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 26, 'ja sünnib, et seal paigas, kus neile öeldi: teie ei ole Minu rahvas! hüütakse neid elava Jumala lasteks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 27, 'Aga Jesaja hüüab Iisraeli kohta: \"Kuigi Iisraeli laste arv oleks otsekui mereliiv, pääseb sellest ainult jääk;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 28, 'sest lõplikult ja kiiresti teostab Issand Oma sõna maa peal!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 29, 'Ja nõnda nagu Jesaja on ette öelnud: \"Kui mitte vägede Issand ei oleks meile jätnud seemet, oleksime olnud nagu Soodom ja saanud Gomorra sarnaseks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 30, 'Mis me siis nüüd ütleme? Seda, et paganad, kes õigust ei taotlenud, on kätte saanud õiguse, ent õiguse, mis tuleb usust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 31, 'Aga Iisrael, kes taotles õiguse käsku, ei ole käsku kätte saanud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 32, 'Mispärast? Sellepärast et taotlus ei olnud usust, vaid otsekui tegudest; nad tõukasid endid vastu komistuskivi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 33, 'nõnda nagu on kirjutatud: \"Vaata, Ma panen Siionisse komistuskivi ja pahanduskalju; ja kes usub Temasse, ei jää häbisse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 1, 'Vennad, minu südame hea meel ja palve Jumala poole nende eest on, et nad õndsaks saaksid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 2, 'Sest ma annan neile tunnistuse, et nad on väga agarad Jumala suhtes, kuid mitte õiget tunnetust mööda;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 3, 'sest kui nad ei mõista Jumala õigust ja püüavad üles seada oma õigust, siis ei ole nad alistunud Jumala õigusele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 4, 'Sest käsu lõppsiht on Kristus õiguseks igaühele, kes usub.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 5, 'Sest Mooses kirjutab õigusest, mis tuleb käsust, et inimene, kes seda täidab, elab sellest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 6, 'Aga õigus, mis tuleb usust, ütleb nõnda: \"Ära ütle oma südames: kes läheb üles taevasse? See on: Kristust alla tooma;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 7, 'või: kes läheb alla sügavusse? See on: Kristust surnuist üles tooma!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 8, 'Vaid mis ta ütleb? \"Sõna on sinule ligidal, sinu suus ja sinu südames!\" See on usu sõna, mida me kuulutame!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 9, 'Sest kui sa oma suuga tunnistad, et Jeesus on Issand, ja oma südames usud, et Jumal on Tema surnuist üles äratanud, siis sa saad õndsaks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 10, 'Sest südamega usutakse õiguseks, ent suuga tunnistatakse õndsuseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 11, 'Sest Kiri ütleb: \"Ükski, kes Temasse usub, ei jää häbisse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 12, 'Siin ei ole vahet juudi ja kreeklase vahel, sest üks ja sama on kõikide Issand, rikas kõikide heaks, kes Teda appi hüüavad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 13, 'Sest igaüks, kes hüüab appi Issanda nime, päästetakse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 14, 'Kuidas nad nüüd saavad appi hüüda Teda, Kellesse nad ei ole uskunud? Ja kuidas nad võivad uskuda Temasse, kellest nad ei ole kuulnud? Ja kuidas nad saavad kuulda ilma kuulutajata?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 15, 'Ja kuidas nad võivad kuulutada, kui neid ei läkitata? Nõnda nagu on kirjutatud: \"Kui armsad on nende sammud, kes häid sõnumeid kuulutavad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 16, 'Aga mitte kõik ei ole võtnud kuulda Evangeeliumi sõna. Sest Jesaja ütleb: \"Issand, kes usub meie kuulutust?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 17, 'Nii tuleb siis usk kuuldust, aga kuuldu Kristuse sõna kaudu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 18, 'Aga ma küsin: kas nad siis ei ole kuulnud? On küll! \"Üle kogu ilmamaa käib nende hääl ja maailma otsteni nende sõna!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 19, 'Aga ma küsin: kas Iisrael ei ole teada saanud? Esimesena ütleb Mooses: \"Mina ärritan teid rahvaga, kes ei ole rahvas, ja vihastan teid mõistmatu rahvaga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 20, 'Ent Jesaja räägib julgesti ja ütleb: \"Mind on leidnud need, kes mind ei ole otsinud; ma olen saanud ilmsiks neile, kes mind ei ole nõudnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 21, 'Aga Iisraeli kohta ta ütleb: \"Kogu päeva ma sirutan käsi rahva poole, kes ei kuula sõna ja räägib vastu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 1, 'Siis ma nüüd ütlen: kas vahest Jumal on Oma rahva hüljanud? Mitte sugugi! Sest minagi olen Iisraeli mees, Aabrahami soost, Benjamini suguharust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 2, 'Jumal ei ole hüljanud Oma rahvast, kelle Tema on ette ära tundnud. Või kas te ei tea, mis Kiri ütleb Eelijast, kuidas tema Jumala ees kaebab Iisraeli pärast, öeldes:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 3, '\"Issand, Sinu prohvetid on nad tapnud ja Sinu altarid maha kiskunud, ja mina olen üksi üle jäänud, ja nemad tahavad võtta mu elu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 4, 'Aga mis ütleb temale Jumala vastus? \"Mina olen Enesele üle jätnud seitse tuhat meest, kes ei ole nõtkutanud põlvi Baali ees!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 5, 'Samuti on ka siis praegusel ajal jääk üle jäänud armuvalikut mööda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 6, 'Aga kui see on armust, siis ei ole see enam tegudest; muidu arm ei oleks arm. Aga kui see on tegudest, siis ei ole see enam arm; muidu tegu ei oleks enam tegu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 7, 'Mis nüüd? Mida Iisrael taotleb, seda ta ei ole saavutanud; ent äravalitud on selle saavutanud ja teised on paadunud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 8, 'nõnda nagu on kirjutatud: \"Jumal on neile andnud uimuse vaimu, silmad mitte nägema ja kõrvad mitte kuulma tänapäevani!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 9, 'Ja Taavet ütleb: \"Nende laud saagu neile püüdepaelaks ja lõksuks ja pahanduseks ja kättemaksuks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 10, 'Nende silmad saagu pimedaks, et nad ei näeks, ja murra nende selg kõveraks jäädavalt!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 11, 'Ma ütlen nüüd: ega nad ole komistanud selleks, et langeda? Ei sugugi mitte! Vaid nende eksimise läbi tuleb pääste paganaile, et paganad teeksid neid kadedaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 12, 'Aga kui nende eksimine on maailma rikkus ja nende kahju paganate rikkus, saati siis nende täisarv!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 13, 'Mina aga kõnelen teile, paganaile! Sest sedavõrd kui ma olen paganate Apostel, austan ma oma ametit,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 14, 'et võiksin kuidagi õhutada neid, kes on mu liha ja veri, ja mõned nende seast päästa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 15, 'Sest kui juba nende hülgamine on maailma lepitus, mis on siis nende vastuvõtt muud kui surnuist ellusaamine?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 16, 'Aga kui uudsevili on püha, siis on ka taigen püha; ja kui juur on püha, siis on ka oksad pühad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 17, 'Kui nüüd okstest mõned on ära murtud ja sina, kes olid metsõlipuu, oled jätkatud nende asemele ja oled ühes nendega osa saanud õlipuu mahlakast juurest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 18, 'siis ära kiitle okste vastu. Ja kui sa kiitled, siis mõtle, et sina ei kanna juurt, vaid juur kannab sind.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 19, 'Sina ehk ütled nüüd: \"Oksad on ära murtud, et mind külge jätkataks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 20, 'Õige küll! Uskmatuse pärast on nad ära murtud; aga sina püsid usu läbi. Ära saa omas meeles suureliseks, vaid karda!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 21, 'Sest kui Jumal ei säästnud loodud oksi, ega Ta sindki säästa!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 22, 'Sellepärast vaata Jumala heldust ja valjust; valjust küll nende vastu, kes on langenud; aga heldust sinu vastu, kui sa püsid helduses, muidu sindki raiutakse maha!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 23, 'Aga nemadki, kui nad ei jää uskmatusse, jätkatakse puu külge; sest Jumal võib nad jälle külge jätkata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 24, 'Sest kui sina loomulikust metsõlipuust oled ära lõigatud ja loomu vastu oled jätkatud väärisõlipuu külge, saati siis, et nemad kui loodud oksad, jätkatakse iseeneste õlipuu külge.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 25, 'Sest mina ei taha, vennad, et teile jääks teadmatuks see saladus - et teie ei oleks eneste meelest targad - et Iisraelile osalt on tulnud paadumus, kuni paganate täisarv on läinud sisse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 26, 'ja kogu Iisrael päästetakse, nõnda nagu on kirjutatud: \"Siionist tuleb Päästja ja kõrvaldab Jaakobist jumalatuse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 27, 'Ja see on Minu leping nendega, kui Ma ära võtan nende patud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 28, 'Evangeeliumi poolest on nad küll vaenlased teie pärast, aga valiku poolest on nad armastatud esiisade pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 29, 'Sest Jumal ei võta tagasi Oma armuande ja kutsumist!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 30, 'Sest otsekui teie muiste olite Jumalale sõnakuulmatud, aga nüüd olete armu saanud nende sõnakuulmatuse läbi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 31, 'nõnda on nüüd ka nemad sõnakuulmatud, et ka nemad nüüd teile osaks saanud armu kaudu saaksid armu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 32, 'Sest Jumal on kõik inimesed kinni pannud sõnakuulmatuse alla, et Ta armu annaks kõikidele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 33, 'Oh seda Jumala rikkuse ja tarkuse ja tunnetuse sügavust! Kui väljauurimatud on Tema kohtumõistmised ja äraarvamatud Tema teed!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 34, 'Sest kes on ära tundnud Issanda meele? Või kes on olnud Tema nõuandja?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 35, 'Või kes on Temale midagi enne andnud, et Temale peaks jälle tasutama?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 36, 'Sest Tema seest ja Tema läbi ja Tema poole on kõik asjad. Temale olgu au igavesti! Aamen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 1, 'Mina manitsen teid nüüd, vennad, Jumala suure südamliku halastuse tõttu andma oma ihud elavaks pühaks ja Jumala meelepäraseks ohvriks. See olgu teie mõistlik jumalateenistus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 2, 'Ja ärge saage selle maailma sarnaseks, vaid muutuge teiseks oma meele uuendamise teel, et te uuriksite, mis on Jumala hea ja meelepärane ja täielik tahtmine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 3, 'Sest selle armu läbi, mis mulle on antud, ma ütlen teile igaühele, et ta ei mõtleks üleolevalt selle kohta, mida tuleb mõelda, vaid mõtleks nõnda, et saaks arukaks sedamööda, kuidas Jumal igaühele usu mõõdu on jaganud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 4, 'Sest otsekui meil ühes ihus on palju liikmeid, aga kõigil liikmeil ei ole sama töö,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 5, 'nõnda oleme meiegi paljud üks ihu Kristuses, aga üksikult igaüks üksteise liikmed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 6, 'Aga meil on armu mööda, mis meile antud, mõnesuguseid armuandeid: olgu prohvetliku kuulutamise anne, mis toimugu usu mõõtu mööda;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 7, 'olgu hoolekanne, mis toimugu hoolekandeametis; olgu keegi õpetaja, siis toimigu ta õpetamisametis;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 8, 'manitseb keegi, siis ta olgu manitsemisametis; kes teistele annetab, andku siira südamega; kes teisi juhatab, olgu hoolas; kes vaeseid hooldab, tehku seda rõõmuga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 9, 'Armastus ärgu olgu silmakirjalik. Hoiduge kurjast eemale, kiinduge heasse!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 10, 'Olge vennaarmastuse poolest hellasüdamelised üksteise vastu; vastastikuses austamises jõudke üksteisest ette!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 11, 'Ärge olge viitsimatud hoolsuses; olge tulised vaimus; teenige Issandat!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 12, 'Olge rõõmsad lootuses, kannatlikud ahastuses, püsivad palves!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 13, 'Võtke osa pühade puudusest; püüdke olla külalislahked.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 14, 'Õnnistage neid, kes teid taga kiusavad! Õnnistage ja ärge needke!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 15, 'Olge rõõmsad rõõmsatega, nutke nutjatega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 16, 'Mõtelge ühesuguselt üksteise suhtes! Ärge nõudke kõrgeid asju, vaid hoiduge madaluse poole; ärge olge eneste meelest targad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 17, 'Ärge tasuge ühelegi kurja kurjaga; mõtelge ikka sellele, mis hea on kõigi inimeste suhtes!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 18, 'Kui võimalik on ja niipalju kui teist oleneb, pidage rahu kõigi inimestega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 19, 'Ärge makske ise kätte, armsad, vaid andke maad Jumala vihale, sest on kirjutatud: \"Minu käes on kättemaks, Mina tasun kätte! ütleb Issand.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 20, '\"Kui nüüd sinu vaenlasel on nälg, sööda teda; kui tal on janu, jooda teda; sest seda tehes sa kogud tuliseid süsi tema pea peale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 21, 'Ära lase kurjal võitu saada enese üle, vaid võida sina kuri ära heaga!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 1, 'Iga hing olgu allaheitlik valitsemas olevaile ülemustele; sest ülemust ei ole muud kui Jumalalt; kus neid on, seal on nad Jumala poolt seatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 2, 'Kes nüüd ülemusele vastu paneb, see paneb vastu Jumala korrale; aga kes vastu panevad, saadavad eneste peale nuhtluse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 3, 'Sest valitsejad ei ole hirmuks headele tegudele, vaid kurjadele. Kui sina aga ei taha tunda hirmu ülemuse ees, siis tee head, ja siis sa saad temalt kiitust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 4, 'Sest tema on Jumala teener sinu heaks; aga kui sa kurja teed, karda, sest ta ei kanna mõõka asjata; ta on Jumala teenija, kättemaksja nuhtluseks sellele, kes kurja teeb.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 5, 'Siis on tarvis olla allaheitlik, mitte ainult nuhtluse, vaid ka südametunnistuse pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 6, 'Sellepärast makske ka makse; sest nemad on Jumala sulased, kes selleks peavad oma ametit.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 7, 'Tasuge kõigile, mis teie kohus on: maksu, kellele maksu; tolliraha, kellele tolliraha; kartust, kellele kartust; au, kellele au tuleb anda!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 8, 'Ärgu olgu teil midagi kellegagi võlgu, kui et te üksteist armastate, sest kes teist armastab, on käsu täitnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 9, 'Sest käsk: \"Sa ei tohi abielu rikkuda; sa ei tohi tappa; sa ei tohi varastada; sa ei tohi ülekohut tunnistada; sa ei tohi himustada\", ja iga teine käsk on kokku võetud sellesse sõnasse: \"Armasta oma ligimest nagu iseennast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 10, 'Armastus ei tee ligimesele kurja; siis on armastus käsu täitmine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 11, 'Ja seda tehke sellepärast et te teate tunni juba käes olevat unest üles ärgata; sest nüüd on meie pääste lähemal kui siis, kui me usklikuks saime.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 12, 'Öö on möödumas, aga päev on lähedal. Siis heitkem enestest ära pimeduse teod ja varustugem valguse relvadega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 13, 'Elagem ausasti nagu päeva ajal, mitte öistes pidutsemistes ega joominguis, mitte kiimaluses ega iharuses, mitte riius ega kadeduses,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 14, 'vaid varustuge Issanda Jeesuse Kristusega ja ärge muutke liha eest hoolitsemist himude ärritamiseks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 1, 'Usu poolest nõrgale olge vastutulelikud ilma vaidlust alustamata arvamiste kohta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 2, 'Mõni usub, et ta võib süüa kõike, aga kes on nõrk, sööb taimetoitu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 3, 'Kes sööb, ärgu pidagu halvaks seda, kes mitte ei söö; ja kes ei söö, ärgu mõistku hukka seda, kes sööb; sest Jumal on tema vastu võtnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 4, 'Kes oled sina, et sa mõistad hukka teise sulase? Tema seisab või langeb iseenese isandale. Aga tema jääb seisma, sest Jumal võib teda püsti hoida.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 5, 'Mõni arvab üht päeva paremaks teisest, aga teine peab kõik päevad ühesuguseks. Igaüks olgu enese mõttes kindel omas veendumuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 6, 'Kes paneb tähele päeva, paneb seda tähele Issandale; ja kes sööb, sööb Issandale, sest tema tänab Jumalat; ja kes mitte ei söö, jääb söömata Issandale ning tänab Jumalat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 7, 'Sest keegi meist ei ela iseenesele ja keegi meist ei sure iseenesele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 8, 'Sest kui me elame, siis elame Issandale, ja kui me sureme, siis sureme Issandale. Kas me siis elame või sureme, oleme Issanda Omad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 9, 'Sest Kristus suri selleks ja sai jälle elavaks, et Ta oleks nii surnute kui ka elavate Issand.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 10, 'Aga sina, miks sa mõistad kohut oma venna üle? Või sina teine, miks sa paned oma venda halvaks? Meid kõiki ju seatakse Jumala kohtujärje ette.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 11, 'Sest on kirjutatud: \"Nii tõesti kui Ma elan, ütleb Issand, Minu ees peab nõtkuma iga põlv ja iga keel peab tunnistama Jumalat!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 12, 'Nõnda tuleb meil igaühel anda aru iseenesest Jumalale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 13, 'Ärgem siis enam mõistkem kohut üksteise üle, vaid pigemini otsustagem hoiduda saamast vennale komistuseks ja pahanduseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 14, 'Ma tean ja olen veendunud Issandas Jeesuses, et midagi ei ole ebapüha iseenesest, vaid on ebapüha üksnes sellele, kes midagi peab ebapühaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 15, 'Aga kui sinu vend saab nukraks roa pärast, siis ei käi sina enam armastuse järgi. Ära saada hukka oma roaga seda, kelle eest Kristus on surnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 16, 'Sellepärast ärgu saagu hea, mis teil on, pilgatavaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 17, 'Sest Jumala riik ei ole mitte söömine ega joomine, vaid õigus ja rahu ja rõõm Pühas Vaimus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 18, 'Sest kes neis asjus orjab Kristust, see on Jumalale meelepärane ja kõlvuline inimestele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 19, 'Siis taotlegem nüüd seda, mis läheb tarvis rahuks ja vastastikuseks paranduseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 20, 'Ära riku roa pärast Jumala teost! Kõik on küll puhas, aga paha on see sellele inimesele, kes sööb pahandusega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 21, 'Hea on liha mitte süüa ja viina mitte juua, ja mitte teha seda, mis su vennale on komistuseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 22, 'Usk, mis sul on, pea enesele Jumala ees. Õnnis on see, kes iseennast ei mõista hukka selles, mida ta peab õigeks;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 23, 'aga kui keegi kõheldes sööb, siis on ta hukka mõistetud, sest see ei tule usust. Aga kõik, mis ei ole usust, on patt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 1, 'Aga meie, kes oleme tugevad, peame kandma jõuetute nõrkusi ega tohi elada iseeneste meele heaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 2, 'Igaüks meist olgu ligimesele meelepärane tema heaks, et teda parandada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 3, 'Sest Kristuski ei elanud Enese meele heaks, vaid nõnda nagu on kirjutatud: \"Nende teotamised, kes Sind teotavad, on langenud Minu peale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 4, 'Sest mis iganes enne on kirjutatud, on kirjutatud meile õpetuseks, et meil kannatlikkuse ja Kirja troosti kaudu oleks lootust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 5, 'Aga kannatlikkuse ja troosti Jumal andku teile üksmeelt isekeskis Kristuse Jeesuse järele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 6, 'et te ühel meelel ja ühe suuga austaksite Jumalat ja meie Issanda Jeesuse Kristuse Isa!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 7, 'Sellepärast võtke vastu üksteist, nõnda nagu ka Kristus teid on vastu võtnud Jumala austuseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 8, 'Sest ma ütlen, et Jeesus Kristus sai ümberlõikamise sulaseks Jumala tõe pärast, et kinnitada isadele antud tõotused,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 9, 'aga et paganad ülistavad Jumalat Tema halastuse pärast, nõnda nagu on kirjutatud: \"Sellepärast ma tahan Sind ülistada paganate seas ja laulda kiitust Su nimele!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 10, 'Ja taas ütleb tema: \"Olge rõõmsad, paganad, ühes Tema rahvaga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 11, 'Ja veel: \"Kiitke Issandat, kõik paganad, ja ülistage Teda, kõik rahvad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 12, 'Ja jälle ütleb Jesaja: \"Jesse juur tuleb ja see, kes tõuseb valitsema paganrahvaid; Tema peale hakkavad paganad lootma!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 13, 'Aga lootuse Jumal täitku teid kõige rõõmu ja rahuga usus, et teil oleks rohkesti lootust Püha Vaimu väes!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 14, 'Mu vennad, mina isiklikult olen teie suhtes veendunud selles, et teie isegi olete täis headust, täidetud kõige teadmisega ning võite küll juhatada üksteist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 15, 'Ometi olen ma üsna julgesti mõnd teile kirjutanud, vennad, otsekui teile meeldetuletuseks, selle armu pärast, mille Jumal mulle on andnud selleks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 16, 'et ma oleksin Jeesuse Kristuse teenija paganate seas ja toimetaksin Jumala Evangeeliumi püha teenistust, et paganad saaksid meelepäraseks ja Pühas Vaimus pühitsetud ohvriannetuseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 17, 'Sellepärast ma tohin Kristuses Jeesuses kiidelda Jumala asjust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 18, 'Sest mina ei julge kõnelda millestki muust kui sellest, mis Kristus minu läbi paganate sõnakuulelikuks tegemiseks on korda saatnud sõna ja teoga');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 19, 'tunnustähtede ja imetegude väel, Püha Vaimu väel, nõnda et mina Jeruusalemmast ja selle ümberkaudsest maast alates Illürikonini olen täide viinud Kristuse Evangeeliumi kuulutamise;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 20, 'kuna ma nõnda olen pidanud oma auks armuõpetust mitte kuulutada seal, kus Kristust juba on nimetatud, et ma ei ehitaks teiste rajatud alusele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 21, 'vaid nõnda nagu on kirjutatud: \"Need, kellele ei ole Temast midagi kuulutatud, saavad näha, ja need, kes ei ole kuulnud, mõistavad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 22, 'See ongi see, mis mind mitu korda on takistanud teie juurde tulemast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 23, 'Aga et mul nüüd ei ole neis paigus enam aset ja et ma nii mitu aastat väga olen igatsenud teie juurde tulla,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 24, 'siis ma ehk Hispaaniasse minnes tulen teie juurde, sest ma loodan teie kaudu matkates näha teid ja teie saatel jõuda sinna, niipea kui ma enne teie keskel ennast olen pisut rõõmustanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 25, 'Aga nüüd ma lähen Jeruusalemma püha rahvast abistama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 26, 'Sest Makedoonia ja Ahhaia on heaks arvanud toime panna korjanduse neile, kes Jeruusalemmas elavate seas kehvad on.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 27, 'Nemad on seda heaks arvanud ja nad on ka nende võlglased. Sest kui paganad on osa saanud nende vaimulikest andidest, siis on ka nende kohus neid aidata ihulike andidega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 28, 'Kui ma siis selle asja olen toimetanud ja neile selle saavutuse ustavalt kätte saatnud, tahan ma teie kaudu minna Hispaaniasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 29, 'Aga ma tean, kui ma teie juurde tulen, et ma siis tulen Kristuse täie õnnistusega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 30, 'Ent mina manitsen teid, vennad, meie Issanda Jeesuse Kristuse läbi ja Vaimu armastuse läbi, minuga ühes võidelda eestpalvetega minu eest Jumala poole,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 31, 'et ma pääseksin uskmatu rahva käest Juudamaal ja et minu abistus Jeruusalemma heaks oleks armas pühade meelest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 32, 'et ma Jumala tahtel võiksin rõõmuga tulla teie juurde ja teie keskel leida kosutust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 33, 'Aga rahu Jumal olgu teie kõikidega! Aamen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 1, 'Mina annan teie hooleks meie õe Foibe, kes on Kenkrea koguduse käsiline,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 2, 'et te võtaksite teda vastu Issandas, nõnda nagu sobib pühadele, ja oleksite temale abiks kõiges, milles temal iganes teid on vaja. Sest tema on abiks olnud paljudele, ka mulle enesele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 3, 'Tervitage minu kaastöölisi Kristuses Jeesuses, Priskat ja Akvilat,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 4, 'kes minu elu eest on pannud oma kaela tapavalmis, keda ei täna mitte mina üksi, vaid ka kõik paganate kogudused,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 5, 'ja kogudust nende majas. Tervitage Epainetost, minu armast, kes oli esimene Aasias pöörduma Kristuse poole.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 6, 'Tervitage Maarjat, kes on palju vaeva näinud teie eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 7, 'Tervitage Andronikost ja Juuniust, minu sugulasi ja kaasvange, kes on kuulsad Apostlite hulgas ja kes enne mind on olnud Kristuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 8, 'Tervitage Ampliatost, mu armast Issandas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 9, 'Tervitage Urbanust, meie kaastöölist Kristuses, ja mu armast Stahhüst.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 10, 'Tervitage Apellest, kes on läbi katsutud Kristuses. Tervitage Aristobulose perekonda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 11, 'Tervitage Heroodioni, minu sugulast. Tervitage Narkissose perekonna liikmeid, kes on Issandas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 12, 'Tervitage Trüfainat ja Trüfoosat, kes teevad tööd Issandas. Tervitage Persist, armast õde, kes on näinud palju vaeva Issandas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 13, 'Tervitage Ruufust, Issandas äravalitut, ja tema ema, kes on ka minu ema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 14, 'Tervitage Asünkritost, Flegonit, Hermest, Patrobast, Hermast ja vendi, kes on nende juures.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 15, 'Tervitage Filologost ja Juuliat, Neereust ja tema õde ja Olümpat ja kõiki pühasid, kes on nende juures.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 16, 'Tervitage üksteist püha suudlusega. Kõik Kristuse kogudused tervitavad teid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 17, 'Aga mina manitsen teid, vennad, pidama silmas neid, kes tekitavad lõhesid ja pahandust õpetuse vastu, mis te olete õppinud, ja tõmbuge neist tagasi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 18, 'Sest niisugused inimesed ei teeni mitte meie Issandat Jeesust, vaid oma kõhtu, ja petavad magusate sõnadega ja libedate kõnedega tõemeelsete südameid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 19, 'Sest teie sõnakuulmine on saanud kõigile teatavaks; seepärast ma rõõmustun teist, kuid tahan, et teie oleksite targad heale, aga kohtlased pahale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 20, 'Aga rahu Jumal tallab varsti saatana teie jalge alla. Meie Issanda Jeesuse Kristuse arm olgu teiega! Aamen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 21, 'Teid tervitavad Timoteos, minu kaastööline, ja Luukius ja Jaason ja Sosipatros, minu sugulased.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 22, 'Mina, Tertius, kes ma selle kirja olen kirjutanud, tervitan teid Issandas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 23, 'Teid tervitab Gaajus, kes on olnud külalislahke minule ja kõigele kogudusele. Teid tervitab Erastos, linna laekur, ja vend Kvartus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 24, 'Meie Issanda Jeesuse Kristuse arm olgu teie kõikidega! Aamen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 25, 'Ent sellele, kes teid võib kinnitada minu Evangeeliumi ja Jeesuse Kristuse jutluse järgi selle saladuse ilmutust mööda, mis on olnud igavesest ajast varjul,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 26, 'aga nüüd on saanud avalikuks ja on prohvetite kirjade kaudu igavese Jumala käsul tehtud teatavaks kõigile rahvastele usu sõnakuulelikkuse saavutamiseks -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 27, 'ainsale targale Jumalale, Jeesuse Kristuse läbi, Temale olgu au ajastute ajastuteni! Aamen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 1, 'Paulus, Jumala tahtel kutsutud Kristuse Jeesuse Apostel, ja vend Soostenes');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 2, 'Korintoses olevale Jumala kogudusele, pühitsetuile Kristuses Jeesuses, kutsutud pühadele, ühes nende kõikidega, kes appi hüüavad meie Issanda Jeesuse Kristuse nime kõigis nende ja meie paigus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 3, 'Armu teile ja rahu Jumalalt, meie Isalt ja Issandalt Jeesuselt Kristuselt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 4, 'Ma tänan alati oma Jumalat teie pärast selle Jumala armu eest, mis teile on antud Kristuses Jeesuses,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 5, 'et te olete kõiges saanud rikkaks Tema sees igasuguses õpetuse sõnas ja igasuguses tunnetuses,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 6, 'sedamööda nagu tunnistus Kristusest on kinnitatud teie sees,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 7, 'nõnda et teil ei ole puudu ühestki armuandest ja ootate meie Issanda Kristuse ilmumist,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 8, 'kes ka teid tahab kinnitada otsani, et te võiksite olla laitmatud meie Issanda Jeesuse Kristuse päeval.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 9, 'Jumal on ustav, kelle läbi te olete kutsutud Tema Poja Jeesuse Kristuse, meie Issanda osadusse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 10, 'Aga ma manitsen teid, vennad, meie Issanda Jeesuse Kristuse nime läbi, et te kõik ühtviisi räägiksite ja et ei oleks lõhesid teie seas, vaid et oleksite kokku liidetud samas meeles ja samas mõttes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 11, 'Sest ma olen Kloe pererahvalt teist, mu vennad, saanud kuulda, et teie seas on riidlemisi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 12, 'Ma räägin sellest, et teie seast igaüks räägib omaviisi; üks: \"Mina olen Pauluse poolt!\" teine: \"Mina olen Apollose poolt!\" kolmas: \"Mina olen Keefase poolt!\" ja neljas: \"Mina olen Kristuse poolt!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 13, 'Kas Kristus on jaotatud mitmeks? Kas Paulus löödi risti teie eest? Või ristiti teid Pauluse nimesse?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 14, 'Ma tänan Jumalat, et ma kedagi muud teie seast ei ole ristinud kui üksnes Krispose ja Gaajuse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 15, 'et keegi ei saaks öelda, ma olevat ristinud iseenese nimesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 16, 'Siiski ma ristisin ka Stefanase pere; muud kedagi ma ei tea ristinud olevat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 17, 'Sest Kristus ei läkitanud mind ristima, vaid armuõpetust kuulutama, mitte sõna tarkuses, et Kristuse risti ei tehtaks tühiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 18, 'Sest sõna ristist on jõledus neile, kes hukka lähevad, aga meile, kes päästetakse, on see Jumala vägi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 19, 'Sest on kirjutatud: \"Mina kaotan tarkade tarkuse ja teen tühjaks mõistlike mõistuse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 20, 'Kus on targad? Kus kirjatundjad? Kus selle ajastu arutlejad? Eks Jumal ole selle maailma tarkuse teinud jõleduseks?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 21, 'Sest maailm Jumala tarkuses ei õppinud tundma Jumalat tarkuse kaudu ja seepärast oli Jumalale meelepärast jõleda jutluse kaudu päästa need, kes usuvad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 22, 'Sest juudid nõuavad tunnustähti ja kreeklased otsivad tarkust,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 23, 'aga meie kuulutame ristilöödud Kristust, Kes on juutidele pahandus ja paganaile jõledus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 24, 'kuid neile nende seast, kes on kutsutud, niihästi juutidele kui kreeklastele, me kuulutame Kristust, Jumala väge ja Jumala tarkust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 25, 'Sest mis jõle on Jumala ees, on targem kui inimesed, ja mis nõder on Jumala ees, on vägevam kui inimesed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 26, 'Vaadake ometi, vennad, oma kutsumist, et mitte palju tarku liha poolest, mitte palju vägevaid, mitte palju suursuguseid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 27, 'vaid mis on jõle maailma ees, selle on Jumal valinud, et tema teeks häbi tarkadele; ja mis on nõder maailma ees, selle on Jumal valinud, et tema teeks häbi sellele, mis on vägev;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 28, 'ja mis maailma ees on halvast soost ja põlatud, selle on Jumal valinud, selle, mis midagi ei ole, et teha tühjaks seda, mis midagi on,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 29, 'et ükski liha ei kiitleks Jumala ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 30, 'Ent temast on teie olemine Kristuses Jeesuses, Kes on saanud meile tarkuseks Jumalalt ja õiguseks ja pühitsuseks ja lunastuseks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 31, 'et oleks nõnda, nagu on kirjutatud: \"Kes kiitleb, see kiidelgu Issandast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 1, 'Ka mina, kui ma tulin teie juurde, vennad, ei tulnud mitte kõne või tarkuse hiilgega teile kuulutama Jumala tunnistust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 2, 'Sest ma ei arvanud teie seas mitte midagi muud teadvat kui vaid Jeesust Kristust, ja teda kui ristilöödut.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 3, 'Ja mina olin teie juures nõtruses ja kartuses ja suures väristuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 4, 'Ja minu kõne ja minu jutlus ei olnud inimliku tarkuse meelitavais sõnus, vaid Vaimu ja väe osutamises,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 5, 'et teie usk ei oleks inimeste tarkuses, vaid Jumala väes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 6, 'Ent me kuulutame tarkust täiuslike seas, aga mitte selle maailma ega selle ajastu ülemate tarkust, kes hävivad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 7, 'vaid me kuulutame Jumala salajas olevat ja varjatud tarkust, mis Jumal on ette määranud enne maailma-ajastute algust meie auks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 8, 'mida ükski selle maailma ülemaist ei ole tundnud; sest kui nad seda oleksid tundnud, ei oleks nad au Issandat risti löönud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 9, 'vaid nõnda nagu on kirjutatud: \"Mida silm ei ole näinud ja kõrv ei ole kuulnud ja mis inimese südamesse ei ole tõusnud, mis Jumal on valmistanud neile, kes Teda armastavad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 10, 'Ent Jumal on selle meile ilmutanud Vaimu läbi; sest Vaim uurib kõik asjad, ka Jumala sügavused.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 11, 'Sest kes inimesist teab seda, mis on inimeses, kui aga inimese vaim, kes temas on? Samuti ei tea ka ükski, mis on Jumalas, kui aga Jumala Vaim.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 12, 'Aga meie ei ole saanud maailma vaimu, vaid Vaimu, kes on Jumalast, et teaksime, mis Jumal meile armust on andnud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 13, 'ja seda me ka räägime mitte sõnadega, mida õpetab inimlik tarkus, vaid mida õpetab Vaim, ja seletame vaimseid asju vaimsetega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 14, 'Aga maine inimene ei võta seda vastu, mis on Jumala Vaimust; sest see on temale jõledus ja ta ei või sellest aru saada, sellepärast et seda tuleb ära mõista vaimselt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 15, 'Aga vaimulik inimene mõistab kõik ära, ent teda ennast ei mõista ükski ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 16, 'Sest kes on Issanda meele ära tundnud, et temale nõu anda? Aga meil on Kristuse meel!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 1, 'Ka mina, vennad, ei võinud teile rääkida kui vaimulikele, vaid kui lihalikele, kui väetitele lastele Kristuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 2, 'Ma jootsin teile piima ega andnud tahket rooga; sest seda te ei talunud sellal ega talu veel praegugi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 3, 'Te olete ju alles lihalikud. Sest kui teie seas on kadedust ja riidu, eks te siis ole lihalikud ja eks te käitu inimeste taoliselt?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 4, 'Sest kui üks ütleb: \"Mina olen Pauluse poolt!\" ja teine: \"Mina olen Apollose poolt!\" eks te siis ole nagu inimesed kunagi?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 5, 'Mis on siis Apollos? Või mis on Paulus? Mis muud kui käsilised, kelle kaudu te olete saanud usklikuks, ja sedamööda, kuidas Issand kummalegi on andnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 6, 'Mina istutasin, Apollos kastis, aga Jumal laskis kasvada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 7, 'Nõnda ei ole siis midagi see, kes istutab, ega see, kes kastab, vaid Jumal, Kes laseb kasvada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 8, 'Aga istutaja ja kastja on ühesugused; kuid kumbki saab oma palga oma töö järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 9, 'Sest me oleme Jumala kaastöölised, teie olete Jumala põllumaa ja Jumala ehitus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 10, 'Jumala armu järgi, mis minule on antud, olen ma kui tark hooneehitaja pannud aluse, ja teine ehitab selle peale; aga igaüks vaadaku, kuidas tema selle peale ehitab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 11, 'Sest teist alust ei või keegi panna kui see, mis juba on pandud, see on Jeesus Kristus!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 12, 'Aga kui keegi sellele alusele ehitab hoone kullast, hõbedast, kalliskividest, puust, heintest, õlest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 13, 'siis saab igaühe töö avalikuks; sest selle teeb selgeks Issanda päev, sest see saab tules ilmsiks ja tuli ise katsub läbi igaühe töö, missugune see on.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 14, 'Kui kellegi töö, mis tema sellele alusele on ehitanud, jääb püsima, siis ta saab palga;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 15, 'aga kui kellegi töö põleb ära, siis ta saab sellest kahju; kuid tema ise pääseb, aga ometi otsekui läbi tule.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 16, 'Eks te tea, et te olete Jumala tempel ja et Jumala Vaim elab teie sees?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 17, 'Kui keegi rikub Jumala templi, siis Jumal rikub tema; sest Jumala tempel on püha ja seesama olete teie.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 18, 'Ärgu ükski ennast petku! Kui keegi teie seast arvab enese targa olevat selles maailmas, siis ta saagu jõledaks, et ta saaks targaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 19, 'Sest selle maailma tarkus on jõledus Jumala ees. Sest on kirjutatud: \"Tema tabab tarku nende kavaluses!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 20, 'Ja jälle: \"Issand tunneb tarkade mõtlemised, et need on tühised!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 21, 'Ärgu siis ükski kiidelgu inimestest! Sest kõik on teie päralt,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 22, 'olgu Paulus, olgu Apollos, olgu Keefas, olgu maailm, olgu elu, olgu surm, olgu praegused asjad, olgu tulevased asjad, kõik on teie päralt;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 23, 'aga teie olete Kristuse päralt ja Kristus on Jumala päralt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 1, 'Nõnda pidagu meid igaüks Kristuse sulaseiks ja Jumala saladuste majapidajaiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 2, 'Ent majapidajailt nõutakse sel juhul kõigepealt, et igaüks leitaks ustav olevat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 3, 'Aga minule ei tähenda see midagi, et teie või mõni inimlik kohtupäev minu üle kohut mõistate; ja mina isegi ei mõista kohut enese üle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 4, 'Sest mina ei tea enesel ühtki süüd olevat; aga sellega ei ole ma veel õigeks mõistetud. Sest Issand on See, Kes minu üle kohut mõistab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 5, 'Sellepärast ärge mõistke ühtki kohut enneaegu, enne kui Issand tuleb, Kes ka pimeduse salajased asjad toob valge ette ja südamete nõud teeb avalikuks; ja siis saab igaüks kiituse Jumalalt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 6, 'Seda olen mina, vennad, tähendanud enese ja Apollose kohta teie pärast, et te meist õpiksite - mitte üle selle, mis on kirjutatud -, et keegi teist ei uhkustaks teise vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 7, 'Sest kes eesõigustab sind? Ja mis on sinul, mida sina ei oleks saanud? Aga kui sa selle oled saanud, mis sa siis kiitled, otsekui ei oleks sa seda mitte saanud?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 8, 'Juba te olete täis söönud, juba olete saanud rikkaks; te olete saanud kuningaiks ilma meieta. Kui te ometi tõesti oleksite kuningad, et ka meie võiksime ühes teiega olla kuningad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 9, 'Sest mulle näib, et Jumal meid, Apostleid, on asetanud viimasteks, otsekui surmamõistetuiks; sest me oleme saanud silmaimeks maailmale, nii inglitele kui ka inimestele;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 10, 'meie oleme ogarad Kristuse pärast, aga teie olete arukad Kristuses; meie oleme nõdrad, aga teie olete tugevad; teie ausad, aga meie autud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 11, 'Praeguse hetkenigi kannatame nälga ja janu ja oleme alasti, ja meid pekstakse rusikatega ja meil ei ole kuskil asu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 12, 'Ja me näeme vaeva tööd tehes oma kätega; kui meid sõideldakse, siis me õnnistame; kui meid taga kiusatakse, siis me kannatame ära;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 13, 'kui meid sõimatakse, siis me räägime lahkesti. Me oleme saanud otsekui maailma pühkmeks ja kõikide roistuks tänapäevani.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 14, 'Mina ei kirjuta seda teie häbistamiseks, vaid noomin teid kui oma armsaid lapsi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 15, 'Sest ehkki teil oleks kümme tuhat kasvatajat Kristuses, ometi ei ole teil mitut isa, sest mina sünnitasin teid Evangeeliumi läbi Kristuses Jeesuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 16, 'Sellepärast ma manitsen teid: võtke mind eeskujuks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 17, 'Otse sellepärast ma läkitasin teile Timoteose, kes on mu armas ja ustav poeg Issandas. Tema tuletab teile meelde minu teed, mida ma käin Kristuses Jeesuses, nõnda nagu ma igal pool koguduses õpetan.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 18, 'Mõned on hakanud suurustama, otsekui ma ei tulekski teie juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 19, 'Aga ma tulen varsti teie juurde, kui Issand tahab, ja siis saan selgusele mitte suurustajate sõnade, vaid väe kohta!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 20, 'Sest Jumala Riik ei ole mitte sõnades, vaid väes!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 21, 'Mis te tahate: kas pean tulema teie juurde vitsaga või armastuse ning tasase vaimuga?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 1, 'Üldse kuuldub teie seas olevat hoorust ja niisugust hoorust, millist ei leidu paganategi seas, et kellelgi on oma isa naine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 2, 'Ja te olete pealegi suurelised ega ole pigemini saanud kurvaks, et see, kes niisuguse teo on teinud, lükataks teie seast välja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 3, 'Aga mina, ehk ma küll olen ihulikult eemal, aga vaimus siiski ligi, olen otsekui teie juures viibides omalt poolt juba otsustanud, et see, kes niisuguse teo on teinud -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 4, 'pärast seda kui teie ja minu vaim ühes meie Issanda Jeesuse väega on kokku saanud -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 5, 'Issanda Jeesuse nimel üle antaks saatanale liha hukkamiseks, et ta vaim päästetaks Issanda päeval.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 6, 'Teie kiitlemine ei ole hea. Eks te tea, et pisut haputaignat teeb kogu taigna hapuks?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 7, 'Pühkige välja vana haputaigen, et oleksite uus taigen, nõnda nagu te oletegi hapnemata. Sest ka meie Paasatall Kristus on tapetud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 8, 'Siis pidagem pühi, mitte vana haputaignaga, mitte pahede ega kurjuse haputaignaga, vaid puhtuse ja tõe hapnemata taignaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 9, 'Ma kirjutasin oma kirjas, et teil ärgu olgu tegemist hoorajatega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 10, 'muidugi mitte üldiselt selle maailma hoorajatega ja ahnetega ja riisujatega ja ebajumala kummardajatega, sest muidu te peaksite minema ära maailmast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 11, 'Aga praegu ma kirjutasin teile, et teil ärgu olgu midagi tegemist niisugusega, keda nimetatakse vennaks ja kes on kas hooraja või ahne või ebajumala kummardaja või pilkaja või joodik või anastaja; niisugusega ärge ühes söögegi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 12, 'Sest kas minu asi on mõista kohut nende üle, kes on väljas? Eks te mõista kohut nende üle, kes on sees?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 13, 'Ent nende üle, kes on väljas, mõistab Jumal kohut. Lükake siis kurjategija endi seast välja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 1, 'Kuidas julgeb keegi teie seast, kui tal on tegemist teisega, käia kohut ülekohtuste juures ja mitte pühade juures?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 2, 'Või kas te ei tea, et pühad mõistavad kohut maailma üle? Ja kui te mõistate kohut maailma üle, kas te siis ei kõlba otsuseid tegema kõige vähemais asjus?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 3, 'Eks te tea, et kord me mõistame kohut Inglitegi üle, miks siis mitte ajalikes asjus?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 4, 'Kui teil nüüd on õiendusi ajalikes asjus, kas te siis seate kohtunikeks neid, kellel ei ole lugupidamist koguduses?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 5, 'Seda ma ütlen teile häbiks. Kas siis teie seas ei ole ühtainustki tarka, kes kohut mõistaks venna ja venna vahel?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 6, 'Vaid vend käib kohut vennaga ja pealegi uskmatute ees!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 7, 'Juba seegi on üldse veaks teie seas, et te üksteisega kohut käite. Miks te pigemini ei kannata ülekohut? Miks te pigemini ei lase enestele kahju teha?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 8, 'Vaid te teete ülekohut ja kahju, ja pealegi vendadele!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 9, 'Või kas te ei tea, et ülekohtused ei päri Jumala Riiki? Ärge eksige: hoorajad ja ebajumala kummardajad ja abielurikkujad ja salajased ropud ja poisipilastajad');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 10, 'ja vargad ja ahned, joodikud, pilkajad ja anastajad ei päri Jumala Riiki!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 11, 'Ja sellised olid teist mõningad; aga te olete puhtaks pestud, olete pühitsetud, olete õigeks tehtud Issanda Jeesuse Kristuse nimes ja meie Jumala Vaimus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 12, 'Kõike on mul luba teha, aga kõigest ei ole kasu; kõike on mul luba teha, aga miski ei tohi saada meelevalda minu üle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 13, 'Toit on kõhu jaoks ja kõht on toidu jaoks, ja Jumal teeb lõpu nii ühele kui teisele. Aga ihu ei ole hooruse jaoks, vaid Issanda jaoks ja Issand ihu jaoks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 14, 'Ent Jumal on üles äratanud Issanda ja äratab üles ka meid Oma väe läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 15, 'Eks te tea, et teie ihud on Kristuse liikmed? Kas peaksin siis võtma Kristuse liikmed ja tegema nad hoora liikmeiks? Ärgu seda sündigu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 16, 'Või te ei tea, et see, kes hoiab hoora poole, on temaga üks ihu? Sest \"need kaks\", ütleb tema, \"peavad olema üks liha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 17, 'Aga kes hoiab Issanda poole, see on üks vaim Temaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 18, 'Põgenege hooruse eest! Iga patt, mida inimene iganes teeb, on väljaspool ihu, aga kes hoorab, teeb pattu omaenese ihu vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 19, 'Või te ei tea, et teie ihu on teie sees oleva Püha Vaimu tempel, kelle te olete saanud Jumalalt, ja et te ei ole iseeneste omad?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 20, 'Sest te olete kalli hinnaga ostetud. Austage siis Jumalat oma ihus!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 1, 'Aga mis puutub sellesse, millest kirjutasite, siis mehele on hea naist mitte puudutada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 2, 'Kuid hooruspattude pärast olgu igal mehel oma naine ja igal naisel oma mees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 3, 'Mees täitku oma kohust naise vastu ja nõndasamuti ka naine mehe vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 4, 'Naisel ei ole meelevalda oma ihu üle, vaid mehel; ja nõndasamuti ei ole mehel meelevalda oma ihu üle, vaid naisel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 5, 'Ärge tõmbuge eemale teineteisest muidu kui ehk mõlemate tahtes mõneks ajaks, et oleksite vabad palveks, ja tulge jälle ühte, et saatan teid ei kiusaks teie taltumatuse pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 6, 'Ent seda ma ütlen andes luba, mitte käskides.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 7, 'Sest ma tahaksin, et kõik inimesed oleksid nõnda nagu minagi; aga igaühel on oma armuanne Jumalalt, ühel nõnda, teisel teisiti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 8, 'Ent ma ütlen vallalistele ja lesknaistele: neile on hea, kui nad jäävad nõnda nagu minagi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 9, 'Aga kui nad ei suuda endid talitseda, siis nad abiellugu, sest parem on abielluda kui himudes põleda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 10, 'Aga abielulistele ei käsi mina, vaid Issand, et naine ärgu mingu mehest lahku.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 11, 'On aga keegi lahutatud, siis ta jäägu abielutuks või leppigu ära oma mehega. Ja mees ärgu hüljaku naist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 12, 'Aga muile ütlen mina, mitte Issand: kui ühel vennal on uskmatu naine ja naine heal meelel tahab elada temaga, siis ta ärgu hüljaku teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 13, 'Samuti kui ühel naisel on uskmatu mees ja mees heal meelel tahab elada temaga, siis ta ärgu hüljaku meest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 14, 'Sest uskmatu mees on pühitsetud naise läbi ja uskmatu naine on pühitsetud mehe läbi, sest muidu oleksid teie lapsed rüvedad, aga nüüd on nad pühad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 15, 'Aga kui uskmatu pool lahkub, siis ta lahkugu; niisuguseis asjus ei ole vend ega õde orjastatud; ent Jumal on meid rahus kutsunud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 16, 'Sest mis tead sina, naine, kas sa oma mehe päästad? Või mis tead sina, mees, kas sa oma naise päästad?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 17, 'Ainult nõnda nagu Jumal on jaganud igaühele, nõnda nagu Issand on igaühe kutsunud, nõnda ta käitugu ja nõnda korraldan mina kõigis kogudustes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 18, 'On keegi kutsutud ümberlõigatuna, siis ärgu ta seda varjaku; on keegi kutsutud eesnahas, siis ärgu ta lasku ennast ümber lõigata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 19, 'Ümberlõikamine ei ole midagi ja eesnahk ei ole midagi, vaid tähtis on Jumala käskude pidamine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 20, 'Igaüks jäägu sellesse kutsesse, milles ta on kutsutud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 21, 'Kui sa oled kutsutud orjana, siis ära hooli sellest. Aga kui sa võid ka vabaks saada, siis kasuta pigemini seda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 22, 'Sest kes on kutsutud orjana Issandas, on Issandas vabakslastu; samuti, kes on kutsutud vabana, on Kristuse ori.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 23, 'Teie olete kallilt ostetud, ärge saage inimeste orjaks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 24, 'Jäägu, vennad, igaüks Jumala ees sellesse, milles ta on kutsutud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 25, 'Ent neitsite kohta ei ole mul Issanda käsku, aga ma annan nõu otsekui see, kes Issandalt on saanud armu olla usaldusväärne.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 26, 'Siis ma arvan eelseisva kitsikuse pärast hea olevat, et inimene jääb, nagu ta on.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 27, 'Kui sa oled naisega seotud, siis ära püüa vabaneda; kui oled vallaline, siis ära otsi naist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 28, 'Aga kui sa ka abiellud, siis sa ei tee pattu; ja kui neitsi läheb mehele, siis ta ei tee pattu. Ent sellised saavad kannatada ihulikku viletsust, mina aga säästaksin teid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 29, 'Ent seda ütlen, vennad, et aeg on lühike; sellepärast olgu nii, et needki, kellel on naised, olgu nagu need, kellel neid ei ole;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 30, 'ja olgu nii, et kes nutavad, nagu ei nutakski, ja kes on rõõmsad, nagu ei olekski rõõmsad, ja kes ostavad, nagu ei saakski seda enesele pidada,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 31, 'ja kes seda maailma tarvitavad, nagu ei tarvitakski seda; sest selle maailma nägu kaob.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 32, 'Ent mina tahaksin, et te oleksite muretud. Vallaline mees hoolitseb selle eest, mis kuulub Issandale, kuidas ta saaks olla Issanda meele järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 33, 'Aga naisemees hoolitseb selle eest, mis kuulub maailmale, kuidas ta saaks olla naise meele järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 34, 'Nii on ta jaotatud kaheks. Ja vallaline naine või neitsi hoolitseb selle eest, mis kuulub Issandale, et ta võiks olla püha nii ihu kui ka vaimu poolest; aga abielunaine hoolitseb selle eest, mis kuulub maailmale, kuidas tema saaks olla mehe meele järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 35, 'Aga seda ma ütlen teie eneste kasuks, ei mitte, et köit panna teile kaela, vaid et te elaksite viisakalt ja püsiksite takistamatult Issandas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 36, 'Aga kui keegi arvab vääriti käitlevat oma neitsit, kelle paras naimaiga on möödumas, ja kui see peab nõnda sündima, siis tehku ta, mida tahab; ta ei tee pattu. Nad abiellugu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 37, 'Aga kes on kindel oma südames ja kellel seda ei ole vaja, vaid on meelevald oma tahtmise üle ning on otsustanud oma südames oma neitsi jätta neitsiks, see teeb hästi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 38, 'Kes siis oma neitsi naib, teeb hästi, ja kes teda ei nai, teeb paremini.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 39, 'Naine on seotud niikaua kui tema mees elab; aga kui ta mees on läinud magama, on ta vaba minema mehele, kellele tahab, kui see vaid sünnib Issandas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 40, 'Aga ta on õndsam, kui ta jääb, nagu ta on. See on minu arvamine. Ent ma mõtlen, et minulgi on Jumala Vaim.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 1, 'Mis puutub ebajumala ohvrilihasse, siis teame: meil kõigil on tunnetus. Tunnetus teeb suureliseks, ent armastus ehitab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 2, 'Kui keegi arvab midagi tunnetavat, ta ei tunneta veel nõnda, kuidas tuleb tunnetada,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 3, 'aga kes armastab Jumalat, selle on Jumal ära tundnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 4, 'Mis nüüd puutub ebajumala ohvriliha söömisse, siis teame, et maailmas ei ole ühtki ebajumalat ja et ei ole muud Jumalat kui Üks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 5, 'Sest kuigi on olemas niinimetatud jumalaid, olgu taevas või maa peal, nagu ju on olemas palju jumalaid ja palju isandaid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 6, 'on meil ometigi ainult üks Jumal, Isa, Kellest on kõik asjad ja Kellesse me suundume, ja üks Issand Jeesus Kristus, Kelle läbi on kõik ja ka meie Tema läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 7, 'Aga kõikidel ei ole seda teadmist, vaid harjumusest ebajumalatega söövad mõned tänini ebajumala ohvriliha sellisena ja nende südametunnistus, mis on nõder, rüvetub.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 8, 'Roog ei vii meid lähemale Jumalale; kui me ei söö, ei ole meile sellest kahju, ja kui sööme, ei ole meil sellest kasu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 9, 'Vaadake ainult, et see teie vabadus ei saaks nõtradele komistuseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 10, 'Sest kui keegi, kellel on tunnetus, näeb sind ebajumala kojas lauas istuvat, kas siis tema südametunnistus, kui see on nõrk, ei ärrita teda sööma ebajumala ohvriliha?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 11, 'Siis läheb ju sinu tunnetuse läbi hukka nõder vend, kelle pärast Kristus on surnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 12, 'Aga kui te nõnda teete pattu vendade vastu ja haavate nende nõrka südametunnistust, siis te teete pattu Kristuse vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 13, 'Sellepärast: kui roog mu venda pahandab, siis ma ei söö iialgi enam liha, et ma oma venda ei pahandaks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 1, 'Eks ma ole vaba? Eks ma ole Apostel? Eks ma ole näinud Jeesust Kristust, meie Issandat? Eks teie ole minu töö Issandas?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 2, 'Kui ma Apostel ei ole muile, siis ma olen seda ometi teile; sest teie olete minu Apostliameti pitser Issandas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 3, 'Minu kaitsekõne neile, mu arvustajaile, on see:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 4, 'kas meil ei ole õigust süüa ja juua?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 5, 'Kas meil ei ole õigust naiseks kaasa võtta usuõde, nagu muil Apostlitel ning Issanda vendadel ja Keefasel?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 6, 'Või kas üksi minul ja Barnabasel pole õigust olla ilma kehaliku tööta?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 7, 'Kes iganes läheb omal kulul sõtta? Kes istutab viinamäe, aga ei söö selle vilja? Või kes hoiab karja, aga ei söö karja piima?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 8, 'Kas ma räägin seda inimeste viisil? Eks käskki ütle seda?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 9, 'On ju Moosese käsuõpetuses kirjutatud: \"Ära seo kinni härja suud, kui ta pahmast tallab!\" Ega Jumal härgade eest hoolitse?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 10, 'Eks ta seda kõige rohkem ütle meie pärast? Sest meie pärast on kirjutatud, et kündja peab kündma lootuses ja rehepeksja peksma lootuses osa saada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 11, 'Kui me teile oleme külvanud vaimulikku seemet, kas on see siis suur asi, kui me lõikame teie ihulikku vara?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 12, 'Kui teistel on niisugune õigus teie kohta, mispärast mitte palju rohkem meil? Me ainult ei ole tarvitanud seda õigust, vaid me talume kõike, et me mingit takistust ei teeks Kristuse Evangeeliumile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 13, 'Eks te tea, et need, kes pühakojas tööd teevad, pühakojast ka söövad, et need, kes teenivad ohvrialtari juures, oma osa saavad altariga samal määral?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 14, 'Nõnda on ka Issand seadnud neile, kes Evangeeliumi kuulutavad, et nad elaksid Evangeeliumist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 15, 'Aga mina ei ole sellest midagi tarvitanud. Ent ma ei ole seda kirjutanud, et mulle nõnda peaks sündima; sest mulle oleks palju parem surra, kui - ei, keegi ei saa mu kiitlemist tühjaks teha!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 16, 'Sest kui ma armuõpetust kuulutan, ei ole mul sellest kiitlemist, sest mu kohus on seda teha. Ja häda mulle, kui ma armuõpetust ei kuuluta!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 17, 'Sest kui ma teen seda vabast tahtest, siis ma saan palga; aga kui sunniviisil, siis on ometi amet usaldatud minu kätte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 18, 'Mis on siis mu palk? See, et ma armuõpetust kuulutades tasuta levitan Evangeeliumi, ilma et ma tarvitaksin oma õigust, mis Evangeeliumis on lubatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 19, 'Sest ehk ma küll olen vaba kõikidest, olen ma ometi hakanud kõikide orjaks, et ma seda rohkem inimesi võiksin võita.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 20, 'Nii olen ma juutidele olnud otsekui juut, et ma võidaksin juudid; neile, kes on käsu all, olen ma otsekui käsualune, kuigi ma käsu all ei ole, et ma võidaksin käsualused.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 21, 'Käsuta olijaile ma olen otsekui käsuta - ehk ma küll ei ole ilma Jumala käsuta, vaid elan Kristusele käsus - et võita need, kes on käsuta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 22, 'Nõtradele ma olen saanud nõdraks, et võita nõdrad. Ma olen kõikidele saanud kõigeks, et ma igapidi mõned päästaksin.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 23, 'Aga kõike ma teen Evangeeliumi pärast, et mina sellest ka osa saaksin.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 24, 'Eks te tea, et need, kes võidu jooksevad, jooksevad küll kõik, aga võiduanni saab üks? Jookske nõnda, et teie selle saate!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 25, 'Aga iga võidujooksja on kasin kõigis asjus, nemad küll selleks, et saavutada kaduvat pärga, aga meie, et saavutada kadumatut pärga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 26, 'Sellepärast ma ei jookse nagu pimedast peast, ma ei võitle nõnda nagu see, kes tuult peksab,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 27, 'vaid ma talitsen oma ihu ja teen selle oma orjaks, et ma muile jutlust öeldes ise ei saaks kõlvatuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 1, 'Sest ma ei taha, vennad, et teil oleks teadmata, et meie esiisad olid kõik pilve all ja läksid kõik merest läbi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 2, 'ja ristiti kõik Moosesesse pilves ja meres,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 3, 'ja sõid kõik sama vaimulikku rooga');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 4, 'ja jõid kõik sama vaimulikku jooki, sest nad jõid vaimulikust kaljust, mis neid järgis. Ent see kalju oli Kristus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 5, 'Aga suurem hulk neist ei olnud Jumalale meelepärased, sest nad löödi maha kõrbes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 6, 'Ent need lood sündisid meile eeltähenduseks, et me ei himustaks kurja, nagu nemad himustasid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 7, 'Ärge ka saage ebajumala kummardajaiks, nõnda nagu mõned neist said, nagu on kirjutatud: \"Rahvas istus maha sööma ja jooma ning tõusis üles mängima.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 8, 'Ärgem pidagem ka hooraelu, nõnda nagu mõned neist pidasid ja langesid ühel päeval kakskümmend kolm tuhat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 9, 'Ärgem ka kiusakem Issandat, nõnda nagu mõned neist kiusasid ja said surma madude läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 10, 'Ärge ka nurisege, nõnda nagu mõned neist nurisesid ja said otsa hukkaja läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 11, 'See sündis neile eeltähendavalt ja on kirjutatud meile manitsuseks, kellele maailma lõpu ajad on vastu jõudnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 12, 'Sellepärast, kes enese arvab seisvat, katsugu, et ta ei langeks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 13, 'Teid ei ole veel tabanud muud kui inimlik kiusatus; aga ustav on Jumal, Kes ei lase teid rohkem kiusata kui te suudate kanda, vaid ühes kiusatusega valmistab ka väljapääsu, nõnda et te suudaksite kanda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 14, 'Sellepärast, minu armsad, põgenege ebajumala teenistuse eest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 15, 'Ma räägin nõnda kui arusaajaile; otsustage ise, mida ma räägin.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 16, 'Õnnistuse karikas, mida me õnnistame, eks see ole Kristuse Vere osadus? Leib, mida me murrame, eks see ole Kristuse Ihu osadus?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 17, 'Sellepärast et on ainult üks leib, siis me, kuigi paljud, oleme üks ihu; sest me kõik saame osa ühest leivast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 18, 'Vaadake Iisraeli peale, nagu ta liha poolest on! Eks need, kes söövad ohvreid, ole altari osalised?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 19, 'Mis ma nüüd ütlen? Kas seda, et ebajumala ohver on midagi? Või et ebajumal on midagi?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 20, 'Ei! Vaid ma ütlen: mida paganad ohverdavad, seda nad ohverdavad kurjadele vaimudele, aga mitte Jumalale. Ent ma ei taha, et teil oleks osadust kurjade vaimudega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 21, 'Te ei või juua Issanda karikat ja kurjade vaimude karikat; te ei või osa võtta Issanda lauast ja kurjade vaimude lauast!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 22, 'Või tahame Issandat ärritada? Kas me oleme vägevamad temast?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 23, 'Kõik on lubatav, kuid kõigest ei ole kasu; kõik on lubatav, kuid kõik ei ehita kogudust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 24, 'Ärgu ükski otsigu oma kasu, vaid teise kasu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 25, 'Kõike, mida lihaturul müüakse, sööge, südametunnistusest hoolimata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 26, 'Sest Issanda päralt on maa ja selle täius!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 27, 'Kui keegi uskmatuist kutsub teid ja te tahate minna, siis sööge kõike, mis teile ette pannakse, südametunnistusest hoolimata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 28, 'Aga kui keegi teile ütleb: \"See on ohvriliha!\", siis ärge sööge selle inimese pärast, kes tegi märkuse, ja südametunnistuse pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 29, 'Ent mina ei räägi sinu, vaid teise südametunnistusest. Sest milleks peaks teise südametunnistus otsustama minu vabaduse üle?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 30, 'Kui ma midagi vastu võtan tänuga, miks mind pilgatakse selle pärast, mille eest ma tänan?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 31, 'Kas te siis sööte või joote või midagi teete, seda tehke kõik Jumala auks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 32, 'Ärge olge pahanduseks ei juutidele ega kreeklastele ega Jumala kogudusele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 33, 'nagu minagi olen kõiges kõikidele meelepärane ega otsi oma, vaid paljude kasu, et nad päästetaks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 1, 'Võtke mind eeskujuks, nagu minagi võtan Kristuse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 2, 'Aga ma kiidan teid, vennad, et te kõiges tuletate mind meelde ja peate mu seadmisi, nõnda nagu ma need teile olen andnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 3, 'Aga ma tahan, et te teaksite, et Kristus on iga mehe pea ja et mees on naise pea ja Jumal on Kristuse pea.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 4, 'Iga mees, kes palvetab või prohvetlikult räägib kaetud peaga, häbistab oma pead.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 5, 'Aga iga naine, kes palvetab või prohvetlikult räägib katmata peaga, häbistab oma pead, sest see on otse sama, kui oleks tema pea paljaks aetud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 6, 'Sest kui naine ei kata oma pead, siis lõigaku ta ka juuksed maha. Aga et naisel on häbiks juukseid lõigata või pead paljaks ajada, siis ta katku oma pea.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 7, 'Mehel ei ole tarvis oma pead katta, sest tema on Jumala kuju ja aupaiste, naine aga on mehe aupaiste.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 8, 'Sest mees ei ole naisest, vaid naine on mehest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 9, 'Meest ei loodud ka mitte naise pärast, vaid naine loodi mehe pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 10, 'Sellepärast peab naisel olema pea peal meelevallatunnus Inglite pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 11, 'Ometi ei ole naine meheta ega mees naiseta Issandas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 12, 'Sest otsekui naine mehest, nõnda on ka mees naise läbi, aga kõik on Jumalast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 13, 'Otsustage iseenestes, kas naisele kõlbab katmata peaga paluda Jumalat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 14, 'Eks ju loodus ise teid õpeta, et mehele on häbiks kanda pikki juukseid;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 15, 'ent naisele, kui ta kannab pikki juukseid, on see auks; sest pikad juuksed on temale antud liniku eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 16, 'Aga kui keegi on tülinorija, see teadku, et meil ei ole seesugust kommet ega ka mitte Jumala kogudustel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 17, 'Aga seda käskides ma ei kiida seda, et te tulete kokku mitte paremuseks vaid pahemuseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 18, 'Sest esmalt ma kuulen, et kui te kokku tulete Jumala koguduses, teil on lõhesid eneste keskel, ja osalt ma usun seda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 19, 'Sest teie seas peab olema lahkõpetusi, et saaks ilmsiks, kes teie seast on püsivad katsumistes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 20, 'Kui te nüüd kokku tulete, siis ei ole Issanda õhtusöömaaja pidamist;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 21, 'sest sööma asudes võtab igaüks enne iseenese toidu ära, ja mõni jääb ilma ja mõni joob liiaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 22, 'Kas teil ei ole kodasid, kus te võite süüa? Või kas põlgate Jumala kogudust ja teete häbi neile, kellel ei ole midagi? Mis ma pean teile ütlema? Kas pean teid kiitma? Selle poolest ma ei kiida teid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 23, 'Sest mina olen Issandalt saanud selle, mis ma teilegi olen andnud, et Issand Jeesus sel ööl, mil Ta ära anti, võttis leiva');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 24, 'ja tänas ja murdis ning ütles: \"See on Minu IhU, mis teie eest antakse. Seda tehke Minu mälestuseks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 25, 'Samuti Ta võttis ka karika pärast söömaaega ning ütles: \"See karikas on uus leping Minu Veres. Seda tehke nii sageli kui te iganes seda joote, Minu mälestuseks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 26, 'Sest iga kord, kui te seda leiba sööte ja karikast joote, te kuulutate Issanda surma, kuni Ta tuleb!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 27, 'Sellepärast: kes iganes seda leiba sööb või Issanda karikat joob kõlvatult, sellel on Issanda Ihust ja Verest süüd.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 28, 'Aga inimene katsugu ennast läbi ja nõnda söögu ta seda leiba ja joogu sellest karikast;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 29, 'sest kes sööb ja joob, see sööb ja joob enesele nuhtlust, kui ta enesele ei anna aru sellest ihust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 30, 'Sellepärast on ka palju nõrku ja põdejaid teie seas ja paljud on läinud magama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 31, 'Sest kui me enestest ise aru annaksime, siis meie üle ei mõistetaks kohut;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 32, 'aga kui meie üle kohut mõistetakse, siis Issand karistab meid, et meid ühes maailmaga hukka ei mõistetaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 33, 'Seepärast, mu vennad, kui te kokku tulete sööma, siis oodake üksteist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 34, 'Kui keegi on näljane, söögu kodus, et te ei tuleks kokku nuhtluseks. Muud asjad ma korraldan, kui ma tulen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 1, 'Mis puutub vaimuannetesse, vennad, ei taha ma teid jätta teadmatusse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 2, 'Te teate, et alles paganad olles te käisite keeletute ebajumalate juures, nõnda nagu teid veeti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 3, 'Sellepärast ma teen teile teatavaks, et ükski, kes räägib Jumala Vaimus, ei ütle: \"Neetud on Jeesus!\" Ja ükski ei või öelda: \"Jeesus on Issand!\" kui vaid Pühas Vaimus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 4, 'Armuanded on küll mitmesugused, aga Vaim on sama;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 5, 'ja ametid on mitmesugused, aga Issand on sama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 6, 'Ja väeavaldused on mitmesugused, aga sama on Jumal, Kes teeb kõike kõikide sees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 7, 'Aga igaühele antakse Vaimu ilmutus ühiseks kasuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 8, 'Nii antakse ühele Vaimu läbi tarkuse sõna, teisele aga tunnetuse sõna sama Vaimu kaudu;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 9, 'teisele antakse usku samas Vaimus, teisele andeid terveks teha samas Vaimus;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 10, 'teisele vägevaid tegusid, teisele prohvetlikku kuulutamist, teisele vaimude äratundmist, teisele mõnesuguste keelte kõnelemist, teisele keelte tõlgitsemist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 11, 'Ent kõike seda teeb üks ja sama Vaim, jagades igaühele eriti, nõnda nagu Tema tahab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 12, 'Sest otsekui ihu on üks ja tal on palju liikmeid, aga kõik ihu liikmed, kuigi neid on palju, on üks ihu, nõnda on ka Kristus:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 13, 'sest me kõik oleme ühe Vaimuga ristitud üheks ihuks, olgu juudid või kreeklased, olgu orjad või vabad, ja oleme kõik sama Vaimuga joodetud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 14, 'Sest ihu ei ole üks liige, vaid palju liikmeid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 15, 'Kui jalg ütleks: \"Et ma ei ole käsi, siis ma ei kuulu ihusse!\" kas ta sellepärast ei kuulu ihusse?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 16, 'Ja kui kõrv ütleks: \"Et ma ei ole silm, siis ma ei kuulu ihusse!\" kas ta sellepärast ei kuulu ihusse?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 17, 'Kui kõik ihu oleks silm, kuhu jääks siis kuulmine? Kui kõik oleks kuulmine, kuhu jääks siis haistmine?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 18, 'Aga nüüd Jumal asetaski liikmed, igaühe neist, ihu külge, nõnda nagu tema tahtis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 19, 'Ja kui kõik need oleksid üks liige, kuhu jääks siis ihu?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 20, 'Nüüd aga ongi palju liikmeid, aga üks ihu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 21, 'Silm ei või öelda käele: \"Mulle ei ole sind tarvis\" Või jälle pea jalgadele: \"Mulle ei ole teid tarvis!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 22, 'Vaid palju enam on tarvis neid ihu liikmeid, mis näivad olevat nõrgemad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 23, 'ja neid, mida me peame autumaiks ihu küljes, katame iseäralise auga, ja liikmeid, millest meil on häbi, me ehime kõige enam;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 24, 'ent meie suursuguseil liikmeil pole seda tarvis. Sest Jumal on ihu nõnda ühte liitnud, et Ta sellele, mis on alam, on andnud rohkem au,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 25, 'et ihus ei oleks lahkmeelt, vaid et liikmed üksmeeles kannaksid muret üksteise eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 26, 'Ja kui liige kannatab, siis kannatavad teisedki liikmed ühes temaga, ja kui üht liiget austatakse, siis teisedki liikmed rõõmutsevad ühes temaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 27, 'Aga teie olete Kristuse ihu ja igaüks omast kohast Tema liikmed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 28, 'Ja Jumal on seadnud koguduses mõned - esiteks Apostleiks, teiseks prohveteiks, kolmandaks õpetajaiks; siis Ta on andnud imetegusid, siis andeid terveks teha, abistada ja valitseda, rääkida mõnesuguseid keeli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 29, 'Ega kõik ole Apostlid? Ega kõik prohvetid? Ega kõik õpetajad? Ega kõik imetegijad?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 30, 'Ega kõigil ole tervekstegemise armuandeid? Ega kõik räägi keeltega? Ega kõik tõlgitse?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 31, 'Aga olge agarad püüdma suuremaid armuandeid! Ja ma näitan veel teile tee, mis on üle kõige.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 1, 'Kui ma inimeste ja inglite keeltega räägiksin, aga mul poleks armastust, oleksin ma vaid kumisev vask ja kõlisev kelluke!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 2, 'Ja kui mul oleks prohvetianne ja ma teaksin kõik saladused ja kõik tunnetatu, ja kui mul oleks kõik usk, nõnda et võiksin mägesid teisale paigutada, aga mul poleks armastust, siis ei oleks minust ühtigi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 3, 'Ja kui ma jagaksin kõik oma vara vaestele ja kui ma annaksin oma ihu põletada ja mul poleks armastust, siis ei oleks mul sellest mingit kasu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 4, 'Armastus on pikameelne, armastus on täis heldust; ta ei ole kade, armastus ei suurustle, ta ei ole iseennast täis;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 5, 'ta ei ole viisakuseta, ta ei otsi omakasu, ta ei ärritu, ta ei pea meeles paha;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 6, 'ta ei rõõmutse ülekohtust, aga ta rõõmutseb ühes tõega;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 7, 'tema vabandab kõik, usub kõik, loodab kõik, sallib kõik!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 8, 'Armastus ei hävi ilmaski! Aga olgu prohveti ennustused, need kaovad; olgu keeled, need lakkavad; olgu tunnetus, see lõpeb ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 9, 'Sest poolik on, mida me tunnetame, ja poolik, mida me ennustame.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 10, 'Aga kui tuleb täiuslik asi, siis kaob see, mis on poolik!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 11, 'Kui ma olin väeti laps, siis ma rääkisin nagu väeti laps, ma mõtlesin nagu väeti laps ja arvasin nagu väeti laps; aga kui ma sain meheks, siis ma hülgasin selle, mis on omane väetile lapsele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 12, 'Sest nüüd me näeme nagu peeglis tuhmi kujutist, aga siis palgest palgesse; nüüd ma tunnetan poolikult, aga siis ma tunnetan täiesti, nagu minagi olen täiesti tunnetatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 13, 'Ent nüüd jääb usk, lootus, armastus, need kolm; aga suurim neist on armastus!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 1, 'Taotlege armastust ja olge agarad taotlema vaimuandeid, aga kõige enam prohvetlikku kõnelemist!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 2, 'Sest kes keeltega räägib, ei kõnele inimestele, vaid Jumalale; sest keegi ei saa temast aru, vaimust ta ju räägib saladusi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 3, 'Aga prohvetlikult kõneleja räägib inimestele nende ehitamiseks ja manitsuseks ja troostiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 4, 'Keeltega rääkija ehitab iseennast; aga prohvetlikult kõneleja ehitab kogudust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 5, 'Ma tahaksin küll, et te kõik räägiksite keeltega, aga veel enam, et te prohvetlikult kõneleksite; sest prohvetlikult kõneleja on suurem keeltega rääkijast, kui see mitte ühtlasi ei tõlgitse, nõnda et kõne saab koguduse ehitamiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 6, 'Aga nüüd, vennad, kui ma tuleksin teie juurde ja räägiksin keeltega, mis kasu ma tooksin teile, kui ma teile ei räägiks kas ilmutuse või tunnetuse või ennustuse või õpetuse kaudu?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 7, 'Nõnda on ju lugu ka elutute asjadega, mis annavad häält, olgu vile või kannel; kui need ei annaks erinevaid hääli, kuidas teatakse, mida puhutakse vilel või mida mängitakse kandlel?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 8, 'Samuti kui pasun annab segast häält, kes siis hakkab valmistama sõjaks?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 9, 'Nõnda ka teie: kui te keeltega ei räägi arusaadavalt, kuidas siis teatakse, mida räägitakse? Te räägiksite ju tuulde!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 10, 'Maailmas on ei tea kui palju mitmesuguseid keelemurdeid ja ükski neist pole häälikuteta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 11, 'Kui mina nüüd ei tunne hääliku tähendust, olen ma sellele, kes räägib, umbkeelne ja rääkija on mulle umbkeelne.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 12, 'Samuti teiegi olles agarad taotlema vaimuandeid, püüdke saada neid rohkesti koguduse ehitamiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 13, 'Seepärast, kes keeltega räägib, palugu Jumalat, et ta võiks ka tõlgitseda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 14, 'Sest kui ma keeltega rääkides palvetan, siis palvetab mu vaim, aga mu mõistus on viljatu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 15, 'Mida siis teha? Ma tahan palvetada vaimus ja tahan palvetada ka mõistusega; ma tahan laulda vaimus ja tahan laulda ka mõistusega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 16, 'Sest kui sa teed tänupalvet vaimus, kuidas siis see, kes võhiku kohal seisab, sinu tänu peale saab öelda: \"Aamen!\"; ta ju ei tea, mida sa ütled.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 17, 'Sest sa tänad küll hästi, aga teist see ei ehita.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 18, 'Ma tänan Jumalat, et mina räägin keeltega rohkem kui teie kõik!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 19, 'Aga ma tahan koguduses pigemini rääkida viis sõna oma mõistusega, et ka muid õpetada, kui kümme tuhat sõna keeltega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 20, 'Vennad, ärge saage lasteks mõtteviisis, vaid olge lapsed kurjuses; kuid mõtteviisi poolest saage täisealiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 21, 'Käsuõpetuses on kirjutatud: \"Teiste keeltega ja teiste huultega ma räägin sellele rahvale ja siiski nad ei kuule mind!\", ütleb Issand.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 22, 'Nii ei ole siis keeled tunnuseks usklikele, vaid uskmatuile; aga prohvetlik kõne ei ole tunnuseks uskmatuile, vaid usklikele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 23, 'Kui nüüd kõik kogudus tuleks kokku ühte paika ja kõik räägiksid keeltega, ja tuleksid sisse võhikud või uskmatud, kas nad ei ütleks, et te jampsite?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 24, 'Aga kui nemad kõik kõneleksid prohvetlikult ja tuleks uskmatu või võhik, siis nad kõik paljastaksid tema olukorra ja kõik arvustaksid teda;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 25, 'tema südame saladused tuleksid ilmsiks ja ta heidaks siis silmili maha ja kummardaks Jumalat ning tunnistaks, et Jumal on tõesti teie sees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 26, 'Mis siis nüüd, vennad? Kui te kokku tulete, siis on igaühel midagi, kas laulu või õpetust või ilmutust või keeltega rääkimist või tõlgitsemist; sündigu see kõik koguduse ehitamiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 27, 'Kui keeltega räägitakse, siis rääkigu kaks või kõige rohkem kolm ükshaaval, ja üks tõlgitsegu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 28, 'Aga kui tõlgitsejat ei ole, olgu rääkija vait koguduses ja rääkigu iseenesele ja Jumalale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 29, 'Aga prohveteist kõnelgu kaks või kolm ja teised arvustagu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 30, 'Ja kui kellelegi teisele, kes istub, ilmutatakse midagi, siis olgu esimene vait.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 31, 'Sest te võite kõik ükshaaval prohvetlikult kõnelda, et kõik õpiksid ja saaksid troosti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 32, 'Ja prohvetite vaimud alistuvad prohveteile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 33, 'Sest Jumal ei ole mitte korratuse, vaid rahu Jumal. Nõnda nagu kõigis pühade kogudustes,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 34, 'olgu naised koguduses vait, sest neil ei ole luba kõnelda, vaid nad olgu allaheitlikud, nõnda nagu käskki ütleb.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 35, 'Aga kui nad tahavad midagi õppida, siis nad küsigu kodus oma meestelt. Sest naisele on häbiks kõnelda koguduses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 36, 'Või kas Jumala sõna on teist lähtunud? Või kas see on tulnud üksnes teie juurde?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 37, 'Kui keegi arvab enese olevat prohveti või Vaimu mõjualuse, see teadku, et mida ma teile kirjutan, see on Issanda käsk!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 38, 'Aga kui keegi ei taha seda teada, ärgu teadku!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 39, 'Niisiis, vennad, taotlege prohvetlikku kõnelemist ja ärge ka keelake keeltega rääkimist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 40, 'Kõik sündigu viisakalt ja korra järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 1, 'Ent ma juhin teie tähelepanu Evangeeliumile, mida ma teile kuulutasin, mille te ka vastu võtsite ja milles ka püsite');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 2, 'ning milles te ka õndsaks saate - kui te peate seda kinni nende sõnadega, millega mina teile seda kuulutasin - olgu siis, et asjata saite usklikuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 3, 'Sest ma olen teile kõigepealt teada andnud, mida ma olen saanud, et Kristus suri meie pattude eest kirjade järgi');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 4, 'ja et Ta maeti ja et Ta üles äratati kolmandal päeval kirjade järgi');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 5, 'ja et Ta ilmus Keefasele, pärast seda neile kaheteistkümnele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 6, 'Pärast Ta ilmus ühekorraga rohkem kui viiesajale vennale, kellest suurem hulk veel praegugi on elus, aga mõned on läinud magama;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 7, 'pärast seda Ta ilmus Jakoobusele, siis kõigile Apostlitele;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 8, 'aga pärast kõike Ta ilmus ka minule kui äbarikule.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 9, 'Sest mina olen Apostlite seast kõige väiksem, kes ei ole väärt, et mind Apostliks hüütakse, sest ma kiusasin taga Jumala kogudust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 10, 'Aga Jumala armust olen mina, mis ma olen; ja see arm minu vastu ei ole olnud tühine, vaid ma olen palju rohkem tööd teinud kui nemad kõik; aga mitte mina, vaid Jumala arm, mis on minuga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 11, 'Olgu nüüd mina või olgu nemad, nõnda me kuulutame ja nõnda olete saanud usklikuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 12, 'Aga kui Kristusest kuulutatakse, et Ta on surnuist üles äratatud, kuidas siis mõned teie seast ütlevad, et ei olevatki surnute ülestõusmist?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 13, 'Aga kui ei ole surnute ülestõusmist, siis ei ole ka Kristus üles äratatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 14, 'Ent kui Kristus mitte ei ole üles äratatud, siis on meie jutlus tühine ja tühine on ka teie usk;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 15, 'ja meid leitakse siis Jumala valetunnistajad olevat, sest me oleme tunnistanud Jumala vastu, et Ta on üles äratanud Kristuse, Keda Tema pole äratanud, kui surnuid üles ei äratata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 16, 'Sest kui surnuid ei äratata, siis ei ole ka Kristust äratatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 17, 'Aga kui Kristust ei ole äratatud, siis on teie usk tühine ja te olete alles oma pattude sees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 18, 'Siis on ka need, kes Kristuse sees on läinud hingama, hukka saanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 19, 'Kui meie selles elus oleme lootnud ainult Kristuse peale, siis me oleme kõigist inimestest kõige armetumad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 20, 'Aga nüüd on Kristus surnuist üles äratatud ja on saanud esmaseks nende seast, kes on läinud hingama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 21, 'Sest kui juba surm on tulnud inimese kaudu, siis on ka surnute ülestõusmine inimese kaudu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 22, 'Sest nõnda nagu kõik surevad Aadamas, nõnda tehakse ka kõik elavaiks Kristuses,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 23, 'aga igaüks oma järjekorras: esimesena Kristus, selle järel Kristuse Omad Tema tulemises;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 24, 'siis tuleb ots, kui Ta annab Riigi Jumala ja Isa kätte pärast seda, kui Ta on hävitanud kõik valitsuse ja kõik meelevalla ja võimu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 25, 'Sest Tema peab valitsema, kuni Ta on pannud kõik vaenlased Tema jalge alla.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 26, 'Viimne vaenlane, kellele ots tehakse, on surm!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 27, 'Sest \"ta on kõik alistanud Tema jalge alla!\" Kui Ta aga ütleb, et kõik on alistatud Tema alla, siis on avalik, et ei ole alistatud See, Kes kõik Temale alistas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 28, 'Aga kui kõik Temale on alistatud, siis peab ka Poeg Ise alistatama Sellele, Kes kõik Temale on alistanud, et Jumal oleks kõik kõigis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 29, 'Mis teevad muidu need, kes endid lasevad ristida surnute eest, kui surnuid koguni ei äratata? Mispärast siis neid ristitakse nende eest?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 30, 'Ja mispärast oleme meiegi hädaohus igal hetkel?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 31, 'Iga päev ma olen surmasuus, nii tõesti kui teie, vennad, olete minu kiitus, mis mul on Kristuses Jeesuses, meie Issandas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 32, 'Kui ma inimeste kombel olen Efesoses võidelnud metsalistega, siis mis kasu on mul sellest? Kui surnuid ei äratata, siis \"söögem ja joogem, sest homme me sureme!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 33, 'Ärge eksige! \"Kurjad kõned rikkuda võivad kombeid häid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 34, 'Kainestuge õieti ja ärge tehke pattu; sest mõned ei tea midagi Jumalast; seda ma ütlen teile häbiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 35, 'Aga mõni küsib: \"Kuidas surnud ärkavad üles? Ja millise ihuga nad tulevad?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 36, 'Rumal! See, mida sa külvad, ei saa elavaks, kui see enne ei sure.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 37, 'Ja mida sa külvad, sa ei külva selle ihuna, mis peab tõusma, vaid paljast iva, olgu nisuiva või mis tahes iva.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 38, 'Aga Jumal annab temale ihu, millise tahab, ja igale seemnele tema oma ihu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 39, 'Kõik liha ei ole sama liha, vaid ise on inimeste liha ja ise veiste liha ja ise lindude ja ise kalade liha;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 40, 'ja on taevalikke ihusid ja maiseid ihusid; aga teistsugune on taevalike auhiilgus ja teistsugune maiste auhiilgus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 41, 'Isesugune on päikese hiilgus ja isesugune kuu hiilgus ja isesugune tähtede hiilgus; sest tähe ja tähe hiilgusel on oma vahe.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 42, 'Nõnda on ka surnute ülestõusmine. Kaduvuses külvatakse, kadumatuses äratatakse üles;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 43, 'autuses külvatakse, auhiilguses äratatakse üles; nõtruses külvatakse, väes äratatakse üles;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 44, 'maine ihu külvatakse, vaimne ihu äratatakse üles; sest kui on olemas maine ihu, siis on ka olemas vaimne ihu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 45, 'Nõnda on ka kirjutatud: \"Esimene inimene Aadam sai elavaks hingeks.\" Viimne Aadam sai vaimuks, kes elustab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 46, 'Aga vaimne ihu ei ole esimene, vaid maine; selle järel on vaimne.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 47, 'Esimene inimene oli maast, muldne, teine Inimene on taevast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 48, 'Milline on muldne, sellised on ka muldsed; ja milline on taevane, sellised on ka taevased.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 49, 'Ja otse nõnda nagu me oleme kandnud muldse inimese kuju, nõnda me kanname kord ka taevast kuju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 50, 'Aga seda ma ütlen, vennad, et liha ja veri ei või pärida Jumala Riiki ega kaduvus pärida kadumatust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 51, 'Vaata, ma ütlen teile saladuse: me kõik ei lähe magama, aga me kõik muutume,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 52, 'äkitselt ühe silmapilguga, viimse pasuna hüüdes. Sest pasun hüüab ja surnud tõusevad üles kadumatutena ja me muutume.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 53, 'Sest see kaduv peab riietuma kadumatusega ja see surev riietub surematusega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 54, 'Aga kui see kaduv riietub kadumatusega ja see surev riietub surematusega, siis saab tõeks sõna, mis on kirjutatud: \"Surm on neelatud võidusse!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 55, 'Surm, kus on sinu võit? Surm, kus on sinu astel?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 56, 'Aga surma astel on patt ja patu vägi on käsk.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 57, 'Ent tänu Jumalale, Kes meile võidu annab meie Issanda Jeesuse Kristuse läbi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 58, 'Sellepärast, mu armsad vennad, olge kindlad, vankumatud ja ikka innukad Issanda töös, teades, et teie vaevanägemine Issandas ei ole asjatu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 1, 'Mis puutub korjandusse pühade heaks, siis tehke teiegi nõnda, nagu ma olen korraldanud Galaatia kogudustes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 2, 'Igal esimesel nädalapäeval pangu igaüks teie seast midagi tallele sedamööda, kuidas temal on jõudu, et mitte alles siis raha ei kogutaks, kui ma tulen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 3, 'Aga kui ma saabun, läkitan ma kirjadega need, keda te arvate kõlbavaks viima teie anni Jeruusalemma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 4, 'Ja kui asi on seda väärt, et minagi läheksin, siis nad tulgu ühes minuga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 5, 'Ent ma mõtlen tulla teie juurde, kui ma olen käinud läbi Makedoonia; sest Makedooniast ma lähen läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 6, 'Teie juures ma ehk viibin mõne aja või vahest kogu talvegi, et te mind siis saadaksite, kuhu ma iganes lähen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 7, 'Sest ma ei taha teid seekord näha mitte ainult mööda minnes, vaid ma loodan jääda tükiks ajaks teie juurde, kui Issand lubab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 8, 'Aga Efesosse ma jään nelipühani,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 9, 'sest mulle on seal avanenud suur uks viljarikkaks tööks ja seal on palju vastaseid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 10, 'Kui Timoteos tuleb, siis katsuge, et ta võiks olla ilma kartuseta teie juures, sest ta teeb Issanda tööd nõnda nagu minagi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 11, 'Ärgu siis ükski teda halvaks pangu, vaid saatke ta rahus teele, et ta tuleks minu juurde, sest ma ootan teda ühes vendadega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 12, 'Mis aga puutub vend Apollosesse, siis olen ma temale sageli peale käinud, et ta tuleks teie juurde ühes vendadega, aga tal ei olnud üldse tahtmist nüüd tulla; ta tuleb, kui tal leidub parajat aega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 13, 'Valvake, seiske usus, olge mehed, saage tugevaks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 14, 'Kõik teie asjad sündigu armastuses!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 15, 'Ma palun teid, vennad: teie teate Stefanase peret, et see on Ahhaias esimene pöördunu ja et nemad on andunud pühade abistamisele;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 16, 'olge teiegi sõnakuulelikud niisugustele ja igaühele, kes töötab ühes ning näeb vaeva.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 17, 'Ma rõõmustun Stefanase ja Fortunaatuse ja Ahhaikose tulekust, sest nemad täidavad minule teie aset;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 18, 'nemad on ju jahutanud minu ja teie vaimu. Pidage siis niisugustest lugu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 19, 'Aasia kogudused tervitavad teid. Teid tervitavad Issandas Akvila ja Priska ühes kogudusega nende majas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 20, 'Teid tervitavad kõik vennad. Tervitage üksteist püha suudlusega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 21, 'Tervitus on minu, Pauluse käega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 22, 'Kui keegi Issandat Jeesust Kristust ei pea armsaks, siis olgu ta ära neetud! Maaran ata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 23, 'Issanda Jeesuse Kristuse arm olgu teiega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 24, 'Minu armastus on teie kõikidega Kristuses Jeesuses!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 1, 'Paulus, Kristuse Jeesuse Apostel Jumala tahtest, ja vend Timoteos Korintoses olevale Jumala kogudusele ja kõigile pühadele kogu Ahhaias.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 2, 'Armu teile ja rahu Jumalalt, meie Isalt ja Issandalt Jeesuselt Kristuselt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 3, 'Kiidetud olgu Jumal ja meie Issanda Jeesuse Kristuse Isa, halastuse Isa ja kõige troosti Jumal,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 4, 'Kes meid trööstib kõigis meie viletsusis, et meie võime trööstida neid, kes on kõiksuguses viletsuses, troostiga, millega Jumal meid endid trööstib.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 5, 'Sest otsekui Kristuse kannatamisi tuleb rohkesti meie peale, nõndasamuti tuleb meile rohkesti ka troosti Kristuse kaudu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 6, 'Olgu meil nüüd viletsust, siis sünnib see teie trööstimiseks ja päästmiseks; või kui saame troosti, siis sünnib seegi teie trööstimiseks ja see teostub samade kannatamiste kandmiseks, mida meiegi kannatame;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 7, 'ja meie lootus teie kohta on kindel, sest me teame, et otsekui teil on osa kannatustest, nõnda on teil osa ka troostist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 8, 'Sest me ei taha, vennad, et teil oleks teadmata, missuguses viletsuses me olime Aasias, kuidas meid ülemäära vaevati üle meie jõu, nõnda et me isegi olime kahevahel oma elu pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 9, 'Ja me olime enestegi arvates surma mõistetud, et me ei loodaks eneste peale, vaid Jumala peale, Kes surnud üles äratab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 10, 'Tema päästis meid nii suurest surmast ja päästab veelgi; Tema peale me loodame, et Tema ka edaspidi päästab,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 11, 'kui ühtlasi ka teie appi tulete palves meie eest, et paljude isikute käest meie heaks tulnud armuanni eest tõuseks tänu paljude suust meie eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 12, 'Sest meie kiitlus on see: meie südametunnistuse tõendus, et meie Jumala pühaduses ja puhtuses oleme elanud maailmas, mitte lihalikus tarkuses, vaid Jumala armus, ja iseäranis teie juures.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 13, 'Sest me ei kirjuta teile muud midagi kui aga seda, mida te siin loete või mida te ka tunnetate, ja mida te, nagu ma loodan, veel lõpuni saate tunnetada,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 14, 'nõnda nagu te ka meid olete osalt tundnud, et me oleme teie kiitlus, nagu ka teie olete meie kiitlus meie Issanda Jeesuse päeval.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 15, 'Ja niisuguse usaldusega ma mõtlesin juba ennemini tulla teie juurde, et teie saaksite veel teisegi armuanni osalisteks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 16, 'ja teie kaudu siis matkata Makedooniasse ja Makedooniast tulla tagasi jälle teie juurde ja teilt saada saatjaid Judeasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 17, 'Kui ma nüüd nõnda kavatsesin, kas ma siis toimisin kergemeelselt? Või mis ma kavatsen, kas ma kavatsen liha järgi, selleks, et mu kõnes oleks \"jah\", \"jah\" ja \"ei\", \"ei\"?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 18, 'Kuid Jumal on ustav, nii et meie kõne teie vastu ei ole \"jah\" ja \"ei\" ühtlasi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 19, 'Sest Jumala Poeg Jeesus Kristus, Keda meie, mina, Silvaanus ja Timoteos, oleme teile kuulutanud, ei olnud mitte \"jah\" ja \"ei\", vaid Temas sai tõeks \"jah\".');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 20, 'Sest Jumala tõotused puha on Tema sees \"jah\" ja sellepärast ka Tema läbi \"aamen\" Jumala auks meie läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 21, 'Aga kes meid ühes teiega kinnitab Kristusesse ja kes meid on võidnud, See on Jumal,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 22, 'Kes meid ka on kinnitanud pitseriga ja on andnud Vaimu pandi meie südamesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 23, 'Aga ma kutsun Jumala tunnistajaks oma hinge peale, et ma tahtes teid säästa, ei ole veel tulnud Korintosesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 24, 'Ei mitte, et me tahame olla valitsejad teie usu üle, vaid me oleme teie rõõmu kaastegelased; sest teie püsite usus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 1, 'Aga ma olin oma mõttes otsustanud mitte tulla jälle teie juurde kurvastusega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 2, 'Sest kui mina teid kurvastan, kes on siis, kes mind rõõmustab, kui vaid see, kelle ma olen teinud kurvaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 3, 'Ja ma olen kirjutanud teile seda otse selleks, et mulle teie juurde tulles ei saaks osaks kurvastust neilt, kes mind pidid rõõmustama; sest mul on teist kõigist see lootus, et minu rõõm on teie kõikide rõõm.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 4, 'Sest ma kirjutasin teile suures viletsuses ja südame ahastuses paljude silmapisaratega, ei mitte selleks, et te kurvastuksite, vaid et te tunneksite selle armastuse, mida mul on rohkesti teie vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 5, 'Aga kui keegi on kurvastanud, ei ole ta mind kurvastanud, vaid teataval määral, et mitte liialdada, teid kõiki.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 6, 'Niisugusel on küllalt sellest noomitusest, mille ta on saanud paljude poolt,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 7, 'nõnda et teie hoopis vastupidi peate temale andeks andma ja teda kinnitama, et teda veel suurem kurvastus ära ei neelaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 8, 'Sellepärast ma manitsen teid osutada temale armastust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 9, 'Sest selleks ma olengi kirjutanud, et teada saada teie meelt, kas te olete igapidi sõnakuulelikud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 10, 'Aga kellele te midagi andeks annate, sellele ka mina annan andeks. Sest ka mina, mis ma andeks andsin, kui mul oli midagi andeks anda, andsin andeks teie pärast Kristuse palge ees,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 11, 'et saatan meid kavalasti ei petaks, sest tema mõtted ei ole meile teadmatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 12, 'Aga kui ma tulin Troasse kuulutama Kristuse Evangeeliumi ja mulle oli uks avatud Issandas,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 13, 'ei leidnud ma siiski mitte rahu oma vaimus, sest ma ei kohanud oma venda Tiitust. Ma jätsin nad siis jumalaga ja läksin Makedooniasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 14, 'Aga tänu Jumalale, kes meile ikka annab võimust Kristuses ja teeb avalikuks oma tunnetuse lõhna meie kaudu kõigis paigus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 15, 'Sest meie oleme Jumalale Kristuse hea lõhn niihästi nende seas, kes päästetakse, kui ka nende seas, kes hukka lähevad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 16, 'ühtedele küll surmalehk surmaks, kuid teistele elulõhn eluks. Ja kes kõlbab selleks?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 17, 'Sest me ei ole nagu mitmed, kes Jumala sõnaga hangeldavad, vaid puhtast meelest, jah, otsekui Jumalast, me kõneleme Kristuses Jumala ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 1, 'Kas hakkame siis jälle iseendid soovitama? Või kas vajame, nagu mõningad, soovituskirju teie kätte või teie käest?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 2, 'Teie olete meie kiri, kirjutatud meie südamesse, kõigile inimestele tuttav ja loetav,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 3, 'sest teist on ju ilmsi näha, et te olete Kristuse kiri, mis meie teenimistöö läbi on valminud, kirjutatud mitte tindiga, vaid elava Jumala Vaimuga, ka mitte kivilauakestele, vaid lihastele südamelauakestele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 4, 'Aga niisugune lootus on meil Kristuse läbi Jumala peale,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 5, 'mitte nii, et me oleksime iseenestest võimelised midagi mõtlema, nagu iseenesest, vaid meie võime tuleb Jumalalt,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 6, 'Kes meid ka on teinud võimeliseks olema Uue Lepingu sulased, mitte kirjatähe, vaid Vaimu sulased; sest kirjatäht suretab, aga Vaim teeb elavaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 7, 'Aga kui surma amet, mis kirjatähtedega oli uuristatud kividesse, esines nii suures aus, et Iisraeli lapsed ei suutnud vaadata Moosese palgesse ta palge hiilguse pärast, mis ometi oli kaduv,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 8, 'kuidas ei peaks palju rohkem Vaimu amet olema au sees?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 9, 'Sest kui hukkamõistmise ametil oli au, siis on õiguse amet veel palju suurem au poolest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 10, 'Sest too, mis oli enne au sees, ei olegi enam niisugusel määral au sees selle üliväga suure au pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 11, 'Sest kui see, mis on kaduv, on tekkinud au läbi, kui palju rohkem on siis au sees see, mis on püsiv.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 12, 'Et meil nüüd on niisugune lootus, siis me räägime täie julgusega');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 13, 'ega tee nõnda nagu Mooses, kes pani katte oma palgele, et Iisraeli lapsed ei näeks selle lõppu, mis on kaduv.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 14, 'Ent nende meeled on paadunud; sest veel tänapäevalgi jääb sama kate Vana Testamendi lugemisel ära võtmata, sest alles Kristuses see kaob.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 15, 'Veel tänapäevalgi, kui Moosest loetakse, on kate nende südame peal;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 16, 'aga kui nende süda pöördub Issanda poole, siis kate võetakse ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 17, 'Sest Issand on Vaim, ja kus on Issanda Vaim, seal on vabadus!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 18, 'Aga me kõik, kes katmatu palgega vaatleme Issanda auhiilgust otsekui peeglis, muutume samasuguseks kujuks aust ausse nagu Issanda Vaimust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 1, 'Sellepärast et meil on niisugune amet selle halastuse järgi, mis meile on osaks saanud, siis me ei lähe araks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 2, 'vaid me oleme loobunud salajastest häbitegudest ega ela tigedas kavaluses ega võltsi Jumala sõna, vaid tõe ilmsikstoomisega me asetume kõigi inimeste südametunnistuse otsuse ette Jumala ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 3, 'Kui nüüd ka meie Evangeelium on kinni kaetud, siis on see kinni kaetud nende eest, kes hukka lähevad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 4, 'kelle uskmatud meeled selle maailma jumal on teinud sõgedaks, et neile ei paistaks Kristuse au Evangeeliumi valgus, Kes on Jumala kuju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 5, 'Sest me ei kuuluta mitte iseendid, vaid Kristust Jeesust, et Tema on Issand ja meie teie orjad Jeesuse pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 6, 'Sest Jumal, Kes ütles: \"Paistku valgus pimedusest!\", on See, Kes on hakanud paistma meie südametes, et tekiks tunnetuse valgus Jeesuse Kristuse isikus olevast Jumala aust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 7, 'Aga meil on see aare saviriistades, et üliväga suur vägi oleks Jumala poolt ja mitte meist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 8, 'Meid rõhutakse kõikepidi, kuid ei suruta maha; me oleme nõutud, aga me ei heida meelt;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 9, 'me oleme taga kiusatud, aga me ei ole hüljatud; me oleme maha vajutatud, aga me ei hukku.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 10, 'Me kanname alati enestega Issanda Jeesuse surma oma ihus, et ka Jeesuse elu meie ihus tuleks ilmsiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 11, 'Sest meid, kes elame, antakse alatasa surma Issanda Jeesuse pärast, et ka Jeesuse elu meie surelikus lihas saaks avalikuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 12, 'Nõnda siis on surm vägev meie sees, aga elu teie sees!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 13, 'Aga et meil on seesama usu Vaim sedamööda, nagu on kirjutatud: \"Ma usun, sellepärast ma räägin!\", siis meiegi usume ja sellepärast me ka räägime,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 14, 'teades, et See, Kes üles äratas Issanda Jeesuse, ka meid üles äratab ühes Jeesusega ja seab ühes teiega Enese ette.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 15, 'Sest kõik sünnib teie pärast, et üha rohkemate kaudu üliväga suureks saav arm valmistaks ikka rohkemat tänu Jumala austuseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 16, 'Sellepärast me ei loidu, vaid kuigi meie väline inimene kõduneb, uueneb sisemine inimene ometi päev-päevalt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 17, 'Sest see silmapilkne kerge viletsus saavutab meile määratu suure ja rohke igavese au,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 18, 'meile, kes me ei vaata nähtavaile, vaid nähtamatuile asjadele; sest nähtavad asjad on ajalikud, nähtamatud aga on igavesed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 1, 'Sest me teame, et kui meie maine telkhoone maha kistakse, on meil elamu Jumala käest, mitte kätega tehtud igavene hoone taevas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 2, 'Sellepärast me ka ohkame igatsedes, et kaetud saada taevase eluasemega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 3, 'ometi nõnda, et meid kaetult kord ei leitaks alasti olevat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 4, 'Sest meiegi, kes elame selles telgis, ohkame olles koorma all, sellepärast et me ei taha kattest vabaneda, vaid kaetud olla, et elu neelaks ära selle, mis on surelik.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 5, 'Aga meid on sellesama tarvis valmistanud Jumal, Kes meile on andnud Vaimu pandi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 6, 'Sellepärast me oleme julged igal ajal ja teame, et niikaua kui me kodus oleme ihus, oleme Issanda juurest ära;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 7, 'sest me käime usus ja mitte nägemises;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 8, 'aga me oleme julges meeles ja himustaksime ennemini ära olla ihust ja kodus olla Issanda juures.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 9, 'Sellepärast me ka püüame olla Temale meelepärased, kas oleme kodus või võõrsil.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 10, 'Sest me kõik peame saama ilmsiks Kristuse kohtujärje ees, et igaühele tasutaks vastavalt sellele, mis ta ihus olles tegi, olgu see hea või kuri!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 11, 'Et me nüüd teame, mis on Issanda kartus, siis me meelitame inimesi uskuma, aga Jumalale me oleme tuntud, ja ma loodan, et me ka teie südametunnistuses oleme tuntud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 12, 'Me ei soovita jälle endid teie ees, vaid me anname teile põhjust kiidelda meist, et teil oleks millega kiidelda nende ees, kes kiitlevad sellega, mis on silma ees, aga mitte sellega, mis on südames.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 13, 'Sest kui me oleme olnud arust ära, siis oleme seda olnud Jumalale; ja kui me oleme selge meelega, siis teile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 14, 'Sest Kristuse armastus sunnib meid ning me otsustame nõnda: kui üks on surnud kõikide eest, siis on kõik surnud;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 15, 'ja Ta on surnud kõikide eest, et need, kes elavad, ei elaks enam enestele, vaid Temale, kes nende eest on surnud ja üles tõusnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 16, 'Sellepärast me ei tunne praegusest ajast peale kedagi liha poolest; ja kui me ka oleme tundnud Kristust liha poolest, siis me ei tunne Teda nüüd mitte enam nõnda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 17, 'Niisiis: kui keegi on Kristuses, siis ta on uus loodu; vana on möödunud, vaata, uus on tekkinud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 18, 'Aga kõik on Jumalast, kes meid on Iseenesega lepitanud Kristuse läbi ja on meile andnud lepitusameti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 19, 'Sest Jumal oli Kristuses ja lepitas maailma Iseenesega ega arvanud neile nende üleastumisi süüks ja on pannud meie sisse lepitussõna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 20, 'Seepärast me oleme nüüd käskjalad Kristuse asemel, otsekui manitseks Jumal meie läbi. Me palume Kristuse asemel: andke endid lepitada Jumalaga!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 21, 'Ta on Tema, Kes ei teadnud mingist patust, meie eest teinud patuks, et meie saaksime Jumala õiguseks Tema sees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 1, 'Aga olles kaastegevad me ka manitseme, et te Jumala armu ilmaasjata vastu ei võtaks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 2, 'Sest Ta ütleb: \"Ma olen sind kuulnud soodsal ajal ja aidanud päästepäeval!\" Näe, nüüd on hästi soodus aeg, vaata, nüüd on päästepäev!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 3, 'Me ei anna üheski asjas mingit põhjust pahanduseks, et meie ametit ei laidetaks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 4, 'vaid kõiges me näitame endid kui Jumala abilised: suures kannatuses, viletsustes, hädades, kitsikustes,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 5, 'hoopide all, vangis, mässudes, vaevanägemistes, valvamistes, paastumistes,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 6, 'puhtuses, tunnetuses, pikas meeles, helduses, Pühas Vaimus, silmakirjatsematus armastuses,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 7, 'tõe sõnas, Jumala väes, õiguse sõjariistade varal paremas ja vasakus käes,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 8, 'aus ja häbis, kurja kõne all ja hea all, kui eksitajad ja ometi tõtt rääkijad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 9, 'kui tundmatud ja ometi küll tuntud, kui surijad, ja vaata, me elame; kui karistatud, kuid mitte surmatud;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 10, 'kui kurvastatud, kuid ikka rõõmsad; kui vaesed, kes aga teevad paljusid rikkaks; kui need, kellel ei ole midagi ja kelle päralt on kõik!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 11, 'Meie suu on avatud teie vastu, korintlased, meie süda on avardunud;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 12, 'meie sees ei ole teil kitsas, vaid teil on kitsas teie omas südames.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 13, 'Vastutasuks tehke sedasama - ma ütlen teile kui lastele - avardage ka teie oma süda!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 14, 'Ärge hakake võõras ikkes vedama ühes uskmatutega; sest mis on ühist õigusel ülekohtuga? Mis on ühist valgusel pimedusega?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 15, 'Kuidas sobib Kristus ühte Beliariga? Või mis osa on usklikul uskmatuga?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 16, 'Kuidas sünnib Jumala tempel ühte ebajumalatega? Sest meie oleme elava Jumala tempel, nõnda nagu Jumal on öelnud: \"Ma elan nende sees ja käin ja olen nende Jumal, ja nad peavad olema Mu rahvas!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 17, 'Sellepärast \"minge ära nende keskelt ja eralduge neist\", ütleb Issand, \"ja ärge puudutage roojast, siis Ma võtan teid vastu');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 18, 'ja olen teile Isaks, ja teie olete Mulle poegadeks ja tütardeks\", ütleb Kõigeväeline Issand.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 1, 'Et meil nüüd on niisugused tõotused, mu armsad, siis puhastagem endid kõigest liha ja vaimu rüvedusest ja tehkem täielikuks oma pühitsus Jumala kartuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 2, 'Andke meile maad oma südames! Me ei ole teinud kellelegi ülekohut, ei ole kellelegi saatnud kahju, ei ole kedagi petnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 3, 'Seda ma ei ütle teie hukkamõistmiseks; sest mina olen juba öelnud, et te olete meie südames, et me ühes sureksime ja ühes elaksime.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 4, 'Mul on palju lootust teie suhtes, mul on teist palju kiitlemist, ma olen täis troosti; ma olen üliväga rõõmus kõiges meie kitsikuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 5, 'Sest ka Makedooniasse saabudes ei olnud meie lihal mingit rahu, vaid meid vaevati kõikepidi; väljaspool oli võitlusi ja seespool kartust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 6, 'Aga Jumal, kes masendatuid kinnitab, on meid kinnitanud Tiituse tulemisega;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 7, 'kuid mitte ainult tema tulemisega, vaid ka selle troostiga, mille ta oli teilt saanud, sest ta jutustas meile teie igatsemisest, teie kurtmisest, teie innust minu heaks, nõnda et ma rõõmustusin veel enam.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 8, 'Sest kuigi ma teid kirjaga kurvastasin, ei kahetse ma seda mitte; ja kuigi ma kahetsesin - ma näen ju, et see kiri teid on kurvastanud, olgugi lühikeseks ajaks -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 9, 'siis olen nüüd rõõmus, mitte sellest, et kurvastusite, vaid et kurvastusite meeleparanduseks; sest te kurvastusite Jumala meele järgi, et teil ei oleks mingit kahju meilt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 10, 'Sest kurvastus Jumala meele järgi saavutab meeleparanduse, mis toob pääste, mida ei kahetseta; aga maailma kurvastus toob surma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 11, 'Sest vaadake, kuidas otse see, et te saite kurvaks Jumala meele järgi, on teis tekitanud millise hoole, millise vabandamise, millise meelepaha, millise kartuse, millise igatsemise, millise innukuse, millise karistuse! Te olete kõikepidi osutanud endid puhtaiks selles asjas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 12, 'Nõnda siis: kuigi ma teile kirjutasin, siis ei sündinud see mitte tema pärast, kes tegi paha, ega tema pärast, kellele tehti paha, vaid selleks, et teie hool meie heaks saaks avalikuks teie keskel Jumala ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 13, 'Selle poolest oleme trööstitud. Oma troostiks me rõõmustusime aga veel rohkem Tiituse rõõmust, et tema vaim kosutust on saanud teie kõikide poolt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 14, 'Sest kui ma temale teist midagi olen kiidelnud, ei ole ma jäänud häbisse, vaid otsekui me teile oleme rääkinud tervet tõtt, nõnda on ka meie kiitlemine teist Tiitusele osutunud tõeks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 15, 'Ja tema süda on seda rohkem teie poole, kui ta meenutab teie kõikide sõnakuulelikkust, kuidas te kartuse ja väristusega võtsite ta vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 16, 'Olen rõõmus, et ma kõiges võin olla julge teie suhtes!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 1, 'Aga me teeme teile, vennad, teatavaks armu, mille Jumal on andnud Makedoonia kogudustele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 2, 'et neil keset suurt kitsikuse katsumist oli rõõmu ülikülluses ja nende põhjatu vaesus kasvas nende südamlikkuse rikkuseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 3, 'Sest nad on andnud jõudu mööda - ma tunnistan seda, koguni üle jõu - ja seda vabatahtlikult,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 4, 'käies meile kangesti peale anumisega võtta vastu see and ja lubada neid osa võtta pühade abistamisest;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 5, 'ja mitte ainult nõnda nagu lootsime, vaid nad andsid iseendidki esiti Issandale, siis meile, Jumala tahtel,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 6, 'nii et me õhutasime Tiitust ka teie keskel selle anni korjamist nõnda lõpetama, nagu ta seda juba oli alustanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 7, 'Aga otsekui te kõiges olete rikkad, usus ja sõnas ja tunnetuses ja kõiksuguses usinuses ja armastuses meie poolt teie vastu, nõnda olge helde käega selleski armastustöös.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 8, 'Ma ei ütle seda käskides, vaid seades teiste usinust teile eeskujuks, ma tahaksin teada saada, kas teiegi armastus on tõsine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 9, 'Sest te tunnete meie Issanda Jeesuse Kristuse armu, et Tema, kuigi Ta oli rikas, sai vaeseks teie pärast, et teie Tema vaesusest saaksite rikkaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 10, 'Selle asja poolest ma annan nõu; sest see tuleb kasuks teile, kes juba mullu olite esimesed mitte ainult tegema, vaid ka tahtma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 11, 'Aga nüüd viige ka täide oma tegemine, et nõnda nagu teil oli himu tahta, nõnda te ka lõpetaksite oma teo, sedamööda kuidas teil jõudu on.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 12, 'Sest kui on head tahtmist anda, siis on see vastuvõetav sel määral kuidas jõud kannab, mitte üle jõu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 13, 'Sest ei ole vaja, et mis teistele on kergenduseks, teile oleks koormaks, vaid tasakaalustuseks');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 14, 'nende puudusele olgu see, mida teil praegusel ajal on ülearu, et ka see, mida neil on ülearu, aitaks teie puudust ja nõnda sünniks tasakaal,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 15, 'nagu on kirjutatud: \"Sellel, kel oli palju, ei olnud ülearu, ja kel oli pisut, ei olnud puudust!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 16, 'Aga tänu olgu Jumalale, Kes niisuguse hoole teie heaks andis Tiituse südamesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 17, 'Sest ta võttis kuulda manitsust; ent ta enese hool oli veel suurem ja ta läks omast tahtest teele teie juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 18, 'Me läkitasime ühes temaga venna, kelle kuulsus Evangeeliumi kuulutamises on tuttav kõigis kogudustes;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 19, 'ja mitte ainult seda, vaid ta on ka koguduste poolt valitud meie teekonna kaaslaseks kohale viima seda armastuseandi, mida meie kogume, Issanda Enese auks ja meie hea tahte tunnuseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 20, 'Nõnda me hoidume selle eest, et ükski meid ei laimaks selle rohke anni suhtes, mida me teostame.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 21, 'Sest me hoolitseme selle eest, mis on hea, mitte ainult Issanda ees, vaid ka inimeste ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 22, 'Me aga läkitasime ühes nendega veel oma venna, kelle innukust me oleme sageli saanud tunda paljudes asjades ja kes on nüüd veel palju innukam selle suure usalduse tõttu, mis tal teie vastu on.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 23, 'Mis Tiitusesse puutub, siis on ta minu seltsimees ja kaastööline teie juures; mis puutub meie vendadesse, siis on nemad koguduste Apostlid ja Kristuse au.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 24, 'Niisiis andke neile koguduste ees tõestus oma armastusest ja sellest, et meie kiitlemine teie kohta on tõsi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 1, 'Pühade abistamisest aga on mul tõesti ülearune teile kirjutada;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 2, 'sest ma tean teie head tahet ning kiitlen teist sellega makedoonlaste ees, et Ahhaia juba mullu on olnud valmis ja et teie innukus on paljusid õhutanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 3, 'Aga ma läkitasin need vennad, et meie kiitlemine teist selles asjas ei läheks tühja, vaid te oleksite valmis, nagu ma olen rääkinud;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 4, 'muidu, kui makedoonlasi peaks tulema ühes minuga ja nad ei leiaks teid valmis olevatena, meie - või kas pean ütlema \"teie\" - jääksime häbisse oma lootuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 5, 'Olen siis arvanud tarvilikuks õhutada vendi minema teele teie poole ette valmistama teie varemini lubatud tänuandi, et see oleks valmis tänuna ja mitte ihnutsemisena.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 6, 'Aga pange tähele: kes kasinasti külvab, see lõikab ka kasinasti, ja kes rohkesti külvab, see lõikab ka rohkesti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 7, 'Igaüks andku nõnda, kuidas süda kutsub, mitte kurva meelega ega sunniviisil, sest Jumal armastab rõõmsat andjat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 8, 'Aga Jumal on vägev teile andma kõike armu rohkesti, et teil ikka oleks kõike igati küllaldaselt ning oleksite rikkad iga hea teo tarvis,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 9, 'nõnda nagu on kirjutatud: \"Ta puistab välja, Ta jagab vaestele, Tema õigus püsib igavesti!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 10, 'Aga Tema, Kes annab seemet külvajale, annab ka leiba toiduseks ja rohkendab teie külvi ja kasvatab teie õiguse vilja,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 11, 'nõnda et te, saades rikkaks kõige poolest, võiksite helde südamega teha kõike head, mis meie läbi valmistab tänu Jumalale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 12, 'Sest selle anni kaudu antud abi ei kõrvalda mitte ainult pühade puudust, vaid toob ka rohket õnnistust selle läbi, et paljud tänavad Jumalat');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 13, 'ja selle abistamise tõestuse tõttu ülistavad Jumalat selle eest, et te nii allaheitlikult tunnistate Kristuse Evangeeliumi ja nii helde südamega teete andide osalisteks neid ja kõiki,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 14, 'ja oma palvetes teie eest nad avaldavad igatsust teie järele Jumala üliväga suure armu tõttu, mis teile on osaks saanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 15, 'Tänu olgu Jumalale Tema ütlematu suure anni eest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 1, 'Aga mina, Paulus, manitsen teid isiklikult Kristuse tasase ja järeleandliku meele pärast, kes ma teie silma ees olles küll olen alandlik, kuid ära olles julge teie vastu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 2, 'ja palun, et mul ei oleks tarvis teie juures viibides südi olla selle julgusega, millega ma mõtlen südikas olla nende vastu, kes meist arvavad, nagu käiksime meie liha järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 3, 'Sest kuigi me käime lihas, ei sõdi me mitte liha viisi järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 4, 'Sest meie võitluse relvad ei ole lihalikud, vaid nad on vägevad Jumala ees maha lõhkuma kindlustusi, hävitades kõik mõistuse targutused.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 5, 'Ja me lükkame ümber kõik kõrgistused, mis tõstetakse Jumala tunnetuse vastu, võttes vangi iga mõtte Kristuse sõnakuulelikkuse alla');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 6, 'ja olles valmis nuhtlema kõike sõnakuulmatust, kui teie sõnakuulelikkus enne on saanud täielikuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 7, 'Vaadake õieti seda, mis on silme ees! Kui keegi on eneses veendunud, et ta on Kristuse päralt, siis ta mõelgu iseeneses ka seda, et otsekui tema on Kristuse päralt, nõnda oleme ka meie.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 8, 'Sest kui ma ka rohkem hakkaksin kiitlema meie meelevallast, mis Issand on andnud teie ehitamiseks ja mitte mahalõhkumiseks, siis ma ei jääks mitte häbisse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 9, 'et ei näiks, nagu tahaksin ma teid hirmutada kirjadega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 10, 'Sest öeldakse: \"Tema kirjad on küll ranged ja võimsad, kuid ihulikult meie juures olles on ta nõrk ja ta kõne on kõhn!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 11, 'Nõnda ütleja mõtelgu, et millised me oleme eemal olles kirjade kaudu sõnadelt, sellised me oleme ka teie juures olles tegudelt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 12, 'Sest me ei julge arvata iseendid mõningate sekka või võrrelda nendega, kes iseendid soovitavad; ent nad ei saa aru, et nad endid mõõdavad iseenestega ja võrdlevad iseenestega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 13, 'Aga me ei taha ennast kiita ülemäära, vaid seda mõõdupuud mööda, mille Jumal meile on mõõduks määranud, et me ulatuksime ka teieni.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 14, 'Sest me ei pinguta ennast liialt, nagu ei saakski me teie juurde, sest me oleme juba ulatunud teieni Kristuse Evangeeliumiga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 15, 'Me ei kiida ennast ülemäära võõra vaevaga, vaid meil on lootus, et teie usu kasvades ka meie oma mõõdupuule vastavalt saame üliväga suureks teie seas,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 16, 'et me neisse maadesse, mis on teist tagapool, saaksime viia Evangeeliumi ega kiitleks sellega, mis on korda saadetud võõral tegevusalal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 17, 'Aga kes kiitleb, see kiidelgu Issandast!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 18, 'Sest mitte see ei ole kõlvuline, kes ennast ise soovitab, vaid keda Issand soovitab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 1, 'Oh, et te minult ometi salliksite natuke rumalust! Küllap te seda sallitegi ka minult!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 2, 'Sest ma olen armukade teie pärast jumaliku armukadedusega; olen ma ju teid kihlanud üheainsale mehele, Kristusele, et teid esitada Temale kui puhast neitsit.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 3, 'Aga mina kardan, et nõnda nagu madu pettis Eevat oma kavalusega, vahest teiegi mõtted rikutakse ja need loobuvad siirast meelest ja kasinusest Kristuse suhtes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 4, 'Sest kui keegi tuleb ja kuulutab mingisugust teist Jeesust, keda meie ei ole kuulutanud, või kui te saate teise vaimu, keda te ei ole saanud, või teise Evangeeliumi kui see, mille te olete vastu võtnud, siis te seda küll salliksite.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 5, 'Sest ma ei arvanud ennast sugugi vähema olevat neist ülisuurtest Apostlitest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 6, 'Aga ehk ma küll olen õppimatu kõnelt, ei ole ma seda siiski mitte tunnetuselt, vaid me oleme kõikepidi seda kõigis asjus teile avalikuks teinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 7, 'Või olen ma teinud pattu sellega, et ma ennast alandasin, et teid ülendataks, ja teile ilma palgata kuulutasin Jumala Evangeeliumi?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 8, 'Teisi kogudusi ma riisusin, neilt palka võttes, et teid teenida,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 9, 'ja kui mul oli puudust teie juures olles, ei olnud ma ühelegi vaevaks. Sest mis mul puudus, selle tasusid vennad, kes tulid Makedooniast, ja mingeis asjus ma ei tahtnud olla teile koormaks ega taha nüüdki.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 10, 'Nii tõesti kui Kristuse tõde on minus, ei tõkestata seda mu kiitlemist Ahhaiamaa kohtades.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 11, 'Miks? Kas sellepärast, et ma ei armasta teid? Jumal teab seda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 12, 'Aga mida ma teen, tahan ma teha veelgi, et ära lõigata nende põhjust, kes otsivad põhjust, et neid leitaks neis asjus, millega nad kiitlevad, olevat samasugused nagu meie.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 13, 'Sest need inimesed on valeapostlid, petised töötegijad, kes endid moondavad Kristuse Apostleiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 14, 'Ja see ei ole ime; sest saatan ise moondab ennast valguseingliks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 15, 'Sellepärast ei ole suur asi, kui ka tema abilised endid moondavad õiguse abilisteks. Ent nende ots on nende tegude järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 16, 'Veel ma ütlen: ükski ärgu arvaku mind rumala olevat; aga kui ometi, siis võtke mind vastu kui rumalat, et ka mina võiksin pisut kiidelda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 17, 'Mida ma nüüd räägin, seda ma ei räägi Issanda nõu järgi, vaid otsekui rumaluses, selles kindlas arvamises, et mul on asja kiitlemiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 18, 'Kuna paljud kiitlevad liha poolest, siis kiitlen minagi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 19, 'Sest te sallite rumalaid heal meelel, olles ise mõistlikud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 20, 'Sest te sallite, kui keegi teid orjastab, kui keegi teid paljaks sööb, kui keegi teid koorib, kui keegi on ülbe, kui keegi lööb teile vastu silmi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 21, 'Häbi pärast ma ütlen, et meie nagu oleksime olnud nõdrad. Aga mille üle keegi uhke on - ma räägin rumalusest - selle üle olen ka mina uhke.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 22, 'Nemad on heebrea mehed, mina ka; nemad on Iisraeli lapsed, mina ka; nemad on Aabrahami sugu, mina ka.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 23, 'Nemad on Kristuse teenijad - ma räägin pööraselt - mina olen rohkem. Ma olen rohkem näinud vaeva, olen rohkem olnud vangis, olen palju rohkem saanud hoope, olen sagedasti olnud surmahädas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 24, 'Juutide käest ma olen saanud viis korda ühe hoobi vähem kui nelikümmend.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 25, 'Kolm korda on mind vitstega pekstud, üks kord kividega visatud, kolm korda on laev hukka läinud, terve öö ja päeva ma olen olnud mere sügavuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 26, 'Sagedasti olen ma oma teekondadel hädaohus olnud jõgedel, hädas mõrtsukate käes, hädas oma rahva seas, hädas paganate keskel, hädas linnas, hädas kõrbes, hädas merel, hädas valevendade seas,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 27, 'töös ning vaevas; sagedasti valvamises, näljas ja janus, sagedasti paastumistes, külmas ja alastiolekus;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 28, 'peale muude asjade igapäevane rahva kokkuvool, mure kõigi koguduste eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 29, 'Kes on nõder, ja mina ei peaks jääma nõdraks? Kes pahandab, ja minu hing ei peaks süttima?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 30, 'Kui tuleb kiidelda, siis ma kiitlen oma nõtrusest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 31, 'Jumal ja meie Issanda Jeesuse Kristuse Isa, Kes on igavesti kiidetud, teab, et ma ei valeta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 32, 'Damaskuses valvas kuningas Aretase pealik damasklaste linna ja tahtis mind kinni võtta,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 33, 'ja mind lasti aknaaugust korviga üle müüri alla ja nõnda ma pääsesin tema käest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 1, 'Tuleb kiidelda, ehk mul sellest küll pole kasu. Aga nüüd ma siirdun nägemustesse ja Issanda ilmutustesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 2, 'Ma tunnen inimest Kristuses, keda neljateistkümne aasta eest tõmmati kolmanda taevani - kas ta oli ihus, seda ma ei tea, või kas ta oli ihust väljas, mina ei tea, Jumal teab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 3, 'Ja ma tean, et sama inimene - kas ta oli ihus või ihust lahus, mina ei tea, Jumal teab -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 4, 'tõmmati paradiisi ja ta kuulis räägitamatuid sõnu, mida inimene ei tohi rääkida.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 5, 'Sellestsamast mehest ma tahangi kiidelda; aga iseenesest ma ei kiitle muuga kui oma nõtrustega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 6, 'Sest kui ma tahaksingi kiidelda, ei oleks ma rumal, sest ma räägiksin tõtt. Aga ma keeldun sellest, et keegi ei arvaks minust rohkem kui ta mind näeb olevat, või mida ta minust kuuleb');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 7, 'ka väga suurte ilmutuste tõttu. Sellepärast et ma ei läheks kõrgiks, on mulle liha sisse antud vai, saatana ingel, mind rusikaga lööma, et ma ei läheks kõrgiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 8, 'Sellesama pärast olen ma kolm korda Issandat palunud, et see minust lahkuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 9, 'Aga Ta ütles mulle: \"Sulle saab küllalt Minu armust; sest vägi saab nõtruses täie võimuse!\" Nii ma siis tahan meelsamini kiidelda oma nõtrustest, et Kristuse vägi asuks elama minusse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 10, 'Sellepärast olen meeleldi nõtrustes, vägivalla all, hädades, tagakiusamistes ja kitsikustes Kristuse pärast; sest kui olen nõder, siis olen vägev!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 11, 'Ma olen läinud rumalaks; teie olete mind selleks sundinud. Sest mina oleksin pidanud saama kiitust teilt; sest ma pole sugugi vähem kui ülisuured Apostlid, ehk ma küll ei ole midagi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 12, 'Apostli tunnusteod on ju teie seas täide saadetud kõige kannatlikkusega, nii tunnustähtede kui imede ja väeavaldustega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 13, 'Sest mille poolest teie olete olnud halvemad kui teised kogudused, kui vaid selle poolest, et mina ise ei ole olnud teile koormaks? Andke mulle see süü andeks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 14, 'Vaata, ma olen nüüd kolmandat korda valmis tulema teie juurde ega tule teile koormaks; sest mina ei otsi teie oma, vaid teid endid; sest lapsed pole kohustatud koguma varandust vanemaile, vaid vanemad lastele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 15, 'Aga ma teen meeleldi kulu ja kulutan iseennast teie hingede eest, kuigi mind, kes ma teid väga armastan, pisut armastatakse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 16, 'Aga olgu peale, mina ei ole teid koormanud, kuid \"ma olin kelm ja võtsin teid kavalusega kinni!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 17, 'Kas ma kellegi kaudu neist, keda ma läkitasin teie juurde, olen teid koorinud?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 18, 'Mina õhutasin Tiitust tulema ja ühes temaga ma saatsin ühe venna. Kas Tiitus teid on koorinud? Kas me ei ole käinud ühesuguses vaimus? Ja kas ka mitte samades jälgedes?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 19, 'Teie mõtlete ammugi, et me kostame eneste eest teie ees? Me räägime Kristuses Jumala ees. Aga kõik, armsad vennad, teie ehitamiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 20, 'Sest ma kardan, et kui ma tulen, ma ei leia teid niisugustena nagu tahan, ja et teie leiate mind seesugusena, nagu te ei taha. Ma kardan, et teie seas vahest on riidu, kadedust, viha, jonni, laimamisi, keelekandmist, hooplemist, korratust;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 21, 'ja et kui ma tulen, minu Jumal mind jälle alandab teie juures ja ma pean olema kurb mitme pärast, kes enne on teinud pattu ega ole pöördunud oma rüvedusest ja hoorusest ja kiimalusest, mida nad on harrastanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 1, 'Kolmandat korda juba tulen teie juurde. Kahe ja kolme tunnistaja ütlusega tehakse iga asi kindlaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 2, 'Olen enne öelnud ja ütlen veel ette ära - nagu ütlesin siis, kui teist korda teie juures olin, ja samuti nüüd, kui ma ära olen - neile, kes enne on pattu teinud, ja kõigile muile, et kui ma jälle tulen, ma ei säästa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 3, 'Te otsite ju tõestust selle kohta, kas Kristus räägib minus. Tema ei ole nõder teie suhtes, vaid vägev teie sees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 4, 'Sest kuigi Tema löödi risti nõtruses, ometi elab Ta Jumala väe läbi. Nõnda meiegi oleme küll nõdrad Tema sees, kuid elame ühes Temaga Jumala väest teie jaoks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 5, 'Katsuge iseendid läbi, kas te olete usus. Uurige iseendid! Või te ehk ei tunne iseendist, et Jeesus Kristus on teie sees? Või ei ole te ehk kõlvulised?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 6, 'Ma loodan, et te tunnete ära, et me ei ole kõlbmatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 7, 'Aga me palume Jumalat, et teie ei teeks ühtki kurja, mitte et meie näiksime kõlvulised olevat, vaid et teie teeksite head ja meie oleksime otsekui kõlbmatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 8, 'Sest me ei suuda midagi tõe vastu, vaid üksnes tõe poolt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 9, 'Sest me rõõmutseme, kui meie oleme nõdrad ja teie olete vägevad; ja seda me palumegi, et teie saaksite täiuslikuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 10, 'Sellepärast ma kirjutan seda eemal olles, et ei oleks vaja teie juures olles olla range selle meelevalla järgi, mille Issand on andnud minule koguduse ehitamiseks ja mitte mahakiskumiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 11, 'Lõppeks, vennad, olge rõõmsad, seadke oma asjad korda, võtke manitsust, olge üksmeelsed, pidage rahu. Küll armastuse ja rahu Jumal on siis teiega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 12, 'Teretage üksteist püha suudlusega! Kõik pühad saadavad teile tervituse!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 13, 'Issanda Jeesuse Kristuse arm ja Jumala armastus ja Püha Vaimu osadus olgu teie kõikidega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 14, '');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 1, 'Paulus, Apostel, mitte inimeste poolt valitud ega ühegi inimese kaudu, vaid Jeesuse Kristuse ja Jumala Isa läbi, Kes Tema on surnuist üles äratanud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 2, 'ja kõik vennad, kes on minu juures, Galaatia kogudustele:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 3, 'Armu teile ja rahu Jumalalt, meie Isalt, ja Issandalt Jeesuselt Kristuselt,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 4, 'Kes Enese andis meie pattude eest, et Ta meid viiks välja praegusest kurjast ajastust Jumala ja meie Isa tahte järgi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 5, 'Kellele olgu austus ajastute ajastuteni! Aamen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 6, 'Ma panen imeks, et te Kristusest, Kes teid on armus kutsunud, nii ruttu ära pöördute teistsuguse Evangeeliumi juurde,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 7, 'mis ei olegi teine Evangeelium; on vaid mõningaid, kes teid segavad ja tahavad Kristuse Evangeeliumi pöörata teiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 8, 'Aga kui me ise, või isegi mõni Ingel taevast, peaks teile kuulutama Evangeeliumi peale selle, mida meie teile oleme kuulutanud - ta olgu neetud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 9, 'Nagu me ennegi oleme öelnud, nõnda ütlen mina ka nüüd jälle: kui keegi teile kuulutab Evangeeliumi peale selle, mis te olete saanud, siis ta olgu neetud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 10, 'Kas ma nüüd püüan inimeste või Jumala heakskiitmist? Või püüan ma olla inimestele meelepärane? Sest kui ma veel tahaksin olla inimestele meelepärane, siis ma ei oleks Kristuse sulane!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 11, 'Sest ma annan teile teada, vennad, et Evangeelium, mida mina olen kuulutanud, ei ole inimestelt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 12, 'Sest ma pole seda saanud ega õppinud inimestelt, vaid Jeesuse Kristuse ilmutuse kaudu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 13, 'Sest te olete kuulnud minu endisest elust juudi õpetuses, et ma üliväga taga kiusasin Jumala kogudust ja hävitasin seda,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 14, 'ja et ma juudi õpetuses ette jõudsin mitmest samaealisest oma hõimus, olles palju innukam harrastama oma esiisade pärimusi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 15, 'Aga kui oli sellele meelepärane, Kes mind mu ema ihust valis ja Oma armu läbi kutsus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 16, 'ilmutada minus Oma Poega, et ma kuulutaksin Teda paganate seas, siis ma ei hakanud kohe nõu pidama liha ja verega');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 17, 'ega läinud ka Jeruusalemma nende juurde, kes enne mind olid Apostlid, vaid ma läksin Araabiasse ja tulin jälle tagasi Damaskusesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 18, 'Hiljemini alles, kolme aasta pärast, ma läksin Jeruusalemma tegema tutvust Keefasega ja jäin tema juurde viieteistkümneks päevaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 19, 'Kedagi muud Apostlitest ma ei näinud kui vaid Jakoobust, Issanda venda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 20, 'Ja mida ma teile kirjutan, vaata, ma kinnitan Jumala ees, et ma ei valeta!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 21, 'Pärast ma tulin Süüria ja Kiliikia maakohtadesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 22, 'Aga ma olin näo poolest tundmatu Judea kogudustele, kes on Kristuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 23, 'Nad olid ainult kuulnud, et see, kes meid enne taga kiusas, nüüd jutlustab seda usku, mida ta enne hävitas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 24, 'Ja nad andsid Jumalale austust minu pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 1, 'Siis, neljateistkümne aasta pärast, ma läksin jälle üles Jeruusalemma ühes Barnabasega ja võtsin ka Tiituse kaasa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 2, 'Ent ma läksin sinna ilmutuse tagajärjel ja esitasin neile Evangeeliumi, mida ma kuulutasin paganate seas, eriti neile tähtsamaile, et ma kuidagi tühja ei jookseks ega oleks jooksnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 3, 'Aga Tiitustki, mu kaaslast, ehk ta küll on kreeklane, ei sunnitud end laskma ümber lõigata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 4, 'Ent sissepugenud valevendade pärast, kes olid hiilinud varitsema meie vabadust, mis meil on Kristuses Jeesuses, et meid orjastada,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 5, 'ei andnud me silmapilkugi järele ega alistunud neile, et Evangeeliumi tõde jääks teie juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 6, 'Ja need, kes näisid midagi olevat - olgu missugused nad iganes olid, mulle on see ükskõik, Jumal ei pea ju ühest inimesest rohkem lugu kui teisest - need, kes olid tähtsamad, ei lisanud mulle midagi juurde,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 7, 'vaid vastupidi, kui nad nägid, et minu hooleks on usaldatud Evangeeliumi kuulutamine ümberlõikamatuile, nõnda nagu Peetruse hooleks ümberlõigatuile -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 8, 'sest See, Kes tegev oli Peetruses Apostliametiks ümberlõigatute seas, oli ka minus tegev paganate heaks -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 9, 'ja kui Jakoobus ja Keefas ja Johannes, keda peeti sambaiks, tundsid ära armu, mis mulle oli antud, andsid nad mulle ja Barnabasele kätt osaduse täheks, et meie paganate seas ja nemad ümberlõigatute seas, kuulutaksime armuõpetust,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 10, 'ainult et me ka mõtleksime vaestele, ja just seda ma olen olnud usin tegema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 11, 'Aga kui Keefas tuli Antiookiasse, astusin ma isiklikult ta vastu, sest teda oli tarvis noomida.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 12, 'Sest enne kui Jakoobuse juurest oli tulnud mõningaid, sõi ta ühes paganatega; aga kui need tulid, tõmbus ta tagasi ja läks kõrvale, kartes ümberlõigatuid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 13, 'Ja ühes temaga hakkasid ka teised juudid silmakirjatsema, nõnda et ka Barnabas kaasa tõmmati nende silmakirjatsemisse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 14, 'Aga kui ma nägin, et nad õigesti ei käinud Evangeeliumi tõe järgi, ütlesin ma kõikide ees Keefale: \"Kui sina, olles juut, elad paganate ja mitte juutide kommete järgi, miks sa siis sunnid paganaid elama juudi kommete järgi?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 15, 'Meie oleme sünnilt juudid ja mitte patused paganate seast,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 16, 'aga teades, et inimene ei saa õigeks käsu tegudest, vaid üksnes Kristuse Jeesuse usu kaudu, oleme ka meie uskunud Kristusesse Jeesusesse, et me saaksime õigeks Kristuse usust ja mitte käsu tegudest, sest käsu tegudest ei saa õigeks ükski liha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 17, 'Aga kui otsides õigekssaamist Kristuses, meiegi osutume patusteks, kas on siis Kristus patu teenija? Ei sugugi mitte!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 18, 'Sest kui ma jälle ehitan üles selle, mille ma olen maha kiskunud, siis ma teen enese üleastujaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 19, 'Sest mina olen käsu läbi käsule surnud, et ma elaksin Jumalale; ma olen ühes Kristusega risti löödud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 20, 'Ent nüüd ei ela enam mina, vaid Kristus elab minu sees! Ja mida ma nüüd elan lihas, seda ma elan usus Jumala Pojasse, Kes mind on armastanud ja on Iseenese andnud minu eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 21, 'Ma ei tee Jumala armu tühjaks. Sest kui käsu kaudu tuleks õigus, siis oleks Kristus ilmaasjata surnud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 1, 'Oh te mõistmatud galaatlased, kes teid on võlunud? Teid, kelle silmade ette Jeesus Kristus oli joonistatud ristilööduna?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 2, 'Seda üksnes ma tahan teilt teada, kas te saite Vaimu käsu tegudest või usu kuulutamisest?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 3, 'Kas te olete nii mõistmatud? Te algasite Vaimus; kas tahate nüüd lihas lõpetada?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 4, 'Kas te nii suurt olete kannatanud ilmaasjata? Teisiti see olekski ilmaasjata!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 5, 'Tema, kes siis annab teile Vaimu ja teeb vägevaid tegusid teie sees, kas Ta teeb seda käsu tegudest või usu kuulutamisest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 6, 'nõnda nagu \"Aabraham uskus Jumalat, ja see arvati temale õiguseks!\"?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 7, 'Te ju teate, et need, kes on usust, on Aabrahami lapsed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 8, 'Aga et Kiri nägi ette ära, et Jumal teeb paganad usust õigeks, kuulutas see Aabrahamile selle hea sõnumi: \"Sinus õnnistatakse kõik rahvad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 9, 'Sellepärast õnnistatakse neid, kes on usust, ühes uskliku Aabrahamiga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 10, 'Sest kõik, kes endid rajavad käsutegudele, on needuse all; sest on kirjutatud: \"Neetud on igaüks, kes ei püsi kõiges, mis on kirja pandud käsuraamatus, nii et ta seda teeks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 11, 'Et nüüd käsu kaudu ükski ei saa õigeks Jumala ees, on ilmne, sest õige peab usust elama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 12, 'Käsk aga ei ole usust, vaid \"kes teeb nende järgi, see elab nende varal!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 13, 'Kristus on meid lahti ostnud käsu needusest, kui ta sai needuseks meie eest - sest on kirjutatud: \"Neetud on igaüks, kes puu küljes ripub\" -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 14, 'et Aabrahami õnnistus saaks paganaile osaks Jeesuses Kristuses ja me usu kaudu saaksime Vaimu tõotuse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 15, 'Vennad, mina räägin inimese viisil! Inimesegi viimset tahet, kui see on nimetatud, ei tee ükski tühjaks ega lisa sellele midagi juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 16, 'Aga nüüd on tõotused antud Aabrahamile ja tema soole. Ei ole mitte öeldud: \"Ja sinu sugudele\", otsekui paljude kohta, vaid nagu ühe kohta: \"Ja sinu soole,\" kes on Kristus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 17, 'Aga ma ütlen seda: lepingut, mille Jumal on enne kinnitanud, ei tee nelisada kolmkümmend aastat hiljemini tekkinud käsk mitte tühjaks, nii et see hävitaks tõotuse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 18, 'Sest kui pärand tuleks käsust, ei oleks see enam tõotusest. Ent Jumal on selle kinkinud Aabrahamile tõotuse kaudu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 19, 'Milleks on siis käsk? See on üleastumiste pärast juurde lisatud seniks, kui tuleb sugu, kellele oli antud tõotus; ja käsk seati inglite kaudu vahemehe käe läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 20, 'Kuid vahemeest ei vajata ühe jaoks; ent Jumal on üks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 21, 'Kas siis käsk on Jumala tõotuste vastu? Ei sugugi. Sest kui oleks antud käsk, mis võiks teha elavaks, siis tuleks õigus tõesti käsust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 22, 'Aga Kiri on kõik pannud kinni patu alla, et tõotus antaks Jeesuse Kristuse usust neile, kes usuvad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 23, 'Aga enne kui tuli usk, olime käsu valve all, kinni pandud tulevase usu ilmumise jaoks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 24, 'Nõnda on käsk saanud meie kasvatajaks Kristuse poole, et me saaksime õigeks usust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 25, 'Aga et usk on tulnud, ei ole me mitte enam kasvataja all.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 26, 'Sest te olete kõik usu kaudu Jumala lapsed Kristuses Jeesuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 27, 'Sest nii paljud kui teid on Kristusesse ristitud, olete Kristusega riietatud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 28, 'Ei ole siin juuti ega kreeklast, ei ole siin orja ega vaba, ei ole siin meest ega naist, sest te kõik olete üks Kristuses Jeesuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 29, 'Aga kui te olete Kristuse Omad, siis olete ka Aabrahami sugu ja pärijad tõotuse järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 1, 'Aga ma ütlen: niikaua kui pärija on alaealine, ei ole mingit vahet orja ja tema vahel, ehk ta küll on kõige isand;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 2, 'vaid ta on eestkostjate ja valitsejate all kuni isa poolt määratud ajani.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 3, 'Nõnda ka meie: kui olime alaealised, siis olime orjastatud maailma algjõudude alla.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 4, 'Aga kui aeg täis sai, läkitas Jumal Oma Poja, Kes sündis naisest ja sai käsu alla,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 5, 'lahti ostma käsualuseid, et me saaksime lapse seisuse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 6, 'Aga et te nüüd olete lapsed, on Jumal läkitanud teie südamesse Oma Poja Vaimu, Kes hüüab: \"Abba, Isa!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 7, 'Nõnda ei ole sa enam ori, vaid laps; aga kui sa oled laps, siis oled ka pärija Jumala kaudu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 8, 'Aga siis, kui te ei tundnud Jumalat, te orjasite neid, kes olemise poolest ei olegi jumalad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 9, 'Ent nüüd, kus te olete Jumala ära tundnud, või õigemini, Jumal on teid ära tundnud, kuidas te pöördute jälle nõrkade ja viletsate algjõudude poole, mida te jälle tahate uuesti orjata?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 10, 'Te arvestate päevi ja kuid ja aegu ja aastaid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 11, 'Ma kardan teie pärast, et ma teie kallal ehk asjata olen vaeva näinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 12, 'Saage minusuguseiks, sest minagi olen saanud teiesuguseks, vennad, ma palun teid! Te ei ole mind millegagi solvanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 13, 'Te ju teate, et ma liha nõtruses teile kuulutasin Evangeeliumi esimesel korral,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 14, 'ja te ei pannud kiusatust, mis teil oli minu kehalisest seisukorrast, mitte halvaks ega võõrastanud mind, vaid võtsite mind vastu kui Jumala Ingli, kui Kristuse Jeesuse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 15, 'Kus on nüüd teie õndsuse kiitlus? Sest ma tunnistan teile, et teie, kui see oleks olnud võimalik, oleksite oma silmad kiskunud välja ja andnud minule.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 16, 'Kas ma nüüd olen saanud teie vaenlaseks, et ma olen teile tõtt öelnud?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 17, 'Nad on õhinal teie ümber, kuid mitte kaunil viisil, vaid nad tahavad teid välja jätta, et te innukad oleksite nende kasuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 18, 'Hea on olla innukas hea suhtes alatasa ja mitte ainult siis, kui mina teie juures olen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 19, 'Mu lapsukesed, kelle pärast ma nüüd jälle olen lapsevaevas, kuni Kristus teie sees saab kuju!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 20, 'Küll ma tahaksin nüüd olla teie juures ja oma häältki muuta, sest ma olen päris nõutu teie pärast!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 21, 'Öelge mulle, kes tahate olla käsu all, kas te ei kuule käsuõpetust?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 22, 'Sest on kirjutatud, et Aabrahamil oli kaks poega, üks ümmardajast ja teine vabast naisest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 23, 'Aga ümmardaja poeg oli sündinud liha järgi, vaba naise poeg aga tõotuse kaudu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 24, 'Need on võrdluskujud. Sest need naised on kaks lepingut; üks Siinai mäelt, mis sünnitab orjapõlveks, see on Haagar.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 25, 'Sest Haagar tähendab Siinai mäge Araabias ja vastab praegusele Jeruusalemmale, sest see orjab ühes oma lastega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 26, 'Aga see Jeruusalemm, mis on ülal, on vaba naine, ja see on meie kõikide ema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 27, 'Sest on kirjutatud: \"Rõõmutse, sigimatu, kes pole sünnitanud; tõsta häält ja hüüa valjusti, kes pole olnud lapsevaevas, sest vallalisel saab olema rohkem lapsi kui sellel, kellel on mees!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 28, 'Ent teie, vennad, olete nõnda nagu Iisak, tõotuse lapsed!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 29, 'Aga nõnda nagu tol ajal see, kes oli sünnitatud liha järgi, taga kiusas seda, kes oli sündinud Vaimu järgi, nõnda on ka nüüd.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 30, 'Aga mida ütleb Kiri? \"Kihuta minema ümmardaja ja tema poeg, sest ümmardaja poeg ei või pärida ühes vaba naise pojaga!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 31, 'Niisiis, vennad, ei ole me ümmardaja, vaid oleme vaba naise lapsed!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 1, 'Vabaduseks on Kristus meid vabastanud. Püsige siis selles ja ärge laske endid jälle panna orjaikkesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 2, 'Vaata, mina, Paulus, ütlen teile: kui te lasete endid ümber lõigata, siis ei ole teil Kristusest mingit kasu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 3, 'Aga ma tunnistan jälle igale inimesele, kes laseb ennast ümber lõigata, et ta kohus on täita kõike käsku.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 4, 'Te olete jäänud ilma Kristusest, kes tahate saada õigeks käsu kaudu; te olete langenud ära armust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 5, 'Sest me ootame usust õiguse lootust Vaimu kaudu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 6, 'Sest Kristuses Jeesuses ei kehti ümberlõikamine ega ümberlõikamatus, vaid usk, mis on tegev armastuse kaudu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 7, 'Te jooksite hästi; kes on teid takistanud olemast tõele sõnakuulelikud?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 8, 'Veenmine selleks ei tulnud mitte Temalt, Kes teid kutsub.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 9, 'Pisut haputaignat teeb kogu taigna hapuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 10, 'Mina loodan teist Issandas, et te ei hakka teisiti mõtlema; aga kes teid teeb segaseks, peab kandma oma nuhtlust, olgu see kes tahes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 11, 'Aga mind, vennad, kui ma veel jutlustan ümberlõikamist, miks mind veel taga kiusatakse? Siis ju oleks pahandus ristist kõrvaldatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 12, 'Oh et need, kes teid tülitavad, endid ära lõikaksid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 13, 'Sest teie, vennad, olete kutsutud vabaduseks; aga mitte lihale ajet andvaks vabaduseks, vaid teenige üksteist armastuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 14, 'Sest kõik käsk on täidetud ühes sõnas, nimelt selles: \"Armasta oma ligimest nagu iseennast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 15, 'Aga kui te isekeskis purelete ja üksteist sööte, siis katsuge, et te üksteist ära ei neela.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 16, 'Aga ma ütlen: käige Vaimus, siis te ei täida mitte liha himusid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 17, 'Sest liha himustab Vaimu vastu ja Vaim liha vastu; need on üksteise vastu, et te ei teeks seda, mida te tahate.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 18, 'Aga kui te olete Vaimu juhitavad, siis te ei ole käsu all.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 19, 'Aga liha teod on ilmsed, need on: hoorus, rüvedus, kiimalus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 20, 'ebajumalateenistus, nõidus, vaen, riid, kade meel, vihastumised, jonn, kildkonnad, lahkõpetused,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 21, 'tapmised, joomised, pidutsemised ja muud sellesarnast, millest ma teile ette ütlen, nagu ma ka juba enne olen öelnud, et need, kes teevad seesugust, ei päri Jumala Riiki.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 22, 'Aga Vaimu vili on armastus, rõõm, rahu, pikk meel, lahkus, heatahtlikkus, ustavus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 23, 'tasadus, kasinus; selliste vastu ei ole käsk.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 24, 'Aga kes on Kristuse Jeesuse päralt, need on oma liha risti löönud ühes tema ihalduste ja himudega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 25, 'Kui me elame Vaimus, siis käigem ka Vaimus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 26, 'Ärgem olgem ahned tühjale aule, üksteist ärritades, üksteist kadestades.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 1, 'Vennad, kui inimene ka satuks mingisse eksitusse, siis teie, vaimulikud, parandage niisugust jälle tasase vaimuga; ja valva sa iseennastki, et ka sind ei kiusataks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 2, 'Kandke üksteise koormat, ja nõnda te täidate Kristuse käsku.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 3, 'Sest kui keegi mõtleb enese midagi olevat, ilma midagi olemata, see petab iseennast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 4, 'Ent igaüks katsugu läbi oma töö, ja siis on tal üksi eneses kiitlemist, mitte teisele;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 5, 'sest igaüks kandku oma koormat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 6, 'Aga keda õpetatakse sõnaga, see jagagu kõike head sellele, kes teda õpetab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 7, 'Ärge eksige, Jumal ei anna Ennast pilgata, sest mis inimene külvab, seda ta ka lõikab!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 8, 'Sest kes külvab oma lihale, see lõikab lihast kadu; aga kes külvab Vaimule, see lõikab Vaimust igavest elu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 9, 'Ja kui me teeme head, siis ärgem tüdigem, sest me saame omal ajal lõigata, kui me ei väsi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 10, 'Seepärast nüüd, et meil veel on aega, tehkem head kõikidele, aga kõige enam usukaaslastele!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 11, 'Vaadake, kui suurte tähtedega ma oma käega teile olen kirjutanud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 12, 'Kõik, kes püüavad meeldida liha poolest, need sunnivad teid ümberlõikamisele ainult sellepärast, et neid taga ei kiusataks Kristuse risti tõttu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 13, 'Sest needki ise, kes lasevad endid ümber lõigata, ei pea käsku, vaid tahavad, et teid ümber lõigataks ja nad teie lihast võiksid kiidelda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 14, 'Aga mulle ärgu juhtugu seda, et ma kiitleksin muust kui meie Issanda Jeesuse Kristuse ristist, Kelle läbi maailm on minule risti löödud ja mina maailmale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 15, 'Sest ümberlõikamine ei tähenda midagi ega ümberlõikamatus, vaid uus loodu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 16, 'Ja kõigile, kes käivad seda juhtnööri mööda, neile olgu rahu ja halastus, ja ka Jumala Iisraelile!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 17, 'Viimaks ärgu tehku mulle keegi vaeva, sest mina kannan Jeesuse arme oma ihul.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 18, 'Meie Issanda Jeesuse Kristuse arm olgu teie vaimuga, vennad! Aamen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 1, 'Paulus, Jumala tahtest Kristuse Jeesuse Apostel pühadele, kes on Efesoses, ja usklikele Kristuses Jeesuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 2, 'Armu teile ja rahu Jumalalt, meie Isalt ja Issandalt Jeesuselt Kristuselt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 3, 'Kiidetud olgu Jumal ja meie Issanda Jeesuse Kristuse Isa, Kes meid on õnnistanud kõige vaimuliku õnnistusega taevasis asjus Kristuses,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 4, 'nõnda nagu Ta meid on valinud Tema sees enne maailma rajamist olema pühad ja laitmatud Tema ees armastuses,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 5, 'meid ette ära määrates lapseõiguse osalisiks Jeesuse Kristuse kaudu Enese juurde Oma tahtmise heaksarvamisel,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 6, 'Oma armu au kiituseks, mille Ta meile on kinkinud selles Armsas,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 7, 'Kelles meil on lunastus Tema vere läbi, üleastumiste andekssaamine Tema armu rikkust mööda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 8, 'Seda armu on Ta rohkesti välja valanud meie üle kõige tarkuse ja mõistusega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 9, 'kui Ta meile tegi teatavaks Oma tahtmise saladuse Oma hea meele kohaselt, nõnda nagu Ta seda Eneses oli kavatsenud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 10, 'aegade täiuse korraldamiseks, et Kristuses kokku võetaks kõik, mis on taevas ja mis on maa peal,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 11, 'Temas, Kelles meiegi oleme saanud pärijaiks, Kes selleks enne oleme määratud selle kavatsust mööda, Kes kõik teeb Oma tahtmise nõu järgi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 12, 'et me oleksime Tema au kiituseks, Kes enne oleme lootnud Kristuses,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 13, 'Kelles teiegi olete sellest alates, kui saite kuulda tõe sõna, oma õndsuse Evangeeliumi, ja Kelles teiegi saades usklikuks olete kinnitatud tõotatud Püha Vaimu pitseriga, selle Vaimu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 14, 'Kes on meie pärandi tagatis Tema omandi lunastamiseks - Tema au kiituseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 15, 'Sellepärast ka mina, kui ma sain kuulda teie usust Issandasse Jeesusesse ja armastusest, mis teil on kõigi pühade vastu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 16, 'ei lakka tänamast teie eest ja tuletan teid meelde oma palvetes,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 17, 'et meie Issanda Jeesuse Kristuse Jumal, au Isa, teile annaks tarkuse ja ilmutuse Vaimu Tema tunnetamises');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 18, 'ja valgustaks teie südame silmad, et te teaksite, mis lootus see on, milleks Ta teid on kutsunud, ja mis on Tema pärandi au rikkus pühade sees,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 19, 'ja mis on Tema ülemäära suur vägi meie suhtes, kes usume Tema jõutugevuse mõjul,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 20, 'millega Ta on olnud mõjuv Kristuses, kui Ta Tema surnuist üles äratas ja pani istuma Oma paremale käele taevas,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 21, 'kõrgele üle kõige valitsuse ja võimu ja väe ja ülemuse ning üle iga nime, mida nimetatakse mitte ainult selles, vaid ka tulevases maailmas,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 22, 'ja asetas kõik Tema jalge alla ja pani Tema kõigi asjade üle peaks kogudusele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 23, 'kes on Tema Ihu, selle täius, Kes kõik kõiges täidab!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 1, 'Jumal on teinud elavaks ka teid, kes olite surnud oma üleastumistes ja pattudes,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 2, 'milles te käisite enne selle maailma ajastu järgi, õhuvalla vürsti järgi, vaimu järgi, kes nüüd on tegev sõnakuulmatuse laste sees,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 3, 'kelle seas ka me kõik enne käisime oma liha himudes ja tegime liha ja mõtete tahtmist mööda ning olime loomu poolest viha lapsed nagu teisedki.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 4, 'Aga Jumal, Kes rikas on halastusest, Oma suure armastuse pärast, millega Ta meid on armastanud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 5, 'kuigi me olime surnud üleastumistes, on meid ühes Kristusega teinud elavaks - armust olete õndsaks saanud -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 6, 'ja on meid ühes Temaga üles äratanud ja ühes Temaga asetanud istuma taevalikku olukorda Kristuses Jeesuses,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 7, 'et osutada tulevail ajastuil Oma armu ülemäärast rikkust helduses meie vastu Kristuses Jeesuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 8, 'Sest teie olete armust õndsaks saanud usu kaudu ja see pole mitte teist enestest; see on Jumala and;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 9, 'mitte tegudest, et ükski ei saaks kiidelda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 10, 'Sest meie oleme Tema teos, Kristuses Jeesuses loodud headele tegudele, mis Jumal on enne valmistanud, et me käiksime nendes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 11, 'Sellepärast tuletage meelde, et teie, kes enne olite liha poolest paganad ja keda ümberlõikamatuiks kutsusid need, keda lihas kätega tehtud ümberlõikamise tõttu kutsutakse ümberlõigatuiks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 12, 'olite tol ajal lahus Kristusest, kaugel ära Iisraeli kodakondsuse õigusest ja võõrad tõotuse lepingule, ilma lootuseta ja ilma Jumalata siin maailmas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 13, 'Aga nüüd olete teie, kes enne olite kaugel, Kristuses Jeesuses saanud ligi Kristuse vere läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 14, 'Sest Tema on meie rahu, Kes on mõlemad teinud üheks ja kiskunud maha lahutava vaheseina, vaenu, kui Ta Oma Lihas');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 15, 'kaotas käsusõnade käsu seadlustega, et Ta need kaks looks Enese sees üheks uueks inimeseks, tehes rahu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 16, 'ja lepitaks mõlemad Jumalaga ühes ihus risti kaudu, kui Ta vaenu suretas Enese läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 17, 'Ja Tema tuli ning kuulutas rahu teile, kes kaugel olite, ja rahu neile, kes lähedal olid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 18, 'sest Tema läbi on meil mõlemail ligipääs ühes Vaimus Isa juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 19, 'Nii ei ole te siis mitte enam võõrad ega majalised, vaid pühade kaaskodanikud ja Jumala kodakondsed,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 20, 'rajatud Apostlite ja prohvetite alusele, kus Kristus Jeesus Ise on nurgakivi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 21, 'milles kogu hoone on kokku liidetud ja kasvab pühaks templiks Issandas,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 22, 'Kelles ka teid ühes üles ehitatakse Jumala eluasemeks Vaimus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 1, 'Seetõttu mina, Paulus, olen Kristuse Jeesuse vang teie pärast, paganad -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 2, 'kui te vahest olete kuulnud Jumala armu majapidamisest, mis minu kätte on antud teie heaks;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 3, 'sest ilmutuse kaudu on mulle antud teada saladus, nagu ma lühidalt enne kirjutasin,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 4, 'selleks et te lugedes võiksite ära tunda minu arusaamise Kristuse saladusest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 5, 'mida endistes põlvedes inimlastele ei ole teada antud, nagu see nüüd on ilmutatud Tema pühadele Apostlitele ja prohvetitele Vaimu läbi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 6, 'et paganadki on kaaspärijad ja ühes ihus ja osalised tema tõotusest Kristuses Jeesuses Evangeeliumi kaudu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 7, 'mille teenijaks ma olen saanud Jumala armuande järgi, mis mulle on antud Tema väe mõjustust mööda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 8, 'Minule, kõige vähemale kõigi pühade seast, on see arm antud paganate seas kuulutada Kristuse äraarvamatut rikkust');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 9, 'ja selgeks teha, milline on selle saladuse korraldus, mis igavikest alates on olnud varjul Jumalas, kes kõik on loonud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 10, 'et Jumala mitmesugune tarkus nüüd saaks teatavaks koguduse kaudu taevalistele valitsustele ja võimudele');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 11, 'ajastute ettemääramise järgi, mille Ta teostas Kristuses Jeesuses, meie Issandas,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 12, 'Kelle sees meil on julge ligipääs usalduses usu kaudu Temasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 13, 'Seetõttu ma palun, et te ei nõrkeks minu kitsikustes teie pärast, mis on teie au.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 14, 'Sellepärast ma nõtkutan põlvi Isa ees,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 15, 'kelle lasteks nimetatakse kõiki suguvõsasid taevas ja maa peal,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 16, 'et Ta annaks teile Oma au rikkust mööda saada tugevaks Tema Vaimu läbi seespidise inimese poolest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 17, 'et Kristus usu kaudu elaks teie südameis ning te oleksite juurdunud ja rajatud armastusse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 18, 'et võiksite täiesti mõista ühes kõigi pühadega, milline on armastuse laius ja pikkus ja kõrgus ja sügavus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 19, 'ja ära tunda Kristuse armastuse, mis ületab kõik tunnetuse; et te oleksite täidetud Jumala kõige täiusega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 20, 'Aga Sellele, Kes enam kui rohkesti võib teha üle kõige selle, mida me palume või mõistame väge mööda, mis meis on tegev,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 21, 'Temale olgu austus koguduses ja Kristuses Jeesuses ajastute ajastu kõigi sugupõlvedeni. Aamen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 1, 'Nii manitsen teid siis mina, kes olen vang Issanda pärast, väärikalt käima kutsumise järgi, millega te olete kutsutud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 2, 'kõige alanduse ja tasadusega, pika meelega, sallides üksteist armastuses,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 3, 'ja olles usinad üksmeelt pidama vaimus rahusideme läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 4, 'Üks ihu ja üks Vaim, nagu te ka olete kutsutud üheks lootuseks oma kutsumise poolest;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 5, 'üks Issand, üks usk, üks ristimine,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 6, 'üks Jumal ja kõikide Isa, Kes on kõikide üle ja kõikide läbi ja teie kõikide sees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 7, 'Aga igaühele meist on antud armu Kristuse annetuse mõõtu mööda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 8, 'Sellepärast öeldakse: \"Tema läks üles kõrgele, viis vangid vangi, andis inimestele ande!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 9, 'Aga et Ta on läinud üles, mis on see muud kui et Ta ka enne on läinud alla maa alamaisse paikadesse?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 10, 'Kes on läinud alla, on Sama, Kes on ka läinud üles üle kõigi taevaste, et Tema kõik täidaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 11, 'Ja Tema on pannud mõned Apostleiks ja mõned prohveteiks ja mõned Evangeeliumi kuulutajaiks ja mõned karjaseiks ja õpetajaiks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 12, 'et pühi inimesi täielikult valmistada abiliste tööle Kristuse Ihu ülesehitamiseks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 13, 'seniks kui me kõik jõuame ühisele usule ja Jumala Poja tunnetusele ning täieks meheks saame Kristuse täiuse täisea mõõtu mööda,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 14, 'ega jääks enam väetiteks lasteks, keda õõtsutatakse ja kõigutatakse igas õpetuse tuules inimeste pettemänguga ja nende vembutamisega eksiõpetusse võrgutamiseks;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 15, 'vaid et me tõtt taotledes armastuses kasvaksime kõiges Selle sisse, Kes on pea, Kristus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 16, 'Kelles kogu ihu on kokku liidetud ja koos hoitakse iga kõõluse kaasabil vastavalt iga üksiku liikme tegevusele Tema mõõdu järgi ja Kes teeb, et ihu kasvab enese ülesehitamiseks armastuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 17, 'Seda ma siis ütlen ja hoiatan teid Issandas, et te ei elaks enam, nagu ka paganad elavad oma meele tühjuses,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 18, 'kes pimestunuina mõistuse poolest ja võõrdunuina Jumala elust neis pesitseva rumaluse ja nende südame paadumuse tõttu');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 19, 'on südametunnistuses tuimaks jäänud ning endid andnud kõlvatuse kätte kõike roppust ahnesti tegema.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 20, 'Aga nõnda ei ole te õppinud Kristust tundma,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 21, 'kui te tõesti olete Temast kuulnud ja Temas õpetatud, nõnda nagu tõde on Jeesuses:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 22, 'et teil tuleb enesest ära heita endise elu poolest vana inimene, kes ennast hävitab järgides petlikke himusid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 23, 'ja saada uueks oma meele vaimus');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 24, 'ning riietuda uue inimesega, kes Jumala sarnaseks on loodud tõelise õiguse ja pühaduse sisse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 25, 'Sellepärast jätke maha vale ja rääkige tõtt igaüks oma ligimesega, sest me oleme üksteise liikmed!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 26, 'Vihastuge, aga ärge tehke pattu! Ärge laske päeva looja minna oma vihastuse üle');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 27, 'ja ärge andke maad kuradile!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 28, 'Kes varastab, ärgu varastagu enam, vaid tehku pigemini tööd ning soetagu vara oma käte tööga, et tal oleks jagada sellele, kellel on puudus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 29, 'Ükski nurjatu sõna ärgu tulgu teie suust, vaid ainult seesugune, mis on hea tarvilikuks kasvatuseks, et see pakuks mõnu kuulajaile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 30, 'Ja ärge kurvastage Jumala Püha Vaimu, kellega te olete otsekui pitseriga kinnitatud lunastuse päevani.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 31, 'Kõik kibedus ja äkiline meel ja viha ja kisa ja pilge jäägu teist kaugele ära ühes kõige pahaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 32, 'Aga olge lahked üksteise vastu, südamlikud, andke andeks üksteisele, nõnda nagu ka Jumal Kristuses teile on andeks andnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 1, 'Siis võtke nüüd Jumal enestele eeskujuks nagu armsad lapsed');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 2, 'ja käige armastuses, nõnda nagu ka Kristus meid on armastanud ja on Iseenese andnud meie eest anniks ja ohvriks, Jumalale magusaks lõhnaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 3, 'Aga hoorus ja kõik roppus või ahnus olgu nimetamata teie seas, nõnda nagu sünnis on pühadele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 4, 'ja riivatus ja rumalad sõnad või tühised naljad, mis kõik ei sobi, vaid pigemini olgu tänu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 5, 'Sest seda te teate tunnetades, et mitte ühelgi hoorajal ega ropul ega ahnel, kuna ta on ebajumala ori, ei ole pärandit Kristuse ja Jumala riigis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 6, 'Ükski ärgu petku teid tühjade sõnadega, sest niisuguste asjade pärast tuleb Jumala viha sõnakuulmatute laste peale;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 7, 'ärge siis saage nende kaaslasteks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 8, 'Sest te olite varemalt pimedus, aga nüüd te olete valgus Issandas. Käige nagu valguse lapsed -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 9, 'sest valguse vili on kõiksugune headus ja õigus ja tõde -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 10, 'ja katsuge, mis on Issandale meelepärane!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 11, 'Ärgu teil olgu tegemist pimeduse viljatute tegudega, vaid mõistke need pigemini hukka!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 12, 'Sest mis kõik seal salajas nende kaudu sünnib, on häbi öeldagi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 13, 'Aga kõik saab avalikuks, kui valgus seda paljastab; sest kõik, mis tuleb ilmsiks, on valgus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 14, 'Sellepärast Ta ütleb: \"Ärka üles, kes magad, ja tõuse üles surnuist, siis Kristus valgustab sind!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 15, 'Siis vaadake hästi, kuidas te elate, mitte kui rumalad, vaid kui targad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 16, 'pidades aega kalliks, sest päevad on kurjad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 17, 'Seepärast ärge olge rumalad, vaid mõistke, mis on Issanda tahtmine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 18, 'Ärge joovastuge viinast, kust tuleb õnnetu elu, vaid saage täis Vaimu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 19, 'rääkides isekeskis psalmide ja kiituseviiside ja vaimulike lauludega, lauldes ja mängides kannelt Issandale oma südames,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 20, 'kõige eest alatasa tänades Jumalat ja Isa meie Issanda Jeesuse Kristuse nimel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 21, 'Ja olge allaheitlikud üksteisele Kristuse kartuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 22, 'Naised, olge allaheitlikud oma meestele, otsekui Issandale!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 23, 'Sest mees on naise pea, nõnda nagu ka Kristus on Koguduse pea, Tema on ihu Õnnistegija.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 24, 'Aga nõnda nagu Kogudus on allaheitlik Kristusele, nõnda olgu ka naised oma meestele kõigis asjus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 25, 'Mehed, armastage oma naisi, nõnda nagu ka Kristus on armastanud Kogudust ja on andnud Iseenese tema eest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 26, 'et teda pühitseda, pestes puhtaks veepesemisega sõna sees,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 27, 'ja seada Enese ette Kogudus austatuna, nii et tal ei oleks laiku ega kortsu ega muud sellesarnast, vaid et ta oleks püha ja laitmatu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 28, 'Nõnda peavad mehedki omi naisi armastama nagu iseenese ihu. Kes oma naist armastab, armastab iseennast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 29, 'Sest ükski ei ole iialgi vihanud iseenese liha, vaid igaüks toidab ja hoiab seda, nõnda nagu ka Issand Kogudust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 30, 'Sest meie oleme Tema Ihu liikmed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 31, '\"Seepärast inimene jätab maha oma isa ja ema ning hoiab oma naise poole, ja need kaks on üks liha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 32, 'See saladus on suur; aga mina räägin Kristusest ja Kogudusest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 33, 'Olgu nüüd kuidas on, aga teiegi igaüks omaette armastage oma naist nagu iseennast; aga naine kartku meest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 1, 'Lapsed, kuulake oma vanemate sõna Issandas, sest see on õige.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 2, '\"Austa oma isa ja ema!\" - see on esimene käsusõna, millel on tõotus:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 3, '\"Et su käsi hästi käiks ja sina kaua elaksid maa peal!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 4, 'Ja teie, isad, ärge ärritage oma lapsi vihale, vaid kasvatage neid karistuses ja Issanda manitsuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 5, 'Orjad, olge sõnakuulelikud neile, kes teie isandad on liha poolest, kartuse ja värinaga oma südame kohtlases meeles, otsekui Kristusele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 6, 'mitte silmakirjaks, nagu püüdes olla inimestele meelepärast, vaid kui Kristuse orjad, kes südamest teevad, mida Jumal tahab,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 7, 'orjates hea meelega otsekui Issandat ja mitte inimesi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 8, 'teades, et Issand selle hea, mis keegi on teinud, tasub igaühele, olgu ta ori või vaba.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 9, 'Ja teie, isandad, tehke neile samuti ja jätke ähvardamine, sest te teate, et nende ja teie Issand on taevas ja et Temal ei ole isikute eelistamist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 10, 'Lõppeks, mu vennad, saage vägevaks Issandas ja Ta tugevuse jõus!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 11, 'Varustage endid kogu Jumala sõjavarustusega, et te suudaksite seista kuradi kavalate rünnakute vastu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 12, 'Sest meil ei ole maadlemist vere ja lihaga, vaid valitsuste ja võimudega, selle pimeduse maailma valitsejatega, taevaaluste kurjuse vaimudega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 13, 'Sellepärast võtke kätte kõik Jumala sõjavarustus, et te suudaksite vastu panna kurjal päeval ja jääda püsima, kui te kõik olete sooritanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 14, 'Siis seiske nüüd ja teie niuded olgu vöötatud tõega ja teil olgu seljas õiguse soomusrüü');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 15, 'ja kingadeks jalas valmidus rahuevangeeliumi kuulutamiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 16, 'Kõige selle juures haarake usukilp, millega te võite kustutada kõik tigeda tulised nooled!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 17, 'Ja võtke enesele päästekiiver ja vaimumõõk, see on Jumala sõna,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 18, 'iga palve ja anumise kaudu, palvetage igal ajal Vaimus, ning olge selleks valvel kõige püsivusega ja eestpalvetega kõigi pühade eest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 19, 'ja minugi eest, et minule, kui ma suu avan, antaks julgesti kuulutada Evangeeliumi saladust,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 20, 'mille jaoks mina olen saadik ka ahelas, et ma julgesti kõneleksin sellest, nõnda nagu mu kohus on kõnelda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 21, 'Aga et ka teie teaksite, kuidas minu käsi käib, siis jutustab teile kõik Tühhikos, armas vend ja ustav abiline Issandas,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 22, 'kelle ma otse selleks läkitasin teie juurde, et te saaksite teada, kuidas on lugu meiega, ja et ta kinnitaks teie südameid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 23, 'Rahu olgu vendadele ja armastus usuga Jumalalt Isalt ja Issandalt Jeesuselt Kristuselt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 24, 'Arm nende kõikidega, kes armastavad meie Issandat Jeesust Kristust kadumatuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 1, 'Paulus ja Timoteos, Kristuse Jeesuse sulased, kõigile pühadele Kristuses Jeesuses, kes elavad Filipis, ja ühtlasi koguduse juhatajaile ja abilistele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 2, 'Armu teile ja rahu Jumalalt, meie Isalt, ja Issandalt Jeesuselt Kristuselt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 3, 'Ma tänan oma Jumalat iga kord, kui ma teid meelde tuletan,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 4, 'alati igas oma palves teie kõikide eest rõõmuga palvetades;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 5, 'ma tänan teie osaduse pärast Evangeeliumiga esimesest päevast siiani;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 6, 'ja olen kindel selles, et See, Kes teis on alustanud hea töö, viib selle lõpule Kristuse Jeesuse päevani.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 7, 'Nõnda ongi mulle õige seda teist kõigist mõtelda, sest ma kannan teid oma südames, kes te kõik siis, kui ma olen ahelais, ja ka siis, kui kaitsen Evangeeliumi ja seda kinnitan, ühes minuga olete armu osalised.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 8, 'Sest Jumal on mu tunnistaja, kuidas ma teid kõiki igatsen Kristuse Jeesuse südamlikkusega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 9, 'Ja seda ma palun Jumalalt, et teie armastus veel rohkem ja rohkem kasvaks selges tunnetamises ja kõiges tajumises,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 10, 'et te võiksite kindlaks teha, mis on peaasi, ja oleksite puhtad ning laitmatud Kristuse päevaks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 11, 'täis õiguse vilja, mis tuleb Jeesuse Kristuse läbi, Jumala austuseks ja kiituseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 12, 'Aga ma tahan teada anda teile, vennad, et see, mis minuga on juhtunud, on andnud Evangeeliumile veel rohkem hoogu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 13, 'nõnda et kogu kohtukojas ja kõigile muile on saanud avalikuks, et ma olen ahelais Kristuse pärast');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 14, 'ja et suurem osa vendi mu ahelate tõttu on saanud vahvaks Issandas ja palju rohkem julgevad kartmatult rääkida Jumala sõna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 15, 'Mõned küll kuulutavad Kristust kadeduse ja riiu pärast, kuid mõned ka hea meelega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 16, 'Teised kuulutavad armastusest, teades, et ma viibin siin Evangeeliumi kaitsmiseks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 17, 'teised aga kuulutavad Kristust kiusu pärast, mitte puhtast meelest; nad ju mõtlevad suurendada minu vangipõlve viletsust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 18, 'Aga mis sellest? Kui aga Kristust kuulutatakse igal kombel, olgu silmakirjaks, olgu tõemeelega, ja sellest ma rõõmutsen ja tahan ka edaspidi rõõmutseda;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 19, 'sest ma tean, et see tuleb mulle päästeks teie palve kaudu ja Jeesuse Kristuse Vaimu abil');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 20, 'minu kindlat ootust ja lootust mööda, et ma milleski ei jää häbisse, vaid et Kristus nüüdki, nagu ikka, täie julgusega saab auliseks minu ihus, olgu elu või surma läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 21, 'Sest minule on elamine Kristus ja suremine kasu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 22, 'Aga kui lihas elamine on tulusam mu tööle, siis ma ei tea, kumba valida.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 23, 'Mind tõmbab nii see kui teine: ma himustan siit lahkuda ja olla Kristuse juures, sest see on hoopis palju parem;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 24, 'aga lihasse jäämine on vajalikum teie pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 25, 'Ja selles veendumuses ma tean, et ma jään lihasse ja jään teie kõikide juurde teie usu eduks ja rõõmuks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 26, 'et teie kiitlemine oleks rohke Kristuses Jeesuses minu läbi selle tõttu, et ma jälle tulen teie juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 27, 'Ainult käituge Kristuse Evangeeliumi vääriliselt, et ma, olgu tulles ja teid nähes või tulemata jäädes, kuuleksin teist, et te püsite ühes vaimus ja ühel meelel minuga võitlete Evangeeliumi usu eest');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 28, 'ega lase endid heidutada mitte millestki vastaste poolt. See on neile hukatuse, teile aga pääste tähiseks, ja nimelt Jumalalt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 29, 'Sest teile on armust antud Kristuse pärast mitte ainult uskuda Temasse, vaid ka kannatada Tema pärast');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 30, 'ja võidelda sama võitlust, mida te olete näinud minust ja nüüd minust kuulete.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 1, 'Kui nüüd mingisugune manitsus Kristuses, kui mingisugune armastuse troost, kui mingisugune Vaimu osadus, kui mingisugune südamlikkus ja kaastundmus kehtib,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 2, 'siis tehke mu rõõm täielikuks sellega, et mõtlete sama ja peate ühesugust armastust, olles üksmeelsed ja ühemõttelised');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 3, 'ega tee midagi riiu ega tühja au pärast, vaid arvate alanduses üksteist ülemaks kui iseennast,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 4, 'ja et ükski ei vaata selle peale, mis on tulus temale, vaid ka selle peale, mis on tulus teistele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 5, 'Olgu teil samasugune meel, mis oli ka Kristusel Jeesusel,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 6, 'Kes, kuigi ta oli Jumala nägu, ei arvanud saagiks olla Jumalaga ühesugune,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 7, 'vaid loobus Iseenese olust ning võttis orja näo, saades inimeste sarnaseks; ja Ta leiti välimuselt inimesena;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 8, 'Ta alandas iseennast, saades sõnakuulelikuks surmani, pealegi ristisurmani.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 9, 'Sellepärast ongi Jumal Tema ülendanud kõrgele ja andnud Temale nime üle kõigi nimede,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 10, 'et Jeesuse nimes nõtkuksid kõik põlved, niihästi taevaliste kui maapealsete kui ka maa-aluste omad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 11, 'ja iga keel tunnistaks, et Jeesus Kristus on Issand, Jumala Isa auks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 12, 'Nõnda siis, mu armsad, otsekui te ikka olete olnud sõnakuulelikud, mitte üksnes nagu siis, kui ma olin teie juures, vaid ka nüüd veel rohkem minu ära olles, nõudke oma päästet kartuse ja värinaga!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 13, 'Sest Jumal on See, kes teis on tegev, et te tahate ja tegutsete Tema hea meele järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 14, 'Tehke kõik nurisemata ja kaksipidi mõtlemata,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 15, 'et te oleksite laitmatud ja puhtad, veatud Jumala lapsed keset tigedat ja pöörast sugupõlve, kelle seas teie paistate otsekui taevatähed maailmas,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 16, 'hoides elu sõna, minule kiitluseks Kristuse päeval, et ma ei ole tühja jooksnud ega teinud tühja tööd.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 17, 'Ja kuigi minu veri peaks kahjana valatama teie usu ohvri ja jumalateenistuse üle, siiski ma rõõmutsen ja olen rõõmus ühes teie kõikidega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 18, 'Sellesama pärast olge ka teie rõõmsad ja rõõmutsege ühes minuga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 19, 'Ma loodan Issandas Jeesuses Timoteose varsti läkitada teie juurde, et ka minul oleks hea meel teada saades, kuidas teie käsi käib.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 20, 'Sest mul pole kedagi muud nii üksmeelset minuga, kes nõnda tõsiselt hoolitseks teie eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 21, 'Sest nad kõik otsivad eneste oma, aga mitte seda, mis on Kristuse Jeesuse Oma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 22, 'Aga tema ustavust te teate, sest nagu laps oma isaga, nõnda on ta minuga ühes töötanud Evangeeliumi kasuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 23, 'Teda ma loodan nüüd läkitada, niipea kui näen, kuidas minu asi areneb.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 24, 'Ent mul on kindel lootus Issandas, et ma ka ise varsti tulen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 25, 'Aga ma olen arvanud tarviliseks läkitada teie juurde vend Epafroditose, oma kaastöölise ja kaasvõitleja, teie Apostli ja minu vajaduste eest hoolitseja,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 26, 'sellepärast et ta teid kõiki igatses ja suurt tuska tundis sellest, et olite kuulnud tema haigestumisest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 27, 'Sest tema oli ka tõesti suremas haige; ent Jumal andis temale armu, aga mitte üksi temale, vaid ka minule, et mulle ei tuleks kurbust kurbuse peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 28, 'Sellepärast ma läkitasin tema seda nobedamini, et te teda nähes jälle rõõmustuksite ja mul oleks vähem kurbust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 29, 'Siis võtke ta vastu Issandas kõige rõõmuga ja pidage niisuguseid mehi kalliks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 30, 'Sest Kristuse töö pärast oli ta surma suus ega hoolinud oma elust, et täita seda, mis teil jäi tegemata hoolitsemises minu eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 1, 'Peale kõige, mu vennad, rõõmutsege Issandas! Üht ja sama kirjutades ei tüüta see mind, aga teid see kinnitab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 2, 'Pidage silmas koeri, pidage silmas kurje töötegijaid, hoiduge katkilõikamise eest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 3, 'Sest ümberlõigatud, need oleme meie, kes Jumalat teenime vaimus, ja kiitleme Kristusest Jeesusest ega looda liha peale,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 4, 'ehkki mul oleks lootust liha peale. Kui keegi muu arvab, et tema võib liha peale loota, siis mina veel rohkem!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 5, 'Ma olen ümber lõigatud kaheksandal päeval, ma olen Iisraeli soost, Benjamini suguharust, ma olen heebrealane, sündinud heebrealasist, käsuõpetuselt variser,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 6, 'innukas koguduse tagakiusaja, laitmatu käsuõiguse poolest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 7, 'Aga mis mulle oli kasuks, olen ma arvanud kahjuks Kristuse pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 8, 'Jah tõesti, mina arvan kõik kahjuks oma Issanda Kristuse Jeesuse ülivõimsa tunnetuse vastu, Kelle pärast ma olen minetanud kõik selle ja pean kõike pühkmeiks, et kasuks saada Kristust');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 9, 'ja et mind leitaks Tema seest ega oleks mul oma õigust, mis on käsust, vaid see õigus, mis tuleb Kristuse usu kaudu, see, mis tuleb Jumalalt usu varal,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 10, 'et ma tunneksin ära Tema ja Ta ülestõusmise väe ja Tema kannatamise osaduse ning muutuksin Tema surma sarnaseks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 11, 'kui ma kuidagi pääsen ülestõusmisele surnute seast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 12, 'Ei mitte, et ma selle juba oleksin kätte saanud või oleksin juba täiuslik, vaid ma püüan kätte saada seda, mille pärast Kristus Jeesus mind on kätte saanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 13, 'Vennad, mina ei arva seda juba kätte saanud olevat; ent üht ma ütlen: ma unustan ära, mis on taga, ja sirutun sinnapoole, mis on ees,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 14, 'ma pürin seatud eesmärgi poole, taevase kutsumise võiduhinna poole Kristuses Jeesuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 15, 'Nii mitu nüüd, kui meid on täiuslikku, mõelgem sama! Ja kui te midagi mõtlete teisiti, siis Jumal ilmutab teile ka selle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 16, 'Ometi, kuhu oleme jõudnud, sealt käigem sama teed!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 17, 'Järgige minu eeskuju, vennad, ja vaadelge neid, kes elavad nõnda nagu meie teile eeskujuks oleme.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 18, 'Sest paljud, kellest ma teile olen sagedasti öelnud ja nüüd ütlen ka nuttes, elavad Kristuse risti vaenlastena.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 19, 'Nende ots on hukatus, nende jumal on nende kõht ja nende au on nende häbis; nad taotlevad maapealseid asju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 20, 'Sest meie ühiskond on teavas, kust me ka ootame Issandat Jeesust Kristust kui Õnnistegijat,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 21, 'Kes meie alanduse ihu muudab Oma äraseletatud ihu sarnaseks väge mööda, millega Ta võib ka kõik teha Oma alamaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 1, 'Nõnda, mu armsad ja igatsetud vennad, minu rõõm ja pärg, seiske siis Issandas kindlatena, armsad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 2, 'Euodiat ma manitsen ja ma manitsen Süntühhet olema ühemõttelised Issandas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 3, 'Ja ma palun ka sind, tõsine kaastööline, aita neid naisi, kes on võidelnud Evangeeliumi eest ühes minuga ja Kleemensiga ja mu teiste abimeestega, kelle nimed on eluraamatus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 4, 'Olge ikka rõõmsad Issandas! Ja taas ma ütlen: olge rõõmsad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 5, 'Teie leebus saagu teatavaks kõigile inimestele. Issand on lähedal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 6, 'Ärge muretsege ühtigi, vaid laske kõiges oma palumised palve ja anumisega ühes tänuga saada Jumalale teatavaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 7, 'Ja Jumala rahu, mis on ülem kõigest mõistusest, hoiab teie südamed ja mõtted Kristuses Jeesuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 8, 'Viimaks, vennad, kõik, mis on tõsine, mis aus, mis õige, mis kasin, mis armas, mis on hea kuulda kui vooruslik komme ja kiituse väärt, sellele mõelge!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 9, 'Mis te ka olete õppinud ja saanud ja kuulnud ja näinud minult, seda tehke; ja rahu Jumal on siis teiega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 10, 'Väga suureks rõõmuks Issandas aga oli mulle see, et te juba uuesti virgusite mõtlema minule: sellele te mõtlesite ennegi, aga teil ei olnud juhust selleks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 11, 'Ma ei ütle seda puuduse pärast; sest ma olen õppinud olema rahul sellega, mis mul on.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 12, 'Oskan elada vähesega ja oskan elada külluses, olen kõigega ja kõigi oludega harjunud: nii olema söönud kui ka nägema nälga, elama nii külluses kui ka puuduses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 13, 'Ma suudan kõik Temas, Kes mind teeb vägevaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 14, 'Siiski te tegite hästi, et mind toetasite minu viletsuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 15, 'Ent teiegi, filiplased, teate, et armuõpetuse algusaegadest, kui ma läksin teele Makedooniast, ükski Kogudus ei olnud osaduses minuga andmise ja võtmise arvepidamises kui ainult teie,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 16, 'sest Tessaloonikassegi te saatsite minu tarviduseks ühe ja teise korra.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 17, 'Mitte, et ma andi püüan, vaid ma otsin vilja, mis kasuks oleks teie arvele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 18, 'Mul on nüüd kõike ja mul on rohkesti; ma sain küllalt, kui ma Epafroditoselt võtsin vastu, mis teie olite lähetanud magusa lõhnana ja armsa ohvrina, mis on Jumalale meelepärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 19, 'Aga küll minu Jumal täidab kõik teie vajaduse Oma rikkust mööda auga Kristuses Jeesuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 20, 'Aga Jumalale ja meie Isale olgu austus ajastute ajastuteni! Aamen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 21, 'Tervitage kõiki pühasid Kristuses Jeesuses! Teid tervitavad minu juures olevad vennad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 22, 'Kõik pühad saadavad teile tervitusi, aga iseäranis need, kes on keisri perest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 23, 'Meie Issanda Jeesuse Kristuse arm olgu teie vaimuga!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 1, 'Paulus, Jumala tahtel Jeesuse Kristuse Apostel, ja vend Timoteos,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 2, 'Kolossas elavaile pühadele ja usklikele vendadele Kristuses. Armu teile ja rahu Jumalalt, meie Isalt, ja Issandalt Jeesuselt Kristuselt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 3, 'Me täname Jumalat ja oma Issanda Jeesuse Kristuse Isa, palvetades igal ajal teie eest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 4, 'sest me oleme kuulnud teie usust Kristuses Jeesuses ja teie armastusest kõigi pühade vastu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 5, 'mis teil on taevasse tallele pandud lootuse pärast, millest te varemalt olete kuulnud Evangeeliumi tõesõna kaudu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 6, 'mis on tulnud teie juurde ja nõnda kogu maailmas on kandmas vilja ning kasvab, nõnda ka teie seas alates sellest päevast, mil te kuulsite ja tunnetasite Jumala armu tõe sees,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 7, 'nõnda nagu te seda ka õppisite Epafrase, meie armsa kaassulase käest, kes on ustav Kristuse abiline teie heaks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 8, 'kes meile ka on teatanud teie armastusest Vaimus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 9, 'Sellepärast ei ole ka meie sellest päevast, mil me seda kuulsime, lakanud palvet tegemast ja palumast teie eest, et teid täidetaks tema tahtmise tunnetusega kõiges vaimulikus tarkuses ja arusaamises,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 10, 'et te elaksite väärikalt Issandale, et Temale kõigiti meeldida, vilja kandes kõiges heas töös ning kasvades Jumala tunnetuses');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 11, 'ja saades vägevaks kõige väega Tema au vägevust mööda kõigeks püsivuseks ja pikaks meeleks, rõõmuga');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 12, 'tänades Isa, Kes teid on teinud kõlbavaks osa saama püha rahva pärandist valguses,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 13, 'Kes meid on päästnud pimeduse võimusest ja on meid asetanud Oma armsa Poja Riiki.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 14, 'Temas on meil lunastus, pattude andekssaamine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 15, 'Tema on nähtamatu Jumala kuju, kõige loodu esmasündinu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 16, 'Sest Tema läbi on loodud kõik, mis on taevastes ja mis on maa peal, mis nähtav ja nähtamatu, olgu aujärjed, ülemused, valitsused, võimud; kõik on loodud Tema läbi ja Temasse;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 17, 'ja Tema Ise on enne kõiki, ja kõik püsivad koos Tema sees;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 18, 'ja Tema on ihu - see on Koguduse - pea; Ta on algus ja esmasündinu surnutest, et Ta kõiges saaks esikohale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 19, 'Sest Jumal arvas heaks kõik täiuse lasta elada Temas');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 20, 'ja tehes rahu ristivere läbi, lepitada Tema läbi Enese poole kõik, niihästi selle, mis on maa peal, kui ka selle, mis on taevas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 21, 'Ja Ta on teid, kes enne olite Temast võõrdunud ja oma meelsuse poolest vaenlased, tehes kurja, nüüd ka lepitatud oma lihalikus ihus surma läbi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 22, 'et seada teid pühadena ja veatuina ja laitmatuina enese ette,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 23, 'kui te aga jääte ususse hästi rajatuina ja kindlaina ega lase endid ära pöörata Evangeeliumi lootusest, mida te olete kuulnud ja mis on kuulutatud kõigele loodule taeva all ja mille teenijaks mina, Paulus, olen saanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 24, 'Nüüd ma olen rõõmus oma kannatamistes teie pärast, ja mis mul veel puudub Kristuse viletsustest, selle ma täiendan omas lihas Tema Ihu heaks, mis on Kogudus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 25, 'kelle teenijaks ma olen saanud Jumala korralduse järgi, mis minu kätte on usaldatud, et teile täielikult kuulutada Jumala sõna:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 26, 'seda saladust, mis oli varjul endiste ajastute ja sugupõlvede eest, aga nüüd on avalikuks tehtud Tema pühadele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 27, 'kellele Jumal tahtis teada anda, milline on selle saladuse auhiilguse rikkus paganate seas - see on Kristus teie sees, auhiilguse lootus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 28, 'Keda me kuulutame, manitsedes igat inimest ja õpetades igat inimest kõiges tarkuses, et valmistada igat inimest täielikuks Kristuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 29, 'Selle jaoks minagi näen vaeva ja võitlen Tema tarmu mööda, mis minus on vägevasti tegev.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 1, 'Sellepärast ma tahan, et teil oleks teada, kui suur võitlus mul on teie ja laodikealaste pärast ja kõikide pärast, kes mu palet lihas ei ole näinud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 2, 'et saaksid kinnitust nende südamed, olles kokku liidetud armastuses ja täieliku mõistmise kogu rikkusesse ning tunnetaksid Jumala saladuse, Kristuse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 3, 'Kelles peituvad kõik tarkuse ja tunnetuse aarded.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 4, 'Seda ma ütlen selleks, et keegi teid ei petaks meelitavate kõnedega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 5, 'Sest kuigi mina liha poolest olen eemal, olen ma siiski vaimus teiega ühes ning olen rõõmus, nähes teie head korda ja teie kindlat usku Kristusesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 6, 'Otsekui te nüüd olete vastu võtnud Kristuse Jeesuse, Issanda, nõnda käige Temas');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 7, 'juurdunuina Temasse ja rajatuina Temale ning kinnitatuina usus, nõnda nagu teid on õpetatud, ja olge ülevoolavad tänus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 8, 'Vaadake, et keegi teid ei võtaks saagiks mõtteteaduse ja tühja pettusega, mis vastavad inimeste pärimusele, maailma algjõududele ja mitte Kristusele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 9, 'Sest Temas elab kõik Jumala olemise täius ihulikult.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 10, 'Ja te olete täidetud Temas, Kes on iga valitsuse ja võimu pea;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 11, 'ja Temas te olete ka ümber lõigatud ümberlõikamisega, mida ei toimetata kätega, vaid lihaliku ihu äraheitmise teel, Kristuse ümberlõikamisega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 12, 'kui teid ühes Temaga maha maeti ristimises, milles teid ühes üles äratati usu läbi, mis on tegev Jumala väes, Kes Tema surnuist üles äratas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 13, 'Ka teid, kes olite surnud üleastumistes ja oma ümberlõikamata lihas, on Ta ühes Temaga teinud elavaks, andes meile andeks kõik üleastumised,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 14, 'kui Ta kustutas meie kohta käiva võlakirja ühes selle sätetega; see oli meie vastu ja selle Ta kõrvaldas, seda risti külge naelutades.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 15, 'Ta riisus ülemustelt ja võimudelt relvad ning tegi nad avaliku pilke alusteks, võidutsedes nende üle Kristuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 16, 'Sellepärast ärgu ükski mõistku kohut teie üle sööma või jooma ja pühade või noorkuu või hingamispäevade pärast,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 17, 'mis on vaid tulevaste asjade vari, kuna ihu on Kristuse oma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 18, 'Ärgu siis teie võiduandi teilt riisugu ükski, kellele meeldib alandlikkus ja Inglite austamine ning kes tungib sellesse, mis ta nägemuses on näinud, lastes ilmaasjata ennast täis puhuda oma lihaliku meele poolt,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 19, 'ja kes ei pea kinni peast, kelles kogu ihu kokku liidetakse ja koos hoitakse kõõluste ja sidemete abil ning kasvab Jumala poolt seatud kasvamist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 20, 'Kui te nüüd ühes Kristusega olete surnud maailma algjõududele, miks te siis, nagu elaksite maailmas, lasete endile teha määrusi:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 21, '\"Ära puuduta! Ära maitse! Ära katsu käega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 22, '- seda, mis kõik on määratud kadumisele kulutamise teel - inimeste käskude ja õpetuste järgi?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 23, 'Sel kõigel on küll tarkuse nägu omatehtud jumalateenistuse, alandlikkuse ja ihu kurnamise tõttu, aga mis on ilma erilise väärtuseta ja mis sünnib liha rahuldamiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 1, 'Kui te nüüd ühes Kristusega olete surnuist üles tõusnud, siis otsige seda, mis on ülal, kus Kristus on istumas Jumala paremal käel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 2, 'Mõelge sellele, mis on ülal, mitte sellele, mis on maa peal!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 3, 'Sest teie olete surnud ja teie elu on varjul ühes Kristusega Jumalas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 4, 'Kui Kristus, meie elu, saab avalikuks, siis saate ka teie ühes Temaga avalikuks au sees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 5, 'Sellepärast surmake oma maapealsed liikmed: hoorus, roppus, kired, pahad himud ja ahnus, mis on ebajumalateenistus;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 6, 'nende pärast tuleb Jumala viha;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 7, 'nende sees käisite muiste ka teie, kui elasite neis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 8, 'Ent nüüd jätke teiegi maha see kõik: viha, ärritatavus, kurjus, pilkamine, ropud sõnad oma suust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 9, 'Ärge rääkige valet üksteisest, sest te olete endid lahti riietanud vanast inimesest tema tegudega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 10, 'ja olete riietunud uude inimesse, kes on uuendatud õigesti tunnetama oma Looja kuju järgi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 11, 'kus ei ole kreeklast ega juuti, ei ümberlõigatut ega ümberlõikamatut, ei harimatut ega sküüti, ei orja ega vaba, vaid kõik ja kõikide sees on Kristus!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 12, 'Riietuge siis kui Jumala valitud pühad ja armastatud südamliku halastusega, heldusega, alandusega, tasadusega, pika meelega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 13, 'sallides üksteist ja andeks andes üksteisele, kui kellelgi on kaebust teise peale. Nõnda nagu Kristus teile on andeks andnud, nõnda tehke ka teie.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 14, 'Aga kõigele sellele lisaks olgu armastus; see on täiuslik side.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 15, 'Ja Kristuse rahu valitsegu teie südameis, sest selleks te olete kutsutud ühes ihus. Ja olge tänulikud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 16, 'Kristuse sõna elagu rohkesti teie seas. Kõige tarkusega õpetage ja juhatage üksteist psalmidega ja kiituslauludega ja vaimulike lauludega, tänumeeles lauldes Issandale oma südames.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 17, 'Ja kõik, mida te iganes teete sõnaga või tööga, seda tehke kõik Issanda Jeesuse nimel, tänades Jumalat Isa Tema läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 18, 'Naised, olge meestele allaheitlikud, nõnda nagu on kohus Issandas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 19, 'Mehed, armastage oma naisi ja ärge olge karmid nende vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 20, 'Lapsed, olge oma vanemaile sõnakuulelikud kõigis asjus, sest see on Issandale meelepärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 21, 'Isad, ärge ärritage oma lapsi, et nad ei läheks araks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 22, 'Orjad, olge sõnakuulelikud kõigis asjus neile, kes isandad on liha poolest, mitte silmakirjaks nagu need, kes püüavad olla inimestele meelepärast, vaid siirast südamest, kartes Issandat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 23, 'Mida te iganes teete, seda tehke südamest, nõnda nagu Issandale ja mitte nagu inimestele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 24, 'teades, et te saate Issandalt pärandi palga. Te teenite Issandat Kristust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 25, 'Aga kes teeb ülekohut, saab kätte, mis ta on ülekohut teinud, ja ei tehta vahet isikute vahel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 1, 'Isandad, tehke oma orjadele, mis on õige ja kohus, ja teadke, et ka teil on Issand taevas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 2, 'Jääge kindlasti palvesse ja valvake palves tänuga,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 3, 'paludes ühtlasi ka meie eest, et Jumal meile avaks sõna ukse, et saaksime rääkida Kristuse saladust, mille pärast mina olengi seotud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 4, 'et ma seda võiksin avaldada, nõnda nagu mul tuleb kõnelda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 5, 'Kohelge targasti neid, kes on väljaspool, ja pidage aega kalliks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 6, 'Teie kõne olgu alati armu sees, soolaga soolatud, et te teaksite, kuidas teil tuleb igaühele kosta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 7, 'Kuidas mu käsi käib, küll seda kõike teatab teile Tühhikos, armas vend ja ustav abiline ning kaassulane Issandas,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 8, 'kelle ma praegu läkitan teie juurde selle tarvis, et te saaksite teada, kuidas meie käsi käib, ja et ta kinnitaks teie südameid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 9, 'ühes ustava ja armsa venna Oneesimosega, kes on teie seast. Küll nad annavad teile teada, kuidas siin kõigega lugu on.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 10, 'Teile saadab tervisi Aristarhos, mu vangiseltsiline, ja Markus, Barnabase õepoeg, kelle kohta te juba olete saanud juhatusi, et kui ta tuleb teie juurde, te võtaksite ta vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 11, 'Ja teid tervitab Jeesus, keda hüütakse Justuseks. Need on ümberlõigatuist olnud ainsad kaastöölised Jumala Riigi alal, ja nad on olnud mulle troostiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 12, 'Teid tervitab Epafras, kes on teie seast, Kristuse Jeesuse sulane, kes alati võitleb teie eest palveis, et te püsiksite täiuslikena ja oleksite täidetud kõige Jumala tahtmisega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 13, 'Sest ma annan temale selle tunnistuse, et ta kannab väga suurt hoolt teie eest ja nende eest, kes on Laodikeas ja kes on Hierapolises.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 14, 'Teid tervitab arst Luukas, kes mulle on armas, ja Deemas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 15, 'Tervitage vendi Laodikeas ja Nümfat ja kogudust ta majas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 16, 'Ja kui see kiri on loetud teie juures, siis tehke nii, et seda loetaks ka Laodikea Koguduses, ja et teie loeksite ka Laodikeast tulevat kirja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 17, 'Ja öelge Arhipposele: \"Pea silmas oma ametit, mille sa oled saanud Issandalt, et sa seda täidaksid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 18, 'See tervitus on minu, Pauluse, oma käega. Mõelge mu ahelatele! Arm teiega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 1, 'Paulus ja Silvaanus ja Timoteos tessalooniklaste Kogudusele Jumalas Isas ja Issandas Jeesuses Kristuses. Armu teile ja rahu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 2, 'Me täname alati Jumalat teie kõikide eest ning tuletame teid meelde oma palvetes, lakkamatult');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 3, 'meenutades teie usu tegu ja armastuse tööd ning kannatlikkust lootuses meie Issandale Jeesusele Kristusele Jumala ja meie Isa ees,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 4, 'teades, et teie, Jumala armastatud vennad, olete valitud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 5, 'Sest meie Evangeelium ei tulnud teie juurde mitte ainult sõnaga, vaid ka väega ning Püha Vaimuga ja väga kindla veendumusega, nagu te ju isegi teate, missugused me olime teie seas teie pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 6, 'Ja teist said meie ja Issanda järgijad, kui te võtsite sõna vastu suures viletsuses Püha Vaimu rõõmuga,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 7, 'nii et te olete saanud eeskujuks kõigile usklikele Makedoonias ja Ahhaias.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 8, 'Sest teie juurest on Issanda sõna kostnud mitte ainult Makedooniasse ja Ahhaiasse, vaid ka igale poole on levinud teie usk Jumalasse, nõnda et meil pole tarvis sellest midagi rääkida.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 9, 'Ise nad ju jutustavad meist, kuidas me oleme tulnud teie juurde ja kuidas te olete pöördunud ebajumalaist Jumala poole teenima elavat ja tõelist Jumalat');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 10, 'ning ootama taevast Tema Poega, Kelle Tema on surnuist üles äratanud, Jeesust, Kes meid tõmbab välja tulevasest vihast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 1, 'Teie ise ju teate, vennad, et meie tulek teie juurde ei ole olnud tühine;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 2, 'vaid, kuigi meie, nagu te teate, olime kannatanud Filipis ja saanud teotada, võtsime siiski oma Jumalas julguse kuulutada teile Jumala Evangeeliumi suure võitlusega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 3, 'Sest meie manitsus ei tule mitte eksimeelest, ei ebapuhtast mõttest ega kavalusest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 4, 'vaid nõnda nagu Jumal on meid arvanud kõlbavaks usaldada Evangeelium meie kätte, nõnda me ka räägime, ei mitte, nagu tahaksime olla inimestele, vaid Jumalale meelepärast, Kes katsub läbi meie südamed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 5, 'Sest me ei ole iialgi tarvitanud meelitavaid sõnu, nagu te teate, ega ka ettekäändeid ahnuseks: Jumal on tunnistaja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 6, 'Me ei otsi ka austust inimestelt, ei teilt ega muilt,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 7, 'kuigi me Kristuse Apostlitena oleksime võinud esineda tähtsatena; vaid me oleme saanud leebeiks teie seas nagu imetaja ema, kes hellitab oma lapsi;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 8, 'nõnda meie, teid ihaldades, olime valmis teile andma mitte ainult Jumala Evangeeliumi, vaid ka oma hinged, sest te olite meile armsaks saanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 9, 'Te mäletate ju, vennad, meie tööd ja vaeva: ööd ja päevad me tegime tööd selleks, et mitte kedagi teie seast koormata, ja kuulutasime teie seas Jumala Evangeeliumi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 10, 'Teie ja Jumal olete tunnistajad, kui pühasti ja õigesti ja laitmatult me suhtusime teisse usklikesse;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 11, 'nagu te ka teate, kuidas me igaüht teie seast otsekui isa oma lapsi');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 12, 'õhutasime ja julgustasime, teid manitsedes elama nõnda, kuidas kohus on Jumala ees, Kes teid kutsub Oma Riiki ja ausse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 13, 'Ja seepärast meiegi täname lakkamatult Jumalat, et teie, kuuldes meilt Jumala sõna, ei võtnud seda vastu mitte inimeste sõnana, vaid sellena, mida see tõesti on, Jumala sõnana, mis ka on tegev teie sees, kes usute.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 14, 'Sest teie, vennad, olete saanud nende Kristuses Jeesuses olevate Jumala Koguduste järgijaiks, mis on Judeas, sest ka teie olete saanud sedasama kannatada oma suguvendade poolt, nõnda nagu nemadki juutide poolt,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 15, 'kes tapsid Issanda Jeesuse ja prohvetid, ja kiusasid meid taga, ja ei ole Jumalale meelepärast, ja on kõigi inimeste vastased');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 16, 'ning keelavad meid rääkimast paganaile nende päästmiseks, et täita kõigiti oma pattude mõõtu. Aga viha ongi juba tulnud nende peale lõpuni.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 17, 'Meie aga, vennad, olles teist lahutatud natukeseks ajaks küll palge, mitte aga südame poolest, oleme seda suurema himuga püüdnud näha saada teie palet.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 18, 'Seepärast oleme tahtnud tulla teie juurde, mina, Paulus, küll üks kord ja teinegi kord, aga saatan on meid takistanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 19, 'Sest kes on meie lootus või rõõm või kiitlemise pärg - kas mitte teiegi - meie Issanda Jeesuse Kristuse ees Tema tulemises?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 20, 'Sest teie olete meie au ja rõõm.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 1, 'Sellepärast meie, kui me enam ei läbenud kannatada, arvasime heaks jääda üksinda Ateenasse');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 2, 'ja läkitasime Timoteose, oma venna ja Jumala kaastöölise Kristuse Evangeeliumi kuulutamises, teid kinnitama ja manitsema teie usu pärast,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 3, 'et ükski ennast ei laseks vintsutada neis viletsusis. Sest te isegi teate, et meid on neisse pandud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 4, 'Sest ka siis, kui olime teie juures, ütlesime teile ette, et meid vaevad ootavad, nõnda nagu ongi sündinud ja on teile teada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 5, 'Sellepärast ka mina, kui ma enam ei läbenud, läkitasin tema kuulama teie usku, kas ehk vahest kiusaja ei ole teid kiusanud ja kas meie vaev ei ole saanud tühjaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 6, 'Aga kui Timoteos nüüd tuli meie juurde teie juurest ja tõi rõõmsaid sõnumeid teie usust ja armastusest ja et te peate meid alati heas mälestuses ning igatsete meid näha saada, nõnda nagu meiegi teid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 7, 'siis oleme trööstitud teie suhtes, vennad, kõiges oma hädas ja kitsikuses teie usu läbi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 8, 'sest et nüüd me elame, kui teie püsite kindlasti Issandas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 9, 'Sest missugust tänu me võimegi anda Jumalale teie pärast kõige selle rõõmu eest, mis meil on teist oma Jumala ees,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 10, 'kuna me ööd ja päevad oleme kõige palavamas palves, et saaksime näha teie palet ja täita seda, mis teie usul veel on puudu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 11, 'Aga meie Jumal ja Isa Ise ja meie Issand Jeesus Kristus juhtigu meie tee teie juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 12, 'Ent teid kasvatagu Issand ja tehku teid rikkaks armastuse poolest üksteise vastu ja kõikide vastu, nagu meiegi oleme teie vastu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 13, 'et kinnitada teie südamed laitmatuiks pühitsuses Jumala ja meie Isa ees meie Issanda Jeesuse tulekul kõigi ta pühadega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 1, 'Peale selle nüüd, vennad, me palume ja manitseme teid Issandas Jeesuses, et teie, nõnda nagu te meilt olete õppinud, kuidas elada ja olla Jumalale meeltmööda, ja nõnda nagu te juba elategi, kasvaksite veel täiuslikumaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 2, 'Sest te teate, mis käsud me teile oleme andnud Issanda Jeesuse läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 3, 'Sest see on Jumala tahtmine, teie pühitsus, et te hoiduksite hooruse eest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 4, 'et igaüks teie seast teaks hoida oma astjat pühaduses ja aus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 5, 'mitte himude kiimas, nõnda nagu paganad, kes ei tunne Jumalat,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 6, 'et ükski teist ei teeks ülekohut ega petaks oma venda asjaajamises, sest et Issand on kättemaksja kõigi niisuguste asjade eest, nõnda nagu me olemegi teile enne öelnud ning tunnistanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 7, 'Sest Jumal ei ole meid mitte kutsunud rüvedusele, vaid pühitsusele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 8, 'Seepärast, kes seda hülgab, ei hülga inimest, vaid Jumala, Kes ka annab Oma Püha Vaimu teie sisse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 9, 'Aga vennalikust armastusest ei ole vaja teile kirjutada, sest teid endid on Jumal õpetanud armastama üksteist;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 10, 'sest te teete seda ka kõigile vendadele kogu Makedoonias. Me ainult manitseme teid, vennad, et te kasvaksite veel täiuslikumaks');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 11, 'ja otsiksite au selles, et elate vaiksesti ja ajate oma asju ning teete tööd oma kätega, nõnda nagu me oleme teid käskinud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 12, 'et te käitumises oleksite ausad nende vastu, kes on väljaspool, ega vajaks ühegi abi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 13, 'Aga me ei taha, et teil, vennad, oleks teadmata nende järg, kes on läinud magama, et teiegi ei läheks nõnda kurvaks nagu teised, kellel ei ole lootust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 14, 'Sest kui me usume, et Jeesus on surnud ning üles tõusnud, nõnda ka Jumal toob esile Jeesuse kaudu need, kes ühes Temaga on läinud magama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 15, 'Sest seda me ütleme teile Issanda sõnaga, et meie, kes elame ja üle jääme Issanda tulemiseni, ei jõua mitte ette neist, kes on läinud magama;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 16, 'sest et Issand Ise tuleb taevast alla sõjahüüuga, peaingli hääle ning Jumala pasunaga, ja Kristuses surnud tõusevad üles esmalt;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 17, 'selle järele kistakse meid, kes elame ja üle jääme, ühtlasi nendega pilvede peal Issandale vastu üles õhku, ja nõnda saame olla ikka ühes Issandaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 18, 'Kinnitage nüüd üksteist nende sõnadega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 1, 'Aga aegadest ja tundidest ei ole teile, vennad, tarvis kirjutada,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 2, 'sest te ise teate selgesti, et Issanda päev tuleb nagu varas öösel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 3, 'Kui nad ütlevad: \"Nüüd on rahu ja julgeolek!\", tabab neid äkiline hukatus nõnda nagu lapsevaev naise, kes on käima peal; ja nad ei pääse mitte pakku.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 4, 'Aga teie, vennad, ei ole mitte pimeduses, nii et see päev teid saaks haarata kui varas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 5, 'Teie kõik olete ju valguse lapsed ja päeva lapsed. Meie ei ole mitte öö ega pimeduse lapsed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 6, 'Siis ärgem magagem nagu teised, vaid valvakem ja olgem kained.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 7, 'Sest kes magavad, need magavad öösel, ja kes joobnud on, need on öösel joobnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 8, 'Aga meie, kes oleme päeva lapsed, olgem kained, varustatud usu ja armastuse raudrüüga ja päästelootuse kiivriga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 9, 'Sest Jumal ei ole meid pannud viha alla, vaid pääste omandamisele meie Issanda Jeesuse Kristuse läbi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 10, 'Kes meie eest suri, et meie, kas valvame või magame, ühes Temaga elaksime.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 11, 'Sellepärast manitsege üksteist ja kosutage üksteist, nagu te seda teetegi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 12, 'Aga me palume teid, vennad, tunnustada neid, kes teevad tööd teie seas ja on teie ülevaatajad Issandas ning juhatavad teid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 13, 'ja pidada neid üpris väga armsaks nende töö pärast. Pidage rahu isekeskis!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 14, 'Me manitseme teid, vennad: noomige korratuid, julgustage argu, toetage nõtru, olge pika meelega kõikide vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 15, 'Katsuge, et ükski teisele ei tasu kurja kurjaga, vaid püüdke ikka teha head üksteisele ja kõikidele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 16, 'Olge ikka rõõmsad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 17, 'Palvetage lakkamata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 18, 'Olge tänulikud kõige eest; sest see on Jumala tahtmine teie suhtes Kristuses Jeesuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 19, 'Ärge kustutage Vaimu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 20, 'Ärge pange halvaks prohvetlikult kõnelemist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 21, 'Katsuge kõike läbi; pidage kinni, mis hea on.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 22, 'Hoiduge kõiksuguse kurja eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 23, 'Aga rahu Jumal Ise pühitsegu teid täielikult ja kogu teie vaim ja hing ja ihu säiligu laitmatuina meie Issanda Jeesuse Kristuse tulekuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 24, 'Ustav on See, Kes teid kutsub; küll Ta teebki seda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 25, 'Vennad, palvetage meie eest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 26, 'Tervitage kõiki vendi püha suudlusega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 27, 'Ma vannutan teid Issanda juures, et te selle kirja ette loeksite kõigile vendadele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 28, 'Meie Issanda Jeesuse Kristuse arm olgu teiega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 1, 'Paulus ja Silvaanus ja Timoteos tessalooniklaste Kogudusele Jumalas, meie Isas, ja Issandas Jeesuses Kristuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 2, 'Armu teile ja rahu Jumalalt, meie Isalt, ja Issandalt Jeesuselt Kristuselt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 3, 'Me oleme alati kohustatud Jumalat tänama teie eest, vennad, nõnda nagu on sünnis, sest et teie usk vägevasti kasvab ja armastus jõudsasti edeneb igaühes teie kõikide seas üksteise vastu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 4, 'nõnda et me isegi teist kiitleme Jumala Kogudustes, teie kannatlikkusest ja usust, kõigis teie tagakiusamistes ja viletsusis, mida te kannatate');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 5, 'Jumala õige kohtu tõestuseks, et teid arvataks Jumala riiki väärt olevat, mille pärast te ka kannatate,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 6, 'kui see kuidagi on õige Jumala ees, et teie vaevajaile tasutaks vaevamisega');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 7, 'ja teile, keda vaevatakse, antaks hingamist ühes meiega, kui Issand Jeesus ilmub taevast Oma väe Inglitega');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 8, 'tuleleegis ja maksab kätte neile, kes ei tunne Jumalat ja kes ei alistu meie Issanda Jeesuse Kristuse Evangeeliumile,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 9, 'kes saavad nuhtluseks igavese hukatuse, hülgamise Issanda palge eest ja Tema vägevuse auhiilgusest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 10, 'kui Ta sel päeval tuleb, et austatud olla Oma pühades ja imestatav kõigi usklike seas, sest et meie tunnistust teie kohta on ustud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 11, 'Selleks me palumegi alati teie eest, et meie Jumal peaks teid kutsumisväärseks ja teeks täielikuks kõik teie hea meele tubliduse ja teie usu teo väes,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 12, 'et meie Issanda Jeesuse Kristuse nime austataks teie sees ja teid Tema sees meie Jumala ja meie Issanda Jeesuse Kristuse armu mööda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 1, 'Aga me palume teid, vennad, meie Issanda Jeesuse Kristuse tulemise ja meie kokkusaamise pärast Temaga,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 2, 'et te ei annaks endid niipea kõigutada sinna ja tänna eemale õigest meelest ega heidutada ei vaimu läbi ega sõna läbi või kirja läbi, otsekui oleks see meie lähetatud, nagu peaks Issanda päev olema käes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 3, 'Ärgu ükski teid petku mingil kombel; sest see päev ei tule mitte enne, kui on tulnud ärataganemine ja saanud avalikuks ülekohtu-inimene, hukatusepoeg,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 4, 'kes paneb vastu ja tõstab enese üle kõige, mida nimetatakse Jumalaks või jumalateenistuseks, nii et ta istub Jumala templisse ja ütleb enese olevat Jumala.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 5, 'Eks te mäleta, et ma seda teile ütlesin, kui olin alles teie juures?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 6, 'Ja nüüd te teate, mis takistab teda saamast avalikuks omal ajal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 7, 'Sest ülekohtu saladus on juba mõjumas; ainult peab enne vahelt kõrvaldatama see, kes teda siiamaani takistab;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 8, 'ja siis saab avalikuks ülekohtune - kelle Issand Jeesus hävitab Oma suu vaimuga ja kellele ta teeb otsa Oma tulemise ilmumisega -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 9, 'kelle tulemine sünnib saatana võimu mõjustusel igasuguses valeväes ja valetunnustähtedega ja valeimetegudega');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 10, 'ja igasuguse ülekohtu pettuses neile, kes hukka lähevad, sellepärast et nad ei võtnud vastu tõearmastust, et nad oleksid võinud pääseda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 11, 'Ja sellepärast Jumal läkitab neile vägeva eksituse, et nad hakkavad uskuma valet,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 12, 'et mõistetaks hukka kõik need, kes ei ole uskunud tõde, vaid kellel on olnud hea meel ülekohtust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 13, 'Aga me peame alati tänama Jumalat teie eest, Issanda armastatud vennad, et Jumal algusest teid on valinud õndsuseks Vaimu pühitsemise ja tõeusu kaudu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 14, 'milleks ta on teidki kutsunud meie Evangeeliumi läbi, et te kätte saaksite meie Issanda Jeesuse Kristuse au.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 15, 'Siis seiske nüüd, vennad, ja pidage kinni neist õpetusist, mis te meilt olete õppinud suusõnal või kirja teel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 16, 'Aga meie Issand Jeesus Kristus Ise ja Jumal, meie Isa, Kes meid on armastanud ja meile on andnud igavese troosti ja hea lootuse armuannina,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 17, 'trööstigu teie südameid ja kinnitagu teid igasuguses heas teos ja sõnas!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 1, 'Lõppeks, vennad, paluge meie eest, et Issanda sõna leviks ja et see tõuseks ausse nõnda nagu teiegi juures,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 2, 'ja et me pääseksime nurjatute ja kurjade inimeste käest. Sest usk ei ole mitte igaühe oma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 3, 'Aga Issand on ustav, Kes teid kinnitab ja kurja eest hoiab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 4, 'Ja me loodame teist Issandas, et te nüüd ja ka edaspidi teete, mida me käsime.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 5, 'Ent Issand juhtigu teie südamed Jumala armastuse ja Kristuse kannatlikkuse poole.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 6, 'Aga me käsime teid, vennad, meie Issanda Jeesuse Kristuse nimel hoiduda igast vennast, kes elab korratut elu ja mitte seda pärimust mööda, mille olete meilt saanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 7, 'Sest te teate ise, kuidas te peate meie eeskuju järgi elama, sest me ei ole elanud korratult teie keskel');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 8, 'ega ole kellegi juures leiba muidu söönud, vaid oleme vaeva ja rängeldamisega ööd ja päevad teinud tööd, et mitte kedagi teie seast koormata;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 9, 'ei mitte, et meil poleks selleks meelevalda, vaid et me annaksime teile head eeskuju käia meie järel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 10, 'Sest ka siis, kui olime teie juures, seadsime nii, et kes ei taha tööd teha, ärgu ka söögu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 11, 'Me kuuleme ju, et mõned teie seast elavad korratut elu ega tee tööd, vaid janditavad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 12, 'Ent niisuguseid me käsime ja manitseme Issandas Jeesuses Kristuses, et nad vaikselt tööd tehes sööksid omaenese leiba!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 13, 'Kuid teie, vennad, ärge tüdige head tegemast!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 14, 'Aga kui keegi meie sõna, mis meie kirja kaudu on öeldud, ei võta kuulda, siis pidage teda silmas ja ärge tehke tegemist temaga, et ta häbeneks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 15, 'Ometi ärge pidage teda vaenlaseks, vaid hoiatage teda kui venda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 16, 'Aga Rahuissand Ise andku teile rahu alati ja igal viisil! Issand olgu teie kõikidega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 17, 'See tervitus on minu, Pauluse käega; see on tunnuseks igas kirjas; nõnda ma kirjutan.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 18, 'Meie Issanda Jeesuse Kristuse arm olgu teie kõikidega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 1, 'Paulus, Kristuse Jeesuse Apostel Jumala, meie Õnnistegija, ja Kristuse Jeesuse, meie lootuse käsul,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 2, 'Timoteosele, oma tõelisele pojale usus. Armu, halastust, rahu Jumalalt Isalt ja Kristuselt Jeesuselt, meie Issandalt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 3, 'Nõnda kuidas ma Makedooniasse minnes sind õhutasin jääma kauemaks Efesosse, et mõningaid manitseda, et nad ei õpetaks teisiti');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 4, 'ega teeks tegemist tühjade juttudega ja lõpmatute suguvõsade üleslugemistega, mis tekitavad rohkem küsimusi kui Jumala majapidamisest arusaamist usus, nõnda tee.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 5, 'Sest käskimise eesmärk on armastus puhtast südamest ja heast südametunnistusest ja silmakirjatsematust usust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 6, 'Neist on mõned ära taganenud ja kaldunud kõrvale tühja kõne poole,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 7, 'tahtes olla käsuõpetajad, kuigi nad ei mõista seda, mida nad räägivad, ega seda, mida nad kindlasti väidavad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 8, 'Me ju teame, et käsk on hea, kui keegi seda käsitleb käsu kohaselt');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 9, 'ja peab meeles, et käsku pole seatud õigele, vaid ülekohtustele ja kangekaelseile, jumalakartmatuile ja patustele, õelatele ja roojastele, isatapjaile ja ematapjaile, mõrtsukaile,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 10, 'hoorajaile, poisipilastajaile, inimesemüüjaile, valelikele, valevandujaile ja kõigele muule, mis käib õige õpetuse vastu -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 11, 'õndsa Jumala au Evangeeliumi järgi, mis on usaldatud minu kätte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 12, 'Ma tänan teda, kes mind on teinud vägevaks, Kristust Jeesust, meie Issandat, et Ta mind on pidanud ustavaks, pannes mind Oma teenistusse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 13, 'ehk ma küll enne olin pilkaja ja tagakiusaja ja vägivaldne. Aga ma olen armu saanud, sest ma olin seda teinud teadmata, uskmatus meeles.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 14, 'Meie Issanda arm aga on olnud üliväga suur usu ja armastusega, mis on Kristuses Jeesuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 15, 'Ustav on see sõna ja kõigiti vastuvõetav, et Kristus Jeesus on tulnud maailma päästma patuseid, kelle seast mina olen suurim;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 16, 'kuid sellepärast ma olen saanud armu, et Jeesus Kristus kõigepealt minus osutaks kõike Oma pikka meelt eeskujuks neile, kes veel edaspidi saavad usklikuks Temasse igaveseks eluks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 17, 'Aga ajastute Kuningale, kadumatule, nähtamatule, ainule Jumalale olgu au ja austus ajastute ajastuteni! Aamen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 18, 'Selle käsu ma panen su südamele, mu poeg Timoteos, vastavalt varemaile sinu kohta käivaile ennustustele, et sa nende varal võitleksid õilsat võitlust,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 19, 'omades usku ja head südametunnistust, mille mõningad on tõuganud enesest ära ja on selle tõttu usu suhtes läinud põhja nagu laev.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 20, 'Nende seast on Hümenaios ja Aleksandros, keda ma olen andnud saatana meelevalda, et nad karistuse all võõrduksid pilkamisest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 1, 'Ma manitsen siis nüüd kõigepealt tegema palveid, palvusi, eestpalveid, tänupalveid kõigi inimeste eest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 2, 'kuningate ja kõigi ülemate eest, et võiksime vaikset ja rahulikku elu elada kõiges jumalakartuses ja aususes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 3, 'Sest see on hea ja armas Jumala, meie Õnnistegija meelest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 4, 'Kes tahab, et kõik inimesed õndsaks saaksid ja tõe tunnetusele tuleksid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 5, 'Sest Jumal on üks, ka vahemees üks Jumala ja inimeste vahel, inimene Kristus Jeesus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 6, 'Kes andis Iseenese lunastushinnaks kõikide eest, et sellest antaks tunnistust parajail aegadel;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 7, 'ja selle tarvis olen mina pandud kuulutajaks ja Apostliks - ma räägin tõtt Kristuses ega valeta mitte - paganate õpetajaks usus ja tões.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 8, 'Ma tahan siis, et mehed palvetaksid igas kogumispaigas ja tõstaksid üles pühad käed ilma vihata ja kahtlemiseta;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 9, 'nõndasamuti ka, et naised, viisakalt riietatud, endid ehiksid häbeliku ja mõistliku meelega, mitte juuksepalmikutega ega kullaga ega pärlitega ega kalliste riietega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 10, 'vaid heade tegudega, nõnda kui sobib naistele, kes endid tunnistavad jumalakartlikeks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 11, 'Naine õppigu vaiksel viisil, olles kõigiti alistuv.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 12, 'Aga naisele ma ei luba õpetada ega valitseda mehe üle, vaid ta elagu vaikselt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 13, 'Sest Aadam loodi enne, siis Eeva;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 14, 'ega Aadamat petetud, vaid naist peteti ja ta sattus üleastumisse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 15, 'aga ta saab õndsaks lastesünnitamise läbi, kui ta jääb ususse ja armastusse ning pühitsusse mõistliku meelega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 1, 'Ustav on see sõna: kui keegi püüab Koguduse ülevaataja ametisse, siis ta igatseb kaunist tööd.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 2, 'Niisiis tuleb Koguduse ülevaatajal olla laitmatu, ühe naise mees, kaine, mõistlik, viisakas, külalislahke, osav õpetama;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 3, 'mitte joodik, mitte riiakas, vaid leebe, rahunõudja, mitte rahaahne,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 4, 'vaid kes oma maja hästi valitseb, kes oma lapsi peab sõnakuulmises kõige aususega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 5, 'Sest kui keegi iseenese maja ei oska juhtida, kuidas ta võib hoolt kanda Jumala Koguduse eest?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 6, 'Ta ärgu olgu alles vastpöördunu, et ta ei läheks uhkeks ega langeks sama nuhtluse alla kui kurat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 7, 'Ka olgu tal hea tunnistus neilt, kes on väljaspool Kogudust, et ta ei satuks naeru alla ja kuradi köitesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 8, 'Nõndasamuti tuleb ka abilistel olla ausad, mitte kahekeelsed, mitte joomamehed, mitte liigkasuvõtjad, vaid sellised,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 9, 'kellel usu saladus on puhtas südametunnistuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 10, 'Ent nad katsutagu enne läbi ja siis, kui nad on laitmatud, astugu nad ametisse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 11, 'Nõndasamuti olgu nende naised ausad, mitte keelepeksjad, kained, ustavad kõigis asjus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 12, 'Koguduse abilised olgu ühe naise mehed, kes oma lapsi ja oma peret hästi juhivad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 13, 'Sest need, kes on hästi pidanud oma ametit, saavutavad enestele kauni lugupidamise ja suure julguse usus, mis on Kristuses Jeesuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 14, 'Seda kirjutan sulle, ehk ma küll loodan pea tulla sinu juurde,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 15, 'et kui ma viibin, sa teaksid, kuidas tuleb käituda Jumala kojas, mis on elava Jumala Kogudus, tõe sammas ja alustugi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 16, 'Ja vastuvaieldamatu suur on jumalakartuse saladus: Jumal on avalikuks saanud lihas, õigeks mõistetud Vaimus, ilmunud inglitele, kuulutatud paganate seas, usutud maailmas, üles võetud ausse!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 1, 'Aga Vaim ütleb selge sõnaga, et viimsel ajal mõned taganevad usust ja hoiavad eksitajate vaimude ja kurjade vaimude õpetuste poole,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 2, 'valekuulutajate silmakirjaliku vagaduse tõttu, kes oma südametunnistusse otsekui tulise märgi on põletanud');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 3, 'ja keelavad abiellumast ning maitsmast rooga, mis Jumal on loonud selleks, et usklikud ja tõetundjad seda vastu võtaksid tänuga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 4, 'Sest kõik, mis Jumal on loonud, on hea, ja miski ei ole hüljatav, kui seda vastu võetakse tänuga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 5, 'Sest seda pühitsetakse Jumala sõna ja palve läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 6, 'Kui sa seda vendadele ette paned, oled sa Kristuse Jeesuse hea abiline ja toidad ennast usu ja hea õpetuse sõnadega, mida sa oled järginud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 7, 'Ent kõlvatut ja vananaiste vada väldi, aga harjuta ennast jumalakartuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 8, 'Sest ihulikust harjutusest on pisut kasu; aga jumalakartusest on kasu kõigile asjadele ja sellel on käesoleva ja tulevase elu tõotus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 9, 'Ustav on see sõna ja kõigiti vastuvõetav.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 10, 'Sest selleks me näeme vaeva ja võitleme, et me oleme lootnud elava Jumala peale, Kes on kõigi inimeste, iseäranis usklike, Õnnistegija.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 11, 'Seda käsi ja õpeta!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 12, 'Ükski ärgu põlaku sinu noorust, vaid ole usklikele eeskujuks sõnas, elus, armastuses, usus, meelepuhtuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 13, 'Kuni ma tulen, ole hoolas ette lugema, manitsema ja õpetama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 14, 'Ära jäta eneses peituvat armuannet hooletusse, mis sulle anti ennustuse läbi, kui vanemad panid oma käed sinu peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 15, 'Seda harrasta, selles ela, et sinu edenemine oleks ilmne kõikidele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 16, 'Pane tähele iseennast ja õpetust; püsi kindlasti selles; sest kui sa seda teed, päästad sa enese ja need, kes sind kuulevad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 1, 'Vana meest ära sõitle valjusti, vaid manitse teda kui isa, nooremaid kui vendi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 2, 'vanemaid naisi kui emasid, nooremaid kui õdesid kõiges meelepuhtuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 3, 'Austa leski, kes on õiged lesknaised.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 4, 'Aga kui kellelgi lesel on lapsi või lapselapsi, siis õppigu need esiti vagalt kohtlema oma majarahvast ja tasuma, mis nad on võlgu oma vanemaile. Sest see on meelepärane Jumala ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 5, 'Aga kes õige lesk on ja üksi maha jäänud, see loodab Jumala peale ja jääb kindlasti anumistesse ja palvetesse ööd ja päevad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 6, 'Aga kes elab lihahimus, see on elusalt surnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 7, 'Ja sedagi pane südamele, et nad oleksid laitmatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 8, 'Aga kui keegi enese omaste ja iseäranis kodakondsete eest ei kanna hoolt, see on usu ära salanud ja on pahem kui uskmatu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 9, 'Leskede hulka loetagu ainult niisugune, kes on vähemalt kuuskümmend aastat vana ja on olnud ühe mehe naine,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 10, 'ja kes on tunnustatud heade tegude poolest, kui ta on kasvatanud lapsi, kui ta on vastu võtnud võõraid, kui ta on pühade jalgu pesnud, kui ta on aidanud hädasolijaid, kui ta on püüdnud teha kõiksugust head.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 11, 'Kuid nooremaid leski keeldu vastu võtmast, sest kui iharus neid Kristuse kiuste vallutab, siis nad tahavad minna mehele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 12, 'ja neid tuleb süüdistada selles, et nad on hüljanud esimese usu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 13, 'Kui nad on pealegi jõude, harjuvad nad käima mööda maju, ja siis ei ole nad mitte ainult jõude, vaid ka lobisejad ning uudishimulikud ja räägivad sobimatuid asju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 14, 'Ma tahan siis, et nooremad läheksid mehele ja tooksid lapsi ilmale, valitseksid majatalitusi ega annaks vastasele põhjust solvamiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 15, 'Sest mõned ongi juba pöördunud saatana jälgedesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 16, 'Kui kellelgi usklikul mehel või naisel on leski omaste seas, siis nad hooldagu neid ja ärgu saagu need koormaks Kogudusele, et see võiks hooldada tõelisi leski.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 17, 'Vanemaid, kes hästi juhatavad Kogudusi, peetagu kahekordse austuse vääriliseks, eriti neid, kes töötavad sõnas ja õpetuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 18, 'Sest Kiri ütleb: \"Ära seo kinni härja suud, kui ta pahmast tallab!\" ja: \"Töötegija on oma palga väärt!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 19, 'Vanema vastu tõstetud kaebust ära võta kuulda, kui ei ole juures kaht või kolme tunnistajat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 20, 'Kes pattu teevad, neid noomi kõikide ees, et ka teistel oleks hirm.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 21, 'Ma vannutan sind Jumala ja Kristuse Jeesuse ja äravalitud inglite ees, et sa seda pead silmas ilma eelarvamiseta ega tee midagi erapoolikult.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 22, 'Ära ole kärmas kellelegi käsi pea peale panema ja ära tee ennast teiste pattude osaliseks. Pea ennast puhtana.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 23, 'Ära joo enam ainult vett, vaid tarvita pisut viina oma kõhu pärast ja oma sageda põdemise pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 24, 'Mõnede inimeste patud on kohe avalikud ja on kohe hukkamõistetavad, aga mõnede omad saavad tagantjärele ilmsiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 25, 'Samuti on ka head teod kohe avalikud, ja needki, mis seda ei ole, ei või jääda varjule.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 1, 'Kõik need, kes on orjaikke all, pidagu oma isandaid kõige austuse väärt, et Jumala nime ja õpetust ei pilgataks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 2, 'Aga kellel on usklikud isandad, ärgu pidagu neid halvemaks, sellepärast et nad on vennad, vaid orjaku neid veel parema meelega, sest et nad on usklikud ja armsad ning harrastavad heategevust. Seda õpeta ja manitse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 3, 'Kui keegi õpetab teisiti ega hoia meie Issanda Jeesuse Kristuse tervete sõnade ja õpetuse poole, mis viib jumalakartusele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 4, 'siis on see iseennast täis ega saa aru millestki, vaid on haige küsimuste ja vaidlemiste poolest, millest tõuseb kadedust, riidu, laimu, kurje kahtlusi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 5, 'alalisi tülitsusi inimeste vahel, kelle mõistus on rikutud ja kellel on tõde läinud käest, kes peavad jumalakartust tuluallikaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 6, 'Ent jumalakartus on suur tuluallikas, kui ta on ühendatud rahulolemisega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 7, 'Ei ole me ju midagi toonud maailma, seepärast me ei või midagi siit ära viia.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 8, 'Aga kui meil on peatoidust ja ihukatet, siis olgem sellega rahul.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 9, 'Ent kes tahavad rikkaks saada, need langevad kiusatusse ja võrku ja paljudesse rumalaisse ja kahjulikesse himudesse, mis suruvad inimesed alla hukatusse ja hävitusse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 10, 'Sest rahaahnus on kõigi kurjade asjade juur; raha ihaldades on mitmed ära eksinud usust ja on iseendile valmistanud palju torkavat valu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 11, 'Aga sina, Jumala inimene, põgene selle eest! Taotle õigust, jumalakartust, usku, armastust, kannatlikkust, tasadust!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 12, 'Võitle head usuvõitlust, hakka kinni igavesest elust; selleks sa oled kutsutud ja oled tunnistanud head tunnistust paljude tunnistajate ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 13, 'Ma käsin sind Jumala ees, Kes kõik teeb elavaks, ja Kristuse Jeesuse ees, Kes Pontius Pilaatuse ees andis hea tunnistuse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 14, 'et sa peaksid käsku kinni veatult ja laitmatult kuni meie Issanda Jeesuse Kristuse ilmumiseni,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 15, 'mille omal ajal toob nähtavale õnnis ja ainus vägev valitseja, kuningate Kuningas ja kõigi isandate Issand,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 16, 'Kellel üksi on surematus, Kes elab ligipääsmatus valguses, Keda ükski inimene ei ole näinud ega võigi näha! Temale olgu austus ja igavene vägi! Aamen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 17, 'Neid, kes on rikkad selles maailmas, manitse mitte olla suurelised ega loota kaduva rikkuse peale, vaid elava Jumala peale, Kes meile annab kõike rohkesti tarvituseks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 18, 'teha head ja rikkaks saada heade tegude poolest, olla helde käega ning jagada teistele');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 19, 'ja koguda enestele raudvara heaks aluseks tuleviku jaoks, et saavutada tõelist elu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 20, 'Oh Timoteos! Hoia enda hooleks antud vara ning pöördu ära kõlvatuist tühjest juttudest ja valenimelise tunnetuse väidetest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 21, 'mida mõningad kiidavad enestel olevat ning on ära eksinud usust. Arm teiega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 1, 'Paulus, Jumala tahtel Kristuse Jeesuse Apostel tõotuse järgi elust, mis on Kristuses Jeesuses,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 2, 'armsale pojale Timoteosele: armu, halastust, rahu Jumalalt Isalt ja Kristuselt Jeesuselt, meie Issandalt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 3, 'Ma tänan Jumalat, Keda ma oma esivanemaist saadik teenin puhta südametunnistusega, nagu ma ka lakkamata mõtlen sinule oma palvetes ööd ja päevad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 4, 'igatsedes sind näha ja meelde tuletades sinu pisaraid, et täituda rõõmuga,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 5, 'kui mulle meenub sinu silmakirjatsematu usk, mis enne elas sinu vanaemas Loises ja sinu emas Euniikes ja nüüd, nagu ma olen veendunud, elab ka sinus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 6, 'Selles asjas ma tuletan sinule meelde, et sa õhutaksid lõkkele Jumala armuande, mis sulle sai minu käte pealepanemise kaudu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 7, 'Sest Jumal ei ole meile andnud arguse vaimu, vaid väe ja armastuse ja mõistliku meele vaimu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 8, 'Ärgu olgu sul siis häbi meie Issanda tunnistusest ega minust, Tema vangist, vaid kannata kurja kaasa Evangeeliumiga Jumala väge mööda,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 9, 'Kes meid on päästnud ja on kutsunud püha kutsega, mitte meie tegusid mööda, vaid Oma nõu ja armu järgi, mis meile on antud Kristuses Jeesuses enne igavesi aegu;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 10, 'ent nüüd on saanud avalikuks meie Õnnistegija Kristuse Jeesuse ilmumise läbi, Kes on hävitanud surma ja toonud valge ette elu ja kadumatu põlve Evangeeliumi kaudu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 11, 'milleks mina olen seatud kuulutajaks ja Apostliks ja õpetajaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 12, 'Sel põhjusel ma ka kannatan seda ega ole mul häbi sellest; sest ma tean, Kellesse ma usun, ja olen julge selles, et Ta on vägev säilitama minu kätte ustud vara tolle päevani.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 13, 'Tervete sõnade eeskujuks võta, mida sa oled kuulnud minult usus ja armastuses, mis on Kristuses Jeesuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 14, 'Kaunist sinu hooleks antud vara hoia Püha Vaimu läbi, Kes meis elab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 15, 'Sa tead seda, et kõik, kes on Aasias, on löönud lahku minust, nende seas Fügelos ja Hermogenes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 16, 'Issand osutagu halastust Onesiforose perele, sest tema on mind sagedasti kosutanud ega ole mitte häbenenud mu ahelaid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 17, 'vaid kui ta saabus Rooma, otsis ta mind suure hoolega ning leidis mind.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 18, 'Issand andku temale leida halastust Issanda juures tol päeval! Ja kui palju ta Efesoses oli mulle abiks, tead sina paremini.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 1, 'Saa siis vahvaks, mu poeg, armus, mis on Kristuses Jeesuses!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 2, 'Ja mis sa minult oled kuulnud paljude tunnistajate abil, see anna ustavate inimeste kätte, kes on osavad õpetama ka teisi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 3, 'Kannata siis ühtlasi kurja kui Jeesuse Kristuse õilis sõdur!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 4, 'Mitte ükski, kes on sõjateenistuses, ei anna ennast peatoiduse hankimise kimpu, muidu ta ei saa olla selle meelt mööda, kes ta on sõtta kutsunud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 5, 'Ja kui keegi võitleb, ei saa ta võidupärga, kui ta ei võitle seadusepäraselt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 6, 'Töötaja põllumees peab kõige esiti viljast osa saama!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 7, 'Saa aru, mis ma ütlen! Ent Issand andku sulle arusaamist kõigest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 8, 'Pea meeles Jeesust Kristust, Kes on surnuist üles äratatud, Kes on Taaveti soost minu Evangeeliumi järgi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 9, 'mille pärast mina ka kannatan kurja, kandes isegi ahelaid otsekui kurjategija! Kuid Jumala sõna ei ole aheldatud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 10, 'Sel põhjusel ma talun kõike nende pärast, kes on valitud, et nemadki saavutaksid õndsuse Kristuses Jeesuses igavese auga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 11, 'Ustav on see sõna: kui me ühes Temaga oleme surnud, siis me ka elame ühes Temaga;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 12, 'kui me ühes kannatame, siis me ka valitseme ühes Temaga; kui me Tema salgame, siis Tema salgab ka meid;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 13, 'kui me ei usu, Tema jääb siiski ustavaks; Ta ei või ennast salata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 14, 'Seda tuleta meelde ja kinnita Jumala ees, et nad ei riidleks sõnade pärast. Sellest ei ole mingit kasu, see segab kuuljate meeli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 15, 'Püüa hoolega osutuda kõlbavaks Jumalale kui töötegija, kellel pole tarvis häbeneda, kes tõesõna jagab õieti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 16, 'Kuid kõlvatute tühjade juttude eest hoidu eemale; nende rääkijad lähevad ju ikka kaugemale jumalakartmatuses');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 17, 'ja nende kõne sööbib enese ümber otsekui vähktõbi; nende seast on Hümenaios ja Fileetos,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 18, 'kes on eksinud ära tõest ja ütlevad ülestõusmise juba olnud olevat, ja rikuvad mõnede usu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 19, 'Ent Jumala rajatud alus püsib kindlana ja temal on see pitser: Issand tunneb neid, kes Tema Omad on; ja: ülekohtust loobugu igaüks, kes Issanda nime nimetab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 20, 'Aga suures majas ei ole mitte ainult kuld- ja hõbe-, vaid ka puuastjaid ja saviastjaid, ja muist on väärikamaks ja muist halvemaks tarvitamiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 21, 'Kui keegi nüüd iseennast neist puhastab, saab ta astjaks väärikama tarvituse jaoks ja on pühitsetud ning tarvilik oma isandale, kõlvuline igaks heaks teoks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 22, 'Põgene nooreea himude eest! Taotle õigust, usku, armastust, rahu nendega, kes Issandat appi hüüavad puhtast südamest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 23, 'Väldi rumalaid ja lapsikuid küsimusi; sest sa tead, et need toovad tüli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 24, 'Ent Issanda sulane ärgu tülitsegu, vaid olgu lahke kõikide vastu, osav õpetama, valmis kannatama kurja,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 25, 'kes tasase meelega noomib vastupanijaid, et Jumal neile kuidagi annaks meelt parandada ja tunnetada tõde,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 26, 'kaineneda ja vabaneda kuradi paelust, kes on nad kinni võtnud täitma tema tahtmist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 1, 'Aga seda tea, et viimseil päevil tuleb raskeid aegu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 2, 'Sest inimesed on siis enesearmastajad, rahaahned, hooplejad, ülbed, teotajad, sõnakuulmatud vanemaile, tänamatud, õelad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 3, 'südametud, leppimatud, laimajad, pillajad, toored, hea põlgajad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 4, 'petturid, kergemeelsed, sõgedad, rohkem lõbuarmastajad kui jumalaarmastajad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 5, 'kellel on jumalakartuse nägu, aga kes salgavad tema väge. Ja nendest hoidu eemale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 6, 'Sest nende seas on ka mõned, kes poetuvad majadesse ja võtavad oma võrku pattudega koormatud ja mõnesuguste himudega aetavaid naisterahvaid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 7, 'kes alati on õppijad, kuid kunagi ei pääse tõe tunnetusele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 8, 'Aga otsekui Jannes ja Jambres hakkasid vastu Moosesele, nõnda hakkavad need vastu tõele; nad on inimesed, kes on arust ära ega pea paika usu poolest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 9, 'Aga nad ei saa ka mitte enam kaugele, sest nende hullustus saab kõigile avalikuks, nagu ka nondega sündis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 10, 'Ent sina oled järginud mind minu õpetuses, eluviisis, nõuandes, usus, pikas meeles, armastuses, kannatlikkuses,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 11, 'tagakiusamistes, kannatamistes, mis said mulle osaks Antiookias, Ikoonionis, Lüstras; milliseid tagakiusamisi ma kannatasin ja millest kõigist mind päästis Issand.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 12, 'Ja kõiki, kes tahavad elada jumalakartlikult Kristuses Jeesuses, kiusatakse taga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 13, 'Aga kurjad inimesed ja petised lähevad ikka pahemaks, eksitades teisi ja eksides.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 14, 'Ent sina jää sellesse, mida oled õppinud ja milles oled kindel, sest sa tead, kellelt sa selle oled õppinud;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 15, 'ja et sina lapsest saadik tunned pühi kirju, mis võivad sind teha targaks, õndsuseks usu kaudu, mis on Kristuses Jeesuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 16, 'Kõik Kiri on Jumala Vaimu poolt sisendatud ja on kasulik õpetuseks, noomimiseks, parandamiseks, juhatamiseks õiguses,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 17, 'et Jumala inimene oleks täielik ja kõigele heale tööle valmistunud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 1, 'Ma tunnistan kindlasti Jumala ja Kristuse Jeesuse ees, Kes mõistab kohut elavate ja surnute üle, niihästi Tema tulemist kui ka Tema Riiki.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 2, 'Kuuluta sõna, astu esile, olgu parajal või ebasobival ajal, noomi, hoiata, manitse kõige pika meelega ja õpetamisega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 3, 'Sest tuleb aeg, et nad tervet õpetust ei taha sallida, vaid enestele otsivad õpetajaid iseeneste himude järgi, sedamööda kuidas nende kõrvad sügelevad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 4, 'ja käänavad kõrvad ära tõest ning pöörduvad tühjade juttude poole.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 5, 'Aga sina ole igapidi kaine, kannata kurja, tee Evangeeliumikuulutaja tööd, täida oma ametit õieti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 6, 'Sest mind juba ohverdatakse ja minu lahkumiseaeg on jõudnud ligi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 7, 'Ma olen head võitlemist võidelnud, ma olen oma jooksmise lõpetanud, ma olen usu säilitanud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 8, 'Nüüd on minule tallele pandud õiguse pärg, mille Issand, õige kohtumõistja, mulle annab tol päeval, aga mitte ainult minule, vaid ka kõigile, kes armastavad Ta ilmumist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 9, 'Katsu ruttu minu juurde tulla!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 10, 'Sest Deemas jättis mind maha ning hakkas armastama seda maailma ja läks Tessaloonikasse, Kreskes Galaatiasse, Tiitus Dalmaatiasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 11, 'Luukas üksi on mu juures. Võta Markus ja too ta enesega, sest teda on mulle väga vaja abiliseametisse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 12, 'Tühhikose ma läkitasin Efesosse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 13, 'Kuub, mille ma jätsin Troasse Karpose juurde, too tulles kaasa, ja raamatud, eriti pärgamendid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 14, 'Vasksepp Aleksandros on mulle teinud palju paha. Issand tasugu talle ta tegusid mööda!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 15, 'Hoidu ka sina tema eest, sest ta on kõvasti vastu pannud meie sõnadele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 16, 'Kui ma esimest korda kohtus kostsin enese eest, ei olnud ükski mulle toeks, vaid kõik jätsid mind maha. Ärgu arvatagu seda neile süüks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 17, 'Aga Issand seisis minu eest ja kinnitas mind, et sõna kuulutamine minu läbi teostuks täiel määral ja kõik paganad kuuleksid seda; ja ma pääsesin lõukoera suust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 18, 'Küll Issand mind ka välja tõmbab kõigist kurjadest tegudest ja aitab Oma taevasesse Riiki. Temale olgu austus ajastute ajastuteni! Aamen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 19, 'Tervita Priskat ja Akvilat ja Onesiforose peret.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 20, 'Erastos jäi Korintosesse; Trofimose ma jätsin haigelt Mileetosesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 21, 'Rutta, et sa enne talve saabud! Eubulos ja Puudes ja Linos ja Klaudia ja kõik vennad tervitavad sind.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 22, 'Issand Jeesus Kristus olgu sinu vaimuga! Arm olgu teiega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 1, 'Paulus, Jumala sulane, Jeesuse Kristuse Apostel Jumala valitute usu ja tõetunnetuse järgi, mis vastab jumalakartusele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 2, 'Apostel igavese elu lootuses, mille Jumal, Kes ei valeta, on tõotanud enne igavesi aegu -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 3, 'omal ajal aga tegi Tema Oma sõna avalikuks jutlustamises, mis minu kätte on usaldatud Jumala, meie Õnnistegija käsul -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 4, 'Tiitusele, tõelisele pojale meie ühise usu poolest: armu ja rahu Jumalalt Isalt ja Issandalt Kristuselt Jeesuselt, meie Õnnistegijalt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 5, 'Selle tarvis ma jätsin sind Kreetasse, et sa korraldaksid, mis veel jäi korraldamata, ja seaksid vanemaid igasse linna, nagu ma sind käskisin,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 6, 'kui kuskil leidub laitmatu isik, ühe naise mees, kellel on usklikud lapsed, nõnda et neile ei saa ette heita kõlvatut elu ega kangekaelsust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 7, 'Sest Koguduse ülevaataja peab olema kui Jumala majapidaja, laitmatu, mitte isemeelne, mitte äkiline, mitte joodik, mitte riiakas, mitte liigkasupüüdja,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 8, 'vaid külalislahke, headuse armastaja, mõistlik, õige, püha, kasin;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 9, 'ta pidagu kinni ustavast sõnast vastavalt õpetusele, et ta oleks võimeline niihästi manitsema terves õpetuses kui ka kummutama vasturääkijate väited.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 10, 'Sest on olemas palju kangekaelseid, tühja jutu mehi ja eksitajaid, kõige enam ümberlõigatute seas,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 11, 'kelle suu tuleb sulgeda, kes pööravad ära terved pered, õpetades sündmatuid asju häbematu kasu pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 12, 'Üks neist, nende oma prohvet, on öelnud: \"Kreetalased on luiskajad, õelad loomad ja laisklejad vatsad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 13, 'See tunnistus on tõsi. Sellepärast noomi neid valjusti, et nad saaksid terveks usus');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 14, 'ega paneks tähele juutide tühje jutte ja tõest eemalepöördunud inimeste käske!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 15, 'Puhtaile on kõik puhas, aga rüvedaile ja uskmatuile pole miski puhas, vaid rüve on niihästi nende meel kui südametunnistus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 16, 'Nemad väidavad, et nad tunnevad Jumalat, aga tegudega nad salgavad; sest nad on jälgid ja sõnakuulmatud ja igaks heaks tööks kõlbmatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 1, 'Ent sina räägi seda, mis sünnib ühte terve õpetusega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 2, 'Vanad mehed olgu karsked, ausad, mõistlikud, terved usus, armastuses, kannatlikkuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 3, 'Samuti käitugu elatanud naised nõnda, nagu on kohane pühadele; nad ärgu olgu keelekandjad ega suured viinavõtjad, vaid õpetagu head,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 4, 'et nad võiksid mõistlikult juhatada noori naisi armastama oma mehi ja lapsi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 5, 'olema viisakad, kasinad, majatalituses hoolikad, head, allaheitlikud oma meestele, et Jumala sõna mitte ei pilgataks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 6, 'Samuti käsi nooremaid mehi käituda viisakalt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 7, 'Sea ennast igapidi heategude eeskujuks, õpetuses osuta selgust, ausust,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 8, 'tervet vääramata sõna, et vastane tunneks häbi, kui tal ei ole teist rääkida midagi paha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 9, 'Orje käsi alistuda oma isandaile ja olla kõiges nende meele järgi, mitte vastu rääkida,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 10, 'mitte midagi näpata, vaid osutada täit head ustavust, et kaunistada kõiges Jumala, meie Õnnistegija õpetust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 11, 'Sest Jumala õndsakstegev arm on ilmunud kõigile inimestele');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 12, 'ja kasvatab meid, et me hülgaksime jumalakartmatu elu ja maailma himud ning elaksime mõistlikult ja õieti ja jumalakartlikult praegusel maailmaajastul,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 13, 'oodates õndsa lootuse täitumist ja suure Jumala ning meie Õnnistegija Jeesuse Kristuse auhiilguse ilmumist,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 14, 'Kes Iseenese andis meie eest, et meid lunastada kõigest ülekohtust ja puhastada Enesele pärisrahvaks, Kes agar on tegema häid tegusid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 15, 'Seda räägi ja manitse ning veena kõige nõudlikkusega. Keegi ärgu põlaku sind!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 1, 'Tuleta neile meelde, et nad alistuksid valitsejaile ja ülemaile, oleksid sõnakuulelikud, valmis igaks heaks teoks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 2, 'ei laimaks kedagi, ei oleks riiakad, vaid leebed, osutades kõike tasadust kõigi inimeste vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 3, 'Sest ka meie olime kord mõistmatud, sõnakuulmatud, eksijad, mitmesuguste himude ja lõbude orjad ning elasime kurjuses ja kadeduses, olime vihatavad ja vihkasime üksteist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 4, 'Aga kui Jumala, meie Õnnistegija heldus ja inimesearmastus ilmus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 5, 'siis Ta päästis meid, ei mitte õiguse tegude tõttu, mida me oleksime teinud, vaid Oma halastust mööda uuestisünni pesemise ja Püha Vaimu uuendamise kaudu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 6, 'Keda Tema on rikkalikult välja valanud meie peale Jeesuse Kristuse, meie Õnnistegija läbi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 7, 'et meie, tema armu läbi õigeks tehtud, saaksime igavese elu pärijaiks lootuse järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 8, 'See sõna on ustav; ja ma tahan, et sa neid asju teravasti rõhutaksid, et need, kes usuvad Jumalasse, oleksid hoolsad heade tegude tegemises; need on head ja kasulikud inimestele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 9, 'Aga väldi rumalaid uurimusi ja sugukondade nimekirju ning riidu ja vaidlusi käsu pärast; sest need on kasutud ja tühised.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 10, 'Eksiõpetusega inimesest pöördu ära, kui sa teda ühe korra või kaks oled noominud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 11, 'teades, et niisugune on pöörane ja teeb pattu ning on iseenese hukka mõistnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 12, 'Kui ma Artemase või Tühhikose läkitan sinu juurde, siis tõtta tulema minu juurde Nikoopolisse; sest ma olen otsustanud seal viibida talve.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 13, 'Käsutundja Zeenas ja Apollos varusta hoolsasti teekonnale, et neil midagi ei puuduks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 14, 'Õppigu meiegi omad harrastama häid tegusid hädavajalikeks tarvidusteks, et nad ei jääks viljatuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 15, 'Sind tervitavad kõik, kes on minuga. Tervita neid, kes meid armastavad usus. Arm olgu teie kõikidega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 1, 'Paulus, Kristuse Jeesuse vang ja vend Timoteos armsale Fileemonile, meie kaastöölisele');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 2, 'ja armsale õele Appiale ja Arhipposele, meie kaasvõitlejale, ja sinu majasse kogunevale kogudusele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 3, 'Armu teile ja rahu Jumalalt, meie Isalt, ja Issandalt Jeesuselt Kristuselt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 4, 'Ma tänan alati oma Jumalat, kui ma mõtlen sinule oma palveis;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 5, 'sest ma kuulen sinu armastusest ja usust, mis sul on Issanda Jeesuse poole ja kõigi pühade vastu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 6, 'et su usu ühendus minuga saaks vägevaks kõige hea tunnetamises, mis meis on Kristuse kohta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 7, 'Sest mul oli palju rõõmu ja troosti sinu armastusest, sest et pühade südamed on kosutatud sinu läbi, vend.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 8, 'Sellepärast, kuigi mul on Kristuses palju julgust sind käskida, mida tuleb teha,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 9, 'ma siiski armastuse pärast pigemini palun sind, olles, mis ma olen, vana Paulus, ja nüüd pealegi Kristuse Jeesuse vang.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 10, 'Ma palun sind oma poja Oneesimose pärast, kelle ma sünnitasin ahelais olles ja');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 11, 'kes kord oli sinule kasutu, aga nüüd on sinule ja minule väga kasulik; tema ma läkitan sulle tagasi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 12, 'tema, see on, mu oma südame!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 13, 'Ma oleksin tahtnud teda pidada enese juures, et ta sinu asemel oleks abiks mulle, kes olen ahelais evangeeliumi pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 14, 'Kuid ilma sinu nõustuseta ma ei tahtnud midagi teha, et sinu heategu ei näiks olevat sunnitud, vaid on vabatahtlik.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 15, 'Sest ta oligi vist sellepärast mõneks ajaks eraldatud sinust, et sa teda saaksid pidada igavesti,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 16, 'mitte kui orja, vaid rohkem kui orja, kui armast venda, iseäranis minule armast, kui palju enam sinule niihästi liha poolest kui Issandas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 17, 'Kui sa nüüd mind pead oma kaaslaseks, siis võta ta vastu kui mind ennast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 18, 'Aga kui ta sulle on teinud mingit ülekohut või kui tal on võlgu sinuga, siis pane see minu arvele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 19, 'Mina, Paulus, olen seda kirjutanud ise oma käega: \"Mina maksan!\" Ma seda ei ütlegi, et sa iseenesegi oled mulle võlgu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 20, 'Jah, vend, mina tahaksin, et mul sinust oleks kasu Issandas! Kosuta mu südant Kristuses!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 21, 'Veendunud olles sinu sõnakuulmises, kirjutan sulle. Ma tean, et sa teed veel enamgi kui ma ütlen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 22, 'Ühtlasi valmista mulle paik küllatulekuks, sest ma loodan, et mind teie palvete kaudu teile tagasi kingitakse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 23, 'Sind tervitavad Epafras, mu kaasvang Kristuses Jeesuses,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 24, 'Markus, Aristarhos, Deemas, Luukas, minu kaastöölised.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 25, 'Meie Issanda Jeesuse Kristuse arm olgu teie vaimuga!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 1, 'Kui Jumal muiste mitu korda ja mitmel kombel vanemaile oli rääkinud prohvetite kaudu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 2, 'siis on Ta neil viimseil päevil meile rääkinud Poja kaudu, Kelle Ta on pannud kõigi asjade pärijaks, Kelle läbi Ta on teinud maailmaajastud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 3, 'Kes, olles Tema aupaistus ning Ta olemuse kuju ja kandes kõike Oma vägeva sõnaga, pärast seda kui Ta Iseenese läbi oli toimetanud meie pattude puhastuse, istus Ausuuruse paremale käele kõrguses');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 4, 'ning sai seda ülemaks Inglitest, mida kõrgema nime Ta on pärinud kui nemad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 5, 'Sest missugusele Inglile on Ta iganes öelnud: \"Sina oled Mu Poeg, täna Ma sünnitasin Sind!\", või jälle: \"Mina tahan olla Temale Isaks ja Tema peab olema Mulle Pojaks!\"?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 6, 'Ent sellest, kui Ta jälle toob esmasündinu maailma, ütleb Ta: \"Ka kõik Jumala Inglid kummardagu Teda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 7, 'Ent Inglitest Ta ütleb küll: \"Ta teeb Oma Inglid tuulteks ja Oma teenijad tuleleegiks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 8, 'Aga Pojale Ta ütleb: \"Su aujärg, Jumal, on ikka ja igavesti, Su valitsuskepp on õigluse kepp!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 9, 'Sa armastad õigust ja vihkad ülekohut; sellepärast on Jumal, Sinu Jumal, Sind võidnud rõõmuõliga enam kui Su kaaslasi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 10, 'Ja: \"Sina, Issand, panid maale aluse ja taevad on Su käte tööd!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 11, 'Need hävivad, aga Sina püsid; nad kõik kuluvad nagu kuub');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 12, 'ja Sa rullid nad kokku nagu mantli, nagu kuue, ja nad muudetakse; aga Sina oled seesama ja Sinu aastad ei lõpe!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 13, 'Või missugusele Inglile on Ta kunagi öelnud: \"Istu Mu paremale käele, kuni Ma panen Su vaenlased Su jalgealuseks järiks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 14, 'Eks nad kõik ole teenijad vaimud, läkitatud abistama neid, kes õndsuse pärivad?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 1, 'Sellepärast tuleb meil palju hoolsamini silmas pidada seda, mida oleme kuulnud, et me kuidagi mööda ei libiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 2, 'Sest kui Inglite kaudu räägitud sõna püsis kindlana ja kõik üleastumine ja sõnakuulmatus sai oma õige palga,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 3, 'kuidas me siis võime pääseda pakku, kui me ei hooli nii suurest päästest, mis oma kuulutuse alguse sai Issandalt ja mille kinnitasid meisse need, kes seda kuulsid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 4, 'kui Jumal ühes nendega andis tunnistust tunnustähtedega ja imedega ja mõnesuguste vägevate tegudega ja Püha Vaimu jagamistega Oma tahte järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 5, 'Sest mitte Inglitele ei alistanud Ta tulevast maailma, millest me räägime,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 6, 'vaid keegi on kuskil tunnistanud, öeldes: \"Mis on inimene, et Sa temale mõtled, või inimese poeg, et Sa tema eest hoolitsed?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 7, 'Sa tegid ta pisut alamaks Inglitest; Sa ehtisid teda au ja austusega;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 8, 'kõik Sa panid tema jalge alla!\" Sest alistades kõik temale, ei jätnud ta midagi temale alistamata. Aga nüüd me ei näe veel kõike temale alistatuna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 9, 'Ent Teda, Kes sai üürikeseks ajaks alamaks Inglitest, Jeesust, me näeme Tema surma kannatamise pärast au ja austusega ehitud, et Ta Jumala armust igaühe eest maitseks surma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 10, 'Sest Temale, Kelle pärast ja Kelle läbi on kõik, sobis, et Ta selle, kes palju lapsi ausse viib, nende õndsuse ülemjuhi, teeks täiuslikuks kannatuste kaudu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 11, 'Sest niihästi pühitseja kui pühitsetavad on kõik üht Isa; sel põhjusel Tema ka ei häbene neid nimetada vendadeks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 12, 'öeldes: \"Ma tahan Su nime kuulutada Oma vendadele, keset kogudust Ma tahan laulda Sulle kiitust!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 13, 'Ja jälle: \"Ma loodan Tema peale!\"; ja jälle: \"Vaata, siin olen Mina ja lapsed, keda Jumal Mulle on andnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 14, 'Et nüüd lapsed on liha ja vere osalised, siis Temagi sai otse samal viisil osa sellest, et Ta surma läbi kaotaks selle, kelle võimu all oli surm, see on kuradi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 15, 'ja vabastaks need, kes surma kartusest olid kogu eluaja kinni orjapõlves.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 16, 'Sest ega Ta ometi hoolitse Inglite eest, vaid Ta hoolitseb Aabrahami soo eest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 17, 'Sellepärast pidi Ta kõiges saama vendade sarnaseks, et Ta oleks halastaja ja ustav ülempreester teenistuses Jumala ees rahva pattude lepitamiseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 18, 'Sest selles, milles Ta on kannatanud kiusatud olles, võib Ta aidata neid, keda kiusatakse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 1, 'Sellepärast, pühad vennad, taevase kutsumise osalised, pange tähele meie tunnistuse Apostlit ja ülempreestrit Jeesust,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 2, 'Kes on ustav Oma ametisseseadjale nagu Mooseski kogu Tema kojas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 3, 'Sest Tema on Moosesest niivõrd suurema au vääriline, kuivõrd maja valmistajal on suurem au kui majal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 4, 'Sest igal majal on oma valmistaja; aga kes kõik on valmistanud, see on Jumal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 5, 'Ja Mooses oli küll ustav sulasena kogu Tema kojas tunnistuseks sellest, mida pidi räägitama,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 6, 'aga Kristus kui Poeg on Tema koja üle; ja Tema koda oleme meie, kui me julguse ning lootuse kiitlemise hoiame otsani kindla.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 7, 'Sellepärast nõnda nagu Püha Vaim ütleb: \"Täna, kui te kuulete Tema häält,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 8, 'ärge paadutage oma südameid, nõnda nagu sündis nurina ajal, kiusatuse päeval kõrbes,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 9, 'kus teie isad Mind kiusasid ning katsusid, kuigi nad olid näinud Minu tegusid nelikümmend aastat,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 10, 'mispärast Ma vihastusin selle sugupõlve peale ning ütlesin: nemad eksivad alati oma südamega, ent Minu teid nad ei õppinud tundma;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 11, 'sellepärast Ma vandusin Oma vihas: nad ei pääse mitte Minu hingamisse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 12, 'Katsuge, vennad, et kellelgi teie seast ei oleks kuri uskmatu süda, mis loobub elavast Jumalast,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 13, 'vaid manitsege üksteist iga päev, niikaua kui veel öeldakse \"täna\", et ükski teie seast ei paaduks patu pettusest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 14, 'Sest me oleme saanud Kristuse osalisiks, kui me vaid otsani kindla hoiame esimese usulootuse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 15, 'Kui öeldakse: \"Täna, kui te kuulete Tema häält, ärge paadutage oma südameid, nõnda nagu sündis nurina ajal\", -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 16, 'kes siis olid need kuuljad, kes hakkasid nurisema? Kas need ei olnud kõik, kes olid väljunud Egiptusest Moosese juhatusel?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 17, 'Aga kellele Ta oli vihane nelikümmend aastat? Kas mitte neile, kes olid pattu teinud, kelle kehad langesid kõrbes?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 18, 'Kellele Ta siis vandus, et nad ei pääse mitte Tema hingamisse? Kas mitte neile, kes olid sõnakuulmatud?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 19, 'Ja nii me näeme, et nad oma uskmatuse pärast ei võinud sisse saada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 1, 'Siis kartkem, et niikaua kui tõotus pääseda Tema hingamisse on täitumata, keegi teist kuidagi ei osutuks hiljaks jäänuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 2, 'Sest rõõmusõnum on kuulutatud meile nõnda nagu neilegi. Ent kuuldud sõnast ei olnud neil kasu, sest see ei imbunud usu läbi neisse, kes seda kuulsid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 3, 'Sest meie, kes usume, saame hingamisse, nõnda nagu Tema on öelnud: \"Siis ma vandusin Oma vihas: nad ei pääse mitte Minu hingamisse!\", ehk küll Tema teod olid valmis maailma asutamisest alates.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 4, 'Sest kuskil on Ta seitsmendast päevast öelnud nõnda: \"Ja Jumal hingas seitsmendal päeval kõigist Oma tegudest\";');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 5, 'ja siin jälle: \"Nad ei pääse mitte Minu hingamisse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 6, 'Et nüüd asi nii on, et mõned pääsevad sinna ja et need, kellele rõõmusõnumit esmalt kuulutati, ei ole sinna sisse saanud sõnakuulmatuse pärast,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 7, 'siis Ta määrab uuesti ühe päeva \"tänapäevaks\", öeldes Taaveti kaudu nii pika aja pärast, nagu on enne öeldud: \"Täna, kui te kuulete tema häält, ärge paadutage oma südameid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 8, 'Sest kui Joosua nad oleks viinud hingamisse, siis ta ei räägiks teisest, pärastisest päevast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 9, 'Seega on Jumala rahval hingamisaeg veel ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 10, 'Sest kes Tema hingamisse on pääsenud, see hingab ka ise oma tegudest, otsekui Jumal Oma tegudest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 11, 'Siis olgem agarad sellesse hingamisse minema, et keegi ei langeks sõnakuulmatusse samal eeskujul.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 12, 'Sest Jumala sõna on elav ja vägev ja teravam kui ükski kaheterane mõõk ning tungib läbi, kuni ta lõhestab hinge ja vaimu, liikmed ja üdi, ja on südame meelsuse ja mõtete hindaja;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 13, 'ja ükski loodu ei ole temale nähtamatu, vaid kõik on alasti ja paljastatud Tema silma ees; ja Temale tuleb meil aru anda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 14, 'Et meil nüüd on suur ülempreester Jeesus, Jumala Poeg, Kes taevad on läbinud, siis pidagem kinni tunnistusest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 15, 'Sest meil pole niisugune ülempreester, kellel ei oleks kaastundmust meie nõtrustega, vaid kes kõiges on kiusatud otsekui meie, siiski ilma patuta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 16, 'Läki siis julgusega armuaujärje ette, et me saaksime halastust ja leiaksime armu oma abiks õigeks ajaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 1, 'Sest iga ülempreester, kes võetakse inimeste seast, seatakse inimeste heaks toimetama teenistust Jumala ees, tooma ande ja ohvreid pattude eest;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 2, 'ja tema võib säästa mõistmatuid ning eksijaid; on ta ju ise ka nõtruse sees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 3, 'Ja selle nõtruse pärast peab tema niisamuti nagu rahva eest, ka iseenese eest ohvrit tooma pattude pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 4, 'Ka ei võta keegi enesele ise seda au, vaid Jumala kutsel nagu Aarongi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 5, 'Nõnda Kristuski ei võtnud Ise Enesele au saada ülempreestriks, vaid Ta sai selle Temalt, Kes Temale ütles: \"Sina oled Mu Poeg, täna Ma sünnitasin Sind!\",');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 6, 'nõnda nagu Ta teiseski kohas ütleb: \"Sina oled preester igavesti Melkisedeki korra järgi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 7, 'Tema ohverdas Oma liha päevil palveid ja anumisi suure hüüdmise ja silmaveega Sellele, Kes Teda võis päästa surmast, ja Tema palvet kuuldi Ta jumalakartuse pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 8, 'Ja ehk Ta oligi Poeg, õppis Ta sõnakuulmist sellest, mida Ta kannatas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 9, 'Ja kui Ta oli saanud täielikuks, sai Tema igavese õndsuse alustajaks kõigile, kes on Temale sõnakuulelikud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 10, 'ja Jumal nimetas Tema ülempreestriks Melkisedeki korra järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 11, 'Sellest oleks meil palju rääkida ning seda on raske sõnadega seletada, sest te olete läinud tuimaks kuulma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 12, 'Sest teie, kes aja poolest peaksite olema õpetajad, vajate jälle, et teile õpetataks Jumala sõnade esimesi algeid, ja olete saanud nende aruliseks, kellele läheb tarvis piima, aga mitte tahket rooga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 13, 'Sest igaüks, kes veel tarvitab piima, ei saa õieti aru õiguse sõnast, sest ta on alles laps.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 14, 'Aga täisealiste jaoks on tahke roog, nende jaoks, kelle meeled vilumuse tõttu on harjunud vahet tegema hea ja kurja vahel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 1, 'Sellepärast jätkem kõrvale algõpetus Kristusest ja püüdkem täiuslikkusele ning ärgem hakakem uuesti rajama alust pöördumisele surnud tegudest ja usule Jumalasse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 2, 'õpetusele ristimisest ja käte pealepanemisest, surnute ülestõusmisest ja igavesest hukatusest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 3, 'Ja nii me teemegi, kui Jumal lubab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 4, 'Sest on võimatu neid, kes korra olid valgustatud ning maitsesid taevalist andi ja said osa Pühast Vaimust,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 5, 'ja maitsesid Jumala head sõna ja tulevase maailmaajastu väge');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 6, 'ja siis ära taganesid, uuesti tuua meeleparandamisele, sellepärast et nad iseeneste kahjuks Jumala Poja risti löövad ja naeruks panevad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 7, 'Sest maa, mis joob enesesse rikkalikult sadanud vihma ja kasvatab tarvilist vilja neile, kes seda harivad, saab õnnistuse Jumalalt;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 8, 'kuid maa, mis kannab kibuvitsu ja ohakaid, on kõlbmatu ja lähedal needusele, ja lõppeks ta põletatakse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 9, 'Aga, armsad, teie suhtes me usume seda, mis on parem ja soodus õndsusele, kuigi me räägime nõnda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 10, 'Sest Jumal ei ole ülekohtune, et Ta unustaks ära teie teod ja armastuse, mida olete osutanud Tema nimele, kui te pühadele abiks olite ja veelgi olete.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 11, 'Aga me ihaldame seda, et te igaüks osutaksite sama indu täieliku lootuse säilitamiseks otsani;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 12, 'et te ei läheks loiuks, vaid järgiksite neid, kes usu ja pika meele tõttu pärivad selle, mis on tõotatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 13, 'Sest kui Jumal Aabrahamile andis tõotuse, vandus Ta Iseenese juures, sest ei olnud kedagi suuremat, kelle juures Ta oleks võinud vanduda,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 14, 'ning ütles: \"Tõesti, õnnistusega Ma õnnistan sind ja teen sind väga paljuks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 15, 'Ja nii Aabraham ootas kannatlikult ning sai tõotuse kätte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 16, 'Sest inimesed vannuvad suurema juures ja vanne on neile kinnituseks kui kõige vasturääkimise lõpetus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 17, 'Nii on Jumal selleks, et tõotuse pärijaile veel selgemini näidata oma nõu kindlust, vannet tarvitanud vahendiks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 18, 'et kahe kõikumatu asja läbi, milles Jumalal on võimatu valetada, oleks vägev julgustus meil, kes oleme usaldanud kinni haarata eelolevast lootusest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 19, 'mis meile on otsekui hinge ankur, kindel ja tugev ning ulatub sissepoole eesriide taha,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 20, 'kuhu eeljooksjana meie heaks on läinud sisse Jeesus, kui ta sai ülempreestriks igavesti Melkisedeki korra järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 1, 'Sest see Melkisedek - Saalemi kuningas, kõige kõrgema Jumala preester, kes tuli vastu Aabrahamile, kui see oli koju minemas pärast võitu kuningate üle, ja õnnistas teda,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 2, 'kellele ka Aabraham jagas kümnist kõigest saagist ja kes on esmalt, nagu teda tõlgitsetakse, \"Õiguse kuningas\", aga siis ka Saalemi kuningas, see on \"Rahu kuningas\",');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 3, 'kes on isata, emata, ilma suguvõsata, ilma päevade alguseta ja ilma eluotsata, ent kes on sarnastatud Jumala Pojaga - jääb preestriks igavesti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 4, 'Vaadake siis, kui suur on see, kellele ka peavanem Aabraham andis saagist kümnist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 5, 'On ju neil Leevi poegadelgi, kes saavad preestriameti, käsk võtta kümnist käsuõpetuse järgi rahva, see on oma vendade käest, ehk küll nemadki on lähtunud Aabrahami niudeist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 6, 'Kuid see, kes ei põlvnenudki nendest, võttis kümnise Aabrahami käest ja õnnistas teda, kellel olid tõotused.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 7, 'Ent vasturääkimata on selge, et alam saab õnnistuse ülemalt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 8, 'Ja siin võtavad kümnist surelikud inimesed, aga seal see, kellest tunnistatakse, et ta elab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 9, 'Ja Aabrahami kaudu on, niiöelda, Leevigi, kes võtab kümnist, pidanud kümnist maksma;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 10, 'sest ta oli veel isa niudeis, kui Melkisedek temale vastu tuli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 11, 'Kui nüüd täiuslikkus oleks leviitide preestriameti kaudu olnud saavutatud - sest sellele on rajatud rahva käsuõpetus - mis vajadust siis veel oli, et teistsugune preester tõusis Melkisedeki korra järgi ega nimetatud teda Aaroni korra järgi?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 12, 'Sest preestriameti muutmisega sünnib paratamatult ka käsuõpetuse muutus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 13, 'Sest See, Kellest seda öeldakse, on pärit teisest suguharust, kellest veel ükski ei ole saanud altari juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 14, 'On ju teada, et meie Issand on tärganud Juudast, kelle suguharule Mooses ei ole midagi kõnelnud preestriametist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 15, 'Ja asi saab veel palju selgemaks, kui tõuseb teistsugune Melkisedeki sarnane Preester,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 16, 'Kes ei ole lihaliku käsusõna käsku mööda saanud preestriks, vaid hääbumatu elu väge mööda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 17, 'Sest tunnistatakse: \"Sina oled preester igavesti Melkisedeki korra järgi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 18, 'Endise käsusõna tühistus sünnib ju selle tõttu, et see oli jõuetu ja kasutu -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 19, 'ei ole ju käsk midagi viinud täiuslikkusele - ent asemele seatakse parem lootus, mille kaudu me tuleme Jumala ligi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 20, 'Ja niivõrd kui see ei tekkinud ilma vandeta - sest need on saanud preestreiks ilma vandeta,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 21, 'Tema aga vandega selle seadmisel, kes Temale ütles: \"Issand on vandunud ja Tema ei kahetse seda mitte: Sina oled preester igavesti!\" -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 22, 'Sedavõrd on palju parem ka leping, mille käemeheks on saanud Jeesus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 23, 'Oli ju neid palju, kes said preestreiks, sellepärast et surm neid keelas jäämast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 24, 'Aga Temal on selle tõttu, et Ta jääb igavesti, preestriamet, mis ei lähe kellegi teise kätte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 25, 'Sellepärast võib Ta ka täielikult päästa need, kes tulevad Tema läbi Jumala juurde, elades aina selleks, et kosta nende eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 26, 'Niisugune Ülempreester peabki ju meil olema: püha, veatu, laitmatu, eraldatud patustest ja kõrgemale saanud kui taevad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 27, 'Kellel ei ole mitte iga päev vaja nõnda nagu ülempreestril esiti tuua ohvreid enese pattude eest ja siis rahva pattude eest; sest seda on Ta igaks puhuks teinud, kui Ta Iseenese tõi ohvriks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 28, 'Sest käsk seab nõdrad inimesed ülempreestreiks, aga vande sõna, mis on hilisem käsuõpetusest, seab Poja, Kes on täiuslikuks saanud igaveseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 1, 'Aga peaasi selles, millest me kõneleme, on see: meil on selline Ülempreester, Kes istub Ausuuruse istme paremal käel taevas,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 2, 'pühamu ja tõelise telgi ametitalitaja, mille on ehitanud Jumal ja mitte inimene.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 3, 'Sest iga ülempreester seatakse ohverdama ande ja ohvreid, mispärast on paratamata vajalik, et Tal oleks midagi ning seda, mida ohverdada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 4, 'Kui Ta nüüd oleks maa peal, ei Ta siis olekski preester, sest on olemas neid, kes käsuõpetuse järgi ohverdavad ande');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 5, 'ja kes teenivad selles, mis on taevase pühamu kuju ja vari, nagu Moosesele ilmutati, kui ta hakkas telki valmistama. Sest nii öeldi talle: \"Vaata, et sa kõik teed selle eeskuju järgi, mis sulle mäel näidati!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 6, 'Aga nüüd on Tema saanud seda kallima ameti, mida parema lepingu vahemees Ta ka on, mis on rajatud parematele tõotustele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 7, 'Sest kui esimene leping oleks olnud laitmatu, ei siis oleks otsitud aset teisele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 8, 'Sest neid laites ütleb prohvet neile: \"Vaata, päevad tulevad, ütleb Issand, mil Ma teen Iisraeli sooga ja Juuda sooga uue lepingu:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 9, 'mitte selle lepingu sarnase, mille Ma tegin nende vanematega sel päeval, mil Ma võtsin nad kättpidi, et viia nad välja Egiptusemaalt - sest nad ei jäänud Minu lepingusse ja nii Minagi ei hoolinud neist, ütleb Issand -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 10, 'vaid leping, mille Ma teen Iisraeli sooga pärast neid päevi, ütleb Issand, on niisugune: Ma panen neile meelde Oma käsud ja kirjutan need neile südamesse; siis Ma olen neile Jumalaks ja nemad on Mulle rahvaks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 11, 'Siis ei õpeta keegi enam oma kaaskodanikku ega vend oma venda, öeldes: \"Tunne Issandat!\", sest nad kõik tunnevad Mind, niihästi nende pisukesed kui nende suured.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 12, 'Sest Ma olen armuline nende ülekohtustele tegudele ega tuleta enam meelde nende patte!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 13, 'Kui ta nimetab uut, siis on ta esimese tunnistanud vananenuks. Aga mis vananeb ja iganeb, on hävimisele ligi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 1, 'Esimesel lepingul olid ju ka oma Jumalateenistuse korraldused ja oma maine pühamu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 2, 'Sest enne valmistati esimene telk, milles olid küünlajalg ja laud ja vaateleivad laual; seda kutsutakse \"pühaks paigaks.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 3, 'Ent teise eesriide taga oli telk, mida kutsutakse \"kõige pühamaks paigaks.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 4, 'Seal oli kuldne suitsutusaltar ja seaduselaegas, mis oli üleni kullaga karratud; selle sees oli kuldkruus mannaga ja Aaroni kepp, mis oli õitsenud, ja seaduselauad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 5, 'Ent laeka peal olid auhiilguse keerubid, kes varjasid lepituskaant. Sellest kõigest ei ole nüüd tarvis üksikasjalikult rääkida.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 6, 'Et need kõik nõnda on korrastatud, siis lähevad eestelki alati preestrid, kes toimetavad Jumalateenistusi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 7, 'aga teise läheb korra aastas üksnes ülempreester, mitte ilma vereta, mille ta ohverdab oma ja rahva eksimuste eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 8, 'Sel kombel näitab Püha Vaim, et tee pühasse paika veel ei ole avatud, niikaua kui esimene telk veel on olemas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 9, 'See on praeguse aja kohta võrdum, mille kohaselt ohverdatakse ande ja ohvreid, mis ei suuda teha südametunnistuse poolest täiuslikuks seda, kes toimetab teenistust,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 10, 'vaid mis samuti nagu toidud ja joogid ja mitmesugused pesemised on ainult liha korraldused, kehtivad seniks, kui kätte jõuab uuendatud korra aeg.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 11, 'Aga Kristus, kui Tema tuli tulevaste heade andide Ülempreestrina, suurema ja täiuslikuma telgiga, mis ei ole kätega tehtud, see on, mis ei ole pärit sellest loomisest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 12, 'ja ka mitte sikkude ega vasikate verega, vaid Iseenese Verega, läks ükskord sinna pühasse paika ning saavutas igavese lunastuse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 13, 'Sest kui sikkude ja härgade veri ja lehma tuhk, mis riputatakse nende peale, kes on rüvetunud, pühitseb liha puhtuseks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 14, 'kui palju enam Kristuse Veri, Kes igavese Vaimu läbi Iseenese veatuna ohverdas Jumalale, puhastab meie südametunnistuse surnud tegudest teenima elavat Jumalat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 15, 'Ja sellepärast on Tema uue lepingu vahemees, et pärast Tema surma, mis oli lunastuseks esimese lepingu ajal olnud üleastumistest, need, kes on kutsutud, kätte saaksid igavese pärandi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 16, 'Sest seal, kus on olemas testament, on hädavajalik, et tõendataks ka testamenditegija surm.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 17, 'Sest testament hakkab kehtima pärast surma; sest see ei ole kunagi jõus, niikaua kui testamenditegija on elus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 18, 'Sellepärast ei ole ka esimest lepingut pühitsetud ilma vereta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 19, 'Sest kui Mooses kõigele rahvale oli ette lugenud kogu käsu käsuõpetuse järgi, võttis ta vasikate ja sikkude vere ühes vee ja punase villa ja iisopiga ja piserdas seda niihästi raamatu enese kui ka kõige rahva peale');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 20, 'ning ütles: \"See on selle lepingu veri, mille Jumal on seadnud teie jaoks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 21, 'Ja samuti ta piserdas verd ka telgi ja kõigi Jumalateenistuseriistade peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 22, 'Ja peaaegu kõik puhastatakse verega käsuõpetuse järgi, ja ilma verd valamata ei ole andeksandmist olemas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 23, 'Nii on siis tarvis, et taevalike asjade kujud puhastatakse sel teel, aga taevasi asju endid puhastatakse paremate ohvritega kui need.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 24, 'Sest Kristus ei läinud kätega tehtud pühamusse kui tõelise püha paiga kujusse, vaid taevasse Enesesse, et nüüd ilmuda Jumala palge ette meie eest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 25, 'aga mitte Ennast mitu korda ohverdama, nõnda nagu ülempreester igal aastal läks pühasse paika võõra verega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 26, 'sest muidu oleks Ta pidanud mitu korda kannatama maailma rajamisest alates; aga nüüd on Ta maailmaajastute lõpul korra ilmunud, et Oma ohvriga kaotada patt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 27, 'Ja otsekui inimestele on määratud kord surra, aga pärast seda kohus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 28, 'nõnda ka Kristus, Kes üks kord tõi Enese ohvriks, et ära võtta paljude patud, ilmub teist korda ilma patuta neile, kes Teda ootavad õndsuseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 1, 'Käsk, olles ainult tulevaste hüvede vari, aga mitte asjade täiskuju ise, ei või iialgi samade iga-aastaste ohvritega, mida ühtelugu ohverdatakse, teha täiuslikuks nende toojaid,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 2, 'sest eks muidu oleksid ju ohvrite toomised lõppenud, sellepärast et neil, kes toimetavad jumalateenistust, ei oleks enam mingit patust südametunnistust, kui nad kord on puhtaks tehtud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 3, 'Ent ohvritega tuletatakse iga aasta patte meelde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 4, 'Sest on võimatu, et härgade ja sikkude veri võib patud ära võtta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 5, 'Sellepärast Ta maailma tulles ütleb: \"Ohvrit ega andi Sa ei tahtnud, aga Sa valmistasid Mulle ihu:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 6, 'põletus- ja patuohvrid ei olnud Sulle meelepärast!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 7, 'Siis Ma ütlesin: vaata, Ma tulen - rullraamatus on Minust kirjutatud - tegema Su tahtmist, oh Jumal!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 8, 'Kuna Ta eespool ütleb: \"Ohvreid ja ande ning põletus- ja patuohvreid Sa ei tahtnud ega olegi need Su meele järgi, kuigi neid käsu järgi tuuakse\",');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 9, 'siis ütleb Ta pärast: \"Vaata, Ma tulen tegema Su tahtmist!\" Sellega Ta tunnistab tühjaks esimese ohvri, et panna kehtima teine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 10, 'Selles tahtmises oleme meiegi pühitsetud Jeesuse Kristuse Ihu ohvriga ühel hoobil.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 11, 'Ja iga preester seisab päevast päeva oma ametis ja ohverdab sageli samu ohvreid, mis iialgi ei või patte ära võtta;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 12, 'aga Tema, kui Ta üheainsa ohvri oli viinud pattude eest, on istunud jäädavalt Jumala paremale käele');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 13, 'ja ootab nüüd ainult, kuni Ta vaenlased pannakse Tema jalgealuseks järiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 14, 'Sest ühe ohvriga on Ta jäädavalt teinud täiuslikeks need, keda pühitsetakse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 15, 'Seda tunnistab meile ju ka Püha Vaim; sest kui Ta oli öelnud:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 16, '\"Niisugune on leping, mille Ma teen nendega pärast neid päevi, ütleb Issand: Ma panen neile südamesse Oma käsud ja kirjutan need neile meelde');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 17, 'ega mäleta enam nende patte ja ülekohtutegusid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 18, 'Aga kus need on andeks antud, seal ei ole enam tarvis ohvrit nende eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 19, 'Nii on meil siis, vennad, Jeesuse Veres julgus sissepääsuks pühasse paika;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 20, 'ja selle on Ta meile avanud uueks ja elavaks teeks eesriide, see on Oma liha kaudu;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 21, 'ja meil on suur Preester Jumala koja üle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 22, 'Astugem siis ligi tõelise südamega, täielikus usus, südame poolest piserdamisega puhastatud kurjast südametunnistusest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 23, 'ja ihu poolest pestud puhta veega, pidagem kõikumata kinni lootuse tunnistusest. Sest ustav on See, Kes seda on tõotanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 24, 'Ja pidagem üksteist silmas õhutamiseks armastusele ja headele tegudele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 25, 'Ning ärgem jätkem maha oma koguduse kooskäimisi, nõnda nagu mõnel on kombeks, vaid manitsegem üksteist, ja seda enam, et näete selle päeva lähenevat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 26, 'Sest kui me meelega pattu teeme, pärast seda kui oleme saanud tõe tunnetuse, siis ei jää enam mingit ohvrit üle pattude eest;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 27, 'vaid mingi hirmus kohtu ootamine ja äge tuli, mis tahab ära süüa vastupanijad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 28, 'Kui keegi rikub Moosese käsku, siis peab ta ilma armuta surema kahe või kolme tunnistaja sõna peale,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 29, 'kui palju hirmsama nuhtluse te arvate siis ära teeninud olevat selle, kes Jumala Poega jalgadega tallab ega pea pühaks lepingu Verd, millega ta on pühitsetud, ja teotab armu Vaimu?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 30, 'Me tunneme ju Seda, Kes ütleb: \"Minu käes on kättemaks, Mina tasun kätte!\" ja taas: \"Issand mõistab kohut Oma rahvale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 31, 'Hirmus on elava Jumala kätte langeda!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 32, 'Aga tuletage meelde endisi päevi, mil teie, olles valgustatud, talusite palju kannatamiste võitlust,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 33, 'kord nii, et te teotamiste ja viletsuste läbi olite tehtud maailma naeruks, kord jälle nii, et saite nende osalisteks, kellel oli sama saatus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 34, 'Sest te olete ühes vangidega kannatanud ja rõõmuga vastu võtnud oma vara riisumise, teades, et teil on parem ja jäädav vara taevas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 35, 'Ärge siis heitke ära oma julgust, mis saab suure palga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 36, 'Sest kannatlikkust läheb teile tarvis, et teie, tehes Jumala tahtmist, saaksite kätte tõotuse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 37, 'Sest \"veel üsna pisut, pisut aega, siis tuleb See, Kes peab tulema ega viivita mitte!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 38, 'Aga Minu õige peab usust elama, ja kui ta hakkab kõhklema, siis ei ole Minu hingel temast head meelt!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 39, 'Ent meie ei ole need, kes kõhklevad hukatuseks, vaid need, kes usuvad hinge päästmiseks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 1, 'Aga usk on kindel usaldus selle vastu, mida oodatakse, ja veendumus selles, mida ei nähta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 2, 'Selle kohta on ju vanad saanud tunnistuse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 3, 'Usu kaudu me tunneme, et maailmad on valmistatud Jumala sõna läbi, nii et mitte sellest, mida võib näha, ei ole tekkinud see, mida nähakse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 4, 'Usu läbi tõi Aabel Jumalale parema ohvri kui Kain, ja usu kaudu ta sai tunnistuse, et tema oli õige, kui Jumal andis tunnistuse tema andide kohta, ja usu kaudu ta räägib veel pärast surmagi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 5, 'Usu läbi võeti ära Eenok, et ta surma ei näeks, ja teda ei leitud enam, sest Jumal oli ta ära võtnud. Sest enne äravõttu oli ta juba saanud tunnistuse, et ta oli Jumalale meelepärane.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 6, 'Aga ilma usuta on võimatu olla meelepärane; sest kes Jumala juurde tuleb, peab uskuma, et Tema on olemas ja et Ta annab palga neile, kes Teda otsivad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 7, 'Usu kaudu sai Noa ilmutuse sellest, mida veel ei olnud näha, ja ehitas pühas kartuses laeva oma perekonna päästmiseks; ja selle kaudu ta mõistis hukka maailma ning sai selle õiguse pärijaks, mis tuleb usust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 8, 'Usu läbi kuulas Aabraham sõna, kui teda kutsuti välja minema paika, mille ta pidi saama pärandiks; ja ta läks välja ilma teadmata, kuhu ta läheb.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 9, 'Usu läbi asus ta võõrana elama tõotusemaale otsekui võõrale maale ja elas telkides ühes Iisaki ja Jaakobiga, kes olid sama tõotuse kaaspärijad;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 10, 'sest ta ootas linna, millel on alused ja mille ehitaja ning valmistaja on Jumal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 11, 'Usu läbi sai isegi Saara rammu soo rajamiseks ja pealegi üle oma ea, sellepärast et ta pidas ustavaks teda, kes tõotuse oli andnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 12, 'Sellepärast sündis ka ühest ja pealegi elatanud mehest nii palju nagu taevas tähti ja nagu mere ääres liiva, mida ei saa ära lugeda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 13, 'Usus need kõik surid ega saanud tõotusi kätte, vaid nägid neid kaugelt ja teretasid neid ning tunnistasid endid olevat võõrad ja majalised maa peal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 14, 'Sest need, kes seda ütlevad, näitavad, et nad otsivad kodumaad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 15, 'Ja kui nad oleksid mõelnud seda kodumaad, kust nad olid väljunud, siis oleks neil ju olnud juhust minna tagasi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 16, 'Ent nüüd nad igatsevad paremat kodumaad, see on taevast; seepärast ei ole Jumalal neist häbi ja Ta laseb Ennast nimetada nende Jumalaks; sest Ta on neile valmistanud linna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 17, 'Usu läbi viis Aabraham ohvriks Iisaki, kui teda katsuti, ja ohverdas selle ainusündinu, kui ta oli saanud tõotused');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 18, 'ning kui temale oli öeldud: \"Iisakist loetakse sinu sugu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 19, 'Sest ta mõtles, et Jumal on võimeline ka surnuist üles äratama; selle mõistukujuks ta sai tema ka tagasi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 20, 'Usu läbi õnnistas ka Iisak Jaakobit ja Eesavit tulevaste asjade suhtes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 21, 'Usu läbi õnnistas Jaakob surres kumbagi Joosepi poegadest ja palvetas oma kepi pära najal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 22, 'Usu läbi tuletas Joosep oma eluotsal meelde Iisraeli laste väljumist ja tegi korralduse oma luude kohta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 23, 'Usu läbi varjasid vanemad Moosest pärast ta sündimist kolm kuud, sest nad nägid ta ilusa lapse olevat ega kartnud kuninga käsku.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 24, 'Usu läbi keeldus Mooses, kui ta suureks sai, kandmast vaarao tütrepoja nime,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 25, 'pidades paremaks ühes Jumala rahvaga kannatada viletsust kui üürikest aega nautida paturõõmu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 26, 'arvates Kristuse teotust suuremaks rikkuseks kui Egiptuse aarded; sest ta tõstis oma silmad tasu poole.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 27, 'Usu läbi ta jättis maha Egiptuse ega kartnud kuninga viha, sest otsekui nähes teda, kes on nähtamatu, püsis ta kindlana.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 28, 'Usu läbi ta pidas Paasapüha ja vere piserdust, et esmasündinute surmaja ei puudutaks neid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 29, 'Usu läbi nad läksid Punasest merest läbi nagu kuiva maad mööda, mida ka egiptlased katsusid teha, aga uppusid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 30, 'Usu läbi langesid Jeeriko müürid, kui seitse päeva oli käidud nende ümber.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 31, 'Usu läbi ei saanud hoor Raahab hukka ühes sõnakuulmatutega, kui ta salakuulajad oli rahuga vastu võtnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 32, 'Ja mida ma veel ütleksin? Mul puudub aeg jutustada Giideonist ja Baarakist ja Simsonist ja Jeftast ja Taavetist ja Saamuelist ja prohvetitest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 33, 'kes usu läbi võitsid ära kuningriigid, viisid täide õiguse, said kätte tõotused, sulgesid lõukoerte lõuad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 34, 'kustutasid tule väe, pääsesid ära mõõgatera eest, said nõtrusest tugevaks, said vägevaks sõjas, ajasid pakku võõraste sõjahulgad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 35, 'Naised said tagasi oma surnud ülestõusmise läbi. Teised lasksid ennast piinata ega võtnud vastu vabastamist, et saaksid parema ülestõusmise osalisiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 36, 'Teised said kogeda pilget ja rooska, peale selle ahelaid ja vangitorni.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 37, 'Neid on kividega visatud, piinatud, lõhki saetud, mõõgaga surmatud; nad on lambanahas ja kitsenahas käinud ühest kohast teise, puuduses, viletsuses, kurja kannatades, nemad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 38, 'kelle väärt maailm ei olnud. Nad eksisid ümber kõrbetes ja mägedel ja koobastes ja maa-aukudes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 39, 'Ja need kõik, olles usu läbi saanud tunnistuse, ei saavutanud ometi mitte seda, mis oli tõotatud;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 40, 'sest Jumal oli meile varunud midagi paremat, et nad ilma meieta ei pääseks täielikkusesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 1, 'Sellepärast meiegi, et nii suur pilv tunnistajaid on meie ümber, pangem maha kõik koorem ja meid nii hõlpsasti takerdav patt ning jookskem kannatlikkusega meile määratud võidujooksmist,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 2, 'vaadates usu alustajale ja täidesaatjale Jeesusele, Kes risti kannatas Temale oodatava rõõmu asemel, häbist hoolimata, ja on istunud Jumala aujärje paremale käele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 3, 'Kujutlege ometi Teda, Kes niisugust vastuhakkamist Enese vastu on saanud kannatada patustelt, et te ei väsiks ega läheks araks oma hinges.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 4, 'Teie ei ole veel vereni vastu pannud võideldes patuga');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 5, 'ja olete unustanud manitsuse, mis teile otsekui lastele ütleb: \"Mu poeg, ära põlga Issanda karistust ja ära saa araks, kui Tema sind noomib!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 6, 'Sest keda Issand armastab, seda Ta karistab; Ta peksab igat poega, keda Ta vastu võtab.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 7, 'Taluge karistust kasvatuseks: Jumal kohtleb teid kui poegi; sest milline poeg on see, keda isa ei karista?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 8, 'Ent kui te olete ilma karistuseta, millest kõik on osa saanud, siis te olete värdjad ja mitte pojad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 9, 'Pealegi on meile meie lihased isad olnud karistajaiks ja me oleme neid kartnud; kas me palju enam ei tahaks alistuda vaimude Isale ning elada?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 10, 'Sest nemad on küll meid mõnd päeva oma heaksarvamist mööda karistanud, aga Tema karistab meid tõesti selle hea otstarbega, et me saaksime osa Tema pühadusest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 11, 'Aga mingi karistus, kui see on käes, ei näi olevat rõõmuks, vaid on kurbuseks; aga pärast toob see neile, kes sellega on õpetatud, õiguse rahuvilja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 12, 'Seepärast ajage jälle sirgu lõdvad käed ja halvatud põlved');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 13, 'ja õgvendage teerajad oma jalgadele, et see, mis lonkav, ei nikastuks, vaid ennemini saaks terveks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 14, 'Nõudke rahu kõikidega ja pühitsust; ilma selleta ei saa ükski Issandat näha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 15, 'Ja pange tähele, et keegi ei jääks ilma Jumala armust, et ükski viha juur ei kasvaks üles ega tooks tüli ja selle läbi paljud ei rüvetuks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 16, 'et keegi ei oleks hooraja või roojane, kes nagu Eesav üheainsa kõhutäie eest andis käest esmasünniõiguse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 17, 'Sest te teate, kuidas ta pärast küll tahtis pärida õnnistust, aga siis hüljati kui kõlvatu; sest ta ei leidnud mahti meeleparanduseks, ehk ta seda küll silmapisaratega otsis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 18, 'Sest te ei ole astunud käega katsutava ja tules põleva mäe ligi, ei pimeduse ega pilkase pimeduse, ei maru,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 19, 'ei pasuna helina ega niisuguse sõnade hääle ligi, mille kuuljad palusid, et neile sõna enam ei räägitaks;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 20, 'sest nemad ei suutnud taluda seda keeldu: \"Isegi kui üks loom peaks puutuma mäe külge, siis visatagu ta kividega surnuks!\";');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 21, 'ja nii hirmus oli see nähtus, et Mooses ütles: \"Ma olen ehmunud ja värisen!\";');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 22, 'vaid te olete tulnud Siioni mäe ligi ja elava Jumala linna, taevase Jeruusalemma juurde ja lugematu hulga Inglite juurde,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 23, 'ja esmasündinute piduliku kogu ning koguduse juurde, kes on kirja pandud taevasse, ja Jumala, kõikide kohtumõistja juurde, ja õigete vaimude juurde, kes on saanud täiuslikeks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 24, 'ja uue lepingu vahemehe Jeesuse juurde ja Piserdamisvere juurde, mis paremini räägib kui Aabeli veri.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 25, 'Katsuge, et teiegi teda ei hülga, Kes räägib! Sest kui ei ole pääsenud need, kes maapealse kõneleja hülgasid, kui palju vähem pääseme meie, kui me pöördume ära Temast, kes on taevastest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 26, 'Kelle hääl tookord pani kõikuma maa, aga Kes nüüd on tõotanud ja öelnud: \"Veel kord Ma panen värisema mitte ainult maa, vaid ka taeva!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 27, 'Ent \"veel kord\" näitab, et see, mis kõigub, peab muutuma, sest ta on loodud, et püsiks see, mida ei saa kõigutada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 28, 'Seepärast, saades kuningriigi, mis ei kõigu, olgem tänulikud ja teenigem seega Jumalat tema meelt mööda pelglikkuse ja aukartusega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 29, 'Sest meie Jumal on hävitav tuli!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 1, 'Vendade armastus jäägu püsima!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 2, 'Ärge unustage külalislahkust, sest selle läbi on mõningad ilma teadmata külalisteks vastu võtnud Ingleid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 3, 'Pidage meeles vange, otsekui oleksite kaasvangid, ja vaevatuid, sest ka teie olete veel ihus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 4, 'Abielu olgu igapidi au sees ja abieluvoodi rüvetamata! Sest Jumal nuhtleb hoorajaid ja abielurikkujaid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 5, 'Teie meelelaad olgu rahaahnuseta; olge rahul sellega, mis teil on. Sest Tema on öelnud: \"Ma ei hülga sind ega jäta sind maha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 6, 'Nii võime siis julgesti öelda: \"Issand on minu abimees, ei ma karda! Mis võib inimene mulle teha?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 7, 'Mõelge oma juhatajaile, kes teile on rääkinud Jumala sõna, pannes tähele nende eluotsa järgige nende usku!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 8, 'Jeesus Kristus on Seesama, eile ja täna ja igavesti!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 9, 'Ärge laske endid vintsutada mitmelaadiliste ja võõraste õpetustega; sest hea on, et süda kinnitatakse armu läbi, aga mitte toitudega, millest ei ole saanud kasu need, kes nende määruste järgi käivad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 10, 'Meil on ohvrialtar, kust toidust võtta ei ole luba neil, kes telgis peavad teenistust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 11, 'Sest nende loomade kehad, kelle vere ülempreester viib ohvriks patu eest pühasse paika, põletatakse väljaspool leeri.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 12, 'Seepärast on ka Jeesus, et pühitseda rahvast Oma Vere läbi, kannatanud väljaspool väravat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 13, 'Siis mingem nüüd Tema juurde väljapoole leeri ning kandkem Tema teotust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 14, 'Sest meil pole siin jäädavat linna, vaid meie otsime tulevast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 15, 'Viigem siis nüüd Tema kaudu alati Jumalale kiitusohvrit, see on nende huulte vilja, kes tunnistavad Tema nime.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 16, 'Ärge unustage head teha ja osadust pidada, sest sellised ohvrid on Jumala meele järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 17, 'Olge sõnakuulelikud oma juhatajaile ja alistuge neile; sest nemad valvavad teie hingi nõnda nagu need, kellel tuleb aru anda, et nad teeksid seda rõõmuga ja mitte ohates; sest teil ei oleks sellest kasu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 18, 'Palvetage meie eest! Sest me oleme veendunud selles, et meil on hea südametunnistus ja me tahame kõiges käituda kaunisti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 19, 'Eriti ma palun seda teha selleks, et mind rutemini antaks teile tagasi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 20, 'Aga rahu Jumal, Kes surnuist on üles toonud igavese lepingu Vere läbi lammaste suure Karjase, meie Issanda Jeesuse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 21, 'varustagu teid kõige heaga Tegema Tema tahtmist ja saatku teie sees korda seda, mis on Tema meelt mööda, Jeesuse Kristuse läbi, Kellele olgu austus ajastute ajastuteni! Aamen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 22, 'Aga ma palun teid, vennad, võtke heaks see manitsussõna! Ma olen teile ju lühidalt kirjutanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 23, 'Teadke, et meie vend Timoteos on jälle vaba. Temaga ühes, kui ta varsti tuleb, saan ma teid näha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 24, 'Tervitage kõiki oma juhatajaid ja kõiki pühasid! Teid tervitavad Itaalia vennad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 25, 'Arm olgu teie kõikidega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 1, 'Jakoobus, Jumala ja Issanda Jeesuse Kristuse sulane, saadab tervisi hajuvil asuvaile kaheteistkümnele suguharule.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 2, 'Pidage lausa rõõmuks, mu vennad, kui te satute mitmesugustesse kiusatustesse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 3, 'ja teadke, et teie usu katsumine saadab kannatlikkust!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 4, 'Ent kannatlikkus omagu täiuslikku tegu, et te oleksite täiuslikud ja laitmatud ega oleks teil mingit puudust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 5, 'Aga kui kellelgi teist on puudu tarkusest, see palugu Jumalalt, Kes kõigile annab suisa ega tee etteheiteid, ja siis antakse temale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 6, 'Ent ta palugu usus, ilma kahtlemata; sest kes kahtleb, sarnaneks merelainele, mida tuul tõstab ning sinna ja tänna peksab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 7, 'Niisugune inimene ärgu ometi arvaku, et ta midagi saab Issandalt;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 8, 'ta on kaksipidise meelega mees, ebakindel kõigil oma teedel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 9, 'Ent madal vend kiidelgu oma suurusest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 10, 'aga rikas oma madalusest, sest ta kaob nagu rohu õieke.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 11, 'Sest päike tõusis palavaga ja kuivatas rohu ära, ja selle õieke varises maha ning tema näo ilu hävis. Nõnda närtsib ka rikas oma retkedel!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 12, 'Õnnis see mees, kes ära kannatab kiusatuse; sest kui ta on läbi katsutud, saab ta elukrooni, mille Issand on tõotanud neile, kes Teda armastavad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 13, 'Ärgu ükski kiusatuses olles öelgu: \"Mind kiusab Jumal!\" Sest Jumalat ei kiusata kurjaga ja Tema Ise ei kiusa kedagi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 14, 'Aga igaüht kiusatakse, kui tema enese himu teda veab ning ahvatleb;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 15, 'pärast, kui himu on rasestunud, toob ta patu ilmale, aga kui patt on täiesti tehtud, sünnitab ta surma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 16, 'Ärge eksige, mu armsad vennad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 17, 'Kõik hea and ja kõik täiuslik annetus on ülalt ja tuleb valguse Isalt, Kelle juures ei ole muutust ega varjutuste varju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 18, 'Tema on meid Oma tahtel sünnitanud tõe sõnaga, et oleksime Tema loodute esmasündinute hulgast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 19, 'Teie teate seda, mu armsad vennad. Olgu vaid iga inimene nobe kuulma, pikaline rääkima, pikaline vihale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 20, 'Sest mehe viha ei tekita õigust Jumala ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 21, 'Sellepärast heitke ära kõik rüvedus ja viimnegi paha ning võtke tasase meelega vastu sõna, mis teisse istutati ja võib teie hinged päästa!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 22, 'Aga olge sõna tegijad ja mitte ükspäinis kuuljad, iseendid pettes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 23, 'Sest kui keegi on sõna kuulja ja mitte tegija, siis on ta mehe sarnane, kes vaatab oma ihulikku palet peeglis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 24, 'Ta vaatas ennast, läks minema ja unustas varsti, missugune ta oli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 25, 'Aga kes vabaduse täiusliku käsu sisse kummardades on vaadanud ja jääb selle juurde, ei ole unustav kuulja, vaid on teo tegija; see on õnnis oma tegemises.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 26, 'Kui keegi arvab Jumalat teenivat ja ei talitse oma keelt, vaid petab oma südant, selle jumalateenistus on tühine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 27, 'Puhas ja laitmatu jumalateenistus Jumala ja Isa ees on see: vaestelaste ja lesknaiste eest hoolitseda nende viletsuses ja hoida ennast maailmast reostamatuna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 1, 'Mu vennad, pidage usku meie au Issandasse Jeesusesse Kristusesse, sõltumata isikute lugupidamisest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 2, 'Sest kui teie koosolekule tuleb mees, kuldsõrmus sõrmes ja uhkes rüüs, ent tuleb ka vaene räpases rüüs,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 3, 'ja te vaatate sellele, kes kannab uhket rüüd ning ütlete temale: \"Sina istu siia mugavasse kohta!\", ja ütlete vaesele: \"Sina seisa seal!\" või: \"Istu siia mu jalajäri ette!\",');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 4, 'kas te siis ei ole valesti otsustanud ja saanud halbade juhtmõtetega kohtumõistjaiks?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 5, 'Kuulge, mu armsad vennad, kas ei ole Jumal valinud selle maailma vaesed saama rikkaiks usus ja Kuningriigi pärijaiks, mille Ta on tõotanud neile, kes Teda armastavad?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 6, 'Teie aga olete vaest halvaks pannud. Eks tarvita just rikkad vägivalda teie vastu ja vea teid kohtukodadesse?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 7, 'Eks nemad pilka seda kallist nime, mis on nimetatud teie üle?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 8, 'Kui te tõesti täidate kuninglikku käsku Kirja sõna järgi: \"Armasta oma ligimest nagu iseennast!\", siis te teete hästi;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 9, 'aga kui te peate ühest rohkem lugu kui teisest, siis te teete pattu ja käsk tunnistab teid üleastujaiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 10, 'Sest kes kogu käsuõpetust peab ja eksib ühe vastu, on saanud süüdlaseks kõigi vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 11, 'Sest See, Kes on öelnud: \"Sa ei tohi abielu rikkuda!\", on ka öelnud: \"Sa ei tohi tappa!\" Kui sa nüüd ei riku abielu, aga tapad, siis oled saanud käsust üleastujaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 12, 'Nõnda rääkige ja nõnda toimige kui need, kellele tuleb kohut mõista vabaduse käsu järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 13, 'Sest kohus on halastuseta sellele, kes ei ole osutanud halastust; kuid halastus võib võidurõõmutseda kohtu üle!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 14, 'Mis kasu on sellest, mu vennad, kui keegi ütleb enesel usku olevat, aga tegusid temal ei ole? Ega usk või teda õndsaks teha?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 15, 'Kui vend või õde on alasti ja neil puudub igapäevane toidus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 16, 'ja keegi teie seast ütleb neile: \"Minge rahuga, soojendage endid ja sööge kõhud täis!\", aga te ei anna neile ihu tarbeid, mis on sellest kasu?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 17, 'Nõnda ka usk, kui tal ei ole tegusid, on iseenesest surnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 18, 'Aga mõni ehk ütleb: \"Sinul on usk ja minul on teod!\" Näita mulle oma usku lahus tegudest, küll mina näitan sulle usku oma tegudega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 19, 'Sina usud, et Jumal on ainus. Sa teed hästi! Ka kurjad vaimud usuvad seda ja värisevad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 20, 'Ent kas tahad teada, oh tühine inimene, et usk lahus tegudest on tõhutu?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 21, 'Eks Aabraham, meie isa, mõistetud õigeks tegudest, kui ta oma poja Iisaki viis ohvrialtarile?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 22, 'Sa näed, et usk töötas ühes tema tegudega ja et tegudest sai usk täiuslikuks');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 23, 'ja täitus Kiri, mis ütleb: \"Aabraham uskus Jumalat ja see arvati temale õiguseks ning ta nimetati Jumala sõbraks.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 24, 'Te näete, et inimene mõistetakse õigeks tegudest ja mitte ükspäinis usust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 25, 'Ja eks nõndasamuti ka hoor Raahab mõistetud õigeks tegudest, kui ta vastu võttis käskjalad ja nad ära saatis teist teed?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 26, 'Sest samuti nagu ihu ilma vaimuta on surnud, nõnda ka usk ilma tegudeta on surnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 1, 'Mu vennad, ärgu püüdku paljud saada õpetajaiks, sest te teate, et me saame seda suurema nuhtluse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 2, 'Sest me kõik eksime palju. Kui keegi kõnes ei eksi, siis ta on täiuslik mees ja võib ka talitseda kogu ihu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 3, 'Kui me paneme suulised hobustele suhu, et nad oleksid meile sõnakuulelikud, siis me juhime kogu nende keha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 4, 'Vaata laevugi, ehk need küll on suured ja kangeist tuultest aetavad, juhitakse üsna väikese tüüriga sinna, kuhu päramehe tahtele meeldib.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 5, 'Nõnda on ka keel pisuke liige ja kiitleb suurist asjust. Vaata, kui pisuke tuli süütab suure metsa!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 6, 'Samuti on keel tuli, on maailm täis ülekohut; keel meie liikmete seas saab selliseks, mis reostab kogu ihu ja süütab põlema eluratta, nagu ta ise on süüdatud põrgust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 7, 'Sest kõikide, niihästi metselajate kui lindude, niihästi roomajate kui mereelajate loomu võib inimese loomujõud talitseda ja on talitsenud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 8, 'kuid keelt ei suuda ükski inimene talitseda, seda rahutut pahategijat, täis surmavat mürki.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 9, 'Sellega me täname Issandat ja Isa, ja sellega me sajatame inimesi, kes on loodud Jumala sarnaseiks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 10, 'Samast suust lähtuvad tänu ja sajatus! Nii ei tohi olla, mu vennad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 11, 'Kas allikas samast soonest keedab välja magusat ja mõru vett?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 12, 'Ega viigipuu, mu vennad, või kanda õlimarju või viinapuu viigimarju? Ei soolaallikas või anda magusat vett!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 13, 'Kes teie seas on tark ja arusaaja? Näidaku see oma hea eluviisiga oma tegusid targas tasaduses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 14, 'Kui teil aga on südames kibedat kadedust ja riiakat meelt, siis ärge kiidelge ja ärge valetage tõe vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 15, 'See ei ole see tarkus, mis tuleb ülalt, vaid muldne, maine, kurjast vaimust pärit.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 16, 'Sest kus on kadedust ja riiakust, seal on korratust ja igat halba asja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 17, 'Ent tarkus, mis on ülalt on küll kõigepealt puhas, siis rahulik, leplik, sõnakuulelik, täis halastust ja head vilja, kahtluseta, teeskluseta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 18, 'Õiguse vilja aga külvatakse rahus nende heaks, kes peavad rahu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 1, 'Kust tõusevad võitlemised ja kust tülid teie seas? Kas mitte sealt, teie himudest, mis sõdivad teie liikmetes?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 2, 'Te himustate ja teil siiski ei ole; te tapate ja kadestate ega või midagi saavutada; te tülitsete ja sõdite. Teil ei ole, sest te ei palu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 3, 'Te palute ja ei saa, sest te palute pahasti, tahtes seda kulutada oma himudes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 4, 'Te abielurikkujad, eks te tea, et maailma sõprus on vaen Jumala vastu? Kes nüüd tahab olla maailma sõber, see saab Jumala vaenlaseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 5, 'Või arvate, et Kiri asjata ütleb: \"Kadeduseni ta himustab vaimu, kes meis elab\"?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 6, 'Aga ta annab veel suuremat armu; sellepärast Kiri ütleb: \"Jumal paneb suurelistele vastu, aga alandlikele Ta annab armu.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 7, 'Siis alistuge Jumalale! Seiske vastu kuradile, siis ta põgeneb teie juurest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 8, 'Tulge Jumala ligi, siis Tema tuleb teie ligi! Puhastage käed, te patused, ja kasige südamed, te kaksipidi mõtlejad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 9, 'Tundke ära oma viletsus ja leinake ja nutke! Teie naer muutugu nutuks ja teie rõõm tusaks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 10, 'Alanduge Issanda ette, siis Ta ülendab teid!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 11, 'Ärge rääkige, vennad, paha üksteisest! Kes vennast räägib paha ja mõistab kohut venna üle, see räägib paha käsuõpetusest ja mõistab kohut käsuõpetuse üle. Aga kui sa mõistad kohut käsuõpetuse üle, siis sa ei ole käsutäitja, vaid kohtumõistja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 12, 'Üks on Käsuandja ja Kohtumõistja, Kes võib teha õndsaks ja panna hukka; aga kes oled sina, kes mõistad kohut ligimese üle?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 13, 'Kuulge nüüd, kes ütlete: \"Täna või homme me läheme sinna linna ja viibime seal aasta ja kaupleme ning saavutame kasu!\" -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 14, 'teie, kes ei teagi, missugune on homme teie elu; sest te olete suits, mida pisut aega nähakse ja mis siis haihtub -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 15, 'selle asemel et öelda: \"Kui Issand tahab ja me elame, siis teeme seda või teist!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 16, 'Ent nüüd te kiitlete oma hooplemisega. Kõik niisugune kiitlemine on paha.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 17, 'Kes siis mõistab teha head ja ei tee seda, sellele on see patuks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 1, 'Kuulge nüüd, te rikkad, nutke ja uluge oma viletsusi, mis on tulemas!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 2, 'Teie rikkus on mädanenud ja teie riided on koitanud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 3, 'Teie kuld ja hõbe on roostetanud ja nende rooste on teile tunnistuseks ja sööb ära kõik teie liha nagu tuli! Te olete kogunud vara viimseil päevil.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 4, 'Vaata, töötegijate palk, mille te olete kinni pidanud neilt, oma põldudel lõikajailt, kisendab teie vastu, ja lõikajate kaebed on tunginud vägede Issanda kõrvade ette!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 5, 'Te olete priisanud ja prassinud maa peal; te olete nuumanud oma südameid otsekui veristuspäeval!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 6, 'Te olete hukka mõistnud Õige ja olete Ta tapnud; Ta ei pane teile vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 7, 'Siis olge nüüd pikameelsed, vennad, Issanda tulemiseni! Vaata, põllumees ootab maa kallist vilja ja on pikameelne seda oodates, kuni ta saab varajase ja hilise vihma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 8, 'Olge nüüd teiegi pikameelsed, kinnitage oma südameid, sest Issanda tulemine on lähedal!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 9, 'Ärge ägage, vennad, üksteise pärast, et teid hukka ei mõistetaks! Vaata, Kohtumõistja seisab ukse ees!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 10, 'Võtke, vennad, vaeva kannatamise ja pikameelse ootamise eeskujuks prohvetid, kes Issanda nimel on rääkinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 11, 'Vaata, me kiidame õndsaks neid, kes on olnud püsivad kannatustes; Iiobi püsivusest te olete kuulnud ja näinud Issanda antud otsa, et Issand on väga halastav ja armuline.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 12, 'Aga kõigepealt, mu vennad, ärge vanduge, ei taeva ega maa juures ega mingit muud vannet! Teie \"jah\" olgu \"jah\", ja teie \"ei\" olgu \"ei\", et te ei langeks kohtu alla!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 13, 'Kui kellelgi teie seast on vaeva, siis ta palvetagu; kui kellegi käsi hästi käib, siis ta laulgu kiituslaule.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 14, 'Kui keegi teie seast on haige, siis ta kutsugu enese juurde Koguduse vanemad ja need palvetagu tema kohal ja võidku teda õliga Issanda nimel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 15, 'Ja usupalve päästab tõbise, ja Issand teeb ta terveks; ja kui ta on pattu teinud, siis see antakse temale andeks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 16, 'Tunnistage üksteisele oma eksimused ja palvetage üksteise eest, et saaksite terveks; õige inimese vägev palve suudab palju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 17, 'Eelija oli samasugune nõder inimene nagu meiegi, ja ta palus kangesti, et ei sajaks vihma; ja kolm aastat ja kuus kuud ei sadanud vihma maa peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 18, 'Ja tema palus jälle ja taevas andis vihma ja maa kandis oma vilja.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 19, 'Vennad, kui keegi teie seast eksib ära tõest ja keegi pöörab tema tagasi,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 20, 'siis teadke, et kes patuse pöörab tema eksiteelt, see päästab tema hinge ja katab kinni pattude hulga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 1, 'Peetrus, Jeesuse Kristuse Apostel, Pontoses, Galaatias, Kapadookias, Aasias ja Bitüünias hajuvil asuvaile valitud majalistele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 2, 'Jumala Isa etteteadmist mööda Vaimu pühitsemise kaudu sõnakuulmiseks ja piserdamiseks Jeesuse Kristuse Verega! Arm ja rahu rohkenegu teile!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 3, 'Kiidetud olgu Jumal ja meie Issanda Jeesuse Kristuse Isa, Kes Oma suurt halastust mööda meid on uuesti sünnitanud elavaks lootuseks Jeesuse Kristuse ülestõusmise läbi surnuist');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 4, 'kadumatu ja rüvetamatu ja närtsimatu pärandi saamiseks, mis taevas on tallel teie jaoks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 5, 'mida Jumala väega valvatakse usu läbi õndsuse jaoks, mis on valmis ilmsiks tulema viimsel ajal!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 6, 'Sellest te väga rõõmutsete, kuigi te nüüd vajaduse korral pisut kurvastute mõnesugustes kiusatustes,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 7, 'et teie usk, kui see on läbi katsutud, leitaks kallihinnalisem olevat kullast, mis kaob ja siiski tules läbi katsutakse, ja oleks teile kiituseks ja hiilguseks ja auks Jeesuse Kristuse ilmumisel,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 8, 'Keda te küll ei ole näinud, ja siiski armastate, Kellesse te nüüd usute, kuigi te Teda ei näe, ja rõõmutsete ütlematu ning väga kalli rõõmuga,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 9, 'kes saavutate usu eesmärgi, hingede õndsuse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 10, 'Seda õndsust on uurinud ja juurelnud prohvetid, kes on ennustanud teile määratud armust,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 11, 'juureldes seda, mis ja millise aja kohta teateid andis Kristuse Vaim nende sees, kui Ta ette ilmutas Kristust tabavaid kannatusi ja neile järgnevaid austusi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 12, 'Neile ilmutati, et mitte neile endile, vaid teile oli kasuks see, mis nüüd on kuulutatud teile nende kaudu, Kes teile taevast läkitatud Püha Vaimu läbi on toonud Evangeeliumi; ja sellesse igatsevad Inglidki vaadata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 13, 'Sellepärast vöötage oma meele niuded ja olge kained; ja lootke täiesti armu peale, mida teile pakutakse Jeesuse Kristuse ilmumises.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 14, 'Kui sõnakuulelikkuse lapsed ärge anduge endistele himudele, nagu siis kui teil puudus õige teadmine;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 15, 'vaid nõnda nagu See, Kes teid on kutsunud, on püha, saage ka ise pühaks kõigis eluviisides;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 16, 'sest on kirjutatud: \"Olge pühad, sest Mina olen püha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 17, 'Ja et te appi hüüate teda kui Isa, Kes isikule vaatamata mõistab kohut igaühe tegu mööda, siis veetke kartuses oma majaliseks olemise aeg,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 18, 'teades, et teid ei ole kaduvaga, hõbeda või kullaga lunastatud teie tühiseist esivanemailt päritud eluviisidest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 19, 'vaid Kristuse kui veatu ja laitmatu talle kalli Verega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 20, 'Kes küll oli ette määratud enne maailma rajamist, aga aegade lõpul on saanud ilmsiks teie pärast,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 21, 'kes Tema kaudu olete usklikud Jumalasse, kes Tema surnuist üles äratas ja Temale andis au, nii et teie usk on ka lootus Jumala peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 22, 'Et te olete oma hinged puhastanud tõe sõnakuulmises teesklematuks vennalikuks armastuseks, siis armastage üksteist innukalt ja südamest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 23, 'sest et olete uuesti sünnitatud mitte kaduvast, vaid kadumatust seemnest, Jumala elava ning püsiva sõna läbi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 24, 'Sest \"kõik liha on nagu rohi ja kõik tema hiilgus nagu rohu õieke; rohi kuivab ära ja õieke variseb maha,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 25, 'aga Issanda sõna püsib igavesti!\" Ja see on see sõna, mis teile rõõmusõnumina on kuulutatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 1, 'Siis pange nüüd maha kõik kurjus ja kõik kavalus ja silmakirjatsus ja kadedus ja kõik keelekandmine');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 2, 'ning himustage otsekui vastsündinud lapsed vaimset täispiima, et te selle läbi võiksite kasvada õndsuseks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 3, 'kui te olete maitsnud, et Issand on helde, tulles Tema juurde');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 4, 'kui elava kivi juurde, mis küll inimeste poolt on põlatud, aga Jumala juures on ära valitud ja väga kallis,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 5, 'ja teiegi ehituge üles kui elavad kivid vaimulikuks kojaks ja pühaks preesterkonnaks ohverdama vaimulikke ohvreid, mis on Jumalale meelepärased Jeesuse Kristuse kaudu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 6, 'Sellepärast ongi lugeda Kirjas: \"Vaata, Mina panen Siionisse valitud kalli nurgakivi, ja kes Temasse usub, see ei satu häbisse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 7, 'Teile nüüd, kes usute, on Ta kallihinnaline; aga uskmatuile on Ta \"kivi, mille hooneehitajad põlgasid, kuid mis sai nurgakiviks\",');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 8, 'ja \"komistuskiviks ja pahanduskaljuks\". Nemad \"tõukavad Tema vastu\", sest nad ei kuule sõna, ja selleks on need ka pandud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 9, 'Teie aga olete \"valitud sugu, kuninglik preesterkond, püha rahvas, omandrahvas, et te kuulutaksite Tema aulisi tegusid\", Kes teid on kutsunud pimedusest Oma imelise valguse juurde,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 10, 'teie, kes muiste olite \"mitterahvas\", aga nüüd olete Jumala rahvas; kes \"ei olnud armu saanud\", aga nüüd olete armu saanud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 11, 'Armsad, ma manitsen teid kui \"võõraid ja majalisi\" hoiduda lihalikest himudest, mis sõdivad hinge vastu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 12, 'ja elada vooruslikku elu paganate keskel, et nad selles, mille pärast nad teist kui kurjategijaist paha räägivad, teie häid tegusid nähes annaksid Jumalale austust \"katsumispäeval\"!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 13, 'Alistuge kõigele inimeste seatud korrale Issanda pärast, olgu kuningale kui ülemale valitsejale,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 14, 'või pealikuile kui neile, keda tema on läkitanud kurjategijaile nuhtluseks, aga heategijaile kiituseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 15, 'Sest nõnda on Jumala tahtmine, et teie, tehes head, tõkestaksite mõistmatute inimeste rumalust -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 16, 'kui vabad ja mitte kui need, kellele vabadus on kurjuse katteks, vaid kui Jumala sulased.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 17, 'Austage kõiki, armastage vendi, kartke Jumalat, austage kuningat!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 18, 'Sulased, olge kõige kartusega allaheitlikud oma isandaile, mitte ainult headele ja leplikele, vaid ka tigedaile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 19, 'Sest see on arm, kui keegi südametunnistuse pärast Jumala ees talub viletsusi, kannatades süütult.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 20, 'Sest mis kiitus see on, kui teid pekstakse patu pärast ning te jääte kannatlikuks? Aga kui te head tehes ja kurja kannatades püsite kannatlikena, siis on see arm Jumala juures.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 21, 'Selleks te ju olete kutsutud, sest et Kristuski kannatas teie eest ja jättis teile eeskuju, et te käiksite Tema jälgedes,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 22, '\"kes ei teinud pattu ja Kelle suust ei leitud pettust\",');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 23, 'Kes ei sõimanud vastu, kui Teda sõimati, Kes ei ähvardanud, kui Ta kannatas, vaid jättis selle hooleks, Kes mõistab kohut õigesti,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 24, 'Kes meie patud ise kandis üles ristipuule Oma ihus, et meie pattudele ära sureksime ja elaksime õigusele, Kelle vermete tõttu te olete terveks saanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 25, 'Sest te olite \"nagu eksijad lambad\", aga nüüd te olete pöördunud oma hingede Karjase ja Hooldaja poole.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 1, 'Samuti teie, naised, olge allaheitlikud oma meestele, et ka need, kes ei ole sõnakuulelikud sõnale, naiste eluviiside läbi võidetaks ilma sõnata usule,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 2, 'kui nad vaatavad, kuidas te elate jumalakartuses puhast elu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 3, 'Teie ehteks ärgu olgu välispidine juuste palmitsemine, kulla ümberriputamine ega toredate riietega riietumine,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 4, 'vaid varjul olev südame inimene, tasase ja vaikse vaimu kadumatuses, mis on kallis Jumala ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 5, 'Sest nõnda on ka ennevanasti pühad naised, kes lootsid Jumala peale, endid ehtinud ja olnud allaheitlikud oma meestele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 6, 'nõnda nagu Saara oli sõnakuulelik Aabrahamile ja \"hüüdis teda isandaks\", ja tema lasteks te olete saanud, kui te teete head ega karda ühtki hirmutust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 7, 'Samuti teie, mehed, elage naisega targasti kui nõrgema astjaga ja osutage neile austust, sest nad on elu armuanni kaaspärijad nagu teiegi, et teie palved poleks tagajärjeta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 8, 'Aga viimaks, olge kõik üksmeelsed, kaastundlikud, vennaarmastajad, halastajad, alandlikud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 9, 'Ärge tasuge kurja kurjaga ega sõimu sõimuga, vaid vastupidi, õnnistage, sest te teate, et olete kutsutud pärima õnnistust!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 10, 'Sest \"kes armastab elu ja näha häid päevi, see hoidku oma keelt kurja eest ja oma huuli pettust rääkimast;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 11, 'ta pöördugu ära kurjast ning tehku head; ta otsigu rahu ja nõudku seda taga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 12, 'Sest Issanda silmad on õigete poole ja tema kõrvad nende palvetamise poole; aga Issanda pale on pahategijate vastu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 13, 'Ja kes peaks teile kurja tegema, kui te heategemises olete agarad?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 14, 'Aga kuigi te kannataksite õiguse pärast, siis olete ometi õndsad. \"Ärge kartke nende hirmutamist ja ärge kohkuge\",');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 15, 'vaid pühitsege Issandat Kristust oma südameis, olles alati valmis andma vastust igaühele, kes teilt nõuab seletust lootuse kohta, mis teis on.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 16, 'Aga vastake tasaduse ja kartusega, ja teil olgu hea südametunnistus, et jääksid häbisse selles, mida teist paha räägivad need, kes teotavad teie head eluviisi Kristuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 17, 'Sest parem on, kui nõnda on Jumala tahtmine, head tehes kannatada kui paha tehes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 18, 'On ju ka Kristus kord surnud pattude pärast, õige ülekohtuste eest, et Ta meid juhiks Jumala juurde, kui Ta küll surmati liha poolest, kuid elavaks tehti vaimu poolest,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 19, 'kelles Ta ka läks ära ja kuulutas vangis olevaile vaimudele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 20, 'kes muiste ei kuulanud sõna, kui Jumala pikk meel ootas Noa päevil, kui ehitati laeva, millel mõned üksikud, see on kaheksa hinge, päästeti läbi vee.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 21, 'Sellele võrdluskujule vastavalt päästab teidki nüüd vesi ristimisena, mitte kui liha rüveduse kõrvaldamine, vaid kui hea südametunnistuse nõudmine Jumala ees Jeesuse Kristuse ülestõusmise kaudu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 22, 'Kes on läinud taevasse ja on Jumala paremal käel ja Kellele on alistatud Inglid ja võimud ja väed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 1, 'Et nüüd Kristus on kannatanud lihas, siis relvastuge teiegi sama meelega; sest kes lihas on kannatanud, see on lakanud pattu tegemast,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 2, 'nõnda et ta aega, mis tal veel on elada lihas, enam ei ela inimeste himude järgi, vaid Jumala tahtmise järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 3, 'Sest sellest on küllalt, et teil möödunud aeg kulus ära paganate tahte täitmiseks, kui te elasite lodevuses, himudes, viinajoomises, öistes olenguis, jootudes ja jõledais ebajumalateenistustes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 4, 'Nemad võõrastavad seda, et teie nendega ühes ei jookse samas lodeva elu voolus, ning pilkavad teid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 5, 'Nad peavad aru andma Sellele, Kes on valmis kohut mõistma elavate ja surnute üle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 6, 'Sest selleks on surnuile kuulutatud Evangeelium, et kohus nende üle oleks mõistetud nagu inimeste üle lihas ja nad elaksid nagu Jumal vaimus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 7, 'Aga kõigi asjade lõpp on lähedal! Olge siis mõistlikud ja kained palveiks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 8, 'Kõigepealt olgu teie armastus südamlik üksteise vastu, sest \"armastus katab pattude hulga\".');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 9, 'Olge külalislahked üksteise vastu ilma nurisemata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 10, 'Teenigu igaüks teist selle andega, mille ta on saanud, kui Jumala mõnesuguse armu head majapidajad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 11, 'Kui keegi räägib, siis ta rääkigu kui Jumala sõnu; kui keegi teenib, siis ta teenigu kui jõust, mida Jumal annab, et kõiges austataks Jumalat Jeesuse Kristuse läbi, Kelle on au ja vägi ajastute ajastuteni! Aamen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 12, 'Armsad, ärge pidage võõraks tulekuumust enestes, mis teile on saanud katsumiseks, otsekui sünniks teile midagi võõrast,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 13, 'vaid sedamööda nagu teil on osa Kristuse kannatamistest, olge rõõmsad, et võiksite rõõmustuda ning hõisata Tema au ilmumisel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 14, 'Kui teid solvatakse Kristuse nime pärast, siis te olete õndsad, sest au ja Jumala Vaim hingab teie peal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 15, 'Ärgu siis keegi teist kannatagu mõrtsukana või vargana või kurjategijana või salakaebajana.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 16, 'Aga kui keegi kannatab ristiinimesena, siis ta ärgu häbenegu, vaid andku Jumalale austust selle nimega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 17, 'Sest aeg on kohtul alata Jumala kojast; kui see aga algab kõigepealt meist, missugune ots ootab neid, kes ei ole sõnakuulelikud Jumala Evangeeliumile?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 18, 'Ja kui õige vaevalt pääseb, kuhu siis saab õel ja patune?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 19, 'Seepärast usaldagu need, kes Jumala tahtmise järgi kannatavad, oma hinged ustava Looja hooleks, tehes head.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 1, 'Kogudusevanemaid teie seas ma nüüd manitsen kui kaasvanem ja Kristuse kannatuste tunnistaja, kes ka olen tulevase au osaline, mis peab ilmuma:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 2, 'hoidke teile hoida antud Jumala karja mitte sundusest, vaid vabast tahtest Jumala meele järgi, mitte alatu kasu tõttu, vaid innust,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 3, 'mitte isandaina valitsedes kogudusi kui liisuosi, vaid olles karjale eeskujuks;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 4, 'siis te saate, kui Ülimkarjane ilmub, närtsimatu aupärja!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 5, 'Samuti teie, nooremad, alistuge vanemaile ja riietuge kõik alandlikkusega üksteise vastu; sest \"Jumal paneb suurelistele vastu, aga alandlikele Ta annab armu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 6, 'Siis alanduge Jumala vägeva käe alla, et Ta teid ülendaks omal ajal!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 7, 'Heitke kõik oma mure Tema peale, sest Tema peab hoolt teie eest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 8, 'Olge kained, valvake, sest teie vastane, kurat, käib ümber nagu möirgaja lõukoer otsides, keda neelata!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 9, 'Tema vastu seiske kindlad usus ning teadke, et teie vendadel maailmas tuleb neidsamu kannatusi täielikult kanda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 10, 'Aga kõige armu Jumal Ise, Kes meid on kutsunud Oma igavesse ausse Kristuses, võtku teid, kui te pisut olete kannatanud, valmistada, kinnitada, teha tugevaks ja rajada!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 11, 'Tema päralt on võimus ajastute ajastuteni! Aamen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 12, 'Silvaanuse, minu arvates ustava venna kaudu olen teile lühidalt kirjutanud, manitsedes ja tunnistades, et see on tõeline Jumala arm, milles te olete.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 13, 'Teid tervitab kaasvalitud kogudus Baabülonis ja Markus, mu poeg.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 14, 'Tervitage üksteist armastuse suudlusega! Rahu teile kõikidele, kes olete Kristuses!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 1, 'Siimon Peetrus, Jeesuse Kristuse sulane ja apostel, neile, kes on saanud niisama kalli usu kui meiegi meie Jumala ja Õnnistegija Jeesuse Kristuse õiguses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 2, 'Armu ja rahu lisatagu teile rohkesti Jumala ja Jeesuse, meie Issanda tunnetuses!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 3, 'Nõnda nagu Tema jumalik vägi meile on annetanud kõik, mida vajatakse eluks ja jumalakartuseks, Tema tunnetuse kaudu, Kes meid on kutsunud Oma au ja vooruslikkusega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 4, 'millega on meile kingitud kallid ja suurimad tõotused, et te nende läbi saaksite osa jumalikust loomust, kui te olete põgenenud ära hukkumisest, mis on maailmas himude tõttu;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 5, 'siis kandke sellesama pärast kõikepidi hoolt ja osutage oma usus vooruslikkust ja vooruslikkuses tunnetust,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 6, 'tunnetuses taltumust, taltumuses kannatlikkust, kannatlikkuses jumalakartust,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 7, 'jumalakartuses vennaarmastust ja vennaarmastuses armastust kõikide vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 8, 'Sest kui teil seda kõike on ja veel lisatakse, siis ei lase see teid olla viitsimatud ja viljatud meie Issanda Jeesuse Kristuse tunnetamises.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 9, 'Aga kellel see kõik puudub, on pime, lühinägelik, ja on unustanud puhastuse endistest pattudest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 10, 'Sellepärast, vennad, püüdke seda enam teha kindlaks oma kutsumine ja valik; sest kui te seda teete, siis te ei komista iialgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 11, 'Sest nõnda avaneb teile rohkel mõõdul sissepääs meie Issanda ja Õnnistegija Jeesuse Kristuse igavesse Kuningriiki.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 12, 'Sellepärast ma tahan alati teile seda meelde tuletada, ehk te küll seda teate ja olete kindlasti rajatud tõele, mis teil on käes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 13, 'Ma pean õigeks, niikaua kui ma selles ihulikus telgis olen, teid meeldetuletuse kaudu virgutada,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 14, 'sest et ma tean, et ma varsti maha panen oma telgi, nagu ka meie Issand Jeesus Kristus mulle on avaldanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 15, 'Ent ma tahan ka hoolt kanda, et teil pärast minu lahkumist alati oleks võimalik seda meelde tuletada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 16, 'Sest me ei ole Issanda Jeesuse Kristuse väge ja tulemist teile teatavaks tehes mitte järginud kavalasti sepitsetud tühje jutte, vaid me oleme ise oma silmaga näinud Tema suurt aulisust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 17, 'Sest Tema sai Jumalalt Isalt au ja austust kõige kõrgemalt ault, kui Temale kostis niisugune hääl: \"See on Minu armas Poeg, Kellest Mul on hea meel!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 18, 'Ja selle hääle me kuulsime taevast tulevat, olles ühes Temaga pühal mäel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 19, 'Ja meil on veel kindlam prohvetlik sõna, ja te teete hästi, et te seda tähele panete kui küünalt, mis paistab pimedas paigas, kuni päev jõuab kätte ning koidutäht tõuseb teie südameis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 20, 'Ja see olgu teile kõigepealt teada, et ükski prohvetikuulutus Kirjas ei ole omavolilise seletuse jaoks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 21, 'Sest inimese tahtel ei ole iialgi ühtki prohveti kuulutust esile toodud, vaid Pühast Vaimust kantuina on inimesed rääkinud, saades seda Jumala käest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 1, 'Aga valeprohveteidki tõusis rahva seas, nõnda nagu teiegi seas saab olema lahkõpetajaid, kes salamahti toovad sisse hukutavaid valeõpetusi, salgavad ära Peremehegi, Kes nad on ostnud, ja tõmbavad eneste peale äkilise hukatuse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 2, 'Ja paljud järgivad nende lodevat elu, kelle pärast pilgatakse tõe teed;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 3, 'ja ahnuses kasutavad nad teid petlike sõnadega. Nende kohus on juba ammu valvas ja nende hukatus ei tuku.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 4, 'Sest kui Jumal ei säästnud ingleidki, kes pattu tegid, vaid tõukas nad põrgupimeduse soppidesse säilitatavaiks kohtu jaoks,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 5, 'ja kui Ta ei säästnud muistset maailma, vaid varjas ainult õigusekuulutajat Noad, neid kaheksakesi, kui ta laskis tulla veeuputuse jumalatute maailma peale;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 6, 'ja mõistis ka hukka Soodoma ja Gomorra linnad, põletades need tuhaks ja pannes hirmu täheks neile, kes tulevikus on jumalatud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 7, 'ja päästis Loti, kes oli õige ja keda üleannetud inimesed piinasid oma kiimalise käitumisega -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 8, 'sest elades nende keskel see õige vaevas päevast päeva oma vaga hinge, nähes ja kuuldes ülekohtusi tegusid -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 9, 'nii Issand teab päästa jumalakartlikke kiusatusest, aga ülekohtusi säilitada kohtupäevaks, et neid nuhelda,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 10, 'iseäranis aga neid, kes liha järgi elavad roppudes himudes ja põlgavad Issanda valitsust. Need on jultunud, iseennast täis, ei karda pilgata aukandjaid vaime,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 11, 'kuna Inglidki, kuigi nad küll võimuselt ja väelt on suuremad, ühtki pilkavat otsust ei lausu nende kohta Issanda ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 12, 'Ent need on nagu mõistmatud loomad, kes on oma loomu poolest sündinud püütavaiks ja hukutavaiks; nad pilkavad seda, mida nad ei tea, ja hukkuvad ka oma hukus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 13, 'saades kätte ülekohtu palga; nad peavad lõbuks igapäevast laiutamist; nad on ebapuhtuse ja häbi laigud, kes oma pettuste varal lõbutsevad, kui nad pidutsevad ühes teiega;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 14, 'nende silmad õhkuvad abielurikkumisest ja nende iha ei lakka; nemad ahvatlevad kinnitamatuid hingi, neil on ahnusega vilunud süda, nad on needuse lapsed!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 15, 'Nad on hüljanud otsese tee, nad on ära eksinud, käies Bileami, Beori poja teed, kellele ülekohtu palk oli armas,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 16, 'aga kes oma üleastumise pärast sai karistada: keeletu koormakandja loom rääkis inimese häälega ja takistas prohveti jõledust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 17, 'Need on ilma veeta lätted, marutuulest aetavad udupilved, kellele on varutud pilkane pimedus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 18, 'Sest rääkides tühje ülerinna sõnu, ahvatlevad nad lodevate lihahimudega neid, kes vaevalt olid pääsenud pakku eksiteel käijate seast;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 19, 'nad tõotavad neile vabadust, olles ise kaduvuse orjad, sest kelle võidetud keegi on, selle ori ta on.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 20, 'Sest kui nad Issanda ja Õnnistegija Jeesuse Kristuse tunnetuse läbi pääsesid pakku maailma ebapuhtusest, kuid jälle segunevad sellega ja jäävad alla, siis on nende viimne lugu saanud pahemaks kui esimene.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 21, 'Sest neil oleks parem, et õiguse tee oleks jäänud neile tundmatuks, kui seda tundes ära pöörduda käsust, mis neile on antud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 22, 'Neile on tulnud kätte, mis tõeline vanasõna ütleb: \"Koer pöördub tagasi oma okse juurde\" ja \"pestud emis läheb porisse püherdama!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 1, 'See on, armsad, juba teine kiri, mille ma teile kirjutan, ja mõlematega ma hoian oma meeldetuletamisega ärkvel teie puhast meelt,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 2, 'et te meenutaksite neid sõnu, mis pühad prohvetid enne on rääkinud, ja Issanda ning Õnnistegija käsku, mille on andnud teie Apostlid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 3, 'Seda teadke kõigepealt, et viimseil päevil tuleb pilkesõnadega pilkajaid, kes elavad iseeneste himude järgi');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 4, 'ning ütlevad: \"Kus on Tema tulemise tõotus? Sest sellest ajast, kui isad läksid hingama, jääb kõik nõnda, nagu oli loomise algusest!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 5, 'Sest neil, kes seda tahavad, on teadmata, et taevad olid vanasti olemas ja maa koosnes veest ja vee läbi Jumala sõna väe tõttu');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 6, 'ja et nende läbi tookordne maailm hukkus veeuputuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 7, 'Ent praegused taevad ja maa säilitatakse sama Sõna läbi ning hoitakse alal tule jaoks jumalatute inimeste kohtu ja hukatuse päevaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 8, 'Aga see üks asi ärgu olgu teile, armsad, teadmata, et üks päev on Issanda juures nagu tuhat aastat ja tuhat aastat nagu üks päev!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 9, 'Issand ei viivita tõotust täitmast, nõnda nagu mõned peavad seda viivituseks, vaid Tema on pika meelega teie vastu; sest Ta ei taha, et keegi hukkuks, vaid et kõik tuleksid meeleparandusele!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 10, 'Aga Issanda päev tuleb otsekui varas öösel, mil taevad raginal hukkuvad ja maailma algained tules laostuvad ja ilmamaad ja kõiki seal tehtud töid ei leidu enam.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 11, 'Kui see nüüd kõik nõnda laostub, missugused siis teie peate olema pühas elus ja jumalakartuses,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 12, 'oodates ja igatsedes Jumala päeva tulekut, mille pärast taevad süüdatult laostuvad ja maailma algained kuumuses ära sulavad?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 13, 'Aga meie ootame tema tõotuse järgi uusi taevaid ja uut maad, kus õigus elab!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 14, 'Sellepärast, armsad, seda oodates püüdke, et Ta teid leiaks veatuina ja laitmatuina rahus;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 15, 'ja meie Issanda pikka meelt arvake õndsuseks, nõnda nagu ka meie armas vend Paulus teile on kirjutanud temale antud tarkust mööda,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 16, 'nagu ka kõigis kirjades, milles ta räägib neist asjust, kus on raske aru saada mõnest asjast, mida õppimatud ja kinnitamatud inimesed nagu muidki kirju väänavad iseeneste hukatuseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 17, 'Et teie nüüd, armsad, seda ette ära teate, siis hoiduge sellest, et üleannetute eksitused teid ei kisuks ühtlasi kaasa eksitusse ning te ei langeks ära oma kindlalt aluselt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 18, 'Aga kasvage meie Issanda ja Õnnistegija Jeesuse Kristuse armus ja tunnetuses. Temale olgu austus nii nüüd kui igaviku päevil!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 1, 'Mis algusest oli, mis me oleme kuulnud, mis me oma silmaga oleme näinud, mida me oleme vaadelnud ja mida meie käed on katsunud, seda me räägime elu Sõnast');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 2, '- ja elu ilmus, ja me oleme näinud ja tunnistame ja kuulutame teile seda igavest elu, mis oli Isa juures ja ilmus meile -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 3, 'mida me oleme näinud ja kuulnud, seda me kuulutame teile, et teilgi oleks osadus meiega. Ja meie osadus on Isaga ja Tema Pojaga, Jeesuse Kristusega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 4, 'Ja seda me kirjutame teile, et meie rõõm oleks täielik.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 5, 'Ja see on kuulutus, mille me oleme kuulnud Temalt ja kuulutame teile, et Jumal on valgus ja Temas ei ole mingit pimedust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 6, 'Kui me ütleme, et meil on osadus Temaga, aga käime pimeduses, siis me valetame ega tee tõtt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 7, 'Aga kui me käime valguses, nõnda nagu Tema on valguses, siis on meil osadus üksteisega, ja Jeesuse Kristuse, Tema Poja Veri puhastab meid kõigest patust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 8, 'Kui me ütleme, et meil ei ole pattu, siis me petame iseendid ja tõde ei ole meie sees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 9, 'Kui me oma patud tunnistame, on Tema ustav ja õige, nii et Ta meile annab patud andeks ja puhastab meid kõigest ülekohtust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 10, 'Kui me ütleme, et me pole pattu teinud, siis me teeme Tema valelikuks ja Tema sõna ei ole meie sees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 1, 'Minu lapsukesed, seda ma kirjutan teile, et te ei teeks pattu. Ja kui keegi pattu teeb, siis on meil eestkostja Isa juures, Jeesus Kristus, Kes on õige.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 2, 'Ja Tema on lepitus meie pattude eest; aga mitte ükspäinis meie, vaid ka kogu maailma pattude eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 3, 'Ja sellest me tunneme, et me oleme Tema ära tundnud, et me peame Tema käske.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 4, 'Kes ütleb: \"Mina tunnen Teda\", ega pea Tema käske, see on valelik ja tõde ei ole tema sees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 5, 'Aga kes peab Tema sõna, selles on tõesti Jumala armastus saanud täiuslikuks. Sellest me tunneme, et me oleme Tema sees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 6, 'Kes ütleb enese Tema sisse jäävat, see on ka kohustatud elama niisugust elu nagu Tema elas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 7, 'Armsad, ma ei kirjuta teile uut käsku, vaid vana käsku, mis teil on olnud algusest; see vana käsk on sõna, mida te olete kuulnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 8, 'Taas ma kirjutan teile uue käsu, mis on tõsi Tema sees ja teie sees, sest pimedus möödub ja tõeline valgus juba paistab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 9, 'Kes ütleb enese valguses olevat, aga vihkab oma venda, see on pimeduses siitsaadik.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 10, 'Kes oma venda armastab, see püsib valguses ja temas ei ole pahandust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 11, 'Aga kes oma venda vihkab, see on pimeduses ja käib pimeduses ega tea, kuhu ta läheb, sest pimedus on teinud ta silmad pimedaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 12, 'Ma kirjutan teile, lapsukesed, sest teie patud on andeks antud Tema nime pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 13, 'Ma kirjutan teile, isad, sest te olete ära tundnud Tema, Kes on algusest. Ma kirjutan teile, noored mehed, sest te olete ära võitnud tigeda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 14, 'Ma olen kirjutanud teile, lapsukesed, sest te olete ära tundnud Isa. Ma olen kirjutanud teile, isad, sest te olete ära tundnud Tema, Kes on algusest. Ma olen kirjutanud teile, noored mehed, sest te olete tugevad ja Jumala sõna püsib teie sees ja et te olete ära võitnud tigeda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 15, 'Ärge armastage maailma ega seda, mis on maailmas. Kui keegi maailma armastab, siis ei ole Isa armastust tema sees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 16, 'Sest kõik, mis on maailmas, lihahimu, silmahimu ja elukõrkus, ei ole Isast, vaid on maailmast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 17, 'Ja maailm kaob ja tema himu; aga kes teeb Jumala tahtmist, püsib igavesti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 18, 'Lapsukesed, nüüd on viimne aeg! Ja nagu te olete kuulnud, et kristusevastane tuleb, nõnda ongi nüüd ilmunud palju kristusevastaseid; siit me tunneme, et käes on viimne aeg.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 19, 'Meist nad on lähtunud, kuid nad ei olnud meist; sest kui nad oleksid olnud meist, siis nad oleksid jäänud meie juurde. Ent nad läksid ära, et saaks ilmsiks, et kõik ei ole meist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 20, 'Teil on võidmine Pühalt ja teil kõigil on teadmine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 21, 'Mina ei ole teile kirjutanud sellepärast et te ei tea tõde, vaid et te seda teate ja et ühtki valet ei tule tõe seest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 22, 'Kes on valelik kui mitte see, kes salgab, et Jeesus on Kristus? See on kristusevastane, kes salgab Isa ja Poega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 23, 'Igaüks, kes salgab Poja, sel ei ole ka Isa. Kes tunnistab Poega, sellel on ka Isa.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 24, 'Mida te olete algusest saadik kuulnud, see püsigu teis. Kui teis püsib see, mida te algusest saadik olete kuulnud, siis teiegi püsite Pojas ja Isas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 25, 'Ja see on tõotus, mille Tema meile on tõotanud: igavene elu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 26, 'Seda ma olen teile kirjutanud neist, kes teid eksitavad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 27, 'Ja teie - see võidmine, mille te olete saanud Temalt, jääb ju teisse ja teil ei ole vajadust, et keegi teid õpetaks; vaid nagu Tema võidmine teid kõiges õpetab, nõnda on see tõsi ega ole vale; ja nõnda nagu see teid on õpetanud, nõnda püsige Temas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 28, 'Ja nüüd, lapsukesed, jääge Temasse, et kui Ta ilmub, meil oleks julgus ja mitte häbi Tema ees, kui ta tuleb.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 29, 'Kui te teate, et Ta on õige, siis te mõistate, et igaüks, kes teeb õigust, on Temast sündinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 1, 'Vaadake, missuguse armastuse Isa on meile andnud, et meid hüütakse Jumala lasteks! Ja seda me olemegi. Sellepärast maailm ei tunne meid, sest ta ei tunne Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 2, 'Armsad, me oleme nüüd Jumala lapsed ega ole veel saanud avalikuks, mis me tulevikus oleme; aga me teame, et me Tema ilmudes oleme Tema sarnased, sest me näeme siis Teda nagu Tema on.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 3, 'Ja igaüks, kellel on niisugune lootus Tema peale, puhastab ennast, nõnda nagu Temagi on puhas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 4, 'Igaüks, kes pattu teeb, teeb ka seda, mis on käsu vastu; ja patt on see, mis on käsu vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 5, 'Ja te teate, et Tema on ilmunud patte ära võtma; ja Temas ei ole pattu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 6, 'Ükski, kes jääb Tema sisse, ei tee pattu; ükski, kes pattu teeb, ei ole teda näinud ega Teda tundnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 7, 'Lapsukesed, ärgu ükski teid eksitagu! Kes teeb õigust, see on õige, nõnda nagu Temagi on õige.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 8, 'Kes pattu teeb, see on kuradist; sest kurat teeb pattu algusest saadik. Selleks on Jumala Poeg ilmunud, et Ta tühistaks kuradi teod.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 9, 'Ükski, kes on Jumalast sündinud, ei tee pattu, sest tema seeme püsib Temas ja ta ei või pattu teha, sellepärast et ta on Jumalast sündinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 10, 'Sellest ilmneb, kummad on Jumala lapsed ja kummad kuradi lapsed. Ükski, kes ei tee õigust, ei ole Jumalast, ega see, kes ei armasta oma venda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 11, 'Sest see on sõnum, mida te algusest saadik olete kuulnud, et me peame üksteist armastama;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 12, 'mitte nõnda, nagu Kain, kes oli tigedast ja tappis oma venna. Ja mispärast ta tappis tema? Sellepärast et tema teod olid kurjad ja tema venna teod õiged.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 13, 'Ärge pange imeks, vennad, kui maailm teid vihkab!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 14, 'Me teame, et oleme tulnud surmast elusse, sest me armastame vendi; kes venda ei armasta, see jääb surmasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 15, 'Igaüks, kes vihkab oma venda, on inimesetapja; ja te teate, et ühelgi inimesetapjal ei ole igavest elu, mis temasse jääks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 16, 'Sellest me oleme ära tundnud Jumala armastuse, et Tema Oma elu on jätnud meie eest; ja meiegi peame jätma elu vendade eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 17, 'Kui nüüd kellelgi on selle maailma vara ja ta näeb oma venna puuduses olevat ja suleb oma südame tema eest, kuidas saab Jumala armastus jääda temasse?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 18, 'Lapsukesed, ärgem armastagem sõnaga ega keelega, vaid teoga ja tõega!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 19, 'Sellest me tunneme, et oleme tõe seest ja võime vaigistada tema ees oma südant,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 20, 'et kui meie süda meid hukka mõistab, Jumal on suurem kui meie süda ja teab kõik.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 21, 'Armsad, kui meie süda meid hukka ei mõista, on meil julgus Jumala ees,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 22, 'ja mida me iganes palume, seda me saame Temalt, sest me peame Tema käske ja teeme, mis on Temale meelepärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 23, 'Ja see on Tema käsk, et me peame uskuma Tema Poja Jeesuse Kristuse nimesse ja armastama üksteist, nagu Tema meile on käsu andnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 24, 'Ja kes peab Tema käske, see jääb Temasse ja Tema sellesse. Ja sellest me tunneme, et Tema jääb meisse, sellest Vaimust, Kelle Ta meile on andnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 1, 'Armsad, ärge uskuge igat vaimu, vaid katsuge vaimud läbi, kas nad on Jumalast; sest palju valeprohveteid on välja läinud maailma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 2, 'Sellest te tunnetate Jumala Vaimu: iga vaim, kes tunnistab Jeesust Kristust lihas tulnud olevat, see on Jumalast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 3, 'Ja ükski vaim, kes ei tunnista seda Jeesust, ei ole Jumalast; ja see on kristusevastase vaim, kelle te olete kuulnud tulemas olevat ja nüüd juba on maailmas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 4, 'Teie, lapsukesed, olete Jumalast ja olete nad ära võitnud; sest suurem on See, Kes on teie sees, kui see, kes on maailmas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 5, 'Nemad on maailmast; sellepärast nad räägivad maailmast ja maailm kuuleb neid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 6, 'Meie oleme Jumalast; kes tunneb Jumalat, võtab meid kuulda; kes ei ole Jumalast, ei võta meid kuulda. Sellest me tunneme tõe Vaimu ja eksituse vaimu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 7, 'Armsad, armastagem üksteist, sest armastus on Jumalast, ja igaüks, kes armastab, on Jumalast sündinud ja tunneb Jumalat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 8, 'Kes ei armasta, ei ole Jumalat ära tundnud; sest Jumal on armastus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 9, 'Selles on Jumala armastus meie vastu avalikuks saanud, et Jumal Oma ainusündinud Poja on läkitanud maailma, et me Tema läbi elaksime.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 10, 'Selles on armastus - ei mitte selles, et meie oleme armastanud Jumalat, vaid selles, et Tema meid on armastanud ja on läkitanud Oma Poja lepituseks meie pattude eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 11, 'Armsad, kui Jumal meid nõnda on armastanud, siis peame ka meie üksteist armastama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 12, 'Jumalat ei ole iialgi ükski näinud. Kui me üksteist armastame, siis jääb Jumal meisse ja Tema armastus on saanud täiuslikuks meie sees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 13, 'Sellest, et Ta meile on andnud osa Oma Vaimust, me tunneme, et me jääme Temasse ja Tema meisse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 14, 'Ja me oleme näinud ja tunnistame, et Isa on läkitanud Poja maailma Õnnistegijaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 15, 'Kes tunnistab, et Jeesus on Jumala Poeg, sellesse jääb Jumal ning tema Jumalasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 16, 'Ja me oleme tundma õppinud ja uskunud armastust, mis Jumalal on meie vastu. Jumal on armastus, ja kes jääb armastusse, see jääb Jumalasse ning Jumal jääb temasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 17, 'Selles on armastus saanud täiuslikuks meie sees, et meil oleks julgust kohtupäeval; sest otsekui Tema on, nõnda oleme ka meie selles maailmas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 18, 'Kartust ei ole armastuses, vaid täiuslik armastus ajab kartuse välja, sest kartuses on nuhtlust; aga kes kardab, ei ole saanud täiuslikuks armastuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 19, 'Me armastame, sest Tema on meid enne armastanud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 20, 'Kui keegi ütleb: \"Mina armastan Jumalat\", ja vihkab oma venda, siis ta on valelik. Sest kes ei armasta oma venda, keda ta on näinud, ei või armastada Jumalat, Keda ta ei ole näinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 21, 'Ja meil on Temalt see käsk, et kes armastab Jumalat, see armastab ka oma venda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 1, 'Igaüks, kes usub, et Jeesus on Kristus, on Jumalast sündinud; ja igaüks, kes armastab Teda, Kes Tema on sünnitanud, see armastab ka Temast sündinut.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 2, 'Sellest me tunneme, et me armastame Jumala lapsi, kui armastame Jumalat ja täidame Tema käske.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 3, 'Sest see on Jumala armastus, et me peame Tema käske, ja Tema käsud ei ole rasked!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 4, 'Sest kõik, mis Jumalast on sündinud, võidab maailma ära; ja see ongi see võit, mis on ära võitnud maailma: meie usk!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 5, 'Kes on, kes võidab maailma, kui aga see, kes usub, et Jeesus on Jumala Poeg?');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 6, 'Tema on See, Kes tuleb vee ja vere kaudu, Jeesus Kristus, mitte veega üksi, vaid vee ja verega. Ja Vaim on, Kes tunnistab, et Vaim on tõde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 7, 'Sest kolm on, Kes tunnistavad:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 8, 'Vaim ja vesi ja veri, ja need kolm liituvad ühte.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 9, 'Kui me võtame vastu inimeste tunnistuse, siis on Jumala tunnistus suurem, sest see on Jumala tunnistus, mis Ta on tunnistanud Oma Pojast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 10, 'Kes usub Jumala Pojasse, sellel on tunnistus enese sees. Kes ei usu Jumalat, on teinud tema valelikuks, sest ta pole uskunud tunnistust, mis Jumal on tunnistanud Oma Pojast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 11, 'Ja see on see tunnistus, et Jumal on meile andnud igavese elu ja see elu on Tema Pojas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 12, 'Kellel on Poeg, sellel on elu; kellel Jumala Poega ei ole, sellel ei ole elu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 13, 'Seda ma olen teile kirjutanud, et te teaksite enestel olevat igavese elu, teil, kes usute Jumala Poja nimesse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 14, 'Ja see on see julge avameelsus, mis meil on Tema ees, et kui me midagi palume Tema tahtmist mööda, Ta kuuleb meid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 15, 'Ja kui me teame, et Ta meid kuuleb kõiges, mida me palume, siis me ka teame, et meil on käes need palved, mis me Temalt oleme palunud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 16, 'Kui keegi näeb oma venda pattu tegevat, pattu, mis ei ole surmaks, siis ta tehku palvet, ja Jumal annab temale elu, neile nimelt, kes ei tee pattu surmaks. On patt, mis on surmaks; selle kohta ma ei ütle, et tuleks paluda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 17, 'Kõik ülekohus on patt; ja on pattu, mis ei ole surmaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 18, 'Me teame, et ükski Jumalast sündinu ei tee pattu, vaid Jumalast Sündinu hoiab teda, ja tige ei puutu temasse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 19, 'Me teame, et oleme Jumalast ja kõik maailm on tigeda võimuses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 20, 'Aga me teame, et Jumala Poeg on tulnud ja andnud meile mõistuse, et me tunnetaksime Tõelise ja oleksime Tõelises, Tema Pojas Jeesuses Kristuses. Seesama on tõeline Jumal ja igavene elu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 21, 'Lapsukesed, hoiduge ebajumalaist!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 1, 'Vanem äravalitud emandale ja tema lastele, keda ma armastan tões - ja mitte üksi mina, vaid ka kõik, kes on tunnetanud tõe -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 2, 'tõe pärast, mis jääb meisse ja on meiega igavesti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 3, 'Arm, halastus ja rahu Jumalalt Isalt ja Issandalt Jeesuselt Kristuselt, Isa Pojalt, olgu meie kõikidega tões ja armastuses!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 4, 'Mind rõõmustas väga, et leidsin sinu laste seast neid, kes käivad tões vastavalt käsule, mille oleme saanud Isalt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 5, 'Ja nüüd ma palun sind, emand, mitte kui uut käsku sulle kirjutades, vaid sama, mis meil oli algusest alates, et me peame üksteist armastama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 6, 'Ja see on armastus, et me käime Tema käskude järgi. See on käsk, et teie, nagu te algusest olete kuulnud, käiksite selles.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 7, 'Sest palju eksitajaid on tulnud maailma, kes ei tunnista Jeesust Kristust, kes lihas pidi tulema. See on eksitaja ja kristusevastane.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 8, 'Vaadake ette, et te ei mineta seda, mis me oleme vaevaga saavutanud, vaid et te saaksite kätte täie palga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 9, 'Igaüks, kes astub Kristuse õpetusest üle ega jää selle sisse, sellel ei ole Jumalat. Kes püsib selles õpetuses, sellel on niihästi Isa kui Poeg.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 10, 'Kui keegi tuleb teie juurde ega too enesega sama õpetust, siis ärge võtke seda oma majasse ja ärge öelge temale: \"Tere tulemast!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 11, 'Sest kes temale ütleb: \"Tere tulemast!\" saab tema kurjade tegude osaliseks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 12, 'Mul oleks teile palju kirjutada, aga ma ei taha seda teha paberil ja tindiga, vaid ma loodan tulla teie juurde ja teiega rääkida suu suud vastu, et meie rõõm oleks täielik.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 13, 'Sind tervitavad sinu äravalitud õe lapsed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 1, 'Vanem armsale Gaajusele, keda ma armastan tões.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 2, 'Armas, soovin sulle, et kõigis asjus sinu käsi hästi käiks ja sa oleksid terve, nõnda nagu su hingegi lugu on hea.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 3, 'Mind ju rõõmustas väga, kui vennad tulid ja tunnistust andsid sinu tõesusest, nõnda nagu sa käidki tões.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 4, 'Mul ei ole suuremat rõõmu kui see, et ma kuulen oma lapsi tões käivat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 5, 'Armas, sina toimid ustavana kõiges, mida sa teed koguni võõrastele vendadele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 6, 'kes on koguduse ees andnud tunnistust sinu armastusest, kelle suhtes sa teed hästi, kui sa neid saadad edasi teekonnal väärikalt Jumala ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 7, 'Sest tema nime pärast on nad läinud välja ega võta midagi paganate käest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 8, 'Sellepärast me oleme kohustatud võtma niisuguseid vastu, et saaksime tõe kaastöölisteks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 9, 'Ma kirjutasin mõne sõna kogudusele; kuid Diotrefes, kes nende seas püüab olla ülem, ei võta meid vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 10, 'Sellepärast mina, kui tulen, tuletan temale meelde teod, mida ta teeb, kui ta meid laimab kurjade sõnadega ega ole rahul sellegagi, et ei võta ise vendi vastu, vaid takistab ka neid, kes tahavad seda teha, ja lükkab nad välja kogudusest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 11, 'Armas, ära võta eeskujuks kurja, vaid head. Kes teeb head, on Jumalast; kes teeb kurja, ei ole Jumalat näinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 12, 'Demeetriosel on kõigilt ja tõelt eneselt hea tunnistus, ja meiegi tunnistame sedasama, ja sina tead, et meie tunnistus on tõsi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 13, 'Mul oleks sulle palju kirjutada, kuid ma ei taha sinule kirjutada tindi ja sulega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 14, 'vaid loodan sind varsti näha, ja siis me räägime suu suud vastu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 1, 'Juudas, Jeesuse Kristuse sulane, Jakoobuse vend, Jumalas Isas armastatud ja Jeesuses Kristuses hoitud kutsutuile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 2, 'Halastust ja rahu ja armastust lisatagu teile rohkesti!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 3, 'Armsad, et mul oli kange hool kirjutada teile meie ühisest õndsusest, siis oli mul tungiv vajadus manitseda teid võitlemisele pühadele kord antud usu poolest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 4, 'Sest teie sekka on poetunud mõningaid inimesi, kes juba ammu on pandud kirja tulevase hukatuse jaoks, jumalatuid, kes meie Jumala armu tarvitavad, et lodevalt elada, ning salgavad meie ainsa valitseja ja meie Issanda Jeesuse Kristuse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 5, 'Aga ma tahan meelde tuletada teile, kes kõike juba olete korra teada saanud, et Issand, kui Ta rahva oli päästnud Egiptusest, teisel korral hukkas ära need, kes ei uskunud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 6, 'ja et Tema Inglid, kes oma seisust ei hoidnud, vaid jätsid maha oma eluaseme, pani kinni igaveste ahelatega pilkase pimeduse alla suure päeva kohtuks;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 7, 'nagu Soodoma ja Gomorra ja nende ümberkaudsed linnad, kes samal kombel kui needki elasid porduelu ja ajasid taga teist liha, on hoiatavaks näiteks, kannatades igavese tule nuhtlust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 8, 'Nõndasamuti rüvetavad needki unenägijad oma liha, põlgavad Issanda valitsust, pilkavad aukandjaid vaime.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 9, 'Aga ei Peaingel Miikaelgi, kui ta riidles ning vaidles kuradiga Moosese keha pärast, ei julgenud lausuda pilkavat otsust, vaid ütles: \"Issand sõidelgu sind!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 10, 'Kuid need pilkavad seda, millest nad midagi ei tea; aga millest nad loomu poolest kui mõistmatud elajad aru saavad, selles nad saadavad endid hukka.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 11, 'Häda neile, et nad on läinud Kaini teed ja on andunud Bileami pettusele tasu eest, ja Korahi kombel vastuhakkamisega on läinud hukka!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 12, 'Nemad on teie armastussöömaaegadel mustuselaigud, kes kartmatult ühes pidutsevad iseendid nuumates; nad on tuulte aetavad veeta pilved, sügisesed viljata puud, kahekordselt surnud, juurtega välja kistud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 13, 'metsikud merelained, mis vahutavad välja oma häbi, eksijad tähed, kellele pilkane pimedus on varutud igaveseks ajaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 14, 'Ent nende kohta on ka Eenok, seitsmes Aadamast, ennustanud, öeldes: \"Vaata, Issand tuleb Oma pühade kümnetuhandetega');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 15, 'pidama kohut kõigile ja nuhtlema kõiki jumalatuid kõigi nende jumalakartmatute tegude eest, mis nad jumalatuses on teinud, ja kõigi kõvade sõnade eest, mis jumalatud patused Tema vastu on rääkinud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 16, 'Need on nurisejad, saatusega rahulolematud, kes käivad oma himude järgi ja kelle suu räägib ülbeid sõnu, meelitades inimesi kasu pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 17, 'Aga teie, armsad, tuletage endile meelde need sõnad, mis meie Issanda Jeesuse Kristuse apostlid on ette kuulutanud,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 18, 'kui nad teile ütlesid, et viimsel ajal tuleb pilkajaid, kes käivad oma õelate himude järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 19, 'Need on need, kes tekitavad lõhenemisi, maised, kellel ei ole Vaimu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 20, 'Aga teie, armsad, rajage endid oma pühimale usule ja palvetage Pühas Vaimus,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 21, 'ja hoidke endid Jumala armastuses ja oodake meie Issanda Jeesuse Kristuse halastust igaveseks eluks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 22, 'Ja halastage teiste peale, kes kahtlevad,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 23, 'päästke nad, kiskuge nad tulest välja; aga teiste peale halastage kartuses, võigastades kuubegi, mis liha läbi on pilastatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 24, 'Aga Temale, Kes teid võib hoida komistamast ja teid veatuina seada Oma auhiilguse palge ette hõiskamisega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 25, 'ainule Jumalale, meie Õnnistegijale, Jeesuse Kristuse, meie Issanda läbi olgu au, auhiilgus, võimus ja valitsus enne kõike maailmaajastut ja nüüd ja kõigi ajastuteni! Aamen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 1, 'Jeesuse Kristuse Ilmutus, mille Jumal Temale andis, et Ta näitaks Tema sulastele, mis varsti peab sündima; ja seda Ta näitas, läkitades Oma Ingli kaudu Oma sulasele Johannesele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 2, 'kes on tunnistanud Jumala sõna ja Jeesuse Kristuse tunnistust, kõike, mida ta on näinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 3, 'Õnnis on see, kes loeb, ja need, kes kuulevad selle prohvetliku kuulutuse sõnu ja peavad tallel, mis sellesse on kirja pandud; sest aeg on ligidal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 4, 'Johannes seitsmele Aasia kogudusele: armu teile ja rahu Sellelt, Kes on ja Kes oli ja Kes tuleb; ja neilt seitsmelt vaimult, kes on Tema aujärje ees;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 5, 'ja Jeesuselt Kristuselt, Kes on ustav tunnistaja, esmasündinu surnute seast ja maailma kuningate valitseja. Temale, Kes meid on armastanud ja meid on vabastanud meie pattudest Oma Verega,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 6, 'ja on meid teinud kuningriigiks, preestriks Jumalale ja Oma Isale, Temale olgu au ja vägi ajastute ajastuteni! Aamen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 7, 'Vaata, Tema tuleb pilvedega ja kõik silmad saavad Teda näha, ja need, kes Tema läbi pistsid, ja kõik maa suguharud tõstavad kaebehäält Tema pärast. Tõepoolest, aamen!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 8, '\"Mina olen A ja O\", ütleb Issand Jumal, Kes on ja Kes oli ja Kes tuleb, Kõigeväeline.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 9, 'Mina, Johannes, teie vend ja teie kaaslane viletsuses ja kuningriigis ja kannatlikkuses Jeesuses, olin saarel, mida kutsutakse Patmoseks, Jumala sõna ja Jeesuse Kristuse tunnistuse pärast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 10, 'Ma olin vaimus Issanda päeval ja kuulsin oma selja taga suurt, otsekui pasuna häält,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 11, 'mis ütles: \"Mida sa näed, kirjuta raamatusse ja läkita neile seitsmele kogudusele: Efesosse ja Smürnasse ja Pergamoni ja Tüatiirasse ja Sardesesse ja Filadelfiasse ja Laodikeasse!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 12, 'Ja ma pöördusin ümber vaatama seda häält, mis minuga rääkis. Ja kui ma olin pöördunud, nägin ma seitset kuld küünlajalga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 13, 'Ja keset neid seitset küünlajalga üht, kes oli Inimese Poja sarnane, riietatud pika rüüga ja vöötatud rinde alt kuldvööga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 14, 'Tema pea ja juuksed olid valged nagu valge vill, nagu lumi, ja Tema silmad nagu tuleleek;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 15, 'ja Tema jalad olid otsekui ahjus hõõguv, hiilgav vasemaak ja Tema hääl oli nagu suurte vete kohin.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 16, 'Ja Temal oli paremas käes seitse taevatähte, ja Tema suust läks välja terav kaheterane mõõk, ja Tema pale oli otsekui päike, kui see paistab oma väes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 17, 'Kui ma Teda nägin, siis ma langesin maha Tema jalgade ette nagu surnu. Ja Ta pani Oma parema käe mu peale ja ütles mulle: \"Ära karda, Mina olen Esimene ja Viimne');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 18, 'ja Elav; Ma olin surnud, ja vaata, Ma olen elav ajastute ajastuteni, ja Minu käes on surma ja surmavalla võtmed!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 19, 'Kirjuta siis, mida sa oled näinud ja mis nüüd on ja mis pärast seda sünnib!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 20, 'Nende seitsme taevatähe saladus, mida sa nägid Minu paremas käes, ja nende seitsme kuldküünlajala saladus on see: need seitse tähte on seitsme koguduse Inglid, ja need seitse küünlajalga on seitse kogudust!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 1, 'Efesose koguduse Inglile kirjuta: nõnda ütleb see, Kes neid seitset tähte peab Oma paremas käes, Kes kõnnib seitsme kuldküünlajala vahel:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 2, 'Ma tean su tegusid ja sinu tööd ja su kannatlikkust ja et sa ei või sallida kurje ja oled läbi katsunud need, kes endid ütlevad Apostlid olevat, aga ei ole, ning oled leidnud nad olevat valelikud;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 3, 'ja sinul on kannatlikkust ja sa oled kandnud raskusi Minu nime pärast ega ole raugenud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 4, 'Aga Mul on sinu vastu, et sa oled maha jätnud oma esimese armastuse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 5, 'Siis mõtle nüüd, kust sa oled langenud, ja paranda meelt ja tee esimesi tegusid; aga kui mitte, siis Ma tulen su juurde ja lükkan su küünlajala tema asemelt ära, kui sa meelt ei paranda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 6, 'Aga see sul on, et sa vihkad nikolaiitide tegusid, mida vihkan Minagi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 7, 'Kellel kõrv on, see kuulgu, mis Vaim kogudustele ütleb: kes võidab, sellele ma annan süüa elupuust, mis on Jumala paradiisis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 8, 'Ja Smürna koguduse Inglile kirjuta: nõnda ütleb Esimene ja Viimne, Kes oli surnud ja on saanud elavaks:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 9, 'Ma tean su viletsust ning vaesust - kuid sa oled siiski rikas - ja pilkamist nende poolt, kes ütlevad endid juudid olevat, aga ei ole, vaid on saatana kogudus!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 10, 'Ära karda seda, mida sul tuleb kannatada! Vaata, kurat tahab visata mõned teie seast vangitorni, et teid kiusataks; ja teil on viletsust kümme päeva. Ole ustav surmani, siis Ma annan sulle elukrooni!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 11, 'Kellel kõrv on, see kuulgu, mis Vaim kogudustele ütleb: kes võidab, sellele ei tee teine surm mingit kahju!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 12, 'Ja Pergamoni koguduse Inglile kirjuta: nõnda ütleb See, Kelle käes on terav kaheterane mõõk:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 13, 'Ma tean, kus sa elad: seal, kus on saatana aujärg! Ja sa pead kinni Mu nimest ega ole Minu usku salanud ka neil päevil, mil Antipas, Mu tunnistaja, mu ustav, tapeti teie juures, kus saatan elab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 14, 'Aga Mul on pisut sinu vastu, et sul on seal neid, kes peavad kinni Bileami õpetusest, kes Baalakit õpetas võrgutama Iisraeli sööma ebajumalate ohvreid ja hoorama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 15, 'Samuti on sul ka neid, kes samasuguselt kinni peavad nikolaiitide õpetusest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 16, 'Paranda siis meelt; aga kui mitte, siis Ma tulen su juurde nobedasti ja sõdin nende vastu oma suu mõõgaga!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 17, 'Kellel kõrv on, see kuulgu, mis Vaim kogudustele ütleb: kes võidab, sellele Ma annan süüa varjule pandud mannat ja annan temale valge kivikese ja kivikese peale kirjutatud uue nime, mida ükski muu ei tunne kui aga see, kes selle saab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 18, 'Ja Tüatiira koguduse Inglile kirjuta: nõnda ütleb Jumala Poeg, Kellel on silmad nagu tuleleek ja jalad hiilgavad otsekui vasemaak:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 19, 'Ma tean sinu tegusid ja armastust ja usku ja teenimist ja kannatlikkust, ja et sinu viimseid tegusid on rohkem kui esimesi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 20, 'Aga Mul on sinu vastu, et sa sallid naist Iisebeli, kes ennast nimetab prohvetiks ja õpetab ning ahvatleb Minu sulaseid hoorama ja sööma ebajumalate ohvreid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 21, 'Ma olen temale andnud aega meeleparanduseks, aga ta ei taha pöörduda oma porduelust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 22, 'Vaata, Ma viskan ta haigevoodisse ja need, kes temaga abielu rikuvad, suurde viletsusse, kui nad ei pöördu tema tegudest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 23, 'Ja tema lapsed Ma lõpetan surmaga, ja kõik kogudused tunnevad ära, et Mina olen See, Kes katsub läbi neerud ja südamed, ja Ma annan teile igaühele teie tegude järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 24, 'Aga teile Ma ütlen ja muile Tüatiiras olevaile, kellel ei ole seda õpetust ja kes ei ole, nagu nad ütlevad, ära tundnud saatana sügavusi: Ma ei pane teile peale muud koormat,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 25, 'pidage vaid kinni, mis teil on, kuni Ma tulen!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 26, 'Ja kes võidab ja otsani peab Minu tegusid, sellele Ma annan meelevalla paganate üle');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 27, 'ja ta peab neid karjatama raudkepiga, nõnda nagu saviastjad pihuks lüüakse, otsekui Minagi olen saanud meelevalla Oma Isalt;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 28, 'ja Mina annan temale koidutähe.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 29, 'Kellel kõrv on, see kuulgu, mis Vaim kogudustele ütleb!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 1, 'Ja Sardese koguduse Inglile kirjuta: nõnda ütleb See, Kelle on seitse Jumala vaimu ja need seitse tähte: Ma tean sinu tegusid, et sul on nimi, et sa elad, aga oled surnud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 2, 'Saa valvsaks ja kinnita muid, kes on suremas; sest Mina ei ole leidnud, et su teod oleksid täielikud Minu Jumala ees!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 3, 'Siis mõtle, kuidas sa sõna vastu võtsid ja kuulsid, ja pea seda ning paranda meelt! Kui sa nüüd ei valva, tulen Ma kui varas ja sina ei tea, mil tunnil Ma su peale tulen.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 4, 'Ometi on sul ka Sardeses mõned nimed, kes ei ole reostanud oma riideid, ja nad peavad Minuga käima valgeis riideis, sest nad on seda väärt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 5, 'Kes võidab, see riietatakse nõnda valgete riietega ja Mina ei kustuta tema nime eluraamatust, ja Ma tahan tunnistada tema nime Oma Isa ees ja kõigi Tema Inglite ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 6, 'Kellel kõrv on, see kuulgu, mis Vaim kogudustele ütleb!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 7, 'Ja Filadelfia koguduse Inglile kirjuta: nõnda ütleb Püha, Tõeline, Kelle käes on Taaveti võti ja Kes avab ja ükski ei sule; Kes suleb ja ükski ei ava:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 8, 'Ma tean sinu tegusid. Vaata, Ma olen seadnud su ette avatud ukse ja ükski ei suuda seda sulgeda; sest sul on pisut rammu, ja sa oled pidanud Mu sõna ega ole salanud Mu nime!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 9, 'Vaata, Ma annan saatana kogudusest mõned, kes ütlevad endid juudid olevat, aga ei ole, vaid valetavad! Vaata, Ma teen, et nad tulevad ja kummardavad sinu jalgade ette ja tunnevad ära, et Ma sind olen armastanud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 10, 'Et sa Mu kannatlikkuse sõna oled pidanud, siis Minagi tahan sind hoida kiusatustunni eest, mis on tulemas kogu maailma peale neid kiusama, kes maa peal elavad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 11, 'Ma tulen nobedasti; pea kinni, mis sul on, et ükski sinu krooni ei võtaks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 12, 'Kes võidab, selle Ma teen sambaks Oma Jumala templis, ja tema ei lähe sealt enam välja, ja Ma kirjutan tema peale Oma Jumala nime ja Oma Jumala linna nime, Uue Jeruusalemma, mis maha tuleb taevast Minu Jumala juurest, ja Oma uue nime.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 13, 'Kellel kõrv on, see kuulgu, mis Vaim kogudustele ütleb!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 14, 'Ja Laodikea koguduse Inglile kirjuta: nõnda ütleb Aamen, ustav ja tõeline tunnistaja, Jumala loodu algus:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 15, 'Ma tean sinu tegusid, et sa pole külm ega kuum. Oh, oleksid sa külm või kuum!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 16, 'Aga nüüd, et sa oled leige ja mitte külm ega kuum, siis tahan Ma sind välja sülitada Oma suust!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 17, 'Sa ju ütled: ma olen rikas ja mul on vara küllalt ega ole mul midagi vaja. Ja sa ei teagi, et sa oled vilets ja armetu ja vaene ja pime ja alasti!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 18, 'Ma annan sulle nõu osta Minult kulda, mis tules on puhastatud, et sa võiksid rikkaks saada, ja valged riided, et sa nendega riietuksid ja ei nähtaks sinu alastuse häbi, ja silmasalvi võida silmi, et sa näeksid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 19, 'Ma noomin ja karistan kõiki, keda Ma armastan. Ole siis väga hoolas ja paranda meelt!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 20, 'Vaata, Ma seisan ukse taga ja koputan: kui keegi Mu häält kuuleb ja ukse avab, selle juurde Ma lähen sisse ja söön õhtust ühes temaga ja tema Minuga!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 21, 'Kes võidab, sellele Ma annan istuda ühes Minuga Minu aujärjel, nõnda nagu Minagi olen võitnud ja istunud ühes Oma Isaga Tema aujärjele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 22, 'Kellel kõrv on, see kuulgu, mis Vaim kogudustele ütleb!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 1, 'Pärast seda ma nägin, ja vaata: uks oli avatud taevas ja endine hääl, mida olin kuulnud otsekui pasunat rääkivat minuga, ütles: \"Astu siia üles, ja Ma näitan sulle, mis pärast seda peab sündima!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 2, 'Ja sedamaid olin mina vaimus. Ja vaata, aujärg seisis taevas, ja Keegi istus aujärjel!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 3, 'Ja See, Kes istus, oli jumelt otsekui jaspise- ja sardisekivi, ja vikerkaar oli aujärje ümber, jumelt sarnane smaragdikivile.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 4, 'Ja aujärje ümber oli kakskümmend neli aujärge; ja aujärgedel ma nägin istuvat kahtkümmend nelja vanemat, riietatud valgeisse riietesse ja kuldpärjad peas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 5, 'Ja aujärjest väljus välke ja hääli ja piksemürinat, ja seitse tulist lampi põles aujärje ees; need on Jumala seitse vaimu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 6, 'Ja aujärje ees oli otsekui klaasmeri, mägikristalli sarnane; ja keset aujärge ja aujärje ümber oli neli olendit, täis silmi eest ja tagant.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 7, 'Ja esimene olend oli lõukoera sarnane, ja teine olend oli härja sarnane, ja kolmandal olendil oli nägu otsekui inimesel, ja neljas olend oli lendava kotka sarnane.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 8, 'Ja neil neljal olendil oli igaühel kuus tiiba, ümberringi ja seestpoolt nad olid täis silmi. Nad ei lakka ööd ja päevad ütlemast: \"Püha, püha, püha on Issand Jumal, Kõigeväeline, Kes oli ja Kes on ja Kes tuleb!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 9, 'Ja nii sageli kui olendid annavad austust, au ja tänu Sellele, Kes aujärjel istub ja elab ajastute ajastuteni,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 10, 'heidavad need kakskümmend neli vanemat maha selle ette, Kes aujärjel istub, ja nad kummardavad Teda, Kes elab ajastute ajastuteni, ja panevad oma pärjad maha aujärje ette ning ütlevad:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 11, 'Sina, meie Issand ja Jumal, oled väärt võtma austust ja au ja väge, sest sina oled loonud kõik asjad, ja Sinu tahte läbi on need olemas ja on loodud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 1, 'Ja ma nägin Selle paremas käes, Kes istus aujärjel, rullraamatut, täis kirjutatud seestpoolt ja väljastpoolt ja kinni pandud seitsme pitseriga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 2, 'Ja ma nägin vägevat Inglit; see kuulutas suure häälega: \"Kes on vääriline avama seda raamatut ja lahti võtma selle pitsereid?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 3, 'Ja ükski ei võinud ei taevas ega maa peal ega maa all avada raamatut ega vaadata sinna sisse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 4, 'Ja mina nutsin väga, et kedagi ei leitud väärt olevat avama raamatut ja vaatama sinna sisse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 5, 'Ja üks vanemaist ütles mulle: \"Ära nuta! Vaata, lõukoer Juuda suguharust, Taaveti juur, on võitnud nii, et Tema võib avada raamatu ja lahti võtta selle seitse pitserit!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 6, 'Ja ma nägin keset aujärge ja nelja olendit ja keset vanemaid seismas Talle, Kes oli otsekui tapetud; ja Tal oli seitse sarve ja seitse silma, mis on Jumala seitse vaimu, kes on läkitatud kogu ilmamaale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 7, 'Ja Ta tuli ja võttis raamatu selle paremast käest, Kes istus aujärjel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 8, 'Ja kui Ta oli võtnud raamatu, siis need olendid ja need kakskümmend neli vanemat heitsid maha Talle ette, ja igaühel oli käes kannel ja kuldkausid, täis suitsutusrohte; need on pühade palved.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 9, 'Ja nad laulsid uut laulu ning ütlesid: \"Sina oled väärt võtma raamatut ja lahti tegema selle pitsereid, sest Sina oled olnud tapetud ja oled Oma Verega Jumalale ostnud inimesi kõigist suguharudest ja keeltest ja rahvaist ja paganahõimudest');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 10, 'ja oled nad teinud kuningriigiks ja preestriks meie Jumalale, ja nad valitsevad maa peal.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 11, 'Ja ma nägin ja kuulsin paljude Inglite häält aujärje ja olendite ja vanemate ümbert; ja nende arv oli kümme tuhat korda kümme tuhat ja tuhat korda tuhat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 12, 'Ja need ütlesid suure häälega: \"Tall, Kes on tapetud, on väärt võtma väge ja rikkust ja tarkust ja rammu ja au ja austust ja kiitust!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 13, 'Ja kõike loodut, mis on taevas ja maa peal ja maa all ja meres, ja kõike, mis nendes on, kuulsin ma ütlevat: \"Sellele, Kes istub aujärjel, ja Tallele olgu kiitus ja au ja austus ja vägi ajastute ajastuteni!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 14, 'Ja need neli olendit ütlesid: \"Aamen!\" Ja vanemad heitsid maha ja kummardasid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 1, 'Ja ma nägin, kuidas Tall võttis lahti ühe seitsmest pitserist, ja ma kuulsin üht neljast olendist otsekui pikse häälega ütlevat: \"Tule!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 2, 'Ja ma vaatasin, ja näe: valge hobune; ja ta seljas istujal oli amb käes; ja talle anti pärg ja ta väljus võites ja võitu saavutama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 3, 'Ja kui Tall võttis lahti teise pitseri, kuulsin ma teist olendit ütlevat: \"Tule!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 4, 'Ja väljus teine hobune, tulipunane, ja ta seljas istujal lasti rahu maa pealt ära võtta, et inimesed üksteist tapaksid; ja temale anti suur mõõk.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 5, 'Ja kui Tall võttis lahti kolmanda pitseri, kuulsin ma kolmandat olendit ütlevat: \"Tule!\" Ja ma vaatasin, ja näe: must hobune, ja tema seljas istujal olid kaalud käes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 6, 'Ja ma kuulsin otsekui häält nelja olendi keskelt ütlevat: \"Mõõt nisu teenari eest ja kolm mõõtu otri teenari eest; ja õlile ega viinale ära tee kahju!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 7, 'Ja kui Ta võttis lahti neljanda pitseri, kuulsin ma neljanda olendi häält ütlevat: \"Tule!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 8, 'Ja ma vaatasin, ja näe, tuhkur hobune, ja kes tema seljas istus, selle nimi oli Surm, ja Surmavald käis ühes temaga; ja neile anti meelevald neljanda osa ilmamaa üle, tappa mõõga ja nälja ja surmaga ja maapealsete kiskjate elajate abil.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 9, 'Ja kui Ta võttis lahti viienda pitseri, siis ma nägin altari all nende hingi, kes olid tapetud Jumala sõna pärast ja tunnistuse pärast, mis neil oli.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 10, 'Ja nad kisendasid suure häälega ning ütlesid: \"Oh Püha ja Tõeline Valitseja, kui kaua Sa ei mõista kohut ega tasu kätte meie verd neile, kes elavad maa peal?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 11, 'Ja neile anti igaühele pikk valge rüü, ja neile öeldi, et nad oleksid rahul veel natuke aega, kuni saab täis nende kaassulaste ja nende vendade arv, kes tapetakse nõnda nagu nemadki.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 12, 'Ja ma nägin, kui Ta võttis lahti kuuenda pitseri, et siis sündis suur maavärisemine, ja päike läks mustaks nagu karvane kotiriie ja kuu läks kogunisti nagu vereks;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 13, 'ja taevatähed kukkusid maha, otsekui viigipuu ajab maha oma toored marjad, kui suur tuul teda raputab;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 14, 'ja taevas veeres ära nagu rullraamat, ja kõik mäed ja saared nihkusid oma paigust;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 15, 'ja ilmamaa kuningad ja suured isandad ja sõjapealikud ja rikkad ja võimumehed ja kõik orjad ja kõik vabad pugesid varjule koobastesse ja mägede kaljudesse,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 16, 'ja nad ütlesid mägedele ja kaljudele: \"Langege meie peale ja varjake meid Selle palge eest, Kes aujärjel istub, ja Talle viha eest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 17, 'Sest on tulnud Tema suur vihapäev, ja kes võib püsida?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 1, 'Pärast seda ma nägin nelja Inglit seisvat maa neljal nurgal ning kinni pidavat nelja maatuult, et ükski tuul ei puhuks maa peale ega mere peale ega ühegi puu peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 2, 'Ja ma nägin teist Inglit tõusvat päevatõusu poolt, ning ta käes oli elava Jumala pitsat, ja tema hüüdis suure häälega nendele neljale Inglile, kellele oli antud teha kahju maale ja merele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 3, 'ning ütles: \"Ärge tehke kahju maale ega merele ega puudele, enne kui me oleme pannud pitseri oma Jumala sulaste otsaesisele!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 4, 'Ja ma kuulsin nende arvu, kes olid pitseriga märgitud, sada nelikümmend neli tuhat pitseriga märgitut kõigist Iisraeli laste suguharudest:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 5, 'Juuda suguharust kaksteist tuhat pitseriga märgitut; Ruubeni suguharust kaksteist tuhat; Gaadi suguharust kaksteist tuhat;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 6, 'Aaseri suguharust kaksteist tuhat; Naftali suguharust kaksteist tuhat; Manasse suguharust kaksteist tuhat;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 7, 'Siimeoni suguharust kaksteist tuhat; Leevi suguharust kaksteist tuhat; Issaskari suguharust kaksteist tuhat;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 8, 'Sebuloni suguharust kaksteist tuhat; Joosepi suguharust kaksteist tuhat; Benjamini suguharust kaksteist tuhat pitseriga märgitut.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 9, 'Pärast seda ma nägin, ja vaata, suur hulk rahvast, keda ükski ei võinud ära lugeda kõigist rahvahõimudest ja suguharudest ja rahvaist ja keeltest; need seisid aujärje ees ja Talle ees, riietatud valgeisse rüüdesse ja palmioksad käes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 10, 'Ja nad hüüdsid suure häälega ja ütlesid: \"Õnnistus meie Jumalale, Kes aujärjel istub, ja Tallele!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 11, 'Ja kõik Inglid seisid aujärje ja vanemate ja nelja olendi ümber ja heitsid aujärje ette silmili maha ja kummardasid Jumalat');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 12, 'ning ütlesid: \"Aamen, kiitus ja austus ja tarkus ja tänu ja au ja vägi ja ramm meie Jumalale ajastute ajastuteni! Aamen.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 13, 'Ja üks vanemaist hakkas kõnelema ning ütles minule: \"Need seal valgeis rüüdes, kes nad on ja kust nad tulid?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 14, 'Ja ma ütlesin temale: \"Mu isand, sina tead!\" Ja ta ütles mulle: \"Need on, kes tulevad suurest viletsusest ja on oma rüüd pesnud ja oma rüüd valgeks teinud Talle Veres!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 15, 'Sellepärast on nad Jumala aujärje ees ja teenivad Teda ööd ja päevad Tema templis. Ja See, Kes aujärjel istub, laotab Oma telgi nende üle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 16, 'Neil ei ole siis enam nälga ega janu; ka ei lange nende peale päikest ega mingisugust palavat;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 17, 'sest Tall, Kes on keset aujärge, hoiab neid ja juhatab nad elava vee allikaile; ja Jumal pühib ära kõik pisarad nende silmist!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 1, 'Ja kui Tall võttis lahti seitsmenda pitseri, tekkis taevas vaikus ligi pooleks tunniks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 2, 'Ja ma nägin seitset Inglit, kes seisid Jumala ees, ja neile anti seitse pasunat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 3, 'Ja teine Ingel tuli ja astus altari äärde, kuldsuitsutusastja käes, ja talle anti palju suitsutusrohte, et ta neid lisaks kõigi pühade palvetele kuldaltaril, mis oli aujärje ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 4, 'Ja suitsutusrohtude suits pühade palvetega tõusis Ingli käest Jumala ette.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 5, 'Ja Ingel võttis suitsutusastja ja täitis selle tulega altarilt ja viskas ta maa peale. Siis sündis hääli ja mürinaid ja välke ning maavärisemine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 6, 'Ja need seitse Inglit, kelle käes oli seitse pasunat, valmistusid pasunat puhuma.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 7, 'Ja esimene puhus pasunat. Siis tuli rahet ja tuld verega segamini ja paisati maa peale. Ja kolmas osa maad põles ära ja kolmas osa puid põles ära ja kõik haljas rohi põles ära.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 8, 'Ja teine Ingel puhus pasunat. Siis heideti merre otsekui suur mägi, mis tules põles, ja kolmas osa merd sai vereks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 9, 'Ja kolmas osa mere loomi, kellel hing sees, suri ära ja kolmas osa laevu läks hukka.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 10, 'Ja kolmas Ingel puhus pasunat. Siis kukkus taevast maha suur täht, mis põles otsekui tõrvalont ja langes kolmanda osa jõgede peale ja veeallikate peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 11, 'Ja tähe nimi on Koirohi. Ja kolmas osa vetest muutus koirohuks, ja palju inimesi suri vete kätte, sest need olid mõruks läinud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 12, 'Ja neljas Ingel puhus pasunat. Siis löödi kolmas osa päikest ja kolmas osa kuud ja kolmas osa tähti, nii et kolmas osa neist pimeneks ja kolmas osa päeva ei paistaks, ja ööd niisamuti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 13, 'Ja ma nägin ja kuulsin kotka lendavat kesktaeva all ning suure häälega ütlevat: \"Häda, häda, häda neile, kes elavad maa peal, nende kolme Ingli järgneva pasunahääle pärast, kes veel peavad pasunat puhuma!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 1, 'Ja viies Ingel puhus pasunat. Siis ma nägin tähe taevast maa peale kukkunud olevat, ja temale anti sügavuse kaevu võti.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 2, 'Ja ta avas sügavuse kaevu, ja kaevust tõusis suits otsekui suure ahju suits. Ja päike ja õhk läksid pimedaks kaevu suitsust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 3, 'Ja suitsust väljusid rohutirtsud maa peale ja neile anti meelevald, nagu skorpionidel on meelevald maa peal.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 4, 'Ja neile öeldi, et nad ei tohi kurja teha ei maa rohule ega millelegi, mis haljas on, ega ühelegi puule, vaid ainult inimestele, kellel ei ole Jumala pitserit otsaesisel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 5, 'Ja neile anti käsk, et nad neid ei tapaks, vaid et nad neid piinaksid viis kuud; ja nende piinamine on otsekui skorpioni piinamine, kui ta inimest nõelab.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 6, 'Ja neil päevil inimesed otsivad surma ega leia seda mitte, ja püüavad surra, aga surm põgeneb nende eest!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 7, 'Ja rohutirtsud olid välimuselt sõjaks valmistatud hobuste sarnased ja neil olid peas pärjad otsekui kullast ja nende nägu oli nagu inimeste nägu;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 8, 'ja neil olid juuksed nagu naiste juuksed ja nende hambad olid otsekui lõukoerte hambad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 9, 'Ja neil olid soomusrüüd, otsekui raudrüüd, ja nende tiibade kahin oli otsekui vankrite mürin, kui palju hobuseid jookseb sõtta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 10, 'Ja neil olid sabad ja astlad nagu skorpionidel, ja nende sabades oli võim inimestele kahju teha viis kuud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 11, 'Ja neil oli kuningaks sügavuse Ingel; tema nimi on heebrea keeli Abadoon ja kreeka keeli Apollüon.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 12, 'Esimene häda on möödas; vaata, kaks häda tuleb veel pärast seda!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 13, 'Ja kuues Ingel puhus pasunat. Siis ma kuulsin üht häält Jumala ees oleva kuldaltari neljast nurgast;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 14, 'see ütles kuuendale Inglile, kelle käes oli pasun: \"Päästa lahti need neli Inglit, kes on seotud suure Eufrati jõe ääres!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 15, 'Siis päästeti lahti neli Inglit, kes on valmis tunniks ja päevaks ja kuuks ja aastaks, et tappa kolmas osa inimesi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 16, 'Ja ratsaväelaste arv on kakskümmend tuhat korda kümme tuhat; ma kuulsin nende arvu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 17, 'Ja nõnda ma nägin nägemuses hobuseid ja nende seljas istujaid, ja neil olid tulekarva ja purpursinised ja väävlikarva soomusrüüd; ja hobuste pead olid nagu lõukoerte pead, ja nende suust käis välja tuld ja suitsu ja väävlit.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 18, 'Neist kolmest nuhtlusest sai surma kolmas osa inimesi tule ja suitsu ja väävli läbi, mis nende hobuste suust välja käis.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 19, 'Sest hobuste võim on nende suus ja nende sabades; sest nende sabad on madude sarnased; neil on pead ja nendega nad teevad kahju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 20, 'Aga järelejäänud inimesed, keda neis nuhtlustes ei surmatud, ei pöördunud siiski mitte oma käte tegudest, et nad poleks kummardanud kurje vaime ning kuld- ja hõbe- ja vask- ja kivi- ja puujumalaid, kes ei või näha ega kuulda ega kõndida.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 21, 'Ja nad ei pöördunud oma tapmistest ega oma nõidustest, ei oma hoorusest ega oma vargustest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 1, 'Ja ma nägin teist vägevat Inglit taevast maha tulevat; see oli riietatud pilvega ja vikerkaar oli tema pea kohal, ja tema pale oli otsekui päike ja tema jalad nagu tulesambad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 2, 'Ja tema käes oli avatud raamatuke, ja ta pani oma parema jala mere ja vasaku maa peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 3, 'Ja ta kisendas suure häälega, otsekui lõukoer möirgab. Ja kui ta kisendas, siis hakkasid seitse pikset müristama oma häältega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 4, 'Ja kui need seitse pikset oma häältega olid rääkinud, tahtsin ma kirjutada. Siis ma kuulsin häält taevast ütlevat: \"Pane pitseriga kinni, mis seitse pikset on kõnelnud, ja ära kirjuta seda!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 5, 'Ja Ingel, keda ma nägin seisvat mere ja maa peal, tõstis oma käe taeva poole');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 6, 'ja vandus Selle juures, Kes elab ajastute ajastuteni, Kes on loonud taeva ja mis seal on ja maa ja mis seal on ja mere ja mis seal on, et aega ei saa enam olema,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 7, 'vaid seitsmenda Ingli hääle päevil, kui ta hakkab pasunat puhuma, läheb täide Jumala saladus, nõnda nagu Ta seda rõõmsat sõnumit on kuulutanud prohvetitele, Oma sulastele!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 8, 'Ja hääl, mida ma taevast kuulsin, rääkis jälle minuga ja ütles: \"Mine võta avatud raamatuke Ingli käest, kes seisab mere ja maa peal!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 9, 'Ja ma läksin Ingli juurde ning ütlesin temale: \"Anna mulle see raamatuke!\" Ja tema ütles mulle: \"Võta ja söö see ära! Ja see peab su kõhus olema mõru, aga su suus magus nagu mesi!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 10, 'Ja ma võtsin raamatukese Ingli käest ja sõin selle ära. Ja see oli mu suus magus nagu mesi. Ja kui ma selle olin söönud, sai see mu kõhus mõruks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 11, 'Ja minule öeldi: \"Sa pead veel ennustama rahvaste ja rahvahõimude ja keelte ja paljude kuningate kohta!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 1, 'Siis anti mulle kepi sarnane pilliroog ning öeldi: \"Tõuse ja mõõda Jumala tempel ja altar ja need, kes seal kummardavad;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 2, 'aga väljaspool templit olev õu jäta kõrvale ja ära seda mõõda, sest see on antud paganaile. Ja nad tallavad püha linna nelikümmend ja kaks kuud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 3, 'Ja mina annan Oma kahele tunnistajale meelevalla kotiriidesse riietatuina prohvetlikult kuulutada tuhat kakssada kuuskümmend päeva.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 4, 'Nemad on need kaks õlipuud ja kaks küünlajalga, mis seisavad ilmamaa Issanda ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 5, 'Ja kui keegi tahab neile kurja teha, siis väljub tuli nende suust ja sööb nende vaenlased; kui keegi tahab neile kurja teha, siis peab see nõnda tapetama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 6, 'Neil on meelevald sulgeda taevas, et nende prohvetliku kuulutamise päevil ei sajaks vihma, ja neil on meelevald vete üle muuta neid vereks ja lüüa maad igasuguse nuhtlusega nii sageli kui nad vaid tahavad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 7, 'Ja kui nad oma tunnistuse on lõpetanud, siis hakkab metsaline, kes tõuseb sügavusest, nendega sõda pidama ja võidab nad ära ja surmab nad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 8, 'Ja nende kehad vedelevad suure linna tänavail, mida vaimulikult hüütakse Soodomaks ja Egiptuseks, kus ka nende Issand risti löödi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 9, 'Ja rahvaste ja suguharude ja keelte ja rahvahõimude seast nähakse nende kehi kolm ja pool päeva ja nende kehi ei lasta hauda panna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 10, 'Ja need, kes maa peal elavad, rõõmustuvad nendest ja on väga rõõmsad ning läkitavad ande üksteisele, sest et need kaks prohvetit vaevasid neid, kes maa peal elavad.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 11, 'Ja pärast neid kolme ja poolt päeva läks elu vaim Jumalast nende sisse ja nad tõusid püsti oma jalgele, ja suur kartus tuli nende peale, kes neid vaatasid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 12, 'Ja nad kuulsid suurt häält taevast neile ütlevat: \"Tulge siia üles!\" Ja nad läksid üles taevasse pilves, ja nende vaenlased vaatasid neid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 13, 'Ja samal tunnil sündis suur maavärisemine, ja kümnes osa linna langes maha, ja selles maavärisemises sai surma inimesi seitse tuhat nime. Siis teised lõid kartma ja andsid taeva Jumalale austust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 14, 'Teine häda on möödas, vaata, kolmas häda tuleb varsti!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 15, 'Ja seitsmes Ingel puhus pasunat. Siis sündisid suured hääled taevas ning ütlesid: \"Maailma valitsus on saanud meie Issanda ja Tema Kristuse omaks, ja Tema valitseb ajastute ajastuteni\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 16, 'Ja need kakskümmend neli vanemat, kes istuvad Jumala ees oma aujärgedel, heitsid silmili maha ja kummardasid Jumalat');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 17, 'ning ütlesid: \"Me täname Sind, Issand, kõigeväeline Jumal, Kes oled ja Kes olid, et sa oled võtnud kätte Oma suure väe ja oled saanud kuningaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 18, 'Ja paganrahvad on vihastunud, kuid Sinu viha on tulnud, ja tulnud on aeg mõista kohut surnutele ja kätte anda palk Su sulastele-prohvetitele ja pühadele ja neile, kes kardavad Sinu nime, pisukestele ja suurtele, ja hävitada need, kes hävitavad maad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 19, 'Ja Jumala tempel taevas avanes ja Tema seaduselaegast nähti Tema templis. Siis sündis välke ja hääli ja mürinat ja maavärisemine ja suur rahesadu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 1, 'Ja suur imetäht ilmus taevas: naine, riietatud päikesega ja kuu tema jalge all, ja temal peas pärg kaheteistkümnest tähest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 2, 'Ta oli käima peal ja kisendas lapsevaevas ja tal oli suur valu sünnitades.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 3, 'Ja teine imetäht ilmus taevas, ja vaata, suur tulipunane lohe, seitsme peaga ja kümne sarvega, ja ta peade peal seitse ehissidet.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 4, 'Tema saba pühkis ära kolmanda osa taevatähti ja viskas need maa peale. Ja lohe seisis naise ees, kes oli sünnitamas, et niipea kui ta on sünnitanud, neelata laps.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 5, 'Ja naine tõi ilmale poeglapse, kes peab raudkepiga karjatama kõiki paganrahvaid. Ja naise laps kisti Jumala ja Tema aujärje juurde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 6, 'Ja naine põgenes kõrbe, kus temal oli Jumalast valmistatud ase, et teda seal toidetaks tuhat kakssada kuuskümmend päeva.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 7, 'Ja sõda tõusis taevas: Miikael ja tema Inglid sõdisid lohe vastu, ja lohe sõdis ja tema Inglid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 8, 'Ja ta ei saanud võimust ja nende aset ei leitud enam taevast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 9, 'Ja suur lohe, see vana madu, keda hüütakse Kuradiks ja Saatanaks, kes eksitab kogu maailma, visati maa peale, ja tema Inglid visati välja ühes temaga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 10, 'Ja ma kuulsin suurt häält taevast ütlevat: \"Nüüd on õnnistus ja vägi ja kuninglik valitsus saanud meie Jumala ja meelevald Tema Kristuse kätte, sest välja on visatud meie vendade süüdistaja, kes nende peale kaebab meie Jumala ees päevad ja ööd!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 11, 'Ja nemad on tema võitnud Talle vere tõttu ja oma tunnistuse sõna tõttu ega ole oma elu armastanud surmani.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 12, 'Seepärast olge väga rõõmsad, taevad, ja teie, kes seal sees elate! Häda maale ja merele, sest kurat on tulnud maha teie juurde ja tal on suur viha, sest ta teab, et tal on pisut aega!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 13, 'Ja kui lohe nägi, et ta oli visatud maa peale, ta kiusas taga naist, kes oli ilmale toonud poeglapse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 14, 'Ja naisele anti kaks suure kotka tiiba, et ta lendaks kõrbe oma paika, kus teda toidetakse aeg ja ajad ja pool aega eemal mao palge eest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 15, 'Ja madu purskas oma suust vett naisele järele otsekui jõge, et teda jõevooluga ära uhtuda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 16, 'Aga maa aitas naist: maa avas oma suu ja neelas ära jõe, mille lohe oma suust välja ajas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 17, 'Ja lohe sai vihaseks naise peale ja läks sõdima nendega, kes naise soost olid üle jäänud ja kes peavad Jumala käske ja kellel on Jeesuse tunnistus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 1, 'Ja ma nägin metsalist tõusvat merest. Sel oli seitse pead ja kümme sarve ja tema sarvede peal kümme ehissidet ja tema peade peal Jumala pilkenimesid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 2, 'Ja metsaline, keda ma nägin, oli pantri sarnane, ja tema jalad olid nagu karu jalad ja tema suu nagu lõukoera suu, ja lohe andis temale oma väe ja oma aujärje ja suure võimu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 3, 'Ja ma nägin ühe tema peadest olevat nagu surmavalt haavatud; ja tema surmahaav paranes. Ja kogu ilmamaa imetles jälgides metsalist,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 4, 'ja nad kummardasid lohet, et ta metsalisele oli andnud selle võimu, ja kummardasid metsalist ning ütlesid: \"Kes on metsalise sarnane ja kes suudab sõdida tema vastu?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 5, 'Ja temale anti suu rääkida suurustamise ja jumalapilke sõnu, ja temale anti meelevald tegutseda nelikümmend ja kaks kuud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 6, 'Ja ta avas oma suu pilkamiseks Jumala vastu, et pilgata Tema nime ja tema telki, neid, kes taevas elavad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 7, 'Ja temale anti võimus pidada sõda pühadega ja nad ära võita; ja temale anti meelevald iga suguharu ja rahva ja keele ja rahvahõimu üle.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 8, 'Ja teda hakkavad kummardama kõik, kes maa peal elavad, kelle nimed maailma algusest ei ole kirjutatud tapetud Talle eluraamatusse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 9, 'Kui kellelgi on kõrv, siis ta kuulgu!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 10, 'Kui keegi viib vangi, siis ta ise satub vangi; kui keegi tapab mõõgaga, siis tapetakse teda ennast mõõgaga. Siin on pühade kannatlikkus ja usk.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 11, 'Ja ma nägin teist metsalist tõusvat maa seest; ja sel oli kaks sarve otsekui tallel ja ta rääkis nagu lohe.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 12, 'Ja ta teeb kõik esimese metsalise võimuga tema ees. Ja ta teeb, et maa ja need, kes seal peal elavad, kummardavad esimest metsalist, kelle surmahaav oli paranenud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 13, 'Ja ta teeb suuri tunnustähti, nõnda et ta inimeste ees laseb tuldki tulla taevast alla maa peale.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 14, 'Ja ta eksitab neid, kes maa peal elavad, tunnustähtedega, mis temale on antud teha esimese metsalise ees, ning käsib neid, kes maa peal elavad, teha kuju metsalisele, kellel oli mõõga haav ja kes virgus ellu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 15, 'Ja temale anti meelevald anda metsalise kujule vaim, et ka metsalise kuju räägiks ja teeks, et need, kes iialgi ei kummarda metsalise kuju, ära tapetaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 16, 'Ja tema teeb, et kõik, nii pisukesed kui suured, nii rikkad kui vaesed, nii vabad kui orjad, võtavad märgi oma parema käe peale või oma otsaesisele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 17, 'ja et ükski muu ei saa osta ega müüa kui aga see, kellel on see märk: metsalise nimi või tema nime arv.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 18, 'Siin on tarkus! Kellel on mõistust, see arvaku ära metsalise arv; sest see on inimese arv. Ja tema arv on kuussada kuuskümmend kuus.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 1, 'Ja ma nägin, ja vaata, Tall seisis Siioni mäel ja ühes Temaga sada nelikümmend neli tuhat, kellele oli otsaesisele kirjutatud Tema Isa nimi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 2, 'Ja ma kuulsin häält taevast otsekui hulga vete kohisemist ja nagu suure müristamise häält; ja hääl, mida ma kuulsin, oli otsekui kandlelööjate hääl, kes löövad oma kandleid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 3, 'Ja nemad laulsid uut laulu aujärje ees ja nende nelja olendi ja vanemate ees. Ja ükski ei võinud seda laulu õppida kui aga need sada nelikümmend neli tuhat, kes on ära ostetud ilmamaalt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 4, 'Need on need, kes endid ei ole rüvetanud naistega, sest nad on neitsilikud; need on, kes Talle järgivad, kuhu Ta iganes läheb; need on inimeste seast ära ostetud esianniks Jumalale ja Tallele,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 5, 'ja nende suust ei ole leitud kavalust; nemad on veatud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 6, 'Ja ma nägin teist Inglit lendavat kesktaeva kohal; sellel oli igavene Evangeelium, et armuõpetust kuulutada neile, kes elavad maa peal, ja kõigile rahvahõimudele ja suguharudele ja keeltele ja rahvastele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 7, 'Ja ta ütles suure häälega: \"Kartke Jumalat ja andke Temale austust, sest on tulnud Tema kohtutund, ja kummardage Teda, Kes on teinud taeva ja maa ja mere ja veteallikad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 8, 'Ja veel teine Ingel järgnes temale ning ütles: \"Langenud, langenud on suur linn Baabülon, kes oma hooruskiima viinaga on jootnud kõiki rahvaid!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 9, 'Ja veel kolmas Ingel järgnes neile ning ütles suure häälega: \"Kui keegi kummardab metsalist ja tema kuju ja võtab tema märgi oma otsaesisele või oma käe peale,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 10, 'siis see saab ka juua Jumala kange viha viinast, mis segamata on valatud Tema viha karikasse; ja Teda peab vaevatama tules ja väävlis pühade Inglite ees ja Talle ees.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 11, 'Ja nende valu suits tõuseb üles ajastute ajastuteni; ja ei ole rahu päevad ega ööd neil, kes kummardavad metsalist ja tema kuju ega neil, kes vastu võtavad tema nime märgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 12, 'Siin on pühade kannatlikkus; siin on need, kes peavad Jumala käske ja Jeesuse usku!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 13, 'Ja ma kuulsin häält taevast ütlevat: \"Kirjuta: õndsad on surnud, kes Issandas surevad nüüdsest peale; tõesti, ütleb Vaim, nad hingavad oma vaevadest, sest nende teod lähevad nendega ühes!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 14, 'Ja ma nägin: vaata, valge pilv ja see, kes pilve peal istus, oli Inimese Poja sarnane; Temal oli kuldpärg peas ja terav sirp käes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 15, 'Ja templist väljus teine Ingel ning hüüdis suure häälega sellele, kes istus pilve peal: \"Pista sirp sisse ja põima, sest lõikuseaeg on tulnud, maa lõikus on ju valminud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 16, 'Ja see, kes istus pilve peal, laskis oma sirbi käia üle maa, ja maa lõigati paljaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 17, 'Ja teine Ingel väljus taeva templist, ja temalgi oli terav sirp käes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 18, 'Ja veel teine Ingel väljus altarist, ja temal oli võimus tule üle. Ja ta hüüdis suure häälega sellele, kellel oli terav sirp käes, ning ütles: \"Pista sisse oma terav sirp ja lõika maha maa viinapuu tarjad, sest selle kobarad on küpsed!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 19, 'Ja Ingel laskis käia oma sirbi üle maa ja lõikas maa viinamäe paljaks ja viskas marjad Jumala kange viha suurde surutõrde.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 20, 'Ja surutõrt sõtkuti väljaspool linna, ja tõrrest jooksis verd välja hobuste valjastest saadik tuhat kuussada vagu maad!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 1, 'Ja ma nägin teist imetähte taevas, suurt ja imelist: seitse Inglit, kelle käes oli seitse viimset nuhtlust; sest nendega sai Jumala kange viha täielikuks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 2, 'Ja ma nägin otsekui klaasmerd segatud tulega ja neid, kes olid võitnud metsalise ja ta kuju ja ta nime arvu, seisvat klaasmere ääres, Jumala kandled käes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 3, 'Ja nad laulsid Jumala sulase Moosese laulu ja Talle laulu, öeldes: \"Suured ja imelised on Sinu teod, Issand Jumal, Kõigeväeline! Õiged ja tõelised on Sinu teed, Sa rahvaste Kuningas!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 4, 'Kes ei peaks kartma Sind, Issand, ja andma austust Sinu nimele? Sest Sina üksi oled püha; sest kõik paganad tulevad ja kummardavad Sind, sellepärast et Su õiged kohtuotsused on saanud avalikuks!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 5, 'Ja pärast seda ma nägin: tunnistustelgi tempel taevas avati.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 6, 'Ja need seitse Inglit, kelle käes oli seitse nuhtlust, väljusid templist, riietatud puhta ja hiilgava lõuendiga ja rinde ümbert vöötatud kuldvööga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 7, 'Ja üks neist neljast olendist andis seitsmele Inglile seitse kuldkaussi täis kanget Jumala viha, kes elab ajastute ajastuteni.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 8, 'Ja tempel sai täis suitsu Jumala aust ja Tema väest, ja ükski ei võinud sinna templisse sisse minna enne kui oli täide saadetud seitsme Ingli seitse nuhtlust.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 1, 'Ja ma kuulsin suurt häält templis ütlevat seitsmele Inglile: \"Minge ja valage Jumala kange viha kausid välja maa peale!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 2, 'Ja esimene läks ja valas oma kausi välja maa peale. Ja kurje ning ilgeid mädapaiseid tekkis inimeste külge, kellel oli metsalise märk ja kes kummardasid tema kuju.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 3, 'Ja teine Ingel valas oma kausi välja merre, ja see sai otsekui surnu vereks, ja suri iga elav hing, kõik, kes meres olid.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 4, 'Ja kolmas Ingel valas oma kausi välja jõgedesse ja veeallikaisse, ja need said vereks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 5, 'Ja ma kuulsin vete Inglit ütlevat: \"Õige oled Sina, Kes oled ja Kes olid, ja Sina, Püha, et Sa nõnda oled kohut mõistnud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 6, 'Sest nemad on pühade ja prohvetite verd valanud, ja verd oled ka Sina neile juua andnud; nad on seda väärt!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 7, 'Ja ma kuulsin altarit ütlevat: tõesti, Issand Jumal, Sa Kõigeväeline, tõelised ja õiged on Sinu kohtuotsused!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 8, 'Ja neljas Ingel valas välja oma kausi päikese peale. Ja temale anti inimesi tulega kõrvetada.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 9, 'Ja suur kuumus kõrvetas inimesi ja nemad pilkasid Jumala nime, Kelle meelevalla all on need nuhtlused, ega parandanud meelt, et nad Temale oleksid austust andnud.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 10, 'Ja viies Ingel valas välja oma kausi metsalise aujärje peale. Ja tema kuningriik pimenes; ja nad närisid oma keelt valu pärast');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 11, 'ja pilkasid taeva Jumalat oma valude ja paisete pärast ega pöördunud oma tegudest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 12, 'Ja kuues Ingel valas välja oma kausi suure Eufrati jõe peale, ja selle vesi kuivas ära, et tee oleks valmis kuningaile, kes tulevad päevatõusu poolt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 13, 'Ja ma nägin lohe suust ja metsalise suust ja valeprohveti suust väljuvat kolm rüvedat vaimu otsekui konnad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 14, 'Need on nimelt kuradite tunnustähti tegevad vaimud, kes lähevad välja kõige ilmamaa kuningate juurde neid koguma sõtta Kõigeväelise Jumala suureks päevaks. -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 15, 'Vaata, ma tulen kui varas! Õnnis see, kes valvab ja hoiab oma riideid, et ta ei käiks alasti ja et ei nähtaks Tema häbi. -');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 16, 'Ja need kogusid nad kokku ühte paika, mida heebrea keeles kutsutakse Harmagedooniks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 17, 'Ja seitsmes Ingel valas oma kausi välja õhku, ja suur hääl kostis taeva templist aujärjelt ning ütles: \"See on sündinud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 18, 'Ja sündis hääli ja müristamisi ja välke ja tekkis suur maavärisemine, millist ei ole olnud sest ajast, kui inimesi on olnud maa peal; säärane suur maavärisemine oli see.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 19, 'Ja suur linn jagunes kolmeks osaks, ja paganrahvaste linnad langesid. Ja suurt Baabüloni tuletati meelde Jumala ees, et Jumal annaks temale oma raevuviina karika.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 20, 'Ja kõik saared põgenesid ja mägesid ei leidunud enam.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 21, 'Ja suuri raheteri, talendiraskusi, tuli taevast maha inimeste peale. Ja inimesed pilkasid Jumalat rahe nuhtluse pärast; sest selle nuhtlus oli väga vali.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 1, 'Siis tuli üks seitsmest Inglist, kelle käes olid need seitse kaussi, ja kõneles minuga ning ütles: \"Tule, ma tahan sulle näidata suure hoora nuhtlemist, kes istub suurte vete peal,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 2, 'kellega on hooranud ilmamaa kuningad ja kelle hooruse viinast on ilmamaa elanikud joobnuks saanud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 3, 'Ka tema viis mind vaimus ära kõrbe. Seal ma nägin naist istuvat helkjaspunase metsalise seljas, kes oli täis pilkenimesid ja kellel oli seitse pead ja kümme sarve.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 4, 'Ja naine oli riietatud purpuriga ja helkjaspunase riidega ning ehitud kulla ja kalliskivide ja pärlitega, ja temal oli käes kuldkarikas täis jälkusi ja oma hooruse saasta.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 5, 'Ja tema otsaesisele oli kirjutatud nimi, saladus: \"Suur Baabülon, ilmamaa hoorade ja jälkuste ema!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 6, 'Ja ma nägin naise olevat joobnud pühade verest ja Jeesuse tunnistajate verest. Ja ma imestasin üliväga teda nähes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 7, 'Ja Ingel ütles mulle: \"Mispärast sa imestad? Ma ütlen sulle saladuse naisest ja metsalistest, kes naist kannab ja kellel on seitse pead ja kümme sarve.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 8, 'Metsaline, keda sa nägid, on olnud, ja teda ei ole enam; aga ta tuleb sügavusest üles ja läheb hukatusse; ja ilmamaa elanikud, kelle nimed ei ole kirjutatud eluraamatusse maailma asutamisest alates, imestavad metsalist nähes, sest ta oli ja teda ei ole, aga ta tuleb jälle!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 9, 'Siia kuulub mõistus, millel on tarkust. Need seitse pead on seitse mäge, millel naine istub, ja on seitse kuningat:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 10, 'viis on neist langenud, üks on alles, ja teine ei ole veel tulnud. Kui ta tuleb, peab ta jääma natukeseks ajaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 11, 'Ja metsaline, kes on olnud ja keda ei ole enam, on ise kaheksas ja ühtlasi üks neist seitsmest, ja ta läheb hukatusse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 12, 'Ja need kümme sarve, mis sa nägid, on kümme kuningat, kes ei ole veel saanud valitsust, aga nad saavad võimu kui kuningad üheks tunniks ühes metsalisega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 13, 'Neil on üks ja sama nõu, ja nad annavad metsalise kätte oma väe ja võimu.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 14, 'Nemad hakkavad sõdima Tallega, ja Tall võidab nad ära, sest Tema on isandate Isand ja kuningate Kuningas, ja ühes Temaga võidavad need, kes on kutsutud ja valitud ja ustavad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 15, 'Ja ta ütles mulle: \"Veed, mis sa nägid seal, kus hoor istub, on rahvad ja rahvahulgad ja rahvahõimud ja keeled.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 16, 'Ja need kümme sarve, mis sa nägid, ja metsaline, need vihkavad hoora ja teevad tema puupaljaks ja alasti ja söövad tema liha ja põletavad ta ära tulega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 17, 'Sest Jumal on neile andnud südamesse teha Tema nõu järgi ja heita ühte nõusse ning anda Oma kuninglik valitsus metsalise kätte, kuni täituvad Jumala sõnad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 18, 'Ja naine, keda sa nägid, on suur linn, kelle käes on valitsus ilmamaa kuningate üle!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 1, 'Pärast seda ma nägin teist Inglit taevast maha tulevat, ja temal oli suur meelevald; ja ilmamaa läks valgeks tema auhiilgusest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 2, 'Ja tema hüüdis võimsa häälega ning ütles: \"Langenud, langenud on suur Baabülon ja on saanud kurjade vaimude eluasemeks ja kõigi rüvedate vaimude ulualuseks ja kõigi rüvedate ja vihatud lindude pesapaigaks!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 3, 'Sest tema hooruskiima viina on joonud kõik rahvad, ja ilmamaa kuningad on temaga hooranud ja ilmamaa kaupmehed on tema liiast toredusest rikkaks saanud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 4, 'Ja ma kuulsin teist häält taevast ütlevat: \"Minge välja temast, Minu rahvas, et te ei saaks tema pattude osaliseks ja et teid ei tabaks tema nuhtlused!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 5, 'Sest tema patud on ulatunud taevani ja Jumal on tuletanud meelde tema ülekohtused teod.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 6, 'Makske temale kätte, nõnda nagu tema on kätte maksnud, ja tasuge temale kahekordselt tema tegusid mööda; karikasse, mille tema on täis valanud, valage temale kahekordselt;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 7, 'nii palju kui tema on ülistanud iseennast ja taga ajanud toredust, niisama palju tehke temale piina ja leina. Sest ta ütleb oma südames: ma istun kui kuninganna ega ole lesk ega saa kunagi näha kurvastust!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 8, 'Sellepärast tulevad ühel päeval tema nuhtlused: surm ja lein ja nälg, ja ta põletatakse ära tulega, sest vägev on Issand Jumal, kes on mõistnud kohut tema üle!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 9, 'Ja teda uluvad taga ja kaebavad ilmamaa kuningad, kes temaga on hooranud ja toredasti elanud, kui nad näevad tema põlemise suitsu,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 10, 'hirmu pärast tema piina ees kaugel seistes ning öeldes: \"Häda, häda, Baabülon, sa suur linn, sa vägev linn, sest ühe silmapilguga on su kohus tulnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 11, 'Ja ilmamaa kaupmehed nutavad ja leinavad tema pärast, sest keegi ei osta enam nende kaupa:');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 12, 'ei kaubaks toodud kulda, ei hõbedat, ei kalliskive, ei pärleid, ei kallist lõuendit, ei purpurit, ei siidi, ei helkjaspunast riiet, ei mingisugust healõhnalist puud, ei mingisugust elevandiluust riista ega mingisugust kallimast puust riista, ei vasest, ei rauast ega marmorkivist riista,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 13, 'ei kaneeli, ei juuksevõiet, ei suitsutusrohte, ei salvi, ei viirukit, ei viina, ei õli, ei peenikest jahu, ei nisu, ei veiseid, ei lambaid, ei hobuseid, ei tõldu, ei orje ega inimhingi!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 14, 'Ja puuvili, mida su hing ihaldas, on su käest ära läinud, ja kõik, mis lihav ja läikiv, on su käest ära läinud, ja seda sa enam ei leia!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 15, 'Nende asjade kaupmehed, kes selle linna kaudu rikkaks said, seisavad kaugel hirmu pärast tema piina ees, nuttes ja leinates,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 16, 'ja ütlevad: \"Häda, häda, sa suur linn, kes olid riietatud kalli lõuendi ja purpuri ja helkjaspunase riidega ning olid ilustatud kulla ja kalliskivide ja pärlitega, sest ühe silmapilguga on nii suur rikkus hävitatud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 17, 'Ja kõik tüürimehed ja kõik rannasõidu-laevnikud ja meremehed ja kõik, kes merel sõidavad, seisid eemal');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 18, 'ja kisendasid, kui nad nägid tema põlemise suitsu, ning ütlesid: \"Kes on selle suure linna sarnane?\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 19, 'Ja nad puistasid põrmu pea peale ja kisendasid nuttes ja leinates ning ütlesid: \"Häda häda, sa suur linn, kelle varaga on rikkaks saanud kõik, kellel oli laevu merel - ühe silmapilguga on ta hävitatud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 20, 'Rõõmutsege temast, taevas ja pühad ja Apostlid ja prohvetid! Sest Jumal on teie kohtu mõistnud tema kätte!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 21, 'Ja üks tugev Ingel tõstis kivi, nagu suure veskikivi, ja viskas selle merre, öeldes: \"Nõnda visatakse suur linn Baabülon äkitselt ära ja teda ei leita enam!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 22, 'Su sees ei ole enam kuulda kandlelööjate ja mängumeeste ja vileajajate ja pasunapuhujate häält ega leidu sinus enam ühtki ametimeest; ja enam ei kosta su sees veski mürin;');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 23, 'su sees ei paista enam küünlavalgus, ei ole enam kuulda peigmehe ega pruudi häält su sees! Sest sinu kaupmehed olid maa suured isandad, sinu nõidusega on kõik rahvad viidud eksitusse!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 24, 'Temast on leitud prohvetite ja pühade verd ja kõikide verd, kes on tapetud maa peal!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 1, 'Pärast seda ma kuulsin otsekui hulga rahva suurt häält taevast ütlevat: \"Halleluuja! Õnnistus ja austus ja vägi olgu meie Jumalale!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 2, 'Sest tõelised ja õiged on Tema kohtud, et Ta on kohut mõistnud suure hoora üle, kes rikkus ilmamaa oma hooraeluga, ja on Oma sulaste vere temale kätte maksnud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 3, 'Ja nad ütlesid teist korda: \"Halleluuja!\" Ja tema suits tõuseb üles ajastute ajastuteni.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 4, 'Ja need kakskümmend neli olendit heitsid maha ja kummardasid Jumalat, kes aujärjel istub ja ütlesid: \"Aamen, halleluuja!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 5, 'Ja aujärjelt kostis hääl, mis ütles: \"Kiitke meie Jumalat, kõik Tema sulased, kes Teda kardate, nii pisukesed kui suured!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 6, 'Ja ma kuulsin otsekui hulga rahva häält ja otsekui suurte vete kohinat ja otsekui kange pikse müristamist, ütlevat: \"Halleluuja! Sest Issand, meie Kõigeväeline Jumal, on võtnud kuningliku valitsuse Oma kätte!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 7, 'Rõõmutsegem ja hõisakem ning andkem Temale austust! Sest Talle pulmad on tulnud ja Tema naine on ennast valmistanud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 8, 'Ja naisele anti riietumiseks hiilgav ja puhas lõuend. See lõuend on pühade õiged teod!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 9, 'Ja Ingel ütles mulle: \"Kirjuta: õndsad on need, kes Talle pulma õhtusöömaajale on kutsutud!\" Ja tema ütles veel mulle: \"Need on tõelised Jumala sõnad!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 10, 'Ja ma heitsin maha tema jalge ette, et teda kummardada, aga tema ütles mulle: \"Vaata, ära tee seda! Ma olen sinu ja nende su vendade kaassulane, kellel on Jeesuse tunnistus. Kummarda Jumalat, sest Jeesuse tunnistus on prohvetikuulutamise vaim!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 11, 'Ja ma nägin taeva avatud, ja vaata: valge hobune! Ja Selle nimi, Kes tema seljas istus, on Ustav ja Tõeline, ja Tema mõistab kohut ja sõdib õiguses.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 12, 'Aga Tema silmad olid tuleleek ja Temal oli peas palju ehissidemeid; ja Temale oli kirjutatud nimi, mida ei tea keegi muu kui Tema Ise.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 13, 'Ja Temal oli seljas verega kastetud kuub ja Tema nimeks on pandud Jumala Sõna.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 14, 'Ja sõjaväed taevas järgisid Teda valgete hobuste seljas ja olid riietatud valge ja puhta lõuendiga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 15, 'Ja Tema suust tuli välja terav mõõk, et sellega lüüa rahvaid. Ja Tema Ise karjatab neid raudkepiga. Ja Ta ise sõtkub kõigeväelise Jumala tulise viha viina surutõrt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 16, 'Ja Temale on Tema kuue ja puusa peale kirjutatud nimi: \"Kuningate Kuningas ja isandate Issand!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 17, 'Ja ma nägin üht Inglit seisvat päikeses ja see kisendas suure häälega ning hüüdis kõigile kesktaeva kohal lendavaile lindudele: \"Tulge ja lennake kokku suurele Jumala söömaajale');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 18, 'sööma kuningate liha ja ülemate pealikute liha ja vägevate liha ja hobuste ja nende seljas istujate liha ja kõigi vabade ja orjade ja pisukeste ja suurte liha!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 19, 'Ja ma nägin metsalist ja ilmamaa kuningaid ja nende sõjaväge olevat kogunenud sõda pidama Sellega, Kes istus hobuse seljas, ja Tema sõjaväega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 20, 'Ja metsaline võeti kinni ja temaga ühes valeprohvet, kes tema ees oli teinud oma imeteod, millega ta oli eksitanud neid, kes metsalise märgi olid vastu võtnud ja kes kummardasid tema kuju. Nad mõlemad visati elusalt tulejärve, mis põleb väävliga.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 21, 'Ja teised tapeti mõõgaga, mis lähtus Selle suust, Kes istus hobuse seljas. Ja kõigi lindude kõhud said täis nende lihast.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 1, 'Ja ma nägin Ingli taevast maha tulevat; sellel oli sügaviku võti ja suured ahelad käes.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 2, 'Ja ta võttis kinni lohe, vana mao, see on Kuradi ehk Saatana, ja sidus ta ahelaisse tuhandeks aastaks');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 3, 'ja heitis ta sügavikku ja pani ta luku taha ja vajutas pealepoole teda pitseri, et ta enam ei eksitaks rahvaid, kuni need tuhat aastat otsa saavad. Pärast seda peab teda natukeseks ajaks lahti lastama.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 4, 'Ja ma nägin aujärgi ja neid, kes nendel istusid; ja kohus anti nende kätte; ja ma nägin nende hingi, kelle pead olid ära raiutud Jeesuse tunnistuse ja Jumala sõna pärast ja kes ei olnud kummardanud metsalist ega tema kuju ega olnud võtnud tema märki oma otsaesisele ja oma käe peale. Ja nad virgusid ellu usus ja valitsesid kuningikult ühes Kristusega tuhat aastat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 5, 'Aga muud surnud ei elustunud mitte, kuni need tuhat aastat otsa saavad. See on esimene ülestõusmine.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 6, 'Õnnis ja püha on see, kellel on osa esimesest ülestõusmisest; nende üle ei ole teisel surmal meelevalda, vaid nad peavad olema Jumala ja Kristuse preestrid ja valitsema kuninglikult ühes Temaga need tuhat aastat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 7, 'Ja kui need tuhat aastat täis saavad, siis lastakse saatan lahti oma vangist.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 8, 'Ja ta läheb välja eksitama rahvaid, kes elavad ilmamaa neljas nurgas, Googi ja Maagoogi, neid sõtta koguma. Nende arv on nagu mereäärne liiv!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 9, 'Ja nad tulid üles ilmamaa lagendikule ja piirasid ümber pühade leeri ja armastatud linna. Siis langes tuli taevast maha ja sõi nad ära!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 10, 'Ja kurat, kes neid eksitas, heideti tule ja väävli järve, kus on ka metsaline ja valeprohvet. Ja neid vaevatakse päevad ja ööd ajastute ajastuteni!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 11, 'Ja ma nägin suurt valget aujärge ja Seda, Kes sellel istub, Kelle palge eest põgenesid maa ja taevas, ja neile ei leitud aset.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 12, 'Ja ma nägin surnuid, suuri ja pisukesi, seisvat aujärje ees, ja raamatud avati. Ja teine raamat avati, see on eluraamat. Ja surnutele mõisteti kohut sedamööda kuidas raamatuisse oli kirjutatud, nende tegude järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 13, 'Ja meri andis tagasi need surnud, kes temas olid, ja surm ja surmavald andsid tagasi surnud, kes neis olid, ja igaühele mõisteti kohut tema tegude järgi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 14, 'Ja surm ja surmavald heideti tulejärve! See on teine surm, tulejärv.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 15, 'Ja keda ei leitud kirjutatud olevat eluraamatusse, heideti tulejärve!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 1, 'Ja ma nägin uut taevast ja uut maad; sest esimene taevas ja esimene maa olid kadunud ja merd ei olnud enam.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 2, 'Ja ma nägin püha linna, uut Jeruusalemma, taevast Jumala juurest maha tulevat, valmistunud otsekui pruut, kaunistatud oma mehele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 3, 'Ja ma kuulsin suurt häält aujärjelt ütlevat: \"Vaata, Jumala telk on inimeste juures! Ja Tema asub nende juurde elama ja nemad on Tema rahvad ja Jumal Ise on nendega.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 4, 'Ja Tema pühib ära kõik pisarad nende silmist, ja surma ei ole enam ega leinamist ega kisendamist ega vaeva ei ole enam. Sest endised asjad on möödunud!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 5, 'Ja Kes aujärjel istus, ütles: \"Vaata, Ma teen kõik uueks!\" Ja Ta ütles: \"Kirjuta, sest need sõnad on ustavad ja tõelised!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 6, 'Ja Ta ütles mulle: \"See on sündinud! Mina olen A ja O, algus ja ots. Mina annan sellele, kellel on janu, eluvee allikast ilma hinnata.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 7, 'Kes võidab, see pärib selle, ja Mina olen temale Jumalaks ja tema on Minule pojaks.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 8, 'Aga argade ja uskmatute ja jälkide ja tapjate ja hoorajate ja nõidade ja ebajumalateenijate ja kõigi valelike osa on tule ja väävliga põlevas järves! See on teine surm.\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 9, 'Ja minu juurde tuli üks seitsmest Inglist, kelle käes olid need seitse kaussi, täis seitset viimset nuhtlust; ja ta rääkis minuga ning ütles: \"Tule, ma näitan sulle mõrsjat, Talle naist!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 10, 'Ja tema kandis mind vaimus suure ja kõrge mäe otsa ja näitas mulle linna, püha Jeruusalemma, mis oli maha tulemas taevast Jumala juurest');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 11, 'ja millel on Jumala auhiilgus; ja tema valgus on kõige kallima kivi sarnane, otsekui jaspiskivi, mis hiilgab nagu mägikristall.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 12, 'Linnal on suur ja kõrge müür ja kaksteist väravat ja väravate peal kaksteist Inglit; ja väravate peale on kirjutatud nimed. Need on kaheteistkümne Iisraeli laste suguharu nimed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 13, 'Ida pool on kolm väravat, põhja pool kolm väravat, lõuna pool kolm väravat, lääne pool kolm väravat.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 14, 'Ja linna müüril on kaksteist aluskivi ja nende peal Talle kaheteistkümne Apostli nimed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 15, 'Ja sellel, kes minuga rääkis, oli käes kuldpilliroog, et mõõta linna ja tema väravaid ja tema müüri.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 16, 'Ja linn on nelinurkne ja tema pikkus on niisama suur kui tema laius. Ja ta mõõtis linna pillirooga kaksteist tuhat vagu. Tema pikkus ja laius ja kõrgus on võrdsed.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 17, 'Ja ta mõõtis tema müüri - sada nelikümmend neli küünart, inimese mõõdu järgi, mis on Ingli mõõt.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 18, 'Ja tema müüri alusehitus oli jaspiskivist ja linn puhtast kullast, selge klaasi sarnane.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 19, 'Ja linna müüri aluskivid olid ehitud kõiksuguste kalliskividega. Esimene aluskivi oli jaspis, teine safiir, kolmas kaltsedoon, neljas smaragd,');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 20, 'viies sardoonüks, kuues sardion, seitsmes krüsoliit, kaheksas berüll, üheksas topaas, kümnes krüsopraas, üheteistkümnes hüatsint, kaheteistkümnes ametüst.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 21, 'Ja need kaksteist väravat olid kaksteist pärlit: iga värav oli ühest pärlist ja linna tänav selgest kullast otsekui läbipaistev klaas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 22, 'Ja templit ma seal ei näinud, sest Issand, kõigeväeline Jumal, on tema tempel, ja Tall.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 23, 'Ja linnale ei ole tarvis päikest ega kuud, et need seal paistaksid; sest Jumala auhiilgus valgustab teda ja tema lamp on Tall.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 24, 'Ja rahvad hakkavad käima tema valguses, ja ilmamaa kuningad toovad sinna oma auhiilguse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 25, 'Ja tema väravaid ei suleta päeval, aga ööd seal ei olegi.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 26, 'Ja rahvaste auhiilgus ja kallisvarad tuuakse sinna sisse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 27, 'Aga sinna sisse ei pääse midagi, mis ei ole puhas, ei keegi, kes teeb jälkusi ja valet, vaid üksnes need, kes on kirjutatud Talle eluraamatusse.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 1, 'Ja ta näitas mulle puhast eluveejõge, selget nagu mägikristall. See voolas välja Jumala ja Talle aujärjest.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 2, 'Keset linna tänavat ja mõlemal pool jõge oli elupuu; see kandis vilja kaksteist korda, igas kuus andes oma vilja. Ja puu lehed tulid terviseks rahvastele.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 3, 'Ja midagi neetut ei ole enam. Ja Jumala ja Talle aujärg on siis seal, ja Tema sulased teenivad Teda.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 4, 'Ja nad näevad Tema palet, ja Tema nimi on nende otsaesisel.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 5, 'Ja ööd ei ole enam, ja neile ei ole vaja lambivalgust ega päevavalget, sest Issand Jumal valgustab neid, ja nemad valitsevad ajastute ajastuteni.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 6, 'Ja Ta ütles mulle: \"Need on ustavad ja tõelised sõnad! Ja Issand, prohvetite hingede Jumal, on läkitanud Oma Ingli Oma sulastele näitama, mis peab varsti sündima.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 7, 'Ja vaata, Ma tulen nobedasti! Õnnis see, kes peab selle raamatu ennustuse sõnu!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 8, 'Ja mina, Johannes, olen see, kes seda kuulis ja nägi. Ja kui ma seda olin kuulnud ja näinud, ma heitsin maha, kummardama Ingli jalgade ette, kes mulle seda näitas.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 9, 'Ja ta ütles mulle: \"Vaata, ära tee seda! Sest ma olen sinu ja su vendade, prohvetite ja nende kaassulane, kes selle raamatu sõnu peavad. Kummarda Jumalat!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 10, 'Ja ta ütles mulle: \"Ära pane pitseriga kinni selle raamatu ennustussõnu, sest aeg on ligidal!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 11, 'Kes ülekohut teeb, tehku veel ülekohut, ja kes on rüve, see rüvetugu veel enam; aga kes on õige, tehku veel õigust, ja kes on püha, see pühitsegu ennast veel enam!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 12, 'Vaata, Ma tulen pea, ja Mu palk on Minuga tasuda kätte igaühele nõnda nagu tema tegu on!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 13, 'Mina olen A ja O, Esimene ja Viimne, algus ja ots!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 14, 'Õndsad need, kes oma rüüd pesevad, et neil oleks meelevald süüa elupuust ja väravaist linna sisse minna!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 15, 'Väljaspool on koerad ja nõiad ja hoorajad ja tapjad ja ebajumalateenijad ja kõik, kes valet armastavad ja teevad.');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 16, 'Mina, Jeesus, läkitan Oma Ingli teile seda tunnistama kogudustes! Mina olen Taaveti juur ja sugu, helkjas koidutäht!\"');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 17, 'Ja Vaim ja pruut ütlevad: \"Tule!\" Ja kes seda kuuleb, öelgu: \"Tule!\" Ja kellel on janu, tulgu; ja kes tahab, võtku eluvett ilma hinnata!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 18, 'Mina tunnistan kõikidele, kes kuulevad selle raamatu ennustussõnu: kui keegi neile midagi juurde lisab, siis paneb Jumal tema peale nuhtlused, mis selles raamatus on kirja pandud!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 19, 'Ja kui keegi võtab midagi ära selle prohvetiraamatu sõnadest, siis Jumal võtab ära tema osa elupuust ja pühast linnast, millest on kirjutatud selles raamatus!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 20, 'Tema, kes seda tunnistab ütleb: \"Tõesti, Mina tulen varsti!\" Aamen, tule, Issand Jeesus!');
INSERT INTO `Est_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 21, 'Issanda Jeesuse Kristuse arm olgu kõikidega! Aamen.');
