INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 1, 'Livre de la generation de Jefus Chrift, fils de David, fils d\'Abraham.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 2, 'Abraham engendra Ifaac. Et Ifaac engendra Jacob. Et Jacob engendra Juda &amp; fes freres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 3, 'Et Jude engendra Pharez &amp; Zara de Thamar. Et Pharez engendra Efrom. Et Efrom engendra Aram.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 4, 'Et Aram engendra Aminadab. Et Aminadab engendra Naaffson. Et Naaffon engendra Salmon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 5, 'Salmon engendra Boos de Rachab. Et Boos engendra Obed de Ruth. Et Obed engendra Jeffé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 6, 'Et Jeffé engendra le Roi David. Et le Roi David engendra Salomon, de celle qui avoit efté femme d\'Urie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 7, 'Et Salomon engendra Roboam. Et Roboam engendra Abia. Et Abia engendra Afa.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 8, 'Et Afa engendra Jofaphat. Et Jofaphat engendra Joram. Et Joram engendra Hozias.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 9, 'Et Hozias engendra Joatham. Et Joatham engendra Achaz. Et Achaz engendra Ezechias.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 10, 'Et Ezechias engendra Manaffé. Et Manaffé engendra Amon. Et Amon engendra Jofias');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 11, 'Et Jofias engendra Jakim. Et Jakim engendra Jechonias &amp; fes freres, fur le temps qu\'ils furent tranfportez en Babylone.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 12, 'Et apres qu\'ils eurent efte tranfportez en Babylone, Jechonias engendra Salathiël. Et Salathiël engendra Zorobabel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 13, 'Et Zorobabel engendra Abiud. Et Abiud engendra Eliakim. Et Eliakim engendra Azor.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 14, 'Et Azor engendra Sadoc. Et Sadoc engendra Achim. Et Achim engendra Eliud.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 15, 'Et Eliud engendra Eleazar. Et Eleazar engendra Matthan. Et Matthan engendra Jacob.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 16, 'Et Jacob engendra Jofeph, le mari de Marie, de laquelle eft né Jefus, qui eft dit Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 17, 'Toutes les generations donc depuis Abraham Jufques à David, font quatorze generations. Et depuis David jufques au temps qu\'ils furent tranfportez en Babylone, quatorze generations. Et depuis qu\'ils eurent efté trnafportez en Babylone, jufques à Chrift, quatorze generations.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 18, 'Or la naiffance de Jefus Chrift advint ainfi: Marie fa mere eftant fiancée à Jofeph, devant qu\'ils fuffent enfemble, elle fe trouva enceinte du S.Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 19, 'Alors Jofeph fon mari, d\'autant qu\'il eftoit jufte, &amp; qu\'il ne la vouloit point diffamer, la voulut laiffer fecrettement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 20, 'Mais comme il penfoit à ces chofes, voivi, l\'Ange du Seigneur lui apparut en fonge, difant, Jofeph fils de David, ne crain point de recevoir Marie ta femme: car ce qui eft engendré en elle, eft du S.Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 21, 'Et elle enfantera un fils, &amp; tu appellera fon nom JESUS: car il fauvera fon peuple de leurs pechez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 22, 'Or tout ceci eft advenu, afin que fuft accompli ce dont le Seigneur avoit parlé par le Prophete, difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 23, 'Voici, la vierge fera enceinte, &amp; enfantera un fils, &amp; on appellera fon nom Emmanuel, qui vaut autant à dire que DIEU-AVEC-NOUS.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 24, 'Jofeph donc eftant efveillé de fon dormir, fit ainfi que l\'Ange du Seigneur lui avoit commandé, &amp; receut fa femme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 25, 'Et ne la connut point jufques à ce qu\'elle euft enfanté fon premier né, &amp; appella fon nom Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 1, 'Or Jefus eftant né en Beth-lehem ville de Judée, au temps du Roi Herode, voici des Sages d\'Orient, arriver en Jerufalem,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 2, 'Difans, Où eft le Roi des Juifs qui eft né? car nous avons veu fon eftoile en Orient, &amp; fommes venus l\'adorer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 3, 'Or le Roi Herode ayant entendu cela, fut troublé, &amp; toute Jerufalem avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 4, 'Et ayant affemblé tous les principaux Sacrificateurs &amp; les Scribes du peuple, il s\'informa d\'eux, où le Chrift devoit naiftre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 5, 'Et ils lui dirent, En Beth-lehem ville de Judée: car il eft ainfi écrit par le Prophete,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 6, 'Et toi Beth-lehem terre de Juda, tu n\'es nullement la plus petite entre les gouverneurs de Juda, car de toi fortira le conducteur qui paiftra mon peuple Ifraël.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 7, 'Alors Herode ayant appellé en fecret les Sages, s\'enquit d\'eux foigneufement du temps que l\'eftoile leur eftoit apparuë.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 8, 'Et les envoyant en Beth-lehem, il leur dit, Allez, &amp; vous enquerez foigneufement touchant le petit enfant: &amp; quand vous l\'aurez trouvé, faites le moi fçavoir, afin que j\'y aille auffi, &amp; que je l\'adore.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 9, 'Eux donc ayant ouï le Roi, s\'en allerent: &amp; voici, l\'eftoile qu\'ils avoyent veuë en Orient, alloit devant eux, jufques à ce qu\'elle vint, &amp; s\'arrefta fur le lieu où eftoit le petit enfant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 10, 'Et quand ils virent l\'eftoille, ils s\'éjouïrent d\'une fort grande joye.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 11, 'Et eftans venus en la maifon, ils trouverent le petit enfant avec Marie fa mere, lequel ils adorerent, en fe profternant en terre, &amp; apres avoir defployé leurs trefors, ils lui prefenterent des dons, affavoir, de l\'or, de l\'encens, &amp; de la myrrhe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 12, 'Et eftans divinement advertis en fonge, de ne pas retourner vers Herode, ils fe retirerent en leur païs par un autre chemin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 13, 'Or apres qu\'ils fe furent retirez, voici l\'Ange du Seigneur apparut en fonge à Jofeph, difant, Leve toi, &amp; pren le petit enfant &amp; fa mere, &amp; t\'enfui en Egypte, &amp; te tien là jufques à ce que je te le die: car Herode cherchera le petit enfant pour le mettre à mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 14, 'Jofeph donc eftant efveillé, prit de nuict le petit enfant &amp; fa mere, &amp; fe retira en Egypte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 15, 'Et fe tint là jufques à la mort d\'Herode: afin que fuft accompli ce dont le Seigneur avoit parlé par le Prophete, difant, J\'ai appellé mon fils hors d\'Egypte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 16, 'Alors Herode fe voyant moqué des Sages, fut fort en colere, &amp; ayant envoyé fes gens, mit à mort tous les enfans qui eftoyent en Beth-lehem &amp; en tout fon territoire, depuis ceux de deux ans &amp; au deffous, felon le temps dont il s\'eftoit exactement enquis des Sages.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 17, 'Alors fut accompli ce dont avoit parlé Jeremie le Prophete, difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 18, 'On a ouï en Rama une voix, une lamentation, un pleur, &amp; un grand gemiffement, Rachel pleurant fes enfans, &amp; n\'a point voulu eftre confolée, de ce qu\'ils ne font plus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 19, 'Or Herode eftant mort, voici, l\'Ange du Seigneur apparut en fonge à Jofeph en Egypte,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 20, 'Difant, Leve toi, &amp; pren le petit enfant &amp; fa mere, &amp; t\'en va au païs d\'Ifraël: car ceux qui cherchoyent l\'ame du petit enfant font morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 21, 'Jofeph donc eftant efveillé, prit le petit enfant &amp; fa mere, &amp; s\'en vint au païs d\'Ifraël.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 22, 'Mais quand il entendit qu\'Archelaus regnoit en Judée au lieu d\'Herode fon pere, il craignit de s\'en aller là. Et eftant divinement adverti en fonge, il fe retira és quartiers de Galilée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 23, 'Et y eftant venu, il habita en la ville appellée Nazareth: afin que ce qui avoit efté dit par les Prophetes, fuft accompli, affavoir, Il fera appellé Nazarien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 1, 'Or en ce temps-là Jean Baptifte vint, prefchant au defert de Judée:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 2, 'Et difant, Amendez-vous: car le Royaume des cieux eft approché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 3, 'Car c\'eft celui-ci duquel il a efté parlé par Efaïe le Prophete, difant, La voix de celui qui crie au defert, eft, Preparez le chemin du Seigneur, dreffez fes fentiers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 4, 'Or ce Jean avoit fon veftement de poils de chameau, &amp; une ceinture de cuir à l\'entour de fes reins: &amp; fon manger eftoit des fauterelles &amp; du miel fauvage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 5, 'Alors fortoyent pour venir vers lui ceux de Jerufalem, &amp; de toute la Judée, &amp; du païs des environs du Jordain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 6, 'Et eftoyent baptifez par lui au Jordain, confeffans leurs pechez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 7, 'Lui donc voyant plufieurs des Pharifiens &amp; des Sadduciens venir à fon baptefme, leur dit, Engeances de viperes qui vous a avifez de fuïr l\'ire à venir?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 8, 'Faites donc des fruits convenables à repentance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 9, 'Et ne prefumez point de dire en vous-mefmes, Nous avons Abraham pour pere: car je vous dis que mefmes de ces pierres, Dieu peut fufciter des enfans à Abraham.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 10, 'Or la coignée eft déja mife à la racine des arbres: tout arbre donc qui ne fait point de bon fruit s\'en va eftre coupé &amp; jetté au feu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 11, 'Quant à moy je vous baptife d\'eau en repentance: mais celui qui vient apres moi, eft plus fort que moi, duquel je ne fuis pas digne de porter les fouliers: Celui-là vous baptifera du S.Efprit, &amp; de feu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 12, 'Il a fon van en fa main, &amp; il nettoyera entierment fon aire, &amp; affemblera fon froment au grenier: mais il bruflera la paille au feu qui ne s\'efteint point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 13, 'Alors Jefus vint de Galilée au Jordain vers Jean, pour eftre baptifé par lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 14, 'Mais Jean l\'en empefchoit fort, difant, J\'ai befoin moi d\'eftre baptifé par toi, &amp; tu viens vers moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 15, 'Et Jefus refpondant lui dit, Laiffe pour maintenant: car ainfi nous eft-il convenable d\'accomplir toute juftice. Alors il le laiffa.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 16, 'Et quand Jefus eut efté baptifé, il remonta incontinent de l\'eau: &amp; voici les cieux lui furent ouverts, &amp; il vid l\'Efprit de Dieu defcendre comme une colombe, &amp; venir fur lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 17, 'Et voici une voix du ciel, difant, Celui-ci eft mon Fils bien-aimé, en qui j\'ai pris mon bon plaifir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 1, 'Alors Jefus fut emmené par l\'Efprit au defert, pour eftre tenté du diable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 2, 'Et quand il eut jeufné quarante jours &amp; quarante nuits, finalement il eut faim.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 3, 'Et le Tentateur s\'approchant de lui, dit, Si tu es le Fils de Dieu, di que ces pierres deviennent pain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 4, 'Mais il refpondit, &amp; dit, Il eft écrit, L\'homme ne vivra point de pain feulement, mais de toute parole qui fort de la bouche de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 5, 'Alors le diable le tranfporta en la fainte ville, &amp; le mit fur les creneaux du temple,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 6, 'Et lui dit, Si tu es le Fils de Dieu, jette-toi en bas: car il eft écrit, Il donnera charge de toi à fes Anges, &amp; ils te porteront en leurs mains, de peur que tu heurtes ton pied à quelque pierre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 7, 'Jefus lui dit, Derechef il eft écrit, Tu ne tenteras point le Seigneur ton Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 8, 'Derechef le diable le tranfporta fur une fort haute montagne, &amp; lui montra tous les royaumes du monde, &amp; leur gloire:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 9, 'Et lui dit, je te donnerai toutes ces chofes, fi en te profternant en terre, tu m\'adores.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 10, 'Alors Jefus lui dit, Va Satan: car il eft écrit, Tu adoreras le Seigneur ton Dieu, &amp; tu ferviras à lui feul.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 11, 'Alors le diable le laiffa, &amp; voici, les Anges s\'approcherent, &amp; le fervirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 12, 'Or Jefus ayant entendu que Jean avoit efté mis en prifon, fe retira en Galilée,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 13, 'Et ayant laiffé Nazareth, il vint habiter en Capernaum ville maritime, és confins de Zabulon &amp; de Nephthali:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 14, 'Afin que fuft accompli ce dont il avoit efté parlé par Efaïe le Prophete, difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 15, 'Le païs de Zabulon &amp; de Nephthali vers le chemin de la mer, au delà du Jordain, la Galilée des Gentils:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 16, 'Le peuple qui gifoit en tenebres, a veu une grande lumiere, &amp; à ceux qui gifoyent en la region &amp; en l\'ombre de mort, la lumiere s\'eft levée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 17, 'Dés lors Jefus commença à prefcher, &amp; à dire, Amendez-vous, car le Royaume des cieux eft approché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 18, 'Et comme Jefus cheminoit le long de la mer de Galilée, il vid deux freres, Simon qui fut dit Pierre, &amp; André fon frere, qui jettoyent leur filé en la mer: car ils eftoyent pefcheurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 19, 'Et il leur dit, Venez apres moi, &amp; je vous ferai pefcheurs d\'hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 20, 'Et eux incontinent laiffans leur filez, le fuivirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 21, 'Et de là eftant allé plus outre, il vid deux autres freres, Jacques fils de Zebedée &amp; Jean fon frere en une naffelle, avec Zebedée leur pere, qui racouftroyent leurs filez, &amp; il les appella.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 22, 'Et eux incontinent ayans laiffé leur naffelle &amp; leur pere, le fuivirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 23, 'Et jefus tournoyoit par toute la Galilée, enfeignant en leurs Synagogues, &amp; prefchant l\'Evangile du Royaume, &amp; gueriffant toute forte de langueur entre le peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 24, 'Et fa renommée courut par toute la Syrie: &amp; on lui prefentoit tous ceux qui fe portoyent mal, qui eftoyent detenus de diverfes maladies &amp; tourmens, &amp; demoniaques, &amp; lunatiques, &amp; paralytiques: &amp; il les gueriffoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 25, 'Et de fort grandes troupes le fuivirent de Galilée, &amp; de Decapolis, &amp; de Jerufalem, &amp; de Judée, &amp; d\'outre le Jordain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 1, 'Or Jefus voyant les troupes, monta fur une montagne: &amp; comme il fe fut affis, fes difciples s\'approcherent de lui:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 2, 'Et ayant ouvert fa bouche, il les enfeignoit, difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 3, 'Bien-heureux font les pauvres en efprit: car le Royaume des Cieux eft à eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 4, 'Bien-heureux font ceux qui menent deüil: car ils feront confolez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 5, 'Bien-heureux font les debonnaires: car ils heriteront la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 6, 'Bien-heureux font ceux qui ont faim &amp; foif de juftice: car ils feront raffafiez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 7, 'Bien-heureux font les mifericordieux: car mifericorde leur fera faite.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 8, 'Bien-heureux font ceux qui font nets nets de coeur: car ils verront Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 9, 'Bien-heureux font ceux qui procurent la paix: car ils feront appellez enfans de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 10, 'Bien-heureux font ceux qui font perfecutez pour juftice: car le royaume des cieux eft à eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 11, 'Vous ferez bien-heureux quand on vous aura injuriez &amp; perfecutez, &amp; quand, à caufe de moi, on aura dit contre vous, en mentant, quelque mauvaife parole que ce foit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 12, 'Ejouïffez-vous, &amp; vous efgayez: car voftre falaire eft grand és cieux: car on a ainfi perfecuté les Prophetes qui ont efté devant vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 13, 'Vous eftes le fel de la terre: or fi le fel perd fa faveur, de quoi le falera-t\'on? il ne vaut plus rien, qu\'à eftre jetté dehors, &amp; foulé des hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 14, 'Vous eftes la lumiere du monde. La ville affife fur une montagne ne peut eftre cachée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 15, 'Et on n\'allume point la chandelle, pour la mettre fous un boiffeau, mais fur un chandelier, &amp; elle éclaire à tous ceux qui font en la maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 16, 'Ainfi reluife voftre lumiere devant les hommes, afin qu\'ils voyent vos bonnes oeuvres, &amp; qu\'ils glorifient voftre Pere qui eft és cieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 17, 'Ne penfez point que je fois venu aneantir la Loi ou les Prophetes: je ne fuis point venu les aneantir, mais les accomplir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 18, 'Car en verité je vous dis, que jufques à ce que le Ciel &amp; la terre foyent paffez, un feul jota ou un feul poinct de la Loi ne paffera point, que toutes chofes ne foyent faites.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 19, 'Celui donc qui aura violé l\'un de ces plus petits commandemens, &amp; enfeigné ainfi les hommes, fera tenu le plus petit au Royaume des cieux: mais celui qui les aura faits &amp; enfeignez, celui-là fera tenu grand au Royaume des cieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 20, 'Car je vous dis que fi voftre juftice ne furpaffe celle des Scribes &amp; des Pharifiens, vous n\'entrerez point au Royaume des cieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 21, 'Vous avez entendu qu\'il a efté dit aux anciens, Tu ne tueras point: &amp; qui tuera, fera puniffable par jugement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 22, 'Mais moi, je vous dis, Que quiconque fe courrouce contre fon frere fans caufe, fera puniffable par jugement: &amp; qui dira à fon frere, Raca, fera puniffable par confeil: &amp; qui lui dira fol, fera puniffable par la gehenne du feu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 23, 'Si donc tu apportes ton offrande à l\'autel, &amp; que là il te fouvienne que ton frere a quelque chofe contre toi,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 24, 'Laiffe là ton offrande devant l\'autel, &amp; t\'en va: reconcilie toi premierement avec ton frere, &amp; alors vien &amp; offre ton offrande.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 25, 'Sois bien-toft d\'accord avec ton adverfe partie, tandis que tu es en chemin avec elle; de peur que ton adverfe partie ne te livre au juge, &amp; que le juge ne te livre au fergent, &amp; que tu ne fois mis en prifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 26, 'En verité je te dis que tu ne fortiras point de là, jufques à ce que tu ayes rendu le dernier quadrin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 27, 'Vous avez entendu qu\'il a efté dit aux anciens, Tu ne commettras point adultere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 28, 'Mais moi, je vous dis, que quiconque regarde une femme pour la convoiter, il a déja commis adultere avec elle en fon coeur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 29, 'Que fi ton oeil droit te fait chopper, arrache-le, &amp; le jette arriere de toi: car il vaut mieux qu\'un de tes membres periffe, que non pas que tout ton corps foit jetté en la gehenne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 30, 'Et fi ta main droite te fait chopper, coupe-la, &amp; la jette arriere de toi: car il vaut mieux qu\'un de tes membres periffe que non pas que tout ton corps foit jetté en la gehenne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 31, 'Il a efté dit auffi, Si quelqu\'un delaiffe fa femme, qu\'il lui baille la lettre de divorce.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 32, 'Mais moi, je vous dis, Que quiconque aura delaiffé fa femme, fi ce n\'eft pour caufe de paillardife, il la fait devenir adultere: &amp; quiconque fe mariera à la femme delaiffée, il commet adultere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 33, 'Derechef vous avez entendu qu\'il a efté dit aux anciens, Tu ne te parjureras point, mais tu rendras au Seigneur ce que tu auras promis par jurement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 34, 'Mais moi, je vous dis, Ne jurez aucunement, ni par le ciel, car c\'eft le trône de Dieu:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 35, 'Ni par la terre, car c\'eft le marchepied de fes pieds: ni par Jerufalem, car c\'eft la ville du grand Roi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 36, 'Tu ne jureras point non plus par ta tefte: car tu ne peux faire un cheveu blanc ou noir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 37, 'Mais voftre parole foit, Oui, oui: Non, non: &amp; ce qui eft par deffus, eft du malin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 38, 'Vous avez entendu qu\'il a efté dit, OEil pour oeil, &amp; dent pour dent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 39, 'Mais moi, je vous dit, Ne refiftez point au mefchant: mais fi quelqu\'un te frappe en ta jouë droite, tourne lui auffi l\'autre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 40, 'Et fi quelqu\'un veut plaider contre toi, &amp; t\'ofter ton faye, laiffe-lui encore le manteau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 41, 'Et fi quelqu\'un te veut contraindre d\'aller avec lui une lieuë, vas en deux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 42, 'Donne à celui qui te demande: &amp; ne te deftourne point de celui qui veut emprunter de toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 43, 'Vous avez entendu qu\'il a efté dit, Tu aimeras ton prochain, &amp; haïras ton ennemi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 44, 'Mais moi, je vous dis, Aimez vos ennemis, beniffez ceux qui vous maudiffent, faites du bien à ceux qui vous haïffent: &amp; priez pour ceux qui vous courent fus, &amp; qui vous prefecutent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 45, 'Afin que vous foyez enfans de voftre pere qui eft és cieux: car il fait lever fon foleil fur les méchans &amp; fur les bons, &amp; envoye fa pluye fur les juftes &amp; fur les injuftes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 46, 'Car fi vous aimez ceux qui vous aiment, quel falaire en aurez-vous? Les peagers mefmes ne font-ils pas le mefme?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 47, 'Et fi vous faites accüeil feulement à vos freres, que faites vous plus que les autres? Les peagers mefmes ne font-ils pas auffi le femblable?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 48, 'Soyez donc parfaits, comme voftre pere qui eft és cieux eft parfait.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 1, 'Prenez garde que vous ne faciez voftre aumofne devant les hommes, pour eftre regardez d\'eux: autrement vous n\'aurez point de falaire envers voftre Pere qui eft és cieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 2, 'Quand donc tu feras ton aumofne, ne fai point fonner la trompette devant toi, ainfi que font les hypocrites és fynagogues, &amp; és ruës, afin qu\'ils en foyent honorez des hommes: En verité je vous dis, qu\'ils reçoivent leur falaire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 3, 'Mais quand tu fais ton aumofne, que ta main gauche ne fçache point ce que fait ta droite:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 4, 'Afin que ton aumofne foit en fecret: &amp; ton pere qui te voit en fecret, te le rendra à découvert.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 5, 'Et quand tu prieras, ne fois point comme les hypocrites, car ils aiment à prier en fe tenant debout és fynagogues, &amp; és coins des ruës, afin qu\'ils foyent veus des hommes: En verité je vous dis, qu\'ils reçoivent leur falaire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 6, 'Mais toi quand tu pries, entre en ton cabinet, &amp; ayant fermé ta porte, prie ton Pere qui eft en fecret: &amp; ton Pere qui te voit en fecret, te le rendras à découvert.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 7, 'Or quand vous priez, n\'ufez point de vaines redites comme les Payens: car ils penfent eftre exaucez par leur long parler.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 8, 'Ne leur reffemblez don point: car voftre Pere fçait dequoi vous avez befoin, devant que vous le lui demandiez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 9, 'Vous donc priez ainfi, Noftre Pere qui es és cieux, ton Nom foit fanctifié.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 10, 'Ton regne vienne. Ta volonté foit faite en la terre comme au ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 11, 'Donne-nous aujourd\'hui noftre pain quotidien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 12, 'Et nous quitte nos debtes, comme auffi nous quittons à nos detteurs, les leurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 13, 'Et ne nous indui point en tentation, mais delivre nous du malin. Car à toi eft le regne, &amp; la puiffance, &amp; la gloire à jamais. Amen.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 14, 'Car fi vous quittez aux hommes leurs offenfes, voftre Pere celefte vous quittera auffi les voftres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 15, 'Mais fi vous ne quittez point aux hommes leurs offenfes, voftre Pere ne vous quittera point auffi vos offenfes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 16, 'Et quand vous jeufnerez, ne devenez point d\'un regard trifte, comme les hypocrites: car ils fe rendent tous défaits de vifage, afin qu\'il apparoiffe aux hommes qu\'ils jeufnent: En verité je vous dis, qu\'ils reçoivent leur falaire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 17, 'Mais toi, quand tu jeufnes, oin ta tefte, &amp; lave ton vifage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 18, 'Afin qu\'il n\'apparoiffe point aux hommes que tu jeufnes, mais à ton Pere qui eft en fecret: &amp; ton Pere qui te voit en fecret, te le rendra à découvert.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 19, 'Ne vous amaffez point des treforts en la terre, où la tigne &amp; la roüille gaftent tout, &amp; où les larrons percent &amp; dérobent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 20, 'Mais amaffez-vous des trefors au ciel, où, ni la tigne ni la roüille ne gaftent rien, &amp; où les larrons ne percent ni ne dérobent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 21, 'Car là où eft voftre trefor, là auffi fera voftre coeur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 22, 'L\'oeil eft la lumiere du corps: fi donc ton oeil eft fimple, tout ton corps fera éclairé:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 23, 'Mais fi ton oeil eft mauvais, tout ton corps fera tenebreux. Si donc la lumiere qui eft en toi eft tenebres, combien grandes feront ces tenebres-là?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 24, 'Nul ne peut fervir à deux maiftres: car ou il haïra l\'un, &amp; aimera l\'autre: ou il s\'attachera à l\'un, &amp; méprifera l\'autre. Vous ne pouvez fervir à Dieu, &amp; à Mammon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 25, 'Partant je vous dis, Ne foyez point en fouci pour voftre vie, ce que vous mangerez, &amp; ce que vous boirez: ni pour voftre corps, dequoi vous ferez veftus. La vie n\'eft-elle pas plus que la nourriture, &amp; le corps plus que le veftement?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 26, 'Regardez aux oifeaux du ciel: car ils ne fement, ni ne moiffonnent, ni n\'affemblent en des greniers: &amp; voftre Pere celefte les nourrit. N\'eftes-vous pas beaucoup plus excellens qu\'eux?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 27, 'Et qui eft celui d\'entre vous, qui par fon fouci puiffe adjoufter une coudée à fa ftature?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 28, 'Et pourquoi eftes-vous en fouci du veftement? Apprenez bien comme croiffent les lis des champs: ils ne travaillent, ni ne filent:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 29, 'Neantmoins, je vous dis, que Salomon mefmes en toute fa gloire, n\'a point efté veftu comme l\'un d\'eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 30, 'Si Dieu donc reveft ainfi l\'herbe des champs, qui eft aujourd\'hui, &amp; demain eft mife au four, ne vous reveftira-t\'il pas beaucoup pluftoft, ô gens de petite foi?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 31, 'Ne foyez donc point en fouci, difans, Que mangerons-nous? ou que boirons-nous? ou dequoi ferons-nous veftus?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 32, '(Veu que les Payens recherchent toutes ces chofes) car voftre Pere celefte connoift que vous avez befoin de toutes ces chofes-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 33, 'Mais cherchez premierement le Royaume de Dieu, &amp; fa juftice, &amp; toutes ces chofes vous feront baillées par deffus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 34, 'Ne foyez donc point en fouci du lendemain: car le lendemain fe fouciera de ce qui le concerne. A chaque jour fuffit fon affliction.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 1, 'Ne jugez point, afin que vous ne foyez point jugez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 2, 'Car de tel jugement que vous jugerez, vous ferez jugez: &amp; de telle mefure que vous mefurerez, on vous mefurera reciproquement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 3, 'Et pourquoi regardes-tu le feftu qui eft en l\'oeil de ton frere, &amp; tu ne prens pas garde au chevron qui eft en ton oeil?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 4, 'Ou comment dis-tu à ton frere, Permets que j\'ofte le feftu de ton oeil: &amp; voici un chevron en ton oeil?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 5, 'Hypocrite, ofte premierement le chevron de ton oeil, &amp; alors tu adviferas à tirer le feftu de l\'oeil de ton frere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 6, 'Ne donnez point les chofes faintes aux chiens, &amp; ne jettez point vos perles devant les pourceaux: de peur qu\'ils les foulent à leurs pieds, &amp; que fe retournans ils vous defchirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 7, 'Demandez, &amp; il vous fera donné: cherchez, &amp; vous trouverez: heurtez, &amp; il vous fera ouvert.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 8, 'Car quiconque demande, il reçoit: &amp; quiconque cherche, il trouve: &amp; il fera ouvert à celui qui heurte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 9, 'Et qui eft l\'homme d\'entre vous, qui donne une pierre à fon fils, s\'il lui demande du pain?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 10, 'Et s\'il lui demande du poiffon, lui donnera-t\'il un ferpent?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 11, 'Si donc vous, qui eftes mauvais, fçavez bien donner à vos enfans des chofes bonnes: combien plus voftre Pere qui eft és cieux, donnera-til des biens à ceux qui les lui demandent?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 12, 'Toutes les chofes donc que vous voulez que les hommes vous faffent, faites les leur auffi femblablement: car c\'eft là la Loi &amp; les Prophetes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 13, 'Entrez par la porte eftroite: car c\'est la porte large, &amp; le chemin fpacieux qui mene à la perdition, &amp; il y en a beaucoup qui entrent par elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 14, 'Car la porte eft eftroite, &amp; le chemin eftroit qui mene à la vie, &amp; il y en a peu qui le trouvent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 15, 'Or donnez-vous garde des faux Prophetes, qui viennent à vous en habit de brebis, mais au dedans font des loups raviffans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 16, 'Vous les connoiftrez à leurs fruits: cueille-t\'on les raifins des efpines, ou les figues des chardons?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 17, 'Ainfi tout bon arbre fait de bons fruits, mais le mauvais arbre fait de mauvais fruits.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 18, 'Le bon arbre ne peut faire de mauvais fruits, ni le mauvais arbre faire de bons fruits.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 19, 'Tout arbre qui ne fait point de bon fruit, eft coupé &amp; jetté au feu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 20, 'Vous les connoiftrez donc à leurs fruits.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 21, 'Tous ceux qui me difent, Seigneur, Seigneur, n\'entreront pas au Royaume des cieux: mais celui qui fait la volonté de mon Pere qui eft és cieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 22, 'Plufieurs me diront en ce jour-là, Seigneur, Seigneur, n\'avons-nous pas prophetizé en ton Nom? &amp; n\'avons-nous pas jetté hors les diables en ton Nom? &amp; n\'avons-nous pas fait plufieurs vertus en ton Nom?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 23, 'Et alors je leur declarerai tout ouvertement, Je ne vous ai jamais connus: departez-vous de moi, vous qui faites le meftier d\'iniquité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 24, 'Quiconque donc oit ces paroles que je dis, &amp; les met en effet, je l\'accomparerai à l\'homme prudent qui a bafti fa maifon fur le roc.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 25, 'Et quand la pluye eft tombée, &amp; les torrens font venus, &amp; les vents ont foufflé, &amp; ont choqué cette maifon-là, elle n\'eft point tombée: car elle eftoit fondée fur le roc.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 26, 'Mais quiconque oit ces paroles que je dis, &amp; ne les met point en effet, fera accomparé à l\'homme fol, qui a bafti fa maifon fur le fable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 27, 'Et quand la pluye eft tombée, &amp; les torrens font venus, &amp; les vents ont foufflé, &amp; ont choqué cette maifon-là, elle eft tombée, &amp; fa ruïne a efté grande.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 28, 'Et il advint que quand Jefus eut achevé ces propos, les troupes furent eftonnées de fa doctrine.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 29, 'Car il les enfeignoit comme ayant authorité, &amp; non pas comme les Scribes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 1, 'Et quand il fut defcendu de la montagne, de grandes troupes le fuivirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 2, 'Et voici, un lepreux vint &amp; fe profterna devant lui, difant, Seigneur, fi tu veux, tu me peux nettoyer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 3, 'Et Jefus eftendant fa main, le toucha, difant, Je le veux, fois nettoyé: &amp; incontinent fa lepre fut nettoyée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 4, 'Puis Jefus lui dit, Garde toi de le dire à perfonne: mais va-t\'en, &amp; te monftre au Sacrificateur, &amp; offre le don que Moyfe a ordonné pour leur eftre en tefmoignage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 5, 'Et quand Jefus fut entré en Capernaum, un Centenier vint à lui, le priant:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 6, 'Et difant, Seigneur, mon garçon gift paralytique en la maifon, grievement tourmenté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 7, 'Jefus lui dit, Je m\'y en irai, &amp; le guerirai.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 8, 'Et le Centenier refpondant dit, Seigneur, je ne fuis pas digne que tu entres fous mon toict: mais feulement di la parole, &amp; mon garçon fera gueri.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 9, 'Car je fuis auffi homme conftitué fous la puiffance d\'autrui, &amp; qui ai fous moi des gens de guerre: &amp; je dis à l\'un, Va, &amp; il va: &amp; à l\'autre, Vien, &amp; il vient: &amp; à mon ferviteur, Fai cela, &amp; il le fait.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 10, 'Ce que Jefus ayant ouï, il s\'eftonna, &amp; dit à ceux qui le fuivoyent, En verité, je vous dis, que mefmes en Ifraël je n\'ai point trouvé une fi grande foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 11, 'Mais je vous dis que plufieurs viendront d\'Orient &amp; d\'Occident, &amp; feront à table au royaume des cieux, avec Abraham, Ifaac &amp; Jacob.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 12, 'Et les enfans du royaume feront jettez és tenebres de dehors: là il y aura pleur &amp; grincement de dents.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 13, 'Alors Jefus dit au Centenier, Va, &amp; qu\'il te foit fait ainfi que tu as creu. Et en ce mefme inftant fon garçon fut gueri.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 14, 'Puis Jefus eftant venu en la maifon de Pierre, vid la belle mere d\'icelui gifante au lict, &amp; yant la fievre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 15, 'Et il toucha fa main, &amp; la fievre la laiffa: puis elle fe leva, &amp; les fervit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 16, 'Et le foir eftant venu, on lui prefenta plufieurs demoniaques, defquels il jetta hors les efprits malins par fa parole: &amp; guerit tous ceux qui fe portoyent mal:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 17, 'Afin que fuft accompli ce dont il avoit efté parlé par Efaïe le Prophete, difant, Il a pris nos langueurs &amp; a porté nos maladies.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 18, 'Or Jefus voyant de grandes troupes à l\'entour de foi, commanda qu\'on paffaft à l\'autre rivage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 19, 'Alors un Scribe s\'eftant approché, lui dit, Maiftre, je te fuivrai par tout où tu iras.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 20, 'Et Jefus lui dit, Les renards ont des tanieres, &amp; les oifeaux du ciel ont des nids: mais le Fils de l\'homme n\'a point où il puiffe repofer fa tefte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 21, 'Puis un autre de fes difciples lui dit, Seigneur, permets moi d\'aller premierement enfevelir mon pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 22, 'Et Jefus lui dit, Sui moi, &amp; laiffe les morts enfevelir leurs morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 23, 'Et quand il fut entré en la naffelle, fes difciples le fuivirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 24, 'Et voici, une grande tourmente advint en la mer, tellement que la naffelle eftoit couverte de vagues: &amp; il dormoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 25, 'Et fes difciples vinrent, &amp; l\'efveillerent, difans, Seigneur, fauve nous, nous periffons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 26, 'Et il leur dit, Pourquoi avez-vous peur, gens de petite foi? Alors s\'eftant levé il tanfa les vents &amp; la mer: &amp; il fe fit un grand calme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 27, 'Dont ces gens-là s\'eftonnerent, difans, Qui eft celui-ci, que les vents mefmes &amp; la mer lui obeïffent?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 28, 'Et quand il fut paffé outre, en la contrée des Gergefeniens, deux demoniaques, eftans fortis des fepulchres, lui vinrent au devant, lefquels eftoyent fort fafcheux: tellement que nul ne pouvoit paffer par ce chemin-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 29, 'Et voici ils s\'efcrierent, difans, Qu\'y a-t\'il entre nous &amp; toi, Jefus, fils de Dieu? es-tu venu ici nous tourmenter devant le temps?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 30, 'Or il y avoit loin d\'eux un grand troupeau de pourceaux qui paiffoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 31, 'Et les diables le prioyent, difans, Si tu nous jettes hors, permets nous de nous en aller en ce troupeau de pourceaux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 32, 'Et il leur dit, Allez. Eux donc fortans, s\'en allerent au troupeau de pourceaux: &amp; voila tout ce troupeau de pourceaux fe precipita en la mer, &amp; mourut és eaux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 33, 'Alors les porchers s\'enfuïrent: &amp; eftans venus en la ville, ils raconterent toutes ces chofes, &amp; ce qui eftoit advenu aux demoniaques.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 34, 'Et voici, toute la ville alla au devant de Jefus: &amp; le voyans, ils le prierent de fe retirer de leurs quartiers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 1, 'Alors eftant entré en la naffelle, il repaffa, &amp; vint en fa ville.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 2, 'Et voici, on lui prefenta un paralytique gifant en un lict. Et Jefus voyant leur foi, dit au paralytique, Aye bon courage, mon fils, tes pechez te font pardonnez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 3, 'Et voici quelques-uns des Scribes difoyent en eux-mefmes, Celui-ci blafpheme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 4, 'Et Jefus voyant leurs penfées, leur dit, Pourquoi penfez-vous du mal en vos coeurs?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 5, 'Car lequel eft le plus aifé de dire, Tes pechez te font pardonnez: ou de dire, Leve-toi &amp; chemine?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 6, 'Or afin que vous fçachiez que le Fils de l\'homme a pouvoir en la terre de pardonner les pechez, Leve-toi, (dit-il au paralytique) pren ton lict, &amp; t\'en va en ta maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 7, 'Et il fe leva, &amp; s\'en alla en fa maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 8, 'Ce que les troupes voyans, elles s\'eftonnerent, &amp; glorifierent Dieu qui avoit donné un tel pouvoir aux hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 9, 'Puis Jefus paffant outre, vid un homme affis au lieu du peage, nommé Matthieu: &amp; lui dit, Sui moi. Et fe levant il le fuivit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 10, 'Et comme Jefus eftoit à table en la maifon d\'icelui, voici que plufieurs peagers, &amp; mal vivans, qui eftoyent là venus, fe mirent à table avec Jefus &amp; fes difciples.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 11, 'Ce que les Pharifiens voyans, ils dirent à fes difciples, Pourquoi voftre Maiftre mange-t\'il avec les peagers, &amp; les gens de mauvaife vie?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 12, 'Et Jefus ayant entendu cela, leur dit, Ceux qui font en fanté n\'ont point befoin de medecin, mais ceux qui fe portent mal.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 13, 'Mais allez, &amp; apprenez ce que c\'eft, Je veux mifericorde, &amp; non point facrifice. Car je ne fuis point venu appeller les juftes, mais les pecheurs à repentance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 14, 'Alors les difciples de Jean vinrent à lui, difans, Pourquoi nous &amp; les Pharifiens jeufnons-nous fouvent, &amp; tes difciples ne jeufnent point?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 15, 'Et Jefus leur refpondit, Les gens de la chambre du nouveau marié, peuvent-ils mener deüil, pendant que le nouveau marié eft avec eux? Mais les jours viendront que le nouveau marié leur fera ofté, &amp; alors ils jeufneront.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 16, 'Auffi perfonne ne met une piece de drap neuf à un vieux veftement: car ce qui eft mis pour remplir, emporte du veftement, &amp; la rupture en eft plus grande.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 17, 'Pareillement on ne met pas le vin nouveau en de vieux vaiffeaux: autrement les vaiffeaux fe rompent, &amp; le vin s\'épand, &amp; les vaiffeaux fe perdent: mais on met le vin nouveau en des vaiffeaux neufs, &amp; l\'un &amp; l\'autre fe conferve.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 18, 'Et comme il leur difoit ces chofes, voici venir un Seigneur, lequel fe profterna devant lui, difant, Ma fille eft maintenant decedée, mais vien, &amp; mets ta main fur elle, &amp; elle vivra.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 19, 'Et Jefus s\'eftant levé le fuivit, avec fes difciples.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 20, 'Et voici, une femme travaillée d\'une perte de fang depuis douze ans, vint par derriere, &amp; toucha le bord de fon veftement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 21, 'Car elle difoit en foi-mefme, Si feulement je touche fon veftement, je ferai guerie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 22, 'Alors Jefus s\'eftant retourné, &amp; la regardant, dit, Aye bon courage, ma fille, ta foi t\'a fauvée. Et en ce mefme inftant la femme fut guerie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 23, 'Et quand Jefus fut venu en la maifon de ce Seigneur-là, &amp; eût veu les meneftriers, &amp; la troupe là affemblée qui menoit un grand bruit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 24, 'Il leur dit, Retirez-vous: car la jeune fille n\'eft pas morte, mais elle dort. Et ils fe moquoyent de lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 25, 'Et quand la troupe là affemblée eût efté mife dehors, il entra &amp; prit la main de la jeune fille, &amp; elle fe leva.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 26, 'Et le bruit en courut par tout ce quartier-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 27, 'Et comme Jefus paffoit outre, deux aveugles le fuivirent, crians, &amp; difans, Fils de David, aye pitié de nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 28, 'Puis quand il fut arrivé en la maifon, ces aveugles vinrent à lui, aufquels il dit, Croyez-vous que je puiffe faire cela? Ils lui refpondirent, Oui vrayement, Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 29, 'Alors il toucha leurs yeux difant, Qu\'il vous foit fait felon voftre foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 30, 'Et leurs yeux furent ouverts: &amp; Jefus leur defendit avec menaces, difant, Prenez garde que perfonne ne le fçache.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 31, 'Mais eux eftant partis, firent courir fa renommée par tout ce quartier-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 32, 'Et ainfi qu\'ils fortoyent, voici, on lui prefenta un homme muët, demoniaque.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 33, 'Et quand le diable eut efté jetté hors, le muët parla: dont les troupes s\'eftonnerent, difans, Rien de femblable ne fut jamais veu en Ifraël.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 34, 'Mais les Pharifiens difoyent, Il jette hors les diables par le prince des diables.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 35, 'Et Jefus tournoyoit par toutes les villes &amp; les bourgades enfeignant en leurs fynagogues, &amp; prefchant l\'Evangile du royaume, &amp; gueriffant toute forte de maladie, &amp; toute forte de langueur parmi le peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 36, 'Et voyant les troupes, il fut émeu de compaffion envers elles, &amp; de ce qu\'elles eftoyent éparfes &amp; errantes, comme des brebis qui n\'ont point de pafteur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 37, 'Alors il dit à fes difciples, Certes la moiffon eft grande, mais il y a peu d\'ouvriers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 38, 'Priez donc le Seigneur de la moiffon, qu\'il pouffe des ouvriers en fa moiffon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 1, 'Alors ayant appellé à foi fes douze difciples, il leur donna pouvoir fur les efprits immondes, pour les jetter hors, &amp; pour guerir toute forte de maladie, &amp; toute forte de langueur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 2, 'Or ce font ici les noms des douze Apoftres: le premier eft Simon, nommé Pierre, &amp; André fon frere: Jaques fils de Zebedée, &amp; Jean fon frere:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 3, 'Philippe &amp; Barthelemi: Thomas, &amp; Matthieu le peager: Jaques fils d\'Alphée, &amp; Lebbée furnommé Thadée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 4, 'Simon Cananeen, &amp; Judas Ifcariot, qui mefme le trahit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 5, 'Jefus envoya ces douze-là, &amp; leur commanda, difant, N\'allez point vers les Gentils, &amp; n\'entrez en aucune ville des Samaritains:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 6, 'Mais pluftoft allez aux brebis de la maifon d\'Israël qui font peries.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 7, 'Et quand vous ferez partis, prefchez, difans, Le royaume des cieux eft approché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 8, 'Gueriffez les malades, nettoyez les lepreux, reffufcitez les morts, jettez hors les diables: vous l\'avez receu pour neant, donnez le pour neant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 9, 'Ne faites point provifion d\'or ni d\'argent, ni de monnoye en vos ceintures:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 10, 'Ni de malette pour le chemin, ni de deux robbes, ni de fouliers, ni de bafton: car l\'ouvrier eft digne de fa nourriture.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 11, 'Et en quelque ville ou bourgade que vous entrerez, enquerez-vous qui y eft digne: &amp; demeurez chez lui jufques à ce que vous partiez de là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 12, 'Et quand vous entrerez en quelque maifon, falüez-la.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 13, 'Que fi lamaifon en eft digne, que voftre paix vienne fur elle: mais fi elle n\'en est pas digne, que voftre paix retourne à vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 14, 'Et quiconque ne vous recevra, &amp; n\'écoutera vos paroles, partans de la maifon, ou de la ville, fecoüez la poudre de vos pieds.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 15, 'En verité je vous dis, que ceux du païs de Sodome &amp; de Gomorre feront traittez plus tolerablement au jour du jugement, que cette ville-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 16, 'Voici je vous envoye comme des brebis au milieu des loups: foyez donc prudens comme ferpens, &amp; fimples comme colombes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 17, 'Et donnez-vous garde des hommes: car ils vous livreront aux confiftoires, &amp; vous foüetteront en leurs fynagogues.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 18, 'Et vous ferez menez devant les Gouverneurs, &amp; mefmes devant les Rois, à caufe de moi, en tefmoignage à eux &amp; aux nations.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 19, 'Mais quand ils vous livreront, ne foyez point en fouci, quoi, ou comment vous parlerez: car en ce mefme inftant il vous fera donné ce que vous aurez à dire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 20, 'Car ce n\'eft pas vous qui parlez, mais c\'eft l\'Efprit de voftre Pere qui parle en vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 21, 'Or le frere livrera fon frere à la mort, &amp; le pere l\'enfant: &amp; les enfans s\'éleveront contre leurs peres &amp; meres, &amp; les feront mettre à mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 22, 'Et vous ferez haïs de tous, à caufe de mon Nom: mais qui perfeverera jufques à la fin, celui-là fera fauvé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 23, 'Or quand il vous perfecuteront en cette ville-là, fuyez-vous-en en une autre: car en verité, je vous dis, que vous n\'aurez point achevé d\'aller par toutes les villes d\'Ifraël, que le Fils de l\'homme ne foit venu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 24, 'Le difciple n\'eft point par deffus le maiftre, ni le ferviteur par deffus fon feigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 25, 'Il fuffit au difciple, qu\'il foit comme fon maiftre: &amp; que le ferviteur foit comme fon feigneur. S\'ils ont appellé le pere de famille Beelzebul, combien plus fes domeftiques?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 26, 'Ne les craignez donc point: car rien n\'eft couvert qui ne fe connoiffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 27, 'Ce que je vous dis en tenebres, dites-le en lumiere: &amp; ce que vous oyez en l\'oreille, prefchez-le fur les maifons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 28, 'Et ne craignez point ceux qui tuënt le corps, &amp; ne peuvent tuër l\'ame: mais craignez pluftoft celui qui peut deftruire &amp; l\'ame &amp; le corps en la gehenne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 29, 'Deux paffereaux ne fe vendent-ils pas une pite? neantmoins l\'un d\'eux ne tombera point en terre fans voftre Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 30, 'Et mefmes les cheveux de voftre tefte font tous contez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 31, 'Ne craignez donc point, vous valez mieux que beaucoup de paffereaux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 32, 'Quiconque donc me confeffera devant les hommes, je le confefferai devant mon Pere qui eft és cieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 33, 'Mais quiconque me reniera devant les hommes, je le renierai auffi devant mon Pere qui eft és cieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 34, 'Ne penfez point que je fois venu mettre la paix en la terre: je n\'y fuis point venu mettre la paix, mais l\'efpée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 35, 'Car je fuis venu mettre en diffenfion l\'homme contre fon pere, &amp; la fille contre fa mere, &amp; la belle-fille contre fa belle-mere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 36, 'Et les propres domeftiques de l\'homme lui feront ennemis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 37, 'Qui aime pere ou mere plus que moi, n\'eft pas digne de moi: &amp; qui aime fils ou fille plus que moi, n\'eft pas digne de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 38, 'Et qui ne prend fa croix, &amp; ne vient apres moi, n\'eft pas digne de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 39, 'Qui aura trouvé fa vie, la perdra: &amp; qui aura perdu fa vie pour l\'amour de moi, la trouvera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 40, 'Qui vous reçoit, il me reçoit: &amp; qui me reçoit, il reçoit celui qui m\'a envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 41, 'Qui reçoit un Prophete, au nom de Prophete, il recevra falaire de Prophete: &amp; qui reçoit un jufte au nom de jufte, il recevra falaire de jufte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 42, 'Et quiconque aura donné à boire un verre d\'eau froide feulement à l\'un de ces petits, au nom de difciple, je vous dis en verité qu\'il ne perdra point fon falaire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 1, 'Et il advint que quand Jefus eut achevé de donner ces mandemens à fes douze difciples, il partit de là pour aller enfeigner &amp; prefcher en leurs villes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 2, 'Or Jean ayant ouï en la prifon les faits de Chrift, envoya deux de fes difciples lui dire,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 3, 'Es-tu celui qui devoit venir, ou fi nous devons en attendre un autre?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 4, 'Et jefus refpondant, leur dit, Allez &amp; rapportez à Jean les chofes que vous oyez &amp; que vous voyez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 5, 'Les aveugles recouvrent la veuë, les boiteux cheminent, les lepreux font nettoyez, &amp; les fourds oyent, les morts font reffufcitez, &amp; l\'Evangile eft annoncé aux pauvres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 6, 'Et bien-heureux eft celui qui ne fera point fcandalifé en moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 7, 'Et comme ils s\'en alloyent, Jefus commença à dire de Jean aux troupes, Qu\'eftes-vous allez voir au defert? un rofeau agité du vent?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 8, 'Mais qu\'eftes-vous allez voir? un homme veftu de precieux ornemens? voici, ceux qui portent des habits precieux, font és maifons des Rois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 9, 'Mais qu\'eftes-vous allez voir? un Prophete? voire, je vous dis, &amp; plus que Prophete.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 10, 'Car c\'eft celui-ci duquel il eft écrit, Voici, j\'envoye mon meffager devant ta face, qui appreftera ton chemin devant toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 11, 'En verité, je vous dis, qu\'entre ceux qui font nez de femmes, il n\'en a efté fufcité aucun plus grand que Jean Baptifte: toutefois celui qui eft le moindre au royaume des cieux, eft plus grand que lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 12, 'Or depuis les jours de Jean Baptifte jufques à maintenant, le royaume des cieux eft forcé, &amp; les violents le raviffent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 13, 'Car tous les Prophetes &amp; la Loi ont prophetizé jufques à Jean.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 14, 'Et fi vous voulez recevoir mon dire, c\'eft l\'Elie qui devoit venir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 15, 'Qui a des oreilles pour ouïr, qu\'il oye.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 16, 'Mais à qui accomparerai-je cette generation? elle eft femblable aux petits enfans qui font affis és marchez, &amp; qui crient à leurs compagnons,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 17, 'Et leur difent, Nous vous avons fluté, &amp; vous n\'avez point danfé: nous vous avons chanté complaintes, &amp; vous n\'avez point lamenté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 18, 'Car Jean eft venu ne mangeant ni ne beuvant: &amp; ils difent, Il a le diable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 19, 'Le Fils de l\'homme eft venu mangeant &amp; beuvant: &amp; ils difent, Voilà un mangeur &amp; un beuveur, un ami des peagers &amp; des mal-vivans. Mais la fapience a efté juftifiée par fes enfans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 20, 'Alors il fe prit à reprocher aux villes efquelles avoyent efté faites plufieurs de fes vertus, qu\'elles ne s\'eftoyent point amendées, difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 21, 'Mal-heur fur toi, Corazin: Mal-heur fur toi, Bethfaïda: car fi les vertus qui ont efté faites au milieu de vous, euffent efté faites à Tyr &amp; à Sidon, elles fe fuffent dés longtemps repenties avec le fac &amp; la cendre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 22, 'Partant je vous dis, que Tyr &amp; Sidon feront plus tolerablement traitées au jour du jugement que vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 23, 'Et toi Capernaum, qui as efté élevée jufques au ciel, tu feras abbaiffée jufques en enfer: car fi les vertus qui ont efté faites au milieu de toi, euffent efté faites à Sodome, elle fuft demeurée jufques à ce jourd\'hui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 24, 'Partant je vous dis, que ceux de Sodome feront plus tolerablement traittez au jour du jugement que toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 25, 'En ce temps-là jefus prenant la parole, dit, Je te rends graces, ô Pere, Seigneur du ciel &amp; de la terre, de ce que tu as caché ces chofes aux fages &amp; aux entendus, &amp; les as relevées aux petits enfans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 26, 'Il eft ainfi, Pere, parce que tel a efté ton bon-plaifir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 27, 'Toutes chofes m\'ont efté données en main de mon Pere: &amp; nul ne connoit le Fils finon le Pere: &amp; nul auffi ne connoit le Pere finon le Fils, &amp; celui à qui le Fils l\'aura voulu reveler.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 28, 'Venez à moi vous tous qui eftes travaillez &amp; chargez, &amp; je vous foulagerai.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 29, 'Chargez mon joug fur vous, &amp; apprenez de moi, que je fuis debonnaire &amp; humble de coeur: &amp; vous trouverez repos à vos ames.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 30, 'Car mon joug eft aifé, &amp; mon fardeau eft leger.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 1, 'En ce temps-là Jefus alloit par des blez un jour de Sabbat: &amp; fes difciples ayans faim commencerent à arracher des efpics, &amp; à les manger.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 2, 'Les Pharifiens voyans cela, lui dirent, Voila, tes difciples font ce qu\'il n\'eft pas permis de faire au Sabbat.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 3, 'Mais il leur dit, N\'avez-vous point leu ce que fit David quand il eut faim, &amp; ceux qui eftoyent avec lui?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 4, 'Comme il entra en la maifon de Dieu, &amp; mangea les pains de propofition, lefquels il ne lui eftoit pas permis de manger, ni à ceux qui eftoyent avec lui, mais aux Sacrificateurs feulement?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 5, 'Ou, n\'avez-vous point leu en la Loi qu\'au temple és jours des Sabbats, les Sacrificateurs violent le Sabbat, &amp; n\'en font point coupables.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 6, 'Or je vous dis, qu\'il y\'a ici quelqu\'un plus grand que le temple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 7, 'Que fi vous fçaviez ce que c\'eft, Je veux mifericorde, &amp; non point facrifice, vous n\'auriez pas condamné ceux qui ne font point coupables.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 8, 'Car le Fils de l\'homme eft feigneur mefme du Sabbat.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 9, 'Puis eftant parti de là, il vint en leur Synagogue.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 10, 'Et voici, il y avoit là un homme ayant la main feche, &amp; ils l\'interrogerent difant, Eft-il permis de guerir és Sabbats? afin de l\'accufer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 11, 'Et il leur dit, Qui fera celui d\'entre vous, s\'il a une brebis &amp; qu\'elle vienne à tomber au jour du Sabbat en une foffe, qui ne l\'empoigne &amp; ne la releve?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 12, 'Et combien l\'homme vaut mieux qu\'une brebis? Il eft donc permis de bien faire és Sabbats.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 13, 'Alors il dit au perfonnage, Eften ta main. Et il l\'eftendit: &amp; elle fut renduë faine comme l\'autre');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 14, 'Or les Pharifiens eftans fortis, prirent confeil contre lui pour le faire perir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 15, 'Mais Jefus connoiffant cela, partit de là: &amp; de grandes troupes le fuivirent, &amp; il les guerit tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 16, 'Et leur defendit avec menaces de ne le point donner à connoiftre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 17, 'Afin que fuft accompli ce dont il avoit efté parlé par Efaïe le Prophete, difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 18, 'Voici mon ferviteur que j\'ai éleu, mon bien-aimé, auquel mon ame prend fon bon-plaifir: je mettrai mon Efprit fur lui, &amp; il annoncera jugement aux Nations.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 19, 'Il ne debattra point, &amp; ne criera point, &amp; perfonne n\'orra fa voix par les ruës.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 20, 'Il ne brifera point le rofeau caffé, &amp; n\'efteindra point le lumignon qui fume, jufques à ce qu\'il face venir en avant le jugement en victoire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 21, 'Et les Nations efpereront en fon Nom.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 22, 'Alors lui fut prefenté un homme tourmenté du diable, aveugle, &amp; muët, lequel il guerit: tellement que celui qui avoit efté aveugle &amp; muët parloit &amp; voyoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 23, 'Dont toutes les troupes furent eftonnées, &amp; difoyent, Celui-ci n\'eft-il pas le fils de David?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 24, 'Mais les Pharifiens ayans entendu cela, difoyent, Celui-ci ne jette hors les diables, finon de par Beelzebul prince des diables.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 25, 'Mais Jefus connoiffant leurs penfées, leur dit, Tout royaume divifé contre foi-mefme, fera reduit en defert: &amp; toute ville ou maifon divifée contre foi-mefme, ne fubfiftera point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 26, 'Or fi Satan jette hors Satan, il eft divifé contre foi-mefme: comment donc fubfiftera fon royaume?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 27, 'Que fi je jette hors les diables par Beelzebul, vos fils par qui les jettent-ils hors? partant ils feront vos juges.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 28, 'Mais fi je jette hors les diables par l\'Efprit de Dieu, vrayement le royaume de Dieu eft parvenu à vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 29, 'Ou, comment pourra quelqu\'un entrer en la maifon d\'un homme fort, &amp; piller fon bien, fi premierement il n\'a lié l\'homme fort, &amp; alors il pillera fa maifon?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 30, 'Qui n\'eft point avec moi, il eft contre moi: &amp; qui n\'affemble point avec moi, il efpard.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 31, 'Partant je vous dis, Tout peché &amp; blafpheme contre l\'Efprit, ne leur fera point pardonné.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 32, 'Et fi quelqu\'un a parlé contre le Fils de l\'homme, il lui fera pardonné: mais qui aura parlé contre le S.Efprit, il ne lui fera pardonné, ni en ce fiecle-ici, ni en celui qui eft à venir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 33, 'Ou faites l\'arbre bon, &amp; fon fruict bon: ou faites l\'arbre mauvais, &amp; fon fruict mauvais: car l\'arbre eft connu par le fruict.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 34, 'Engeance de viperes: comment pourriez-vous parler bien, eftans mauvais? car de l\'abondance du coeur la bouche parle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 35, 'L\'homme de bien tire du bon trefor de fon coeur des chofes bonnes: &amp; l\'homme mauvais tire des chofes mauvaifes du mauvais trefor de fon coeur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 36, 'Or je vous dis que de toute parole oifeufe que les hommes auront dite, ils en rendront conte au jour du jugement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 37, 'Car tu feras juftifié par tes paroles, &amp; par tes paroles tu feras condamné.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 38, 'Alors quelques-uns des Scribes &amp; des Pharisiens lui refpondirent, difans, Maiftre, nous voudrions bien te voir faire quelque figne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 39, 'Mais lui refpondant, leur dit: La nation méchante &amp; adultere recherche un figne: mais il ne lui fera donné aucun figne, finon le figne de Jonas le Prophete.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 40, 'Car comme Jonas fut au ventre de la baleine trois jours &amp; trois nuits, ainfi le Fils de l\'homme fera au coeur de la terre trois jours &amp; trois nuits.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 41, 'Ceux de Ninive s\'éleveront au jugement contre cette nation, &amp; la condamneront: parce qu\'ils fe font amendez à la predication de Jonas: &amp; voici il y a ici plus que Jonas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 42, 'La reine de Midi s\'élevera au jugement contre cette nation, &amp; la condamnera: parce qu\'elle vint du bout de la terre pour ouïr la fapience de Salomon: &amp; voici il y a ici plus que Salomon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 43, 'Or quand l\'efprit immonde eft forti d\'un homme, il va par des lieux fecs, cherchant du repos, &amp; n\'en trouve point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 44, 'Alors il dit, Je retournerai en ma maifon, dont je fuis forti. Et y eftant venu, il la trouve vuide, baliée &amp; parée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 45, 'Alors il s\'en va, &amp; prend avec foi fept autres efprits pires que lui, lefquels y eftans entrez, habitent là: &amp; la fin de cet homme-là eft pire que le commencement: ainfi en adviendra-t\'il auffi à cette nation perverfe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 46, 'Et comme il parloit encore aux troupes, voici, fa mere &amp; fes freres eftoyent dehors, cherchans de parler à lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 47, 'Et quelqu\'un lui dit, Voilà, ta mere &amp; tes freres font là dehors, cherchans de parler à toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 48, 'Mais lui refpondant, dit à celui qui avoit dit cela, Qui eft ma mere, &amp; qui font mes freres?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 49, 'Et eftendant fa main fur fes difciples, il dit, Voici ma mere &amp; mes freres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 50, 'Car quiconque fera la volonté de mon Pere qui eft és cieux, celui-là eft mon frere, &amp; ma soeur, &amp; ma mere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 1, 'Ce mefme jour Jefus eftant parti de la maifon, s\'affit aupres de la mer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 2, 'Et de grandes troupes s\'affemblerent vers lui, tellement qu\'il monta en une naffelle, &amp; s\'affit, &amp; toute la multitude fe tenoit fur le rivage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 3, 'Alors il leur dit plufieurs chofes par fimilitudes, difant, Voici, un femeur eft forti pour femer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 4, 'Et comme il femoit, une partie de la femence cheut aupres du chemin, &amp; les oifeaux vinrent, &amp; la mangerent toute.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 5, 'Et l\'autre partie cheut en des lieux pierreux, où elle n\'avoit gueres de terre: &amp; auffi-toft elle fe leva, parce qu\'elle n\'entroit pas profondement en terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 6, 'Et le Soleil eftant levé, elle fut havie: &amp; parce qu\'elle n\'avoit point de racine, elle fe fecha.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 7, 'Et l\'autre partie cheut entre les efpines: &amp; les efpines monterent, &amp; l\'eftoufferent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 8, 'Et l\'autre partie cheut en bonne terre, &amp; rendit du fruit: un grain cent, l\'autre foixante, &amp; l\'autre trente.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 9, 'Qui a des oreilles pour ouïr, qu\'il oye.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 10, 'Alors les difciples s\'approchans, lui dirent, Pourquoi parles-tu à eux par fimilitudes?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 11, 'Il refpondit, &amp; leur dit, Parce qu\'il vous eft donné de connoift les fecrets du royaume des cieux, mais il ne leur eft point donné.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 12, 'Car à celui qui a, il lui fera donné, &amp; il en aura tant plus: mais à celui qui n\'a rien, mefme ce qu\'il a lui fera ofté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 13, 'Pour cette caufe je parle à eux par fimilitudes, parce qu\'en voyant, ils ne voyent point: &amp; en oyant, ils n\'oyent &amp; n\'entendent point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 14, 'Ainfi eft accomplie en eux la prophetie d\'Efaïe, laquelle dit, En oyant vous orrez, &amp; n\'entendrez point: &amp; en voyant vous verrez, &amp; n\'appercevrez point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 15, 'Car le coeur de ce peuple eft engraiffé, &amp; ils ont ouï dur de leurs oreilles, &amp; ont cligné de leurs yeux: afin qu\'ils n\'apperçoivent des yeux, &amp; n\'oyent des oreilles, &amp; n\'entendent du coeur, &amp; ne fe convertiffent, &amp; que je ne les gueriffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 16, 'Or vos yeux font bien-heureux, car ils apperçoivent: &amp; vos oreilles, car elles oyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 17, 'Car en verité je vous dis, que plufieurs Prophetes &amp; plufieurs juftes ont defiré de voir les chofes que vous voyez, &amp; ne les ont pas veuës, &amp; d\'ouïr les chofes que vous oyez, &amp; ne les ont pas ouïes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 18, 'Vous donc oyez la fimilitude du femeur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 19, 'Toute perfonne à qui il advient d\'ouïr la Parole du royaume, &amp; qui ne l\'entend point, le malin vient, &amp; ravit ce qui eft femé au coeur: c\'eft celui qui a receu la femence aupres du chemin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 20, 'Et celui qui a receu la femence en des lieux pierreux, c\'eft celui qui oit la Parole, &amp; incontinent la reçoit avec joye:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 21, 'Mais il n\'a point de racine en foi-mefme, pourtant n\'eft-il qu\'à temps: tellement qu\'oppreffion ou perfecution advenant pour la Parole, il eft incontinent fcandalizé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 22, 'Et celui qui a receu la femence entre les efpines, c\'eft celui qui oit la Parole de Dieu: mais le fouci de ce monde, &amp; la fallace des richeffes eftouffent la Parole, &amp; elle devient infructueuse.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 23, 'Mais celui qui a receu la femence en bonne terre, c\'eft celui qui oit la Parole, &amp; qui l\'entend: affavoir celui qui porte &amp; produit du fruit, l\'un cent, l\'autre foixante, &amp; l\'autre trente.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 24, 'Il leur propofa une autre fimilitude, difant, Le royaume des cieux reffemble à un homme qui a femé de la bonne femence en fon champ.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 25, 'Mais pendant que les hommes dormoyent, fon ennemi eft venu, qui a semé de l\'ivroye parmi le blé, puis s\'en eft allé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 26, 'Et apres que la femence fut venuë en herbe, &amp; eut produit du fruit, alors auffi apparut l\'ivroye.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 27, 'Alors les ferviteurs du pere de famille eftans venus vers lui, lui dirent, Seigneur, n\'as-tu pas femé de la bonne femence en ton champ? d\'où vient donc qu\'il y a de l\'ivroye?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 28, 'Mais il leur dit, C\'eft l\'ennemi qui a fait cela. Et les ferviteurs lui dirent, Veux-tu donc que nous y allions, &amp; cueillions l\'ivroye?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 29, 'Et il leur dit, Non: de peur qu\'il advienne qu\'en cueillant l\'ivroye, vous arrachiez le bled quand &amp; quand.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 30, 'Laiffez les croiftre tous deux enfemble, jufques à la moiffon: &amp; en la saifon de la moiffon, je dirai aux moiffonneurs, Cueillez premierement l\'ivroye, &amp; la liez en des faiffeaux pour la brufler: mais affemblez le bled en mon grenier.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 31, 'Il leur propofa une autre fimilitude, difant, Le royaume des cieux eft semblable au grain de femence de mouftarde, que quelqu\'un a pris &amp; femé en fon champ:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 32, 'Qui eft bien la plus petite de toutes les femences: mais quand il eft creu, il eft plus grand que les autres herbes, &amp; devient arbre: tellement que les oifeaux du ciel y viennent, &amp; font leurs nids en fes branches.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 33, 'Il leur dit une autre fimilitude, Le royaume des cieux eft femblable au levain qu\'une femme prend, &amp; met parmi trois mefures de farine, jufques à ce qu\'elle foit toute levée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 34, 'Jefus dit toutes ces chofes aux troupes en fimilitudes: &amp; ne parloit point à eux fans fimilitude:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 35, 'Afin que fuft accompli ce dont il avoit efté parlé par le Prophete, difant, J\'ouvrirai ma bouche en fimilitudes: Je dégorgerai les chofes qui ont été cachées dés la fondation du monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 36, 'Alors Jefus ayant laiffé les troupes, vint à la maifon, &amp; fes difciples vinrent à lui, difans, Declare-nous la fimilitude de l\'ivroye du champ.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 37, 'Lequel refpondant leur dit, Celui qui feme la bonne femence, c\'eft le Fils de l\'homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 38, 'Et le champ, c\'eft le monde. La bonne femence, ce font les enfans du royaume. L\'ivroye, ce font les enfans du malin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 39, 'Et l\'ennemi qui l\'a femée, c\'eft le diable. La moiffon, c\'eft la fin du monde. Et les moiffonneurs font les Anges.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 40, 'Tout ainfi donc qu\'on cueille l\'ivroye, &amp; qu\'on la brufle au feu, il en fera ainfi en la fin de ce monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 41, 'Le Fils de l\'homme envoyera fes Anges, qui cueilliront de fon royaume tous les fcandales, &amp; ceux qui font iniquité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 42, 'Et les jetteront en la fournaife de feu: là il y aura pleur &amp; grincement de dents.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 43, 'Alors reluiront les juftes comme le foleil au royaume de leur Pere. Qui a des oreilles pour ouïr, qu\'il oye.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 44, 'Derechef, le royaume des cieux eft femblable à un trefor caché en un champ, que quelqu\'un ayant trouvé, l\'a caché: Puis de joye qu\'il en a, il s\'en va, &amp; vend tout ce qu\'il a, &amp; achette ce champ-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 45, 'Derechef, le royaume des cieux eft femblable à un marchand qui cherche de bonnes perles:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 46, 'Lequel ayant trouvé quelque perle de grand prix, s\'en eft allé, &amp; a vendu tout ce qu\'il avoit, &amp; l\'a achetée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 47, 'Derechef, le royaume des cieux eft femblable à un filet jetté en la mer, &amp; amaffant de toute forte de chofes:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 48, 'Lequel eftant plein, les pefcheurs le tirent en haut fur le rivage, &amp; eftans affis, mettent le bon à part en leurs vaiffeaux, &amp; jettent hors ce qui ne vaut rien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 49, 'Il en fera ainfi en la fin du monde: les Anges viendront &amp; fepareront les mauvais du milieu des juftes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 50, 'Et les jetteront en la fournaife de feu: là il y aura pleur &amp; grincement de dents.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 51, 'Jefus leur dit, Avez-vous entendu toutes ces chofes? ils lui refpondirent, Oui, Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 52, 'Et il leur dit, Partant tout Scribe qui eft bien appris quant au royaume des cieux, eft femblable à un pere de famille qui tire de fon trefor des chofes nouvelles &amp; anciennes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 53, 'Et il avint que quand Jefus eût achevé ces fimilitudes, il fe retira de cette contrée-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 54, 'Et eftant venu en fon païs, il les enfeignoit en leur fynagogue: tellement qu\'ils eftoyent eftonnez, &amp; difoyent, D\'où vient à celui-ci cette fapience, &amp; ces vertus?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 55, 'Celui-ci n\'eft-il pas le fils du charpentier? fa mere n\'eft-elle pas appellée Marie? &amp; fes freres, Jaques, &amp; Jofes, &amp; Simon, &amp; Jude?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 56, 'Et fes foeurs ne font-elles pas toutes parmi nous? D\'où viennent donc à celui-ci toutes ces chofes?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 57, 'Tellement qu\'ils eftoyent fcandalizez en lui. Et Jefus leur dit, Un Prophete n\'eft fans honneur, finon en fon païs, &amp; en fa maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 58, 'Et il ne fit là gueres de vertus, à caufe de leur incredulité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 1, 'En ce temps-là Herode le Tetraque ouït la renommée de Jefus:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 2, 'Et dit à fes ferviteurs, C\'eft Jean Baptifte: il eft reffufcité des morts, &amp; pourtant les vertus montrent leur force en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 3, 'Car Herode avoit pris Jean, &amp; l\'avoit lié &amp; mis en prifon, à caufe d\'Herodias femme de Philippe fon frere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 4, 'Car Jean luy difoit, Il ne t\'eft pas permis de l\'avoir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 5, 'Et il eut bien voulu le mettre à mort, mais il craignoit le populaire; parce qu\'ils le tenoyent pour Prophete.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 6, 'Or au jour du feftin de la naiffance d\'Herode, la fille d\'Herodias danfa en pleine fale, &amp; pleut à Herode.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 7, 'Dont il lui promit avec ferment, de lui donner tout ce qu\'elle demanderoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 8, 'Elle donc eftant pouffée auparavant par fa mere, lui dit, Donne-moi ici en un plat la tefte de Jean Baptifte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 9, 'Et le Roi fut marri: mais à caufe des fermens, &amp; de ceux qui eftoyent à table avec lui, il commanda qu\'on la lui baillaft.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 10, 'Et ayant envoyé l\'executeur, il fit decapiter Jean en la prifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 11, 'Et fa tefte fut apportée en un plat, &amp; donnée à la fille: &amp; elle la prefenta à fa mere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 12, 'Puis les difciples d\'icelui vinrent, &amp; emporterent fon corps, &amp; l\'enfevelirent: &amp; vinrent l\'annoncer à Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 13, 'Et Jefus l\'ayant ouï, fe retira de-là en une naffelle, en un lieu defert à part. Et quand les troupes eurent entendu cela, elles le fuivirent à pied des villes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 14, 'Et Jefus fortant vid une grande multitude, &amp; fut émeu de compaffion envers eux, &amp; guerit les malades d\'entr\'eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 15, 'Or comme il fe faifoit tard, fes difciples vinrent à lui, difans, Ce lieu eft defert, &amp; l\'heure eft déjà paffée: donne congé à ces troupes, afin qu\'elles s\'en aillent aux bourgades, &amp; qu\'elles achettent des vivres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 16, 'Mais Jefus leur dit, Ils n\'ont pas befoin de s\'en aller: donnez leur vous-mefmes à manger.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 17, 'Et ils dirent, Nous n\'avons ici que cinq pains &amp; deux poiffons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 18, 'Et il leur dit, Apportez-les moi ici.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 19, 'Et apres avoir commandé que les troupes s\'arrangeaffent fur l\'herbe, il prit les cinq pains &amp; les deux poiffons, &amp; levant les yeux au ciel, il rendit graces. Et apres avoir rompu les pains, il les bailla aux difciples, &amp; les difciples aux troupes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 20, 'Dont ils mangerent tous, &amp; furent raffafiez. Puis ils recüeillirent du refte des pieces, douze corbeilles pleines.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 21, 'Or ceux qui en avoyent mangé, eftoyent environ cinq mille hommes, fans les femmes &amp; les petits enfans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 22, 'Incontinent apres Jefus contraignit fes difciples de monter en la naffelle, &amp; de paffer outre devant lui, pendant qu\'il donneroit congé aux troupes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 23, 'Et quand il leur eût donné congé, il monta en la montagne pour eftre à part, afin de prier. Et le foir eftant venu, il eftoit là feul.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 24, 'Or la naffelle eftoit déja au milieu de la mer, tourmentée des vagues: car le vent eftoit contraire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 25, 'Et fur la quatriéme veille de la nuit, Jefus vint vers eux, cheminant fur la mer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 26, 'Et fes difciples le voyant cheminer fur la mer, furent troublez, difans, C\'eft un fantofme: &amp; de la peur ils s\'écrierent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 27, 'Mais incontinent Jefus parla à eux, difant, Affeurez-vous: c\'eft moi, n\'ayez point de peur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 28, 'Et Pierre lui refpondant, dit, Seigneur, fi c\'eft toi, commande que j\'aille vers toi fur les eaux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 29, 'Il dit, Vien. Et Pierre eftant defcendu de la naffelle, chemina fur les eaux pour aller à Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 30, 'Mais voyant le vent fort, il eut peur: &amp; comme il commençoit à s\'enfoncer, il s\'écria, difant, Seigneur, fauve-moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 31, 'Et incontinent Jefus eftendit fa main &amp; le prit, lui difant, Homme de petite foi, pourquoi as-tu douté?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 32, 'Et quand ils furent montez en la naffelle, le vent s\'appaifa.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 33, 'Alors ceux qui eftoyent en la naffelle, vinrent, &amp; l\'adorerent, difans, Vrayement tu es le Fils de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 34, 'Puis eftans paffez outre, ils vinrent en la contrée de Genezareth.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 35, 'Et quand les hommes de ce lieu-là l\'eurent reconnu, ils envoyerent par toute la contrée d\'alentour, &amp; lui prefenterent tous ceux qui fe portoyent mal.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 36, 'Et le prioyent que feulement ils touchaffent le bord de fon veftement: &amp; tous ceux qui le toucherent, furent gueris.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 1, 'Alors vinrent à Jefus des Scribes &amp; des Pharifiens de Jerufalem, difans,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 2, 'Pourquoi tes difciples outre-paffent-ils la tradition des anciens? Car ils ne lavent point leurs mains, quand ils prennent leur repas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 3, 'Mais lui refpondant leur dit, Et vous, pourquoi outre-paffez-vous le commandement de Dieu par voftre tradition?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 4, 'Car Dieu a commandé, difant, Honore ton pere &amp; ta mere. Item, Qui maudira pere ou mere, meure de mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 5, 'Mais vous dites, Quiconque aura dit à fon pere ou à fa mere, Tout don qui fera offert de par moi, fera à ton profit:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 6, 'Encore qu\'il n\'honore pas fon pere ou fa mere, fera hors de coulpe: &amp; ainfi vous avez annullé le commandement de Dieu par voftre tradition.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 7, 'Hypocrites, Efaïe a bien prophetizé de vous, difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 8, 'Ce peuple-ici s\'approche de moi de fa bouche, &amp; m\'honore de fes levres: mais leur coeur eft fort éloigné de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 9, 'Mais ils m\'honorent en vain, enfeignans des doctrines, qui ne font que des commandemens d\'hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 10, 'Puis ayant appellé les troupes, il leur dit, Oyez &amp; entendez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 11, 'Ce n\'eft pas ce qui entre en la bouche, qui foüille l\'homme: mais ce qui fort de la bouche, c\'eft cela qui foüille l\'homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 12, 'Alors les difciples s\'approchans, lui dirent, N\'as-tu pas connu que les Pharifiens ont efté fcandalizez, quand ils ont ouï ce propos?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 13, 'Et lui refpondant, dit, Toute plante que mon Pere celefte n\'a point plantée, fera deracinée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 14, 'Laiffez-les: ce font aveugles, conducteurs d\'aveugles. Que fi un aveugle conduit un autre aveugle, tous deux cherront en la foffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 15, 'Alors Pierre prenant la parole, lui dit, Expofe nous cette fimilitude.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 16, 'Et Jefus dit, Vous auffi, eftes-vous encore fans entendement?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 17, 'N\'entendez-vous pas encore que tout ce qui entre en la bouche, s\'en va au ventre, &amp; eft jetté au retrait?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 18, 'Mais les chofes qui fortent de la bouche, partent du coeur, &amp; ces chofes-là foüillent l\'homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 19, 'Car du coeur fortent les penfées malignes, les meurtres, les adulteres, les paillardifes, les larrecins, les faux tefmoignages, les detractions.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 20, 'Ce font ces chofes-là qui foüillent l\'homme: mais de manger fans avoir les mains lavées, cela ne foüille point l\'homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 21, 'Apres, Jefus partant de là, fe retira és quartiers de Tyr &amp; de Sidon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 22, 'Et voici une femme Cananéenne, partie de ces quartiers-là, qui s\'écria, lui difant, Seigneur, fils de David, aye pitié de moi: ma fille eft miferablement tourmentée du diable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 23, 'Mais il ne lui refpondit mot. Alors fes difciples s\'approchans le prierent, difans, Donne lui congé: car elle crie en nous fuivant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 24, 'Et il refpondit, &amp; dit, Je ne fuis envoyé finon aux brebis peries de la maifon d\'Ifraël.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 25, 'Et elle vint &amp; l\'adora, difant, Seigneur, aide moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 26, 'Et lui, refondant dit, Il n\'eft pas bon de prendre le pain des enfans, &amp; le jetter aux petits chiens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 27, 'Mais elle dit, Il eft bien vrai, Seigneur: toutefois les petits chiens mangent des miettes qui tombent de la table de leurs maiftres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 28, 'Alors Jefus refpondant, lui dit, O femme, ta foi eft grande: qu\'il te foit fait comme tu veux. Et dés ce mefme inftant fa fille fut guerie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 29, 'Alors Jefus partant de là, vint prés de la mer de Galilée: puis monta fur la montagne, &amp; s\'affit là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 30, 'Alors plufieurs troupes vinrent à lui, ayans avec eux des boiteux, des aveugles, des muëts, des manchots, &amp; plufieurs autres: lefquels ils mirent aux pieds de Jefus, &amp; il les guerit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 31, 'Tellement que les troupes s\'étonnerent, voyans les muëts parler, les manchots eftre fains, les boiteux cheminer, &amp; les aveugles voir: &amp; glorifierent le Dieu d\'Ifraël.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 32, 'Alors Jefus ayant appellé fes difciples, dit, Je fuis émeu de compaffion envers cette multitude: car il y a déja trois jours qu\'ils ne bougent d\'avec moi, &amp; ils n\'ont rien à manger; &amp; de les renvoyer à jeun, je ne le veux point: de peur qu\'ils defaillent en chemin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 33, 'Et fes difciples lui dirent, D\'où nous viendroyent au defert tant de pains, pour raffafier une telle multitude?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 34, 'Et Jefus leur dit, Combien avez-vous de pains? Ils lui dirent, Sept, &amp; quelque peu de petits poiffons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 35, 'Alors il commanda aux troupes de s\'arranger par terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 36, 'Et ayant pris les fept pains &amp; les poiffons, apres qu\'il eût rendu graces il les rompit, &amp; les bailla à fes difciples, &amp; les difciples au peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 37, 'Et tous en mangerent, &amp; furent raffafiez, &amp; enleverent du refte des pieces de pain, fept corbeilles pleines.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 38, 'Or ceux qui en avoyent mangé, eftoyent quatre mille hommes, fans les femmes &amp; les petits enfans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 39, 'Alors Jefus ayant donné congé aux troupes, monta fur une naffelle, &amp; vint au territoire de Magdala.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 1, 'Alors des Pharifiens &amp; des Sadduciens vinrent à lui, &amp; le tentans, le requirent qu\'il leur montraft quelque figne du ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 2, 'Mais lui refpondant leur dit, Quand le foir eft venu, vous dites, Il fera beau temps: car le ciel eft rouge.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 3, 'Et le matin vous dites, Il fera aujourd\'hui tempefte, car le ciel malplaifant eft rouge. Hypocrites, vous fçavez bien juger de l\'apparence du ciel, &amp; vous ne pouvez juger des fignes des faifons?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 4, 'La nation méchante &amp; adultere recherche un figne: &amp; il ne lui fera point donné de figne, finon le figne de Jonas le Prophete. Et les laiffant il s\'en alla.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 5, 'Et quand fes difciples furent venus au rivage de de-là, ils avoyent oublié à prendre des pains.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 6, 'Et Jefus leur dit, Avifez, &amp; vous donnez garde du levain des Pharifiens &amp; des Sadduciens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 7, 'Or ils penfoyent en eux-mefmes, difans, C\'eft parce que nous n\'avons point pris de pains.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 8, 'Et Jefus connoiffant cela, leur dit, Gens de petite foi, qu\'eft-ce que vous penfez en vous-mefmes, touchant ce que vous n\'avez point pris de pains?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 9, 'N\'entendez-vous point encores, &amp; ne vous fouvient-il plus des cinq pains des cinq mille hommes, &amp; combien de corbeilles vous en recüeilliftes?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 10, 'Ni des fept pains des quatre mille homme, &amp; combien de corbeilles vous en recüeilliftes?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 11, 'Comment n\'entendez-vous point, que ce n\'eft pas touchant le pain que je vous ai dit, que vous vous donnaffiez garde du levain des Pharifiens &amp; des Sadduciens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 12, 'Alors ils entendirent qu\'il n\'avoit pas dit, qu\'ils fe donnaffent garde du levain du pain, mais du levain de la doctrine des Pharifiens &amp; des Sadduciens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 13, 'Et Jefus venant és parties de Cefarée de Philippe, interrogea fes difciples, difant, Qui difent les hommes que je fuis, moi le Fils de l\'homme?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 14, 'Et ils lui refpondirent, Les uns, Jean Baptifte: les autres, Elie, &amp; les autres, Jeremie, ou l\'un des Prophetes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 15, 'Il leur dit, Mais vous, qui dites-vous que je fuis?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 16, 'Simon Pierre refpondant dit, Tu es le Chrift, le Fils du Dieu vivant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 17, 'Et Jefus refpondant lui dit, Tu es bien-heureux, Simon fils de Jona: car la chair &amp; le fang ne te l\'a pas revelé, mais mon Pere qui eft és cieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 18, 'Et je te dis auffi, que tu es Pierre, &amp; fur cette pierre j\'edifierai mon Eglife: &amp; les portes d\'enfer n\'auront point de force contr\'elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 19, 'Et je te donnerai les clefs du royaume des cieux: &amp; quoi que tu auras lié en terre, il fera lié és cieux: &amp; quoi que tu auras deflié en terre, il fera deflié és cieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 20, 'Alors il commanda expreffément, qu\'ils ne diffent à perfonne qu\'il fuft Jefus le Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 21, 'Dés lors Jefus commença à declarer à fes difciples, qu\'il lui faloit aller à Jerufalem, &amp; fouffrir beaucoup de chofes de la part des Anciens, &amp; des principaux Sacrificateurs, &amp; des Scribes: &amp; eftre mis à mort, &amp; reffufciter au troifiéme jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 22, 'Alors Pierre l\'ayant pris à part, fe prit à le tanfer, difant, Seigneur, aye pitié de toi, ceci ne t\'adviendra point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 23, 'Mais lui s\'eftant retourné, dit à Pierre, Va arriere de moi, Satan, tu m\'es en fcandale: car tu ne comprens point les chofes qui font de Dieu, mais les chofes qui font des hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 24, 'Alors Jefus dit à fes difciples, Si quelqu\'un veut venir apres moi, qu\'il renonce à foi-mefme, &amp; qu\'il charge fur foi fa croix, &amp; qu\'il me fuive.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 25, 'Car quiconque voudra fauver fon ame, il la perdra, &amp; quiconque perdra fon ame pour l\'amour de moi, il la trouvera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 26, 'Car que profite-t\'il à l\'homme, s\'il gagne tout le monde, &amp; qu\'il faffe perte de fon ame? ou que donnera l\'homme pour recompenfe de fon ame?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 27, 'Car le Fils de l\'homme doit venir en la gloire de fon Pere avec fes Anges: &amp; alors il rendra à un chacun felon fes oeuvres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 28, 'En verité je vous dis qu\'il y a quelques-uns de ceux qui font ici prefens, qui ne goufteront point la mort, jufques à ce qu\'ils ayent veu le Fils de l\'homme venir en fon regne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 1, 'Et fix jours apres, Jefus prit Pierre, &amp; Jaques, &amp; Jean fon frere, &amp; les mena en une haute montagne à part.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 2, 'Et fut transfiguré en leur prefence: &amp; fa face refplendit comme le foleil, &amp; fes veftemens devinrent blancs comme la lumiere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 3, 'Et voici, Moyfe &amp; Elie furent veus parlans avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 4, 'Alors Pierre prenant la parole, dit à Jefus, Seigneur, il eft bon que nous foyons ici: fi tu veux, faifons ici trois tabernacles, un pour toi, &amp; un pour Moyfe, &amp; un pour Elie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 5, 'Et comme il parloit encore, voici une nuée refplendiffante qui les enombra: puis voilà une voix qui vint de la nuée, difant, Celui-ci eft mon Fils bien-aimé, auquel j\'ai pris mon bon-plaifir: efcoutez-le.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 6, 'Ce que les difciples ayans ouï, ils tomberent fur leur face en terre, &amp; eurent tres-grand\'peur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 7, 'Alors Jefus vint vers eux, &amp; les toucha, leur difant, Levez-vous &amp; n\'ayez point de peur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 8, 'Et eux élevans leurs yeux, ne virent perfonne, finon Jefus tout feul.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 9, 'Et comme ils defcendoyent de la montagne, Jefus leur commanda, difant, Ne dites à perfonne la vifion, jufques à ce que le Fils de l\'homme foit reffufcité des morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 10, 'Et fes difciples l\'interrogerent, difans, pourquoi donc les Scribes difent-ils, qu\'il faut qu\'Elie vienne premierement?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 11, 'Et Jefus refpondant, leur dit, De vrai, Elie viendra premierement, &amp; reftablira toutes chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 12, 'Mais je vous dis qu\'Elie eft déja venu, &amp; ils ne l\'ont point connu: mais ils lui ont fait tout ce qu\'ils ont voulu: ainfi auffi le Fils de l\'homme doit fouffrir par eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 13, 'Alors les difciples entendirent bien, que c\'eftoit de Jean Baptifte qu\'il leur avoit parlé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 14, 'Et quand ils furent venus vers les troupes, un homme vint à lui s\'agenoüillant devant lui,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 15, 'Et difant, Seigneur, aye pitié de mon fils, car il eft lunatique, &amp; eft miferablement affligé: car fouvent il tombe au feu, &amp; fouvent en l\'eau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 16, 'Et je l\'ai prefenté à tes difciples, mais ils ne l\'ont peu guerir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 17, 'Et Jefus refpondant, dit, O generation incredule, &amp; de fens renverfé! jufques à quand ferai-je avec vous? jufques à quand finalement vous fupporterai-je? amenez-le moi ici.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 18, 'Et Jefus tança le diable, lequel fortit d\'icelui: &amp; dés ce mefme inftant l\'enfant fut gueri.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 19, 'Alors les difciples vinrent à part à Jefus, &amp; lui dirent, Pourquoi ne l\'avons-nous peu jetter hors?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 20, 'Et Jefus leur refpondit, C\'eft à caufe de voftre incredulité: car en verité je vous dit, que fi vous aviez de la foi auffi gros qu\'un grain de femence de mouftarde, vous diriez à cette montagne, Traverfe d\'ici là, &amp; elle traverferoit, &amp; rien ne vous feroit impoffible:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 21, 'Mais cette forte de diables ne fort point, finon par oraifon &amp; par jeufne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 22, 'Et comme ils converfoyent en Galilée, Jefus leur dit, Il adviendra que le Fils de l\'homme fera livré és mains des hommes:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 23, 'Et ils le mettront à mort, mais au troifiéme jour il reffufcitera. Et les difciples en furent grandement contriftez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 24, 'Et quand ils furent venus à Capernaum, ceux qui recevoyent les didrachmes, s\'adrefferent à Pierre, &amp; lui dirent, Voftre maiftre ne paye-t\'il pas les didrachmes?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 25, 'Il dit, Oui. Et quand il fut entré en la maifon, Jefus le prevint, difant, Que te femble, Simon? Les rois de la terre de qui prennent-ils les tributs, ou les impofts? eft-ce de leurs enfans, ou des eftrangers?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 26, 'Pierre dit, Des eftrangers. Jefus lui refpondit, Les enfans donc font francs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 27, 'Mais afin que nous ne les fcandalizions point, va-t\'en à la mer, &amp; jette le hameçon: &amp; pren le premier poiffon qui montera: &amp; quand tu lui auras ouvert la gueule, tu trouveras un ftatere: pren-le, &amp; leur baille pour moi &amp; pour toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 1, 'En cette mefme heure-là les difciples vinrent à Jefus, difans, Qui eft le plus grand au royaume des cieux?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 2, 'Et Jefus ayant appellé un petit enfant, le mit au milieu d\'eux;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 3, 'Et dit, En verité je vous dis, que fi vous n\'eftes changez, &amp; ne devenez comme les petits enfans, vous n\'entrerez point au royaume des cieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 4, 'Partant quiconque fe fera humilié foi-mefme, comme eft ce petit enfant, c\'eft celui-là qui eft le plus grand au royaume des cieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 5, 'Et quiconque reçoit un tel petit enfant en mon Nom, il me reçoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 6, 'Mais quiconque fcandalize un de ces petits qui croyent en moi, il lui vaudroit mieux qu\'on lui pendift une meule d\'afne au col, &amp; qu\'il fuft plongé au profond de la mer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 7, 'Malheur au monde à caufe des fcandales: Car il eft neceffaire qu\'il advienne des fcandales: toutefois malheur à l\'homme par qui fcandale advient.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 8, 'Que fi ta main ou ton pied te fait chopper, coupe-le, &amp; le jette arriere de toi: car il vaut mieux que tu entres boiteux ou manchot en la vie, que d\'avoir deux pieds ou deux mains, &amp; eftre jetté au feu eternel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 9, 'Et fi ton oeil te fait chopper arrache-le, &amp; le jette arriere de toi: car il vaut mieux que tu entres en la vie, n\'ayant qu\'un oeil, que d\'avoir deux yeux, &amp; eftre jetté en la gehenne du feu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 10, 'Prenez garde que vous ne méprifiez pas un de ces petits: car je vous dis qu\'és cieux leurs Anges voyent tousjours la face de mon Pere qui eft és cieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 11, 'Car le Fils de l\'homme eft venu pour fauver ce qui eftoit peri.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 12, 'Que vous femble? Si un homme a cent brebis, &amp; qu\'il y en ait une égarée, ne laiffe-t\'il pas les quatre-vingts &amp; dix-neuf, pour s\'en aller aux montagnes chercher celle qui eft égarée?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 13, 'Et s\'il advient qu\'il la trouve, en verité je vous dis, qu\'il a plus de joye d\'elle, que des quatre-vingts &amp; dix-neuf qui n\'ont point efté égarées.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 14, 'Auffi la volonté de voftre Pere qui eft és cieux, n\'eft pas qu\'aucun de ces petits periffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 15, 'Que fi ton frere a peché contre toi, va &amp; le repren entre toi &amp; lui feul. S\'il t\'écoute, tu as gagné ton frere:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 16, 'Mais s\'il ne t\'écoute point, prens-en avec toi encore un ou deux: afin qu\'en la bouche de deux ou de trois tefmoins toute parole foit ferme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 17, 'Que s\'il ne daigne les écouter, di-le à l\'Eglife: &amp; s\'il ne daigne écouter l\'Eglife, qu\'il te foit comme un payen &amp; un peager.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 18, 'En verité je vous dis, que quoi que vous aurez lié fur la terre, il fera lié au ciel: &amp; quoi que vous aurez délié fur la terre, il fera délié au ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 19, 'Derechef je vous dis, que fi deux d\'entre vous s\'accordent fur la terre, de toute chofe qu\'ils demanderont, il leur fera fait de mon Pere qui eft és cieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 20, 'Car où il y en a deux ou trois affemblez en mon Nom, je fuis là au milieu d\'eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 21, 'Alors Pierre s\'approchant de lui, dit, Seigneur, jufques à combien de fois mon frere pechera-t\'il contre moi, &amp; je lui pardonnerai? fera-ce bien jufques à fept fois?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 22, 'Jefus lui refpondit, Je ne te dis point, jufques à fept fois, mais jufques à fept fois feptante fois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 23, 'Partant le royaume des cieux eft accomparé à un roi, lequel voulut conter avec fes ferviteurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 24, 'Et quand il eut commencé à conter, on lui en prefenta un qui lui devoit dix mille talens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 25, 'Et d\'autant qu\'il n\'avoit pas dequoi payer, fon feigneur commanda qu\'il fuft vendu, lui, fa femme, &amp; fes enfans, &amp; tout ce qu\'il avoit, &amp; que la debte fut payée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 26, 'Or ce ferviteur-là fe jettant en terre, le fupplioit, difant, Seigneur, ufe de patience envers moi, &amp; je te rendrai le tout.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 27, 'Alors le Seigneur de ce ferviteur-là, eftant émeu de compaffion, le relacha, &amp; lui quitta la debte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 28, 'Mais quand ce ferviteur-là fut parti, il trouva l\'un de fes compagnons de fervice, qui lui devoit cent deniers: lequel il faifit, &amp; l\'étrangloit, difant, Paye moi ce que tu me dois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 29, 'Et fon compagnon de fervice fe jettant à fes pieds, le prioit, difant, Ufe de patience envers moi, &amp; je te rendrai le tout.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 30, 'Mais il n\'en voulut rien faire: ains s\'en alla, &amp; le mit en prifon, jufques à ce qu\'il euft payé la debte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 31, 'Or fes autres compagnons de fervice, voyans ce qui avoit efté fait, furent fort marris: dont ils s\'en vinrent, &amp; declarerent à leur feigneur tout ce qui avoit efté fait.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 32, 'Alors fon feigneur l\'appella &amp; lui dit, Méchant ferviteur, je t\'ai quitté toute cette debte, parce que tu m\'en as prié:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 33, 'Ne te faloit-il pas auffi avoir pitié de ton compagnon de fervice, ainfi que j\'avois eu pitié de toi?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 34, 'Alors fon feigneur eftant en colere, le livra aux fergeans, jufques à ce qu\'il lui euft payé tout ce qui lui étoit deu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 35, 'Ainfi vous en fera mon Pere celefte, fi vous ne pardonnez de coeur chacun à fon frere fes fautes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 1, 'Il arriva que quand Jefus eût achevé ces propos, il partit de Galilée, &amp; vint és quartiers de Judée, outre le Jordain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 2, 'Et de grandes troupes le fuivirent, &amp; là il les guerit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 3, 'Alors des Pharifiens vinrent à lui, le tentans &amp; lui difans, Eft-il permis à l\'homme de repudier fa femme pour quelque caufe que ce foit?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 4, 'Lequel refpondant leur dit, N\'avez-vous point leu, que celui qui les fit dés le commencement, les fit mafle &amp; femelle?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 5, 'Et qu\'il dit, Pour cette caufe l\'homme delaiffera fon pere &amp; fa mere, &amp; s\'adjoindra à fa femme: &amp; les deux feront une mefme chair?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 6, 'Ainfi ils ne font plus deux, mais ils font une mefme chair: Donc ce que Dieu a conjoint, que l\'homme ne le fepare point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 7, 'Ils lui dirent, Pourquoi donc Moyfe a-t\'il commandé de bailler la lettre de divorce, &amp; de la repudier?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 8, 'Il leur dit, C\'eft que Moyfe pour la dureté de voftre coeur vous a permis de repudier vos femmes: mais du commencement il n\'eftoit pas ainfi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 9, 'Or moi je vous dis, que quiconque repudiera fa femme, finon pour caufe de paillardife, &amp; fe mariera à une autre, commet adultere: &amp; qui fe fera marié à celle qui eft repudiée, commet adultere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 10, 'Ses difciples lui dirent, Si telle eft la condition de l\'homme avec la femme, il n\'eft point expedient de fe marier.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 11, 'Mais il leur dit, Tous ne comprennent pas cela, mais ceux aufquels il eft donné.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 12, 'Car il y a des eunuques, qui font ainfi nés du ventre de leur mere: &amp; il y a des eunuques qui ont efté faits eunuques par les hommes: &amp; il y a des eunuques, qui fe font eux-mémes faits eunuques pour le royaume des cieux. Qui peut comprendre ceci, le comprenne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 13, 'Alors lui furent prefentez des petits enfans, afin qu\'il leur impofaft les mains, &amp; qu\'il priaft, dont les difciples les tancerent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 14, 'Mais Jefus leur dit, Laiffez les petits enfans, &amp; ne les empefchez point de venir à moi: car à tels eft le royaume des cieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 15, 'Et leur ayant impofé les mains, il partit de là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 16, 'Et voici, quelqu\'un s\'approchant de lui, dit, Bon Maiftre, quel bien ferai-je, afin que j\'aye la vie eternelle?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 17, 'Il lui refpondit, Pourquoi m\'appelles-tu bon? Il n\'y a nul bon qu\'un feul, affavoir Dieu: Que fi tu veux entrer en la vie, garde les commandemens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 18, 'Il lui dit, Quels? Et Jefus lui répondit, Tu ne tuëras point. Tu ne commetras point adultere. Tu ne déroberas point. Tu ne diras point faux témoignage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 19, 'Honore ton pere &amp; ta mere. Item, Tu aimeras ton prochain comme toi mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 20, 'Le jeune homme lui dit, J\'ai gardé toutes ces chofes dés ma jeuneffe: que me defaut-il encore?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 21, 'Jefus lui dit, Si tu veux eftre parfait, va, ven ce que tu as, &amp; le donne aux pauvres, &amp; tu auras un trefor au ciel: puis vien &amp; me fui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 22, 'Mais quand le jeune homme eût entendu cette parole, il s\'en alla tout trifte: car il avoit de grands biens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 23, 'Alors Jefus dit à fes difciples, En verité je vous dis, qu\'un riche entrera difficilement au royaume des cieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 24, 'Et je vous dis derechef, Il eft plus aifé qu\'un chameau paffe par le trou d\'une aiguille, que non pas qu\'un riche entre au royaume de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 25, 'Ses difciples ayans ouï ces chofes s\'eftonnerent fort, difans, Qui eft-ce donc qui peut eftre fauvé?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 26, 'Et Jefus les regardant, leur dit, Quant aux hommes, cela eft impoffible: mais quant à Dieu, toutes chofes font poffibles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 27, 'Alors Pierre prenant la parole, lui dit, Voici, nous avons tout delaiffé, &amp; t\'avons fuivi: que nous en adviendra-t\'il donc?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 28, 'Et Jefus leur dit, En verité je vous dis, que vous qui m\'avez fuivi en la regeneration, quand le Fils de l\'homme fera affis au trône de fa gloire, vous auffi ferez affis fur douze trônes, jugeant les douze lignées d\'Ifraël.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 29, 'Et quiconque aura delaiffé maifons, ou freres, ou foeurs, ou pere, ou mere, ou femme, ou enfans, ou champs, à caufe de mon Nom, il en recevra cent fois autant, &amp; heritera la vie eternelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 30, 'Mais plufieurs qui font les premiers, feront les derniers: &amp; les derniers feront les premiers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 1, 'Car le royaume des cieux eft femblable à un pere de famille, lequel fortit dés le poinct du jour pour loüer des ouvriers pour fa vigne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 2, 'Et quand il eût accordé avec les ouvriers à un denier par jour, il les envoya en fa vigne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 3, 'Puis eftant forti environ fur les trois heures, il en vid d\'autres qui eftoyent à ne rien faire au marché:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 4, 'AUfquels il dit, Allez-vous-en auffi en ma vigne, &amp; je vous baillerai ce qui fera de raifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 5, 'Et ils s\'y en allerent. Puis il fortit derechef environ fur les fix &amp; neuf heures, &amp; fit de mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 6, 'Et environ fur les onze heures il fortit, &amp; en trouva d\'autres qui eftoyent à ne rien faire, aufquels il dit, Pourquoi vous tenés-vous ici tout le jour à ne rien faire?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 7, 'Ils lui refpondirent, Parce que perfonne ne nous a loüez. Et il leur dit, Allez-vous-en auffi en ma vigne, &amp; vous recevrez ce qui fera de raifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 8, 'Quand donc le foir fut venu, le feigneur de la vigne dit à celui qui avoit la charge de fes affaires, Appelle les ouvriers, &amp; leur paye leur falaire: commençant depuis les derniers jufques aux premiers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 9, 'Alors ceux qui avoyent efté loüez vers les onze heures eftans venus, ils receurent chacun un denier.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 10, 'Or quand les premiers furent venus, ils penfoyent recevoir davantage: mais ils receurent auffi chacun un denier.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 11, 'Et l\'ayans receu, ils murmuroyent contre le pere de famille:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 12, 'Difans, Ces derniers-ici n\'ont travaillé qu\'une heure, &amp; tu les as faits pareils à nous, qui avons porté le faix du jour, &amp; la chaleur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 13, 'Et il refpondit à l\'un d\'eux, &amp; dit, Compagnon, je ne te fais point de tort: n\'as-tu pas accordé avec moi à un denier?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 14, 'Pren ce qui eft tien, &amp; t\'en va: je veux donner à ce dernier autant qu\'à toi:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 15, 'Ne m\'eft-il pas permis de faire ce que je veux de mes biens? Ton oeil eft-il malin de ce que je fuis bon?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 16, 'Ainfi les derniers feront les premiers: &amp; les premiers feront les derniers. Car il y a beaucoup d\'appellez, mais peu d\'éleus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 17, 'Et Jefus montant à Jerufalem, prit à part fur le chemin fes douze difciples, &amp; leur dit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 18, 'Voici, nous montons à Jerufalem, &amp; le Fils de l\'homme fera livré aux principaux Sacrificateurs &amp; aux Scribes, &amp; ils le condamneront à mort:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 19, 'Et le livreront aux nations pour s\'en moquer, &amp; le foüetter, &amp; le crucifier: mais au troifiéme jour il reffufcitera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 20, 'Alors la mere des fils de Zebedée vint à lui avec fes fils, fe profternant &amp; lui demandant quelque chofe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 21, 'Et il lui dit, Que veux-tu? Elle lui dit, Ordonne que mes deux fils qui font ici, foyent affis, l\'un à ta main droite, &amp; l\'autre à ta gauche, en ton royaume.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 22, 'Et Jefus refondant, dit, Vous ne fçavez ce que vous demandez. Pouvez-vous boire la coupe que je dois boire, &amp; eftre baptizez du baptefme duquel je dois eftre baptizé? Ils lui dirent, Nous le pouvons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 23, 'Et il leur dit, De vrai, vous boirez ma coupe, &amp; ferez baptizez du baptefme duquel je ferai baptizé: mais de feoir à ma droite ou à ma gauche, ce n\'eft point à moi de la donner, mais il fera donné à ceux aufquels il eft preparé de mon Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 24, 'Les dix autres ayans ouï cela, furent indignez contre les deux freres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 25, 'Or Jefus les ayant appellez, leur dit, Vous fçavez, que les princes des nations les maiftrifent: &amp; que les grands ufent d\'autorité fur elles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 26, 'Mais il ne fera point ainfi entre vous: au contraire quiconque voudra eftre grand entre vous, qu\'il foit voftre miniftre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 27, 'Et quiconque voudra eftre le premier entre vous, qu\'il foit voftre ferviteur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 28, 'Tout ainfi que le Fils de l\'homme n\'eft point venu pour eftre fervi, mais pour fervir, &amp; donner fa vie en rançon pour plufieurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 29, 'Et comme ils partoyent de Jerico, une grande troupe le fuivit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 30, 'Et voici, deux aveugles qui eftoyent affis prés du chemin, ayans ouï que Jefus paffoit, crierent, difans, Seigneur, Fils de David, aye pitié de nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 31, 'Et la troupe les tança, afin qu\'ils fe teuffent: mais ils crioyent tant plus fort, difans, Seigneur, Fils de David, aye pitié de nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 32, 'Et Jefus s\'arreftant les appella, &amp; leur dit, Que voulez-vous que je vous faffe?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 33, 'Ils lui dirent, Seigneur, que nos yeux foyent ouverts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 34, 'Et Jefus eftant efmeu de compaffion, toucha leurs yeux: &amp; incontinent leurs yeux recouvrerent la veuë, &amp; ils le fuivirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 1, 'Or quand ils furent prés de Jerufalem, &amp; furent venus à Bethphagé au mont des Oliviers, alors Jefus envoya deux difciples,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 2, 'Leur difant, Allez en la bourgade qui eft vis à vis de vous, &amp; incontinent vous trouverez une afneffe attachée, &amp; fon poulain avec elle: détachez-les, &amp; me les amenez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 3, 'Que fi quelqu\'un vous dit quelque chofe, vous direz que le Seigneur en a affaire: &amp; incontinent il les envoyera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 4, 'Or tout cela fe fit, afin que fuft accompli, ce dont il avoit efté parlé par le Prophete, difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 5, 'Dites à la fille de Sion, Voici, ton Roi vient à toi, debonnaire, &amp; monté fur une afneffe, &amp; le poulain de celle qui eft fous le joug.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 6, 'Les difciples donc s\'en allerent, &amp; firent ainfi que Jefus leur avoit ordonné.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 7, 'Et amenerent l\'afneffe &amp; le poulain, &amp; mirent leurs veftemens deffus, &amp; le firent affeoir fur iceux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 8, 'Alors de grandes troupes étendirent leurs robbes par le chemin: &amp; les autres coupoyent des rameaux des arbres, &amp; les étendoyent par le chemin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 9, 'Et les troupes qui alloyent devant, &amp; qui fuivoyent, crioyent, difant, Hofanna, au Fils de David: Benit foit celui qui vient au nom du Seigneur: Hofanna és lieux tres-hauts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 10, 'Et quand il fut entré dans Jerufalem, toute la ville fut emuë, difant, Qui eft celui-ci?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 11, 'Et les troupes difoyent, C\'eft Jefus le Prophete de Nazareth de Galilée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 12, 'Et Jefus entra au temple de Dieu, &amp; chaffa dehors tous ceux qui vendoyent &amp; achetoyent au temple: &amp; renverfa les tables des changeurs, &amp; les felles de ceux qui vendoyent des pigeons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 13, 'Et il leur dit, Il eft écrit, Ma maifon fera appellée maifon de priere: mais vous en avez fait une caverne de brigands.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 14, 'Alors des aveugles &amp; des boiteux vinrent à lui au temple, &amp; il les guerit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 15, 'Mais quand les principaux Sacrificateurs &amp; les Scribes eurent veu les merveilles qu\'il avoit faites, &amp; les enfans crians au temple, difans, Hofanna au Fils de David, ils en furent indignez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 16, 'Et lui dirent, Ois-tu ce que ceux-ci difent? Et Jefus leur dit, Oui. Ne leuftes vous jamais, Tu as accompli la loüange par la bouche des enfans, &amp; de ceux qui tettent?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 17, 'Et les ayans laiffez, il fortit hors de la ville, pour s\'en aller à Bethanie, &amp; logea là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 18, 'Or le matin, comme il retournoit en la ville, il eut faim.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 19, 'Et voyant un figuier qui eftoit fur le chemin, il s\'y adreffa, &amp; n\'y trouva rien finon des fueilles feulement, &amp; lui dit, Qu\'à tout jamais il ne naiffe de toi aucun fruit. Et le figuier fecha incontinent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 20, 'Les difciples ayans veu cela s\'étonnerent, difans, Comment eft-ce que le figuier eft devenu fec à l\'inftant?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 21, 'Jefus refpondant, leur dit, En verité je vous dis, pourveu que vous ayez la foi, &amp; que vous ne foyez point en doute, non feulement vous ferez ce qui a efté fait au figuier: mais auffi fi vous dites à cette montagne, Ofte-toi, &amp; te jette dans la mer, cela fe fera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 22, 'Et quoi que vous demandiez en priant, fi vous croyez, vous le recevrez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 23, 'Puis quand il fut venu au temple, les principaux Sacrificateurs, &amp; les Anciens du temple, vinrent à lui, comme il enfeignoit, &amp; lui dirent, De quelle authorité fais-tu ces chofes: &amp; qui eft celui qui t\'a donné cette authorité?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 24, 'Jefus refpondant leur dit, Je vous interrogerai auffi d\'une chofe, laquelle fi vous me dites, je vous dirai auffi de quelle authorité je fais ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 25, 'Le Baptefme de Jean d\'où eftoit-il? Du ciel, ou des hommes? Or ils difputoyent en eux-mefmes, difans, Si nous difons, Du ciel, il nous dira, Pourquoi donc ne l\'avez-vous point creu?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 26, 'Et fi nous difons, Des hommes, nous craignons les troupes, car tous tiennent Jean pour Prophete.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 27, 'Alors ils refpondirent à Jefus, difans, Nous ne fçavons. Et il leur dit, Je ne vous dirai point auffi de quelle authorité je fais ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 28, 'Mais que vous femble? Un homme avoit deux fils: &amp; venant au premier, il lui dit, Mon fils, va-t\'en, &amp; travaille aujourd\'hui en ma vigne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 29, 'Lequel refpondant, dit, Je n\'y veux pas aller: mais puis apres s\'eftant repenti, il s\'y en alla.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 30, 'Puis il vint à l\'autre, &amp; lui dit de mefme, lequel refpondit, &amp; dit, J\'y vais, Seigneur: mais il ne s\'y en alla point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 31, 'Lequel de ces deux fit la volonté du pere? Ils lui dirent, Le premier. Jefus leur dit, En verité je vous dis, que les peagers &amp; les paillardes vous devancent au royaume de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 32, 'Car Jean eft venu à vous par la voye de juftice, &amp; vous ne l\'avez point creu: mais les peagers &amp; les paillardes l\'ont creu: &amp; vous ayans veu cela, ne vous eftes point repentis puis apres pour le croire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 33, 'Efcoutez une autre fimilitude. Il y avoit un pere de famille, qui planta une vigne, &amp; l\'environna d\'une haye, &amp; y creufa un preffoir, &amp; y baftit une tour: puis la loüa à des vignerons, &amp; s\'en alla dehors.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 34, 'Or la faifon des fruits eftant prochaine, il envoya fes ferviteurs vers les vignerons, pour en recevoir les fruits.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 35, 'Mais les vignerons ayans pris fes ferviteurs, foüetterent l\'un, &amp; tuërent l\'autre, &amp; affommerent de pierres l\'autre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 36, 'Derechef il envoya d\'autres ferviteurs en plus grand nombre que les premiers, &amp; ils leur en firent de mefmes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 37, 'Enfin il envoya vers eux fon propre fils, difant, Ils porteront reverence à mon fils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 38, 'Mais quand les vignerons virent le fils, ils dirent entr\'eux, Celui-ci eft l\'heritier: venez, mettons-le à mort, &amp; nous faififfons de fon heritage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 39, 'Et l\'ayans pris, ils le jetterent hors de la vigne, &amp; le mirent à mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 40, 'Quand donc le feigneur de la vigne fera venu, que fera-t\'il à ces vignerons-là?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 41, 'Ils lui dirent, il les fera perir mal-heureufement comme des méchans, &amp; loüera fa vigne à d\'autres vignerons, qui lui rendront les fruits en leurs faifons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 42, 'Et Jefus leur dit, Ne leuftes vous jamais és Efcritures, La pierre que les edifians ont rejettée, eft devenuë la maitreffe pierre du coin. Ceci a efté fait par le Seigneur, &amp; c\'eft une chofe merveilleufe devant nos yeux?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 43, 'Partant je vous dis, que le royaume de Dieu vous fera ofté, &amp; fera donné à une nation qui en rapportera les fruits.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 44, 'Or celui qui cherra fur cette pierre, fera du tout froiffé: &amp; celui fur qui elle cherra, elle le brifera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 45, 'Et quand les principaux Sacrificateurs &amp; les Pharifiens eurent entendu ces fimilitudes, ils apperceurent qu\'il parloit d\'eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 46, 'Et cherchans à le faifir, ils craignirent les troupes, parce qu\'on le tenoit pour Prophete.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 1, 'Alors Jefus prenant la parole, leur parla derechef en fimilitude, difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 2, 'Le royaume des cieux eft femblable à un roi qui fit les nopces de fon fils:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 3, 'Et envoya fes ferviteurs, pour appeller ceux qui avoyent efté conviez aux nopces: mais ils n\'y voulurent point venir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 4, 'Derechef il envoya d\'autres ferviteurs, difant, Dites aux conviez, Voici, j\'ai apprefté mon difner, mes taureaux &amp; mes beftes engraiffées font tuées, &amp; tout eft preft: venez aux nopces.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 5, 'Mais eux n\'en tenans conte, s\'en allerent, l\'un à fa métairie, &amp; l\'autre à fon trafic.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 6, 'Et les autres prirent fes ferviteurs, &amp; les outragerent, &amp; les tuërent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 7, 'Quand le roi l\'entendit, il fe mit en colere: &amp; ayant envoyé fes gens de guerre, il fit perir ces meurtriers-là, &amp; brufla leur ville.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 8, 'Alors il dit à fes ferviteurs, Or bien, les nopces font appreftées, mais ceux qui eftoyent conviez n\'en eftoyent pas dignes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 9, 'Allez donc aux quarrefours des chemins, &amp; autant que vous en trouverez, conviez-les aux nopces.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 10, 'Alors fes ferviteurs fortirent vers les chemins, &amp; affemblerent autant qu\'ils en trouverent, tant mauvais que bons: tellement que le lieu des nopces fut rempli de gens qui eftoyent à table.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 11, 'Et le roi y eftant entré, pour voir ceux qui eftoyent à table, vid là un homme qui n\'eftoit pas veftu d\'une robbe de nopces.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 12, 'Et il lui dit, Compagnon, comment es-tu entré ici fans avoir une robbe de nopces? Et il eut la bouche clofe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 13, 'Alors le roi dit aux ferviteurs, Liez-le pieds &amp; mains, &amp; le jettez és tenebres de dehors: là il y aura pleur &amp; grincement de dents.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 14, 'Car il y a beaucoup d\'appellez, mais peu d\'éleus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 15, 'Alors les Pharifiens s\'eftans retirez, prirent confeil comment ils l\'enlaceroyent en paroles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 16, 'Et lui envoyerent leurs difciples avec des Herodiens, difans, Maiftre, nous fçavons que tu es veritable, &amp; que tu enfeignes la voye de Dieu en verité, &amp; que tu ne te foucies de perfonne: car tu ne regardes point à l\'apparence des hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 17, 'Di-nous donc ce qui te femble? Eft-il permis de payer tribut à Cefar, ou non?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 18, 'Et Jefus connoiffant leur malice, dit, Hypocrites, pourquoi me tentez-vous?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 19, 'Montrez-moi la monnoye du tribut. Et ils lui prefenterent un denier.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 20, 'Et il leur dit, De qui eft cette image, &amp; cette infcription?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 21, 'Ils lui dirent, De Cefar. Alors il leur dit, Rendez donc à Cefar les chofes qui font à Cefar, &amp; à Dieu celles qui font à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 22, 'Oyans cela ils s\'eftonnerent, &amp; le laiffant s\'en allerent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 23, 'Ce jour-là les Sadduciens (qui difent qu\'il n\'y a point de refurrection) vinrent à luy, &amp; l\'interrogerent:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 24, 'Difans, Maiftre, Moyfe a dit, Si quelqu\'un vient à mourir fans avoir des enfans, fon frere prendra la femme d\'icelui, &amp; fufcitera lignée à fon frere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 25, 'Or il y avoit parmi nous fept freres, dont le premier apres qu\'il fut marié mourut, &amp; n\'ayant point eu lignée, laiffa fa femme à fon frere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 26, 'Semblablement auffi le fecond, puis le troifiéme, jufques au feptiéme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 27, 'Or apres eux tous, la femme mourut auffi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 28, 'En la refurrection donc, duquel des fept fera-t\'elle femme? car tous l\'ont euë.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 29, 'Mais Jefus refpondant leur dit, Vous errez, ne fçachans point les Efcritures, ni la vertu de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 30, 'Car en la refurrection, on ne prend ni on ne donne des femmes en mariage: mais on eft comme les Anges de Dieu au ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 31, 'Et quant à la refurrection des morts, n\'avez-vous point leu ce dont Dieu vous a parlé, difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 32, 'Je fuis le Dieu d\'Abraham, &amp; le Dieu d\'Ifaac, &amp; le Dieu de Jacob? Dieu n\'eft pas le Dieu des morts, mais le Dieu des vivans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 33, 'Et les troupes oyans cela, s\'estonnoyent de fa doctrine.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 34, 'Or quand les Pharifiens entendirent qu\'il avoit fermé la bouche aux Sadduciens, ils s\'affemblerent d\'un accord.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 35, 'Et l\'un d\'entr\'eux, docteur de la Loi, l\'interrogea en le tentant, &amp; difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 36, 'Maiftre, lequel eft le grand commandement de la Loi?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 37, 'Jefus lui dit, Tu aimeras le Seigneur ton Dieu de tout ton coeur, &amp; de toute ton ame, &amp; de toute ta penfée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 38, 'Celui-ci eft le premier &amp; le grand commandement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 39, 'Et le fecond femblable à icelui eft, Tu aimeras ton prochain comme toi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 40, 'De ces deux commandemens dependent toute la Loi &amp; les Prophetes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 41, 'Et les Pharifiens eftans affemblez, Jefus les interrogea,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 42, 'Difant, Que vous femble-t\'il du Chrift? de qui eft-il Fils? Ils lui refpondirent, de David.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 43, 'Et il leur dit, Comment donc David l\'appelle-t\'il en Efprit, Seigneur? difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 44, 'Le Seigneur a dit à mon Seigneur, Sieds-toi à ma dextre, jufques à ce que j\'aye mis tes ennemis pour le marchepied de tes pieds.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 45, 'Si donc David l\'appelle Seigneur, comment eft-il fon Fils?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 46, 'Et nul ne lui pouvoit refpondre un feul mot: &amp; perfonne ne l\'ofa plus interroger depuis ce jour-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 1, 'Alors Jefus parla aux troupes, &amp; à fes difciples,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 2, 'Difant, Les Scribes &amp; les Pharifiens font affis en la chaire de Moyfe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 3, 'Toutes les chofes donc qu\'ils vous diront que vous gardiez, gardez-les &amp; les faites: mais ne faites point felon leurs oeuvres: car ils difent, &amp; ne font pas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 4, 'Car ils lient enfemble des fardeaux pefans &amp; infuportables, &amp; les mettent fur les efpaules des hommes: mais ils ne veulent point remuër de leur doigt.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 5, 'Et font toutes leurs oeuvres pour eftre regardez des hommes: car ils eflargiffent leurs phylacteres, &amp; allongent les franges de leurs veftemens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 6, 'Et aiment les premieres places aux banquets, &amp; les premieres feances aux fynagogues:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 7, 'Et les falutations aux marchez, &amp; d\'eftre appellez des hommes, Noftre maiftre, Noftre maiftre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 8, 'Mais vous, ne foyez point appellez, Noftre maiftre: car un feul eft voftre docteur, affavoir Chrift: &amp; quant à vous, vous eftes tous freres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 9, 'Et n\'appellez aucun en la terre voftre pere: car un feul eft voftre Pere, affavoir celui qui eft és cieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 10, 'Et ne foyez point appellez docteurs: car un feul eft voftre docteur, affavoir Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 11, 'Mais que celui qui eft le plus grand entre vous, foit voftre ferviteur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 12, 'Car quiconque s\'élevera fera abaiffé, &amp; quiconque s\'abaiffera, fera élevé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 13, 'Mais mal-heur fur vous, Scribes &amp; Pharifiens hypocrites, d\'autant que vous fermez le royaume des cieux au devant des hommes: car vous-mefmes n\'y entrez point, &amp; ne fouffrez point que ceux qui y veulent entrer, y entrent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 14, 'Mal-heur fur vous, Scribes &amp; Pharifiens hypocrites: car vous mangez entierement les maifons des veuves, voire en faifant femblant de prier beaucoup: pour cela vous en recevrez plus grande condamnation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 15, 'Mal-heur fur vous, Scribes &amp; Pharifiens hypocrites: car vous tournoyez la mer &amp; la terre, pour faire un profelyte: &amp; quand il l\'eft devenu, vous le rendez fils de la gehenne au double plus que vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 16, 'Mal-heur fur vous, conducteurs aveugles, qui dites, Quiconque aura juré par le temple, ce n\'eft rien: mais qui aura juré par l\'or du temple, il eft redevable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 17, 'Fols &amp; aveugles: car lequel eft le plus grand, l\'or, ou le temple qui fanctifie l\'or?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 18, 'Et quiconque, dites-vous, aura juré par l\'autel, ce n\'eft rien: mais qui aura juré par le don qui eft fur icelui, il eft redevable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 19, 'Fols &amp; aveugles: car lequel eft le plus grand, le don, ou l\'autel, qui fanctifie le don?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 20, 'Celui donc qui jure par l\'autel, il jure par icelui, &amp; par toutes les chofes qui font deffus icelui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 21, 'Et quiconque jure par le temple, il jure par icelui, &amp; par celui qui y habite.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 22, 'Et quiconque jure par le ciel, il jure par le trône de Dieu, &amp; par celui qui eft affis deffus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 23, 'Mal-heur fur vous, Scribes &amp; Pharifiens hypocrites: car vous difmez la mente, l\'anet &amp; le cumin, &amp; laiffez les chofes de la Loi qui font de plus grande importance, affavoir le jugement, la mifericorde, &amp; la loyauté: il faloit faire ces chofes-ici, &amp; ne laiffer point celles-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 24, 'Conducteurs aveugles, qui coulez le moucheron, &amp; engloutiffez le chameau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 25, 'Mal-heur fur vous, Scribes &amp; Pharifiens hypocrites: car vous nettoyez le dehors de la coupe &amp; du plat: mais dedans ils font pleins de rapine &amp; d\'excez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 26, 'Pharifien aveugle, nettoye premierement le dedans de la coupe &amp; du plat, afin que le dehors auffi d\'iceux foit net.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 27, 'Mal-heur fur vous, Scribes &amp; Pharifiens hypocrites: car vous eftes femblables aux fepulchres blanchis, qui apparoiffent beaux par dehors, mais au dedans ils font tous pleins d\'offemens de morts, &amp; de toute ordure.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 28, 'Pareillement auffi vous montrez-vous juftes par dehors aux hommes, mais au dedans vous eftes pleins d\'hypocrifie &amp; d\'iniquité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 29, 'Mal-heur fur vous, Scribes &amp; Pharifiens hypocrites: car vous baftiffez les tombeaux des Prophetes, &amp; reparez les fepulcres des juftes:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 30, 'Et dites, Si nous euffions efté és jours de nos peres, nous n\'euffions point efté leurs compagnons au fang des Prophetes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 31, 'Ainfi vous eftes tefmoins contre vous-mefmes: que vous eftes enfans des meurtriers des Prophetes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 32, 'Vous donc auffi achevez de remplir la mefure de vos peres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 33, 'Serpens, engeances de viperes, comment éviterez-vous le jugement de la gehenne?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 34, 'Pource voici, je vous envoye des Prophetes, &amp; des Sages, &amp; des Scribes: &amp; vous en tuërez, &amp; en crucifierez, &amp; en fouëtterez en vos Synagogues, &amp; les pourfuivrez de ville en ville:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 35, 'Afin que vienne fur vous tout le fang jufte qui a efté refpandu en la terre, depuis le fang d\'Abel le jufte, jufques au fang de Zacharie fils de Barachie, lequel vous avez mis à mort entre le temple &amp; l\'autel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 36, 'En verité je vous dis, que toutes ces chofes viendront fur cette generation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 37, 'Jerufalem, Jerufalem, qui tuës les Prophetes, &amp; qui lapides ceux qui te font envoyez: combien de fois ai-je voulu raffembler tes enfans, comme la poulle raffemble fes pouffins fous fes ailes, &amp; vous ne l\'avez point voulu?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 38, 'Voici, voftre maifon s\'en va vous eftre laiffée deferte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 39, 'Car je vous dis, que dés cette heure vous ne me verrez plus, jufqu\'à ce que vous difiez, Benit foit celui qui vient au nom du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 1, 'Alors Jefus fortant, s\'en alloit hors du temple: &amp; fes difciples vinrent pour lui montrer les baftimens du temple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 2, 'Et Jefus leur dit, Ne voyez-vous pas toutes ces chofes? En verité je vous dis, qu\'il ne fera ici laiffé pierre fur pierre qui ne foit demolie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 3, 'Et lui eftant affis fur la montagne des Oliviers, les difciples vinrent à lui à part, difans, Di nous quand ces chofes adviendront, &amp; quel fera le figne de ton advenement, &amp; de la fin du monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 4, 'Et Jefus refpondant leur dit, Prenez garde que nul ne vous feduife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 5, 'Car plufieurs viendront en mon Nom, difans, Je fuis le Chrift: &amp; en feduiront plufieurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 6, 'Or vous avez à ouïr des guerres &amp; des bruits de guerres: prenez garde que vous ne foyez troublez: car il faut qur toutes ces chofes adviennent: mais ce ne fera pas encore la fin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 7, 'Car nation s\'élevera contre nation, &amp; royaume contre royaume: &amp; il y aura des famines, &amp; des peftilences, &amp; des tremblemens de terre en tous lieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 8, 'Mais toutes ces chofes font des commencemens de douleurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 9, 'Alors ils vous livreront pour eftre affligez, &amp; vous tuëront: &amp; vous ferez haïs de toutes nations, à caufe de mon Nom.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 10, 'Alors plufieurs auffi feront fcandalizez: &amp; fe trahiront l\'un l\'autre, &amp; fe haïront l\'un l\'autre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 11, 'Davantage, plufieurs faux prophetes s\'éleveront, &amp; en feduiront plufieurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 12, 'Et parce que l\'iniquité fera multipliée, la charité de plufieurs fe refroidira.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 13, 'Mais qui aura perfeveré jufques à la fin, celui-là fera fauvé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 14, 'Et cét Evangile du royaume fera prefché en toute la terre habitable, en tefmoignage à toutes nations: &amp; alors viendra la fin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 15, 'Quand donc vous verrez l\'abomination de la defolation, qui eft dite par Daniel le Prophete, eftre eftablie au lieu faint, (qui lit l\'entende.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 16, 'Alors que ceux qui feront en Judée, s\'enfuyent aux montagnes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 17, 'Et que celui qui fera fur la maifon, ne defcende point, pour emporter aucune chofe de fa maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 18, 'Et que celui qui eft au champ, ne retourne point en arriere, pour emporter fes habillemens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 19, 'Mais malheur fur les femmes enceintes, &amp; fur celles qui allaiteront en ces jours-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 20, 'Or priez que voftre fuite ne foit point en hyver, ni au jour du Sabbat.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 21, 'Car il y aura alors une grande affliction, telle qu\'il n\'y en a point eu de femblable, depuis le commencement du monde, jufques à maintenant, &amp; n\'y en aura.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 22, 'Que fi ces jours-là n\'euffent efté abbregez, nulle perfonne ne feroit fauvée: mais à caufe des éleus, ces jours-là feront abbregez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 23, 'Alors fi quelqu\'un vous dit, Voici, le Chrift eft ici, ou il eft là, ne le croyez point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 24, 'Car de faux Chrifts &amp; de faux Prophetes s\'éleveront, &amp; feront de grands fignes &amp; miracles: voire pour feduire les éleus mefmes, s\'il eftoit poffible.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 25, 'Voici, je vous l\'ai predit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 26, 'Si donc on vous dit, Voici, il eft au defert, ne fortez point: Voici, il eft és cabinets, ne le croyez point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 27, 'Car comme l\'éclair fort d\'Orient, &amp; fe montre jufques en Occident, autant en fera-t\'il auffi de l\'advenement du Fils de l\'homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 28, 'Car où fera le corps mort, là s\'affembleront auffi les aigles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 29, 'Or incontinent apres l\'affliction de ces jours-là, le foleil deviendra obfcur, &amp; la lune ne donnera point fa lumiere, &amp; les eftoiles cherront du ciel, &amp; les vertus des cieux feront ébranlées.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 30, 'Et alors le figne du Fils de l\'homme apparoiftra au ciel, &amp; alors auffi toutes les lignées de la terre fe lamenteront en fe frappant la poictrine, &amp; verront le Fils de l\'homme venir és nuées du ciel, avec puiffance &amp; grande gloire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 31, 'Lequel envoyera fes Anges avec un grand fon de trompette, qui affembleront en un fes éleus des quatre vents, depuis l\'un des bouts des cieux jufques à l\'autre bout.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 32, 'Or apprenez la fimilitude du figuier. Quand déja fon rameau eft en féve é qu\'il jette des fueilles, vous connoiffez que l\'efté eft prochain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 33, 'Vous auffi pareillement, quand vous verrez toutes ces chofes, fçachez qu\'il eft prochain à la porte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 34, 'En verité je vous dis, que cette generation ne paffera point, que toutes ces chofes-là ne foyent advenuës.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 35, 'Le ciel &amp; la terre pafferont: mais mes paroles ne pafferont point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 36, 'Or quant à ce jour-là, &amp; à l\'heure, nul ne le fçait, non pas mefmes les Anges du ciel, mais mon Pere feul.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 37, 'Mais comme eftoyent les jours de Noé, ainfi en prendra-t\'il de l\'advenement du Fils de l\'homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 38, 'Car comme ils eftoyent és jours de devant le deluge, mangeans &amp; beuvans, fe marians &amp; baillans en mariage, jufques au jour que Noé entra en l\'arche:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 39, 'Et n\'apperceurent point le deluge, jufques à ce qu\'il fuft venu, &amp; les emporta tous: ainfi en prendra-t\'il de l\'advenement du Fils de l\'homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 40, 'Alors deux feront au champ: l\'un fera pris, &amp; l\'autre laiffé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 41, 'Deux moudront au moulin: l\'une fera prife, &amp; l\'autre laiffée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 42, 'Veillez donc: car vous ne fçavez à quelle heure doit venir voftre Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 43, 'Mais fçachez cela, que fi le pere de famille fçavoit à quelle veille de la nuict le larron doit venir, il veilleroit, &amp; ne laifferoit point percer fa maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 44, 'Partant vous auffi foyez prefts: car le Fils de l\'homme viendra à l\'heure que vous n\'y penferez point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 45, 'Qui eft donc le ferviteur fidele &amp; prudent, que fon maiftre a eftabli fur la compagnie de fes ferviteurs, pour leur bailler la nourriture en temps?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 46, 'Bien-heureux eft ce ferviteur-là, que fon maiftre trouvera ainfi faifant, quand il viendra.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 47, 'En verité je vous dis, qu\'il l\'eftablira fur tous fes biens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 48, 'Que fi ce mauvais ferviteur-là dit en fon coeur, Mon maiftre met longtemps à venir:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 49, 'Et qu\'il fe prenne à bettre fes compagnons de fervice, &amp; mefmes à boire &amp; à manger avec les yvrognes:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 50, 'Le maiftre de ce ferviteur-là viendra au jour qu\'il ne l\'attend point, &amp; à l\'heure qu\'il ne fçait point::');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 51, 'Et le feparera, &amp; le mettra au rang des hypocrites: là il y aura pleur &amp; grincement de dents.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 1, 'Alors le royaume des cieux fera femblable à dix vierges, lefquelles ayans prix leurs lampes, s\'en allerent au devant de l\'efpoux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 2, 'Or il y en avoit d\'icelles cinq fages, &amp; cinq folles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 3, 'Les folles, en prenant leurs lampes n\'avoyent point pris d\'huile avec elles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 4, 'Mais les fages avoyent pris de l\'huile en leurs vaiffeaux avec leurs lampes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 5, 'Et comme l\'efpoux tardoit à venir, elles fommeillerent toutes, &amp; s\'endormirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 6, 'Or à la minuict il fe fit un cri, difant, Voici, l\'efpoux vient, fortez au devant de lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 7, 'Alors ces vierges-là fe leverent toutes, &amp; apprefterent leurs lampes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 8, 'Et les folles dirent aux fages, Baillez-nous de voftre huile: car nos lampes s\'efteignent:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 9, 'Mais les fages refpondirent, difans, Nous ne pouvons, de peur que nous n\'en ayons point affez pour nous &amp; pour vous: mais allez pluftoft vers ceux qui en vendent &amp; en achetez pour vous-mefmes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 10, 'Or pendant qu\'elles en alloyent acheter, l\'efpoux vint: &amp; celles qui eftoyent preftes entrerent avec lui aux nopces, &amp; la porte fut fermée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 11, 'Puis apres les autres vierges vinrent auffi, difans, Seigneur, Seigneur, ouvre nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 12, 'Mais il refpondit, &amp; dit, En verité je vous dis, que je ne vous connois point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 13, 'Veillez donc: car vous ne fçavez ni le jour ni l\'heure, en laquelle le Fils de l\'homme viendra.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 14, 'Car c\'eft comme un homme, lequel s\'en allant dehors, appella fes ferviteurs, &amp; leur commit fes biens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 15, 'Et il bailla à l\'un cinq talens, &amp; à l\'autre deux, &amp; à l\'autre un: à chacun felon fa portée: &amp; incontinent s\'en alla dehors.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 16, 'Or celui qui avoit receu les cinq talens, s\'en alla, &amp; trafiqua d\'eux, &amp; en fit cinq autres talens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 17, 'Semblablement auffi celui qui avoit receu les deux, en gagna auffi deux autres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 18, 'Mais celui qui en avoit receu un, s\'en alla, &amp; l\'enfoüit en terre, &amp; cacha l\'argent de fon maiftre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 19, 'Or long-temps apres, le maiftre de ces ferviteurs-là vint, &amp; conta avec eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 20, 'Alors celui qui avoit receu les cinq talens, vint, &amp; prefenta cinq autres talens, difant, Seigneur, tu m\'as commis cinq talens: voici, j\'en ai gagné cinq autres par deffus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 21, 'Et fon feigneur lui dit, Cela va bien, bon ferviteur &amp; loyal: tu as efté loyal en peu de chofe, je t\'eftablirai fur beaucoup: entre en la joye de ton feigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 22, 'Puis celui qui avoit receu les deux talens, vint, &amp; dit, Seigneur, tu m\'as commis deux talens: voici, j\'en ai gagné deux autres par deffus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 23, 'Et fon feigneur lui dit, Cela va bien, bon ferviteur &amp; loyal: tu as efté loyal en peu de chofe je t\'eftablirai fur beaucoup: entre en la joye de ton feigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 24, 'Mais celui qui n\'avoit receu qu\'un talent, vint, &amp; dit, Seigneur, je connoiffois que tu eftois un homme rude, moiffonnant là où tu n\'as point femé, &amp; affemblant là où tu n\'as point épars:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 25, 'C\'eft pourquoi, craignant, je m\'en fuis allé, &amp; ai caché ton talent en terre: voici, tu as ce qui eft tien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 26, 'Et fon feigneur répondant, lui dit, Mauvais ferviteur &amp; lafche, tu fçavois que je moiffonnois là où je n\'ai point femé, &amp; affemblois là où je n\'ai point épars.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 27, 'Il faloit donc que tu baillaffes mon argent aux banquiers, &amp; eftant venu j\'euffe receu le mien avec ufure.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 28, 'Oftez-lui donc le talent, &amp; le donnez à celui qui a les dix talens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 29, 'Car à chacun qui aura, il fera donné, &amp; il en aura tant plus: mais à celui qui n\'a rien, cela mefmes qu\'il a, lui fera ofté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 30, 'Jettez donc le ferviteur inutile és tenebres de dehors: là il y aura pleur &amp; grincement de dents.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 31, 'Or quand le Fils de l\'homme fera venu en fa gloire, &amp; tous les faints Anges avec lui, alors il fe féera fur le thrône de fa gloire:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 32, 'Et toutes les nations feront affemblées devant lui, &amp; il les feparera les uns d\'avec les autres: comme le berger fepare les brebis d\'avec les boucs:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 33, 'Et il mettra les brebis à fa droite, &amp; les boucs à fa gauche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 34, 'Alors le Roi dira à ceux qui feront à fa droite, Venez, les benits de mon Pere, poffedez en heritage le royaume qui vous a efté preparé dés la fondation du monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 35, 'Car j\'ai eu faim, &amp; vous m\'avez donné à manger: j\'ai eu foif, &amp; vous m\'avez donné à boire: j\'eftois eftranger, &amp; vous m\'avez recüeilli:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 36, 'J\'eftois nud, &amp; vous m\'avez vétu: j\'eftois malade, &amp; vous m\'avez vifité: j\'eftois en prifon, &amp; vous eftes venus vers moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 37, 'Alors les juftes lui répondront, difans, Seigneur, quand eft-ce que nous t\'avons veu avoir faim, &amp; que nous t\'avons donné à manger? ou avoir foif, &amp; que nous t\'avons donné à boire?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 38, 'Et quand eft-ce que nous t\'avons veu eftranger, &amp; que nous t\'avons recüeilli? ou nud, &amp; que nous t\'avons veftu?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 39, 'Ou quand eft-ce que nous t\'avons veu malade, ou en prifon, &amp; que nous fommes venus vers toi?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 40, 'Et le Roi répondant, leur dira, En verité je vous dis, qu\'entant que vous l\'avez fait à l\'un de ces plus petits de mes freres, vous me l\'avez fait.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 41, 'Alors il dira auffi à ceux qui feront à fa gauche, Maudits, départez-vous de moi au feu eternel, qui eft preparé au Diable &amp; à fes Anges.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 42, 'Car j\'ai eu faim, &amp; vous ne m\'avez point donné à manger: j\'ai eu foif, &amp; vous ne m\'avez point donné à boire:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 43, 'J\'eftois eftranger, &amp; vous ne m\'avez point recüeilli: nud, &amp; vous ne m\'avez point veftu: malade &amp; en prifon, &amp; vous ne m\'avez point vifité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 44, 'Alors auffi ceux-là lui répondront, difans, Seigneur, quand eft-ce que nous t\'avons veu avoir faim, ou avoir foif, ou eftre eftranger, ou nud, ou malade, ou en prifon, &amp; que nous ne t\'avons point fubvenu?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 45, 'Alors il leur répondra, difant, En verité je vous dis, qu\'entant que vous ne l\'avez point fait à l\'un de ces plus petits, vous ne me l\'avez point fait auffi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 46, 'Et ceux-ci s\'en iront aux peines eternelles: mais les juftes s\'en iront à la vie eternelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 1, 'Et il advint que quand Jefus euft achevé tous ces propos, il dit à fes difciples,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 2, 'Vous fçavez que dans deux jours la folemnité de la Pafques fe fait, &amp; que le Fils de l\'homme s\'en va eftre livré pour eftre crucifié.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 3, 'Alors les principaux Sacrificateurs, &amp; les Scribes, &amp; les Anciens du peuple, s\'affemblerent en la falle du fouverain Sacrificateur, nommé Caïphe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 4, 'Et tinrent confeil de faifir Jefus par fineffe, &amp; de le mettre à mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 5, 'Mais ils difoyent, Non point durant la fefte, de peur qu\'il fe faffe tumulte entre le peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 6, 'Et comme Jefus eftoit à Bethanie, en la maifon de Simon, dit le lepreux:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 7, 'Une femme eftoit venuë à luy, ayant une boite d\'oignement de grand prix, &amp; l\'avoit répandu fur la tefte d\'icelui eftant à table.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 8, 'Et fes difciples voyans cela, furent indignez, difans, A quoy fert ce degaft?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 9, 'Car cet oignement pouvoit eftre vendu beaucoup, &amp; eftre donné aux pauvres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 10, 'Mais Jefus appercevant cela, leur dit, Pourquoi donnez-vous de la fafcherie à cette femme? car elle a fait un bon acte envers moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 11, 'Parce que vous aurez toûjours les pauvres avec vous: mais vous ne m\'aurez point toûjours.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 12, 'Car ce qu\'elle a répandu cet oignement fur mon corps, elle l\'a fait pour l\'appareil de ma fepulture.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 13, 'En verité je vous dis, qu\'en quelque lieu que fera prefché cet Evangile en tout le monde, cela auffi qu\'elle a fait fera recité en memoire d\'elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 14, 'Alors l\'un des douze qui eftoit appellé Judas Ifcariot, s\'en alla vers les principaux Sacrificateurs:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 15, 'Et leur dit, Que me voulez-vous donner, &amp; je vous le livrerai? Et ils lui conterent trente pieces d\'argent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 16, 'Et dés lors il cherchoit opportunité pour le livrer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 17, 'Or le premier jour de la fefte des pains fans levain, les difciples vinrent à Jefus, lui difans, Où veux-tu que nous t\'appreftions à manger l\'agneau de Pafque?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 18, 'Et il refpondit, Allez en la ville vers un tel, &amp; lui dites, Le Maiftre dit, mon temps eft prés, je ferai la Pafque chez toi avec mes difciples.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 19, 'Et les difciples firent comme Jefus leur avoit ordonné, &amp; preparerent la Pafque.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 20, 'Quand donc le foir fut venu, il fe mit à table avec les douze.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 21, 'Et comme ils mangeoyent, il dit, En verité je vous dit, que l\'un de vous me trahira.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 22, 'Alors ils furent fort contriftez, &amp; chacun d\'eux fe prit à lui dire, Seigneur eft-ce moi?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 23, 'Mais il leur répondit, &amp; dit, Celui qui a mis la main au plat pour tremper avec moi, c\'eft celui qui me trahira.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 24, 'Or quant au Fils de l\'homme, il s\'en va, felon qu\'il eft écrit de lui: mais malheur à cet homme-là, par lequel le Fils de l\'homme eft trahi: il euft efté bon à cet homme-là de n\'eftre point né.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 25, 'Et Judas qui le trahiffoit, répondant, dit, Maiftre, eft-ce moi? Il lui dit, Tu l\'as dit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 26, 'Et comme ils mangeoyent, Jefus prit le pain: &amp; apres qu\'il eût benit, il le rompit &amp; le bailla à fes difciples, &amp; dit, Prenez, mangez: ceci eft mon corps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 27, 'Puis ayant pris la coupe, &amp; rendu graces, il la leur bailla, difant, Beuvez-en tous:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 28, 'Car ceci eft mon fang, le fang du nouveau Teftament, lequel eft répandu pour plufieurs en remiffion des pechez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 29, 'Or je vous dis, que depuis cette heure je ne boirai point de ce fruit de vigne-ici, jufques à ce jour-là, que je le boirai nouveau avec vous, au royaume de mon Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 30, 'Et quand ils eurent chanté le cantique, ils s\'en allerent en la montagne des Oliviers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 31, 'Alors Jefus leur dit, Vous ferez tous fcandalizez en moi cette nuict-ici: car il eft écrit, Je frapperai le berger, &amp; les brebis du troupeau feront éparfes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 32, 'Mais apres que je ferai reffufcité, j\'irai devant vous en Galilée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 33, 'Et Pierre prenant la parole, lui dit, Quand bien tous feroyent fcandalizez en toi, je ne ferai jamais fcandalizé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 34, 'Jefus lui dit, En verité je te dis, qu\'en cette propre nuit, devant que le coq chante, tu me renieras par trois fois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 35, 'Pierre lui dit, Quand mefmes il me faudroit mourir avec toi, fi eft-ce que je ne te renierai point. Autant auffi en dirent tous les difciples.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 36, 'Alors Jefus s\'en vint avec eux en un lieu dit Gethfemané: &amp; dit à fes difciples, Séez-vous ici, jufques à ce que je m\'en aille, &amp; que je prie là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 37, 'Alors il prit avec foi Pierre &amp; les deux fils de Zebedée, &amp; commença à eftre contrifté, &amp; à eftre fort angoiffé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 38, 'Alors il leur dit, Mon ame eft faifie de trifteffe jufques à la mort: demeurez ici, &amp; veillez avec moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 39, 'Et s\'en allant un peu plus outre, il fe jetta en terre fur fa face, priant &amp; difant, Mon Pere, s\'il eft poffible, que cette coupe paffe arriere de moi: toutefois non point comme je veux, mais comme tu veux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 40, 'Puis il vint à fes difciples, &amp; les trouva dormans, &amp; dit à Pierre, Eft-il poffible que vous n\'ayez peu veiller une heure avec moi?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 41, 'Veillez &amp; priez, que vous n\'entriez en tentation: car quant à l\'efprit il eft prompt, mais la chair eft foible.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 42, 'Derechef il s\'en alla pour la feconde fois, &amp; pria, difant, Mon Pere, s\'il n\'eft pas poffible que cette coupe paffe arriere de moi, fans que je la boive, ta volonté foit faite.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 43, 'Puis il revint, &amp; les trouva derechef dormans: car leurs yeux eftoyent appefantis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 44, 'Et les ayant laiffez, il s\'en alla derechef, &amp; pria pour la troifiéme fois, difant les mefmes paroles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 45, 'Alors il vint à fes difciples, &amp; leur dit, Dormez d\'orefenavant, &amp; vous repofez: voici, l\'heure eft prochaine, &amp; le Fils de l\'homme s\'en va eftre livré és mains des méchans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 46, 'Levez-vous, allons: voici, celui qui me trahit s\'approche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 47, 'Et comme il parloit encore, voici, Judas l\'un des douze, vint, &amp; une grande troupe avec lui, avec des efpées &amp; des baftons, de par les principaux Sacrificateurs &amp; les Anciens du peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 48, 'Or celui qui le trahiffoit, leur avoit donné un fignal, difant, Celui que je baiferai, c\'eft lui: faififfez-le.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 49, 'Et incontinent s\'approchant de Jefus, il lui dit, Maiftre, bien te foit: &amp; le baifa.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 50, 'Alors Jefus lui dit, Compagnon, pour quel fujet es-tu ici? Alors ils s\'approcherent, &amp; jetterent les mains fur Jefus, &amp; le faifirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 51, 'Et voici l\'un de ceux qui eftoyent avec Jefus, avançant la main tira fon efpée, &amp; en frappa le ferviteur du fouverain Sacrificateur, &amp; lui emporta l\'oreille.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 52, 'Alors Jefus lui dit, Remets ton efpée en fon lieu: car tous ceux qui auront pris l\'efpée, periront par l\'efpée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 53, 'Penfes-tu que je ne puiffe maintenant prier mon Pere, qui me bailleroit prefentement plus de douze legions d\'Anges?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 54, 'Comment donc feroyent accomplies les Ecritures, qui difent qu\'il faut qu\'il arrive ainfi?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 55, 'A ce mefme inftant Jefus dit aux troupes, Vous eftes fortis avec des efpées &amp; des baftons, comme apres un brigand, pour me prendre: j\'eftois tous les jours affis entre vous, enfeignant au temple, &amp; vous ne m\'avez point faifi?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 56, 'Mais tout ceci eft advenu, afin que les Ecritures des Prophetes foyent accomplies. Alors tous les difciples l\'abandonnerent, &amp; s\'enfuïrent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 57, 'Mais ceux qui avoyent faifi Jefus, l\'amenerent à Caïphe le fouverain Sacrificateur, où les Scribes &amp; les Anciens eftoyent affemblez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 58, 'Et Pierre le fuivit de loin, jufques à la cour du fouverain Sacrificateur: &amp; eftant entré dedans, il s\'affit avec les officiers, pour voir quelle en feroit la fin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 59, 'Or les principaux Sacrificateurs, &amp; les Anciens, &amp; tout le confeil, cherchoyent de faux tefmoignages contre Jefus, afin qu\'ils le miffent à mort:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 60, 'Et n\'en trouvoyent point: &amp; bien que plufieurs faux tefmoins fuffent venus, ils n\'en trouverent point de propres. Mais à la fin deux faux tefmoins s\'approcherent,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 61, 'Et dirent, Celui-ci a dit, Je puis défaire le temple de Dieu, &amp; le rebaftir en trois jours.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 62, 'Alors le fouverain Sacrificateur fe leva, &amp; lui dit, Ne répons-tu rien? Qu\'eft-ce que témoignent ceux-ci contre toi?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 63, 'Et Jefus fe teut. Alors le fouverain Sacrificateur prenant la parole, lui dit, Je t\'adjure par le Dieu vivant, que tu nous dies fi tu es le Chrift, le Fils de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 64, 'Jefus lui dit, Tu l\'as dit. Qui plus eft, je vous dis, Ci-apres vous verrez le Fils de l\'homme affis à la dextre de la vertu de Dieu, &amp; venant és nuées du ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 65, 'Alors le fouverain Sacrificateur déchira fes veftemens, difant, Il a blafphemé: qu\'avons-nous plus à faire de témoins? Voici, vous avez ouï maintenant fon blafpheme. Que vous en femble?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 66, 'Eux répondans dirent, Il eft coupable de mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 67, 'Alors ils lui cracherent au vifage, &amp; le foufleterent: &amp; les autres lui donnoyent des coups de leurs verges.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 68, 'Difans, Chrift prophetize-nous, qui eft celui qui t\'a frapé?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 69, 'Or Pierre étoit affis dehors en la cour: &amp; une fervante s\'adreffa à lui, difant: Tu eftois auffi avec Jefus le Galiléen.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 70, 'Mais il le nia devant tous, difant, Je ne fçai ce que tu dis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 71, 'Et comme il fortoit dehors au portail, une autre fervante le vid: &amp; dit à ceux qui eftoyent là, Celui-ci auffi eftoit avec Jefus le Nazarien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 72, 'Et derechef il le nia avec ferment, difant, Je ne connois point cet homme-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 73, 'Et un peu apres, ceux qui fe trouvoyent là, vinrent, &amp; dirent à Pierre: Vrayement tu es auffi de ceux-là, car ton langage te donne à connoiftre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 74, 'Alors il fe prit à fe maudire, &amp; à jurer, difant, Je ne connois point cet homme-là. Et incontinent le coq chanta.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 75, 'Alors Pierre eut fouvenance de la parole de Jefus, qui lui avoit dit, Devant que le coq chante, tu me renieras par trois fois. Ainfi il fortit dehors, &amp; pleura amerement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 1, 'Puis quand le matin fut venu, tous les principaux Sacrificateurs &amp; les Anciens du peuple tinrent confeil contre Jefus, pour le mettre à mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 2, 'A raifon dequoi ils l\'amenerent lié, &amp; le livrerent à Ponce Pilate Gouverneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 3, 'Alors Judas qui l\'avoit trahi, voyant qu\'il eftoit condamné, fe repentit, &amp; reporta les trente pieces d\'argent aux principaux Sacrificateurs, &amp; aux Anciens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 4, 'Difant, J\'ai peché en trahiffant le fang innocent. Mais ils lui dirent, Que nous importe? tu y adviferas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 5, 'Alors apres avoir jetté les pieces d\'argent dans le temple, il fe retira, &amp; s\'en alla, &amp; s\'étrangla.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 6, 'Et les principaux Sacrificateurs ayant pris les pieces d\'argent, dirent, Il n\'eft pas permis de les mettre au trefor car c\'eft un prix de fang.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 7, 'Et apres qu\'ils eurent pris confeil, ils en acheterent le champ d\'un potier, pour la fepulture des eftrangers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 8, 'Et pour cette caufe ce champ-là a efté appellé jufques à ce jourd\'hui, Le champ de fang.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 9, 'Alors fut accompli ce dont il avoit efté parlé par Jeremie le Prophete, difant, Et ils ont pris trente pieces d\'argent, le prix de celui qui a efté apprecié, lequel ceux d\'entre les enfans d\'Ifraël ont apprecié:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 10, 'Et les ont baillées pour acheter le champ d\'un potier, felon ce que le Seigneur m\'avoit ordonné.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 11, 'Or Jefus fut prefenté devant le Gouverneur: &amp; le Gouverneur l\'interrogea, difant, Es-tu le Roi des Juifs? Jefus lui dit, Tu le dis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 12, 'Et comme il eftoit accufé par les principaux Sacrificateurs &amp; les Anciens, il ne répondoit rien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 13, 'Alors Pilate lui dit, N\'ois-tu pas combien ils amenent de témoignages contre toi?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 14, 'Mais il ne lui refpondit rien à une feule parole: tellement que le Gouverneur s\'étonnoit grandement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 15, 'Or le Gouverneur avoit de couftume de relafcher au jour de la fefte un prifonnier au peuple: affavoir celui qu\'ils vouloyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 16, 'Et alors il y avoit un prifonnier notable, dit Barrabas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 17, 'Quand donc ils furent affemblez, Pilate leur dit, Lequel voulez-vous que je vous relâche, Barrabas, ou Jefus qu\'on appelle Chrift?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 18, '(Car il fçavoit bien qu\'ils l\'avoyent livré par envie.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 19, 'Et comme il eftoit affis au fiege judicial, fa femme envoya vers lui, difant, N\'aye rien à faire avec ce jufte-là: car j\'ai aujourd\'hui beaucoup fouffert en fongeant à caufe de lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 20, 'Alors les principaux Sacrificateurs, &amp; les Anciens perfuaderent aux troupes qu\'ils demandaffent Barrabas, &amp; qu\'ils fiffent perir Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 21, 'Et le Gouverneur prenant la parole, leur dit, Lequel des deux voulez-vous que je relafche? Ils dirent, Barrabas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 22, 'Mais Pilate leur dit, Que ferai-je donc de Jefus qu\'on appelle Chrift? Tous lui dirent, Qu\'il foit crucifié.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 23, 'Et le Gouverneur leur dit, Mais quel mal a-t\'il fait? Alors ils crierent d\'autant plus, difans, Qu\'il foit crucifié.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 24, 'Pilate donc voyant qu\'il ne gagnoit rien, mais que le tumulte s\'eflevoit tant plus, prit de l\'eau, &amp; lava fes mains devant le peuple, difant, Je fuis innocent du fang de ce jufte-ici: vous y adviferez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 25, 'Et tout le peuple refpondant, dit, Son fang foit fur nous &amp; fur nos enfans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 26, 'Alors il leur relafcha Barrabas: &amp; leur livra Jefus, apres l\'avoir fait foüetter, afin qu\'il fuft crucifié.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 27, 'Alors les foldats du Gouverneur amenerent Jefus au Pretoire, &amp; affemblerent devant lui toute la bande.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 28, 'Et l\'ayans deveftu, mirent fur lui un manteau d\'écarlate.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 29, 'Et plierent une couronne d\'épines, &amp; la mirent fur fa tefte, &amp; un rofeau en fa main droite: &amp; s\'agenoüillans devant lui, fe mocquoyent de lui, difans, Bien te foit, Roi des Juifs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 30, 'Et apres avoir craché contre lui, ils prirent le rofeau, &amp; en frapoyent fa tefte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 31, 'Puis s\'eftans mocquez de lui, ils lui dépoüillerent le manteau, &amp; le veftirent de fes veftemens, &amp; l\'amenerent pour eftre crucifié.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 32, 'Et comme ils fortoyent, ils trouverent un Cyrenien, nommé Simon: lequel ils contraignirent de porter la croix d\'icelui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 33, 'Et eftans arrivez en la place dite Golgota, (qui vaut autant à dire que la place du Teft)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 34, 'Ils lui donnerent à boire du vinaigre meflé avec du fiel: &amp; quand il en euft goufté, il n\'en voulut point boire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 35, 'Et apres l\'avoir crucifié, ils partagerent fes veftemens, en jettant le fort, afin que fuft accompli ce qui eft dit par le Prophete, Ils fe font partagez mes veftemens, &amp; ont jetté le fort fur mon faye.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 36, 'Et eftans affis, ils le gardoyent là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 37, 'Ils mirent auffi au deffus de fa tefte fon dicton par écrit en ces mots, CELUI-CI EST JESUS LE ROI DES JUIFS.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 38, 'Alors deux brigands furent crucifiez avec lui: l\'un à droite, &amp; l\'autre à gauche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 39, 'Et ceux qui paffoyent prés de là, lui difoyent des outrages, hochans la tefte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 40, 'Et difans, Toi qui desfais le temple, &amp; le rebaftis en trois jours, fauve-toi toi-mefme: fi tu es le Fils de Dieu, defcen de la croix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 41, 'Semblablement auffi les principaux Sacrificateurs, avec les Scribes &amp; les Anciens, fe mocquans, difoyent,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 42, 'Il a fauvé les autres, il ne fe peut fauver foi-mefme: s\'il eft le Roi d\'Ifraël, qu\'il defcende maintenant de la croix, &amp; nous croirons à lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 43, 'Il fe confie en Dieu, qu\'il le delivre maintenant, s\'il l\'a pour agreable: car il a dit, Je fuis le Fils de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 44, 'Cela même lui reprochoyent auffi les brigands qui eftoyent crucifiez avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 45, 'Mais depuis fix heures il fe fit des tenebres fur tout le païs, jufques à neuf heures.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 46, 'Et environ les neuf heures, Jefus s\'efcria à haute voix, difant, Eli, Eli, lamma fabachthani? c\'eft à dire, Mon Dieu, Mon Dieu, pourquoi m\'as-tu abandonné?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 47, 'Et quelques-uns de ceux qui eftoyent là prefens, ayans ouï cela, difoyent, Celui-ci appelle Elie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 48, 'Et incontinent quelqu\'un d\'entr\'eux courut, &amp; prit une efponge: &amp; l\'ayant emplie de vinaigre, la mit à l\'entour d\'un rofeau, &amp; lui en bailla à boire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 49, 'Mais les autres difoyent, Laiffe, voyons fi Elie viendra le fauver.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 50, 'Et alors Jefus ayant crié derechef à haute voix, rendit l\'efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 51, 'Et voila, le voile du temple fe fendit en deux, depuis le haut jufques au bas: &amp; la terre trembla, &amp; les pierres fe fendirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 52, 'Et les fepulcres s\'ouvrirent: &amp; plufieurs corps des Saincts qui avoyent efté endormis fe leverent:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 53, 'Lefquels eftans fortis des fepulcres, apres la refurrection d\'icelui, entrerent en la fainte Cité, &amp; apparurent à plufieurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 54, 'Or le Centenier, &amp; ceux qui eftoyent avec lui gardans Jefus, ayans veu le tremblement de terre, &amp; les chofes qui eftoyent avenuës, eurent grand\'peur, difans, Veritablement celui-ci eftoit le Fils de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 55, 'Il y avoit là auffi plufieurs femmes qui regardoyent de loin, lefquelles avoyent fuivi Jefus depuis la Galilée, en lui fubvenant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 56, 'Entre lefquelles eftoit Marie Magdeleine, &amp; Marie mere de Jaques &amp; de Jofes, &amp; la mere des fils de Zebedée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 57, 'Et le foir eftant venu, il vint un riche homme, qui eftoit d\'Arimathée, nommé Jofeph, qui mefme avoit efté difciple de Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 58, 'Icelui vint à Pilate, &amp; demanda le corps de Jefus. Alors Pilate commanda que le corps fuft rendu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 59, 'Ainfi Jofeph prit le corps, &amp; l\'enveloppa d\'un linceul net.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 60, 'Et le mit en fon fepulcre neuf qu\'il avoit taillé en un roc: &amp; ayant roulé une grande pierre à la porte du fepulcre, il s\'en alla.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 61, 'Et Marie Magdeleine, &amp; l\'autre Marie eftoyent là affifes à l\'endroit du fepulcre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 62, 'Or le lendemain, qui eft apres la preparation du Sabbat, les principaux Sacrificateurs &amp; les Pharifiens s\'affemblerent vers Pilate,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 63, 'Difans, Seigneur, il nous fouvient que ce feducteur-là, quand il vivoit encore, difoit, Dans trois jours je reffufciterai.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 64, 'Commande donc que le fepulcre foit gardé feurement jufques au troifiéme jour: de peur que fes difciples viennent de nuit, &amp; le dérobent, &amp; difent au peuple, Il eft reffufcité des morts: dont le dernier abus fera pire que le premier.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 65, 'Mais Pilate leur dit, Vous avez la garde: allez, &amp; l\'affeurez comme vous l\'entendez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 66, 'Eux donc s\'en allerent, &amp; affeurerent le fepulcre, fcellans la pierre avec des gardes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 1, 'Or au foir du Sabbat, au jour qui devoit luire pour le premier de la femaine, Marie Magdeleine, &amp; l\'autre Marie vinrent voir le fepulcre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 2, 'Et voici, il fe fit un grand tremblement de terre: car l\'Ange du Seigneur defcendit du ciel, &amp; vint, &amp; roula la pierre arriere de la porte du fepulcre, &amp; s\'affit fur elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 3, 'Et fon regard eftoit comme un éclair, &amp; fon veftement comme de la neige.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 4, 'Et les gardes, pour la peur qu\'ils eurent de lui, furent effrayez, &amp; devinrent comme morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 5, 'Mais l\'Ange prenant la parole, dit aux femmes, Vous autres, ne craignez point; car je fçais que vous cherchez Jefus qui a efté crucifié.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 6, 'Il n\'eft point ici: car il eft reffufcité, comme il l\'avoit dit. Venez, voyez le lieu où gifoit le Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 7, 'Et vous en allez promptement: &amp; dites à fes difciples, qu\'il eft reffufcité des morts: &amp; voici, il s\'en va devant vous en Galilée: vous le verrez là: voici, je vous l\'ai dit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 8, 'Alors elles partirent promptement du fepulcre avec crainte &amp; grande joye: &amp; coururent l\'annoncer à fes difciples.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 9, 'Mais comme elles alloyent pour l\'annoncer à fes difciples, voici, Jefus leur vint au devant, difant, Bien vous foit. Et elles s\'approcherent, &amp; empoignerent fes pieds, &amp; l\'adorerent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 10, 'Alors Jefus, leur dit, Ne craignez point: allez, &amp; annoncez à mes freres qu\'ils aillent en Galilée &amp; qu\'ils me verront là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 11, 'Or quand elles furent parties, voici, quelques-uns de la garde vinrent en la ville, &amp; rapporterent aux principaux Sacrificateurs toutes les chofes qui eftoyent avenuës.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 12, 'Alors ils s\'affemblerent avec les Anciens, &amp; apres qu\'ils eurent pris confeil, ils donnerent une bonne fomme d\'argent aux foldats,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 13, 'En leur difant, Dites, Ses difciples font venus de nuit, &amp; l\'ont dérobé comme nous dormions.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 14, 'Et fi le Gouverneur vient à entendre ceci, nous le lui perfuaderons, &amp; vous en mettrons hors de peine.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 15, 'Et eux ayant pris l\'argent, firent ainfi qu\'ils eftoyent inftruits, tellement que ce propos a efté divulgué entre les Juifs, jufques à ce jourd\'hui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 16, 'Mais les onze difciples s\'en allerent en Galilée, en la montagne où Jefus leur avoit ordonné.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 17, 'Et quand ils l\'eurent veu, ils l\'adorerent: mais quelques-uns douterent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 18, 'Et Jefus s\'approchant parla à eux, difant, Toute puiffance m\'eft donnée au ciel &amp; en la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 19, 'Allez donc, &amp; endoctrinez toutes nations, les baptifans au nom du Pere, &amp; du Fils, &amp; du faint Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 20, 'Et les enfeignans de garder tout ce que je vous ay commandé. Et voici, je fuis toûjours avec vous, jufques à la fin du monde. Amen. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 1, 'Le commencement de l\'Evangile de Jefus Chrift Fils de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 2, 'Ainfi qu\'il eft écrit és Prophetes, Voici, j\'envoye mon meffager devant ta face, qui preparera ta voye au devant de toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 3, 'La voix de celui qui crie au defert eft, Preparez le chemin du Seigneur, dreffez fes fentiers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 4, 'Jean baptifoit au defert, &amp; prefchoit le Baptefme de repentance, en remiffion des pechez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 5, 'Et tout le païs de Judée, &amp; ceux de Jerufalem, alloyent vers lui, &amp; eftoyent tous baptifez par lui au fleuve du Jordain, confeffans leurs pechez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 6, 'Or Jean eftoit veftu de poils de chameau, &amp; avoit une ceinture de cuir autour de fes reins, &amp; mangeoit des fauterelles &amp; du miel fauvage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 7, 'Et prefchoit, difant, Celui qui eft plus fort que moi, vient apres moi, duquel je ne fuis pas digne en me baiffant de deflier la courroye des fouliers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 8, 'Il eft vrai que je vous ai baptizez d\'eau: mais il vous baptizera du faint Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 9, 'Or il advint en ces jours-là que Jefus vint de Nazareth, ville de Galilée, &amp; fut baptizé par Jean au Jordain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 10, 'Et incontinent comme il fortoit hors de l\'eau, il vid les cieux fe fendre, &amp; le faint Efprit defcendre fur lui comme une colombe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 11, 'Alors il fe fit une voix, venant des cieux, difant, Tu es mon fils bien-aimé, auquel j\'ai pris mon bon-plaifir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 12, 'Et incontinent l\'efprit le pouffa au defert.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 13, 'Et il fut là au defert quarante jours, eftant tenté de Satan: &amp; eftoit avec les beftes fauvages, &amp; les Anges le fervoyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 14, 'Or apres que Jean eût efté mis en prifon, Jefus vint en Galilée, prefchant l\'Evangile du Royaume de Dieu:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 15, 'Et difant, Le temps eft accompli, &amp; le royaume de Dieu eft approché: amendez-vous, &amp; croyez à l\'Evangile.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 16, 'Or en cheminant auprés de la mer de Galilée, il vid Simon &amp; André fon frere, jettans leurs filez en la mer, (car ils eftoyent pefcheurs.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 17, 'Alors Jefus leur dit, Venez aprez moi, &amp; je vous ferai eftre pefcheurs d\'hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 18, 'Et auffi-toft laiffant leurs filez, ils le fuivirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 19, 'Et de là paffant un peu plus outre, il vid Jaques fils de Zebedée &amp; Jean fon frere, qui racouftroyent leurs filez en la neffelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 20, 'Et incontinent il les appella, &amp; eux laiffans leur pere Zebedée en la naffelle avec les ouvriers, le fuivirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 21, 'Puis ils entrerent en Capernaum: &amp; toft apres au jour du Sabbat, eftant entré en la Synagogue, il enfeignoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 22, 'Et ils s\'eftonnoyent de fa doctrine: car il les enfeignoit comme ayant authorité, &amp; non point comme les Scribes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 23, 'Or il fe trouva en leur Synagogue un homme qui avoit un efprit immonde, lequel s\'écria,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 24, 'Difant, Ha! qu\'y a-t\'il entre toi &amp; nous, Jefus Nazarien? es-tu venu pour nous deftruire? je fçay qui tu es affavoir le faint de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 25, 'Mais Jefus le tança, difant, Tai-toi, &amp; fors de lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 26, 'Alors l\'efprit immonde le dérompant, &amp; s\'écriant à haute voix, fortit hors de lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 27, 'Et ils s\'eftonnerent tous, tellement qu\'ils s\'enqueroyent entr\'eux, difans, Qu\'eft ceci, quelle doctrine nouvelle eft celle-ci? qu\'il commande d\'authorité, mefmes aux efprits immondes, &amp; ils lui obeïssent?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 28, 'Ainfi fa renommée courut incontinent par toute la contrée d\'alentour de la Galilée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 29, 'Et toft apres eftans fortis de la Synagogue, ils vinrent avec Jaques &amp; Jean en la maifon de Simon &amp; d\'André.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 30, 'Or la belle-mere de Simon gifoit malade de la fiévre: &amp; auffi-toft ils lui parlerent d\'elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 31, 'Alors s\'approchant, il la leva, en la prenant par la main: &amp; auffi-toft la fiévre la laiffa, &amp; elle les fervit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 32, 'Le foir eftant venu, comme le foleil fe couchoit, ils lui apportoyent tous ceux qui fe portoyent mal, &amp; les demoniaques.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 33, 'Et toute la ville eftoit affemblée à la porte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 34, 'Et il guerit tous ceux qui eftoyent malades de diverfes maladies: &amp; jetta hors plufieurs diables, ne permettant point que les diables diffent qu\'ils le connuffent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 35, 'Puis au matin comme il eftoit encore fort nuit, s\'étant levé il fortit, &amp; s\'en alla en un lieu defert, &amp; prioit là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 36, 'Et Simon, &amp; les autres qui eftoyent avec lui, le fuivirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 37, 'Et quand ils l\'eurent trouvé, ils lui dirent, Tous te cherchent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 38, 'Alors il leur dit, Allons-nous-en aux prochaines bourgades, afin que j\'y prefche auffi: car je fuis venu pour cela.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 39, 'Il prefchoit donc en leurs Synagogues, par toute la Galilée, &amp; jettoit hors les diables.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 40, 'Et un lepreux vint à lui, le priant, &amp; s\'agenoüillant devant lui, &amp; difant, Si tu veux, tu peux me nettoyer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 41, 'Alors Jefus eftant émeu de compaffion, eftendit fa main, &amp; le toucha, &amp; lui dit, Je le veux, fois nettoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 42, 'Et quand il l\'eût dit, la lepre partit auffi-toft de lui, &amp; il fut nettoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 43, 'Et l\'ayant menacé, il le renvoya incontinent,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 44, 'Et lui dit, Garde-toi d\'en rien dire à perfonne: mais va-t\'en, &amp; te montre au Sacrificateur, &amp; prefente pour ta purification les chofes que Moyfe a commandées, pour leur eftre en témoignage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 45, 'Mais lui eftant parti commença à publier plufieurs chofes, &amp; à divulguer l\'affaire: tellement que Jefus ne pouvoit entrer à découvert en la ville, mais il fe tenoit en des lieux deferts, &amp; de toutes parts on venoit à lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 1, 'Quelques jours apres il entra derechef en Capernaum: &amp; on ouït dire qu\'il eftoit en la maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 2, 'Et auffi-toft plufieurs s\'y affemblerent, tellement que mefmes l\'endroit d\'auprés de la porte ne les pouvoit contenir, &amp; il leur annonçoit la parole.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 3, 'Alors quelques-uns vinrent à lui, apportans un paralytique, qui eftoit porté par quatre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 4, 'Mais parce qu\'ils ne pouvoyent approcher de lui à caufe de la foule, ils découvrirent le toit du lieu où eftoit Jefus: &amp; l\'ayant percé, devalerent le petit lit auquel le paralytique gifoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 5, 'Alors Jefus ayant veu leur foi, dit au paralytique, Mon fils, tes pechez te font pardonnez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 6, 'Or quelques-uns des Scribes eftoyent là affis, &amp; difputoyent en leurs coeurs, difans,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 7, 'Pourquoi celui-ci prononce-t\'il ainfi des blafphemes? Qui eft-ce qui peut pardonner les pechez, finon un feul Dieu?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 8, 'Et incontinent Jefus ayant connu en fon efprit, qu\'ils difputoyent ainfi en eux-mefmes, leur dit, Pourquoi difputez-vous de telles chofes en vos coeurs?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 9, 'Lequel eft le plus aifé, ou de dire au paralytique, Tes pechez te font pardonnez: ou de dire, Leve-toi, &amp; charge ton petit lit, &amp; chemine?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 10, 'Or afin que vous fçachiez que le Fils de l\'homme a pouvoir de pardonner les pechez en la terre (il dit au paralytique.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 11, 'Je te dis, Leve-toi &amp; charge ton petit lit, &amp; t\'en va en ta maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 12, 'Et lui fe leva auffi-toft, &amp; ayant chargé fon petit lit, il fortit en la prefence de tous: tellement qu\'ils furent tous eftonnez, &amp; glorifioyent Dieu, difans, Nous ne vifmes jamais une telle chofe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 13, 'Alors il s\'en alla derechef vers la mer, &amp; tout le peuple venoit à lui, &amp; il les enfeignoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 14, 'Et en paffant, il vid Levi fils d\'Alphée, affis au lieu du peage, &amp; lui dit, Sui moi: &amp; lui s\'eftant levé le fuivit');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 15, 'Et il advint comme Jefus eftoit à table en la maifon d\'icelui, que plufieurs peagers &amp; gens de mauvaife vie, fe mirent auffi à table avec Jefus &amp; fes difciples: car il y en avoit beaucoup qui pareillement l\'avoyent fuivi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 16, 'Dont les Scribes &amp; les Pharifiens, voyans qu\'il mangeoit avec les peagers &amp; gens de mauvaife vie, difoyent à fes difciples, Pourquoi eft-ce qu\'il mange &amp; qu\'il boit avec les peagers &amp; les mal-vivans?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 17, 'Et Jefus ayant entendu cela, leur dit, Ceux qui font en fanté n\'ont pas befoin de medecin: mais ceux qui fe portent mal. Je ne fuis point venu appeller les juftes, mais les pecheurs à repentance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 18, 'Or les difciples de Jean &amp; des Pharifiens jeufnoyent: lefquels vinrent vers lui, &amp; lui dirent, Pourquoi les difciples de Jean &amp; des Pharifiens jeufnent-ils: mais tes difciples ne jeufnent point?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 19, 'Et Jefus leur refpondit, Les gens de nopces peuvent-ils jeufner pendant que le nouveau marié eft avec eux? Durant le temps qu\'ils ont le nouveau marié avec eux, ils ne peuvent jeufner.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 20, 'Mais les jours viendront que le nouveau marié leur fera ofté, &amp; alors ils jeufneront en ces jours-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 21, 'Auffi nul ne coud une piece de drap neuf à un vieux veftement: autrement la piece du drap neuf, qui a efté mife pour remplage, emporte la pièce du vieux drap: &amp; la rupture en eft pire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 22, 'Pareillement nul ne met le vin nouveau en de vieux vaiffeaux: autrement le vin nouveau rompt les vaiffeaux, &amp; le vin s\'épand, &amp; les vaiffeaux fe perdent: mais le vin nouveau doit eftre mis en des vaiffeaux neufs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 23, 'Et il advint comme il paffoit, en un jour de Sabbat, par des blés, que fes difciples en cheminant fe prirent à arracher des épics.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 24, 'Alors les Pharifiens lui dirent, Regarde, pourquoi font-ils ce qui n\'eft pas permis aux Sabbats?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 25, 'Mais il leur dit, Ne leuftes-vous jamais ce que fit David quand il eut neceffité, &amp; qu\'il eut faim, lui &amp; ceux qui eftoyent avec lui?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 26, 'Comme il entra en la maifon de Dieu, au temps d\'Abiathar principal Sacrificateur, &amp; mangea les pains de propofition, qu\'il n\'eftoit permis de manger finon aux Sacrificateurs: &amp; en donna auffi à ceux qui eftoyent avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 27, 'Puis il leur dit, Le Sabbat eft fait pour l\'homme, &amp; non point l\'homme, pour le Sabbat:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 28, 'Ainfi le Fils de l\'homme eft Seigneur auffi du Sabbat.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 1, 'Puis il entra derechef en la Synagogue: &amp; il y avoit là un homme qui avoit la main feche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 2, 'Et ils l\'obfervoyent, pour voir, s\'il le gueriroit au Sabbat, afin de l\'accufer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 3, 'Alors il dit à l\'homme qui avoit la main feche, Leve-toi en place.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 4, 'Puis il leur dit, Eft-il permis de bien faire és Sabbats, ou de mal faire? de fauver une perfonne, ou de la tuër? Mais ils fe teurent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 5, 'Alors en les regardant à l\'entour avec indignation, &amp; pareillement marri de l\'endurciffement de leur coeur, il dit au perfonnage, Eften ta main. Et il l\'eftendit: &amp; fa main fut renduë faine comme l\'autre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 6, 'Alors les Pharifiens eftans fortis, confulterent avec les Herodiens contre lui, comment ils le feroyent perir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 7, 'Mais Jefus avec fes difciples fe retira vers la mer, &amp; une grande multitude le fuivit de Galilée, &amp; de Judée, &amp; de Jerufalem, &amp; d\'Idumée, &amp; de par delà le Jordain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 8, 'Et ceux qui habitoyent à l\'entour de Tyr &amp; de Sidon, ayans entendu les grandes chofes qu\'il faifoit, vinrent vers lui en grande multitude.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 9, 'Et il dit à fes difciples, qu\'une petite naffelle ne bougeaft de là pour le fervir, à caufe des troupes, afin qu\'elles ne l\'empreffaffent point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 10, 'Car il en avoit gueri beaucoup, tellement que tous ceux qui eftoyent affligez, fe jettoyent contre lui, afin qu\'ils le touchaffent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 11, 'Et les efprits immondes, quand ils le voioyent, fe profternoyent devant lui, &amp; s\'écrioyent, difans, Tu es le Fils de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 12, 'Mais il les tançoit fort, afin qu\'ils ne le fiffent point connoiftre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 13, 'Puis il monta en une montagne, &amp; appella ceux qu\'il voulut, &amp; ils vinrent à lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 14, 'Et il en ordonna douze pour eftre avec lui, &amp; pour les envoyer prefcher:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 15, 'Et avoir puiffance de guerir les maladies, &amp; de jetter hors les diables:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 16, 'Affavoir Simon, lequel il furnomma Pierre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 17, 'Puis Jaques fils de Zebedée, &amp; Jean frere de Jaques, aufquels il impofa le nom de Boanerges, (qui vaut autant à dire qu\'enfans de tonnerre)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 18, 'Et André, &amp; Philippe, &amp; Barthelemi, &amp; Matthieu, &amp; Thomas, &amp; Jaques fils d\'Alphée, &amp; Thadée, &amp; Simon le Cananéen,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 19, 'Et Judas Ifcariot, qui auffi le trahit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 20, 'Puis ils vinrent en la maifon: &amp; derechef une multitude s\'affembla, tellement qu\'ils ne pouvoyent pas mefmes prendre leur repas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 21, 'Et quand ceux qui lui attouchoyent eurent entendu cela, ils fortirent pour le faifir: car ils difoyent, qu\'il eftoit hors du fens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 22, 'Et les Scribes, qui eftoyent defcendus de Jerufalem, difoyent, Il a Beelzebul, &amp; jette hors les diables par le prince des diables.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 23, 'Mais lui les ayant appellez, leur dit par fimilitudes, Comment peut Satan jetter Satan dehors?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 24, 'Car fi un royaume eft divifé contre foi-mefme, ce royaume-là ne peut fubfifter.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 25, 'Et fi une maifon eft divisée contre foi-mefme, cette maifon-là ne peut fubfifter.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 26, 'Pareillement fi Satan s\'éleve contre foi-mefme, &amp; eft divifé, il ne peut fubfifter, mais il s\'en va faillir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 27, 'Nul ne peut entrer en la maifon d\'un homme fort, &amp; piller fon bien, fi premierement il n\'a lié l\'homme fort: &amp; alors il pillera fa maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 28, 'En verité je vous dis que toutes fortes de pechez feront pardonnez aux enfans des hommes, &amp; toutes fortes de blafphemes par lefquels ils auront blafphemé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 29, 'Mais quiconque aura blafphemé contre le faint Efprit, n\'aura point de pardon eternellement, ains il fera coupable de condamnation eternelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 30, 'C\'eft parce qu\'ils difoyent, Il a l\'efprit immonde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 31, 'Ses freres donc &amp; fa mere vinrent: &amp; eftans dehors envoyerent quelques-uns vers lui pour l\'appeller: &amp; la multitude eftoit affife à l\'entour de lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 32, 'Ainfi donc on lui dit, Voila ta mere &amp; tes freres là dehors, qui te demandent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 33, 'Mais il leur refpondit, difant, Qui eft ma mere, ou qui font mes freres?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 34, 'Et en regardant de tous coftez à l\'environ, ceux qui eftoyent affis à l\'entour de lui, il dit, Voici ma mere &amp; mes freres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 35, 'Car quiconque fera la volonté de Dieu, celui-là eft mon frere, &amp; ma foeur, &amp; ma mere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 1, 'Puis il fe prit derechef à enfeigner aupres de la mer: &amp; de grandes troupes s\'affemblerent vers lui: tellement qu\'eftant monté en une naffelle, il fe tenoit affis en la naffelle fur la mer, &amp; tout le peuple eftoit fur terre aupres de la mer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 2, 'Et il leur enfeignoit beaucoup de chofes par fimilitudes, &amp; leur difoit en fa doctrine,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 3, 'Efcoutez; Voici, un femeur s\'en alla pour femer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 4, 'Et il advint qu\'en femant, une partie de la femence cheut auprés du chemin, &amp; les oifeaux du ciel vinrent, &amp; la mangerent toute.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 5, 'L\'autre partie cheut en des lieux pierreux, où elle n\'avoit gueres de terre: &amp; auffi-toft elle fe leva, parce qu\'elle n\'entroit pas profondement en terre:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 6, 'Mais quand le foleil fut levé, elle fut havie: &amp; parce qu\'elle n\'avoit nulle racine, elle fe fecha.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 7, 'L\'autre partie cheut entre les épines: &amp; les épines monterent, &amp; l\'eftoufferent, &amp; elle ne rendit point de fruit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 8, 'Et l\'autre partie cheut en bonne terre, &amp; rendit du fruit, montant &amp; croiffant: tellement qu\'un grain en apporta trente, &amp; l\'autre foixante, &amp; l\'autre cent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 9, 'Alors il leur dit, Qui a des oreilles pour ouïr, qu\'il oye.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 10, 'Et quand il fut à part, ceux qui eftoyent autour de lui avec les douze, l\'interrogerent touchant la fimilitude.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 11, 'Et il leur dit, Il vous eft donné de connoiftre le fecret du royaume de Dieu: mais à ceux qui font dehors, toutes chofes fe traittent en fimilitudes:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 12, 'Afin qu\'en voyant ils voyent, &amp; n\'apperçoivent point: &amp; qu\'en oyant ils oyent, &amp; n\'entendent point: de peur qu\'ils fe convertiffent, &amp; que les pechez leur foyent pardonnez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 13, 'Puis il leur dit, Ne fçavez-vous pas cette fimilitude? &amp; comment connoiftrez-vous toutes les fimilitudes?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 14, 'Le femeur c\'eft celui qui feme la parole.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 15, 'Et voici, ceux qui reçoivent la femence auprés du chemin, ce font ceux où la parole eft femée: mais apres qu\'ils l\'ont ouïe, incontinent Satan vient, &amp; ofte la parole femée en leurs coeurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 16, 'Et voici femblablement, ceux qui reçoivent la femence en des lieux pierreux, ce font ceux qui ayans ouï la parole, auffi-toft la reçoivent avec joye:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 17, 'Mais ils n\'ont point de racine en eux-mefmes, &amp; ne font qu\'à temps: tellement qu\'oppreffion ou perfecution advenant pour la parole, ils font incontinent fcandalifez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 18, 'Auffi ceux qui reçoivent la femence entre les épines, ce font ceux qui oyent la parole:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 19, 'Mais les folicitudes de ce monde, &amp; la fallace des richeffes, &amp; les convoitifes des autres chofes, eftant entrées en leurs efprits, eftouffent la parole, &amp; elle devient infructueufe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 20, 'Mais voici, ceux qui ont receu la femence en bonne terre, ce font ceux qui oyent la parole &amp; la reçoivent, &amp; portent du fruit: l\'un trente, &amp; l\'autre foixante, &amp; l\'autre cent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 21, 'Il leur difoit auffi, La chandelle eft-elle apportée afin qu\'elle foit mife fous un boiffeau, ou fous un lit? N\'eft-ce point afin qu\'elle foit mife fur un chandelier?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 22, 'Car il n\'y a rien de fecret, qui ne foit enfin manifefté: &amp; il n\'y a rien de caché, qui ne vienne en evidence.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 23, 'Si quelqu\'un a des oreilles pour ouïr, qu\'il oye.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 24, 'Davantage il leur dit, Regardez ce que vous oyez: de telle mefure que vous mefurerez, il vous fera mefuré: &amp; à vous qui oyez, il fera adjoufté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 25, 'Car à celui qui a, il lui fera donné: &amp; à celui qui n\'a rien, cela mefme qu\'il a, lui fera ofté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 26, 'Davantage il dit, Le royaume de Dieu eft comme fi un homme ayant jetté la femence en la terre, dormoit &amp; fe levoit de nuit &amp; de jour:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 27, 'Et que la femence germaft &amp; creuft, lui ne fçachant comment.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 28, 'Car la terre d\'elle-mefme produit premierement l\'herbe, puis apres les épics, puis le plein froment en épic:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 29, 'Et quand le fruit s\'eft montré, incontinent on y met la faucille, à caufe que la moiffon eft prefte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 30, 'Puis il difoit, A quoi accomparerons-nous le royaume de Dieu, ou de quelle fimilitude le reprefenterons-nous?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 31, 'Il en prend comme du grain de femence de mouftarde, lequel eftant femé en terre, eft bien la plus petite de toutes les femences qui font jettées en la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 32, 'Mais apres eftre femé, il fe leve, &amp; devient plus grand que toutes les autres herbes: &amp; jette de grandes branches, tellement que les oifeaux du ciel peuvent baftir leurs logettes fous fon ombre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 33, 'Ainfi par plufieurs telles fimilitudes, il leur traittoit de la parole felon qu\'ils pouvoyent ouïr.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 34, 'Et il ne parloit point à eux fans fimilitude: mais à part il declaroit le tout à fes difciples.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 35, 'Ce jour-là, quand le foir fut venu, il leur dit, Paffons de-là l\'eau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 36, 'Et apres avoir laiffé les troupes, ils le prirent avec eux ainfi qu\'il eftoit en la naffelle. Or il y avoit auffi d\'autres petites naffelles avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 37, 'Alors un grand tourbillon de vent fe leva, tellement que les vagues fe jettoyent en la naffelle, de forte qu\'elle s\'empliffoit déja.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 38, 'Or il eftoit en la pouppe, dormant fur un oreiller: alors ils l\'éveillerent, &amp; lui dirent, Maiftre, ne te foucies-tu point de ce que nous periffons?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 39, 'Mais lui s\'eftant levé tança le vent, &amp; dit à la mer, Tai-toi, tien-toi coye. Alors le vent ceffa, &amp; il fe fit un grand calme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 40, 'Puis il leur dit, Pourquoi eftes-vous ainfi craintifs? Comment, n\'avez-vous point de foi?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 41, 'Mais ils craignirent de grande crainte, &amp; difoyent l\'un à l\'autre, Mais qui eft celui-ci, que le vent mefmes &amp; la mer lui obeïffent?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 1, 'Et ils arriverent de-là la mer, en la contrée des Gadareniens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 2, 'Et quand il fut forti de la naffelle, incontinent un homme qui avoit un efprit immonde fortit des fepulcres, &amp; le vint rencontrer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 3, 'Lequel avoit fa demeure és fepulcres, &amp; nul ne le pouvoit tenir lié, non pas mefmes de chaines:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 4, 'Parce que fouvent quand il avoit efté lié de ceps &amp; de chaines, il avoit rompu les chaines, &amp; mis les ceps en pieces: &amp; perfonne ne le pouvoit dompter.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 5, 'Et il eftoit continuellement de nuit &amp; de jour és montagnes &amp; és fepulcres, criant &amp; fe frapant de pierres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 6, 'Quand donc de fort loin il vid Jefus, il accourut, &amp; fe profterna devant lui:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 7, 'Et criant à haute voix, il dit, Qu\'y a-t\'il entre toi &amp; moi, Jefus Fils du Dieu fouverain? je t\'adjure de par Dieu que tu ne me tourmentes point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 8, '(Car Jefus lui difoit, Sors de cet homme, efprit immonde.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 9, 'Alors il l\'interrogea, Comment as-tu nom? Et il refpondit, difant, J\'ai nom Legion: car nous fommes plufieurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 10, 'Et il le prioit fort qu\'il ne les envoyaft point hors de cette contrée-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 11, 'Or il y avoit là vers les montagnes un grand troupeau de pourceaux qui paiffoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 12, 'Et tous ces diables-là le prioyent, difans, Envoye nous és pourceaux, afin que nous entrions dans eux. Et Jefus incontinent le leur permit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 13, 'Alors ces efprits immondes eftans fortis, entrerent és pourceaux: &amp; le troupeau fe jetta du haut en bas en la mer: (or il y en avoit environ deux mille) &amp; ils furent eftouffez en la mer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 14, 'Et ceux qui paiffoyent les pourceaux s\'enfuïrent, &amp; en porterent les nouvelles en la ville, &amp; par les villages.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 15, 'Alors ils fortirent pour voir ce qui eftoit advenu, &amp; vinrent à Jefus, &amp; virent celui qui avoit efté demoniaque, affis &amp; veftu, &amp; de bon fens: voire celui qui avoit eu la legion: &amp; ils eurent peur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 16, 'Et ceux qui avoyent veu cela, leur raconterent ce qui eftoit arrivé à ce demoniaque, &amp; touchant les pourceaux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 17, 'Alors ils fe prirent à le prier qu\'il s\'en allaft de leurs quartiers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 18, 'Et quand il fut entré en la naffelle, celui qui avoit efté demoniaque le prioit qu\'il fuft avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 19, 'Mais Jefus ne le lui permit point, mais lui dit, Va t\'en en ta maifon vers les tiens, &amp; leur raconte combien grandes chofes le Seigneur t\'a faites, &amp; comme il a eu pitié de toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 20, 'Il s\'en alla donc, &amp; fe mit à publier en Decapolis combien grandes chofes Jefus lui avoit faites: &amp; tous s\'en eftonnoyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 21, 'Et quand Jefus fut derechef paffé à l\'autre rivage en une naffelle, de grandes troupes s\'affemblerent vers lui; &amp; il eftoit prés de la mer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 22, 'Alors voici, l\'un des principaux de la Synagogue, nommé Jaïrus, vint: &amp; l\'ayant veu, fe jetta à fes pieds:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 23, 'Et le prioit fort, difant, Ma petite fille eft à l\'extremité, je te prie de venir, &amp; de lui impofer les mains, afin qu\'elle foit guerie, &amp; qu\'elle vive.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 24, 'Jefus donc s\'en alla avec lui: &amp; de grandes troupes le fuivoyent, tellement qu\'elles l\'empreffoyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 25, 'Or il y avoit une certaine femme travaillée d\'une perte de fang depuis douze ans:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 26, 'Laquelle avoit beaucoup fouffert entre les mains de plufieurs medecins, &amp; avoit defpenfé tout fon bien, &amp; n\'avoit rien profité: mais pluftoft elle eftoit allée en empirant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 27, 'Elle ayant ouï parler de Jefus, vint en la foule par derriere, &amp; toucha fon habillement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 28, 'Car elle difoit, Si feulement je touche fes habillemens, je ferai guerie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 29, 'Et incontinent le flux de fon fang s\'eftancha, &amp; elle fentit en fon corps, qu\'elle eftoit guerie de fon fleau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 30, 'Et auffi-toft Jefus reconnoiffant en foi-mefme la vertu qui eftoit fortie de lui, fe retourna vers la foule, difant, Qui a touché mes habillemens?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 31, 'Et fes difciples lui dirent, Tu vois que la foule t\'empreffe, &amp; tu dis, Qui m\'a touché?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 32, 'Mais il regardoit tout à l\'entour, pour voir celle qui avoit fait cela.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 33, 'Alors la femme craignant &amp; tremblant, fçachant ce qui avoit efté fait en fa perfonne, vint &amp; fe jetta devant lui, &amp; lui dit toute la verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 34, 'Et il lui dit, Ma fille, ta foi t\'a fauvée: va t\'en en paix, &amp; fois guerie de ton fleau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 35, 'Comme il parloit encore, quelques-uns arriverent de chez le principal de la Synagogue, difans, Ta fille eft morte: pourquoi travailles-tu le maiftre davantage?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 36, 'Et incontinent que Jefus eût oüi ce qu\'on difoit, il dit au principal de la Synagogue, Ne crain point: croi feulement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 37, 'Et il ne permit point qu\'aucun le fuivift, finon Pierre, &amp; Jaques, &amp; Jean frere de Jaques.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 38, 'Puis il vint en la maifon du principal de la Synagogue, &amp; apperçeut le trouble, &amp; ceux qui pleuroyent &amp; jettoyent de grands cris.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 39, 'Et alors eftant entré dedans, il leur dit, Pourquoi eft-ce que vous vous troublez, &amp; que vous pleurez? La petite fille n\'eft pas morte, mais elle dort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 40, 'Et ils fe rioyent de lui: mais les ayant fait fortir, il prit le pere &amp; la mere de la petite fille, &amp; ceux qui eftoyent avec lui, &amp; entra là où la petite fille eftoit gifante.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 41, 'Et ayant pris la main de l\'enfant, il lui dit, Talitha cumi, qui vaut autant à dire, eftant expofé, que, Petite fille (je te dis) leve toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 42, 'Et incontinent la petite fille fe leva, &amp; cheminoit: car elle eftoit âgée de douze ans. Dont ils furent eftonnez d\'un grand eftonnement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 43, 'Et il leur commanda fort que perfonne ne le fçeuft: &amp; puis il dit qu\'on lui donnaft à manger.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 1, 'Apres il partit de là, &amp; vint en fon païs: &amp; fes difciples le fuivoyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 2, 'Et quand le Sabbat fut venu, il fe prit à enfeigner en la Synagogue: &amp; beaucoup de ceux qui l\'oioyent, s\'eftonnoyent, difans, D\'où viennent ces chofes à celui-ci? &amp; quelle eft cette fageffe qui lui eft donnée, que mefmes de telles vertus fe font par fes mains?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 3, 'Celui-ci n\'eft-il pas charpentier? fils de Marie, frere de Jaques, &amp; de Jofes, &amp; de Jude, &amp; de Simon? fes foeurs auffi ne font-elles pas parmi nous? Et ils eftoyent fcandalifez en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 4, 'Alors Jefus leur dit, Un Prophete n\'eft point fans honneur, finon en fon païs, &amp; entre fes parens &amp; ceux de fa famille.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 5, 'Et il ne peut faire là aucune vertu, finon qu\'il guerit quelque peu de malades, leur ayant impofé les mains.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 6, 'Et il s\'eftonnoit de leur incredulité: &amp; tournoyoit à l\'entour des bourgades, enfeignant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 7, 'Alors il appella les douze, &amp; commença de les envoyer deux à deux, &amp; leur donna puiffance fur les efprits immondes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 8, 'Et leur commanda de ne rien prendre pour porter fur le chemin, finon un bafton feulement, ni mallete, ni pain, ni monnoye en leur ceinture.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 9, 'Mais qu\'ils fuffent chauffez de fouliers, &amp; qu\'ils ne fe veftiffent point de deux robbes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 10, 'Il leur difoit auffi, En quelque part que vous entrerez en une maifon, demeurez-y jufques à ce que vous partiez de là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 11, 'Et tous ceux qui ne vous recevront point, &amp; ne vous orront point, en partant de là fecoüez la poudre de deffous vos pieds, en témoignage contre eux: En verité je vous dis, que ceux de Sodome &amp; de Gomorrhe feront plus tolerablement traittez au jour du jugement, que cette ville-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 12, 'Eux donc eftans partis, prefcherent qu\'on s\'amendaft.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 13, 'Et jetterent hors plufieurs diables, &amp; oignirent d\'huile plufieurs malades, &amp; les guerirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 14, 'Or le roi Herode en ouït parler, (car le nom d\'icelui eftoit fort celebre) &amp; dit, Ce Jean qui baptizoit, eft reffufcité des morts, &amp; pourtant les vertus montrent leur force en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 15, 'Les autres difoyent, C\'eft Elie: &amp; les autres difoyent, C\'eft un Prophete, ou comme l\'un des Prophetes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 16, 'Quand donc Herode eût entendu cela, il dit, Celui-ci eft ce Jean que j\'ai decapité: il eft reffufcité des morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 17, 'Car Herode ayant envoyé fes gens avoit fait prendre Jean, &amp; l\'avoit lié en prifon, à caufe d\'Herodias femme de Philippe fon frere, parce qu\'il l\'avoit prife en mariage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 18, 'Car Jean difoit à Herode, Il ne t\'eft pas permis d\'avoir celle qui eft femme de ton frere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 19, 'Et pourtant Herodias lui en vouloit, &amp; defiroit de le faire mourir: mais elle ne pouvoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 20, 'Car Herode craignoit Jean, fçachant qu\'il eftoit homme jufte &amp; faint, &amp; l\'avoit en reverence: &amp; l\'ayant ouï, faifoit beaucoup de chofes pour l\'amour de lui, &amp; l\'écoutoit volontiers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 21, 'Mais un jour eftant venu à propos, qu\'Herode faifoit le feftin du jour de fa naiffance aux grands feigneurs, &amp; aux capitaines, &amp; aux principaux de Galilée:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 22, 'Et la fille d\'Herodias eftant entrée, &amp; ayant danfé, &amp; ayant pleu à Herode, &amp; auffi à ceux qui eftoyent enfemble à table: le roi dit à la jeune fille, Demande-moi ce que tu voudras, &amp; je te le donnerai.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 23, 'Et il lui jura, difant, Tout ce que tu me demanderas, je te le donnerai, voire jufques à la moitié de mon royaume.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 24, 'Elle donc eftant fortie, dit à fa mere, Qu\'eft-ce que je demanderai? Et elle dit, La tefte de Jean Baptifte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 25, 'Et incontinent eftant rentrée avec grande affection vers le roi, elle lui fit fa requefte, difant, Je voudrois que tout à cette heure tu me donnaffes en un plat la tefte de Jean Baptifte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 26, 'Et le roi eftant tres-marri, ne voulut pas toutefois l\'éconduire, à caufe du ferment, &amp; de ceux qui eftoyent à table avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 27, 'Mais il envoya incontinent l\'un de fes gardes, &amp; commanda qu\'il apportaft la tefte d\'icelui: lequel s\'y en alla, &amp; le decapita en la prifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 28, 'Et apporta la tefte d\'icelui en un plat, &amp; la donna à la jeune fille, &amp; la jeune fille la donna à fa mere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 29, 'Et fes difciples l\'ayant ouï, vinrent &amp; emporterent fon corps, &amp; le mirent en un fepulcre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 30, 'Et les Apoftres fe raffemblerent vers Jefus, &amp; lui raconterent tout ce qu\'ils avoyent fait &amp; enfeigné.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 31, 'Et il leur dit, Venez-vous-en à part en un lieu retiré, &amp; vous repofez un petit: car il y avoit beaucoup d\'allans &amp; de venans, tellement qu\'ils n\'avoyent pas mefme le loifir de manger.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 32, 'Ils s\'en allerent donc en une naffelle en un lieu retiré à part.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 33, 'Mais le peuple vid qu\'ils s\'en alloyent, &amp; plufieurs le reconnurent, &amp; y accoururent à pied de toutes les villes, &amp; y vinrent devant eux, &amp; s\'affemblerent vers lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 34, 'Alors Jefus eftant forti, vid là de grandes troupes, &amp; fut efmeu de compaffion envers elles, de ce qu\'elles eftoyent comme des brebis qui n\'avoyent point de pafteur: &amp; fe mit à leur enfeigner plufieurs chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 35, 'Et comme il eftoit déja tard, fes difciples vinrent à lui, difans, Ce lieu eft defert, &amp; il eft déja tard.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 36, 'Donne-leur congé, afin qu\'ils s\'en aillent aux villages &amp; aux bourgades d\'alentour, &amp; qu\'ils achetent des pains pour eux: car ils n\'ont rien à manger.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 37, 'Et refondant, il leur dit, Mais vous, donnez-leur à manger. Ils lui dirent, Irions-nous acheter des pains pour deux cens deniers, pour leur donner à manger?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 38, 'Et il leur dit, Combien avez-vous de pains? allez &amp; regardez. Et apres l\'avoir fçeu, ils dirent, Cinq, &amp; deux poiffons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 39, 'Alors il leur commanda qu\'ils les fiffent tous affeoir par tablées fur l\'herbe verte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 40, 'Et ils s\'affirent par rangées, par centaines &amp; par cinquantaines.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 41, 'Et quand il eût pris les cinqs pains &amp; les deux poiffons, en regardant vers le ciel, il rendit graces, &amp; rompit les pains, &amp; les bailla à fes difciples, afin qu\'ils les miffent devant eux: &amp; partagea les deux poiffons à tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 42, 'Et tous en mangerent, &amp; furent raffafiez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 43, 'Et ils recüeillirent des pieces de pain douze corbeilles pleines, &amp; quelque refte de poiffons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 44, 'Or ceux qui en avoyent mangé eftoyent environ cinq mille hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 45, 'Incontinent apres cela il contraignit fes difciples de monter en la naffelle, &amp; d\'aller devant lui, outre la mer vers Bethfaïda, pendant qu\'il donneroit congé aux troupes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 46, 'Et quand il leur eût donné congé, il s\'en alla en la montagne pour prier.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 47, 'Le foir eftant venu, la naffelle eftoit au milieu de la mer, &amp; il eftoit lui feul fur la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 48, 'Et il vid qu\'ils avoyent grand\'peine à tirer, car le vent leur eftoit contraire: &amp; environ la quatriéme veille de la nuit, il vint vers eux cheminant fur la mer, &amp; les vouloit paffer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 49, 'Mais quand ils le virent cheminant fur la mer, ils penferent que ce fuft un fantofme: dont ils s\'écrierent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 50, 'Car ils le voyoyent tous, &amp; furent troublez: mais incontinent il parla à eux, &amp; leur dit, Affeurez-vous, c\'eft moi: n\'ayez point de peur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 51, 'Alors il monta en la naffelle vers eux, &amp; le vent ceffa: dont ils s\'eftonnerent beaucoup plus fort en eux-mefmes, &amp; s\'émerveillerent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 52, 'Car ils n\'avoyent pas bien pris garde au fait des pains: d\'autant que leur coeur eftoit ftupide.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 53, 'Et quand ils furent paffez outre, ils vinrent en la contrée de Genezareth, &amp; arriverent au port.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 54, 'Et comme ils furent fortis de la naffelle, ceux du lieu le reconnurent incontinent:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 55, 'Et coururent ça &amp; là par toute la contrée d\'alentour, &amp; fe prirent à lui apporter ça &amp; là en de petits lits ceux qui fe portoyent mal, là où ils oyoyent dire qu\'il eftoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 56, 'Et par tout où il eftoit entré és bourgades, ou és villes, ou és villages, ils mettoyent les malades és marchez: &amp; le prioyent que pour le moins ils puffent toucher le bord de fa robe: &amp; tous ceux qui le touchoyent eftoyent gueris.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 1, 'Alors les Pharifiens, &amp; quelques-uns des Scribes qui eftoyent venus de Jerufalem, s\'affemblerent vers lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 2, 'Et voyans quelques-uns de fes difciples prendre leur repas avec les mains communes, (c\'eft à dire, fans eftre lavées) ils les en blamoyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 3, '(Car les Pharifiens &amp; tous les Juifs ne mangent point qu\'ils ne lavent fouvent leurs mains, retenans la tradition des anciens:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 4, 'Et retournans du marché, ils ne mangent point, qu\'ils ne foyent lavez. Il y a auffi beaucoup d\'autres chofes qu\'ils ont prifes à garder, comme les lavemens des coupes, des brocs, de la vaiffelle, &amp; des chalits.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 5, 'Là deffus les Pharifiens &amp; les Scribes l\'interrogerent, difans, Pourquoi tes difciples ne cheminent-ils point felon la tradition des anciens: mais prennent leur repas fans laver les mains?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 6, 'Il répondit, &amp; leur dit, Certainement Efaïe a bien prophetizé de vous hypocrites: comme il eft efcrit, Ce peuple-ici m\'honore des levres, mais leur coeur eft bien fort efloigné de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 7, 'Mais ils m\'honorent en vain, enfeignant des doctrines, qui ne font que des commandemens d\'hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 8, 'Car en laiffant le commandement de Dieu, vous retenez la tradition des hommes, affavoir les lavemens des brocs &amp; des coupes: &amp; faites beaucoup d\'autres chofes femblables.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 9, 'Il leur dit auffi, Vous annullez bien le commandement de Dieu, afin que vous gardiez voftre tradition.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 10, 'Car Moyfe a dit, Honore ton pere &amp; ta mere. Item, Qui maudira fon pere ou fa mere, qu\'il meure de mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 11, 'Mais vous dites, Si quelqu\'un dit à fon pere ou à fa mere, Le corban (c\'eft à dire le don) qui fera fait de par moi, viendra à ton profit, il fera hors de coulpe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 12, 'Et ne fouffrez plus qu\'il faffe rien au benefice de fon pere ou de fa mere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 13, 'Ainfi vous mettez la parole de Dieu à neant par voftre tradition que vous avez eftablie, &amp; faites plufieurs chofes femblables.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 14, 'Puis ayant appellé toutes les troupes, il leur dit, Efcoutez moi, vous tous, &amp; entendez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 15, 'Il n\'y a rien de ce qui entre par dehors en l\'homme, qui le puiffe foüiller: mais les chofes qui fortent de lui, ce font les chofes qui foüillent l\'homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 16, 'Si quelqu\'un a des oreilles pour ouïr, qu\'il oye.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 17, 'Puis quand il fut entré en la maifon, s\'eftant retiré d\'avec les troupes, fes difciples l\'interrogerent touchant la fimilitude.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 18, 'Et il leur dit, Et vous, eftes-vous ainfi auffi fans entendement? n\'entendez-vous point encore, que tout ce qui entre en l\'homme par dehors ne le peut foüiller?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 19, 'Car il n\'entre point en fon coeur, mais au ventre, &amp; fort au retrait, purgeant toute les viandes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 20, 'Il leur difoit donc, ce qui fort de l\'homme, c\'eft ce qui foüille l\'homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 21, 'Car du dedans, c\'eft à dire, du coeur des hommes, fortent les mauvaifes penfées, les adulteres, les paillardifes, les meurtres,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 22, 'Les larcins, les mauvaifes pratiques pour avoir le bien d\'autrui, les méchancetez, la fraude, l\'infolence, le mauvais regard, le blafpheme, la fierté, la folie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 23, 'Tous ces maux-là fortent du dedans, &amp; foüillent l\'homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 24, 'Puis s\'eftant levé de là, il s\'en alla aux confins de Tyr &amp; de Sidon: &amp; eftant entré en une maifon, il vouloit que perfonne ne le fçeuft; mais il ne pût eftre celé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 25, 'Car une femme de laquelle une petite fille avoit un efprit immonde, dés qu\'elle eût ouï parler de lui, vint &amp; fe jetta à fes pieds:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 26, '(Or cette femme eftoit Grecque, Syropheniciene de nation) &amp; le pria qu\'il jettaft le diable hors de fa fille.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 27, 'Et Jefus lui dit, Souffre que les enfans foyent les premiers raffafiez: car il n\'eft pas bon de prendre le pain des enfans, &amp; le jetter aux petits chiens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 28, 'Mais elle refpondit, &amp; lui dit, Il eft vrai, Seigneur: car les petits chiens mangent fous la table des miettes que les enfans laiffent tomber.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 29, 'Alors il lui dit, Pour cette parole va-t\'en: le diable eft forti de ta fille.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 30, 'Et quand elle s\'en fut allée en fa maifon, elle trouva que le diable eftoit forti, &amp; fa fille couchée fur le lict.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 31, 'Puis Jefus eftant parti derechef des quartiers de Tyr &amp; de Sidon, vint à la mer de Galilée, par le milieu des quartiers de Decapolis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 32, 'Alors on lui amena un fourd ayant le parler empefché: &amp; on le pria qu\'il lui impofaft les mains.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 33, 'Et l\'ayant tiré à part de la multitude, il mit fes doigts és oreilles d\'icelui: &amp; ayant craché lui toucha la langue.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 34, 'Puis en regardant au ciel, il foûpira, &amp; lui dit, Hephphatah, c\'eft à dire, Ouvre-toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 35, 'Et incontinent fes oreilles furent ouvertes, &amp; le lien de fa langue fut deflié, &amp; il parla aifément.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 36, 'Et il leur commanda de ne le dire à perfonne. Mais plus il le defendoit, &amp; plus ils le publioyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 37, 'Et ils s\'eftonnoyent tant &amp; plus, difans, Il a tout bien fait: il fait ouïr les fourds, &amp; parler les muëts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 1, 'En ces jours-là, comme il y avoit de fort grandes troupes, &amp; qu\'elles n\'avoyent rien à manger, Jefus appella fes difciples, &amp; leur dit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 2, 'Je fuis émeu de compaffion envers les troupes: car il y a déja trois jours qu\'ils ne bougent d\'avec moi, &amp; ils n\'ont rien à manger.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 3, 'Et fi je les renvoye à jeun en leur maifon, ils defaudront en chemin: car quelques-uns d\'entr\'eux font venus de loin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 4, 'Et fes difciples lui refpondirent, D\'où les pourra-t\'on raffafier ici de pains en ce defert?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 5, 'Et il leur demanda, Combien avez-vous de pains? Ils lui dirent, Sept.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 6, 'Alors il commanda aux troupes de s\'affeoir par terre: &amp; prit les fept pains, &amp; apres avoir rendu graces, il les rompit, &amp; les bailla à fes difciples, pour les mettre devant les troupes: &amp; ils les mirent devant elles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 7, 'Ils avoyent auffi quelque peu de petits poiffons: &amp; apres qu\'il eût rendu graces, il commanda qu\'ils les miffent auffi devant les troupes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 8, 'Ils en mangerent donc, &amp; furent raffafiez: &amp; emporterent du refte des pieces de pain, fept corbeilles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 9, '(Or ceux qui en avoyent mangé eftoyent environ quatre mille.) Et puis il leur donna congé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 10, 'Et monta incontinent en une naffelle avec fes difciples, &amp; vint és quartiers de Dalmanutha.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 11, 'Et les Pharifiens s\'en vinrent, &amp; fe prirent à difputer avec lui, lui demandant quelque figne du ciel, en le tentant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 12, 'Alors il dit en foûpirant profondement en fon efprit, Pourquoi cette generation demande-t\'elle un figne? En verité je vous dis, qu\'il ne fera point donné de figne à cette generation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 13, 'Et les ayant laiffez, il monta derechef en la naffelle, &amp; paffa à l\'autre rivage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 14, 'Or ils avoyent oublié à prendre des pains, &amp; n\'en avoyent qu\'un avec eux en la naffelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 15, 'Et il leur commanda, difant, Avifez, donnez-vous garde du levain des Pharifiens, &amp; du levain d\'Herode.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 16, 'Dont ils penfoyent en eux-mefmes, difans, C\'eft parce que nous n\'avons point de pains.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 17, 'Et Jefus connoiffant cela, leur dit, Pourquoi penfez-vous touchant ce que vous n\'avez point de pains? ne confiderez-vous point encore, &amp;n\'entendez-vous point? avez-vous encore voftre coeur ftupide?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 18, 'Ayans des yeux, ne voyez-vous point? ayans des oreilles, n\'oyez-vous point? &amp; n\'avez-vous point de fouvenance?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 19, 'Quand je diftribuai les cinq pains aux cinq mille hommes, combien en recüeilliftes-vous de corbeilles pleines du refte des pieces? Ils lui dirent, Douze.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 20, 'Et quand je diftribuai les fept pains aux quatre mille hommes, combien recüeilliftes-vous de corbeilles pleines du refte des pieces? Ils lui dirent, Sept.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 21, 'Et il leur dit, Comment n\'entendez-vous point?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 22, 'Puis il vint à Bethfaïda: &amp; ils lui prefenterent un aveugle, &amp; le prioyent qu\'il le touchaft.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 23, 'Alors il prit la main de l\'aveugle, &amp; le mena hors de la bourgade; &amp; ayant craché fur fes yeux, &amp; lui ayant impofé les mains, il lui demanda s\'il voyoit quelque chofe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 24, 'Et cet homme-là ayant levé la veuë, dit, Je vois marcher des hommes qui femblent des arbres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 25, 'Puis apres il mit derechef les mains fur les yeux d\'icelui, &amp; lui fit lever la veuë: &amp; il fut rétabli, &amp; les voyoit tous de loin clairement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 26, 'Puis il le renvoya en fa maifon, difant, N\'entre point en la bourgade, &amp; ne le di à perfonne de la bourgade.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 27, 'Et Jefus &amp; fes difciples eftans partis de là, vinrent aux bourgades de Cefarée, dite de Philippe: &amp; fur le chemin il interrogea fes difciples, leur difant, Qui difent les hommes que je fuis?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 28, 'Ils répondirent, les uns, Jean Baptifte: &amp; les autres, Elie: &amp; les autres, l\'un des Prophetes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 29, 'Alors il leur dit, Et vous, qui dites-vous que je fuis? Pierre répondant, lui dit, Tu es le Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 30, 'Et il leur defendit avec menaces, qu\'ils ne diffent cela de lui à perfonne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 31, 'Et il commença à les enfeigner, qu\'il faloit que le Fils de l\'homme fouffrift beaucoup, &amp; qu\'il fuft rejetté des Anciens, &amp; des principaux Sacrificateurs, &amp; des Scribes: &amp; qu\'il fuft mis à mort, &amp; qu\'il reffufcitaft trois jours apres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 32, 'Et tenoit ces propos tout ouvertement. Alors Pierre le prit, &amp; fe mit à le tancer:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 33, 'Mais lui fe retournant, &amp; regardant fes difciples, tança Pierre, difant, Va arriere de moi, Satan: car tu ne comprens point les chofes qui font de Dieu, mais celles qui font des hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 34, 'Puis ayant appellé les troupes avec fes difciples, il leur dit, Quiconque veut venir apres moi, qu\'il renonce à foi-mefme, &amp; qu\'il charge fur foi fa croix, &amp; qu\'il me fuive.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 35, 'Car quiconque voudra fauver fon ame, il la perdra: mais quiconque perdra fon ame pour l\'amour de moi &amp; de l\'Evangile, celui-là la fauvera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 36, 'Car que profitera-t\'il à l\'homme s\'il a gaigné tout le monde, &amp; qu\'il faffe perte de fon ame?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 37, 'Ou que donnera l\'homme pour recompenfe de fon ame?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 38, 'Car quiconque aura eu honte de moi &amp; de mes paroles parmi cette nation adultere &amp; pechereffe, le Fils de l\'homme aura auffi honte de lui, quand il fera venu en la gloire de fon Pere avec les faints Anges.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 1, 'Il leur difoit auffi, En verité je vous dis, qu\'il y a quelques-uns qui font ici prefens, qui ne goufteront point la mort, jufques à ce qu\'ils ayent veu le regne de Dieu eftre venu avec puiffance:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 2, 'Et le fixiéme jour apres, Jefus prit avec foi Pierre, &amp; Jaques, &amp; Jean, &amp; les mena feuls à part fur une haute montagne: &amp; fut transfiguré devant eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 3, 'Et fes veftemens devinrent reluifans, &amp; fort blancs, comme neige, tels qu\'il n\'y a foulon fur la terre qui les fceuft ainfi blanchir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 4, 'Puis Elie fut veu avec Moyfe: &amp; ils parloyent avec Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 5, 'Alors Pierre prenant la parole dit à Jefus, Maiftre, il eft bon que nous foyons ici: faifons y donc trois tabernacles, un pour toi, &amp; un pour Moyfe, &amp; un pour Elie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 6, 'Or il ne fçavoit ce qu\'il difoit: car ils eftoyent épouvantez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 7, 'Et une nuée vint qui les enombra: puis de la nuée vint une voix qui difoit, Celui-ci eft mon Fils bien-aimé: écoutez-le.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 8, 'Et auffi-toft ayant regardé à l\'entour ils ne virent plus perfonne, finon Jefus feul avec eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 9, 'Et comme ils defcendoyent de la montagne, il leur commanda expreffement qu\'ils ne racontaffent à perfonne ce qu\'ils avoyent veu, finon apres que le Fils de l\'homme feroit reffufcité des morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 10, 'Donc ils retinrent cette parole-là en eux-mefmes, s\'entre-demandans ce que c\'eftoit à dire cela, Reffufciter des morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 11, 'Puis apres ils l\'interrogerent, difans, Pourquoi les Scribes difent-ils qu\'il faut qu\'Elie vienne premierement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 12, 'Il répondit, &amp; leur dit, De vrai, Elie eftant venu premierement, doit reftablir toutes chofes: &amp; comme il eft écrit du Fils de l\'homme, il faut qu\'il fouffre beaucoup, &amp; qu\'il foit aneanti.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 13, 'Mais je vous dis, que mefmes Elie eft venu, &amp; ils lui ont fait tout ce qu\'ils ont voulu, comme il eft écrit de lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 14, 'Puis eftant revenu vers les difciples, il vid une grande troupe à l\'entour d\'eux, &amp; des Scribes qui queftionnoyent avec eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 15, 'Et incontinent toute la troupe le voyant, fut faifie d\'eftonnement, &amp; accourans ils le faluërent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 16, 'Alors il interrogea les Scribes, difant, Dequoi eftes-vous en queftion avec eux?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 17, 'Et quelqu\'un de la troupe répondit, &amp; dit, Maiftre, je t\'ai amené mon fils qui a un efprit muët,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 18, 'Qui le dérompt par tout où il le prend; &amp; alors il écume &amp; grince les dents, &amp; devient fec: &amp; j\'ai requis tes difciples qu\'ils le jettaffent hors, mais ils n\'ont peu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 19, 'Alors Jefus lui répondant, dit, O nation incredule! jufques à quand finalement ferai-je avec vous? jufques à quand finalement vous fupporterai-je? Amenez-le moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 20, 'Ils l\'amenerent donc jufques à lui, &amp; quand il l\'eut veu, incontinent l\'efprit le dérompit, dont l\'enfant tomba à terre, &amp; fe tournoit ça &amp; là en écumant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 21, 'Alors il interrogea le pere d\'icelui, difant, Combien y a-t\'il de temps que ceci lui eft advenu? Lequel dit, Dés fon enfance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 22, 'Et fouvent il l\'a jetté, &amp; au feu, &amp; en l\'eau, pour le faire perir: mais fi tu y peux quelque chofe, aide nous, eftant émeu de compaffion envers nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 23, 'Alors Jefus lui dit, Si tu le peux croire, toutes chofes font poffibles au croyant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 24, 'Et incontinent le pere de l\'enfant s\'écriant avec larmes, dit, Je crois, Seigneur: fubvien à mon incredulité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 25, 'Et quand Jefus vid que le peuple y accouroit l\'un fur l\'autre il tança l\'efprit immonde, lui difant, Efprit muët &amp; fourd, je te commande, moi, Sors de lui, &amp; n\'y rentre jamais.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 26, 'Alors l\'efprit fortit, en s\'écriant, &amp; le dérompant bien fort, dont l\'enfant devint comme mort, tellement que plufieurs difoyent, Il eft mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 27, 'Mais Jefus l\'ayant pris par la main, le dreffa, &amp; il fe leva.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 28, 'Puis eftant entré en la maifon, fes difciples l\'interrogerent à part, difans, Pourquoi ne l\'avons-nous pû jetter dehors?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 29, 'Et il leur refpondit, Cette efpece de diables ne peut fortir autrement que par oraifon &amp; par jeune.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 30, 'Puis eftant partis de là, ils cheminerent par la Galilée: &amp; il ne voulut point qu\'aucun le fçeuft.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 31, 'Car il enfeignoit fes difciples, &amp; leur difoit, Le Fils de l\'homme, s\'en va eftre livré és mains des hommes, &amp; ils le feront mourir: mais apres qu\'il aura efté mis à mort, il reffufcitera au troifiéme jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 32, 'Mais ils n\'entendoyent point ce difcours, &amp; craignoyent de le lui demander.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 33, 'Apres ces chofes il vint à Capernaum: &amp; quand il fut en la maifon, il les interrogea, difant, Dequoi difputiez-vous entre vous par le chemin?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 34, 'Et ils fe teurent: car ils avoyent difputé les uns contre les autres en chemin, qui eftoit le plus grand d\'entr\'eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 35, 'Et quand il fut affis, il appella les douze, &amp; leur dit, Si quelqu\'un veut eftre le premier, il fera le dernier de tous, &amp; le ferviteur de tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 36, 'Et ayant pris un petit enfant, il le mit au milieu d\'eux: &amp; apres l\'avoir pris entre fes bras, il leur dit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 37, 'Quiconque recevra l\'un de tels petits enfans en mon Nom, il me reçoit: &amp; quiconque me reçoit, il ne me reçoit pas, mais il reçoit celui qui m\'a envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 38, 'Alors Jean prit la parole, &amp; dit, Maiftre, nous avons veu quelqu\'un qui jettoit hors les diables par ton Nom, lequel toutefois ne nous fuit point: &amp; nous l\'avons empefché, d\'autant qu\'il ne nous fuit point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 39, 'Et Jefus dit, Ne l\'empefchez point: car il n\'y a perfonne qui faffe vertu par mon Nom, qui auffi-toft puiffe mal parler de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 40, 'Car qui n\'eft point contre nous, il eft pour nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 41, 'Car quiconque vous donnera un verre d\'eau à boire en mon Nom, parce que vous eftes à Chrift, en verité je vous dis, qu\'il ne perdra pas fon falaire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 42, 'Et quiconque fcandalizera l\'un de ces petits qui croyent en moi, il lui vaudroit mieux qu\'on lui mift une pierre de meule à l\'entour de fon col, &amp; qu\'on le jettaft en la mer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 43, 'Que fi ta main te fait chopper, coupe-la: il vaut mieux que tu entres manchot en la vie, que d\'avoir deux mains, &amp; aller en la gehenne, au feu qui ne s\'efteint point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 44, 'Là où leur ver ne meurt point, &amp; le feu ne s\'efteint point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 45, 'Et fi ton pied te fait chopper, coupe-le: il vaut mieux que tu entres boiteux en la vie, que d\'avoir deux pieds, &amp; eftre jetté en la gehenne, au feu qui ne s\'efteint point:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 46, 'Là où leur ver ne meurt point, &amp; le feu ne s\'efteint point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 47, 'Si auffi ton oeil te fait chopper, arrache-le: il vaut mieux que tu entres au royaume de Dieu, n\'ayant qu\'un oeil, que d\'avoir deux yeux &amp; eftre jetté en la gehenne du feu:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 48, 'Là où leur ver ne meurt point, &amp; le feu ne s\'efteint point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 49, 'Car un chacun fera falé de feu, &amp; toute oblation fera falée de fel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 50, 'C\'eft une bonne chofe que le fel: mais fi le fel perd fa faveur, dequoi lui rendra-t\'on fa faveur? Ayez du fel en vous-mefmes, &amp; foyez en paix entre vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 1, 'Puis eftant parti de là, il vint és quartiers de Judée, outre le Jordain: &amp; derechef les troupes s\'affemblerent vers lui, &amp; derechef il les enfeignoit, comme il avoit accouftumé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 2, 'Alors les Pharifiens vinrent, &amp; le tentans lui demanderent, Eft-il permis à l\'homme de delaiffer fa femme?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 3, 'Il refpondit, &amp; leur dit, Qu\'eft-ce que Moyfe vous a commandé?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 4, 'Ils dirent, Moyfe a permis d\'écrire la lettre de divorce, &amp; de la delaiffer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 5, 'Et Jefus refpondant leur dit, Il vous a écrit ce commandement pour la dureté de voftre coeur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 6, 'Mais au commencement de la creation, Dieu les fit mafle &amp; femelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 7, 'Pour cette caufe l\'homme laiffera fon pere &amp; fa mere, &amp; s\'adjoindra à fa femme:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 8, 'Et les deux feront une mefme chair: partant ils ne font plus deux, mais une mefme chair.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 9, 'Donc ce que Dieu a conjoint, que l\'homme ne le fepare point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 10, 'Derechef fes difciples l\'interrogerent de cela mefme en la maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 11, 'Et il leur dit, Quiconque delaiffera fa femme, &amp; fe mariera à une autre,commet adultere contre elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 12, 'Pareillement fi la femme delaiffe fon mari, &amp; fe marie à un autre, elle commet adultere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 13, 'Et ils lui prefenterent de petits enfans, afin qu\'il les touchaft: mais les difciples tançoyent ceux qui les prefentoyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 14, 'Et Jefus voyant cela, il en fut indigné, &amp; leur dit, Laiffez les petits enfans venir à moi, &amp; ne les en empefchez point: car à tels eft le royaume de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 15, 'En verité je vous dis, Que quiconque ne recevra le royaume de Dieu comme petit enfant, n\'entrera point en icelui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 16, 'Et apres les avoir pris entre fes bras, leur impofant les mains, il les benit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 17, 'Et comme il fortit pour fe mettre en chemin, quelqu\'un accourut, &amp; s\'eftant agenoüillé devant lui, l\'interrogea, difant, Bon Maiftre, que ferai-je pour heriter la vie eternelle?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 18, 'Mais Jefus lui refpondit, Pourquoi m\'appelles-tu bon? il n\'y a nul bon qu\'un feul, affavoir Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 19, 'Tu fçais les commandemens: Ne commets point adultere. Ne tuë point. Ne dérobe point. Ne di point faux témoignage. Ne fai dommage à perfonne. Honore ton pere &amp; ta mere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 20, 'Il refpondit, &amp; lui dit, Maiftre, j\'ai gardé toutes ces chofes-là dés ma jeuneffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 21, 'Et Jefus ayant jetté l\'oeil fur lui, l\'aima, &amp; lui dit, Il te manque une chofe: va, &amp; ven tout ce que tu as, &amp; le donne aux pauvres, &amp; tu auras un trefor au ciel, puis vien, &amp; me fui, ayant chargé la croix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 22, 'Mais il fut fafché de ce mot, &amp; s\'en alla tout trifte: car il avoit de grands biens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 23, 'Alors Jefus ayant regardé à l\'entour, dit à fes difciples, Combien ceux qui ont des richeffes, entreront-ils difficilement au royaume de Dieu!');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 24, 'Et fes difciples s\'eftonnerent de ce difcours. Mais Jefus prenant derechef la parole, leur dit, Enfans, qu\'il eft difficile à ceux qui fe fient és richeffes, d\'entrer au royaume de Dieu!');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 25, 'Il eft plus aifé qu\'un chameau paffe par le trou d\'une aiguille, que non pas qu\'un riche entre au royaume de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 26, 'Dont ils s\'eftonnerent d\'autant plus, difans entr\'eux, Et qui peut eftre fauvé?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 27, 'Mais Jefus les ayant regardez, dit, Il eft impoffible quant aux hommes, mais non point quant à Dieu; car toutes chofes font poffibles à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 28, 'Alors Pierre fe prit à lui dire, Voici, nous avons tout abandonné, &amp; t\'avons fuivi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 29, 'Jefus refpondant dit, En verité je vous dis, qu\'il n\'y a nul qui ait laiffé maifon, ou freres, ou foeurs, ou pere, ou mere, ou femme, ou enfans, ou champs, pour l\'amour de moi, &amp; de l\'Evangile,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 30, 'Qui n\'en reçoive maintenant en ce temps-ici cent fois autant, maifons, &amp; freres, &amp; soeurs, &amp; mere, &amp; enfans, &amp; champs, avec perfecutions: &amp; au fiecle à venir, la vie eternelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 31, 'Mais plufieurs qui font les premiers, feront les derniers: &amp; les derniers feront les premiers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 32, 'Or ils eftoyent en chemin, montans à Jerufalem: &amp; Jefus alloit devant eux; &amp; ils s\'épouvantoyent, &amp; craignoyent en le fuivant. Et ayant derechef pris les douze, il fe mit à leur declarer les chofes qui lui devoyent avenir,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 33, 'Difant, Voici, nous montons à Jerufalem: &amp; le Fils de l\'homme fera livré aux principaux Sacrificateurs, &amp; aux Scribes: &amp; ils le condamneront à mort, &amp; le livreront aux nations:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 34, 'Et fe mocqueront de lui, &amp; le foüetteront, &amp; cracheront contre lui, puis ils le feront mourir: mais il reffufcitera au troifiéme jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 35, 'Alors Jaques &amp; Jean, fils de Zebedée vinrent à lui, difans, Maiftre, nous voudrions que tu nous fiffes ce que nous te demanderons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 36, 'Et il leur dit, Que voulez-vous que je vous faffe?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 37, 'Et ils lui dirent, Octroye-nous que nous foyons affis en ta gloire, l\'un à ta main droite &amp; l\'autre à ta gauche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 38, 'Et Jefus leur dit, Vous ne fçavez ce que vous demandez: pouvez-vous boire la coupe que je dois boire, &amp; eftre baptizez du baptefme dont je dois eftre baptizé?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 39, 'Et ils lui dirent, Nous le pouvons. Et Jefus leur dit, Il eft vrai que vous boirez la coupe que je dois boire, &amp; que vous ferez baptizez du baptefme dont je dois eftre baptizé:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 40, 'Mais de feoir à ma droite &amp; à ma gauche, ce n\'eft point à moi de le donner: mais il fera donné à ceux aufquels il eft preparé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 41, 'Ce que les dix autres ayant ouï, ils commencerent à s\'indigner contre Jaques &amp; Jean.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 42, 'Mais Jefus les appellant, leur dit, Vous fçavez que ceux qui font eftat de dominer fur les nations, les maiftrifent: &amp; les grands d\'entr\'eux ufent d\'authorité fur elles:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 43, 'Mais il ne fera point ainfi entre vous: ains quiconque voudra eftre le plus grand entre vous, fera voftre miniftre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 44, 'Et quiconque d\'entre vous voudra eftre le premier, fera le ferviteur de tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 45, 'Car auffi le Fils de l\'homme n\'eft point venu pour eftre fervi: mais pour fervir, &amp; donner fa vie en rançon pour plufieurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 46, 'Puis apres ils arriverent à Jerico: &amp; lui partant de Jerico avec fes difciples, &amp; une grande troupe, un aveugle, dit Bartimée, c\'eft à dire, fils de Timée, eftoit affis auprés du chemin, &amp; mendioit:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 47, 'Lequel ayant entendu que c\'eftoit Jefus le Nazarien, fe prit à crier, &amp; à dire, Jefus Fils de David, aye pitié de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 48, 'Et plufieurs le tançoyent, afin qu\'il fe teuft: mais il crioit tant plus fort, Fils de David, aye pitié de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 49, 'Et Jefus s\'eftant arrefté, dit qu\'on l\'appellaft: &amp; ils appellerent l\'aveugle, lui difans, Pren courage, leve-toi: il t\'appelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 50, 'Et en jettant bas fon manteau, il fe leva, &amp; s\'en vint vers Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 51, 'Alors Jefus prenant la parole, lui dit, Que veux-tu que je te faffe? Et l\'aveugle lui dit, Maiftre, que je recouvre la veuë.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 52, 'Et Jefus lui dit, Va-t\'en, ta foi t\'a fauvé. Et incontinent il recouvra la veuë, &amp; fuivoit Jefus par le chemin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 1, 'Et comme ils approchoyent de Jerufalem, eftans prés de Bethphagé &amp; de Bethanie, vers le mont des Oliviers, il envoya deux de fes difciples.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 2, 'Et il leur dit, Allez-vous-en en cette bourgade qui eft vis à vis de vous: &amp; incontinent que vous y entrerez, vous trouverez un afnon attaché, fur lequel jamais homme ne s\'affit: détachez-le, &amp; l\'amenez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 3, 'Et fi quelqu\'un vous dit, Pourquoi faites-vous cela? dites que le Seigneur en a affaire: &amp; incontinent il l\'envoyera ici.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 4, 'Ils partirent donc, &amp; trouverent l\'afnon qui eftoit attaché dehors aupres de la porte, entre deux chemins, &amp; le détacherent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 5, 'Et quelques-uns de ceux qui eftoyent là, leur dirent, Que voulez-vous faire de détacher cet afnon?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 6, 'Mais ils leur refpondirent comme Jefus leur avoit commandé: &amp; ils les laifferent aller.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 7, 'Ils amenerent donc l\'afnon à Jefus, &amp; mirent leurs veftemens fur icelui: &amp; il s\'affit deffus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 8, 'Et plufieurs eftendoyent leurs veftemens par le chemin: les autres coupoyent des rameaux des arbres, &amp; les efpandoyent par le chemin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 9, 'Et ceux qui alloyent devant, &amp; ceux qui fuivoyent, crioyent, difans, Hofanna: Benit foit celui qui vient au nom du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 10, 'Benit foit le regne de David noftre pere, lequel vient au nom du Seigneur: Hofanna és lieux tres-hauts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 11, 'Ainfi Jefus entra dans Jerufalem, &amp; au temple: &amp; quand il eut regardé de toutes parts, &amp; que déja il eftoit tard, il fortit pour aller à Bethanie avec les douze:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 12, 'Et le lendemain quand ils furent partis de Bethanie, il eut faim:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 13, 'Et voyant de loin un figuier qui avoit des fueilles, il s\'y en alla pour voir s\'il y trouveroit quelque chofe: &amp; y eftant venu, il ne trouva rien finon des fueilles: car ce n\'eftoit pas la faifon des figues.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 14, 'Alors Jefus prenant la parole dit au figuier, Qu\'à jamais perfonne ne mange du fruit de toi. Et fes difciples l\'entendirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 15, 'Ils vinrent donc à Jerufalem. Et quand Jefus fut entré au temple, il fe prit à jetter hors ceux qui vendoyent &amp; achetoyent au temple, &amp; renverfa les tables des changeurs, &amp; les felles de ceux qui vendoyent des pigeons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 16, 'Et ne permettoit point que perfonne portaft aucun vaiffeau par le temple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 17, 'Et enfeignoit, en leur difant, N\'eft-il pas écrit, Ma Maifon fera appellée maifon d\'oraifon par toutes nations? mais vous en avez fait une caverne de brigands.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 18, 'Ce que les Scribes &amp; les principaux Sacrificateurs ayant entendu, ils cherchoyent comment ils le feroyent perir: car ils le craignoyent, à caufe que tout le commun peuple s\'eftonnoit de fa doctrine.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 19, 'Et le foir eftant venu, Jefus fortit de la ville.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 20, 'Et le matin comme ils paffoyent aupres du figuier,ils le virent feché dés les racines.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 21, 'Alors Pierre s\'eftant reffouvenu, lui dit, Maiftre, voici, le figuier que tu as maudit eft feché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 22, 'Et Jefus refpondant leur dit, Ayez la foi de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 23, 'En verité je vous dis, que quiconque dira à cette montagne, Enleve-toi, &amp; te jette en la mer: &amp; ne fera point de difficulté en fon coeur, mais croira que ce qu\'il dit fe fera, tout ce qu\'il aura dit lui fera fait.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 24, 'Partant je vous dis, Tout ce que vous demanderez en priant, croyez que vous le recevrez: &amp; il vous fera fait.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 25, 'Mais quand vous vous prefenterez pour faire voftre oraifon, pardonnez fi vous avez quelque chofe contre quelqu\'un, comme voftre Pere qui eft és cieux vous pardonne auffi vos fautes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 26, 'Car fi vous ne pardonnez, voftre Pere qui eft és cieux ne vous pardonnera point auffi vos fautes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 27, 'Puis derechef ils vinrent à Jerufalem: &amp; comme il cheminoit au temple, les principaux Sacrificateurs, &amp; les Scribes, &amp; les Anciens vinrent vers lui:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 28, 'Et ils lui dirent, De quelle authorité fais-tu ces chofes? &amp; qui eft celui qui t\'a donné cette authorité que tu faffes ces chofes?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 29, 'Et Jefus refpondant leur dit, Je vous interrogerai auffi d\'une chofe, &amp; me refpondez: &amp; alors je vous dirai de quelle authorité je fais ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 30, 'Le Baptefme de Jean eftoit-il du ciel ou des hommes? Refpondez-moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 31, 'Or ils difputoyent entr\'eux, difans, Si nous difons, Du ciel, il dira, Pourquoi ne l\'avez-vous point creu?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 32, 'D\'autre-part, fi nous difons, Des hommes, nous craignons le commun peuple. Car tous tenoyent que Jean avoit efté vrayement Prophete.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 33, 'Alors pour refponfe ils dirent à Jefus, Nous ne fçavons. Et Jefus refpondant leur dit, Je ne vous dirai point auffi de quelle authorité je fais ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 1, 'Puis il fe prit à leur dire par fimilitude, Quelqu\'un planta une vigne, &amp; l\'environna d\'une haye, &amp; y creufa une foffe pour un preffoir, &amp; y baftit une tour: puis apres il la loüa à des vignerons, &amp; s\'en alla dehors.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 2, 'Or en la faifon il envoya un ferviteur vers les vignerons, afin de recevoir d\'eux du fruit de la vigne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 3, 'Mais eux le prenans, le battirent &amp; le renvoyerent à vuide.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 4, 'Derechef il leur envoya un autre ferviteur: &amp; lui jettans des pierres ils lui froifferent toute la tefte, &amp; le renvoyerent, l\'ayant honteufement traitté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 5, 'Et derechef il en envoya encore un autre, lequel ils tuërent: &amp; plufieurs autres, defquels ils battirent les uns, &amp; tuërent les autres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 6, 'Or ayant encore un fils bien-aimé, il le leur envoya auffi pour le dernier, difant, Ils revereront mon fils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 7, 'Mais ces vignerons-là dirent entr\'eux, C\'eft ici l\'heritier: venez, tuons-le, &amp; l\'heritage fera noftre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 8, 'Et le prenans ils le tuërent, &amp; le jetterent hors de la vigne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 9, 'Que fera donc le feigneur de la vigne? Il viendra, &amp; fera perir ces vignerons-là, &amp; baillera la vigne à d\'autres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 10, 'Et n\'avez-vous point leu cette Ecriture? La pierre que les edifians ont rejettée, eft devenuë la maiftreffe pierre du coin:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 11, 'Ceci a efté fait par le Seigneur, &amp; c\'eft une chofe merveilleufe devant nos yeux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 12, 'Alors ils tafcherent de le faifir, mais ils craignirent le peuple: car ils connurent bien qu\'il avoit dit cette fimilitude contr\'eux: &amp; en le laiffant, ils s\'en allerent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 13, 'Or ils envoyerent quelques-uns des Pharifiens &amp; des Herodiens, pour l\'enlacer en paroles:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 14, 'Lefquels eftans venus vers lui, lui dirent, Maiftre, nous fçavons que tu es veritable, &amp; que tu ne te foucies d\'aucun: car tu n\'as point d\'égard à l\'apparence des hommes, mais tu enfeignes la voye de Dieu en verité: Eft-il permis de payer le tribut à Cefar, ou non? le payerons-nous, ou fi nous ne le payerons point?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 15, 'Lui connoiffant leur hypocrifie, leur dit, Pourquoi me tentez-vous? apportez moi un denier, que je le voye.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 16, 'Et ils le lui prefenterent. Alors il leur dit, De qui eft cette image, &amp; cette infcription? Ils lui dirent, De Cefar.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 17, 'Et Jefus refpondant leur dit, Rendez à Cefar les chofes qui font à Cefar, &amp; à Dieu celles qui font à Dieu. Et ils en furent eftonnez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 18, 'Alors les Sadduciens (qui difent qu\'il n\'y a point de refurrection) vinrent à lui, &amp; l\'interrogerent, difans,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 19, 'Maiftre, Moyfe nous a laiffé par efcrit, Que fi le frere de quelqu\'un eft mort, &amp; a laiffé fa femme, &amp; n\'a point laiffé d\'enfans, que fon frere prenne la femme d\'icelui, &amp; qu\'il fufcite lignée à fon frere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 20, 'Or il y avoit fept freres, dont le premier prit une femme, &amp; mourant ne laiffa point de lignée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 21, 'Et le fecond la prit, &amp; mourut: &amp; lui auffi ne laiffa point de lignée. Et le troifiéme femblablement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 22, 'Les fept donc la prirent, ne laiffans point de lignée. La femme auffi mourut la derniere de tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 23, 'En la refurrection donc, quand ils feront reffufcitez, duquel d\'eux fera-t\'elle femme? car les fept l\'ont euë à femme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 24, 'Alors Jefus refpondant leur dit, Ce que vous vous fourvoyez, n\'eft-ce pas dautant que vous ne fçavez point les Efcritures, ni la puiffance de Dieu?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 25, 'Car quand ils feront reffufcitez des morts, ils ne prendront point à femme, &amp; on ne leur baillera point de femmes en mariage: mais ils feront comme les Anges qui font és cieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 26, 'Et quant aux morts, pour montrer qu\'ils reffufcitent, n\'avez-vous point leu au livre de Moyfe, comme Dieu parla à lui au buiffon, difant, Je fuis le Dieu d\'Abraham, &amp; le Dieu d\'Ifaac, &amp; le Dieu de Jacob?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 27, 'Dieu n\'eft point le Dieu des morts, mais le Dieu des vivans. Vous vous fourvoyez donc grandement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 28, 'Alors quelqu\'un des Scribes eftant venu là, les ayant ouïs difputer enfemble, &amp; fçachant qu\'il leur avoit refpondu bien à propos, l\'interrogea, difant, Quel eft le premier commandement de tous?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 29, 'Jefus lui refpondit, Le premier de tous les commandemens eft, Efcoute Ifraël, Le Seigneur noftre Dieu eft le feul Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 30, 'Et tu aimeras le Seigneur ton Dieu de tout ton coeur, &amp; de toute ton ame, &amp; de toute ta penfée, &amp; de toute ta force. Celui-ci eft le premier commandement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 31, 'Et le fecond femblable à icelui, eft, Tu aimeras ton prochain comme toi-mefme. Il n\'y a point d\'autre commandement plus grand que ceux-ci.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 32, 'Et le Scribe lui dit, Maiftre, tu as bien dit à la verité, qu\'il y a un feul Dieu, &amp; qu\'il n\'y en a point d\'autre que lui:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 33, 'Et que l\'aimer de tout fon coeur, &amp; de toute fon intelligence, &amp; de toute fon ame, &amp; de toute fa force, &amp; aimer fon prochain comme foi-mefme, eft plus que tous les holocauftes &amp; les facrifices.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 34, 'Et Jefus voyant qu\'il avoit refpondu prudemment, lui dit, Tu n\'es pas loin du royaume de Dieu. Et nul ne l\'ofoit plus interroger.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 35, 'Et Jefus enfeignant au temple, prenant la parole, leur dit, Comment eft-ce que les Scribes difent que le Chrift eft fils de David?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 36, 'Car David mefme a dit par le faint Efprit, le Seigneur a dit à mon Seigneur, Sieds-toi à ma dextre, jufques à ce que j\'aye mis tes ennemis pour le marchepied de tes pieds.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 37, 'David donc lui-mefme l\'appelle Seigneur; comment donc eft-il fon fils? Et de grandes troupes prenoyent plaifir à l\'ouïr.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 38, 'Davantage, il leur difoit en fa doctrine, Donnez-vous garde des Scribes, qui aiment à fe promener en robbes longues, &amp; les falutations és marchez:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 39, 'Et les premieres feances és Synagogues, &amp; les premieres places és banquets:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 40, 'Qui mangent entierement les maifons des veuves: voire un faifant femblant de prier beaucoup; ils en recevront plus grande condamnation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 41, 'Jefus auffi eftant affis vis à vis du tronc, prenoit garde comment le peuple mettoit de l\'argent au tronc.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 42, 'Or plufieurs riches y mettoyent beaucoup: &amp; une pauvre veuve vint, laquelle y mit deux petites pieces qui font un quadrin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 43, 'Alors appellant fes difciples, il leur dit, En verité je vous dis, que cette pauvre veuve a plus mis au tronc que tous ceux qui y ont mis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 44, 'Car tous y ont mis de ce qui leur abonde: mais celle-ci y a mis de fa pauvreté tout ce qu\'elle avoit, voire toute fa fubftance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 1, 'Et comme il partoit du temple, un de fes difciples lui dit, Maiftre, regarde quelles pierres, &amp; quels baftimens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 2, 'Alors Jefus refpondant lui dit, Ne vois-tu pas ces grands baftimens? Il ne fera laiffé pierre fur pierre qui ne foit demolie,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 3, 'Et comme il étoit affis au mont des Oliviers vis à vis du temple, Pierre &amp; Jaques, Jean &amp; André l\'interrogerent à part,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 4, 'Difans, Di nous quand ces chofes-là adviendront, &amp; quel fera le figne quand toutes ces chofes doivent eftre accomplies.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 5, 'Alors Jefus leur refpondant, commença à leur dire, Prenez garde que quelqu\'un ne vous feduife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 6, 'Car plufieurs viendront en mon Nom, difans, C\'eft moi qui fuis le Chrift, &amp; en feduiront plufieurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 7, 'Or quand vous orrez des guerres &amp; des bruits de guerres, ne foyez point troublez: car il faut que ces chofes adviennent, mais ce ne fera pas encore la fin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 8, 'Car nation s\'élevera contre nation, &amp; royaume contre royaume: &amp; il y aura des tremblemens de terre en tous lieux, &amp; des famines &amp; des troubles: ces chofes-là feront des commencemens de douleurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 9, 'Mais prenez garde à vous-mefmes: car ils vous livreront aux confiftoires, &amp; aux Synagogues: vous ferez foüettez, &amp; ferez prefentez devant les gouverneurs &amp; les rois, à caufe de moi, pour leur eftre en tefmoignage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 10, 'Et il faut que l\'Evangile foit premierement prefché en toutes les nations.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 11, 'Or quand ils vous meneront pour vous livrer, ne foyez point auparavant en fouci de ce que vous aurez à dire, &amp; n\'y meditez point: mais tout ce qui vous fera donné en cet inftant-là, dites-le: car ce n\'eft pas vous qui parlez, mais le S. Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 12, 'Alors le frere livrera fon frere à la mort, &amp; le pere l\'enfant: &amp; les enfans s\'éleveront contre leurs peres &amp; leurs meres, &amp; les feront mettre à mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 13, 'Et vous ferez haïs de tous à caufe de mon Nom: mais qui perfeverera jufques à la fin, celui-là fera fauvé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 14, 'Or quand vous verrez l\'abomination de la defolation (qui a efté dite par Daniel le Prophete) eftre eftablie là où elle ne doit point eftre: (qui lit l\'entende) alors que ceux qui feront en Judée s\'enfuyent aux montagnes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 15, 'Et que celui qui fera fur la maifon, ne defcende point en la maifon, &amp; n\'y entre point pour emporter aucune chofe de fa maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 16, 'Et que celui qui fera au champ, ne retourne point en arriere, pour emporter fon habillement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 17, 'Mais mal-heur fur celles qui feront enceintes, &amp; fur celles qui allaitteront en ces jours-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 18, 'Priez donc que voftre fuite n\'advienne point en hyver.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 19, 'Car en ces jours-là il y aura une telle affliction, qu\'il n\'y en a point eu de femblable depuis le commencement de la creation des chofes que Dieu a creées, jufques à maintenant, &amp; n\'y en aura.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 20, 'Et fi le Seigneur n\'euft abbregé ces jours-là, nulle perfonne ne feroit fauvée: mais il a abbregé ces jours-là, à caufe des éleus qu\'il a éleus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 21, 'Et alors fi quelqu\'un vous dit, Voici, le Chrift eft ici: ou, Voici, il eft là, ne le croyez point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 22, 'Car de faux Chrifts &amp; de faux Prophetes s\'éleveront, &amp; feront des fignes &amp; des miracles, pour feduire mefmes les éleus, s\'il eftoit poffible.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 23, 'Mais donnez-vous en garde: voici, je vous ai predit le tout.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 24, 'Auffi en ces jours-là, apres cette affliction-là, le Soleil fera obfcurci, &amp; la Lune ne donnera point de clarté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 25, 'Et les étoiles du ciel cherront, &amp; les vertus qui font és cieux feront efbranlées.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 26, 'Et alors ils verront le Fils de l\'homme venir és nuées avec grande puiffance &amp; gloire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 27, 'Alors il envoyera fes Anges, &amp; affemblera en un fes éleus, des quatre vents, depuis le bout de la terre jufques au bout du ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 28, 'Or apprenez la fimilitude du figuier: Quand fon rameau eft en féve, &amp; qu\'il jette des fueilles, vous connoiffez que l\'efté eft prochain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 29, 'Vous auffi pareillement, quand vous verrez que ces chofes-là adviendront, fçachez qu\'il eft prochain à la porte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 30, 'En verité je vous dis, que cette generation ne paffera point, tant que toutes ces chofes-là foyent faites.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 31, 'Le ciel &amp; la terre pafferont, mais mes paroles ne pafferont point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 32, 'Or quant à ce jour-là &amp; à l\'heure, nul ne le fçait, non pas mefmes les Anges qui font au Ciel, ni auffi le Fils: mais le Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 33, 'Prenez garde à vous veillez, &amp; priez: car vous ne fçavez quand fera ce temps-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 34, 'C\'eft comme fi un homme allant dehors, laiffoit fa maifon, &amp; donnoit authorité à fes ferviteurs, &amp; à un chacun fa tafche, &amp; commandoit au portier qu\'il veillaft.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 35, 'Veillez donc: (car vous ne fçavez quand le Seigneur de la maifon viendra, au foir, ou à minuit, ou à l\'heure que le coq chante, ou au matin.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 36, 'De peur qu\'arrivant foudainement, il vous trouve dormans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 37, 'Or les chofes que je vous dis, je les dis à tous: Veillez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 1, 'Or la fefte de Pafque, &amp; des pains fans levain, eftoit deux jours apres: &amp; les principaux Sacrificateurs &amp; les Scribes cherchoyent comment ils le pourroyent faifir par fineffe: &amp; le mettre à mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 2, 'Mais ils difoyent, Non point durant la fefte, de peur qu\'il fe face tumulte entre le peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 3, 'Et comme il eftoit à Bethanie en la maifon de Simon, dit le lepreux, &amp; qu\'il eftoit à table, il vint là une femme qui avoit une boite d\'onguent d\'afpic liquide precieux: &amp; elle rompit la boite, &amp; efpandit l\'onguent fur la tefte d\'icelui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 4, 'Dont quelques-uns furent indignez en eux-mefmes, &amp; difoyent, A quoi fert le dégaft de cet onguent?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 5, 'Car cet onguent pouvoit eftre vendu plus de trois cent deniers, &amp; eftre donné aux pauvres. Ainfi ils en fremiffoyent contr\'elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 6, 'Mais Jefus dit, Laiffez-la, pourquoi lui donnez-vous de la fafcherie? elle a fait un bon acte envers moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 7, 'Parce que vous aurez toûjours les pauvres avec vous, &amp; vous leur pourrez bien faire, toutes les fois que vous voudrez: mais vous ne m\'aurez point toûjours.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 8, 'Elle a fait ce qui eftoit en elle: elle a anticipé d\'oindre mon corps pour l\'appareil de ma fepulture.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 9, 'En verité je vous dis, qu\'en quelque lieu que cet Evangile fera prefché en tout le monde, cela auffi qu\'elle a fait fera recité en memoire d\'elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 10, 'Alors Judas Ifcariot, l\'un des douze, s\'en alla vers les principaux Sacrificateurs, afin qu\'il le leur livraft.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 11, 'Lefquels l\'ayant ouï s\'éjouïrent, &amp; lui promirent de lui donner de l\'argent: &amp; il cherchoit comment il le livreroit commodément.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 12, 'Or le premier jour des pains fans levain qu\'on facrifioit l\'agneau de Pafque, fes difciples lui dirent, Où veux-tu que nous allions, afin de t\'apprefter à manger l\'agneau de Pafque?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 13, 'Alors il envoya deux de fes difciples, &amp; leur dit, Allez en la ville, &amp; un homme vous viendra à la rencontre, portant une cruche d\'eau: fuivez-le.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 14, 'Et en quelque lieu qu\'il entrera, dites au maiftre de la maifon: Le maiftre dit, Où eft le logis où je mangerai l\'agneau de Pafque avec mes difciples?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 15, 'Et il vous montrera une grande chambre ornée &amp; preparée: appreftez-nous là l\'agneau de Pafque.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 16, 'Ainfi les difciples partirent, &amp; vinrent en la ville, &amp; trouverent comme il leur avoit dit, &amp; apprefterent l\'agneau de Pafque.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 17, 'Puis le foir eftant venu, il s\'en vint avec les douze.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 18, 'Et comme ils eftoyent à table, &amp; mangeoyent, Jefus dit, En verité je vous dis, que l\'un de vous qui mange avec moi me trahira.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 19, 'Alors ils fe prirent à fe contrifter: &amp; lui dirent l\'un apres l\'autre, Eft-ce moi? &amp; l\'autre, Eft-ce moi?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 20, 'Mais il refpondit, &amp; leur dit, C\'eft l\'un des douze, lequel trempe avec moi au plat.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 21, 'Certes le Fils de l\'homme s\'en va, felon qu\'il eft écrit de lui: mais mal-heur à cet homme-là par lequel le Fils de l\'homme eft trahi: il euft efté bon à cet homme-là de n\'eftre point né.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 22, 'Et comme ils mangeoyent, Jefus prit le pain: &amp; apres avoir rendu graces, il le rompit: puis le leur bailla, &amp; dit, Prenez, mangez, ceci eft mon corps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 23, 'Puis ayant pris la coupe, il rendit graces, &amp; la leur bailla: &amp; ils en burent tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 24, 'Et il leur dit, Ceci eft mon fang, le fang du nouveau Teftament, lequel eft refpandu pour plufieurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 25, 'En verité je vous dis, que je ne boirai plus du fruit de la vigne, jufques à ce jour-là que je le boirai nouveau au royaume de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 26, 'Et quand ils eurent chanté le cantique, ils s\'en allerent en la montagne des Oliviers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 27, 'Alors Jefus leur dit, Vous ferez tous fcandalifez en moi cette nuit-ici: car il eft écrit, Je fraperai le berger, &amp; les brebis feront éparfes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 28, 'Mais apres que je ferai reffufcité, j\'irai devant vous en Galilée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 29, 'Et Pierre lui dit, Quand bien tous feroyent fcandalizez, fi eft-ce que je ne le ferai point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 30, 'Alors Jefus lui dit, En verité je te dis, qu\'aujourd\'hui en cette propre nuit, devant que le coq ait chanté deux fois tu me renieras par trois fois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 31, 'Mais il difoit encore de plus fort, Quand mefmes il me faudroit mourir avec toi, fi eft-ce que je ne te renierai point. Et tous auffi difoyent de mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 32, 'Puis apres ils vinrent en un lieu nommé Gethfemané: &amp; il dit à fes difciples, Sées-vous ici, jufques à ce que j\'aye prié.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 33, 'Alors il prit avec foi Pierre, &amp; Jaques, &amp; Jean, &amp; commença à s\'épouvanter, &amp; à eftre fort angoiffé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 34, 'Alors il leur dit, Mon ame eft faifie de trifteffe jufques à la mort: demeurez ici, &amp; veillez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 35, 'Et s\'en allant un peu plus outre, il fe jetta en terre: &amp; prioit, Que s\'il fe pouvoit faire, l\'heure fe paffaft arriere de lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 36, 'Et il difoit, Abba, Pere, toutes chofes te font poffibles, tranfporte cette coupe arriere de moi: toutesfois non point ce que je veux, mais ce que tu veux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 37, 'Puis il vint, &amp; les trouva dormans: &amp; il dit à Pierre, Simon, dors-tu? n\'as-tu peu veiller une heure?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 38, 'Veillez, &amp; priez, que vous n\'entriez en tentation: car quant à l\'efprit il eft prompt, mais la chair eft foible.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 39, 'Et derechef il s\'en alla, &amp; pria, difant le mefme propos.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 40, 'Puis eftant retourné, il les trouva derechef dormans, car leurs yeux eftoyent appefantis: &amp; ils ne fçavoyent que lui refpondre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 41, 'Puis il vint pour la troifiéme fois, &amp; leur dit, Dormez dorefnavant, &amp; vous repofez: il fuffit, l\'heure eft venuë: voici, le Fils de l\'homme s\'en va eftre livré és mains des méchans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 42, 'Levez-vous, allons: voici, celui qui me trahit s\'approche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 43, 'Et auffi-toft, comme il parloit encore, Judas (qui eftoit l\'un des douze) vint &amp; une grande troupe avec lui, avec des efpées &amp; des baftons, de par les principaux Sacrificateurs, &amp; les Scribes, &amp; les Anciens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 44, 'Or celui qui le trahiffoit avoit donné un fignal entr\'eux, difant, Quiconque je baiferai, c\'eft celui-là, faififfez-le &amp; l\'emmenez feurement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 45, 'Quand donc il fut venu, incontinent il s\'approcha de lui, &amp; dit, Maiftre, Maiftre: &amp; le baifa.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 46, 'Alors ils jetterent les mains fur lui, &amp; le faifirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 47, 'Et quelqu\'un de ceux qui eftoyent là prefens tira fon efpée, &amp; en frapa le ferviteur du fouverain Sacrificateur, &amp; lui emporta l\'oreille.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 48, 'Alors Jefus prit la parole, &amp; leur dit, Eftes-vous fortis comme apres un brigand avec des efpées &amp; des baftons, pour me prendre?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 49, 'J\'eftois tous les jours entre vous, enfeignant au temple, &amp; vous ne m\'avez point faifi: mais tout ceci eft advenu afin que les Efcritures foyent accomplies.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 50, 'Alors tous l\'ayans abandonné, s\'enfuïrent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 51, 'Et un certain jeune homme le fuivoit, enveloppé d\'un linceul fur le corps nud: &amp; quelques jeunes gens le faifirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 52, 'Mais en laiffant fon linceul, il s\'enfuït arriere d\'eux tout nud.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 53, 'Or de là ils emmenerent Jefus au fouverain Sacrificateur, chez lequel s\'affemblerent tous les principaux Sacrificateurs, les Anciens, &amp; les Scribes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 54, 'Et Pierre le fuivoit de loin jufques dedans, en la court du fouverain Sacrificateur: &amp; il eftoit affis avec les ferviteurs, &amp; fe chauffoit au feu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 55, 'Or les principaux Sacrificateurs &amp; tout le confiftoire cherchoyent un tefmoignage contre Jefus, pour le mettre à mort: &amp; n\'en trouvoyent point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 56, 'Car plufieurs difoyent de faux témoignages contre lui, mais les tefmoignages n\'eftoyent point conformes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 57, 'Alors quelques-uns s\'éleverent, &amp; porterent faux tefmoignage contre lui, difans,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 58, 'Nous l\'avons ouï qu\'il difoit, Je déferai ce temple-ici qui eft fait de main, &amp; en trois jours j\'en rebaftirai un autre qui ne fera point fait de main.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 59, 'Mais encore avec tout cela leurs tefmoignages n\'eftoyent point conformes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 60, 'Alors le fouverain Sacrificateur fe levant au milieu, interrogea Jefus, difant, Ne refpons-tu rien? qu\'eft-ce que tefmoignent ceux-ci contre toi?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 61, 'Mais il fe teut, &amp; ne refpondit rien. Derechef le fouverain Sacrificateur l\'interrogea, &amp; lui dit, Es-tu le Chrift le Fils de Dieu benit?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 62, 'Et Jefus lui dit, Je le fuis: &amp; vous verrez le Fils de l\'homme affis à la dextre de la vertu de Dieu, &amp; venant és nuées du ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 63, 'Alors le fouverain Sacrificateur defchira fes veftemens, &amp; dit, Qu\'avons-nous encore à faire de tefmoins?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 64, 'Vous avez ouï le blafpheme: que vous en femble? Alors eux tous le condamnerent comme eftant coupable de mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 65, 'Et quelques-uns fe prirent à cracher contre lui, &amp; à lui couvrir la face, &amp; à lui bailler des foufflets: &amp; lui difoyent, Prophetize nous. Et les fergens lui bailloyent des coups de leurs verges.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 66, 'Or comme Pierre eftoit en bas en la court, une des fervantes du fouverain Sacrificateur vint:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 67, 'Et quand elle eut apperceu Pierre qui fe chauffoit, elle le regarda en face, &amp; lui dit, Et toi, tu eftois avec Jefus le Nazarien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 68, 'Mais il le nia, difant, Je ne le connois point, &amp; ne fçai ce que tu dis: &amp; il fortit dehors au portail, &amp; le coq chanta.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 69, 'Et quand la fervante l\'eut veu derechef, elle fe prit à dire à ceux qui eftoyent là prefens, Celui-ci eft de ceux-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 70, 'Mais il le nia derechef. Et derechef un peu apres, ceux qui eftoyent là prefens, dirent à Pierre, Vrayement tu es de ceux-là, car tu es Galiléen, &amp; ton langage s\'y rapporte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 71, 'Alors il fe prit à fe maudire, &amp; à jurer, difant, Je ne connois point cet homme-là que vous dites.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 72, 'Et le coq chanta pour la feconde fois, &amp; Pierre fe reffouvint de la parole que Jefus lui avoit dite, Devant que le coq ait chanté deux fois, tu me renieras par trois fois. Et s\'eftant jetté hors il pleura.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 1, 'Et incontinent au matin, les principaux Sacrificateurs avec les Anciens &amp; les Scribes, &amp; tout le confiftoire, ayans tenu confeil, lierent Jefus, &amp; l\'emmenerent, &amp; le livrerent à Pilate.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 2, 'Et Pilate l\'interrogea, difant, Es-tu le roi des Juifs? Jefus refpondant lui dit, Tu le dis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 3, 'Et les principaux Sacrificateurs l\'accufoyent de plufieurs chofes: mais lui ne refpondoit rien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 4, 'Pilate donc l\'interrogea derechef, difant, Ne refpons-tu rien? voila combien de chofes ils tefmoignent contre toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 5, 'Mais Jefus ne refpondit rien non plus, tellement que Pilate s\'en eftonnoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 6, 'Or il leur relafchoit à la fefte un prifonnier, qui que ce fuft qu\'ils demandaffent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 7, 'Et il y en avoit un, dit Barrabas, qui eftoit prifonnier avec fes complices de fedition, lefquels avoyent commis meurtre en la fedition.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 8, 'Et le peuple s\'écriant tout haut, fe prit à demander qu\'il fift comme il leur avoit toûjours fait.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 9, 'Pilate donc leur refpondit, difant, Voulez-vous que je vous relafche le Roi des Juifs?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 10, 'Car il fçavoit bien que les principaux Sacrificateurs l\'avoyent livré par envie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 11, 'Mais les principaux Sacrificateurs émeurent le peuple, afin qu\'il leur relafchaft pluftoft Barrabas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 12, 'Et Pilate refpondant leur dit derechef, Que voulez-vous donc que je face à celui que vous appellez Roi des Juifs?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 13, 'Et derechef ils s\'écrierent, difant, Crucifie-le.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 14, 'Alors Pilate leur dit, Mais quel mal a-t\'il fait? Et ils s\'écrierent tant plus fort, Crucifie-le.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 15, 'Pilate donc voulant contenter le peuple, leur relafcha Barrabas, &amp; apres avoir fait foüetter Jefus, le livra, afin qu\'il fuft crucifié.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 16, 'Alors les foldats l\'emmenerent dans la court qui eft le pretoire, &amp; appellerent toute la bande.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 17, 'Et le veftirent d\'une robbe de pourpre, &amp; lui mirent à l\'entour de la tefte une couronne d\'épines qu\'ils avoyent pliée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 18, 'Et fe prirent à le faluër, en difant, Bien te foit, Roi des Juifs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 19, 'Et ils lui frapoyent la tefte avec un rofeau, &amp; crachoyent contre lui, &amp; fe mettans à genoux, lui faifoyent la reverence.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 20, 'Puis apres quand ils fe furent moquez de lui, ils le deveftirent de la robbe de pourpre, &amp; le reveftirent de fes veftemens, &amp; l\'emmenerent dehors pour le crucifier.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 21, 'Et contraignirent un certain paffant nommé Simon Cyrenien, lequel venoit des champs, qui eftoit pere d\'Alexandre &amp; de Rufus, de porter la croix d\'icelui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 22, 'Et puis le menerent en la place de Golgotha, qui vaut autant à dire que la place du Teft.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 23, 'Puis ils lui donnerent à boire du vin mixtionné avec de la myrrhe: mais il ne le prit point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 24, 'Et quand ils l\'eurent crucifié, ils partagerent fes veftemens, en jettant le fort fur iceux, pour fçavoir ce que chacun emporteroit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 25, 'Or il eftoit trois heures quand ils le crucifierent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 26, 'Et le dicton de fa condamnation portoit en écrit, LE ROI DES JUIFS.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 27, 'Ils crucifierent auffi avec lui deux brigands: l\'un à fa droite, &amp; l\'autre à fa gauche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 28, 'Ainfi fut accomplie l\'Efcriture qui dit, Et il a efté mis au rang des malfaiteurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 29, 'Et ceux qui paffoyent prés de là lui difoyent des outrages, hochans la tefte, &amp; difans, Hé! toi, qui defais le temple &amp; le rebaftis en trois jours,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 30, 'Sauve-toi toi-mefme, &amp; defcen de la croix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 31, 'Semblablement auffi les principaux Sacrificateurs mefmes fe moquans avec les Scribes difoyent les uns aux autres, Il a fauvé les autres, il ne fe peut fauver foi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 32, 'Que le Chrift, le Roi d\'Ifraël, defcende maintenant de la croix, afin que nous le voyïons, &amp; le croyïons. Et ceux auffi qui eftoyent crucifiez avec lui, lui difoyent des outrages.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 33, 'Mais quand il fut fix heures, il fe fit des tenebres fur tout le païs jufques à neuf heures.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 34, 'Et à neuf heures Jefus cria à haute voix, difant, Eloi, Eloi, lamma fabachthani? qui vaut autant à dire que Mon Dieu, Mon Dieu, pourquoi m\'as-tu abandonné?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 35, 'Et quelques-uns de ceux qui eftoyent là prefens, ayans ouï cela, difoyent, Voici, il appelle Elie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 36, 'Et quelqu\'un accourut, &amp; emplit une efponge de vinaigre: &amp; la mit à l\'entour d\'un rofeau, &amp; lui en bailla à boire, difant, Laiffez, voyons fi Elie viendra pour l\'ofter.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 37, 'Et Jefus apres avoir jetté un grand cri, rendit l\'efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 38, 'Et le voile du temple fe fendit en deux, depuis le haut jufques au bas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 39, 'Et le Centenier qui eftoit là vis à vis de lui, voyant qu\'il avoit rendu l\'efprit, en criant ainfi, dit, Veritablement cet homme eftoit le Fils de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 40, 'Il y avoit auffi des femmes qui regardoyent de loin: entre lefquelles eftoyent Marie Magdeleine, &amp; Marie mere de Jaques le petit, &amp; de Jofes, &amp; Salomé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 41, 'Lefquelles dés lors mefmes qu\'il eftoit en Galilée, l\'avoyent fuivi, &amp; lui avoyent fubvenu: &amp; plufieurs autres qui eftoyent montées enfemble avec lui à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 42, 'Et le foir eftant déja venu, (dautant que c\'eftoit le jour de la preparation, qui eft devant le Sabbat.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 43, 'Jofeph d\'Arimathée, confeiller honorable (lequel auffi attendoit le regne de Dieu) s\'eftant enhardi, s\'en vint vers Pilate, &amp; lui demanda le corps de Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 44, 'Et Pilate s\'eftonna de ce qu\'il eftoit déja mort: puis ayant appellé le Centenier, il l\'interrogea s\'il y avoit long-temps qu\'il eftoit mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 45, 'Ce qu\'ayant connu du Centenier, il donna le corps à Jofeph.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 46, 'Et icelui ayant acheté un linceul, le defcendit de la croix, &amp; l\'enveloppa du linceul, &amp; le mit dans un fepulcre qui eftoit taillé en un roc: puis il roula une pierre à la porte du fepulcre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 47, 'Et Marie Magdeleine, &amp; Marie mere de Jofes, regardoyent où on le mettoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 1, 'Or le Sabbat eftant paffé, Marie Magdeleine, &amp; Marie mere de Jaques, &amp; Salomé, acheterent des onguents aromatiques pour le venir embaumer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 2, 'Et de fort grand matin, le premier jour de la femaine, elles arriverent au fepulcre, le Soleil eftant levé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 3, 'Et difoyent entr\'elles, Qui nous roulera la pierre de la porte du fepulcre?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 4, 'Et ayant regardé, elles virent que la pierre eftoit roulée, car elle eftoit fort grande.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 5, 'Puis eftant entrées dans le fepulcre, elles virent un jeune homme affis à main droite, veftu d\'une robbe blanche: dont elles s\'épouvanterent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 6, 'Mais il leur dit, Ne vous efpouvantez point: vous cherchez Jefus le Nazarien qui a efté crucifié: il eft reffufcité, il n\'eft point ici: voici le lieu où on l\'avoit mis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 7, 'Mais allez, dites à fes difciples, &amp; à Pierre, qu\'il s\'en va devant vous en Galilée: vous le verrez là, comme il vous a dit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 8, 'Et incontinent en partant de là elles s\'enfuïrent arriere du fepulcre: car le tremblement &amp; la frayeur les avoit faifies &amp; n\'en dirent rien à perfonne: car elles avoyent peur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 9, 'Or Jefus eftant reffufcité, le matin au premier jour de la femaine, apparut premierement à Marie Magdeleine, de laquelle il avoit jetté hors fept diables.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 10, 'Et elle s\'en alla, &amp; l\'annonça à ceux qui avoyent efté avec lui: lefquels eftoyent en deüil &amp; pleuroyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 11, 'Eux ayans entendu qu\'il vivoit, &amp; qu\'elle l\'avoit veu, ne le creurent point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 12, 'Puis apres ces chofes, il fe montra en une autre forme à deux d\'entr\'eux, qui eftoyent en chemin pour aller aux champs:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 13, 'Et ceux-là eftans retournez l\'annoncerent aux autres, mais ils ne les creurent non plus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 14, 'Finalement il fe montra aux onze, eftans enfemble, affis, &amp; leur reprocha leur incredulité &amp; dureté de coeur, dautant qu\'ils n\'avoyent point creu ceux qui l\'avoyent veu reffufcité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 15, 'Et il leur dit, Allez-vous-en par tout le monde, &amp; prefchez l\'Evangile à toute creature.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 16, 'Qui aura creu &amp; aura efté baptizé fera fauvé: mais qui n\'aura point creu fera condamné.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 17, 'Et ce font ici les fignes qui accompagneront ceux qui auront creu: Ils jetteront hors les diables par mon Nom: ils parleront de nouveaux langages:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 18, 'Ils chafferont les ferpens. Et quand ils auront beu quelque chofe mortelle, elle ne leur nuira nullement: ils impoferont les mains fur les malades, &amp; ils fe porteront bien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 19, 'Or le Seigneur, apres avoir parlé à eux, fut élevé en haut au ciel: &amp; s\'affit à la dextre de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 20, 'Eux auffi eftans partis prefcherent par tout, le Seigneur operant avec eux, &amp; confirmant la parole par les fignes qui s\'en enfuivoyent. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 1, 'PARCE que plufieurs fe font appliquez à mettre par ordre un recit des chofes qui ont efté pleinement certifiées entre nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 2, 'Comme nous les ont baillé à connoiftre ceux qui les ont veuës eux-mefmes dés le commencement, et ont efté miniftres de la Parole.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 3, 'Il m\'a auffi femblé bon, apres avoir tout compris dés le commencement jufques à la fin, tres-excellent Theophile, de t\'en écrire par ordre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 4, 'Afin que tu connoiffes la certitude des chofes, defquelles tu as efté informé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 5, 'Au temps d\'Herode roi de Judée, il y avoit un certain Sacrificateur nommé Zacharie, du rang d\'Abia: et fa femme eftoit des filles d\'Aaron: et fon nom eftoit Elizabeth.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 6, 'Et ils eftoyent tous deux juftes devant Dieu, cheminans en tous les commandemens et en toutes les ordonnances du Seigneur, fans reproche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 7, 'Et ils n\'avoyent point d\'enfans, à caufe qu\'Elizabeth eftoit fterile: et tous deux eftoyent fort avancez en âge.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 8, 'Or il advint comme Zacharie exerçoit la facrificature devant le Seigneur à fon tour;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 9, 'Que felon la couftume de la facrificature le fort lui efcheut d\'offrir le parfum, entrant au temple du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 10, 'Et toute la multitude du peuple eftoit en priere dehors, à l\'heure qu\'on offroit le parfum.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 11, 'Alors l\'Ange du Seigneur s\'apparut à lui, fe tenant du cofté droit de l\'autel du parfum.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 12, 'Et Zacharie fut troublé quand il vid, et la crainte le faifit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 13, 'Mais l\'Ange lui dit, Zacharie de crain point: dautant que ta priere eft exaucée, et Elizabeth ta femme enfantera un fils, et tu appelleras fon nom Jean.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 14, 'Et tu en auras joye et lieffe et plufieurs s\'éjouïront de fa naiffance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 15, 'Car il fera grand devant le Seigneur, et ne boira ni vin ni cervoife: et fera rempli du Saint Efprit dés le ventre de fa mere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 16, 'Et il convertira plufieurs des enfans d\'Ifraël au Seigneur leur Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 17, 'Et ira devant lui en l\'efprit et en la vertu d\'Elie, afin qu\'il convertiffe les cœurs des peres envers les enfans, et les rebelles à la prudence des juftes, pour preparer au Seigneur un peuple bien ordonné.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 18, 'Alors Zacharie dit à l\'Ange. Comment connoitrai-je ceci: car je fuis ancien, et ma femme eft fort âgée?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 19, 'Et l\'Ange refpondant lui dit, Je fuis Gabriel qui affifte devant Dieu, et qui ai efté envoyé pour parler à toi et t\'annoncer ces bonnes nouvelles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 20, 'Dont voici, tu feras fans parler, et de fait tu ne pourras parler jufqu\'au jour que ces chofes aviendront: dautant que tu n\'as point creu à mes paroles, qui feront accomplies en leur faifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 21, 'Or le peuple attendoit Zacharie, et s\'eftonnoit de ce qu\'il tardoit tant au peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 22, 'Et quand il fut forti, il ne pouvoit parler à eux: alors ils connurent qu\'il avoit veu quelque vifion au temple: car il le leur donnoit à entendre par fignes: et il demeura muët.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 23, 'Et il advint que quand les jours de fon miniftere furent achevez, il retourna en fa maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 24, 'Et apres ces jours-là, Elizabeth fa femme conceut: et elle fe cacha par l\'efpace de cinq mois, en difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 25, 'Certes le Seigneur m\'a fait ainfi és jours efquels il m\'a regardée, pour ofter mon opprobre d\'entre les hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 26, 'Or au fixiéme mois, l\'Ange Gabriel fut envoyé de Dieu en une ville de Galilée, laquelle avoit nom Nazareth.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 27, 'Vers une Vierge fiancée à un homme, qui avoit nom Jofeph, de la maifon de David: et le nom de la Vierge eftoit Marie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 28, 'Et l\'Ange eftant entré au lieu où elle eftoit, lui dit, Bien te foit: qui es receuë en grace: Le Seigneur eft avec toi: tu es benite entre les femmes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 29, 'Et quand elle l\'eût veu, elle fut fort troublée à caufe des paroles d\'icelui: et penfoit en elle-mefme quelle eftoit cette falutation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 30, 'Alors l\'Ange lui dit, Marie, ne crain point: car tu as trouvé grace devant Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 31, 'Et voici, tu concevras en ton ventre, et tu enfanteras un fils, et appelleras fon nom Jesus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 32, 'Il fera grand et fera appellé le Fils du Souverain, et le Seigneur Dieu lui donnera le trône de David fon pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 33, 'Et il regnera fur la maifon de Jacob eternellement, et il n\'y aura nulle fin à fon regne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 34, 'Alors Marie dit à l\'Ange, Comment fe fera ceci, veu que je ne connois point d\'homme?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 35, 'Et l\'Ange refpondant, lui dit, Le Saint Efprit furviendra en toi, et la vertu du Souverain t\'enombrera: dont cela auffi qui naiftra de toi Saint, fera appellé le Fils de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 36, 'Et voila Elizabeth ta coufine, elle a auffi conceu un fils en fa vieilleffe: et c\'eft ici le fixiéme mois à celle qui eftoit appellée fterile.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 37, 'Car chofe quelconque ne fera impoffible par devers Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 38, 'Et Marie dit, voici la fervante du Seigneur: qu\'il me foit fait felon ta parole. Ainfi l\'ange partit d\'avec elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 39, 'Or en ces jour-là Marie fe leva, et s\'en alla en hafte au païs des montagnes, en une ville de Juda.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 40, 'Et entra en la maifon de Zacharie, et falüa Elizabeth.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 41, 'Et il advint que fi toft qu\'Elizabeth eût ouï la falutation de Marie, le petit enfant treffallit en fon ventre, et Elizabeth fut remplie du Saint Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 42, 'Et elle s\'écria à haute voix, et dit, Tu es benite entre les femmes, et benit eft le fruit de ton ventre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 43, 'Et d\'où me vient ceci que la mere de mon Seigneur vienne vers moi?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 44, 'Car voici, incontinent que la voix de ta falutation eft parvenuë à mes oreilles, le petit enfant a treffailli de joye en mon ventre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 45, 'Or bien-heureufe eft celle qui a creu: car les chofes qui lui ont efté dites par le Seigneur, auront leur accompliffement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 46, 'Alors Marie dit, Mon ame magnifie le Seigneur,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 47, 'Et mon efprit s\'eft éjouï en Dieu, qui eft mon Sauveur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 48, 'Car il a regardé la petiteffe de fa fervante: voici, certes d\'orefnavant tous âges me diront bien-heureufe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 49, 'Car le Puiffant m\'a fait de grandes chofes, et fon Nom eft Saint.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 50, 'Et fa mifericorde eft de generation en generation, à ceux qui le craignent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 51, 'Il a operé puiffamment par fon bras: il a diffipé les orgueilleux en la penfée de leur cœur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 52, 'Il a mis bas de leurs trônes les puiffans, et a élevé les petits.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 53, 'Il a rempli de biens ceux qui avoyent faim: il a renvoyé les riches vuides.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 54, 'Il a receu en fa protection Ifraël fon ferviteur, afin d\'avoir fouvenance de fa mifericorde,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 55, '(Selon qu\'il en a parlé à nos peres, affavoir à Abraham et à fa pofterité) à jamais.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 56, 'Et Marie demeura avec elle environ trois mois: puis elle s\'en retourna en fa maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 57, 'Or le terme d\'Elizabeth fut accompli pour enfanter: et elle enfanta un fils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 58, 'Et fes circonvoifins et fes parens, ayans entendu que le Seigneur avoit amplement declaré fa mifericorde envers elle, s\'en réjouïffoyent avec elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 59, 'Et il advint qu\'au huictiéme jour ils vinrent pour circoncir le petit enfant, et l\'appelloyent Zacharie du nom de fon pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 60, 'Mais fa mere prit la parole, et dit, Non, mais il fera nommé Jean.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 61, 'Et ils lui dirent, Il n\'y a aucun en ta parenté qui foit appellé de ce nom,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 62, 'Alors ils firent figne au pere d\'icelui, qu\'il declaraft comment il vouloit qu\'il fuft nommé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 63, 'Lequel ayant demandé des tablettes, écrivit, difant, Jean eft fon nom. Dont tous furent eftonnez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 64, 'Et à l\'inftant fa bouche fut ouverte, et fa langue déliée, tellement qu\'il parloit en loüant Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 65, 'Dont crainte furvint à tous leurs circonvoifins: et toutes ces paroles furent divulguées par tout le païs des montagnes de Judée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 66, 'Et tous ceux qui les entendirent les mirent en leur cœur, difans, Que fera-ce de ce petit enfant? Et la main du Seigneur eftoit avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 67, 'Alors Zacharie fon pere fut rempli du Saint Efprit, et prophetiza, difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 68, 'Benit foit le Seigneur, le Dieu d\'Ifraël, de ce qu\'il a vifité et a fait délivrance de fon peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 69, 'Et nous a élevé la corne de falut, en la maifon de David fon ferviteur:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 70, 'Ainfi qu\'il a parlé par la bouche de fes faints Prophetes, qui ont efté de tout temps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 71, 'Que nous ferions fauvez d\'entre les mains de nos ennemis, et de la main de tous ceux qui nous haïffent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 72, 'Pour faire mifericorde envers nos peres, et avoir memoire de fa fainte alliance:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 73, 'Qui eft le jurement qu\'il a juré à Abraham noftre pere:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 74, 'Affavoir qu\'il nous donneroit, qu\'apres eftre delivrez de la main de nos ennemis, nous le fervirions fans crainte,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 75, 'En fainteté et en juftice, devant lui, tous les jours de nôtre vie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 76, 'Et toi, petit enfant, tu feras appellé Prophete du Souverain: car tu iras devant la face du Seigneur, pour preparer fon chemin:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 77, 'Et pour donner connoiffance du falut à fon peuple, par la remiffion de leurs pechez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 78, 'Par les entrailles de la mifericorde de noftre Dieu, defquelles nous a vifité l\'Orient d\'en haut.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 79, 'Afin qu\'il reluife à ceux qui font affis en tenebres et en ombre de mort, pour addreffer nos pieds au chemin de paix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 80, 'Et le petit enfant croiffoit, et eftoit fortifié en Efprit, et il fut dans les deferts, jufques au jour qu\'il devoit eftre donné à connoiftre à Ifraël.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 1, 'OR il advint en ces jour-là qu\'un edit fut publié de la part de Cefar Augufte, que tout le monde fuft enrollé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 2, '(Cette premiere defcription fut faite, lors qu Cyrenius avoit le gouvernement de Syrie.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 3, 'Ainfi tous alloyent pour eftre enrollez, un chacun en fa ville:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 4, 'Jofeph auffi monta de Galilée en Judée, affavoir de la ville de Nazareth: en la cité de David, qui eft appellée Bethlehem, (à caufe qu\'il eftoit de la maifon et famille de David.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 5, 'Pour eftre enrollé avec Marie, la femme qui lui avoit efté fiancée, laquelle eftoit enceinte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 6, 'Et il advint comme ils eftoyent là, que le terme d\'icelle pour enfanter fut accompli.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 7, 'Et elle enfanta fon fils premier-né, et l\'emmaillotta, et le coucha fur une creche, à caufe qu\'il n\'y avoit point de place pour eux en l\'hoftellerie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 8, 'Or il avoit en la mefme contrée des bergers couchans aux champs: et gardans les veilles de la nuit fur leur troupeau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 9, 'Et voici, l\'Ange du Seigneur furvint vers eux, et la clarté du Seigneur refplendit autour d\'eux: dont ils furent faifis d\'une fort grand peur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 10, 'Alors l\'Ange leur dit, N\'ayez point de peur: car voici, je vous annonce une grand joye, laquelle fera à tout le peuple:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 11, 'C\'eft qu\'aujourd\'hui en la cité de David, le Sauveur vous eft né, qui eft Christ le Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 12, 'Et vous aurez ces enfeignes, c\'eft que vous trouverez le petit enfant emmaillotté, et gifant en une creche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 13, 'Et auffi-toft avec l\'Ange il y eut une multitude d\'armées celeftes, loüans Dieu, et difans,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 14, 'Gloire foit à Dieu és cieux tres-hauts, et en terre paix, envers les hommes bonne volonté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 15, 'Et il advint qu\'apres que les Anges s\'en furent allez d\'avec eux au ciel, les bergers dirent entr\'eux, Allons donc jufques à Bethlehem, et voyons cette chofe qui eft advenuë, que le Seigneur nous a notifiée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 16, 'Ils vinrent donc à grand\'hafte, et trouverent Marie et Jofeph, et le petit enfant gifant en la creche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 17, 'Et quand ils l\'eurent veu, ils divulguerent ce qui leur avoit efté dit touchant ce petit enfant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 18, 'Dont tous ceux qui les ouïrent, s\'eftonnerent des chofes qui leur eftoyent dites par les bergers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 19, 'Et Marie gardoit foigneufement toutes ces chofes, les ruminant en fon coeur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 20, 'Puis apres les bergers s\'en retournerent, glorifians et loüans Dieu de toutes les chofes qu\'ils avoyent ouïes et veuës, felon qu\'il leur en avoit efté parlé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 21, 'Et quand les huict jours furent accomplis pour circoncir l\'enfant, alors fon nom fut appellé Jesus, lequel avoit efté nommé par l\'Ange, devant qu\'il fuft conceu au ventre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 22, 'Et quand les jours de la purification d\'icelle furent accomplis felon la Loi de Moyfe, ils le porterent à Jerufalem, pour le prefenter au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 23, '(Comme il eft écrit en la Loi du Seigneur, Que tout mafle ouvrant la matrice fera appellé faint au Seigneur.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 24, 'Et pour offrir l\'oblation, felon qu\'il eft dit en la Loi du Seigneur, une paire de tourterelles, ou deux pigeonneaux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 25, 'Or voici, il y avoit un homme à Jerufalem qui avoit nom Simeon: cet homme-là eftoit jufte et craignant Dieu, lequel attendoit la confolation d\'Ifraël: et le faint Efprit eftoit fur lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 26, 'Et il avoit efté adverti divinement par le faint Efprit, qu\'il ne verroit point la mort que premierement il n\'euft veu le Christ du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 27, 'Icelui eftant meu par l\'Esprit vint au temple: et comme le pere et la mere portoyent dedans le petit enfant Jésus, pour faire de lui felon la couftume de la Loi:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 28, 'Il le prit entre fes bras, et benit Dieu, et dit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 29, 'Seigneur, tu laiffes maintenant aller ton ferviteur en paix, felon ta parole;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 30, 'Car mes yeux ont veu ton falut:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 31, 'Lequel tu as preparé devant la face de tous les peuples:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 32, 'La lumiere pour efclairer les nations, et pour eftre la gloire de ton peuple Ifraël.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 33, 'Et Jofeph et la mere d’icelui s’eftonnoyent des chofes qui eftoyent dites de lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 34, 'Et Simon les benit, et dit à Marie mere d’icelui, Voici, celui-ci eft mis pour le trébuchement et pour le relevement de plufieurs en Ifraël, et pour eftre un figne auquel on contredira;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 35, '(Et mefmes auffi une efpée percera ta propre ame) afin que les penfées de plufieurs cœurs foyent découvertes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 36, 'Il y avoit auffi Anne la Propheteffe, fille de Phanuel, de la lignée d’Afer: laquelle eftoit déja avancée en âge, et avoit vécu avec fon mari fept ans depuis fa virginité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 37, 'Et elle eftant veuve d’environ quatre vingts et quatre ans, ne bougeoit du temple, fervant à Dieu en jeufnes et en oraifons, nuit et jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 38, 'Et eftant furvenuë en ce mefme inftant, elle loüoit auffi de fa part le Seigneur, et parloit de lui à tous ceux qui attendoyent la delivrance à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 39, 'Et quand il eurent accompli tout ce qui eft felon la Loi du Seigneur, ils s’en retournerent en Galilée, à Nazareth leur ville.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 40, 'Or le petit enfant croiffoit et fe fortifioit en efprit, eftant rempli de fapience: et la grace de Dieu eftoit fur lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 41, 'Or fon pere et fa mere alloyent tous les ans à Jerufalem, à la fefte de Pafque.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 42, 'Lui donc eftant venu en l’âge de douze ans, comme ils furent montez à Jerufalem felon la couftume de la fefte:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 43, 'Quand il eurent accompli les jours de la fefte, eux s’en retournans, l’enfant Jésus demeura à Jerufalem: et Jofeph et la mere d’icelui ne s’en apperceurent point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 44, 'Mais eftimans qu’il eftoit en la compagnie, ils cheminerent une journée, et le cherchoyent entre leurs parens et ceux de leur connoiffance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 45, 'Et ne le trouvans point, ils s’en retournerent à Jerufalem, en le cherchant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 46, 'Et il advint que trois jours apres ils le trouverent au temple, affis au milieu des docteurs, les efcoutant, et les interrogeant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 47, 'Dont tous ceux qui l’oioyent, s’eftonnoyen de fa fapience et de fes refponfes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 48, 'Et quand ils le virent, ils s’eftonnerent, et fa mere lui dit, Mon enfant, pourquoi nous as-tu fait ainfi? Voici, ton pere et moi te cherchions eftans en grand’ peine.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 49, 'Alors il leur dit, Pourquoi eft-ce que vous me cherchiez, ne fçaviez-vous pas qu’il me faut eftre occupé aux affaires de mon Pere?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 50, 'Mais ils n’entendirent point ce qu’il leur difoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 51, 'Alors il defcendit avec eux, et vint à Nazareth, et leur eftoit fujet: et fa mere confervoit toutes ces paroles-là en fon cœur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 52, 'Et Jésus s’avançoit en fapience, et en ftature, et en grace envers Dieu et envers les hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 1, 'OR en la quiziéme année de l’empire de Tibere Cefar, lorsque Ponce Pilate eftoit gouverneur de Judée, et qu’Herode eftoit Tetrarque en Galilée, et fon frere Philippe pareillement Tetrarque en la contrée d’Iturée et de Thraconite, et Lyfanias Tetrarque en Abilene:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 2, 'Anne et Caïphe eftans fouverains Sacrificateurs, la parole de Dieu fut addreffée à Jean fils de Zacharie, au defert.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 3, 'Et il vint en tout le païs d’alentour le Jordain, prefchant le Baptefme de repentance en remiffion des pechez:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 4, 'Comme il eft écrit au livre des paroles d’Efaïe le Prophete, difant, La voix de celui qui crie au defert eft, Preparez le chemin du Seigneur, dreffez fes fentiers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 5, 'Toute valée fera comblée, et toute montagne et tout coftau fera abbaiffé, et les chofes tortuës feront redreffées, et les chemins rabotteux feront applanis:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 6, 'Et toute chair verra le falut de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 7, 'Il difoit donc aux troupes qui venoyent pour eftre baptizez par lui, Engeances de viperes, qui vous a advifez de fuïr l’ire à venir?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 8, 'Faites donc des fruits convenables à repentance, et ne vous prenez point à dire en vous-mefmes, Nous avons Abraham pour pere: car je vous dis, que mefmes de ces pierres ici, Dieu peut fufciter des enfans à Abraham.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 9, 'Or la coignée eft déja mife à la racine des arbres: tout arbre donc qui ne fait point de bons fruits, s’en va eftre coupé, et jetté au feu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 10, 'Alors les troupes l’interrogerent, difans, Que ferons-nous donc?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 11, 'Lui refpondant leur dit, Que celui qui a deux robbes, en donne une à celui qui n’en a point: et que celui qui a à manger, faffe le femblable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 12, 'Et il vint auffi des peagers pour eftre baptizez, et ils lui dirent, Maiftre, que ferons-nous?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 13, 'Et il leur dit, N’exigez rien outre ce qui vous eft ordonné.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 14, 'Les gens de guerre l’interrogerent auffi, difans, Et nous, que ferons-nous? Il leur dit, N’ufez point de concuffions, et ne circonvenez perfonne, mais contentez-vous de vos gages.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 15, 'Et comme le peuple attendoit, et que tous penfoyent à Jean en leurs cœurs, s’il ne feroit point le Christ:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 16, 'Jean prit la parole, et dit à tous: Il eft vrai que je vous baptize d’eau: mais il en vient un plus fort que moi, duquel je ne fuis pas digne de délier la courroye des fouliers: c’eft lui qui vous baptizera du faint Efprit et de feu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 17, 'Il a fon van en fa main, et il nettoyera entierement fon aire, et affemblera le froment en fon grenier: mais il bruflera la paille au feu qui ne s’efteint point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 18, 'Ainfi donc, admoneftant auffi de plufieurs autres chofes, il evangelizoit au peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 19, 'Mais Herode le Tetrarque eftant repris par lui à caufe d’Herodias femme de Philippe fon frere, et de tous les maux qu’il avoit faits,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 20, 'Adjoufta encore par deffus tous les autres celui-ci, c’eft qu’il mit Jean en prifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 21, 'Or il advint que comme tout le peuple eftoit baptizé, Jésus auffi eftant baptizé, et priant, le ciel s’ouvrit:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 22, 'Et le faint Efprit defcendit en forme corporelle fur lui, comme une colombe: et il y eut une voix du ciel, difant: Tu es mon Fils bien-aimé, j’ai pris mon bon-plaifir en toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 23, 'Et Jésus commençoit d’eftre environ de trente ans, fils (comme on l’eftimoit) de Jofeph, fils d’Heli:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 24, 'Fils de Matthat, fils de Levi, fils de Melchi, fils de Janna, fils de Jofeph:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 25, 'Fils de Matthatie, fils d’Amos, fils de Nahum, fils d’Heli, fils de Naggé:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 26, 'Fils de Maath, fils de Matthatie, fils de Semei, fils de Jofeph, fils de Juda:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 27, 'Fils de Johanna, fils de Rhefa, fils de Zorobabel, fils de Salathiel, fils de Neri.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 28, 'Fils de Melchi, fils d’Addi, fils de Cofam, fils d’Elmodam, fils de Er:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 29, 'Fils de Jofé, fils d’Eliezer, fils de Jorim, fils de Matthat, fils de Levi:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 30, 'Fils de Simon, fils de Juda, fils de Jofeph, fils de Jonan, fils d’Eliakim:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 31, 'Fils de Melca, fils de Maïnan, fils de Matthata, fils de Nathan, fils de David:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 32, 'Fils de Jeffé, fils d’Obed, fils de Booz, fils de Salmon, fils de Naaffon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 33, 'Fils d’Aminadab, fils d’Aram, fils d’Efrom, fils de Pharez, fils de Juda.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 34, 'Fils de Jacob, fils d’Ifaac, fils d’Abraham, fils de Thara, fils de Nachor:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 35, 'Fils de Sarug, fils de Ragau, fils de Phaleg, fils de Heber, fils de Sala:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 36, 'Fils de Caïnan, fils d’Arphaxad, fils de Sem, fils de Noé, fils de Lamech:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 37, 'Fils de Mathufala, fils d’Henoc, fils de Jared, fils Mahalaleel, fils de Caïnan:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 38, 'Fils d’Enos, fils de Seth, fils d’Adam, qui fut crée de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 1, 'OR Jésus eftant plein du faint Efprit s’en retourna de devers le Jordain: et il fut mené par la vertu de l’Efprit au defert:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 2, 'Et là il fut tenté du diable par quarante jours, et ne mangea rien du tout durant ces jours-là: mais apres qu’ils furent paffez, finalement il eut faim.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 3, 'Et le diable lui dit, Si tu es le Fils de Dieu, di à cette pierre-ici qu’elle devienne pain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 4, 'Et Jésus lui refpondit, difant, Il eft écrit, Que l’homme ne vivra point de pain feulement, mais de toute parole de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 5, 'Alors le diable l’emmena en une haute montagne, et lui montra en un moment de temps tous les royaumes du monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 6, 'Et le diable lui dit, Je te donnerai toute cette puiffance, et leur gloire: car elle m’eft baillée, et je la donne à qui je veux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 7, 'Partant fi tu te veux profterner devant moi, tout fera tien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 8, 'Mais Jésus refpondant lui dit, Va arriere de moi, Satan: car il eft écrit, Tu adoreras le Seigneur ton Dieu, et tu le ferviras lui feul.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 9, 'Il le mena auffi à Jerufalem, et le mit fur les creneaux du temple, et lui dit, Si tu es le Fils de Dieu, jette-toi d’ici en bas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 10, 'Car il eft écrit, Qu’il donnera charge de toi à fes Anges, pour te conferver:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 11, 'Et qu’ils te porteront en leurs mains, de peur que tu heurtes de ton pied à quelque pierre,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 12, 'Mais Jésus refpondant, lui dit, Tu ne tenteras point le Seigneur ton Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 13, 'Et quand toute la tentation fut finie, le diable fe retira d’avec lui pour un temps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 14, 'Et Jésus par la vertu de l’Efprit retourna en Galilée: et fa renommée courut par tout le païs d’alentour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 15, 'Car il enfeignoit en leurs Synagogues, et eftoit honoré de tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 16, 'Or il vint à Nazareth, où il avoit efté nourri, et entra en la Synagogue, au jour du Sabbat, felon fa couftume, et il fe leva pour lire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 17, 'Et on lui bailla le livre du Prophete Efaïe: et quand il eut déployé le livre, il trouva le paffage où il eft écrit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 18, 'L’Efprit du Seigneur eft fur moi, dautant qu’il m’a oint: il m’a envoyé pour evangelizer aux pauvres: pour guerir ceux qui ont le cœur froiffé:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 19, 'Pour publier délivrance aux captifs, et aux aveugles le recouvrement de la veuë, pour mettre en liberté ceux qui font brifez, et publier l’an agreable du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 20, 'Puis ayant ployé le livre, et l’ayant rendu au miniftre, il s’affit: et les yeux de tous ceux qui eftoyent en la Synagogue eftoyent fichez fur lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 21, 'Alors il commença à leur dire, Cette Efcriture eft accomplie aujourd’hui, vous l’oyans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 22, 'Et tous lui rendoyent tefmoignage, et s’eftonnoyent des paroles pleines de grace qui procedoyent de fa bouche: et difoyent, Celui-ci n’eft-il pas le fils de Jofeph?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 23, 'Dont il leur dit, affeurément vous me direz ce proverbe, Medecin, gueri toi toi-mefme: fais auffi ici en ton païs toutes les chofes que nous avons ouï dire que tu as faites à Capernaum.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 24, 'Mais il leur dit, En verité je vous dis, que nul Prophete n’eft receu en fon païs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 25, 'Or je vous dis pour vrai, qu’il y avoit plufieurs veuves au temps d’Elie, en Ifraël, lors que le ciel fut fermé en Ifraël, lors que le ciel fut fermé trois ans et fix mois: tellement qu’une grande famine advint par tout le païs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 26, 'Et toutefois Elie ne fut envoyé vers aucune d’entr’elles, finon vers une femme veuve, à Sarepta de Sion.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 27, 'Il y avoit auffi plufieurs lepreux en Ifraël, au temps d’Elifée le Prophete: toutefois pas un d’entr’eux ne fut nettoyé, finon Naaman le Syrien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 28, 'Et ils furent tous remplis de colere en la Synagogue, oyans ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 29, 'Dont ils fe leverent, et le jetterent hors de la ville, et le menerent jufques au bord de la montagne (fur laquelle leur ville eftoit baftie) pour le precipiter.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 30, 'Mais il paffa par le milieu d’eux, et s’en alla.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 31, 'Et defcendit à Capernaum, ville de Galilée, et là il les enfeignoit aux Sabbats.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 32, 'Et ils s’eftonnoyent de fa doctrine: car fa parole eftoit avec authorité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 33, 'Or il y avoit en la Synagogue un homme qui avoit un efprit de demon impur: lequel s’écria à haute voix,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 34, 'Difant, Ha, qu’y a-t’il entre nous et toi, Jésus Nazarien? Es-tu venu pour nous deftruire? Je fçais qui tu es, le Saint de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 35, 'Et Jésus le tança, difant, Tai-toi, et fors de lui. Et le diable, apres avoir jetté d’impetuofité l’homme au beau milieu, fortit de lui, fans lui avoir fait aucun dommage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 36, 'Alors un eftonnement les faifit tous, et ils parloyent entr’eux, difans, Quelle parole eft celle-ci, qu’il commande avec puiffance et vertu aux efprits immondes, et ils fortent?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 37, 'Et fa renommée s’épandit en tous les quartiers du païs d’alentour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 38, 'Et quand Jésus fe fut levé de la Synagogue, il entra en la maifon de Simon: et la belle-mere de Simon eftoit detenuë d’une groffe fiévre, et on le pria pour elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 39, 'Et s’eftant panché fur elle, il tança la fiévre, et la fiévre la laiffa: et incontinent elle fe leva, et les fervit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 40, 'Et comme le foleil fe couchoit, tous ceux qui avoyent des malades de diverfes maladies, les lui amenerent: et lui, impofant les mains fur un chacun, les gueriffoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 41, 'Les diables auffi fortoyent hors de plufieurs, crians, et difans, Tu es le Christ, le Fils de Dieu: mais il les tançoit, et ne leur permettoit pas de dire qu’ils fceuffent qu’il eftoit le Christ.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 42, 'Et quand il fut jour, il partit, et s’en alla en un lieu defert: et les troupes le cherchoyent, et vinrent jufques à lui, et le retenoyent, afin qu’il ne partift point d’avec elles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 43, 'Mais il leur dit, Il me faut bien evangelizer auffi le royaume de Dieu aux autres villes: car je fuis envoyé pour cela.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 44, 'Et il prefchoit és Synagogues de Galilée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 1, 'OR il advint comme la foule eftoit toute fur lui, pour ouïr la parole de Dieu, que lui fe tenoit fur le bord du lac de Genezaret.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 2, 'Et voyant deux naffelles qui eftoyent prés du rivage du lac, (or les pefcheurs en eftoyent defcendus, et lavoyent leurs rets) il monta en l’une des naffelles qui eftoit à Simon:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 3, 'Et il pria de le mener un peu arriere de terre: puis eftant affis, il enfeignoit de la naffelle les troupes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 4, 'Et quand il eut ceffé de parler, il dit à Simon, Mene en pleine eau, et lafchez vos rets pour pefcher.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 5, 'Alors Simon refpondant lui dit, Maiftre, nous avons travaillé toute la nuit, et n’avons rien pris: toutefois à ta parole je lafcherai les filez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 6, 'Ce qu’ayant fait, ils enfermerent une grande quantité de poiffons, tellement que leurs filez fe rompoyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 7, 'Dont ils firent figne à leurs compagnons qui eftoyent en l’autre naffelle, qu’ils vinrent pour les aider: lefquels vinrent, et ils remplirent les deux naffelles, tellement qu’elles s’enfonçoyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 8, 'Et quand Simon Pierre eut veu cela, il fe jetta aux genoux de Jésus, difant, Seigneur, retire-toi de moi: car je fuis un homme pecheur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 9, 'Car une frayeur l’avoit du tout faifi, et tous ceux qui eftoyent avec lui, à caufe de la prife des poiffons qu’ils avoyent faite: femblablement auffi Jaques et Jean fils de Zebedée, qui eftoyent compagnons de Simon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 10, 'Alors Jésus dit à Simon, N’aye point de peur: d’orefenavant tu feras preneur d’hommes vivans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 11, 'Et quand ils eurent amené les naffelles à terre, ils abandonnerent tout, et le fuivirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 12, 'Or il advint comme il eftoit en une ville, voici un homme plein de lepre, lequel voyant Jésus, fe jettant en terre fur fa face, le pria, difant, Seigneur, fi tu veux, tu me peux nettoyer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 13, 'Et il eftendit la main, et le toucha, difant, Je le veux, fois nettoyé. Et incontinent la lepre le quitta.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 14, 'Et il lui commanda qu’il ne le dift à perfonne: mais va, dit-il, et te montre au Sacrificateur, et offre pour ta purification, comme Moyfe a commandé, pour leur eftre en tefmoignage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 15, 'Et de plus en plus fa renommée s’épandoit: tellement que de grandes troupes s’affembloyent pour l’ouïr, et pour eftre gueris par lui de leurs maladies.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 16, 'Mais il fe tenoit retiré és deferts, et prioit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 17, 'Et il advint au jour qu’il enfeignoit, que des Pharifiens et des docteurs de la Loi eftoyent là affis, qui eftoyent venus de toutes les bourgades de Galilée, et de Judée, et de Jerufalem: et la puiffance du Seigneur eftoit là pour les guerir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 18, 'Alors voici des hommes qui portoyent en un lit un homme qui eftoit perclus: et ils cherchoyent de le porter au dedans, et de le mettre devant lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 19, 'Et ne trouvans point par quel cofté ils le pourroyent mettre dedans, à caufe de la foule, ils monterent fur la maifon, et le devalerent par les tuiles avec le petit lit, au milieu, devant Jésus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 20, 'Dont voyant leur foi, il lui dit, Homme, tes pechez te font pardonnez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 21, 'Alors les Scribes et les Pharifiens commencerent à penfer, difans, Qui eft celui-ci qui prononce des blafphemes? Qui eft-ce qui peut pardonner les pechez, finon un feul Dieu?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 22, 'Mais Jésus connoiffant leurs penfées, prit la parole, et leur dit, Que difcourez-vous en vos cœurs?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 23, 'Lequel eft le plus aifé, ou de dire, Tes pechez te font pardonnez: ou de dire, Leve-toi, et chemine?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 24, 'Or afin que vous fçachiez que le Fils de l’homme a pouvoir en la terre de pardonner les pechez, (il dit au paralytique) Je te dis, Leve-toi, charge ton petit lit, et t’en va en ta maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 25, 'Et à l’inftant icelui d’eftant levé devant eux, chargea font lit, fur lequel il gifoit, et s’en alla en fa maifon, glorifiant Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 26, 'Et un eftonnement les faifit tous, dont ils glorifioyent Dieu: et ils furent remplis de crainte, difans, Certes nous avons veu aujourd’hui des chofes qu’on n’euft jamais attenduës.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 27, 'Apres ces chofes il s’en alla, et vid un peager nommé Levi, affis au lieu du peage, et lui dit, Sui-moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 28, 'Lequel abandonnant tout, fe leva, et le fuivit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 29, 'Et Levi fit un grand banquet en fa maifon, où il y avoit une groffe affemblée de peagers, et d’autres gens qui eftoyent avec eux à table.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 30, 'Et ceux d’entr’eux qui eftoyent Scribes et Pharifiens, murmuroyent contre fes difciples, difans, Pourquoi eft-ce que vous mangez et que vous beuvez avec les peagers et les gens de mauvaife vie?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 31, 'Alors Jésus prenant la parole, leur dit, Ceux qui font en fanté, n’ont pas befoin de medecin, mais ceux qui fe portent mal.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 32, 'Je ne fuis point venu appeller à repentance les juftes, mais les pecheurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 33, 'Ils lui dirent auffi, Pourquoi eft-ce que les difciples de Jean jeufnent fouvent, et font des prieres, femblablement auffi ceux des Pharifiens: mais les tiens mangent et boivent?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 34, 'Et il leur dit, Pouvez-vous faire jeufner les gens de la chambre du nouveau marié, pendant que le nouveau marié eft avec eux?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 35, 'Mais les jours viendront que le nouveau marié leur fera ofté: alors ils jeufneront en ces jours-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 36, 'Puis il leur dit une fimilitude; Nul ne met une piece d’un veftement neuf à un vieux veftement: autrement ce qui eft neuf defchire, et la piece du neuf ne fe rapporte point au vieil.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 37, 'Pareillement nul ne met le vin nouveau en de vieux vaiffeaux: autrement le vin nouveau rompra les vaiffeaux, et fe refpandra, et les vaiffeaux feront perdus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 38, 'Mais le vin nouveau doit eftre mis en des vaiffeaux neufs: et ainfi l’un et l’autre fe confervent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 39, 'Et il n’y a perfonne qui boive du vieil qui vueille auffi-toft du nouveau: car il dit, Le vieil vaut mieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 1, 'OR il advint au jour du Sabbat fecond-premier, qu’il paffoit par des bleds: et fes difciples arrachoyent des efpics, et les froyans entre leurs mains en mangeoyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 2, 'Et quelques-uns des Pharifiens leur dirent, Pourquoi faites-vous ce qu’il n’eft pas permis de faire és Sabbats?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 3, 'Alors Jésus prenant la parole leur dit, N’avez-vous pas au moins leu ce que fit David quand il eut faim, lui et ceux qui eftoyent avec lui?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 4, 'Comme il entra en la maifon de Dieu, et prit les pains de propofition, et en mangea, et en donna auffi à ceux qui eftoyent avec lui, lefquels il n’eft pas permis de manger finon aux Sacrificateurs feulement?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 5, 'Puis leur dit, Le Fils de l’homme eft Seigneur auffi du Sabbat.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 6, 'Il advint auffi en un autre Sabbat, qu’il entra en la Synagogue, et enfeignoit: et il y avoit là un homme duquel la main droite eftoit feche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 7, 'Or les Scribes et les Pharifiens efpioyent s’il le gueriroit au Sabbat, afin qu’ils trouvaffent dequoi l’accufer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 8, 'Mais il connoiffoit leurs penfées: dont il dit à l’homme qui avoit la main feche, Leve-toi, et te tien debout au milieu. Et lui fe levant, fe tint debout.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 9, 'Jésus donc leur dit, Je vous demanderai une chofe, Eft-il permis és Sabbats de bien faire, ou de mal faire? de fauver une perfonne, ou de la tuër?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 10, 'Et quand il les eut tous regardez à l’environ, il dit au perfonnage: Eften ta main. Ce qu’il fit: et fa main lui fut renduë faine comme l’autre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 11, 'Dont ils furent remplis de forcenerie, et devifoyent enfemble que c’eft qu’ils pourroyent faire à Jésus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 12, 'Or il advint en ces jours-là, qu’il s’en alla en une montagne pour prier: et fut toute la nuit en priere à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 13, 'Et quand le jour fut venu, il appella fes difciples: et en éleut douze, lefquels il nomma auffi Apoftres:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 14, 'Affavoir Simon, qu’il nomma auffi Pierre, et André fon frere, Jaques et Jean: Philippe et Barthelemi:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 15, 'Matthieu et Thomas: Jaques, fils d’Alphée: et Simon appellé Zelotes:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 16, 'Jude frere de Jaques, et Judas Ifcariot, qui auffi fut traiftre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 17, 'Puis defcendant avec eux, il s’arrefta en une plaine avec la troupe de fes difciples, et une grande multitude de peuple de toute la Judée maritime de Tyr et de Sidon, lefquels eftoyent venus pour l’ouïr, et pour eftre gueris de leurs maladies:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 18, 'Et qui eftoyent tourmentez des efprits immondes: et ils furent gueris.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 19, 'Et toute la multitude tafchoit à le toucher: car vertu fortoit de lui: et il les gueriffoit tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 20, 'Alors élevant fes yeux vers fes difciples, il leur difoit, Vous eftes bien-heureux, vous pauvres: car le royaume de Dieu eft voftre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 21, 'Vous eftes bien-heureux, vous qui maintenant avez faim: car vous ferez raffafiez. Vous eftes bien-heureux, vous qui pleurez maintenant: car vous rirez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 22, 'Vous ferez bien-heureux quand les hommes vous haïront, et vous retrancheront, et vous diront des outrages, et rejetteront voftre nom comme mauvais, à caufe du Fils de l’homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 23, 'Ejouïffez-vous en ce jour-là, et fautez de joye: car voici, voftre falaire eft grand au ciel: car leurs peres en faifoyent de mémes aux Prophetes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 24, 'Mais mal-heur fur vous, riches: car vous remportez voftre confolation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 25, 'Mal-heur fur vous qui eftes remplis: car vous aurez faim. Mal-heur fur vous qui riez maintenant: car vous lamenterez et pleurerez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 26, 'Mal-heur fur vous quand tous les hommes diront du bien de vous: car leurs peres en faifoyent de mefmes aux faux Prophetes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 27, 'Mais à vous qui oyez, je vous dis, Aimez vos ennemis: faites du bien à ceux qui vous haïffent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 28, 'Beniffez ceux qui vous maudiffent, et priez pour ceux qui vous courent fus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 29, 'Et à celui qui te frape en une jouë, prefente lui auffi l’autre: et fi quelqu’un t’ofte ton manteau, ne l’empefche point de prendre auffi le faye.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 30, 'Et à tout homme qui te demande baille lui: et à celui qui t’ofte le tien, ne le redemande point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 31, 'Et comme vous voulez que les hommes vous faffent, faites-leur auffi femblablement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 32, 'Car fi vous aimez ceux qui vous aiment, quel gré vous en fçaura-t’on? veu que les mal-vivans aiment auffi ceux qui les aiment.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 33, 'Et fi vous faites du bien à ceux qui vous font du bien, quel gré vous en fçaura-t’on? car les mal-vivans font auffi le mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 34, 'Et fi vous preftez à ceux de qui vous efperez le recevoir, quel gré vous en fçaura-t’on? car les mal-vivans preftent auffi aux mal-vivans, afin qu’ils en reçoivent la pareille.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 35, 'Partant aimez vos ennemis, et faites du bien, et preftez fans en rien efperer, et voftre falaire fera grand, et vous ferez les fils du Souverain: car il eft benin envers les ingrats et les mauvais.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 36, 'Soyez donc mifericordieux, comme auffi voftre Pere eft mifericordieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 37, 'Davantage ne jugez point, et vous ne ferez point jugez, ne condamnez point, et vous ne ferez point condamnez: quittez, et il vous fera quitté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 38, 'Donnez, et il vous fera donné. On vous donnera au fein bonne mefure, preffée et entaffée, et qui s’en ira par deffus: car de la mefure que vous mefurerez, on vous mefurera reciproquement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 39, 'Pareillement il leur difoit une fimilitude, Eft-il poffible qu’un aveugle puiffe mener un autre aveugle? ne cherront-ils point tous deux en une foffe?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 40, 'Le difciple n’eft point par deffus fon maiftre: mais tout difciple qui fera bien accompli, fera rendu conforme à fon maiftre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 41, 'Davantage, pourquoi regardes-tu le feftu qui eft en l’œil de ton frere, et tu n’apperçois point un chevron en ton propre œil?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 42, 'Ou comment peux-tu dire à ton frere, Mon frere, permets que j’ofte le feftu qui eft en ton œil: toi qui ne vois point un chevron qui eft en ton œil? Hypocrite, jette premierement le chevron hors de ton œil, et alors tu adviferas à tirer le feftu qui eft en l’œil de ton frere?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 43, 'Certes l’arbre n’eft point bon, qui fait de mauvais fruit: ni l’arbre n’eft point mauvais, qui fait de bon fruit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 44, 'Car chaque arbre eft connu par fon propre fruit. Car auffi les figues ne fe cueillent point des efpines, et du buiffon on ne vendange point les raifins.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 45, 'L’homme de bien tire de bonnes chofes du bon trefor de fon cœur: et le mauvais homme tire de mauvaifes chofes du mauvais trefor de fon cœur: car c’eft de l’abondance du cœur que la bouche parle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 46, 'Mais pourquoi m’appellez-vous Seigneur, Seigneur, et ne faites point ce que je dis?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 47, 'Quiconque vient à moi, et oit mes paroles, et les met en effet, je vous montrerai à qui il eft femblable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 48, 'Il eft femblable à l’homme qui en baftiffant une maifon, aura fouï et creufé, et aura mis le fondement fur le roc: et quand il eft venu une ravine d’eaux, le fleuve a choqué cette maifon-là: mais il ne l’a peu esbranler: car elle eftoit fondée fur le roc.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 49, 'Mais celui qui les a ouïes, et ne les a point mifes en effet, eft femblable à l’homme qui aura bafti fa maifon à terre, fans fondement: laquelle le fleuve a choquée, et incontinent elle eft tombée, et la ruïne de cette maifon-là a efté grande.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 1, 'OR quand il eut achevé toutes ces paroles, le peuple l’efcoutant, il entra en Capernaum.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 2, 'Et le ferviteur d’un certain Centenier, qui lui eftoit fort cher, eftant malade s’en alloit mourir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 3, 'Et quand le Centenier ouït parler de Jésus, il envoya cers lui des anciens des Juifs, le priant qu’il vinft pour guerir fon ferviteur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 4, 'Lefquels eftant venus vers Jésus, le prierent affectueufement, difans, qu’il eftoit digne qu’on lui ectroyaft cela.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 5, 'Car, difoyent-ils, il aime noftre nation, et nous a bafti la Synagogue.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 6, 'Jésus donc s’en alloit avec eux. Et comme déja il n’eftoit plus gueres loin de la maifon, le Centenier envoya vers lui fes amis, lui difant, Seigneur, ne te travaille point: car je ne fuis pas digne que tu entres fous mon toict:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 7, 'C’eft pourquoi auffi je ne me fuis point reputé digne d’aller vers toi: mais di la parole, et mon ferviteur fera gueri.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 8, 'Car je fuis auffi homme conftitué fous la puiffance d’autrui, ayant fous moi des gens de guerre, et je dis à l’un, Va, et il va: et à l’autre, Vien, et il vient: et à mon ferviteur, Fai cela, et il le fait.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 9, 'Lefquelles chofes ouïes, Jésus l’admira, et fe retournant, dit à la troupe qui le fuivoit, Je vous dis, que je n’ai point trouvé de fi grande foi, mefmes en Ifraël.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 10, 'Et quand ceux qui avoyent efté envoyez furent retournez en la maifon, ils trouverent le ferviteur qui avoit efté malade, fe portant bien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 11, 'Or il advint le jour fuivant, que Jésus alloit en une ville nommée Naïn: et plufieurs de fes difciples, et une groffe troupe, alloyent avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 12, 'Et comme il approchoit de la porte de la ville, voici, on portoit dehors un mort, fils unique de fa mere, laquelle eftoit veuve: a et une grande compagnie de la ville eftoit avec elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 13, 'Et quand le Seigneur l’eut veuë, il fut émeu de compaffion envers elle: et lui dit, Ne pleure point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 14, 'Et s’eftant approché, il toucha la biere, (or ceux qui portoyent le corps s’arrefterent) et il dit, Jeune homme, je te dis, leve-toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 15, 'Et celui qui eftoit mort fe raffit et commença à parler, et il le rendit à fa mere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 16, 'Et la crainte les faifit tous, et ils glorifioyent Dieu, difans, Certes un grand Prophete s’eft levé entre nous, et certes Dieu a vifité fon peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 17, 'Et ce bruit courut de lui par toute la Judée, et par toute la contrée d’alentour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 18, 'Or toutes ces chofes furent rapportées à Jean par fes difciples.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 19, 'Dont Jean appella deux de fes difciples, et les envoya vers Jésus, difant, Es-tu celui qui devoit venir, ou fi nous en devons attendre un autre?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 20, 'Or eux eftans venus vers lui, lui dirent, Jean Baptifte nous a envoyez vers toi, difant, Es-tu celui qui devoit venir, ou fi nous devons attendre un autre?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 21, 'Or en cette mefme heure-là, il en guerit plufieurs de maladies et de fleaux, et de malins efprits: et donna la veuë à plufieurs aveugles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 22, 'Puis refpondant: il leur dit, Allez, et rapportez à Jean ce que vous avez veu et ouï, affavoir que les aveugles recouvrent la veuë, les boiteux cheminent, les lepreux font nettoyez, les fourds oyent, les morts reffufcitent, l’Evangile eft prefché aux pauvres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 23, 'Et bien-heureux eft quiconque n’aura point efté fcandalizé en moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 24, 'Puis quand les meffagers de Jean furent partis, il fe prit à dire de Jean aux troupes, Qu’eftes-vous allez voir au defert? un rofeau agité du vent?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 25, 'Mais qu’eftes-vous allez voir? un homme reveftu de precieux veftemens? voici, ceux qui font magnifiquement reveftus, et qui vivent en delices, font és maifons des rois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 26, 'Mais qu’eftes-vous allez voir? un Prophete? voire, je vous dis, et plus que Prophete.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 27, 'C’eft celui duquel il eft écrit, Voici, j’envoye mon meffager devant ta face, lequel preparera ta voye devant toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 28, 'Car je vous dis, qu’entre ceux qui font nez de femme, il n’y nul Prophete plus grand que Jean Baptifte: et toutefois celui qui eft le moindre au royaume de Dieu, eft plus grand que lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 29, 'Et tout le peuple qui oyoit cela, et les peagers qui avoyent efté baptizez du baptefme de Jean, juftifierent Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 30, 'Mais les Pharifiens, et les docteurs de la Loi, qui n’avoyent point efté baptizez par lui, rejetterent le confeil de Dieu contre eux-mefmes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 31, 'Alors le Seigneur dit, A qui donc accomparerai-je les hommes de cette generation? Et à quoi reffemblent-ils?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 32, 'Ils font femblables aux enfans qui font affis au marché, et crient les uns aux autres, et difent, Nous vous avons flufté, et vous n’avez point danfé: nous vous avons chanté complaintes, et vous n’avez point pleuré.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 33, 'Car Jean Baptifte eft venu, ne mangeant point de pain, et ne beuvant point de vin: et vous dites, Il a le diable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 34, 'Le Fils de l’homme eft venu, mangeant et beuvant: et vous dites, Voici un mangeur et un beuveur, un ami de peagers et des gens de mauvaife vie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 35, 'Mais la Sapience a efté juftifiée par tous fes enfans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 36, 'Et l’un des Pharifiens le pria de mangez chez lui: dont il entra en la maifon du Pharifien, et fe mit à table.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 37, 'Or voici, il y avoit une femme de la ville qui avoit efté de mauvaife vie, laquelle ayant connu qu’il eftoit à table en la maifon du Pharifien, apporta une boîte d’oignement:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 38, 'Et fe tenant derriere aux pieds d’icelui, et pleurant, elle fe prit à les arroufer de fes larmes: et lui baifoit les pieds, et les oignoit de l’oignement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 39, 'Alors le Pharifien qui l’avoit convié, l’ayant apperceuë, dit en foi-mefme, Si celui-ci eftoit Prophete, certes il fçauroit qui et quelle eft cette femme qui le touche: car elle eft de mauvaife vie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 40, 'Et Jésus prenant la parole lui dit, Simon, j’ai quelque chofe à te dire: Et il dit, Maiftre, di la.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 41, 'Un creancier avoit deux debteurs: l’un lui devoit cinq cens deniers, et l’autre cinquante.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 42, 'Et comme ils n’avoyent as dequoi payer, il quitta la debte à l’un et à l’autre. Or di donc, lequel d’eux l’aimera le plus?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 43, 'Simon refpondant lui dit, J’eftime que c’eft celui à qui il a quitté le plus: et il lui dit, Tu as droitement jugé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 44, 'Alors fe tournant vers la femme, il dit à Simon, Vois-tu cette femme? je fuis entré en ta maifon, et tu ne m’as point donné d’eau pour laver mes pieds: mais celle-ci a arroufé mes pieds de larmes, et les a effuyez de fes propres cheveux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 45, 'Tu ne m’as point donné un baifer: mais elle, depuis que je fuis entré, n’a ceffé de baifer mes pieds.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 46, 'Tu n’as point oint ma tefte d’huile: mais elle a oint mes pieds d’oignement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 47, 'A raifon dequoi je te dis que fes pechez, qui font grands, lui font pardonnez: car elle a beaucoup aimé, et celui auquel il eft moins pardonné, celui-là aime moins.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 48, 'Puis il dit à la femme, Tes pechez te font pardonnez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 49, 'Et ceux qui eftoyent enfemble à table fe prirent à dire entr’eux, Qui eft celui-ci qui mefmes pardonne les pechez?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 50, 'Mais il dit à la femme, Ta foi t’a fauvée, va-t’en en paix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 1, 'OR il advint puis apres, qu’il alloit de ville en ville, et de bourgade en bourgade, prefchant et annonçant le royaume de Dieu: et les douze eftoyent avec lui;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 2, 'Et auffi quelques femmes qui avoyent efté gueries par lui de malins efprits, et de maladies: affavoir Marie qu’on appelloit Magdeleine, de laquelle eftoyent fortis fept diables:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 3, 'Et Jeanne femme de Chuzas, lequel avoit le maniement des affaires d’Herode: et Sufanne, et plufieurs autres: lefquelles lui fubvenoyent de leurs biens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 4, 'Et comme une grande troupe s’affembloit, et que plufieurs alloyent à lui de toutes les villes, il dit par fimilitude;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 5, 'Un femeur fortit pour femer fa femence: et en femant, une partie de la femence cheut auprés du chemin, et fut foulée aux pieds, et les oifeaux du ciel la mangerent toute.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 6, 'Et l’autre partie cheut fur des pierres: et quand elle fut levée, elle fe fecha, à caufe qu’elle n’avoit point d’humeur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 7, 'Et l’autre partie cheut entre les efpines: et les efpines fe leverent enfemble, et l’étoufferent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 8, 'Et l’autre partie cheut en une bonne terre: et quand elle fut levée, elle rendit du fruit cent fois autant. En difant ces chofes, il crioit, Qui a des oreilles pour ouïr, qu’il oye.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 9, 'Et fes difciples l’interrogerent, demandans quelle eftoit cette fimilitude-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 10, 'Et il refpondit, Il vous eft donné de connoiftre les fecrets du royaume de Dieu: mais aux autres il n’en eft parlé que par fimilitude: afin qu’en voyant ils ne voyent point, et qu’en oyant ils n’entendent point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 11, 'Or voici la fimilitude: La femence, c’eft la parole de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 12, 'Et ceux qui font aupres du chemin, ce font ceux qui oyent la parole: et apres le diable vient, et ofte de leur cœur la parole, afin qu’en croyant ils ne foyent point fauvez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 13, 'Et ceux qui font en des lieux pierreux, ce font ceux qui apres avoir ouï la parole, la reçoivent avec joye: mais ils n’ont point de racine: ils croyent pour un temps, mais au temps de tentation, ils fe retirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 14, 'Et ce qui eft cheu entre les efpines, ce font ceux qui ont ouï la parole: mais eux eftant partis, font eftouffez par les follicitudes et richeffes et voluptez de cette vie, et ne rapportent point de fruit à maturité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 15, 'mais ce qui eft cheu en une bonne terre, ce font ceux qui ayant ouï la parole, la retiennent en un cœur honnefte et bon, et en rapportent du fruit avec patience.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 16, 'Nul apres avoir allumé la chandelle, ne la couvre d’un vaiffeau, ni ne la met fous un lit: mais la met fur un chandelier, afin que ceux qui entrent voyent la lumiere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 17, 'Car il n’y a point de fecret qui ne foit manifefté: ni de chofe cachée qui ne fe connoiffe, et qui ne vienne en lumiere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 18, 'Regardez donc comment vous oyez: car quiconque a il lui fera donné: et quiconque n’a rien, mefmes ce qu’il penfe avoir, lui fera ofté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 19, 'Alors fa mere et fes freres furvinrent vers lui, et ne pouvoyent l’aborder pour la preffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 20, 'Et il lui fut rapporté, en difant, Ta mere et tes freres font là dehors, defirans de te voir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 21, 'Mais lui refpndant, leur dit, Ma mere et mes freres font ceux qui oyent la parole de Dieu, et la mettent en effet.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 22, 'Or il advint un jour qu’il monta en une naffelle, avec fes difciples, et leur dit, Paffons outre le lac. Et ils partirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 23, 'Or comme ils voguoyent, il s’endormit, et une tempefte de vent defcendit au lac: tellement qu’ils s’empliffoyent d’eau, et eftoyent en peril.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 24, 'Alors ils vinrent vers lui et l’éveillerent, difans, Maiftre, Maiftre, nous periffons. Mais lui s’eftant levé, tança le vent, et la tempefte de l’eau: lefquels cefferent, et il fe fit un calme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 25, 'Alors il leur dit, Où eft voftre foi? Et craignans il s’eftonnerent, difans entr’eux, Mais qui eft celui-ci, qu’il commande mefme aux vents, et à l’eau, et ils lui obeïffent?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 26, 'Puis ils tirerent à la contrée des Gadareniens, qui eft vis à vis de la Galilée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 27, 'Et quand il fut forti de la naffelle fur terre, un certain homme de cette ville-là lui vint à la rencontre, qui avoit les diables dés long-temps: et n’eftoit point veftu de veftemens, et ne demeuroit point en maifon, mais dans les fepulcres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 28, 'Lui donc appercevant Jésus, et s’écriant, fe jetta devant lui, et dit à haute voix, Qu’y a-t’il entre moi et toi, Jésus Fils du Dieu Souverain: je te prie ne me tourmente point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 29, 'Car il commandoit à l’efprit immonde qu’il fortift hors de l’homme: car il l’avoit tenu enferré dés long-tems, et icelui eftoit bien lié de chaines et gardé aux ceps: mais defrompant les liens, il eftoit emporté par le diable aux deferts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 30, 'Alors Jésus l’interrogea, difant, Comment as-tu nom? et il dit Legion: car plufieurs diables eftoyent entrez en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 31, 'Et ils le prioyent qu’il ne leur commandaft point d’aller en l’abyfme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 32, 'Or il y avoit là un grand troupeau de pourceaux, paiffans en la montagne: dont ils le prioyent qu’il leur permift d’entrer en eux, et il le leur permit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 33, 'Les diables donc fortans de l’homme entrerent dans les pourceaux: et le troupeau fe jetta du haut en bas au lac, et fut eftouffé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 34, 'Et quand les porchers eurent veu ce qui eftoit advenu, ils s’enfuïrent, et eftans partis, le raconterent en la ville et par les champs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 35, 'Alors les gens fortirent pour voir ce qui eftoit advenu, et vinrent vers Jésus, et trouverent l’homme duquel les diables eftoyent fortis, affis aux pieds de Jésus, eftant veftu, et de fens raffis et pofé: donc ils eurent peur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 36, 'Et ceux auffi qui avoyent veu le tout, leur raconterent comment le demoniaque avoit efté delivré.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 37, 'Alors toute la multitude de la contrée circonvoifine des Gadareniens le prierent qu’il fe retiraft de devers eux: car ils eftoyent faifis de grande crainte. Lui donc eftant monté en la naffelle, s’en retourna.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 38, 'Et l’homme duquel les diables eftoyent fortis, le prioit qu’il fuft avec lui: mais Jésus le renvoya, difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 39, 'Retourne-t’en en ta maifon, et raconte combien grandes chofes Dieu t’a faites. Il s’en alla donc, publiant par toute la ville toutes les chofes que Jésus avoit faites.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 40, 'Et il advint que quand Jésus fut retourné, la multitude le receut: car tous l’attendoyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 41, 'Alors, voici, un homme vint, qui avoit nom Jaïrus, lequel eftoit le principal de la Synagogue: et fe jettant aux pieds de Jésus il le pria qu’il vinft en fa maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 42, 'Car il avoit une fille unique d’environ douze, qui fe mouroit. Or comme il s’en alloit les troupes l’empreffoyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 43, 'Alors une femme qui avoit une perte de fang depuis douze ans, (laquelle avoit defpenfé tout fon bien en medecins, et cependant n’avoit peu eftre guerie par aucun.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 44, 'S’approchant de lui par derriere, toucha le bord de fon veftement: et à l’inftant fon flux de fang s’eftancha.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 45, 'Alors Jésus dit, Qui eft-ce qui m’a touché? Et comme tous le nioyent, Pierre dit, et ceux qui eftoyent avec lui, Maiftre, les troupes te preffent et te foulent, et tu dis, Qui eft-ce qui m’a touché?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 46, 'Mais Jésus dit, Quelqu’un m’a touché: car j’ai connu qu’il eft forti de la vertu de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 47, 'Cette femme donc voyant que cela ne lui avoit point efté caché, toute tremblante vint: et fe jettant devant lui, lui declara devant tout le peuple, pour quelle caufe elle l’avoit touché, et comme elle avoit efté guerie à l’inftant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 48, 'Et il lui dit, Fille, affeure-toi, ta foi t’a guerie: va en paix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 49, 'Comme il parloit encore, quelqu’un de chez le principal de la Synagogue vint, lui difant, Ta fille eft morte, ne travaille point le Maiftre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 50, 'Mais Jésus l’ayant entendu, refpondit au pere de la fille, difant, Ne crain point: croi feulement, et elle fera guerie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 51, 'Et quand il fut entré en la maifon, il ne laiffa entrer perfonne, finon Pierre, et Jacques, et Jean, et le pere et la mere de la fille.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 52, 'Or tous pleuroyent, et la plaignoyent: mais il dit, Ne pleurez point: elle n’eft pas morte, mais elle dort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 53, 'Et ils fe rioyent de lui, fçachant qu’elle eftoit morte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 54, 'Mais lui les ayant tous mis dehors, et ayant pris la main d’icelle, cria, difant, Fille, leve-toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 55, 'Et l’efprit d’icelle retourna, et elle fe leva à l’inftant, et il commanda qu’on lui baillaft à manger.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 56, 'Et le pere et la mere d’icelle furent eftonnez: mais il leur commanda qu’ils ne diffent à perfonne ce qui avoit efté fait.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 1, 'PUIS apres Iefus, ayant appellé fes douze difciples tous enfemble, leur donna puiffance et authorité fur toutes fortes de diables, et de guerir les malades.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 2, 'Il les envoya donc publier le royaume de Dieu, et guerir les malades.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 3, 'Et il leur dit, Ne portez rien pour le chemin, ni baftons, ni malette, ni pain, ni argent, et n’ayez point un chacun deux habillemens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 4, 'Et en quelque maifon que vous entriez, demeurez-y, et vous en allez de là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 5, 'Et par tout où l’on ne vous recevra point, en partant de cette ville-là fecoüez de vos pieds la poudre, en tefmoignage contr’eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 6, 'Eux donc eftans partis alloyent de bourgade en bourgade, evangelizans, et gueriffans partout.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 7, 'Or Herode le Tetrarque ouït parler de toutes les chofes que Jésus faifoit: et eftoit en perplexité, à caufe que quelques-uns difoyent que Jean eftoit reffufcité des morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 8, 'Et quelques-uns, qu’Elie eftoit apparu: et les autres, que quelqu’un des Anciens Prophetes eftoit reffufcité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 9, 'Alors Herode dit, J’ai decapité Jean: qui eft donc celui-ci de qui j’entens telles chofes? Et il cherchoit de le voir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 10, 'Puis les Apoftres eftans retournez, lui raconterent toutes les chofes qu’ils avoyent faites. Alors Jésus les prit, et fe retira à part en une contrée deferte de la ville appellée Bethfaïda.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 11, 'Et quand les troupes le fceurent, elles le fuivirent: et les ayant receuës, leur parloit du royaume de Dieu, et gueriffoit ceux qui avoyent befoin de guerifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 12, 'Or le jour avoit commencé à decliner: dont les douze venans vers lui, lui dirent, Donne congé à cette multitude, afin qu’ils s’en aillent aux bourgades et aux villages qui font à l’environ, pour s’y retirer et trouver à manger: car nous fommes ici en un païs defert.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 13, 'Mais il leur dit, Vous autres donnez leur à manger. Et ils dirent, Nous n’avons pas plus de cinq pains et deux poiffons, finon que nous allaffions acheter des vivres pour tout ce peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 14, 'Car ils eftoyent environ cinq mille hommes. Alors il dit à fes difciples, Faites les arranger par cinquantaines en chacune rangée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 15, 'Et ils firent ainfi, et les firent tous arranger.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 16, 'Alors il prit les cinq pains et les deux poiffons, et regardant vers le ciel il les benit, les rompit, et les diftribua à fes difciples, afin qu’ils les miffent devant la troupe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 17, 'Ainfi il en mangerent tous, et furent raffafiez: et il fut recüeilli de ce qui leur eftoit demeuré de refte des pieces de pain, douze corbeilles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 18, 'Or il advint que comme il eftoit à part en priere, et que les difciples eftoyent avec lui, il les interrogea, difant, Qui difent les troupes que je fuis?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 19, 'Eux refpondans, dirent, les uns, Jean Baptifte: et les autres, Elie: et les autres, Quelqu’un des anciens Prophetes eft reffufcité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 20, 'Et il leur dit, Et vous, qui dites-vous que je fuis? Et Pierre refpondant dit, Le Christ de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 21, 'Alors ufant de menaces, il leur commanda qu’ils ne le diffent à perfonne:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 22, 'Difant, Il faut que le Fils fe l’homme fouffre beaucoup, et qu’il foit rejetté des Anciens, et des Scribes: et qu’il foit mis à mort: et qu’il reffufcite au troifiéme jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 23, 'Puis il difoit à tous, Si quelqu’un veut venir apres moi, qu’il renonce à foi-mefme, qu’il charge fur foi de jour en jour fa croix, et qu’il me fuive.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 24, 'Car quiconque voudra fauver fa vie, il la perdra: mais quiconque perdra fa vie, il la perdra: mais quiconque perdra fa vie pour l’amour de moi, celui-là la fauvera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 25, 'Car que profite-t’il à l’homme s’il gagne tout le monde, et qu’il fe deftruife foi-mefme, et qu’il faffe perte de foi-mefme?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 26, 'Car quiconque aura eu honte de moi et de mes paroles, le Fils de l’homme aura honte de lui, quand il viendra en fa gloire, et du Pere, et des faints Anges.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 27, 'Et je vous dis pour vrai, qu’il y en a de ceux qui font ici prefens, qui ne goufteront point la mort, jufques à ce qu’ils ayent veu le regne de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 28, 'Or il advint environ huit jours apres ces paroles, qu’il prit avec lui Pierre, et Jean, et Jaques, et monta en une montagne pour prier.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 29, 'Et comme il prioit, la forme de fa face devint tout autre, et fon veftement devint blanc, et refplendiffant comme un efclair.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 30, 'Et voici, deux perfonnages parloyent avec lui, qui eftoyent Moyfe et Elie:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 31, 'Qui apparurent en gloire, et parloyent de fon iffuë, qu’il devoit accomplir à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 32, 'Or Pierre et ceux qui eftoyent avec lui, eftoyent appefantis de fommeil: et quand il furent réveillez, ils virent la gloire d’icelui, et les deux perfonnages qui eftoyent avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 33, 'Et il advint, comme ces perfonnages-là fe départoyent d’avec lui, que Pierre dit à Jésus, Maiftre, il eft bon que nous foyons ici, faifons-y donc trois tabernacles, un pour toi, et un pour Moyfe, et un pour Elie: ne fçachant pas ce qu’il difoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 34, 'Et comme il difoit ces chofes, une nuée vint, qui les enombra, et ainfi qu’ils entroyent en la nuée, ils eurent peur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 35, 'Alors une voix vint de la nuée, difant, Celui-ci eft mon Fils bien-aimé, écoutez-le.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 36, 'Et comme la voix fe prononçoit, Jésus fe trouva feul: et ils fe teurent tous, et ne rapporterent en ces jours-là rien à perfonne de ce qu’ils avoyent veu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 37, 'Or il advint le jour enfuivant, qu’eux eftans defcendus de la montagne, une grande troupe le vint rencontrer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 38, 'Et voici, un homme d’entre la troupe s’écria, difant, Maiftre, je te prie regarde à mon fils, car il m’eft unique.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 39, 'Et voici, un efprit le prend, et il s’écrie foudainement, et le dérompt en le faifant écumer, et à grand’ peine fe départ de lui, mefmes en le froiffant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 40, 'Or j’ai prié tes difciples qu’ils le jettaffent hors, mais ils n’ont peu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 41, 'Et Jésus refpondant dit, O generation infidele et de fens renverfé! jufques à quand finalement ferai-je avec vous, et vous fupporterai-je? amene ici ton fils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 42, 'Et comme il approchoit feulement, le diable le froiffa, et le dérompit: mais Jésus tança l’efprit immonde, et guerit l’enfant, et le rendit à fon pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 43, 'Et tous furent eftonnez de la magnifique vertu de Dieu: et comme tous s’eftonnoyent de tout ce qu’il faifoit, il dit à fes difciples,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 44, 'Vous autres, mettez ces paroles en vos oreilles: car il adviendra que le Fils de l’homme fera livré és mains des hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 45, 'Mais ils n’entendoyent point cette parole, et elle leur eftoit tellement cachée, qu’ils ne la comprenoyent point: et ils craignoyent de l’interroger touchant cette parole.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 46, 'Puis ils entrerent en difpute entr’eux, affavoir lequel d’entr’eux eftoit le plus grand.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 47, 'Mais Jésus voyant la penfée de leur coeur, prit un petit enfant, et le mit auprés de foi:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 48, 'Et leur dit, Quiconque recevra ce petit enfant en mon Nom, il me reçoit: et quiconque me recevra, il reçoit celui qui m’a envoyé. Car celui qui eft le plus petit d’entre vous tous, celui-là fera grand.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 49, 'Et Jean prenant la parole, dit, Maiftre, nous avons veu quelqu’un qui jettoit hors les diables en ton Nom, et l’en avons empefché, dautant qu’il ne te fuit point avec nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 50, 'Alors Jésus lui dit, Ne l’empefchez point: car qui n’eft point contre nous, il eft pour nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 51, 'Or il advint quand les jours de fon élevation s’accompliffoyent, qu’il dreffa fa face, tout refolu d’aller à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 52, 'Et il envoya des meffagers devant foi, lefquels eftans partis entrerent en une bourgade des Samaritains, pour lui preparer logis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 53, 'Mais les Samaritains ne le receurent point, parce que fa face eftoit comme de celui qui alloit à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 54, 'Et quand Jaques et Jean fes difciples virent cela, ils dirent, Seigneur, veux-tu que nous difions que le feu defcende du ciel, et les confume, comme auffi fit Elie?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 55, 'Mais Jésus fe retournant les tança, et dit, Vous ne fçavez de quel efprit vous eftes quand à vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 56, 'Car le Fils de l’homme n’eft point venu pour faire perir les ames des hommes, mais pour les fauver. Ainfi ils s’en allerent en une autre bourgade.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 57, 'Et il advint comme ils alloyent par le chemin, qu’un certain lui dit, Je te fuivrai, Seigneur, quelque-part que tu ailles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 58, 'Mais Jésus lui refpondit, Les renards ont des tanieres, et les oifeaux du ciel ont des nids, mais le Fils de l’homme n’a point où repofer fa tefte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 59, 'Puis il dit à un autre, Sui moi: lequel dit, Permets moi premierement d’aller enfevelir mon pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 60, 'Et Jésus lui dit, Laiffe les morts enfevelir leurs morts, mais toi, va, et annonce le royaume de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 61, 'Alors un autre auffi dit, Je te fuivrai, Seigneur, mais permets moi de prendre premierement congé de ceux qui font en ma maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 62, 'Mais Jésus lui refpondit, Nul qui met la main à la charruë, et regarde en arriere, n’eft bien difpofé pour le royaume de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 1, 'OR apres ces chofes le Seigneur en ordonna auffi foixante et dix autres, et les envoya deux à deux devant fa face, en toute ville et en tout lieu où il devoit venir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 2, 'Et il leur difoit, La moiffon eft grande, mais il y a peu d’ouvriers: priez donc le Seigneur de la moiffon, qu’il pouffe des ouvriers en fa moiffon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 3, 'Allez, voici, je vous envoye comme des agneaux au milieu des loups.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 4, 'Ne portez ni bourfe, ni malette, ni fouliers: et ne falüez perfonne par le chemin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 5, 'Et en quelque maifon que vous entrerez, dites premierement, Paix foit à cette maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 6, 'Que fi quelque enfant de paix eft là, voftre paix repofera fur lui: finon, elle retournera à vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 7, 'Et demeurez en cette maifon-là, mangeans et buvans de ce qui fera mis devant vous: car l’ouvrier eft digne de fon falaire. Ne paffez point de maifon en maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 8, 'Mefmes auffi en quelque ville que vous entrerez, et qu’ils vous recevront, mangez de ce qui fera mis devant vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 9, 'Et gueriffez les malades qui feront en elle, et leur dites, Le royaume de Dieu eft approché de vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 10, 'Mais en quelque ville que vous ferez entrez, et ils ne vous auront point receus, fortez en fes ruës, et dites,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 11, 'Nous fecoüons contre vous mefmes la poudre de voftre ville, qui s’eft attachée à nous: toutefois fçachez cela, que le royaume de Dieu eft approché de vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 12, 'Or je vous dis, qu’en cette journée-là ceux de Sodome feront traittez plus tolerablement que cette ville-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 13, 'Mal-heur fur toi, Chorazim, mal-heur fur toi Bethfaïda: car fi les vertus qui ont efté faites en vous, euffent efté faites à Tyr et à Sydon, elles fe fuffent dés long-temps repenties, gifantes avec le fac et la cendre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 14, 'Partant Tyr et Sidon feront traitées plus tolerablement au jugement que vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 15, 'Et toi Capernaum, qui as efté élevée jufques au ciel, tu feras abbaiffée jufques en enfer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 16, 'Qui vous écoute il m’écoute, et qui vous rejette il me rejette: or qui me rejette il rejette celui qui m’a envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 17, 'Or les foixante et dix s’en revinrent avec joye, difans, Seigneur, les diables mefmes nous font affujettis en ton Nom.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 18, 'Et il leur dit, Je contemplois Satan tombant du ciel comme un éclair.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 19, 'Voici, je vous donne puiffance de marcher fur les ferpens et fur les fcorpions, et fur toute la force de l’ennemi: et rien ne vous bleffera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 20, 'Toutefois ne vous éjouïffez point de ce que lesefprits vous font affujettis: mais éjouïffez-vous pluftoft de ce que vos noms font écrits és cieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 21, 'En ce mefme inftant Jésus s’éjouït en efprit, et dit, Je te rens graces, ô Pere, parce que tel a efté ton bon plaifir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 22, 'Toutes chofes m’ont efté données en main de mon Pere: et nul ne connoift qui eft le Fils, finon le Pere: ni qui eft le Pere, finon le Fils, et celui auquel le Fils le voudra reveler.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 23, 'Puis fe tournant vers fes difciples, il dit à part, Bien-heureux font les yeux qui voyent ce que vous voyez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 24, 'Car je vous dis, que plufieurs Prophetes et plufieurs Rois ont defiré de voir les chofes que vous voyez, et ne les ont point veuës, et d’ouïr les chofes que vous oyez, et ne les ont point ouïes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 25, 'Alors voici un docteur de la Loi, qui fe leva le tentant, et difant, Maiftre, en quoi faifant heriterai-je la vie eternelle?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 26, 'Auquel il dit, Qu’eft-il écrit en la Loi? comment lis-tu?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 27, 'Et il refpondit, et dit, Tu aimeras le Seigneur ton Dieu de tout ton coeur, et de toute ta penfée: Et ton prochain comme toi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 28, 'Alors il lui dit, Tu as droitement refpondu: fai cela, et tu vivras.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 29, 'Mais lui fe voulant juftifier, dit à Jésus, Et qui eft mon prochain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 30, 'Jésus refpondant, dit, Un homme defcendoit de Jerufalem à Jerico, et tomba entre les mains des brigands, qui le dépoüillerent: et apres qu’ils l’eurent navré de plufieurs coups, ils s’en allerent le laiffans à demi mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 31, 'Or par rencontre un Sacrificateur defcendoit par le chemin: et quand il le vid, il paffa de l’autre cofté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 32, 'Semblablement auffi un Levite eftant arrivé à l’endroit, et le voyant, paffa de l’autre cofté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 33, 'Mais un Samaritain paffant fon chemin, vint à l’endroit d’icelui, et le voyant fut émeu de compaffion.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 34, 'Et s’approchant lui refferra fes playes, et y mit deffus de l’huile et du vin: puis le mit fur fa befte, et le mena en l’hoftellerie, et le penfa.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 35, 'Le lendemain au depart, il tira deux deniers, et les bailla à l’hofte: et dit, Penfe-le: et tout ce que tu defpenferas davantage, je te le rendrai à mon retour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 36, 'Lequel donc de ces trois te femble avoir efté le prochain à celui qui tomba entre les mains des brigands.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 37, 'Et il lui dit, Celui qui a ufé de mifericorde envers lui. Jésus donc lui dit, Va, et toi auffi fai le femblable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 38, 'Puis il advint comme ils s’en alloyent, qu’il entra en une bourgade: et une femme nommée Marthe le receut en fa maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 39, 'Or elle avoit une foeur, nommée Marie, laquelle auffi fe tenant affife aux pieds de Jésus, écoutoit fa parole.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 40, 'Et Marthe eftoit diftraite à faire beaucoup de fervice: laquelle eftant furvenuë vers lui, dit, Seigneur, ne te foucies-tu point que ma foeur me laiffe fervir toute feule? Di lui donc qu’elle m’aide de fon cofté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 41, 'Et Jésus refpondant lui dit, Marthe, Marthe, tu te foucies et te travailles de beaucoup de chofes:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 42, 'Mais une chofe eft neceffaire. Or Marie a choifi la bonne part, qui ne lui fera point oftée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 1, 'Il advint auffi comme il eftoit en priere en quelque lieu, apres qu’il eut ceffé, que quelqu’un de fes difciples lui dit, Seigneur, enfeigné nous à prier, ainfi que Jean a auffi enfeigné fes difciples.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 2, 'Et il leur dit, Quand vous priez, dites, Noftre Pere qui es és cieux. Ton nom foit fanctifié. Ton regne vienne. Ta volonté foit faite en la terre comme au ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 3, 'Donne nous de jour à autre noftre pain quotidien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 4, 'Et nous pardonne nos pechez: car nous pardonnons auffi à tous ceux qui nous doivent. Et ne nous indui point en tentation, mais delivre nous du malin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 5, 'Puis il leur dit, Qui fera celui d’entre vous qui aura un ami, lequel ira à lui à la minuit, et lui dira, Mon ami, prefte moi trois pains:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 6, 'Car un mien ami m’eft furvenu en paffant, et je n’ai rien pour lui prefenter.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 7, 'Et que celui qui eft dedans refponde, et die, Ne me fafche point: car ma porte eft déja fermée, et mes petits enfans font avec moi au lit: je ne me puis lever pour t’en donner.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 8, 'Je vous dis, Encore qu’il ne fe leve point pour lui en donner, parce qu’il eft fon ami, toutefois pour fon importunité il fe levera, et lui en donnera autant qu’il en aura befoin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 9, 'Et moi je vous dis, Demandez, et il vous sera donné: cherchez, et vous trouverez: heurtez, et il vous fera ouvert.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 10, 'Car quiconque demande, il reçoit: et qui cherche, il trouve: et à celui qui heurte, il fera ouvert.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 11, 'Que fi un enfant demande du pain à quelqu’un d’entre vous qui foit pere, lui baillera-t’il une pierre? Ou s’il demande du poiffon, lui baillera-t’il au lieu du poiffon un ferpent?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 12, 'Ou s’il demande un oeuf, lui baillera-t’il un fcorpion?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 13, 'Si vous donc qui eftes méchans, fçavez bien donner à vos enfans de bonnes chofes, combien plus voftre Pere celefte donnera-t’il le faint Efprit à ceux qui le lui demandent?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 14, 'Alors il jetta hors un diable, qui eftoit muët: et il advint quand le diable fut forti, que le muët parla: dont les troupes s’eftonnerent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 15, 'Et quelques-uns d’entr’eux dirent, C’eft par Beelzebul prince des diables, qu’il jette hors les diables.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 16, 'Mais les autres pour le tenter, lui demandoyent un figne du ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 17, 'Mais lui connoiffant leurs penfées, leur dit, Tout royaume divifé contre foi-mefme fera reduit en defert, et toute maifon divifée contre foi-mefme tombe en ruïne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 18, 'Que fi Satan eft auffi divifé contre foi-mefme, comment fubfiftera fon regne, puis que vous dites que je jette hors les diables par Beelzebul?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 19, 'Que fi je jette hors les diables par Beelzebul, vos fils par qui les jettent-ils hors? Pour cette caufe ils feront vos juges.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 20, 'Mais fi je jette hors les diables par le doigt de Dieu, certes le regne de Dieu eft parvenu à vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 21, 'Quand un fort homme bien armé garde fon hoftel, les chofes qu’il a font en feureté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 22, 'Mais fi un plus que lui furvient qui le furmonte, il lui ofte toutes fes armes efquelles il fe confioit, et partage fes defpoüilles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 23, 'Qui n’eft point avec moi, il eft contre moi: et qui ne recueille point avec moi, il épard.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 24, 'Quand l’efprit immonde eft forti de quelque homme, il chemine par des lieux fecs, cherchant du repos, et n’en trouvant point, dit, Je retournerai en ma maifon dont je fuis forti.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 25, 'Et quand il vient, il la trouve baliée et parée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 26, 'Et alors il s’en va, et prend avec foi fept autres efprits pires que lui, et ils y entrent, et habitent là, tellement que la derniere condition de cet homme-là eft pire que la premiere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 27, 'Or il advint comme il difoit ces chofes, qu’une femme d’entre les troupes éleva fa voix, et lui dit, Bien-heureux eft le ventre qui t’a porté, et les mammelles que tu as tettées.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 28, 'Alors il dit, Mais pluftoft bien-heureux font ceux qui oyent la parole de Dieu, et qui la gardent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 29, 'Et comme les troupes s’amaffoyent, il fe prit à dire, Cette generation eft méchante: elle demande un figne, et il ne lui fera point donné de figne, finon le figne de Jonas le Prophete.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 30, 'Car comme Jonas fut un figne à ceux de Ninive, ainfi le fera auffi le Fils de l’homme à cette generation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 31, 'La reine de Midi fe levera au jugement avec les hommes de cette generation, et les condamnera: dautant qu’elle vint du bout de la terre pour ouïr la fapience de Salomon: et voici, il y a ici plus que Salomon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 32, 'Les gens de Ninive fe leveront au jugement avec cette generation, et la condamneront, dautant qu’ils fe font amendez à la predication de Jonas: et voici, il y a ici plus que Jonas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 33, 'Or nul n’allume la chandelle, et la met en un lieu caché, ni fous un boiffeau: mais fur un chandelier, afin que ceux qui entrent voyent la lumiere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 34, 'La chandelle du corps, c’eft l’oeil: fi donc ton oeil eft fimple, tout ton corps auffi fera éclairé: mais s’il eft mauvais, ton corps auffi fera tenebreux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 35, 'Regarde donc que la lumiere qui eft en toi ne foit tenebres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 36, 'Si donc tout ton corps eft éclairé, n’ayant aucune partie tenebreufe, il fera éclairé par tout, comme quand la chandelle t’éclaire par fa lumiere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 37, 'Et comme il parloit, quelque Pharifien le pria de difner chez lui: et Iefus y entra, et fe mit à table.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 38, 'Mais le Pharifien s’eftonna de ce qu’il vid qu’il ne s’eftoit point premierement lavé devant le difner.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 39, 'Et le Seigneur lui dit, Vous autres Pharifiens nettoyez le dehors de la coupe et du plat: mais le dedans de vous eft tout plein de rapine et de méchanceté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 40, 'Infenfez, celui qui a fait le dehors, n’a t’il pas fait auffi le dedans?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 41, 'Mais pluftoft donnez en aumofne ce que vous avez, et voici, toutes chofes vous feront nettes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 42, 'Mais mal-heur fur vous, Pharifiens: car vous difmez bien la menthe, et la ruë, et toute forte d’herbage, et laiffez en arriere le jugement et la charité de Dieu: il faloit faire ces chofes-ici, et ne laiffer point celles-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 43, 'Mal-heur fur vous, Pharifiens, qui aimez les premieres feances és Synagogues, et les falutations és marchez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 44, 'Mal-heur fur vous, Scribes et Pharifiens hypocrites: car vous eftes comme les fepulcres qui n’apparoiffent point, et les hommes qui paffent par deffus n’en fçavent rien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 45, 'Alors quelqu’un des docteurs de la Loi prit le propos, et lui dit, Maiftre, en difant ces chofes tu nous dis auffi outrage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 46, 'Et il dit, Mal-heur auffi fur vous, Docteurs de la Loi: car vous chargez les hommes de charges infupportables, mais vous mefmes ne touchez point les fardeaux de l’un de vos doigts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 47, 'Mal-heur fur vous, car vous baftiffez les fepulcres des Prophetes, lefquels vos peres ont tuëz.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 48, 'Pour vrai vous tefmoignez, que vous confentez aux actes de vos peres: car ils les ont tuëz, et vous baftiffez leurs fepulcres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 49, 'Pour cette caufe auffi la Sapience de Dieu a dit, Le leur envoyerai des Prophetes et des Apoftres, et ils en tuëront, et en chafferont:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 50, 'Afin que le fang de tous les Prophetes, qui a efté refpandu dés la fondation du monde, foit redemandé de cette generation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 51, 'Depuis le fang d’Abel jufqu’au fang de Zacharie, qui fut tué entre l’autel et le temple: voire je vous dis, qu’il fera redemandé à cette nation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 52, 'Mal-heur fur vous, Docteurs de la Loi: car ayans retiré la clef de connoiffance, vous mefmes n’y eftes point entrés, et avez empefché ceux qui y entroyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 53, 'Et comme il leur difoit ces chofes, les Scribes et les Pharifiens fe prirent à le tenir de prés, et à lui tirer de la bouche plufieurs chofes:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 54, 'En l’efpiant, et tachant de recueillir captieufement quelque chofe de fa bouche, pour l’accufer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 1, 'CEPENDANT les troupes s’eftans affemblée à milliers, tellement qu’ils fe fouloyent les uns les autres, il fe prit à dire à fes difciples, En premier lieu, donnez-vous garde du levain des Pharifiens, qui eft hypocrifie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 2, 'Car rien n’eft couvert, qui ne doive eftre revelé, et rien n’eft caché qui ne doive eftre connu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 3, 'Partant les chofes que vous avez dites en tenebres, feront ouïes en lumiere, et ce dont vous avez parlé en l’oreille és chambres, fera prefché fur les maifons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 4, 'Or je dis à vous, mes amis, N’ayez point de peur de ceux qui tuënt le corps, et qui apres cela ne fçauroyent rien faire davantage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 5, 'Mais je vous montrerai qui vous devez craindre: craignez celui qui a puiffance, apres qu’il a tué, d’envoyer en la gehenne: voire je vous dis, craignez celui-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 6, 'Ne vend-on pas cinq petits paffereaux deux pites, et un feul d’eux n’eft point oublié devant Dieu?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 7, 'Mefmes tous les cheveux de voftre tefte font contez: ne craignez donc point; vous valez mieux que beaucoup de paffereaux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 8, 'Or je vous dis, que quiconque me confeffera devant les hommes, le Fils de l’homme le confeffera auffi devant les Anges de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 9, 'Mais qui me reniera devant les hommes, il fera renié devant les Anges de Dieu,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 10, 'Et quiconque parlera contre le Fils de l’homme, il lui fera pardonné: mais à celui qui aura blafphemé contre le faint Efprit, il lui fera point pardonné.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 11, 'Et quand ils vous emmeneront aux Synagogues, et aux Magiftrats et aux Puiffances, ne foyez point en fouci comment, ou quelle chofe vous refpondrez, ou ce que vous aurez à dire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 12, 'Car le S. Efprit vous enfeignera en ce mefme inftant-là ce qu’il vous faudra dire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 13, 'Et quelqu’un de la troupe lui dit, Maiftre, di à mon frere qu’il partage avec moi l’heritage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 14, 'Mais il lui refpondit, O homme! qui eft-ce qui m’a eftabli juge ou partageur fur vous?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 15, 'Puis il leur dit, Voyez, et vous gardez d’avarice: car encore que les biens abondent à quelqu’un, fi eft-ce qu’il n’a pas la vie par fes biens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 16, 'Et il leur recita une fimilitude, difant, Les champs de quelque riche homme avoyent rapporté en abondance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 17, 'Dont il penfoit en foi-mefme, difant, Que ferai-je? car je n’ai point où je puiffe affembler mes fruits.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 18, 'Puis il dit, Voici ce que je ferai: j’abbattrai mes greniers, et en baftirai de plus grands, et y affemblerai tous mes revenus et mes biens:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 19, 'Puis je dirai à mon ame, Ame, tu as beaucoup de biens, affemblez pour beaucoup d’années: repofe-toi, mange, et boi, et fai grand’ chere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 20, 'Mais Dieu lui dit, Infenfé, en cette mefme nuit on te redemandera ton ame: et les chofes que tu as appreftées à qui feront-elle?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 21, 'Ainfi en prend-il de celui qui fait de grands amas de biens pour foi, et n’eft point riche en Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 22, 'Alors il dit à fes difciples, Pour cette caufe je vous dis, ne foyez point en fouci de voftre vie, ce que vous mangerez, ni voftre corps, de quoi vous ferez veftus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 23, 'La vie eft plus que la viande, et le corps plus le veftement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 24, 'Confiderez que les corbeaux ne fement ni ne moiffonnent, et n’ont point de celier ni de grenier, et toutefois Dieu les nourrit: de combien valez-vous mieux que les oifeaux?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 25, 'Et qui eft celui de vous, qui par fon fouci puiffe adjoufter une coudée à fa ftature?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 26, 'Si donc vous ne pouvez pas mefmes ce qui eft tres-petit, pourquoi eftes-vous en fouci du refte?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 27, 'Confiderez comment croiffent les lis: ils ne travaillent ni ne filent: et je vous dis que Salomon mefme en toute fa gloire n’eftoit point veftu comme l’un d’eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 28, 'Que fi Dieu reveft ainfi l’herbe qui eft aujourd’hui au champ, et demain eft mife au four, combien plus vous reveftira-t’il, ô gens de petit foi?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 29, 'Vous donc ne demandez point ce que vous mangerez, ou ce que vous boirez, et ne foyez point en fufpens:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 30, '(Car les gens de ce monde font apres à rechercher toutes ces chofes; mais voftre Pere fçait que vous avez befoin de ces chofes.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 31, 'Mais pluftoft cherchez le royaume de Dieu, et toutes ces chofes vous feront baillées par deffus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 32, 'Ne crain point, petit troupeau: car le bon-plaifir de voftre Pere a efté de vous donner le royaume.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 33, 'Vendez ce que vous avez, et donnez-en l’aumofne: faites-vous des bourfes qui ne s’envieilliffent point, un trefor és cieux qui ne defaille jamais, d’où le larron n’approche point, et où la tigne ne gafte rien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 34, 'Car là où eft voftre trefor, là auffi fera voftre coeur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 35, 'Que vos reins foyent trouffez, et vos chandelles allumées.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 36, 'Et que vous foyez femblables aux ferviteurs qui attendent leur maiftre, quand il retournera des nopces: afin que quand il fera venu, et heurtera, incontinent ils lui ouvrent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 37, 'Bien-heureux font ces ferviteurs-là, que le maiftre trouvera veillans, quand il arrivera. En verité je vous dis, qu’il fe trouffera, et les fera mettre à table, et s’avançant les fervira.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 38, 'Que s’il arrive fur la feconde veille, et pareillement fur la troifiéme, et qu’il les trouve ainfi, bien-heureux font ces ferviteurs-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 39, 'Or fçachez ceci, que fi le pere de famille euft fceu à quelle heure le larron euft deu venir, il euft veillé, et n’euft point laiffé percer fa maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 40, 'Vous donc auffi foyez prefts: car le Fils de l’homme viendra à l’heure que vous n’y penferez point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 41, 'Alors Pierre lui dit, Seigneur, dis-tu cette fimilitude pour nous, ou auffi pour tous?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 42, 'Et le Seigneur dit, Mais qui est le defpencier fidele et prudent, que le maiftre aura eftabli fur fon mefnage, pour donner en temps l’ordinaire?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 43, 'Bien-heureux eft ce ferviteur-là, que fon maiftre trouvera ainfi faifant, quand il arrivera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 44, 'Je vous dis pour vrai, qu’il l’eftablira fur tout ce qu’il a.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 45, 'Que fi ce ferviteur-là dit en fon coeur, Mon Maiftre met long-temps à venir, et qu’il fe prenne à battre les ferviteurs et les fervantes, et à manger, et à boire, et à s’enyvrer;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 46, 'Le maiftre de ce ferviteur-là viendra au jour qu’il ne l’attend point, et à l’heure qu’il ne fçait point, et le feparera, et le mettra au rang des defloyaux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 47, 'Or ce ferviteur qui a connu la volonté de fon maiftre et ne s’eft point mis en devoir, et n’a point fait felon la volonté d’icelui, fera battu de plufieurs coups.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 48, 'Mais celui qui ne l’a point connuë, et a fait des chofes dignes d’eftre battu, fera battu de moins de coups: et à un chacun à qui il aura efté beaucoup donné, il lui fera beaucoup redemandé: et à celui à qui il aura efté beaucoup commis, il lui fera tant plus redemandé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 49, 'Je fuis venu mettre le feu en la terre: et que veux-je plus s’il eft déja allumé?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 50, 'Or j’ai à eftre baptizé d’un bâtefme, et comment fuis-je preffé jufques à ce qu’il foit parfait?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 51, 'Penfez-vous que je fois venu mettre la paix en la terre? Non vous dis-je, mais pluftoft la divifion.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 52, 'Car deformais ils feront cinq en une maifon, divifez, trois contre deux, et deux contre trois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 53, 'Le pere fera divifé contre le fils, et le fils contre le pere: la mere contre la fille, et la fille contre la mere: la belle-mere contre fa belle-fille, et la belle-fille contre fa belle-mere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 54, 'Puis il difoit aux troupes, Quand vous voyez une nuée qui fe leve d’Occident, incontinent vous dites, La pluye vient: et il advient ainfi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 55, 'Et quand vous oyez le vent de Midi fouffler, vous dites qu’il fera chaud: et il advient ainfi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 56, 'Hypocrites, vous fçavez bien difcerner l’apparence du ciel et de la terre: et comment ne difcernez-vous point cette faifon?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 57, 'Et que ne jugez-vous auffi de vous-mefmes ce qui eft jufte?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 58, 'Or quand tu vas au Magiftrat avec ton adverfe partie, mets peine en chemin d’eftre délivré d’elle, afin qu’elle ne te tire devant le juge, et que l’executeur ne te mette en prifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 59, 'Je te dis, que tu ne fortiras point de là, jufques à ce que tu ayes rendu la derniere pite.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 1, 'EN ce mefme inftant-là quelques-uns qui fe trouvoyent là prefens, lui raconterent touchant les Galiléens, defquels Pilate avoit meflé le fang avec leurs facrifices.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 2, 'Et Jésus refpondant leur dit, Penfez-vous que ces Galiléens-là fuffent plus pecheurs que tous les autres Galiléens, parce qu’ils ont fouffert de telles chofes?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 3, 'Non, vous dis-je: mais fi vous ne vous amendez, vous perirez tous femblablement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 4, 'Ou penfez-vous que ces dix-huit, fur lefquels tomba la tour en Siloé, et les tua, euffent offenfé plus que tous les habitans de Jerufalem?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 5, 'Non, vous dis-je: mais fi vous ne vous amendez, vous perirez tous femblablement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 6, 'Il difoit auffi cette fimilitude, Quelqu’un avoit un figuier planté en fa vigne, et y vint chercher du fruit, et n’en trouva point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 7, 'Dont il dit au vigneron, Voici, il y a trois ans que je viens chercher du fruit en ce figuier, et je n’en trouve point, coupe-le: à quel propos auffi empefche-t’il la terre?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 8, 'Et le vigneron refpondant lui dit, Seigneur, laiffe-le encore pour cette année, jufques à ce que je l’aye defchauffé, et que j’y aye mis du fient.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 9, 'Que s’il fait du fruit, bien: finon tu le couperas ci-apres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 10, 'Or comme il enfeignoit en une de leurs Synagogues en un jour de Sabbat,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 11, 'Voici, il furvint une femme qui avoit un efprit de maladie, déja par l’efpace de dix-huit ans, laquelle eftoit courbée, et ne pouvoit aucunement fe redreffer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 12, 'Et quand Jésus l’eut veuë, il l’appella, et lui dit, Femme, tu es delivrée de ta maladie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 13, 'Et lui impofa les mains: et à l’inftant elle fut redreffée, et glorifioit Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 14, 'Mais le maiftre de la Synagogue, indigné de ce que Jésus avoit gueri au jour du Sabbat, prenant la parole dit à l’affemblée, Il y a fix jours aufquels il faut travailler: venez donc en ces jours-là, et foyez gueris, et non point au jour du Sabbat.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 15, 'Dont le Seigneur lui refpondit, et dit, Hypocrite, chacun de vous ne deftache-t’il pas fon boeuf, ou fon afne de la creche au jour du Sabbat, et les mene abbreuver?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 16, 'Et ne faloit-il point deflier de ce lien au jour du Sabbat celle-ci, qui eft fille d’Abraham, laquelle Satan avoit liée, il y a déja dix-huit ans?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 17, 'Lui difant ces chofes, tous fes adverfaires eftoyent confus: mais toutes les troupes s’éjouïffoyent de toutes les chofes glorieufes qui eftoyent faites par lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 18, 'Alors il difoit, A quoi eft femblable le regne de Dieu, et à quoi l’accomparerai-je?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 19, 'Il eft femblable au grain de femence de moutarde, qu’un homme prit, et mit en fon jardin, lequel creut, et devint un grand arbre, tellement que les oifeaux du ciel faifoyent leurs nids en fes branches.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 20, 'Derechef il dit, A quoi accomparerai-je le regne de Dieu?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 21, 'Il eft femblable au levain, qu’une femme prit, et mit parmi trois mefures de farine, jufques à ce qu’elle fuft toute levée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 22, 'Puis il s’en alloit par les villes et les bourgades, enfeignant, et tenant le chemin de Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 23, 'Et quelqu’un lui dit, Seigneur, y a-t’il peu de gens qui foyent fauvez?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 24, 'Alors il leur dit, Mettez peine d’entrer par la porte eftroitte: car je vous dis, que plufieurs tafcheront d’entrer, et ne pourront.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 25, 'Et quand le pere de famille fera levé, et aura fermé la porte, et que vous eftans dehors, vous mettrez à heurter à la porte, difans, Seigneur, Seigneur, ouvre-nous, et que lui refpondant vous dira, Je ne fçai d’où vous eftes;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 26, 'Alors vous vous prendrez à dire, Nous avons mangé et beu en ta prefence, et tu as enfeigné en nos ruës.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 27, 'Alors il dira, Je vous dis que je ne fçai d’où vous eftes: departez-vous de moi, vous tous qui faites le meftier d’iniquité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 28, 'Là il y aura pleur et grincement de dents: quand vous verrez Abraham, et Ifaac, et Jacob, et tous les Prophetes au royaume de Dieu, et que vous ferez jettez dehors.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 29, 'Il y en viendra auffi d’Orient, et d’Occident, et d’Aquilon, et de Midi, lefquels feront à table au royaume de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 30, 'Et voici il y en a des derniers qui feront les premiers, et des premiers qui feront les derniers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 31, 'En ce mefme jour-là quelques Pharifiens vinrent vers lui, lui difans, Retire-toi, et t’en va d’ici: car Herode te veut tuër.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 32, 'Alors il leur dit, Allez, et dites à ce renard-là, Voici, je jette hors les diables, et acheve de donner guerifon aujourd’hui et demain, et au troifiéme jour je prens fin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 33, 'Tant y a qu’il me faut marcher aujourd’hui et demain, et le jour enfuivant: car il n’efchet point qu’aucun Prophete meure hors de Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 34, 'Jerufalem, Jerufalem, qui tuës les Prophetes, et qui lapides ceux qui te font envoyez, combien de fois ai-je voulu raffembler en un tes enfans, ainfi que la poulle raffemble fa couvée fous fes ailes, et vous ne l’avez point voulu?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 35, 'Voici, voftre maifon s’en va vous eftre laiffée deferte. Or en verité je vous dis, que vous ne me verrez point jusques à ce qu’il adviendra que vous direz, Benit foit celui qui vient au Nom du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 1, 'IL advint auffi que lui eftant entré en la maifon d’un des principaux des Pharifiens, en un jour de Sabbat, pour prendre fa refection, ils l’obfervoyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 2, 'Et voici, un homme hydropique eftoit là devant lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 3, 'Et Jésus prenant la parole, parla aux Docteurs de la Loi, et aux Pharifiens, difant, Eft-il permis de guerir au jour du Sabbat?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 4, 'Et ils ne dirent mot. Alors ayant pris le malade, il le guerit, et le renvoya.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 5, 'Puis leur refpondant, il dit, Qui fera celui d’entre vous, qui aura un afne, ou un boeuf, qui tombe en un puits, qui ne le retire hors incontinent au jour du Sabbat.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 6, 'Et ils ne lui pouvoyent repliquer à ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 7, 'Il propofoit auffi aux conviez une fimilitude, prenant garde comme ils choififfoyent les premieres places à table, en leur difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 8, 'Quand tu feras convié par quelqu’un aux nopces, ne te mets point à table au plus haut lieu, de peur qu’il advienne qu’un plus honorable que toi foit convié par lui:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 9, 'Et que celui qui aura convié et toi et lui, vienne, et te die, Fai place à celui-ci: et qu’alors tu commences avec honte à tenir le plus bas lieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 10, 'Mais quand tu feras convié, va, et te mets au plus bas lieu: afin que quand celui qui t’a convié viendra, il te die, Mon ami, monte plus haut. Alors cela te tournera à honneur devant tous ceux qui feront à table avec toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 11, 'Car quiconque s’éleve fera abaiffé: et quiconque s’abbaiffe fera élevé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 12, 'Il difoit auffi à celui qui l’avoit convié, Quand tu fais un difner, ou un fouper, n’appelle point tes amis, ni tes freres, ni tes parens, ni tes riches voifins: de peur que par aventure ils te convient de leur part, et que la pareille te foit renduë.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 13, 'Mais quand tu feras un banquet, convie les pauvres, les impotens, les boiteux, et les aveugles:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 14, 'Et tu feras bien-heureux: parce qu’ils n’ont pas dequoi te rendre la pareille: car la pareille te fera renduë en la refurrection des juftes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 15, 'Et l’un de ceux qui eftoyent enfemble à table, ayant ouï ces paroles, lui dit, Bien-heureux fera celui qui mangera du pain au royaume de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 16, 'Mais il lui dit, Un homme fit un grand fouper, et y convia beaucoup de gens:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 17, 'Et envoya fon ferviteur à l’heure du fouper, dire à ceux qui eftoyent conviez, Venez, car tout eft déja preft.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 18, 'Mais ils fe prirent tous d’un accord à s’excufer. Le premier lui dit, J’ai acheté un heritage, et il me faut neceffairement partir pour l’aller voir: je te prie, tien moi pour excufé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 19, 'Et l’autre dit, J’ai acheté cinq couples de boeufs, et je m’en vai pour les éprouver: je te prie, tien moi pour excufé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 20, 'Et l’autre dit, J’ai pris une femme en mariage, et partant je n’y puis aller.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 21, 'Ainfi le ferviteur s’en retourna, et rapporta ces chofes à fon maiftre. Alors le pere de famille tout en colere, dit à fon ferviteur, Va-t’en viftement par les places et par les ruës de la ville, et amene ceans les pauvres, et les impotens, et les boiteux et les aveugles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 22, 'Et le ferviteur dit, Mon Maiftre, il a efté fait ainfi que tu as commandé, et il y a encore de la place.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 23, 'Et le maiftre dit au ferviteur, Va par les chemins et par les hayes, et contrain d’entrer ceux que tu trouveras, afin que ma maifon foit remplie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 24, 'Car je vous dis, Que nul de ces hommes-là qui avoyent efté conviez ne gouftera de mon fouper.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 25, 'Or de grandes troupes alloyent avec lui: et lui fe tournant leur dit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 26, 'Si quelqu’un vient vers moi, et ne hait fon pere et fa mere, et fa femme, et fes enfans, et fes freres, et fes foeurs, et encore mefmes fon ame, il ne peut eftre mon difciple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 27, 'Et quiconque ne charge fur foi fa croix et ne vient apres moi, il ne peut eftre mon difciple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 28, 'Car qui eft celui d’entre vous, qui voulant baftir une tour, premierement ne s’affée, et ne calcule les defpens, s’il a pour l’achever?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 29, 'De peur qu’apres qu’il aura pofé le fondement, et n’aura peû achever, tous ceux qui le verront commencent à fe mocquer de lui,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 30, 'Difant, Cet homme a commencé à baftir, et n’a peû achever.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 31, 'Ou, qui eft le Roi qui parte pour donner bataille à un autre Roi, qui premierement ne s’affée, &amp; ne confulte s’il pourra avec dix mille hommes aller rencontrer celui qui vient avec vingt mille contre lui?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 32, 'Autrement ce Roi-là eftant encore loin, il envoye un ambaffade, &amp; demande les moyens de paix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 33, 'Ainfi donc chacun de vous qui ne renonce pas à tout ce qu’il a, ne peut eftre mon difciple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 34, 'Le fel eft bon: mais fi le fel perd fa faveur, dequoi le falera-t’on?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 35, 'Il n’eft propre, ni pour mettre en la terre, ni au fumier: mais on le jette dehors. Qui a des oreilles pour ouïr, qu’il oye.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 1, 'OR tous les peagers &amp; les gens de mauvaife vie s’approchoyent de lui pour l’ouïr.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 2, 'Dont les Pharifiens &amp; les Scribes murmuroyent, difans, Celui-ci reçoit les gens de mauvaife vie, &amp; mange avec eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 3, 'Mais il leur propofa cette fimilitude, difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 4, 'Qui eft l’homme d’entre vous, qui ayant cent brebis, s’il en perd une, ne laiffe les quatre vingts &amp; dix-neuf au defert, &amp; ne s’en aille apres celle qui eft perduë, jufques à ce qu’il l’ait trouvée;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 5, 'Et l’ayant trouvée, ne la mette fur fes efpaules bien joyeux:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 6, 'Puis eftant venu en la maifon n’appelle fes amis &amp; fes voifins, &amp; ne leur die, Ejouïffez-vous avec moi: car j’ai trouvé ma brebis qui eftoit perduë?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 7, 'Je vous dis, qu’ainfi il y aura joye au ciel pour un feul pecheur venant à s’amender, plus que pour quatre vingts &amp; dix-neuf juftes, qui n’ont pas befoin de repentance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 8, 'Ou qui eft le femme qui ayant dix dragmes, fi elle perd une dragme, n’allume la chandelle, &amp; ne balie la maifon, &amp; ne la cherche diligemment, jufqu’à ce qu’elle l’ait trouvée?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 9, 'Et quand elle l’a trouvée, n’appelle fes amies &amp; fes voifines, difant, Ejouïffez-vous avec moi: car j’ai trouvé la dragme que j’avois perduë?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 10, 'Ainfi je vous dis, qu’il y aura joye devant les Anges de Dieu pour un feul pecheur venant à s’amender.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 11, 'Il leur dit auffi, Un homme avoit deux fils:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 12, 'Dont le plus jeune dit à fon pere, Mon pere donne-moi la part du bien qui m’appartient. Ainfi il leur partagea les biens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 13, 'Et peu de jours apres, quand le plus jeune fils eut tout affemblé, il s’en alla dehors en un païs éloigné: &amp; là il diffipa fon bien, en vivant prodigalement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 14, 'Et apres qu’il eut tout dépenfé, il advint une famine en ce païs-là: dont il commença à avoir difette.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 15, 'Alors il s’en alla, &amp; fe mit avec un des citoyens du lieu, qui l’envoya en fes poffeffions pour paiftre les pourceaux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 16, 'Et il defiroit de remplir fon ventre des gouffes que les pourceaux mangeoyent: mais perfonne ne lui en donnoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 17, 'Dont eftant revenu à foi-mefme, il dit, Combien y a-t’il de mercenaires en la maifon de mon pere, qui ont du pain &amp; plus, &amp; moi je meurs de faim?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 18, 'Je me leverai, &amp; m’en irai vers mon pere, &amp; lui dirai, Mon pere, j’ai peché contre le ciel &amp; devant toi:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 19, 'Et je ne fuis plus digne d’eftre appellé ton fils: fai moi comme à l’un de tes mercenaires');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 20, 'Ainfi donc il partit, &amp; vint vers fon pere. Or lui eftant encore loin, fon pere le vid, &amp; fut émeu de compaffion &amp; accourut, &amp; fe jetta à fon col, &amp; le baifa.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 21, 'Mais le fils lui dit, Mon pere, j’ai peché contre le ciel, &amp; devant toi: &amp; je ne fuis plus digne d’eftre appellé ton fils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 22, 'Or le pere dit à fes ferviteurs, Tirez-moi hors la plus belle robbe, &amp; le veftez, &amp; lui donnez un anneau en fa main, &amp; des fouliers en fes pieds:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 23, 'Et amenez-moi le veau gras, &amp; le tuez, faifons bonne chere en le mangeant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 24, 'Car mon fils que voici eftoit mort, &amp; il eft retrouvé. Et ils commencerent à faire bonne chere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 25, 'Or fon fils aifné eftoit aux champs, &amp; comme en venant il approchoit de la maifon, il ouït la melodie &amp; les danfes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 26, 'Et il appella l’un des ferviteurs, &amp; l’interrogea ce que c’eftoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 27, 'Lequel lui dit, Ton frere eft venu, &amp; ton pere a tué le veau gras, parce qu’il l’a recouvré fain &amp; fauf.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 28, 'Mais il fe mit en colere, &amp; ne voulut point entrer. Son pere donc eftant forti le prioit d’entrer :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 29, 'Mais il refpondit, &amp; dit à fon pere, Voici il y a tant d’années que je te fers, &amp; jamais n’outrepaffai ton commandement, &amp; tu ne me donnas jamais un chevreau pour faire bonne chere avec mes amis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 30, 'Mais quand celui-ci ton fils, qui a mangé ton bien avec les paillardes, eft venu, tu lui as tué le veau gras.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 31, 'Et le pere lui dit, Mon enfant, tu es toûjours avec moi, &amp; tous mes biens font tiens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 32, 'Or il faloit faire bonne chere, &amp; s’éjouïr, parce que celui-ci ton frere eftoit mort, &amp; il eft retourné à vie: il eftoit perdu, &amp; il eft retrouvé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 1, 'IL difoit auffi à fes difciples, Il y avoit un riche homme qui avoit un maiftre d’hoftel, lequel fut accufé envers lui comme diffipateur des biens d’icelui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 2, 'Lequel il appella, &amp; lui dit, Qu’eft-ce que j’ois dire de toi? ren conte de ton adminiftration: car tu n’auras plus la puiffance de manier la defpenfe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 3, 'Alors le maiftre d’hoftel dit en foi-mefme, Que ferai-je, mon maiftre m’oftant l’adminiftration? je ne puis fouïr la terre, &amp; j’ai honte de mendier.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 4, 'Or je fçai ce que je ferai, afin que quand mon adminiftration me fera oftée, quelques-uns me reçoivent en leurs maifons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 5, 'Alors il appella un chacun des detteurs de fon maiftre, &amp; dit au premier, Combien dois-tu à mon maiftre?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 6, 'Lequel lui dit, Cent mefures d’huile. Et il lui dit, Pren ta fcedule, &amp; t’affieds vifte, &amp; en efcri cinquante.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 7, 'Puis il dit à un autre, Et toi combien dois-tu? Lequel dit, Cent mefures de froment. Et il dit, Pren ta fcedule, &amp; en efcri quatre-vingts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 8, 'Et le maiftre loüa le maiftre d’hoftel inique, parce qu’il avoit fait prudemment: ainfi les enfans de ce fiecle font plus prudens en leur generation, que ne font les enfans de lumiere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 9, 'Et moi auffi je vous dis, Faites-vous des amis des richeffes iniques: afin que quand vous defaudrez, ils vous reçoivent és tabernacles eternels.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 10, 'Qui eft loyal en bien petite chofe, il eft auffi loyal en grande chofe: &amp; qui eft injufte en bien peu de chofe, il eft auffi injufte en grande chofe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 11, 'Si donc vous n’avez pas efté loyaux és richeffes iniques, qui fe fiera en vous des vrayes richeffes?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 12, 'Et fi en ce qui eft à autrui vous n’avez pas efté loyaux, qui vous baillera ce qui eft voftre?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 13, 'Nul ferviteur ne peut fervir à deux maiftre: car il haïra l’un &amp; aimera l’autre: ou il fe tiendra à l’un &amp; méprifera l’autre. Vous ne pouvez fervir à Dieu &amp; aux richeffes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 14, 'Or les Pharifiens auffi qui eftoyent avaricieux, oioyent toutes ces chofes, &amp; fe mocquoyent de lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 15, 'Et il leur dit, C’eft vous qui vous juftifiez vous-mefmes, devant les hommes: car ce qui eft haut devant les hommes, eft en abomination devant Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 16, 'La Loi &amp; les Prophetes ont duré jufques à Jean: depuis ce temps-là le regne de Dieu eft evangelizé, &amp; chacun le force.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 17, 'Or il eft plus aifé que le ciel &amp; la terre fe paffent, que non pas qu’il tombe un feul point de la Loi');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 18, 'Quiconque delaiffe fa femme, &amp; fe marie à une autre, commet adultere: &amp; quiconque prend celle qui eft delaiffée de fon mari, commet adultere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 19, 'Or il y avoit un riche homme qui fe veftoit de pourpre &amp; de fin lin, &amp; qui par chaque jour traittoit bien &amp; magnifiquement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 20, 'Il y avoit d’autre part un pauvre, nommé Lazare, qui gifoit à la porte d’icelui, eftant tout plein d’ulceres:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 21, 'Lequel defiroit d’eftre raffafié des miettes qui tomboyent de la table du riche: &amp; mefmes les chiens venoyent, &amp; lui lechoyent fes ulceres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 22, 'Or il advint que le pauvre mourut, &amp; qu’il fut porté par les Anges au fein d’Abraham: Or le riche auffi mourut, &amp; fut enfeveli.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 23, 'Et lui eftant en enfer, &amp; élevant fes yeux, comme il eftoit és tourmens, il vid de loin Abraham, &amp; Lazare au fein d’icelui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 24, 'Et s’écriant, il dit, Pere Abraham, aye pitié de moi, &amp; envoye Lazare, afin qu’il moüille d’eau le bout de fon doigt, &amp; qu’il rafraifchiffe ma langue: car je fuis griévement tourmenté en cette flamme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 25, 'Et Abraham refpondit, Mon fils, fouvien-toi que tu as receu tes biens en ta vie, &amp; Lazare femblablement les maux: &amp; maintenant il eft confolé, &amp; tu es griévement tourmenté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 26, 'Et outre tout cela, il y a un grand abyfme eftabli entre vous &amp; nous: tellement que ceux qui veulent paffer d’ici vers vous, ne le peuvent, ni de là paffer ici.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 27, 'Et il dit, Je te prie donc, pere, que tu l’envoyes en la maifon de mon pere:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 28, 'Car j’ai cinq freres, afin qu’il leur attefte: de peur qu’eux auffi ne viennent en ce lieu de tourment.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 29, 'Abraham lui refpondit, Ils ont Moyfe &amp; les Prophetes, qu’ils les écoutent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 30, 'Mais il dit, Non, pere Abraham, mais fi quelqu’un des morts va vers eux, ils s’amenderont.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 31, 'Et Abraham lui dit, s’ils n’écoutent Moyfe &amp; les Prophetes, ils ne feront non plus perfuadez, quand bien quelqu’un des morts reffufciteroit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 1, 'OR il dit à fes difciples, Il ne fe peut faire qu’il n’arrive des fcandales: toutefois mal-heur à celui par qui ils arrivent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 2, 'Il lui vaudroit mieux qu’on lui mit une pierre de meule à l’entour de fon col: &amp; qu’il fût jetté en la mer, que de fcandalizer un feul de ces petits.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 3, 'Prenez garde à vous: Si donc ton frere a peché contre toi, repren-le: &amp; s’il s’amende, pardonne lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 4, 'Et fi fept fois le jour il a peché contre toi, &amp; que fept fois le jour il retourne à toi, difant, Je me repens: ru lui pardonneras.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 5, 'Alors les Apoftres dirent au Seigneur, Augmente-nous la foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 6, 'Et le Seigneur dit, Si vous aviez de la foi auffi gros qu’un grain de femence de mouftarde, vous pourriez dire à ce meurier, Defracine toi, &amp; te plante en la mer: &amp; il vous obeïroit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 7, 'Mais qui eft celui qui d’entre vous qui ait un ferviteur labourant ou paiffant le beftail, qui le voyant retourner des champs, lui die, Avance-toi incontinent, &amp; te mets à table:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 8, 'Et ne lui die pluftoft, Apprefte moi à foupper, &amp; te trouffe, &amp; me fers jufques à ce que j’aye mangé &amp; beu: &amp; apres cela tu mangeras &amp; tu boiras?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 9, 'Sçait-il gré à ce Seviteur-là, parce qu’il a fait ce qui lui avoit efté commande? Je ne le penfe pas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 10, 'Vous auffi femblablement, quand vous aurez fait toutes les chofes qui vous font commandées, dites, Nous fommes ferviteurs inutiles, dautant que ce que nous eftions tenus de faire, nous l’avons fait.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 11, 'Et il advint qu’en allant à Jerufalem, il paffoit par le millier de la Samarie, &amp; de la Galilée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 12, 'Et comme il entroit en une bourgade, dix hommes lepreux le rencontrerent, lesquels s’arrefterent de loin,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 13, 'Et éleverent leur voix, difans, Jefus, noftre Maiftre, aye pitié de nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 14, 'Et quand il les eut veus, il leur dit, Allez, montrez-vous aux Sacrificateurs. Et il advint qu’en s’en allant ils furent nettoyez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 15, 'Et l’un d’entr’eux voyant qu’il eftoit gueri, s’en retourna, glorifiant Dieu à haute voix:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 16, 'Et fe jetta en terre fur fa face aux pieds d’icelui, lui rendant graces. Or il eftoit Samaritain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 17, 'Alors Jefus prenant la parole, dit, Les dix n’ont-ils pas efté nettoyez? &amp; les neuf où font-ils?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 18, 'Nul ne s’eft trouvé qui foit retourné pour rendre gloire à Dieu, finon cet eftranger.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 19, 'Alors il lui dit, Leve-toi: va, ta foit t’a fauvé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 20, 'Or eftant interrogé par les Pharifiens, quand le regne de Dieu viendroit: il leur refpondit, &amp; dit, Le regne de Dieu ne viendra point avec apparence.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 21, 'Et on ne dira point, Voici, il eft ici: ou voila, il eft là: car voici, le regne de Dieu eft dans vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 22, 'Il dit auffi à fes difciples, Les jours viendront que vous defirerez de voir l’un des jours du Fils de l’homme, &amp; ne le verrez point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 23, 'Alors on vous dira, Voici, il eft ici: ou voila, il eft là, mais n’y allez point, &amp; ne les fuivez point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 24, 'Car comme l’éclair éclaire de l’un des coftez de deffous le ciel, &amp; reluit jufques à l’autre qui eft fous le ciel, tel fera auffi le Fils de l’homme en fon jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 25, 'Mais premierement il faut qu’il fouffre beaucoup, &amp; qu’il foit rejetté de cette nation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 26, 'Et comme il advint és jours de Noé, il en fera de mefme jours du Fils de l’homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 27, 'On mangeoit &amp; on beuvoit, on prenoit &amp; on bailloit à femme, jufques au jour que Noé entra en l’arche: &amp; le deluge vint, qui les fit tous perir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 28, 'Semblablement auffi comme il advint és jours de Loth: on mangeoit, on beuvoit, on achetoit, on vendoit, plantoit &amp; on baftiffoit:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 29, 'Mais au jours que Loth fortit de Sodome, il plût feu &amp; fouffre du ciel, qui les fit tous perir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 30, 'Il en fera tout de mefme au jour que le Fils de l’homme fera revelé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 31, 'En ce jour-là, que celui qui fera fur la maifon, &amp; aura fon mefnage en la maifon, ne defcende point pour l’emporter: &amp; que celui qui fera aux champs, femblablement ne retourne point à ce qui eft demeuré en arriere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 32, 'Souvenez-vous de la femme de Loth.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 33, 'Quiconque cherchera à fauver fa vie, il la perdra, &amp; quiconque la perdra, il la vivifiera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 34, 'Je vous dis, qu’en cette nuict-là deux feront en un mefme lit: l’un fera pris, &amp; l’autre laiffé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 35, 'Il y en aura deux, lefquelles moudront enfemble: l’une fera prife, &amp; l’autre laiffée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 36, 'Deux feront aux champs: l’un fera pris &amp; l’autre laiffée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 37, 'Et eux refpondans, lui dirent, Où Seigneur? Et il leur dit, En quelque lieu que fera le corp mort, là auffi s’affembleront les aigles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 1, 'OR il leur auffi une fimilitude, tendant à ce qu’il faut toûjours prier, &amp; ne fe point laffer,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 2, 'Difant, Il y avoit un Juge en une ville, lequel ne craignoit point Dieu, &amp; ne refpectoit perfonne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 3, 'Or il y avoit en cette ville-là une veuve, qui vint à lui, difant, Fai moi juftice de ma partie adverfe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 4, 'Et durant un long-temps il n’en voulut rien faire. Toutefois apres cela il dit en foi-mefme, bien que je ne craine point Dieu, &amp; que je ne refpecte perfonne:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 5, 'Neantmoins parce que cette veuve me donne de la peine, je lui ferai juftice, de peur que finalement elle vienne, &amp; me rompe la tefte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 6, 'Et le Seigneur dit, Efcoutez ce que dit le juge inique.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 7, 'Et Dieu ne vengera-t’il point fes éleus qui crient à lui jour &amp; nuit, bien qu’il differe de fe courroucer pour l’amour d’eux?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 8, 'Je vous dis, que bien-toft il les vengera. Mais quand le Fils de l’homme viendra, penfez-vous qu’il trouve de la foi en la terre?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 9, 'Il dit auffi cette fimilitude à quelques-uns qui fe confioyent en eux-mefmes d’eftre juftes, &amp; qui tenoyent les autres pour rien:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 10, 'Deux hommes monterent au temple pour prier, l’un Pharifien, &amp; l’autre Peager.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 11, 'Le Pharifien fe tenant à part prioit en foi-mefme, difant telles chofes, O Dieu! je te rens graces, que je ne fuis point comme le refte des hommes, qui font raviffeurs, injuftes, adulteres: ni mefmes auffi comme ce Peager.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 12, 'Je jeufne deux fois la femaine: je donne la difme de tout ce que je poffede.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 13, 'Mais le Peager fe tenant loin, n’ofoit pas mefme lever les yeux vers le ciel: mais frapoit fa poitrine, difant, O Dieu! fois appaifé envers moi qui fuis pecheur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 14, 'Je vous dis, que celui-ci defcendit juftifié en fa maifon pluftoft que l’autre: Car quiconque s’éleve, fera abbaiffé: &amp; qui s’abbaiffe, fera élevé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 15, 'Or on lui prefenta auffi de petits enfans, afin qu’il les touchaft? ce que les difciples voyans ils les tancerent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 16, 'Mais Jefus les ayant fait venir à foi, dit, Laiffez les petits enfans venir à moi, &amp; ne les empefchez point: Car à tels eft le royaume de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 17, 'En verité je vous dis, Quiconque ne recevra comme enfant le regne de Dieu, n’entrera point en icelui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 18, 'Alors quelque Seigneur l’interrogea, difant, Bon Maiftre, que ferai-je pour heriter la vie eternelle?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 19, 'Jefus lui dit, Pourquoi m’appelles-tu bon? Il n’y a nul bon qu’un feul, affavoir Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 20, 'Tu fçais les commandemens, Tu ne commettras point adultere. Tu ne tuëras point. Tu ne déroberas point. Tu ne diras point faux tefmoignage. Honore ton pere &amp; ta mere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 21, 'Et celui-là dit, J’ai gardé toutes ces chofes dés ma jeuneffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 22, 'Quand Jefus eut ouï cela, il lui dit, Il te manque encore une chofe: ven tout ce que tu as, &amp; le diftribuë aux pauvres, &amp; tu auras un trefor au ciel: puis vien, &amp; me fui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 23, 'Icelui ayant entendu ces chofes devint fort trifte; car il eftoit grandement riche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 24, 'Jefus voyant qu’il eftoit devenu fort trifte, dit, Qu’il eft mal-aifé que ceux qui ont des biens entrent au royaume de Dieu!');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 25, 'Il eft certes plus aifé qu’un chameau entre par le trou d’une aiguille, que non pas qu’un riche entre au royaume de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 26, 'Et ceux qui ouïrent cela, dirent, Et qui peut donc eftre fauvé?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 27, 'Et il leur dit, Les chofes qui font impoffibles aux hommes, font poffibles à Dieu');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 28, 'Et Pierre dit, Voici, nous avons tout abandonné &amp; t’avons fuivi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 29, 'Et il leur dit, En verité je vous dis, qu’il n’y a pas un qui ait abandonné fa maifon, ou fes parens, ou fes freres, ou fa femme, ou fes enfans, pour l’amour du royaume de Dieu,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 30, 'Qui ne reçoive beaucoup plus en ce temps-ici, &amp; au fiecle à venir la vie eternelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 31, 'Puis Jefus prit à part les douze, &amp; leur dit, Voici, nous montons à Jerufalem, &amp; toutes les chofes, qui font écrites par les Prophetes touchant le Fils de l’homme, feront accomplies:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 32, 'Car il fera livré aux Nations, &amp; fera mocqué, &amp; injurié, &amp; on lui crachera au vifage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 33, 'Et apres qu’ils l’auront foüetté, ils le mettront à mort: mais au troifiéme jour il reffufcitera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 34, 'Et ils n’entendirent rien de ces chofes: mais ce difcours leur eftoit caché, &amp; ils n’entendoyent point ce qu’il leur difoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 35, 'Or il advint comme il approchoit de Jerico, qu’il y avoit un aveugle affis prés du chemin, &amp; mendiant:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 36, 'Lequel oyant la multitude qui paffoit, demanda ce que c’eftoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 37, 'Et ils lui refpondirent, que Jefus le Nazarien paffoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 38, 'Alors il cria, difant, Jefus fils de David, aye pitié de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 39, 'Et ceux qui alloyent devant, le tançoyent, afin qu’il fe teuft: mais il crioit beaucoup plus fort, Fils de David, aye pitié de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 40, 'Et Jefus s’eftant arrefté commanda qu’on l’amenaft vers lui. Ouis quand il fut approché, il l’interrogea,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 41, 'Difant, Que veux-tu que je te faffe? Et il refpondit, Seigneur, que je recouvre la veuë.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 42, 'Et Jefus lui dit, Recouvre la veuë, ta foi t’a sauvé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 43, 'Et à l’inftant il recouvra la veuë, &amp; le fuivoit, glorifiant Dieu. Et tout le peuple voyant cela donna loüange à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 1, 'ET Iefus eftant entré dans Jerico, alloit par la ville.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 2, 'Et voici un homme appellé Zachée, qui eftoit principal peager, &amp; eftoit riche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 3, 'Et il tafchoit à voir lequel eftoit Jefus: &amp; ne pouvoit pour la foule, car il eftoit de petite ftature.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 4, 'Et il accourut devant, &amp; monta fur un fycomore pour le voir: car il devoit paffer par là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 5, 'Et quand Jefus fut venu à l’endroit, regardant en haut, il le vid, &amp; lui dit, Zachée, defcen haftivement: car il faut que je demeure aujourd’hui en ta maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 6, 'Alors il defcendit haftivement, &amp; le receut avec joye.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 7, 'Et tous voyans cela murmuroyent, difans, Qu’il eftoit entré chez un homme de mauvaife vie pour y loger.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 8, 'Et Zachée fe prefentant là, dit au Seigneur, Voici, Seigneur, je donne la moitié de mes biens aux pauvres: &amp; fi j’ai circonvenu quelqu’un de quelque chofe, j’en rens le quadruple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 9, 'Alors Jefus lui dit, Le falut eft advenu aujourd’hui à cette maifon-ici: parce que celui-ci eft fils d’Abraham.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 10, 'Car le Fils de l’homme eft venu chercher &amp; fauver ce qui eftoit perdu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 11, 'Or ceux oyans ces chofes, Jefus pourfuivit fon propos, &amp; propofa une fimilitude, parce qu’il eftoit prés de Jerufalem, &amp; qu’ils penfoyent qu’à l’inftant le regne de Dieu devoit eftre manifefté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 12, 'Il dit donc, Un noble homme s’en alla en un païs éloigné conquerir pour foi un royaume, &amp; puis s’en revenir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 13, 'Et ayant appellé dix fiens ferviteurs, il leur donna dix marcs, &amp; leur dit, Trafiquez jufques à ce que je vienne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 14, 'Or fes citoyens le haïffoyent: c’eft pourquoi ils envoyerent une ambaffade apres lui, en difant, Nous ne voulons point que celui-ci regne fur nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 15, 'Il advint donc apres qu’il fut retourné, ayant conquis le royaume, qu’il commanda qu’on lui appellaft ces ferviteurs-là, aufquels il avoit baillé l’argent, afin qu’il fceuft combien chacun auroit gagné par fon trafic.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 16, 'Alors le premier vint, difant, Seigneur, ton marc a fait dix autres marcs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 17, 'Et il lui dit, C’eft bien fait, bon ferviteur: parce que tu as efté fidele en peu de chofe, aye puiffance fur dix villes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 18, 'Et l’autre vint, difant, Seigneur, ton marc en a fait cinq autres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 19, 'Et à celui-ci il dit auffi, Et toi, fois fur cinq villes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 20, 'Et l’autre vint, difant, Seigneur, voici ton marc que j’ai tenu enveloppé en un linge.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 21, 'Car je t’ai craint, parce que es un homme rude: tu prens ce que tu n’as point mis, &amp; tu moiffonnes ce que tu n’as point femé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 22, 'Et il lui dit, Mefchant ferviteur, je te jugerai par ta parole: tu fçavois que je fuis un homme rude, prenant ce que je n’ai point mis, &amp; moiffonnant ce que je n’ai point femé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 23, 'Et pourquoi n’as-tu pas mis mon argent à la banque, &amp; à mon retour je l’euffe redemandé avec ufure?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 24, 'Alors il dit à ceux qui eftoyent prefens, Oftez-lui le marc, &amp; le donnez à celui qui a les dix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 25, 'Et ils dirent, Seigneur, il a dix marcs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 26, 'Ainfi je vous dis qu’à un chacun qui aura, il fera donnée: &amp; à celui qui n’a rien, cela mefme qu’il a lui fera ofté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 27, 'Au furplus amenez ici ces miens ennemis, qui n’ont point voulu que je regnaffe fur eux, &amp; les tuëz devant moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 28, 'Et ayant dit ces chofes, il alloit devant eux, montant à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 29, 'Et il advint comme il approchoit de Beth-phagé &amp; de Bethanie, vers la montagne qui eft appellée des Oliviers, qu’il envoya deux de fes difciples,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 30, 'Difant, Allez à la bourgade qui eft vis à vis de vous, en laquelle eftant entrez, vous trouverez un afnon attaché, fur lequel jamais homme ne monta: deftachez-le, &amp; l’amenez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 31, 'Que fi quelqu’un vous demande pourquoi vous le détachez, vous lui direz ainfi, Parce que le Seigneur en a affaire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 32, 'Et ceux qui eftoyent envoyez s’en allerent, &amp; trouverent ainfi qu’il leur avoit dit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 33, 'Et comme ils deftachoyent l’afnon, les maiftres leur dirent, Pourquoi deftachez-vous ces afnon?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 34, 'Ils refpondirent, Le Seigneur en a affaire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 35, 'Ils l’amenerent donc à Jefus, &amp; jetterent leurs veftemens fur l’afnon: Puis ils mirent Jefus deffus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 36, 'Et comme il alloit, ils eftendoyent leurs veftemens par le chemin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 37, 'Et comme déja il approchoit à la defcente de la montagne des Oliviers, toute la multitude des difciples s’éjouïffant, fe prit à loüer Dieu à haute voix, pour toutes les vertus qu’ils avoyent veuës:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 38, 'Difans, Benit foit le Roi qui vient au nom du Seigneur, Paix foit au ciel, &amp; gloire és lieux tres-hauts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 39, 'Alors quelques-uns d’entre les Pharifiens de la troupe lui dirent, Maiftre, repren tes difciples.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 40, 'Lui refpondant, leur dit, Je vous dis, fi ceux-ci fe taifent, les pierres mefmes crieront.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 41, 'Et quand il fut approché, voyant la ville, il pleura fur elle, difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 42, 'O fi toi auffi euffes connu, voire au moins en cette tienne journée, les chofes qui appartiennent à ta paix! mais maintenant elles font cachée de devant tes yeux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 43, 'Car les jours viendront fur toi que tes ennemis t’affiegeront de tranchées, &amp; t’environneront, &amp; t’en-ferreront de tous coftez:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 44, 'Et te raferont toi, &amp; tes enfans qui font en toi, &amp; ne laifferont en toi pierre fur pierre, parce que tu n’as point connu le temps de ta vifitation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 45, 'Puis eftant entré au temple, il fe prit à jettez hors ceux qui vendoyent &amp; achetoyent en icelui:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 46, 'Leur difant, Il eft écrit, Ma maifon eft la maifon de priere: mais vous en avez fait une caverne de brigands.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 47, 'Et il eftoit tous les jours enfeignant au temple. Et les principaux Sacrificateurs &amp; les Scribes, &amp; les principaux du peuple, tafchoyent à le faire mourir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 48, 'Mais ils se trouvoyent aucune chofe qu’ils lui puiffent faire: car tout le peuple eftoit fort attentif à l’ouïr.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 1, 'OR il advint en l’in de ces jours-là, comme il enfeignoit le peuple au temple, &amp; evangelizoit, que les principaux Sacrificateurs, &amp; les Scribes avec les Anciens, furvinrent,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 2, 'Et parlerent à lui, difans, Di-nous de quelle authorité tu fais ces chofes: ou qui eft celui qui t’a donné cette authorité?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 3, 'Et Jefus refpondant, leur dit, Je vous interrogerai moi auffi d’un poinct, &amp; me refpondez:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 4, 'Le baptefme de Jean eftoit-il du ciel, ou des hommes?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 5, 'Or ils difputoyent entr’eux, difans, Si nous difons, Du ciel: il dira, Pourquoi donc ne l’avez-vous point creu?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 6, 'Et fi nous difons, Des hommes, tout le peuple nous lapidera: car ils font perfuadez que Jean eftoit Prophete:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 7, 'Et ils refpondirent, Qu’ils ne fçavoyent d’ou il eftoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 8, 'Et Jefus leur dit; je ne vous dy point auffi de quelle authoritéie je fai ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 9, 'Alors il fe prit à dire au peuple cette fimilitude, Un certain homme planta une vigne, &amp; la loüa à des vignerons, &amp; fut dehors un long-temps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 10, 'Et quand ce vint la faifon, il envoya un fien ferviteur vers vignerons, afin qu’ils lui baillaffent du fruit de la vigne, lefquels l’ayans battu, le renvoyerent à vuide.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 11, 'Et derechef il y envoya un autre ferviteur: lequel ayans auffi battu, apres l’avoir vilainement traité, ils le renvoyerent à vuide.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 12, 'Et puis il y envoya auffi un troifiéme, lequel ayans auffi navré, ils le jetterent dehors.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 13, 'Alors le Seigneur de la vigne dit, Que ferai-je? j’y envoyerai mon fils bien-aimé: peut-eftre quand ils le verront qu’ils le revereront.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 14, 'Mais quand les vignerons le virent, ils tinrent propos entr’eux, difans, Celui-ci eft l’heritier, venez, tuons-le, afin que l’heritage devienne noftre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 15, 'Et ils le jetterent hors de la vigne, &amp; le tuërent. Que leur fera donc le feigneur de la vigne?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 16, 'Il viendra, &amp; fera perir ces vignerons-là, &amp; baillera la vigne à d’autres. Ce qu’ayans ouï, ils dirent, Ainfi n’advienne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 17, 'Alors il les regarda, &amp; dit, Que veut donc dire ce qui eft écrit: La pierre que les edifians ont rejettée, eft devenuë la maiftreffe pierre du coin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 18, 'Quiconque cherra fur cette pierre-là, fera froiffé: &amp; elle brifera celui fur lequel elle cherra.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 19, 'Alors les principaux Sacrificateurs &amp; les Scribes, en ce mefme inftant tafcherent à mettre les mains fur lui: (mais ils craignirent le peuple) car ils connurent bien qu’il avoit dit cette fimilitude contr’eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 20, 'Et l’efpians, ils envoyerent des gens attitrez, qui contrefaifoyent les gens de bien, pour le furprendre en paroles, afin de le livrer à la feigneurie &amp; à la puiffance du Gouverneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 21, 'Lefquels l’interrogerent difans, Maiftre, nous fçavons que tu dis &amp; que tu enfeignes droitement, &amp; ne regardes point à l’apparence des perfonnes, mais enfeignes la voye de Dieu en verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 22, 'Nous eft-il permis de payer le tribut à Cefar, ou non?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 23, 'Mais lui ayant apperceu leur rufe, leur dit, Pourquoi me tentez-vous?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 24, 'Montrez-moi un denier: De qui a-t’il l’image &amp; l’infcription? Eux refpondans, dirent, De Cefar.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 25, 'Et il leur dit, Rendez donc à Cefar les chofes qui font à Cefar, &amp; à Dieu les chofes qui font à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 26, 'Ainfi ils ne peurent trouver à redire en fa parole devant le peuple: mais tout eftonnez de fa refponfe ils fe teurent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 27, 'Alors quelques-uns d’entre les Sadduciens (qui nient formellement la refurrection) s’approcherent, &amp; l’interrogerent,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 28, 'Difans, Maiftre, Moyfe nous a laiffé par écrit, Si le frere de quelqu’un eft mort ayant une femme, &amp; qu’il foit mort fans enfans: que fon frere prenne la femme d’icelui, &amp; qu’il fufcite lignée à fon frere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 29, 'Or il y eut fept freres, dont le premier prit une femme, &amp; mourut fans enfans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 30, 'Et le fecond la prit, &amp; mourut auffi fans enfans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 31, 'Puis le troifiéme la prit, &amp; femblablement tous les fept, &amp; moururent fans avoir laiffé d’enfans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 32, 'Et apres tous, la femme auffi mourut.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 33, 'Duquel d’eux donc fera-t’elle femme en la refurrection? car les fept l’ont euë à femme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 34, 'Et Jefus refpondant, leur dit, Les enfans de ce fiecle-ci prennent &amp; font pris en mariage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 35, 'Mais ceux qui feront faits dignes d’obtenir ce fiecle-là, &amp; la refurrection des morts, ne prendront ni ne feront pris en mariage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 36, 'Car ils ne pourront plus mourir dautant qu’ils feront pareils aux Anges, &amp; feront les fils de Dieu, eftans fils de la refurrection.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 37, 'Or qu’il foit ainfi que les morts reffufcitent, Moyfe mefme l’a montré auprès du buiffon, quand il appelle le Seigneur, le Dieu d’Abraham, &amp; le Dieu d’Ifaac, &amp; le Dieu de Jacob.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 38, 'Or il n’eft point le Dieu des morts, mais des vivans: car tous vivent à lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 39, 'Et quelques-uns d’entre les Scribes refpondans, dirent, Maiftre, tu as bien dit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 40, 'Or ils ne l’ofoyent plus interroger de rien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 41, 'Mais il leur dit, Comment dit-on que le Chrift eft Fils de David?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 42, 'Veu que David mefme dit au livre des Pfeaumes, Le Seigneur a dit à mon Seigneur, Sieds-toi à ma dextre,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 43, 'Jufqu’à ce que j’aye mis tes ennemis pour le marche-pied de tes pieds:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 44, 'David donc l’appelle Seigneur, &amp; comment eft-il fon fils?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 45, 'Et comme tout le peuple efcoutoit, il dit à fes difciples,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 46, 'Donnez-vous garde des Scribes, qui fe promenent volontiers en robbes longues, &amp; qui aiment les falutations és marchez, &amp; les premieres feances és Synagogues, &amp; les premieres places és banquets:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 47, 'Lefquels mangent entierement les maifons des veuves, voire en faifant femblant de prier beaucoup: ils en recevront une plus grande condamnation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 1, 'ET comme il regardoit, il vid les riches qui mettoyent leurs dons au tronc.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 2, 'Il vid auffi une pauvre veuve qui y mettoit deux pites.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 3, 'Dont il dit, Je vous dis pour vrai, que cette pauvre veuve a plus mis que tous les autres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 4, 'Car tous ceux-ci ont mis aux offrandes de Dieu, de ce qui leur abonde: mais celle-ci y mis de fa difette tout le vivre qu’elle avoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 5, 'Et comme quelques-uns difoyent du temple, qu’il eftoit orné de belles pierres &amp; de dons, il dit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 6, 'Eft-ce cela que vous regardez: Les jours viendront efquels il ne fera laiffé pierre fur pierre qui ne foit démolie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 7, 'Alors ils l’interrogerent, difans, Maiftre, quand fera-ce donc que ces chofes adviendront? &amp; quel figne y aura-t’il quand ces chofes devront advenir?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 8, 'Et il dit, Prenez garde que vous ne foyez feduits: car plufieurs viendront en mon Nom, difans, C’eft moi qui fuis le Chrift, &amp; le temps approche: n’allez donc point apres eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 9, 'Et quand vous orrez des guerres &amp; des feditions, ne vous épouvantez point: car il faut que ces chofes adviennent premierement, mais la fin ne fera point incontinent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 10, 'Alors il leur dit, Nation s’élevera contre nation, &amp; royaume contre royaume.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 11, 'Et il y aura de grands tremblemens de terre en tous lieux, &amp; des famines, &amp; des peftilences: &amp; il y aura des épouvantemens, &amp; de grands fignes du ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 12, 'Mais devant toutes ces chofes, ils mettront les mains fur vous, &amp; vous perfecuteront, vous livrant aux Synagogues, aux prifons: &amp; vous tireront devant les Rois &amp; les Gouverneurs, pour l’amour de mon Nom.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 13, 'Et cela vous fervira pour tefmoignage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 14, 'Mettez donc en vos coeurs de ne premediter point comment vous aurez à refpondre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 15, 'Car je vous donnerai une bouche &amp; une fapience, à laquelle tous ceux qui vous feront contraires ne pourront contredire ni refifter.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 16, 'Auffi vous ferez livrez de peres &amp; de meres, &amp; de freres, &amp; de parens, &amp; d’amis: &amp; ils feront mourir d’entre vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 17, 'Et vous ferez haïs de tous pour l’amour de mon Nom.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 18, 'Mais un cheveu de voftre tefte ne fera point perdu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 19, 'Poffedez vos ames par voftre patience.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 20, 'Et quand vous verrez Jerufalem eftre environnée d’armées, fçachez alors que fa defolation eft prochaine.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 21, 'Alors que ceux qui font en Judée, s’enfuyent aux montagnes: &amp; que ceux qui font au milieu d’elle, fe retirent: &amp; que ceux qui font aux champs, n’entrent point en elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 22, 'Car ce feront là les jours de vengeance, afin que toutes les chofes qui font écrites foyent accomplies.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 23, 'Or mal-heur fur celles qui feront enceintes, &amp; fur celles qui allaiteront en ces jours-là: car il y aura une grande extremité fur le païs, &amp; ire en ce peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 24, 'Et ils tomberont au trenchant de l’efpée, &amp; feront menez captifs en toutes nations: &amp; Jerufalem fera foulée par les nations, jufqu’à ce que les temps des nations foyent accomplis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 25, 'Davantage il y aura des fignes au Soleil &amp; en la Lune, &amp; és eftoiles, &amp; détreffe aux nations, tellement qu’on ne fçaura que devenir fur la terre, la mer bruyant &amp; les ondes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 26, 'De forte que les hommes feront comme rendans l’ame de peur, &amp; à caufe de l’attente des chofes qui furviendront au monde univerfel: Car les vertus de cieux feront ébranlées.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 27, 'Et alors on verra le Fils de l’homme venir en une nuée avec puiffance &amp; grande gloire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 28, 'Or quand ces chofes commenceront à advenir: dreffez-vous en haut, &amp; levez vos teftes, dautant que voftre delivrance approche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 29, 'Et il leur dit une fimilitude; Voyez le figuier, &amp; tous les arbres:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 30, 'Quand déja ils pouffent, vous connoiffez de vous-mefmes en regardant, que l’efté eft déja prés.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 31, 'Vous auffi pareillement, quand vous verrez que ces chofes adviendront, fçachez que le regne de Dieu eft prés.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 32, 'En verité je vous dis, que cet âge ne paffera point, jufqu’à ce que toutes ces chofes adviennent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 33, 'Le ciel &amp; la terre pafferont, mais mes paroles ne pafferont point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 34, 'Prenez donc garde à vous mefmes, que d’aventure vos coeurs ne foyent accablez par la gourmandife &amp; l’yvrognerie, &amp; par les foucis de cette vie: &amp; que ce jour-là ne vous furprenne fubitement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 35, 'Car il furprendra comme un laqs tous ceux qui habitent fur le deffus de toute la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 36, 'Veillez donc, prians en tout temps, afin que vous foyez faits dignes d’éviter toutes ces chofes qui doivent advenir, &amp; que vous puiffiez fubfifter devant le Fils de l’homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 37, 'Or il eftoit de jour enfeignant au temple: &amp; fortant il demeuroit la nuit en la montagne qui eft appellée des Oliviers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 38, 'Et dés le poinct du jour, tout le peuple venoit vers lui au temple, pour l’ouïr.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 1, 'OR la fefte des pains fans levain, qu’on appelle Pafque, approchoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 2, 'Et les principaux Sacrificateurs &amp; les Scribes cherchoyent comment ils le pourroyent mettre à mort: car ils craignoyent le peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 3, 'Mais Satan entra en Judas, furnommé Ifcarioth, qui eftoit du nombre des douze.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 4, 'Lequel s’en alla, &amp; parla avec les principaux Sacrificateurs &amp; les capitaines, comment il leur livreroit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 5, 'Dont ils furent joyeux, &amp; accorderent de lui bailler de l’argent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 6, 'Et il en convint avec eux, &amp; cherchoit le temps propre pour le leur livrer fans émeute.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 7, 'Or le jour des pains fans levain arriva, auquel il faloit facrifier l’agneau de Pafque.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 8, 'Et Iefus envoya Pierre &amp; Jean, difant, Allez, &amp; nous appreftez l’agneau de Pafque, afin que nous le mangions.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 9, 'Et ils lui dirent, Où veux-tu que nous l’appreftions?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 10, 'Et il leur dit, Voici, quand vous ferez entrez dans la ville, vous rencontrerez un homme portant une cruche d’eau, fuivez-le en la maifon où il entrera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 11, 'Et dites au maiftre de la maifon, Le Maiftre t’envoye dire, Où eft le logis où je mangerai l’agneau de Pafque avec mes difciples?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 12, 'Et il vous montrera une grande chambre haute, parée: appreftez-là l’agneau de Pafque.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 13, 'Alors ils s’en allerent, &amp; trouverent felon qu’il leur avoit dit, &amp; apprefterent l’agneau de Pafque.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 14, 'Quand donc l’heure fut venuë, il fe mit à table, &amp; les douze Apoftres avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 15, 'Alors il leur dit, J’ai grandement defiré de manger cet agneau de Pafque avec vous, avant que je fouffre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 16, 'Car je vous dis, que je n’en mangerai plus jufques à ce qu’il foit accompli au royaume de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 17, 'Et il prit la coupe, &amp; rendit graces, &amp; dit, Prenez-la, &amp; la diftribuez entre vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 18, 'Car je vous dis, que je ne boirai plus du fruict de la vigne, jufques à ce que le regne de Dieu foit venu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 19, 'Puis prenant le pain, &amp; ayant rendu graces, il le rompit, &amp; le leur bailla, difant, Ceci eft mon corps, lequel eft donné pour vous: faites ceci en commemoration de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 20, 'Semblablement auffi il leur bailla la coupe apres le fouper, difant, Cette coupe eft le nouveau Teftament en mon fang, qui eft refpondu pour vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 21, 'Toutefois voici, la main de celui qui me trahit, eft avec moi à table.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 22, 'Et certes le Fils de l’homme s’en va, felon qu’il eft determiné: toutefois mal-heur à cet homme-là, par qui il eft trahi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 23, 'Alors ils fe prirent à s’entredemander l’un à l’autre, affavoir qui feroit celui d’entr’eux à qui il adviendroit de commettre cela.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 24, 'Il advint auffi une contention entr’eux feroit eftimé le plus grand.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 25, 'Mais il leur dit, Les rois des nations les maiftrifent: &amp; ceux qui ufent d’authorité fur elles, font nommez bienfaiteurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 26, 'Mais il n’en fera point ainfi de vous: ains le plus grand d’entre vous foit comme le moindre: &amp; celui qui gouverne, comme celui qui fert.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 27, 'Car lequel eft le plus grand, celui qui eft à table, ou celui qui fert? n’eft-ce point celui qui eft à table? or je fuis au milieu de vous comme celui qui fert.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 28, 'Or vous eftes ceux qui avez perfeveré avec moi en mes tentations.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 29, 'Partant je vous difpofe le royaume, comme mon Pere me l’a difpofé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 30, 'Afin que vous mangiez &amp; beuviez à ma table en mon royaume, &amp; que vous foyez affis fur des trônes, jugeans les douze lignées d’Ifraël.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 31, 'Auffi le Seigneur dit, Simon, Simon, voici, Satan a demandé inftamment à vous cribler comme le blé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 32, 'Mais j’ai prié pour toi, que ta foi ne defaille point: toi donc, quand quelque jour tu feras converti, confirme tes freres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 33, 'Et il lui dit, Seigneur, je fuis tout preft d’aller avec toi, &amp; en prifon, &amp; à la mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 34, 'Mais Jefus dit, Pierre, je te dis, que le coq ne chantera point aujourd’hui, que premierement tu ne renies par trois de m’avoir connu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 35, 'Puis il leur dit, Quand je vous ai envoyez fans bourfe, fans malette, &amp; fans fouliers, avez-vous eu faute de quelque chofe? Et ils refpondirent, De rien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 36, 'Et il leur dit, Mais maintenant, qui a une bourfe la prenne, &amp; qui a une malette femblablement: &amp; qui n’en a point, vende fa robbe, &amp; achete une efpée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 37, 'Car je vous dis, qu’il faut que ceci auffi qui eft écrit, foit accompli en moi, affavoir, Et il a efté mis au rang des iniques. Car certainement les chofes qui font dites de moi, s’en vont eftre accomplies.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 38, 'Et ils dirent, Seigneur, voici deux efpées. Et il leur dit, C’eft affez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 39, 'Puis il partit, &amp; s’en alla, felon fa couftume, au mont des Oliviers: &amp; fes difciples auffi fuivirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 40, 'Et quand il fut venu au lieu, il leur dit, Priez que vous n’entriez en tentation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 41, 'Alors il fe retira loin d’eux environ un ject de pierre, &amp; s’agenoüillant prioit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 42, 'Difant, Pere, fi tu voulois tranfporter cette coupe arriere de moi: toutefois que ma volonté ne foit point faite, mais la tienne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 43, 'Et un Ange s’apparut du ciel à lui, le fortifiant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 44, 'Et lui eftant en agonie, prioit plus inftamment: &amp; fa fuëur devint comme des grumeaux de fang découlans en terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 45, 'Puis s’eftant levé de fa priere, il vint vers fes difciples, lefquels il trouva dormans de trifteffe:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 46, 'Et il leur dit, Pourquoi dormez-vous? levez-vous, &amp; priez, que vous n’entriez en tentation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 47, 'Et comme il parloit encore, voici une troupe: &amp; celui qui fe nommoit Judas, l’un des douze, vint devant eux, &amp; s’approcha de Jefus pour le baifer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 48, 'Mais Jefus lui dit, Judas, trahis-tu le Fils de l’homme par un baifer?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 49, 'Alors ceux qui eftoyent autour de lui, voyans ce qui alloit advenir, dirent, Seigneur, fraperons-nous de l’efpée?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 50, 'Et un certain d’entr’eux frappa le ferviteur du fouverain Sacrificateur, &amp; lui emporta l’oreille droite.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 51, 'Mais Jefus prenant la parole, dit, Laiffez les faire jufques ici. Et ayant touché l’oreille d’icelui, il le guerit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 52, 'Puis Jefus dit aux principaux Sacrificateurs, &amp; aux Capitaines du temple, &amp; aux Anciens qui eftoyent venus contre lui, Eftes-vous fortis comme apres un brigand avec des efpées &amp; des baftons?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 53, 'Bien que j’aye journellement au temple avec vous, vous n’avez point eftendu les mains fur moi: mais c’eft ici voftre heure &amp; la puiffance des tenebres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 54, 'Alors il l’empoignerent, &amp; l’emmenerent, &amp; le firent entrer en la maifon du fouverain Sacrificateur. Or Pierre fuivoit de loin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 55, 'Et ayans allumé du feu au milieu de la court, &amp; s’eftans affis enfemble, Pierre auffi s’affit au milieu d’eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 56, 'Et une fervente le voyant affis aupres du feu, &amp; ayant l’oeil fiché fur lui, dit, Celui-ci auffi eftoit avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 57, 'Mais il le renia, difant, Femme, je ne le connois point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 58, 'Et un peu apres un autre le voyant, dit, Tu es auffi de ceux-là. Mais Pierre dit, O homme! je n’en fuis point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 59, 'Et environ l’efpace d’une heure apres, quelque autre affirmoit, Pour vrai celui-ci auffi eftoit avec lui: car il eft Galiléen.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 60, 'Et Pierre dit, O homme! je ne fçai ce que tu dis. Et à l’inftant, comme il parloit encore, le coq chanta.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 61, 'Et le Seigneur fe retournant, regarda Pierre: &amp; Pierre fe ramenteut la parole du Seigneur, comme il avoit dit, Devant que le coq chante, tu me renieras par trois fois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 62, 'Alors Pierre eftant forti dehors, pleura amerement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 63, 'Or ceux qui tenoyent Jefus, fe mocquoyent de lui, &amp; le frapoyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 64, 'Et l’ayans bandé, lui donnoyent des coups fur le vifage: &amp; l’interrogeoient, difans, Prophetize, qui eft celui t’a frappé?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 65, 'Et difoyent plufieurs autres chofes contre lui, en l’outrageant de paroles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 66, 'Quand le jour fut venu, les Anciens du peuple, &amp; les principaux Sacrificateurs, &amp; les Scribes s’affemblerent, &amp; l’emmenerent dans le confeil.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 67, 'Et ils lui dirent, Si tu es le Chrift, di le nous, Et il leur refpondit, Si je vous le dis, vous ne le croirez point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 68, 'Qua fi auffi je vous interroge, vous ne me refpondrez point, &amp; ne laifferez point aller.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 69, 'Deformais le Fils de l’homme fera affis à la dextre de la vertu de Dieu');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 70, 'Alors ils lui dit, Vous le dites vous-mefmes, que le fuis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 71, 'Alors ils dirent, Qu’avons-nous befoin encore de tefmoignage? car nous-mefmes l’avons ouï de fa bouche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 1, 'PUIS apres il fe leverent tous, &amp; le menerent à Pilate.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 2, 'Et ils prirent à l’accufer, difans, Nous avons trouvé celui-ci fubvertiffant la nation, &amp; fe difant eftre le Chrift, le Roi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 3, 'Alors Pilate l’interrogea, difant, Es-tu le Roi des Juifs? Lui refpondant dit, Tu le dis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 4, 'Et Pilate dit aux principaux Sacrificateurs &amp; aux troupes, Je ne trouve aucun crime en cet homme-ici.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 5, 'Mais ils s’efforçoyent tant plus, difans, Il efmeut le peuple, enfeignant par toute la Judée, ayant commencé depuis la Galilée jufques ici.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 6, 'Quand Pilate ouït parler de Galilée, il demanda, fi le perfonnage eftoit Galilée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 7, 'Et ayant entendu qu’il eftoit de la jurifdiction d’Herode, il le renvoya vers Herode: lequel auffi en ces jours-là eftoit à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 8, 'Et quand Herode vid Jefus, il en fut fort joyeux: car il y avoit long-temps qu’il defiroit de le voir, à caufe qu’il oyoit dire plufieurs chofes de lui: &amp; il efperoit qu’il lui verroit faire quelque figne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 9, 'Or il l’interrogea par divers propos: mais il ne lui refpondoit rien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 10, 'Et les principaux Sacrificateurs &amp; les Scribes comparurent là, l’accufans avec grande vehemence.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 11, 'Mais Herode avec fes gens, l’ayant mefprifé, &amp; s’eftant mocqué de lui, apres qu’il l’eut reveftu d’un veftement blanc, le renvoya à Pilate.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 12, 'Et en ce mefme jour Pilate &amp; Herode devinrent amis entr’eux: car auparavant ils eftoyent en inimitié entr’eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 13, 'Alors Pilate ayant appellé les principaux Sacrificateurs, &amp; les Gouverneurs, &amp; le peuple, leur dit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 14, 'Vous m’avez prefenté cet homme comme fubvertiffant le peuple: &amp; voici, l’en ayant fait refpondre devant vous, je n’ai trouvé en cet homme aucun crime de ceux defquels vous l’accufez:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 15, 'Ni mefme Herode: car je vous ai renvoyez vers lui, &amp; voici, rien ne lui a efté fait qui emporte qu’il foit digne de mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 16, 'Quand donc je l’aurai chaftié, je le relafcherai.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 17, 'Or il faloit qu’il leur relafchaft quelqu’un à la fefte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 18, 'Dont toutes les troupes s’efcrierent enfemble, difant, Ofte nous celui-ci, &amp; nous relafche Barrabas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 19, 'Lequel avoit efté mis en prifon, pour quelque fedition faite en la ville avec meurtre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 20, 'Derechef donc Pilate parla à eux, voulant relafcher Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 21, 'Mais ils s’efcrioyent, difans, Crucifie, crucifie-le.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 22, 'Et pour la troifiéme fois, il leur dit, Mais quel mal à fait celui-ci? je ne trouve en lui aucun crime digne de mort: l’ayant donc fait foüetter, je le relafcherai.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 23, 'Mais ils rechargeoyent à grands cris, demandans qu’il fuft crucifié: &amp; leurs cris &amp; ceux des principaux Sacrificateurs fe renforçoyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 24, 'Alors Pilate prononça là-deffus, que ce qu’ils demandoyent fuft fait.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 25, 'Et il leur relafcha celui qui pour fedition &amp; pour meurtre avoit efté mis en prifon, &amp; lequel ils demandoyent: &amp; abandonna Jefus à leur volonté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 26, 'Et comme ils le menoyent, ils prirent un certain Simon Cyrenien, qui venoit des champs, &amp; lui chargerent la croix pour la porter apres Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 27, 'Et une grande multitude de peuple &amp; de femmes le fuivoyent, lefquelles fe frappoyent la poitrine, &amp; le pleuroyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 28, 'Mais Jefus fe retournant vers elles, dit, Filles de Jerufalem, ne pleurez point fur moi, mais pleurez fur vous-mefmes, &amp; fur vos enfants.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 29, 'Car voici, les jours viendront efquels on dira, Bien-heureufes font les fteriles, &amp; les ventres qui n’ont point enfanté, &amp; les mammelles qui n’ont allaitté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 30, 'Alors ils fe prendront à dire aux montagnes, Tombez fur nous: &amp; aux coftaux, Couvrez-nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 31, 'Car s’ils font ces chofes au bois verd, que fera-t’il fait au bois fec?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 32, 'Deux autres auffi qui eftoyent mal-faiteurs, furent menez pour les faire mourir avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 33, 'Eftans donc venus au lieu qui eft appellé le Teft, ils le crucifierent là, &amp; les mal-faiteurs: l’un à la droite, &amp; l’autre à la gauche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 34, 'Mais Jefus difoit, Pere, pardonne leur: car ils ne fçavent ce qu’ils font. Puis faifant le partage de fes habillemens, ils jetterent le fort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 35, 'Et le peuple fe tenoit là regardant. Pareillement les Gouverneurs fe mocquoyent de lui avec eux, difans, Il a fauvé les autres, qu’il fe fauve foi-mefme, s’il eft le Chrift, l’éleu de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 36, 'Les foldats auffi fe mocquoyent de lui, s’approchans, &amp; lui prefentans du vinaigre,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 37, 'Et difans, Si tu es le roi des Juifs, fauve-toi toi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 38, 'Or il y avoit un efcriteau fur lui en lettres Grecques, &amp; Romaines, &amp; Hebraïques, en ces mots, Celui-ci est le Roi des Juifs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 39, 'Et un des mal-faiteurs qui eftoyent pendus, l’outrageoit, difant, Si tu es le Chrift, fauve-toi toi-mefme, &amp; nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 40, 'Mais l’autre refpondant le tançoit, difant, Au moins ne crains-tu point Dieu, veu que tu es en la mefme condamnation?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 41, 'Et quant à nous, nous y fommes juftement: car nous recevons des chofes dignes de nos forfaits: mais celui-ci n’a rien fait qui ne fe deuft faire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 42, 'Puis il difoit à Jefus, Seigneur, fouvien-toi de moi quand tu viendras en ton regne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 43, 'Alors Jefus lui dit, En verité je te dis, qu’aujourd’hui tu feras avec moi en Paradis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 44, 'Or il eftoit environ fix heures, &amp; il fe fit des tenebres par tout le païs jufques à neuf heures.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 45, 'Et le foleil fut obfcurci, &amp; le voile du temple fe fendit par le milieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 46, 'Alors Jefus criant à haute voix dit, Pere, je remets mon efprit entre tes mains. Et ayant dit cela, il rendit l’efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 47, 'Or le Centenier voyant ce qui eftoit advenu, glorifia Dieu, difant, Pour vrai cet homme-ci eftoit jufte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 48, 'Et toutes les troupes qui s’eftoyent affemblées à ce fpectacle, voyant les chofes qui eftoyent advenuës, s’en retournoyent frapans leurs poitrines.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 49, 'Or tous ceux de fa connoiffance fe tenoyent loin, &amp; les femmes qui enfemble l’avoyent fuivi de Galilée, regardans ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 50, 'Et voici une perfonnage appellé Jofeph, qui eftoit confeiller, homme de bien &amp; jufte:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 51, 'Lequel n’avoit point confenti à leur confenti à leur confeil, ni à leur acte, &amp; eftoit d’Arimathée ville des Juifs, qui auffi attendoit le regne de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 52, 'Lui eftant venu vers Pilate, demanda le corps de Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 53, 'Et l’ayant defcendu de la croix, il l’enveloppa en un linceul, &amp; le mit en un fepulcre taillé dans le roc, auquel perfonne n’avoit encore efté mis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 54, 'Or c’eftoit le jour de la preparation, &amp; le jour du Sabbat commençoit à venir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 55, 'Et les femmes auffi qui eftoyent venuës avec lui de Galilée, ayant fuivi Iofeph, regarderent le fepulcre, &amp; comment le corps d’icelui feroit mis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 56, 'Puis eftant retournées, elles preparerent des fenteurs &amp; des onguens: &amp; le jour du Sabbat elles fe repoferent felon le commandement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 1, 'MAIS le premier jour de la femaine, eftant encore fort matin, elles vinrent au fepulcre, apportans les fenteurs qu’elles avoyent preparées: &amp; quelques autres qui eftoyent avec elles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 2, 'Or elles trouverent la pierre roulée de devant le fepulcre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 3, 'Et eftant entrées, elles ne trouverent point le corps du Seigneur Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 4, 'Et il advint que comme elles eftoyent en grande perplexité touchant cela, voici, deux perfonnages furvinrent vers elles en veftemens reluifans comme un efclair.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 5, 'Et comme elles eftoyent tout efpouvantées, &amp; baiffoyent la face en terre, ils leur dirent, Pourquoi cherchez-vous entre les morts celui qui eft vivant?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 6, 'Il n’eft point ici, mais il eft reffufcité. Qu’il vous fouvienne comme il parla à vous, quand il eftoit encore en Galilée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 7, 'Difant, qu’il faloit que le Fils de l’homme fuft livré entre les mains des mal-vivans, &amp; qu’il fuft crucifié: &amp; qu’il reffufcitaft au troifiéme jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 8, 'Elles eurent fouvenance des paroles d’icelui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 9, 'Et eftans retournées de devers le fepulcre, elles annoncerent toutes ces chofes aux onze difciples, &amp; à tous les autres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 10, 'C’eftoit Marie Magdelaine, &amp; Jeanne, &amp; Marie mere de Jaques, &amp; les autres qui eftoyent avec elles, lefquelles dirent ces chofes aux Apoftres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 11, 'Mais les paroles d’icelles leur femblerent comme des refveries, &amp; ils ne les creurent point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 12, 'Toutefois Pierre s’eftant levé, courut au fepulcre: &amp; s’eftant courbé pour regarder, vid feulement les linceuls mis à part: puis il partit, s’efmerveillant en foi-mefme de ce qui eftoit advenu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 13, 'Or voici, deux d’entr’eux eftoyent en chemin en ce mefme jour, pour aller en une bourgade nommée Emmaüs, laquelle eftoit loin de Jerufalem environ foixante ftades:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 14, 'Lefquels devifoyent entr’eux de toutes ces chofes qui eftoyent advenuës.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 15, 'Et il advint comme ils en devifoyent &amp; en conferoyent entr’eux, que Jefus auffi lui-mefme s’eftant approché fe mit à cheminer avec eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 16, 'Mais leurs yeux eftoyent retenus, afin qu’ils ne le peuffent reconnoiftre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 17, 'Et il leur dit, Quels font ces difcours que vous tenez entre vous en cheminant &amp; pourquoi eftes-vous tout triftes?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 18, 'Alors l’un d’eux, qui avoit nom Cleopas, refpondit, &amp; lui dit, Es-tu feul eftranger à Jerufalem, qui ne fçaches point les chofes qui y font advenuës ces jours-ici?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 19, 'Et il leur dit, Quelles? Ils refpondirent, Touchant Jefus le Nazarien, qui à efté homme Prophete, puiffant en oeuvres &amp; en paroles devant Dieu &amp; tout le peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 20, 'Et comment les principaux Sacrificateurs, &amp; nos Gouverneurs l’ont livré en condamnation de mort, &amp; l’ont crucifié.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 21, 'Or nous efperions que ce fuft celui qui devoit delivrer Ifraël, &amp; encore avec tout cela, c’eft aujourd’hui le troifiéme jour que ces chofes-là font advenuës.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 22, 'Mais auffi quelques femmes des noftres nous ont grandement eftonnez, qui ont efté de grand matin au fepulcre:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 23, 'Et n’ayant point trouvé fon corps, font venuës, difant, que mefmes elles avoyent veu une vifion d’Anges, lefquels difoyent qu’il eft vivant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 24, 'Dont aucuns des noftres font allez au fepulcre, &amp; ont trouvé ainfi que les femmes avoyent dit: mais quant à lui ils ne l’on point veu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 25, 'Alors il leur dit, O gens defpourveus de fens, &amp; tardifs de coeur à croire à toutes les chofes que les Prophetes ont prononcées!');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 26, 'Ne faloit-t’il pas que le Chrift fouffrift ces chofes, &amp; qu’ainfi il entraft en fa gloire?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 27, 'Puis commençant par Moyfe, &amp; fuivant par tous les Prophetes, il leur declaroit en toutes les Ecritures les chofes qui eftoyent de lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 28, 'Et ils s’approcherent de la bourgade où ils alloyent: mais lui faifoit femblant d’aller plus loin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 29, 'Mais ils le forcerent, difans, Demeure avec nous: car le foir commence à venir, &amp; le jour eft déja decliné. Il entra donc pour demeurer avec eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 30, 'Et il advint que comme il eftoit à table avec eux, il prit le pain, &amp; rendit graces, puis l’ayant rompu, le leur diftribua.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 31, 'Alors leurs yeux furent ouverts, tellement qu’ils le reconnurent: mais il fe difparut de devant eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 32, 'Alors ils dirent entr’eux, Noftre coeur ne brufloit-il pas dedans nous, quand il parloit à nous par le chemin, &amp; nous declaroit les Ecritures?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 33, 'Et fe levans au mefme inftant, ils retournerent à Jerufalem, où ils trouverent les onze affemblez, &amp; ceux qui eftoyent avec eux:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 34, 'Qui difoyent, Le Seigneur eft vrayement reffufcité, &amp; s’eft apparu à Simon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 35, 'Dont ceux-ci auffi reciterent les chofes qui leur estoyent advenuës en chemin, &amp; comme il avoit efté reconnu d’eux en rompant le pain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 36, 'Et comme ils tenoyent ces propos, Jefus lui-mefme fe prefenta au milieu d’eux, &amp; leur dit, Paix foit avec vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 37, 'Mais eux tout troublez &amp; épouvantez, penfoyent voir un efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 38, 'Dont il leur dit, Pourquoi eftes-vous troublez, &amp; pourquoi monte-t’il des penfées en vos coeur?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 39, 'Voyez mes mains &amp; mes pieds, car je fuis moi-mefme: taftez-moi, &amp; voyez: car un efprit n’a ni chair ni os, comme vous voyez que j’ai.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 40, 'Quand il eut dit ces chofes, il leur montra fes mains, &amp; fes pieds.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 41, 'Mais comme encore de joye ils ne croioyent point, &amp; s’eftonnoyent, il leur dit, Avez-vous ici quelque chofe à manger?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 42, 'Et ils lui prefenterent une piece de poiffon rofti, &amp; d’un rayon de miel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 43, 'Et l’ayant pris, il en mangea devant eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 44, 'Puis il leur dit, Ce font ici les difcours que je vous tenois, quand j’eftois encore avec vous, qu’il faloit que toutes les chofes qui font écrites de moi en la Loi de Moyfe, &amp; és Prophetes, &amp; és Pfeaumes, fuffent accomplies.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 45, 'Alors il leur ouvrit l’entendement pour entendre les Ecritures:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 46, 'Et leur dit, Il eft ainfi écrit, &amp; ainfi faloit-il que le Chrift fouffrift, &amp; reffufcitaft des morts au troifiéme jour:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 47, 'Et qu’on prefchaft en fon Nom la repentance &amp; la remiffion des pechez, par toutes les Nations, en commençant depuis Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 48, 'Or vous eftes tefmoins de ces chofes, &amp; voici, je m’en vais envoyer la promeffe de mon Pere fur vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 49, 'Vous donc demeurez en la ville de Jerufalem, jufques à ce que vous foyez reveftus de la vertu d’en haut.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 50, 'Apres il les mena dehors jufques à Bethanie, puis élevant fes mains en haut, il les benit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 51, 'Et il advint qu’en les beniffant, il fe retira d’avec eux, &amp; fut élevé au ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 52, 'Et eux l’ayant adoré, s’en retournerent à Jerufalem avec grande joye.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 53, 'Et eftoyent toûjours au temple, loüans &amp; beniffans Dieu. Amen. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 1, 'AU commencement eftoit la Parole, &amp; la Parole eftoit avec Dieu : &amp; cette Parole eftoit Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 2, 'Elle eftoit au commencement avec Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 3, 'Toutes chofes ont efté faites par elle : &amp; fans elle rien de ce qui a efté fait, n\'a efté fait.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 4, 'En elle eftoit la vie, &amp; la vie eftoit la lumière des hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 5, 'Et la lumière luit és ténèbres, &amp; les ténèbres ne l\'ont point comprife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 6, 'Il y eut un homme envoyé de Dieu, qui avoit nom Jean.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 7, 'Il vint pour porter tefmoignage, à ce qu\'il rendift tefmoignage de la lumiere, afin que tous creuffent par lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 8, 'Il n\'eftoit pas cette Lumiere-là, mais il eftoit envoyé pour tefmoigner de la lumiere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 9, 'Elle eftoit la Lumiere veritable, qui illumine tout homme venant au monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 10, 'Elle eftoit au monde, &amp; le monde a efté fait par elle : mais le monde ne l\'a point connuë.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 11, 'Il eft venu chez foi : &amp; les fiens ne l\'ont point receu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 12, 'Mais à tous ceux qui l\'ont receu, il leur a donné ce droit d\'eftre faits enfants de Dieu, affavoir à ceux qui croyent en fon Nom :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 13, 'Lefquels ne font point nez de fang, ni de la volonté de la chair, ni de la volonté de l\'homme : mais ils font nez de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 14, 'Et cette Parole a efté faite chair : &amp; a habité entre nous (&amp; nous avons contemplé fa gloire, voire une gloire comme de l\'Unique iffu du Pere) pleine de grace &amp; de verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 15, 'Jean a rendu tefmoignage de lui, &amp; a crié, difant, Celui-ci eft celui duquel je difant, Celui qui vient apres moi eft preferé à moi, car il eftoit premier que moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 16, 'Et de fa pleinitude nous avons tous receu, &amp; grace pour grace.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 17, 'Car la Loi efté donnée par Moyfe : la grace &amp; la verité eft advenuë par Jefus-Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 18, 'Nul ne vid jamais Dieu : le Fils unique qui eft au sein du Pere, lui-mefme l\'a declaré.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 19, 'C\'eft donc ici le témoignage de Jean, lors que les Juifs envoyerent de Jerusalem des Sacrificateurs &amp; des Levites, pour l\'interroger, difans, Toi, qui es-tu ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 20, 'Et il l\'advoüa, &amp; ne le nia point : voire il l\'advoüa, difant, Ce n\'eft pas moi qui fuis le Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 21, 'Alors il l\'interrogèrent, Qui es-tu donc ? Es-tu Elie ? Et il dit, Je ne le fuis point. Es-tu le Prophète ? Et il refpondit, Non.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 22, 'Ils lui dirent donc, Qui es-tu ? Afin que nous donnions refponfe à ceux qui nous ont envoyez ? Que dis-tu de toi-mefme ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 23, 'Il dit, Je fuis la voix de celui qui crie au defert, Applaniffez le chemin du Seigneur, ainfi qu\'a Efaïe le Prophete.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 24, 'Or ceux qui avoyent efté envoyez vers lui eftoyent d\'entre les Pharifiens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 25, 'Et ils l\'interrogerent, &amp; lui dirent, Pourquoi donc baptizes-tu, fi tu n\'es point le Chrift, ni Elie, ni le Prophete ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 26, 'Jean leur refpondit, difant, Je baptize d\'eau, quant à moi : mais il y en a un au milieu de vous que vous ne connoiffez point :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 27, 'C\'eft celui qui vient apres moi, qui eft preferé à moi, duquel je ne fuis pas digne de délier la courroye du foulier.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 28, 'Ces chofes advinrent à Bethabara, outre le Jordain, là où Jean baptizoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 29, 'Le lendemain Jean vid Jefus venir à lui, &amp; dit, Voici l\'Agneau de Dieu, qui ofte le peché du monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 30, 'C\'eft celui-ci duquel je difois, Apres moi vient un perfonnage qui eft preferé à moi : car il eftoit premier que moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 31, 'Et de moi, je ne le connoiffois point : mais afin qu\'il foit manifefté à Ifraël.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 32, 'Voire Jean en rendit tefmoignage, difant, J\'ai veu l\'Efprit defcendant du ciel comme une colombe, qui auffi eft demeuré fur lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 33, 'Et de moi, je ne le connoiffois point mais celui qui m\'a envoyé baptizer d\'eau, m\'avoit dit, Celui fur lequel tu verras l\'Efprit defcendre, &amp; demeurer fur lui, c\'eft celui qui baptize du S. Efprit');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 34, 'Et je l\'ai veu, &amp; j\'ai rendu tefmoignage, Que c\'est lui qui eft le Fils de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 35, 'Le lendemain derechef Jean s\'arrefta, &amp; deux de fes difciples :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 36, 'Et regardant Jefus qui cheminoit, il dit, Voila l\'Agneau de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 37, 'Et les deux difciples l\'ouïrent tenant ce difcours, dont ils fuivirent Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 38, 'Alors Jefus fe retourna, &amp; voyant qu\'ils le fuivoyent, leur dit, Que cherchez-vous ? Ils refpondirent, Rabbi, (qui vaut autant à dire que Maiftre) où demeures-tu ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 39, 'Il leur dit, Venez, &amp; le voyez. Ils y allerent, &amp; virent où il demeuroit : &amp; demeurent avec lui ce jour-là : car il eftoit environ dix heures.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 40, 'Or André, frere de Simon Pierre, eftoit l\'un des deux qui en avoyent fuivi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 41, 'Celui-ci trouva le premier Simon fon frere, &amp; lui dit, Nous avons trouvé le Meffie (qui vaut autant à dire que le Chrift.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 42, 'Et il le mena vers Jefus. Jefus ayant jetté la veuë fur lui, dit, Tu es Simon le fils de Jona, tu feras appellé Cephas (qui vaut autant à dire que Pierre.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 43, 'Le lendemain Jefus voulut aller en Galilée &amp; trouva Philippe, auquel il dit, Sui moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 44, 'Or Philippe eftoit de Bethfaïda, la ville d\'André &amp; de Pierre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 45, 'Philippe trouva Nathanaël, &amp; lui dit, Nous avons trouvé Jefus qui eft de Nazareth, fils de Jofeph, qui eft celui duquel Moyfe a efcrit en la Loi &amp; les Prophetes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 46, 'Et Nathanaël lui dit, Peut-il venir quelque chofe de bon de Nazareth ? Philippe lui dit, Viens &amp; voi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 47, 'Jefus apperceut Nathanaël venir vers lui, &amp; dit de lui, Voici vrayement un Ifraëlite auquel il n\'y a point de fraude.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 48, 'Nathanaël lui dit, D\'où me connois-tu ? Jefus refpondit, &amp; lui dit, Avant que Philippe t\'euft appellé quand tu eftois fous le figuier, je te voyois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 49, 'Nathanaël refpondit, &amp; lui dit, Maiftre, tu es le Fils de Dieu : tu es le Roi d\'Ifraël.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 50, 'Jefus refpondit, &amp; lui dit, Parce que je t\'ai dit que je te voyois fous le figuier, tu crois : tu verras de plus grandes chofes que ceci.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 51, 'Il lui dit auffi, En verité, en verité je vous dis, Deformais vous verrez le ciel ouvert, &amp; les Anges de Dieu montans &amp; defcendans fur le Fils de l\'homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 1, 'OR trois jours apres on faifoit des nopces à Cana de Galilée : &amp; la mere de Jefus eftoit là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 2, 'Et Jefus fut auffi convié aux nopces &amp; fes difciples.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 3, 'Or le vin eftant failli, la mere de Jefus lui dit, Ils n\'ont point de vin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 4, 'Mais Jefus lui refpondit, Qu\'y a-t\'il entre moi &amp; toi, femme ? Mon heure n\'eft point encore venuë.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 5, 'Sa mere dit aux ferviteurs, Faites tout ce qu\'il vous dira.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 6, 'Or il y avoit fix cruches de pierre, mifes felon l\'ufage de la Purification des Juifs, lefquelles contenoyent chacune deux à trois mefures.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 7, 'Et Jefus leur dit, Empliffez ces cruches d\'eau. Et ils les emplirent jufques au haut.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 8, 'Verfez-en maintenant, &amp; en portez au maiftre d\'hoftel. Et ils lui en porterent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 9, 'Quand le maiftre d\'hoftel eut goufté l\'eau qui avoit efté convertie en vin, (Or il ne fçavoit d\'où cela venoit, mais les ferviteurs qui avoyent puifé l\'eau le fçavoyent bien) il appella le marié,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 10, 'Et lui dit, Tout homme fert le bon vin le premier, puis le moindre apres qu\'on a beu plus largement : mais toi, tu as gardé le bon vin jusques à maintenant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 11, 'Jefus fit ce commencement de fignes à Cana ville de Galilée, &amp; manifefta fa gloire : &amp; fes difciples creurent en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 12, 'Apres cela il defcendit à Capernaum, lui &amp; fa mere &amp; fes freres, &amp; fes difciples : &amp; ils demeurerent là un bien peu de jours.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 13, 'Car la folemnité de la Pafque des Juifs eftoit prochaine. Jefus donc monta à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 14, 'Et trouva au temple des gens qui vendoyent des bœufs, &amp; des brebis, &amp; des pigeons ; &amp; les changeurs qui y eftoyent affis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 15, 'Et ayant fait un foüet de cordelettes, il les jetta tous hors du temple, &amp; les brebis, &amp; les bœufs : &amp; refpandit la monnoye des changeurs, &amp; renverfa les tables :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 16, 'Et dit à ceux qui vendoyent des pigeons, Oftez ces chofes d\'ici, &amp; ne faites point de la maifon de mon Pere un lieu de marché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 17, 'Alors fes difciples fe fouvinrent qu\'il eftoit efcrit, Le zele de ta maifon m\'a rongé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 18, 'Les Juifs donc prenans la parole, lui dirent, Quel figne nous montres-tu, que tu entreprens de faire de telles chofes ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 19, 'Jefus refpondit, &amp; leur dit, Abbattez ce temple-ci, &amp; en trois jours je le releverai.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 20, 'Les Juifs dirent, On a efté quarante-fix ans à baftir ce temple, &amp; tu le releveras en trois jours ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 21, 'Mais lui parloit du temple de fon corps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 22, 'C\'eft pourquoi quand il fut reffufcité des morts, fes difciples fe fouvinrent qu\'il leur avoit dit cela, &amp; creurent à l\'Ecriture, &amp; à la parole que Jefus avoit dite.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 23, 'Et comme il eftoit à Jerufalem à Pafque au jour de la fefte, plufieurs creurent en fon Nom, contemplant les fignes qu\'il faifoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 24, 'Mais Jefus ne fe fioit point à eux, parce qu\'il les connoiffoit tous :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 25, 'Et qu\'il ne lui eftoit point de befoin qu\'aucun lui rendift tefmoignage de l\'homme : car lui-mefme fçavoit ce qui eftoit en l\'homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 1, 'OR il y avoit un homme d\'entre les Pharifiens, nommé Nicodeme, l\'un des principaux d\'entre les Juifs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 2, 'Celui-ci vint de nuict à Jefus, &amp; lui dit, Maiftre, nous fçavons que tu es un Docteur, venu de Dieu : Car nul ne peut faire ces fignes que tu fais, fi Dieu n\'eft avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 3, 'Jefus refpondit, &amp; lui dit, En verité, en verité je te dis, finon que quelqu\'un foit né derechef, il ne peut voir le royaume de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 4, 'Nicodeme lui dit, Comment l\'homme peut-il derechef entrer au ventre de sa mere, &amp; naiftre ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 5, 'Jefus refpondit, En verité, en verité je te dis, finon que quelqu\'un foit né d\'eau &amp; d\'Efprit, il ne peut entrer au royaume de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 6, 'Ce qui eft né de la chair, eft chair : &amp; ce qui eft né de l\'Efprit, eft efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 7, 'Ne t\'efmerveille point que je t\'ai dit, Il vous faut eftre nés derechef.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 8, 'Le vent fouffle où il veut, &amp; tu en entens le fon : mais tu ne fçais d\'où il vient, ni où il va ? Ainfi en prend-il de tout homme qui eft né de l\'Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 9, 'Nicodeme refpondit, &amp; lui dit, Comment fe peuvent faire ces chofes ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 10, 'Jefus refpondit, &amp; lui dit, Tu es Docteur d\'Ifraël, &amp; tu ne connois point ces chofes ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 11, 'En verité, en verité je te dis, Que ce que nous fçavons, nous le difons, &amp; ce que nous avons veu, nous le tefmoignons, mais vous ne recevez point noftre tefmoignage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 12, 'Si je vous ai dit les chofes terriennes, &amp; vous ne les croyez point : comment croirez-vous fi je vous dis les chofes celeftes ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 13, 'Car perfonne n\'eft monté au ciel, finon celui qui eft defcendu du ciel, affavoir le Fils de l\'homme qui eft au ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 14, 'Or comme Moyfe éleva le ferpent au defert, ainfi faut-il que le Fils de l\'homme foit élevé :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 15, 'Afin que quiconque croit en lui, ne periffe point, mais qu\'il ait la vie eternelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 16, 'Car Dieu a tellement aimé le monde, qu\'il a donné fon Fils unique, afin que quiconque croit en lui ne periffe point, mais qu\'il ait la vie eternelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 17, 'Car Dieu n\'a point envoyé fon Fils au monde, pour condamner le monde : mais afin que le monde foit fauvé par lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 18, 'Qui croit en lui, ne fera point condamné : mais qui ne croit point eft déja condamné : car il n\'a point creu au Nom du Fils unique de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 19, 'Or voici la condamnation, c\'eft que la Lumiere eft venuë au monde, &amp; les hommes ont mieux aimé les tenebres que la Lumiere : parce que leurs œuvres font mefchantes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 20, 'Car quiconque s\'addonne à chofes mefchantes, hait la lumiere, &amp; ne vient point à la Lumiere, de peur que fes œuvres ne foyent redargüées.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 21, 'Mais celui qui s\'addonne à la verité, vient à la Lumiere, afin que fes œuvres foyent manifeftées, dautant qu\'elles font faites felon Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 22, 'Apres ces chofes, Jefus avec fes difciples vint au territoire de Judée : &amp; demeuroit là avec eux, &amp; baptizoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 23, 'Or Jean baptizoit auffi à Enon, prés de Salim, parce qu\'il y avoit là beaucoup d\'eaux : &amp; on venoit là, on y eftoit baptizé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 24, 'Car Jean n\'avoit point efté mis en prifon');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 25, 'Or il y eut une queftion meuë des difciples de Jean avec les Juifs, touchant la purification.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 26, 'Donc il vinrent à Jean ; &amp; lui dirent, Maiftre, celui qui eftoit avec toi outre le Jourdain, auquel tu as rendu témoignage : voila, il baptize : &amp; tous viennent à lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 27, 'Jean refpondit, &amp; dit, L\'homme ne peut recevoir chofe aucune, finon qu\'il lui foit donné du ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 28, 'Vous-mefmes m\'eftes tefmoins, comme j\'ai dit, Ce n\'eft pas moi, qui fuis le Chrift, mais je fuis envoyé pour aller devant lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 29, 'Celui qui a la mariée, eft le marié : mais l\'ami du marié qui affifte, &amp; qui l\'oit, eft tout réjouï pour la voix du marié : c\'eft pourquoi cette mienne joye eft accomplie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 30, 'Il faut qu\'il croiffe, mais que je fois amoindri.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 31, 'Celui qui eft venu d\'en haut, eft par deffus tous : celui qui eft venu de la terre, eft de la terre, &amp; parle comme venu de la terre : celui qui eft venu du ciel, eft par deffus tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 32, 'Et ce qu\'il a veu &amp; ouï, il le témoigne : mais nul ne reçoit fon témoignage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 33, 'Celui qui a receu fon témoignage, a feellé que Dieu eft veritable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 34, 'Car celui que Dieu a envoyé, annonce les paroles de Dieu : car Dieu ne lui donne point l\'efprit par mefure.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 35, 'Le Pere aime le Fils, &amp; lui a donné toutes chofes en main.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 36, 'Qui croit au Fils, a la vie eternelle : mais qui defobeït au Fils, ne verra point la vie : ains l\'ire de Dieu demeure fur lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 1, 'QUAND donc le Seigneur eut connu que les Pharifiens avoyent ouï dire qu\'il faifoit &amp; baptizoit plus de difciples que Jean.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 2, '(Toutefois Jefus ne baptizoit point lui-mefme, mais fes difciples.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 3, 'Il laiffa la Judée, &amp; s\'en alla derechef en Galilée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 4, 'Or il faloit qu\'il traverfaft par la Samarie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 5, 'Il vint donc en une ville de Samarie, nommée Sichar, qui eft prés de la poffeffion que Jacob donna à Jofeph fon fils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 6, 'Or il y avoit là une fontaine de Jacob. Jefus donc eftant laffé du chemin, fe tenoit là ainfi affis fur la fontaine : c\'eftoit environ les fix heures.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 7, 'Une femme Samaritaine vint pour puifer de l\'eau, &amp; Jefus lui dit, Donne moi à boire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 8, 'Car fes difciples s\'en eftoyent allez à la ville pour acheter des vivres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 9, 'Cette femme Samaritaine donc lui dit, Comment toi qui es Juif me demandes-tu à boire, à moi qui fuis une femme Samaritaine ? Car les Juifs n\'ont point de communication avec les Samaritains.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 10, 'Jefus refpondit, &amp; lui dit, Si tu fçavois le don de Dieu, &amp; qui eft celui qui te dit, Donne moi à boire, tu lui en euffes demandé toi-mefme, &amp; il t\'euft donné de l\'eau vive.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 11, 'La femme lui dit, Seigneur, tu n\'as pas avec quoi puifer, &amp; le puits eft profond : d\'où as-tu donc cette eau vive ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 12, 'Es-tu plus grand que Jacob noftre pere, qui nous a donné le puits, &amp; lui-mefme en a beu, &amp; fes enfans, &amp; fon beftail ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 13, 'Jefus refpondit, &amp; lui dit, Quiconque boit de cette eau-ci, aura encore foif.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 14, 'Mais qui boira de l\'eau que je lui donnerai, n\'aura plus jamais foif : mais l\'eau que je lui donnerai fera faite en lui une fontaine d\'eau faillante en vie eternelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 15, 'La femme lui dit, Seigneur, donne moi de cette eau, afin que je n\'aye plus de foif, &amp; que je ne vienne plus ici pour en puifer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 16, 'Jefus lui dit, Va t\'en &amp; appelle ton mari, &amp; t\'en vien ici.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 17, 'La femme refpondit &amp; lui dit, Je n\'ai point de mari. Jefus lui dit, Tu as bien dit, Je n\'ai point de mari.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 18, 'Car tu as eu cinq maris, &amp; celui que tu as maintenant, n\'eft point ton mari : en cela tu as dit la verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 19, 'La femme lui dit, Seigneur, je vois que tu es Prophete.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 20, 'Nos peres ont adoré en cette montagne, &amp; vous dites qu\'à Jerusalem eft le lieu où il faut adorer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 21, 'Jefus lui dit, Femme, croi moi, que l\'heure vient que vous n\'adorez le Pere, ni en cette montagne, ni à Jerusalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 22, 'Vous adorez ce que vous ne connoiffez point : nous adorons ce que nous connoiffons : car le falut eft des Juifs');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 23, 'Mais l\'heure vient, &amp; eft maintenant, que les vrais adorateurs adoreront le Pere en efprit &amp; en verité : car auffi le Pere en demande de tels qui l\'adorent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 24, 'Dieu eft efprit : &amp; il faut que ceux qui l\'adorent, l\'adorent en efprit &amp; en verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 25, 'La femme lui refpondit, Je fçais que le Meffie (qui eft appellé Chrift) doit venir : quand donc il fera venu, il nous annoncera toutes chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 26, 'Jefus lui dit, C\'eft moi, qui parle à toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 27, 'Et fur cela fes difciples vinrent, &amp; s\'eftonnerent de ce qu\'il parloit avec une femme : toutefois nul ne dit, Que demandes-tu ? Ou, Pourquoi parles-tu avec elle ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 28, 'La femme donc laiffa fa cruche, &amp; s\'en alla à la ville, &amp; dit aux gens,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 29, 'Venez, voyez un homme qui m\'a dit tout ce que j\'ai fait : celui-ci n\'eft-il point le Chrift ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 30, 'Ils fortirent donc de la ville, &amp; vinrent vers lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 31, 'Or cependant les difciples le prioyent, difans, Maiftre, mange.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 32, 'Mais il leur dit, J\'ai à manger d\'une viande que vous ne fçavez point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 33, 'Les difciples donc difoyent entr\'eux, Quelqu\'un lui auroit-il apporté à manger ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 34, 'Jefus leur dit, Ma viande eft que je faffe la volonté de celui qui m\'a envoyé, &amp; que j\'accompliffe fon œuvre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 35, 'Ne dites-vous pas qu\'il y a encore quatre mois, &amp; la moiffon viendra ? Voici, je vous dis, levez vos yeux, &amp; regardez les contrées : car elles font déja blanches pour moiffonner.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 36, 'Or celui qui moiffonne reçoit le falaire, &amp; affemble le fruict en vie eternelle : afin que celui qui feme ait enfemble joye, &amp; celui qui moiffonne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 37, 'Car en cela eft ce dire veritable, Que l\'un feme, &amp; l\'autre moiffonne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 38, 'Je vous ai envoyez moiffonner ce en quoi vous n\'avez point travaillé : d\'autres ont travaillé, &amp; vous eftes entrez en leur travail.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 39, 'Or plufieurs des Samaritains de cette ville-là creurent en lui, pour la parole de la femme, laquelle avoit rendu ce témoignage, Il m\'a dit tout ce que j\'ai fait.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 40, 'Quand donc les Samaritains furent venus vers lui, ils le prierent qu\'il demeuraft avec eux : &amp; il demeura là deux jours.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 41, 'Et beaucoup plus de gens creurent pour fa parole.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 42, 'Et ils difoyent à la femme, Ce n\'eft plus pour ta parole que nous croyons : car nous-mefmes l\'avons ouï : &amp; fçavons que celui-ci eft veritablement le Chrift, le Sauveur du monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 43, 'Or deux jours apres il partit de là : &amp; s\'en alla en Galilée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 44, 'Car Jefus avoit rendu témoignage qu\'un Prophete n\'eft point honoré en fon païs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 45, 'Quand donc il fut venu en Galilée, les Galiléens le receurent, ayant veu toutes les chofes qu\'il avoit faites à Jerufalem au jour de la fefte : car eux auffi eftoyent venus à la fefte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 46, 'Jefus donc vint derechef à Cana ville de Galilée, où il avoit converti l\'eau en vin. Or il y avoit un feigneur de cour, duquel le fils eftoit malade à Capernaum.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 47, 'Celui-là ayant entendu que Jefus eftoit venu de Judée en Galilée, s\'en alla vers lui, &amp; le pria qu\'il defcendift, pour guerir fon fils : car il s\'en alloit mourir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 48, 'Alors Jefus lui dit, Si vous ne voyez des fignes &amp; des miracles, vous ne croyent point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 49, 'Et ce feigneur de cour lui dit, Seigneur, defcen devant que mon fils meure.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 50, 'Jefus lui dit, Va, ton fils vit. Cet homme creut à la parole que Jefus lui avoit dite, &amp; s\'en alloit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 51, 'Et comme déja il defcendoit, fes ferviteurs lui vinrent au devant, &amp; lui apporterent des nouvelles, difans, Ton fils vit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 52, 'Alors il leur demanda à quelle heure il s\'eftoit trouvé mieux. Et ils lui dirent, Hier fur les fept heures la fiévre le laiffa.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 53, 'Le pere donc connut que c\'eftoit à cette mefme heure-là que Jefus lui avoit dit, Ton fils vit. Et il creut, &amp; toute fa maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 54, 'Jefus fit encore ce fecond figne, quand il fut venu de Judée en Galilée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 1, 'APRES ces chofes-là il y avoit une fefte des Juifs : &amp; Jefus monta à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 2, 'Or il y a à Jerufalem, en la porte aux brebis, un lavoir qui eft appellé en Hebreu Bethefda, ayant cinq porches :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 3, 'Efquels gifoit une grande multitude de malades, aveugles, boiteux, &amp; gens qui avoyent les membres fecs ; attendans le mouvement de l\'eau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 4, 'Car un Ange defcendoit en certain temps au lavoir, &amp; alors le premier qui defcendoit au lavoir apres le troublement de l\'eau, eftoit gueri, de quelque maladie qu\'il fuft detenu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 5, 'Or il y avoit là un certain homme qui eftoit detenu de maladie depuis trente-huit ans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 6, 'Jefus le voyant gifant par terre, &amp; connoiffant qu\'il avoit déja efté longtemps là, lui dit, Veux-tu eftre gueri ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 7, 'Le maladie lui refpondit, Seigneur, je n\'ai perfonne qui me jette au lavoir quand l\'eau eft troublée : car pendant que j\'y viens, un autre y defcend avant moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 8, 'Jefus lui dit, Leve-toi, charge ton petit lict, &amp; marche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 9, 'Et incontinent l\'homme fut rendu fain, &amp; chargea fon petit lict, &amp; marchoit. Or il eftoit Sabbat en ce jour-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 10, 'Les Juifs donc dirent à celui qui avoit efté rendu fain, Il eft Sabbat, il ne t\'eft point permis de charger ton petit lict.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 11, 'Il leur refpondit, Celui qui m\'a rendu fain, m\'a dit, Charge ton petit lict, &amp; marche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 12, 'Alors ils lui demanderent, Qui eft celui-là qui t\'a dit, Charge ton petit lict, &amp; marche ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 13, 'Et celui qui avoit efté gueri ne fçavoit qui c\'eftoit : car Jefus, s\'eftoit efcoulé du milieu des troupes qui eftoyent en ce lieu-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 14, 'Depuis, Jefus le trouva au temple, &amp; lui dit, Voici, tu as efté rendu fain, ne peche plus deformais, de peur que pis ne t\'advienne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 15, 'Cet homme s\'en alla, &amp; rapporta aux Juifs que c\'eftoit Jefus qui l\'avoit rendu fain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 16, 'Pour cette caufe donc les Juifs pourfuivoyent Jefus, &amp; cherchoyent à le faire mourir, dautant qu\'il avoit fait ces chofes au Sabbat.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 17, 'Mais Jefus leur refpondit, Mon Pere travaille jufques à maintenant, &amp; je travaille auffi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 18, 'Pour cette caufe donc les Juifs tafchoyent tant plus de le mettre à mort, parce que non feulement il avoit violé le Sabbat, mais auffi qu\'il difoit que Dieu eftoit fon propre Pere, fe faifant égal à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 19, 'Jefus donc refpondit, &amp; leur dit, En verité, en verité je vous dis, que le Fils ne peut rien faire de par foi-mefme, finon qu\'il le voye faire au Pere : car quelque chofe qu\'il faffe, le Fils le fait femblablement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 20, 'Car le Pere aime le Fils, &amp; lui demontre toutes les chofes qu\'il fait : voire lui demontrera de plus grandes œuvres que celle-ci, afin que vous vous émerveilliez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 21, 'Car comme le Pere reffufcite les morts &amp; les vivifie, femblablement auffi le Fils vivifie ceux qu\'il veut.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 22, 'Car le Pere ne juge perfonne, mais il a donné tout jugement au Fils :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 23, 'Afin que tous honorent le Fils, comme ils honorent le Pere. Celui qui n\'honore le Fils, n\'honore point le Pere qui l\'a envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 24, 'En verité, en verité je vous dis, que celui qui oit ma parole, &amp; croit à celui qui m\'a envoyé, a la vie eternelle, &amp; ne viendra point en condamnation, mais il eft paffé de la mort à la vie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 25, 'En verité, en verité je vous dis, que l\'heure vient, &amp; eft déja, que les morts orront la voix du Fils de Dieu, &amp; ceux qui l\'auront ouïe, vivront.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 26, 'Car comme le Pere a vie en foi-mefme, ainfi il a donné au Fils d\'avoir vie en foi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 27, 'Et il lui a donné puiffance d\'exercer auffi jugement, entant qu\'il eft le Fils de l\'homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 28, 'Ne foyez point eftonnez de cela : car l\'heure viendra en laquelle tous ceux qui font és fepulcres orront fa voix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 29, 'Et fortiront, affavoir ceux qui auront bien fait, en refurrection de vie ; mais ceux qui auront mal fait, en refurrection de condamnation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 30, 'Je ne puis rien faire de par moi-même : je juge ainfi que j\'ois, &amp; mon jugement eft jufte : car je ne cherche point ma volonté, mais la volonté du Pere qui m\'a envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 31, 'Si je rens témoignage touchant moi-mefme, mon témoignage n\'eft point digne de foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 32, 'C\'eft un autre qui rend témoignage de moi, &amp; je fçai que le témoignage qu\'il rend de moi eft digne de foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 33, 'Vous avez envoyé vers Jean, &amp; il a rendu témoignage à la verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 34, 'Or je ne cherche point le témoignage des hommes : mais je dis ces chofes afin que vous foyez fauvez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 35, 'Il eftoit une chandelle ardente &amp; luifante : &amp; vous avez voulu pour un peu de temps vous éjouïr en fa lumiere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 36, 'Mais moi j\'ai un témoignage plus grand que celui de Jean : car les œuvres que mon Pere m\'a données pour les accomplir, ces œuvres-là mefmes que je fais témoignent de moi que mon Pere m\'a envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 37, 'Et le Pere qui m\'a envoyé, a lui-mefme rendu témoignage de moi. Jamais vous n\'ouïftes fa voix, ni ne viftes fa femblance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 38, 'Et vous n\'avez point fa parole demeurante en vous : car vous ne croyez point à celui qu\'il a envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 39, 'Enquerez vous diligemment des Ecritures : car vous eftimez avoir par elles la vie eternelle : &amp; ce font elles qui portent témoignage de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 40, 'Mais vous ne voulez point venir à moi, pour avoir la vie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 41, 'Je ne cherche point de la gloire par les hommes ;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 42, 'Mais je connois bien que vous n\'avez point l\'amour de Dieu en vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 43, 'Je fuis venu au nom de mon Pere, &amp; vous ne me recevez point : fi un autre vient en fon propre nom, vous recevez celui-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 44, 'Comme pouvez-vous croire, veu que vous cherchez la gloire l\'un de l\'autre, &amp; ne cherchez point la gloire qui vient de Dieu feul ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 45, 'Ne penfez point que je vous doive accufer envers mon Pere : Moyfe auquel vous avez efperance, eft celui qui vous accufera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 46, 'Car fi vous croyïez à Moyfe : vous croiriez auffi à moi : veu qu\'il a écrit de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 47, 'Mais fi vous ne croyez point à fes écrits, comment croirez-vous à mes paroles ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 1, 'APRES ces chofes Jefus s\'en alla outre la mer de Galilée, qui eft de Tiberias.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 2, 'Et de grandes troupes le fuivoyent, à caufe qu\'ils voioyent les fignes qu\'il faifoit fur ceux qui eftoyent malades.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 3, 'Mais Jefus monta en une montagne, &amp; s\'affit là avec fes difciples.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 4, 'Or le jour de Pafque (qui eftoit la fefte des Juifs) eftoit prochain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 5, 'Jefus donc ayant levé fes yeux, &amp; voyant que de grandes troupes venoyent à lui, dit à Philippe, D\'où acheterons-nous des pains, afin que ceux-ci ayent à manger ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 6, '(Or il difoit cela pour l\'efprouver : car il fçavoit bien ce qu\'il devoit faire.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 7, 'Philippe lui refpondit, Pour deux cens deniers de pain ne leur fuffiroit pas, à ce que chacun d\'eux en prift tant foit peu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 8, 'Et l\'un de fes difciples, affavoir André, frere de Simon Pierre, lui dit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 9, 'Il y a ici un petit garçon qui a cinq pains d\'orge &amp; deux poiffons : mais qu\'eft-ce de cela pour tant de gens ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 10, 'Alors Jefus dit, Faites affeoir les gens. (Or il y avoit beaucoup d\'herbe en ce lieu-là.) Les gens donc s\'affirent, environ au nombre de cinq mille.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 11, 'Et Jefus prit les pains : &amp; apres qu\'il eut rendu graces il les diftribua aux difciples, &amp; les difciples à ceux qui eftoyent affis, &amp; femblablement des poiffons autant qu\'ils en vouloyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 12, 'Et apres qu\'ils furent raffafiez, il dit à fes difciples, Amaffez les pieces qui font de refte, afin que rien ne foit perdu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 13, 'Il les amafferent donc, &amp; emplirent douze corbeilles de pieces des cinq pains d\'orge qui eftoyent demeurées à ceux qui en avoyent mangé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 14, 'Or les gens ayant veu le miracle que Jefus avoit fait, difoyent, Celui-ci eft veritablement le Prophete qui devoit venir au monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 15, 'Donc Jefus ayant connu qu\'ils devoyent venir pour le ravir, afin de le faire Roi, fe retira derechef tout feul en la montagne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 16, 'Et quand le foir fut venu, fes difciples defcendirent à la mer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 17, 'Et eftant montez en la naffelle, tiroyent outre la mer Capernaum : &amp; il faisoit déja abfcur, &amp; Jefus n\'eftoit point venu à eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 18, 'Et la mer s\'éleva par un grand vent qui fouffloit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 19, 'Apres donc qu\'ils eurent ramé environ vingt cinq ou trente ftades, ils virent Jefus cheminant fur la mer, &amp; s\'approchant de la naffelle : dont ils eurent peur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 20, 'Mais il leur dit, C\'eft moi, ne craignez point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 21, 'Ils le receurent donc volontiers en la naffelle, &amp; incontinent la naffelle prit terre au lieu où ils alloyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 22, 'Le lendemain les troupes qui eftoyent demeurées de l\'autre cofté de la mer, voyans qu\'il n\'y avoit point là d\'autre naffelle, finon cette feule, en laquelle eftoyent entrés fes difciples : &amp; que Jefus n\'eftoit point entré avec fes difciples en la naffelle, mais que fes difciples s\'en eftoyent allez feuls :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 23, '(Or d\'autres naffelles eftoyent venuës de Tiberias, prés le lieu où ils avoyent mangé le pain, apres que le Seigneur eut rendu graces.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 24, 'Les troupes donc voyans que Jefus n\'eftoit point là, ni fes difciples, eux auffi monterent dans les naffelles, &amp; vinrent à Capernaum, cherchans Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 25, 'Et l\'ayant trouvé outre la mer, ils lui dirent, Maiftre, quand es-tu arrivé ici ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 26, 'Jefus leur refpondit, &amp; dit, En verité, en verité je vous dis, vous me cherchez, non point parce que vous avez mangé des pains, &amp; avez efté raffafiez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 27, 'Travaillez , non point apres la viande qui perit, mais apres celle qui eft permanente à vie eternelle, laquelle le Fils de l\'homme vous donnera. Car le Pere, affavoir Dieu, l\'a approuvé de fon cachet.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 28, 'Ils lui dirent donc, Que ferons-nous pour ouvrer les œuvres de Dieu ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 29, 'Jefus refpondit, &amp; leur dit, C\'eft ici l\'œuvre de Dieu, que vous croyïez en celui qu\'il a envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 30, 'Alors ils lui dirent, quel figne donc fais-tu, afin que nous le voyïons, &amp; que nous croyïons à toi ? quelle œuvre fais-tu ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 31, 'Nos peres ont mangé la manne au defert, ainfi qu\'il eft efcrit, Il leur a donné à manger le pain du ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 32, 'Jefus donc leur dit, En verité, en verité je vous dis, ce n\'eft point Moyfe qui vous a donné le pain du ciel : mais mon Pere vous donne le vrai pain du ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 33, 'Car le pain de Dieu c\'eft celui qui eft defcendu du ciel, &amp; qui donne la vie au monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 34, 'Ils lui dirent donc, Seigneur, donne-nous toûjours ce pain-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 35, 'Et Jefus leur dit, Je fuis le pain de vie : qui vient à moi, il n\'aura point de faim : &amp; qui croit en moi, n\'aura jamais foif.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 36, 'Mais je vous ai dit, que vous m\'avez veu, &amp; vous ne croyez point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 37, 'Tout ce que mon Pere me donne, viendra à moi, &amp; je ne jetterai point hors celui qui viendra à moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 38, 'Car je fuis defcendu du ciel, non point pour faire ma volonté, mais la volonté de celui qui m\'a envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 39, 'Et c\'eft ici la volonté du Pere qui m\'a envoyé, que je ne perde rien de tout ce qu\'il m\'a donné : mais que je le reffufcite au dernier jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 40, 'Et c\'eft ici auffi la volonté de celui qui m\'a envoyé, que quiconque contemple le Fils, &amp; croit en lui, ait la vie eternelle : &amp; partant je le reffufciterai au dernier jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 41, 'Les Juifs donc murmuroyent de lui, parce qu\'il avoit dit, Je fuis le pain defcendu du ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 42, 'Car ils difoyent, N\'eft-ce pas ici Jefus fils de Jofeph, duquel nous connoiffons le pere &amp; la mere : comment donc dit celui-ci, Je fuis defcendu du ciel ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 43, 'Jefus donc refpondit, &amp; leur dit, Ne murmurez point entre vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 44, 'Nul ne peut venir à moi, fi le Pere, qui m\'a envoyé, ne le tire : &amp; je le reffufciterai au dernier jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 45, 'Il eft efcrit és Prophetes, Et ils feront enfeignez de Dieu. Quiconque donc a ouï du Pere, &amp; a appris, vient à moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 46, 'Non point qu\'aucun ait veu le Pere, finon celui qui eft de Dieu : celui-là a veu le Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 47, 'En verité, en verité je vous dis, Qui croit en moi a la vie eternelle,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 48, 'Je fuis le pain de vie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 49, 'Vos peres ont mangé la manne au defert, &amp; font morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 50, 'C\'eft ici le pain qui eft defcendu du ciel, afin que fi quelqu\'un en mange il ne meure point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 51, 'Je fuis le pain vivifiant, qui fuis defcendu du ciel : fi quelqu\'un en mange de ce pain-ci, il vivra eternellement : &amp; le pain que je donnerai c\'eft ma chair, laquelle je donnerai pour la vie du monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 52, 'Les Juifs donc fe debattoyent entr\'eux, difans, Comment celui-ci peut-il donner fa chair à manger ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 53, 'Alors Jefus leur dit, En verité, en verité je vous dis, que fi vous ne mangez la chair du Fils de l\'homme &amp; ne beuvez fon fang, vous n\'aurez point de vie en vous-mefmes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 54, 'Celui qui mange ma chair, &amp; qui boit mon fang, a la vie eternelle : &amp; je le reffufciterai au dernier jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 55, 'Car ma chair eft vrayement viande, &amp; mon fang eft vrayement breuvage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 56, 'Celui qui mange ma chair &amp; qui boit mon fang, demeure en moi &amp; moi en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 57, 'Comme le Pere qui eft vivant m\'a envoyé, &amp; je fuis vivant de par le Pere ; ainfi celui qui me mangera, vivra auffi de par moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 58, 'C\'eft ici le pain qui eft defcendu du ciel ; non point comme vos peres ont mangé la manne, &amp; font morts : qui mangera ce pain-ci, vivra eternellement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 59, 'Il dit ces chofes en la Synagogue, enfeignant à Capernaum.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 60, 'Plufieurs donc de fes difciples l\'ayant ouï, dirent, Cette parole eft rude : qui la peut ouïr ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 61, 'Mais Jefus fçachant en foi-mefme que fes difciples murmuroyent de cela, leur dit, Ceci vous fcandalize-t\'il ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 62, 'Que fera-ce donc fi vous voyez le Fils de l\'homme monter là où il eftoit premierement ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 63, 'C\'eft l\'Efprit qui vivifie ; la chair ne profite de rien : les paroles que je vous dis, font efprit &amp; vie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 64, 'Mais il y en a d\'entre vous qui ne croyent point. Car Jefus fçavoit dés le commencement, qui feroyent ceux qui ne croiroyent point, &amp; qui feroit celui qui le trahiroit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 65, 'Dont il leur dit, Partant je vous ai dit, que nul ne peut venir à moi, s\'il ne lui eft donné de mon Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 66, 'Dés cette heure-là plufieurs de fes difciples s\'en allerent en arriere, &amp; ne cheminoyent plus avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 67, 'Dont Jefus dit aux douze, Et vous, ne vous en voulez-vous point auffi aller ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 68, 'Simon Pierre dont lui refpondit, Seigneur, à qui nous en irons nous ? Tu as les paroles de vie éternelle :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 69, 'Et nous avons creu, &amp; avons connu que tu es le Fils du Dieu vivant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 70, 'Jefus leur refpondit, Ne vous ai-je pas choifi vous douze, &amp; l\'un de vous eft diable ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 71, 'Or il difoit cela de Juda Ifcariot, fils de Simon : car c\'eftoit celui à qui il devoit arriver de le trahir, bien qu\'il fuft l\'un des douze.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 1, 'APRES ces chofes Jefus converfoit en Galilée : car il ne vouloit point convefer en Judée, parce que les Juifs cherchoyent à le mettre à mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 2, 'Or fefte des Juifs, dite des Tabernacles, eftoit prochaine.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 3, 'Ses freres donc lui dirent, Pars d\'ici, &amp; t\'en va en Judée, afin que tes difciples auffi contemplent les œuvres que tu fais.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 4, 'Car nul ne fait aucune chofe en fecret, qui cherche de fe porter franchement. Si tu fais ces chofes-ici, montre-toi toi-mefme au monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 5, 'Car fes freres mefmes ne croioyent point en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 6, 'Jefus donc leur dit, Mon temps n\'eft point encore venu, mais voftre temps eft toûjours preft.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 7, 'Le monde ne vous peut avoir en haine : mais il m\'a en haine , parce que je rends tefmoignage de lui que fes œuvres font mefchantes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 8, 'Vous autres montez à cette fefte ; pour moi je ne monte point encore à cette fefte, dautant que mon temps n\'eft point encore accompli.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 9, 'Et leur ayant dit ces chofes il demeura en Galilée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 10, 'Et comme fes freres furent montez, alors il monta auffi à la fefte, non point manifeftement, mais comme en cachette.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 11, 'Les Juifs donc le cherchoyent à la fefte, &amp; difoyent, Où eft celui-là ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 12, 'Et il y avoit un grand murmure de lui entre les troupes. Les uns difoyent, Il eft homme de bien : &amp; les autres difoyent, Non eft, mais il feduit le peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 13, 'Toutefois nul ne parloit point franchement de lui pour crainte des Juifs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 14, 'Et comme la fefte eftoit déja demi paffée, Jefus monta au temple, &amp; enfeignoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 15, 'Dont les Juifs s\'eftonnoyent, difant, Comment celui-ci fçait-il les Efcritures, veu qu\'il ne les a point apprifes ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 16, 'Jefus leur refpondit, &amp; dit, Ma doctrine n\'eft point mienne, mais de celui m\'a envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 17, 'Si quelqu\'un veut faire la volonté d\'icelui, il connoiftra de la doctrine, affavoir fi elle eft de Dieu, ou fi je parle de par moi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 18, 'Qui parle de par foi-mefme, il cherche fa propre gloire : mais celui qui cherche la gloire de celui qui l\'a envoyé, celui-là eft veritable, &amp; il n\'y a point d\'injuftice en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 19, 'Moyfe ne vous a-t\'il pas baillé la Loi, &amp; toutefois nul de vous ne met en effet la Loi : Pourquoi tafchez-vous à me faire mourir ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 20, 'Les troupes refpondirent, Tu as le diable : qui eft-ce qui tafche à te faire mourir ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 21, 'Jefus refpondit, &amp; leur dit, J\'ai fait une œuvre, &amp; vous en eftes tous eftonnez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 22, 'Moyfe vous a baillé la circoncifion, (non point qu\'elle foit de Moyfe, mais des Peres) &amp; vous circoncifez l\'homme au Sabbat.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 23, 'Si l\'homme reçoit la circoncifion au Sabbat, afin que la Loi de Moyfe ne foit point violée : eftes-vous irritez contre moi, parce que j\'ai gueri un homme tout entier au Sabbat ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 24, 'Ne jugez point felon l\'apparence, mais jugez d\'un droit jugement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 25, 'Alors quelques-uns de ceux de Jerufalem difoyent, n\'eft-ce pas celui-ci qu\'ils cherchent à faire mourir ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 26, 'Et voici, il parle franchement, &amp; ils ne lui difent rien : les Gouverneurs auroyent-ils bien connu de fait que celui-ci eft veritablement le Chrift ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 27, 'Or nous fçavons bien d\'où eft celui-ci : mais quand le Chrift viendra, nul ne fçaura d\'où il eft.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 28, 'Jefus donc crioit au temple, enfeignant &amp; difant, Et vous me connoiffez, &amp; fçavez d\'où je fuis : &amp; je ne fuis point venu de par moi-mefme, mais celui qui m\'a envoyé, eft veritable, lequel vous ne connoiffez point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 29, 'Mais moi je le connois : car je fuis de par lui, &amp; c\'eft lui qui m\'a envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 30, 'Alors ils tafchoyent de l\'empoigner : mais nul ne mit la main fur lui, dautant que fon heure n\'eftoit point encore venuë.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 31, 'Et plufieurs d\'entre les troupes creurent en lui, &amp; difoyent, Quand le Chrift fera venu, fera-t\'il plus de fignes que celui-ci n\'a fait ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 32, 'Les Pharifiens entendirent la troupe murmurant ces chofes de lui : dont iceux Pharifiens avec les principaux Sacrificateurs envoyerent des fergens pour l\'empoigner.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 33, 'Jefus donc leur dit, Je fuis encore pour un peu de temps avec vous, puis je m\'en vais vers celui qui m\'a envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 34, 'Vous me chercherez, &amp; ne me trouverez point : &amp; là où je ferai, vous n\'y pouvez venir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 35, 'Dont les Juifs dirent entr\'eux, Où doit aller celui-ci, que nous ne le trouverons point ? Doit-il aller vers ceux qui font épars entre les Grecs &amp; enfeigner les Grecs ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 36, 'Quel eft ce difcours qu\'il a tenu, Vous me chercherez, &amp; ne me trouverez point : &amp; là où je ferai vous n\'y pouvez venir ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 37, 'Or en la derniere &amp; grande journée de la fefte, Jefus fe trouva là, criant &amp; difant, Si quelqu\'un a foif, qu\'il vienne à moi &amp; qu\'il boive.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 38, 'Qui croit en moi fuivant ce que dit l\'Ecriture, il découlera des fleuves d\'eau vivante de fon ventre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 39, '(Or il difoit cela de l\'efprit que devoyent recevoir ceux qui croioyent en lui : car le fainct Efprit n\'eftoit point encore donné, parce que Jefus n\'eftoit point encore glorifié.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 40, 'Plufieurs donc de la troupe ayant ouï ce difcours, difoyent, Celui-ci eft veritablement le Prophete.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 41, 'Les autres difoyent, Celui-ci eft le Chrift. Et les autres difoyent, Mais auffi le Chrift viendra-t\'il de Galilée ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 42, 'L\'Efcriture ne dit-elle pas que le Chrift viendra de la femence de David &amp; de la bourgade de Bethlehem, où a efté David ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 43, 'Il y eut donc diffenfion entre le peuple pour lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 44, 'Et quelques-uns d\'entr\'eux le vouloyent empoigner : mais nul ne mit les mains fur lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 45, 'Ainfi les Sergens s\'en revinrent aux principaux Sacrificateurs &amp; aux Pharifiens, lefquels leur dirent, Pourquoi ne l\'avez-vous point amené ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 46, 'Les Sergens refpondirent, Jamais homme ne parla comme fait cet homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 47, 'Les Pharifiens donc leur refpondirent, N\'avez-vous point efté feduits vous auffi ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 48, 'Aucun des Gouverneurs ou des Pharifiens a-t\'il creu en lui ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 49, 'Mais ce populaire-ci, qui ne fçait ce que c\'eft de la Loi, eft plus qu\'execrable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 50, 'Nicodeme (celui qui eftoit venu vers Jefus de nuit, &amp; qui eftoit l\'un d\'entr\'eux) leur dit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 51, 'Noftre Loi juge-t\'elle un homme avant que de l\'avoir ouï, &amp; avoir connu ce qu\'il a fait ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 52, 'Ils refpondirent, &amp; lui dirent, N\'es-tu pas auffi de Galilée : enquiers toi, &amp; fçache que nul Prophete n\'a efté fufcité de Galilée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 53, 'Et chacun s\'en alla en fa maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 1, 'MAIS Jefus s\'en alla en la montagne des Oliviers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 2, 'Et au poinct du jour, il vint derechef au temple, &amp; tout le peuple vint vers lui, &amp; eftant affis il les enfeignoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 3, 'Alors les Scribes &amp; les Scribes &amp; les Pharifiens lui amenerent une femme furprife en adultere : &amp; l\'ayant mife là au milieu,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 4, 'Lui dirent, Maiftre, cette femme-ci a efté furprife fur le fait mefme, commettant adultere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 5, 'Or en la Loi, Moyfe nous a commandé de lapider celles qui font telles, Toi donc qu\'en dis-tu ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 6, 'Or ils difoyent cela le tentans, afin qu\'ils euffent dequoi l\'accufer. Mais Jefus eftant encliné en bas, écrivoit du doigt en terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 7, 'Et comme ils continuoyent de l\'interroger, lui s\'eftant redreffé, leur dit, Que celui de vous qui eft fans peché, jette le premier la pierre contr\'elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 8, 'Et derechef eftant encliné, il écrivoit en terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 9, 'Or quand ils eurent ouï cela, eftant redarguëz par leur confcience, ils fortirent un à un, commençans depuis les plus anciens jufques aux derniers : tellement que Jefus demeura feul, &amp; la femme qui eftoit là au milieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 10, 'Alors Jefus s\'eftant redreffé, &amp; ne voyant perfonne finon la femme, lui dit, Femme, où font ceux qui t\'accufoyent ? Nul ne t\'a-t\'il condamnée ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 11, 'Elle dit, Nul, Seigneur. Et Jefus lui dit, Je ne te condamne point auffi : va, &amp; ne peche plus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 12, 'Jefus donc parla derechef à eux, difant, Je fuis la lumiere du monde : celui qui me fuit, ne cheminera point en tenebres, mais il aura la lumiere de vie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 13, 'Alors les Pharifiens lui dirent, Tu rens tefmoignage de toi-mefme, ton tefmoignage n\'eft point digne de foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 14, 'Jefus refpondit, &amp; leur dit, Encore que je rende tefmoignage eft digne de foi : car je fçais d\'où je fuis venu, &amp; où je vais : mais vous ne fçavez d\'où je viens, ni où je vais.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 15, 'Vous jugez felon la chair : moi, je ne juge perfonne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 16, 'Que fi mefme je juge, mon jugement eft digne de foi : car je ne fuis point feul, mais il y a moi &amp; le Pere qui m\'a envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 17, 'Mefmes il eft écrit en voftre Loi, que le tefmoignage de deux hommes eft digne de foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 18, 'Je fuis celui qui rens tefmoignage de moi-mefme, &amp; le Pere qui m\'a envoyé rend tefmoignage de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 19, 'Ils lui dirent donc, Où eft ton Pere ? Jefus refpondit, Vous ne connoiffez ni moi, ni mon Pere. Si vous me connoiffez, vous connoiftriez auffi mon Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 20, 'Jefus dit ces paroles en la treforerie, enfeignant au temple : &amp; nul ne l\'empoigna, parce que fon heure n\'eftoit point encore venuë.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 21, 'Et Jefus leur dit derechef, Je m\'en vais, &amp; vous me chercherez, &amp; mourrez en voftre peché : là où je vais, vous ne pouvez venir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 22, 'Les Juifs donc difoyent, Se tuëra-t\'il foi-mefme ? D\'autant qu\'il dit, Là où je vais, vous ne pouvez venir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 23, 'Alors il leur dit, Vous eftes d\"embas, moi, je fuis d\'en haut : vous eftes de ce monde, moi, je ne fuis point de ce monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 24, 'Partant je vous ai dit, que vous mourrez en vos pechez : car fi vous ne croyez que c\'eft moi, vous mourrez en vos pechez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 25, 'Alors ils lui dirent, Toi, qui es-tu ? Et Jefus leur dit, Ce que je vous dis dés le commencement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 26, 'J\'ai beaucoup de chofes à parler &amp; à juger de vous : mais celui qui m\'as envoyé, eft veritable : &amp; les chofes que j\'ai ouïes de lui, je les dis au monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 27, 'Ils ne connurent point qu\'il leur parloit du Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 28, 'Jefus donc leur dit, Quand vous aurez elevé le Fils de l\'homme, vous connoiftrez alors que c\'eft moi, &amp; que je ne fais rien de moi-mefme, mais que je dis ces chofes ainfi que mon Pere m\'a enfeigné.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 29, 'Car celui qui m\'a envoyé eft avec moi : le Pere ne m\'a point laiffé feul : parce que je fais toûjours les chofes qui lui plaifent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 30, 'Comme il difoit ces chofes, plufieurs creurent en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 31, 'Alors Jefus difoit aux Juifs qui avoyent creu en lui, Si vous perfiftez en ma parole, vous ferez vrayement mes difciples :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 32, 'Et connoiftrez la verité, &amp; la verité vous affranchira.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 33, 'Ils lui refpondirent, Nous fommes la pofterité d\'Abraham, &amp; jamais nous ne fervimes à perfonne : comment donc dis-tu, Vous ferez affranchis ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 34, 'Jefus leur refpondit, En verité, en verité je vous dis, quiconque fait peché, eft ferf de peché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 35, 'Or le ferf ne demeure point à toûjours en la maifon, le fils y demeure à toûjours.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 36, 'Si donc le fils vous affranchit, vous ferez vrayement francs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 37, 'Je fçais que vous eftes la pofterité d\'Abraham : mais vous tafchez à me faire mourir, parce que ma parole n\'a point de lieu en vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 38, 'Je vous dis ce que j\'ai veu chez mon Pere, &amp; vous auffi faites les chofes que vous avez veuës chez voftre pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 39, 'Ils refpondirent, Noftre pere eft Abraham. Jefus leur dit, Si vous eftiez enfans d\'Abraham, vous feriez les œuvres d\'Abraham.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 40, 'Or maintenant vous tafchez à me faire mourir, moi qui fuis un homme qui vous ai dit la verité, laquelle j\'ai ouïe de Dieu : Abraham n\'a point fait cela.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 41, 'Vous faites les œuvres de voftre Pere. Dont ils lui dirent, Nous ne fommes point nez de paillardife. Nous avons un Pere, qui eft Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 42, 'Jefus donc leur dit, Si Dieu eftoit voftre Pere, certes vous m\'aimeriez : car je fuis venu de Dieu, &amp; viens de devers lui : car je ne fuis point venu de par moi-mefme, mais il m\'a envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 43, 'Pourquoi n\'entendez-vous point mon parler ? D\'autant que vous ne pouvez ouïr ma parole.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 44, 'Le pere dont vous eftes iffus, c\'eft le diable, &amp; vous voulez faire les defirs de voftre pere. Il a efté meurtrier dés le commencement, &amp; n\'a point perfeveré en la verité : car la verité n\'eft point en lui. Toutes les fois qu\'il profere monfonge, il parle de ce qui lui eft propre : car il eft menteur &amp; eft le pere de monfonge.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 45, 'Et parce que je dis la verité, vous ne me croyez point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 46, 'Qui eft celui d\'entre vous qui me redarguëra de peché ? &amp; fi je dis la verité, pourquoi ne me croyez-vous point ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 47, 'Celui qui eft de Dieu, oit les parole de Dieu : partant ne les oyez-vous point, à caufe que vous n\'eftes point de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 48, 'Alors les Juifs refpondirent &amp; lui dirent, Ne difons-nous pas bien que tu es un Samaritain, &amp; que tu as le diable ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 49, 'Jefus refpondit, Je n\'ai point le diable, mais j\'honore mon Pere, &amp; vous me deshonorez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 50, 'Or je ne cherche point ma gloire : il y a qui la cherche, &amp; qui en juge.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 51, 'En verité, en verité je vous dis, que fi quelqu\'un garde ma parole, il ne verra jamais la mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 52, 'Dont les juifs lui dirent, Maintenant nous connoiffons que tu as le diable. Abraham eft mort &amp; les Prophetes, &amp; tu dis, Si quelqu\'un garde ma parole, il ne gouftera jamais la mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 53, 'Es-tu plus grand que noftre pere Abraham qui eft mort ? Les Prophetes auffi font morts : qui te fais-tu toi-mefme ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 54, 'Jefus refpondit, Si je me glorifie moi-mefme, ma gloire n\'eft rien : mon Pere eft celui qui me glorifie, duquel vous dites qu\'il eft voftre Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 55, 'Toutefois vous ne l\'avez point connu, mais moi je le connois : &amp; fi je dis que je ne le connois point, je ferai menteur femblable à vous : mais je le connois, &amp; garde fa parole.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 56, 'Abraham voftre pere a treffailli de joye de voir cette mienne journée ; &amp; l\'a veuë, &amp; s\'en eft éjouï.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 57, 'Les Juifs donc lui dirent, Tu n\'as point encore cinquante ans, &amp; tu as veu Abraham ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 58, 'Jefus leur dit, En verité, en verité je vous dis, avant qu\'Abraham fuft, je fuis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 59, 'Alors ils leverent des pierres pour jetter contre lui : mais Jefus fe cacha, &amp; fortit du temple, ayant paffé au travers d\'eux : &amp; ainfi s\'en alla.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 1, 'ET ainfi que Iefus paffoit, il vid un homme aveugle dés fa naiffance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 2, 'Dont fes Difciples l\'interrogerent, difant, Maiftre, qui a peché, celui-ci, ou fon pere, ou fa mere, pour eftre ainfi né aveugle ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 3, 'Jefus refpondit, ni celui-ci n\'a peché, ni fon pere, ni fa mere : mais c\'eft afin que les œuvres de Dieu foyent manifeftées en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 4, 'Il me faut faire les œuvres de celui qui m\'a envoyé, tandis qu\'il eft jour. La nuit vient que nul ne peut travailler.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 5, 'Tandis que je fuis au monde, je fuis la lumiere du monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 6, 'Quand il eut dit cela, il cracha en terre, &amp; fit de la bouë de fa falive, &amp; oignit de cette bouë les yeux de l\'aveugle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 7, 'Et lui dit, Va t\"en, &amp; te lave au lavoir de Siloë, (qui vaut autant à dire qu\'envoyé.) Il alla donc, &amp; fe lava, &amp; revint voyant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 8, 'Or les voifins, &amp; ceux qui avoyent veu auparavant qu\'il eftoit aveugle, difoyent, N\'eft-ce pas celui-ci qui eftoit affis, &amp; qui mendioit ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 9, 'Les uns difoyent, C\'eft celui-ci. Et les autres difoyent, Il lui reffemble. Lui difoit, C\'eft moi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 10, 'Ils lui dirent donc, Comment ont efté ouverts tes yeux ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 11, 'Il refpondit, &amp; dit, Cet homme qu\'on appelle Jefus, a fait de la bouë, &amp; en a oint mes yeux, &amp; m\'a dit, Va au lavoir de Siloë, &amp; te lave. Apres donc que j\'y fuis allé, &amp; me fuis lavé, j\'ai recouvré la veuë.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 12, 'Alors il lui dirent, Où eft celui-là ? Il dit, je ne fçais.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 13, 'Ils l\'amenerent aux Pharifiens, voire celui qui avoit autrefois efté aveugle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 14, 'Or il eftoit Sabbat quand Jefus avoit fait de la bouë, &amp; lui avoit ouvert les yeux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 15, 'Les Pharifiens donc l\'interrogerent auffi derechef, comment il avoit recouvré la veuë. Et il leur dit, Il a mis de la bouë fur mes yeux, &amp; je me fuis lavé, &amp; je vois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 16, 'Quelques-uns donc d\'entre les Pharifiens difoyent, Cet homme-ci n\'eft point de Dieu : car il ne garde point le Sabbat. Les autres difoyent, Comment un méchant homme peut-il faire ces fignes ? Et il y avoit diffenfion entr\'eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 17, 'Ils dirent derechef à l\'aveugle, Toi que dis-tu de lui, de ce qu\'il t\'a ouvert les yeux ? Il refpondit, Il eft Prophete.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 18, 'Mais les Juifs ne creurent point de lui qu\'il euft efté aveugle : &amp; qu\'il euft recouvré la veuë, jufques à ce qu\'ils eurent appellé le pere &amp; la mere de celui qui avoit recouvré la veuë.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 19, 'Ils les interrogerent donc, difans, Eft-ce ci voftre fils que vous dites eftre né aveugle ? Comment donc voit-il maintenant ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 20, 'Son pere &amp; fa mere leur refpondirent, &amp; dirent, Nous fçavons que c\'eft ici noftre fils, &amp; qu\'il eft né aveugle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 21, 'Mais comment maintenant il voit, ou, qui lui a ouvert les yeux, nous ne le fçavons point : il a de l\'âge, interrogez-le, il parlera touchant foi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 22, 'Son pere &amp; fa mere dirent ces chofes, parce qu\'ils craignoyent les Juifs. Car les Juifs avoyent déja arrefté, que fi quelqu\'un l\'advoüoit eftre le Chrift, il feroit jetté hors de la Synagogue.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 23, 'Pour cette caufe fon pere &amp; fa mere refpondirent, Il a de l\'âge, interrogez-le.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 24, 'Ils appellerent donc pour la feconde fois l\'homme qui avoit efté aveugle, &amp; lui dirent, Donne gloire à Dieu : nous fçavons que cet homme eft méchant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 25, 'Il refpondit, &amp; dit, S\'il eft méchant, je ne fçais : une chofe fçais-je bien, c\'eft que j\'eftois aveugle, &amp; maintenant je vois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 26, 'Dont ils lui dirent derechef, Que t\'a-t\'il fait ? comment a-t\'il ouvert tes yeux ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 27, 'Il refpondit, Je vous l\'ai déja dit, &amp; vous ne l\'avez point efcouté : pourquoi le voulez-vous encore ouïr ? Voulez-vous auffi eftre fes difciples ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 28, 'Alors ils l\'injurierent, &amp; dirent, Toi, fois fon difciple : quant à nous, nous fommes difciples de Moyfe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 29, 'Nous fçavons que Dieu a parlé à Moyfe : mais quant à celui-ci, nous ne fçavons d\'où il eft.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 30, 'L\'homme refpondit, &amp; leur dit, Certes, c\'eft une chofe eftrange, que vous ne fçavez d\'où il eft, &amp; toutefois il a ouvert mes yeux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 31, 'Or nous fçavons que Dieu n\'exauce point les mefchans : mais fi quelqu\'un eft ferviteur de Dieu, &amp; fait fa volonté, il l\'exauce.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 32, 'On n\'ouït jamais dire, qu\'aucun ouvrift les yeux d\'un aveugle né.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 33, 'Si celui-ci n\'eftoit de Dieu, il ne pourroit rien faire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 34, 'Ils refpondirent, &amp; lui dirent, Tu es du tout né en pechez, &amp; tu nous eifeignes. Et ils le jetterent hors de la Synagogue.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 35, 'Jefus entendit comme ils l\'avoyent jetté hors : &amp; l\'ayant trouvé, lui dit, Crois-tu au Fils de Dieu ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 36, 'Et il refpondit, a dit, Qui eft-il, Seigneur, afin que je croye en lui ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 37, 'Jefus lui dit, Tu l\'as veu, &amp; c\'eft celui qui parle à toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 38, 'Alors il dit, J\'y crois, Seigneur : &amp; l\'adora.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 39, 'Et Jefus dit, Je fuis venu en ce monde pour exercer jugement, afin que ceux qui ne voyent point, voyent, &amp; que ceux qui voyent, deviennent aveugles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 40, 'Et quelques-uns d\'entre les Pharifiens qui eftoyent avec lui, ouïrent cela, &amp; lui dirent, Et nous, fommes-nous auffi aveugles ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 41, 'Jefus leur dit, Si vous eftiez aveugles, vous n\'auriez point de peché : mais maintenant vous dites, Nous voyons : c\'eft pourquoi voftre peché demeure.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 1, 'EN verité, en verité je vous dis, Que celui qui n\'entre point par la porte à la bergerie des brebis, mais y monte par ailleurs, eft larron &amp; brigand.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 2, 'Mais celui qui entre par la porte eft le berger des brebis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 3, 'Le portier ouvre à celui-là, &amp; les brebis oyent fa voix, &amp; il appelle fes propres brebis par leur nom, &amp; les mene dehors.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 4, 'Et quand il a mis hors fes brebis, il va devant elles, &amp; les brebis le fuivent : car elles connoiffent fa voix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 5, 'Mais elles ne fuivront point un eftranger, au contraire elles s\'enfuïront arriere de lui : car elles ne connoiffent point la voix des eftrangers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 6, 'Jefus leur dit cette fimilitude : mais il n\'entendirent point quelles eftoyent les chofes qu\'il leur difoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 7, 'Jefus donc leur dit derechef, En verité, en verité je vous dis, que je fuis la porte des brebis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 8, 'Tout autant qu\'il en eft venu devant moi, font des larrons &amp; des brigans : mais les brebis ne les ont point ouïs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 9, 'Je fuis la porte, fi quelqu\'un entre par moi, il fera fauvé ; &amp; entrera &amp; fortira, &amp; trouvera pafture.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 10, 'Le larron ne vient finon pour defrober, &amp; tuër, &amp; deftruire : je fuis venu afin qu\'elles ayent vie, voire qu\'elles en ayent en abondance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 11, 'Je fuis le bon berger :le bon berger met fa vie pour fes brebis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 12, 'Mais le mercenaire, &amp; celui qui n\'eft point berger (à qui n\'appartiennent point les brebis) voit venir le loup, &amp; abandonne les brebis, &amp; s\'enfuit : &amp; le loup les ravit, &amp; épard les brebis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 13, 'Ainfi le mercenaire s\'enfuit, parce qu\'il eft mercenaire, &amp; qu\'il ne fe foucie point des brebis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 14, 'Je fuis le bon berger, &amp; connois mes brebis, &amp; fuis connu des miennes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 15, 'Comme le Pere me connoit, auffi je connois le Pere, &amp; mets ma vie pour mes brebis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 16, 'J\'ai encore d\'autres brebis qui ne font point de cette bergerie : il me les faut auffi amener, &amp; elles orront ma voix, &amp; il y aura un feul troupeau, &amp; un feul berger.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 17, 'Pour cette caufe le Pere m\'aime, parce que je laiffe ma vie, afin que je la prenne derechef.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 18, 'Nul ne me l\'ofte, mais je la laiffe de par moi-mefme : j\'ai puiffance de la laiffer, &amp; j\'ai puiffance de la reprendre. J\'ai receu ce mandement de mon Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 19, 'Alors il advint derechef diffenfion entre les Juifs pour ces propos.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 20, 'Et plufieurs d\'entr\'eux difoyent, Il a le diable, &amp; eft hors du fens, pourquoi l\'écoutez-vous ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 21, 'Les autres difoyent, Ces paroles ne font point d\'un demoniaque : le diable peut-il ouvrir les yeux des aveugles ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 22, 'Or la fefte de la dedicace fe fit à Jerufalem ; &amp; il eftoit hyver.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 23, 'Et Juifs fe promenoit au temple, au porche de Salomon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 24, 'Les Juifs donc l\'environnerent, &amp; lui dirent, Jufques à quand tiens-tu noftre ame en fufpens ? Si tu es le Chrift, di le nous franchement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 25, 'Jefus leur refpondit, Je vous l\'ai dit, &amp; vous ne le croyez point : les œuvres que je fais au nom de mon Pere, rendent témoignage de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 26, 'Mais vous ne croyez point : car vous n\'eftes point de mes brebis : comme je vous ai dit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 27, 'Mes brebis oyent ma voix, &amp; je les connois, &amp; elles me fuivent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 28, 'Et moi je leur donne la vie eternelle : &amp; elles ne periront jamais : nul auffi ne les ravira de ma main.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 29, 'Mon Pere, qui me les a données, eft plus grand que tous, &amp; perfonne ne les peut ravir des mains de mon Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 30, 'Moi &amp; le Pere fommes un.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 31, 'Alors les Juifs prirent derechef des pierres pour le lapider.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 32, 'Jefus refpondit, Je vous ai fait voir plufieurs bonnes œuvres de par mon Pere, pour laquelle d\'icelles me lapidez-vous ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 33, 'Les Juifs lui refpondirent, difans, Nous ne te lapidons point pour quelque bonne œuvre : mais pour blafpheme, &amp; parce que toi eftant homme, tu te fais Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 34, 'Jefus leur refpondit, N\'eft-il pas écrit en voftre Loi, J\'ai dit, Vous eftes dieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 35, 'Si elle a appellé ceux-là dieux, aufquels la parole de Dieu eft addreffée, &amp; l\'Efcriture ne peut eftre enfrainte :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 36, 'Dites-vous que je blafpheme, moi que le Pere a fanctifié &amp; qu\'il a envoyé au monde, parce que j\'ai dit, Je fuis le Fils de Dieu ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 37, 'Si je ne fais les œuvres de mon Pere, ne me croyez point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 38, 'Mais fi je les fais, &amp; vous ne me voulez point croire, croyez aux œuvres : afin que vous connoiffiez &amp; croyïez que le Pere eft en moi, &amp; moi en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 39, 'Ils cherchoyent donc derechef à l\'empoigner : mais il efchappa de leurs mains :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 40, 'Et s\'en alla derechef outre le Jourdain, au quartier où Jean baptizoit premierement, &amp; demeura-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 41, 'Et plufieurs vinrent à lui, &amp; difoyent, Quand à Jean, il n\'a fait aucun figne : mais toutes les chofes que Jean a dites de celui-ci, eftoyent veritables.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 42, 'Et plufieurs creurent là en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 1, 'OR il y avoit un certain homme malade, appellé Lazare, de Bethanie, de la bourgade de Marie &amp; de Marthe fa fœur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 2, '(Et Marie fut celle qui oignit d\'oignement le Seigneur, &amp; effuya les pieds d\'icelui de fes cheveux : de laquelle le frere Lazare eftoit malade.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 3, 'Ses fœurs donc envoyerent vers lui, difans, Seigneur, voici, celui que tu aimes eft malade.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 4, 'Jefus ayant entendu cela, dit, Cette maladie n\'eft point à la mort ; mais pour la gloire de Dieu : afin que le Fils de Dieu foit glorifié par elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 5, 'Or Jefus aimoit Marthe, &amp; fa fœur, &amp; Lazare.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 6, 'Et apres avoir entendu qu\'il eftoit malade, il demeura deux jours au mefme lieu où il eftoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 7, 'Et apres cela il dit à fes difciples, Allons derechef en Judée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 8, 'Les difciples lui dirent, Maiftre, les Juifs cherchoyent nagueres à te lapider, &amp; tu y vas derechef ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 9, 'Jefus refpondit, N\'y a-t\'il pas douze heures au jour ? Si quelqu\'un chemine de jour, il ne bronche point : car il voit la lumiere de ce monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 10, 'Mais fi quelqu\'un chemine de nuit, il bronche : car il n\'y a point de lumiere avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 11, 'Il dit ces chofes, &amp; puis apres leur dit, Lazare noftre ami dort : mais j\'y vais pour l\'éveiller.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 12, 'Dont fes difciples lui dirent, Seigneur, s\'il dort il fera gueri.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 13, 'Or Jefus avoit dit cela de la mort d\'icelui : mais ils penfoyent qu\'il parlaft du dormir du fommeil.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 14, 'Jefus donc leur dit alors ouvertement, Lazare eft mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 15, 'Et je fuis joyeux pour l\'amour de vous, que je n\'y eftois point, afin que vous croyïez : mais allons vers lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 16, 'Alors Thomas appellé Didyme, dit à fes condifciples, Allons y auffi, afin que nous mourrions avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 17, 'Jefus donc eftant venu, le trouva qu\'il eftoit déja depuis quatre jours au fepulcre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 18, '(Or Bethanie eftoit prés de Jerufalem environ quinze ftades.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 19, 'Et plufieurs des Juifs eftoyent venus vers Marthe &amp; Marie, pour les confoler touchant leur frere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 20, 'Quand donc Marthe ouït dire que Jefus venoit, elle alla au devant de lui : mais Marie fe tenoit affife en la maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 21, 'Marthe donc dit à Jefus, Seigneur fi tu euffes efté ici, mon frere ne fuft pas mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 22, 'Mais auffi fçais-je maintenant que tout ce que tu demanderas à Dieu, Dieu te le donnera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 23, 'Jefus lui dit, Ton frere reffufcitera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 24, 'Marthe lui dit, Je fçais qu\'il reffufcitera en la refurrection au dernier jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 25, 'Jefus lui dit, Je fuis la refurrection &amp; la vie : qui croit en moi, encore qu\'il foit mort, il vivra :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 26, 'Et quiconque vit &amp; croit en moi, ne mourra jamais. Crois-tu cela ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 27, 'Elle lui dit, Ouï, Seigneur, je crois que tu es le Chrift, le Fils de Dieu, qui devoit venir au monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 28, 'Puis ayant dit cela, elle s\'en alla, &amp; appella Marie fa fœur en fecret, difant, Le Maiftre eft ici, &amp; t\'appelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 29, 'Et auffi-toft qu\'elle l\'eut entendu, elle fe leva haftivement, &amp; s\'en vint vers lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 30, 'Or Jefus n\'eftoit point encore venu à la bourgade, mais il eftoit au lieu où Marthe l\'avoit rencontré.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 31, 'Alors les Juifs qui eftoyent avec elle en la maifon, &amp; la confoloyent, quand ils virent que Marie s\'eftoit levée fi-toft, &amp; qu\'elle eftoit fortie, la fuivirent, difans, Elle s\'en va au fepulcre, afin de pleurer là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 32, 'Quand donc Marie fut venuë là où eftoit Jefus, l\'ayant veu, elle fe jetta à fes pieds, lui difant, Seigneur, fi tu euffes efté ici, mon frere ne fuft pas mort !');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 33, 'Quand Jefus vid pleurant, &amp; les Juifs qui eftoyent là venus avec elle, auffi pleurans, il fremit en fon efprit, &amp; s\'emeut foi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 34, 'Et dit, Où l\'avez-vous mis ? Ils lui refpondirent, Seigneur, vien &amp; voi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 35, 'Et Jefus pleura.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 36, 'Les Juifs donc dirent, Voyez comme il l\'aimoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 37, 'Et quelques-uns d\'entr\'eux dirent, Celui-ci qui a ouvert les yeux de l\'aveugle, ne pouvoit-il pas faire auffi que cet homme ne mouruft point ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 38, 'Alors Jefus fremiffant derechef en foi-mefme, vint au fepulcre. (Or c\'eftoit une grotte : &amp; il y avoit une pierre mife deffus.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 39, 'Jefus dit, Levez la pierre. Mais Marthe, la fœur de celui qui eftoit mort, lui dit, Seigneur, il put déja : car il eft de quatre jours.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 40, 'Jefus lui dit, Ne t\'ai-je pas dit, que fi tu crois, tu verras la gloire de Dieu ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 41, 'Ils leverent donc la pierre de deffus le lieu où gifoit le mort. alors Jefus levant fes yeux en haut, dit, Pere, je te rends graces que tu m\'as exaucé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 42, 'Or je fçavois bien que tu m\'exauces toûjours : mais je l\'ai dit à caufe des troupes qui font à l\'entour, afin qu\'elles croyent que c\'eft toi qui m\'as envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 43, 'Et ayant dit ces chofes, il cria à haute voix, Lazare vien-t\'en dehors.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 44, 'Alors fortit le mort, ayant les mains &amp; les pieds liez de bandes : &amp; fon vifage eftoit enveloppé d\'un couvrechef. Jefus leur dit, Defliez-le, &amp; le laiffez aller.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 45, 'Plufieurs donc des Juifs qui eftoyent venus vers Marie, &amp; avoyent veu les chofes que Jefus avoit faites, creurent en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 46, 'Mais quelques-uns d\'entr\'eux s\'en allerent aux Pharifiens, &amp; leur dirent les chofes que Jefus avoit faites.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 47, 'Alors les principaux Sacrificateurs &amp; les Pharifiens affemblerent le confeil, &amp; difoyent, Que faifons-nous ? car cét-homme-ci fait beaucoup de fignes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 48, 'Si nous le laiffons ainfi, chacun croira en lui : &amp; les Romains viendront qui nous extermineront, &amp; le lieu &amp; la nation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 49, 'Alors un certain d\'entr\'eux appellé Caïphe, qui eftoit le fouverain Sacrificateur de cette année-là, leur dit, Vous n\'y entendez rien :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 50, 'Et ne confiderez point qu\'il nous eft expedient qu\'un homme meure pour le peuple, &amp; que toute la nation ne periffe point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 51, 'Or il ne dit point cela de par foi-mefme, mais lui eftant le fouverain Sacrificateur de cette année-là, prophetiza, que Jefus devoit mourir pour la nation :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 52, 'Et non pas feulement pour la nation, mais auffi afin qu\'il affemblaft en un les enfans de Dieu qui eftoyent difperfez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 53, 'Depuis ce jour-là donc ils confulterent enfemble de le mettre à mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 54, 'C\'eft pourquoi Jefus ne cheminoit plus ouvertement entre les Juifs : mais il s\'en alla de là en la contrée qui eft prés du defert, en une ville appellée Ephraïm, &amp; il converfoit là avec fes difciples.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 55, 'Or le jour de la Pafque des Juifs eftoit prochain, &amp; plufieurs de ces quartiers-là monterent à Jerufalem avant Pafque, afin qu\'ils fe purifiaffent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 56, 'Ils cherchoyent donc Jefus, &amp; difoyent entr\'eux reftans au temple, Que vous femble ? Vous femble-il qu\'il ne viendra point à la fefte ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 57, 'or les principaux Sacrificateurs, &amp; les Pharifiens avoyent donné commandement, que fi quelqu\'un connoiffoit où il eftoit, il le declaraft, afin qu\'ils l\'empoignaffent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 1, 'JEFUS donc fix jours devant Pafque vint à Bethanie, où eftoit Lazare qui avoit efté mort, lequel il avoit reffufcité des morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 2, 'Et on lui fit là un fouper, &amp; Marthe fervoit à table, &amp; Lazare eftoit l\'un de ceux qui eftoyent à table avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 3, 'Alors Marie ayant pris une livre d\'oignement d\'afpic liquide de grand prix, en oignit les pieds de Jefus, &amp; les effuya de fes cheveux : &amp; la maifon fut remplie de l\'odeur de l\'oignement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 4, 'Alors Judas Ifariot, fils de Simon, l\'un de fes difciples, celui à qui il devoit arriver de le trahir, dit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 5, 'Pourquoi cét oignement n\'a-t\'il efté vendu trois cens deniers, &amp; donné aux pauvres ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 6, 'Or il dit cela, non point qu\'il fe fouciaft des pauvres : mais parce qu\'il eftoit larron, &amp; qu\'il avoit la bourfe, &amp; portoit ce qu\'on y mettoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 7, 'Jefus donc dit, Laiffe-la : elle l\'a gardé pour le jour de l\'appareil de ma fepulture.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 8, 'Car vous aurez toûjours les pauvres avec vous : mais vous ne m\'aurez pas toûjours.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 9, 'Alors de grandes troupes des Juifs connurent qu\'il eftoit là : &amp; y vinrent, non feulement pour Jefus, mais auffi afin qu\'ils viffent Lazare, lequel avoit reffufcité des morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 10, 'Dont les principaux Sacrificateurs confulterent de mettre auffi à mort Lazare.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 11, 'Car plufieurs des Juifs fe departoyent à caufe de lui, &amp; croioyent en Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 12, 'Le lendemain une grande troupe, qui eftoit venuë à la fefte, ayant ouï dire que Jefus venoit à Jerufalem,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 13, 'Prirent des rameaux de palmes, &amp; fortirent au devant de lui, &amp; crioyent, Hofanna, Benit foit le Roi d\'Ifraël qui vient au nom du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 14, 'Et Jefus ayant recouvré un afnon s\'affit deffus, fuivant ce qui eft efcrit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 15, 'Ne crain point, fille de Sion : voici, ton Roi vient, affis fur le poulain de l\'afneffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 16, 'Or fes difciples n\'entendirent point ces chofes du premier coup : mais quand Jefus fut glorifié, alors ils fe fouvinrent que ces chofes eftoyent efcrites de lui, &amp; qu\'ils avoient fait ces chofes en fon endroit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 17, 'Et la troupe qui eftoit avec lui, rendoit témoignage qu\'il avoit appellé Lazare du fepulcre, &amp; qu\'il l\'avoit reffufcité des morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 18, 'C\'eft pourquoi auffi le peuple alla au devant de lui : car ils avoyent entendu comme il avoit fait ce miracle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 19, 'Dont les Pharifiens difoyent entr\'eux-mefmes, Ne voyez-vous pas que vous ne profitez de rien ? Voici, le monde va apres lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 20, 'Or il avoit quelques Grecs de ceux qui eftoyent montez pour adorer à la fefte ;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 21, 'Lefquels vinrent vers Philippe, qui eftoit de Bethfaïda de Galilée, &amp; le prierent, difans, Seigneur, nous defirons de voir Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 22, 'Philippe vint, &amp; le dit à André, &amp; derechef André &amp; Philippe le dirent à Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 23, 'Et Jefus leur refpondit, difant, L\'heure eft venuë que le Fils de l\'homme doit eftre glorifié.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 24, 'En verité, en verité je vous dis, fi le grain de froment tombant en la terre ne meurt point, il demeure feul : mais s\'il meurt, il apporte beaucoup de fruit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 25, 'Qui aime fa vie, il la perdra : &amp; qui hait fa vie en ce monde, il la gardera en vie eternelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 26, 'Si quelqu\'un me fert, qu\'il me fuive : &amp; là où je ferai, là auffi fera celui qui me fert, &amp; fi quelqu\'un me fert, mon Pere l\'honorera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 27, 'Maintenant mon ame eft troublée : &amp; que dirai-je ? Pere, delivre moi de cette heure : mais pour cela fuis-je venu à cette heure.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 28, 'Pere, glorifie ton Nom. Alors une voix vint du ciel, difant, Et je l\'ai glorifié, &amp; derechef je le glorifierai.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 29, 'Et la troupe qui eftoit-là, &amp; qui l\'avoit ouïe, difoit, que c\'eftoit un tonnerre qui avoit efté fait : les autres difoyent, Un Ange a parlé à lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 30, 'Jefus prit la parole, &amp; dit, Cette voix n\'eft point venuë pour moi, mais pour vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 31, 'Maintenant eft le jugement de ce monde : maintenant le prince de ce monde fera jetté dehors.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 32, 'Et moi, fi je fuis enlevé de la terre, je tirerai tous hommes à moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 33, 'Or il difoit cela, fignifiant de quelle mort il devoit mourir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 34, 'Les troupes lui refpondirent, Nous avons entendu par la Loi que le Chrift demeure eternellement : comment donc dis-tu qu\'il faut que le Fils de l\'homme foit enlevé ? Qui eft ce Fils de l\'homme ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 35, 'Alors Jefus leur dit, Encore pour un petit de temps la Lumiere eft avec vous : cheminez tandis que vous avez la Lumiere, de peur que les tenebres ne vous furprennent. Car celui qui chemine en tenebres, ne fçait où il va.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 36, 'Tandis que vous avez la Lumiere, croyez en la Lumiere, afin que vous foyez enfans de Lumiere. Jefus dit ces chofes, puis il s\'en alla, &amp; fe cacha de devant eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 37, 'Et bien qu\'il euft fait tant de fignes devant eux, ils ne creurent point en lui :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 38, 'Afin que la parole d\'Efaïe le Prophete fuft accompli, laquelle il a dite, Seigneur qui a creu à noftre parole, &amp; à qui a efté revelé le bras du Seigneur ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 39, 'C\'eft pourquoi ils ne pouvoyent croire, à caufe que derechef Efaïe dit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 40, 'Il a aveuglé leurs yeux, &amp; a endurci leur cœur, afin qu\'ils ne voyent des yeux, &amp; n\'entendent du cœur, &amp; ne foyent convertis, &amp; que je ne les gueriffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 41, 'Efaïe dit ces chofes, quand il vid la gloire d\'icelui, &amp; parla de lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 42, 'Toutefois plufieurs des principaux mefmes creurent en lui : mais ils ne confeffoyent point à caufe des Pharifiens, de peur qu\'ils ne fuffent jettez hors de la Synagogue.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 43, 'Car ils ont mieux aimé la gloire des hommes que la gloire de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 44, 'Or Jefus s\'efcria, &amp; dit, Qui croit en moi, ne croit point en moi, mais en celui qui m\'a envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 45, 'Et qui me contemple, contemple celui qui m\'a envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 46, 'Je fuis venu au monde pour eftre la lumiere, afin que quiconque croit en moi ne demeure point en tenebres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 47, 'Et fi quelqu\'un oit mes paroles, &amp; ne les croit point, je ne le juge point : car je ne fuis point venu pour juger le monde, mais pour fauver le monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 48, 'Celui qui me rejette, &amp; ne reçoit point mes paroles, il a qui le juge : la parole que j\'ai portée, ce fera celle qui le jugera au dernier jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 49, 'Car je n\'ai point parlé de par moi-mefme, mais le Pere qui m\'a envoyé, m\'a donné commandement de ce que j\'ai à dire &amp; à parler.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 50, 'Et je fçais que fon commandement eft vie eternelle : les chofes donc que je dis, je les dis ainfi que mon Pere m\'a dit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 1, 'OR devant la fefte de Pafque Jefus fçachant que fon heure eftoit venuë pour paffer de ce monde au Pere, comme il avoit aimé les fiens qui eftoyent au monde, il les aima jufqu\'à la fin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 2, 'Et apres avoir foupé, (le diable ayant déja mis au cœur de Judas Ifcariot, fils de Simon, de le trahir.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 3, 'Jefus fçachant que le Pere lui avoit donné toutes chofes entre les mains, &amp; qu\'il eftoit venu de Dieu, &amp; s\'en alloit à Dieu :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 4, 'Se leva du fouper, &amp; ofta fa robe : &amp; ayant pris un linge, il s\'en ceignit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 5, 'Puis il mit de l\'eau en un baffin, &amp; fe prit à laver les pieds de fes difciples, &amp; à les effuyer du linge duquel il eftoit ceint.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 6, 'Il vint donc à Simon Pierre : &amp; Pierre lui dit, Seigneur, me laves-tu les pieds ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 7, 'Jefus refpondit, &amp; lui dit, Tu ne fçais pas maintenant ce que je fais, mais tu le fçauras ci-apres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 8, 'Pierre lui dit, Tu ne me laveras jamais les pieds. Jefus refpondit, Si je ne te lave, tu n\'auras point de part avec moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 9, 'Simon Pierre lui dit, Seigneur, non feulement mes pieds, mais auffi les mains &amp; la tefte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 10, 'Jefus lui dit, Celui qui eft lavé, n\'a befoin finon qu\'on lui lave les pieds, mais eft tout net. Or vous eftes nets, mais non pas tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 11, 'Car il fçavoit lequel c\'eftoit qui le trahiroit : c\'eft pourquoi il dit, vous n\'eftes pas nets tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 12, 'Apres donc qu\'il euft lavé leurs pieds, &amp; repris fes veftemens, &amp; qu\'il fe fuft remis à table, il leur dit, Sçavez-vous bien ce que je vous ai fait ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 13, 'Vous m\'appellez Maiftre &amp; Seigneur, &amp; vous dites bien : car je le fuis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 14, 'Si donc moi, qui fuis le Seigneur &amp; le Maiftre, ai lavé vos pieds, vous auffi devez laver les pieds les uns des autres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 15, 'Car je vous ai donné un exemple, afin qu\'ainfi que je vous ai fait, vous faffiez auffi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 16, 'En verité, en verité je vous dis, Que le ferviteur n\'eft point plus grand que fon maiftre, ni l\'ambaffadeur plus grand que celui qui l\'a envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 17, 'Si vous fçavez ces chofes, vous eftes bien heureux fi vous les faites.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 18, 'Je ne parle point de vous tous : je fçais ceux que j\'ai eleus : mais il faut que l\'Efcriture foit accomplie, difant, Celui qui mange le pain avec moi a levé fon talon contre moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 19, 'Dés maintenant je vous le dis, devant qu\'il advienne : afin que quand il fera advenu, vous croyïez que c\'eft moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 20, 'En verité, en verité je vous dis, Si j\'envoye quelqu\'un, quiconque le reçoit, il me reçoit : &amp; qui me reçoit, il reçoit celui qui m\'a envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 21, 'Quand Jefus eut dit ces chofes, il fut efmeu en esprit : dont il declara, &amp; dit, En verité, en verité je vous dis, que l\'un de vous me trahira.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 22, 'Alors les difciples fe regardoyent les uns les autres, eftans en perplexité duquel il parloit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 23, 'Or il y avoit l\'un des difciples de Jefus, lequel Jefus aimoit, qui eftoit à table au fein d\'icelui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 24, 'Simon Pierre donc lui fit figne, qu\'il demandaft qui eftoit celui duquel il parloit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 25, 'Lui donc eftant encliné fur l\'eftomac de Jefus, lui dit, Seigneur, qui eft-ce ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 26, 'Jefus refpondit, C\'eft celui auquel je baillerai le morceau trempé. Et ayant trempé le morceau, il le bailla à Judas Ifcariot, fils de Simon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 27, 'Et apres le morceau, alors Satan entra en lui. Jefus lui dit donc, Fai bien-toft ce que tu fais.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 28, 'Mais nul de ceux qui eftoyent à table n\'entendit pourquoi il lui avoit dit cela.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 29, 'Car quelques-uns penfoyent, parce que Judas avoit la bourfe, que Jefus lui euft dit, Achete ce qui nous eft neceffaire pour la fefte, ou qu\'il donnaft quelque chofe aux pauvres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 30, 'Apres donc qu\'il eut pris le morceau il partit incontinent. Or il eftoit nuict.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 31, 'Comme donc il fut forti, Jefus dit, Maintenant le Fils de l\'homme eft glorifié, &amp; Dieu eft glorifié en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 32, 'Si Dieu eft glorifié en lui, Dieu auffi le glorifiera en foi-mefme, &amp; incontinent il le glorifiera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 33, 'Mes petits enfans, je fuis encore pour un peu avec vous : vous me chercherez : mais comme j\'ai dit aux Juifs, que là où je vais ils ne pouvoyent venir, je vous le dis auffi maintenant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 34, 'Je vous donne un nouveau commandement, que vous vous aimiez l\'un l\'autre : voire que comme je vous ai aimez, vous vous aimiez auffi l\'un l\'autre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 35, 'Par cela tous connoiftront que vous eftes mes difciples, fi vous avez de l\'amour l\'un pour l\'autre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 36, 'Simon Pierre lui dit, Seigneur, où vas-tu ? Jefus lui refpondit, Là où je m\'en vis, tu ne me peux maintenant fuivre, mais tu me fuivras ci-apres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 37, 'Pierre lui dit, Seigneur, pourquoi ne te puis-je maintenant fuivre ? Je mettrai ma vie pour toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 38, 'Jefus lui refpondit, Tu mettras ta vie pour moi ? En verité, en verité je te dis, que le coq ne chantera point, jufques à ce que tu m\'ayes renié trois fois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 1, 'VOSTRE cœur ne foit point troublé : vous croyez en Dieu : croyez auffi en moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 2, 'Il y a plufieurs demeures en la maifon de mon Pere : s\'il eftoit autrement, je vous l\'euffe dit. Je vais vous apprefter lieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 3, 'Et quand je m\'en ferai allé, &amp; vous aurai preparé le lieu, je retournerai derechef, &amp; vous recevrai à moi, afin que là où je fuis, vous foyez auffi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 4, 'Et vous fçavez là où je vais, &amp; fçavez le chemin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 5, 'Thomas lui dit, Seigneur, nous ne fçavons là où tu vas : comment donc pouvons-nous fçavoir le chemin ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 6, 'Jefus lui dit, Je fuis le chemin, &amp; la verité, &amp; la vie : nul ne vient au Pere finon par moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 7, 'Si vous me connoiffiez, vous connoiftriez auffi mon Pere : &amp; dés maintenant vous le connoiffez, &amp; vous l\'avez veu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 8, 'Philippe lui dit, Seigneur, montre nous le Pere, &amp; il nous fuffit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 9, 'Jefus lui refpondit, Je fuis depuis fi long-tems avec vous, &amp; tu ne m\'as point connu ? Philippe, qui m\'a veu, il a veu mon Pere : &amp; comment dis-tu, Montre nous le Pere ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 10, 'Ne crois-tu point que je fuis au Pere, &amp; le Pere en moi ? Les paroles que je vous dis, je ne les dis point de par moi-mefme : mais le Pere qui demeure en moi, c\'eft celui qui fait les œuvres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 11, 'Croyez moi que je fuis en mon Pere, &amp; le Pere en moi ; finon, croyez moi pour ces œuvres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 12, 'En verité, en verité je vous dis, qui croit en moi, celui-là auffi fera les œuvres que je fais, &amp; en fera de plus grandes que celles-ci : car je m\'en vais au Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 13, 'Et quoi que vous demandiez en mon Nom, je le ferai : afin que le Pere foit glorifié par le Fils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 14, 'Si vous demandez en mon Nom quelque chofe, je le ferai');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 15, 'Si vous m\'aimez, gardez mes commandemens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 16, 'Je prierai le Pere, &amp; il vous donnera un autre Confolateur, pour demeure avec vous eternellement :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 17, 'Affavoir l\'Efprit de verité, que le monde ne peut recevoir, parce qu\'il ne le voit point &amp; ne le connoit point : mais vous le connoiffez ; car il demeure avec vous, &amp; fera en vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 18, 'Je ne vous laifferai point orphelins : je viendrai vers vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 19, 'Encore un peu, &amp; le monde ne me verra plus, mais vous me verrez ; parce que je vis, vous auffi vivrez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 20, 'En ce jour-là vous connoiftrez que je fuis en mon Pere, &amp; vous en moi, &amp; moi en vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 21, 'Qui a mes commandemens, &amp; les garde, c\'eft celui qui m\'aime : &amp; celui qui m\'aime, il fera aimé de mon Pere, &amp; je l\'aimerai, &amp; me declarerai à lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 22, 'Jude (non pas Ifcariot) lui dit, Seigneur, d\'où vient que tu te declareras à nous, &amp; non point au monde ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 23, 'Jefus refpondit, &amp; lui dit, Si quelqu\'un m\'aime, il gardera ma parole : &amp; mon Pere l\'aimera ; &amp; nous viendrons à lui, &amp; ferons noftre demeure chez lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 24, 'Qui ne m\'aime point, il ne garde point mes paroles. Et la parole que vous oyez n\'eft point mienne, mais du Pere qui m\'a envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 25, 'Je vous ai dit ces chofes demeurant avec vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 26, 'Mais le Confolateur qui, est le Saint Efprit, que le Pere envoyera en mon Nom, vous enfeignera toutes chofes, &amp; vous reduira en memoire toutes les chofes que je vous ai dites.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 27, 'Je vous laiffe la paix, je vous donne ma paix : &amp; je ne vous la donne point comme le monde la donne. Voftre cœur ne foit point troublé, &amp; ne foit point craintif.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 28, 'Vous avez ouï que je vous ai dit, Le m\'en vais, &amp; je m\'en viens à vous. Si vous m\'aimez, vous feriez certes joyeux de ce que j\'ai dit, Je m\'en vais au Pere : car le Pere eft plus grand que moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 29, 'Et maintenant je vous l\'ai dit devant qu\'il foit avenu, afin que quand il fera avenu vous croyïez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 30, 'Je ne parlerai plus gueres avec vous : car le prince de monde vient, &amp; n\'a rien en moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 31, 'Mais c\'est afin que le monde connoiffe que j\'aime le Pere, &amp; fais ainfi que le Pere m\'a commandé. Levez-vous, partons d\'ici.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 1, 'JE fuis le vrai fep, &amp; mon Pere eft le vigneron.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 2, 'Il taille tout farment qui ne porte point de fruit en moi : &amp; efmonde celui qui porte du fruit, afin qu\'il porte plus de fruit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 3, 'Vous eftes déja nets pour la parole que je vous ai dite.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 4, 'Demeurez en moi, &amp; moi en vous. Comme le farment ne peut de lui-mefme porter de fruit, s\'il ne demeure au fep : ni vous auffi femblablement, fi vous ne demeurez en moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 5, 'Je fuis le cep, &amp; vous en estes les farmens. Celui qui demeure en moi, &amp; moi en lui, porte beaucoup de fruit : car hors de moi, vous ne pouvez rien faire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 6, 'Si quelqu\'un ne demeure en moi, il eft jetté hors comme le farment, &amp; fe feche : puis on l\'amaffe, &amp; on le met au feu, &amp; il brufle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 7, 'Si vous demeurez en moi, &amp; mes paroles demeurent en vous, demandez tout ce que vous voudrez, &amp; il vous fera fait.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 8, 'En cela mon Pere eft glorifié ; que vous apportiez beaucoup de fruit : &amp; alors vous deviendrez mes difciples.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 9, 'Comme le Pere m\'a aimé, ainfi auffi je vous ai aimez : demeurez en mon amour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 10, 'Si vous gardez mes commandemens, vous demeurerez en mon amour : comme j\'ai gardé les commandemens de mon Pere, &amp; je demeure en fon amour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 11, 'Je vous ai dit ces chofes, afin que ma joye demeure en vous, &amp; que voftre joye foit accomplie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 12, 'C\'eft ici mon commandement, que vous vous aimiez l\'un l\'autre, comme je vous ai aimez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 13, 'Nul n\'a plus grande amour que celle-ci, affavoir quand quelqu\'un met fon ame pour fes amis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 14, 'Vous ferez mes amis, fi vous faites tout ce que je vous commande.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 15, 'Je ne vous appelle plus ferviteurs : car le ferviteur ne fçait ce que fon maiftre fait : mais je vous ai nommé mes amis, parce que je vous ai fait connoître tout ce que j\'ai ouï de mon Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 16, 'Ce n\'eft point vous qui m\'avez éleu, mais c\'eft moi qui vous ai éleus &amp; qui vous ai eftablis, afin que vous alliez, &amp; apportiez du fruit, &amp; que voftre fruit foit permanent : afin que tout ce que vous demanderez au Pere en mon Nom, il vous le donne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 17, 'Je vous commande ces chofes, afin que vous vous aimiez l\'un l\'autre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 18, 'Si le monde vous a en haine, fçachez qu\'il m\'a eu en haine premier que vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 19, 'Si vous euffiez efté du monde, le monde aimeroit ce qui feroit fien : or parce que vous n\'eftes point du monde, mais que je vous ai éleus du monde, pourtant le monde vous a en haine.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 20, 'Souvenez-vous de la parole que je vous ai dite, Que le ferviteur n\'eft point plus grand que fon maiftre. S\'ils m\'ont perfecuté, auffi vous perfecuteront-ils : s\'ils ont gardé ma parole, auffi garderont-ils la voftre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 21, 'Mais ils vous feront toutes ces chofes à caufe de mon Nom : parce qu\'ils ne connoiffent point celui qui m\'a envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 22, 'Si je ne fuffe venu, &amp; n\'euffe parlé à eux, ils n\'auroyent point de peché : mais maintenant ils n\'ont point d\'excufe de leur peché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 23, 'Celui qui m\'a en haine, il a auffi en haine mon Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 24, 'Si je n\'euffe fait entr\'eux les œuvres que nul autre n\'a faites ; ils n\'auroyent point de peché : mais maintenant ils les ont veuës, &amp; ont eu en haine &amp; moi &amp; mon Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 25, 'Mais c\'eft afin que foit accomplie la parole qui eft écrite en leur Loi, Ils m\'ont eu en haine fans caufe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 26, 'Mais quand le Confolateur fera venu, lequel je vous envoyerai de par mon Pere, affavoir l\'Efprit de verité, qui procede de mon Pere, celui-là témoignera de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 27, 'Et vous auffi en témoignerez : car vous eftes dés le commencement avec moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 1, 'JE vous ai dit ces chofes, afin que vous ne foyez point fcandalizez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 2, 'Ils vous chafferont hors des Synagogues : mefmes le temps vient que quiconque vous fera mourir, penfera faire fervice à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 3, 'Et ils vous feront ces chofes, parce qu\'ils n\'ont point connu le Pere ni moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 4, 'Mais je vous ai dit ces chofes, afin que quand l\'heure fera venuë, il vous fouvienne que je vous les ai dites : toutefois je ne vous ai point dit ces chofes dés le commencement, parce que j\'eftois avec vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 5, 'Et maintenant je m\'en vais à celui qui m\'a envoyé, &amp; perfonne de vous ne me demande, Où vas-tu ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 6, 'Mais parce que je vous ai dit ces chofes, la trifteffe a rempli voftre cœur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 7, 'Toutefois je vous dis la verité, il vous eft expedient que je m\'en aille : car fi je ne m\'en vais, le Confolateur ne viendra point à vous : &amp; fi je m\'en vais, je vous l\'envoyerai.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 8, 'Et quand il fera venu, il convaincra le monde de peché, de juftice, &amp; de jugement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 9, 'De peché, parce qu\'ils ne croyent point en moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 10, 'De juftice, parce que je m\'en vais à mon Pere, &amp; que vous ne me verrez plus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 11, 'De jugement, parce que le prince de ce monde eft déja jugé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 12, 'J\'ai à vous dire encore plufieurs chofes, mais vous ne les pouvez porter maintenant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 13, 'Mais quand celui-là fera venu, affavoir l\'Efprit de verité, il vous conduira en toute verité, il vous parlera point de par foi-mefme : mais il dira tout ce qu\'il aura ouï, &amp; vous annoncera les chofes à venir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 14, 'Celui-là me glorifiera : car il prendra du mien, &amp; vous l\'annoncera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 15, 'Tout ce qu\'a mon Pere, eft mien, c\'eft pourquoi j\'ai dit qu\'il prendra du mien, &amp; qu\'il vous l\'annoncera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 16, 'Un petit de temps, &amp; vous ne me verrez point : &amp; derechef un petit de temps, &amp; vous me verrez : car je m\'en vais à mon Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 17, 'Dont quelques-uns de fes difciples dirent entr\'eux, Qu\'eft-ce qu\'il nous dit ? Un petit de temps, &amp; vous ne me verrez point : &amp; derechef un petit de temps, &amp; vous me verrez : car je m\'en vais à mon Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 18, 'Ils difoyent donc, Qu\'eft-ce qu\'il dit, Un petit de temps ; nous ne fçavons ce qu\'il dit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 19, 'Et Jefus connoiffant qu\'ils le vouloyent interroger, leur dit, Vous demandez entre vous touchant ce que j\'ai dit, Un petit de temps, &amp; vous ne me verrez plus : &amp; derechef un petit de temps, &amp; vous me verrez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 20, 'En verité, en verité je vous dis, que vous pleurerez &amp; lamenterez, &amp; le monde s\'éjouïra : voire que vous ferez contriftez : mais voftre trifteffe fera convertie en joye.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 21, 'Quand la femme enfante, elle fent fes douleurs, parce que font terme eft venu : mais apres qu\'elle a fait un petit enfant, il ne lui fouvient plus de l\'angoiffe, pour la joye qu\'elle a qu\'une creature humaine eft née au monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 22, 'Vous donc auffi avez maintenant de la trifteffe : mais je vous verrai derechef, &amp; voftre cœur s\'éjouïra, &amp; perfonne ne vous oftera voftre joye.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 23, 'Et en ce jour-là vous ne m\'interrogerez de rien. En verité, en verité je vous dis, que toutes les chofes que vous demanderez au Pere en mon Nom, il vous les donnera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 24, 'Jufques à prefent vous n\'avez rien demandé en mon Nom : demandez, &amp; vous recevez, afin que voftre joye foit accomplie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 25, 'Je vous ai dit ces chofes par fimilitudes : mais l\'heure vient que je ne parlerai plus à vous par fimilitudes : mais je vous parlerai ouvertement de mon Pere :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 26, 'En ce jour-là vous demanderez en mon Nom ; &amp; je ne vous dis point que je prierai le Pere pour vous :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 27, 'Car le Pere lui-mefme vous aime, parce que vous m\'avez aimés &amp; que vous avez creu que je fuis iffu de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 28, 'Je fuis iffu du Pere, &amp; fuis venu au monde : derechef je laiffe le monde, &amp; m\'en vais au Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 29, 'Ses difciples lui dirent, Voici maintenant tu parles ouvertement, &amp; ne dis nulle fimilitude.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 30, 'Maintenant nous fçavons que tu fçais toutes chofes, &amp; n\'as pas befoin qu\'aucun t\'interroge : par cela nous croyons que tu es iffu de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 31, 'Jefus refpondit, Croyez-vous maintenant ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 32, 'Voici, l\'heure vient, &amp; eft déja venuë, que vous ferez efpars chacun à part foi, &amp; me delaifferez feul : mais je ne fuis point feul : car le Pere eft avec moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 33, 'Je vous ai dit ces chofes, afin que vous ayez paix en moi : vous aurez de l\'angoiffe au monde, mais ayez bon courage, j\'ai vaincu le monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 1, 'JESUS dit ces chofes : puis levant les yeux au ciel, il dit, Pere, l\'heure eft venuë, glorifie ton Fils, afin que ton Fils te glorifie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 2, 'Comme tu lui as donné puiffance fur toute chair, afin qu\'il donne la vie eternelle à tous ceux que tu lui as donnez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 3, 'Et c\'eft ici la vie eternelle, Qu\'ils te connoiffent feul vrai Dieu, &amp; celui que tu as envoyé, Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 4, 'Je t\'ai glorifié fur la terre, j\'ai achevé l\'œuvre que tu m\'as baillée à faire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 5, 'Et maintenant glorifie moi, toi Pere, envers toi-mefme, de la gloire que j\'ai euë par devers toit devant que le monde fuft fait.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 6, 'J\'ai manifefté ton Nom aux hommes, lefquels tu m\'as donnez du monde : ils eftoyent tiens, &amp; tu me les as donnez, &amp; ils ont gardé ta parole.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 7, 'Maintenant ils ont connu que tout ce que tu m\'as donné eft de toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 8, 'Car je leur ai donné les paroles que tu m\'as données &amp; ils les ont receuës, &amp; ont vrayement connu que je fuis iffu de toi, &amp; ont creu que tu m\'as envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 9, 'Je prie pour eux : je ne prie point pour le monde, mais pour ceux que tu m\'as donnez : dautant qu\'ils font tiens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 10, 'Et tout ce qui eft mien, eft tien : &amp; ce qui eft tien, eft mien : &amp; je fuis glorifié en eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 11, 'Et maintenant je ne fuis plus au monde : mais ceux-ci font au monde, &amp; je viens à toi, Pere Saint ; garde les en ton Nom, voire ceux que tu m\'as donnez, afin qu\'ils foyent un, ainfi que nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 12, 'Quand j\'eftois avec eux au monde, je les gardois en ton Nom : j\'ai gardé ceux que tu m\'as donnez, &amp; pas un d\'eux n\'eft peri, finon le fils de perdition, afin que l\'Efcriture fuft accomplie,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 13, 'Et maintenant je viens à toi, &amp; dis ces chofes eftant encore au monde, afin qu\'ils ayent ma joye accomplie en eux-mefmes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 14, 'Je leur ai donné ta parole, &amp; le monde les a eus en haine, parce qu\'ils ne font point du monde, comme auffi je ne fuis point du monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 15, 'Je ne prie point que tu les oftes du monde, mais que tu les gardes de mal.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 16, 'Ils ne font point du monde, comme auffi je ne fuis point du monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 17, 'Sanctifie-les par ta verité : ta parole eft verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 18, 'Comme tu m\'as envoyé au monde, ainfi auffi je les ai envoyez au monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 19, 'Et pour eux je me fanctifie moi-mefme, afin qu\'eux auffi foyent fanctifiez en verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 20, 'Or je ne prie point feulement pour eux, mais auffi pour ceux qui croiront en moi par leur parole.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 21, 'Afin que tous foyent un ; ainfi que toi, Pere, es en moi, &amp; moi en toi, afin qu\'eux auffi foyent un en nous : à ce que le monde croye que c\'eft toi qui m\'as envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 22, 'Et moi auffi je leur ai donné la gloire que tu m\'as donnée : afin qu\'ils foyent un, comme nous fommes un.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 23, 'Je fuis en eux, &amp; toi en moi, afin qu\'ils foyent confommez en un, &amp; que le monde connoiffe que c\'eft toi qui m\'as envoyé, &amp; que tu les aimes, ainfi que tu m\'as aimé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 24, 'Pere, mon defir eft touchant ceux que tu m\'as donnez, que là où je fuis, ils foyent auffi avec moi : afin qu\'ils contemplent ma gloire, laquelle tu m\'as donnée : dautant que tu m\'as aimé devant la fondation du monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 25, 'Pere jufte, le monde ne t\'a point connu, mais moi je t\'ai connu, &amp; ceux-ci ont connu que c\'eft toi qui m\'as envoyé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 26, 'Et je leur ai fait connoiftre ton Nom, &amp; le leur ferai connoiftre, afin que l\'amour duquel tu m\'as aimé, foit en eux, &amp; moi en eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 1, 'APRES que Jefus eut dit ces chofes, il s\'en alla outre le torrent de Cedron, où il y avoit un jardin, auquel il entra avec ses difciples.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 2, 'Or Judas, qui le trahiffoit, fçavoit auffi le lieu : car Jefus s\'eftoit fouvent affemblé là avec ses difciples.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 3, 'Judas donc ayant pris une bande de foldats, &amp; des fergeans, de par les principaux Sacrificateurs &amp; de par les Pharifiens, s\'en vint là avec lanternes, &amp; flambeaux, &amp; armes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 4, 'Or Jefus fçachant toutes les chofes qui lui devoyent advenir, s\'avançant leur dit, Qui cherchez-vous ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 5, 'Ils lui refpondirent, Jefus le Nazarien. Jefus leur dit, C\'eft moi. Et Judas, qui le trahiffoit, eftoit auffi avec eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 6, 'Apres donc qu\'il leur eut dit, C\'eft moi, ils s\'en allerent à la renverfe, &amp; cheurent par terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 7, 'Derechef donc il les interrogea, Qui cherchez-vous ? Et ils refpondirent, Jefus le Nazarien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 8, 'Jefus refpondit, Je vous ai dit que c\'eft moi : fi donc vous me cherchez, laiffez aller ceux-ci.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 9, 'C\'eftoit afin que la parole qu\'il avoit dite fuft accomplie, Je n\'ai perdu pas un de ceux que tu m\'as donnez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 10, 'Or Simon Pierre ayant une efpée, la tira, &amp; frappa le ferviteur du Souverain Sacrificateur, &amp; lui coupa l\'oreille droite ; &amp; ce ferviteur-là avoit nom Malchus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 11, 'Jefus donc dit à Pierre, Remets ton efpée au fourreau. Ne boirai-je pas la coupe que le Pere m\'a donnée ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 12, 'Alors la bande, &amp; le capitaine, &amp; les fergeans des Juifs empoignerent Jefus, &amp; le lierent :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 13, 'Et l\'emmenerent premierement à Anne : car il eftoit beau-pere de Caïphe, qui eftoit le fouverain Sacrificateur de cette année-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 14, 'Or Caïphe eftoit celui qui avoit donné confeil aux Juifs, qu\'il eftoit expedient qu\'un homme mourût pour le peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 15, 'Or Simon Pierre, avec un autre difciple, fuivoit Jefus. Et ce difciple eftoit connu du fouverain Sacrificateur ; &amp; il entra avec Jefus en la fale du fouverain Sacrificateur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 16, 'Mais Pierre eftoit dehors à la porte. L\'autre difciple donc, qui eftoit connu du fouverain Sacrificateur, fortit dehors, &amp; parla à la portiere, laquelle fit entrer Pierre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 17, 'La fervante donc, qui eftoit la portiere, dit à Pierre, n\'es-tu point auffi des difciples de cet homme ? Il dit, Je n\'en fuis point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 18, 'Et les ferviteurs &amp; les fergeans, ayant fait du brafier, eftoyent là, parce qu\'il faifoit froid, &amp; fe chauffoyent. Pierre auffi eftoit avec eux, &amp; fe chauffoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 19, 'Le fouverain Sacrificateur donc interrogea Jefus touchant fes difciples, &amp; touchant fa doctrine.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 20, 'Jefus lui refpondit, J\'ai ouvertement parlé au monde, j\'ai toûjours enfeigné en la Synagogue &amp; au temple, où les Juifs s\'affemblent toûjours, &amp; je n\'ai rien dit en cachette.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 21, 'Pourquoi m\'interroges-tu ? Interroge ceux qui ont ouï ce que je leur ai dit : voila, ceux-là fçavent ce que j\'ai dit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 22, 'Quand il eut dit ces chofes, un des fergeans qui eftoit prefent, bailla un coup de fa verge à Jefus, difant, Eft-ce ainfi que tu refpons au fouverain Sacrificateur ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 23, 'Jefus lui refpondit, Si j\'ai mal parlé, ren tefmoignage du mal : &amp; fi j\'ay bien dit, pourquoi me frappes-tu ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 24, 'Or Anne l\'avoit envoyé lié à Caïphe Souverain Sacrificateur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 25, 'Et Simon Pierre eftoit là, &amp; fe chauffoit : alors ils lui dirent, N\'es-tu pas auffi de fes difciples ? Lui le nia, &amp; dit, Je n\'en fuis point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 26, 'Et l\'un des ferviteurs du fouverain Sacrificateur, parent de celui à qui Pierre avoit coupé l\'oreille, dit, Ne t\'ai-je pas veu au jardin avec lui ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 27, 'Pierre donc le nia derechef, &amp; incontinent le coq chanta.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 28, 'Puis apres ils menerent Jefus de Caïphe au pretoire : or c\'eftoit au matin, &amp; ils n\'entrerent point au Pretoire, de peur qu\'ils ne fuffent foüillez, mais afin qu\'ils peuffent manger l\'agneau de Pafque.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 29, 'Pilate donc fortit vers eux, &amp; dit, Quelle accufation apportez-vous contre cet homme-ci ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 30, 'Il refpondirent, &amp; lui dirent, Si celui-ci n\'eftoit malfaiteur, nous ne te l\'euffions pas livré.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 31, 'Alors Pilate leur dit, Prenez-le vous, &amp; le jugez felon voftre Loi. Alors les Juifs lui dirent, Il ne nous eft pas permis de mettre aucun à mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 32, 'Et ce fut afin que la parole de Jefus fuft accomplie, laquelle il avoit dite, fignifiant de quelle mort il devoit mourir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 33, 'Pilate donc entra derechef au Pretoire, &amp; appella Jefus, &amp; lui dit, Es-tu le Roi des Juifs ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 34, 'Jefus lui refpondit, Dis-tu ceci de par toi-mefme, ou fi les autres te l\'ont dit de moi ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 35, 'Pilate refpondit, Suis-je Juif ? Ta nation &amp; les principaux Sacrificateurs t\'ont livré à moi : qu\'as-tu fait ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 36, 'Jefus refpondit, Mon regne n\'eft point de ce monde : fi mon regne eftoit de ce monde, mes gens combattroyent, afin que je ne fuffe point livré aux Juifs : mais maintenant mon regne n\'eft point d\'ici bas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 37, 'Alors Pilate lui dit, Es-tu donc Roi ? Jefus refpondit, Tu le dis que je fuis Roi : je fuis né pour cela : &amp; pour cela je fuis venu au monde, afin que je rende tefmoignage à la verité. Quiconque eft de verité, oit ma voix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 38, 'Pilate lui dit, Qu\'eft-ce que verité ? Et quand il eut dit cela, il fortit derechef vers les Juifs, &amp; leur dit, Je ne trouve aucun crime en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 39, 'Or vous avez une couftume, que je vous en delivre un à la fefte de Pafque : voulez-vous donc que je vous delivre le Roi des Juifs ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 40, 'Alors tous derechef s\'écrierent, difans, Non pas celui-ci, mais Barrabas. Or Barrabas eftoit un brigand.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 1, 'ALORS donc Pilate prit Jefus, &amp; le foüetta.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 2, 'Et les foldats plierent une couronne d\'efpines, qu\'ils mirent fur fa tefte, &amp; le veftirent d\'un veftement de pourpre :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 3, 'Et difoyent, Roi des Juifs, bien te foit : Et lui donnoyent des coups de leurs verges.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 4, 'Cela eftant fait, Pilate forit derechef dehors, &amp; leur dit, Voici, je vous l\'amene dehors, afin que vous connoiffiez que je ne trouve aucun crime en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 5, 'Jefus donc forit dehors portant la couronne d\'efpines, &amp; le veftement de pourpre : &amp; Pilate leur dit, Voici l\'homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 6, 'Mais quand les principaux Sacrificateurs &amp; les fergens le virent, ils s\'écrierent, difans, Crucifie, crucifie. Pilate leur dit, prenez-le vous mefmes, &amp; le crucifiez : car je ne trouve point de crime en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 7, 'Les Juifs lui refpondirent, Nous avons une Loi, &amp; felon noftre Loi il doit mourir : car il s\'eft fait le Fils de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 8, 'Quand donc Pilate eut ouï cette parole, il craignit plus fort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 9, 'Et il entra derechef au pretoire, &amp; dit à Jefus, D\'où es-tu ? Et Jefus ne lui donna point de refponfe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 10, 'Alors Pilate lui dit, Ne parles-tu point à moi ? Ne fçais-tu pas que j\'ai puiffance de te crucifier, &amp; puiffance de te delivrer ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 11, 'Jefus refpondit, Tu n\'aurois puiffance quelconque fur moi, s\'il ne t\'eftoit donné d\'en haut : pour cette caufe celui qui m\'a livré à toi, à plus grand peché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 12, 'Depuis cela Pilate tafchoit à le delivrer : mais les Juifs crioyent, difans, Si tu delivres celui-ci, tu n\'es point ami de Cefar : car quiconque fe fait Roi, contredit à Cefar.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 13, 'Et quand Pilate eut ouï cette parole, il amena Jefus dehors, &amp; s\'affit au fiege judicial, au lieu appellé Pavement, &amp; en Hebreu Gabatha.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 14, 'Or il eftoit alors la preparation de Pafque, environ fix heures : &amp; Pilate dit aux Juifs, Voici voftre Roi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 15, 'Mais ils crioyent, Ofte, ofte, crucifie-le. Pilate leur dit, Crucifierai-je voftre Roi ? Les principaux Sacrificateurs refpondirent, Nous n\'avons point de Roi finon Cefar.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 16, 'Alors donc il le leur livra pour eftre crucifié. Ils prirent donc Jefus, &amp; l\'emmenerent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 17, 'Et lui portant fa croix vint en la place qu\'on appelle la place du Teft, &amp; en Hebreu Golgotha :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 18, 'Où ils le crucifierent, &amp; avec lui deux autres ; l\'un deça, &amp; l\'autre delà, &amp; Jefus au milieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 19, 'Or Pilate écrivit un écriteau, &amp; le mit fur la croix : où il y avoit en écrit, Jesus Nazarien le Roi des Juifs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 20, 'Plufieurs donc des Juifs leurent cet écriteau, dautant que ce lieu, où Jefus eftoit crucifié, eftoit prés de la ville : &amp; il eftoit écrit en Hebreu, en Grec, &amp; en Latin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 21, 'C\'eft pourquoi les principaux Sacrificateurs des Juifs dirent à Pilate, n\'écri point, Le Roi des Juifs : mais que celui-ci a dit, Je fuis le Roi des Juifs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 22, 'Pilate refpondit, Ce que j\'ai écrit, je l\'ai écrit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 23, 'Or quand les foldats eurent crucifié Jefus, ils prirent fes veftemens, &amp; en firent quatre parties, à chaque foldat une partie : ils prirent auffi le faye : mais le faye eftoit fans coufture, tiffu depuis le haut tout au long.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 24, 'Dont ils dirent entr\'eux, Ne le mettons point en pieces, mais jettons le fort deffus à qui il fera. Et cela afin que l\'Efcriture fuft accomplie, difant, Ils ont partagé mes veftemens entr\'eux, &amp; ont jetté le fort fur mon habillement. Les foldats donc firent ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 25, 'Or là prés de la croix de Jefus eftoit fa mere, &amp; la fœur de fa mere Marie femme de Cleophas, &amp; Marie Magdeleine.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 26, 'Et Jefus voyant fa mere, &amp; le difciple qu\'il aimoit, eftre là, dit à fa mere, Femme, voila ton fils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 27, 'Puis il dit au difciple, Voila ta mere. Et dés cette heure-là le difciple la receut chez foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 28, 'Puis apres Jefus fçachant que toutes chofes eftoient déja accomplies, afin que l\'Efcriture fuft accomplie, dit, J\'ai foif.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 29, 'Et il y avoit là un vaiffeau plein de vinaigre. Ils emplirent donc de vinaigre une éponge, &amp; la mirent à l\'entour de l\'hyffope, &amp; la lui prefenterent à la bouche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 30, 'Et quand Jefus eut pris le vinaigre, il dit, Tout eft accompli. Et ayant baiffé la tefte il rendit l\'efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 31, 'Alors les Juifs, afin que les corps ne demeuraffent point en croix au jour du Sabbat, parce qu\'alors eftoit le jour de la preparation, (car c\'eftoit le grand jour de ce Sabbat-là) prierent Pilate qu\'on leur rompift les jambes, &amp; qu\'on les oftaft.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 32, 'Les foldats donc vinrent, &amp; rompirent les jambes du premier, &amp; de l\'autre qui eftoit crucifié avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 33, 'Mais quand ils vinrent à Jefus, &amp; virent qu\'il eftoit déja mort, ils ne lui rompirent point les jambes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 34, 'Mais l\'un des foldats lui perça le cofté, avec une lance, &amp; incontinent il en fortit du fang &amp; de l\'eau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 35, 'Et celui qui l\'a veu, l\'a tefmoigné, &amp; fon tefmoignage eft digne de foi : &amp; celui-là fçait qu\'il dit vrai, afin que vous le croyïez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 36, 'Car ces chofes-là font advenuës, afin que l\'Efcriture fuft accomplie, Pas un de fes os ne fera caffé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 37, 'Et encore une autre Efcriture dit, Ils verront celui qu\'ils ont percé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 38, 'Or apres ces chofes, Jofeph d\'Arimathée (qui eftoit difciple de Jefus, fecret toutefois pour la crainte des Juifs) pria Pilate qu\'il lui permift d\'ofter le corps de Jefus : &amp; Pilate le lui permit. Il vint donc, &amp; prit le corps de Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 39, 'Nicodeme auffi (celui qui eftoit premierement venu de nuit à Jefus) y vint, apportant une mixtion de myrrhe &amp; d\'aloës, environ cent livres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 40, 'Alors ils prirent le corps de Jefus, &amp; le banderent de linges avec des fenteurs aromatiques, comme c\'eft la couftume des Juifs d\'enfevelir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 41, 'Or il y avoit au lieu qu\'il fut crucifié un jardin, &amp; au jardin un fepulcre neuf, auquel perfonne n\'avoit encore efté mis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 42, 'Ils mirent donc là Jefus à caufe du jour de la preparation des Juifs, parce que le fepulcre eftoit prés.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 1, 'OR le premier jour de la femaine, Marie Magdeleine vint le matin au fepulcre, comme il faifoit encore obfur : &amp; vid que la pierre eftoit oftée du fepulcre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 2, 'Alors elle courut, &amp; vint à Simon Pierre, &amp; à l\'autre difciple que Jefus aimoit, &amp; leur dit, On a enlevé le Seigneur du fepulcre, &amp; nous ne fçavons où on l\'a mis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 3, 'Alors Pierre partit, &amp; l\'autre difciple, &amp; ils s\'en vinrent au fepulcre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 4, 'Et couroyent eux deux enfemble : mais l\'autre difciple couroit plus vifte que Pierre, &amp; vint le premier au fepulcre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 5, 'Et s\'eftant baiffé il vid bien les linges mis à cofté : toutefois il n\'y entra point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 6, 'Alors Simon Pierre vint, le fuivant, &amp; entra au fepulcre, &amp; vid les linges mis à cofté,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 7, 'Et le couvrechef qui avoit efté fur fa tefte, non point mis avec les linges, mais enveloppé en un lieu à part.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 8, 'Alors l\'autre difciple, qui eftoit venu le premier au fepulcre, y entra auffi, &amp; le vid, &amp; creut.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 9, 'Car ils ne fçavoyent point encore l\'Efcriture, qu\'il faloit qu\'il reffufcitaft des morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 10, 'Les difciples donc s\'en allerent derechef chez eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 11, 'Mais Marie fe tenoit prés du fepulcre dehors en pleurant. Comme donc elle pleuroit, elle fe baiffa dans le fepulcre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 12, 'Et elle vid deux Anges veftus de blanc, affis l\'un à la tefte, &amp; l\'autre aux pieds, là où gifoit auparavant le corps de Jefus,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 13, 'Et ils lui dirent, Femme, pourquoi pleures-tu ? Elle leur dit, Parce qu\'on a en levé mon Seigneur : &amp; je ne fçais où on l\'a mis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 14, 'Et quand elle eut dit cela, en fe retournant en arriere, elle vid Jefus qui eftoit là : &amp; elle ne fçavoit point que c\'eftoit Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 15, 'Jefus lui dit, Femme, pourquoi pleures-tu ? qui cherches-tu ? Elle penfant que ce fuft le jardinier, lui dit, Seigneur, fi tu l\'as emporté, dis moi où tu l\'as mis, &amp; je l\'ofterai.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 16, 'Jefus lui dit, Marie. Elle s\'eftant retournée, lui dit, Rabboni, qui eft à dire, Maiftre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 17, 'Jefus lui dit, Ne me touche point : car je ne fuis point encore monté à mon Pere : mais va à mes freres, &amp; leur dit : Je monte à mon Pere, &amp; à voftre Pere, &amp; à mon Dieu, &amp; à voftre Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 18, 'Marie Magdeleine vint, annonçant aux difciples qu\'elle avoit veu le Seigneur, &amp; qu\'il lui avoit dit ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 19, 'Et quand le foir de ce jour-là fut venu, qui eftoit le premier de la femaine, &amp; que les portes du lieu où les difciples eftoyent affemblez, pour la crainte qu\'ils avoyent des Juifs, eftoyent fermées, Jefus vint, &amp; fut là au milieu d\'eux, &amp; leur dit : Paix vous foit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 20, 'Et quand il leur eut dit cela, il leur montra fes mains &amp; fon cofté. Alors les difciples s\'éjouïrent, quand ils eurent veu le Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 21, 'Et leur dit derechef, Paix vous foit : comme mon Pere m\'a envoyé, ainfi auffi je vous envoye.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 22, 'Et quand il eut dit cela, il fouffla fur eux, &amp; leur dit, Recevez le fainct Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 23, 'A quiconque vous pardonnerez les pechez, ils feront pardonnez : &amp; à quiconque vous les retiendrez, ils feront retenus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 24, 'Or Thomas l\'un des douze, appellé Didyme, n\'eftoit point avec eux quand Jefus vint.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 25, 'Dont les autres difciples lui dirent, Nous avons veu le Seigneur. Mais il leur dit, Si je ne vois les enfeignes des cloux en fes mains, &amp; fi je ne mets mon doigt là où eftoyent les cloux, &amp; fi je ne mets ma main en fon cofté, je ne le croirai point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 26, 'Et huict jours apres, fes difciples eftoyent derechef là dedans, &amp; Thomas avec eux. Alors Jefus vint, les portes eftant fermées, &amp; fut là au milieu d\'eux &amp; leur dit : Paix vous foit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 27, 'Puis il dit à Thomas, Mets ton doigt ici, &amp; regarde mes mains : avance auffi ta main, &amp; la mets en mon cofté : &amp; ne fois point incredule, mais fidele.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 28, 'Et Thomas refpondit &amp; lui dit : Mon Seigneur &amp; mon Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 29, 'Jefus lui dit, parce que tu m\'as veu, Thomas, tu as creu : bien-heureux font ceux qui n\'ont point veu, &amp; ont creu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 30, 'Jefus fit auffi plufieurs autres fignes en la prefence de fes difciples, lefquels ne font point efcrits en ce livre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 31, 'Mais ces chofes font efcrites, afin que vous croyïez que Jefus eft le Chrift, le Fils de Dieu, &amp; qu\'en croyant vous ayez la vie par fon Nom.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 1, 'APRES cela Jefus fe manifefta derechef prés de la mer de Tiberias, &amp; fe manifefta ainfi :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 2, 'Simon Pierre, &amp; Thomas, appellé Didyme, &amp; Nathanaël, qui eftoit de Cana de Galilée, &amp; les fils de Zebedée, &amp; deux autres de fes difciples eftoyent enfemble.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 3, 'Simon Pierre leur dit, Je m\'en vais pefcher. Ils lui dirent, Nous y allons auffi avec toi. Ils partirent, &amp; monterent incontinent en la naffelle, &amp; ne prirent rien en cette nuict-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 4, 'Mais le matin eftant venu, Jefus fe trouva au rivage : toutefois les difciples ne connurent point que c\'eftoit Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 5, 'Jefus donc leur dit, Enfans, avez-vous quelque petit poiffon à manger ? Ils lui refpondirent, Non.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 6, 'Et il leur dit, jettez le filé à cofté droit de la naffelle, &amp; vous en trouverez. Ils le jetterent donc, &amp; ne le pouvoyent plus tirer pour la multitude des poiffons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 7, 'C\'eft pourquoi le difciple que Jefus aimoit, dit à Pierre, C\'eft le Seigneur. Quand donc Simon Pierre eut ouï que c\'eftoit le Seigneur, il fe ceignit de fa jupe, (parce qu\'il eftoit nud) &amp; fe jetta en la mer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 8, 'Et les autres difciples vinrent en la terre, mais feulement environ deux cens coudées) trainans le filé de poiffons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 9, 'Or quand ils furent defcendus en terre, ils virent de la braife mife, &amp; du poiffon mis deffus, &amp; du pain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 10, 'Jefus leur dit : apportez des poiffons que vous avez maintenant pris.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 11, 'Simon Pierre monta, &amp; tira le filé plein de cent cinquante trois grands poiffons : &amp; bien qu\'il y en euft tant, le filé ne fut point rompu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 12, 'Jefus leur dit : Venez &amp; difnez. Et nul de fes difciples ne l\'ofoit interroger, Qui es-tu ? Sçachans que c\'eftoit le Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 13, 'Jefus donc vint, &amp; prit du pain, &amp; leur en bailla : &amp; du poiffon femblablement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 14, 'Ce fut la troifième fois que Jefus fe manifefta à fes difciples, apres eftre reffufcité des morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 15, 'Or apres qu\'ils eurent difné, Jefus dit à Simon Pierre, Simon fils de Jona, m\'aimes-tu plus que ne font ceux-ci ? Il lui refpondit, Oui vrayement, Seigneur : tu fçais que je t\'aime. Il lui dit, Pai mes agneaux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 16, 'Il lui dit encore derechef, Simon fils de Jona, m\'aimes-tu ? Il lui refpondit, Oui vrayement, Seigneur, tu fçais que je t\'aime. Il lui dit, Pai mes brebis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 17, 'Il lui dit pour la troifième fois, Simon fils de Jona, m\'aimes-tu ? Pierre fut contrifté de ce qu\'il lui avoit dit pour la troifième fois, M\'aimes-tu ? Et il lui refpondit, Seigneur, tu fçais toutes chofes, tu fçais que je t\'aime. Jefus lui dit, Pai mes brebis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 18, 'En verité, en verité je te dis, quand tu eftois plus jeune, tu te ceignois, &amp; allois où tu voulois ; mais quand tu fera ancien, tu eftendras tes mains, &amp; un autre te ceindra, &amp; te menera où tu ne voudras pas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 19, 'Or il dit cela fignifiant de quelle mort il devoit glorifier Dieu. Quand il eut dit cela, il lui dit, Sui moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 20, 'Et Pierre fe retournant vid le difciple que Jefus aimoit, qui fuivoit : lequel auffi s\'eftoit encliné au fouper fur l\'eftomac d\'icelui, &amp; avoit dit, Seigneur, qui eft celui à qui il adviendra de te trahir ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 21, 'Quand donc Pierre le vid, il dit à Jefus, Seigneur, &amp; celui-ci, quoi ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 22, 'Jefus lui dit, Si je veux qu\'il demeure jufques à ce que je vienne, qu\'en as-tu à faire ? Toi, fui moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 23, 'Or cette parole courut entre les freres, que ce difciple-là ne mourroit point. Toutefois Jefus ne lui avoit pas dit, Il ne mourra point : mais, Si je veux qu\'il demeure jufques à ce que je vienne, qu\'en as-tu à faire ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 24, 'C\'eft ce difciple-là qui rend tefmoignage de ces chofes, &amp; qui a efcrit ces chofes, &amp; nous fçavons que fon tefmoignage eft digne de foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 25, 'Il y a auffi plufieurs autres chofes que Jefus a faites, lefquelles eftant écrites de poinct en poinct, je ne pense pas que le monde mefme peuft tenir les livres qu\'on en écriroit. Amen. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 1, 'Nous avons fait le premier traité, ô Theophile, touchant toutes les chofes que Jefus s\'eft mis à faire &amp; à enfeigner.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 2, 'Jufques au jour qu\'il fut receu en haut, apres avoir donné fes mandemens aux Apoftres par le Saint Efprit, lefquels il avoit éleus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 3, 'Aufquels auffi apres avoir fouffert il fe prefenta foi-mefme vivant, avec plufieurs preuves affeurées, eftant veu par eux l\'efpace de quarante jours, &amp; parlant des chofes qui appartiennent au royaume de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 4, 'Et les ayant affemblez, leur commanda qu\'ils ne fe départiffent point de Jerufalem, mais qu\'ils attendiffent la promeffe du Pere, Laquelle, dit-il, vous avez ouïe de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 5, 'Car Jean a baptizé d\'eau, mais vous ferez baptifez du Saint Efprit, dans peu de jours.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 6, 'Eux donc eftant affemblez l\'interrogerent, difans, Seigneur, fera-ce en ce temps-ci que tu reftabliras le royaume à Ifraël?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 7, 'Mais il leur dit, Ce n\'eft point à vous de connoiftre les temps ou les faifons, que le Pere a mifes en fa propre puiffance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 8, 'Mais vous recevrez la vertu du Saint Efprit venant fur vous: &amp; me ferez témoins, tant à Jerufalem qu\'en toute la Judée, &amp; la Samarie, &amp; jufques au bout de la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 9, 'Et quand il eut dit ces chofes, il fut élevé, eux le regardans, &amp; une nuée le fouftenant, l\'emporta de devant leurs yeux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 10, 'Et comme ils avoyent les yeux fichez vers le Ciel, lui s\'en allant, voici, deux hommes fe prefenterent devant eux en veftemens blancs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 11, 'Lefquels auffi dirent, Hommes Galiléens, pourquoi vous arreftez-vous regardans au ciel? Ce Jefus ici, qui a efté élevé en haut d\'avec vous au ciel, viendra ainfi que vous l\'avez contemplé allant au ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 12, 'Alors ils s\'en retournerent à Jerufalem, de la montagne qu\'on appelle des Oliviers qui eft prés de Jerufalem le chemin d\'un Sabbat.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 13, 'Et quand ils furent entrez, ils monterent en une chambre haute, où demeuroyent Pierre &amp; Jaques, Jean &amp; André, Philippe &amp; Thomas, Barthelemi &amp; Matthieu, Jaques fils d\'Alphée, &amp; Simon Zelotes, &amp; Jude frere de Jaques.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 14, 'Tous ceux-ci perfeveroyent d\'un accord en priere &amp; en oraifon avec les femmes, &amp; Marie mere de Jefus, &amp; avec les freres d\'icelui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 15, 'Or en ces jours-là Pierre fe leva au milieu des difciples, (&amp; là eftoit affemblée une compagnie d\'environ fix vingt perfonnes,) &amp; dit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 16, 'Hommes freres, il faloit que cette Efcriture fuft accomplie, que le Sainct Efprit avoit predite par la bouche de David touchant Judas, qui a efté la guide de ceux qui ont pris Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 17, 'Car il eftoit du nombre avec nous, &amp; avoit receu fa part de cette adminiftration.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 18, 'Lui donc s\'eftant acquis un champ du falaire de mefchanceté, &amp; s\'eftant precipité, s\'eft crevé par le milieu, &amp; toutes fes entrailles ont efté efpanduës.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 19, 'Ce qui a efté connu de tous les habitans de Jerufalem: tellement que ce champ-là e efté appellé en leur propre langue, Haceldama, c\'eft à dire, le champ du fang.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 20, 'Car il eft efcrit au livre des Pfeaumes, Que fa demeure foit deferte &amp; qu\'il n\'y ait nul qui y habite. Item, Qu\'un autre prenne fon adminiftration.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 21, 'Il faut donc que d\'entre ces hommes-là qui fe font affemblez avec nous, tout le temps que le Seigneur Jefus eft allé &amp; venu entre nous,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 22, 'Commençant dés le Baptefme de Jean, jufques au jour qu\'il a efté enlevé d\'avec nous, quelqu\'un d\'entr\'eux foit tefmoin avec nous de fa refurrection.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 23, 'Alors ils en prefenterent deux, affavoir Jofeph appellé Barfabas, (qui eftoit furnommé jufte) &amp; Matthias.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 24, 'Et en priant ils dirent, Toi Seigneur, qui connois les coeurs de tous, montre lequel de ces deux tu as éleu:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 25, 'Afin qu\'il prenne fa part de ce miniftere &amp; Apoftolat, dont Judas s\'eft deftourné pour s\'en aller en fon lieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 26, 'Alors ils jetterent le fort fur eux: &amp; le fort cheut fur Matthias, qui d\'un commun accord fut mis au nombre des onze Apoftres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 1, 'Et comme le jour de la Pentecofte s\'accompliffoit, ils eftoyent tous d\'un accord en un mefme lieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 2, 'Alors il fe fit foudainement un fon du ciel, comme d\'un vent qui fouffle en vehemence, lequel remplit toute la maifon où ils eftoyent affis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 3, 'Et il leur apparut des langues departies comme de feu, &amp; elles fe poferent fur chacun d\'eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 4, 'Dont ils furent tous remplis du Saint Efprit, &amp; commencerent à parler des langages eftranges, ainfi que l\'Efprit leur donnoit à parler.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 5, 'Or il y avoit des Juifs fejournans à Jerufalem, hommes devots, de toute nation qui eft fous le Ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 6, 'Apres donc que le bruit en fut fait, une multitude vint enfemble, laquelle fut toute efmeuë, parce que chacun les oyoit parler en fon propre langage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 7, 'Dont tous eftoyent eftonnez, &amp; s\'émerveilloyent, difans l\'un à l\'autre, Voici, tous ceux-ci qui parlent, ne font-ils pas Galiléens?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 8, 'Comment donc chacun de nous les oyons-nous parler en noftre propre langage, auquel nous fommes nez?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 9, 'Parthes, &amp; Medes, &amp; Elamites, &amp; nous qui habitons en Mefopotamie, &amp; en Judée, &amp; en Cappadoce, en Ponte, &amp; en Afie:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 10, 'Et en Phrygie, &amp; en Pamphylie, en Egypte, &amp; és parties de Libye, qui eft à l\'endroit de Cyrene, &amp; nous qui nous tenons à Rome.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 11, 'Tant Juifs que Profelytes, Creteins &amp; Arabes, nous les oyons parler chacun en nos propres langues, les chofes magnifiques de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 12, 'Ils eftoyent donc tous eftonnez, &amp; ne fçavoyent que penfer, difans l\'un à l\'autre, Que veut dire ceci?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 13, 'Et les autres fe mocquans, difoyent, C\'eft qu\'ils font pleins de vin doux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 14, 'Mais Pierre fe prefentant avec les onze, éleva fa voix, &amp; leur dit, Hommes Juifs, &amp; vous tous qui habitez à Jerufalem, ceci vous foit notoire, &amp; mettez mes paroles en vos oreilles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 15, 'Car ceux-ci ne font point yvres, comme vous penfez, veu qu\'il eft la troifiéme heure du jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 16, 'Mais c\'eft ici ce qui a efté dit par le Prophete Joël,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 17, 'Et il aviendra és derniers jours (dit Dieu) que je répandrai de mon Efprit fur toute chair: &amp; vos fils prophetizeront, &amp; vos filles auffi, &amp; vos jeunes gens verront des vifions, &amp; vos anciens fongeront des fonges.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 18, 'Et pour vrai en ces jours-là je répandrai de mon Efprit fur mes ferviteurs &amp; fur mes fervantes, dont ils prophetizeront.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 19, 'Et je ferai des chofes merveilleufes au ciel en haut, &amp; des fignes en la terre en bas, fang &amp; feu, &amp; vapeur de fumée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 20, 'Le Soleil fera changé en tenebres, &amp; la Lune en fang, devant que ce grand &amp; notable jour du Seigneur vienne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 21, 'Et il aviendra que quiconque invoquera le Nom du Seigneur fera fauvé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 22, 'Hommes Ifraëlites, oyez ces paroles: Jefus le Nazarien, perfonnage approuvé de Dieu entre vous par vertus, par merveilles, &amp; par fignes, lefquels Dieu a faits par lui au milieu de vous, comme auffi vous le fçavez:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 23, 'Ayant efté livré par le confeil defini &amp; par la providence de Dieu, vous l\'avez pris, &amp; l\'avez mis en la croix, &amp; fait mourir par les mains des iniques.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 24, 'Lequel Dieu a reffufcité, ayant delié les douleurs de la mort, parce qu\'il n\'eftoit pas poffible qu\'il fuft retenu par elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 25, 'Car David dit de lui, Je contemplois toûjours le Seigneur en ma prefence: car il eft à ma dextre, afin que je ne fois point ébranlé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 26, 'C\'eft pourquoi mon coeur s\'eft éjoüi, &amp; ma langue a eu lieffe, &amp; de plus ma chair repofera en efperance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 27, 'Car tu ne delaifferas point mon ame au fepulcre, &amp; tu ne permettras point que ton Saint fente de corruption.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 28, 'Tu m\'as fait connoiftre les voyes de vie, tu me rempliras de lieffe avec ta face.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 29, 'Hommes freres, je vous puis bien dire franchement touchant le Patriarche David, qu\'il eft mort &amp; qu\'il a efté enfeveli: &amp; fon fepulcre eft entre nous jufques à ce jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 30, 'Lui donc eftant Prophete, &amp; fçachant que Dieu lui avoit promis avec ferment, que du fruict de fes reins, felon la chair, il fufciteroit le Chrift, pour le faire feoir fur fon trône:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 31, 'Il a dit de la refurrection de Chrift, en la prevoyant, que fon ame n\'a point efté delaiffée au fepulcre, &amp; que fa chair n\'a point fenti de corruption.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 32, 'Dieu a reffufcité ce Jefus, de laquelle chofe nous fommes tous tefmoins.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 33, 'Apres donc qu\'il a efté élevé par la dextre de Dieu, &amp; qu\'il a receu de fon Pere la promeffe du Saint Efprit, il a refpandu ce que maintenant vous voyez &amp; oyez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 34, 'Car David n\'eft point monté aux cieux: mais lui-mefme dit, Le Seigneur a dit à mon Seigneur, Sieds-toi à ma dextre,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 35, 'Jufques à ce que j\'aye mis tes ennemis pour le marche-pied de tes pieds.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 36, 'Que donc toute la maifon d\'Ifraël fçache affeurément que Dieu l\'a fait Seigneur &amp; Chrift, voire ce Jefus que vous avez crucifié.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 37, 'Ayant ouï ces chofes, ils eurent componction de coeur, &amp; dirent à Pierre &amp; aux autres Apoftres, Hommes freres, que ferons-nous?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 38, 'Et Pierre leur dit, Amendez-vous, &amp; que chacun de vous foit baptizé au Nom de Jefus Chrift, en remiffion des pechez, &amp; vous recevrez le don du Sainct Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 39, 'Car à vous &amp; à vos enfans eft faite la promeffe, &amp; à tous ceux qui font loin, autant que le Seigneur noftre Dieu en appellera à foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 40, 'Et par plufieurs autres paroles il tefmoignoit, &amp; les exhortoit, difant, Sauvez-vous de cette nation perverfe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 41, 'Ceux donc qui receurent d\'un franc courage fa parole furent baptizez: &amp; furent adjouftées en ce jour-là environ trois mille ames.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 42, 'Or ils perfeveroyent tous en la doctrine des Apoftres, &amp; en la communion, &amp; en la fraction du pain, &amp; aux prieres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 43, 'Or toute perfonne avoit de la crainte: &amp; beaucoup de merveilles &amp; de fignes fe faifoyent par les Apoftres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 44, 'Et tous ceux qui croioyent, eftoyent enfemble en un mefme lieu, &amp; avoyent toutes chofes communes:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 45, 'Et vendoyent leurs poffeffions &amp; leurs biens, &amp; les diftribuoyent à tous, felon que chacun en avoit befoin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 46, 'Et tous les jours ils perfeveroyent tous d\'un accord au temple: &amp; rompans le pain de maifon en maifon, ils prenoyent leur repas avec joye &amp; fimplicité de coeur:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 47, 'Loüans Dieu, &amp; ayans grace envers tout le peuple. Et le Seigneur adjoignoit de jour en jour à l\'Eglife des gens pour eftre fauvez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 1, 'Or Pierre &amp; Jean montoyent enfemble au temple à l\'heure de la priere, qui eftoit à neuf heures.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 2, 'Et un certain homme boiteux dés le ventre de fa mere, y eftoit porté, lequel on mettoit journellement à la porte du temple nommée la Belle, pour demander l\'aumofne à ceux qui entroyent au temple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 3, 'Lui voyant Pierre &amp; Jean qui vouloyent entrer au temple, les pria de lui donner l\'aumofne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 4, 'Mais Pierre avec Jean ayant l\'oeil fiché fur lui, dit, Regarde-nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 5, 'Et il les regardoit attentivement, s\'attendant de recevoir quelque chofe d\'eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 6, 'Alors Pierre dit, Je n\'ai ni argent ni or: mais ce que j\'ai, je te le donne; Au nom de Jefus-Chrift le Nazarien leve-toi &amp; chemine.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 7, 'Et l\'ayant pris par la main droite, il le leva: &amp; incontinent les plantes &amp; les chevilles de fes pieds devinrent fermes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 8, 'Et il fauta, &amp; fe tint debout, &amp; cheminoit: &amp; entra avec eux au temple, cheminant, &amp; fautant, &amp; loüant Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 9, 'Et tout le peuple le vid cheminant &amp; loüant Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 10, 'Et le reconnoiffans que c\'eftoit celui-là mefme qui eftoit affis à la Belle porte du temple, pour avoir l\'aumofne, ils furent remplis d\'ébahiffement &amp; d\'eftonnement touchant ce qui eftoit avenu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 11, 'Et comme le boiteux qui avoit efté gueri tenoit par la main Pierre &amp; Jean, tout le peuple tout eftonné courut à eux au porche qui s\'appelle de Salomon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 12, 'Mais Pierre voyant cela dit au peuple, Hommes Ifraëlites, pourquoi vous eftonnez-vous de ceci? ou pourquoi avez-vous l\'oeil fiché fur nous, comme fi par noftre puiffance ou par noftre faincteté, nous avions fait cheminer celui-ci?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 13, 'Le Dieu d\'Abraham, &amp; d\'Ifaac, &amp; de Jacob, &amp; le Dieu de nos peres a glorifié fon Fils Jefus, lequel vous avez livré &amp; renié devant la face de Pilate, bien qu\'il jugeaft qu\'il devoit eftre delivré.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 14, 'Mais vous avez renié le Saint, &amp; le Jufte, &amp; avez requis qu\'on vous donnaft un meurtrier:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 15, 'Et avez mis à mort le Prince de vie, que Dieu a reffufcité des morts: dequoi nous fommes tefmoins.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 16, 'Et par la foi du Nom d\'icelui, fon Nom a raffermi celui-ci, lequel vous voyez &amp; connoiffez. Voire la foi qui eft par lui, a donné à celui-ci cette entiere difpofition de tous fes membres en la prefence de vous tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 17, 'Et maintenant, freres, je fçais que vous l\'avez fait par ignorance, comme auffi vos Gouverneurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 18, 'Mais Dieu a ainfi accompli les chofes qu\'il avoit predites par la bouche de tous fes Prophetes, que le Chrift devoit fouffrir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 19, 'Amendez-vous donc, &amp; vous convertiffez, afin que vos pechez foyent affacez:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 20, 'Quand les temps de raffraifchiffement feront venus de la prefence du Seigneur, &amp; qu\'il aura envoyé Jefus-Chrift, qui auparavant vous a efté annoncé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 21, 'Lequel il faut que le ciel contienne jufques au temps du reftabliffement de toutes les chofes que Dieu a prononcées par la bouche de tous fes faints Prophetes, dés le commencement du monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 22, 'Car Moyfe mefme a dit aux Peres, Le Seigneur voftre Dieu vous fufcitera d\'entre vos freres un Prophete tel que moi: vous l\'efcouterez en tout ce qu\'il vous dira.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 23, 'Et il aviendra que toute perfonne qui n\'aura point efcouté ce Prophete-là, fera exterminée d\'entre le peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 24, 'Et mefmes tous les Prophetes, depuis Samuël, &amp; ceux qui l\'ont fuivi: tous, tant qu\'il y en a qui ont parlé, ont auffi predit ces jours-ci.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 25, 'Vous eftes les enfans des Prophetes, &amp; de l\'alliance que Dieu a traittée avec nos Peres, difant à Abraham, Et en ta femence feront benites toutes les familles de la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 26, 'C\'eft pour vous premierement que Dieu ayant fufcité fon Fils Jefus, l\'a envoyé pour vous benir, en retirant chacun de vous de vos mauvaiftiez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 1, 'Mais comme ils parloyent au peuple, les Sacrificateurs, &amp; le Capitaine du temple, &amp; les Sadduciens furvinrent:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 2, 'Eftans en grand\'peine de ce qu\'ils enfeignoyent le peuple, &amp; qu\'ils annonçoyent la refurrection des morts au Nom de Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 3, 'Et ayans jetté les mains fur eux, ils les mirent en prifon jufques au lendemain: car le foir eftoit déja venu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 4, 'Et plufieurs de ceux qui avoyent ouï la parole creurent: &amp; le nombre des perfonnes fut environ cinq mille.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 5, 'Or il avint que le lendemain leurs Gouverneurs s\'affemblerent, &amp; les Anciens, &amp; les Scribes, à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 6, 'Et Anne fouverain Sacrificateur, &amp; Caïphe, &amp; Jean, &amp; Alexandre, &amp; tous ceux qui eftoyent de la lignée Sacerdotale.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 7, 'Et les ayans mis en place ils les interrogerent, difant, Par quelle puiffance, ou au nom de qui avez-vous fait ceci?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 8, 'Alors Pierre eftant rempli du Saint Efprit, leur dit, Gouverneurs du peuple, &amp; vous Anciens d\'Ifraël,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 9, 'Puisque nous fommes aujourd\'hui recherchez pour un bien qui a efté fait à l\'endroit d\'un homme impotent, pour fçavoir comment il a efté gueri:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 10, 'Qu\'il foit notoire à vous tous, &amp; à tout le peuple d\'Ifraël, que ç\'a efté au Nom de Jefus-Chrift le Nazarien, que vous avez crucifié, lequel Dieu a reffufcité des morts: voire en vertu du Nom de celui-là, cet homme affifte fain ici devant vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 11, 'C\'eft cette pierre qui a efté rejettée par vous les edifians, qui a efté faite le principal du coin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 12, 'Et il n\'y a point de falut en aucun autre: car auffi il n\'y a point d\'autre Nom fous le ciel qui foit donné aux hommes, par lequel il nous faille eftre fauvez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 13, 'Eux alors voyans la hardieffe de Pierre &amp; de Jean, &amp; connoiffant auffi qu\'ils eftoyent hommes fans lettres &amp; idiots, s\'émerveilloyent, &amp; reconnoiffoyent bien qu\'ils avoyent efté avec Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 14, 'Et voyans que l\'homme qui avoit efté gueri, eftoit prefent avec eux, ils ne pouvoyent contredire en rien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 15, 'Alors leur ayant commandé de fortir hors du confeil, ils conferoyent entr\'eux,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 16, 'Difant, Que ferons-nous à ces gens-ci? car il eft manifefte à tous les habitans de Jerufalem, qu\'un figne notoire a efté fait par eux, &amp; nous ne le pouvons nier.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 17, 'Mais afin qu\'il ne foit plus divulgué parmi le peuple, defendons-leur avec menaces expreffes, qu\'ils n\'ayent plus à parler à homme vivant en ce Nom.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 18, 'Et les appellans, ils leur commanderent que totalement ils ne parlaffent ni n\'enfeignaffent au Nom de Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 19, 'Mais Pierre &amp; Jean refpondans, leur dirent, Jugez, s\'il eft jufte devant Dieu de vous obeïr pluftoft qu\'à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 20, 'Car nous ne pouvons que nous ne difions les chofes que nous avons veuës &amp; ouïes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 21, 'Alors ils les relafcherent avec menaces, ne trouvans point comment ils les peuffent punir, à caufe du peuple, parce que tous glorifioyent Dieu de ce qui avoit efté fait.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 22, 'Car l\'homme fur lequel avoit efté fait un tel miracle de guerifon, avoit plus de quarante ans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 23, 'Or apres qu\'on les eut laiffez aller, ils vinrent vers leurs gens, &amp; leur reciterent tout ce que les principaux Sacrificateurs &amp; les Anciens leur avoyent dit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 24, 'Ce qu\'ayans entendu, ils éleverent tous d\'un accord la voix à Dieu, &amp; dirent, Seigneur, tu es le Dieu qui a fait le ciel &amp; la terre, la mer, &amp; toutes les chofes qui y font.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 25, 'Qui as dit par la bouche de David ton ferviteur, Pourquoi eft-ce que les Nations ont fremi, &amp; que les peuples ont projetté des chofes vaines?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 26, 'Les Rois de la terre fe font trouvez en perfonne, &amp; les Princes fe font affemblez en un mefme lieu, contre le Seigneur, &amp; contre fon Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 27, 'Car de vrai, contre ton Saint Fils Jefus que tu as oinct, fe font affemblez Herode &amp; Ponce Pilate, avec les Nations &amp; les peuples d\'Ifraël,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 28, 'Pour faire toutes les chofes que ta main &amp; ton confeil avoyent auparavant determinées d\'eftre faites.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 29, 'Maintenant donc, Seigneur, regarde à leurs menaces, &amp; donne à tes ferviteurs d\'annoncer ta parole avec toute hardieffe:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 30, 'En eftendant ta main à ce que guerifon, &amp; fignes, &amp; merveilles fe faffent par le Nom de ton Saint Fils Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 31, 'Et quand ils eurent prié, le lieu auquel ils eftoyent affemblez, trembla: &amp; ils furent tous remplis du Saint Efprit, &amp; annonçoyent la parole de Dieu avec hardieffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 32, 'Or la multitude de ceux qui croioyent, n\'eftoit qu\'un coeur &amp; qu\'une ame: &amp; nul ne difoit aucune chofe eftre fienne de ce qu\'il poffedoit, mais toutes chofes eftoyent communes entr\'eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 33, 'Auffi les Apoftres rendoyent témoignage à grande puiffance de la refurrection du Seigneur Jefus: &amp; une grande grace eftoit fur eux tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 34, 'Car il n\'y avoit entr\'eux aucun difetteux, parce que tous ceux qui poffedoyent des champs ou des maifons, les vendoyent, &amp; apportoyent le prix des chofes qui eftoyent venduës:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 35, 'Et le mettoyent aux pieds des Apoftres: &amp; cela eftoit diftribué à chacun, felon qu\'il en avoit befoin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 36, 'Jofes donc, qui par les Apoftres fut furnommé Barnabas (c\'eft à dire, fils de confolation) Levite &amp; Cyprien de nation,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 37, 'Ayant une poffeffion, la vendit, &amp; en apporta le prix &amp; le mit aux pieds des Apoftres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 1, 'Mais un certain homme nommé Ananias avec Saphira fa femme, vendit une poffeffion.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 2, 'Et fouftraya une partie du prix, fa femme confentant auffi à cela, &amp; en apporta quelque partie, &amp; la mit aux pieds des Apoftres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 3, 'Dont Pierre dit, Ananias, pourquoi Satan a-t\'il rempli ton coeur pour mentir au Saint Efprit, &amp; fouftraire du prix de la poffeffion?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 4, 'Si tu l\'euffes gardée, ne te demeuroit-elle pas, &amp; eftant venduë n\'eftoit-elle pas en ta puiffance? qu\'y avoit-il pourquoi tu deuffes mettre cela en ton coeur? tu n\'as point menti aux hommes, mais à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 5, 'Et Ananias oyant ces paroles, tomba, &amp; rendit l\'efprit: dont il avint une grande crainte à tous ceux qui entendirent ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 6, 'Et quelques jeunes compagnons fe levans le prirent, &amp; l\'emporterent hors, &amp; l\'enterrerent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 7, 'Il avint environ l\'efpace de trois heures apres que fa femme auffi, ne fçachant point ce qui eftoit avenu, entra:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 8, 'Et Pierre prenant la parole, lui dit, Di-moi, avez-vous autant vendu le champ? Et elle dit, Oui, autant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 9, 'Alors Pierre lui dit, Qu\'y a-t\'il que vous ayez fait complot entre vous de tenter l\'Efprit du Seigneur? voici à la porte les pieds de ceux qui ont enterré ton mari, &amp; ils t\'emporteront.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 10, 'Et au mefme inftant elle tomba à fes pieds, &amp; rendit l\'efprit. Et quand les jeunes compagnons furent entrez ils la trouverent morte, &amp; l\'emporterent hors, &amp; l\'enterrerent auprés de fon mari.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 11, 'Dont il avint une grande crainte à toute l\'Eglife, &amp; à tous ceux qui oyoyent ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 12, 'Et beaucoup de fignes &amp; de miracles fe faifoyent entre le peuple, par les mains des Apoftres: &amp; ils eftoyent tous d\'un accord au porche de Salomon:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 13, 'Et nul des autres ne s\'ofoit adjoindre à eux, mais le peuple les magnifioit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 14, 'Et de plus en plus s\'augmentoit la multitude de ceux qui croyoyent au Seigneur, tant d\'hommes que de femmes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 15, 'Tellement qu\'ils apportoyent les malades és ruës, &amp; les mettoyent en de petits lits &amp; couchettes, afin que quand Pierre viendroit au moins fon ombre paffaft fur quelqu\'un d\'entr\'eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 16, 'Pareillement le populaire des villes qui eftoyent voifines, s\'affembloit à Jerufalem, apportant les malades, &amp; tous ceux qui eftoyent tourmentez des efprits immondes: lefquels tous eftoyent gueris.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 17, 'Alors le fouverain Sacrificateur fe leva, lui &amp; tous ceux qui eftoyent avec lui, (qui eftoit la fecte des Sadduciens) &amp; ils furent remplis d\'envie:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 18, 'Et jetterent les mains fur les Apoftres, &amp; les mirent en la prifon publique.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 19, 'Mais l\'Ange du Seigneur ouvrit de nuit les portes de la prifon, &amp; les ayant mis dehors, leur dit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 20, 'Allez, &amp; vous prefentans au temple, annoncez au peuple toutes les paroles de cette vie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 21, 'Eux donc ayans ouï cela, entrerent au temple environ le poinct du jour, &amp; enfeignoyent. Mais le fouverain Sacrificateur eftant venu, &amp; ceux qui eftoyent avec lui, affemblerent le confeil, &amp; tous les Anciens des enfans d\'Ifraël, &amp; envoyerent à la prifon pour les faire amener.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 22, 'Mais quand les fergens y furent venus, ils ne les trouverent point en la prifon: ainfi ils s\'en retournerent, &amp; le rapporterent,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 23, 'Difans, Nous avons bien trouvé la prifon fermée avec toute feureté, &amp; les gardes qui eftoyent dehors devant les portes: mais quand nous l\'avons ouverte, nous n\'avons trouvé perfonne dedans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 24, 'Incontinent que le fouverain Sacrificateur &amp; le Capitaine du temple, &amp; les principaux Sacrificateurs eurent ouï ces paroles, ils eftoyent en perplexité d\'eux que deviendroit cela.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 25, 'Mais quelqu\'un furvint qui leur rapporta, difant, Voila, les hommes que vous aviez mis en prifon, font au temple, &amp; fe tenant là enfeignent le peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 26, 'Alors le Capitaine du temple avec les fergens, s\'en alla, &amp; les amena, mais fans violence: car ils craignoyent le peuple, qu\'ils ne fuffent lapidez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 27, 'Et les ayant amenez, ils les prefenterent au confeil. Et le fouverain Sacrificateur les interrogea,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 28, 'Difant, Ne vous avons-nous pas defendu par exprés commandement de n\'enfeigner point en ce Nom-ci? &amp; voici, vous avez rempli Jerufalem de voftre doctrine, &amp; vous voulez faire venir fur nous le fang de cet homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 29, 'Alors Pierre &amp; les autres Apoftres refpondans, dirent, Il faut pluftoft obeïr à Dieu qu\'aux hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 30, 'Le Dieu de nos peres a reffufcité Jefus, lequel vous avez mis à mort, le pendant au bois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 31, 'C\'eft celui que Dieu a élevé par fa dextre pour Prince &amp; Sauveur, pour donner repentance à Ifraël, &amp; remiffion des pechez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 32, 'Et nous lui fommes témoins de ce que nous difons: &amp; le Saint Efprit auffi que Dieu a donné à ceux qui lui obeïffent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 33, 'Eux donc ayant ouï cela, grinçoyent les dents, &amp; confultoyent pour les mettre à mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 34, 'Alors un Pharifien, nommé Gamaliël, Docteur de la Loi, honorable à tout le peuple, fe levant au confeil, commanda que les Apoftres fe retiraffent un peu dehors.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 35, 'Puis il leur dit, Hommes Ifraëlites, prenez garde à vous touchant ces gens ce que vous devez faire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 36, 'Car devant ce temps-ci, s\'eft levé Theudas, fe difant eftre quelque chofe, auquel s\'adjoignit un nombre d\'hommes environ quatre cens: lequel a efté défait, &amp; tous ceux qui s\'eftoyent joints à lui ont efté rompus &amp; reduits à rien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 37, 'Apres lui fe leva Judas le Galiléen és jours de la defcription, &amp; deftourna un grand peuple apres foi: &amp; celui-ci auffi eft peri, &amp; tous ceux qui s\'eftoyent joints à lui ont efté efpars ça &amp; là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 38, 'Et maintenant je vous le dis, deportez-vous de ces hommes, &amp; les laiffez: car fi ce confeil ou cet oeuvre eft des hommes, il fera défait.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 39, 'Mais s\'il eft de Dieu, vous ne le pourrez défaire: &amp; regardez que mefmes vous ne foyez trouvez faire la guerre à Dieu. Et ils furent de fon opinion.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 40, 'Alors ils appellerent les Apoftres, &amp; apres les avoir foüettez, ils leur commanderent de ne parler point au Nom de Jefus, &amp; les laifferent aller.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 41, 'Eux donc s\'en allerent de devant le confeil, s\'éjouïffans d\'avoir efté rendus dignes de fouffrir opprobres pour le Nom d\'icelui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 42, 'Et tous les jours ils ne ceffoyent, au temple, &amp; de maifon en maifon, d\'enfeigner &amp; d\'annoncer Jefus-Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 1, 'Et en ces jours-là comme les difciples fe multiplioyent, il avint un murmure des Grecs contre les Hebreux, parce que leurs veuves eftoyent mefprifées au fervice ordinaire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 2, 'C\'eft pourquoi les douze, ayant appellé la multitude des difciples, dirent, Il n\'eft pas raifonnable que nous laiffions la parole de Dieu pour fervir aux tables.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 3, 'Regardez donc, freres, de choifir fept hommes d\'entre vous, de qui on ait bon témoignage, pleins du Saint Efprit &amp; de fapience, aufquels nous commettions cette affaire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 4, 'Et quand à nous, nous pourfuivrons à vacquer à la priere, &amp; à l\'adminiftration de la parole.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 5, 'Et ce difcours pleut à toute la compagnie qui eftoit là prefente: dont ils éleurent Eftienne, perfonnage plein de foi &amp; du Saint Efprit, &amp; Philippe, &amp; Procore, &amp; Nicanor, &amp; Timon, &amp; Parmenas, &amp; Nicolas profelyte Antiochien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 6, 'Lefquels ils prefenterent devant les Apoftres: &amp; eux apres avoir prié, leur impoferent les mains.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 7, 'Et la parole de Dieu croiffoit, &amp; le nombre des difciples fe multiplioit fort à Jerufalem: un grand nombre auffi des Sacrificateurs obeïffoit à la foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 8, 'Or Eftienne plein de foi &amp; de vertu, faifoit de grands miracles &amp; fignes entre le peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 9, 'Et quelques-uns de la Synagogue qui eft appellée des Libertins, &amp; des Cyreniens, &amp; des Alexandrins, &amp; de ceux qui eftoyent de Cilice, &amp; d\'Afie, fe leverent difputans contre Eftienne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 10, 'Et ils ne pouvoyent refifter à la fapience &amp; à l\'Efprit par lequel il parloit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 11, 'Alors ils fubornerent des hommes, lefquels difoyent, Nous lui avons ouï proferer des paroles blafphematoires contre Moyfe &amp; contre Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 12, 'Et ils efmeurent le peuple, &amp; les Anciens, &amp; les Scribes, &amp; lui courans fus le ravirent, &amp; l\'amenerent au confeil:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 13, 'Et prefenterent de faux tefmoins, qui difoyent, Cét homme-ci ne ceffe de preferer des paroles blafphematoires contre ce faint lieu &amp; la Loi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 14, 'Car nous lui avons ouï dire, que ce Jefus le Nazarien deftruira ce lieu-ci, &amp; changera les ordonnances que Moyfe nous a baillées.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 15, 'Et comme tous ceux qui eftoyent affis au confeil avoyent les yeux fichez fur lui, ils virent fon vifage comme le vifage d\'un Ange.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 1, 'Alors le fouverain Sacrificateur dit, Ces chofes font-elles ainfi?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 2, 'Et Eftienne dit, Hommes freres &amp; peres, oyez; Le Dieu de gloire s\'apparut à noftre pere Abraham du temps qu\'il eftoit en Mefopotamie, devant qu\'il demeuraft en Carran:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 3, 'Et lui dit, Sors de ton païs, &amp; d\'avec ton parentage, &amp; vien au païs que je te montrerai.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 4, 'Alors eftant forti du païs des Caldéens, il habita en Carran. Et de là, apres que fon pere fut mort, Dieu le tranfporta en ce païs auquel vous habitez maintenant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 5, 'Et il ne lui donna aucun heritage en icelui, non pas feulement pour affeoir le pied, bien qu\'il lui euft promis de le lui donner en poffeffion, &amp; à fa pofterité apres lui, encore qu\'il n\'euft point d\'enfant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 6, 'Mais Dieu en parla ainfi, Ta pofterité fejournera en une terre eftrange: &amp; là on l\'affervira, &amp; on la mal-menera quatre cens ans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 7, 'Mais je jugerai la nation à laquelle ils auront fervi, dit Dieu: &amp; apres cela ils fortiront &amp; me ferviront en ce lieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 8, 'Puis il lui donna l\'alliance de la circoncifion: &amp; ainfi Abraham engendra Ifaac, lequel il circoncit au huictiéme jour: &amp; Ifaac engendra Jacob, &amp; Jacob les douze Patriarches.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 9, 'Et les Patriarches eftans meus d\'envie, vendirent Jofeph pour eftre mené en Egypte: mais Dieu eftoit avec lui:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 10, 'Qui le delivra de toutes fes tribulations, &amp; lui donna grace &amp; fapience devant Pharao roi d\'Egypte, qui l\'eftablit gouverneur fur l\'Egypte, &amp; fur toute fa maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 11, 'Or il avint une famine par tout le païs d\'Egypte, &amp; en Canaan, &amp; une grande angoiffe: tellement que nos Peres ne pouvoyent trouver de vivres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 12, 'Mais quand Jacob eut entendu qu\'il y avoit du blé en Egypte, il y envoya premierement nos Peres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 13, 'Et à la feconde fois Jofeph fut reconnu par fes freres, &amp; la lignée de Jofeph fut declarée à Pharao.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 14, 'Alors Jofeph envoya querir Jacob fon pere, &amp; tout fon parentage, qui eftoyent foixante &amp; quinze ames.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 15, 'Jacob donc defcendit en Egypte, &amp; y mourut, lui &amp; nos Peres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 16, 'Lefquels furent tranfportez en Sichem, &amp; mis au fepulcre qu\'Abraham avoit acheté à prix d\'argent des fils d\'Emmor, fils de Sichem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 17, 'Mais comme le tems de la promeffe s\'approchoit, pour laquelle Dieu avoit juré à Abraham, le peuple creut &amp; fe multiplia en Egypte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 18, 'Jufques à ce qu\'un autre roi fe leva en Egypte, lequel n\'avoit point connu Jofeph.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 19, 'Lui ufant de rufe contre noftre nation, mal-mena nos peres jufques à leur faire expofer à l\'abandon leurs enfans, afin d\'en faire faillir la race.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 20, 'Auquel temps nafquit Moyfe, qui fut excellemment beau, &amp; fut nourri trois mois en la maifon de fon pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 21, 'Puis quand il fut expofé à l\'abandon, la fille de Pharao l\'emporta, &amp; le nourrit pour foi comme fon fils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 22, 'Et Moyfe fut inftruit en toute la fapience des Egyptiens: or il eftoit puiffant en dits &amp; en faits.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 23, 'Mais quand il vint à l\'âge de quarante ans, il lui monta au coeur d\'aller vifiter fes freres les enfans d\'Ifraël:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 24, 'Et voyant l\'un d\'eux à qui on faifoit tort, il le defendit, &amp; vengea celui qui eftoit outragé, en tuant l\'Egyptien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 25, 'Or il penfoit que fes freres entendiffent que Dieu leur devoit donner delivrance par fa main: mais ils ne l\'entendirent point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 26, 'Et le jour fuivant il fe trouva entr\'eux comme ils fe querelloyent, &amp; tafcha de les mettre d\'accord, difant, Hommes, vous eftes freres, pourquoi vous faites vous tort l\'un à l\'autre?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 27, 'Mais celui qui faifoit tort à fon prochain, le rebuta, difant, Qui t\'a ordonné prince &amp; juge fur nous?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 28, 'Me veux-tu tuër, ainfi que tu tuas hier l\'Egyptien?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 29, 'Alors Moyfe s\'enfuït fur un tel difcours, &amp; fut eftranger au païs de Madian, où il engendra deux fils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 30, 'Et quarante ans eftant accomplis, l\'Ange du Seigneur s\'apparut à lui au defert de la montagne de Sina, en une flamme de feu qui eftoit en un buiffon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 31, 'Et quand Moyfe le vid, il s\'efmerveilla de la vifion: &amp; comme il approchoit pour confiderer ce que c\'eftoit, la voix du Seigneur lui fut addreffée,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 32, 'Difant, Je fuis le Dieu de tes peres, le Dieu d\'Abraham, &amp; le Dieu d\'Ifaac, &amp; le Dieu de Jacob. Et Moyfe tout tremblant n\'ofoit confiderer ce que c\'eftoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 33, 'Alors le Seigneur lui dit, Dechauffe tes fouliers de tes pieds: car le lieu où tu es, eft une terre fainte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 34, 'J\'ai veu, j\'ai veu l\'affliction de mon peuple qui eft en Egypte, j\'ai ouï leur gemiffement, &amp; je fuis defcendu pour les delivrer: maintenant donc vien-ça, je t\'envoyerai en Egypte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 35, 'Ce Moyfe, qu\'ils avoyent renié, difant, Qui t\'a ordonné prince &amp; juge? c\'eft celui que Dieu envoya pour prince &amp; liberateur par la main de l\'Ange qui lui eftoit apparu au buiffon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 36, 'C\'eft celui qui les tira hors, faifant des miracles &amp; des fignes en Egypte, en la mer rouge, &amp; au defert par quarante ans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 37, 'C\'eft ce Moyfe qui a dit aux enfans d\'Ifraël, Le Seigneur voftre Dieu vous fufcitera d\'entre vos freres un Prophete tel que moi: efcoutez-le.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 38, 'C\'eft celui qui fut en l\'affemblée au defert, avec l\'Ange qui parloit à lui en la montagne de Sina, &amp; qui fut avec nos peres: lequel a receu les divines paroles vives pour nous les donner.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 39, 'Auquel nos peres ne voulurent point obeïr, mais le rebuterent, &amp; fe deftournerent en leur coeur pour retourner en Egypte,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 40, 'Difans à Aaron, Fai nous des Dieux qui aillent devant nous; car nous ne fçavons ce qui eft avenu à ce Moyfe qui nous a amenez hors du païs d\'Egypte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 41, 'Et en ces jours-là ils firent un veau, &amp; offrirent des facrifices à l\'idole, &amp; s\'éjouïrent és oeuvres de leurs mains.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 42, 'Dont auffi Dieu fe deftourna, &amp; les abandonna à fervir la gendarmerie du ciel, ainfi qu\'il eft efcrit au livre des Prophetes, Maifon d\'Ifraël, m\'avez-vous offert des facrifices &amp; des oblations par quarante ans au defert?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 43, 'Mais vous avez porté le tabernacle de Moloch, &amp; l\'eftoile de voftre Dieu Remphan: lefquelles figures vous avez faites pour les adorer: C\'eft pourquoi je vous tranfporterai outre Babylone.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 44, 'Le tabernacle du tefmoignage a efté avec nos Peres au defert, comme avoit ordonné celui qui avoit dit à Moyfe, qu\'il le fift felon le patron qu\'il avoit veu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 45, 'Lequel tabernacle auffi nos Peres receurent, &amp; l\'amenerent avec Jofué au païs qui eftoit poffedé par les nations, que Dieu a dejettées de devant nos Peres, jufques aux jours de David.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 46, 'Lequel trouva grace devant Dieu, &amp; demanda de trouver un Tabernacle au Dieu de Jacob.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 47, 'Et Salomon lui edifia une maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 48, 'Mais le Souverain n\'habite point és temples faits de main, comme dit le Prophete.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 49, 'Le ciel eft mon trône, &amp; la terre eft le marche-pied de mes pieds: quelle maifon me baftirez-vous? dit le Seigneur, ou quel eft le lieu de mon repos?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 50, 'Ma main n\'a-t\'elle pas fait toutes ces chofes?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 51, 'Gens de col roide, &amp; incirconcis de coeur &amp; d\'oreilles, vous vous aheurtez toûjours contre le Saint Efprit: comme vos Peres ont fait, auffi faites-vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 52, 'Lequel des Prophetes vos Peres n\'ont-ils point perfecuté? Ils ont mefme tué ceux-là qui ont predit l\'avenement du Jufte, duquel maintenant vous avez efté traiftres &amp; meurtriers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 53, 'Vous qui avez receu la Loi par la difpofition des Anges, &amp; ne l\'avez point gardée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 54, 'Eux oyans ces chofes crevoyent en leurs coeurs, &amp; grinçoyent les dents contre lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 55, 'Mais lui eftant rempli du Saint Efprit, ayant les yeux fichez vers le ciel, vid la gloire de Dieu, &amp; Jefus eftant à la dextre de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 56, 'Et il dit, Voici, je vois les cieux ouverts, &amp; le Fils de l\'homme eftant à la dextre de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 57, 'Alors ils s\'efcrierent à haute voix, &amp; eftouperent leurs oreilles, &amp; tous d\'un accord fe jetterent fur lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 58, 'Et l\'ayant jetté hors de la ville ils le lapidoyent: &amp; les tefmoins mirent leurs veftemens aux pieds d\'un jeune homme nommé Saul.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 59, 'Et ils lapidoyent Eftienne invoquant &amp; difant, Seigneur Jefus, reçoi mon efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 60, 'Puis s\'eftant mis à genoux, il s\'efcria à haute voix, Seigneur, ne leur impute point ce peché. Et quand il eut dit cela, il s\'endormit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 1, 'Or Saul eftoit confentant à la mort d\'icelui. Et en ce tems-là il fe fit une grande prefecution contre l\'Eglife qui eftoit à Jerufalem: dont tous furent efpars par les quartiers de Judée &amp; de Samarie, hormis les Apoftres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 2, 'Et quelques hommes craignans Dieu, emporterent Eftienne pour l\'enfevelir, &amp; menerent un grand deüil fur lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 3, 'Mais Saul ravageoit l\'Eglife, entrant par toutes les maifons: &amp; trainant par force hommes &amp; femmes, il les mettoit en prifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 4, 'Ceux donc qui furent efpars, alloyent ça &amp; là annonçans la parole de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 5, 'Et Philippe eftant defcendu en une ville de Samarie, leur prefcha Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 6, 'Et les troupes eftoyent attentives d\'un accord à ce que Philippe difoit, oyans &amp; voyans les miracles qu\'il faifoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 7, 'Car les efprits immondes en criant à haute voix fortoyent hors de plufieurs qui en eftoyent detenus, &amp; beaucoup de perclus &amp; de boiteux furent gueris.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 8, 'Dont il avint une grande joye en cette ville-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 9, 'Or il y avoit eu auparavant en la ville un homme qui s\'appelloit Simon, qui exerçoit l\'art d\'enchanteur, &amp; enforceloit le peuple de Samarie, fe difant eftre quelque grand perfonnage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 10, 'Auquel tous eftoyent attentitfs, depuis le plus petit jufques au plus grand, difans, Celui-ci eft la vertu de Dieu, la grande.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 11, 'Et ils eftoyent attentifs à lui, parce que dés long-tems il les avoit enforcelez d\'entendement par fes enchantemens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 12, 'Mais quand ils eurent creu à Philippe annonçant ce qui appartient au royaume de Dieu, &amp; au Nom de Jefus Chrift, tant hommes que femmes furent baptizez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 13, 'Et Simon creut auffi lui-mefme: lequel apres avoir efté baptizé, ne bougeoit d\'auprés de Philippe: &amp; voyant les fignes &amp; les vertus qui fe faifoyent, eftoit ravi comme hors de foi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 14, 'Or quand les Apoftres qui eftoyent à Jerufalem, eurent entendu que Samarie avoit receu la parole de Dieu, ils leur envoyerent Pierre &amp; Jean:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 15, 'Lefquels eftans là defcendus prierent pour eux, afin qu\'ils receuffent le Saint Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 16, '(Car il n\'eftoit point encore defcendu fur aucun d\'eux, mais feulement ils eftoyent baptizez au Nom du Seigneur Jefus.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 17, 'Puis ils leur impoferent les mains, &amp; ils receurent le S.Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 18, 'Alors Simon ayant apperceu que par l\'impofition des mains des Apoftres, le Saint Efprit eftoit donné, il leur prefenta de l\'argent,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 19, 'Difant, Donnez-moi auffi cette puiffance, que tous ceux à qui j\'impoferai les mains, reçoivent le Saint Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 20, 'Mais Pierre lui dit, Ton argent periffe avec toi, de ce que tu as eftimé que le don de Dieu s\'acquiert par argent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 21, 'Tu n\'as point de part ni d\'heritage en cette affaire: car ton coeur n\'eft point droit devant Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 22, 'Repen-toi donc de cette tienne malice, &amp; prie Dieu fi d\'aventure la penfée de ton coeur te pourroit eftre pardonnée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 23, 'Car je vois que tu es en un fiel tres-amer, &amp; en lien d\'iniquité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 24, 'Alors Simon refpondit, &amp; dit, Vous, priez pour moi envers le Seigneur, afin que rien ne vienne fur moi des chofes que vous avez dites.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 25, 'Eux donc, apres avoir teftifié &amp; annoncé la parole du Seigneur, retournerent à Jerufalem, &amp; annoncerent l\'Evangile en plufieurs bourgades des Samaritains.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 26, 'Puis apres l\'Ange du Seigneur parla à Philippe, difant, Leve-toi, &amp; t\'en va vers le Midi, au chemin qui defcend de Jerufalem à Gaza, celle qui eft deferte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 27, 'Lui donc fe levant, s\'en alla: &amp; voici un homme Ethiopien, Eunuque, qui eftoit l\'un des principaux Seigneurs de la Cour de Candace Reine des Ethiopiens, commis fur toutes fes richeffes, &amp; qui eftoit venu pour adorer à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 28, 'Or il s\'en retournoit eftant affis en fon chariot, &amp; lifoit le Prophete Efaïe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 29, 'Alors l\'Efprit dit à Philippe, Approche, &amp; t\'adjoin à ce chariot.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 30, 'Et Philippe accourut, &amp; l\'ouït qu\'il lifoit le Prophete Efaïe, &amp; lui dit, Mais entens-tu ce que tu lis?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 31, 'Lequel dit, Et comment le pourrois-je entendre, fi quelqu\'un ne me guide? Et il pria Philippe de monter, &amp; de s\'affeoir avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 32, 'Or le paffage de l\'Ecriture qu\'il lifoit eftoit celui-ci, Il a efté mené comme une brebis à la tuërie, &amp; comme un agneau muët devant celui qui le tond; ainfi il n\'a point ouvert fa bouche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 33, 'En fon abbaiffement fon jugement a efté hauffé: mais qui recitera fa durée? car fa vie eft enlevée de la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 34, 'Alors l\'Eunuque prit la parole, &amp; dit à Philippe, Je te prie, de qui eft-ce que le Prophete dit cela? de foi, ou de quelque autre?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 35, 'Alors Philippe ouvrant fa bouche, &amp; commençant par cette Efcriture, lui annonça Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 36, 'Et comme ils alloyent par le chemin, ils vinrent à quelque eau: alors l\'Eunuque dit, Voici de l\'eau: qui eft-ce qui m\'empefche d\'eftre baptifé?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 37, 'Et Philippe dit, Si tu crois de tout ton coeur, il eft permis. L\'Eunuque donc refpondant, dit, Je crois que Jefus-Chrift eft le Fils de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 38, 'Et il commanda que le chariot fuft arrefté: &amp; tous deux defcendirent en l\'eau, Philippe &amp; l\'Eunuque: &amp; il le baptiza.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 39, 'Et quand ils furent remontez de l\'eau, l\'Efprit du Seigneur ravit Philippe, &amp; l\'Eunuque ne le vid plus: &amp; il s\'en alla joyeux par fon chemin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 40, 'Mais Philippe fe retrouva à Azote: &amp; en paffant il annonça l\'Evangile par toutes les villes, jufques à ce qu\'il vint à Cefarée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 1, 'Or saul tout enflammé encore de menaces &amp; de tuërie, contre les difciples du Seigneur, s\'eftant adreffé au fouverain Sacrificateur,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 2, 'Lui demanda des lettres de fa part pour porter à Damas aux Synagogues: afin que s\'il en trouvoit quelques-uns de cette fecte, hommes &amp; femmes, il les amenaft liez à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 3, 'Or il avint qu\'en cheminant il approcha de Damas, &amp; fubitement une lumiere refplendit du ciel comme un efclair à l\'entour de lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 4, 'Dont eftant tombé par terre, il ouït une voix qui lui difoit, Saul, Saul, pourquoi me perfecutes-tu?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 5, 'Et il lui refpondit, Qui es-tu, Seigneur? Et le Seigneur dit, Je fuis Jefus, lequel tu perfecutes: il t\'eft dur de regimber contre les aiguillons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 6, 'Et tout tremblant &amp; effrayé il dit, Seigneur, que veux-tu que je faffe? Et le Seigneur lui dit, Leve-toi, &amp; entre en la ville, &amp; là il te fera dit ce que tu dois faire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 7, 'Or les hommes qui cheminoyent avec lui s\'arrefterent tout épouvantez, oyans bien la voix, mais ne voyans perfonne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 8, 'Et Saul fe leva de terre, &amp; ouvrant fes yeux ne voyoit perfonne: &amp; ils le condifirent par la main, &amp; le menerent à Damas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 9, 'Où il fut trois jours fans voir, &amp; fans manger ni boire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 10, 'Or il y avoit un certain difciple à Damas, nommé Ananias, auquel le Seigneur dit en vifion, Ananias: Et il dit, Me voici, Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 11, 'Et le Seigneur lui dit, Leve-toi, &amp; t\'en va en la ruë qui eft nommée la Droite, &amp; cherche en la maifon de Judas un nommé Saul, qui eft de Tarfe: car voila il prie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 12, '(Or Saul avoit veu en vifion un perfonnage nommé Ananias, entrant, &amp; lui impofant la main, afin qu\'il recouvraft la veuë.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 13, 'Et Ananias refpondit, Seigneur, j\'ai ouï parler à plufieurs touchant ce perfonnage, combien de maux il a fait à tes Saincts à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 14, 'Mefmes ici il a authorité de par les principaux Sacrificateurs, de lier tous ceux qui invoquent ton Nom.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 15, 'Mais le Seigneur lui dit, Va: car il m\'eft un inftrument d\'élite, pour porter mon Nom devant les Gentils, &amp; les Rois, &amp; les enfans d\'Ifraël.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 16, 'Car je lui montrerai combien il lui faut fouffrir pour mon Nom.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 17, 'Ananias donc s\'en alla, &amp; entra en la maifon: &amp; lui impofant les mains, dit, Frere Saul, le Seigneur Jefus qui t\'eft apparu par le chemin par lequel tu venois, m\'a envoyé afin que tu recouvres la veuë, &amp; que tu fois rempli du Sainct Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 18, 'Et auffi-toft il cheut de fes yeux comme des efcailles, &amp; à l\'inftant il recouvra la veuë: puis il fe leva, &amp; fut baptizé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 19, 'Et ayant mangé il reprit fes forces. Ainfi Saul fut quelques jours avec les difciples qui eftoyent à Damas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 20, 'Et prefcha incontinent és Synagogues, que Chrift eftoit le Fils de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 21, 'Dont tous ceux qui l\'oioyent, eftoyent comme ravis hors d\'eux-mefmes, &amp; difoyent, N\'eft-ce pas celui-ci qui a deftruit à Jerufalem ceux qui invoquoyent ce Nom-là, &amp; eft venu ici expres afin qu\'il les amenaft liez aux principaux Sacrificateurs?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 22, 'Mais Saul fe fortifioit de plus en plus, &amp; confondoit les Juifs qui habitoyent à Damas, confirmant que celui-ci eftoit le Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 23, 'Or long-temps apres, les Juifs comploterent enfemble pour le mettre à mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 24, 'Mais leurs embufches vinrent à la connoiffance de Saul. Or ils gardoyent les portes jour &amp; nuict, afin de le mettre à mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 25, 'Mais les difciples le prenans de nuict, le defcendirent par la muraille, en le devallant en une corbeille.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 26, 'Et quand Saul fut venu à Jerufalem, il tafchoit de fe joindre aux difciples: mais ils le craignoyent tous, ne croyans point qu\'il fuft difciple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 27, 'Mais Barnabas le prit, &amp; le mena aux Apoftres, &amp; leur recita comment par le chemin il avoit veu le Seigneur, qui avoit parlé à lui: &amp; comment il avoit parlé franchement à Damas au Nom de Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 28, 'Ainfi donc il eftoit allant &amp; venant avec eux à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 29, 'Et fe portant franchement au Nom du Seigneur Jefus, il parloit, &amp; difputoit contre les Grecs: mais ils tafchoyent de le mettre à mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 30, 'Ce que les freres connoiffant, ils le menerent à Cefarée, &amp; l\'envoyerent à Tarfe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 31, 'Ainfi donc les Eglifes par toute la Judée, &amp; la Galilée, &amp; la Samarie avoyent paix, eftant edifiées &amp; cheminant en la crainte du Seigneur, &amp; eftoyent multipliées par la confolation du Saint Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 32, 'Or il avint que comme Pierre paffoit par devers tous, il vint auffi vers les Saints qui habitoyent à Lydde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 33, 'Et trouva là un homme nommé Enée, qui depuis huict ans gifoit en un petit lict, &amp; eftoit paralytique.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 34, 'Et Pierre lui dit, Enée, Jefus-Chrift te gueriffe: leve-toi, &amp; te fai ton lict: &amp; incontinent il fe leva.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 35, 'Et tous ceux qui habitoyent à Lydde &amp; à Saron, le virent: lefquels furent convertis au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 36, 'Or il y avoit auffi à Joppe une certaine difciple nommée Tabitha, qui finifie Dorcas: laquelle eftoit pleine de bonnes oeuvres &amp; d\'aumofnes qu\'elle faifoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 37, 'Il avint en ces jours-là qu\'elle devint malade &amp; mourut. Et quand ils l\'eurent lavée, ils la mirent en une chambre haute.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 38, 'Et dautant que Lydde eftoit prés de Joppe, les difciples entendans que Pierre eftoit à Lydde, envoyerent vers lui deux hommes, le priant qu\'il ne tardaft point de venir jufqu\'à eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 39, 'Pierre donc fe leva, &amp; s\'en vint avec eux. Et quand il fut arrivé, ils le menerent en la chambre haute: &amp; toutes les veuves fe prefenterent à lui en pleurant, &amp; montrant combien Dorcas faifoit de robbes &amp; de veftemens quand elle eftoit avec elles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 40, 'Mais Pierre apres les avoir tous envoyez dehors, fe mit à genoux, &amp; pria: puis en fe retournant vers le corps, il dit, Tabitha, leve-toi. Et elle ouvrit fes yeux, &amp; voyant Pierre elle fe raffit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 41, 'Alors il lui donna la main, &amp; la leva: puis ayant appellé les Saints &amp; les veuves, il la leur prefenta vivante.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 42, 'Et cela fut connu par toute Joppe: &amp; plufieurs creurent au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 43, 'Et il avint qu\'il demeura plufieurs jours à Joppe, chez un certain Simon conroyeur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 1, 'Or il y avoit un certain perfonnage à Cefarée, nommé Corneille, Centenier de la bande appellée Italique.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 2, 'Homme devot &amp; craignant Dieu, avec toute fa famille, faifant auffi beaucoup d\'aumofnes au peuple, &amp; priant Dieu affiduellement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 3, 'Il vid en vifion manifeftement, environ les neuf heures du jour, un Ange de Dieu qui vint à lui, &amp; lui dit, Corneille.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 4, 'Et icelui ayant les yeux fichez vers lui, &amp; effrayé dit, Qu\'y a-til, Seigneur? Et il lui dit, Tes oraifons &amp; tes aumofnes font montées en memoire devant Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 5, 'Maintenant donc envoye des gens à Joppe, &amp; envoye querir Simon qui eft furnommé Pierre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 6, 'Il eft logé chez un certain Simon conroyeur, qui a fa maifon prés de la mer: celui-là te dira ce qu\'il te faut faire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 7, 'Quand l\'Ange qui parloit à Corneille s\'en fut allé, il appella deux de fes ferviteurs, &amp; un foldat craignant Dieu, d\'entre ceux qui fe tenoyent autour de lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 8, 'Aufquels ayant recité le tout, il les envoya à Joppe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 9, 'Or le lendemain comme ils cheminoyent &amp; approchoyent de la ville, Pierre monta fur la maifon pour prier, environ les fix heures.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 10, 'Et il avint qu\'ayant faim, il voulut prendre fa refection: &amp; comme ceux de la maifon lui appreftoyent à manger, il lui furvint un raviffement d\'efprit:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 11, 'Et il vid le ciel ouvert, &amp; un vaiffeau defcendant fur lui comme un grand linceul lié par les quatre bouts, &amp; devallant en terre:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 12, 'Auquel il y avoit de toutes fortes d\'animaux terreftres, à quatre pieds, &amp; des beftes fauvages, &amp; des reptiles, &amp; des oifeaux du ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 13, 'Et une voix lui fut adreffée, difant, Pierre, leve-toi, tuë &amp; mange.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 14, 'Mais Pierre refpondit, Ainfi n\'advienne, Seigneur: car jamais je ne mangeai aucune chofe polluë ou foüillée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 15, 'Et la voix dit encore pour la feconde fois, Les chofes que Dieu a purifiées, ne les tien point pour polluës.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 16, 'Or cela avint par trois fois: &amp; puis apres le vaiffeau fe retira au ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 17, 'Et comme Pierre eftoit en perplexité en foi-mefme, quelle vifion c\'eftoit qu\'il avoit veuë: alors voici, les hommes qui eftoyent envoyez de la part de Corneille, s\'enquerans de la maifon de Simon, furvinrent à la porte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 18, 'Et ayant appellé quelqu\'un, demanderent fi Simon, qui eftoit furnommé Pierre, eftoit logé là-dedans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 19, 'Et comme Pierre penfoit à la vifion, l\'Efprit lui dit, Voici trois perfonnages qui te demandent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 20, 'Leve-toi donc &amp; defcen, &amp; t\'en va avec eux, fans en faire difficulté: car c\'eft moi qui les ai envoyez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 21, 'Pierre donc eftant defcendu vers les gens qui lui avoyent efté envoyez de la part de Corneille, leur dit, Voici, je fuis celui que vous cherchez: quelle eft la caufe pour laquelle vous eftes venus?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 22, 'Lefquels dirent, Corneille Centenier, homme jufte &amp; craignant Dieu, &amp; ayant tefmoignage de toute la nation des Juifs, &amp; efté adverti de Dieu par un S.Ange, de t\'envoyer querir pour venir en fa maifon, &amp; t\'ouïr parler.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 23, 'Alors Pierre les ayant fait entrer, les logea, &amp; le lendemain il s\'en alla avec eux, &amp; quelques-uns des freres de Joppe lui tinrent compagnie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 24, 'Et le lendemain ils entrerent à Cefarée. Or Corneille les attendoit, ayant appellé fes parens &amp; fes familiers amis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 25, 'Et il avint que comme Pierre entroit, Corneille venant au devant de lui, &amp; fe jettant à fes pieds, l\'adora.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 26, 'Mais Pierre le releva, difant, Leve-toi: je fuis auffi homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 27, 'Puis parlant avec lui, il entra, &amp; trouva plufieurs qui s\'eftoyent là affemblez:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 28, 'Et leur dit, Vous fçavez comme il n\'eft pas permis à un homme Juif de fe joindre, ou d\'aller vers un eftranger: mais Dieu m\'a montré que je ne die aucun homme eftre pollu ou foüillé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 29, 'C\'eft pourquoi auffi eftant envoyé querir, je fuis venu fans contredire: je vous demande donc, Pour quelle caufe m\'avez-vous envoyé querir?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 30, 'Alors Corneille lui dit, Il y\'a quatre jours à cette heure que j\'eftois en jeufne, &amp; faifois la priere à neuf heures en ma maifon: alors voici un homme fe prefenta devant moi en un veftement reluifant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 31, 'Et dit, Corneille, ta priere eft exaucée, &amp; tes aumofnes ont efté ramentuës devant Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 32, 'Envoye donc à Joppe, &amp; envoye querir de-là Simon furnommé Pierre, qui eft logé en la maifon de Simon conroyeur prés de la mer, lequel eftant venu parlera à toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 33, 'C\'eft pourquoi j\'ai incontinent envoyé vers toi, &amp; tu as bien fait de venir. Or maintenant nous fommes tous prefens devant Dieu, pour ouïr ce qui t\'eft commandé de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 34, 'Alors Pierre ayant ouvert fa bouche, dit, En verité j\'apperçois que Dieu n\'a point d\'efgard à l\'apparence des perfonnes:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 35, 'Mais qu\'en toute nation celui qui le craint, &amp; s\'adonne à juftice, lui eft agreable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 36, 'C\'eft ce qu\'il a envoyé finifier aux enfans d\'Ifraël, annonçant la paix par Jefus Chrift, lequel eft le Seigneur de tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 37, 'Vous fçavez la parole avenuë par toute la Judée en commençant par la Galilée, apres le Baptefme que Jean a prefché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 38, 'C\'eft affavoir, comme Dieu a oinct du Saint Efprit &amp; de vertu Jefus le Nazarien, lequel a paffé de lieu en lieu, en bien-faifant, &amp; en gueriffant tous ceux qui eftoyent oppreffez du diable: car Dieu eftoit avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 39, 'Et nous fommes tefmoins de toutes les chofes qu\'il a faites, tant au païs des Juifs qu\'à Jerufalem: lequel ils ont fait mourrir le pendant au bois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 40, 'C\'eft celui-là que Dieu a reffufcité au troifiéme jour, &amp; qu\'il a donné pour eftre manifefté;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 41, 'Non point à tout le peuple, mais aux tefmoins auparavant ordonnez de Dieu: à nous qui avons mangé &amp; beu avec lui, apres qu\'il a efté reffufcité des morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 42, 'Et il nous a commandé de prefcher au peuple, &amp; de tefmoigner que c\'eft lui qui eft ordonné de Dieu pour eftre le juge des vivans &amp; des morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 43, 'Tous les Prophetes lui rendent tefmoignage, que quiconque croira en lui, recevra remiffion de fes pechez par fon Nom.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 44, 'Comme Pierre tenoit encore ce difcours, le Saint Efprit defcendit fur tous ceux qui efcoutoyent ce qu\'il difoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 45, 'Dont les fideles de la Circoncifion qui eftoyent venus avec Pierre, s\'eftonnerent que le don du Saint Efprit eftoit auffi refpandu fur les Gentils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 46, 'Car ils les oioyent parler des langages, &amp; magnifier Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 47, 'Alors Pierre prit la parole, difant, Quelqu\'un pourroit-il empefcher qu\'on baptizaft d\'eau ceux qui ont receu le Saint Efprit comme nous?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 48, 'Et il commanda qu\'ils fuffent baptizez au nom du Seigneur. Alors ils le prierent de demeurer là quelques jours.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 1, 'Or les Apoftres &amp; les freres qui eftoyent en Judée, entendirent comme les Gentils auffi avoyent receu la parole de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 2, 'Quand donc Pierre fut remonté à Jerufalem, ceux de la Circoncifion difputoyent avec lui:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 3, 'Difans, Tu es entré chez des hommes incirconcis, &amp; as mangé avec eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 4, 'Alors Pierre commençant leur expofa le tout par ordre, difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 5, 'J\'eftois en priere en la ville de Joppe, &amp; eftant ravi en efprit, je vis une vifion, affavoir un vaiffeau defcendant comme un grand linceul qui fe devalloit du ciel, lié par les quatre bouts, &amp; qui vint jufqu\'à moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 6, 'Dans lequel ayant jetté mes yeux, j\'apperceus, &amp; vis des animaux terreftres, à quatre pieds, &amp; des beftes fauvages, &amp; des reptiles, &amp; des oifeaux du ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 7, 'J\'ouïs une voix qui me dit, Pierre, leve-toi, tuë, &amp; mange.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 8, 'Et je refpondis, Ainfi n\'advienne, Seigneur: car jamais chofe polluë ou foüillée n\'entra en ma bouche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 9, 'Et derechef la voix me refpondit du ciel, Ce que Dieu a purifié, ne le tien point pour pollu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 10, 'Et cela fe fit jufques à trois fois: &amp; derechef toutes ces chofes furent retirées au ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 11, 'Puis voici, trois hommes en ce mefme inftant fe prefenterent en la maifon où j\'eftois, ayant efté envoyez de Cefarée vers moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 12, 'Et l\'efprit me dit que j\'allaffe avec eux, fans en faire difficulté: auffi ces fix freres-ici vinrent avec moi, &amp; nous entrafmes en la maifon du perfonnage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 13, 'Et il nous recita comme il avoit veu un Ange en fa maifon qui s\'eftoit prefenté à lui, &amp; lui avoit dit, Envoye des gens à Joppe, &amp; envoye querir Simon qui eft furnommé Pierre:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 14, 'Lequel te dira des paroles par lefquelles tu feras fauvé, toi &amp; toute ta maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 15, 'Et quand j\'eus commencé à parler, le Saint Efprit defcendit fur eux, comme auffi il eftoit defcendu fur nous au commencement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 16, 'Alors je me fouvins de la parole du Seigneur, comme il difoit, Jean a baptizé d\'eau: mais vous ferez baptizez du Saint Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 17, 'Puis donc que Dieu leur a donné un pareil don comme à nous, qui avons creu au Seigneur Jefus-Chrift, qui eftois-je moi, qui peuffe empefcher Dieu?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 18, 'Alors ayant ouï ces chofes, ils s\'appaiferent &amp; glorifierent Dieu, difans, Dieu donc a auffi donné aux Gentils repentance pour avoir la vie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 19, 'Or quant à ceux auffi qui avoyent efté efpars par l\'oppreffion advenuë à caufe d\'Eftienne: ils pafferent jufqu\'en Phenice, &amp; en Cypre, &amp; à Antioche, fans annoncer à perfonne la Parole, finon aux Juifs feulement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 20, 'Toutefois il y en eut quelques-uns d\'entr\'eux, Cypriens, &amp; Cyreniens, lefquels eftans entrez à Antioche, parloyent aux Grecs, annonçans le Seigneur Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 21, 'Et la main du Seigneur eftoit avec eux: tellement qu\'un grand nombre ayant creu, fut converti au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 22, 'Dont le bruit en vint jufques aux oreilles de l\'Eglife qui eftoit à Jerufalem: à raifon de quoi ils envoyerent Barnabas, pour paffer jufqu\'à Antioche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 23, 'Lequel eftant arrivé, &amp; ayant veu la grace de Dieu, fe réjouït, &amp; les exhortoit tous de perfeverer avec fermeté de coeur au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 24, 'Car il eftoit homme de bien, &amp; plein du Saint Efprit &amp; de foi: &amp; une grande multitude fut adjointe au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 25, 'Puis Barnabas s\'en alla à Tarfe, pour rechercher Saul:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 26, 'Et l\'ayant trouvé, il le mena à Antioche: &amp; il avint que tout l\'an entier, ils s\'affemblerent avec l\'Eglife, &amp; enfeignerent un grand peuple, tellement qu\'à Antioche premierement les difciples furent nommez Chreftiens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 27, 'En ces jours-là quelques Prophetes defcendirent de Jerufalem à Antioche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 28, 'Et l\'un d\'eux nommé Agabus fe leva, &amp; fignifia par l\'Efprit, qu\'une grande famine devoit avenir par tout le monde: laquelle auffi avint fous Claude Cefar.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 29, 'Et les difciples chacun felon fon pouvoir, determinerent d\'envoyer quelque chofe pour fubvenir aux freres demeurans en Judée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 30, 'Ce qu\'ils firent auffi, l\'envoyant aux Anciens par les mains de Barnabas &amp; de Saul.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 1, 'Et en ce mefme temps-là le roi Herode fe mit à mal-mener quelques-uns de ceux de l\'Eglife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 2, 'Et mit à mort par l\'efpée Jaques frere de Jean.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 3, 'Et voyant que cela eftoit agreable aux Juifs, il fit encore davantage, &amp; s\'avança d\'empoigner auffi Pierre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 4, 'Or c\'eftoit és jours des pains fans levain. Et quand il l\'eut empoigné, il le mit en prifon &amp; le bailla à garder à quatre quatraines de foldats: le voulant produire au fupplice devant le peuple apres la fefte de Pafque.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 5, 'Ainfi Pierre eftoit gardé en la prifon: mais l\'Eglife faifoit fans ceffe des prieres à Dieu pour lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 6, 'Et comme Herode le devoit produire au fupplice, Pierre dormoit cette nuit-là entre deux foldats, lié de deux chaines, &amp; les gardes devant la porte gardoyent la prifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 7, 'Et voici, un Ange du Seigneur furvint, &amp; une lumiere refplendit en la prifon: &amp; frappant le cofté de Pierre, il l\'efveilla, difant, Leve-toi legerement. Et les chaines tomberent de fes mains.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 8, 'Alors l\'Ange lui dit, Cein-toi, &amp; chauffe tes fouliers. Ce qu\'il fit. Puis il lui dit, Jette ta robbe fur toi &amp; me fui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 9, 'Lui donc fortant, le fuivit, &amp; il ne fçavoit point que ce qui fe faifoit par l\'Ange fuft vrai: mais il penfoit voir quelque vifion.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 10, 'Et quand ils eurent paffé la premiere &amp; la feconde garde, ils vinrent à la porte de fer, qui fe rend en la ville, laquelle s\'ouvrit à eux d\'elle-mefme; &amp; eftans fortis ils pafferent une ruë &amp; incontinent l\'Ange fe departit de lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 11, 'Alors Pierre eftant revenu à foi-mefme, dit, Je connois maintenant pour vrai que le Seigneur a envoyé fon Ange, &amp; m\'a delivré de la main d\'Herode, &amp; de toute l\'attente du peuple des Juifs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 12, 'Et ayant confidéré le tout, il vint en la maifon de Marie, mere de Jean furnommé Marc: où plufieurs eftoyent affemblez, &amp; faifans des prieres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 13, 'Et quand il eut heurté à la porte du porche, une fille nommée Rhode vint pour efcouter:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 14, 'Laquelle ayant connu la voix de Pierre, de joye n\'ouvrit point le porche: mais s\'encourut en la maifon, &amp; annonça que Pierre eftoit devant le porche:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 15, 'Et ils lui dirent, Tu es folle: Mais elle au contraire affirmoit qu\'il eftoit ainfi: &amp; eux difoyent, C\'eft fon Ange.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 16, 'Mais Pierre continuoit à heurter: &amp; quand ils eurent ouvert, ils le virent, &amp; furent comme ravis hors d\'eux-mefmes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 17, 'Et lui leur ayant fait figne de la main qu\'ils fiffent filence, leur recita comment le Seigneur l\'avoit mis hors de la prifon, &amp; leur dit, Annoncez ces chofes à Jaques &amp; aux freres. Puis fortant de là, il s\'en alla en un autre lieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 18, 'Mais le jour eftant venu, il y eut un grand trouble entre les foldats, pour fçavoir ce que Pierre feroit devenu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 19, 'Et Herode l\'ayant cherché, &amp; ne le trouvant point, apres en avoir fait le procez aux gardes, commanda qu\'ils fuffent menez au fupplice. Puis il defcendit de Judée à Cefarée, où il fejourna.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 20, 'Or il eftoit en humeur de faire la guerre aux Tyriens &amp; aux Sidoniens. Mais ils vinrent à lui tous d\'un accord: &amp; ayans gagné Blafte qui eftoit chambellan du Roi, ils demanderent la paix, parce que leur païs eftoit nourri de celui du roi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 21, 'Et en un certain jour ordonné, Herode reveftu d\'une robbe royale, s\'affit au fiege judicial, &amp; les haranguoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 22, 'Dont le peuple s\'efcria, Voix de Dieu, &amp; non point d\'homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 23, 'Et à l\'inftant un Ange du Seigneur le frappa, parce qu\'il n\'avoit point donné gloire à Dieu: il fut rongé de vermine, &amp; rendit l\'efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 24, 'Mais la parole du Seigneur croiffoit &amp; fe multiplioit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 25, 'Barnabas auffi &amp; Saul, apres avoir accompli leur charge, retournerent de Jerufalem, ayans auffi pris avec eux Jean, qui eftoit furnommé Marc.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 1, 'Or il y avoit à Antioche, en l\'Eglife qui y eftoit, certains Prophetes &amp; Docteurs, affavoir Barnabas, &amp; Simeon, qui eftoit appellé Niger, &amp; Lucius le Cyrenien, &amp; Manahem, (qui avoit efté nourri avec Herode le Tetrarque) &amp; Saul.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 2, 'Eux donc fervans en leur miniftere au Seigneur, &amp; eftans en jeufne, le Saint Efprit dit, Separez-moi Barnabas &amp; Saul, pour l\'oeuvre auquel je les ai appellez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 3, 'Alors ayans jeufné &amp; prié, &amp; leur ayans impofé les mains, ils leur baillerent congé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 4, 'Eux donc eftant envoyez du Saint Efprit, defcendirent à Seleucie: &amp; delà navigerent en Cypre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 5, 'Et quand ils furent à Salamis, ils annoncerent la parole de Dieu dans les Synagogues des Juifs: &amp; avoyent auffi Jean pour aide.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 6, 'Puis ayant traverfé l\'Ifle jufqu\'à Paphos, ils trouverent là un certain enchanteur, faux prophete Juif, qui avoit nom Barjefu:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 7, 'Lequel eftoit avec le Proconful Serge Paul, homme prudent. Lui ayant fait appeller Barnabas &amp; Saul, defiroit d\'ouïr la parole de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 8, 'Mais Elymas, c\'est à dire, enchanteur (car fon nom eft ainfi expofé) leur refiftoit, cherchant à deftourner le Proconful de la foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 9, 'Mais Saul (qui eft auffi appellé Paul) eftant rempli du Saint Efprit, ayant les yeux fichez fur lui, dit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 10, 'O plein de toute fraude &amp; de toute rufe: fils du diable, ennemi de toute juftice, ne cefferas-tu point de renverfer les voyes du Seigneur qui font droites?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 11, 'Pource voici maintenant la main du Seigneur fur toi, &amp; tu feras aveugle fans voir le foleil jufqu\'à un certain temps. Et à l\'inftant obfcurité &amp; tenebres tomberent fur lui, &amp; tournoyant il cherchoit qui le conduiroit par la main.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 12, 'Alors le Proconful, voyant ce qui eftoit advenu, creut, eftant tout ravi en admiration de la doctrine du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 13, 'Et quand Paul &amp; ceux qui eftoyent avec lui, furent partis de Paphos, ils vinrent à Perge, ville de Pamphilie. Alors Jean s\'eftant retiré d\'avec eux, s\'en retourna à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 14, 'Et eux partans de Perge, vinrent à Antioche ville de Pifidie: là où eftant entrez en la Synagogue au jour du Sabbat, ils s\'affirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 15, 'Or apres la lecture de la Loi &amp; des Prophetes, les principaux de la Synagogue envoyerent vers eux, difans, Hommes freres, s\'il y a de voftre part quelque parole d\'exhortation pour le peuple, dites-la.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 16, 'Alors Paul s\'eftant levé, &amp; ayant fait figne de la main qu\'on fift filence, dit, Hommes Ifraëlites, &amp; vous qui craignez Dieu, oyez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 17, 'Le Dieu de ce peuple d\'Ifraël a éleu nos peres, &amp; a hauffé ce peuple de temps qu\'ils demeuroyent au païs d\'Egypte, &amp; les en fit fortir avec un bras élevé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 18, 'Et a fupporté leur train au defert, environ le temps de quarante ans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 19, 'Puis ayant deftruit fept nations au païs de Canaan, il leur a diftribué leur païs par fort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 20, 'Et environ quatre cens cinquante ans apres cela, il leur donna des Juges jufqu\'à Samuel le Prophete.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 21, 'Puis apres ils demanderent un roi, &amp; Dieu leur donna Saül fils de Kis, homme de la lignée de Benjamin: ainfi fe pafferent quarante ans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 22, 'Et Dieu l\'ayant ofté, leur fufcita David pour roi, duquel auffi il donna tefmoignage, &amp; dit, J\'ai trouvé David fils de Jeffé, perfonnage felon mon coeur, lequel fera toute ma volonté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 23, 'Dieu, felon fa promeffe, a fufcité de la femence d\'icelui le Sauveur à Ifraël, affavoir Jefus:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 24, 'Jean ayant auparavant prefché le Baptefme de repentance à tout le peuple d\'Ifraël, devant l\'advenement d\'icelui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 25, 'Et comme Jean achevoit fa courfe, il difoit, Qui me prefumez-vous eftre? Je ne fuis point celui-là; mais voici, il en vient un apres moi, duquel je ne fuis pas digne de délier le foulier de fes pieds.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 26, 'Hommes freres, enfans de la race d\'Abraham, &amp; ceux qui d\'entre vous craignez Dieu, c\'eft à vous que la parole de ce falut a efté envoyée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 27, 'Car les habitans de Jerufalem, &amp; leurs gouverneurs, ne l\'ayant point connu, ont mefmes en le condamnant accompli les paroles des Prophetes, qui fe lifent chaque Sabbat.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 28, 'Et bien qu\'ils ne trouvaffent en lui aucun crime digne de mort, ils requirent Pilate qu\'il le mift à mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 29, 'Et apres qu\'ils eurent accompli toutes les chofes qui eftoyent écrites de lui, on l\'ofta du bois, &amp; on le mit en un fepulcre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 30, 'Mais Dieu l\'a reffufcité des morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 31, 'Et il a efté veu par plufieurs jours, de ceux qui eftoyent montez enfemble avec lui de Galilée à Jerufalem: lefquels font fes tefmoins envers le peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 32, 'Et nous auffi vous annonçons touchant la promeffe qui a efté faite à nos peres,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 33, 'Que Dieu l\'a accomplie envers nous qui fommes leurs enfans, ayant fufcité Jefus, comme auffi il eft efcrit au Pfeaume fecond, Tu es mon Fils, je t\'ai aujourd\'hui engendré.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 34, 'Et pour montrer qu\'il l\'a reffufcité des morts, pour ne devoir plus retourner au fepulcre, il a dit ainfi, Je vous donnerai les fainctetez de David affeurées.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 35, 'Et c\'eft pourquoi il dit auffi en un autre endroit, Tu ne permettras point que ton Saint fente de corruption.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 36, 'Car certes David, apres avoir en fon temps fervi au confeil de Dieu, s\'eft endormi: &amp; a efté mis avec fes peres, &amp; a fenti de la corruption.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 37, 'Mais celui que Dieu a reffufcité, n\'a point fenti de corruption.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 38, 'Qu\'il vous foit donc notoire, hommes freres, que par lui vous eft annoncée la remiffion des pechez:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 39, 'Et que de tout ce dont vous n\'avez peu eftre juftifiez par la Loi de Moyfe, quiconque croit eft juftifié par lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 40, 'Regardez donc qu\'il ne vous avienne ce qui eft dit és Prophetes,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 41, 'Voyez contempteurs, &amp; vous eftonnez, &amp; foyez évanouïs: car je m\'en vais faire une oeuvre en voftre temps; voire une oeuvre que vous ne croirez point fi quelqu\'un vous la recite.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 42, 'Puis eftant partis de la Synagogue des Juifs, les Gentils les prierent qu\'au Sabbat enfuivant ils leur annonçaffent ces paroles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 43, 'Et quand l\'affemblée fut departie, plufieurs des Juifs &amp; profelytes fervans Dieu, fuivirent Paul &amp; Barnabas, lefquels en parlant à eux les exhorterent de perfeverer en la grace de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 44, 'Mais au jour du Sabbat enfuivant, prefque toute la ville s\'affembla pour ouïr la parole de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 45, 'Alors les Juifs voyans les troupes, furent remplis d\'envie, &amp; contredifoyent à ce que Paul difoit, voire contredifans &amp; blafphemans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 46, 'Alors Paul &amp; Barnabas s\'eftant enhardis, dirent, C\'eftoit bien à vous qu\'il faloit premierement annoncer la parole de Dieu: mais puis que vous la rejettez &amp; que vous vous jugés vous-mefmes indignes de la vie eternelle, voici nous nous tournons vers les Gentils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 47, 'Car le Seigneur nous l\'a ainfi commandé, difant, Je t\'ai ordonné pour eftre la lumiere des Gentils, afin que tu fois en falut jufques au bout de la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 48, 'Et les Gentils oyans cela, s\'ejouïffoyent &amp; glorifioyent la parole du Seigneur: &amp; tous ceux qui eftoyent ordonnez à la vie eternelle, creurent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 49, 'Ainfi la parole du Seigneur s\'efpandoit par tout le païs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 50, 'Mais les Juifs inciterent quelques femmes devotes &amp; honorables, &amp; les principaux de la ville, &amp; efmeurent perfecution contre Paul &amp; Barnabas, &amp; les jetterent hors de leurs quartiers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 51, 'Mais eux ayans fecoüé la poudre de leurs pieds contre eux, s\'en vinrent à Iconie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 52, 'Et les difciples eftoyent remplis de joye, &amp; du S.Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 1, 'Or il avint à Iconie, qu\'ils entrerent enfemble en la Synagogue des Juifs, &amp; parlerent en telle façon, qu\'une grande multitude de Juifs &amp; de Grecs, creut.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 2, 'Mais les Juifs qui eftoyent rebelles, efmeurent &amp; irriterent les efprits des Gentils contre les freres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 3, 'Ainfi donc ils demeurerent là affez long-temps, fe portans hardiment au Seigneur, lequel rendoit témoignage à la parole de fa grace, donnant que des fignes &amp; des merveilles fe fiffent par leurs mains.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 4, 'Mais la multitude de la ville fut partagée eu deux, &amp; les uns eftoyent du cofté des Juifs, &amp; les autres du cofté des Apoftres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 5, 'Et comme il fe preparoit une efmeute des Gentils &amp; des Juifs avec leurs Gouverneurs, pour leur faire outrage, &amp; pour le lapider:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 6, 'La chofe entenduë, ils s\'enfuïrent aux villes de Lycaonie, affavoir à Lyftre &amp; à Derbe, &amp; aux quartiers d\'alentour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 7, 'Et ils eftoyent là annonçans l\'Evangile.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 8, 'Or un certain homme de Lyftre, impotent de fes pieds, eftoit là affis, boiteux dés le ventre de fa mere, lequel n\'avoit jamais cheminé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 9, 'Il ouït parler Paul, lequel ayant fiché fes yeux fur lui, &amp; voyant qu\'il avoit la foi d\'etre gueri,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 10, 'Dit à haute voix, Leve-toi droit fur tes pieds. Et il faillit, &amp; chemina.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 11, 'Et les troupes ayant veu ce que Paul avoit fait, éleverent leurs voix, difans en langage Lycaonique, Les dieux eftans devenus femblables aux hommes, font defcendus vers nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 12, 'Et ils appelloyent Barnabas Jupiter, &amp; Paul Mercure, parce que c\'eftoit lui qui portoit la parole.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 13, 'Et mefme le Sacrificateur de Jupiter, qui eftoit devant leur ville, ayant amené des taureaux couronnez, jufqu\'à l\'netrée de la porte, vouloit facrifier avec la foule.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 14, 'Mais quand les Apoftres, Barnabas &amp; Paul, eurent entendu cela, ils defchirerent leurs veftemens, &amp; fe jetterent au milieu de la troupe, s\'efcrians,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 15, 'Et difans, Hommes, pourquoi faites-vous ces chofes? Nous fommes auffi hommes, fujets à mefmes affections que vous, vous annonçans que de telles chofes vaines vous vous convertiffiez au Dieu vivant, qui a fait le ciel &amp; la terre, la mer, &amp; toute les chofes qui font en eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 16, 'Lequel és temps paffez a laiffé toutes les Nations cheminer en leurs voyes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 17, 'Nonobftant qu\'il ne s\'eft point laiffé fans témoignage, en bien faifant, &amp; en nous donnant des pluyes du ciel, &amp; des faifons fertiles, &amp; rempliffant nos coeurs de viande &amp; de joye.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 18, 'Et en difant ces chofes, à peine appaiferent-ils les troupes qu\'elles ne leur facrifiaffent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 19, 'Alors quelques Juifs furvinrent d\'Antioche, &amp; d\'Iconie: lefquels gagnerent le peuple, tellement qu\'ayans lapidé Paul, ils le traifnerent hors de la ville, penfans qu\'il fuft mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 20, 'Mais les difciples s\'eftant affemblez à l\'entour de lui, il fe leva, &amp; entra en la ville, &amp; le lendemain il s\'en alla avec Barnabas à Derbe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 21, 'Et apres qu\'ils eurent annoncé l\'Evangile en cette ville-là, &amp; endoctriné plufieurs, ils retournerent à Lyftre, à Iconie, &amp; à Antioche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 22, 'Confirmans les courages des difciples, &amp; les exhortans de perfifter en la foi, &amp; leur remonftrans que c\'eft par plufieurs afflicitions qu\'il nous faut entrer au royaume de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 23, 'Et apres que par l\'avis des affemblées, ils eurent eftabli des Anciens par chaque Eglife, ayans prié avec jeufnes, ils les recommanderent au Seigneur, auquel ils avoyent creu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 24, 'Puis ayans traverfé la Pifidie, ils s\'en vinrent en Pamphilie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 25, 'Et ayant annoncé la parole à Perge, ils defcendirent à Attalie:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 26, 'Et de-là navigerent à Antioche, d\'où ils avoyent efté recommandez à la grace de Dieu, pour l\'oeuvre qu\'ils avoyent accomplie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 27, 'Et quand ils furent venus, &amp; eurent affemblé l\'Eglife, ils reciterent toutes les chofes que Dieu avoit faites par eux, &amp; qu\'il avoit ouvert aux Gentils la porte de la foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 28, 'Et ils demeurerent là long-temps avec les difciples.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 1, 'Or quelques-uns, qui eftoyent defcendus de Judée, enfeignoyent les freres, difans, Si vous n\'eftes circoncis felon l\'ufage de Moyfe, vous ne pouvez eftre fauvez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 2, 'Dont un grand debat &amp; une grande difpute eftant furvenuë à Paul &amp; à Barnabas contr\'eux, il fut ordonné que Paul &amp; Barnabas, &amp; quelques autres d\'entr\'eux, monteroyent à Jerufalem vers les Apoftres &amp; les Anciens, pour cette queftion.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 3, 'Eux donc eftant envoyez de par l\'Eglife traverferent la Phenice &amp; la Samarie, recitans la converfion des Gentils: &amp; donnerent une grande joye à tous les freres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 4, 'Puis eftant arrivez à Jerufalem, ils furent receus de l\'Eglife, &amp; des Apoftres, &amp; des Anciens, &amp; annoncerent toutes les chofes que Dieu avoit faites par eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 5, 'Mais quelques-uns, difoyent-ils, de la fecte des Pharifiens qui ont creu, fe font levez, difans, qu\'il les faut circoncir, &amp; leur commander de garder la Loi de Moyfe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 6, 'Alors les Apoftres &amp; les Anciens s\'affemblerent pour avifer à cette affaire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 7, 'Et apres une grande difpute, Pierre fe leva, &amp; leur dit, Hommes freres, vous fçavez que dés long-temps Dieu a éleu d\'entre nous, que les Gentils ouïffent par ma bouche la parole de l\'Evangile, &amp; creuffent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 8, 'Et Dieu qui connoit les coeurs, leur en a rendu témoignage, en leur donnant le Saint Efprit, comme auffi à nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 9, 'Et n\'a point fait de difference entre nous &amp; eux, ayant purifié leurs coeurs par la foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 10, 'Maintenant donc pourquoi tentez-vous Dieu, pour mettre un joug fur le col des difciples, lequel ni nos peres ni nous n\'avons peu porter?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 11, 'Mais nous croyons que nous ferons fauvez par la grace du Seigneur Jefus Chrift, comme eux auffi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 12, 'Alors toute la multitude fe teut: &amp; ils efcoutoyent Barnabas &amp; Paul, recitans quels fignes &amp; quelles merveilles Dieu avoit faites par eux entre les Gentils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 13, 'Et apres qu\'ils fe furent teus Jaques prit la parole, difant, Hommes freres, efcoutez-moi:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 14, 'Simeon a recité comme Dieu a premierement regardé les Gentils, pour en prendre un peuple à fon Nom.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 15, 'Et à cela s\'accordent les paroles des Prophetes, felon qu\'il eft efcrit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 16, 'Apres ceci je retournerai &amp; rebaftirai le tabernacle de David, qui eft decheu, &amp; rebaftirai fes ruïnes, &amp; le redrefferai:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 17, 'Afin que le refte des hommes recherche le Seigneur, &amp; toutes les nations fur lefquelles mon Nom eft reclamé, dit le Seigneur, qui fait toutes ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 18, 'De tous temps font à Dieu connuës toutes fes oeuvres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 19, 'C\'eft pourquoi je fuis d\'avis de ne point fafcher ceux des Gentils qui fe convertiffent à Dieu:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 20, 'Mais bien de leur efcrire qu\'ils ayent à s\'abftenir des foüillures des idoles, &amp; de paillardife, &amp; des chofes eftouffées, &amp; du fang.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 21, 'Car quant à Moyfe, il a d\'ancienneté des gens par chaque ville qui le prefchent, veu qu\'és Synagogues il eft leu chaque Sabbat.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 22, 'Alors il fembla bon aux Apoftres &amp; aux Anciens avec toute l\'Eglife, d\'envoyer à Antioche des gens choifis d\'entr\'eux, avec Paul &amp; Barnabas, affavoir Judas furnommé Barfabas, &amp; Silas, hommes principaux entre les freres:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 23, 'En efcrivant par eux ce qui s\'enfuit: Les Apoftres, &amp; les Anciens, &amp; les freres, aux freres qui font des Gentils, à Antioche, &amp; en Syrie, &amp; en Cilicie, falut.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 24, 'Parce que nous avons entendu que quelques-uns eftant partis d\'entre nous, vous ont troublez par quelques propos, renverfans vos ames, en vous commandant d\'eftre circoncis, &amp; de garder la Loi, aufquels nous n\'en avions point donné de charge:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 25, 'Nous avons efté d\'avis, eftant affemblez d\'un accord, d\'envoyer vers vous des perfonnages que nous avons choifis, avec nos tres-chers Barnabas &amp; Paul.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 26, 'Perfonnages qui ont abandonné leurs vies pour le Nom du Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 27, 'Nous avons donc envoyé Judas &amp; Silas, lefquels auffi vous feront entendre le mefme de bouche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 28, 'Car il a femblé bon au Saint Efprit &amp; à nous, de ne mettre point de plus grande charge fur vous que ces chofes neceffaires:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 29, 'C\'eft que vous vous abfteniez des chofes facrifiées aux idoles, &amp; du fang, &amp; des chofes eftouffées, &amp; de paillardife: defquelles chofes fi vous vous gardez, vous ferez bien. Bien vous foit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 30, 'Eux donc ayans pris congé, vinrent à Antioche: &amp; ayant affemblé la multitude, ils rendirent les lettres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 31, 'Et quand ils les eurent leuës, ils furent réjouïs de la confolation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 32, 'Pareillement auffi Judas &amp; Silas, qui eftoyent auffi Prophetes, exhorterent les freres par plufieurs paroles, &amp; les confirmerent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 33, 'Et quand ils eurent demeuré-là quelque efpace de temps, il furent renvoyez en paix par les freres vers les Apoftres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 34, 'Toutefois il fembla bon à Silas de demeurer-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 35, 'Et Paul &amp; Barnabas demeurerent à Antioche, enfeignans &amp; annonçans, avec auffi plufieurs autres, la parole du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 36, 'Et quelques jours apres, Paul dit à Barnabas, Or fus, retournons, &amp; vifitons nos freres, par toutes les villes efquelles nous avons annoncé la parole du Seigneur, pour voir comment ils fe portent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 37, 'Et Barnabas confeilloit de prendre avec eux Jean, furnommé Marc.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 38, 'Mais il ne fembloit pas raifonnable à Paul, que celui qui s\'eftoit départi d\'avec eux dés la Pamphylie, &amp; n\'eftoit point allé avec eux en cette oeuvre-là, leur fuft adjoint.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 39, 'Dont il y eut tel different, qu\'ils fe feparerent l\'un de l\'autre, &amp; que Barnabas prenant Marc, navigera en Cypre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 40, 'Mais Paul ayant choifi Silas pour l\'accompagner, fe départit eftant recommandé à la grace de Dieu par les freres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 41, 'Et traverfa la Syrie &amp; la Cilicie confirmant les Eglifes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 1, 'Or il arriva à Derbe &amp; à Lyftre: &amp; voici, il y avoit là un certain difciple nommé Timothée, fils d\'une femme Juifve, fidele, mais d\'un pere Grec.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 2, 'Lequel avoit un bon témoignage des freres qui eftoyent à Lyftre &amp; à Iconie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 3, 'C\'eft pourquoi Paul voulut qu\'il allaft avec lui: &amp; l\'ayant pris avec foi, le circoncit, à caufe des Juifs qui eftoyent en ces lieux-là: car tous fçavoyent que fon pere eftoit Grec.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 4, 'Eux donc paffans par les villes, les inftruifoyent de garder les ordonnances decretées par les Apoftres, &amp; par les Anciens de Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 5, 'Ainfi les Eglifes eftoyent confirmées en la foi, &amp; croiffoyent en nombre chaque jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 6, 'Puis ayant traverfé la Phrygie &amp; la contrée de Galatie, il leur fut defendu par le Saint Efprit d\'annoncer la Parole en Afie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 7, 'Eftans donc venus en Myfie, ils effaioyent d\'aller en Bithynie: mais l\'Efprit de Jefus ne leur permit point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 8, 'C\'eft pourquoy ayans paffé la Myfie, ils defcendirent à Troas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 9, 'Or il apparut de nuit à Paul une vifion, c\'eft qu\'un homme Macedonien, fe prefentant devant lui, le pria, difant, Paffe en Macedone, &amp; nous aide.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 10, 'Quand donc il eut veu la vifion, incontinent nous tafchafmes d\'aller en Macedone, concluans par cela que le Seigneur nous avoit appellez pour leur evangelizer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 11, 'Ainfi eftans partis de Troas, nous tirafmes droit à Samothrace, &amp; le lendemain à Neapolis:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 12, 'Et de-là à Philippes, qui eft la premiere ville du quartier de Macedone, &amp; eft colonie: &amp; fejournafmes quelques temps en la ville.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 13, 'Et au jour du Sabbat nous fortifmes hors de la ville, auprés du fleuve où l\'on avoit accouftumé de faire la priere: &amp; eftans là affis, nous parlions aux femmes qui y eftoyent affemblées.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 14, 'Alors une certaine femme nommée Lydie, de la ville de Thyatire, marchande de pourpre, fervante à Dieu, nous ouït: de laquelle le Seigneur ouvrit le coeur pour entendre aux chofes que Paul difoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 15, 'Et quand elle eut efté baptizée avec fa famille, elle nous pria, difant, Si vous m\'avez eftimée eftre fidele au Seigneur, entrez en ma maifon, &amp; y demeurez: Et elle nous contraignit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 16, 'Or il advint comme nous allions à la priere, qu\'une certaine fervante qui avoit l\'efprit de Python, nous rencontra, laquelle faifoit un grand profit à fes maiftres par fes devinemens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 17, 'Elle fuivant Paul &amp; nous, crioit, difant: Ces gens font ferviteurs du Dieu fouverain, lefquels nous annoncent la voye de falut.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 18, 'Et elle fit cela par plufieurs jours: mais Paul en eftant ennuyé, fe retournant dit à l\'efprit, Je te commande au Nom de Jefus Chrift que tu fortes d\'elle. Et il en fortit au mefme inftant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 19, 'Alors les maiftres voyans que l\'efperance de leur profit eftoit perduë, empoignerent Paul &amp; Silas, &amp; les tirerent en la place du marché aux Magiftrats:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 20, 'Et les prefenterent aux Gouverneurs, difans, Ces gens-ci eftans Juifs, troublent noftre ville:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 21, 'Et annoncent des ordonnances qu\'il ne nous eft point permis de recevoir, ni de garder, veu que nous fommes Romains.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 22, 'Auffi le populaire s\'éleva enfemble contr\'eux: &amp; les Gouverneurs leur defchirans leurs robbes, commanderent qu\'ils fuffent foüettez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 23, 'Et apres leur avoir donné plufieurs coups de foüet, ils les mirent en prifon, commandant au Geolier qu\'il les gardaft feurement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 24, 'Lequel ayant receu un tel commandement, les mit au fond de la prifon, &amp; leur enferra les pieds aux ceps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 25, 'Or fur la minuict Paul &amp; Silas prioient, chantans des loüanges à Dieu: tellement que les prifonniers les entendoyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 26, 'Et foudainement il advint un grand tremblement de terre, de forte que les fondemens de la prifon croufloyent: &amp; incontinent toutes les portes furent ouvertes, &amp; les liens de tous furent lafchez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 27, 'Alors le Geolier eftant éveillé, &amp; voyant les portes de la prifon ouvertes, tira fon efpée, &amp; fe vouloit tuër, penfant que les prifonniers s\'en fuffent fuïs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 28, 'Mais Paul cria à haute voix, difant, Ne te fai point de mal: car nous fommes tous ici.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 29, 'Alors ayant demandé de la lumiere il faillit dedans, &amp; tremblant fe jetta aux pieds de Paul &amp; de Silas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 30, 'Et les ayant menez hors il leur dit, Seigneurs, que me faut-il faire pour eftre fauvé?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 31, 'Ils lui dirent, Croi au seigneur Jefus Chrift, &amp; tu feras fauvé, toi &amp; ta maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 32, 'Et ils lui annoncerent la parole du Seigneur, &amp; à tous ceux qui eftoyent en fa maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 33, 'Apres cela, les prenant en cette mefme heure de la nuit, il lava leurs playes: puis incontinent il fut baptizé, lui &amp; tous fes domeftiques.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 34, 'Et les ayant amenez en fa maifon, il leur dreffa la table, &amp; s\'éjouït, parce qu\'avec toute fa maifon il avoit creu en Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 35, 'Et le jour eftant venu, les Gouverneurs y envoyerent des fergens, difans, Baille congé à ces gens-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 36, 'Alors le Geolier rapporta ces paroles à Paul, difant, Les Gouverneurs ont envoyé dire qu\'on vous baillaft congé: maintenant donc fortez, &amp; vous en allez en paix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 37, 'Mais Paul leur dit, Apres nous avoir foüettez publiquement, fans forme de jugement, nous qui fommes Romains, ils nous ont mis en prifon: &amp; maintenant ils nous mettent dehors en cachette: il n\'en fera point ainfi: mais qu\'ils viennent eux-mefmes, &amp; qu\'ils nous mettent dehors.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 38, 'Et les fergens rapporterent ces paroles aux Gouverneurs, lefquels craignirent, ayant entendu qu\'ils eftoyent Romains.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 39, 'C\'eft pourquoi ils vinrent vers eux, &amp; les prierent, &amp; les ayant mis hors, ils les requirent qu\'ils partiffent de la ville.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 40, 'Alors eftant fortis de la prifon, ils entrerent chez Lydie: &amp; ayant veu les freres, ils les confolerent, &amp; puis ils partirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 1, 'Puis ayant traverfé par Amphipolis &amp; Apollonie, ils vinrent à Theffalonique, où il y avoit une Synagogue de Juifs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 2, 'Et Paul, felon fa couftume, entra vers eux, &amp; par trois Sabbats difputoit avec eux par les Efcritures:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 3, 'Leur declarant &amp; propofant qu\'il avoit fallu que le Chrift fouffrift, &amp; reffufcitaft des morts: &amp; que ce Jefus eftoit le Chrift, lequel, difoit-il, je vous annonce.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 4, 'Et quelques uns d\'entr\'eux creurent, &amp; furent adjoints à Paul &amp; à Silas, &amp; une grande multitude de Grecs fervans Dieu, &amp; de femmes de qualité en affez grand nombre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 5, 'Mais les Juifs rebelles, eftant efmeus d\'envie, prirent certains vautneans bateurs de pavé, lefquels ayans fait un amas de peuple, efmeurent toute la ville, &amp; faifans effort à la maifon de Jafon, cherchoyent à les amener vers le peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 6, 'Et ne les ayans point trouvez, ils tirerent Jafon &amp; quelques freres aux Gouverneurs de la ville, en criant, Ceux-ci qui ont remüé tout le monde, font auffi venus ici.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 7, 'Lefquels Jafon a retirez chez foi: &amp; eux tous font contre le decret de Cefar, difans, Qu\'il y a un autre roi qu\'ils nomment Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 8, 'Ils efmeurent donc le peuple, &amp; les Gouverneurs de la ville qui entendoyent ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 9, 'Mais apres avoir receu caution de Jafon &amp; des autres, ils les laifferent aller.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 10, 'Et incontinent les freres mirent hors de nuit Paul &amp; Silas, pour aller à Berée: lefquels eftans venus là, entrerent en la Synagogue des Juifs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 11, 'Or ceux-ci furent plus courageux que les Juifs qui eftoyent à Theffalonique, entant qu\'ils receurent la parole avec toute promptitude, conferans journellement les Efcritures, pour fçavoir s\'il eftoit ainfi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 12, 'Plufieurs donc d\'entr\'eux creurent, &amp; des femmes Grecques honorables, &amp; des hommes en affez grand nombre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 13, 'Mais quand les Juifs de Theffalonique fceurent que la parole de Dieu eftoit auffi annoncée par Paul à Berée, ils y vinrent &amp; efmeurent le peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 14, 'Mais alors les freres envoyerent incontinent Paul dehors, comme pour aller vers la mer: mais Silas &amp; Timothée demeurerent encore là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 15, 'Et ceux qui avoyent pris la charge de mettre Paul à fauveté, le menerent jufqu\'à Athenes, &amp; apres avoir receu mandement de fa part à Silas &amp; à Timothée, de revenir bien-toft vers lui, ils partirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 16, 'Or comme Paul les attendoit à Athenes, fon efprit s\'aigriffoit en lui-mefme, confiderant la ville du tout adonnée à l\'idolatrie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 17, 'Il difputoit donc en la Synagogue avec les Juifs, &amp; avec les devots, &amp; tous les jours en la place du marché avec ceux qui fe rencontroyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 18, 'Alors quelques-uns d\'entre les Philofophes Epicuriens &amp; Stoïciens s\'adrefferent à lui en paroles: &amp; les uns difoyent, Mais que veut dire ce babillard? Et les autres difoyent, Il femble eftre annonciateur des Dieux eftranges: parce qu\'il leur annonçoit Jefus &amp; la refurrection.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 19, 'Et l\'ayant apprehendé, ils le menerent en l\'Areopage, difans, Ne pourrons-nous point fçavoir quelle eft cette nouvelle doctrine dont tu parles?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 20, 'Car tu nous mets de certaines chofes eftranges en nos oreilles: nous voulons donc fçavoir ce que veulent dire ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 21, 'Or tous les Atheniens &amp; les eftrangers qui fe tenoyent là, ne vaquoyent à autre chofe qu\'à dire ou à ouïr quelque nouvelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 22, 'Alors Paul fe tenant au milieu de l\'Areopage, dit, Hommes Atheniens, je vous apperçois en toutes chofes hommes par trop devotieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 23, 'Car en paffant &amp; contemplant vos devotions, j\'ai trouvé mefmes un autel auquel il eftoit efcrit, AU DIEU INCONNU. Celui donc que vous honorez fans le connoiftre, c\'eft celui que je vous annonce.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 24, 'Le Dieu qui a fait le monde &amp; toutes les chofes qui y font, eftant Seigneur du ciel &amp; de la terre, n\'habite point en des temples faits de mains:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 25, 'Et il n\'eft point fervi par les mains des hommes comme ayant neceffité de quelque chofe, veu que c\'eft lui qui donne à tous la vie, &amp; la refpiration, &amp; toutes chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 26, 'Et il a fait d\'un feul fang tout le genre humain, pour habiter fur toute l\'eftenduë de la terre: ayant determiné les faifons qu\'il a auparavant ordonnées, &amp; les bornes de leur habitation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 27, 'Afin qu\'ils cherchent le Seigneur, fi en quelque forte ils pourroyent le toucher en taftonnant, &amp; le trouver: encore qu\'il ne foit pas loin de chacun de nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 28, 'Car par lui avons-nous la vie, &amp; le mouvement, &amp; l\'eftre: comme mefmes quelques-uns de vos Poëtes ont dit: Car auffi nous fommes fon lignage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 29, 'Eftans donc le lignage de Dieu, nous ne devons nullement eftimer que la Divinité foit femblable à or, ou à argent, ou à pierre taillée par art &amp; par invention d\'homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 30, 'Dieu donc, ayant diffimulé les temps de l\'ignorance, maintenant denonce à tous hommes en tous lieux qu\'ils ayent à fe repentir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 31, 'Parce qu\'il a ordonné un jour, auquel il doit juger le monde univerfel en juftice, par l\'homme qu\'il a determiné, dont il a donné certitude à tous, l\'ayant reffufcité des morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 32, 'Et quand ils ouïrent ce mot de la refurrection des morts, les uns s\'en mocquoyent, &amp; les autres difoyent, Nous t\'orrons derechef fur cela.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 33, 'Ainfi Paul fortit du milieu d\'eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 34, 'De certains perfonnages toutefois fe joignirent à lui, &amp; creurent: entre lefquels eftoit auffi Denis l\'Areopagite, &amp; une femme nommée Damaris, &amp; d\'autres avec eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 1, 'Or apres ces chofes Paul partant d\'Athenes, vint à Corinthe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 2, 'Et ayant trouvé là un certain Juif nommé Aquile, de la nation de Ponte, qui un peu auparavant eftoit venu d\'Italie, avec Prifcille fa femme, (parce que Claude avoit commandé que tous les Juifs vuidaffent de Rome) il s\'adreffa à eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 3, 'Et parce qu\'il eftoit de mefme meftier, il demeura avec eux, &amp; travailloit. Et leur meftier eftoit de faire des tentes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 4, 'Et il difputoit en la Synagogue chaque Sabbat, &amp; induifoit à croire tant les Juifs que les Grecs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 5, 'Et quand Silas &amp; Timothée furent venus de Macedone, Paul eftant enferré en fon efprit, teftifioit aux Juifs que Jefus eftoit le Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 6, 'Et comme ils s\'oppofoyent, &amp; blafphemoyent, ayant fecoüé fes veftemens, il leur dit, Voftre fang foit fur voftre tefte: j\'en fuis net: dés maintenant je m\'en vais vers les Gentils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 7, 'Alors eftant parti de là il entra en la maifon d\'un nommé Jufte, fervant Dieu, duquel la maifon tenoit à la Synagogue.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 8, 'Et Crifpe principal de la Synagogue creut au Seigneur avec toute fa maifon: plufieurs autres auffi des Corinthiens l\'ayant ouï, creurent, &amp; furent baptifez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 9, 'Or le Seigneur dit de nuit par vifion à Paul, Ne crain point, mais parle, &amp; ne te tai point:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 10, 'Car je fuis avec toi, &amp; nul ne mettra les mains fur toi pour te malfaire: car j\'ai un grand peuple en cette ville-ci.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 11, 'Il demeura donc là un an &amp; fix mois, enfeignant entr\'eux la parole de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 12, 'Mais du temps que Gallion eftoit Proconful d\'Achaïe, les Juifs tous d\'un accord s\'éleverent contre Paul, &amp; l\'amenerent au fiege judicial,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 13, 'Difans, Celui-ci induit les gens à fervir Dieu contre la Loi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 14, 'Et comme Paul vouloit ouvrir fa bouche, Gallion dit aux Juifs, O Juifs! fi c\'eftoit la matiere de quelque outrage, ou malefice, je vous fupporterois autant qu\'il feroit raifonnable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 15, 'Mais s\'il eft queftion de paroles, &amp; de mots, &amp; de voftre Loi, vous y regarderez vous-mefmes: car je ne veux point eftre juge de ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 16, 'Et il les chaffa du fiege judicial.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 17, 'Alors tous les Grecs ayant empoigné Softhenes le principal de la Synagogue, le battoyent devant le fiege judicial, &amp; Gallion ne s\'en foucioit pas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 18, 'Mais quand Paul eut encore efté là affez long-temps, il prit congé des freres, &amp; navigea en Syrie, (&amp; avec lui Prifcille &amp; Aquile) apres qu\'il fe fut fait raire la tefte à Cenchrée: car il avoit un voeu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 19, 'Puis il arriva à Ephefe, &amp; les laiffa là: mais eftant entré en la Synagogue, il difputa avec les Juifs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 20, 'Lefquels le prians qu\'il demeuraft encore plus long-temps avec eux, il ne s\'y voulut point accorder.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 21, 'Mais il prit congé d\'eux, difant, Il me faut abfolument faire la fefte prochaine à Jerufalem: mais je retournerai encore vers vous, s\'il plaift à Dieu. Ainfi il defancra d\'Ephefe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 22, 'Et quand il fut defcendu à Cefarée, il monta à Jerufalem: &amp; apres avoir falüé l\'Eglife il defcendit à Antioche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 23, 'Puis ayant fejourné là quelque temps, il s\'en alla, traverfant tout d\'un train la contrée de Galatie &amp; de Phrygie, confirmans tous les difciples.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 24, 'Mais un certain Juif nommé Apollos, Alexandrin de nation, homme eloquent &amp; puiffant és Ecritures, vint à Ephefe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 25, 'Lequel eftoit aucunement inftruit en la voye du Seigneur: &amp; eftant en ferveur d\'efprit, il parloit, &amp; enfeignoit diligemment les chofes qui font du Seigneur, connoiffant feulement le Baptefme de Jean.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 26, 'Il commença donc à parler franchement en la Synagogue. Et quand Prifcille &amp; Aquile l\'eurent ouï, ils le prirent avec eux, &amp; lui declarerent plus avant la voye de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 27, 'Et comme il vouloit paffer en Achaïe, les freres l\'ayant exhorté à cela, efcrivirent aux difciples qu\'ils le receuffent: lequel eftant arrivé là, profita beaucoup à ceux qui avoyent creu par la grace.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 28, 'Car il convainquoit publiquement les Juifs en grande vehemence, demontrant par les Efcritures que Jefus eftoit le Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 1, 'Il advint comme Apollos eftoit à Corinthe, que Paul, apres avoir traverfé tous les quartiers d\'en-haut, vint à Ephefe, où ayant trouvé de certains difciples, il leur dit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 2, 'Avez-vous receu le Saint Efprit, quand vous avez creu? Mais ils lui refpondirent, Nous n\'avons pas mefmes ouï dire s\'il y a un Saint Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 3, 'Alors il leur dit, En quoi donc avez-vous efté baptizez? Lefquels refpondirent, Au Baptefme de Jean.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 4, 'Alors Paul dit, Jean a bien baptizé du Baptefme de repentance, difant au peuple qu\'ils creuffent en celui qui venoit apres lui: affavoir en Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 5, 'Ayans ouï ces chofes, ils furent baptizez au Nom du Seigneur Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 6, 'Et apres que Paul leur eut impofé les mains, le Saint Efprit vint fur eux, &amp; ainfi ils parloyent des langages, &amp; prophetifoyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 7, 'Et tous ces perfonnages-là eftoyent environ douze.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 8, 'Puis il entra en la Synagogue, &amp; parloit franchement l\'efpace de trois mois, difputant &amp; induifant à ce qui eft du royaume de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 9, 'Mais comme quelques-uns s\'endurciffoyent, &amp; eftoyent rebelles, médifans de la voye du Seigneur devant la multitude, lui s\'eftant departi d\'avec eux, fepara les difciples, difputant tous les jours en l\'efcole d\'un certain Tyrannus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 10, 'Et cela continua l\'efpace de deux ans: tellement que tous ceux qui habitoyent en Afie, ouïrent la parole du Seigneur Jefus, tant Juifs que Grecs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 11, 'et Dieu faifoit des vertus non accouftumées, par les mains de Paul:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 12, 'De forte que mefmes on portoit de deffus fon corps des couvrechefs &amp; des mouchoirs fur les malades: &amp; leurs maladies fe departoyent d\'eux, &amp; les mauvais efprits fortoyent hors.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 13, 'Alors quelques-uns d\'entre les Juifs, exorciftes, qui trottoyent ça &amp; là, effayerent d\'invoquer le nom du Seigneur Jefus fur ceux qui avoyent de mauvais efprits, difans, Nous vous adjurons par Jefus que Paul prefche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 14, '(Et ceux qui faifoyent cela, eftoyent fept certains fils de Sceva Juif, l\'un des principaux Sacrificateurs.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 15, 'Mais l\'efprit malin refpondant, dit, Je connois Jefus, &amp; fçais qui eft Paul: mais vous autres, qui eftes-vous?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 16, 'Et l\'homme en qui eftoit le malin efprit, fautant fur eux, &amp; s\'en eftant rendu maiftre, ufa de force contr\'eux, de forte qu\'ils s\'enfuïrent nuds &amp; navrez de cette maifon-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 17, 'Or cela vint à la connoiffance de tous les Juifs &amp; des Grecs demeurans à Ephefe: dont la crainte les faifit tous, &amp; le Nom du Seigneur Jefus eftoit magnifié.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 18, 'Et plufieurs de ceux qui avoyent creu, venoyent confeffans &amp; declarans leurs actes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 19, 'Plufieurs auffi de ceux qui s\'eftoyent adonnez à des chofes curieufes, apporterent leurs livres, &amp; les bruflerent devant tous: dont calculans le prix, ils trouverent qu\'il montoit à cinquante mille pieces d\'argent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 20, 'Ainfi la parole du Seigneur croiffoit puiffamment, &amp; fe renforçoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 21, 'Or comme ces chofes furent accomplies, Paul propofa par l\'efprit de paffer par la Macedone, &amp; par l\'Achaïe, &amp; d\'aller à Jerufalem, difant, Apres que j\'aurai efté là, il me faut auffi voir Rome.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 22, 'Et ayans envoyé en Macedone deux de ceux qui l\'affiftoyent, affavoir Timothée &amp; Erafte, il demeura pour un temps en Afie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 23, 'Mais en ce temps-là il avint un grand trouble, à caufe de la doctrine.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 24, 'Car un certain nommé Demetrius, qui travailloit d\'argenterie, &amp; faifoit de petits temples d\'argent de Diane, apportoit beaucoup de profit aux ouvriers du meftier:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 25, 'Lefquels ayant affemblé, avec ceux qui travailloyent en ces ouvrages-là, il dit, Hommes, vous fçavez que tout noftre gain vient de cet ouvrage:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 26, 'Or vous voyez &amp; oyez comme non feulement à Ephefe, mais prefque par toute l\'Afie, ce Paul-ci par fes perfuafions a deftourné une grande multitude, difant que ceux-là ne font point Dieux, qui font faits de mains.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 27, 'Et il n\'y a pas feulement de danger pour nous, en ce que noftre fait ne vienne à eftre décrié, mais auffi que le temple de la grande Diane ne foit plus rien eftimé du tout, &amp; qu\'il n\'avienne que fa majefté, que toute l\'Afie &amp; le monde univerfel a en reverence, ne vienne auffi à neant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 28, 'Eux donc, ayant ouï ces chofes, furent tout remplis de colere, &amp; s\'écrierent, difans, Grande eft la Diane des Ephefiens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 29, 'Et toute la ville fut remplie de confufion: &amp; ils fe jetterent tous d\'un courage dans le theatre, &amp; ravirent Gaïe &amp; Ariftarque Macedoniens, compagnons de voyage de Paul.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 30, 'Et comme Paul vouloit entrer vers le peuple, les difciples ne lui permirent point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 31, 'Quelques-uns auffi d\'entre les Afiarques qui lui eftoyent amis, envoyerent vers lui, le prians qu\'il ne fe prefentaft point au theatre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 32, 'Les uns donc crioyent d\'un, &amp; les autres d\'autre: car l\'affemblée eftoit confufe: &amp; plufieurs ne fçavoyent pour quelle caufe ils eftoyent affemblez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 33, 'Alors Alexandre fut avancé hors de la foule, les Juifs le pouffans en avant. Et Alexandre faifant figne de la main, vouloit alleguer quelque excufe au peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 34, 'Mais quand ils eurent connu qu\'il eftoit Juif, une voix de tous s\'éleva, quafi par l\'efpace de deux heures, en criant, Grande eft la Diane des Ephefiens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 35, 'Alors le Greffier ayant appaifé la multitude, dit, Hommes Ephefiens, &amp; qui eft celui des hommes qui ne fçache que la ville des Ephefiens eft dediée au fervice de la grande Diane, &amp; à l\'image qui eft defcenduë de Jupiter?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 36, 'Ces chofes donc eftant telles fans contredit, il faut que vous foyez appaifez, &amp; que vous ne faffiez rien à l\'eftourdie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 37, 'Car vous avez amené ces gens, qui ne font ni facrileges, ni diffamateurs de voftre Deeffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 38, 'Que fi Demetrius &amp; les ouvriers qui font avec lui, ont quelque caufe contre quelqu\'un, on tient les plaids, &amp; il y a des Proconfuls: qu\'ils s\'accufent l\'un l\'autre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 39, 'Et fi vous demandez quelque chofe d\'autre cas, cela fe pourra conclurre en une affemblée deuëment convoquée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 40, 'Car nous fommes en danger d\'eftre accufez de fedition pour ce jourd\'hui: veu qu\'il n\'y a caufe aucune pour laquelle nous puiffions rendre raifon de cette efmeute. Et quand il eut dit ces chofes, il bailla congé à l\'affemblée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 41, '');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 1, 'Or apres que le trouble fut ceffé, Paul ayant appellé les difciples, &amp; les ayant embraffez, partit pour aller en Macedone.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 2, 'Et quand il eut cheminé par ces quartiers-là, &amp; les eut exhortez par plufieurs paroles, il vint en Grece.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 3, 'Là où quand il eut efté trois mois, d\'autant que les Juifs lui dreffoyent des embufches, s\'il euft navigé en Syrie, l\'avis fut de retourner par Macedone.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 4, 'Et Sopater Beréen le devoit accompagner jufques en Afie: &amp; des Theffaloniciens, Ariftarque, &amp; Second, &amp; Gaïe Derbien, &amp; Timothée, &amp; de ceux d\'Afie, Tychique, &amp; Trophime.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 5, 'Ceux-ci donc eftant allez devant, nous attendirent à Troas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 6, 'Mais quant à nous, apres les jours des pains fans levain, nous defancrafmes de Philippes, &amp; vinfmes vers eux à Troas au bout de cinq jours: là où nous fejournafmes fept jours.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 7, 'Et le premier jour de la femaine, les difciples eftant affemblez pour rompre le pain, Paul (dautant qu\'il devoit partir le lendemain) traittoit de la parole avec eux: &amp; eftendit fon difcours jufqu\'à la minuict.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 8, 'Or il y avoit en la chambre haute, où ils eftoyent affemblez, beaucoup de lampes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 9, 'Et un jeune homme nommé Eutyche, affis fur une feneftre, eftant abbattu de profond fommeil, pendant que Paul traittoit de la parole plus longuement, emporté de fommeil cheut en bas du troifiéme eftage, &amp; fut levé mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 10, 'Mais Paul eftant defcendu, fe pancha fur lui, &amp; l\'embraffa, &amp; dit, Ne vous troublez point: car fon ame eft en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 11, 'Et quand il fut remonté: &amp; eut rompu le pain, &amp; mangé, &amp; parlé long-temps jufqu\'à l\'aube du jour, ainfi il partit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 12, 'Et ils amenerent là le jeune homme vivant, dont ils furent grandement confolez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 13, 'Or eftant venus au navire, nous fufmes portez à Affos: &amp; de là nous devions reprendre Paul: car il l\'avoit ainfi ordonné, voulant quant à lui faire ce chemin par terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 14, 'Quand donc il nous eut rencontrez à Affos, nous le prifmes avec nous, &amp; vinfmes à Metelin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 15, 'Puis eftant paris de là, le jour enfuivant nous vinfmes à l\'endroit de Chios. Le lendemain nous arrivafmes à Samos: &amp; ayans demeuré à Trogylle, le jour enfuivant nous vinfmes à Milet.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 16, 'Car Paul avoit propofé de paffer outre Ephefe, afin qu\'il ne lui falluft point fejourner en Afie: car il fe haftoit d\'eftre (s\'il lui eftoit poffible) le jour de la Pentecofte à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 17, 'Or il envoya de Milet à Ephefe, &amp; appella à foi les Anciens de l\'Eglife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 18, 'Lefquels eftant venus vers lui, il leur dit, Vous fçavez comme je me fuis porté toûjours avec vous, dés le premier jour que je fuis entré en Afie:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 19, 'Servant au Seigneur avec toute humilité, &amp; avec plufieurs larmes, &amp; tentations, lefquelles me font advenuës par les embufches des Juifs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 20, 'Comme je n\'ai rien retenu à dire des chofes qui vous eftoyent utiles, que je ne vous les aye prefchées &amp; enfeignées publiquement &amp; par les maifons:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 21, 'Teftifiant tant aux Juifs qu\'aux Grecs la repentance qui eft envers Dieu, &amp; la foi en Jefus-Chrift noftre Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 22, 'Et maintenant voici, eftant lié par l\'efprit, je m\'en vais à Jerufalem, ignorant les chofes qui m\'y doivent avenir:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 23, 'Sinon que le Saint Efprit m\'advertit de ville en ville, difant, Que des liens &amp; des tribulations m\'attendent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 24, 'Mais je ne fais cas de rien: &amp; ma vie ne m\'eft point precieufe, moyennant qu\'avec joye j\'acheve ma courfe, &amp; le miniftere que j\'ai receu du Seigneur Jefus, pour teftifier l\'Evangile de la grace de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 25, 'Et maintenant voici, je fçais que nul de vous tous, parmi lefquels j\'ai paffé, prefchant le royaume de Dieu, ne verra plus ma face.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 26, 'Pour ce je vous prens à tefmoins aujourd\'hui que je fuis net du fang de tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 27, 'Car je ne me fuis point retenu que je vous aye annoncé tout le confeil de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 28, 'Prenez donc garde à vous-mefmes, &amp; à tout le troupeau, auquel le Saint Efprit vous a eftablis Evefques, pour paiftre l\'Eglife de Dieu, laquelle il a acquife par fon propre fang.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 29, 'Car je fçais cela, qu\'apres mon depart il fe fourrera parmi vous des loups tres-dangereux, n\'efpargnans point le troupeau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 30, 'Et d\'entre vous-mefmes fe leveront des hommes annonçans des chofes perverfes, afin d\'attirer des difciples apres eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 31, 'Partant veillez, vous fouvenans comme par l\'efpace de trois ans, nuit &amp; jour, je n\'ai ceffé avec larmes d\'admonefter un chacun.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 32, 'Et maintenant, freres, je vous recommande à Dieu, &amp; à la parole de fa grace: lequel eft puiffant pour achever de vous edifier, &amp; de vous donner l\'heritage avec tous les Saints.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 33, 'Je n\'ai convoité l\'argent, ni l\'or, ni la robbe d\'aucun.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 34, 'Et vous fçavez vous-mefmes que ces mains ont fourni les chofes qui m\'eftoyent neceffaires, &amp; à ceux qui eftoyent avec moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 35, 'Je vous ai montré en tout, qu\'en travaillant ainfi, il faut fupporter les infirmes, &amp; fe fouvenir des paroles du Seigneur Jefus: car il a dit, Que c\'eft une chofe plus heureufe de donner que de recevoir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 36, 'Et quand il eut dit ces paroles, il fe mit à genoux, &amp; pria avec eux tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 37, 'Alors il y eut un grand pleur de tous: &amp; fe jettans fur le col de Paul, ils le baifoyent:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 38, 'Eftans triftes principalement pour la parole qu\'il avoit dite, Qu\'ils ne verroyent plus fa face. Et ils le conduifirent au navire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 1, 'Ainfi donc eftant partis &amp; reculez d\'eux, nous tirafmes tout droit à Coos, &amp; le jour enfuivant à Rhode, &amp; de là à Patara.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 2, 'Et ayant trouvé là un navire qui traverfoit en Phenice, nous montafmes deffus, &amp; partifmes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 3, 'Puis ayans defcouvert Cypre, nous la laiffafmes à main gauche: &amp; tirans vers la Syrie, nous arrivafmes à Tyr: car le navire devoit là defcharger fa charge.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 4, 'Et ayans trouvé là des difciples, nous y demeurafmes fept jours. Ils difoyent par l\'Efprit à Paul qu\'il ne montaft point à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 5, 'Mais ces jours-là eftant accomplis nous partifmes &amp; nous mifmes en chemin, eftant conduits de tous avec leurs femmes &amp; leurs enfans, jufques hors la ville: &amp; ayans mis les genoux en terre fur le rivage, nous priafmes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 6, 'Puis apres nous eftre embraffez les uns les autres, nous montafmes au navire, &amp; les autres retournerent chez eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 7, 'Ainfi achevant noftre navigation de Tyr, nous abordafmes à Ptolemaïs: &amp; apres avoir falüé les freres, nous demeurafmes un jour avec eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 8, 'Le lendemain Paul &amp; fa compagnie partirent, &amp; nous vinfmes à Cefarée: &amp; eftant entrez en la maifon de Philippe l\'Evangelifte (qui eftoit l\'un des fept) nous demeurafmes chez lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 9, 'Il avoit quatre filles vierges, lefquelles prophetifoyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 10, 'Et comme nous demeurions là plufieurs jours, il furvint un Prophete de Judée, nommé Agabus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 11, 'Lequel eftant venu vers nous, &amp; ayant pris la ceinture de Paul, s\'en lia les mains &amp; les pieds, difant, Le Saint Efprit dit ces chofes, Ainfi les Juifs lieront à Jerufalem, l\'homme à qui eft cette ceinture, &amp; le livreront és mains des Gentils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 12, 'Et quand nous eufmes entendu ces chofes, tant nous que ceux qui eftoyent du lieu, nous l\'exhortafmes qu\'il ne montaft point à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 13, 'Alors Paul refpondit, Que faites-vous, en pleurant, &amp; affligeant mon coeur? car pour moi, je fuis tout preft non feulement d\'eftre lié, mais auffi de mourir à Jerufalem pour le Nom du Seigneur Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 14, 'Ainfi, parce qu\'il ne pouvoit eftre induit à cela, nous nous en deportafmes, difans, La volonté du Seigneur foit faite.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 15, 'Et quelques jours apres, ayans chargé nos hardes, nous montafmes à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 16, 'Et quelques-uns des difciples vinrent auffi de Cefarée avec nous, amenans avec eux un certain Mnafon, qui eftoit Cyprien, ancien difciple, chez lequel nous devions loger.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 17, 'Quand nous fufmes venus à Jerufalem, les freres nous receurent fort volontiers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 18, 'Et le jour fuivant, Paul vint avec nous chez Jaques, &amp; tous les Anciens y furent affemblez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 19, 'Et apres qu\'il les eut embrassez, il recita de poinct en poinct les chofes que Dieu avoit faites entre les Gentils par fon miniftere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 20, 'Ce qu\'ayans ouï, ils glorifierent le Seigneur, &amp; lui dirent, Frere, tu vois combien il y a de milliers de Juifs qui ont creu, &amp; tous font zelateurs de la Loi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 21, 'Or ils ont efté informez de toi, que tu enfeignes tous les Juifs qui font entre les Gentils de fe départir de Moyfe, difant qu\'ils ne doivent point circoncir leurs enfans, ni cheminer felon les ftatuts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 22, 'Qu\'eft-il donc de faire? Il faut entierement affembler la multitude: car ils orront dire que tu es venu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 23, 'Fai donc ce que nous te difons; Nous avons quatre hommes qui ont fait un voeu:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 24, 'Pren-les, &amp; te purifie avec eux, &amp; contribuë avec eux, afin qu\'ils fe rafent la tefte: &amp; que tous fçachent qu\'il n\'eft rien des chofes dont ils ont efté informez de toi, mais que tu chemines auffi gardant la Loi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 25, 'Mais touchant ceux qui ont creu d\'entre les Gentils, nous en avons écrit, apres avoir ordonné qu\'ils n\'obfervent rien de femblable, finon qu\'ils fe gardent de ce qui eft facrifié aux idoles, &amp; du fang, &amp; des chofes eftouffées, &amp; de paillardife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 26, 'Alors Paul ayant pris ces hommes-là avec foi, &amp; le jour enfuivant s\'eftant purifié avec eux, entra au temple, denonçcant l\'accompliffement des jours de la purification, jufques à ce que l\'oblation fuft prefentée pour chacun d\'eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 27, 'Et comme les fept jours fe devoyent accomplir, quelques Juifs d\'Afie, l\'ayans veu au temple, efmeurent toute la multitude, &amp; jetterent les mains fur lui,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 28, 'Crians, Hommes Ifraëlites, aidez-nous: voici cet homme qui par tout enfeigne chacun contre le peuple, &amp; la Loi, &amp; ce lieu-ci: davantage il a auffi amené des Grecs dans le temple, &amp; a pollué ce faint lieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 29, 'Car auparavant ils avoyent veu Trophime Ephefien en la ville avec lui, lequel ils eftimoyent que Paul euft amené dans le temple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 30, 'Et toute la ville fut efmeuë, &amp; le peuple y accourut: &amp; ayans empoigné Paul, ils le tiroyent hors du temple: &amp; incontinent les portes furent fermées.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 31, 'Mais comme ils tafchoyent à le mettre à mort, le bruit vint au Capitaine de la bande de la garnifon, que toute Jerufalem eftoit en trouble.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 32, 'Lequel tout à l\'heure prit des foldats &amp; des Centeniers &amp; courut vers eux. Eux donc voyans le Capitaine &amp; les foldats, ils cefferent de battre Paul.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 33, 'Alors le Capitaine approcha, &amp; fe faifit de lui, commandant qu\'il fuft lié de deux chaines: puis il demanda qui il eftoit, &amp; ce qu\'il avoit fait.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 34, 'Et les uns crioyent d\'un, &amp; les autres d\'autre, en la foule: &amp; parce qu\'il ne pouvoit connoiftre rien de certain à caufe du bruit, il commanda qu\'il fuft mené en la fortereffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 35, 'Et quand il fut venu aux degrez, il avint qu\'il fut porté des foldats pour la violence de la foule.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 36, 'Car la multitude du peuple le fuivoit, criant, Ofte-le.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 37, 'Et comme Paul commençoit à eftre mené en la fortereffe, il dit au Capitaine, M\'eft-il permis de te dire quelque chofe? Et il demanda, Sçais-tu parler Grec?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 38, 'N\'es-tu pas l\'Egyptien qui ces jours paffez as efmeu une fedition, &amp; as retiré au defert quatre mil brigandeaux?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 39, 'Et Paul lui dit, Certes je fuis homme Juif, citoyen, natif de Tharfe, ville renommée de Cilicie: mais je te prie, permets moi de parler au peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 40, 'Et quand il lui eut permis, Paul fe tenant fur les degrez, fit figne de la main au peuple: puis un grand filence eftant fait, il parla à eux en langue Hebraïque, difant:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 1, 'Hommes freres &amp; peres, efcoutez-moi en la defenfe dont j\'ufe maintenant envers vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 2, 'Et quand ils ouïrent qu\'il parloit à eux en langue Hebraïque, ils firent tant plus de filence. Alors il dit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 3, 'Certes je fuis homme Juif, né à Tharfe de Cilicie, mais nourri en cette ville aux pieds de Gamaliël, ayant efté inftruit en la perfection de la Loi des Peres, eftant zelateur de Dieu, comme vous eftes tous aujourd\'hui:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 4, 'Qui ai prefecuté cette doctrine jufques à la mort, liant &amp; mettant és prifons tant hommes que femmes:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 5, 'Comme mefmes le fouverain Sacrificateur m\'eft tefmoin, &amp; toute l\'affemblée des Anciens: defquels auffi ayant receu des lettres addreffantes aux freres, j\'allois à Damas pour amener auffi liez à Jerufalem ceux qui eftoyent là, afin qu\'ils fuffent punis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 6, 'Or il avint comme je cheminois &amp; approchois de Damas, environ le midi, que fubitement une grande lumiere venant du ciel, refplendit comme un efclair à l\'entour de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 7, 'Et je tombai fur la place: &amp; ouïs une voix qui me dit, Saul, Saul, pourquoi me perfecutes-tu?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 8, 'Et je refpondis, Qui es-tu, Seigneur? Et il me dit, Je fuis Jefus le Nazarien, lequel tu perfecutes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 9, 'Or ceux qui eftoyent avec moi virent bien la lumiere, &amp; en furent tout effrayez: mais ils n\'ouïrent point la voix de celui qui parloit à moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 10, 'Alors je dis, Seigneur, que ferai-je? Et le Seigneur me dit, Leve-toi, &amp; t\'en va à Damas: &amp; là il te fera parlé touchant tout ce qui t\'eft ordonné de faire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 11, 'Et parce que je ne voyois goute, à caufe de la fplendeur de cette lumiere-là, je fus amené par la main par ceux de ma compagnie, &amp; vins à Damas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 12, 'Depuis, un certain Ananias, homme craignant Dieu felon la Loi, ayant tefmoignage de tous les Juifs qui demeuroyent là, vint à moi:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 13, 'Et efant prés de moi, me dit, Frere Saul, recouvre la veuë. Et à cette mefme heure je regardai vers lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 14, 'Et il dit, Le Dieu de nos Peres t\'a preordonné pour connoiftre fa volonté, &amp; voir le Jufte, &amp; ouïr la voix de fa bouche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 15, 'Car tu lui feras tefmoin envers tous hommes des chofes que tu as veuës &amp; ouïes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 16, 'Et maintenant que tardes-tu? leve-toi &amp; fois baptizé, &amp; lavé de tes péchez, en invoquant le Nom du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 17, 'Or apres il avint que quand je fus retourné à Jerufalem, &amp; que je priois au temple, je fus ravi en extafe:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 18, 'Et je le vis qui me difoit, Hafte-toi, &amp; pars legerement de Jerufalem: car ils ne recevront point le tefmoignage que tu leur rendras de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 19, 'Et je dis, Seigneur, eux-mefmes fçavent comme je mettois en prifon, &amp; foüettois par les Synagogues, ceux qui croioyent en toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 20, 'Et lors que le fang d\'Eftienne ton martyr fut efpandu, j\'y eftois auffi prefent, &amp; confentois à fa mort, &amp; gardois les veftemens de ceux qui le mettoyent à mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 21, 'Mais il me dit, Va-t\'en, car je t\'envoyerai loin vers les Gentils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 22, 'Or ils l\'efcouterent jufques à ce mot: alors ils éleverent leurs voix, difans, Ofte de la terre un tel homme, car il n\'eft point convenable qu\'il vive.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 23, 'Et comme ils crioyent à haute voix, &amp; fecoüoyent leurs veftemens, &amp; jettoyent la poudre en l\'air.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 24, 'Le Capitaine commanda qu\'il fuft mené en la fortereffe, &amp; ordonna qu\'il fuft examiné par le foüet, afin qu\'il fceuft pour quelle caufe ils crioyent ainfi contre lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 25, 'Quand donc ils l\'eurent garotté de couroyes, Paul dit au Centenier qui eftoit prés de lui, vous eft-il permis de foüetter un homme Romain, &amp; non condamné?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 26, 'Ce que le Centenier ayant entendu, il s\'en alla au Capitaine pour l\'advertir, difant, Regarde ce que tu as à faire: car cet homme-ci eft Romain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 27, 'Et le Capitaine vint à lui, &amp; lui dit, Di-moi, es-tu Romain? Et il refpondit, Oui vrayement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 28, 'Le Capitaine lui dit, J\'ai acquis cette bourgeoifie à grande fomme d\'argent: Et Paul dit, Et moi je le fuis de naiffance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 29, 'Incontinent donc ceux qui le devoyent examiner fe retirerent de lui: &amp; le Capitaine auffi craignit quand il eut connu qu\'il eftoit bourgeois de Rome, &amp; qu\'il l\'avoit lié.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 30, 'Or le lendemain voulant fçavoir pour certain pour quelle caufe il eftoit accufé des Juifs, il le délia des liens, &amp; commanda que les principaux Sacrificateurs s\'affemblaffent, &amp; tout le confeil, &amp; amena Paul, &amp; le prefenta devant eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 1, 'Et Paul ayant les yeux dreffez vers le confeil, dit, Hommes freres, j\'ai converfé en toute bonne confcience devant Dieu jufques à ce jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 2, 'Alors le fouverain Sacrificateur Ananias commanda à ceux qui eftoyent prés de lui de le fraper au vifage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 3, 'Alors Paul lui dit, Dieu te frapera, paroi blanchie: veu que tu es affis pour me juger felon la Loi, &amp; qu\'en tranfgreffant la Loi, tu commandes que je fois frapé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 4, 'Et ceux qui eftoyent prefens dirent, Injuries-tu le fouverain Sacrificateur de Dieu?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 5, 'Et Paul dit, Freres, je ne fçavois pas qu\'il fuft fouverain Sacrificateur: car il eft écrit, Tu ne mefdiras point du prince de ton peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 6, 'Et Paul fçachant qu\'une partie d\'entr\'eux eftoit de Sadduciens, &amp; l\'autre de Pharifiens, s\'efcria dans le confeil, Hommes freres, je fuis Pharifien, fils de Pharifien: je fuis tiré en caufe pour l\'efperance &amp; la refurrection des morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 7, 'Et quand il eut dit cela, il s\'efmeut une diffenfion entre les Pharifiens &amp; les Sadduciens: &amp; l\'affemblée fut divifée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 8, 'Car les Sadduciens difent qu\'il n\'y a point de refurrection, ni d\'Ange, ni d\'Efprit: mais les Pharifiens confeffent l\'un &amp; l\'autre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 9, 'Et il fe fit un grand cri. Alors les Scribes de la partie des Pharifiens fe leverent &amp; debattoyent, difans, Nous ne trouvons rien de mal en cet homme-ci: mais fi un Efprit ou un Ange a parlé à lui, ne bataillons point contre Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 10, 'Et comme il fut advenu une grande divifion, le Capitaine, craignant que Paul ne fuft mis en pieces par eux, commanda que les foldats defcendiffent, &amp; qu\'ils le raviffent du milieu d\'eux, &amp; l\'amenaffent en la fortereffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 11, 'Et la nuit fuivante, le Seigneur fe prefenta à lui, &amp; dit, Aye bon courage, Paul: car comme tu as rendu tefmoignage de moi à Jerufalem, ainfi t\'en faut-il auffi tefmoigner à Rome.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 12, 'Le lendemain eftant venu, quelques Juifs firent un complot &amp; un ferment avec execration, difans, Qu\'ils ne mangeroyent ni ne boiroyent jufques à ce qu\'ils euffent tué Paul.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 13, 'Et ils eftoyent plus de quarante qui avoyent fait cette conjuration avec execration de ferment.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 14, 'Lefquels s\'addrefferent aux principaux Sacrificateurs &amp; aux Anciens, &amp; leur dirent, Nous avons fait un voeu avec execration de ferment, que nous ne goufterions de rien jufques à ce que nous ayons tué Paul.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 15, 'Vous donc maintenant, faites fçavoir au Capitaine par l\'avis du Confeil qu\'il vous l\'amene demain, comme fi vous vouliez connoiftre de lui quelque chofe plus exactement: &amp; nous ferons tout prefts pour le tuër devant qu\'il approche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 16, 'Mais le fils de la foeur de Paul, ayant entendu ces embufches, vint &amp; entra en la fortereffe, &amp; le rapporta à Paul.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 17, 'Et Paul ayant appellé un des Centeniers, lui dit, Mene ce jeune homme vers le Capitaine: car il a quelque chofe à lui rapporter.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 18, 'Il le prit donc, &amp; le mena vers le Capitaine, &amp; dit, Paul qui eft prifonnier m\'a appellé, &amp; m\'a prié d\'amener ce jeune homme vers toi, dautant qu\'il a quelque chofe à te dire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 19, 'Et le Capitaine le prenant par la main, fe retira à part, &amp; lui demanda, Qu\'eft-ce que tu as à me rapporter?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 20, 'Et il lui dit, Les Juifs ont confpiré de te prier que demain tu envoyes Paul au Confeil, comme s\'ils vouloyent s\'enquerir de lui plus exactement de quelque chofe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 21, 'Mais ne t\'y accorde point: car plus de quarante hommes d\'entr\'eux font en embufches contre lui, qui ont fait un voeu avec execration de ferment, de ne manger ni boire jufques à ce qu\'ils l\'ayent mis à mort: &amp; maintenant ils font tout prefts, attendans ce que tu leur promettras.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 22, 'Le Capitaine donc renvoya le jeune homme, lui commandant qu\'il ne dift à perfonne qu\'il lui euft declaré ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 23, 'Puis ayant appellé deux certains Centeniers, il dit, Tenez prefts deux cens foldats, pour aller jufques à Cefarée, &amp; foixante &amp; dix hommes de cheval, &amp; deux cens Archers, à trois heures de nuict.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 24, 'Et qu\'il y ait des montures preftes afin qu\'ayans fait monter Paul, ils le menent en fauveté au Gouverneur Felix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 25, 'Et il lui efcrivit une lettre d\'une telle teneur:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 26, 'Claude Lyfias au tres-excellent Gouverneur Felix, Salut.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 27, 'Comme cet homme empoigné des Juifs eftoit preft d\'eftre tué par eux, je fuis furvenu avec la garnifon, &amp; leur ai ofté, ayant connu qu\'il eftoit Romain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 28, 'Et voulant fçavoir la caufe pourquoi ils l\'accufoyent, je le menai en leur confeil.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 29, 'Là où j\'ai trouvé qu\'il eftoit accufé touchant des queftions de leur Loi, n\'ayant commis nul crime digne de mort ou d\'emprifonnement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 30, 'Et ayant efté adverti des embufches que les Juifs avoyent dreffées contre lui, incontinent je te l\'ai envoyé: ayant auffi commandé aux accufateurs de dire devant toi les chofes qu\'ils ont contre lui. Bien te foit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 31, 'Les foldats donc felon qu\'il leur eftoit enjoint, prirent Paul, &amp; le menerent de nuit à Antipatris.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 32, 'Et le lendemain ayans laiffé les hommes de cheval pour aller avec lui, ils s\'en retournerent à la fortereffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 33, 'Eux donc eftant venus à Cefarée, apres avoir rendu les lettres au Gouverneur, prefenterent auffi Paul devant lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 34, 'Et quand le Gouverneur les eut leuës, &amp; l\'eut interrogé de quelle Province il eftoit, ayant entendu qu\'il eftoit de Cilicie,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 35, 'Je t\'orrai, dit-il, plus amplement, quand tes accufateurs feront auffi venus. Et il commanda qu\'il fuft gardé au palais d\'Herode.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 1, 'Or cinq jours apres, Ananias le fouverain Sacrificateur defcendit avec les Anciens, &amp; Tertulle un certain orateur, lefquels comparurent devant le Gouverneur contre Paul.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 2, 'Et Paul eftant appellé, Tertulle commença à l\'accufer, difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 3, 'Tres-excellent Felix, nous reconnoiffons en tout &amp; par tout avec tout remerciement, que nous avons obtenu une grande paix par toi, &amp; par les bonnes ordonnances que tu as dreffées en ce peuple, felon ta pourvoyance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 4, 'Mais afin que je ne t\'empefche point plus longuement, je te prie que tu nous oyes en peu de paroles felon ton equité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 5, 'C\'eft que nous avons trouvé cet homme peftilentieux, &amp; efmouvant fedition entre tous les Juifs par tout le monde, &amp; chef de la fecte des Nazariens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 6, 'Qui mefmes a attenté de profaner le temple; lequel auffi nous avons faifi, &amp; l\'avons voulu juger felon noftre Loi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 7, 'Mais le Capitaine Lyfias furvenant, nous l\'ofta d\'entre les mains avec une grande violence:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 8, 'Commandant que fes accufateurs vinffent vers toi: duquel toi-mefme apres l\'inquifition faite, pourras fçavoir toutes ces chofes, defquelles nous l\'accufons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 9, 'Alors les Juifs auffi s\'y accorderent, difans, que ces chofes alloyent ainfi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 10, 'Mais Paul, apres que le Gouverneur lui eut fait figne qu\'il parlaft, refpondit, Sçachant qu\'il y a déja plufieurs années que tu es juge de cette nation, je refpons pour moi-mefme de meilleur courage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 11, 'Veu que tu peux connoiftre qu\'il n\'y a pas plus de douze jours que je fuis monté pour adorer à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 12, 'Et ils ne m\'ont point trouvé au temple difputant avec aucun, ni faifant amas de peuple, ni és Synagogues, ni en la ville:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 13, 'Et ne peuvent maintenir les chofes dont ils m\'accufent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 14, 'Or je te confeffe bien ce poinct, que felon la voye qu\'ils appellent fecte, ainfi je fers le Dieu de mes Peres, croyant à toutes les chofes qui font efcrites en la Loi &amp; és Prophetes:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 15, 'Ayant efperance en Dieu, que la refurrection des morts, tant des juftes que des injuftes, laquelle auffi ceux-ci mefmes attendent, adviendra.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 16, 'Et partant auffi je mets peine d\'avoir toûjours la confcience fans offenfe envers Dieu &amp; envers les hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 17, 'Or apres plufieurs années, je fuis venu pour faire des aumofnes &amp; des oblations en ma nation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 18, 'Et comme je vaquois à cela, ils m\'ont trouvé purifié au temple, non point avec troupe ni tumulte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 19, 'Et ce furent de certains Juifs d\'Afie:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 20, 'Lefquels devoyent comparoiftre devant toi, &amp; m\'accufer, s\'ils avoyent quelque chofe contre moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 21, 'Ou que ceux-ci mefmes diënt, s\'ils ont trouvé en moi quelque chofe d\'inique, quand j\'ai efté prefenté au Confeil:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 22, 'Sinon quant à cette feule parole, de laquelle je me fuis écrié eftant entr\'eux, Aujourd\'hui je fuis tiré en caufe par vous, pour la refurrection des morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 23, 'Felix ayant ouï ces chofes, le remit à une autre fois, difant, Apres que j\'aurai plus exactement connu ce que c\'eft de cette fecte, quand le Capitaine Lyfias fera defcendu, je connoitrai entierement de vos affaires.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 24, 'Et il commanda à un Centenier que Paul fuft tenu en garde, &amp; qu\'il euft relafche, &amp; qu\'on n\'empefchaft perfonne des fiens de le fervir, ou de venir vers lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 25, 'Et quelques jours apres, Felix vint avec Drufille fa femme, qui eftoit Juifve: &amp; envoya querir Paul, &amp; l\'ouït parler de la foi qui eft en Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 26, 'Et comme il traittoit de la juftice, &amp; de la temperance, &amp; du jugement à venir, Felix tout effrayé, refpondit, Paul maintenant va t\'en: &amp; quand j\'aurai la commodité je te rappellerai:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 27, 'Efperant quand &amp; quand que quelque argent lui feroit baillé de Paul, pour le delivrer: pour laquelle caufe auffi fouvent il l\'envoyoit querir, &amp; devifoit avec lui. Deux ans eftans accomplis, Felix eut pour fucceffeur Portius Feftus, &amp; voulant gratifier aux Juifs, il laiffa Paul emprifonné.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 1, 'Feftus donc eftant entré en la Province, monta trois jours apres de Cefarée à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 2, 'Et le fouverain Sacrificateur, &amp; les premiers d\'entre les Juifs, comparurent devant lui contre Paul, &amp; le prioyent:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 3, 'Demandans de la faveur contre lui, afin qu\'il le fift venir à Jerufalem, dreffans des embufches pour le tuër par le chemin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 4, 'A quoi Feftus refpondit, que Paul eftoit bien gardé à Cefarée, &amp; qu\'en bref il s\'y en devoit aller.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 5, 'Que ceux donc (dit-il) d\'entre vous qui le peuvent faire, defcendent enfemble: &amp; s\'il y a quelque crime en cet homme, qu\'ils l\'accufent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 6, 'Et n\'ayant pas demeuré entr\'eux plus de dix jours, il defcendit à Cefarée: &amp; le lendemain s\'affit au fiege judicial, &amp; commanda que Paul fuft amené.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 7, 'Lequel eftant venu là, les Juifs qui eftoyent defcendus de Jerufalem l\'environnerent, lui mettant à fus plufieurs crimes griefs, lefquels ils ne pouvoyent prouver:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 8, 'Paul refpondant qu\'il n\'avoit en rien failli, ni contre la Loi des Juifs, ni contre le temple, ni contre Cefar.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 9, 'Mais Feftus voulant gratifier aux Juifs, refpondit à Paul, &amp; dit, Veux-tu monter à Jerufalem &amp; eftre là jugé de ces chofes devant moi?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 10, 'Et Paul dit, J\'affifte au fiege judicial de Cefar, où il me faut eftre jugé: je n\'ai fait aucun tort aux Juifs, comme auffi tu le reconnois tres-bien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 11, 'Que fi j\'ai forfait, ou commis quelque chofe digne de mort, je ne refufe point de mourir: mais s\'il n\'eft rien de ce dont ils m\'accufent, nul ne me peut donner à eux: j\'en appelle à Cefar.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 12, 'Alors quand Feftus eut conféré avec le confeil, il refpondit, En as-tu appellé à Cefar? tu iras à Cefar.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 13, 'Or quelques jours apres, le Roi Agrippa &amp; Bernice arriverent à Cefarée, pour falüer Feftus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 14, 'Et apres avoir demeuré là plufieurs jours, Feftus fit mention au Roi de l\'affaire de Paul, difant, Un certain perfonnage a efté laiffé prifonnier par Felix:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 15, 'A caufe duquel, comme j\'eftois à Jerufalem, les principaux Sacrificateurs &amp; les Anciens des Juifs font comparus, requerans condamnation contre lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 16, 'Aufquels j\'ai refpondu que ce n\'eft point l\'ufage des Romains, de livrer quelqu\'un à la mort, devant que celui qui eft accufé ait fes accufateurs prefens, &amp; qu\'il ait lieu de fe defendre du crime.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 17, 'Quand donc ils furent venus ici, fans que j\'ufaffe d\'aucun delai, le jour enfuivant, feant au fiege judicial, je commandai que le perfonnage fuft amené:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 18, 'Duquel les accufateurs eftant là prefens, n\'amenerent aucun crime touchant ce dont je me doutois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 19, 'Mais ils avoyent quelques queftions contre lui, touchant leur fuperftition, &amp; touchant un certain Jefus mort, lequel Paul affirmoit eftre vivant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 20, 'Or moi eftant en perplexité, comme je m\'enquerois de cela, je demandai s\'il vouloit aller à Jerufalem, &amp; eftre là jugé de ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 21, 'Mais parce qu\'il en appella, demandant d\'eftre refervé à la connoiffance d\'Augufte, je commandai qu\'il fuft gardé jufques à ce que je l\'envoyaffe à Cefar.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 22, 'Alors Agrippa dit à Feftus, Je voudrois bien ouïr auffi le perfonnage. Demain, dit-il, tu l\'orras.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 23, 'Le lendemain donc quand Agrippa &amp; Bernice furent venus avec une grande pompe, &amp; furent entrez en l\'auditoire avec les Capitaines, &amp; les gens d\'autorité de la ville, Paul par le commandement de Feftus fut amené.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 24, 'Et Feftus dit, Roi Agrippa, &amp; vous tous qui eftes ici avec nous, voyez cet homme, duquel toute la multitude des Juifs m\'eft venuë interpeller, tant à Jerufalem qu\'ici, criant qu\'il ne le faloit plus laiffer vivre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 25, 'Mais moi ayant trouvé qu\'il n\'avoit rien fait qui fuft digne de mort: &amp; lui-mefme en ayant appellé à Augufte, j\'ai arrefté de le lui envoyer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 26, 'Dequoi je n\'ai rien que j\'efcrive de certain au Seigneur. C\'eft pourquoi je vous l\'ai prefenté, &amp; principalement à toi, Roi Agrippa: afin qu\'apres l\'inquifition faite, j\'aye dequoi efcrire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 27, 'Car il me femble qu\'il n\'y a point de raifon d\'envoyer un prifonnier, fans fignifier le cas qu\'on lui met à fus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 1, 'Alors Agrippa dit à Paul, Il t\'eft permis de parler pour toi. Paul donc ayant eftendu la main, commença à rendre raifon de fon fait, difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 2, 'Roi Agrippa, je m\'eftime heureux que je dois refpondre aujourd\'hui devant toi, de toutes les chofes dont je fuis accufé par les Juifs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 3, 'Et fur tout dautant que je fçais que tu as tres-bonne connoiffance de toutes les couftumes &amp; queftions qui font entre les Juifs. C\'eft pourquoi je te prie que tu m\'oyes patiemment.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 4, 'Ainfi donc, quant à la vie que j\'ai menée dés ma jeuneffe, telle qu\'elle a efté du commencement parmi ma nation à Jerufalem, tous les Juifs la fçavent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 5, 'Ayans par ci-devant connu (s\'ils en veulent rendre tefmoignage) que dés mes anceftres j\'ai vefcu Pharifien, felon la fecte la plus exquife de noftre religion.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 6, 'Et maintenant j\'affifte eftant tiré en caufe, pour l\'efperance de la promeffe que Dieu a faite à nos Peres:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 7, 'A laquelle nos douze lignées fervant Dieu continuellement nuict &amp; jour, efperent de parvenir: &amp; de laquelle efperance (ô roi Agrippa) je fuis accufé par les Juifs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 8, 'Quoi? tenez-vous pour une chofe incroyable que Dieu reffufcite les morts?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 9, 'Certainement quant à moi, il m\'a bien femblé qu\'il faloit que je fiffe de grands efforts contre le Nom de Jefus le Nazarien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 10, 'Ce que j\'ai fait auffi à Jerufalem: &amp; ai conftitué prifonniers plufieurs des Saints, ayant receu pouvoir de le faire, des principaux Sacrificateurs: &amp; quand on les mettoit à mort, j\'en baillois ma voix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 11, 'Et fouvent par toutes les Synagogues en les puniffant, je les contraignois à blafphemer; &amp; eftant forcené contr\'eux tout outre, je les perfecutois jufques aux villes eftrangeres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 12, 'En vaquant à cela, comme j\'allois auffi à Damas avec pouvoir &amp; commiffion de par les principaux Sacrificateurs,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 13, 'Je vis (ô Roi) par le chemin, en plein midi une lumiere du ciel, plus grande que la fplendeur du Soleil, qui refplendit à l\'entour de moi, &amp; de ceux qui cheminoyent avec moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 14, 'Dont nous tous eftans tombez par terre, j\'ouïs une voix parlant à moi, &amp; difant en langue Hebraïque, Saul, Saul, pourquoi me perfecutes-tu? il t\'eft dur de regimber contre les aiguillons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 15, 'Alors je dis, Qui es-tu, Seigneur? Et il refpondit, Je fuis Jefus lequel tu perfecutes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 16, 'Mais leve-toi, &amp; te tiens fur tes pieds: car pour cette caufe je te fuis apparu, pour te conftituer miniftre &amp; tefmoin, tant des chofes que tu as veuës, que de celles efquelles je t\'apparoiftrai:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 17, 'Te delivrant du peuple, &amp; des Gentils, vers lefquels maintenant je t\'envoye,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 18, 'Pour ouvrir leurs yeux, afin qu\'ils foyent convertis des tenebres à la lumiere, &amp; de la puiffance de Satan à Dieu, &amp; qu\'ils reçoivent la remiffion de leurs pechez, &amp; part entre ceux qui font fanctifiez, par la foi qui eft envers moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 19, 'Partant (ô roi Agrippa) je n\'ai point efté rebelle à la vifion celefte:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 20, 'Mais premierement j\'ai annoncé à ceux qui eftoyent à Damas, &amp; puis à Jerufalem, &amp; par toute la contrée de Judée, &amp; aux Gentils, qu\'ils fe repentiffent, &amp; fe convertiffent à Dieu, en faifant des oeuvres convenables à repentance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 21, 'Pour cette caufe les Juifs m\'ayans pris dans le temple, ont tafché de me tuër.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 22, 'Mais ayant receu du fecours par l\'aide de Dieu, je fuis vivant jufques à ce jourd\'hui, teftifiant à petit &amp; à grand, &amp; ne difant rien finon les chofes que tant les Prophetes que Moyfe, ont predites devoir avenir:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 23, 'Affavoir qu\'il faloit que le Chrift fouffrift, &amp; qu\'il fuft le premier de la refurrection des morts qui deuft annoncer la lumiere au peuple &amp; aux Gentils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 24, 'Et comme il ufoit de cette defenfe, Feftus dit à haute voix, Tu es hors du fens, Paul: le grand fçavoir és lettres te met hors du fens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 25, 'Et Paul dit, Je ne fuis point hors du fens, tres-excellent Feftus: mais je prefere des paroles de verité &amp; de fens raffis:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 26, 'Car le Roi fçait ce que c\'eft de ces chofes, auquel auffi je parle franchement, parce que j\'eftime qu\'il n\'ignore rien de ces chofes: car cela auffi n\'a point efté fait en quelque coin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 27, 'O Roi Agrippa! crois-tu aux Prophetes? je fçais que tu y crois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 28, 'Et Agrippa refpondit à Paul, Tu me perfuades à peu-prés d\'eftre Chreftien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 29, 'Alors Paul dit, Je fouhaitterois envers Dieu, que non feulement toi, mais auffi tous ceux qui m\'oyent aujourd\'hui, &amp; à peu-prés &amp; bien avant, fuffent faits tels que je fuis, hormis ces liens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 30, 'Et Paul ayant dit ces chofes, le Roi fe leva, &amp; le Gouverneur, &amp; Bernice, &amp; ceux qui eftoyent affis avec eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 31, 'Et quand ils furent retirez à part, ils confererent entr\'eux, difans, Cet homme ne commet rien digne de mort ou de prifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 32, 'Et Agrippa dit à Feftus, Cet homme pouvoit eftre relafché, s\'il n\'euft appellé à Cefar.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 1, 'Or depuis qu\'il fut arrefté que nous navigerions en Italie, ils baillerent Paul avec de certains autres prifonniers à un Centenier nommé Jule, de la bande appellée Augufte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 2, 'Et eftans montez en un navire d\'Adramite, nous partifmes pour tirer vers les quartiers d\'Afie, Ariftarque de Macedone Theffalonicien, eftant avec nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 3, 'Et le jour fuivant nous arrivafmes à Sidon; &amp; Jule traittant humainement Paul, permit qu\'il allaft vers fes amis, &amp; qu\'ils euffent foin de lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 4, 'Puis eftans partis de là nous tinfmes la route d\'au deffous de Cypre, parce que les vents eftoyent contraires.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 5, 'Et apres avoir paffé la mer qui eft à l\'endroit de Cilicie &amp; de Pamphylie, nous vinfmes à Myra ville de Lycie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 6, 'Là où le Centenier trouva un navire d\'Alexandrie, tirant en Italie, auquel il nous fit monter.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 7, 'Et comme par plufieurs jours nous navigions pefamment, tellement qu\'à grand\'peine eftions-nous parvenus à l\'endroit de Gnide, parce que le vent ne nous pouffoit point, nous paffafmes au deffous de Crete à l\'endroit de Salmone:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 8, 'Laquelle coftoyans avec grande peine, nous vinfmes en un lieu qui eft appellé Beaux-ports, prés duquel eftoit la ville de Lafée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 9, 'Et dautant que beaucoup de temps s\'eftoit paffé, &amp; que la navigation eftoit déja perilleufe, parce que déja mémes le jeufne eftoit paffé, Paul les admonefta;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 10, 'Leur difant, Hommes, je vois que la navigation fera avec peril &amp; grand dommage non feulement de la charge du navire, mais auffi de nos vies.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 11, 'Mais le Centenier croyoit plus au gouverneur &amp; au patron de navire, qu\'à ce que difoit Paul.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 12, 'Et dautant que le port n\'eftoit point en bonne affiette pour hyverner, la plufpart furent d\'avis de partir de là pour voir fi on pourroit aborder à Phenix, pour y paffer l\'hyver: qui eft un port de Crete, regardant vers le vent de Libs &amp; de Corus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 13, 'Alors le vent de Midi commençant à fouffler doucement, penfant eftre au deffus de leur intention, eftans partis, ils coftoyerent Crete de plus prés.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 14, 'Mais un peu apres, un vent tempeftueux, qu\'on appelle Euroclydon, fe leva du cfté d\'elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 15, 'Et le navire eftant emporté du vent, tellement qu\'il ne pouvoit parer au vent, nous fufmes emportez, ayans abandonné le navire au vent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 16, 'Et ayans paffé au deffous d\'une petite Ifle appellée Clauda, à grand\'peine peufmes-nous eftre maiftres de l\'efquif:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 17, 'Lequel ayans attiré, les nautonniers cherchoyent tous remedes: ceignans le navire par deffous: &amp; craignans de tomber en Syrte, apres avoir abbatu la fartie, ils eftoyent ainfi portez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 18, 'Or parce que nous eftions agitez de grande tempefte, le jour fuivant ils firent le ject.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 19, 'Puis le troifiéme jour nous jettafmes de nos propres mains l\'equipage du navire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 20, 'Et ne nous apparoiffans par plufieurs jours ni Soleil ni eftoiles, &amp; une grande tempefte nous preffant de prés, toute efperance de nous pouvoir fauver à l\'avenir fut oftée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 21, 'Mais aprés qu\'ils eurent efté long-temps fans manger, alors Paul fe tenant au milieu d\'eux, dit, O hommes! certes il faloit me croire, &amp; ne partir point de Crete, &amp; gagner ce dommage &amp; cette perte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 22, 'Mais maintenant je vous exhorte que vous preniez bon courage: car il n\'y aura perte aucune entre vous quant à la vie: mais feulement du navire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 23, 'Car cette propre nuit s\'eft prefenté à moi l\'Ange du Dieu auquel je fuis, &amp; auquel je fers:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 24, 'Difant, Paul, ne crain point, il faut que tu fois prefenté à Cefar: &amp; voici, Dieu t\'a donné tous ceux qui navigent avec toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 25, 'C\'eft pourquoi, ô hommes! ayez bon courage: car je crois à Dieu, qu\'il fera ainfi qu\'il m\'a efté dit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 26, 'Mais il faut que nous foyons jettez en quelque Ifle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 27, 'Quand donc la quatorzième nuit fut venuë, comme nous eftions portez çà &amp; là en la mer Adriatique, environ la minuit, les mariniers eurent opinion que quelque contrée leur approchoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 28, 'Et jettans la fonde en bas, ils trouverent vingt braffes: puis eftans paffez un peu plus outre, &amp; ayans derechef fondé, ils trouverent quinze braffes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 29, 'Et craignans qu\'ils ne tombaffent en quelque efcueil, ils jetterent quatre ancres de la pouppe du navire, defirans que le jour vinft.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 30, 'Et comme les mariniers cherchoyent à s\'enfuïr du navire, ayans devalé l\'efquif en la mer, comme s\'ils euffent voulu lafcher les ancres du cofté de la prouë.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 31, 'Paul dit au Centenier &amp; aux foldats, Si ceux-ci ne demeurent dans le navire, vous ne pouvez vous fauver.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 32, 'Alors les foldats couperent les cordes de l\'efquif, &amp; le laifferent choir en bas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 33, 'Et ainfi que le jour approchoit, Paul les exhorta tous de prendre quelque nourriture, difant, C\'eft aujourd\'hui le quatorziéme jour, qu\'en attendant vous eftes demeurez à jeun, &amp; n\'avez rien pris.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 34, 'Je vous exhorte donc que vous preniez quelque nourriture, veu qu\'il eft expedient pour voftre confervation: car il ne cherra de nul de vous un cheveu de la tefte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 35, 'Et quand il eut dit ces chofes, &amp; pris du pain, il rendit graces à Dieu devant tous: &amp; l\'ayant rompu il commença à manger.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 36, 'Alors tous ayans pris courage, fe prirent auffi à manger.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 37, 'Or nous eftions au navire en tout deux cens foixante &amp; feize ames.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 38, 'Et quand ils furent raffafiez de viandes, ils allegerent le navire, jettant le blé en la mer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 39, 'Or le jour eftant venu, ils ne reconnoiffoyent point le païs: mais ils apperceurent un golfe ayant un rivage, auquel ils deliberoyent de jetter le navire, s\'ils euffent peu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 40, 'C\'eft pourquoi ayant retiré les ancres, ils l\'abandonnerent à la mer, lafchans quand &amp; quand les attaches des gouvernaux: &amp; l\'artimon eftant levé au vent, ils tirerent vers le rivage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 41, 'Mais eftant tombés en un lieu où deux courans fe rencontroyent, ils y heurterent le navire, &amp; la prouë eftant fichée demeuroit ferme, mais la pouppe fe rompoit par la violence des vagues.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 42, 'Alors le confeil des foldats fut de tuër les prifonniers, de peur que quelqu\'un s\'eftant fauvé à nage, ne s\'enfuift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 43, 'Mais le Centenier voulant fauver Paul, les empefcha d\'executer ce confeil, &amp; commanda que ceux qui pourroyent nager fe jettaffent hors les premiers, &amp; fe fauvaffent en terre:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 44, 'Et le refte, les uns fur des ais, &amp; les autres fur quelques pieces du navire. Et ainfi il avint que tous fe fauverent en terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 1, 'Eftans donc venus à fauveté, ils reconnurent alors que l\'Ifle eftoit appellée Malte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 2, 'Et les Barbares uferent d\'une finguliere humanité envers nous: car ils allumerent un grand feu, &amp; nous recueillirent tous, pour la pluye qui nous preffoit, &amp; pour le froid.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 3, 'Alors Paul ayant ramaffé quelque quantité de farmens, comme il les eut mis au feu, une vipere fortit hors à caufe de la chaleur, &amp; lui faifit la main.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 4, 'Et quand les Barbares virent la befte pendante à fa main, ils dirent l\'un à l\'autre, Certainement cet homme-ci eft meurtrier: lequel apres eftre efchappé de la mer, la vengeance ne permet point qu\'il vive.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 5, 'Mais lui ayant fecoüé la befte dans le feu, n\'en eut aucun mal:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 6, 'Au lieu qu\'ils s\'attendoyent qu\'il deuft s\'enfler, ou fubitement choir tout mort: mais quand ils eurent long-temps attendu, &amp; qu\'ils eurent veu que nul inconvenient ne lui en advenoit, ils changerent de langage, difans, Qu\'il eftoit Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 7, 'Or en cet endroit-là eftoyent les poffeffions du principal de l\'Ifle nommé Publius, lequel nous recueillit, &amp; durant trois jours nous logea fort gracieufement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 8, 'Et il avint que le pere de Publius gifoit detenu de fievre &amp; de dyfentrie, vers lequel Paul alla: &amp; quand il eut prié, &amp; lui eut impofé les mains, il le guerit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 9, 'Cela donc eftant avenu, tous les autres auffi de l\'Ifle qui eftoyent malades, vinrent vers lui, &amp; furent gueris.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 10, 'Lefquels auffi nous firent de grands honneurs, &amp; au depart nous fournirent ce qui nous eftoit neceffaire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 11, 'Or trois mois apres nous partifmes en un navire d\'Alexandrie qui avoit hyverné en l\'Ifle, &amp; avoit pour enfeigne Caftor &amp; Pollux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 12, 'Et eftans arrivez à Syracufe, nous demeurafmes là trois jours.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 13, 'De là ayans tournoyé, nous arrivafmes à Rhege. Et un jour apres, le vent de Midi eftant furvenu, nous vinfmes le deuxiéme jour à Puzol.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 14, 'Auquel lieu ayant trouvé des freres, nous fufmes priez de demeurer avec eux fept jours. Et ainfi nous vinfmes à Rome.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 15, 'Et quand les freres de là eurent ouï de nos nouvelles, ils vinrent au devant de nous jufques au Marché d\'Appius, &amp; aux Trois boutiques: lefquels Paul voyant, rendit graces à Dieu, &amp; prit courage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 16, 'Quand donc nous fufmes venus à Rome, le Centenier livra les prifonniers au Capitaine general: mais quant à Paul, il lui fut permis de demeurer à part avec un foldat qui le gardoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 17, 'Or il avint trois jours apres, que Paul convoqua les principaux des Juifs: &amp; quand ils furent venus, il leur dit, Hommes freres: bien que je n\'aye rien commis contre le peuple, ni contre les couftumes des Peres, toutefois eftant emprifonné dés Jerufalem, j\'ai efté livré és mains des Romains:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 18, 'Lefquels apres m\'avoir examiné me vouloyent relafcher, parce qu\'il n\'y avoit en moi aucun crime digne de mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 19, 'Mais les Juifs s\'y oppofans, j\'ai efté contraint d\'en appeller à Cefar, non point que j\'aye dequoi accufer ma nation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 20, 'Pour cette caufe donc je vous ai appellez, pour vous voir &amp; parler à vous: car c\'eft pour l\'efperance d\'Ifraël que je fuis environné de cette chaifne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 21, 'Mais ils lui refpondirent, Nous n\'avons point receu de lettres de Judée touchant toi, ni perfonne des freres n\'eft venu qui ait rapporté ou dit quelque mal de toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 22, 'Neantmoins nous entendrons bien volontiers de toi quel eft ton fentiment: car quant à cette fecte, il nous eft notoire qu\'on lui contredit par tout.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 23, 'Et quand ils eurent affigné un jour, plufieurs vinrent à lui au logis: aufquels il expofoit par tefmoignages le royaume de Dieu, &amp; les induifoit à croire ce qui eft de Jefus, tant par la Loi de Moyfe que par les Prophetes, depuis le matin jufques au foir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 24, 'Et les uns furent perfuadez par les chofes qu\'il difoit: mais les autres n\'y croioyent point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 25, 'C\'eft pourquoi eftans en difcord entr\'eux, ils fe departirent, apres que Paul leur eut dit un mot, affavoir, Le Saint Efprit a bien parlé à nos Peres par Efaïe le Prophete,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 26, 'Difant, Va vers ce peuple, &amp; di, Vous orrez de l\'oreille, &amp; n\'entendrez point: &amp; en regardant vous verrez, &amp; n\'appercevrez point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 27, 'Car le coeur de ce peuple eft engraiffé: &amp; ils ont ouï dur des oreilles, &amp; ont cligné de leurs yeux, afin qu\'ils ne voyent des yeux, &amp; qu\'ils n\'oyent des oreilles, &amp; qu\'ils n\'entendent du coeur, &amp; qu\'ils ne fe convertiffent, &amp; que je ne les gueriffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 28, 'Qu\'il vous foit donc notoire, que ce falut de Dieu eft envoyé aux Gentils, &amp; ils l\'orront.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 29, 'Et quand il eut dit ces chofes, les Juifs partirent d\'avec lui, ayans une grande difpute entr\'eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 30, 'Mais Paul demeura deux ans entiers en fon propre loüage, &amp; recueilloit tous ceux qui venoyent vers lui:');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 31, 'Prefchant le royaume de Dieu, &amp; enfeignant les chofes qui font du Seigneur Jefus Chrift, avec toute hardieffe de parler, fans aucun empefchement. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 1, 'PAUL ferviteur de Jefus Chrift, appellé à eftre Apoftre, mis à part, pour annoncer l\'Evangile de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 2, '(Lequel il avoit auparavant promis par fes Prophetes és faintes Ecritures.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 3, 'Touchant fon Fils (qui a efté fait de la femence de David felon la chair :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 4, 'Et a efté pleinement declaré Fils de Dieu en puiffance, felon l\'Efprit de fanctification, par la refurrection des morts) c\'eft affavoir noftre Seigneur Jefus Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 5, '(Par lequel nous avons receu grace &amp; charge d\'Apoftre, afin qu\'il y ait obeïffance de foi, entre tous les Gentils en fon Nom.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 6, 'Entre lefquels auffi vous eftes, vous qui eftes appellez de Jefus Chrift.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 7, 'A vous tous qui eftes à Rome, bien-aimez de Dieu, appellez à eftre faints : Grace vous foit &amp; paix de par Dieu noftre Pere, &amp; de par le Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 8, 'Premierement je rens graces touchant vous tous à mon Dieu par Jefus Chrift, de ce que voftre foi eft renommée par tout le monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 9, 'Car Dieu ( auquel je fers en mon efprit en l\'Evangile de fon Fils) m\'eft tefmoin comme fans ceffe je fais mention de vous :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 10, 'Requerant toûjours en mes oraifons que je vienne vers vous, fi enfin en quelque maniere que ce foit je puis obtenir adreffe pour parvenir à vous, par la volonté de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 11, 'Car je difire grandement de vous voir, pour vous départir quelque don fpirituel, afin que vous foyez confirmez :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 12, 'C\'eft à dire, afin d\'eftre confolé avec vous, par la foi mutuelle de vous &amp; de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 13, 'Or mes freres je ne veux point que vous ignorez que j\'ai fouvent propofe de venir vers vous (mais j\'en ai efté empefché jufques à prefent) afin que je recueilliffe quelque fruit entre vous auffi, comme entre les autres nations.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 14, 'Je fuis debiteur tant aux Grecs, qu\'aux Barbares, tant aux fages qu\'aux ignorans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 15, 'Ainfi, entant qu\'en moi eft, je fuis preft d\'evangelizer à vous auffi qui eftes à Rome.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 16, 'Car je ne prens point à honte l\'Evangile de Chrift, veu que c\'eft la puiffance de Dieu en falut à tout croyant, au Juif premierement, puis auffi au Grec.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 17, 'Car en lui fe revele tout à plein la juftice de Dieu de foi en foi : felon qu\'il eft écrit, Or le jufte vivra de foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 18, 'Car l\'ire de Dieu fe revele tout à plein du ciel fur toute impieté &amp; injuftice des hommes, d\'autant qu\'ils detiennent la verité en injuftice.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 19, 'Parce que ce qui fe peut connoiftre de Dieu eft manifefté en eux : car Dieu le leur a manifefté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 20, 'Car les chofes invifibles d\'icelui (affavoir tant fa puiffance eternelle que fa divinité) fe voyent comme à l\'œil depuis la creation du monde, eftant confiderées en ces ouvrages, afin qu\'ils foyent rendus inexcufables.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 21, 'Parce qu\'ayans connu Dieu, ils ne l\'on point glorifié comme Dieu, &amp; ne lui ont point rendu graces : mais ils font devenus vains en leurs difcours, &amp; leur cœur deftitué d\'intelligence, a efté rempli de tenebres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 22, 'Se difans eftre fages, ils font devenus fols :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 23, 'Et ont changé la gloire de Dieu incorruptible, en la reffemblance &amp; image de l\'homme corruptible, &amp; des oifeaux, &amp; des beftes à quatre pieds, &amp; des reptiles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 24, 'C\'eft pourquoi auffi Dieu les a livrez aux convoitifes de leurs propres cœurs, à ordure, pour deshonorer entr\'eux leurs propres corps :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 25, 'Lefquels ont changé la verité de Dieu en fauffeté, &amp; ont adoré, &amp; ont fervi la creature, en delaiffant le Createur, qui eft benit eternellement. Amen.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 26, 'C\'eft pourquoi Dieu les a livrez à leurs affections infames : car mefmes les femmes d\'entr\'eux ont changé l\'ufage naturel en celui qui eft contre nature.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 27, 'Et femblablement auffi les mafles, laiffans le naturel ufage de la femme, fe font embrafez en leur convoitife l\'un envers l\'autre, commettans mafle avec mafle des chofes infames ; &amp; recevans en eux-mefmes la recompenfe de leur erreur, telle qu\'il appartenoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 28, 'Car comme ils n\'ont tenu conte de reconnoiftre Dieu, auffi Dieu les a livrez à un efprit dépourveu de tout jugement, pour commettre des chofes qui ne font nullement convenables :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 29, 'Eftans remplis de toute injuftice, paillardife, mefchanceté, avarice, mauvaiftié : pleins d\'envie, de meurtre, de querelle, de fraude, de malignité :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 30, 'Rapporteurs, detracteurs, haïffans Dieu, injurieux, orgueilleux, vanteurs, inventeurs de maux, rebelles à peres &amp; à meres :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 31, 'Sans entendement, ne tenans point ce qu\'ils ont accordé, fans affection naturelle, gens qui jamais ne fe rappaifent, fans mifericorde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 32, 'Lefquels bien qu\'ils ayent connu le droict de Dieu, affavoir que ceux qui commettent de telles chofes, font dignes de mort : ne les commettent pas feulement, mais auffi favorifent à ceux qui les commettent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 1, 'Partant, ô homme ! Quiconque tu fois qui juges des autres, tu es fans excufe : car en ce que tu juges autrui, tu te condamnes toi-mefme, veu que toi qui juges, commets les mefmes chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 2, 'Or nous fçavons que le jugement de Dieu eft felon verité fur ceux qui commettent de telles chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 3, 'Et penfes-tu, ô toi homme, qui juges de ceux qui commettent de telles chofes &amp; qui les commets, que tu doives efchapper le jugement de Dieu ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 4, 'Ou mefprifes-tu les richeffes de fa benignité, &amp; de fa patience, &amp; de fa longue attente : ne connoiffant point que la benignité de Dieu te convie à repentance ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 5, 'Mais par ta dureté, &amp; ton cœur, qui eft fans repentance, tu t\'amaffes ire au jour de l\'ire &amp; de la declaration du jufte jugement de Dieu :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 6, 'Qui rendra à chacun felon fes œuvres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 7, 'Affavoir à ceux qui avec patience à bien faire cherchent gloire, honneur, &amp; immortalité, la vie eternelle :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 8, 'Mais à ceux qui font contentieux, &amp; qui fe rebellent contre la verité, &amp; obeïffent à l\'injuftice, indignation &amp; ire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 9, 'Il y aura tribulation &amp; angoiffe fur toute ame d\'homme faifant mal, du Juif premierement, puis auffi du Grec.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 10, 'Mais gloire, honneur, &amp; paix à chacun qui fait bien : au Juif premierement, puis auffi au Grec.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 11, 'Car envers Dieu il n\'y a point d\'égard à l\'apparence des perfonnes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 12, 'Car tous ceux qui auront peché fans la Loi, periront auffi fans la Loi : &amp; tous ceux qui auront peché en la Loi, feront jugez par la Loi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 13, '(Car ce ne font point ceux qui oyent la Loi, qui font juftes devant Dieu : mais ceux qui mettent en effet la Loi, feront juftifiez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 14, 'Car veu que les gentils, qui n\'ont point la Loi, font naturellement les chofes qui font de la Loi, n\'ayans point la Loi, ils font Loi à eux-mefmes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 15, 'Lefquels montrent l\'œuvre de la Loi, écrite en leurs cœurs, leur confcience rendant pareillement témoignage, &amp; leurs penfées entr\'elles s\'accufant, ou auffi s\'excufant.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 16, 'Au jour que Dieu jugera les fecrets des hommes par Jefus-Chrift, felon mon Evangile.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 17, 'Voici, tu es furnommé Juif, &amp; tu te repofes du tout en la Loi, &amp; te glorifies en Dieu :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 18, 'Et tu connois fa volonté, &amp; fçais difcerner ce qui eft contraire, eftant inftruit par la Loi :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 19, 'Et tu penfes eftre le conducteur des aveugles, la lumiere de ceux qui font en tenebres :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 20, 'L\'inftructeur des ignorans, l\'enfeigneur des idiots, ayant le patron de la connoiffance &amp; de la verité en la Loi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 21, 'Toi donc qui enfeignes autrui, ne t\'enfeignes-tu point toi-mefme ? Toi qui prefches qu\'on ne doit point dérobber, dérobbes-tu ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 22, 'Toi qui dis que l\'on ne doit point commettre adultere, commets-tu adultere ? Toi qui as en abomination les idoles, commets-tu facrilege ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 23, 'Toi qui te glorifies en la Loi, deshonnores-tu Dieu par la tranfgreffion de la Loi ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 24, 'Car le nom de Dieu eft blafphemé à caufe de vous entre les Gentils : comme il eft efcrit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 25, 'Car il eft vrai, que la circoncifion eft profitable, fi tu gardes la Loi : mais fi tu es tranfgreffeur de la Loi, ta circoncifion devient prepuce.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 26, 'Si donc le prepuce garde les ordonnances de la Loi, fon prepuce ne lui fera-t\'il point reputé pour circoncifion ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 27, 'Et fi le prepuce qui eft de nature, accomplit la Loi, ne te jugera-t\'il point, toi qui par la lettre &amp; circoncifion es tranfgreffeur de la Loi ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 28, 'Car celui-là n\'eft point Juif qui l\'eft par dehors, &amp; la circoncifion n\'eft point celle qui eft faite par dehors en la chair.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 29, 'Mais celui-là eft Juif, qui l\'eft au-dedans, &amp; la circoncifion eft celle qui eft du cœur en efprit, non point en la lettre : duquel Iuif la loüange n\'eft point des hommes, mais de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 1, 'Quel eft donc l\'avantage du Juif ? Ou quel eft le profit de la circoncifion ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 2, 'Grand en toute maniere : fur tout en ce que les oracles de Dieu leur ont efté commis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 3, 'Car qu\'en eft-il fi quelques-uns n\'ont point creu ? Leur incredulité aneantira-t\'elle la foi de Dieu ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 4, 'Ainfi n\'advienne : mais Dieu foit veritable, &amp; tout homme menteur : felon qu\'il eft écrit, Afin que tu fois trouvé jufte en tes paroles, &amp; que tu ayes gain de caufe quand tu es jugé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 5, 'Que fi noftre injuftice recommande la juftice de Dieu, que dirons-nous ? Dieu eft-il injufte quand il punit ? (je parle en homme.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 6, 'Ainfi n\'advienne : autrement, comment Dieu jugera-t\'il le monde ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 7, 'Car fi la verité de Dieu eft plus abondante par ma menterie à fa gloire, pourquoi fuis-je encore condamné pour pecheur ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 8, 'Mais pluftoft (felon que nous fommes blafmez, &amp; felon que difent quelques-uns que nous difons) que ne faifons nous des maux afin qu\'il en arrive du bien ? (defquels la condamnation eft jufte.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 9, 'Quoi donc ? Sommes-nous plus excellens ? Nullement. Car nous avons ci-devant convaincu que tous, tant Juifs que Grecs, font fous le peché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 10, 'Selon qu\'il eft écrit, Il n\'y a nul jufte, non pas un feul.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 11, 'Il n\'y a nul qui entende, il n\'y a nul qui recherche Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 12, 'Ils ont tous fourvoyé, &amp; ont efté enfemble rendus inutiles ; il n\'y a nul qui faffe bien, non pas mefmes jufques à un.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 13, 'C\'eft un fepulcre ouvert, que leur gofier : Ils ont frauduleufement ufé de leurs langues : Sous leurs lèvres il y a du venin d\'afpic.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 14, 'Defquels la bouche eft pleine de malediction &amp; d\'amertume.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 15, 'leurs pieds font legers à efpandre le fang.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 16, 'Deftruction &amp; mifere eft en leurs voyes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 17, 'Et ils n\'ont point connu la voye de paix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 18, 'La crainte de Dieu n\'eft point devant leurs yeux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 19, 'Or nous fçavons que tout ce que la Loi dit, elle le dit à ceux qui font fous la Loi, afin que toute bouche foit fermée, &amp; que tout le monde foit coulpable devant Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 20, 'C\'eft pourquoi nulle chair ne fera juftifiée devant lui par les œuvres de la Loi : car par la Loi eft donnée la connoiffance du peché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 21, 'Mais maintenant la juftice de Dieu eft manifeftée fans la Loi, lui eftant rendu témoignage par la Loi, &amp; par les Prophetes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 22, 'Voire la juftice de Dieu, qui eft par la foi en Jefus-Chrift, envers tous &amp; fur tous les croyans : car il n\'y a nulle difference : veu que tous ont peché, &amp; font entierement deftituez de la gloire de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 23, 'Eftant juftifiez gratuïtement par fa grace, par la redemption qui eft en Jefus-Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 24, 'Lequel Dieu a ordonné de tout temps pour propitiatoire par la foi en fon fang, afin de demontrer fa juftice, par la remiffion des pechez precedens fuivant la patience de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 25, 'Voire afin de demontrer fa juftice au temps prefent, afin qu\'il foit trouvé jufte, &amp; juftifiant celui qui eft de la foi de Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 26, 'Où eft donc la vanterie ? Elle eft forclofe. Par quelle Loi ? Eft-ce par celle des œuvres ? Non : mais par la Loi de la foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 27, 'Nous concluons donc que l\'homme eft juftifié par la foi fans les œuvres de la Loi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 28, 'Dieu eft-il feulement le Dieu des Juifs ? Ne l\'eft-il point auffi des Gentils ? Certes il l\'eft auffi bien des Gentils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 29, 'Car il y a un feul Dieu qui juftifiera de la foi la Circoncifion, &amp; le prepuce par la foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 30, 'Aneantiffons-nous donc la Loi par la foi ? Ainfi n\'advienne : mais nous eftabliffons la Loi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 31, '');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 1, 'Que dirons-nous donc qu\'Abraham noftre pere a trouvé felon la chair ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 2, 'Certes fi Abraham a efté juftifié par les œuvres, il a dequoi fe vanter, mais non pas envers Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 3, 'Car que dit l\'Efcriture ? Abraham a creu à Dieu, &amp; il lui a efté alloüé à juftice.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 4, 'Or à celui qui œuvre, le falaire ne lui eft point alloüé pour grace, mais pour chofe deuë.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 5, 'Mais à celui qui n\'œuvre point, mais croit en celui qui juftifie le méchant, fa foi eft alloüée à juftice.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 6, 'Comme auffi David declare la beatitude de l\'homme à qui Dieu alloüe la juftice fans œuvres, difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 7, 'Bien-heureux font ceux defquels les iniquitez font pardonnées, &amp; defquels les pechez font couverts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 8, 'Bien-heureux eft l\'homme auquel le Seigneur n\'aura point imputé le peché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 9, 'Cette declaration donc de la beatitude, eft-ce feulement en la circoncifion, ou auffi au prepuce ? Car nous difons que la foi a efté alloüée à Abraham à juftice.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 10, 'Comment donc lui a-t\'elle efté alloüée ? A-ce efté, lui eftant déja circoncis, ou durant le prepuce ? Ce n\'a point efté en la circoncifion, mais durant le prepuce.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 11, 'Puis il receut le figne de la circoncifion, pour un fceau de la juftice de la foi, laquelle il avoit receuë durant le prepuce, afin qu\'il fuft pere de tous ceux qui croyent eftant au prepuce, &amp; que la juftice leur fuft auffi alloüée :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 12, 'Et pere de la circoncifion, affavoir de ceux qui ne font point feulement de la circoncifion, mais qui auffi fuivent le train de la foi de noftre pere Abraham, laquelle il a euë durant le prepuce.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 13, 'Car la promeffe n\'eft point advenue par la Loi à Abraham ou à fa femence (affavoir d\'eftre heritier du monde) mais par la juftice de la foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 14, 'Car fi ceux qui font de la Loi font heritiers, la foi eft aneantie, &amp; la promeffe abolie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 15, 'Veu que la Loi engendre ire : car là où il n\'y a point de Loi, il n\'y a point auffi de tranfgreffion.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 16, 'Pour cette caufe c\'eft par foi : à ce que ce foit par grace, afin que la promeffe foit affeurée à toute la femence : non feulement à celle qui eft de la Loi, mais auffi à celle qui eft de la foi d\'Abraham, lequel eft pere de nous tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 17, 'Selon qu\'il eft écrit, Je t\'ai eftabli pere de plufieurs nations devant Dieu, auquel il a creu, lequel fait vivre les morts, &amp; appelle les chofes qui ne font point, comme fi elles eftoient.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 18, 'Lequel Abraham outre efperance creut fous efperance, à ce qu\'il devinft pere de plufieurs nations : felon ce qui avoit efté dit, Ainfi fera ta femence.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 19, 'Et n\'eftant pas debile en la foi, il n\'eut point d\'égard à fon corps déja amorti, veu qu\'il avoit environ cent ans, ni auffi à l\'amortiffement de la matrice de Sara.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 20, 'Et il ne fit point de doute fur la promeffe de Dieu par deffiance : mais il fut fortifié par la foi, donnant gloire à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 21, 'Et fçachant certainement que celui qui lui avoit promis, eftoit puiffant auffi pour le faire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 22, 'C\'eft pourquoi auffi cela lui a efté alloüé à juftice.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 23, 'Or que celui lui ait efté alloüé à juftice, n\'a point efté écrit feulement pour lui :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 24, 'Mais auffi pour nous, aufquels auffi il fera alloüé, affavoir à nous qui croyons en celui qui a reffufcité des morts Jefus noftre Seigneur :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 25, 'Lequel a efté livré pour nos offenfes, &amp; eft reffufcité pour noftre juftification.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 1, 'ESTANS donc juftifiez par la foi, nous avons paix envers Dieu, par noftre Seigneur Jefus-Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 2, 'Par lequel auffi nous avons efté amenez par la foi à cette grace, en laquelle nous nous tenons fermes, &amp; nous glorifions en l\'efperance de la gloire de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 3, 'Et non feulement cela, mais nous nous glorifions mefme dans les tribulations : fçachant que la tribulation produit la patience :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 4, 'Et al patience l\'efpreuve, &amp; l\'efpreuve l\'efperance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 5, 'Or l\'efperance ne confond point, parce que la deliction de Dieu eft efpanduë en nos cœurs par le Saint Efprit qui nous a efté donné.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 6, 'Car du temps que nous eftions encore defnuez de toute force, Chrift eft mort en fon temps pour nous qui eftions du tout méchans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 7, 'Car à grande peine advient\'il que quelqu\'un meure pour un jufte : mais encore pourroit-il eftre que quelqu\'un oferoit mourir pour quelque bien-faiteur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 8, 'Mais Dieu recommande du tout fa dilection envers nous, en ce que lors que nous n\'eftions que pecheurs Chrift eft mort pour nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 9, 'Beaucoup pluftoft donc, eftans maintenant juftifiez en fon fang, ferons-nous fauvez de l\'ire par lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 10, 'Car fi lors que nous eftions ennemis, nous avons efté reconciliez avec Dieu par la mort de fon Fils : beaucoup plus eftans déja reconciliez, ferons-nous fauvez par la vie d\'icelui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 11, 'Et non feulement cela, mais nous nous glorifions mefmes en Dieu par noftre Seigneur Jefus-Chrift : par lequel maintenant nous avons obtenu la reconciliation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 12, 'C\'eft pourquoi comme par un feul homme le peché eft entré au monde, &amp; par le peché la mort : &amp; ainfi la mort eft parvenuë fur tous les hommes dautant que tous ont peché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 13, 'Car jufques à la Loi le peché eftoit au monde : or le peché n\'eft point imputé, quand il n\'y a point de Loi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 14, 'Mais la mort a regné depuis Adam jufques à Moyfe, mefmes fur ceux qui n\'ont point peché à la façon de la tranfgreffion d\'Adam, qui eft la figure de celui qui devoit venir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 15, 'Mais il n\'en prend pas du don comme de l\'offenfe. Car fi par l\'offenfe d\'un feul plufieurs font morts, beaucoup pluftoft la grace de Dieu, &amp; la donation par la grace, qui eft d\'un feul homme, affavoir de Jefus Chrift, a abondé fur plufieurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 16, 'Et il n\'en prend pas du don comme de ce qui eft par un feul qui a peché. Car la coulpe eft d\'une feule offenfe en condamnation : mais le don eft de plufieurs offenfes en juftification.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 17, 'Car fi par l\'offenfe d\'un feul la mort a regné par un feul, beaucoup pluftoft ceux qui reçoivent l\'abondance de grace, &amp; du don de juftice, regneront en vie par feul Jefus-Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 18, 'Comme donc par une feule offenfe la coulpe eft venuë fur tous les hommes en condamnation, ainfi auffi par une feule juftice juftifiante, le don eft venu fur tous les hommes en juftification de vie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 19, 'Car comme par la defobeïffance d\'un feul homme plufieurs ont efté rendus pecheurs, ainfi par l\'obeïffance d\'un feul plufieurs feront rendus juftes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 20, 'Or la Loi eft entrevenuë, afin que l\'offenfe abondaft : mais là où le peché a abondé, la grace y a abondé par deffus :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 21, 'Afin que comme le peché a regné à mort, ainfi auffi la grace regnaft par juftice à vie eternelle, par Jefus Chrift noftre Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 1, 'QUE dirons-nous donc ? Demeurerons-nous en peché, afin que la grace abonde ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 2, 'Ainfi n\'advienne. Car nous qui fommes morts à peché, comment vivrons-nous encore en lui ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 3, 'Ne fçavez-vous pas que nous tous qui avons efté baptizez en Jefus Chrift, avons efté baptizez en fa mort ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 4, 'Nous fommes donc enfevelis avec lui en fa mort par le baptefme : afin que comme Chrift eft reffufcité des morts par la gloire du Pere, nous auffi pareillement cheminons en nouveauté de vie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 5, 'Car fi nous avons efté faits une mefme plante avec lui par la conformité de fa mort, auffi le ferons-nous par la conformité de fa refurrection.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 6, 'Sçachant cela, que noftre vieil homme a efté crucifié avec lui, à ce que le corps du peché fuft reduit à neant : afin que nous ne fervions plus au peché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 7, 'Car celui qui eft mort, eft quitte de peché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 8, 'Or fi nous fommes morts avec Chrift, nous croyons que nous vivrons auffi avec lui :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 9, 'Sçachant que Chrift eftant reffufcité des morts ne meurt plus : la mort n\'a plus de domination fur lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 10, 'Car ce qu\'il eft mort, il eft mort pour une fois au peché : mais ce qu\'il eft vivant, il eft vivant à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 11, 'vous auffi faites ainfi voftre conte, que vous eftes voirement morts au peché, mais vivans à Dieu en Jefus Chrift noftre Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 12, 'Que le peché donc ne regne point en voftre corps mortel, pour lui obeïr en fes convoitifes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 13, 'Et n\'appliquez point vos membres pour eftre inftrumens d\'iniquité à peché : mais appliquez-vous à Dieu, comme de morts eftans faits vivans &amp; vos membres pour eftre inftrumens de juftice à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 14, 'Car le peché n\'aura point de domination fur vous puis, que vous n\'eftes point fous la Loi, mais fous la Grace.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 15, 'Quoi donc, pecherons-nous, parce que nous ne fommes point fous la Loi, mais fous la Grace ? Ainfi n\'advienne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 16, 'Ne fçavez-vous pas bien qu\'à quiconque vous vous rendez ferfs pour obeïr, vous eftes ferfs de celui à qui vous obeïffez, foit du peché à mort, ou d\'obeïffance à juftice ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 17, 'Or graces à Dieu que vous avez efté ferfs du peché : mais vous avez obeï de cœur à la forme expreffe de doctrine à laquelle vous avez efté attirez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 18, 'Ayans donc efté affranchis du peché, vous eftes faits ferfs à la juftice.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 19, 'Je parle à la façon des hommes, à caufe de l\'infirmité de voftre chair. Ainfi donc que vous avez appliqué vos membres pour fervir à la fouillure &amp; à l\'iniquité, à commettre iniquité : ainfi appliquez maintenant vos membres pour fervir à la juftice, en faincteté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 20, 'Car lors que vous eftiez ferfs du peché, vous eftiez francs quant à la juftice.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 21, 'Quel fruict donc aviez-vous alors és chofes defquelles maintenant vous avez honte ? Certes leur fin eft la mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 22, 'Mais maintenant ayans efté affranchis du peché, &amp; faits ferfs à Dieu, vous avez voftre fruict en fanctification : &amp; pour fin, la vie eternelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 23, 'Car les gages du peché, c\'eft la mort : mais le don de Dieu c\'eft la vie eternelle par Jefus Chrift noftre Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 1, 'NE fçavez-vous pas, freres, (car je parle à ceux qui entendent que c\'eft de la Loi) que la Loi a domination fur la perfonne tout le temps qu\'elle eft en vie :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 2, 'Car la femme qui eft en puiffance de mari, eft liée à fon mari par la Loi, tandis qu\'il eft en vie : mais fi fon mari meurt, elle eft delivrée de la Loi du mari.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 3, 'Le mari donc eftant vivant, fi elle fe joint à un autre mari, elle fera appellée adultere : mais fon mari eftant mort, elle eft delivrée de la Loi : tellement qu\'elle ne fera point adultere fi elle eft jointe à un autre mari.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 4, 'Ainfi, mes freres, vous eftes auffi morts à la Loi par le corps de Chrift : afin que vous foyez à un autre, affavoir à celui qui eft reffufcité des morts, afin que nous fructifiïons à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 5, 'Car quand nous eftions en la chair les affections des pechez (eftant efmeuës par la Loi) avoyent vigueur rn nos membres, pour fructifier à la mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 6, 'Mais maintenant nous fommes delivrez de la Loi, eftant mort ce à quoi nous eftions retenus : afin que nous fervions en nouveauté d\'efprit, &amp; non point en vieilleffe de lettre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 7, 'Que dirons-nous donc ? La Loi eft-elle peché ? Ainfi n\'advienne : au contraire je n\'ai point connu le peché, finon par la Loi. Car je n\'euffe point connu ce que c\'eftoit de convoitife, fi la Loi n\'euft dit, Tu ne convoiteras point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 8, 'Mais le peché, ayant pris occafion, a engendré en moi toute convoitife par le commandement : car fans la Loi le peché eft mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 9, 'Car jadis que j\'eftois fans la Loi, je vivois : mais quand le commandement eft venu, le peché a commencé à revivre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 10, 'Et moi je fuis devenu mort : &amp; le commandement qui m\'eftoit ordonné pour vie a efté trouvé me tourner à mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 11, 'Car le peché prenant occafion par le commandement m\'a feduit, &amp; par lui m\'a mis à mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 12, 'La Loi donc eft fainct, &amp; le commandement eft fainct, &amp; jufte, &amp; bon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 13, 'Ce donc qui eft bon m\'eft-il tourné à mort ? Ainfi n\'advienne : mais le peché afin qu\'il apparuft peché, m\'a engendré la mort par le bien : à ce que le peché fuft rendu exceffivement pechant par le commandement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 14, 'Car nous fçavons que la Loi eft fpirituelle : mais je fuis charnel, vendu fous le peché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 15, 'Car je n\'approuve point ce que je fais, veu que je ne fais point ce que je veux, mais je fais ce que je hais.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 16, 'Or fi je fais ce que je ne veux point, je confens à la Loi qu\'elle eft bonne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 17, 'Maintenant donc ce n\'eft plus moi qui fais cela : mais c\'eft le peché habitant en moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 18, 'Car je fçais qu\'en moi (c\'eft à dire en ma chair) il n\'habite point de bien : car le vouloir eft bien attaché à moi : mais je ne trouve point le moyen de parfaire le bien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 19, 'Car je ne fais point le bien que je veux, mais je fais le mal que je ne veux point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 20, 'Que fi je fais ce que je ne veux point, ce n\'eft plus moi qui le fais, mais le peché qui habite en moi :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 21, 'Je trouve donc cette Loi en moi, c\'eft que quand je veux faire le bien, le mal eft attaché à moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 22, 'Car je prens plaifir à la Loi de Dieu quant à l\'homme de dedans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 23, 'Mais je vois une autre Loi en mes membres, bataillant contre la Loi de mon entendement, &amp; me rendant prifonnier à la Loi du peché qui eft en mes membres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 24, '(Las ! Miferable que je fuis ! Qui me delivrera du corps de cette mort ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 25, 'Je rens graces à Dieu par Jefus-Chrift noftre Seigneur.) Je fers donc moi-mefme de l\'entendement à la Loi de Dieu, mais de la chair à la Loi du peché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 1, 'AINSI donc il n\'y a maintenant nulle condamnation à ceux qui font en Jefus Chrift : lefquels ne cheminent point felon la chair, mais felon l\'Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 2, 'Car la Loi de l\'efprit de vie qui eft en Jefus Chrift, m\'a affranchi de la Loi du peché &amp; de la mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 3, 'Car ce qui eftoit impoffible à la Loi, d\'autant qu\'elle eftoit foible en la chair, Dieu ayant envoyé fon propre Fils en forme de chair de peché, &amp; pour le peché, a condamné le peché en la chair :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 4, 'Afin que la juftice de la Loi fuft accomplie en nous, qui ne cheminons point felon la chair, mais felon l\'Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 5, 'Car ceux qui font felon la chair, font affectionnez aux chofes de la chair : mais ceux qui font felon l\'Efprit, aux chofes de l\'Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 6, 'Car l\'affection de la chair eft mort : mais l\'affection de l\'Efprit eft vie &amp; paix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 7, 'Parce que l\'affection de la chair eft inimitié contre Dieu : car elle ne fe rend point fujette à la Loi de Dieu : &amp; de vrai elle ne le peut.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 8, 'C\'eft pourquoi ceux qui font en la chair, ne peuvent plaire à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 9, 'Or vous n\'eftes point en la chair, mais en Efprit, voire fi l\'Efprit de Dieu habite en vous : mais fi quelqu\'un n\'a point l\'Efprit de Chrift, celui-là n\'eft point à lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 10, 'Et fi Chrift eft en vous, le corps eft bien mort à caufe du peché : mais l\'Efprit eft vie à caufe de la juftice.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 11, 'Or fi l\'Efprit de celui qui a reffufcité Jefus des morts, habite en vous, celui qui a reffufcité Chrift des morts, vivifiera auffi vos corps mortels, par fon Efprit habitant en vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 12, 'Partant donc, mes freres, nous fommes detteurs, non point à la chair, pour vivre felon la chair.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 13, 'Car fi vous vivez felon la chair, vous mourrez : mais fi par l\'Efprit vous mortifiés les faits du corps, vous vivrés.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 14, 'Car tous ceux qui font conduits par l\'Efprit de Dieu, font enfans de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 15, 'Car vous n\'avez point receu un Efprit de fervitude pour eftre derechef en crainte ; mais vous avez receu l\'Efprit d\'adoption, par lequel nous crions, Abba Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 16, 'C\'eft ce mefme Efprit qui rend tefmoignage à noftre efprit que nous fommes enfans de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 17, 'Et fi nous fommes enfans, nous fommes donc heritiers : heritiers dis-je de Dieu, &amp; coheritiers de Chrift : voire fi nous fouffrons avec lui, afin que nous foyons auffi glorifiez, avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 18, 'Car tout bien conté j\'eftime que les fouffrances du temps prefent ne font point à contrepefer à la gloire à venir qui doit eftre revelée en nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 19, 'Car le grand &amp; ardent defir des creatures, eft en ce qu\'elles attendent que les enfans de Dieu foyent revelez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 20, 'Car les creatures font fujettes à vanité, non point de leur volonté, mais à caufe de celui qui les a affujetties, fous efperance qu\'elles feront auffi delivrées de la fervitude de corruption, pour eftre en la liberté de la gloire des enfans de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 21, 'Car nous fçavons que toutes les creatures foufpirent &amp; font en travail enfemble jufques à maintenant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 22, 'Et non feulement elles, mais nous auffi, qui avons les premices de l\'Efprit, nous-mefmes foufpirons en nous-mefmes, en attendant l\'adoption, affavoir la redemption de noftre corps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 23, 'Car ce que nous fommes fauvez, c\'eft en efperance : or l\'efperance qu\'on voit, n\'eft point efperance : car pourquoi mefmes quelqu\'un efpereroit-il ce qu\'il voit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 24, 'Mais fi nous efperons ce que nous ne voyons point, c\'eft que nous l\'attendons par patience.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 25, 'Pareillement auffi l\'Efprit foulage de fa part nos foibleffes : car nous ne fçavons point ce que nous devons prier comme il appartient : mais l\'Efprit lui-mefme prie pour nous par des foufpirs qui ne fe peuvent exprimer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 26, 'Mais celui qui fonde les cœurs, connoit quelle eft l\'affection de l\'Efprit : car il prie pour les faints felon Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 27, 'Or nous fçavons auffi que toutes chofes aident enfemble en bien à ceux qui aiment Dieu affavoir, à ceux qui font appellez felon fon propos arrefté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 28, 'Car ceux qu\'il a connus auparavant, il les a auffi predeftinez à eftre conformes à l\'image de fon Fils : afin qu\'il foit le premier né entre plufieurs freres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 29, 'Et ceux qu\'il a predeftinez, il les a auffi appellez : &amp; ceux qu\'il a appellez, il les a auffi juftifiez : &amp; ceux qu\'il a juftifiez, il les a auffi glorifiez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 30, 'Que dirons-nous donc à ces chofes ? Si Dieu eft pour nous, qui eft-ce qui fera contre nous ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 31, 'Lui qui n\'a point efpargné fon propre Fils, mais l\'a livré pour nous tous, comment ne nous donnera-t\'il auffi toutes chofes avec lui ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 32, 'Qui eft-ce qui intentera accufation contre les éleus de Dieu ? Dieu eft celui qui juftifie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 33, 'Qui fera celui qui condamnera ? Chrift eft celui qui eft mort, &amp;, qui plus eft, qui eft reffufcité : lequel auffi eft à la dextre de Dieu, &amp; qui mefmes prie pour nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 34, 'Qui eft-ce qui nous feparera de la dilection de Chrift ? sera-ce oppreffion, ou angoiffe, ou perfecution, ou famine, ou nudité, ou peril, ou efpée ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 35, 'Ainfi qu\'il eft efcrit, Nous fommes livrez à la mort pour l\'amour de toi tous les jours, &amp; fommes eftimez comme des brebis de la boucherie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 36, 'Au contraire en toutes ces chofes nous fommes plus que vainqueurs par celui qui nous a aimez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 37, 'Car je fuis affeuré que ni mort, ni vie, ni Ange, ni Principautez, ni Puiffances, ni chofes prefentes, ni chofes à venir,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 38, 'Ni hauteffe, ni profondeur, ni aucune autre creature, ne nous pourra feparer de la dilection de Dieu, qu\'il nous a montré en Jefus-Chrift noftre Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 39, '');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 1, 'JE dis verité en Chrift, je ne mens point, ma confcience me rendant témoignage par le Saint Efprit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 2, 'Que j\'ai une grande trifteffe &amp; un continuel tourment en mon cœur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 3, 'Car je defirerois moi-mefme d\'eftre feparé de Chrift pour mes freres, qui font mes parens felon la chair :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 4, 'Lefquels font Ifraëlites, defquels eft l\'adoption, &amp; la gloire, &amp; les alliances, &amp; l\'ordonnance de la Loi, &amp; le fervice divin, &amp; les promeffes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 5, 'Defquels font les peres, &amp; defquels felon la chair eft Chrift, qui eft Dieu fur toutes chofes, benit eternellement. Amen.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 6, 'Toutefois il ne fe peut faire que la parole de Dieu foit décheute : car tous ceux qui font d\'Ifraël, ne font pas pourtant Ifraël :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 7, 'Et pour eftre la femence d\'Abraham, ils ne font pas tous enfans : mais en Ifaac te fera appellée femence.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 8, 'C\'eft à dire ce ne font pas ceux qui font enfans de la chair, qui font enfans de Dieu : mais ceux qui font enfans de la promeffe, font reputez pour femence.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 9, 'Car voici la parole de la promeffe ; Je viendrai en cette mefme faifon, &amp; Sara aura un fils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 10, 'Et non feulement celui-ci, mais auffi Rebecca, quand elle eut conceu d\'un, affavoir de noftre pere Ifaac.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 11, 'Car devant que les enfans fuffent nés, &amp; qu\'ils euffent fait ni bien ni mal, (afin que le propos arrefté felon l\'élection de Dieu, demeuraft, non point par les œuvres, mais par celui qui appelle,)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 12, 'Il lui fut dit, Le plus grand fervira au moindre :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 13, 'Ainfi qu\'il eft efcrit, J\'ai aimé Jacob, &amp; ai haï Efau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 14, 'Que dirons-nous donc ? Y a-t\'il de l\'iniquité en Dieu ? Ainfi n\'advienne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 15, 'Car il dit à Moyfe, J\'aurai compaffion de celui de qui j\'aurai compaffion : &amp; je ferai mifericorde à celui à qui je ferai mifericorde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 16, 'Ce n\'eft point donc ni du voulant ni du courant : mais de Dieu qui fait mifericorde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 17, 'Car l\'Efcriture dit à Pharao, Je t\'ai fufcité à cette propre fin, pour demontrer en toi ma puiffance, &amp; afin que mon Nom foit publié en toute la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 18, 'Il a donc compaffion de celui qu\'il veut, &amp; endurcit celui qu\'il veut.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 19, 'Or tu me diras, Pourquoi fe plaint-il encore ? car qui eft celui qui peut refifter à fa volonté ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 20, 'Mais pluftoft, ô homme ! Qui es-tu, toi qui conteftes contre Dieu ? La chofe formée dira-t\'elle à celui qui l\'a formée, Pourquoi m\'as-tu ainfi faite ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 21, 'Le potier de terre n\'a-t\'il pas la puiffance de faire d\'une mefme maffe de terre un vaiffeau à honneur, &amp; un autre à des-honneur ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 22, 'Et qu\'eft ce, fi Dieu en voulant montrer fon ire, &amp; donner à connoître fa puiffance, a toleré en grande patience les vaiffeaux d\'ire appareillez à perdition ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 23, 'Et pour donner à connoître les richeffes de fa gloire és vaiffeaux de mifericorde, lefquels il a preparez à gloire ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 24, 'Lefquels il a auffi appellez, affavoir nous, non point feulement d\'entre les Juifs, mais auffi d\'entre les Gentils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 25, 'Selon auffi qu\'il dit en Ofée, J\'appellerai mon peuple, celui qui n\'eftoit point mon peuple : &amp; la bien-aimée, celle qui n\'eftoit point bien-aimée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 26, 'Et il adviendra, qu\'au lieu qu\'il leur a efté dit, Vous n\'eftes point mon peuple, là ils feront appellez les enfans du Dieu vivant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 27, 'Auffi Efaïe crie touchant Ifraël, Quand le nombre des enfans d\'Ifraël feroit comme le fablon de la mer, il n\'y en aura qu\'un petit refte de fauvé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 28, 'Car le Seigneur met à fin &amp; abrege l\'affaire en juftice : voire il fera une affaire abbregée fur la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 29, 'Et comme Efaïe avoit dit auparavant, Si le Seigneur des armées ne nous euft laiffé quelque femence, nous euffions efté faits comme Sodome, &amp; euffions efté femblables à Gomorrhe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 30, 'Que dirons-nous donc ? Que les Gentils qui ne pourchaffoyent point la juftice, ont atteint la juftice, voire la jufte qui eft par la foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 31, 'Mais Ifraël pourchaffant la Loi de juftice, n\'eft point parvenu à la Loi de juftice.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 32, 'Pourquoi ? Parce que ce n\'a point efté par la foi, mais comme par les œuvres de la Loi : car ils ont heurté contre la Pierre d\'achoppement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 33, 'Ainfi qu\'il eft efcrit, Voici, je mets en Sion la pierre d\'achoppement, &amp; la pierre de trebufchement : &amp; quiconque croira en lui ne fera point confus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 1, 'FRERES, quant à la bonne affection de mon cœur, &amp; à la priere que je fais à Dieu pour Ifraël, c\'eft qu\'ils foyent fauvez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 2, 'Car je leur rens témoignage qu\'ils ont le zele de Dieu, mais non pas felon connoiffance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 3, 'Car ne connoiffans point la juftice de Dieu, &amp; cherchans à eftablir leur propre juftice, ils ne fe font point rangez à la juftice de Dieu');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 4, 'Car Chrift eft la fin de la Loi, en juftice à tout croyant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 5, 'Car Moyfe décrit ainfi la juftice qui eft par la Loi, affavoir Que l\'homme qui fera ces chofes, vivra par elles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 6, 'Mais la juftice qui eft par la foi, dit ainfi ; Ne di point en ton cœur, qui montera au ciel ? Cela eft ramener Chrift d\'en haut :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 7, 'Ou, Qui defcendra en l\'abyfme ? Cela eft ramener Chrift des morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 8, 'Mais que dit-elle ? La parole eft prés de toi en ta bouche, &amp; en ton cœur. C\'eft là la parole de la foi, laquelle nous prefchons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 9, 'Car fi tu confeffes le Seigneur Jefus de ta bouche, &amp; que tu croyes en ton cœur que Dieu l\'a reffufcité des morts, tu feras fauvé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 10, 'Car de cœur on croit à juftice, &amp; de bouche on fait confeffion à falut.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 11, 'Car l\'Efcriture dit, Quiconque croit en lui, ne fera point confus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 12, 'D\'autant qu\'il n\'y a point de difference du Juif ni du Grec : car il y a un mefme Seigneur de tous, qui eft riche envers tous ceux qui l\'invoquent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 13, 'Car quiconque invoquera le nom du Seigneur fera fauvé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 14, 'Comment donc invoqueront-ils celui auquel ils n\'ont point creu ? &amp; comment croiront-ils en celui duquel ils n\'ont point ouï parler ? &amp; comment orront-ils s\'il n\'y a quelqu\'un qui leur prefche ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 15, 'Et comment prefchera-t\'on, finon qu\'il y en ait qui foyent envoyez ? Ainfi qu\'il eft efcrit, O que font beaux les pieds de ceux qui annoncent la paix, voire de ceux qui annoncent les bonnes chofes !');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 16, 'Mais tous n\'ont pas obeï à l\'Evangile : car Efaïe dit, Seigneur, qui a creu à noftre predication ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 17, 'La foi donc eft par l\'ouïr : &amp; l\'ouïr par la parole de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 18, 'Mais je demande, Ne l\'ont-ils point ouï ? Au contraire leur fon eft allé par toute la terre, &amp; leurs paroles jufques aux bouts du monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 19, 'Mais je demande, Ifraël ne l\'a-t\'il point connu ? Moyfe le premier dit ; Je vous provoquerai à jaloufie par celui qui n\'eft point peuple : je vous efmouverai à ire par une nation deftituée d\'intelligence.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 20, 'Et Efaïe s\'en hardit tout à fait &amp; dit, J\'ai efté trouvé de ceux qui ne me cherchoyent point : &amp; fuis manifeftement apparu à ceux qui ne s\'enqueroyent point de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 21, 'Mais quant à Ifraël, il dit, J\'ai tout le jour eftendu mes mains vers un peuple rebelle &amp; contredifant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 1, 'JE demande donc, Dieu a-t\'il rejetté fon peuple ? Ainfi n\'advienne : car je fuis auffi Ifraëlite, de la pofterité d\'Abraham, de la lignée de Benjamin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 2, 'Dieu n\'a point rejetté fon peuple, lequel il a auparavant connu. Ne fçavez-vous pas ce que l\'Efcriture dit d\'Elie ? Comment il parle à Dieu contre Ifraël, difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 3, 'Seigneur, ils ont tué tes Prophetes, &amp; ont demoli tes autels : &amp; je fuis demeuré moi feul, &amp; ils cherchent à m\'ofter la vie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 4, 'Mais que lui fut-il refpondu de Dieu ? Je me fuis refervé fept mille hommes qui n\'ont point ployé le genoüil devant Baal.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 5, 'Ainfi donc auffi au temps prefent il y a du refte felon l\'élection de grace.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 6, 'Que fi c\'eft par la grace, ce n\'eft plus par les œuvres, autrement la grace, n\'eft plus grace. Mais fi c\'eft par les œuvres ce n\'eft plus par la grace : autrement l\'œuvre n\'eft plus œuvre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 7, 'Quoi donc ? Ce qu\'Ifraël eft apres à chercher, il ne l\'a point obtenu, mais l\'élection l\'a obtenu, &amp; les autres ont efté endurcis,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 8, '(Ainfi qu\'il eft efcrit, Dieu leur a donné un efprit affoupi, &amp; des oreilles pour ne point ouïr) jufques au jour prefent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 9, 'Et David dit ; Que leur table leur foit tournée en laqs, en piege &amp; en trébuchement, &amp; cela pour leur retribution.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 10, 'Que leurs yeux foyent obfcurcis pour ne point voir : &amp; courbe continuellement leur dos.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 11, 'Mais je demande, Ont-ils choppé pour trébucher ? Ainfi n\'advienne : mais par leur cheute le salut eft advenu aux Gentils, pour les provoquer à la jaloufie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 12, 'Or fi leur cheute eft la richeffe du monde, &amp; leur diminution la richeffe des Gentils : combien plus le fera leur abondance ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 13, 'Car je parle à vous, Gentils : entant certes que je fuis Apoftre des Gentils, je rens honorable mon miniftere :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 14, 'Pour voir fi en quelque façon je puis provoquer ceux de ma chair à la jaloufie, &amp; en fauver quelques-uns.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 15, 'Car fi leur rejection eft la reconciliation du monde, quelle fera leur reception finon la vie d\'entre les mort ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 16, 'Or fi les premices font faintes, auffi eft la maffe : &amp; fi la racine eft fainte, auffi font les branches.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 17, 'Que fi quelques-unes des branches ont efté retranchées, &amp; toi qui eftois olivier fauvage as efté enté en leur place, &amp; as efté fait participant de la racine &amp; de la graiffe de l\'olivier,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 18, 'Ne te glorifie point contre les branches : que fi tu te glorifies, ce n\'eft pas toi qui portes la racine, mais c\'eft la racine qui te porte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 19, 'Or tu diras, Les branches ont efté retranchées, afin que j\'y fuffe enté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 20, 'C\'eft bien dit : elles ont efté retranchées par incredulité, &amp; toi tu es debout par la foi : ne t\'éleve point par orgueil, mais crain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 21, 'Car fi Dieu n\'a point efpargné les branches naturelles, garde qu\'il n\'advienne qu\'il ne t\'efpargne point auffi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 22, 'Regarde donc la benignité &amp; la feverité de Dieu : affavoir la feverité fur ceux qui font trebuchez : &amp; la benignité envers toi, fi tu perfeveres en fa benignité : autrement tu feras auffi retranché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 23, 'Et quant à ceux-là auffi, ils ne perfeverent point en incredulité, ils feront entez : car Dieu eft puiffant pour les enter derechef.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 24, 'Car fi tu as efté coupé de l\'olivier qui de nature eftoit fauvage ; &amp; as efté contre nature enté en l\'olivier franc ; ceux qui le font felon nature, combien pluftoft feront-ils entez en leur propre olivier ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 25, 'Car, freres, je ne veux pas que vous ignoriés ce myftere, afin que vous ne foyez point fages en vous-mefmes : c\'eft qu\'il eft advenu un endurciffement en Ifraël en partie, jufques à ce que la plenitude des Gentils foit entrée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 26, 'Et ainfi tout Ifraël fera fauvé : ainfi qu\'il eft efcrit, Celui qui délivre viendra de Sion, &amp; détournera de Jacob les impiétés.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 27, 'Et ils auront de par moi cette alliance, c\'eft que j\'ofterai de deffus eux leurs pechez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 28, 'Ils font certes ennemis quant à l\'Evangile à caufe de vous : mais ils font bien-aimez quant à l\'élection à caufe des Peres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 29, 'Car les dons &amp; la vocation de Dieu font fans repentance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 30, 'Car comme vous avez auffi autresfois efté rebelles à Dieu &amp; maintenant vous avez obtenu mifericorde par la rebellion de ceux-ci ;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 31, 'Pareillement auffi maintenant ils ont efté rebelles, afin qu\'ils obtiennent auffi mifericorde, par la mifericorde qui vous a efté faite.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 32, 'Car Dieu a tous enclos fous la rebellion, afin qu\'il fift mifericorde à tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 33, 'O profondeur des richeffes, &amp; de la fapience, &amp; de la connoiffance de Dieu ! que fes jugemens font incomprehenfibles, &amp; fes voyes impoffibles à trouver !');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 34, 'Car qui eft-ce qui a connu la penfée du Seigneur ? Ou qui a efté fon confeiller ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 35, 'Ou qui eft-ce qui lui a donné le premier, &amp; il lui fera rendu ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 36, 'Car de lui, &amp; par lui, &amp; pour lui font toutes chofes : à lui foit gloire eternellement. Amen.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 1, 'JE vous exhorte donc, freres, par les compaffions de Dieu, que vous prefentiez vos corps en facrifice vivant, faint, plaifant à Dieu, qui eft voftre raifonnable fervice.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 2, 'Et ne vous conformez point à ce prefent fiecle, mais foyez transformez par le renouvellement de voftre entendement, afin que vous efprouviez quelle eft la volonté de Dieu, bonne &amp; plaifante, &amp; parfaite.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 3, 'Or par la grace qui m\'eft donnée, je dis à chacun d\'entre vous, que nul ne prefume d\'eftre fage par deffus ce qu\'il faut eftre fage : mais qu\'il foit fage à fobrieté, felon que Dieu departi à chacun la mefure de foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 4, 'Car comme nous avons plufieurs membres en un feul corps, &amp; tous les membres n\'ont pas une mefme operation :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 5, 'Ainfi nous, qui fommes plufieurs, fommes un feul corps en Chrift : &amp; chacun en fon endroit membres l\'un de l\'autre');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 6, 'Or ayans des dons differens, felon la grace qui nous eft donnée ; foit prophetie, prophetizons felon l\'analogie de la foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 7, 'Soit miniftere, que ce foit en adminiftration : foit que quelqu\'un enfeigne, qu\'il donne enfeignement :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 8, 'Soit que quelqu\'un exhorte, que ce foit en exhortation : foit que quelqu\'un diftribuë qu\'il le faffe en fimplicité : foit que quelqu\'un prefide, qu\'il le faffe foigneufement : foit que quelqu\'un exercice mifericorde : qu\'il le faffe joyeufement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 9, 'Que la charité foit fans feintife. Ayez en horreur le mal, vous tenans collez au bien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 10, 'Enclins par charité fraternelle à montrer de l\'affection l\'un envers l\'autre : prevenant l\'un l\'autre par honneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 11, 'Non pareffeux à vous employer pour autrui : fervens d\'efprit : fervans au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 12, 'Joyeux en efperance : patiens en tribulation : perfeverans en oraifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 13, 'Communiquans aux neceffitez des Saints : pourchaffans l\'hofpitalité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 14, 'Beniffez ceux, qui vous perfecutent : beniffez-les, &amp; ne les maudiffez point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 15, 'Soyez en joye avec ceux qui font en joye : &amp; foyez en pleur avec ceux qui font en pleur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 16, 'Ayans un mefme fentiment les uns envers les autres : n\'affectans point les chofes hautes : mais vous accommodans aux chofes baffes. Ne foyez point fages en vous-mefmes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 17, 'Ne rendez à perfonne mal pour mal. Pourchaffez les chofes honneftes devant tous les hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 18, 'S\'il fe peut faire, entant qu\'en vous eft, ayez la paix avec tous les hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 19, 'Ne vous vangez point vous-mefmes, mes bien-aimez : mais donnez lieu à l\'ire : car il eft efcrit, A moi appartient la vengeance : je le rendrai, dit le Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 20, 'Si donc ton ennemi a faim, donne lui à manger : s\'il a foif, donne lui à boire : car en ce faifant tu lui affembleras des chardons de feu fur fa tefte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 21, 'Ne fois point furmonté du mal : mais furmonte le mal par le bien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 1, 'QUE toute perfonne foit fujette aux puiffances fuperieures : car il n\'y a point de Puiffance finon de par Dieu : &amp; les Puiffances qui fubfiftent, font ordonnées de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 2, 'C\'eft pourquoi celui qui refifte à la Puiffance, refifte à l\'ordonnance de Dieu : &amp; ceux qui y refiftent, feront venir condamnation fur eux-mefmes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 3, 'Car les Princes ne font point à crainte pour de bonnes œuvres, mais pour de mauvaifes. Or veux-tu ne craindre point la Puiffances ? Sai bien, &amp; tu recevras d\'elle de la loüange.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 4, 'Car le prince eft ferviteur de Dieu, pour ton bien : mais fi tu fais mal, crain : dautant qu\'il ne porte point l\'efpée fans caufe : car il eft ferviteur de Dieu, ordonné pour faire juftice en ire de celui qui fait mal.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 5, 'Et partant il faut eftre fujets, non feulement pour l\'ire, mais auffi pour la confcience.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 6, 'Car pour cette caufe auffi vous payez les tributs : dautant qu\'ils font miniftres de Dieu, s\'employans à cela.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 7, 'Rendez donc à tous ce qui leur eft deu : à qui tribut, le tribut : à qui peage, le peage : à qui crainte, la crainte : à qui honneur, l\'honneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 8, 'Ne devez rien à perfonne finon que vous vous aimiez l\'un l\'autre : car celui qui aime autrui, il a accompli la Loi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 9, 'Car ce qui eft dit, Tu ne commettras point adultere : Tu ne tuëras point : Tu ne déroberas point : Tu ne diras point faux témoignage : Tu ne convoiteras point : &amp; s\'il y a quelque autre commandement, il eft fommairement compris en ce poinct ici : Tu aimeras ton prochain comme toi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 10, 'La charité ne fait point de mal au prochain : l\'accompliffement donc de la Loi c\'eft la charité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 11, 'Mefmes veu la faifon, affavoir qu\'il eft déja temps de nous réveiller du fommeil : car maintenant le falut eft plus prés de nous, que lors que nous avons creu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 12, 'La nuit eft paffée, &amp; le jour eft approché : rejettons donc les œuvres de tenebres, &amp; foyons reveftus des armes de lumiere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 13, 'Cheminons honneftement comme de jour : non point en gourmandifes, ni en yvrogneries : non point en couches, ni en infolences : non point en querelles, ni en envie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 14, 'Mais foyez reveftus du Seigneur Jefus Chrift, &amp; n\'ayez point de foin de la chair pour accomplir fes convoitifes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 1, 'OR quant à celui qui eft debile en la foi, recevez-le, non point en debats de difputes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 2, 'L\'un croit qu\'on peut manger de toutes chofes, &amp; l\'autre qui eft debile, mange des herbes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 3, 'Que celui qui mange, ne mefprife pas celui qui ne mange point, &amp; que celui qui ne mange pas, ne juge point celui qui mange : car Dieu l\'a pris à foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 4, 'Qui es-tu toi, qui juges le ferviteur d\'autrui ? Il fe tient ferme ou trebuche à fon propre Seigneur : mefmes il fera affermi : car Dieu eft puiffant pour l\'affermir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 5, 'L\'un eftime un jour plus que l\'autre, &amp; l\'autre eftime chaque jour également : que chacun foit pleinement refolu en fon entendement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 6, 'Celui qui à égard au jour, il y a égard au Seigneur : &amp; celui qui n\'a point d\'égard au jour, il n\'y a point d\'égard au Seigneur. Celui qui mange, il mange au Seigneur : car il en rend graces à Dieu : &amp; celui qui ne mange point, il ne mange point au Seigneur, &amp; en rend graces à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 7, 'Car nul de nous ne vit à foi, &amp; nul ne meurt à foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 8, 'Car foit que nous vivons, nous vivons au Seigneur : ou foit que nous mourions, nous mourons au Seigneur. Soit donc que nous vivions, foit que nous mourions, nous fommes au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 9, 'Car pour cela Chrift eft mort, &amp; eft reffufcité, &amp; eft retourné en vie : afin qu\'il ait feigneurie tant fur les morts que fur les vivans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 10, 'Mais toi, pourquoi juges-tu ton frere ? ou toi auffi, pourquoi mefprifes-tu ton frere ? certes nous comparoiftrons tous devant le fiege judicial de Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 11, 'Car il eft efcrit, Je fuis vivant, dit le Seigneur, que tout genouil fe ployera devant moi, &amp; toute langue donnera loüange à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 12, 'Ainfi donc chacun de nous rendra conte pour foi-mefme à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 13, 'Ne jugeons plus donc l\'un l\'autre : mais ufés pluftoft du jugement en cela, de ne mettre aucun achoppement ou trébuchement devant voftre frere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 14, 'Je fçais &amp; fuis perfuadé par le Seigneur Jefus, que rien n\'eft fouillé de foi-mefme : finon à celui qui eftime quelque chofe eft fouillée, elle lui eft fouillée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 15, 'Mais fi ton frere eft contrifté pour la viande, tu ne chemines point felon la charité : Ne deftrui point pour ta viande celui pour lequel Chrift eft mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 16, 'Que donc voftre bien ne foit point blafmé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 17, 'Car le Royaume de Dieu n\'eft point viande ni breuvage : mais juftice, paix, &amp; joye, par le Sainct Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 18, 'Car celui qui en cela fert à Chrift, eft agreable à Dieu, &amp; eft approuvé des hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 19, 'Pourchaffons donc les chofes qui font de paix, &amp; qui font d\'edification mutuelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 20, 'Ne ruïne point l\'œuvre de Dieu pour la viande. Il eft vrai que toutes chofes font nettes : mais il y a du mal pour l\'homme qui mange avec achoppement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 21, 'Il eft bon de ne point manger de chair, &amp; de ne point boire du vin, &amp; de ne faire aucune chofe en laquelle ton frere choppe, ou fe fcandalife, ou foit infirme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 22, 'As-tu la foi : aye-la en toi-mefme devant Dieu. Bien-heureux eft celui qui ne juge point foi-mefme en ce qu\'il approuve.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 23, 'Mais celui qui en fait fcrupule, il eft condamné, s\'il en mange : car il n\'en mange point par foi : or tout ce qui n\'eft point de la foi eft peché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 1, 'OR nous devons, nous qui fommes forts, fupporter les infirmitez des foibles, &amp; non point complaire à nous-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 2, 'Partant que chacun de nous, complaife à fon prochain en bien, pour l\'edification.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 3, 'Car auffi Chrift n\'a point voulu complaire à foi-mefme, mais ainfi qu\'il eft écrit, Les reproches de ceux qui te font des reproches, font tombées fur moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 4, 'Car toutes les chofes qui ont efté auparavant écrites, ont efté écrites pour noftre endoctrinement : afin que par patience &amp; confolation des Efcritures nous ayons efperance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 5, 'Or le Dieu de patience &amp; de confolation vous donne d\'avoir un mefme fentiment entre vous felon Jefus-Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 6, 'Afin que tous d\'un courage &amp; d\'une bouche vous glorifiez le Dieu &amp; Pere de noftre Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 7, 'C\'eft pourquoi recevez-vous l\'un l\'autre, comme auffi Chrift nous a receus à foi à la gloire de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 8, 'Or je dis que Jefus-Chrift a efté miniftre de la Circoncifion, pour la verité de Dieu, afin de ratifier les promeffes faites aux Peres :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 9, 'Et de faire que les Gentils honorent Dieu pour fa mifericorde : felon qu\'il eft efcrit, Pour cette caufe je te donnerai loüange entre les Gentils, &amp; pfalmodierai en ton Nom.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 10, 'Et derechef il dit, Gentils, éjouïffez-vous avec fon peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 11, 'Et derechef, Toutes nations, loüez le Seigneur : &amp; vous tous peuples, celebrez-le.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 12, 'Et derechef Efaïe, dit, Il y aura une racine de Jeffe, &amp; un qui s\'élevera pour gouverner les Gentils : les Gentils auront efperance en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 13, 'Le Dieu d\'efperance donc, vous veille remplir de toute joye, &amp; de paix, en croyant : afin que vous abondiez en efperance par la puiffance du Saint Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 14, 'Or mes freres, je fuis auffi moi-mefme perfuadé de vous, que vous eftes auffi pleins de bonté, remplis de toute connoiffance, &amp; que vous pouvez mefmes vous admonefter l\'un l\'autre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 15, 'Mais, freres, je vous ai efcrit en quelque forte, plus librement, comme vous remettant en memoire, à caufe de la grace qui m\'a efté donnée de Dieu :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 16, 'Afin que je fois miniftre de Jefus-Chrift, envers les Gentils, vacquant au facrifice de l\'Evangile de Dieu, à ce qu l\'oblation des Gentils foit agreable, eftant fanctifiée par le S. Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 17, 'J\'ai donc de quoi me glorifier en Jefus-Chrift, és chofes qui appartiennent à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 18, 'Car je n\'oferois rien dire que Chrift n\'ait fait pour moi pour amener les Gentils à l\'obeïffance, par parole, &amp; par œuvre :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 19, 'Avec vertu de fignes &amp; de miracles, en la puiffance de l\'Efprit de Dieu : tellement que depuis Jerufalem &amp; à l\'environ, jufques en l\'Illyric, j\'ai fait abonder l\'Evangile de Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 20, 'M\'eftudiant ainfi affectueufement d\'annoncer l\'Evangile, non pas où il avoit efté fait mention de Chrift : (afin que je n\'edifiaffe point fur la fondation d\'autrui.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 21, 'Mais, felon qu\'il eft efcrit, Ceux aufquels il n\'a rien efté annoncé de lui, le verront : &amp; ceux qui n\'en ont rien ouï, l\'entendront.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 22, 'C\'eft pourquoi auffi, j\'ai efté fouventefois empefché de venir vers vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 23, 'Mais maintenant, veu que je n\'ai plus de lieu en ces quartiers-ci, &amp; que déja par plufieurs années j\'ai un grand defir de venir vers vous :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 24, 'Quand je partirai pour aller en Efpagne, je viendrai vers vous : car j\'efpere que je vous verrai en paffant par devers vous, &amp; que je ferai là conduit de par vous, apres avoir efté premierement en partie raffafié d\'avoir efté avec vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 25, 'Mais pour le prefent je m\'en vais à Jerufalem pour fubvenir aux Saints.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 26, 'Car il a femblé aux Macedoniens &amp; aux Achaïens de faire une contribution pour communiquer aux pauvres d\'entre les Saints qui font à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 27, 'Car il leur a femblé bon ainfi, &amp; auffi ils leur font obligez. Car fi les Gentils ont efté participans de leurs biens fpirituels, ils leur doivent auffi adminiftrer quant aux charnels.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 28, 'Apres donc que j\'aurai achevé cela, &amp; que j\'aurai configné ce fruict, j\'irai par vos quartiers en Efpagne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 29, 'Et je fçais que quand je viendrai vers vous, j\'y viendrai avec une abondance de benediction de l\'Evangile de Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 30, 'Or je vous exhorte, freres, par noftre Seigneur Jefus Chrift, &amp; par la dilection de l\'Efprit, que vous combattiez avec moi, en vos prieres à Dieu pour moi :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 31, 'Afin que je fois delivré des rebelles qui font en Judée : &amp; que mon adminiftration que j\'ai à faire à Jerufalem foit renduë agreable aux Saints.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 32, 'Afin que je vienne vers vous en joye par la volonté de Dieu, &amp; que je me recrée avec vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 33, 'Or le Dieu de paix foit avec vous tous. Amen.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 1, 'JE vous recommande noftre fœur Phebe, laquelle eft Diaconiffe de l\'Eglife de Cenchrée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 2, 'Afin que vous la recueilliez au Seigneur, comme il appartient aux Saints, &amp; que vous l\'affiftiez en toute chofe qu\'elle aura befoin de vous : car elle a efté hofteffe de plufieurs, voire de moi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 3, 'Salüez Prifcille &amp; Aquile, mes compagnons d\'œuvre en Jefus-Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 4, 'Qui ont foufmis leur col pour ma vie : aufquels je ne rens point grace moi feul, mais auffi toutes les Eglifes des Gentils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 5, 'Salüez auffi l\'Eglife qui eft en leur maifon. Salüez Epainete mon bien-aimé, qui eft les premices d\'Achaïe en Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 6, 'Salüez Marie, laquelle a fort travaillé envers nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 7, 'Salüez Andronique &amp; Junias mes coufins, &amp; qui ont efté prifonniers avec moi, lefquels font notables entre les Apoftres, &amp; qui ont mefmes efté devant moi en Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 8, 'Salüez Amplias mon bien-aimé au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 9, 'Salüez Urbain noftre compagnon d\'œuvre en Chrift, &amp; Stachys mon bien-aimé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 10, 'Salüez Appelles approuvé en Chrift. Salüez ceux de chez Ariftobule.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 11, 'Salüez Herodion mon coufin. Salüez ceux de chez Narciffe, qui font en noftre Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 12, 'Salüez Tryphene &amp; Thryphofe, lefquelles travaillent en noftre Seigneur. Salüez Perfide la bien-aimée, laquelle a beaucoup travaillé en noftre Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 13, 'Salüez Rufus efleu au Seigneur, fa mere &amp; la mienne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 14, 'Salüez Afyncrite, Phlegon, Hermas, Patrobas, Hermes, &amp; les freres qui font avec eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 15, 'Salüez Philologue, &amp; julie, Nerée, &amp; fa fœur, &amp; Olympe, &amp; tous les Saints qui font avec eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 16, 'Salüez-vous l\'un l\'autre par un faint baifer. Les Eglifes de Chrift vous falüent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 17, 'Or je vous exhorte, freres, que vous preniez garde à ceux qui font des partialitez &amp; des fcandales contre la doctrine que vous avez apprife, &amp; que vous vous deftourniez d\'eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 18, 'Car ceux qui font tels, ne fervent point noftre Seigneur Jefus Chrift, mais leur propre ventre : &amp; par de douces paroles &amp; des flatteries feduifent les cœurs des fimples.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 19, 'Car voftre obeïffance eft parvenuë à la connoiffance de tous. Je m\'éjouïs donc de vous ; mais je defire que vous foyez fages quant au bien, &amp; fimples quant au mal.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 20, 'Or le Dieu de paix brifera en bref Satan fous vos pieds. La grace de noftre Seigneur Jefus-Chrift foit avec vous. Amen.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 21, 'Timothée mon compagnon d\'œuvre vous faluë, &amp; Lucius, &amp; Jafon, &amp; Sofipater, mes coufins.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 22, '(Moi Tertius qui ai efté l\'efcrivain de cette Epiftre, vous faluë en noftre Seigneur.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 23, 'Gaius mon hofte, &amp; de toute l\'Eglife, vous faluë : Erafte le procureur de la ville vous faluë, &amp; Quartus noftre frere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 24, 'La grace de noftre Seigneur Jefus Chrift foit avec vous tous. Amen.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 25, 'Or à celui qui eft puiffant pour vous affermir felon mon Evangile, &amp; felon la predication de Jefus-Chrift, fuivant la revelation du myftere qui a efté teu dés le temps jadis ;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 26, 'Mais qui eft maintenant manifefté &amp; donné à connoitre par les Efcritures des Prophetes, fuivant le commandement du Dieu Eternel, afin qu\'il y ait obeïffance de foi parmi toutes les nations ;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 27, 'A Dieu feul fage foit gloire à tout jamais, par Jefus-Chrift. Amen. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 1, 'PAUL appellé à eftre Apoftre de Jefus Chrift, par la volonté de Dieu, &amp; le frere Softhenes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 2, 'A l\'Eglife de Dieu qui eft à Corinthe, aux fanctifiez en Jefus Chrift, qui eftes appellez à eftre faints ; avec tous ceux qui en quelque lieu que ce foit invoquent le nom de Jefus Chrift, Seigneur d\'eux &amp; de nous :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 3, 'Grace vous foit, &amp; paix de par Dieu noftre Pere, &amp; de par le Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 4, 'Je rends toûjours graces à mon dieu à caufe de vous touchant la grace de Dieu qui vous eft donnée en Jefus Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 5, 'De ce qu\'en toutes chofes vous eftes enrichis en lui, en tout don de parole &amp; en toute connoiffance :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 6, 'Selon que le témoignage de Jefus Chrift a efté confirmé en vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 7, 'Tellement qu\'il ne vous defaut aucun don, pendant que vous attendez la manifeftation de noftre Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 8, 'Lequel auffi vous affermira jufques à la fin, pour eftre irreprehenfibles en la journée de noftre Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 9, 'Dieu est fidele, par lequel vous avez efté appellez à la communion de fon Fils Jefus Chrift noftre Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 10, 'Or je vous prie, freres, par le Nom de noftre Seigneur Jefus Chrift, que vous parliez tous un mefme langage, &amp; qu\'il n\'y ait point de partialitez entre vous : mais que vous foyez bien unis en un mefme fens, &amp; en un mefme advis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 11, 'Car, mes freres, il m\'a efté declaré touchant vous par ceux qui font de chez Chloé, qu\'il y a des contentions entre vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 12, 'Or voici ce que je dis, c\'est que chacun de vous dit, De moi je fuis de Paul : Et de moi, d\'Apollos : Et de moi, de Cephas : Et de moi, de Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 13, 'Chrift eft-il divifé ? Paul a-t\'il efté crucifié pour vous ? ou avez-vous efté baptifez au nom de Paul ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 14, 'Je rends graces à Dieu que je n\'ai baptizé aucun d\'entre vous, finon Crifpus, &amp; Gaius :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 15, 'Afin que quelqu\'un ne die point que j\'aye baptizé en mon nom.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 16, 'J\'ai bien auffi baptizé la famille de Stephanas : au furplus je ne fçais fi j\'ai baptizé quelqu\'autre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 17, 'Car Chrift ne m\'a point envoyé pour baptizer, mais pour evangelizer : non point avec fageffe de paroles, afin que la croix de Chrift ne foit point aneantie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 18, 'Car à ceux qui periffent, la parole de la croix leur eft folie : mais à nous qui obtenons le falut, elle eft la vertu de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 19, 'Car il eft efcrit, J\'abolirai la fapience des fages, &amp; aneantirai l\'intelligence des entendus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 20, 'Où est le fage ? où est le fcribe ? où est le difputeur de ce fiecle ? Dieu n\'a-t\'il pas affoli la fapience de ce monde ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 21, 'Car dautant qu\'en la fapience de Dieu, le monde n\'a point connu Dieu par la fapience, le bon plaifir de Dieu a efté de fauver les croyans par la folie de la predication.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 22, 'Puis que les Juifs demandent un figne, &amp; que les Grecs cherchent la fapience.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 23, 'Mais quant à nous, nous prefchons Chrift crucifié, qui eft fcandale aux Juifs, &amp; folie aux Grecs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 24, 'Mais à ceux qui font appellez, tant Juifs que Grecs, nous leur ptrefchons Chrift, la puiffance de Dieu &amp; la fapience de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 25, 'Car la folie de Dieu eft plus fage que les hommes : &amp; la foibleffe de Dieu eft plus forte que les hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 26, 'Car, mes freres, vous voyez voftre vocation, que vous n\'eftes point beaucoup de fages felon la chair, ni beaucoup de forts, ni beaucoup de nobles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 27, 'Mais Dieu a choifi les chofes foles de ce monde, pour rendre confufes les fages : &amp; Dieu a choifi les chofes foibles de ce monde, pour rendre confufes les fortes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 28, 'Et Dieu a choifi les chofes viles de ce monde, &amp; les mefprifées, voire celles qui ne font point, afin d\'abolir celles qui font.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 29, 'Afin que nulle chair ne fe glorifie devant lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 30, 'Or c\'eft de lui que vous eftes en Jefus Chrift, qui vous a efté fait de par Dieu fapience, &amp; juftice, &amp; fanctification, &amp; redemption :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 31, 'Afin que (comme il eft efcrit) celui qui fe glorifie, fe glorifie au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 1, 'ET de moi, freres, quand je fuis venu vers vous, je n\'y fuis point venu avec excellence de bien parler ou de fapience, en vous annonçant le tefmoignage de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 2, 'Car je n\'ai rien propofé de fçavoir entre vous, finon Jefus Chrift, &amp; icelui crucifié.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 3, 'Et j\'ai efté moi-mefme entre vous en foibleffe, en crainte, &amp; en grand tremblement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 4, 'Et ma parole &amp; ma predication n\'a point esté en parole attrayantes de fapience humaine : mais en evidence d\'efprit &amp; de puiffance :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 5, 'Afin que voftre foi ne foit point en la fapience des hommes, mais en la puiffance de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 6, 'Or nous propofons une fapience entre les parfaits, voire une fapience, non point de ce monde, ni des princes de ce fiecles qui s\'en vont à neant :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 7, 'Mais nous propofons la fapience de Dieu qui est en myftere, c\'est à dire, cachée : que Dieu avoit déja devant les fiecles determinée à noftre gloire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 8, 'Laquelle nul des princes de ce fiecle n\'a connuë : car s\'ils l\'euffent connuë, jamais ils n\'euffent crucifié le Seigneur de gloire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 9, 'Mais ainfi qu\'il eft efcrit, Ce font les chofes qu\'œil n\'a point veuës, ni oreille ouïes, &amp; qui ne font point montées au cœur de l\'homme, que Dieu a preparées à ceux qui l\'aiment.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 10, 'Mais Dieu nous les a revelées par fon Efprit. Car l\'Efprit fonde toutes chofes, voire mefmes les chofes profondes de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 11, 'Car qui eft-ce des hommes qui fçache les chofes de l\'homme, finon l\'efprit de l\'homme qui eft en lui ? Pareillement auffi nul n\'a connu les chofes de Dieu finon l\'Efprit de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 12, 'Or nous avons receu non point l\'Efprit de ce monde, mais l\'Efprit qui eft de Dieu : afin que nous connoiffions les chofes qui nous ont efté données de Dieu :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 13, 'Lefquelles auffi nous propofons, non point en paroles qu\'enfeigne la fapience humaine, approprians les chofes fpirituelles à ceux qui font fpirituels.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 14, 'Or l\'homme animal ne comprend point les chofes qui font de l\'Efprit de Dieu : car elles lui font folie, &amp; il ne les peut entendre, dautant qu\'elles fe difcernent fpirituellement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 15, 'Mais le fpirituel, difcerne toutes chofes, &amp; il n\'eft jugé de perfonne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 16, 'Car qui a connu l\'intention du Seigneur, qui le puiffe inftruire ? Mais nous avons l\'intention de Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 1, 'MAIS de moi, freres, je n\'ai pû parler à vous comme à des fpirituels, mais comme à des charnels, comme à des enfans en Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 2, 'Je vous ai point donné de la viande : car vous ne le pouviez encore porter : mefmes maintenant vous ne le pouvez encore, parce que vous eftes encore charnels.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 3, 'Car y ayant entre vous de l\'envie, &amp; des contentions, &amp; des partialitez, n\'eftes-vous pas charnels, &amp; ne cheminez-vous pas à la façon des hommes ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 4, 'Car quand l\'un dit, De moi, je fuis de Paul : &amp; l\'autre, De moi, je fuis d\'Apollos, n\'eftes-vous pas charnels ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 5, 'Qui eft donc Paul, &amp; qui eft Apollos, finon des Miniftres, par lefquels vous avez creu, voire comme le Seigneur a donné à chacun ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 6, 'J\'ai planté, Apollos a arrofé : mais Dieu a donné l\'accroiffement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 7, 'C\'eft pourquoi, ni celui qui plante n\'eft rien, ni celui qui arrofe : mais Dieu qui donne l\'accroiffement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 8, 'Or tant celui qui plante que celui qui arrofe, font une mefme chofe : mais chacun recevra fon propre falaire felon fon labeur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 9, 'Car nous fommes ouvriers avec Dieu : vous eftes le labourage de Dieu, l\'edifice de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 10, 'J\'ai pofé, felon la grace de Dieu qui m\'a efté donnée, le fondement, comme un architecte bien expert : &amp; un autre edifie deffus : mais que chacun regarde comment il edifie deffus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 11, 'Car nul ne peut pofer un autre fondement que celui qui eft pofé, lequel eft Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 12, 'Que fi quelqu\'un edifie fur ce fondement, de l\'or, de l\'argent, des pierres precieufes, du bois, du foin, du chaume :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 13, 'L\'œuvre de chacun fera manifeftée : car le jour la declarera, dautant qu\'elle fera manifeftée par le feu : &amp; le feu efprouvera quelle fera l\'œuvre de chacun.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 14, 'Si l\'œuvre de quelqu\'un qui aura edifié deffus, demeure, il en recevra du falaire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 15, 'Si l\'œuvre de quelqu\'un brufle il en fera perte, mais il fera fauvé quant à lui, toutefois ainfi que par feu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 16, 'Ne fçavez-vous pas que vous eftes le temple de Dieu, &amp; que l\'Efprit de Dieu habite en vous ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 17, 'Si quelqu\'un deftruit le temple de Dieu, Dieu le deftruira : car le temple de Dieu eft faint, lequel vous eftes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 18, 'Que nul ne s\'abufe foi-mefme : fi quelqu\'un d\'entre vous penfe eftre fage en ce monde, qu\'il foit rendu fol, afin qu\'il foit rendu fage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 19, 'Car la fageffe de ce monde eft folie devant Dieu : dautant qu\'il eft efcrit, C\'eft lui qui furprend les fages en leur rufe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 20, 'Et derechef, Le Seigneur connoit les difcours des fages, qu\'ils font vains.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 21, 'Que nul donc ne fe glorifie és hommes ? car toutes chofes font à vous :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 22, 'Soit Paul, foit Apollos, foit Cephas, foit le monde, foit la vie, foit la mort, foit les chofes prefentes, ou les chofes à venir, toutes chofes font à vous, &amp; vous à Chrift, &amp; Chrift à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 23, '');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 1, 'QUE chacun nous tienne comme Miniftres de Chrift, &amp; difpenfateurs des myfteres de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 2, 'Mais au refte, il eft requis entre les difpenfateurs, que chacun foit trouvé fidele.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 3, 'Pour moi je me foucie peu d\'eftre jugé de vous, ou de jugement d\'homme : mefmes auffi je ne me juge point moi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 4, 'Car je ne me fens en rien coulpable, mais pour cela je ne fuis point juftifié : mais celui qui me juge, c\'eft le Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 5, 'C\'eft pourquoi ne jugez de rien avant le temps, jufques à ce que le Seigneur vienne, lequel auffi mettra en lumiere les chofes cachées des tenebres, &amp; manifeftera les confeils des cœurs : &amp; alors fera renduë à chacun fa loüange de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 6, 'Or, mes freres, j\'ai tourné ces propos par une façon de parler, fur moi &amp; fur Apollos, à caufe de vous : afin que vous appreniez en nous, que perfonne ne prefume outre ce qui eft efcrit : à ce que l\'un pour l\'autre vous ne vous enfliez point contre autrui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 7, 'Car qui eft-ce qui met la difference entre toi &amp; un autre, &amp; qu\'eft-ce que tu as, que tu ne l\'ayes receu ? &amp; fi tu l\'as receu, pourquoi t\'en glorifies-tu, comme fi tu ne l\'avois point receu ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 8, 'Vous eftes déja foulez, vous eftes déja enrichis, vous eftes faits Rois fans nous : &amp; à la mienne volonté que vous regnaffiez, afin que nous regnaffions auffi avec vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 9, 'Car je penfe que Dieu nous a mis en montre, nous qui fommes les derniers Apoftres, comme des gens condamnez à la mort, veu que nous fommes rendus le fpectacle du monde, &amp; des Anges, &amp; des hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 10, 'Nous fommes fols pour l\'amour de Chrift, mais vous eftes fages en Chrift : nous fommes debiles, &amp; vous eftes forts : vous estes honorables, &amp; nous fans honneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 11, 'Jufques à cette heure nous fouffrons &amp; faim &amp; foif, &amp; fommes nuds, &amp; fommes fouffletez, &amp; fommes errans çà &amp; là :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 12, 'Et travaillons ouvrans de nos propres mains : on dit mal de nous, &amp; nous beniffons : nous fommes perfecutez, &amp; nous l\'endurons :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 13, 'Nous fommes faits comme les ballieures du monde, &amp; comme la raclure de tous, jufques à maintenant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 14, 'Je n\'efcris point ces chofes pour vous faire honte : mais je vous admonefte comme mes chers enfans :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 15, 'Car quand vous auriez dix mille pedagogues en Chrift, neantmoins vous n\'avez pas plufieurs peres, car c\'eft moi qui vous ai engendrez en Jefus Chrift par l\'Evangile.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 16, 'Je vous prie donc que vous foyez mes imitateurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 17, 'Pour cette caufe je vous ai envoyé Timothée, qui eft mon fils bien-aimé &amp; fidele au Seigneur : lequel vous ramentevra mes voyes en Chrift, comme j\'enfeigne par tout en chaque Eglife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 18, 'Or quelques-uns fe font enflez comme fi je ne devois point venir vers vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 19, 'Mais je viendrai bien-toft vers vous, fi le Seigneur le veut : &amp; connoiftrai non point la parole de ceux qui fe font enflez, mais la vertu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 20, 'Car le royaume de Dieu ne confifte point en paroles, mais en vertu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 21, 'Que voulez-vous ? Viendrai-je à vous avec la verge, ou bien en charité &amp; efprit de douceur ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 1, 'ON oit entierement affeurer qu\'il y a entre vous de la paillardife, voire une telle paillardife, qu\'entre les Gentils il n\'eft point fait mention de femblable : c\'est que quelqu\'un entretient la femme de fon pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 2, 'Et vous eftes encore enflez, &amp; n\'avez point pluftoft mené deuil, afin que celui qui a commis cét acte fuft ofté du milieu de vous ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 3, 'Moi certes, comme abfent de corps, mais prefent d\'efprit ai déja arrefté comme prefent, touchant celui qui a commis un tel acte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 4, '(Vous &amp; mon efprit eftant affemblez au Nom de noftre Seigneur Jefus Chrift, avec la puiffance de noftre Seigneur Jefus Chrift.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 5, 'Qu\'un tel foit livré à Satan : à la deftruction de la chair : afin que l\'efprit foit fauvé au jour du Seigneur Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 6, 'Voftre vanterie n\'eft pas bonne : ne fçavez-vous pas bien qu\'un peu de levain fait lever toute la pafte ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 7, 'Repurgez donc le vieil levain, afin que vous foyez une nouvelle pafte ; comme vous eftes fans levain : car Chrift noftre Pafque a efté facrifié pour nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 8, 'C\'eft pourquoi faifons la fefte, non point avec du vieil levain, ni avec du levain de mauvaiftié &amp; de malice, mais avec des pains fans levain de fincerité &amp; de verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 9, 'Je vous ai efcrit par lettres, que vous ne vous entremefliez point avec les paillards :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 10, 'Mais non abfolument avec les paillards de ce monde, ou avaricieux, ou raviffeurs, ou idolatres : car autrement certes il vous faudroit fortir du monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 11, 'Or maintenant, je vous efcris que vous ne vous y entremeftiez point : c\'eft que fi quelqu\'un qui fe nomme frere, eft paillard, ou avaricieux, ou idolatre, ou mefdifant, ou yvrogne, ou raviffeur, vous ne mangiez pas mefmes avec un tel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 12, 'Car qu\'ai-je à faire de juger auffi de ceux qui font de dehors ? Ne jugez-vous pas de ceux qui font de dedans ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 13, 'Mais Dieu juge ceux qui font de dehors. Oftez donc d\'entre vous mefme le méchant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 1, 'QUAND quelqu\'un d\'entre vous a quelque affaire contre un autre, eft-il bien fi hardi que d\'aller en jugement par devant les iniques, &amp; non point par devant les Saints ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 2, 'Ne fçavez-vous pas que les Saints jugeront le monde ? &amp; fi le monde eft jugé par vous, eftes-vous indignes de juger des plus petites chofes ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 3, 'Ne fçavez-vous pas que nous jugerons les Anges ? Combien plus des chofes appartenantes à cette vie ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 4, 'Si donc vous avez des procés touchant les affaires de cette vie, eftabliffez au fiege ceux qui font de moindre eftime en l\'Eglife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 5, 'Je le dis à voftre honte : eft-il ainfi qu\'il n\'y ait point de fage entre vous, non pas mefmes un feul, qui puiffe juger entre fes freres ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 6, 'Mais un frere a des procés contre fon frere, &amp; cela par devant les infideles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 7, 'Déja certes il y a entierement du defaut en vous, de ce que vous avez des procés entre vous. Pourquoi n\'endurez vous pas pluftoft que l\'on vous face tort ? Pourquoi ne recevez-vous pas pluftoft du dommage ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 8, 'Mais vous faites tort, &amp; endommagez, &amp; cela encore à l\'endroit de vos freres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 9, 'Ne fçavez-vous pas que les injuftes n\'heriteront point le royaume de Dieu ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 10, 'Ne vous abufez point : ni les paillards, ni les idolatres, ni les adulteres, ni les effeminez, ni ceux qui habitent avec les mafles, ni les larrons, ni les avaricieux, ni les yvrognes, ni les médifans, ni les raviffeurs n\'heriteront point le royaume de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 11, 'Et vous eftiez telles chofes quelques-uns : mais vous en avez efté lavez, mais vous en avez efté fanctifiez, mais vous en avez efté juftifiez au Nom du Seigneur Jefus, &amp; par l\'Efprit de noftre Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 12, 'Toutes chofes me font licites, mais toutes chofes ne font pas expedientes : toutes chofes me font licites, mais je ne ferai point affujetti fous la puiffance d\'aucune chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 13, 'Les viandes font pour le ventre, &amp; le ventre pour les viandes : mais Dieu deftruira &amp; lui &amp; elles. Or le corps n\'eft point pour la paillardife, mais pour le Seigneur, &amp; le Seigneur pour le corps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 14, 'Or Dieu a reffufcité le Seigneur, &amp; nous reffufcitera auffi par fa puiffance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 15, 'Ne fçavez-vous pas que vos corps font les membres de Chrift ? Ofterai-je donc les membres de Chrift, pour en faire les membres d\'une paillarde ? Ainfi n\'advienne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 16, 'Ne fçavez-vous pas que celui qui s\'adjoint à une paillarde, eft fait un mefme corps avec elle ? Car deux (dit-il) feront une mefme chair.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 17, 'Mais celui qui eft adjoint au Seigneur, eft un mefme Efprit avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 18, 'Fuyez la paillardife : car quelque peché que l\'homme commette il eft hors du corps : mais celui qui paillarde, peche contre fon propre corps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 19, 'Ne fçavez-vous pas que voftre corps eft le temple du Saint Efprit qui eft en vous, lequel vous avez de Dieu, &amp; que vous n\'eftes point à vous mefmes ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 20, 'Car vous avez efté achetez par prix. Glorifiez donc Dieu en voftre corps &amp; en voftre efprit, lefquels appartiennent à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 1, 'Or touchant les poincts dont vous m\'avez efcrit, il eft bon à l\'homme de ne point toucher à la femme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 2, 'Toutefois pour éviter la paillardife, que chacun ait fa femme, &amp; que chacune ait fon mari.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 3, 'Que le mari rende la bienvueillance deuë à la femme : femblablement auffi la femme au mari.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 4, 'La femme n\'a point la puiffance de fon propre corps, mais le mari : femblablement auffi le mari n\'a point la puiffance de fon propre corps, mais la femme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 5, 'Ne vous fraudez point l\'un l\'autre, fi ce n\'eft par un confentement mutuel pour un temps, afin que vous vacquiez au jeufne &amp; à l\'oraifon : &amp; derechef retournez enfemble, afin que Satan ne vous tente point à caufe de voftre incontinence.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 6, 'Or je dis ceci par permiffion, non point par commandement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 7, 'Car je voudrois que tous hommes fuffent comme moi : mais chacun a fon propre don de Dieu, l\'un en une maniere, &amp; l\'autre en une autre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 8, 'Or je dis à ceux qui ne font point mariez, &amp; aux veuves, qu\'il leur eft bon s\'ils demeurent comme moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 9, 'Mais s\'ils ne fe contiennent, qu\'ils fe marient : car il vaut mieux fe marier que de brufler.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 10, 'Et quant aux mariez, je leur commande, non point moi, mais le Seigneur, Que la femme ne fe departe point du mari.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 11, 'Et fi elle s\'en depart, qu\'elle demeure fans eftre mariée, ou qu\'elle fe reconcilie au mari. Auffi que le mari ne delaiffe point fa femme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 12, 'Mais aux autres je leur dis, non point le Seigneur, Si quelque frere a une femme infidele, &amp; qu\'elle confente d\'habiter avec lui, qu\'il ne la delaiffe point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 13, 'Et fi quelque femme a un mari infidele, &amp; qu\'il confente d\'habiter avec elle, qu\'elle ne le delaiffe point non plus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 14, 'Car le mari infidele eft fanctifié en la femme : &amp; la femme infidele eft fanctifiée au mari : autrement vos enfans feroyent pollus : or maintenant ils font faints.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 15, 'Que fi l\'infidele fe depart, qu\'il fe departe : car le frere ou la fœur ne font point affervis en tel cas : mais Dieu nous a appellez à la paix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 16, 'Car que fçais-tu femme, fi tu fauveras ton mari ? Ou que fçais-tu mari, fi tu fauveras ta femme ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 17, 'Toutefois que chacun chemine ainfi que Dieu lui a departi, voire chacun comme le Seigneur l\'a appellé. Et c\'eft ainfi que j\'en ordonne en toutes les Eglifes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 18, 'Quelqu\'un eft-il appellé eftant circoncis ? Qu\'il ne ramene point le prepuce : quelqu\'un eft-il appellé eftant au prepuce ? Qu\'il ne foit point circoncis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 19, 'La Circoncifion n\'eft rien, &amp; le prepuce n\'eft rien, mais l\'obfervation des commandemens de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 20, 'Que chacun fe tienne en la vocation en laquelle il eft appellé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 21, 'Es-tu appellé eftant ferf ? Ne t\'en foucie point : mais fi tu peux auffi eftre mis en liberté, ufes-en pluftoft.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 22, 'Car le ferf qui eft appellé en noftre Seigneur, eft l\'affranchi de noftre Seigneur : femblablement auffi celui qui eft appellé eftant franc, eft ferf de Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 23, 'Vous eftes achetez par prix, ne devenez point ferfs des hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 24, 'Freres, que chacun fe tienne envers Dieu en ce en quoi il eft appellé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 25, 'Or touchant les vierges, je n\'ai point de commandement du Seigneur, mais j\'en donne advis comme ayant obtenu mifericorde du Seigneur pour eftre fidele.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 26, 'J\'eftime donc que cela eft bon pour la neceffité prefente, entant qu\'il eft bon à l\'homme d\'eftre ainfi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 27, 'Es-tu lié à une femme ? Ne cherche point de feparation : es-tu delivré de femme ? Ne cherche point de femme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 28, 'Que fi tu te maries, tu n\'as point peché : &amp; fi la vierge fe marie, elle n\'a point peché. Toutefois ceux qui font tels auront de la tribulation en la chair : or je vous efpargne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 29, 'Mais je vous dis ceci, mes freres, que le temps eft racourci : il refte que ceux qui ont femmes foyent comme n\'en ayans point :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 30, 'Et ceux qui font en pleurs, comme s\'ils n\'eftoyent point en pleurs : &amp; ceux qui font en joye, comme s\'ils n\'eftoyent point en joye : &amp; ceux qui achetent, comme ne poffedans point :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 31, 'Et ceux qui ufent de ce monde, comme n\'en abufans point : car la figure de ce monde paffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 32, 'Or je voudrois que vous fuffiez fans folicitude. Celui qui n\'eft point marié a foin des chofes qui font du Seigneur, comme il plaira au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 33, 'Mais celui qui eft marié, a foin des chofes de ce monde, comment il plaira à fa femme, &amp; eft divifé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 34, 'La femme qui n\'eft point mariée &amp; la vierge, a foin des chofes qui font du Seigneur, pour eftre fainte de corps &amp; d\'efprit : mais celle qui eft mariée, a foin des chofes qui font du monde, comment elle plaira à fon mari.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 35, 'Or je dis ceci ayant efgard à ce qui vous eft expedient, non point pour vous enlacer, mais tendant à ce qui eft bien-feant : &amp; propre à adherer au Seigneur fans aucune diftraction.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 36, 'Mais fi quelqu\'un penfe que ce foit deshonorer fa vierge, qu\'elle paffe la fleur de fon âge, &amp; qu\'il faille qu\'il foit fait ainfi, qu\'un tel faffe ce qu\'il veut, il ne peche point : qu\'elles foyent mariées.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 37, 'Mais celui qui demeure ferme en fon cœur, &amp; n\'a point de neceffité, mais a puiffance fur fa propre volonté, &amp; a arrefté cela en fon cœur de garder fa vierge, il fait bien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 38, 'Celui donc qui marie fa vierge, fait bien : mais celui qui ne la marie point, fait mieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 39, 'La femme eft liée par la Loi tout le temps que fon mari vit : mais fi fon mari meurt, elle eft en liberté de fe remarier à qui elle veut ; feulement que ce foit au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 40, 'Toutefois elle eft plus heureufe fi elle demeure ainfi, felon mon avis. Or j\'eftime auffi que j\'ai l\'efprit de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 1, 'TOUCHANT les chofes qui font facrifiées aux idoles, nous fçavons que nous avons tous la connoiffance : la connoiffance enfle ; mais la charité edifie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 2, 'Mais fi quelqu\'un penfe fçavoir quelque chofe, il n\'a encore rien connu comme il faut connoitre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 3, 'Mais fi quelqu\'un aime Dieu, il eft connu de lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 4, 'Ainfi donc quant au manger des chofes facrifiées aux idoles, nous fçavons que l\'idole n\'eft rien au monde, &amp; qu\'il n\'y a aucun autre Dieu qu\'un feul.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 5, 'Car encore qu\'il y en ait qui foyent appellez Dieux, foit au ciel, foit en terre, (comme il y a plufieurs Dieux, &amp; plufieurs Seigneurs.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 6, 'Toutefois nous n\'avons qu\'un feul Dieu, qui eft le Pere duquel font toutes chofes, &amp; nous pour lui : &amp; un feul Seigneur Jefus Chrift, par lequel font toutes chofes, &amp; nous par lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 7, 'Mais il n\'y a pas de la connoiffance en tous : car quelques-uns en mangent avec confcience de l\'idole, jufques à maintenant, comme des chofes facrifiées à l\'idole : &amp; leur confcience eftant debile, elle eft poluée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 8, 'Or la viande ne nous rend pas plus agreables à Dieu : car fi nous mangeons, &amp; fi nous ne mangeons point, nous n\'en avons pas moins.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 9, 'Mais prenez garde que cette puiffance que vous avez, ne foit en quelque forte en fcandale aux infirmes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 10, 'Car fi quelqu\'un te voit, toi qui as de la connoiffance, eftre à table au temple des idoles, al confcience de celui qui eft foible, ne fera-t\'elle pas induite à manger des chofes facrifiées à l\'idole ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 11, 'Et ainfi ton frere qui eft foible, pour lequel Chrift eft mort, perira par ta connoiffance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 12, 'Or quand vous pechez ainfi contre vos freres, &amp; navrez leur confcience qui eft debile, vous pechez contre Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 13, 'Pour cette caufe, fi la viande fcandalize mon frere, je ne mangerai jamais de chair, afin que je ne fcandalize point mon frere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 1, 'Ne fuis-je pas Apoftre ? Ne fuis-je pas libre ? N\'ai-je pas veu noftre Seigneur Jefus Chrift ? N\'eftes-vous pas mon ouvrage au Seigneur ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 2, 'Si je fuis Apôtre aux autres, au moins le fuis-je à vous : car vous eftes le feau de mon Apoftolat au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 3, 'Telle eft ma defenfe envers ceux qui me controllent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 4, 'N\'avons-nous pas la puiffance de manger &amp; de boire ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 5, 'N\'avons-nous pas la puiffance de mener avec nous une femme fœur, ainfi auffi que les autres Apoftres, &amp; les freres du Seigneur, &amp; Cephas ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 6, 'Ou, moi feul &amp; Barnabas n\'avons-nous pas la puiffance de ne travailler point ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 7, 'Qui eft-ce qui va jamais à la guerre à fa folde ? Qui eft-ce qui plante la vigne, &amp; n\'en mange point de fruit ? Qui eft-ce qui paift le troupeau, &amp; ne mange point du laict du troupeau ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 8, 'Dis-je, ces chofes felon l\'homme ? La Loi ne dit-elle point auffi le mefme ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 9, 'Car il eft écrit en al Loi de Moyfe, Tu n\'emmufeleras point le bœuf qui foule le grain. Dieu a-t\'il foin des bœufs ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 10, 'Ne dit-il point entierement ces chofes pour nous ? Certes elles font écrites pour nous : car celui qui laboure, doit labourer fous efperance, &amp; celui qui foule le blé, fous efperance d\'en eftre participant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 11, 'Si nous vous avons femé les chofes fpirituelles, eft-ce fi grand cas que nous recueillions les voftres charnelles ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 12, 'Si les autres font participans de cette puiffance fur nous, pourquoi non pluftoft nous ? Toutefois nous n\'avons point ufé de cette puiffance : mais nous fupportons tout, afin que nous ne donnions aucun empefchement à l\'Evangile de Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 13, 'Ne fçavez-vous pas que ceux qui vacquent aux chofes facrées, mangent de ce qui eft facré ? &amp; que ceux qui fervent à l\'autel, participent à l\'autel ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 14, 'De mefme auffi le Seigneur a ordonné que ceux qui annoncent l\'Evangile, vivent de l\'Evangile.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 15, 'Toutefois je n\'ai ufé de pas une de ces chofes. Or je n\'ai point écrit ceci, afin qu\'il en foit fait ainfi en mon endroit : car il m\'eft bon de mourir pluftoft, que fi quelqu\'un aneantiffoit ma gloire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 16, 'Car encore que j\'evangelize, je n\'ai pas dequoi me glorifier : parce que la neceffité m\'en eft impofée : &amp; malheur eft fur moi, fi je n\'evangelize.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 17, 'Que fi je le fais volontiers, j\'en ai du falaire : mais fi je le fais à contrecœur, toutefois la difpenfation m\'en eft commife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 18, 'Quel falaire donc en ai-je ? C\'eft qu\'en prefchant l\'Evangile, je faffe que l\'Evangile de Chrift n\'apporte point de defpence, afin que je n\'abufe point de ma puiffance en l\'Evangile.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 19, 'Car bien que je fois en liberté à l\'endroit de tous, je me fuis affervi à tous, afin de gagner plus de perfonnes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 20, 'Et je me fuis fait aux Juifs comme Juif, afin de gagner les Juifs : à ceux qui font fous la Loi, comme fi j\'eftois fous la Loi, afin de gagner ceux qui font fous la Loi :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 21, 'A ceux qui font fans Loi, comme fi j\'eftois fans Loi, (bien que je ne fois point fans Loi quant à Dieu : mais fous la Loi de Chrift :) afin de gagner ceux qui font fans Loi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 22, 'Je me fuis fait comme foible aux foibles, afin de gagner les foibles : je me fuis fait toutes chofes à tous, afin qu\'abfolument j\'en fauve quelques-uns.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 23, 'Et je fais cela à caufe de l\'Evangile, afin que j\'en fois fait auffi participant avec les autres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 24, 'Ne fçavez-vous pas que quand on court en la lice tous courent bien, mais un feul emporte le prix ? Courez tellement que vous l\'emportiez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 25, 'Or quiconque luite, vit entierement par regime : &amp; quand à ceux-là ils le font pour avoir une couronne corruptible : mais nous, une incorruptible.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 26, 'Je cours donc, non pas fans fçavoir comment : je combats, non point comme battant l\'air.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 27, 'Mais je matte &amp; reduis mon corps en fervitude, de peur qu\'en quelque maniere, apres avoir prefché aux autres, je ne fois trouvé moi-mefme non recevable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 1, 'OR, freres, je ne veux pas que vous ignoriez que nos Peres ont tous efté fous la nuée, &amp; ont tous paffé par la mer :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 2, 'Et ont tous efté baptifez en Moyfe en la nuée &amp; en la mer :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 3, 'Et ont tous mangé d\'une mefme viande fpirituelle :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 4, 'Et ont tous beu d\'un mefme breuvage fpirituel : car ils beuvoient de la pierre fpirituelle qui les fuivoit, &amp; la pierre eftoit Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 5, 'Mais Dieu n\'a point pris plaifir en plufieurs d\'eux : car ils ont efté accablez au defert.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 6, 'Or ces chofes ont efté des exemples pour nous, afin que nous ne foyons point convoiteux des chofes mauvaifes, comme eux auffi ont convoité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 7, 'Et que vous ne deveniez point idolatres, comme quelques-uns d\'entr\'eux : ainfi qu\'il eft efcrit, Le peuple s\'eft affis pour manger &amp; pour boire, puis ils fe font levez pour joüer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 8, 'Et que nous ne paillardions point, comme quelques-uns d\'entreux ont paillardé, &amp; font tombez en un jour vingt &amp; trois mille.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 9, 'Et que nous ne tentions point Chrift, comme auffi quelques-uns d\'entr\'eux l\'ont tenté, &amp; ont efté deftruits par les ferpens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 10, 'Et que vous ne murmuriez point, comme auffi quelques-uns d\'entr\'eux ont murmuré, &amp; font peris par le deftructeur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 11, 'Or toutes ces chofes leur advenoient en exemple, &amp; font écrites pour nous admonefter, comme ceux aufquels les derniers temps font parvenus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 12, 'C\'eft pourquoi que celui qui eftime eftre debout, regarde, qu\'il ne tombe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 13, 'Tentation ne vous a point faifis finon humaine. Or Dieu eft fidele, lequel ne permettra point que vous foyez tentez outre ce que vous pouvez : mais il donnera avec la tentation l\'iffuë, afin que vous la puiffiez fouftenir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 14, 'Partant mes bien-aimez, fuyez arriere de l\'idolatrie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 15, 'Je parle comme à ceux qui font entendus : Jugez vous-mefmes de ce que je dis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 16, 'La coupe de benediction, laquelle nous beniffons, n\'eft-elle pas la communion du fang de Chrift ? &amp; le pain que nous rompons, n\'eft-il pas la communion du corps de Chrift ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 17, 'Dautant que nous qui fommes plufieurs, fommes un feul pain &amp; un feul corps. Car nous fommes tous participans d\'un mefme pain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 18, 'Voyez l\'Ifraël qui est felon la chair : ceux qui mangent les facrifices, ne font-ils pas participans de l\'autel ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 19, 'Que dis-je donc ? Que l\'idole foit quelque chofe ? Ou que ce qui eft facrifié à l\'idole, foit quelque chofe ? Non.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 20, 'Mais je dis que les chofes que les Gentils facrifient, ils les facrifient aux idoles, &amp; non point à Dieu : or je ne veux point que vous foyez participans des diables.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 21, 'Vous ne pouvez boire la coupe du Seigneur, &amp; la coupe des diables : vous ne pouvez eftre participans de la table du Seigneur, &amp; de la table des diables.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 22, 'Voulons-nous provoquer le Seigneur à jaloufie ? Sommes-nous plus forts que lui ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 23, 'Toutes chofes me font licites, mais toutes chofes ne font pas expedientes : toutes chofes me font licites, mais toutes chofes n\'edifient pas .');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 24, 'Que perfonne ne cherche ce qui lui eft propre, mais que chacun cherche ce qui eft pour autrui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 25, 'Mangez de tout ce qui fe vend à la boucherie, fans vous en enquerir pour la confcience.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 26, 'Car la terre eft au Seigneur, &amp; le contenu en elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 27, 'Que fi quelqu\'un des infideles vous convie, &amp; vous y voulez aller, mangez de tout ce qui eft mis devant vous, fans vous en enquerir pour la confcience.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 28, 'Mais fi quelqu\'un vous dit, Cela eft facrifié aux idoles, n\'en mangez point, à caufe de celui-là qui vous en a advertis, &amp; à caufe de la confcience : car la terre eft au Seigneur, &amp; le contenu en elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 29, 'Or je dis la confcience, non point la tienne, mais celle de l\'autre : car pourquoi ma liberté eft-elle jugée par la confcience d\'autrui ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 30, 'Et fi par grace j\'en puis eftre participant, pourquoi fuis-je blafmé en ce dequoi je rens graces ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 31, 'Soit donc que vous mangiez, foit que vous beuviez, ou que vous faffiez quelque autre chofe, faites toutes chofes à la gloire de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 32, 'Soyez tels que vous ne bailliez aucun achoppement ni aux Juifs, ni aux Grecs, ni à l\'Eglife de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 33, 'Comme auffi je complais à tous en toutes chofes, ne cherchant point ma commodité propre, mais celle de plufieurs, afin qu\'ils foient fauvez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 1, 'SOYEZ mes imitateurs, comme auffi je le fuis de Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 2, 'Or, freres, je vous louë de ce que vous avez fouvenance de tout ce qui eft de moi, &amp; que vous gardez mes ordonnances comme je vous les ai baillées.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 3, 'Mais je veux que vous fçachiez que le chef de tout homme, c\'eft Chrift : &amp; le chef de la femme, c\'eft l\'homme : &amp; le chef de Chrift c\'eft Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 4, 'Tout homme priant, ou prophetizant, en ayant quelque chofe fur la tefte, deshonore fon chef :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 5, 'Mais toute femme priant ou prophetizant fans avoir la tefte couverte, deshonore fon chef : car c\'eft tout un comme fi elle eftoit rafée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 6, 'Partant fi la femme n\'eft point couverte, qu\'elle foit mefmes tonduë : or s\'il eft deshonnefte à la femme d\'eftre tonduë ou d\'eftre rafée, qu\'elle foit couverte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 7, 'Car quant à l\'homme, il ne doit point couvrir fa tefte, veu qu\'il eft l\'image &amp; la gloire de Dieu : mais la femme eft la gloire de l\'homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 8, 'Car l\'homme n\'eft point de la femme, mais la femme eft de l\'homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 9, 'Car auffi l\'homme n\'a pas efté creé pour la femme, mais la femme pour l\'homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 10, 'C\'eft pourquoi la femme, à caufe des Anges, doit avoir fur fa tefte une marque qu\'elle eft fous puiffance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 11, 'Toutefois ni l\'homme n\'eft point fans la femme, ni la femme fans l\'homme en nostre Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 12, 'Car comme la femme est de l\'homme, femblablement l\'homme eft par la femme : mais toutes chofes font de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 13, 'Jugez-en entre vous-mefmes : eft-il convenable que la femme prie Dieu fans eftre couverte ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 14, 'La nature mefme ne vous enfeigne-t\'elle pas que fi l\'homme nourrit fa chevelure, ce lui eft du deshonneur ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 15, 'Mais fi la femme nourrit fa chevelure ce lui eft de la gloire, parce que fa chevelure lui eft baillée pour couverture.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 16, 'Que s\'il y a quelqu\'un qui penfe eftre contentieux, nous n\'avons pas une telle couftume, ni auffi les Eglifes de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 17, 'Or en ceci que je vous declare, je ne vous louë point : c\'eft que vous vous affemblez non point en mieux, mais en pis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 18, 'Car premierement, quand vous vous affemblez en l\'Eglife, j\'entens qu\'il y a des partialitez entre vous : &amp; j\'en crois quelque partie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 19, 'Car il faut qu\'il y ait mefmes des herefies entre vous, afin que ceux qui font de mife foyent manifeftez entre vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 20, 'Quand donc vous vous affemblez enfemble, cela n\'eft point manger la Cene du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 21, 'Car chacun s\'avance de prendre fon fouper participer quand ce vient à manger : &amp; l\'un a faim, &amp; l\'autre fait bonne chere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 22, 'N\'avez-vous point donc de maifons pour manger &amp; pour boire ? Mefprifez-vous l\'Eglife de Dieu ? &amp; faites-vous honte à ceux qui n\'ont pas de quoi ? Que vous dirai-je ? Vous louërai-je ? Je ne vous louë point en ceci.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 23, 'Car j\'ai receu du Seigneur ce qu\'auffi je vous ai baillé : c\'eft que le Seigneur Jefus, en la nuit en laquelle il fut trahi, prit du pain :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 24, 'Et ayant rendu graces le rompit, &amp; dit, Prenez, mangez, ceci eft mon corps, qui eft rompu pour vous : faites ceci en commemoration de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 25, 'Semblablement auffi apres le fouper il prit la coupe, difant, Cette coupe eft la nouvelle alliance en mon fang : faites ceci toutefois &amp; quantes que vous en boirez en commemoration de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 26, 'Car toutefois &amp; quantes que vous mangerez de ce pain, &amp; que vous boirez de cette coupe, vous annoncerez la mort du Seigneur, jufques à ce qu\'il vienne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 27, 'C\'eft pourquoi quiconque mangera de ce pain, ou boira de la coupe du Seigneur indignement, fera coulpable du corps &amp; du fang du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 28, 'Que chacun donc s\'efprouve foi-mefme, &amp; ainfi qu\'il mange de ce pain, &amp; qu\'il boive de cette coupe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 29, 'Car celui qui en mange &amp; qui en boit indignement, mange &amp; boit fon jugement, ne difcernant point le corps du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 30, 'Pour cette caufe plufieurs font foibles &amp; malades entre vous, &amp; plufieurs dorment.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 31, 'Car certes fi nous nous jugions nous-mefmes, nous ne ferions point jugez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 32, 'Mais quand nous fommes jugez, nous fommes enfeignez par le Seigneur, afin que nous ne foyons point condamnez avec le monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 33, 'Partant mes freres, quand vous vous affemblez pour manger, attendez vous l\'un l\'autre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 34, 'Que fi quelqu\'un a faim, qu\'il mange en la maifon, afin que vous ne vous affembliez point en jugement. Touchant les autres points, j\'en ordonnerai quand je ferai venu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 1, 'OR touchant les dons fpirituels, je ne veux point, freres, que vous foyez ignorans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 2, 'Vous fçavez comme vous eftiez Gentils, qui eftiez tranfportez apres les idoles muettes, felon que vous eftiez menez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 3, 'Pour ce je vous fais fçavoir, que nul parlant par l\'Efprit de Dieu, ne dire Jefus eft malediction : &amp; nul ne peut dire que Jefus eft Seigneur, finon par le Saint Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 4, 'Or il y a diverfité de dons : mais il y a un mefme Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 5, 'Il y a auffi diverfité d\'adminifrations : mais il y a un mefme Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 6, 'Il y a pareillement diverfité d\'operations : mais il y a un mefme Dieu, qui opere toutes chofes en tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 7, 'Mais à chacun eft donnée la manifeftation de l\'Efprit pour ce qui eft expedient.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 8, 'Car à l\'un eft donnée par l\'Efprit, la parole de fapience : &amp; à l\'autre, felon le mefme Efprit, la parole de connoiffance :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 9, 'Et à l\'autre, la foi en ce mefme Efprit : &amp; à l\'autre, des dons de guerifons en ce mefme Efprit :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 10, 'Et à l\'autre, des operations de vertus : &amp; à l\'autre, la prophetie : &amp; à l\'autre, le don de difcerner les efprits : &amp; à l\'autre, la diverfité de langages : &amp; à l\'autre, le don d\'interpreter les langages.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 11, 'Mais ce feul &amp; mefme Efprit fait toutes ces chofes, diftribuant particulierement à chacun felon qu\'il veut.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 12, 'Car comme le corps eft un, &amp; a plufieurs membres : mais tous les membres de ce corps, qui eft un, encore qu\'ils foient plufieurs, font un corps : en telle maniere auffi est Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 13, 'Car nous avons tous efté baptizez en un mefme Efprit pour eftre un mefme corps : foit Juifs, foit Grecs, foit ferfs, foit francs : &amp; avons tous efté abbruvez d\'un mefme Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 14, 'Car auffi le corps n\'eft point un feul membre, mais plufieurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 15, 'Si le pied dit, Dautant que je ne fuis point la main, je ne fuis point du corps, n\'eft-il point du corps pourtant ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 16, 'Et fi l\'oreille dit, d\'autant que je ne fuis point l\'œil, je ne fuis point du corps, n\'eft-elle point du corps pourtant ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 17, 'Si tout le corps eft l\'œil, où fera l\'oüie ? Si tout est l\'oüie, où fera le flair ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 18, 'Mais maintenant Dieu a pofé chaque membre au corps, ainfi qu\'il a voulu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 19, 'Que fi tous eftoient un feul membre, où feroit le corps ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 20, 'Mais maintenant il y a plufieurs membres : toutefois il n\'y a qu\'un feul corps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 21, 'Et l\'œil ne peut dire à la main, Je n\'ai que faire de toi : ni auffi la tefte aux pieds, Je n\'ai que faire de vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 22, 'Et qui plus eft, les membres du corps qui femblent eftre les plus debiles, font beaucoup plus neceffaires.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 23, 'Et ceux que nous eftimons eftre les moins honorables au corps, font accouftrez par nous plus foigneufement : &amp; les parties qui font en nous les moins belles à voir, ont le plus de parement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 24, 'Et les parties qui font belles en nous, n\'en ont point de befoin : mais Dieu a temperé le corps enfemble, donnant plus d\'honneur à celui qui en avoit befoin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 25, 'Afin qu\'il n\'y ait point de divifion au corps : mais que les membres ayent un foin mutuel les uns pour les autres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 26, 'Et foit que l\'un des membres fouffre quelque chofes, tous les membres fouffrent avec lui : ou foit que l\'un des membres foit honoré, tous les membres enfemble s\'en éjoüiffent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 27, 'Or vous eftes le corps de Chrift, &amp; fes membres, chacun en fon endroit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 28, 'Et Dieu en a mis les uns en l\'Eglife, premierement Apoftres, fecondement Prophetes, tiercement Docteurs, &amp; puis les vertus : confequemment les de guerifons, les fecours, les gouvernemens, les diverfitez de langages.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 29, 'Tous font-ils Apoftres ? Tous font-ils Prophetes ? Tous font-ils Docteurs ? Tous font-ils ayans des vertus ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 30, 'Tous ont-ils les dons de guerifons ? Tous parlent-ils des langages ? Tous interpretent-ils ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 31, 'Mais foyez convoiteux des plus excellens donc : &amp; je m\'en vais vous en montrer encore un chemin qui furpaffe de beaucoup.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 1, 'QUAND je parlerois les langages des hommes, voire des Anges, &amp; que je n\'aye point de charité, je fuis comme l\'airain qui refonne, ou comme la cymbale qui tinte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 2, 'Et quand bien j\'aurois le don de Prophetie, &amp; connoitrois tous les fecrets &amp; toute fcience : &amp; quand j\'aurois toute la foi, tellement que je tranfportaffe les montagnes, &amp; que je n\'aye point de charité, je ne fuis rien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 3, 'Et quand mefmes je diftribuërois tout mon bien à la nourriture des pauvres, &amp; quand mefmes je livrerois mon corps pour eftre bruflé, &amp; que je n\'aye point de charité, cela ne me profite en rien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 4, 'La charité eft d\'un efprit patient, elle fe montre benigne : la charité n\'eft point envieufe : la charité n\'ufe point d\'infolence : elle ne s\'enfle point :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 5, 'elle ne fe porte point deshonneftement : elle ne cherche point fon propre profit : elle n\'eft point dépiteufe : elle ne penfe point à mal.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 6, 'Elle ne s\'éjoüit point de l\'injuftice : mais elle s\'éjoüit de la verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 7, 'Elle couvre tout, elle croit tout, elle efpere tout, elle fuporte tout.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 8, 'La charité ne dechet jamais, au lieu que quant aux propheties, elles feront abolies : &amp; quant aux langages, ils cefferont : &amp; quant à la connoiffance, elle fera abolie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 9, 'Car nous connoiffons en partie, &amp; prophetizons en partie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 10, 'Mais quand la perfection fera venuë, alors ce qui eft en partie fera aboli.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 11, 'Quand j\'eftois enfant, je parlois comme enfant, je jugeois comme enfant, je penfois comme enfant : mais quand je fuis devenu homme j\'ai aboli ce qui eftoit de l\'enfance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 12, 'Car nous voyons maintenant par un miroir obfcurement, mais alors nous verrons face à face : maintenant je connois en partie, mais alors je connoitrai felon que j\'ai efté auffi connu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 13, 'Or maintenant ces trois chofes demeurent, la Foi, l\'Efperance, &amp; la Charité ; mais la plus grande d\'elles, eft la Charité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 1, 'POURCHAFFEZ la charité : foyez convoiteux des dons fpirituels : mais beaucoup plus de prophetifer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 2, 'Car celui qui parle en langage inconnu, ne parle point aux hommes, mais à Dieu : car nul ne l\'entend, &amp; il prononce des myfteres en efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 3, 'Mais celui qui prophetife, propofe aux hommes edification, &amp; exhortation, &amp; confolation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 4, 'Celui qui parle un langage inconnu, s\'edifie foi-mefme : mais celui qui prophetife, edifie l\'Eglife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 5, 'Je defire bien que vous parliez tous des langages : mais beaucoup plus, que vous prophetifiez : car celui qui prophetife eft plus grand que celui qui parle des langages : fi ce n\'eft qu\'il interprete, afin que l\'Eglife en reçoive de l\'edification.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 6, 'Maintenant donc, freres, fi je viens à vous, parlant des langages inconnus, que vous profiterai-je, fi je ne vous parle par revelation, ou par fcience, ou par prophetie, ou par doctrine ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 7, 'Et de fait, les chofes qui font fans ame, qui donnent leur fon, foit un haut-bois, foit une harpe, fi elles ne donnent quelque diftinction en leurs tons, comment connoitra-t\'on ce qui eft fonné fur le haut-bois ou fur la harpe ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 8, 'Car fi la trompette donne un fon qu\'on n\'entende point, qui eft-ce qui fe preparera à la bataille ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 9, 'Pareillement auffi vous, fi vous ne prononcez par voftre langage une parole qui puiffe eftre entenduë, comment entendra-t\'on ce qui fe dit ? car vous ferez parlans en l\'air.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 10, 'Il y a (felon qu\'il efchet) tant de divers fons au monde, &amp; nul d\'eux n\'eft muet.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 11, 'Si donc je ne fçais ce que l\'on veut fignifier par la parole, je ferai barbare à celui qui parle : &amp; celui qui parle me fera barbare.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 12, 'Ainfi donc, puis que vous eftes convoiteux des dons fpirituels, cherchez d\'y abonder pour l\'edification de l\'Eglife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 13, 'C\'eft pourquoi il faut que celui qui parle un langage inconnu prie de pouvoir interpreter.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 14, 'Car fi je prie en un langage inconnu, mon efprit prie, mais mon intelligence eft fans fruict.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 15, 'Quoi donc ? Je prierai d\'efprit, mais je prierai auffi d\'intelligence : je chanterai auffi d\'intelligence.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 16, 'Autrement fi tu benis d\'efprit celui qui eft du fimple populaire, comment dira-t\'il Amen à ton action de graces ? Car il ne fçait ce que tu dis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 17, 'Il eft vrai que tu rens bien graces : mais un autre n\'en eft point edifié.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 18, 'Je rens graces à mon Dieu que je parle plus de langages que vous tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 19, 'Mais j\'aime mieux prononcer en l\'Eglife cinq paroles en mon intelligence, afin que j\'inftruife auffi les autres, que dix mille paroles en un langage inconnu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 20, 'Freres, ne foyez point enfans de fens, mais foyez petits enfans en malice : mais quant au fens, foyez hommes faits.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 21, 'Il eft efcrit en la Loi, Partant je parlerai à ce peuple-ci par des gens d\'autre langage, &amp; par des levres eftranges : &amp; encore ainfi ils ne m\'entendront point, dit le Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 22, 'C\'eft pourquoi les langages font pour figne, non point aux croyans, mais aux infideles : au contraire, la prophetie non point aux infideles, mais aux croyans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 23, 'Si donc toute l\'Eglife s\'affemble en un, &amp; que tous parlent des langages eftranges, &amp; que le commun peuple, ou les infideles y entrent ne diront-ils point que vous eftes hors du fens ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 24, 'Mais fi tous prophetifent, &amp; qu\'il y entre quelque infidele, ou quelqu\'un du commun, il eft redargué de tous, &amp; eft jugé de tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 25, 'Et ainfi les fecrets de fon cœur font manifeftez : dont il fe jettera fur fa face, &amp; adorera Dieu, &amp; declarera pleinement que vrayement Dieu eft entre vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 26, 'Que fera-ce donc, freres ? C\'eft que toutes les fois que vous vous affemblerez, felon que chacun de vous aura pfeaume, ou doctrine, ou langage, ou revelation, ou interpretation, le tout fe faffe à edification.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 27, 'Soit que quelqu\'un parle un langage inconnu, que cela fe faffe par deux, ou au plus par trois, &amp; cela par tout, mais qu\'il y en ait un qui interprete.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 28, 'Que s\'il n\'y a point d\'interprete, qu\'il fe taife en l\'Eglife, &amp; qu\'il parle à foi-mefme &amp; à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 29, 'Et que deux ou trois Prophetes parlent, &amp; que les autres en jugent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 30, 'Et fi quelque chofe eft revelée à un autre qui eft affis, que le premier fe taife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 31, 'Car vous pouvez tous prophetifer l\'un apres l\'autre, afin que tous apprennent, &amp; que tous foyent confolez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 32, 'Et les efprits des Prophetes font fujets aux Prophetes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 33, 'Car Dieu n\'eft point Dieu de confufion, mais de paix, comme on voit en toutes les Eglifes des Saints.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 34, 'Que les femmes qui font entre vous fe taifent dans les Eglifes : car il ne leur eft point permis de parler, mais elles doivent eftre fujettes, comme auffi dit la Loi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 35, 'Que fi elles veulent apprendre quelque chofe, qu\'elles interrogent leurs propres maris en la maifon : car il eft deshonnefte que les femmes parlent en l\'Eglife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 36, 'La parole de Dieu eft-elle procedée de vous ? eft-elle parvenuë feulement à vous ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 37, 'Si quelqu\'un penfe eftre Prophete, ou fpirituel, qu\'il reconnoiffe que les chofes que je vous efcris font des commandemens du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 38, 'Que fi quelqu\'un eft ignorant, qu\'il foit ignorant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 39, 'Partant, freres, foyez convoiteux de prophetifer, &amp; n\'empefchez point de parler des langages.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 40, 'Que toutes chofes fe faffent honneftement &amp; par ordre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 1, 'OR je vous declare, freres, l\'Evangile que je vous ai annoncé, &amp; que vous avez receu, &amp; auquel vous vous tenez fermes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 2, 'Et par lequel vous eftes auffi fauvez, fi vous retenez en quelle maniere je vous l\'ai annoncé : fi ce n\'eft que vous ayez creu en vain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 3, 'Car avant toutes chofes, je vous ai baillé ce que j\'avois auffi receu, affavoir, que Chrift eft mort pour nos pechez, felon les Efcritures :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 4, 'Et qu\'il a efté enfeveli, &amp; qu\'il eft reffufcité le troifiéme jour, felon les Efcritures.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 5, 'Et qu\'il a efté veu de Cephas, &amp; puis des douze.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 6, 'Depuis il a efté veu de plus de cinq cens freres à une fois, defquels plufieurs font vivans jufques à prefent : &amp; quelques-uns dorment.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 7, 'Depuis il a efté veu de Jaques, &amp; puis de tous les Apoftres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 8, 'Et apres tous, il a efté auffi veu de moi, comme d\'un avorton.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 9, 'Car je fuis le moindre des Apoftres, qui ne fuis pas digne d\'eftre appelé Apoftre, dautant que j\'ai perfecuté l\'Eglife de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 10, 'Mais par la grace de Dieu je fuis ce que je fuis : &amp; la grace qui eft envers moi, n\'a point efté vaine : mais j\'ai travaillé beaucoup plus qu\'eux tous : toutefois non point moi, mais la grace de Dieu, qui eft avec moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 11, 'Soit donc moi, foit eux, nous prefchons ainfi, &amp; vous l\'avez ainfi creu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 12, 'Or fi on prefche que Chrift eft reffufcité des morts, comment difent quelques-uns d\'entre vous, qu\'il n\'y a point de refurrection des morts ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 13, 'Car s\'il n\'y a point de refurrection des morts, Chrift auffi n\'eft point reffufcité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 14, 'Et fi Chrift n\'eft point reffufcité, noftre predication donc eft vaine, &amp; voftre foi auffi eft vaine.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 15, 'Et mefmes nous fommes trouvez faux tefmoins de Dieu : car nous avons porté témoignage de par Dieu qu\'il a reffufcité Chrift, lequel il n\'a point reffufcité, voire fi les morts ne reffufcitent point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 16, 'Car fi les morts ne reffufcitent point Chrift auffi n\'eft point reffufcité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 17, 'Et fi Chrift n\'eft point reffufcité, voftre foi eft vaine, vous eftes encore en vos pechez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 18, 'Ceux donc auffi qui dorment en Chrift font peris.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 19, 'Si nous avons efperance en Chrift, en cette vie feulement, nous fommes les plus miferables de tous les hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 20, 'Or maintenant Chrift eft reffufcité des morts, &amp; a efté fait les premices des dormans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 21, 'Car depuis que la mort eft par un homme, auffi la refurrection des morts est par un homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 22, 'Car comme en Adam tous meurent, pareillement auffi en Chrift tous font vivifiez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 23, 'Mais chacun en fon rang : les premices, c\'eft Chrift : puis apres, ceux qui font de Chrift feront vivifiez en fon advenement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 24, 'Et puis la fin, quand il aura remis le royaume de à Dieu le Pere : quand il aura aboli tout empire, &amp; toute puiffance &amp; force.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 25, 'Car il faut qu\'il regne, jufques à ce que qu\'il ait mis tous fes ennemis fous fes pieds.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 26, 'L\'ennemi qui fera détruit le dernier, c\'eft la mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 27, 'Car il a affujetti toutes chofes fous les pieds d\'icelui : (or quand il dit que toutes chofes lui font affujetties, il appert que celui qui lui affujetti toutes chofes, eft excepté.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 28, 'Et quand toutes chofes lui auront efté affujetties, alors auffi le Fils mefme fera affujetti à celui qui lui a affujetti toutes chofes : afin que Dieu foit tout en tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 29, 'Autrement que feront ceux qui font baptifez pour morts, fi abfolument les morts ne reffufcitent point ? Pourquoi auffi font-ils baptizez pour morts ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 30, 'Pourquoi auffi fommes-nous en danger à toute heure ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 31, 'Par noftre gloire que j\'ai en noftre Seigneur Jefus Chrift, je meurs de jour en jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 32, 'Si j\'ai combattu contre les beftes à Ephefe, felon l\'homme, que me profite-t\'il, fi les morts ne reffufcitent point ? Mangeons &amp; beuvons : car demain nous mourrons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 33, 'Ne foyez point feduits. Les mauvaifes compagnies corrompent les bonnes mœurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 34, 'Efveillez-vous juftement, &amp; ne pechez point : car quelques-uns font fans connoiffance de Dieu : je vous le dis à voftre honte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 35, 'Mais quelqu\'un dira, Comment reffufcitent les morts ? &amp; en quel corps viendront-ils ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 36, 'O fol ! Ce que tu femes n\'eft point vivifié, s\'il ne meurt.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 37, 'Et quand à ce que tu femes, tu ne femes point le corps qui naiftra, mais le grain nud, felon qu\'il efchet, de blé, ou de quelque autre grain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 38, 'Mais Dieu lui donne le corps ainfi qu\'il veut, &amp; à chacune des femences fon propre corps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 39, 'Toute chair n\'est point une mefme forte de chair : mais autre eft la chair des hommes, &amp; autre la chair des beftes, &amp; autre des poiffons, &amp; autre des oifeaux :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 40, 'il y a auffi des corps celeftes, &amp; des corps terreftres : mais autre eft la gloire des celeftes, &amp; autre celle des terreftres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 41, 'Autre eft la gloire du Soleil, &amp; autre la gloire de la Lune, &amp; autre la gloire des étoiles : car une étoile eft differente de l\'autre étoile en gloire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 42, 'Ainfi auffi fera la refurrection des morts : le corps eft femé en corruption, il reffufcitera en incorruption.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 43, 'Il eft femé en deshonneur, il reffufcitera en gloire : il eft femé en foibleffe, il reffufcitera en force.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 44, 'Il eft femé corps fenfuel : il reffufcitera corps fpirituel, il y a un corps fenfuel, &amp; il y a un corps fpirituel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 45, 'Comme auffi il eft efcrit, Le premier homme Adam a efté fait ame vivante : &amp; le dernier Adam en efprit vivifiant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 46, 'Or ce qui eft fpirituel n\'est point le premier, mais ce qui eft fenfuel : puis apres ce qui eft fpirituel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 47, 'Le premier homme eftant de terre, eft de poudre : &amp; le fecond homme, affavoir le Seigneur, eft du ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 48, 'Tel qu\'eft celui qui eft de poudre, tels auffi font ceux qui font de poudre, &amp; tel qu\'eft le celefte, tels auffi font les celeftes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 49, 'Et comme nous avons porté l\'image de celui qui eft de poudre, auffi porterons-nous l\'image du celefte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 50, 'Voila donc ce que je dis, freres, que la chair &amp; le fang ne peuvent heriter le royaume de Dieu : ni la corruption n\'herite point l\'incorruption.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 51, 'Voici, je vous dis un myftere ; Il eft vrai que nous ne dormirons point tous, mais nous ferons tous tranfmuez :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 52, 'En un moment, &amp; en un clin d\'œil, à la derniere trompette, (car elle fonnera) &amp; les morts reffufciteront incorruptibles, &amp; nous ferons tranfmuez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 53, 'Car il faut que ce corruptible-ici revefte l\'incorruption : &amp; que ce mortel-ici revefte l\'immortalité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 54, 'Or quand ce corruptible-ici aura reveftu l\'incorruption, &amp; que ce mortel-ici aura reveftu l\'immortalité, alors fera accomplie la parole qui eft écrite, La mort eft engloutie en victoire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 55, 'Où est, ô mort, ton aiguillon ? Où est, ô fepulcre, ta victoire ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 56, 'Or l\'aiguillon de la mort, c\'est le peché : &amp; la puiffance du peché, c\'est la Loi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 57, 'Mais graces à Dieu, qui nous a donné la victoire par noftre Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 58, 'C\'eft pourquoi, mes freres bien-aimez, foyez fermes, immuables, abondans toûjours en l\'œuvre du Seigneur : fçachans que voftre labeur n\'eft point vain au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 1, 'TOUCHANT la collecte qui fe fait pour les Saints, faites-en comme j\'en ai ordonné aux Eglifes de Galatie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 2, 'C\'eft que chaque premier jour de la femaine, chacun de vous mette à part par devers foi, affemblant ce qu\'il pourra felon ce qu\'il aura profperé : afin que lors que je viendrai, les collectes ne foyent point à faire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 3, 'Puis quand je ferai arrivé, j\'envoyerai ceux que vous approuverez par lettres, pour porter voftre liberalité à Jerufalem.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 4, 'Et s\'il eft à propos que j\'y aille, ils viendront auffi avec moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 5, 'Je viendrai donc vers vous, ayant paffé par la Macedone : (car je pafferai par la Macedone.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 6, 'Et peut-eftre que je fejournerai avec vous, ou mefmes que j\'y pafferai l\'hyver : afin que vous me conduifiez partout là où j\'irai.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 7, 'Car je ne vous veux point maintenant voir en paffant : mais j\'efpere que je demeurerai avec vous quelque temps, fi le Seigneur le permet.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 8, 'Toutefois je demeurerai à Ephefe jufques à la Pentecofte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 9, 'Car une grande porte &amp; d\'efficace m\'eft ouverte ; mais il y a plufieurs adverfaires.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 10, 'Que fi Timothée vient, prenez garde qu\'il foit avec vous feurement : car il s\'employe à l\'œuvre du Seigneur comme moi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 11, 'Que nul donc ne le méprife : mais conduifez-le en paix, afin qu\'il vienne à moi ; car je l\'attends avec les freres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 12, 'Touchant Apollos nostre frere, je l\'ai beaucoup prié qu\'il allaft vers vous avec les freres : mais il n\'a nullement eu la volonté d\'y aller maintenant : toutefois il ira quand il aura la commodité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 13, 'Veillez, foyez fermes en la foi, portez-vous vaillamment, fortifiez-vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 14, 'Que toutes vos affaires fe faffent en charité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 15, 'Or je vous prie, freres, vous connoiffez la famille de Stephanas, comme elle eft les premices d\'Achaïe, &amp; qu\'ils fe font du tout adonnez au fervice des Saints.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 16, 'Donnez ordre que vous foyez auffi affujettis à de telles gens, &amp; à chacun qui s\'employe en l\'œuvre, &amp; qui travaille avec nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 17, 'Or je m\'éjoüis de la venuë de Stephanas, de Fortunat, &amp; d\'Achaïque : parce qu\'ils ont fuppleé à voftre deffaut.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 18, 'Car ils ont recreé mon efprit &amp; le voftre : reconnoiffés donc ceux qui font tels.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 19, 'Les Eglifes d\'Afie vous faluënt. Aquile &amp; Prifcille (avec l\'Eglife qui eft en leur maifon) vous faluënt affectueufement au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 20, 'Tous les freres vous faluënt. Saluëz vous l\'un l\'autre d\'un faint baifer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 21, 'La salutation de la propre main de moi Paul.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 22, 'S\'il y a quelqu\'un qui n\'aime point le Seigneur Jefus Chrift, qu\'il foit anatheme Maranatha.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 23, 'La grace de noftre Seigneur Jefus Chrift foit avec vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 24, 'Ma dilection foit avec vous tous en Jefus Chrift, Amen. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 1, 'PAUL Apoftre de Jefus Chrift par la volonté de Dieu, &amp; le frere Timothée, à l\'Eglife de Dieu qui eft à Corinthe, avec tous les Saints qui font en toute l\'Achaïe :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 2, 'Grace vous foit &amp; paix de par Dieu noftre Pere, &amp; de par le Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 3, 'Benit foit Dieu qui eft le Pere de noftre Seigneur Jefus Chrift, le Pere des mifericordes, &amp; le Dieu de toute confolation :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 4, 'Qui nous confole en toute noftre affliction, afin que par la confolation de laquelle nous-mefmes fommes confolez de Dieu, nous puiffions confoler ceux qui font en quelque affliction que ce foit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 5, 'Car comme les fouffrances de Chrift abondant en nous, pareillement auffi noftre confolation abonde par Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 6, 'Et foit que nous foyons affligez, c\'eft pour voftre confolation &amp; falut, qui fe produit en endurant les mefmes fouffrances que nous fouffrons auffi : foit que nous foyons confolez, c\'eft pour voftre confolation &amp; falut.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 7, 'Et l\'efperance que nous avons de vous est ferme, fçachans que comme vous eftes participans des fouffrances, pareillement auffi le ferez-vous de la confolation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 8, 'Car freres, nous ne voulons point que vous ignoriés quant à noftre affliction qui nous eft advenuë en Afie, que nous avons efté exceffivement furchargés par deffus nostre force : tellement que nous en avons efté en extréme perplexité, mefme de la vie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 9, 'Mefme nous avions dejà en nous-mefmes la fentence de mort : confiance en nous-mefmes, mais en Dieu qui reffufcite les morts :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 10, 'Lequel nous a délivrez d\'une fi grande mort, &amp; nous delivre : auquel nous efperons qu\'encore ci-apres il nous delivrera :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 11, 'Moyennant auffi voftre aide par la priere que vous ferez pour nous, afin que par plufieurs, graces foyent renduës pour nous, pour le don qui nous aura fait, pour l\'efgard de plufieurs perfonnes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 12, 'Car c\'eft là noftre gloire, affavoir le tefmoignage de noftre confcience, qu\'en fimplicité &amp; fincerité de Dieu, &amp; non point en fapience charnelle, mais felon la grace de Dieu, nous avons converfé au monde, &amp; fingulierement envers vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 13, 'Car nous ne vous efcrivons point d\'autres chofes que celles que vous fçavez, ou mefmes connoiffez : &amp; j\'efpere que vous les reconnoiftrez jufques au bout.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 14, 'Comme auffi vous nous avez reconnus en partie, que nous fommes voftre gloire, comme vous estes auffi la noftre pour le jour du Seigneur Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 15, 'Et en cette confiance je voulois premierement venir vers vous, afin que vous euffiez une feconde grace :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 16, 'Et puis paffer par devers vous en Macedone : puis derechef de Macedone venir vers vous, &amp; eftre conduit par vous en Judée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 17, 'Quand donc je propofois cela, ai-je ufé de legereté ? Selon la chair, tellement qu\'en mon endroit il y ait, Oui, oui : &amp; puis, Non, non ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 18, 'Or Dieu eft fidele, que noftre parole de laquelle j\'ai ufé envers vous, n\'a point efté, Oui, &amp; Non.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 19, 'Car Jefus Chrift le Fils de Dieu, qui par nous a efté prefché entre vous, affavoir par moi, &amp; par Silvain, &amp; par Timothée, n\'a point efté, Oui, &amp; Non : mais a efté, Oui en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 20, 'Car autant qu\'il y a de promeffes de Dieu, elles font, Oui en lui, &amp; Amen en lui, à la gloire de Dieu par nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 21, 'Or celui qui nous confirme avec vous en Chrift, &amp; qui nous a oinct, c\'est Dieu ;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 22, 'Lequel auffi nous a feelez, &amp; nous a donné les arrhes de l\'Efprit en nos cœurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 23, 'Or j\'appelle Dieu à tefmoin fur mon ame, que ç\'a efté pour vous efpargner que je ne fuis point encore venu à Corinthe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 24, 'Non point que nous ayons domination fur voftre foi, mais nous aidons à voftre joye : car c\'eft par la foi que vous eftes debout.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 1, 'MAIS j\'avois deliberé cela en moi-mefme, de ne venir point vers vous derechef avec trifteffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 2, 'Car fi je vous contrifte, qui fera-ce puis apres qui me réjouïra, finon celui qui auroit efté contrifté par moi ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 3, 'Et vous ai efcrit ceci mefme, afin que quand je viendrai, je n\'aye point de trifteffe par ceux defquels je devois recevoir de la joye : me confiant de vous tous que ma joye eft celle de vous tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 4, 'Car je vous ai écrit en grande affliction &amp; angoiffe de cœur, avec beaucoup de larmes : non point afin que vous fceuffiez la charité que j\'ai tres-abondamment envers vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 5, 'Que fi quelqu\'un a efté caufe de trifteffe, ce n\'eft point moi qu\'il a contrifté, mais aucunement (afin que je ne le furcharge) vous tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 6, 'Il fuffit à celui qui eft tel, de cette cenfure qui a esté faite par plufieurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 7, 'Tellement qu\'au contraire vous devez pluftoft lui pardonner &amp; le confoler, afin que celui qui eft tel, ne foit point englouti de la trop grande trifteffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 8, 'C\'eft pourquoi je vous prie, de ratifier envers lui vostre charité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 9, 'Car je vous ai auffi efcrit pour cela, afin de connoiftre l\'experience de vous, affavoir fi vous eftes obeïffans en toues chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 10, 'Or à celui à qui vous pardonnez quelque chofe, je pardonne auffi : car de ma part auffi fi j\'ai pardonné quelque chofe à celui à qui j\'ai pardonné, je l\'ai fait à caufe de vous, devant la face de Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 11, 'Afin que nous ne foyons point circonvenus par Satan : car nous n\'ignorons pas fes machinations.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 12, 'Au furplus eftant venu à Troas pour l\'Evangile de Chrift, encore que la porte me fuft ouverte par le Seigneur,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 13, 'Je n\'ai point eu de repos en mon efprit, d\'autant que je n\'ai point trouvé Tite mon frere : mais ayant pris congé d\'eux, je m\'en fuis venu en Macedone.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 14, 'Or graces à Dieu qui nous fait toûjours triompher en Chrift : &amp; qui manifefte par nous l\'odeur de fa connoiffance en tous lieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 15, 'Car nous fommes la bonne odeur de Chrift à Dieu, en ceux qui font fauvez, &amp; en ceux qui periffent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 16, 'A ceux-ci, odeur de mort à mort : &amp; à ceux-là odeur de vie à vie. Et qui eft fuffifant pour ces chofes ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 17, 'Car nous ne falfifions point la Parole de Dieu, comme plufieurs, mais nous parlons touchant Chrift comme en fincerité, &amp; comme de par Dieu, devant Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 1, 'COMMENÇONS-nous derechef à nous recommander nous-mefmes ? Ou bien avons-nous befoin, comme quelques-uns, d\'epiftres recommandatoires de par vous ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 2, 'Vous eftes noftre epiftre, écrite en nos cœurs, laquelle eft connuë &amp; leuë de tous les hommes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 3, 'Entant qu\'il apparoit en vous que vous eftes l\'epiftre de Chrift, adminiftrée par nous, &amp; écrite non point d\'encre, mais de l\'Efprit du Dieu vivant : non point en des plaques de pierres, mais és plaques charnelles du cœur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 4, 'Or nous avons une telle confiance en Dieu par Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 5, 'Non point que nous foyons fuffifans de penfer quelque chofe de nous, comme de nous-mefmes : mais noftre fuffifance est de Dieu :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 6, 'Lequel auffi nous a rendus fuffifans pour eftre miniftres du nouveau Teftament : non pas de lettre, mais d\'Efprit : car la lettre tuë, mais l\'Efprit vivifie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 7, 'Que fi le miniftere de mort efcrit en lettres &amp; gravé en pierres, a efté glorieux, tellement que les enfans d\'Ifraël ne pouvoyent regarder en la face de Moyfe pour la gloire de fa face ; (laquelle gloire devoit prendre fin :)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 8, 'Comment eft-ce que le miniftere de l\'efprit ne fera plus glorieux ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 9, 'Car fi le miniftere de condamnation a esté glorieux, le miniftere de juftice le furpaffe de beaucoup en gloire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 10, 'Car mefmes ce qui a efté glorifié, n\'a point efté glorifié en cet égard, à caufe de la fureminente gloire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 11, 'Car fi ce qui devoir prendre fin a esté glorieux, ce qui eft permanent, eft beaucoup plus glorieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 12, 'Ayans donc une telle efperance, nous ufons de grande hardieffe de parler.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 13, 'Et nous ne fommes point comme Moyfe, qui mettoit un voile fur fa face, afin que les enfans d\'Ifraël ne regardaffent point à la fin de ce qui devoit étre aboli.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 14, 'Mais leurs entendemens font endurcis : car jufques à ce jourd\'hui ce mefme voile demeure en la lecture de l\'ancien Teftament, fans eftre ofté : (lequel eft aboli par Chrift :)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 15, 'Jufques à ce jourd\'hui donc, quand on lit Moyfe, le voile demeure fur leur cœur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 16, 'Mais quand il fe fera converti au Seigneur, le voile fera ofté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 17, 'Or le Seigneur eft cet Efprit-là : &amp; là où eft l\'Efprit du Seigneur, là eft la liberté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 18, 'Ainfi nous tous qui contemplons comme en un miroir la gloire du Seigneur à face découverte, fommes transformez en la mefme image de gloire en gloire, comme par l\'Efprit du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 1, 'POUR cette caufe ayans ce miniftere, felon la mifericorde que nous avons receuë, nous ne nous relafchons point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 2, 'Mais nous avons entierement rejetté les cachettes de honte : ne cheminans point avec rufe, &amp; ne falfifians point la Parole de Dieu, mais nous approuvans à toute confcience des hommes devant Dieu, par la manifeftation de la verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 3, 'Que fi noftre Evangile eft encore couvert, il eft couvert à ceux qui periffent :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 4, 'Efquels le Dieu de ce fiecle a aveuglé les entendemens, affavoir des incredules, afin que la lumiere de l\'Evangile de la gloire de Chrift (qui eft l\'image de Dieu) ne leur refplendift point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 5, 'Car nous ne nous prefchons point nous-mefmes, mais Jefus Chrift le Seigneur : &amp; que nous fommes vos ferviteurs pour l\'amour de Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 6, 'Car Dieu qui a dit que la lumiere refplendift des tenebres, est celui qui a relui en nos cœurs, pour donner illumination de la connoiffance de la gloire de Dieu en la face de Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 7, 'Mais nous avons ce trefor en des vaiffeaux de terre, afin que l\'excellence de cette force foit de Dieu, &amp; non point de nous :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 8, 'Eftans oppreffez en toutes fortes :mais non point reduits du tout à l\'étroit : eftans en perplexité, mais non point deftituez :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 9, 'Eftant perfecutez, mais non point abandonnez : eftans abbatus, mais non point perdus :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 10, 'Portant toûjours partout en noftre corps la mortification du Seigneur Jefus, afin auffi que la vie de Jefus foit manifeftée en noftre corps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 11, 'Car nous qui vivons, fommes toûjours livrez à la mort pour l\'amour de Jefus, afin auffi que la vie de Jefus foit manifeftée en noftre chair mortelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 12, 'La mort donc fe defploye en nous, mais la vie en nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 13, 'Or ayans un mefme efprit de foi, felon qu\'il eft efcrit, j\'ai creu, pource ai-je parlé : auffi croyons-nous, &amp; pourtant parlons-nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 14, 'Sçachans que celui qui a reffufcité le Seigneur Jefus, nous reffufcitera par Jefus, &amp; nous fera comparoiftre en fa prefence avec vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 15, 'Car toutes ces chofes font pour vous, afin que cette tres-grande grace redonde à la gloire de Dieu, par le remerciement de plufieurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 16, 'C\'eft pourquoi nous ne nous relafchons point : mais bien que noftre homme exterieur fe déchée, toutefois l\'interieur eft renouvellé de jour en jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 17, 'Car noftre legere affliction, qui ne fait que paffer, produit en nous un poids eternel d\'une gloire fouverainement excellente :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 18, 'Quand nous ne regardons point aux chofes vifibles, mais aux invifibles : car les chofes vifibles font pour un temps : mais les invifibles font eternelles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 1, 'CAR nous fçavons que fi noftre habitation terreftre de cette loge eft deftruite, nous avons un edifice de par Dieu, affavoir une maifon eternelle és cieux, qui n\'eft point faite de main.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 2, 'Car auffi pour cela gemiffons-nous, dfirans tant &amp; plus d\'eftre reveftus de noftre domicile qui eft du ciel :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 3, 'Voire mefmes fi nous fommes trouvez veftus, &amp; non point nuds.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 4, 'Car nous qui fommes en cette loge, gemiffons eftans chargez : entant que nous defirons, non point d\'eftre dépoüillez, mais d\'eftre reveftus : afin que ce qui eft mortel, foit englouti par la vie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 5, 'Or celui qui nous a formez à cela mefme, c\'eft Dieu : lequel auffi nous a donné les arrhes de l\'Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 6, 'Nous avons donc toûjours confiance, &amp; fçavons que logeans au corps, nous fommes eftrangers du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 7, 'Car c\'eft par foi que nous cheminons, &amp; non point par veuë.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 8, 'Mais nous avons confiance, &amp; aimons mieux eftre eftrangers de ce corps, &amp; eftre avec le Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 9, 'C\'eft pourquoi auffi nous nous eftudions de lui eftre agreables, &amp; prefens &amp; abfens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 10, 'Car il nous faut tous comparoiftre devant le fiege judicial de Chrift, afin que chacun emporte en fon corps felon qu\'il aura fait, ou bien, ou mal.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 11, 'Sçachans donc ce que c\'eft de la frayeur du Seigneur, nous induifons les hommes à la foi, &amp; fommes manifeftez à Dieu : &amp; je m\'attends auffi que nous fommes manifeftez en vos confciences.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 12, 'Car nous ne nous recommandons point derechef envers vous, mais c\'eft que nous nous donnons occafion de vous glorifier de nous : afin que vous ayez dequoi refpondre à ceux qui fe glorifient de l\'apparence, &amp; non point du cœur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 13, 'Car foit que nous foyons tranfportez d\'entendement, nous le fommes à Dieu : foit que nous foyons de fens raffis, nous le fommes à vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 14, 'Car la charité de Chrift nous eftreint tenans cela pour certain, que fi un eft mort pour tous, tous auffi font morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 15, 'Et qu\'il eft mort pour tous, afin que ceux qui vivent, ne vivent point d\'orefnavant à eux-mefmes, mais à celui qui eft mort &amp; qui eft reffufcité pour eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 16, 'C\'eft pourquoi dés maintenant nous ne connoiffons perfonne felon la chair ; mefmes encore que nous ayons connu Chrift felon la chair, toutefois maintenant nous ne le connoiffons plus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 17, 'Si donc quelqu\'un eft en Chrift, qu\'il foit nouvelle creature : les chofes vieilles font paffées, voici, toutes chofes font faites nouvelles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 18, 'Or le tout eft de Dieu, qui nous a reconciliez à foi par Jefus Chrift, &amp; nous a donné le miniftere de reconciliation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 19, 'Car Dieu eftoit en Chrift, reconciliant le monde à foi, en ne leur imputant point leurs forfaits : &amp; a mis en nous la Parole de reconciliation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 20, 'Nous fommes donc ambaffadeurs pour Chrift, comme fi Dieu exhortoit par nous : nous fupplions pour Chrift, que vous foyez reconciliez à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 21, 'Car il a fait celui qui n\'a point connu de peché, eftre peché pour nous, afin que nous fuffions juftice de Dieu en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 1, 'AINSI donc eftans ouvriers avec lui, nous vous prions auffi que vous n\'ayez point receu la grace de Dieu en vain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 2, 'Car il dit, Je t\'ai exaucé en un temps agreable, &amp; t\'ai fecouru au jour de falut : Voici maintenant le temps agreable, voici maintenant le jour de falut.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 3, 'En forte que nous ne donnions aucun fcandale, afin que nostre miniftere ne foit point deshonoré.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 4, 'Mais nous rendans recommandables en toutes chofes, comme eftans miniftres de Dieu en grande patience, en afflictions, en neceffité, en angoiffes,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 5, 'En battures, en prifons, en troubles, en travaux, en veilles, en jeufnes,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 6, 'Par pureté, par connoiffance, par un efprit patient, par benignité, par le Saint Efprit, par une charité non feinte,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 7, 'Par la Parole de verité, par la puiffance de Dieu, par les armes de juftice à droit &amp; à gauche :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 8, 'Parmi l\'honneur &amp; l\'ignominie, parmi le diffame &amp; la bonne renommée :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 9, 'Comme feducteurs, &amp; toutefois veritables : comme inconnus, &amp; toutefois reconnus : comme mourans, &amp; voici nous vivons : comme chaftiez, &amp; toutefois non mis à mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 10, 'Comme contriftez, &amp; toutefois toûjours joyeux : comme pauvres, &amp; toutefois enrichiffans plufieurs : comme n\'ayans rien, &amp; toutefois poffedans toutes chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 11, 'O Corinthiens ! Noftre bouche eft ouverte envers vous, noftre cœur s\'eft élargi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 12, 'Vous n\'eftes point à l\'eftroit dans nous : mais vous eftes à l\'eftroit en vos entrailles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 13, 'Or pour nous recompenfer de mefmes (je parle comme à mes enfans) elargiffez-vous auffi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 14, 'Ne vous accouplez point avec les infideles : car quelle participation y a-t\'il de la juftice avec l\'iniquité : &amp; quelle communication y a-t\'il de la lumiere avec les tenebres ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 15, 'Et quel accord y a-t\'il de Chrift avec Belial ? Ou quelle portion a le fidele avec l\'infidele ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 16, 'Et quelle convenance y a-t\'il du temple de Dieu avec les idoles ? Car vous eftes le temple du Dieu vivant, ainfi que Dieu a dit, J\'habiterai : &amp; ferai leur Dieu, &amp; il feront mon peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 17, 'C\'eft pourquoi departez-vous du milieu d\'eux, &amp; vous en feparez, dit le Seigneur : &amp; ne touchez à chofe quelconque foüillée, &amp; je vous recevrai.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 18, 'Et vous ferai pour pere, &amp; vous me ferez pour fils &amp; pour filles, dit le Seigneur Tout-puiffant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 1, 'OR donc, bien-aimez, puis que nous avons de telles promeffes, nettoyons-nous de toute foüilleure de chair &amp; d\'efprit, achevans la fanctification en la crainte de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 2, 'Recevez-nous : nous n\'avons fait tort à perfonne, nous n\'avons corrompu perfonne, nous n\'avons pillé perfonne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 3, 'Je ne dis point ceci à voftre condamnation : car j\'ai dit ci-devant que vous eftes en nos cœurs à mourir &amp; à vivre enfemble.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 4, 'J\'ai une grande liberté en voftre endroit, j\'ai grandement dequoi me glorifier de vous : je fuis rempli de confolation, je fuis plein de joye tant &amp; plus en toute noftre affliction.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 5, 'Car nous eftans venus en Macedone, noftre chair n\'a eu aucune relafche ; au contraire nous avons en toutes façons efté affligez : combats au dehors, craintes au dedans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 6, 'Mais Dieu qui confole les abbatus, nous a confolez par la venuë de Tite.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 7, 'Et non feulement par fa venuë, mais auffi par la confolation de laquelle il a efté confolé en vous : nous racontant voftre grand defir, voftre pleur, voftre affection ardente envers moi : en forte que je m\'en fuis tant &amp; plus éjoüi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 8, 'Car encore que je vous aye contriftez par mon epiftre, je ne m\'en repens point : encore que je m\'en fuffe repenti : car je voi que cette epiftre-là, quoi que pour un temps, vous a contriftez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 9, 'J\'en fuis maintenant joyeux, non point parce que vous avez efté contriftez, mais parce que vous avez efté contriftez à repentance : car vous avez efté contriftez felon Dieu, tellement que vous n\'avez efté endommagez en rien de par nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 10, 'Car la trifteffe qui eft felon Dieu, produit une repentance à falut, dont on ne fe repent jamais : mais la trifteffe de ce monde produit la mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 11, 'Car voici, ceci mefme que vous avez efté contriftez felon Dieu, quel foin a-t\'il produit en vous ? Voire quelle fatisfaction ? Voire marriffement, voire crainte, voire grand defir, voire zele, voire vengeance ? Vous vous eftes par tout montrez eftre purs en cette affaire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 12, 'Encore donc que je vous aye efcrit, ce n\'a point efté à caufe de celui qui a commis la faute, ni à caufe de celui envers qui elle a efté commife ; mais pour manifefter entre vous le foin que j\'ai de vous devant Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 13, 'Pour cette caufe nous avons efté confolez de voftre confolation : mais encore nous fommes-nous de plus fort éjoüis pour la joye de Tite, de ce que fon efprit a efté recreé par vous tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 14, 'Et de ce que fi je me fuis glorifié de vous envers lui en aucune chofe, je n\'en ai point receu de confufion : mais comme nous avons dit toutes chofes en verité, pareillement ce dequoi je m\'eftois glorifié envers Tite, a efté trouvé verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 15, 'C\'eft pourquoi quand il lui fouvient de l\'obeïffance de vous tous, &amp; comment vous l\'avez receu avec crainte &amp; tremblement, fon affection eft d\'autant plus grande envers vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 16, 'Je m\'éjouïs donc qu\'en toutes chofes je me puis affeurer de vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 1, 'OR, freres, nous vous faifons fçavoir la grace de Dieu qui a efté donnée aux Eglifes de Macedone.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 2, 'C\'eft qu\'en une grande efpreuve d\'affliction, l\'abondance de leur joye, &amp; leur profonde pauvreté, a redondé en richeffes de leur prompte liberalité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 3, 'Car je fuis tefmoin qu\'ils ont efté volontaires felon leur pouvoir, voire outre leur pouvoir :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 4, 'Nous requerans avec de grandes prieres, que nous receuffions la grace &amp; focieté de cette fubvention qui fe fait envers les Saints.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 5, 'Et ils n\'ont point fait comme nous nous attendions, mais ils fe font donnez premierement eux-mefmes au Seigneur, &amp; puis à nous par la volonté de Dieu :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 6, 'Afin que nous exhortaffions Tite, qu\'ainfi qu\'il avoit auparavant commencé, pareillement auffi il achevaft cette grace par devers vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 7, 'C\'eft pourquoi, comme vous abondez en toutes chofes, en foi, &amp; en parole, &amp; en connoiffance, &amp; en toute diligence, &amp; en voftre charité envers nous, faites que vous abondiez auffi en cette grace.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 8, 'Je ne le dis point par commandement, mais pour efprouver auffi par la diligence des autres, le naïf de voftre charité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 9, 'Car vous connoiffez la grace de noftre Seigneur Jefus Chrift, affavoir qu\'il s\'eft rendu pauvre pour vous, bien qu\'il fuft riche : afin que par fa pauvreté vous fuffiez rendus riches.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 10, 'En ceci dont je donne advis : car cela vous eft convenable, voire à vous qui avez commencé dés l\'année paffée, non pas le fait feulement, mais auffi la franche volonté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 11, 'Maintenant donc achevez auffi le fait : afin qu\'ainfi que la promptitude du vouloir y a efté, tel foit auffi le parfaire felon ce que vous avez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 12, 'Car fi la promptitude de courage va devant, on eft agreable felon ce qu\'on a, &amp; non pas felon ce qu\'on n\'a point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 13, 'Car ce n\'eft point afin que les autres foient foulagez, &amp; que vous foyez foulez : mais afin que ce foit par égalité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 14, 'Maintenant voftre abondance fupplée à leur indigence, afin auffi que leur abondance foit pour voftre indigence, à ce qu\'il y ait de l\'égalité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 15, 'Ainfi qu\'il eft efcrit, Celui qui avoit beaucoup, n\'a rien eu de furabondant : &amp; celui qui avoit peu, n\'a point eu moins.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 16, 'Or graces à Dieu qui a donné le mefme foin pour vous au cœur de Tite :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 17, 'Affavoir qu\'il a eu mon exhortation pour agreable, &amp; mefmes qu\'eftant fort affectionné, il s\'en eft allé vers vous volontairement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 18, 'Or nous avons auffi envoyé avec lui le frere, duquel la loüange eft en l\'affaire de l\'Evangile par toutes les Eglifes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 19, 'Et non feulement cela, mais auffi il a efté ordonné par les Eglifes compagnon de noftre voyage pour cette grace qui eft adminiftrée par nous à la gloire du Seigneur mefme, &amp; pour fervir à la promptitude de voftre courage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 20, 'Nous donnans garde que perfonne ne vous reprenne en cette abondance qui eft adminiftrée par nous :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 21, 'Et procurans ce qui eft bon, non feulement devant le Seigneur, mais auffi devant les hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 22, 'Nous avons auffi envoyé avec eux noftre frere, lequel nous avons efprouvé fouventefois en plufieurs chofes, eftre diligent, &amp; maintenant beaucoup plus diligent, pour la grande affeurance que j\'ai de vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 23, 'Ainfi donc quant à Tite, il eft eft mon affocié &amp; compagnon d\'œuvre envers vous : &amp; quant à nos freres, ils font ambaffadeurs des Eglifes, &amp; la gloire de Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 24, 'Montrez donc envers eux, &amp; devant les Eglifes, l\'approbation de voftre charité, &amp; de ce que nous nous glorifions de vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 1, 'CAR de vous écrire de la fubvention qui fe fait envers les Saints, ce m\'eft une chofe fuperfluë.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 2, 'Veu que je fçay la promptitude de voftre courage, pour laquelle je me glorifie de vous envers ceux de Macedone : leur faifant entendre que l\'Achaïe eft prefte dés l\'année paffée : &amp; voftre zele en a provoqué plufieurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 3, 'Or j\'ai envoyé ces freres, afin que ce que je me fuis glorifié de vous, ne foit pas vain en cet endroit, &amp; que vous foyez prefts, comme j\'ai dit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 4, 'De peur que ceux de Macedone eftans venus avec moi, &amp; vous ayans trouvez mal prefts, nous ne recevions de la honte (je n\'ofe dire vous) en cette affeurance de nous eftre glorifiez de vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 5, 'C\'eft pourquoi j\'ai eftimé qu\'il eftoit neceffaire de prier les freres, que premierement ils allaffent vers vous, &amp; qu\'ils achevaffent de mettre en ordre la beneficence, &amp; non point comme chicheté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 6, 'Or je dis ceci, Que celui qui feme chichement, recueillera auffi chichement : &amp; que celui qui feme liberalement, recueillera liberalement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 7, 'Que chacun en faffe felon qu\'il a propofé en fon cœur, non point à regret, ou par contrainte : car Dieu aime celui qui donne gayement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 8, 'Et Dieu eft puiffant pour faire abonder toute grace en vous, afin qu\'ayans toûjours toute fuffifance en toute chofe, vous foyez abondans en toute bonne œuvre :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 9, '(Ainfi qu\'il eft écrit, Il a efpars, il a donné aux pauvres, fa juftice demeure eternellement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 10, 'Or celui qui fournit de femence au femeur, vous veuille auffi pourvoir de pain à manger, &amp; multiplier voftre femence, &amp; augmenter les revenus de voftre juftice.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 11, 'Eftans pleinement enrichis en toute prompte liberalité, laquelle fait que par nous graces foyent renduës à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 12, 'Car l\'adminiftration de cette oblation n\'eft pas feulement pour subvenir aux indigences des Saints, mais auffi redonde en ce que plufieurs en rendent graces à Dieu :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 13, 'En glorifiant Dieu par le moyen de l\'efpreuve de cette fubvention, de ce que vous vous fubmettez à la confeffion de l\'Evangile de Chrift : &amp; de voftre prompte &amp; liberale communication envers eux &amp; envers tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 14, 'Et en ce qu\'ils prient pour vous, en vous defirant affectueufement pour l\'excellente grace de Dieu fur vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 15, 'Or graces à Dieu de fon don inenarrable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 1, 'AU refte, moi Paul, je vous prie par la douceur &amp; debonnaireté de Chrift : moi qui en prefence fuis petit entre vous : mais abfent fuis hardi envers vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 2, 'Je vous requiers donc eftant prefent il ne me faille point ufer de hardieffe, par cette affeurance de laquelle je delibere de me porter hardiment envers quelques-uns, qui nous eftiment comme fi nous cheminons felon la chair.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 3, 'Certes cheminant en la chair, nous ne guerroyons point felon la chair :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 4, 'Car les armes de noftre guerre ne font point charnelles, mais puiffantes de par Dieu, à la deftruction des fortereffes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 5, 'Deftruifans les confeils &amp; toute hauteffe qui s\'eleve contre la connoiffance de Dieu : &amp; amenans toute penfée prifonniere à l\'obeïffance de Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 6, 'Et ayans la vengeance toute prefte contre toute defobeïffance, quand voftre obeïffance aura efté accomplie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 7, 'Regardez-vous les chofes felon l\'apparence ? Si quelqu\'un fe confie en foi-mefme, qu\'il eft à Chrift, qu\'il penfe cela derechef en foi-mefme, que comme il eft à Chrift, pareillement auffi nous fommes à Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 8, 'Car fi je me veux mefmes glorifier davantage de noftre puiffance, laquelle le Seigneur nous a donnée à edification, &amp; non point à voftre deftruction, je n\'en recevrai point de honte :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 9, 'Afin qu\'il ne femble point que je vous veuille efpouvanter par des epiftres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 10, 'Car les epiftres (difent-ils) font bien graves &amp; fortes, mais la prefence du corps eft foible, &amp; la parole contemptible.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 11, 'Que celui qui eft tel, penfe cela, que tels que nous fommes de parole par epiftres eftant abfens ; tels auffi fommes nous de fait eftant prefens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 12, 'Car nous ne nous ofons pas mettre du rang de quelques-uns, ni accomparer à eux, lefquels fe recommandent eux-mefmes : mais ils n\'entendent point qu\'ils fe mefurent eux-mefmes par eux-mefmes &amp; s\'accomparent eux-mefmes à eux-mefmes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 13, 'Mais quant à nous, nous ne nous glorifions point de ce qui n\'eft point de noftre mefure : mais felon la mefure reglée, laquelle mefure Dieu nous a departie, nous nous glorifierons d\'eftre parvenus mefmes jufques à vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 14, 'Car nous ne nous eftendons point nous-mefmes plus qu\'il ne faut, comme fi nous n\'eftions point parvenus jufques à vous, veu que nous fommes parvenus mefmes jufques à vous, par la predication de l\'Evangile de Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 15, 'Ne nous glorifions point en ce qui n\'eft point de noftre mefure, c\'eft à dire és labeurs d\'autrui : mais ayans efperance que voftre foi venant à croiftre en vous, ne ferons amplement accreus en ce qui nous a efté departi felon la mefure reglée :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 16, 'Jufques à evangelizer és lieux qui font plus outre que vous : &amp; non point nous glorifier en ce qui a efté departi à autrui felon la mefure reglée, affavoir és chofes déja toutes preparées.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 17, 'Mais que celui qui fe glorifie, fe glorifie au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 18, 'Car ce n\'eft point celui qui fe recommande foi-mefme, qui eft approuvé, mais celui que le Seigneur recommande.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 1, 'A la mienne volonté que vous me fupportaffiez un petit en mon imprudence : mais encore fupportez moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 2, 'Car je fuis jaloux de vous d\'une jaloufie de Dieu : car je vous ai appropriez à un feul mari, pour vous prefenter comme une vierge chafte à Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 3, 'Mais je crains qu\'ainfi que le ferpent a feduit Eve par fa rufe, femblablement en quelque forte vos penfées ne foyent corrompuës, fe deftournant de la fimplicité qui eft en Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 4, 'Car fi quelqu\'un venoit, qui vous prefchaft un autre Jefus que nous n\'avons prefché, ou fi vous receviez un autre Efprit que vous n\'avez receu, ou un autre Evangile que vous n\'avez accepté, vous feriez bien de l\'endurer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 5, 'Mais j\'eftime que je n\'ai efté en rien moindre que les plus excellens Apoftres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 6, 'Que fi je fuis comme quelqu\'un du vulgaire à parler, toutefois je ne le fuis point en connoiffance : mais nous avons efté entierement manifeftez en toutes chofes envers vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 7, 'Ai-je commis une offenfe en ce que je fuis abbaiffé moi-mefme, afin que vous fuffiez élevez ? Parce que fans rien prendre je vous ai annoncé l\'Evangile de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 8, 'J\'ai pillé les autres Eglifes, prenant entretenement pour vous fervir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 9, 'Et quand j\'eftois avec vous, &amp; que j\'avois difette, je n\'ai efté en charge à perfonne : car les freres qui eftoyent venus de Macedone, ont fuppleé à ce qui me defailloit : &amp; je me fuis gardé de vous eftre en charge en chofe quelconque, &amp; m\'en garderai.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 10, 'La verité de Chrift eft en moi, que cette gloire ne me fera point bouchée pour moi és contrées d\'Achaïe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 11, 'Pourquoi ? Eft-ce parce que je ne vous aime point ? Dieu le fçait.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 12, 'Mais ce que je retranche l\'occafion à ceux qui demandent occafion : afin qu\'en ce en quoi ils fe glorifient, ils foyent auffi trouvez tels que nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 13, 'Car tels faux Apoftres font des ouvriers cauteleux, fe déguifans en Apoftres de Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 14, 'Et ce n\'est pas de merveilles : car Satan mefme fe déguife en Ange de lumiere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 15, 'Ce n\'eft donc pas grand cas fi fes miniftres auffi fe déguifent en miniftres de juftice, defquels la fin fera comme leurs œuvres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 16, 'Je le dis encore, afin que perfonne ne me penfe eftre imprudent : finon recevez-moi, mefmes comme imprudent, afin que je me vante auffi quelque peu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 17, 'Ce que je dis en cette affeurance de vanterie, je ne le dis pas felon le Seigneur, mais comme par imprudence.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 18, 'Puis que plufieurs fe vantent felon la chair, je me vanterai auffi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 19, 'Car vous endurez volontiers les imprudens, parce que vous eftes fages.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 20, 'Mefmes vous endurez fi quelqu\'un vous affervit, fi quelqu\'un vous mange, fi quelqu\'un prend de vous, fi quelqu\'un s\'éleve fur vous, fi quelqu\'un vous frappe au vifage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 21, 'Je le dis par opprobre, voire comme fi nous avions efté fans aucune vertu ; mais en quelque chofe que quelqu\'un foit hardi (je parle en imprudent) je fuis hardi auffi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 22, 'Sont-ils Hebreux ? Je le fuis auffi. Sont-ils Ifraëlites ? Je le fuis auffi. Sont-ils de la femence d\'Abraham ? J\'en fuis auffi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 23, 'Sont-ils miniftres de Chrift ? (Je parle comme imprudent) Je le fuis par deffus, en travaux davantage, en battures par deffus eux, en prifons davantage, en morts fouventefois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 24, 'J\'ai receu des Juifs par cinq fois quarante coups moins un.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 25, 'J\'ai efté battu de verges par trois fois, j\'ai efté lapidé une fois, j\'ai fait naufrage trois fois, j\'ai paffé l\"efpace d\'un jour &amp; d\'une nuit entiere en la profonde mer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 26, 'En voyages fouvent, en perils des fleuves, en perils des brigands, en perils de ma nation, en perils des Gentils, en perils en ville, en perils en defert, en perils en mer, en perils entre de faux freres :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 27, 'En peine &amp; en travail, en veilles fouvent, en faim &amp; en foif, en jeufnes fouvent, en froidure &amp; en nudité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 28, 'Outre les chofes de dehors, ce qui me tient affiegé de jour en jour, c\'est le foin que j\'ai de toutes les Eglifes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 29, 'Qui eft affoibli, que je ne fois affoibli auffi ? Qui eft fcandalizé, que je n\'en fois auffi bruflé ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 30, 'S\'il fe faut vanter, je me vanterai des chofes qui font de mon infirmité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 31, 'Dieu qui eft le Pere de noftre Seigneur Jefus Chrift, &amp; qui eft benit eternellement, fçait que je ne mens point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 32, 'A Damas le gouverneur pour le roi Aretas avoit mis des gardes en la ville des Damafceniens, me voulant faifir :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 33, 'Mais je fus defcendu de la muraille en une corbeille par une feneftre :&amp; ainfi j\'efchappai de fes mains.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 1, 'CERTES il ne m\'eft point convenable de me vanter : car je viendrai jufques aux vifions &amp; aux revelations du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 2, 'Je connois un homme en Chrift, il y a quatorze ans paffez, (fi ce fut en corps, je ne fçais : fi ce fut hors du corps, je ne fçais : Dieu le fçait :) qui a efté ravi jufques au troifiefme ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 3, 'Et je fçais qu\'un tel homme (fi ce fut en corps, ou fi ce fut hors du corps, je ne fçais : Dieu le fçait :)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 4, 'A efté ravi en Paradis, &amp; a ouï des paroles inenarrables, lefquelles il n\'eft pas poffible à l\'homme d\'exprimer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 5, 'Je me vanterai d\'un tel homme ; mais de moi-mefme je ne me vanterai point, finon en mes infirmitez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 6, 'Car quand je me voudrai vanter, je ne ferai point imprudent : car je dirai la verité : mais je m\'en efpargne, afin qu\'aucun ne m\'eftime point par deffus ce qu\'il me voit eftre, ou par deffus ce qu\'il entend de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 7, 'Et de peur que je ne m\'élevaffe outre mefure, à caufe de l\'excellence des revelations, il m\'a efté mis une écharde en la chair, un Ange de Satan, pour me fouffleter, afin que je ne m\'elevaffe point outre mefure.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 8, 'Pour laquelle chofe j\'ai prié trois fois le Seigneur, afin qu\'icelui fe departift de moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 9, 'Mais il m\'a dit, Ma grace te fuffit : car ma vertu s\'accomplit en l\'infirmité. Je me vanterai donc tres-volontiers pluftoft en mes infirmitez, afin que la vertu de Chrift habite en moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 10, 'Et partant je prens plaifir en infirmitez, en injures, en neceffitez, en perfecutions, en angoiffes pour Chrift : car quand je fuis foible, alors je fuis fort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 11, 'J\'ai efté imprudent en me vantant : vous m\'y avez contraint : car je devois eftre recommandé par vous ; veu que je n\'ai efté moindre en nulle chofe que les plus excellens Apoftres, encore que je ne fois rien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 12, 'Certes les enfeignes de mon Apoftolat, ont efté accomplies entre vous avec toute patience, avec fignes, merveilles, &amp; vertus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 13, 'Car en quoi avez-vous efté moindres que les autres Eglifes, finon que je ne fuis point devenu lafche au travail à voftre dommage ? Pardonnez moi ce tort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 14, 'Voici pour la troifiéme fois que je fuis preft d\'aller vers vous : &amp; je ne vous ferai point en charge : car je ne demande point le voftre, mais vous-mefmes : auffi les enfans ne doivent point faire d\'amas pour leurs peres, mais les peres pour leurs enfans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 15, 'Et quant à moi, je defpenferai tres-volontiers, &amp; ferai defpenfé pour vos ames : bien que vous aimant tant &amp; plus, je fois moins aimé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 16, 'Mais foit, que je ne vous aye point chargez, toutefois eftant rufé, je vous ai pris par fineffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 17, 'Ai-je donc fait mon profit de vous, par aucun de ceux que je vous ai envoyez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 18, 'J\'ai prié Tite, &amp; ai envoyé un frere avec lui. Tite a-t\'il fait fon profit de vous ? N\'avons-nous pas cheminé d\'un mefme efprit ? N\'avons-nous pas cheminé d\'un mefme train ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 19, 'Penfez-vous derechef que nous mettions en avant nos deffenfes envers-vous ? Nous parlons devant Dieu en Chrift : &amp; le tout, ô tres-chers ! Pour voftre edification.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 20, 'Car je crains qu\'il n\'advienne, que quand je viendrai je ne vous trouve point tels que je voudrois : &amp; que je fois trouvé de vous tel que vous ne voudriez pas : &amp; qu\'il n\'y ait en quelque forte querelles, envies, courroux, debats, detractions, murmures, enfleures, tumultes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 21, 'Et qu\'eftant derechef venu, mon Dieu ne m\'abbaiffe envers vous : &amp; que je ne mene deüil de plufieurs d\'entre ceux qui ont peché auparavant, &amp; qui ne fe font point amendez de l\'ordure, &amp; de la paillardiffe, &amp; de l\'infolence qu\'ils ont commife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 1, 'C\'EST ici la troifiéme fois que je viens à vous. En la bouche de deux ou trois tefmoins toute parole fera confirmée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 2, 'J\'ai déja dit, &amp; je le dis dés cette heure, comme fi j\'eftois prefent pour la feconde fois, &amp; maintenant abfent j\'efcris à ceux qui ont peché auparavant : &amp; à tous les autres, que fi je viens derechef, je n\'efpargnerai perfonne :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 3, 'Puis que vous cherchez l\'experience de Chrift qui parle en moi, lequel n\'eft point foible envers vous, mais eft puiffant en vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 4, 'Car encore qu\'il ait efté crucifié par infirmité, neantmoins eft vivant par la vertu de Dieu : certes auffi nous fommes foibles en lui, mais nous vivrons avec lui, par la puiffance de Dieu envers vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 5, 'Examinez-vous vous mefmes fi vous eftes en la foi : efprouvez-vous vous-mefmes : ne vous reconnoiffez-vous point vous mefmes, affavoir que Jefus-Chrift eft en vous ? Si ce n\'eft qu\'en quelque forte vous fuffiez reprouvez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 6, 'Mais j\'efpere que vous connoiftrez que nous fommes point reprouvez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 7, 'Or je prie Dieu que vous ne faffiez aucun mal : non point afin que nous foyons trouvez approuvez, mais afin que vous faffiez ce qui eft bon, &amp; que nous foyons comme reprouvez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 8, 'Car nous ne pouvons rien contre la verité, mais pour la verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 9, 'Car nous nous éjouïffons fi nous fommes foibles, &amp; que vous foyez forts, &amp; mefmes nous fouhaitons cela, affavoir voftre entier accompliffement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 10, 'Partant j\'efcris ces chofes eftant abfent, afin que quand je ferai prefent, je n\'ufe point de rigueur, felon la puiffance laquelle le Seigneur m\'a donnée à édification, &amp; non point à deftruction.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 11, 'Au furplus, freres, éjouïffez-vous, tendez à vous rendre parfaits, foyez confolez, foyez tous d\'un confentement, vivez en paix, &amp; le Dieu de dilection &amp; de paix fera avec vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 12, 'Saluëz-vous l\'un l\'autre d\'un faint baifer. Tous les Saints vous faluënt.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 13, 'La grace du Seigneur Jefus Chrift, &amp; la dilection de Dieu, &amp; la communication du Saint Efprit foit avec vous tous. Amen. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 14, '');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 1, 'PAUL Apoftre (non point de par les hommes, mais de par Jefus Chrift, &amp; de par Dieu le Pere, qui l\'a reffufcité des morts.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 2, 'Et tous les freres qui font avec moi, aux Eglifes de Galatie :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 3, 'Grace vous foit &amp; paix de par Dieu le Pere, &amp; de par noftre Seigneur Jefus Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 4, 'Qui s\'eft donné foi-mefme pour nos pechez, afin que felon la volonté de Dieu noftre Pere il nous retiraft du prefent fiecle mauvais.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 5, 'Auquel foit gloire aux fiecles des fiecles. Amen.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 6, 'Je m\'eftonne qu\'en delaiffant Chrift qui vous avoit appellez par grace, vous eftes fubitement tranfportez à un autre Evangile :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 7, 'Qui n\'eft pas un autre Evangile : mais il y en a qui vous troublent &amp; qui veulent renverfer l\'Evangile de Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 8, 'Or quand bien nous-mefmes, ou un Ange du ciel, vous evangelizeroit outre ce que nous vous avons evangelizé, qu\'il foit execration.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 9, 'Ainfi que nous avons déja dit, maintenant auffi je le dis derechef, Si quelqu\'un vous evangelize outre ce que vous avez receu, qu\'il foit execration.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 10, 'Car maintenant prefchai-je les hommes, ou Dieu ? Ou cherchai-je à complaire aux hommes ? Car fi je complaifois encore aux hommes, je ne ferois point ferviteur de Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 11, 'Or, freres, je vous declare que l\'Evangile qui a efté annoncé par moi, n\'eft point felon l\'homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 12, 'Car je ne l\'ai ni receu ni apris d\'aucun homme, mais par la revelation de Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 13, 'Car vous avez entendu quelle a efté autrefois ma converfation au Judaïfme : comment je perfecutois l\'Eglife de Dieu à outrance, &amp; la degaftois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 14, 'Et avançois au Judaïfme plus que plufieurs de mes pareils en nation : eftant le plus ardent zelateur des traditions de mes peres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 15, 'Mais quand ç\'a efté le bon-plaifir de Dieu, (qui m\'avoit mis à part dés le ventre de ma mere, &amp; qui m\'a appellé par fa grace.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 16, 'De reveler fon Fils en moi, afin que je l\'evangelizaffe entre les Gentils, je ne pris point confeil incontinent de la chair &amp; du fang :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 17, 'Et ne revins point à Jerufalem vers ceux qui avoyent efté Apoftres devant moi : mais je m\'en allai en Arabie, &amp; derechef à Damas.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 18, 'Depuis trois ans apres, je revins à Jerufalem pour vifiter Pierre : &amp; demeurai chez lui quinze jours.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 19, 'Et ne vis nul autre des Apoftres, finon Jaques frere du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 20, 'Or des chofes que je vous efcris, voici je vous dis devant Dieu, que je ne mens point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 21, 'Depuis je vins és contrées de Syrie &amp; de Cilicie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 22, 'Or j\'eftois inconnu de face aux Eglifes de Judée, qui eftoyent en Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 23, 'Mais ils avoyent feulement ouï dire, Celui qui autrefois nous perfecutoit, annonce maintenant la foi, laquelle autrefois il deftruifoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 24, 'Dont ils glorifioyent Dieu en moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 1, 'DU depuis, quatorze ans pares, je montai derechef à Jerufalem avec Barnabas, &amp; pris auffi avec moi Tite.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 2, 'Or j\'y montai par revelation, &amp; conferai avec eux touchant l\'Evangile que je prefche entre les Gentils, mefmes en particulier avec ceux qui font en eftime : afin qu\'en quelque forte que ce foit je ne couruffe ou n\'euffe couru en vain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 3, 'Mais auffi Tite mefme qui eftoit avec moi, bien qu\'il fuft Grec, ne fuft point contraint d\'eftre circoncis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 4, 'Et cela à caufe des faux freres qui s\'eftoyent fourrez en l\'Eglife, lefquels eftoyent couvertement entrez pour efpier noftre liberté que nous avons en Jefus Chrift, afin de nous reduire en fervitude :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 5, 'Aufquels nous n\'avons cedé par fubmiffion quelconque, non pas mefme un moment : afin que la verité de l\'Evangile fuft permanente en vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 6, 'Et je n\'ai chofe aucune differente de ceux qui femblent eftre quelque chofe, quels qu\'ils ayent efté autrefois : car Dieu n\'accepte point l\'apparence exterieure de l\'homme : car ceux qui font en eftime, ne m\'ont rien apporté davantage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 7, 'Mais au contraire, quand ils ont veu que la predication de l\'Evangile du Prepuce m\'eftoit commife, comme celle de la Circoncifion, à Pierre :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 8, '(Car celui qui a operé avec efficace par Pierre en la charge d\'Apoftre envers la Circoncifion, a auffi operé avec efficace par moi envers les Gentils.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 9, 'Et Jaques, Cephas, &amp; Jean, (qui font eftimez eftre les colonnes) ayans connu la grace qui m\'eftoit donnée, me baillerent à moi &amp; à Barnabas la main d\'affociation : afin que nous allafsions vers les Gentils, &amp; eux vers ceux de la Circoncifion.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 10, 'Seulement que nous euffions fouvenance des pauvres : ce que je me fuis auffi eftudié de faire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 11, 'Et quant Pierre fut venu à Antioche, je lui refiftai en face, parce qu\'il eftoit à reprendre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 12, 'Car devant que quelques-uns fuffent venus de la part de Jaques, il mangeoit avec les Gentils : mais quand ceux-là furent venus, il s\'en retira, &amp; fe fepara, craignant ceux qui eftoyent de la Circoncifion.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 13, 'Et les autres Juifs ufoyent auffi de diffimulation comme lui, tellement que Barnabas mefme fe laiffoit emporter à leur diffimulation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 14, 'Mais quand je vis qu\'ils ne cheminoyent point de droit pied felon la verité de l\'Evangile, je dis à Pierre devant tous, Si toi qui es Juif, vis comme les Gentils, &amp; non comme les Juifs, pourquoi contrains-tu les Gentils à judaïzer ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 15, 'Nous qui fommes Juifs de nature, &amp; non point pecheurs d\'entre les Gentils :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 16, 'Sçachans que l\'homme n\'eft point juftifié par les œuvres de la Loi, mais feulement par la foi de Jefus Chrift ; nous auffi avons creu en Jefus Chrift, afin que nous fuffions juftifiez par la foi de Chrift, &amp; non point par les œuvres de la Loi : parce que nulle chair ne fera juftifiée par les œuvres de la Loi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 17, 'Or fi en cherchant d\'eftre juftifiez par Chrift, nous fommes auffi trouvez pecheurs, Chrift eft-il pourtant miniftre de peché ? Ainfi n\'advienne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 18, 'Car fi je reédifie les chofes que j\'ai deftruites, je me conftituë moi-mefme tranfgreffeur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 19, 'Car par la Loi je fuis mort à la Loi : afin que je vive à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 20, 'Je fuis crucifié avec Chrift, &amp; vis, non point maintenant moi, mais Chrift vit en moi : &amp; ce que je vis maintenant en la chair, je vis en la foi du Fils de Dieu, qui m\'a aimé, &amp; qui s\'eft donné foi-mefme pour moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 21, 'Je n\'aneantis point la grace de Dieu : car fi la juftice eft par la Loi, Chrift donc eft mort pour neant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 1, 'O Galates infenfez ! Qui vous a enforcelez pour faire que vous n\'obeïffiez point à la verité, aufquels Jefus Chrift a efté ci-devant pourtrait devant les yeux, &amp; crucifié entre-vous ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 2, 'Je voudrois feulement entendre ceci de vous, Avez-vous receu l\'Efprit par les œuvres de la Loi, ou par la predication de la foi ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 3, 'Eftes-vous fi infenfez, qu\'en ayant commencé par l\'Efprit, maintenant vous acheviez par la chair ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 4, 'Avez-vous tant fouffert en vain ? Voire fi c\'eft mefmes en vain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 5, 'Celui donc qui vous fournit l\'Efprit, &amp; qui produit les vertus en vous, le fait-il par les œuvres de la Loi, ou par la predication de la foi ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 6, 'Ainfi qu\'Abraham a creu à Dieu, &amp; il lui a efté alloüé à juftice.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 7, 'Sçachez donc que ceux qui font de la foi, font enfans d\'Abraham.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 8, 'Dont l\'Ecriture prevoyant que Dieu juftifie les Gentils par la foi, a devant evangelizé à Abraham, difant, Toutes les nations feront benites en toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 9, 'C\'eft pourquoi ceux qui font de la foi, font benits avec le fidele Abraham.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 10, 'Car tous ceux qui font des œuvres de la Loi, font fous la malediction : car il eft efcrit, Maudit est quiconque n\'eft permanent en toutes les chofes qui font efcrites au livre de la Loi pour les faire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 11, 'Or que par la Loi nul ne foit juftifié envers Dieu, il appert dautant que le jufte vivra de foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 12, 'Or la Loi n\'eft point de la foi : mais l\'homme qui aura fait ces chofes, vivra par elles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 13, 'Chrift nous a rachetez de la malediction de la Loi, quand il a efté fait malediction pour nous : (car il eft efcrit, Maudit est quiconque pend au bois.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 14, 'Afin que la benediction d\'Abraham advint aux Gentils par Jefus Chrift ? A ce que nous recevions la promeffe de l\'Efprit par la foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 15, 'Freres, je parle à la façon des hommes. Bien qu\'une alliance foit d\'un homme, fi elle eft confirmée, nul ne la caffe, ou y adjoufte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 16, 'Or les promeffes ont efté dites à Abraham, &amp; à fa femence. Il ne dit point, Et aux femences, comme parlant de plufieurs : mais comme d\'une. Et à ta femence, qui eft Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 17, 'Voila donc ce que je dis, quant à l\'alliance qui auparavant à efté confirmé de Dieu en Chrift, que la Loi qui eft venuë quatre cens &amp; trente ans apres, ne la peut enfreindre, pour abolir la promeffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 18, 'Car fi l\'heritage est de la Loi, il n\'eft plus par la promeffe. Or Dieu l\'a donné à Abraham par la promeffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 19, 'A quoi donc fert la Loi ? Elle a efté adjouftée à caufe des tranfgreffions, jufques à ce que la femence vinft, au regard de laquelle la promeffe avoit efté faite : &amp; a efté ordonnée par les Anges par la main d\'un Moyenneur :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 20, 'Or le Moyenneur n\'eft point d\'un feul : mais Dieu eft un feul');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 21, 'La Loi donc a-t\'elle efté adjouftée contre les promeffes de Dieu ? Ainfi n\'advienne : car fi la Loi euft efté donnée pour pouvoir vivifier, vrayement la juftice feroit de la Loi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 22, 'Mais l\'Efcriture a tout enclos fous le peché, afin que la promeffe par la foi de Jefus Chrift fuft donnée aux croyans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 23, 'Or devant que la foi vinft, nous eftions gardez fous la Loi, eftans enclos fous l\'attente de la foi qui devoit eftre revelée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 24, 'La Loi donc a efté noftre pedagogue pour nous amener à Chrift, afin que nous foyons juftifiez par le foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 25, 'Mais la foi eftant venuë, nous ne fommes plus fous le pedagogue.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 26, 'Car vous eftes tous enfans de Dieu par la foi en Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 27, 'Car vous tous, qui avez efté baptizez en Chrift, avez efté reveftus de Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 28, 'Là où il n\'y ni Juif ni Grec : là où il n\'y a ni ferf ni franc : là où il n\'y a ni mafle ni femelle : car vous eftes tous un en Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 29, 'Que fi vous eftes de Chrift, vous eftes donc la femence d\'Abraham, &amp; heritiers felon la promeffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 1, 'OR je dis que durant tout le temps que l\'heritier eft enfant, il n\'eft different en rien du ferf, bien qu\'il foit feigneur de tout.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 2, 'Mais il eft fous des tuteurs &amp; des curateurs, jufques au temps determiné par le pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 3, 'Nous auffi pareillement, lors que nous eftions enfans, nous eftions affervis fous les rudimens du monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 4, 'Mais quand l\'accompliffement du temps eft venu, Dieu a envoyé fon Fils, fait de femme, &amp; fait fujet à la Loi :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 5, 'Afin qu\'il rachetaft ceux qui estoyent fous la Loi : afin que nous receuffions l\'adoption des enfans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 6, 'Et parce que vous eftes enfans, Dieu a envoyé l\'Efprit de fon Fils en vos cœurs, criant, Abba, Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 7, 'Maintenant donc tu n\'es plus ferf, mais fils : que fi tu es fils, tu es auffi heritier de Dieu par Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 8, 'Mais lors que vous ne connoiffiez point Dieu, vous ferviez ceux qui de nature ne font dieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 9, 'Or maintenant, puis que vous avez connu Dieu, ou pluftoft que vous avez efté connus de Dieu, comment retournez-vous derechef aux rudimens foibles &amp; pauvres, aufquels vous voulez derechef fervir comme auparavant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 10, 'Vous obfervez les jours, &amp; les mois, &amp; les temps, &amp; les années.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 11, 'Je crains de vous que d\'adventure je n\'aye travaillé en vain envers vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 12, 'Soyez comme moi : car je fuis auffi comme vous : je vous en prie, freres : vous ne m\'avez fait aucun tort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 13, 'Et vous fçavez comme par ci-devant avec infirmité de la chair, je vous ai evangelizé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 14, 'Et vous n\'avez point mefprifé ni rejetté mon efpreuve, qui eftoit en ma chair : mais vous m\'avez receu comme un Ange de Dieu, voire comme Jefus Chrift mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 15, 'Quel eftoit donc le tefmoignage de voftre beatitude ? Car je vous rends témoignage, que s\'il euft efté poffible, vous euffiez arraché vos yeux &amp; me les euffiez donnez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 16, 'Suis-je donc devenu voftre ennemi, en vous difant la verité ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 17, 'Ils font jaloux de vous, mais non pas de bonne forte : au contraire ils vous veulent exclurre, afin que vous foyez jaloux d\'eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 18, 'Mais il eft bon d\'eftre toûjours jaloux en bien, &amp; non feulement quand je fuis prefent avec vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 19, 'Mes petits enfans, pour lefquels enfanter je travaille derechef, jufques à ce que Chrift foit formé en vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 20, 'Je voudrois maintenant eftre avec vous, &amp; changer ma parole : car je fuis en perplexité quant à vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 21, 'Dites-moi vous qui voulez eftre fous la Loi, n\'oyez-vous point la Loi ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 22, 'Car il eft efcrit qu\'Abraham a eu deux fils, l\'un de la fervante, &amp; l\'autre de la franche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 23, 'Mais celui qui eftoit de la fervante, nafquit felon la chair, &amp; celui qui eftoit de la franche, par la promeffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 24, 'Lefquelles chofes doivent eftre entendues par allegorie : car ce font les deux alliances l\'une au mont de Sina, engendrant à fervitude, qui eft Agar :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 25, 'Car ce nom d\'Agar veut dire Sina, qui eft une montagne en Arabie, &amp; correfpondante à la Jerufalem de maintenant, laquelle fert avec fes enfans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 26, 'Mais la Jerufalem d\'en haut eft franche, laquelle eft la mere de nous tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 27, 'Car il eft écrit, Réjoüi toi, fterile, qui n\'enfantois point : efforce toi &amp; t\'efcrie, toi qui n\'eftois point en travail d\'enfant : car il y a beaucoup plus d\'enfans de la delaiffée que de celle qui avoit un mari.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 28, 'Or quant à nous, mes freres, nous fommes enfans de la promeffe, ainfi qu\'Ifaac.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 29, 'Mais comme alors celui qui eftoit né felon la chair, perfecutoit celui qui estoit né felon l\'Efprit, pareillement auffi maintenant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 30, 'Mais que dit l\'Ecriture ? Jette hors la fervante &amp; fon fils : car le fils de la fervante ne fera point heritier avec le fils de la franche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 31, 'Or, freres, nous fommes point enfans de la fervante, mais de la franche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 1, 'TENEZ-vous donc fermes en la liberté de laquelle Chrift nous a affranchis, &amp; ne foyez point derechef retenus du joug de fervitude.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 2, 'Voici, moi Paul vous dis, que fi vous eftes circoncis, Chrift ne vous profitera de rien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 3, 'Et derechef je protefte à tout homme qui fe circoncit, qu\'il eft obligé d\'accomplir toute la Loi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 4, 'Chrift eft aneanti à l\'efgard de vous tous qui voulez eftre juftifiez par la Loi, &amp; vous eftes décheus de la grace.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 5, 'Car par la foi en Efprit nous attendons l\'efperance de juftice.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 6, 'Car en Jefus Chrift, ni circoncifion, ni prepuce n\'a aucune vertu, mais la foi ouvrante par la charité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 7, 'Vous couriez bien, qui vous a donné de l\'empefchement pour faire, que vous n\'obeïffiez point à la verité ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 8, 'Cette perfuafion ne vient point de celui qui vous appelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 9, 'Un peu de levain fait lever toute la pafte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 10, 'Je m\'affeure de vous au Seigneur, que vous n\'aurez point d\'autre fentiment : mais celui qui vous trouble en portera la condamnation, quiconque foit-il.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 11, 'Et quant à moi, freres, fi je prefche encore la circoncifion, pourquoi fouffré-je encore perfecution ? Le fcandale de la croix eft donc aboli.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 12, 'A la mienne volonté que ceux qui vous mettent en trouble fuffent retranchez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 13, 'Car, freres, vous avez efté appellez à la liberté : feulement ne prenez point une telle liberté pour une occafion de vivre felon la chair : mais fervez l\'un à l\'autre par charité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 14, 'Car toute la Loi eft accomplie en une feul parole, affavoir en celle-ci, Tu aimeras ton prochain comme toi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 15, 'Que fi vous vous entremordez &amp; entrerongez, gardez que vous ne foyez confumez l\'un par l\'autre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 16, 'Or je vous dis, cheminez felon l\'Efprit : &amp; vous n\'accomplirez point les convoitifes de la chair.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 17, 'Car la chair convoite contre l\'Efprit, &amp; l\'Efprit contre la chair : &amp; ces chofes-là font oppofées l\'une à l\'autre : tellement que vous ne faites point les chofes que vous voudriez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 18, 'Que fi vous eftes conduits par l\'Efprit, vous n\'eftes point fous la Loi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 19, 'Car les œuvres de la chair font manifeftes, lefquelles font adultere, paillardife, fouilleure, infolence,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 20, 'Idolatrie, empoifonnement, inimitiez, querelles, dépits, coleres, contentions, divifions, herefies,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 21, 'Envies, meurtres, yvrogneries, gourmandifes, &amp; chofes femblables à celle-là : defquelles je vous predis, comme auffi j\'ai predit, que ceux qui commettent de telles chofes n\'heriteront point le royaume de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 22, 'Mais le fruict de l\'Efprit eft charité, joye, paix, efprit patient, benignité, bonté, loyauté, douceur, temperance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 23, 'La Loi ne s\'adreffe point contre de telles chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 24, 'Or ceux qui font de Chrift, ont crucifié la chair avec les affections &amp; les convoitifes d\'icelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 25, 'Si nous vivons par l\'Efprit, cheminons auffi en Efprit :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 26, 'Ne foyons point convoiteux de vaine gloire, nous provoquans l\'un l\'autre, nous portans de l\'envie l\'un à l\'autre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 1, 'FRERES, encore qu\'un homme eft furpris en quelque faute, vous qui eftes fpirituels, redreffez un tel homme avec un efprit de douceur, te confiderant toi-mefme que tu ne fois auffi tenté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 2, 'Portez les charges les uns des autres, &amp; ainfi accompliffez la Loi de Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 3, 'Car fi quelqu\'un penfe eftre quelque chofe, encore qu\'il ne foit rien, il fe deçoit foi-mefme par fa fantaifie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 4, 'Mais chacun efprouve fon œuvre : &amp; alors il aura dequoi fe glorifier en foi-mefme feulement, &amp; non point en autrui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 5, 'Car chacun portera fon propre fardeau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 6, 'Que celui qui eft enfeigné en la Parole faffe participant de tous fes biens celui qui l\'enfeigne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 7, 'Ne vous abufez point. Dieu ne peut eftre mocqué : car ce que l\'homme aura femé, il le moiffonnera auffi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 8, 'Car celui qui feme à fa chair, il moiffonnera auffi de la chair, la corruption : mais celui qui feme à l\'Efprit, il moiffonnera, de l\'efprit la vie eternelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 9, 'Or ne nous laffons point en bien faifant : car nous moiffonnerons en la propre faifon, fi nous ne devenons lafches.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 10, 'C\'eft pourquoi pendant que nous avons le temps, faifons du bien à tous : mais principalement aux domeftiques de la foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 11, 'Vous voyez quelles grandes lettres je vous ai efcrites de ma propre main.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 12, 'Tous ceux qui cherchent une belle apparence en la chair, font ceux qui vous contraignent d\'eftre circoncis : afin feulement qu\'ils n\'endurent point de perfecution pour la croix de Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 13, 'Car ceux-là mefmes qui font circoncis ne gardent point la Loi : mais ils veulent que vous foyez circoncis, afin qu\'ils fe glorifient en voftre chair.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 14, 'Mais pour moi, je ne m\'advienne que je me glorifie, finon en la croix de noftre Seigneur Jefus Chrift, par laquelle le monde m\'eft crucifié, &amp; moi au monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 15, 'Car en Jefus Chrift, ni circoncifion, ni prepuce n\'a aucune vertu, mais la nouvelle creature.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 16, 'Et tous ceux qui marcheront felon cette regle, paix &amp; mifericorde foit fur eux, &amp; fur l\'Ifraël de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 17, 'Au refte que nul ne me donne de la facherie : car je porte en mon corps le fleftriffures du Seigneur Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 18, 'Freres, la grace de noftre Seigneur Jefus Chrift foit avec voftre efprit. Amen. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 1, 'PAUL Apoftre de Jefus Chrift par la volonté de Dieu, aux Saints &amp; Fideles en Jefus Chrift, qui font à Ephefe :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 2, 'Grace vous foit &amp; paix de par Dieu noftre Pere, &amp; de par le Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 3, 'Benit foit Dieu, qui eft le Pere de noftre Seigneur Jefus Chrift, qui nous a benits en toute benediction fpirituelle, és lieux celeftes en Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 4, 'Selon qu\'il nous avoit éleus en lui, devant la fondation du monde, afin que nous fuffions faincts &amp; irreprehenfibles devant lui en charité :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 5, 'Nous ayans predeftinez pour nous adopter à foi par Jefus Chrift, felon le bon plaifir de fa volonté :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 6, 'A la loüange de la gloire de fa grace, de laquelle il nous a rendus agreables en fon bien-aimé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 7, 'En qui nous avons redemption des offenfes, felon les richeffes de fa grace :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 8, 'Laquelle il a fait largement abonder fur nous en toute fapience &amp; intelligence :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 9, 'Nous ayant donné à connoiftre le fecret de fa volonté, felon fon bon plaifir, lequel il avoit premierement arrefté en foi :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 10, 'Afin qu\'en la difpenfation de l\'accompliffement des temps, il recueillift enfemble le tout en Chrift, tant les chofes qui font aux cieux que celles qui font en la terre, en lui mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 11, 'En qui auffi nous fommes faits fon heritage, ayans efté predeftinez, fuivant le propos arrefté de celui qui accomplit en efficace toutes chofes felon le confeil de fa volonté :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 12, 'Afin que nous foyons à la loüange de fa gloire, nous qui avons les premiers efperé en Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 13, 'En qui vous eftes auffi, ayans ouï la parole de verité, affavoir l\'Evangile de voftre falut : auquel auffi ayans creu vous avez efté feellez du S. Efprit de la promeffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 14, 'Lequel eft l\'arrhe de noftre heritage, jusques à la redemption de la poffeffion acquife, à la loüange de fa gloire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 15, 'Pour cette caufe moi auffi ayant entendu la foi que vous avez au Seigneur Jefus, &amp; la charité que vous avez envers tous les Saints,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 16, 'Je ne ceffe de rendre graces pour vous, faifant mention de vous en mes prieres :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 17, 'Afin que le Dieu de noftre Seigneur Jefus Chrift, le Pere de gloire, vous donne l\'Efprit de fapience &amp; de revelation, par la connoiffance d\'icelui :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 18, 'Affavoir les yeux de voftre entendement illuminez, afin que vous fçachiez quelle eft l\'efperance de fa vocation : &amp; quelles font les richeffes de la gloire de fon heritage és Saints :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 19, 'Et quelle eft l\'excellente grandeur de fa puiffance envers nous qui croyons, felon l\'efficace de la puiffance de fa force :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 20, 'Laquelle il a déployée avec efficace en Chrift, quand il l\'a reffufcité des morts, &amp; l\'a fait feoir à fa dextre és lieux celeftes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 21, 'Par deffus toute principauté, &amp; puiffance, &amp; vertu, &amp; feigneurie, &amp; par deffus tout nom qui fe nomme, non feulement en ce fiecle, mais auffi en celui qui eft à venir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 22, 'Et a affujetti toutes chofes fous les pieds d\'icelui, &amp; l\'a donné fur toutes chofes pour eftre chef à l\'Eglife :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 23, 'Laquelle eft fon corps, &amp; l\'accompliffement de celui qui accomplit tout en tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 1, 'ET lors que vous eftiez morts en vos fautes &amp; pechez,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 2, 'Efquels autrefois vous avez cheminé, fuivant le train de ce monde, felon le prince de la puiffance de l\'air, qui eft l\'efprit qui opere maintenant avec efficace és enfans de rebellion :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 3, 'Entre lefquels auffi nous avons tous converfé autrefois és convoitifes de noftre chair, accompliffans les defirs de la chair &amp; de nos penfées : &amp; nous eftions de nature enfans d\'ire, comme auffi les autres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 4, 'mais Dieu, qui eft riche en mifericorde, par fa grande charité de laquelle il nous a aimez,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 5, 'Du temps mefme que nous eftions morts en nos fautes, nous a vivifiez enfemble avec Chrift, par la grace duquel vous eftes fauvez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 6, 'Et nous a reffufcité enfemble, &amp; nous a fait feoir enfemble és lieux celeftes en Jefus Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 7, 'Afin qu\'il montraft és fiecles à venir les abondamment excellentes richeffes de fa grace, par fa benignité envers nous en Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 8, 'Car vous eftes fauvez par grace, par la foi : &amp; cela non point de vous, c\'est le don de Dieu :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 9, 'Non point par les œuvres, afin que nul ne fe glorifie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 10, 'Car nous fommes fon ouvrage, eftans créez en Jefus Chrift à bonnes œuvres, que Dieu a preparées, afin que nous cheminions en elles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 11, 'Partant, ayez fouvenance que vous qui eftiez autrefois Gentils en la chair, &amp; eftiez appellez Prepuce par celle qui eft appellée la Circoncifion, faite de main en la chair,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 12, 'Eftiez en ce tems-là hors de Chrift, n\'ayans rien de commun avec la republique d\'Ifraël ; eftant eftrangers des alliances de la promeffe, n\'ayans point d\'efperance, &amp; eftans fans Dieu au monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 13, 'Mais maintenant par Jefus Chrift, vous qui eftiez autrefois loin, eftes approchez par le fang de Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 14, 'Car il eft noftre paix, qui de tous les deux en a fait un, ayant rompu la clofture de la paroi entre-moyenne :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 15, 'Ayant aboli en fa chair l\'inimité, affavoir la Loi des commandemens, qui confifte en ordonnances : afin qu\'il creaft les deux en foi-mefme, en un homme nouveau, en faifant la paix :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 16, 'Et qu\'il ralliaft les uns &amp; les autres en un corps à Dieu, par la croix, ayant deftruit en elle l\'inimitié.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 17, 'Et eftant venu il a evangelizé la paix à vous qui eftiez loin, &amp; à ceux qui eftoyent prés.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 18, 'Car nous avons par lui, les uns &amp; les autres, accez au Pere en un mefme Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 19, 'Vous n\'eftes donc plus eftrangers ni forains, mais combourgeois des Saints, &amp; domeftiques de Dieu :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 20, 'Eftans edifiez fur le fondement des Apoftres &amp; des Prophetes, Jefus Chrift lui-mefme eftant la maiftreffe pierre du coin :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 21, 'En qui tout l\'edifice rapporté &amp; ajufté enfemble, fe leve pour eftre un temple fainct au Seigneur :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 22, 'En qui vous auffi eftes enfemble edifiez, pour eftre un tabernacle de Dieu en Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 1, 'POUR cette caufe, moi Paul fuis le prifonnier de Jefus Chrift pour vous les Gentils :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 2, 'Voire fi vous avez entendu la difpenfation de la grace de Dieu, qui m\'a efté donnée envers vous :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 3, 'Comment par revelation il m\'a donné à connoiftre le myftere : (ainfi que par ci-devant je vous ai efcrit en peu de mots :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 4, 'D\'où le lifant vous pouvés connoiftre l\'intelligence que j\'ai au myftere de Chrift.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 5, 'Lequel n\'a point efté donné à connoiftre aux enfans des hommes és autres âges, ainfi que maintenant il a efté revelé par l\'Efprit à fes faints Apoftres &amp; Prophetes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 6, 'Affavoir que les Gentils font coheritiers, &amp; d\'un mefme corps, &amp; participans enfemble de la promeffe d\'icelui en Jefus Chrift, par l\'Evangile.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 7, 'Duquel j\'ai efté fait miniftre, felon le don de la grace de Dieu, lequel m\'a efté donné fuivant l\'efficace de fa puiffance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 8, 'Cette grace m\'a efté donnée à moi qui fuis le moindre de tous les Saincts, pour annoncer entre les Gentils les richeffes incomprehenfibles de Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 9, 'Et pour mettre en evidence devant tous quelle eft la communication du myftere, qui eftoit caché de tout temps en Dieu, qui a creé toutes chofes par Jefus Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 10, 'Afin que la fapience de Dieu, qui eft diverfe en toutes fortes, foit maintenant donnée à connoiftre aux Principautez &amp; aux Puiffances, és lieux celeftes, par l\'Eglife :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 11, 'Suivant le propos arrefté dés les fiecles, lequel il a eftabli en Jefus Chrift noftre Seigneur :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 12, 'Par lequel nous avons hardieffe &amp; accez en confiance, par la foi que nous avons en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 13, 'C\'eft pourquoi je requiers que vous ne vous laffiez point à caufe de mes tribulations que j\'endure pour l\'amour de vous ; ce qui eft voftre gloire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 14, 'Pour laquelle caufe je ploye mes genoux, devant le Pere de noftre Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 15, '(Duquel toute la parenté eft nommée és cieux &amp; en la terre.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 16, 'Afin que felon les richeffes de fa gloire, il vous donne d\'eftre puiffament fortifiez par fon Efprit en l\'homme interieur :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 17, 'Tellement que Chrift habite en vos cœurs par la foi :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 18, 'Afin qu\'eftant enracinez &amp; fondez en charité, vous puiffiez finalement comprendre avec tous les Saincts, quelle est la largeur &amp; la longueur, &amp; la profondeur &amp; la hauteur ;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 19, 'Et connoiftre la dilection de Chrift, laquelle furpaffe toute connoiffance : afin que vous foyez remplis en toute plenitude de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 20, 'Or à celui qui par la puiffance qui agit en nous avec efficace, peut faire en toute abondance par deffus tout ce que nous demandons &amp; penfons,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 21, 'A lui foit gloire en l\'Eglife, en Jefus Chrift, en tous les âges du fiecle des fiecles. Amen.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 1, 'JE vous prie donc, moi le prifonnier au Seigneur, que vous cheminez dignement comme il eft féant à la vocation à la vocation à laquelle vous eftes appellés :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 2, 'Avec toute humilité &amp; douceur, avec un efprit patient, fupportans l\'un l\'autre en charité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 3, 'Eftans foigneux de garder l\'unité de l\'Efprit par le lien de la paix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 4, 'Il y a un feul corps &amp; un feul Efprit, comme auffi vous eftes appellez en une feule efperance de voftre vocation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 5, 'Il y a un feul Seigneur, une feule foi, un feul Baptefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 6, 'Un feul Dieu &amp; Pere de tous, qui eft fur tous, &amp; parmi tous, &amp; en nous tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 7, 'Mais la grace eft donnée à chacun de nous, felon la mefure du don de Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 8, 'Pour laquelle chofe il dit, Eftant monté en haut il a mené captive une grande multitude de captifs, &amp; a donné des dons aux hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 9, 'Or ce qu\'il eft monté, qu\'eft-ce autre chofe finon que premierement il eftoit defcendu és parties les plus baffes de la terre ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 10, 'Celui qui eft defcendu, c\'eft le mefme qui eft monté fur tous les cieux, afin qu\'il remplift toutes chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 11, 'Lui mefme donc a donné les uns pour eftre Apoftres, &amp; les autres pour eftre Prophetes, &amp; les autres pour eftre Evangeliftes, &amp; les autres pour eftre Pafteurs &amp; Docteurs :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 12, 'Pour l\'affemblage des Saints, pour l\'œuvre du miniftere, pour l\'edification du corps de Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 13, 'Jufques à ce que nous nous rencontrions tous en l\'unité de la foi, &amp; de la connoiffance du Fils de Dieu, en homme parfait, à la mefure de la parfaite ftature de Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 14, 'Afin que nous ne foyons plus enfans, flottans, &amp; eftans demeurez çà &amp; là à tous vents de doctrine, par la piperie des hommes, &amp; par leur rufe à cauteleufement feduire :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 15, 'Mais afin que fuivans la verité avec charité, nous croiffions en tout en celui qui eft le chef, affavoir Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 16, 'Duquel tout le corps bien ajufté &amp; ferré enfemble par toutes les jointures du fourniffement, prend l\'accroiffement du corps, felon la vigueur qui eft en la mefure de chaque partie, pour l\'edification de foi-mefme en charité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 17, 'Voici donc ce que je dis &amp; attefte de par le Seigneur, c\'eft que vous ne cheminez plus comme auffi le refte des Gentils chemine en la vanité de leur penfée :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 18, 'Ayans leur entendement obfcurci de tenebres, &amp; eftans eftrangez de la vie de Dieu, à caufe de l\'ignorance qui eft en eux, par l\'endurciffement de leur cœur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 19, 'Lefquels ayans perdu tout fentiment, fe font abandonnez à diffolution, pour commettre toute fouilleure, à qui en feroit pis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 20, 'Mais vous n\'avez point ainfi appris Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 21, 'Voire fi vous l\'avez efcouté, &amp; fi vous avez efté enfeignez de par lui, ainfi que la verité eft en Jefus :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 22, 'Affavoir que vous defpouilliez le vieil homme, quant à la converfation precedente, lequel fe corrompt par les convoitifes qui feduifent :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 23, 'Et que vous foyez renouvellez en l\'efprit de voftre entendement :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 24, 'Et que vous foyez reveftus du nouvel homme, creé felon Dieu en juftice &amp; vraye fainteté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 25, 'C\'eft pourquoi ayans defpouillé le menfonge, parlez en verité chacun avec fon prochain : car nous fommes membres les uns des autres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 26, 'Courroucez-vous, &amp; ne pechez point : que le Soleil ne fe couche point fur voftre courroux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 27, 'Et ne donnez point de lieu au diable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 28, 'Que celui qui defroboit, ne defrobbe plus, mais pluftoft qu\'il travaille en ouvrant de fes mains en ce qui eft bon : afin qu\'il ait pour departir à celui qui en a befoin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 29, 'Que nul propos infect ne forte de voftre bouche : mais celui qui eft bon à l\'ufage d\'edification, afin qu\'il donne grace à ceux qui l\'oyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 30, 'Et ne contriftez point le Saint Efprit de Dieu, par lequel vous avez efté feellez pour le jour de la redemption.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 31, 'Que toute amertume, &amp; colere, &amp; ire, &amp; crierie, &amp; mefdifance, foyent oftées de vous, avec toute malice.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 32, 'Mais foyez benins les uns envers les autres, pleins de compaffion, &amp; pardonnans les uns aux autres, ainfi auffi que Dieu vous a pardonné par Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 1, 'SOYEZ donc imitateurs de Dieu, comme chers enfans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 2, 'Et cheminez en charité, ainfi que Chrift auffi nous a aimez, &amp; s\'eft donné foi-mefme pour nous, en oblation &amp; facrifice à Dieu, en odeur de bonne fenteur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 3, 'Que la paillardife, &amp; toute fouileure, ou avarice, ne foit pas mefmes nommé entre vous, ainfi qu\'il appartient aux Saints :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 4, 'Ni chofes vilaine, ni parole folle, ni plaifanterie, qui font chofes qui ne font pas bien feantes : mais pluftoft action de graces.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 5, 'Car vous fçavez ceci, que nul paillard, ou immonde, ou avaricieux, qui eft idolatre, n\'a point d\'heritage au royaume de Chrift &amp; de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 6, 'Que nul ne vous feduife par de vains difcours : car pour ces chofes l\'ire de Dieu vient fur les enfans de rebellion.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 7, 'Ne foyez donc point leurs compagnons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 8, 'Car vous eftiez autrefois tenebres : mais maintenant vous eftes lumiere au Seigneur : cheminez comme enfans de lumiere :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 9, '(Car le fruit de l\'Efprit confifte en toute debonnaireté, juftice, &amp; verité.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 10, 'Efprouvans ce qui eft agreable au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 11, 'Et ne communiquez point aux œuvres infructueufes de tenebres, mais mefmes redarguez-les pluftoft.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 12, 'Car il eft mefmes deshonnefte de dire les chofes qui font faites par eux en cachette.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 13, 'Mais toutes chofes eftant redarguées par la lumiere, font renduës manifeftes : car la lumiere eft celle qui manifefte tout.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 14, 'A caufe dequoi il dit, Reveille-toi, toi qui dors, &amp; te releve des morts, &amp; Chrift t\'éclairera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 15, 'Avifez donc comment vous cheminez foigneufement, non point comme eftant dénuez de fageffe, mais comme eftans fages :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 16, 'Rachetans le temps : car les jours font mauvais.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 17, 'C\'eft pourquoi ne foyez point fans prudence, mais bien entendans quelle eft la volonté du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 18, 'Et ne vous enyvrez point de vin, auquel il y a de la diffolution : mais foyez remplis de l\'Efprit :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 19, 'Parlans entre vous par Pfeaumes, loüange, &amp; chanfons fpirituelles : chantans &amp; pfalmodians en voftre cœur au Seigneur :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 20, 'Rendans toûjours graces pour toutes chofes, au non de noftre Seigneur Jefus Chrift, à noftre Dieu &amp; Pere :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 21, 'Vous foufmettans les uns aux autres en la crainte de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 22, 'Femmes, foyez fujettes à vos propres maris, comme au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 23, 'Car le mari eft le chef de la femme, ainfi que Chrift auffi eft le chef de l\'Eglife, &amp; pareillement eft le Sauveur de fon corps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 24, 'Comme donc l\'Eglife eft fujette à Chrift, que femblablement auffi les femmes le foyent à leurs propres maris, en toutes chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 25, 'Vous maris, aimez vos femmes, comme auffi Chrift a aimé l\'Eglife, &amp; s\'eft donné foi-mefme pour elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 26, 'Afin qu\'il la fanctifiaft, apres l\'avoir nettoyée par le lavement d\'eau par la parole :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 27, 'Afin qu\'il fe la rendift une Eglife glorieufe, n\'ayant point de tache, ni ride, ni autre telle chofe : mais afin qu\'elle fuft fainte &amp; irreprehenfible.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 28, 'Ainfi les maris doivent aimer leurs femmes comme leurs propres corps : Celui qui aime fa femme, s\'aime foi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 29, 'Car perfonne n\'eut jamais en haine fa chair, mais il la nourrit &amp; l\'entretient, comme auffi le Seigneur l\'Eglife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 30, 'Car nous fommes membres de fon corps, eftant de fa chair &amp; de fes os.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 31, 'Pour cela l\'homme delaiffera fon pere &amp; fa mere, &amp; s\'adjoindra à fa femme : &amp; les deux feront une mefme chair.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 32, 'Ce myftere eft grand : or je parle touchant Chrift &amp; l\'Eglife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 33, 'Partant quant à vous auffi, que chacun en fon endroit aime fa femme comme foi-mefme, &amp; que la femme revere fon mari.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 1, 'ENFANS, obeïffez à vos peres &amp; meres au Seigneur : car cela eft jufte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 2, 'Honore ton pere &amp; ta mere, (qui eft le premier commandement, avec promeffe.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 3, 'Afin que bien te foit, &amp; que tu fois de longue vie fur la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 4, 'Et vous peres, ne depitez point vos enfans : mais nourriffez-les en la difcipline &amp; remontrance du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 5, 'Serviteurs, obeïffez à ceux qui font vos maiftres felon la chair, avec crainte &amp; tremblement, en fimplicité de voftre cœur, comme à Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 6, 'Non point fervans à l\'œil, comme voulans complaire aux hommes, mais comme ferfs de Chrift, faifans de courage la volonté de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 7, 'Servans de bonne affection au Seigneur &amp; non point aux hommes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 8, 'Sçachans que chacun recevra du Seigneur le bien qu\'il aura fait, foit ferf, foit franc.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 9, 'Et vous maiftres, faites envers eux le femblable, relachans les menaces, fçachans que le Seigneur au tant d\'eux que de vous, eft és cieux : &amp; qu\'envers lui il n\'y a point d\'acception de perfonne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 10, 'Au refte, mes freres, fortifiez-vous au Seigneur, &amp; en la puiffance de fa force.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 11, 'Soyez reveftus de toutes les armures de Dieu, afin que vous puiffiez refifter contre les embufches du diable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 12, 'Car nous n\'avons point la luite contre le fang &amp; la chair, mais contre les Principautez, contre les Puiffances, contre les Seigneurs du monde, gouverneurs des tenebres de ce fiecle, contre les malices fpirituelles qui font és lieux celeftes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 13, 'C\'eft pourquoi prenez toutes les armures de Dieu, afin que vous puiffiez refifter au mauvais jour, &amp; ayans tout achevé demeurer fermes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 14, 'Soyez donc fermes, ayans vos reins ceints de verité, &amp; eftans reveftus de la cuiraffe de juftice :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 15, 'Et ayans les pieds chauffez de la preparation de l\'Evangile de paix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 16, 'Prenans fur tout le bouclier de la foi, par lequel vous puiffiez éteindre tous les dards enflammez du malin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 17, 'Prenez auffi le cafque de falut, &amp; l\'efpée de l\'Efprit, qui eft la Parole de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 18, 'Prians en toutes fortes de priere &amp; de requefte, en tout temps, en efprit, &amp; veillans à cela avec toute perfeverance &amp; requefte pour tous les Saints :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 19, 'Et pour moi, afin que la parole me foit donnée à bouche ouverte en hardieffe : afin de donner à connoiftre le myftere de l\'Evangile.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 20, 'Pour lequel je fuis ambaffadeur en la chaîne, afin que je parle franchement comme il me faut parler.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 21, 'Or afin que vous auffi entendiez mon eftat, &amp; ce que je fais, Tychique noftre frere bien-aimé, &amp; fidele miniftre au Seigneur, vous fera fçavoir le tout.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 22, 'Lequel je vous ai envoyé exprés pour cela, afin que vous entendiez noftre eftat, &amp; qu\'il confole vos cœurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 23, 'Paix foit aux freres, &amp; charité avec foi, de par Dieu le Pere, &amp; de par le Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 24, 'Grace foit avec tous ceux qui aiment noftre Seigneur Jefus Chrift en pureté. amen. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 1, 'PAUL &amp; Timothée ferviteurs de Jefus Chrift, à tous les Saints en Jefus Chrift, qui font à Philippes, avec les Evefques &amp; les Diacres :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 2, 'Grace vous foit &amp; paix de par Dieu noftre Pere, &amp; de par le Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 3, 'Je rends graces à mon Dieu toutes les fois que je fais mention de vous,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 4, 'Faifant toûjours des prieres avec joye pour vous tous, en toutes mes oraifons ;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 5, 'A caufe de la communion de l\'Evangile que vous avez demontrée, depuis le premier jour jufques à maintenant :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 6, 'Eftant affeuré de cela mefme, que celui qui a commencé cette bonne œuvre en vous, l\'achevera jufques à la journée de Jefus Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 7, 'Comme il m\'eft raifonnable de penfer cela de vous tous, parce que je retiens en mon cœur que vous avez tous efté participans de la grace avec moi en mes liens, &amp; la defenfe &amp; confirmation de l\'Evangile.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 8, 'Car Dieu m\'eft tefmoin comme je vous affectionne tous d\'une cordiale affection de Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 9, 'Et je requiers ceci, afin que voftre charité abonde encore de plus en plus, avec reconnoiffance &amp; toute intelligence :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 10, 'Afin que vous difcerniez les chofes contraires, pour eftre purs &amp; fans achoppement jufques à la journée de Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 11, 'Eftant remplis de fruits de juftice qui font par Jefus Chrift, à la gloire &amp; loüange de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 12, 'Or, freres, je veux bien que vous fachiez que les chofes qui me font advenuës, font advenuës à un tant plus grand avancement de l\'Evangile.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 13, 'En forte que mes liens en Chrift ont efté rendus celebres par tout le Pretoire, &amp; par tous les autres lieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 14, 'Et que plufieurs des freres au Seigneur, eftans affeurez par mes liens, ofent parler plus hardiment de la Parole fans crainte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 15, 'Il eft vrai que quelques-uns prefchent Chrift par envie &amp; contention, &amp; les autres au contraire par une bonne volonté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 16, 'Les uns annoncent Chrift par contention, non point purement, penfans adjoufter de l\'affliction à mes liens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 17, 'Mais les autres le font par charité, fçachans que je fuis ordonné pour la defenfe de l\'Evangile.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 18, 'Quoi donc ? Toutefois en quelque maniere que ce foit, ou par occafion, ou en verité, Chrift eft annoncé : &amp; en cela je m\'éjoüis, &amp; m\'en éjoüirai.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 19, 'Car je fçais que cela me tournera à falut par voftre priere, &amp; par la fubvention de l\'Efprit de Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 20, 'Selon ma ferme attente &amp; mon efperance, que je ne ferai confus en rien : mais qu\'en toute affeurance, comme toûjours, auffi maintenant Chrift fera magnifié en mon corps, foit par la vie, foit par la mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 21, 'Car Chrift m\'eft gain à vivre &amp; à mourir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 22, 'Or fi de vivre en la chair cela m\'eft profitable, &amp; que c\'eft que je dois choifir, je n\'en fçais rien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 23, 'Car je fuis enferré des deux coftez, mon defir tendant bien à déloger, &amp; eftre avec Chrift, ce qui m\'eft beaucoup meilleur :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 24, 'Mais il eft plus neceffaire pour vous que je demeure en la chair.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 25, 'Et je fçais cela comme tout affeuré, que je demeurerai &amp; perfevererai avec vous tous, à voftre avancement, &amp; à la joye de voftre foi :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 26, 'Afin que voftre gloire abonde en Jefus Chrift par moi, au moyen de mon retour vers vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 27, 'Seulement cheminés dignement comme il eft feant felon l\'Evangile de Chrift : afin que foit que je vienne &amp; que je vous voye, foit que je fois abfent, j\'entende quant à voftre eftat, que vous perfiftiez en un mefme Efprit; combattans enfemble tous d\'un courage par la foi de l\'Evangile, &amp; n\'eftans en rien efpouvantez par les adverfaires.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 28, 'Ce qui leur eft une demonftration de perdition, mais à vous, de falut : &amp; cela de par Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 29, 'Dautant qu\'il vous a efté gratuïtement donné pour Chrift, non feulement de croire en lui, mais auffi de fouffrir pour lui :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 30, 'En ayant le mefme combat que vous avez veu en moi, &amp; que maintenant vous entendez estre en moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 1, 'SI donc il y a quelque confolation en Chrift, fi quelque foulas de charité, fi quelque communion d\'efprit, fi quelques cordiales affections &amp; mifericordes,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 2, 'Rendez ma joye accomplie : tellement que vous ayez un mefme fentiment, ayans une mefme charité, eftans d\'un mefme courage, &amp; d\'un mefme fentiment.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 3, 'Que rien ne fe faffe par contention, ou vaine gloire : mais en eftimant l\'un l\'autre par humilité de cœur, plus excellent que foi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 4, 'Ne regardez point chacun à fon particulier, mais auffi à ce qui appartient aux autres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 5, 'Qu\'il y ait donc un mefme fentiment en vous, qui a efté auffi en Jefus-Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 6, 'Lequel eftant en forme de Dieu, n\'a point reputé rapine d\'eftre égal à Dieu :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 7, 'Toutefois s\'eft aneanti foi-mefme, ayant pris la forme de ferviteur, fait à la femblance des hommes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 8, 'Et eftant trouvé en figure comme un homme, il s\'eft abbaiffé foi-mefme, &amp; a efté obeïffant jufques à la mort, voire la mort de la Croix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 9, 'C\'eft pourquoi auffi Dieu l\'a fouverainement élevé, &amp; lui a donné un nom, qui eft fur tout nom.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 10, 'Afin qu\'au Nom de Jefus, tout genoüil fe ploye, de ceux qui font és cieux, &amp; en la terre, &amp; fous la terre :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 11, 'Et que toute langue confeffe que Jefus Chrift eft le Seigneur, à la gloire de Dieu le Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 12, 'C\'eft pourquoi, mes bien-aimez, ainfi que vous avez toûjours obeï, non feulement comme en ma prefence, mais beaucoup plus maintenant en mon abfence, employez-vous à voftre propre falut avec crainte &amp; tremblement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 13, 'Car c\'eft Dieu qui produit en vous avec efficace, &amp; le vouloir, &amp; le parfaire, felon fon bon-plaifir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 14, 'Faites toutes chofes fans murmures ni queftions :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 15, 'Afin que vous foyez fans reproche, &amp; fimples, enfans de Dieu, irreprehenfibles au milieu de la generation tortuë &amp; perverfe : entre lefquels vous reluifez comme des flambeaux au monde, qui portent au devant d\'eux la parole de vie :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 16, 'Pour me glorifier en la journée de Chrift, que je n\'ai point couru en vain, ni travaillé en vain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 17, 'Que fi mefme je fers d\'afperfion fur le facrifice &amp; fervice de voftre foi, j\'en fuis joyeux, &amp; m\'en éjoüis avec vous tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 18, 'Vous auffi pareillement foyez-en joyeux, &amp; vous en éjoüiffez avec moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 19, 'Or j\'efpere au Seigneur Jefus de vous envoyerbien-toft Timothée, afin que j\'aye auffi tant meilleur courage quand j\'aurai connu voftre eftat.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 20, 'Car je n\'ai perfonne de pareil courage, qui foit vrayement foigneux de ce qui vous concerne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 21, 'Car tous cherchent ce qui eft de leur particulier, non point ce qui eft de Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 22, 'Mais vous connoiffez fon efpreuve, qu\'il a fervi avec moi en l\'Evangile, comme l\'enfant fert au pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 23, 'J\'ai donc efperance de l\'envoyer incontinent que j\'aurai pourveu à mes affaires.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 24, 'Et je m\'affeure au Seigneur que moi-mefme auffi viendrai bien-toft.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 25, 'Mais j\'ai eftimé qu\'il eftoit neceffaire de vous envoyer Epaphrodite mon frere, compagnon d\'œuvre &amp; d\'armes avec moi, qui a efté auffi envoyé de voftre part pour m\'adminiftrer ce dont j\'ai eu befoin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 26, 'Car il vous defiroit tous fingulierement, &amp; eftoit fort angoiffé de ce que vous aviez entendu qu\'il avoit efté malade.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 27, 'Et de fait il a efté malade, voire tres-proche de la mort : mais Dieu a eu pitié de lui, &amp; non feulement de lui, mais auffi de moi, afin que je n\'euffe pas trifteffe fur trifteffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 28, 'Je l\'ai donc envoyé tant plus foigneufement, afin qu\'en le voyant vous vous rejoüiffiez derechef, &amp; que j\'aye tant moins de trifteffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 29, 'Recevez-le donc au Seigneur avec toute joye : &amp; ayez en eftime ceux qui font tels.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 30, 'Car il a efté proche de la mort pour l\'œuvre de Chrift, n\'ayant eu aucun égard à fa propre vie, afin qu\'il fuppleaft au defaut de voftre fervice envers moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 1, 'AU refte, mes freres, éjouïffez-vous au Seigneur. il ne m\'eft point grief, &amp; c\'eft voftre feureté, que je vous efcrive les mefmes chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 2, 'Prenez garde aux chiens, prenez garde aux mauvais ouvriers, prenez garde à la concifion.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 3, 'Car ce fommes nous qui fommes la Circoncifion, nous qui fervons à Dieu en Efprit, &amp; qui nous glorifions en Jefus Chrift, &amp; qui n\'avons point confiance en la chair :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 4, 'Encore que je pourrois auffi avoir confiance en la chair : Si quelqu\'un eftime qu\'il a dequoi fe confier en la chair, j\'en ai encore davantage :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 5, 'Moi qui fuis circoncis le huictiéme jour, qui fuis de la race d\'Ifraël, de la tribu de Benjamin, Hebreu, né des Hebreux, Pharifien de religion :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 6, 'Quant au zele, perfecutant l\'Eglife, quant à la juftice qui eft en la Loi, eftant fans reproche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 7, 'Mais ce qui m\'eftoit gain, je l\'ai reputé m\'eftre dommage pour l\'amour de Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 8, 'Voire certes je repute toutes chofes m\'eftre dommage pour l\'excellence de la connoiffance de Jefus Chrift mon Seigneur, pour l\'amour duquel je me fuis privé de toutes ces chofes, &amp; les repute comme fiente, afin que je gagne Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 9, 'Et que je fois trouvé en lui, ayant non point ma juftice qui eft de la Loi, mais celle qui eft par la foi de Chrift, affavoir la juftice qui eft de Dieu par la foi :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 10, 'Pour le connoiftre, &amp; la vertu de fa refurrection, &amp; la communion de fes afflictions, en eftant rendu conforme à fa mort :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 11, 'Effayant fi par aucune maniere je puis parvenir à la refurrection des morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 12, 'Non point que j\'aye déja apprehendé, ou que je fois déja rendu accompli : mais je pourfuis pour tafcher d\'apprehender : pour laquelle caufe auffi j\'ai efté apprehendé de Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 13, 'Freres, quant à moi, je ne me repute point encore avoir apprehendé :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 14, 'Mais je fais une chofes ; c\'eft qu\'en oubliant les chofes qui font en arriere, &amp; m\'avançant aux chofes qui font en devant, je tire vers le but, affavoir au prix de la fupernelle vocation de Dieu en Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 15, 'C\'eft pourquoi, nous tous qui fommes parfaits, ayans ce fentiment : &amp; fi en quelque chofe vous avez un autre fentiment, Dieu vous le revelera auffi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 16, 'Toutefois cheminons d\'une mefme regle, en ce à quoi nous fommes parvenus, &amp; ayons un mefme fentiment.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 17, 'Soyez d\'un accord mes imitateurs ; freres, &amp; confiderez ceux qui cheminent ainfi, felon que vous nous avez pour patron.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 18, 'Car plufieurs cheminent, defquels je vous ai fouvent dit, &amp; maintenant je le dis auffi en pleurant, qu\'ils font ennemis de la croix de Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 19, 'Defquels la fin eft perdition, le Dieu defquels eft le ventre, &amp; la gloire en leur confufion, qui ont leur affection aux chofes terriennes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 20, 'Mais noftre converfation eft de bourgeois des cieux, d\'où auffi nous attendons le Sauveur, affavoir le Seigneur Jefus Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 21, 'Lequel transformera noftre corps vil, afin qu\'il foit rendu conforme à fon corps glorieux, felon cette efficace par laquelle il peut mefmes affujettir toutes chofes à foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 1, 'PARTANT, mes freres bien-aimez, ma joye &amp; ma couronne, tenez-vous ainfi au Seigneur, mes bien-aimez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 2, 'Je prie Evodie, &amp; prie Syntiche, d\'avoir un mefme fentiment au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 3, 'Je te prie auffi, mon vrai compagnon, aide-leur, comme à celles qui ont combatu avec moi en l\'Evangile, avec Clement auffi &amp; mes autres compagnons d\'œuvre, defquels les noms font au livre de vie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 4, 'Ejouïffez-vous toûjours au Seigneur: &amp; derechef je vous dis, éjouïffez-vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 5, 'Que voftre debonnaireté foit connuë de tous les hommes. le Seigneur eft prés.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 6, 'Ne foyez en fouci de rien : mais qu\'en toutes chofes vos requeftes foyent notifiées à Dieu par priere &amp; par fupplication avec action de graces.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 7, 'Et la paix de Dieu, laquelle furmonte tout entendement, gardera vos cœurs &amp; vos fens en Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 8, 'Au refte, freres, que toutes les chofes qui font veritables, toutes les chofes qui font venerables, toutes les chofes qui font juftes, toutes les chofes qui font pures, toutes les chofes qui font aimables, toutes les chofes qui font de bonne renommée, s\'il y a quelque vertu, &amp; quelque loüange, penfez à ces chofes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 9, 'Lefquelles auffi vous avez apprifes, &amp; receuës, &amp; entenduës, &amp; venuës en moi. faites ces chofes, &amp; le Dieu de paix fera avec vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 10, 'Or je fuis grandement éjouï au Seigneur, qu\'à la fin vous eftes reverdis quant au foin que vous avez de moi : à quoi auffi vous penfiez, mais vous n\'aviez pas la commodité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 11, 'Non point que je dife ceci ayant égard à quelque indigence : car j\'ai appris d\'eftre content des chofes felon que je me trouve.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 12, 'Car je fçais eftre abbaiffé ; je fçais auffi eftre abondant : par tout &amp; en toutes chofes je fuis inftruit, tant à eftre raffafié, qu\'à avoir faim, tant à abonder, qu\'à avoir difette.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 13, 'Je puis toutes chofes en Chrift qui me fortifie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 14, 'Neantmoins vous avez bien fait de communiquer à mon affliction.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 15, 'Vous fçavez auffi, vous Philippiens, qu\'au commencement de la predication de l\'Evangile, quand je partis de Macedone, nulle Eglife ne me communiqua rien en matiere de bailler &amp; de recevoir, finon vous feuls :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 16, 'Car mefmes moi eftant à Theffalonique, vous m\'avez envoyé une fois, voire deux, ce qui m\'eftoit de befoin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 17, 'Non point que je cherche des dons, mais je recherche le fruit abondant qui foit alloüé à voftre conte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 18, 'Or j\'ai receu le tout, &amp; abonde : j\'ai efté rempli, ayant receu d\'Epaphrodite ce qui m\'a esté envoyé de par vous, comme une odeur de bonne fenteur, un facrifice agreable &amp; plaifant à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 19, 'Auffi mon Dieu fuppléera à tout ce dont vous aurez befoin, felon fes richeffes, avec gloire en Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 20, 'Or à noftre Dieu &amp; Pere foit gloire aux fiecles des fiecles. amen.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 21, 'Saluëz chacun des Saints en Jefus Chrift. les freres qui font avec moi vous faluënt.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 22, 'Tous les Saints vous faluënt, &amp; principalement ceux qui font de la maifon de Cefar.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 23, 'La grace de noftre Seigneur Jefus Chrift foit avec vous tous. Amen. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 1, 'PAUL Apoftre de Jefus Chrift par la volonté de Dieu, &amp; le frere Timothée :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 2, 'Aux Saints &amp; Freres fideles en Chrift qui font à Coloffes : Grace vous foit &amp; paix, de par Dieu noftre Pere, &amp; de par le Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 3, 'Nous rendons toûjours graces de vous à Dieu, qui eft le Pere de noftre Seigneur Jefus Chrift prians toûjours pour vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 4, 'Ayans ouï parler de voftre foi en Jefus Chrift, &amp; de la charité que vous avez envers tous les Saints :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 5, 'Pour l\'efperance qui vous eft refervée és cieux, laquelle vous avez ci-devant ouïe par la parole de verité, affavoir de l\'Evangile ;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 6, 'Qui eft parvenu à vous, comme auffi il eft par tout le monde, &amp; fructifie ainfi en vous, depuis le jour que vous avez ouï &amp; connu la grace de Dieu en verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 7, 'Comme auffi vous l\'avez appris d\'Epaphras noftre cher compagnon de fervice, qui eft fidele miniftre de Chrift pour vous :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 8, 'Lequel auffi nous a declaré voftre charité que vous avez en Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 9, 'Et partant auffi dés le jour que nous avons entendu cela, nous ne ceffons de prier pour vous, &amp; de demander que vous foyez remplis de la connoiffance de la volonté d\'icelui, en toute fapience &amp; intelligence fpirituelle :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 10, 'Afin que vous cheminiez dignement comme il eft feant felon le Seigneur, en lui plaifant entierement, fructifians en toute bonne œuvre, &amp; croiffans en la connoiffance de Dieu :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 11, 'Eftans fortifiez en toute force, felon la vertu de fa gloire, en toute fouffrance &amp; efprit patient avec joye :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 12, 'Rendans graces au Pere qui nous a rendus capables de participer à l\'heritage des Saints en la lumiere :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 13, 'Lequel nous a delivrez de la puiffance des tenebres, &amp; nous a tranfportez au Royaume de fon Fils bien-aimé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 14, 'En qui nous avons delivrance par fon fang, affavoir remiffion des pechez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 15, 'Lequel eft l\'image de Dieu invifible, le premier né de toute creature.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 16, 'Car par lui ont efté creés toutes les chofes qui font és cieux &amp; qui font en la terre, vifibles &amp; invifibles, foit les Trônes, ou les Dominations, ou les Principautez, ou les Puiffances : toutes chofes ont efté creés par lui &amp; pour lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 17, 'Et il eft devant toutes chofes, &amp; toutes fubfiftent par lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 18, 'Et c\'eft lui qui eft le Chef du corps de l\'Eglife, &amp; qui eft le commencement &amp; le premier-né d\'entre les morts : afin qu\'il tienne le premier lieu en toutes chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 19, 'Car le bon-plaifir du Pere a efté que toute plenitude habitaft en lui :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 20, 'Et de reconcilier par lui toutes chofes à foi, ayant fait la paix par fang de la croix d\'icelui, affavoir tant les chofes qui font és cieux, que celles qui font en la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 21, 'Et vous qui eftiez autrefois eftrangez de lui, &amp; qui eftiez fes ennemis en voftre entendement, en mauvaifes œuvres :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 22, 'Maintenant toutefois il vous a reconciliez au corps de fa chair, par la mort, pour vous rendre faints, fans tache, &amp; irreprehenfibles devant foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 23, 'Voire fi vous demeurez en la foi, eftans fondez &amp; fermes, &amp; n\'eftes point tranfportez hors de l\'efperance de l\'Evangile que vous avez ouï, lequel eft prefché entre toute creature qui eft fous le ciel, duquel moi Paul ai efté fait miniftre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 24, 'Dont je m\'éjouïs maintenant en mes fouffrances pour vous, &amp; accomplis le refte des afflictions de Chrift en ma chair, pour fon corps, qui eft l\'Eglife :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 25, 'De laquelle j\'ai efté fait miniftre, felon la difpenfation de Dieu qui m\'a efté donnée envers vous, pour accomplir la parole de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 26, 'Affavoir le myftere qui avoit efté caché dés tous les fiecles &amp; âges : mais qui eft maintenant manifefté à fes Saints :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 27, 'Aufquels Dieu a voulu donner à connoiftre quelles font les richeffes de la gloire de ce myftere entre les Gentils, qui eft Chrift en vous, l\'efperance de gloire :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 28, 'Lequel nous annonçons, admoneftans tout homme, &amp; enfeignans tout homme, &amp; enfeignans tout homme en toute fapience : afin que nous rendions tout homme parfait en Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 29, 'A quoi auffi je travaille, combattant felon efficace, laquelle agit puiffamment en moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 1, 'CAR je veux que vous fçachiez combien eft grand le combat que j\'ai pour vous, &amp; pour ceux qui font à Laodicée, &amp; pour tous ceux qui n\'ont point veu ma prefence en la chair :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 2, 'Afin que leurs cœurs foyent confolez, eux eftans joints enfemble en charité, &amp; en toues richeffes de pleine certitude d\'intelligence, à la connoiffance du myftere de noftre Dieu &amp; Pere &amp; de Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 3, 'En qui font cachez tous les threfors de fapience &amp; de fcience.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 4, 'Or je dis ceci, afin que nul ne vous abufe par des paroles de perfuafion.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 5, 'Car bien que je fois abfent de corps, toutefois d\'efprit je fuis avec vous, en m\'éjouïffant, &amp; voyant voftre ordre, &amp; la fermeté de voftre foi que vous avez en Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 6, 'Ainfi donc que vous avez receu le Seigneur Jefus Chrift, cheminez en lui :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 7, 'Eftans enracinez &amp; edifiez en lui, &amp; confirmez en la foi, comme vous avez efté enfeignez, abondans en elle avec action de graces.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 8, 'Prenez garde que nul ne vous butine par la philofophie &amp; vaine deception, felon la tradition des hommes, felon les rudimens du monde, &amp; non point felon Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 9, 'Car en lui habite corporellement toute plenitude de Deïté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 10, 'Et vous eftes rendus accomplis en lui, qui eft le chef de toute principauté &amp; puiffance :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 11, 'En qui auffi vous avez efté circoncis d\'une circoncifion faite fans main, par le dépouillement du corps des pechez de la chair, affavoir par la circoncifion de Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 12, 'Eftans enfevelis avec lui par le Baptefme : en qui auffi vous eftes enfemble reffufcitez, par la foi de l\'efficace de Dieu qui l\'a reffufcité des morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 13, 'Et lors que vous eftiez morts en vos offenfes &amp; au prepuce de voftre chair, il vous a vivifiez enfemble avec lui, vous ayant gratuïtement pardonné toutes vos offenfes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 14, 'En ayant effacé l\'obligation qui eftoit contre nous, laquelle confiftoit aux ordonnances, &amp; nous eftoit contraire : &amp; laquelle il a entierement abolie, l\'ayant fichée en la croix :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 15, 'Ayant dépouillé les Principautez &amp; les Puiffances, lefquelles il a publiquement menées en montre, triomphant d\'elles en elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 16, 'Que nul donc ne vous condamne en manger ou en boire, ou en diftinction d\'un jour de fefte, ou de nouvelle Lune, ou de Sabbats.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 17, 'Lefquelles chofes font ombre de celles qui eftoyent à venir, mais le corps en eft en Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 18, 'Que nul ne vous maiftrife à fon plaifir par l\'humilité d\'efprit, &amp; fervice des Anges, s\'ingerant és chofes qu\'il n\'a point veuës, eftant temerairement enflé du fens de fa chair :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 19, 'Et ne retenant point le chef, duquel tout le corps eftant fourni &amp; ajufté enfemble par les jointures &amp; les liaifons, croift en accroiffement de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 20, 'Si donc vous eftes morts avec Chrift, quant aux rudimens du monde, pourquoi vous charge-t\'on d\'ordonnances, comme fi vous viviez au monde ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 21, 'Affavoir, Ne mange, Ne goufte, Ne touche point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 22, 'Qui font toutes chofes periffables par l\'ufage, eftant establies fuivant les commandemens &amp; les doctrines des hommes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 23, 'Lefquelles ont toutefois quelque apparence de fapience en devotion volontaire &amp; humilité d\'efprit, &amp; en ce qu\'elles n\'efpargnent nullement le corps, &amp; n\'ont aucun égard au raffafiement de la chair.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 1, 'SI donc vous eftes reffufcitez avec Chrift, cherchez les chofes qui font en haut, là où Chrift eft affis à la dextre de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 2, 'Penfez aux chofes qui font en haut, non point à celles qui font fur la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 3, 'Car vous eftes morts, &amp; voftre vie eft cachée avec Chrift en Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 4, 'Quand Chrift, qui eft voftre vie, apparoiftra, alors auffi vous apparoiftrez avec lui en gloire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 5, 'Mortifiez donc vos membres qui font fur la terre, paillardife, fouillure, appetit defordonné, mauvaife, &amp; avarice, qui eft idolatrie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 6, 'Pour lefquelles chofes l\'ire de Dieu vient fur les enfans de rebellion.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 7, 'Efquelles auffi vous avez cheminé autrefois, quand vous viviez en elles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 8, 'Mais maintenant vous auffi dépouillez toutes ces chofes, ire, colere, mauvaiftié, médifance, parole deshonnefte hors de voftre bouche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 9, 'Ne mentez point l\'un à l\'autre, ayans deveftu le vieil homme avec fes actes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 10, 'Et ayans reveftu le nouvel homme, lequel fe renouvelle en connoiffance felon l\'image de celui qui l\'a creé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 11, 'Là où il n\'y a ni Grec, ni Juif, ni Circoncifion, ni Prepuce, ni Barbare, ni Scythe, ni ferf, ni franc, mais Chrift y eft tout, &amp; en tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 12, 'Soyez donc (comme éleus de Dieu, faints &amp; bien-aimez) reveftus des entrailles de mifericorde, de benignité, d\'humilité, de douceur, d\'efprit patient :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 13, 'Supportans l\'un l\'autre, &amp; pardonnans les uns aux autres, fi l\'un a querelle contre l\'autre : comme Chrift vous a pardonné, vous auffi faites le femblable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 14, 'Et outre tout cela, foyés revestus de charité, qui eft le lien de perfection.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 15, 'Et que la paix de Dieu tienne le principal lieu en vos cœurs, à laquelle vous eftes appellez en un corps, &amp; foyez reconnoiffans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 16, 'Que la parole de Chrift habite en vous plantureufement en toute fapience, en vous enfeignant &amp; admoneftant l\'un l\'autre par Pfeaumes, loüanges, chanfons fpirituelles, avec grace, chantans de voftre cœur au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 17, 'Et quelque chofes que vous faffiez, foit par parole ou par œuvre, faites le tout au Nom du Seigneur Jefus, rendans graces par lui à noftre Dieu &amp; Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 18, 'Femmes, foyez fujettes à vos propres maris, ainfi qu\'il appartient felon le Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 19, 'Maris, aimez vos femmes, &amp; ne vous enaigriffez point contre elles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 20, 'Enfans, obeïffez à peres &amp; à meres en toutes chofes : car cela eft plaifant au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 21, 'Peres, n\'irritez point vos enfans, afin qu\'ils ne perdent point courage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 22, 'Serviteurs, obeïffez en toutes chofes à ceux qui font vos maiftres felon la chair, ne fervans point à l\'œil, comme voulans complaire aux hommes, mais en fimplicité de cœur, craignans Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 23, 'Et quelque chofe que vous faffiez, faites-le tout avec courage, comme au Seigneur, &amp; non point comme aux hommes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 24, 'Sçachans que vous recevrez du Seigneur le falaire de l\'heritage : car vous fervez à Chrift le Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 25, 'Mais celui qui fait injuftement, recevra ce qu\'il aura fait injuftement : &amp; il n\'y aura point d\'égard à l\'apparence des perfonnes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 1, 'MAISTRES, rendez le droict &amp; l\'equité à vos ferviteurs, fçachans que vous auffi avez un Seigneur és cieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 2, 'Perfeverez en priere, veillans en elle avec action de graces :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 3, 'Prians enfemble auffi pour nous, afin que Dieu nous ouvre la porte de la parole, pour annoncer le myftere de Chrift, pour lequel auffi je fuis prifonnier :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 4, 'Afin que je le manifefte comme il faut que je parle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 5, 'Cheminez fagement envers ceux de dehors, rachetans le temps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 6, 'Que voftre parole foit toûjours confite en fel avec grace, afin que vous fçachiez comment vous avez à refpondre à chacun.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 7, 'Tychique noftre frere bien-aimé, &amp; fidele miniftre, &amp; compagnon de fervice au Seigneur, vous fera fçavoir tout mon eftat :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 8, 'Lequel j\'ai envoyé vers vous expreffément, afin qu\'il connoiffe de voftre eftat, &amp; qu\'il confole vos cœurs ;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 9, 'Avec Onefime noftre fidele &amp; bien-aimé frere, qui eft des voftres : ils vous avertiront de toutes les affaires de par deça.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 10, 'Ariftarque, qui eft prifonnier avec moi, vous faluë, &amp; Marc le coufin de Barnabas : (touchant lequel vous avez receu mandement, s\'il vient à vous, recuëillez-le.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 11, 'Et Jefus qui eft appellé Jufte : lefquels font de la Circoncifion : ceux-ci feuls font mes compagnons d\'œuvre au royaume de Dieu, lefquels m\'ont efté en confolation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 12, 'Epaphras qui eft des voftres, ferviteur de Chrift, vous faluë, combattant toûjours pour vous en prieres, afin que vous demeuriez parfaits &amp; accomplis en toute la volonté de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 13, 'Car je lui rens témoignage qu\'il a un grand zele pour vous, &amp; pour ceux qui font à Laodicée, &amp; pour ceux qui font à Hierapolis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 14, 'Luc le medecin bien-aimé vous faluë, &amp; Demas auffi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 15, 'Salüez les freres qui font à Laodicée, &amp; Nymphas, &amp; l\'Eglife qui eft en fa maifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 16, 'Et quand cette Epiftre aura efté leuë entre vous, faites qu\'elle foit auffi leuë en l\'Eglife des Laodiciens, &amp; que vous lifiez auffi celle qui est venuë de Laodicée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 17, 'Et dites à Archippe, Regarde l\'adminiftration que tu as receuë au Seigneur, afin que tu l\'accompliffes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 18, 'La salutation de la propre main de moi Paul. Ayez fouvenance de mes liens. Grace foit avec vous. Amen. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 1, 'PAUL &amp; Silvain, &amp; Timothée, à l\'Eglife des Theffaloniciens, qui est en Dieu le Pere, &amp; au Seigneur Jefus Chrift : Grace vous foit &amp; paix, de par Dieu noftre Pere, &amp; de par le Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 2, 'Nous rendons toûjours graces à Dieu de vous tous, faifons mention de vous en nos prieres :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 3, 'Nous ramentevans fans ceffe l\'œuvre de voftre foi, &amp; le travail de voftre charité, &amp; la patience de voftre efperance que vous avés en noftre Seigneur Jefus Chrift devant noftre Dieu &amp; Pere :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 4, 'Sçachans, freres bien-aimez de Dieu, voftre élection.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 5, 'Car noftre predication de l\'Evangile n\'a point efté en voftre endroit, feulement en parole, mais auffi en vertu, &amp; en Saint Efprit, &amp; en grande certitude, ainfi que vous fçavez quels nous avons efté entre vous pour l\'amour de vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 6, 'Auffi avez-vous efté imitateurs de nous, &amp; du Seigneur : ayans receu avec joye du Saint Efprit la Parole accompagnée de grande affliction :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 7, 'Tellement que vous avez efté pour patron à tous les croyans en la Macedone &amp; en l\'Achaïe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 8, 'Car la parole du Seigneur a retenti de par vous, non feulement en la Macedone &amp; en l\'Achaïe, mais auffi en tous lieux : &amp; voftre foi envers Dieu eft divulguée, tellement qu\'il ne nous eft pas befoin d\'en rien dire :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 9, 'Car eux-mefmes racontent de nous, quelle entrée nous avons euë vers vous, &amp; comment vous avez efté convertis des idoles à Dieu, pour fervir le Dieu vivant &amp; vrai :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 10, 'Et pour attendre des cieux fon Fils Jefus, qu\'il a reffufcité des mors, lequel nous delivre de l\'ire à venir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 1, 'CAR, freres, vous-mefmes fçavez que noftre entrée vers vous n\'a point efté vaine :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 2, 'Mais encore qu\'auparavant nous euffions efté affligez &amp; outragez à Philippes, comme vous fçavez, nous avons pris hardieffe en noftre Dieu de vous annoncer l\'Evangile de Dieu avec un grand combat.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 3, 'Car noftre exhortation n\'a point efté par abufion, ni par vilenie, ni en fraude.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 4, 'Mais comme nous avons efté approuvez de Dieu, à ce que la predication de l\'Evangile nous fuft commife, ainfi parlons-nous, non point comme voulant complaire aux hommes, mais à Dieu qui efprouve nos cœurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 5, 'Car auffi nous ne nous fommes jamais trouvez en parole de flatterie, ainfi que vous le fçavez, ni en pretexte d\'avarice : Dieu en est tefmoin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 6, 'Et nous n\'avons point cherché la gloire des hommes, ni de par vous, ni par autres : encore que nous euffions pû montrer de l\'authorité comme Apoftres de Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 7, 'Mais nous avons efté doux au milieu de vous, comme fi une nourrice nourriffoit tendrement fes enfans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 8, 'Eftant donc ainfi affectionnez envers vous, nous fouhaittions de vous élargir, non feulement l\'Evangile de Dieu, mais auffi nos propres ames, parce que vous eftiez bien-aimez de nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 9, 'Car, freres, vous vous fouvenez de noftre labeur &amp; travail : veu que nous vous avons prefché l\'Evangile de Dieu, travaillans nuit &amp; jour, afin de ne charger aucun de vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 10, 'Vous eftes tefmoins, &amp; Dieu auffi, comme nous nous fommes portez fainctement &amp; juftement, &amp; fans reproche envers vous qui croyez :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 11, 'Ainfi que vous fçavez que nous avons exhorté chacun de vous : (comme un pere fes enfans :)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 12, 'Et confolé, &amp; adjuré que vous cheminaffiez dignement, comme il eft feant felon Dieu, qui vous appelle à fon royaume &amp; à fa gloire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 13, 'Pour cette caufe auffi, nous rendons graces à Dieu fans ceffe, de ce que quand vous avez receu de nous la parole de la predication de Dieu, vous l\'avez receuë non point comme parole des hommes, mais (ainfi qu\'elle eft veritablement) comme parole de Dieu, laquelle auffi agit avec efficace en vous qui croyez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 14, 'Car, freres, vous eftes faits imitateurs des Eglifes de Dieu qui font en Judée en Jefus Chrift, parce que vous avez auffi fouffert les mefmes chofes de par ceux de voftre propre nation, comme auffi eux par les Juifs :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 15, 'Lefquels ont mefme mis à mort le Seigneur Jefus, &amp; leurs propres Prophetes, &amp; nous ont déchaffez &amp; ne plaifent point à Dieu, &amp; font adverfaires à tous hommes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 16, 'Nous empefchans de parler aux Gentils à ce qu\'ils foyent fauvez, afin qu\'ils comblent toûjours leurs pechez : car l\'ire eft parvenuë fur eux jufques au bout.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 17, 'Or, freres, entant que nous avons efté privez de vous pour un moment de temps, de veuë, non point de cœur, nous nous fommes employez avec un grand defir de voir voftre face.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 18, 'C\'eft pourquoi nous avons voulu venir vers vous (au moins moi Paul) une fois, voire deux : mais Satan nous y a mis de l\'empefchement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 19, 'Car quelle eft noftre efperance, ou joye, ou couronne de gloire ? n\'eft-ce pas vous auffi devant noftre Seigneur Jefus Chrift à fa venuë ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 20, 'Certes vous eftes noftre gloire &amp; noftre joye.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 1, 'C\'EST pourquoi ne pouvant plus endurer, il nous fembla bon d\'eftre laiffez feuls à Athenes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 2, 'Et nous envoyames Timothée noftre frere, &amp; minftre de Dieu, &amp; noftre compagnon d\'œuvre en l\'Evangile de Chrift, pour vous affermir, &amp; pour vous exhorter touchant voftre foi :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 3, 'Afin que nul ne foit troublé en ces afflictions, car vous fçavez vous mefmes que nous fommes ordonnez à cela.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 4, 'Car quand nous eftions avec vous, nous vous predifions que nous aurions à fouffrir des afflictions : comme auffi il eft advenu, &amp; vous le fçavez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 5, 'Et partant auffi ne pouvant plus endurer, je l\'envoyai pour connoiftre de l\'eftat de voftre foi : de peur que celui qui tente, ne vous euft tentez en quelque forte, &amp; que noftre labeur ne fuft tourné à neant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 6, 'Or Timothée eftant revenu depuis peu vers nous de devers vous, &amp; nous ayant apporté de joyeufes nouvelles touchant voftre foi &amp; voftre charité, &amp; comme vous avez toûjours bonne fouvenance de nous, defirant grandement de nous voir, comme nous auffi vous :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 7, 'Pour cette caufe, freres, nous avons efté confolez en vous, en toute noftre affliction &amp; neceffité, par voftre foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 8, 'Car maintenant nous vivons, fi vous tenez fermes au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 9, 'Car quelle action de graces pouvons nous rendre à Dieu touchant vous, pour toute la joye de laquelle nous nous éjouïffons à caufe de vous devant noftre Dieu ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 10, 'Priant jour &amp; nuit de plus en plus de revoir voftre face, afin de fuppléer à ce qui defaut à voftre foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 11, 'Or noftre Dieu &amp; Pere, &amp; noftre Seigneur Jefus, veuille addreffer noftre chemin vers vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 12, 'Et le Seigneur vous accroiffe, &amp; vous faffe abonder tant &amp; plus en charité les uns envers les autres, &amp; envers tous, comme nous auffi abondons envers vous :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 13, 'Pour affermir vos cœurs fans reproche en fainteté, devant Dieu qui eft noftre Pere, à la venuë de noftre Seigneur Jefus Chrift, avec tous fes Saints.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 1, 'AU refte donc, freres, nous vous prions &amp; requerons par le Seigneur Jefus, qu\'ainfi que vous avez receu de nous comme il faut converfer &amp; plaire à Dieu, vous abondiez de plus en plus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 2, 'Car vous fçavez quels commandemens nous vous avons donnez de par le Seigneur Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 3, 'Car c\'eft ici la volonté de Dieu, voftre fanctification, que vous vous abfteniez de paillardife :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 4, 'A ce que chacun de vous fçache poffeder fon vaiffeau fanctification &amp; honneur :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 5, 'Non point avec paffion de convoitife, comme les Gentils qui ne connoiffent point Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 6, 'Que perfonne ne foule ou faffe fon profit au dommage de fon frere en aucune affaire, dautant que le Seigneur eft le vengeur de toutes ces chofes, comme auffi nous vous l\'avons auparavant dit &amp; teftifié.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 7, 'Car Dieu ne nous a point appellez à ordure, mais à fanctification.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 8, 'C\'eft pourquoi celui qui rejette ceci, ne rejette point un homme, mais Dieu, qui a auffi mis fon faint Efprit en nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 9, 'Quand à la dilection fraternelle, vous n\'avez point de befoin que je vous en efcrive : car vous-mefmes eftes enfeignez de Dieu, à vous aimer l\'un l\'autre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 10, 'Car auffi vous le faites envers tous les freres qui font en toute la Macedone : mais, freres, nous vous prions que vous y abondiez de plus en plus :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 11, 'Et que vous vous evertuïez à vivre paifiblement, &amp; à faire vos propres affaires, &amp; que vous travailliez de vos propre mains, comme nous vous l\'avons commandé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 12, 'Afin que vous vous portiez honneftement envers ceux qui font de dehors, &amp; que vous n\'ayez faute de rien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 13, 'Or, freres, je ne veux point que vous foyez ignorans touchant ceux qui dorment, afin que vous ne foyez point contriftez comme les autres qui n\'ont point d\'efperance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 14, 'Car fi nous croyons que Jefus eft mort, &amp; qu\'il eft reffufcité : pareillement auffi ceux qui dorment en Jefus, Dieu les ramenera avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 15, 'Car nous vous difons ceci par la parole du Seigneur, c\'eft que nous qui vivrons &amp; refterons à la venuë du Seigneur, ne previendrons point ceux qui dorment.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 16, 'Car le Seigneur lui-mefme avec cri d\'exhortation, &amp; voix d\'archange, &amp; avec la trompette de Dieu, defcendra du ciel : &amp; ceux qui font morts en Chrift reffufciteront premierement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 17, 'Puis apres nous qui vivrons &amp; refterons ferons ravis enfemble avec eux és nuées au devant du Seigneur en l\'air : &amp; ainfi nous ferons toûjours avec le Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 18, 'C\'eft pourquoi confolez-vous l\'un l\'autre par ces paroles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 1, 'OR touchant les temps &amp; les momens, freres, vous n\'avez point de befoin qu\'on vous en efcrive.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 2, 'Car vous-mefmes fçavez tres-bien que le jour du Seigneur viendra comme le larron en la nuit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 3, 'Car quand ils diront, Paix &amp; feureté, alors il leur furviendra une foudaine deftruction, comme le travail à celle qui eft enceinte, &amp; ils n\'efchapperont point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 4, 'Mais quant à vous, freres, vous n\'eftes point en tenebres, de forte que ce jour-là vous furprenne comme le larron.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 5, 'Vous eftes tous enfans de lumiere, &amp; enfans du jour : nous ne fommes point de la nuit, ni des tenebres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 6, 'Ainfi donc ne dormons point comme les autres mais veillons &amp; foyons fobres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 7, 'Car ceux qui dorment, dorment de nuit : &amp; ceux qui s\'enyvrent, s\'enyvrent de nuit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 8, 'Mais nous qui fommes de jour, foyons fobres, eftant reveftus du corcelet de la foi &amp; de la charité, &amp; pour cafque, de l\'efperance de falut.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 9, 'Car Dieu ne nous a point ordonnez à ire, mais pour l\'acquifition du falut par noftre Seigneur Jefus Chrift ;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 10, 'Qui eft mort pour nous, afin que foit que nous veillons, foit que nous dormions, nous vivions avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 11, 'C\'eft pourquoi exhortez-vous l\'un l\'autre, &amp; edifiez-vous chacun l\'un l\'autre, comme auffi vous faites.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 12, 'Or, freres, nous vous prions que vous reconnoiffiez ceux qui travaillent entre vous, &amp; qui prefident fur vous au Seigneur, &amp; qui vous admoneftent :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 13, 'Et que vous les ayez en fouveraine amour pour l\'œuvre qu\'ils font. Soyez en paix entre vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 14, 'Pareillement nous vous prions, freres, que vous admoneftiez les déreglez : que vous confoliez ceux qui font de petit courage : que vous foyez d\'efprit patient envers tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 15, 'Avifez que nul ne rende à aucun mal pour mal : mais pourchaffez toûjours ce qui eft bon, tant les uns envers les autres, qu\'envers tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 16, 'Soyez toûjours joyeux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 17, 'Priez fans ceffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 18, 'Rendez graces en toutes chofes : car telle eft la volonté de Dieu par Jefus Chrift envers vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 19, 'N\'efteignez point l\'Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 20, 'Ne mefprifez point les propheties.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 21, 'Efprouvez toutes chofes : retenez ce qui eft bon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 22, 'Abftenez-vous de toute apparence de mal.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 23, 'Or le Dieu de paix vous veuille fanctifier entierement : &amp; voftre efprit entier, &amp; l\'ame, &amp; le corps, foit confervé fans reproche à la venuë de noftre Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 24, 'Celui qui vous appelle eft fidele, qui auffi le fera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 25, 'Freres priez pour nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 26, 'Saluëz tous les freres, par un faint baifer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 27, 'Je vous adjure par le Seigneur que cette Epiftre foit leuë à tous les faints freres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 28, 'La grace de noftre Seigneur Jefus Chrift foit avec vous. Amen. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 1, 'PAUL &amp; Silvain, &amp; Timothée, à l\'Eglife des Theffaloniciens, qui eft en Dieu noftre Pere &amp; au Seigneur Jefus Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 2, 'Grace vous foit, &amp; paix de par Dieu noftre Pere, &amp; de par le Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 3, 'Freres, nous devons toûjours rendre graces à Dieu de vous, comme c\'eft bien raifon, dautant que voftre foi croift grandement, &amp; que la charité de chacun de vous abonde de l\'un envers l\'autre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 4, 'Tellement que mefmes nous nous glorifions de vous és Eglifes de Dieu, à caufe de voftre patience &amp; de voftre foi en toues vos perfecutions &amp; afflictions que vous fouftenez :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 5, 'Lefquelles font une manifefte demonftration du jufte jugement de Dieu : à ce que vous foyez repurez dignes du royaume de Dieu : pour lequel auffi vous fouffrez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 6, 'Veu que c\'eft une chofe jufte envers Dieu, qu\'il rende affliction à ceux qui vous affligent :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 7, 'Et à vous qui eftes affligez, relafche avec nous, lors que le Seigneur Jefus fera revelé du ciel avec les Anges de fa puiffance,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 8, 'Avec flamme de feu, exercant vengeance contre ceux qui ne connoiffent point Dieu, &amp; qui n\'obeïffent point à l\'Evangile de noftre Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 9, 'Lefquels feront punis de perdition eternelle, de par la face du Seigneur, &amp; de par la gloire de fa force.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 10, 'Quand il viendra pour eftre glorifié en fes Saints, &amp; eftre rendu admirable en tous les croyans (parce que noftre témoignage envers vous a efté creu) en ce jour-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 11, 'Pour laquelle caufe auffi nous prions toûjours pour vous, que noftre Dieu vous rende dignes de fa vocation, &amp; accompliffe en vous tout le bon plaifir de fa bonté, &amp; l\'œuvre de la foi puiffamment :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 12, 'Afin que le Nom de noftre Seigneur Jefus Chrift foit glorifié en vous, &amp; vous en lui, felon la grace de noftre Dieu, &amp; du Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 1, 'OR, freres, nous vous prions par l\'advenement de noftre Seigneur Jefus Chrift, &amp; par noftre recueil en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 2, 'Que vous ne foyez point toft esbranlez d\'entendement, ni troublez par efprit, ni par parole, ni par epiftre, comme de par nous, comme fi la journée de Chrift eftoit prochaine.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 3, 'Que nul ne vous feduife en quelque forte que ce foit : car ce jour-là ne viendra point que premierement ne foit advenuë la revolte, &amp; que l\'homme de peché ne foit revelé, le fils de perdition :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 4, 'Qui s\'oppofe &amp; s\'éleve contre tout ce qui eft nommé Dieu, ou qu\'on adore, jufques à eftre affis comme Dieu au temple de Dieu, fe portant comme s\'il eftoit Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 5, 'Ne vous fouvient-il point que quand j\'eftois encore avec vous, je vous difois ces chofes ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 6, 'Et maintenant vous fçavez ce qui le retient, afin qu\'il foit revelé en fon temps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 7, 'Car déja le myftere d\'iniquité fe met en train : feulement celui qui obtient maintenant, obtiendra jufques à ce qu\'il foit aboli.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 8, 'Et alors le mefchant fera revelé, lequel le Seigneur deconfira par l\'Efprit de fa bouche, &amp; abolira par la clarté de fon advenement :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 9, 'Duquel mefchant l\'advenement eft felon l\'efficace de Satan : en toute puiffance, &amp; fignes, &amp; miracles de menfonge :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 10, 'Et en toute feduction d\'iniquité, en ceux qui periffent : dautant qu\'ils n\'ont point receu la dilection de verité, pour eftre fauvez :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 11, 'Et partant Dieu leur envoyera efficace d\'erreur pour croire au menfonge :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 12, 'Afin que tous ceux-là foyent jugez qui n\'ont point creu à la verité, mais ont pris plaifir à l\'iniquité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 13, 'Mais, ô freres bien aimez du Seigneur, nous devons toûjours rendre graces à Dieu quant à vous, de ce que Dieu vous a éleus dés le commencement à falut, en fanctification d\'Efprit, &amp; par la foi de verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 14, 'A quoi il vous a appellez par noftre Evangile, à l\'acquifition de la gloire de noftre Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 15, 'C\'eft pourquoi, freres, demeurez fermes, &amp; retenez les enfeignemens que vous avez appris, foit par noftre parole, ou par noftre Epiftre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 16, 'Or lui-mefme Jefus Chrift, noftre Seigneur, &amp; noftre Dieu &amp; Pere qui nous a aimez &amp; nous a donné confolation eternelle, &amp; bonne efperance en grace,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 17, 'Veuille confoler vos cœurs, &amp; vous affermir en toute bonne parole &amp; bonne œuvre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 1, 'AU refte, freres, priez pour nous, afin que la parole du Seigneur ait fon cours, &amp; foit glorifiée ainfi qu\'entre vous :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 2, 'Et que nous foyons delivrez des gens defordonnez &amp; mefchans, car la foi n\'eft point de tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 3, 'Or le Seigneur eft fidele, qui vous affermira, &amp; gardera du malin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 4, 'Auffi nous affeurons-nous de vous par le Seigneur, que vous faites &amp; ferez toutes les chofes que nous vous commandons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 5, 'Or le Seigneur veuille addreffer vos cœurs à l\'amour de Dieu, &amp; à l\'attente de Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 6, 'Freres, nous vous denonçons auffi au Nom de noftre Seigneur Jefus Chrift, que vous vous retirez d\'avec tout frere cheminant defordonnément, &amp; non point felon l\'enfeignement qu\'il a receu de nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 7, 'Car vous-mefmes fçavez comment il faut que vous nous enfuiviez : car nous ne nous fommes point portez defordonnément entre vous :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 8, 'Et n\'avons point mangé le pain d\'aucun pour neant, mais en labeur &amp; en travail, travaillant nuit &amp; jour, afin de ne charger aucun de vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 9, 'Non point que nous n\'en ayons bien la puiffance, mais afin de nous donner nous-mefmes pour patron en voftre endroit, afin que vous nous enfuiviez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 10, 'Car auffi quand nous eftions avec vous, nous vous denoncions cela mefme, que fi quelqu\'un ne veut pas travailler, qu\'il ne mange point auffi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 11, 'Car nous entendons qu\'il y en a quelques-uns entre vous qui cheminent defordonnément, ne faifant rien, mais vivant curieufement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 12, 'Or nous denonçons à ceux qui font tels, &amp; les exhortons par noftre Seigneur Jefus Chrift, qu\'en travaillant ils mangent leur pain paifiblement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 13, 'Mais quant à vous, freres, ne vous laffez point en bien-faifant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 14, 'Et fi quelqu\'un n\'obeït point à noftre parole portée par céte Epître, marquez-le par lettres, &amp; ne converfez point avec lui, afin qu\'il ait de la honte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 15, 'Toutefois ne le tenez point comme ennemi, mais admoneftez-le comme frere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 16, 'Or le Seigneur de paix vous donne toûjours paix en toute maniere. Le Seigneur foit avec vous tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 17, 'La salutation de la propre main de moi Paul, qui eft un figne en toutes mes Epiftres : j\'efcris ainfi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 18, 'La grace de noftre Seigneur Jefus Chrift foit avec vous tous. Amen. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 1, 'PAUL Apoftre de Jefus Chrift par le mandement de Dieu noftre Sauveur, &amp; du Seigneur Jefus Chrift noftre efperance :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 2, 'A Timothée mon vrai fils en la foi, grace, &amp; mifericorde, &amp; paix de par Dieu noftre Pere, &amp; de par Jefus Chrift noftre Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 3, 'Suivant ce dont je t\'ai prié que tu demeuraffes à Ephefe, quand j\'allois en Macedone, je t\'avertis que tu denonces à certains qu\'ils n\'enfeignent point une diverfe doctrine.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 4, 'Et qu\'ils ne s\'adonnent point aux fables &amp; genealogies qui font fans fin, lefquelles engendrent pluftoft des queftions que l\'edification de Dieu, laquelle confifte en foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 5, 'Or la fin du commandement, c\'eft la charité procedante d\'un cœur pur, &amp; d\'une bonne confcience, &amp; d\'une foi non feinte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 6, 'Defquelles chofes quelques-uns s\'eftant dévoyez, fe font deftournez à un vain babil :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 7, 'Voulant eftre docteurs de la Loi, n\'entendant point les chofes qu\'ils difent, ni defquelles ils affeurent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 8, 'Or nous fçavons que la Loi eft bonne, fi quelqu\'un en ufe legitimement :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 9, 'Sçachant cela, que la Loi n\'eft point mife pour le jufte, mais pour les iniques, &amp; qui ne fe peuvent ranger : pour ceux qui font fans pieté, &amp; les mal-vivans : pour les gens fans religion, &amp; profanes : pour les meurtriers de pere &amp; de mere, &amp; homicides :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 10, 'Pour les paillards, pour ceux qui habitent avec les mafles, les parjures : &amp; s\'il y a quelque autre chofe qui foit contraire à la faine doctrine :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 11, 'Suivant l\'Evangile de la gloire de Dieu bien-heureux, lequel Evangile m\'eft commis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 12, 'Et je rens graces à celui qui m\'a fortifié, affavoir à Jefus Chrift noftre Seigneur, de ce qu\'il m\'a eftimé fidele, m\'ayant eftabli au miniftere :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 13, 'Moi qui eftois auparavant un blafphemateur, &amp; perfecuteur, &amp; oppreffeur : mais mifericorde m\'a efté faite, entant que je l\'ai fait par ignorance, eftant en infidelité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 14, 'Mais la grace de noftre Seigneur a d\'autant plus abondé avec foi &amp; dilection, laquelle eft en Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 15, 'Cette parole eft certaine, &amp; digne d\'eftre entierement receuë, c\'eft que Jefus Chrift eft venu au monde pour fauver les pecheurs, defquels je fuis le premier.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 16, 'Mais pour cette caufe mifericorde m\'a efté faite, afin que Jefus Chrift montraft en moi le premier toute clemence, pour un exemplaire à ceux qui viendront à croire en lui à vie eternelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 17, 'Or au Roi des fiecles, immortel, invifible, à Dieu feul fage, foit honneur &amp; gloire és fiecles des fiecles. Amen.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 18, 'Mon fils Timothée, je te recommande ce Commandement, que felon les propheties qui auparavant ont efté de toi, par elles tu faffes devoir de guerroyer en bonne guerre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 19, 'Ayant foi &amp; bonne confcience, laquelle quelques-uns ayant rejettée ont fait naufrage quant à la foi :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 20, 'D\'entre lefquels font Hymenée, &amp; Alexandre, lefquels j\'ai livré à Satan, afin qu\'ils apprennent par ce chaftiment à ne plus blafphemer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 1, 'J\'ADMONESTE donc qu\'avant toutes chofes on faffe des requeftes, des fupplications &amp; des actions de graces pour tous les hommes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 2, 'Pour les Rois, &amp; pour tous ceux qui font conftituez en dignité, afin que nous puiffions mener une vie paifible &amp; tranquille, en toute pieté &amp; honnefteté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 3, 'Car cela eft bon &amp; agreable devant Dieu noftre Sauveur :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 4, 'Lequel veut que tous les hommes foyent fauvez, &amp; viennent à la connoiffance de la verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 5, 'Car il y a un feul Dieu, &amp; un feul Moyenneur entre Dieu &amp; les hommes, affavoir Jefus Chrift homme :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 6, 'Qui s\'eft donné foi-mefme en rançon pour tous, afin d\'eftre en témoignage en fon propre temps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 7, 'A quoi j\'ai efté eftabli Heraut &amp; Apoftre (je dis verité en Chrift, je ne mens point) Docteur des Gentils en foi &amp; en verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 8, 'Je veux donc que les hommes prient en tout lieu, levant leurs mains pures, fans colere, &amp; fans queftion.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 9, 'Pareillement auffi que les femmes fe parent d\'un accouftrement honnefte avec vergogne &amp; modeftie, non point avec treffes, ni or, ni perles, ni habillemens fomptueux :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 10, 'Mais de bonnes œuvres, comme il eft feant à des femmes qui font profeffion de fervir Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 11, 'Que la femme apprene en filence, en toute fujettion.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 12, 'Car je ne permets point que la femme enfeigne, ni qu\'elle ufe d\'authorité fur le mari, mais qu\'elle foit en filence.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 13, 'Car Adam a efté formé le premier, &amp; puis apres Eve.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 14, 'Et ce n\'a point efté Adam qui a efté feduit, mais la femme ayant efté feduite, a efté en tranfgreffion.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 15, 'Or elle fera fauvée en engendrant des enfans, fi elle demeure en foi, dilection, &amp; fanctification, avec modeftie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 1, 'CETTE parole eft certaine, Si quelqu\'un a affection d\'eftre Evefque, il defire une œuvre excellente.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 2, 'Mais il faut que l\'Evefque foit irreprehenfible, mari d\'une feule femme, vigilant, attrempé, honorable, hofpitalier, propre à enfeigner :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 3, 'Non point addonné au vin, non batteur, non convoiteux de gain deshonnefte : mais benin, non quereleux, non avaricieux :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 4, 'Conduifant honneftement fa propre maifon, ayant fes enfans fujets en toute reverence :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 5, '(Car fi quelqu\'un ne fçait pas conduire fa propre maifon, comment pourra-t\'il gouverner l\'Eglife de Dieu ?)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 6, 'Non point nouvel apprenti : de peur qu\'eftant enflé d\'orgueil : il ne tombe en la condamnation du calomniateur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 7, 'Il faut auffi qu\'il ait bon témoignage de ceux qui font de dehors, afin qu\'il ne tombe point en reproche, &amp; au piege du diable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 8, 'Semblablement il faut que les Diacres foyent graves, non doubles en parole, non adonnez à beaucoup de vin, non convoiteux de gain deshonnefte :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 9, 'Retenans le myftere de la foi en une confcience pure.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 10, 'Et que ceux-ci auffi foyent premierement efprouvez, puis qu\'ils fervent, eftant trouvez irreprehenfibles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 11, 'Semblablement il faut que leurs femmes foyent honneftes, non mefdifantes, fobres, fideles en toutes chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 12, 'Que les Diacres foyent maris d\'une feul femme, conduifant honneftes leurs enfans, &amp; leurs propres maifons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 13, 'Car ceux qui auront bien fervi, acquierent un bon degré pour eux, &amp; une grande liberté en la foi, laquelle eft en Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 14, 'Je t\'écris ces chofes, efperant que je viendrai bien-toft vers toi :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 15, 'Que fi je tarde, c\'eft afin que tu fçaches, comment il faut converfer en la maifon de Dieu, qui eft l\'Eglife du Dieu vivant, la colomne &amp; l\'appui de la vérité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 16, 'Et fans contredit le myftere de pieté eft grand, affavoir que Dieu a efté manifefté en chair, juftifié en Efprit, veu des Anges, prefché aux Gentils, creu au monde, &amp; enlevé en gloire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 1, 'OR l\'Efprit dit notamment qu\'és derniers temps, quelques-uns fe revolteront de la foi, s\'adonnant aux efprits abufeurs, &amp; aux doctrines des diables :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 2, 'Enfeignant des menfonges par hypocrifie, eftant cauterizez en leur propre confcience :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 3, 'Defendant de fe marier, commandant de s\'abftenir des viandes que Dieu a creées pour les fideles : &amp; pour en ufer avec action de graces.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 4, 'Car toute creature de Dieu eft bonne, &amp; rien n\'eft à rejetter, eftant pris avec action de graces.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 5, 'Car elle eft fanctifiée par la parole de Dieu, &amp; par la priere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 6, 'Si tu propofes ces chofes aux freres, tu feras bon Miniftre de Jefus Chrift, nourri és paroles de la foi &amp; de la bonne doctrine que tu as foigneufement fuivie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 7, 'Mais rejette les fables profanes, &amp; femblables à celles des vieilles : &amp; t\'exerce en la pieté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 8, 'Car l\'exercice corporel eft profitable à peu de chofes, mais la pieté eft profitable à toutes chofes, ayant les promeffes de la vie prefente, &amp; de celle qui eft à venir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 9, 'Cette parole eft certaine, &amp; digne d\'eftre entierement receuë.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 10, 'Car pour cela auffi nous travaillons, &amp; fommes en opprobre, parce que nous efperons au Dieu vivant : qui eft le confervateur de tous les hommes, mais principalement des fideles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 11, 'Annonce ces chofes, &amp; les enfeigne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 12, 'Que nul ne mefprife ta jeuneffe : mais fois patron des fideles en paroles, en converfation, en dilection, en efprit, en foi, en pureté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 13, 'Sois attentif à la lecture, à l\'exhortation, &amp; a l\'endoctrinement jufques à ce que je vienne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 14, 'Ne neglige point le don qui eft en toi, lequel t\'a efté donné par prophetie, par l\'impofotion des mains de la compagnie des Anciens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 15, 'Pratique ces chofes, &amp; y fois attentif : afin qu\'il foit connu à tous que tu profites.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 16, 'Entens à toi &amp; à l\'endoctrinement : fois perfeverant en ces chofes : car en ce faifant tu te fauveras toi-mefme, &amp; ceux qui t\'écoutent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 1, 'NE reprens pas rudement l\'homme ancien ; mais admonefte le comme pere : les jeunes comme freres :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 2, 'Les femmes anciennes comme meres : les jeunes comme fœurs, en toue pureté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 3, 'Honore les veuves qui font vraiment veuves.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 4, 'Mais fi quelque veuve a des enfans ou des enfans de fes enfans, qu\'ils apprennent premierement à montrer leur pieté envers leur propre maifon, &amp; à rendre la pareille à leurs devanciers : car cela eft bon &amp; agreable devant Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 5, 'Or celle qui eft vraiment veuve &amp; delaiffée feule, efpere en Dieu, &amp; perfevere en prieres &amp; oraifons nuit &amp; jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 6, 'Mais celle qui vit en delices, eft morte en vivant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 7, 'Denonce donc ces chofes, afin qu\'elles foyent irreprehenfibles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 8, 'Que fi quelqu\'un n\'a pas foin des fiens, &amp; principalement de ceux de fa famille, il a renié la foi, &amp; eft pire qu\'un infidele.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 9, 'Que la veuve foit enrôlée n\'ayant pas moins de foixante ans, &amp; qui ait efté femme d\'un feul mari :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 10, 'Ayant témoignage d\'avoir fait de bonnes œuvres, fi elle a nourri fes propres enfans, fi elle a logé les eftrangers, fi elle a lavé les pieds des Saints, fi elle a fubvenu aux affligez, fi elle a foigneufement fuivi toute bonne œuvre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 11, 'Mais refufe les veuves qui font plus jeunes : car quand elles font devenuës lafcives contre Chrift, elles fe veulent marier :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 12, 'Ayant leur condamnation, entant qu\'elles ont fauffé leur premiere foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 13, 'Et avec cela auffi eftant oifeufes, elles apprennent d\'aller de maifon en maifon : &amp; font non feulement oifeufes, mais auffi babillardes, &amp; curieufes, en babillant de chofes malfeantes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 14, 'Je veux donc que les jeunes veuves fe marient, qu\'elles procréent lignée, qu\'elles gouvernent le mefnage : &amp; qu\'elles ne donnent aucune occafion à l\'adverfaire de mefdire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 15, 'Car quelques-unes fe font déja devoyées apres Satan.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 16, 'Que fi quelque homme ou femme fidele a des veuves, qu\'il leur fubvienne, &amp; que l\'Eglife n\'en foit point chargée, afin qu\'il y ait affez pour celles qui font vraiment veuves.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 17, 'Que les Anciens qui prefident deuëment, foyent reputez dignes de double honneur : principalement ceux qui travaillent en la Parole &amp; en l\'endoctrinement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 18, 'Car l\'Efcriture dit, Tu n\'emmufeleras point le bœuf qui foule le grain : Et l\'ouvrier eft digne de fon falaire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 19, 'Ne reçoi point d\'accufation contre l\'Ancien, finon fous deux ou trois tefmoins.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 20, 'Reprens publiquement ceux qui pechent, afin que les autres auffi en ayent crainte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 21, 'Je t\'adjure devant Dieu, &amp; le Seigneur Jefus Chrift, &amp; les Anges éleus, que tu gardes ces chofes fans preferer l\'un à l\'autre, ne faifant rien en panchant d\'un cofté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 22, 'N\'impofe point haftivement les mains fur aucun, &amp; ne communique point aux pechez d\'autrui : garde toi pur toi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 23, 'Ne boi plus d\'eau, mais ufe d\'un peu de vin à caufe de ton eftomac, &amp; à caufe des maladies que tu as fouvent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 24, 'Les pechez de quelques-uns fe manifeftent devant, &amp; viennent en avant à leur condamnation : mais en d\'autres ils fuivent apres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 25, 'Semblablement auffi les bonnes œuvres fe manifeftent devant, &amp; celles qui font autrement ne peuvent eftre cachées.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 1, 'QUE tous ferfs qui font fous le joug, reputent leur propres maiftres dignes de tout honneur, afin que le Nom de Dieu &amp; fa doctrine ne foit blafphemée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 2, 'Et ceux qui ont des maiftres fideles, qu\'ils ne les méprifent point à caufe qu\'ils font freres, mais pluftoft qu\'ils les fervent, à caufe qu\'ils font fideles &amp; bien-aimez, eftant participans du benefice. Enfeigne ces chofes, &amp; exhorte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 3, 'Si quelqu\'un enfeigne autrement, &amp; ne confent point aux faines paroles de noftre Seigneur Jefus Chrift, &amp; à la doctrine qui eft felon la pieté ;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 4, 'Il eft enflé, ne fçachant rien, mais eftant fol apres des queftions &amp; debats de paroles, defquelles s\'engendrent envies, querelles, médifances, mauvais foupçons,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 5, 'Vaines difputes d\'hommes qui font corrompus d\'entendement, &amp; deftituez de verité, reputans la pieté eftre gain : retire toi de ceux qui font tels.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 6, 'Or la pieté avec contentement d\'efprit eft un grand gain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 7, 'Car nous n\'avons rien apporté au monde : auffi eft-il evident que nous n\'en pouvons rien emporter.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 8, 'Mais ayans la nourriture, &amp; dequoi nous puiffions eftre couverts, cela nous fuffira.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 9, 'Or ceux qui veulent devenir riches, tombent en tentation, &amp; au piege, &amp; en plufieurs defirs fols &amp; nuifibles, qui plongent les hommes en deftruction &amp; perdition.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 10, 'Car la racine de tous maux c\'eft la convoitife des richeffes : defquelles quelques-uns ayans envie, fe font defvoyez de la foi, &amp; font eux-mefmes enferrez en plufieurs douleurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 11, 'Mais toi, ô homme de Dieu, fui ces chofes, &amp; pourchaffe juftice, pieté, foi, charité, patience, debonnaireté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 12, 'Combats le bon combat de la foi : apprehende la vie eternelle, à laquelle auffi tu es appellé, &amp; en as fait une bonne profeffion devant beaucoup de tefmoins.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 13, 'Je t\'enjoins devant Dieu qui vivifie toutes chofes, &amp; devant Jefus Chrift qui a fait cette belle confeffion devant Ponce Pilate :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 14, 'Que tu gardes ce commandement, eftant fans macule &amp; fans reprehenfion, jufques à l\'apparition de noftre Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 15, 'Laquelle le bien-heureux &amp; feul Prince, Roi des rois, &amp; Seigneur des Seigneurs, montrera en fa propre faifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 16, 'Lequel feul a immortalité, &amp; habite en une lumiere inacceffible ; lequel nul des hommes n\'a veu, &amp; ne peut voir : auquel foit honneur &amp; force eternelle. Amen.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 17, 'Denonce à ceux qui font riches en ce monde, qu\'ils ne foyent point hautains : qu\'ils ne mettent point leur confiance en l\'incertitude des richeffes, mais au Dieu vivant qui nous baille toutes chofes abondamment pour en joüir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 18, 'Qu\'ils faffent du bien, qu\'ils foyent riches en bonnes œuvres, qu\'ils foyent faciles à diftribuer, communicatifs :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 19, 'Se faifans un trefor d\'un bon fondement pour l\'avenir, afin qu\'ils apprehendent la vie eternelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 20, 'O Timothée ! Garde le depoft, fuyant les crieries vaines &amp; profanes, &amp; les contradictions d\'une fcience fauffement ainfi nommée :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 21, 'De laquelle quelques-uns faifant profeffion, fe font dévoyez de la foi. Grace foit avec toi. Amen. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 1, 'PAUL Apoftre de Jefus Chrift, par la volonté de Dieu, felon la promeffe de la vie qui eft en Jefus Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 2, 'A Timothée mon fils bien-aimé, grace, mifericorde, &amp; paix, de par Dieu le Pere, &amp; de par Jefus Chrift noftre Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 3, 'Je rens graces à Dieu, auquel je fers dés mes anceftres en pure confcience, de ce que fans ceffe je fais mention de toi en mes prieres nuit &amp; jour :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 4, 'Defirant grandement de te voir, ayant fouvenance de tes larmes, afin que je fois rempli de joye :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 5, 'Me reduifant en memoire la foi non feinte qui eft en toi, laquelle a premierement habité en Loïs ta grand\' mere, &amp; en Eunice ta mere : &amp; je fuis perfuadé qu\'elle habite auffi en toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 6, 'Pour laquelle caufe je t\'admonefte que tu r\'allumes le don de Dieu qui eft en toi, par l\'impofition de mes mains.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 7, 'Car Dieu ne nous a point donné un efprit de timidité : mais de force, de dilection, &amp; de fens raffis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 8, 'Ne prens donc point à honte le témoignage de noftre Seigneur, ni moi qui fuis fon prifonnier : mais fois participant des afflictions de l\'Evangile, felon la puiffance de Dieu ;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 9, 'Qui nous a fauvez &amp; appellez par une faincte vacation : non point felon nos œuvres, mais felon fon propos arrefté &amp; la grace laquelle nous a efté donnée en Jefus Chrift devant les temps eternels :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 10, 'Et eft maintenant manifeftée par l\'apparition de noftre Sauveur Jefus Chrift, qui a deftruit la mort, &amp; amis en lumiere la vie &amp; l\'immortalité par l\'Evangile.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 11, 'A quoi je fuis eftabli Heraut &amp; Apoftre, &amp; Docteur des Gentils :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 12, 'Pour laquelle caufe auffi j\'endure ces chofes : toutefois je ne les prens point à honte : car je fçai à qui j\'ai creu, &amp; fuis perfuadé qu\'il eft puiffant pour garder mon depoft jufques à cette journée-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 13, 'Retien le vray patron des faines paroles que tu as oüies de moi, en foi &amp; en charité qui eft en Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 14, 'Garde le bon depoft par le Sainct Efprit qui habite en nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 15, 'Tu fçais cela que tous ceux qui font en Afie, fe font deftournez de moi : d\'entre lefquels font Phygelle &amp; Hermogene.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 16, 'Le Seigneur faffe mifericorde à la maifon d\'Onefiphore : car fouventefois il m\'a recreé, &amp; n\'a point pris à honte ma chaîne :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 17, 'Au contraire quand il a efté à Rome, il m\'a cherché tres-foigneufement, &amp; m\'a trouvé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 18, 'Le Seigneur lui donne de trouver mifericorde envers le Seigneur en cette journée-là : &amp; tout ce en quoi il m\'a fervi à Ephefe, tu le connois tres-bien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 1, 'TOI donc, mon fils, fois fortifié en la grace laquelle eft en Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 2, 'Et les chofes que tu as entenduës de moi entre plufieurs tefmoins, commets-les à des gens fideles, qui foyent fuffifans pour enfeigner auffi les autres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 3, 'Toi donc, endure les travaux comme bon foldat de Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 4, 'Nul qui va à la guerre ne s\'empefche des affaires de cette vie : afin qu\'il plaife à celui qui l\'a enrôllé pour la guerre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 5, 'Pareillement fi quelqu\'un combat en la lice, il n\'eft point couronné, s\'il n\'a combattu deuëment.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 6, 'Il faut que le laboureur en travaillant premierement, recueille puis apres les fruits.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 7, 'Confidere ce que je dis : &amp; le Seigneur te donne entendement en toutes chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 8, 'Souvient-toi que Jefus Chrift eft reffufcité des morts, eftant de la femence de David, felon mon Evangile :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 9, 'Auquel j\'endure des travaux jufques aux liens, comme mal-faiteur : mais la parole de Dieu n\'eft point liée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 10, 'Pour cette caufe je fouffre toutes chofes pour l\'amour des éleus, afin qu\'ils obtiennent auffi le falut qui eft en Jefus Chrift, avec gloire eternelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 11, 'Cette parole eft certaine : que fi nous mourons avec lui, nous vivrons auffi avec lui :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 12, 'Si nous fouffrons avec lui, nous regnerons auffi avec lui. Si nous le renions, il nous reniera auffi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 13, 'Si nous fommes defloyaux, il demeure fidele : il ne fe peut renier foi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 14, 'Ramentoi ces chofes, proteftant devant le Seigneur qu\'on ne debatte point de paroles : qui eft une chofe qui ne revient à aucun profit, mais à la ruïne des auditeurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 15, 'Eftudie-toi de te rendre approuvé à Dieu, ouvrier fans reproche, détaillant droitement la parole de verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 16, 'Mais reprime les vaines &amp; profanes crieries : car elles pafferont plus avant en impieté :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 17, 'Et leur parole rongera comme une gangreine ; d\'entre lefquels font Hymenée &amp; Philete :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 18, 'Qui font devoyez de la verité, en difant que la refurrection eft déja advenuë, &amp; renverfent la foi de quelques-uns.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 19, 'Toutefois le fondement de Dieu demeure ferme, ayant ce feau, Le Seigneur connoit ceux qui font fiens : &amp;, Quiconque invoque le Nom de Chrift, qu\'il fe retire d\'iniquité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 20, 'Or en une grande maifon il n\'y a pas feulement des vaiffeaux d\'or &amp; d\'argent, mais auffi de bois &amp; de terre, les uns à honneur, &amp; les autres à deshonneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 21, 'Si quelqu\'un donc fe purifie de ces chofes, il fera un vaiffeau fanctifié à honneur, &amp; utile au Seigneur, &amp; appareillé à toute bonne œuvre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 22, 'Fui auffi les defirs de jeuneffe, pourchaffe juftice, foi, charité, &amp; paix, avec ceux qui invoque de cœur pur le Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 23, 'Et rejette les queftions folles, &amp; qui font fans inftruction, fçachant qu\'elles engendrent des debats.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 24, 'Or il ne faut point, que le ferviteur du Seigneur foit debateur : mais qu\'il foit doux envers tous, propre à endoctriner, fupportant patiemment les mauvais :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 25, 'Enfeignant avec douceur ceux qui ont un fentiment contraire, pour effayer fi quelque jour Dieu leur donnera repentance pour reconnoiftre la verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 26, 'Et qu\'ils fe réveillent en fortant du piege du diable, par lequel ils ont efté pris pour faire fa volonté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 1, 'OR fçache ceci, qu\'és derniers jours il furviendra des temps facfcheux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 2, 'Car les hommes feront amateurs d\'eux-mefmes, avaricieux, vanteurs, orgueilleux, diffamateurs, defobeïffans à peres &amp; à meres, ingrats, profanes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 3, 'Sans affection naturelle, fans loyauté, calomniateurs, incontinens, cruels, haïffans les bons :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 4, 'Traites, temeraires, enflez, amateurs de voluptez pluftoft que de Dieu :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 5, 'Ayans l\'apparence de la pieté, mais ayant renié fa force : deftourne-toi auffi de telles gens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 6, 'Car d\'entre ceux-ci font ceux qui fe fourrent és maifons, &amp; qui tiennent captives les femmelettes chargées de pechez, tranfportées par diverfes convoitifes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 7, 'Lefquelles apprennent toûjours, &amp; jamais ne peuvent parvenir à la pleine connoiffance de la verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 8, 'Et comme Jannes &amp; Jambres ont refifté à Moyfe, ceux-ci pareillement refiftent à la verité : gens du tout corrompus d\'entendement, reprouvez quant à la foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 9, 'Mais ils n\'avanceront pas plus outre : car leur folie fera manifeftée à tous, comme auffi a efté celle de ceux-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 10, 'Mais toi, tu as pleinement compris ma doctrine, conduite, intention, foi, douceur, charité, patience,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 11, 'Mes perfecutions &amp; afflictions, telles qu\'elle me font advenuës à Antioche, &amp; à Iconie, &amp; à Lyftre : voire quelles perfecutions j\'ai fouftenuës, &amp; comment le Seigneur m\'a delivré de toutes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 12, 'Or tous ceux auffi qui veulent vivre felon la pieté en Jefus Chrift, fouffriront perfecution.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 13, 'Mais les hommes mauvais &amp; abufeurs s\'avanceront en empirant, feduifans &amp; eftant feduits.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 14, 'Or toi, demeure és chofes que tu as apprifes, &amp; qui t\'ont efté commifes, fçachant de qui tu les as apprifes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 15, 'Et que dés ton enfance tu as la connoiffance des fainctes Lettres, lefquelles te peuvent rendre fage à falut, par la foi qui eft en Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 16, 'Toute l\'Efcriture eft divinement infpirée, &amp; profitable à endoctriner, à convaincre, à corriger, &amp; à inftruire felon juftice :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 17, 'Afin que l\'homme de Dieu foit accompli, &amp; parfaitement inftruit à toute bonne œuvre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 1, 'JE te fomme donc devant Dieu, &amp; devant le Seigneur Jefus Chrift, qui doit juger les vivans &amp; les morts, en fon apparition &amp; en fon regne :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 2, 'Prefche la Parole, infifte en temps &amp; hors temps : reprens, tance, exhorte en toute douceur d\'efprit &amp; doctrine.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 3, 'Car il viendra un temps qu\'ils ne fouffriront point la faine doctrine : mais ayans les oreilles chatoüilleufes, ils s\'affembleront des docteurs felon leurs propres defirs :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 4, 'Et deftourneront leurs oreilles de la verité : &amp; fe tourneront aux fables.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 5, 'Mais toi, veille en toutes chofes ; endure les afflictions ; fais l\'œuvre d\'un Evangelifte, rens ton miniftere pleinement approuvé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 6, 'Car de moi, je m\'en vais maintenant eftre mis pour afperfion du facrifice, &amp; le temps de mon deflogement eft prochain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 7, 'J\'ai combattu le bon combat, j\'ai achevé la courfe, j\'ai gardé la foi :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 8, 'Quant au refte, la couronne de juftice m\'eft refervée, laquelle le Seigneur jufte juge me rendra , en cette journée-là : &amp; non feulement à moi, mais auffi à tous ceux qui auront aimé fon apparition.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 9, 'Diligente-toi de venir bien-toft vers moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 10, 'Car Demas m\'a abandonné, ayant aimé ce prefent fiecle, &amp; s\'en eft allé à Theffalonique, Crefcens en Galatie, Tite en Dalmatie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 11, 'Luc eft feul avec moi. Prens Marc &amp; l\'amene avec toi : car il m\'eft bien utile pour le miniftere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 12, 'J\'ai auffi envoyé Tychique à Ephefe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 13, 'Quand tu viendras apporte avec toi la manteline, que j\'ai laiffée à Troas chez Carpe, &amp; les livres, mais principalement les parchemins.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 14, 'Alexandre le forgeron m\'a fait fentir beaucoup de maux : le Seigneur lui rende felon fes œuvres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 15, 'Duquel auffi donne toi garde : car il a grandement refifté à nos paroles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 16, 'Nul ne m\'a affifté en ma premiere defenfe, mais tous m\'ont abandonné : qu\'il ne leur foit point imputé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 17, 'Mais le Seigneur m\'a affifté, &amp; m\'a fortifié, afin que la predication fuft renduë par moi pleinement approuvée, &amp; que tous les Gentils l\'ouïffent : &amp; j\'ai efté delivré de la gueule du Lion.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 18, 'Le Seigneur auffi me delivrera de toute mauvaife œuvre, &amp; me fauvera en fon royaume celefte : à lui foit gloire és fiecles des fiecles. Amen.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 19, 'Saluë Prifce &amp; Aquile, &amp; la famille d\'Onefiphore.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 20, 'Erafte eft demeuré à Corinthe, &amp; j\'ai laiffé Trophime malade à Milet.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 21, 'Diligente-toi de venir devant l\'hyver. Eubulus, &amp; Pudens, &amp; Linus, &amp; Claudia, &amp; tous les freres te faluënt.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 22, 'Le Seigneur Jefus Chrift foit avec ton efprit. Grace foit avec vous. Amen. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 1, 'PAUL ferviteur de Dieu, &amp; Apoftre de Jefus Chrift, felon la foi des éleus de Dieu, &amp; la connoiffance de la verité, qui eft felon la pieté :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 2, 'Sous l\'efperance de la vie eternelle, laquelle Dieu qui ne peut mentir a promife devant les temps eternels :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 3, 'Mais il l\'a manifeftée en fon propre temps, affavoir fa Parole, par la predication qui m\'eft commife, par le mandement de Dieu noftre Sauveur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 4, 'A Tite mon vrai fils, felon la foi commune entre nous : grace, mifericorde, &amp; paix, de par Dieu noftre Pere, &amp; de par le Seigneur Jefus Chrift noftre Sauveur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 5, 'La caufe pour laquelle je t\'ai laiffé en Crete, c\'eft afin que tu pourfuives de dreffer en bon ordre les chofes qui reftent, que tu eftabliffes des Anciens de ville en ville, fuivant ce que je t\'ai ordonné :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 6, 'Affavoir s\'il y a quelqu\'un qui foit irreprehenfible, mari d\'une feule femme, ayant des enfans fideles, non accufez de diffolution, ou qui ne fe puiffent ranger.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 7, 'Car il faut que l\'Evefque foit irreprehenfible, comme difpenfateur de la maifon de Dieu, non adonné à fon fens, non colere, non fujet au vin, non bateur, non convoiteux de gain deshonnefte :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 8, 'Mais hofpitalier, amateur des gens de bien, fage, jufte, faint, continent :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 9, 'Retenant ferme la Parole fidele qui eft felon inftruction, afin qu\'il foit fuffifant, tant pour admonefter par la faine doctrine, que pour convaincre les contredifans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 10, 'Car il y en a plufieurs qui ne fe peuvent ranger, caufeurs &amp; feducteurs d\'efprits : principalement ceux qui font de la Circoncifion : aufquels il faut fermer la bouche :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 11, 'Qui renverfent les maifons toutes entieres, enfeignans pour gain deshonnefte ce qu\'il ne faut point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 12, 'Quelqu\'un d\'entr\'eux, eftant leur propre prophete a dit, Les Creteins font toûjours menteurs, mauvaifes beftes, ventres pareffeux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 13, 'Ce témoignage eft veritable : pour cette caufe reprens-les vivement, afin qu\'ils foyent fains en la foi :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 14, 'Ne s\'adonnans point aux fables judaïques, &amp; aux commandemens des hommes qui fe deftournent de la verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 15, 'Toutes chofes font bien pures à ceux qui font purs : mais rien n\'eft pur aux fouillez &amp; aux infideles, au contraire leur entendement &amp; leur confcience font fouillez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 16, 'Ils font profeffion de connoiftre Dieu, mais ils le renient par œuvres : veu qu\'ils font abominables &amp; rebelles, &amp; reprouvez à toute bonne œuvre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 1, 'MAIS toi propofe les chofes qui conviennent à la faine doctrine.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 2, 'Que les hommes anciens foyent fobres, graves, bien raffis, fains en la foi, en charité, &amp; en patience.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 3, 'Pareillement que les femmes anciennes foyent d\'une contenance convenable à la fainteté, non mefdifantes, non fujettes à beaucoup de vin, enfeignant ce qui eft bon :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 4, 'Afin qu\'elles inftruifent les jeunes femmes à eftre modeftes, à aimer leurs maris, à aimer leurs enfans :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 5, 'A eftre fages, pures, gardant la maifon, bonnes, fujettes à leurs propres maris : afin que la parole de Dieu ne foit point blafmée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 6, 'Semblablement exhorte les jeunes hommes à eftre moderez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 7, 'Te montrant toi-mefme en toutes chofes pour patron de bonnes œuvres, montrant incorruption en doctrine, gravité, integrité :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 8, 'Parole faine, &amp; qu\'on ne puiffe condamner : afin que celui qui eft contraire, foit rendu confus, n\'ayant rien à dire de mal de vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 9, 'Que les ferviteurs foyent fujets à leurs maiftres, leur complaifans en toutes chofes, non contredifans :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 10, 'Ne fouftrayans rien, mais montrans toute fidelité : afin qu\'ils rendent honorable en toutes chofes la doctrine de Dieu noftre Sauveur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 11, 'Car la grace de Dieu falutaire à tous hommes, eft clairement apparuë :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 12, 'Nous enfeignant qu\'en renonçant à l\'impieté &amp; aux mondaines convoitifes, nous vivons en ce prefent fiecle, fobrement, juftement, &amp; religieufement :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 13, 'Attendans la bien-heureufe efperance &amp; l\'apparition de la gloire du grand Dieu, qui eft noftre Sauveur Jefus Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 14, 'Lequel s\'eft donné foi-mefme pour nous, afin qu\'il nous rachetaft pour lui eftre un peuple peculier, adonné aux bonnes œuvres.fes, &amp; admonnefte, &amp; reprens avec toute authorité de commander : que perfonne ne te mefprife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 15, '');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 1, 'ADMONNEFTE les qu\'ils foyent fujets aux Principautez &amp; Puiffances, qu\'ils obeïffent aux Gouverneurs, qu\'ils foyent prefts à toute bonne œuvre :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 2, 'Qu\'ils ne mefdifent de perfonne, qu\'ils ne foyent point quereleux, mais benins, &amp; montrans toute debonnaireté envers tous hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 3, 'Car nous eftions auffi autrefois infenfez, rebelles, abufez, fervans à diverfes convoitifes &amp; voluptez, vivans en malice &amp; envie, dignes d\'eftre haïs, &amp; nous haïffans l\'un l\'autre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 4, 'Mais quand la benignité &amp; l\'amour de Dieu noftre Sauveur envers les hommes eft clairement apparuë, il nous a fauvez :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 5, 'Non point par œuvres de juftice que nous euffions faites, mais felon fa mifericorde, par le lavement de la regeneration &amp; le renouvellement du Saint Efprit :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 6, 'Lequel il a efpandu abondamment en nous par Jefus Chrift noftre Sauveur :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 7, 'Afin qu\'ayant efté juftifiez par fa grace, nous foyons heritiers felon l\'efperance de la vie eternelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 8, 'Cette parole eft certaine, &amp; je veux que tu affirmes ces chofes, afin que ceux qui ont creu à Dieu, ayent foin de s\'appliquer principalement aux bonnes œuvres : voila les chofes qui font bonnes &amp; utiles aux hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 9, 'Mais reprime les folles queftions, &amp; genealogies, &amp; contentions, &amp; debats de la Loi : car ils font inutiles &amp; vains.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 10, 'Rejette l\'homme heretique, apres la premiere &amp; feconde admonition :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 11, 'Sçachant que celui eft tel eft renverfé, &amp; peche eftant condamné par foi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 12, 'Quand j\'envoyerai vers toi Artemas ou Tychique, diligente-toi de venir vers moi à Nicopolis : car j\'ai deliberé de faire là mon hyver.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 13, 'Accompagne foigneufement Zenas Docteur de la Loi, &amp; Apollos, afin que rien ne leur defaille.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 14, 'Et que les noftres auffi apprennent à s\'appliquer principalement aux bonnes œuvres, pour les ufages neceffaires, afin qu\'ils ne foyent point fans fruit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 15, 'Tous ceux qui font avec moi te faluënt. Saluë ceux qui nous aiment en foi. Grace foit avec vous tous. Amen. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 1, '');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 2, 'Et à Apphie noftre bien-aimée, &amp; à Archippe noftre compagnon d\'armes, &amp; à l\'Eglife qui eft en ta maifon :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 3, 'Grace vous foit &amp; paix, de par Dieu noftre Pere, &amp; de par le Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 4, 'Je rends graces à mon Dieu, faifant toûjours mention de toit en mes prieres :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 5, 'Entendant ta charité &amp; la foi que tu as envers le Seigneur Jefus, &amp; envers tous les Saints :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 6, 'Afin que la communication de ta foi montre fon efficace, en faifant reconnoiftre par tout le bien qui eft en vous par Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 7, 'Car frere, nous avons une grande joye &amp; confolation de ta charité, en ce que les entrailles des Saints ont efté recreées par toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 8, 'C\'eft pourquoi, bien que j\'aye une grande liberté en Chrift de te commander ce qui eft de ton devoir :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 9, 'Toutefois je te prie pluftoft par charité, encore que je fois tel, affavoir Paul ancien, &amp; mefme maintenant prifonnier de Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 10, 'Je te prie donc pour mon fils Onefime, lequel j\'ai engendré en mes liens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 11, 'Qui t\'a autrefois efté inutile, mais maintenant eft bien utile à toi &amp; à moi : &amp; lequel je te renvoye.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 12, 'Reçois le donc, c\'eft affavoir mes propres entrailles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 13, 'Lequel je voulois retenir par devers moi, afin qu\'il me fervift au lieu de toi, aux liens de l\'Evangile.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 14, 'Mais je n\'ai rien voulu faire fans ton advis, afin que ton bien ne fuft point comme par contrainte, mais comme volontaire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 15, 'Car pour cette caufe peut-eftre a-t\'il efté feparé de toi pour un temps, afin que tu le recouvraffes pour jamais :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 16, 'Non plus comme ferf, mais au deffus de ferf, c\'eft affavoir comme frere bien-aimé, principalement de moi, &amp; combien plus de toi, &amp; felon la chair &amp; felon le Seigneur ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 17, 'Si donc tu me tiens pour compagnon, reçois-le comme moi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 18, 'Que s\'il t\'a fait quelque tort, ou s\'il te doit, mets-le moi en compte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 19, 'Moi Paul ai efcrit de ma propre main, je le payerai : afin que je ne te die point que mefmes tu te dois toi-mefme à moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 20, 'Voire, frere, que je reçoive ce plaifir de toi au Seigneur : recrée mes entrailles au Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 21, 'Je t\'ai efcrit m\'affeurant de ton obeïffance, fçachant que tu feras mefmes plus que je ne dis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 22, 'Mais auffi quand &amp; quand prepare moi un logis : car j\'efpere que je vous ferai donné par vos prieres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 23, 'Epaphras prifonnier avec moi en Jefus Chrift te faluë ;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 24, 'Enfemble Marc, Ariftarque, Demas, &amp; Luc, mes compagnons d\'œuvre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 25, 'La grace de noftre Seigneur Jefus Chrift foit avec voftre efprit. Amen. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 1, 'DIEU ayant jadis, à plufieurs manieres, parlé aux Peres par les Prophetes,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 2, 'A parlé à nous en ces derniers jours par fon Fils, lequel il a eftabli heritier de toutes chofes : par lequel auffi il a fait les fiecles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 3, 'Lequel Fils eftant la refplendeur de la gloire, &amp; la marque engravée de la perfonne d\'icelui, &amp; fouftenant toutes chofes par fa parole puiffance, ayant fait par foi-mefme la purgation de nos pechez, s\'eft affis à la dextre de la Majefté és lieux tres-hauts :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 4, 'Eftant fait d\'autant plus excellent que les Anges, qu\'il a herité un nom plus excellent par deffus eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 5, 'Car auquel des Anges a-t\'il jamais dit, C\'eft toi qui es mon Fils, je t\'ai aujourd\'hui engendré ? Et derechef, Je lui ferai Pere, &amp; il me fera Fils ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 6, 'Et encore quand il introduit au monde fon Fils premier-né, il dit : Et que tous les Anges de Dieu l\'adorent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 7, 'Et quant aux Anges, il dit, Faifant fes Anges les vents, &amp; fes miniftres la flamme de feu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 8, 'Mais il dit quant au Fils, O Dieu ! Ton trône demeure és fiecles des fiecles, &amp; le fceptre de ton royaume eft un fceptre de droiture.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 9, 'Tu as aimé la juftice, &amp; as haï l\'iniquité : pour cette caufe, ô Dieu ! Ton Dieu t\'a oinct d\'huile de lieffe par deffus tes compagnons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 10, 'Item, toi, Seigneur, as fondé la terre dés le commencement, &amp; les cieux font les œuvres de tes mains.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 11, 'Ils periront, mais tu es permanent : &amp; ils s\'en vieilliront tous comme un veftement :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 12, 'Et tu les ployeras en rouleau comme un habit, &amp; ils feront changez : mais toi, tu es le mefme, &amp; tes ans ne defaudront point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 13, 'Et auquel des Anges a-t\'il jamais dit, Sieds toi à ma dextre, jufques à ce que j\'aye mis tes ennemis pour le marchepied de tes pieds ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 14, 'Ne font-ils pas tous des efprits adminiftrateurs, envoyez pour fervir, pour l\'amour de ceux qui doivent recevoir l\'heritage de falut ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 1, 'POUR cette caufe il nous faut prendre de plus prés garde aux chofes que nous avons ouïes, afin que nous ne venions point à nous écouler.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 2, 'Car fi la parole prononcée par les Anges, a efté ferme, &amp; toute tranfgreffion &amp; defobeïffance a receu une jufte retribution :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 3, 'Comment efchapperons-nous fi nous negligeons un fi grand falut, lequel ayant premierement commencé d\'eftre declaré par le Seigneur nous a efté confirmé par ceux qui l\'avoyent ouï ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 4, 'Dieu en outre leur rendant enfemble témoignage par fignes &amp; miracles, &amp; diverfes vertus, &amp; diftributions du Sainct Efprit, felon fa volonté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 5, 'Car ce n\'eft point aux Anges qu\'il a affujetti le monde à venir, duquel nous parlons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 6, 'Et quelqu\'un a tefmoigné en quelque lieu, difant, Qu\'eft-ce que de l\'homme, que tu te fouviennes de lui : ou du fils de l\'homme que tu le vifites ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 7, 'Tu l\'as fait un petit moindre que les Anges, tu l\'as couronné de gloire &amp; d\'honneur, &amp; l\'as eftabli fur les œuvres de tes mains.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 8, 'Tu as affujetti toutes chofes fous fes pieds. Or en ce qu\'il lui a affujetti toutes chofes, il n\'a rien laiffé qui ne lui foit affujetti. Si eft-ce que nous ne voyons point encore maintenant toutes chofes lui eftre affujetties.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 9, 'Mais nous voyons couronné de gloire &amp; d\'honneur celui qui avoit efté fait un petit moindre que les Anges, affavoir Jefus, par la paffion de fa mort, afin que par la grace de Dieu il gouftaft la mort pour tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 10, 'Car il eftoit convenable que celui pour lequel font toutes chofes, &amp; par lequel font toutes chofes, puis qu\'il amenoit plufieurs enfans à la gloire, confacraft le Prince de leur falut par afflictions.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 11, 'Car &amp; celui qui fanctifie, &amp; ceux qui font fanctifiez, font tous d\'un : pour laquelle caufe il ne prend point à honte de les appeller freres,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 12, 'Difant, J\'annoncerai ton Nom à mes freres, &amp; te louërai au milieu de l\'affemblée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 13, 'Et derechef, Je me confierai en lui. Et encore, Me voici, moi &amp; les enfans que Dieu m\'a donnez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 14, 'Puis donc que les enfans participent à la chair &amp; au fang, lui auffi femblablement a participé aux mefmes chofes, afin que par la mort il deftruifift celui qui avoit l\'empire de la mort, c\'eft affavoir le diable :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 15, 'Et qu\'il delivraft tous ceux qui pour la crainte de la mort, eftoyent toute leur vie affujettis à la fervitude.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 16, 'Car certes il n\'a nullement pris les Anges, mais il a pris la femence d\'Abraham.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 17, 'C\'eft pourquoi il a fallu qu\'il fuft femblable en toutes chofes à fes freres, afin qu\'il fuft fouverain Sacrificateur, mifericordieux, &amp; fidele és chofes qui doivent eftre faites envers Dieu, afin de faire propitiation pour les pechez du peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 18, 'Car par ce qu\'il a fouffert eftant tenté, il eft auffi puiffant pour fecourir ceux qui font tentez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 1, 'C\'EST pourquoi, freres faints, qui eftes participans de la vocation celefte, confiderez Jefus Chrift l\'Apoftre &amp; le fouverain Sacrificateur de noftre profeffion,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 2, 'Qui eft fidele à celui qui l\'a eftabli, ainfi que Moyfe auffi eftoit fidele en toute la maifon d\'icelui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 3, 'Car celui-ci eft reputé digne d\'une plus grande gloire que Moyfe, entant que celui qui a edifié la maifon, eft en plus grande dignité que la maifon mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 4, 'Car toute maifon eft baftie par quelqu\'un : or celui qui a bafti toutes ces chofes c\'eft Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 5, 'Et quant à Moyfe, il a bien efté fidele en toute la maifon d\'icelui comme ferviteur, pour tefmoigner les chofes qui devoyent eftre dites :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 6, 'Mais Chrift eft comme Fils fur fa maifon ; duquel nous fommes la maifon, fi nous retenons ferme jufques à la fin l\'affeurance &amp; la gloire de l\'efperance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 7, 'Partant ainfi que dit le Saint Efprit, Aujourd\'hui fi vous oyez fa voix,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 8, 'N\'endurciffez point vos cœurs, ainfi qu\'en l\'irritation, au jour de la tentation au defert :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 9, 'Là où vos peres m\'ont tenté, &amp; m\'ont efprouvé, &amp; ont veu mes œuvres par quarante ans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 10, 'C\'eft pourquoi j\'ai efté ennuyé de cette generation, &amp; ai dit, Ils errent toûjours en leurs cœurs, &amp; ils n\'ont point connu mes voyes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 11, 'Dont j\'ai juré en mon ire : Si jamais ils entrent en mon repos.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 12, 'Freres, prenez garde qu\'il n\'y ait en quelqu\'un de vous un mauvais cœur d\'incredulité pour fe revolter du Dieu vivant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 13, 'Mais exhortez-vous l\'un l\'autre chaque jour, tandis que ce jourd\'hui eft nommé, de peur que quelqu\'un d\'entre vous ne s\'endurciffe par feduction de peché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 14, 'Car nous avons efté faits participans de Chrift, voire fi nous retenons ferme jufques à la fin, le commencement de ce qui nous fouftient :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 15, 'Pendant qu\'il eft dit, Aujourd\'hui fi vous oyez fa voix, n\'endurciffez point vos cœurs, ainfi qu\'en l\'irritation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 16, 'Car quelques-uns l\'ayans ouïe, le provoquerent à ire : mais non pas tous ceux qui fortirent hors d\'Egypte par Moyfe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 17, 'Mais defquels a-t\'il efté ennuyé par quarante ans ? N\'a-ce pas efté de ceux qui pecherent, defquels les corps tomberent au defert ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 18, 'Et aufquels jura-t\'il qu\'ils n\'entreroyent point en fon repos, finon à ceux qui furent rebelles ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 19, 'Ainfi nous voyons qu\'ils n\'y peurent entrer, à caufe de leur incredulité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 1, 'CRAIGNONS donc que quelqu\'un d\'entre vous ayant delaiffé la promeffe d\'entrer au repos d\'icelui ne s\'en trouve privé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 2, 'Car il a efté evangelifé comme auffi à ceux-là : mais la parole de la predication ne leur a de rien profité, parce qu\'elle n\'eftoit point meflée avec la foi, en ceux qui l\'oüirent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 3, 'Car nous qui avons creu, entrerons au repos, fuivant ce qui a efté dit, Partant j\'ai juré en mon ire, Si jamais ils entrent en mon repos ; bien que les ouvrages d\'icelui fuffent déja achevez dés la fondation du monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 4, 'Car il a dit ainfi en quelque lieu, touchant le feptiéme jour, Et Dieu fe repofa de tous fes ouvrages au feptiéme jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 5, 'Et derechef en ce paffage, S\'ils entrent en mon repos.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 6, 'Puis donc qu\'il refte que quelques-uns y entre, &amp; que ceux aufquels premierement il a efté evangelizé, n\'y font point entrez à caufe de leur rebellion :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 7, 'Il determine derechef un certain jour, fçavoir aujourd\'hui fi vous oyez fa voix, n\'endurciffez point vos cœurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 8, 'Car fi Jofué les euft introduits au repos, jamais apres cela il n\'euft parlé d\'un autre jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 9, 'Il refte donc un repos pour le peuple de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 10, 'Car celui qui eft entré au repos d\'icelui, s\'eft repofé auffi de fes œuvres, comme Dieu s\'eft repofé des fiennes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 11, 'Eftudions-nous donc d\'entrer en ce repos-là, de peur que quelqu\'un ne tombe par un mefme exemple de rebellion.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 12, 'Car la parole de Dieu eft vivante &amp; d\'efficace, &amp; plus penetrante que nulle efpée à deux trenchans : &amp; elle atteint jufques à la divifion de l\'ame, &amp; de l\'efprit, &amp; des jointures, &amp; des moüelles, &amp; eft juge des penfées &amp; intentions du cœur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 13, 'Et il n\'y a creature aucune qui foit cachée devant lui : mais toutes chofes font nuës &amp; entierement ouvertes aux yeux de celui devant lequel nous avons à faire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 14, 'Puis donc que nous avons un fouverain &amp; grand Sacrificateur, Jefus Fils de Dieu, qui eft entré és cieux, tenons ferme la profeffion.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 15, 'Car nous n\'avons point un fouverain Sacrificateur, qui ne puiffe avoir compaffion de nos infirmitez : mais nous avons celui qui a efté tenté de mefme que nous en toutes chofes, horfmis le peché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 16, 'Allons donc avec affeurance au thrône de grace : afin que nous obtenions mifericorde, &amp; que nous trouvions grace, pour eftre aidez en temps convenable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 1, 'OR tout fouverain Sacrificateur fe prenant d\'entre les hommes, eft eftabli pour les hommes és chofes qui fe font envers Dieu, afin qu\'il offre des dons &amp; des facrifices pour les pechez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 2, 'Eftant propre à avoir fuffifament pitié des ignorans &amp; errans : dautant que lui-mefme auffi eft environné d\'infirmité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 3, 'Et à caufe d\'elle il doit offrir pour les pechez, comme pour le peuple, auffi pour foi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 4, 'Or nul ne s\'attribuë cet honneur, mais celui-là en joüit, qui eft appellé de Dieu, comme Aaron.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 5, 'Pareillement auffi Chrift ne s\'eft point glorifié foi-mefme pour eftre fait fouverain Sacrificateur : mais celui l\'a glorifié, qui lui a dit, C\'eft toi qui es mon Fils, je t\'ai aujourd\'hui engendré.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 6, 'Comme auffi en un autre lieu il dit, Tu es Sacrificateur eternellement à la façon de Melchifedech.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 7, 'Lequel és jours de fa chair, ayant offert avec grand cri &amp; larmes des prieres &amp; des fupplications, à celui qui le pouvoit fauver de mort, &amp; ayant efté exaucé de ce qu\'il craignoit :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 8, 'Encore qu\'il fuft Fils, toutefois il a appris l\'obeïffance par les chofes qu\'il a fouffertes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 9, 'Et eftant confacré, il a efté autheur de falut eternel à tous ceux qui lui obeïffent :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 10, 'Eftant appellé de Dieu, fouverain Sacrificateur à la façon de Melchifedech.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 11, 'Duquel nous avons un long propos à dire, &amp; difficile à declarer : veu que vous eftes devenus lafches à oüir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 12, 'Parce que là où vous devriez eftre maiftres, veu le temps, vous avez derechef befoin qu\'on vous enfeigne quels font les rudimens du commencement des paroles de Dieu : &amp; vous eftes devenus tels que vous avez encore befoin de lait, &amp; non pas de viande ferme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 13, 'Car quiconque ufe de lait, ne fçait ce que c\'eft de la Parole de juftice : car il eft enfant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 14, 'Mais la viande ferme eft pour ceux qui font déja hommes faits, affavoir pour ceux qui par l\'habitude ont les fens exercez à difcerner le bien &amp; le mal.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 1, 'C\'EST pourquoi laiffans la parole qui donne le commencement de Chrift, tendons à la perfection, ne mettans point derechef le fondement de la repentance des œuvres mortes, &amp; de la foi en Dieu,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 2, 'De la doctrine des baptefmes, &amp; de l\'impofition des mains, de la refurrection des morts, &amp; du jugement eternel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 3, 'Et nous ferons cela, fi Dieu le permet.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 4, 'Car il eft impoffible que ceux qui ont une fois efté illuminez, &amp; ont goufté le don celefte, &amp; ont efté faits participans du Saint Efprit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 5, 'Et ont goufté la bonne Parole de Dieu, &amp; les puiffances du fiecle à venir :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 6, 'S\'ils retombent, foyent derechef renouvellez à repentance, veu qu\'ils crucifient derechef le Fils de Dieu quant à eux, &amp; l\'expofent à opprobre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 7, 'Car la terre qui boit fouvent la pluye qui vient fur elle, &amp; produit de l\'herbage propre à ceux defquels elle eft labourée, reçoit la benediction de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 8, 'Mais celle qui produit des efpines &amp; des chardons, eft rejettée, &amp; prochaine de malediction : de laquelle la fin tend à eftre bruflée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 9, 'Or nous fommes perfuadez quant à vous, bien-aimez, de chofes meilleures &amp; convenables à falut, encore que nous parlions ainfi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 10, 'Car Dieu n\'eft point injufte, pour mettre en oubli voftre œuvre, &amp; le travail de la charité que vous avez montrée envers fon nom, entant que vous avez fubvenu aux Saints &amp; y fubvenez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 11, 'Mais nous defirons que chacun de vous montre le mefme foin, pour la pleine certitude de l\'efperance jufques à la fin :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 12, 'Afin que vous ne deveniez point lafches, mais que vous enfuiviez ceux qui par foi &amp; patience reçoivent les promeffes en heritage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 13, 'Car quand Dieu fit la promeffe à Abraham, parce qu\'il ne pouvoit jurer par un plus grand, il jura par foi-mefme,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 14, 'Difant, Certes je te benirai abondamment, &amp; te multiplierai merveilleufement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 15, 'Et ainfi Abraham ayant attendu patiemment, obtint la promeffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 16, 'Car les hommes jurent par un plus grand qu\'eux : &amp; le ferment fait pour confirmation, leur eft la fin de tout different.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 17, 'En quoi Dieu voulant montrer d\'abondant l\'immuable fermeté de fon confeil aux heritiers de la promeffe, s\'eft interpofé par ferment :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 18, 'Afin que par deux chofes immuables (efquelles il eft impoffible que Dieu mente) nous ayons une ferme confolation, nous qui avons noftre refuge à obtenir l\'efperance qui nous eft propofée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 19, 'Laquelle nous tenons comme une anchre feure &amp; ferme de l\'ame, &amp; penetrante jufques au dedans du voile :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 20, 'Où Jefus eft entré comme avant-coureur pour nous, eftant fait fouverain Sacrificateur eternellement, à la façon de Melchifedech.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 1, 'CAR ce Melchifedech eftoit Roi de Salem, Sacrificateur du Dieu Souverain, lequel vint au devant d\'Abraham comme il retournoit de la desfaite des Rois, &amp; le benit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 2, 'Auquel auffi Abraham partagea la difme de tout : &amp; premierement eft interpreté Roi de juftice, &amp; puis auffi Roi de Salem, c\'eft à dire, Roi de paix :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 3, 'Sans pere, fans mere, fans genealogie, n\'ayant ni commencement de jours, ni fin de vie : mais eftant fait femblable au Fils de Dieu il demeure Sacrificateur à toûjours.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 4, 'Or confiderez combien grand a efté celui-ci, auquel mefmes Abraham le Patriarche donna la difme du butin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 5, 'Et quant à ceux d\'entre les enfans de Levi qui recevoyent la Sacrificature, ils ont bien l\'ordonnance de difmer le peuple felon la Loi, (c\'eft à dire leurs freres) bien qu\'ils foyent fortis des reins d\'Abraham.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 6, 'Mais celui qui n\'eft point conté d\'une mefme race qu\'eux, a difmé Abraham, &amp; a benit celui qui avoit les promeffes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 7, 'Or fans contredit quelconque, ce qui eft moindre, eft benit par ce qui eft plus grand.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 8, 'Et ici les hommes qui font mortels, prennent les difmes : mais là celui les prend duquel il eft témoigné qu\'il eft vivant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 9, 'Et, par maniere de parler, Levi mefme, qui prend les difmes, a efté difmé en Abraham.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 10, 'Car il eftoit encore és reins de fon pere, quand Melchifedech lui vint au devant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 11, 'Si donc la perfection euft efté en la Sacrificature Levitique, (car le peuple a receu la Loi fous elle) quel befoin eftoit-il davantage qu\'un autre Sacrificateur fe levaft à la façon de Melchifedech, &amp; qui ne fuft point dit à la façon d\'Aaron ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 12, 'Car la Sacrificature eftant changée, il eft neceffaire qu\'il y ait auffi un changement de Loi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 13, 'Car celui au regard duquel ces chofes font dites, appartient à une autre tribu, de laquelle nul n\'a affifté à l\'autel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 14, 'Veu qu\'il eft notoire que noftre Seigneur eft iffu de Juda, au regard de laquelle tribu Moyfe n\'a rien dit de la Sacrificature.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 15, 'Et d\'abondant ceci eft encore plus manifefte, dautant qu\'un autre Sacrificateur à la fimilitude de Melchifedech, eft mis en avant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 16, 'Lequel n\'a point efté faits Sacrificateur felon la Loi du commandement charnel, mais felon la puiffance de la vie non periffable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 17, 'Car il témoigne ainfi, Tu es Sacrificateur eternellement, à la façon de Melchifedech.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 18, 'Car il fe fait abolition du mandement precedent, à caufe de fa foibleffe, &amp; qu\'il ne pouvoit profiter.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 19, 'Car la Loi n\'a rien amené à perfection : mais ce qui a efté introduit par deffus, affavoir une meilleure efperance, par laquelle nous approchons de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 20, 'Et mefmes entant que ce n\'a point efté fans ferment : car ceux-là ont efté faits Sacrificateurs fans ferment :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 21, 'Mais celui-ci avec ferment, par celui qui lui a dit, Le Seigneur à juré, &amp; ne s\'en repentira point, Tu es Sacrificateur eternellement, à la façon de Melchifedech.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 22, 'D\'une tant plus excellente alliance, Jefus a efté fait pleige.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 23, 'Davantage, quant aux Sacrificateurs, il en a efté fait plufieurs, parce que la mort les empefchoit de durer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 24, 'Mais celui-ci, parce qu\'il demeure eternellement, a une Sacrificature perpetuelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 25, 'Et partant auffi il peut fauver à plein ceux qui s\'approchent de Dieu par lui ; eftant toûjours vivant pour interceder pour eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 26, 'Car il nous convenoit d\'avoir un tel fouverain Sacrificateur, fainct, innocent, fans macule, feparé des pecheurs, &amp; exalté par deffus les cieux :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 27, 'Qui n\'euft point de neceffité, comme les fouverains Sacrificateurs, d\'offrir tous les jours des facrifices, premierement pour fes pechez, puis apres pour ceux du peuple : car il a fait ceci une fois, s\'eftant offert foi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 28, 'Car la Loi ordonne pour fouverains Sacrificateurs des hommes infirmes : mais la Parole du ferment, qui eft apres la Loi, ordonne le Fils qui eft confacré à jamais.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 1, 'OR la fomme de noftre propos, c\'eft que nous avons un tel fouverain Sacrificateur, qui eft affis à la dextre du trône de la majefté de Dieu és cieux,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 2, 'Miniftre du Sanctuaire &amp; du vrai Tabernacle, lequel le Seigneur à fiché, &amp; non point l\'homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 3, 'Car tout fouverain Sacrificateur eft ordonné pour offrir des dons &amp; des facrifices : c\'eft pourquoi il eft neceffaire que celui-ci auffi ait quelque chofe pour offrir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 4, 'Car s\'il eftoit fur la terre, il ne feroit pas mefme Sacrificateur, pendant qu\'il y auroit des Sacrificateurs offrans les dons felon la Loi :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 5, 'Lefquels fervent au patron &amp; à l\'ombre des chofes celeftes, felon qu\'il fut refpondu de par Dieu à Moyfe, quand il devoit achever le Tabernacle, Or advife (dit-il) que tu faffes toutes chofes felon le patron qui t\'a efté montré en la montagne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 6, 'Mais maintenant noftre fouverain Sacrificateur a obtenu un miniftere d\'autant plus excellent qu\'il eft Mediateur d\'une plus excellente alliance, qui eft eftablie fous de meilleures promeffes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 7, 'Car s\'il n\'y euft eu rien à redire en cette premiere-là, il n\'euft jamais efté cherché de lieu à une feconde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 8, 'Car en les reprenant il leur dit, Voici, les jours viendront, dit le Seigneur, que j\'accomplirai fur la maifon d\'Ifraël &amp; fur la maifon de Juda une nouvelle alliance :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 9, 'Non point felon l\'alliance que je difpofai envers leurs peres, au jour que je les pris par la main, pour les tirer hors du païs d\'Egypte : parce qu\'ils n\'ont point perfifté en mon alliance, &amp; je les ai mefprifez, dit le Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 10, 'Car voici l\'alliance que je difpoferai envers la maifon d\'Ifraël apres ces jours-là, dit le Seigneur, C\'eft que je mettrai mes loix en leur entendement, &amp; les efcrirai en leur cœur, &amp; je leur ferai Dieu, &amp; ils me feront peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 11, 'Et chacun n\'enfeignera point fon prochain, ni chacun fon frere, difant, Connoi le Seigneur : car tous me connoiftront, depuis le plus petit jufques au plus grand d\'entr\'eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 12, 'Car je ferai appaifé quant à leurs injuftices, &amp; n\'aurai plus fouvenance de leurs pechez, ni de leurs iniquitez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 13, 'En difant une nouvelle, il envieillit la premiere : or ce qui devient vieil &amp; ancien, eft prés d\'eftre aboli.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 1, 'LA premiere alliance donc avoit auffi des ordonnances du fervice divin, &amp; le Sanctuaire mondain.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 2, 'Car le Tabernacle a efté conftruit, affavoir le premier où eftoit le chandelier, &amp; la table, &amp; les pains de propofition : qui eft appellé, Les lieux faints.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 3, 'Et apres le fecond voile, il y avoit le Tabernacle, qui eft appellé, Le lieu tres-faint :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 4, 'Ayant un encenfoir d\'or, &amp; l\'Arche de l\'Alliance entierement couverte d\'or à l\'entour : en laquelle eftoit une cruche d\'or, où eftoit la Manne, &amp; la verge d\'Aaron qui avoit fleuri, &amp; les Tables de l\'alliance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 5, 'Et fur cette Arche eftoyent les Cherubins de gloire, faifant ombre au Propitiatoire, defquelles chofes il n\'eft pas befoin maintenant de parler par le menu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 6, 'Or ces chofes eftant ainfi ordonnées, les Sacrificateurs entrent bien toûjours au premier Tabernacle pour accomplir le fervice.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 7, 'Mais le feul fouverain Sacrificateur entre au fecond une fois l\'an, &amp; cela non point fans fang, lequel il offre pour foi-mefme, &amp; pour les fautes du peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 8, 'Le Saint Efprit declarant par cela, que le chemin des lieux faints n\'eft point encore manifefté, tandis que le premier Tabernacle eft encore debout, qui eftoit figure pour le temps d\'alors :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 9, 'Durant lequel des dons &amp; des facrifices eftoyent offerts, qui ne pouvoyent fanctifier quant à la confcience celui qui faifoit le fervice,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 10, 'Seulement ordonnez en viandes, &amp; breuvages, &amp; divers lavemens : &amp; ceremonies charnelles, jufques au temps que cela foit redreffé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 11, 'Mais Chrift eftant venu pour eftre le fouverain Sacrificateur des biens à venir, par un plus grand &amp; plus parfait tabernacle, non point fait de main, c\'eft à dire non point de cette ftructure :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 12, 'Et non point le fang des boucs ou des veaux, mais par fon propre fang, eft entré une fois és lieux faints, ayant obtenu une redemption eternelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 13, 'Car fi le fang des taureaux &amp; des boucs, &amp; la cendre de la genice, dont on fait afperfion, fanctifie les foüillez quant à la pureté de la chair :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 14, 'Combien plus le fang de Chrift, qui par l\'Efprit eternel s\'eft offert à Dieu foi-mefme fans nulle tache, purifiera-t\'il voftre confcience des œuvres mortes, pour fervir le Dieu vivant ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 15, 'Et partant il eft Mediateur du Nouveau Teftament, afin que la mort entrevenant pour la rançon des tranfgreffions, lefquelles eftoyent fous le premier Teftament, ceux qui font appellez reçoivent la promeffe de l\'heritage eternel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 16, 'Car où il y a un teftament, il eft neceffaire que la mort du teftateur entrevienne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 17, 'Car és morts le teftament eft confirmé, veu qu\'il n\'a point encore de vertu durant que le teftateur eft en vie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 18, 'C\'eft pourquoi auffi le premier mefme n\'a point efté dedié fans fang.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 19, 'Car apres que Moyfe recité à tout le peuple tous les commandemens felon la Loi, ayant pris le fang des veaux &amp; des boucs, avec de l\'eau &amp; de la laine teinte en pourpre, &amp; de l\'hyffope, il en fit afperfion fur le livre, &amp; fur tout le peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 20, 'Difant, C\'eft ici le fang du Teftament, lequel Dieu vous a ordonné.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 21, 'Puis auffi il fit afperfion du fang femblablement, fur le Tabernacle &amp; fur tous les vaiffeaux du fervice.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 22, 'Et prefque toutes chofes felon la Loi font purifiées par fang, &amp; fans effufion de fang il ne fe fait point de remiffion.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 23, 'Il a donc fallu que les chofes qui reprefentoyent celles qui font és cieux, fuffent purifiées par de telles chofes, mais que les celeftes foyent purifiées par de plus excellens facrifices que ceux-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 24, 'Car Chrift n\'eft point entré és lieux faints faits de main, qui eftoyent des figures correfpondantes aux vrais, mais eft entré au ciel mefme, pour maintenant comparoiftre pour nous devant la face de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 25, 'Mais non point qu\'il s\'offre fouventefois foi-mefme, ainfi que le fouverain Sacrificateur entre és lieux faints chacun an avec un autre fang :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 26, '(Autrement il lui euft fallu fouventefois fouffert depuis la fondation du monde :) mais maintenant en la confommation des fiecles, il eft comparu une fois pour l\'abolition du peché, par le facrifice de foi-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 27, 'Et tout ainfi qu\'il eft ordonné aux hommes de mourir une fois, &amp; apres cela s\'enfuit le jugement :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 28, 'Pareillement auffi Chrift ayant efté offert une fois, pour ofter les pechez de plufieurs, apparoiftra pour la feconde fois fans peché à ceux qui l\'attendent à falut.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 1, 'CAR la Loi ayant l\'ombre des biens à venir, non point la vive image des chofes, ne peut jamais par les mefmes facrifices, lefquels on offre chacun an continuellement, fanctifier ceux qui s\'y addreffent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 2, 'Autrement n\'euffent-ils pas ceffé d\'eftre offerts, veu que les facrifians eftant purifiez une fois n\'euffent plus eu aucune confcience de peché ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 3, 'Or il y a en ces facrifices-là une commemoration des pechez reïterée d\'an en an.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 4, 'Car il eft impoffible que le fang des taureaux &amp; des boucs ofte les pechez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 5, 'C\'eft pourquoi, entrant au monde il dit, Tu n\'as point voulu de facrifice ni d\'offrande, mais tu m\'as approprié un corps :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 6, 'Tu n\'as point pris de plaifir és holocauftes, ni en l\'oblation pour le peché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 7, 'Alors j\'ai dit, Me voici, je viens : au commencement du livre il eft efcrit de moi, que je faffe ô Dieu ! Ta volonté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 8, 'Ayant dit auparavant, Tu n\'as point voulu de facrifice, ni d\'offrande, ni d\'holocauftes, ni d\'oblation pour le peché, &amp; tu n\'y as point pris plaifir : (lefquelles chofes font offertes felon la Loi :) alors il a dit, Me voici, je viens afin de faire, ô Dieu ! Ta volonté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 9, 'Il ofte le premier, afin qu\'il eftabliffe le fecond.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 10, 'Par laquelle volonté nous fommes fanctifiez, affavoir par l\'oblation une feul fois faite du corps de Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 11, 'Tout Sacrificateur donc affifte chaque jour, adminiftrant &amp; offrant fouventefois les mefmes facrifices, lefquels ne peuvent jamais ofter les pechez :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 12, 'Mais celui-ci ayant offert un feul facrifice pour le pechez, eft affis pour toûjours à la dextre de Dieu :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 13, 'Attendant ce qui refte, affavoir jufques à ce que fes ennemis foyent mis pour le marchepied de fes pieds.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 14, 'Car par une feul oblation il a confacré pour toûjours ceux qui font fanctifiez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 15, 'Et auffi le Saint Efprit nous le témoigne : car apres avoir dit en premier lieu,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 16, 'C\'eft ici l\'alliance que je traiterai avec eux apres ces jour-là, dit le Seigneur, Je mettrai mes loix en leur cœur, &amp; les efcrirai en leurs entendemens :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 17, 'Et n\'aurai plus fouvenance de leurs pechez, ni de leurs iniquitez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 18, 'Or là où il y a remiffion de ces chofes, il n\'y a plus d\'oblation pour le peché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 19, 'Veu donc, freres, que nous avons la liberté d\'entrer aux lieux faints, par le fang de Jefus,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 20, 'Par le chemin lequel il nous a dedié nouveau &amp; vivant, par le voile, c\'eft à dire par fa propre chair :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 21, 'Et que nous avons un grand Sacrificateur commis fur la maifon de Dieu :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 22, 'Allons avec un vrai cœur en pleine certitude de foi, ayant les cœurs purifiez de mauvaife confcience, &amp; le corps lavé d\'eau nette.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 23, 'Retenons la profeffion de noftre efperance fans varier : car celui qui l\'a promis, eft fidele.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 24, 'Et prenons garde l\'un à l\'autre, afin de nous inciter à la charité &amp; aux bonnes œuvres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 25, 'Ne delaiffant point noftre mutuelle affemblée, comme quelques-uns ont de couftume : mais nous admoneftant l\'un l\'autre : &amp; cela d\'autant plus que vous voyez approcher le jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 26, 'Car fi nous pechons volontairement apres avoir la connoiffance de la verité, il ne refte plus de facrifice pour les pechez :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 27, 'Mais une attente terrible de jugement, &amp; la ferveur d\'un feu qui doit devorer les adverfaires.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 28, 'Si quelqu\'un avoit mefprifé la Loi de Moyfe, il mouroit fans aucune mifericorde, fur le témoignage de deux ou de trois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 29, 'Combien pires tourmens penfez-vous que meritera celui qui aura foulé aux pieds le Fils de Dieu, &amp; qui aura tenu pour une chofe profane le fang de l\'Alliance, par lequel il avoit efté fanctifié : &amp; qui aura outragé l\'efprit de grace ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 30, 'Car nous connoiffons celui qui a dit, A moi eft la vengeance, &amp; je le rendrai, dit le Seigneur. Et derechef, Le Seigneur jugera fon peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 31, 'C\'eft une chofe terrible de tomber és mains du Dieu vivant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 32, 'Ramentevez-vous les jours precedens, efquels apres avoir efté illuminez, vous avez fouftenu un grand combat de fouffrances :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 33, 'Quand d\'une part vous avez efté efchaffaudez devant tous, par opprobres &amp; tribulations : &amp; quand d\'autre part vous avez efté faits compagnons de ceux qui eftoyent ainfi haraffez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 34, 'Car vous avez auffi efté participans de l\'affliction de mes liens, &amp; avez receu en joye le raviffement de vos biens : connoiffant en vous-mefmes que vous avez une meilleure fubftance és cieux, &amp; qui eft permanente.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 35, 'Ne rejettez donc point au loin voftre confiance, laquelle a une grande remuneration.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 36, 'Car vous avez befoin de patience, afin qu\'ayant fait la volonté de Dieu, vous en remportiez la promeffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 37, 'Car encore tant foit peu de temps, &amp; celui qui doit venir, viendra, &amp; ne tardera point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 38, 'Or le jufte vivra de foi : mais fi quelqu\'un fe fouftrait, mon ame ne prend point de plaifir en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 39, 'Mais nous ne fommes point pour nous fouftraire à perdition, mais pour fuivre la foi pour la confervation de l\'ame.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 1, 'OR la foi eft une fubfiftence des chofes qu\'on efpere, &amp; une demonftration des chofes qu\'on ne voit point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 2, 'car par elle les anciens ont obtenu témoignage.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 3, 'Par la foi nous entendons que les fiecles ont efté ordonnez par la Parole de Dieu, de forte que les chofes qui fe voyent, n\'ont point efté faites de chofes apparuffent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 4, 'Par la foi Abel a offert un plus excellent facrifice à Dieu que Caïn : par laquelle il a obtenu témoignage d\'eftre jufte, dautant que Dieu rendoit témoignage de fes dons : &amp; lui eftant mort, parle encore par elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 5, 'Par la foi Enoch a efté tranfporté, pour ne point voir la mort : &amp; ne fut point trouvé, parce que Dieu l\'avoit tranfporté : car devant qu\'il fuft tranfporté, il a obtenu témoignage d\'avoir efté agreable à Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 6, 'Or il eft impoffible de lui eftre agreable fans la foi : car il faut que celui qui vient à Dieu, croye que Dieu eft, &amp; qu\'il eft le remunerateur de ceux qui le requierent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 7, 'Par la foi Noé ayant efté divinement averti des chofes qui ne fe voyoyent point encore, craignit, &amp; baftit l\'Arche pour la confervation de fa famille : par laquelle Arche il condamna le monde, &amp; fut fait heritier de la juftice qui eft felon la foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 8, 'Par la foi Abraham eftant appellé, obeït, pour venir au lieu qu\'il devoit recevoir en heritage, &amp; partit, ne fçachant où il alloit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 9, 'Par la foi il demeura comme eftranger en la terre promife, comme fi elle ne lui euft point appartenu, habitant en des tentes avec Ifaac &amp; Jacob, heritiers avec lui de la mefme promeffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 10, 'Car il attendoit la cité qui a des fondemens, &amp; de laquelle Dieu eft l\'architecte &amp; le baftiffeur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 11, 'Par la foi auffi Sara receut vertu à concevoir femence, &amp; enfanta hors d\'age, parce qu\'elle eftima que celui qui le lui avoit promis, eftoit fidele.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 12, 'Partant auffi d\'un feul (voire mefmes amorti,) font nés des gens en multitude comme les eftoiles du ciel, &amp; comme le fablon qui eft au rivage de la mer, lequel ne fe peut nombrer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 13, 'En la foi tous ceux-ci font morts, n\'ayant point receu les promeffes, mais les ayant veuës de loin, creuës, &amp; falüées : ayant fait profeffion d\'eftre eftrangers &amp; pelerins fur la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 14, 'Car ceux qui tiennent ces difcours montrent qu\'ils cherchent leur païs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 15, 'Et certes s\'ils fe fuffent ramentus celui dont ils eftoyent fortis, ils avoyent du temps pour y retourner.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 16, 'Mais maintenant ils en defirent un meilleur, c\'eft à dire, le celefte. C\'eft pourquoi Dieu mefme ne prend point à honte d\'eftre appellé leur Dieu, car il leur avoit preparé une cité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 17, 'Par la foi Abraham offrit Ifaac quand il fut efprouvé, voire celui qui avoit receu les promeffes, offrit fon fils unique.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 18, '(Au regard duquel il avoit efté dit, En Ifaac te fera appellée femence.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 19, 'Ayant eftimé que Dieu le pouvoit mefmes reffufciter des morts : dont auffi il le recouvra par quelque reffemblance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 20, 'Par la foi Ifaac donna la benediction touchant les chofes à venir, à Jacob, &amp; à Efau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 21, 'Par la foi Jacob en mourant benit chacun des fils de Jofeph : &amp; adora fur le bout de fon bafton.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 22, 'Par la foi Jofeph en mourant fit mention de la fortie des enfans d\'Ifraël, &amp; donna charge touchant fes os.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 23, 'Par la foi Moyfe eftant né, fut caché trois mois par fes pere &amp; mere, parce qu\'ils le voyoyent beau petit enfant, ne craignirent point l\'edit du Roi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 24, 'Par la foi Moyfe eftant déja grand, refufa d\'eftre nommé fils de la fille de Pharao.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 25, 'Choififfant pluftoft d\'eftre affligé avec le peuple de Dieu, que de joüir pour un peu de temps des delices du peché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 26, 'Ayant eftimé que l\'opprobre de Chrift eftoit de plus grandes richeffes, que les trefors d\'Egypte : car il regardoit à la remuneration.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 27, 'Par la foi il quitta l\'Egypte, n\'ayant point craint la fureur du Roi : car il tint ferme, comme voyant celui qui eft invifible.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 28, 'Par la foi il celebra la Pafque &amp; l\'effufion du fang, afin que celui qui deftruifoit les premiers nez, ne les touchaft point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 29, 'Par la foi ils traverferent la mer rouge, comme par le fec : ce que les Egyptiens voulant experimenter, ils furent engloutis.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 30, 'Par la foi les murs de Jerico tomberent, apres qu\'ils eurent efté circuïs par fept jours.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 31, 'Par la foi Rahab la paillarde ne perit point avec les incredules, ayant recueilli les efpies en paix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 32, 'Et que dirai-je plus ? car le temps me defaudra, fi je veux raconter de Gedeon, &amp; de Barac, &amp; de Samfon, &amp; de Jephté, &amp; de David, &amp; de Samuël, &amp; des Prophetes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 33, 'Lefquels par la foi ont combattu les royaumes, ont exercé juftice, ont obtenu les promeffes, ont fermé les gueules des lions :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 34, 'Ont efteint la force du feu, font efchappez des trenchans des efpées, de malades font devenus vigoureux, fe font montrez forts en bataille, ont tourné en fuite les armées des eftrangers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 35, 'Les femmes ont par refurrection receu leurs morts : mais d\'autres ont efté eftendus au tourment, ne tenant conte d\'eftre delivrez, afin d\'obtenir une meilleure refurrection.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 36, 'Et les autres ont efté efprouvez par des mocqueries &amp; par des battures : davantage auffi par liens &amp; par prifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 37, 'Ils ont efté lapidez, ils ont efté fciez, ils ont efté tentez, ils ont efté mis à mort par occifion d\'efpée, ils ont cheminé çà &amp; là veftus de peaux de brebis &amp; de chevres, deftituez, affligez, tourmentez :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 38, 'Defquels le monde n\'eftoit pas digne : errans és deferts, és montagnes, és cavernes, &amp; és trous de la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 39, 'Et tous ceux-là ayant obtenu témoignage par la foi, n\'ont point receu la promeffe :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 40, 'Dieu ayant pourveu quelque chofes de meilleur pour nous, afin qu\'ils ne vinffent point à la perfection fans nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 1, 'NOUS donc auffi, veu que nous fommes environnez d\'une fi groffe nuée de tefmoins, rejettant tout fardeau, &amp; le peché qui nous enveloppe fi aifément, pourfuivons conftamment la courfe qui nous eft propofée :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 2, 'Regardant à Jefus le chef &amp; le confommateur de la foi : lequel, pour la joye qui lui eftoit propofée, a fouffert la croix, ayant mefprifé la honte, &amp; s\'eft affis à la dextre du trône de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 3, 'C\'eft pourquoi confiderez diligemment celui qui a fouffert une telle contraction des pecheurs contre foi : afin que vous ne deveniez point lafches en defaillant en vos courages.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 4, 'Vous n\'avez point encore refifté jufques au fang, en combattant contre le peché :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 5, 'Et vous avez oublié l\'exhortation laquelle parle à vous comme aux enfans, difant, Mon enfant, ne neglige point le chaftiment du Seigneur, &amp; ne perds point courage quand tu es repris de lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 6, 'Car le Seigneur chaftie celui qu\'il aime, &amp; foüette tout enfant qu\'il avoüe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 7, 'Si vous endurez le chaftiment, Dieu fe prefente à vous comme à fes enfans : car qui eft l\'enfant que le pere ne chaftie point ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 8, 'Mais fi vous eftes fans chaftiment, dont tous font participans, vous eftes donc enfans fuppofez, &amp; non point legitimes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 9, 'Et puis que nous avons bien eu pour chaftieurs les peres de noftre chair, &amp; les avons eus en reverence : ne ferons nous point donc beaucoup plus fujets au Pere des efprits, &amp; vivrons ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 10, 'Car quant à ceux-là, ils nous chaftioyent pour un peu de temps, comme bon leur fembloit : mais celui-ci nous chaftie pour noftre profit, afin que nous foyons participans de fa fainteté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 11, 'Or tout chaftiment fur l\'heure ne femble point eftre de joye, mais de trifteffe : mais puis apres il rend un fruit paifible de juftice à ceux qui font exercez par icelui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 12, 'Relevez donc vos mains qui font lafches, &amp; vos genoux qui font déjoints.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 13, 'Et faites les fentiers droits à vos pieds, afin que ce qui cloche ne fe devoye point, mais que pluftoft il foit remis en fon entier.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 14, 'Pourchaffez la paix avec tous, &amp; la fanctification fans laquelle nul ne verra le Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 15, 'Prenant garde que nul ne foit defaillant de la grace de Dieu : que quelque racine d\'amertume bourgeonnant en haut ne vous deftourne, &amp; que plufieurs ne foyent foüillez par elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 16, 'Que nul ne foit paillard ou profane comme Efaü, qui pour une viande vendit font droit d\'aineffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 17, 'Car vous fçavez que mefmes puis apres defirant d\'heriter la benediction, il fut rejetté : car il ne trouva point lieu de repentance, encore qu\'il l\'euft demandée avec larmes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 18, 'Car vous n\'eftes point venus à une montagne qui fe puiffe toucher à la main, ni au feu bruflant, ni au tourbillon, ni à l\'obfcurité, ni à la tempefte,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 19, 'Ni au retentiffement de la trompette, ni à la voix des paroles : laquelle ceux qui l\'oyoyent requirent que la parole ne leur fuft plus adreffée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 20, 'Car ils ne pouvoyent porter ce qui eftoit enjoint, affavoir, Si mefmes une befte touche la montagne, elle fera lapidée, ou percée d\'un dard.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 21, 'Et Moyfe (tant eftoit terrible ce qui apparoiffoit) dit, Je fuis efpouvanté, &amp; en tremble tout.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 22, 'Mais vous eftes venus à la montagne de Sion, &amp; la cité du Dieu vivant, à la Jerusalem celefte, &amp; aux miliers d\'Anges,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 23, 'Et à l\'affemblée &amp; Eglife des premier-nez qui font efcrits és cieux, &amp; à Dieu qui eft le Juge de tous, &amp; aux efprits des juftes fanctifiez :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 24, 'Et à Jefus le Mediateur de la nouvelle alliance, &amp; au fang de l\'afperfion, prononçant de meilleures chofes que celui d\'Abel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 25, 'Voyez que vous ne mefprifiez point celui qui parle : car fi ceux-là qui mefprifoyent celui qui parloit fur la terre, ne font point efchappez, nous ferons punis beaucoup plus, fi nous nous deftournons de celui qui parle des cieux ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 26, 'Duquel la voix efmut alors la terre : mais maintenant il a denoncé, difant, Encore une fois, j\'efmouvrai non feulement la terre, mais auffi le ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 27, 'Or ce mot, Encore une fois, fignifie l\'abolition des chofes muables, comme de celles qui ont efté faites de main, afin que celles qui font immuables, demeurent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 28, 'C\'eft pourquoi apprehendant le royaume qui ne peut eftre esbranlé, retenons la grace par laquelle nous fervions à Dieu, tellement que nous lui foyons agreables avec reverence &amp; crainte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 29, 'Car auffi noftre Dieu eft un feu confumant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 1, 'QUE l\'amour fraternelle demeure.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 2, 'N\'oubliez point l\'hofpitalité : car par elle quelques-uns ont logé des Anges, n\'en fçachant rien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 3, 'Ayez fouvenance des prifonniers, comme fi vous eftiez emprifonnez avec eux : &amp; de ceux qui font tourmentez, comme vous-mefmes auffi eftant du mefme corps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 4, 'Le mariage eft honorable entre tous, &amp; la couche fans macule. Mais Dieu jugera les paillards &amp; les adulteres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 5, 'Que vos mœurs foyent fans avarice, eftant contents de ce que vous avez prefentement : car lui-mefme a dit, Je ne te delaifferai point, &amp; je ne t\'abandonnerai point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 6, 'Tellement que nous pouvons dire en affeurance, Le Seigneur m\'eft en aide ; dont je ne craindrai point ce que l\'homme me pourroit faire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 7, 'Ayez fouvenance de vos conducteurs, qui vous ont porté la Parole de Dieu : defquels enfuivez la foi, confiderant quelle a efté l\'iffuë de leur converfation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 8, 'Jefus Chrift a efté le mefme hier &amp; aujourd\'hui, &amp; l\'eft auffi eternellement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 9, 'Ne foyez point emportez çà &amp; là par des doctrines diverfes &amp; eftranges : car il eft bon que le cœur foit affermi par grace, non point par viandes, lefquelles n\'ont de rien profité à ceux qui s\'y font occupez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 10, 'Nous avons un Autel duquel ceux qui fervent au Tabernacle, n\'ont point la puiffance de manger.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 11, 'Car les corps des beftes defquelles le fang eft apporté pour le peché par le fouverain Sacrificateur dans le Sanctuaire font bruflez hors du camp.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 12, 'Partant auffi Jefus, afin qu\'il fanctifiaft le peuple par fon propre fang, a fouffert hors de la porte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 13, 'Sortons donc vers lui hors du camp, portans fon opprobre :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 14, 'Car nous n\'avons point ici de cité permanente : mais nous recherchons celle qui eft à venir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 15, 'Offrons donc par lui facrifice de loüange à toûjours à Dieu, c\'eft à dire, le fruit des levres, confeffans fon Nom.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 16, 'Or ne mettez point en oubli la beneficence &amp; la communication : car Dieu prend plaifir à de tels facrifices.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 17, 'Obeïffez à vos conducteurs, &amp; vous y foumettez : car ils veillent pour vos ames, comme ceux qui en doivent rendre conte : afin que ce qu\'ils en font, ils le faffent joyeufement, &amp; non point à regret : car cela ne vous viendroit point à profit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 18, 'Priez pour nous : car nous nous affeurons que nous avons une bonne confcience, difirans de converfer honneftement entre tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 19, 'Et je vous prie de le faire d\'autant plus grandement, afin que je vous fois tant pluftoft rendu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 20, 'Or le Dieu de paix, qui a ramené des morts le grand Pafteur des brebis, par le fang de l\'Alliance eternelle, affavoir noftre Seigneur Jefus Chrift,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 21, 'Vous rende accomplis en toute bonne œuvre, pour faire fa volonté, faifant en vous ce qui eft agreable davant lui, par Jefus Chrift, auquel foit gloire aux fiecles des fiecles. Amen.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 22, 'Auffi, freres, je vous prie de fupporter la parole d\'admonition : car je vous ai efcrit en peu de paroles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 23, 'Vous fçavez que le frere Timothée eft delivré, avec lequel je vous verrai, s\'il vient bien-toft.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 24, 'Salüez tous vos conducteurs, &amp; tous les Saints. Ceux qui font d\'Italie vous falüent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 25, 'Grace foit avec vous tous : Amen. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 1, 'JAQUES ferviteur de Dieu, &amp; du Seigneur Jefus Chrift, aux douze lignées qui font efparfes, Salut.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 2, 'Mes freres, tenez pour une parfaite joye, quand vous cherrez en diverfes tentations :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 3, 'Sçachant que l\'efpreuve de voftre foi engendre la patience.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 4, 'Or il faut que la patience ait une œuvre parfaite, afin que vous foyez parfaits &amp; entiers : de forte que rien ne vous defaille.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 5, 'Que fi quelqu\'un d\'entre vous a faute de fapience, qu\'il la demande à Dieu, qui la donne à tous benignement, &amp; ne la reproche point, &amp; elle lui fera donnée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 6, 'Mais qu\'il la demande en foi ne doutant nullement : car celui qui doute eft femblable au flot de la mer, agité du vent &amp; demené.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 7, 'Or que cet homme-là ne s\'attende point de recevoir chofe aucune du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 8, 'L\'homme double de cœur eft inconftant en toutes fes voyes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 9, 'Or que le frere qui eft de baffe condition fe glorifie en fa hauteffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 10, 'Que le riche au contraire fe glorifie en fa baffe condition : car il paffera comme la fleur de l\'herbe ;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 11, 'Car comme le foleil ardent n\'eft pas pluftoft levé, que l\'herbe eft bruflée &amp; fa fleur eft cheute, &amp; fa belle apparence eft perie ; tout ainfi auffi fe fleftrira le riche avec fes entreprifes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 12, 'Bien-heureux eft l\'homme qui endure tentation : car quand il aura efté rendu efprouvé, il recevra la couronne de vie, que Dieu a promife à ceux qui l\'aiment.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 13, 'Quand quelqu\'un eft tenté, qu\'il ne die point, Je fuis tenté de Dieu : car Dieu ne peut eftre tenté de maux, &amp; auffi ne tente-t\'il perfonne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 14, 'Mais chacun eft tenté, quand il eft attiré &amp; amorcé par fa propre convoitife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 15, 'Puis apres quand la convoitife à conceu, elle enfante le peché, &amp; le peché eftant amené à fa fin, engendre la mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 16, 'Mes freres bien-aimez, ne vous abufez point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 17, 'Toute bonne donation, &amp; tout don parfait eft d\'en haut, defcendant du Pere des lumieres, par devers lequel il n\'y a point de variation, ni d\'ombrage de changement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 18, 'Il nous a de fon propre vouloir engendrez par la parole de verité, afin que nous fuffions comme les premices de fes creatures.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 19, 'Partant, mes freres bien-aimez, que tout homme foit prompt à oüir, tardif à parler, &amp; tardif à colere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 20, 'Car la colere de l\'homme n\'accomplit point la juftice de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 21, 'C\'eft pourquoi rejettans toute ordure &amp; fuperfluïté de malice, recevez en douceur la parole plantée en vous, laquelle peut fauver vos ames :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 22, 'Et mettez en effet la parole, &amp; ne l\'efcoutez point feulement, en vous decevant vous-mefmes par de vains difcours.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 23, 'Car fi quelqu\'un efcoute la parole, &amp; ne la met point en effet, il eft femblable à l\'homme qui confidere en un miroir fa face naturelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 24, 'Car s\'eftant confideré foi-mefme, &amp; s\'en eftant allé, il a auffi-toft oublié quel il eftoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 25, 'Mais celui qui aura regardé au dedans de la Loi parfaite, qui eft de liberté, &amp; aura perfeveré, n\'eftant point efcouteur oublieux, mais mettant en effet l\'œuvre, celui-là fera bienheureux en ce qu\'il aura fait.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 26, 'Si quelqu\'un penfe eftre religieux entre vous, ne tenant point en bride fa langue, mais feduifant fon cœur, la religion d\'un tel perfonnage eft vaine.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 27, 'La religion pure &amp; fans macule envers noftre Dieu &amp; Pere, c\'eft de vifiter les orphelins &amp; les veuves en leurs tribulations, &amp; fe conferver fans eftre entaché de ce monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 1, 'MES freres, n\'ayez point la foi de noftre Seigneur Jefus Chrift glorieux, en ayant égard à l\'apparence des perfonnes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 2, 'Car s\'il entre en voftre affemblée un homme qui porte un anneau d\'or, veftu de quelque precieux veftement, &amp; qu\'il y entre auffi quelque pauvre, veftu de quelque méchant habit :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 3, 'Et que vous ayez égard à celui qui porte le precieux veftement, &amp; lui difiez, Toi, affieds-toi ici honorablement, &amp; que vous difiez au pauvre, Toi, tien toi là debout, ou affieds-toi fous mon marche-pied :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 4, 'N\'avez-vous pas fait difference en vous-mefmes, &amp; n\'eftes-vous pas devenus juges de mauvaifes penfées ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 5, 'Efcoutez, mes freres bien-aimez, Dieu n\'a-t\'il point choifi les pauvres de ce monde, pour eftre riches en la foi, &amp; heritiers du Royaume qu\'il a promis à ceux qui l\'aiment ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 6, 'Mais vous avez deshonoré le pauvre. Or les riches ne vous maiftrifent-ils pas, &amp; ne vous tirent-ils pas aux plaidoyers ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 7, 'Eux-mefmes ne blafphement-ils pas le bon Nom qui a efté invoqué fur vous ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 8, 'Toutefois fi vous accompliffez la Loi royale felon l\'Ecriture, affavoir, Tu aimeras ton prochain comme toi-mefme, vous faites bien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 9, 'Mais fi vous avez égard à l\'apparence des perfonnes, vous commettez un peché &amp; eftes redargüez par la Loi comme tranfgreffeurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 10, 'Car quiconque aura gardé toute la Loi, s\'il vient à faillir en un feul point, il eft coupable de tous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 11, 'Car celui qui a dit, Tu ne commettras point adultere, a dit auffi, Tu ne tuëras point. Si donc tu ne commets point adultere, mais tu tuës, tu es tranfgreffeur de la Loi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 12, 'Parlez &amp; faites comme ceux qui doivent eftre jugez par la Loi de liberté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 13, 'Car condamnation fans mifericorde fera fur celui qui n\'aura point ufé de mifericorde : &amp; la mifericorde fe glorifie contre la condamnation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 14, 'Mes freres, que profitera-t\'il fi quelqu\'un dit qu\'il a la foi, &amp; qu\'il n\'ait point les œuvres : la foi le pourra-t\'elle fauver ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 15, 'Et fi le frere ou la fœur font nuds, &amp; ont faute du vivre quotidien :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 16, 'Et que quelqu\'un d\'entre vous leur die, Allez en paix, chauffez vous, &amp; vous raffafiez, &amp; vous ne leur donnez point les chofes neceffaires pour le corps, que leur profitera-t\'il ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 17, 'Pareillement auffi la foi, fi elle n\'a point les œuvres, elle eft morte en elle mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 18, 'Mefme quelqu\'un dira, Tu as la foi, &amp; mon j\'ai les œuvres : montre moi donc ta foi fans les œuvres, &amp; je te montrerai ma foi par mes œuvres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 19, 'Tu crois qu\'il y a un feul Dieu, tu fais bien : les diables le croyent auffi, &amp; en tremblent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 20, 'Mais, ô homme vain ! Veux-tu fçavoir comme la foi qui eft fans les œuvres eft morte ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 21, 'Abraham noftre pere n\'a-t\'il point efté juftifié par les œuvres, quand il offrit fon fils Ifaac fur l\'autel ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 22, 'Ne vois-tu pas que la foi operoit avec fes œuvres, &amp; que par les œuvres la foi a efté renduë accomplie ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 23, 'Et que l\'Efcriture a efté accomplie, difant, Abraham a creu à Dieu, &amp; lui a efté alloüé à juftice, &amp; il a efté appellé ami de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 24, 'Ne voyez vous pas donc que l\'homme eft juftifié par les œuvres, &amp; non feulement par la foi ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 25, 'Semblablement auffi Rahab la paillarde, n\'a-t\'elle point efté juftifiée par les œuvres, quand elle eut recueilli les meffagers, &amp; les eut mis dehors par un autre chemin ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 26, 'Car ainfi que le corps fans l\'efprit eft mort, auffi la foi qui eft fans les œuvres eft morte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 1, 'MES freres, ne foyez point plufieurs maiftres : fçachant que nous en recevrons une plus grande condamnation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 2, 'Car nous choppons tous en plufieurs chofes : Si quelqu\'un ne choppe point en parole, il eft homme parfait &amp; peut mefmes tenir en bride tout le corps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 3, 'Voila, nous mettons aux chevaux des mords en leurs bouches, afin qu\'ils nous obeïffent, &amp; menons çà &amp; là tout le corps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 4, 'Voila auffi les navires, encore qu\'ils foyent fi grands, &amp; qu\'ils foyent agitez de rudes vents, fi eft-ce qu\'ils font menez par tout çà &amp; là avec un petit gouvernail, felon que porte la volonté de celui qui gouverne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 5, 'Ainfi la langue eft un petit membre, &amp; elle fe vante de grandes chofes : voila un petit feu, combien allume-t\'il de grand bois ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 6, 'La langue auffi eft un feu, voire un monde d\'iniquité : ainfi la langue eft pofée entre nos membres, laquelle foüille tout le corps, &amp; enflamme tout le monde qui a efté creé, &amp; eft enflammée de la gehenne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 7, 'Car toute nature de beftes, &amp; d\'oifeaux, &amp; de reptiles, &amp; de poiffons de mer, fe dompte, &amp; a efté domptée par la nature humaine.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 8, 'Mais nul homme ne peut dompter la langue, entant que c\'eft un mal qui ne fe peut reprimer, &amp; elle eft pleine de venin mortel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 9, 'Par elle nous beniffons noftre Dieu &amp; Pere : &amp; par elle nous maudiffons les hommes faits à la femblance de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 10, 'D\'une mefme bouche procede la benediction &amp; la malediction. Mes freres, il ne faut point que ces chofes aillent ainfi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 11, 'Une fontaine jette-t\'elle d\'un mefme trou le doux &amp; l\'amer ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 12, 'Mes freres, un figuier peut-il produire des olives, ou une vigne des figues ? Ainfi nulle fontaine ne peut jetter de l\'eau falée &amp; de l\'eau douce.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 13, 'Qui eft-ce qui eft fage &amp; entendu parmi vous ? Qu\'il montre par une bonne converfation fes œuvres en douceur de fapience.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 14, 'Mais fi vous avez de l\'envie amere, &amp; de l\'irritation en vos cœurs, ne vous glorifiez point, &amp; ne mentez point contre la verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 15, 'Car ce n\'eft point là la fapience qui defcend d\'en-haut : mais elle eft terriene, fenfuelle, &amp; diabolique.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 16, 'Car où il y a de l\'envie &amp; de l\'irritation, là eft le trouble &amp; toute œuvre méchante.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 17, 'Mais la fapience qui eft d\'en-haut, premierement eft pure, puis paifible, moderée, traitable, pleine de mifericorde, &amp; de bons fruits, fans faire beaucoup de difficultez, &amp; fans hypocrifie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 18, 'Or le fruit de juftice fe feme en paix pour ceux qui s\'adonnent à la paix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 1, 'D\'OÙ viennent les combats &amp; les quereles entre vous ? N\'eft-ce point d\'ici, affavoir de vos voluptez, lefquelles guerroyent en vos membres ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 2, 'Vous convoitez &amp; ne l\'avez point : vous eftes envieux &amp; jaloux, &amp; ne pouvez obtenir : vous querelez &amp; combattez, &amp; n\'avez point ce que vous defirez, parce que vous ne le demandez point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 3, 'Vous demandez &amp; ne recevez point : parce que vous demandez mal, afin que vous le dépenfiez en vos voluptez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 4, 'Adulteres &amp; adultereffes, ne fçavez-vous pas que l\'amitié du monde eft inimitié contre Dieu ? Qui voudra donc eftre ami du monde, il fe rend ennemi de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 5, 'Penfez-vous que l\'Efcriture die en vain, L\'Efprit qui a habité en nous, convoite à envie ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 6, 'Mais il donne une plus grande grace. C\'eft pourquoi il dit, Dieu refifte aux orgueilleux, mais il fait grace aux humbles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 7, 'Affujettiffez-vous donc à Dieu : refiftez au diable, &amp; il s\'enfuïra de vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 8, 'Approchez-vous de Dieu, &amp; il s\'approchera de vous : pecheurs, nettoyez vos mains : &amp; vous, doubles de cœur, purifiez vos cœurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 9, 'Sentez vos miferes, &amp; lamentez, &amp; pleurez : que voftre ris foit converti en pleur, &amp; voftre joye en trifteffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 10, 'Humiliez-vous en la prefence du Seigneur, &amp; il vous élevera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 11, 'Freres, ne detractez point les uns des autres : celui qui detracte de fon frere, &amp; qui juge fon frere, il detracte de la Loi, &amp; juge la Loi : or fi tu juge la Loi, tu n\'es point faifeur de la Loi, mais juge.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 12, 'Il y a un feul Legiflateur, qui peut fauver &amp; deftruire : toi qui es-tu, qui juges autrui ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 13, 'Or çà maintenant, vous qui dites, allons aujourd\'hui &amp; demain en une telle ville, &amp; demeurons là un an, &amp; y trafiquons &amp; gagnons.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 14, '(Qui toutefois ne fçavez pas ce qui aviendra le lendemain : Car qu\'eft-ce de voftre vie ? ce n\'eft certes qu\'une vapeur qui apparoift pour un peu, puis s\'évanoüit.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 15, 'Au lieu que vous deviez dire, Si le Seigneur le veut, a fi nous vivons, nous ferons ceci ou cela.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 16, 'Mais maintenant vous vous vantez en vos fiertez : toute telle vanterie eft mauvaife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 17, 'Il y a donc peché à celui qui fçais faire le bien &amp; ne le fait point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 1, 'OR fus maintenant, vous riches, pleurez, heurlans pour vos miferes, lefquelles s\'en vont tomber fur vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 2, 'Vos richeffes font pourries : vos veftemens font devenus tout rongez de tignes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 3, 'Voftre or &amp; voftre argent eft enroüillé, &amp; leur roüille vous fera en tefmoignage, &amp; mangera voftre chair comme le feu. Vous avez amaffé un trefor pour les derniers jours.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 4, 'Voici le falaire des ouvriers qui ont moiffonné vos champs (duquel ils ont efté fruftrez par vous) crie : &amp; les cris de ceux qui ont moiffonné eft entré aux oreilles du Seigneur des armées.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 5, 'Vous avez vefcu en delices fur la terre, &amp; vous vous eftes débordez &amp; avez raffafié vos cœurs comme au jour des facrifices.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 6, 'Vous avez condamné, &amp; avez mis à mort le jufte, lequel ne vous refifte point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 7, 'Or donc, freres, attendez patiemment jufques à la venuë du Seigneur : voici, le laboureur attend le fruit precieux de la terre, ufant de patience, jufques à ce qu\'il reçoive la pluye de la premiere &amp; de la derniere faifon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 8, 'Vous donc auffi attendez patiemment, &amp; affermiffez vos cœurs : car la venuë du Seigneur eft prochaine.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 9, 'Freres, ne vous plaignez point les uns des autres, afin que vous ne foyez point condamnez : voici, le juge fe tient à la porte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 10, 'Mes freres, prenez pour un exemple d\'affliction &amp; de patience, les Prophetes qui ont parlé au Nom du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 11, 'Voici, nous tenons pour bienheureux ceux qui ont enduré : vous avez entendu la patience de Job : &amp; avez veu la fin du Seigneur : car le Seigneur eft plein de compaffion &amp; pitoyable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 12, 'Or fur toutes chofes, mes freres, ne jurez point par le ciel, ni par la terre, ni par quelque autre ferment : mais voftre Oüi foit Oüi, &amp; voftre Non, Non : afin que vous ne tombiez point en condamnation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 13, 'Y a-t\'il quelqu\'un qui fouffre entre vous ? Qu\'il prie. Y a-t\'il quelqu\'un d\'entre vous qui foit en repos d\'efprit ? Qu\'il pfalmodie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 14, 'Y a-t-il quelqu\'un d\'entre vous malade ? Qu\'il appelle les Anciens de l\'Eglife, &amp; qu\'ils prient pour lui, &amp; qu\'ils l\'oignent d\'huile au Nom du Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 15, 'Et la priere de foi fauvera le malade, &amp; le Seigneur le relevera : &amp; s\'il a commis des pechez, ils lui feront pardonnez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 16, 'Confeffez vos fautes l\'un envers l\'autre, &amp; priez l\'un pour l\'autre, afin que vous en foyez gueris : car la priere du jufte faite avec vehemence eft de grande efficace.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 17, 'Elie eftoit un homme fujet à de femblables affections que nous, &amp; neantmoins il requit en priant qu\'il ne plût point, &amp; il ne plut point fur la terre par trois ans &amp; fix mois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 18, 'Et derechef il requit en priant, &amp; le ciel donna d la pluye, &amp; la terre produifit fon fruict.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 19, 'Freres, fi quelqu\'un d\'entre vous fe fourvoye de la verité, &amp; que quelqu\'un le redreffe,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 20, 'Il doit fçavoir que celui qui aura radreffé un pecheur, de ce qu\'il s\'eftoit fourvoyé, fauvera une ame de mort, &amp; couvrira une multitude de pechez. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 1, 'PIERRE Apoftre de Jefus Chrift, aux eftrangers qui eftes efpars en Ponte, en Galatie, en Cappadoce, en Afie, &amp; en Bithynie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 2, 'Efleus felon la providence de Dieu le Pere, en fanctification d\'Efprit, à l\'obeïffance, &amp; à l\'afperfion du fang de Jefus Chrift : Grace &amp; paix vous foit multipliée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 3, 'Benit foit Dieu qui eft le Pere de noftre Seigneur Jefus Chrift, qui par fa grande mifericorde nous a regenerez en efperance vive, par la refurrection de Jefus Chrift d\'entre les morts :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 4, 'Pour obtenir l\'heritage incorruptible, qui ne fe peut foüiller ni fleftrir, confervé dans les cieux pour nous :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 5, 'Qui fommes gardez en la vertu de Dieu par la foi, pour avoir le falut preft d\'eftre revelé au dernier temps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 6, 'En quoi vous vous éjoüiffez, eftant maintenant un peu de temps contriftez en diverfes tentations, s\'il eft convenable :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 7, 'Afin que l\'efpreuve de voftre foi, qui eft beaucoup plus precieufe que l\'or, (qui perit, &amp; toutefois eft efprouvé par le feu,) vous tourne à loüange, &amp; à honneur, &amp; à gloire, quand Jefus Chrift fera revelé :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 8, 'Lequel, quoi que vous ne l\'ayez point veu, vous aimez : auquel, quoi que maintenant vous ne le voyïez point, vous croyez, &amp; vous éjoüiffez d\'une joye inenarable &amp; glorieufe :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 9, 'Remportant la fin de voftre foi, affavoir le falut des ames.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 10, 'Duquel falut les Prophetes (qui ont prophetizé de la grace qui eftoit refervée pour vous) fe font enquis, &amp; l\'on diligemment recherché ;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 11, 'Recherchant diligemment quand &amp; en quel temps l\'Efprit de Chrift, qui eftoit en eux, rendant témoignage auparavant, declaroit les fouffrances qui devoyent avenir à Chrift, &amp; les gloires qui s\'en devoyent fuivre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 12, 'Aufquels il a efté revelé que ce n\'eftoit point pour eux-mefmes, mais pour nous qu\'ils adminiftroyent ces chofes, lefquelles maintenant vous ont efté annoncées par ceux qui vous ont prefché l\'Evangile par le Saint Efprit envoyé du ciel : efquelles chofes les Anges defirent de regarder jufques au fond.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 13, 'Vous donc, en ayant les reins de voftre entendement ceints avec fobrieté, efperez parfaitement en la grace qui vous eft prefentée, jufques à ce que Jefus Chrift foit revelé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 14, 'Comme enfans obeïffans, ne vous conformez point à vos convoitifes de par ci-devant en voftre ignorance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 15, 'Mais comme celui qui vous a appellez eft Saint, vous auffi pareillement foyez faints en toute voftre converfation :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 16, 'Dautant qu\'il eft efcrit, Soyez faints, car je fuis Saint.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 17, 'Et fi vous invoquez pour Pere celui qui fans avoir efgard à l\'apparence des perfonnes, juge felon l\'œuvre de chacun, converfez en crainte durant le temps de voftre fejour temporel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 18, 'Sçachant que vous avez efté rachetez de voftre vaine converfation, qui vous avoit efté enfeignée par vos peres, non point par des chofes corruptibles, comme par argent, ou par or :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 19, 'Mais par le precieux fang de Chrift comme de l\'agneau fans macule &amp; fans tache ;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 20, 'Déja ordonné devant la fondation du monde, mais manifefté és derniers temps pour vous,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 21, 'Qui par lui croyez en Dieu qui l\'a reffufcité des morts, &amp; lui a donné gloire, afin que voftre foi &amp; voftre efperance fuft en Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 22, 'Ayant donc purifié vos ames, en obeïffance à la verité par l\'Efprit, pour vous adonner à la charité fraternelle, fans feintife, aimez-vous l\'un l\'autre affectueufement d\'un cœur pur :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 23, 'Eftant regenerez, non point par une femence corruptible, mais incorruptible, affavoir par la Parole de Dieu vivante &amp; qui demeure à toûjours.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 24, 'Parce que toute chair eft comme l\'herbe, &amp; toute la gloire de l\'homme comme la fleur de l\'herbe : l\'herbe eft fechée, &amp; fa fleur eft cheüe :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 25, 'Mais la Parole du Seigneur demeure eternellement : &amp; cette Parole eft celle qui vous a efté evangelizée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 1, 'AYANT donc dépoüillé toute malice &amp; toute fraude, &amp; feintifes, &amp; envies, &amp; toutes detractions,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 2, 'Defirez affectueufement, comme enfans nagueres nez, le laict d\'intelligence, &amp; qui eft fans fraude, afin que vous croiffiez par icelui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 3, 'Voire pourveu que vous ayez goufté que le Seigneur eft benin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 4, 'Duquel vous approchant, voire de lui qui eft la pierre vive, qui a efté rejetée des hommes, mais qui eft éleuë &amp; precieufe envers Dieu :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 5, 'Vous auffi comme des pierres vives, eftes edifiez pour eftre une maifon fpirituelle, &amp; une fainte Sacrificature, pour offrir des facrifices fpirituels, agreables à Dieu par Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 6, 'C\'eft pourquoi auffi il eft contenu en l\'Efcriture, Voici, je mets en Sion la maiftreffe pierre du coin, éleuë &amp; precieufe : &amp; celui qui croira en elle, ne fera point confus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 7, 'Elle eft donc honneur à vous qui croyez : mais quant aux rebelles, la pierre que les edifians ont rejettée, a efté faite la maiftreffe pierre du coin, &amp; pierre d\'achoppement, &amp; pierre de trébuchement :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 8, 'Lefquels s\'heurtent contre la Parole, &amp; font rebelles : à quoi auffi ils ont efté ordonnez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 9, 'mais vous eftes la generation éleuë, la Sacrificature royale, la nation fainte, le peuple acquis, afin que vous annonciez les vertus de celui qui vous a appellez des tenebres à fa merveilleufe lumiere :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 10, 'Vous qui jadis n\'eftiez point peuple, mais qui maintenant eftes le peuple de Dieu : qui jadis n\'aviez point obtenu mifericorde, mais qui maintenant avez obtenu mifericorde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 11, 'Bien-aimez, je vous exhorte, que comme eftrangers &amp; voyageurs, vous vous abfteniez des convoitifes charnelles qui guerroyent contre l\'ame :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 12, 'Ayans voftre converfation honnefte envers les Gentils, afin qu\'en ce qu\'ils detractent de vous comme de malfaiteurs, ils glorifient Dieu au jour de la vifitation pour vos bonnes œuvres qu\'ils auront veuës.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 13, 'Rendez-vous donc fujets à tout ordre humain pour l\'amour de Dieu : foit au Roi, comme à celui qui eft par deffus les autres :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 14, 'Soit aux Gouverneurs, comme à ceux qui font envoyez de par lui, pour exercer vengeance fur les malfaiteurs, &amp; à la loüange de ceux qui font bien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 15, 'Car telle eft la volonté de Dieu, qu\'en faifant bien, vous fermiez la bouche à l\'ignorance des hommes fols :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 16, 'Comme libres, &amp; non point comme ayans la liberté pour couverture de malice, mais comme ferviteurs de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 17, 'Portez honneur à tous. aimez la fraternité. craignez Dieu. honorez le Roi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 18, 'Serviteurs, foyez fujets en toute crainte à vos maiftres, non feulement aux bons &amp; equitables, mais auffi aux facheux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 19, 'Car cela eft agreable, fi quelqu\'un à caufe de la confcience qu\'il a envers Dieu, endure de la fafcherie, fouffrant injuftement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 20, 'Autrement quel honneur vous eft-ce fi eftant fouffletez pour avoir mal fait, vous l\'endurez ? Mais fi en bien faifant, eftant toutefois affligez, vous endurez, voila où Dieu prend plaifir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 21, 'Car vous eftes auffi appellez à cela : veu auffi que Chrift a fouffert pour nous, nous laiffant un patron, afin que vous enfuiviez fes traces.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 22, 'Lequel n\'a point commis de peché, ni fraude aucune n\'a efté trouvée en fa bouche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 23, 'Lequel quand on lui difoit des outrages n\'en rendoit point : &amp; quand on lui faifoit du mal, n\'ufoit point de menaces : mais fe remettoit à celui qui juge juftement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 24, 'Lequel mefme a porté nos pechez en fon corps fur le bois : afin qu\'eftant morts à péché, nous vivions à juftice : par la bature duquel mefme vous avez efté gueris.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 25, 'Car vous eftiez comme des brebis errantes : mais maintenant vous eftes convertis au Pafteur &amp; Evefque de vos ames.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 1, 'SEMBLABLEMENT que les femmes fe rendent fujettes à leurs propres maris, afin mefmes s\'il y en a qui n\'obeïffent point à la parole, ils foyent gagnez fans parole par la converfation des femmes ;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 2, 'Ayans veu voftre chafte converfation, qui eft avec crainte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 3, 'Defquelles le parement ne foit point celui de dehors, qui confifte en entortillement de cheveux, ou en parure d\'or, ou en accouftrement d\'habits :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 4, 'Mais l\'homme qui eft caché, affavoir celui du cœur, qui confifte en l\'incorruption d\'un efprit doux &amp; paifible, qui eft de grand prix devant Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 5, 'Car ainfi auffi autrefois fe paroyent les faintes femmes, qui efperoyent en Dieu, eftant fujettes à leurs propres maris :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 6, 'Comme Sara obeïffoit à Abraham, l\'appellant fon Seigneur : de laquelle vous eftes filles en bien faifant, quand mefme vous ne craignez aucun efpouvantement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 7, 'Vous maris femblablement comportez vous difcretement avec elles, comme avec un vaiffeau plus fragile, affavoir feminin, leur portant du refpect, comme ceux qui eftes auffi enfemble heritiers de la grace de vie : afin que vos prieres ne foyent point interrompuës.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 8, 'Et finalement foyez tous d\'un confentement, &amp; adonnez à une mutuelle compaffion, vous entr\'aimant fraternellement, mifericordieux, gracieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 9, 'Ne rendant point mal pour mal, ni outrage pour outrage : mais au contraire beniffant : fçachant que vous eftes appellez à cela, afin que vous obteniez l\'heritage de benediction.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 10, 'Car que celui qui veut aimer fa vie, &amp; voir fes jours bien-heureux, garde fa langue de mal, &amp; fes levres qu\'elles ne prononcent point de fraude :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 11, 'Qu\'il fe deftourne du mal, &amp; qu\'il faffe le bien : qu\'il cherche la pourchaffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 12, 'Car les yeux du Seigneur font fur les juftes, &amp; fes oreilles font ouvertes à leurs prieres : mais la face du Seigneur eft fur ceux qui font les maux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 13, 'Et qui eft-ce qui vous fera du mal, fi vous enfuivez le bien ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 14, 'Mefmes encore que vous enduriez quelque chofe pour juftice, vous eftes bien-heureux : mais ne craignez point pour la crainte d\'eux, &amp; n\'en foyez point troublez :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 15, 'Mais fanctifiez le Seigneur en vos cœurs, &amp; foyez toûjours prefts à refpondre avec douceur &amp; reverence, à chacun qui vous demande raifon de l\'efperance qui eft en vous :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 16, 'Ayans une bonne confcience, afin que ceux qui blafment voftre bonne converfation en Chrift, foyent confus, en ce qu\'ils detractent de vous comme de mal-faiteurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 17, 'Car il vaut mieux que vous fouffriez en bien-faifant, fi telle eft la volonté de Dieu, qu\'en mal-faifant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 18, 'Car auffi Chrift a fouffert une fois pour les pechez, lui jufte pour les injuftes : afin qu\'il nous amenaft à Dieu, ayant efté mortifié en chair, mais vivifié par l\'Efprit :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 19, 'Par lequel auffi eftant allé il a prefché aux efprits en chartre :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 20, 'Ayant efté jadis defobeïffans, quand la patience de Dieu attendoit une fois és jours de Noé, lors que l\'Arche s\'appreftoit, en laquelle un petit nombre, affavoir huit perfonnes furent fauvées par l\'eau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 21, 'A quoi auffi maintenant refpond, à l\'oppofite, la figure qui nous fauve, affavoir le Baptefme : non point celui par lequel les ordures de la chair font nettoyées, mais l\'atteftation d\'une bonne confcience devant Dieu, par la refurrection de Jefus Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 22, 'Qui eft à la dextre de Dieu, eftant allé au ciel : auquel font affujettis les Anges, &amp; les Puiffances, &amp; les Vertus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 1, 'Or donc, puis que Chrift a fouffert pour nous en la chair ; vous auffi foyez armez de cette mefme penfée, c\'eft que celui qui a fouffert en la chair, a defifté de peché :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 2, 'Afin que le temps qui refte en la chair, vous ne viviez plus felon les convoitifes des hommes, mais felon la volonté de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 3, 'Car le temps paffé nous doit avoir fuffit pour avoir accompli la volonté des Gentils, quand nous converfions en infolences, convoitifes, yvrogneries, gourmandifes, beuveries, &amp; idolatries abominables.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 4, 'En quoi ils fe trouvent eftranges, en vous blafmant, quand vous ne courez point avec eux en un mefme abandon de diffolution.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 5, 'Ceux qui rendront conte à celui qui eft preft de juger les vivans &amp; les morts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 6, 'Car c\'eft pour cela auffi qu\'il a efté evangelizé aux morts, afin qu\'ils fuffent jugez felon les hommes en la chair, &amp; qu\'ils vécuffent felon Dieu en efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 7, 'Or la fin de toutes chofes eft prochaine. Soyez donc fobres &amp; veillans à prier.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 8, 'Et fur tout ayez entre vous une vehemente charité : car la charité couvrira une multitude de pechez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 9, 'Soyez hofpitaliers les uns envers les autres, fans murmures.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 10, 'Que chacun, felon qu\'il a receu le don, l\'adminiftre envers les autres, comme bons difpenfateurs de la diverfe grace de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 11, 'Si quelqu\'un parle, qu\'il parle comme les paroles de Dieu : fi quelqu\'un adminiftre, qu\'il adminiftre comme par la puiffance que Dieu fournit : afin qu\'en toutes chofes Dieu foit glorifié par Jefus Chrift, auquel appartient gloire &amp; force aux fiecles des fiecles. Amen.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 12, 'Bien-aimez, ne trouvez point eftrange quand vous eftes comme en la fournaife pour voftre efpreuve, comme fi quelque chofe d\'eftrange vous avenoit :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 13, 'Mais entant que vous communiquez aux fouffrances de Chrift, éjoüiffez-vous : afin qu\'auffi à la revelation de fa gloire vous vous éjoüiffiez en vous efgayant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 14, 'Si on vous dit des injures au nom de Chrift, vous eftes bien-heureux : car l\'Efprit de gloire &amp; de Dieu repofe fur vous, lequel quant à eux eft blafphemé, mais quant à vous eft glorifié.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 15, 'Et de fait, que nul de vous ne fouffre comme meurtrier, ou larron, ou malfaiteur, ou curieux des affaires d\'autrui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 16, 'Mais fi quelqu\'un fouffre comme Chreftien, qu\'il ne le prenne point à honte, mais qu\'il glorifie Dieu en cet endroit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 17, 'Car il eft temps auffi que le jugement commence par la maifon de Dieu : &amp; s\'il commence premierement par nous, quelle fera la fin de ceux qui n\'obeïffent point à l\'Evangile de Dieu ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 18, 'Et fi le jufte eft difficilement fauvé, où comparoiftra le méchant &amp; le pecheur ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 19, 'Que ceux-là donc auffi qui fouffrent par la volonté de Dieu, lui recommandent leurs ames, comme au fidele Createur, en bien-faifant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 1, 'JE prie les Anciens qui font entre vous, moi qui fuis Ancien avec eux, &amp; tefmoin des fouffrances de Chrift, &amp; qui fuis auffi participant de la gloire laquelle doit eftre revelée :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 2, 'Paiffez le troupeau de Chrift qui vous eft commis, en prenant garde fur lui, non point par contrainte, mais volontairement : non point pour gain deshonnefte, mais d\'un prompt courage :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 3, 'Et non point comme ayant domination fur les heritages du Seigneur, mais tellement que vous foyez pour patron du troupeau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 4, 'Et quand le fouverain Pafteur apparoiftra, vous recevrez la couronne incorruptible de gloire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 5, 'Semblablement vous jeunes gens, affujettiffez-vous aux anciens, tellement que vous foyez tous fujets l\'un à l\'autre : foyez parez par dedans d\'humilité ; parce que Dieu refifte aux orgueilleux, mais il fait grace aux humbles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 6, 'Humiliez vous donc fous la puiffante main de Dieu, afin qu\'il vous éleve quand il fera temps :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 7, 'Defchargeans tout voftre fouci fur lui : car il foin de vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 8, 'Soyez fobres, &amp; veillez : dautant que voftre adverfaire le diable chemine comme un lion rugiffant à l\'entour de vous, cherchant qui il pourra engloutir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 9, 'Auquel il vous faut refifter, eftant fermes en la foi, fçachant que les mefmes fouffrances s\'accompliffent en la compagnie de vos freres qui eft par le monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 10, 'Or le Dieu de toute grace qui nous a appellez à fa gloire eternelle en Jefus Chrift, apres que vous aurez un peu fouffert, vous rende accomplis, vous affermiffe, fortifie, &amp; eftabliffe.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 11, 'A lui foit gloire, &amp; force aux fiecles des fiecles. Amen');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 12, 'Je vous ai efcrit brevement par Silvain noftre frere, qui vous eft fidele, comme j\'eftime, vous exhortant &amp; témoignant que c\'eft la vraye grace de Dieu, en laquelle vous eftes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 13, 'L\'Eglife qui eft à Babylone, eleuë enfemble avec vous, &amp; Marc mon fils, vous faluënt.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 14, 'Salüez-vous l\'un l\'autre d\'un baifer de charité. Paix foit à vous tous qui eftes en Jefus Chrift. Amen. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 1, 'SIMEON Pierre ; ferviteur &amp; Apoftre de Jefus Chrift, à vous qui avez obtenu une foi de pareil prix avec nous, par la juftice de noftre Dieu, &amp; Sauveur Jefus Chrift :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 2, 'Grace &amp; paix vous foit multipliée en la connoiffance de Dieu &amp; de noftre Seigneur Jefus :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 3, 'Puis que fa divine puiffance nous a donné tout ce qui appartient à la vie &amp; à la pieté, par la connoiffance de celui nous a appellez par fa propre gloire &amp; vertu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 4, 'Par lefquelles nous font données les grandes &amp; precieufes promeffes, afin que par elles vous foyez faits participans de la nature divine, eftant efchappez de la corruption qui eft au monde en convoitife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 5, 'Vous donc auffi de mefme y apportant toute diligence, ajouftez vertu par deffus avec voftre foi : &amp;avec vertu fcience :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 6, 'Et avec fcience, temperance : &amp; avec temperance, patience : &amp; avec patience, pieté :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 7, 'Et avec pieté, amour fraternelle : &amp; avec amour fraternelle, charité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 8, 'Car fi ces chofes font en vous, &amp; y abondent, elles ne vous laifferont point oifeux, ni fteriles en la connoiffance de noftre Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 9, 'Car celuy en qui ces chofes ne fe trouvent point, eft aveugle, &amp; ne voit goutte de loin, ayant oublié la purification de fes vieux pechez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 10, 'Partant, freres, eftudiez-vous pluftoft à affermir voftre vocation &amp; election : car en ce faifant vous ne chopperez jamais.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 11, 'Car par ce moyen l\'entrée au royaume eternel de noftre Seigneur &amp; Sauveur Jefus Chrift vous fera abondamment fournie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 12, 'C\'eft pourquoi je ne fera point pareffeux à vous ramentevoir toûjours ces chofes, encore que vous foyez connoiffans &amp; fondez en la verité prefente.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 13, 'Car j\'eftime que c\'eft une chofe jufte, tandis que je fuis en ce tabernacle, de vous éveiller par avertiffement :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 14, 'Sçachant qu\'en bref j\'ai à déloger de ce mien tabernacle, comme noftre Seigneur Jefus Chrift mefme me l\'a declaré.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 15, 'Mais je mettrai peine qu\'apres mon depart auffi vous puiffiez continuellement vous ramentevoir ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 16, 'Car nous ne vous avons point donné à connoitre la puiffance &amp; la venuë de noftre Seigneur Jefus Chrift, en enfuivant des fables artificiellement compofées : mais comme ayant veu fa Majefté de nos propres yeux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 17, 'Car il avoit receu de Dieu le Pere honneur &amp; gloire, quand une telle voix lui fut envoyée de la gloire magnifique, Celui-ci eft mon Fils bien-aimé, auquel j\'ai pris mon bon plaifir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 18, 'Et nous oüifmes cette voix envoyée du ciel, eftant avec luy fur fa fainte montagne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 19, 'Nous avons auffi la parole des Prophetes tres-ferme, à laquelle vous faites bien d\'entendre, comme à une chandelle qui éclaire en un lieu obfur, jufques à ce que le jour commence à luire &amp; que l\'eftoile du matin fe leve en vos cœurs :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 20, 'Si vous entendez premierement cela, que nulle prophetie de l\'Efcriture n\'eft de particuliere declaration.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 21, 'Car la prophetie n\'a point efté jadis apportée par la volonté humaine : mais les faints hommes de Dieu eftant pouffez du Saint Efprit ont parlé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 1, 'MAIS il y a eu auffi de faux Prophetes entre le peuple, comme il y aura pareillement entre vous de faux Docteurs, qui introduiront couvertement des sectes de perdition, &amp; renieront le Seigneur qui les a rachetez, amenans fur eux-mefmes une foudaine perdition.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 2, 'Et plufieurs enfuivront leurs perditions : par lefquels la voye de verité fera blafphemée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 3, 'De forte que par avarice ils feront trafic de vous par des paroles déguifées, fur lefquels dés long temps &amp; leur perdition ne fon fommeille point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 4, 'Car fi Dieu n\'a point efpargné les Anges qui ont peché : mais les ayant abyfmez avec des chaifnes d\'obfcurité, il les a livrez pour eftre refervez au jugement :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 5, 'Et n\'a point efpargné le monde ancien, mais a gardé Noé, lui huictiefme, heraut de juftice, &amp; a amené le deluge fur le monde des méchans :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 6, 'Et a condamné à fubverfion les villes de Sodome &amp; de Gomorrhe, les reduifant en cendre, &amp; les mettant pour exemple à celui qui vivroyent en impieté :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 7, 'Et en a delivré le jufte Lot, qui eftoit oppreffé des abominables par leur infame converfation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 8, '(Car ce jufte-là habitant entr\'eux, parce qu\'il oyoit &amp; voyoit, tourmentoit de jour en jour fon ame jufte, à caufe de leurs méchans actes.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 9, 'Le Seigneur fçais auffi delivrer de tentation ceux qui l\'honorent, &amp; referver les injuftes pour eftre punis au jour du jugement :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 10, 'Et principalement ceux qui cheminent apres la chair, en convoitife de pollution, &amp; mefprifent les Seigneuries, audacieux, abonnez à leur fens, qui n\'ont point honneur de blafmer les dignitez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 11, 'Au lieu que les Anges, qui font plus grands en force &amp; en puiffance, ne donnent point fentence de blafme contre elles devant le Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 12, 'Mais ceux-ci comme des beftes brutes, qui fuivent leur fenfualité, eftant faites pour eftre prifes &amp; deftruites, blafmant ce qu\'ils n\'entendent point, periront par leur propre corruption :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 13, 'Remportant le falaire d\'iniquité, prenant leur plaifir en leurs delices ordinaires, taches, &amp; foüillures, prenant leurs delices en leurs tromperies, en banquetant avec vous :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 14, 'Ayant les yeux pleins d\'adultere, &amp; qui ne ceffent jamais de pecher, amorçant les ames mal affeurées, ayant le cœur exercé en rapines, enfans de malediction :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 15, 'Lefquels ayant delaiffé le droit chemin, fe font fourvoyez ayant enfuivi le train de Balaam fils de Bofor, qui aima le falaire d\'iniquité, mais il fut redargüé de fon injuftice.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 16, 'Car une afneffe muëtte parlant en voix d\'homme, reprima la folie du Prophete.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 17, 'Ce font des fontaines fans eau, &amp; des nuées agitées par le tourbillon : aufquels l\'obfcurité des tenebres eft refervée eternellement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 18, 'Car en prononçant des difcours fort enflez de vanité, ils amorcent, par les convoitifes de la chair, &amp; par infolences, ceux qui eftoyent à bon efcient efchappez d\'entre ceux qui converfent en erreur :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 19, 'Leur promettant la liberté, encore qu\'ils foyent eux-mefmes ferfs de corruption : car on eft reduit en la fervitude de celui par lequel on eft furmonté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 20, 'Car fi apres eftre efchappez des foüillures du monde, par la connoiffance du Seigneur &amp; Sauveur Jefus Chrift : toutefois eftant derechef entortillez en elles, ils en font furmontez, leur derniere condition leur eft devenuë pire que la premiere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 21, 'Car il leur euft mieux vallu n\'avoir point connu la voye de juftice, qu\'apres l\'avoir connuë fe deftourner arriere du faint commandement qui leur avoit efté baillé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 22, 'Mais ce qu\'on dit par un vrai proverbe leur eft avenu ; Le chien eft retourné à fon propre vomiffement : &amp; la truye lavée eft retournée à fa veautrer au bourbier.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 1, 'BIEN-aimez, je vous efcris deja cette feconde Epiftre, par la quelle je réveille par avertiffemens voftre pur entendement :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 2, 'Afin que vous ayez fouvenance des paroles qui auparavant ont efté dites par les faints Prophetes, &amp; de noftre commandement, qui fommes Apoftres du Seigneur &amp; Sauveur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 3, 'Sçachant premierement cela, qu\'il viendra és derniers jours des mocqueurs, cheminant felon leurs propres convoitifes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 4, 'Et difant, Où eft la promeffe de fon advenement ? Car depuis que les Peres font endormis toutes chofes perfeverent ainfi dés le commencement de la creation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 5, 'Car ils ignorent volontairement ce point, que dés jadis les cieux ont en leur eftre, &amp; la terre confiftant ont dans l\'eau, &amp; parmi l\'eau, par la Parole de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 6, 'Par lefquelles chofes le monde d\'alors eft peri, eftant couvert d\'un deluge d\'eaux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 7, 'Mais les cieux qui font maintenant, &amp; la terre, font refervez par la mefme Parole, eftant gardez pour le feu au jour du jugement,&amp; de la deftruction des méchans hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 8, 'Mais, ô vous bien-aimez ! N\'ignorez point une chofe, c\'eft qu\'un jour eft envers le Seigneur comme mille ans, &amp; mille ans font comme un jour.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 9, 'Le Seigneur ne tarde point fa promeffe, comme quelques-uns eftiment retardement, mais il eft patient envers nous, ne voulant point qu\'aucun periffe, mais que tous viennent à repentance.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 10, 'Mais le jour du Seigneur viendra comme le larron en la nuit, auquel les cieux pafferont avec un bruit fifflant de tempefte, &amp; les elemens feront diffous par la chaleur, &amp; la terre, &amp; toutes les œuvres qui font en elle, brufleront entierement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 11, 'Veu donc que toutes ces chofes fe doivent diffoudre, quels vous faut-il eftre en faintes converfations, &amp; œuvres de pieté ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 12, 'En attendant, &amp; vous haftant à la venuë du jour de Dieu, par lequel les cieux eftant enflambez feront diffous, &amp; les elemens fe fondront de chaleur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 13, 'Or nous attendons, felon fa promeffe, de nouveaux cieux &amp; une nouvelle terre, efquelles habite la juftice.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 14, 'C\'eft pourquoi, bien-aimez, en attendant ces chofes, eftudiez-vous à eftre trouvez de lui fans tache &amp; fans reproche en paix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 15, 'Et tenez pour falut la longue attente de noftre Seigneur : comme auffi noftre bien-aimé frere Paul vous en a efcrit felon la fapience qui lui a efté donnée :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 16, 'Comme celui qui en toutes fes Epiftres parle de ces points : entre lefquels il y a des chofes difficiles à entendre, que les ignorans &amp; mal-affeurez tordent, comme auffi les autres Efcritures, à leur propre perdition.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 17, 'Vous donc, bien-aimez, puis que vous en eftes déja avertis, gardez-vous qu\'eftant emportez avec les autres par la feduction des abominables, vous ne decheïez de voftre fermeté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 18, 'Mais croiffez en grace &amp; en connoiffance de noftre Seigneur &amp; Sauveur Jefus Chrift. A lui foit gloire, &amp; maintenant, &amp; jufques au jour d\'eternité. Amen. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 1, 'CE qui eftoit dés le commencement, ce que nous avons ouï, ce que nous avons veu de nos propres yeux, ce que nous avons contemplé, &amp; que nos propres mains ont touché, de la Parole de vie :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 2, '(Car la vie a efté manifeftée, &amp; nous l\'avons veuë, &amp; nous auffi le témoignons, &amp; vous annonçons la vie eternelle, laquelle eftoit avec le Pere, &amp; qui nous a efté manifeftée.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 3, 'Cela donc que nous avons veu &amp; ouï, nous vous l\'annonçons : afin que vous ayez communion avec nous, &amp; que noftre communion foit avec le Pere, &amp; avec fon Fils Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 4, 'Et nous vous efcrivons ces chofes, afin que voftre joye foit accomplie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 5, 'Or c\'eft ici la promeffe que nous avons oüie de lui, &amp; que nous vous annonçons, c\'eft que Dieu eft lumiere, &amp; qu\'en lui il n\'y a tenebres quelconques.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 6, 'Si nous difons que nous avons communion avec lui, &amp; nous cheminons en tenebres, nous mentons, &amp; ne nous portons point en verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 7, 'Mais fi nous cheminons en lumiere, comme il eft en lumiere, nous avons communion l\'un avec l\'autre, &amp; le fang de fon Fils Jefus Chrift nous purifie de tout peché.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 8, 'Si nous difons que nous n\'avons point de peché, nous nous feduifons nous mefme, &amp; il n\'y a point de verité en nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 9, 'Si nous confeffons nos pechez, il eft fidele &amp; jufte pour nous pardonner nos pechez, &amp; nous nettoyer de toute iniquité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 10, 'Si nous difons que nous n\'avons point peché, nous le faifons menteur, &amp; fa parole n\'eft point en nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 1, 'MES petits enfans, je vous efcris ces chofes, afin que vous ne pechiez point : que fi quelqu\'un a peché, nous avons un Advocat envers le Pere, affavoir Jefus Chrift le Jufte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 2, 'Car c\'eft lui qui eft la propitiation pour nos pechez, &amp; non feulement pour les noftres : mais auffi pour ceux de tout le monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 3, 'Et par cela nous fçavons que nous l\'avons connu, fi nous gardons fes commandemens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 4, 'Celui qui dit, Je l\'ai connu, &amp; ne garde point fes commandemens, il eft menteur, &amp; il n\'y a point de verité en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 5, 'Mais celui qui garde fa parole, l\'amour de Dieu eft vrayement accompli en lui : &amp; par cela nous fçavons que nous fommes en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 6, 'Celui qui dit qu\'il demeure en lui doit auffi cheminer comme lui a cheminé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 7, 'Freres, je ne vous efcris point un commandement nouveau, mais le commandement ancien que vous avez eu dés le commencement : l\'ancien commandement c\'eft la Parole que vous avez entenduë dés le commencement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 8, 'Derechef, je vous efcris un commandement nouveau, ce qui eft veritable en lui &amp; en vous : parce que les tenebres font paffées, &amp; la vraye lumiere luit déja.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 9, 'Celui qui dit qu\'il eft en lumiere, &amp; hait fon frere, il eft en tenebres jufques à cette heure.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 10, 'Celui qui aime fon frere, demeure en lumiere, &amp; il n\'y a point d\'achoppement en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 11, 'Mais celui qui hait fon frere, il eft en tenebres, &amp; chemine en tenebres, &amp; ne fçait où il va : car les tenebres lui ont aveuglé les yeux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 12, 'Petits enfans, je vous efcris, parce que vos pechez vous font pardonnez par fon Nom.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 13, 'Peres, je vous efcris, parce que vous avez connu celui qui eft dés le commencement. Jeunes gens, je vous efcris, parce que vous avez furmonté le malin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 14, 'Jeunes enfans, je vous efcris, parce que vous avez connu le Pere. Peres, je vous ai efcrit, parce que vous avez connu celui qui eft dés le commencement. Jeunes gens, je vous ai efcrit, parce que vous eftes forts, &amp; que la parole de Dieu demeure en vous, &amp; que vous avez furmonté le malin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 15, 'N\'aimez point le monde ni les chofes qui font au monde : fi quelqu\'un aime le monde, l\'amour du Pere n\'eft point en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 16, 'Car tout ce qui eft au monde, (affavoir la convoitife de la chair, &amp; la convoitife des yeux, &amp; l\'outrecuidance de la vie,) n\'eft point du Pere, mais eft du monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 17, 'Et le monde paffe, &amp; fa convoitife : mais celui qui fait la volonté de Dieu, demeure eternellement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 18, 'Jeunes enfans, le dernier temps eft ; &amp; comme vous avez entendu que l\'Antechrift viendra, dés maintenant mefmes il y a plufieurs Antechrifts, dont nous connoiffons que c\'eft le dernier temps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 19, 'Ils font fortis d\'entre nous : mais ils n\'eftoyent point d\'entre nous : car s\'ils euffent efté d\"entre nous, ils fuffent demeurez avec nous : mais c\'eft afin qu\'il fuft manifefté que tous ne font point d\'entre nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 20, 'Mais vous avez l\'onction de par le Sainct, &amp; connoiffez toutes chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 21, 'Je ne vous ai pas efcrit comme fi vous ne connoiffiez point la verité, mais parce que vous la connoiffez, &amp; qu\'aucune menterie n\'eft de verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 22, 'Qui eft-ce qui eft menteur, finon celui qui nie que Jefus eft le Chrift ? Celui-là eft l\'Antechrift qui nie le Pere &amp; le Fils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 23, 'Quiconque nie le Fils, n\'a non plus le Pere : quiconque confeffe le Fils, a auffi le Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 24, 'Que cela donc que vous avez entendu dés le commencement, foit permanent en vous : car fi ce que vous avez entendu dés le commencement eft permanent en vous, vous demeurerez auffi au Fils &amp; au Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 25, 'Et c\'eft ici le promeffe, laquelle il vous a annoncée, affavoir la vie eternelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 26, 'Je vous ai efcrit ces chofes, touchant ceux qui vous feduifent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 27, 'Mais l\'onction que vous avez receuë de lui, demeure en vous : &amp; vous n\'avez pas befoin qu\'on vous enfeigne : mais comme la mefme onction vous enfeigne toutes chofes, &amp; eft veritable, &amp; n\'eft point menterie, &amp; comme elle vous a enfeignez, vous demeurerez en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 28, 'Maintenant donc, mes petits enfans, demeurez en lui : afin que quand il apparoiftra, nous ayons affeurance, &amp; que nous ne foyons point confus de fa prefence, à fa venuë.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 29, 'Si vous fçavez qu\'il eft jufte, fçachez que quiconque fait juftice, eft né de lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 1, 'VOYEZ quelle charité le Pere nous a donnée que nous foyons nommez enfans de Dieu : pour ce le monde ne nous connoift point, dautant qu\'il ne l\'a point connu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 2, 'Bien-aimez, nous fommes maintenant enfans de Dieu, mais ce que nous ferons, n\'eft point encore apparu : or nous fçavons qu\'apres qu\'il fera apparu, nous ferons femblable à lui : car nous le verrons ainfi qu\'il eft.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 3, 'Et quiconque a cette efperance en lui, fe purifie comme auffi lui eft pur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 4, 'Quiconque fait peché, fait auffi contre la Loi : &amp; le peché eft ce qui eft contre la Loi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 5, 'Or vous fçavez qu\'il eft apparu, afin qu\'il oftaft nos pechez : &amp; il n\'y a point de peché en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 6, 'Quiconque demeure en lui, ne peche point : quiconque peche, ne l\'a point veu, ni ne l\'a point connu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 7, 'Mes petits enfans, que nul ne vous feduife : celui qui fait juftice, eft jufte, comme lui eft jufte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 8, 'Celui qui fait peché, il eft du diable : car le diable peche dés le commencement. Or le Fils de Dieu eft apparu, afin qu\'il defift les œuvres du diable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 9, 'Quiconque eft né de Dieu, ne fait point de peché ; car la femence d\'icelui demeure en lui : &amp; il ne peut pecher, parce qu\'il eft né de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 10, 'Par ceci les enfans de Dieu &amp; les enfans du diable font manifeftez : quiconque ne fait point juftice, &amp; qui n\'aime point fon frere, n\'eft point de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 11, 'Car c\'eft ici ce que vous avez oüi annoncer dés le commencement, affavoir, Que nous nous aimions l\'un l\'autre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 12, 'Non point comme Caïn qui eftoit du malin, &amp; tua fon frere. Et pour quelle caufe le tua-t\'il ? Parce que fes œuvres eftoyent mauvaifes, &amp; celles de fon frere eftoyent juftes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 13, 'Mes freres, ne vous eftonnez point fi le monde vous hait.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 14, 'En ce que nous aimons les freres, nous fçavons que nous fommes tranfportez de la mort à la vie : celui qui n\'aime point fon frere, il demeure en la mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 15, 'Quiconque hait fon frere eft meurtrier : &amp; vous fçavez que nul meurtrier n\'a la vie eternelle demeurante en foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 16, 'A ceci nous avons connu la charité, c\'eft qu\'il a mis fa vie pour nous : nous devons donc auffi mettre nos vies pour nos freres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 17, 'Or celui qui aura des biens de ce monde, &amp; verra fon frere avoir neceffité, &amp; lui fermera fes entrailles, comment eft-ce que la charité de Dieu demeure en lui ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 18, 'Mes petits enfans, n\'aimons point de parole, ni de la langue ; mais d\'œuvre &amp; de verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 19, 'Car à ceci nous connoiffons que nous fommes de verité, &amp; nous affeurons nos cœurs devant lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 20, 'Que fi noftre cœur nous condamne, Dieu certes eft plus grand que noftre cœur, &amp; connoift toutes chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 21, 'Bien-aimez, fi noftre cœur ne nous condamne point, nous avons affeurance envers Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 22, 'Et quoi que nous demandions, nous le recevons de lui : car nous gardons fes commandemens, &amp; faifons les chofes qui lui font agreables.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 23, 'Et c\'eft ici fon commandement, Que nous croïyons au Nom de fon Fils Jefus Chrift, &amp; que nous nous aimions l\'un l\'autre, comme il nous en a donné le commandement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 24, 'Et celui qui garde fes commandemens demeure en lui, &amp; lui en icelui : &amp; par ceci nous connoiffons qu\'il demeure en nous, affavoir par l\'Efprit qu\'il nous a donné');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 1, 'BIEN-aimez, ne croyez point à tout efprit, mais efprouvez les efprits s\'ils font de Dieu : car plufieurs faux prophetes font venus au monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 2, 'Connoiffez par ceci l\'Efprit de Dieu. Tout efprit qui confeffe que Jefus Chrift eft venu en chair, eft de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 3, 'Et tout efprit qui ne confeffe point que Jefus Chrift eft venu en chair, n\'eft point de Dieu : &amp; tel eft l\'efprit de l\'Antechrift ; duquel vous avez oüi parler qu\'il viendra, &amp; eft déja maintenant au monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 4, 'mes petits enfans, vous eftes de Dieu, &amp; les avez furmontez : car celui qui eft en vous eft plus grand que celui qui eft au monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 5, 'Ils font du monde, c\'eft pourquoi ils parlent du monde, &amp; le monde les efcoute.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 6, 'Nous fommes de Dieu : celui qui connoit Dieu, nous efcoute, celui qui n\'eft point de Dieu, ne nous efcoute point : nous connoiffons à ceci l\'Efprit de verité &amp; l\'efprit d\'erreur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 7, 'Bien-aimez, aimons nous l\'un l\'autre : car la charité eft de Dieu, &amp; quiconque aime, eft né de Dieu, &amp; connoit Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 8, 'Celui qui n\'aime point, n\'a point connu Dieu : car Dieu eft charité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 9, 'En cela la charité de Dieu envers nous eft manifeftée, que Dieu a envoyé fon Fils unique au monde, afin que nous vivions par lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 10, 'En ceci eft la charité, non point que nous ayons aimé Dieu, mais parce que lui nous a aimez, &amp; a envoyé fon Fils pour eftre la propitiation pour nos pechez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 11, 'Bien-aimez, fi Dieu nous a ainfi aimez, nous nous devons auffi aimer l\'un l\'autre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 12, 'Nul ne vid jamais Dieu : fi nous nous aimons l\'un l\'autre, Dieu demeure en nous, &amp; fa charité eft accomplie en nous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 13, 'Par ceci nous connoiffons que nous demeurons en lui, &amp; lui en nous, parce qu\'il nous a donné de fon Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 14, 'Et nous l\'avons veu, &amp; témoignons que le Pere a envoyé le Fils pour eftre le Sauveur du monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 15, 'Quiconque confeffera que Jefus eft le Fils de Dieu, Dieu demeure en lui, &amp; lui en Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 16, 'Et nous avons connu &amp; creu la charité que Dieu a envers nous. Dieu eft charité : &amp; celui qui demeure en charité, demeure en Dieu, &amp; Dieu en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 17, 'En ceci la charité envers nous eft accomplie, (afin que pour le jour du jugement nous ayons affeurance) que tel qu\'il eft tels fommes-nous en ce monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 18, 'Il n\'y a point de peur en la charité, mais la parfaite charité chaffe dehors la peur : car la peur apporte de la peine, &amp; celui qui a peur n\'eft point accompli en charité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 19, 'Nous l\'aimons, dautant que lui nous a aimez le premier.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 20, 'Si quelqu\'un dit, J\'aime Dieu, &amp; il hait fon frere, il eft menteur. Car celui qui n\'aime point fon frere lequel il voit, comment peut-il aimer Dieu lequel il ne voit point ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 21, 'Et nous avons ce commandement de par lui, que celui qui aime Dieu, aime auffi fon frere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 1, 'QUICONQUE croit que Jefus eft le Chrift, il eft né de Dieu : &amp; quiconque aime celui qui l\'a engendré, aime auffi celui qui eft né de lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 2, 'Par ceci nous connoiffons que nous aimons les enfans de Dieu, quand nous aimons Dieu, &amp; gardons fes commandemens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 3, 'Car c\'eft ici l\'amour de Dieu, que nous gardions fes commandemens : &amp; fes commandemens ne font point griefs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 4, 'Car tout ce qui eft né de Dieu, furmonte le monde : &amp; c\'eft ici la victoire qui a furmonté le monde, affavoir noftre foi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 5, 'Qui eft celui qui furmonte le monde, finon celui qui croit que Jefus eft le Fils de Dieu ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 6, 'C\'eft celui qui eft venu par eau &amp; par fang, affavoir Jefus Chrift ; non feulement par eau, mais par eau &amp; par fang : &amp; c\'eft l\'Efprit qui témoigne, que l\'Efprit eft la verité');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 7, 'Car il y en a trois qui rendent témoignage au ciel, le Pere, la Parole, &amp; le Saint Efprit : &amp; ces trois-là font un.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 8, 'Il y a auffi trois qui rendent témoignage en la terre, affavoir l\'Efprit, l\'eau &amp; le fang : &amp; ces trois-là fe rapportent à un.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 9, 'Si nous recevons le témoignage des hommes, le témoignage de Dieu eft plus grand : car c\'eft là le témoignage de Dieu, lequel il a témoigné de fon Fils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 10, 'Celui qui croit au Fils de Dieu, il a le témoignage de Dieu en foi-mefme : celui qui ne croit point à Dieu, il l\'a fait menteur : car il n\'a point creu au témoignage que Dieu à témoigné de fon propre Fils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 11, 'Et c\'eft ici le témoignage, affavoir que Dieu nous a donné la vie eternelle : &amp; que cette vie eft en fon Fils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 12, 'Celui qui a le Fils, a la vie : celui qui n\'a point le Fils de Dieu, n\'a point la vie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 13, 'Je vous ai efcrit ces chofes ; à vous qui croyez au Nom du Fils de Dieu, afin que vous fçachiez que vous avez la vie eternelle, &amp; afin que vous croiyez au Nom du Fils de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 14, 'Et c\'eft ici l\'affeurance que nous avons envers lui, que fi nous demandons quelque chofe felon fa volonté, il nous exauce.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 15, 'Et fi nous fçavons qu\'il nous exauce, quoi que nous demandions, nous fçavons que nous obtenons les requeftes lefquelles nous lui avons demandées.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 16, 'Si quelqu\'un voit fon frere pecher du peché qui n\'eft point à mort, il demandera à Dieu , &amp; il lui donnera la vie : voire à ceux qui ne pechent point à mort : Il y a un peché à mort : je ne dis point que tu pries pour ce peché-là.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 17, 'Toute iniquité eft peché : mais il y a quelque peché qui n\'eft point à mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 18, 'Nous fçavons que quiconque eft né de Dieu, ne peche point : mais celui qui eft engendré de Dieu fe conferve foi-mefme, &amp; le malin ne le touche point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 19, 'Nous fçavons que nous fommes de Dieu, &amp; tout le monde gift en mauvaiftié.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 20, 'Mais nous fçavons que le Fils de Dieu eft venu, &amp; nous a donné de l\'entendement pour connoitre celui qui eft le veritable, affavoir en fon Fils Jefus Chrift : Il eft le vrai Dieu, &amp; la vie eternelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 21, 'Mes petits enfans, gardez-vous des idoles. Amen. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 1, '');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 2, 'A caufe de la verité qui demeure en nous, &amp; qui fera avec nous à jamais.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 3, 'Grace, mifericorde, &amp; paix de par Dieu le Pere, &amp; de par le Seigneur Jefus Chrift le Fils du Pere, foit avec vous en verité &amp; en charité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 4, 'J\'ai efté fort réjoüi de ce que j\'ai trouvé de tes enfans cheminans en verité : felon que nous avons receu le commandement du Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 5, 'Et maintenant, Dame, je te prie, (non point comme t\'écrivant un nouveau commandement, mais celui que nous avons eu dés le commencement,) que nous nous aimions l\'un l\'autre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 6, 'Et c\'eft ici la charité, que nous cheminions felon fes commandemens : &amp; le commandement eft, felon que vous avez entendu dés le commencement, que vous cheminez en lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 7, 'Car plufieurs feducteurs font entrez au monde, lefquels ne confeffent point que Jefus Chrift eft venu en la chair : un tel eft feducteur &amp; Antechrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 8, 'Prenez garde à vous-mefmes : afin que nous ne perdions point ce que nous avons fait : mais que nous recevions un plein falaire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 9, 'Quiconque tranfgreffe, &amp; ne demeure point en la doctrine de Chrift, n\'a point Dieu : celui qui demeure en la doctrine de Chrift, a &amp; le Pere &amp; le Fils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 10, 'Si quelqu\'un vient vers vous, &amp; n\'apporte point cette doctrine, ne le recevez point en voftre maifon, &amp; ne le falüez point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 11, 'Car celui qui le falüe, communique à fes œuvres mauvaifes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 12, 'Bien que j\'euffe plufieurs chofes à vous efcrire, je ne les ai point voulu efcrire avec du papier &amp; de l\'encre : mais j\'efpere de venir vers vous, &amp; de parler bouche à bouche, afin que noftre joye foit accomplie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 13, 'Les enfans de ta fœur éleuë te falüent. amen. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 1, '');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 2, 'Bien-aimé, je defire que tu profperes en toutes chofes, &amp; que tu fois en fanté, felon que ton ame eft en profperité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 3, 'Car j\'ai efté fort éjoüi quand les freres font venus, &amp; ont rendu témoignage de ta fincerité, comme tu chemines en verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 4, 'Je n\'ai point de plus grande joye que celle-ci, qui eft d\'entendre que mes enfans cheminent en verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 5, 'Bien-aimé, tu fais fidelement en tout ce que tu fais envers les freres, &amp; envers les eftrangers.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 6, 'Lefquels ont rendu témoignage de ta charité en la prefence de l\'Eglife : lefquels tu feras bien d\'accompagner dignement comme il eft feant felon Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 7, 'Car ils font partis pour fon Nom, ne prenant rien des Gentils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 8, 'Nous devons donc recevoir ceux qui font tels, afin que nous aidions à la verité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 9, 'J\'ai efcrit à l\'Eglife : mais Diotrephes qui aime d\'eftre le premier entr\'eux ne nous reçoit point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 10, 'Pour cette caufe, fi je viens, je ramentevrai les actes qu\'il commet, babillant de nous en de mauvais difcours : &amp; non content de cela, non feulement il ne reçoit point les freres, mais il empefche ceux qui les veulent recevoir, &amp; les jette hors de l\'Eglife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 11, 'Bien-aimé, n\'enfui point le mal, mais le bien : celui qui fait bien, il eft de Dieu : mais celui qui fait mal, n\'a point veu Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 12, 'Tous rendent témoignage à Demetrius, &amp; la verité mefme : &amp; nous en rendons auffi témoignage, &amp; vous fçavez que noftre témoignage eft veritable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 13, 'J\'avois plufieurs chofes à efcrire, mais je ne veux point t\'écrire avec encre &amp; plume :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 14, 'mais j\'efpere de te voir en bref, &amp; nous parlerons bouche à bouche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 15, 'Paix te foit. les amis te falüent. salüe les amis nom par nom. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 1, '');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 2, 'Mifericorde, &amp; paix, &amp; dilection vous foit multipliée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 3, 'Bien-aimez, comme ainfi foit que m\'eftudiant entierement à vous efcrire du falut commun, il m\'a efté neceffaire de vous efcrire, pour vous exhorter à fouftenir le combat pour la foi, laquelle a efté une fois baillée aux Saints.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 4, 'Car quelques-uns fe font gliffez lefquels dés long-temps auparavant eftoyent enrôllez à une telle condamnation ; gens fans pieté, changeans la grace de noftre Dieu en diffolution, &amp; renonçans le feul dominateur Jefus Chrift, noftre Dieu &amp; Seigneur.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 5, 'Or je vous veux ramentevoir ces chofes, veu qu\'une fois vous fçavez cela, affavoir que le Seigneur ayant delivré le peuple du païs d\'Egypte, deftruifit puis apres ceux qui n\'avoyent point creu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 6, 'Et il a refervé fous l\'obfcurité en des liens eternels, jufques au jugement de la grande journée, les Anges qui n\'ont point gardé leur origine, mais ont delaiffé leur propre domicile.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 7, 'Comme Sodome &amp; Gomorrhe, &amp; les villes circonvoifines, lefquelles ayant paillardé en pareille maniere que ceux-là, &amp; s\'eftant débordées apres une autre chair, ont efté propofées pour exemple : ayant receu jugement de feu eternel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 8, 'Et ceux-ci neantmoins femblablement eftant endormis, d\'une part foüillent leur chair, &amp; d\'ailleurs mefprifent la feigneurie, &amp; blafment les dignitez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 9, 'Toutefois Michel l\'Archange, quand il debattoit difputant avec le diable touchant le corps de Moyfe, n\'ofa jetter fentence de blafme, mais il dit feulement, Le Seigneur te redarguë.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 10, 'Mais ceux-ci médifent de tout ce qu\'ils n\'entendent point, &amp; fe corrompent en tout ce qu\'ils connoiffent naturellement comme les beftes brutes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 11, 'Malheur fur eux : car ils ont fuivi le train de Caïn, &amp; fe font débordez en l\'erreur du falaire de Balaam, &amp; font peris felon la contradiction de Coré.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 12, 'Ceux-ci font des taches en vos repas de charité, en banquetant avec vous, fe repaiffant eux-mefmes fans crainte : nuées fans eau, emportées des vents çà &amp; là : arbres dont le fruit fe pourrit, &amp; fans fruit, deux fois morts &amp; déracinez :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 13, 'Vagues impetuëufes de la mer, qui efcument leurs vilenies : eftoiles errantes, à qui eft refervée l\'obfcurité des tenebres eternellement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 14, 'Defquels auffi a prophetizé Enoch, feptiéme homme apres Adam, difant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 15, 'Voici, le Seigneur eft venu avec fes Saints, qui font par millions, pour donner jugement contre tous &amp; convaincre tous les méchans d\'entr\'eux, de tous leurs actes méchans, qu\'il ont commis méchamment, &amp; de toutes les rudes paroles que les pecheurs méchans ont proferées contre lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 16, 'Ce font des murmurateurs, quereleux, cheminans felon leurs convoitifes : &amp; leur bouche prononce des propos fort enflez, ayant en admiration les perfonnes pour leur profit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 17, 'Mais vous, bien-aimez, ayez fouvenance des paroles qui ont efté dites auparavant par les Apoftres de noftre Seigneur Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 18, 'Comme il vous difoyent qu\'il y auroit au dernier temps des mocqueurs, cheminans felon leurs convoitifes de méchancetez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 19, 'Ce font ceux qui fe feparent eux-mefmes, gens fenfuels, n\'ayant point l\'Efprit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 20, 'Mais vous, bien-aimez, vous edifiant vous-mefmes fur voftre tres-fainte foi, &amp; priant par le Saint Efprit,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 21, 'Confervez-vous les uns les autres en la dilection de Dieu, attendant la mifericorde de noftre Seigneur Jefus Chrift à vie eternelle :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 22, 'Et ayez pitié des uns, en ufant de difcretion :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 23, 'Et fauvez les autres par frayeur, comme les arrachant hors du feu, haïffant mefme la robbe tachée par la chair.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 24, 'Or à celui qui vous peut garder fans chopper, &amp; vous prefenter reprenfibles davant fa gloire, avec joye,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 25, 'A Dieu feul fage noftre Sauveur, foit gloire &amp; magnificence, force &amp; puiffance, maintenant &amp; à tout jamais. Amen. ');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 1, 'La Revelation de Jefus Chrift, laquelle Dieu lui a donnée, pour declarer à fes ferviteurs les chofes qui doivent eftre faites bien-toft : lequel les a fignifiées, les ayant envoyées par fon Ange à fon ferviteur :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 2, 'Qui a témoigné de la Parole de Dieu, &amp; du témoignage de Jefus Chrift, &amp; de toutes les chofes qu\'il a veuës.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 3, 'Bien-heureux eft celui qui lit, &amp; ceux qui oyent les paroles de cette Prophetie, &amp; gardent les chofes qui font efcrites en elle : car le temps eft prés.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 4, 'Jean aux fept Eglifes qui eftes en Afie. Grace &amp; paix vous foit de par celui qui est, &amp; qui estoit &amp; qui est a venir : &amp; de par les fept Efprits qui font devant fon trône.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 5, 'Et de par Jefus Chrift, qui eft le fidele témoin, le premier né d\'entre les morts, &amp; le Prince des Rois de la terre :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 6, 'A celui qui nous a aimez, &amp; nous a lavez de nos pechez par fon fang, &amp; nous a faits Rois &amp; Sacrificateurs à Dieu fon Pere : à lui foit gloire &amp; force és fiecles des fiecles. Amen.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 7, 'Voici, il vient avec les nuées, &amp; tout œil le verra, voire mefme ceux-là qui l\'ont percé : &amp; toutes les tribus de la terre menerons deüil devant lui : oüi, Amen.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 8, 'Je fuis Alpha &amp; Omega, le commencement &amp; la fin, dit le Seigneur, qui est, &amp; qui estoit, &amp; qui est a venir, le Tout-puiffant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 9, 'Moi Jean, qui fuis auffi voftre frere &amp; compagnon en l\'affliction, &amp; au regne, &amp; en la patience de Jefus Chrift, eftois en l\'ifle appellé Patmos, pour la Parole de Dieu, &amp; pour le témoignage de Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 10, 'Or je fus ravi en efprit un jour de Dimanche, &amp; oüis derriere moi une grande voix comme d\'une trompette,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 11, 'Difant, Je fuis Alpha &amp; Omega, le premier &amp; le dernier : en outre, Efcris en un livre ce que tu vois, &amp; l\'envoye aux fept Eglifes qui font en Afie : affavoir à Ephefe, &amp; à Smyrne, &amp; à Pergame, &amp; à Thyatire, &amp; à Sarde, &amp; à Philadelphie, &amp; à Ladodicée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 12, 'Alors je me retournai pour voir la voix qui avoit parlé à moi : &amp; m\'eftant retourné, je vis fept chandeliers d\'or :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 13, 'Et au milieu des fept chandeliers d\'or, un perfonnage femblable à un homme, veftu d\'une longue robbe, &amp; ceint d\'une ceinture d\'or à l\'endroit des mammelles :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 14, 'Et fon chef &amp; fes cheveux eftoyent blancs comme laine blanche, comme neige, &amp; fes yeux eftoyent comme flamme de feu :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 15, 'Et fes pieds eftoyent femblables à de l\'airain tres-luifant, comme s\'ils euffent efté embrafez en une fournaife : &amp; fa voix eftoit comme le bruit de groffes eaux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 16, 'Et il avoit en fa main droite fept eftoiles : &amp; de fa bouche il fortoit une efpée aiguë à deux trenchans : &amp; fon regard eftoit femblable au Soleil quand il reluit en fa force.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 17, 'Et quand je l\'eus veu, je tombai à fes pieds comme mort, &amp; il mit fa main droite fur moi, me difant, Ne crains point, je fuis le premier &amp; le dernier :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 18, 'Et qui vis, mais j\'ai efté mort, &amp; voici je fuis vivant aux fiecles des fiecles, Amen : &amp; je tiens les clefs de l\'enfer &amp; de la mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 19, 'Efcris les chofes que tu as veuës, &amp; celles qui font, &amp; celles qui doivent eftre faites ci-apres :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 20, 'Le myftere des fept eftoiles que tu as veuës en ma main droite, &amp; les fept chandeliers d\'or. Les fept eftoiles font les Anges des fept Eglifes : &amp; les fept chandeliers que tu as veus, font les fept Eglifes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 1, 'ESCRIS à l\'Ange de l\'Eglife d\'Ephefe, Celui qui tient les fept eftoiles en fa main droite, lequel chemine au milieu des fept chandeliers d\'or, dit ces chofes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 2, 'Je connois tes œuvres, &amp; ton travail, &amp; ta patience, &amp; que tu ne peux fouffrir les mauvais, &amp; as efprouvé ceux qui fe difent eftre Apoftres, &amp; ne le font point : &amp; les as trouvez menteurs :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 3, 'Et as enduré, &amp; as eu patience, &amp; as travaillé pour mon Nom, &amp; ne t\'es point laffé.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 4, 'Mais j\'ai quelque chofe contre toi, c\'eft que tu as delaiffé ta premiere charité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 5, 'C\'eft pourquoi fouvien-toi d\'où tu es decheu, &amp; te repens, &amp; fai les premieres œuvres : autrement je viendrai à toi bien-toft, &amp; ofterai ton chandelier de fon lieu, fi tu ne te repens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 6, 'Mais tu as ceci, que tu hais les actes des Nicolaïtes, lefquels je hais moi auffi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 7, 'Que celui qui a des oreille, oye ce que l\'Efprit dit aux Eglifes. A celui qui vaincra je lui donnerai à manger de l\'arbre de vie, lequel eft au milieu du Paradis de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 8, 'Efcris auffi à l\'Ange de l\'Eglife de ceux de Smyrne, Le premier &amp; le dernier, qui a efté mort, &amp; eft retourné en vie, dit ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 9, 'Je connois tes œuvres, &amp; ta tribulation &amp; ta pauvreté, (mais tu es riche) &amp; le blafpheme de ceux qui fe difent eftre Juifs, &amp; ne le font point, mais la fynagogue de Satan.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 10, 'Ne crains rien des chofes que tu as à fouffrir. Voici, il adviendra que le diable mettra quelques-uns d\'entre vous en prifon, afin que vous foyez efprouvez : &amp; vous aurez une tribulation de dix jours : fois fidele jufques à la mort, je te donnerai la couronne de vie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 11, 'Que celui qui a oreille, oye ce que l\'Efprit dit aux Eglifes. Celui qui vaincra n\'aura point de nuifance par la mort feconde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 12, 'Efcris auffi à l\'Ange de l\'Eglife qui eft à Pergame, Celui qui a l\'efpée aiguë à deux trenchans, dit ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 13, 'Je connois tes œuvres, &amp; où tu habites, affavoir là où eft le fiege de Satan, &amp; que tu retiens mon Nom, &amp; n\'as point renoncé ma foi : mefme lors qu\'Antipas mon fidele martyr a efté mis à mort entre vous, là où habite Satan.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 14, 'Mais j\'ai quelque peu de chofe contre toi : c\'eft que tu en as là qui retiennent la doctrine de Balaam, qui enfeignoit Balak à mettre fcandale devant les enfans d\'Ifraël, afin qu\'ils mangeaffent des chofes facrifiées aux idoles, &amp; qu\'ils paillardaffent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 15, 'Pareillement tu en as toi auffi qui retiennent la doctrine des Nicolaïtes : ce que je hais.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 16, 'Repens-toi : autrement je viendrai à toi bien-toft, &amp; combattrai contr\'eux par l\'efpée de ma bouche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 17, 'Que celui qui a oreille, oye ce que l\'Efprit dit aux Eglifes. A celui qui vaincra je lui donnerai à manger de la Manne qui eft cachée, &amp; lui donnerai un caillou blanc, &amp; au caillou un nouveau nom efcrit, lequel nul ne connoit finon celui qui le reçoit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 18, 'Efcris auffi à l\'Ange de l\'Eglife qui eft à Thyatire, Le Fils de Dieu qui a fes yeux comme une flamme de feu, &amp; duquel les pieds font femblables à de l\'airain tres-luifant, dit ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 19, 'Je connois tes œuvres, &amp; ta charité, &amp; ton fervice, &amp; ta foi, &amp; ta patience, &amp; tes œuvres, &amp; que tes dernieres œuvres paffent les premieres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 20, 'Mais j\'ai quelque peu de chofe contre toi : c\'eft que tu fouffres que la femme Jezabel, qui fe dit eftre propheteffe, enfeigne, &amp; feduife mes ferviteurs, pour les faire paillarder, &amp; manger des chofes facrifiées aux idoles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 21, 'Et je lui ai donné du temps , afin qu\'elle fe repentift de fa paillardife : &amp; elle ne s\'eft point repentie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 22, 'Voici, je la mets au lit, &amp; ceux qui commettent adultere avec elle, en grande affliction, s\'ils ne fe repentent de leurs actes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 23, 'Et je tuërai de mort fes enfans, &amp; toutes les Eglifes connoitront que je fuis celui qui fonde les reins &amp; les cœurs : &amp; je rendrai à chacun de vous felon fes œuvres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 24, 'Mais je vous dis à vous, &amp; aux autres qui font à Thyatire, à tous ceux qui n\'ont point connu les profondeurs de Satan, (comme ils parlent) je ne mettrai point fur vous une autre charge.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 25, 'Mais retenez ce que vous avez jufques à ce que je vienne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 26, 'Car à celui qui aura vaincu, &amp; aura gardé mes œuvres jufques à la fin, je lui donnerai puiffance fur les nations.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 27, 'Et il gouvernera avec une verge de fer, &amp; elles feront brifées comme les vaiffeaux d\'un potier : comme j\'ai receu moi de mon Pere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 28, 'Et je lui donnerai l\'eftoile du matin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 29, 'Que celui qui a oreille, oye ce que l\'Efprit dit aux Eglifes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 1, 'ESCRIS auffi à l\'Ange de l\'Eglife qui eft à Sarde, Celui qui a les fept Efprits de Dieu, &amp; les fept eftoiles, dit ces chofes. Je connois tes œuvres : c\'eft que tu as le bruit de vivre, &amp; tu es mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 2, 'Sois veillant, &amp; confirme le refte qui s\'en va mourir : car je n\'ai point trouvé tes œuvres parfaites devant Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 3, 'Souvien-toi donc quelles chofes tu as receuës &amp; oüies, &amp; les garde &amp; te repens. Que fi tu ne veilles, je viendrai contre toi comme le larron, &amp; tu ne fçauras à quelle heure je viendrai contre toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 4, 'Toutefois tu as quelque peu de perfonnes auffi à Sarde, qui n\'ont point foüillé leurs veftemens, &amp; qui chemineront avec moi en veftemens blancs : car ils en font dignes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 5, 'Celui qui vaincra fera veftu de veftemens blancs, &amp; je n\'effacerai point fon nom du livre de vie, mais je confefferai fon nom devant mon Pere, &amp; devant fes Anges.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 6, 'Que celui qui a oreille, oye ce que l\'Efprit dit aux Eglifes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 7, 'Efcris auffi à l\'Ange de l\'Eglife qui eft à Philadelphie, Le Saint &amp; le Veritable, qui a la clef de David, qui ouvre &amp; nul ne ferme, qui ferme &amp; nul n\'ouvre, dit ces chofes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 8, 'Je connois tes œuvres : voici, je t\'ai donné la porte ouverte devant toi, &amp; nul ne la peut fermer, parce que tu as un peu de force, &amp; tu as gardé ma Parole, &amp; n\'as point renoncé mon Nom.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 9, 'Voici, je mettrai ceux de l\'affemblée de Satan, qui fe difent eftre Juifs, &amp; ne le font point, mais mentent : voici, je les ferai venir, &amp; fe profterner devant tes pieds, &amp; connoiftre que je t\'aime.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 10, 'Parce que tu as gardé la parole de ma patience, moi auffi je te garderai de l\'heure de la tentation qui doit venir au monde univerfel, pour efprouver ceux qui habitent fur la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 11, 'Voici, je viens bien-toft : tien ferme ce que tu as, afin que nul ne prenne ta couronne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 12, 'Celui qui vaincra, je le ferai eftre une colonne au temple de mon Dieu, &amp; il ne fortira plus dehors : &amp; j\'écrirai fur lui le nom de mon Dieu, &amp; le nom de la cité de mon Dieu, qui eft la nouvelle Jerufalem, laquelle defcend du ciel de devers mon Dieu, &amp; mon nouveau Nom.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 13, 'Que celui qui a oreille, oye ce que l\'Efprit dit aux Eglifes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 14, 'Efcris auffi à l\'Ange de l\'Eglife de ceux de Laodicée, L\'Amen, le témoin fidele &amp; veritable, le commencement de la creature de Dieu, dit ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 15, 'Je connois tes œuvres : c\'eft que tu n\'es ni froid, ni boüillant : à la mienne volonté que tu fuffes froid, ou boüillant :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 16, 'C\'eft pourquoi dautant que tu es tiede, &amp; que tu n\'es ni froid, ni boüillant, je te vomirai hors de ma bouche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 17, 'Car tu dis, Je fuis riche, &amp; fuis enrichi, &amp; n\'ai faute de rien : &amp; tu ne connois point que tu es malheureux, &amp; miferable, &amp; pauvre, &amp; aveugle, &amp; nud.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 18, 'Je te confeille que tu achetes de moi de l\'or efprouvé par le feu, afin que tu deviennes riches : &amp; des veftemens blancs, afin que tu en fois veftu, &amp; que la vergogne de ta nudité n\'apparoiffe point : &amp; d\'oindre tes yeux de collyre, afin que tu voyes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 19, 'Je reprens &amp; chaftie tous ceux que j\'aime : prens donc zele, &amp; te repens.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 20, 'Voici, je me tiens à la porte &amp; frappe : fi quelqu\'un oit ma voix, &amp; ouvre la porte, j\'entrerai vers lui, &amp; foupperai avec lui, &amp; lui avec moi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 21, 'Celui qui vaincra, je le ferai feoir avec moi en mon trône, ainfi que moi auffi ai vaincu, &amp; fuis affis avec mon Pere en fon trône.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 22, 'Que celui qui a oreille, oye ce que l\'Efprit dit aux Eglifes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 1, 'APRES ces chofes je regardai, &amp; voici une porte ouverte au ciel : &amp; la premiere voix que j\'avois oüie comme d\'une trompette parlant avec moi, difoit, Monte ici, &amp; je te montrerai les chofes qui doivent eftre faites ci-apres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 2, 'Et incontinent je fus ravi en efprit : &amp; voici un trône eftoit pofé au ciel, &amp; il y avoit quelqu\'un affis fur le trône.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 3, 'Or celui qui eftoit affis, eftoit femblable de regard à une pierre de jafpe &amp; de fardoine ; &amp; l\'arc celefte eftoit à l\'entour du trône, femblable à voir à une efmeraude.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 4, 'Et à l\'entour du trône il y avoit vingt-quatre fieges : &amp; je vis fur les fieges vingt-quatre Anciens affis, veftus d\'habillemens blancs, &amp; qui avoyent fur leurs teftes des couronnes d\'or.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 5, 'Et du trône procedoyent des efclairs, &amp; des tonnerres, &amp; des voix : &amp; il y avoit fept lampes de feu ardentes devant le trône, lefquelles font les fept Efprits de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 6, 'Et au devant du trône il y avoit une mer de verre femblable à du criftal : &amp; au milieu du trône &amp; à l\'entour du trône, quatre animaux pleins d\'yeux devant &amp; derriere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 7, 'Et le premier animal eftoit femblable à un lion : &amp; le fecond animal femblable à un veau : &amp; le troifiéme animal avoit la face comme un homme : &amp; le quatriéme animal eftoit femblable à une aigle volante.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 8, 'Et les quatre animaux avoyent chacun à part foi fix ailes à l\'entour, &amp; par dedans eftoyent pleins d\'yeux : &amp; ils n\'ont point de ceffe ni jour ni nuit, difant, Saint, faint, faint, le Seigneur Dieu tout-puiffant, Qui estoit, &amp; qui est, &amp; qui est a venir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 9, 'Or quand les animaux donnoyent gloire, &amp; honneur, &amp; action de graces, à celui qui eftoit affis fur le trône, à celui qui eft vivant és fiecles des fiecles :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 10, 'Les vingt-quatre Anciens fe profternoyent devant celui qui eftoit affis fur le trône, &amp; adoroyent le Vivant és fiecles des fiecles, &amp; jettoyent leurs couronnes devant le trône, difant,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 11, 'Seigneur, tu es digne de recevoir gloire, &amp; honneur, &amp; puiffance : car tu as creé toutes chofes, &amp; à ta volonté elles font, &amp; ont efté creées.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 1, 'PUIS je vis en la main droite de celui qui eftoit affis fur le trône, un livre efcrit dedans &amp; dehors, feellé de fept fceaux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 2, 'Je vis auffi un fort Ange, publiant à haute voix, Qui eft-ce qui eft digne d\'ouvrir le livre, &amp; d\'en délier les fceaux ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 3, 'Or nul ne pouvoit ni au ciel, ni en la terre, ni au deffous de la terre, ouvrir le livre, ni le regarder.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 4, 'Dont je pleurois tres-fort, parce que nul n\'eftoit trouvé digne d\'ouvrir le livre, ni le lire, ni de le regarder.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 5, 'Et l\'un des Anciens me dit, Ne pleure point : voici le Lion, qui eft de la tribu de Juda, la racine de David, a vaincu pour ouvrir le livre, &amp; délier les fept fceaux d\'icelui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 6, 'Et je regardai, &amp; voici au milieu du trône &amp; des quatre animaux, &amp; au milieu des Anciens, un Agneau qui fe tenoit là comme mis à mort, ayant fept cornes &amp; fept yeux, qui font les fept Efprits de Dieu envoyez en toute la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 7, 'Et il vint, &amp; prit le livre de la main droite de celui qui eftoit affis fur le trône.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 8, 'Et quand il eut pris le livre, les quatre animaux &amp; les vingt-quatre Anciens fe profternerent devant l\'Agneau, ayans chacun des harpes, &amp; des phioles d\'or pleines de parfums, qui font les prieres des Saints.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 9, 'Et ils chantoyent une chanfon nouvelle, difant, Tu es digne de prendre le livre, &amp; d\'en ouvrir les fceaux : car tu as efté mis à mort, &amp; nous as rachetez à Dieu par ton fang, de toute tribu, &amp; langue, &amp; peuple, &amp; nation :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 10, 'Et nous as faits Rois &amp; Sacrificateurs à noftre Dieu : &amp; nous regnerons fur la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 11, 'Puis je regardai, &amp; j\'oüis la voix de plufieurs Anges à l\'entour du trône &amp; des Anciens, &amp; leur nombres eftoit dix mille fois dix mille, &amp; mille fois mille :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 12, 'Et ils difoyent à haute voix, Digne eft l\'Agneau qui a efté mis à mort, de recevoir puiffance, &amp; richeffes, &amp; fapience, &amp; force, &amp; honneur, &amp; gloire, &amp; loüange.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 13, 'J\'oüis auffi toute creature qui eft au ciel, &amp; en la terre, &amp; au deffous de la terre, &amp; qui eft en la mer, voire toutes chofes qui font comprifes en eux, difant, A celui qui eft affis fur le trône, &amp; à l\'Agneau, foit loüange, &amp; honneur, &amp; gloire, &amp; force és fiecles des fiecles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 14, 'Et les quatre animaux difoyent, Amen : &amp; les quatre Anciens fe profternerent &amp; adorerent le Vivant és fiecles des fiecles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 1, 'ALORS je regardai quand l\'Agneau eut ouvert l\'un des fceaux, &amp; j\'oüis l\'un des quatre animaux, difant, comme fi c\'euft efté une voix de tonnerre, Vien &amp; voi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 2, 'Et je regardai, &amp; voici un cheval blanc : &amp; celui qui eftoit monté deffus avoit un arc, &amp; il lui fut donné une couronne : &amp; il fortit victorieux, &amp; afin qu\'il vainquift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 3, 'Et quand il eut ouvert le fecond fceau, j\'oüis le fecond animal, difant, Vien &amp; voi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 4, 'Et il forit un autre cheval qui eftoit roux : &amp; il fut donné à celui qui eftoit monté deffus , de pouvoir ofter la paix de la terre , afin qu\'on fe tuë l\'un l\'autre : &amp; il lui fut donné une grande efpée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 5, 'Et quand il eut ouvert le troifiéme fceau, j\'oüis le troifiéme animal, difant, Vien, &amp; voi. Et je regardai, &amp; voici un cheval noir : &amp; celui qui eftoit monté deffus, avoit une balance en fa main.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 6, 'Et j\'oüis une voix au milieu des quatre animaux, qui difoit, Le chenis de froment pour un denier, &amp; les trois chenis d\'orge pour un dernier : &amp; ne nui point au vin, ni à l\'huile.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 7, 'Et quand il eut ouvert le quatriéme fceau, j\'oüis la voix du quatriéme animal, difant, Vien, &amp; voi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 8, 'Et je regardai, &amp; voici un cheval fauve : &amp; celui qui eftoit monté deffus avoit nom la Mort, &amp; l\'Enfer fuivoit apres lui : &amp; il leur fut donné puiffance fur la quatriéme partie de la terre, pour tuër avec l\'efpée, &amp; par famine, &amp; par mortalité, &amp; par les beftes fauvages de la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 9, 'Et quand il eut ouvert le cinquiéme fceau, je vis fous l\'autel les ames de ceux qui avoyent efté tuez pour la parole de Dieu, &amp; pour le témoignage qu\'ils avoyent maintenu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 10, 'Et elles crioyent à haute voix, difant, Jufques à quand Seigneur, qui es faint &amp; veritable, ne juges-tu point &amp; ne venges-tu point noftre fang, de ceux qui habitent fur la terre ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 11, 'Et il leur fut donné à chacun des robbes blanches, &amp; il leur fut dit, qu\'ils fe repofaffent encore un peu de temps, jufques à ce que fuffent accomplis leurs compagnons de fervice, &amp; leurs freres qui doivent eftre mis à mort comme eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 12, 'Et je regardai quand il eut ouvert le fixiéme fceau, &amp; il fut fait un grand tremblement de terre : &amp; le foleil devint noir comme un fac fait de poil, &amp; la lune devint toute comme du fang.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 13, 'Et les eftoiles du ciel tomberent fur la terre, comme quand le figuier jette çà &amp; là fes figons, eftant fecoüé par un grand vent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 14, 'Et le ciel fe retira comme un livre lequel on roule : &amp; toute montagne, &amp; toutes ifles furent remuées de leurs lieux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 15, 'Et les rois de la terre, &amp; les princes, &amp; les riches, &amp; les capitaines, &amp; les puiffans, &amp; tout ferf, &amp; tout franc fe cacherent és cavernes, &amp; entre les roches des montagnes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 16, 'Et difoyent aux montagnes &amp; aux rochers, Tombez fur nous, &amp; nous cachez de devant la face de celui qui eft affis fur le trône, &amp; de devant l\'ire de l\'Agneau :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 17, 'Car la grande journée de fon ire eft venuë, &amp; qui eft-ce qui pourra fubfifter ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 1, 'OR apres ces chofes, je vis quatre Anges fe tenant fur les quatre coins de la terre, qui retenoyent les quatre vents de la terre, afin ni fur la mer, ni fur aucun arbre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 2, 'Puis je vis un autre Ange qui montoit du cofté d\'Orient, tenant le fceau du Dieu vivant, lequel cria à haute voix aux quatre Anges, aufquels il eftoit donné de nuire à la terre &amp; à la mer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 3, 'Difant, Ne nuifez point à la terre, ni à la mer, ni aux arbres jufques à que nous ayons marqué les ferviteurs de noftre Dieu en leurs fronts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 4, 'Et j\'oüis le nombre de ceux qui eftoyent marquez, il y avoit cent quarante quatre mille marquez de toutes les tribus des enfans d\'Ifraël.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 5, 'De la tribu de Juda, douze mille marquez. De la tribu de Ruben, douze mille marquez. De la tribu de Gad, douze mille marquez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 6, 'De la tribu d\'Afer, douze mille marquez. De la tribu de Nephthali, douze mille marquez. De la tribu de Manaffé, douze mille marquez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 7, 'De la tribu de Simeon, douze mille marquez. De la tribu de Levi, douze mille marquez. De la tribu d\'Iffacar, douze mille marquez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 8, 'De la tribu de Zabulon, douze mille marquez. De la tribu de Jofeph, douze mille marquez. De la tribu de Benjamin, douze mille marquez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 9, 'Apres ces chofes, je regardai, &amp; voici une grande multitude, laquelle nul ne fçauroit nombrer, de toutes nations, &amp; tribu, &amp; peuples &amp; langues, qui fe tenoyent devant le trône, &amp; en la prefence de l\'Agneau, veftus de longues robbes blanches, &amp; ayant des palmes en leurs mains.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 10, 'Et ils crioyent à haute voix, difant, Le falut eft de noftre Dieu qui eft affis fur le trône, &amp; de l\'Agneau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 11, 'Et tous les Anges fe tenoyent à l\'entour du trône, &amp; des Anciens, &amp; des quatre animaux, &amp; fe profternerent devant le trône fur leurs faces, &amp; adorerent Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 12, 'Difant, Amen : Loüange, &amp; gloire, &amp; fapience, &amp; action de graces, &amp; honneur, &amp; puiffance, &amp; force appartient à noftre Dieu és fiecles des fiecles. Amen.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 13, 'Alors l\'un des Anciens prit la parole, me difant, Ceux qui font veftus de longues robbes blanches, qui font-ils, &amp; d\'où font-ils venus ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 14, 'Et je lui dis, Seigneur, tu le fçais. Et il me dit, Ceux-ci font ceux qui font venus de la grande tribulation : &amp; ils ont lavé leurs longues robes, &amp; les ont blanchies au fang de l\'Agneau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 15, 'C\'eft pourquoi ils font devant le trône de Dieu, &amp; le fervent jour &amp; nuit en fon temple : &amp; celui qui eft affis fur le trône habitera avec eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 16, 'Ils n\'auront plus de faim, &amp; n\'auront plus de foif, &amp; le foleil ne frappera plus fur eux, ni chaleur quelconque.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 17, 'Car l\'Agneau, qui eft au milieu du trône, les paiftra &amp; les conduire aux vives fontaines des eaux : &amp; Dieu effuyera toute larme de leurs yeux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 1, 'ET quand il eut ouvert le feptiéme fceau, il fe fit filence au Ciel environ une demi heure.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 2, 'Et je vis les fept Anges qui affiftent devant Dieu, aufquels furent baillées fept trompettes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 3, 'Et un autre Ange vint, &amp; fe tint devant l\'autel, ayant un encenfoir d\'or, &amp; plufieurs parfums lui furent baillez, pour offrir avec les prieres de tous les Saints, fur l\'autel d\'or, qui eft devant le trône.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 4, 'Et la fumée des parfums avec les prieres des Saints, monta de la main de l\'Ange devant Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 5, 'Puis l\'Ange prit l\'encenfoir &amp; le remplit du feu de l\'autel &amp; le jetta en la terre : &amp; il fe fit des tonnerres, &amp; des voix, &amp; des efclairs, &amp; un tremblement de terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 6, 'Alors les fept Anges qui avoyent les fept trompettes, fe preparerent pour fonner des trompettes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 7, 'Et le premier Ange fonna de la trompette, &amp; il fe fit de la grefle &amp; du feu mélez de fang, &amp; ils furent jettez en la terre : &amp; la troifiéme partie des arbres fut brûlée, &amp; toute herbe verte fut brûlée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 8, 'Et le fecond Ange fonna de la trompette : &amp; comme une grande montagne ardente de feu fut jettée en la mer : &amp; la troifiéme partie de la mer devint fang.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 9, 'Et la troifiéme partie des creatures qui eftoyent en la mer, lefquelles avoyent vie, mourut : &amp; la troifiéme partie des navires perit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 10, 'Et le troifiéme Ange fonna de la trompette, &amp; il tomba du ciel une grande eftoile ardente comme un flambeau, &amp; elle cheut en la troifiéme partie des fleuves, &amp; és fontaines des eaux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 11, 'Et le nom de l\'eftoile eft dit, Abfynte : &amp; la troifiéme partie des eaux devint abfynte, &amp; plufieurs des hommes moururent par les eaux, à caufe qu\'elles eftoyent devenuës ameres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 12, 'Puis le quatriéme Ange fonna de la trompette : &amp; la troifiéme partie du Soleil fut frappée, &amp; la troifiéme partie de la Lune, &amp; la troifiéme partie des eftoiles, tellement que la troifiéme partie d\'iceux fut obfcurcie, &amp; la troifiéme partie du jour ne lui-foit point, ni femblablement de la nuit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 13, 'Alors je regardai, &amp; j\'oüis un Ange volant par le milieu du ciel, difant à haute voix, Malheur, mal-heur, mal-heur aux habitans de la terre, pour le fons des trompettes qui reftent des trois Anges qui doivent fonner de la trompette.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 1, 'ALORS le cinquiéme Ange fonna de la trompette : &amp; je vis une eftoile qui tomba du ciel en la terre : &amp; la clef du puits de l\'abyfme lui fut baillée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 2, 'Et il ouvrit le puits de l\'abyfme : &amp; une fumée monta du puits comme la fumée d\'une grande fournaife : &amp; le foleil &amp; l\'air furent obfcurcis de la fumée du puits.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 3, 'Et il fortit de la fumée du puits des fauterelles en la terre : &amp; il leur fut baillé une puiffance femblable à la puiffance qu\'ont les fcorpions de la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 4, 'Et il leur fut dit qu\'elles ne nuififfent point à l\'herbe de la terre, ni à nulle verdure, ni à nul arbre : mais feulement aux hommes qui n\'ont point la marque de Dieu en leurs fronts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 5, 'Et il leur fut permis non point de les tuër, mais de les tourmenter par cinq mois, &amp; leurs tourmens font femblables au tourment que donne le fcorpion quand il frappe l\'homme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 6, 'Et en ces jours-là les hommes chercheront la mort, &amp; ne la trouveront point : &amp; defireront de mourir, &amp; la mort s\'enfuïra d\'eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 7, 'Or la forme des sauterelles eftois femblable à des chevaux preparez à la bataille : &amp; fur leurs teftes il y avoit comme des couronnes femblables à de l\'or : &amp; leurs faces eftoyent, comme des faces d\'hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 8, 'Et elles avoyent les cheveux comme des cheveux de femmes, &amp; leurs dents eftoyent comme des dents de lions.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 9, 'Et elles avoyent des cuiraffes comme des cuiraffes de fer : &amp; le bruit de leurs aifles eftoit comme le bruit des chariots, quand plufieurs chevaux courent au combat.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 10, 'Et elles avoyent des queües femblables à des queües de fcorpions : &amp; avoyent des aiguillons en leurs queües : &amp; leur puiffance eftoit de nuire aux hommes par cinq mois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 11, 'Et elles avoyent fur elles pour Roi, l\'Ange de l\'abyfme qui a nom en Hebreu, Abaddon, &amp; duquel le nom en Grec eft Apollyon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 12, 'Un mal-heur eft paffé : &amp; voici venir encore deux mal-heurs apres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 13, 'Alors le fixiéme Ange fonna de la trompette, &amp; j\'oüis une voix procedante des quatre cornes de l\'autel d\'or, qui eft devant la face de Dieu :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 14, 'Laquelle dit au fixiéme Ange qui avoit la trompette, Délie les quatre Anges qui font liez fur le grand fleuve Euphrate.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 15, 'Les quatre Anges donc furent déliez, qui eftoyent prefts pour l\'heure, &amp; le jour, &amp; le mois, &amp; l\'an, afin de tuër la troifiéme partie des hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 16, 'Et le nombre de l\'armée à cheval eftoit de vingt mille fois dix mille ; car j\'entendis leur nombre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 17, 'Et je vis ainfi les chevaux en vifion : &amp; ceux qui eftoyent montez deffus, ayant des cuiraffes de feu, &amp; de hyacinte, &amp; de fouphre : &amp; les teftes de lions : &amp; il fortoit de leur gueule du feu, &amp; de la fumée, &amp; du fouphre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 18, 'Par ces trois chofes la troifiéme partie des hommes fut tuée, affavoir par le feu, &amp; par la fumée, &amp; par le fouphre qui fortoit de leur bouche.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 19, 'Car leur puiffance eftoit en leur bouche &amp; en leurs queües : car leurs queües eftoyent femblables à des ferpens, ayant des teftes par lefquelles elles nuifoyent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 20, 'Et le refte des hommes qui ne furent point tuez par ces playes, ne fe repentirent pas pourtant des œuvres de leurs mains, à ce qu\'ils n\'adoraffent point les diables, &amp; les idoles d\'or, &amp; d\'argent, &amp; de cuivre, &amp; de pierre, &amp; de bois, qui ne peuvent ni voir, ni oüir, ni cheminer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 21, 'Ils ne fe repentirent point auffi de leurs meurtres, ni de leurs empoifonnemens, ni de leurs paillardifes, ni de leurs larcins.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 1, 'ALORS je vis un autre fort Ange defcendant du ciel, environné d\'une nuée, en la tefte duquel eftoit l\'arc celefte : &amp; fa face eftoit comme le foleil, &amp; fes pieds comme des colonnes de feu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 2, 'Et il avoit en fa main un livret ouvert : &amp; mit fon pied droit fur la mer, &amp; le gauche fur la terre :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 3, 'Et cria à haute voix, comme quand un lion rugit : &amp; quand il eut crié, les fept tonnerres profererent leurs voix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 4, 'Et quand les fept tonnerres eurent proferé leurs voix, je m\'en allois les écrire : mais j\'oüis une voix du ciel me difant, Cachette les chofes que les fept tonnerres ont proferées, &amp; ne les efcris point.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 5, 'Et l\'Ange que j\'avois veu fe tenant fur la mer &amp; fur la terre, leva fa main vers le ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 6, 'Et jura par le Vivant és fiecles des fiecles, qui a creé le ciel &amp; les chofes qui y font, &amp; le terre &amp; les chofes qui y font, &amp; la mer &amp; les chofes qui y font, qu\'il n\'y auroit plus de temps :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 7, 'Mais qu\'és jours de la voix du feptiéme Ange, quand il commencera à fonner de la trompette, le myftere de Dieu fera confommé, comme il a declaré à fes ferviteurs Prophetes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 8, 'Et la voix que j\'avois oüie du ciel, parla derechef à moi, difant, Va, &amp; prens le livret ouvert, qui eft en la main de l\'Ange qui fe tient fur la mer &amp; fur la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 9, 'Je m\'en allai donc vers l\'Ange, &amp; lui dis, Baille-moi le livret, &amp; il me dit, Prens-le, &amp; le devore : &amp; il mettra ton ventre en amertume, mais en ta bouche il fera doux comme du miel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 10, 'Je pris donc le livret de la main de l\'Ange, &amp; le devorai : &amp; il eftoit doux en ma bouche comme du miel : mais quand je l\'eus devoré, mon ventre fut en amertume.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 11, 'Alors il me dit, à plufieurs peuples, &amp; nations, &amp; langues, &amp; rois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 1, 'ALORS il me fut baillé un rofeau femblable à une verge : &amp; il fe prefenta un Ange qui me dit, Leve-toi, &amp; mefure le Temple de Dieu, &amp; l\'autel, &amp; ceux qui adorent en icelui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 2, 'Mais jette hors le parvis qui eft hors du temple, &amp; ne le mefure point : car il eft donné aux Gentils ; ils fouleront aux pieds la fainte cité par quarante deux mois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 3, 'Mais je la donnerai à mes deux témoins qui prophetizeront par mille deux cens foixante jours, eftant veftus de facs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 4, 'Ceux-ci font les deux olives &amp; les deux chandeliers qui fe tiennent en la prefence du Seigneur de la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 5, 'Et fi quelqu\'un leur veut nuire ; le feu fort de leur bouche, &amp; devore leurs ennemis : car fi quelqu\'un leur veut nuire, il faut qu\'il foit ainfi tué.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 6, 'Ceux-ci ont puiffance de fermer le ciel, afin qu\'il ne pleuve point és jours de leur prophetie ; &amp; ont puiffance fur les eaux de les tourner en fang, &amp; de frapper la terre de toute playe, toutes &amp; quantes fois qu\'ils voudront.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 7, 'Et quand ils auront achevé leur témoignage, la befte qui monte de l\'abyfme fera la guerre contr\'eux, &amp; les vaincra, &amp; les tuëra.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 8, 'Et leurs corps morts feront gifans és places de la grande cité, qui eft appellée fpirituellement Sodome &amp; Egypte, là où auffi noftre Seigneur a efté crucifié.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 9, 'Et ceux des tribus, &amp; des peuples, &amp; des langues, &amp; des nations, verront leurs corps morts par trois jours &amp; demi, &amp; ne permettront point que leurs corps morts foyent mis és fepulcres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 10, 'Et les habitans de la terre s\'éjoüiront d\'eux, &amp; s\'égayeront, &amp; envoyeront des prefens les uns aux autres : parce que ces deux Prophetes auront tourmenté ceux qui habitent fur la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 11, 'Mais apres ces trois jours-là &amp; demi, l\'Efprit de vie venant de Dieu entra en eux, &amp; ils fe tinrent fur leurs pieds, &amp; une grande crainte faifit ceux qui les avoyent veus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 12, 'Apres cela ils oüirent une grande voix du ciel, leur difant, Montez ici : &amp; ils monterent au ciel en une nuée : &amp; leurs ennemis les virent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 13, 'Et à cette mefme heure-là il fe fit un grand tremblement de terre : &amp; la dixiéme partie de la Cité tomba, &amp; là furent tuez en ce tremblement de terre fept mille hommes en nombre : &amp; les autres furent efpouvantez, &amp; donnerent gloire au Dieu du ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 14, 'Le fecond mal-heur eft paffé : &amp; voici, le troifiéme mal-heur viendra bien-toft.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 15, 'Le feptiéme Ange donc fonna de la trompette, &amp; il fe fit de grandes voix au ciel, qui difoyent, Les royaumes du monde font reduits à noftre Seigneur, &amp; à fon Chrift, &amp; il regnera és fiecles des fiecles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 16, 'Alors les vingt-quatre Anciens qui font affis devant Dieu fur leurs fieges, fe profternerent fur leurs faces, &amp; adorerent Dieu ;');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 17, 'Difant, Nous te rendons graces, Seigneur Dieu tout-puiffant, qui es, &amp; qui estois, &amp; qui es a venir, de ce que tu as pris ta grande puiffance, &amp; as commencé ton regne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 18, 'Et les nations fe font courroucées, &amp; ton ire eft venuë, &amp; le temps des morts pour eftre jugez, &amp; pour donner le falaire à tes ferviteurs les Prophetes, &amp; aux Saints, &amp; à ceux qui craignent ton Nom, petits &amp; grands, &amp; pour deftruire ceux qui deftruifent la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 19, 'Alors le temple de Dieu fut ouvert au ciel, &amp; l\'Arche de fon alliance fut veüe au temple d\'icelui : &amp; il fe fit des efclairs, &amp; des voix, &amp; des tonnerres, &amp; un tremblement de terre, &amp; une grande grefle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 1, 'OR un grand figne apparut au ciel, affavoir une femme reveftuë du foleil, fous les pieds de laquelle eftoit la lune : &amp; il y avoit fur fa tefte une couronne de douze eftoiles :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 2, 'Laquelle eftoit enceinte, dont elle crioit fe trouvant en travail d\'enfant, &amp; fouffrant des tourmens pour enfanter.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 3, 'Il apparut auffi un autre figne au ciel, &amp; voici un grand dragon roux, ayant fept teftes &amp; dix cornes, &amp; fur fes teftes fept diademes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 4, 'Et fa queüe trainoit la troifiéme partie des eftoiles du ciel, lefquelles il jetta en la terre ; puis le dragon s\'arrefta devant la femme qui devoit enfanter afin que quand elle auroit enfanté, il devoraft fon enfant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 5, 'Or elle enfanta un fils mafle, qui doit gouverner toutes les nations avec une verge de fer, &amp; fon enfant fut ravi à Dieu, &amp; au trône d\'icelui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 6, 'Et la femme s\'enfuit en un defert, où elle a un lieu preparé de Dieu, afin qu\'on la nourriffe là mille deux cens foixante jours.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 7, 'Et il fe fit une bataille au ciel. Michel &amp; fes Anges combattoyent contre le dragon : &amp; le dragon combattoit &amp; fes anges.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 8, 'Mais ils ne furent pas les plus forts ; &amp; leur lieu ne fut plus trouvé au ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 9, 'Et fut jetté le grand dragon, le ferpent ancien, appellé le diable &amp; Satan, qui feduit tout le monde, il fut jetté en la terre : &amp; fes anges furent jettez avec lui.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 10, 'Alors j\'oüis une grande voix au ciel, difant, Maintenant eft advenu le falut, &amp; la force, &amp; le regne de noftre Dieu, &amp; la puiffance de fon Chrift : car l\'accufateur de nos freres eft dejetté, qui les accufoit devant noftre Dieu jour &amp; nuit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 11, 'Mais ils l\'on vaincu, à caufe du fang de l\'Agneau, &amp; à caufe de la parole de leur témoignage, &amp; n\'ont point aimé leurs vies jufques à les expofer à la mort.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 12, 'Partant éjoüiffez-vous, cieux, &amp; vous qui y habitez : Mal-heur fur vous, habitans de la terre &amp; de la mer : car le diable eft defcendu vers vous, eftant en grande fureur, fçachant qu\'il a peu de temps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 13, 'Quand donc le dragon euft veu qu\'il avoit efté jetté en la terre, il perfecuta la femme qui avoit enfanté l\'enfant mafle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 14, 'Mais deux ailes d\'une grande aigle furent baillées à la femme, afin qu\'elle s\'envolaft de devant le ferpent au defert en fon lieu, là où elle eft nourrie par un temps, &amp; par des temps, &amp; par la moitié d\'un temps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 15, 'Et le ferpent jetta de l\'eau comme un fleuve de fa gueule apres la femme, afin qu\'il la fift emporter par le fleuve.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 16, 'Mais la terre aida à la femme : car la terre ouvrit fa bouche, &amp; engloutit le fleuve que le dragon avoit jetté de fa gueule.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 17, 'Alors le dragon fut irrité contre la femme &amp; s\'en alla faire la guerre contre les autres qui font de la femence d\'icelle, qui gardent les commandemens de Dieu, &amp; qui ont le témoignage de Jefus Chrift.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 18, 'Et je me tins fur le fable de la mer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 1, 'ALORS je vis monter de la mer une befte qui avoit fept teftes &amp; dix cornes : &amp; fur fes cornes dix diademes : &amp; fur fes teftes un nom de blafpheme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 2, 'Et la befte que je vis eftoit femblable à un leopard, &amp; fes pieds eftoyent comme les pieds d\'un ours, &amp; fa gueule comme la gueule d\'un lion : &amp; le dragon lui donna fa puiffance, &amp; fon trône, &amp; un grand pouvoir.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 3, 'Et je vis l\'une de fes teftes comme frappée à mort, mais fa playe mortelle fut guerie : &amp; toute la terre s\'émerveillant alla apres la befte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 4, 'Et adorerent le dragon qui avoit donné pouvoir à la befte, &amp; adorerent la befte, difant, Qui eft femblable à la befte, &amp; qui pourra combattre contr\'elle ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 5, 'Et il lui fut baillé une bouche proferant de grandes chofes &amp; des blafphemes, &amp; il lui fut baillé puiffance d\'accomplir quarante deux mois.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 6, 'Et elle ouvrit fa bouche en blafphemes contre Dieu, à blafphemer fon Nom, &amp; fon tabernacle, &amp; ceux qui habitent au ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 7, 'Il lui fut auffi donné de faire la guerre contre les Saints, &amp; de les vaincre : il lui fut auffi baillé puiffance fur toute tribu, &amp; langue, &amp; nation.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 8, 'Tellement que tous ceux qui habitent fur la terre l\'adorerent, defquels les noms ne font point écrits au livre de vie de l\'Agneau occis dés la fondation du monde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 9, 'Si quelqu\'un a oreille, qu\'il oye.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 10, 'Si quelqu\'un mene en captivité, il fera mené en captivité : fi quelqu\'un tuë avec l\'efpée, il faut qu\'il foit tué avec l\'efpée : ici eft la patience &amp; la foi des Saints.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 11, 'Puis je vis une autre befte montant de la terre, laquelle avoit deux cornes femblables à celles de l\'Agneau : mais elle parloit comme le dragon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 12, 'Et exerçoit toute la puiffance de la premiere befte, en prefence d\'elle : &amp; faifoit que la terre &amp; fes habitans adoraffent la premiere befte, la playe mortelle de laquelle avoit efté guerie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 13, 'Et faifoit de grands fignes, voire jufques à faire defcendre du feu du ciel en terre, devant les hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 14, 'Et feduifoit les habitans de la terre, à caufe des fignes qu\'il lui eftoit donné de faire devant la befte : commandant aux habitans de la terre de faire une image à la befte qui ayant receu le coup mortel de l\'efpée, neantmoins eftoit vivante.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 15, 'Et il lui permis de donner ame à l\'image de la befte, à ce que mefmes l\'image de la befte parlaft, &amp; fift que tous ceux qui n\'auroyent point adoré l\'image de la befte, fuffent tuez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 16, 'Et faifoit que tous, petits &amp; grands, riches &amp; pauvres, francs &amp; ferfs, prenoyent une marque en leur main droite, ou en leurs fronts :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 17, 'Et qu\'aucun ne pouvoit acheter ou vendre, s\'il n\'avoit la marque ou le nom de la befte, ou le nombre de fon nom.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 18, 'Ici eft la fapience. Que celui qui a entendement, conte le nombre de la befte : car c\'eft un nombre d\'homme : &amp; fon nombre eft fix cens foixante-fix.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 1, 'PUIS je regardai, &amp; voici, l\'Agneau fe tenoit fur la montagne de Sion, &amp; avec lui cent quarante quatre mille qui avoyent le nom de fon Pere écrit en leurs fronts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 2, 'Et j\'oüis une voix du ciel comme le bruit de plufieurs eaux, &amp; comme le bruit d\'un grand tonnerre : &amp; oüis une voix de joüeurs de harpes joüans de leurs harpes :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 3, 'Qui chantoyent comme une chanfon nouvelle devant le trône, &amp; devant les quatre animaux &amp; les Anciens : &amp; nul ne pouvoit apprendre la chanfon, finon les cent quarante quatre mille, lefquels ont efté achetez d\'entre ceux de la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 4, 'Ceux-ci font ceux qui ne fe font point foüillez avec les femmes : car ils font vierges. Ceux-ci font ceux qui fuivent l\'Agneau quelque part qu\'il aille. Ceux-ci font ceux qui ont efté achetez d\'entre les hommes pour eftre premices à Dieu &amp; à l\'Agneau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 5, 'Et en leur bouche il n\'a efté trouvé aucune fraude : car ils font fans tache devant le trône de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 6, 'Puis je vis un autre Ange, voler par le milieu du ciel, ayant l\'Evangile eternel, afin qu\'il evangelizaft à ceux qui habitent fur la terre, &amp; à toute nation, &amp; tribu, &amp; langue, &amp; peuple.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 7, 'Difant à haute voix, Craignez Dieu, &amp; lui donnez gloire : car l\'heure de fon jugement eft venuë : &amp; adorez celui qui a fait le ciel &amp; la terre, la mer, &amp; les fontaines d\'eaux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 8, 'Et un autre Ange le fuivit, difant, Elle eft cheute, elle eft cheute Babylone, cette grande cité, parce quelle a abbreuvé toutes les nations du vin de l\'ire de fa paillardife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 9, 'Et un troifiéme Ange fuivit ceux-là, difant à haute voix, Si quelqu\'un adore la befte &amp; fon image, &amp; prend la marque en fon front, ou en fon main,');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 10, 'Celui-là auffi boira du vin de l\'ire de Dieu, voire du vin pur verfé en la coupe de fon ire : &amp; fera tourmenté de feu &amp; de fouphre devant les faints Anges, &amp; devant l\'Agneau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 11, 'Et la fumée de leur tourment montera aux fiecles des fiecles : &amp; n\'auront point de repos ni jour ni nuit, ceux qui adorent la befte &amp; fon image, &amp; quiconque prend la marque de fon nom.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 12, 'Ici eft la patience des Saints : ici font ceux qui gardent les commandemens de Dieu, &amp; la foi de Jefus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 13, 'Alors j\'oüis une voix du ciel me difant, Efcris, Bien-heureux font les morts qui dorefnavant meurent au Seigneur : Oüi pour certain, dit l\'Efprit : car ils fe repofent de leurs travaux, &amp; leurs œuvres les fuivent.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 14, 'Et je regardai, &amp; voici une nuée blanche, &amp; fur la nuée quelqu\'un eftant affis femblable à un homme, ayant fur fa tefte une couronne d\'or, &amp; en fa main une faucille trenchante.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 15, 'Et un autre Ange fortit du temple, criant à haute voix à celui qui eftoit affis fur la nuée, Jette ta faucille, &amp; moiffonne, car l\'heure de moiffonner t\'eft venuë : dautant que la moiffon de la terre eft meure.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 16, 'Alors celui qui eftoit affis fur la nuée, jetta fa faucille fur la terre, &amp; la terre fut moiffonnée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 17, 'Et un autre Ange fortit du temple qui eft au ciel, ayant lui auffi une faucille trenchante.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 18, 'Et un autre Ange fortit de l\'autel, ayant puiffance fur le feu, &amp; cria jettant un grand cri à celui qui avoit la faucille trenchante, difant, Jette ta faucille trenchante, &amp; vendange les grappes de la vigne de la terre : car fes raifins font meurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 19, 'Et l\'Ange jetta fa faucille trenchante en la terre, &amp; vendangea la vigne de la terre, &amp; jetta la vendange en la grande cuve de l\'ire de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 20, 'Et la cuve fut foulée hors de la Cité : &amp; de la cuve il fortit du fang jufques aux freins des chevaux par mille fix cens ftades.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 1, '');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 2, 'Je vis auffi comme une mer de verre meflée de feu : &amp; ceux qui avoyent obtenu la victoire de la befte, &amp; de fon image, &amp; de fa marque, &amp; du nombre de fon nom, fe tenant fur la mer qui eftoit comme de verre, ayant les harpes de Dieu :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 3, 'Lefquels chantoyent le cantique de Moyfe ferviteur de Dieu, &amp; le cantique de l\'Agneau, difant, Grandes &amp; merveilleufes font tes œuvres, Seigneur Dieu tout-puiffant : tes voyes font juftes &amp; veritables, Roi des Saints.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 4, 'Seigneur, qui eft-ce qui ne te craindra &amp; ne magnifiera ton Nom ? Car toi feul es Saint : dont toutes les nations viendront &amp; adoreront devant toi, veu que tes jugemens font tout à plein manifeftez.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 5, 'Et apres ces chofes je regardai, &amp; voici le temple du Tabernacle du témoignage fut ouvert au ciel.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 6, 'Et les fept Anges qui avoyent les fept playes fortirent du temple, eftant veftus de lin pur &amp; blanc, &amp; trouffez fur leurs poictrines avec des ceintures d\'or.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 7, 'Et l\'un des quatre animaux donna aux fept Anges fept phioles d\'or, pleines de l\'ire du Dieu vivant és fiecles des fiecles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 8, 'Et le temple fut rempli de la fumée procedante de la majefté de Dieu &amp; de fa puiffance : &amp; nul ne pouvoit entrer au temple jufques à ce que les fept playes des fept Anges fuffent accomplies.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 1, 'ALORS j\'oüis du temple une groffe voix, difant aux fept Anges, Allez, &amp; verfez fur la terre les phioles de l\'ire de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 2, 'Ainfi donc le premier Ange s\'en alla, &amp; verfa fa phiole fur la terre : &amp; il fe fit une playe mauvaife &amp; dangereufe fur les hommes qui avoyent la marque de la befte, &amp; fur ceux qui adoroyent fon image.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 3, 'Et le fecond Ange verfa fa phiole en la mer, laquelle devint fang comme d\'une charogne, &amp; toute ame vivante mourut en la mer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 4, 'Et le troifiéme Ange verfa fa phiole fur les fleuves, &amp; fur les fontaines des eaux, &amp; elles devinrent fang.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 5, 'Et j\'oüis l\'Ange des eaux, difant, Seigneur, tu es jufte qui es, &amp; qui estois, &amp; qui seras : dautant que tu as fait un tel jugement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 6, 'Parce qu\'ils ont refpandu le fang des Saints &amp; des Prophetes, tu leur as auffi baillé du fang à boire : car ils en font dignes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 7, 'Et j\'en oüis un autre du Sanctuaire, difant, Pour certain, Seigneur Dieu tout-puiffant, tes jugemens font veritables &amp; juftes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 8, 'Puis le quatriéme Ange verfa fa phiole fur le foleil, &amp; il lui fut donné de brufler les hommes par feu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 9, 'Et les hommes furent bruflez par de grandes chaleurs, &amp; blafphemerent le Nom de Dieu qui a puiffance fur ces playes : &amp; ne fe repentirent point pour lui donner gloire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 10, 'Apres cela le cinquiéme Ange verfa fa phiole fur le fiege de la befte, &amp; fon regne devint tenebreux : &amp; de douleur ils mafchoyent leurs langues.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 11, 'Et à caufe de leurs peines &amp; de leurs playes, il blafphemerent le Dieu du ciel, &amp; ne fe repentirent point de leurs actes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 12, 'Puis le fixiéme Ange verfa fa phiole fur le grand fleuve Euphrate, &amp; l\'eau d\'icelui tarit, afin que la voye des Rois de devers le foleil levant fuft preparée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 13, 'Et je vis fortir de la gueule du dragon, &amp; de la gueule de la befte, &amp; de la bouche du faux prophete, trois efprits immondes femblables à des grenoüilles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 14, 'Car ce font des efprits diaboliques faifans des fignes, qui s\'en vont vers les rois de la terre &amp; du monde univerfel, pour les affembler à la bataille de ce grand jour-là du Dieu tout-puiffant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 15, '(Voici, je viens comme le larron : Bien-heureux eft celui qui veille, &amp; qui garde fes veftemens, afin qu\'il ne chemine point nud, &amp; qu\'on ne voye point fa vergogne.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 16, 'Et il les affembla au lieu qui eft appellé en Hebreu Armageddon.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 17, 'Puis le feptiéme Ange verfa fa phiole en l\'air : &amp; il fortit une groffe voix du temple du ciel, de devers le trône, difant ; C\'eft fait.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 18, 'Alors il fe fit des efclairs, &amp; des voix, &amp; des tonnerres : &amp; il fe fit un grand tremblement de terre, voire un tel tremblement &amp; fi grand, qu\'il n\'en fut jamais de tel depuis que les hommes ont efté fur la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 19, 'Et la grande cité fut divifée en trois parties, &amp; les villes des nations tomberent : &amp; la grande Babylone vint en memoire devant Dieu, pour lui donner la coupe du vin de l\'indignation de fon ire.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 20, 'Et toute ifle s\'enfuït, &amp; les montagnes ne furent plus trouvées.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 21, 'Et il defcendit du ciel fur les hommes une grefle groffe comme du poids d\'un talent : &amp; les hommes blafphemerent Dieu à caufe de la playe de la grefle : car la playe qu\'elle fit fut fort grande.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 1, 'ALORS l\'un des fept Anges qui avoyent les fept phioles, vint, &amp; parla à moi, me difant, Vien, je te montrerai la condamnation de la grande paillarde, laquelle fe fied fur plufieurs eaux :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 2, 'Avec laquelle les rois de la terre ont paillardé, &amp; du vin de la paillardife de laquelle ont efté enyvrez les habitans de la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 3, 'Ainfi donc il me tranfporta en efprit en un defert : &amp; je vis une femme montée fur une befte de couleur d\'écarlatte, qui eftoit pleine de noms de blafpheme, ayant fept teftes &amp; dix cornes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 4, 'Et la femme eftoit accouftrée de pourpre &amp; d\'écarlatte, &amp; parée d\'or, &amp; de pierres precieufes, &amp; de perles : tenant en fa main une coupe d\'or, pleine d\'abominations de la foüillure de fa paillardife.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 5, 'Et en fon front il y avoit un nom efcrit, Myftere, la grande Babylone, la mere des paillardifes &amp; des abominations de la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 6, 'Et je vis la femme enyvrée du fang des Saints, &amp; du fang des Martyrs de Jefus : &amp; la voyant je m\'émerveillai d\'un grand esbahiffement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 7, 'Et l\'Ange me dit, Pourquoi t\'émerveilles-tu ? Je te dirai le myftere de la femme, &amp; de la befte qui la porte, laquelle a fept teftes &amp; dix cornes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 8, 'La befte que tu as veüe, a efté, &amp; n\'eft plus : &amp; doit monter de l\'abyfme, &amp; s\'en aller à perdition : &amp; les habitans de la terre, defquels les noms ne font point efcrits au livre de vie dés la fondation du monde, s\'émerveilleront voyant la befte laquelle eftoit, &amp; n\'eft plus, &amp; toutefois elle eft.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 9, 'Ici eft l\'entendement qui a de la fapience. Les fept teftes font fept montagnes fur lefquelles eft affife la femme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 10, 'Et ce font fept rois : les cinq font cheus : l\'un eft, &amp; l\'autre n\'eft point encore venu : &amp; quand il fera venu, il faut qu\'il demeure pour un peu de temps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 11, 'Et la befte qui eftoit, &amp; n\'eft plus, c\'eft auffi le huictiéme roi, &amp; eft des fept, &amp; s\'en va à perdition.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 12, 'Et les dix cornes que tu as veües, font dix rois, qui n\'ont point encore commencé à regner, mais ils prendront puiffance comme rois, en un mefme temps avec la befte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 13, 'Ceux-ci ont un mefme confeil, &amp; bailleront leur puiffance &amp; authorité à la befte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 14, 'Ceux-ci combattront contre l\'Agneau : mais l\'Agneau les vaincra : dautant qu\'il eft le Seigneur des feigneurs, &amp; le Roi des rois : &amp; ceux qui font avec lui, font appellez, &amp; éleus, &amp; fideles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 15, 'Puis il me dit, Les eaux que tu as veües, fur lefquelles eft affife la paillarde, ce font peuples, &amp; nations, &amp; langues.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 16, 'Et les dix cornes que tu as veües à la befte, font ceux qui hairont la paillarde, &amp; la rendront defolée &amp; nuë, &amp; mangeront la chair, &amp; la brufleront au feu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 17, 'Car Dieu a mis en leurs cœurs qu\'ils faffent ce qu\'il lui plaift, &amp; qu\'ils arreftent une mefme chofe, &amp; qu\'ils donnent leur royaume à la befte, jufques à ce que les paroles de Dieu foyent accomplies.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 18, 'Et la femme que tu as veüe, c\'eft la grande Cité, qui a font regne fur les rois de la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 1, 'ET apres ces chofes je vis un autre Ange defcendre du ciel, ayant une grande puiffance, &amp; la terre fut illuminée de fa gloire :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 2, 'Et il s\'écria de force à haute voix, difant, Elle eft cheute, elle eft cheute, la grande Babylone, &amp; eft devenuë l\'habitation des diables, &amp; le repaire de tout efprit immonde, &amp; le repaire de tout oifeau immonde &amp; execrable.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 3, 'Car toutes les nations ont beu du vin de l\'ire de fa paillardife : &amp; les rois de la terre ont paillardé avec elle : &amp; les marchands de la terre font devenus riches de l\'abondance de fes delices.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 4, 'Puis j\'oüis du ciel une autre voix difant, Sortez d\'elle, mon peuple, afin que vous ne foyez point participans de fes pechez, &amp; que vous ne receviez point de fes playes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 5, 'Car fes pechez fe font entrefuivis jufques au ciel, &amp; Dieu a eu fouvenance des iniquitez d\'icelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 6, 'Rendez-lui ainfi qu\'elle vous a fait, &amp; lui payez au double felon fes œuvres : en la coupe en laquelle elle vous a verfé, verfez-lui en au double.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 7, 'D\'autant qu\'elle s\'eft glorifiée, &amp; qu\'elle a efté en delices, d\'autant donnez lui de tourment &amp; de pleur : car elle dit en fon cœur, Je fieds reine, &amp; ne fuis point veuve, &amp; ne verrai point de deüil.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 8, 'C\'eft pourquoi en un feul jour viendront fes playes, la mort, &amp;le deüil, &amp; la famine, &amp; elle fera entierement bruflée au feu : car le Seigneur Dieu eft fort, qui la jugera.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 9, 'Et les rois de la terre, qui ont paillardé avec elle, &amp; ont vefcu en delices, quand ils verront la fumée de fon brûlement, la pleureront, &amp; meneront deüil fur elle, en fe battant la poictrine :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 10, 'Se tenant loin pour la crainte de fon tourment, difant, Las, las ! Babylone la grande cité, la cité fi puiffante, comment eft-ce que ta condamnation eft venuë en un inftant ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 11, 'Les marchands de la terre pleureront auffi, &amp; meneront deüil à caufe d\'elle, de ce que nul n\'achete plus de leur marchandife :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 12, 'Marchandife d\'or &amp; d\'argent, &amp; de pierres precieufes, &amp; de perles, &amp; de crefpe, &amp; de pourpre, &amp; de foye, &amp; d\'écarlatte, &amp; de toute forte de bois odorant, &amp; de tous vaiffeaux d\'yvoire, &amp; de tous vaiffeaux de bois tres-precieux, &amp; d\'airain, &amp; de fer, &amp; de marbre :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 13, 'Et canelle, &amp; fenteurs, &amp; oignemens, &amp; encens, &amp; vin, &amp; huile, &amp; fine fleur de farine, &amp; blé, &amp; jumens, &amp; brebis, &amp; chevaux, &amp; chariots, &amp; ferfs, &amp; ames d\'hommes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 14, '(Et les fruits du defir de ton ame font departis de toi : &amp; toutes chofes delicates &amp; excellentes te font peries : &amp; d\'orenavant tu ne trouveras plus ces chofes.)');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 15, 'Les marchands donc de ces chofes qui en font devenus riches, fe tiendront loin d\'elle, pour la crainte de fon tourment, pleurant &amp; menant deüil.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 16, 'Et difant, Las, las ! La grande cité, qui eftoit reveftuë de crefpe, &amp; de pourpre, &amp; d\'écarlatte, &amp; eftoit parée d\'or, &amp; ornée de pierres precieufes &amp; de perles : comment en un inftant ont efté mifes à neant tant de richeffes !');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 17, 'Tout patron de navire auffi, &amp; toute la troupe de ceux qui hantent és navires, &amp; tous nautonniers, &amp; quiconque trafique fur la mer fe tiendront loin :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 18, 'Et voyant la fumée de fon bruflement, s\'efcrieront en difant, Quelle cité eftoit femblable à cette grande cité ?');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 19, 'Et jetteront de la poudre fur leurs teftes, &amp; crieront en pleurant &amp; menant deüil, &amp; difant, Las, las ! La grande cité, en laquelle tous ceux qui avoyent des navires en la mer, eftoyent devenus riches de fon opulence : comment a-t\'elle efté defolée en un inftant !');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 20, 'O ciel ! Ejoüi-toi d\'elle, &amp; vous faints Apoftres &amp; Prophetes : car Dieu a pris vengeance d\'elle pour l\'amour de vous.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 21, 'Puis un fort Ange prit une pierre comme une grande meule, &amp; la jetta en la mer, difant, Ainfi fera jettée d\'impetuofité Babylone cette grande cité, &amp; ne fera plus trouvée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 22, 'Et la voix des joüeurs de harpe, &amp; des muficiens, &amp; des joüeurs de haut-bois, &amp; des fonneurs de trompette, ne fera plus oüie en toi : &amp; tout ouvrier, de quelque meftier que ce foit, ne fera plus trouvé en moi : &amp; le bruit de la meule ne fera plus oüi en toi.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 23, 'Et la lumiere de la chandelle ne luira plus en toi : &amp; la voix de l\'efpoux &amp; de l\'efpoufe ne fera plus oüie en toi : parce que tes marchands eftoyent des princes en la terre, parce que par tes empoifonnemens toutes les nations ont efté feduites.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 24, 'Et en elle a efté trouvé le fang des Prophetes &amp; des Saints, &amp; de tous ceux qui ont efté mis à mort fur la terre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 1, 'OR apres ces chofes j\'oüis une voix d\'une grande multitude au ciel, difant, Hallelu-jah : Salut, &amp; gloire, &amp; honneur, &amp; puiffance appartient au Seigneur noftre Dieu :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 2, 'Car fes jugemens font veritables &amp; juftes, parce qu\'il a fait juftice de la grande paillarde qui a corrompu la terre par fa paillardife, &amp; a vengé le fang de fes ferviteurs de la main d\'icelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 3, 'Et derechef ils dirent, Hallelu-jah : &amp; la fumée d\'icelle en monte à jamais.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 4, 'Et les vingt-quatre Anciens &amp; les quatre animaux fe jetterent fur leurs faces ; &amp; adorerent Dieu feant fur le trône, difans, Amen, Hallelu-jah.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 5, 'Et une voix partit du trône, difant, Loüez noftre Dieu, tous fes ferviteurs, &amp; vous qui le craignez, tant petits que grands.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 6, 'Puis j\'oüis comme une voix d\'une grande affemblée, &amp; comme le fon de plufieurs eaux, &amp; comme la voix de forts tonnerres, difant, Hallelu-jah : car le Seigneur noftre Dieu Tout-puiffant eft entré en fon regne.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 7, 'Ejoüiffons nous, &amp; nous égayons, &amp; lui donnons gloire : car les nopces de l\'Agneau font venuës, &amp; fa femme s\'eft parée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 8, 'Et il lui a efté donné d\'eftre veftuë de crefpe pur &amp; luifant : car le crefpe font les juftifications des Saints.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 9, 'Alors il me dit, Efcris, Bien-heureux font ceux qui font appellez au banquet des nopces de l\'Agneau. Il me dit auffi, Ces paroles de Dieu font veritables.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 10, 'Alors je me jettai à fes pieds pour l\'adorer. Et il me dit, Garde-toi de le faire : je fuis ton compagnon de fervice, &amp; de tes freres qui ont le témoignage de Jefus. Adore Dieu : Car le témoignage de Jefus eft l\'Efprit de prophetie.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 11, 'Puis je vis le ciel ouvert, &amp; voici un cheval blanc, &amp; celui qui eftoit monté deffus eftoit appellé Fidele &amp; Veritable, lequel juge &amp; bataille juftement.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 12, 'Et fes yeux eftoyent comme une flamme de feu : &amp; il y avoit fur fa tefte plufieurs diademes, ayant un nom efcrit que nul n\'a connu finon lui-mefme.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 13, 'Il eftoit veftu d\'une robbe teinte en fang, &amp; fon nom s\'appelle, La Parole de Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 14, 'Et les armées qui font au ciel le fuivoyent fur des chevaux blancs, veftus de crefpe blanc &amp; net.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 15, 'Et de fa bouche il fortoit une efpée trenchante, afin qu\'il en frappe les nations : car il les gouvernera avec une verge de fer, &amp; c\'eft lui qui foulera la cuve du vin du courroux &amp; de l\'ire de Dieu Tout-puiffant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 16, 'Et en fon veftement &amp; en fa cuiffe il avoit en efcrit ce nom, Le Roi des rois, et le Seigneur des seigneurs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 17, 'Puis je vis un Ange fe tenant dans le foleil, qui cria à haute voix, difant à tous les oifeaux qui voloyent par le milieu du ciel, Venez &amp; vous affemblez au banquet du grand Dieu :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 18, 'Afin que vous mangiez la chair des rois, &amp; la chair des capitaines, &amp; la chair des forts, &amp; la chair des chevaux &amp; de ceux qui font montez deffus, &amp; la chair de tous, francs &amp; ferfs, petits &amp; grands.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 19, 'Alors je vis la befte, &amp; les rois de la terre, &amp; leurs armées affemblées pour faire la guerre contre celui qui eftoit monté fur le cheval, &amp; contre fon armée.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 20, 'Mais la befte fut prife, &amp; avec elle le faux prophete qui avoit fait les fignes devant elle, par lefquels il avoit feduit ceux qui avoyent la marque de la befte, &amp; qui avoyent adoré fon image : ces deux furent jettez tout vifs dans l\'eftang de feu ardent de fouphre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 21, 'Et le refte fut tué par l\'efpée qui fortoit de la bouche de celui qui eftoit monté fur le cheval, &amp; tous les oifeaux furent foulez de leurs chairs.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 1, 'APRES cela je vis un Ange defcendant du ciel, qui avoit la clef de l\'abyfme, &amp; une grande chaîne en fa main :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 2, 'Lequel faifit le dragon, affavoir le vieux ferpent, qui eft le diable &amp; Satan, &amp; le lia pour mille ans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 3, 'Et le jetta en l\'abyfme, &amp; l\'enferma, &amp; feella fur lui : afin qu\'il ne feduife plus les nations, jufques à ce que les mille ans foyent accomplis : &amp; puis apres il faut qu\'il foit délié pour un peu de temps.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 4, 'Alors je vis des trônes, fur lefquels s\'affirent des gens, &amp; le jugement leur fut donné : &amp; je vis les ames des decapitez pour le témoignage de Jefus, &amp; pour la Parole de Dieu, &amp; qui n\'avoyent point adoré la befte, ni fon image : &amp; qui vivroyent &amp; regneroyent avec Chrift mille ans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 5, 'Mais le refte des morts ne doit point reffufciter, jufques à ce que les mille ans foyent accomplis : c\'eft là la refurrection premiere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 6, 'Bien-heureux &amp; faint eft celui qui a part en la premiere refurrection : la feconde mort n\'a point de puiffance fur eux, mais ils feront Sacrificateurs de Dieu &amp; de Chrift, &amp; regneront avec lui mille ans.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 7, 'Et quand les mille ans feront accomplis, Satan fera délié de fa prifon :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 8, 'Et fortira pour feduire les nations qui font fur les quatre coins de la terre, Gog &amp; Magog, pour les affembler en bataille ; dont le nombre eft comme le fable de la mer.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 9, 'Et ils font montez fur la largeur de la terre, &amp; ont environné le camp des Saints, &amp; la Cité bien-aimée : mais il defcendit du feu du ciel de par Dieu, lequel les devora.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 10, 'Et le diable qui les feduifoit, fut jetté en l\'eftang de feu &amp; de fouphre, là où eft la befte &amp; le faux prophete : &amp; ils feront tourmentez jour &amp; nuit és fiecles des fiecles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 11, 'Puis je vis un grand trône blanc, &amp; quelqu\'un affis deffus, de devant lequel s\'enfuit la terre &amp; le ciel : &amp; il ne fe trouva point de lieu pour eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 12, 'Je vis auffi les morts grands &amp; petits fe tenans devant Dieu : &amp; les livres furent ouverts : &amp; un autre livre fut ouvert, affavoir le livre de vie : &amp; les morts furent jugez par les chofes qui eftoyent écrites és livres felon leurs œuvres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 13, 'Et la mer rendit les morts qui eftoyent en elle, &amp; la mort &amp; l\'enfer rendirent les morts qui eftoyent en eux : &amp; ils furent jugez chacun felon leurs œuvres.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 14, 'Et l\'enfer &amp; la mort furent jettez en l\'eftang de feu : c\'eft là la mort feconde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 15, 'Et quiconque ne fut point trouvé efcrit au livre de vie, fut jetté en l\'eftang de feu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 1, 'PUIS je vis un nouveau ciel &amp; une nouvelle terre : car le premier ciel &amp; la premiere terre s\'en eftoit allée, &amp; la mer n\'eftoit plus.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 2, 'Et moi Jean vis la fainte Cité, la Jerufalem nouvelle, defcendant du ciel, de par Dieu, parée comme une efpoufe ornée pour fon mari.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 3, 'Et j\'oüis une grande voix du ciel, difant, Voici le tabernacle de Dieu avec les hommes, &amp; il habitera avec eux, &amp; ils feront fon peuple, &amp; Dieu lui-mefme fera leur Dieu avec eux.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 4, 'Et Dieu effuyera toute larme de leurs yeux, &amp; la mort ne fera plus : &amp; il n\'y aura plus ni deüil, ni cri, ni travail : car les premieres chofes font paffées.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 5, 'Et celui qui eftoit affis fur le trône, dit, Voici, je fais toutes chofes nouvelles, Puis il me dit, Efcris : car ces paroles font veritables &amp; certaines.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 6, 'Il me dit auffi, C\'eft fait : Je fuis Alpha &amp; Omega, le commencement &amp; la fin : à celui qui aura foif je lui donnerai de la fontaine de l\'eau de vie fans qu\'elle lui coufte rien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 7, 'Celui qui vaincra, heritera toutes chofes : &amp; je lui fera Dieu, &amp; il me fera fils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 8, 'Mais aux timides, &amp; aux incredules, &amp; aux execrables, &amp; aux meurtriers, &amp; aux paillards, &amp; aux empoifonneurs, &amp; aux idolatres, &amp; à tous menteurs, leur part fera en l\'eftang ardent de feu &amp; de fouphre, qui eft la mort feconde.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 9, 'Alors l\'un des fept Anges qui avoyent eu les fept phioles pleines des fept dernieres playes, vint à moi, &amp; parla à moi, difant, Vien, &amp; je te montrerai l\'Efpoufe qui eft la femme de l\'Agneau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 10, 'Et il me tranfporta en Efprit en une grande &amp; haute montagne : &amp; me montra la grande Cité, la faincte Jerufalem, defcendant du ciel de devers Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 11, 'Ayant la gloire de Dieu : &amp; fa lumiere eftoit femblable à une pierre tres-precieufe, comme à une pierre de jafpe tirant fur le criftal.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 12, 'Et elle avoit une grande &amp; haute muraille, avec douze portes, &amp; és portes douze Anges : &amp; des noms efcris fur elles, qui font les noms des douze tribus des enfans d\'Ifraël.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 13, 'Du cofté d\'Orient, trois portes : du cofté d\'Aquilon, trois portes : du cofté de Midi, trois portes : &amp; du cofté d\'Occident, trois portes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 14, 'Et la muraille de la Cité avoit douze fondemens : &amp; en eux eftoyent efcrits les noms des douze Apoftres de l\'Agneau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 15, 'Et celui qui parloit à moi avoit un rofeau d\'or pour mefurer la Cité, &amp; fes portes, &amp; fa muraille.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 16, 'Et la Cité eftoit fituée &amp; baftie en quarré, &amp; fa longueur eftoit auffi grande que fa largeur : &amp; il mefura la Cité avec le rofeau d\'or, jufques à douze mille ftades : &amp; la longueur, &amp; la largeur, &amp; la hauteur d\'icelle eftoyent égales.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 17, 'Puis il mefura la muraille de cent quarante-quatre coudées, de la mefure du perfonnage, affavoir de l\'Ange.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 18, 'Et le baftiment de la muraille eftoit de jafpe : mais la Cité eftoit d\'or pur, femblable à du verre tres-clair.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 19, 'Et les fondemens de la muraille de la Cité eftoyent ornez de toute pierre precieufe. Le premier fondement eftoit de jafpe : le fecond, de fapphir : le troifiéme, de chalcedoine : le quatriéme, d\'efmeraude :');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 20, 'Le cinquiéme, de fardonix : le fixiéme, de fardoine : le feptiéme, de chryfolithe : le huictiéme, de beryl : le neuviéme, de topaze : le dixiéme, de chryfophrafe : l\'onziéme, de hyacinthe : le douziéme, d\'amethyfte.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 21, 'Et les douze portes eftoyent douze perles : chacune des portes eftoit d\'une perle. Et la ruë de la Cité eftoit d\'or pur, comme du verre tres-luifant.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 22, 'Et je ne vis point de temple en elle : car le Seigneur Dieu tout-puiffant eft fon temple, &amp; l\'Agneau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 23, 'Et la Cité n\'a pas befoin du foleil ni de la lune, afin qu\'ils luifent en elle : car la clarté de Dieu l\'a illuminée, &amp; l\'Agneau eft fa chandelle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 24, 'Et les nations qui auront efté fauvées, chemineront en fa lumiere : &amp; les rois de la terre apporteront leur gloire &amp; leur honneur en elle.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 25, 'Et fes portes ne feront point fermées de jour : car il n\'y aura point là de nuit.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 26, 'Et on apportera en elle la gloire &amp; l\'honneur des Gentils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 27, 'Il n\'entrera en elle aucune chofe foüillée, ou qui commette abomination &amp; fauffeté : mais feulement ceux qui font efcrits au livre de vie de l\'Agneau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 1, 'PUIS il me montra un fleuve pur d\'eau vive, refplendiffant comme du cryftal, procedant du trône de Dieu &amp; de l\'Agneau.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 2, 'Et au milieu de la place d\'icelle, &amp; des deux coftez du fleuve, l\'arbre de vie, portant douze fruits, rendant fon fruit chaque mois : &amp; les feüilles de l\'arbre font pour la fanté des Gentils.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 3, 'Et toute chofe maudite ne fera plus, mais le trône de Dieu &amp; de l\'Agneau fera en elle : &amp; fes ferviteurs le ferviront.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 4, 'Et ils verront fa face : &amp; fon Nom fera en leurs fronts.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 5, 'Et il n\'y aura plus là de nuit, &amp; ils n\'ont que faire de lumiere de chandelle, ni de lumiere du foleil : car le Seigneur Dieu les illumine, &amp; ils regneront és fiecles des fiecles.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 6, 'Puis il me dit, Ces paroles font certaines &amp; veritables : &amp; le Seigneur le Dieu des faints Prophetes a envoyé fon Ange, pour montrer à fes ferviteurs les chofes qui doivent eftre faites bien-toft.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 7, 'Voici, je viens bien-toft : bienheureux eft celui qui garde les paroles de la prophetie de ce livre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 8, 'Et moi Jean fuis celui qui ai oüi &amp; veu ces chofes. Et apres que j\'eus oüi &amp; veu, je me jettai pour me profterner devant les pieds de l\'Ange qui me montroit ces chofes.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 9, 'Mais il me dit, Garde-toi de le faire : car je fuis ton compagnon de fervice, &amp; de tes freres les Prophetes, &amp; de ceux qui gardent les paroles de ce livre : adore Dieu.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 10, 'Il me dit auffi, Ne cachette point les paroles de la prophetie de ce livre, car le temps eft prés.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 11, 'Que celui qui eft injufte, foit injufte encore : &amp; que celui qui eft fale, fe fale faliffe encore : &amp; que celui qui eft jufte, foit juftifié encore : &amp; que celui qui eft faint, foit fanctifié encore.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 12, 'Or voici, je viens bien-toft : &amp; mon falaire eft avec moi, pour rendre à chacun felon fon œuvre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 13, 'Je fuis Alpha &amp; Omega, le premier &amp; le dernier, le commencement &amp; la fin.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 14, 'Bienheureux font ceux qui font fes commandemens, afin qu\'ils ayent droit en l\'arbre de vie, &amp; qu\'ils entrent par les portes en la Cité.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 15, 'Mais dehors feront les chiens, &amp; les empoifonneurs, &amp; les paillards, &amp; les meurtriers, &amp; les idolatres, &amp; quiconque aime &amp; commet fauffeté.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 16, 'Moi Jefus ai envoyé mon Ange pour vous témoigner ces chofes par les Eglifes : je fuis la racine &amp; la pofterité de David, l\'eftoile refplendiffante &amp; matiniere.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 17, 'Et l\'Efprit &amp; l\'Efpoufe difent, Vien : Et que celui qui l\'oit, dife, Vien : Et que celui qui a foif, vienne : Et que celui qui veut, prenne de l\'eau de vie fans qu\'elle lui coufte rien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 18, 'Or je protefte à chacun qui oit les paroles de la prophetie de ce livre, que fi quelqu\'un ajoufte à ces chofes, Dieu ajouftera fur lui les playes efcrites en ce livre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 19, 'Et fi quelqu\'un ofte des paroles du livre de cette prophetie, Dieu oftera fa part du livre de vie, &amp; de la fainte Cité, &amp; des chofes qui font efcrites en ce livre.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 20, 'Celui qui rend témoignage de ces chofes, dit, Pour certain je viens bien-toft : Amen. Voire, Seigneur Jefus, vien.');
INSERT INTO `FreGeneve1669_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 21, 'La grace de noftre Seigneur Jefus Chrift foit avec vous tous. Amen. ');
