INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 1, 'ܟܬܒܐ ܕܝܠܝܕܘܬܗ ܕܝܫܘܥ ܡܫܝܚܐ ܒܪܗ ܕܕܘܝܕ ܒܪܗ ܕܐܒܪܗܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 2, 'ܐܒܪܗܡ ܐܘܠܕ ܠܐܝܤܚܩ ܐܝܤܚܩ ܐܘܠܕ ܠܝܥܩܘܒ ܝܥܩܘܒ ܐܘܠܕ ܠܝܗܘܕܐ ܘܠܐܚܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 3, 'ܝܗܘܕܐ ܐܘܠܕ ܠܦܪܨ ܘܠܙܪܚ ܡܢ ܬܡܪ ܦܪܨ ܐܘܠܕ ܠܚܨܪܘܢ ܚܨܪܘܢ ܐܘܠܕ ܠܐܪܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 4, 'ܐܪܡ ܐܘܠܕ ܠܥܡܝܢܕܒ ܥܡܝܢܕܒ ܐܘܠܕ ܠܢܚܫܘܢ ܢܚܫܘܢ ܐܘܠܕ ܠܤܠܡܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 5, 'ܤܠܡܘܢ ܐܘܠܕ ܠܒܥܙ ܡܢ ܪܚܒ ܒܥܙ ܐܘܠܕ ܠܥܘܒܝܕ ܡܢ ܪܥܘܬ ܥܘܒܝܕ ܐܘܠܕ ܠܐܝܫܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 6, 'ܐܝܫܝ ܐܘܠܕ ܠܕܘܝܕ ܡܠܟܐ ܕܘܝܕ ܐܘܠܕ ܠܫܠܝܡܘܢ ܡܢ ܐܢܬܬܗ ܕܐܘܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 7, 'ܫܠܝܡܘܢ ܐܘܠܕ ܠܪܚܒܥܡ ܪܚܒܥܡ ܐܘܠܕ ܠܐܒܝܐ ܐܒܝܐ ܐܘܠܕ ܠܐܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 8, 'ܐܤܐ ܐܘܠܕ ܠܝܗܘܫܦܛ ܝܗܘܫܦܛ ܐܘܠܕ ܠܝܘܪܡ ܝܘܪܡ ܐܘܠܕ ܠܥܘܙܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 9, 'ܥܘܙܝܐ ܐܘܠܕ ܠܝܘܬܡ ܝܘܬܡ ܐܘܠܕ ܠܐܚܙ ܐܚܙ ܐܘܠܕ ܠܚܙܩܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 10, 'ܚܙܩܝܐ ܐܘܠܕ ܠܡܢܫܐ ܡܢܫܐ ܐܘܠܕ ܠܐܡܘܢ ܐܡܘܢ ܐܘܠܕ ܠܝܘܫܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 11, 'ܝܘܫܝܐ ܐܘܠܕ ܠܝܘܟܢܝܐ ܘܠܐܚܘܗܝ ܒܓܠܘܬܐ ܕܒܒܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 12, 'ܡܢ ܒܬܪ ܓܠܘܬܐ ܕܝܢ ܕܒܒܠ ܝܘܟܢܝܐ ܐܘܠܕ ܠܫܠܬܐܝܠ ܫܠܬܐܝܠ ܐܘܠܕ ܠܙܘܪܒܒܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 13, 'ܙܘܪܒܒܠ ܐܘܠܕ ܠܐܒܝܘܕ ܐܒܝܘܕ ܐܘܠܕ ܠܐܠܝܩܝܡ ܐܠܝܩܝܡ ܐܘܠܕ ܠܥܙܘܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 14, 'ܥܙܘܪ ܐܘܠܕ ܠܙܕܘܩ ܙܕܘܩ ܐܘܠܕ ܠܐܟܝܢ ܐܟܝܢ ܐܘܠܕ ܠܐܠܝܘܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 15, 'ܐܠܝܘܕ ܐܘܠܕ ܠܐܠܝܥܙܪ ܐܠܝܥܙܪ ܐܘܠܕ ܠܡܬܢ ܡܬܢ ܐܘܠܕ ܠܝܥܩܘܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 16, 'ܝܥܩܘܒ ܐܘܠܕ ܠܝܘܤܦ ܓܒܪܗ ܕܡܪܝܡ ܕܡܢܗ ܐܬܝܠܕ ܝܫܘܥ ܕܡܬܩܪܐ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 17, 'ܟܠܗܝܢ ܗܟܝܠ ܫܪܒܬܐ ܡܢ ܐܒܪܗܡ ܥܕܡܐ ܠܕܘܝܕ ܫܪܒܬܐ ܐܪܒܥܤܪܐ ܘܡܢ ܕܘܝܕ ܥܕܡܐ ܠܓܠܘܬܐ ܕܒܒܠ ܫܪܒܬܐ ܐܪܒܥܤܪܐ ܘܡܢ ܓܠܘܬܐ ܕܒܒܠ ܥܕܡܐ ܠܡܫܝܚܐ ܫܪܒܬܐ ܐܪܒܥܤܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 18, 'ܝܠܕܗ ܕܝܢ ܕܝܫܘܥ ܡܫܝܚܐ ܗܟܢܐ ܗܘܐ ܟܕ ܡܟܝܪܐ ܗܘܬ ܡܪܝܡ ܐܡܗ ܠܝܘܤܦ ܥܕܠܐ ܢܫܬܘܬܦܘܢ ܐܫܬܟܚܬ ܒܛܢܐ ܡܢ ܪܘܚܐ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 19, 'ܝܘܤܦ ܕܝܢ ܒܥܠܗ ܟܐܢܐ ܗܘܐ ܘܠܐ ܨܒܐ ܕܢܦܪܤܝܗ ܘܐܬܪܥܝ ܗܘܐ ܕܡܛܫܝܐܝܬ ܢܫܪܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 20, 'ܟܕ ܗܠܝܢ ܕܝܢ ܐܬܪܥܝ ܐܬܚܙܝ ܠܗ ܡܠܐܟܐ ܕܡܪܝܐ ܒܚܠܡܐ ܘܐܡܪ ܠܗ ܝܘܤܦ ܒܪܗ ܕܕܘܝܕ ܠܐ ܬܕܚܠ ܠܡܤܒ ܠܡܪܝܡ ܐܢܬܬܟ ܗܘ ܓܝܪ ܕܐܬܝܠܕ ܒܗ ܡܢ ܪܘܚܐ ܗܘ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 21, 'ܬܐܠܕ ܕܝܢ ܒܪܐ ܘܬܩܪܐ ܫܡܗ ܝܫܘܥ ܗܘ ܓܝܪ ܢܚܝܘܗܝ ܠܥܡܗ ܡܢ ܚܛܗܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 22, 'ܗܕܐ ܕܝܢ ܟܠܗ ܕܗܘܬ ܕܢܬܡܠܐ ܡܕܡ ܕܐܬܐܡܪ ܡܢ ܡܪܝܐ ܒܝܕ ܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 23, 'ܕܗܐ ܒܬܘܠܬܐ ܬܒܛܢ ܘܬܐܠܕ ܒܪܐ ܘܢܩܪܘܢ ܫܡܗ ܥܡܢܘܐܝܠ ܕܡܬܬܪܓܡ ܥܡܢ ܐܠܗܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 24, 'ܟܕ ܩܡ ܕܝܢ ܝܘܤܦ ܡܢ ܫܢܬܗ ܥܒܕ ܐܝܟܢܐ ܕܦܩܕ ܠܗ ܡܠܐܟܗ ܕܡܪܝܐ ܘܕܒܪܗ ܠܐܢܬܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 25, 'ܘܠܐ ܚܟܡܗ ܥܕܡܐ ܕܝܠܕܬܗ ܠܒܪܗ ܒܘܟܪܐ ܘܩܪܬ ܫܡܗ ܝܫܘܥ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 1, 'ܟܕ ܕܝܢ ܐܬܝܠܕ ܝܫܘܥ ܒܒܝܬ ܠܚܡ ܕܝܗܘܕܐ ܒܝܘܡܝ ܗܪܘܕܤ ܡܠܟܐ ܐܬܘ ܡܓܘܫܐ ܡܢ ܡܕܢܚܐ ܠܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 2, 'ܘܐܡܪܝܢ ܐܝܟܘ ܡܠܟܐ ܕܝܗܘܕܝܐ ܕܐܬܝܠܕ ܚܙܝܢ ܓܝܪ ܟܘܟܒܗ ܒܡܕܢܚܐ ܘܐܬܝܢ ܠܡܤܓܕ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 3, 'ܫܡܥ ܕܝܢ ܗܪܘܕܤ ܡܠܟܐ ܘܐܬܬܙܝܥ ܘܟܠܗ ܐܘܪܫܠܡ ܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 4, 'ܘܟܢܫ ܟܠܗܘܢ ܪܒܝ ܟܗܢܐ ܘܤܦܪܐ ܕܥܡܐ ܘܡܫܐܠ ܗܘܐ ܠܗܘܢ ܕܐܝܟܐ ܡܬܝܠܕ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 5, 'ܗܢܘܢ ܕܝܢ ܐܡܪܘ ܒܒܝܬ ܠܚܡ ܕܝܗܘܕܐ ܗܟܢܐ ܓܝܪ ܟܬܝܒ ܒܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 6, 'ܐܦ ܐܢܬܝ ܒܝܬ ܠܚܡ ܕܝܗܘܕܐ ܠܐ ܗܘܝܬܝ ܒܨܝܪܐ ܒܡܠܟܐ ܕܝܗܘܕܐ ܡܢܟܝ ܓܝܪ ܢܦܘܩ ܡܠܟܐ ܕܗܘ ܢܪܥܝܘܗܝ ܠܥܡܝ ܐܝܤܪܐܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 7, 'ܗܝܕܝܢ ܗܪܘܕܤ ܡܛܫܝܐܝܬ ܩܪܐ ܠܡܓܘܫܐ ܘܝܠܦ ܡܢܗܘܢ ܒܐܝܢܐ ܙܒܢܐ ܐܬܚܙܝ ܠܗܘܢ ܟܘܟܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 8, 'ܘܫܕܪ ܐܢܘܢ ܠܒܝܬ ܠܚܡ ܘܐܡܪ ܠܗܘܢ ܙܠܘ ܥܩܒܘ ܥܠ ܛܠܝܐ ܚܦܝܛܐܝܬ ܘܡܐ ܕܐܫܟܚܬܘܢܝܗܝ ܬܘ ܚܘܐܘܢܝ ܕܐܦ ܐܢܐ ܐܙܠ ܐܤܓܘܕ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 9, 'ܗܢܘܢ ܕܝܢ ܟܕ ܫܡܥܘ ܡܢ ܡܠܟܐ ܐܙܠܘ ܘܗܐ ܟܘܟܒܐ ܗܘ ܕܚܙܘ ܒܡܕܢܚܐ ܐܙܠ ܗܘܐ ܩܕܡܝܗܘܢ ܥܕܡܐ ܕܐܬܐ ܩܡ ܠܥܠ ܡܢ ܐܝܟܐ ܕܐܝܬܘܗܝ ܛܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 10, 'ܟܕ ܕܝܢ ܚܙܐܘܗܝ ܠܟܘܟܒܐ ܚܕܝܘ ܚܕܘܬܐ ܪܒܬܐ ܕܛܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 11, 'ܘܥܠܘ ܠܒܝܬܐ ܘܚܙܐܘܗܝ ܠܛܠܝܐ ܥܡ ܡܪܝܡ ܐܡܗ ܘܢܦܠܘ ܤܓܕܘ ܠܗ ܘܦܬܚܘ ܤܝܡܬܗܘܢ ܘܩܪܒܘ ܠܗ ܩܘܪܒܢܐ ܕܗܒܐ ܘܡܘܪܐ ܘܠܒܘܢܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 12, 'ܘܐܬܚܙܝ ܠܗܘܢ ܒܚܠܡܐ ܕܠܐ ܢܗܦܟܘܢ ܠܘܬ ܗܪܘܕܤ ܘܒܐܘܪܚܐ ܐܚܪܬܐ ܐܙܠܘ ܠܐܬܪܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 13, 'ܟܕ ܕܝܢ ܐܙܠܘ ܐܬܚܙܝ ܡܠܐܟܐ ܕܡܪܝܐ ܒܚܠܡܐ ܠܝܘܤܦ ܘܐܡܪ ܠܗ ܩܘܡ ܕܒܪ ܠܛܠܝܐ ܘܠܐܡܗ ܘܥܪܘܩ ܠܡܨܪܝܢ ܘܬܡܢ ܗܘܝ ܥܕܡܐ ܕܐܡܪ ܐܢܐ ܠܟ ܥܬܝܕ ܗܘ ܓܝܪ ܗܪܘܕܤ ܠܡܒܥܝܗ ܠܛܠܝܐ ܐܝܟ ܕܢܘܒܕܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 14, 'ܝܘܤܦ ܕܝܢ ܩܡ ܫܩܠܗ ܠܛܠܝܐ ܘܠܐܡܗ ܒܠܠܝܐ ܘܥܪܩ ܠܡܨܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 15, 'ܘܗܘܐ ܬܡܢ ܥܕܡܐ ܠܡܘܬܗ ܕܗܪܘܕܤ ܕܢܬܡܠܐ ܡܕܡ ܕܐܬܐܡܪ ܡܢ ܡܪܝܐ ܒܢܒܝܐ ܕܐܡܪ ܕܡܢ ܡܨܪܝܢ ܩܪܝܬ ܠܒܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 16, 'ܗܝܕܝܢ ܗܪܘܕܤ ܟܕ ܚܙܐ ܕܐܬܒܙܚ ܡܢ ܡܓܘܫܐ ܐܬܚܡܬ ܛܒ ܘܫܕܪ ܩܛܠ ܛܠܝܐ ܟܠܗܘܢ ܕܒܝܬ ܠܚܡ ܘܕܟܠܗܘܢ ܬܚܘܡܝܗ ܡܢ ܒܪ ܬܪܬܝܢ ܫܢܝܢ ܘܠܬܚܬ ܐܝܟ ܙܒܢܐ ܕܥܩܒ ܡܢ ܡܓܘܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 17, 'ܗܝܕܝܢ ܐܬܡܠܝ ܡܕܡ ܕܐܬܐܡܪ ܒܝܕ ܐܪܡܝܐ ܢܒܝܐ ܕܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 18, 'ܩܠܐ ܐܫܬܡܥ ܒܪܡܬܐ ܒܟܝܐ ܘܐܠܝܐ ܤܓܝܐܐ ܪܚܝܠ ܒܟܝܐ ܥܠ ܒܢܝܗ ܘܠܐ ܨܒܝܐ ܠܡܬܒܝܐܘ ܡܛܠ ܕܠܐ ܐܝܬܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 19, 'ܟܕ ܡܝܬ ܕܝܢ ܗܪܘܕܤ ܡܠܟܐ ܐܬܚܙܝ ܡܠܐܟܐ ܕܡܪܝܐ ܒܚܠܡܐ ܠܝܘܤܦ ܒܡܨܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 20, 'ܘܐܡܪ ܠܗ ܩܘܡ ܕܒܪ ܠܛܠܝܐ ܘܠܐܡܗ ܘܙܠ ܠܐܪܥܐ ܕܐܝܤܪܐܝܠ ܡܝܬܘ ܠܗܘܢ ܓܝܪ ܗܢܘܢ ܕܒܥܝܢ ܗܘܘ ܢܦܫܗ ܕܛܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 21, 'ܘܝܘܤܦ ܩܡ ܕܒܪ ܠܛܠܝܐ ܘܠܐܡܗ ܘܐܬܐ ܠܐܪܥܐ ܕܐܝܤܪܐܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 22, 'ܟܕ ܕܝܢ ܫܡܥ ܕܐܪܟܠܐܘܤ ܗܘܐ ܡܠܟܐ ܒܝܗܘܕ ܚܠܦ ܗܪܘܕܤ ܐܒܘܗܝ ܕܚܠ ܕܢܐܙܠ ܠܬܡܢ ܘܐܬܚܙܝ ܠܗ ܒܚܠܡܐ ܕܢܐܙܠ ܠܐܬܪܐ ܕܓܠܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 23, 'ܘܐܬܐ ܥܡܪ ܒܡܕܝܢܬܐ ܕܡܬܩܪܝܐ ܢܨܪܬ ܐܝܟ ܕܢܬܡܠܐ ܡܕܡ ܕܐܬܐܡܪ ܒܢܒܝܐ ܕܢܨܪܝܐ ܢܬܩܪܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 1, 'ܒܗܘܢ ܕܝܢ ܒܝܘܡܬܐ ܗܢܘܢ ܐܬܐ ܝܘܚܢܢ ܡܥܡܕܢܐ ܘܡܟܪܙ ܗܘܐ ܒܚܘܪܒܐ ܕܝܗܘܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 2, 'ܘܐܡܪ ܬܘܒܘ ܩܪܒܬ ܠܗ ܡܠܟܘܬܐ ܕܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 3, 'ܗܢܘ ܓܝܪ ܗܘ ܕܐܡܝܪ ܒܝܕ ܐܫܥܝܐ ܢܒܝܐ ܩܠܐ ܕܩܪܐ ܒܚܘܪܒܐ ܛܝܒܘ ܐܘܪܚܗ ܕܡܪܝܐ ܘܐܫܘܘ ܠܫܒܝܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 4, 'ܗܘ ܕܝܢ ܝܘܚܢܢ ܐܝܬܘܗܝ ܗܘܐ ܠܒܘܫܗ ܕܤܥܪܐ ܕܓܡܠܐ ܘܐܤܪ ܚܨܐ ܕܡܫܟܐ ܥܠ ܚܨܘܗܝ ܘܡܐܟܘܠܬܗ ܩܡܨܐ ܘܕܒܫܐ ܕܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 5, 'ܗܝܕܝܢ ܢܦܩܐ ܗܘܬ ܠܘܬܗ ܐܘܪܫܠܡ ܘܟܠܗ ܝܗܘܕ ܘܟܠܗ ܐܬܪܐ ܕܚܕܪܝ ܝܘܪܕܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 6, 'ܘܥܡܕܝܢ ܗܘܘ ܡܢܗ ܒܝܘܪܕܢܢ ܢܗܪܐ ܟܕ ܡܘܕܝܢ ܒܚܛܗܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 7, 'ܟܕ ܚܙܐ ܕܝܢ ܤܓܝܐܐ ܡܢ ܦܪܝܫܐ ܘܡܢ ܙܕܘܩܝܐ ܕܐܬܝܢ ܠܡܥܡܕ ܐܡܪ ܠܗܘܢ ܝܠܕܐ ܕܐܟܕܢܐ ܡܢܘ ܚܘܝܟܘܢ ܠܡܥܪܩ ܡܢ ܪܘܓܙܐ ܕܐܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 8, 'ܥܒܕܘ ܗܟܝܠ ܦܐܪܐ ܕܫܘܝܢ ܠܬܝܒܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 9, 'ܘܠܐ ܬܤܒܪܘܢ ܘܬܐܡܪܘܢ ܒܢܦܫܟܘܢ ܕܐܒܐ ܐܝܬ ܠܢ ܐܒܪܗܡ ܐܡܪ ܐܢܐ ܠܟܘܢ ܓܝܪ ܕܡܫܟܚ ܐܠܗܐ ܡܢ ܗܠܝܢ ܟܐܦܐ ܠܡܩܡܘ ܒܢܝܐ ܠܐܒܪܗܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 10, 'ܗܐ ܕܝܢ ܢܪܓܐ ܤܝܡ ܥܠ ܥܩܪܐ ܕܐܝܠܢܐ ܟܠ ܐܝܠܢܐ ܗܟܝܠ ܕܦܐܪܐ ܛܒܐ ܠܐ ܥܒܕ ܡܬܦܤܩ ܘܢܦܠ ܒܢܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 11, 'ܐܢܐ ܡܥܡܕ ܐܢܐ ܠܟܘܢ ܒܡܝܐ ܠܬܝܒܘܬܐ ܗܘ ܕܝܢ ܕܒܬܪܝ ܐܬܐ ܚܤܝܢ ܗܘ ܡܢܝ ܗܘ ܕܠܐ ܫܘܐ ܐܢܐ ܡܤܢܘܗܝ ܠܡܫܩܠ ܗܘ ܡܥܡܕ ܠܟܘܢ ܒܪܘܚܐ ܕܩܘܕܫܐ ܘܒܢܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 12, 'ܗܘ ܕܪܦܫܐ ܒܐܝܕܗ ܘܡܕܟܐ ܐܕܪܘܗܝ ܘܚܛܐ ܟܢܫ ܠܐܘܨܪܘܗܝ ܘܬܒܢܐ ܡܘܩܕ ܒܢܘܪܐ ܕܠܐ ܕܥܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 13, 'ܗܝܕܝܢ ܐܬܐ ܝܫܘܥ ܡܢ ܓܠܝܠܐ ܠܝܘܪܕܢܢ ܠܘܬ ܝܘܚܢܢ ܕܢܥܡܕ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 14, 'ܗܘ ܕܝܢ ܝܘܚܢܢ ܟܠܐ ܗܘܐ ܠܗ ܘܐܡܪ ܐܢܐ ܤܢܝܩ ܐܢܐ ܕܡܢܟ ܐܬܥܡܕ ܘܐܢܬ ܠܘܬܝ ܐܬܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 15, 'ܗܘ ܕܝܢ ܝܫܘܥ ܥܢܐ ܘܐܡܪ ܠܗ ܫܒܘܩ ܗܫܐ ܗܟܢܐ ܓܝܪ ܝܐܐ ܠܢ ܕܢܡܠܐ ܟܠܗ ܟܐܢܘܬܐ ܘܗܝܕܝܢ ܫܒܩܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 16, 'ܟܕ ܥܡܕ ܕܝܢ ܝܫܘܥ ܡܚܕܐ ܤܠܩ ܡܢ ܡܝܐ ܘܐܬܦܬܚܘ ܠܗ ܫܡܝܐ ܘܚܙܐ ܪܘܚܐ ܕܐܠܗܐ ܕܢܚܬܐ ܐܝܟ ܝܘܢܐ ܘܐܬܬ ܥܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 17, 'ܘܗܐ ܩܠܐ ܡܢ ܫܡܝܐ ܕܐܡܪ ܗܢܘ ܒܪܝ ܚܒܝܒܐ ܕܒܗ ܐܨܛܒܝܬ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 1, 'ܗܝܕܝܢ ܝܫܘܥ ܐܬܕܒܪ ܡܢ ܪܘܚܐ ܕܩܘܕܫܐ ܠܡܕܒܪܐ ܕܢܬܢܤܐ ܡܢ ܐܟܠܩܪܨܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 2, 'ܘܨܡ ܐܪܒܥܝܢ ܝܘܡܝܢ ܘܐܪܒܥܝܢ ܠܝܠܘܢ ܐܚܪܝܬ ܕܝܢ ܟܦܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 3, 'ܘܩܪܒ ܗܘ ܕܡܢܤܐ ܘܐܡܪ ܠܗ ܐܢ ܒܪܗ ܐܢܬ ܕܐܠܗܐ ܐܡܪ ܕܗܠܝܢ ܟܐܦܐ ܢܗܘܝܢ ܠܚܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 4, 'ܗܘ ܕܝܢ ܥܢܐ ܘܐܡܪ ܟܬܝܒ ܕܠܐ ܗܘܐ ܒܠܚܡܐ ܒܠܚܘܕ ܚܝܐ ܒܪܢܫܐ ܐܠܐ ܒܟܠ ܡܠܐ ܕܢܦܩܐ ܡܢ ܦܘܡܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 5, 'ܗܝܕܝܢ ܕܒܪܗ ܐܟܠܩܪܨܐ ܠܡܕܝܢܬ ܩܘܕܫܐ ܘܐܩܝܡܗ ܥܠ ܟܢܦܐ ܕܗܝܟܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 6, 'ܘܐܡܪ ܠܗ ܐܢ ܒܪܗ ܐܢܬ ܕܐܠܗܐ ܫܕܝ ܢܦܫܟ ܠܬܚܬ ܟܬܝܒ ܓܝܪ ܕܠܡܠܐܟܘܗܝ ܢܦܩܕ ܥܠܝܟ ܘܥܠ ܐܝܕܝܗܘܢ ܢܫܩܠܘܢܟ ܕܠܐ ܬܬܩܠ ܒܟܐܦܐ ܪܓܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 7, 'ܐܡܪ ܠܗ ܝܫܘܥ ܬܘܒ ܟܬܝܒ ܕܠܐ ܬܢܤܐ ܠܡܪܝܐ ܐܠܗܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 8, 'ܬܘܒ ܕܒܪܗ ܐܟܠܩܪܨܐ ܠܛܘܪܐ ܕܛܒ ܪܡ ܘܚܘܝܗ ܟܠܗܝܢ ܡܠܟܘܬܐ ܕܥܠܡܐ ܘܫܘܒܚܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 9, 'ܘܐܡܪ ܠܗ ܗܠܝܢ ܟܠܗܝܢ ܠܟ ܐܬܠ ܐܢ ܬܦܠ ܬܤܓܘܕ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 10, 'ܗܝܕܝܢ ܐܡܪ ܠܗ ܝܫܘܥ ܙܠ ܠܟ ܤܛܢܐ ܟܬܝܒ ܓܝܪ ܕܠܡܪܝܐ ܐܠܗܟ ܬܤܓܘܕ ܘܠܗ ܒܠܚܘܕܘܗܝ ܬܦܠܘܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 11, 'ܗܝܕܝܢ ܫܒܩܗ ܐܟܠܩܪܨܐ ܘܗܐ ܡܠܐܟܐ ܩܪܒܘ ܘܡܫܡܫܝܢ ܗܘܘ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 12, 'ܟܕ ܫܡܥ ܕܝܢ ܝܫܘܥ ܕܝܘܚܢܢ ܐܫܬܠܡ ܫܢܝ ܠܗ ܠܓܠܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 13, 'ܘܫܒܩܗ ܠܢܨܪܬ ܘܐܬܐ ܥܡܪ ܒܟܦܪܢܚܘܡ ܥܠ ܝܕ ܝܡܐ ܒܬܚܘܡܐ ܕܙܒܘܠܘܢ ܘܕܢܦܬܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 14, 'ܕܢܬܡܠܐ ܡܕܡ ܕܐܬܐܡܪ ܒܝܕ ܐܫܥܝܐ ܢܒܝܐ ܕܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 15, 'ܐܪܥܐ ܕܙܒܘܠܘܢ ܐܪܥܐ ܕܢܦܬܠܝ ܐܘܪܚܐ ܕܝܡܐ ܥܒܪܘܗܝ ܕܝܘܪܕܢܢ ܓܠܝܠܐ ܕܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 16, 'ܥܡܐ ܕܝܬܒ ܒܚܫܘܟܐ ܢܘܗܪܐ ܪܒܐ ܚܙܐ ܘܐܝܠܝܢ ܕܝܬܒܝܢ ܒܐܬܪܐ ܘܒܛܠܠܐ ܕܡܘܬܐ ܢܘܗܪܐ ܕܢܚ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 17, 'ܡܢ ܗܝܕܝܢ ܫܪܝ ܝܫܘܥ ܠܡܟܪܙܘ ܘܠܡܐܡܪ ܬܘܒܘ ܩܪܒܬ ܠܗ ܓܝܪ ܡܠܟܘܬܐ ܕܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 18, 'ܘܟܕ ܡܗܠܟ ܥܠ ܝܕ ܝܡܐ ܕܓܠܝܠܐ ܚܙܐ ܬܪܝܢ ܐܚܝܢ ܫܡܥܘܢ ܕܐܬܩܪܝ ܟܐܦܐ ܘܐܢܕܪܐܘܤ ܐܚܘܗܝ ܕܪܡܝܢ ܡܨܝܕܬܐ ܒܝܡܐ ܐܝܬܝܗܘܢ ܗܘܘ ܓܝܪ ܨܝܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 19, 'ܘܐܡܪ ܠܗܘܢ ܝܫܘܥ ܬܘ ܒܬܪܝ ܘܐܥܒܕܟܘܢ ܕܬܗܘܘܢ ܨܝܕܐ ܕܒܢܝ ܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 20, 'ܗܢܘܢ ܕܝܢ ܡܚܕܐ ܫܒܩܘ ܡܨܝܕܬܗܘܢ ܘܐܙܠܘ ܒܬܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 21, 'ܘܟܕ ܥܒܪ ܡܢ ܬܡܢ ܚܙܐ ܐܚܪܢܐ ܐܚܐ ܬܪܝܢ ܝܥܩܘܒ ܒܪ ܙܒܕܝ ܘܝܘܚܢܢ ܐܚܘܗܝ ܒܐܠܦܐ ܥܡ ܙܒܕܝ ܐܒܘܗܘܢ ܕܡܬܩܢܝܢ ܡܨܝܕܬܗܘܢ ܘܩܪܐ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 22, 'ܗܢܘܢ ܕܝܢ ܡܚܕܐ ܫܒܩܘ ܠܐܠܦܐ ܘܠܐܒܘܗܘܢ ܘܐܙܠܘ ܒܬܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 23, 'ܘܡܬܟܪܟ ܗܘܐ ܝܫܘܥ ܒܟܠܗ ܓܠܝܠܐ ܘܡܠܦ ܗܘܐ ܒܟܢܘܫܬܗܘܢ ܘܡܟܪܙ ܤܒܪܬܐ ܕܡܠܟܘܬܐ ܘܡܐܤܐ ܟܠ ܟܐܒ ܘܟܘܪܗܢ ܒܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 24, 'ܘܐܫܬܡܥ ܛܒܗ ܒܟܠܗ ܤܘܪܝܐ ܘܩܪܒܘ ܠܗ ܟܠܗܘܢ ܐܝܠܝܢ ܕܒܝܫ ܒܝܫ ܥܒܝܕܝܢ ܒܟܘܪܗܢܐ ܡܫܚܠܦܐ ܘܐܝܠܝܢ ܕܐܠܝܨܝܢ ܒܬܫܢܝܩܐ ܘܕܝܘܢܐ ܘܕܒܪ ܐܓܪܐ ܘܡܫܪܝܐ ܘܐܤܝ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 25, 'ܘܐܙܠܘ ܒܬܪܗ ܟܢܫܐ ܤܓܝܐܐ ܡܢ ܓܠܝܠܐ ܘܡܢ ܥܤܪܬ ܡܕܝܢܬܐ ܘܡܢ ܐܘܪܫܠܡ ܘܡܢ ܝܗܘܕ ܘܡܢ ܥܒܪܐ ܕܝܘܪܕܢܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 1, 'ܟܕ ܚܙܐ ܕܝܢ ܝܫܘܥ ܠܟܢܫܐ ܤܠܩ ܠܛܘܪܐ ܘܟܕ ܝܬܒ ܩܪܒܘ ܠܘܬܗ ܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 2, 'ܘܦܬܚ ܦܘܡܗ ܘܡܠܦ ܗܘܐ ܠܗܘܢ ܘܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 3, 'ܛܘܒܝܗܘܢ ܠܡܤܟܢܐ ܒܪܘܚ ܕܕܝܠܗܘܢ ܗܝ ܡܠܟܘܬܐ ܕܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 4, 'ܛܘܒܝܗܘܢ ܠܐܒܝܠܐ ܕܗܢܘܢ ܢܬܒܝܐܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 5, 'ܛܘܒܝܗܘܢ ܠܡܟܝܟܐ ܕܗܢܘܢ ܢܐܪܬܘܢ ܠܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 6, 'ܛܘܒܝܗܘܢ ܠܐܝܠܝܢ ܕܟܦܢܝܢ ܘܨܗܝܢ ܠܟܐܢܘܬܐ ܕܗܢܘܢ ܢܤܒܥܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 7, 'ܛܘܒܝܗܘܢ ܠܡܪܚܡܢܐ ܕܥܠܝܗܘܢ ܢܗܘܘܢ ܪܚܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 8, 'ܛܘܒܝܗܘܢ ܠܐܝܠܝܢ ܕܕܟܝܢ ܒܠܒܗܘܢ ܕܗܢܘܢ ܢܚܙܘܢ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 9, 'ܛܘܒܝܗܘܢ ܠܥܒܕܝ ܫܠܡܐ ܕܒܢܘܗܝ ܕܐܠܗܐ ܢܬܩܪܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 10, 'ܛܘܒܝܗܘܢ ܠܐܝܠܝܢ ܕܐܬܪܕܦܘ ܡܛܠ ܟܐܢܘܬܐ ܕܕܝܠܗܘܢ ܗܝ ܡܠܟܘܬܐ ܕܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 11, 'ܛܘܒܝܟܘܢ ܐܡܬܝ ܕܡܚܤܕܝܢ ܠܟܘܢ ܘܪܕܦܝܢ ܠܟܘܢ ܘܐܡܪܝܢ ܥܠܝܟܘܢ ܟܠ ܡܠܐ ܒܝܫܐ ܡܛܠܬܝ ܒܕܓܠܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 12, 'ܗܝܕܝܢ ܚܕܘ ܘܪܘܙܘ ܕܐܓܪܟܘܢ ܤܓܝ ܒܫܡܝܐ ܗܟܢܐ ܓܝܪ ܪܕܦܘ ܠܢܒܝܐ ܕܡܢ ܩܕܡܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 13, 'ܐܢܬܘܢ ܐܢܘܢ ܡܠܚܗ ܕܐܪܥܐ ܐܢܗܘ ܕܝܢ ܕܡܠܚܐ ܬܦܟܗ ܒܡܢܐ ܬܬܡܠܚ ܠܡܕܡ ܠܐ ܐܙܠܐ ܐܠܐ ܕܬܫܬܕܐ ܠܒܪ ܘܬܬܕܝܫ ܡܢ ܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 14, 'ܐܢܬܘܢ ܐܢܘܢ ܢܘܗܪܗ ܕܥܠܡܐ ܠܐ ܡܫܟܚܐ ܕܬܛܫܐ ܡܕܝܢܬܐ ܕܥܠ ܛܘܪܐ ܒܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 15, 'ܘܠܐ ܡܢܗܪܝܢ ܫܪܓܐ ܘܤܝܡܝܢ ܠܗ ܬܚܝܬ ܤܐܬܐ ܐܠܐ ܥܠ ܡܢܪܬܐ ܘܡܢܗܪ ܠܟܠ ܐܝܠܝܢ ܕܒܒܝܬܐ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 16, 'ܗܟܢܐ ܢܢܗܪ ܢܘܗܪܟܘܢ ܩܕܡ ܒܢܝܢܫܐ ܕܢܚܙܘܢ ܥܒܕܝܟܘܢ ܛܒܐ ܘܢܫܒܚܘܢ ܠܐܒܘܟܘܢ ܕܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 17, 'ܠܐ ܬܤܒܪܘܢ ܕܐܬܝܬ ܕܐܫܪܐ ܢܡܘܤܐ ܐܘ ܢܒܝܐ ܠܐ ܐܬܝܬ ܕܐܫܪܐ ܐܠܐ ܕܐܡܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 18, 'ܐܡܝܢ ܓܝܪ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܥܕܡܐ ܕܢܥܒܪܘܢ ܫܡܝܐ ܘܐܪܥܐ ܝܘܕ ܚܕܐ ܐܘ ܚܕ ܤܪܛܐ ܠܐ ܢܥܒܪ ܡܢ ܢܡܘܤܐ ܥܕܡܐ ܕܟܠ ܢܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 19, 'ܟܠ ܡܢ ܗܟܝܠ ܕܢܫܪܐ ܚܕ ܡܢ ܦܘܩܕܢܐ ܗܠܝܢ ܙܥܘܪܐ ܘܢܠܦ ܗܟܢܐ ܠܒܢܝܢܫܐ ܒܨܝܪܐ ܢܬܩܪܐ ܒܡܠܟܘܬܐ ܕܫܡܝܐ ܟܠ ܕܝܢ ܕܢܥܒܕ ܘܢܠܦ ܗܢܐ ܪܒܐ ܢܬܩܪܐ ܒܡܠܟܘܬܐ ܕܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 20, 'ܐܡܪ ܐܢܐ ܠܟܘܢ ܓܝܪ ܕܐܠܐ ܬܐܬܪ ܟܐܢܘܬܟܘܢ ܝܬܝܪ ܡܢ ܕܤܦܪܐ ܘܦܪܝܫܐ ܠܐ ܬܥܠܘܢ ܠܡܠܟܘܬܐ ܕܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 21, 'ܫܡܥܬܘܢ ܕܐܬܐܡܪ ܠܩܕܡܝܐ ܠܐ ܬܩܛܘܠ ܘܟܠ ܕܢܩܛܘܠ ܡܚܝܒ ܗܘ ܠܕܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 22, 'ܐܢܐ ܕܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܟܠ ܡܢ ܕܢܪܓܙ ܥܠ ܐܚܘܗܝ ܐܝܩܐ ܡܚܝܒ ܗܘ ܠܕܝܢܐ ܘܟܠ ܕܢܐܡܪ ܠܐܚܘܗܝ ܪܩܐ ܡܚܝܒ ܗܘ ܠܟܢܘܫܬܐ ܘܡܢ ܕܢܐܡܪ ܠܠܐ ܡܚܝܒ ܗܘ ܠܓܗܢܐ ܕܢܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 23, 'ܐܢ ܗܘ ܗܟܝܠ ܕܡܩܪܒ ܐܢܬ ܩܘܪܒܢܟ ܥܠ ܡܕܒܚܐ ܘܬܡܢ ܬܬܕܟܪ ܕܐܚܝܕ ܥܠܝܟ ܐܚܘܟ ܐܟܬܐ ܡܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 24, 'ܫܒܘܩ ܬܡܢ ܩܘܪܒܢܟ ܩܕܡ ܡܕܒܚܐ ܘܙܠ ܠܘܩܕܡ ܐܬܪܥܐ ܥܡ ܐܚܘܟ ܘܗܝܕܝܢ ܬܐ ܩܪܒ ܩܘܪܒܢܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 25, 'ܗܘܝܬ ܡܬܐܘܐ ܥܡ ܒܥܠ ܕܝܢܟ ܥܓܠ ܥܕ ܥܡܗ ܐܢܬ ܒܐܘܪܚܐ ܕܠܡܐ ܒܥܠ ܕܝܢܟ ܢܫܠܡܟ ܠܕܝܢܐ ܘܕܝܢܐ ܢܫܠܡܟ ܠܓܒܝܐ ܘܬܦܠ ܒܝܬ ܐܤܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 26, 'ܘܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟ ܕܠܐ ܬܦܘܩ ܡܢ ܬܡܢ ܥܕܡܐ ܕܬܬܠ ܫܡܘܢܐ ܐܚܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 27, 'ܫܡܥܬܘܢ ܕܐܬܐܡܪ ܕܠܐ ܬܓܘܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 28, 'ܐܢܐ ܕܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܟܠ ܡܢ ܕܚܙܐ ܐܢܬܬܐ ܐܝܟ ܕܢܪܓܝܗ ܡܚܕܐ ܓܪܗ ܒܠܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 29, 'ܐܢ ܕܝܢ ܥܝܢܟ ܕܝܡܝܢܐ ܡܟܫܠܐ ܠܟ ܚܨܝܗ ܘܫܕܝܗ ܡܢܟ ܦܩܚ ܠܟ ܓܝܪ ܕܢܐܒܕ ܚܕ ܗܕܡܟ ܘܠܐ ܟܠܗ ܦܓܪܟ ܢܦܠ ܒܓܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 30, 'ܘܐܢ ܐܝܕܟ ܕܝܡܝܢܐ ܡܟܫܠܐ ܠܟ ܦܤܘܩ ܫܕܝܗ ܡܢܟ ܦܩܚ ܠܟ ܓܝܪ ܕܢܐܒܕ ܚܕ ܡܢ ܗܕܡܝܟ ܘܠܐ ܟܠܗ ܦܓܪܟ ܢܦܠ ܒܓܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 31, 'ܐܬܐܡܪ ܕܡܢ ܕܫܪܐ ܐܢܬܬܗ ܢܬܠ ܠܗ ܟܬܒܐ ܕܕܘܠܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 32, 'ܐܢܐ ܕܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܟܠ ܡܢ ܕܫܪܐ ܐܢܬܬܗ ܠܒܪ ܡܢ ܡܠܬܐ ܕܙܢܝܘܬܐ ܥܒܕ ܠܗ ܕܬܓܘܪ ܘܡܢ ܕܫܩܠ ܫܒܝܩܬܐ ܓܐܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 33, 'ܬܘܒ ܫܡܥܬܘܢ ܕܐܬܐܡܪ ܠܩܕܡܝܐ ܕܠܐ ܬܕܓܠ ܒܡܘܡܬܟ ܬܫܠܡ ܕܝܢ ܠܡܪܝܐ ܡܘܡܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 34, 'ܐܢܐ ܕܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܠܐ ܬܐܡܘܢ ܤܟ ܠܐ ܒܫܡܝܐ ܕܟܘܪܤܝܐ ܗܘ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 35, 'ܘܠܐ ܒܐܪܥܐ ܕܟܘܒܫܐ ܗܝ ܕܬܚܝܬ ܪܓܠܘܗܝ ܐܦܠܐ ܒܐܘܪܝܫܠܡ ܕܡܕܝܢܬܗ ܗܝ ܕܡܠܟܐ ܪܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 36, 'ܐܦܠܐ ܒܪܫܟ ܬܐܡܐ ܕܠܐ ܡܫܟܚ ܐܢܬ ܠܡܥܒܕ ܒܗ ܡܢܬܐ ܚܕܐ ܕܤܥܪܐ ܐܘܟܡܬܐ ܐܘ ܚܘܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 37, 'ܐܠܐ ܬܗܘܐ ܡܠܬܟܘܢ ܐܝܢ ܐܝܢ ܘܠܐ ܠܐ ܡܕܡ ܕܡܢ ܗܠܝܢ ܝܬܝܪ ܡܢ ܒܝܫܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 38, 'ܫܡܥܬܘܢ ܕܐܬܐܡܪ ܕܥܝܢܐ ܚܠܦ ܥܝܢܐ ܘܫܢܐ ܚܠܦ ܫܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 39, 'ܐܢܐ ܕܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܠܐ ܬܩܘܡܘܢ ܠܘܩܒܠ ܒܝܫܐ ܐܠܐ ܡܢ ܕܡܚܐ ܠܟ ܥܠ ܦܟܟ ܕܝܡܝܢܐ ܐܦܢܐ ܠܗ ܐܦ ܐܚܪܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 40, 'ܘܡܢ ܕܨܒܐ ܕܢܕܘܢ ܥܡܟ ܘܢܫܩܘܠ ܟܘܬܝܢܟ ܫܒܘܩ ܠܗ ܐܦ ܡܪܛܘܛܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 41, 'ܡܢ ܕܡܫܚܪ ܠܟ ܡܝܠܐ ܚܕ ܙܠ ܥܡܗ ܬܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 42, 'ܡܢ ܕܫܐܠ ܠܟ ܗܒ ܠܗ ܘܡܢ ܕܨܒܐ ܕܢܐܙܦ ܡܢܟ ܠܐ ܬܟܠܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 43, 'ܫܡܥܬܘܢ ܕܐܬܐܡܪ ܕܪܚܡ ܠܩܪܝܒܟ ܘܤܢܝ ܠܒܥܠܕܒܒܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 44, 'ܐܢܐ ܕܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܐܚܒܘ ܠܒܥܠܕܒܒܝܟܘܢ ܘܒܪܟܘ ܠܡܢ ܕܠܐܛ ܠܟܘܢ ܘܥܒܕܘ ܕܫܦܝܪ ܠܡܢ ܕܤܢܐ ܠܟܘܢ ܘܨܠܘ ܥܠ ܐܝܠܝܢ ܕܕܒܪܝܢ ܠܟܘܢ ܒܩܛܝܪܐ ܘܪܕܦܝܢ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 45, 'ܐܝܟܢܐ ܕܬܗܘܘܢ ܒܢܘܗܝ ܕܐܒܘܟܘܢ ܕܒܫܡܝܐ ܗܘ ܕܡܕܢܚ ܫܡܫܗ ܥܠ ܛܒܐ ܘܥܠ ܒܝܫܐ ܘܡܚܬ ܡܛܪܗ ܥܠ ܟܐܢܐ ܘܥܠ ܥܘܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 46, 'ܐܢ ܓܝܪ ܡܚܒܝܢ ܐܢܬܘܢ ܠܐܝܠܝܢ ܕܡܚܒܝܢ ܠܟܘܢ ܡܢܐ ܐܓܪܐ ܐܝܬ ܠܟܘܢ ܠܐ ܗܐ ܐܦ ܡܟܤܐ ܗܝ ܗܕܐ ܥܒܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 47, 'ܘܐܢ ܫܐܠܝܢ ܐܢܬܘܢ ܒܫܠܡܐ ܕܐܚܝܟܘܢ ܒܠܚܘܕ ܡܢܐ ܝܬܝܪ ܥܒܕܝܢ ܐܢܬܘܢ ܠܐ ܗܐ ܐܦ ܡܟܤܐ ܗܝ ܗܕܐ ܥܒܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 48, 'ܗܘܘ ܗܟܝܠ ܐܢܬܘܢ ܓܡܝܪܐ ܐܝܟܢܐ ܕܐܒܘܟܘܢ ܕܒܫܡܝܐ ܓܡܝܪ ܗܘ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 1, 'ܚܘܪܘ ܕܝܢ ܒܙܕܩܬܟܘܢ ܕܠܐ ܬܥܒܕܘܢܗ ܩܕܡ ܒܢܝ ܐܢܫܐ ܐܝܟ ܕܬܬܚܙܘܢ ܠܗܘܢ ܘܐܠܐ ܐܓܪܐ ܠܝܬ ܠܟܘܢ ܠܘܬ ܐܒܘܟܘܢ ܕܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 2, 'ܐܡܬܝ ܗܟܝܠ ܕܥܒܕ ܐܢܬ ܙܕܩܬܐ ܠܐ ܬܩܪܐ ܩܪܢܐ ܩܕܡܝܟ ܐܝܟ ܕܥܒܕܝܢ ܢܤܒܝ ܒܐܦܐ ܒܟܢܘܫܬܐ ܘܒܫܘܩܐ ܐܝܟ ܕܢܫܬܒܚܘܢ ܡܢ ܒܢܝ ܐܢܫܐ ܘܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܩܒܠܘ ܐܓܪܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 3, 'ܐܢܬ ܕܝܢ ܡܐ ܕܥܒܕ ܐܢܬ ܙܕܩܬܐ ܠܐ ܬܕܥ ܤܡܠܟ ܡܢܐ ܥܒܕܐ ܝܡܝܢܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 4, 'ܐܝܟ ܕܬܗܘܐ ܙܕܩܬܟ ܒܟܤܝܐ ܘܐܒܘܟ ܕܚܙܐ ܒܟܤܝܐ ܗܘ ܢܦܪܥܟ ܒܓܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 5, 'ܘܡܐ ܕܡܨܠܐ ܐܢܬ ܠܐ ܬܗܘܐ ܐܝܟ ܢܤܒܝ ܒܐܦܐ ܕܪܚܡܝܢ ܠܡܩܡ ܒܟܢܘܫܬܐ ܘܒܙܘܝܬܐ ܕܫܘܩܐ ܠܡܨܠܝܘ ܕܢܬܚܙܘܢ ܠܒܢܝ ܐܢܫܐ ܘܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܩܒܠܘ ܐܓܪܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 6, 'ܐܢܬ ܕܝܢ ܐܡܬܝ ܕܡܨܠܐ ܐܢܬ ܥܘܠ ܠܬܘܢܟ ܘܐܚܘܕ ܬܪܥܟ ܘܨܠܐ ܠܐܒܘܟ ܕܒܟܤܝܐ ܘܐܒܘܟ ܕܚܙܐ ܒܟܤܝܐ ܢܦܪܥܟ ܒܓܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 7, 'ܘܡܐ ܕܡܨܠܝܢ ܐܢܬܘܢ ܠܐ ܗܘܝܬܘܢ ܡܦܩܩܝܢ ܐܝܟ ܚܢܦܐ ܤܒܪܝܢ ܓܝܪ ܕܒܡܡܠܠܐ ܤܓܝܐܐ ܡܫܬܡܥܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 8, 'ܠܐ ܗܟܝܠ ܬܕܡܘܢ ܠܗܘܢ ܐܒܘܟܘܢ ܓܝܪ ܝܕܥ ܡܢܐ ܡܬܒܥܐ ܠܟܘܢ ܥܕܠܐ ܬܫܐܠܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 9, 'ܗܟܢܐ ܗܟܝܠ ܨܠܘ ܐܢܬܘܢ ܐܒܘܢ ܕܒܫܡܝܐ ܢܬܩܕܫ ܫܡܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 10, 'ܬܐܬܐ ܡܠܟܘܬܟ ܢܗܘܐ ܨܒܝܢܟ ܐܝܟܢܐ ܕܒܫܡܝܐ ܐܦ ܒܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 11, 'ܗܒ ܠܢ ܠܚܡܐ ܕܤܘܢܩܢܢ ܝܘܡܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 12, 'ܘܫܒܘܩ ܠܢ ܚܘܒܝܢ ܐܝܟܢܐ ܕܐܦ ܚܢܢ ܫܒܩܢ ܠܚܝܒܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 13, 'ܘܠܐ ܬܥܠܢ ܠܢܤܝܘܢܐ ܐܠܐ ܦܨܢ ܡܢ ܒܝܫܐ ܡܛܠ ܕܕܝܠܟ ܗܝ ܡܠܟܘܬܐ ܘܚܝܠܐ ܘܬܫܒܘܚܬܐ ܠܥܠܡ ܥܠܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 14, 'ܐܢ ܓܝܪ ܬܫܒܩܘܢ ܠܒܢܝܢܫܐ ܤܟܠܘܬܗܘܢ ܢܫܒܘܩ ܐܦ ܠܟܘܢ ܐܒܘܟܘܢ ܕܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 15, 'ܐܢ ܕܝܢ ܠܐ ܬܫܒܩܘܢ ܠܒܢܝܢܫܐ ܐܦܠܐ ܐܒܘܟܘܢ ܫܒܩ ܠܟܘܢ ܤܟܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 16, 'ܐܡܬܝ ܕܝܢ ܕܨܝܡܝܢ ܐܢܬܘܢ ܠܐ ܬܗܘܘܢ ܟܡܝܪܐ ܐܝܟ ܢܤܒܝ ܒܐܦܐ ܡܚܒܠܝܢ ܓܝܪ ܦܪܨܘܦܝܗܘܢ ܐܝܟ ܕܢܬܚܙܘܢ ܠܒܢܝܢܫܐ ܕܨܝܡܝܢ ܘܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܩܒܠܘ ܐܓܪܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 17, 'ܐܢܬ ܕܝܢ ܡܐ ܕܨܐܡ ܐܢܬ ܐܫܝܓ ܐܦܝܟ ܘܡܫܘܚ ܪܫܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 18, 'ܐܝܟ ܕܠܐ ܬܬܚܙܐ ܠܒܢܝܢܫܐ ܕܨܐܡ ܐܢܬ ܐܠܐ ܠܐܒܘܟ ܕܒܟܤܝܐ ܘܐܒܘܟ ܕܚܙܐ ܒܟܤܝܐ ܗܘ ܢܦܪܥܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 19, 'ܠܐ ܬܤܝܡܘܢ ܠܟܘܢ ܤܝܡܬܐ ܒܐܪܥܐ ܐܬܪ ܕܤܤܐ ܘܐܟܠܐ ܡܚܒܠܝܢ ܘܐܝܟܐ ܕܓܢܒܐ ܦܠܫܝܢ ܘܓܢܒܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 20, 'ܐܠܐ ܤܝܡܘ ܠܟܘܢ ܤܝܡܬܐ ܒܫܡܝܐ ܐܝܟܐ ܕܠܐ ܤܤܐ ܘܠܐ ܐܟܠܐ ܡܚܒܠܝܢ ܘܐܝܟܐ ܕܓܢܒܐ ܠܐ ܦܠܫܝܢ ܘܠܐ ܓܢܒܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 21, 'ܐܝܟܐ ܓܝܪ ܕܐܝܬܝܗ ܤܝܡܬܟܘܢ ܬܡܢ ܗܘ ܐܦ ܠܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 22, 'ܫܪܓܐ ܕܦܓܪܐ ܐܝܬܝܗ ܥܝܢܐ ܐܢ ܥܝܢܟ ܗܟܝܠ ܬܗܘܐ ܦܫܝܛܐ ܐܦ ܟܠܗ ܦܓܪܟ ܢܗܝܪ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 23, 'ܐܢ ܕܝܢ ܥܝܢܟ ܬܗܘܐ ܒܝܫܐ ܟܠܗ ܦܓܪܟ ܚܫܘܟܐ ܢܗܘܐ ܐܢ ܗܟܝܠ ܢܘܗܪܐ ܕܒܟ ܚܫܘܟܐ ܗܘ ܚܫܘܟܟ ܟܡܐ ܢܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 24, 'ܠܐ ܐܢܫ ܡܫܟܚ ܠܬܪܝܢ ܡܪܘܢ ܠܡܦܠܚ ܐܘ ܓܝܪ ܠܚܕ ܢܤܢܐ ܘܠܐܚܪܢܐ ܢܪܚܡ ܐܘ ܠܚܕ ܢܝܩܪ ܘܠܐܚܪܢܐ ܢܫܘܛ ܠܐ ܡܫܟܚܝܢ ܐܢܬܘܢ ܠܐܠܗܐ ܠܡܦܠܚ ܘܠܡܡܘܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 25, 'ܡܛܠ ܗܢܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܠܐ ܬܐܨܦܘܢ ܠܢܦܫܟܘܢ ܡܢܐ ܬܐܟܠܘܢ ܘܡܢܐ ܬܫܬܘܢ ܘܠܐ ܠܦܓܪܟܘܢ ܡܢܐ ܬܠܒܫܘܢ ܠܐ ܗܐ ܢܦܫܐ ܝܬܝܪܐ ܡܢ ܤܝܒܪܬܐ ܘܦܓܪܐ ܡܢ ܠܒܘܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 26, 'ܚܘܪܘ ܒܦܪܚܬܐ ܕܫܡܝܐ ܕܠܐ ܙܪܥܝܢ ܘܠܐ ܚܨܕܝܢ ܘܠܐ ܚܡܠܝܢ ܒܐܘܨܪܐ ܘܐܒܘܟܘܢ ܕܒܫܡܝܐ ܡܬܪܤܐ ܠܗܘܢ ܠܐ ܗܐ ܐܢܬܘܢ ܡܝܬܪܝܢ ܐܢܬܘܢ ܡܢܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 27, 'ܡܢܘ ܕܝܢ ܡܢܟܘܢ ܟܕ ܝܨܦ ܡܫܟܚ ܠܡܘܤܦܘ ܥܠ ܩܘܡܬܗ ܐܡܬܐ ܚܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 28, 'ܘܥܠ ܠܒܘܫܐ ܡܢܐ ܝܨܦܝܢ ܐܢܬܘܢ ܐܬܒܩܘ ܒܫܘܫܢܐ ܕܕܒܪܐ ܐܝܟܢܐ ܪܒܝܢ ܕܠܐ ܠܐܝܢ ܘܠܐ ܥܙܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 29, 'ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܝܢ ܕܐܦܠܐ ܫܠܝܡܘܢ ܒܟܠܗ ܫܘܒܚܗ ܐܬܟܤܝ ܐܝܟ ܚܕܐ ܡܢܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 30, 'ܐܢ ܕܝܢ ܠܥܡܝܪܐ ܕܚܩܠܐ ܕܝܘܡܢܐ ܐܝܬܘܗܝ ܘܡܚܪ ܢܦܠ ܒܬܢܘܪܐ ܐܠܗܐ ܗܟܢܐ ܡܠܒܫ ܠܐ ܤܓܝ ܝܬܝܪ ܠܟܘܢ ܙܥܘܪܝ ܗܝܡܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 31, 'ܠܐ ܗܟܝܠ ܬܐܨܦܘܢ ܐܘ ܬܐܡܪܘܢ ܡܢܐ ܢܐܟܘܠ ܐܘ ܡܢܐ ܢܫܬܐ ܐܘ ܡܢܐ ܢܬܟܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 32, 'ܟܠܗܝܢ ܓܝܪ ܗܠܝܢ ܥܡܡܐ ܗܘ ܒܥܝܢ ܠܗܝܢ ܐܒܘܟܘܢ ܕܝܢ ܕܒܫܡܝܐ ܝܕܥ ܕܐܦ ܠܟܘܢ ܡܬܒܥܝܢ ܗܠܝܢ ܟܠܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 33, 'ܒܥܘ ܕܝܢ ܠܘܩܕܡ ܡܠܟܘܬܗ ܕܐܠܗܐ ܘܙܕܝܩܘܬܗ ܘܟܠܗܝܢ ܗܠܝܢ ܡܬܬܘܤܦܢ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 34, 'ܠܐ ܗܟܝܠ ܬܐܨܦܘܢ ܕܡܚܪ ܗܘ ܓܝܪ ܡܚܪ ܝܨܦ ܕܝܠܗ ܤܦܩ ܠܗ ܠܝܘܡܐ ܒܝܫܬܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 1, 'ܠܐ ܬܕܘܢܘܢ ܕܠܐ ܬܬܕܝܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 2, 'ܒܕܝܢܐ ܓܝܪ ܕܕܝܢܝܢ ܐܢܬܘܢ ܬܬܕܝܢܘܢ ܘܒܟܝܠܬܐ ܕܡܟܝܠܝܢ ܐܢܬܘܢ ܡܬܬܟܝܠ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 3, 'ܡܢܐ ܕܝܢ ܚܙܐ ܐܢܬ ܓܠܐ ܕܒܥܝܢܗ ܕܐܚܘܟ ܘܩܪܝܬܐ ܕܒܥܝܢܟ ܠܐ ܒܚܪ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 4, 'ܐܘ ܐܝܟܢܐ ܐܡܪ ܐܢܬ ܠܐܚܘܟ ܫܒܘܩ ܐܦܩ ܓܠܐ ܡܢ ܥܝܢܟ ܘܗܐ ܩܪܝܬܐ ܒܥܝܢܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 5, 'ܢܤܒ ܒܐܦܐ ܐܦܩ ܠܘܩܕܡ ܩܪܝܬܐ ܡܢ ܥܝܢܟ ܘܗܝܕܝܢ ܢܬܒܚܪ ܠܟ ܠܡܦܩܘ ܓܠܐ ܡܢ ܥܝܢܗ ܕܐܚܘܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 6, 'ܠܐ ܬܬܠܘܢ ܩܘܕܫܐ ܠܟܠܒܐ ܘܠܐ ܬܪܡܘܢ ܡܪܓܢܝܬܟܘܢ ܩܕܡ ܚܙܝܪܐ ܕܠܡܐ ܢܕܘܫܘܢ ܐܢܝܢ ܒܪܓܠܝܗܘܢ ܘܢܗܦܟܘܢ ܢܒܙܥܘܢܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 7, 'ܫܐܠܘ ܘܢܬܝܗܒ ܠܟܘܢ ܒܥܘ ܘܬܫܟܚܘܢ ܩܘܫܘ ܘܢܬܦܬܚ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 8, 'ܟܠ ܓܝܪ ܕܫܐܠ ܢܤܒ ܘܕܒܥܐ ܡܫܟܚ ܘܠܐܝܢܐ ܕܢܩܫ ܡܬܦܬܚ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 9, 'ܐܘ ܡܢܘ ܡܢܟܘܢ ܓܒܪܐ ܕܢܫܐܠܝܘܗܝ ܒܪܗ ܠܚܡܐ ܠܡܐ ܟܐܦܐ ܡܘܫܛ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 10, 'ܘܐܢ ܢܘܢܐ ܢܫܐܠܝܘܗܝ ܠܡܐ ܚܘܝܐ ܡܘܫܛ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 11, 'ܘܐܢ ܗܟܝܠ ܐܢܬܘܢ ܕܒܝܫܐ ܐܢܬܘܢ ܝܕܥܝܢ ܐܢܬܘܢ ܡܘܗܒܬܐ ܛܒܬܐ ܠܡܬܠ ܠܒܢܝܟܘܢ ܟܡܐ ܝܬܝܪܐܝܬ ܐܒܘܟܘܢ ܕܒܫܡܝܐ ܢܬܠ ܛܒܬܐ ܠܐܝܠܝܢ ܕܫܐܠܝܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 12, 'ܟܠ ܡܐ ܕܨܒܝܢ ܐܢܬܘܢ ܕܢܥܒܕܘܢ ܠܟܘܢ ܒܢܝ ܐܢܫܐ ܗܟܢܐ ܐܦ ܐܢܬܘܢ ܥܒܕܘ ܠܗܘܢ ܗܢܘ ܓܝܪ ܢܡܘܤܐ ܘܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 13, 'ܥܘܠܘ ܒܬܪܥܐ ܐܠܝܨܐ ܕܦܬܐ ܗܘ ܬܪܥܐ ܘܐܪܘܝܚܐ ܐܘܪܚܐ ܐܝܕܐ ܕܡܘܒܠܐ ܠܐܒܕܢܐ ܘܤܓܝܐܐ ܐܢܘܢ ܐܝܠܝܢ ܕܐܙܠܝܢ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 14, 'ܡܐ ܩܛܝܢ ܬܪܥܐ ܘܐܠܝܨܐ ܐܘܪܚܐ ܕܡܘܒܠܐ ܠܚܝܐ ܘܙܥܘܪܐ ܐܢܘܢ ܐܝܠܝܢ ܕܡܫܟܚܝܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 15, 'ܐܙܕܗܪܘ ܡܢ ܢܒܝܐ ܕܓܠܐ ܕܐܬܝܢ ܠܘܬܟܘܢ ܒܠܒܘܫܐ ܕܐܡܪܐ ܡܢ ܠܓܘ ܕܝܢ ܐܝܬܝܗܘܢ ܕܐܒܐ ܚܛܘܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 16, 'ܡܢ ܦܐܪܝܗܘܢ ܕܝܢ ܬܕܥܘܢ ܐܢܘܢ ܠܡܐ ܠܩܛܝܢ ܡܢ ܟܘܒܐ ܥܢܒܐ ܐܘ ܡܢ ܩܘܪܛܒܐ ܬܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 17, 'ܗܟܢܐ ܟܠ ܐܝܠܢܐ ܛܒܐ ܦܐܪܐ ܫܦܝܪܐ ܥܒܕ ܐܝܠܢܐ ܕܝܢ ܒܝܫܐ ܦܐܪܐ ܒܝܫܐ ܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 18, 'ܠܐ ܡܫܟܚ ܐܝܠܢܐ ܛܒܐ ܦܐܪܐ ܒܝܫܐ ܠܡܥܒܕ ܘܠܐ ܐܝܠܢܐ ܒܝܫܐ ܦܐܪܐ ܛܒܐ ܠܡܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 19, 'ܟܠ ܐܝܠܢܐ ܕܠܐ ܥܒܕ ܦܐܪܐ ܛܒܐ ܡܬܦܤܩ ܘܒܢܘܪܐ ܢܦܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 20, 'ܡܕܝܢ ܡܢ ܦܐܪܝܗܘܢ ܬܕܥܘܢ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 21, 'ܠܐ ܗܘܐ ܟܠ ܕܐܡܪ ܠܝ ܡܪܝ ܡܪܝ ܥܐܠ ܠܡܠܟܘܬܐ ܕܫܡܝܐ ܐܠܐ ܡܢ ܕܥܒܕ ܨܒܝܢܗ ܕܐܒܝ ܕܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 22, 'ܤܓܝܐܐ ܢܐܡܪܘܢ ܠܝ ܒܗܘ ܝܘܡܐ ܡܪܝ ܡܪܝ ܠܐ ܒܫܡܟ ܐܬܢܒܝܢ ܘܒܫܡܟ ܫܐܕܐ ܐܦܩܢ ܘܒܫܡܟ ܚܝܠܐ ܤܓܝܐܐ ܥܒܕܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 23, 'ܘܗܝܕܝܢ ܐܘܕܐ ܠܗܘܢ ܕܡܢ ܡܬܘܡ ܠܐ ܝܕܥܬܟܘܢ ܐܪܚܩܘ ܠܟܘܢ ܡܢܝ ܦܠܚܝ ܥܘܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 24, 'ܟܠ ܗܟܝܠ ܕܫܡܥ ܡܠܝ ܗܠܝܢ ܘܥܒܕ ܠܗܝܢ ܢܬܕܡܐ ܠܓܒܪܐ ܚܟܝܡܐ ܗܘ ܕܒܢܐ ܒܝܬܗ ܥܠ ܫܘܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 25, 'ܘܢܚܬ ܡܛܪܐ ܘܐܬܘ ܢܗܪܘܬܐ ܘܢܫܒ ܪܘܚܐ ܘܐܬܛܪܝܘ ܒܗ ܒܒܝܬܐ ܗܘ ܘܠܐ ܢܦܠ ܫܬܐܤܘܗܝ ܓܝܪ ܥܠ ܫܘܥܐ ܤܝܡܢ ܗܘܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 26, 'ܘܟܠ ܡܢ ܕܫܡܥ ܡܠܝ ܗܠܝܢ ܘܠܐ ܥܒܕ ܠܗܝܢ ܢܬܕܡܐ ܠܓܒܪܐ ܤܟܠܐ ܕܒܢܐ ܒܝܬܗ ܥܠ ܚܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 27, 'ܘܢܚܬ ܡܛܪܐ ܘܐܬܘ ܢܗܪܘܬܐ ܘܢܫܒ ܪܘܚܐ ܘܐܬܛܪܝܘ ܒܒܝܬܐ ܗܘ ܘܢܦܠ ܘܗܘܬ ܡܦܘܠܬܗ ܪܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 28, 'ܘܗܘܐ ܕܟܕ ܫܠܡ ܝܫܘܥ ܡܠܐ ܗܠܝܢ ܬܗܝܪܝܢ ܗܘܘ ܟܢܫܐ ܥܠ ܝܘܠܦܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 29, 'ܡܠܦ ܗܘܐ ܠܗܘܢ ܓܝܪ ܐܝܟ ܡܫܠܛܐ ܘܠܐ ܐܝܟ ܤܦܪܝܗܘܢ ܘܦܪܝܫܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 1, 'ܟܕ ܢܚܬ ܕܝܢ ܡܢ ܛܘܪܐ ܢܩܦܘܗܝ ܟܢܫܐ ܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 2, 'ܘܗܐ ܓܪܒܐ ܚܕ ܐܬܐ ܤܓܕ ܠܗ ܘܐܡܪ ܡܪܝ ܐܢ ܨܒܐ ܐܢܬ ܡܫܟܚ ܐܢܬ ܠܡܕܟܝܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 3, 'ܘܦܫܛ ܐܝܕܗ ܝܫܘܥ ܩܪܒ ܠܗ ܘܐܡܪ ܨܒܐ ܐܢܐ ܐܬܕܟܐ ܘܒܗ ܒܫܥܬܐ ܐܬܕܟܝ ܓܪܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 4, 'ܘܐܡܪ ܠܗ ܝܫܘܥ ܚܙܝ ܠܡܐ ܠܐܢܫ ܐܡܪ ܐܢܬ ܐܠܐ ܙܠ ܚܘܐ ܢܦܫܟ ܠܟܗܢܐ ܘܩܪܒ ܩܘܪܒܢܐ ܐܝܟ ܕܦܩܕ ܡܘܫܐ ܠܤܗܕܘܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 5, 'ܟܕ ܥܠ ܕܝܢ ܝܫܘܥ ܠܟܦܪܢܚܘܡ ܩܪܒ ܠܗ ܩܢܛܪܘܢܐ ܚܕ ܘܒܥܐ ܗܘܐ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 6, 'ܘܐܡܪ ܡܪܝ ܛܠܝܝ ܪܡܐ ܒܒܝܬܐ ܘܡܫܪܝ ܘܒܝܫܐܝܬ ܡܫܬܢܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 7, 'ܐܡܪ ܠܗ ܝܫܘܥ ܐܢܐ ܐܬܐ ܘܐܤܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 8, 'ܥܢܐ ܩܢܛܪܘܢܐ ܗܘ ܘܐܡܪ ܡܪܝ ܠܐ ܫܘܐ ܐܢܐ ܕܬܥܘܠ ܬܚܝܬ ܡܛܠܠܝ ܐܠܐ ܒܠܚܘܕ ܐܡܪ ܒܡܠܬܐ ܘܢܬܐܤܐ ܛܠܝܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 9, 'ܐܦ ܐܢܐ ܓܝܪ ܓܒܪܐ ܐܢܐ ܕܬܚܝܬ ܫܘܠܛܢܐ ܘܐܝܬ ܬܚܝܬ ܐܝܕܝ ܐܤܛܪܛܝܘܛܐ ܘܐܡܪ ܐܢܐ ܠܗܢܐ ܕܙܠ ܘܐܙܠ ܘܠܐܚܪܢܐ ܕܬܐ ܘܐܬܐ ܘܠܥܒܕܝ ܕܥܒܕ ܗܕܐ ܘܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 10, 'ܟܕ ܫܡܥ ܕܝܢ ܝܫܘܥ ܐܬܕܡܪ ܘܐܡܪ ܠܕܐܬܝܢ ܥܡܗ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܐܦ ܠܐ ܒܐܝܤܪܐܝܠ ܐܫܟܚܬ ܐܝܟ ܗܕܐ ܗܝܡܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 11, 'ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܝܢ ܕܤܓܝܐܐ ܢܐܬܘܢ ܡܢ ܡܕܢܚܐ ܘܡܢ ܡܥܪܒܐ ܘܢܤܬܡܟܘܢ ܥܡ ܐܒܪܗܡ ܘܐܝܤܚܩ ܘܝܥܩܘܒ ܒܡܠܟܘܬܐ ܕܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 12, 'ܒܢܝܗ ܕܝܢ ܕܡܠܟܘܬܐ ܢܦܩܘܢ ܠܚܫܘܟܐ ܒܪܝܐ ܬܡܢ ܢܗܘܐ ܒܟܝܐ ܘܚܘܪܩ ܫܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 13, 'ܘܐܡܪ ܝܫܘܥ ܠܩܢܛܪܘܢܐ ܗܘ ܙܠ ܐܝܟܢܐ ܕܗܝܡܢܬ ܢܗܘܐ ܠܟ ܘܐܬܐܤܝ ܛܠܝܗ ܒܗ ܒܫܥܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 14, 'ܘܐܬܐ ܝܫܘܥ ܠܒܝܬܗ ܕܫܡܥܘܢ ܘܚܙܐ ܠܚܡܬܗ ܕܪܡܝܐ ܘܐܚܝܕܐ ܠܗ ܐܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 15, 'ܘܩܪܒ ܠܐܝܕܗ ܘܫܒܩܬܗ ܐܫܬܐ ܘܩܡܬ ܘܡܫܡܫܐ ܗܘܬ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 16, 'ܟܕ ܗܘܐ ܕܝܢ ܪܡܫܐ ܩܪܒܘ ܩܕܡܘܗܝ ܕܝܘܢܐ ܤܓܝܐܐ ܘܐܦܩ ܕܝܘܝܗܘܢ ܒܡܠܬܐ ܘܠܟܠܗܘܢ ܐܝܠܝܢ ܕܒܝܫܐܝܬ ܥܒܝܕܝܢ ܗܘܘ ܐܤܝ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 17, 'ܐܝܟ ܕܢܬܡܠܐ ܡܕܡ ܕܐܬܐܡܪ ܒܝܕ ܐܫܥܝܐ ܢܒܝܐ ܕܐܡܪ ܕܗܘ ܢܤܒ ܟܐܒܝܢ ܘܟܘܪܗܢܝܢ ܢܛܥܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 18, 'ܟܕ ܚܙܐ ܕܝܢ ܝܫܘܥ ܟܢܫܐ ܤܓܝܐܐ ܕܚܕܝܪܝܢ ܠܗ ܦܩܕ ܕܢܐܙܠܘܢ ܠܥܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 19, 'ܘܩܪܒ ܤܦܪܐ ܚܕ ܘܐܡܪ ܠܗ ܪܒܝ ܐܬܐ ܒܬܪܟ ܠܐܬܪ ܕܐܙܠ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 20, 'ܐܡܪ ܠܗ ܝܫܘܥ ܠܬܥܠܐ ܢܩܥܐ ܐܝܬ ܠܗܘܢ ܘܠܦܪܚܬܐ ܕܫܡܝܐ ܡܛܠܠܐ ܒܪܗ ܕܝܢ ܕܐܢܫܐ ܠܝܬ ܠܗ ܐܝܟܐ ܕܢܤܡܘܟ ܪܫܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 21, 'ܐܚܪܢܐ ܕܝܢ ܡܢ ܬܠܡܝܕܘܗܝ ܐܡܪ ܠܗ ܡܪܝ ܐܦܤ ܠܝ ܠܘܩܕܡ ܐܙܠ ܐܩܒܘܪ ܐܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 22, 'ܝܫܘܥ ܕܝܢ ܐܡܪ ܠܗ ܬܐ ܒܬܪܝ ܘܫܒܘܩ ܠܡܝܬܐ ܩܒܪܝܢ ܡܝܬܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 23, 'ܘܟܕ ܤܠܩ ܝܫܘܥ ܠܤܦܝܢܬܐ ܤܠܩܘ ܥܡܗ ܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 24, 'ܘܗܐ ܙܘܥܐ ܪܒܐ ܗܘܐ ܒܝܡܐ ܐܝܟܢܐ ܕܐܠܦܐ ܬܬܟܤܐ ܡܢ ܓܠܠܐ ܗܘ ܕܝܢ ܝܫܘܥ ܕܡܝܟ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 25, 'ܘܩܪܒܘ ܬܠܡܝܕܘܗܝ ܐܥܝܪܘܗܝ ܘܐܡܪܝܢ ܠܗ ܡܪܢ ܦܨܢ ܐܒܕܝܢ ܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 26, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܠܡܢܐ ܕܚܘܠܬܢܝܢ ܐܢܬܘܢ ܙܥܘܪܝ ܗܝܡܢܘܬܐ ܗܝܕܝܢ ܩܡ ܘܟܐܐ ܒܪܘܚܐ ܘܒܝܡܐ ܘܗܘܐ ܫܠܝܐ ܪܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 27, 'ܐܢܫܐ ܕܝܢ ܐܬܕܡܪܘ ܘܐܡܪܝܢ ܡܢܘ ܗܢܐ ܕܪܘܚܐ ܘܝܡܐ ܡܫܬܡܥܝܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 28, 'ܘܟܕ ܐܬܐ ܝܫܘܥ ܠܥܒܪܐ ܠܐܬܪܐ ܕܓܕܪܝܐ ܐܪܥܘܗܝ ܬܪܝܢ ܕܝܘܢܐ ܕܢܦܩܝܢ ܡܢ ܒܝܬ ܩܒܘܪܐ ܒܝܫܐ ܕܛܒ ܐܝܟ ܕܠܐ ܐܢܫ ܢܫܟܚ ܢܥܒܪ ܒܗܝ ܐܘܪܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 29, 'ܘܩܥܘ ܘܐܡܪܝܢ ܡܐ ܠܢ ܘܠܟ ܝܫܘܥ ܒܪܗ ܕܐܠܗܐ ܐܬܝܬ ܠܟܐ ܩܕܡ ܙܒܢܐ ܕܬܫܢܩܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 30, 'ܐܝܬ ܗܘܐ ܕܝܢ ܠܗܠ ܡܢܗܘܢ ܒܩܪܐ ܕܚܙܝܪܐ ܤܓܝܐܐ ܕܪܥܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 31, 'ܗܢܘܢ ܕܝܢ ܫܐܕܐ ܒܥܝܢ ܗܘܘ ܡܢܗ ܘܐܡܪܝܢ ܐܢ ܡܦܩ ܐܢܬ ܠܢ ܐܦܤ ܠܢ ܕܢܐܙܠ ܠܒܩܪܐ ܕܚܙܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 32, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܙܠܘ ܘܡܚܕܐ ܢܦܩܘ ܘܥܠܘ ܒܚܙܝܪܐ ܘܟܠܗ ܒܩܪܐ ܗܝ ܬܪܨܬ ܠܥܠ ܠܫܩܝܦܐ ܘܢܦܠܘ ܒܝܡܐ ܘܡܝܬܘ ܒܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 33, 'ܗܢܘܢ ܕܝܢ ܕܪܥܝܢ ܗܘܘ ܥܪܩܘ ܘܐܙܠܘ ܠܡܕܝܢܬܐ ܘܚܘܝܘ ܟܠܡܕܡ ܕܗܘܐ ܘܕܗܢܘܢ ܕܝܘܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 34, 'ܘܢܦܩܬ ܟܠܗ ܡܕܝܢܬܐ ܠܐܘܪܥܗ ܕܝܫܘܥ ܘܟܕ ܚܙܐܘܗܝ ܒܥܘ ܡܢܗ ܕܢܫܢܐ ܡܢ ܬܚܘܡܝܗܘܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 1, 'ܘܤܠܩ ܠܐܠܦܐ ܘܥܒܪ ܐܬܐ ܠܡܕܝܢܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 2, 'ܘܩܪܒܘ ܠܗ ܡܫܪܝܐ ܟܕ ܪܡܐ ܒܥܪܤܐ ܘܚܙܐ ܝܫܘܥ ܗܝܡܢܘܬܗܘܢ ܘܐܡܪ ܠܗܘ ܡܫܪܝܐ ܐܬܠܒܒ ܒܪܝ ܫܒܝܩܝܢ ܠܟ ܚܛܗܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 3, 'ܐܢܫܐ ܕܝܢ ܡܢ ܤܦܪܐ ܐܡܪܘ ܒܢܦܫܗܘܢ ܗܢܐ ܡܓܕܦ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 4, 'ܝܫܘܥ ܕܝܢ ܝܕܥ ܡܚܫܒܬܗܘܢ ܘܐܡܪ ܠܗܘܢ ܡܢܐ ܡܬܚܫܒܝܢ ܐܢܬܘܢ ܒܝܫܬܐ ܒܠܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 5, 'ܡܢܐ ܓܝܪ ܦܫܝܩ ܠܡܐܡܪ ܕܫܒܝܩܝܢ ܠܟ ܚܛܗܝܟ ܐܘ ܠܡܐܡܪ ܩܘܡ ܗܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 6, 'ܕܬܕܥܘܢ ܕܝܢ ܕܫܘܠܛܢܐ ܐܝܬ ܠܒܪܗ ܕܐܢܫܐ ܒܐܪܥܐ ܠܡܫܒܩ ܚܛܗܐ ܐܡܪ ܠܗܘ ܡܫܪܝܐ ܩܘܡ ܫܩܘܠ ܥܪܤܟ ܘܙܠ ܠܒܝܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 7, 'ܘܩܡ ܐܙܠ ܠܒܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 8, 'ܟܕ ܚܙܘ ܕܝܢ ܟܢܫܐ ܗܢܘܢ ܕܚܠܘ ܘܫܒܚܘ ܠܐܠܗܐ ܕܝܗܒ ܫܘܠܛܢܐ ܕܐܝܟ ܗܢܐ ܠܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 9, 'ܘܟܕ ܥܒܪ ܝܫܘܥ ܡܢ ܬܡܢ ܚܙܐ ܓܒܪܐ ܕܝܬܒ ܒܝܬ ܡܟܤܐ ܕܫܡܗ ܡܬܝ ܘܐܡܪ ܠܗ ܬܐ ܒܬܪܝ ܘܩܡ ܐܙܠ ܒܬܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 10, 'ܘܟܕ ܤܡܝܟܝܢ ܒܒܝܬܐ ܐܬܘ ܡܟܤܐ ܘܚܛܝܐ ܤܓܝܐܐ ܐܤܬܡܟܘ ܥܡ ܝܫܘܥ ܘܥܡ ܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 11, 'ܘܟܕ ܚܙܘ ܦܪܝܫܐ ܐܡܪܝܢ ܠܬܠܡܝܕܘܗܝ ܠܡܢܐ ܥܡ ܡܟܤܐ ܘܚܛܝܐ ܠܥܤ ܪܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 12, 'ܝܫܘܥ ܕܝܢ ܟܕ ܫܡܥ ܐܡܪ ܠܗܘܢ ܠܐ ܤܢܝܩܝܢ ܚܠܝܡܐ ܥܠ ܐܤܝܐ ܐܠܐ ܐܝܠܝܢ ܕܒܝܫܐܝܬ ܥܒܝܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 13, 'ܙܠܘ ܝܠܦܘ ܡܢܘ ܚܢܢܐ ܒܥܐ ܐܢܐ ܘܠܐ ܕܒܚܬܐ ܠܐ ܓܝܪ ܐܬܝܬ ܕܐܩܪܐ ܠܙܕܝܩܐ ܐܠܐ ܠܚܛܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 14, 'ܗܝܕܝܢ ܩܪܒܘ ܠܗ ܬܠܡܝܕܘܗܝ ܕܝܘܚܢܢ ܘܐܡܪܝܢ ܠܡܢܐ ܚܢܢ ܘܦܪܝܫܐ ܨܝܡܝܢ ܚܢܢ ܤܓܝ ܘܬܠܡܝܕܝܟ ܠܐ ܨܝܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 15, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܕܠܡܐ ܡܫܟܚܝܢ ܒܢܘܗܝ ܕܓܢܘܢܐ ܠܡܨܡ ܟܡܐ ܕܚܬܢܐ ܥܡܗܘܢ ܐܬܝܢ ܕܝܢ ܝܘܡܬܐ ܟܕ ܢܫܬܩܠ ܡܢܗܘܢ ܚܬܢܐ ܘܗܝܕܝܢ ܢܨܘܡܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 16, 'ܠܐ ܐܢܫ ܪܡܐ ܐܘܪܩܥܬܐ ܚܕܬܐ ܥܠ ܢܚܬܐ ܒܠܝܐ ܕܠܐ ܬܬܘܦ ܡܠܝܘܬܗ ܡܢ ܗܘ ܢܚܬܐ ܘܢܗܘܐ ܒܙܥܐ ܝܬܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 17, 'ܘܠܐ ܪܡܝܢ ܚܡܪܐ ܚܕܬܐ ܒܙܩܐ ܒܠܝܬܐ ܕܠܐ ܡܨܛܪܝܢ ܙܩܐ ܘܚܡܪܐ ܡܬܐܫܕ ܘܙܩܐ ܐܒܕܢ ܐܠܐ ܪܡܝܢ ܚܡܪܐ ܚܕܬܐ ܒܙܩܐ ܚܕܬܬܐ ܘܬܪܝܗܘܢ ܡܬܢܛܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 18, 'ܟܕ ܕܝܢ ܗܠܝܢ ܡܡܠܠ ܗܘܐ ܥܡܗܘܢ ܐܬܐ ܐܪܟܘܢܐ ܚܕ ܩܪܒ ܤܓܕ ܠܗ ܘܐܡܪ ܒܪܬܝ ܗܫܐ ܡܝܬܬ ܐܠܐ ܬܐ ܤܝܡ ܐܝܕܟ ܥܠܝܗ ܘܬܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 19, 'ܘܩܡ ܝܫܘܥ ܘܬܠܡܝܕܘܗܝ ܘܐܙܠܘ ܒܬܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 20, 'ܘܗܐ ܐܢܬܬܐ ܕܪܕܐ ܗܘܐ ܕܡܗ ܫܢܝܢ ܬܪܬܥܤܪܐ ܐܬܬ ܡܢ ܒܤܬܪܗ ܘܩܪܒܬ ܠܩܪܢܐ ܕܠܒܘܫܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 21, 'ܐܡܪܐ ܗܘܬ ܓܝܪ ܒܢܦܫܗ ܐܦܢ ܒܠܚܘܕ ܠܡܐܢܗ ܩܪܒܐ ܐܢܐ ܡܬܐܤܝܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 22, 'ܝܫܘܥ ܕܝܢ ܐܬܦܢܝ ܚܙܗ ܘܐܡܪ ܠܗ ܐܬܠܒܒܝ ܒܪܬܝ ܗܝܡܢܘܬܟܝ ܐܚܝܬܟܝ ܘܐܬܐܤܝܬ ܐܢܬܬܐ ܗܝ ܡܢ ܗܝ ܫܥܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 23, 'ܘܐܬܐ ܝܫܘܥ ܠܒܝܬܗ ܕܐܪܟܘܢܐ ܘܚܙܐ ܙܡܪܐ ܘܟܢܫܐ ܕܡܫܬܓܫܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 24, 'ܘܐܡܪ ܠܗܘܢ ܦܪܘܩܘ ܠܟܘܢ ܛܠܝܬܐ ܓܝܪ ܠܐ ܡܝܬܬ ܐܠܐ ܕܡܟܐ ܗܝ ܘܓܚܟܝܢ ܗܘܘ ܥܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 25, 'ܘܟܕ ܐܦܩ ܠܟܢܫܐ ܥܠ ܐܚܕܗ ܒܐܝܕܗ ܘܩܡܬ ܛܠܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 26, 'ܘܢܦܩ ܛܒܐ ܗܢܐ ܒܟܠܗ ܐܪܥܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 27, 'ܘܟܕ ܥܒܪ ܝܫܘܥ ܡܢ ܬܡܢ ܕܒܩܘܗܝ ܤܡܝܐ ܬܪܝܢ ܕܩܥܝܢ ܘܐܡܪܝܢ ܐܬܪܚܡ ܥܠܝܢ ܒܪܗ ܕܕܘܝܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 28, 'ܘܟܕ ܐܬܐ ܠܒܝܬܐ ܩܪܒܘ ܠܗ ܗܢܘܢ ܤܡܝܐ ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܡܗܝܡܢܝܢ ܐܢܬܘܢ ܕܡܫܟܚ ܐܢܐ ܗܕܐ ܠܡܥܒܕ ܐܡܪܝܢ ܠܗ ܐܝܢ ܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 29, 'ܗܝܕܝܢ ܩܪܒ ܠܥܝܢܝܗܘܢ ܘܐܡܪ ܐܝܟܢܐ ܕܗܝܡܢܬܘܢ ܢܗܘܐ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 30, 'ܘܡܚܕܐ ܐܬܦܬܚ ܥܝܢܝܗܘܢ ܘܟܐܐ ܒܗܘܢ ܝܫܘܥ ܘܐܡܪ ܚܙܘ ܠܐ ܐܢܫ ܢܕܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 31, 'ܗܢܘܢ ܕܝܢ ܢܦܩܘ ܐܛܒܘܗܝ ܒܟܠܗ ܐܪܥܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 32, 'ܘܟܕ ܢܦܩ ܝܫܘܥ ܩܪܒܘ ܠܗ ܚܪܫܐ ܕܐܝܬ ܥܠܘܗܝ ܕܝܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 33, 'ܘܡܢ ܕܢܦܩ ܕܝܘܐ ܡܠܠ ܗܘ ܚܪܫܐ ܘܐܬܕܡܪܘ ܟܢܫܐ ܘܐܡܪܝܢ ܠܐ ܡܬܘܡ ܐܬܚܙܝ ܗܟܢܐ ܒܐܝܤܪܐܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 34, 'ܦܪܝܫܐ ܕܝܢ ܐܡܪܝܢ ܗܘܘ ܒܪܫܐ ܕܕܝܘܐ ܡܦܩ ܕܝܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 35, 'ܘܡܬܟܪܟ ܗܘܐ ܝܫܘܥ ܒܡܕܝܢܬܐ ܟܠܗܝܢ ܘܒܩܘܪܝܐ ܘܡܠܦ ܗܘܐ ܒܟܢܘܫܬܗܘܢ ܘܡܟܪܙ ܤܒܪܬܐ ܕܡܠܟܘܬܐ ܘܡܐܤܐ ܟܠ ܟܘܪܗܢܝܢ ܘܟܠ ܟܐܒܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 36, 'ܟܕ ܚܙܐ ܕܝܢ ܝܫܘܥ ܠܟܢܫܐ ܐܬܪܚܡ ܥܠܝܗܘܢ ܕܠܐܝܢ ܗܘܘ ܘܫܪܝܢ ܐܝܟ ܥܪܒܐ ܕܠܝܬ ܠܗܘܢ ܪܥܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 37, 'ܘܐܡܪ ܠܬܠܡܝܕܘܗܝ ܚܨܕܐ ܤܓܝ ܘܦܥܠܐ ܙܥܘܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 38, 'ܒܥܘ ܗܟܝܠ ܡܢ ܡܪܐ ܚܨܕܐ ܕܢܦܩ ܦܥܠܐ ܠܚܨܕܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 1, 'ܘܩܪܐ ܠܬܪܥܤܪ ܬܠܡܝܕܘܗܝ ܘܝܗܒ ܠܗܘܢ ܫܘܠܛܢܐ ܥܠ ܪܘܚܐ ܛܢܦܬܐ ܕܢܦܩܘܢ ܘܠܡܐܤܝܘ ܟܠ ܟܐܒ ܘܟܘܪܗܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 2, 'ܕܝܠܗܘܢ ܕܝܢ ܕܬܪܥܤܪ ܫܠܝܚܐ ܫܡܗܐ ܐܝܬܝܗܘܢ ܗܠܝܢ ܩܕܡܝܗܘܢ ܫܡܥܘܢ ܕܡܬܩܪܐ ܟܐܦܐ ܘܐܢܕܪܐܘܤ ܐܚܘܗܝ ܘܝܥܩܘܒ ܒܪ ܙܒܕܝ ܘܝܘܚܢܢ ܐܚܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 3, 'ܘܦܝܠܝܦܘܤ ܘܒܪ ܬܘܠܡܝ ܘܬܐܘܡܐ ܘܡܬܝ ܡܟܤܐ ܘܝܥܩܘܒ ܒܪ ܚܠܦܝ ܘܠܒܝ ܕܐܬܟܢܝ ܬܕܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 4, 'ܘܫܡܥܘܢ ܩܢܢܝܐ ܘܝܗܘܕܐ ܤܟܪܝܘܛܐ ܗܘ ܕܐܫܠܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 5, 'ܠܗܠܝܢ ܬܪܥܤܪ ܫܕܪ ܝܫܘܥ ܘܦܩܕ ܐܢܘܢ ܘܐܡܪ ܒܐܘܪܚܐ ܕܚܢܦܐ ܠܐ ܬܐܙܠܘܢ ܘܠܡܕܝܢܬܐ ܕܫܡܪܝܐ ܠܐ ܬܥܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 6, 'ܙܠܘ ܠܟܘܢ ܕܝܢ ܝܬܝܪܐܝܬ ܠܘܬ ܥܪܒܐ ܕܐܒܕܘ ܡܢ ܒܝܬ ܝܤܪܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 7, 'ܘܟܕ ܐܙܠܝܢ ܐܢܬܘܢ ܐܟܪܙܘ ܘܐܡܪܘ ܕܩܪܒܬ ܡܠܟܘܬܐ ܕܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 8, 'ܟܪܝܗܐ ܐܤܘ ܘܓܪܒܐ ܕܟܘ ܘܕܝܘܐ ܐܦܩܘ ܡܓܢ ܢܤܒܬܘܢ ܡܓܢ ܗܒܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 9, 'ܠܐ ܬܩܢܘܢ ܕܗܒܐ ܘܠܐ ܤܐܡܐ ܘܠܐ ܢܚܫܐ ܒܟܝܤܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 10, 'ܘܠܐ ܬܪܡܠܐ ܠܐܘܪܚܐ ܘܠܐ ܬܪܬܝܢ ܟܘܬܝܢܝܢ ܘܠܐ ܡܤܢܐ ܘܠܐ ܫܒܛܐ ܫܘܐ ܗܘ ܓܝܪ ܦܥܠܐ ܤܝܒܪܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 11, 'ܠܐܝܕܐ ܕܝܢ ܡܕܝܢܬܐ ܐܘ ܩܪܝܬܐ ܕܥܐܠܝܢ ܐܢܬܘܢ ܠܗ ܫܐܠܘ ܡܢܘ ܫܘܐ ܒܗ ܘܬܡܢ ܗܘܘ ܥܕܡܐ ܕܢܦܩܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 12, 'ܘܡܐ ܕܥܐܠܝܢ ܐܢܬܘܢ ܠܒܝܬܐ ܫܐܠܘ ܫܠܡܗ ܕܒܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 13, 'ܘܐܢ ܗܘ ܕܫܘܐ ܒܝܬܐ ܫܠܡܟܘܢ ܢܐܬܐ ܥܠܘܗܝ ܐܢ ܕܝܢ ܠܐ ܫܘܐ ܫܠܡܟܘܢ ܥܠܝܟܘܢ ܢܦܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 14, 'ܡܢ ܕܠܐ ܕܝܢ ܡܩܒܠ ܠܟܘܢ ܘܠܐ ܫܡܥ ܡܠܝܟܘܢ ܟܕ ܢܦܩܝܢ ܐܢܬܘܢ ܡܢ ܒܝܬܐ ܐܘ ܡܢ ܩܪܝܬܐ ܗܝ ܦܨܘ ܚܠܐ ܡܢ ܪܓܠܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 15, 'ܘܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܠܐܪܥܐ ܕܤܕܘܡ ܘܕܥܡܘܪܐ ܢܗܘܐ ܢܝܚ ܒܝܘܡܐ ܕܕܝܢܐ ܐܘ ܠܡܕܝܢܬܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 16, 'ܗܐ ܐܢܐ ܡܫܕܪ ܐܢܐ ܠܟܘܢ ܐܝܟ ܐܡܪܐ ܒܝܢܝ ܕܐܒܐ ܗܘܘ ܗܟܝܠ ܚܟܝܡܐ ܐܝܟ ܚܘܘܬܐ ܘܬܡܝܡܐ ܐܝܟ ܝܘܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 17, 'ܐܙܕܗܪܘ ܕܝܢ ܡܢ ܒܢܝܢܫܐ ܡܫܠܡܝܢ ܠܟܘܢ ܓܝܪ ܠܒܝܬ ܕܝܢܐ ܘܒܟܢܘܫܬܗܘܢ ܢܢܓܕܘܢܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 18, 'ܘܩܕܡ ܗܓܡܘܢܐ ܘܡܠܟܐ ܡܩܪܒܝܢ ܠܟܘܢ ܡܛܠܬܝ ܠܤܗܕܘܬܐ ܕܝܠܗܘܢ ܘܕܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 19, 'ܐܡܬܝ ܕܝܢ ܕܢܫܠܡܘܢܟܘܢ ܠܐ ܬܐܨܦܘܢ ܐܝܟܢܐ ܐܘ ܡܢܐ ܬܡܠܠܘܢ ܡܬܝܗܒ ܠܟܘܢ ܓܝܪ ܒܗܝ ܫܥܬܐ ܡܐ ܕܬܡܠܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 20, 'ܠܐ ܗܘܐ ܓܝܪ ܐܢܬܘܢ ܡܡܠܠܝܢ ܐܠܐ ܪܘܚܐ ܕܐܒܘܟܘܢ ܡܡܠܠܐ ܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 21, 'ܢܫܠܡ ܕܝܢ ܐܚܐ ܠܐܚܘܗܝ ܠܡܘܬܐ ܘܐܒܐ ܠܒܪܗ ܘܢܩܘܡܘܢ ܒܢܝܐ ܥܠ ܐܒܗܝܗܘܢ ܘܢܡܝܬܘܢ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 22, 'ܘܬܗܘܘܢ ܤܢܝܐܝܢ ܡܢ ܟܠܢܫ ܡܛܠ ܫܡܝ ܐܝܢܐ ܕܝܢ ܕܢܤܝܒܪ ܥܕܡܐ ܠܚܪܬܐ ܗܘ ܢܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 23, 'ܡܐ ܕܪܕܦܝܢ ܠܟܘܢ ܕܝܢ ܒܡܕܝܢܬܐ ܗܕܐ ܥܪܘܩܘ ܠܟܘܢ ܠܐܚܪܬܐ ܐܡܝܢ ܓܝܪ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܠܐ ܬܫܠܡܘܢ ܐܢܝܢ ܟܠܗܝܢ ܡܕܝܢܬܐ ܕܒܝܬ ܐܝܤܪܝܠ ܥܕܡܐ ܕܢܐܬܐ ܒܪܗ ܕܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 24, 'ܠܝܬ ܬܠܡܝܕܐ ܕܝܬܝܪ ܡܢ ܪܒܗ ܘܠܐ ܥܒܕܐ ܡܢ ܡܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 25, 'ܤܦܩ ܠܗ ܠܬܠܡܝܕܐ ܕܢܗܘܐ ܐܝܟ ܪܒܗ ܘܠܥܒܕܐ ܐܝܟ ܡܪܗ ܐܢ ܠܡܪܗ ܕܒܝܬܐ ܩܪܘ ܒܥܠܙܒܘܒ ܚܕ ܟܡܐ ܠܒܢܝ ܒܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 26, 'ܠܐ ܗܟܝܠ ܬܕܚܠܘܢ ܡܢܗܘܢ ܠܝܬ ܓܝܪ ܡܕܡ ܕܟܤܐ ܕܠܐ ܢܬܓܠܐ ܘܕܡܛܫܝ ܕܠܐ ܢܬܝܕܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 27, 'ܡܕܡ ܕܐܡܪ ܐܢܐ ܠܟܘܢ ܒܚܫܘܟܐ ܐܘܡܪܘܗܝ ܐܢܬܘܢ ܒܢܗܝܪܐ ܘܡܕܡ ܕܒܐܕܢܝܟܘܢ ܫܡܥܝܢ ܐܢܬܘܢ ܐܟܪܙܘ ܥܠ ܐܓܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 28, 'ܘܠܐ ܬܕܚܠܘܢ ܡܢ ܐܝܠܝܢ ܕܩܛܠܝܢ ܦܓܪܐ ܢܦܫܐ ܕܝܢ ܠܐ ܡܫܟܚܝܢ ܠܡܩܛܠ ܕܚܠܘ ܕܝܢ ܝܬܝܪܐܝܬ ܡܢ ܡܢ ܕܡܫܟܚ ܕܠܢܦܫܐ ܘܠܦܓܪܐ ܢܘܒܕ ܒܓܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 29, 'ܠܐ ܬܪܬܝܢ ܨܦܪܝܢ ܡܙܕܒܢܢ ܒܐܤܪ ܘܚܕܐ ܡܢܗܝܢ ܒܠܥܕ ܡܢ ܐܒܘܟܘܢ ܠܐ ܢܦܠܐ ܥܠ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 30, 'ܕܝܠܟܘܢ ܕܝܢ ܐܦ ܡܢܐ ܕܪܫܟܘܢ ܟܠܗܝܢ ܡܢܝܢ ܐܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 31, 'ܠܐ ܗܟܝܠ ܬܕܚܠܘܢ ܡܢ ܨܦܪܐ ܤܓܝܐܬܐ ܡܝܬܪܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 32, 'ܟܠܢܫ ܗܟܝܠ ܕܢܘܕܐ ܒܝ ܩܕܡ ܒܢܝܢܫܐ ܐܘܕܐ ܒܗ ܐܦ ܐܢܐ ܩܕܡ ܐܒܝ ܕܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 33, 'ܡܢ ܕܝܢ ܕܢܟܦܘܪ ܒܝ ܩܕܡ ܒܢܝܢܫܐ ܐܟܦܘܪ ܒܗ ܐܦ ܐܢܐ ܩܕܡ ܐܒܝ ܕܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 34, 'ܠܐ ܬܤܒܪܘܢ ܕܐܬܝܬ ܕܐܪܡܐ ܫܝܢܐ ܒܐܪܥܐ ܠܐ ܐܬܝܬ ܕܐܪܡܐ ܫܝܢܐ ܐܠܐ ܚܪܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 35, 'ܐܬܝܬ ܓܝܪ ܕܐܦܠܘܓ ܓܒܪܐ ܥܠ ܐܒܘܗܝ ܘܒܪܬܐ ܥܠ ܐܡܗ ܘܟܠܬܐ ܥܠ ܚܡܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 36, 'ܘܒܥܠܕܒܒܘܗܝ ܕܓܒܪܐ ܒܢܝ ܒܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 37, 'ܡܢ ܕܪܚܡ ܐܒܐ ܐܘ ܐܡܐ ܝܬܝܪ ܡܢ ܕܠܝ ܠܐ ܫܘܐ ܠܝ ܘܡܢ ܕܪܚܡ ܒܪܐ ܐܘ ܒܪܬܐ ܝܬܝܪ ܡܢ ܕܠܝ ܠܐ ܫܘܐ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 38, 'ܘܟܠ ܕܠܐ ܫܩܠ ܙܩܝܦܗ ܘܐܬܐ ܒܬܪܝ ܠܐ ܫܘܐ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 39, 'ܡܢ ܕܐܫܟܚ ܢܦܫܗ ܢܘܒܕܝܗ ܘܡܢ ܕܢܘܒܕ ܢܦܫܗ ܡܛܠܬܝ ܢܫܟܚܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 40, 'ܡܢ ܕܡܩܒܠ ܠܟܘܢ ܠܝ ܡܩܒܠ ܘܡܢ ܕܠܝ ܡܩܒܠ ܠܡܢ ܕܫܠܚܢܝ ܡܩܒܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 41, 'ܡܢ ܕܡܩܒܠ ܢܒܝܐ ܒܫܡ ܢܒܝܐ ܐܓܪܐ ܕܢܒܝܐ ܢܤܒ ܘܡܢ ܕܡܩܒܠ ܙܕܝܩܐ ܒܫܡ ܙܕܝܩܐ ܐܓܪܐ ܕܙܕܝܩܐ ܢܤܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 42, 'ܘܟܠ ܕܡܫܩܐ ܠܚܕ ܡܢ ܗܠܝܢ ܙܥܘܪܐ ܟܤܐ ܕܩܪܝܪܐ ܒܠܚܘܕ ܒܫܡܐ ܕܬܠܡܝܕܐ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܠܐ ܢܘܒܕ ܐܓܪܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 1, 'ܘܗܘܐ ܕܟܕ ܫܠܡ ܝܫܘܥ ܠܡܦܩܕܘ ܠܬܪܥܤܪ ܬܠܡܝܕܘܗܝ ܫܢܝ ܡܢ ܬܡܢ ܠܡܠܦܘ ܘܠܡܟܪܙܘ ܒܡܕܝܢܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 2, 'ܝܘܚܢܢ ܕܝܢ ܟܕ ܫܡܥ ܒܝܬ ܐܤܝܪܐ ܥܒܕܘܗܝ ܕܡܫܝܚܐ ܫܕܪ ܒܝܕ ܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 3, 'ܘܐܡܪ ܠܗ ܐܢܬ ܗܘ ܗܘ ܕܐܬܐ ܐܘ ܠܐܚܪܝܢ ܗܘ ܡܤܟܝܢ ܐܢܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 4, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܙܠܘ ܐܫܬܥܘ ܠܝܘܚܢܢ ܐܝܠܝܢ ܕܫܡܥܝܢ ܐܢܬܘܢ ܘܚܙܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 5, 'ܤܡܝܐ ܚܙܝܢ ܘܚܓܝܪܐ ܡܗܠܟܝܢ ܘܓܪܒܐ ܡܬܕܟܝܢ ܘܚܪܫܐ ܫܡܥܝܢ ܘܡܝܬܐ ܩܝܡܝܢ ܘܡܤܟܢܐ ܡܤܬܒܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 6, 'ܘܛܘܒܘܗܝ ܠܐܝܢܐ ܕܠܐ ܢܬܟܫܠ ܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 7, 'ܟܕ ܕܝܢ ܐܙܠܘ ܫܪܝ ܝܫܘܥ ܠܡܐܡܪ ܠܟܢܫܐ ܥܠ ܝܘܚܢܢ ܡܢܐ ܢܦܩܬܘܢ ܠܚܘܪܒܐ ܠܡܚܙܐ ܩܢܝܐ ܕܡܢ ܪܘܚܐ ܡܬܬܙܝܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 8, 'ܘܐܠܐ ܡܢܐ ܢܦܩܬܘܢ ܠܡܚܙܐ ܓܒܪܐ ܕܢܚܬܐ ܪܟܝܟܐ ܠܒܝܫ ܗܐ ܐܝܠܝܢ ܕܪܟܝܟܐ ܠܒܝܫܝܢ ܒܝܬ ܡܠܟܐ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 9, 'ܘܐܠܐ ܡܢܐ ܢܦܩܬܘܢ ܠܡܚܙܐ ܢܒܝܐ ܐܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܘܝܬܝܪ ܡܢ ܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 10, 'ܗܢܘ ܓܝܪ ܕܥܠܘܗܝ ܟܬܝܒ ܕܗܐ ܐܢܐ ܡܫܕܪ ܐܢܐ ܡܠܐܟܝ ܩܕܡ ܦܪܨܘܦܟ ܕܢܬܩܢ ܐܘܪܚܐ ܩܕܡܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 11, 'ܐܡܝܢ ܐܡܪܢܐ ܠܟܘܢ ܕܠܐ ܩܡ ܒܝܠܝܕܝ ܢܫܐ ܕܪܒ ܡܢ ܝܘܚܢܢ ܡܥܡܕܢܐ ܙܥܘܪܐ ܕܝܢ ܒܡܠܟܘܬ ܫܡܝܐ ܪܒ ܗܘ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 12, 'ܡܢ ܝܘܡܝ ܝܘܚܢܢ ܕܝܢ ܡܥܡܕܢܐ ܘܥܕܡܐ ܠܗܫܐ ܡܠܟܘܬܐ ܕܫܡܝܐ ܒܩܛܝܪܐ ܡܬܕܒܪܐ ܘܩܛܝܪܢܐ ܡܚܛܦܝܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 13, 'ܟܠܗܘܢ ܓܝܪ ܢܒܝܐ ܘܐܘܪܝܬܐ ܥܕܡܐ ܠܝܘܚܢܢ ܐܬܢܒܝܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 14, 'ܘܐܢ ܨܒܝܢ ܐܢܬܘܢ ܩܒܠܘ ܕܗܘܝܘ ܐܠܝܐ ܕܥܬܝܕ ܠܡܐܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 15, 'ܡܢ ܕܐܝܬ ܠܗ ܐܕܢܐ ܕܢܫܡܥ ܢܫܡܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 16, 'ܠܡܢ ܕܝܢ ܐܕܡܝܗ ܠܫܪܒܬܐ ܗܕܐ ܕܡܝܐ ܠܛܠܝܐ ܕܝܬܒܝܢ ܒܫܘܩܐ ܘܩܥܝܢ ܠܚܒܪܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 17, 'ܘܐܡܪܝܢ ܙܡܪܢ ܠܟܘܢ ܘܠܐ ܪܩܕܬܘܢ ܘܐܠܝܢ ܠܟܘܢ ܘܠܐ ܐܪܩܕܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 18, 'ܐܬܐ ܓܝܪ ܝܘܚܢܢ ܕܠܐ ܐܟܠ ܘܠܐ ܫܬܐ ܘܐܡܪܝܢ ܕܝܘܐ ܐܝܬ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 19, 'ܐܬܐ ܒܪܗ ܕܐܢܫܐ ܐܟܠ ܘܫܬܐ ܘܐܡܪܝܢ ܗܐ ܓܒܪܐ ܐܟܘܠܐ ܘܫܬܐ ܚܡܪܐ ܘܪܚܡܐ ܕܡܟܤܐ ܘܕܚܛܝܐ ܘܐܙܕܕܩܬ ܚܟܡܬܐ ܡܢ ܥܒܕܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 20, 'ܗܝܕܝܢ ܫܪܝ ܝܫܘܥ ܠܡܚܤܕܘ ܡܕܝܢܬܐ ܐܝܠܝܢ ܕܗܘܘ ܒܗܝܢ ܚܝܠܘܗܝ ܤܓܝܐܐ ܘܠܐ ܬܒܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 21, 'ܘܐܡܪ ܗܘܐ ܘܝ ܠܟܝ ܟܘܪܙܝܢ ܘܝ ܠܟܝ ܒܝܬ ܨܝܕܐ ܕܐܠܘ ܒܨܘܪ ܘܒܨܝܕܢ ܗܘܘ ܚܝܠܐ ܐܝܠܝܢ ܕܗܘܘ ܒܟܝܢ ܟܒܪ ܕܝܢ ܒܤܩܐ ܘܒܩܛܡܐ ܬܒܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 22, 'ܒܪܡ ܐܡܪ ܐܢܐ ܠܟܝܢ ܕܠܨܘܪ ܘܠܨܝܕܢ ܢܗܘܐ ܢܝܚ ܒܝܘܡܐ ܕܕܝܢܐ ܐܘ ܠܟܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 23, 'ܘܐܢܬܝ ܟܦܪܢܚܘܡ ܗܝ ܕܥܕܡܐ ܠܫܡܝܐ ܐܬܬܪܝܡܬܝ ܥܕܡܐ ܠܫܝܘܠ ܬܬܚܬܝܢ ܕܐܠܘ ܒܤܕܘܡ ܗܘܘ ܚܝܠܐ ܐܝܠܝܢ ܕܗܘܘ ܒܟܝ ܩܝܡܐ ܗܘܬ ܥܕܡܐ ܠܝܘܡܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 24, 'ܒܪܡ ܐܡܪܢܐ ܠܟܝ ܕܠܐܪܥܐ ܕܤܕܘܡ ܢܗܘܐ ܢܝܚ ܒܝܘܡܐ ܕܕܝܢܐ ܐܘ ܠܟܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 25, 'ܒܗܘ ܙܒܢܐ ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܡܘܕܐ ܐܢܐ ܠܟ ܐܒܝ ܡܪܐ ܕܫܡܝܐ ܘܕܐܪܥܐ ܕܟܤܝܬ ܗܠܝܢ ܡܢ ܚܟܝܡܐ ܘܤܟܘܠܬܢܐ ܘܓܠܝܬ ܐܢܝܢ ܠܝܠܘܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 26, 'ܐܝܢ ܐܒܝ ܕܗܟܢܐ ܗܘܐ ܨܒܝܢܐ ܩܕܡܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 27, 'ܟܠ ܡܕܡ ܐܫܬܠܡ ܠܝ ܡܢ ܐܒܝ ܘܠܐ ܐܢܫ ܝܕܥ ܠܒܪܐ ܐܠܐ ܐܢ ܐܒܐ ܐܦ ܠܐ ܠܐܒܐ ܐܢܫ ܝܕܥ ܐܠܐ ܐܢ ܒܪܐ ܘܠܡܢ ܕܨܒܐ ܒܪܐ ܕܢܓܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 28, 'ܬܘ ܠܘܬܝ ܟܠܟܘܢ ܠܐܝܐ ܘܫܩܝܠܝ ܡܘܒܠܐ ܘܐܢܐ ܐܢܝܚܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 29, 'ܫܩܘܠܘ ܢܝܪܝ ܥܠܝܟܘܢ ܘܝܠܦܘ ܡܢܝ ܕܢܝܚ ܐܢܐ ܘܡܟܝܟ ܐܢܐ ܒܠܒܝ ܘܡܫܟܚܝܢ ܐܢܬܘܢ ܢܝܚܐ ܠܢܦܫܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 30, 'ܢܝܪܝ ܓܝܪ ܒܤܝܡ ܗܘ ܘܡܘܒܠܝ ܩܠܝܠܐ ܗܝ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 1, 'ܒܗܘ ܙܒܢܐ ܡܗܠܟ ܗܘܐ ܝܫܘܥ ܒܫܒܬܐ ܒܝܬ ܙܪܥܐ ܘܬܠܡܝܕܘܗܝ ܟܦܢܘ ܘܫܪܝܘ ܡܠܓܝܢ ܫܒܠܐ ܘܐܟܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 2, 'ܦܪܝܫܐ ܕܝܢ ܟܕ ܚܙܘ ܐܢܘܢ ܐܡܪܝܢ ܠܗ ܗܐ ܬܠܡܝܕܝܟ ܥܒܕܝܢ ܡܕܡ ܕܠܐ ܫܠܝܛ ܠܡܥܒܕ ܒܫܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 3, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܠܐ ܩܪܝܬܘܢ ܡܢܐ ܥܒܕ ܕܘܝܕ ܟܕ ܟܦܢ ܘܐܝܠܝܢ ܕܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 4, 'ܐܝܟܢܐ ܥܠ ܠܒܝܬܐ ܕܐܠܗܐ ܘܠܚܡܐ ܕܦܬܘܪܗ ܕܡܪܝܐ ܐܟܠ ܗܘ ܕܠܐ ܫܠܝܛ ܗܘܐ ܠܗ ܠܡܐܟܠ ܘܠܐ ܠܐܝܠܝܢ ܕܥܡܗ ܐܠܐ ܐܢ ܠܟܗܢܐ ܒܠܚܘܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 5, 'ܐܘ ܠܐ ܩܪܝܬܘܢ ܒܐܘܪܝܬܐ ܕܟܗܢܐ ܒܗܝܟܠܐ ܡܚܠܝܢ ܠܗ ܠܫܒܬܐ ܘܕܠܐ ܥܕܠܝ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 6, 'ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܝܢ ܕܪܒ ܡܢ ܗܝܟܠܐ ܐܝܬ ܗܪܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 7, 'ܐܠܘ ܕܝܢ ܝܕܥܝܢ ܗܘܝܬܘܢ ܡܢܘ ܚܢܢܐ ܨܒܐ ܐܢܐ ܘܠܐ ܕܒܚܬܐ ܠܐ ܡܚܝܒܝܢ ܗܘܝܬܘܢ ܠܐܝܠܝܢ ܕܕܠܐ ܥܕܠܝ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 8, 'ܡܪܗ ܓܝܪ ܕܫܒܬܐ ܐܝܬܘܗܝ ܒܪܗ ܕܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 9, 'ܘܫܢܝ ܡܢ ܬܡܢ ܝܫܘܥ ܘܐܬܐ ܠܟܢܘܫܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 10, 'ܘܓܒܪܐ ܚܕ ܐܝܬ ܗܘܐ ܬܡܢ ܕܝܒܝܫܐ ܐܝܕܗ ܘܡܫܐܠܝܢ ܗܘܘ ܠܗ ܘܐܡܪܝܢ ܕܐܢ ܫܠܝܛ ܒܫܒܬܐ ܠܡܐܤܝܘ ܐܝܟ ܕܢܐܟܠܘܢ ܩܪܨܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 11, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܡܢܘ ܡܢܟܘܢ ܓܒܪܐ ܕܐܝܬ ܠܗ ܥܪܒܐ ܚܕ ܘܐܢ ܢܦܠ ܒܚܒܪܐ ܒܝܘܡܐ ܕܫܒܬܐ ܠܐ ܐܚܕ ܘܡܩܝܡ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 12, 'ܟܡܐ ܕܝܢ ܝܬܝܪ ܒܪܢܫܐ ܡܢ ܥܪܒܐ ܡܕܝܢ ܫܠܝܛ ܗܘ ܒܫܒܬܐ ܠܡܥܒܕ ܕܫܦܝܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 13, 'ܗܝܕܝܢ ܐܡܪ ܠܗܘ ܓܒܪܐ ܦܫܘܛ ܐܝܕܟ ܘܦܫܛ ܐܝܕܗ ܘܬܩܢܬ ܐܝܟ ܚܒܪܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 14, 'ܘܢܦܩܘ ܦܪܝܫܐ ܘܡܠܟܐ ܢܤܒܘ ܥܠܘܗܝ ܐܝܟ ܕܢܘܒܕܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 15, 'ܝܫܘܥ ܕܝܢ ܝܕܥ ܘܫܢܝ ܠܗ ܡܢ ܬܡܢ ܘܐܙܠܘ ܒܬܪܗ ܟܢܫܐ ܤܓܝܐܐ ܘܐܤܝ ܠܟܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 16, 'ܘܟܐܐ ܒܗܘܢ ܕܠܐ ܢܓܠܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 17, 'ܕܢܬܡܠܐ ܡܕܡ ܕܐܬܐܡܪ ܒܝܕ ܐܫܥܝܐ ܢܒܝܐ ܕܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 18, 'ܗܐ ܥܒܕܝ ܕܐܨܛܒܝܬ ܒܗ ܚܒܝܒܝ ܕܤܘܚܬ ܒܗ ܢܦܫܝ ܪܘܚܝ ܐܤܝܡ ܥܠܘܗܝ ܘܕܝܢܐ ܠܥܡܡܐ ܢܟܪܙ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 19, 'ܠܐ ܢܬܚܪܐ ܘܠܐ ܢܩܥܐ ܘܠܐ ܐܢܫ ܢܫܡܥ ܩܠܗ ܒܫܘܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 20, 'ܩܢܝܐ ܪܥܝܥܐ ܠܐ ܢܬܒܪ ܘܫܪܓܐ ܕܡܛܦܛܦ ܠܐ ܢܕܥܟ ܥܕܡܐ ܕܢܦܩ ܕܝܢܐ ܠܙܟܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 21, 'ܘܒܫܡܗ ܥܡܡܐ ܢܤܒܪܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 22, 'ܗܝܕܝܢ ܩܪܒܘ ܠܗ ܕܝܘܢܐ ܚܕ ܕܚܪܫ ܘܥܘܝܪ ܘܐܤܝܗ ܐܝܟܢܐ ܕܚܪܫܐ ܘܤܡܝܐ ܢܡܠܠ ܘܢܚܙܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 23, 'ܘܡܬܕܡܪܝܢ ܗܘܘ ܟܠܗܘܢ ܟܢܫܐ ܘܐܡܪܝܢ ܕܠܡܐ ܗܢܘ ܒܪܗ ܕܕܘܝܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 24, 'ܦܪܝܫܐ ܕܝܢ ܟܕ ܫܡܥܘ ܐܡܪܝܢ ܗܢܐ ܠܐ ܡܦܩ ܫܐܕܐ ܐܠܐ ܒܒܥܠܙܒܘܒ ܪܫܐ ܕܕܝܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 25, 'ܝܫܘܥ ܕܝܢ ܝܕܥ ܡܚܫܒܬܗܘܢ ܘܐܡܪ ܠܗܘܢ ܟܠ ܡܠܟܘ ܕܬܬܦܠܓ ܥܠ ܢܦܫܗ ܬܚܪܒ ܘܟܠ ܒܝ ܘܡܕܝܢܐ ܕܢܬܦܠܓ ܥܠ ܢܦܫܗ ܠܐ ܢܩܘܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 26, 'ܘܐܢ ܤܛܢܐ ܠܤܛܢܐ ܡܦܩ ܥܠ ܢܦܫܗ ܐܬܦܠܓ ܐܝܟܢܐ ܗܟܝܠ ܩܝܡܐ ܡܠܟܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 27, 'ܘܐܢ ܐܢܐ ܒܒܥܠܙܒܘܒ ܡܦܩ ܐܢܐ ܕܝܘܐ ܒܢܝܟܘܢ ܒܡܢܐ ܡܦܩܝܢ ܠܗܘܢ ܡܛܠ ܗܢܐ ܗܢܘܢ ܢܗܘܘܢ ܠܟܘܢ ܕܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 28, 'ܘܐܢ ܒܪܘܚܐ ܕܐܠܗܐ ܐܢܐ ܡܦܩ ܐܢܐ ܕܝܘܐ ܩܪܒܬ ܠܗ ܥܠܝܟܘܢ ܡܠܟܘܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 29, 'ܐܘ ܐܝܟܢܐ ܐܢܫ ܡܫܟܚ ܕܢܥܘܠ ܠܒܝܬ ܚܤܝܢܐ ܘܡܐܢܘܗܝ ܢܒܘܙ ܐܠܐ ܐܢ ܠܘܩܕܡ ܢܐܤܪܝܘܗܝ ܠܚܤܝܢܐ ܘܗܝܕܝܢ ܒܝܬܗ ܢܒܘܙ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 30, 'ܡܢ ܕܠܐ ܗܘܐ ܥܡܝ ܠܘܩܒܠܝ ܗܘ ܘܡܢ ܕܠܐ ܟܢܫ ܥܡܝ ܡܒܕܪܘ ܡܒܕܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 31, 'ܡܛܠ ܗܢܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܟܠ ܚܛܗܝܢ ܘܓܘܕܦܝܢ ܢܫܬܒܩܘܢ ܠܒܢܝܢܫܐ ܓܘܕܦܐ ܕܝܢ ܕܥܠ ܪܘܚܐ ܠܐ ܢܫܬܒܩ ܠܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 32, 'ܘܟܠ ܡܢ ܕܢܐܡܪ ܡܠܬܐ ܥܠ ܒܪܗ ܕܐܢܫܐ ܢܫܬܒܩ ܠܗ ܟܠ ܕܝܢ ܕܥܠ ܪܘܚܐ ܕܩܘܕܫܐ ܢܐܡܪ ܠܐ ܢܫܬܒܩ ܠܗ ܠܐ ܒܥܠܡܐ ܗܢܐ ܘܠܐ ܒܥܠܡܐ ܕܥܬܝܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 33, 'ܐܘ ܥܒܕܘ ܐܝܠܢܐ ܫܦܝܪܐ ܘܦܐܪܘܗܝ ܫܦܝܪܐ ܐܘ ܥܒܕܘ ܐܝܠܢܐ ܒܝܫܐ ܘܦܐܪܘܗܝ ܒܝܫܐ ܡܢ ܦܐܪܘܗܝ ܗܘ ܓܝܪ ܡܬܝܕܥ ܐܝܠܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 34, 'ܝܠܕܐ ܕܐܟܕܢܐ ܐܝܟܢܐ ܡܫܟܚܝܢ ܐܢܬܘܢ ܛܒܬܐ ܠܡܡܠܠܘ ܕܒܝܫܐ ܐܢܬܘܢ ܡܢ ܬܘܬܪܝ ܠܒܐ ܓܝܪ ܡܡܠܠ ܦܘܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 35, 'ܓܒܪܐ ܛܒܐ ܡܢ ܤܝܡܬܐ ܛܒܬܐ ܡܦܩ ܛܒܬܐ ܘܓܒܪܐ ܒܝܫܐ ܡܢ ܤܝܡܬܐ ܒܝܫܬܐ ܡܦܩ ܒܝܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 36, 'ܐܡܪ ܐܢܐ ܠܟܘܢ ܓܝܪ ܕܟܠ ܡܠܐ ܒܛܠܐ ܕܢܐܡܪܘܢ ܒܢܝ ܐܢܫܐ ܢܬܠܘܢ ܦܬܓܡܗ ܒܝܘܡܐ ܕܕܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 37, 'ܡܢ ܡܠܝܟ ܓܝܪ ܬܙܕܕܩ ܘܡܢ ܡܠܝܟ ܬܬܚܝܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 38, 'ܗܝܕܝܢ ܥܢܘ ܐܢܫܐ ܡܢ ܤܦܪܐ ܘܡܢ ܦܪܝܫܐ ܘܐܡܪܝܢ ܠܗ ܡܠܦܢܐ ܨܒܝܢ ܚܢܢ ܕܢܚܙܐ ܡܢܟ ܐܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 39, 'ܗܘ ܕܝܢ ܥܢܐ ܘܐܡܪ ܠܗܘܢ ܫܪܒܬܐ ܒܝܫܬܐ ܘܓܝܪܬܐ ܐܬܐ ܒܥܝܐ ܘܐܬܐ ܠܐ ܬܬܝܗܒ ܠܗ ܐܠܐ ܐܬܗ ܕܝܘܢܢ ܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 40, 'ܐܝܟܢܐ ܓܝܪ ܕܗܘܐ ܝܘܢܢ ܒܟܪܤܗ ܕܢܘܢܐ ܬܠܬܐ ܝܘܡܝܢ ܘܬܠܬܐ ܠܝܠܘܢ ܗܟܢܐ ܢܗܘܐ ܒܪܗ ܕܐܢܫܐ ܒܠܒܗ ܕܐܪܥܐ ܬܠܬܐ ܐܝܡܡܝܢ ܘܬܠܬܐ ܠܝܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 41, 'ܓܒܪܐ ܢܝܢܘܝܐ ܢܩܘܡܘܢ ܒܕܝܢܐ ܥܡ ܫܪܒܬܐ ܗܕܐ ܘܢܚܝܒܘܢܗ ܕܗܢܘܢ ܬܒܘ ܒܟܪܘܙܘܬܗ ܕܝܘܢܢ ܘܗܐ ܕܪܒ ܡܢ ܝܘܢܢ ܬܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 42, 'ܡܠܟܬܐ ܕܬܝܡܢܐ ܬܩܘܡ ܒܕܝܢܐ ܥܡ ܫܪܒܬܐ ܗܕܐ ܘܬܚܝܒܝܗ ܕܐܬܬ ܡܢ ܥܒܪܝܗ ܕܐܪܥܐ ܕܬܫܡܥ ܚܟܡܬܗ ܕܫܠܝܡܘܢ ܘܗܐ ܕܝܬܝܪ ܡܢ ܫܠܝܡܘܢ ܗܪܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 43, 'ܐܡܬܝ ܕܝܢ ܕܪܘܚܐ ܛܢܦܬܐ ܬܦܘܩ ܡܢ ܒܪܢܫܐ ܡܬܟܪܟܐ ܒܐܬܪܘܬܐ ܕܡܝܐ ܠܝܬ ܒܗܘܢ ܘܒܥܝܐ ܢܝܚܐ ܘܠܐ ܡܫܟܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 44, 'ܗܝܕܝܢ ܐܡܪܐ ܐܗܦܘܟ ܠܒܝܬܝ ܡܢ ܐܝܟܐ ܕܢܦܩܬ ܘܐܬܝܐ ܡܫܟܚܐ ܕܤܪܝܩ ܘܚܡܝܡ ܘܡܨܒܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 45, 'ܗܝܕܝܢ ܐܙܠܐ ܕܒܪܐ ܥܡܗ ܫܒܥ ܪܘܚܐ ܐܚܪܢܝܢ ܕܡܢܗ ܒܝܫܢ ܘܥܐܠܢ ܘܥܡܪܢ ܒܗ ܘܗܘܝܐ ܚܪܬܗ ܕܓܒܪܐ ܗܘ ܒܝܫܐ ܡܢ ܩܕܡܝܬܗ ܗܟܢܐ ܢܗܘܐ ܠܗ ܠܫܪܒܬܐ ܗܕܐ ܒܝܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 46, 'ܟܕ ܗܘ ܕܝܢ ܡܡܠܠ ܠܟܢܫܐ ܐܬܘ ܐܡܗ ܘܐܚܘܗܝ ܩܝܡܝܢ ܠܒܪ ܘܒܥܝܢ ܕܢܡܠܠܘܢ ܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 47, 'ܐܡܪ ܠܗ ܕܝܢ ܐܢܫ ܗܐ ܐܡܟ ܘܐܚܝܟ ܩܝܡܝܢ ܠܒܪ ܘܒܥܝܢ ܕܢܡܠܠܘܢ ܥܡܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 48, 'ܗܘ ܕܝܢ ܥܢܐ ܘܐܡܪ ܠܡܢ ܕܐܡܪ ܠܗ ܡܢ ܗܝ ܐܡܝ ܘܡܢ ܐܢܘܢ ܐܚܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 49, 'ܘܦܫܛ ܐܝܕܗ ܠܘܬ ܬܠܡܝܕܘܗܝ ܘܐܡܪ ܗܐ ܐܡܝ ܘܗܐ ܐܚܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 50, 'ܟܠܢܫ ܓܝܪ ܕܥܒܕ ܨܒܝܢܗ ܕܐܒܝ ܕܒܫܡܝܐ ܗܘܝܘ ܐܚܝ ܘܚܬܝ ܘܐܡܝ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 1, 'ܒܗܘ ܕܝܢ ܝܘܡܐ ܢܦܩ ܝܫܘܥ ܡܢ ܒܝܬܐ ܘܝܬܒ ܥܠ ܝܕ ܝܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 2, 'ܘܐܬܟܢܫܘ ܠܘܬܗ ܟܢܫܐ ܤܓܝܐܐ ܐܝܟ ܕܢܤܩ ܢܬܒ ܠܗ ܒܐܠܦܐ ܘܟܠܗ ܟܢܫܐ ܩܐܡ ܗܘܐ ܥܠ ܤܦܪ ܝܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 3, 'ܘܤܓܝ ܡܡܠܠ ܗܘܐ ܥܡܗܘܢ ܒܦܠܐܬܐ ܘܐܡܪ ܗܐ ܢܦܩ ܙܪܘܥܐ ܕܢܙܪܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 4, 'ܘܟܕ ܙܪܥ ܐܝܬ ܕܢܦܠ ܥܠ ܝܕ ܐܘܪܚܐ ܘܐܬܬ ܦܪܚܬܐ ܘܐܟܠܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 5, 'ܘܐܚܪܢܐ ܢܦܠ ܥܠ ܫܘܥܐ ܐܝܟܐ ܕܠܝܬ ܗܘܐ ܡܕܪܐ ܤܓܝܐܐ ܘܒܪ ܫܥܬܗ ܫܘܚ ܡܛܠ ܕܠܝܬ ܗܘܐ ܥܘܡܩܐ ܕܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 6, 'ܟܕ ܕܢܚ ܕܝܢ ܫܡܫܐ ܚܡ ܘܡܛܠ ܕܠܝܬ ܗܘܐ ܠܗ ܥܩܪܐ ܝܒܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 7, 'ܘܐܚܪܢܐ ܢܦܠ ܒܝܬ ܟܘܒܐ ܘܤܠܩܘ ܟܘܒܐ ܘܚܢܩܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 8, 'ܘܐܚܪܢܐ ܢܦܠ ܒܐܪܥܐ ܛܒܬܐ ܘܝܗܒ ܦܐܪܐ ܐܝܬ ܕܡܐܐ ܘܐܝܬ ܕܫܬܝܢ ܘܐܝܬ ܕܬܠܬܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 9, 'ܡܢ ܕܐܝܬ ܠܗ ܐܕܢܐ ܕܢܫܡܥ ܢܫܡܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 10, 'ܘܩܪܒܘ ܬܠܡܝܕܘܗܝ ܘܐܡܪܝܢ ܠܗ ܠܡܢܐ ܒܦܠܐܬܐ ܡܡܠܠ ܐܢܬ ܥܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 11, 'ܗܘ ܕܝܢ ܥܢܐ ܘܐܡܪ ܠܗܘܢ ܕܠܟܘܢ ܗܘ ܝܗܝܒ ܠܡܕܥ ܐܪܙܐ ܕܡܠܟܘܬܐ ܕܫܡܝܐ ܠܗܢܘܢ ܕܝܢ ܠܐ ܝܗܝܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 12, 'ܠܡܢ ܓܝܪ ܕܐܝܬ ܠܗ ܢܬܝܗܒ ܠܗ ܘܢܬܝܬܪ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 13, 'ܘܠܡܢ ܕܠܝܬ ܠܗ ܘܗܘ ܕܐܝܬ ܠܗ ܢܫܬܩܠ ܡܢܗ ܡܛܠ ܗܢܐ ܒܦܠܐܬܐ ܡܡܠܠ ܐܢܐ ܥܡܗܘܢ ܡܛܠ ܕܚܙܝܢ ܘܠܐ ܚܙܝܢ ܘܫܡܥܝܢ ܘܠܐ ܫܡܥܝܢ ܘܠܐ ܡܤܬܟܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 14, 'ܘܫܠܡܐ ܒܗܘܢ ܢܒܝܘܬܗ ܕܐܫܥܝܐ ܕܐܡܪ ܕܫܡܥܐ ܬܫܡܥܘܢ ܘܠܐ ܬܤܬܟܠܘܢ ܘܡܚܙܐ ܬܚܙܘܢ ܘܠܐ ܬܕܥܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 15, 'ܐܬܥܒܝ ܠܗ ܓܝܪ ܠܒܗ ܕܥܡܐ ܗܢܐ ܘܒܐܕܢܝܗܘܢ ܝܩܝܪܐܝܬ ܫܡܥܘ ܘܥܝܢܝܗܘܢ ܥܡܨܘ ܕܠܐ ܢܚܙܘܢ ܒܥܝܢܝܗܘܢ ܘܢܫܡܥܘܢ ܒܐܕܢܝܗܘܢ ܘܢܤܬܟܠܘܢ ܒܠܒܗܘܢ ܘܢܬܦܢܘܢ ܘܐܤܐ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 16, 'ܕܝܠܟܘܢ ܕܝܢ ܛܘܒܝܗܝܢ ܠܥܝܢܝܟܘܢ ܕܚܙܝܢ ܘܠܐܕܢܝܟܘܢ ܕܫܡܥܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 17, 'ܐܡܝܢ ܓܝܪ ܐܡܪܢܐ ܠܟܘܢ ܕܤܓܝܐܐ ܢܒܝܐ ܘܙܕܝܩܐ ܐܬܪܓܪܓܘ ܕܢܚܙܘܢ ܡܕܡ ܕܚܙܝܢ ܐܢܬܘܢ ܘܠܐ ܚܙܘ ܘܠܡܫܡܥ ܡܕܡ ܕܫܡܥܝܢ ܐܢܬܘܢ ܘܠܐ ܫܡܥܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 18, 'ܐܢܬܘܢ ܕܝܢ ܫܡܥܘ ܡܬܠܐ ܕܙܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 19, 'ܟܠ ܕܫܡܥ ܡܠܬܐ ܕܡܠܟܘܬܐ ܘܠܐ ܡܤܬܟܠ ܒܗ ܐܬܐ ܒܝܫܐ ܘܚܛܦ ܡܠܬܐ ܕܙܪܝܥܐ ܒܠܒܗ ܗܢܘ ܗܘ ܕܥܠ ܝܕ ܐܘܪܚܐ ܐܙܕܪܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 20, 'ܗܘ ܕܝܢ ܕܥܠ ܫܘܥܐ ܐܙܕܪܥ ܗܘ ܗܘ ܕܫܡܥ ܡܠܬܐ ܘܒܪ ܫܥܬܗ ܒܚܕܘܬܐ ܡܩܒܠ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 21, 'ܠܝܬ ܠܗ ܕܝܢ ܥܩܪܐ ܒܗ ܐܠܐ ܕܙܒܢܐ ܗܘ ܘܡܐ ܕܗܘܐ ܐܘܠܨܢܐ ܐܘ ܪܕܘܦܝܐ ܡܛܠ ܡܠܬܐ ܥܓܠ ܡܬܟܫܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 22, 'ܗܘ ܕܝܢ ܕܒܝܬ ܟܘܒܐ ܐܙܕܪܥ ܗܘ ܗܘ ܕܫܡܥ ܡܠܬܐ ܘܪܢܝܐ ܕܥܠܡܐ ܗܢܐ ܘܛܘܥܝܝ ܕܥܘܬܪܐ ܚܢܩܝܢ ܠܗ ܠܡܠܬܐ ܘܕܠܐ ܦܐܪܐ ܗܘܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 23, 'ܗܘ ܕܝܢ ܕܥܠ ܐܪܥܐ ܛܒܬܐ ܐܙܕܪܥ ܗܘ ܗܘ ܕܫܡܥ ܡܠܬܝ ܘܡܤܬܟܠ ܘܝܗܒ ܦܐܪܐ ܘܥܒܕ ܐܝܬ ܕܡܐܐ ܘܐܝܬ ܕܫܬܝܢ ܘܐܝܬ ܕܬܠܬܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 24, 'ܐܚܪܢܐ ܡܬܠܐ ܐܡܬܠ ܠܗܘܢ ܘܐܡܪ ܕܡܝܐ ܡܠܟܘܬܐ ܕܫܡܝܐ ܠܓܒܪܐ ܕܙܪܥ ܙܪܥܐ ܛܒܐ ܒܩܪܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 25, 'ܘܟܕ ܕܡܟܘ ܐܢܫܐ ܐܬܐ ܒܥܠܕܒܒܗ ܘܙܪܥ ܙܝܙܢܐ ܒܝܢܬ ܚܛܐ ܘܐܙܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 26, 'ܟܕ ܕܝܢ ܝܥܐ ܥܤܒܐ ܘܥܒܕ ܦܐܪܐ ܗܝܕܝܢ ܐܬܚܙܝܘ ܐܦ ܙܝܙܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 27, 'ܘܩܪܒܘ ܥܒܕܘܗܝ ܕܡܪܐ ܒܝܬܐ ܘܐܡܪܘ ܠܗ ܡܪܢ ܠܐ ܗܐ ܙܪܥܐ ܛܒܐ ܙܪܥܬ ܒܩܪܝܬܟ ܡܢ ܐܝܡܟܐ ܐܝܬ ܒܗ ܙܝܙܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 28, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܓܒܪܐ ܒܥܠܕܒܒܐ ܥܒܕ ܗܕܐ ܐܡܪܝܢ ܠܗ ܥܒܕܘܗܝ ܨܒܐ ܐܢܬ ܢܐܙܠ ܢܓܒܐ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 29, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܕܠܡܐ ܟܕ ܡܓܒܝܢ ܐܢܬܘܢ ܙܝܙܢܐ ܬܥܩܪܘܢ ܥܡܗܘܢ ܐܦ ܚܛܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 30, 'ܫܒܘܩܘ ܪܒܝܢ ܬܪܝܗܘܢ ܐܟܚܕܐ ܥܕܡܐ ܠܚܨܕܐ ܘܒܙܒܢܐ ܕܚܨܕܐ ܐܡܪ ܐܢܐ ܠܚܨܘܕܐ ܓܒܘ ܠܘܩܕܡ ܙܝܙܢܐ ܘܐܤܘܪܘ ܐܢܘܢ ܡܐܤܪܝܬܐ ܕܢܐܩܕܘܢ ܚܛܐ ܕܝܢ ܟܢܫܘ ܐܢܝܢ ܠܐܘܨܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 31, 'ܐܚܪܢܐ ܡܬܠܐ ܐܡܬܠ ܠܗܘܢ ܘܐܡܪ ܕܡܝܐ ܡܠܟܘܬܐ ܕܫܡܝܐ ܠܦܪܕܬܐ ܕܚܪܕܠܐ ܕܢܤܒ ܓܒܪܐ ܙܪܥܗ ܒܩܪܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 32, 'ܘܗܝ ܙܥܘܪܝܐ ܗܝ ܡܢ ܟܠܗܘܢ ܙܪܥܘܢܐ ܡܐ ܕܝܢ ܕܪܒܬ ܪܒܐ ܗܝ ܡܢ ܟܠܗܘܢ ܝܪܩܘܢܐ ܘܗܘܝܐ ܐܝܠܢܐ ܐܝܟ ܕܬܐܬܐ ܦܪܚܬܐ ܕܫܡܝܐ ܬܩܢ ܒܤܘܟܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 33, 'ܐܚܪܢܐ ܡܬܠܐ ܐܡܪ ܠܗܘܢ ܕܡܝܐ ܡܠܟܘܬܐ ܕܫܡܝܐ ܠܚܡܝܪܐ ܗܘ ܕܫܩܠܬ ܐܢܬܬܐ ܛܡܪܬ ܒܬܠܬ ܤܐܝܢ ܕܩܡܚܐ ܥܕܡܐ ܕܟܠܗ ܚܡܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 34, 'ܗܠܝܢ ܟܠܗܝܢ ܡܠܠ ܝܫܘܥ ܒܦܠܐܬܐ ܠܟܢܫܐ ܘܕܠܐ ܦܠܐܬܐ ܠܐ ܡܡܠܠ ܗܘܐ ܥܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 35, 'ܐܝܟ ܕܢܬܡܠܐ ܡܕܡ ܕܐܬܐܡܪ ܒܝܕ ܢܒܝܐ ܕܐܡܪ ܐܦܬܚ ܦܘܡܝ ܒܡܬܠܐ ܘܐܒܥ ܟܤܝܬܐ ܕܡܢ ܩܕܡ ܬܪܡܝܬܗ ܕܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 36, 'ܗܝܕܝܢ ܝܫܘܥ ܫܒܩ ܠܟܢܫܐ ܘܐܬܐ ܠܒܝܬܐ ܘܩܪܒܘ ܠܘܬܗ ܬܠܡܝܕܘܗܝ ܘܐܡܪܝܢ ܠܗ ܦܫܩ ܠܢ ܡܬܠܐ ܗܘ ܕܙܝܙܢܐ ܘܕܩܪܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 37, 'ܗܘ ܕܝܢ ܥܢܐ ܘܐܡܪ ܠܗܘܢ ܗܘ ܕܙܪܥ ܙܪܥܐ ܛܒܐ ܐܝܬܘܗܝ ܒܪܗ ܕܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 38, 'ܘܩܪܝܬܐ ܐܝܬܝܗ ܥܠܡܐ ܙܪܥܐ ܕܝܢ ܛܒܐ ܒܢܝܗ ܐܢܘܢ ܕܡܠܟܘܬܐ ܙܝܙܢܐ ܕܝܢ ܐܝܬܝܗܘܢ ܒܢܘܗܝ ܕܒܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 39, 'ܒܥܠܕܒܒܐ ܕܝܢ ܕܙܪܥ ܐܢܘܢ ܐܝܬܘܗܝ ܤܛܢܐ ܚܨܕܐ ܕܝܢ ܐܝܬܘܗܝ ܫܘܠܡܗ ܕܥܠܡܐ ܚܨܘܕܐ ܕܝܢ ܡܠܐܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 40, 'ܐܝܟܢܐ ܗܟܝܠ ܕܡܬܓܒܝܢ ܙܝܙܢܐ ܘܝܩܕܝܢ ܒܢܘܪܐ ܗܟܢܐ ܢܗܘܐ ܒܫܘܠܡܗ ܕܥܠܡܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 41, 'ܢܫܕܪ ܒܪܗ ܕܐܢܫܐ ܡܠܐܟܘܗܝ ܘܢܓܒܘܢ ܡܢ ܡܠܟܘܬܗ ܟܠܗܘܢ ܡܟܫܘܠܐ ܘܟܠܗܘܢ ܥܒܕܝ ܥܘܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 42, 'ܘܢܪܡܘܢ ܐܢܘܢ ܒܐܬܘܢܐ ܕܢܘܪܐ ܬܡܢ ܢܗܘܐ ܒܟܝܐ ܘܚܘܪܩ ܫܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 43, 'ܗܝܕܝܢ ܙܕܝܩܐ ܢܢܗܪܘܢ ܐܝܟ ܫܡܫܐ ܒܡܠܟܘܬܗ ܕܐܒܘܗܘܢ ܡܢ ܕܐܝܬ ܠܗ ܐܕܢܐ ܕܢܫܡܥ ܢܫܡܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 44, 'ܬܘܒ ܕܡܝܐ ܡܠܟܘܬܐ ܕܫܡܝܐ ܠܤܝܡܬܐ ܕܡܛܫܝܐ ܒܩܪܝܬܐ ܗܝ ܕܐܫܟܚܗ ܓܒܪܐ ܘܛܫܝܗ ܘܡܢ ܚܕܘܬܗ ܐܙܠ ܙܒܢ ܟܠ ܕܐܝܬ ܠܗ ܘܙܒܢܗ ܠܩܪܝܬܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 45, 'ܬܘܒ ܕܡܝܐ ܡܠܟܘܬܐ ܕܫܡܝܐ ܠܓܒܪܐ ܬܓܪܐ ܕܒܥܐ ܗܘܐ ܡܪܓܢܝܬܐ ܛܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 46, 'ܟܕ ܕܝܢ ܐܫܟܚ ܡܪܓܢܝܬܐ ܚܕܐ ܝܩܝܪܬ ܕܡܝܐ ܐܙܠ ܙܒܢ ܟܠ ܡܐ ܕܐܝܬ ܠܗ ܘܙܒܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 47, 'ܬܘܒ ܕܡܝܐ ܡܠܟܘܬܐ ܕܫܡܝܐ ܠܡܨܝܕܬܐ ܕܢܦܠܬ ܒܝܡܐ ܘܡܢ ܟܠ ܓܢܤ ܟܢܫܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 48, 'ܘܟܕ ܡܠܬ ܐܤܩܘܗ ܠܤܦܪܝ ܝܡܐ ܘܝܬܒܘ ܓܒܝܘ ܘܛܒܐ ܐܪܡܝܘ ܒܡܐܢܐ ܘܒܝܫܐ ܫܕܘ ܠܒܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 49, 'ܗܟܢܐ ܢܗܘܐ ܒܫܘܠܡܗ ܕܥܠܡܐ ܢܦܩܘܢ ܡܠܐܟܐ ܘܢܦܪܫܘܢ ܒܝܫܐ ܡܢ ܒܝܢܝ ܙܕܝܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 50, 'ܘܢܪܡܘܢ ܐܢܘܢ ܒܐܬܘܢܐ ܕܢܘܪܐ ܬܡܢ ܢܗܘܐ ܒܟܝܐ ܘܚܘܪܩ ܫܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 51, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܤܬܟܠܬܘܢ ܟܠܗܝܢ ܗܠܝܢ ܐܡܪܝܢ ܠܗ ܐܝܢ ܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 52, 'ܐܡܪ ܠܗܘܢ ܡܛܠ ܗܢܐ ܟܠ ܤܦܪܐ ܕܡܬܬܠܡܕ ܠܡܠܟܘܬ ܫܡܝܐ ܕܡܐ ܠܓܒܪܐ ܡܪܐ ܒܝܬܐ ܕܡܦܩ ܡܢ ܤܝܡܬܗ ܚܕܬܬܐ ܘܥܬܝܩܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 53, 'ܘܗܘܐ ܕܟܕ ܫܠܡ ܝܫܘܥ ܡܬܠܐ ܗܠܝܢ ܫܢܝ ܡܢ ܬܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 54, 'ܘܐܬܐ ܠܡܕܝܢܬܗ ܘܡܠܦ ܗܘܐ ܠܗܘܢ ܒܟܢܘܫܬܗܘܢ ܐܝܟܢܐ ܕܢܬܗܪܘܢ ܘܢܐܡܪܘܢ ܐܝܡܟܐ ܠܗ ܠܗܢܐ ܚܟܡܬܐ ܗܕܐ ܘܚܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 55, 'ܠܐ ܗܘܐ ܗܢܐ ܒܪܗ ܕܢܓܪܐ ܠܐ ܐܡܗ ܡܬܩܪܝܐ ܡܪܝܡ ܘܐܚܘܗܝ ܝܥܩܘܒ ܘܝܘܤܐ ܘܫܡܥܘܢ ܘܝܗܘܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 56, 'ܘܐܚܘܬܗ ܟܠܗܝܢ ܠܐ ܗܐ ܠܘܬܢ ܐܢܝܢ ܐܝܡܟܐ ܠܗ ܗܟܝܠ ܠܗܢܐ ܗܠܝܢ ܟܠܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 57, 'ܘܡܬܟܫܠܝܢ ܗܘܘ ܒܗ ܗܘ ܕܝܢ ܝܫܘܥ ܐܡܪ ܠܗܘܢ ܠܝܬ ܢܒܝܐ ܕܨܥܝܪ ܐܠܐ ܒܡܕܝܢܬܗ ܘܒܒܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 58, 'ܘܠܐ ܥܒܕ ܬܡܢ ܚܝܠܐ ܤܓܝܐܐ ܡܛܠ ܠܐ ܗܝܡܢܘܬܗܘܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 1, 'ܒܗܘ ܕܝܢ ܙܒܢܐ ܫܡܥ ܗܪܘܕܤ ܛܛܪܪܟܐ ܫܡܥܗ ܕܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 2, 'ܘܐܡܪ ܠܥܒܕܘܗܝ ܗܢܘ ܝܘܚܢܢ ܡܥܡܕܢܐ ܗܘ ܩܡ ܡܢ ܒܝܬ ܡܝܬܐ ܡܛܠ ܗܢܐ ܚܝܠܐ ܡܤܬܥܪܝܢ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 3, 'ܗܘ ܓܝܪ ܗܪܘܕܤ ܐܚܕ ܗܘܐ ܠܝܘܚܢܢ ܘܐܤܪܗ ܘܐܪܡܝܗ ܒܝܬ ܐܤܝܪܐ ܡܛܠ ܗܪܘܕܝܐ ܐܢܬܬ ܦܝܠܝܦܘܤ ܐܚܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 4, 'ܐܡܪ ܗܘܐ ܠܗ ܓܝܪ ܝܘܚܢܢ ܕܠܐ ܫܠܝܛ ܕܬܗܘܐ ܠܟ ܐܢܬܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 5, 'ܘܨܒܐ ܗܘܐ ܠܡܩܛܠܗ ܘܕܚܠ ܗܘܐ ܡܢ ܥܡܐ ܕܐܝܟ ܕܠܢܒܝܐ ܐܚܝܕܝܢ ܗܘܘ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 6, 'ܟܕ ܗܘܐ ܕܝܢ ܒܝܬ ܝܠܕܗ ܕܗܪܘܕܤ ܪܩܕܬ ܒܪܬܗ ܕܗܪܘܕܝܐ ܩܕܡ ܤܡܝܟܐ ܘܫܦܪܬ ܠܗ ܠܗܪܘܕܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 7, 'ܡܛܠ ܗܢܐ ܒܡܘܡܬܐ ܝܡܐ ܠܗ ܕܢܬܠ ܠܗ ܟܠܡܕܡ ܕܬܫܐܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 8, 'ܗܝ ܕܝܢ ܡܛܠ ܕܡܠܦܐ ܗܘܬ ܠܐܡܗ ܐܡܪܬ ܗܒ ܠܝ ܗܪܟܐ ܒܦܝܢܟܐ ܪܫܗ ܕܝܘܚܢܢ ܡܥܡܕܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 9, 'ܘܟܪܝܬ ܠܗ ܠܡܠܟܐ ܡܛܠ ܕܝܢ ܡܘܡܬܐ ܘܤܡܝܟܐ ܦܩܕ ܕܢܬܝܗܒ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 10, 'ܘܫܕܪ ܦܤܩܗ ܪܫܗ ܕܝܘܚܢܢ ܒܝܬ ܐܤܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 11, 'ܘܐܝܬܝ ܪܫܗ ܒܦܝܢܟܐ ܘܐܬܝܗܒ ܠܛܠܝܬܐ ܘܐܝܬܝܬܗ ܠܐܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 12, 'ܘܩܪܒܘ ܬܠܡܝܕܘܗܝ ܫܩܠܘ ܫܠܕܗ ܩܒܪܘ ܘܐܬܘ ܚܘܝܘ ܠܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 13, 'ܝܫܘܥ ܕܝܢ ܟܕ ܫܡܥ ܫܢܝ ܡܢ ܬܡܢ ܒܐܠܦܐ ܠܐܬܪܐ ܚܘܪܒܐ ܒܠܚܘܕܘܗܝ ܘܟܕ ܫܡܥܘ ܟܢܫܐ ܐܙܠܘ ܒܬܪܗ ܒܝܒܫܐ ܡܢ ܡܕܝܢܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 14, 'ܘܢܦܩ ܝܫܘܥ ܚܙܐ ܟܢܫܐ ܤܓܝܐܐ ܘܐܬܪܚܡ ܥܠܝܗܘܢ ܘܐܤܝ ܟܪܝܗܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 15, 'ܟܕ ܗܘܐ ܕܝܢ ܪܡܫܐ ܩܪܒܘ ܠܘܬܗ ܬܠܡܝܕܘܗܝ ܘܐܡܪܘ ܠܗ ܐܬܪܐ ܚܘܪܒܐ ܗܘ ܘܥܕܢܐ ܥܒܪ ܠܗ ܫܪܝ ܟܢܫܐ ܕܐܢܫܐ ܕܢܐܙܠܘܢ ܠܩܘܪܝܐ ܘܢܙܒܢܘܢ ܠܗܘܢ ܤܝܒܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 16, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܠܐ ܡܬܒܥܐ ܠܗܘܢ ܠܡܐܙܠ ܗܒܘ ܠܗܘܢ ܐܢܬܘܢ ܠܡܐܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 17, 'ܗܢܘܢ ܕܝܢ ܐܡܪܘ ܠܗ ܠܝܬ ܠܢ ܬܢܢ ܐܠܐ ܚܡܫ ܓܪܝܨܢ ܘܬܪܝܢ ܢܘܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 18, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܝܬܘ ܐܢܘܢ ܠܝ ܠܗܪܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 19, 'ܘܦܩܕ ܠܟܢܫܐ ܠܡܤܬܡܟܘ ܥܠ ܐܪܥܐ ܘܫܩܠ ܗܢܘܢ ܚܡܫܐ ܠܚܡܝܢ ܘܬܪܝܢ ܢܘܢܝܢ ܘܚܪ ܒܫܡܝܐ ܘܒܪܟ ܘܩܨܐ ܘܝܗܒ ܠܬܠܡܝܕܘܗܝ ܘܗܢܘܢ ܬܠܡܝܕܐ ܤܡܘ ܠܟܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 20, 'ܘܐܟܠܘ ܟܠܗܘܢ ܘܤܒܥܘ ܘܫܩܠܘ ܬܘܬܪܐ ܕܩܨܝܐ ܬܪܥܤܪ ܩܘܦܝܢܝܢ ܟܕ ܡܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 21, 'ܗܢܘܢ ܕܝܢ ܐܢܫܐ ܕܐܟܠܘ ܗܘܝܢ ܗܘܘ ܐܠܦܐ ܚܡܫܐ ܤܛܪ ܡܢ ܢܫܐ ܘܛܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 22, 'ܘܡܚܕܐ ܐܠܨ ܠܬܠܡܝܕܘܗܝ ܕܢܤܩܘܢ ܠܤܦܝܢܬܐ ܘܢܐܙܠܘܢ ܩܕܡܘܗܝ ܠܥܒܪܐ ܥܕ ܫܪܐ ܗܘ ܠܟܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 23, 'ܘܟܕ ܫܪܐ ܠܟܢܫܐ ܤܠܩ ܠܛܘܪܐ ܒܠܚܘܕܘܗܝ ܠܡܨܠܝܘ ܘܟܕ ܚܫܟܬ ܒܠܚܘܕܘܗܝ ܗܘܐ ܬܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 24, 'ܘܐܠܦܐ ܪܚܝܩܐ ܗܘܬ ܡܢ ܐܪܥܐ ܐܤܛܕܘܬܐ ܤܓܝܐܐ ܟܕ ܡܫܬܓܫܐ ܤܓܝ ܡܢ ܓܠܠܐ ܪܘܚܐ ܓܝܪ ܠܩܘܒܠܗ ܗܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 25, 'ܒܡܛܪܬܐ ܕܝܢ ܪܒܝܥܝܬܐ ܕܠܠܝܐ ܐܬܐ ܠܘܬܗܘܢ ܝܫܘܥ ܟܕ ܡܗܠܟ ܥܠ ܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 26, 'ܘܚܙܐܘܗܝ ܬܠܡܝܕܘܗܝ ܕܡܗܠܟ ܥܠ ܡܝܐ ܘܐܬܬܙܝܥܘ ܘܐܡܪܝܢ ܗܘܘ ܕܚܙܘܐ ܗܘ ܕܓܠܐ ܘܡܢ ܕܚܠܬܗܘܢ ܩܥܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 27, 'ܗܘ ܕܝܢ ܝܫܘܥ ܒܪܫܥܬܗ ܡܠܠ ܥܡܗܘܢ ܘܐܡܪ ܐܬܠܒܒܘ ܐܢܐ ܐܢܐ ܠܐ ܬܕܚܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 28, 'ܘܥܢܐ ܟܐܦܐ ܘܐܡܪ ܠܗ ܡܪܝ ܐܢ ܐܢܬ ܗܘ ܦܩܘܕ ܠܝ ܐܬܐ ܠܘܬܟ ܥܠ ܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 29, 'ܝܫܘܥ ܕܝܢ ܐܡܪ ܠܗ ܬܐ ܘܢܚܬ ܟܐܦܐ ܡܢ ܐܠܦܐ ܘܗܠܟ ܥܠ ܡܝܐ ܕܢܐܬܐ ܠܘܬ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 30, 'ܘܟܕ ܚܙܐ ܪܘܚܐ ܕܩܫܝܐ ܕܚܠ ܘܫܪܝ ܠܡܛܒܥ ܘܐܪܝܡ ܩܠܗ ܘܐܡܪ ܡܪܝ ܦܪܘܩܝܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 31, 'ܘܒܪ ܫܥܬܗ ܦܫܛ ܐܝܕܗ ܡܪܢ ܘܐܚܕܗ ܘܐܡܪ ܠܗ ܙܥܘܪ ܗܝܡܢܘܬܐ ܠܡܢܐ ܐܬܦܠܓܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 32, 'ܘܟܕ ܤܠܩܘ ܠܐܠܦܐ ܫܠܝܬ ܪܘܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 33, 'ܘܐܬܘ ܗܢܘܢ ܕܒܐܠܦܐ ܤܓܕܘ ܠܗ ܘܐܡܪܘ ܫܪܝܪܐܝܬ ܒܪܗ ܐܢܬ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 34, 'ܘܪܕܘ ܘܐܬܘ ܠܐܪܥܐ ܕܓܢܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 35, 'ܘܐܫܬܘܕܥܘܗܝ ܐܢܫܐ ܕܐܬܪܐ ܗܘ ܘܫܕܪܘ ܠܟܠܗܝܢ ܩܘܪܝܐ ܕܚܕܪܝܗܘܢ ܘܩܪܒܘ ܠܗ ܟܠܗܘܢ ܐܝܠܝܢ ܕܒܝܫ ܒܝܫ ܥܒܝܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 36, 'ܘܒܥܝܢ ܗܘܘ ܡܢܗ ܕܢܩܪܒܘܢ ܐܦܢ ܠܟܢܦܐ ܒܠܚܘܕ ܕܠܒܘܫܗ ܘܐܝܠܝܢ ܕܩܪܒܘ ܐܬܐܤܝܘ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 1, 'ܗܝܕܝܢ ܩܪܒܘ ܠܘܬ ܝܫܘܥ ܦܪܝܫܐ ܘܤܦܪܐ ܕܡܢ ܐܘܪܫܠܡ ܘܐܡܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 2, 'ܠܡܢܐ ܬܠܡܝܕܝܟ ܥܒܪܝܢ ܥܠ ܡܫܠܡܢܘܬܐ ܕܩܫܝܫܐ ܘܠܐ ܡܫܝܓܝܢ ܐܝܕܝܗܘܢ ܡܐ ܕܐܟܠܝܢ ܠܚܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 3, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܠܡܢܐ ܐܦ ܐܢܬܘܢ ܥܒܪܝܢ ܐܢܬܘܢ ܥܠ ܦܘܩܕܢܐ ܕܐܠܗܐ ܡܛܠ ܡܫܠܡܢܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 4, 'ܐܠܗܐ ܓܝܪ ܐܡܪ ܝܩܪ ܠܐܒܘܟ ܘܠܐܡܟ ܘܡܢ ܕܡܨܚܐ ܠܐܒܘܗܝ ܘܠܐܡܗ ܡܡܬ ܢܡܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 5, 'ܐܢܬܘܢ ܕܝܢ ܐܡܪܝܢ ܐܢܬܘܢ ܟܠ ܡܢ ܕܢܐܡܪ ܠܐܒܐ ܐܘ ܠܐܡܐ ܩܘܪܒܢܝ ܡܕܡ ܕܬܬܗܢܐ ܡܢܝ ܘܠܐ ܢܝܩܪ ܠܐܒܘܗܝ ܐܘ ܠܐܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 6, 'ܘܒܛܠܬܘܢ ܡܠܬܐ ܕܐܠܗܐ ܡܛܠ ܡܫܠܡܢܘܬܐ ܕܝܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 7, 'ܢܤܒܝ ܒܐܦܐ ܫܦܝܪ ܐܬܢܒܝ ܥܠܝܟܘܢ ܐܫܥܝܐ ܘܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 8, 'ܥܡܐ ܗܢܐ ܒܤܦܘܬܗ ܗܘ ܡܝܩܪ ܠܝ ܠܒܗܘܢ ܕܝܢ ܤܓܝ ܪܚܝܩ ܡܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 9, 'ܘܤܪܝܩܐܝܬ ܕܚܠܝܢ ܠܝ ܟܕ ܡܠܦܝܢ ܝܘܠܦܢܐ ܕܦܘܩܕܢܐ ܕܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 10, 'ܘܩܪܐ ܠܟܢܫܐ ܘܐܡܪ ܠܗܘܢ ܫܡܥܘ ܘܐܤܬܟܠܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 11, 'ܠܐ ܗܘܐ ܡܕܡ ܕܥܐܠ ܠܦܘܡܐ ܡܤܝܒ ܠܒܪܢܫܐ ܐܠܐ ܡܕܡ ܕܢܦܩ ܡܢ ܦܘܡܐ ܗܘ ܗܘ ܡܤܝܒ ܠܒܪܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 12, 'ܗܝܕܝܢ ܩܪܒܘ ܬܠܡܝܕܘܗܝ ܘܐܡܪܝܢ ܠܗ ܝܕܥ ܐܢܬ ܕܦܪܝܫܐ ܕܫܡܥܘ ܡܠܬܐ ܗܕܐ ܐܬܟܫܠܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 13, 'ܗܘ ܕܝܢ ܥܢܐ ܘܐܡܪ ܠܗܘܢ ܟܠ ܢܨܒܬܐ ܐܝܕܐ ܕܠܐ ܢܨܒܗ ܐܒܝ ܕܒܫܡܝܐ ܬܬܥܩܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 14, 'ܫܒܘܩܘ ܠܗܘܢ ܤܡܝܐ ܐܢܘܢ ܢܓܘܕܐ ܕܤܡܝܐ ܤܡܝܐ ܕܝܢ ܠܤܡܝܐ ܐܢ ܢܕܒܪ ܬܪܝܗܘܢ ܒܓܘܡܨܐ ܢܦܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 15, 'ܘܥܢܐ ܫܡܥܘܢ ܟܐܦܐ ܘܐܡܪ ܠܗ ܡܪܝ ܦܫܩ ܠܢ ܡܬܠܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 16, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܥܕܡܐ ܠܗܫܐ ܐܦ ܐܢܬܘܢ ܠܐ ܡܤܬܟܠܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 17, 'ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܕܡܕܡ ܕܥܐܠ ܠܦܘܡܐ ܠܟܪܤܐ ܗܘ ܐܙܠ ܘܡܢ ܬܡܢ ܒܬܕܟܝܬܐ ܡܫܬܕܐ ܠܒܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 18, 'ܡܕܡ ܕܝܢ ܕܡܢ ܦܘܡܐ ܢܦܩ ܡܢ ܠܒܐ ܢܦܩ ܘܗܘܝܘ ܡܤܝܒ ܠܗ ܠܒܪܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 19, 'ܡܢ ܠܒܐ ܗܘ ܓܝܪ ܢܦܩܢ ܡܚܫܒܬܐ ܒܝܫܬܐ ܓܘܪܐ ܩܛܠܐ ܙܢܝܘܬܐ ܓܢܒܘܬܐ ܤܗܕܘܬ ܫܘܩܪܐ ܓܘܕܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 20, 'ܗܠܝܢ ܐܢܝܢ ܕܡܤܝܒܢ ܠܒܪܢܫܐ ܐܢ ܐܢܫ ܕܝܢ ܢܠܥܤ ܟܕ ܠܐ ܡܫܓܢ ܐܝܕܘܗܝ ܠܐ ܡܤܬܝܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 21, 'ܘܢܦܩ ܡܢ ܬܡܢ ܝܫܘܥ ܘܐܬܐ ܠܬܚܘܡܐ ܕܨܘܪ ܘܕܨܝܕܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 22, 'ܘܗܐ ܐܢܬܬܐ ܟܢܥܢܝܬܐ ܡܢ ܬܚܘܡܐ ܗܢܘܢ ܢܦܩܬ ܟܕ ܩܥܝܐ ܘܐܡܪܐ ܐܬܪܚܡ ܥܠܝ ܡܪܝ ܒܪܗ ܕܕܘܝܕ ܒܪܬܝ ܒܝܫܐܝܬ ܡܬܕܒܪܐ ܡܢ ܫܐܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 23, 'ܗܘ ܕܝܢ ܠܐ ܦܢܝܗ ܦܬܓܡܐ ܘܩܪܒܘ ܬܠܡܝܕܘܗܝ ܒܥܘ ܡܢܗ ܘܐܡܪܝܢ ܫܪܝܗ ܕܩܥܝܐ ܒܬܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 24, 'ܗܘ ܕܝܢ ܥܢܐ ܘܐܡܪ ܠܗܘܢ ܠܐ ܐܫܬܕܪܬ ܐܠܐ ܠܘܬ ܥܪܒܐ ܕܛܥܘ ܡܢ ܒܝܬ ܐܝܤܪܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 25, 'ܗܝ ܕܝܢ ܐܬܬ ܤܓܕܬ ܠܗ ܘܐܡܪܬ ܡܪܝ ܥܕܪܝܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 26, 'ܐܡܪ ܠܗ ܠܐ ܫܦܝܪ ܠܡܤܒ ܠܚܡܐ ܕܒܢܝܐ ܘܠܡܪܡܝܘ ܠܟܠܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 27, 'ܗܝ ܕܝܢ ܐܡܪܬ ܐܝܢ ܡܪܝ ܐܦ ܟܠܒܐ ܐܟܠܝܢ ܡܢ ܦܪܬܘܬܐ ܕܢܦܠܝܢ ܡܢ ܦܬܘܪܐ ܕܡܪܝܗܘܢ ܘܚܐܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 28, 'ܗܝܕܝܢ ܐܡܪ ܠܗ ܝܫܘܥ ܐܘ ܐܢܬܬܐ ܪܒܐ ܗܝ ܗܝܡܢܘܬܟܝ ܢܗܘܐ ܠܟܝ ܐܝܟ ܕܨܒܝܐ ܐܢܬܝ ܘܐܬܐܤܝܬ ܒܪܬܗ ܡܢ ܗܝ ܫܥܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 29, 'ܘܫܢܝ ܡܢ ܬܡܢ ܝܫܘܥ ܘܐܬܐ ܥܠ ܓܢܒ ܝܡܐ ܕܓܠܝܠܐ ܘܤܠܩ ܠܛܘܪܐ ܘܝܬܒ ܬܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 30, 'ܘܩܪܒܘ ܠܘܬܗ ܟܢܫܐ ܤܓܝܐܐ ܕܐܝܬ ܗܘܘ ܥܡܗܘܢ ܚܓܝܪܐ ܘܤܡܝܐ ܘܚܪܫܐ ܘܦܫܝܓܐ ܘܐܚܪܢܐ ܤܓܝܐܐ ܘܐܪܡܝܘ ܐܢܘܢ ܠܘܬ ܪܓܠܘܗܝ ܕܝܫܘܥ ܘܐܤܝ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 31, 'ܐܝܟ ܕܢܬܕܡܪܘܢ ܟܢܫܐ ܗܢܘܢ ܕܚܙܝܢ ܚܪܫܐ ܕܡܡܠܠܝܢ ܘܦܫܝܓܐ ܕܡܬܚܠܡܝܢ ܘܚܓܝܪܐ ܕܡܗܠܟܝܢ ܘܤܡܝܐ ܕܚܙܝܢ ܘܫܒܚܘ ܠܐܠܗܐ ܕܝܤܪܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 32, 'ܗܘ ܕܝܢ ܝܫܘܥ ܩܪܐ ܠܬܠܡܝܕܘܗܝ ܘܐܡܪ ܠܗܘܢ ܡܬܪܚܡ ܐܢܐ ܥܠ ܟܢܫܐ ܗܢܐ ܕܗܐ ܬܠܬܐ ܝܘܡܝܢ ܩܘܝܘ ܠܘܬܝ ܘܠܝܬ ܠܗܘܢ ܡܐ ܕܢܐܟܠܘܢ ܘܕܐܫܪܐ ܐܢܘܢ ܟܕ ܨܝܡܝܢ ܠܐ ܨܒܐ ܐܢܐ ܕܠܡܐ ܢܥܘܦܘܢ ܒܐܘܪܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 33, 'ܐܡܪܝܢ ܠܗ ܬܠܡܝܕܘܗܝ ܐܝܡܟܐ ܠܢ ܒܚܘܪܒܐ ܠܚܡܐ ܕܢܤܒܥ ܟܢܫܐ ܗܢܐ ܟܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 34, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܟܡܐ ܠܚܡܝܢ ܐܝܬ ܠܟܘܢ ܐܡܪܝܢ ܠܗ ܫܒܥܐ ܘܩܠܝܠ ܢܘܢܐ ܕܩܕܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 35, 'ܘܦܩܕ ܠܟܢܫܐ ܕܢܤܬܡܟܘܢ ܥܠ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 36, 'ܘܫܩܠ ܠܗܠܝܢ ܫܒܥܐ ܠܚܡܝܢ ܘܠܢܘܢܐ ܘܫܒܚ ܘܩܨܐ ܘܝܗܒ ܠܬܠܡܝܕܘܗܝ ܘܬܠܡܝܕܐ ܝܗܒܘ ܠܟܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 37, 'ܘܐܟܠܘ ܟܠܗܘܢ ܘܤܒܥܘ ܘܫܩܠܘ ܬܘܬܪܐ ܕܩܨܝܐ ܡܠܐ ܫܒܥܐ ܐܤܦܪܝܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 38, 'ܗܢܘܢ ܕܝܢ ܕܐܟܠܘ ܗܘܝܢ ܗܘܘ ܐܪܒܥܐ ܐܠܦܝܢ ܓܒܪܐ ܤܛܪ ܡܢ ܢܫܐ ܘܛܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 39, 'ܘܟܕ ܫܪܐ ܠܟܢܫܐ ܤܠܩ ܠܐܠܦܐ ܘܐܬܐ ܠܬܚܘܡܐ ܕܡܓܕܘ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 1, 'ܘܩܪܒܘ ܦܪܝܫܐ ܘܙܕܘܩܝܐ ܡܢܤܝܢ ܠܗ ܘܫܐܠܝܢ ܠܗ ܐܬܐ ܡܢ ܫܡܝܐ ܕܢܚܘܐ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 2, 'ܗܘ ܕܝܢ ܥܢܐ ܘܐܡܪ ܠܗܘܢ ܡܐ ܕܗܘܐ ܪܡܫܐ ܐܡܪܝܢ ܐܢܬܘܢ ܨܚܘܐ ܗܘ ܤܡܩܬ ܓܝܪ ܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 3, 'ܘܒܨܦܪܐ ܐܡܪܝܢ ܐܢܬܘܢ ܝܘܡܢܐ ܤܬܘܐ ܗܘ ܤܡܩܬ ܓܝܪ ܫܡܝܐ ܟܡܝܪܐܝܬ ܢܤܒܝ ܒܐܦܐ ܦܪܨܘܦܐ ܕܫܡܝܐ ܝܕܥܝܢ ܐܢܬܘܢ ܕܬܒܩܘܢ ܐܬܘܬܐ ܕܙܒܢܐ ܗܢܐ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܕܬܦܪܫܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 4, 'ܫܪܒܬܐ ܒܝܫܬܐ ܘܓܝܪܬܐ ܐܬܐ ܒܥܝܐ ܘܐܬܐ ܠܐ ܡܬܝܗܒܐ ܠܗ ܐܠܐ ܐܬܗ ܕܝܘܢܢ ܢܒܝܐ ܘܫܒܩ ܐܢܘܢ ܘܐܙܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 5, 'ܘܟܕ ܐܬܘ ܬܠܡܝܕܘܗܝ ܠܥܒܪܐ ܛܥܘ ܕܢܤܒܘܢ ܥܡܗܘܢ ܠܚܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 6, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܚܙܘ ܐܙܕܗܪܘ ܡܢ ܚܡܝܪܐ ܕܦܪܝܫܐ ܘܕܙܕܘܩܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 7, 'ܗܢܘܢ ܕܝܢ ܡܬܪܥܝܢ ܗܘܘ ܒܢܦܫܗܘܢ ܘܐܡܪܝܢ ܕܠܚܡܐ ܠܐ ܢܤܒܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 8, 'ܝܫܘܥ ܕܝܢ ܝܕܥ ܘܐܡܪ ܠܗܘܢ ܡܢܐ ܡܬܚܫܒܝܢ ܐܢܬܘܢ ܒܢܦܫܟܘܢ ܙܥܘܪܝ ܗܝܡܢܘܬܐ ܕܠܚܡܐ ܠܐ ܫܩܠܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 9, 'ܠܐ ܥܕܡܐ ܠܗܫܐ ܐܤܬܟܠܬܘܢ ܠܐ ܥܗܕܝܢ ܐܢܬܘܢ ܠܗܢܘܢ ܚܡܫܐ ܠܚܡܝܢ ܕܚܡܫܐ ܐܠܦܝܢ ܘܟܡܐ ܩܘܦܝܢܝܢ ܫܩܠܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 10, 'ܘܠܐ ܠܗܢܘܢ ܫܒܥܐ ܠܚܡܝܢ ܕܐܪܒܥܐ ܐܠܦܝܢ ܘܟܡܐ ܐܤܦܪܝܕܝܢ ܫܩܠܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 11, 'ܐܝܟܢ ܠܐ ܐܤܬܟܠܬܘܢ ܕܠܐ ܗܘܐ ܥܠ ܠܚܡܐ ܐܡܪܬ ܠܟܘܢ ܐܠܐ ܕܬܙܕܗܪܘܢ ܡܢ ܚܡܝܪܐ ܕܦܪܝܫܐ ܘܕܙܕܘܩܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 12, 'ܗܝܕܝܢ ܐܤܬܟܠܘ ܕܠܐ ܐܡܪ ܕܢܙܕܗܪܘܢ ܡܢ ܚܡܝܪܐ ܕܠܚܡܐ ܐܠܐ ܡܢ ܝܘܠܦܢܐ ܕܦܪܝܫܐ ܘܕܙܕܘܩܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 13, 'ܟܕ ܕܝܢ ܐܬܐ ܝܫܘܥ ܠܐܬܪܐ ܕܩܤܪܝܐ ܕܦܝܠܝܦܘܤ ܡܫܐܠ ܗܘܐ ܠܬܠܡܝܕܘܗܝ ܘܐܡܪ ܡܢܘ ܐܡܪܝܢ ܥܠܝ ܐܢܫܐ ܕܐܝܬܝ ܒܪܗ ܕܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 14, 'ܗܢܘܢ ܕܝܢ ܐܡܪܘ ܐܝܬ ܕܐܡܪܝܢ ܝܘܚܢܢ ܡܥܡܕܢܐ ܐܚܪܢܐ ܕܝܢ ܐܠܝܐ ܘܐܚܪܢܐ ܐܪܡܝܐ ܐܘ ܚܕ ܡܢ ܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 15, 'ܐܡܪ ܠܗܘܢ ܐܢܬܘܢ ܕܝܢ ܡܢܘ ܐܡܪܝܢ ܐܢܬܘܢ ܕܐܝܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 16, 'ܥܢܐ ܫܡܥܘܢ ܟܐܦܐ ܘܐܡܪ ܐܢܬ ܗܘ ܡܫܝܚܐ ܒܪܗ ܕܐܠܗܐ ܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 17, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗ ܛܘܒܝܟ ܫܡܥܘܢ ܒܪܗ ܕܝܘܢܐ ܕܒܤܪܐ ܘܕܡܐ ܠܐ ܓܠܐ ܠܟ ܐܠܐ ܐܒܝ ܕܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 18, 'ܐܦ ܐܢܐ ܐܡܪ ܐܢܐ ܠܟ ܕܐܢܬ ܗܘ ܟܐܦܐ ܘܥܠ ܗܕܐ ܟܐܦܐ ܐܒܢܝܗ ܠܥܕܬܝ ܘܬܪܥܐ ܕܫܝܘܠ ܠܐ ܢܚܤܢܘܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 19, 'ܠܟ ܐܬܠ ܩܠܝܕܐ ܕܡܠܟܘܬܐ ܕܫܡܝܐ ܘܟܠ ܡܕܡ ܕܬܐܤܘܪ ܒܐܪܥܐ ܢܗܘܐ ܐܤܝܪ ܒܫܡܝܐ ܘܡܕܡ ܕܬܫܪܐ ܒܐܪܥܐ ܢܗܘܐ ܫܪܐ ܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 20, 'ܗܝܕܝܢ ܦܩܕ ܠܬܠܡܝܕܘܗܝ ܕܠܐܢܫ ܠܐ ܢܐܡܪܘܢ ܕܗܘܝܘ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 21, 'ܘܡܢ ܗܝܕܝܢ ܫܪܝ ܝܫܘܥ ܠܡܚܘܝܘ ܠܬܠܡܝܕܘܗܝ ܕܥܬܝܕ ܗܘ ܕܢܐܙܠ ܠܐܘܪܫܠܡ ܘܤܓܝ ܢܚܫ ܡܢ ܩܫܝܫܐ ܘܡܢ ܪܒܝ ܟܗܢܐ ܘܤܦܪܐ ܘܢܬܩܛܠ ܘܠܝܘܡܐ ܕܬܠܬܐ ܢܩܘܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 22, 'ܘܕܒܪܗ ܟܐܦܐ ܘܫܪܝ ܠܡܟܐܐ ܒܗ ܘܐܡܪ ܚܤ ܠܟ ܡܪܝ ܕܬܗܘܐ ܠܟ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 23, 'ܗܘ ܕܝܢ ܐܬܦܢܝ ܘܐܡܪ ܠܟܐܦܐ ܙܠ ܠܟ ܠܒܤܬܪܝ ܤܛܢܐ ܬܘܩܠܬܐ ܐܢܬ ܠܝ ܕܠܐ ܡܬܪܥܐ ܐܢܬ ܕܐܠܗܐ ܐܠܐ ܕܒܢܝ ܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 24, 'ܗܝܕܝܢ ܐܡܪ ܝܫܘܥ ܠܬܠܡܝܕܘܗܝ ܡܢ ܕܨܒܐ ܕܢܐܬܐ ܒܬܪܝ ܢܟܦܘܪ ܒܢܦܫܗ ܘܢܫܩܘܠ ܙܩܝܦܗ ܘܢܐܬܐ ܒܬܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 25, 'ܡܢ ܕܨܒܐ ܓܝܪ ܕܢܚܐ ܢܦܫܗ ܢܘܒܕܝܗ ܘܡܢ ܕܢܘܒܕ ܢܦܫܗ ܡܛܠܬܝ ܢܫܟܚܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 26, 'ܡܢܐ ܓܝܪ ܡܬܗܢܐ ܒܪܢܫܐ ܐܢ ܟܠܗ ܥܠܡܐ ܢܩܢܐ ܘܢܦܫܗ ܢܚܤܪ ܐܘ ܡܢܐ ܢܬܠ ܒܪܢܫܐ ܬܚܠܘܦܐ ܕܢܦܫܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 27, 'ܥܬܝܕ ܗܘ ܓܝܪ ܒܪܗ ܕܐܢܫܐ ܕܢܐܬܐ ܒܬܫܒܘܚܬܐ ܕܐܒܘܗܝ ܥܡ ܡܠܐܟܘܗܝ ܩܕܝܫܐ ܘܗܝܕܝܢ ܢܦܪܘܥ ܠܐܢܫ ܐܢܫ ܐܝܟ ܥܒܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 28, 'ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܐܝܬ ܐܢܫܐ ܕܩܝܡܝܢ ܬܢܢ ܕܠܐ ܢܛܥܡܘܢ ܡܘܬܐ ܥܕܡܐ ܕܢܚܙܘܢ ܠܒܪܗ ܕܐܢܫܐ ܕܐܬܐ ܒܡܠܟܘܬܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 1, 'ܘܒܬܪ ܫܬܐ ܝܘܡܝܢ ܕܒܪ ܝܫܘܥ ܠܟܐܦܐ ܘܠܝܥܩܘܒ ܘܠܝܘܚܢܢ ܐܚܘܗܝ ܘܐܤܩ ܐܢܘܢ ܠܛܘܪܐ ܪܡܐ ܒܠܚܘܕܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 2, 'ܘܐܫܬܚܠܦ ܝܫܘܥ ܩܕܡܝܗܘܢ ܘܢܗܪ ܦܪܨܘܦܗ ܐܝܟ ܫܡܫܐ ܢܚܬܘܗܝ ܕܝܢ ܚܘܪܘ ܐܝܟ ܢܘܗܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 3, 'ܘܐܬܚܙܝܘ ܠܗܘܢ ܡܘܫܐ ܘܐܠܝܐ ܟܕ ܡܡܠܠܝܢ ܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 4, 'ܥܢܐ ܕܝܢ ܟܐܦܐ ܘܐܡܪ ܠܝܫܘܥ ܡܪܝ ܫܦܝܪ ܗܘ ܠܢ ܕܬܢܢ ܢܗܘܐ ܘܐܢ ܨܒܐ ܐܢܬ ܢܥܒܕ ܬܢܢ ܬܠܬ ܡܛܠܝܢ ܚܕܐ ܠܟ ܘܚܕܐ ܠܡܘܫܐ ܘܚܕܐ ܠܐܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 5, 'ܘܥܕ ܗܘ ܡܡܠܠ ܗܐ ܥܢܢܐ ܢܗܝܪܬܐ ܐܛܠܬ ܥܠܝܗܘܢ ܘܩܠܐ ܗܘܐ ܡܢ ܥܢܢܐ ܕܐܡܪ ܗܢܘ ܒܪܝ ܚܒܝܒܐ ܕܒܗ ܐܨܛܒܝܬ ܠܗ ܫܡܥܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 6, 'ܘܟܕ ܫܡܥܘ ܬܠܡܝܕܐ ܢܦܠܘ ܥܠ ܐܦܝܗܘܢ ܘܕܚܠܘ ܛܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 7, 'ܘܐܬܩܪܒ ܠܘܬܗܘܢ ܝܫܘܥ ܘܩܪܒ ܠܗܘܢ ܘܐܡܪ ܩܘܡܘ ܠܐ ܬܕܚܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 8, 'ܘܐܪܝܡܘ ܥܝܢܝܗܘܢ ܘܠܐܢܫ ܠܐ ܚܙܘ ܐܠܐ ܐܢ ܠܝܫܘܥ ܒܠܚܘܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 9, 'ܘܟܕ ܢܚܬܝܢ ܡܢ ܛܘܪܐ ܦܩܕ ܐܢܘܢ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܠܥܝܢ ܐܢܫ ܠܐ ܬܐܡܪܘܢ ܚܙܘܐ ܗܢܐ ܥܕܡܐ ܕܢܩܘܡ ܒܪܗ ܕܐܢܫܐ ܡܢ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 10, 'ܘܫܐܠܘܗܝ ܬܠܡܝܕܘܗܝ ܘܐܡܪܝܢ ܠܗ ܡܢܐ ܗܟܝܠ ܤܦܪܐ ܐܡܪܝܢ ܕܐܠܝܐ ܘܠܐ ܕܢܐܬܐ ܠܘܩܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 11, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܐܠܝܐ ܐܬܐ ܠܘܩܕܡ ܕܟܠ ܡܕܡ ܢܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 12, 'ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܝܢ ܕܗܐ ܐܠܝܐ ܐܬܐ ܘܠܐ ܝܕܥܘܗܝ ܘܥܒܕܘ ܒܗ ܟܠ ܡܐ ܕܨܒܘ ܗܟܢܐ ܐܦ ܒܪܗ ܕܐܢܫܐ ܥܬܝܕ ܕܢܚܫ ܡܢܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 13, 'ܗܝܕܝܢ ܐܤܬܟܠܘ ܬܠܡܝܕܐ ܕܥܠ ܝܘܚܢܢ ܡܥܡܕܢܐ ܐܡܪ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 14, 'ܘܟܕ ܐܬܘ ܠܘܬ ܟܢܫܐ ܩܪܒ ܠܗ ܓܒܪܐ ܘܒܪܟ ܥܠ ܒܘܪܟܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 15, 'ܘܐܡܪ ܠܗ ܡܪܝ ܐܬܪܚܡ ܥܠܝ ܒܪܝ ܕܐܝܬ ܠܗ ܒܪ ܐܓܪܐ ܘܒܝܫܐܝܬ ܥܒܝܕ ܟܡܐ ܓܝܪ ܙܒܢܝܢ ܒܢܘܪܐ ܢܦܠ ܘܟܡܐ ܙܒܢܝܢ ܒܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 16, 'ܘܩܪܒܬܗ ܠܬܠܡܝܕܝܟ ܘܠܐ ܐܫܟܚܘ ܠܡܐܤܝܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 17, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܐܘܢ ܫܪܒܬܐ ܕܠܐ ܡܗܝܡܢܐ ܘܡܥܩܠܬܐ ܥܕܡܐ ܠܐܡܬܝ ܐܗܘܐ ܥܡܟܘܢ ܘܥܕܡܐ ܠܐܡܬܝ ܐܤܝܒܪܟܘܢ ܐܝܬܝܗܝ ܠܝ ܠܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 18, 'ܘܟܐܐ ܒܗ ܝܫܘܥ ܘܢܦܩ ܡܢܗ ܫܐܕܐ ܘܐܬܐܤܝ ܛܠܝܐ ܡܢ ܗܝ ܫܥܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 19, 'ܗܝܕܝܢ ܩܪܒܘ ܬܠܡܝܕܐ ܠܘܬ ܝܫܘܥ ܒܠܚܘܕܘܗܝ ܘܐܡܪܘ ܠܗ ܠܡܢܐ ܚܢܢ ܠܐ ܐܫܟܚܢ ܠܡܐܤܝܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 20, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܡܛܠ ܠܐ ܗܝܡܢܘܬܟܘܢ ܐܡܝܢ ܓܝܪ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܐܢ ܬܗܘܐ ܒܟܘܢ ܗܝܡܢܘܬܐ ܐܝܟ ܦܪܕܬܐ ܕܚܪܕܠܐ ܬܐܡܪܘܢ ܠܛܘܪܐ ܗܢܐ ܕܫܢܐ ܡܟܐ ܘܢܫܢܐ ܘܡܕܡ ܠܐ ܢܚܤܢܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 21, 'ܗܢܐ ܕܝܢ ܓܢܤܐ ܠܐ ܢܦܩ ܐܠܐ ܒܨܘܡܐ ܘܒܨܠܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 22, 'ܟܕ ܡܬܗܦܟܝܢ ܕܝܢ ܒܓܠܝܠܐ ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܥܬܝܕ ܗܘ ܒܪܗ ܕܐܢܫܐ ܕܢܫܬܠܡ ܒܐܝܕܝ ܒܢܝ ܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 23, 'ܘܢܩܛܠܘܢܝܗܝ ܘܠܝܘܡܐ ܕܬܠܬܐ ܢܩܘܡ ܘܟܪܝܬ ܠܗܘܢ ܛܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 24, 'ܘܟܕ ܐܬܘ ܠܟܦܪܢܚܘܡ ܩܪܒܘ ܗܢܘܢ ܕܢܤܒܝܢ ܬܪܝܢ ܬܪܝܢ ܙܘܙܝܢ ܕܟܤܦ ܪܫܐ ܠܘܬ ܟܐܦܐ ܘܐܡܪܘ ܠܗ ܪܒܟܘܢ ܠܐ ܝܗܒ ܬܪܝܢ ܙܘܙܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 25, 'ܐܡܪ ܠܗܘܢ ܐܝܢ ܘܟܕ ܥܠ ܟܐܦܐ ܠܒܝܬܐ ܩܕܡܗ ܝܫܘܥ ܘܐܡܪ ܠܗ ܡܢܐ ܡܬܚܙܐ ܠܟ ܫܡܥܘܢ ܡܠܟܝܗ ܕܐܪܥܐ ܡܢ ܡܢ ܢܤܒܝܢ ܡܟܤܐ ܘܟܤܦ ܪܫܐ ܡܢ ܒܢܝܗܘܢ ܐܘ ܡܢ ܢܘܟܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 26, 'ܐܡܪ ܠܗ ܫܡܥܘܢ ܡܢ ܢܘܟܪܝܐ ܐܡܪ ܠܗ ܝܫܘܥ ܡܕܝܢ ܒܢܝ ܚܐܪܐ ܐܢܘܢ ܒܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 27, 'ܕܠܐ ܕܝܢ ܢܟܫܠ ܐܢܘܢ ܙܠ ܠܝܡܐ ܘܐܪܡܐ ܒܠܘܥܐ ܘܢܘܢܐ ܩܕܡܝܐ ܕܤܠܩ ܦܬܚ ܦܘܡܗ ܘܬܫܟܚ ܐܤܬܪܐ ܗܝ ܤܒ ܘܗܒ ܚܠܦܝ ܘܚܠܦܝܟ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 1, 'ܒܗܝ ܫܥܬܐ ܩܪܒܘ ܬܠܡܝܕܐ ܠܘܬ ܝܫܘܥ ܘܐܡܪܝܢ ܡܢܘ ܟܝ ܪܒ ܒܡܠܟܘܬܐ ܕܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 2, 'ܘܩܪܐ ܝܫܘܥ ܛܠܝܐ ܘܐܩܝܡܗ ܒܝܢܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 3, 'ܘܐܡܪ ܐܡܝܢ ܐܡܪܢܐ ܠܟܘܢ ܕܐܠܐ ܬܬܗܦܟܘܢ ܘܬܗܘܘܢ ܐܝܟ ܛܠܝܐ ܠܐ ܬܥܠܘܢ ܠܡܠܟܘܬܐ ܕܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 4, 'ܡܢ ܗܟܝܠ ܕܡܡܟܟ ܢܦܫܗ ܐܝܟ ܗܢܐ ܛܠܝܐ ܗܘ ܢܗܘܐ ܪܒ ܒܡܠܟܘܬܐ ܕܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 5, 'ܘܡܢ ܕܢܩܒܠ ܐܝܟ ܛܠܝܐ ܗܢܐ ܒܫܡܝ ܠܝ ܡܩܒܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 6, 'ܘܟܠ ܕܢܟܫܠ ܠܚܕ ܡܢ ܗܠܝܢ ܙܥܘܪܐ ܕܡܗܝܡܢܝܢ ܒܝ ܦܩܚ ܗܘܐ ܠܗ ܕܬܗܘܐ ܬܠܝܐ ܪܚܝܐ ܕܚܡܪܐ ܒܨܘܪܗ ܘܡܛܒܥ ܒܥܘܡܩܘܗܝ ܕܝܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 7, 'ܘܝ ܠܥܠܡܐ ܡܢ ܡܟܫܘܠܐ ܐܢܢܩܐ ܓܝܪ ܕܢܐܬܘܢ ܡܟܫܘܠܐ ܘܝ ܕܝܢ ܠܓܒܪܐ ܕܒܐܝܕܗ ܢܐܬܘܢ ܡܟܫܘܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 8, 'ܐܢ ܕܝܢ ܐܝܕܟ ܐܘ ܪܓܠܟ ܡܟܫܠܐ ܠܟ ܦܤܘܩܝܗ ܘܫܕܝܗ ܡܢܟ ܛܒ ܗܘ ܠܟ ܕܬܥܘܠ ܠܚܝܐ ܟܕ ܚܓܝܤ ܐܢܬ ܐܘ ܟܕ ܦܫܝܓ ܘܠܐ ܟܕ ܐܝܬ ܠܟ ܬܪܬܝܢ ܐܝܕܝܢ ܐܘ ܬܪܬܝܢ ܪܓܠܝܢ ܬܦܠ ܒܢܘܪܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 9, 'ܘܐܢ ܗܘ ܕܥܝܢܟ ܡܟܫܠܐ ܠܟ ܚܨܝܗ ܘܫܕܝܗ ܡܢܟ ܛܒ ܗܘ ܠܟ ܕܒܚܕܐ ܥܝܢܐ ܬܥܘܠ ܠܚܝܐ ܘܠܐ ܟܕ ܐܝܬ ܠܟ ܬܪܬܝܢ ܥܝܢܝܢ ܬܦܠ ܒܓܗܢܐ ܕܢܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 10, 'ܚܙܘ ܠܐ ܬܒܤܘܢ ܥܠ ܚܕ ܡܢ ܗܠܝܢ ܙܥܘܪܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܓܝܪ ܕܡܠܐܟܝܗܘܢ ܒܫܡܝܐ ܒܟܠܙܒܢ ܚܙܝܢ ܦܪܨܘܦܗ ܕܐܒܝ ܕܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 11, 'ܐܬܐ ܓܝܪ ܒܪܗ ܕܐܢܫܐ ܕܢܚܐ ܡܕܡ ܕܐܒܝܕ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 12, 'ܡܢܐ ܡܬܚܙܐ ܠܟܘܢ ܐܢ ܢܗܘܘܢ ܠܐܢܫ ܡܐܐ ܥܪܒܝܢ ܘܢܛܥܐ ܚܕ ܡܢܗܘܢ ܠܐ ܫܒܩ ܬܫܥܝܢ ܘܬܫܥܐ ܒܛܘܪܐ ܘܐܙܠ ܒܥܐ ܠܗܘ ܕܛܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 13, 'ܘܐܢ ܢܫܟܚܗ ܐܡܝܢ ܐܡܪܢܐ ܠܟܘܢ ܕܚܕܐ ܒܗ ܝܬܝܪ ܡܢ ܬܫܥܝܢ ܘܬܫܥܐ ܕܠܐ ܛܥܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 14, 'ܗܟܢܐ ܠܝܬ ܨܒܝܢܐ ܩܕܡ ܐܒܘܟܘܢ ܕܒܫܡܝܐ ܕܢܐܒܕ ܚܕ ܡܢ ܙܥܘܪܐ ܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 15, 'ܐܢ ܕܝܢ ܐܤܟܠ ܒܟ ܐܚܘܟ ܙܠ ܐܟܤܝܗܝ ܒܝܢܝܟ ܘܠܗ ܒܠܚܘܕ ܐܢ ܫܡܥܟ ܝܬܪܬ ܐܚܘܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 16, 'ܘܐܠܐ ܫܡܥܟ ܕܒܪ ܥܡܟ ܚܕ ܐܘ ܬܪܝܢ ܕܥܠ ܦܘܡ ܬܪܝܢ ܐܘ ܬܠܬܐ ܤܗܕܝܢ ܬܩܘܡ ܟܠ ܡܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 17, 'ܐܢ ܕܝܢ ܐܦ ܠܐ ܠܗܢܘܢ ܢܫܡܥ ܐܡܪ ܠܥܕܬܐ ܐܢ ܕܝܢ ܐܦ ܠܐ ܠܥܕܬܐ ܢܫܡܥ ܢܗܘܐ ܠܟ ܐܝܟ ܡܟܤܐ ܘܐܝܟ ܚܢܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 18, 'ܘܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܟܠ ܡܐ ܕܬܐܤܪܘܢ ܒܐܪܥܐ ܢܗܘܐ ܐܤܝܪ ܒܫܡܝܐ ܘܡܕܡ ܕܬܫܪܘܢ ܒܐܪܥܐ ܢܗܘܐ ܫܪܐ ܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 19, 'ܬܘܒ ܐܡܪܢܐ ܠܟܘܢ ܕܐܢ ܬܪܝܢ ܡܢܟܘܢ ܢܫܬܘܘܢ ܒܐܪܥܐ ܥܠ ܟܠ ܨܒܘ ܕܢܫܐܠܘܢ ܢܗܘܐ ܠܗܘܢ ܡܢ ܠܘܬ ܐܒܝ ܕܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 20, 'ܐܝܟܐ ܓܝܪ ܕܬܪܝܢ ܐܘ ܬܠܬܐ ܟܢܝܫܝܢ ܒܫܡܝ ܬܡܢ ܐܢܐ ܒܝܢܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 21, 'ܗܝܕܝܢ ܩܪܒ ܠܘܬܗ ܟܐܦܐ ܘܐܡܪ ܡܪܝ ܟܡܐ ܙܒܢܝܢ ܐܢ ܢܤܟܠ ܒܝ ܐܚܝ ܐܫܒܘܩ ܠܗ ܥܕܡܐ ܠܫܒܥ ܙܒܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 22, 'ܐܡܪ ܠܗ ܝܫܘܥ ܠܐ ܐܡܪ ܐܢܐ ܠܟ ܥܕܡܐ ܠܫܒܥ ܐܠܐ ܥܕܡܐ ܠܫܒܥܝܢ ܙܒܢܝܢ ܫܒܥ ܫܒܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 23, 'ܡܛܠ ܗܢܐ ܐܬܕܡܝܬ ܡܠܟܘܬܐ ܕܫܡܝܐ ܠܓܒܪܐ ܡܠܟܐ ܕܨܒܐ ܕܢܤܒ ܚܘܫܒܢܐ ܡܢ ܥܒܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 24, 'ܘܟܕ ܫܪܝ ܠܡܤܒ ܩܪܒܘ ܠܗ ܚܕ ܕܚܝܒ ܪܒܘ ܟܟܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 25, 'ܘܟܕ ܠܝܬ ܠܗ ܠܡܦܪܥ ܦܩܕ ܡܪܗ ܕܢܙܕܒܢ ܗܘ ܘܐܢܬܬܗ ܘܒܢܘܗܝ ܘܟܠ ܡܕܡ ܕܐܝܬ ܠܗ ܘܢܦܪܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 26, 'ܘܢܦܠ ܗܘ ܥܒܕܐ ܤܓܕ ܠܗ ܘܐܡܪ ܡܪܝ ܐܓܪ ܥܠܝ ܪܘܚܐ ܘܟܠ ܡܕܡ ܦܪܥ ܐܢܐ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 27, 'ܘܐܬܪܚܡ ܡܪܗ ܕܥܒܕܐ ܗܘ ܘܫܪܝܗܝ ܘܚܘܒܬܗ ܫܒܩ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 28, 'ܢܦܩ ܕܝܢ ܥܒܕܐ ܗܘ ܘܐܫܟܚ ܠܚܕ ܡܢ ܟܢܘܬܗ ܕܚܝܒ ܗܘܐ ܠܗ ܕܝܢܪܐ ܡܐܐ ܘܐܚܕܗ ܘܚܢܩ ܗܘܐ ܠܗ ܘܐܡܪ ܠܗ ܗܒ ܠܝ ܡܕܡ ܕܚܝܒ ܐܢܬ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 29, 'ܘܢܦܠ ܗܘ ܟܢܬܗ ܥܠ ܪܓܠܘܗܝ ܒܥܐ ܡܢܗ ܘܐܡܪ ܠܗ ܐܓܪ ܥܠܝ ܪܘܚܐ ܘܦܪܥ ܐܢܐ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 30, 'ܗܘ ܕܝܢ ܠܐ ܨܒܐ ܐܠܐ ܐܙܠ ܐܪܡܝܗ ܒܝܬ ܐܤܝܪܐ ܥܕܡܐ ܕܢܬܠ ܠܗ ܡܐ ܕܚܝܒ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 31, 'ܟܕ ܚܙܘ ܕܝܢ ܟܢܘܬܗܘܢ ܡܕܡ ܕܗܘܐ ܟܪܝܬ ܠܗܘܢ ܛܒ ܘܐܬܘ ܐܘܕܥܘ ܠܡܪܗܘܢ ܟܠ ܕܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 32, 'ܗܝܕܝܢ ܩܪܝܗܝ ܡܪܗ ܘܐܡܪ ܠܗ ܥܒܕܐ ܒܝܫܐ ܗܝ ܟܠܗ ܚܘܒܬܐ ܫܒܩܬ ܠܟ ܕܒܥܝܬ ܡܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 33, 'ܠܐ ܘܠܐ ܗܘܐ ܠܟ ܐܦ ܐܢܬ ܕܬܚܘܢ ܠܟܢܬܟ ܐܝܟܢܐ ܕܐܢܐ ܚܢܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 34, 'ܘܪܓܙ ܡܪܗ ܘܐܫܠܡܗ ܠܡܢܓܕܢܐ ܥܕܡܐ ܕܢܦܪܘܥ ܟܠ ܡܕܡ ܕܚܝܒ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 35, 'ܗܟܢܐ ܢܥܒܕ ܠܟܘܢ ܐܒܝ ܕܒܫܡܝܐ ܐܠܐ ܬܫܒܩܘܢ ܐܢܫ ܠܐܚܘܗܝ ܡܢ ܠܒܟܘܢ ܤܟܠܘܬܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 1, 'ܘܗܘܐ ܕܟܕ ܫܠܡ ܝܫܘܥ ܡܠܐ ܗܠܝܢ ܫܩܠ ܡܢ ܓܠܝܠܐ ܘܐܬܐ ܠܬܚܘܡܐ ܕܝܗܘܕ ܠܥܒܪܐ ܕܝܘܪܕܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 2, 'ܘܐܬܘ ܒܬܪܗ ܟܢܫܐ ܤܓܝܐܐ ܘܐܤܝ ܐܢܘܢ ܬܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 3, 'ܘܩܪܒܘ ܠܘܬܗ ܦܪܝܫܐ ܘܡܢܤܝܢ ܗܘܘ ܠܗ ܘܐܡܪܝܢ ܐܢ ܫܠܝܛ ܠܐܢܫ ܕܢܫܪܐ ܐܢܬܬܗ ܒܟܠ ܥܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 4, 'ܗܘ ܕܝܢ ܥܢܐ ܘܐܡܪ ܠܗܘܢ ܠܐ ܩܪܝܬܘܢ ܕܗܘ ܕܥܒܕ ܡܢ ܒܪܫܝܬ ܕܟܪܐ ܘܢܩܒܬܐ ܥܒܕ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 5, 'ܘܐܡܪ ܡܛܠ ܗܢܐ ܢܫܒܘܩ ܓܒܪܐ ܠܐܒܘܗܝ ܘܠܐܡܗ ܘܢܩܦ ܠܐܢܬܬܗ ܘܢܗܘܘܢ ܬܪܝܗܘܢ ܚܕ ܒܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 6, 'ܡܕܝܢ ܠܐ ܗܘܘ ܬܪܝܢ ܐܠܐ ܚܕ ܦܓܪ ܡܕܡ ܗܟܝܠ ܕܐܠܗܐ ܙܘܓ ܒܪܢܫܐ ܠܐ ܢܦܪܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 7, 'ܐܡܪܝܢ ܠܗ ܠܡܢܐ ܗܟܝܠ ܡܘܫܐ ܦܩܕ ܕܢܬܠ ܟܬܒܐ ܕܫܘܒܩܢܐ ܘܢܫܪܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 8, 'ܐܡܪ ܠܗܘܢ ܡܘܫܐ ܠܘܩܒܠ ܩܫܝܘܬ ܠܒܟܘܢ ܐܦܤ ܠܟܘܢ ܕܬܫܪܘܢ ܢܫܝܟܘܢ ܡܢ ܒܪܫܝܬ ܕܝܢ ܠܐ ܗܘܐ ܗܟܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 9, 'ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܝܢ ܕܡܢ ܕܫܒܩ ܐܢܬܬܗ ܕܠܐ ܓܘܪܐ ܘܢܤܒ ܐܚܪܬܐ ܓܐܪ ܘܡܢ ܕܢܤܒ ܫܒܝܩܬܐ ܓܐܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 10, 'ܐܡܪܝܢ ܠܗ ܬܠܡܝܕܘܗܝ ܐܢ ܗܟܢܐ ܐܝܬ ܥܕܠܝܐ ܒܝܢܝ ܓܒܪܐ ܠܐܢܬܬܐ ܠܐ ܦܩܚ ܠܡܤܒ ܐܢܬܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 11, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܠܐ ܟܠܢܫ ܤܦܩ ܠܗ ܠܡܠܬܐ ܗܕܐ ܐܠܐ ܡܢ ܕܝܗܝܒ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 12, 'ܐܝܬ ܓܝܪ ܡܗܝܡܢܐ ܕܡܢ ܟܪܤܐ ܕܐܡܗܘܢ ܐܬܝܠܕܘ ܗܟܢܐ ܘܐܝܬ ܡܗܝܡܢܐ ܕܡܢ ܒܢܝܢܫܐ ܗܘܘ ܡܗܝܡܢܐ ܘܐܝܬ ܡܗܝܡܢܐ ܕܗܢܘܢ ܥܒܕܘ ܢܦܫܗܘܢ ܡܗܝܡܢܐ ܡܛܠ ܡܠܟܘܬܐ ܕܫܡܝܐ ܡܢ ܕܡܫܟܚ ܕܢܤܦܩ ܢܤܦܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 13, 'ܗܝܕܝܢ ܩܪܒܘ ܠܗ ܛܠܝܐ ܕܢܤܝܡ ܐܝܕܗ ܥܠܝܗܘܢ ܘܢܨܠܐ ܘܟܐܘ ܒܗܘܢ ܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 14, 'ܗܘ ܕܝܢ ܝܫܘܥ ܐܡܪ ܠܗܘܢ ܫܒܘܩܘ ܛܠܝܐ ܐܬܝܢ ܠܘܬܝ ܘܠܐ ܬܟܠܘܢ ܐܢܘܢ ܕܕܐܝܠܝܢ ܓܝܪ ܕܐܝܟ ܗܠܝܢ ܐܢܘܢ ܐܝܬܝܗ ܡܠܟܘܬܐ ܕܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 15, 'ܘܤܡ ܐܝܕܗ ܥܠܝܗܘܢ ܘܐܙܠ ܡܢ ܬܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 16, 'ܘܐܬܐ ܚܕ ܩܪܒ ܘܐܡܪ ܠܗ ܡܠܦܢܐ ܛܒܐ ܡܢܐ ܕܛܒ ܐܥܒܕ ܕܢܗܘܘܢ ܠܝ ܚܝܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 17, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗ ܡܢܐ ܩܪܐ ܐܢܬ ܠܝ ܛܒܐ ܠܝܬ ܛܒܐ ܐܠܐ ܐܢ ܚܕ ܐܠܗܐ ܐܢ ܕܝܢ ܨܒܐ ܐܢܬ ܕܬܥܘܠ ܠܚܝܐ ܛܪ ܦܘܩܕܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 18, 'ܐܡܪ ܠܗ ܐܝܠܝܢ ܗܘ ܕܝܢ ܝܫܘܥ ܐܡܪ ܠܗ ܕܠܐ ܬܩܛܘܠ ܘܠܐ ܬܓܘܪ ܘܠܐ ܬܓܢܘܒ ܘܠܐ ܬܤܗܕ ܤܗܕܘܬ ܫܘܩܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 19, 'ܘܝܩܪ ܠܐܒܘܟ ܘܠܐܡܟ ܘܬܚܒ ܠܩܪܝܒܟ ܐܝܟ ܢܦܫܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 20, 'ܐܡܪ ܠܗ ܗܘ ܥܠܝܡܐ ܗܠܝܢ ܟܠܗܝܢ ܢܛܪܬ ܐܢܝܢ ܡܢ ܛܠܝܘܬܝ ܡܢܐ ܚܤܝܪ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 21, 'ܐܡܪ ܠܗ ܝܫܘܥ ܐܢ ܨܒܐ ܐܢܬ ܓܡܝܪܐ ܠܡܗܘܐ ܙܠ ܙܒܢ ܩܢܝܢܟ ܘܗܒ ܠܡܤܟܢܐ ܘܬܗܘܐ ܠܟ ܤܝܡܬܐ ܒܫܡܝܐ ܘܬܐ ܒܬܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 22, 'ܫܡܥ ܕܝܢ ܗܘ ܥܠܝܡܐ ܡܠܬܐ ܗܕܐ ܘܐܙܠ ܟܕ ܟܪܝܐ ܠܗ ܐܝܬ ܗܘܐ ܠܗ ܓܝܪ ܩܢܝܢܐ ܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 23, 'ܝܫܘܥ ܕܝܢ ܐܡܪ ܠܬܠܡܝܕܘܗܝ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܥܛܠܐ ܗܝ ܠܥܬܝܪܐ ܕܢܥܘܠ ܠܡܠܟܘܬ ܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 24, 'ܬܘܒ ܕܝܢ ܐܡܪܢܐ ܠܟܘܢ ܕܕܠܝܠ ܗܘ ܠܓܡܠܐ ܠܡܥܠ ܒܚܪܘܪܐ ܕܡܚܛܐ ܐܘ ܥܬܝܪܐ ܕܢܥܘܠ ܠܡܠܟܘܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 25, 'ܬܠܡܝܕܐ ܕܝܢ ܟܕ ܫܡܥܘ ܬܗܝܪܝܢ ܗܘܘ ܛܒ ܘܐܡܪܝܢ ܡܢܘ ܟܝ ܡܫܟܚ ܕܢܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 26, 'ܚܪ ܒܗܘܢ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܠܘܬ ܒܢܝܢܫܐ ܗܕܐ ܠܐ ܡܫܟܚܐ ܠܘܬ ܐܠܗܐ ܕܝܢ ܟܠܡܕܡ ܡܫܟܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 27, 'ܗܝܕܝܢ ܥܢܐ ܟܐܦܐ ܘܐܡܪ ܠܗ ܗܐ ܐܢܚܢܢ ܫܒܩܢ ܟܠܡܕܡ ܘܐܬܝܢ ܒܬܪܟ ܡܢܐ ܟܝ ܢܗܘܐ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 28, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܐܢܬܘܢ ܕܐܬܝܬܘܢ ܒܬܪܝ ܒܥܠܡܐ ܚܕܬܐ ܡܐ ܕܝܬܒ ܒܪܗ ܕܐܢܫܐ ܥܠ ܬܪܢܘܤ ܕܫܘܒܚܗ ܬܬܒܘܢ ܐܦ ܐܢܬܘܢ ܥܠ ܬܪܥܤܪ ܟܘܪܤܘܢ ܘܬܕܘܢܘܢ ܬܪܥܤܪ ܫܒܛܐ ܕܐܝܤܪܐܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 29, 'ܘܟܠܢܫ ܕܫܒܩ ܒܬܐ ܐܘ ܐܚܐ ܐܘ ܐܚܘܬܐ ܐܘ ܐܒܐ ܐܘ ܐܡܐ ܐܘ ܐܢܬܬܐ ܐܘ ܒܢܝܐ ܐܘ ܩܘܪܝܐ ܡܛܠ ܫܡܝ ܚܕ ܒܡܐܐ ܢܩܒܠ ܘܚܝܐ ܕܠܥܠܡ ܢܐܪܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 30, 'ܤܓܝܐܐ ܕܝܢ ܩܕܡܝܐ ܕܢܗܘܘܢ ܐܚܪܝܐ ܘܐܚܪܝܐ ܩܕܡܝܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 1, 'ܕܡܝܐ ܓܝܪ ܡܠܟܘܬܐ ܕܫܡܝܐ ܠܓܒܪܐ ܡܪܐ ܒܝܬܐ ܕܢܦܩ ܒܨܦܪܐ ܕܢܐܓܘܪ ܦܥܠܐ ܠܟܪܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 2, 'ܩܨ ܕܝܢ ܥܡ ܦܥܠܐ ܡܢ ܕܝܢܪܐ ܒܝܘܡܐ ܘܫܕܪ ܐܢܘܢ ܠܟܪܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 3, 'ܘܢܦܩ ܒܬܠܬ ܫܥܝܢ ܘܚܙܐ ܐܚܪܢܐ ܕܩܝܡܝܢ ܒܫܘܩܐ ܘܒܛܝܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 4, 'ܘܐܡܪ ܠܗܘܢ ܙܠܘ ܐܦ ܐܢܬܘܢ ܠܟܪܡܐ ܘܡܕܡ ܕܘܠܐ ܝܗܒ ܐܢܐ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 5, 'ܗܢܘܢ ܕܝܢ ܐܙܠܘ ܘܢܦܩ ܬܘܒ ܒܫܬ ܘܒܬܫܥ ܫܥܝܢ ܘܥܒܕ ܗܟܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 6, 'ܘܠܐܦܝ ܚܕܥܤܪܐ ܫܥܝܢ ܢܦܩ ܘܐܫܟܚ ܐܚܪܢܐ ܕܩܝܡܝܢ ܘܒܛܝܠܝܢ ܘܐܡܪ ܠܗܘܢ ܡܢܐ ܩܝܡܝܢ ܐܢܬܘܢ ܝܘܡܐ ܟܠܗ ܘܒܛܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 7, 'ܐܡܪܝܢ ܠܗ ܕܠܐ ܐܢܫ ܐܓܪܢ ܐܡܪ ܠܗܘܢ ܙܠܘ ܐܦ ܐܢܬܘܢ ܠܟܪܡܐ ܘܡܕܡ ܕܘܠܐ ܢܤܒܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 8, 'ܟܕ ܗܘܐ ܕܝܢ ܪܡܫܐ ܐܡܪ ܡܪܐ ܟܪܡܐ ܠܪܒܝܬܗ ܩܪܝ ܦܥܠܐ ܘܗܒ ܠܗܘܢ ܐܓܪܗܘܢ ܘܫܪܐ ܡܢ ܐܚܪܝܐ ܘܥܕܡܐ ܠܩܕܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 9, 'ܘܐܬܘ ܗܢܘܢ ܕܚܕܥܤܪܐ ܫܥܝܢ ܢܤܒܘ ܕܝܢܪ ܕܝܢܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 10, 'ܘܟܕ ܐܬܘ ܩܕܡܝܐ ܤܒܪܘ ܕܝܬܝܪ ܫܩܠܝܢ ܘܫܩܠܘ ܕܝܢܪ ܕܝܢܪ ܐܦ ܗܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 11, 'ܘܟܕ ܫܩܠܘ ܪܛܢܘ ܥܠ ܡܪܐ ܒܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 12, 'ܘܐܡܪܝܢ ܗܠܝܢ ܐܚܪܝܐ ܚܕܐ ܫܥܐ ܥܒܕܘ ܘܐܫܘܝܬ ܐܢܘܢ ܥܡܢ ܕܫܩܠܢ ܝܘܩܪܗ ܕܝܘܡܐ ܘܚܘܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 13, 'ܗܘ ܕܝܢ ܥܢܐ ܘܐܡܪ ܠܚܕ ܡܢܗܘܢ ܚܒܪܝ ܠܐ ܡܥܘܠ ܐܢܐ ܒܟ ܠܐ ܗܘܐ ܒܕܝܢܪ ܩܨܬ ܥܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 14, 'ܤܒ ܕܝܠܟ ܘܙܠ ܨܒܐ ܐܢܐ ܕܝܢ ܕܠܗܢܐ ܐܚܪܝܐ ܐܬܠ ܐܝܟ ܕܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 15, 'ܐܘ ܠܐ ܫܠܝܛ ܠܝ ܕܡܕܡ ܕܨܒܐ ܐܢܐ ܐܥܒܕ ܒܕܝܠܝ ܐܘ ܥܝܢܟ ܒܝܫܐ ܕܐܢܐ ܛܒ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 16, 'ܗܟܢܐ ܢܗܘܘܢ ܐܚܪܝܐ ܩܕܡܝܐ ܘܩܕܡܝܐ ܐܚܪܝܐ ܤܓܝܐܝܢ ܐܢܘܢ ܓܝܪ ܩܪܝܐ ܘܙܥܘܪܝܢ ܓܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 17, 'ܥܬܝܕ ܗܘܐ ܕܝܢ ܝܫܘܥ ܕܢܤܩ ܠܐܘܪܫܠܡ ܘܕܒܪ ܠܬܪܥܤܪ ܬܠܡܝܕܘܗܝ ܒܝܢܘܗܝ ܘܠܗܘܢ ܒܐܘܪܚܐ ܘܐܡܪ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 18, 'ܗܐ ܤܠܩܝܢ ܚܢܢ ܠܐܘܪܫܠܡ ܘܒܪܗ ܕܐܢܫܐ ܡܫܬܠܡ ܠܪܒܝ ܟܗܢܐ ܘܠܤܦܪܐ ܘܢܚܝܒܘܢܝܗܝ ܠܡܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 19, 'ܘܢܫܠܡܘܢܝܗܝ ܠܥܡܡܐ ܘܢܒܙܚܘܢ ܒܗ ܘܢܢܓܕܘܢܝܗܝ ܘܢܙܩܦܘܢܝܗܝ ܘܠܝܘܡܐ ܕܬܠܬܐ ܢܩܘܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 20, 'ܗܝܕܝܢ ܩܪܒܬ ܠܗ ܐܡܗܘܢ ܕܒܢܝ ܙܒܕܝ ܗܝ ܘܒܢܝܗ ܘܤܓܕܬ ܠܗ ܘܫܐܠܐ ܗܘܬ ܠܗ ܡܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 21, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗ ܡܢܐ ܨܒܝܐ ܐܢܬܝ ܐܡܪܐ ܠܗ ܐܡܪ ܕܢܬܒܘܢ ܗܠܝܢ ܬܪܝܢ ܒܢܝ ܚܕ ܡܢ ܝܡܝܢܟ ܘܚܕ ܡܢ ܤܡܠܟ ܒܡܠܟܘܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 22, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܡܢܐ ܫܐܠܝܢ ܐܢܬܘܢ ܡܫܟܚܝܢ ܐܢܬܘܢ ܠܡܫܬܐ ܟܤܐ ܕܐܢܐ ܥܬܝܕ ܠܡܫܬܐ ܐܘ ܡܥܡܘܕܝܬܐ ܕܐܢܐ ܥܡܕ ܐܢܐ ܬܥܡܕܘܢ ܐܡܪܝܢ ܠܗ ܡܫܟܚܝܢ ܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 23, 'ܐܡܪ ܠܗܘܢ ܟܤܝ ܬܫܬܘܢ ܘܡܥܡܘܕܝܬܐ ܕܐܢܐ ܥܡܕ ܐܢܐ ܬܥܡܕܘܢ ܕܬܬܒܘܢ ܕܝܢ ܡܢ ܝܡܝܢܝ ܘܡܢ ܤܡܠܝ ܠܐ ܗܘܬ ܕܝܠܝ ܕܐܬܠ ܐܠܐ ܠܐܝܠܝܢ ܕܐܬܛܝܒܬ ܡܢ ܐܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 24, 'ܟܕ ܕܝܢ ܫܡܥܘ ܥܤܪܐ ܪܓܙܘ ܥܠ ܗܢܘܢ ܬܪܝܢ ܐܚܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 25, 'ܘܩܪܐ ܐܢܘܢ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܝܕܥܝܢ ܐܢܬܘܢ ܕܪܫܝܗܘܢ ܕܥܡܡܐ ܡܪܝܗܘܢ ܐܢܘܢ ܘܪܘܪܒܢܝܗܘܢ ܫܠܝܛܝܢ ܥܠܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 26, 'ܠܐ ܗܟܢܐ ܢܗܘܐ ܒܝܢܬܟܘܢ ܐܠܐ ܡܢ ܕܨܒܐ ܒܟܘܢ ܕܢܗܘܐ ܪܒܐ ܢܗܘܐ ܠܟܘܢ ܡܫܡܫܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 27, 'ܘܡܢ ܕܨܒܐ ܒܟܘܢ ܕܢܗܘܐ ܩܕܡܝܐ ܢܗܘܐ ܠܟܘܢ ܥܒܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 28, 'ܐܝܟܢܐ ܕܒܪܗ ܕܐܢܫܐ ܠܐ ܐܬܐ ܕܢܫܬܡܫ ܐܠܐ ܕܢܫܡܫ ܘܕܢܬܠ ܢܦܫܗ ܦܘܪܩܢܐ ܚܠܦ ܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 29, 'ܘܟܕ ܢܦܩ ܝܫܘܥ ܡܢ ܐܝܪܝܚܘ ܐܬܐ ܗܘܐ ܒܬܪܗ ܟܢܫܐ ܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 30, 'ܘܗܐ ܤܡܝܐ ܬܪܝܢ ܝܬܒܝܢ ܗܘܘ ܥܠ ܝܕ ܐܘܪܚܐ ܘܟܕ ܫܡܥܘ ܕܝܫܘܥ ܥܒܪ ܝܗܒܘ ܩܠܐ ܘܐܡܪܝܢ ܐܬܪܚܡ ܥܠܝܢ ܡܪܝ ܒܪܗ ܕܕܘܝܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 31, 'ܟܢܫܐ ܕܝܢ ܟܐܝܢ ܗܘܘ ܒܗܘܢ ܕܢܫܬܩܘܢ ܘܗܢܘܢ ܝܬܝܪܐܝܬ ܐܪܝܡܘ ܩܠܗܘܢ ܘܐܡܪܝܢ ܡܪܢ ܐܬܪܚܡ ܥܠܝܢ ܒܪܗ ܕܕܘܝܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 32, 'ܘܩܡ ܝܫܘܥ ܘܩܪܐ ܐܢܘܢ ܘܐܡܪ ܡܢܐ ܨܒܝܢ ܐܢܬܘܢ ܕܐܥܒܕ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 33, 'ܐܡܪܝܢ ܠܗ ܡܪܢ ܕܢܬܦܬܚܢ ܥܝܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 34, 'ܘܐܬܪܚܡ ܥܠܝܗܘܢ ܝܫܘܥ ܘܩܪܒ ܠܥܝܢܝܗܘܢ ܘܒܪ ܫܥܬܗ ܐܬܦܬܚ ܥܝܢܝܗܘܢ ܘܐܙܠܘ ܒܬܪܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 1, 'ܘܟܕ ܩܪܒ ܠܐܘܪܫܠܡ ܘܐܬܐ ܠܒܝܬ ܦܓܐ ܥܠ ܓܢܒ ܛܘܪܐ ܕܙܝܬܐ ܫܕܪ ܝܫܘܥ ܬܪܝܢ ܡܢ ܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 2, 'ܘܐܡܪ ܠܗܘܢ ܙܠܘ ܠܩܪܝܬܐ ܗܕܐ ܕܠܩܘܒܠܟܘܢ ܘܡܚܕܐ ܡܫܟܚܝܢ ܐܢܬܘܢ ܚܡܪܐ ܕܐܤܝܪܐ ܘܥܝܠܐ ܥܡܗ ܫܪܘ ܐܝܬܘ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 3, 'ܘܐܢ ܐܢܫ ܐܡܪ ܠܟܘܢ ܡܕܡ ܐܡܪܘ ܠܗ ܕܠܡܪܢ ܡܬܒܥܝܢ ܘܡܚܕܐ ܡܫܕܪ ܠܗܘܢ ܠܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 4, 'ܗܕܐ ܕܝܢ ܕܗܘܬ ܕܢܬܡܠܐ ܡܕܡ ܕܐܬܐܡܪ ܒܝܕ ܢܒܝܐ ܕܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 5, 'ܐܡܪܘ ܠܒܪܬ ܨܗܝܘܢ ܗܐ ܡܠܟܟܝ ܐܬܐ ܠܟܝ ܡܟܝܟ ܘܪܟܝܒ ܥܠ ܚܡܪܐ ܘܥܠ ܥܝܠܐ ܒܪ ܐܬܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 6, 'ܘܐܙܠܘ ܬܠܡܝܕܐ ܘܥܒܕܘ ܐܝܟܢܐ ܕܦܩܕ ܠܗܘܢ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 7, 'ܘܐܝܬܝܘ ܠܚܡܪܐ ܘܠܥܝܠܐ ܘܤܡܘ ܥܠ ܥܝܠܐ ܢܚܬܝܗܘܢ ܘܪܟܒ ܥܠܘܗܝ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 8, 'ܘܤܘܓܐܐ ܕܟܢܫܐ ܡܫܘܝܢ ܗܘܘ ܡܐܢܝܗܘܢ ܒܐܘܪܚܐ ܐܚܪܢܐ ܕܝܢ ܦܤܩܝܢ ܗܘܘ ܤܘܟܐ ܡܢ ܐܝܠܢܐ ܘܪܡܝܢ ܒܐܘܪܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 9, 'ܟܢܫܐ ܕܝܢ ܐܝܠܝܢ ܕܐܙܠܝܢ ܗܘܘ ܩܕܡܘܗܝ ܘܐܬܝܢ ܒܬܪܗ ܩܥܝܢ ܗܘܘ ܘܐܡܪܝܢ ܐܘܫܥܢܐ ܠܒܪܗ ܕܕܘܝܕ ܒܪܝܟ ܗܘ ܕܐܬܐ ܒܫܡܗ ܕܡܪܝܐ ܐܘܫܥܢܐ ܒܡܪܘܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 10, 'ܘܟܕ ܥܠ ܠܐܘܪܫܠܡ ܐܬܬܙܝܥܬ ܟܠܗ ܡܕܝܢܬܐ ܘܐܡܪܝܢ ܗܘܘ ܡܢܘ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 11, 'ܟܢܫܐ ܕܝܢ ܐܡܪܝܢ ܗܘܘ ܗܢܘ ܝܫܘܥ ܢܒܝܐ ܕܡܢ ܢܨܪܬ ܕܓܠܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 12, 'ܘܥܠ ܝܫܘܥ ܠܗܝܟܠܐ ܕܐܠܗܐ ܘܐܦܩ ܠܟܠܗܘܢ ܕܙܒܢܝܢ ܘܡܙܒܢܝܢ ܒܗܝܟܠܐ ܘܤܚܦ ܦܬܘܪܐ ܕܡܥܪܦܢܐ ܘܟܘܪܤܘܬܐ ܕܗܢܘܢ ܕܡܙܒܢܝܢ ܝܘܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 13, 'ܘܐܡܪ ܠܗܘܢ ܟܬܝܒ ܗܘ ܕܒܝܬܝ ܒܝܬ ܨܠܘܬܐ ܢܬܩܪܐ ܐܢܬܘܢ ܕܝܢ ܥܒܕܬܘܢܝܗܝ ܡܥܪܬܐ ܕܠܤܛܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 14, 'ܘܩܪܒܘ ܠܗ ܒܗܝܟܠܐ ܤܡܝܐ ܘܚܓܝܤܐ ܘܐܤܝ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 15, 'ܟܕ ܚܙܘ ܕܝܢ ܪܒܝ ܟܗܢܐ ܘܦܪܝܫܐ ܬܕܡܪܬܐ ܕܥܒܕ ܘܛܠܝܐ ܕܩܥܝܢ ܒܗܝܟܠܐ ܘܐܡܪܝܢ ܐܘܫܥܢܐ ܠܒܪܗ ܕܕܘܝܕ ܐܬܒܐܫ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 16, 'ܘܐܡܪܝܢ ܠܗ ܫܡܥ ܐܢܬ ܡܢܐ ܐܡܪܝܢ ܗܠܝܢ ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܝܢ ܡܢ ܡܬܘܡ ܠܐ ܩܪܝܬܘܢ ܕܡܢ ܦܘܡܐ ܕܛܠܝܐ ܘܕܝܠܘܕܐ ܬܩܢܬ ܬܫܒܘܚܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 17, 'ܘܫܒܩ ܐܢܘܢ ܘܢܦܩ ܠܒܪ ܡܢ ܡܕܝܢܬܐ ܠܒܝܬ ܥܢܝܐ ܘܒܬ ܬܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 18, 'ܒܨܦܪܐ ܕܝܢ ܟܕ ܗܦܟ ܠܡܕܝܢܬܐ ܟܦܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 19, 'ܘܚܙܐ ܬܬܐ ܚܕܐ ܒܐܘܪܚܐ ܘܐܬܐ ܠܘܬܗ ܘܠܐ ܐܫܟܚ ܒܗ ܡܕܡ ܐܠܐ ܐܢ ܛܪܦܐ ܒܠܚܘܕ ܘܐܡܪ ܠܗ ܠܐ ܢܗܘܘܢ ܒܟܝ ܬܘܒ ܦܐܪܐ ܠܥܠܡ ܘܡܚܕܐ ܝܒܫܬ ܬܬܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 20, 'ܘܚܙܘ ܬܠܡܝܕܐ ܘܬܗܪܘ ܘܐܡܪܝܢ ܐܝܟܢܐ ܒܪ ܫܥܬܗ ܝܒܫܬ ܬܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 21, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܐܢ ܬܗܘܐ ܒܟܘܢ ܗܝܡܢܘܬܐ ܘܠܐ ܬܬܦܠܓܘܢ ܠܐ ܒܠܚܘܕ ܗܕܐ ܕܬܬܐ ܬܥܒܕܘܢ ܐܠܐ ܐܦܢ ܠܛܘܪܐ ܗܢܐ ܬܐܡܪܘܢ ܕܐܫܬܩܠ ܘܦܠ ܒܝܡܐ ܬܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 22, 'ܘܟܠ ܡܕܡ ܕܬܫܐܠܘܢ ܒܨܠܘܬܐ ܘܬܗܝܡܢܘܢ ܬܤܒܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 23, 'ܘܟܕ ܐܬܐ ܝܫܘܥ ܠܗܝܟܠܐ ܩܪܒܘ ܠܗ ܪܒܝ ܟܗܢܐ ܘܩܫܝܫܐ ܕܥܡܐ ܟܕ ܡܠܦ ܘܐܡܪܝܢ ܠܗ ܒܐܝܢܐ ܫܘܠܛܢ ܗܠܝܢ ܥܒܕ ܐܢܬ ܘܡܢܘ ܝܗܒ ܠܟ ܫܘܠܛܢܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 24, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܐܫܐܠܟܘܢ ܐܦ ܐܢܐ ܡܠܬܐ ܚܕܐ ܘܐܢ ܬܐܡܪܘܢ ܠܝ ܘܐܦ ܐܢܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܒܐܝܢܐ ܫܘܠܛܢܐ ܗܠܝܢ ܥܒܕ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 25, 'ܡܥܡܘܕܝܬܗ ܕܝܘܚܢܢ ܐܝܡܟܐ ܐܝܬܝܗ ܡܢ ܫܡܝܐ ܗܝ ܐܘ ܡܢ ܒܢܝܢܫܐ ܗܢܘܢ ܕܝܢ ܡܬܪܥܝܢ ܗܘܘ ܒܢܦܫܗܘܢ ܘܐܡܪܝܢ ܕܐܢ ܢܐܡܪ ܡܢ ܫܡܝܐ ܐܡܪ ܠܢ ܘܥܠ ܡܢܐ ܠܐ ܗܝܡܢܬܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 26, 'ܘܕܢܐܡܪ ܡܢ ܒܢܝܢܫܐ ܕܚܠܝܢ ܐܢܚܢܢ ܡܢ ܟܢܫܐ ܟܠܗܘܢ ܓܝܪ ܐܝܟ ܢܒܝܐ ܐܚܝܕܝܢ ܗܘܘ ܠܗ ܠܝܘܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 27, 'ܥܢܘ ܘܐܡܪܝܢ ܠܗ ܠܐ ܝܕܥܝܢܢ ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܦ ܠܐ ܐܢܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܒܐܝܢܐ ܫܘܠܛܢܐ ܗܠܝܢ ܥܒܕ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 28, 'ܡܢܐ ܕܝܢ ܡܬܚܙܐ ܠܟܘܢ ܓܒܪܐ ܚܕ ܐܝܬ ܗܘܘ ܠܗ ܒܢܝܐ ܬܪܝܢ ܘܩܪܒ ܠܘܬ ܩܕܡܝܐ ܘܐܡܪ ܠܗ ܒܪܝ ܙܠ ܝܘܡܢܐ ܦܠܘܚ ܒܟܪܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 29, 'ܗܘ ܕܝܢ ܥܢܐ ܘܐܡܪ ܠܐ ܨܒܐ ܐܢܐ ܒܚܪܬܐ ܕܝܢ ܐܬܬܘܝ ܘܐܙܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 30, 'ܘܩܪܒ ܠܘܬ ܐܚܪܢܐ ܘܐܡܪ ܠܗ ܗܟܘܬ ܗܘ ܕܝܢ ܥܢܐ ܘܐܡܪ ܐܢܐ ܡܪܝ ܘܠܐ ܐܙܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 31, 'ܡܢܘ ܡܢ ܗܠܝܢ ܬܪܝܗܘܢ ܥܒܕ ܨܒܝܢܐ ܕܐܒܘܗܝ ܐܡܪܝܢ ܠܗ ܗܘ ܩܕܡܝܐ ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܡܟܤܐ ܘܙܢܝܬܐ ܩܕܡܝܢ ܠܟܘܢ ܠܡܠܟܘܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 32, 'ܐܬܐ ܓܝܪ ܠܘܬܟܘܢ ܝܘܚܢܢ ܒܐܘܪܚܐ ܕܟܐܢܘܬܐ ܘܠܐ ܗܝܡܢܬܘܢܝܗܝ ܡܟܤܐ ܕܝܢ ܘܙܢܝܬܐ ܗܝܡܢܘܗܝ ܐܢܬܘܢ ܕܝܢ ܐܦ ܠܐ ܟܕ ܚܙܝܬܘܢ ܐܬܬܘܝܬܘܢ ܒܚܪܬܐ ܕܬܗܝܡܢܘܢ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 33, 'ܫܡܥܘ ܐܚܪܢܐ ܡܬܠܐ ܓܒܪܐ ܚܕ ܐܝܬ ܗܘܐ ܡܪܐ ܒܝܬܐ ܘܢܨܒ ܟܪܡܐ ܘܐܚܕܪܗ ܤܝܓܐ ܘܚܦܪ ܒܗ ܡܥܨܪܬܐ ܘܒܢܐ ܒܗ ܡܓܕܠܐ ܘܐܘܚܕܗ ܠܦܠܚܐ ܘܚܙܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 34, 'ܟܕ ܕܝܢ ܡܛܐ ܙܒܢܐ ܕܦܐܪܐ ܫܕܪ ܠܥܒܕܘܗܝ ܠܘܬ ܦܠܚܐ ܕܢܫܕܪܘܢ ܠܗ ܡܢ ܦܐܪܐ ܕܟܪܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 35, 'ܘܐܚܕܘ ܦܠܚܐ ܠܥܒܕܘܗܝ ܘܐܝܬ ܕܡܚܐܘܗܝ ܘܐܝܬ ܕܪܓܡܘܗܝ ܘܐܝܬ ܕܩܛܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 36, 'ܘܬܘܒ ܫܕܪ ܐܚܪܢܐ ܥܒܕܐ ܕܤܓܝܐܝܢ ܡܢ ܩܕܡܝܐ ܘܗܟܘܬ ܥܒܕܘ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 37, 'ܐܚܪܝܬ ܕܝܢ ܫܕܪ ܠܘܬܗܘܢ ܠܒܪܗ ܟܕ ܐܡܪ ܟܒܪ ܢܒܗܬܘܢ ܡܢ ܒܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 38, 'ܦܠܚܐ ܕܝܢ ܟܕ ܚܙܐܘܗܝ ܠܒܪܐ ܐܡܪܘ ܒܝܢܬܗܘܢ ܗܢܘ ܝܪܬܐ ܬܘ ܢܩܛܠܝܘܗܝ ܘܢܐܚܘܕ ܝܪܬܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 39, 'ܘܐܚܕܘ ܐܦܩܘܗܝ ܠܒܪ ܡܢ ܟܪܡܐ ܘܩܛܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 40, 'ܡܐ ܕܐܬܐ ܗܟܝܠ ܡܪܗ ܕܟܪܡܐ ܡܢܐ ܢܥܒܕ ܠܦܠܚܐ ܗܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 41, 'ܐܡܪܝܢ ܠܗ ܕܒܝܫ ܒܝܫ ܢܘܒܕ ܐܢܘܢ ܘܟܪܡܐ ܢܘܚܕ ܠܐܚܪܢܐ ܦܠܚܐ ܐܝܠܝܢ ܕܝܗܒܝܢ ܠܗ ܦܐܪܐ ܒܙܒܢܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 42, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܠܐ ܡܡܬܘܡ ܩܪܝܬܘܢ ܒܟܬܒܐ ܕܟܐܦܐ ܕܐܤܠܝܘ ܒܢܝܐ ܗܝ ܗܘܬ ܠܪܫܐ ܕܙܘܝܬܐ ܡܢ ܠܘܬ ܡܪܝܐ ܗܘܬ ܗܕܐ ܘܐܝܬܝܗ ܬܕܡܘܪܬܐ ܒܥܝܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 43, 'ܡܛܠ ܗܢܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܬܫܬܩܠ ܡܢܟܘܢ ܡܠܟܘܬܐ ܕܐܠܗܐ ܘܬܬܝܗܒ ܠܥܡܐ ܕܥܒܕ ܦܐܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 44, 'ܘܡܢ ܕܢܦܠ ܥܠ ܟܐܦܐ ܗܕܐ ܢܬܪܥܥ ܘܟܠ ܡܢ ܕܗܝ ܬܦܠ ܥܠܘܗܝ ܬܕܪܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 45, 'ܘܟܕ ܫܡܥܘ ܪܒܝ ܟܗܢܐ ܘܦܪܝܫܐ ܡܬܠܘܗܝ ܝܕܥܘ ܕܥܠܝܗܘܢ ܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 46, 'ܘܒܥܘ ܠܡܐܚܕܗ ܘܕܚܠܘ ܡܢ ܟܢܫܐ ܡܛܠ ܕܐܝܟ ܕܠܢܒܝܐ ܐܚܝܕܝܢ ܗܘܘ ܠܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 1, 'ܘܥܢܐ ܬܘܒ ܝܫܘܥ ܒܡܬܠܐ ܘܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 2, 'ܐܬܕܡܝܬ ܡܠܟܘܬܐ ܕܫܡܝܐ ܠܓܒܪܐ ܡܠܟܐ ܕܥܒܕ ܡܫܬܘܬܐ ܠܒܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 3, 'ܘܫܕܪ ܠܥܒܕܘܗܝ ܕܢܩܪܘܢ ܠܡܙܡܢܐ ܠܡܫܬܘܬܐ ܘܠܐ ܨܒܘ ܠܡܐܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 4, 'ܬܘܒ ܫܕܪ ܥܒܕܐ ܐܚܪܢܐ ܘܐܡܪ ܐܡܪܘ ܠܡܙܡܢܐ ܕܗܐ ܫܪܘܬܝ ܡܛܝܒܐ ܘܬܘܪܝ ܘܡܦܛܡܝ ܩܛܝܠܝܢ ܘܟܠ ܡܕܡ ܡܛܝܒ ܬܘ ܠܡܫܬܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 5, 'ܗܢܘܢ ܕܝܢ ܒܤܘ ܘܐܙܠܘ ܐܝܬ ܕܠܩܪܝܬܗ ܘܐܝܬ ܕܠܬܐܓܘܪܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 6, 'ܫܪܟܐ ܕܝܢ ܐܚܕܘ ܠܥܒܕܘܗܝ ܘܨܥܪܘ ܘܩܛܠܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 7, 'ܟܕ ܫܡܥ ܕܝܢ ܡܠܟܐ ܪܓܙ ܘܫܕܪ ܚܝܠܘܬܗ ܐܘܒܕ ܠܩܛܘܠܐ ܗܢܘܢ ܘܠܡܕܝܢܬܗܘܢ ܐܘܩܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 8, 'ܗܝܕܝܢ ܐܡܪ ܠܥܒܕܘܗܝ ܡܫܬܘܬܐ ܡܛܝܒܐ ܘܗܢܘܢ ܕܡܙܡܢܝܢ ܗܘܘ ܠܐ ܫܘܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 9, 'ܙܠܘ ܗܟܝܠ ܠܡܦܩܢܐ ܕܐܘܪܚܬܐ ܘܟܠ ܡܢ ܕܡܫܟܚܝܢ ܐܢܬܘܢ ܩܪܘ ܠܡܫܬܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 10, 'ܘܢܦܩܘ ܥܒܕܐ ܗܢܘܢ ܠܐܘܪܚܬܐ ܘܟܢܫܘ ܟܠ ܕܐܫܟܚܘ ܒܝܫܐ ܘܛܒܐ ܘܐܬܡܠܝ ܒܝܬ ܡܫܬܘܬܐ ܤܡܝܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 11, 'ܘܥܠ ܡܠܟܐ ܕܢܚܙܐ ܤܡܝܟܐ ܘܚܙܐ ܬܡܢ ܓܒܪܐ ܕܠܐ ܠܒܝܫ ܠܒܘܫܐ ܕܡܫܬܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 12, 'ܘܐܡܪ ܠܗ ܚܒܪܝ ܐܝܟܢܐ ܥܠܬ ܠܟܐ ܟܕ ܢܚܬܐ ܕܡܫܬܘܬܐ ܠܝܬ ܠܟ ܗܘ ܕܝܢ ܐܫܬܬܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 13, 'ܗܝܕܝܢ ܐܡܪ ܡܠܟܐ ܠܡܫܡܫܢܐ ܐܤܘܪܘ ܐܝܕܘܗܝ ܘܪܓܠܘܗܝ ܘܐܦܩܘܗܝ ܠܚܫܘܟܐ ܒܪܝܐ ܬܡܢ ܢܗܘܐ ܒܟܝܐ ܘܚܘܪܩ ܫܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 14, 'ܤܓܝܐܝܢ ܐܢܘܢ ܓܝܪ ܩܪܝܐ ܘܙܥܘܪܝܢ ܓܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 15, 'ܗܝܕܝܢ ܐܙܠܘ ܦܪܝܫܐ ܢܤܒܘ ܡܠܟܐ ܕܐܝܟܢܐ ܢܨܘܕܘܢܝܗܝ ܒܡܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 16, 'ܘܫܕܪܘ ܠܘܬܗ ܬܠܡܝܕܝܗܘܢ ܥܡ ܕܒܝܬ ܗܪܘܕܤ ܘܐܡܪܝܢ ܠܗ ܡܠܦܢܐ ܝܕܥܝܢܢ ܕܫܪܝܪ ܐܢܬ ܘܐܘܪܚܐ ܕܐܠܗܐ ܒܩܘܫܬܐ ܡܠܦ ܐܢܬ ܘܠܐ ܫܩܝܠ ܐܢܬ ܨܦܬܐ ܕܐܢܫ ܠܐ ܓܝܪ ܢܤܒ ܐܢܬ ܒܐܦܐ ܕܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 17, 'ܐܡܪ ܠܢ ܗܟܝܠ ܐܝܟܢܐ ܡܬܚܙܐ ܠܟ ܫܠܝܛ ܠܡܬܠ ܟܤܦ ܪܫܐ ܠܩܤܪ ܐܘ ܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 18, 'ܝܫܘܥ ܕܝܢ ܝܕܥ ܒܝܫܘܬܗܘܢ ܘܐܡܪ ܡܢܐ ܡܢܤܝܢ ܐܢܬܘܢ ܠܝ ܢܤܒܝ ܒܐܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 19, 'ܚܘܐܘܢܝ ܕܝܢܪܐ ܕܟܤܦ ܪܫܐ ܗܢܘܢ ܕܝܢ ܩܪܒܘ ܠܗ ܕܝܢܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 20, 'ܘܐܡܪ ܠܗܘܢ ܝܫܘܥ ܕܡܢܘ ܨܠܡܐ ܗܢܐ ܘܟܬܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 21, 'ܐܡܪܝܢ ܕܩܤܪ ܐܡܪ ܠܗܘܢ ܗܒܘ ܗܟܝܠ ܕܩܤܪ ܠܩܤܪ ܘܕܐܠܗܐ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 22, 'ܘܟܕ ܫܡܥܘ ܐܬܕܡܪܘ ܘܫܒܩܘܗܝ ܘܐܙܠܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 23, 'ܒܗܘ ܝܘܡܐ ܩܪܒܘ ܙܕܘܩܝܐ ܘܐܡܪܝܢ ܠܗ ܠܝܬ ܚܝܬ ܡܝܬܐ ܘܫܐܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 24, 'ܘܐܡܪܝܢ ܠܗ ܡܠܦܢܐ ܡܘܫܐ ܐܡܪ ܠܢ ܕܐܢ ܐܢܫ ܢܡܘܬ ܟܕ ܠܝܬ ܠܗ ܒܢܝܐ ܢܤܒ ܐܚܘܗܝ ܐܢܬܬܗ ܘܢܩܝܡ ܙܪܥܐ ܠܐܚܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 25, 'ܐܝܬ ܗܘܘ ܕܝܢ ܠܘܬܢ ܐܚܐ ܫܒܥܐ ܩܕܡܝܐ ܫܩܠ ܐܢܬܬܐ ܘܡܝܬ ܘܕܠܝܬ ܗܘܐ ܠܗ ܒܢܝܐ ܫܒܩܗ ܐܢܬܬܗ ܠܐܚܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 26, 'ܗܟܘܬ ܐܦ ܗܘ ܕܬܪܝܢ ܘܐܦ ܗܘ ܕܬܠܬܐ ܘܥܕܡܐ ܠܫܒܥܬܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 27, 'ܒܚܪܬܐ ܕܝܢ ܕܟܠܗܘܢ ܡܝܬܬ ܐܦ ܐܢܬܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 28, 'ܒܩܝܡܬܐ ܗܟܝܠ ܠܐܝܢܐ ܡܢ ܗܠܝܢ ܫܒܥܐ ܬܗܘܐ ܐܢܬܬܐ ܟܠܗܘܢ ܓܝܪ ܢܤܒܘܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 29, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܛܥܝܢ ܐܢܬܘܢ ܕܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܟܬܒܐ ܘܠܐ ܚܝܠܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 30, 'ܒܩܝܡܬܐ ܓܝܪ ܕܡܝܬܐ ܠܐ ܢܤܒܝܢ ܢܫܐ ܐܦܠܐ ܢܫܐ ܗܘܝܢ ܠܓܒܪܐ ܐܠܐ ܐܝܟ ܡܠܐܟܐ ܕܐܠܗܐ ܒܫܡܝܐ ܐܝܬܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 31, 'ܥܠ ܩܝܡܬܐ ܕܝܢ ܕܡܝܬܐ ܠܐ ܩܪܝܬܘܢ ܡܕܡ ܕܐܬܐܡܪ ܠܟܘܢ ܡܢ ܐܠܗܐ ܕܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 32, 'ܕܐܢܐ ܐܢܐ ܐܠܗܗ ܕܐܒܪܗܡ ܐܠܗܗ ܕܐܝܤܚܩ ܐܠܗܗ ܕܝܥܩܘܒ ܘܐܠܗܐ ܠܐ ܗܘܐ ܕܡܝܬܐ ܐܠܐ ܕܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 33, 'ܘܟܕ ܫܡܥܘ ܟܢܫܐ ܡܬܬܡܗܝܢ ܗܘܘ ܒܝܘܠܦܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 34, 'ܦܪܝܫܐ ܕܝܢ ܟܕ ܫܡܥܘ ܕܫܬܩ ܠܙܕܘܩܝܐ ܐܬܟܢܫܘ ܐܟܚܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 35, 'ܘܫܐܠܗ ܚܕ ܡܢܗܘܢ ܕܝܕܥ ܢܡܘܤܐ ܟܕ ܡܢܤܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 36, 'ܡܠܦܢܐ ܐܝܢܐ ܦܘܩܕܢܐ ܪܒ ܒܢܡܘܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 37, 'ܝܫܘܥ ܕܝܢ ܐܡܪ ܠܗ ܕܬܪܚܡ ܠܡܪܝܐ ܐܠܗܟ ܡܢ ܟܠܗ ܠܒܟ ܘܡܢ ܟܠܗ ܢܦܫܟ ܘܡܢ ܟܠܗ ܚܝܠܟ ܘܡܢ ܟܠܗ ܪܥܝܢܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 38, 'ܗܢܘ ܦܘܩܕܢܐ ܪܒܐ ܘܩܕܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 39, 'ܘܕܬܪܝܢ ܕܕܡܐ ܠܗ ܕܬܪܚܡ ܠܩܪܝܒܟ ܐܝܟ ܢܦܫܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 40, 'ܒܗܠܝܢ ܬܪܝܢ ܦܘܩܕܢܝܢ ܬܠܝܐ ܐܘܪܝܬܐ ܘܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 41, 'ܟܕ ܟܢܝܫܝܢ ܕܝܢ ܦܪܝܫܐ ܫܐܠ ܐܢܘܢ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 42, 'ܘܐܡܪ ܡܢܐ ܐܡܪܝܢ ܐܢܬܘܢ ܥܠ ܡܫܝܚܐ ܒܪ ܡܢܘ ܐܡܪܝܢ ܠܗ ܒܪ ܕܘܝܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 43, 'ܐܡܪ ܠܗܘܢ ܘܐܝܟܢܐ ܕܘܝܕ ܒܪܘܚ ܩܪܐ ܠܗ ܡܪܝܐ ܐܡܪ ܓܝܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 44, 'ܕܐܡܪ ܡܪܝܐ ܠܡܪܝ ܬܒ ܠܟ ܡܢ ܝܡܝܢܝ ܥܕܡܐ ܕܐܤܝܡ ܒܥܠܕܒܒܝܟ ܬܚܝܬ ܪܓܠܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 45, 'ܐܢ ܗܟܝܠ ܕܘܝܕ ܩܪܐ ܠܗ ܡܪܝܐ ܐܝܟܢܐ ܒܪܗ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 46, 'ܘܠܐ ܐܢܫ ܐܫܟܚ ܕܢܬܠ ܠܗ ܦܬܓܡܐ ܘܠܐ ܐܢܫ ܐܡܪܚ ܬܘܒ ܡܢ ܗܘ ܝܘܡܐ ܠܡܫܐܠܘܬܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 1, 'ܗܝܕܝܢ ܝܫܘܥ ܡܠܠ ܥܡ ܟܢܫܐ ܘܥܡ ܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 2, 'ܘܐܡܪ ܠܗܘܢ ܥܠ ܟܘܪܤܝܐ ܕܡܘܫܐ ܝܬܒܘ ܤܦܪܐ ܘܦܪܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 3, 'ܟܠ ܡܕܡ ܗܟܝܠ ܕܢܐܡܪܘܢ ܠܟܘܢ ܕܬܛܪܘܢ ܛܪܘ ܘܥܒܕܘ ܐܝܟ ܥܒܕܝܗܘܢ ܕܝܢ ܠܐ ܬܥܒܕܘܢ ܐܡܪܝܢ ܓܝܪ ܘܠܐ ܥܒܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 4, 'ܘܐܤܪܝܢ ܡܘܒܠܐ ܝܩܝܪܬܐ ܘܤܝܡܝܢ ܥܠ ܟܬܦܬܐ ܕܒܢܝ ܐܢܫܐ ܗܢܘܢ ܕܝܢ ܒܨܒܥܗܘܢ ܠܐ ܨܒܝܢ ܕܢܩܪܒܘܢ ܠܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 5, 'ܘܟܠܗܘܢ ܥܒܕܝܗܘܢ ܥܒܕܝܢ ܕܢܬܚܙܘܢ ܠܒܢܝ ܐܢܫܐ ܡܦܬܝܢ ܓܝܪ ܬܦܠܝܗܘܢ ܘܡܘܪܟܝܢ ܬܟܠܬܐ ܕܡܪܛܘܛܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 6, 'ܘܪܚܡܝܢ ܪܫ ܤܡܟܐ ܒܚܫܡܝܬܐ ܘܪܫ ܡܘܬܒܐ ܒܟܢܘܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 7, 'ܘܫܠܡܐ ܒܫܘܩܐ ܘܕܢܗܘܘܢ ܡܬܩܪܝܢ ܡܢ ܐܢܫܐ ܪܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 8, 'ܐܢܬܘܢ ܕܝܢ ܠܐ ܬܬܩܪܘܢ ܪܒܝ ܚܕ ܗܘ ܓܝܪ ܪܒܟܘܢ ܐܢܬܘܢ ܕܝܢ ܟܠܟܘܢ ܐܚܐ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 9, 'ܘܐܒܐ ܠܐ ܬܩܪܘܢ ܠܟܘܢ ܒܐܪܥܐ ܚܕ ܗܘ ܓܝܪ ܐܒܘܟܘܢ ܕܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 10, 'ܘܠܐ ܬܬܩܪܘܢ ܡܕܒܪܢܐ ܡܛܠ ܕܚܕ ܗܘ ܡܕܒܪܢܟܘܢ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 11, 'ܗܘ ܕܝܢ ܕܪܒ ܒܟܘܢ ܢܗܘܐ ܠܟܘܢ ܡܫܡܫܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 12, 'ܡܢ ܓܝܪ ܕܢܪܝܡ ܢܦܫܗ ܢܬܡܟܟ ܘܡܢ ܕܢܡܟ ܢܦܫܗ ܢܬܬܪܝܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 13, 'ܘܝ ܠܟܘܢ ܤܦܪܐ ܘܦܪܝܫܐ ܢܤܒܝ ܒܐܦܐ ܕܐܟܠܝܢ ܐܢܬܘܢ ܒܬܐ ܕܐܪܡܠܬܐ ܒܥܠܬܐ ܕܡܘܪܟܝܢ ܐܢܬܘܢ ܨܠܘܬܟܘܢ ܡܛܠ ܗܢܐ ܬܩܒܠܘܢ ܕܝܢܐ ܝܬܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 14, 'ܘܝ ܠܟܘܢ ܤܦܪܐ ܘܦܪܝܫܐ ܢܤܒܝ ܒܐܦܐ ܕܐܚܝܕܝܢ ܐܢܬܘܢ ܡܠܟܘܬܐ ܕܫܡܝܐ ܩܕܡ ܒܢܝ ܐܢܫܐ ܐܢܬܘܢ ܓܝܪ ܠܐ ܥܐܠܝܢ ܐܢܬܘܢ ܘܠܐܝܠܝܢ ܕܥܐܠܝܢ ܠܐ ܫܒܩܝܢ ܐܢܬܘܢ ܠܡܥܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 15, 'ܘܝ ܠܟܘܢ ܤܦܪܐ ܘܦܪܝܫܐ ܢܤܒܝ ܒܐܦܐ ܕܡܬܟܪܟܝܢ ܐܢܬܘܢ ܝܡܐ ܘܝܒܫܐ ܕܬܥܒܕܘܢ ܚܕ ܓܝܘܪܐ ܘܡܐ ܕܗܘܐ ܥܒܕܝܢ ܐܢܬܘܢ ܠܗ ܒܪܗ ܕܓܗܢܐ ܐܥܦܐ ܥܠܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 16, 'ܘܝ ܠܟܘܢ ܢܓܘܕܐ ܤܡܝܐ ܕܐܡܪܝܢ ܐܢܬܘܢ ܕܡܢ ܕܝܡܐ ܒܗܝܟܠܐ ܠܐ ܗܘܐ ܡܕܡ ܡܢ ܕܝܢ ܕܝܡܐ ܒܕܗܒܐ ܕܒܗܝܟܠܐ ܚܐܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 17, 'ܤܟܠܐ ܘܤܡܝܐ ܡܢܐ ܓܝܪ ܪܒ ܕܗܒܐ ܐܘ ܗܝܟܠܐ ܕܗܘ ܡܩܕܫ ܠܗ ܠܕܗܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 18, 'ܘܡܢ ܕܝܡܐ ܒܡܕܒܚܐ ܠܐ ܗܘܐ ܡܕܡ ܡܢ ܕܝܢ ܕܝܡܐ ܒܩܘܪܒܢܐ ܕܠܥܠ ܡܢܗ ܚܐܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 19, 'ܤܟܠܐ ܘܥܘܝܪܐ ܡܢܐ ܪܒ ܩܘܪܒܢܐ ܐܘ ܡܕܒܚܐ ܕܡܩܕܫ ܠܩܘܪܒܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 20, 'ܡܢ ܕܝܡܐ ܗܟܝܠ ܒܡܕܒܚܐ ܝܡܐ ܒܗ ܘܒܟܠ ܡܐ ܕܐܝܬ ܠܥܠ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 21, 'ܘܡܢ ܕܝܡܐ ܒܗܝܟܠܐ ܝܡܐ ܒܗ ܘܒܡܢ ܕܥܡܪ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 22, 'ܘܡܢ ܕܝܡܐ ܒܫܡܝܐ ܝܡܐ ܒܟܘܪܤܝܗ ܕܐܠܗܐ ܘܒܡܢ ܕܝܬܒ ܠܥܠ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 23, 'ܘܝ ܠܟܘܢ ܤܦܪܐ ܘܦܪܝܫܐ ܢܤܒܝ ܒܐܦܐ ܕܡܥܤܪܝܢ ܐܢܬܘܢ ܢܢܥܐ ܘܫܒܬܐ ܘܟܡܘܢܐ ܘܫܒܩܬܘܢ ܝܩܝܪܬܗ ܕܢܡܘܤܐ ܕܝܢܐ ܘܚܢܢܐ ܘܗܝܡܢܘܬܐ ܗܠܝܢ ܕܝܢ ܘܠܐ ܗܘܐ ܕܬܥܒܕܘܢ ܘܗܠܝܢ ܠܐ ܬܫܒܩܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 24, 'ܢܓܘܕܐ ܤܡܝܐ ܕܡܨܠܠܝܢ ܒܩܐ ܘܒܠܥܝܢ ܓܡܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 25, 'ܘܝ ܠܟܘܢ ܤܦܪܐ ܘܦܪܝܫܐ ܢܤܒܝ ܒܐܦܐ ܕܡܕܟܝܢ ܐܢܬܘܢ ܒܪܗ ܕܟܤܐ ܘܕܙܒܘܪܐ ܠܓܘ ܕܝܢ ܡܠܝܢ ܚܛܘܦܝܐ ܘܥܘܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 26, 'ܦܪܝܫܐ ܥܘܝܪܐ ܕܟܘ ܠܘܩܕܡ ܓܘܗ ܕܟܤܐ ܘܕܙܒܘܪܐ ܕܗܘܐ ܐܦ ܒܪܗܘܢ ܕܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 27, 'ܘܝ ܠܟܘܢ ܤܦܪܐ ܘܦܪܝܫܐ ܢܤܒܝ ܒܐܦܐ ܕܕܡܝܢ ܐܢܬܘܢ ܠܩܒܪܐ ܡܟܠܫܐ ܕܡܢ ܠܒܪ ܡܬܚܙܝܢ ܫܦܝܪܐ ܡܢ ܠܓܘ ܕܝܢ ܡܠܝܢ ܓܪܡܐ ܕܡܝܬܐ ܘܟܠܗ ܛܢܦܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 28, 'ܗܟܢܐ ܐܦ ܐܢܬܘܢ ܡܢ ܠܒܪ ܡܬܚܙܝܢ ܐܢܬܘܢ ܠܒܢܝ ܐܢܫܐ ܐܝܟ ܙܕܝܩܐ ܘܡܢ ܠܓܘ ܡܠܝܢ ܐܢܬܘܢ ܥܘܠܐ ܘܡܤܒ ܒܐܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 29, 'ܘܝ ܠܟܘܢ ܤܦܪܐ ܘܦܪܝܫܐ ܢܤܒܝ ܒܐܦܐ ܕܒܢܝܢ ܐܢܬܘܢ ܩܒܪܐ ܕܢܒܝܐ ܘܡܨܒܬܝܢ ܐܢܬܘܢ ܒܝܬ ܩܒܘܪܐ ܕܙܕܝܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 30, 'ܘܐܡܪܝܢ ܐܢܬܘܢ ܕܐܠܘ ܗܘܝܢ ܒܝܘܡܝ ܐܒܗܝܢ ܠܐ ܗܘܝܢ ܗܘܝܢ ܠܗܘܢ ܫܘܬܦܐ ܒܕܡܐ ܕܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 31, 'ܡܕܝܢ ܡܤܗܕܝܢ ܐܢܬܘܢ ܥܠ ܢܦܫܟܘܢ ܕܒܢܝܐ ܐܢܬܘܢ ܕܗܢܘܢ ܕܩܛܠܘ ܠܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 32, 'ܘܐܦ ܐܢܬܘܢ ܡܠܘ ܡܫܘܚܬܐ ܕܐܒܗܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 33, 'ܚܘܘܬܐ ܝܠܕܐ ܕܐܟܕܢܐ ܐܝܟܢܐ ܬܥܪܩܘܢ ܡܢ ܕܝܢܐ ܕܓܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 34, 'ܡܛܠ ܗܢܐ ܗܐ ܐܢܐ ܡܫܕܪ ܐܢܐ ܠܘܬܟܘܢ ܢܒܝܐ ܘܚܟܝܡܐ ܘܤܦܪܐ ܡܢܗܘܢ ܩܛܠܝܢ ܐܢܬܘܢ ܘܙܩܦܝܢ ܐܢܬܘܢ ܘܡܢܗܘܢ ܡܢܓܕܝܢ ܐܢܬܘܢ ܒܟܢܘܫܬܟܘܢ ܘܬܪܕܦܘܢ ܐܢܘܢ ܡܢ ܡܕܝܢܐ ܠܡܕܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 35, 'ܐܝܟܢܐ ܕܢܐܬܐ ܥܠܝܟܘܢ ܟܠܗ ܕܡܐ ܕܙܕܝܩܐ ܕܐܬܐܫܕ ܥܠ ܐܪܥܐ ܡܢ ܕܡܗ ܕܗܒܝܠ ܙܕܝܩܐ ܘܥܕܡܐ ܠܕܡܗ ܕܙܟܪܝܐ ܒܪ ܒܪܟܝܐ ܗܘ ܕܩܛܠܬܘܢ ܒܝܢܝ ܗܝܟܠܐ ܠܡܕܒܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 36, 'ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܢܐܬܝܢ ܗܠܝܢ ܟܠܗܝܢ ܥܠ ܫܪܒܬܐ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 37, 'ܐܘܪܫܠܡ ܐܘܪܫܠܡ ܩܛܠܬ ܢܒܝܐ ܘܪܓܡܬ ܠܐܝܠܝܢ ܕܫܠܝܚܝܢ ܠܘܬܗ ܟܡܐ ܙܒܢܝܢ ܨܒܝܬ ܕܐܟܢܫ ܒܢܝܟܝ ܐܝܟ ܕܟܢܫܐ ܬܪܢܓܘܠܬܐ ܦܪܘܓܝܗ ܬܚܝܬ ܓܦܝܗ ܘܠܐ ܨܒܝܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 38, 'ܗܐ ܡܫܬܒܩ ܠܟܘܢ ܒܝܬܟܘܢ ܚܪܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 39, 'ܐܡܪܢܐ ܠܟܘܢ ܓܝܪ ܕܠܐ ܬܚܙܘܢܢܝ ܡܢ ܗܫܐ ܥܕܡܐ ܕܬܐܡܪܘܢ ܒܪܝܟ ܗܘ ܕܐܬܐ ܒܫܡܗ ܕܡܪܝܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 1, 'ܘܢܦܩ ܝܫܘܥ ܡܢ ܗܝܟܠܐ ܠܡܐܙܠ ܘܩܪܒܘ ܬܠܡܝܕܘܗܝ ܡܚܘܝܢ ܗܘܘ ܠܗ ܒܢܝܢܗ ܕܗܝܟܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 2, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܠܐ ܗܐ ܚܙܝܢ ܐܢܬܘܢ ܗܠܝܢ ܟܠܗܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܠܐ ܬܫܬܒܩ ܗܪܟܐ ܟܐܦ ܥܠ ܟܐܦ ܕܠܐ ܬܤܬܬܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 3, 'ܘܟܕ ܝܬܒ ܝܫܘܥ ܥܠ ܛܘܪܐ ܕܙܝܬܐ ܩܪܒܘ ܬܠܡܝܕܘܗܝ ܘܐܡܪܝܢ ܒܝܢܝܗܘܢ ܘܠܗ ܐܡܪ ܠܢ ܐܡܬܝ ܗܠܝܢ ܢܗܘܝܢ ܘܡܢܐ ܗܝ ܐܬܐ ܕܡܐܬܝܬܟ ܘܕܫܘܠܡܗ ܕܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 4, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܐܙܕܗܪܘ ܠܐ ܐܢܫ ܢܛܥܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 5, 'ܤܓܝܐܐ ܓܝܪ ܢܐܬܘܢ ܒܫܡܝ ܘܢܐܡܪܘܢ ܕܐܢܐ ܐܢܐ ܡܫܝܚܐ ܘܤܓܝܐܐ ܢܛܥܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 6, 'ܥܬܝܕܝܢ ܐܢܬܘܢ ܕܝܢ ܠܡܫܡܥ ܩܐܪܤܐ ܘܫܡܥܐ ܕܩܪܒܐ ܚܙܘ ܠܐ ܬܬܕܘܕܘܢ ܘܠܐ ܓܝܪ ܕܟܠܗܝܢ ܢܗܘܝܢ ܐܠܐ ܠܐ ܥܕܟܝܠ ܫܘܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 7, 'ܢܩܘܡ ܓܝܪ ܥܡܐ ܥܠ ܥܡܐ ܘܡܠܟܘܬܐ ܥܠ ܡܠܟܘܬܐ ܘܢܗܘܘܢ ܟܦܢܐ ܘܡܘܬܢܐ ܘܙܘܥܐ ܒܕܘܟܐ ܕܘܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 8, 'ܗܠܝܢ ܕܝܢ ܟܠܗܝܢ ܪܫܐ ܐܢܝܢ ܕܚܒܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 9, 'ܗܝܕܝܢ ܢܫܠܡܘܢܟܘܢ ܠܐܘܠܨܢܐ ܘܢܩܛܠܘܢܟܘܢ ܘܬܗܘܘܢ ܤܢܝܐܝܢ ܡܢ ܟܠܗܘܢ ܥܡܡܐ ܡܛܠ ܫܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 10, 'ܗܝܕܝܢ ܢܬܟܫܠܘܢ ܤܓܝܐܐ ܘܢܤܢܘܢ ܚܕ ܠܚܕ ܘܢܫܠܡܘܢ ܚܕ ܠܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 11, 'ܘܤܓܝܐܐ ܢܒܝܐ ܕܓܠܐ ܢܩܘܡܘܢ ܘܢܛܥܘܢ ܠܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 12, 'ܘܡܛܠ ܤܓܝܐܘܬ ܥܘܠܐ ܢܦܘܓ ܚܘܒܐ ܕܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 13, 'ܡܢ ܕܢܤܝܒܪ ܕܝܢ ܥܕܡܐ ܠܚܪܬܐ ܗܘ ܢܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 14, 'ܘܬܬܟܪܙ ܗܕܐ ܤܒܪܬܐ ܕܡܠܟܘܬܐ ܒܟܠܗ ܥܠܡܐ ܠܤܗܕܘܬܐ ܕܟܠܗܘܢ ܥܡܡܐ ܘܗܝܕܝܢ ܢܐܬܐ ܫܘܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 15, 'ܡܐ ܕܝܢ ܕܚܙܝܬܘܢ ܐܬܐ ܛܢܦܬܐ ܕܚܘܪܒܐ ܕܐܬܐܡܪ ܒܕܢܝܐܝܠ ܢܒܝܐ ܕܩܝܡܐ ܒܕܘܟܬܐ ܩܕܝܫܬܐ ܗܘ ܕܩܪܐ ܢܤܬܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 16, 'ܗܝܕܝܢ ܐܝܠܝܢ ܕܒܝܗܘܕ ܐܢܘܢ ܢܥܪܩܘܢ ܠܛܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 17, 'ܘܗܘ ܕܒܐܓܪܐ ܗܘ ܠܐ ܢܚܘܬ ܠܡܤܒ ܕܒܒܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 18, 'ܘܐܝܢܐ ܕܒܚܩܠܐ ܗܘ ܠܐ ܢܬܗܦܟ ܠܒܤܬܪܗ ܠܡܤܒ ܠܒܫܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 19, 'ܘܝ ܕܝܢ ܠܒܛܢܬܐ ܘܠܐܝܠܝܢ ܕܡܝܢܩܢ ܒܗܢܘܢ ܝܘܡܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 20, 'ܨܠܘ ܕܝܢ ܕܠܐ ܢܗܘܐ ܥܪܘܩܝܟܘܢ ܒܤܬܘܐ ܘܠܐ ܒܫܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 21, 'ܢܗܘܐ ܓܝܪ ܗܝܕܝܢ ܐܘܠܨܢܐ ܪܒܐ ܐܝܢܐ ܕܠܐ ܗܘܐ ܡܢ ܪܫܝܬܗ ܕܥܠܡܐ ܘܥܕܡܐ ܠܗܫܐ ܘܠܐ ܢܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 22, 'ܘܐܠܘ ܠܐ ܐܬܟܪܝܘ ܝܘܡܬܐ ܗܢܘܢ ܠܐ ܚܝܐ ܗܘܐ ܟܠ ܒܤܪ ܡܛܠ ܓܒܝܐ ܕܝܢ ܢܬܟܪܘܢ ܝܘܡܬܐ ܗܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 23, 'ܗܝܕܝܢ ܐܢ ܐܢܫ ܢܐܡܪ ܠܟܘܢ ܗܐ ܗܪܟܐ ܗܘ ܡܫܝܚܐ ܐܘ ܗܪܟܐ ܠܐ ܬܗܝܡܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 24, 'ܢܩܘܡܘܢ ܓܝܪ ܡܫܝܚܐ ܕܓܠܐ ܘܢܒܝܐ ܕܟܕܒܘܬܐ ܘܢܬܠܘܢ ܐܬܘܬܐ ܪܘܪܒܬܐ ܐܝܟ ܕܢܛܥܘܢ ܐܢ ܡܫܟܚܐ ܐܦ ܠܓܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 25, 'ܗܐ ܩܕܡܬ ܐܡܪܬ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 26, 'ܐܢ ܗܟܝܠ ܢܐܡܪܘܢ ܠܟܘܢ ܗܐ ܒܚܘܪܒܐ ܗܘ ܠܐ ܬܦܩܘܢ ܐܘ ܕܗܐ ܒܬܘܢܐ ܗܘ ܠܐ ܬܗܝܡܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 27, 'ܐܝܟܢܐ ܓܝܪ ܕܒܪܩܐ ܢܦܩ ܡܢ ܡܕܢܚܐ ܘܡܬܚܙܐ ܥܕܡܐ ܠܡܥܪܒܐ ܗܟܢܐ ܬܗܘܐ ܡܐܬܝܬܗ ܕܒܪܗ ܕܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 28, 'ܐܝܟܐ ܕܐܢ ܢܗܘܐ ܦܓܪܐ ܬܡܢ ܢܬܟܢܫܘܢ ܢܫܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 29, 'ܡܚܕܐ ܕܝܢ ܒܬܪ ܐܘܠܨܢܐ ܕܝܘܡܬܐ ܗܢܘܢ ܫܡܫܐ ܢܚܫܟ ܘܤܗܪܐ ܠܐ ܢܚܘܐ ܢܘܗܪܗ ܘܟܘܟܒܐ ܢܦܠܘܢ ܡܢ ܫܡܝܐ ܘܚܝܠܐ ܕܫܡܝܐ ܢܬܬܙܝܥܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 30, 'ܘܗܝܕܝܢ ܢܬܚܙܐ ܢܝܫܗ ܕܒܪܗ ܕܐܢܫܐ ܒܫܡܝܐ ܘܗܝܕܝܢ ܢܪܩܕܢ ܟܠܗܝܢ ܫܪܒܬܐ ܕܐܪܥܐ ܘܢܚܙܘܢ ܠܒܪܗ ܕܐܢܫܐ ܕܐܬܐ ܥܠ ܥܢܢܝ ܫܡܝܐ ܥܡ ܚܝܠܐ ܘܫܘܒܚܐ ܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 31, 'ܘܢܫܕܪ ܡܠܐܟܘܗܝ ܥܡ ܫܝܦܘܪܐ ܪܒܐ ܘܢܟܢܫܘܢ ܠܓܒܝܐ ܕܝܠܗ ܡܢ ܐܪܒܥܬ ܪܘܚܐ ܡܢ ܪܫܗܘܢ ܕܫܡܝܐ ܘܥܕܡܐ ܠܪܫܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 32, 'ܡܢ ܬܬܐ ܕܝܢ ܝܠܦܘ ܦܠܐܬܐ ܕܡܚܕܐ ܕܤܘܟܝܗ ܪܟܢ ܘܦܪܥܝܢ ܛܪܦܝܗ ܝܕܥܝܢ ܐܢܬܘܢ ܕܡܛܐ ܩܝܛܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 33, 'ܗܟܢܐ ܐܦ ܐܢܬܘܢ ܡܐ ܕܚܙܝܬܘܢ ܗܠܝܢ ܟܠܗܝܢ ܕܥܘ ܕܡܛܬ ܠܗ ܠܬܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 34, 'ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܠܐ ܬܥܒܪ ܫܪܒܬܐ ܗܕܐ ܥܕܡܐ ܕܗܠܝܢ ܟܠܗܝܢ ܢܗܘܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 35, 'ܫܡܝܐ ܘܐܪܥܐ ܢܥܒܪܘܢ ܘܡܠܝ ܠܐ ܢܥܒܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 36, 'ܥܠ ܝܘܡܐ ܕܝܢ ܗܘ ܘܥܠ ܫܥܬܐ ܗܝ ܐܢܫ ܠܐ ܝܕܥ ܐܦܠܐ ܡܠܐܟܐ ܕܫܡܝܐ ܐܠܐ ܐܒܐ ܒܠܚܘܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 37, 'ܐܝܟܢܐ ܕܝܢ ܕܝܘܡܝ ܢܘܚ ܗܟܢܐ ܬܗܘܐ ܡܐܬܝܬܗ ܕܒܪܗ ܕܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 38, 'ܐܝܟܢܐ ܓܝܪ ܕܐܝܬܝܗܘܢ ܗܘܘ ܩܕܡ ܛܘܦܢܐ ܐܟܠܝܢ ܘܫܬܝܢ ܘܢܤܒܝܢ ܢܫܐ ܘܝܗܒܝܢ ܠܓܒܪܐ ܥܕܡܐ ܠܝܘܡܐ ܕܥܠ ܢܘܚ ܠܟܘܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 39, 'ܘܠܐ ܝܕܥܘ ܥܕܡܐ ܕܐܬܐ ܛܘܦܢܐ ܘܫܩܠ ܠܟܠܗܘܢ ܗܟܢܐ ܬܗܘܐ ܡܐܬܝܬܗ ܕܒܪܗ ܕܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 40, 'ܗܝܕܝܢ ܬܪܝܢ ܢܗܘܘܢ ܒܩܪܝܬܐ ܚܕ ܢܬܕܒܪ ܘܚܕ ܢܫܬܒܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 41, 'ܘܬܪܬܝܢ ܢܗܘܝܢ ܛܚܢܢ ܒܪܚܝܐ ܚܕܐ ܡܬܕܒܪܐ ܘܚܕܐ ܡܫܬܒܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 42, 'ܐܬܬܥܝܪܘ ܗܟܝܠ ܕܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܒܐܝܕܐ ܫܥܬܐ ܐܬܐ ܡܪܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 43, 'ܗܕܐ ܕܝܢ ܕܥܘ ܕܐܠܘ ܝܕܥ ܗܘܐ ܡܪܐ ܒܝܬܐ ܒܐܝܕܐ ܡܛܪܬܐ ܐܬܐ ܓܢܒܐ ܡܬܬܥܝܪ ܗܘܐ ܘܠܐ ܫܒܩ ܗܘܐ ܕܢܬܦܠܫ ܒܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 44, 'ܡܛܠ ܗܢܐ ܐܦ ܐܢܬܘܢ ܗܘܘ ܡܛܝܒܝܢ ܕܒܫܥܬܐ ܕܠܐ ܤܒܪܝܢ ܐܢܬܘܢ ܢܐܬܐ ܒܪܗ ܕܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 45, 'ܡܢܘ ܟܝ ܐܝܬܘܗܝ ܥܒܕܐ ܡܗܝܡܢܐ ܘܚܟܝܡܐ ܕܐܩܝܡܗ ܡܪܗ ܥܠ ܒܢܝ ܒܝܬܗ ܕܢܬܠ ܠܗܘܢ ܤܝܒܪܬܐ ܒܙܒܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 46, 'ܛܘܒܘܗܝ ܠܥܒܕܐ ܗܘ ܕܢܐܬܐ ܡܪܗ ܢܫܟܚܝܘܗܝ ܕܥܒܕ ܗܟܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 47, 'ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܢܩܝܡܝܘܗܝ ܥܠ ܟܠ ܕܐܝܬ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 48, 'ܐܢ ܕܝܢ ܢܐܡܪ ܥܒܕܐ ܗܘ ܒܝܫܐ ܒܠܒܗ ܕܡܪܝ ܡܘܚܪ ܠܡܐܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 49, 'ܘܢܫܪܐ ܠܡܡܚܐ ܟܢܘܬܗ ܘܢܗܘܐ ܐܟܠ ܘܫܬܐ ܥܡ ܪܘܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 50, 'ܢܐܬܐ ܡܪܗ ܕܥܒܕܐ ܗܘ ܒܝܘܡܐ ܕܠܐ ܤܒܪ ܘܒܫܥܬܐ ܕܠܐ ܝܕܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 51, 'ܘܢܦܠܓܝܘܗܝ ܘܢܤܝܡ ܡܢܬܗ ܥܡ ܢܤܒܝ ܒܐܦܐ ܬܡܢ ܢܗܘܐ ܒܟܝܐ ܘܚܘܪܩ ܫܢܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 1, 'ܗܝܕܝܢ ܬܕܡܐ ܡܠܟܘܬܐ ܕܫܡܝܐ ܠܥܤܪ ܒܬܘܠܢ ܗܢܝܢ ܕܢܤܒ ܠܡܦܕܝܗܝܢ ܘܢܦܩ ܠܐܘܪܥ ܚܬܢܐ ܘܟܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 2, 'ܚܡܫ ܕܝܢ ܡܢܗܝܢ ܚܟܝܡܢ ܗܘܝ ܘܚܡܫ ܤܟܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 3, 'ܘܗܢܝܢ ܤܟܠܬܐ ܢܤܒ ܠܡܦܕܝܗܝܢ ܘܠܐ ܢܤܒ ܥܡܗܝܢ ܡܫܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 4, 'ܗܢܝܢ ܕܝܢ ܚܟܝܡܬܐ ܢܤܒ ܡܫܚܐ ܒܡܐܢܐ ܥܡ ܠܡܦܕܝܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 5, 'ܟܕ ܐܘܚܪ ܕܝܢ ܚܬܢܐ ܢܡ ܟܠܗܝܢ ܘܕܡܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 6, 'ܘܒܦܠܓܗ ܕܠܠܝܐ ܗܘܬ ܩܥܬܐ ܗܐ ܚܬܢܐ ܐܬܐ ܦܘܩܘ ܠܐܘܪܥܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 7, 'ܗܝܕܝܢ ܩܡ ܟܠܗܝܢ ܒܬܘܠܬܐ ܗܠܝܢ ܘܬܩܢ ܠܡܦܕܝܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 8, 'ܐܡܪܢ ܕܝܢ ܗܢܝܢ ܤܟܠܬܐ ܠܚܟܝܡܬܐ ܗܒܝܢ ܠܢ ܡܢ ܡܫܚܟܝܢ ܕܗܐ ܕܥܟܘ ܠܗܘܢ ܠܡܦܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 9, 'ܥܢܝ ܗܠܝܢ ܚܟܝܡܬܐ ܘܐܡܪܢ ܠܡܐ ܠܐ ܢܤܦܩ ܠܢ ܘܠܟܝܢ ܐܠܐ ܙܠܝܢ ܠܘܬ ܐܝܠܝܢ ܕܡܙܒܢܝܢ ܘܙܒܢܝܢ ܠܟܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 10, 'ܘܟܕ ܐܙܠ ܠܡܙܒܢ ܐܬܐ ܚܬܢܐ ܘܐܝܠܝܢ ܕܡܛܝܒܢ ܗܘܝ ܥܠ ܥܡܗ ܠܒܝܬ ܚܠܘܠܐ ܘܐܬܬܚܕ ܬܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 11, 'ܒܚܪܬܐ ܕܝܢ ܐܬܝ ܐܦ ܗܢܝܢ ܒܬܘܠܬܐ ܐܚܪܢܝܬܐ ܘܐܡܪܢ ܡܪܢ ܡܪܢ ܦܬܚ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 12, 'ܗܘ ܕܝܢ ܥܢܐ ܘܐܡܪ ܠܗܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܝܢ ܕܠܐ ܝܕܥ ܐܢܐ ܠܟܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 13, 'ܐܬܬܥܝܪܘ ܗܟܝܠ ܕܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܠܝܘܡܐ ܗܘ ܘܠܐ ܠܫܥܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 14, 'ܐܝܟ ܓܒܪܐ ܓܝܪ ܕܚܙܩ ܩܪܐ ܠܥܒܕܘܗܝ ܘܐܫܠܡ ܠܗܘܢ ܩܢܝܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 15, 'ܐܝܬ ܕܝܗܒ ܠܗ ܚܡܫ ܟܟܪܝܢ ܘܐܝܬ ܕܬܪܬܝܢ ܘܐܝܬ ܕܚܕܐ ܐܢܫ ܐܢܫ ܐܝܟ ܚܝܠܗ ܘܚܙܩ ܡܚܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 16, 'ܐܙܠ ܕܝܢ ܗܘ ܕܢܤܒ ܚܡܫ ܟܟܪܝܢ ܐܬܬܓܪ ܒܗܝܢ ܘܝܬܪ ܚܡܫ ܐܚܪܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 17, 'ܘܗܟܘܬ ܐܦ ܗܘ ܕܬܪܬܝܢ ܐܬܬܓܪ ܬܪܬܝܢ ܐܚܪܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 18, 'ܗܘ ܕܝܢ ܕܢܤܒ ܚܕܐ ܐܙܠ ܚܦܪ ܒܐܪܥܐ ܘܛܫܝ ܟܤܦܐ ܕܡܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 19, 'ܒܬܪ ܕܝܢ ܙܒܢܐ ܤܓܝܐܐ ܐܬܐ ܡܪܗܘܢ ܕܥܒܕܐ ܗܢܘܢ ܘܢܤܒ ܡܢܗܘܢ ܚܘܫܒܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 20, 'ܘܩܪܒ ܗܘ ܕܢܤܒ ܗܘܐ ܚܡܫ ܟܟܪܝܢ ܘܩܪܒ ܚܡܫ ܐܚܪܢܝܢ ܘܐܡܪ ܡܪܝ ܚܡܫ ܟܟܪܝܢ ܝܗܒܬ ܠܝ ܗܐ ܚܡܫ ܐܚܪܢܝܢ ܐܬܬܓܪܬ ܥܠܝܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 21, 'ܐܡܪ ܠܗ ܡܪܗ ܐܝܘ ܥܒܕܐ ܛܒܐ ܘܡܗܝܡܢܐ ܥܠ ܩܠܝܠ ܡܗܝܡܢ ܗܘܝܬ ܥܠ ܤܓܝ ܐܩܝܡܟ ܥܘܠ ܠܚܕܘܬܗ ܕܡܪܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 22, 'ܘܩܪܒ ܗܘ ܕܬܪܬܝܢ ܟܟܪܘܗܝ ܘܐܡܪ ܡܪܝ ܬܪܬܝܢ ܟܟܪܝܢ ܝܗܒܬ ܠܝ ܗܐ ܬܪܬܝܢ ܐܚܪܢܝܢ ܐܬܬܓܪܬ ܥܠܝܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 23, 'ܐܡܪ ܠܗ ܡܪܗ ܐܝܘ ܥܒܕܐ ܛܒܐ ܘܡܗܝܡܢܐ ܥܠ ܩܠܝܠ ܡܗܝܡܢ ܗܘܝܬ ܥܠ ܤܓܝ ܐܩܝܡܟ ܥܘܠ ܠܚܕܘܬܗ ܕܡܪܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 24, 'ܩܪܒ ܕܝܢ ܐܦ ܗܘ ܕܢܤܒ ܚܕܐ ܟܟܪܐ ܘܐܡܪ ܡܪܝ ܝܕܥ ܗܘܝܬ ܠܟ ܕܓܒܪܐ ܐܢܬ ܩܫܝܐ ܘܚܨܕ ܐܢܬ ܐܝܟܐ ܕܠܐ ܙܪܥܬ ܘܡܟܢܫ ܐܢܬ ܡܢ ܐܝܟܐ ܕܠܐ ܒܕܪܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 25, 'ܘܕܚܠܬ ܘܐܙܠܬ ܛܫܝܬܗ ܟܟܪܟ ܒܐܪܥܐ ܗܐ ܐܝܬ ܠܟ ܕܝܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 26, 'ܥܢܐ ܡܪܗ ܘܐܡܪ ܠܗ ܥܒܕܐ ܒܝܫܐ ܘܚܒܢܢܐ ܝܕܥ ܗܘܝܬ ܕܚܨܕ ܐܢܐ ܐܝܟܐ ܕܠܐ ܙܪܥܬ ܘܡܟܢܫ ܐܢܐ ܡܢ ܐܝܟܐ ܕܠܐ ܒܕܪܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 27, 'ܘܠܐ ܗܘܐ ܠܟ ܕܬܪܡܐ ܟܤܦܝ ܥܠ ܦܬܘܪܐ ܘܐܬܐ ܗܘܝܬ ܐܢܐ ܘܬܒܥ ܗܘܝܬ ܕܝܠܝ ܥܡ ܪܒܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 28, 'ܤܒܘ ܗܟܝܠ ܡܢܗ ܟܟܪܐ ܘܗܒܘܗ ܠܗܘ ܕܐܝܬ ܠܗ ܥܤܪ ܟܟܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 29, 'ܠܡܢ ܓܝܪ ܕܐܝܬ ܠܗ ܢܬܝܗܒ ܠܗ ܘܢܬܬܘܤܦ ܠܗ ܘܗܘ ܕܝܢ ܕܠܝܬ ܠܗ ܘܐܦ ܗܘ ܕܐܝܬ ܠܗ ܢܫܬܩܠ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 30, 'ܘܠܥܒܕܐ ܒܛܝܠܐ ܐܦܩܘܗܝ ܠܚܫܘܟܐ ܒܪܝܐ ܬܡܢ ܢܗܘܐ ܒܟܝܐ ܘܚܘܪܩ ܫܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 31, 'ܡܐ ܕܐܬܐ ܕܝܢ ܒܪܗ ܕܐܢܫܐ ܒܫܘܒܚܗ ܘܟܠܗܘܢ ܡܠܐܟܘܗܝ ܩܕܝܫܐ ܥܡܗ ܗܝܕܝܢ ܢܬܒ ܥܠ ܬܪܢܘܤ ܕܫܘܒܚܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 32, 'ܘܢܬܟܢܫܘܢ ܩܕܡܘܗܝ ܟܠܗܘܢ ܥܡܡܐ ܘܢܦܪܫ ܐܢܘܢ ܚܕ ܡܢ ܚܕ ܐܝܟ ܪܥܝܐ ܕܡܦܪܫ ܥܪܒܐ ܡܢ ܓܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 33, 'ܘܢܩܝܡ ܥܪܒܐ ܡܢ ܝܡܝܢܗ ܘܓܕܝܐ ܡܢ ܤܡܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 34, 'ܗܝܕܝܢ ܢܐܡܪ ܡܠܟܐ ܠܗܢܘܢ ܕܡܢ ܝܡܝܢܗ ܬܘ ܒܪܝܟܘܗܝ ܕܐܒܝ ܝܪܬܘ ܡܠܟܘܬܐ ܕܥܬܝܕܐ ܗܘܬ ܠܟܘܢ ܡܢ ܬܪܡܝܬܗ ܕܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 35, 'ܟܦܢܬ ܓܝܪ ܘܝܗܒܬܘܢ ܠܝ ܠܡܐܟܠ ܘܨܗܝܬ ܘܐܫܩܝܬܘܢܢܝ ܐܟܤܢܝܐ ܗܘܝܬ ܘܟܢܫܬܘܢܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 36, 'ܥܪܛܠܝܐ ܗܘܝܬ ܘܟܤܝܬܘܢܢܝ ܟܪܝܗ ܗܘܝܬ ܘܤܥܪܬܘܢܢܝ ܘܒܝܬ ܐܤܝܪܐ ܗܘܝܬ ܘܐܬܝܬܘܢ ܠܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 37, 'ܗܝܕܝܢ ܢܐܡܪܘܢ ܠܗ ܗܢܘܢ ܙܕܝܩܐ ܡܪܢ ܐܡܬܝ ܚܙܝܢܟ ܕܟܦܢ ܐܢܬ ܘܬܪܤܝܢܟ ܐܘ ܕܨܗܐ ܐܢܬ ܘܐܫܩܝܢܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 38, 'ܘܐܡܬܝ ܚܙܝܢܟ ܕܐܟܤܢܝܐ ܐܢܬ ܘܟܢܫܢܟ ܐܘ ܕܥܪܛܠܝ ܐܢܬ ܘܟܤܝܢܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 39, 'ܘܐܡܬܝ ܚܙܝܢܟ ܟܪܝܗܐ ܐܘ ܒܝܬ ܐܤܝܪܐ ܘܐܬܝܢ ܠܘܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 40, 'ܘܥܢܐ ܡܠܟܐ ܘܐܡܪ ܠܗܘܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܟܡܐ ܕܥܒܕܬܘܢ ܠܚܕ ܡܢ ܗܠܝܢ ܐܚܝ ܙܥܘܪܐ ܠܝ ܗܘ ܥܒܕܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 41, 'ܗܝܕܝܢ ܢܐܡܪ ܐܦ ܠܗܢܘܢ ܕܡܢ ܤܡܠܗ ܙܠܘ ܠܟܘܢ ܡܢܝ ܠܝܛܐ ܠܢܘܪܐ ܕܠܥܠܡ ܗܝ ܕܡܛܝܒܐ ܠܐܟܠܩܪܨܐ ܘܠܡܠܐܟܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 42, 'ܟܦܢܬ ܓܝܪ ܘܠܐ ܝܗܒܬܘܢ ܠܝ ܠܡܐܟܠ ܘܨܗܝܬ ܘܠܐ ܐܫܩܝܬܘܢܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 43, 'ܘܐܟܤܢܝܐ ܗܘܝܬ ܘܠܐ ܟܢܫܬܘܢܢܝ ܘܥܪܛܠܝܐ ܗܘܝܬ ܘܠܐ ܟܤܝܬܘܢܢܝ ܘܟܪܝܗܐ ܗܘܝܬ ܘܒܝܬ ܐܤܝܪܐ ܗܘܝܬ ܘܠܐ ܤܥܪܬܘܢܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 44, 'ܗܝܕܝܢ ܢܥܢܘܢ ܐܦ ܗܢܘܢ ܘܢܐܡܪܘܢ ܡܪܢ ܐܡܬܝ ܚܙܝܢܟ ܟܦܢܐ ܐܘ ܨܗܝܐ ܐܘ ܐܟܤܢܝܐ ܐܘ ܥܪܛܠܝܐ ܐܘ ܟܪܝܗܐ ܐܘ ܒܝܬ ܐܤܝܪܐ ܘܠܐ ܫܡܫܢܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 45, 'ܗܝܕܝܢ ܢܥܢܐ ܘܢܐܡܪ ܠܗܘܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܟܡܐ ܕܠܐ ܥܒܕܬܘܢ ܠܚܕ ܡܢ ܗܠܝܢ ܙܥܘܪܐ ܐܦ ܠܐ ܠܝ ܥܒܕܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 46, 'ܘܢܐܙܠܘܢ ܗܠܝܢ ܠܬܫܢܝܩܐ ܕܠܥܠܡ ܘܙܕܝܩܐ ܠܚܝܐ ܕܠܥܠܡ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 1, 'ܘܗܘܐ ܕܟܕ ܫܠܡ ܝܫܘܥ ܟܠܗܝܢ ܡܠܐ ܗܠܝܢ ܐܡܪ ܠܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 2, 'ܝܕܥܝܢ ܐܢܬܘܢ ܕܒܬܪ ܬܪܝܢ ܝܘܡܝܢ ܗܘܐ ܦܨܚܐ ܘܒܪܗ ܕܐܢܫܐ ܡܫܬܠܡ ܕܢܙܕܩܦ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 3, 'ܗܝܕܝܢ ܐܬܟܢܫܘ ܪܒܝ ܟܗܢܐ ܘܤܦܪܐ ܘܩܫܝܫܐ ܕܥܡܐ ܠܕܪܬܗ ܕܪܒ ܟܗܢܐ ܕܡܬܩܪܐ ܩܝܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 4, 'ܘܐܬܡܠܟܘ ܥܠ ܝܫܘܥ ܕܒܢܟܠܐ ܢܐܚܕܘܢܝܗܝ ܘܢܩܛܠܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 5, 'ܘܐܡܪܝܢ ܗܘܘ ܠܐ ܒܥܕܥܕܐ ܕܠܐ ܢܗܘܐ ܫܓܘܫܝܐ ܒܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 6, 'ܘܟܕ ܗܘܐ ܝܫܘܥ ܒܒܝܬ ܥܢܝܐ ܒܒܝܬܗ ܕܫܡܥܘܢ ܓܪܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 7, 'ܩܪܒܬ ܠܗ ܐܢܬܬܐ ܕܐܝܬ ܥܠܝܗ ܫܛܝܦܬܐ ܕܡܫܚܐ ܕܒܤܡܐ ܤܓܝ ܕܡܝܐ ܘܐܫܦܥܬܗ ܥܠ ܪܫܗ ܕܝܫܘܥ ܟܕ ܤܡܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 8, 'ܚܙܘ ܕܝܢ ܬܠܡܝܕܘܗܝ ܘܐܬܒܐܫ ܠܗܘܢ ܘܐܡܪܘ ܠܡܢܐ ܐܒܕܢܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 9, 'ܡܫܟܚ ܗܘܐ ܓܝܪ ܕܢܙܕܒܢ ܗܢܐ ܒܤܓܝ ܘܢܬܝܗܒ ܠܡܤܟܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 10, 'ܝܫܘܥ ܕܝܢ ܝܕܥ ܘܐܡܪ ܠܗܘܢ ܡܢܐ ܡܠܐܝܢ ܐܢܬܘܢ ܠܗ ܠܐܢܬܬܐ ܥܒܕܐ ܫܦܝܪܐ ܥܒܕܬ ܠܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 11, 'ܒܟܠܙܒܢ ܓܝܪ ܡܤܟܢܐ ܐܝܬ ܠܟܘܢ ܥܡܟܘܢ ܠܝ ܕܝܢ ܠܐ ܒܟܠܙܒܢ ܐܝܬ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 12, 'ܗܕܐ ܕܝܢ ܕܐܪܡܝܬ ܒܤܡܐ ܗܢܐ ܥܠ ܓܘܫܡܝ ܐܝܟ ܕܠܡܩܒܪܢܝ ܥܒܕܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 13, 'ܘܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܐܝܟܐ ܕܬܬܟܪܙ ܤܒܪܬܝ ܗܕܐ ܒܟܠܗ ܥܠܡܐ ܢܬܡܠܠ ܐܦ ܡܕܡ ܕܥܒܕܬ ܗܕܐ ܠܕܘܟܪܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 14, 'ܗܝܕܝܢ ܐܙܠ ܚܕ ܡܢ ܬܪܥܤܪ ܕܡܬܩܪܐ ܝܗܘܕܐ ܤܟܪܝܘܛܐ ܠܘܬ ܪܒܝ ܟܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 15, 'ܘܐܡܪ ܠܗܘܢ ܡܢܐ ܨܒܝܢ ܐܢܬܘܢ ܠܡܬܠ ܠܝ ܘܐܢܐ ܡܫܠܡ ܐܢܐ ܠܗ ܠܟܘܢ ܗܢܘܢ ܕܝܢ ܐܩܝܡܘ ܠܗ ܬܠܬܝܢ ܕܟܤܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 16, 'ܘܡܢ ܗܝܕܝܢ ܒܥܐ ܗܘܐ ܠܗ ܦܠܥܐ ܕܢܫܠܡܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 17, 'ܒܝܘܡܐ ܕܝܢ ܩܕܡܝܐ ܕܦܛܝܪܐ ܩܪܒܘ ܬܠܡܝܕܐ ܠܘܬ ܝܫܘܥ ܘܐܡܪܘ ܠܗ ܐܝܟܐ ܨܒܐ ܐܢܬ ܕܢܛܝܒ ܠܟ ܕܬܠܥܤ ܦܨܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 18, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܙܠܘ ܠܡܕܝܢܬܐ ܠܘܬ ܦܠܢ ܘܐܡܪܘ ܠܗ ܪܒܢ ܐܡܪ ܙܒܢܝ ܡܛܐ ܠܗ ܠܘܬܟ ܥܒܕ ܐܢܐ ܦܨܚܐ ܥܡ ܬܠܡܝܕܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 19, 'ܘܬܠܡܝܕܘܗܝ ܥܒܕܘ ܐܝܟܢܐ ܕܦܩܕ ܠܗܘܢ ܝܫܘܥ ܘܛܝܒܘ ܦܨܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 20, 'ܘܟܕ ܗܘܐ ܪܡܫܐ ܤܡܝܟ ܗܘܐ ܥܡ ܬܪܥܤܪ ܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 21, 'ܘܟܕ ܠܥܤܝܢ ܐܡܪ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܚܕ ܡܢܟܘܢ ܡܫܠܡ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 22, 'ܘܟܪܝܬ ܠܗܘܢ ܛܒ ܘܫܪܝܘ ܠܡܐܡܪ ܠܗ ܚܕ ܚܕ ܡܢܗܘܢ ܠܡܐ ܐܢܐ ܡܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 23, 'ܗܘ ܕܝܢ ܥܢܐ ܘܐܡܪ ܡܢ ܕܨܒܥ ܐܝܕܗ ܥܡܝ ܒܠܓܬܐ ܗܘ ܢܫܠܡܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 24, 'ܘܒܪܗ ܕܐܢܫܐ ܐܙܠ ܐܝܟܢܐ ܕܟܬܝܒ ܥܠܘܗܝ ܘܝ ܠܗ ܕܝܢ ܠܓܒܪܐ ܗܘ ܕܒܐܝܕܗ ܒܪܗ ܕܐܢܫܐ ܡܫܬܠܡ ܦܩܚ ܗܘܐ ܠܗ ܠܓܒܪܐ ܗܘ ܐܠܘ ܠܐ ܐܬܝܠܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 25, 'ܥܢܐ ܝܗܘܕܐ ܡܫܠܡܢܐ ܘܐܡܪ ܕܠܡܐ ܐܢܐ ܗܘ ܪܒܝ ܐܡܪ ܠܗ ܝܫܘܥ ܐܢܬ ܐܡܪܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 26, 'ܟܕ ܕܝܢ ܠܥܤܝܢ ܫܩܠ ܝܫܘܥ ܠܚܡܐ ܘܒܪܟ ܘܩܨܐ ܘܝܗܒ ܠܬܠܡܝܕܘܗܝ ܘܐܡܪ ܤܒܘ ܐܟܘܠܘ ܗܢܘ ܦܓܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 27, 'ܘܫܩܠ ܟܤܐ ܘܐܘܕܝ ܘܝܗܒ ܠܗܘܢ ܘܐܡܪ ܤܒܘ ܐܫܬܘ ܡܢܗ ܟܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 28, 'ܗܢܘ ܕܡܝ ܕܕܝܬܩܐ ܚܕܬܐ ܕܚܠܦ ܤܓܝܐܐ ܡܬܐܫܕ ܠܫܘܒܩܢܐ ܕܚܛܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 29, 'ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܝܢ ܕܠܐ ܐܫܬܐ ܡܢ ܗܫܐ ܡܢ ܗܢܐ ܝܠܕܐ ܕܓܦܬܐ ܥܕܡܐ ܠܝܘܡܐ ܕܒܗ ܐܫܬܝܘܗܝ ܥܡܟܘܢ ܚܕܬܐ ܒܡܠܟܘܬܗ ܕܐܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 30, 'ܘܫܒܚܘ ܘܢܦܩܘ ܠܛܘܪ ܙܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 31, 'ܗܝܕܝܢ ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܢܬܘܢ ܟܠܟܘܢ ܬܬܟܫܠܘܢ ܒܝ ܒܗܢܐ ܠܠܝܐ ܟܬܝܒ ܓܝܪ ܕܐܡܚܐ ܠܪܥܝܐ ܘܢܬܒܕܪܘܢ ܥܪܒܐ ܕܥܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 32, 'ܡܢ ܒܬܪ ܕܩܐܡ ܐܢܐ ܕܝܢ ܩܕܡ ܐܢܐ ܠܟܘܢ ܠܓܠܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 33, 'ܥܢܐ ܟܐܦܐ ܘܐܡܪ ܠܗ ܐܦܢ ܟܠܢܫ ܢܬܟܫܠ ܒܟ ܐܢܐ ܡܬܘܡ ܠܐ ܐܬܟܫܠ ܒܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 34, 'ܐܡܪ ܠܗ ܝܫܘܥ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟ ܕܒܗܢܐ ܠܠܝܐ ܩܕܡ ܕܢܩܪܐ ܬܪܢܓܠܐ ܬܠܬ ܙܒܢܝܢ ܬܟܦܘܪ ܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 35, 'ܐܡܪ ܠܗ ܟܐܦܐ ܐܢ ܢܗܘܐ ܠܝ ܠܡܡܬ ܥܡܟ ܠܐ ܐܟܦܘܪ ܒܟ ܘܗܟܘܬ ܐܦ ܟܠܗܘܢ ܬܠܡܝܕܐ ܐܡܪܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 36, 'ܗܝܕܝܢ ܐܬܐ ܥܡܗܘܢ ܝܫܘܥ ܠܕܘܟܬܐ ܕܡܬܩܪܝܐ ܓܕܤܡܢ ܘܐܡܪ ܠܬܠܡܝܕܘܗܝ ܬܒܘ ܗܪܟܐ ܥܕ ܐܙܠ ܐܨܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 37, 'ܘܕܒܪ ܠܟܐܦܐ ܘܠܬܪܝܗܘܢ ܒܢܝ ܙܒܕܝ ܘܫܪܝ ܠܡܬܟܡܪܘ ܘܠܡܬܥܩܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 38, 'ܘܐܡܪ ܠܗܘܢ ܟܪܝܐ ܗܝ ܠܗ ܠܢܦܫܝ ܥܕܡܐ ܠܡܘܬܐ ܩܘܘ ܠܝ ܗܪܟܐ ܘܫܗܪܘ ܥܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 39, 'ܘܦܪܩ ܩܠܝܠ ܘܢܦܠ ܥܠ ܐܦܘܗܝ ܘܡܨܠܐ ܗܘܐ ܘܐܡܪ ܐܒܝ ܐܢ ܡܫܟܚܐ ܢܥܒܪܢܝ ܟܤܐ ܗܢܐ ܒܪܡ ܠܐ ܐܝܟ ܕܐܢܐ ܨܒܐ ܐܢܐ ܐܠܐ ܐܝܟ ܕܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 40, 'ܘܐܬܐ ܠܘܬ ܬܠܡܝܕܘܗܝ ܘܐܫܟܚ ܐܢܘܢ ܟܕ ܕܡܟܝܢ ܘܐܡܪ ܠܟܐܦܐ ܗܟܢܐ ܠܐ ܐܫܟܚܬܘܢ ܚܕܐ ܫܥܐ ܕܬܫܗܪܘܢ ܥܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 41, 'ܐܬܬܥܝܪܘ ܘܨܠܘ ܕܠܐ ܬܥܠܘܢ ܠܢܤܝܘܢܐ ܪܘܚܐ ܡܛܝܒܐ ܦܓܪܐ ܕܝܢ ܟܪܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 42, 'ܬܘܒ ܐܙܠ ܕܬܪܬܝܢ ܙܒܢܝܢ ܨܠܝ ܘܐܡܪ ܐܒܝ ܐܢ ܠܐ ܡܫܟܚ ܗܢܐ ܟܤܐ ܕܢܥܒܪ ܐܠܐ ܐܢ ܐܫܬܝܬܗ ܢܗܘܐ ܨܒܝܢܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 43, 'ܘܐܬܐ ܬܘܒ ܐܫܟܚ ܐܢܘܢ ܟܕ ܕܡܟܝܢ ܥܝܢܝܗܘܢ ܓܝܪ ܝܩܝܪܢ ܗܘܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 44, 'ܘܫܒܩ ܐܢܘܢ ܘܐܙܠ ܬܘܒ ܨܠܝ ܕܬܠܬ ܙܒܢܝܢ ܘܠܗ ܠܡܠܬܐ ܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 45, 'ܗܝܕܝܢ ܐܬܐ ܠܘܬ ܬܠܡܝܕܘܗܝ ܘܐܡܪ ܠܗܘܢ ܕܡܟܘ ܡܟܝܠ ܘܐܬܬܢܝܚܘ ܗܐ ܡܛܬ ܫܥܬܐ ܘܒܪܗ ܕܐܢܫܐ ܡܫܬܠܡ ܒܐܝܕܝܗܘܢ ܕܚܛܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 46, 'ܩܘܡܘ ܢܐܙܠ ܗܐ ܡܛܐ ܗܘ ܕܡܫܠܡ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 47, 'ܘܥܕ ܗܘ ܡܡܠܠ ܗܐ ܝܗܘܕܐ ܡܫܠܡܢܐ ܚܕ ܡܢ ܬܪܥܤܪܬܐ ܐܬܐ ܘܟܢܫܐ ܥܡܗ ܤܓܝܐܐ ܥܡ ܤܦܤܪܐ ܘܚܘܛܪܐ ܡܢ ܠܘܬ ܪܒܝ ܟܗܢܐ ܘܩܫܝܫܐ ܕܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 48, 'ܘܝܗܒ ܗܘܐ ܠܗܘܢ ܐܬܐ ܝܗܘܕܐ ܡܫܠܡܢܐ ܘܐܡܪ ܠܗܘ ܕܢܫܩ ܐܢܐ ܗܘܝܘ ܠܗ ܐܚܘܕܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 49, 'ܘܡܚܕܐ ܩܪܒ ܠܘܬ ܝܫܘܥ ܘܐܡܪ ܫܠܡ ܪܒܝ ܘܢܫܩܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 50, 'ܗܘ ܕܝܢ ܝܫܘܥ ܐܡܪ ܠܗ ܥܠ ܗܝ ܕܐܬܝܬ ܚܒܪܝ ܗܝܕܝܢ ܐܬܩܪܒܘ ܘܐܪܡܝܘ ܐܝܕܝܗܘܢ ܥܠ ܝܫܘܥ ܘܐܚܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 51, 'ܘܗܐ ܚܕ ܡܢ ܗܢܘܢ ܕܥܡ ܝܫܘܥ ܐܘܫܛ ܐܝܕܗ ܘܫܡܛ ܤܦܤܪܐ ܘܡܚܝܗܝ ܠܥܒܕܗ ܕܪܒ ܟܗܢܐ ܘܫܩܠܗ ܐܕܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 52, 'ܗܝܕܝܢ ܐܡܪ ܠܗ ܝܫܘܥ ܐܗܦܟ ܤܦܤܪܐ ܠܕܘܟܬܗ ܟܠܗܘܢ ܓܝܪ ܗܢܘܢ ܕܢܤܒܘ ܤܝܦܐ ܒܤܝܦܐ ܢܡܘܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 53, 'ܐܘ ܤܒܪ ܐܢܬ ܕܠܐ ܡܫܟܚ ܐܢܐ ܕܐܒܥܐ ܡܢ ܐܒܝ ܘܢܩܝܡ ܠܝ ܗܫܐ ܝܬܝܪ ܡܢ ܬܪܬܥܤܪܐ ܠܓܝܘܢܝܢ ܕܡܠܐܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 54, 'ܐܝܟܢܐ ܗܟܝܠ ܢܬܡܠܘܢ ܟܬܒܐ ܕܗܟܢܐ ܘܠܐ ܕܢܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 55, 'ܒܗܝ ܫܥܬܐ ܐܡܪ ܝܫܘܥ ܠܟܢܫܐ ܐܝܟ ܕܥܠ ܓܝܤܐ ܢܦܩܬܘܢ ܒܤܦܤܪܐ ܘܒܚܘܛܪܐ ܕܬܐܚܕܘܢܢܝ ܟܠܝܘܡ ܠܘܬܟܘܢ ܒܗܝܟܠܐ ܝܬܒ ܗܘܝܬ ܘܡܠܦ ܘܠܐ ܐܚܕܬܘܢܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 56, 'ܗܕܐ ܕܝܢ ܕܗܘܬ ܕܢܬܡܠܘܢ ܟܬܒܐ ܕܢܒܝܐ ܗܝܕܝܢ ܬܠܡܝܕܐ ܟܠܗܘܢ ܫܒܩܘܗܝ ܘܥܪܩܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 57, 'ܘܗܢܘܢ ܕܐܚܕܘܗܝ ܠܝܫܘܥ ܐܘܒܠܘܗܝ ܠܘܬ ܩܝܦܐ ܪܒ ܟܗܢܐ ܐܝܟܐ ܕܤܦܪܐ ܘܩܫܝܫܐ ܟܢܝܫܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 58, 'ܫܡܥܘܢ ܕܝܢ ܟܐܦܐ ܐܙܠ ܗܘܐ ܒܬܪܗ ܡܢ ܪܘܚܩܐ ܥܕܡܐ ܠܕܪܬܗ ܕܪܒ ܟܗܢܐ ܘܥܠ ܝܬܒ ܠܓܘ ܥܡ ܕܚܫܐ ܕܢܚܙܐ ܚܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 59, 'ܪܒܝ ܟܗܢܐ ܕܝܢ ܘܩܫܝܫܐ ܘܟܢܘܫܬܐ ܟܠܗ ܒܥܝܢ ܗܘܘ ܥܠ ܝܫܘܥ ܤܗܕܐ ܐܝܟ ܕܢܡܝܬܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 60, 'ܘܠܐ ܐܫܟܚܘ ܘܐܬܘ ܤܓܝܐܐ ܤܗܕܐ ܕܫܘܩܪܐ ܐܚܪܝܬ ܕܝܢ ܩܪܒܘ ܬܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 61, 'ܘܐܡܪܝܢ ܗܢܐ ܐܡܪ ܕܡܫܟܚ ܐܢܐ ܕܐܫܪܐ ܗܝܟܠܐ ܕܐܠܗܐ ܘܠܬܠܬܐ ܝܘܡܝܢ ܐܒܢܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 62, 'ܘܩܡ ܪܒ ܟܗܢܐ ܘܐܡܪ ܠܗ ܠܐ ܡܕܡ ܡܦܢܐ ܐܢܬ ܦܬܓܡܐ ܡܢܐ ܡܤܗܕܝܢ ܥܠܝܟ ܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 63, 'ܝܫܘܥ ܕܝܢ ܫܬܝܩ ܗܘܐ ܘܥܢܐ ܪܒ ܟܗܢܐ ܘܐܡܪ ܠܗ ܡܘܡܐ ܐܢܐ ܠܟ ܒܐܠܗܐ ܚܝܐ ܕܬܐܡܪ ܠܢ ܐܢ ܐܢܬ ܗܘ ܡܫܝܚܐ ܒܪܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 64, 'ܐܡܪ ܠܗ ܝܫܘܥ ܐܢܬ ܐܡܪܬ ܐܡܪܢܐ ܠܟܘܢ ܕܝܢ ܕܡܢ ܗܫܐ ܬܚܙܘܢܝܗܝ ܠܒܪܗ ܕܐܢܫܐ ܕܝܬܒ ܡܢ ܝܡܝܢܐ ܕܚܝܠܐ ܘܐܬܐ ܥܠ ܥܢܢܝ ܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 65, 'ܗܝܕܝܢ ܪܒ ܟܗܢܐ ܨܪܝ ܡܐܢܘܗܝ ܘܐܡܪ ܗܐ ܓܕܦ ܡܢܐ ܡܟܝܠ ܡܬܒܥܝܢ ܠܢ ܤܗܕܐ ܗܐ ܗܫܐ ܫܡܥܬܘܢ ܓܘܕܦܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 66, 'ܡܢܐ ܨܒܝܢ ܐܢܬܘܢ ܥܢܘ ܘܐܡܪܝܢ ܚܝܒ ܗܘ ܡܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 67, 'ܗܝܕܝܢ ܪܩܘ ܒܐܦܘܗܝ ܘܡܩܦܚܝܢ ܗܘܘ ܠܗ ܐܚܪܢܐ ܕܝܢ ܡܚܝܢ ܗܘܘ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 68, 'ܘܐܡܪܝܢ ܐܬܢܒܐ ܠܢ ܡܫܝܚܐ ܡܢܘ ܗܘ ܕܡܚܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 69, 'ܟܐܦܐ ܕܝܢ ܝܬܒ ܗܘܐ ܠܒܪ ܒܕܪܬܐ ܘܩܪܒܬ ܠܘܬܗ ܐܡܬܐ ܚܕܐ ܘܐܡܪܐ ܠܗ ܐܦ ܐܢܬ ܥܡ ܝܫܘܥ ܗܘܝܬ ܢܨܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 70, 'ܗܘ ܕܝܢ ܟܦܪ ܩܕܡ ܟܠܗܘܢ ܘܐܡܪ ܠܐ ܝܕܥ ܐܢܐ ܡܢܐ ܐܡܪܐ ܐܢܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 71, 'ܘܟܕ ܢܦܩ ܠܤܦܐ ܚܙܬܗ ܐܚܪܬܐ ܘܐܡܪܐ ܠܗܘܢ ܕܬܡܢ ܗܘܐ ܐܦ ܗܢܐ ܥܡ ܝܫܘܥ ܢܨܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 72, 'ܘܬܘܒ ܟܦܪ ܒܡܘܡܬܐ ܕܠܐ ܝܕܥ ܐܢܐ ܠܗ ܠܓܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 73, 'ܡܢ ܒܬܪ ܩܠܝܠ ܕܝܢ ܩܪܒܘ ܗܢܘܢ ܕܩܝܡܝܢ ܘܐܡܪܘ ܠܟܐܦܐ ܫܪܝܪܐܝܬ ܐܦ ܐܢܬ ܡܢܗܘܢ ܐܢܬ ܐܦ ܡܡܠܠܟ ܓܝܪ ܡܘܕܥ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 74, 'ܗܝܕܝܢ ܫܪܝ ܠܡܚܪܡܘ ܘܠܡܐܡܐ ܕܠܐ ܝܕܥܢܐ ܠܗ ܠܓܒܪܐ ܘܒܗ ܒܫܥܬܐ ܩܪܐ ܬܪܢܓܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 75, 'ܘܐܬܕܟܪ ܟܐܦܐ ܡܠܬܐ ܕܝܫܘܥ ܕܐܡܪ ܠܗ ܕܩܕܡ ܕܢܩܪܐ ܬܪܢܓܠܐ ܬܠܬ ܙܒܢܝܢ ܬܟܦܘܪ ܒܝ ܘܢܦܩ ܠܒܪ ܒܟܐ ܡܪܝܪܐܝܬ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 1, 'ܟܕ ܕܝܢ ܗܘܐ ܨܦܪܐ ܡܠܟܐ ܢܤܒܘ ܥܠ ܝܫܘܥ ܟܠܗܘܢ ܪܒܝ ܟܗܢܐ ܘܩܫܝܫܐ ܕܥܡܐ ܐܝܟ ܕܢܡܝܬܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 2, 'ܘܐܤܪܘܗܝ ܘܐܘܒܠܘܗܝ ܘܐܫܠܡܘܗܝ ܠܦܝܠܛܘܤ ܗܓܡܘܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 3, 'ܗܝܕܝܢ ܝܗܘܕܐ ܡܫܠܡܢܐ ܟܕ ܚܙܐ ܕܐܬܚܝܒ ܝܫܘܥ ܐܬܬܘܝ ܘܐܙܠ ܐܗܦܟ ܗܠܝܢ ܬܠܬܝܢ ܕܟܤܦܐ ܠܪܒܝ ܟܗܢܐ ܘܠܩܫܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 4, 'ܘܐܡܪ ܚܛܝܬ ܕܐܫܠܡܬ ܕܡܐ ܙܟܝܐ ܗܢܘܢ ܕܝܢ ܐܡܪܘ ܠܗ ܠܢ ܡܐ ܠܢ ܐܢܬ ܝܕܥ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 5, 'ܘܫܕܝܗܝ ܟܤܦܐ ܒܗܝܟܠܐ ܘܫܢܝ ܘܐܙܠ ܚܢܩ ܢܦܫܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 6, 'ܪܒܝ ܟܗܢܐ ܕܝܢ ܫܩܠܘܗܝ ܠܟܤܦܐ ܘܐܡܪܘ ܠܐ ܫܠܝܛ ܕܢܪܡܝܘܗܝ ܒܝܬ ܩܘܪܒܢܐ ܡܛܠ ܕܛܝܡܝ ܕܡܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 7, 'ܘܢܤܒܘ ܡܠܟܐ ܘܙܒܢܘ ܒܗ ܐܓܘܪܤܗ ܕܦܚܪܐ ܠܒܝܬ ܩܒܘܪܐ ܕܐܟܤܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 8, 'ܡܛܠ ܗܢܐ ܐܬܩܪܝ ܐܓܘܪܤܐ ܗܘ ܩܪܝܬܐ ܕܕܡܐ ܥܕܡܐ ܠܝܘܡܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 9, 'ܗܝܕܝܢ ܐܬܡܠܝ ܡܕܡ ܕܐܬܐܡܪ ܒܝܕ ܢܒܝܐ ܕܐܡܪ ܕܢܤܒܬ ܬܠܬܝܢ ܕܟܤܦܐ ܕܡܘܗܝ ܕܝܩܝܪܐ ܕܩܨܘ ܡܢ ܒܢܝ ܐܝܤܪܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 10, 'ܘܝܗܒܬ ܐܢܘܢ ܠܐܓܘܪܤܗ ܕܦܚܪܐ ܐܝܟ ܕܦܩܕ ܠܝ ܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 11, 'ܗܘ ܕܝܢ ܝܫܘܥ ܩܡ ܩܕܡ ܗܓܡܘܢܐ ܘܫܐܠܗ ܗܓܡܘܢܐ ܘܐܡܪ ܠܗ ܐܢܬ ܗܘ ܡܠܟܐ ܕܝܗܘܕܝܐ ܐܡܪ ܠܗ ܝܫܘܥ ܐܢܬ ܐܡܪܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 12, 'ܘܟܕ ܐܟܠܝܢ ܗܘܘ ܩܪܨܘܗܝ ܪܒܝ ܟܗܢܐ ܘܩܫܝܫܐ ܡܕܡ ܦܬܓܡܐ ܗܘ ܠܐ ܦܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 13, 'ܗܝܕܝܢ ܐܡܪ ܠܗ ܦܝܠܛܘܤ ܠܐ ܫܡܥ ܐܢܬ ܟܡܐ ܡܤܗܕܝܢ ܥܠܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 14, 'ܘܠܐ ܝܗܒ ܠܗ ܦܬܓܡܐ ܘܠܐ ܒܚܕܐ ܡܠܐ ܘܥܠ ܗܕܐ ܐܬܕܡܪ ܛܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 15, 'ܒܟܠ ܥܐܕܐ ܕܝܢ ܡܥܕ ܗܘܐ ܗܓܡܘܢܐ ܕܢܫܪܐ ܐܤܝܪܐ ܚܕ ܠܥܡܐ ܐܝܢܐ ܕܗܢܘܢ ܨܒܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 16, 'ܐܤܝܪ ܗܘܐ ܠܗܘܢ ܕܝܢ ܐܤܝܪܐ ܝܕܝܥܐ ܕܡܬܩܪܐ ܒܪ ܐܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 17, 'ܘܟܕ ܟܢܝܫܝܢ ܐܡܪ ܠܗܘܢ ܦܝܠܛܘܤ ܠܡܢ ܨܒܝܢ ܐܢܬܘܢ ܕܐܫܪܐ ܠܟܘܢ ܠܒܪ ܐܒܐ ܐܘ ܠܝܫܘܥ ܕܡܬܩܪܐ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 18, 'ܝܕܥ ܗܘܐ ܓܝܪ ܦܝܠܛܘܤ ܕܡܢ ܚܤܡܐ ܐܫܠܡܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 19, 'ܟܕ ܝܬܒ ܕܝܢ ܗܓܡܘܢܐ ܥܠ ܒܝܡ ܕܝܠܗ ܫܠܚܬ ܠܗ ܐܢܬܬܗ ܘܐܡܪܐ ܠܗ ܠܐ ܠܟ ܘܠܗܘ ܙܕܝܩܐ ܤܓܝ ܓܝܪ ܚܫܬ ܒܚܠܡܝ ܝܘܡܢܐ ܡܛܠܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 20, 'ܪܒܝ ܟܗܢܐ ܕܝܢ ܘܩܫܝܫܐ ܐܦܝܤܘ ܠܟܢܫܐ ܕܢܫܐܠܘܢ ܠܒܪ ܐܒܐ ܠܝܫܘܥ ܕܝܢ ܕܢܘܒܕܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 21, 'ܘܥܢܐ ܗܓܡܘܢܐ ܘܐܡܪ ܠܗܘܢ ܠܡܢ ܨܒܝܢ ܐܢܬܘܢ ܕܐܫܪܐ ܠܟܘܢ ܡܢ ܬܪܝܗܘܢ ܗܢܘܢ ܕܝܢ ܐܡܪܘ ܠܒܪ ܐܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 22, 'ܐܡܪ ܠܗܘܢ ܦܝܠܛܘܤ ܘܠܝܫܘܥ ܕܡܬܩܪܐ ܡܫܝܚܐ ܡܢܐ ܐܥܒܕ ܠܗ ܐܡܪܝܢ ܟܠܗܘܢ ܢܙܕܩܦ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 23, 'ܐܡܪ ܠܗܘܢ ܗܓܡܘܢܐ ܡܢܐ ܓܝܪ ܕܒܝܫ ܥܒܕ ܗܢܘܢ ܕܝܢ ܝܬܝܪܐܝܬ ܩܥܘ ܘܐܡܪܘ ܢܙܕܩܦ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 24, 'ܦܝܠܛܘܤ ܕܝܢ ܟܕ ܚܙܐ ܕܡܕܡ ܠܐ ܡܘܬܪ ܐܠܐ ܝܬܝܪܐܝܬ ܪܘܒܐ ܗܘܐ ܫܩܠ ܡܝܐ ܐܫܝܓ ܐܝܕܘܗܝ ܠܥܝܢ ܟܢܫܐ ܘܐܡܪ ܡܚܤܝ ܐܢܐ ܡܢ ܕܡܗ ܕܗܢܐ ܙܕܝܩܐ ܐܢܬܘܢ ܬܕܥܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 25, 'ܘܥܢܘ ܟܠܗ ܥܡܐ ܘܐܡܪܘ ܕܡܗ ܥܠܝܢ ܘܥܠ ܒܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 26, 'ܗܝܕܝܢ ܫܪܐ ܠܗܘܢ ܠܒܪ ܐܒܐ ܘܢܓܕ ܒܦܪܓܠܐ ܠܝܫܘܥ ܘܐܫܠܡܗ ܕܢܙܕܩܦ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 27, 'ܗܝܕܝܢ ܐܤܛܪܛܝܘܛܐ ܕܗܓܡܘܢܐ ܕܒܪܘܗܝ ܠܝܫܘܥ ܠܦܪܛܘܪܝܢ ܘܟܢܫܘ ܥܠܘܗܝ ܠܟܠܗ ܐܤܦܝܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 28, 'ܘܐܫܠܚܘܗܝ ܘܐܠܒܫܘܗܝ ܟܠܡܝܤ ܕܙܚܘܪܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 29, 'ܘܓܕܠܘ ܟܠܝܠܐ ܕܥܘܙܢܝܐ ܘܤܡܘ ܒܪܫܗ ܘܩܢܝܐ ܒܝܡܝܢܗ ܘܒܪܟܘ ܥܠ ܒܘܪܟܝܗܘܢ ܩܕܡܘܗܝ ܘܡܒܙܚܝܢ ܗܘܘ ܒܗ ܘܐܡܪܝܢ ܫܠܡ ܡܠܟܐ ܕܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 30, 'ܘܪܩܘ ܒܦܪܨܘܦܗ ܘܫܩܠܘ ܩܢܝܐ ܘܡܚܝܢ ܗܘܘ ܠܗ ܥܠ ܪܫܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 31, 'ܘܟܕ ܒܙܚܘ ܒܗ ܐܫܠܚܘܗܝ ܟܠܡܝܤ ܘܐܠܒܫܘܗܝ ܢܚܬܘܗܝ ܘܐܘܒܠܘܗܝ ܕܢܙܕܩܦ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 32, 'ܘܟܕ ܢܦܩܝܢ ܐܫܟܚܘ ܓܒܪܐ ܩܘܪܝܢܝܐ ܕܫܡܗ ܫܡܥܘܢ ܠܗܢܐ ܫܚܪܘ ܕܢܫܩܘܠ ܙܩܝܦܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 33, 'ܘܐܬܘ ܠܕܘܟܬܐ ܕܡܬܩܪܝܐ ܓܓܘܠܬܐ ܗܝ ܕܡܬܦܫܩܐ ܩܪܩܦܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 34, 'ܘܝܗܒܘ ܠܗ ܕܢܫܬܐ ܚܠܐ ܕܚܠܝܛ ܒܡܪܪܬܐ ܘܛܥܡ ܘܠܐ ܨܒܐ ܠܡܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 35, 'ܘܟܕ ܙܩܦܘܗܝ ܦܠܓܘ ܢܚܬܘܗܝ ܒܦܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 36, 'ܘܝܬܒܝܢ ܗܘܘ ܘܢܛܪܝܢ ܠܗ ܬܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 37, 'ܘܤܡܘ ܠܥܠ ܡܢ ܪܫܗ ܥܠܬܐ ܕܡܘܬܗ ܒܟܬܒܐ ܗܢܘ ܝܫܘܥ ܡܠܟܐ ܕܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 38, 'ܘܐܙܕܩܦܘ ܥܡܗ ܬܪܝܢ ܠܤܛܝܐ ܚܕ ܡܢ ܝܡܝܢܗ ܘܚܕ ܡܢ ܤܡܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 39, 'ܐܝܠܝܢ ܕܝܢ ܕܥܒܪܝܢ ܗܘܘ ܡܓܕܦܝܢ ܗܘܘ ܥܠܘܗܝ ܘܡܢܝܕܝܢ ܪܫܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 40, 'ܘܐܡܪܝܢ ܤܬܪ ܗܝܟܠܐ ܘܒܢܐ ܠܗ ܠܬܠܬܐ ܝܘܡܝܢ ܦܨܐ ܢܦܫܟ ܐܢ ܒܪܗ ܐܢܬ ܕܐܠܗܐ ܘܚܘܬ ܡܢ ܙܩܝܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 41, 'ܗܟܘܬ ܐܦ ܪܒܝ ܟܗܢܐ ܡܒܙܚܝܢ ܗܘܘ ܥܡ ܤܦܪܐ ܘܩܫܝܫܐ ܘܦܪܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 42, 'ܘܐܡܪܝܢ ܠܐܚܪܢܐ ܐܚܝ ܢܦܫܗ ܠܐ ܡܫܟܚ ܠܡܚܝܘ ܐܢ ܡܠܟܗ ܗܘ ܕܐܝܤܪܝܠ ܢܚܘܬ ܗܫܐ ܡܢ ܙܩܝܦܐ ܘܢܗܝܡܢ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 43, 'ܬܟܝܠ ܥܠ ܐܠܗܐ ܢܦܪܩܝܘܗܝ ܗܫܐ ܐܢ ܨܒܐ ܒܗ ܐܡܪ ܓܝܪ ܕܒܪܗ ܐܢܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 44, 'ܗܟܘܬ ܐܦ ܓܝܤܐ ܗܢܘܢ ܕܐܙܕܩܦܘ ܥܡܗ ܡܚܤܕܝܢ ܗܘܘ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 45, 'ܡܢ ܫܬ ܫܥܝܢ ܕܝܢ ܗܘܐ ܚܫܘܟܐ ܥܠ ܟܠܗ ܐܪܥܐ ܥܕܡܐ ܠܫܥܐ ܬܫܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 46, 'ܘܠܐܦܝ ܬܫܥ ܫܥܝܢ ܩܥܐ ܝܫܘܥ ܒܩܠܐ ܪܡܐ ܘܐܡܪ ܐܝܠ ܐܝܠ ܠܡܢܐ ܫܒܩܬܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 47, 'ܐܢܫܝܢ ܕܝܢ ܡܢ ܗܢܘܢ ܕܩܝܡܝܢ ܗܘܘ ܬܡܢ ܟܕ ܫܡܥܘ ܐܡܪܝܢ ܗܘܘ ܗܢܐ ܠܐܠܝܐ ܩܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 48, 'ܘܒܗ ܒܫܥܬܐ ܪܗܛ ܚܕ ܡܢܗܘܢ ܘܫܩܠ ܐܤܦܘܓܐ ܘܡܠܗ ܚܠܐ ܘܤܡܗ ܒܩܢܝܐ ܘܡܫܩܐ ܗܘܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 49, 'ܫܪܟܐ ܕܝܢ ܐܡܪܝܢ ܗܘܘ ܫܒܘܩܘ ܢܚܙܐ ܐܢ ܐܬܐ ܐܠܝܐ ܠܡܦܪܩܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 50, 'ܗܘ ܕܝܢ ܝܫܘܥ ܬܘܒ ܩܥܐ ܒܩܠܐ ܪܡܐ ܘܫܒܩ ܪܘܚܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 51, 'ܘܡܚܕܐ ܐܦܝ ܬܪܥܐ ܕܗܝܟܠܐ ܐܨܛܪܝ ܠܬܪܝܢ ܡܢ ܠܥܠ ܥܕܡܐ ܠܬܚܬ ܘܐܪܥܐ ܐܬܬܙܝܥܬ ܘܟܐܦܐ ܐܨܛܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 52, 'ܘܒܝܬ ܩܒܘܪܐ ܐܬܦܬܚܘ ܘܦܓܪܐ ܤܓܝܐܐ ܕܩܕܝܫܐ ܕܫܟܝܒܝܢ ܗܘܘ ܩܡܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 53, 'ܘܢܦܩܘ ܘܒܬܪ ܩܝܡܬܗ ܥܠܘ ܠܡܕܝܢܬܐ ܩܕܝܫܬܐ ܘܐܬܚܙܝܘ ܠܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 54, 'ܩܢܛܪܘܢܐ ܕܝܢ ܘܕܥܡܗ ܕܢܛܪܝܢ ܗܘܘ ܠܝܫܘܥ ܟܕ ܚܙܘ ܙܘܥܐ ܘܐܝܠܝܢ ܕܗܘܝ ܕܚܠܘ ܛܒ ܘܐܡܪܘ ܫܪܝܪܐܝܬ ܗܢܐ ܒܪܗ ܗܘܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 55, 'ܐܝܬ ܗܘܝ ܕܝܢ ܐܦ ܬܡܢ ܢܫܐ ܤܓܝܐܬܐ ܕܚܙܝܢ ܗܘܝ ܡܢ ܪܘܚܩܐ ܗܢܝܢ ܕܐܬܝ ܗܘܝ ܒܬܪܗ ܕܝܫܘܥ ܡܢ ܓܠܝܠܐ ܘܡܫܡܫܢ ܗܘܝ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 56, 'ܕܚܕܐ ܡܢܗܝܢ ܡܪܝܡ ܡܓܕܠܝܬܐ ܘܡܪܝܡ ܐܡܗ ܕܝܥܩܘܒ ܘܕܝܘܤܐ ܘܐܡܗܘܢ ܕܒܢܝ ܙܒܕܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 57, 'ܟܕ ܗܘܐ ܕܝܢ ܪܡܫܐ ܐܬܐ ܓܒܪܐ ܥܬܝܪܐ ܡܢ ܪܡܬܐ ܕܫܡܗ ܝܘܤܦ ܕܐܦ ܗܘ ܐܬܬܠܡܕ ܗܘܐ ܠܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 58, 'ܗܢܐ ܩܪܒ ܠܘܬ ܦܝܠܛܘܤ ܘܫܐܠ ܦܓܪܗ ܕܝܫܘܥ ܘܦܩܕ ܦܝܠܛܘܤ ܕܢܬܝܗܒ ܠܗ ܦܓܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 59, 'ܘܫܩܠܗ ܝܘܤܦ ܠܦܓܪܐ ܘܟܪܟܗ ܒܚܝܨܐ ܕܟܬܢܐ ܢܩܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 60, 'ܘܤܡܗ ܒܒܝܬ ܩܒܘܪܐ ܚܕܬܐ ܕܝܠܗ ܕܢܩܝܪ ܒܟܐܦܐ ܘܥܓܠܘ ܟܐܦܐ ܪܒܬܐ ܐܪܡܝܘ ܥܠ ܬܪܥܐ ܕܒܝܬ ܩܒܘܪܐ ܘܐܙܠܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 61, 'ܐܝܬ ܗܘܝ ܕܝܢ ܬܡܢ ܡܪܝܡ ܡܓܕܠܝܬܐ ܘܡܪܝܡ ܐܚܪܬܐ ܕܝܬܒܢ ܗܘܝ ܠܩܘܒܠܗ ܕܩܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 62, 'ܠܝܘܡܐ ܕܝܢ ܕܡܚܪ ܕܐܝܬܘܗܝ ܒܬܪ ܥܪܘܒܬܐ ܐܬܟܢܫܘ ܪܒܝ ܟܗܢܐ ܘܦܪܝܫܐ ܠܘܬ ܦܝܠܛܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 63, 'ܘܐܡܪܝܢ ܠܗ ܡܪܢ ܐܬܕܟܪܢ ܕܗܘ ܡܛܥܝܢܐ ܐܡܪ ܗܘܐ ܟܕ ܚܝ ܕܡܢ ܒܬܪ ܬܠܬܐ ܝܘܡܝܢ ܩܐܡ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 64, 'ܦܩܘܕ ܗܟܝܠ ܡܙܕܗܪܝܢ ܒܩܒܪܐ ܥܕܡܐ ܠܬܠܬܐ ܝܘܡܝܢ ܕܠܡܐ ܢܐܬܘܢ ܬܠܡܝܕܘܗܝ ܢܓܢܒܘܢܝܗܝ ܒܠܠܝܐ ܘܢܐܡܪܘܢ ܠܥܡܐ ܕܡܢ ܒܝܬ ܡܝܬܐ ܩܡ ܘܬܗܘܐ ܛܘܥܝܝ ܐܚܪܝܬܐ ܒܝܫܐ ܡܢ ܩܕܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 65, 'ܐܡܪ ܠܗܘܢ ܦܝܠܛܘܤ ܐܝܬ ܠܟܘܢ ܩܤܛܘܢܪܐ ܙܠܘ ܐܙܕܗܪܘ ܐܝܟܢܐ ܕܝܕܥܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 66, 'ܗܢܘܢ ܕܝܢ ܐܙܠܘ ܐܙܕܗܪܘ ܒܩܒܪܐ ܘܚܬܡܘ ܟܐܦܐ ܗܝ ܥܡ ܩܤܛܘܢܪܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 1, 'ܒܪܡܫܐ ܕܝܢ ܒܫܒܬܐ ܕܢܓܗ ܚܕ ܒܫܒܐ ܐܬܬ ܡܪܝܡ ܡܓܕܠܝܬܐ ܘܡܪܝܡ ܐܚܪܬܐ ܕܢܚܙܝܢ ܩܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 2, 'ܘܗܐ ܙܘܥܐ ܪܒܐ ܗܘܐ ܡܠܐܟܐ ܓܝܪ ܕܡܪܝܐ ܢܚܬ ܡܢ ܫܡܝܐ ܘܩܪܒ ܥܓܠ ܟܐܦܐ ܡܢ ܬܪܥܐ ܘܝܬܒ ܗܘܐ ܥܠܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 3, 'ܐܝܬܘܗܝ ܗܘܐ ܕܝܢ ܚܙܘܗ ܐܝܟ ܒܪܩܐ ܘܠܒܘܫܗ ܚܘܪ ܗܘܐ ܐܝܟ ܬܠܓܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 4, 'ܘܡܢ ܕܚܠܬܗ ܐܬܬܙܝܥܘ ܐܝܠܝܢ ܕܢܛܪܝܢ ܗܘܘ ܘܗܘܘ ܐܝܟ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 5, 'ܥܢܐ ܕܝܢ ܡܠܐܟܐ ܘܐܡܪ ܠܢܫܐ ܐܢܬܝܢ ܠܐ ܬܕܚܠܢ ܝܕܥ ܐܢܐ ܓܝܪ ܕܠܝܫܘܥ ܕܐܙܕܩܦ ܒܥܝܢ ܐܢܬܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 6, 'ܠܐ ܗܘܐ ܬܢܢ ܩܡ ܠܗ ܓܝܪ ܐܝܟܢܐ ܕܐܡܪ ܬܐܝܝܢ ܚܙܝܝܢ ܕܘܟܬܐ ܕܤܝܡ ܗܘܐ ܒܗ ܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 7, 'ܘܙܠܝܢ ܒܥܓܠ ܐܡܪܝܢ ܠܬܠܡܝܕܘܗܝ ܕܩܡ ܡܢ ܒܝܬ ܡܝܬܐ ܘܗܐ ܩܕܡ ܠܟܘܢ ܠܓܠܝܠܐ ܬܡܢ ܬܚܙܘܢܝܗܝ ܗܐ ܐܡܪܬ ܠܟܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 8, 'ܘܐܙܠ ܥܓܠ ܡܢ ܩܒܪܐ ܒܕܚܠܬܐ ܘܒܚܕܘܬܐ ܪܒܬܐ ܘܪܗܛܢ ܕܢܐܡܪܢ ܠܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 9, 'ܘܗܐ ܝܫܘܥ ܦܓܥ ܒܗܝܢ ܘܐܡܪ ܠܗܝܢ ܫܠܡ ܠܟܝܢ ܗܢܝܢ ܕܝܢ ܩܪܒ ܐܚܕ ܪܓܠܘܗܝ ܘܤܓܕܝܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 10, 'ܗܝܕܝܢ ܐܡܪ ܠܗܝܢ ܝܫܘܥ ܠܐ ܬܕܚܠܢ ܐܠܐ ܙܠܝܢ ܐܡܪܝܢ ܠܐܚܝ ܕܢܐܙܠܘܢ ܠܓܠܝܠܐ ܘܬܡܢ ܢܚܙܘܢܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 11, 'ܟܕ ܐܙܠܝܢ ܕܝܢ ܐܬܘ ܐܢܫܐ ܡܢ ܩܤܛܘܢܪܐ ܗܢܘܢ ܠܡܕܝܢܬܐ ܘܐܡܪܘ ܠܪܒܝ ܟܗܢܐ ܟܠ ܡܕܡ ܕܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 12, 'ܘܐܬܟܢܫܘ ܥܡ ܩܫܝܫܐ ܘܢܤܒܘ ܡܠܟܐ ܘܝܗܒܘ ܟܤܦܐ ܠܐ ܙܥܘܪ ܠܩܤܛܘܢܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 13, 'ܘܐܡܪܝܢ ܠܗܘܢ ܐܡܪܘ ܕܬܠܡܝܕܘܗܝ ܐܬܘ ܓܢܒܘܗܝ ܒܠܠܝܐ ܟܕ ܕܡܟܝܢ ܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 14, 'ܘܐܢ ܐܫܬܡܥܬ ܗܕܐ ܩܕܡ ܗܓܡܘܢܐ ܚܢܢ ܡܦܝܤܝܢ ܚܢܢ ܠܗ ܘܠܟܘܢ ܕܠܐ ܨܦܬܐ ܥܒܕܝܢ ܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 15, 'ܗܢܘܢ ܕܝܢ ܟܕ ܢܤܒܘ ܟܤܦܐ ܥܒܕܘ ܐܝܟ ܕܐܠܦܘ ܐܢܘܢ ܘܢܦܩܬ ܡܠܬܐ ܗܕܐ ܒܝܬ ܝܗܘܕܝܐ ܥܕܡܐ ܠܝܘܡܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 16, 'ܬܠܡܝܕܐ ܕܝܢ ܚܕܥܤܪ ܐܙܠܘ ܠܓܠܝܠܐ ܠܛܘܪܐ ܐܝܟܐ ܕܘܥܕ ܐܢܘܢ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 17, 'ܘܟܕ ܚܙܐܘܗܝ ܤܓܕܘ ܠܗ ܡܢܗܘܢ ܕܝܢ ܐܬܦܠܓܘ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 18, 'ܘܩܪܒ ܝܫܘܥ ܡܠܠ ܥܡܗܘܢ ܘܐܡܪ ܠܗܘܢ ܐܬܝܗܒ ܠܝ ܟܠ ܫܘܠܛܢ ܒܫܡܝܐ ܘܒܐܪܥܐ ܘܐܝܟܢܐ ܕܫܕܪܢܝ ܐܒܝ ܡܫܕܪ ܐܢܐ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 19, 'ܙܠܘ ܗܟܝܠ ܬܠܡܕܘ ܟܠܗܘܢ ܥܡܡܐ ܘܐܥܡܕܘ ܐܢܘܢ ܒܫܡ ܐܒܐ ܘܒܪܐ ܘܪܘܚܐ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 20, 'ܘܐܠܦܘ ܐܢܘܢ ܕܢܛܪܘܢ ܟܠ ܡܐ ܕܦܩܕܬܟܘܢ ܘܗܐ ܐܢܐ ܥܡܟܘܢ ܐܢܐ ܟܠܗܘܢ ܝܘܡܬܐ ܥܕܡܐ ܠܫܘܠܡܗ ܕܥܠܡܐ ܐܡܝܢ ܀ ܀ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 1, 'ܪܫܐ ܕܐܘܢܓܠܝܘܢ ܕܝܫܘܥ ܡܫܝܚܐ ܒܪܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 2, 'ܐܝܟ ܕܟܬܝܒ ܒܐܫܥܝܐ ܢܒܝܐ ܗܐ ܡܫܕܪ ܐܢܐ ܡܠܐܟܝ ܩܕܡ ܦܪܨܘܦܟ ܕܢܬܩܢ ܐܘܪܚܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 3, 'ܩܠܐ ܕܩܪܐ ܒܡܕܒܪܐ ܛܝܒܘ ܐܘܪܚܗ ܕܡܪܝܐ ܘܐܫܘܘ ܫܒܝܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 4, 'ܗܘܐ ܝܘܚܢܢ ܒܡܕܒܪܐ ܡܥܡܕ ܘܡܟܪܙ ܡܥܡܘܕܝܬܐ ܕܬܝܒܘܬܐ ܠܫܘܒܩܢܐ ܕܚܛܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 5, 'ܘܢܦܩܐ ܗܘܬ ܠܘܬܗ ܟܠܗ ܟܘܪ ܕܝܗܘܕ ܘܟܠܗܘܢ ܒܢܝ ܐܘܪܫܠܡ ܘܡܥܡܕ ܗܘܐ ܠܗܘܢ ܒܝܘܪܕܢܢ ܟܕ ܡܘܕܝܢ ܒܚܛܗܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 6, 'ܗܘ ܕܝܢ ܝܘܚܢܢ ܠܒܝܫ ܗܘܐ ܠܒܘܫܐ ܕܤܥܪܐ ܕܓܡܠܐ ܘܐܤܝܪ ܗܘܐ ܥܪܩܬܐ ܕܡܫܟܐ ܒܚܨܘܗܝ ܘܡܐܟܘܠܬܗ ܐܝܬܝܗ ܗܘܬ ܩܡܨܐ ܘܕܒܫܐ ܕܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 7, 'ܘܡܟܪܙ ܗܘܐ ܘܐܡܪ ܗܐ ܐܬܐ ܒܬܪܝ ܕܚܝܠܬܢ ܡܢܝ ܗܘ ܕܠܐ ܫܘܐ ܐܢܐ ܕܐܓܗܢ ܐܫܪܐ ܥܪܩܐ ܕܡܤܢܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 8, 'ܐܢܐ ܐܥܡܕܬܟܘܢ ܒܡܝܐ ܗܘ ܕܝܢ ܢܥܡܕܟܘܢ ܒܪܘܚܐ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 9, 'ܘܗܘܐ ܒܝܘܡܬܐ ܗܢܘܢ ܐܬܐ ܝܫܘܥ ܡܢ ܢܨܪܬ ܕܓܠܝܠܐ ܘܐܬܥܡܕ ܒܝܘܪܕܢܢ ܡܢ ܝܘܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 10, 'ܘܡܚܕܐ ܕܤܠܩ ܡܢ ܡܝܐ ܚܙܐ ܕܐܤܬܕܩܘ ܫܡܝܐ ܘܪܘܚܐ ܐܝܟ ܝܘܢܐ ܕܢܚܬܬ ܥܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 11, 'ܘܩܠܐ ܗܘܐ ܡܢ ܫܡܝܐ ܐܢܬ ܗܘ ܒܪܝ ܚܒܝܒܐ ܒܟ ܐܨܛܒܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 12, 'ܘܡܚܕܐ ܐܦܩܬܗ ܪܘܚܐ ܠܡܕܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 13, 'ܘܗܘܐ ܬܡܢ ܒܡܕܒܪܐ ܝܘܡܬܐ ܐܪܒܥܝܢ ܟܕ ܡܬܢܤܐ ܡܢ ܤܛܢܐ ܘܐܝܬܘܗܝ ܗܘܐ ܥܡ ܚܝܘܬܐ ܘܡܫܡܫܝܢ ܗܘܘ ܠܗ ܡܠܐܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 14, 'ܒܬܪ ܕܐܫܬܠܡ ܕܝܢ ܝܘܚܢܢ ܐܬܐ ܠܗ ܝܫܘܥ ܠܓܠܝܠܐ ܘܡܟܪܙ ܗܘܐ ܤܒܪܬܐ ܕܡܠܟܘܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 15, 'ܘܐܡܪ ܫܠܡ ܠܗ ܙܒܢܐ ܘܡܛܬ ܡܠܟܘܬܐ ܕܐܠܗܐ ܬܘܒܘ ܘܗܝܡܢܘ ܒܤܒܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 16, 'ܘܟܕ ܡܗܠܟ ܚܕܪܝ ܝܡܐ ܕܓܠܝܠܐ ܚܙܐ ܠܫܡܥܘܢ ܘܠܐܢܕܪܐܘܤ ܐܚܘܗܝ ܕܪܡܝܢ ܡܨܝܕܬܐ ܒܝܡܐ ܐܝܬܝܗܘܢ ܗܘܘ ܓܝܪ ܨܝܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 17, 'ܘܐܡܪ ܠܗܘܢ ܝܫܘܥ ܬܘ ܒܬܪܝ ܘܐܥܒܕܟܘܢ ܨܝܕܐ ܕܒܢܝ ܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 18, 'ܘܡܚܕܐ ܫܒܩܘ ܡܨܝܕܬܗܘܢ ܘܐܙܠܘ ܒܬܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 19, 'ܘܟܕ ܥܒܪ ܩܠܝܠ ܚܙܐ ܠܝܥܩܘܒ ܒܪ ܙܒܕܝ ܘܠܝܘܚܢܢ ܐܚܘܗܝ ܘܐܦ ܠܗܘܢ ܒܤܦܝܢܬܐ ܕܡܬܩܢܝܢ ܡܨܝܕܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 20, 'ܘܩܪܐ ܐܢܘܢ ܘܡܚܕܐ ܫܒܩܘ ܠܙܒܕܝ ܐܒܘܗܘܢ ܒܤܦܝܢܬܐ ܥܡ ܐܓܝܪܐ ܘܐܙܠܘ ܒܬܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 21, 'ܘܟܕ ܥܠܘ ܠܟܦܪܢܚܘܡ ܡܚܕܐ ܡܠܦ ܗܘܐ ܒܫܒܐ ܒܟܢܘܫܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 22, 'ܘܬܡܝܗܝܢ ܗܘܘ ܒܝܘܠܦܢܗ ܡܠܦ ܗܘܐ ܠܗܘܢ ܓܝܪ ܐܝܟ ܡܫܠܛܐ ܘܠܐ ܐܝܟ ܤܦܪܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 23, 'ܘܐܝܬ ܗܘܐ ܒܟܢܘܫܬܗܘܢ ܓܒܪܐ ܕܐܝܬ ܒܗ ܪܘܚܐ ܛܡܐܬܐ ܘܩܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 24, 'ܘܐܡܪ ܡܐ ܠܢ ܘܠܟ ܝܫܘܥ ܢܨܪܝܐ ܐܬܝܬ ܠܡܘܒܕܘܬܢ ܝܕܥ ܐܢܐ ܠܟ ܡܢ ܐܢܬ ܩܕܝܫܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 25, 'ܘܟܐܐ ܒܗ ܝܫܘܥ ܘܐܡܪ ܤܟܘܪ ܦܘܡܟ ܘܦܘܩ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 26, 'ܘܫܕܬܗ ܪܘܚܐ ܛܢܦܬܐ ܘܩܥܬ ܒܩܠܐ ܪܡܐ ܘܢܦܩܬ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 27, 'ܘܐܬܕܡܪܘ ܟܠܗܘܢ ܘܒܥܝܢ ܗܘܘ ܚܕ ܥܡ ܚܕ ܘܐܡܪܝܢ ܕܡܢܐ ܗܝ ܗܕܐ ܘܡܢܘ ܝܘܠܦܢܐ ܗܢܐ ܚܕܬܐ ܕܒܫܘܠܛܢܐ ܘܐܦ ܠܪܘܚܐ ܛܢܦܬܐ ܦܩܕ ܘܡܫܬܡܥܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 28, 'ܘܡܚܕܐ ܢܦܩ ܛܒܗ ܒܟܠܗ ܐܬܪܐ ܕܓܠܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 29, 'ܘܢܦܩܘ ܡܢ ܟܢܘܫܬܐ ܘܐܬܘ ܠܒܝܬܗ ܕܫܡܥܘܢ ܘܕܐܢܕܪܐܘܤ ܥܡ ܝܥܩܘܒ ܘܝܘܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 30, 'ܘܚܡܬܗ ܕܫܡܥܘܢ ܪܡܝܐ ܗܘܬ ܒܐܫܬܐ ܘܐܡܪܘ ܠܗ ܥܠܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 31, 'ܘܩܪܒ ܐܚܕܗ ܒܐܝܕܗ ܘܐܩܝܡܗ ܘܡܚܕܐ ܫܒܩܬܗ ܐܫܬܗ ܘܡܫܡܫܐ ܗܘܬ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 32, 'ܒܪܡܫܐ ܕܝܢ ܒܡܥܪܒܝ ܫܡܫܐ ܐܝܬܝܘ ܠܘܬܗ ܟܠܗܘܢ ܐܝܠܝܢ ܕܒܝܫܐܝܬ ܥܒܝܕܝܢ ܘܕܝܘܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 33, 'ܘܡܕܝܢܬܐ ܟܠܗ ܟܢܝܫܐ ܗܘܬ ܥܠ ܬܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 34, 'ܘܐܤܝ ܠܤܓܝܐܐ ܕܒܝܫܐܝܬ ܥܒܝܕܝܢ ܗܘܘ ܒܟܘܪܗܢܐ ܡܫܚܠܦܐ ܘܕܝܘܐ ܤܓܝܐܐ ܐܦܩ ܘܠܐ ܫܒܩ ܗܘܐ ܠܗܘܢ ܠܕܝܘܐ ܕܢܡܠܠܘܢ ܡܛܠ ܕܝܕܥܝܢ ܗܘܘ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 35, 'ܘܒܨܦܪܐ ܩܕܡ ܩܡ ܛܒ ܘܐܙܠ ܠܐܬܪܐ ܚܘܪܒܐ ܘܬܡܢ ܡܨܠܐ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 36, 'ܘܒܥܝܢ ܗܘܘ ܠܗ ܫܡܥܘܢ ܘܕܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 37, 'ܘܟܕ ܐܫܟܚܘܗܝ ܐܡܪܝܢ ܠܗ ܟܠܗܘܢ ܐܢܫܐ ܒܥܝܢ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 38, 'ܐܡܪ ܠܗܘܢ ܗܠܟܘ ܠܩܘܪܝܐ ܘܠܡܕܝܢܬܐ ܕܩܪܝܒܢ ܕܐܦ ܬܡܢ ܐܟܪܙ ܠܗܕܐ ܓܝܪ ܐܬܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 39, 'ܘܡܟܪܙ ܗܘܐ ܒܟܠܗܝܢ ܟܢܘܫܬܗܘܢ ܒܟܠܗ ܓܠܝܠܐ ܘܡܦܩ ܫܐܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 40, 'ܘܐܬܐ ܠܘܬܗ ܓܪܒܐ ܘܢܦܠ ܥܠ ܪܓܠܘܗܝ ܘܒܥܐ ܗܘܐ ܡܢܗ ܘܐܡܪ ܠܗ ܐܢ ܨܒܐ ܐܢܬ ܡܫܟܚ ܐܢܬ ܠܡܕܟܝܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 41, 'ܗܘ ܕܝܢ ܝܫܘܥ ܐܬܪܚܡ ܥܠܘܗܝ ܘܦܫܛ ܐܝܕܗ ܩܪܒ ܠܗ ܘܐܡܪ ܨܒܐ ܐܢܐ ܐܬܕܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 42, 'ܘܒܗ ܒܫܥܬܐ ܐܙܠ ܓܪܒܗ ܡܢܗ ܘܐܬܕܟܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 43, 'ܘܟܐܐ ܒܗ ܘܐܦܩܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 44, 'ܘܐܡܪ ܠܗ ܚܙܝ ܠܡܐ ܠܐܢܫ ܐܡܪ ܐܢܬ ܐܠܐ ܙܠ ܚܘܐ ܢܦܫܟ ܠܟܗܢܐ ܘܩܪܒ ܩܘܪܒܢܐ ܚܠܦ ܬܕܟܝܬܟ ܐܝܟܢܐ ܕܦܩܕ ܡܘܫܐ ܠܤܗܕܘܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 45, 'ܗܘ ܕܝܢ ܟܕ ܢܦܩ ܫܪܝ ܗܘܐ ܡܟܪܙ ܤܓܝ ܘܐܛܒܗ ܠܡܠܬܐ ܐܝܟܢܐ ܕܠܐ ܢܫܟܚ ܗܘܐ ܝܫܘܥ ܓܠܝܐܝܬ ܕܢܥܘܠ ܠܡܕܝܢܬܐ ܐܠܐ ܠܒܪ ܗܘܐ ܒܐܬܪܐ ܚܘܪܒܐ ܘܐܬܝܢ ܗܘܘ ܠܘܬܗ ܡܢ ܟܠ ܕܘܟܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 1, 'ܘܥܠ ܬܘܒ ܝܫܘܥ ܠܟܦܪܢܚܘܡ ܠܝܘܡܬܐ ܘܟܕ ܫܡܥܘ ܕܒܒܝܬܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 2, 'ܐܬܟܢܫܘ ܤܓܝܐܐ ܐܝܟܢܐ ܕܠܐ ܐܫܟܚ ܐܚܕ ܐܢܘܢ ܐܦܠܐ ܩܕܡ ܬܪܥܐ ܘܡܡܠܠ ܗܘܐ ܥܡܗܘܢ ܡܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 3, 'ܘܐܬܘ ܠܘܬܗ ܘܐܝܬܝܘ ܠܗ ܡܫܪܝܐ ܟܕ ܫܩܝܠܝܢ ܠܗ ܒܝܬ ܐܪܒܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 4, 'ܘܕܠܐ ܐܫܟܚܘ ܠܡܬܩܪܒܘ ܠܘܬܗ ܡܛܠ ܟܢܫܐ ܤܠܩܘ ܠܗܘܢ ܠܐܓܪܐ ܘܐܪܝܡܘ ܬܛܠܝܠܐ ܕܐܬܪ ܕܐܝܬܘܗܝ ܗܘܐ ܝܫܘܥ ܘܫܒܘܗ ܥܪܤܐ ܕܪܡܐ ܗܘܐ ܒܗ ܡܫܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 5, 'ܟܕ ܚܙܐ ܕܝܢ ܝܫܘܥ ܗܝܡܢܘܬܗܘܢ ܐܡܪ ܠܗܘ ܡܫܪܝܐ ܒܪܝ ܫܒܝܩܝܢ ܠܟ ܚܛܗܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 6, 'ܐܝܬ ܗܘܘ ܕܝܢ ܬܡܢ ܡܢ ܤܦܪܐ ܘܦܪܝܫܐ ܕܝܬܒܝܢ ܘܡܬܪܥܝܢ ܗܘܘ ܒܠܒܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 7, 'ܕܡܢܐ ܗܢܐ ܡܡܠܠ ܓܘܕܦܐ ܡܢܘ ܡܫܟܚ ܠܡܫܒܩ ܚܛܗܐ ܐܠܐ ܐܢ ܚܕ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 8, 'ܝܫܘܥ ܕܝܢ ܝܕܥ ܒܪܘܚܗ ܕܗܠܝܢ ܡܬܪܥܝܢ ܒܢܦܫܗܘܢ ܘܐܡܪ ܠܗܘܢ ܡܢܐ ܡܬܪܥܝܢ ܐܢܬܘܢ ܗܠܝܢ ܒܠܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 9, 'ܐܝܕܐ ܦܫܝܩܐ ܠܡܐܡܪ ܠܡܫܪܝܐ ܕܫܒܝܩܝܢ ܠܟ ܚܛܗܝܟ ܐܘ ܠܡܐܡܪ ܕܩܘܡ ܫܩܘܠ ܥܪܤܟ ܘܗܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 10, 'ܕܬܕܥܘܢ ܕܝܢ ܕܫܠܝܛ ܗܘ ܒܪܗ ܕܐܢܫܐ ܒܐܪܥܐ ܠܡܫܒܩ ܚܛܗܐ ܐܡܪ ܠܡܫܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 11, 'ܠܟ ܐܡܪ ܐܢܐ ܩܘܡ ܫܩܘܠ ܥܪܤܟ ܘܙܠ ܠܒܝܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 12, 'ܘܩܡ ܒܪ ܫܥܬܗ ܘܫܩܠ ܥܪܤܗ ܘܢܦܩ ܠܥܝܢ ܟܠܗܘܢ ܐܝܟܢܐ ܕܢܬܕܡܪܘܢ ܟܠܗܘܢ ܘܢܫܒܚܘܢ ܠܐܠܗܐ ܟܕ ܐܡܪܝܢ ܕܠܐ ܡܡܬܘܡ ܚܙܝܢ ܗܟܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 13, 'ܘܢܦܩ ܬܘܒ ܠܘܬ ܝܡܐ ܘܟܠܗ ܟܢܫܐ ܐܬܝܢ ܗܘܘ ܠܘܬܗ ܘܡܠܦ ܗܘܐ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 14, 'ܘܟܕ ܥܒܪ ܚܙܐ ܠܠܘܝ ܒܪ ܚܠܦܝ ܕܝܬܒ ܒܝܬ ܡܟܤܐ ܘܐܡܪ ܠܗ ܬܐ ܒܬܪܝ ܘܩܡ ܐܙܠ ܒܬܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 15, 'ܘܗܘܐ ܕܟܕ ܤܡܝܟ ܒܒܝܬܗ ܤܓܝܐܐ ܡܟܤܐ ܘܚܛܝܐ ܤܡܝܟܝܢ ܗܘܘ ܥܡ ܝܫܘܥ ܘܥܡ ܬܠܡܝܕܘܗܝ ܐܝܬܝܗܘܢ ܗܘܘ ܓܝܪ ܤܓܝܐܐ ܘܐܬܘ ܒܬܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 16, 'ܘܤܦܪܐ ܘܦܪܝܫܐ ܟܕ ܚܙܐܘܗܝ ܕܠܥܤ ܥܡ ܡܟܤܐ ܘܥܡ ܚܛܝܐ ܐܡܪܘ ܠܬܠܡܝܕܘܗܝ ܡܢܘ ܥܡ ܡܟܤܐ ܘܚܛܝܐ ܐܟܠ ܘܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 17, 'ܟܕ ܫܡܥ ܕܝܢ ܝܫܘܥ ܐܡܪ ܠܗܘܢ ܠܐ ܤܢܝܩܝܢ ܚܠܝܡܐ ܥܠ ܐܤܝܐ ܐܠܐ ܐܝܠܝܢ ܕܒܝܫ ܒܝܫ ܥܒܝܕܝܢ ܠܐ ܐܬܝܬ ܕܐܩܪܐ ܠܙܕܝܩܐ ܐܠܐ ܠܚܛܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 18, 'ܬܠܡܝܕܘܗܝ ܕܝܢ ܕܝܘܚܢܢ ܘܦܪܝܫܐ ܨܝܡܝܢ ܗܘܘ ܘܐܬܘ ܘܐܡܪܝܢ ܠܗ ܠܡܢܐ ܬܠܡܝܕܘܗܝ ܕܝܘܚܢܢ ܘܕܦܪܝܫܐ ܨܝܡܝܢ ܘܬܠܡܝܕܝܟ ܕܝܠܟ ܠܐ ܨܝܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 19, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܠܡܐ ܡܫܟܚܝܢ ܒܢܘܗܝ ܕܓܢܘܢܐ ܟܡܐ ܕܚܬܢܐ ܥܡܗܘܢ ܗܘ ܕܢܨܘܡܘܢ ܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 20, 'ܢܐܬܘܢ ܕܝܢ ܝܘܡܬܐ ܕܡܐ ܕܐܫܬܩܠ ܡܢܗܘܢ ܚܬܢܐ ܗܝܕܝܢ ܢܨܘܡܘܢ ܒܗܘ ܝܘܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 21, 'ܠܐ ܐܢܫ ܪܡܐ ܐܘܪܩܥܬܐ ܚܕܬܐ ܘܚܐܛ ܥܠ ܡܐܢܐ ܒܠܝܐ ܕܠܐ ܢܤܒܐ ܡܠܝܘܬܗ ܗܝ ܚܕܬܐ ܡܢ ܒܠܝܐ ܘܗܘܐ ܤܕܩܐ ܝܬܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 22, 'ܘܠܐ ܐܢܫ ܪܡܐ ܚܡܪܐ ܚܕܬܐ ܒܙܩܐ ܒܠܝܬܐ ܕܠܐ ܚܡܪܐ ܡܨܪܐ ܠܙܩܐ ܘܙܩܐ ܐܒܕܢ ܘܚܡܪܐ ܡܬܐܫܕ ܐܠܐ ܪܡܝܢ ܚܡܪܐ ܚܕܬܐ ܒܙܩܐ ܚܕܬܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 23, 'ܘܗܘܐ ܕܟܕ ܐܙܠ ܝܫܘܥ ܒܫܒܬܐ ܒܝܬ ܙܪܥܐ ܬܠܡܝܕܘܗܝ ܡܗܠܟܝܢ ܗܘܘ ܘܡܠܓܝܢ ܫܒܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 24, 'ܘܐܡܪܝܢ ܠܗ ܦܪܝܫܐ ܚܙܝ ܡܢܐ ܥܒܕܝܢ ܒܫܒܬܐ ܡܕܡ ܕܠܐ ܫܠܝܛ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 25, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܠܐ ܡܡܬܘܡ ܩܪܝܬܘܢ ܡܢܐ ܥܒܕ ܕܘܝܕ ܟܕ ܐܤܬܢܩ ܘܟܦܢ ܗܘ ܘܕܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 26, 'ܐܝܟܢܐ ܥܠ ܠܒܝܬܗ ܕܐܠܗܐ ܟܕ ܐܒܝܬܪ ܪܒ ܟܗܢܐ ܘܠܚܡܐ ܕܦܬܘܪܗ ܕܡܪܝܐ ܐܟܠ ܗܘ ܕܠܐ ܫܠܝܛ ܠܡܐܟܠ ܐܠܐ ܐܢ ܠܟܗܢܐ ܘܝܗܒ ܐܦ ܠܐܝܠܝܢ ܕܥܡܗ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 27, 'ܘܐܡܪ ܠܗܘܢ ܕܫܒܬܐ ܡܛܠ ܒܪܢܫܐ ܐܬܒܪܝܬ ܘܠܐ ܗܘܐ ܒܪܢܫܐ ܡܛܠ ܫܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 28, 'ܡܪܗ ܗܘ ܗܟܝܠ ܘܐܦ ܕܫܒܬܐ ܒܪܗ ܕܐܢܫܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 1, 'ܘܥܠ ܬܘܒ ܝܫܘܥ ܠܟܢܘܫܬܐ ܘܐܝܬ ܗܘܐ ܬܡܢ ܓܒܪܐ ܚܕ ܕܝܒܝܫܐ ܐܝܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 2, 'ܘܢܛܪܝܢ ܗܘܘ ܠܗ ܕܐܢ ܡܐܤܐ ܠܗ ܒܫܒܬܐ ܢܩܛܪܓܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 3, 'ܘܐܡܪ ܠܗܘ ܓܒܪܐ ܕܝܒܝܫܐ ܐܝܕܗ ܩܘܡ ܒܡܨܥܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 4, 'ܐܡܪ ܕܝܢ ܐܦ ܠܗܘܢ ܫܠܝܛ ܒܫܒܬܐ ܠܡܥܒܕ ܕܛܒ ܐܘ ܕܒܝܫ ܢܦܫܐ ܠܡܚܝܘ ܐܘ ܠܡܘܒܕܘ ܗܢܘܢ ܕܝܢ ܫܬܝܩܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 5, 'ܘܚܪ ܒܗܘܢ ܒܚܡܬܐ ܟܕ ܟܪܝܐ ܠܗ ܥܠ ܩܫܝܘܬ ܠܒܗܘܢ ܘܐܡܪ ܠܗܘ ܓܒܪܐ ܦܫܘܛ ܐܝܕܟ ܘܦܫܛ ܘܬܩܢܬ ܐܝܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 6, 'ܘܢܦܩܘ ܦܪܝܫܐ ܒܪ ܫܥܬܗ ܥܡ ܕܒܝܬ ܗܪܘܕܤ ܘܡܠܟܐ ܢܤܒܘ ܥܠܘܗܝ ܐܝܟܢܐ ܕܢܘܒܕܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 7, 'ܘܝܫܘܥ ܥܡ ܬܠܡܝܕܘܗܝ ܐܙܠ ܠܗ ܠܘܬ ܝܡܐ ܘܥܡܐ ܤܓܝܐܐ ܡܢ ܓܠܝܠܐ ܢܩܦܗ ܗܘܐ ܘܡܢ ܝܗܘܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 8, 'ܘܡܢ ܐܘܪܫܠܡ ܘܡܢ ܐܕܘܡ ܘܡܢ ܥܒܪܐ ܕܝܘܪܕܢܢ ܘܡܢ ܨܘܪ ܘܡܢ ܨܝܕܢ ܟܢܫܐ ܤܓܝܐܐ ܕܫܡܥܘ ܗܘܘ ܟܠ ܕܥܒܕ ܐܬܘ ܠܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 9, 'ܘܐܡܪ ܠܬܠܡܝܕܘܗܝ ܕܢܩܪܒܘܢ ܠܗ ܤܦܝܢܬܐ ܡܛܠ ܟܢܫܐ ܕܠܐ ܢܚܒܨܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 10, 'ܤܓܝܐܐ ܓܝܪ ܡܐܤܐ ܗܘܐ ܥܕܡܐ ܕܢܗܘܘܢ ܢܦܠܝܢ ܥܠܘܗܝ ܡܛܠ ܕܢܬܩܪܒܘܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 11, 'ܘܐܝܠܝܢ ܕܐܝܬ ܗܘܝ ܠܗܘܢ ܡܚܘܬܐ ܕܪܘܚܐ ܛܢܦܬܐ ܡܐ ܕܚܙܐܘܗܝ ܢܦܠܝܢ ܗܘܘ ܘܩܥܝܢ ܘܐܡܪܝܢ ܐܢܬ ܗܘ ܒܪܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 12, 'ܘܤܓܝ ܟܐܐ ܗܘܐ ܒܗܘܢ ܕܠܐ ܢܓܠܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 13, 'ܘܤܠܩ ܠܛܘܪܐ ܘܩܪܐ ܠܐܝܠܝܢ ܕܨܒܐ ܘܐܬܘ ܠܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 14, 'ܘܓܒܐ ܬܪܥܤܪ ܕܢܗܘܘܢ ܥܡܗ ܘܕܢܫܕܪ ܐܢܘܢ ܕܢܟܪܙܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 15, 'ܘܕܢܗܘܘܢ ܫܠܝܛܝܢ ܕܢܐܤܘܢ ܟܪܝܗܐ ܘܢܦܩܘܢ ܕܝܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 16, 'ܘܫܡܝ ܠܫܡܥܘܢ ܫܡܐ ܟܐܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 17, 'ܘܠܝܥܩܘܒ ܒܪ ܙܒܕܝ ܘܠܝܘܚܢܢ ܐܚܘܗܝ ܕܝܥܩܘܒ ܤܡ ܠܗܘܢ ܫܡܐ ܒܢܝ ܪܓܫܝ ܕܐܝܬܘܗܝ ܒܢܝ ܪܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 18, 'ܘܐܢܕܪܐܘܤ ܘܦܝܠܝܦܘܤ ܘܒܪ ܬܘܠܡܝ ܘܡܬܝ ܘܬܐܘܡܐ ܘܝܥܩܘܒ ܒܪ ܚܠܦܝ ܘܬܕܝ ܘܫܡܥܘܢ ܩܢܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 19, 'ܘܝܗܘܕܐ ܤܟܪܝܘܛܐ ܗܘ ܕܐܫܠܡܗ ܘܐܬܘ ܠܒܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 20, 'ܘܐܬܟܢܫܘ ܟܢܫܐ ܬܘܒ ܐܝܟܢܐ ܕܠܐ ܢܫܟܚܘܢ ܗܘܘ ܠܚܡܐ ܠܡܐܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 21, 'ܘܫܡܥܘ ܐܚܝܢܘܗܝ ܘܢܦܩܘ ܠܡܐܚܕܗ ܐܡܪܝܢ ܗܘܘ ܓܝܪ ܕܡܢ ܗܘܢܗ ܢܦܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 22, 'ܘܤܦܪܐ ܐܝܠܝܢ ܕܡܢ ܐܘܪܫܠܡ ܢܚܬܘ ܐܡܪܝܢ ܗܘܘ ܒܥܠܙܒܘܒ ܐܝܬ ܒܗ ܘܒܪܫܐ ܕܕܝܘܐ ܡܦܩ ܕܝܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 23, 'ܘܩܪܐ ܐܢܘܢ ܝܫܘܥ ܘܒܡܬܠܐ ܐܡܪ ܠܗܘܢ ܐܝܟܢܐ ܡܫܟܚ ܤܛܢܐ ܠܤܛܢܐ ܠܡܦܩܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 24, 'ܐܢ ܡܠܟܘܬܐ ܓܝܪ ܥܠ ܢܦܫܗ ܬܬܦܠܓ ܠܐ ܡܫܟܚܐ ܠܡܩܡ ܡܠܟܘܬܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 25, 'ܘܐܢ ܒܝܬܐ ܥܠ ܢܦܫܗ ܢܬܦܠܓ ܠܐ ܡܫܟܚ ܒܝܬܐ ܗܘ ܠܡܩܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 26, 'ܘܐܢ ܗܘ ܕܤܛܢܐ ܩܡ ܥܠ ܢܦܫܗ ܘܐܬܦܠܓ ܠܐ ܡܫܟܚ ܠܡܩܡ ܐܠܐ ܚܪܬܗ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 27, 'ܠܐ ܐܢܫ ܡܫܟܚ ܕܢܥܘܠ ܠܒܝܬ ܚܤܝܢܐ ܘܢܚܛܘܦ ܡܐܢܘܗܝ ܐܠܐ ܐܢ ܠܘܩܕܡ ܠܚܤܝܢܐ ܢܐܤܘܪ ܘܗܝܕܝܢ ܒܝܬܗ ܢܒܘܙ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 28, 'ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܟܠܗܘܢ ܚܛܗܐ ܘܓܘܕܦܐ ܕܢܓܕܦܘܢ ܒܢܝ ܐܢܫܐ ܢܫܬܒܩܘܢ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 29, 'ܡܢ ܕܝܢ ܕܢܓܕܦ ܥܠ ܪܘܚܐ ܕܩܘܕܫܐ ܠܝܬ ܠܗ ܫܘܒܩܢܐ ܠܥܠܡ ܐܠܐ ܡܚܝܒ ܗܘ ܠܕܝܢܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 30, 'ܡܛܠ ܕܐܡܪܝܢ ܗܘܘ ܕܪܘܚܐ ܛܢܦܬܐ ܐܝܬ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 31, 'ܘܐܬܘ ܐܡܗ ܘܐܚܘܗܝ ܩܝܡܝܢ ܠܒܪ ܘܫܕܪܘ ܕܢܩܪܘܢܝܗܝ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 32, 'ܝܬܒ ܗܘܐ ܕܝܢ ܚܕܪܘܗܝ ܟܢܫܐ ܘܐܡܪܘ ܠܗ ܗܐ ܐܡܟ ܘܐܚܝܟ ܠܒܪ ܒܥܝܢ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 33, 'ܘܥܢܐ ܘܐܡܪ ܠܗܘܢ ܡܢ ܗܝ ܐܡܝ ܘܡܢ ܐܢܘܢ ܐܚܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 34, 'ܘܚܪ ܒܐܝܠܝܢ ܕܝܬܒܝܢ ܠܘܬܗ ܘܐܡܪ ܗܐ ܐܡܝ ܘܗܐ ܐܚܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 35, 'ܡܢ ܕܢܥܒܕ ܓܝܪ ܨܒܝܢܗ ܕܐܠܗܐ ܗܘܝܘ ܐܚܝ ܘܚܬܝ ܘܐܡܝ ܀ ܘܐܬܟܢܫܘ ܠܘܬܗ ܟܢܫܐ ܤܓܝܐܐ ܐܝܟ ܕܢܤܩ ܢܬܒ ܠܗ ܒܤܦܝܢܬܐ ܒܝܡܐ ܘܟܠܗ ܟܢܫܐ ܩܐܡ ܗܘܐ ܥܠ ܐܪܥܐ ܥܠ ܝܕ ܝܡܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 1, 'ܬܘܒ ܕܝܢ ܫܪܝ ܗܘܐ ܡܠܦ ܥܠ ܝܕ ܝܡܐ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 2, 'ܘܡܠܦ ܗܘܐ ܠܗܘܢ ܒܡܬܠܐ ܤܓܝ ܘܐܡܪ ܗܘܐ ܒܝܘܠܦܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 3, 'ܫܡܥܘ ܗܐ ܢܦܩ ܙܪܘܥܐ ܠܡܙܪܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 4, 'ܘܟܕ ܙܪܥ ܐܝܬ ܕܢܦܠ ܥܠ ܝܕ ܐܘܪܚܐ ܘܐܬܬ ܦܪܚܬܐ ܘܐܟܠܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 5, 'ܐܚܪܢܐ ܕܝܢ ܢܦܠ ܥܠ ܫܘܥܐ ܐܝܟܐ ܕܠܝܬ ܐܪܥܐ ܤܓܝ ܘܒܪ ܫܥܬܗ ܒܠܨ ܡܛܠ ܕܠܝܬ ܗܘܐ ܥܘܡܩܐ ܕܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 6, 'ܟܕ ܕܢܚ ܕܝܢ ܫܡܫܐ ܚܡܐ ܘܡܛܠ ܕܠܝܬ ܗܘܐ ܠܗ ܥܩܪܐ ܝܒܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 7, 'ܘܐܚܪܢܐ ܢܦܠ ܒܝܬ ܟܘܒܐ ܘܤܠܩܘ ܟܘܒܐ ܘܚܢܩܘܗܝ ܘܦܐܪܐ ܠܐ ܝܗܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 8, 'ܐܚܪܢܐ ܕܝܢ ܢܦܠ ܥܠ ܐܪܥܐ ܛܒܬܐ ܘܤܠܩ ܘܪܒܐ ܘܝܗܒ ܦܐܪܐ ܐܝܬ ܕܬܠܬܝܢ ܘܐܝܬ ܕܫܬܝܢ ܘܐܝܬ ܕܡܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 9, 'ܘܐܡܪ ܗܘܐ ܡܢ ܕܐܝܬ ܠܗ ܐܕܢܐ ܕܢܫܡܥ ܢܫܡܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 10, 'ܟܕ ܗܘܘ ܕܝܢ ܒܠܚܘܕܝܗܘܢ ܫܐܠܘܗܝ ܗܢܘܢ ܕܥܡܗ ܥܡ ܬܪܥܤܪܬܗ ܡܬܠܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 11, 'ܘܐܡܪ ܠܗܘܢ ܝܫܘܥ ܠܟܘܢ ܝܗܝܒ ܠܡܕܥ ܐܪܙܐ ܕܡܠܟܘܬܗ ܕܐܠܗܐ ܠܒܪܝܐ ܕܝܢ ܟܠ ܡܕܡ ܒܡܬܠܐ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 12, 'ܕܟܕ ܚܙܝܢ ܢܚܙܘܢ ܘܠܐ ܢܚܙܘܢ ܘܟܕ ܫܡܥܝܢ ܢܫܡܥܘܢ ܘܠܐ ܢܤܬܟܠܘܢ ܕܠܡܐ ܢܬܦܢܘܢ ܘܢܫܬܒܩܘܢ ܠܗܘܢ ܚܛܗܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 13, 'ܘܐܡܪ ܠܗܘܢ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܠܗ ܠܡܬܠܐ ܗܢܐ ܘܐܝܟܢܐ ܟܠܗܘܢ ܡܬܠܐ ܬܕܥܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 14, 'ܙܪܘܥܐ ܕܙܪܥ ܡܠܬܐ ܙܪܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 15, 'ܗܠܝܢ ܕܝܢ ܕܥܠ ܝܕ ܐܘܪܚܐ ܗܠܝܢ ܐܢܘܢ ܕܡܙܕܪܥܐ ܒܗܘܢ ܡܠܬܐ ܘܡܐ ܕܫܡܥܘ ܡܚܕܐ ܐܬܐ ܤܛܢܐ ܘܫܩܠ ܠܗ ܠܡܠܬܐ ܕܙܪܝܥܐ ܒܠܒܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 16, 'ܘܗܢܘܢ ܕܥܠ ܫܘܥܐ ܐܙܕܪܥܘ ܗܠܝܢ ܐܢܘܢ ܕܡܐ ܕܫܡܥܘ ܡܠܬܐ ܡܚܕܐ ܒܚܕܘܬܐ ܡܩܒܠܝܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 17, 'ܘܠܝܬ ܠܗܘܢ ܥܩܪܐ ܒܢܦܫܗܘܢ ܐܠܐ ܕܙܒܢܐ ܐܢܘܢ ܘܡܐ ܕܗܘܐ ܐܘܠܨܢܐ ܐܘ ܪܕܘܦܝܐ ܡܛܠ ܡܠܬܐ ܥܓܠ ܡܬܟܫܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 18, 'ܘܗܢܘܢ ܕܒܝܬ ܟܘܒܐ ܡܙܕܪܥܝܢ ܗܠܝܢ ܐܢܘܢ ܗܢܘܢ ܕܫܡܥܘ ܡܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 19, 'ܘܪܢܝܐ ܕܥܠܡܐ ܗܢܐ ܘܛܘܥܝܝ ܕܥܘܬܪܐ ܘܫܪܟܐ ܕܪܓܝܓܬܐ ܐܚܪܢܝܬܐ ܥܐܠܢ ܚܢܩܢ ܠܗ ܠܡܠܬܐ ܘܕܠܐ ܦܐܪܐ ܗܘܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 20, 'ܘܗܢܘܢ ܕܒܐܪܥܐ ܛܒܬܐ ܐܙܕܪܥܘ ܗܠܝܢ ܐܢܘܢ ܕܫܡܥܝܢ ܡܠܬܐ ܘܡܩܒܠܝܢ ܘܝܗܒܝܢ ܦܐܪܐ ܒܬܠܬܝܢ ܘܒܫܬܝܢ ܘܒܡܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 21, 'ܘܐܡܪ ܠܗܘܢ ܕܠܡܐ ܐܬܐ ܫܪܓܐ ܕܬܚܝܬ ܤܐܬܐ ܢܬܬܤܝܡ ܐܘ ܬܚܝܬ ܥܪܤܐ ܠܐ ܗܘܐ ܕܥܠ ܡܢܪܬܐ ܢܬܬܤܝܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 22, 'ܠܝܬ ܓܝܪ ܡܕܡ ܕܛܫܐ ܕܠܐ ܢܬܓܠܐ ܘܠܐ ܗܘܐ ܒܛܘܫܝܐ ܘܠܐ ܡܬܓܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 23, 'ܐܢ ܐܢܫ ܐܝܬ ܠܗ ܐܕܢܐ ܕܢܫܡܥ ܢܫܡܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 24, 'ܘܐܡܪ ܠܗܘܢ ܚܙܘ ܡܢܐ ܫܡܥܝܢ ܐܢܬܘܢ ܒܗܝ ܟܝܠܬܐ ܕܡܟܝܠܝܢ ܐܢܬܘܢ ܡܬܬܟܝܠ ܠܟܘܢ ܘܡܬܬܘܤܦ ܠܟܘܢ ܠܐܝܠܝܢ ܕܫܡܥܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 25, 'ܡܢ ܕܐܝܬ ܠܗ ܓܝܪ ܢܬܝܗܒ ܠܗ ܘܡܢ ܕܠܝܬ ܠܗ ܐܦ ܗܘ ܕܐܝܬ ܠܗ ܢܫܬܩܠ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 26, 'ܘܐܡܪ ܗܘܐ ܗܟܢܐ ܗܝ ܡܠܟܘܬܐ ܕܐܠܗܐ ܐܝܟ ܐܢܫ ܕܢܪܡܐ ܙܪܥܐ ܒܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 27, 'ܘܢܕܡܟ ܘܢܩܘܡ ܒܠܠܝܐ ܘܒܐܝܡܡܐ ܘܙܪܥܐ ܢܪܒܐ ܘܢܐܪܟ ܟܕ ܗܘ ܠܐ ܝܕܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 28, 'ܐܪܥܐ ܓܝܪ ܡܝܬܝܐ ܠܗ ܠܦܐܪܐ ܘܠܘܩܕܡ ܗܘܐ ܥܤܒܐ ܘܒܬܪܗ ܫܒܠܐ ܐܚܪܝܬ ܕܝܢ ܚܛܬܐ ܡܫܡܠܝܬܐ ܒܫܒܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 29, 'ܡܐ ܕܫܡܢ ܕܝܢ ܦܐܪܐ ܡܚܕܐ ܐܬܝܐ ܡܓܠܐ ܕܡܛܝ ܚܨܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 30, 'ܘܐܡܪ ܠܡܢܐ ܢܕܡܝܗ ܠܡܠܟܘܬܐ ܕܐܠܗܐ ܘܒܐܝܢܐ ܡܬܠܐ ܢܡܬܠܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 31, 'ܐܝܟ ܦܪܕܬܐ ܗܝ ܕܚܪܕܠܐ ܗܝ ܕܡܐ ܕܐܙܕܪܥܬ ܒܐܪܥܐ ܙܥܘܪܝܐ ܗܝ ܡܢ ܟܠܗܘܢ ܙܪܥܘܢܐ ܕܥܠ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 32, 'ܘܡܐ ܕܐܙܕܪܥܬ ܤܠܩܐ ܘܗܘܝܐ ܪܒܐ ܡܢ ܟܠܗܘܢ ܝܪܩܘܢܐ ܘܥܒܕܐ ܤܘܟܐ ܪܘܪܒܬܐ ܐܝܟ ܕܬܫܟܚ ܕܒܛܠܠܗ ܦܪܚܬܐ ܬܫܟܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 33, 'ܒܡܬܠܐ ܕܐܝܟ ܗܠܝܢ ܡܡܠܠ ܗܘܐ ܝܫܘܥ ܥܡܗܘܢ ܡܬܠܐ ܐܝܟ ܕܡܫܟܚܝܢ ܗܘܘ ܠܡܫܡܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 34, 'ܘܕܠܐ ܡܬܠܐ ܠܐ ܡܡܠܠ ܗܘܐ ܥܡܗܘܢ ܠܬܠܡܝܕܘܗܝ ܕܝܢ ܒܝܢܘܗܝ ܘܠܗܘܢ ܡܦܫܩ ܗܘܐ ܟܠܡܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 35, 'ܘܐܡܪ ܠܗܘܢ ܒܗܘ ܝܘܡܐ ܒܪܡܫܐ ܢܥܒܪ ܠܢ ܠܥܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 36, 'ܘܫܒܩܘ ܠܟܢܫܐ ܘܕܒܪܘܗܝ ܟܕ ܒܤܦܝܢܬܐ ܗܘ ܘܤܦܝܢܐ ܐܚܪܢܝܬܐ ܐܝܬ ܗܘܝ ܥܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 37, 'ܘܗܘܬ ܥܠܥܠܐ ܪܒܬܐ ܘܪܘܚܐ ܘܓܠܠܐ ܢܦܠܝܢ ܗܘܘ ܒܤܦܝܢܬܐ ܘܩܪܝܒܐ ܗܘܬ ܕܬܬܡܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 38, 'ܗܘ ܕܝܢ ܝܫܘܥ ܥܠ ܒܤܕܝܐ ܕܡܟ ܗܘܐ ܒܚܪܬܗ ܕܤܦܝܢܬܐ ܘܐܬܘ ܐܩܝܡܘܗܝ ܘܐܡܪܝܢ ܠܗ ܪܒܢ ܠܐ ܒܛܝܠ ܠܟ ܕܐܒܕܝܢ ܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 39, 'ܘܩܡ ܘܟܐܐ ܒܪܘܚܐ ܘܐܡܪ ܠܝܡܐ ܫܠܝ ܙܓܝܪ ܐܢܬ ܘܫܠܝܬ ܪܘܚܐ ܘܗܘܐ ܢܘܚܐ ܪܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 40, 'ܘܐܡܪ ܠܗܘܢ ܠܡܢܐ ܕܚܘܠܬܢܝܢ ܐܢܬܘܢ ܗܟܢ ܘܠܡܢܐ ܠܝܬ ܒܟܘܢ ܗܝܡܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 41, 'ܘܕܚܠܘ ܕܚܠܬܐ ܪܒܬܐ ܘܐܡܪܝܢ ܗܘܘ ܚܕ ܠܚܕ ܡܢܘ ܟܝ ܗܢܐ ܕܪܘܚܐ ܘܝܡܐ ܡܫܬܡܥܝܢ ܠܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 1, 'ܘܐܬܐ ܠܥܒܪܐ ܕܝܡܐ ܠܐܬܪܐ ܕܓܕܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 2, 'ܘܟܕ ܢܦܩ ܡܢ ܤܦܝܢܬܐ ܦܓܥ ܒܗ ܡܢ ܒܝܬ ܩܒܘܪܐ ܓܒܪܐ ܕܐܝܬ ܒܗ ܪܘܚܐ ܛܢܦܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 3, 'ܘܥܡܪ ܗܘܐ ܒܝܬ ܩܒܘܪܐ ܘܒܫܫܠܬܐ ܐܢܫ ܠܐ ܡܫܟܚ ܗܘܐ ܠܡܐܤܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 4, 'ܡܛܠ ܕܟܠ ܐܡܬܝ ܕܒܤܘܛܡܐ ܘܒܫܫܠܬܐ ܡܬܐܤܪ ܗܘܐ ܫܫܠܬܐ ܡܬܒܪ ܗܘܐ ܘܤܘܛܡܐ ܡܦܤܩ ܗܘܐ ܘܠܐ ܐܢܫ ܡܫܟܚ ܗܘܐ ܠܡܟܒܫܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 5, 'ܘܒܟܠ ܙܒܢ ܒܠܠܝܐ ܘܒܐܝܡܡܐ ܒܒܝܬ ܩܒܘܪܐ ܘܒܛܘܪܐ ܐܝܬܘܗܝ ܗܘܐ ܘܩܥܐ ܗܘܐ ܘܡܨܠܦ ܢܦܫܗ ܒܟܐܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 6, 'ܟܕ ܚܙܐ ܕܝܢ ܠܝܫܘܥ ܡܢ ܪܘܚܩܐ ܪܗܛ ܤܓܕ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 7, 'ܘܩܥܐ ܒܩܠܐ ܪܡܐ ܘܐܡܪ ܡܐ ܠܝ ܘܠܟ ܝܫܘܥ ܒܪܗ ܕܐܠܗܐ ܡܪܝܡܐ ܡܘܡܐ ܐܢܐ ܠܟ ܒܐܠܗܐ ܕܠܐ ܬܫܢܩܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 8, 'ܐܡܪ ܗܘܐ ܠܗ ܓܝܪ ܦܘܩ ܡܢ ܒܪܢܫܐ ܪܘܚܐ ܛܢܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 9, 'ܘܫܐܠܗ ܐܝܟܢܐ ܫܡܟ ܐܡܪ ܠܗ ܠܓܝܘܢ ܫܡܢ ܡܛܠ ܕܤܓܝܐܐ ܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 10, 'ܘܒܥܐ ܗܘܐ ܡܢܗ ܤܓܝ ܕܠܐ ܢܫܕܪܝܘܗܝ ܠܒܪ ܡܢ ܐܬܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 11, 'ܐܝܬ ܗܘܐ ܕܝܢ ܬܡܢ ܠܘܬ ܛܘܪܐ ܒܩܪܐ ܪܒܬܐ ܕܚܙܝܪܐ ܕܪܥܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 12, 'ܘܒܥܝܢ ܗܘܘ ܡܢܗ ܗܢܘܢ ܫܐܕܐ ܘܐܡܪܝܢ ܫܕܪܝܢ ܥܠ ܗܢܘܢ ܚܙܝܪܐ ܕܒܗܘܢ ܢܥܘܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 13, 'ܘܐܦܤ ܠܗܘܢ ܘܢܦܩ ܪܘܚܐ ܗܠܝܢ ܛܢܦܬܐ ܘܥܠ ܒܚܙܝܪܐ ܘܪܗܛܬ ܗܝ ܒܩܪܐ ܠܫܩܝܦܐ ܘܢܦܠܬ ܒܝܡܐ ܐܝܟ ܬܪܝܢ ܐܠܦܝܢ ܘܐܬܚܢܩܘ ܒܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 14, 'ܘܗܢܘܢ ܕܪܥܝܢ ܗܘܘ ܠܗܘܢ ܥܪܩܘ ܘܐܡܪܘ ܒܡܕܝܢܬܐ ܘܐܦ ܒܩܘܪܝܐ ܘܢܦܩܘ ܠܡܚܙܐ ܡܕܡ ܕܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 15, 'ܘܐܬܘ ܠܘܬ ܝܫܘܥ ܘܚܙܐܘܗܝ ܠܗܘ ܕܫܐܕܘܗܝ ܟܕ ܠܒܝܫ ܘܡܢܟܦ ܘܝܬܒ ܗܘ ܕܐܝܬ ܗܘܐ ܒܗ ܠܓܝܘܢ ܘܕܚܠܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 16, 'ܘܐܫܬܥܝܘ ܠܗܘܢ ܗܢܘܢ ܕܚܙܘ ܕܐܝܟܢܐ ܗܘܐ ܠܗܘ ܕܫܐܕܘܗܝ ܘܐܦ ܥܠ ܗܢܘܢ ܚܙܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 17, 'ܘܫܪܝܘ ܒܥܝܢ ܡܢܗ ܕܢܐܙܠ ܠܗ ܡܢ ܬܚܘܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 18, 'ܘܟܕ ܤܠܩ ܠܤܦܝܢܬܐ ܒܥܐ ܗܘܐ ܡܢܗ ܗܘ ܕܫܐܕܘܗܝ ܕܥܡܗ ܢܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 19, 'ܘܠܐ ܫܒܩܗ ܐܠܐ ܐܡܪ ܠܗ ܙܠ ܠܒܝܬܟ ܠܘܬ ܐܢܫܝܟ ܘܐܫܬܥܐ ܠܗܘܢ ܡܕܡ ܕܥܒܕ ܠܟ ܡܪܝܐ ܘܕܐܬܪܚܡ ܥܠܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 20, 'ܘܐܙܠ ܘܫܪܝ ܡܟܪܙ ܒܥܤܪܬ ܡܕܝܢܬܐ ܡܕܡ ܕܥܒܕ ܠܗ ܝܫܘܥ ܘܟܠܗܘܢ ܬܡܝܗܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 21, 'ܘܟܕ ܥܒܪ ܝܫܘܥ ܒܤܦܝܢܬܐ ܠܗܘ ܥܒܪܐ ܬܘܒ ܐܬܟܢܫܘ ܥܠܘܗܝ ܟܢܫܐ ܤܓܝܐܐ ܟܕ ܐܝܬܘܗܝ ܥܠ ܝܕ ܝܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 22, 'ܘܐܬܐ ܚܕ ܕܫܡܗ ܝܘܐܪܫ ܡܢ ܪܒܝ ܟܢܘܫܬܐ ܘܟܕ ܚܙܝܗܝ ܢܦܠ ܠܘܬ ܪܓܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 23, 'ܘܒܥܐ ܗܘܐ ܡܢܗ ܤܓܝ ܘܐܡܪ ܠܗ ܒܪܬܝ ܒܝܫܐܝܬ ܥܒܝܕܐ ܬܐ ܤܝܡ ܐܝܕܟ ܥܠܝܗ ܘܬܬܚܠܡ ܘܬܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 24, 'ܘܐܙܠ ܥܡܗ ܝܫܘܥ ܘܕܒܝܩ ܗܘܐ ܠܗ ܟܢܫܐ ܤܓܝܐܐ ܘܚܒܨܝܢ ܗܘܘ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 25, 'ܐܢܬܬܐ ܕܝܢ ܚܕܐ ܕܐܝܬܝܗ ܗܘܬ ܒܡܪܕܝܬܐ ܕܕܡܐ ܫܢܝܢ ܬܪܬܥܤܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 26, 'ܐܝܕܐ ܕܤܓܝ ܤܒܠܬ ܡܢ ܐܤܘܬܐ ܤܓܝܐܐ ܘܐܦܩܬ ܟܠ ܡܕܡ ܕܐܝܬ ܠܗ ܘܡܕܡ ܠܐ ܐܬܥܕܪܬ ܐܠܐ ܐܦ ܝܬܝܪܐܝܬ ܐܬܐܠܨܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 27, 'ܟܕ ܫܡܥܬ ܥܠ ܝܫܘܥ ܐܬܬ ܒܚܒܨܐ ܕܟܢܫܐ ܡܢ ܒܤܬܪܗ ܩܪܒܬ ܠܠܒܘܫܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 28, 'ܐܡܪܐ ܗܘܬ ܓܝܪ ܕܐܦܢ ܠܠܒܘܫܗ ܩܪܒܐ ܐܢܐ ܚܝܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 29, 'ܘܡܚܕܐ ܝܒܫܬ ܡܥܝܢܐ ܕܕܡܗ ܘܐܪܓܫܬ ܒܦܓܪܗ ܕܐܬܐܤܝܬ ܡܢ ܡܚܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 30, 'ܝܫܘܥ ܕܝܢ ܡܚܕܐ ܝܕܥ ܒܢܦܫܗ ܕܚܝܠܐ ܢܦܩ ܡܢܗ ܘܐܬܦܢܝ ܠܘܬ ܟܢܫܐ ܘܐܡܪ ܡܢܘ ܩܪܒ ܠܡܐܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 31, 'ܘܐܡܪܝܢ ܠܗ ܬܠܡܝܕܘܗܝ ܚܙܐ ܐܢܬ ܠܟܢܫܐ ܕܚܒܨܝܢ ܠܟ ܘܐܡܪ ܐܢܬ ܡܢܘ ܩܪܒ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 32, 'ܘܚܐܪ ܗܘܐ ܕܢܚܙܐ ܡܢܘ ܗܕܐ ܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 33, 'ܗܝ ܕܝܢ ܐܢܬܬܐ ܟܕ ܕܚܝܠܐ ܘܪܬܝܬܐ ܕܝܕܥܬ ܡܐ ܕܗܘܐ ܠܗ ܐܬܬ ܢܦܠܬ ܩܕܡܘܗܝ ܘܐܡܪܬ ܠܗ ܟܠܗ ܫܪܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 34, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗ ܒܪܬܝ ܗܝܡܢܘܬܟܝ ܐܚܝܬܟܝ ܙܠܝ ܒܫܠܡܐ ܘܗܘܝܬܝ ܚܠܝܡܐ ܡܢ ܡܚܘܬܟܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 35, 'ܘܥܕ ܗܘ ܡܡܠܠ ܐܬܘ ܡܢ ܕܒܝܬ ܪܒ ܟܢܘܫܬܐ ܘܐܡܪܝܢ ܕܒܪܬܟ ܡܝܬܬ ܠܡܢܐ ܡܟܝܠ ܡܥܡܠ ܐܢܬ ܠܡܠܦܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 36, 'ܝܫܘܥ ܕܝܢ ܫܡܥ ܠܡܠܬܐ ܕܐܡܪܘ ܘܐܡܪ ܠܗܘ ܪܒ ܟܢܘܫܬܐ ܠܐ ܬܕܚܠ ܒܠܚܘܕ ܗܝܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 37, 'ܘܠܐ ܫܒܩ ܠܐܢܫ ܕܢܐܙܠ ܥܡܗ ܐܠܐ ܠܫܡܥܘܢ ܟܐܦܐ ܘܠܝܥܩܘܒ ܘܠܝܘܚܢܢ ܐܚܘܗܝ ܕܝܥܩܘܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 38, 'ܘܐܬܘ ܠܒܝܬܐ ܕܗܘ ܪܒ ܟܢܘܫܬܐ ܘܚܙܐ ܕܪܗܝܒܝܢ ܘܒܟܝܢ ܘܡܝܠܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 39, 'ܘܥܠ ܘܐܡܪ ܠܗܘܢ ܡܢܐ ܪܗܝܒܝܢ ܐܢܬܘܢ ܘܒܟܝܢ ܛܠܝܬܐ ܠܐ ܡܝܬܬ ܐܠܐ ܕܡܟܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 40, 'ܘܓܚܟܝܢ ܗܘܘ ܥܠܘܗܝ ܗܘ ܕܝܢ ܐܦܩ ܠܟܠܗܘܢ ܘܕܒܪ ܠܐܒܘܗ ܕܛܠܝܬܐ ܘܠܐܡܗ ܘܠܗܢܘܢ ܕܥܡܗ ܘܥܠ ܠܐܝܟܐ ܕܪܡܝܐ ܗܘܬ ܛܠܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 41, 'ܘܐܚܕ ܒܐܝܕܗ ܕܛܠܝܬܐ ܘܐܡܪ ܠܗ ܛܠܝܬܐ ܩܘܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 42, 'ܘܒܪ ܫܥܬܗ ܩܡܬ ܛܠܝܬܐ ܘܡܗܠܟܐ ܗܘܬ ܐܝܬܝܗ ܗܘܬ ܓܝܪ ܒܪܬ ܫܢܝܢ ܬܪܬܥܤܪܐ ܘܡܬܕܡܪܝܢ ܗܘܘ ܕܘܡܪܐ ܪܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 43, 'ܘܦܩܕ ܐܢܘܢ ܤܓܝ ܕܠܐ ܐܢܫ ܢܕܥ ܗܕܐ ܘܐܡܪ ܕܢܬܠܘܢ ܠܗ ܠܡܠܥܤ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 1, 'ܘܢܦܩ ܡܢ ܬܡܢ ܘܐܬܐ ܠܡܕܝܢܬܗ ܘܕܒܝܩܝܢ ܗܘܘ ܠܗ ܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 2, 'ܘܟܕ ܗܘܬ ܫܒܬܐ ܫܪܝ ܠܡܠܦܘ ܒܟܢܘܫܬܐ ܘܤܓܝܐܐ ܕܫܡܥܘ ܐܬܕܡܪܘ ܘܐܡܪܝܢ ܗܘܘ ܐܝܡܟܐ ܠܗ ܗܠܝܢ ܠܗܢܐ ܘܐܝܕܐ ܗܝ ܚܟܡܬܐ ܕܐܬܝܗܒܬ ܠܗ ܕܚܝܠܐ ܕܐܝܟ ܗܠܝܢ ܒܐܝܕܘܗܝ ܢܗܘܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 3, 'ܠܐ ܗܘܐ ܗܢܐ ܢܓܪܐ ܒܪܗ ܕܡܪܝܡ ܘܐܚܘܗܝ ܕܝܥܩܘܒ ܘܕܝܘܤܐ ܘܕܝܗܘܕܐ ܘܕܫܡܥܘܢ ܘܠܐ ܗܐ ܐܚܘܬܗ ܬܢܢ ܠܘܬܢ ܘܡܬܟܫܠܝܢ ܗܘܘ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 4, 'ܘܐܡܪ ܠܗܘܢ ܝܫܘܥ ܠܝܬ ܢܒܝܐ ܕܨܥܝܪ ܐܠܐ ܐܢ ܒܡܕܝܢܬܗ ܘܒܝܬ ܐܚܝܢܘܗܝ ܘܒܒܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 5, 'ܘܠܐ ܡܫܟܚ ܗܘܐ ܕܢܥܒܕ ܬܡܢ ܐܦ ܠܐ ܚܕ ܚܝܠܐ ܐܠܐ ܐܢ ܕܥܠ ܟܪܝܗܐ ܩܠܝܠ ܤܡ ܐܝܕܗ ܘܐܤܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 6, 'ܘܡܬܕܡܪ ܗܘܐ ܒܚܤܝܪܘܬ ܗܝܡܢܘܬܗܘܢ ܘܡܬܟܪܟ ܗܘܐ ܒܩܘܪܝܐ ܟܕ ܡܠܦ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 7, 'ܘܩܪܐ ܠܬܪܥܤܪܬܗ ܘܫܪܝ ܕܢܫܕܪ ܐܢܘܢ ܬܪܝܢ ܬܪܝܢ ܘܝܗܒ ܠܗܘܢ ܫܘܠܛܢܐ ܥܠ ܪܘܚܐ ܛܢܦܬܐ ܕܢܦܩܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 8, 'ܘܦܩܕ ܐܢܘܢ ܕܠܐ ܢܫܩܠܘܢ ܡܕܡ ܠܐܘܪܚܐ ܐܠܐ ܐܢ ܫܒܛܐ ܒܠܚܘܕ ܠܐ ܬܪܡܠܐ ܘܠܐ ܠܚܡܐ ܘܠܐ ܢܚܫܐ ܒܟܝܤܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 9, 'ܐܠܐ ܢܤܐܢܘܢ ܛܠܪܐ ܘܠܐ ܢܠܒܫܘܢ ܬܪܬܝܢ ܟܘܬܝܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 10, 'ܘܐܡܪ ܠܗܘܢ ܕܠܐܝܢܐ ܒܝܬܐ ܕܥܐܠܝܢ ܐܢܬܘܢ ܬܡܢ ܗܘܘ ܥܕܡܐ ܕܢܦܩܝܢ ܐܢܬܘܢ ܡܢ ܬܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 11, 'ܘܟܠ ܡܢ ܕܠܐ ܢܩܒܠܘܢܟܘܢ ܘܠܐ ܢܫܡܥܘܢܟܘܢ ܡܐ ܕܢܦܩܝܢ ܐܢܬܘܢ ܡܢ ܬܡܢ ܦܨܘ ܚܠܐ ܕܒܬܚܬܝܐ ܕܪܓܠܝܟܘܢ ܠܤܗܕܘܬܗܘܢ ܘܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܢܗܘܐ ܢܝܚ ܠܤܕܘܡ ܘܠܥܡܘܪܐ ܒܝܘܡܐ ܕܕܝܢܐ ܐܘ ܠܡܕܝܢܬܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 12, 'ܘܢܦܩܘ ܗܘܘ ܘܐܟܪܙܘ ܕܢܬܘܒܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 13, 'ܘܫܐܕܐ ܤܓܝܐܐ ܡܦܩܝܢ ܗܘܘ ܘܡܫܚܝܢ ܗܘܘ ܒܡܫܚܐ ܟܪܝܗܐ ܤܓܝܐܐ ܘܡܐܤܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 14, 'ܘܫܡܥ ܗܪܘܕܤ ܡܠܟܐ ܥܠ ܝܫܘܥ ܐܬܝܕܥ ܗܘܐ ܠܗ ܓܝܪ ܫܡܗ ܘܐܡܪ ܗܘܐ ܝܘܚܢܢ ܡܥܡܕܢܐ ܩܡ ܡܢ ܒܝܬ ܡܝܬܐ ܡܛܠ ܗܢܐ ܚܝܠܐ ܡܤܬܥܪܝܢ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 15, 'ܐܚܪܢܐ ܐܡܪܝܢ ܗܘܘ ܕܐܠܝܐ ܗܘ ܘܐܚܪܢܐ ܕܢܒܝܐ ܗܘ ܐܝܟ ܚܕ ܡܢ ܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 16, 'ܟܕ ܫܡܥ ܕܝܢ ܗܪܘܕܤ ܐܡܪ ܝܘܚܢܢ ܗܘ ܕܐܢܐ ܦܤܩܬ ܪܫܗ ܗܘ ܩܡ ܡܢ ܒܝܬ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 17, 'ܗܘ ܓܝܪ ܗܪܘܕܤ ܫܕܪ ܗܘܐ ܐܚܕܗ ܠܝܘܚܢܢ ܘܐܤܪܗ ܒܝܬ ܐܤܝܪܐ ܡܛܠ ܗܪܘܕܝܐ ܐܢܬܬ ܦܝܠܝܦܘܤ ܐܚܘܗܝ ܗܝ ܕܢܤܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 18, 'ܐܡܪ ܗܘܐ ܓܝܪ ܝܘܚܢܢ ܠܗܪܘܕܤ ܕܠܐ ܫܠܝܛ ܠܟ ܕܬܤܒ ܐܢܬܬ ܐܚܘܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 19, 'ܗܝ ܕܝܢ ܗܪܘܕܝܐ ܠܚܝܡܐ ܗܘܬ ܠܗ ܘܨܒܝܢ ܗܘܬ ܠܡܩܛܠܗ ܘܠܐ ܡܫܟܚܐ ܗܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 20, 'ܗܪܘܕܤ ܓܝܪ ܕܚܠ ܗܘܐ ܡܢ ܝܘܚܢܢ ܥܠ ܕܝܕܥ ܗܘܐ ܕܓܒܪܐ ܗܘ ܙܕܝܩܐ ܘܩܕܝܫܐ ܘܡܢܛܪ ܗܘܐ ܠܗ ܘܤܓܝܐܬܐ ܫܡܥ ܗܘܐ ܠܗ ܘܥܒܕ ܘܒܤܝܡܐܝܬ ܫܡܥ ܗܘܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 21, 'ܘܗܘܐ ܝܘܡܐ ܝܕܝܥܐ ܟܕ ܗܪܘܕܤ ܒܒܝܬ ܝܠܕܗ ܚܫܡܝܬܐ ܥܒܕ ܗܘܐ ܠܪܘܪܒܢܘܗܝ ܘܠܟܝܠܝܪܟܐ ܘܠܪܫܐ ܕܓܠܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 22, 'ܘܥܠܬ ܒܪܬܗ ܕܗܪܘܕܝܐ ܪܩܕܬ ܘܫܦܪܬ ܠܗ ܠܗܪܘܕܤ ܘܠܐܝܠܝܢ ܕܤܡܝܟܝܢ ܥܡܗ ܘܐܡܪ ܡܠܟܐ ܠܛܠܝܬܐ ܫܐܠܝ ܡܢܝ ܡܕܡ ܕܨܒܝܐ ܐܢܬܝ ܘܐܬܠ ܠܟܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 23, 'ܘܝܡܐ ܠܗ ܕܡܕܡ ܕܬܫܐܠܝܢ ܐܬܠ ܠܟܝ ܥܕܡܐ ܠܦܠܓܗ ܕܡܠܟܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 24, 'ܗܝ ܕܝܢ ܢܦܩܬ ܘܐܡܪܐ ܠܐܡܗ ܡܢܐ ܐܫܐܠܝܘܗܝ ܐܡܪܐ ܠܗ ܪܫܗ ܕܝܘܚܢܢ ܡܥܡܕܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 25, 'ܘܡܚܕܐ ܥܠܬ ܒܒܛܝܠܘܬܐ ܠܘܬ ܡܠܟܐ ܘܐܡܪܐ ܠܗ ܨܒܝܐ ܐܢܐ ܒܗܕܐ ܫܥܬܐ ܕܬܬܠ ܠܝ ܥܠ ܦܝܢܟܐ ܪܫܗ ܕܝܘܚܢܢ ܡܥܡܕܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 26, 'ܘܟܪܝܬ ܠܗ ܤܓܝ ܠܡܠܟܐ ܡܛܠ ܕܝܢ ܡܘܡܬܐ ܘܡܛܠ ܤܡܝܟܐ ܠܐ ܨܒܐ ܕܢܓܠܙܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 27, 'ܐܠܐ ܡܚܕܐ ܫܕܪ ܡܠܟܐ ܐܤܦܘܩܠܛܪܐ ܘܦܩܕ ܕܢܝܬܐ ܪܫܗ ܕܝܘܚܢܢ ܘܐܙܠ ܦܤܩܗ ܪܫܗ ܕܝܘܚܢܢ ܒܝܬ ܐܤܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 28, 'ܘܐܝܬܝ ܒܦܝܢܟܐ ܘܝܗܒ ܠܛܠܝܬܐ ܘܗܝ ܛܠܝܬܐ ܝܗܒܬ ܠܐܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 29, 'ܘܫܡܥܘ ܬܠܡܝܕܘܗܝ ܘܐܬܘ ܫܩܠܘ ܫܠܕܗ ܘܤܡܘ ܒܒܝܬ ܩܒܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 30, 'ܘܐܬܟܢܫܘ ܫܠܝܚܐ ܠܘܬ ܝܫܘܥ ܘܐܡܪܘ ܠܗ ܟܠ ܡܐ ܕܥܒܕܘ ܘܟܠ ܡܐ ܕܐܠܦܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 31, 'ܘܐܡܪ ܠܗܘܢ ܬܘ ܠܟܘܢ ܢܐܙܠ ܠܕܒܪܐ ܒܠܚܘܕܝܢ ܘܐܬܬܢܝܚܘ ܩܠܝܠ ܐܝܬ ܗܘܘ ܓܝܪ ܤܓܝܐܐ ܕܐܙܠܝܢ ܘܐܬܝܢ ܘܠܝܬ ܗܘܐ ܠܗܘܢ ܐܬܪܐ ܐܦ ܠܐ ܠܡܐܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 32, 'ܘܐܙܠܘ ܠܐܬܪܐ ܚܘܪܒܐ ܒܤܦܝܢܬܐ ܒܠܚܘܕܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 33, 'ܘܚܙܘ ܐܢܘܢ ܤܓܝܐܐ ܟܕ ܐܙܠܝܢ ܘܐܫܬܘܕܥܘ ܐܢܘܢ ܘܒܝܒܫܐ ܪܗܛܘ ܡܢ ܟܠܗܝܢ ܡܕܝܢܬܐ ܩܕܡܘܗܝ ܠܬܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 34, 'ܘܢܦܩ ܝܫܘܥ ܚܙܐ ܟܢܫܐ ܤܓܝܐܐ ܘܐܬܪܚܡ ܥܠܝܗܘܢ ܕܕܡܝܢ ܗܘܘ ܠܥܪܒܐ ܕܠܝܬ ܠܗܘܢ ܪܥܝܐ ܘܫܪܝ ܗܘܐ ܠܡܠܦܘ ܐܢܘܢ ܤܓܝܐܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 35, 'ܘܟܕ ܗܘܐ ܥܕܢܐ ܤܓܝܐܐ ܩܪܒܘ ܠܘܬܗ ܬܠܡܝܕܘܗܝ ܘܐܡܪܝܢ ܠܗ ܕܗܢܐ ܐܬܪܐ ܚܘܪܒܐ ܗܘ ܘܥܕܢܐ ܤܓܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 36, 'ܫܪܝ ܐܢܘܢ ܕܢܐܙܠܘܢ ܠܐܓܘܪܤܐ ܕܚܕܪܝܢ ܘܠܩܘܪܝܐ ܘܢܙܒܢܘܢ ܠܗܘܢ ܠܚܡܐ ܠܝܬ ܠܗܘܢ ܓܝܪ ܡܕܡ ܠܡܐܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 37, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܗܒܘ ܠܗܘܢ ܐܢܬܘܢ ܠܡܠܥܤ ܐܡܪܝܢ ܠܗ ܢܐܙܠ ܢܙܒܢ ܕܡܐܬܝܢ ܕܝܢܪܝܢ ܠܚܡܐ ܘܢܬܠ ܠܗܘܢ ܠܥܤܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 38, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܙܠܘ ܚܙܘ ܟܡܐ ܠܚܡܝܢ ܐܝܬ ܠܟܘܢ ܗܪܟܐ ܘܟܕ ܚܙܘ ܐܡܪܝܢ ܠܗ ܚܡܫܐ ܠܚܡܝܢ ܘܬܪܝܢ ܢܘܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 39, 'ܘܦܩܕ ܠܗܘܢ ܕܢܤܡܟܘܢ ܠܟܠܢܫ ܤܡܟܝܢ ܤܡܟܝܢ ܥܠ ܥܤܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 40, 'ܘܐܤܬܡܟܘ ܤܡܟܝܢ ܤܡܟܝܢ ܕܡܐܐ ܡܐܐ ܘܕܚܡܫܝܢ ܚܡܫܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 41, 'ܘܢܤܒ ܗܢܘܢ ܚܡܫܐ ܠܚܡܝܢ ܘܬܪܝܢ ܢܘܢܝܢ ܘܚܪ ܒܫܡܝܐ ܘܒܪܟ ܘܩܨܐ ܠܚܡܐ ܘܝܗܒ ܠܬܠܡܝܕܘܗܝ ܕܢܤܝܡܘܢ ܠܗܘܢ ܘܗܢܘܢ ܬܪܝܢ ܢܘܢܝܢ ܦܠܓܘ ܠܟܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 42, 'ܘܐܟܠܘ ܟܠܗܘܢ ܘܤܒܥܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 43, 'ܘܫܩܠܘ ܩܨܝܐ ܬܪܥܤܪ ܩܘܦܝܢܝܢ ܟܕ ܡܠܝܢ ܘܡܢ ܢܘܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 44, 'ܐܝܬܝܗܘܢ ܗܘܘ ܕܝܢ ܕܐܟܠܘ ܠܚܡܐ ܚܡܫܐ ܐܠܦܝܢ ܓܒܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 45, 'ܘܡܚܕܐ ܐܠܨ ܠܬܠܡܝܕܘܗܝ ܕܢܤܩܘܢ ܠܤܦܝܢܬܐ ܘܢܐܙܠܘܢ ܩܕܡܘܗܝ ܠܥܒܪܐ ܠܒܝܬ ܨܝܕܐ ܥܕ ܫܪܐ ܗܘ ܠܟܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 46, 'ܘܟܕ ܫܪܐ ܐܢܘܢ ܐܙܠ ܠܛܘܪܐ ܠܡܨܠܝܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 47, 'ܟܕ ܗܘܐ ܕܝܢ ܪܡܫܐ ܤܦܝܢܬܐ ܐܝܬܝܗ ܗܘܬ ܡܨܥܬ ܝܡܐ ܘܗܘ ܒܠܚܘܕܘܗܝ ܥܠ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 48, 'ܘܚܙܐ ܐܢܘܢ ܕܡܫܬܢܩܝܢ ܟܕ ܪܕܝܢ ܪܘܚܐ ܓܝܪ ܠܘܩܒܠܗܘܢ ܗܘܬ ܘܒܡܛܪܬܐ ܪܒܝܥܝܬܐ ܕܠܠܝܐ ܐܬܐ ܠܘܬܗܘܢ ܝܫܘܥ ܟܕ ܡܗܠܟ ܥܠ ܡܝܐ ܘܨܒܐ ܗܘܐ ܕܢܥܒܪ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 49, 'ܗܢܘܢ ܕܝܢ ܚܙܐܘܗܝ ܕܡܗܠܟ ܥܠ ܡܝܐ ܘܤܒܪܘ ܠܗܘܢ ܕܚܙܘܐ ܗܘ ܕܓܠܐ ܘܩܥܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 50, 'ܟܠܗܘܢ ܓܝܪ ܚܙܐܘܗܝ ܘܕܚܠܘ ܘܒܪ ܫܥܬܗ ܡܠܠ ܥܡܗܘܢ ܘܐܡܪ ܠܗܘܢ ܐܬܠܒܒܘ ܐܢܐ ܐܢܐ ܠܐ ܬܕܚܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 51, 'ܘܤܠܩ ܠܘܬܗܘܢ ܠܤܦܝܢܬܐ ܘܫܠܝܬ ܪܘܚܐ ܘܛܒ ܡܬܕܡܪܝܢ ܗܘܘ ܘܬܗܝܪܝܢ ܒܢܦܫܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 52, 'ܠܐ ܓܝܪ ܐܤܬܟܠܘ ܗܘܘ ܡܢ ܠܚܡܐ ܗܘ ܡܛܠ ܕܠܒܗܘܢ ܡܥܒܝ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 53, 'ܘܟܕ ܥܒܪܘ ܥܒܪܐ ܐܬܘ ܠܐܪܥܐ ܕܓܢܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 54, 'ܘܟܕ ܢܦܩܘ ܡܢ ܤܦܝܢܬܐ ܒܪ ܫܥܬܗ ܐܤܬܟܠܘܗܝ ܐܢܫܝ ܐܬܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 55, 'ܘܪܗܛܘ ܒܟܠܗ ܐܪܥܐ ܗܝ ܘܫܪܝܘ ܠܡܝܬܝܘ ܠܐܝܠܝܢ ܕܒܝܫܐܝܬ ܥܒܝܕܝܢ ܟܕ ܫܩܝܠܝܢ ܠܗܘܢ ܒܥܪܤܬܐ ܠܐܝܟܐ ܕܫܡܥܝܢ ܗܘܘ ܕܐܝܬܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 56, 'ܘܐܝܟܐ ܕܥܐܠ ܗܘܐ ܠܩܘܪܝܐ ܘܠܡܕܝܢܬܐ ܒܫܘܩܐ ܤܝܡܝܢ ܗܘܘ ܟܪܝܗܐ ܘܒܥܝܢ ܗܘܘ ܡܢܗ ܕܐܦܢ ܠܟܢܦܐ ܕܠܒܘܫܗ ܢܩܪܒܘܢ ܘܟܠܗܘܢ ܐܝܠܝܢ ܕܩܪܒܝܢ ܗܘܘ ܠܗ ܡܬܐܤܝܢ ܗܘܘ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 1, 'ܘܟܢܫܘ ܠܘܬܗ ܦܪܝܫܐ ܘܤܦܪܐ ܕܐܬܘ ܡܢ ܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 2, 'ܘܚܙܘ ܐܢܫܝܢ ܡܢ ܬܠܡܝܕܘܗܝ ܕܐܟܠܝܢ ܠܚܡܐ ܟܕ ܠܐ ܡܫܓܢ ܐܝܕܝܗܘܢ ܘܐܬܥܕܠܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 3, 'ܟܠܗܘܢ ܓܝܪ ܝܗܘܕܝܐ ܘܦܪܝܫܐ ܐܢ ܗܘ ܕܒܛܝܠܐܝܬ ܠܐ ܡܫܝܓܝܢ ܐܝܕܝܗܘܢ ܠܐ ܠܥܤܝܢ ܡܛܠ ܕܐܚܝܕܝܢ ܡܫܠܡܢܘܬܐ ܕܩܫܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 4, 'ܘܡܢ ܫܘܩܐ ܐܠܐ ܥܡܕܝܢ ܠܐ ܠܥܤܝܢ ܘܤܓܝܐܬܐ ܐܚܪܢܝܬܐ ܐܝܬ ܐܝܠܝܢ ܕܩܒܠܘ ܕܢܛܪܘܢ ܡܥܡܘܕܝܬܐ ܕܟܤܐ ܘܕܩܤܛܐ ܘܕܡܐܢܝ ܢܚܫܐ ܘܕܥܪܤܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 5, 'ܘܫܐܠܘܗܝ ܤܦܪܐ ܘܦܪܝܫܐ ܠܡܢܐ ܬܠܡܝܕܝܟ ܠܐ ܡܗܠܟܝܢ ܐܝܟ ܡܫܠܡܢܘܬܐ ܕܩܫܝܫܐ ܐܠܐ ܟܕ ܠܐ ܡܫܓܢ ܐܝܕܝܗܘܢ ܐܟܠܝܢ ܠܚܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 6, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܫܦܝܪ ܐܬܢܒܝ ܥܠܝܟܘܢ ܐܫܥܝܐ ܢܒܝܐ ܢܤܒܝ ܒܐܦܐ ܐܝܟ ܕܟܬܝܒ ܕܥܡܐ ܗܢܐ ܒܤܦܘܬܗ ܗܘ ܡܝܩܪ ܠܝ ܠܒܗܘܢ ܕܝܢ ܤܓܝ ܪܚܝܩ ܡܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 7, 'ܘܤܪܝܩܐܝܬ ܕܚܠܝܢ ܠܝ ܟܕ ܡܠܦܝܢ ܝܘܠܦܢܐ ܕܦܘܩܕܢܐ ܕܒܢܝ ܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 8, 'ܫܒܩܬܘܢ ܓܝܪ ܦܘܩܕܢܐ ܕܐܠܗܐ ܘܐܚܝܕܝܢ ܐܢܬܘܢ ܡܫܠܡܢܘܬܐ ܕܒܢܝ ܐܢܫܐ ܡܥܡܘܕܝܬܐ ܕܟܤܐ ܘܕܩܤܛܐ ܘܤܓܝܐܬܐ ܐܝܠܝܢ ܕܠܗܠܝܢ ܕܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 9, 'ܐܡܪ ܠܗܘܢ ܫܦܝܪ ܛܠܡܝܢ ܐܢܬܘܢ ܦܘܩܕܢܐ ܕܐܠܗܐ ܕܬܩܝܡܘܢ ܡܫܠܡܢܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 10, 'ܡܘܫܐ ܓܝܪ ܐܡܪ ܕܝܩܪ ܠܐܒܘܟ ܘܠܐܡܟ ܘܡܢ ܕܡܨܚܐ ܠܐܒܐ ܘܠܐܡܐ ܡܘܬܐ ܢܡܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 11, 'ܐܢܬܘܢ ܕܝܢ ܐܡܪܝܢ ܐܢܬܘܢ ܐܢ ܢܐܡܪ ܓܒܪܐ ܠܐܒܘܗܝ ܐܘ ܠܐܡܗ ܩܘܪܒܢܝ ܡܕܡ ܕܡܢܝ ܬܐܬܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 12, 'ܘܠܐ ܫܒܩܝܢ ܐܢܬܘܢ ܠܗ ܕܢܥܒܕ ܡܕܡ ܠܐܒܘܗܝ ܐܘ ܠܐܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 13, 'ܘܡܤܠܝܢ ܐܢܬܘܢ ܡܠܬܐ ܕܐܠܗܐ ܡܛܠ ܡܫܠܡܢܘܬܐ ܕܐܫܠܡܬܘܢ ܘܕܕܡܝܢ ܠܗܠܝܢ ܤܓܝܐܬܐ ܥܒܕܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 14, 'ܘܩܪܐ ܝܫܘܥ ܠܟܢܫܐ ܟܠܗ ܘܐܡܪ ܠܗܘܢ ܫܘܡܥܘܢܝ ܟܠܟܘܢ ܘܐܤܬܟܠܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 15, 'ܠܝܬ ܡܕܡ ܕܠܒܪ ܡܢ ܒܪܢܫܐ ܗܘ ܘܥܐܠ ܠܗ ܕܡܫܟܚ ܡܤܝܒ ܠܗ ܐܠܐ ܡܕܡ ܕܢܦܩ ܡܢܗ ܗܘ ܗܘ ܡܤܝܒ ܠܒܪ ܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 16, 'ܡܢ ܕܐܝܬ ܠܗ ܐܕܢܐ ܕܢܫܡܥ ܢܫܡܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 17, 'ܟܕ ܕܝܢ ܥܠ ܝܫܘܥ ܠܒܝܬܐ ܡܢ ܟܢܫܐ ܫܐܠܘܗܝ ܬܠܡܝܕܘܗܝ ܥܠ ܡܬܠܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 18, 'ܐܡܪ ܠܗܘܢ ܗܟܢܐ ܐܦ ܐܢܬܘܢ ܥܛܠܝܢ ܐܢܬܘܢ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܕܟܠ ܡܕܡ ܕܡܢ ܠܒܪ ܥܐܠ ܠܒܪ ܐܢܫܐ ܠܐ ܡܫܟܚ ܡܤܝܒ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 19, 'ܕܠܐ ܗܘܐ ܠܠܒܗ ܥܐܠ ܐܠܐ ܠܟܪܤܗ ܘܡܫܬܕܐ ܒܬܕܟܝܬܐ ܕܡܕܟܝܐ ܟܠܗ ܡܐܟܘܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 20, 'ܡܕܡ ܕܝܢ ܕܢܦܩ ܡܢ ܒܪܢܫܐ ܗܘ ܗܘ ܡܤܝܒ ܠܒܪ ܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 21, 'ܡܢ ܠܓܘ ܓܝܪ ܡܢ ܠܒܐ ܕܒܢܝ ܐܢܫܐ ܢܦܩܢ ܡܚܫܒܬܐ ܒܝܫܬܐ ܓܘܪܐ ܙܢܝܘܬܐ ܓܢܒܘܬܐ ܩܛܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 22, 'ܥܠܘܒܘܬܐ ܒܝܫܘܬܐ ܢܟܠܐ ܨܚܢܘܬܐ ܥܝܢܐ ܒܝܫܬܐ ܓܘܕܦܐ ܫܒܗܪܢܘܬܐ ܫܛܝܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 23, 'ܗܠܝܢ ܟܠܗܝܢ ܒܝܫܬܐ ܡܢ ܠܓܘ ܗܘ ܢܦܩܢ ܘܡܤܝܒܢ ܠܗ ܠܒܪܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 24, 'ܡܢ ܬܡܢ ܩܡ ܝܫܘܥ ܘܐܬܐ ܠܬܚܘܡܐ ܕܨܘܪ ܘܕܨܝܕܢ ܘܥܠ ܠܒܝܬܐ ܚܕ ܘܠܐ ܨܒܐ ܗܘܐ ܕܐܢܫ ܢܕܥ ܒܗ ܘܠܐ ܐܫܟܚ ܕܢܛܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 25, 'ܡܚܕܐ ܓܝܪ ܫܡܥܬ ܐܢܬܬܐ ܚܕܐ ܡܛܠܬܗ ܕܐܝܬ ܗܘܬ ܠܒܪܬܗ ܪܘܚܐ ܛܢܦܬܐ ܘܐܬܬ ܢܦܠܬ ܩܕܡ ܪܓܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 26, 'ܗܝ ܕܝܢ ܐܢܬܬܐ ܐܝܬܝܗ ܗܘܬ ܚܢܦܬܐ ܡܢ ܦܘܢܝܩܐ ܕܤܘܪܝܐ ܘܒܥܝܐ ܗܘܬ ܡܢܗ ܕܢܦܩ ܫܐܕܐ ܡܢ ܒܪܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 27, 'ܘܐܡܪ ܠܗ ܝܫܘܥ ܫܒܘܩܝ ܠܘܩܕܡ ܕܢܤܒܥܘܢ ܒܢܝܐ ܠܐ ܗܘܬ ܓܝܪ ܫܦܝܪܐ ܕܢܤܒ ܠܚܡܐ ܕܒܢܝܐ ܘܢܪܡܐ ܠܟܠܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 28, 'ܗܝ ܕܝܢ ܥܢܬ ܘܐܡܪܐ ܠܗ ܐܝܢ ܡܪܝ ܐܦ ܟܠܒܐ ܡܢ ܬܚܝܬ ܦܬܘܪܐ ܐܟܠܝܢ ܦܪܬܘܬܐ ܕܒܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 29, 'ܐܡܪ ܠܗ ܝܫܘܥ ܙܠܝ ܡܛܠ ܗܕܐ ܡܠܬܐ ܢܦܩ ܠܗ ܫܐܕܐ ܡܢ ܒܪܬܟܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 30, 'ܘܐܙܠܬ ܠܒܝܬܗ ܘܐܫܟܚܬ ܒܪܬܗ ܟܕ ܪܡܝܐ ܒܥܪܤܐ ܘܢܦܝܩ ܡܢܗ ܫܐܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 31, 'ܬܘܒ ܢܦܩ ܝܫܘܥ ܡܢ ܬܚܘܡܐ ܕܨܘܪ ܘܕܨܝܕܢ ܘܐܬܐ ܠܝܡܐ ܕܓܠܝܠܐ ܒܬܚܘܡܐ ܕܥܤܪܬ ܡܕܝܢܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 32, 'ܘܐܝܬܝܘ ܠܗ ܚܪܫܐ ܚܕ ܦܐܩܐ ܘܒܥܐ ܗܘܘ ܡܢܗ ܕܢܤܝܡ ܥܠܘܗܝ ܐܝܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 33, 'ܘܢܓܕܗ ܡܢ ܟܢܫܐ ܒܠܚܘܕܘܗܝ ܘܐܪܡܝ ܨܒܥܬܗ ܒܐܕܢܘܗܝ ܘܪܩ ܘܩܪܒ ܠܠܫܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 34, 'ܘܚܪ ܒܫܡܝܐ ܘܐܬܬܢܚ ܘܐܡܪ ܠܗ ܐܬܦܬܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 35, 'ܘܒܗ ܒܫܥܬܐ ܐܬܦܬܚ ܐܕܢܘܗܝ ܘܐܫܬܪܝ ܐܤܪܐ ܕܠܫܢܗ ܘܡܠܠ ܦܫܝܩܐܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 36, 'ܘܙܗܪ ܐܢܘܢ ܕܠܐܢܫ ܠܐ ܢܐܡܪܘܢ ܘܟܡܐ ܕܗܘ ܡܙܗܪ ܗܘܐ ܠܗܘܢ ܗܢܘܢ ܝܬܝܪ ܡܟܪܙܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 37, 'ܘܝܬܝܪܐܝܬ ܡܬܕܡܪܝܢ ܗܘܘ ܘܐܡܪܝܢ ܕܟܠ ܡܕܡ ܫܦܝܪ ܥܒܕ ܠܚܪܫܐ ܥܒܕ ܕܢܫܡܥܘܢ ܘܕܠܐ ܡܡܠܠܝܢ ܕܢܡܠܠܘܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 1, 'ܒܗܢܘܢ ܕܝܢ ܝܘܡܬܐ ܟܕ ܟܢܫܐ ܤܓܝܐܐ ܐܝܬ ܗܘܐ ܘܠܐ ܐܝܬ ܗܘܐ ܡܕܡ ܕܢܐܟܠܘܢ ܩܪܐ ܠܬܠܡܝܕܘܗܝ ܘܐܡܪ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 2, 'ܡܬܪܚܡ ܐܢܐ ܥܠ ܟܢܫܐ ܗܢܐ ܕܗܐ ܬܠܬܐ ܝܘܡܝܢ ܩܘܝܘ ܠܘܬܝ ܘܠܝܬ ܠܗܘܢ ܡܢܐ ܢܐܟܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 3, 'ܘܐܢ ܗܘ ܕܫܪܐ ܐܢܐ ܠܗܘܢ ܟܕ ܨܝܡܝܢ ܠܒܬܝܗܘܢ ܥܝܦܝܢ ܒܐܘܪܚܐ ܐܢܫܐ ܓܝܪ ܡܢܗܘܢ ܡܢ ܪܘܚܩܐ ܐܬܝܐܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 4, 'ܐܡܪܝܢ ܠܗ ܬܠܡܝܕܘܗܝ ܐܝܡܟܐ ܡܫܟܚ ܐܢܫ ܗܪܟܐ ܒܚܘܪܒܐ ܕܢܤܒܥ ܠܚܡܐ ܠܗܠܝܢ ܟܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 5, 'ܘܫܐܠ ܐܢܘܢ ܗܘ ܟܡܐ ܠܚܡܝܢ ܐܝܬ ܠܟܘܢ ܐܡܪܝܢ ܠܗ ܫܒܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 6, 'ܘܦܩܕ ܠܟܢܫܐ ܕܢܤܬܡܟܘܢ ܥܠ ܐܪܥܐ ܘܢܤܒ ܗܢܘܢ ܫܒܥܐ ܠܚܡܝܢ ܘܒܪܟ ܘܩܨܐ ܘܝܗܒ ܠܬܠܡܝܕܘܗܝ ܕܢܤܝܡܘܢ ܘܤܡܘ ܠܟܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 7, 'ܘܐܝܬ ܗܘܘ ܢܘܢܐ ܩܠܝܠ ܘܐܦ ܥܠܝܗܘܢ ܒܪܟ ܘܐܡܪ ܕܢܤܝܡܘܢ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 8, 'ܘܐܟܠܘ ܘܤܒܥܘ ܘܫܩܠܘ ܬܘܬܪܐ ܕܩܨܝܐ ܫܒܥܐ ܐܤܦܪܝܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 9, 'ܐܝܬܝܗܘܢ ܗܘܘ ܕܝܢ ܐܢܫܐ ܕܐܟܠܘ ܐܝܟ ܐܪܒܥܐ ܐܠܦܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 10, 'ܘܫܪܐ ܐܢܘܢ ܘܤܠܩ ܡܚܕܐ ܠܤܦܝܢܬܐ ܥܡ ܬܠܡܝܕܘܗܝ ܘܐܬܐ ܠܐܬܪܐ ܕܕܠܡܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 11, 'ܘܢܦܩܘ ܦܪܝܫܐ ܘܫܪܝܘ ܠܡܒܥܐ ܥܡܗ ܘܫܐܠܝܢ ܗܘܘ ܠܗ ܐܬܐ ܡܢ ܫܡܝܐ ܟܕ ܡܢܤܝܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 12, 'ܘܐܬܬܢܚ ܒܪܘܚܗ ܘܐܡܪ ܡܢܐ ܒܥܝܐ ܐܬܐ ܫܪܒܬܐ ܗܕܐ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܠܐ ܬܬܝܗܒ ܠܗ ܐܬܐ ܠܫܪܒܬܐ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 13, 'ܘܫܒܩ ܐܢܘܢ ܘܤܠܩ ܠܤܦܝܢܬܐ ܘܐܙܠܘ ܠܗܘ ܥܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 14, 'ܘܛܥܘ ܕܢܤܒܘܢ ܠܚܡܐ ܘܐܠܐ ܚܕܐ ܓܪܝܨܬܐ ܠܝܬ ܗܘܐ ܥܡܗܘܢ ܒܤܦܝܢܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 15, 'ܘܦܩܕ ܐܢܘܢ ܘܐܡܪ ܠܗܘܢ ܚܙܘ ܐܙܕܗܪܘ ܡܢ ܚܡܝܪܐ ܕܦܪܝܫܐ ܘܡܢ ܚܡܝܪܗ ܕܗܪܘܕܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 16, 'ܘܡܬܚܫܒܝܢ ܗܘܘ ܚܕ ܥܡ ܚܕ ܘܐܡܪܝܢ ܕܠܚܡܐ ܠܝܬ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 17, 'ܝܫܘܥ ܕܝܢ ܝܕܥ ܘܐܡܪ ܠܗܘܢ ܡܢܐ ܪܢܝܢ ܐܢܬܘܢ ܕܠܚܡܐ ܠܝܬ ܠܟܘܢ ܠܐ ܥܕܡܐ ܠܗܫܐ ܝܕܥܝܢ ܐܢܬܘܢ ܘܠܐ ܡܤܬܟܠܝܢ ܐܢܬܘܢ ܥܕܟܝܠ ܠܒܐ ܩܫܝܐ ܐܝܬ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 18, 'ܘܥܝܢܐ ܐܝܬ ܠܟܘܢ ܘܠܐ ܚܙܝܢ ܐܢܬܘܢ ܘܐܕܢܐ ܐܝܬ ܠܟܘܢ ܘܠܐ ܫܡܥܝܢ ܐܢܬܘܢ ܘܠܐ ܥܗܕܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 19, 'ܟܕ ܗܠܝܢ ܚܡܫܐ ܠܚܡܝܢ ܩܨܝܬ ܠܚܡܫܐ ܐܠܦܝܢ ܟܡܐ ܩܘܦܝܢܝܢ ܕܩܨܝܐ ܟܕ ܡܠܝܢ ܫܩܠܬܘܢ ܐܡܪܝܢ ܠܗ ܬܪܥܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 20, 'ܐܡܪ ܠܗܘܢ ܘܟܕ ܫܒܥܐ ܠܐܪܒܥܐ ܐܠܦܝܢ ܟܡܐ ܐܤܦܪܝܕܝܢ ܕܩܨܝܐ ܟܕ ܡܠܝܢ ܫܩܠܬܘܢ ܐܡܪܝܢ ܫܒܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 21, 'ܐܡܪ ܠܗܘܢ ܐܝܟܘ ܠܐ ܥܕܡܐ ܠܗܫܐ ܡܤܬܟܠܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 22, 'ܘܐܬܐ ܠܒܝܬ ܨܝܕܐ ܘܐܝܬܝܘ ܠܗ ܤܡܝܐ ܘܒܥܝܢ ܗܘܘ ܡܢܗ ܕܢܩܪܘܒ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 23, 'ܘܐܚܕ ܒܐܝܕܗ ܕܤܡܝܐ ܘܐܦܩܗ ܠܒܪ ܡܢ ܩܪܝܬܐ ܘܪܩ ܒܥܝܢܘܗܝ ܘܤܡ ܐܝܕܗ ܘܫܐܠܗ ܕܡܢܐ ܚܙܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 24, 'ܚܪ ܘܐܡܪ ܚܙܐ ܐܢܐ ܒܢܝ ܐܢܫܐ ܐܝܟ ܐܝܠܢܐ ܕܡܗܠܟܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 25, 'ܬܘܒ ܤܡ ܐܝܕܗ ܥܠ ܥܝܢܘܗܝ ܘܬܩܢ ܘܚܙܐ ܗܘܐ ܟܠ ܡܕܡ ܢܗܝܪܐܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 26, 'ܘܫܕܪܗ ܠܒܝܬܗ ܘܐܡܪ ܐܦ ܠܐ ܠܩܪܝܬܐ ܬܥܘܠ ܘܠܐ ܬܐܡܪ ܠܐܢܫ ܒܩܪܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 27, 'ܘܢܦܩ ܝܫܘܥ ܘܬܠܡܝܕܘܗܝ ܠܩܘܪܝܐ ܕܩܤܪܝܐ ܕܦܝܠܝܦܘܤ ܘܡܫܐܠ ܗܘܐ ܠܬܠܡܝܕܘܗܝ ܒܐܘܪܚܐ ܘܐܡܪ ܠܗܘܢ ܡܢܘ ܐܡܪܝܢ ܥܠܝ ܐܢܫܐ ܕܐܝܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 28, 'ܗܢܘܢ ܕܝܢ ܐܡܪܘ ܕܝܘܚܢܢ ܡܥܡܕܢܐ ܘܐܚܪܢܐ ܕܐܠܝܐ ܘܐܚܪܢܐ ܚܕ ܡܢ ܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 29, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܢܬܘܢ ܕܝܢ ܡܢܘ ܐܡܪܝܢ ܐܢܬܘܢ ܥܠܝ ܕܐܝܬܝ ܥܢܐ ܫܡܥܘܢ ܘܐܡܪ ܠܗ ܐܢܬ ܗܘ ܡܫܝܚܐ ܒܪܗ ܕܐܠܗܐ ܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 30, 'ܘܟܐܐ ܒܗܘܢ ܕܠܐܢܫ ܠܐ ܢܐܡܪܘܢ ܥܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 31, 'ܘܫܪܝ ܗܘܐ ܠܡܠܦܘ ܐܢܘܢ ܕܥܬܝܕ ܗܘ ܒܪܗ ܕܐܢܫܐ ܕܢܚܫ ܤܓܝ ܘܕܢܤܬܠܐ ܡܢ ܩܫܝܫܐ ܘܡܢ ܪܒܝ ܟܗܢܐ ܘܡܢ ܤܦܪܐ ܘܢܬܩܛܠ ܘܠܬܠܬܐ ܝܘܡܝܢ ܢܩܘܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 32, 'ܘܥܝܢ ܒܓܠܐ ܡܠܬܐ ܡܡܠܠ ܗܘܐ ܘܕܒܪܗ ܟܐܦܐ ܘܫܪܝ ܠܡܟܐܐ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 33, 'ܗܘ ܕܝܢ ܐܬܦܢܝ ܘܚܪ ܒܬܠܡܝܕܘܗܝ ܘܟܐܐ ܒܫܡܥܘܢ ܘܐܡܪ ܙܠ ܠܟ ܠܒܤܬܪܝ ܤܛܢܐ ܕܠܐ ܪܢܐ ܐܢܬ ܕܐܠܗܐ ܐܠܐ ܕܒܢܝ ܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 34, 'ܘܩܪܐ ܝܫܘܥ ܠܟܢܫܐ ܥܡ ܬܠܡܝܕܘܗܝ ܘܐܡܪ ܠܗܘܢ ܡܢ ܕܨܒܐ ܕܢܐܬܐ ܒܬܪܝ ܢܟܦܘܪ ܒܢܦܫܗ ܘܢܫܩܘܠ ܙܩܝܦܗ ܘܢܐܬܐ ܒܬܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 35, 'ܟܠ ܡܢ ܕܨܒܐ ܓܝܪ ܕܢܚܐ ܢܦܫܗ ܢܘܒܕܝܗ ܘܟܠ ܕܢܘܒܕ ܢܦܫܗ ܡܛܠܬܝ ܘܡܛܠ ܤܒܪܬܝ ܢܚܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 36, 'ܡܢܐ ܓܝܪ ܢܬܥܕܪ ܒܪܢܫܐ ܐܢ ܥܠܡܐ ܟܠܗ ܢܐܬܪ ܘܢܦܫܗ ܢܚܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 37, 'ܐܘ ܡܢܐ ܢܬܠ ܒܪܢܫܐ ܬܚܠܘܦܐ ܕܢܦܫܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 38, 'ܟܠ ܓܝܪ ܕܢܒܗܬ ܒܝ ܘܒܡܠܝ ܒܫܪܒܬܐ ܗܕܐ ܚܛܝܬܐ ܘܓܝܪܬܐ ܘܐܦ ܒܪܗ ܕܐܢܫܐ ܢܒܗܬ ܒܗ ܡܐ ܕܐܬܐ ܒܫܘܒܚܐ ܕܐܒܘܗܝ ܥܡ ܡܠܐܟܘܗܝ ܩܕܝܫܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 1, 'ܘܐܡܪ ܗܘܐ ܠܗܘܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܐܝܬ ܐܢܫܐ ܕܩܝܡܝܢ ܬܢܢ ܕܠܐ ܢܛܥܡܘܢ ܡܘܬܐ ܥܕܡܐ ܕܢܚܙܘܢ ܡܠܟܘܬܗ ܕܐܠܗܐ ܕܐܬܬ ܒܚܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 2, 'ܘܒܬܪ ܫܬܐ ܝܘܡܝܢ ܕܒܪ ܝܫܘܥ ܠܟܐܦܐ ܘܠܝܥܩܘܒ ܘܠܝܘܚܢܢ ܘܐܤܩ ܐܢܘܢ ܠܛܘܪܐ ܪܡܐ ܒܠܚܘܕܝܗܘܢ ܘܐܬܚܠܦ ܠܥܢܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 3, 'ܘܡܙܗܪ ܗܘܐ ܠܒܘܫܗ ܘܡܚܘܪ ܛܒ ܐܝܟ ܬܠܓܐ ܐܝܟܢܐ ܕܒܢܝ ܐܢܫܐ ܠܡܚܘܪܘ ܒܐܪܥܐ ܠܐ ܡܫܟܚܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 4, 'ܘܐܬܚܙܝܘ ܠܗܘܢ ܐܠܝܐ ܘܡܘܫܐ ܟܕ ܡܡܠܠܝܢ ܥܡ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 5, 'ܘܐܡܪ ܠܗ ܟܐܦܐ ܪܒܝ ܫܦܝܪ ܗܘ ܠܢ ܕܗܪܟܐ ܢܗܘܐ ܘܢܥܒܕ ܬܠܬ ܡܛܠܝܢ ܠܟ ܚܕܐ ܘܠܡܘܫܐ ܚܕܐ ܘܠܐܠܝܐ ܚܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 6, 'ܠܐ ܕܝܢ ܝܕܥ ܗܘܐ ܡܢܐ ܐܡܪ ܐܝܬܝܗܘܢ ܗܘܘ ܓܝܪ ܒܕܚܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 7, 'ܘܗܘܬ ܥܢܢܐ ܘܡܛܠܐ ܗܘܬ ܥܠܝܗܘܢ ܘܩܠܐ ܡܢ ܥܢܢܐ ܕܐܡܪ ܗܢܘ ܒܪܝ ܚܒܝܒܐ ܠܗ ܫܡܥܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 8, 'ܘܡܢ ܫܠܝܐ ܟܕ ܚܪܘ ܬܠܡܝܕܐ ܠܐܢܫ ܠܐ ܚܙܘ ܐܠܐ ܠܝܫܘܥ ܒܠܚܘܕܘܗܝ ܥܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 9, 'ܘܟܕ ܢܚܬܝܢ ܡܢ ܛܘܪܐ ܡܦܩܕ ܗܘܐ ܠܗܘܢ ܕܠܐܢܫ ܠܐ ܢܐܡܪܘܢ ܡܕܡ ܕܚܙܘ ܐܠܐ ܐܢ ܡܐ ܕܩܡ ܒܪܗ ܕܐܢܫܐ ܡܢ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 10, 'ܘܐܚܕܘܗ ܠܡܠܬܐ ܒܢܦܫܗܘܢ ܘܒܥܝܢ ܗܘܘ ܕܡܢܐ ܗܝ ܗܕܐ ܡܠܬܐ ܕܡܐ ܕܩܡ ܡܢ ܒܝܬ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 11, 'ܘܡܫܐܠܝܢ ܗܘܘ ܠܗ ܘܐܡܪܝܢ ܡܢܐ ܗܟܝܠ ܐܡܪܝܢ ܤܦܪܐ ܕܐܠܝܐ ܘܠܐ ܕܢܐܬܐ ܠܘܩܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 12, 'ܐܡܪ ܠܗܘܢ ܐܠܝܐ ܐܬܐ ܠܘܩܕܡ ܕܟܠܡܕܡ ܢܬܩܢ ܘܐܝܟܢܐ ܟܬܝܒ ܥܠ ܒܪܗ ܕܐܢܫܐ ܕܤܓܝ ܢܚܫ ܘܢܤܬܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 13, 'ܐܠܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܐܦ ܐܠܝܐ ܐܬܐ ܘܥܒܕܘ ܒܗ ܟܠ ܡܐ ܕܨܒܘ ܐܝܟܢܐ ܕܟܬܝܒ ܥܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 14, 'ܘܟܕ ܐܬܐ ܠܘܬ ܬܠܡܝܕܐ ܚܙܐ ܠܘܬܗܘܢ ܟܢܫܐ ܤܓܝܐܐ ܘܤܦܪܐ ܟܕ ܕܪܫܝܢ ܥܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 15, 'ܘܒܪ ܫܥܬܗ ܟܠܗ ܟܢܫܐ ܚܙܐܘܗܝ ܘܬܘܗܘ ܘܪܗܛܘ ܫܐܠܘ ܒܫܠܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 16, 'ܘܡܫܐܠ ܗܘܐ ܠܤܦܪܐ ܡܢܐ ܕܪܫܝܢ ܐܢܬܘܢ ܥܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 17, 'ܘܥܢܐ ܚܕ ܡܢ ܟܢܫܐ ܘܐܡܪ ܡܠܦܢܐ ܐܝܬܝܬ ܒܪܝ ܠܘܬܟ ܕܐܝܬ ܠܗ ܪܘܚܐ ܕܠܐ ܡܡܠܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 18, 'ܘܐܝܟܐ ܕܡܕܪܟܐ ܠܗ ܚܒܛܐ ܠܗ ܘܡܪܥܬ ܘܡܚܪܩ ܫܢܘܗܝ ܘܝܒܫ ܘܐܡܪܬ ܠܬܠܡܝܕܝܟ ܕܢܦܩܘܢܝܗܝ ܘܠܐ ܐܫܟܚܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 19, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗ ܐܘܢ ܫܪܒܬܐ ܕܠܐ ܡܗܝܡܢܐ ܥܕܡܐ ܠܐܡܬܝ ܐܗܘܐ ܠܘܬܟܘܢ ܘܥܕܡܐ ܠܐܡܬܝ ܐܤܝܒܪܟܘܢ ܐܝܬܐܘܗܝ ܠܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 20, 'ܘܐܝܬܝܘܗܝ ܠܘܬܗ ܘܟܕ ܚܙܬܗ ܪܘܚܐ ܒܪ ܫܥܬܗ ܚܒܛܬܗ ܘܢܦܠ ܥܠ ܐܪܥܐ ܘܡܬܒܥܩ ܗܘܐ ܘܡܪܥܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 21, 'ܘܫܐܠ ܝܫܘܥ ܠܐܒܘܗܝ ܕܟܡܐ ܠܗ ܙܒܢܐ ܗܐ ܡܢ ܕܗܟܢܐ ܗܘ ܐܡܪ ܠܗ ܗܐ ܡܢ ܛܠܝܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 22, 'ܘܙܒܢܝܢ ܤܓܝܐܢ ܐܪܡܝܬܗ ܒܢܘܪܐ ܘܒܡܝܐ ܕܬܘܒܕܝܘܗܝ ܐܠܐ ܡܕܡ ܕܡܫܟܚ ܐܢܬ ܥܕܪܝܢܝ ܘܐܬܪܚܡ ܥܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 23, 'ܐܡܪ ܠܗ ܝܫܘܥ ܐܢ ܡܫܟܚ ܐܢܬ ܕܬܗܝܡܢ ܟܠ ܡܕܡ ܡܫܟܚ ܕܢܗܘܐ ܠܡܢ ܕܡܗܝܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 24, 'ܘܒܪ ܫܥܬܗ ܩܥܐ ܐܒܘܗܝ ܕܛܠܝܐ ܟܕ ܒܟܐ ܘܐܡܪ ܡܗܝܡܢ ܐܢܐ ܡܪܝ ܥܕܪ ܠܚܤܝܪܘܬ ܗܝܡܢܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 25, 'ܟܕ ܚܙܐ ܕܝܢ ܝܫܘܥ ܕܪܗܛ ܥܡܐ ܘܡܬܟܢܫ ܠܘܬܗ ܟܐܐ ܒܗܝ ܪܘܚܐ ܛܢܦܬܐ ܘܐܡܪ ܠܗ ܪܘܚܐ ܚܪܫܬܐ ܕܠܐ ܡܡܠܠܐ ܐܢܐ ܦܩܕ ܐܢܐ ܠܟܝ ܦܘܩܝ ܡܢܗ ܘܬܘܒ ܠܐ ܬܥܠܝܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 26, 'ܘܩܥܐ ܫܐܕܐ ܗܘ ܤܓܝ ܘܫܚܩܗ ܘܢܦܩ ܘܗܘܐ ܐܝܟ ܡܝܬܐ ܐܝܟ ܕܤܓܝܐܐ ܢܐܡܪܘܢ ܕܡܝܬ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 27, 'ܗܘ ܕܝܢ ܝܫܘܥ ܐܚܕܗ ܒܐܝܕܗ ܘܐܩܝܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 28, 'ܟܕ ܥܠ ܕܝܢ ܠܒܝܬܐ ܝܫܘܥ ܫܐܠܘܗܝ ܬܠܡܝܕܘܗܝ ܒܠܚܘܕܝܗܘܢ ܠܡܢܐ ܚܢܢ ܠܐ ܐܫܟܚܢ ܠܡܦܩܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 29, 'ܐܡܪ ܠܗܘܢ ܗܢܐ ܓܢܤܐ ܒܡܕܡ ܠܐ ܡܫܟܚ ܠܡܦܩ ܐܠܐ ܒܨܘܡܐ ܘܒܨܠܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 30, 'ܘܟܕ ܢܦܩ ܡܢ ܬܡܢ ܥܒܪܝܢ ܗܘܘ ܒܓܠܝܠܐ ܘܠܐ ܨܒܐ ܗܘܐ ܕܐܢܫ ܢܕܥ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 31, 'ܡܠܦ ܗܘܐ ܓܝܪ ܠܬܠܡܝܕܘܗܝ ܘܐܡܪ ܠܗܘܢ ܕܒܪܗ ܕܐܢܫܐ ܡܫܬܠܡ ܒܐܝܕܝ ܐܢܫܐ ܘܢܩܛܠܘܢܝܗܝ ܘܡܐ ܕܐܬܩܛܠ ܒܝܘܡܐ ܕܬܠܬܐ ܢܩܘܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 32, 'ܗܢܘܢ ܕܝܢ ܠܐ ܝܕܥܝܢ ܗܘܘ ܠܗ ܠܡܠܬܐ ܘܕܚܠܝܢ ܗܘܘ ܕܢܫܐܠܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 33, 'ܘܐܬܘ ܠܟܦܪܢܚܘܡ ܘܟܕ ܥܠܘ ܠܒܝܬܐ ܡܫܐܠ ܗܘܐ ܠܗܘܢ ܕܡܢܐ ܡܬܚܫܒܝܢ ܗܘܝܬܘܢ ܒܐܘܪܚܐ ܒܝܢܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 34, 'ܗܢܘܢ ܕܝܢ ܫܬܝܩܝܢ ܗܘܘ ܐܬܚܪܝܘ ܗܘܘ ܓܝܪ ܒܐܘܪܚܐ ܚܕ ܥܡ ܚܕ ܕܡܢܘ ܪܒ ܒܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 35, 'ܘܝܬܒ ܝܫܘܥ ܘܩܪܐ ܠܬܪܥܤܪ ܘܐܡܪ ܠܗܘܢ ܡܢ ܕܨܒܐ ܕܢܗܘܐ ܩܕܡܝܐ ܢܗܘܐ ܐܚܪܝܐ ܕܟܠ ܐܢܫ ܘܡܫܡܫܢܐ ܕܟܠ ܐܢܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 36, 'ܘܢܤܒ ܛܠܝܐ ܚܕ ܘܐܩܝܡܗ ܒܡܨܥܬܐ ܘܫܩܠܗ ܥܠ ܕܪܥܘܗܝ ܘܐܡܪ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 37, 'ܟܠ ܡܢ ܕܢܩܒܠ ܐܝܟ ܗܢܐ ܛܠܝܐ ܒܫܡܝ ܠܝ ܗܘ ܡܩܒܠ ܘܡܢ ܕܠܝ ܡܩܒܠ ܠܐ ܗܘܐ ܠܝ ܡܩܒܠ ܐܠܐ ܠܡܢ ܕܫܕܪܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 38, 'ܐܡܪ ܠܗ ܝܘܚܢܢ ܪܒܝ ܚܙܝܢ ܐܢܫ ܕܡܦܩ ܫܐܕܐ ܒܫܡܟ ܘܟܠܝܢܝܗܝ ܥܠ ܕܠܐ ܢܩܦ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 39, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܠܐ ܬܟܠܘܢܝܗܝ ܠܝܬ ܓܝܪ ܐܢܫ ܕܥܒܕ ܚܝܠܐ ܒܫܡܝ ܘܡܫܟܚ ܥܓܠ ܐܡܪ ܥܠܝ ܕܒܝܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 40, 'ܡܢ ܕܠܐ ܗܘܐ ܗܟܝܠ ܠܘܩܒܠܟܘܢ ܚܠܦܝܟܘܢ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 41, 'ܟܠ ܕܝܢ ܕܢܫܩܝܟܘܢ ܟܤܐ ܕܡܝܐ ܒܠܚܘܕ ܒܫܡܐ ܕܕܡܫܝܚܐ ܐܢܬܘܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܠܐ ܢܘܒܕ ܐܓܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 42, 'ܘܟܠ ܡܢ ܕܢܟܫܠ ܠܚܕ ܡܢ ܗܠܝܢ ܙܥܘܪܐ ܕܡܗܝܡܢܝܢ ܒܝ ܦܩܚ ܗܘܐ ܠܗ ܐܠܘ ܪܡܝܐ ܗܘܬ ܪܚܝܐ ܕܚܡܪܐ ܒܨܘܪܗ ܘܫܕܐ ܒܝܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 43, 'ܐܢ ܕܝܢ ܡܟܫܠܐ ܠܟ ܐܝܕܟ ܦܤܘܩܝܗ ܦܩܚ ܗܘ ܠܟ ܦܫܝܓܐ ܕܬܥܘܠ ܠܚܝܐ ܐܘ ܟܕ ܐܝܬ ܠܟ ܬܪܬܝܢ ܐܝܕܝܢ ܬܐܙܠ ܠܓܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 44, 'ܐܝܟܐ ܕܬܘܠܥܗܘܢ ܠܐ ܡܝܬܐ ܘܢܘܪܗܘܢ ܠܐ ܕܥܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 45, 'ܘܐܢ ܪܓܠܟ ܡܟܫܠܐ ܠܟ ܦܤܘܩܝܗ ܦܩܚ ܗܘ ܠܟ ܕܬܥܘܠ ܠܚܝܐ ܚܓܝܤܐ ܐܘ ܟܕ ܐܝܬ ܠܟ ܬܪܬܝܢ ܪܓܠܝܢ ܬܦܠ ܒܓܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 46, 'ܐܝܟܐ ܕܬܘܠܥܗܘܢ ܠܐ ܡܝܬܐ ܘܢܘܪܗܘܢ ܠܐ ܕܥܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 47, 'ܘܐܢ ܥܝܢܟ ܡܟܫܠܐ ܠܟ ܚܨܝܗ ܦܩܚ ܗܘ ܠܟ ܕܒܚܕܐ ܥܝܢܟ ܬܥܘܠ ܠܡܠܟܘܬܗ ܕܐܠܗܐ ܐܘ ܟܕ ܐܝܬ ܠܟ ܬܪܬܝܢ ܥܝܢܝܢ ܬܦܠ ܒܓܗܢܐ ܕܢܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 48, 'ܐܝܟܐ ܕܬܘܠܥܗܘܢ ܠܐ ܡܝܬܐ ܘܢܘܪܗܘܢ ܠܐ ܕܥܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 49, 'ܟܠ ܓܝܪ ܒܢܘܪܐ ܢܬܡܠܚ ܘܟܠ ܕܒܚܬܐ ܒܡܠܚܐ ܬܬܡܠܚ ܀ 50 ܫܦܝܪܐ ܗܝ ܡܠܚܐ ܐܢ ܕܝܢ ܡܠܚܐ ܬܦܟܗ ܒܡܢܐ ܬܬܡܠܚ ܬܗܘܐ ܒܟܘܢ ܡܠܚܐ ܘܒܫܝܢܐ ܗܘܘ ܚܕ ܥܡ ܚܕ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 50, '');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 1, 'ܘܩܡ ܡܢ ܬܡܢ ܘܐܬܐ ܠܬܚܘܡܐ ܕܝܗܘܕ ܠܥܒܪܐ ܕܝܘܪܕܢܢ ܘܐܙܠܘ ܠܬܡܢ ܠܘܬܗ ܟܢܫܐ ܤܓܝܐܐ ܘܡܠܦ ܗܘܐ ܠܗܘܢ ܬܘܒ ܐܝܟ ܕܡܥܕ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 2, 'ܘܩܪܒܘ ܦܪܝܫܐ ܡܢܤܝܢ ܠܗ ܘܡܫܐܠܝܢ ܕܐܢ ܫܠܝܛ ܠܓܒܪܐ ܕܢܫܒܘܩ ܐܢܬܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 3, 'ܐܡܪ ܠܗܘܢ ܡܢܐ ܦܩܕܟܘܢ ܡܘܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 4, 'ܗܢܘܢ ܕܝܢ ܐܡܪܝܢ ܡܘܫܐ ܐܦܤ ܠܢ ܕܢܟܬܘܒ ܟܬܒܐ ܕܫܘܒܩܢܐ ܘܢܫܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 5, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܠܘܩܒܠ ܩܫܝܘܬ ܠܒܟܘܢ ܟܬܒ ܠܟܘܢ ܦܘܩܕܢܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 6, 'ܡܢ ܒܪܫܝܬ ܕܝܢ ܕܟܪܐ ܘܢܩܒܬܐ ܥܒܕ ܐܢܘܢ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 7, 'ܡܛܠ ܗܢܐ ܢܫܒܘܩ ܓܒܪܐ ܠܐܒܘܗܝ ܘܠܐܡܗ ܘܢܩܦ ܠܐܢܬܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 8, 'ܘܢܗܘܘܢ ܬܪܝܗܘܢ ܚܕ ܒܤܪ ܡܟܝܠ ܠܐ ܗܘܘ ܬܪܝܢ ܐܠܐ ܚܕ ܒܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 9, 'ܡܕܡ ܕܐܠܗܐ ܗܟܝܠ ܙܘܓ ܒܪܢܫܐ ܠܐ ܢܦܪܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 10, 'ܘܫܐܠܘܗܝ ܬܘܒ ܬܠܡܝܕܘܗܝ ܒܒܝܬܐ ܥܠ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 11, 'ܘܐܡܪ ܠܗܘܢ ܟܠ ܡܢ ܕܢܫܪܐ ܐܢܬܬܗ ܘܢܤܒ ܐܚܪܬܐ ܓܐܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 12, 'ܘܐܢ ܐܢܬܬܐ ܬܫܪܐ ܒܥܠܗ ܘܬܗܘܐ ܠܐܚܪܢܐ ܓܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 13, 'ܘܡܩܪܒܝܢ ܗܘܘ ܠܗ ܛܠܝܐ ܕܢܩܪܘܒ ܠܗܘܢ ܬܠܡܝܕܘܗܝ ܕܝܢ ܟܐܝܢ ܗܘܘ ܒܗܠܝܢ ܕܡܩܪܒܝܢ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 14, 'ܝܫܘܥ ܕܝܢ ܚܙܐ ܘܐܬܒܐܫ ܠܗ ܘܐܡܪ ܠܗܘܢ ܫܒܘܩܘ ܛܠܝܐ ܐܬܝܢ ܠܘܬܝ ܘܠܐ ܬܟܠܘܢ ܐܢܘܢ ܕܕܐܝܠܝܢ ܓܝܪ ܕܐܝܟ ܗܠܝܢ ܐܢܘܢ ܐܝܬܝܗ ܡܠܟܘܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 15, 'ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܟܠ ܕܠܐ ܢܩܒܠ ܡܠܟܘܬܐ ܕܐܠܗܐ ܐܝܟ ܛܠܝܐ ܠܐ ܢܥܘܠ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 16, 'ܘܫܩܠ ܐܢܘܢ ܥܠ ܕܪܥܘܗܝ ܘܤܡ ܐܝܕܗ ܥܠܝܗܘܢ ܘܒܪܟ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 17, 'ܘܟܕ ܪܕܐ ܒܐܘܪܚܐ ܪܗܛ ܚܕ ܢܦܠ ܥܠ ܒܘܪܟܘܗܝ ܘܡܫܐܠ ܗܘܐ ܠܗ ܘܐܡܪ ܡܠܦܢܐ ܛܒܐ ܡܢܐ ܐܥܒܕ ܕܐܬܪ ܚܝܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 18, 'ܐܡܪ ܠܗ ܝܫܘܥ ܡܢܐ ܩܪܐ ܐܢܬ ܠܝ ܛܒܐ ܠܝܬ ܛܒܐ ܐܠܐ ܐܢ ܚܕ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 19, 'ܦܘܩܕܢܐ ܝܕܥ ܐܢܬ ܠܐ ܬܓܘܪ ܠܐ ܬܓܢܘܒ ܠܐ ܬܩܛܘܠ ܘܠܐ ܬܤܗܕ ܤܗܕܘܬܐ ܕܓܠܬܐ ܠܐ ܬܛܠܘܡ ܝܩܪ ܠܐܒܘܟ ܘܠܐܡܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 20, 'ܗܘ ܕܝܢ ܥܢܐ ܘܐܡܪ ܠܗ ܡܠܦܢܐ ܗܠܝܢ ܟܠܗܝܢ ܢܛܪܬ ܐܢܝܢ ܡܢ ܛܠܝܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 21, 'ܝܫܘܥ ܕܝܢ ܚܪ ܒܗ ܘܐܚܒܗ ܘܐܡܪ ܠܗ ܚܕܐ ܚܤܝܪܐ ܠܟ ܙܠ ܙܒܢ ܟܠ ܡܕܡ ܕܐܝܬ ܠܟ ܘܗܒ ܠܡܤܟܢܐ ܘܬܗܘܐ ܠܟ ܤܝܡܬܐ ܒܫܡܝܐ ܘܤܒ ܨܠܝܒܐ ܘܬܐ ܒܬܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 22, 'ܗܘ ܕܝܢ ܐܬܟܡܪ ܒܡܠܬܐ ܗܕܐ ܘܐܙܠ ܟܕ ܥܝܝܩܐ ܠܗ ܐܝܬ ܗܘܐ ܠܗ ܓܝܪ ܢܟܤܐ ܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 23, 'ܚܪ ܕܝܢ ܝܫܘܥ ܒܬܠܡܝܕܘܗܝ ܘܐܡܪ ܠܗܘܢ ܟܡܐ ܥܛܠܐ ܠܐܝܠܝܢ ܕܐܝܬ ܠܗܘܢ ܢܟܤܐ ܕܢܥܠܘܢ ܠܡܠܟܘܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 24, 'ܬܠܡܝܕܐ ܕܝܢ ܡܬܕܡܪܝܢ ܗܘܘ ܥܠ ܡܠܘܗܝ ܘܥܢܐ ܬܘܒ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܒܢܝ ܟܡܐ ܥܛܠܐ ܠܐܝܠܝܢ ܕܬܟܝܠܝܢ ܥܠ ܢܟܤܝܗܘܢ ܕܢܥܠܘܢ ܠܡܠܟܘܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 25, 'ܦܫܝܩ ܗܘ ܠܓܡܠܐ ܕܢܥܘܠ ܒܚܪܘܪܐ ܕܡܚܛܐ ܐܘ ܥܬܝܪܐ ܠܡܠܟܘܬܗ ܕܐܠܗܐ ܠܡܥܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 26, 'ܗܢܘܢ ܕܝܢ ܝܬܝܪܐܝܬ ܡܬܕܡܪܝܢ ܗܘܘ ܘܐܡܪܝܢ ܒܝܢܝܗܘܢ ܡܢܘ ܡܫܟܚ ܠܡܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 27, 'ܚܪ ܕܝܢ ܒܗܘܢ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܠܘܬ ܒܢܝ ܐܢܫܐ ܗܕܐ ܠܐ ܡܫܟܚܐ ܐܠܐ ܠܘܬ ܐܠܗܐ ܟܠ ܡܕܡ ܓܝܪ ܡܫܟܚܐ ܠܘܬ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 28, 'ܘܫܪܝ ܟܐܦܐ ܠܡܐܡܪ ܗܐ ܚܢܢ ܫܒܩܢ ܟܠ ܡܕܡ ܘܢܩܦܢܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 29, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܠܝܬ ܐܢܫ ܕܫܒܩ ܒܬܐ ܐܘ ܐܚܐ ܐܘ ܐܚܘܬܐ ܐܘ ܐܒܐ ܐܘ ܐܡܐ ܐܘ ܐܢܬܬܐ ܐܘ ܒܢܝܐ ܐܘ ܩܘܪܝܐ ܡܛܠܬܝ ܘܡܛܠ ܤܒܪܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 30, 'ܘܠܐ ܢܩܒܠ ܚܕ ܒܡܐܐ ܗܫܐ ܒܙܒܢܐ ܗܢܐ ܒܬܐ ܘܐܚܐ ܘܐܚܘܬܐ ܘܐܡܗܬܐ ܘܒܢܝܐ ܘܩܘܪܝܐ ܥܡ ܪܕܘܦܝܐ ܘܒܥܠܡܐ ܕܐܬܐ ܚܝܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 31, 'ܤܓܝܐܐ ܕܝܢ ܩܕܡܝܐ ܕܢܗܘܘܢ ܐܚܪܝܐ ܘܐܚܪܝܐ ܩܕܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 32, 'ܟܕ ܤܠܩܝܢ ܗܘܘ ܕܝܢ ܒܐܘܪܚܐ ܠܐܘܪܫܠܡ ܗܘ ܝܫܘܥ ܩܕܝܡ ܗܘܐ ܠܗܘܢ ܘܡܬܬܡܗܝܢ ܗܘܘ ܘܐܙܠܝܢ ܗܘܘ ܒܬܪܗ ܟܕ ܕܚܝܠܝܢ ܘܕܒܪ ܠܬܪܥܤܪܬܗ ܘܫܪܝ ܕܢܐܡܪ ܠܗܘܢ ܡܕܡ ܕܥܬܝܕ ܕܢܗܘܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 33, 'ܕܗܐ ܤܠܩܝܢ ܚܢܢ ܠܐܘܪܫܠܡ ܘܒܪܗ ܕܐܢܫܐ ܡܫܬܠܡ ܠܪܒܝ ܟܗܢܐ ܘܠܤܦܪܐ ܘܢܚܝܒܘܢܝܗܝ ܠܡܘܬܐ ܘܢܫܠܡܘܢܝܗܝ ܠܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 34, 'ܘܢܒܙܚܘܢ ܒܗ ܘܢܢܓܕܘܢܝܗܝ ܘܢܪܩܘܢ ܒܐܦܘܗܝ ܘܢܩܛܠܘܢܝܗܝ ܘܠܝܘܡܐ ܕܬܠܬܐ ܢܩܘܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 35, 'ܘܩܪܒܘ ܠܘܬܗ ܝܥܩܘܒ ܘܝܘܚܢܢ ܒܢܝ ܙܒܕܝ ܘܐܡܪܝܢ ܠܗ ܡܠܦܢܐ ܨܒܝܢ ܚܢܢ ܕܟܠ ܕܢܫܐܠ ܬܥܒܕ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 36, 'ܐܡܪ ܠܗܘܢ ܡܢܐ ܨܒܝܢ ܐܢܬܘܢ ܐܥܒܕ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 37, 'ܐܡܪܝܢ ܠܗ ܗܒ ܠܢ ܕܚܕ ܢܬܒ ܡܢ ܝܡܝܢܟ ܘܚܕ ܡܢ ܤܡܠܟ ܒܫܘܒܚܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 38, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܡܢܐ ܫܐܠܝܢ ܐܢܬܘܢ ܡܫܟܚܝܢ ܐܢܬܘܢ ܕܬܫܬܘܢ ܟܤܐ ܕܐܢܐ ܫܬܐ ܐܢܐ ܘܡܥܡܘܕܝܬܐ ܕܐܢܐ ܥܡܕ ܐܢܐ ܬܥܡܕܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 39, 'ܐܡܪܝܢ ܠܗ ܡܫܟܚܝܢܢ ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܟܤܐ ܕܫܬܐ ܐܢܐ ܬܫܬܘܢ ܘܡܥܡܘܕܝܬܐ ܕܥܡܕ ܐܢܐ ܬܥܡܕܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 40, 'ܕܬܬܒܘܢ ܕܝܢ ܡܢ ܝܡܝܢܝ ܘܡܢ ܤܡܠܝ ܠܐ ܗܘܬ ܕܝܠܝ ܠܡܬܠ ܐܠܐ ܠܐܝܠܝܢ ܕܡܛܝܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 41, 'ܘܟܕ ܫܡܥܘ ܥܤܪܐ ܫܪܝܘ ܪܛܢܝܢ ܥܠ ܝܥܩܘܒ ܘܝܘܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 42, 'ܘܩܪܐ ܐܢܘܢ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܝܕܥܝܢ ܐܢܬܘܢ ܕܐܝܠܝܢ ܕܡܤܬܒܪܝܢ ܪܫܐ ܕܥܡܡܐ ܡܪܝܗܘܢ ܐܢܘܢ ܘܪܘܪܒܢܝܗܘܢ ܫܠܝܛܝܢ ܥܠܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 43, 'ܠܐ ܕܝܢ ܗܟܢܐ ܢܗܘܐ ܒܝܢܬܟܘܢ ܐܠܐ ܡܢ ܕܨܒܐ ܒܟܘܢ ܕܢܗܘܐ ܪܒܐ ܢܗܘܐ ܠܟܘܢ ܡܫܡܫܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 44, 'ܘܐܝܢܐ ܡܢܟܘܢ ܕܨܒܐ ܕܢܗܘܐ ܩܕܡܝܐ ܢܗܘܐ ܥܒܕܐ ܕܟܠܢܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 45, 'ܐܦ ܒܪܗ ܓܝܪ ܕܐܢܫܐ ܠܐ ܐܬܐ ܕܢܫܬܡܫ ܐܠܐ ܕܢܫܡܫ ܘܕܢܬܠ ܢܦܫܗ ܦܘܪܩܢܐ ܚܠܦ ܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 46, 'ܘܐܬܘ ܠܐܝܪܝܚܘ ܘܟܕ ܢܦܩ ܝܫܘܥ ܡܢ ܐܝܪܝܚܘ ܗܘ ܘܬܠܡܝܕܘܗܝ ܘܟܢܫܐ ܤܓܝܐܐ ܛܝܡܝ ܒܪ ܛܝܡܝ ܤܡܝܐ ܝܬܒ ܗܘܐ ܥܠ ܝܕ ܐܘܪܚܐ ܘܚܕܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 47, 'ܘܫܡܥ ܕܝܫܘܥ ܗܘ ܢܨܪܝܐ ܘܫܪܝ ܠܡܩܥܐ ܘܠܡܐܡܪ ܒܪܗ ܕܕܘܝܕ ܐܬܪܚܡ ܥܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 48, 'ܘܟܐܝܢ ܗܘܘ ܒܗ ܤܓܝܐܐ ܕܢܫܬܘܩ ܗܘ ܕܝܢ ܝܬܝܪܐܝܬ ܩܥܐ ܗܘܐ ܘܐܡܪ ܒܪܗ ܕܕܘܝܕ ܐܬܪܚܡ ܥܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 49, 'ܘܩܡ ܝܫܘܥ ܘܦܩܕ ܕܢܩܪܘܢܝܗܝ ܘܩܪܐܘܗܝ ܠܤܡܝܐ ܘܐܡܪܝܢ ܠܗ ܐܬܠܒܒ ܩܘܡ ܩܪܐ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 50, 'ܗܘ ܕܝܢ ܤܡܝܐ ܫܕܐ ܠܒܫܗ ܘܩܡ ܐܬܐ ܠܘܬ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 51, 'ܐܡܪ ܠܗ ܝܫܘܥ ܡܢܐ ܨܒܐ ܐܢܬ ܐܥܒܕ ܠܟ ܗܘ ܕܝܢ ܤܡܝܐ ܐܡܪ ܠܗ ܪܒܝ ܕܐܚܙܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 52, 'ܘܝܫܘܥ ܐܡܪ ܠܗ ܚܙܝ ܗܝܡܢܘܬܟ ܐܚܝܬܟ ܘܡܚܕܐ ܐܬܚܙܝ ܠܗ ܘܐܙܠ ܗܘܐ ܒܐܘܪܚܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 1, 'ܘܟܕ ܩܪܒ ܠܐܘܪܫܠܡ ܥܠ ܓܢܒ ܒܝܬ ܦܓܐ ܘܒܝܬ ܥܢܝܐ ܠܘܬ ܛܘܪܐ ܕܙܝܬܐ ܫܕܪ ܬܪܝܢ ܡܢ ܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 2, 'ܘܐܡܪ ܠܗܘܢ ܙܠܘ ܠܩܪܝܬܐ ܗܝ ܕܠܩܘܒܠܢ ܘܒܪ ܫܥܬܗ ܕܥܐܠܝܢ ܐܢܬܘܢ ܠܗ ܡܫܟܚܝܢ ܐܢܬܘܢ ܥܝܠܐ ܕܐܤܝܪ ܕܐܢܫ ܡܢ ܒܢܝܢܫܐ ܠܐ ܪܟܒܗ ܫܪܘ ܐܝܬܐܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 3, 'ܘܐܢ ܐܢܫ ܢܐܡܪ ܠܟܘܢ ܡܢܐ ܥܒܕܝܢ ܐܢܬܘܢ ܗܕܐ ܐܡܪܘ ܠܗ ܕܠܡܪܢ ܡܬܒܥܐ ܘܡܚܕܐ ܡܫܕܪ ܠܗ ܠܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 4, 'ܘܐܙܠܘ ܐܫܟܚܘ ܥܝܠܐ ܕܐܤܝܪ ܥܠ ܬܪܥܐ ܠܒܪ ܒܫܘܩܐ ܘܟܕ ܫܪܝܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 5, 'ܐܢܫܝܢ ܡܢ ܐܝܠܝܢ ܕܩܝܡܝܢ ܐܡܪܘ ܠܗܘܢ ܡܢܐ ܥܒܕܝܢ ܐܢܬܘܢ ܕܫܪܝܢ ܐܢܬܘܢ ܥܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 6, 'ܗܢܘܢ ܕܝܢ ܐܡܪܘ ܠܗܘܢ ܐܝܟ ܕܦܩܕ ܐܢܘܢ ܝܫܘܥ ܘܫܒܩܘ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 7, 'ܘܐܝܬܝܘܗܝ ܠܥܝܠܐ ܠܘܬ ܝܫܘܥ ܘܐܪܡܝܘ ܥܠܘܗܝ ܡܐܢܝܗܘܢ ܘܪܟܒ ܥܠܘܗܝ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 8, 'ܤܓܝܐܐ ܕܝܢ ܡܫܘܝܢ ܗܘܘ ܢܚܬܝܗܘܢ ܒܐܘܪܚܐ ܘܐܚܪܢܐ ܦܤܩܝܢ ܗܘܘ ܤܘܟܐ ܡܢ ܐܝܠܢܐ ܘܡܫܘܝܢ ܒܐܘܪܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 9, 'ܘܗܢܘܢ ܕܩܕܡܘܗܝ ܘܗܢܘܢ ܕܒܬܪܗ ܩܥܝܢ ܗܘܘ ܘܐܡܪܝܢ ܐܘܫܥܢܐ ܒܪܝܟ ܗܘ ܕܐܬܐ ܒܫܡܗ ܕܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 10, 'ܘܒܪܝܟܐ ܗܝ ܡܠܟܘܬܐ ܕܐܬܝܐ ܕܐܒܘܢ ܕܘܝܕ ܐܘܫܥܢܐ ܒܡܪܘܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 11, 'ܘܥܠ ܝܫܘܥ ܠܐܘܪܫܠܡ ܠܗܝܟܠܐ ܘܚܙܐ ܟܠܡܕܡ ܟܕ ܗܘܐ ܕܝܢ ܥܕܢܐ ܕܪܡܫܐ ܢܦܩ ܠܒܝܬ ܥܢܝܐ ܥܡ ܬܪܥܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 12, 'ܘܠܝܘܡܐ ܐܚܪܢܐ ܟܕ ܢܦܩ ܡܢ ܒܝܬ ܥܢܝܐ ܟܦܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 13, 'ܘܚܙܐ ܬܬܐ ܚܕܐ ܡܢ ܪܘܚܩܐ ܕܐܝܬ ܒܗ ܛܪܦܐ ܘܐܬܐ ܠܘܬܗ ܕܐܢ ܢܫܟܚ ܒܗ ܡܕܡ ܘܟܕ ܐܬܐ ܠܐ ܐܫܟܚ ܒܗ ܐܠܐ ܐܢ ܛܪܦܐ ܙܒܢܐ ܓܝܪ ܠܐ ܗܘܐ ܗܘܐ ܕܬܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 14, 'ܘܐܡܪ ܠܗ ܡܟܝܠ ܘܠܥܠܡ ܐܢܫ ܡܢܟܝ ܦܐܪܐ ܠܐ ܢܐܟܘܠ ܘܫܡܥܘ ܬܠܡܝܕܘܗܝ ܘܐܬܘ ܠܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 15, 'ܘܥܠ ܝܫܘܥ ܠܗܝܟܠܐ ܕܐܠܗܐ ܘܫܪܝ ܕܢܦܩ ܠܐܝܠܝܢ ܕܙܒܢܝܢ ܘܡܙܒܢܝܢ ܒܗܝܟܠܐ ܘܗܦܟ ܦܬܘܪܐ ܕܡܥܪܦܢܐ ܘܟܘܪܤܘܬܐ ܕܗܢܘܢ ܕܡܙܒܢܝܢ ܝܘܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 16, 'ܘܠܐ ܫܒܩ ܗܘܐ ܕܐܢܫ ܢܥܒܪ ܡܐܢܐ ܒܓܘ ܗܝܟܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 17, 'ܘܡܠܦ ܗܘܐ ܘܐܡܪ ܠܗܘܢ ܠܐ ܟܬܝܒ ܕܒܝܬܝ ܒܝܬ ܨܠܘܬܐ ܢܬܩܪܐ ܠܟܠܗܘܢ ܥܡܡܐ ܐܢܬܘܢ ܕܝܢ ܥܒܕܬܘܢܝܗܝ ܡܥܪܬܐ ܕܠܤܛܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 18, 'ܘܫܡܥܘ ܪܒܝ ܟܗܢܐ ܘܤܦܪܐ ܘܒܥܝܢ ܗܘܘ ܕܐܝܟܢܐ ܢܘܒܕܘܢܝܗܝ ܕܚܠܝܢ ܗܘܘ ܓܝܪ ܡܢܗ ܡܛܠ ܕܟܠܗ ܥܡܐ ܬܡܝܗܝܢ ܗܘܘ ܒܝܘܠܦܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 19, 'ܘܟܕ ܗܘܐ ܪܡܫܐ ܢܦܩܘ ܠܒܪ ܡܢ ܡܕܝܢܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 20, 'ܘܒܨܦܪܐ ܟܕ ܥܒܪܝܢ ܚܙܘ ܬܬܐ ܗܝ ܟܕ ܝܒܝܫܐ ܡܢ ܥܩܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 21, 'ܘܐܬܕܟܪ ܫܡܥܘܢ ܘܐܡܪ ܠܗ ܪܒܝ ܗܐ ܬܬܐ ܗܝ ܕܠܛܬ ܝܒܫܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 22, 'ܘܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܬܗܘܐ ܒܟܘܢ ܗܝܡܢܘܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 23, 'ܐܡܝܢ ܓܝܪ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܡܢ ܕܢܐܡܪ ܠܛܘܪܐ ܗܢܐ ܕܐܫܬܩܠ ܘܦܠ ܒܝܡܐ ܘܠܐ ܢܬܦܠܓ ܒܠܒܗ ܐܠܐ ܢܗܝܡܢ ܕܗܘܐ ܗܘ ܡܕܡ ܕܐܡܪ ܢܗܘܐ ܠܗ ܡܕܡ ܕܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 24, 'ܡܛܠ ܗܢܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܟܠ ܡܕܡ ܕܡܨܠܝܢ ܐܢܬܘܢ ܘܫܐܠܝܢ ܐܢܬܘܢ ܗܝܡܢܘ ܕܢܤܒܝܢ ܐܢܬܘܢ ܘܢܗܘܐ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 25, 'ܘܡܐ ܕܩܝܡܝܢ ܐܢܬܘܢ ܠܡܨܠܝܘ ܫܒܘܩܘ ܡܕܡ ܕܐܝܬ ܠܟܘܢ ܥܠ ܐܢܫ ܕܐܦ ܐܒܘܟܘܢ ܕܒܫܡܝܐ ܢܫܒܘܩ ܠܟܘܢ ܤܟܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 26, 'ܐܢ ܕܝܢ ܐܢܬܘܢ ܠܐ ܫܒܩܝܢ ܐܢܬܘܢ ܐܦܠܐ ܐܒܘܟܘܢ ܕܒܫܡܝܐ ܢܫܒܘܩ ܠܟܘܢ ܤܟܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 27, 'ܘܐܬܘ ܬܘܒ ܠܐܘܪܫܠܡ ܘܟܕ ܡܗܠܟ ܗܘܐ ܒܗܝܟܠܐ ܐܬܘ ܠܘܬܗ ܪܒܝ ܟܗܢܐ ܘܤܦܪܐ ܘܩܫܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 28, 'ܘܐܡܪܝܢ ܠܗ ܒܐܝܢܐ ܫܘܠܛܢܐ ܗܠܝܢ ܥܒܕ ܐܢܬ ܘܡܢܘ ܝܗܒ ܠܟ ܫܘܠܛܢܐ ܗܢܐ ܕܗܠܝܢ ܬܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 29, 'ܗܘ ܕܝܢ ܝܫܘܥ ܐܡܪ ܠܗܘܢ ܐܫܐܠܟܘܢ ܐܦ ܐܢܐ ܡܠܬܐ ܚܕܐ ܕܬܐܡܪܘܢ ܠܝ ܘܐܢܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܒܐܝܢܐ ܫܘܠܛܢܐ ܗܠܝܢ ܥܒܕ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 30, 'ܡܥܡܘܕܝܬܗ ܕܝܘܚܢܢ ܡܢ ܐܝܡܟܐ ܗܝ ܡܢ ܫܡܝܐ ܐܘ ܡܢ ܒܢܝ ܐܢܫܐ ܐܡܪܘ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 31, 'ܘܐܬܚܫܒܘ ܒܢܦܫܗܘܢ ܘܐܡܪܘ ܕܐܢ ܢܐܡܪ ܠܗ ܕܡܢ ܫܡܝܐ ܐܡܪ ܠܢ ܘܠܡܢܐ ܠܐ ܗܝܡܢܬܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 32, 'ܘܕܢܐܡܪ ܡܢ ܒܢܝ ܐܢܫܐ ܕܚܠܬܐ ܗܝ ܡܢ ܥܡܐ ܟܠܗܘܢ ܓܝܪ ܐܚܝܕܝܢ ܗܘܘ ܠܗ ܠܝܘܚܢܢ ܕܫܪܝܪܐܝܬ ܢܒܝܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 33, 'ܘܥܢܘ ܘܐܡܪܝܢ ܠܗ ܠܝܫܘܥ ܠܐ ܝܕܥܝܢܢ ܐܡܪ ܠܗܘܢ ܐܦ ܠܐ ܐܢܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܒܐܝܢܐ ܫܘܠܛܢܐ ܗܠܝܢ ܥܒܕ ܐܢܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 1, 'ܘܫܪܝ ܕܢܡܠܠ ܥܡܗܘܢ ܒܡܬܠܐ ܓܒܪܐ ܚܕ ܢܨܒ ܟܪܡܐ ܘܐܚܕܪܗ ܤܝܓܐ ܘܚܦܪ ܒܗ ܡܥܨܪܬܐ ܘܒܢܐ ܒܗ ܡܓܕܠܐ ܘܐܘܚܕܗ ܠܦܠܚܐ ܘܚܙܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 2, 'ܘܫܕܪ ܠܘܬ ܦܠܚܐ ܥܒܕܗ ܒܙܒܢܐ ܕܡܢ ܦܐܪܐ ܕܟܪܡܐ ܢܤܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 3, 'ܗܢܘܢ ܕܝܢ ܡܚܐܘܗܝ ܘܫܕܪܘܗܝ ܟܕ ܤܦܝܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 4, 'ܘܫܕܪ ܬܘܒ ܠܘܬܗܘܢ ܥܒܕܐ ܐܚܪܢܐ ܘܐܦ ܠܗܘ ܪܓܡܘܗܝ ܘܨܠܦܘܗܝ ܘܫܕܪܘܗܝ ܒܨܥܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 5, 'ܘܫܕܪ ܬܘܒ ܐܚܪܢܐ ܐܦ ܠܗܘ ܩܛܠܘܗܝ ܘܠܤܓܝܐܐ ܥܒܕܐ ܐܚܪܢܐ ܫܕܪ ܘܡܢܗܘܢ ܡܚܘ ܡܢܗܘܢ ܕܝܢ ܩܛܠܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 6, 'ܚܪܬܐ ܕܝܢ ܚܕ ܒܪܐ ܚܒܝܒܐ ܐܝܬ ܗܘܐ ܠܗ ܘܫܕܪܗ ܠܘܬܗܘܢ ܐܚܪܝܬ ܐܡܪ ܓܝܪ ܟܒܪ ܢܒܗܬܘܢ ܡܢ ܒܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 7, 'ܗܢܘܢ ܕܝܢ ܦܠܚܐ ܐܡܪܘ ܒܢܦܫܗܘܢ ܗܢܘ ܝܪܬܐ ܬܘ ܢܩܛܠܝܘܗܝ ܘܬܗܘܐ ܕܝܠܢ ܝܪܬܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 8, 'ܘܢܤܒܘ ܩܛܠܘܗܝ ܘܐܦܩܘܗܝ ܠܒܪ ܡܢ ܟܪܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 9, 'ܡܢܐ ܗܟܝܠ ܢܥܒܕ ܡܪܐ ܟܪܡܐ ܢܐܬܐ ܢܘܒܕ ܠܗܢܘܢ ܦܠܚܐ ܘܢܬܠܝܘܗܝ ܟܪܡܐ ܠܐܚܪܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 10, 'ܘܐܦܠܐ ܟܬܒܐ ܗܢܐ ܩܪܝܬܘܢ ܕܟܐܦܐ ܕܐܤܠܝܘ ܒܢܝܐ ܗܝ ܗܘܬ ܠܪܫܐ ܕܙܘܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 11, 'ܡܢ ܠܘܬ ܡܪܝܐ ܗܘܬ ܗܕܐ ܘܐܝܬܝܗ ܬܕܡܘܪܬܐ ܒܥܝܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 12, 'ܘܒܥܝܢ ܗܘܘ ܠܡܐܚܕܗ ܘܕܚܠܘ ܡܢ ܥܡܐ ܝܕܥܘ ܓܝܪ ܕܥܠܝܗܘܢ ܐܡܪ ܡܬܠܐ ܗܢܐ ܘܫܒܩܘܗܝ ܘܐܙܠܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 13, 'ܘܫܕܪܘ ܠܘܬܗ ܐܢܫܐ ܡܢ ܤܦܪܐ ܘܡܢ ܕܒܝܬ ܗܪܘܕܤ ܕܢܨܘܕܘܢܝܗܝ ܒܡܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 14, 'ܗܢܘܢ ܕܝܢ ܐܬܘ ܘܫܐܠܘܗܝ ܡܠܦܢܐ ܝܕܥܝܢ ܚܢܢ ܕܫܪܝܪ ܐܢܬ ܘܠܐ ܫܩܝܠ ܐܢܬ ܨܦܬܐ ܠܐܢܫ ܠܐ ܓܝܪ ܚܐܪ ܐܢܬ ܒܦܪܨܘܦܐ ܕܒܢܝ ܐܢܫܐ ܐܠܐ ܒܫܪܪܐ ܐܘܪܚܐ ܕܐܠܗܐ ܡܠܦ ܐܢܬ ܫܠܝܛ ܠܡܬܠ ܟܤܦ ܪܫܐ ܠܩܤܪ ܐܘ ܠܐ ܢܬܠ ܐܘ ܠܐ ܢܬܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 15, 'ܗܘ ܕܝܢ ܝܕܥ ܢܟܠܗܘܢ ܘܐܡܪ ܠܗܘܢ ܡܢܐ ܡܢܤܝܢ ܐܢܬܘܢ ܠܝ ܐܝܬܘ ܠܝ ܕܝܢܪܐ ܐܚܙܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 16, 'ܘܐܝܬܝܘ ܠܗ ܐܡܪ ܠܗܘܢ ܕܡܢܘ ܨܠܡܐ ܗܢܐ ܘܟܬܒܐ ܗܢܘܢ ܕܝܢ ܐܡܪܘ ܕܩܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 17, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܕܩܤܪ ܗܒܘ ܠܩܤܪ ܘܕܐܠܗܐ ܠܐܠܗܐ ܘܬܡܗܘ ܗܘܘ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 18, 'ܘܐܬܘ ܙܕܘܩܝܐ ܠܘܬܗ ܗܢܘܢ ܕܐܡܪܝܢ ܕܩܝܡܬܐ ܠܝܬ ܘܡܫܐܠܝܢ ܗܘܘ ܠܗ ܘܐܡܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 19, 'ܡܠܦܢܐ ܡܘܫܐ ܟܬܒ ܠܢ ܕܐܢ ܡܐܬ ܐܚܘܗܝ ܕܐܢܫ ܘܫܒܩ ܐܢܬܬܐ ܘܒܢܝܐ ܠܐ ܫܒܩ ܢܤܒ ܐܚܘܗܝ ܐܢܬܬܗ ܘܢܩܝܡ ܙܪܥܐ ܠܐܚܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 20, 'ܫܒܥܐ ܐܚܝܢ ܐܝܬ ܗܘܘ ܩܕܡܝܐ ܢܤܒ ܐܢܬܬܐ ܘܡܝܬ ܘܠܐ ܫܒܩ ܙܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 21, 'ܘܕܬܪܝܢ ܢܤܒܗ ܘܡܝܬ ܟܕ ܐܦ ܠܐ ܗܘ ܫܒܩ ܙܪܥܐ ܘܕܬܠܬܐ ܗܟܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 22, 'ܘܫܒܥܬܝܗܘܢ ܢܤܒܘܗ ܘܠܐ ܫܒܩܘ ܙܪܥܐ ܐܚܪܝܬ ܟܠܗܘܢ ܡܝܬܬ ܐܦ ܗܝ ܐܢܬܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 23, 'ܒܩܝܡܬܐ ܗܟܝܠ ܕܐܝܢܐ ܡܢܗܘܢ ܬܗܘܐ ܐܢܬܬܐ ܫܒܥܬܝܗܘܢ ܓܝܪ ܢܤܒܘܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 24, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܠܐ ܗܘܐ ܡܛܠ ܗܕܐ ܛܥܝܢ ܐܢܬܘܢ ܕܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܟܬܒܐ ܘܠܐ ܚܝܠܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 25, 'ܡܐ ܓܝܪ ܕܩܡܘ ܡܢ ܡܝܬܐ ܠܐ ܢܤܒܝܢ ܢܫܐ ܐܦ ܠܐ ܢܫܐ ܗܘܝܢ ܠܓܒܪܐ ܐܠܐ ܐܝܬܝܗܘܢ ܐܝܟ ܡܠܐܟܐ ܕܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 26, 'ܥܠ ܡܝܬܐ ܕܝܢ ܕܩܝܡܝܢ ܠܐ ܩܪܝܬܘܢ ܒܟܬܒܐ ܕܡܘܫܐ ܐܝܟܢܐ ܡܢ ܤܢܝܐ ܐܡܪ ܠܗ ܐܠܗܐ ܐܢܐ ܐܢܐ ܐܠܗܗ ܕܐܒܪܗܡ ܘܐܠܗܗ ܕܐܝܤܚܩ ܘܐܠܗܗ ܕܝܥܩܘܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 27, 'ܘܠܐ ܗܘܐ ܐܠܗܐ ܕܡܝܬܐ ܐܠܐ ܕܚܝܐ ܐܢܬܘܢ ܗܟܝܠ ܤܓܝ ܛܥܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 28, 'ܘܩܪܒ ܚܕ ܡܢ ܤܦܪܐ ܘܫܡܥ ܐܢܘܢ ܕܕܪܫܝܢ ܘܚܙܐ ܕܫܦܝܪ ܐܬܝܒ ܠܗܘܢ ܦܬܓܡܐ ܘܫܐܠܗ ܐܝܢܘ ܦܘܩܕܢܐ ܩܕܡܝܐ ܕܟܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 29, 'ܐܡܪ ܠܗ ܝܫܘܥ ܩܕܡܝ ܡܢ ܟܠܗܘܢ ܦܘܩܕܢܐ ܫܡܥ ܐܝܤܪܝܠ ܡܪܝܐ ܐܠܗܢ ܡܪܝܐ ܚܕ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 30, 'ܘܕܬܪܚܡ ܠܡܪܝܐ ܐܠܗܟ ܡܢ ܟܠܗ ܠܒܟ ܘܡܢ ܟܠܗ ܢܦܫܟ ܘܡܢ ܟܠܗ ܪܥܝܢܟ ܘܡܢ ܟܠܗ ܚܝܠܟ ܗܢܘ ܦܘܩܕܢܐ ܩܕܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 31, 'ܘܕܬܪܝܢ ܕܕܡܐ ܠܗ ܕܬܚܒ ܠܩܪܝܒܟ ܐܝܟ ܢܦܫܟ ܦܘܩܕܢܐ ܐܚܪܢܐ ܕܪܒ ܡܢ ܗܠܝܢ ܠܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 32, 'ܐܡܪ ܠܗ ܗܘ ܤܦܪܐ ܫܦܝܪ ܪܒܝ ܒܫܪܪܐ ܐܡܪܬ ܕܚܕ ܗܘ ܘܠܝܬ ܐܚܪܝܢ ܠܒܪ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 33, 'ܘܕܢܪܚܡܝܘܗܝ ܐܢܫ ܡܢ ܟܠܗ ܠܒܐ ܘܡܢ ܟܠܗ ܪܥܝܢܐ ܘܡܢ ܟܠܗ ܢܦܫܐ ܘܡܢ ܟܠܗ ܚܝܠܐ ܘܕܢܪܚܡ ܩܪܝܒܗ ܐܝܟ ܢܦܫܗ ܝܬܝܪܐ ܗܝ ܡܢ ܟܠܗܘܢ ܝܩܕܐ ܘܕܒܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 34, 'ܝܫܘܥ ܕܝܢ ܚܙܝܗܝ ܕܚܟܝܡܐܝܬ ܡܦܢܐ ܦܬܓܡܐ ܥܢܐ ܘܐܡܪ ܠܗ ܠܐ ܗܘܝܬ ܪܚܝܩ ܡܢ ܡܠܟܘܬܐ ܕܐܠܗܐ ܘܠܐ ܐܢܫ ܬܘܒ ܐܡܪܚ ܕܢܫܐܠܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 35, 'ܘܥܢܐ ܝܫܘܥ ܘܐܡܪ ܟܕ ܡܠܦ ܒܗܝܟܠܐ ܐܝܟܢܐ ܐܡܪܝܢ ܤܦܪܐ ܕܡܫܝܚܐ ܒܪܗ ܗܘ ܕܕܘܝܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 36, 'ܗܘ ܓܝܪ ܕܘܝܕ ܐܡܪ ܒܪܘܚܐ ܕܩܘܕܫܐ ܕܐܡܪ ܡܪܝܐ ܠܡܪܝ ܬܒ ܠܟ ܡܢ ܝܡܝܢܝ ܥܕܡܐ ܕܐܤܝܡ ܒܥܠܕܒܒܝܟ ܟܘܒܫܐ ܬܚܝܬ ܪܓܠܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 37, 'ܗܘ ܗܟܝܠ ܕܘܝܕ ܩܪܐ ܠܗ ܡܪܝ ܘܐܝܟܢܐ ܒܪܗ ܐܝܬܘܗܝ ܘܟܠܗ ܟܢܫܐ ܫܡܥ ܗܘܐ ܠܗ ܒܤܝܡܐܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 38, 'ܘܒܝܘܠܦܢܗ ܐܡܪ ܗܘܐ ܠܗܘܢ ܐܙܕܗܪܘ ܡܢ ܤܦܪܐ ܕܨܒܝܢ ܕܒܐܤܛܠܐ ܢܗܠܟܘܢ ܘܪܚܡܝܢ ܫܠܡܐ ܒܫܘܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 39, 'ܘܪܝܫ ܡܘܬܒܐ ܒܟܢܘܫܬܐ ܘܪܝܫ ܤܡܟܐ ܒܚܫܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 40, 'ܗܢܘܢ ܕܐܟܠܝܢ ܒܬܐ ܕܐܪܡܠܬܐ ܒܥܠܬܐ ܕܡܘܪܟܝܢ ܨܠܘܬܗܘܢ ܗܢܘܢ ܢܩܒܠܘܢ ܕܝܢܐ ܝܬܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 41, 'ܘܟܕ ܝܬܒ ܝܫܘܥ ܠܘܩܒܠ ܒܝܬ ܓܙܐ ܚܐܪ ܗܘܐ ܐܝܟܢܐ ܟܢܫܐ ܪܡܝܢ ܥܘܪܦܢܐ ܒܝܬ ܓܙܐ ܘܤܓܝܐܐ ܥܬܝܪܐ ܪܡܝܢ ܗܘܘ ܤܓܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 42, 'ܘܐܬܬ ܐܪܡܠܬܐ ܚܕܐ ܡܤܟܢܬܐ ܐܪܡܝܬ ܬܪܝܢ ܡܢܝܢ ܕܐܝܬܝܗܘܢ ܫܡܘܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 43, 'ܘܩܪܐ ܝܫܘܥ ܠܬܠܡܝܕܘܗܝ ܘܐܡܪ ܠܗܘܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܗܕܐ ܐܪܡܠܬܐ ܡܤܟܢܬܐ ܝܬܝܪ ܡܢ ܟܠܗܘܢ ܐܢܫܐ ܕܪܡܝܢ ܐܪܡܝܬ ܒܝܬ ܓܙܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 44, 'ܟܠܗܘܢ ܓܝܪ ܡܢ ܡܕܡ ܕܝܬܝܪ ܠܗܘܢ ܐܪܡܝܘ ܗܕܐ ܕܝܢ ܡܢ ܚܤܝܪܘܬܗ ܟܠ ܡܕܡ ܕܐܝܬ ܗܘܐ ܠܗ ܐܪܡܝܬܗ ܟܠܗ ܩܢܝܢܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 1, 'ܘܟܕ ܢܦܩ ܝܫܘܥ ܡܢ ܗܝܟܠܐ ܐܡܪ ܠܗ ܚܕ ܡܢ ܬܠܡܝܕܘܗܝ ܡܠܦܢܐ ܗܐ ܚܙܝ ܐܝܠܝܢ ܟܐܦܐ ܘܐܝܠܝܢ ܒܢܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 2, 'ܝܫܘܥ ܕܝܢ ܐܡܪ ܠܗ ܚܙܐ ܐܢܬ ܗܠܝܢ ܒܢܝܢܐ ܪܘܪܒܐ ܠܐ ܡܫܬܒܩܐ ܗܪܟܐ ܟܐܦ ܥܠ ܟܐܦ ܕܠܐ ܡܤܬܬܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 3, 'ܘܟܕ ܝܬܒ ܝܫܘܥ ܒܛܘܪܐ ܕܙܝܬܐ ܠܘܩܒܠ ܗܝܟܠܐ ܫܐܠܘܗܝ ܟܐܦܐ ܘܝܥܩܘܒ ܘܝܘܚܢܢ ܘܐܢܕܪܐܘܤ ܒܠܚܘܕܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 4, 'ܐܡܪ ܠܢ ܐܡܬܝ ܗܠܝܢ ܢܗܘܝܢ ܘܡܢܐ ܐܬܐ ܡܐ ܕܩܪܝܒܢ ܗܠܝܢ ܟܠܗܝܢ ܠܡܫܬܠܡܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 5, 'ܗܘ ܕܝܢ ܝܫܘܥ ܫܪܝ ܠܡܐܡܪ ܠܗܘܢ ܚܙܘ ܕܠܡܐ ܐܢܫ ܢܛܥܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 6, 'ܤܓܝܐܐ ܓܝܪ ܢܐܬܘܢ ܒܫܡܝ ܘܢܐܡܪܘܢ ܕܐܢܐ ܐܢܐ ܘܠܤܓܝܐܐ ܢܛܥܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 7, 'ܡܐ ܕܝܢ ܕܫܡܥܬܘܢ ܩܪܒܐ ܘܛܒܐ ܕܩܐܪܤܐ ܠܐ ܬܕܚܠܘܢ ܥܬܝܕ ܗܘ ܕܢܗܘܐ ܐܠܐ ܠܐ ܥܕܟܝܠ ܚܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 8, 'ܢܩܘܡ ܓܝܪ ܥܡܐ ܥܠ ܥܡܐ ܘܡܠܟܘ ܥܠ ܡܠܟܘ ܘܢܗܘܘܢ ܙܘܥܐ ܒܕܘܟܐ ܕܘܟܐ ܘܢܗܘܘܢ ܟܦܢܐ ܘܫܓܘܫܝܐ ܗܠܝܢ ܪܫܐ ܐܢܝܢ ܕܚܒܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 9, 'ܚܙܘ ܕܝܢ ܐܢܬܘܢ ܢܦܫܟܘܢ ܢܫܠܡܘܢܟܘܢ ܓܝܪ ܠܕܝܢܐ ܘܒܟܢܘܫܬܗܘܢ ܬܬܢܓܕܘܢ ܘܩܕܡ ܡܠܟܐ ܘܗܓܡܘܢܐ ܬܩܘܡܘܢ ܡܛܠܬܝ ܠܤܗܕܘܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 10, 'ܠܘܩܕܡ ܕܝܢ ܥܬܝܕܐ ܕܬܬܟܪܙ ܤܒܪܬܝ ܒܟܠܗܘܢ ܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 11, 'ܡܐ ܕܡܩܪܒܝܢ ܠܟܘܢ ܕܝܢ ܕܢܫܠܡܘܢܟܘܢ ܠܐ ܬܩܕܡܘܢ ܬܐܨܦܘܢ ܡܢܐ ܬܡܠܠܘܢ ܘܠܐ ܬܪܢܘܢ ܐܠܐ ܡܕܡ ܕܡܬܝܗܒ ܠܟܘܢ ܒܗܝ ܫܥܬܐ ܗܘ ܡܠܠܘ ܠܐ ܗܘܐ ܓܝܪ ܐܢܬܘܢ ܡܡܠܠܝܢ ܐܠܐ ܪܘܚܐ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 12, 'ܢܫܠܡ ܕܝܢ ܐܚܐ ܠܐܚܘܗܝ ܠܡܘܬܐ ܘܐܒܐ ܠܒܪܗ ܘܢܩܘܡܘܢ ܒܢܝܐ ܥܠ ܐܒܗܝܗܘܢ ܘܢܡܝܬܘܢ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 13, 'ܘܬܗܘܘܢ ܤܢܝܐܝܢ ܡܢ ܟܠܢܫ ܡܛܠ ܫܡܝ ܡܢ ܕܝܢ ܕܢܤܝܒܪ ܥܕܡܐ ܠܚܪܬܐ ܗܘ ܢܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 14, 'ܡܐ ܕܝܢ ܕܚܙܝܬܘܢ ܐܬܐ ܛܢܦܬܐ ܕܚܘܪܒܐ ܗܝ ܕܐܡܝܪܐ ܒܕܢܝܐܝܠ ܢܒܝܐ ܕܩܝܡܐ ܐܝܟܐ ܕܠܐ ܘܠܐ ܗܘ ܕܩܪܐ ܢܤܬܟܠ ܗܝܕܝܢ ܐܝܠܝܢ ܕܒܝܗܘܕ ܐܢܘܢ ܢܥܪܩܘܢ ܠܛܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 15, 'ܘܡܢ ܕܒܐܓܪܐ ܗܘ ܠܐ ܢܚܘܬ ܘܠܐ ܢܥܘܠ ܠܡܫܩܠ ܡܕܡ ܡܢ ܒܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 16, 'ܘܡܢ ܕܒܚܩܠܐ ܗܘ ܠܐ ܢܬܗܦܟ ܠܒܤܬܪܗ ܕܢܫܩܘܠ ܠܒܫܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 17, 'ܘܝ ܕܝܢ ܠܒܛܢܬܐ ܘܠܐܝܠܝܢ ܕܡܝܢܩܢ ܒܗܢܘܢ ܝܘܡܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 18, 'ܨܠܘ ܕܝܢ ܕܠܐ ܢܗܘܐ ܥܪܘܩܝܟܘܢ ܒܤܬܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 19, 'ܢܗܘܐ ܓܝܪ ܒܝܘܡܬܐ ܗܢܘܢ ܐܘܠܨܢܐ ܕܠܐ ܗܘܐ ܐܟܘܬܗ ܡܢ ܪܝܫ ܒܪܝܬܐ ܕܒܪܐ ܐܠܗܐ ܥܕܡܐ ܠܗܫܐ ܘܠܐ ܢܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 20, 'ܘܐܠܘ ܠܐ ܡܪܝܐ ܕܟܪܝ ܝܘܡܬܐ ܗܢܘܢ ܠܐ ܚܝܐ ܗܘܐ ܟܠ ܒܤܪ ܐܠܐ ܡܛܠ ܓܒܝܐ ܕܓܒܐ ܟܪܝ ܝܘܡܬܐ ܗܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 21, 'ܗܝܕܝܢ ܐܢ ܐܢܫ ܢܐܡܪ ܠܟܘܢ ܕܗܐ ܗܪܟܐ ܗܘ ܡܫܝܚܐ ܘܗܐ ܗܪܬܡܢ ܠܐ ܬܗܝܡܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 22, 'ܢܩܘܡܘܢ ܓܝܪ ܡܫܝܚܐ ܕܕܓܠܘܬܐ ܘܢܒܝܐ ܕܟܕܒܘܬܐ ܘܢܬܠܘܢ ܐܬܘܬܐ ܘܬܕܡܪܬܐ ܘܢܛܥܘܢ ܐܢ ܡܫܟܚܐ ܐܦ ܠܓܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 23, 'ܐܢܬܘܢ ܕܝܢ ܐܙܕܗܪܘ ܗܐ ܩܕܡܬ ܐܡܪܬ ܠܟܘܢ ܟܠ ܡܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 24, 'ܒܗܢܘܢ ܕܝܢ ܝܘܡܬܐ ܒܬܪ ܐܘܠܨܢܐ ܗܘ ܫܡܫܐ ܢܚܫܟ ܘܤܗܪܐ ܠܐ ܢܬܠ ܢܘܗܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 25, 'ܘܟܘܟܒܐ ܢܦܠܘܢ ܡܢ ܫܡܝܐ ܘܚܝܠܘܬܐ ܕܫܡܝܐ ܢܬܬܙܝܥܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 26, 'ܘܗܝܕܝܢ ܢܚܙܘܢܝܗܝ ܠܒܪܗ ܕܐܢܫܐ ܟܕ ܐܬܐ ܒܥܢܢܐ ܥܡ ܚܝܠܐ ܪܒܐ ܘܥܡ ܫܘܒܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 27, 'ܗܝܕܝܢ ܢܫܕܪ ܡܠܐܟܘܗܝ ܘܢܟܢܫ ܠܓܒܘܗܝ ܡܢ ܐܪܒܥܬܝܗܝܢ ܪܘܚܐ ܡܢ ܪܫܗ ܕܐܪܥܐ ܘܥܕܡܐ ܠܪܫܗ ܕܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 28, 'ܡܢ ܬܬܐ ܕܝܢ ܝܠܦܘ ܦܠܐܬܐ ܕܡܐ ܕܪܟ ܤܘܟܝܗ ܘܦܪܥܘ ܛܪܦܝܗ ܝܕܥܝܢ ܐܢܬܘܢ ܕܡܛܐ ܩܝܛܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 29, 'ܗܟܢܐ ܐܦ ܐܢܬܘܢ ܡܐ ܕܚܙܝܬܘܢ ܗܠܝܢ ܕܗܘܝܢ ܕܥܘ ܕܩܪܝܒܐ ܗܝ ܥܠ ܬܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 30, 'ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܠܐ ܬܥܒܪ ܫܪܒܬܐ ܗܕܐ ܥܕܡܐ ܕܗܠܝܢ ܟܠܗܝܢ ܢܗܘܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 31, 'ܫܡܝܐ ܘܐܪܥܐ ܢܥܒܪܘܢ ܘܡܠܝ ܠܐ ܢܥܒܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 32, 'ܥܠ ܕܝܢ ܝܘܡܐ ܗܘ ܘܥܠ ܫܥܬܐ ܗܝ ܐܢܫ ܠܐ ܝܕܥ ܐܦܠܐ ܡܠܐܟܐ ܕܫܡܝܐ ܘܠܐ ܒܪܐ ܐܠܐ ܐܢ ܐܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 33, 'ܚܙܘ ܐܬܬܥܝܪܘ ܘܨܠܘ ܠܐ ܓܝܪ ܝܕܥܝܢ ܐܢܬܘܢ ܐܡܬܝ ܗܘ ܙܒܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 34, 'ܐܝܟ ܓܒܪܐ ܗܘ ܓܝܪ ܕܚܙܩ ܘܫܒܩ ܒܝܬܗ ܘܝܗܒ ܫܘܠܛܢܐ ܠܥܒܕܘܗܝ ܘܠܐܢܫ ܐܢܫ ܥܒܕܗ ܘܠܬܪܥܐ ܦܩܕ ܕܢܗܘܐ ܥܝܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 35, 'ܐܬܬܥܝܪܘ ܗܟܝܠ ܕܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܐܡܬܝ ܐܬܐ ܡܪܗ ܕܒܝܬܐ ܒܪܡܫܐ ܐܘ ܒܦܠܓܗ ܕܠܠܝܐ ܐܘ ܒܡܩܪܐ ܬܪܢܓܠܐ ܐܘ ܒܨܦܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 36, 'ܕܠܡܐ ܢܐܬܐ ܡܢ ܫܠܝܐ ܘܢܫܟܚܟܘܢ ܟܕ ܕܡܟܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 37, 'ܡܕܡ ܕܠܟܘܢ ܕܝܢ ܐܡܪ ܐܢܐ ܠܟܠܟܘܢ ܗܘ ܐܡܪ ܐܢܐ ܗܘܝܬܘܢ ܥܝܪܝܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 1, 'ܒܬܪ ܕܝܢ ܬܪܝܢ ܝܘܡܝܢ ܗܘܐ ܗܘܐ ܦܨܚܐ ܕܦܛܝܪܐ ܘܒܥܝܢ ܗܘܘ ܪܒܝ ܟܗܢܐ ܘܤܦܪܐ ܐܝܟܢܐ ܒܢܟܠܐ ܢܐܚܕܘܢ ܘܢܩܛܠܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 2, 'ܘܐܡܪܝܢ ܗܘܘ ܠܐ ܒܥܕܥܕܐ ܕܠܡܐ ܢܗܘܐ ܫܓܘܫܝܐ ܒܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 3, 'ܘܟܕ ܗܘ ܐܝܬܘܗܝ ܒܒܝܬ ܥܢܝܐ ܒܒܝܬܗ ܕܫܡܥܘܢ ܓܪܒܐ ܟܕ ܤܡܝܟ ܐܬܬ ܐܢܬܬܐ ܕܐܝܬ ܥܠܝܗ ܫܛܝܦܬܐ ܕܒܤܡܐ ܕܢܪܕܝܢ ܪܫܝܐ ܤܓܝ ܕܡܝܐ ܘܦܬܚܬܗ ܘܐܫܦܥܬܗ ܥܠ ܪܫܗ ܕܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 4, 'ܐܝܬ ܗܘܘ ܕܝܢ ܐܢܫܐ ܡܢ ܬܠܡܝܕܐ ܕܐܬܒܐܫ ܠܗܘܢ ܒܢܦܫܗܘܢ ܘܐܡܪܘ ܕܠܡܢܐ ܗܘܐ ܐܒܕܢܐ ܕܗܢܐ ܒܤܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 5, 'ܡܫܟܚ ܗܘܐ ܓܝܪ ܠܡܙܕܒܢܘ ܝܬܝܪ ܡܢ ܬܠܬܡܐܐ ܕܝܢܪܝܢ ܘܠܡܬܝܗܒܘ ܠܡܤܟܢܐ ܘܡܙܕܥܦܝܢ ܗܘܘ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 6, 'ܗܘ ܕܝܢ ܝܫܘܥ ܐܡܪ ܫܘܒܩܘܗ ܡܢܐ ܡܗܪܝܢ ܐܢܬܘܢ ܠܗ ܥܒܕܐ ܫܦܝܪܐ ܥܒܕܬ ܠܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 7, 'ܒܟܠܙܒܢ ܓܝܪ ܡܤܟܢܐ ܐܝܬ ܠܟܘܢ ܥܡܟܘܢ ܘܐܡܬܝ ܕܨܒܝܢ ܐܢܬܘܢ ܡܫܟܚܝܢ ܐܢܬܘܢ ܕܬܥܒܕܘܢ ܠܗܘܢ ܕܫܦܝܪ ܐܢܐ ܕܝܢ ܠܐ ܒܟܠܙܒܢ ܐܝܬܝ ܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 8, 'ܗܘ ܕܐܝܬ ܗܘܐ ܠܗ ܠܗܕܐ ܥܒܕܬ ܘܩܕܡܬ ܐܝܟ ܕܠܩܒܘܪܬܐ ܒܤܡܬ ܓܘܫܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 9, 'ܘܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܟܠ ܐܝܟܐ ܕܬܬܟܪܙ ܤܒܪܬܝ ܗܕܐ ܒܟܠܗ ܥܠܡܐ ܘܐܦ ܡܕܡ ܕܥܒܕܬ ܗܕܐ ܢܬܡܠܠ ܠܕܘܟܪܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 10, 'ܝܗܘܕܐ ܕܝܢ ܤܟܪܝܘܛܐ ܚܕ ܡܢ ܬܪܥܤܪ ܐܙܠ ܠܘܬ ܪܒܝ ܟܗܢܐ ܐܝܟ ܕܢܫܠܡܝܘܗܝ ܠܗܘܢ ܠܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 11, 'ܗܢܘܢ ܕܝܢ ܟܕ ܫܡܥܘ ܚܕܝܘ ܘܐܫܬܘܕܝܘ ܟܤܦܐ ܕܢܬܠܘܢ ܠܗ ܘܒܥܐ ܗܘܐ ܠܗ ܦܠܥܐ ܕܢܫܠܡܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 12, 'ܘܒܝܘܡܐ ܩܕܡܝܐ ܕܦܛܝܪܐ ܕܒܗ ܕܒܚܝܢ ܝܗܘܕܝܐ ܦܨܚܐ ܐܡܪܝܢ ܠܗ ܬܠܡܝܕܘܗܝ ܐܝܟܐ ܨܒܐ ܐܢܬ ܕܢܐܙܠ ܢܛܝܒ ܠܟ ܕܬܐܟܘܠ ܦܨܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 13, 'ܘܫܕܪ ܬܪܝܢ ܡܢ ܬܠܡܝܕܘܗܝ ܘܐܡܪ ܠܗܘܢ ܙܠܘ ܠܡܕܝܢܬܐ ܘܗܐ ܦܓܥ ܒܟܘܢ ܓܒܪܐ ܕܫܩܝܠ ܡܐܢܐ ܕܡܝܐ ܙܠܘ ܒܬܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 14, 'ܘܠܐܝܟܐ ܕܥܐܠ ܐܡܪܘ ܠܡܪܐ ܒܝܬܐ ܪܒܢ ܐܡܪ ܐܝܟܘ ܒܝܬ ܡܫܪܝܐ ܐܝܟܐ ܕܐܟܘܠ ܥܡ ܬܠܡܝܕܝ ܦܨܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 15, 'ܘܗܐ ܡܚܘܐ ܠܟܘܢ ܥܠܝܬܐ ܪܒܬܐ ܕܡܫܘܝܐ ܘܡܛܝܒܐ ܬܡܢ ܬܩܢܘ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 16, 'ܘܢܦܩܘ ܬܠܡܝܕܘܗܝ ܘܐܬܘ ܠܡܕܝܢܬܐ ܘܐܫܟܚܘ ܐܝܟܢܐ ܕܐܡܪ ܠܗܘܢ ܘܛܝܒܘ ܦܨܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 17, 'ܘܟܕ ܗܘܐ ܪܡܫܐ ܐܬܐ ܥܡ ܬܪܥܤܪܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 18, 'ܘܟܕ ܤܡܝܟܝܢ ܘܠܥܤܝܢ ܐܡܪ ܝܫܘܥ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܚܕ ܡܢܟܘܢ ܕܐܟܠ ܥܡܝ ܗܘ ܢܫܠܡܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 19, 'ܗܢܘܢ ܕܝܢ ܫܪܝܘ ܡܬܬܥܝܩܝܢ ܘܐܡܪܝܢ ܠܗ ܚܕ ܚܕ ܠܡܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 20, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܚܕ ܡܢ ܬܪܥܤܪ ܕܨܒܥ ܥܡܝ ܒܠܓܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 21, 'ܘܒܪܗ ܕܐܢܫܐ ܐܙܠ ܐܝܟܢܐ ܕܟܬܝܒ ܥܠܘܗܝ ܘܝ ܕܝܢ ܠܓܒܪܐ ܗܘ ܕܒܐܝܕܗ ܡܫܬܠܡ ܒܪܗ ܕܐܢܫܐ ܦܩܚ ܗܘܐ ܠܗ ܠܓܒܪܐ ܗܘ ܐܠܘ ܠܐ ܐܬܝܠܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 22, 'ܘܟܕ ܗܢܘܢ ܠܥܤܝܢ ܢܤܒ ܝܫܘܥ ܠܚܡܐ ܘܒܪܟ ܘܩܨܐ ܘܝܗܒ ܠܗܘܢ ܘܐܡܪ ܠܗܘܢ ܤܒܘ ܗܢܐ ܐܝܬܘܗܝ ܦܓܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 23, 'ܘܢܤܒ ܟܤܐ ܘܐܘܕܝ ܘܒܪܟ ܘܝܗܒ ܠܗܘܢ ܘܐܫܬܝܘ ܡܢܗ ܟܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 24, 'ܘܐܡܪ ܠܗܘܢ ܗܢܘ ܕܡܝ ܕܕܝܬܩܐ ܚܕܬܐ ܕܚܠܦ ܤܓܝܐܐ ܡܬܐܫܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 25, 'ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܬܘܒ ܠܐ ܐܫܬܐ ܡܢ ܝܠܕܐ ܕܓܦܬܐ ܥܕܡܐ ܠܝܘܡܐ ܗܘ ܕܒܗ ܐܫܬܝܘܗܝ ܚܕܬܐܝܬ ܒܡܠܟܘܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 26, 'ܘܫܒܚܘ ܘܢܦܩܘ ܠܛܘܪ ܙܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 27, 'ܘܐܡܪ ܠܗܘܢ ܝܫܘܥ ܕܟܠܟܘܢ ܬܬܟܫܠܘܢ ܒܝ ܒܗܢܐ ܠܠܝܐ ܟܬܝܒ ܓܝܪ ܕܐܡܚܐ ܠܪܥܝܐ ܘܢܬܒܕܪܘܢ ܐܡܪܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 28, 'ܐܠܐ ܡܐ ܕܩܡܬ ܩܕܡ ܐܢܐ ܠܟܘܢ ܠܓܠܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 29, 'ܐܡܪ ܠܗ ܟܐܦܐ ܐܢ ܟܠܗܘܢ ܢܬܟܫܠܘܢ ܐܠܐ ܠܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 30, 'ܐܡܪ ܠܗ ܝܫܘܥ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟ ܕܐܢܬ ܝܘܡܢܐ ܒܠܠܝܐ ܗܢܐ ܩܕܡ ܕܢܩܪܐ ܬܪܢܓܠܐ ܬܪܬܝܢ ܙܒܢܝܢ ܬܠܬ ܬܟܦܘܪ ܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 31, 'ܗܘ ܕܝܢ ܝܬܝܪܐܝܬ ܐܡܪ ܗܘܐ ܕܐܢ ܢܗܘܐ ܠܝ ܠܡܡܬ ܥܡܟ ܠܐ ܐܟܦܘܪ ܒܟ ܡܪܝ ܘܐܟܘܬܗ ܐܦ ܟܠܗܘܢ ܐܡܪܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 32, 'ܘܐܬܘ ܠܕܘܟܬܐ ܐܝܕܐ ܕܡܬܩܪܝܐ ܓܕܤܡܢ ܘܐܡܪ ܠܬܠܡܝܕܘܗܝ ܬܒܘ ܗܪܟܐ ܥܕ ܡܨܠܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 33, 'ܘܕܒܪ ܥܡܗ ܠܟܐܦܐ ܘܠܝܥܩܘܒ ܘܠܝܘܚܢܢ ܘܫܪܝ ܠܡܬܟܡܪܘ ܘܠܡܬܬܥܩܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 34, 'ܘܐܡܪ ܠܗܘܢ ܟܪܝܐ ܗܝ ܠܗ ܠܢܦܫܝ ܥܕܡܐ ܠܡܘܬܐ ܩܘܘ ܗܪܟܐ ܘܐܬܬܥܝܪܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 35, 'ܘܩܪܒ ܩܠܝܠ ܘܢܦܠ ܥܠ ܐܪܥܐ ܘܡܨܠܐ ܗܘܐ ܕܐܢ ܡܫܟܚܐ ܬܥܒܪ ܡܢܗ ܫܥܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 36, 'ܘܐܡܪ ܐܒܐ ܐܒܝ ܟܠ ܡܕܡ ܡܫܟܚ ܐܢܬ ܐܥܒܪ ܡܢܝ ܟܤܐ ܗܢܐ ܐܠܐ ܠܐ ܨܒܝܢܝ ܕܝܠܝ ܐܠܐ ܕܝܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 37, 'ܘܐܬܐ ܐܫܟܚ ܐܢܘܢ ܟܕ ܕܡܟܝܢ ܘܐܡܪ ܠܟܐܦܐ ܫܡܥܘܢ ܕܡܟܬ ܠܟ ܠܐ ܐܫܟܚܬ ܚܕܐ ܫܥܐ ܠܡܬܬܥܪܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 38, 'ܐܬܬܥܝܪܘ ܘܨܠܘ ܕܠܐ ܬܥܠܘܢ ܠܢܤܝܘܢܐ ܪܘܚܐ ܨܒܝܐ ܘܡܛܝܒܐ ܐܠܐ ܦܓܪܐ ܟܪܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 39, 'ܘܐܙܠ ܬܘܒ ܨܠܝ ܘܗܝ ܡܠܬܐ ܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 40, 'ܘܗܦܟ ܐܬܐ ܬܘܒ ܐܫܟܚ ܐܢܘܢ ܟܕ ܕܡܟܝܢ ܡܛܠ ܕܥܝܢܝܗܘܢ ܝܩܝܪܢ ܗܘܝ ܘܠܐ ܝܕܥܝܢ ܗܘܘ ܡܢܐ ܢܐܡܪܘܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 41, 'ܘܐܬܐ ܕܬܠܬ ܙܒܢܝܢ ܘܐܡܪ ܠܗܘܢ ܕܡܟܘ ܡܟܝܠ ܘܐܬܬܢܝܚܘ ܡܛܬ ܚܪܬܐ ܘܐܬܬ ܫܥܬܐ ܘܗܐ ܡܫܬܠܡ ܒܪܗ ܕܐܢܫܐ ܒܐܝܕܝܗܘܢ ܕܚܛܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 42, 'ܩܘܡܘ ܢܐܙܠ ܗܐ ܩܪܒ ܗܘ ܕܡܫܠܡ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 43, 'ܘܥܕ ܗܘ ܡܡܠܠ ܐܬܐ ܝܗܘܕܐ ܤܟܪܝܘܛܐ ܚܕ ܡܢ ܬܪܥܤܪ ܘܥܡܐ ܤܓܝܐܐ ܥܡ ܤܦܤܪܐ ܘܚܘܛܪܐ ܡܢ ܠܘܬ ܪܒܝ ܟܗܢܐ ܘܤܦܪܐ ܘܩܫܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 44, 'ܘܝܗܒ ܠܗܘܢ ܐܬܐ ܡܫܠܡܢܐ ܗܘ ܕܡܫܠܡ ܘܐܡܪ ܗܘ ܕܢܫܩ ܐܢܐ ܗܘܝܘ ܐܘܚܕܘܗܝ ܙܗܝܪܐܝܬ ܘܐܘܒܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 45, 'ܘܒܪ ܫܥܬܗ ܩܪܒ ܘܐܡܪ ܠܗ ܪܒܝ ܪܒܝ ܘܢܫܩܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 46, 'ܗܢܘܢ ܕܝܢ ܐܪܡܝܘ ܥܠܘܗܝ ܐܝܕܝܐ ܘܐܚܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 47, 'ܚܕ ܕܝܢ ܡܢ ܗܢܘܢ ܕܩܝܡܝܢ ܫܡܛ ܤܝܦܐ ܘܡܚܝܗܝ ܠܥܒܕܗ ܕܪܒ ܟܗܢܐ ܘܫܩܠܗ ܐܕܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 48, 'ܗܘ ܕܝܢ ܝܫܘܥ ܥܢܐ ܘܐܡܪ ܠܗܘܢ ܐܝܟ ܕܥܠ ܓܝܤܐ ܢܦܩܬܘܢ ܒܤܝܦܐ ܘܒܚܘܛܪܐ ܕܬܐܚܕܘܢܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 49, 'ܟܠܝܘܡ ܠܘܬܟܘܢ ܗܘܝܬ ܟܕ ܡܠܦ ܐܢܐ ܒܗܝܟܠܐ ܘܠܐ ܐܚܕܬܘܢܢܝ ܐܠܐ ܕܢܫܠܡܘܢ ܟܬܒܐ ܗܘܬ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 50, 'ܗܝܕܝܢ ܫܒܩܘܗܝ ܬܠܡܝܕܘܗܝ ܘܥܪܩܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 51, 'ܘܥܠܝܡܐ ܚܕ ܐܬܐ ܗܘܐ ܒܬܪܗ ܘܥܛܝܦ ܤܕܘܢܐ ܥܪܛܠ ܘܐܚܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 52, 'ܗܘ ܕܝܢ ܫܒܩ ܤܕܘܢܐ ܘܥܪܩ ܥܪܛܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 53, 'ܘܐܘܒܠܘܗܝ ܠܝܫܘܥ ܠܘܬ ܩܝܦܐ ܪܒ ܟܗܢܐ ܘܐܬܟܢܫܘ ܠܘܬܗ ܟܠܗܘܢ ܪܒܝ ܟܗܢܐ ܘܤܦܪܐ ܘܩܫܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 54, 'ܫܡܥܘܢ ܕܝܢ ܡܢ ܪܘܚܩܐ ܐܬܐ ܗܘܐ ܒܬܪܗ ܥܕܡܐ ܠܓܘ ܕܪܬܐ ܕܪܒ ܟܗܢܐ ܘܝܬܒ ܗܘܐ ܥܡ ܡܫܡܫܢܐ ܘܫܚܢ ܠܘܩܒܠ ܢܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 55, 'ܪܒܝ ܟܗܢܐ ܕܝܢ ܘܟܠܗ ܟܢܫܗܘܢ ܒܥܝܢ ܗܘܘ ܥܠ ܝܫܘܥ ܤܗܕܘܬܐ ܕܢܡܝܬܘܢܝܗܝ ܘܠܐ ܐܫܟܚܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 56, 'ܟܕ ܤܓܝܐܐ ܓܝܪ ܡܤܗܕܝܢ ܗܘܘ ܥܠܘܗܝ ܠܐ ܫܘܝܢ ܗܘܝ ܤܗܕܘܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 57, 'ܐܢܫܝܢ ܕܝܢ ܩܡܘ ܥܠܘܗܝ ܤܗܕܐ ܕܫܘܩܪܐ ܘܐܡܪܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 58, 'ܕܚܢܢ ܫܡܥܢܝܗܝ ܕܐܡܪ ܕܐܢܐ ܫܪܐ ܐܢܐ ܗܝܟܠܐ ܗܢܐ ܕܥܒܝܕ ܒܐܝܕܝܐ ܘܠܬܠܬܐ ܝܘܡܝܢ ܒܢܐ ܐܢܐ ܐܚܪܢܐ ܕܠܐ ܥܒܝܕ ܒܐܝܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 59, 'ܘܐܦܠܐ ܕܝܢ ܗܟܢܐ ܫܘܝܐ ܗܘܬ ܤܗܕܘܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 60, 'ܘܩܡ ܪܒ ܟܗܢܐ ܒܡܨܥܬܐ ܘܫܐܠܗ ܠܝܫܘܥ ܘܐܡܪ ܠܐ ܡܦܢܐ ܐܢܬ ܦܬܓܡܐ ܡܢܐ ܡܤܗܕܝܢ ܥܠܝܟ ܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 61, 'ܗܘ ܕܝܢ ܫܬܝܩ ܗܘܐ ܘܡܕܡ ܠܐ ܥܢܝܗܝ ܘܬܘܒ ܫܐܠܗ ܪܒ ܟܗܢܐ ܘܐܡܪ ܐܢܬ ܗܘ ܡܫܝܚܐ ܒܪܗ ܕܡܒܪܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 62, 'ܗܘ ܕܝܢ ܝܫܘܥ ܐܡܪ ܠܗ ܐܢܐ ܐܢܐ ܘܬܚܙܘܢ ܠܒܪܗ ܕܐܢܫܐ ܕܝܬܒ ܡܢ ܝܡܝܢܐ ܕܚܝܠܐ ܘܐܬܐ ܥܠ ܥܢܢܝ ܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 63, 'ܪܒ ܟܗܢܐ ܕܝܢ ܨܪܐ ܟܘܬܝܢܗ ܘܐܡܪ ܡܢܐ ܡܟܝܠ ܡܬܒܥܝܢ ܠܢ ܤܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 64, 'ܗܐ ܡܢ ܦܘܡܗ ܫܡܥܬܘܢ ܓܘܕܦܐ ܡܢܐ ܡܬܚܙܐ ܠܟܘܢ ܗܢܘܢ ܕܝܢ ܟܠܗܘܢ ܕܢܘ ܕܚܝܒ ܗܘ ܡܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 65, 'ܘܫܪܝܘ ܐܢܫܝܢ ܪܩܝܢ ܒܦܪܨܘܦܗ ܘܡܚܦܝܢ ܐܦܘܗܝ ܘܡܩܦܚܝܢ ܠܗ ܘܐܡܪܝܢ ܐܬܢܒܐ ܘܕܚܫܐ ܡܚܝܢ ܗܘܘ ܠܗ ܥܠ ܦܟܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 66, 'ܘܟܕ ܫܡܥܘܢ ܠܬܚܬ ܒܕܪܬܐ ܐܬܬ ܥܠܝܡܬܐ ܚܕܐ ܕܪܒ ܟܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 67, 'ܚܙܬܗ ܕܫܚܢ ܘܚܪܬ ܒܗ ܘܐܡܪܐ ܠܗ ܘܐܦ ܐܢܬ ܥܡ ܝܫܘܥ ܗܘܝܬ ܢܨܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 68, 'ܗܘ ܕܝܢ ܟܦܪ ܘܐܡܪ ܠܐ ܝܕܥ ܐܢܐ ܡܢܐ ܐܡܪܐ ܐܢܬܝ ܘܢܦܩ ܠܒܪ ܠܤܦܐ ܘܩܪܐ ܬܪܢܓܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 69, 'ܘܚܙܬܗ ܬܘܒ ܥܠܝܡܬܐ ܗܝ ܘܫܪܝܬ ܕܬܐܡܪ ܠܐܝܠܝܢ ܕܩܝܡܝܢ ܕܐܦ ܗܢܐ ܡܢܗܘܢ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 70, 'ܗܘ ܕܝܢ ܬܘܒ ܟܦܪ ܘܒܬܪ ܩܠܝܠ ܬܘܒ ܗܢܘܢ ܕܩܝܡܝܢ ܐܡܪܘ ܠܟܐܦܐ ܫܪܝܪܐܝܬ ܡܢܗܘܢ ܐܢܬ ܐܦ ܓܝܪ ܓܠܝܠܝܐ ܐܢܬ ܘܡܡܠܠܟ ܕܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 71, 'ܗܘ ܕܝܢ ܫܪܝ ܗܘܐ ܡܚܪܡ ܘܝܡܐ ܕܠܐ ܝܕܥ ܐܢܐ ܠܓܒܪܐ ܗܢܐ ܕܐܡܪܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 72, 'ܘܒܗ ܒܫܥܬܐ ܩܪܐ ܬܪܢܓܠܐ ܕܬܪܬܝܢ ܙܒܢܝܢ ܘܐܬܕܟܪ ܫܡܥܘܢ ܡܠܬܗ ܕܝܫܘܥ ܕܐܡܪ ܗܘܐ ܠܗ ܕܩܕܡ ܕܢܩܪܐ ܬܪܢܓܠܐ ܬܪܬܝܢ ܙܒܢܝܢ ܬܠܬ ܬܟܦܘܪ ܒܝ ܘܫܪܝ ܕܢܒܟܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 1, 'ܘܡܚܕܐ ܒܨܦܪܐ ܥܒܕܘ ܡܠܟܐ ܪܒܝ ܟܗܢܐ ܥܡ ܩܫܝܫܐ ܘܥܡ ܤܦܪܐ ܘܥܡ ܟܠܗ ܟܢܘܫܬܐ ܘܐܤܪܘ ܠܝܫܘܥ ܘܐܘܒܠܘܗܝ ܘܐܫܠܡܘܗܝ ܠܦܝܠܛܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 2, 'ܘܫܐܠܗ ܦܝܠܛܘܤ ܐܢܬ ܗܘ ܡܠܟܐ ܕܝܗܘܕܝܐ ܗܘ ܕܝܢ ܥܢܐ ܘܐܡܪ ܠܗ ܐܢܬ ܐܡܪܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 3, 'ܘܐܟܠܝܢ ܗܘܘ ܩܪܨܘܗܝ ܪܒܝ ܟܗܢܐ ܒܤܓܝܐܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 4, 'ܗܘ ܕܝܢ ܦܝܠܛܘܤ ܬܘܒ ܫܐܠܗ ܘܐܡܪ ܠܗ ܠܐ ܡܦܢܐ ܐܢܬ ܦܬܓܡܐ ܚܙܝ ܟܡܐ ܡܤܗܕܝܢ ܥܠܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 5, 'ܗܘ ܕܝܢ ܝܫܘܥ ܡܕܡ ܦܬܓܡܐ ܠܐ ܝܗܒ ܐܝܟܢܐ ܕܢܬܕܡܪ ܦܝܠܛܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 6, 'ܡܥܕ ܗܘܐ ܕܝܢ ܒܟܠ ܥܐܕܐ ܠܡܫܪܐ ܠܗܘܢ ܐܤܝܪܐ ܚܕ ܐܝܢܐ ܕܫܐܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 7, 'ܘܐܝܬ ܗܘܐ ܚܕ ܕܡܬܩܪܐ ܒܪ ܐܒܐ ܕܐܤܝܪ ܗܘܐ ܥܡ ܥܒܕܝ ܐܤܛܤܝܢ ܗܢܘܢ ܕܩܛܠܐ ܒܐܤܛܤܝܢ ܥܒܕܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 8, 'ܘܩܥܘ ܥܡܐ ܘܫܪܝܘ ܠܡܫܐܠ ܐܝܟ ܕܡܥܕ ܗܘܐ ܥܒܕ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 9, 'ܗܘ ܕܝܢ ܦܝܠܛܘܤ ܥܢܐ ܘܐܡܪ ܨܒܝܢ ܐܢܬܘܢ ܐܫܪܐ ܠܟܘܢ ܡܠܟܐ ܕܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 10, 'ܝܕܥ ܗܘܐ ܓܝܪ ܦܝܠܛܘܤ ܕܡܢ ܚܤܡܐ ܐܫܠܡܘܗܝ ܪܒܝ ܟܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 11, 'ܪܒܝ ܟܗܢܐ ܕܝܢ ܝܬܝܪܐܝܬ ܚܦܛܘ ܠܟܢܫܐ ܕܠܒܪ ܐܒܐ ܢܫܪܐ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 12, 'ܗܘ ܕܝܢ ܦܝܠܛܘܤ ܐܡܪ ܠܗܘܢ ܡܢܐ ܗܟܝܠ ܨܒܝܢ ܐܢܬܘܢ ܐܥܒܕ ܠܗܢܐ ܕܩܪܝܢ ܐܢܬܘܢ ܡܠܟܐ ܕܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 13, 'ܗܢܘܢ ܕܝܢ ܬܘܒ ܩܥܘ ܙܩܘܦܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 14, 'ܗܘ ܕܝܢ ܦܝܠܛܘܤ ܐܡܪ ܠܗܘܢ ܡܢܐ ܓܝܪ ܕܒܝܫ ܥܒܕ ܘܗܢܘܢ ܝܬܝܪܐܝܬ ܩܥܝܢ ܗܘܘ ܙܩܘܦܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 15, 'ܦܝܠܛܘܤ ܕܝܢ ܨܒܐ ܕܢܥܒܕ ܨܒܝܢܐ ܠܟܢܫܐ ܘܫܪܐ ܠܗܘܢ ܠܒܪ ܐܒܐ ܘܐܫܠܡ ܠܗܘܢ ܠܝܫܘܥ ܟܕ ܡܢܓܕ ܕܢܙܕܩܦ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 16, 'ܐܤܛܪܛܝܘܛܐ ܕܝܢ ܐܘܒܠܘܗܝ ܠܓܘ ܕܪܬܐ ܕܐܝܬܝܗ ܦܪܛܘܪܝܢ ܘܩܪܘ ܠܟܠܗ ܐܤܦܝܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 17, 'ܘܐܠܒܫܘܗܝ ܐܪܓܘܢܐ ܘܓܕܠܘ ܤܡܘ ܠܗ ܟܠܝܠܐ ܕܟܘܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 18, 'ܘܫܪܝܘ ܠܡܫܐܠ ܒܫܠܡܗ ܫܠܡ ܡܠܟܐ ܕܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 19, 'ܘܡܚܝܢ ܗܘܘ ܠܗ ܥܠ ܪܫܗ ܒܩܢܝܐ ܘܪܩܝܢ ܗܘܘ ܒܐܦܘܗܝ ܘܒܪܟܝܢ ܗܘܘ ܥܠ ܒܘܪܟܝܗܘܢ ܘܤܓܕܝܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 20, 'ܘܟܕ ܒܙܚܘ ܒܗ ܐܫܠܚܘܗܝ ܐܪܓܘܢܐ ܘܐܠܒܫܘܗܝ ܡܐܢܘܗܝ ܘܐܦܩܘܗܝ ܕܢܙܩܦܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 21, 'ܘܫܚܪܘ ܚܕ ܕܥܒܪ ܗܘܐ ܫܡܥܘܢ ܩܘܪܝܢܝܐ ܕܐܬܐ ܗܘܐ ܡܢ ܩܪܝܬܐ ܐܒܘܗܝ ܕܐܠܟܤܢܕܪܘܤ ܘܕܪܘܦܘܤ ܕܢܫܩܘܠ ܙܩܝܦܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 22, 'ܘܐܝܬܝܘܗܝ ܠܓܓܘܠܬܐ ܕܘܟܬܐ ܕܡܬܦܫܩܐ ܩܪܩܦܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 23, 'ܘܝܗܒܘ ܠܗ ܠܡܫܬܐ ܚܡܪܐ ܕܚܠܝܛ ܒܗ ܡܘܪܐ ܗܘ ܕܝܢ ܠܐ ܢܤܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 24, 'ܘܟܕ ܙܩܦܘܗܝ ܦܠܓܘ ܡܐܢܘܗܝ ܘܐܪܡܝܘ ܥܠܝܗܘܢ ܦܤܐ ܡܢܘ ܡܢܐ ܢܤܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 25, 'ܐܝܬ ܗܘܐ ܕܝܢ ܫܥܐ ܬܠܬ ܟܕ ܙܩܦܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 26, 'ܘܟܬܝܒܐ ܗܘܬ ܥܠܬܐ ܕܡܘܬܗ ܒܟܬܒܐ ܗܢܐ ܗܘ ܡܠܟܐ ܕܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 27, 'ܘܙܩܦܘ ܥܡܗ ܬܪܝܢ ܠܤܛܝܐ ܚܕ ܡܢ ܝܡܝܢܗ ܘܚܕ ܡܢ ܤܡܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 28, 'ܘܫܠܡ ܟܬܒܐ ܕܐܡܪ ܕܥܡ ܥܘܠܐ ܐܬܚܫܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 29, 'ܘܐܦ ܐܝܠܝܢ ܕܝܢ ܕܥܒܪܝܢ ܗܘܘ ܡܓܕܦܝܢ ܗܘܘ ܥܠܘܗܝ ܘܡܢܝܕܝܢ ܪܫܝܗܘܢ ܘܐܡܪܝܢ ܐܘܢ ܫܪܐ ܗܝܟܠܐ ܘܒܢܐ ܠܗ ܠܬܠܬܐ ܝܘܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 30, 'ܦܨܐ ܢܦܫܟ ܘܚܘܬ ܡܢ ܙܩܝܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 31, 'ܘܗܟܢܐ ܐܦ ܪܒܝ ܟܗܢܐ ܓܚܟܝܢ ܗܘܘ ܚܕ ܥܡ ܚܕ ܘܤܦܪܐ ܘܐܡܪܝܢ ܐܚܪܢܐ ܐܚܝ ܢܦܫܗ ܠܐ ܡܫܟܚ ܠܡܚܝܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 32, 'ܡܫܝܚܐ ܡܠܟܗ ܕܐܝܤܪܝܠ ܢܚܘܬ ܗܫܐ ܡܢ ܙܩܝܦܐ ܕܢܚܙܐ ܘܢܗܝܡܢ ܒܗ ܘܐܦ ܗܢܘܢ ܕܝܢ ܕܙܩܝܦܝܢ ܗܘܘ ܥܡܗ ܡܚܤܕܝܢ ܗܘܘ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 33, 'ܘܟܕ ܗܘܝ ܫܬ ܫܥܝܢ ܗܘܐ ܚܫܘܟܐ ܥܠ ܟܠܗ ܐܪܥܐ ܥܕܡܐ ܠܫܥܐ ܬܫܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 34, 'ܘܒܬܫܥ ܫܥܝܢ ܩܥܐ ܝܫܘܥ ܒܩܠܐ ܪܡܐ ܘܐܡܪ ܐܝܠ ܐܝܠ ܠܡܢܐ ܫܒܩܬܢܝ ܕܐܝܬܝܗ ܐܠܗܝ ܐܠܗܝ ܠܡܢܐ ܫܒܩܬܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 35, 'ܘܐܢܫܝܢ ܕܫܡܥܘ ܡܢ ܗܢܘܢ ܕܩܝܡܝܢ ܐܡܪܝܢ ܗܘܘ ܠܐܠܝܐ ܩܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 36, 'ܪܗܛ ܕܝܢ ܚܕ ܘܡܠܐ ܐܤܦܘܓܐ ܚܠܐ ܘܐܤܪ ܒܩܢܝܐ ܕܢܫܩܝܘܗܝ ܘܐܡܪܘ ܫܒܘܩܘ ܢܚܙܐ ܐܢ ܐܬܐ ܐܠܝܐ ܡܚܬ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 37, 'ܗܘ ܕܝܢ ܝܫܘܥ ܩܥܐ ܒܩܠܐ ܪܡܐ ܘܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 38, 'ܘܐܦܝ ܬܪܥܐ ܕܗܝܟܠܐ ܐܨܛܪܝ ܠܬܪܝܢ ܡܢ ܠܥܠ ܥܕܡܐ ܠܬܚܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 39, 'ܟܕ ܚܙܐ ܕܝܢ ܩܢܛܪܘܢܐ ܗܘ ܕܩܐܡ ܗܘܐ ܠܘܬܗ ܕܗܟܢܐ ܩܥܐ ܘܫܠܡ ܐܡܪ ܫܪܝܪܐܝܬ ܗܢܐ ܓܒܪܐ ܒܪܗ ܗܘܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 40, 'ܐܝܬ ܗܘܝ ܕܝܢ ܐܦ ܢܫܐ ܡܢ ܪܘܚܩܐ ܕܚܙܝܢ ܗܘܝ ܡܪܝܡ ܡܓܕܠܝܬܐ ܘܡܪܝܡ ܐܡܗ ܕܝܥܩܘܒ ܙܥܘܪܐ ܘܕܝܘܤܐ ܘܫܠܘܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 41, 'ܗܢܝܢ ܕܟܕ ܗܘ ܒܓܠܝܠܐ ܢܩܝܦܢ ܗܘܝ ܠܗ ܘܡܫܡܫܢ ܠܗ ܘܐܚܪܢܝܬܐ ܤܓܝܐܬܐ ܕܤܠܩ ܗܘܝ ܥܡܗ ܠܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 42, 'ܘܟܕ ܗܘܐ ܪܡܫܐ ܕܥܪܘܒܬܐ ܕܐܝܬܝܗ ܩܕܡ ܫܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 43, 'ܐܬܐ ܝܘܤܦ ܗܘ ܕܡܢ ܪܡܬܐ ܡܝܩܪܐ ܒܘܠܘܛܐ ܐܝܢܐ ܕܐܦ ܗܘ ܡܤܟܐ ܗܘܐ ܠܡܠܟܘܬܐ ܕܐܠܗܐ ܘܐܡܪܚ ܘܥܠ ܠܘܬ ܦܝܠܛܘܤ ܘܫܐܠ ܦܓܪܗ ܕܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 44, 'ܦܝܠܛܘܤ ܕܝܢ ܬܡܗ ܕܐܢ ܡܢ ܟܕܘ ܡܝܬ ܘܩܪܐ ܠܩܢܛܪܘܢܐ ܘܫܐܠܗ ܕܐܢ ܡܢ ܩܕܡ ܥܕܢܐ ܡܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 45, 'ܘܟܕ ܝܠܦ ܝܗܒ ܦܓܪܗ ܠܝܘܤܦ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 46, 'ܘܙܒܢ ܝܘܤܦ ܟܬܢܐ ܘܐܚܬܗ ܘܟܪܟܗ ܒܗ ܘܤܡܗ ܒܩܒܪܐ ܕܢܩܝܪ ܗܘܐ ܒܫܘܥܐ ܘܥܓܠ ܟܐܦܐ ܥܠ ܬܪܥܗ ܕܩܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 47, 'ܡܪܝܡ ܕܝܢ ܡܓܕܠܝܬܐ ܘܡܪܝܡ ܗܝ ܕܝܘܤܐ ܚܙܝ ܐܝܟܐ ܕܐܬܬܤܝܡ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 1, 'ܘܟܕ ܥܒܪܬ ܫܒܬܐ ܡܪܝܡ ܡܓܕܠܝܬܐ ܘܡܪܝܡ ܕܝܥܩܘܒ ܘܫܠܘܡ ܙܒܢ ܗܪܘܡܐ ܕܢܐܬܝܢ ܢܡܫܚܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 2, 'ܒܫܦܪܐ ܕܝܢ ܒܚܕ ܒܫܒܐ ܐܬܝ ܠܒܝܬ ܩܒܘܪܐ ܟܕ ܕܢܚ ܫܡܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 3, 'ܘܐܡܪܢ ܗܘܝ ܒܢܦܫܗܝܢ ܡܢ ܕܝܢ ܥܓܠ ܠܢ ܟܐܦܐ ܡܢ ܬܪܥܐ ܕܒܝܬ ܩܒܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 4, 'ܘܚܪ ܚܙܝ ܕܡܥܓܠܐ ܗܝ ܟܐܦܐ ܪܒܐ ܗܘܬ ܓܝܪ ܛܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 5, 'ܘܥܠܝܢ ܠܒܝܬ ܩܒܘܪܐ ܘܚܙܝ ܥܠܝܡܐ ܕܝܬܒ ܡܢ ܝܡܝܢܐ ܘܥܛܝܦ ܐܤܛܠܐ ܚܘܪܬܐ ܘܬܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 6, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗܝܢ ܠܐ ܬܕܚܠܢ ܠܝܫܘܥ ܢܨܪܝܐ ܒܥܝܢ ܐܢܬܝܢ ܗܘ ܕܐܙܕܩܦ ܩܡ ܠܗ ܠܐ ܗܘܐ ܬܢܢ ܗܐ ܕܘܟܬܐ ܐܝܟܐ ܕܤܝܡ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 7, 'ܐܠܐ ܙܠܝܢ ܐܡܪܝܢ ܠܬܠܡܝܕܘܗܝ ܘܠܟܐܦܐ ܕܗܐ ܩܕܡ ܠܟܘܢ ܠܓܠܝܠܐ ܬܡܢ ܬܚܙܘܢܝܗܝ ܐܝܟܢܐ ܕܐܡܪ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 8, 'ܘܟܕ ܫܡܥ ܥܪܩ ܘܢܦܩ ܡܢ ܩܒܪܐ ܐܚܝܕ ܗܘܐ ܠܗܝܢ ܓܝܪ ܬܗܪܐ ܘܪܬܝܬܐ ܘܠܐܢܫ ܡܕܡ ܠܐ ܐܡܪܝܢ ܕܚܝܠܢ ܗܘܝ ܓܝܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 9, 'ܒܫܦܪܐ ܕܝܢ ܒܚܕ ܒܫܒܐ ܩܡ ܘܐܬܚܙܝ ܠܘܩܕܡ ܠܡܪܝܡ ܡܓܕܠܝܬܐ ܗܝ ܕܫܒܥܐ ܫܐܕܝܢ ܐܦܩ ܗܘܐ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 10, 'ܘܗܝ ܐܙܠܬ ܤܒܪܬ ܠܗܢܘܢ ܕܥܡܗ ܗܘܘ ܕܐܒܝܠܝܢ ܗܘܘ ܘܒܟܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 11, 'ܘܗܢܘܢ ܟܕ ܫܡܥܘ ܕܐܡܪܢ ܕܚܝ ܘܐܬܚܙܝ ܠܗܝܢ ܠܐ ܗܝܡܢܘ ܐܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 12, 'ܒܬܪ ܗܠܝܢ ܐܬܚܙܝ ܠܬܪܝܢ ܡܢܗܘܢ ܒܕܡܘܬܐ ܐܚܪܬܐ ܟܕ ܡܗܠܟܝܢ ܘܐܙܠܝܢ ܠܩܪܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 13, 'ܘܗܢܘܢ ܐܙܠܘ ܐܡܪܘ ܠܫܪܟܐ ܐܦ ܠܐ ܠܗܢܘܢ ܗܝܡܢܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 14, 'ܐܚܪܝܬ ܕܝܢ ܐܬܚܙܝ ܠܚܕܥܤܪ ܟܕ ܤܡܝܟܝܢ ܘܚܤܕ ܠܚܤܝܪܘܬ ܗܝܡܢܘܬܗܘܢ ܘܠܩܫܝܘܬ ܠܒܗܘܢ ܕܠܗܢܘܢ ܕܚܙܐܘܗܝ ܕܩܡ ܠܐ ܗܝܡܢܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 15, 'ܘܐܡܪ ܠܗܘܢ ܙܠܘ ܠܥܠܡܐ ܟܠܗ ܘܐܟܪܙܘ ܤܒܪܬܝ ܒܟܠܗ ܒܪܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 16, 'ܐܝܢܐ ܕܡܗܝܡܢ ܘܥܡܕ ܚܝܐ ܘܐܝܢܐ ܕܠܐ ܡܗܝܡܢ ܡܬܚܝܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 17, 'ܐܬܘܬܐ ܕܝܢ ܠܐܝܠܝܢ ܕܡܗܝܡܢܝܢ ܗܠܝܢ ܢܩܦܢ ܒܫܡܝ ܫܐܕܐ ܢܦܩܘܢ ܘܒܠܫܢܐ ܚܕܬܐ ܢܡܠܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 18, 'ܘܚܘܘܬܐ ܢܫܩܠܘܢ ܘܐܢ ܤܡܐ ܕܡܘܬܐ ܢܫܬܘܢ ܠܐ ܢܗܪ ܐܢܘܢ ܘܐܝܕܝܗܘܢ ܢܤܝܡܘܢ ܥܠ ܟܪܝܗܐ ܘܢܬܚܠܡܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 19, 'ܝܫܘܥ ܕܝܢ ܡܪܢ ܡܢ ܒܬܪ ܕܡܠܠ ܥܡܗܘܢ ܠܫܡܝܐ ܤܠܩ ܘܝܬܒ ܡܢ ܝܡܝܢܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 20, 'ܗܢܘܢ ܕܝܢ ܢܦܩܘ ܘܐܟܪܙܘ ܒܟܠ ܕܘܟܐ ܘܡܪܢ ܡܥܕܪ ܗܘܐ ܠܗܘܢ ܘܡܫܪ ܡܠܝܗܘܢ ܒܐܬܘܬܐ ܕܥܒܕܝܢ ܗܘܘ ܀ ܀ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 1, 'ܡܛܠ ܕܤܓܝܐܐ ܨܒܘ ܕܢܟܬܒܘܢ ܬܫܥܝܬܐ ܕܤܘܥܪܢܐ ܐܝܠܝܢ ܕܚܢܢ ܡܦܤܝܢ ܚܢܢ ܒܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 2, 'ܐܝܟ ܡܕܡ ܕܐܫܠܡܘ ܠܢ ܗܢܘܢ ܕܡܢ ܩܕܝܡ ܗܘܘ ܚܙܝܐ ܘܡܫܡܫܢܐ ܕܝܠܗ ܕܡܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 3, 'ܐܬܚܙܝ ܐܦ ܠܝ ܡܛܠ ܕܩܪܝܒ ܗܘܝܬ ܝܨܝܦܐܝܬ ܠܟܠܗܘܢ ܕܟܠ ܡܕܡ ܒܛܟܤܗ ܐܟܬܘܒ ܠܟ ܢܨܝܚܐ ܬܐܘܦܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 4, 'ܕܬܕܥ ܫܪܪܐ ܕܡܠܐ ܕܐܬܬܠܡܕܬ ܠܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 5, 'ܗܘܐ ܒܝܘܡܬܗ ܕܗܪܘܕܤ ܡܠܟܐ ܕܝܗܘܕܐ ܟܗܢܐ ܚܕ ܕܫܡܗ ܗܘܐ ܙܟܪܝܐ ܡܢ ܬܫܡܫܬܐ ܕܒܝܬ ܐܒܝܐ ܘܐܢܬܬܗ ܡܢ ܒܢܬܗ ܕܐܗܪܘܢ ܫܡܗ ܗܘܐ ܐܠܝܫܒܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 6, 'ܬܪܝܗܘܢ ܕܝܢ ܙܕܝܩܝܢ ܗܘܘ ܩܕܡ ܐܠܗܐ ܘܡܗܠܟܝܢ ܒܟܠܗܘܢ ܦܘܩܕܢܘܗܝ ܘܒܟܐܢܘܬܗ ܕܡܪܝܐ ܕܠܐ ܥܕܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 7, 'ܒܪܐ ܕܝܢ ܠܝܬ ܗܘܐ ܠܗܘܢ ܡܛܠ ܕܐܠܝܫܒܥ ܥܩܪܬܐ ܗܘܬ ܘܬܪܝܗܘܢ ܤܓܝܐܝ ܒܝܘܡܬܗܘܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 8, 'ܗܘܐ ܗܘܐ ܕܝܢ ܟܕ ܡܟܗܢ ܗܘܐ ܒܛܟܤܐ ܕܬܫܡܫܬܗ ܩܕܡ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 9, 'ܒܥܝܕܐ ܕܟܗܢܘܬܐ ܡܛܝܗܝ ܕܢܤܝܡ ܒܤܡܐ ܘܥܠ ܠܗܝܟܠܗ ܕܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 10, 'ܘܟܠܗ ܟܢܫܐ ܕܥܡܐ ܡܨܠܐ ܗܘܐ ܠܒܪ ܒܥܕܢܐ ܕܒܤܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 11, 'ܘܐܬܚܙܝ ܠܗ ܠܙܟܪܝܐ ܡܠܐܟܐ ܕܡܪܝܐ ܕܩܐܡ ܡܢ ܝܡܝܢܐ ܕܡܕܒܚܐ ܕܒܤܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 12, 'ܘܐܫܬܓܫ ܙܟܪܝܐ ܟܕ ܚܙܝܗܝ ܘܕܚܠܬܐ ܢܦܠܬ ܥܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 13, 'ܘܐܡܪ ܠܗ ܡܠܐܟܐ ܠܐ ܬܕܚܠ ܙܟܪܝܐ ܡܛܠ ܕܐܫܬܡܥܬ ܨܠܘܬܟ ܘܐܢܬܬܟ ܐܠܝܫܒܥ ܬܐܠܕ ܠܟ ܒܪܐ ܘܬܩܪܐ ܫܡܗ ܝܘܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 14, 'ܘܬܗܘܐ ܠܟ ܚܕܘܬܐ ܘܐܪܘܙܐ ܘܤܓܝܐܐ ܢܚܕܘܢ ܒܡܘܠܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 15, 'ܢܗܘܐ ܓܝܪ ܪܒ ܩܕܡ ܡܪܝܐ ܘܚܡܪܐ ܘܫܟܪܐ ܠܐ ܢܫܬܐ ܘܪܘܚܐ ܕܩܘܕܫܐ ܢܬܡܠܐ ܥܕ ܗܘ ܒܟܪܤܐ ܕܐܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 16, 'ܘܤܓܝܐܐ ܡܢ ܒܢܝ ܐܝܤܪܝܠ ܢܦܢܐ ܠܘܬ ܡܪܝܐ ܐܠܗܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 17, 'ܘܗܘ ܢܐܙܠ ܩܕܡܘܗܝ ܒܪܘܚܐ ܘܒܚܝܠܐ ܕܐܠܝܐ ܢܒܝܐ ܕܢܦܢܐ ܠܒܐ ܕܐܒܗܐ ܥܠ ܒܢܝܐ ܘܠܐܝܠܝܢ ܕܠܐ ܡܬܛܦܝܤܝܢ ܠܝܕܥܬܐ ܕܟܐܢܐ ܘܢܛܝܒ ܠܡܪܝܐ ܥܡܐ ܓܡܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 18, 'ܘܐܡܪ ܙܟܪܝܐ ܠܡܠܐܟܐ ܐܝܟܢܐ ܐܕܥ ܗܕܐ ܐܢܐ ܓܝܪ ܐܝܬܝ ܤܒܐ ܘܐܢܬܬܝ ܤܓܝܐܬ ܒܝܘܡܬܗ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 19, 'ܘܥܢܐ ܡܠܐܟܐ ܘܐܡܪ ܠܗ ܐܢܐ ܐܢܐ ܓܒܪܝܐܝܠ ܕܩܐܡ ܐܢܐ ܩܕܡ ܐܠܗܐ ܘܐܫܬܠܚܬ ܕܐܡܠܠ ܥܡܟ ܘܐܤܒܪܟ ܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 20, 'ܡܟܝܠ ܬܗܘܐ ܫܬܝܩ ܘܠܐ ܬܫܟܚ ܠܡܡܠܠܘ ܥܕܡܐ ܠܝܘܡܐ ܕܗܠܝܢ ܢܗܘܝܢ ܥܠ ܕܠܐ ܗܝܡܢܬ ܠܡܠܝ ܗܠܝܢ ܕܡܬܡܠܝܢ ܒܙܒܢܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 21, 'ܥܡܐ ܕܝܢ ܩܐܡ ܗܘܐ ܘܡܤܟܐ ܠܙܟܪܝܐ ܘܬܡܝܗܝܢ ܗܘܘ ܥܠ ܬܘܚܪܬܗ ܕܒܗܝܟܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 22, 'ܟܕ ܢܦܩ ܕܝܢ ܙܟܪܝܐ ܠܐ ܡܫܟܚ ܗܘܐ ܕܢܡܠܠ ܥܡܗܘܢ ܘܐܤܬܟܠܘ ܕܚܙܘܐ ܚܙܐ ܒܗܝܟܠܐ ܘܗܘ ܡܪܡܙ ܪܡܙ ܗܘܐ ܠܗܘܢ ܘܩܘܝ ܟܕ ܚܪܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 23, 'ܘܟܕ ܐܬܡܠܝܘ ܝܘܡܬܐ ܕܬܫܡܫܬܗ ܐܙܠ ܠܒܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 24, 'ܘܗܘܐ ܡܢ ܒܬܪ ܝܘܡܬܐ ܗܢܘܢ ܒܛܢܬ ܐܠܝܫܒܥ ܐܢܬܬܗ ܘܡܛܫܝܐ ܗܘܬ ܢܦܫܗ ܝܪܚܐ ܚܡܫܐ ܘܐܡܪܐ ܗܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 25, 'ܕܗܠܝܢ ܥܒܕ ܠܝ ܡܪܝܐ ܒܝܘܡܬܐ ܕܚܪ ܒܝ ܠܡܤܒ ܚܤܕܝ ܕܒܝܬ ܒܢܝ ܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 26, 'ܒܝܪܚܐ ܕܝܢ ܕܫܬܐ ܐܫܬܠܚ ܓܒܪܝܐܝܠ ܡܠܐܟܐ ܡܢ ܠܘܬ ܐܠܗܐ ܠܓܠܝܠܐ ܠܡܕܝܢܬܐ ܕܫܡܗ ܢܨܪܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 27, 'ܠܘܬ ܒܬܘܠܬܐ ܕܡܟܝܪܐ ܠܓܒܪܐ ܕܫܡܗ ܝܘܤܦ ܡܢ ܒܝܬܗ ܕܕܘܝܕ ܘܫܡܗ ܠܒܬܘܠܬܐ ܡܪܝܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 28, 'ܘܥܠ ܠܘܬܗ ܡܠܐܟܐ ܘܐܡܪ ܠܗ ܫܠܡ ܠܟܝ ܡܠܝܬ ܛܝܒܘܬܐ ܡܪܢ ܥܡܟܝ ܒܪܝܟܬ ܒܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 29, 'ܗܝ ܕܝܢ ܟܕ ܚܙܬ ܐܬܪܗܒܬ ܒܡܠܬܗ ܘܡܬܚܫܒܐ ܗܘܬ ܕܡܢܐ ܗܘ ܫܠܡܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 30, 'ܘܐܡܪ ܠܗ ܡܠܐܟܐ ܠܐ ܬܕܚܠܝܢ ܡܪܝܡ ܐܫܟܚܬܝ ܓܝܪ ܛܝܒܘܬܐ ܠܘܬ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 31, 'ܗܐ ܓܝܪ ܬܩܒܠܝܢ ܒܛܢܐ ܘܬܐܠܕܝܢ ܒܪܐ ܘܬܩܪܝܢ ܫܡܗ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 32, 'ܗܢܐ ܢܗܘܐ ܪܒ ܘܒܪܗ ܕܥܠܝܐ ܢܬܩܪܐ ܘܢܬܠ ܠܗ ܡܪܝܐ ܐܠܗܐ ܟܘܪܤܝܗ ܕܕܘܝܕ ܐܒܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 33, 'ܘܢܡܠܟ ܥܠ ܒܝܬܗ ܕܝܥܩܘܒ ܠܥܠܡ ܘܠܡܠܟܘܬܗ ܤܘܦ ܠܐ ܢܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 34, 'ܐܡܪܐ ܡܪܝܡ ܠܡܠܐܟܐ ܐܝܟܢܐ ܬܗܘܐ ܗܕܐ ܕܓܒܪܐ ܠܐ ܚܟܝܡ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 35, 'ܥܢܐ ܡܠܐܟܐ ܘܐܡܪ ܠܗ ܪܘܚܐ ܕܩܘܕܫܐ ܬܐܬܐ ܘܚܝܠܗ ܕܥܠܝܐ ܢܓܢ ܥܠܝܟܝ ܡܛܠ ܗܢܐ ܗܘ ܕܡܬܝܠܕ ܒܟܝ ܩܕܝܫܐ ܗܘ ܘܒܪܗ ܕܐܠܗܐ ܢܬܩܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 36, 'ܘܗܐ ܐܠܝܫܒܥ ܐܚܝܢܬܟܝ ܐܦ ܗܝ ܒܛܢܐ ܒܪܐ ܒܤܝܒܘܬܗ ܘܗܢܐ ܝܪܚܐ ܕܫܬܐ ܠܗ ܠܗܝ ܕܡܬܩܪܝܐ ܥܩܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 37, 'ܡܛܠ ܕܠܐ ܥܛܠ ܠܐܠܗܐ ܡܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 38, 'ܐܡܪܐ ܡܪܝܡ ܗܐ ܐܢܐ ܐܡܬܗ ܕܡܪܝܐ ܢܗܘܐ ܠܝ ܐܝܟ ܡܠܬܟ ܘܐܙܠ ܡܠܐܟܐ ܡܢ ܠܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 39, 'ܩܡܬ ܕܝܢ ܡܪܝܡ ܒܗܘܢ ܒܝܘܡܬܐ ܗܢܘܢ ܘܐܙܠܬ ܒܛܝܠܐܝܬ ܠܛܘܪܐ ܠܡܕܝܢܬܐ ܕܝܗܘܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 40, 'ܘܥܠܬ ܠܒܝܬܗ ܕܙܟܪܝܐ ܘܫܐܠܬ ܫܠܡܗ ܕܐܠܝܫܒܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 41, 'ܘܗܘܐ ܕܟܕ ܫܡܥܬ ܐܠܝܫܒܥ ܫܠܡܗ ܕܡܪܝܡ ܕܨ ܥܘܠܐ ܒܟܪܤܗ ܘܐܬܡܠܝܬ ܐܠܝܫܒܥ ܪܘܚܐ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 42, 'ܘܩܥܬ ܒܩܠܐ ܪܡܐ ܘܐܡܪܬ ܠܡܪܝܡ ܡܒܪܟܬܐ ܐܢܬܝ ܒܢܫܐ ܘܡܒܪܟ ܗܘ ܦܐܪܐ ܕܒܟܪܤܟܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 43, 'ܐܝܡܟܐ ܠܝ ܗܕܐ ܕܐܡܗ ܕܡܪܝ ܬܐܬܐ ܠܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 44, 'ܗܐ ܓܝܪ ܟܕ ܢܦܠ ܩܠܐ ܕܫܠܡܟܝ ܒܐܕܢܝ ܒܚܕܘܬܐ ܪܒܬܐ ܕܨ ܥܘܠܐ ܒܟܪܤܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 45, 'ܘܛܘܒܝܗ ܠܐܝܕܐ ܕܗܝܡܢܬ ܕܗܘܐ ܫܘܠܡܐ ܠܐܝܠܝܢ ܕܐܬܡܠܠ ܥܡܗ ܡܢ ܠܘܬ ܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 46, 'ܘܐܡܪܬ ܡܪܝܡ ܡܘܪܒܐ ܢܦܫܝ ܠܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 47, 'ܘܚܕܝܬ ܪܘܚܝ ܒܐܠܗܐ ܡܚܝܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 48, 'ܕܚܪ ܒܡܘܟܟܐ ܕܐܡܬܗ ܗܐ ܓܝܪ ܡܢ ܗܫܐ ܛܘܒܐ ܢܬܠܢ ܠܝ ܫܪܒܬܐ ܟܠܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 49, 'ܕܥܒܕ ܠܘܬܝ ܪܘܪܒܬܐ ܗܘ ܕܚܝܠܬܢ ܘܩܕܝܫ ܫܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 50, 'ܘܚܢܢܗ ܠܕܪܐ ܘܫܪܒܬܐ ܥܠ ܐܝܠܝܢ ܕܕܚܠܝܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 51, 'ܥܒܕ ܙܟܘܬܐ ܒܕܪܥܗ ܘܒܕܪ ܚܬܝܪܝ ܒܬܪܥܝܬܐ ܕܠܒܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 52, 'ܤܚܦ ܬܩܝܦܐ ܡܢ ܟܘܪܤܘܬܐ ܘܐܪܝܡ ܡܟܝܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 53, 'ܟܦܢܐ ܤܒܥ ܛܒܬܐ ܘܥܬܝܪܐ ܫܪܐ ܤܦܝܩܐܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 54, 'ܥܕܪ ܠܐܝܤܪܝܠ ܥܒܕܗ ܘܐܬܕܟܪ ܚܢܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 55, 'ܐܝܟ ܕܡܠܠ ܥܡ ܐܒܗܝܢ ܥܡ ܐܒܪܗܡ ܘܥܡ ܙܪܥܗ ܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 56, 'ܩܘܝܬ ܕܝܢ ܡܪܝܡ ܠܘܬ ܐܠܝܫܒܥ ܐܝܟ ܝܪܚܐ ܬܠܬܐ ܘܗܦܟܬ ܠܒܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 57, 'ܐܠܝܫܒܥ ܕܝܢ ܗܘܐ ܗܘܐ ܠܗ ܙܒܢܐ ܕܬܐܠܕ ܘܝܠܕܬ ܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 58, 'ܘܫܡܥܘ ܫܒܒܝܗ ܘܒܢܝ ܛܘܗܡܗ ܕܐܤܓܝ ܐܠܗܐ ܚܢܢܗ ܠܘܬܗ ܘܚܕܝܢ ܗܘܘ ܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 59, 'ܘܗܘܐ ܠܝܘܡܐ ܕܬܡܢܝܐ ܘܐܬܘ ܠܡܓܙܪܗ ܠܛܠܝܐ ܘܩܪܝܢ ܗܘܘ ܠܗ ܒܫܡܐ ܕܐܒܘܗܝ ܙܟܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 60, 'ܘܥܢܬ ܐܡܗ ܘܐܡܪܐ ܠܗܘܢ ܠܐ ܗܟܢܐ ܐܠܐ ܢܬܩܪܐ ܝܘܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 61, 'ܘܐܡܪܘ ܠܗ ܕܠܝܬ ܐܢܫ ܒܫܪܒܬܟܝ ܕܡܬܩܪܐ ܒܫܡܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 62, 'ܘܪܡܙܘ ܠܐܒܘܗܝ ܕܐܝܟܢܐ ܨܒܐ ܕܢܫܡܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 63, 'ܘܫܐܠ ܦܢܩܝܬܐ ܘܟܬܒ ܘܐܡܪ ܝܘܚܢܢ ܗܘ ܫܡܗ ܘܐܬܕܡܪܘ ܟܠܢܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 64, 'ܘܡܚܕܐ ܐܬܦܬܚ ܦܘܡܗ ܘܠܫܢܗ ܘܡܠܠ ܘܒܪܟ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 65, 'ܘܗܘܬ ܕܚܠܬܐ ܥܠ ܟܠܗܘܢ ܫܒܒܝܗܘܢ ܘܒܟܠܗ ܛܘܪܐ ܕܝܗܘܕ ܗܠܝܢ ܡܬܡܠܠܢ ܗܘܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 66, 'ܘܟܠܗܘܢ ܕܫܡܥܘ ܡܬܚܫܒܝܢ ܗܘܘ ܒܠܒܗܘܢ ܘܐܡܪܝܢ ܡܢܐ ܟܝ ܢܗܘܐ ܛܠܝܐ ܗܢܐ ܘܐܝܕܗ ܕܡܪܝܐ ܐܝܬ ܗܘܬ ܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 67, 'ܘܐܬܡܠܝ ܙܟܪܝܐ ܐܒܘܗܝ ܪܘܚܐ ܕܩܘܕܫܐ ܘܐܬܢܒܝ ܘܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 68, 'ܡܒܪܟ ܗܘ ܡܪܝܐ ܐܠܗܗ ܕܐܝܤܪܝܠ ܕܤܥܪ ܥܡܗ ܘܥܒܕ ܠܗ ܦܘܪܩܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 69, 'ܘܐܩܝܡ ܠܢ ܩܪܢܐ ܕܦܘܪܩܢܐ ܒܒܝܬܗ ܕܕܘܝܕ ܥܒܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 70, 'ܐܝܟ ܕܡܠܠ ܒܦܘܡܐ ܕܢܒܝܘܗܝ ܩܕܝܫܐ ܕܡܢ ܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 71, 'ܕܢܦܪܩܢ ܡܢ ܒܥܠܕܒܒܝܢ ܘܡܢ ܐܝܕܐ ܕܟܠܗܘܢ ܤܢܐܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 72, 'ܘܥܒܕ ܚܢܢܗ ܥܡ ܐܒܗܝܢ ܘܥܗܕ ܠܕܝܬܩܘܗܝ ܩܕܝܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 73, 'ܘܡܘܡܬܐ ܕܝܡܐ ܠܐܒܪܗܡ ܐܒܘܢ ܕܢܬܠ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 74, 'ܕܢܬܦܪܩ ܡܢ ܐܝܕܐ ܕܒܥܠܕܒܒܝܢ ܘܕܠܐ ܕܚܠܐ ܢܦܠܘܚ ܩܕܡܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 75, 'ܟܠܗܘܢ ܝܘܡܬܢ ܒܟܐܢܘܬܐ ܘܒܙܕܝܩܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 76, 'ܘܐܢܬ ܛܠܝܐ ܢܒܝܗ ܕܥܠܝܐ ܬܬܩܪܐ ܬܐܙܠ ܓܝܪ ܩܕܡ ܦܪܨܘܦܗ ܕܡܪܝܐ ܕܬܛܝܒ ܐܘܪܚܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 77, 'ܕܢܬܠ ܡܕܥܐ ܕܚܝܐ ܠܥܡܗ ܒܫܘܒܩܢܐ ܕܚܛܗܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 78, 'ܒܪܚܡܐ ܕܚܢܢܐ ܕܐܠܗܢ ܕܒܗܘܢ ܢܤܥܪܢ ܕܢܚܐ ܡܢ ܪܘܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 79, 'ܠܡܢܗܪܘ ܠܐܝܠܝܢ ܕܒܚܫܘܟܐ ܘܒܛܠܠܐ ܕܡܘܬܐ ܝܬܒܝܢ ܕܢܬܪܘܨ ܪܓܠܝܢ ܒܐܘܪܚܐ ܕܫܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 80, 'ܛܠܝܐ ܕܝܢ ܪܒܐ ܗܘܐ ܘܡܬܚܝܠ ܒܪܘܚܐ ܘܒܚܘܪܒܐ ܐܝܬܘܗܝ ܗܘܐ ܥܕܡܐ ܠܝܘܡܐ ܕܬܚܘܝܬܗ ܕܠܘܬ ܐܝܤܪܝܠ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 1, 'ܗܘܐ ܕܝܢ ܒܝܘܡܬܐ ܗܢܘܢ ܘܢܦܩ ܦܘܩܕܢܐ ܡܢ ܐܓܘܤܛܘܤ ܩܤܪ ܕܢܬܟܬܒ ܟܠܗ ܥܡܐ ܕܐܘܚܕܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 2, 'ܗܕܐ ܡܟܬܒܢܘܬܐ ܩܕܡܝܬܐ ܗܘܬ ܒܗܓܡܢܘܬܐ ܕܩܘܪܝܢܘܤ ܒܤܘܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 3, 'ܘܐܙܠ ܗܘܐ ܟܠܢܫ ܕܢܬܟܬܒ ܒܡܕܝܢܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 4, 'ܤܠܩ ܗܘܐ ܕܝܢ ܐܦ ܝܘܤܦ ܡܢ ܢܨܪܬ ܡܕܝܢܬܐ ܕܓܠܝܠܐ ܠܝܗܘܕ ܠܡܕܝܢܬܗ ܕܕܘܝܕ ܕܡܬܩܪܝܐ ܒܝܬ ܠܚܡ ܡܛܠ ܕܐܝܬܘܗܝ ܗܘܐ ܡܢ ܒܝܬܗ ܘܡܢ ܫܪܒܬܗ ܕܕܘܝܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 5, 'ܥܡ ܡܪܝܡ ܡܟܝܪܬܗ ܟܕ ܒܛܢܐ ܕܬܡܢ ܢܬܟܬܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 6, 'ܘܗܘܐ ܕܟܕ ܬܡܢ ܐܢܘܢ ܐܬܡܠܝܘ ܝܘܡܬܗ ܕܬܐܠܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 7, 'ܘܝܠܕܬ ܒܪܗ ܒܘܟܪܐ ܘܟܪܟܬܗ ܒܥܙܪܘܪܐ ܘܐܪܡܝܬܗ ܒܐܘܪܝܐ ܡܛܠ ܕܠܝܬ ܗܘܐ ܠܗܘܢ ܕܘܟܬܐ ܐܝܟܐ ܕܫܪܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 8, 'ܪܥܘܬܐ ܕܝܢ ܐܝܬ ܗܘܘ ܒܗ ܒܐܬܪܐ ܕܫܪܝܢ ܗܘܘ ܬܡܢ ܘܢܛܪܝܢ ܡܛܪܬܐ ܕܠܠܝܐ ܥܠ ܡܪܥܝܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 9, 'ܘܗܐ ܡܠܐܟܐ ܕܐܠܗܐ ܐܬܐ ܠܘܬܗܘܢ ܘܬܫܒܘܚܬܗ ܕܡܪܝܐ ܐܢܗܪܬ ܥܠܝܗܘܢ ܘܕܚܠܘ ܕܚܠܬܐ ܪܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 10, 'ܘܐܡܪ ܠܗܘܢ ܡܠܐܟܐ ܠܐ ܬܕܚܠܘܢ ܗܐ ܓܝܪ ܡܤܒܪ ܐܢܐ ܠܟܘܢ ܚܕܘܬܐ ܪܒܬܐ ܕܬܗܘܐ ܠܟܠܗ ܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 11, 'ܐܬܝܠܕ ܠܟܘܢ ܓܝܪ ܝܘܡܢܐ ܦܪܘܩܐ ܕܐܝܬܘܗܝ ܡܪܝܐ ܡܫܝܚܐ ܒܡܕܝܢܬܗ ܕܕܘܝܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 12, 'ܘܗܕܐ ܠܟܘܢ ܐܬܐ ܡܫܟܚܝܢ ܐܢܬܘܢ ܥܘܠܐ ܕܟܪܝܟ ܒܥܙܪܘܪܐ ܘܤܝܡ ܒܐܘܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 13, 'ܘܡܢ ܫܠܝ ܐܬܚܙܝܘ ܥܡ ܡܠܐܟܐ ܚܝܠܘܬܐ ܤܓܝܐܐ ܕܫܡܝܐ ܟܕ ܡܫܒܚܝܢ ܠܐܠܗܐ ܘܐܡܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 14, 'ܬܫܒܘܚܬܐ ܠܐܠܗܐ ܒܡܪܘܡܐ ܘܥܠ ܐܪܥܐ ܫܠܡܐ ܘܤܒܪܐ ܛܒܐ ܠܒܢܝ ܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 15, 'ܘܗܘܐ ܕܟܕ ܐܙܠܘ ܡܢ ܠܘܬܗܘܢ ܡܠܐܟܐ ܠܫܡܝܐ ܡܠܠܘ ܪܥܘܬܐ ܚܕ ܥܡ ܚܕ ܘܐܡܪܝܢ ܢܪܕܐ ܥܕܡܐ ܠܒܝܬ ܠܚܡ ܘܢܚܙܐ ܠܡܠܬܐ ܗܕܐ ܕܗܘܬ ܐܝܟ ܕܡܪܝܐ ܐܘܕܥ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 16, 'ܘܐܬܘ ܡܤܪܗܒܐܝܬ ܘܐܫܟܚܘ ܠܡܪܝܡ ܘܠܝܘܤܦ ܘܠܥܘܠܐ ܕܤܝܡ ܒܐܘܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 17, 'ܘܟܕ ܚܙܘ ܐܘܕܥܘ ܠܡܠܬܐ ܕܐܬܡܠܠܬ ܥܡܗܘܢ ܥܠܘܗܝ ܥܠ ܛܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 18, 'ܘܟܠܗܘܢ ܕܫܡܥܘ ܐܬܕܡܪܘ ܥܠ ܐܝܠܝܢ ܕܐܬܡܠܠ ܠܗܘܢ ܡܢ ܪܥܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 19, 'ܡܪܝܡ ܕܝܢ ܢܛܪܐ ܗܘܬ ܟܠܗܝܢ ܡܠܐ ܗܠܝܢ ܘܡܦܚܡܐ ܒܠܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 20, 'ܘܗܦܟܘ ܪܥܘܬܐ ܗܢܘܢ ܟܕ ܡܫܒܚܝܢ ܘܡܗܠܠܝܢ ܠܐܠܗܐ ܥܠ ܟܠ ܕܚܙܘ ܘܫܡܥܘ ܐܝܟܢܐ ܕܐܬܡܠܠ ܥܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 21, 'ܘܟܕ ܡܠܘ ܬܡܢܝܐ ܝܘܡܝܢ ܕܢܬܓܙܪ ܛܠܝܐ ܐܬܩܪܝ ܫܡܗ ܝܫܘܥ ܕܐܬܩܪܝ ܡܢ ܡܠܐܟܐ ܩܕܡ ܕܢܬܒܛܢ ܒܟܪܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 22, 'ܘܟܕ ܐܬܡܠܝܘ ܝܘܡܬܐ ܕܬܕܟܝܬܗܘܢ ܐܝܟ ܢܡܘܤܐ ܕܡܘܫܐ ܐܤܩܘܗܝ ܠܐܘܪܫܠܡ ܕܢܩܝܡܘܢܝܗܝ ܩܕܡ ܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 23, 'ܐܝܟ ܕܟܬܝܒ ܒܢܡܘܤܐ ܕܡܪܝܐ ܕܟܠ ܕܟܪܐ ܦܬܚ ܡܪܒܥܐ ܩܕܝܫܐ ܕܡܪܝܐ ܢܬܩܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 24, 'ܘܕܢܬܠܘܢ ܕܒܚܬܐ ܐܝܟܢܐ ܕܐܡܝܪ ܒܢܡܘܤܐ ܕܡܪܝܐ ܙܘܓܐ ܕܫܘܦܢܝܢܐ ܐܘ ܬܪܝܢ ܦܪܘܓܐ ܕܝܘܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 25, 'ܓܒܪܐ ܕܝܢ ܚܕ ܐܝܬ ܗܘܐ ܒܐܘܪܫܠܡ ܫܡܗ ܗܘܐ ܫܡܥܘܢ ܘܓܒܪܐ ܗܢܐ ܟܐܝܢ ܗܘܐ ܘܙܕܝܩ ܘܡܤܟܐ ܗܘܐ ܠܒܘܝܐܗ ܕܐܝܤܪܝܠ ܘܪܘܚܐ ܕܩܘܕܫܐ ܐܝܬ ܗܘܬ ܥܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 26, 'ܘܐܡܝܪ ܗܘܐ ܠܗ ܡܢ ܪܘܚܐ ܕܩܘܕܫܐ ܕܠܐ ܢܚܙܐ ܡܘܬܐ ܥܕܡܐ ܕܢܚܙܐ ܠܡܫܝܚܗ ܕܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 27, 'ܗܘ ܗܢܐ ܐܬܐ ܗܘܐ ܒܪܘܚܐ ܠܗܝܟܠܐ ܘܟܕ ܡܥܠܝܢ ܠܗ ܐܒܗܘܗܝ ܠܝܫܘܥ ܛܠܝܐ ܕܢܥܒܕܘܢ ܚܠܦܘܗܝ ܐܝܟܢܐ ܕܦܩܝܕ ܒܢܡܘܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 28, 'ܩܒܠܗ ܥܠ ܕܪܥܘܗܝ ܘܒܪܟ ܠܐܠܗܐ ܘܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 29, 'ܡܟܝܠ ܫܪܐ ܐܢܬ ܠܗ ܠܥܒܕܟ ܡܪܝ ܐܝܟ ܡܠܬܟ ܒܫܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 30, 'ܕܗܐ ܚܙܝ ܥܝܢܝ ܚܢܢܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 31, 'ܗܘ ܕܛܝܒܬ ܒܦܪܨܘܦܐ ܕܟܠܗܝܢ ܐܡܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 32, 'ܢܘܗܪܐ ܠܓܠܝܢܐ ܕܥܡܡܐ ܘܫܘܒܚܐ ܠܥܡܟ ܐܝܤܪܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 33, 'ܝܘܤܦ ܕܝܢ ܘܐܡܗ ܬܡܝܗܝܢ ܗܘܘ ܥܠ ܐܝܠܝܢ ܕܡܬܡܠܠܢ ܗܘܝ ܥܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 34, 'ܘܒܪܟ ܐܢܘܢ ܫܡܥܘܢ ܘܐܡܪ ܠܡܪܝܡ ܐܡܗ ܗܐ ܗܢܐ ܤܝܡ ܠܡܦܘܠܬܐ ܘܠܩܝܡܐ ܕܤܓܝܐܐ ܒܐܝܤܪܝܠ ܘܠܐܬܐ ܕܚܪܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 35, 'ܘܒܢܦܫܟܝ ܕܝܢ ܕܝܠܟܝ ܬܥܒܪ ܪܘܡܚܐ ܐܝܟ ܕܢܬܓܠܝܢ ܡܚܫܒܬܐ ܕܠܒܘܬܐ ܕܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 36, 'ܘܚܢܐ ܕܝܢ ܢܒܝܬܐ ܒܪܬܗ ܕܦܢܘܐܝܠ ܡܢ ܫܒܛܐ ܕܐܫܝܪ ܐܦ ܗܝ ܩܫܝܫܬ ܒܝܘܡܬܗ ܗܘܬ ܘܫܒܥ ܫܢܝܢ ܥܡ ܒܥܠܗ ܚܝܬ ܡܢ ܒܬܘܠܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 37, 'ܘܗܘܬ ܐܪܡܠܬܐ ܐܝܟ ܫܢܝܢ ܬܡܢܐܝܢ ܘܐܪܒܥ ܘܠܐ ܦܪܩܐ ܗܘܬ ܡܢ ܗܝܟܠܐ ܘܒܨܘܡܐ ܘܒܨܠܘܬܐ ܦܠܚܐ ܗܘܬ ܒܐܝܡܡܐ ܘܒܠܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 38, 'ܘܐܦ ܗܝ ܩܡܬ ܒܗ ܒܫܥܬܐ ܘܐܘܕܝܬ ܠܡܪܝܐ ܘܡܡܠܠܐ ܗܘܬ ܥܠܘܗܝ ܥܡ ܟܠܢܫ ܕܡܤܟܐ ܗܘܐ ܠܦܘܪܩܢܗ ܕܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 39, 'ܘܟܕ ܫܠܡܘ ܟܠ ܡܕܡ ܐܝܟ ܕܒܢܡܘܤܐ ܕܡܪܝܐ ܗܦܟܘ ܠܓܠܝܠܐ ܠܢܨܪܬ ܡܕܝܢܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 40, 'ܛܠܝܐ ܕܝܢ ܪܒܐ ܗܘܐ ܘܡܬܚܝܠ ܒܪܘܚܐ ܘܡܬܡܠܐ ܚܟܡܬܐ ܘܛܝܒܘܬܐ ܕܐܠܗܐ ܐܝܬ ܗܘܬ ܥܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 41, 'ܘܐܢܫܘܗܝ ܒܟܠ ܫܢܐ ܐܙܠܝܢ ܗܘܘ ܠܐܘܪܫܠܡ ܒܥܕܥܕܐ ܕܦܨܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 42, 'ܘܟܕ ܗܘܐ ܒܪ ܫܢܝܢ ܬܪܬܥܤܪܐ ܤܠܩܘ ܐܝܟܢܐ ܕܡܥܕܝܢ ܗܘܘ ܠܥܕܥܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 43, 'ܘܟܕ ܫܠܡܘ ܝܘܡܬܐ ܗܦܟܘ ܠܗܘܢ ܝܫܘܥ ܕܝܢ ܛܠܝܐ ܦܫ ܠܗ ܒܐܘܪܫܠܡ ܘܝܘܤܦ ܘܐܡܗ ܠܐ ܝܕܥܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 44, 'ܤܒܪܝܢ ܗܘܘ ܓܝܪ ܕܥܡ ܒܢܝ ܠܘܝܬܗܘܢ ܗܘ ܘܟܕ ܐܬܘ ܡܪܕܐ ܝܘܡܐ ܚܕ ܒܥܐܘܗܝ ܠܘܬ ܐܢܫܘܬܗܘܢ ܘܠܘܬ ܡܢ ܕܝܕܥ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 45, 'ܘܠܐ ܐܫܟܚܘܗܝ ܘܗܦܟܘ ܠܗܘܢ ܬܘܒ ܠܐܘܪܫܠܡ ܘܒܥܝܢ ܗܘܘ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 46, 'ܘܡܢ ܒܬܪ ܬܠܬܐ ܝܘܡܝܢ ܐܫܟܚܘܗܝ ܒܗܝܟܠܐ ܟܕ ܝܬܒ ܡܨܥܬ ܡܠܦܢܐ ܘܫܡܥ ܡܢܗܘܢ ܘܡܫܐܠ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 47, 'ܘܬܡܝܗܝܢ ܗܘܘ ܟܠܗܘܢ ܐܝܠܝܢ ܕܫܡܥܝܢ ܗܘܘ ܠܗ ܒܚܟܡܬܗ ܘܒܦܬܓܡܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 48, 'ܘܟܕ ܚܙܐܘܗܝ ܬܡܗܘ ܘܐܡܪܬ ܠܗ ܐܡܗ ܒܪܝ ܠܡܢܐ ܥܒܕܬ ܠܢ ܗܟܢܐ ܕܗܐ ܐܒܘܟ ܘܐܢܐ ܒܛܘܪܦܐ ܤܓܝܐܐ ܒܥܝܢ ܗܘܝܢ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 49, 'ܐܡܪ ܠܗܘܢ ܡܢܐ ܒܥܝܢ ܗܘܝܬܘܢ ܠܝ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܕܒܝܬ ܐܒܝ ܘܠܐ ܠܝ ܕܐܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 50, 'ܗܢܘܢ ܕܝܢ ܠܐ ܐܫܬܘܕܥܘ ܠܡܠܬܐ ܕܐܡܪ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 51, 'ܘܢܚܬ ܥܡܗܘܢ ܘܐܬܐ ܠܢܨܪܬ ܘܡܫܬܥܒܕ ܗܘܐ ܠܗܘܢ ܐܡܗ ܕܝܢ ܢܛܪܐ ܗܘܬ ܟܠܗܝܢ ܡܠܐ ܒܠܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 52, 'ܝܫܘܥ ܕܝܢ ܪܒܐ ܗܘܐ ܒܩܘܡܬܗ ܘܒܚܟܡܬܗ ܘܒܛܝܒܘܬܐ ܠܘܬ ܐܠܗܐ ܘܒܢܝܢܫܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 1, 'ܒܫܢܬ ܚܡܫܥܤܪܐ ܕܝܢ ܕܡܠܟܘܬܗ ܕܛܝܒܪܝܘܤ ܩܤܪ ܒܗܓܡܢܘܬܐ ܕܦܢܛܝܘܤ ܦܝܠܛܘܤ ܒܝܗܘܕ ܟܕ ܪܫܐ ܪܒܝܥܝܐ ܗܪܘܕܤ ܒܓܠܝܠܐ ܘܦܝܠܝܦܘܤ ܐܚܘܗܝ ܪܫܐ ܪܒܝܥܝܐ ܒܐܝܛܘܪܝܐ ܘܒܐܬܪܐ ܕܛܪܟܘܢܐ ܘܠܘܤܢܝܐ ܪܫܐ ܪܒܝܥܝܐ ܕܐܒܝܠܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 2, 'ܒܪܒܘܬ ܟܗܢܘܬܐ ܕܚܢܢ ܘܕܩܝܦܐ ܗܘܬ ܡܠܬܐ ܕܐܠܗܐ ܥܠ ܝܘܚܢܢ ܒܪ ܙܟܪܝܐ ܒܚܘܪܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 3, 'ܘܐܬܐ ܒܟܠܗ ܐܬܪܐ ܕܚܕܪܝ ܝܘܪܕܢܢ ܟܕ ܡܟܪܙ ܡܥܡܘܕܝܬܐ ܕܬܝܒܘܬܐ ܠܫܘܒܩܢܐ ܕܚܛܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 4, 'ܐܝܟܢܐ ܕܟܬܝܒ ܒܟܬܒܐ ܕܡܠܐ ܕܐܫܥܝܐ ܢܒܝܐ ܕܐܡܪ ܩܠܐ ܕܩܪܐ ܒܚܘܪܒܐ ܛܝܒܘ ܐܘܪܚܗ ܕܡܪܝܐ ܘܬܪܘܨܘ ܒܦܩܥܬܐ ܫܒܝܠܐ ܠܐܠܗܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 5, 'ܟܠܗܘܢ ܢܚܠܐ ܢܬܡܠܘܢ ܘܟܠܗܘܢ ܛܘܪܐ ܘܪܡܬܐ ܢܬܡܟܟܘܢ ܘܢܗܘܐ ܥܪܡܐ ܠܫܦܝܐ ܘܐܬܪܐ ܥܤܩܐ ܠܦܩܥܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 6, 'ܘܢܚܙܐ ܟܠ ܒܤܪ ܚܝܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 7, 'ܘܐܡܪ ܗܘܐ ܠܟܢܫܐ ܐܝܠܝܢ ܕܐܬܝܢ ܗܘܘ ܠܘܬܗ ܠܡܥܡܕ ܝܠܕܐ ܕܐܟܕܢܐ ܡܢܘ ܚܘܝܟܘܢ ܠܡܥܪܩ ܡܢ ܪܘܓܙܐ ܕܥܬܝܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 8, 'ܥܒܕܘ ܗܟܝܠ ܦܐܪܐ ܕܫܘܝܢ ܠܬܝܒܘܬܐ ܘܠܐ ܬܫܪܘܢ ܠܡܐܡܪ ܒܢܦܫܟܘܢ ܕܐܒܐ ܐܝܬ ܠܢ ܐܒܪܗܡ ܐܡܪ ܐܢܐ ܠܟܘܢ ܓܝܪ ܕܡܢ ܗܠܝܢ ܟܐܦܐ ܡܫܟܚ ܐܠܗܐ ܠܡܩܡܘ ܒܢܝܐ ܠܐܒܪܗܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 9, 'ܗܐ ܕܝܢ ܢܪܓܐ ܤܝܡ ܥܠ ܥܩܪܐ ܕܐܝܠܢܐ ܟܠ ܐܝܠܢܐ ܗܟܝܠ ܕܦܐܪܐ ܛܒܐ ܠܐ ܥܒܕ ܡܬܦܤܩ ܘܒܢܘܪܐ ܢܦܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 10, 'ܘܡܫܐܠܝܢ ܗܘܘ ܠܗ ܟܢܫܐ ܘܐܡܪܝܢ ܡܢܐ ܗܟܝܠ ܢܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 11, 'ܥܢܐ ܘܐܡܪ ܠܗܘܢ ܡܢ ܕܐܝܬ ܠܗ ܬܪܬܝܢ ܟܘܬܝܢܝܢ ܢܬܠ ܠܡܢ ܕܠܝܬ ܠܗ ܘܡܢ ܕܐܝܬ ܠܗ ܤܝܒܪܬܐ ܗܟܢܐ ܢܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 12, 'ܘܐܬܘ ܐܦ ܡܟܤܐ ܠܡܥܡܕ ܘܐܡܪܝܢ ܠܗ ܡܠܦܢܐ ܡܢܐ ܢܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 13, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܠܐ ܬܬܒܥܘܢ ܡܕܡ ܝܬܝܪ ܥܠ ܡܐ ܕܦܩܝܕ ܠܟܘܢ ܠܡܬܒܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 14, 'ܘܡܫܐܠܝܢ ܗܘܘ ܠܗ ܦܠܚܝ ܐܤܛܪܛܝܐ ܘܐܡܪܝܢ ܡܢܐ ܢܥܒܕ ܐܦ ܚܢܢ ܐܡܪ ܠܗܘܢ ܠܐ ܒܐܢܫ ܬܬܛܓܪܘܢ ܘܠܐܢܫ ܠܐ ܬܥܫܩܘܢ ܘܢܤܦܩܢ ܠܟܘܢ ܐܦܤܘܢܝܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 15, 'ܟܕ ܕܝܢ ܡܤܒܪ ܗܘܐ ܥܡܐ ܥܠ ܝܘܚܢܢ ܘܟܠܗܘܢ ܡܬܚܫܒܝܢ ܗܘܘ ܒܠܒܗܘܢ ܕܕܠܡܐ ܗܘܝܘ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 16, 'ܥܢܐ ܝܘܚܢܢ ܘܐܡܪ ܠܗܘܢ ܐܢܐ ܗܐ ܡܥܡܕ ܐܢܐ ܠܟܘܢ ܒܡܝܐ ܐܬܐ ܕܝܢ ܗܘ ܕܚܝܠܬܢ ܡܢܝ ܗܘ ܕܠܐ ܫܘܐ ܐܢܐ ܕܐܫܪܐ ܥܪܩܐ ܕܡܤܢܘܗܝ ܗܘ ܢܥܡܕܟܘܢ ܒܪܘܚܐ ܕܩܘܕܫܐ ܘܒܢܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 17, 'ܗܘ ܕܐܚܝܕ ܪܦܫܐ ܒܐܝܕܗ ܘܡܕܟܐ ܐܕܪܘܗܝ ܘܚܛܐ ܟܢܫ ܠܐܘܨܪܘܗܝ ܘܬܒܢܐ ܢܘܩܕ ܒܢܘܪܐ ܕܠܐ ܕܥܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 18, 'ܐܦ ܐܚܪܢܝܬܐ ܕܝܢ ܤܓܝܐܬܐ ܡܠܦ ܗܘܐ ܘܡܤܒܪ ܠܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 19, 'ܗܪܘܕܤ ܕܝܢ ܛܛܪܪܟܐ ܡܛܠ ܕܡܬܟܤܤ ܗܘܐ ܡܢ ܝܘܚܢܢ ܥܠ ܗܪܘܕܝܐ ܐܢܬܬ ܦܝܠܝܦܘܤ ܐܚܘܗܝ ܘܥܠ ܟܠܗܝܢ ܒܝܫܬܐ ܕܥܒܕ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 20, 'ܐܘܤܦ ܐܦ ܗܕܐ ܥܠ ܟܠܗܝܢ ܘܚܒܫܗ ܠܝܘܚܢܢ ܒܝܬ ܐܤܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 21, 'ܗܘܐ ܕܝܢ ܟܕ ܥܡܕ ܟܠܗ ܥܡܐ ܘܐܦ ܝܫܘܥ ܥܡܕ ܘܟܕ ܡܨܠܐ ܐܬܦܬܚܘ ܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 22, 'ܘܢܚܬܬ ܪܘܚܐ ܕܩܘܕܫܐ ܥܠܘܗܝ ܒܕܡܘܬ ܓܘܫܡܐ ܕܝܘܢܐ ܘܩܠܐ ܗܘܐ ܡܢ ܫܡܝܐ ܕܐܡܪ ܐܢܬ ܗܘ ܒܪܝ ܚܒܝܒܐ ܕܒܟ ܐܨܛܒܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 23, 'ܗܘ ܕܝܢ ܝܫܘܥ ܐܝܬܘܗܝ ܗܘܐ ܐܝܟ ܒܪ ܫܢܝܢ ܬܠܬܝܢ ܘܡܤܬܒܪ ܗܘܐ ܒܪ ܝܘܤܦ ܒܪ ܗܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 24, 'ܒܪ ܡܛܬܬ ܒܪ ܠܘܝ ܒܪ ܡܠܟܝ ܒܪ ܝܐܢܝ ܒܪ ܝܘܤܦ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 25, 'ܒܪ ܡܬܬܐ ܒܪ ܥܡܘܨ ܒܪ ܢܚܘܡ ܒܪ ܚܤܠܝ ܒܪ ܢܓܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 26, 'ܒܪ ܡܐܬ ܒܪ ܡܛܬ ܒܪ ܫܡܥܝ ܒܪ ܝܘܤܦ ܒܪ ܝܗܘܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 27, 'ܒܪ ܝܘܚܢܢ ܒܪ ܪܤܐ ܒܪ ܙܘܪܒܒܠ ܒܪ ܫܠܬܐܝܠ ܒܪ ܢܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 28, 'ܒܪ ܡܠܟܝ ܒܪ ܐܕܝ ܒܪ ܩܘܤܡ ܒܪ ܐܠܡܘܕܕ ܒܪ ܥܝܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 29, 'ܒܪ ܝܘܤܐ ܒܪ ܐܠܝܥܙܪ ܒܪ ܝܘܪܡ ܒܪ ܡܬܝܬܐ ܒܪ ܠܘܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 30, 'ܒܪ ܫܡܥܘܢ ܒܪ ܝܗܘܕܐ ܒܪ ܝܘܤܦ ܒܪ ܝܘܢܡ ܒܪ ܐܠܝܩܝܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 31, 'ܒܪ ܡܠܝܐ ܒܪ ܡܐܢܝ ܒܪ ܡܛܬܐ ܒܪ ܢܬܢ ܒܪ ܕܘܝܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 32, 'ܒܪ ܐܝܫܝ ܒܪ ܥܘܒܝܕ ܒܪ ܒܥܙ ܒܪ ܤܠܡܘܢ ܒܪ ܢܚܫܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 33, 'ܒܪ ܥܡܝܢܕܒ ܒܪ ܐܪܡ ܒܪ ܚܨܪܘܢ ܒܪ ܦܪܨ ܒܪ ܝܗܘܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 34, 'ܒܪ ܝܥܩܘܒ ܒܪ ܐܝܤܚܩ ܒܪ ܐܒܪܗܡ ܒܪ ܬܪܚ ܒܪ ܢܚܘܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 35, 'ܒܪ ܤܪܘܓ ܒܪ ܐܪܥܘ ܒܪ ܦܠܓ ܒܪ ܥܒܪ ܒܪ ܫܠܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 36, 'ܒܪ ܩܝܢܢ ܒܪ ܐܪܦܟܫܪ ܒܪ ܫܝܡ ܒܪ ܢܘܚ ܒܪ ܠܡܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 37, 'ܒܪ ܡܬܘܫܠܚ ܒܪ ܚܢܘܟ ܒܪ ܝܪܕ ܒܪ ܡܗܠܠܐܝܠ ܒܪ ܩܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 38, 'ܒܪ ܐܢܘܫ ܒܪ ܫܝܬ ܒܪ ܐܕܡ ܕܡܢ ܐܠܗܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 1, 'ܝܫܘܥ ܕܝܢ ܟܕ ܡܠܐ ܪܘܚܐ ܕܩܘܕܫܐ ܗܦܟ ܡܢ ܝܘܪܕܢܢ ܘܕܒܪܬܗ ܪܘܚܐ ܠܚܘܪܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 2, 'ܝܘܡܬܐ ܐܪܒܥܝܢ ܕܢܬܢܤܐ ܡܢ ܐܟܠܩܪܨܐ ܘܠܐ ܠܥܤ ܡܕܡ ܒܗܢܘܢ ܝܘܡܬܐ ܘܟܕ ܫܠܡ ܐܢܘܢ ܠܚܪܬܐ ܟܦܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 3, 'ܘܐܡܪ ܠܗ ܐܟܠܩܪܨܐ ܐܢ ܒܪܗ ܐܢܬ ܕܐܠܗܐ ܐܡܪ ܠܟܐܦܐ ܗܕܐ ܕܬܗܘܐ ܠܚܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 4, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗ ܟܬܝܒ ܗܘ ܕܠܐ ܗܘܐ ܒܠܚܡܐ ܒܠܚܘܕ ܚܝܐ ܒܪܢܫܐ ܐܠܐ ܒܟܠ ܦܬܓܡ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 5, 'ܘܐܤܩܗ ܤܛܢܐ ܠܛܘܪܐ ܪܡܐ ܘܚܘܝܗ ܟܠܗܝܢ ܡܠܟܘܬܐ ܕܐܪܥܐ ܒܥܕܢܐ ܙܥܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 6, 'ܘܐܡܪ ܠܗ ܐܟܠܩܪܨܐ ܠܟ ܐܬܠ ܫܘܠܛܢܐ ܗܢܐ ܟܠܗ ܘܫܘܒܚܗ ܕܠܝ ܡܫܠܡ ܘܠܡܢ ܕܐܨܒܐ ܝܗܒ ܐܢܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 7, 'ܐܢ ܗܟܝܠ ܬܤܓܘܕ ܩܕܡܝ ܕܝܠܟ ܢܗܘܐ ܟܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 8, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗ ܟܬܝܒ ܗܘ ܕܠܡܪܝܐ ܐܠܗܟ ܬܤܓܘܕ ܘܠܗ ܒܠܚܘܕܘܗܝ ܬܦܠܘܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 9, 'ܘܐܝܬܝܗ ܠܐܘܪܫܠܡ ܘܐܩܝܡܗ ܥܠ ܟܢܦܐ ܕܗܝܟܠܐ ܘܐܡܪ ܠܗ ܐܢ ܒܪܗ ܐܢܬ ܕܐܠܗܐ ܐܪܡܐ ܢܦܫܟ ܡܟܐ ܠܬܚܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 10, 'ܟܬܝܒ ܓܝܪ ܕܠܡܠܐܟܘܗܝ ܢܦܩܕ ܥܠܝܟ ܕܢܢܛܪܘܢܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 11, 'ܘܥܠ ܕܪܥܝܗܘܢ ܢܫܩܠܘܢܟ ܕܠܐ ܬܬܩܠ ܪܓܠܟ ܒܟܐܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 12, 'ܥܢܐ ܕܝܢ ܝܫܘܥ ܘܐܡܪ ܠܗ ܐܡܝܪ ܗܘ ܕܠܐ ܬܢܤܐ ܠܡܪܝܐ ܐܠܗܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 13, 'ܘܟܕ ܫܠܡ ܐܟܠܩܪܨܐ ܟܠܗܘܢ ܢܤܝܘܢܘܗܝ ܦܪܩ ܡܢ ܠܘܬܗ ܥܕ ܙܒܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 14, 'ܘܗܦܟ ܝܫܘܥ ܒܚܝܠܐ ܕܪܘܚܐ ܠܓܠܝܠܐ ܘܢܦܩ ܥܠܘܗܝ ܛܒܐ ܒܟܠܗ ܐܬܪܐ ܕܚܕܪܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 15, 'ܘܗܘ ܡܠܦ ܗܘܐ ܒܟܢܘܫܬܗܘܢ ܘܡܫܬܒܚ ܗܘܐ ܡܢ ܟܠ ܐܢܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 16, 'ܘܐܬܐ ܠܢܨܪܬ ܐܝܟܐ ܕܐܬܪܒܝ ܘܥܠ ܐܝܟܢܐ ܕܡܥܕ ܗܘܐ ܠܟܢܘܫܬܐ ܒܝܘܡܐ ܕܫܒܬܐ ܘܩܡ ܠܡܩܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 17, 'ܘܐܬܝܗܒ ܠܗ ܤܦܪܐ ܕܐܫܥܝܐ ܢܒܝܐ ܘܦܬܚ ܝܫܘܥ ܤܦܪܐ ܘܐܫܟܚ ܕܘܟܬܐ ܐܝܟܐ ܕܟܬܝܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 18, 'ܪܘܚܗ ܕܡܪܝܐ ܥܠܝ ܘܡܛܠ ܗܕܐ ܡܫܚܢܝ ܠܡܤܒܪܘ ܠܡܤܟܢܐ ܘܫܠܚܢܝ ܠܡܐܤܝܘ ܠܬܒܝܪܝ ܠܒܐ ܘܠܡܟܪܙܘ ܠܫܒܝܐ ܫܘܒܩܢܐ ܘܠܥܘܝܪܐ ܚܙܝܐ ܘܠܡܫܪܪܘ ܠܬܒܝܪܐ ܒܫܘܒܩܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 19, 'ܘܠܡܟܪܙܘ ܫܢܬܐ ܡܩܒܠܬܐ ܠܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 20, 'ܘܟܪܟ ܤܦܪܐ ܘܝܗܒܗ ܠܡܫܡܫܢܐ ܘܐܙܠ ܝܬܒ ܟܠܗܘܢ ܕܝܢ ܕܒܟܢܘܫܬܐ ܥܝܢܝܗܘܢ ܚܝܪܢ ܗܘܝ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 21, 'ܘܫܪܝ ܠܡܐܡܪ ܠܘܬܗܘܢ ܕܝܘܡܢܐ ܐܫܬܠܡ ܟܬܒܐ ܗܢܐ ܒܐܕܢܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 22, 'ܘܤܗܕܝܢ ܗܘܘ ܠܗ ܟܠܗܘܢ ܘܡܬܕܡܪܝܢ ܗܘܘ ܒܡܠܐ ܕܛܝܒܘܬܐ ܕܢܦܩܢ ܗܘܝ ܡܢ ܦܘܡܗ ܘܐܡܪܝܢ ܗܘܘ ܠܐ ܗܘܐ ܗܢܐ ܒܪ ܝܘܤܦ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 23, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܟܒܪ ܬܐܡܪܘܢ ܠܝ ܡܬܠܐ ܗܢܐ ܐܤܝܐ ܐܤܐ ܢܦܫܟ ܘܟܠ ܕܫܡܥܢ ܕܥܒܕܬ ܒܟܦܪܢܚܘܡ ܥܒܕ ܐܦ ܗܪܟܐ ܒܡܕܝܢܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 24, 'ܗܘ ܕܝܢ ܐܡܪ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܠܝܬ ܢܒܝܐ ܕܡܬܩܒܠ ܒܡܕܝܢܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 25, 'ܫܪܪܐ ܓܝܪ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܤܓܝ ܐܪܡܠܬܐ ܐܝܬ ܗܘܝ ܒܐܝܤܪܝܠ ܒܝܘܡܝ ܐܠܝܐ ܢܒܝܐ ܟܕ ܐܬܬܚܕܘ ܫܡܝܐ ܫܢܝܢ ܬܠܬ ܘܝܪܚܐ ܫܬܐ ܘܗܘܐ ܟܦܢܐ ܪܒܐ ܒܟܠܗ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 26, 'ܘܠܘܬ ܚܕܐ ܡܢܗܝܢ ܠܐ ܐܫܬܕܪ ܐܠܝܐ ܐܠܐ ܠܨܪܦܬ ܕܨܝܕܢ ܠܘܬ ܐܢܬܬܐ ܐܪܡܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 27, 'ܘܤܓܝܐܐ ܓܪܒܐ ܐܝܬ ܗܘܘ ܒܝܬ ܐܝܤܪܝܠ ܒܝܘܡܝ ܐܠܝܫܥ ܢܒܝܐ ܘܚܕ ܡܢܗܘܢ ܠܐ ܐܬܕܟܝ ܐܠܐ ܐܢ ܢܥܡܢ ܐܪܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 28, 'ܘܟܕ ܫܡܥܘ ܗܠܝܢ ܗܢܘܢ ܕܒܟܢܘܫܬܐ ܐܬܡܠܝܘ ܚܡܬܐ ܟܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 29, 'ܘܩܡܘ ܐܦܩܘܗܝ ܠܒܪ ܡܢ ܡܕܝܢܬܐ ܘܐܝܬܝܘܗܝ ܥܕܡܐ ܠܓܒܝܢܐ ܕܛܘܪܐ ܗܘ ܕܡܕܝܢܬܗܘܢ ܒܢܝܐ ܗܘܬ ܥܠܘܗܝ ܕܢܫܕܘܢܝܗܝ ܡܢ ܫܩܝܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 30, 'ܗܘ ܕܝܢ ܥܒܪ ܒܝܢܬܗܘܢ ܘܐܙܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 31, 'ܘܢܚܬ ܠܟܦܪܢܚܘܡ ܡܕܝܢܬܐ ܕܓܠܝܠܐ ܘܡܠܦ ܗܘܐ ܠܗܘܢ ܒܫܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 32, 'ܘܬܡܝܗܝܢ ܗܘܘ ܒܝܘܠܦܢܗ ܕܡܫܠܛܐ ܗܘܬ ܡܠܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 33, 'ܘܐܝܬ ܗܘܐ ܒܟܢܘܫܬܐ ܓܒܪܐ ܕܐܝܬ ܗܘܐ ܒܗ ܪܘܚܐ ܕܫܐܕܐ ܛܢܦܐ ܘܙܥܩ ܒܩܠܐ ܪܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 34, 'ܘܐܡܪ ܫܒܘܩܝܢܝ ܡܐ ܠܢ ܘܠܟ ܝܫܘܥ ܢܨܪܝܐ ܐܬܝܬ ܠܡܘܒܕܘܬܢ ܝܕܥ ܐܢܐ ܠܟ ܡܢ ܐܢܬ ܩܕܝܫܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 35, 'ܘܟܐܐ ܒܗ ܝܫܘܥ ܘܐܡܪ ܤܟܘܪ ܦܘܡܟ ܘܦܘܩ ܡܢܗ ܘܫܕܝܗܝ ܫܐܕܐ ܒܡܨܥܬܐ ܘܢܦܩ ܡܢܗ ܟܕ ܠܐ ܤܪܚ ܒܗ ܡܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 36, 'ܘܬܡܗܐ ܪܒܐ ܐܚܕ ܠܟܠܢܫ ܘܡܡܠܠܝܢ ܗܘܘ ܥܡ ܚܕܕܐ ܘܐܡܪܝܢ ܡܢܐ ܗܝ ܟܝ ܡܠܬܐ ܗܕܐ ܕܒܫܘܠܛܢܐ ܘܒܚܝܠܐ ܦܩܕܐ ܠܪܘܚܐ ܛܢܦܬܐ ܘܢܦܩܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 37, 'ܘܢܦܩ ܥܠܘܗܝ ܛܒܐ ܒܟܠܗ ܐܬܪܐ ܕܚܕܪܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 38, 'ܘܟܕ ܢܦܩ ܝܫܘܥ ܡܢ ܟܢܘܫܬܐ ܥܠ ܠܒܝܬܗ ܕܫܡܥܘܢ ܘܚܡܬܗ ܕܫܡܥܘܢ ܐܠܝܨܐ ܗܘܬ ܒܐܫܬܐ ܪܒܬܐ ܘܒܥܘ ܡܢܗ ܡܛܠܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 39, 'ܘܩܡ ܠܥܠ ܡܢܗ ܘܟܐܐ ܒܐܫܬܗ ܘܫܒܩܬܗ ܘܡܚܕܐ ܩܡܬ ܘܡܫܡܫܐ ܗܘܬ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 40, 'ܡܥܪܒܝ ܫܡܫܐ ܕܝܢ ܟܠܗܘܢ ܐܝܠܝܢ ܕܐܝܬ ܗܘܐ ܠܗܘܢ ܟܪܝܗܐ ܕܟܪܝܗܝܢ ܒܟܘܪܗܢܐ ܡܫܚܠܦܐ ܐܝܬܝܘ ܐܢܘܢ ܠܘܬܗ ܗܘ ܕܝܢ ܥܠ ܚܕ ܚܕ ܡܢܗܘܢ ܐܝܕܗ ܤܐܡ ܗܘܐ ܘܡܐܤܐ ܗܘܐ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 41, 'ܘܢܦܩܝܢ ܗܘܘ ܐܦ ܫܐܕܐ ܡܢ ܤܓܝܐܐ ܟܕ ܡܙܥܩܝܢ ܘܐܡܪܝܢ ܕܐܢܬ ܗܘ ܡܫܝܚܐ ܒܪܗ ܕܐܠܗܐ ܘܟܐܐ ܗܘܐ ܒܗܘܢ ܘܠܐ ܫܒܩ ܗܘܐ ܠܗܘܢ ܕܢܐܡܪܘܢ ܕܝܕܥܝܢ ܕܗܘܝܘ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 42, 'ܘܠܨܦܪܗ ܕܝܘܡܐ ܢܦܩ ܐܙܠ ܠܗ ܠܐܬܪܐ ܚܘܪܒܐ ܘܟܢܫܐ ܒܥܝܢ ܗܘܘ ܠܗ ܘܐܬܘ ܥܕܡܐ ܠܘܬܗ ܘܐܚܕܘܗܝ ܕܠܐ ܢܐܙܠ ܠܗ ܡܢ ܠܘܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 43, 'ܗܘ ܕܝܢ ܝܫܘܥ ܐܡܪ ܠܗܘܢ ܕܐܦ ܠܡܕܝܢܬܐ ܐܚܪܢܝܬܐ ܘܠܐ ܠܝ ܠܡܤܒܪܘ ܡܠܟܘܬܗ ܕܐܠܗܐ ܕܥܠ ܗܕܐ ܗܘ ܐܫܬܕܪܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 44, 'ܘܗܘ ܡܟܪܙ ܗܘܐ ܒܟܢܘܫܬܐ ܕܓܠܝܠܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 1, 'ܗܘܐ ܕܝܢ ܟܕ ܟܢܫ ܥܠܘܗܝ ܟܢܫܐ ܠܡܫܡܥ ܡܠܬܐ ܕܐܠܗܐ ܘܗܘ ܩܐܡ ܗܘܐ ܥܠ ܝܕ ܝܡܬܐ ܕܓܢܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 2, 'ܚܙܐ ܤܦܝܢܐ ܬܪܬܝܢ ܕܩܝܡܢ ܥܠ ܓܢܒ ܝܡܬܐ ܘܨܝܕܐ ܕܤܠܩܘ ܡܢܗܝܢ ܘܡܫܝܓܝܢ ܡܨܝܕܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 3, 'ܘܚܕܐ ܡܢܗܝܢ ܕܫܡܥܘܢ ܗܘܬ ܟܐܦܐ ܘܤܠܩ ܝܫܘܥ ܝܬܒ ܒܗ ܘܐܡܪ ܕܢܕܒܪܘܢܗ ܩܠܝܠ ܡܢ ܝܒܫܐ ܠܡܝܐ ܘܝܬܒ ܗܘܐ ܘܡܠܦ ܡܢ ܤܦܝܢܬܐ ܠܟܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 4, 'ܘܟܕ ܫܬܩ ܡܢ ܡܡܠܠܗ ܐܡܪ ܠܫܡܥܘܢ ܕܒܪܘ ܠܥܘܡܩܐ ܘܐܪܡܘ ܡܨܝܕܬܟܘܢ ܠܨܝܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 5, 'ܥܢܐ ܫܡܥܘܢ ܘܐܡܪ ܠܗ ܪܒܝ ܠܠܝܐ ܟܠܗ ܠܐܝܢ ܘܡܕܡ ܠܐ ܐܚܕܢ ܥܠ ܡܠܬܟ ܕܝܢ ܪܡܐ ܐܢܐ ܡܨܝܕܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 6, 'ܘܟܕ ܗܕܐ ܥܒܕܘ ܚܒܫܘ ܢܘܢܐ ܤܓܝܐܐ ܕܛܒ ܘܡܨܛܪܝܐ ܗܘܬ ܡܨܝܕܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 7, 'ܘܪܡܙܘ ܠܚܒܪܝܗܘܢ ܕܒܤܦܝܢܬܐ ܐܚܪܬܐ ܕܢܐܬܘܢ ܢܥܕܪܘܢ ܐܢܘܢ ܘܟܕ ܐܬܘ ܡܠܘ ܐܢܝܢ ܤܦܝܢܐ ܬܪܬܝܗܝܢ ܐܝܟ ܕܩܪܝܒܢ ܗܘܝ ܠܡܛܒܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 8, 'ܟܕ ܚܙܐ ܕܝܢ ܫܡܥܘܢ ܟܐܦܐ ܢܦܠ ܩܕܡ ܪܓܠܘܗܝ ܕܝܫܘܥ ܘܐܡܪ ܠܗ ܒܥܐ ܐܢܐ ܡܢܟ ܡܪܝ ܦܪܘܩ ܠܟ ܡܢܝ ܕܓܒܪܐ ܐܢܐ ܚܛܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 9, 'ܬܡܗܐ ܓܝܪ ܐܚܕܗ ܗܘܐ ܘܠܟܠܗܘܢ ܕܥܡܗ ܥܠ ܨܝܕܐ ܗܘ ܕܢܘܢܐ ܕܨܕܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 10, 'ܗܟܘܬ ܕܝܢ ܐܦ ܠܝܥܩܘܒ ܘܠܝܘܚܢܢ ܒܢܝ ܙܒܕܝ ܕܐܝܬܝܗܘܢ ܗܘܘ ܫܘܬܦܐ ܕܫܡܥܘܢ ܐܡܪ ܕܝܢ ܝܫܘܥ ܠܫܡܥܘܢ ܠܐ ܬܕܚܠ ܡܢ ܗܫܐ ܒܢܝ ܐܢܫܐ ܬܗܘܐ ܨܐܕ ܠܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 11, 'ܘܩܪܒܘ ܐܢܝܢ ܤܦܝܢܐ ܠܐܪܥܐ ܘܫܒܩܘ ܟܠ ܡܕܡ ܘܐܬܘ ܒܬܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 12, 'ܘܟܕ ܗܘܐ ܝܫܘܥ ܒܚܕܐ ܡܢ ܡܕܝܢܬܐ ܐܬܐ ܓܒܪܐ ܕܡܠܐ ܟܠܗ ܓܪܒܐ ܚܙܐ ܠܝܫܘܥ ܘܢܦܠ ܥܠ ܐܦܘܗܝ ܘܒܥܐ ܗܘܐ ܡܢܗ ܘܐܡܪ ܠܗ ܡܪܝ ܐܢ ܨܒܐ ܐܢܬ ܡܫܟܚ ܐܢܬ ܠܡܕܟܝܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 13, 'ܘܦܫܛ ܐܝܕܗ ܝܫܘܥ ܩܪܒ ܠܗ ܘܐܡܪ ܠܗ ܨܒܐ ܐܢܐ ܐܬܕܟܐ ܘܒܪ ܫܥܬܗ ܐܙܠ ܡܢܗ ܓܪܒܗ ܘܐܬܕܟܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 14, 'ܘܦܩܕܗ ܕܠܐܢܫ ܠܐ ܬܐܡܪ ܐܠܐ ܙܠ ܚܘܐ ܢܦܫܟ ܠܟܗܢܐ ܘܩܪܒ ܚܠܦ ܬܕܟܝܬܟ ܐܝܟܢܐ ܕܦܩܕ ܡܘܫܐ ܠܤܗܕܘܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 15, 'ܘܢܦܩ ܥܠܘܗܝ ܛܒܐ ܝܬܝܪܐܝܬ ܘܡܬܟܢܫ ܗܘܐ ܥܡܐ ܤܓܝܐܐ ܠܡܫܡܥ ܡܢܗ ܘܠܡܬܐܤܝܘ ܡܢ ܟܘܪܗܢܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 16, 'ܗܘ ܕܝܢ ܡܫܢܐ ܗܘܐ ܠܕܒܪܐ ܘܡܨܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 17, 'ܘܗܘܐ ܒܚܕ ܡܢ ܝܘܡܬܐ ܟܕ ܡܠܦ ܗܘܐ ܝܫܘܥ ܝܬܒܝܢ ܗܘܘ ܦܪܝܫܐ ܘܡܠܦܝ ܢܡܘܤܐ ܕܐܬܘ ܗܘܘ ܡܢ ܟܠ ܩܘܪܝܐ ܕܓܠܝܠܐ ܘܕܝܗܘܕ ܘܕܐܘܪܫܠܡ ܘܚܝܠܐ ܕܡܪܝܐ ܐܝܬܘܗܝ ܗܘܐ ܠܡܐܤܝܘܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 18, 'ܘܐܢܫܐ ܐܝܬܝܘ ܒܥܪܤܐ ܓܒܪܐ ܚܕ ܡܫܪܝܐ ܘܒܥܝܢ ܗܘܘ ܕܢܥܠܘܢ ܢܤܝܡܘܢܝܗܝ ܩܕܡܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 19, 'ܘܟܕ ܠܐ ܐܫܟܚܘ ܕܐܝܟܢܐ ܢܥܠܘܢܝܗܝ ܡܛܠ ܤܘܓܐܐ ܕܥܡܐ ܤܠܩܘ ܠܗܘܢ ܠܐܓܪܐ ܘܫܒܘܗܝ ܥܡ ܥܪܤܗ ܡܢ ܬܛܠܝܠܐ ܠܡܨܥܬܐ ܩܕܡܘܗܝ ܕܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 20, 'ܟܕ ܚܙܐ ܕܝܢ ܝܫܘܥ ܗܝܡܢܘܬܗܘܢ ܐܡܪ ܠܗܘ ܡܫܪܝܐ ܓܒܪܐ ܫܒܝܩܝܢ ܠܟ ܚܛܗܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 21, 'ܘܫܪܝܘ ܤܦܪܐ ܘܦܪܝܫܐ ܡܬܚܫܒܝܢ ܘܐܡܪܝܢ ܡܢܘ ܗܢܐ ܕܡܡܠܠ ܓܘܕܦܐ ܡܢܘ ܡܫܟܚ ܠܡܫܒܩ ܚܛܗܐ ܐܠܐ ܐܢ ܐܠܗܐ ܒܠܚܘܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 22, 'ܝܫܘܥ ܕܝܢ ܝܕܥ ܡܚܫܒܬܗܘܢ ܘܥܢܐ ܘܐܡܪ ܠܗܘܢ ܡܢܐ ܡܬܚܫܒܝܢ ܐܢܬܘܢ ܒܠܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 23, 'ܐܝܕܐ ܦܫܝܩܐ ܠܡܐܡܪ ܕܫܒܝܩܝܢ ܠܟ ܚܛܗܝܟ ܐܘ ܠܡܐܡܪ ܩܘܡ ܗܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 24, 'ܕܬܕܥܘܢ ܕܝܢ ܕܫܠܝܛ ܗܘ ܒܪܗ ܕܐܢܫܐ ܒܐܪܥܐ ܕܢܫܒܘܩ ܚܛܗܐ ܐܡܪ ܠܡܫܪܝܐ ܠܟ ܐܡܪ ܐܢܐ ܩܘܡ ܫܩܘܠ ܥܪܤܟ ܘܙܠ ܠܒܝܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 25, 'ܘܡܚܕܐ ܩܡ ܠܥܢܝܗܘܢ ܘܫܩܠ ܥܪܤܗ ܘܐܙܠ ܠܒܝܬܗ ܟܕ ܡܫܒܚ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 26, 'ܘܬܡܗܐ ܐܚܕ ܠܟܠܢܫ ܘܡܫܒܚܝܢ ܗܘܘ ܠܐܠܗܐ ܘܐܬܡܠܝܘ ܕܚܠܬܐ ܘܐܡܪܝܢ ܕܚܙܝܢ ܝܘܡܢܐ ܬܕܡܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 27, 'ܒܬܪ ܗܠܝܢ ܢܦܩ ܝܫܘܥ ܘܚܙܐ ܡܟܤܐ ܕܫܡܗ ܠܘܝ ܕܝܬܒ ܒܝܬ ܡܟܤܐ ܘܐܡܪ ܠܗ ܬܐ ܒܬܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 28, 'ܘܫܒܩ ܟܠ ܡܕܡ ܘܩܡ ܐܙܠ ܒܬܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 29, 'ܘܥܒܕ ܠܗ ܠܘܝ ܒܒܝܬܗ ܩܘܒܠܐ ܪܒܐ ܘܐܝܬ ܗܘܐ ܟܢܫܐ ܤܓܝܐܐ ܕܡܟܤܐ ܘܕܐܚܪܢܐ ܕܤܡܝܟܝܢ ܗܘܘ ܥܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 30, 'ܘܪܛܢܝܢ ܗܘܘ ܤܦܪܐ ܘܦܪܝܫܐ ܘܐܡܪܝܢ ܠܬܠܡܝܕܘܗܝ ܠܡܢܐ ܥܡ ܡܟܤܐ ܘܚܛܝܐ ܐܟܠܝܢ ܐܢܬܘܢ ܘܫܬܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 31, 'ܘܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܠܐ ܡܬܒܥܐ ܐܤܝܐ ܠܚܠܝܡܐ ܐܠܐ ܠܐܝܠܝܢ ܕܒܝܫ ܒܝܫ ܥܒܝܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 32, 'ܠܐ ܐܬܝܬ ܕܐܩܪܐ ܠܙܕܝܩܐ ܐܠܐ ܠܚܛܝܐ ܠܬܝܒܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 33, 'ܗܢܘܢ ܕܝܢ ܐܡܪܝܢ ܠܗ ܠܡܢܐ ܬܠܡܝܕܘܗܝ ܕܝܘܚܢܢ ܨܝܡܝܢ ܐܡܝܢܐܝܬ ܘܡܨܠܝܢ ܐܦ ܕܦܪܝܫܐ ܕܝܠܟ ܕܝܢ ܐܟܠܝܢ ܘܫܬܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 34, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܠܐ ܡܫܟܚܝܢ ܐܢܬܘܢ ܠܒܢܘܗܝ ܕܓܢܘܢܐ ܟܡܐ ܕܚܬܢܐ ܥܡܗܘܢ ܕܬܥܒܕܘܢ ܕܢܨܘܡܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 35, 'ܢܐܬܘܢ ܕܝܢ ܝܘܡܬܐ ܟܕ ܢܬܬܪܝܡ ܚܬܢܐ ܡܢܗܘܢ ܗܝܕܝܢ ܢܨܘܡܘܢ ܒܗܢܘܢ ܝܘܡܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 36, 'ܘܐܡܪ ܠܗܘܢ ܡܬܠܐ ܕܠܐ ܐܢܫ ܩܐܕ ܐܘܪܩܥܬܐ ܡܢ ܡܐܢܐ ܚܕܬܐ ܘܪܡܐ ܥܠ ܡܐܢܐ ܒܠܝܐ ܕܠܐ ܠܚܕܬܐ ܩܐܕ ܘܠܒܠܝܐ ܠܐ ܫܠܡܐ ܐܘܪܩܥܬܐ ܕܡܢ ܚܕܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 37, 'ܘܠܐ ܐܢܫ ܪܡܐ ܚܡܪܐ ܚܕܬܐ ܒܙܩܐ ܒܠܝܬܐ ܘܐܢ ܕܝܢ ܠܐ ܡܒܙܥ ܚܡܪܐ ܚܕܬܐ ܠܙܩܐ ܘܗܘ ܚܡܪܐ ܡܬܐܫܕ ܘܙܩܐ ܐܒܕܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 38, 'ܐܠܐ ܚܡܪܐ ܚܕܬܐ ܒܙܩܐ ܚܕܬܬܐ ܪܡܝܢ ܘܬܪܝܗܘܢ ܡܬܢܛܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 39, 'ܘܠܐ ܐܢܫ ܫܬܐ ܚܡܪܐ ܥܬܝܩܐ ܘܡܚܕܐ ܒܥܐ ܚܕܬܐ ܐܡܪ ܓܝܪ ܥܬܝܩܐ ܒܤܝܡ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 1, 'ܗܘܐ ܕܝܢ ܒܫܒܬܐ ܟܕ ܡܗܠܟ ܝܫܘܥ ܒܝܬ ܙܪܥܐ ܬܠܡܝܕܘܗܝ ܡܠܓܝܢ ܗܘܘ ܫܒܠܐ ܘܦܪܟܝܢ ܒܐܝܕܝܗܘܢ ܘܐܟܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 2, 'ܐܢܫܝܢ ܕܝܢ ܡܢ ܦܪܝܫܐ ܐܡܪܝܢ ܠܗܘܢ ܡܢܐ ܥܒܕܝܢ ܐܢܬܘܢ ܡܕܡ ܕܠܐ ܫܠܝܛ ܠܡܥܒܕ ܒܫܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 3, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܘܠܐ ܗܕܐ ܩܪܝܬܘܢ ܡܕܡ ܕܥܒܕ ܕܘܝܕ ܟܕ ܟܦܢ ܗܘ ܘܐܝܠܝܢ ܕܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 4, 'ܕܥܠ ܠܒܝܬܗ ܕܐܠܗܐ ܘܠܚܡܐ ܕܦܬܘܪܗ ܕܡܪܝܐ ܢܤܒ ܐܟܠ ܘܝܗܒ ܠܐܝܠܝܢ ܕܥܡܗ ܗܘ ܕܠܐ ܫܠܝܛ ܗܘܐ ܠܡܐܟܠ ܐܠܐ ܠܟܗܢܐ ܒܠܚܘܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 5, 'ܘܐܡܪ ܠܗܘܢ ܕܡܪܗ ܗܘ ܕܫܒܬܐ ܒܪܗ ܕܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 6, 'ܗܘܐ ܕܝܢ ܠܫܒܬܐ ܐܚܪܬܐ ܥܠ ܠܟܢܘܫܬܐ ܘܡܠܦ ܗܘܐ ܘܐܝܬ ܗܘܐ ܬܡܢ ܓܒܪܐ ܕܐܝܕܗ ܕܝܡܝܢܐ ܝܒܝܫܐ ܗܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 7, 'ܘܤܦܪܐ ܘܦܪܝܫܐ ܢܛܪܝܢ ܗܘܘ ܠܗ ܕܐܢ ܗܘ ܕܡܐܤܐ ܒܫܒܬܐ ܕܢܫܟܚܘܢ ܢܐܟܠܘܢ ܩܪܨܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 8, 'ܗܘ ܕܝܢ ܝܕܥ ܡܚܫܒܬܗܘܢ ܘܐܡܪ ܠܗܘ ܓܒܪܐ ܕܝܒܝܫܐ ܐܝܕܗ ܩܘܡ ܬܐ ܠܟ ܠܡܨܥܬ ܟܢܘܫܬܐ ܘܟܕ ܐܬܐ ܘܩܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 9, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܫܐܠܟܘܢ ܡܢܐ ܫܠܝܛ ܒܫܒܬܐ ܕܛܒ ܠܡܥܒܕ ܐܘ ܕܒܝܫ ܢܦܫܐ ܠܡܚܝܘ ܐܘ ܠܡܘܒܕܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 10, 'ܘܚܪ ܒܗܘܢ ܒܟܠܗܘܢ ܘܐܡܪ ܠܗ ܦܫܘܛ ܐܝܕܟ ܘܦܫܛ ܘܬܩܢܬ ܐܝܕܗ ܐܝܟ ܚܒܪܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 11, 'ܗܢܘܢ ܕܝܢ ܐܬܡܠܝܘ ܚܤܡܐ ܘܡܡܠܠܝܢ ܗܘܘ ܚܕ ܥܡ ܚܕ ܕܡܢܐ ܢܥܒܕܘܢ ܠܗ ܠܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 12, 'ܗܘܐ ܕܝܢ ܒܝܘܡܬܐ ܗܢܘܢ ܢܦܩ ܝܫܘܥ ܠܛܘܪܐ ܠܡܨܠܝܘ ܘܬܡܢ ܐܓܗ ܗܘܐ ܒܨܠܘܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 13, 'ܘܟܕ ܢܓܗܬ ܩܪܐ ܠܬܠܡܝܕܘܗܝ ܘܓܒܐ ܡܢܗܘܢ ܬܪܥܤܪ ܗܢܘܢ ܕܫܠܝܚܐ ܫܡܝ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 14, 'ܫܡܥܘܢ ܗܘ ܕܫܡܝ ܟܐܦܐ ܘܐܢܕܪܐܘܤ ܐܚܘܗܝ ܘܝܥܩܘܒ ܘܝܘܚܢܢ ܘܦܝܠܝܦܘܤ ܘܒܪ ܬܘܠܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 15, 'ܘܡܬܝ ܘܬܐܘܡܐ ܘܝܥܩܘܒ ܒܪ ܚܠܦܝ ܘܫܡܥܘܢ ܕܡܬܩܪܐ ܛܢܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 16, 'ܘܝܗܘܕܐ ܒܪ ܝܥܩܘܒ ܘܝܗܘܕܐ ܤܟܪܝܘܛܐ ܗܘ ܕܗܘܐ ܡܫܠܡܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 17, 'ܘܢܚܬ ܥܡܗܘܢ ܝܫܘܥ ܘܩܡ ܒܦܩܥܬܐ ܘܟܢܫܐ ܤܓܝܐܐ ܕܬܠܡܝܕܘܗܝ ܘܤܘܓܐܐ ܕܟܢܫܐ ܕܥܡܐ ܡܢ ܟܠܗ ܝܗܘܕ ܘܡܢ ܐܘܪܫܠܡ ܘܡܢ ܤܦܪ ܝܡܐ ܕܨܘܪ ܘܕܨܝܕܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 18, 'ܕܐܬܘ ܕܢܫܡܥܘܢ ܡܠܬܗ ܘܕܢܬܐܤܘܢ ܡܢ ܟܘܪܗܢܝܗܘܢ ܘܐܝܠܝܢ ܕܡܬܐܠܨܝܢ ܡܢ ܪܘܚܐ ܛܢܦܬܐ ܘܡܬܐܤܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 19, 'ܘܟܠܗ ܟܢܫܐ ܒܥܝܢ ܗܘܘ ܠܡܬܩܪܒܘ ܠܗ ܚܝܠܐ ܓܝܪ ܢܦܩ ܗܘܐ ܡܢܗ ܘܠܟܠܗܘܢ ܡܐܤܐ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 20, 'ܘܐܪܝܡ ܥܝܢܘܗܝ ܥܠ ܬܠܡܝܕܘܗܝ ܘܐܡܪ ܛܘܒܝܟܘܢ ܡܤܟܢܐ ܕܕܝܠܟܘܢ ܗܝ ܡܠܟܘܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 21, 'ܛܘܒܝܟܘܢ ܐܝܠܝܢ ܕܟܦܢܝܢ ܗܫܐ ܕܬܤܒܥܘܢ ܛܘܒܝܟܘܢ ܠܕܒܟܝܢ ܗܫܐ ܕܬܓܚܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 22, 'ܛܘܒܝܟܘܢ ܡܐ ܕܤܢܝܢ ܠܟܘܢ ܒܢܝܢܫܐ ܘܡܦܪܫܝܢ ܠܟܘܢ ܘܡܚܤܕܝܢ ܠܟܘܢ ܘܡܦܩܝܢ ܫܡܟܘܢ ܐܝܟ ܒܝܫܐ ܚܠܦ ܒܪܗ ܕܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 23, 'ܚܕܘ ܒܗܘ ܝܘܡܐ ܘܕܘܨܘ ܕܐܓܪܟܘܢ ܤܓܝ ܒܫܡܝܐ ܗܟܢܐ ܓܝܪ ܥܒܕܝܢ ܗܘܘ ܐܒܗܬܗܘܢ ܠܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 24, 'ܒܪܡ ܘܝ ܠܟܘܢ ܥܬܝܪܐ ܕܩܒܠܬܘܢ ܒܘܝܐܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 25, 'ܘܝ ܠܟܘܢ ܤܒܥܐ ܕܬܟܦܢܘܢ ܘܝ ܠܟܘܢ ܠܕܓܚܟܝܢ ܗܫܐ ܕܬܒܟܘܢ ܘܬܬܐܒܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 26, 'ܘܝ ܠܟܘܢ ܟܕ ܢܗܘܘܢ ܐܡܪܝܢ ܥܠܝܟܘܢ ܒܢܝ ܐܢܫܐ ܕܫܦܝܪ ܗܟܢܐ ܓܝܪ ܥܒܕܝܢ ܗܘܘ ܠܢܒܝܐ ܕܕܓܠܘܬܐ ܐܒܗܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 27, 'ܠܟܘܢ ܕܝܢ ܐܡܪ ܐܢܐ ܠܕܫܡܥܝܢ ܐܚܒܘ ܠܒܥܠܕܒܒܝܟܘܢ ܘܥܒܕܘ ܕܫܦܝܪ ܠܐܝܠܝܢ ܕܤܢܝܢ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 28, 'ܘܒܪܟܘ ܠܐܝܠܝܢ ܕܠܝܛܝܢ ܠܟܘܢ ܘܨܠܘ ܥܠ ܐܝܠܝܢ ܕܕܒܪܝܢ ܠܟܘܢ ܒܩܛܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 29, 'ܘܠܕܡܚܐ ܠܟ ܥܠ ܦܟܟ ܩܪܒ ܠܗ ܐܚܪܢܐ ܘܡܢ ܡܢ ܕܫܩܠ ܡܪܛܘܛܟ ܠܐ ܬܟܠܐ ܐܦ ܟܘܬܝܢܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 30, 'ܠܟܠ ܕܫܐܠ ܠܟ ܗܒ ܠܗ ܘܡܢ ܡܢ ܕܫܩܠ ܕܝܠܟ ܠܐ ܬܬܒܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 31, 'ܘܐܝܟܢܐ ܕܨܒܝܢ ܐܢܬܘܢ ܕܢܥܒܕܘܢ ܠܟܘܢ ܒܢܝ ܐܢܫܐ ܗܟܘܬ ܥܒܕܘ ܠܗܘܢ ܐܦ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 32, 'ܐܢ ܓܝܪ ܡܚܒܝܢ ܐܢܬܘܢ ܠܐܝܠܝܢ ܕܡܚܒܝܢ ܠܟܘܢ ܐܝܕܐ ܗܝ ܛܝܒܘܬܟܘܢ ܐܦ ܓܝܪ ܚܛܝܐ ܠܐܝܠܝܢ ܕܡܚܒܝܢ ܠܗܘܢ ܪܚܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 33, 'ܘܐܢ ܥܒܕܝܢ ܐܢܬܘܢ ܕܛܒ ܠܐܝܠܝܢ ܕܡܛܐܒܝܢ ܠܟܘܢ ܐܝܕܐ ܗܝ ܛܝܒܘܬܟܘܢ ܐܦ ܚܛܝܐ ܓܝܪ ܗܟܢܐ ܥܒܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 34, 'ܘܐܢ ܡܘܙܦܝܢ ܐܢܬܘܢ ܠܡܢ ܕܤܒܪܝܢ ܐܢܬܘܢ ܕܬܬܦܪܥܘܢ ܡܢܗ ܐܝܕܐ ܗܝ ܛܝܒܘܬܟܘܢ ܐܦ ܚܛܝܐ ܓܝܪ ܠܚܛܝܐ ܡܘܙܦܝܢ ܕܗܟܘܬ ܢܬܦܪܥܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 35, 'ܒܪܡ ܐܚܒܘ ܠܒܥܠܕܒܒܝܟܘܢ ܘܐܛܐܒܘ ܠܗܘܢ ܘܐܘܙܦܘ ܘܠܐ ܬܦܤܩܘܢ ܤܒܪܐ ܕܐܢܫ ܘܢܗܘܐ ܤܓܝ ܐܓܪܟܘܢ ܘܬܗܘܘܢ ܒܢܘܗܝ ܕܪܡܐ ܕܗܘ ܒܤܝܡ ܗܘ ܥܠ ܒܝܫܐ ܘܥܠ ܟܦܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 36, 'ܗܘܘ ܗܟܝܠ ܡܪܚܡܢܐ ܐܝܟܢܐ ܕܐܦ ܐܒܘܟܘܢ ܡܪܚܡܢܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 37, 'ܠܐ ܬܕܘܢܘܢ ܘܠܐ ܡܬܬܕܝܢܝܢ ܐܢܬܘܢ ܠܐ ܬܚܝܒܘܢ ܘܠܐ ܡܬܚܝܒܝܢ ܐܢܬܘܢ ܫܪܘ ܘܬܫܬܪܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 38, 'ܗܒܘ ܘܡܬܝܗܒ ܠܟܘܢ ܒܟܝܠܬܐ ܛܒܬܐ ܘܪܩܝܥܬܐ ܘܡܫܦܥܬܐ ܢܪܡܘܢ ܒܥܘܒܝܟܘܢ ܒܗܝ ܓܝܪ ܟܝܠܬܐ ܕܡܟܝܠܝܢ ܐܢܬܘܢ ܡܬܬܟܝܠ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 39, 'ܘܐܡܪ ܗܘܐ ܠܗܘܢ ܡܬܠܐ ܠܡܐ ܡܫܟܚ ܤܡܝܐ ܠܤܡܝܐ ܠܡܕܒܪܘ ܠܐ ܬܪܝܗܘܢ ܒܓܘܡܨܐ ܢܦܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 40, 'ܠܝܬ ܬܠܡܝܕܐ ܕܝܬܝܪ ܡܢ ܪܒܗ ܟܠܢܫ ܓܝܪ ܕܓܡܝܪ ܢܗܘܐ ܐܝܟ ܪܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 41, 'ܡܢܐ ܕܝܢ ܚܙܐ ܐܢܬ ܓܠܐ ܕܒܥܝܢܗ ܕܐܚܘܟ ܩܪܝܬܐ ܕܝܢ ܕܒܥܝܢܟ ܠܐ ܡܬܚܙܝܐ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 42, 'ܐܘ ܐܝܟܢܐ ܡܫܟܚ ܐܢܬ ܠܡܐܡܪ ܠܐܚܘܟ ܐܚܝ ܫܒܘܩ ܐܦܩ ܓܠܐ ܡܢ ܥܝܢܟ ܕܗܐ ܩܪܝܬܐ ܕܒܥܝܢܟ ܕܝܠܟ ܠܐ ܡܬܚܙܝܐ ܠܟ ܢܤܒ ܒܐܦܐ ܐܦܩ ܠܘܩܕܡ ܩܪܝܬܐ ܡܢ ܥܝܢܟ ܘܗܝܕܝܢ ܢܬܚܙܐ ܠܟ ܠܡܦܩܘ ܓܠܐ ܡܢ ܥܝܢܗ ܕܐܚܘܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 43, 'ܠܐ ܐܝܬ ܐܝܠܢܐ ܛܒܐ ܕܥܒܕ ܦܐܪܐ ܒܝܫܐ ܐܦ ܠܐ ܐܝܠܢܐ ܒܝܫܐ ܕܥܒܕ ܦܐܪܐ ܛܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 44, 'ܟܠ ܐܝܠܢܐ ܓܝܪ ܡܢ ܦܐܪܘܗܝ ܗܘ ܡܬܝܕܥ ܠܐ ܓܝܪ ܠܩܛܝܢ ܡܢ ܟܘܒܐ ܬܐܢܐ ܐܦ ܠܐ ܡܢ ܤܢܝܐ ܩܛܦܝܢ ܥܢܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 45, 'ܓܒܪܐ ܛܒܐ ܡܢ ܤܝܡܬܐ ܛܒܬܐ ܕܒܠܒܗ ܡܦܩ ܛܒܬܐ ܘܓܒܪܐ ܒܝܫܐ ܡܢ ܤܝܡܬܐ ܒܝܫܬܐ ܕܒܠܒܗ ܡܦܩ ܒܝܫܬܐ ܡܢ ܬܘܬܪܝ ܠܒܐ ܓܝܪ ܡܡܠܠܢ ܤܦܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 46, 'ܡܢܐ ܩܪܝܢ ܐܢܬܘܢ ܠܝ ܡܪܝ ܡܪܝ ܘܡܕܡ ܕܐܡܪ ܐܢܐ ܠܐ ܥܒܕܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 47, 'ܟܠ ܐܢܫ ܕܐܬܐ ܠܘܬܝ ܘܫܡܥ ܡܠܝ ܘܥܒܕ ܠܗܝܢ ܐܚܘܝܟܘܢ ܠܡܢܐ ܕܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 48, 'ܕܡܐ ܠܓܒܪܐ ܕܒܢܐ ܒܝܬܐ ܘܚܦܪ ܘܥܡܩ ܘܤܡ ܫܬܐܤܐ ܥܠ ܫܘܥܐ ܟܕ ܗܘܐ ܕܝܢ ܡܠܐܐ ܐܬܛܪܝ ܡܠܐܐ ܒܒܝܬܐ ܗܘ ܘܠܐ ܐܫܟܚ ܕܢܙܝܥܝܘܗܝ ܤܝܡܐ ܗܘܬ ܓܝܪ ܫܬܐܤܬܗ ܥܠ ܫܘܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 49, 'ܘܗܘ ܕܫܡܥ ܘܠܐ ܥܒܕ ܕܡܐ ܠܓܒܪܐ ܕܒܢܐ ܒܝܬܗ ܥܠ ܥܦܪܐ ܕܠܐ ܫܬܐܤܬܐ ܘܟܕ ܐܬܛܪܝ ܒܗ ܢܗܪܐ ܒܪ ܫܥܬܗ ܢܦܠ ܘܗܘܬ ܡܦܘܠܬܗ ܪܒܐ ܕܒܝܬܐ ܗܘ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 1, 'ܘܟܕ ܫܠܡ ܡܠܐ ܟܠܗܝܢ ܠܡܫܡܥܬܗ ܕܥܡܐ ܥܠ ܝܫܘܥ ܠܟܦܪܢܚܘܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 2, 'ܥܒܕܗ ܕܝܢ ܕܩܢܛܪܘܢܐ ܚܕ ܥܒܝܕ ܗܘܐ ܒܝܫܐܝܬ ܐܝܢܐ ܕܝܩܝܪ ܗܘܐ ܥܠܘܗܝ ܘܩܪܝܒ ܗܘܐ ܠܡܡܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 3, 'ܘܫܡܥ ܥܠ ܝܫܘܥ ܘܫܕܪ ܠܘܬܗ ܩܫܝܫܐ ܕܝܗܘܕܝܐ ܘܒܥܐ ܗܘܐ ܡܢܗ ܐܝܟ ܕܢܐܬܐ ܢܚܐ ܠܥܒܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 4, 'ܗܢܘܢ ܕܝܢ ܟܕ ܐܬܘ ܠܘܬ ܝܫܘܥ ܒܥܝܢ ܗܘܘ ܡܢܗ ܒܛܝܠܐܝܬ ܘܐܡܪܝܢ ܫܘܐ ܗܘ ܕܬܥܒܕ ܠܗ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 5, 'ܪܚܡ ܓܝܪ ܠܥܡܢ ܘܐܦ ܒܝܬ ܟܢܘܫܬܐ ܗܘ ܒܢܐ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 6, 'ܝܫܘܥ ܕܝܢ ܐܙܠ ܗܘܐ ܥܡܗܘܢ ܟܕ ܕܝܢ ܠܐ ܤܓܝ ܪܚܝܩ ܡܢ ܒܝܬܐ ܫܕܪ ܠܘܬܗ ܩܢܛܪܘܢܐ ܪܚܡܘܗܝ ܘܐܡܪ ܠܗ ܡܪܝ ܠܐ ܬܥܡܠ ܠܐ ܓܝܪ ܫܘܐ ܐܢܐ ܕܬܥܘܠ ܬܚܝܬ ܡܛܠܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 7, 'ܡܛܠ ܗܘ ܐܢܐ ܠܐ ܫܘܝܬ ܕܠܘܬܟ ܐܬܐ ܐܠܐ ܐܡܪ ܒܡܠܬܐ ܘܢܬܐܤܐ ܛܠܝܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 8, 'ܐܦ ܐܢܐ ܓܝܪ ܓܒܪܐ ܐܢܐ ܕܡܫܥܒܕ ܐܢܐ ܬܚܝܬ ܫܘܠܛܢܐ ܘܐܝܬ ܬܚܝܬ ܐܝܕܝ ܐܤܛܪܛܝܘܛܐ ܘܐܡܪ ܐܢܐ ܠܗܢܐ ܕܙܠ ܘܐܙܠ ܘܠܐܚܪܢܐ ܕܬܐ ܘܐܬܐ ܘܠܥܒܕܝ ܥܒܕ ܗܕܐ ܘܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 9, 'ܟܕ ܫܡܥ ܕܝܢ ܝܫܘܥ ܗܠܝܢ ܐܬܕܡܪ ܒܗ ܘܐܬܦܢܝ ܘܐܡܪ ܠܟܢܫܐ ܕܐܬܐ ܒܬܪܗ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܐܦ ܠܐ ܒܝܬ ܐܝܤܪܝܠ ܐܫܟܚܬ ܐܝܟ ܗܕܐ ܗܝܡܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 10, 'ܘܗܦܟܘ ܗܢܘܢ ܕܐܫܬܕܪܘ ܠܒܝܬܐ ܘܐܫܟܚܘ ܠܥܒܕܐ ܗܘ ܕܟܪܝܗ ܗܘܐ ܟܕ ܚܠܝܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 11, 'ܘܗܘܐ ܠܝܘܡܐ ܕܒܬܪܗ ܐܙܠ ܗܘܐ ܠܡܕܝܢܬܐ ܕܫܡܗ ܢܐܝܢ ܘܬܠܡܝܕܘܗܝ ܥܡܗ ܘܟܢܫܐ ܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 12, 'ܘܟܕ ܩܪܒ ܠܬܪܥܐ ܕܡܕܝܢܬܐ ܚܙܐ ܟܕ ܡܠܘܝܢ ܡܝܬܐ ܕܝܚܝܕܝܐ ܗܘܐ ܠܐܡܗ ܘܗܝ ܐܡܗ ܐܪܡܠܬܐ ܗܘܬ ܘܟܢܫܐ ܤܓܝܐܐ ܕܒܢܝ ܡܕܝܢܬܐ ܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 13, 'ܚܙܗ ܕܝܢ ܝܫܘܥ ܘܐܬܪܚܡ ܥܠܝܗ ܘܐܡܪ ܠܗ ܠܐ ܬܒܟܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 14, 'ܘܐܙܠ ܩܪܒ ܠܥܪܤܐ ܘܗܢܘܢ ܕܫܩܝܠܝܢ ܗܘܘ ܠܗ ܩܡܘ ܘܐܡܪ ܥܠܝܡܐ ܠܟ ܐܡܪ ܐܢܐ ܩܘܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 15, 'ܘܝܬܒ ܗܘ ܡܝܬܐ ܘܫܪܝ ܠܡܡܠܠܘ ܘܝܗܒܗ ܠܐܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 16, 'ܘܐܚܕܬ ܕܚܠܬܐ ܠܐܢܫܐ ܟܠܗܘܢ ܘܡܫܒܚܝܢ ܗܘܘ ܠܐܠܗܐ ܘܐܡܪܝܢ ܕܢܒܝܐ ܪܒܐ ܩܡ ܒܢ ܘܤܥܪ ܐܠܗܐ ܠܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 17, 'ܘܢܦܩܬ ܥܠܘܗܝ ܡܠܬܐ ܗܕܐ ܒܟܠܗ ܝܗܘܕ ܘܒܟܠܗ ܐܬܪܐ ܕܚܕܪܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 18, 'ܘܐܫܬܥܝܘ ܠܝܘܚܢܢ ܬܠܡܝܕܘܗܝ ܗܠܝܢ ܟܠܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 19, 'ܘܩܪܐ ܝܘܚܢܢ ܠܬܪܝܢ ܡܢ ܬܠܡܝܕܘܗܝ ܘܫܕܪ ܐܢܘܢ ܠܘܬ ܝܫܘܥ ܘܐܡܪ ܐܢܬ ܗܘ ܗܘ ܕܐܬܐ ܐܘ ܠܐܚܪܝܢ ܗܘ ܡܤܟܝܢ ܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 20, 'ܘܐܬܘ ܠܘܬ ܝܫܘܥ ܘܐܡܪܝܢ ܠܗ ܝܘܚܢܢ ܡܥܡܕܢܐ ܫܕܪܢ ܠܘܬܟ ܘܐܡܪ ܐܢܬ ܗܘ ܗܘ ܕܐܬܐ ܐܘ ܠܐܚܪܝܢ ܗܘ ܡܤܟܝܢ ܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 21, 'ܒܗ ܕܝܢ ܒܗܝ ܫܥܬܐ ܤܓܝܐܐ ܐܤܝ ܡܢ ܟܘܪܗܢܐ ܘܡܢ ܡܚܘܬܐ ܘܡܢ ܪܘܚܐ ܒܝܫܬܐ ܘܠܤܓܝܐܐ ܤܡܝܐ ܝܗܒ ܗܘܐ ܠܡܚܙܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 22, 'ܘܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܙܠܘ ܐܡܪܘ ܠܝܘܚܢܢ ܟܠܡܕܡ ܕܚܙܝܬܘܢ ܘܫܡܥܬܘܢ ܕܤܡܝܐ ܚܙܝܢ ܘܚܓܝܪܐ ܡܗܠܟܝܢ ܘܓܪܒܐ ܡܬܕܟܝܢ ܘܚܪܫܐ ܫܡܥܝܢ ܘܡܝܬܐ ܩܝܡܝܢ ܘܡܤܟܢܐ ܡܤܬܒܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 23, 'ܘܛܘܒܘܗܝ ܠܡܢ ܕܠܐ ܢܬܟܫܠ ܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 24, 'ܟܕ ܕܝܢ ܐܙܠܘ ܬܠܡܝܕܘܗܝ ܕܝܘܚܢܢ ܫܪܝ ܠܡܐܡܪ ܠܟܢܫܐ ܥܠ ܝܘܚܢܢ ܡܢܐ ܢܦܩܬܘܢ ܠܚܘܪܒܐ ܠܡܚܙܐ ܩܢܝܐ ܕܡܢ ܪܘܚܐ ܡܬܬܙܝܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 25, 'ܘܐܠܐ ܡܢܐ ܢܦܩܬܘܢ ܠܡܚܙܐ ܓܒܪܐ ܕܢܚܬܐ ܪܟܝܟܐ ܠܒܝܫ ܗܐ ܐܝܠܝܢ ܕܒܠܒܘܫܐ ܡܫܒܚܐ ܘܒܦܘܢܩܐ ܐܝܬܝܗܘܢ ܒܝܬ ܡܠܟܐ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 26, 'ܘܐܠܐ ܡܢܐ ܢܦܩܬܘܢ ܠܡܚܙܐ ܢܒܝܐ ܐܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܘܝܬܝܪ ܡܢ ܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 27, 'ܗܢܘ ܕܥܠܘܗܝ ܟܬܝܒ ܕܗܐ ܐܢܐ ܡܫܕܪ ܐܢܐ ܡܠܐܟܝ ܩܕܡ ܦܪܨܘܦܟ ܕܢܬܩܢ ܐܘܪܚܐ ܩܕܡܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 28, 'ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܠܝܬ ܢܒܝܐ ܒܝܠܝܕܝ ܢܫܐ ܕܪܒ ܡܢ ܝܘܚܢܢ ܡܥܡܕܢܐ ܙܥܘܪܐ ܕܝܢ ܒܡܠܟܘܬܐ ܕܐܠܗܐ ܪܒ ܗܘ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 29, 'ܘܟܠܗ ܥܡܐ ܕܫܡܥܘ ܐܦ ܡܟܤܐ ܙܕܩܘ ܠܐܠܗܐ ܕܥܡܕܘ ܡܥܡܘܕܝܬܗ ܕܝܘܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 30, 'ܦܪܝܫܐ ܕܝܢ ܘܤܦܪܐ ܛܠܡܘ ܒܢܦܫܗܘܢ ܨܒܝܢܐ ܕܐܠܗܐ ܕܠܐ ܐܬܥܡܕܘ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 31, 'ܠܡܢ ܗܟܝܠ ܐܕܡܐ ܠܐܢܫܐ ܕܫܪܒܬܐ ܗܕܐ ܘܠܡܢ ܕܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 32, 'ܕܡܝܢ ܠܛܠܝܐ ܕܝܬܒܝܢ ܒܫܘܩܐ ܘܩܥܝܢ ܠܚܒܪܝܗܘܢ ܘܐܡܪܝܢ ܙܡܪܢ ܠܟܘܢ ܘܠܐ ܪܩܕܬܘܢ ܘܐܠܝܢ ܠܟܘܢ ܘܠܐ ܒܟܝܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 33, 'ܐܬܐ ܓܝܪ ܝܘܚܢܢ ܡܥܡܕܢܐ ܠܐ ܐܟܠ ܠܚܡܐ ܘܠܐ ܫܬܐ ܚܡܪܐ ܘܐܡܪܝܢ ܐܢܬܘܢ ܫܐܕܐ ܐܝܬ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 34, 'ܐܬܐ ܒܪܗ ܕܐܢܫܐ ܐܟܠ ܘܫܬܐ ܘܐܡܪܝܢ ܐܢܬܘܢ ܗܐ ܓܒܪܐ ܐܟܘܠܐ ܘܫܬܐ ܚܡܪܐ ܘܪܚܡܐ ܕܡܟܤܐ ܘܕܚܛܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 35, 'ܘܐܙܕܕܩܬ ܚܟܡܬܐ ܡܢ ܟܠܗܘܢ ܒܢܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 36, 'ܐܬܐ ܕܝܢ ܒܥܐ ܡܢܗ ܚܕ ܡܢ ܦܪܝܫܐ ܕܢܠܥܤ ܥܡܗ ܘܥܠ ܠܒܝܬܗ ܕܦܪܝܫܐ ܗܘ ܘܐܤܬܡܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 37, 'ܘܐܢܬܬܐ ܚܛܝܬܐ ܐܝܬ ܗܘܬ ܒܡܕܝܢܬܐ ܗܝ ܘܟܕ ܝܕܥܬ ܕܒܒܝܬܗ ܕܦܪܝܫܐ ܗܘ ܤܡܝܟ ܢܤܒܬ ܫܛܝܦܬܐ ܕܒܤܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 38, 'ܘܩܡܬ ܒܤܬܪܗ ܠܘܬ ܪܓܠܘܗܝ ܘܒܟܝܐ ܗܘܬ ܘܫܪܝܬ ܒܕܡܥܝܗ ܡܨܒܥܐ ܪܓܠܘܗܝ ܘܒܤܥܪܐ ܕܪܫܗ ܡܫܘܝܢ ܠܗܝܢ ܘܡܢܫܩܐ ܗܘܬ ܪܓܠܘܗܝ ܘܡܫܚܐ ܒܤܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 39, 'ܟܕ ܚܙܐ ܕܝܢ ܦܪܝܫܐ ܗܘ ܕܩܪܝܗܝ ܐܬܚܫܒ ܒܢܦܫܗ ܘܐܡܪ ܗܢܐ ܐܠܘ ܢܒܝܐ ܗܘܐ ܝܕܥ ܗܘܐ ܡܢ ܗܝ ܘܡܐ ܛܒܗ ܕܚܛܝܬܐ ܗܝ ܐܢܬܬܐ ܗܝ ܕܩܪܒܬ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 40, 'ܥܢܐ ܕܝܢ ܝܫܘܥ ܘܐܡܪ ܠܗ ܫܡܥܘܢ ܡܕܡ ܐܝܬ ܠܝ ܕܐܡܪ ܠܟ ܗܘ ܕܝܢ ܐܡܪ ܠܗ ܐܡܪ ܪܒܝ ܐܡܪ ܠܗ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 41, 'ܬܪܝܢ ܚܝܒܐ ܐܝܬ ܗܘܘ ܠܚܕ ܡܪܐ ܚܘܒܐ ܚܕ ܚܝܒ ܗܘܐ ܕܝܢܪܐ ܚܡܫܡܐܐ ܘܐܚܪܢܐ ܕܝܢܪܐ ܚܡܫܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 42, 'ܘܕܠܝܬ ܗܘܐ ܠܗܘܢ ܠܡܦܪܥ ܠܬܪܝܗܘܢ ܫܒܩ ܐܝܢܐ ܗܟܝܠ ܡܢܗܘܢ ܝܬܝܪ ܢܚܒܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 43, 'ܥܢܐ ܫܡܥܘܢ ܘܐܡܪ ܤܒܪ ܐܢܐ ܕܗܘ ܕܐܫܬܒܩ ܠܗ ܤܓܝ ܐܡܪ ܠܗ ܝܫܘܥ ܬܪܝܨܐܝܬ ܕܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 44, 'ܘܐܬܦܢܝ ܠܘܬ ܗܝ ܐܢܬܬܐ ܘܐܡܪ ܠܫܡܥܘܢ ܚܙܐ ܐܢܬ ܐܢܬܬܐ ܗܕܐ ܠܒܝܬܟ ܥܠܬ ܡܝܐ ܠܪܓܠܝ ܠܐ ܝܗܒܬ ܗܕܐ ܕܝܢ ܒܕܡܥܝܗ ܪܓܠܝ ܨܒܥܬ ܘܒܤܥܪܗ ܫܘܝܬ ܐܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 45, 'ܐܢܬ ܠܐ ܢܫܩܬܢܝ ܗܕܐ ܕܝܢ ܗܐ ܡܢ ܕܥܠܬ ܠܐ ܫܠܝܬ ܪܓܠܝ ܠܡܢܫܩܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 46, 'ܐܢܬ ܡܫܚܐ ܠܪܫܝ ܠܐ ܡܫܚܬ ܗܕܐ ܕܝܢ ܒܡܫܚܐ ܕܒܤܡܐ ܪܓܠܝ ܡܫܚܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 47, 'ܚܠܦ ܗܕܐ ܐܡܪ ܐܢܐ ܠܟ ܕܫܒܝܩܝܢ ܠܗ ܚܛܗܝܗ ܤܓܝܐܐ ܡܛܠ ܕܐܚܒܬ ܤܓܝ ܗܘ ܕܝܢ ܕܩܠܝܠ ܡܫܬܒܩ ܠܗ ܩܠܝܠ ܡܚܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 48, 'ܘܐܡܪ ܠܗܝ ܐܢܬܬܐ ܫܒܝܩܝܢ ܠܟܝ ܚܛܗܝܟܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 49, 'ܫܪܝܘ ܕܝܢ ܗܢܘܢ ܕܤܡܝܟܝܢ ܐܡܪܝܢ ܒܢܦܫܗܘܢ ܡܢܘ ܗܢܐ ܕܐܦ ܚܛܗܐ ܫܒܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 50, 'ܝܫܘܥ ܕܝܢ ܐܡܪ ܠܗܝ ܐܢܬܬܐ ܗܝܡܢܘܬܟܝ ܐܚܝܬܟܝ ܙܠܝ ܒܫܠܡܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 1, 'ܘܗܘܐ ܡܢ ܒܬܪ ܗܠܝܢ ܡܬܟܪܟ ܗܘܐ ܝܫܘܥ ܒܡܕܝܢܬܐ ܘܒܩܘܪܝܐ ܘܡܟܪܙ ܗܘܐ ܘܡܤܒܪ ܡܠܟܘܬܐ ܕܐܠܗܐ ܘܬܪܥܤܪܬܗ ܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 2, 'ܘܢܫܐ ܗܠܝܢ ܕܐܬܐܤܝ ܡܢ ܟܘܪܗܢܐ ܘܡܢ ܪܘܚܐ ܒܝܫܬܐ ܡܪܝܡ ܕܡܬܩܪܝܐ ܡܓܕܠܝܬܐ ܗܝ ܕܫܒܥܐ ܫܐܕܝܢ ܢܦܩܘ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 3, 'ܘܝܘܚܢ ܐܢܬܬ ܟܘܙܐ ܪܒܝܬܗ ܕܗܪܘܕܤ ܘܫܘܫܢ ܘܐܚܪܢܝܬܐ ܤܓܝܐܬܐ ܐܝܠܝܢ ܕܡܫܡܫܢ ܗܘܝ ܠܗܘܢ ܡܢ ܩܢܝܢܝܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 4, 'ܘܟܕ ܟܢܫܐ ܤܓܝܐܐ ܟܢܫ ܗܘܐ ܘܡܢ ܟܠ ܡܕܝܢܢ ܐܬܝܢ ܗܘܘ ܠܘܬܗ ܐܡܪ ܒܡܬܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 5, 'ܢܦܩ ܙܪܘܥܐ ܠܡܙܪܥ ܙܪܥܗ ܘܟܕ ܙܪܥ ܐܝܬ ܕܢܦܠ ܥܠ ܝܕ ܐܘܪܚܐ ܘܐܬܕܝܫ ܘܐܟܠܬܗ ܦܪܚܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 6, 'ܘܐܚܪܢܐ ܢܦܠ ܥܠ ܫܘܥܐ ܘܒܪ ܫܥܬܗ ܝܥܐ ܘܕܠܝܬ ܗܘܐ ܠܗ ܬܠܝܠܘܬܐ ܝܒܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 7, 'ܘܐܚܪܢܐ ܢܦܠ ܒܝܬ ܟܘܒܐ ܘܝܥܘ ܥܡܗ ܟܘܒܐ ܘܚܢܩܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 8, 'ܘܐܚܪܢܐ ܢܦܠ ܒܐܪܥܐ ܛܒܬܐ ܘܫܦܝܪܬܐ ܘܝܥܐ ܘܥܒܕ ܦܐܪܐ ܚܕ ܒܡܐܐ ܗܠܝܢ ܟܕ ܐܡܪ ܩܥܐ ܗܘܐ ܕܡܢ ܕܐܝܬ ܠܗ ܐܕܢܐ ܕܢܫܡܥ ܢܫܡܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 9, 'ܘܫܐܠܘܗܝ ܬܠܡܝܕܘܗܝ ܕܡܢܘ ܡܬܠܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 10, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܠܟܘܢ ܗܘ ܝܗܝܒ ܠܡܕܥ ܐܪܙܐ ܕܡܠܟܘܬܗ ܕܐܠܗܐ ܠܗܢܘܢ ܕܝܢ ܫܪܟܐ ܒܦܠܐܬܐ ܡܬܐܡܪ ܕܟܕ ܚܙܝܢ ܠܐ ܢܚܙܘܢ ܘܟܕ ܫܡܥܝܢ ܠܐ ܢܤܬܟܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 11, 'ܗܢܘ ܕܝܢ ܡܬܠܐ ܙܪܥܐ ܐܝܬܘܗܝ ܡܠܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 12, 'ܗܢܘܢ ܕܝܢ ܕܥܠ ܝܕ ܐܘܪܚܐ ܐܝܬܝܗܘܢ ܗܢܘܢ ܕܫܡܥܝܢ ܡܠܬܐ ܘܐܬܐ ܒܥܠܕܒܒܐ ܫܩܠ ܡܠܬܐ ܡܢ ܠܒܗܘܢ ܕܠܐ ܢܗܝܡܢܘܢ ܘܢܚܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 13, 'ܗܠܝܢ ܕܝܢ ܕܥܠ ܫܘܥܐ ܗܠܝܢ ܐܢܘܢ ܕܡܐ ܕܫܡܥܘ ܒܚܕܘܬܐ ܡܩܒܠܝܢ ܠܗ ܠܡܠܬܐ ܘܥܩܪܐ ܠܝܬ ܠܗܘܢ ܐܠܐ ܕܙܒܢܐ ܗܝ ܗܝܡܢܘܬܗܘܢ ܘܒܙܒܢ ܢܤܝܘܢܐ ܡܬܟܫܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 14, 'ܗܘ ܕܝܢ ܕܢܦܠ ܒܝܬ ܟܘܒܐ ܗܠܝܢ ܐܢܘܢ ܐܝܠܝܢ ܕܫܡܥܝܢ ܡܠܬܐ ܘܒܨܦܬܐ ܘܒܥܘܬܪܐ ܘܒܪܓܝܓܬܗ ܕܥܠܡܐ ܡܬܚܢܩܝܢ ܘܦܐܪܐ ܠܐ ܝܗܒܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 15, 'ܗܘ ܕܝܢ ܕܒܐܪܥܐ ܛܒܬܐ ܗܠܝܢ ܐܢܘܢ ܐܝܠܝܢ ܕܒܠܒܐ ܫܦܝܐ ܘܛܒܐ ܫܡܥܝܢ ܡܠܬܐ ܘܐܚܕܝܢ ܘܝܗܒܝܢ ܦܐܪܐ ܒܡܤܝܒܪܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 16, 'ܠܐ ܐܢܫ ܡܢܗܪ ܫܪܓܐ ܘܡܚܦܐ ܠܗ ܒܡܐܢܐ ܐܘ ܤܐܡ ܠܗ ܬܚܝܬ ܥܪܤܐ ܐܠܐ ܤܐܡ ܠܗ ܠܥܠ ܡܢ ܡܢܪܬܐ ܕܟܠ ܕܥܐܠ ܢܚܙܐ ܢܘܗܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 17, 'ܠܝܬ ܓܝܪ ܡܕܡ ܕܟܤܐ ܕܠܐ ܢܬܓܠܐ ܘܠܐ ܕܡܛܫܝ ܕܠܐ ܢܬܝܕܥ ܘܢܐܬܐ ܠܓܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 18, 'ܚܙܘ ܐܝܟܢܐ ܫܡܥܬܘܢ ܡܢ ܕܐܝܬ ܠܗ ܓܝܪ ܢܬܝܗܒ ܠܗ ܘܡܢ ܕܠܝܬ ܠܗ ܐܦ ܗܘ ܕܤܒܪ ܕܐܝܬ ܠܗ ܢܫܬܩܠ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 19, 'ܐܬܘ ܕܝܢ ܠܘܬܗ ܐܡܗ ܘܐܚܘܗܝ ܘܠܐ ܡܫܟܚܝܢ ܗܘܘ ܕܢܡܠܠܘܢ ܥܡܗ ܡܛܠ ܟܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 20, 'ܘܐܡܪܘ ܠܗ ܐܡܟ ܘܐܚܝܟ ܩܝܡܝܢ ܠܒܪ ܘܨܒܝܢ ܠܡܚܙܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 21, 'ܗܘ ܕܝܢ ܥܢܐ ܘܐܡܪ ܠܗܘܢ ܗܠܝܢ ܐܢܘܢ ܐܡܝ ܘܐܚܝ ܐܝܠܝܢ ܕܫܡܥܝܢ ܡܠܬܐ ܕܐܠܗܐ ܘܥܒܕܝܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 22, 'ܗܘܐ ܕܝܢ ܒܚܕ ܡܢ ܝܘܡܬܐ ܤܠܩ ܝܫܘܥ ܝܬܒ ܒܤܦܝܢܬܐ ܗܘ ܘܬܠܡܝܕܘܗܝ ܘܐܡܪ ܠܗܘܢ ܢܥܒܪ ܠܗܘ ܥܒܪܐ ܕܝܡܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 23, 'ܘܟܕ ܪܕܝܢ ܕܡܟ ܠܗ ܗܘ ܝܫܘܥ ܘܗܘܬ ܥܠܥܠܐ ܕܪܘܚܐ ܒܝܡܬܐ ܘܩܪܝܒܐ ܗܘܬ ܤܦܝܢܬܐ ܠܡܛܒܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 24, 'ܘܩܪܒܘ ܐܥܝܪܘܗܝ ܘܐܡܪܝܢ ܠܗ ܪܒܢ ܪܒܢ ܐܒܕܝܢܢ ܗܘ ܕܝܢ ܩܡ ܘܟܐܐ ܒܪܘܚܐ ܘܒܡܚܫܘܠܐ ܕܝܡܐ ܘܢܚܘ ܘܗܘܐ ܫܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 25, 'ܘܐܡܪ ܠܗܘܢ ܐܝܟܐ ܗܝ ܗܝܡܢܘܬܟܘܢ ܗܢܘܢ ܕܝܢ ܟܕ ܕܚܝܠܝܢ ܡܬܕܡܪܝܢ ܗܘܘ ܘܐܡܪܝܢ ܚܕ ܠܚܕ ܡܢܘ ܟܝ ܗܢܐ ܕܐܦ ܠܪܘܚܐ ܦܩܕ ܘܠܡܚܫܘܠܐ ܘܠܝܡܐ ܘܡܫܬܡܥܝܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 26, 'ܘܪܕܘ ܘܐܬܘ ܠܐܬܪܐ ܕܓܕܪܝܐ ܕܐܝܬܘܗܝ ܒܥܒܪܐ ܠܘܩܒܠ ܓܠܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 27, 'ܘܟܕ ܢܦܩ ܠܐܪܥܐ ܦܓܥ ܒܗ ܓܒܪܐ ܚܕ ܡܢ ܡܕܝܢܬܐ ܕܐܝܬ ܒܗ ܕܝܘܐ ܡܢ ܙܒܢܐ ܤܓܝܐܐ ܘܡܐܢܐ ܠܐ ܠܒܫ ܗܘܐ ܘܒܒܝܬܐ ܠܐ ܥܡܪ ܗܘܐ ܐܠܐ ܒܒܝܬ ܩܒܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 28, 'ܟܕ ܚܙܐ ܕܝܢ ܠܝܫܘܥ ܩܥܐ ܘܢܦܠ ܩܕܡܘܗܝ ܘܒܩܠܐ ܪܡܐ ܐܡܪ ܡܐ ܠܢ ܘܠܟ ܝܫܘܥ ܒܪܗ ܕܐܠܗܐ ܡܪܝܡܐ ܒܥܐ ܐܢܐ ܡܢܟ ܠܐ ܬܫܢܩܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 29, 'ܦܩܕ ܗܘܐ ܠܗ ܓܝܪ ܝܫܘܥ ܠܪܘܚܐ ܛܢܦܐ ܠܡܦܩ ܡܢ ܒܪܢܫܐ ܤܓܝ ܗܘܐ ܓܝܪ ܙܒܢܐ ܡܢ ܕܫܒܐ ܗܘܐ ܠܗ ܘܡܬܐܤܪ ܗܘܐ ܒܫܫܠܬܐ ܘܒܟܒܠܐ ܡܬܢܛܪ ܗܘܐ ܘܡܦܤܩ ܗܘܐ ܐܤܘܪܘܗܝ ܘܡܬܕܒܪ ܗܘܐ ܡܢ ܫܐܕܐ ܠܚܘܪܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 30, 'ܫܐܠܗ ܕܝܢ ܝܫܘܥ ܡܢ ܫܡܟ ܐܡܪ ܠܗ ܠܓܝܘܢ ܡܛܠ ܕܕܝܘܐ ܤܓܝܐܐ ܥܠܝܠܝܢ ܗܘܘ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 31, 'ܘܒܥܝܢ ܗܘܘ ܡܢܗ ܕܠܐ ܢܦܩܘܕ ܠܗܘܢ ܠܡܐܙܠ ܠܬܗܘܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 32, 'ܐܝܬ ܗܘܐ ܕܝܢ ܬܡܢ ܒܩܪܐ ܕܚܙܝܪܐ ܤܓܝܐܐ ܕܪܥܝܐ ܒܛܘܪܐ ܘܒܥܝܢ ܗܘܘ ܡܢܗ ܕܢܦܤ ܠܗܘܢ ܕܒܚܙܝܪܐ ܢܥܠܘܢ ܘܐܦܤ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 33, 'ܘܢܦܩܘ ܫܐܕܐ ܡܢ ܓܒܪܐ ܘܥܠܘ ܒܚܙܝܪܐ ܘܬܪܨܬ ܒܩܪܐ ܗܝ ܟܠܗ ܠܫܩܝܦܐ ܘܢܦܠܘ ܒܝܡܬܐ ܘܐܬܚܢܩܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 34, 'ܟܕ ܚܙܘ ܕܝܢ ܪܥܘܬܐ ܡܕܡ ܕܗܘܐ ܥܪܩܘ ܘܐܫܬܥܝܘ ܒܡܕܝܢܬܐ ܘܒܩܘܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 35, 'ܘܢܦܩܘ ܐܢܫܐ ܕܢܚܙܘܢ ܡܕܡ ܕܗܘܐ ܘܐܬܘ ܠܘܬ ܝܫܘܥ ܘܐܫܟܚܘܗܝ ܠܓܒܪܐ ܗܘ ܕܢܦܩܘ ܫܐܕܘܗܝ ܟܕ ܠܒܝܫ ܘܡܢܟܦ ܘܝܬܒ ܠܘܬ ܪܓܠܘܗܝ ܕܝܫܘܥ ܘܕܚܠܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 36, 'ܘܐܫܬܥܝܘ ܠܗܘܢ ܐܝܠܝܢ ܕܚܙܘ ܐܝܟܢܐ ܐܬܐܤܝ ܓܒܪܐ ܗܘ ܕܝܘܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 37, 'ܘܒܥܝܢ ܗܘܘ ܡܢܗ ܟܠܗ ܟܢܫܐ ܕܓܕܪܝܐ ܕܢܐܙܠ ܠܗ ܡܢ ܠܘܬܗܘܢ ܡܛܠ ܕܕܚܠܬܐ ܪܒܬܐ ܐܚܕܬ ܐܢܘܢ ܗܘ ܕܝܢ ܝܫܘܥ ܤܠܩ ܠܤܦܝܢܬܐ ܘܗܦܟ ܡܢ ܠܘܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 38, 'ܗܘ ܕܝܢ ܓܒܪܐ ܕܢܦܩܘ ܡܢܗ ܫܐܕܐ ܒܥܐ ܗܘܐ ܡܢܗ ܕܠܘܬܗ ܢܗܘܐ ܘܫܪܝܗܝ ܝܫܘܥ ܘܐܡܪ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 39, 'ܗܦܘܟ ܠܒܝܬܟ ܘܐܫܬܥܐ ܡܕܡ ܕܥܒܕ ܠܟ ܐܠܗܐ ܘܐܙܠ ܘܡܟܪܙ ܗܘܐ ܒܟܠܗ ܡܕܝܢܬܐ ܡܕܡ ܕܥܒܕ ܠܗ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 40, 'ܟܕ ܗܦܟ ܕܝܢ ܝܫܘܥ ܩܒܠܗ ܟܢܫܐ ܤܓܝܐܐ ܟܠܗܘܢ ܓܝܪ ܠܗ ܚܝܪܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 41, 'ܘܓܒܪܐ ܚܕ ܕܫܡܗ ܝܘܐܪܫ ܪܝܫ ܟܢܘܫܬܐ ܢܦܠ ܩܕܡ ܪܓܠܘܗܝ ܕܝܫܘܥ ܘܒܥܐ ܗܘܐ ܡܢܗ ܕܢܥܘܠ ܠܒܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 42, 'ܒܪܬܐ ܓܝܪ ܝܚܝܕܝܬܐ ܐܝܬ ܗܘܬ ܠܗ ܐܝܟ ܒܪܬ ܫܢܝܢ ܬܪܬܥܤܪܐ ܘܩܪܝܒܐ ܗܘܬ ܠܡܡܬ ܘܟܕ ܐܙܠ ܥܡܗ ܗܘ ܝܫܘܥ ܟܢܫܐ ܤܓܝܐܐ ܚܒܨ ܗܘܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 43, 'ܐܢܬܬܐ ܕܝܢ ܚܕܐ ܕܬܪܝܥ ܗܘܐ ܕܡܗ ܫܢܝܢ ܬܪܬܥܤܪܐ ܗܝ ܕܒܝܬ ܐܤܘܬܐ ܟܠܗ ܩܢܝܢܗ ܐܦܩܬ ܘܠܐ ܐܫܟܚܬ ܕܡܢ ܐܢܫ ܬܬܐܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 44, 'ܐܬܩܪܒܬ ܡܢ ܒܤܬܪܗ ܘܩܪܒܬ ܠܟܢܦܐ ܕܡܐܢܗ ܘܡܚܕܐ ܩܡܬ ܡܪܕܝܬܐ ܕܕܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 45, 'ܘܐܡܪ ܝܫܘܥ ܡܢܘ ܩܪܒ ܠܝ ܘܟܕ ܟܠܗܘܢ ܟܦܪܝܢ ܐܡܪ ܠܗ ܫܡܥܘܢ ܟܐܦܐ ܘܕܥܡܗ ܪܒܢ ܟܢܫܐ ܐܠܨܝܢ ܠܟ ܘܚܒܨܝܢ ܘܐܡܪ ܐܢܬ ܡܢܘ ܩܪܒ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 46, 'ܗܘ ܕܝܢ ܐܡܪ ܐܢܫ ܩܪܒ ܠܝ ܐܢܐ ܓܝܪ ܝܕܥܬ ܕܚܝܠܐ ܢܦܩ ܡܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 47, 'ܗܝ ܕܝܢ ܐܢܬܬܐ ܟܕ ܚܙܬ ܕܠܐ ܛܥܬܗ ܐܬܬ ܟܕ ܪܬܝܬܐ ܘܢܦܠܬ ܤܓܕܬ ܠܗ ܘܐܡܪܬ ܠܥܝܢ ܥܡܐ ܟܠܗ ܡܛܠ ܐܝܕܐ ܥܠܬܐ ܩܪܒܬ ܘܐܝܟܢܐ ܡܚܕܐ ܐܬܐܤܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 48, 'ܗܘ ܕܝܢ ܝܫܘܥ ܐܡܪ ܠܗ ܐܬܠܒܒܝ ܒܪܬܝ ܗܝܡܢܘܬܟܝ ܐܚܝܬܟܝ ܙܠܝ ܒܫܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 49, 'ܘܥܕ ܗܘ ܡܡܠܠ ܐܬܐ ܐܢܫ ܡܢ ܕܒܝܬ ܪܒ ܟܢܘܫܬܐ ܘܐܡܪ ܠܗ ܡܝܬܬ ܠܗ ܒܪܬܟ ܠܐ ܬܥܡܠ ܠܡܠܦܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 50, 'ܝܫܘܥ ܕܝܢ ܫܡܥ ܘܐܡܪ ܠܐܒܘܗ ܕܛܠܝܬܐ ܠܐ ܬܕܚܠ ܒܠܚܘܕ ܗܝܡܢ ܘܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 51, 'ܐܬܐ ܕܝܢ ܝܫܘܥ ܠܒܝܬܐ ܘܠܐ ܫܒܩ ܠܐܢܫ ܕܢܥܘܠ ܥܡܗ ܐܠܐ ܠܫܡܥܘܢ ܘܠܝܥܩܘܒ ܘܠܝܘܚܢܢ ܘܠܐܒܘܗ ܕܛܠܝܬܐ ܘܠܐܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 52, 'ܟܠܗܘܢ ܕܝܢ ܒܟܝܢ ܗܘܘ ܘܡܪܩܕܝܢ ܥܠܝܗ ܝܫܘܥ ܕܝܢ ܐܡܪ ܠܐ ܬܒܟܘܢ ܠܐ ܓܝܪ ܡܝܬܬ ܐܠܐ ܕܡܟܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 53, 'ܘܓܚܟܝܢ ܗܘܘ ܥܠܘܗܝ ܕܝܕܥܝܢ ܕܡܝܬܬ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 54, 'ܗܘ ܕܝܢ ܐܦܩ ܠܟܠܢܫ ܠܒܪ ܘܐܚܕܗ ܒܐܝܕܗ ܘܩܪܗ ܘܐܡܪ ܛܠܝܬܐ ܩܘܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 55, 'ܘܗܦܟܬ ܪܘܚܗ ܘܡܚܕܐ ܩܡܬ ܘܦܩܕ ܕܢܬܠܘܢ ܠܗ ܠܡܐܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 56, 'ܘܬܡܗܘ ܐܒܗܝܗ ܗܘ ܕܝܢ ܙܗܪ ܐܢܘܢ ܕܠܐܢܫ ܠܐ ܢܐܡܪܘܢ ܡܐ ܕܗܘܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 1, 'ܘܩܪܐ ܝܫܘܥ ܠܬܪܥܤܪܬܗ ܘܝܗܒ ܠܗܘܢ ܚܝܠܐ ܘܫܘܠܛܢܐ ܥܠ ܟܠܗܘܢ ܫܐܕܐ ܘܟܘܪܗܢܐ ܠܡܐܤܝܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 2, 'ܘܫܕܪ ܐܢܘܢ ܠܡܟܪܙܘ ܡܠܟܘܬܗ ܕܐܠܗܐ ܘܠܡܐܤܝܘ ܟܪܝܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 3, 'ܘܐܡܪ ܠܗܘܢ ܡܕܡ ܠܐ ܬܫܩܠܘܢ ܠܐܘܪܚܐ ܠܐ ܫܒܛܐ ܘܠܐ ܬܪܡܠܐ ܘܠܐ ܠܚܡܐ ܘܠܐ ܟܤܦܐ ܘܠܐ ܬܪܬܝܢ ܟܘܬܝܢܝܢ ܢܗܘܝܢ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 4, 'ܘܠܐܝܢܐ ܒܝܬܐ ܕܥܐܠܝܢ ܐܢܬܘܢ ܠܗ ܬܡܢ ܗܘܘ ܘܡܢ ܬܡܢ ܦܘܩܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 5, 'ܘܠܡܢ ܕܠܐ ܡܩܒܠܝܢ ܠܟܘܢ ܡܐ ܕܢܦܩܝܢ ܐܢܬܘܢ ܡܢ ܡܕܝܢܬܐ ܗܝ ܐܦ ܚܠܐ ܡܢ ܪܓܠܝܟܘܢ ܦܨܘ ܥܠܝܗܘܢ ܠܤܗܕܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 6, 'ܘܢܦܩܘ ܫܠܝܚܐ ܘܡܬܟܪܟܝܢ ܗܘܘ ܒܩܘܪܝܐ ܘܒܡܕܝܢܬܐ ܘܡܤܒܪܝܢ ܗܘܘ ܘܡܐܤܝܢ ܒܟܠ ܕܘܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 7, 'ܫܡܥ ܕܝܢ ܗܪܘܕܤ ܛܛܪܪܟܐ ܟܠܗܝܢ ܕܗܘܝܢ ܗܘܝ ܒܐܝܕܗ ܘܡܬܕܡܪ ܗܘܐ ܡܛܠ ܕܐܡܪܝܢ ܗܘܘ ܐܢܫܝܢ ܕܝܘܚܢܢ ܩܡ ܡܢ ܒܝܬ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 8, 'ܐܚܪܢܐ ܕܝܢ ܐܡܪܝܢ ܗܘܘ ܕܐܠܝܐ ܐܬܚܙܝ ܘܐܚܪܢܐ ܕܢܒܝܐ ܡܢ ܢܒܝܐ ܩܕܡܝܐ ܩܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 9, 'ܘܐܡܪ ܗܪܘܕܤ ܪܫܗ ܕܝܘܚܢܢ ܐܢܐ ܦܤܩܬ ܡܢܘ ܕܝܢ ܗܢܐ ܕܗܠܝܢ ܫܡܥ ܐܢܐ ܥܠܘܗܝ ܘܨܒܐ ܗܘܐ ܕܢܚܙܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 10, 'ܘܟܕ ܗܦܟܘ ܫܠܝܚܐ ܐܫܬܥܝܘ ܠܝܫܘܥ ܟܠܡܕܡ ܕܥܒܕܘ ܘܕܒܪ ܐܢܘܢ ܒܠܚܘܕܝܗܘܢ ܠܐܬܪܐ ܚܘܪܒܐ ܕܒܝܬ ܨܝܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 11, 'ܟܢܫܐ ܕܝܢ ܟܕ ܝܕܥܘ ܐܙܠܘ ܒܬܪܗ ܘܩܒܠ ܐܢܘܢ ܘܡܡܠܠ ܗܘܐ ܥܡܗܘܢ ܥܠ ܡܠܟܘܬܐ ܕܐܠܗܐ ܘܠܐܝܠܝܢ ܕܤܢܝܩܝܢ ܗܘܘ ܥܠ ܐܤܝܘܬܐ ܡܐܤܐ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 12, 'ܟܕ ܕܝܢ ܫܪܝ ܝܘܡܐ ܠܡܨܠܐ ܩܪܒܘ ܬܠܡܝܕܘܗܝ ܘܐܡܪܝܢ ܠܗ ܫܪܝ ܠܟܢܫܐ ܕܢܐܙܠܘܢ ܠܩܘܪܝܐ ܕܚܕܪܝܢ ܘܠܟܦܪܘܢܐ ܕܢܫܪܘܢ ܒܗܘܢ ܘܢܫܟܚܘܢ ܠܗܘܢ ܤܝܒܪܬܐ ܡܛܠ ܕܒܐܬܪܐ ܚܘܪܒܐ ܐܝܬܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 13, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܗܒܘ ܠܗܘܢ ܐܢܬܘܢ ܠܡܐܟܠ ܗܢܘܢ ܕܝܢ ܐܡܪܝܢ ܠܝܬ ܠܢ ܝܬܝܪ ܡܢ ܚܡܫܐ ܠܚܡܝܢ ܘܬܪܝܢ ܢܘܢܝܢ ܐܠܐ ܐܢ ܐܙܠܢܢ ܘܙܒܢܢ ܤܝܒܪܬܐ ܠܗܢܐ ܟܠܗ ܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 14, 'ܗܘܝܢ ܗܘܘ ܓܝܪ ܐܝܟ ܚܡܫܐ ܐܠܦܝܢ ܓܒܪܝܢ ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܤܡܟܘ ܐܢܘܢ ܤܡܟܐ ܚܡܫܝܢ ܐܢܫܝܢ ܒܤܡܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 15, 'ܘܥܒܕܘ ܗܟܘܬ ܬܠܡܝܕܐ ܘܐܤܡܟܘ ܠܟܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 16, 'ܘܢܤܒ ܝܫܘܥ ܗܢܘܢ ܚܡܫܐ ܠܚܡܝܢ ܘܬܪܝܢ ܢܘܢܝܢ ܘܚܪ ܒܫܡܝܐ ܘܒܪܟ ܘܩܨܐ ܘܝܗܒ ܠܬܠܡܝܕܘܗܝ ܕܢܤܝܡܘܢ ܠܟܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 17, 'ܘܐܟܠܘ ܟܠܗܘܢ ܘܤܒܥܘ ܘܫܩܠܘ ܩܨܝܐ ܡܕܡ ܕܐܘܬܪܘ ܬܪܥܤܪ ܩܘܦܝܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 18, 'ܘܟܕ ܡܨܠܐ ܒܠܚܘܕܘܗܝ ܘܬܠܡܝܕܘܗܝ ܥܡܗ ܫܐܠ ܐܢܘܢ ܘܐܡܪ ܡܢܘ ܐܡܪܝܢ ܥܠܝ ܟܢܫܐ ܕܐܝܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 19, 'ܥܢܘ ܘܐܡܪܝܢ ܠܗ ܕܝܘܚܢܢ ܡܥܡܕܢܐ ܘܐܚܪܢܐ ܕܐܠܝܐ ܐܚܪܢܐ ܕܝܢ ܕܢܒܝܐ ܚܕ ܡܢ ܢܒܝܐ ܩܕܡܝܐ ܩܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 20, 'ܐܡܪ ܠܗܘܢ ܐܢܬܘܢ ܕܝܢ ܡܢܘ ܐܡܪܝܢ ܐܢܬܘܢ ܕܐܝܬܝ ܥܢܐ ܫܡܥܘܢ ܘܐܡܪ ܡܫܝܚܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 21, 'ܗܘ ܕܝܢ ܟܐܐ ܒܗܘܢ ܘܙܗܪ ܐܢܘܢ ܕܗܕܐ ܠܐܢܫ ܠܐ ܢܐܡܪܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 22, 'ܘܐܡܪ ܠܗܘܢ ܕܥܬܝܕ ܗܘ ܒܪܗ ܕܐܢܫܐ ܕܤܓܝܐܬܐ ܢܚܫ ܘܕܢܤܬܠܐ ܡܢ ܩܫܝܫܐ ܘܪܒܝ ܟܗܢܐ ܘܤܦܪܐ ܘܢܩܛܠܘܢܝܗܝ ܘܠܝܘܡܐ ܕܬܠܬܐ ܢܩܘܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 23, 'ܘܐܡܪ ܗܘܐ ܩܕܡ ܟܠܢܫ ܡܢ ܕܨܒܐ ܕܢܐܬܐ ܒܬܪܝ ܢܟܦܘܪ ܒܢܦܫܗ ܘܢܫܩܘܠ ܙܩܝܦܗ ܟܠܝܘܡ ܘܢܐܬܐ ܒܬܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 24, 'ܡܢ ܓܝܪ ܕܨܒܐ ܕܢܦܫܗ ܢܚܐ ܡܘܒܕ ܠܗ ܡܢ ܕܝܢ ܕܢܘܒܕ ܢܦܫܗ ܡܛܠܬܝ ܗܢܐ ܡܚܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 25, 'ܡܢܐ ܓܝܪ ܢܬܥܕܪ ܒܪ ܐܢܫܐ ܕܢܐܬܪ ܥܠܡܐ ܟܠܗ ܢܦܫܗ ܕܝܢ ܢܘܒܕ ܐܘ ܢܚܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 26, 'ܡܢ ܕܢܒܗܬ ܒܝ ܕܝܢ ܘܒܡܠܝ ܢܒܗܬ ܒܗ ܒܪܗ ܕܐܢܫܐ ܡܐ ܕܐܬܐ ܒܫܘܒܚܐ ܕܐܒܘܗܝ ܥܡ ܡܠܐܟܘܗܝ ܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 27, 'ܫܪܪܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܐܝܬ ܐܢܫܐ ܕܩܝܡܝܢ ܗܪܟܐ ܕܠܐ ܢܛܥܡܘܢ ܡܘܬܐ ܥܕܡܐ ܕܢܚܙܘܢ ܡܠܟܘܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 28, 'ܗܘܐ ܕܝܢ ܒܬܪ ܡܠܐ ܗܠܝܢ ܐܝܟ ܬܡܢܝܐ ܝܘܡܝܢ ܕܒܪ ܝܫܘܥ ܠܫܡܥܘܢ ܘܠܝܥܩܘܒ ܘܠܝܘܚܢܢ ܘܤܠܩ ܠܛܘܪܐ ܠܡܨܠܝܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 29, 'ܘܟܕ ܗܘ ܡܨܠܐ ܐܬܚܠܦ ܚܙܘܐ ܕܐܦܘܗܝ ܘܢܚܬܘܗܝ ܚܘܪܘ ܘܡܒܪܩܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 30, 'ܘܗܐ ܬܪܝܢ ܓܒܪܝܢ ܡܡܠܠܝܢ ܗܘܘ ܥܡܗ ܕܐܝܬܝܗܘܢ ܡܘܫܐ ܘܐܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 31, 'ܕܐܬܚܙܝܘ ܒܬܫܒܘܚܬܐ ܐܡܪܝܢ ܗܘܘ ܕܝܢ ܥܠ ܡܦܩܢܗ ܕܥܬܝܕ ܗܘܐ ܕܢܫܬܠܡ ܒܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 32, 'ܘܝܩܪܘ ܗܘܘ ܠܗܘܢ ܒܫܢܬܐ ܫܡܥܘܢ ܘܗܢܘܢ ܕܥܡܗ ܘܠܡܚܤܢ ܐܬܬܥܝܪܘ ܘܚܙܘ ܫܘܒܚܗ ܘܠܗܢܘܢ ܬܪܝܢ ܐܢܫܝܢ ܕܩܝܡܝܢ ܗܘܘ ܠܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 33, 'ܘܟܕ ܫܪܝܘ ܠܡܦܪܫ ܡܢܗ ܐܡܪ ܫܡܥܘܢ ܠܝܫܘܥ ܪܒܝ ܫܦܝܪ ܗܘ ܠܢ ܕܗܪܟܐ ܢܗܘܐ ܘܢܥܒܕ ܬܠܬ ܡܛܠܝܢ ܠܟ ܚܕܐ ܘܠܡܘܫܐ ܚܕܐ ܘܠܐܠܝܐ ܚܕܐ ܘܠܐ ܝܕܥ ܗܘܐ ܡܢܐ ܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 34, 'ܘܟܕ ܐܡܪ ܗܠܝܢ ܗܘܬ ܥܢܢܐ ܘܐܛܠܬ ܥܠܝܗܘܢ ܘܕܚܠܘ ܟܕ ܚܙܘ ܠܡܘܫܐ ܘܠܐܠܝܐ ܕܥܠܘ ܒܥܢܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 35, 'ܘܩܠܐ ܗܘܐ ܡܢ ܥܢܢܐ ܕܐܡܪ ܗܢܘ ܒܪܝ ܚܒܝܒܐ ܠܗ ܫܡܥܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 36, 'ܘܟܕ ܗܘܐ ܩܠܐ ܐܫܬܟܚ ܝܫܘܥ ܒܠܚܘܕܘܗܝ ܘܗܢܘܢ ܫܬܩܘ ܘܠܐܢܫ ܠܐ ܐܡܪܘ ܒܗܢܘܢ ܝܘܡܬܐ ܡܕܡ ܕܚܙܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 37, 'ܘܗܘܐ ܠܝܘܡܐ ܕܒܬܪܗ ܟܕ ܢܚܬܝܢ ܡܢ ܛܘܪܐ ܦܓܥ ܒܗܘܢ ܟܢܫܐ ܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 38, 'ܘܓܒܪܐ ܚܕ ܡܢ ܟܢܫܐ ܗܘ ܩܥܐ ܘܐܡܪ ܡܠܦܢܐ ܒܥܐ ܐܢܐ ܡܢܟ ܐܬܦܢܝ ܥܠܝ ܒܪܝ ܕܝܚܝܕܝܐ ܗܘ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 39, 'ܘܪܘܚܐ ܥܕܝܐ ܥܠܘܗܝ ܘܡܢ ܫܠܝܐ ܩܥܐ ܘܡܚܪܩ ܫܢܘܗܝ ܘܡܪܥܬ ܘܠܡܚܤܢ ܦܪܩܐ ܡܢܗ ܡܐ ܕܫܚܩܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 40, 'ܘܒܥܝܬ ܡܢ ܬܠܡܝܕܝܟ ܕܢܦܩܘܢܝܗܝ ܘܠܐ ܐܫܟܚܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 41, 'ܥܢܐ ܕܝܢ ܝܫܘܥ ܘܐܡܪ ܐܘܢ ܫܪܒܬܐ ܕܠܐ ܡܗܝܡܢܐ ܘܡܥܩܠܬܐ ܥܕܡܐ ܠܐܡܬܝ ܐܗܘܐ ܠܘܬܟܘܢ ܘܐܤܝܒܪܟܘܢ ܩܪܒܝܗܝ ܠܟܐ ܠܒܪܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 42, 'ܘܟܕ ܡܩܪܒ ܠܗ ܐܪܡܝܗ ܕܝܘܐ ܗܘ ܘܡܥܤܗ ܘܟܐܐ ܝܫܘܥ ܒܪܘܚܐ ܗܝ ܛܢܦܬܐ ܘܐܤܝܗ ܠܛܠܝܐ ܘܝܗܒܗ ܠܐܒܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 43, 'ܘܐܬܕܡܪܘ ܟܠܗܘܢ ܒܪܒܘܬܗ ܕܐܠܗܐ ܘܟܕ ܟܠܢܫ ܡܬܕܡܪ ܗܘܐ ܥܠ ܟܠ ܕܥܒܕ ܝܫܘܥ ܐܡܪ ܠܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 44, 'ܤܝܡܘ ܐܢܬܘܢ ܡܠܐ ܗܠܝܢ ܒܐܕܢܝܟܘܢ ܒܪܗ ܓܝܪ ܕܐܢܫܐ ܥܬܝܕ ܕܢܫܬܠܡ ܒܐܝܕܝ ܒܢܝ ܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 45, 'ܗܢܘܢ ܕܝܢ ܠܐ ܐܫܬܘܕܥܘܗ ܠܡܠܬܐ ܗܕܐ ܡܛܠ ܕܡܟܤܝܐ ܗܘܬ ܡܢܗܘܢ ܕܠܐ ܢܕܥܘܢܗ ܘܕܚܠܝܢ ܗܘܘ ܕܢܫܐܠܘܢܝܗܝ ܥܠܝܗ ܥܠ ܡܠܬܐ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 46, 'ܘܥܠܬ ܒܗܘܢ ܡܚܫܒܬܐ ܕܡܢܘ ܟܝ ܪܒ ܒܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 47, 'ܝܫܘܥ ܕܝܢ ܝܕܥ ܡܚܫܒܬܐ ܕܠܒܗܘܢ ܘܢܤܒ ܛܠܝܐ ܘܐܩܝܡܗ ܠܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 48, 'ܘܐܡܪ ܠܗܘܢ ܡܢ ܕܡܩܒܠ ܛܠܝܐ ܐܝܟ ܗܢܐ ܒܫܡܝ ܠܝ ܗܘ ܡܩܒܠ ܘܡܢ ܕܠܝ ܡܩܒܠ ܡܩܒܠ ܠܡܢ ܕܫܕܪܢܝ ܐܝܢܐ ܓܝܪ ܕܙܥܘܪ ܒܟܠܟܘܢ ܗܢܐ ܢܗܘܐ ܪܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 49, 'ܘܥܢܐ ܝܘܚܢܢ ܘܐܡܪ ܪܒܢ ܚܙܝܢ ܐܢܫ ܕܡܦܩ ܕܝܘܐ ܒܫܡܟ ܘܟܠܝܢܝܗܝ ܥܠ ܕܠܐ ܐܬܐ ܥܡܢ ܒܬܪܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 50, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܠܐ ܬܟܠܘܢ ܡܢ ܓܝܪ ܕܠܐ ܗܘܐ ܠܘܩܒܠܟܘܢ ܚܠܦܝܟܘܢ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 51, 'ܘܗܘܐ ܕܟܕ ܡܬܡܠܝܢ ܝܘܡܬܐ ܕܤܘܠܩܗ ܐܬܩܢ ܦܪܨܘܦܗ ܕܢܐܙܠ ܠܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 52, 'ܘܫܕܪ ܡܠܐܟܐ ܩܕܡ ܦܪܨܘܦܗ ܘܐܙܠܘ ܥܠܘ ܠܩܪܝܬܐ ܕܫܡܪܝܐ ܐܝܟ ܕܢܬܩܢܘܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 53, 'ܘܠܐ ܩܒܠܘܗܝ ܡܛܠ ܕܦܪܨܘܦܗ ܠܐܘܪܫܠܡ ܤܝܡ ܗܘܐ ܠܡܐܙܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 54, 'ܘܟܕ ܚܙܘ ܝܥܩܘܒ ܘܝܘܚܢܢ ܬܠܡܝܕܘܗܝ ܐܡܪܝܢ ܠܗ ܡܪܢ ܨܒܐ ܐܢܬ ܕܢܐܡܪ ܘܬܚܘܬ ܢܘܪܐ ܡܢ ܫܡܝܐ ܘܬܤܝܦ ܐܢܘܢ ܐܝܟ ܕܐܦ ܐܠܝܐ ܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 55, 'ܘܐܬܦܢܝ ܘܟܐܐ ܒܗܘܢ ܘܐܡܪ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܕܐܝܕܐ ܐܢܬܘܢ ܪܘܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 56, 'ܒܪܗ ܓܝܪ ܕܐܢܫܐ ܠܐ ܐܬܐ ܠܡܘܒܕܘ ܢܦܫܬܐ ܐܠܐ ܠܡܚܝܘ ܘܐܙܠܘ ܠܗܘܢ ܠܩܪܝܬܐ ܐܚܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 57, 'ܘܟܕ ܐܙܠܝܢ ܒܐܘܪܚܐ ܐܡܪ ܠܗ ܐܢܫ ܐܬܐ ܒܬܪܟ ܠܐܬܪ ܕܐܙܠ ܐܢܬ ܡܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 58, 'ܐܡܪ ܠܗ ܝܫܘܥ ܠܬܥܠܐ ܢܩܥܐ ܐܝܬ ܠܗܘܢ ܘܠܦܪܚܬܐ ܕܫܡܝܐ ܡܛܠܠܐ ܠܒܪܗ ܕܝܢ ܕܐܢܫܐ ܠܝܬ ܠܗ ܐܝܟܐ ܕܢܤܡܘܟ ܪܫܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 59, 'ܘܐܡܪ ܠܐܚܪܢܐ ܬܐ ܒܬܪܝ ܗܘ ܕܝܢ ܐܡܪ ܠܗ ܡܪܝ ܐܦܤ ܠܝ ܠܘܩܕܡ ܐܙܠ ܐܩܒܘܪ ܐܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 60, 'ܐܡܪ ܠܗ ܝܫܘܥ ܫܒܘܩ ܡܝܬܐ ܩܒܪܝܢ ܡܝܬܝܗܘܢ ܘܐܢܬ ܙܠ ܤܒܪ ܡܠܟܘܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 61, 'ܐܡܪ ܠܗ ܐܚܪܢܐ ܐܬܐ ܒܬܪܟ ܡܪܝ ܠܘܩܕܡ ܕܝܢ ܐܦܤ ܠܝ ܐܙܠ ܐܫܠܡ ܠܒܢܝ ܒܝܬܝ ܘܐܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 62, 'ܐܡܪ ܠܗ ܝܫܘܥ ܠܐ ܐܢܫ ܪܡܐ ܐܝܕܗ ܥܠ ܚܪܒܐ ܕܦܕܢܐ ܘܚܐܪ ܠܒܤܬܪܗ ܘܚܫܚ ܠܡܠܟܘܬܗ ܕܐܠܗܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 1, 'ܒܬܪ ܗܠܝܢ ܦܪܫ ܝܫܘܥ ܡܢ ܬܠܡܝܕܘܗܝ ܐܚܪܢܐ ܫܒܥܝܢ ܘܫܕܪ ܐܢܘܢ ܬܪܝܢ ܬܪܝܢ ܩܕܡ ܦܪܨܘܦܗ ܠܟܠ ܐܬܪ ܘܡܕܝܢܐ ܕܥܬܝܕ ܗܘܐ ܠܡܐܙܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 2, 'ܘܐܡܪ ܠܗܘܢ ܚܨܕܐ ܤܓܝ ܘܦܥܠܐ ܙܥܘܪܝܢ ܒܥܘ ܗܟܝܠ ܡܢ ܡܪܐ ܚܨܕܐ ܕܢܦܩ ܦܥܠܐ ܠܚܨܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 3, 'ܙܠܘ ܗܐ ܐܢܐ ܡܫܕܪ ܐܢܐ ܠܟܘܢ ܐܝܟ ܐܡܪܐ ܒܝܢܝ ܕܐܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 4, 'ܠܐ ܬܫܩܠܘܢ ܠܟܘܢ ܟܝܤܐ ܘܠܐ ܬܪܡܠܐ ܘܠܐ ܡܤܢܐ ܘܒܫܠܡܐ ܕܐܢܫ ܒܐܘܪܚܐ ܠܐ ܬܫܐܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 5, 'ܘܠܐܝܢܐ ܒܝܬܐ ܕܥܐܠܝܢ ܐܢܬܘܢ ܠܘܩܕܡ ܐܡܪܘ ܫܠܡܐ ܠܒܝܬܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 6, 'ܘܐܢ ܐܝܬ ܬܡܢ ܒܪ ܫܠܡܐ ܢܬܬܢܝܚ ܥܠܘܗܝ ܫܠܡܟܘܢ ܐܢ ܕܝܢ ܠܐ ܥܠܝܟܘܢ ܢܗܦܘܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 7, 'ܒܗ ܕܝܢ ܒܒܝܬܐ ܗܘܘ ܟܕ ܠܥܤܝܢ ܐܢܬܘܢ ܘܫܬܝܢ ܡܢ ܕܝܠܗܘܢ ܫܘܐ ܗܘ ܓܝܪ ܦܥܠܐ ܐܓܪܗ ܘܠܐ ܬܫܢܘܢ ܡܢ ܒܝܬܐ ܠܒܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 8, 'ܘܠܐܝܕܐ ܡܕܝܢܬܐ ܕܥܐܠܝܢ ܐܢܬܘܢ ܘܡܩܒܠܝܢ ܠܟܘܢ ܠܥܤܘ ܡܕܡ ܕܡܬܬܤܝܡ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 9, 'ܘܐܤܘ ܠܐܝܠܝܢ ܕܟܪܝܗܝܢ ܒܗ ܘܐܡܪܘ ܠܗܘܢ ܩܪܒܬ ܥܠܝܟܘܢ ܡܠܟܘܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 10, 'ܠܐܝܕܐ ܡܕܝܢܬܐ ܕܝܢ ܕܥܐܠܝܢ ܐܢܬܘܢ ܘܠܐ ܢܩܒܠܘܢܟܘܢ ܦܘܩܘ ܠܟܘܢ ܠܫܘܩܐ ܘܐܡܪܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 11, 'ܘܐܦ ܚܠܐ ܕܕܒܩ ܠܢ ܒܪܓܠܝܢ ܡܢ ܡܕܝܢܬܟܘܢ ܢܦܨܝܢ ܚܢܢ ܠܟܘܢ ܒܪܡ ܗܕܐ ܕܥܘ ܕܩܪܒܬ ܠܗ ܥܠܝܟܘܢ ܡܠܟܘܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 12, 'ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܠܤܕܘܡ ܢܗܘܐ ܢܝܚ ܒܝܘܡܐ ܗܘ ܐܘ ܠܡܕܝܢܬܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 13, 'ܘܝ ܠܟܝ ܟܘܪܙܝܢ ܘܝ ܠܟܝ ܒܝܬ ܨܝܕܐ ܕܐܠܘ ܒܨܘܪ ܘܒܨܝܕܢ ܗܘܘ ܚܝܠܐ ܕܗܘܘ ܒܟܝܢ ܟܒܪ ܕܝܢ ܒܤܩܐ ܘܒܩܛܡܐ ܬܒܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 14, 'ܒܪܡ ܠܨܘܪ ܘܠܨܝܕܢ ܢܗܘܐ ܢܝܚ ܒܕܝܢܐ ܐܘ ܠܟܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 15, 'ܘܐܢܬܝ ܟܦܪܢܚܘܡ ܗܝ ܕܥܕܡܐ ܠܫܡܝܐ ܐܬܬܪܝܡܬܝ ܥܕܡܐ ܠܫܝܘܠ ܬܬܚܬܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 16, 'ܡܢ ܕܠܟܘܢ ܫܡܥ ܠܝ ܫܡܥ ܘܡܢ ܕܠܟܘܢ ܛܠܡ ܠܝ ܗܘ ܛܠܡ ܘܡܢ ܕܠܝ ܛܠܡ ܛܠܡ ܠܡܢ ܕܫܠܚܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 17, 'ܘܗܦܟܘ ܗܢܘܢ ܫܒܥܝܢ ܕܫܕܪ ܒܚܕܘܬܐ ܪܒܬܐ ܘܐܡܪܝܢ ܠܗ ܡܪܢ ܐܦ ܫܐܕܐ ܡܫܬܥܒܕܝܢ ܠܢ ܒܫܡܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 18, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܚܙܐ ܗܘܝܬ ܠܗ ܠܤܛܢܐ ܕܢܦܠ ܐܝܟ ܒܪܩܐ ܡܢ ܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 19, 'ܗܐ ܝܗܒ ܐܢܐ ܠܟܘܢ ܫܘܠܛܢܐ ܕܗܘܝܬܘܢ ܕܝܫܝܢ ܚܘܘܬܐ ܘܥܩܪܒܐ ܘܟܠܗ ܚܝܠܗ ܕܒܥܠܕܒܒܐ ܘܡܕܡ ܠܐ ܢܗܪܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 20, 'ܒܪܡ ܒܗܕܐ ܠܐ ܬܚܕܘܢ ܕܫܐܕܐ ܡܫܬܥܒܕܝܢ ܠܟܘܢ ܐܠܐ ܚܕܘ ܕܫܡܗܝܟܘܢ ܐܬܟܬܒܘ ܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 21, 'ܒܗ ܒܫܥܬܐ ܪܘܙ ܝܫܘܥ ܒܪܘܚܐ ܕܩܘܕܫܐ ܘܐܡܪ ܡܘܕܐ ܐܢܐ ܠܟ ܐܒܝ ܡܪܐ ܕܫܡܝܐ ܘܕܐܪܥܐ ܕܟܤܝܬ ܗܠܝܢ ܡܢ ܚܟܝܡܐ ܘܤܟܘܠܬܢܐ ܘܓܠܝܬ ܐܢܝܢ ܠܝܠܘܕܐ ܐܝܢ ܐܒܝ ܕܗܟܢܐ ܗܘܐ ܨܒܝܢܐ ܩܕܡܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 22, 'ܘܐܬܦܢܝ ܠܘܬ ܬܠܡܝܕܘܗܝ ܘܐܡܪ ܠܗܘܢ ܟܠ ܡܕܡ ܐܫܬܠܡ ܠܝ ܡܢ ܐܒܝ ܘܠܐ ܐܢܫ ܝܕܥ ܡܢܘ ܒܪܐ ܐܠܐ ܐܢ ܐܒܐ ܘܡܢܘ ܐܒܐ ܐܠܐ ܐܢ ܒܪܐ ܘܠܡܢ ܕܐܢ ܢܨܒܐ ܒܪܐ ܕܢܓܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 23, 'ܘܐܬܦܢܝ ܠܘܬ ܬܠܡܝܕܘܗܝ ܒܠܚܘܕܝܗܘܢ ܘܐܡܪ ܛܘܒܝܗܝܢ ܠܥܝܢܐ ܕܚܙܝܢ ܡܕܡ ܕܐܢܬܘܢ ܚܙܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 24, 'ܐܡܪ ܐܢܐ ܠܟܘܢ ܓܝܪ ܕܢܒܝܐ ܤܓܝܐܐ ܘܡܠܟܐ ܨܒܘ ܕܢܚܙܘܢ ܡܕܡ ܕܚܙܝܢ ܐܢܬܘܢ ܘܠܐ ܚܙܘ ܘܠܡܫܡܥ ܡܕܡ ܕܐܢܬܘܢ ܫܡܥܝܢ ܘܠܐ ܫܡܥܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 25, 'ܘܗܐ ܤܦܪܐ ܚܕ ܩܡ ܕܢܢܤܝܘܗܝ ܘܐܡܪ ܡܠܦܢܐ ܡܢܐ ܐܥܒܕ ܕܐܪܬ ܚܝܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 26, 'ܗܘ ܕܝܢ ܝܫܘܥ ܐܡܪ ܠܗ ܒܢܡܘܤܐ ܐܝܟܢܐ ܟܬܝܒ ܐܝܟܢܐ ܩܪܐ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 27, 'ܥܢܐ ܘܐܡܪ ܠܗ ܕܬܪܚܡ ܠܡܪܝܐ ܐܠܗܟ ܡܢ ܟܠܗ ܠܒܟ ܘܡܢ ܟܠܗ ܢܦܫܟ ܘܡܢ ܟܠܗ ܚܝܠܟ ܘܡܢ ܟܠܗ ܪܥܝܢܟ ܘܠܩܪܝܒܟ ܐܝܟ ܢܦܫܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 28, 'ܐܡܪ ܠܗ ܝܫܘܥ ܬܪܝܨܐܝܬ ܐܡܪܬ ܗܕܐ ܥܒܕ ܘܬܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 29, 'ܗܘ ܕܝܢ ܟܕ ܨܒܐ ܠܡܙܕܩܘ ܢܦܫܗ ܐܡܪ ܠܗ ܘܡܢܘ ܩܪܝܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 30, 'ܐܡܪ ܠܗ ܝܫܘܥ ܓܒܪܐ ܚܕ ܢܚܬ ܗܘܐ ܡܢ ܐܘܪܫܠܡ ܠܐܝܪܝܚܘ ܘܢܦܠܘ ܥܠܘܗܝ ܠܤܛܝܐ ܘܫܠܚܘܗܝ ܘܡܚܐܘܗܝ ܘܫܒܩܘܗܝ ܟܕ ܩܠܝܠ ܩܝܡܐ ܒܗ ܢܦܫܐ ܘܐܙܠܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 31, 'ܘܓܕܫ ܟܗܢܐ ܚܕ ܢܚܬ ܗܘܐ ܒܐܘܪܚܐ ܗܝ ܘܚܙܝܗܝ ܘܥܒܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 32, 'ܘܗܟܢܐ ܐܦ ܠܘܝܐ ܐܬܐ ܡܛܐ ܠܗܝ ܕܘܟܬܐ ܘܚܙܝܗܝ ܘܥܒܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 33, 'ܐܢܫ ܕܝܢ ܫܡܪܝܐ ܟܕ ܪܕܐ ܗܘܐ ܐܬܐ ܐܝܟܐ ܕܐܝܬܘܗܝ ܗܘܐ ܘܚܙܝܗܝ ܘܐܬܪܚܡ ܥܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 34, 'ܘܐܬܩܪܒ ܘܥܨܒ ܡܚܘܬܗ ܘܢܨܠ ܥܠܝܗܝܢ ܚܡܪܐ ܘܡܫܚܐ ܘܤܡܗ ܥܠ ܚܡܪܗ ܘܐܝܬܝܗ ܠܦܘܬܩܐ ܘܐܬܒܛܠ ܠܗ ܥܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 35, 'ܘܠܨܦܪܗ ܕܝܘܡܐ ܐܦܩ ܬܪܝܢ ܕܝܢܪܝܢ ܝܗܒ ܠܦܘܬܩܝܐ ܘܐܡܪ ܠܗ ܝܨܦ ܕܝܠܗ ܘܐܢ ܡܕܡ ܝܬܝܪ ܬܦܩ ܡܐ ܕܗܦܟ ܐܢܐ ܝܗܒ ܐܢܐ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 36, 'ܡܢܘ ܗܟܝܠ ܡܢ ܗܠܝܢ ܬܠܬܐ ܡܬܚܙܐ ܠܟ ܕܗܘܐ ܩܪܝܒܐ ܠܗܘ ܕܢܦܠ ܒܐܝܕܝ ܓܝܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 37, 'ܗܘ ܕܝܢ ܐܡܪ ܗܘ ܕܐܬܪܚܡ ܥܠܘܗܝ ܐܡܪ ܠܗ ܝܫܘܥ ܙܠ ܐܦ ܐܢܬ ܗܟܢܐ ܗܘܝܬ ܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 38, 'ܘܗܘܐ ܕܟܕ ܗܢܘܢ ܪܕܝܢ ܒܐܘܪܚܐ ܥܠ ܠܩܪܝܬܐ ܚܕܐ ܘܐܢܬܬܐ ܕܫܡܗ ܡܪܬܐ ܩܒܠܬܗ ܒܒܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 39, 'ܘܐܝܬ ܗܘܬ ܠܗ ܚܬܐ ܕܫܡܗ ܡܪܝܡ ܘܐܬܬ ܝܬܒܬ ܠܗ ܠܘܬ ܪܓܠܘܗܝ ܕܡܪܢ ܘܫܡܥܐ ܗܘܬ ܡܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 40, 'ܡܪܬܐ ܕܝܢ ܥܢܝܐ ܗܘܬ ܒܬܫܡܫܬܐ ܤܓܝܐܬܐ ܘܐܬܬ ܐܡܪܐ ܠܗ ܡܪܝ ܠܐ ܒܛܝܠ ܠܟ ܕܚܬܝ ܫܒܩܬܢܝ ܒܠܚܘܕܝ ܠܡܫܡܫܘ ܐܡܪ ܠܗ ܡܥܕܪܐ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 41, 'ܥܢܐ ܕܝܢ ܝܫܘܥ ܘܐܡܪ ܠܗ ܡܪܬܐ ܡܪܬܐ ܝܨܦܬܝ ܘܪܗܝܒܬܝ ܥܠ ܤܓܝܐܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 42, 'ܚܕܐ ܗܝ ܕܝܢ ܕܡܬܒܥܝܐ ܡܪܝܡ ܕܝܢ ܡܢܬܐ ܛܒܬܐ ܓܒܬ ܠܗ ܗܝ ܕܠܐ ܬܬܢܤܒ ܡܢܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 1, 'ܘܗܘܐ ܕܟܕ ܗܘ ܡܨܠܐ ܒܕܘܟܬܐ ܚܕܐ ܟܕ ܫܠܡ ܐܡܪ ܠܗ ܚܕ ܡܢ ܬܠܡܝܕܘܗܝ ܡܪܢ ܐܠܦܝܢ ܠܡܨܠܝܘ ܐܝܟܢܐ ܕܐܦ ܝܘܚܢܢ ܐܠܦ ܠܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 2, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܡܬܝ ܕܡܨܠܝܢ ܐܢܬܘܢ ܗܟܢܐ ܗܘܝܬܘܢ ܐܡܪܝܢ ܐܒܘܢ ܕܒܫܡܝܐ ܢܬܩܕܫ ܫܡܟ ܬܐܬܐ ܡܠܟܘܬܟ ܢܗܘܐ ܨܒܝܢܟ ܐܝܟ ܕܒܫܡܝܐ ܐܦ ܒܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 3, 'ܗܒ ܠܢ ܠܚܡܐ ܕܤܘܢܩܢܢ ܟܠܝܘܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 4, 'ܘܫܒܘܩ ܠܢ ܚܛܗܝܢ ܐܦ ܐܢܚܢܢ ܓܝܪ ܫܒܩܢ ܠܟܠ ܕܚܝܒܝܢ ܠܢ ܘܠܐ ܬܥܠܢ ܠܢܤܝܘܢܐ ܐܠܐ ܦܪܘܩܝܢ ܡܢ ܒܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 5, 'ܘܐܡܪ ܠܗܘܢ ܡܢܘ ܡܢܟܘܢ ܕܐܝܬ ܠܗ ܪܚܡܐ ܘܢܐܙܠ ܠܘܬܗ ܒܦܠܓܘܬ ܠܠܝܐ ܘܢܐܡܪ ܠܗ ܪܚܡܝ ܐܫܐܠܝܢܝ ܬܠܬ ܓܪܝܨܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 6, 'ܡܛܠ ܕܪܚܡܐ ܐܬܐ ܠܘܬܝ ܡܢ ܐܘܪܚܐ ܘܠܝܬ ܠܝ ܡܕܡ ܕܐܤܝܡ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 7, 'ܘܗܘ ܪܚܡܗ ܡܢ ܠܓܘ ܢܥܢܐ ܘܢܐܡܪ ܠܗ ܠܐ ܬܗܪܝܢܝ ܕܗܐ ܬܪܥܐ ܐܚܝܕ ܗܘ ܘܒܢܝ ܥܡܝ ܒܥܪܤܐ ܠܐ ܡܫܟܚ ܐܢܐ ܕܐܩܘܡ ܘܐܬܠ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 8, 'ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܐܢ ܡܛܠ ܪܚܡܘܬܐ ܠܐ ܢܬܠ ܠܗ ܡܛܠ ܚܨܝܦܘܬܗ ܢܩܘܡ ܘܢܬܠ ܠܗ ܟܡܐ ܕܡܬܒܥܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 9, 'ܐܦ ܐܢܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܫܐܠܘ ܘܢܬܝܗܒ ܠܟܘܢ ܒܥܘ ܘܬܫܟܚܘܢ ܩܘܫܘ ܘܢܬܦܬܚ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 10, 'ܟܠ ܓܝܪ ܕܫܐܠ ܢܤܒ ܘܕܒܥܐ ܡܫܟܚ ܘܕܢܩܫ ܡܬܦܬܚ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 11, 'ܐܝܢܐ ܓܝܪ ܡܢܟܘܢ ܐܒܐ ܕܢܫܐܠܝܘܗܝ ܒܪܗ ܠܚܡܐ ܠܡܐ ܟܐܦܐ ܡܘܫܛ ܠܗ ܘܐܢ ܢܘܢܐ ܢܫܐܠܝܘܗܝ ܠܡܐ ܚܠܦ ܢܘܢܐ ܚܘܝܐ ܡܘܫܛ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 12, 'ܘܐܢ ܒܪܬܐ ܢܫܐܠܝܘܗܝ ܠܡܐ ܗܘ ܥܩܪܒܐ ܡܘܫܛ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 13, 'ܘܐܢ ܐܢܬܘܢ ܕܒܝܫܐ ܐܝܬܝܟܘܢ ܝܕܥܝܢ ܐܢܬܘܢ ܡܘܗܒܬܐ ܛܒܬܐ ܠܡܬܠ ܠܒܢܝܟܘܢ ܟܡܐ ܝܬܝܪܐܝܬ ܐܒܘܟܘܢ ܡܢ ܫܡܝܐ ܢܬܠ ܪܘܚܐ ܕܩܘܕܫܐ ܠܐܝܠܝܢ ܕܫܐܠܝܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 14, 'ܘܟܕ ܡܦܩ ܫܐܕܐ ܕܐܝܬܘܗܝ ܚܪܫܐ ܗܘܐ ܕܟܕ ܢܦܩ ܗܘ ܫܐܕܐ ܡܠܠ ܗܘ ܚܪܫܐ ܘܐܬܕܡܪܘ ܟܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 15, 'ܐܢܫܐ ܕܝܢ ܡܢܗܘܢ ܐܡܪܘ ܒܒܥܠܙܒܘܒ ܪܫܐ ܕܕܝܘܐ ܡܦܩ ܗܢܐ ܕܝܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 16, 'ܐܚܪܢܐ ܕܝܢ ܟܕ ܡܢܤܝܢ ܠܗ ܐܬܐ ܡܢ ܫܡܝܐ ܫܐܠܝܢ ܗܘܘ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 17, 'ܝܫܘܥ ܕܝܢ ܕܝܕܥ ܗܘܐ ܡܚܫܒܬܗܘܢ ܐܡܪ ܠܗܘܢ ܟܠ ܡܠܟܘ ܕܬܬܦܠܓ ܥܠ ܢܦܫܗ ܬܚܪܒ ܘܒܝܬܐ ܕܥܠ ܩܢܘܡܗ ܡܬܦܠܓ ܢܦܠ ܀ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 18, 'ܘܐܢ ܤܛܢܐ ܥܠ ܢܦܫܗ ܐܬܦܠܓ ܐܝܟܢܐ ܬܩܘܡ ܡܠܟܘܬܗ ܕܐܡܪܝܢ ܐܢܬܘܢ ܕܒܒܥܠܙܒܘܒ ܡܦܩ ܐܢܐ ܕܝܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 19, 'ܘܐܢ ܐܢܐ ܒܒܥܠܙܒܘܒ ܡܦܩ ܐܢܐ ܕܝܘܐ ܒܢܝܟܘܢ ܒܡܢܐ ܡܦܩܝܢ ܡܛܠ ܗܢܐ ܗܢܘܢ ܢܗܘܘܢ ܠܟܘܢ ܕܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 20, 'ܐܢ ܕܝܢ ܒܨܒܥܐ ܕܐܠܗܐ ܡܦܩ ܐܢܐ ܕܝܘܐ ܩܪܒܬ ܠܗ ܥܠܝܟܘܢ ܡܠܟܘܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 21, 'ܐܡܬܝ ܕܚܤܝܢܐ ܟܕ ܡܙܝܢ ܢܛܪ ܕܪܬܗ ܒܫܝܢܐ ܗܘ ܩܢܝܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 22, 'ܐܢ ܕܝܢ ܢܐܬܐ ܡܢ ܕܚܤܝܢ ܡܢܗ ܢܙܟܝܘܗܝ ܟܠܗ ܙܝܢܗ ܫܩܠ ܗܘ ܕܬܟܝܠ ܗܘܐ ܥܠܘܗܝ ܘܒܙܬܗ ܡܦܠܓ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 23, 'ܡܢ ܕܠܐ ܗܘܐ ܥܡܝ ܠܘܩܒܠܝ ܗܘ ܘܡܢ ܕܠܐ ܟܢܫ ܥܡܝ ܡܒܕܪܘ ܡܒܕܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 24, 'ܪܘܚܐ ܛܢܦܬܐ ܡܐ ܕܢܦܩܬ ܡܢ ܒܪ ܐܢܫܐ ܐܙܠܐ ܡܬܟܪܟܐ ܒܐܬܪܘܬܐ ܕܡܝܐ ܠܝܬ ܒܗܘܢ ܕܬܒܥܐ ܠܗ ܢܝܚܐ ܘܡܐ ܕܠܐ ܐܫܟܚܬ ܐܡܪܐ ܐܗܦܘܟ ܠܒܝܬܝ ܐܝܡܟܐ ܕܢܦܩܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 25, 'ܘܐܢ ܐܬܬ ܐܫܟܚܬܗ ܕܚܡܝܡ ܘܡܨܒܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 26, 'ܗܝܕܝܢ ܐܙܠܐ ܕܒܪܐ ܫܒܥ ܪܘܚܝܢ ܐܚܪܢܝܢ ܕܒܝܫܢ ܡܢܗ ܘܥܐܠܢ ܘܥܡܪܢ ܬܡܢ ܘܗܘܝܐ ܚܪܬܗ ܕܒܪܢܫܐ ܗܘ ܒܝܫܐ ܡܢ ܩܕܡܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 27, 'ܘܟܕ ܗܠܝܢ ܡܡܠܠ ܗܘܐ ܐܪܝܡܬ ܐܢܬܬܐ ܚܕܐ ܩܠܗ ܡܢ ܟܢܫܐ ܘܐܡܪܬ ܠܗ ܛܘܒܝܗ ܠܟܪܤܐ ܕܛܥܢܬܟ ܘܠܬܕܝܐ ܕܐܝܢܩܘܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 28, 'ܐܡܪ ܠܗ ܗܘ ܛܘܒܝܗܘܢ ܠܐܝܠܝܢ ܕܫܡܥܘ ܡܠܬܗ ܕܐܠܗܐ ܘܢܛܪܝܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 29, 'ܘܟܕ ܡܬܟܢܫܝܢ ܗܘܘ ܟܢܫܐ ܫܪܝ ܠܡܐܡܪ ܫܪܒܬܐ ܗܕܐ ܒܝܫܬܐ ܐܬܐ ܒܥܝܐ ܘܐܬܐ ܠܐ ܬܬܝܗܒ ܠܗ ܐܠܐ ܐܬܗ ܕܝܘܢܢ ܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 30, 'ܐܝܟܢܐ ܓܝܪ ܕܗܘܐ ܝܘܢܢ ܐܬܐ ܠܢܝܢܘܝܐ ܗܟܢܐ ܢܗܘܐ ܐܦ ܒܪܗ ܕܐܢܫܐ ܠܫܪܒܬܐ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 31, 'ܡܠܟܬܐ ܕܬܝܡܢܐ ܬܩܘܡ ܒܕܝܢܐ ܥܡ ܐܢܫܐ ܕܫܪܒܬܐ ܗܕܐ ܘܬܚܝܒ ܐܢܘܢ ܕܐܬܬ ܡܢ ܥܒܪܝܗ ܕܐܪܥܐ ܕܬܫܡܥ ܚܟܡܬܗ ܕܫܠܝܡܘܢ ܘܗܐ ܕܝܬܝܪ ܡܢ ܫܠܝܡܘܢ ܗܪܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 32, 'ܓܒܪܐ ܢܝܢܘܝܐ ܢܩܘܡܘܢ ܒܕܝܢܐ ܥܡ ܫܪܒܬܐ ܗܕܐ ܘܢܚܝܒܘܢܗ ܕܬܒܘ ܒܟܪܘܙܘܬܗ ܕܝܘܢܢ ܘܗܐ ܕܝܬܝܪ ܡܢ ܝܘܢܢ ܗܪܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 33, 'ܠܐ ܐܢܫ ܡܢܗܪ ܫܪܓܐ ܘܤܐܡ ܠܗ ܒܟܤܝܐ ܐܘ ܬܚܝܬ ܤܐܬܐ ܐܠܐ ܠܥܠ ܡܢ ܡܢܪܬܐ ܕܐܝܠܝܢ ܕܥܐܠܝܢ ܢܚܙܘܢ ܢܘܗܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 34, 'ܫܪܓܗ ܕܦܓܪܟ ܐܝܬܝܗ ܥܝܢܟ ܐܡܬܝ ܗܟܝܠ ܕܥܝܢܟ ܦܫܝܛܐ ܐܦ ܟܠܗ ܦܓܪܟ ܢܗܘܐ ܢܗܝܪ ܐܢ ܕܝܢ ܬܗܘܐ ܒܝܫܐ ܘܐܦ ܦܓܪܟ ܢܗܘܐ ܚܫܘܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 35, 'ܐܙܕܗܪ ܗܟܝܠ ܕܠܡܐ ܢܘܗܪܐ ܕܒܟ ܚܫܘܟܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 36, 'ܐܢ ܕܝܢ ܦܓܪܟ ܟܠܗ ܢܗܝܪ ܘܠܝܬ ܒܗ ܡܢܬܐ ܡܕܡ ܚܫܘܟܐ ܢܗܘܐ ܡܢܗܪ ܟܠܗ ܐܝܟ ܕܫܪܓܐ ܒܕܠܩܗ ܡܢܗܪ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 37, 'ܟܕ ܕܝܢ ܡܡܠܠ ܒܥܐ ܡܢܗ ܦܪܝܫܐ ܚܕ ܕܢܫܬܪܐ ܠܘܬܗ ܘܥܠ ܐܤܬܡܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 38, 'ܗܘ ܕܝܢ ܦܪܝܫܐ ܟܕ ܚܙܝܗܝ ܐܬܕܡܪ ܕܠܐ ܠܘܩܕܡ ܥܡܕ ܡܢ ܩܕܡ ܫܪܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 39, 'ܐܡܪ ܠܗ ܕܝܢ ܝܫܘܥ ܗܫܐ ܐܢܬܘܢ ܦܪܝܫܐ ܒܪܗ ܕܟܤܐ ܘܕܦܝܢܟܐ ܡܕܟܝܢ ܐܢܬܘܢ ܠܓܘ ܡܢܟܘܢ ܕܝܢ ܡܠܐ ܚܛܘܦܝܐ ܘܒܝܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 40, 'ܚܤܝܪܝ ܪܥܝܢܐ ܠܐ ܗܘܐ ܡܢ ܕܥܒܕ ܕܠܒܪ ܘܕܠܓܘ ܗܘ ܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 41, 'ܒܪܡ ܡܕܡ ܕܐܝܬ ܗܒܘܗܝ ܒܙܕܩܬܐ ܘܗܐ ܟܠܡܕܡ ܕܟܐ ܗܘ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 42, 'ܐܠܐ ܘܝ ܠܟܘܢ ܦܪܝܫܐ ܕܡܥܤܪܝܢ ܐܢܬܘܢ ܢܢܥܐ ܘܦܓܢܐ ܘܟܠ ܝܘܪܩ ܘܥܒܪܝܢ ܐܢܬܘܢ ܥܠ ܕܝܢܐ ܘܥܠ ܚܘܒܐ ܕܐܠܗܐ ܗܠܝܢ ܕܝܢ ܘܠܐ ܗܘܐ ܕܬܥܒܕܘܢ ܘܗܠܝܢ ܠܐ ܬܫܒܩܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 43, 'ܘܝ ܠܟܘܢ ܦܪܝܫܐ ܕܪܚܡܝܢ ܐܢܬܘܢ ܪܝܫ ܡܘܬܒܐ ܒܟܢܘܫܬܐ ܘܫܠܡܐ ܒܫܘܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 44, 'ܘܝ ܠܟܘܢ ܤܦܪܐ ܘܦܪܝܫܐ ܢܤܒܝ ܒܐܦܐ ܕܐܝܬܝܟܘܢ ܐܝܟ ܩܒܪܐ ܕܠܐ ܝܕܝܥܝܢ ܘܒܢܝ ܐܢܫܐ ܡܗܠܟܝܢ ܥܠܝܗܘܢ ܘܠܐ ܝܕܥܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 45, 'ܘܥܢܐ ܚܕ ܡܢ ܤܦܪܐ ܘܐܡܪ ܠܗ ܡܠܦܢܐ ܟܕ ܗܠܝܢ ܐܡܪ ܐܢܬ ܐܦ ܠܢ ܡܨܥܪ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 46, 'ܗܘ ܕܝܢ ܐܡܪ ܐܦ ܠܟܘܢ ܤܦܪܐ ܘܝ ܕܡܛܥܢܝܢ ܐܢܬܘܢ ܠܒܢܝ ܐܢܫܐ ܡܘܒܠܐ ܝܩܝܪܬܐ ܘܐܢܬܘܢ ܒܚܕܐ ܡܢ ܨܒܥܬܟܘܢ ܠܐ ܩܪܒܝܢ ܐܢܬܘܢ ܠܗܝܢ ܠܡܘܒܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 47, 'ܘܝ ܠܟܘܢ ܕܒܢܝܢ ܐܢܬܘܢ ܩܒܪܐ ܕܢܒܝܐ ܕܐܒܗܝܟܘܢ ܩܛܠܘ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 48, 'ܤܗܕܝܢ ܐܢܬܘܢ ܗܟܝܠ ܘܨܒܝܢ ܐܢܬܘܢ ܒܥܒܕܐ ܕܐܒܗܝܟܘܢ ܕܗܢܘܢ ܩܛܠܘ ܐܢܘܢ ܘܐܢܬܘܢ ܒܢܝܢ ܐܢܬܘܢ ܩܒܪܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 49, 'ܡܛܠ ܗܢܐ ܐܦ ܚܟܡܬܐ ܕܐܠܗܐ ܐܡܪܬ ܕܗܐ ܐܢܐ ܐܫܕܪ ܠܗܘܢ ܢܒܝܐ ܘܫܠܝܚܐ ܡܢܗܘܢ ܢܪܕܦܘܢ ܘܢܩܛܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 50, 'ܕܢܬܬܒܥ ܕܡܐ ܕܟܠܗܘܢ ܢܒܝܐ ܕܐܬܐܫܕ ܡܢ ܕܐܬܒܪܝ ܥܠܡܐ ܡܢ ܫܪܒܬܐ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 51, 'ܡܢ ܕܡܗ ܕܗܒܝܠ ܥܕܡܐ ܠܕܡܗ ܕܙܟܪܝܐ ܗܘ ܕܐܬܩܛܠ ܒܝܢܝ ܗܝܟܠܐ ܠܡܕܒܚܐ ܐܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܡܬܬܒܥ ܡܢ ܫܪܒܬܐ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 52, 'ܘܝ ܠܟܘܢ ܤܦܪܐ ܕܫܩܠܬܘܢ ܩܠܝܕܐ ܕܝܕܥܬܐ ܐܢܬܘܢ ܠܐ ܥܠܬܘܢ ܘܠܐܝܠܝܢ ܕܥܐܠܝܢ ܟܠܝܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 53, 'ܘܟܕ ܗܠܝܢ ܐܡܪ ܗܘܐ ܠܗܘܢ ܫܪܝܘ ܤܦܪܐ ܘܦܪܝܫܐ ܡܬܒܐܫ ܠܗܘܢ ܘܡܬܚܡܬܝܢ ܘܡܬܟܤܝܢ ܡܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 54, 'ܘܢܟܠܝܢ ܠܗ ܒܤܓܝܐܬܐ ܟܕ ܒܥܝܢ ܠܡܐܚܕ ܡܕܡ ܡܢ ܦܘܡܗ ܕܢܫܟܚܘܢ ܢܐܟܠܘܢ ܩܪܨܘܗܝ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 1, 'ܘܟܕ ܐܬܟܢܫܘ ܪܒܘܬܐ ܕܟܢܫܐ ܤܓܝܐܐ ܐܝܟܢܐ ܕܢܕܝܫܘܢ ܚܕ ܠܚܕ ܫܪܝ ܝܫܘܥ ܠܡܐܡܪ ܠܬܠܡܝܕܘܗܝ ܠܘܩܕܡ ܐܙܕܗܪܘ ܒܢܦܫܟܘܢ ܡܢ ܚܡܝܪܐ ܕܦܪܝܫܐ ܕܐܝܬܘܗܝ ܡܤܒ ܒܐܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 2, 'ܠܝܬ ܕܝܢ ܡܕܡ ܕܟܤܐ ܕܠܐ ܢܬܓܠܐ ܘܠܐ ܕܡܛܫܝ ܕܠܐ ܢܬܝܕܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 3, 'ܟܠ ܓܝܪ ܕܒܚܫܘܟܐ ܐܡܪܬܘܢ ܒܢܗܝܪܐ ܢܫܬܡܥ ܘܡܕܡ ܕܒܬܘܢܐ ܒܐܕܢܐ ܠܚܫܬܘܢ ܥܠ ܐܓܪܐ ܢܬܟܪܙ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 4, 'ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܝܢ ܠܪܚܡܝ ܠܐ ܬܕܚܠܘܢ ܡܢ ܐܝܠܝܢ ܕܩܛܠܝܢ ܦܓܪܐ ܘܡܢ ܒܬܪܟܢ ܠܝܬ ܠܗܘܢ ܡܕܡ ܝܬܝܪ ܠܡܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 5, 'ܐܚܘܝܟܘܢ ܕܝܢ ܡܢ ܡܢ ܬܕܚܠܘܢ ܡܢ ܗܘ ܕܡܢ ܒܬܪ ܕܩܛܠ ܫܠܝܛ ܠܡܪܡܝܘ ܒܓܗܢܐ ܐܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܡܢ ܗܢܐ ܕܚܠܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 6, 'ܠܐ ܚܡܫ ܨܦܪܝܢ ܡܙܕܒܢܢ ܒܬܪܝܢ ܐܤܪܝܢ ܘܚܕܐ ܡܢܗܝܢ ܠܐ ܛܥܝܐ ܩܕܡ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 7, 'ܕܝܠܟܘܢ ܕܝܢ ܐܦ ܡܢܐ ܕܤܥܪܐ ܕܪܫܟܘܢ ܟܠܗܝܢ ܡܢܝܢ ܐܢܝܢ ܠܐ ܗܟܝܠ ܬܕܚܠܘܢ ܕܡܢ ܤܘܓܐܐ ܕܨܦܪܐ ܡܝܬܪܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 8, 'ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܝܢ ܕܟܠ ܕܢܘܕܐ ܒܝ ܩܕܡ ܒܢܝܢܫܐ ܐܦ ܒܪܗ ܕܐܢܫܐ ܢܘܕܐ ܒܗ ܩܕܡ ܡܠܐܟܘܗܝ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 9, 'ܐܝܢܐ ܕܝܢ ܕܟܦܪ ܒܝ ܩܕܡ ܒܢܝܢܫܐ ܢܬܟܦܪ ܒܗ ܩܕܡ ܡܠܐܟܘܗܝ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 10, 'ܘܟܠ ܕܢܐܡܪ ܡܠܬܐ ܥܠ ܒܪܗ ܕܐܢܫܐ ܢܫܬܒܩ ܠܗ ܡܢ ܕܥܠ ܪܘܚܐ ܕܝܢ ܕܩܘܕܫܐ ܢܓܕܦ ܠܐ ܢܫܬܒܩ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 11, 'ܡܐ ܕܡܩܪܒܝܢ ܠܟܘܢ ܕܝܢ ܠܟܢܘܫܬܐ ܩܕܡ ܪܫܐ ܘܫܠܝܛܢܐ ܠܐ ܬܐܨܦܘܢ ܐܝܟܢܐ ܬܦܩܘܢ ܪܘܚܐ ܐܘ ܡܢܐ ܬܐܡܪܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 12, 'ܪܘܚܐ ܓܝܪ ܕܩܘܕܫܐ ܢܠܦܟܘܢ ܒܗܝ ܫܥܬܐ ܡܕܡ ܕܘܠܐ ܕܬܐܡܪܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 13, 'ܐܡܪ ܠܗ ܕܝܢ ܐܢܫ ܡܢ ܗܘ ܟܢܫܐ ܡܠܦܢܐ ܐܡܪ ܠܐܚܝ ܦܠܓ ܥܡܝ ܝܪܬܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 14, 'ܝܫܘܥ ܕܝܢ ܐܡܪ ܠܗ ܓܒܪܐ ܡܢܘ ܐܩܝܡܢܝ ܥܠܝܟܘܢ ܕܝܢܐ ܘܡܦܠܓܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 15, 'ܘܐܡܪ ܠܬܠܡܝܕܘܗܝ ܐܙܕܗܪܘ ܡܢ ܟܠܗ ܝܥܢܘܬܐ ܡܛܠ ܕܠܐ ܗܘܐ ܒܝܬܝܪܘܬܐ ܕܢܟܤܐ ܐܝܬ ܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 16, 'ܘܐܡܪ ܡܬܠܐ ܠܘܬܗܘܢ ܓܒܪܐ ܚܕ ܥܬܝܪܐ ܐܥܠܬ ܠܗ ܐܪܥܗ ܥܠܠܬܐ ܤܓܝܐܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 17, 'ܘܡܬܚܫܒ ܗܘܐ ܒܢܦܫܗ ܘܐܡܪ ܡܢܐ ܐܥܒܕ ܕܠܝܬ ܠܝ ܐܝܟܐ ܕܐܚܡܘܠ ܥܠܠܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 18, 'ܘܐܡܪ ܗܕܐ ܐܥܒܕ ܐܤܬܘܪ ܒܝܬ ܩܦܤܝ ܘܐܒܢܐ ܘܐܘܪܒ ܐܢܘܢ ܘܐܚܡܘܠ ܬܡܢ ܟܠܗ ܥܒܘܪܝ ܘܛܒܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 19, 'ܘܐܡܪ ܠܢܦܫܝ ܢܦܫܝ ܐܝܬ ܠܟܝ ܛܒܬܐ ܤܓܝܐܬܐ ܕܤܝܡܢ ܠܫܢܝܐ ܤܓܝܐܬܐ ܐܬܬܢܝܚܝ ܐܟܘܠܝ ܐܫܬܝ ܐܬܒܤܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 20, 'ܐܡܪ ܠܗ ܕܝܢ ܐܠܗܐ ܚܤܝܪ ܪܥܝܢܐ ܒܗܢܐ ܠܠܝܐ ܢܦܫܟ ܬܒܥܝܢ ܠܗ ܡܢܟ ܘܗܠܝܢ ܕܛܝܒܬ ܠܡܢ ܢܗܘܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 21, 'ܗܟܢܐ ܗܘ ܡܢ ܕܤܐܡ ܠܗ ܤܝܡܬܐ ܘܒܐܠܗܐ ܠܐ ܥܬܝܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 22, 'ܘܐܡܪ ܠܬܠܡܝܕܘܗܝ ܡܛܠ ܗܢܐ ܠܟܘܢ ܐܡܪ ܐܢܐ ܠܐ ܬܐܨܦܘܢ ܠܢܦܫܟܘܢ ܡܢܐ ܬܐܟܠܘܢ ܘܠܐ ܠܦܓܪܟܘܢ ܡܢܐ ܬܠܒܫܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 23, 'ܢܦܫܐ ܓܝܪ ܝܬܝܪܐ ܡܢ ܤܝܒܪܬܐ ܘܦܓܪܐ ܡܢ ܠܒܘܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 24, 'ܐܬܒܩܘ ܒܢܥܒܐ ܕܠܐ ܙܪܥܝܢ ܘܠܐ ܚܨܕܝܢ ܘܠܝܬ ܠܗܘܢ ܬܘܢܐ ܘܒܝܬ ܩܦܤܐ ܘܐܠܗܐ ܡܬܪܤܐ ܠܗܘܢ ܟܡܐ ܗܟܝܠ ܐܢܬܘܢ ܝܬܝܪܝܢ ܐܢܬܘܢ ܡܢ ܦܪܚܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 25, 'ܐܝܢܐ ܕܝܢ ܡܢܟܘܢ ܟܕ ܝܨܦ ܡܫܟܚ ܠܡܘܤܦܘ ܥܠ ܩܘܡܬܗ ܐܡܬܐ ܚܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 26, 'ܐܢ ܕܝܢ ܐܦ ܠܐ ܙܥܘܪܬܐ ܡܫܟܚܝܢ ܐܢܬܘܢ ܡܢܐ ܥܠ ܫܪܟܐ ܝܨܦܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 27, 'ܐܬܒܩܘ ܒܫܘܫܢܐ ܐܝܟܢܐ ܪܒܝܢ ܕܠܐ ܠܐܝܢ ܘܠܐ ܥܙܠܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܝܢ ܕܐܦ ܠܐ ܫܠܝܡܘܢ ܒܟܠܗ ܫܘܒܚܗ ܐܬܟܤܝ ܐܝܟ ܚܕܐ ܡܢ ܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 28, 'ܐܢ ܕܝܢ ܠܥܡܝܪܐ ܕܝܘܡܢܐ ܐܝܬܘܗܝ ܒܚܩܠܐ ܘܡܚܪ ܢܦܠ ܒܬܢܘܪܐ ܐܠܗܐ ܗܟܢܐ ܡܠܒܫ ܟܡܐ ܝܬܝܪ ܠܟܘܢ ܙܥܘܪܝ ܗܝܡܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 29, 'ܘܐܢܬܘܢ ܠܐ ܬܒܥܘܢ ܡܢܐ ܬܐܟܠܘܢ ܘܡܢܐ ܬܫܬܘܢ ܘܠܐ ܢܦܗܐ ܪܥܝܢܟܘܢ ܒܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 30, 'ܗܠܝܢ ܓܝܪ ܟܠܗܝܢ ܥܡܡܐ ܗܘ ܕܥܠܡܐ ܒܥܝܢ ܐܦ ܠܟܘܢ ܕܝܢ ܐܒܘܟܘܢ ܝܕܥ ܕܡܬܒܥܝܢ ܠܟܘܢ ܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 31, 'ܒܪܡ ܒܥܘ ܡܠܟܘܬܗ ܕܐܠܗܐ ܘܗܠܝܢ ܟܠܗܝܢ ܡܬܬܘܤܦܢ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 32, 'ܠܐ ܬܕܚܠ ܓܙܪܐ ܙܥܘܪܐ ܕܨܒܐ ܐܒܘܟܘܢ ܕܢܬܠ ܠܟܘܢ ܡܠܟܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 33, 'ܙܒܢܘ ܩܢܝܢܟܘܢ ܘܗܒܘ ܙܕܩܬܐ ܥܒܕܘ ܠܟܘܢ ܟܝܤܐ ܕܠܐ ܒܠܝܢ ܘܤܝܡܬܐ ܕܠܐ ܓܝܙܐ ܒܫܡܝܐ ܐܝܟܐ ܕܓܢܒܐ ܠܐ ܩܪܒ ܘܤܤܐ ܠܐ ܡܚܒܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 34, 'ܐܝܟܐ ܓܝܪ ܕܐܝܬܝܗ ܤܝܡܬܟܘܢ ܬܡܢ ܢܗܘܐ ܐܦ ܠܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 35, 'ܢܗܘܘܢ ܐܤܝܪܝܢ ܚܨܝܟܘܢ ܘܡܢܗܪܝܢ ܫܪܓܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 36, 'ܘܗܘܘ ܕܡܝܢ ܠܐܢܫܐ ܕܡܤܟܝܢ ܠܡܪܗܘܢ ܕܐܡܬܝ ܢܦܢܐ ܡܢ ܒܝܬ ܡܫܬܘܬܐ ܕܡܐ ܕܐܬܐ ܘܢܩܫ ܡܚܕܐ ܢܦܬܚܘܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 37, 'ܛܘܒܝܗܘܢ ܠܥܒܕܐ ܗܢܘܢ ܐܝܠܝܢ ܕܢܐܬܐ ܡܪܗܘܢ ܘܢܫܟܚ ܐܢܘܢ ܟܕ ܥܝܪܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܢܐܤܘܪ ܚܨܘܗܝ ܘܢܤܡܟ ܐܢܘܢ ܘܢܥܒܪ ܢܫܡܫ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 38, 'ܘܐܢ ܒܡܛܪܬܐ ܕܬܪܬܝܢ ܐܘ ܕܬܠܬ ܢܐܬܐ ܘܢܫܟܚ ܗܟܢܐ ܛܘܒܝܗܘܢ ܠܥܒܕܐ ܗܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 39, 'ܗܕܐ ܕܝܢ ܕܥܘ ܕܐܠܘ ܝܕܥ ܗܘܐ ܡܪܐ ܒܝܬܐ ܒܐܝܕܐ ܡܛܪܬܐ ܐܬܐ ܓܢܒܐ ܡܬܬܥܝܪ ܗܘܐ ܘܠܐ ܫܒܩ ܗܘܐ ܕܢܬܦܠܫ ܒܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 40, 'ܐܦ ܐܢܬܘܢ ܗܟܝܠ ܗܘܘ ܡܛܝܒܐ ܕܒܗܝ ܫܥܬܐ ܕܠܐ ܤܒܪܝܢ ܐܢܬܘܢ ܐܬܐ ܒܪܗ ܕܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 41, 'ܐܡܪ ܠܗ ܫܡܥܘܢ ܟܐܦܐ ܡܪܢ ܠܘܬܢ ܐܡܪ ܐܢܬ ܠܗ ܠܡܬܠܐ ܗܢܐ ܐܘ ܐܦ ܠܘܬ ܟܠܢܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 42, 'ܐܡܪ ܠܗ ܝܫܘܥ ܡܢܘ ܟܝ ܐܝܬ ܪܒܝܬܐ ܡܗܝܡܢܐ ܘܚܟܝܡܐ ܕܢܩܝܡܝܘܗܝ ܡܪܗ ܥܠ ܬܫܡܫܬܗ ܕܢܬܠ ܦܪܤܐ ܒܙܒܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 43, 'ܛܘܒܘܗܝ ܠܗܘ ܥܒܕܐ ܕܢܐܬܐ ܡܪܗ ܢܫܟܚܝܘܗܝ ܕܥܒܕ ܗܟܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 44, 'ܫܪܝܪܐܝܬ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܢܩܝܡܝܘܗܝ ܥܠ ܟܠܗ ܩܢܝܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 45, 'ܐܢ ܕܝܢ ܢܐܡܪ ܥܒܕܐ ܗܘ ܒܠܒܗ ܕܡܪܝ ܡܘܚܪ ܠܡܐܬܐ ܘܢܫܪܐ ܠܡܡܚܐ ܠܥܒܕܐ ܘܠܐܡܗܬܐ ܕܡܪܗ ܘܢܫܪܐ ܠܡܠܥܤ ܘܠܡܫܬܐ ܘܠܡܪܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 46, 'ܢܐܬܐ ܡܪܗ ܕܥܒܕܐ ܗܘ ܒܝܘܡܐ ܕܠܐ ܤܒܪ ܘܒܫܥܬܐ ܕܠܐ ܝܕܥ ܘܢܦܠܓܝܘܗܝ ܘܢܤܝܡ ܡܢܬܗ ܥܡ ܗܢܘܢ ܕܠܐ ܡܗܝܡܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 47, 'ܥܒܕܐ ܕܝܢ ܐܝܢܐ ܕܝܕܥ ܨܒܝܢܐ ܕܡܪܗ ܘܠܐ ܛܝܒ ܠܗ ܐܝܟ ܨܒܝܢܗ ܢܒܠܥ ܤܓܝܐܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 48, 'ܗܘ ܕܝܢ ܕܠܐ ܝܕܥ ܘܥܒܕ ܡܕܡ ܕܫܘܐ ܠܡܚܘܬܐ ܢܒܠܥ ܡܚܘܬܐ ܙܥܘܪܝܬܐ ܟܠ ܓܝܪ ܕܐܬܝܗܒ ܠܗ ܤܓܝ ܤܓܝ ܢܬܬܒܥ ܡܢܗ ܘܠܗܘ ܕܐܓܥܠܘ ܠܗ ܤܓܝ ܝܬܝܪܐܝܬ ܢܬܒܥܘܢ ܒܐܝܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 49, 'ܢܘܪܐ ܐܬܝܬ ܕܐܪܡܐ ܒܐܪܥܐ ܘܨܒܐ ܐܢܐ ܐܠܘ ܡܢ ܟܕܘ ܚܒܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 50, 'ܘܡܥܡܘܕܝܬܐ ܐܝܬ ܠܝ ܕܐܥܡܕ ܘܤܓܝ ܐܠܝܨ ܐܢܐ ܥܕܡܐ ܕܬܫܬܡܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 51, 'ܤܒܪܝܢ ܐܢܬܘܢ ܕܫܝܢܐ ܐܬܝܬ ܕܐܪܡܐ ܒܐܪܥܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܠܐ ܐܠܐ ܦܠܓܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 52, 'ܡܢ ܗܫܐ ܓܝܪ ܢܗܘܘܢ ܚܡܫܐ ܒܒܝܬܐ ܚܕ ܕܦܠܝܓܝܢ ܬܠܬܐ ܥܠ ܬܪܝܢ ܘܬܪܝܢ ܥܠ ܬܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 53, 'ܢܬܦܠܓ ܓܝܪ ܐܒܐ ܥܠ ܒܪܗ ܘܒܪܐ ܥܠ ܐܒܘܗܝ ܐܡܐ ܥܠ ܒܪܬܗ ܘܒܪܬܐ ܥܠ ܐܡܗ ܚܡܬܐ ܥܠ ܟܠܬܗ ܘܟܠܬܐ ܥܠ ܚܡܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 54, 'ܘܐܡܪ ܠܟܢܫܐ ܡܐ ܕܚܙܝܬܘܢ ܥܢܢܐ ܕܕܢܚܐ ܡܢ ܡܥܪܒܐ ܡܚܕܐ ܐܡܪܝܢ ܐܢܬܘܢ ܡܛܪܐ ܐܬܐ ܘܗܘܐ ܗܟܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 55, 'ܘܡܐ ܕܢܫܒܐ ܬܝܡܢܐ ܐܡܪܝܢ ܐܢܬܘܢ ܚܘܡܐ ܗܘܐ ܘܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 56, 'ܢܤܒܝ ܒܐܦܐ ܦܪܨܘܦܐ ܕܐܪܥܐ ܘܕܫܡܝܐ ܝܕܥܝܢ ܐܢܬܘܢ ܕܬܦܪܫܘܢ ܙܒܢܐ ܕܝܢ ܗܢܐ ܐܝܟܢܐ ܠܐ ܦܪܫܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 57, 'ܠܡܢܐ ܕܝܢ ܡܢ ܢܦܫܟܘܢ ܠܐ ܕܝܢܝܢ ܐܢܬܘܢ ܩܘܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 58, 'ܡܐ ܓܝܪ ܕܐܙܠ ܐܢܬ ܥܡ ܒܥܠܕܝܢܟ ܠܘܬ ܐܪܟܘܢܐ ܥܕ ܒܐܘܪܚܐ ܐܢܬ ܗܒ ܬܐܓܘܪܬܐ ܘܬܬܦܪܩ ܡܢܗ ܕܠܡܐ ܢܘܒܠܟ ܠܘܬ ܕܝܢܐ ܘܕܝܢܐ ܢܫܠܡܟ ܠܓܒܝܐ ܘܓܒܝܐ ܢܪܡܝܟ ܒܝܬ ܐܤܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 59, 'ܘܐܡܪ ܐܢܐ ܠܟ ܕܠܐ ܬܦܘܩ ܡܢ ܬܡܢ ܥܕܡܐ ܕܬܬܠ ܫܡܘܢܐ ܐܚܪܝܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 1, 'ܒܗܘ ܕܝܢ ܙܒܢܐ ܐܬܘ ܐܢܫܝܢ ܐܡܪܘ ܠܗ ܥܠ ܓܠܝܠܝܐ ܗܢܘܢ ܕܦܝܠܛܘܤ ܚܠܛ ܕܡܗܘܢ ܥܡ ܕܒܚܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 2, 'ܘܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܤܒܪܝܢ ܐܢܬܘܢ ܕܗܠܝܢ ܓܠܝܠܝܐ ܚܛܝܝܢ ܗܘܘ ܝܬܝܪ ܡܢ ܟܠܗܘܢ ܓܠܝܠܝܐ ܕܗܟܢܐ ܗܘܐ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 3, 'ܠܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܝܢ ܕܐܦ ܟܠܟܘܢ ܐܠܐ ܬܬܘܒܘܢ ܗܟܢܐ ܬܐܒܕܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 4, 'ܐܘ ܗܢܘܢ ܬܡܢܬܥܤܪ ܕܢܦܠ ܥܠܝܗܘܢ ܡܓܕܠܐ ܒܫܝܠܘܚܐ ܘܩܛܠ ܐܢܘܢ ܤܒܪܝܢ ܐܢܬܘܢ ܕܚܛܝܝܢ ܗܘܘ ܝܬܝܪ ܡܢ ܟܠܗܘܢ ܒܢܝܢܫܐ ܕܥܡܪܝܢ ܒܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 5, 'ܠܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܝܢ ܕܐܠܐ ܬܬܘܒܘܢ ܟܠܟܘܢ ܐܟܘܬܗܘܢ ܬܐܒܕܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 6, 'ܘܐܡܪ ܡܬܠܐ ܗܢܐ ܬܬܐ ܐܝܬ ܗܘܬ ܠܐܢܫ ܕܢܨܝܒܐ ܒܟܪܡܗ ܘܐܬܐ ܒܥܐ ܒܗ ܦܐܪܐ ܘܠܐ ܐܫܟܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 7, 'ܘܐܡܪ ܠܦܠܚܐ ܗܐ ܬܠܬ ܫܢܝܢ ܐܬܐ ܐܢܐ ܒܥܐ ܐܢܐ ܦܐܪܐ ܒܬܬܐ ܗܕܐ ܘܠܐ ܡܫܟܚ ܐܢܐ ܦܤܘܩܝܗ ܠܡܢܐ ܡܒܛܠܐ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 8, 'ܐܡܪ ܠܗ ܦܠܚܐ ܡܪܝ ܫܒܘܩܝܗ ܐܦ ܗܕܐ ܫܢܬܐ ܥܕ ܐܦܠܚܝܗ ܘܐܙܒܠܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 9, 'ܘܐܢ ܥܒܕܬ ܦܐܪܐ ܘܐܠܐ ܠܡܢܚܝ ܬܦܤܩܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 10, 'ܟܕ ܕܝܢ ܡܠܦ ܝܫܘܥ ܒܫܒܬܐ ܒܚܕܐ ܡܢ ܟܢܘܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 11, 'ܐܝܬ ܗܘܬ ܬܡܢ ܐܢܬܬܐ ܕܐܝܬ ܗܘܬ ܠܗ ܪܘܚܐ ܕܟܘܪܗܢܐ ܫܢܝܢ ܬܡܢܥܤܪܐ ܘܟܦܝܦܐ ܗܘܬ ܘܠܐ ܡܫܟܚܐ ܗܘܬ ܕܬܬܦܫܛ ܠܓܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 12, 'ܚܙܗ ܕܝܢ ܝܫܘܥ ܘܩܪܗ ܘܐܡܪ ܠܗ ܐܢܬܬܐ ܫܪܝܬܝ ܡܢ ܟܘܪܗܢܟܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 13, 'ܘܤܡ ܐܝܕܗ ܥܠܝܗ ܘܡܚܕܐ ܐܬܦܫܛܬ ܘܫܒܚܬ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 14, 'ܥܢܐ ܕܝܢ ܪܒ ܟܢܘܫܬܐ ܟܕ ܡܬܚܡܬ ܥܠ ܕܐܤܝ ܒܫܒܬܐ ܝܫܘܥ ܘܐܡܪ ܠܟܢܫܐ ܫܬܐ ܐܢܘܢ ܝܘܡܝܢ ܕܒܗܘܢ ܘܠܐ ܠܡܦܠܚ ܒܗܘܢ ܗܘܝܬܘܢ ܐܬܝܢ ܡܬܐܤܝܢ ܘܠܐ ܒܝܘܡܐ ܕܫܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 15, 'ܝܫܘܥ ܕܝܢ ܥܢܐ ܘܐܡܪ ܠܗ ܢܤܒ ܒܐܦܐ ܚܕ ܚܕ ܡܢܟܘܢ ܒܫܒܬܐ ܠܐ ܫܪܐ ܬܘܪܗ ܐܘ ܚܡܪܗ ܡܢ ܐܘܪܝܐ ܘܐܙܠ ܡܫܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 16, 'ܗܕܐ ܕܝܢ ܕܒܪܬܗ ܗܝ ܕܐܒܪܗܡ ܘܐܤܪܗ ܐܟܠܩܪܨܐ ܗܐ ܬܡܢܥܤܪܐ ܫܢܝܢ ܠܐ ܘܠܐ ܗܘܐ ܕܬܫܬܪܐ ܡܢ ܗܢܐ ܐܤܘܪܝܐ ܒܝܘܡܐ ܕܫܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 17, 'ܘܟܕ ܗܠܝܢ ܐܡܪ ܗܘܐ ܒܗܬܝܢ ܗܘܘ ܟܠܗܘܢ ܐܝܠܝܢ ܕܩܝܡܝܢ ܗܘܘ ܠܩܘܒܠܗ ܘܟܠܗ ܥܡܐ ܚܕܐ ܗܘܐ ܒܟܠܗܝܢ ܬܡܝܗܬܐ ܕܗܘܝܢ ܗܘܝ ܒܐܝܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 18, 'ܐܡܪ ܗܘܐ ܕܝܢ ܝܫܘܥ ܠܡܢܐ ܕܡܝܐ ܡܠܟܘܬܐ ܕܐܠܗܐ ܘܠܡܢܐ ܐܕܡܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 19, 'ܕܡܝܐ ܠܦܪܕܬܐ ܕܚܪܕܠܐ ܗܝ ܕܢܤܒ ܓܒܪܐ ܐܪܡܝܗ ܒܓܢܬܗ ܘܪܒܬ ܘܗܘܬ ܐܝܠܢܐ ܪܒܐ ܘܦܪܚܬܐ ܕܫܡܝܐ ܐܩܢܬ ܒܤܘܟܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 20, 'ܬܘܒ ܐܡܪ ܝܫܘܥ ܠܡܢܐ ܐܕܡܝܗ ܠܡܠܟܘܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 21, 'ܕܡܝܐ ܠܚܡܝܪܐ ܕܢܤܒܬ ܐܢܬܬܐ ܛܡܪܬ ܒܩܡܚܐ ܕܬܠܬ ܤܐܝܢ ܥܕܡܐ ܕܟܠܗ ܚܡܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 22, 'ܘܪܕܐ ܗܘܐ ܒܩܘܪܝܐ ܘܒܡܕܝܢܬܐ ܟܕ ܡܠܦ ܘܐܙܠ ܠܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 23, 'ܫܐܠܗ ܕܝܢ ܐܢܫ ܕܐܢ ܙܥܘܪܝܢ ܐܢܘܢ ܐܝܠܝܢ ܕܚܐܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 24, 'ܝܫܘܥ ܕܝܢ ܐܡܪ ܠܗܘܢ ܐܬܟܬܫܘ ܠܡܥܠ ܒܬܪܥܐ ܐܠܝܨܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܓܝܪ ܕܤܓܝܐܐ ܢܒܥܘܢ ܠܡܥܠ ܘܠܐ ܢܫܟܚܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 25, 'ܡܢ ܫܥܬܐ ܕܢܩܘܡ ܡܪܐ ܒܝܬܐ ܘܢܐܚܘܕ ܬܪܥܐ ܘܬܗܘܘܢ ܩܝܡܝܢ ܠܒܪ ܘܢܩܫܝܢ ܒܬܪܥܐ ܘܬܫܪܘܢ ܠܡܐܡܪ ܡܪܢ ܡܪܢ ܦܬܚ ܠܢ ܘܢܥܢܐ ܗܘ ܘܢܐܡܪ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܠܐ ܝܕܥ ܐܢܐ ܠܟܘܢ ܐܝܡܟܐ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 26, 'ܘܬܫܪܘܢ ܠܡܐܡܪ ܩܕܡܝܟ ܐܟܠܢ ܘܐܫܬܝܢ ܘܒܫܘܩܝܢ ܐܠܦܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 27, 'ܘܢܐܡܪ ܠܟܘܢ ܕܠܐ ܝܕܥ ܐܢܐ ܠܟܘܢ ܐܝܡܟܐ ܐܢܬܘܢ ܦܪܘܩܘ ܠܟܘܢ ܡܢܝ ܦܠܚܝ ܫܘܩܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 28, 'ܬܡܢ ܢܗܘܐ ܒܟܝܐ ܘܚܘܪܩ ܫܢܐ ܟܕ ܬܚܙܘܢ ܠܐܒܪܗܡ ܘܠܐܝܤܚܩ ܘܠܝܥܩܘܒ ܘܠܟܠܗܘܢ ܢܒܝܐ ܒܡܠܟܘܬܐ ܕܐܠܗܐ ܐܢܬܘܢ ܕܝܢ ܬܗܘܘܢ ܡܦܩܝܢ ܠܒܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 29, 'ܘܢܐܬܘܢ ܡܢ ܡܕܢܚܐ ܘܡܢ ܡܥܪܒܐ ܘܡܢ ܬܝܡܢܐ ܘܡܢ ܓܪܒܝܐ ܘܢܤܬܡܟܘܢ ܒܡܠܟܘܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 30, 'ܘܗܐ ܐܝܬ ܐܚܪܝܐ ܕܢܗܘܘܢ ܩܕܡܝܐ ܘܐܝܬ ܩܕܡܝܐ ܕܢܗܘܘܢ ܐܚܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 31, 'ܒܗ ܒܗܘ ܝܘܡܐ ܩܪܒܘ ܐܢܫܐ ܡܢ ܦܪܝܫܐ ܘܐܡܪܝܢ ܠܗ ܦܘܩ ܙܠ ܠܟ ܡܟܐ ܡܛܠ ܕܗܪܘܕܤ ܨܒܐ ܠܡܩܛܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 32, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܙܠܘ ܐܡܪܘ ܠܬܥܠܐ ܗܢܐ ܕܗܐ ܡܦܩ ܐܢܐ ܫܐܕܐ ܘܐܤܘܬܐ ܥܒܕ ܐܢܐ ܝܘܡܢܐ ܘܡܚܪ ܘܠܝܘܡܐ ܕܬܠܬܐ ܡܫܬܡܠܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 33, 'ܒܪܡ ܘܠܐ ܠܝ ܕܝܘܡܢܐ ܘܡܚܪ ܐܤܥܘܪ ܘܠܝܘܡܐ ܐܚܪܢܐ ܐܙܠ ܡܛܠ ܕܠܐ ܡܫܟܚܐ ܕܢܒܝܐ ܢܐܒܕ ܠܒܪ ܡܢ ܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 34, 'ܐܘܪܫܠܡ ܐܘܪܫܠܡ ܩܛܠܬ ܢܒܝܐ ܘܪܓܡܬ ܠܐܝܠܝܢ ܕܫܠܝܚܝܢ ܠܘܬܗ ܟܡܐ ܙܒܢܝܢ ܨܒܝܬ ܠܡܟܢܫܘ ܒܢܝܟܝ ܐܝܟ ܬܪܢܓܘܠܬܐ ܕܟܢܫܐ ܦܪܘܓܝܗ ܬܚܝܬ ܓܦܝܗ ܘܠܐ ܨܒܝܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 35, 'ܗܐ ܡܫܬܒܩ ܠܟܘܢ ܒܝܬܟܘܢ ܚܪܒܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܓܝܪ ܕܠܐ ܬܚܙܘܢܢܝ ܥܕܡܐ ܕܬܐܡܪܘܢ ܒܪܝܟ ܗܘ ܕܐܬܐ ܒܫܡܗ ܕܡܪܝܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 1, 'ܘܗܘܐ ܕܟܕ ܥܠ ܠܒܝܬܐ ܕܚܕ ܡܢ ܪܫܐ ܕܦܪܝܫܐ ܕܢܐܟܘܠ ܠܚܡܐ ܒܝܘܡܐ ܕܫܒܬܐ ܘܗܢܘܢ ܢܛܪܝܢ ܗܘܘ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 2, 'ܘܗܐ ܓܒܪܐ ܚܕ ܕܟܢܝܫ ܗܘܐ ܡܝܐ ܐܝܬ ܗܘܐ ܩܕܡܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 3, 'ܘܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܤܦܪܐ ܘܠܦܪܝܫܐ ܕܐܢ ܫܠܝܛ ܒܫܒܬܐ ܠܡܐܤܝܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 4, 'ܗܢܘܢ ܕܝܢ ܫܬܩܘ ܘܐܚܕܗ ܗܘ ܘܐܤܝܗ ܘܫܪܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 5, 'ܘܐܡܪ ܠܗܘܢ ܡܢܘ ܡܢܟܘܢ ܕܢܦܠ ܒܪܗ ܐܘ ܬܘܪܗ ܒܒܪܐ ܒܝܘܡܐ ܕܫܒܬܐ ܘܠܐ ܡܚܕܐ ܕܠܐ ܡܤܩ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 6, 'ܘܠܐ ܐܫܟܚܘ ܠܡܬܠ ܠܗ ܦܬܓܡܐ ܥܠ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 7, 'ܘܐܡܪ ܗܘܐ ܡܬܠܐ ܠܘܬ ܗܢܘܢ ܕܡܙܡܢܝܢ ܬܡܢ ܥܠ ܕܚܙܐ ܗܘܐ ܠܗܘܢ ܕܡܓܒܝܢ ܕܘܟܝܬܐ ܕܪܝܫ ܤܡܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 8, 'ܐܡܬܝ ܕܡܙܕܡܢ ܐܢܬ ܡܢ ܐܢܫ ܠܒܝܬ ܡܫܬܘܬܐ ܠܐ ܬܐܙܠ ܬܤܬܡܟ ܠܟ ܒܪܝܫ ܤܡܟܐ ܕܠܡܐ ܢܗܘܐ ܡܙܡܢ ܬܡܢ ܐܢܫ ܕܡܝܩܪ ܡܢܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 9, 'ܘܢܐܬܐ ܗܘ ܡܢ ܕܠܟ ܘܠܗ ܩܪܐ ܘܢܐܡܪ ܠܟ ܕܗܒ ܕܘܟܬܐ ܠܗܢܐ ܘܬܒܗܬ ܟܕ ܩܐܡ ܐܢܬ ܘܐܚܕ ܐܢܬ ܕܘܟܬܐ ܐܚܪܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 10, 'ܐܠܐ ܡܐ ܕܐܙܕܡܢܬ ܙܠ ܐܤܬܡܟ ܠܟ ܒܚܪܬܐ ܕܡܐ ܕܐܬܐ ܗܘ ܕܩܪܟ ܢܐܡܪ ܠܟ ܪܚܡܝ ܐܬܥܠܐ ܠܥܠ ܘܐܤܬܡܟ ܘܬܗܘܐ ܠܟ ܬܫܒܘܚܬܐ ܩܕܡ ܟܠܗܘܢ ܕܤܡܝܟܝܢ ܥܡܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 11, 'ܡܛܠ ܕܟܠ ܕܢܪܝܡ ܢܦܫܗ ܢܬܡܟܟ ܘܟܠ ܕܢܡܟ ܢܦܫܗ ܢܬܬܪܝܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 12, 'ܐܡܪ ܕܝܢ ܐܦ ܠܗܘ ܕܩܪܝܗܝ ܡܐ ܕܥܒܕ ܐܢܬ ܫܪܘܬܐ ܐܘ ܐܚܫܡܝܬܐ ܠܐ ܬܗܘܐ ܩܪܐ ܪܚܡܝܟ ܐܦܠܐ ܐܚܝܟ ܐܘ ܐܚܝܢܝܟ ܘܠܐ ܫܒܒܝܟ ܥܬܝܪܐ ܕܠܡܐ ܘܐܦ ܗܢܘܢ ܢܩܪܘܢܟ ܘܢܗܘܐ ܠܟ ܦܘܪܥܢܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 13, 'ܐܠܐ ܡܐ ܕܥܒܕ ܐܢܬ ܩܘܒܠܐ ܩܪܝ ܠܡܤܟܢܐ ܤܓܝܦܐ ܚܓܝܤܐ ܤܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 14, 'ܘܛܘܒܝܟ ܕܠܝܬ ܠܗܘܢ ܕܢܦܪܥܘܢܟ ܢܗܘܐ ܓܝܪ ܦܘܪܥܢܟ ܒܩܝܡܐ ܕܙܕܝܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 15, 'ܟܕ ܫܡܥ ܕܝܢ ܚܕ ܡܢ ܗܢܘܢ ܕܤܡܝܟܝܢ ܗܠܝܢ ܐܡܪ ܠܗ ܛܘܒܘܗܝ ܠܡܢ ܕܢܐܟܘܠ ܠܚܡܐ ܒܡܠܟܘܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 16, 'ܐܡܪ ܠܗ ܝܫܘܥ ܓܒܪܐ ܚܕ ܥܒܕ ܐܚܫܡܝܬܐ ܪܒܬܐ ܘܩܪܐ ܠܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 17, 'ܘܫܕܪ ܥܒܕܗ ܒܥܕܢܐ ܕܐܚܫܡܝܬܐ ܕܢܐܡܪ ܠܐܝܠܝܢ ܕܩܪܝܢ ܗܐ ܟܠܡܕܡ ܡܛܝܒ ܠܟܘܢ ܬܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 18, 'ܘܫܪܝܘ ܡܢ ܚܕ ܟܠܗܘܢ ܠܡܫܬܐܠܘ ܐܡܪ ܠܗ ܩܕܡܝܐ ܩܪܝܬܐ ܙܒܢܬ ܘܐܠܝܨ ܐܢܐ ܕܐܦܘܩ ܐܚܙܝܗ ܒܥܐ ܐܢܐ ܡܢܟ ܫܒܘܩܝܢܝ ܕܡܫܬܐܠ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 19, 'ܐܚܪܢܐ ܐܡܪ ܚܡܫܐ ܙܘܓܝܢ ܬܘܪܐ ܙܒܢܬ ܘܐܙܠ ܐܢܐ ܕܐܒܩܐ ܐܢܘܢ ܒܥܐ ܐܢܐ ܡܢܟ ܫܒܘܩܝܢܝ ܕܡܫܬܐܠ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 20, 'ܘܐܚܪܢܐ ܐܡܪ ܐܢܬܬܐ ܢܤܒܬ ܘܡܛܠ ܗܕܐ ܠܐ ܡܫܟܚ ܐܢܐ ܕܐܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 21, 'ܘܐܬܐ ܗܘ ܥܒܕܐ ܘܐܡܪ ܠܡܪܗ ܗܠܝܢ ܗܝܕܝܢ ܪܓܙ ܡܪܐ ܒܝܬܐ ܘܐܡܪ ܠܥܒܕܗ ܦܘܩ ܒܥܓܠ ܠܫܘܩܐ ܘܠܒܪܝܬܐ ܕܡܕܝܢܬܐ ܘܐܥܠ ܠܟܐ ܠܡܤܟܢܐ ܘܠܡܟܐܒܐ ܘܠܡܚܓܪܐ ܘܠܥܘܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 22, 'ܘܐܡܪ ܥܒܕܐ ܡܪܝ ܗܘܐ ܐܝܟ ܕܦܩܕܬ ܘܬܘܒ ܐܝܬ ܐܬܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 23, 'ܘܐܡܪ ܡܪܐ ܠܥܒܕܗ ܦܘܩ ܠܐܘܪܚܬܐ ܘܠܒܝܬ ܤܝܓܐ ܘܐܠܘܨ ܕܢܥܠܘܢ ܕܢܬܡܠܐ ܒܝܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 24, 'ܐܡܪ ܐܢܐ ܠܟܘܢ ܓܝܪ ܕܚܕ ܡܢ ܗܢܘܢ ܐܢܫܐ ܕܩܪܝܢ ܗܘܘ ܠܐ ܢܛܥܡܘܢ ܡܢ ܐܚܫܡܝܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 25, 'ܘܟܕ ܐܙܠܝܢ ܗܘܘ ܥܡܗ ܟܢܫܐ ܤܓܝܐܐ ܐܬܦܢܝ ܘܐܡܪ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 26, 'ܡܢ ܕܐܬܐ ܠܘܬܝ ܘܠܐ ܤܢܐ ܠܐܒܘܗܝ ܘܠܐܡܗ ܘܠܐܚܘܗܝ ܘܠܐܚܘܬܗ ܘܠܐܢܬܬܗ ܘܠܒܢܘܗܝ ܘܐܦ ܠܢܦܫܗ ܬܠܡܝܕܐ ܠܐ ܡܫܟܚ ܕܢܗܘܐ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 27, 'ܘܡܢ ܕܠܐ ܫܩܠ ܨܠܝܒܗ ܘܐܬܐ ܒܬܪܝ ܬܠܡܝܕܐ ܠܐ ܡܫܟܚ ܕܢܗܘܐ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 28, 'ܡܢܘ ܓܝܪ ܡܢܟܘܢ ܕܨܒܐ ܕܢܒܢܐ ܡܓܕܠܐ ܘܠܐ ܠܘܩܕܡ ܝܬܒ ܚܫܒ ܢܦܩܬܗ ܐܢ ܐܝܬ ܠܗ ܠܡܫܠܡܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 29, 'ܕܠܐ ܟܕ ܢܤܝܡ ܫܬܐܤܬܐ ܘܠܐ ܢܫܟܚ ܠܡܫܠܡܘ ܟܠ ܕܚܙܝܢ ܢܗܘܘܢ ܡܒܙܚܝܢ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 30, 'ܘܐܡܪܝܢ ܕܗܢܐ ܓܒܪܐ ܫܪܝ ܠܡܒܢܐ ܘܠܐ ܐܫܟܚ ܠܡܫܠܡܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 31, 'ܐܘ ܡܢܘ ܡܠܟܐ ܕܐܙܠ ܠܩܪܒܐ ܠܡܬܟܬܫܘ ܥܡ ܡܠܟܐ ܚܒܪܗ ܘܠܐ ܠܘܩܕܡ ܡܬܪܥܐ ܕܐܢ ܡܫܟܚ ܒܥܤܪܐ ܐܠܦܝܢ ܠܡܐܪܥ ܠܗܘ ܕܐܬܐ ܥܠܘܗܝ ܒܥܤܪܝܢ ܐܠܦܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 32, 'ܘܐܢ ܕܝܢ ܠܐ ܥܕ ܗܘ ܪܚܝܩ ܡܢܗ ܡܫܕܪ ܐܝܙܓܕܐ ܘܒܥܐ ܥܠ ܫܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 33, 'ܗܟܢܐ ܟܠܢܫ ܡܢܟܘܢ ܕܠܐ ܫܒܩ ܟܠܗ ܩܢܝܢܗ ܠܐ ܡܫܟܚ ܕܢܗܘܐ ܠܝ ܬܠܡܝܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 34, 'ܫܦܝܪܐ ܗܝ ܡܠܚܐ ܐܢ ܕܝܢ ܐܦ ܡܠܚܐ ܬܦܟܗ ܒܡܢܐ ܬܬܡܠܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 35, 'ܠܐ ܠܐܪܥܐ ܘܠܐ ܠܙܒܠܐ ܐܙܠܐ ܠܒܪ ܫܕܝܢ ܠܗ ܡܢ ܕܐܝܬ ܠܗ ܐܕܢܐ ܕܢܫܡܥ ܢܫܡܥ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 1, 'ܡܬܩܪܒܝܢ ܗܘܘ ܕܝܢ ܠܘܬܗ ܡܟܤܐ ܘܚܛܝܐ ܕܢܫܡܥܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 2, 'ܘܤܦܪܐ ܘܦܪܝܫܐ ܪܛܢܝܢ ܗܘܘ ܘܐܡܪܝܢ ܗܢܐ ܠܚܛܝܐ ܡܩܒܠ ܘܐܟܠ ܥܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 3, 'ܘܐܡܪ ܠܗܘܢ ܝܫܘܥ ܡܬܠܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 4, 'ܡܢܘ ܡܢܟܘܢ ܓܒܪܐ ܕܐܝܬ ܠܗ ܡܐܐ ܥܪܒܝܢ ܘܐܢ ܢܐܒܕ ܚܕ ܡܢܗܘܢ ܠܐ ܫܒܩ ܬܫܥܝܢ ܘܬܫܥܐ ܒܕܒܪܐ ܘܐܙܠ ܒܥܐ ܠܗܘ ܕܐܒܕ ܥܕܡܐ ܕܢܫܟܚܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 5, 'ܘܡܐ ܕܐܫܟܚܗ ܚܕܐ ܘܫܩܠ ܠܗ ܥܠ ܟܬܦܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 6, 'ܘܐܬܐ ܠܒܝܬܗ ܘܩܪܐ ܠܪܚܡܘܗܝ ܘܠܫܒܒܘܗܝ ܘܐܡܪ ܠܗܘܢ ܚܕܘ ܥܡܝ ܕܐܫܟܚܬ ܥܪܒܝ ܕܐܒܝܕ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 7, 'ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܗܟܢܐ ܬܗܘܐ ܚܕܘܬܐ ܒܫܡܝܐ ܥܠ ܚܕ ܚܛܝܐ ܕܬܐܒ ܐܘ ܥܠ ܬܫܥܝܢ ܘܬܫܥܐ ܙܕܝܩܝܢ ܕܠܐ ܡܬܒܥܝܐ ܠܗܘܢ ܬܝܒܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 8, 'ܐܘ ܐܝܕܐ ܗܝ ܐܢܬܬܐ ܕܐܝܬ ܠܗ ܥܤܪܐ ܙܘܙܝܢ ܘܬܘܒܕ ܚܕ ܡܢܗܘܢ ܘܠܐ ܡܢܗܪܐ ܫܪܓܐ ܘܚܡܐ ܒܝܬܐ ܘܒܥܝܐ ܠܗ ܒܛܝܠܐܝܬ ܥܕܡܐ ܕܬܫܟܚܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 9, 'ܘܡܐ ܕܐܫܟܚܬܗ ܩܪܝܐ ܠܪܚܡܬܗ ܘܠܫܒܒܬܗ ܘܐܡܪܐ ܠܗܝܢ ܚܕܝܝܢ ܥܡܝ ܕܐܫܟܚܬ ܙܘܙܝ ܕܐܒܝܕ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 10, 'ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܗܟܢܐ ܬܗܘܐ ܚܕܘܬܐ ܩܕܡ ܡܠܐܟܘܗܝ ܕܐܠܗܐ ܥܠ ܚܕ ܚܛܝܐ ܕܬܐܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 11, 'ܘܐܡܪ ܗܘܐ ܠܗܘܢ ܬܘܒ ܝܫܘܥ ܠܓܒܪܐ ܚܕ ܐܝܬ ܗܘܐ ܠܗ ܒܢܝܐ ܬܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 12, 'ܘܐܡܪ ܠܗ ܒܪܗ ܙܥܘܪܐ ܐܒܝ ܗܒ ܠܝ ܦܠܓܘܬܐ ܕܡܛܝܐ ܠܝ ܡܢ ܒܝܬܟ ܘܦܠܓ ܠܗܘܢ ܩܢܝܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 13, 'ܘܡܢ ܒܬܪ ܝܘܡܬܐ ܩܠܝܠ ܟܢܫ ܗܘ ܒܪܗ ܙܥܘܪܐ ܟܠ ܡܕܡ ܕܡܛܝܗܝ ܘܐܙܠ ܠܐܬܪܐ ܪܚܝܩܐ ܘܬܡܢ ܒܕܪ ܩܢܝܢܗ ܟܕ ܚܝܐ ܦܪܚܐܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 14, 'ܘܟܕ ܓܡܪ ܟܠ ܡܕܡ ܕܐܝܬ ܗܘܐ ܠܗ ܗܘܐ ܟܦܢܐ ܪܒܐ ܒܐܬܪܐ ܗܘ ܘܫܪܝ ܚܤܪ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 15, 'ܘܐܙܠ ܢܩܦ ܠܗ ܠܚܕ ܡܢ ܒܢܝ ܡܕܝܢܬܐ ܕܐܬܪܐ ܗܘ ܘܗܘ ܫܕܪܗ ܠܩܪܝܬܐ ܠܡܪܥܐ ܚܙܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 16, 'ܘܡܬܪܓܪܓ ܗܘܐ ܠܡܡܠܐ ܟܪܤܗ ܡܢ ܚܪܘܒܐ ܗܢܘܢ ܕܐܟܠܝܢ ܗܘܘ ܚܙܝܪܐ ܘܠܐ ܐܢܫ ܝܗܒ ܗܘܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 17, 'ܘܟܕ ܐܬܐ ܠܘܬ ܢܦܫܗ ܐܡܪ ܟܡܐ ܗܫܐ ܐܓܝܪܐ ܐܝܬ ܒܝܬ ܐܒܝ ܕܝܬܝܪ ܠܗܘܢ ܠܚܡܐ ܘܐܢܐ ܗܪܟܐ ܠܟܦܢܝ ܐܒܕ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 18, 'ܐܩܘܡ ܐܙܠ ܠܘܬ ܐܒܝ ܘܐܡܪ ܠܗ ܐܒܝ ܚܛܝܬ ܒܫܡܝܐ ܘܩܕܡܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 19, 'ܘܠܐ ܡܟܝܠ ܫܘܐ ܐܢܐ ܕܒܪܟ ܐܬܩܪܐ ܥܒܕܝܢܝ ܐܝܟ ܚܕ ܡܢ ܐܓܝܪܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 20, 'ܘܩܡ ܐܬܐ ܠܘܬ ܐܒܘܗܝ ܘܥܕ ܗܘ ܪܚܝܩ ܚܙܝܗܝ ܐܒܘܗܝ ܘܐܬܪܚܡ ܥܠܘܗܝ ܘܪܗܛ ܢܦܠ ܥܠ ܨܘܪܗ ܘܢܫܩܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 21, 'ܘܐܡܪ ܠܗ ܒܪܗ ܐܒܝ ܚܛܝܬ ܒܫܡܝܐ ܘܩܕܡܝܟ ܘܠܐ ܫܘܐ ܐܢܐ ܕܒܪܟ ܐܬܩܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 22, 'ܐܡܪ ܕܝܢ ܐܒܘܗܝ ܠܥܒܕܘܗܝ ܐܦܩܘ ܐܤܛܠܐ ܪܫܝܬܐ ܐܠܒܫܘܗܝ ܘܤܝܡܘ ܥܙܩܬܐ ܒܐܝܕܗ ܘܐܤܐܢܘܗܝ ܡܤܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 23, 'ܘܐܝܬܘ ܩܛܘܠܘ ܬܘܪܐ ܕܦܛܡܐ ܘܢܐܟܘܠ ܘܢܬܒܤܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 24, 'ܕܗܢܐ ܒܪܝ ܡܝܬܐ ܗܘܐ ܘܚܝܐ ܘܐܒܝܕܐ ܗܘܐ ܘܐܫܬܟܚ ܘܫܪܝܘ ܠܡܬܒܤܡܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 25, 'ܗܘ ܕܝܢ ܒܪܗ ܩܫܝܫܐ ܒܩܪܝܬܐ ܗܘܐ ܘܟܕ ܐܬܐ ܘܩܪܒ ܠܘܬ ܒܝܬܐ ܫܡܥ ܩܠ ܙܡܪܐ ܕܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 26, 'ܘܩܪܐ ܠܚܕ ܡܢ ܛܠܝܐ ܘܫܐܠܗ ܡܢܘ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 27, 'ܐܡܪ ܠܗ ܐܚܘܟ ܐܬܐ ܘܩܛܠ ܐܒܘܟ ܬܘܪܐ ܕܦܛܡܐ ܕܟܕ ܚܠܝܡ ܐܩܒܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 28, 'ܘܪܓܙ ܘܠܐ ܨܒܐ ܗܘܐ ܠܡܥܠ ܘܢܦܩ ܐܒܘܗܝ ܒܥܐ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 29, 'ܗܘ ܕܝܢ ܐܡܪ ܠܐܒܘܗܝ ܗܐ ܟܡܐ ܫܢܝܢ ܦܠܚ ܐܢܐ ܠܟ ܥܒܕܘܬܐ ܘܠܐ ܡܡܬܘܡ ܥܒܪܬ ܦܘܩܕܢܟ ܘܡܢ ܡܬܘܡ ܓܕܝܐ ܠܐ ܝܗܒܬ ܠܝ ܕܐܬܒܤܡ ܥܡ ܪܚܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 30, 'ܠܗܢܐ ܕܝܢ ܒܪܟ ܟܕ ܦܪܚ ܩܢܝܢܟ ܥܡ ܙܢܝܬܐ ܘܐܬܐ ܢܟܤܬ ܠܗ ܬܘܪܐ ܕܦܛܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 31, 'ܐܡܪ ܠܗ ܐܒܘܗܝ ܒܪܝ ܐܢܬ ܒܟܠܙܒܢ ܥܡܝ ܐܢܬ ܘܟܠܡܕܡ ܕܝܠܝ ܕܝܠܟ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 32, 'ܠܡܒܤܡ ܕܝܢ ܘܠܐ ܗܘܐ ܠܢ ܘܠܡܚܕܐ ܕܗܢܐ ܐܚܘܟ ܡܝܬܐ ܗܘܐ ܘܚܝܐ ܘܐܒܝܕܐ ܗܘܐ ܘܐܫܬܟܚ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 1, 'ܘܐܡܪ ܡܬܠܐ ܠܘܬ ܬܠܡܝܕܘܗܝ ܓܒܪܐ ܚܕ ܐܝܬ ܗܘܐ ܥܬܝܪܐ ܘܐܝܬ ܗܘܐ ܠܗ ܪܒܝܬܐ ܘܐܬܐܟܠܘ ܠܗ ܩܪܨܘܗܝ ܕܩܢܝܢܗ ܡܦܪܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 2, 'ܘܩܪܝܗܝ ܡܪܗ ܘܐܡܪ ܠܗ ܡܢܘ ܗܢܐ ܕܫܡܥ ܐܢܐ ܥܠܝܟ ܗܒ ܠܝ ܚܘܫܒܢܐ ܕܪܒܬ ܒܝܬܘܬܟ ܠܐ ܓܝܪ ܡܫܟܚ ܐܢܬ ܡܟܝܠ ܪܒܝܬܐ ܕܬܗܘܐ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 3, 'ܐܡܪ ܗܘ ܪܒܝܬܐ ܒܢܦܫܗ ܡܢܐ ܐܥܒܕ ܕܡܪܝ ܫܩܠ ܠܗ ܡܢܝ ܪܒܬ ܒܝܬܘܬܐ ܕܐܚܦܘܪ ܠܐ ܡܫܟܚ ܐܢܐ ܘܠܡܚܕܪ ܒܗܬ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 4, 'ܝܕܥܬ ܡܢܐ ܐܥܒܕ ܕܡܐ ܕܢܦܩܬ ܡܢ ܪܒܬ ܒܝܬܘܬܐ ܢܩܒܠܘܢܢܝ ܒܒܬܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 5, 'ܘܩܪܐ ܚܕ ܚܕ ܡܢ ܚܝܒܐ ܕܡܪܗ ܘܐܡܪ ܠܩܕܡܝܐ ܟܡܐ ܚܝܒ ܐܢܬ ܠܡܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 6, 'ܐܡܪ ܠܗ ܡܐܐ ܡܬܪܝܢ ܡܫܚܐ ܐܡܪ ܠܗ ܤܒ ܟܬܒܟ ܘܬܒ ܒܥܓܠ ܟܬܘܒ ܚܡܫܝܢ ܡܬܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 7, 'ܘܐܡܪ ܠܐܚܪܢܐ ܘܐܢܬ ܡܢܐ ܚܝܒ ܐܢܬ ܠܡܪܝ ܐܡܪ ܠܗ ܡܐܐ ܟܘܪܝܢ ܚܛܐ ܐܡܪ ܠܗ ܩܒܠ ܟܬܒܟ ܘܬܒ ܟܬܘܒ ܬܡܢܐܝܢ ܟܘܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 8, 'ܘܫܒܚ ܡܪܢ ܠܪܒܝܬܐ ܕܥܘܠܐ ܕܚܟܝܡܐܝܬ ܥܒܕ ܒܢܘܗܝ ܓܝܪ ܕܥܠܡܐ ܗܢܐ ܚܟܝܡܝܢ ܐܢܘܢ ܡܢ ܒܢܘܗܝ ܕܢܘܗܪܐ ܒܫܪܒܬܗܘܢ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 9, 'ܘܐܦ ܐܢܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܥܒܕܘ ܠܟܘܢ ܪܚܡܐ ܡܢ ܡܡܘܢܐ ܗܢܐ ܕܥܘܠܐ ܕܡܐ ܕܓܡܪ ܢܩܒܠܘܢܟܘܢ ܒܡܛܠܝܗܘܢ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 10, 'ܡܢ ܕܒܩܠܝܠ ܡܗܝܡܢ ܐܦ ܒܤܓܝ ܡܗܝܡܢ ܗܘ ܘܡܢ ܕܒܩܠܝܠ ܥܘܠ ܐܦ ܒܤܓܝ ܥܘܠ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 11, 'ܐܢ ܗܟܝܠ ܒܡܡܘܢܐ ܕܥܘܠܐ ܡܗܝܡܢܐ ܠܐ ܗܘܝܬܘܢ ܫܪܪܐ ܠܟܘܢ ܡܢܘ ܡܗܝܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 12, 'ܘܐܢ ܒܕܠܐ ܕܝܠܟܘܢ ܠܐ ܐܫܬܟܚܬܘܢ ܡܗܝܡܢܐ ܕܝܠܟܘܢ ܡܢܘ ܢܬܠ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 13, 'ܠܝܬ ܥܒܕܐ ܕܡܫܟܚ ܠܬܪܝܢ ܡܪܘܢ ܠܡܦܠܚ ܐܘ ܓܝܪ ܠܚܕ ܢܤܢܐ ܘܠܐܚܪܢܐ ܢܪܚܡ ܐܘ ܠܚܕ ܢܝܩܪ ܘܠܐܚܪܢܐ ܢܫܘܛ ܠܐ ܡܫܟܚܝܢ ܐܢܬܘܢ ܠܡܦܠܚ ܠܐܠܗܐ ܘܠܡܡܘܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 14, 'ܦܪܝܫܐ ܕܝܢ ܟܕ ܫܡܥܘ ܗܠܝܢ ܟܠܗܝܢ ܡܛܠ ܕܪܚܡܝܢ ܗܘܘ ܟܤܦܐ ܡܡܝܩܝܢ ܗܘܘ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 15, 'ܝܫܘܥ ܕܝܢ ܐܡܪ ܠܗܘܢ ܐܢܬܘܢ ܐܢܘܢ ܕܡܙܕܩܝܢ ܢܦܫܗܘܢ ܩܕܡ ܒܢܝ ܐܢܫܐ ܐܠܗܐ ܕܝܢ ܝܕܥ ܠܒܘܬܟܘܢ ܕܡܕܡ ܕܪܡ ܒܝܬ ܒܢܝܢܫܐ ܩܕܡ ܐܠܗܐ ܢܕܝܕ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 16, 'ܢܡܘܤܐ ܘܢܒܝܐ ܥܕܡܐ ܠܝܘܚܢܢ ܡܢ ܗܝܕܝܢ ܡܠܟܘܬܐ ܕܐܠܗܐ ܡܤܬܒܪܐ ܘܟܠ ܠܗ ܚܒܨ ܕܢܥܘܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 17, 'ܦܫܝܩ ܗܘ ܕܝܢ ܕܫܡܝܐ ܘܐܪܥܐ ܢܥܒܪܘܢ ܐܘ ܐܬܘܬܐ ܚܕܐ ܡܢ ܢܡܘܤܐ ܬܥܒܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 18, 'ܟܠ ܡܢ ܕܫܪܐ ܐܢܬܬܗ ܘܢܤܒ ܐܚܪܬܐ ܓܐܪ ܘܟܠ ܡܢ ܕܢܤܒ ܫܒܝܩܬܐ ܓܐܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 19, 'ܓܒܪܐ ܕܝܢ ܚܕ ܥܬܝܪܐ ܐܝܬ ܗܘܐ ܘܠܒܫ ܗܘܐ ܒܘܨܐ ܘܐܪܓܘܢܐ ܘܟܠܝܘܡ ܡܬܒܤܡܝܢ ܗܘܐ ܓܐܝܐܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 20, 'ܘܡܤܟܢܐ ܚܕ ܐܝܬ ܗܘܐ ܕܫܡܗ ܠܥܙܪ ܘܪܡܐ ܗܘܐ ܠܘܬ ܬܪܥܗ ܕܗܘ ܥܬܝܪܐ ܟܕ ܡܡܚܝ ܒܫܘܚܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 21, 'ܘܡܬܝܐܒ ܗܘܐ ܕܢܡܠܐ ܟܪܤܗ ܡܢ ܦܪܬܘܬܐ ܕܢܦܠܝܢ ܡܢ ܦܬܘܪܗ ܕܗܘ ܥܬܝܪܐ ܐܠܐ ܐܦ ܟܠܒܐ ܐܬܝܢ ܗܘܘ ܡܠܚܟܝܢ ܫܘܚܢܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 22, 'ܗܘܐ ܕܝܢ ܘܡܝܬ ܗܘ ܡܤܟܢܐ ܘܐܘܒܠܘܗܝ ܡܠܐܟܐ ܠܥܘܒܗ ܕܐܒܪܗܡ ܐܦ ܗܘ ܕܝܢ ܥܬܝܪܐ ܡܝܬ ܘܐܬܩܒܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 23, 'ܘܟܕ ܡܫܬܢܩ ܒܫܝܘܠ ܐܪܝܡ ܥܝܢܘܗܝ ܡܢ ܪܘܚܩܐ ܘܚܙܐ ܠܐܒܪܗܡ ܘܠܠܥܙܪ ܒܥܘܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 24, 'ܘܩܪܐ ܒܩܠܐ ܪܡܐ ܘܐܡܪ ܐܒܝ ܐܒܪܗܡ ܐܬܪܚܡ ܥܠܝ ܘܫܕܪ ܠܠܥܙܪ ܕܢܨܒܘܥ ܪܝܫ ܨܒܥܗ ܒܡܝܐ ܘܢܪܛܒ ܠܝ ܠܫܢܝ ܕܗܐ ܡܫܬܢܩ ܐܢܐ ܒܫܠܗܒܝܬܐ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 25, 'ܐܡܪ ܠܗ ܐܒܪܗܡ ܒܪܝ ܐܬܕܟܪ ܕܩܒܠܬ ܛܒܬܟ ܒܚܝܝܟ ܘܠܥܙܪ ܒܝܫܬܗ ܘܗܫܐ ܗܐ ܡܬܬܢܝܚ ܗܪܟܐ ܘܐܢܬ ܡܫܬܢܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 26, 'ܘܥܡ ܗܠܝܢ ܟܠܗܝܢ ܗܘܬܐ ܪܒܬܐ ܤܝܡܐ ܒܝܢܝܢ ܘܠܟܘܢ ܕܐܝܠܝܢ ܕܨܒܝܢ ܡܟܐ ܕܢܥܒܪܘܢ ܠܘܬܟܘܢ ܠܐ ܢܫܟܚܘܢ ܘܐܦܠܐ ܕܡܢ ܬܡܢ ܢܥܒܪܘܢ ܠܘܬܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 27, 'ܐܡܪ ܠܗ ܡܕܝܢ ܒܥܐ ܐܢܐ ܡܢܟ ܐܒܝ ܕܬܫܕܪܝܘܗܝ ܠܒܝܬ ܐܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 28, 'ܚܡܫܐ ܓܝܪ ܐܚܝܢ ܐܝܬ ܠܝ ܢܐܙܠ ܢܤܗܕ ܐܢܘܢ ܕܠܐ ܐܦ ܗܢܘܢ ܢܐܬܘܢ ܠܕܘܟܬܐ ܗܕܐ ܕܬܫܢܝܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 29, 'ܐܡܪ ܠܗ ܐܒܪܗܡ ܐܝܬ ܠܗܘܢ ܡܘܫܐ ܘܢܒܝܐ ܢܫܡܥܘܢ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 30, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗ ܠܐ ܐܒܝ ܐܒܪܗܡ ܐܠܐ ܐܢ ܐܢܫ ܡܢ ܡܝܬܐ ܢܐܙܠ ܠܘܬܗܘܢ ܬܝܒܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 31, 'ܐܡܪ ܠܗ ܐܒܪܗܡ ܐܢ ܠܡܘܫܐ ܘܠܢܒܝܐ ܠܐ ܫܡܥܝܢ ܐܦ ܠܐ ܐܢ ܐܢܫ ܡܢ ܡܝܬܐ ܢܩܘܡ ܡܗܝܡܢܝܢ ܠܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 1, 'ܘܐܡܪ ܗܘܐ ܝܫܘܥ ܠܬܠܡܝܕܘܗܝ ܠܐ ܡܫܟܚܐ ܕܠܐ ܢܐܬܘܢ ܡܟܫܘܠܐ ܘܝ ܕܝܢ ܠܗܘ ܕܒܐܝܕܗ ܢܐܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 2, 'ܦܩܚ ܗܘܐ ܠܗ ܐܠܘ ܪܚܝܐ ܕܚܡܪܐ ܬܠܝܐ ܒܨܘܪܗ ܘܫܕܐ ܒܝܡܐ ܐܘ ܕܢܟܫܠ ܠܚܕ ܡܢ ܗܠܝܢ ܙܥܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 3, 'ܐܙܕܗܪܘ ܒܢܦܫܟܘܢ ܐܢ ܢܚܛܐ ܐܚܘܟ ܟܐܝ ܒܗ ܘܐܢ ܬܐܒ ܫܒܘܩ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 4, 'ܘܐܢ ܫܒܥ ܙܒܢܝܢ ܒܝܘܡܐ ܢܤܟܠ ܒܟ ܘܫܒܥ ܙܒܢܝܢ ܒܝܘܡܐ ܢܬܦܢܐ ܠܘܬܟ ܘܢܐܡܪ ܕܬܐܒ ܐܢܐ ܫܒܘܩ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 5, 'ܘܐܡܪܘ ܫܠܝܚܐ ܠܡܪܢ ܐܘܤܦ ܠܢ ܗܝܡܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 6, 'ܐܡܪ ܠܗܘܢ ܐܢ ܐܝܬ ܗܘܬ ܠܟܘܢ ܗܝܡܢܘܬܐ ܐܝܟ ܦܪܕܬܐ ܕܚܪܕܠܐ ܐܡܪܝܢ ܗܘܝܬܘܢ ܠܬܘܬܐ ܗܢܐ ܕܐܬܥܩܪ ܘܐܬܢܨܒ ܒܝܡܐ ܘܡܫܬܡܥ ܗܘܐ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 7, 'ܡܢܘ ܕܝܢ ܡܢܟܘܢ ܕܐܝܬ ܠܗ ܥܒܕܐ ܕܕܒܪ ܦܕܢܐ ܐܘ ܕܪܥܐ ܥܢܐ ܘܐܢ ܢܐܬܐ ܡܢ ܚܩܠܐ ܐܡܪ ܠܗ ܡܚܕܐ ܥܒܪ ܐܤܬܡܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 8, 'ܐܠܐ ܐܡܪ ܠܗ ܛܝܒ ܠܝ ܡܕܡ ܕܐܚܫܡ ܘܐܤܘܪ ܚܨܝܟ ܫܡܫܝܢܝ ܥܕܡܐ ܕܐܠܥܤ ܘܐܫܬܐ ܘܒܬܪܟܢ ܐܦ ܐܢܬ ܬܠܥܤ ܘܬܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 9, 'ܠܡܐ ܛܝܒܘܬܗ ܡܩܒܠ ܕܗܘ ܥܒܕܐ ܕܥܒܕ ܡܕܡ ܕܐܬܦܩܕ ܠܗ ܠܐ ܤܒܪ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 10, 'ܗܟܢܐ ܐܦ ܐܢܬܘܢ ܡܐ ܕܥܒܕܬܘܢ ܟܠܗܝܢ ܐܝܠܝܢ ܕܦܩܝܕܢ ܠܟܘܢ ܐܡܪܘ ܕܥܒܕܐ ܚܢܢ ܒܛܝܠܐ ܕܡܕܡ ܕܚܝܒܝܢ ܗܘܝܢ ܠܡܥܒܕ ܥܒܕܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 11, 'ܘܗܘܐ ܕܟܕ ܐܙܠ ܝܫܘܥ ܠܐܘܪܫܠܡ ܥܒܪ ܗܘܐ ܒܝܬ ܫܡܪܝܐ ܠܓܠܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 12, 'ܘܟܕ ܩܪܝܒ ܠܡܥܠ ܠܩܪܝܬܐ ܚܕܐ ܐܪܥܘܗܝ ܥܤܪܐ ܐܢܫܝܢ ܓܪܒܐ ܘܩܡܘ ܡܢ ܪܘܚܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 13, 'ܘܐܪܝܡܘ ܩܠܗܘܢ ܘܐܡܪܝܢ ܪܒܢ ܝܫܘܥ ܐܬܪܚܡ ܥܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 14, 'ܘܟܕ ܚܙܐ ܐܢܘܢ ܐܡܪ ܠܗܘܢ ܙܠܘ ܚܘܘ ܢܦܫܟܘܢ ܠܟܗܢܐ ܘܟܕ ܐܙܠܝܢ ܐܬܕܟܝܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 15, 'ܚܕ ܕܝܢ ܡܢܗܘܢ ܟܕ ܚܙܐ ܕܐܬܕܟܝ ܗܦܟ ܠܗ ܘܒܩܠܐ ܪܡܐ ܡܫܒܚ ܗܘܐ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 16, 'ܘܢܦܠ ܥܠ ܐܦܘܗܝ ܩܕܡ ܪܓܠܘܗܝ ܕܝܫܘܥ ܟܕ ܡܘܕܐ ܠܗ ܘܗܘ ܗܢܐ ܫܡܪܝܐ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 17, 'ܥܢܐ ܕܝܢ ܝܫܘܥ ܘܐܡܪ ܠܐ ܗܘܐ ܥܤܪܐ ܗܘܝܢ ܗܠܝܢ ܕܐܬܕܟܝܘ ܐܝܟܐ ܐܢܘܢ ܬܫܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 18, 'ܠܡܐ ܦܪܫܘ ܕܢܐܬܘܢ ܢܬܠܘܢ ܬܫܒܘܚܬܐ ܠܐܠܗܐ ܐܠܐ ܗܢܐ ܕܡܢ ܥܡܐ ܗܘ ܢܘܟܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 19, 'ܘܐܡܪ ܠܗ ܩܘܡ ܙܠ ܗܝܡܢܘܬܟ ܐܚܝܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 20, 'ܘܟܕ ܫܐܠܘܗܝ ܠܝܫܘܥ ܡܢ ܦܪܝܫܐ ܐܡܬܝ ܐܬܝܐ ܡܠܟܘܬܗ ܕܐܠܗܐ ܥܢܐ ܘܐܡܪ ܠܗܘܢ ܠܐ ܐܬܝܐ ܡܠܟܘܬܗ ܕܐܠܗܐ ܒܢܛܘܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 21, 'ܘܠܐ ܐܡܪܝܢ ܗܐ ܗܪܟܐ ܗܝ ܘܗܐ ܗܪ ܬܡܢ ܗܝ ܗܐ ܓܝܪ ܡܠܟܘܬܗ ܕܐܠܗܐ ܠܓܘ ܡܢܟܘܢ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 22, 'ܘܐܡܪ ܠܬܠܡܝܕܘܗܝ ܢܐܬܘܢ ܝܘܡܬܐ ܕܬܬܪܓܪܓܘܢ ܠܡܚܙܐ ܚܕ ܡܢ ܝܘܡܬܐ ܕܒܪܗ ܕܐܢܫܐ ܘܠܐ ܬܚܙܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 23, 'ܘܐܢ ܢܐܡܪܘܢ ܠܟܘܢ ܗܐ ܗܪܟܐ ܗܘ ܘܗܐ ܗܪ ܬܡܢ ܗܘ ܠܐ ܬܐܙܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 24, 'ܐܝܟܢܐ ܓܝܪ ܕܒܪܩܐ ܒܪܩ ܡܢ ܫܡܝܐ ܘܟܠܗ ܬܚܝܬ ܫܡܝܐ ܡܢܗܪ ܗܟܢܐ ܢܗܘܐ ܒܪܗ ܕܐܢܫܐ ܒܝܘܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 25, 'ܠܘܩܕܡ ܕܝܢ ܥܬܝܕ ܗܘ ܕܢܚܫ ܤܓܝܐܬܐ ܘܢܤܬܠܐ ܡܢ ܫܪܒܬܐ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 26, 'ܘܐܝܟܢܐ ܕܗܘܐ ܒܝܘܡܬܗ ܕܢܘܚ ܗܟܢܐ ܢܗܘܐ ܒܝܘܡܬܗ ܕܒܪܗ ܕܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 27, 'ܕܐܟܠܝܢ ܗܘܘ ܘܫܬܝܢ ܘܢܤܒܝܢ ܢܫܐ ܘܝܗܒܝܢ ܠܓܒܪܐ ܥܕܡܐ ܠܝܘܡܐ ܕܥܠ ܢܘܚ ܠܟܘܝܠܐ ܘܐܬܐ ܛܘܦܢܐ ܘܐܘܒܕ ܠܟܠ ܐܢܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 28, 'ܘܐܝܟܢܐ ܬܘܒ ܕܗܘܐ ܒܝܘܡܬܗ ܕܠܘܛ ܕܐܟܠܝܢ ܗܘܘ ܘܫܬܝܢ ܘܙܒܢܝܢ ܘܡܙܒܢܝܢ ܘܢܨܒܝܢ ܗܘܘ ܘܒܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 29, 'ܒܝܘܡܐ ܕܝܢ ܕܢܦܩ ܠܘܛ ܡܢ ܤܕܘܡ ܐܡܛܪ ܡܪܝܐ ܢܘܪܐ ܘܟܒܪܝܬܐ ܡܢ ܫܡܝܐ ܘܐܘܒܕ ܠܟܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 30, 'ܗܟܢܐ ܢܗܘܐ ܒܝܘܡܐ ܕܡܬܓܠܐ ܒܪܗ ܕܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 31, 'ܒܗܘ ܝܘܡܐ ܡܢ ܕܒܐܓܪܐ ܗܘ ܘܡܐܢܘܗܝ ܒܒܝܬܐ ܠܐ ܢܚܘܬ ܕܢܫܩܘܠ ܐܢܘܢ ܘܡܢ ܕܒܚܩܠܐ ܗܘ ܠܐ ܢܬܗܦܟ ܠܒܤܬܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 32, 'ܐܬܕܟܪܘ ܠܐܢܬܬܗ ܕܠܘܛ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 33, 'ܡܢ ܕܨܒܐ ܕܢܚܐ ܢܦܫܗ ܢܘܒܕܝܗ ܘܡܢ ܕܢܘܒܕ ܢܦܫܗ ܢܚܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 34, 'ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܒܗܘ ܠܠܝܐ ܬܪܝܢ ܢܗܘܘܢ ܒܚܕܐ ܥܪܤܐ ܚܕ ܢܬܕܒܪ ܘܐܚܪܢܐ ܢܫܬܒܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 35, 'ܬܪܬܝܢ ܢܗܘܝܢ ܛܚܢܢ ܐܟܚܕܐ ܚܕܐ ܬܬܕܒܪ ܘܐܚܪܬܐ ܬܫܬܒܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 36, 'ܬܪܝܢ ܢܗܘܘܢ ܒܚܩܠܐ ܚܕ ܢܬܕܒܪ ܘܐܚܪܢܐ ܢܫܬܒܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 37, 'ܥܢܘ ܘܐܡܪܝܢ ܠܗ ܠܐܝܟܐ ܡܪܢ ܐܡܪ ܠܗܘܢ ܐܝܟܐ ܕܦܓܪܐ ܬܡܢ ܢܬܟܢܫܘܢ ܢܫܪܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 1, 'ܐܡܪ ܠܗܘܢ ܕܝܢ ܐܦ ܡܬܠܐ ܕܒܟܠ ܥܕܢ ܢܨܠܘܢ ܘܠܐ ܬܡܐܢ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 2, 'ܕܝܢܐ ܚܕ ܐܝܬ ܗܘܐ ܒܡܕܝܢܬܐ ܚܕܐ ܕܡܢ ܐܠܗܐ ܠܐ ܕܚܠ ܗܘܐ ܘܡܢ ܒܢܝܢܫܐ ܠܐ ܡܬܟܚܕ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 3, 'ܐܪܡܠܬܐ ܕܝܢ ܚܕܐ ܐܝܬ ܗܘܬ ܒܡܕܝܢܬܐ ܗܝ ܘܐܬܝܐ ܗܘܬ ܠܘܬܗ ܘܐܡܪܐ ܬܒܥܝܢܝ ܡܢ ܒܥܠ ܕܝܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 4, 'ܘܠܐ ܨܒܐ ܗܘܐ ܙܒܢܐ ܤܓܝܐܐ ܒܬܪܟܢ ܕܝܢ ܐܡܪ ܒܢܦܫܗ ܐܢ ܡܢ ܐܠܗܐ ܠܐ ܕܚܠ ܐܢܐ ܘܡܢ ܒܢܝܢܫܐ ܠܐ ܡܬܟܚܕ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 5, 'ܐܦܢ ܡܛܠ ܕܡܠܐܝܐ ܠܝ ܗܕܐ ܐܪܡܠܬܐ ܐܬܒܥܝܗ ܕܠܐ ܒܟܠ ܥܕܢ ܬܗܘܐ ܐܬܝܐ ܡܗܪܐ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 6, 'ܘܐܡܪ ܡܪܢ ܫܡܥܘ ܡܢܐ ܐܡܪ ܕܝܢܐ ܕܥܘܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 7, 'ܐܠܗܐ ܕܝܢ ܠܐ ܝܬܝܪܐܝܬ ܢܥܒܕ ܬܒܥܬܐ ܠܓܒܘܗܝ ܕܩܪܝܢ ܠܗ ܒܐܝܡܡܐ ܘܒܠܠܝܐ ܘܡܓܪ ܪܘܚܗ ܥܠܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 8, 'ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܢܥܒܕ ܬܒܥܬܗܘܢ ܒܥܓܠ ܒܪܡ ܢܐܬܐ ܒܪܗ ܕܐܢܫܐ ܘܢܫܟܚ ܟܝ ܗܝܡܢܘܬܐ ܥܠ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 9, 'ܘܐܡܪ ܗܘܐ ܡܬܠܐ ܗܢܐ ܠܘܩܒܠ ܐܢܫܐ ܐܝܠܝܢ ܕܬܟܝܠܝܢ ܥܠ ܢܦܫܗܘܢ ܕܐܝܬܝܗܘܢ ܙܕܝܩܐ ܘܒܤܝܢ ܥܠ ܟܠܢܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 10, 'ܬܪܝܢ ܓܒܪܝܢ ܤܠܩܘ ܠܗܝܟܠܐ ܠܡܨܠܝܘ ܚܕ ܦܪܝܫܐ ܘܐܚܪܢܐ ܡܟܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 11, 'ܘܗܘ ܦܪܝܫܐ ܩܐܡ ܗܘܐ ܒܝܢܘܗܝ ܠܢܦܫܗ ܘܗܠܝܢ ܡܨܠܐ ܗܘܐ ܐܠܗܐ ܡܘܕܐ ܐܢܐ ܠܟ ܕܠܐ ܗܘܝܬ ܐܝܟ ܫܪܟܐ ܕܐܢܫܐ ܚܛܘܦܐ ܘܥܠܘܒܐ ܘܓܝܪܐ ܘܠܐ ܐܝܟ ܗܢܐ ܡܟܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 12, 'ܐܠܐ ܨܐܡ ܐܢܐ ܬܪܝܢ ܒܫܒܬܐ ܘܡܥܤܪ ܐܢܐ ܟܠ ܡܕܡ ܕܩܢܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 13, 'ܗܘ ܕܝܢ ܡܟܤܐ ܩܐܡ ܗܘܐ ܡܢ ܪܘܚܩܐ ܘܠܐ ܨܒܐ ܗܘܐ ܐܦ ܠܐ ܥܝܢܘܗܝ ܢܪܝܡ ܠܫܡܝܐ ܐܠܐ ܛܪܦ ܗܘܐ ܥܠ ܚܕܝܗ ܘܐܡܪ ܐܠܗܐ ܚܘܢܝܢܝ ܠܚܛܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 14, 'ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܢܚܬ ܗܢܐ ܡܙܕܩ ܠܒܝܬܗ ܝܬܝܪ ܡܢ ܗܘ ܦܪܝܫܐ ܟܠ ܐܢܫ ܓܝܪ ܕܢܪܝܡ ܢܦܫܗ ܢܬܡܟܟ ܘܟܠ ܕܢܡܟ ܢܦܫܗ ܢܬܬܪܝܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 15, 'ܩܪܒܘ ܗܘܘ ܠܗ ܕܝܢ ܐܦ ܝܠܘܕܐ ܕܢܩܪܘܒ ܠܗܘܢ ܘܚܙܘ ܐܢܘܢ ܬܠܡܝܕܘܗܝ ܘܟܐܘ ܒܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 16, 'ܗܘ ܕܝܢ ܝܫܘܥ ܩܪܐ ܐܢܘܢ ܘܐܡܪ ܠܗܘܢ ܫܒܘܩܘ ܛܠܝܐ ܐܬܝܢ ܠܘܬܝ ܘܠܐ ܬܟܠܘܢ ܐܢܘܢ ܕܕܐܝܠܝܢ ܓܝܪ ܕܐܝܟ ܗܠܝܢ ܐܢܘܢ ܕܝܠܗܘܢ ܗܝ ܡܠܟܘܬܐ ܕܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 17, 'ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܡܢ ܕܠܐ ܢܩܒܠ ܡܠܟܘܬܐ ܕܐܠܗܐ ܐܝܟ ܛܠܝܐ ܠܐ ܢܥܘܠ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 18, 'ܘܫܐܠܗ ܚܕ ܡܢ ܪܫܢܐ ܘܐܡܪ ܠܗ ܡܠܦܢܐ ܛܒܐ ܡܢܐ ܐܥܒܕ ܕܐܪܬ ܚܝܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 19, 'ܐܡܪ ܠܗ ܝܫܘܥ ܡܢܐ ܩܪܐ ܐܢܬ ܠܝ ܛܒܐ ܠܝܬ ܛܒܐ ܐܠܐ ܐܢ ܚܕ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 20, 'ܦܘܩܕܢܐ ܝܕܥ ܐܢܬ ܠܐ ܬܩܛܘܠ ܘܠܐ ܬܓܘܪ ܘܠܐ ܬܓܢܘܒ ܘܠܐ ܬܤܗܕ ܤܗܕܘܬܐ ܕܫܘܩܪܐ ܝܩܪ ܠܐܒܘܟ ܘܠܐܡܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 21, 'ܐܡܪ ܠܗ ܗܠܝܢ ܟܠܗܝܢ ܢܛܪܬ ܐܢܝܢ ܡܢ ܛܠܝܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 22, 'ܟܕ ܫܡܥ ܕܝܢ ܗܠܝܢ ܝܫܘܥ ܐܡܪ ܠܗ ܚܕܐ ܚܤܝܪܐ ܠܟ ܙܠ ܙܒܢ ܟܠ ܡܕܡ ܕܐܝܬ ܠܟ ܘܗܒ ܠܡܤܟܢܐ ܘܬܗܘܐ ܠܟ ܤܝܡܬܐ ܒܫܡܝܐ ܘܬܐ ܒܬܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 23, 'ܗܘ ܕܝܢ ܟܕ ܫܡܥ ܗܠܝܢ ܟܪܝܬ ܠܗ ܥܬܝܪ ܗܘܐ ܓܝܪ ܛܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 24, 'ܘܟܕ ܚܙܐ ܝܫܘܥ ܕܟܪܝܬ ܠܗ ܐܡܪ ܐܝܟܢܐ ܥܛܠܐ ܠܐܝܠܝܢ ܕܐܝܬ ܠܗܘܢ ܢܟܤܐ ܕܢܥܠܘܢ ܠܡܠܟܘܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 25, 'ܕܠܝܠܐ ܗܝ ܠܓܡܠܐ ܕܒܚܪܘܪܐ ܕܡܚܛܐ ܢܥܘܠ ܐܘ ܥܬܝܪܐ ܠܡܠܟܘܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 26, 'ܐܡܪܝܢ ܠܗ ܐܝܠܝܢ ܕܫܡܥܘ ܘܡܢܘ ܡܫܟܚ ܠܡܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 27, 'ܝܫܘܥ ܕܝܢ ܐܡܪ ܐܝܠܝܢ ܕܠܘܬ ܒܢܝ ܐܢܫܐ ܠܐ ܡܫܟܚܢ ܠܘܬ ܐܠܗܐ ܡܫܟܚܢ ܠܡܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 28, 'ܐܡܪ ܠܗ ܫܡܥܘܢ ܟܐܦܐ ܗܐ ܚܢܢ ܫܒܩܢ ܟܠ ܡܕܡ ܘܐܬܝܢ ܒܬܪܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 29, 'ܐܡܪ ܠܗ ܝܫܘܥ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܠܝܬ ܐܢܫ ܕܫܒܩ ܒܬܐ ܐܘ ܐܒܗܐ ܐܘ ܐܚܐ ܐܘ ܐܢܬܬܐ ܐܘ ܒܢܝܐ ܡܛܠ ܡܠܟܘܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 30, 'ܘܠܐ ܢܩܒܠ ܒܐܥܦܐ ܤܓܝܐܐ ܒܗܢܐ ܙܒܢܐ ܘܒܥܠܡܐ ܕܐܬܐ ܚܝܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 31, 'ܘܕܒܪ ܝܫܘܥ ܠܬܪܥܤܪܬܗ ܘܐܡܪ ܠܗܘܢ ܗܐ ܤܠܩܝܢܢ ܠܐܘܪܫܠܡ ܘܡܫܬܠܡܢ ܟܠܗܝܢ ܕܟܬܝܒܢ ܒܢܒܝܐ ܥܠ ܒܪܗ ܕܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 32, 'ܢܫܬܠܡ ܓܝܪ ܠܥܡܡܐ ܘܢܒܙܚܘܢ ܒܗ ܘܢܪܩܘܢ ܒܐܦܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 33, 'ܘܢܢܓܕܘܢܝܗܝ ܘܢܨܥܪܘܢܝܗܝ ܘܢܩܛܠܘܢܝܗܝ ܘܠܝܘܡܐ ܕܬܠܬܐ ܢܩܘܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 34, 'ܗܢܘܢ ܕܝܢ ܚܕܐ ܡܢ ܗܠܝܢ ܠܐ ܐܤܬܟܠܘ ܐܠܐ ܡܟܤܝܐ ܗܘܬ ܡܢܗܘܢ ܡܠܬܐ ܗܕܐ ܘܠܐ ܝܕܥܝܢ ܗܘܘ ܗܠܝܢ ܕܡܬܡܠܠܢ ܥܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 35, 'ܘܟܕ ܩܪܝܒ ܠܐܝܪܝܚܘ ܤܡܝܐ ܚܕ ܝܬܒ ܗܘܐ ܥܠ ܝܕ ܐܘܪܚܐ ܘܚܕܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 36, 'ܘܫܡܥ ܩܠ ܟܢܫܐ ܕܥܒܪ ܘܡܫܐܠ ܗܘܐ ܕܡܢܘ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 37, 'ܐܡܪܝܢ ܠܗ ܝܫܘܥ ܢܨܪܝܐ ܥܒܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 38, 'ܘܩܥܐ ܘܐܡܪ ܝܫܘܥ ܒܪܗ ܕܕܘܝܕ ܐܬܪܚܡ ܥܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 39, 'ܘܐܝܠܝܢ ܕܐܙܠܝܢ ܗܘܘ ܩܕܡܘܗܝ ܕܝܫܘܥ ܟܐܝܢ ܗܘܘ ܒܗ ܕܢܫܬܘܩ ܗܘ ܕܝܢ ܝܬܝܪܐܝܬ ܩܥܐ ܗܘܐ ܒܪܗ ܕܕܘܝܕ ܐܬܪܚܡ ܥܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 40, 'ܘܩܡ ܝܫܘܥ ܘܦܩܕ ܕܢܝܬܘܢܝܗܝ ܠܘܬܗ ܘܟܕ ܩܪܒ ܠܘܬܗ ܫܐܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 41, 'ܘܐܡܪ ܠܗ ܡܢܐ ܨܒܐ ܐܢܬ ܐܥܒܕ ܠܟ ܗܘ ܕܝܢ ܐܡܪ ܡܪܝ ܕܐܚܙܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 42, 'ܘܝܫܘܥ ܐܡܪ ܠܗ ܚܙܝ ܗܝܡܢܘܬܟ ܐܚܝܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 43, 'ܘܒܪ ܫܥܬܗ ܚܙܐ ܘܐܬܐ ܗܘܐ ܒܬܪܗ ܘܡܫܒܚ ܠܐܠܗܐ ܘܟܠܗ ܥܡܐ ܕܚܙܐ ܝܗܒ ܗܘܐ ܫܘܒܚܐ ܠܐܠܗܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 1, 'ܘܟܕ ܥܠ ܝܫܘܥ ܘܥܒܪ ܒܐܝܪܝܚܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 2, 'ܓܒܪܐ ܚܕ ܕܫܡܗ ܙܟܝ ܥܬܝܪܐ ܗܘܐ ܘܪܒ ܡܟܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 3, 'ܘܨܒܐ ܗܘܐ ܕܢܚܙܐ ܠܝܫܘܥ ܕܡܢܘ ܘܠܐ ܡܫܟܚ ܗܘܐ ܡܢ ܟܢܫܐ ܡܛܠ ܕܒܩܘܡܬܗ ܙܥܘܪ ܗܘܐ ܙܟܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 4, 'ܘܪܗܛ ܩܕܡܗ ܠܝܫܘܥ ܘܤܠܩ ܠܗ ܠܬܬܐ ܦܟܝܗܬܐ ܕܢܚܙܝܘܗܝ ܡܛܠ ܕܗܟܘܬ ܥܬܝܕ ܗܘܐ ܕܢܥܒܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 5, 'ܘܟܕ ܐܬܐ ܠܗܝ ܕܘܟܬܐ ܝܫܘܥ ܚܙܝܗܝ ܘܐܡܪ ܠܗ ܐܤܬܪܗܒ ܚܘܬ ܙܟܝ ܝܘܡܢܐ ܓܝܪ ܘܠܐ ܕܒܒܝܬܟ ܐܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 6, 'ܘܐܤܬܪܗܒ ܢܚܬ ܘܩܒܠܗ ܟܕ ܚܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 7, 'ܟܕ ܚܙܘ ܕܝܢ ܟܠܗܘܢ ܪܛܢܝܢ ܗܘܘ ܘܐܡܪܝܢ ܕܠܘܬ ܓܒܪܐ ܚܛܝܐ ܥܠ ܫܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 8, 'ܩܡ ܕܝܢ ܙܟܝ ܘܐܡܪ ܠܝܫܘܥ ܗܐ ܡܪܝ ܦܠܓܘܬ ܢܟܤܝ ܝܗܒ ܐܢܐ ܠܡܤܟܢܐ ܘܠܟܠܢܫ ܡܕܡ ܕܓܠܙܬ ܚܕ ܒܐܪܒܥܐ ܦܪܥ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 9, 'ܐܡܪ ܠܗ ܝܫܘܥ ܝܘܡܢܐ ܗܘܘ ܚܝܐ ܠܒܝܬܐ ܗܢܐ ܡܛܠ ܕܐܦ ܗܢܐ ܒܪܗ ܗܘ ܕܐܒܪܗܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 10, 'ܐܬܐ ܓܝܪ ܒܪܗ ܕܐܢܫܐ ܕܢܒܥܐ ܘܢܚܐ ܗܘ ܡܕܡ ܕܐܒܝܕ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 11, 'ܘܟܕ ܫܡܥܝܢ ܗܘܘ ܗܠܝܢ ܐܘܤܦ ܠܡܐܡܪ ܡܬܠܐ ܡܛܠ ܕܩܪܝܒ ܗܘܐ ܠܐܘܪܫܠܡ ܘܤܒܪܝܢ ܗܘܘ ܕܒܗܝ ܫܥܬܐ ܥܬܝܕܐ ܕܬܬܓܠܐ ܡܠܟܘܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 12, 'ܘܐܡܪ ܓܒܪܐ ܚܕ ܒܪ ܛܘܗܡܐ ܪܒܐ ܐܙܠ ܠܐܬܪܐ ܪܚܝܩܐ ܕܢܤܒ ܠܗ ܡܠܟܘܬܐ ܘܢܗܦܘܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 13, 'ܘܩܪܐ ܥܤܪܐ ܥܒܕܘܗܝ ܘܝܗܒ ܠܗܘܢ ܥܤܪܐ ܡܢܝܢ ܘܐܡܪ ܠܗܘܢ ܐܬܬܓܪܘ ܥܕ ܐܬܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 14, 'ܒܢܝ ܡܕܝܢܬܗ ܕܝܢ ܤܢܝܢ ܗܘܘ ܠܗ ܘܫܕܪܘ ܐܝܙܓܕܐ ܒܬܪܗ ܘܐܡܪܝܢ ܠܐ ܨܒܝܢ ܚܢܢ ܕܢܡܠܟ ܥܠܝܢ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 15, 'ܘܟܕ ܢܤܒ ܡܠܟܘܬܐ ܘܗܦܟ ܐܡܪ ܕܢܩܪܘܢ ܠܗ ܗܢܘܢ ܥܒܕܘܗܝ ܕܝܗܒ ܠܗܘܢ ܟܤܦܐ ܕܢܕܥ ܡܢܐ ܟܠ ܚܕ ܚܕ ܡܢܗܘܢ ܐܬܬܓܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 16, 'ܘܐܬܐ ܩܕܡܝܐ ܘܐܡܪ ܡܪܝ ܡܢܝܟ ܥܤܪܐ ܡܢܝܢ ܐܘܬܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 17, 'ܐܡܪ ܠܗ ܐܝܘ ܥܒܕܐ ܛܒܐ ܕܒܩܠܝܠ ܐܫܬܟܚܬ ܡܗܝܡܢ ܬܗܘܐ ܫܠܝܛ ܥܠ ܥܤܪܐ ܟܪܟܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 18, 'ܘܐܬܐ ܕܬܪܝܢ ܘܐܡܪ ܡܪܝ ܡܢܝܟ ܚܡܫܐ ܡܢܝܢ ܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 19, 'ܐܡܪ ܐܦ ܠܗܢܐ ܐܦ ܐܢܬ ܬܗܘܐ ܫܠܝܛ ܥܠ ܚܡܫܐ ܟܪܟܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 20, 'ܘܐܬܐ ܐܚܪܢܐ ܘܐܡܪ ܡܪܝ ܗܐ ܡܢܝܟ ܗܘ ܕܐܝܬ ܗܘܐ ܠܘܬܝ ܟܕ ܤܝܡ ܒܤܕܘܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 21, 'ܕܚܠܬ ܓܝܪ ܡܢܟ ܕܓܒܪܐ ܐܢܬ ܩܫܝܐ ܘܫܩܠ ܐܢܬ ܡܕܡ ܕܠܐ ܤܡܬ ܘܚܨܕ ܐܢܬ ܡܕܡ ܕܠܐ ܙܪܥܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 22, 'ܐܡܪ ܠܗ ܡܢ ܦܘܡܟ ܐܕܘܢܟ ܥܒܕܐ ܒܝܫܐ ܝܕܥ ܗܘܝܬ ܠܝ ܕܓܒܪܐ ܐܢܐ ܩܫܝܐ ܘܫܩܠ ܐܢܐ ܡܕܡ ܕܠܐ ܤܡܬ ܘܚܨܕ ܐܢܐ ܡܕܡ ܕܠܐ ܙܪܥܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 23, 'ܠܡܢܐ ܠܐ ܝܗܒܬ ܟܤܦܝ ܥܠ ܦܬܘܪܐ ܘܐܢܐ ܐܬܐ ܗܘܝܬ ܬܒܥ ܠܗ ܥܡ ܪܒܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 24, 'ܘܠܗܢܘܢ ܕܩܝܡܝܢ ܩܕܡܘܗܝ ܐܡܪ ܤܒܘ ܡܢܗ ܡܢܝܐ ܘܗܒܘ ܠܗܘ ܕܐܝܬ ܠܘܬܗ ܥܤܪܐ ܡܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 25, 'ܐܡܪܝܢ ܠܗ ܡܪܢ ܐܝܬ ܠܘܬܗ ܥܤܪܐ ܡܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 26, 'ܐܡܪ ܠܗܘܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܠܟܠ ܡܢ ܕܐܝܬ ܠܗ ܢܬܝܗܒ ܠܗ ܘܡܢ ܗܘ ܕܠܝܬ ܠܗ ܐܦ ܗܘ ܕܐܝܬ ܠܗ ܢܬܢܤܒ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 27, 'ܒܪܡ ܠܗܢܘܢ ܒܥܠܕܒܒܝ ܐܝܠܝܢ ܕܠܐ ܨܒܘ ܕܐܡܠܟ ܥܠܝܗܘܢ ܐܝܬܘ ܐܢܘܢ ܘܩܛܠܘ ܐܢܘܢ ܩܕܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 28, 'ܘܟܕ ܐܡܪ ܗܠܝܢ ܝܫܘܥ ܢܦܩ ܠܩܕܡܘܗܝ ܕܢܐܙܠ ܠܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 29, 'ܘܟܕ ܡܛܝ ܠܒܝܬ ܦܓܐ ܘܒܝܬ ܥܢܝܐ ܥܠ ܓܢܒ ܛܘܪܐ ܕܡܬܩܪܐ ܕܒܝܬ ܙܝܬܐ ܫܕܪ ܬܪܝܢ ܡܢ ܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 30, 'ܘܐܡܪ ܠܗܘܢ ܙܠܘ ܠܩܪܝܬܐ ܗܝ ܕܠܩܘܒܠܢ ܘܟܕ ܥܐܠܝܢ ܐܢܬܘܢ ܠܗ ܗܐ ܡܫܟܚܝܢ ܐܢܬܘܢ ܥܝܠܐ ܕܐܤܝܪ ܕܐܢܫ ܡܡܬܘܡ ܠܐ ܪܟܒ ܥܠܘܗܝ ܫܪܘ ܐܝܬܐܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 31, 'ܘܐܢ ܐܢܫ ܡܫܐܠ ܠܟܘܢ ܠܡܢܐ ܫܪܝܢ ܐܢܬܘܢ ܠܗ ܗܟܢܐ ܐܡܪܘ ܠܗ ܠܡܪܢ ܡܬܒܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 32, 'ܘܐܙܠܘ ܗܢܘܢ ܕܐܫܬܕܪܘ ܘܐܫܟܚܘ ܐܝܟܢܐ ܕܐܡܪ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 33, 'ܘܟܕ ܫܪܝܢ ܠܗ ܠܥܝܠܐ ܐܡܪܝܢ ܠܗܘܢ ܡܪܘܗܝ ܡܢܐ ܫܪܝܢ ܐܢܬܘܢ ܥܝܠܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 34, 'ܘܐܡܪܘ ܠܗܘܢ ܕܠܡܪܢ ܡܬܒܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 35, 'ܘܐܝܬܝܘܗܝ ܠܘܬ ܝܫܘܥ ܘܐܪܡܝܘ ܥܠ ܥܝܠܐ ܡܐܢܝܗܘܢ ܘܐܪܟܒܘܗܝ ܠܝܫܘܥ ܥܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 36, 'ܟܕ ܕܝܢ ܐܙܠ ܦܪܤܝܢ ܗܘܘ ܡܐܢܝܗܘܢ ܒܐܘܪܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 37, 'ܘܟܕ ܩܪܒ ܠܡܚܬܬܐ ܕܛܘܪܐ ܕܒܝܬ ܙܝܬܐ ܫܪܝܘ ܟܠܗ ܟܢܫܐ ܕܬܠܡܝܕܐ ܚܕܝܢ ܘܡܫܒܚܝܢ ܠܐܠܗܐ ܒܩܠܐ ܪܡܐ ܥܠ ܟܠ ܚܝܠܐ ܕܚܙܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 38, 'ܘܐܡܪܝܢ ܗܘܘ ܒܪܝܟ ܗܘ ܡܠܟܐ ܕܐܬܐ ܒܫܡܗ ܕܡܪܝܐ ܫܠܡܐ ܒܫܡܝܐ ܘܫܘܒܚܐ ܒܡܪܘܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 39, 'ܐܢܫܐ ܕܝܢ ܡܢ ܦܪܝܫܐ ܡܢ ܒܝܢܝ ܟܢܫܐ ܐܡܪܝܢ ܠܗ ܪܒܝ ܟܐܝ ܒܬܠܡܝܕܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 40, 'ܐܡܪ ܠܗܘܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܐܢ ܗܠܝܢ ܢܫܬܩܘܢ ܟܐܦܐ ܢܩܥܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 41, 'ܘܟܕ ܩܪܒ ܘܚܙܗ ܠܡܕܝܢܬܐ ܒܟܐ ܥܠܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 42, 'ܘܐܡܪ ܐܠܘ ܟܝ ܝܕܥܬܝ ܐܝܠܝܢ ܕܐܝܬܝܗܝܢ ܕܫܠܡܟܝ ܐܦܢ ܒܗܢܐ ܝܘܡܟܝ ܗܫܐ ܕܝܢ ܐܬܟܤܝ ܠܗܝܢ ܡܢ ܥܝܢܝܟܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 43, 'ܢܐܬܘܢ ܠܟܝ ܕܝܢ ܝܘܡܬܐ ܕܢܚܕܪܘܢܟܝ ܒܥܠܕܒܒܝܟܝ ܘܢܐܠܨܘܢܟܝ ܡܢ ܟܠ ܕܘܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 44, 'ܘܢܤܚܦܘܢܟܝ ܘܠܒܢܝܟܝ ܒܓܘܟܝ ܘܠܐ ܢܫܒܩܘܢ ܒܟܝ ܟܐܦ ܥܠ ܟܐܦ ܚܠܦ ܕܠܐ ܝܕܥܬܝ ܙܒܢܐ ܕܤܘܥܪܢܟܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 45, 'ܘܟܕ ܥܠ ܠܗܝܟܠܐ ܫܪܝ ܠܡܦܩܘ ܠܐܝܠܝܢ ܕܙܒܢܝܢ ܒܗ ܘܡܙܒܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 46, 'ܘܐܡܪ ܠܗܘܢ ܟܬܝܒ ܕܒܝܬܝ ܒܝܬ ܨܠܘܬܐ ܐܝܬܘܗܝ ܐܢܬܘܢ ܕܝܢ ܥܒܕܬܘܢܝܗܝ ܡܥܪܬܐ ܕܠܤܛܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 47, 'ܘܗܘ ܡܠܦ ܗܘܐ ܟܠ ܝܘܡ ܒܗܝܟܠܐ ܪܒܝ ܟܗܢܐ ܕܝܢ ܘܤܦܪܐ ܘܩܫܝܫܐ ܕܥܡܐ ܒܥܝܢ ܗܘܘ ܠܡܘܒܕܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 48, 'ܘܠܐ ܡܫܟܚܝܢ ܗܘܘ ܡܢܐ ܢܥܒܕܘܢ ܠܗ ܟܠܗ ܓܝܪ ܥܡܐ ܬܠܐ ܗܘܐ ܒܗ ܠܡܫܡܥܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 1, 'ܘܗܘܐ ܒܚܕ ܡܢ ܝܘܡܬܐ ܟܕ ܡܠܦ ܒܗܝܟܠܐ ܠܥܡܐ ܘܡܤܒܪ ܩܡܘ ܥܠܘܗܝ ܪܒܝ ܟܗܢܐ ܘܤܦܪܐ ܥܡ ܩܫܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 2, 'ܘܐܡܪܝܢ ܠܗ ܐܡܪ ܠܢ ܒܐܝܢܐ ܫܘܠܛܢܐ ܗܠܝܢ ܥܒܕ ܐܢܬ ܘܡܢܘ ܗܘ ܕܝܗܒ ܠܟ ܫܘܠܛܢܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 3, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܐܫܐܠܟܘܢ ܐܦ ܐܢܐ ܡܠܬܐ ܘܐܡܪܘ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 4, 'ܡܥܡܘܕܝܬܗ ܕܝܘܚܢܢ ܡܢ ܫܡܝܐ ܗܘܬ ܐܘ ܡܢ ܒܢܝ ܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 5, 'ܗܢܘܢ ܕܝܢ ܡܬܚܫܒܝܢ ܗܘܘ ܒܢܦܫܗܘܢ ܘܐܡܪܝܢ ܕܐܢ ܢܐܡܪ ܡܢ ܫܡܝܐ ܐܡܪ ܠܢ ܘܡܛܠ ܡܢܐ ܠܐ ܗܝܡܢܬܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 6, 'ܐܢ ܕܝܢ ܢܐܡܪ ܡܢ ܒܢܝ ܐܢܫܐ ܪܓܡ ܠܢ ܥܡܐ ܟܠܗ ܡܦܤܝܢ ܓܝܪ ܕܝܘܚܢܢ ܢܒܝܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 7, 'ܘܐܡܪܘ ܠܗ ܕܠܐ ܝܕܥܝܢܢ ܡܢ ܐܝܡܟܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 8, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܘܠܐ ܐܢܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܒܐܝܢܐ ܫܘܠܛܢܐ ܗܠܝܢ ܥܒܕ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 9, 'ܘܫܪܝ ܕܢܐܡܪ ܠܥܡܐ ܡܬܠܐ ܗܢܐ ܓܒܪܐ ܚܕ ܢܨܒ ܟܪܡܐ ܘܐܘܚܕܗ ܠܦܠܚܐ ܘܐܒܥܕ ܙܒܢܐ ܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 10, 'ܘܒܙܒܢܐ ܫܕܪ ܥܒܕܗ ܠܘܬ ܦܠܚܐ ܕܢܬܠܘܢ ܠܗ ܡܢ ܦܐܪܐ ܕܟܪܡܐ ܦܠܚܐ ܕܝܢ ܡܚܐܘܗܝ ܘܫܕܪܘܗܝ ܟܕ ܤܪܝܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 11, 'ܘܐܘܤܦ ܘܫܕܪ ܠܥܒܕܗ ܐܚܪܢܐ ܗܢܘܢ ܕܝܢ ܐܦ ܠܗܘ ܡܚܐܘܗܝ ܘܨܥܪܘܗܝ ܘܫܕܪܘܗܝ ܟܕ ܤܪܝܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 12, 'ܘܐܘܤܦ ܘܫܕܪ ܕܬܠܬܐ ܗܢܘܢ ܕܝܢ ܘܐܦ ܠܗܘ ܨܠܦܘܗܝ ܘܐܦܩܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 13, 'ܐܡܪ ܡܪܐ ܟܪܡܐ ܡܢܐ ܐܥܒܕ ܐܫܕܪ ܒܪܝ ܚܒܝܒܐ ܟܒܪ ܢܚܙܘܢܝܗܝ ܘܢܬܟܚܕܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 14, 'ܟܕ ܚܙܐܘܗܝ ܕܝܢ ܦܠܚܐ ܡܬܚܫܒܝܢ ܗܘܘ ܒܢܦܫܗܘܢ ܘܐܡܪܝܢ ܗܢܘ ܝܪܬܐ ܬܘ ܢܩܛܠܝܘܗܝ ܘܬܗܘܐ ܝܪܬܘܬܐ ܕܝܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 15, 'ܘܐܦܩܘܗܝ ܠܒܪ ܡܢ ܟܪܡܐ ܘܩܛܠܘܗܝ ܡܢܐ ܗܟܝܠ ܢܥܒܕ ܠܗܘܢ ܡܪܐ ܟܪܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 16, 'ܢܐܬܐ ܘܢܘܒܕ ܠܦܠܚܐ ܗܢܘܢ ܘܢܬܠ ܟܪܡܐ ܠܐܚܪܢܐ ܟܕ ܫܡܥܘ ܕܝܢ ܐܡܪܘ ܠܐ ܬܗܘܐ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 17, 'ܗܘ ܕܝܢ ܚܪ ܒܗܘܢ ܘܐܡܪ ܘܡܢܐ ܗܝ ܗܝ ܕܟܬܝܒܐ ܕܟܐܦܐ ܕܐܤܠܝܘ ܒܢܝܐ ܗܝ ܗܘܬ ܠܪܝܫ ܩܪܢܐ ܕܙܘܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 18, 'ܘܟܠ ܕܢܦܠ ܥܠ ܗܝ ܟܐܦܐ ܢܬܪܥܥ ܘܟܠ ܡܢ ܕܗܝ ܬܦܠ ܥܠܘܗܝ ܬܕܪܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 19, 'ܒܥܘ ܗܘܘ ܕܝܢ ܪܒܝ ܟܗܢܐ ܘܤܦܪܐ ܕܢܪܡܘܢ ܥܠܘܗܝ ܐܝܕܝܐ ܒܗܝ ܫܥܬܐ ܘܕܚܠܘ ܡܢ ܥܡܐ ܝܕܥܘ ܓܝܪ ܕܥܠܝܗܘܢ ܐܡܪ ܡܬܠܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 20, 'ܘܫܕܪܘ ܠܘܬܗ ܓܫܘܫܐ ܕܡܬܕܡܝܢ ܒܙܕܝܩܐ ܕܢܐܚܕܘܢܝܗܝ ܒܡܠܬܐ ܘܢܫܠܡܘܢܝܗܝ ܠܕܝܢܐ ܘܠܫܘܠܛܢܗ ܕܗܓܡܘܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 21, 'ܘܫܐܠܘܗܝ ܘܐܡܪܝܢ ܠܗ ܡܠܦܢܐ ܝܕܥܝܢܢ ܕܬܪܝܨܐܝܬ ܡܡܠܠ ܐܢܬ ܘܡܠܦ ܘܠܐ ܢܤܒ ܐܢܬ ܒܐܦܐ ܐܠܐ ܒܩܘܫܬܐ ܐܘܪܚܐ ܕܐܠܗܐ ܡܠܦ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 22, 'ܫܠܝܛ ܠܢ ܕܢܬܠ ܟܤܦ ܪܫܐ ܠܩܤܪ ܐܘ ܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 23, 'ܗܘ ܕܝܢ ܐܤܬܟܠ ܚܪܥܘܬܗܘܢ ܘܐܡܪ ܡܢܐ ܡܢܤܝܢ ܐܢܬܘܢ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 24, 'ܚܘܐܘܢܝ ܕܝܢܪܐ ܕܡܢ ܐܝܬ ܒܗ ܨܠܡܐ ܘܟܬܝܒܬܐ ܗܢܘܢ ܕܝܢ ܐܡܪܘ ܕܩܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 25, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܗܒܘ ܗܟܝܠ ܕܩܤܪ ܠܩܤܪ ܘܕܐܠܗܐ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 26, 'ܘܠܐ ܐܫܟܚܘ ܠܡܐܚܕ ܡܢܗ ܡܠܬܐ ܩܕܡ ܥܡܐ ܘܐܬܕܡܪܘ ܥܠ ܦܬܓܡܗ ܘܫܬܩܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 27, 'ܩܪܒܘ ܕܝܢ ܐܢܫܝܢ ܡܢ ܙܕܘܩܝܐ ܗܢܘܢ ܕܐܡܪܝܢ ܕܩܝܡܬܐ ܠܝܬ ܘܫܐܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 28, 'ܘܐܡܪܝܢ ܠܗ ܡܠܦܢܐ ܡܘܫܐ ܟܬܒ ܠܢ ܕܐܢ ܐܢܫ ܢܡܘܬ ܐܚܘܗܝ ܕܐܝܬ ܠܗ ܐܢܬܬܐ ܕܠܐ ܒܢܝܐ ܢܤܒ ܐܚܘܗܝ ܐܢܬܬܗ ܘܢܩܝܡ ܙܪܥܐ ܠܐܚܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 29, 'ܫܒܥܐ ܕܝܢ ܐܚܝܢ ܐܝܬ ܗܘܘ ܩܕܡܝܐ ܢܤܒ ܐܢܬܬܐ ܘܡܝܬ ܕܠܐ ܒܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 30, 'ܘܢܤܒܗ ܕܬܪܝܢ ܠܐܢܬܬܗ ܘܗܢܐ ܡܝܬ ܕܠܐ ܒܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 31, 'ܘܕܬܠܬܐ ܬܘܒ ܢܤܒܗ ܘܗܟܘܬ ܘܐܦ ܫܒܥܬܝܗܘܢ ܘܡܝܬܘ ܘܠܐ ܫܒܩܘ ܒܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 32, 'ܘܡܝܬܬ ܒܚܪܬܐ ܐܦ ܐܢܬܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 33, 'ܒܩܝܡܬܐ ܗܟܝܠ ܕܐܝܢܐ ܡܢܗܘܢ ܬܗܘܐ ܐܢܬܬܐ ܫܒܥܬܝܗܘܢ ܓܝܪ ܢܤܒܘܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 34, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܒܢܘܗܝ ܕܥܠܡܐ ܗܢܐ ܢܤܒܝܢ ܢܫܐ ܘܢܫܐ ܗܘܝܢ ܠܓܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 35, 'ܗܢܘܢ ܕܝܢ ܕܠܗܘ ܥܠܡܐ ܫܘܘ ܘܠܩܝܡܬܐ ܕܡܢ ܒܝܬ ܡܝܬܐ ܠܐ ܢܤܒܝܢ ܢܫܐ ܘܐܦ ܠܐ ܢܫܐ ܗܘܝܢ ܠܓܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 36, 'ܐܦܠܐ ܓܝܪ ܬܘܒ ܠܡܡܬ ܡܫܟܚܝܢ ܐܝܟ ܡܠܐܟܐ ܐܢܘܢ ܓܝܪ ܘܒܢܝܐ ܐܝܬܝܗܘܢ ܕܐܠܗܐ ܡܛܠ ܕܗܘܘ ܒܢܝܐ ܕܩܝܡܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 37, 'ܕܩܝܡܝܢ ܕܝܢ ܡܝܬܐ ܐܦ ܡܘܫܐ ܒܕܩ ܐܕܟܪ ܓܝܪ ܒܤܢܝܐ ܟܕ ܐܡܪ ܡܪܝܐ ܐܠܗܗ ܕܐܒܪܗܡ ܘܐܠܗܗ ܕܐܝܤܚܩ ܘܐܠܗܗ ܕܝܥܩܘܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 38, 'ܐܠܗܐ ܕܝܢ ܠܐ ܗܘܐ ܕܡܝܬܐ ܐܠܐ ܕܚܝܐ ܟܠܗܘܢ ܓܝܪ ܚܝܝܢ ܐܢܘܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 39, 'ܘܥܢܘ ܐܢܫܝܢ ܡܢ ܤܦܪܐ ܘܐܡܪܝܢ ܠܗ ܡܠܦܢܐ ܫܦܝܪ ܐܡܪ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 40, 'ܘܠܐ ܬܘܒ ܐܡܪܚܘ ܠܡܫܐܠܘܬܗ ܥܠ ܡܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 41, 'ܘܐܡܪ ܗܘܐ ܠܗܘܢ ܐܝܟܢܐ ܐܡܪܝܢ ܤܦܪܐ ܥܠ ܡܫܝܚܐ ܕܒܪܗ ܗܘ ܕܕܘܝܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 42, 'ܘܗܘ ܕܘܝܕ ܐܡܪ ܒܟܬܒܐ ܕܡܙܡܘܪܐ ܕܐܡܪ ܡܪܝܐ ܠܡܪܝ ܬܒ ܠܟ ܡܢ ܝܡܝܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 43, 'ܥܕܡܐ ܕܐܤܝܡ ܒܥܠܕܒܒܝܟ ܬܚܝܬ ܪܓܠܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 44, 'ܐܢ ܗܟܝܠ ܕܘܝܕ ܡܪܝ ܩܪܐ ܠܗ ܐܝܟܢܐ ܒܪܗ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 45, 'ܘܟܕ ܟܠܗ ܥܡܐ ܫܡܥ ܗܘܐ ܐܡܪ ܠܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 46, 'ܐܙܕܗܪܘ ܡܢ ܤܦܪܐ ܕܨܒܝܢ ܠܡܗܠܟܘ ܒܐܤܛܠܐ ܘܪܚܡܝܢ ܫܠܡܐ ܒܫܘܩܐ ܘܪܝܫ ܡܘܬܒܐ ܒܟܢܘܫܬܐ ܘܪܝܫ ܤܡܟܐ ܒܐܚܫܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 47, 'ܗܢܘܢ ܕܐܟܠܝܢ ܒܬܐ ܕܐܪܡܠܬܐ ܒܥܠܬܐ ܕܡܘܪܟܝܢ ܨܠܘܬܗܘܢ ܗܢܘܢ ܢܩܒܠܘܢ ܕܝܢܐ ܝܬܝܪܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 1, 'ܚܪ ܕܝܢ ܝܫܘܥ ܒܥܬܝܪܐ ܐܝܠܝܢ ܕܪܡܝܢ ܗܘܘ ܒܝܬ ܓܙܐ ܩܘܪܒܢܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 2, 'ܘܚܙܐ ܐܦ ܐܪܡܠܬܐ ܚܕܐ ܡܤܟܢܬܐ ܕܐܪܡܝܬ ܫܡܘܢܐ ܬܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 3, 'ܘܐܡܪ ܫܪܪܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܗܕܐ ܐܪܡܠܬܐ ܡܤܟܢܬܐ ܐܪܡܝܬ ܝܬܝܪ ܡܢ ܟܠܢܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 4, 'ܟܠܗܘܢ ܓܝܪ ܗܠܝܢ ܡܢ ܡܐ ܕܝܬܝܪ ܗܘܐ ܠܗܘܢ ܐܪܡܝܘ ܒܝܬ ܩܘܪܒܢܐ ܕܐܠܗܐ ܗܕܐ ܕܝܢ ܡܢ ܚܤܝܪܘܬܗ ܟܠ ܕܩܢܝܐ ܗܘܬ ܐܪܡܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 5, 'ܘܟܕ ܐܡܪܝܢ ܗܘܘ ܐܢܫܝܢ ܥܠ ܗܝܟܠܐ ܕܒܟܐܦܐ ܫܦܝܪܬܐ ܘܒܩܘܪܒܢܐ ܡܨܒܬ ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 6, 'ܗܠܝܢ ܕܚܙܝܢ ܐܢܬܘܢ ܢܐܬܘܢ ܝܘܡܬܐ ܕܒܗܘܢ ܠܐ ܬܫܬܒܩ ܟܐܦ ܥܠ ܟܐܦ ܕܠܐ ܬܤܬܬܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 7, 'ܘܡܫܐܠܝܢ ܗܘܘ ܠܗ ܘܐܡܪܝܢ ܡܠܦܢܐ ܐܡܬܝ ܗܠܝܢ ܢܗܘܝܢ ܘܡܢܐ ܗܝ ܐܬܐ ܡܐ ܕܩܪܝܒܢ ܗܠܝܢ ܕܢܗܘܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 8, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܚܙܘ ܠܡܐ ܬܛܥܘܢ ܤܓܝܐܐ ܓܝܪ ܢܐܬܘܢ ܒܫܡܝ ܘܢܐܡܪܘܢ ܕܐܢܐ ܐܢܐ ܡܫܝܚܐ ܘܙܒܢܐ ܩܪܒ ܠܐ ܕܝܢ ܬܐܙܠܘܢ ܒܬܪܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 9, 'ܘܡܐ ܕܫܡܥܝܢ ܐܢܬܘܢ ܩܪܒܐ ܘܫܓܘܫܝܐ ܠܐ ܬܕܚܠܘܢ ܥܬܝܕܢ ܐܢܝܢ ܓܝܪ ܗܠܝܢ ܠܘܩܕܡ ܠܡܗܘܐ ܐܠܐ ܠܐ ܥܕܟܝܠ ܡܛܬ ܚܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 10, 'ܢܩܘܡ ܓܝܪ ܥܡܐ ܥܠ ܥܡܐ ܘܡܠܟܘ ܥܠ ܡܠܟܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 11, 'ܘܙܘܥܐ ܪܘܪܒܐ ܢܗܘܘܢ ܒܕܘܟܐ ܕܘܟܐ ܘܟܦܢܐ ܘܡܘܬܢܐ ܘܢܗܘܝܢ ܕܚܠܬܐ ܘܤܘܪܕܐ ܘܐܬܘܬܐ ܪܘܪܒܬܐ ܡܢ ܫܡܝܐ ܢܬܚܙܝܢ ܘܤܬܘܐ ܪܘܪܒܐ ܢܗܘܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 12, 'ܩܕܡ ܕܝܢ ܗܠܝܢ ܟܠܗܝܢ ܢܪܡܘܢ ܥܠܝܟܘܢ ܐܝܕܝܐ ܘܢܪܕܦܘܢܟܘܢ ܘܢܫܠܡܘܢܟܘܢ ܠܟܢܘܫܬܐ ܘܠܒܝܬ ܐܤܝܪܐ ܘܢܩܪܒܘܢܟܘܢ ܩܕܡ ܡܠܟܐ ܘܗܓܡܘܢܐ ܡܛܠ ܫܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 13, 'ܗܘܝܐ ܠܟܘܢ ܕܝܢ ܠܤܗܕܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 14, 'ܤܝܡܘ ܕܝܢ ܒܠܒܟܘܢ ܕܠܐ ܬܗܘܘܢ ܡܬܝܠܦܝܢ ܠܡܦܩ ܪܘܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 15, 'ܐܢܐ ܓܝܪ ܐܬܠ ܠܟܘܢ ܦܘܡܐ ܘܚܟܡܬܐ ܐܝܕܐ ܕܠܐ ܢܫܟܚܘܢ ܠܡܩܡ ܠܩܘܒܠܗ ܟܠܗܘܢ ܒܥܠܕܒܒܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 16, 'ܢܫܠܡܘܢܟܘܢ ܕܝܢ ܐܒܗܝܟܘܢ ܘܐܚܝܟܘܢ ܘܐܚܝܢܝܟܘܢ ܘܪܚܡܝܟܘܢ ܘܢܡܝܬܘܢ ܡܢܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 17, 'ܘܬܗܘܘܢ ܤܢܝܐܝܢ ܡܢ ܟܠ ܐܢܫ ܡܛܠ ܫܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 18, 'ܘܡܢܬܐ ܡܢ ܪܫܟܘܢ ܠܐ ܬܐܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 19, 'ܒܡܤܝܒܪܢܘܬܟܘܢ ܕܝܢ ܬܩܢܘܢ ܢܦܫܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 20, 'ܡܐ ܕܝܢ ܕܚܙܝܬܘܢ ܠܐܘܪܫܠܡ ܕܚܕܝܪܝܢ ܠܗ ܚܝܠܐ ܗܝܕܝܢ ܕܥܘ ܕܩܪܒ ܠܗ ܚܘܪܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 21, 'ܗܝܕܝܢ ܐܝܠܝܢ ܕܒܝܗܘܕ ܐܢܘܢ ܢܥܪܩܘܢ ܠܛܘܪܐ ܘܐܝܠܝܢ ܕܒܓܘܗ ܐܢܘܢ ܢܥܪܩܘܢ ܘܕܒܩܘܪܝܐ ܠܐ ܢܥܠܘܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 22, 'ܕܝܘܡܬܐ ܐܢܘܢ ܗܠܝܢ ܕܬܒܥܬܐ ܕܢܫܠܡ ܟܠ ܡܐ ܕܟܬܝܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 23, 'ܘܝ ܕܝܢ ܠܐܝܠܝܢ ܕܒܛܢܢ ܘܠܐܝܠܝܢ ܕܡܝܢܩܢ ܒܗܢܘܢ ܝܘܡܬܐ ܢܗܘܐ ܓܝܪ ܐܘܠܨܢܐ ܪܒܐ ܒܐܪܥܐ ܘܪܘܓܙܐ ܥܠ ܥܡܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 24, 'ܘܢܦܠܘܢ ܒܦܘܡܐ ܕܚܪܒܐ ܘܢܫܬܒܘܢ ܠܟܠ ܐܬܪ ܘܐܘܪܫܠܡ ܬܗܘܐ ܡܬܕܝܫܐ ܡܢ ܥܡܡܐ ܥܕܡܐ ܕܢܫܠܡܘܢ ܙܒܢܐ ܕܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 25, 'ܘܢܗܘܝܢ ܐܬܘܬܐ ܒܫܡܫܐ ܘܒܤܗܪܐ ܘܒܟܘܟܒܐ ܘܒܐܪܥܐ ܐܘܠܨܢܐ ܕܥܡܡܐ ܘܦܘܫܟ ܐܝܕܝܐ ܡܢ ܬܘܗܬܐ ܕܩܠܐ ܕܝܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 26, 'ܘܙܘܥܐ ܕܡܦܩ ܢܦܫܬܐ ܕܒܢܝܢܫܐ ܡܢ ܕܚܠܬܐ ܕܡܕܡ ܕܥܬܝܕ ܠܡܐܬܐ ܥܠ ܐܪܥܐ ܘܢܬܬܙܝܥܘܢ ܚܝܠܐ ܕܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 27, 'ܘܗܝܕܝܢ ܢܚܙܘܢܝܗܝ ܠܒܪܗ ܕܐܢܫܐ ܕܐܬܐ ܒܥܢܢܐ ܥܡ ܚܝܠܐ ܤܓܝܐܐ ܘܫܘܒܚܐ ܪܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 28, 'ܡܐ ܕܝܢ ܕܫܪܝ ܗܠܝܢ ܕܢܗܘܝܢ ܐܬܠܒܒܘ ܘܐܪܝܡܘ ܪܫܝܟܘܢ ܡܛܠ ܕܩܪܒ ܠܗ ܦܘܪܩܢܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 29, 'ܘܐܡܪ ܗܘܐ ܠܗܘܢ ܡܬܠܐ ܚܙܘ ܠܬܬܐ ܘܠܟܠܗܘܢ ܐܝܠܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 30, 'ܕܡܐ ܕܡܦܪܥܝܢ ܡܚܕܐ ܡܢܗܘܢ ܡܤܬܟܠܝܢ ܐܢܬܘܢ ܕܩܪܒ ܠܗ ܩܝܛܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 31, 'ܗܟܢܐ ܐܦ ܐܢܬܘܢ ܡܐ ܕܚܙܝܬܘܢ ܗܠܝܢ ܕܗܘܝܢ ܕܥܘ ܕܩܪܝܒܐ ܗܝ ܡܠܟܘܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 32, 'ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܠܐ ܬܥܒܪ ܫܪܒܬܐ ܗܕܐ ܥܕܡܐ ܕܗܠܝܢ ܟܠܗܝܢ ܢܗܘܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 33, 'ܫܡܝܐ ܘܐܪܥܐ ܢܥܒܪܘܢ ܘܡܠܝ ܠܐ ܢܥܒܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 34, 'ܐܙܕܗܪܘ ܕܝܢ ܒܢܦܫܟܘܢ ܕܠܐ ܡܬܘܡ ܢܐܩܪܘܢ ܠܒܘܬܟܘܢ ܒܐܤܘܛܘܬܐ ܘܒܪܘܝܘܬܐ ܘܒܨܦܬܐ ܕܥܠܡܐ ܘܡܢ ܫܠܝܐ ܢܐܬܐ ܥܠܝܟܘܢ ܝܘܡܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 35, 'ܐܝܟ ܨܦܚܬܐ ܓܝܪ ܢܨܦܚ ܥܠ ܟܠܗܘܢ ܐܝܠܝܢ ܕܝܬܒܝܢ ܥܠ ܐܦܝܗ ܕܟܠܗ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 36, 'ܗܘܘ ܗܟܝܠ ܫܗܪܝܢ ܒܟܠܙܒܢ ܘܡܨܠܝܢ ܕܬܫܘܘܢ ܠܡܥܪܩ ܡܢ ܗܠܝܢ ܕܥܬܝܕܢ ܠܡܗܘܐ ܘܬܩܘܡܘܢ ܩܕܡ ܒܪܗ ܕܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 37, 'ܒܐܝܡܡܐ ܕܝܢ ܡܠܦ ܗܘܐ ܒܗܝܟܠܐ ܘܒܠܠܝܐ ܢܦܩ ܗܘܐ ܒܐܬ ܒܛܘܪܐ ܕܡܬܩܪܐ ܕܒܝܬ ܙܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 38, 'ܘܟܠܗ ܥܡܐ ܡܩܕܡܝܢ ܗܘܘ ܠܘܬܗ ܠܗܝܟܠܐ ܠܡܫܡܥ ܡܠܬܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 1, 'ܩܪܝܒ ܗܘܐ ܕܝܢ ܥܕܥܕܐ ܕܦܛܝܪܐ ܕܡܬܩܪܐ ܦܨܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 2, 'ܘܒܥܝܢ ܗܘܘ ܪܒܝ ܟܗܢܐ ܘܤܦܪܐ ܕܐܝܟܢܐ ܢܩܛܠܘܢܝܗܝ ܕܚܠܝܢ ܗܘܘ ܓܝܪ ܡܢ ܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 3, 'ܥܠ ܗܘܐ ܕܝܢ ܤܛܢܐ ܒܝܗܘܕܐ ܕܡܬܩܪܐ ܤܟܪܝܘܛܐ ܕܐܝܬܘܗܝ ܗܘܐ ܡܢ ܡܢܝܢܐ ܕܬܪܥܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 4, 'ܘܐܙܠ ܡܠܠ ܥܡ ܪܒܝ ܟܗܢܐ ܘܤܦܪܐ ܘܪܒܝ ܚܝܠܐ ܕܗܝܟܠܐ ܐܝܟ ܕܢܫܠܡܝܘܗܝ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 5, 'ܘܚܕܝܘ ܘܐܩܝܡܘ ܕܢܬܠܘܢ ܠܗ ܟܤܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 6, 'ܘܐܫܬܘܕܝ ܠܗܘܢ ܘܒܥܐ ܗܘܐ ܦܠܥܐ ܕܢܫܠܡܝܘܗܝ ܠܗܘܢ ܒܠܥܕ ܡܢ ܟܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 7, 'ܘܡܛܝ ܝܘܡܐ ܕܦܛܝܪܐ ܕܒܗ ܐܝܬ ܗܘܐ ܥܝܕܐ ܕܢܬܢܟܤ ܦܨܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 8, 'ܘܫܕܪ ܝܫܘܥ ܠܟܐܦܐ ܘܠܝܘܚܢܢ ܘܐܡܪ ܠܗܘܢ ܙܠܘ ܛܝܒܘ ܠܢ ܦܨܚܐ ܕܢܠܥܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 9, 'ܗܢܘܢ ܕܝܢ ܐܡܪܘ ܠܗ ܐܝܟܐ ܨܒܐ ܐܢܬ ܕܢܛܝܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 10, 'ܐܡܪ ܠܗܘܢ ܗܐ ܡܐ ܕܥܐܠܝܢ ܐܢܬܘܢ ܠܡܕܝܢܬܐ ܦܓܥ ܒܟܘܢ ܓܒܪܐ ܕܫܩܝܠ ܓܪܒܐ ܕܡܝܐ ܙܠܘ ܒܬܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 11, 'ܘܐܝܟܐ ܕܥܐܠ ܐܡܪܘ ܠܡܪܗ ܕܒܝܬܐ ܪܒܢ ܐܡܪ ܐܝܢܘ ܒܝܬ ܡܫܪܝܐ ܐܝܟܐ ܕܐܟܘܠ ܦܨܚܐ ܥܡ ܬܠܡܝܕܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 12, 'ܘܗܐ ܗܘ ܡܚܘܐ ܠܟܘܢ ܥܠܝܬܐ ܚܕܐ ܪܒܬܐ ܕܡܫܘܝܐ ܬܡܢ ܛܝܒܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 13, 'ܘܐܙܠܘ ܐܫܟܚܘ ܐܝܟ ܕܐܡܪ ܠܗܘܢ ܘܛܝܒܘ ܦܨܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 14, 'ܘܟܕ ܗܘܐ ܥܕܢܐ ܐܬܐ ܝܫܘܥ ܐܤܬܡܟ ܘܬܪܥܤܪ ܫܠܝܚܐ ܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 15, 'ܘܐܡܪ ܠܗܘܢ ܪܓܬܐ ܪܓܬܢܝ ܕܗܢܐ ܦܨܚܐ ܐܟܘܠ ܥܡܟܘܢ ܩܕܡ ܕܐܚܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 16, 'ܐܡܪ ܐܢܐ ܠܟܘܢ ܓܝܪ ܕܡܟܝܠ ܠܐ ܐܟܠܝܘܗܝ ܥܕܡܐ ܕܢܫܠܡ ܒܡܠܟܘܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 17, 'ܘܢܤܒ ܟܤܐ ܘܐܘܕܝ ܘܐܡܪ ܤܒܘ ܗܢܐ ܘܦܠܓܘ ܒܝܢܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 18, 'ܐܡܪ ܐܢܐ ܓܝܪ ܠܟܘܢ ܕܠܐ ܐܫܬܐ ܡܢ ܝܠܕܐ ܕܓܦܬܐ ܥܕܡܐ ܕܬܐܬܐ ܡܠܟܘܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 19, 'ܘܢܤܒ ܠܚܡܐ ܘܐܘܕܝ ܘܩܨܐ ܘܝܗܒ ܠܗܘܢ ܘܐܡܪ ܗܢܘ ܦܓܪܝ ܕܥܠ ܐܦܝܟܘܢ ܡܬܝܗܒ ܗܕܐ ܗܘܝܬܘܢ ܥܒܕܝܢ ܠܕܘܟܪܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 20, 'ܘܗܟܘܬ ܐܦ ܥܠ ܟܤܐ ܡܢ ܒܬܪ ܕܐܚܫܡܘ ܐܡܪ ܗܢܐ ܟܤܐ ܕܝܬܩܐ ܚܕܬܐ ܒܕܡܝ ܕܚܠܦܝܟܘܢ ܡܬܐܫܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 21, 'ܒܪܡ ܗܐ ܐܝܕܗ ܕܡܫܠܡܢܝ ܥܠ ܦܬܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 22, 'ܘܒܪܗ ܕܐܢܫܐ ܐܙܠ ܐܝܟܢܐ ܕܐܬܦܪܫ ܒܪܡ ܘܝ ܠܗܘ ܓܒܪܐ ܕܒܐܝܕܗ ܡܫܬܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 23, 'ܘܫܪܝܘ ܕܢܥܩܒܘܢ ܒܝܢܬܗܘܢ ܕܡܢܘ ܟܝ ܡܢܗܘܢ ܗܘ ܕܗܕܐ ܥܬܝܕ ܠܡܤܥܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 24, 'ܗܘܐ ܕܝܢ ܐܦ ܚܪܝܢܐ ܒܝܢܬܗܘܢ ܕܡܢ ܐܝܬ ܒܗܘܢ ܕܪܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 25, 'ܗܘ ܕܝܢ ܝܫܘܥ ܐܡܪ ܠܗܘܢ ܡܠܟܝܗܘܢ ܕܥܡܡܐ ܡܪܝܗܘܢ ܐܢܘܢ ܘܕܫܠܝܛܝܢ ܥܠܝܗܘܢ ܥܒܕܝ ܛܒܬܐ ܡܬܩܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 26, 'ܐܢܬܘܢ ܕܝܢ ܠܐ ܗܟܢܐ ܐܠܐ ܐܝܢܐ ܕܪܒ ܒܟܘܢ ܢܗܘܐ ܐܝܟ ܙܥܘܪܐ ܘܐܝܢܐ ܕܪܫܐ ܗܘ ܐܝܟ ܡܫܡܫܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 27, 'ܡܢܘ ܓܝܪ ܪܒ ܗܘ ܕܤܡܝܟ ܐܘ ܗܘ ܕܡܫܡܫ ܠܐ ܗܘܐ ܗܘ ܕܤܡܝܟ ܐܢܐ ܕܝܢ ܐܝܬܝ ܒܝܢܬܟܘܢ ܐܝܟ ܗܘ ܕܡܫܡܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 28, 'ܐܢܬܘܢ ܐܢܘܢ ܕܝܢ ܕܟܬܪܬܘܢ ܠܘܬܝ ܒܢܤܝܘܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 29, 'ܘܐܢܐ ܡܫܬܘܕܐ ܐܢܐ ܠܟܘܢ ܐܝܟ ܕܐܫܬܘܕܝ ܠܝ ܐܒܝ ܡܠܟܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 30, 'ܕܬܐܟܠܘܢ ܘܬܫܬܘܢ ܥܠ ܦܬܘܪܐ ܕܡܠܟܘܬܐ ܕܝܠܝ ܘܬܬܒܘܢ ܥܠ ܟܘܪܤܘܬܐ ܘܬܕܘܢܘܢ ܬܪܥܤܪ ܫܒܛܐ ܕܐܝܤܪܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 31, 'ܘܐܡܪ ܝܫܘܥ ܠܫܡܥܘܢ ܫܡܥܘܢ ܗܐ ܤܛܢܐ ܫܐܠ ܕܢܥܪܘܒܟܘܢ ܐܝܟ ܕܠܚܛܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 32, 'ܘܐܢܐ ܒܥܝܬ ܥܠܝܟ ܕܠܐ ܬܚܤܪ ܗܝܡܢܘܬܟ ܐܦ ܐܢܬ ܒܙܒܢ ܐܬܦܢܝ ܘܫܪܪ ܐܚܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 33, 'ܫܡܥܘܢ ܕܝܢ ܐܡܪ ܠܗ ܡܪܝ ܥܡܟ ܡܛܝܒ ܐܢܐ ܘܠܒܝܬ ܐܤܝܪܐ ܘܠܡܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 34, 'ܐܡܪ ܠܗ ܝܫܘܥ ܐܡܪ ܐܢܐ ܠܟ ܫܡܥܘܢ ܕܠܐ ܢܩܪܐ ܬܪܢܓܠܐ ܝܘܡܢܐ ܥܕܡܐ ܕܬܠܬ ܙܒܢܝܢ ܬܟܦܘܪ ܕܠܐ ܝܕܥ ܐܢܬ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 35, 'ܘܐܡܪ ܠܗܘܢ ܟܕ ܫܕܪܬܟܘܢ ܕܠܐ ܟܝܤܐ ܘܕܠܐ ܬܪܡܠܐ ܘܡܤܢܐ ܠܡܐ ܚܤܪ ܠܟܘܢ ܡܕܡ ܐܡܪܝܢ ܠܗ ܘܠܐ ܡܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 36, 'ܐܡܪ ܠܗܘܢ ܡܢ ܗܫܐ ܡܢ ܕܐܝܬ ܠܗ ܟܝܤܐ ܢܤܒ ܘܗܟܢܐ ܐܦ ܬܪܡܠܐ ܘܡܢ ܕܠܝܬ ܠܗ ܤܝܦܐ ܢܙܒܢ ܢܚܬܗ ܘܢܙܒܢ ܠܗ ܤܝܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 37, 'ܐܡܪ ܐܢܐ ܠܟܘܢ ܓܝܪ ܕܐܦ ܗܕܐ ܕܟܬܝܒܐ ܘܠܐ ܕܬܬܡܠܐ ܒܝ ܕܥܡ ܥܘܠܐ ܐܬܡܢܐ ܟܠܗܝܢ ܓܝܪ ܕܥܠܝ ܐܫܬܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 38, 'ܘܗܢܘܢ ܐܡܪܝܢ ܠܗ ܡܪܢ ܗܐ ܗܪܟܐ ܐܝܬ ܬܪܝܢ ܤܝܦܝܢ ܐܡܪ ܠܗܘܢ ܤܦܩܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 39, 'ܘܢܦܩ ܘܐܙܠ ܐܝܟ ܕܡܥܕ ܗܘܐ ܠܛܘܪܐ ܕܒܝܬ ܙܝܬܐ ܘܐܙܠܘ ܒܬܪܗ ܐܦ ܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 40, 'ܘܟܕ ܡܛܝ ܠܕܘܟܬܐ ܐܡܪ ܠܗܘܢ ܨܠܘ ܕܠܐ ܬܥܠܘܢ ܠܢܤܝܘܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 41, 'ܘܗܘ ܦܪܩ ܡܢܗܘܢ ܐܝܟ ܡܫܕܐ ܟܐܦܐ ܘܤܡ ܒܘܪܟܘܗܝ ܘܡܨܠܐ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 42, 'ܘܐܡܪ ܐܒܐ ܐܢ ܨܒܐ ܐܢܬ ܢܥܒܪܢܝ ܟܤܐ ܗܢܐ ܒܪܡ ܠܐ ܨܒܝܢܝ ܐܠܐ ܕܝܠܟ ܢܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 43, 'ܘܐܬܚܙܝ ܠܗ ܡܠܐܟܐ ܡܢ ܫܡܝܐ ܕܡܚܝܠ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 44, 'ܘܟܕ ܗܘܐ ܒܕܚܠܬܐ ܬܟܝܒܐܝܬ ܡܨܠܐ ܗܘܐ ܘܗܘܬ ܕܘܥܬܗ ܐܝܟ ܫܠܬܐ ܕܕܡܐ ܘܢܦܠ ܥܠ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 45, 'ܘܩܡ ܡܢ ܨܠܘܬܗ ܘܐܬܐ ܠܘܬ ܬܠܡܝܕܘܗܝ ܘܐܫܟܚ ܐܢܘܢ ܟܕ ܕܡܟܝܢ ܡܢ ܥܩܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 46, 'ܘܐܡܪ ܠܗܘܢ ܡܢܐ ܕܡܟܝܢ ܐܢܬܘܢ ܩܘܡܘ ܨܠܘ ܕܠܐ ܬܥܠܘܢ ܠܢܤܝܘܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 47, 'ܘܥܕ ܗܘ ܡܡܠܠ ܗܐ ܟܢܫܐ ܘܗܘ ܕܡܬܩܪܐ ܝܗܘܕܐ ܚܕ ܡܢ ܬܪܥܤܪ ܐܬܐ ܩܕܡܝܗܘܢ ܘܩܪܒ ܠܘܬ ܝܫܘܥ ܘܢܫܩܗ ܗܕܐ ܓܝܪ ܐܬܐ ܝܗܒ ܗܘܐ ܠܗܘܢ ܕܐܝܢܐ ܕܢܫܩ ܐܢܐ ܗܘܝܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 48, 'ܐܡܪ ܠܗ ܝܫܘܥ ܝܗܘܕܐ ܒܢܘܫܩܬܐ ܡܫܠܡ ܐܢܬ ܠܗ ܠܒܪܗ ܕܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 49, 'ܟܕ ܚܙܘ ܕܝܢ ܐܝܠܝܢ ܕܥܡܗ ܡܕܡ ܕܗܘܐ ܐܡܪܝܢ ܠܗ ܡܪܢ ܢܡܚܐ ܐܢܘܢ ܒܤܝܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 50, 'ܘܡܚܐ ܚܕ ܡܢܗܘܢ ܠܥܒܕܗ ܕܪܒ ܟܗܢܐ ܘܢܤܒܗ ܐܕܢܗ ܕܝܡܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 51, 'ܥܢܐ ܕܝܢ ܝܫܘܥ ܘܐܡܪ ܟܕܘ ܥܕܡܐ ܠܗܕܐ ܘܩܪܒ ܠܐܕܢܗ ܕܗܘ ܕܒܠܥ ܘܐܤܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 52, 'ܘܐܡܪ ܝܫܘܥ ܠܗܢܘܢ ܕܐܬܘ ܥܠܘܗܝ ܪܒܝ ܟܗܢܐ ܘܩܫܝܫܐ ܘܪܒܝ ܚܝܠܐ ܕܗܝܟܠܐ ܐܝܟ ܕܥܠ ܠܤܛܝܐ ܢܦܩܬܘܢ ܥܠܝ ܒܤܝܦܐ ܘܒܚܘܛܪܐ ܕܬܐܚܕܘܢܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 53, 'ܟܠܝܘܡ ܥܡܟܘܢ ܗܘܝܬ ܒܗܝܟܠܐ ܘܠܐ ܐܘܫܛܬܘܢ ܥܠܝ ܐܝܕܝܐ ܐܠܐ ܗܕܐ ܗܝ ܫܥܬܟܘܢ ܘܫܘܠܛܢܐ ܕܚܫܘܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 54, 'ܘܐܚܕܘ ܐܝܬܝܘܗܝ ܠܒܝܬܗ ܕܪܒ ܟܗܢܐ ܘܫܡܥܘܢ ܐܬܐ ܗܘܐ ܒܬܪܗ ܡܢ ܪܘܚܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 55, 'ܐܘܚܕܘ ܕܝܢ ܢܘܪܐ ܡܨܥܬ ܕܪܬܐ ܘܝܬܒܝܢ ܗܘܘ ܚܕܪܝܗ ܘܝܬܒ ܗܘܐ ܐܦ ܗܘ ܫܡܥܘܢ ܒܝܢܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 56, 'ܘܚܙܬܗ ܥܠܝܡܬܐ ܚܕܐ ܕܝܬܒ ܠܘܬ ܢܘܪܐ ܘܚܪܬ ܒܗ ܘܐܡܪܐ ܐܦ ܗܢܐ ܥܡܗ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 57, 'ܗܘ ܕܝܢ ܟܦܪ ܘܐܡܪ ܐܢܬܬܐ ܠܐ ܝܕܥ ܐܢܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 58, 'ܘܒܬܪ ܩܠܝܠ ܚܙܝܗܝ ܐܚܪܢܐ ܘܐܡܪ ܠܗ ܐܦ ܐܢܬ ܡܢܗܘܢ ܐܢܬ ܟܐܦܐ ܕܝܢ ܐܡܪ ܠܐ ܗܘܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 59, 'ܘܒܬܪ ܫܥܐ ܚܕܐ ܐܚܪܢܐ ܡܬܚܪܐ ܗܘܐ ܘܐܡܪ ܫܪܝܪܐܝܬ ܐܦ ܗܢܐ ܥܡܗ ܗܘܐ ܐܦ ܓܠܝܠܝܐ ܗܘ ܓܝܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 60, 'ܐܡܪ ܟܐܦܐ ܓܒܪܐ ܠܐ ܝܕܥ ܐܢܐ ܡܢܐ ܐܡܪ ܐܢܬ ܘܡܚܕܐ ܟܕ ܗܘ ܡܡܠܠ ܩܪܐ ܬܪܢܓܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 61, 'ܘܐܬܦܢܝ ܝܫܘܥ ܘܚܪ ܒܟܐܦܐ ܘܐܬܕܟܪ ܫܡܥܘܢ ܡܠܬܗ ܕܡܪܢ ܕܐܡܪ ܗܘܐ ܠܗ ܕܩܕܡ ܕܢܩܪܐ ܬܪܢܓܠܐ ܬܟܦܘܪ ܒܝ ܬܠܬ ܙܒܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 62, 'ܘܢܦܩ ܠܒܪ ܫܡܥܘܢ ܒܟܐ ܡܪܝܪܐܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 63, 'ܘܓܒܪܐ ܕܐܚܝܕܝܢ ܗܘܘ ܠܝܫܘܥ ܡܒܙܚܝܢ ܗܘܘ ܒܗ ܘܡܚܦܝܢ ܗܘܘ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 64, 'ܘܡܚܝܢ ܗܘܘ ܠܗ ܥܠ ܐܦܘܗܝ ܘܐܡܪܝܢ ܐܬܢܒܐ ܡܢܘ ܡܚܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 65, 'ܘܐܚܪܢܝܬܐ ܤܓܝܐܬܐ ܡܓܕܦܝܢ ܗܘܘ ܘܐܡܪܝܢ ܥܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 66, 'ܘܟܕ ܢܓܗܬ ܐܬܟܢܫܘ ܩܫܝܫܐ ܘܪܒܝ ܟܗܢܐ ܘܤܦܪܐ ܘܐܤܩܘܗܝ ܠܒܝܬ ܟܢܘܫܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 67, 'ܘܐܡܪܝܢ ܠܗ ܐܢ ܐܢܬ ܗܘ ܡܫܝܚܐ ܐܡܪ ܠܢ ܐܡܪ ܠܗܘܢ ܐܢ ܐܡܪ ܠܟܘܢ ܠܐ ܬܗܝܡܢܘܢܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 68, 'ܘܐܢ ܐܫܐܠܟܘܢ ܠܐ ܡܦܢܝܢ ܐܢܬܘܢ ܠܝ ܦܬܓܡܐ ܐܘ ܫܪܝܢ ܐܢܬܘܢ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 69, 'ܡܢ ܗܫܐ ܢܗܘܐ ܒܪܗ ܕܐܢܫܐ ܝܬܒ ܡܢ ܝܡܝܢܐ ܕܚܝܠܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 70, 'ܐܡܪܝܢ ܕܝܢ ܟܠܗܘܢ ܐܢܬ ܗܘ ܗܟܝܠ ܒܪܗ ܕܐܠܗܐ ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܢܬܘܢ ܐܡܪܝܢ ܐܢܬܘܢ ܕܐܢܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 71, 'ܐܡܪܝܢ ܡܢܐ ܬܘܒ ܡܬܒܥܝܢ ܠܢ ܤܗܕܐ ܚܢܢ ܓܝܪ ܫܡܥܢ ܡܢ ܦܘܡܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 1, 'ܘܩܡܘ ܟܠܗ ܟܢܫܗܘܢ ܘܐܝܬܝܘܗܝ ܠܘܬ ܦܝܠܛܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 2, 'ܘܫܪܝܘ ܐܟܠܝܢ ܩܪܨܘܗܝ ܘܐܡܪܝܢ ܠܗܢܐ ܐܫܟܚܢ ܕܡܛܥܐ ܥܡܢ ܘܟܠܐ ܕܟܤܦ ܪܫܐ ܠܩܤܪ ܠܐ ܢܬܠ ܘܐܡܪ ܥܠ ܢܦܫܗ ܕܡܠܟܐ ܗܘ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 3, 'ܦܝܠܛܘܤ ܕܝܢ ܫܐܠܗ ܘܐܡܪ ܠܗ ܐܢܬ ܗܘ ܡܠܟܐ ܕܝܗܘܕܝܐ ܐܡܪ ܠܗ ܐܢܬ ܐܡܪܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 4, 'ܘܐܡܪ ܦܝܠܛܘܤ ܠܪܒܝ ܟܗܢܐ ܘܠܟܢܫܐ ܐܢܐ ܡܕܡ ܥܠܬܐ ܠܐ ܡܫܟܚ ܐܢܐ ܥܠ ܓܒܪܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 5, 'ܗܢܘܢ ܕܝܢ ܡܙܥܩܝܢ ܗܘܘ ܘܐܡܪܝܢ ܕܫܓܫܗ ܠܥܡܢ ܟܕ ܡܠܦ ܒܟܠܗ ܝܗܘܕ ܘܫܪܝ ܡܢ ܓܠܝܠܐ ܘܥܕܡܐ ܠܗܪܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 6, 'ܦܝܠܛܘܤ ܕܝܢ ܟܕ ܫܡܥ ܫܡܐ ܕܓܠܝܠܐ ܫܐܠ ܕܐܢ ܓܒܪܐ ܗܘ ܓܠܝܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 7, 'ܘܟܕ ܝܕܥ ܕܡܢ ܬܚܝܬ ܫܘܠܛܢܐ ܗܘ ܕܗܪܘܕܤ ܫܕܪܗ ܠܘܬܗ ܕܗܪܘܕܤ ܡܛܠ ܕܒܐܘܪܫܠܡ ܗܘܐ ܒܗܢܘܢ ܝܘܡܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 8, 'ܗܪܘܕܤ ܕܝܢ ܟܕ ܚܙܝܗܝ ܠܝܫܘܥ ܚܕܝ ܛܒ ܨܒܐ ܗܘܐ ܓܝܪ ܠܡܚܙܝܗ ܡܢ ܙܒܢܐ ܤܓܝܐܐ ܡܛܠ ܕܫܡܥ ܗܘܐ ܥܠܘܗܝ ܤܓܝܐܬܐ ܘܡܤܒܪ ܗܘܐ ܕܡܕܡ ܐܬܐ ܢܚܙܐ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 9, 'ܘܡܠܐ ܤܓܝܐܬܐ ܡܫܐܠ ܗܘܐ ܠܗ ܝܫܘܥ ܕܝܢ ܡܕܡ ܦܬܓܡܐ ܠܐ ܐܬܝܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 10, 'ܩܝܡܝܢ ܗܘܘ ܕܝܢ ܪܒܝ ܟܗܢܐ ܘܤܦܪܐ ܘܥܙܝܙܐܝܬ ܐܟܠܝܢ ܗܘܘ ܩܪܨܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 11, 'ܗܪܘܕܤ ܕܝܢ ܫܛܗ ܗܘ ܘܦܠܚܘܗܝ ܘܟܕ ܡܒܙܚ ܐܠܒܫܗ ܢܚܬܐ ܕܙܚܘܪܝܬܐ ܘܫܕܪܗ ܠܘܬ ܦܝܠܛܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 12, 'ܘܒܗܘ ܝܘܡܐ ܗܘܘ ܪܚܡܐ ܦܝܠܛܘܤ ܘܗܪܘܕܤ ܥܡ ܚܕܕܐ ܒܥܠܕܒܒܘܬܐ ܗܘܬ ܓܝܪ ܡܢ ܩܕܝܡ ܒܝܢܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 13, 'ܩܪܐ ܕܝܢ ܦܝܠܛܘܤ ܠܪܒܝ ܟܗܢܐ ܘܠܐܪܟܘܢܐ ܘܠܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 14, 'ܘܐܡܪ ܠܗܘܢ ܩܪܒܬܘܢ ܠܝ ܓܒܪܐ ܗܢܐ ܐܝܟ ܡܗܦܟ ܥܡܟܘܢ ܘܗܐ ܐܢܐ ܥܩܒܬܗ ܠܥܢܝܟܘܢ ܘܥܠܬܐ ܡܕܡ ܠܐ ܐܫܟܚܬ ܒܓܒܪܐ ܗܢܐ ܡܢ ܟܠ ܕܡܪܫܝܢ ܐܢܬܘܢ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 15, 'ܐܠܐ ܐܦܠܐ ܗܪܘܕܤ ܫܕܪܬܗ ܓܝܪ ܠܘܬܗ ܘܗܐ ܠܐ ܡܕܡ ܕܫܘܐ ܠܡܘܬܐ ܤܥܝܪ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 16, 'ܐܪܕܝܘܗܝ ܗܟܝܠ ܘܐܫܒܩܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 17, 'ܥܝܕܐ ܓܝܪ ܐܝܬ ܗܘܐ ܕܢܫܪܐ ܠܗܘܢ ܚܕ ܒܥܕܥܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 18, 'ܩܥܘ ܕܝܢ ܟܠܗ ܟܢܫܐ ܘܐܡܪܝܢ ܫܩܘܠܝܗܝ ܠܗܢܐ ܘܫܪܝ ܠܢ ܠܒܪ ܐܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 19, 'ܗܘ ܐܝܢܐ ܕܡܛܠ ܐܤܛܤܝܤ ܕܗܘܬ ܒܡܕܝܢܬܐ ܘܩܛܠܐ ܪܡܐ ܗܘܐ ܒܝܬ ܐܤܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 20, 'ܬܘܒ ܕܝܢ ܡܠܠ ܥܡܗܘܢ ܦܝܠܛܘܤ ܟܕ ܨܒܐ ܕܢܫܪܐ ܠܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 21, 'ܗܢܘܢ ܕܝܢ ܩܥܝܢ ܗܘܘ ܘܐܡܪܝܢ ܙܩܘܦܝܗܝ ܙܩܘܦܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 22, 'ܗܘ ܕܝܢ ܕܬܠܬ ܙܒܢܝܢ ܐܡܪ ܠܗܘܢ ܡܢܐ ܓܝܪ ܕܒܝܫ ܥܒܕ ܗܢܐ ܡܕܡ ܥܠܬܐ ܕܫܘܝܐ ܠܡܘܬܐ ܠܐ ܐܫܟܚܬ ܒܗ ܐܪܕܝܘܗܝ ܗܟܝܠ ܘܐܫܒܩܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 23, 'ܗܢܘܢ ܕܝܢ ܬܟܒܝܢ ܗܘܘ ܒܩܠܐ ܪܡܐ ܘܫܐܠܝܢ ܗܘܘ ܠܗ ܕܢܙܩܦܘܢܝܗܝ ܘܥܫܢ ܗܘܐ ܩܠܗܘܢ ܕܝܠܗܘܢ ܘܕܪܒܝ ܟܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 24, 'ܦܝܠܛܘܤ ܕܝܢ ܦܩܕ ܕܬܗܘܐ ܫܐܠܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 25, 'ܘܫܪܐ ܠܗܘܢ ܠܗܘ ܕܡܛܠ ܐܤܛܤܝܤ ܘܩܛܠܐ ܪܡܐ ܗܘܐ ܒܝܬ ܐܤܝܪܐ ܗܘ ܕܫܐܠܘ ܠܝܫܘܥ ܕܝܢ ܐܫܠܡ ܠܨܒܝܢܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 26, 'ܘܟܕ ܡܘܒܠܝܢ ܠܗ ܐܚܕܘ ܠܫܡܥܘܢ ܩܘܪܝܢܝܐ ܕܐܬܐ ܡܢ ܩܪܝܬܐ ܘܤܡܘ ܥܠܘܗܝ ܙܩܝܦܐ ܕܢܛܥܢ ܒܬܪܗ ܕܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 27, 'ܘܐܬܐ ܗܘܐ ܒܬܪܗ ܤܘܓܐܐ ܕܥܡܐ ܘܢܫܐ ܐܝܠܝܢ ܕܡܪܩܕܢ ܗܘܝ ܘܐܠܝܢ ܥܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 28, 'ܘܐܬܦܢܝ ܝܫܘܥ ܠܘܬܗܝܢ ܘܐܡܪ ܒܢܬ ܐܘܪܫܠܡ ܠܐ ܬܒܟܝܢ ܥܠܝ ܒܪܡ ܥܠ ܢܦܫܟܝܢ ܒܟܝܝܢ ܘܥܠ ܒܢܝܟܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 29, 'ܕܗܐ ܐܬܝܢ ܝܘܡܬܐ ܕܒܗܘܢ ܢܐܡܪܘܢ ܛܘܒܝܗܝܢ ܠܥܩܪܬܐ ܘܠܟܪܤܬܐ ܕܠܐ ܝܠܕ ܘܠܬܕܝܐ ܕܠܐ ܐܝܢܩܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 30, 'ܗܝܕܝܢ ܬܫܪܘܢ ܠܡܐܡܪ ܠܛܘܪܐ ܕܦܠܘ ܥܠܝܢ ܘܠܪܡܬܐ ܕܟܤܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 31, 'ܕܐܢ ܒܩܝܤܐ ܪܛܝܒܐ ܗܠܝܢ ܥܒܕܝܢ ܒܝܒܝܫܐ ܡܢܐ ܢܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 32, 'ܘܐܬܝܢ ܗܘܘ ܥܡܗ ܬܪܝܢ ܐܚܪܢܝܢ ܥܒܕܝ ܒܝܫܬܐ ܕܢܬܩܛܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 33, 'ܘܟܕ ܐܬܘ ܠܕܘܟܬܐ ܚܕܐ ܕܡܬܩܪܝܐ ܩܪܩܦܬܐ ܙܩܦܘܗܝ ܬܡܢ ܘܠܗܢܘܢ ܥܒܕܝ ܒܝܫܬܐ ܚܕ ܡܢ ܝܡܝܢܗ ܘܚܕ ܡܢ ܤܡܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 34, 'ܗܘ ܕܝܢ ܝܫܘܥ ܐܡܪ ܗܘܐ ܐܒܐ ܫܒܘܩ ܠܗܘܢ ܠܐ ܓܝܪ ܝܕܥܝܢ ܡܢܐ ܥܒܕܝܢ ܘܦܠܓܘ ܢܚܬܘܗܝ ܘܐܪܡܝܘ ܥܠܝܗܘܢ ܦܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 35, 'ܩܐܡ ܗܘܐ ܕܝܢ ܥܡܐ ܘܚܙܐ ܘܡܡܝܩܝܢ ܗܘܘ ܒܗ ܐܦ ܐܪܟܘܢܐ ܘܐܡܪܝܢ ܠܐܚܪܢܐ ܐܚܝ ܢܚܐ ܢܦܫܗ ܐܢ ܗܘܝܘ ܡܫܝܚܐ ܓܒܝܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 36, 'ܘܡܒܙܚܝܢ ܗܘܘ ܒܗ ܐܦ ܐܤܛܪܛܝܘܛܐ ܟܕ ܩܪܒܝܢ ܠܘܬܗ ܘܡܩܪܒܝܢ ܠܗ ܚܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 37, 'ܘܐܡܪܝܢ ܠܗ ܐܢ ܐܢܬ ܗܘ ܡܠܟܐ ܕܝܗܘܕܝܐ ܐܚܐ ܢܦܫܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 38, 'ܐܝܬ ܗܘܐ ܕܝܢ ܐܦ ܟܬܒܐ ܕܟܬܝܒ ܠܥܠ ܡܢܗ ܝܘܢܐܝܬ ܘܪܗܘܡܐܝܬ ܘܥܒܪܐܝܬ ܗܢܘ ܡܠܟܐ ܕܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 39, 'ܚܕ ܕܝܢ ܡܢ ܗܢܘܢ ܥܒܕܝ ܒܝܫܬܐ ܕܨܠܝܒܝܢ ܗܘܘ ܥܡܗ ܡܓܕܦ ܗܘܐ ܥܠܘܗܝ ܘܐܡܪ ܐܢ ܐܢܬ ܗܘ ܡܫܝܚܐ ܦܨܐ ܢܦܫܟ ܘܦܨܐ ܐܦ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 40, 'ܘܟܐܐ ܒܗ ܚܒܪܗ ܘܐܡܪ ܠܗ ܐܦ ܠܐ ܡܢ ܐܠܗܐ ܕܚܠ ܐܢܬ ܕܐܦ ܐܢܬ ܒܗ ܐܢܬ ܒܕܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 41, 'ܘܚܢܢ ܟܐܢܐܝܬ ܐܝܟ ܕܫܘܝܢ ܗܘܝܢ ܓܝܪ ܘܐܝܟ ܕܥܒܕܢ ܐܬܦܪܥܢ ܗܢܐ ܕܝܢ ܡܕܡ ܕܤܢܐ ܠܐ ܥܒܝܕ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 42, 'ܘܐܡܪ ܠܝܫܘܥ ܐܬܕܟܪܝܢܝ ܡܪܝ ܡܐ ܕܐܬܐ ܐܢܬ ܒܡܠܟܘܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 43, 'ܐܡܪ ܠܗ ܝܫܘܥ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟ ܕܝܘܡܢܐ ܥܡܝ ܬܗܘܐ ܒܦܪܕܝܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 44, 'ܐܝܬ ܗܘܝ ܕܝܢ ܐܝܟ ܫܥܐ ܫܬ ܘܗܘܐ ܚܫܘܟܐ ܥܠ ܟܠܗ ܐܪܥܐ ܥܕܡܐ ܠܬܫܥ ܫܥܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 45, 'ܘܫܡܫܐ ܚܫܟ ܘܐܨܛܪܝ ܐܦܝ ܬܪܥܐ ܕܗܝܟܠܐ ܡܢ ܡܨܥܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 46, 'ܘܩܥܐ ܝܫܘܥ ܒܩܠܐ ܪܡܐ ܘܐܡܪ ܐܒܝ ܒܐܝܕܝܟ ܤܐܡ ܐܢܐ ܪܘܚܝ ܗܕܐ ܐܡܪ ܘܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 47, 'ܟܕ ܚܙܐ ܕܝܢ ܩܢܛܪܘܢܐ ܡܕܡ ܕܗܘܐ ܫܒܚ ܠܐܠܗܐ ܘܐܡܪ ܫܪܝܪܐܝܬ ܗܢܐ ܓܒܪܐ ܙܕܝܩܐ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 48, 'ܘܟܠܗܘܢ ܟܢܫܐ ܐܝܠܝܢ ܕܟܢܝܫܝܢ ܗܘܘ ܠܚܙܬܐ ܗܕܐ ܟܕ ܚܙܘ ܡܕܡ ܕܗܘܐ ܗܦܟܘ ܟܕ ܛܪܦܝܢ ܥܠ ܚܕܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 49, 'ܘܩܝܡܝܢ ܗܘܘ ܡܢ ܪܘܚܩܐ ܟܠܗܘܢ ܝܕܘܥܘܗܝ ܕܝܫܘܥ ܘܢܫܐ ܐܝܠܝܢ ܕܐܬܝ ܗܘܝ ܥܡܗ ܡܢ ܓܠܝܠܐ ܘܚܙܝܢ ܗܘܘ ܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 50, 'ܓܒܪܐ ܕܝܢ ܚܕ ܕܫܡܗ ܝܘܤܦ ܒܘܠܘܛܐ ܡܢ ܪܡܬܐ ܡܕܝܢܬܐ ܕܝܗܘܕ ܓܒܪܐ ܗܘܐ ܛܒܐ ܘܙܕܝܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 51, 'ܗܢܐ ܠܐ ܫܠܡ ܗܘܐ ܠܨܒܝܢܗܘܢ ܘܠܤܘܥܪܢܗܘܢ ܘܡܤܟܐ ܗܘܐ ܠܡܠܟܘܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 52, 'ܗܢܐ ܩܪܒ ܠܘܬ ܦܝܠܛܘܤ ܘܫܐܠ ܦܓܪܗ ܕܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 53, 'ܘܐܚܬܗ ܘܟܪܟܗ ܒܚܝܨܐ ܕܟܬܢܐ ܘܤܡܗ ܒܒܝܬ ܩܒܘܪܐ ܢܩܝܪܐ ܗܘ ܕܠܐ ܐܢܫ ܥܕܟܝܠ ܐܬܬܤܝܡ ܗܘܐ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 54, 'ܘܝܘܡܐ ܥܪܘܒܬܐ ܗܘܬ ܘܫܒܬܐ ܢܓܗܐ ܗܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 55, 'ܩܪܝܒܢ ܗܘܝ ܕܝܢ ܢܫܐ ܗܠܝܢ ܕܐܬܝ ܥܡܗ ܡܢ ܓܠܝܠܐ ܘܚܙܝܝܗܝ ܠܩܒܪܐ ܘܐܝܟܢܐ ܐܬܬܤܝܡ ܦܓܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 56, 'ܘܗܦܟ ܛܝܒ ܗܪܘܡܐ ܘܒܤܡܐ ܘܒܫܒܬܐ ܫܠܝ ܐܝܟ ܕܦܩܝܕ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 1, 'ܒܚܕ ܒܫܒܐ ܕܝܢ ܒܫܦܪܐ ܥܕ ܚܫܘܟ ܐܬܝ ܠܒܝܬ ܩܒܘܪܐ ܘܐܝܬܝ ܗܪܘܡܐ ܗܠܝܢ ܕܛܝܒ ܗܘܝ ܘܐܝܬ ܗܘܝ ܥܡܗܝܢ ܢܫܐ ܐܚܪܢܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 2, 'ܘܐܫܟܚ ܟܐܦܐ ܕܡܥܓܠܐ ܡܢ ܒܝܬ ܩܒܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 3, 'ܘܥܠܝܢ ܘܠܐ ܐܫܟܚܝܗܝ ܠܦܓܪܐ ܕܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 4, 'ܘܗܘܐ ܕܟܕ ܗܢܝܢ ܬܡܝܗܢ ܥܠ ܗܕܐ ܗܐ ܬܪܝܢ ܓܒܪܝܢ ܩܡܘ ܠܥܠ ܡܢܗܝܢ ܘܡܒܪܩ ܗܘܐ ܠܒܘܫܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 5, 'ܘܗܘܝ ܒܕܚܠܬܐ ܘܟܦܝ ܐܦܝܗܝܢ ܒܐܪܥܐ ܘܐܡܪܝܢ ܠܗܝܢ ܡܢܐ ܒܥܝܢ ܐܢܬܝܢ ܚܝܐ ܥܡ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 6, 'ܠܝܬܘܗܝ ܬܢܢ ܩܡ ܠܗ ܥܗܕܝܢ ܕܡܠܠ ܥܡܟܝܢ ܟܕ ܗܘ ܒܓܠܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 7, 'ܘܐܡܪ ܗܘܐ ܕܥܬܝܕ ܗܘ ܒܪܗ ܕܐܢܫܐ ܕܢܫܬܠܡ ܒܐܝܕܝ ܐܢܫܐ ܚܛܝܐ ܘܢܨܛܠܒ ܘܠܬܠܬܐ ܝܘܡܝܢ ܢܩܘܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 8, 'ܘܗܢܝܢ ܐܬܕܟܪܝܢ ܠܡܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 9, 'ܘܗܦܟ ܡܢ ܩܒܪܐ ܘܐܡܪܝܢ ܗܠܝܢ ܟܠܗܝܢ ܠܚܕܥܤܪ ܘܠܫܪܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 10, 'ܐܝܬܝܗܝܢ ܗܘܝ ܕܝܢ ܡܪܝܡ ܡܓܕܠܝܬܐ ܘܝܘܚܢ ܘܡܪܝܡ ܐܡܗ ܕܝܥܩܘܒ ܘܫܪܟܐ ܕܥܡܗܝܢ ܗܠܝܢ ܕܐܡܪ ܗܘܝ ܠܫܠܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 11, 'ܘܐܬܚܙܝ ܒܥܝܢܝܗܘܢ ܡܠܐ ܗܠܝܢ ܐܝܟ ܫܢܝܬܐ ܘܠܐ ܗܝܡܢܘ ܐܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 12, 'ܫܡܥܘܢ ܕܝܢ ܩܡ ܘܪܗܛ ܠܩܒܪܐ ܘܐܕܝܩ ܚܙܐ ܟܬܢܐ ܕܤܝܡܝܢ ܒܠܚܘܕ ܘܐܙܠ ܟܕ ܡܬܕܡܪ ܒܢܦܫܗ ܥܠ ܡܕܡ ܕܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 13, 'ܘܗܐ ܬܪܝܢ ܡܢܗܘܢ ܒܗ ܒܝܘܡܐ ܐܙܠܝܢ ܗܘܘ ܠܩܪܝܬܐ ܕܫܡܗ ܥܡܐܘܤ ܘܦܪܝܩܐ ܡܢ ܐܘܪܫܠܡ ܐܤܛܕܘܬܐ ܫܬܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 14, 'ܘܗܢܘܢ ܡܡܠܠܝܢ ܗܘܘ ܚܕ ܥܡ ܚܕ ܥܠ ܗܠܝܢ ܟܠܗܝܢ ܕܓܕܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 15, 'ܘܟܕ ܗܢܘܢ ܡܡܠܠܝܢ ܘܒܥܝܢ ܚܕ ܥܡ ܚܕ ܐܬܐ ܗܘ ܝܫܘܥ ܘܡܛܝ ܐܢܘܢ ܘܡܗܠܟ ܗܘܐ ܥܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 16, 'ܘܥܝܢܝܗܘܢ ܐܚܝܕܢ ܗܘܝ ܕܠܐ ܢܤܬܟܠܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 17, 'ܘܐܡܪ ܠܗܘܢ ܡܢܐ ܐܢܝܢ ܡܠܐ ܗܠܝܢ ܕܡܡܠܠܝܢ ܐܢܬܘܢ ܚܕ ܥܡ ܚܕ ܟܕ ܡܗܠܟܝܢ ܐܢܬܘܢ ܘܟܡܝܪܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 18, 'ܥܢܐ ܚܕ ܡܢܗܘܢ ܕܫܡܗ ܩܠܝܘܦܐ ܘܐܡܪ ܠܗ ܐܢܬ ܗܘ ܟܝ ܒܠܚܘܕܝܟ ܢܘܟܪܝܐ ܡܢ ܐܘܪܫܠܡ ܕܠܐ ܝܕܥ ܐܢܬ ܡܕܡ ܕܗܘܐ ܒܗ ܒܗܢܘܢ ܝܘܡܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 19, 'ܐܡܪ ܠܗܘܢ ܡܢܐ ܐܡܪܝܢ ܠܗ ܥܠ ܝܫܘܥ ܗܘ ܕܡܢ ܢܨܪܬ ܓܒܪܐ ܕܗܘܐ ܢܒܝܐ ܘܚܝܠܬܢ ܗܘܐ ܒܡܠܬܐ ܘܒܥܒܕܐ ܩܕܡ ܐܠܗܐ ܘܩܕܡ ܟܘܠܗ ܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 20, 'ܘܐܫܠܡܘܗܝ ܪܒܝ ܟܗܢܐ ܘܩܫܝܫܐ ܠܕܝܢܐ ܕܡܘܬܐ ܘܙܩܦܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 21, 'ܚܢܢ ܕܝܢ ܤܒܪܝܢ ܗܘܝܢ ܕܗܘܝܘ ܥܬܝܕ ܗܘܐ ܕܢܦܪܩܝܘܗܝ ܠܐܝܤܪܝܠ ܘܗܐ ܬܠܬܐ ܝܘܡܝܢ ܗܐ ܡܢ ܕܗܠܝܢ ܟܠܗܝܢ ܗܘܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 22, 'ܐܠܐ ܐܦ ܢܫܐ ܡܢܢ ܐܬܡܗܢ ܩܕܡ ܗܘܝ ܓܝܪ ܠܒܝܬ ܩܒܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 23, 'ܘܟܕ ܠܐ ܐܫܟܚ ܦܓܪܗ ܐܬܝ ܐܡܪܢ ܠܢ ܕܡܠܐܟܐ ܚܙܝܢ ܬܡܢ ܘܐܡܪܝܢ ܥܠܘܗܝ ܕܚܝ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 24, 'ܘܐܦ ܐܢܫܐ ܡܢܢ ܐܙܠܘ ܠܒܝܬ ܩܒܘܪܐ ܘܐܫܟܚܘ ܗܟܢܐ ܐܝܟ ܡܐ ܕܐܡܪ ܢܫܐ ܠܗ ܕܝܢ ܠܐ ܚܙܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 25, 'ܗܝܕܝܢ ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܘ ܚܤܝܪܝ ܪܥܝܢܐ ܘܝܩܝܪܝ ܠܒܐ ܠܡܗܝܡܢܘ ܒܟܠܗܝܢ ܐܝܠܝܢ ܕܡܠܠܘ ܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 26, 'ܠܐ ܗܘܐ ܗܠܝܢ ܥܬܝܕ ܗܘܐ ܕܢܤܝܒܪ ܡܫܝܚܐ ܘܕܢܥܘܠ ܠܬܫܒܘܚܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 27, 'ܘܫܪܝ ܗܘܐ ܡܢ ܡܘܫܐ ܘܡܢ ܟܠܗܘܢ ܢܒܝܐ ܘܡܦܫܩ ܗܘܐ ܠܗܘܢ ܥܠ ܢܦܫܗ ܡܢ ܟܠܗܘܢ ܟܬܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 28, 'ܘܩܪܒܘ ܗܘܘ ܠܩܪܝܬܐ ܗܝ ܕܐܙܠܝܢ ܗܘܘ ܠܗ ܘܗܘ ܡܤܒܪ ܗܘܐ ܠܗܘܢ ܕܐܝܟ ܕܠܕܘܟܐ ܪܚܝܩܐ ܐܙܠ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 29, 'ܘܐܠܨܘܗܝ ܘܐܡܪܝܢ ܠܗ ܦܘܫ ܠܘܬܢ ܡܛܠ ܕܝܘܡܐ ܗܫܐ ܪܟܢ ܠܗ ܠܡܚܫܟ ܘܥܠ ܕܢܩܘܐ ܠܘܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 30, 'ܘܗܘܐ ܕܟܕ ܐܤܬܡܟ ܥܡܗܘܢ ܢܤܒ ܠܚܡܐ ܘܒܪܟ ܘܩܨܐ ܘܝܗܒ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 31, 'ܘܡܚܕܐ ܐܬܦܬܚ ܥܝܢܝܗܘܢ ܘܐܫܬܘܕܥܘܗܝ ܘܗܘ ܐܫܬܩܠ ܠܗ ܡܢܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 32, 'ܘܐܡܪܝܢ ܗܘܘ ܚܕ ܠܚܕ ܠܐ ܗܘܐ ܠܒܢ ܝܩܝܪ ܗܘܐ ܒܓܘܢ ܟܕ ܡܡܠܠ ܥܡܢ ܒܐܘܪܚܐ ܘܡܦܫܩ ܠܢ ܟܬܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 33, 'ܘܩܡܘ ܒܗ ܒܫܥܬܐ ܘܗܦܟܘ ܠܐܘܪܫܠܡ ܘܐܫܟܚܘ ܠܚܕܥܤܪ ܕܟܢܝܫܝܢ ܘܠܐܝܠܝܢ ܕܥܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 34, 'ܟܕ ܐܡܪܝܢ ܕܫܪܝܪܐܝܬ ܩܡ ܡܪܢ ܘܐܬܚܙܝ ܠܫܡܥܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 35, 'ܘܐܦ ܗܢܘܢ ܐܫܬܥܝܘ ܐܝܠܝܢ ܕܗܘܝ ܒܐܘܪܚܐ ܘܐܝܟܢܐ ܐܬܝܕܥ ܠܗܘܢ ܟܕ ܩܨܐ ܠܚܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 36, 'ܘܟܕ ܗܠܝܢ ܡܡܠܠܝܢ ܗܘܘ ܝܫܘܥ ܩܡ ܒܝܢܬܗܘܢ ܘܐܡܪ ܠܗܘܢ ܫܠܡܐ ܥܡܟܘܢ ܐܢܐ ܐܢܐ ܠܐ ܬܕܚܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 37, 'ܘܗܢܘܢ ܐܬܪܗܒܘ ܘܗܘܘ ܒܕܚܠܬܐ ܤܒܪܝܢ ܗܘܘ ܓܝܪ ܕܪܘܚܐ ܚܙܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 38, 'ܘܐܡܪ ܠܗܘܢ ܝܫܘܥ ܡܢܐ ܡܬܙܝܥܝܢ ܐܢܬܘܢ ܘܡܢܐ ܤܠܩܢ ܡܚܫܒܬܐ ܥܠ ܠܒܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 39, 'ܚܙܘ ܐܝܕܝ ܘܪܓܠܝ ܕܐܢܐ ܐܢܐ ܓܘܫܘܢܢܝ ܘܕܥܘ ܕܠܪܘܚܐ ܒܤܪܐ ܘܓܪܡܐ ܠܝܬ ܠܗ ܐܝܟ ܕܚܙܝܢ ܐܢܬܘܢ ܕܐܝܬ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 40, 'ܘܟܕ ܗܠܝܢ ܐܡܪ ܚܘܝ ܐܢܘܢ ܐܝܕܘܗܝ ܘܪܓܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 41, 'ܘܟܕ ܥܕܡܐ ܠܗܫܐ ܠܐ ܡܗܝܡܢܝܢ ܗܘܘ ܡܢ ܚܕܘܬܗܘܢ ܘܡܬܬܡܗܝܢ ܗܘܘ ܐܡܪ ܠܗܘܢ ܐܝܬ ܠܟܘܢ ܬܢܢ ܡܕܡ ܠܡܐܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 42, 'ܗܢܘܢ ܕܝܢ ܝܗܒܘ ܠܗ ܡܢܬܐ ܡܢ ܢܘܢܐ ܕܛܘܝܐ ܘܡܢ ܟܟܪܝܬܐ ܕܕܒܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 43, 'ܘܢܤܒ ܐܟܠ ܠܥܢܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 44, 'ܘܐܡܪ ܠܗܘܢ ܗܠܝܢ ܐܢܝܢ ܡܠܐ ܕܡܠܠܬ ܥܡܟܘܢ ܟܕ ܠܘܬܟܘܢ ܗܘܝܬ ܕܘܠܐ ܗܘ ܕܢܫܬܠܡ ܟܠ ܡܕܡ ܕܟܬܝܒ ܒܢܡܘܤܐ ܕܡܘܫܐ ܘܒܢܒܝܐ ܘܒܡܙܡܘܪܐ ܥܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 45, 'ܗܝܕܝܢ ܦܬܚ ܪܥܝܢܗܘܢ ܠܡܤܬܟܠܘ ܟܬܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 46, 'ܘܐܡܪ ܠܗܘܢ ܕܗܟܢܐ ܟܬܝܒ ܘܗܟܢܐ ܙܕܩ ܗܘܐ ܕܢܚܫ ܡܫܝܚܐ ܘܕܢܩܘܡ ܡܢ ܒܝܬ ܡܝܬܐ ܠܬܠܬܐ ܝܘܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 47, 'ܘܕܢܬܟܪܙ ܒܫܡܗ ܬܝܒܘܬܐ ܠܫܘܒܩܢܐ ܕܚܛܗܐ ܒܟܠܗܘܢ ܥܡܡܐ ܘܫܘܪܝܐ ܢܗܘܐ ܡܢ ܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 48, 'ܘܐܢܬܘܢ ܐܢܘܢ ܤܗܕܐ ܕܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 49, 'ܘܐܢܐ ܐܫܕܪ ܥܠܝܟܘܢ ܡܘܠܟܢܐ ܕܐܒܝ ܐܢܬܘܢ ܕܝܢ ܩܘܘ ܒܐܘܪܫܠܡ ܡܕܝܢܬܐ ܥܕܡܐ ܕܬܠܒܫܘܢ ܚܝܠܐ ܡܢ ܪܘܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 50, 'ܘܐܦܩ ܐܢܘܢ ܥܕܡܐ ܠܒܝܬ ܥܢܝܐ ܘܐܪܝܡ ܐܝܕܘܗܝ ܘܒܪܟ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 51, 'ܘܗܘܐ ܕܟܕ ܡܒܪܟ ܠܗܘܢ ܐܬܦܪܫ ܡܢܗܘܢ ܘܤܠܩ ܠܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 52, 'ܗܢܘܢ ܕܝܢ ܤܓܕܘ ܠܗ ܘܗܦܟܘ ܠܐܘܪܫܠܡ ܒܚܕܘܬܐ ܪܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 53, 'ܘܒܟܠܙܒܢ ܐܝܬܝܗܘܢ ܗܘܘ ܒܗܝܟܠܐ ܟܕ ܡܫܒܚܝܢ ܘܡܒܪܟܝܢ ܠܐܠܗܐ ܐܡܝܢ ܀ ܀ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 1, 'ܒܪܫܝܬ ܐܝܬܘܗܝ ܗܘܐ ܡܠܬܐ ܘܗܘ ܡܠܬܐ ܐܝܬܘܗܝ ܗܘܐ ܠܘܬ ܐܠܗܐ ܘܐܠܗܐ ܐܝܬܘܗܝ ܗܘܐ ܗܘ ܡܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 2, 'ܗܢܐ ܐܝܬܘܗܝ ܗܘܐ ܒܪܫܝܬ ܠܘܬ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 3, 'ܟܠ ܒܐܝܕܗ ܗܘܐ ܘܒܠܥܕܘܗܝ ܐܦܠܐ ܚܕܐ ܗܘܬ ܡܕܡ ܕܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 4, 'ܒܗ ܚܝܐ ܗܘܐ ܘܚܝܐ ܐܝܬܝܗܘܢ ܢܘܗܪܐ ܕܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 5, 'ܘܗܘ ܢܘܗܪܐ ܒܚܫܘܟܐ ܡܢܗܪ ܘܚܫܘܟܐ ܠܐ ܐܕܪܟܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 6, 'ܗܘܐ ܒܪܢܫܐ ܕܐܫܬܕܪ ܡܢ ܐܠܗܐ ܫܡܗ ܝܘܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 7, 'ܗܢܐ ܐܬܐ ܠܤܗܕܘܬܐ ܕܢܤܗܕ ܥܠ ܢܘܗܪܐ ܕܟܠܢܫ ܢܗܝܡܢ ܒܐܝܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 8, 'ܠܐ ܗܘ ܗܘܐ ܢܘܗܪܐ ܐܠܐ ܕܢܤܗܕ ܥܠ ܢܘܗܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 9, 'ܐܝܬܘܗܝ ܗܘܐ ܓܝܪ ܢܘܗܪܐ ܕܫܪܪܐ ܕܡܢܗܪ ܠܟܠܢܫ ܕܐܬܐ ܠܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 10, 'ܒܥܠܡܐ ܗܘܐ ܘܥܠܡܐ ܒܐܝܕܗ ܗܘܐ ܘܥܠܡܐ ܠܐ ܝܕܥܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 11, 'ܠܕܝܠܗ ܐܬܐ ܘܕܝܠܗ ܠܐ ܩܒܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 12, 'ܐܝܠܝܢ ܕܝܢ ܕܩܒܠܘܗܝ ܝܗܒ ܠܗܘܢ ܫܘܠܛܢܐ ܕܒܢܝܐ ܕܐܠܗܐ ܢܗܘܘܢ ܠܐܝܠܝܢ ܕܡܗܝܡܢܝܢ ܒܫܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 13, 'ܐܝܠܝܢ ܕܠܘ ܡܢ ܕܡܐ ܘܠܐ ܡܢ ܨܒܝܢܐ ܕܒܤܪܐ ܘܠܐ ܡܢ ܨܒܝܢܐ ܕܓܒܪܐ ܐܠܐ ܡܢ ܐܠܗܐ ܐܬܝܠܕܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 14, 'ܘܡܠܬܐ ܒܤܪܐ ܗܘܐ ܘܐܓܢ ܒܢ ܘܚܙܝܢ ܫܘܒܚܗ ܫܘܒܚܐ ܐܝܟ ܕܝܚܝܕܝܐ ܕܡܢ ܐܒܐ ܕܡܠܐ ܛܝܒܘܬܐ ܘܩܘܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 15, 'ܝܘܚܢܢ ܤܗܕ ܥܠܘܗܝ ܘܩܥܐ ܘܐܡܪ ܗܢܘ ܗܘ ܕܐܡܪܬ ܕܒܬܪܝ ܐܬܐ ܘܗܘܐ ܠܗ ܩܕܡܝ ܡܛܠ ܕܩܕܡܝ ܗܘ ܡܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 16, 'ܘܡܢ ܡܠܝܘܬܗ ܚܢܢ ܟܠܢ ܢܤܒܢ ܘܛܝܒܘܬܐ ܚܠܦ ܛܝܒܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 17, 'ܡܛܠ ܕܢܡܘܤܐ ܒܝܕ ܡܘܫܐ ܐܬܝܗܒ ܫܪܪܐ ܕܝܢ ܘܛܝܒܘܬܐ ܒܝܕ ܝܫܘܥ ܡܫܝܚܐ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 18, 'ܐܠܗܐ ܠܐ ܚܙܐ ܐܢܫ ܡܡܬܘܡ ܝܚܝܕܝܐ ܐܠܗܐ ܗܘ ܕܐܝܬܘܗܝ ܒܥܘܒܐ ܕܐܒܘܗܝ ܗܘ ܐܫܬܥܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 19, 'ܘܗܕܐ ܗܝ ܤܗܕܘܬܗ ܕܝܘܚܢܢ ܟܕ ܫܕܪܘ ܠܘܬܗ ܝܗܘܕܝܐ ܡܢ ܐܘܪܫܠܡ ܟܗܢܐ ܘܠܘܝܐ ܕܢܫܐܠܘܢܝܗܝ ܐܢܬ ܡܢ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 20, 'ܘܐܘܕܝ ܘܠܐ ܟܦܪ ܘܐܘܕܝ ܕܠܘ ܐܢܐ ܐܢܐ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 21, 'ܘܫܐܠܘܗܝ ܬܘܒ ܡܢܐ ܗܟܝܠ ܐܠܝܐ ܐܢܬ ܘܐܡܪ ܠܐ ܐܝܬܝ ܢܒܝܐ ܐܢܬ ܘܐܡܪ ܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 22, 'ܘܐܡܪܘ ܠܗ ܘܡܢܘ ܐܢܬ ܕܢܬܠ ܦܬܓܡܐ ܠܐܝܠܝܢ ܕܫܕܪܘܢ ܡܢܐ ܐܡܪ ܐܢܬ ܥܠ ܢܦܫܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 23, 'ܐܡܪ ܐܢܐ ܩܠܐ ܕܩܪܐ ܒܡܕܒܪܐ ܕܐܫܘܘ ܐܘܪܚܗ ܕܡܪܝܐ ܐܝܟܢܐ ܕܐܡܪ ܐܫܥܝܐ ܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 24, 'ܗܢܘܢ ܕܝܢ ܕܐܫܬܕܪܘ ܡܢ ܦܪܝܫܐ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 25, 'ܘܫܐܠܘܗܝ ܘܐܡܪܘ ܠܗ ܡܢܐ ܗܟܝܠ ܡܥܡܕ ܐܢܬ ܐܢ ܐܢܬ ܠܐ ܐܝܬܝܟ ܡܫܝܚܐ ܘܠܐ ܐܠܝܐ ܘܠܐ ܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 26, 'ܥܢܐ ܝܘܚܢܢ ܘܐܡܪ ܠܗܘܢ ܐܢܐ ܡܥܡܕ ܐܢܐ ܒܡܝܐ ܒܝܢܬܟܘܢ ܕܝܢ ܩܐܡ ܗܘ ܕܐܢܬܘܢ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 27, 'ܗܢܘ ܗܘ ܕܒܬܪܝ ܐܬܐ ܘܗܘܐ ܠܗ ܩܕܡܝ ܗܘ ܕܐܢܐ ܠܐ ܫܘܐ ܐܢܐ ܕܐܫܪܐ ܥܪܩܐ ܕܡܤܢܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 28, 'ܗܠܝܢ ܒܒܝܬ ܥܢܝܐ ܗܘܝ ܒܥܒܪܐ ܕܝܘܪܕܢܢ ܐܝܟܐ ܕܡܥܡܕ ܗܘܐ ܝܘܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 29, 'ܘܠܝܘܡܐ ܕܒܬܪܗ ܚܙܐ ܝܘܚܢܢ ܠܝܫܘܥ ܕܐܬܐ ܠܘܬܗ ܘܐܡܪ ܗܐ ܐܡܪܗ ܕܐܠܗܐ ܗܘ ܕܫܩܠ ܚܛܝܬܗ ܕܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 30, 'ܗܢܘ ܕܐܢܐ ܐܡܪܬ ܥܠܘܗܝ ܕܒܬܪܝ ܐܬܐ ܓܒܪܐ ܘܗܘܐ ܠܗ ܩܕܡܝ ܡܛܠ ܕܩܕܡܝ ܗܘ ܡܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 31, 'ܘܐܢܐ ܠܐ ܝܕܥ ܗܘܝܬ ܠܗ ܐܠܐ ܕܢܬܝܕܥ ܠܐܝܤܪܝܠ ܡܛܠ ܗܢܐ ܐܬܝܬ ܐܢܐ ܕܒܡܝܐ ܐܥܡܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 32, 'ܘܐܤܗܕ ܝܘܚܢܢ ܘܐܡܪ ܕܚܙܝܬ ܠܪܘܚܐ ܕܢܚܬܐ ܡܢ ܫܡܝܐ ܐܝܟ ܝܘܢܐ ܘܩܘܝܬ ܥܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 33, 'ܘܐܢܐ ܠܐ ܝܕܥ ܗܘܝܬ ܠܗ ܐܠܐ ܡܢ ܕܫܕܪܢܝ ܕܐܥܡܕ ܒܡܝܐ ܗܘ ܐܡܪ ܠܝ ܕܐܝܢܐ ܕܚܙܐ ܐܢܬ ܕܢܚܬܐ ܪܘܚܐ ܘܡܩܘܝܐ ܥܠܘܗܝ ܗܢܘ ܡܥܡܕ ܒܪܘܚܐ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 34, 'ܘܐܢܐ ܚܙܝܬ ܘܐܤܗܕܬ ܕܗܢܘ ܒܪܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 35, 'ܘܠܝܘܡܐ ܐܚܪܢܐ ܩܐܡ ܗܘܐ ܝܘܚܢܢ ܘܬܪܝܢ ܡܢ ܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 36, 'ܘܚܪ ܒܝܫܘܥ ܟܕ ܡܗܠܟ ܘܐܡܪ ܗܐ ܐܡܪܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 37, 'ܘܫܡܥܘ ܬܪܝܗܘܢ ܬܠܡܝܕܘܗܝ ܟܕ ܐܡܪ ܘܐܙܠܘ ܠܗܘܢ ܒܬܪܗ ܕܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 38, 'ܘܐܬܦܢܝ ܝܫܘܥ ܘܚܙܐ ܐܢܘܢ ܕܐܬܝܢ ܒܬܪܗ ܘܐܡܪ ܠܗܘܢ ܡܢܐ ܒܥܝܢ ܐܢܬܘܢ ܐܡܪܝܢ ܠܗ ܪܒܢ ܐܝܟܐ ܗܘܐ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 39, 'ܐܡܪ ܠܗܘܢ ܬܘ ܘܬܚܙܘܢ ܗܢܘܢ ܕܝܢ ܐܬܘ ܘܚܙܘ ܐܝܟܐ ܕܗܘܐ ܘܠܘܬܗ ܗܘܘ ܝܘܡܐ ܗܘ ܘܐܝܬ ܗܘܝ ܐܝܟ ܫܥܐ ܥܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 40, 'ܚܕ ܕܝܢ ܡܢ ܗܢܘܢ ܕܫܡܥܘ ܡܢ ܝܘܚܢܢ ܘܐܙܠܘ ܒܬܪܗ ܕܝܫܘܥ ܐܝܬܘܗܝ ܗܘܐ ܐܢܕܪܐܘܤ ܐܚܘܗܝ ܕܫܡܥܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 41, 'ܗܢܐ ܚܙܐ ܠܘܩܕܡ ܠܫܡܥܘܢ ܐܚܘܗܝ ܘܐܡܪ ܠܗ ܐܫܟܚܢܝܗܝ ܠܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 42, 'ܘܐܝܬܝܗ ܠܘܬ ܝܫܘܥ ܘܚܪ ܒܗ ܝܫܘܥ ܘܐܡܪ ܐܢܬ ܗܘ ܫܡܥܘܢ ܒܪܗ ܕܝܘܢܐ ܐܢܬ ܬܬܩܪܐ ܟܐܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 43, 'ܘܠܝܘܡܐ ܐܚܪܢܐ ܨܒܐ ܝܫܘܥ ܠܡܦܩ ܠܓܠܝܠܐ ܘܐܫܟܚ ܠܦܝܠܝܦܘܤ ܘܐܡܪ ܠܗ ܬܐ ܒܬܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 44, 'ܗܘ ܕܝܢ ܦܝܠܝܦܘܤ ܐܝܬܘܗܝ ܗܘܐ ܡܢ ܒܝܬ ܨܝܕܐ ܡܢ ܡܕܝܢܬܗ ܕܐܢܕܪܐܘܤ ܘܕܫܡܥܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 45, 'ܘܦܝܠܝܦܘܤ ܐܫܟܚ ܠܢܬܢܝܐܝܠ ܘܐܡܪ ܠܗ ܗܘ ܕܟܬܒ ܥܠܘܗܝ ܡܘܫܐ ܒܢܡܘܤܐ ܘܒܢܒܝܐ ܐܫܟܚܢܝܗܝ ܕܝܫܘܥ ܗܘ ܒܪ ܝܘܤܦ ܡܢ ܢܨܪܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 46, 'ܐܡܪ ܠܗ ܢܬܢܝܐܝܠ ܡܢ ܢܨܪܬ ܡܫܟܚ ܡܕܡ ܕܛܒ ܢܗܘܐ ܐܡܪ ܠܗ ܦܝܠܝܦܘܤ ܬܐ ܘܬܚܙܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 47, 'ܘܚܙܝܗܝ ܝܫܘܥ ܠܢܬܢܝܐܝܠ ܟܕ ܐܬܐ ܠܘܬܗ ܘܐܡܪ ܥܠܘܗܝ ܗܐ ܫܪܝܪܐܝܬ ܒܪ ܐܝܤܪܝܠ ܕܢܟܠܐ ܠܝܬ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 48, 'ܐܡܪ ܠܗ ܢܬܢܝܐܝܠ ܡܢ ܐܝܡܟܐ ܝܕܥ ܐܢܬ ܠܝ ܐܡܪ ܠܗ ܝܫܘܥ ܥܕܠܐ ܢܩܪܝܟ ܦܝܠܝܦܘܤ ܟܕ ܬܚܝܬ ܬܬܐ ܐܢܬ ܚܙܝܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 49, 'ܥܢܐ ܢܬܢܝܐܝܠ ܘܐܡܪ ܠܗ ܪܒܝ ܐܢܬ ܗܘ ܒܪܗ ܕܐܠܗܐ ܐܢܬ ܗܘ ܡܠܟܗ ܕܐܝܤܪܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 50, 'ܐܡܪ ܠܗ ܝܫܘܥ ܥܠ ܕܐܡܪܬ ܠܟ ܕܚܙܝܬܟ ܬܚܝܬ ܬܬܐ ܡܗܝܡܢ ܐܢܬ ܕܪܘܪܒܢ ܡܢ ܗܠܝܢ ܬܚܙܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 51, 'ܐܡܪ ܠܗ ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܡܢ ܗܫܐ ܬܚܙܘܢ ܫܡܝܐ ܕܦܬܝܚܝܢ ܘܡܠܐܟܘܗܝ ܕܐܠܗܐ ܟܕ ܤܠܩܝܢ ܘܢܚܬܝܢ ܠܘܬ ܒܪܗ ܕܐܢܫܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 1, 'ܘܠܝܘܡܐ ܕܬܠܬܐ ܗܘܬ ܡܫܬܘܬܐ ܒܩܛܢܐ ܡܕܝܢܬܐ ܕܓܠܝܠܐ ܘܐܡܗ ܕܝܫܘܥ ܬܡܢ ܗܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 2, 'ܘܐܦ ܗܘ ܝܫܘܥ ܘܬܠܡܝܕܘܗܝ ܐܬܩܪܝܘ ܠܗ ܠܡܫܬܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 3, 'ܘܚܤܪ ܗܘܐ ܚܡܪܐ ܘܐܡܪܐ ܠܗ ܐܡܗ ܠܝܫܘܥ ܚܡܪܐ ܠܝܬ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 4, 'ܐܡܪ ܠܗ ܝܫܘܥ ܡܐ ܠܝ ܘܠܟܝ ܐܢܬܬܐ ܠܐ ܥܕܟܝܠ ܐܬܬ ܫܥܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 5, 'ܐܡܪܐ ܐܡܗ ܠܡܫܡܫܢܐ ܡܕܡ ܕܐܡܪ ܠܟܘܢ ܥܒܕܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 6, 'ܐܝܬ ܗܘܝ ܕܝܢ ܬܡܢ ܐܓܢܐ ܕܟܐܦܐ ܫܬ ܕܤܝܡܢ ܠܬܕܟܝܬܐ ܕܝܗܘܕܝܐ ܕܐܚܕܢ ܬܪܝܢ ܬܪܝܢ ܪܒܥܝܢ ܐܘ ܬܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 7, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܡܠܘ ܐܢܝܢ ܡܝܐ ܠܐܓܢܐ ܘܡܠܘ ܐܢܝܢ ܥܕܡܐ ܠܥܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 8, 'ܐܡܪ ܠܗܘܢ ܙܠܘܥܘ ܡܟܝܠ ܘܐܝܬܘ ܠܪܝܫ ܤܡܟܐ ܘܐܝܬܝܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 9, 'ܘܟܕ ܛܥܡ ܗܘ ܪܝܫ ܤܡܟܐ ܡܝܐ ܗܢܘܢ ܕܗܘܘ ܚܡܪܐ ܘܠܐ ܝܕܥ ܗܘܐ ܡܢ ܐܝܡܟܐ ܗܘܐ ܡܫܡܫܢܐ ܕܝܢ ܝܕܥܝܢ ܗܘܘ ܕܗܢܘܢ ܡܠܘ ܐܢܘܢ ܠܡܝܐ ܩܪܐ ܪܝܫ ܤܡܟܐ ܠܚܬܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 10, 'ܘܐܡܪ ܠܗ ܟܠ ܐܢܫ ܠܘܩܕܡ ܚܡܪܐ ܛܒܐ ܡܝܬܐ ܘܡܐ ܕܪܘܝܘ ܗܝܕܝܢ ܐܝܢܐ ܕܒܨܝܪ ܐܢܬ ܕܝܢ ܢܛܪܬܝܗܝ ܠܚܡܪܐ ܛܒܐ ܥܕܡܐ ܠܗܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 11, 'ܗܕܐ ܗܝ ܐܬܐ ܩܕܡܝܬܐ ܕܥܒܕ ܝܫܘܥ ܒܩܛܢܐ ܕܓܠܝܠܐ ܘܐܘܕܥ ܫܘܒܚܗ ܘܗܝܡܢܘ ܒܗ ܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 12, 'ܒܬܪ ܗܕܐ ܢܚܬ ܠܟܦܪܢܚܘܡ ܗܘ ܘܐܡܗ ܘܐܚܘܗܝ ܘܬܠܡܝܕܘܗܝ ܘܬܡܢ ܗܘܘ ܩܠܝܠ ܝܘܡܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 13, 'ܘܩܪܝܒ ܗܘܐ ܦܨܚܐ ܕܝܗܘܕܝܐ ܘܤܠܩ ܠܐܘܪܫܠܡ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 14, 'ܘܐܫܟܚ ܒܗܝܟܠܐ ܠܗܠܝܢ ܕܡܙܒܢܝܢ ܬܘܪܐ ܘܥܪܒܐ ܘܝܘܢܐ ܘܠܡܥܪܦܢܐ ܕܝܬܒܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 15, 'ܘܥܒܕ ܠܗ ܦܪܓܠܐ ܡܢ ܚܒܠܐ ܘܠܟܠܗܘܢ ܐܦܩ ܡܢ ܗܝܟܠܐ ܘܠܥܪܒܐ ܘܠܬܘܪܐ ܘܠܡܥܪܦܢܐ ܘܐܫܕ ܥܘܪܦܢܗܘܢ ܘܦܬܘܪܝܗܘܢ ܗܦܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 16, 'ܘܠܗܢܘܢ ܕܡܙܒܢܝܢ ܝܘܢܐ ܐܡܪ ܫܩܘܠܘ ܗܠܝܢ ܡܟܐ ܘܠܐ ܬܥܒܕܘܢܗ ܠܒܝܬܗ ܕܐܒܝ ܒܝܬ ܬܐܓܘܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 17, 'ܘܐܬܕܟܪܘ ܬܠܡܝܕܘܗܝ ܕܟܬܝܒ ܕܛܢܢܗ ܕܒܝܬܟ ܐܟܠܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 18, 'ܥܢܘ ܕܝܢ ܝܗܘܕܝܐ ܘܐܡܪܘ ܠܗ ܡܢܐ ܐܬܐ ܡܚܘܐ ܐܢܬ ܠܢ ܕܗܠܝܢ ܥܒܕ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 19, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܤܬܘܪܘ ܗܝܟܠܐ ܗܢܐ ܘܠܬܠܬܐ ܝܘܡܝܢ ܐܢܐ ܡܩܝܡ ܐܢܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 20, 'ܐܡܪܝܢ ܠܗ ܝܗܘܕܝܐ ܠܐܪܒܥܝܢ ܘܫܬ ܫܢܝܢ ܐܬܒܢܝ ܗܝܟܠܐ ܗܢܐ ܘܐܢܬ ܠܬܠܬܐ ܝܘܡܝܢ ܡܩܝܡ ܐܢܬ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 21, 'ܗܘ ܕܝܢ ܐܡܪ ܗܘܐ ܥܠ ܗܝܟܠܐ ܕܦܓܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 22, 'ܟܕ ܩܡ ܕܝܢ ܡܢ ܒܝܬ ܡܝܬܐ ܐܬܕܟܪܘ ܬܠܡܝܕܘܗܝ ܕܗܕܐ ܐܡܪ ܗܘܐ ܘܗܝܡܢܘ ܠܟܬܒܐ ܘܠܡܠܬܐ ܕܐܡܪ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 23, 'ܟܕ ܐܝܬܘܗܝ ܗܘܐ ܕܝܢ ܝܫܘܥ ܒܐܘܪܫܠܡ ܒܦܨܚܐ ܒܥܕܥܕܐ ܤܓܝܐܐ ܗܝܡܢܘ ܒܗ ܕܚܙܘ ܐܬܘܬܐ ܕܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 24, 'ܗܘ ܕܝܢ ܝܫܘܥ ܠܐ ܡܗܝܡܢ ܗܘܐ ܠܗܘܢ ܢܦܫܗ ܡܛܠ ܕܗܘ ܝܕܥ ܗܘܐ ܠܟܠܢܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 25, 'ܘܠܐ ܤܢܝܩ ܗܘܐ ܕܐܢܫ ܢܤܗܕ ܠܗ ܥܠ ܟܠ ܒܪܢܫ ܗܘ ܓܝܪ ܝܕܥ ܗܘܐ ܡܢܐ ܐܝܬ ܒܒܪܢܫܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 1, 'ܐܝܬ ܗܘܐ ܕܝܢ ܬܡܢ ܚܕ ܓܒܪܐ ܡܢ ܦܪܝܫܐ ܢܝܩܕܡܘܤ ܫܡܗ ܗܘܐ ܐܪܟܘܢܐ ܕܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 2, 'ܗܢܐ ܐܬܐ ܠܘܬ ܝܫܘܥ ܒܠܠܝܐ ܘܐܡܪ ܠܗ ܪܒܝ ܝܕܥܝܢܢ ܕܡܢ ܐܠܗܐ ܐܫܬܕܪܬ ܡܠܦܢܐ ܠܐ ܓܝܪ ܐܢܫ ܡܫܟܚ ܗܠܝܢ ܐܬܘܬܐ ܠܡܥܒܕ ܕܐܢܬ ܥܒܕ ܐܢܬ ܐܠܐ ܡܢ ܕܐܠܗܐ ܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 3, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗ ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟ ܕܐܢ ܐܢܫ ܠܐ ܡܬܝܠܕ ܡܢ ܕܪܝܫ ܠܐ ܡܫܟܚ ܕܢܚܙܐ ܡܠܟܘܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 4, 'ܐܡܪ ܠܗ ܢܝܩܕܡܘܤ ܐܝܟܢܐ ܡܫܟܚ ܕܢܬܝܠܕ ܓܒܪܐ ܤܒܐ ܕܠܡܐ ܡܫܟܚ ܬܘܒ ܠܟܪܤܐ ܕܐܡܗ ܕܬܪܬܝܢ ܙܒܢܝܢ ܠܡܥܠ ܘܢܬܝܠܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 5, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗ ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟ ܕܐܢ ܐܢܫ ܠܐ ܡܬܝܠܕ ܡܢ ܡܝܐ ܘܪܘܚܐ ܠܐ ܡܫܟܚ ܕܢܥܘܠ ܠܡܠܟܘܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 6, 'ܡܕܡ ܕܝܠܝܕ ܡܢ ܒܤܪܐ ܒܤܪܐ ܗܘ ܘܡܕܡ ܕܝܠܝܕ ܡܢ ܪܘܚܐ ܪܘܚܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 7, 'ܠܐ ܬܬܕܡܪ ܕܐܡܪܬ ܠܟ ܕܘܠܐ ܠܟܘܢ ܠܡܬܝܠܕܘ ܡܢ ܕܪܝܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 8, 'ܪܘܚܐ ܐܬܪ ܕܨܒܝܐ ܢܫܒܐ ܘܩܠܗ ܫܡܥ ܐܢܬ ܐܠܐ ܠܐ ܝܕܥ ܐܢܬ ܐܝܡܟܐ ܐܬܝܐ ܘܠܐܝܟܐ ܐܙܠܐ ܗܟܢܐ ܐܝܬܘܗܝ ܟܠܢܫ ܕܝܠܝܕ ܡܢ ܪܘܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 9, 'ܥܢܐ ܢܝܩܕܡܘܤ ܘܐܡܪ ܠܗ ܐܝܟܢܐ ܡܫܟܚܢ ܗܠܝܢ ܠܡܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 10, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗ ܐܢܬ ܗܘ ܡܠܦܢܗ ܕܐܝܤܪܝܠ ܘܗܠܝܢ ܠܐ ܝܕܥ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 11, 'ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟ ܕܡܕܡ ܕܝܕܥܝܢ ܚܢܢ ܡܡܠܠܝܢ ܚܢܢ ܘܡܕܡ ܕܚܙܝܢ ܡܤܗܕܝܢ ܐܢܚܢܢ ܘܤܗܕܘܬܢ ܠܐ ܡܩܒܠܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 12, 'ܐܢ ܕܒܐܪܥܐ ܐܡܪܬ ܠܟܘܢ ܘܠܐ ܡܗܝܡܢܝܢ ܐܢܬܘܢ ܐܝܟܢܐ ܐܢ ܐܡܪ ܠܟܘܢ ܕܒܫܡܝܐ ܬܗܝܡܢܘܢܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 13, 'ܘܠܐ ܐܢܫ ܤܠܩ ܠܫܡܝܐ ܐܠܐ ܗܘ ܕܢܚܬ ܡܢ ܫܡܝܐ ܒܪܗ ܕܐܢܫܐ ܗܘ ܕܐܝܬܘܗܝ ܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 14, 'ܘܐܝܟܢܐ ܕܐܪܝܡ ܡܘܫܐ ܚܘܝܐ ܒܡܕܒܪܐ ܗܟܢܐ ܥܬܝܕ ܠܡܬܬܪܡܘ ܒܪܗ ܕܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 15, 'ܕܟܠ ܐܢܫ ܕܡܗܝܡܢ ܒܗ ܠܐ ܢܐܒܕ ܐܠܐ ܢܗܘܘܢ ܠܗ ܚܝܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 16, 'ܗܟܢܐ ܓܝܪ ܐܚܒ ܐܠܗܐ ܠܥܠܡܐ ܐܝܟܢܐ ܕܠܒܪܗ ܝܚܝܕܝܐ ܢܬܠ ܕܟܠ ܡܢ ܕܡܗܝܡܢ ܒܗ ܠܐ ܢܐܒܕ ܐܠܐ ܢܗܘܘܢ ܠܗ ܚܝܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 17, 'ܠܐ ܓܝܪ ܫܕܪ ܐܠܗܐ ܠܒܪܗ ܠܥܠܡܐ ܕܢܕܘܢܝܘܗܝ ܠܥܠܡܐ ܐܠܐ ܕܢܚܐ ܠܥܠܡܐ ܒܐܝܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 18, 'ܡܢ ܕܡܗܝܡܢ ܒܗ ܠܐ ܡܬܬܕܝܢ ܘܡܢ ܕܠܐ ܡܗܝܡܢ ܡܢ ܟܕܘ ܕܝܢ ܗܘ ܕܠܐ ܗܝܡܢ ܒܫܡܗ ܕܝܚܝܕܝܐ ܒܪܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 19, 'ܗܢܘ ܕܝܢ ܕܝܢܐ ܕܢܘܗܪܐ ܐܬܐ ܠܥܠܡܐ ܘܐܚܒܘ ܒܢܝܢܫܐ ܠܚܫܘܟܐ ܝܬܝܪ ܡܢ ܕܠܢܘܗܪܐ ܐܝܬܝܗܘܢ ܗܘܘ ܓܝܪ ܥܒܕܝܗܘܢ ܒܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 20, 'ܟܠ ܓܝܪ ܕܤܢܝܬܐ ܥܒܕ ܤܢܐ ܠܢܘܗܪܐ ܘܠܐ ܐܬܐ ܠܢܘܗܪܐ ܕܠܐ ܢܬܟܤܤܘܢ ܥܒܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 21, 'ܗܘ ܕܝܢ ܕܥܒܕ ܫܪܪܐ ܐܬܐ ܠܘܬ ܢܘܗܪܐ ܕܢܬܝܕܥܘܢ ܥܒܕܘܗܝ ܕܒܐܠܗܐ ܥܒܝܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 22, 'ܒܬܪ ܗܠܝܢ ܐܬܐ ܝܫܘܥ ܘܬܠܡܝܕܘܗܝ ܠܐܪܥܐ ܕܝܗܘܕ ܘܬܡܢ ܡܬܗܦܟ ܗܘܐ ܥܡܗܘܢ ܘܡܥܡܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 23, 'ܐܦ ܝܘܚܢܢ ܕܝܢ ܡܥܡܕ ܗܘܐ ܒܥܝܢ ܝܘܢ ܕܥܠ ܓܢܒ ܫܠܝܡ ܡܛܠ ܕܡܝܐ ܐܝܬ ܗܘܘ ܬܡܢ ܤܓܝܐܐ ܘܐܬܝܢ ܗܘܘ ܘܥܡܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 24, 'ܠܐ ܓܝܪ ܥܕܟܝܠ ܢܦܠ ܗܘܐ ܝܘܚܢܢ ܒܝܬ ܐܤܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 25, 'ܗܘܬ ܗܘܬ ܕܝܢ ܒܥܬܐ ܠܚܕ ܡܢ ܬܠܡܝܕܘܗܝ ܕܝܘܚܢܢ ܥܡ ܝܗܘܕܝܐ ܚܕ ܥܠ ܬܕܟܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 26, 'ܘܐܬܘ ܠܘܬ ܝܘܚܢܢ ܘܐܡܪܘ ܠܗ ܪܒܢ ܗܘ ܕܥܡܟ ܗܘܐ ܒܥܒܪܐ ܕܝܘܪܕܢܢ ܕܐܢܬ ܤܗܕܬ ܥܠܘܗܝ ܗܐ ܐܦ ܗܘ ܡܥܡܕ ܘܤܓܝܐܐ ܐܬܝܢ ܠܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 27, 'ܥܢܐ ܝܘܚܢܢ ܘܐܡܪ ܠܗܘܢ ܠܐ ܡܫܟܚ ܒܪܢܫܐ ܠܡܤܒ ܡܢ ܨܒܘܬ ܢܦܫܗ ܡܕܡ ܐܠܐ ܐܢ ܐܬܝܗܒ ܠܗ ܡܢ ܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 28, 'ܐܢܬܘܢ ܤܗܕܝܢ ܐܢܬܘܢ ܠܝ ܕܐܡܪܬ ܕܐܢܐ ܠܐ ܗܘܝܬ ܡܫܝܚܐ ܐܠܐ ܫܠܝܚܐ ܐܢܐ ܕܩܕܡܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 29, 'ܡܢ ܕܐܝܬ ܠܗ ܟܠܬܐ ܚܬܢܐ ܗܘ ܪܚܡܗ ܕܝܢ ܕܚܬܢܐ ܗܘ ܕܩܐܡ ܘܨܐܬ ܠܗ ܚܕܘܬܐ ܪܒܬܐ ܚܕܐ ܡܛܠ ܩܠܗ ܕܚܬܢܐ ܗܕܐ ܗܟܝܠ ܚܕܘܬܐ ܕܝܠܝ ܗܐ ܡܡܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 30, 'ܠܗܘ ܗܘ ܘܠܐ ܠܡܪܒܐ ܘܠܝ ܠܡܒܨܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 31, 'ܗܘ ܓܝܪ ܕܡܢ ܠܥܠ ܐܬܐ ܠܥܠ ܡܢ ܟܠ ܗܘ ܘܗܘ ܕܡܢ ܐܪܥܐ ܡܢ ܐܪܥܐ ܗܘ ܘܡܢ ܐܪܥܐ ܡܡܠܠ ܗܘ ܕܡܢ ܫܡܝܐ ܐܬܐ ܠܥܠ ܡܢ ܟܠ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 32, 'ܘܡܕܡ ܕܚܙܐ ܘܫܡܥ ܡܤܗܕ ܘܤܗܕܘܬܗ ܠܐ ܐܢܫ ܡܩܒܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 33, 'ܗܘ ܕܝܢ ܕܩܒܠ ܤܗܕܘܬܗ ܚܬܡ ܕܐܠܗܐ ܫܪܝܪܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 34, 'ܐܝܢܐ ܓܝܪ ܕܐܠܗܐ ܫܕܪܗ ܡܠܐ ܗܘ ܕܐܠܗܐ ܡܡܠܠ ܠܐ ܗܘܐ ܓܝܪ ܒܟܝܠܐ ܝܗܒ ܐܠܗܐ ܪܘܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 35, 'ܐܒܐ ܡܚܒ ܠܒܪܐ ܘܟܠ ܡܕܡ ܝܗܒ ܒܐܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 36, 'ܡܢ ܕܡܗܝܡܢ ܒܒܪܐ ܐܝܬ ܠܗ ܚܝܐ ܕܠܥܠܡ ܘܡܢ ܕܠܐ ܡܬܛܦܝܤ ܠܒܪܐ ܠܐ ܢܚܙܐ ܚܝܐ ܐܠܐ ܪܘܓܙܗ ܕܐܠܗܐ ܢܩܘܐ ܥܠܘܗܝ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 1, 'ܝܕܥ ܕܝܢ ܝܫܘܥ ܕܫܡܥܘ ܦܪܝܫܐ ܕܬܠܡܝܕܐ ܤܓܝܐܐ ܥܒܕ ܘܡܥܡܕ ܝܬܝܪ ܡܢ ܝܘܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 2, 'ܟܕ ܠܐ ܗܘܐ ܗܘ ܝܫܘܥ ܡܥܡܕ ܗܘܐ ܐܠܐ ܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 3, 'ܘܫܒܩܗ ܠܝܗܘܕ ܘܐܙܠ ܠܗ ܬܘܒ ܠܓܠܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 4, 'ܡܥܒܕܐ ܗܘܬ ܠܗ ܕܝܢ ܕܢܐܬܐ ܢܥܒܪ ܥܠ ܒܝܬ ܫܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 5, 'ܘܐܬܐ ܠܡܕܝܢܬܐ ܕܫܡܪܝܐ ܕܡܬܩܪܝܐ ܫܟܪ ܥܠ ܓܢܒ ܩܪܝܬܐ ܕܝܗܒ ܗܘܐ ܝܥܩܘܒ ܠܝܘܤܦ ܒܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 6, 'ܘܐܝܬ ܗܘܐ ܬܡܢ ܡܥܝܢܐ ܕܡܝܐ ܕܝܥܩܘܒ ܗܘ ܕܝܢ ܝܫܘܥ ܠܐܐ ܗܘܐ ܡܢ ܥܡܠܐ ܕܐܘܪܚܐ ܘܝܬܒ ܗܘܐ ܠܗ ܥܠ ܡܥܝܢܐ ܘܐܝܬ ܗܘܝ ܫܬ ܫܥܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 7, 'ܘܐܬܬ ܐܢܬܬܐ ܡܢ ܫܡܪܝܢ ܕܬܡܠܐ ܡܝܐ ܘܐܡܪ ܠܗ ܝܫܘܥ ܗܒ ܠܝ ܡܝܐ ܐܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 8, 'ܬܠܡܝܕܘܗܝ ܓܝܪ ܥܠܘ ܗܘܘ ܠܡܕܝܢܬܐ ܕܢܙܒܢܘܢ ܠܗܘܢ ܤܝܒܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 9, 'ܐܡܪܐ ܠܗ ܗܝ ܐܢܬܬܐ ܫܡܪܝܬܐ ܐܝܟܢܐ ܐܢܬ ܝܗܘܕܝܐ ܐܢܬ ܘܡܢܝ ܫܐܠ ܐܢܬ ܠܡܫܬܐ ܕܐܝܬܝ ܐܢܬܬܐ ܫܡܪܝܬܐ ܠܐ ܓܝܪ ܡܬܚܫܚܝܢ ܝܗܘܕܝܐ ܥܡ ܫܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 10, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗ ܐܠܘ ܝܕܥܐ ܗܘܝܬܝ ܡܘܗܒܬܐ ܕܐܠܗܐ ܘܡܢܘ ܗܢܐ ܕܐܡܪ ܠܟܝ ܗܒ ܠܝ ܐܫܬܐ ܐܢܬܝ ܫܐܠܐ ܗܘܝܬܝ ܠܗ ܘܝܗܒ ܗܘܐ ܠܟܝ ܡܝܐ ܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 11, 'ܐܡܪܐ ܠܗ ܐܢܬܬܐ ܗܝ ܡܪܝ ܠܐ ܕܘܠܐ ܐܝܬ ܠܟ ܘܒܪܐ ܥܡܝܩܐ ܐܝܡܟܐ ܠܟ ܡܝܐ ܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 12, 'ܠܡܐ ܐܢܬ ܪܒ ܐܢܬ ܡܢ ܐܒܘܢ ܝܥܩܘܒ ܗܘ ܕܗܘ ܝܗܒ ܠܢ ܒܪܐ ܗܕܐ ܘܗܘ ܡܢܗ ܐܫܬܝ ܘܒܢܘܗܝ ܘܥܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 13, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗ ܟܠ ܕܢܫܬܐ ܡܢ ܗܠܝܢ ܡܝܐ ܬܘܒ ܢܨܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 14, 'ܟܠ ܕܝܢ ܕܢܫܬܐ ܡܢ ܡܝܐ ܕܐܢܐ ܐܬܠ ܠܗ ܠܐ ܢܨܗܐ ܠܥܠܡ ܐܠܐ ܡܝܐ ܗܢܘܢ ܕܝܗܒ ܐܢܐ ܠܗ ܢܗܘܘܢ ܒܗ ܡܥܝܢܐ ܕܡܝܐ ܕܢܒܥܝܢ ܠܚܝܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 15, 'ܐܡܪܐ ܠܗ ܐܢܬܬܐ ܗܝ ܡܪܝ ܗܒ ܠܝ ܡܢ ܗܠܝܢ ܡܝܐ ܕܠܐ ܬܘܒ ܐܨܗܐ ܘܠܐ ܗܘܝܬ ܐܬܝܐ ܕܠܝܐ ܡܢ ܗܪܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 16, 'ܐܡܪ ܠܗ ܝܫܘܥ ܙܠܝ ܩܪܝ ܠܒܥܠܟܝ ܘܬܝ ܠܗܪܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 17, 'ܐܡܪܐ ܠܗ ܠܝܬ ܠܝ ܒܥܠܐ ܐܡܪ ܠܗ ܝܫܘܥ ܫܦܝܪ ܐܡܪܬܝ ܕܠܝܬ ܠܝ ܒܥܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 18, 'ܚܡܫܐ ܓܝܪ ܒܥܠܝܢ ܗܘܘ ܠܟܝ ܘܗܢܐ ܕܐܝܬ ܠܟܝ ܗܫܐ ܠܐ ܗܘܐ ܒܥܠܟܝ ܗܕܐ ܫܪܝܪܬܐ ܐܡܪܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 19, 'ܐܡܪܐ ܠܗ ܗܝ ܐܢܬܬܐ ܡܪܝ ܚܙܝܐ ܐܢܐ ܕܢܒܝܐ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 20, 'ܐܒܗܝܢ ܒܗܢܐ ܛܘܪܐ ܤܓܕܘ ܘܐܢܬܘܢ ܐܡܪܝܢ ܐܢܬܘܢ ܕܒܐܘܪܫܠܡ ܗܘ ܐܬܪ ܕܘܠܐ ܠܡܤܓܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 21, 'ܐܡܪ ܠܗ ܝܫܘܥ ܐܢܬܬܐ ܗܝܡܢܝܢܝ ܕܐܬܝܐ ܫܥܬܐ ܕܠܐ ܒܗܢܐ ܛܘܪܐ ܘܐܦ ܠܐ ܒܐܘܪܫܠܡ ܬܤܓܕܘܢ ܠܐܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 22, 'ܐܢܬܘܢ ܤܓܕܝܢ ܐܢܬܘܢ ܠܡܕܡ ܕܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܚܢܢ ܕܝܢ ܤܓܕܝܢ ܚܢܢ ܠܡܐ ܕܝܕܥܝܢ ܚܢܢ ܕܚܝܐ ܡܢ ܝܗܘܕܝܐ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 23, 'ܐܠܐ ܐܬܝܐ ܫܥܬܐ ܘܗܫܐ ܐܝܬܝܗ ܐܡܬܝ ܕܤܓܘܕܐ ܫܪܝܪܐ ܢܤܓܕܘܢ ܠܐܒܐ ܒܪܘܚܐ ܘܒܫܪܪܐ ܐܦ ܐܒܐ ܓܝܪ ܕܐܝܟ ܗܠܝܢ ܗܘ ܤܓܘܕܐ ܒܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 24, 'ܪܘܚܐ ܗܘ ܓܝܪ ܐܠܗܐ ܘܐܝܠܝܢ ܕܤܓܕܝܢ ܠܗ ܒܪܘܚܐ ܘܒܫܪܪܐ ܘܠܐ ܕܢܤܓܕܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 25, 'ܐܡܪܐ ܠܗ ܐܢܬܬܐ ܗܝ ܝܕܥܐ ܐܢܐ ܕܡܫܝܚܐ ܐܬܐ ܘܡܐ ܕܐܬܐ ܗܘ ܡܠܦ ܠܢ ܟܠܡܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 26, 'ܐܡܪ ܠܗ ܝܫܘܥ ܐܢܐ ܐܢܐ ܕܡܡܠܠ ܐܢܐ ܥܡܟܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 27, 'ܘܟܕ ܡܡܠܠ ܐܬܘ ܬܠܡܝܕܘܗܝ ܘܡܬܕܡܪܝܢ ܗܘܘ ܕܥܡ ܐܢܬܬܐ ܡܡܠܠ ܠܐ ܕܝܢ ܐܢܫ ܐܡܪ ܡܢܐ ܒܥܐ ܐܢܬ ܐܘ ܡܢܐ ܡܡܠܠ ܐܢܬ ܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 28, 'ܘܫܒܩܬ ܩܘܠܬܗ ܐܢܬܬܐ ܘܐܙܠܬ ܠܡܕܝܢܬܐ ܘܐܡܪܐ ܠܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 29, 'ܬܘ ܚܙܘ ܓܒܪܐ ܕܐܡܪ ܠܝ ܟܠ ܡܕܡ ܕܥܒܕܬ ܠܡܐ ܗܘܝܘ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 30, 'ܘܢܦܩܘ ܐܢܫܐ ܡܢ ܡܕܝܢܬܐ ܘܐܬܝܢ ܗܘܘ ܠܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 31, 'ܘܒܝܢܬ ܗܠܝܢ ܒܥܝܢ ܗܘܘ ܡܢܗ ܬܠܡܝܕܘܗܝ ܘܐܡܪܝܢ ܠܗ ܪܒܢ ܠܥܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 32, 'ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܐܝܬ ܠܝ ܡܐܟܘܠܬܐ ܕܐܟܘܠ ܐܝܕܐ ܕܐܢܬܘܢ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 33, 'ܐܡܪܝܢ ܬܠܡܝܕܐ ܒܝܢܝܗܘܢ ܠܡܐ ܐܢܫ ܐܝܬܝ ܠܗ ܡܕܡ ܠܡܐܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 34, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܡܐܟܘܠܬܝ ܕܝܠܝ ܐܝܬܝܗ ܕܐܥܒܕ ܨܒܝܢܗ ܕܡܢ ܕܫܕܪܢܝ ܘܐܫܠܡܝܘܗܝ ܠܥܒܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 35, 'ܠܐ ܐܢܬܘܢ ܐܡܪܝܢ ܕܒܬܪ ܐܪܒܥܐ ܝܪܚܝܢ ܐܬܐ ܚܨܕܐ ܗܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܐܪܝܡܘ ܥܝܢܝܟܘܢ ܘܚܙܘ ܐܪܥܬܐ ܕܚܘܪ ܘܡܛܝ ܠܚܨܕܐ ܡܢ ܟܕܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 36, 'ܘܐܝܢܐ ܕܚܨܕ ܐܓܪܐ ܢܤܒ ܘܟܢܫ ܦܐܪܐ ܠܚܝܐ ܕܠܥܠܡ ܘܙܪܘܥܐ ܘܚܨܘܕܐ ܐܟܚܕܐ ܢܚܕܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 37, 'ܒܗܕܐ ܓܝܪ ܐܝܬܝܗ ܡܠܬܐ ܕܫܪܪܐ ܕܐܚܪܝܢ ܗܘ ܙܪܥ ܘܐܚܪܝܢ ܚܨܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 38, 'ܐܢܐ ܫܕܪܬܟܘܢ ܠܡܚܨܕ ܡܕܡ ܕܠܐ ܗܘܐ ܐܢܬܘܢ ܠܐܝܬܘܢ ܒܗ ܐܚܪܢܐ ܓܝܪ ܠܐܝܘ ܘܐܢܬܘܢ ܥܠܬܘܢ ܥܠ ܥܡܠܗܘܢ ܕܗܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 39, 'ܡܢ ܗܝ ܡܕܝܢܬܐ ܕܝܢ ܤܓܝܐܐ ܗܝܡܢܘ ܒܗ ܫܡܪܝܐ ܡܛܠ ܡܠܬܗ ܕܐܢܬܬܐ ܗܝ ܕܡܤܗܕܐ ܗܘܬ ܕܐܡܪ ܠܝ ܟܠܡܕܡ ܕܥܒܕܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 40, 'ܘܟܕ ܐܬܘ ܠܘܬܗ ܗܢܘܢ ܫܡܪܝܐ ܒܥܘ ܡܢܗ ܕܢܗܘܐ ܠܘܬܗܘܢ ܘܗܘܐ ܠܘܬܗܘܢ ܬܪܝܢ ܝܘܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 41, 'ܘܤܓܝܐܐ ܗܝܡܢܘ ܒܗ ܡܛܠ ܡܠܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 42, 'ܘܐܡܪܝܢ ܗܘܘ ܠܐܢܬܬܐ ܗܝ ܕܡܟܝܠ ܠܐ ܗܘܐ ܡܛܠ ܡܠܬܟܝ ܡܗܝܡܢܝܢ ܚܢܢ ܒܗ ܚܢܢ ܓܝܪ ܫܡܥܢ ܘܝܕܥܢ ܕܗܢܘ ܫܪܝܪܐܝܬ ܡܫܝܚܐ ܡܚܝܢܗ ܕܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 43, 'ܘܒܬܪ ܬܪܝܢ ܝܘܡܝܢ ܢܦܩ ܝܫܘܥ ܡܢ ܬܡܢ ܘܐܙܠ ܠܓܠܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 44, 'ܗܘ ܓܝܪ ܝܫܘܥ ܤܗܕ ܕܢܒܝܐ ܒܡܕܝܢܬܗ ܠܐ ܡܬܝܩܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 45, 'ܟܕ ܕܝܢ ܐܬܐ ܠܓܠܝܠܐ ܩܒܠܘܗܝ ܓܠܝܠܝܐ ܕܚܙܘ ܐܬܘܬܐ ܟܠ ܕܥܒܕ ܒܐܘܪܫܠܡ ܒܥܕܥܕܐ ܐܬܘ ܗܘܘ ܓܝܪ ܘܐܦ ܗܢܘܢ ܠܥܕܥܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 46, 'ܐܬܐ ܕܝܢ ܬܘܒ ܝܫܘܥ ܠܩܛܢܐ ܕܓܠܝܠܐ ܐܝܟܐ ܕܥܒܕ ܡܝܐ ܚܡܪܐ ܘܐܝܬ ܗܘܐ ܒܟܦܪܢܚܘܡ ܥܒܕ ܡܠܟܐ ܚܕ ܕܒܪܗ ܟܪܝܗ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 47, 'ܗܢܐ ܫܡܥ ܕܐܬܐ ܝܫܘܥ ܡܢ ܝܗܘܕ ܠܓܠܝܠܐ ܘܐܙܠ ܠܘܬܗ ܘܒܥܐ ܗܘܐ ܡܢܗ ܕܢܚܘܬ ܘܢܐܤܐ ܠܒܪܗ ܩܪܝܒ ܗܘܐ ܓܝܪ ܠܡܡܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 48, 'ܐܡܪ ܠܗ ܝܫܘܥ ܐܢ ܐܬܘܬܐ ܘܬܕܡܪܬܐ ܠܐ ܬܚܙܘܢ ܠܐ ܬܗܝܡܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 49, 'ܐܡܪ ܠܗ ܗܘ ܥܒܕ ܡܠܟܐ ܡܪܝ ܚܘܬ ܥܕܠܐ ܡܐܬ ܠܗ ܛܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 50, 'ܐܡܪ ܠܗ ܝܫܘܥ ܙܠ ܒܪܟ ܚܝ ܗܘ ܘܗܝܡܢ ܗܘ ܓܒܪܐ ܒܡܠܬܐ ܕܐܡܪ ܠܗ ܝܫܘܥ ܘܐܙܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 51, 'ܟܕ ܕܝܢ ܢܚܬ ܗܘܐ ܐܪܥܘܗܝ ܥܒܕܘܗܝ ܘܤܒܪܘܗܝ ܘܐܡܪܝܢ ܠܗ ܒܪܟ ܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 52, 'ܘܫܐܠ ܐܢܘܢ ܒܐܝܢܐ ܥܕܢܐ ܐܬܚܠܡ ܐܡܪܝܢ ܠܗ ܐܬܡܠܝ ܒܫܒܥ ܫܥܝܢ ܫܒܩܬܗ ܐܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 53, 'ܘܝܕܥ ܐܒܘܗܝ ܕܒܗܝ ܫܥܬܐ ܕܒܗ ܐܡܪ ܠܗ ܝܫܘܥ ܕܒܪܟ ܚܝܐ ܘܗܝܡܢ ܗܘ ܘܒܝܬܗ ܟܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 54, 'ܗܕܐ ܬܘܒ ܐܬܐ ܕܬܪܬܝܢ ܥܒܕ ܝܫܘܥ ܟܕ ܐܬܐ ܡܢ ܝܗܘܕ ܠܓܠܝܠܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 1, 'ܒܬܪ ܗܠܝܢ ܗܘܐ ܥܕܥܕܐ ܕܝܗܘܕܝܐ ܘܤܠܩ ܝܫܘܥ ܠܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 2, 'ܐܝܬ ܗܘܐ ܕܝܢ ܬܡܢ ܒܐܘܪܫܠܡ ܕܘܟܬܐ ܚܕܐ ܕܡܥܡܘܕܝܬܐ ܕܡܬܩܪܝܐ ܥܒܪܐܝܬ ܒܝܬ ܚܤܕܐ ܘܐܝܬ ܗܘܐ ܒܗ ܚܡܫܐ ܐܤܛܘܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 3, 'ܘܒܗܠܝܢ ܪܡܝܢ ܗܘܘ ܥܡܐ ܤܓܝܐܐ ܕܟܪܝܗܐ ܘܤܡܝܐ ܘܚܓܝܤܐ ܘܝܒܝܫܐ ܘܡܤܟܝܢ ܗܘܘ ܠܙܘܥܐ ܕܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 4, 'ܡܠܐܟܐ ܓܝܪ ܒܙܒܢ ܙܒܢ ܢܚܬ ܗܘܐ ܠܗ ܠܡܥܡܘܕܝܬܐ ܘܡܙܝܥ ܗܘܐ ܠܗܘܢ ܠܡܝܐ ܘܐܝܢܐ ܕܩܕܡܝܐ ܢܚܬ ܗܘܐ ܡܢ ܒܬܪ ܙܘܥܐ ܕܡܝܐ ܡܬܚܠܡ ܗܘܐ ܟܠ ܟܐܒܐ ܐܝܢܐ ܕܐܝܬ ܗܘܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 5, 'ܐܝܬ ܗܘܐ ܕܝܢ ܬܡܢ ܓܒܪܐ ܚܕ ܕܬܠܬܝܢ ܘܬܡܢܐ ܫܢܝܢ ܐܝܬܘܗܝ ܗܘܐ ܒܟܘܪܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 6, 'ܠܗܢܐ ܚܙܐ ܝܫܘܥ ܕܪܡܐ ܘܝܕܥ ܕܙܒܢܐ ܤܓܝܐܐ ܐܝܬ ܠܗ ܘܐܡܪ ܠܗ ܨܒܐ ܐܢܬ ܕܬܬܚܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 7, 'ܥܢܐ ܗܘ ܟܪܝܗܐ ܘܐܡܪ ܐܝܢ ܡܪܝ ܠܝܬ ܠܝ ܕܝܢ ܐܢܫ ܕܡܐ ܕܐܬܬܙܝܥܘ ܡܝܐ ܢܪܡܝܢܝ ܒܡܥܡܘܕܝܬܐ ܐܠܐ ܥܕ ܐܢܐ ܐܬܐ ܐܢܐ ܐܚܪܝܢ ܡܢ ܩܕܡܝ ܢܚܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 8, 'ܐܡܪ ܠܗ ܝܫܘܥ ܩܘܡ ܫܩܘܠ ܥܪܤܟ ܘܗܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 9, 'ܘܒܪ ܫܥܬܗ ܐܬܚܠܡ ܓܒܪܐ ܗܘ ܘܩܡ ܫܩܠ ܥܪܤܗ ܘܗܠܟ ܘܗܘ ܗܘ ܝܘܡܐ ܫܒܬܐ ܗܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 10, 'ܘܐܡܪܝܢ ܠܗ ܝܗܘܕܝܐ ܠܗܘ ܕܐܬܐܤܝ ܫܒܬܐ ܗܝ ܠܐ ܫܠܝܛ ܠܟ ܕܬܫܩܘܠ ܥܪܤܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 11, 'ܗܘ ܕܝܢ ܥܢܐ ܘܐܡܪ ܠܗܘܢ ܗܘ ܕܥܒܕܢܝ ܚܠܝܡܐ ܗܘ ܐܡܪ ܠܝ ܕܫܩܘܠ ܥܪܤܟ ܘܗܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 12, 'ܘܫܐܠܘܗܝ ܡܢܘ ܗܢܐ ܓܒܪܐ ܕܐܡܪ ܠܟ ܕܫܩܘܠ ܥܪܤܟ ܘܗܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 13, 'ܗܘ ܕܝܢ ܕܐܬܐܤܝ ܠܐ ܝܕܥ ܗܘܐ ܡܢܘ ܝܫܘܥ ܓܝܪ ܐܬܓܢܝ ܗܘܐ ܠܗ ܒܟܢܫܐ ܤܓܝܐܐ ܕܐܝܬ ܗܘܐ ܒܕܘܟܬܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 14, 'ܒܬܪ ܙܒܢ ܐܫܟܚܗ ܝܫܘܥ ܒܗܝܟܠܐ ܘܐܡܪ ܠܗ ܗܐ ܚܠܝܡ ܐܢܬ ܬܘܒ ܠܐ ܬܚܛܐ ܕܠܡܐ ܢܗܘܐ ܠܟ ܡܕܡ ܕܒܝܫ ܡܢ ܩܕܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 15, 'ܘܐܙܠ ܗܘ ܓܒܪܐ ܘܐܡܪ ܠܝܗܘܕܝܐ ܕܝܫܘܥ ܗܘ ܗܘ ܕܐܚܠܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 16, 'ܘܡܛܠ ܗܕܐ ܪܕܦܝܢ ܗܘܘ ܝܗܘܕܝܐ ܠܝܫܘܥ ܘܒܥܝܢ ܗܘܘ ܠܡܩܛܠܗ ܕܗܠܝܢ ܥܒܕ ܗܘܐ ܒܫܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 17, 'ܗܘ ܕܝܢ ܝܫܘܥ ܐܡܪ ܠܗܘܢ ܐܒܝ ܥܕܡܐ ܠܗܫܐ ܥܒܕ ܐܦ ܐܢܐ ܥܒܕ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 18, 'ܘܡܛܠ ܗܕܐ ܝܬܝܪܐܝܬ ܒܥܝܢ ܗܘܘ ܝܗܘܕܝܐ ܠܡܩܛܠܗ ܠܐ ܒܠܚܘܕ ܕܫܪܐ ܗܘܐ ܫܒܬܐ ܐܠܐ ܐܦ ܕܥܠ ܐܠܗܐ ܕܐܒܘܗܝ ܐܝܬܘܗܝ ܐܡܪ ܗܘܐ ܘܡܫܘܐ ܗܘܐ ܢܦܫܗ ܥܡ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 19, 'ܥܢܐ ܕܝܢ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܠܐ ܡܫܟܚ ܒܪܐ ܥܒܕ ܡܕܡ ܡܢ ܨܒܘܬ ܢܦܫܗ ܐܠܐ ܡܕܡ ܕܚܙܐ ܠܐܒܐ ܕܥܒܕ ܐܝܠܝܢ ܓܝܪ ܕܐܒܐ ܥܒܕ ܗܠܝܢ ܐܦ ܒܪܐ ܐܟܘܬܗ ܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 20, 'ܐܒܐ ܓܝܪ ܪܚܡ ܠܒܪܗ ܘܟܠܡܕܡ ܕܥܒܕ ܡܚܘܐ ܠܗ ܘܕܝܬܝܪܝܢ ܡܢ ܗܠܝܢ ܥܒܕܐ ܡܚܘܐ ܠܗ ܕܐܢܬܘܢ ܬܬܕܡܪܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 21, 'ܐܝܟܢܐ ܓܝܪ ܕܐܒܐ ܡܩܝܡ ܡܝܬܐ ܘܡܚܐ ܠܗܘܢ ܗܟܢܐ ܐܦ ܒܪܐ ܠܐܝܠܝܢ ܕܨܒܐ ܡܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 22, 'ܠܐ ܗܘܐ ܓܝܪ ܐܒܐ ܕܐܢ ܠܐܢܫ ܐܠܐ ܟܠܗ ܕܝܢܐ ܝܗܒܗ ܠܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 23, 'ܕܟܠܢܫ ܢܝܩܪ ܠܒܪܐ ܐܝܟ ܕܡܝܩܪ ܠܐܒܐ ܗܘ ܕܠܐ ܡܝܩܪ ܠܒܪܐ ܠܐ ܡܝܩܪ ܠܐܒܐ ܕܫܕܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 24, 'ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܡܢ ܕܫܡܥ ܡܠܬܝ ܘܡܗܝܡܢ ܠܡܢ ܕܫܕܪܢܝ ܐܝܬ ܠܗ ܚܝܐ ܕܠܥܠܡ ܘܠܕܝܢܐ ܠܐ ܐܬܐ ܐܠܐ ܫܢܝ ܠܗ ܡܢ ܡܘܬܐ ܠܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 25, 'ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܐܬܝܐ ܫܥܬܐ ܐܦ ܗܫܐ ܐܝܬܝܗ ܐܡܬܝ ܕܡܝܬܐ ܢܫܡܥܘܢ ܩܠܗ ܕܒܪܗ ܕܐܠܗܐ ܘܗܢܘܢ ܕܫܡܥܝܢ ܢܚܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 26, 'ܐܝܟܢܐ ܓܝܪ ܕܠܐܒܐ ܐܝܬ ܚܝܐ ܒܩܢܘܡܗ ܗܟܢܐ ܝܗܒ ܐܦ ܠܒܪܐ ܕܢܗܘܘܢ ܚܝܐ ܒܩܢܘܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 27, 'ܘܐܫܠܛܗ ܕܢܗܘܐ ܥܒܕ ܐܦ ܕܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 28, 'ܕܒܪܗ ܗܘ ܕܝܢ ܕܐܢܫܐ ܠܐ ܬܬܕܡܪܘܢ ܒܗܕܐ ܕܐܬܝܐ ܫܥܬܐ ܐܡܬܝ ܕܟܠܗܘܢ ܐܝܠܝܢ ܕܒܩܒܪܐ ܐܢܘܢ ܢܫܡܥܘܢ ܩܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 29, 'ܘܢܦܩܘܢ ܐܝܠܝܢ ܕܥܒܕܘ ܛܒܬܐ ܠܩܝܡܬܐ ܕܚܝܐ ܘܐܝܠܝܢ ܕܥܒܕܘ ܒܝܫܬܐ ܠܩܝܡܬܐ ܕܕܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 30, 'ܠܐ ܡܫܟܚ ܐܢܐ ܡܕܡ ܡܢ ܨܒܘܬ ܢܦܫܝ ܠܡܥܒܕ ܐܠܐ ܐܝܟܢܐ ܕܫܡܥ ܐܢܐ ܕܐܢ ܐܢܐ ܘܕܝܢܝ ܟܐܝܢ ܗܘ ܠܐ ܓܝܪ ܒܥܐ ܐܢܐ ܨܒܝܢܝ ܐܠܐ ܨܒܝܢܗ ܕܡܢ ܕܫܕܪܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 31, 'ܐܢ ܐܢܐ ܡܤܗܕ ܐܢܐ ܥܠ ܢܦܫܝ ܤܗܕܘܬܝ ܠܐ ܗܘܬ ܫܪܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 32, 'ܐܚܪܝܢ ܗܘ ܗܘ ܕܡܤܗܕ ܥܠܝ ܘܝܕܥ ܐܢܐ ܕܫܪܝܪܐ ܗܝ ܤܗܕܘܬܗ ܕܡܤܗܕ ܥܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 33, 'ܐܢܬܘܢ ܫܕܪܬܘܢ ܠܘܬ ܝܘܚܢܢ ܘܐܤܗܕ ܥܠ ܫܪܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 34, 'ܐܢܐ ܕܝܢ ܠܐ ܗܘܐ ܡܢ ܒܪܢܫܐ ܢܤܒ ܐܢܐ ܤܗܕܘܬܐ ܐܠܐ ܗܠܝܢ ܐܡܪ ܐܢܐ ܕܐܢܬܘܢ ܬܚܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 35, 'ܗܘ ܫܪܓܐ ܗܘܐ ܕܕܠܩ ܘܡܢܗܪ ܘܐܢܬܘܢ ܨܒܝܬܘܢ ܕܬܫܬܒܗܪܘܢ ܕܫܥܬܐ ܒܢܘܗܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 36, 'ܠܝ ܕܝܢ ܐܝܬ ܠܝ ܤܗܕܘܬܐ ܕܪܒܐ ܡܢ ܕܝܘܚܢܢ ܥܒܕܐ ܓܝܪ ܕܝܗܒ ܠܝ ܐܒܝ ܕܐܫܠܡ ܐܢܘܢ ܗܢܘܢ ܥܒܕܐ ܕܥܒܕ ܐܢܐ ܤܗܕܝܢ ܥܠܝ ܕܐܒܐ ܫܠܚܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 37, 'ܘܐܒܐ ܕܫܠܚܢܝ ܗܘ ܤܗܕ ܥܠܝ ܠܐ ܩܠܗ ܡܡܬܘܡ ܫܡܥܬܘܢ ܘܠܐ ܚܙܘܗ ܚܙܝܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 38, 'ܘܡܠܬܗ ܠܐ ܡܩܘܝܐ ܒܟܘܢ ܡܛܠ ܕܒܗܘ ܕܗܘ ܫܕܪ ܐܢܬܘܢ ܠܐ ܡܗܝܡܢܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 39, 'ܒܨܘ ܟܬܒܐ ܕܒܗܘܢ ܡܤܒܪܝܢ ܐܢܬܘܢ ܕܚܝܐ ܕܠܥܠܡ ܐܝܬ ܠܟܘܢ ܘܗܢܘܢ ܤܗܕܝܢ ܥܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 40, 'ܘܠܐ ܨܒܝܢ ܐܢܬܘܢ ܕܬܐܬܘܢ ܠܘܬܝ ܕܚܝܐ ܕܠܥܠܡ ܢܗܘܘܢ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 41, 'ܫܘܒܚܐ ܡܢ ܒܢܝ ܐܢܫܐ ܠܐ ܢܤܒ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 42, 'ܐܠܐ ܝܕܥܬܟܘܢ ܕܚܘܒܗ ܕܐܠܗܐ ܠܝܬ ܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 43, 'ܐܢܐ ܐܬܝܬ ܒܫܡܗ ܕܐܒܝ ܘܠܐ ܡܩܒܠܝܢ ܐܢܬܘܢ ܠܝ ܘܐܢ ܐܚܪܝܢ ܢܐܬܐ ܒܫܡ ܢܦܫܗ ܠܗܘ ܬܩܒܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 44, 'ܐܝܟܢܐ ܡܫܟܚܝܢ ܐܢܬܘܢ ܠܡܗܝܡܢܘ ܕܫܘܒܚܐ ܚܕ ܡܢ ܚܕ ܡܩܒܠܝܢ ܐܢܬܘܢ ܘܫܘܒܚܐ ܕܡܢ ܚܕ ܐܠܗܐ ܠܐ ܒܥܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 45, 'ܠܡܐ ܤܒܪܝܢ ܐܢܬܘܢ ܕܐܢܐ ܐܟܠ ܐܢܐ ܩܪܨܝܟܘܢ ܩܕܡ ܐܒܐ ܐܝܬܘܗܝ ܡܢ ܕܐܟܠ ܩܪܨܝܟܘܢ ܡܘܫܐ ܗܘ ܕܒܗ ܤܒܪܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 46, 'ܐܠܘ ܓܝܪ ܒܡܘܫܐ ܗܝܡܢܬܘܢ ܐܦ ܒܝ ܡܗܝܡܢܝܢ ܗܘܝܬܘܢ ܡܘܫܐ ܓܝܪ ܥܠܝ ܟܬܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 47, 'ܘܐܢ ܠܟܬܒܘܗܝ ܕܗܘ ܠܐ ܡܗܝܡܢܝܢ ܐܢܬܘܢ ܐܝܟܢܐ ܠܡܠܝ ܕܝܠܝ ܬܗܝܡܢܘܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 1, 'ܒܬܪ ܗܠܝܢ ܐܙܠ ܝܫܘܥ ܠܥܒܪܐ ܕܝܡܐ ܕܓܠܝܠܐ ܕܛܒܪܝܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 2, 'ܘܐܙܠܘ ܒܬܪܗ ܟܢܫܐ ܤܓܝܐܐ ܡܛܠ ܕܚܙܝܢ ܗܘܘ ܐܬܘܬܐ ܕܥܒܕ ܒܟܪܝܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 3, 'ܘܤܠܩ ܝܫܘܥ ܠܛܘܪܐ ܘܬܡܢ ܝܬܒ ܗܘܐ ܥܡ ܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 4, 'ܩܪܝܒ ܗܘܐ ܕܝܢ ܥܕܥܕܐ ܕܦܨܚܐ ܕܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 5, 'ܘܐܪܝܡ ܥܝܢܘܗܝ ܝܫܘܥ ܘܚܙܐ ܟܢܫܐ ܤܓܝܐܐ ܕܐܬܐ ܠܘܬܗ ܘܐܡܪ ܠܦܝܠܝܦܘܤ ܐܝܡܟܐ ܢܙܒܢ ܠܚܡܐ ܕܢܐܟܠܘܢ ܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 6, 'ܗܕܐ ܕܝܢ ܐܡܪ ܟܕ ܡܢܤܐ ܠܗ ܗܘ ܓܝܪ ܝܕܥ ܗܘܐ ܡܢܐ ܥܬܝܕ ܠܡܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 7, 'ܐܡܪ ܠܗ ܦܝܠܝܦܘܤ ܕܡܐܬܝܢ ܕܝܢܪܝܢ ܠܚܡܐ ܠܐ ܤܦܩ ܠܗܘܢ ܟܕ ܩܠܝܠ ܩܠܝܠ ܚܕ ܚܕ ܡܢܗܘܢ ܢܤܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 8, 'ܐܡܪ ܠܗ ܚܕ ܡܢ ܬܠܡܝܕܘܗܝ ܐܢܕܪܐܘܤ ܐܚܘܗܝ ܕܫܡܥܘܢ ܟܐܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 9, 'ܐܝܬ ܬܢܢ ܛܠܝܐ ܚܕ ܕܐܝܬ ܥܠܘܗܝ ܚܡܫ ܓܪܝܨܢ ܕܤܥܪܐ ܘܬܪܝܢ ܢܘܢܝܢ ܐܠܐ ܗܠܝܢ ܡܢܐ ܐܢܘܢ ܠܗܠܝܢ ܟܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 10, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܥܒܕܘ ܐܢܫܐ ܟܠܗܘܢ ܕܢܤܬܡܟܘܢ ܥܤܒܐ ܕܝܢ ܤܓܝ ܗܘܐ ܒܗ ܒܕܘܟܬܐ ܗܝ ܘܐܤܬܡܟܘ ܓܒܪܐ ܒܡܢܝܢܐ ܚܡܫܐ ܐܠܦܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 11, 'ܘܫܩܠ ܝܫܘܥ ܠܚܡܐ ܘܒܪܟ ܘܦܠܓ ܠܗܢܘܢ ܕܤܡܝܟܝܢ ܘܗܟܢܐ ܐܦ ܡܢ ܢܘܢܐ ܟܡܐ ܕܨܒܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 12, 'ܘܟܕ ܤܒܥܘ ܐܡܪ ܠܬܠܡܝܕܘܗܝ ܟܢܫܘ ܩܨܝܐ ܕܝܬܪܘ ܕܠܐ ܢܐܒܕ ܡܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 13, 'ܘܟܢܫܘ ܘܡܠܘ ܬܪܥܤܪ ܩܘܦܝܢܝܢ ܩܨܝܐ ܐܝܠܝܢ ܕܝܬܪܘ ܠܗܢܘܢ ܕܐܟܠܘ ܡܢ ܚܡܫܐ ܠܚܡܝܢ ܕܤܥܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 14, 'ܗܢܘܢ ܕܝܢ ܐܢܫܐ ܕܚܙܘ ܐܬܐ ܕܥܒܕ ܝܫܘܥ ܐܡܪܝܢ ܗܘܘ ܫܪܝܪܐܝܬ ܗܢܘ ܢܒܝܐ ܕܐܬܐ ܠܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 15, 'ܝܫܘܥ ܕܝܢ ܝܕܥ ܕܥܬܝܕܝܢ ܕܢܐܬܘܢ ܢܚܛܦܘܢܝܗܝ ܘܢܥܒܕܘܢܝܗܝ ܡܠܟܐ ܘܫܢܝ ܠܗ ܠܛܘܪܐ ܗܘ ܒܠܚܘܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 16, 'ܘܟܕ ܗܘܐ ܪܡܫܐ ܢܚܬܘ ܬܠܡܝܕܘܗܝ ܠܝܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 17, 'ܘܝܬܒܘ ܒܤܦܝܢܬܐ ܘܐܬܝܢ ܗܘܘ ܠܥܒܪܐ ܠܟܦܪܢܚܘܡ ܘܚܫܟܬ ܗܘܬ ܠܗ ܘܠܐ ܐܬܝ ܗܘܐ ܠܘܬܗܘܢ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 18, 'ܝܡܐ ܕܝܢ ܐܙܕܩܦ ܗܘܐ ܥܠܝܗܘܢ ܡܛܠ ܕܪܘܚܐ ܪܒܬܐ ܢܫܒܬ ܗܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 19, 'ܘܕܒܪܘ ܐܝܟ ܐܤܛܕܘܬܐ ܥܤܪܝܢ ܘܚܡܫܐ ܐܘ ܬܠܬܝܢ ܘܚܙܘ ܠܝܫܘܥ ܟܕ ܡܗܠܟ ܥܠ ܝܡܬܐ ܘܟܕ ܩܪܒ ܠܘܬ ܤܦܝܢܬܗܘܢ ܕܚܠܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 20, 'ܗܘ ܕܝܢ ܝܫܘܥ ܐܡܪ ܠܗܘܢ ܐܢܐ ܐܢܐ ܠܐ ܬܕܚܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 21, 'ܘܨܒܘ ܗܘܘ ܕܢܩܒܠܘܢܝܗܝ ܒܤܦܝܢܬܐ ܘܒܗ ܒܫܥܬܐ ܤܦܝܢܬܐ ܗܝ ܗܘܬ ܠܘܬ ܐܪܥܐ ܗܝ ܕܐܙܠܝܢ ܗܘܘ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 22, 'ܘܠܝܘܡܐ ܕܒܬܪܗ ܗܘ ܟܢܫܐ ܕܩܐܡ ܗܘܐ ܒܥܒܪܐ ܕܝܡܐ ܚܙܘ ܕܤܦܝܢܬܐ ܐܚܪܬܐ ܠܝܬ ܗܘܐ ܬܡܢ ܐܠܐ ܐܢ ܗܝ ܕܤܠܩܘ ܠܗ ܬܠܡܝܕܐ ܘܕܠܐ ܥܠ ܗܘܐ ܥܡܗܘܢ ܝܫܘܥ ܥܡ ܬܠܡܝܕܘܗܝ ܠܤܦܝܢܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 23, 'ܐܬܝ ܗܘܝ ܕܝܢ ܐܠܦܐ ܐܚܪܢܝܬܐ ܡܢ ܛܒܪܝܘܤ ܥܠ ܓܢܒ ܕܘܟܬܐ ܗܝ ܕܐܟܠܘ ܒܗ ܠܚܡܐ ܟܕ ܒܪܟ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 24, 'ܘܟܕ ܚܙܐ ܗܘ ܟܢܫܐ ܕܠܐ ܗܘܐ ܬܡܢ ܝܫܘܥ ܐܦܠܐ ܬܠܡܝܕܘܗܝ ܤܠܩܘ ܠܗܠܝܢ ܐܠܦܐ ܘܐܬܘ ܠܟܦܪܢܚܘܡ ܘܒܥܝܢ ܗܘܘ ܠܗ ܠܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 25, 'ܘܟܕ ܐܫܟܚܘܗܝ ܒܥܒܪܐ ܕܝܡܐ ܐܡܪܝܢ ܠܗ ܪܒܢ ܐܡܬܝ ܐܬܝܬ ܠܗܪܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 26, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܒܥܝܢ ܐܢܬܘܢ ܠܝ ܠܐ ܗܘܐ ܡܛܠ ܕܚܙܝܬܘܢ ܐܬܘܬܐ ܐܠܐ ܕܐܟܠܬܘܢ ܠܚܡܐ ܘܤܒܥܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 27, 'ܠܐ ܬܦܠܚܘܢ ܡܐܟܘܠܬܐ ܕܐܒܕܐ ܐܠܐ ܡܐܟܘܠܬܐ ܕܡܩܘܝܐ ܠܚܝܐ ܕܠܥܠܡ ܐܝܕܐ ܕܒܪܗ ܕܐܢܫܐ ܢܬܠ ܠܟܘܢ ܠܗܢܐ ܓܝܪ ܐܒܐ ܚܬܡ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 28, 'ܐܡܪܝܢ ܠܗ ܡܢܐ ܢܥܒܕ ܕܢܦܠܘܚ ܥܒܕܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 29, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܗܢܘ ܥܒܕܐ ܕܐܠܗܐ ܕܬܗܝܡܢܘܢ ܒܡܢ ܕܗܘ ܫܕܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 30, 'ܐܡܪܝܢ ܠܗ ܡܢܐ ܐܬܐ ܥܒܕ ܐܢܬ ܕܢܚܙܐ ܘܢܗܝܡܢ ܒܟ ܡܢܐ ܤܥܪ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 31, 'ܐܒܗܝܢ ܡܢܢܐ ܐܟܠܘ ܒܡܕܒܪܐ ܐܝܟܢܐ ܕܟܬܝܒ ܕܠܚܡܐ ܡܢ ܫܡܝܐ ܝܗܒ ܠܗܘܢ ܠܡܐܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 32, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܠܐ ܗܘܐ ܡܘܫܐ ܝܗܒ ܠܟܘܢ ܠܚܡܐ ܡܢ ܫܡܝܐ ܐܠܐ ܐܒܝ ܝܗܒ ܠܟܘܢ ܠܚܡܐ ܕܩܘܫܬܐ ܡܢ ܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 33, 'ܠܚܡܗ ܓܝܪ ܕܐܠܗܐ ܐܝܬܘܗܝ ܗܘ ܕܢܚܬ ܡܢ ܫܡܝܐ ܘܝܗܒ ܚܝܐ ܠܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 34, 'ܐܡܪܝܢ ܠܗ ܡܪܢ ܒܟܠܙܒܢ ܗܒ ܠܢ ܠܚܡܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 35, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܢܐ ܐܢܐ ܠܚܡܐ ܕܚܝܐ ܡܢ ܕܐܬܐ ܠܘܬܝ ܠܐ ܢܟܦܢ ܘܡܢ ܕܡܗܝܡܢ ܒܝ ܠܐ ܢܨܗܐ ܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 36, 'ܐܠܐ ܐܡܪܬ ܠܟܘܢ ܕܚܙܝܬܘܢܢܝ ܘܠܐ ܡܗܝܡܢܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 37, 'ܟܠ ܕܝܗܒ ܠܝ ܐܒܝ ܠܘܬܝ ܢܐܬܐ ܘܡܢ ܕܠܘܬܝ ܢܐܬܐ ܠܐ ܐܦܩܗ ܠܒܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 38, 'ܕܢܚܬܬ ܡܢ ܫܡܝܐ ܠܐ ܗܘܐ ܕܐܥܒܕ ܨܒܝܢܝ ܐܠܐ ܕܐܥܒܕ ܨܒܝܢܗ ܕܡܢ ܕܫܕܪܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 39, 'ܗܢܘ ܕܝܢ ܨܒܝܢܗ ܕܡܢ ܕܫܕܪܢܝ ܕܟܠ ܕܝܗܒ ܠܝ ܠܐ ܐܘܒܕ ܡܢܗ ܐܠܐ ܐܩܝܡܝܘܗܝ ܒܝܘܡܐ ܐܚܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 40, 'ܗܢܘ ܓܝܪ ܨܒܝܢܗ ܕܐܒܝ ܕܟܠ ܕܚܙܐ ܠܒܪܐ ܘܡܗܝܡܢ ܒܗ ܢܗܘܘܢ ܠܗ ܚܝܐ ܕܠܥܠܡ ܘܐܢܐ ܐܩܝܡܝܘܗܝ ܒܝܘܡܐ ܐܚܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 41, 'ܝܗܘܕܝܐ ܕܝܢ ܪܛܢܝܢ ܗܘܘ ܥܠܘܗܝ ܕܐܡܪ ܕܐܢܐ ܐܢܐ ܠܚܡܐ ܕܢܚܬܬ ܡܢ ܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 42, 'ܘܐܡܪܝܢ ܗܘܘ ܠܐ ܗܘܐ ܗܢܐ ܝܫܘܥ ܒܪܗ ܕܝܘܤܦ ܗܘ ܕܚܢܢ ܝܕܥܝܢ ܠܐܒܘܗܝ ܘܠܐܡܗ ܘܐܝܟܢܐ ܐܡܪ ܗܢܐ ܕܡܢ ܫܡܝܐ ܢܚܬܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 43, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܠܐ ܬܪܛܢܘܢ ܚܕ ܥܡ ܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 44, 'ܠܐ ܐܢܫ ܡܫܟܚ ܕܢܐܬܐ ܠܘܬܝ ܐܠܐ ܐܢ ܢܓܕܗ ܐܒܐ ܕܫܕܪܢܝ ܘܐܢܐ ܐܩܝܡܝܘܗܝ ܒܝܘܡܐ ܐܚܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 45, 'ܟܬܝܒ ܓܝܪ ܒܢܒܝܐ ܕܢܗܘܘܢ ܟܠܗܘܢ ܡܠܦܐ ܕܐܠܗܐ ܟܠ ܡܢ ܕܫܡܥ ܗܟܝܠ ܡܢ ܐܒܐ ܘܝܠܦ ܡܢܗ ܐܬܐ ܠܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 46, 'ܠܐ ܗܘܐ ܕܚܙܐ ܐܢܫ ܠܐܒܐ ܐܠܐ ܡܢ ܕܡܢ ܐܠܗܐ ܐܝܬܘܗܝ ܗܘ ܗܘ ܚܙܐ ܠܐܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 47, 'ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܡܢ ܕܡܗܝܡܢ ܒܝ ܐܝܬ ܠܗ ܚܝܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 48, 'ܐܢܐ ܐܢܐ ܠܚܡܐ ܕܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 49, 'ܐܒܗܝܟܘܢ ܐܟܠܘ ܡܢܢܐ ܒܡܕܒܪܐ ܘܡܝܬܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 50, 'ܗܢܘ ܕܝܢ ܠܚܡܐ ܕܢܚܬ ܡܢ ܫܡܝܐ ܕܢܐܟܘܠ ܐܢܫ ܡܢܗ ܘܠܐ ܢܡܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 51, 'ܐܢܐ ܐܢܐ ܠܚܡܐ ܚܝܐ ܕܡܢ ܫܡܝܐ ܢܚܬܬ ܘܐܢ ܐܢܫ ܢܐܟܘܠ ܡܢ ܗܢܐ ܠܚܡܐ ܢܚܐ ܠܥܠܡ ܘܠܚܡܐ ܐܝܢܐ ܕܐܢܐ ܐܬܠ ܦܓܪܝ ܗܘ ܕܥܠ ܐܦܝ ܚܝܘܗܝ ܕܥܠܡܐ ܝܗܒ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 52, 'ܢܨܝܢ ܗܘܘ ܕܝܢ ܝܗܘܕܝܐ ܚܕ ܥܡ ܚܕ ܘܐܡܪܝܢ ܐܝܟܢܐ ܡܫܟܚ ܗܢܐ ܦܓܪܗ ܕܢܬܠ ܠܢ ܠܡܐܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 53, 'ܘܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܐܠܐ ܬܐܟܠܘܢ ܦܓܪܗ ܕܒܪܗ ܕܐܢܫܐ ܘܬܫܬܘܢ ܕܡܗ ܠܝܬ ܠܟܘܢ ܚܝܐ ܒܩܢܘܡܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 54, 'ܡܢ ܕܐܟܠ ܕܝܢ ܡܢ ܦܓܪܝ ܘܫܬܐ ܡܢ ܕܡܝ ܐܝܬ ܠܗ ܚܝܐ ܕܠܥܠܡ ܘܐܢܐ ܐܩܝܡܝܘܗܝ ܒܝܘܡܐ ܐܚܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 55, 'ܦܓܪܝ ܓܝܪ ܫܪܝܪܐܝܬ ܐܝܬܘܗܝ ܡܐܟܘܠܬܐ ܘܕܡܝ ܫܪܝܪܐܝܬ ܐܝܬܘܗܝ ܡܫܬܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 56, 'ܡܢ ܕܐܟܠ ܦܓܪܝ ܘܫܬܐ ܕܡܝ ܒܝ ܡܩܘܐ ܘܐܢܐ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 57, 'ܐܝܟܢܐ ܕܫܕܪܢܝ ܐܒܐ ܚܝܐ ܘܐܢܐ ܚܝ ܐܢܐ ܡܛܠ ܐܒܐ ܘܡܢ ܕܢܐܟܠܢܝ ܐܦ ܗܘ ܢܚܐ ܡܛܠܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 58, 'ܗܢܘ ܠܚܡܐ ܕܢܚܬ ܡܢ ܫܡܝܐ ܠܐ ܗܘܐ ܐܝܟ ܕܐܟܠܘ ܐܒܗܝܟܘܢ ܡܢܢܐ ܘܡܝܬܘ ܡܢ ܕܐܟܠ ܗܢܐ ܠܚܡܐ ܢܚܐ ܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 59, 'ܗܠܝܢ ܐܡܪ ܒܟܢܘܫܬܐ ܟܕ ܡܠܦ ܒܟܦܪܢܚܘܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 60, 'ܘܤܓܝܐܐ ܕܫܡܥܘ ܡܢ ܬܠܡܝܕܘܗܝ ܐܡܪܝܢ ܩܫܝܐ ܗܝ ܡܠܬܐ ܗܕܐ ܡܢܘ ܡܫܟܚ ܠܡܫܡܥܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 61, 'ܝܫܘܥ ܕܝܢ ܝܕܥ ܒܢܦܫܗ ܕܪܛܢܝܢ ܥܠ ܗܕܐ ܬܠܡܝܕܘܗܝ ܘܐܡܪ ܠܗܘܢ ܗܕܐ ܡܟܫܠܐ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 62, 'ܐܢ ܬܚܙܘܢ ܗܟܝܠ ܠܒܪܗ ܕܐܢܫܐ ܕܤܠܩ ܠܐܬܪ ܕܐܝܬܘܗܝ ܗܘܐ ܡܢ ܩܕܝܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 63, 'ܪܘܚܐ ܗܝ ܕܡܚܝܐ ܦܓܪܐ ܠܐ ܡܗܢܐ ܡܕܡ ܡܠܐ ܕܐܢܐ ܡܠܠܬ ܥܡܟܘܢ ܪܘܚܐ ܐܢܝܢ ܘܚܝܐ ܐܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 64, 'ܐܠܐ ܐܝܬ ܐܢܫܐ ܡܢܟܘܢ ܕܠܐ ܡܗܝܡܢܝܢ ܝܕܥ ܗܘܐ ܓܝܪ ܝܫܘܥ ܡܢ ܩܕܝܡ ܡܢ ܐܢܘܢ ܐܝܠܝܢ ܕܠܐ ܡܗܝܡܢܝܢ ܘܡܢܘ ܗܘ ܕܡܫܠܡ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 65, 'ܘܐܡܪ ܗܘܐ ܠܗܘܢ ܡܛܠ ܗܢܐ ܐܡܪܬ ܠܟܘܢ ܕܠܐ ܐܢܫ ܡܫܟܚ ܕܢܐܬܐ ܠܘܬܝ ܐܠܐ ܝܗܝܒ ܠܗ ܡܢ ܐܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 66, 'ܡܛܠ ܗܕܐ ܡܠܬܐ ܤܓܝܐܐ ܡܢ ܬܠܡܝܕܘܗܝ ܐܙܠܘ ܠܒܤܬܪܗܘܢ ܘܠܐ ܡܗܠܟܝܢ ܗܘܘ ܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 67, 'ܘܐܡܪ ܝܫܘܥ ܠܬܪܥܤܪܬܗ ܠܡܐ ܐܦ ܐܢܬܘܢ ܨܒܝܢ ܐܢܬܘܢ ܠܡܐܙܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 68, 'ܥܢܐ ܫܡܥܘܢ ܟܐܦܐ ܘܐܡܪ ܡܪܝ ܠܘܬ ܡܢ ܢܐܙܠ ܡܠܐ ܕܚܝܐ ܕܠܥܠܡ ܐܝܬ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 69, 'ܘܚܢܢ ܗܝܡܢܢ ܘܝܕܥܢ ܕܐܢܬ ܗܘ ܡܫܝܚܐ ܒܪܗ ܕܐܠܗܐ ܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 70, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܠܐ ܗܘܐ ܐܢܐ ܓܒܝܬܟܘܢ ܠܬܪܥܤܪ ܘܡܢܟܘܢ ܚܕ ܤܛܢܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 71, 'ܐܡܪ ܗܘܐ ܕܝܢ ܥܠ ܝܗܘܕܐ ܒܪ ܫܡܥܘܢ ܤܟܪܝܘܛܐ ܗܘ ܓܝܪ ܥܬܝܕ ܗܘܐ ܕܢܫܠܡܝܘܗܝ ܚܕ ܡܢ ܬܪܥܤܪ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 1, 'ܒܬܪ ܗܠܝܢ ܡܗܠܟ ܗܘܐ ܝܫܘܥ ܒܓܠܝܠܐ ܠܐ ܓܝܪ ܨܒܐ ܗܘܐ ܠܡܗܠܟܘ ܒܝܗܘܕ ܡܛܠ ܕܝܗܘܕܝܐ ܒܥܝܢ ܗܘܘ ܠܡܩܛܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 2, 'ܘܩܪܝܒ ܗܘܐ ܥܕܥܕܐ ܕܡܛܠܐ ܕܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 3, 'ܘܐܡܪܘ ܠܗ ܐܚܘܗܝ ܠܝܫܘܥ ܫܢܐ ܠܟ ܡܟܐ ܘܙܠ ܠܝܗܘܕ ܕܢܚܙܘܢ ܬܠܡܝܕܝܟ ܥܒܕܐ ܕܥܒܕ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 4, 'ܠܝܬ ܓܝܪ ܐܢܫ ܕܥܒܕ ܡܕܡ ܒܛܘܫܝܐ ܘܨܒܐ ܗܘ ܕܒܓܠܝܐ ܢܗܘܐ ܐܢ ܗܠܝܢ ܥܒܕ ܐܢܬ ܚܘܐ ܢܦܫܟ ܠܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 5, 'ܐܦ ܠܐ ܓܝܪ ܐܚܘܗܝ ܗܝܡܢܘ ܗܘܘ ܒܗ ܒܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 6, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܙܒܢܝ ܕܝܠܝ ܥܕܡܐ ܠܗܫܐ ܠܐ ܡܛܐ ܙܒܢܟܘܢ ܕܝܢ ܕܝܠܟܘܢ ܒܟܠ ܥܕܢ ܡܛܝܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 7, 'ܠܐ ܡܫܟܚ ܥܠܡܐ ܠܡܤܢܟܘܢ ܠܝ ܕܝܢ ܤܢܐ ܡܛܠ ܕܐܢܐ ܡܤܗܕ ܐܢܐ ܥܠܘܗܝ ܕܥܒܕܘܗܝ ܒܝܫܝܢ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 8, 'ܐܢܬܘܢ ܤܩܘ ܠܥܕܥܕܐ ܗܢܐ ܐܢܐ ܠܐ ܤܠܩ ܐܢܐ ܗܫܐ ܠܥܕܥܕܐ ܗܢܐ ܡܛܠ ܕܙܒܢܐ ܕܝܠܝ ܠܐ ܥܕܟܝܠ ܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 9, 'ܗܠܝܢ ܐܡܪ ܘܦܫ ܠܗ ܒܓܠܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 10, 'ܟܕ ܕܝܢ ܤܠܩܘ ܐܚܘܗܝ ܠܥܕܥܕܐ ܗܝܕܝܢ ܐܦ ܗܘ ܤܠܩ ܠܐ ܒܓܠܝܐ ܐܠܐ ܐܝܟ ܕܒܛܘܫܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 11, 'ܝܗܘܕܝܐ ܕܝܢ ܒܥܝܢ ܗܘܘ ܠܗ ܒܥܕܥܕܐ ܘܐܡܪܝܢ ܗܘܘ ܐܝܟܘ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 12, 'ܘܪܛܢܐ ܤܓܝܐܐ ܡܛܠܬܗ ܐܝܬ ܗܘܐ ܒܟܢܫܐ ܐܝܬ ܗܘܘ ܓܝܪ ܕܐܡܪܝܢ ܕܛܒ ܗܘ ܘܐܚܪܢܐ ܐܡܪܝܢ ܗܘܘ ܠܐ ܐܠܐ ܡܛܥܐ ܠܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 13, 'ܠܐ ܕܝܢ ܐܢܫ ܓܠܝܐܝܬ ܡܡܠܠ ܗܘܐ ܥܠܘܗܝ ܡܛܠ ܕܚܠܬܐ ܕܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 14, 'ܟܕ ܕܝܢ ܦܠܓܘ ܝܘܡܬܐ ܕܥܕܥܕܐ ܤܠܩ ܝܫܘܥ ܠܗܝܟܠܐ ܘܡܠܦ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 15, 'ܘܡܬܕܡܪܝܢ ܗܘܘ ܝܗܘܕܝܐ ܘܐܡܪܝܢ ܐܝܟܢܐ ܝܕܥ ܗܢܐ ܤܦܪܐ ܟܕ ܠܐ ܝܠܦ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 16, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܝܘܠܦܢܝ ܠܐ ܗܘܐ ܕܝܠܝ ܐܠܐ ܕܗܘ ܕܫܕܪܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 17, 'ܡܢ ܕܨܒܐ ܕܢܥܒܕ ܨܒܝܢܗ ܡܤܬܟܠ ܝܘܠܦܢܝ ܐܢ ܡܢ ܐܠܗܐ ܗܘ ܐܘ ܐܢܐ ܡܢ ܨܒܘܬ ܢܦܫܝ ܡܡܠܠ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 18, 'ܡܢ ܕܡܢ ܨܒܘܬ ܪܥܝܢܗ ܡܡܠܠ ܫܘܒܚܐ ܠܢܦܫܗ ܒܥܐ ܗܘ ܕܝܢ ܕܫܘܒܚܐ ܕܡܢ ܕܫܕܪܗ ܒܥܐ ܫܪܝܪ ܗܘ ܘܥܘܠܐ ܒܠܒܗ ܠܐ ܐܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 19, 'ܠܐ ܗܘܐ ܡܘܫܐ ܝܗܒ ܠܟܘܢ ܢܡܘܤܐ ܘܠܐ ܐܢܫ ܡܢܟܘܢ ܢܛܪ ܢܡܘܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 20, 'ܡܢܐ ܒܥܝܢ ܐܢܬܘܢ ܠܡܩܛܠܢܝ ܥܢܐ ܟܢܫܐ ܘܐܡܪܝܢ ܕܝܘܐ ܐܝܬ ܠܟ ܡܢܘ ܒܥܐ ܠܡܩܛܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 21, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܚܕ ܥܒܕܐ ܥܒܕܬ ܘܟܠܟܘܢ ܡܬܕܡܪܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 22, 'ܡܛܠ ܗܢܐ ܡܘܫܐ ܝܗܒ ܠܟܘܢ ܓܙܘܪܬܐ ܠܐ ܗܘܐ ܡܛܠ ܕܡܢܗ ܗܝ ܡܢ ܡܘܫܐ ܐܠܐ ܕܡܢ ܐܒܗܬܐ ܗܝ ܘܒܫܒܬܐ ܓܙܪܝܢ ܐܢܬܘܢ ܒܪܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 23, 'ܐܢ ܒܪܢܫܐ ܡܬܓܙܪ ܒܝܘܡܐ ܕܫܒܬܐ ܡܛܠ ܕܠܐ ܢܫܬܪܐ ܢܡܘܤܐ ܕܡܘܫܐ ܥܠܝ ܪܛܢܝܢ ܐܢܬܘܢ ܕܟܠܗ ܒܪܢܫܐ ܐܚܠܡܬ ܒܝܘܡܐ ܕܫܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 24, 'ܠܐ ܬܗܘܘܢ ܕܝܢܝܢ ܒܡܤܒ ܒܐܦܐ ܐܠܐ ܕܝܢܐ ܟܐܢܐ ܕܘܢܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 25, 'ܘܐܡܪܝܢ ܗܘܘ ܐܢܫܐ ܡܢ ܐܘܪܫܠܡ ܠܘ ܗܢܘ ܗܘ ܕܒܥܝܢ ܠܡܩܛܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 26, 'ܘܗܐ ܓܠܝܐܝܬ ܡܡܠܠ ܘܡܕܡ ܠܐ ܐܡܪܝܢ ܠܗ ܕܠܡܐ ܝܕܥܘ ܩܫܝܫܝܢ ܕܗܢܘ ܫܪܝܪܐܝܬ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 27, 'ܐܠܐ ܠܗܢܐ ܝܕܥܝܢ ܚܢܢ ܡܢ ܐܝܡܟܐ ܗܘ ܡܫܝܚܐ ܕܝܢ ܐܡܬܝ ܕܐܬܐ ܠܐ ܐܢܫ ܝܕܥ ܡܢ ܐܝܡܟܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 28, 'ܘܐܪܝܡ ܝܫܘܥ ܩܠܗ ܟܕ ܡܠܦ ܒܗܝܟܠܐ ܘܐܡܪ ܘܠܝ ܝܕܥܝܢ ܐܢܬܘܢ ܘܡܢ ܐܝܡܟܐ ܐܢܐ ܝܕܥܝܢ ܐܢܬܘܢ ܘܡܢ ܨܒܘܬ ܢܦܫܝ ܠܐ ܐܬܝܬ ܐܠܐ ܫܪܝܪ ܗܘ ܡܢ ܕܫܕܪܢܝ ܗܘ ܕܐܢܬܘܢ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 29, 'ܐܢܐ ܕܝܢ ܝܕܥ ܐܢܐ ܠܗ ܕܡܢ ܠܘܬܗ ܐܢܐ ܘܗܘ ܫܕܪܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 30, 'ܘܒܥܘ ܠܡܐܚܕܗ ܘܠܐ ܐܢܫ ܐܪܡܝ ܥܠܘܗܝ ܐܝܕܝܐ ܡܛܠ ܕܠܐ ܥܕܟܝܠ ܐܬܬ ܗܘܬ ܫܥܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 31, 'ܤܓܝܐܐ ܕܝܢ ܡܢ ܟܢܫܐ ܗܝܡܢܘ ܒܗ ܘܐܡܪܝܢ ܡܫܝܚܐ ܡܐ ܕܐܬܐ ܠܡܐ ܕܝܬܝܪܢ ܡܢ ܗܠܝܢ ܐܬܘܬܐ ܕܥܒܕ ܗܢܐ ܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 32, 'ܘܫܡܥܘ ܦܪܝܫܐ ܠܟܢܫܐ ܕܡܡܠܠܝܢ ܥܠܘܗܝ ܗܠܝܢ ܘܫܕܪܘ ܗܢܘܢ ܘܪܒܝ ܟܗܢܐ ܕܚܫܐ ܕܢܐܚܕܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 33, 'ܘܐܡܪ ܝܫܘܥ ܩܠܝܠ ܬܘܒ ܙܒܢܐ ܥܡܟܘܢ ܐܢܐ ܘܐܙܠ ܐܢܐ ܠܘܬ ܡܢ ܕܫܕܪܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 34, 'ܘܬܒܥܘܢܢܝ ܘܠܐ ܬܫܟܚܘܢܢܝ ܘܐܝܟܐ ܕܐܢܐ ܐܝܬܝ ܠܐ ܡܫܟܚܝܢ ܐܢܬܘܢ ܠܡܐܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 35, 'ܐܡܪܝܢ ܝܗܘܕܝܐ ܒܢܦܫܗܘܢ ܠܐܝܟܐ ܥܬܝܕ ܗܢܐ ܠܡܐܙܠ ܕܚܢܢ ܠܐ ܡܫܟܚܝܢ ܚܢܢ ܠܗ ܠܡܐ ܟܝ ܠܐܬܪܘܬܐ ܕܥܡܡܐ ܥܬܝܕ ܕܢܐܙܠ ܘܢܠܦ ܠܚܢܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 36, 'ܡܢܐ ܗܝ ܗܕܐ ܡܠܬܐ ܕܐܡܪ ܕܬܒܥܘܢܢܝ ܘܠܐ ܬܫܟܚܘܢܢܝ ܘܐܝܟܐ ܕܐܢܐ ܐܝܬܝ ܐܢܬܘܢ ܠܐ ܡܫܟܚܝܢ ܐܢܬܘܢ ܠܡܐܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 37, 'ܒܝܘܡܐ ܕܝܢ ܪܒܐ ܕܐܝܬܘܗܝ ܐܚܪܝܐ ܕܥܕܥܕܐ ܩܐܡ ܗܘܐ ܝܫܘܥ ܘܩܥܐ ܘܐܡܪ ܐܢ ܐܢܫ ܨܗܐ ܢܐܬܐ ܠܘܬܝ ܘܢܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 38, 'ܟܠ ܡܢ ܕܡܗܝܡܢ ܒܝ ܐܝܟܢܐ ܕܐܡܪܘ ܟܬܒܐ ܢܗܪܘܬܐ ܕܡܝܐ ܚܝܐ ܢܪܕܘܢ ܡܢ ܟܪܤܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 39, 'ܗܕܐ ܕܝܢ ܐܡܪ ܥܠ ܪܘܚܐ ܕܥܬܝܕܝܢ ܗܘܘ ܠܡܩܒܠܘ ܐܝܠܝܢ ܕܡܗܝܡܢܝܢ ܒܗ ܠܐ ܓܝܪ ܥܕܟܝܠ ܐܬܝܗܒܬ ܗܘܬ ܪܘܚܐ ܡܛܠ ܕܠܐ ܥܕܟܝܠ ܐܫܬܒܚ ܗܘܐ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 40, 'ܤܓܝܐܐ ܕܝܢ ܡܢ ܟܢܫܐ ܕܫܡܥܘ ܡܠܘܗܝ ܐܡܪܝܢ ܗܘܘ ܗܢܘ ܫܪܝܪܐܝܬ ܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 41, 'ܐܚܪܢܐ ܐܡܪܝܢ ܗܘܘ ܗܢܘ ܡܫܝܚܐ ܐܚܪܢܐ ܐܡܪܝܢ ܕܠܡܐ ܡܢ ܓܠܝܠܐ ܐܬܐ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 42, 'ܠܐ ܗܘܐ ܟܬܒܐ ܐܡܪ ܕܡܢ ܙܪܥܗ ܕܕܘܝܕ ܘܡܢ ܒܝܬ ܠܚܡ ܩܪܝܬܐ ܕܝܠܗ ܕܕܘܝܕ ܐܬܐ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 43, 'ܘܗܘܬ ܗܘܬ ܦܠܓܘܬܐ ܒܟܢܫܐ ܡܛܠܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 44, 'ܘܐܝܬ ܗܘܘ ܐܢܫܝܢ ܡܢܗܘܢ ܕܨܒܝܢ ܗܘܘ ܕܢܐܚܕܘܢܝܗܝ ܐܠܐ ܠܐ ܐܢܫ ܐܪܡܝ ܥܠܘܗܝ ܐܝܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 45, 'ܘܐܬܘ ܕܚܫܐ ܗܢܘܢ ܠܘܬ ܪܒܝ ܟܗܢܐ ܘܦܪܝܫܐ ܘܐܡܪܘ ܠܗܘܢ ܟܗܢܐ ܠܡܢܐ ܠܐ ܐܝܬܝܬܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 46, 'ܐܡܪܝܢ ܠܗܘܢ ܕܚܫܐ ܠܐ ܡܡܬܘܡ ܗܟܢܐ ܡܠܠ ܒܪܢܫܐ ܐܝܟ ܕܡܡܠܠ ܓܒܪܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 47, 'ܐܡܪܝܢ ܠܗܘܢ ܦܪܝܫܐ ܠܡܐ ܐܦ ܐܢܬܘܢ ܛܥܝܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 48, 'ܠܡܐ ܐܢܫ ܡܢ ܪܫܐ ܐܘ ܡܢ ܦܪܝܫܐ ܗܝܡܢܘ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 49, 'ܐܠܐ ܐܢ ܥܡܐ ܗܢܐ ܕܠܐ ܝܕܥ ܢܡܘܤܐ ܠܝܛܝܢ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 50, 'ܐܡܪ ܠܗܘܢ ܢܝܩܕܡܘܤ ܚܕ ܡܢܗܘܢ ܗܘ ܕܐܬܐ ܗܘܐ ܠܘܬ ܝܫܘܥ ܒܠܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 51, 'ܕܠܡܐ ܢܡܘܤܐ ܕܝܠܢ ܡܚܝܒ ܠܒܪܢܫܐ ܐܠܐ ܐܢ ܢܫܡܥ ܡܢܗ ܠܘܩܕܡ ܘܢܕܥ ܡܢܐ ܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 52, 'ܥܢܘ ܘܐܡܪܝܢ ܠܗ ܠܡܐ ܐܦ ܐܢܬ ܡܢ ܓܠܝܠܐ ܐܢܬ ܒܨܝ ܘܚܙܝ ܕܢܒܝܐ ܡܢ ܓܠܝܠܐ ܠܐ ܩܐܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 53, 'ܐܙܠ ܗܟܝܠ ܟܠܚܕ ܠܒܝܬܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 1, 'ܝܫܘܥ ܕܝܢ ܐܙܠ ܠܛܘܪܐ ܕܙܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 2, 'ܒܨܦܪܐ ܕܝܢ ܬܘܒ ܐܬܐ ܠܗܝܟܠܐ ܘܟܠܗ ܥܡܐ ܐܬܐ ܗܘܐ ܠܘܬܗ ܘܟܕ ܝܬܒ ܡܠܦ ܗܘܐ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 3, 'ܐܝܬܝܘ ܕܝܢ ܤܦܪܐ ܘܦܪܝܫܐ ܠܐܢܬܬܐ ܕܐܬܬܚܕܬ ܒܓܘܪܐ ܘܟܕ ܐܩܝܡܘܗ ܒܡܨܥܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 4, 'ܐܡܪܝܢ ܠܗ ܡܠܦܢܐ ܗܕܐ ܐܢܬܬܐ ܐܬܬܚܕܬ ܓܠܝܐܝܬ ܒܗ ܒܤܘܥܪܢܐ ܕܓܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 5, 'ܘܒܢܡܘܤܐ ܕܝܢ ܕܡܘܫܐ ܦܩܕ ܕܠܕܐܝܟ ܗܠܝܢ ܢܪܓܘܡ ܐܢܬ ܗܟܝܠ ܡܢܐ ܐܡܪ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 6, 'ܗܕܐ ܐܡܪܘ ܟܕ ܡܢܤܝܢ ܠܗ ܐܝܟܢܐ ܕܬܗܘܐ ܠܗܘܢ ܕܢܩܛܪܓܘܢܝܗܝ ܝܫܘܥ ܕܝܢ ܟܕ ܠܬܚܬ ܐܬܓܗܢ ܡܟܬܒ ܗܘܐ ܥܠ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 7, 'ܟܕ ܕܝܢ ܟܬܪܘ ܟܕ ܡܫܐܠܝܢ ܠܗ ܐܬܦܫܛ ܘܐܡܪ ܠܗܘܢ ܐܝܢܐ ܡܢܟܘܢ ܕܐܝܬܘܗܝ ܕܠܐ ܚܛܗ ܩܕܡܝܐ ܢܫܕܐ ܥܠܝܗ ܟܐܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 8, 'ܘܬܘܒ ܟܕ ܐܬܓܗܢ ܟܬܒ ܗܘܐ ܥܠ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 9, 'ܗܢܘܢ ܕܝܢ ܟܕ ܫܡܥܘ ܢܦܩܝܢ ܗܘܘ ܚܕ ܚܕ ܟܕ ܫܪܝܘ ܡܢ ܩܫܝܫܐ ܘܐܫܬܒܩܬ ܐܢܬܬܐ ܠܚܘܕܝܗ ܟܕ ܐܝܬܝܗ ܒܡܨܥܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 10, 'ܟܕ ܕܝܢ ܐܬܦܫܛ ܝܫܘܥ ܐܡܪ ܠܗ ܠܐܢܬܬܐ ܐܝܟܐ ܐܝܬܝܗܘܢ ܠܐ ܐܢܫ ܚܝܒܟܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 11, 'ܗܝ ܕܝܢ ܐܡܪܬ ܘܠܐ ܐܢܫ ܡܪܝܐ ܐܡܪ ܕܝܢ ܝܫܘܥ ܐܦܠܐ ܐܢܐ ܡܚܝܒ ܐܢܐ ܠܟܝ ܙܠܝ ܘܡܢ ܗܫܐ ܬܘܒ ܠܐ ܬܚܛܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 12, 'ܬܘܒ ܕܝܢ ܡܠܠ ܥܡܗܘܢ ܝܫܘܥ ܘܐܡܪ ܐܢܐ ܐܢܐ ܢܘܗܪܗ ܕܥܠܡܐ ܡܢ ܕܒܬܪܝ ܐܬܐ ܠܐ ܢܗܠܟ ܒܚܫܘܟܐ ܐܠܐ ܢܫܟܚ ܠܗ ܢܘܗܪܐ ܕܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 13, 'ܐܡܪܝܢ ܠܗ ܦܪܝܫܐ ܐܢܬ ܥܠ ܢܦܫܟ ܡܤܗܕ ܐܢܬ ܤܗܕܘܬܟ ܠܐ ܗܘܬ ܫܪܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 14, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܐܦܢ ܐܢܐ ܡܤܗܕ ܐܢܐ ܥܠ ܢܦܫܝ ܫܪܝܪܐ ܗܝ ܤܗܕܘܬܝ ܡܛܠ ܕܕܝܕܥ ܐܢܐ ܡܢ ܐܝܡܟܐ ܐܬܝܬ ܘܠܐܝܟܐ ܐܙܠ ܐܢܐ ܐܢܬܘܢ ܕܝܢ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܡܢ ܐܝܡܟܐ ܐܬܝܬ ܘܠܐ ܠܐܝܟܐ ܐܙܠ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 15, 'ܐܢܬܘܢ ܦܓܪܢܐܝܬ ܕܝܢܝܢ ܐܢܬܘܢ ܐܢܐ ܠܐܢܫ ܠܐ ܕܐܢ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 16, 'ܘܐܢ ܕܐܢ ܐܢܐ ܕܝܢ ܕܝܢܝ ܫܪܝܪ ܗܘ ܡܛܠ ܕܠܐ ܗܘܝܬ ܒܠܚܘܕܝ ܐܠܐ ܐܢܐ ܘܐܒܝ ܕܫܕܪܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 17, 'ܘܒܢܡܘܤܟܘܢ ܕܝܢ ܟܬܝܒ ܕܤܗܕܘܬܐ ܕܬܪܝܢ ܓܒܪܝܢ ܫܪܝܪܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 18, 'ܐܢܐ ܐܢܐ ܕܤܗܕ ܐܢܐ ܥܠ ܢܦܫܝ ܘܐܒܝ ܕܫܕܪܢܝ ܤܗܕ ܥܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 19, 'ܐܡܪܝܢ ܠܗ ܐܝܟܘ ܐܒܘܟ ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܘܠܐ ܠܝ ܝܕܥܝܢ ܐܢܬܘܢ ܘܠܐ ܠܐܒܝ ܐܠܘ ܠܝ ܝܕܥܝܢ ܗܘܝܬܘܢ ܐܦ ܠܐܒܝ ܝܕܥܝܢ ܗܘܝܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 20, 'ܗܠܝܢ ܡܠܐ ܡܠܠ ܒܝܬ ܓܙܐ ܟܕ ܡܠܦ ܒܗܝܟܠܐ ܘܠܐ ܐܢܫ ܐܚܕܗ ܠܐ ܓܝܪ ܥܕܟܝܠ ܐܬܬ ܗܘܬ ܫܥܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 21, 'ܐܡܪ ܠܗܘܢ ܬܘܒ ܝܫܘܥ ܐܢܐ ܐܙܠ ܐܢܐ ܘܬܒܥܘܢܢܝ ܘܬܡܘܬܘܢ ܒܚܛܗܝܟܘܢ ܘܐܝܟܐ ܕܐܢܐ ܐܙܠ ܐܢܐ ܐܢܬܘܢ ܠܐ ܡܫܟܚܝܢ ܐܢܬܘܢ ܠܡܐܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 22, 'ܐܡܪܝܢ ܝܗܘܕܝܐ ܠܡܐ ܟܝ ܢܦܫܗ ܩܛܠ ܕܐܡܪ ܕܐܝܟܐ ܕܐܢܐ ܐܙܠ ܐܢܐ ܐܢܬܘܢ ܠܐ ܡܫܟܚܝܢ ܐܢܬܘܢ ܠܡܐܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 23, 'ܘܐܡܪ ܠܗܘܢ ܐܢܬܘܢ ܡܢ ܕܠܬܚܬ ܐܢܬܘܢ ܘܐܢܐ ܡܢ ܕܠܥܠ ܐܢܐ ܐܢܬܘܢ ܡܢ ܗܢܐ ܐܢܬܘܢ ܥܠܡܐ ܐܢܐ ܠܐ ܗܘܝܬ ܡܢ ܗܢܐ ܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 24, 'ܐܡܪܬ ܠܟܘܢ ܕܬܡܘܬܘܢ ܒܚܛܗܝܟܘܢ ܐܠܐ ܓܝܪ ܬܗܝܡܢܘܢ ܕܐܢܐ ܐܢܐ ܬܡܘܬܘܢ ܒܚܛܗܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 25, 'ܐܡܪܝܢ ܝܗܘܕܝܐ ܐܢܬ ܡܢ ܐܢܬ ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܦܢ ܕܫܪܝܬ ܕܐܡܠܠ ܥܡܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 26, 'ܤܓܝ ܐܝܬ ܠܝ ܥܠܝܟܘܢ ܠܡܐܡܪ ܘܠܡܕܢ ܐܠܐ ܡܢ ܕܫܕܪܢܝ ܫܪܝܪ ܗܘ ܘܐܢܐ ܐܝܠܝܢ ܕܫܡܥܬ ܡܢܗ ܗܠܝܢ ܗܘ ܡܡܠܠ ܐܢܐ ܒܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 27, 'ܘܠܐ ܝܕܥܘ ܕܥܠ ܐܒܐ ܐܡܪ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 28, 'ܐܡܪ ܠܗܘܢ ܬܘܒ ܝܫܘܥ ܐܡܬܝ ܕܬܪܝܡܘܢܗ ܠܒܪܗ ܕܐܢܫܐ ܗܝܕܝܢ ܬܕܥܘܢ ܕܐܢܐ ܐܢܐ ܘܡܕܡ ܡܢ ܨܒܘܬ ܢܦܫܝ ܠܐ ܥܒܕ ܐܢܐ ܐܠܐ ܐܝܟܢܐ ܕܐܠܦܢܝ ܐܒܝ ܗܟܘܬ ܗܘ ܡܡܠܠ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 29, 'ܘܡܢ ܕܫܕܪܢܝ ܥܡܝ ܐܝܬܘܗܝ ܘܠܐ ܫܒܩܢܝ ܒܠܚܘܕܝ ܐܒܝ ܡܛܠ ܕܐܢܐ ܡܕܡ ܕܫܦܪ ܠܗ ܥܒܕ ܐܢܐ ܒܟܠܙܒܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 30, 'ܟܕ ܗܠܝܢ ܡܡܠܠ ܗܘܐ ܤܓܝܐܐ ܗܝܡܢܘ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 31, 'ܘܐܡܪ ܝܫܘܥ ܠܗܢܘܢ ܝܗܘܕܝܐ ܕܗܝܡܢܘ ܒܗ ܐܢ ܐܢܬܘܢ ܬܟܬܪܘܢ ܒܡܠܬܝ ܫܪܝܪܐܝܬ ܬܠܡܝܕܝ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 32, 'ܘܬܕܥܘܢ ܫܪܪܐ ܘܗܘ ܫܪܪܐ ܢܚܪܪܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 33, 'ܐܡܪܝܢ ܠܗ ܙܪܥܗ ܚܢܢ ܕܐܒܪܗܡ ܘܡܢ ܡܬܘܡ ܥܒܕܘܬܐ ܠܐܢܫ ܠܐ ܦܠܝܚܐ ܠܢ ܐܝܟܢܐ ܐܡܪ ܐܢܬ ܕܬܗܘܘܢ ܒܢܝ ܚܐܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 34, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܡܝܢ ܐܡܝܢ ܐܡܪܢܐ ܠܟܘܢ ܕܟܠܡܢ ܕܥܒܕ ܚܛܝܬܐ ܥܒܕܗ ܗܘ ܕܚܛܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 35, 'ܘܥܒܕܐ ܠܐ ܡܩܘܐ ܠܥܠܡ ܒܒܝܬܐ ܒܪܐ ܕܝܢ ܠܥܠܡ ܡܩܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 36, 'ܐܢ ܗܘ ܗܟܝܠ ܕܒܪܐ ܢܚܪܪܟܘܢ ܫܪܝܪܐܝܬ ܬܗܘܘܢ ܒܢܝ ܚܐܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 37, 'ܝܕܥ ܐܢܐ ܕܙܪܥܗ ܐܢܬܘܢ ܕܐܒܪܗܡ ܐܠܐ ܒܥܝܢ ܐܢܬܘܢ ܠܡܩܛܠܢܝ ܡܛܠ ܕܠܡܠܬܝ ܠܐ ܤܦܩܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 38, 'ܐܢܐ ܡܕܡ ܕܚܙܝܬ ܠܘܬ ܐܒܝ ܡܡܠܠ ܐܢܐ ܘܐܢܬܘܢ ܡܕܡ ܕܚܙܝܬܘܢ ܠܘܬ ܐܒܘܟܘܢ ܥܒܕܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 39, 'ܥܢܘ ܘܐܡܪܝܢ ܠܗ ܐܒܘܢ ܕܝܠܢ ܐܒܪܗܡ ܗܘ ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܠܘ ܒܢܘܗܝ ܗܘܝܬܘܢ ܕܐܒܪܗܡ ܥܒܕܘܗܝ ܕܐܒܪܗܡ ܥܒܕܝܢ ܗܘܝܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 40, 'ܗܫܐ ܕܝܢ ܗܐ ܒܥܝܢ ܐܢܬܘܢ ܠܡܩܛܠܢܝ ܠܓܒܪܐ ܕܫܪܝܪܬܐ ܡܠܠܬ ܥܡܟܘܢ ܐܝܕܐ ܕܫܡܥܬ ܡܢ ܐܠܗܐ ܗܕܐ ܐܒܪܗܡ ܠܐ ܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 41, 'ܐܢܬܘܢ ܕܝܢ ܥܒܕܝܢ ܐܢܬܘܢ ܥܒܕܐ ܕܐܒܘܟܘܢ ܐܡܪܝܢ ܠܗ ܚܢܢ ܡܢ ܙܢܝܘܬܐ ܠܐ ܗܘܝܢ ܚܕ ܐܒܐ ܐܝܬ ܠܢ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 42, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܠܘ ܐܠܗܐ ܗܘܐ ܐܒܘܟܘܢ ܡܚܒܝܢ ܗܘܝܬܘܢ ܠܝ ܐܢܐ ܓܝܪ ܡܢ ܐܠܗܐ ܢܦܩܬ ܘܐܬܝܬ ܘܠܐ ܗܘܐ ܡܢ ܨܒܘܬ ܢܦܫܝ ܐܬܝܬ ܐܠܐ ܗܘ ܫܕܪܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 43, 'ܡܛܠ ܡܢܐ ܡܠܬܝ ܠܐ ܡܫܬܘܕܥܝܢ ܐܢܬܘܢ ܥܠ ܕܠܐ ܡܫܟܚܝܢ ܐܢܬܘܢ ܫܡܥܝܢ ܡܠܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 44, 'ܐܢܬܘܢ ܡܢ ܐܒܐ ܐܟܠܩܪܨܐ ܐܝܬܝܟܘܢ ܘܪܓܬܗ ܕܐܒܘܟܘܢ ܨܒܝܢ ܐܢܬܘܢ ܠܡܥܒܕ ܗܘ ܕܡܢ ܒܪܫܝܬ ܩܛܠ ܐܢܫܐ ܗܘ ܘܒܫܪܪܐ ܠܐ ܩܐܡ ܡܛܠ ܕܫܪܪܐ ܠܝܬ ܒܗ ܐܡܬܝ ܕܡܡܠܠ ܟܕܒܘܬܐ ܡܢ ܕܝܠܗ ܗܘ ܡܡܠܠ ܡܛܠ ܕܕܓܠܐ ܗܘ ܐܦ ܐܒܘܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 45, 'ܐܢܐ ܕܝܢ ܕܫܪܪܐ ܡܡܠܠ ܐܢܐ ܠܐ ܡܗܝܡܢܝܢ ܐܢܬܘܢ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 46, 'ܡܢܘ ܡܢܟܘܢ ܡܟܤ ܠܝ ܥܠ ܚܛܝܬܐ ܘܐܢ ܫܪܪܐ ܡܡܠܠ ܐܢܐ ܐܢܬܘܢ ܠܡܢܐ ܠܐ ܡܗܝܡܢܝܢ ܐܢܬܘܢ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 47, 'ܡܢ ܕܡܢ ܐܠܗܐ ܐܝܬܘܗܝ ܡܠܐ ܕܐܠܗܐ ܫܡܥ ܡܛܠ ܗܢܐ ܐܢܬܘܢ ܠܐ ܫܡܥܝܢ ܐܢܬܘܢ ܡܛܠ ܕܠܐ ܗܘܝܬܘܢ ܡܢ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 48, 'ܥܢܘ ܝܗܘܕܝܐ ܘܐܡܪܝܢ ܠܗ ܠܐ ܫܦܝܪ ܐܡܪܝܢ ܐܢܚܢܢ ܕܫܡܪܝܐ ܐܢܬ ܘܕܝܘܐ ܐܝܬ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 49, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܠܝ ܕܝܘܐ ܠܐ ܐܝܬ ܐܠܐ ܠܐܒܝ ܡܝܩܪ ܐܢܐ ܘܐܢܬܘܢ ܡܨܥܪܝܢ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 50, 'ܐܢܐ ܕܝܢ ܠܐ ܒܥܐ ܐܢܐ ܫܘܒܚܝ ܐܝܬ ܗܘ ܕܒܥܐ ܘܕܐܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 51, 'ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܡܢ ܕܡܠܬܝ ܢܛܪ ܡܘܬܐ ܠܐ ܢܚܙܐ ܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 52, 'ܐܡܪܝܢ ܠܗ ܝܗܘܕܝܐ ܗܫܐ ܝܕܥܢ ܕܕܝܘܐ ܐܝܬ ܠܟ ܐܒܪܗܡ ܡܝܬ ܘܢܒܝܐ ܘܐܢܬ ܐܡܪ ܐܢܬ ܕܡܢ ܕܡܠܬܝ ܢܛܪ ܡܘܬܐ ܠܐ ܢܛܥܡ ܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 53, 'ܠܡܐ ܐܢܬ ܪܒ ܐܢܬ ܡܢ ܐܒܘܢ ܐܒܪܗܡ ܕܡܝܬ ܘܡܢ ܢܒܝܐ ܕܡܝܬܘ ܡܢܘ ܥܒܕ ܐܢܬ ܢܦܫܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 54, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܢ ܐܢܐ ܡܫܒܚ ܐܢܐ ܢܦܫܝ ܫܘܒܚܝ ܠܐ ܗܘܐ ܡܕܡ ܐܝܬܘܗܝ ܐܒܝ ܕܡܫܒܚ ܠܝ ܗܘ ܕܐܡܪܝܢ ܐܢܬܘܢ ܕܐܠܗܢ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 55, 'ܘܠܐ ܝܕܥܬܘܢܝܗܝ ܐܢܐ ܕܝܢ ܝܕܥ ܐܢܐ ܠܗ ܘܐܢ ܐܡܪ ܐܢܐ ܕܠܐ ܝܕܥ ܐܢܐ ܠܗ ܗܘܐ ܐܢܐ ܠܝ ܟܕܒܐ ܐܟܘܬܟܘܢ ܐܠܐ ܝܕܥ ܐܢܐ ܠܗ ܘܡܠܬܗ ܢܛܪ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 56, 'ܐܒܪܗܡ ܐܒܘܟܘܢ ܡܤܘܚ ܗܘܐ ܕܢܚܙܐ ܝܘܡܝ ܘܚܙܐ ܘܚܕܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 57, 'ܐܡܪܝܢ ܠܗ ܝܗܘܕܝܐ ܥܕܟܝܠ ܒܪ ܚܡܫܝܢ ܫܢܝܢ ܠܐ ܗܘܝܬ ܘܠܐܒܪܗܡ ܚܙܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 58, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܥܕܠܐ ܢܗܘܐ ܐܒܪܗܡ ܐܢܐ ܐܝܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 59, 'ܘܫܩܠܘ ܟܐܦܐ ܕܢܪܓܡܘܢܝܗܝ ܘܝܫܘܥ ܐܬܛܫܝ ܘܢܦܩ ܡܢ ܗܝܟܠܐ ܘܥܒܪ ܒܝܢܬܗܘܢ ܘܐܙܠ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 1, 'ܘܟܕ ܥܒܪ ܚܙܐ ܓܒܪܐ ܤܡܝܐ ܕܡܢ ܟܪܤ ܐܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 2, 'ܘܫܐܠܘܗܝ ܬܠܡܝܕܘܗܝ ܘܐܡܪܝܢ ܪܒܢ ܡܢܘ ܚܛܐ ܗܢܐ ܐܘ ܐܒܗܘܗܝ ܕܟܕ ܤܡܐ ܢܬܝܠܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 3, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܠܐ ܗܘ ܚܛܐ ܘܠܐ ܐܒܗܘܗܝ ܐܠܐ ܕܢܬܚܙܘܢ ܒܗ ܥܒܕܘܗܝ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 4, 'ܠܝ ܘܠܐ ܠܡܥܒܕ ܥܒܕܐ ܕܡܢ ܕܫܕܪܢܝ ܥܕ ܐܝܡܡܐ ܗܘ ܐܬܐ ܠܠܝܐ ܕܐܢܫ ܠܐ ܡܫܟܚ ܠܡܦܠܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 5, 'ܟܡܐ ܕܒܥܠܡܐ ܐܢܐ ܢܘܗܪܗ ܐܢܐ ܕܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 6, 'ܘܟܕ ܐܡܪ ܗܠܝܢ ܪܩ ܥܠ ܐܪܥܐ ܘܓܒܠ ܛܝܢܐ ܡܢ ܪܘܩܗ ܘܛܫ ܥܠ ܥܝܢܘܗܝ ܕܗܘ ܤܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 7, 'ܘܐܡܪ ܠܗ ܙܠ ܐܫܝܓ ܒܡܥܡܘܕܝܬܐ ܕܫܝܠܘܚܐ ܘܐܙܠ ܐܫܝܓ ܘܐܬܐ ܟܕ ܚܙܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 8, 'ܫܒܒܘܗܝ ܕܝܢ ܘܐܝܠܝܢ ܕܚܙܐ ܗܘܐ ܠܗܘܢ ܡܢ ܩܕܝܡ ܕܚܕܪ ܗܘܐ ܐܡܪܝܢ ܗܘܘ ܠܐ ܗܘܐ ܗܢܘ ܗܘ ܕܝܬܒ ܗܘܐ ܘܚܕܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 9, 'ܐܝܬ ܕܐܡܪܝܢ ܗܘܘ ܕܗܘܝܘ ܘܐܝܬ ܕܐܡܪܝܢ ܗܘܘ ܠܐ ܐܠܐ ܡܕܡܐ ܕܡܐ ܠܗ ܗܘ ܕܝܢ ܐܡܪ ܗܘܐ ܕܐܢܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 10, 'ܐܡܪܝܢ ܠܗ ܐܝܟܢܐ ܐܬܦܬܚ ܥܝܢܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 11, 'ܥܢܐ ܘܐܡܪ ܠܗܘܢ ܓܒܪܐ ܕܫܡܗ ܝܫܘܥ ܥܒܕ ܛܝܢܐ ܘܛܫ ܠܝ ܥܠ ܥܝܢܝ ܘܐܡܪ ܠܝ ܙܠ ܐܫܝܓ ܒܡܝܐ ܕܫܝܠܘܚܐ ܘܐܙܠܬ ܐܫܝܓܬ ܘܐܬܚܙܝ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 12, 'ܐܡܪܝܢ ܠܗ ܐܝܟܘ ܐܡܪ ܠܗܘܢ ܠܐ ܝܕܥ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 13, 'ܘܐܝܬܝܘܗܝ ܠܗܘ ܕܡܢ ܩܕܝܡ ܤܡܝܐ ܗܘܐ ܠܘܬ ܦܪܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 14, 'ܐܝܬܝܗ ܗܘܬ ܕܝܢ ܫܒܬܐ ܟܕ ܥܒܕ ܛܝܢܐ ܝܫܘܥ ܘܦܬܚ ܠܗ ܥܝܢܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 15, 'ܘܬܘܒ ܫܐܠܘܗܝ ܦܪܝܫܐ ܐܝܟܢܐ ܐܬܚܙܝ ܠܟ ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܛܝܢܐ ܤܡ ܥܠ ܥܝܢܝ ܘܐܫܝܓܬ ܘܐܬܚܙܝ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 16, 'ܘܐܡܪܝܢ ܗܘܘ ܐܢܫܐ ܡܢ ܦܪܝܫܐ ܗܢܐ ܓܒܪܐ ܠܐ ܗܘܐ ܡܢ ܐܠܗܐ ܗܘ ܕܫܒܬܐ ܠܐ ܢܛܪ ܐܚܪܢܐ ܕܝܢ ܐܡܪܝܢ ܗܘܘ ܐܝܟܢܐ ܡܫܟܚ ܓܒܪܐ ܚܛܝܐ ܗܠܝܢ ܐܬܘܬܐ ܠܡܥܒܕ ܘܦܠܓܘܬܐ ܐܝܬ ܗܘܬ ܒܝܢܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 17, 'ܐܡܪܝܢ ܬܘܒ ܠܗܘ ܤܡܝܐ ܐܢܬ ܡܢܐ ܐܡܪ ܐܢܬ ܥܠܘܗܝ ܕܦܬܚ ܠܟ ܥܝܢܝܟ ܐܡܪ ܠܗܘܢ ܐܢܐ ܐܡܪ ܐܢܐ ܕܢܒܝܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 18, 'ܠܐ ܕܝܢ ܗܝܡܢܘ ܗܘܘ ܥܠܘܗܝ ܝܗܘܕܝܐ ܕܤܡܝܐ ܗܘܐ ܘܚܙܐ ܥܕܡܐ ܕܩܪܘ ܠܐܒܗܘܗܝ ܕܗܘ ܕܚܙܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 19, 'ܘܫܐܠܘ ܐܢܘܢ ܐܢ ܗܢܘ ܒܪܟܘܢ ܗܘ ܕܐܢܬܘܢ ܐܡܪܝܢ ܐܢܬܘܢ ܕܟܕ ܤܡܐ ܐܬܝܠܕ ܐܝܟܢܐ ܗܫܐ ܚܙܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 20, 'ܥܢܘ ܕܝܢ ܐܒܗܘܗܝ ܘܐܡܪܘ ܝܕܥܝܢܢ ܕܗܢܘ ܒܪܢ ܘܕܟܕ ܤܡܐ ܐܬܝܠܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 21, 'ܐܝܟܢܐ ܕܝܢ ܗܫܐ ܚܙܐ ܐܘ ܡܢܘ ܦܬܚ ܠܗ ܥܝܢܘܗܝ ܠܐ ܝܕܥܝܢܢ ܐܦ ܗܘ ܥܠ ܠܗ ܠܫܢܘܗܝ ܠܗ ܫܐܠܘ ܗܘ ܚܠܦ ܢܦܫܗ ܢܡܠܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 22, 'ܗܠܝܢ ܐܡܪܘ ܐܒܗܘܗܝ ܡܛܠ ܕܕܚܠܝܢ ܗܘܘ ܡܢ ܝܗܘܕܝܐ ܦܤܩܘ ܗܘܘ ܓܝܪ ܝܗܘܕܝܐ ܕܐܢ ܐܢܫ ܢܘܕܐ ܒܗ ܕܡܫܝܚܐ ܗܘ ܢܦܩܘܢܝܗܝ ܡܢ ܟܢܘܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 23, 'ܡܛܠ ܗܢܐ ܐܡܪܘ ܐܒܗܘܗܝ ܕܥܠ ܠܗ ܠܫܢܘܗܝ ܠܗ ܫܐܠܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 24, 'ܘܩܪܐܘܗܝ ܠܓܒܪܐ ܕܬܪܬܝܢ ܙܒܢܝܢ ܠܗܘ ܕܐܝܬܘܗܝ ܗܘܐ ܤܡܝܐ ܘܐܡܪܝܢ ܠܗ ܫܒܚ ܠܐܠܗܐ ܚܢܢ ܓܝܪ ܝܕܥܝܢܢ ܕܗܢܐ ܓܒܪܐ ܚܛܝܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 25, 'ܥܢܐ ܗܘ ܘܐܡܪ ܠܗܘܢ ܐܢ ܚܛܝܐ ܗܘ ܠܐ ܝܕܥ ܐܢܐ ܚܕܐ ܕܝܢ ܝܕܥ ܐܢܐ ܕܤܡܝܐ ܗܘܝܬ ܘܗܫܐ ܗܐ ܚܙܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 26, 'ܐܡܪܝܢ ܠܗ ܬܘܒ ܡܢܐ ܥܒܕ ܠܟ ܐܝܟܢܐ ܦܬܚ ܠܟ ܥܝܢܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 27, 'ܐܡܪ ܠܗܘܢ ܐܡܪܬ ܠܟܘܢ ܘܠܐ ܫܡܥܬܘܢ ܡܢܐ ܬܘܒ ܨܒܝܢ ܐܢܬܘܢ ܠܡܫܡܥ ܠܡܐ ܐܦ ܐܢܬܘܢ ܬܠܡܝܕܐ ܨܒܝܢ ܐܢܬܘܢ ܠܡܗܘܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 28, 'ܗܢܘܢ ܕܝܢ ܨܚܝܘܗܝ ܘܐܡܪܝܢ ܠܗ ܐܢܬ ܗܘ ܬܠܡܝܕܗ ܕܗܘ ܚܢܢ ܓܝܪ ܬܠܡܝܕܐ ܚܢܢ ܕܡܘܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 29, 'ܘܝܕܥܝܢܢ ܕܥܡ ܡܘܫܐ ܐܠܗܐ ܡܠܠ ܠܗܢܐ ܕܝܢ ܠܐ ܝܕܥܝܢܢ ܡܢ ܐܝܡܟܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 30, 'ܥܢܐ ܗܘ ܓܒܪܐ ܘܐܡܪ ܠܗܘܢ ܒܗܕܐ ܗܘ ܗܟܝܠ ܠܡܬܕܡܪܘ ܕܐܢܬܘܢ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܡܢ ܐܝܡܟܐ ܗܘ ܘܥܝܢܝ ܕܝܠܝ ܦܬܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 31, 'ܝܕܥܝܢ ܚܢܢ ܕܝܢ ܕܐܠܗܐ ܒܩܠܐ ܕܚܛܝܐ ܠܐ ܫܡܥ ܐܠܐ ܠܡܢ ܕܕܚܠ ܡܢܗ ܘܥܒܕ ܨܒܝܢܗ ܠܗܘ ܗܘ ܫܡܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 32, 'ܡܢ ܥܠܡ ܠܐ ܐܫܬܡܥܬ ܕܦܬܚ ܐܢܫ ܥܝܢܐ ܕܤܡܝܐ ܕܐܬܝܠܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 33, 'ܐܠܘ ܠܐ ܡܢ ܐܠܗܐ ܗܘܐ ܗܢܐ ܠܐ ܡܫܟܚ ܗܘܐ ܗܕܐ ܠܡܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 34, 'ܥܢܘ ܘܐܡܪܝܢ ܠܗ ܐܢܬ ܟܠܟ ܒܚܛܗܐ ܐܬܝܠܕܬ ܘܐܢܬ ܡܠܦ ܐܢܬ ܠܢ ܘܐܦܩܘܗܝ ܠܒܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 35, 'ܘܫܡܥ ܝܫܘܥ ܕܐܦܩܘܗܝ ܠܒܪ ܘܐܫܟܚܗ ܘܐܡܪ ܠܗ ܐܢܬ ܡܗܝܡܢ ܐܢܬ ܒܒܪܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 36, 'ܥܢܐ ܗܘ ܕܐܬܐܤܝ ܘܐܡܪ ܡܢܘ ܡܪܝ ܕܐܗܝܡܢ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 37, 'ܐܡܪ ܠܗ ܝܫܘܥ ܚܙܝܬܝܗܝ ܘܗܘ ܕܡܡܠܠ ܥܡܟ ܗܘܝܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 38, 'ܗܘ ܕܝܢ ܐܡܪ ܡܗܝܡܢ ܐܢܐ ܡܪܝ ܘܢܦܠ ܤܓܕ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 39, 'ܘܐܡܪ ܝܫܘܥ ܠܕܝܢܗ ܕܥܠܡܐ ܗܢܐ ܐܬܝܬ ܕܐܝܠܝܢ ܕܠܐ ܚܙܝܢ ܢܚܙܘܢ ܘܐܝܠܝܢ ܕܚܙܝܢ ܢܤܡܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 40, 'ܘܫܡܥܘ ܡܢ ܦܪܝܫܐ ܐܝܠܝܢ ܕܥܡܗ ܗܘܘ ܗܠܝܢ ܘܐܡܪܘ ܠܗ ܠܡܐ ܐܦ ܚܢܢ ܤܡܝܐ ܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 41, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܠܘ ܤܡܝܐ ܗܘܝܬܘܢ ܠܝܬ ܗܘܬ ܠܟܘܢ ܚܛܝܬܐ ܗܫܐ ܕܝܢ ܐܡܪܝܢ ܐܢܬܘܢ ܕܚܙܝܢܢ ܡܛܠ ܗܢܐ ܚܛܝܬܟܘܢ ܩܝܡܐ ܗܝ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 1, 'ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܡܢ ܕܠܐ ܥܐܠ ܡܢ ܬܪܥܐ ܠܛܝܪܐ ܕܥܢܐ ܐܠܐ ܤܠܩ ܡܢ ܕܘܟܐ ܐܚܪܢܝܐ ܗܘ ܓܢܒܐ ܗܘ ܘܓܝܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 2, 'ܗܘ ܕܝܢ ܕܥܐܠ ܡܢ ܬܪܥܐ ܪܥܝܐ ܗܘ ܕܥܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 3, 'ܘܠܗܢܐ ܢܛܪ ܬܪܥܐ ܦܬܚ ܠܗ ܬܪܥܐ ܘܥܢܐ ܫܡܥܐ ܩܠܗ ܘܥܪܒܘܗܝ ܩܪܐ ܒܫܡܗܝܗܘܢ ܘܡܦܩ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 4, 'ܘܡܐ ܕܐܦܩ ܥܢܗ ܩܕܡܝܗ ܐܙܠ ܘܥܪܒܘܗܝ ܕܝܠܗ ܐܙܠܝܢ ܒܬܪܗ ܡܛܠ ܕܝܕܥܝܢ ܩܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 5, 'ܒܬܪ ܢܘܟܪܝܐ ܕܝܢ ܠܐ ܐܙܠܐ ܥܢܐ ܐܠܐ ܥܪܩܐ ܡܢܗ ܕܠܐ ܝܕܥܐ ܩܠܗ ܕܢܘܟܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 6, 'ܗܕܐ ܦܠܐܬܐ ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܗܢܘܢ ܕܝܢ ܠܐ ܝܕܥܘ ܡܢܐ ܡܠܠ ܥܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 7, 'ܐܡܪ ܠܗܘܢ ܕܝܢ ܬܘܒ ܝܫܘܥ ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܐܢܐ ܐܢܐ ܬܪܥܗ ܕܥܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 8, 'ܘܟܠܗܘܢ ܐܝܠܝܢ ܕܐܬܘ ܓܢܒܐ ܐܢܘܢ ܘܓܝܤܐ ܐܠܐ ܠܐ ܫܡܥܬ ܐܢܘܢ ܥܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 9, 'ܐܢܐ ܐܢܐ ܬܪܥܐ ܘܒܝ ܐܢ ܐܢܫ ܢܥܘܠ ܢܚܐ ܘܢܥܘܠ ܘܢܦܘܩ ܘܪܥܝܐ ܢܫܟܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 10, 'ܓܢܒܐ ܠܐ ܐܬܐ ܐܠܐ ܕܢܓܢܘܒ ܘܕܢܩܛܘܠ ܘܕܢܘܒܕ ܐܢܐ ܐܬܝܬ ܕܚܝܐ ܢܗܘܘܢ ܠܗܘܢ ܘܡܕܡ ܕܝܬܝܪ ܢܗܘܐ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 11, 'ܐܢܐ ܐܢܐ ܪܥܝܐ ܛܒܐ ܪܥܝܐ ܛܒܐ ܢܦܫܗ ܤܐܡ ܚܠܦ ܥܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 12, 'ܐܓܝܪܐ ܕܝܢ ܕܠܐ ܗܘܐ ܪܥܝܐ ܘܠܘ ܕܝܠܗ ܐܢܘܢ ܥܪܒܐ ܡܐ ܕܚܙܐ ܕܐܒܐ ܕܐܬܐ ܫܒܩ ܥܢܐ ܘܥܪܩ ܘܐܬܐ ܕܐܒܐ ܚܛܦ ܘܡܒܕܪ ܠܗ ܠܥܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 13, 'ܐܓܝܪܐ ܕܝܢ ܥܪܩ ܡܛܠ ܕܐܓܝܪܐ ܗܘ ܘܠܐ ܒܛܝܠ ܠܗ ܥܠ ܥܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 14, 'ܐܢܐ ܐܢܐ ܪܥܝܐ ܛܒܐ ܘܝܕܥ ܐܢܐ ܠܕܝܠܝ ܘܡܬܝܕܥ ܐܢܐ ܡܢ ܕܝܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 15, 'ܐܝܟܢܐ ܕܝܕܥ ܠܝ ܐܒܝ ܘܐܢܐ ܝܕܥ ܐܢܐ ܠܐܒܝ ܘܢܦܫܝ ܤܐܡ ܐܢܐ ܚܠܦ ܥܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 16, 'ܐܝܬ ܠܝ ܕܝܢ ܐܦ ܥܪܒܐ ܐܚܪܢܐ ܐܝܠܝܢ ܕܠܐ ܗܘܘ ܡܢ ܛܝܪܐ ܗܢܐ ܘܐܦ ܠܗܘܢ ܘܠܐ ܠܝ ܠܡܝܬܝܘ ܐܢܘܢ ܘܢܫܡܥܘܢ ܩܠܝ ܘܬܗܘܐ ܥܢܐ ܟܠܗ ܚܕܐ ܘܚܕ ܪܥܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 17, 'ܡܛܠ ܗܢܐ ܐܒܝ ܪܚܡ ܠܝ ܕܐܢܐ ܤܐܡ ܐܢܐ ܢܦܫܝ ܕܬܘܒ ܐܤܒܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 18, 'ܠܐ ܗܘܐ ܐܢܫ ܫܩܠ ܠܗ ܡܢܝ ܐܠܐ ܐܢܐ ܤܐܡ ܐܢܐ ܠܗ ܡܢ ܨܒܝܢܝ ܫܠܝܛ ܐܢܐ ܓܝܪ ܕܐܤܝܡܝܗ ܘܫܠܝܛ ܐܢܐ ܕܬܘܒ ܐܤܒܝܗ ܕܗܢܐ ܦܘܩܕܢܐ ܩܒܠܬ ܡܢ ܐܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 19, 'ܘܗܘܬ ܬܘܒ ܦܠܓܘܬܐ ܒܝܢܝ ܝܗܘܕܝܐ ܡܛܠ ܡܠܐ ܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 20, 'ܘܐܡܪܝܢ ܗܘܘ ܤܓܝܐܐ ܡܢܗܘܢ ܕܕܝܘܐ ܐܝܬ ܠܗ ܘܡܫܢܐ ܫܢܐ ܡܢܐ ܫܡܥܝܢ ܐܢܬܘܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 21, 'ܐܚܪܢܐ ܕܝܢ ܐܡܪܝܢ ܗܘܘ ܗܠܝܢ ܡܠܐ ܠܐ ܗܘܝ ܕܕܝܘܢܐ ܠܡܐ ܕܝܘܐ ܡܫܟܚ ܥܝܢܐ ܕܤܡܝܐ ܠܡܦܬܚܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 22, 'ܗܘܐ ܕܝܢ ܥܐܕܐ ܕܚܘܕܬܐ ܒܐܘܪܫܠܡ ܘܤܬܘܐ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 23, 'ܘܡܗܠܟ ܗܘܐ ܝܫܘܥ ܒܗܝܟܠܐ ܒܐܤܛܘܐ ܕܫܠܝܡܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 24, 'ܘܚܕܪܘܗܝ ܝܗܘܕܝܐ ܘܐܡܪܝܢ ܠܗ ܥܕܡܐ ܠܐܡܬܝ ܢܤܒ ܐܢܬ ܢܦܫܢ ܐܢ ܐܢܬ ܗܘ ܡܫܝܚܐ ܐܡܪ ܠܢ ܓܠܝܐܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 25, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܐܡܪܬ ܠܟܘܢ ܘܠܐ ܡܗܝܡܢܝܢ ܐܢܬܘܢ ܘܥܒܕܐ ܕܐܢܐ ܥܒܕ ܐܢܐ ܒܫܡܗ ܕܐܒܝ ܗܢܘܢ ܤܗܕܝܢ ܥܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 26, 'ܐܠܐ ܐܢܬܘܢ ܠܐ ܡܗܝܡܢܝܢ ܐܢܬܘܢ ܡܛܠ ܕܠܐ ܗܘܝܬܘܢ ܡܢ ܥܪܒܝ ܐܝܟܢܐ ܕܐܡܪܬ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 27, 'ܥܪܒܐ ܕܝܠܝ ܩܠܝ ܫܡܥܝܢ ܘܐܢܐ ܝܕܥ ܐܢܐ ܠܗܘܢ ܘܗܢܘܢ ܐܬܝܢ ܒܬܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 28, 'ܘܐܢܐ ܝܗܒ ܐܢܐ ܠܗܘܢ ܚܝܐ ܕܠܥܠܡ ܘܠܐ ܢܐܒܕܘܢ ܠܥܠܡ ܘܠܐ ܐܢܫ ܢܚܛܘܦ ܐܢܘܢ ܡܢ ܐܝܕܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 29, 'ܐܒܝ ܓܝܪ ܕܝܗܒ ܠܝ ܡܢ ܟܠ ܪܒ ܗܘ ܘܠܐ ܐܢܫ ܡܫܟܚ ܕܡܢ ܐܝܕܗ ܕܐܒܝ ܢܚܛܘܦ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 30, 'ܐܢܐ ܘܐܒܝ ܚܕ ܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 31, 'ܘܫܩܠܘ ܬܘܒ ܝܗܘܕܝܐ ܟܐܦܐ ܠܡܪܓܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 32, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܤܓܝܐܐ ܥܒܕܐ ܫܦܝܪܐ ܡܢ ܠܘܬ ܐܒܝ ܚܘܝܬܟܘܢ ܡܛܠ ܐܝܢܐ ܥܒܕܐ ܡܢܗܘܢ ܪܓܡܝܢ ܐܢܬܘܢ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 33, 'ܐܡܪܝܢ ܠܗ ܝܗܘܕܝܐ ܠܐ ܗܘܐ ܡܛܠ ܥܒܕܐ ܫܦܝܪܐ ܪܓܡܝܢ ܚܢܢ ܠܟ ܐܠܐ ܡܛܠ ܕܡܓܕܦ ܐܢܬ ܘܟܕ ܐܝܬܝܟ ܒܪ ܐܢܫܐ ܥܒܕ ܐܢܬ ܢܦܫܟ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 34, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܠܐ ܗܘܐ ܗܟܢܐ ܟܬܝܒ ܒܢܡܘܤܟܘܢ ܕܐܢܐ ܐܡܪܬ ܕܐܠܗܐ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 35, 'ܐܢ ܠܗܢܘܢ ܐܡܪ ܐܠܗܐ ܡܛܠ ܕܠܘܬܗܘܢ ܗܘܬ ܡܠܬܐ ܕܐܠܗܐ ܘܠܐ ܡܫܟܚ ܟܬܒܐ ܕܢܫܬܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 36, 'ܠܐܝܢܐ ܕܐܒܐ ܩܕܫܗ ܘܫܕܪܗ ܠܥܠܡܐ ܐܢܬܘܢ ܐܡܪܝܢ ܐܢܬܘܢ ܕܡܓܕܦ ܐܢܬ ܥܠ ܕܐܡܪܬ ܠܟܘܢ ܕܒܪܗ ܐܢܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 37, 'ܐܠܐ ܥܒܕ ܐܢܐ ܥܒܕܐ ܕܐܒܝ ܠܐ ܬܗܝܡܢܘܢܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 38, 'ܐܢ ܕܝܢ ܥܒܕ ܐܢܐ ܐܦܢ ܠܝ ܠܐ ܡܗܝܡܢܝܢ ܐܢܬܘܢ ܠܗܘܢ ܠܥܒܕܐ ܗܝܡܢܘ ܕܬܕܥܘܢ ܘܬܗܝܡܢܘܢ ܕܐܒܝ ܒܝ ܘܐܢܐ ܒܐܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 39, 'ܘܒܥܘ ܗܘܘ ܬܘܒ ܕܢܐܚܕܘܢܝܗܝ ܘܢܦܩ ܠܗ ܡܢ ܒܝܬ ܐܝܕܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 40, 'ܘܐܙܠ ܠܗ ܠܥܒܪܐ ܕܝܘܪܕܢܢ ܠܕܘܟܬܐ ܐܝܟܐ ܕܐܝܬܘܗܝ ܗܘܐ ܝܘܚܢܢ ܡܢ ܩܕܝܡ ܟܕ ܡܥܡܕ ܗܘܐ ܘܗܘܐ ܬܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 41, 'ܘܐܬܘ ܐܢܫܐ ܤܓܝܐܐ ܠܘܬܗ ܘܐܡܪܝܢ ܗܘܘ ܕܝܘܚܢܢ ܐܦ ܠܐ ܚܕܐ ܐܬܐ ܥܒܕ ܟܠܡܕܡ ܕܝܢ ܕܐܡܪ ܝܘܚܢܢ ܥܠ ܓܒܪܐ ܗܢܐ ܫܪܝܪ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 42, 'ܘܤܓܝܐܐ ܗܝܡܢܘ ܒܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 1, 'ܐܝܬ ܗܘܐ ܕܝܢ ܚܕ ܕܟܪܝܗ ܠܥܙܪ ܡܢ ܒܝܬ ܥܢܝܐ ܩܪܝܬܐ ܐܚܘܗ ܕܡܪܝܡ ܘܕܡܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 2, 'ܡܪܝܡ ܕܝܢ ܗܕܐ ܐܝܬܝܗ ܗܝ ܕܡܫܚܬ ܒܒܤܡܐ ܪܓܠܘܗܝ ܕܝܫܘܥ ܘܫܘܝܬ ܒܤܥܪܗ ܐܚܘܗ ܗܘܐ ܕܗܕܐ ܠܥܙܪ ܕܟܪܝܗ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 3, 'ܘܫܕܪܝܢ ܬܪܬܝܗܝܢ ܐܚܘܬܗ ܠܘܬ ܝܫܘܥ ܘܐܡܪܢ ܡܪܢ ܗܐ ܗܘ ܕܪܚܡ ܐܢܬ ܟܪܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 4, 'ܝܫܘܥ ܕܝܢ ܐܡܪ ܗܢܐ ܟܘܪܗܢܐ ܠܐ ܗܘܐ ܕܡܘܬܐ ܐܠܐ ܚܠܦ ܬܫܒܘܚܬܐ ܕܐܠܗܐ ܕܢܫܬܒܚ ܒܪܗ ܕܐܠܗܐ ܡܛܠܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 5, 'ܡܚܒ ܗܘܐ ܕܝܢ ܗܘ ܝܫܘܥ ܠܡܪܬܐ ܘܠܡܪܝܡ ܘܠܠܥܙܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 6, 'ܘܟܕ ܫܡܥ ܕܟܪܝܗ ܟܬܪ ܒܕܘܟܬܐ ܕܐܝܬܘܗܝ ܗܘܐ ܬܪܝܢ ܝܘܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 7, 'ܘܒܬܪܟܢ ܐܡܪ ܠܬܠܡܝܕܘܗܝ ܬܘ ܢܐܙܠ ܬܘܒ ܠܝܗܘܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 8, 'ܐܡܪܝܢ ܠܗ ܬܠܡܝܕܘܗܝ ܪܒܢ ܗܫܐ ܝܗܘܕܝܐ ܒܥܝܢ ܗܘܘ ܠܡܪܓܡܟ ܘܬܘܒ ܐܙܠ ܐܢܬ ܠܬܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 9, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܠܐ ܬܪܬܥܤܪܐ ܫܥܝܢ ܐܝܬ ܒܝܘܡܐ ܘܐܢ ܐܢܫ ܡܗܠܟ ܒܐܝܡܡܐ ܠܐ ܡܬܬܩܠ ܡܛܠ ܕܚܙܐ ܢܘܗܪܗ ܕܥܠܡܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 10, 'ܐܢ ܐܢܫ ܕܝܢ ܒܠܠܝܐ ܢܗܠܟ ܡܬܬܩܠ ܡܛܠ ܕܢܗܝܪܐ ܠܝܬ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 11, 'ܗܠܝܢ ܐܡܪ ܝܫܘܥ ܘܒܬܪܟܢ ܐܡܪ ܠܗܘܢ ܠܥܙܪ ܪܚܡܢ ܫܟܒ ܐܠܐ ܐܙܠ ܐܢܐ ܕܐܥܝܪܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 12, 'ܐܡܪܝܢ ܠܗ ܬܠܡܝܕܘܗܝ ܡܪܢ ܐܢ ܕܡܟ ܡܬܚܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 13, 'ܗܘ ܕܝܢ ܝܫܘܥ ܐܡܪ ܥܠ ܡܘܬܗ ܘܗܢܘܢ ܤܒܪܘ ܕܥܠ ܡܕܡܟܐ ܗܘ ܕܫܢܬܐ ܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 14, 'ܗܝܕܝܢ ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܦܫܝܩܐܝܬ ܠܥܙܪ ܡܝܬ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 15, 'ܘܚܕܐ ܐܢܐ ܕܠܐ ܗܘܝܬ ܬܡܢ ܡܛܠܬܟܘܢ ܕܬܗܝܡܢܘܢ ܐܠܐ ܗܠܟܘ ܠܬܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 16, 'ܐܡܪ ܬܐܘܡܐ ܕܡܬܐܡܪ ܬܐܡܐ ܠܬܠܡܝܕܐ ܚܒܪܘܗܝ ܢܐܙܠ ܐܦ ܚܢܢ ܢܡܘܬ ܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 17, 'ܘܐܬܐ ܝܫܘܥ ܠܒܝܬ ܥܢܝܐ ܘܐܫܟܚ ܕܐܪܒܥܐ ܠܗ ܝܘܡܝܢ ܕܐܝܬܘܗܝ ܒܒܝܬ ܩܒܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 18, 'ܐܝܬܝܗ ܗܘܬ ܕܝܢ ܒܝܬ ܥܢܝܐ ܥܠ ܓܢܒ ܐܘܪܫܠܡ ܟܕ ܦܪܝܩܐ ܡܢܗ ܐܝܟ ܐܤܛܕܘܬܐ ܚܡܫܬܥܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 19, 'ܘܤܓܝܐܐ ܡܢ ܝܗܘܕܝܐ ܐܬܝܐܝܢ ܗܘܘ ܠܘܬ ܡܪܬܐ ܘܡܪܝܡ ܕܢܡܠܘܢ ܒܠܒܗܝܢ ܡܛܠ ܐܚܘܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 20, 'ܡܪܬܐ ܕܝܢ ܟܕ ܫܡܥܬ ܕܝܫܘܥ ܐܬܐ ܢܦܩܬ ܠܐܘܪܥܗ ܡܪܝܡ ܕܝܢ ܒܒܝܬܐ ܝܬܒܐ ܗܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 21, 'ܘܐܡܪܬ ܡܪܬܐ ܠܝܫܘܥ ܡܪܝ ܐܠܘ ܬܢܢ ܗܘܝܬ ܠܐ ܡܐܬ ܗܘܐ ܐܚܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 22, 'ܐܠܐ ܐܦ ܗܫܐ ܝܕܥܐ ܐܢܐ ܕܟܡܐ ܕܬܫܐܠ ܠܐܠܗܐ ܝܗܒ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 23, 'ܐܡܪ ܠܗ ܝܫܘܥ ܩܐܡ ܐܚܘܟܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 24, 'ܐܡܪܐ ܠܗ ܡܪܬܐ ܝܕܥܐܢܐ ܕܩܐܡ ܒܢܘܚܡܐ ܒܝܘܡܐ ܐܚܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 25, 'ܐܡܪ ܠܗ ܝܫܘܥ ܐܢܐ ܐܢܐ ܢܘܚܡܐ ܘܚܝܐ ܡܢ ܕܡܗܝܡܢ ܒܝ ܐܦܢ ܢܡܘܬ ܢܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 26, 'ܘܟܠ ܕܚܝ ܘܡܗܝܡܢ ܒܝ ܠܥܠܡ ܠܐ ܢܡܘܬ ܡܗܝܡܢܬܝ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 27, 'ܐܡܪܐ ܠܗ ܐܝܢ ܡܪܝ ܐܢܐ ܡܗܝܡܢܐ ܐܢܐ ܕܐܢܬ ܗܘ ܡܫܝܚܐ ܒܪܗ ܕܐܠܗܐ ܕܐܬܐ ܠܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 28, 'ܘܟܕ ܐܡܪܬ ܗܠܝܢ ܐܙܠܬ ܩܪܬ ܠܡܪܝܡ ܚܬܗ ܟܤܝܐܝܬ ܘܐܡܪܐ ܠܗ ܪܒܢ ܐܬܐ ܘܩܪܐ ܠܟܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 29, 'ܘܡܪܝܡ ܟܕ ܫܡܥܬ ܩܡܬ ܥܓܠ ܘܐܬܝܐ ܗܘܬ ܠܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 30, 'ܗܘ ܕܝܢ ܝܫܘܥ ܠܐ ܥܕܟܝܠ ܐܬܐ ܗܘܐ ܠܩܪܝܬܐ ܐܠܐ ܒܗ ܗܘܐ ܒܗܝ ܕܘܟܬܐ ܕܐܪܥܬܗ ܡܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 31, 'ܐܦ ܗܢܘܢ ܕܝܢ ܝܗܘܕܝܐ ܕܐܝܬ ܗܘܘ ܥܡܗ ܒܒܝܬܐ ܕܡܒܝܐܝܢ ܗܘܘ ܠܗ ܕܚܙܘ ܠܡܪܝܡ ܕܥܓܠ ܩܡܬ ܢܦܩܬ ܐܙܠܘ ܠܗܘܢ ܒܬܪܗ ܤܒܪܘ ܓܝܪ ܕܠܩܒܪܐ ܐܙܠܐ ܠܡܒܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 32, 'ܗܝ ܕܝܢ ܡܪܝܡ ܟܕ ܐܬܬ ܐܝܟܐ ܕܐܝܬܘܗܝ ܗܘܐ ܝܫܘܥ ܘܚܙܬܗ ܢܦܠܬ ܥܠ ܪܓܠܘܗܝ ܘܐܡܪܐ ܠܗ ܐܠܘ ܬܢܢ ܗܘܝܬ ܡܪܝ ܠܐ ܡܐܬ ܗܘܐ ܐܚܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 33, 'ܝܫܘܥ ܕܝܢ ܟܕ ܚܙܗ ܕܒܟܝܐ ܘܠܝܗܘܕܝܐ ܗܢܘܢ ܕܐܬܘ ܥܡܗ ܕܒܟܝܢ ܐܬܥܙܙ ܒܪܘܚܗ ܘܐܙܝܥ ܢܦܫܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 34, 'ܘܐܡܪ ܐܝܟܐ ܤܡܬܘܢܝܗܝ ܘܐܡܪܝܢ ܠܗ ܡܪܢ ܬܐ ܚܙܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 35, 'ܘܐܬܝܢ ܗܘܝ ܕܡܥܘܗܝ ܕܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 36, 'ܘܐܡܪܝܢ ܗܘܘ ܝܗܘܕܝܐ ܚܙܘ ܟܡܐ ܪܚܡ ܗܘܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 37, 'ܐܢܫܝܢ ܕܝܢ ܡܢܗܘܢ ܐܡܪܘ ܠܐ ܡܫܟܚ ܗܘܐ ܗܢܐ ܕܦܬܚ ܥܝܢܘܗܝ ܕܗܘ ܤܡܝܐ ܢܥܒܕ ܕܐܦ ܗܢܐ ܠܐ ܢܡܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 38, 'ܝܫܘܥ ܕܝܢ ܟܕ ܡܬܥܙܙ ܒܝܢܘܗܝ ܘܠܗ ܐܬܐ ܠܒܝܬ ܩܒܘܪܐ ܘܗܘ ܒܝܬ ܩܒܘܪܐ ܐܝܬܘܗܝ ܗܘܐ ܡܥܪܬܐ ܘܟܐܦܐ ܤܝܡܐ ܗܘܬ ܥܠ ܬܪܥܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 39, 'ܘܐܡܪ ܝܫܘܥ ܫܩܘܠܘ ܟܐܦܐ ܗܕܐ ܐܡܪܐ ܠܗ ܡܪܬܐ ܚܬܗ ܕܗܘ ܡܝܬܐ ܡܪܝ ܡܢ ܟܕܘ ܤܪܝ ܠܗ ܐܪܒܥܐ ܠܗ ܓܝܪ ܝܘܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 40, 'ܐܡܪ ܠܗ ܝܫܘܥ ܠܐ ܐܡܪܬ ܠܟܝ ܕܐܢ ܬܗܝܡܢܝܢ ܬܚܙܝܢ ܫܘܒܚܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 41, 'ܘܫܩܠܘ ܟܐܦܐ ܗܝ ܘܗܘ ܝܫܘܥ ܐܪܝܡ ܥܝܢܘܗܝ ܠܥܠ ܘܐܡܪ ܐܒܐ ܡܘܕܐ ܐܢܐ ܠܟ ܕܫܡܥܬܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 42, 'ܘܐܢܐ ܝܕܥ ܐܢܐ ܕܒܟܠܙܒܢ ܫܡܥ ܐܢܬ ܠܝ ܐܠܐ ܡܛܠ ܟܢܫܐ ܗܢܐ ܕܩܐܡ ܐܡܪ ܐܢܐ ܗܠܝܢ ܕܢܗܝܡܢܘܢ ܕܐܢܬ ܫܕܪܬܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 43, 'ܘܟܕ ܐܡܪ ܗܠܝܢ ܩܥܐ ܒܩܠܐ ܪܡܐ ܠܥܙܪ ܬܐ ܠܒܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 44, 'ܘܢܦܩ ܗܘ ܡܝܬܐ ܟܕ ܐܤܝܪܢ ܐܝܕܘܗܝ ܘܪܓܠܘܗܝ ܒܦܤܩܝܬܐ ܘܐܦܘܗܝ ܐܤܝܪܢ ܒܤܘܕܪܐ ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܫܪܐܘܗܝ ܘܫܒܘܩܘ ܐܙܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 45, 'ܘܤܓܝܐܐ ܡܢ ܝܗܘܕܝܐ ܕܐܬܘ ܠܘܬ ܡܪܝܡ ܟܕ ܚܙܘ ܡܕܡ ܕܥܒܕ ܝܫܘܥ ܗܝܡܢܘ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 46, 'ܘܐܢܫܝܢ ܡܢܗܘܢ ܐܙܠܘ ܠܘܬ ܦܪܝܫܐ ܘܐܡܪܘ ܠܗܘܢ ܡܕܡ ܕܥܒܕ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 47, 'ܘܐܬܟܢܫܘ ܪܒܝ ܟܗܢܐ ܘܦܪܝܫܐ ܘܐܡܪܝܢ ܗܘܘ ܡܢܐ ܢܥܒܕ ܕܗܢܐ ܓܒܪܐ ܐܬܘܬܐ ܤܓܝܐܬܐ ܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 48, 'ܘܐܢ ܫܒܩܝܢ ܚܢܢ ܠܗ ܗܟܢܐ ܟܠܗܘܢ ܐܢܫܐ ܡܗܝܡܢܝܢ ܒܗ ܘܐܬܝܢ ܪܗܘܡܝܐ ܫܩܠܝܢ ܐܬܪܢ ܘܥܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 49, 'ܚܕ ܕܝܢ ܡܢܗܘܢ ܕܫܡܗ ܩܝܦܐ ܪܒ ܟܗܢܐ ܗܘܐ ܕܗܝ ܫܢܬܐ ܘܐܡܪ ܠܗܘܢ ܐܢܬܘܢ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܡܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 50, 'ܘܠܐ ܡܬܚܫܒܝܢ ܐܢܬܘܢ ܕܦܩܚ ܠܢ ܕܚܕ ܓܒܪܐ ܢܡܘܬ ܚܠܦ ܥܡܐ ܘܠܐ ܟܠܗ ܥܡܐ ܢܐܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 51, 'ܗܕܐ ܕܝܢ ܡܢ ܨܒܘܬ ܢܦܫܗ ܠܐ ܐܡܪ ܐܠܐ ܡܛܠ ܕܪܒ ܟܗܢܐ ܗܘܐ ܕܗܝ ܫܢܬܐ ܐܬܢܒܝ ܕܥܬܝܕ ܗܘܐ ܝܫܘܥ ܕܢܡܘܬ ܚܠܦ ܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 52, 'ܘܠܐ ܒܠܚܘܕ ܚܠܦ ܥܡܐ ܐܠܐ ܕܐܦ ܒܢܝܐ ܕܐܠܗܐ ܕܡܒܕܪܝܢ ܢܟܢܫ ܠܚܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 53, 'ܘܡܢ ܗܘ ܝܘܡܐ ܐܬܚܫܒܘ ܗܘܘ ܕܢܩܛܠܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 54, 'ܗܘ ܕܝܢ ܝܫܘܥ ܠܐ ܡܗܠܟ ܗܘܐ ܓܠܝܐܝܬ ܒܝܬ ܝܗܘܕܝܐ ܐܠܐ ܐܙܠ ܠܗ ܡܢ ܬܡܢ ܠܐܬܪܐ ܕܩܪܝܒ ܠܚܘܪܒܐ ܠܟܪܟܐ ܕܡܬܩܪܐ ܐܦܪܝܡ ܘܬܡܢ ܡܬܗܦܟ ܗܘܐ ܥܡ ܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 55, 'ܩܪܝܒ ܗܘܐ ܕܝܢ ܦܨܚܐ ܕܝܗܘܕܝܐ ܘܤܠܩܘ ܤܓܝܐܐ ܡܢ ܩܘܪܝܐ ܠܐܘܪܫܠܡ ܩܕܡ ܥܕܥܕܐ ܕܢܕܟܘܢ ܢܦܫܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 56, 'ܘܒܥܝܢ ܗܘܘ ܠܗ ܠܝܫܘܥ ܘܐܡܪܝܢ ܗܘܘ ܚܕ ܠܚܕ ܒܗܝܟܠܐ ܡܢܐ ܤܒܪܝܢ ܐܢܬܘܢ ܕܠܐ ܐܬܐ ܠܥܕܥܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 57, 'ܪܒܝ ܟܗܢܐ ܕܝܢ ܘܦܪܝܫܐ ܦܩܕܘ ܗܘܘ ܕܐܢ ܐܢܫ ܢܕܥ ܐܝܟܘ ܢܒܕܩ ܠܗܘܢ ܐܝܟ ܕܢܐܚܕܘܢܝܗܝ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 1, 'ܝܫܘܥ ܕܝܢ ܩܕܡ ܫܬܐ ܝܘܡܝܢ ܕܦܨܚܐ ܐܬܐ ܠܒܝܬ ܥܢܝܐ ܐܝܟܐ ܕܐܝܬܘܗܝ ܗܘܐ ܠܥܙܪ ܗܘ ܕܐܩܝܡ ܡܢ ܒܝܬ ܡܝܬܐ ܗܘ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 2, 'ܘܥܒܕܘ ܠܗ ܬܡܢ ܚܫܡܝܬܐ ܘܡܪܬܐ ܡܫܡܫܐ ܗܘܬ ܘܠܥܙܪ ܚܕ ܡܢ ܤܡܝܟܐ ܗܘܐ ܕܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 3, 'ܡܪܝܡ ܕܝܢ ܫܩܠܬ ܫܛܝܦܬܐ ܕܒܤܡܐ ܕܢܪܕܝܢ ܪܫܝܐ ܤܓܝ ܕܡܝܐ ܘܡܫܚܬ ܪܓܠܘܗܝ ܕܝܫܘܥ ܘܫܘܝܬ ܒܤܥܪܗ ܪܓܠܘܗܝ ܘܐܬܡܠܝ ܒܝܬܐ ܡܢ ܪܝܚܗ ܕܒܤܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 4, 'ܘܐܡܪ ܝܗܘܕܐ ܤܟܪܝܘܛܐ ܚܕ ܡܢ ܬܠܡܝܕܘܗܝ ܗܘ ܕܥܬܝܕ ܗܘܐ ܕܢܫܠܡܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 5, 'ܠܡܢܐ ܠܐ ܐܙܕܒܢ ܡܫܚܐ ܗܢܐ ܒܬܠܬ ܡܐܐ ܕܝܢܪܝܢ ܘܐܬܝܗܒ ܠܡܤܟܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 6, 'ܗܕܐ ܕܝܢ ܐܡܪ ܠܐ ܗܘܐ ܡܛܠ ܕܥܠ ܡܤܟܢܐ ܒܛܝܠ ܗܘܐ ܠܗ ܐܠܐ ܡܛܠ ܕܓܢܒܐ ܗܘܐ ܘܓܠܘܤܩܡܐ ܠܘܬܗ ܗܘܐ ܘܡܕܡ ܕܢܦܠ ܗܘܐ ܒܗ ܗܘ ܛܥܝܢ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 7, 'ܐܡܪ ܕܝܢ ܝܫܘܥ ܫܒܘܩܝܗ ܠܝܘܡܐ ܕܩܒܘܪܝ ܢܛܪܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 8, 'ܒܟܠܙܒܢ ܓܝܪ ܡܤܟܢܐ ܐܝܬ ܠܟܘܢ ܥܡܟܘܢ ܠܝ ܕܝܢ ܠܐ ܒܟܠܙܒܢ ܐܝܬ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 9, 'ܘܫܡܥܘ ܟܢܫܐ ܤܓܝܐܐ ܡܢ ܝܗܘܕܝܐ ܕܬܡܢ ܗܘ ܝܫܘܥ ܘܐܬܘ ܠܐ ܡܛܠ ܝܫܘܥ ܒܠܚܘܕ ܐܠܐ ܐܦ ܕܢܚܙܘܢ ܠܠܥܙܪ ܗܘ ܕܐܩܝܡ ܡܢ ܒܝܬ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 10, 'ܘܐܬܪܥܝܘ ܗܘܘ ܪܒܝ ܟܗܢܐ ܕܐܦ ܠܠܥܙܪ ܢܩܛܠܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 11, 'ܡܛܠ ܕܤܓܝܐܐ ܡܢ ܝܗܘܕܝܐ ܡܛܠܬܗ ܐܙܠܝܢ ܗܘܘ ܘܡܗܝܡܢܝܢ ܒܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 12, 'ܘܠܝܘܡܐ ܐܚܪܢܐ ܟܢܫܐ ܤܓܝܐܐ ܐܝܢܐ ܕܐܬܐ ܗܘܐ ܠܥܕܥܕܐ ܟܕ ܫܡܥܘ ܕܝܫܘܥ ܐܬܐ ܠܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 13, 'ܫܩܠܘ ܤܘܟܐ ܕܕܩܠܐ ܘܢܦܩܘ ܠܐܘܪܥܗ ܘܩܥܝܢ ܗܘܘ ܘܐܡܪܝܢ ܐܘܫܥܢܐ ܒܪܝܟ ܗܘ ܕܐܬܐ ܒܫܡܗ ܕܡܪܝܐ ܡܠܟܐ ܕܐܝܤܪܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 14, 'ܐܫܟܚ ܕܝܢ ܝܫܘܥ ܚܡܪܐ ܘܝܬܒ ܥܠܘܗܝ ܐܝܟܢܐ ܕܟܬܝܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 15, 'ܠܐ ܬܕܚܠܝܢ ܒܪܬ ܨܗܝܘܢ ܗܐ ܡܠܟܟܝ ܐܬܐ ܠܟܝ ܘܪܟܝܒ ܥܠ ܥܝܠܐ ܒܪ ܐܬܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 16, 'ܗܠܝܢ ܕܝܢ ܠܐ ܝܕܥܘ ܬܠܡܝܕܘܗܝ ܒܗܘ ܙܒܢܐ ܐܠܐ ܟܕ ܐܫܬܒܚ ܝܫܘܥ ܐܬܕܟܪܘ ܬܠܡܝܕܘܗܝ ܕܗܠܝܢ ܟܬܝܒܢ ܗܘܝ ܥܠܘܗܝ ܘܗܠܝܢ ܥܒܕܘ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 17, 'ܘܤܗܕ ܗܘܐ ܟܢܫܐ ܗܘ ܕܥܡܗ ܗܘܐ ܕܩܪܐ ܠܠܥܙܪ ܡܢ ܩܒܪܐ ܘܐܩܝܡܗ ܡܢ ܒܝܬ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 18, 'ܘܡܛܠ ܗܕܐ ܢܦܩܘ ܠܩܘܒܠܗ ܟܢܫܐ ܤܓܝܐܐ ܕܫܡܥܘ ܕܐܬܐ ܗܕܐ ܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 19, 'ܦܪܝܫܐ ܕܝܢ ܐܡܪܝܢ ܗܘܘ ܚܕ ܠܚܕ ܚܙܝܢ ܐܢܬܘܢ ܕܠܐ ܡܘܬܪܝܢ ܐܢܬܘܢ ܡܕܡ ܕܗܐ ܥܠܡܐ ܟܠܗ ܐܙܠ ܠܗ ܒܬܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 20, 'ܐܝܬ ܗܘܘ ܕܝܢ ܐܦ ܡܢ ܥܡܡܐ ܐܢܫܐ ܒܗܘܢ ܕܤܠܩܘ ܠܡܤܓܕ ܒܥܕܥܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 21, 'ܗܠܝܢ ܐܬܘ ܩܪܒܘ ܠܘܬ ܦܝܠܝܦܘܤ ܗܘ ܕܡܢ ܒܝܬ ܨܝܕܐ ܕܓܠܝܠܐ ܘܫܐܠܘܗܝ ܘܐܡܪܝܢ ܠܗ ܡܪܝ ܨܒܝܢ ܚܢܢ ܢܚܙܐ ܠܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 22, 'ܘܐܬܐ ܗܘ ܦܝܠܝܦܘܤ ܘܐܡܪ ܠܐܢܕܪܐܘܤ ܘܐܢܕܪܐܘܤ ܘܦܝܠܝܦܘܤ ܐܡܪܘ ܠܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 23, 'ܝܫܘܥ ܕܝܢ ܥܢܐ ܘܐܡܪ ܠܗܘܢ ܐܬܬ ܫܥܬܐ ܕܢܫܬܒܚ ܒܪܗ ܕܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 24, 'ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܦܪܕܬܐ ܕܚܛܬܐ ܐܠܐ ܢܦܠܐ ܘܡܝܬܐ ܒܐܪܥܐ ܒܠܚܘܕܝܗ ܦܝܫܐ ܐܢ ܕܝܢ ܡܝܬܐ ܦܐܪܐ ܤܓܝܐܐ ܡܝܬܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 25, 'ܡܢ ܕܪܚܡ ܢܦܫܗ ܢܘܒܕܝܗ ܘܡܢ ܕܤܢܐ ܢܦܫܗ ܒܥܠܡܐ ܗܢܐ ܢܛܪܝܗ ܠܚܝܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 26, 'ܐܢ ܠܝ ܐܢܫ ܡܫܡܫ ܢܐܬܐ ܒܬܪܝ ܘܐܝܟܐ ܕܐܢܐ ܐܝܬܝ ܬܡܢ ܢܗܘܐ ܐܦ ܡܫܡܫܢܝ ܡܢ ܕܠܝ ܡܫܡܫ ܢܝܩܪܝܘܗܝ ܐܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 27, 'ܗܫܐ ܢܦܫܝ ܗܐ ܫܓܝܫܐ ܘܡܢܐ ܐܡܪ ܐܒܝ ܦܨܢܝ ܡܢ ܗܕܐ ܫܥܬܐ ܐܠܐ ܡܛܠ ܗܢܐ ܐܬܝܬ ܠܗܕܐ ܫܥܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 28, 'ܐܒܐ ܫܒܚ ܫܡܟ ܘܩܠܐ ܐܫܬܡܥ ܡܢ ܫܡܝܐ ܫܒܚܬ ܘܬܘܒ ܡܫܒܚ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 29, 'ܘܟܢܫܐ ܕܩܐܡ ܗܘܐ ܫܡܥܘ ܘܐܡܪܝܢ ܪܥܡܐ ܗܘܐ ܐܚܪܢܐ ܕܝܢ ܐܡܪܝܢ ܡܠܐܟܐ ܡܠܠ ܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 30, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗܘܢ ܠܐ ܗܘܐ ܡܛܠܬܝ ܗܘܐ ܩܠܐ ܗܢܐ ܐܠܐ ܡܛܠܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 31, 'ܗܫܐ ܕܝܢܗ ܗܘ ܕܥܠܡܐ ܗܢܐ ܗܫܐ ܐܪܟܘܢܐ ܕܥܠܡܐ ܗܢܐ ܡܫܬܕܐ ܠܒܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 32, 'ܘܐܢܐ ܡܐ ܕܐܬܬܪܝܡܬ ܡܢ ܐܪܥܐ ܐܓܕ ܟܠܢܫ ܠܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 33, 'ܗܕܐ ܕܝܢ ܐܡܪ ܕܢܚܘܐ ܒܐܝܢܐ ܡܘܬܐ ܡܐܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 34, 'ܐܡܪܝܢ ܠܗ ܟܢܫܐ ܚܢܢ ܫܡܥܢ ܡܢ ܢܡܘܤܐ ܕܡܫܝܚܐ ܠܥܠܡ ܡܩܘܐ ܐܝܟܢܐ ܐܡܪ ܐܢܬ ܕܥܬܝܕ ܗܘ ܕܢܬܬܪܝܡ ܒܪܗ ܕܐܢܫܐ ܡܢܘ ܗܢܐ ܒܪܗ ܕܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 35, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܩܠܝܠ ܐܚܪܝܢ ܙܒܢܐ ܢܘܗܪܐ ܥܡܟܘܢ ܗܘ ܗܠܟܘ ܥܕ ܐܝܬ ܠܟܘܢ ܢܘܗܪܐ ܕܠܐ ܚܫܘܟܐ ܢܕܪܟܟܘܢ ܘܡܢ ܕܡܗܠܟ ܒܚܫܘܟܐ ܠܐ ܝܕܥ ܠܐܝܟܐ ܐܙܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 36, 'ܥܕ ܐܝܬ ܠܟܘܢ ܢܘܗܪܐ ܗܝܡܢܘ ܒܢܘܗܪܐ ܕܒܢܘܗܝ ܕܢܘܗܪܐ ܬܗܘܘܢ ܗܠܝܢ ܡܠܠ ܝܫܘܥ ܘܐܙܠ ܐܬܛܫܝ ܡܢܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 37, 'ܘܟܕ ܗܠܝܢ ܟܠܗܝܢ ܐܬܘܬܐ ܥܒܕ ܩܕܡܝܗܘܢ ܠܐ ܗܝܡܢܘ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 38, 'ܕܬܬܡܠܐ ܡܠܬܐ ܕܐܫܥܝܐ ܢܒܝܐ ܕܐܡܪ ܡܪܝ ܡܢܘ ܗܝܡܢ ܠܫܡܥܢ ܘܕܪܥܗ ܕܡܪܝܐ ܠܡܢ ܐܬܓܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 39, 'ܡܛܠ ܗܢܐ ܠܐ ܡܫܟܚܝܢ ܗܘܘ ܕܢܗܝܡܢܘܢ ܡܛܠ ܕܬܘܒ ܐܡܪ ܐܫܥܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 40, 'ܕܥܘܪܘ ܥܝܢܝܗܘܢ ܘܐܚܫܟܘ ܠܒܗܘܢ ܕܠܐ ܢܚܙܘܢ ܒܥܝܢܝܗܘܢ ܘܢܤܬܟܠܘܢ ܒܠܒܗܘܢ ܘܢܬܦܢܘܢ ܘܐܤܐ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 41, 'ܗܠܝܢ ܐܡܪ ܐܫܥܝܐ ܟܕ ܚܙܐ ܫܘܒܚܗ ܘܡܠܠ ܥܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 42, 'ܐܦ ܡܢ ܪܫܐ ܕܝܢ ܤܓܝܐܐ ܗܝܡܢܘ ܒܗ ܐܠܐ ܡܛܠ ܦܪܝܫܐ ܠܐ ܡܘܕܝܢ ܗܘܘ ܕܠܐ ܢܗܘܘܢ ܠܒܪ ܡܢ ܟܢܘܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 43, 'ܪܚܡܘ ܓܝܪ ܫܘܒܚܐ ܕܒܢܝܢܫܐ ܝܬܝܪ ܡܢ ܫܘܒܚܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 44, 'ܝܫܘܥ ܕܝܢ ܩܥܐ ܘܐܡܪ ܡܢ ܕܡܗܝܡܢ ܒܝ ܠܐ ܗܘܐ ܒܝ ܡܗܝܡܢ ܐܠܐ ܒܡܢ ܕܫܕܪܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 45, 'ܘܡܢ ܕܠܝ ܚܙܐ ܚܙܐ ܠܡܢ ܕܫܕܪܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 46, 'ܐܢܐ ܢܘܗܪܐ ܐܬܝܬ ܠܥܠܡܐ ܕܟܠ ܡܢ ܕܡܗܝܡܢ ܒܝ ܠܐ ܢܩܘܐ ܒܚܫܘܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 47, 'ܘܡܢ ܕܫܡܥ ܡܠܝ ܘܠܐ ܢܛܪ ܠܗܝܢ ܐܢܐ ܠܐ ܕܐܢ ܐܢܐ ܠܗ ܠܐ ܓܝܪ ܐܬܝܬ ܕܐܕܘܢ ܠܥܠܡܐ ܐܠܐ ܕܐܚܐ ܠܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 48, 'ܡܢ ܕܛܠܡ ܠܝ ܘܠܐ ܡܩܒܠ ܡܠܝ ܐܝܬ ܡܢ ܕܕܐܢ ܠܗ ܡܠܬܐ ܕܡܠܠܬ ܗܝ ܕܝܢܐ ܠܗ ܒܝܘܡܐ ܐܚܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 49, 'ܕܐܢܐ ܡܢ ܢܦܫܝ ܠܐ ܡܠܠܬ ܐܠܐ ܐܒܐ ܕܫܕܪܢܝ ܗܘ ܝܗܒ ܠܝ ܦܘܩܕܢܐ ܡܢܐ ܐܡܪ ܘܡܢܐ ܐܡܠܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 50, 'ܘܝܕܥ ܐܢܐ ܕܦܘܩܕܢܗ ܚܝܐ ܐܢܘܢ ܕܠܥܠܡ ܐܝܠܝܢ ܗܟܝܠ ܕܡܡܠܠ ܐܢܐ ܐܝܟܢܐ ܕܐܡܪ ܠܝ ܐܒܝ ܗܟܢܐ ܡܡܠܠ ܐܢܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 1, 'ܩܕܡ ܕܝܢ ܥܐܕܐ ܕܦܨܚܐ ܝܕܥ ܗܘܐ ܝܫܘܥ ܕܡܛܬ ܫܥܬܐ ܕܢܫܢܐ ܡܢ ܗܢܐ ܥܠܡܐ ܠܘܬ ܐܒܘܗܝ ܘܐܚܒ ܠܕܝܠܗ ܕܒܗܢܐ ܥܠܡܐ ܘܥܕܡܐ ܠܚܪܬܐ ܐܚܒ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 2, 'ܘܟܕ ܗܘܬ ܚܫܡܝܬܐ ܪܡܐ ܗܘܐ ܠܗ ܠܤܛܢܐ ܒܠܒܗ ܕܝܗܘܕܐ ܒܪ ܫܡܥܘܢ ܤܟܪܝܘܛܐ ܕܢܫܠܡܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 3, 'ܗܘ ܕܝܢ ܝܫܘܥ ܡܛܠ ܕܝܕܥ ܗܘܐ ܕܟܠ ܡܕܡ ܝܗܒ ܐܒܐ ܒܐܝܕܘܗܝ ܘܕܡܢ ܐܠܗܐ ܢܦܩ ܘܠܘܬ ܐܠܗܐ ܐܙܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 4, 'ܩܡ ܡܢ ܚܫܡܝܬܐ ܘܤܡ ܢܚܬܘܗܝ ܘܫܩܠ ܤܕܘܢܐ ܡܚܐ ܒܚܨܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 5, 'ܘܐܪܡܝ ܡܝܐ ܒܡܫܓܬܐ ܘܫܪܝ ܠܡܫܓܘ ܪܓܠܐ ܕܬܠܡܝܕܘܗܝ ܘܡܫܘܐ ܗܘܐ ܒܤܕܘܢܐ ܕܡܚܐ ܒܚܨܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 6, 'ܟܕ ܕܝܢ ܐܬܐ ܠܘܬ ܫܡܥܘܢ ܟܐܦܐ ܐܡܪ ܠܗ ܫܡܥܘܢ ܐܢܬ ܡܪܝ ܪܓܠܝ ܡܫܝܓ ܐܢܬ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 7, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗ ܡܕܡ ܕܥܒܕ ܐܢܐ ܐܢܬ ܠܐ ܝܕܥ ܐܢܬ ܗܫܐ ܒܬܪܟܢ ܕܝܢ ܬܕܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 8, 'ܐܡܪ ܠܗ ܫܡܥܘܢ ܟܐܦܐ ܠܥܠܡ ܠܐ ܡܫܝܓ ܐܢܬ ܠܝ ܪܓܠܝ ܐܡܪ ܠܗ ܝܫܘܥ ܐܢ ܠܐ ܡܫܝܓ ܐܢܐ ܠܟ ܠܝܬ ܠܟ ܥܡܝ ܡܢܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 9, 'ܐܡܪ ܠܗ ܫܡܥܘܢ ܟܐܦܐ ܡܕܝܢ ܡܪܝ ܠܐ ܒܠܚܘܕ ܪܓܠܝ ܬܫܝܓ ܠܝ ܐܠܐ ܐܦ ܐܝܕܝ ܐܦ ܪܫܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 10, 'ܐܡܪ ܠܗ ܝܫܘܥ ܗܘ ܕܤܚܐ ܠܐ ܤܢܝܩ ܐܠܐ ܪܓܠܘܗܝ ܒܠܚܘܕ ܢܫܝܓ ܟܠܗ ܓܝܪ ܕܟܐ ܗܘ ܐܦ ܐܢܬܘܢ ܟܠܟܘܢ ܕܟܝܐ ܐܢܬܘܢ ܐܠܐ ܠܐ ܟܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 11, 'ܝܕܥ ܗܘܐ ܓܝܪ ܝܫܘܥ ܠܗܘ ܕܡܫܠܡ ܠܗ ܡܛܠ ܗܢܐ ܐܡܪ ܕܠܐ ܗܘܐ ܟܠܟܘܢ ܕܟܝܐ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 12, 'ܟܕ ܕܝܢ ܐܫܝܓ ܪܓܠܝܗܘܢ ܫܩܠ ܢܚܬܘܗܝ ܘܐܤܬܡܟ ܘܐܡܪ ܠܗܘܢ ܝܕܥܝܢ ܐܢܬܘܢ ܡܢܐ ܥܒܕܬ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 13, 'ܐܢܬܘܢ ܩܪܝܢ ܐܢܬܘܢ ܠܝ ܪܒܢ ܘܡܪܢ ܘܫܦܝܪ ܐܡܪܝܢ ܐܢܬܘܢ ܐܝܬܝ ܓܝܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 14, 'ܐܢ ܐܢܐ ܗܟܝܠ ܡܪܟܘܢ ܘܪܒܟܘܢ ܐܫܝܓܬ ܠܟܘܢ ܪܓܠܝܟܘܢ ܟܡܐ ܐܢܬܘܢ ܚܝܒܝܢ ܐܢܬܘܢ ܕܬܫܝܓܘܢ ܪܓܠܐ ܚܕ ܕܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 15, 'ܗܢܐ ܓܝܪ ܛܘܦܤܐ ܝܗܒܬ ܠܟܘܢ ܕܐܝܟܢܐ ܕܐܢܐ ܥܒܕܬ ܠܟܘܢ ܐܦ ܐܢܬܘܢ ܬܥܒܕܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 16, 'ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܠܝܬ ܥܒܕܐ ܕܪܒ ܡܢ ܡܪܗ ܘܠܐ ܫܠܝܚܐ ܕܪܒ ܡܢ ܡܢ ܕܫܕܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 17, 'ܐܢ ܗܠܝܢ ܬܕܥܘܢ ܛܘܒܢܐ ܐܢܬܘܢ ܐܢ ܬܥܒܕܘܢ ܐܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 18, 'ܠܐ ܗܘܐ ܥܠ ܟܠܟܘܢ ܐܡܪ ܐܢܐ ܝܕܥ ܐܢܐ ܓܝܪ ܠܐܝܠܝܢ ܕܓܒܝܬ ܐܠܐ ܕܟܬܒܐ ܢܫܠܡ ܕܗܘ ܕܐܟܠ ܥܡܝ ܠܚܡܐ ܐܪܝܡ ܥܠܝ ܥܩܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 19, 'ܡܢ ܗܫܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܡܢ ܩܕܡ ܕܢܗܘܐ ܕܡܐ ܕܗܘܐ ܬܗܝܡܢܘܢ ܕܐܢܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 20, 'ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܡܢ ܕܡܩܒܠ ܠܡܢ ܕܡܫܕܪ ܐܢܐ ܠܝ ܡܩܒܠ ܘܡܢ ܕܠܝ ܡܩܒܠ ܡܩܒܠ ܠܡܢ ܕܫܕܪܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 21, 'ܗܠܝܢ ܐܡܪ ܝܫܘܥ ܘܐܬܥܙܙ ܒܪܘܚܗ ܘܐܤܗܕ ܘܐܡܪ ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܚܕ ܡܢܟܘܢ ܢܫܠܡܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 22, 'ܚܪܘ ܕܝܢ ܬܠܡܝܕܐ ܚܕ ܒܚܕ ܡܛܠ ܕܠܐ ܝܕܥܝܢ ܗܘܘ ܕܥܠ ܡܢܘ ܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 23, 'ܐܝܬ ܗܘܐ ܕܝܢ ܡܢ ܬܠܡܝܕܘܗܝ ܚܕ ܕܤܡܝܟ ܗܘܐ ܒܥܘܒܗ ܗܘ ܕܪܚܡ ܗܘܐ ܠܗ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 24, 'ܠܗܢܐ ܪܡܙ ܫܡܥܘܢ ܟܐܦܐ ܕܢܫܐܠܝܘܗܝ ܕܡܢܘ ܗܘ ܕܐܡܪ ܥܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 25, 'ܘܢܦܠ ܗܘ ܬܠܡܝܕܐ ܥܠ ܚܕܝܗ ܕܝܫܘܥ ܘܐܡܪ ܠܗ ܡܪܝ ܡܢܘ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 26, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܗܘ ܗܘ ܕܨܒܥ ܐܢܐ ܠܚܡܐ ܝܗܒ ܐܢܐ ܠܗ ܘܨܒܥ ܝܫܘܥ ܠܚܡܐ ܘܝܗܒ ܠܝܗܘܕܐ ܒܪ ܫܡܥܘܢ ܤܟܪܝܘܛܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 27, 'ܘܒܬܪ ܠܚܡܐ ܗܝܕܝܢ ܐܬܥܠܠ ܒܗ ܤܛܢܐ ܘܐܡܪ ܠܗ ܝܫܘܥ ܡܕܡ ܕܥܒܕ ܐܢܬ ܥܒܕ ܒܥܓܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 28, 'ܗܕܐ ܕܝܢ ܠܐ ܐܢܫ ܝܕܥ ܡܢ ܗܢܘܢ ܤܡܝܟܐ ܕܥܠ ܡܢܐ ܐܡܪ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 29, 'ܐܢܫܝܢ ܓܝܪ ܤܒܪܘ ܡܛܠ ܕܓܠܘܤܩܡܐ ܨܐܕܘܗܝ ܗܘܐ ܕܝܗܘܕܐ ܕܡܦܩܕ ܦܩܕ ܠܗ ܕܢܙܒܢ ܡܕܡ ܕܡܬܒܥܐ ܠܥܕܥܕܐ ܐܘ ܕܢܬܠ ܡܕܡ ܠܡܤܟܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 30, 'ܗܘ ܕܝܢ ܝܗܘܕܐ ܢܤܒ ܠܚܡܐ ܒܪ ܫܥܬܗ ܘܢܦܩ ܠܗ ܠܒܪ ܠܠܝܐ ܗܘܐ ܕܝܢ ܟܕ ܢܦܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 31, 'ܘܐܡܪ ܝܫܘܥ ܗܫܐ ܐܫܬܒܚ ܒܪܗ ܕܐܢܫܐ ܘܐܠܗܐ ܐܫܬܒܚ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 32, 'ܘܐܢ ܐܠܗܐ ܐܫܬܒܚ ܒܗ ܘܐܦ ܐܠܗܐ ܡܫܒܚ ܠܗ ܒܗ ܘܡܚܕܐ ܡܫܒܚ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 33, 'ܒܢܝ ܩܠܝܠ ܐܚܪܝܢ ܥܡܟܘܢ ܐܢܐ ܘܬܒܥܘܢܢܝ ܘܐܝܟܢܐ ܕܐܡܪܬ ܠܝܗܘܕܝܐ ܕܠܐܝܟܐ ܕܐܢܐ ܐܙܠ ܐܢܐ ܐܢܬܘܢ ܠܐ ܡܫܟܚܝܢ ܐܢܬܘܢ ܠܡܐܬܐ ܘܐܦ ܠܟܘܢ ܐܡܪ ܐܢܐ ܗܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 34, 'ܦܘܩܕܢܐ ܚܕܬܐ ܝܗܒ ܐܢܐ ܠܟܘܢ ܕܗܘܝܬܘܢ ܡܚܒܝܢ ܚܕ ܠܚܕ ܐܝܟܢܐ ܕܐܢܐ ܐܚܒܬܟܘܢ ܐܦ ܐܢܬܘܢ ܬܚܒܘܢ ܚܕ ܠܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 35, 'ܒܗܕܐ ܢܕܥ ܟܠ ܐܢܫ ܕܬܠܡܝܕܝ ܐܢܬܘܢ ܐܢ ܚܘܒܐ ܢܗܘܐ ܒܟܘܢ ܚܕ ܠܘܬ ܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 36, 'ܐܡܪ ܠܗ ܫܡܥܘܢ ܟܐܦܐ ܡܪܢ ܠܐܝܟܐ ܐܙܠ ܐܢܬ ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗ ܠܐܝܟܐ ܕܐܙܠ ܐܢܐ ܠܐ ܡܫܟܚ ܐܢܬ ܗܫܐ ܕܬܐܬܐ ܒܬܪܝ ܠܚܪܬܐ ܕܝܢ ܬܐܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 37, 'ܐܡܪ ܠܗ ܫܡܥܘܢ ܟܐܦܐ ܡܪܝ ܠܡܢܐ ܠܐ ܡܫܟܚ ܐܢܐ ܕܐܬܐ ܒܬܪܟ ܗܫܐ ܢܦܫܝ ܚܠܦܝܟ ܤܐܡ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 38, 'ܐܡܪ ܠܗ ܝܫܘܥ ܢܦܫܟ ܚܠܦܝ ܤܐܡ ܐܢܬ ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟ ܕܠܐ ܢܩܪܐ ܬܪܢܓܠܐ ܥܕܡܐ ܕܬܟܦܘܪ ܒܝ ܬܠܬ ܙܒܢܝܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 1, 'ܠܐ ܢܬܕܘܕ ܠܒܟܘܢ ܗܝܡܢܘ ܒܐܠܗܐ ܘܒܝ ܗܝܡܢܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 2, 'ܤܓܝܐܝܢ ܐܢܘܢ ܐܘܢܐ ܒܝܬ ܐܒܝ ܘܐܠܐ ܐܡܪ ܗܘܝܬ ܠܟܘܢ ܕܐܙܠ ܐܢܐ ܕܐܛܝܒ ܠܟܘܢ ܐܬܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 3, 'ܘܐܢ ܐܙܠ ܐܛܝܒ ܠܟܘܢ ܐܬܪܐ ܬܘܒ ܐܬܐ ܘܐܕܒܪܟܘܢ ܠܘܬܝ ܕܐܝܟܐ ܕܐܢܐ ܐܝܬܝ ܐܦ ܐܢܬܘܢ ܬܗܘܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 4, 'ܘܠܐܝܟܐ ܕܐܙܠ ܐܢܐ ܝܕܥܝܢ ܐܢܬܘܢ ܘܐܘܪܚܐ ܝܕܥܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 5, 'ܐܡܪ ܠܗ ܬܐܘܡܐ ܡܪܢ ܠܐ ܝܕܥܝܢܢ ܠܐܝܟܐ ܐܙܠ ܐܢܬ ܘܐܝܟܢܐ ܡܫܟܚܝܢ ܚܢܢ ܐܘܪܚܐ ܠܡܕܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 6, 'ܐܡܪ ܠܗ ܝܫܘܥ ܐܢܐ ܐܢܐ ܐܘܪܚܐ ܘܫܪܪܐ ܘܚܝܐ ܠܐ ܐܢܫ ܐܬܐ ܠܘܬ ܐܒܝ ܐܠܐ ܐܢ ܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 7, 'ܐܠܘ ܠܝ ܝܕܥܝܢ ܗܘܝܬܘܢ ܐܦ ܠܐܒܝ ܝܕܥܝܢ ܗܘܝܬܘܢ ܘܡܢ ܗܫܐ ܝܕܥܝܢ ܐܢܬܘܢ ܠܗ ܘܚܙܝܬܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 8, 'ܐܡܪ ܠܗ ܦܝܠܝܦܘܤ ܡܪܢ ܚܘܢ ܐܒܐ ܘܟܕܘ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 9, 'ܐܡܪ ܠܗ ܝܫܘܥ ܗܢܐ ܟܠܗ ܙܒܢܐ ܥܡܟܘܢ ܐܢܐ ܘܠܐ ܝܕܥܬܢܝ ܦܝܠܝܦܐ ܡܢ ܕܠܝ ܚܙܐ ܚܙܐ ܠܐܒܐ ܘܐܝܟܢܐ ܐܢܬ ܐܡܪ ܐܢܬ ܚܘܢ ܐܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 10, 'ܠܐ ܡܗܝܡܢ ܐܢܬ ܕܐܢܐ ܒܐܒܝ ܘܐܒܝ ܒܝ ܡܠܐ ܐܝܠܝܢ ܕܐܢܐ ܡܡܠܠ ܐܢܐ ܡܢ ܢܦܫܝ ܠܐ ܡܡܠܠ ܐܢܐ ܐܒܝ ܕܝܢ ܕܒܝ ܥܡܪ ܗܘ ܥܒܕ ܥܒܕܐ ܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 11, 'ܗܝܡܢܘ ܕܐܢܐ ܒܐܒܝ ܘܐܒܝ ܒܝ ܘܐܠܐ ܐܦܢ ܡܛܠ ܥܒܕܐ ܗܝܡܢܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 12, 'ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܡܢ ܕܡܗܝܡܢ ܒܝ ܥܒܕܐ ܐܝܠܝܢ ܕܐܢܐ ܥܒܕ ܐܢܐ ܐܦ ܗܘ ܢܥܒܕ ܘܕܝܬܝܪܝܢ ܡܢ ܗܠܝܢ ܢܥܒܕ ܕܐܢܐ ܠܘܬ ܐܒܐ ܐܙܠ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 13, 'ܘܡܕܡ ܕܬܫܐܠܘܢ ܒܫܡܝ ܐܥܒܕ ܠܟܘܢ ܕܢܫܬܒܚ ܐܒܐ ܒܒܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 14, 'ܘܐܢ ܬܫܐܠܘܢܢܝ ܒܫܡܝ ܐܢܐ ܥܒܕܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 15, 'ܐܢ ܪܚܡܝܢ ܐܢܬܘܢ ܠܝ ܦܘܩܕܢܝ ܛܪܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 16, 'ܘܐܢܐ ܐܒܥܐ ܡܢ ܐܒܝ ܘܐܚܪܢܐ ܦܪܩܠܛܐ ܢܬܠ ܠܟܘܢ ܕܢܗܘܐ ܥܡܟܘܢ ܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 17, 'ܪܘܚܐ ܕܫܪܪܐ ܗܘ ܕܥܠܡܐ ܠܐ ܡܫܟܚ ܠܡܩܒܠܘܬܗ ܡܛܠ ܕܠܐ ܚܙܝܗܝ ܘܠܐ ܝܕܥܗ ܐܢܬܘܢ ܕܝܢ ܝܕܥܝܢ ܐܢܬܘܢ ܠܗ ܕܠܘܬܟܘܢ ܥܡܪ ܘܒܟܘܢ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 18, 'ܠܐ ܫܒܩ ܐܢܐ ܠܟܘܢ ܝܬܡܐ ܐܬܐ ܐܢܐ ܓܝܪ ܠܘܬܟܘܢ ܩܠܝܠ ܐܚܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 19, 'ܘܥܠܡܐ ܠܐ ܚܙܐ ܠܝ ܐܢܬܘܢ ܕܝܢ ܬܚܙܘܢܢܝ ܕܐܢܐ ܚܝ ܐܦ ܐܢܬܘܢ ܬܚܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 20, 'ܒܗܘ ܝܘܡܐ ܬܕܥܘܢ ܕܐܢܐ ܒܐܒܝ ܘܐܢܬܘܢ ܒܝ ܐܢܬܘܢ ܘܐܢܐ ܒܟܘܢ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 21, 'ܡܢ ܕܐܝܬ ܠܘܬܗ ܦܘܩܕܢܝ ܘܢܛܪ ܠܗܘܢ ܗܘ ܗܘ ܡܚܒ ܠܝ ܗܘ ܕܝܢ ܕܪܚܡ ܠܝ ܢܬܪܚܡ ܡܢ ܐܒܝ ܘܐܢܐ ܐܪܚܡܝܘܗܝ ܘܐܚܘܝܘܗܝ ܢܦܫܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 22, 'ܐܡܪ ܠܗ ܝܗܘܕܐ ܠܐ ܗܘܐ ܤܟܪܝܘܛܐ ܡܪܝ ܡܢܘ ܠܢ ܥܬܝܕ ܐܢܬ ܠܡܚܘܝܘ ܢܦܫܟ ܘܠܐ ܗܘܐ ܠܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 23, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗ ܡܢ ܕܪܚܡ ܠܝ ܡܠܬܝ ܢܛܪ ܘܐܒܝ ܢܪܚܡܝܘܗܝ ܘܠܘܬܗ ܐܬܝܢ ܚܢܢ ܘܐܘܢܐ ܠܘܬܗ ܥܒܕܝܢ ܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 24, 'ܗܘ ܕܝܢ ܕܠܐ ܪܚܡ ܠܝ ܡܠܬܝ ܠܐ ܢܛܪ ܘܡܠܬܐ ܗܕܐ ܕܫܡܥܝܢ ܐܢܬܘܢ ܠܐ ܗܘܬ ܕܝܠܝ ܐܠܐ ܕܐܒܐ ܕܫܕܪܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 25, 'ܗܠܝܢ ܡܠܠܬ ܥܡܟܘܢ ܟܕ ܠܘܬܟܘܢ ܐܝܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 26, 'ܗܘ ܕܝܢ ܦܪܩܠܛܐ ܪܘܚܐ ܕܩܘܕܫܐ ܗܘ ܕܡܫܕܪ ܐܒܝ ܒܫܡܝ ܗܘ ܢܠܦܟܘܢ ܟܠܡܕܡ ܘܗܘ ܢܥܗܕܟܘܢ ܟܠ ܡܐ ܕܐܡܪ ܐܢܐ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 27, 'ܫܠܡܐ ܫܒܩ ܐܢܐ ܠܟܘܢ ܫܠܡܐ ܕܝܠܝ ܝܗܒ ܐܢܐ ܠܟܘܢ ܠܐ ܗܘܐ ܐܝܟܢܐ ܕܝܗܒ ܥܠܡܐ ܐܢܐ ܝܗܒ ܐܢܐ ܠܟܘܢ ܠܐ ܢܬܕܘܕ ܠܒܟܘܢ ܘܠܐ ܢܕܚܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 28, 'ܫܡܥܬܘܢ ܕܐܢܐ ܐܡܪܬ ܠܟܘܢ ܕܐܙܠ ܐܢܐ ܘܐܬܐ ܐܢܐ ܠܘܬܟܘܢ ܐܠܘ ܪܚܡܝܢ ܗܘܝܬܘܢ ܠܝ ܚܕܝܢ ܗܘܝܬܘܢ ܕܐܙܠ ܐܢܐ ܠܘܬ ܐܒܝ ܕܐܒܝ ܪܒ ܗܘ ܡܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 29, 'ܘܗܫܐ ܗܐ ܐܡܪܬ ܠܟܘܢ ܥܕܠܐ ܢܗܘܐ ܕܡܐ ܕܗܘܐ ܬܗܝܡܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 30, 'ܡܟܝܠ ܠܐ ܐܡܠܠ ܥܡܟܘܢ ܤܓܝܐܬܐ ܐܬܐ ܓܝܪ ܐܪܟܘܢܗ ܕܥܠܡܐ ܘܒܝ ܠܝܬ ܠܗ ܡܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 31, 'ܐܠܐ ܕܢܕܥ ܥܠܡܐ ܕܪܚܡ ܐܢܐ ܠܐܒܝ ܘܐܝܟܢܐ ܕܦܩܕܢܝ ܐܒܝ ܗܟܘܬ ܥܒܕ ܐܢܐ ܩܘܡܘ ܢܐܙܠ ܡܟܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 1, 'ܐܢܐ ܐܢܐ ܓܦܬܐ ܕܫܪܪܐ ܘܐܒܝ ܗܘ ܦܠܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 2, 'ܟܠ ܫܒܫܬܐ ܕܒܝ ܦܐܪܐ ܠܐ ܝܗܒܐ ܫܩܠ ܠܗ ܘܐܝܕܐ ܕܝܗܒܐ ܦܐܪܐ ܡܕܟܐ ܠܗ ܕܦܐܪܐ ܤܓܝܐܐ ܬܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 3, 'ܐܢܬܘܢ ܡܢ ܟܕܘ ܕܟܝܢ ܐܢܬܘܢ ܡܛܠ ܡܠܬܐ ܕܡܠܠܬ ܥܡܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 4, 'ܩܘܘ ܒܝ ܘܐܢܐ ܒܟܘܢ ܐܝܟܢܐ ܕܫܒܫܬܐ ܠܐ ܡܫܟܚܐ ܕܬܬܠ ܦܐܪܐ ܡܢ ܢܦܫܗ ܐܠܐ ܡܩܘܝܐ ܒܓܦܬܐ ܗܟܢܐ ܐܦ ܠܐ ܐܢܬܘܢ ܐܠܐ ܬܩܘܘܢ ܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 5, 'ܐܢܐ ܐܢܐ ܓܦܬܐ ܘܐܢܬܘܢ ܫܒܫܬܐ ܡܢ ܕܡܩܘܐ ܒܝ ܘܐܢܐ ܒܗ ܗܢܐ ܡܝܬܐ ܦܐܪܐ ܤܓܝܐܐ ܡܛܠ ܕܕܠܐ ܐܢܐ ܠܐ ܡܫܟܚܝܢ ܐܢܬܘܢ ܠܡܥܒܕ ܡܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 6, 'ܐܠܐ ܕܝܢ ܐܢܫ ܡܩܘܐ ܒܝ ܡܫܬܕܐ ܠܒܪ ܐܝܟ ܫܒܫܬܐ ܕܝܒܫܐ ܘܠܩܛܝܢ ܪܡܝܢ ܠܗ ܒܢܘܪܐ ܕܬܐܩܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 7, 'ܐܢ ܕܝܢ ܬܩܘܘܢ ܒܝ ܘܡܠܝ ܢܩܘܝܢ ܒܟܘܢ ܟܠ ܡܐ ܕܬܨܒܘܢ ܠܡܫܐܠ ܢܗܘܐ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 8, 'ܒܗܕܐ ܡܫܬܒܚ ܐܒܐ ܕܦܐܪܐ ܤܓܝܐܐ ܬܝܬܘܢ ܘܬܗܘܘܢ ܬܠܡܝܕܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 9, 'ܐܝܟܢܐ ܕܐܚܒܢܝ ܐܒܝ ܐܦ ܐܢܐ ܐܚܒܬܟܘܢ ܩܘܘ ܒܪܚܡܬܝ ܕܝܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 10, 'ܐܢ ܦܘܩܕܢܝ ܬܛܪܘܢ ܬܩܘܘܢ ܒܚܘܒܐ ܕܝܠܝ ܐܝܟܢܐ ܕܐܢܐ ܢܛܪܬ ܦܘܩܕܢܘܗܝ ܕܐܒܝ ܘܡܩܘܐ ܐܢܐ ܒܚܘܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 11, 'ܗܠܝܢ ܡܠܠܬ ܥܡܟܘܢ ܕܚܕܘܬܝ ܬܗܘܐ ܒܟܘܢ ܘܬܫܬܡܠܐ ܚܕܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 12, 'ܗܢܘ ܦܘܩܕܢܝ ܕܬܚܒܘܢ ܚܕ ܠܚܕ ܐܝܟܢܐ ܕܐܢܐ ܐܚܒܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 13, 'ܚܘܒܐ ܕܪܒ ܡܢ ܗܢܐ ܠܝܬ ܕܐܢܫ ܢܦܫܗ ܢܤܝܡ ܚܠܦ ܪܚܡܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 14, 'ܐܢܬܘܢ ܪܚܡܝ ܐܢܬܘܢ ܐܢ ܬܥܒܕܘܢ ܟܠ ܕܡܦܩܕ ܐܢܐ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 15, 'ܠܐ ܡܟܝܠ ܩܪܐ ܐܢܐ ܠܟܘܢ ܥܒܕܐ ܡܛܠ ܕܥܒܕܐ ܠܐ ܝܕܥ ܡܢܐ ܥܒܕ ܡܪܗ ܪܚܡܝ ܕܝܢ ܩܪܝܬܟܘܢ ܡܛܠ ܕܟܠ ܕܫܡܥܬ ܡܢ ܐܒܝ ܐܘܕܥܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 16, 'ܠܐ ܗܘܐ ܐܢܬܘܢ ܓܒܝܬܘܢܢܝ ܐܠܐ ܐܢܐ ܗܘ ܓܒܝܬܟܘܢ ܘܤܡܬܟܘܢ ܕܐܦ ܐܢܬܘܢ ܬܐܙܠܘܢ ܬܝܬܘܢ ܦܐܪܐ ܘܦܐܪܝܟܘܢ ܢܩܘܘܢ ܕܟܠ ܕܬܫܐܠܘܢ ܠܐܒܝ ܒܫܡܝ ܢܬܠ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 17, 'ܗܠܝܢ ܡܦܩܕ ܐܢܐ ܠܟܘܢ ܕܬܚܒܘܢ ܚܕ ܠܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 18, 'ܘܐܢ ܥܠܡܐ ܤܢܐ ܠܟܘܢ ܕܥܘ ܕܩܕܡܝܟܘܢ ܠܝ ܤܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 19, 'ܘܐܠܘ ܡܢ ܥܠܡܐ ܗܘܝܬܘܢ ܥܠܡܐ ܠܕܝܠܗ ܪܚܡ ܗܘܐ ܐܠܐ ܠܐ ܗܘܝܬܘܢ ܡܢ ܥܠܡܐ ܐܢܐ ܓܝܪ ܓܒܝܬܟܘܢ ܡܢ ܥܠܡܐ ܡܛܠ ܗܢܐ ܤܢܐ ܠܟܘܢ ܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 20, 'ܥܗܕܘ ܡܠܬܐ ܕܐܢܐ ܐܡܪܬ ܠܟܘܢ ܕܠܝܬ ܥܒܕܐ ܕܪܒ ܡܢ ܡܪܗ ܐܢ ܠܝ ܪܕܦܘ ܐܦ ܠܟܘܢ ܢܪܕܦܘܢ ܘܐܢ ܡܠܬܝ ܢܛܪܘ ܐܦ ܕܝܠܟܘܢ ܢܛܪܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 21, 'ܐܠܐ ܗܠܝܢ ܟܠܗܝܢ ܢܥܒܕܘܢ ܒܟܘܢ ܡܛܠ ܫܡܝ ܕܝܠܝ ܕܠܐ ܝܕܥܝܢ ܠܡܢ ܕܫܕܪܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 22, 'ܐܠܘ ܐܢܐ ܠܐ ܐܬܝܬ ܡܠܠܬ ܥܡܗܘܢ ܠܝܬ ܗܘܬ ܠܗܘܢ ܚܛܝܬܐ ܗܫܐ ܕܝܢ ܠܝܬ ܠܗܘܢ ܥܠܬܐ ܥܠ ܐܦܝ ܚܛܝܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 23, 'ܡܢ ܕܠܝ ܤܢܐ ܘܐܦ ܠܐܒܝ ܤܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 24, 'ܘܐܠܘ ܥܒܕܐ ܠܐ ܥܒܕܬ ܠܥܢܝܗܘܢ ܐܝܠܝܢ ܕܐܢܫ ܐܚܪܝܢ ܠܐ ܥܒܕ ܠܝܬ ܗܘܐ ܠܗܘܢ ܚܛܝܬܐ ܗܫܐ ܕܝܢ ܘܚܙܘ ܘܤܢܘ ܐܦ ܠܝ ܘܐܦ ܠܐܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 25, 'ܕܬܬܡܠܐ ܡܠܬܐ ܕܟܬܝܒܐ ܒܢܡܘܤܗܘܢ ܕܤܢܐܘܢܝ ܡܓܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 26, 'ܡܐ ܕܝܢ ܕܐܬܐ ܦܪܩܠܛܐ ܗܘ ܕܐܢܐ ܡܫܕܪ ܐܢܐ ܠܟܘܢ ܡܢ ܠܘܬ ܐܒܝ ܪܘܚܐ ܕܫܪܪܐ ܗܘ ܕܡܢ ܠܘܬ ܐܒܝ ܢܦܩ ܗܘ ܢܤܗܕ ܥܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 27, 'ܐܦ ܐܢܬܘܢ ܤܗܕܝܢ ܐܢܬܘܢ ܕܡܢ ܫܘܪܝܐ ܥܡܝ ܐܢܬܘܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 1, 'ܗܠܝܢ ܡܠܠܬ ܥܡܟܘܢ ܕܠܐ ܬܬܟܫܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 2, 'ܢܦܩܘܢܟܘܢ ܓܝܪ ܡܢ ܟܢܘܫܬܗܘܢ ܘܬܐܬܐ ܫܥܬܐ ܕܟܠ ܕܢܩܛܘܠܟܘܢ ܢܤܒܪ ܕܩܘܪܒܢܐ ܡܩܪܒ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 3, 'ܘܗܠܝܢ ܢܥܒܕܘܢ ܡܛܠ ܕܠܐ ܝܕܥܘ ܘܠܐ ܠܐܒܝ ܘܠܐ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 4, 'ܗܠܝܢ ܡܠܠܬ ܥܡܟܘܢ ܕܡܐ ܕܐܬܐ ܥܕܢܗܝܢ ܬܥܗܕܘܢ ܐܢܝܢ ܕܐܢܐ ܐܡܪܬ ܠܟܘܢ ܗܠܝܢ ܕܝܢ ܡܢ ܩܕܝܡ ܠܐ ܐܡܪܬ ܠܟܘܢ ܕܥܡܟܘܢ ܗܘܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 5, 'ܗܫܐ ܕܝܢ ܐܙܠ ܐܢܐ ܠܘܬ ܡܢ ܕܫܕܪܢܝ ܘܠܐ ܐܢܫ ܡܢܟܘܢ ܡܫܐܠ ܠܝ ܠܐܝܟܐ ܐܙܠ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 6, 'ܐܡܪܬ ܠܟܘܢ ܓܝܪ ܗܠܝܢ ܘܐܬܬ ܟܪܝܘܬܐ ܘܡܠܬ ܠܒܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 7, 'ܐܠܐ ܐܢܐ ܫܪܪܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܦܩܚ ܠܟܘܢ ܕܐܢܐ ܐܙܠ ܐܢ ܓܝܪ ܐܢܐ ܠܐ ܐܙܠ ܐܢܐ ܦܪܩܠܛܐ ܠܐ ܐܬܐ ܠܘܬܟܘܢ ܐܢ ܕܝܢ ܐܙܠ ܐܫܕܪܝܘܗܝ ܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 8, 'ܘܡܐ ܕܐܬܐ ܗܘ ܢܟܤܝܘܗܝ ܠܥܠܡܐ ܥܠ ܚܛܝܬܐ ܘܥܠ ܙܕܝܩܘܬܐ ܘܥܠ ܕܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 9, 'ܥܠ ܚܛܝܬܐ ܕܠܐ ܡܗܝܡܢܝܢ ܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 10, 'ܥܠ ܙܕܝܩܘܬܐ ܕܝܢ ܕܠܘܬ ܐܒܝ ܐܙܠ ܐܢܐ ܘܠܐ ܬܘܒ ܚܙܝܢ ܐܢܬܘܢ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 11, 'ܥܠ ܕܝܢܐ ܕܝܢ ܕܐܪܟܘܢܐ ܕܥܠܡܐ ܗܢܐ ܕܝܢ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 12, 'ܬܘܒ ܤܓܝ ܐܝܬ ܠܝ ܠܡܐܡܪ ܠܟܘܢ ܐܠܐ ܠܐ ܡܫܟܚܝܢ ܐܢܬܘܢ ܠܡܐܚܕ ܗܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 13, 'ܡܐ ܕܐܬܐ ܕܝܢ ܪܘܚܐ ܕܫܪܪܐ ܗܘ ܢܕܒܪܟܘܢ ܒܟܠܗ ܫܪܪܐ ܠܐ ܓܝܪ ܢܡܠܠ ܡܢ ܪܥܝܢ ܢܦܫܗ ܐܠܐ ܟܠ ܕܢܫܡܥ ܗܘ ܢܡܠܠ ܘܥܬܝܕܬܐ ܢܘܕܥܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 14, 'ܘܗܘ ܢܫܒܚܢܝ ܡܛܠ ܕܡܢ ܕܝܠܝ ܢܤܒ ܘܢܚܘܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 15, 'ܟܠ ܡܕܡ ܕܐܝܬ ܠܐܒܝ ܕܝܠܝ ܗܘ ܡܛܠ ܗܢܐ ܐܡܪܬ ܠܟܘܢ ܕܡܢ ܕܝܠܝ ܢܤܒ ܘܢܚܘܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 16, 'ܩܠܝܠ ܘܠܐ ܬܚܙܘܢܢܝ ܘܬܘܒ ܩܠܝܠ ܘܬܚܙܘܢܢܝ ܕܐܙܠ ܐܢܐ ܠܘܬ ܐܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 17, 'ܘܐܡܪܘ ܗܘܘ ܬܠܡܝܕܘܗܝ ܚܕ ܠܚܕ ܡܢܐ ܗܝ ܗܕܐ ܕܐܡܪ ܠܢ ܕܩܠܝܠ ܘܠܐ ܬܚܙܘܢܢܝ ܘܬܘܒ ܩܠܝܠ ܘܬܚܙܘܢܢܝ ܘܕܐܙܠ ܐܢܐ ܠܘܬ ܐܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 18, 'ܘܐܡܪܝܢ ܗܘܘ ܡܢܐ ܗܝ ܗܕܐ ܩܠܝܠ ܕܐܡܪ ܠܐ ܝܕܥܝܢܢ ܡܢܐ ܡܡܠܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 19, 'ܝܫܘܥ ܕܝܢ ܝܕܥ ܕܒܥܝܢ ܗܘܘ ܠܡܫܐܠܘܬܗ ܘܐܡܪ ܠܗܘܢ ܥܠ ܗܕܐ ܒܥܝܢ ܐܢܬܘܢ ܥܡ ܚܕܕܐ ܕܐܡܪܬ ܠܟܘܢ ܕܩܠܝܠ ܘܠܐ ܬܚܙܘܢܢܝ ܘܬܘܒ ܩܠܝܠ ܘܬܚܙܘܢܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 20, 'ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܬܒܟܘܢ ܐܢܬܘܢ ܘܬܐܠܘܢ ܘܥܠܡܐ ܢܚܕܐ ܘܠܟܘܢ ܬܟܪܐ ܐܠܐ ܟܪܝܘܬܟܘܢ ܠܚܕܘܬܐ ܬܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 21, 'ܐܢܬܬܐ ܡܐ ܕܝܠܕܐ ܟܪܝܐ ܠܗ ܕܡܛܐ ܝܘܡܐ ܕܡܘܠܕܗ ܡܐ ܕܝܠܕܬ ܕܝܢ ܒܪܐ ܠܐ ܥܗܕܐ ܐܘܠܨܢܗ ܡܛܠ ܚܕܘܬܐ ܕܐܬܝܠܕ ܒܪܢܫܐ ܒܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 22, 'ܐܦ ܐܢܬܘܢ ܗܫܐ ܟܪܝܐ ܠܟܘܢ ܬܘܒ ܕܝܢ ܐܚܙܝܟܘܢ ܘܢܚܕܐ ܠܒܟܘܢ ܘܚܕܘܬܟܘܢ ܠܐ ܐܢܫ ܢܤܒ ܡܢܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 23, 'ܘܒܗܘ ܝܘܡܐ ܠܝ ܠܐ ܬܫܐܠܘܢ ܡܕܡ ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܟܠ ܡܕܡ ܕܬܫܐܠܘܢ ܠܐܒܝ ܒܫܡܝ ܢܬܠ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 24, 'ܥܕܡܐ ܠܗܫܐ ܠܐ ܫܐܠܬܘܢ ܡܕܡ ܒܫܡܝ ܫܐܠܘ ܘܬܤܒܘܢ ܕܬܗܘܐ ܚܕܘܬܟܘܢ ܡܫܡܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 25, 'ܗܠܝܢ ܒܦܠܐܬܐ ܡܠܠܬ ܥܡܟܘܢ ܐܬܝܐ ܕܝܢ ܫܥܬܐ ܐܡܬܝ ܕܠܐ ܐܡܠܠ ܥܡܟܘܢ ܒܦܠܐܬܐ ܐܠܐ ܥܝܢ ܒܓܠܐ ܐܒܕܩ ܠܟܘܢ ܥܠ ܐܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 26, 'ܒܗܘ ܝܘܡܐ ܕܬܫܐܠܘܢ ܒܫܡܝ ܘܠܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܐܢܐ ܐܒܥܐ ܡܢ ܐܒܐ ܥܠܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 27, 'ܗܘ ܓܝܪ ܐܒܐ ܪܚܡ ܠܟܘܢ ܕܐܢܬܘܢ ܪܚܡܬܘܢܢܝ ܘܗܝܡܢܬܘܢ ܕܐܢܐ ܡܢ ܠܘܬ ܐܠܗܐ ܢܦܩܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 28, 'ܢܦܩܬ ܡܢ ܠܘܬ ܐܒܐ ܘܐܬܝܬ ܠܥܠܡܐ ܘܬܘܒ ܫܒܩ ܐܢܐ ܠܥܠܡܐ ܘܐܙܠ ܐܢܐ ܠܝ ܠܘܬ ܐܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 29, 'ܐܡܪܝܢ ܠܗ ܬܠܡܝܕܘܗܝ ܗܐ ܗܫܐ ܓܠܝܐܝܬ ܡܡܠܠ ܐܢܬ ܘܦܠܐܬܐ ܘܠܐ ܚܕܐ ܐܡܪ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 30, 'ܗܫܐ ܝܕܥܝܢ ܚܢܢ ܕܟܠ ܡܕܡ ܝܕܥ ܐܢܬ ܘܠܐ ܤܢܝܩ ܐܢܬ ܕܐܢܫ ܢܫܐܠܟ ܒܗܕܐ ܡܗܝܡܢܝܢ ܚܢܢ ܕܡܢ ܐܠܗܐ ܢܦܩܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 31, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܗܝܡܢܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 32, 'ܕܗܐ ܐܬܝܐ ܫܥܬܐ ܘܗܫܐ ܐܬܬ ܕܬܬܒܕܪܘܢ ܐܢܫ ܠܐܬܪܗ ܘܬܫܒܩܘܢܢܝ ܒܠܚܘܕܝ ܘܠܐ ܗܘܝܬ ܒܠܚܘܕܝ ܕܐܒܐ ܥܡܝ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 33, 'ܗܠܝܢ ܐܡܪܬ ܠܟܘܢ ܕܒܝ ܢܗܘܐ ܠܟܘܢ ܫܠܡܐ ܒܥܠܡܐ ܗܘܐ ܠܟܘܢ ܐܘܠܨܢܐ ܐܠܐ ܐܬܠܒܒܘ ܐܢܐ ܙܟܝܬܗ ܠܥܠܡܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 1, 'ܗܠܝܢ ܡܠܠ ܝܫܘܥ ܘܐܪܝܡ ܥܝܢܘܗܝ ܠܫܡܝܐ ܘܐܡܪ ܐܒܝ ܐܬܬ ܫܥܬܐ ܫܒܚ ܒܪܟ ܕܒܪܟ ܢܫܒܚܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 2, 'ܐܝܟܢܐ ܕܝܗܒܬ ܠܗ ܫܘܠܛܢܐ ܥܠ ܟܠ ܒܤܪ ܕܟܠ ܡܐ ܕܝܗܒܬ ܠܗ ܢܬܠ ܠܗ ܚܝܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 3, 'ܗܠܝܢ ܐܢܘܢ ܕܝܢ ܚܝܐ ܕܠܥܠܡ ܕܢܕܥܘܢܟ ܕܐܢܬ ܐܢܬ ܐܠܗܐ ܕܫܪܪܐ ܒܠܚܘܕܝܟ ܘܡܢ ܕܫܕܪܬ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 4, 'ܐܢܐ ܫܒܚܬܟ ܒܐܪܥܐ ܥܒܕܐ ܗܘ ܕܝܗܒܬ ܠܝ ܕܐܥܒܕ ܫܠܡܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 5, 'ܘܗܫܐ ܫܒܚܝܢܝ ܐܢܬ ܐܒܝ ܠܘܬܟ ܒܗܘ ܫܘܒܚܐ ܕܐܝܬ ܗܘܐ ܠܝ ܠܘܬܟ ܡܢ ܩܕܡ ܕܢܗܘܐ ܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 6, 'ܐܘܕܥܬ ܫܡܟ ܠܒܢܝ ܐܢܫܐ ܗܢܘܢ ܕܝܗܒܬ ܠܝ ܡܢ ܥܠܡܐ ܕܝܠܟ ܗܘܘ ܘܠܝ ܝܗܒܬ ܐܢܘܢ ܘܢܛܪܘ ܡܠܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 7, 'ܗܫܐ ܝܕܥܬ ܕܟܠ ܡܐ ܕܝܗܒܬ ܠܝ ܡܢ ܠܘܬܟ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 8, 'ܕܡܠܐ ܕܝܗܒܬ ܠܝ ܝܗܒܬ ܠܗܘܢ ܘܗܢܘܢ ܩܒܠܘ ܘܝܕܥܘ ܫܪܝܪܐܝܬ ܕܡܢ ܠܘܬܟ ܢܦܩܬ ܘܗܝܡܢܘ ܕܐܢܬ ܫܕܪܬܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 9, 'ܘܐܢܐ ܥܠܝܗܘܢ ܒܥܐ ܐܢܐ ܠܐ ܗܘܐ ܥܠ ܥܠܡܐ ܒܥܐ ܐܢܐ ܐܠܐ ܥܠ ܗܢܘܢ ܕܝܗܒܬ ܠܝ ܕܕܝܠܟ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 10, 'ܘܟܠ ܡܕܡ ܕܕܝܠܝ ܗܘ ܕܝܠܟ ܗܘ ܘܕܝܠܟ ܕܝܠܝ ܗܘ ܘܡܫܒܚ ܐܢܐ ܒܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 11, 'ܡܟܝܠ ܠܐ ܗܘܝܬ ܒܥܠܡܐ ܘܗܠܝܢ ܒܥܠܡܐ ܐܢܘܢ ܘܐܢܐ ܠܘܬܟ ܐܬܐ ܐܢܐ ܐܒܐ ܩܕܝܫܐ ܛܪ ܐܢܘܢ ܒܫܡܟ ܗܘ ܕܝܗܒܬ ܠܝ ܕܢܗܘܘܢ ܚܕ ܐܝܟܢܐ ܕܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 12, 'ܟܕ ܥܡܗܘܢ ܗܘܝܬ ܒܥܠܡܐ ܐܢܐ ܢܛܪ ܗܘܝܬ ܠܗܘܢ ܒܫܡܟ ܠܐܝܠܝܢ ܕܝܗܒܬ ܠܝ ܢܛܪܬ ܘܐܢܫ ܡܢܗܘܢ ܠܐ ܐܒܕ ܐܠܐ ܒܪܗ ܕܐܒܕܢܐ ܕܢܬܡܠܐ ܟܬܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 13, 'ܗܫܐ ܕܝܢ ܠܘܬܟ ܐܬܐ ܐܢܐ ܘܗܠܝܢ ܡܡܠܠ ܐܢܐ ܒܥܠܡܐ ܕܬܗܘܐ ܚܕܘܬܝ ܡܫܡܠܝܐ ܒܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 14, 'ܐܢܐ ܝܗܒܬ ܠܗܘܢ ܡܠܬܟ ܘܥܠܡܐ ܤܢܐ ܐܢܘܢ ܕܠܐ ܗܘܘ ܡܢ ܥܠܡܐ ܐܝܟܢܐ ܕܐܢܐ ܠܐ ܗܘܝܬ ܡܢ ܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 15, 'ܠܐ ܗܘܐ ܕܬܫܩܘܠ ܐܢܘܢ ܡܢ ܥܠܡܐ ܒܥܐ ܐܢܐ ܐܠܐ ܕܬܛܪ ܐܢܘܢ ܡܢ ܒܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 16, 'ܠܐ ܗܘܘ ܓܝܪ ܡܢ ܥܠܡܐ ܐܝܟܢܐ ܕܐܢܐ ܠܐ ܗܘܝܬ ܡܢ ܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 17, 'ܐܒܐ ܩܕܫ ܐܢܘܢ ܒܫܪܪܟ ܕܡܠܬܟ ܕܝܠܟ ܫܪܪܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 18, 'ܐܝܟܢܐ ܕܠܝ ܫܕܪܬ ܠܥܠܡܐ ܐܦ ܐܢܐ ܫܕܪܬ ܐܢܘܢ ܠܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 19, 'ܘܥܠ ܐܦܝܗܘܢ ܐܢܐ ܡܩܕܫ ܐܢܐ ܢܦܫܝ ܕܢܗܘܘܢ ܐܦ ܗܢܘܢ ܡܩܕܫܝܢ ܒܫܪܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 20, 'ܘܠܐ ܗܘܐ ܥܠ ܐܦܝ ܗܠܝܢ ܒܥܐ ܐܢܐ ܒܠܚܘܕ ܐܠܐ ܐܦ ܥܠ ܐܦܝ ܐܝܠܝܢ ܕܡܗܝܡܢܝܢ ܒܝ ܒܡܠܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 21, 'ܕܟܠܗܘܢ ܢܗܘܘܢ ܚܕ ܐܝܟܢܐ ܕܐܢܬ ܐܒܝ ܒܝ ܘܐܢܐ ܒܟ ܕܐܦ ܗܢܘܢ ܒܢ ܚܕ ܢܗܘܘܢ ܕܢܗܝܡܢ ܥܠܡܐ ܕܐܢܬ ܫܕܪܬܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 22, 'ܘܐܢܐ ܫܘܒܚܐ ܕܝܗܒܬ ܠܝ ܝܗܒܬ ܠܗܘܢ ܕܢܗܘܘܢ ܚܕ ܐܝܟܢܐ ܕܚܢܢ ܚܕ ܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 23, 'ܐܢܐ ܒܗܘܢ ܘܐܢܬ ܒܝ ܕܢܗܘܘܢ ܓܡܝܪܝܢ ܠܚܕ ܘܕܢܕܥ ܥܠܡܐ ܕܐܢܬ ܫܕܪܬܢܝ ܘܕܐܚܒܬ ܐܢܘܢ ܐܝܟܢܐ ܕܐܦ ܠܝ ܐܚܒܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 24, 'ܐܒܐ ܗܢܘܢ ܕܝܗܒܬ ܠܝ ܨܒܐ ܐܢܐ ܕܐܬܪ ܕܐܢܐ ܐܦ ܗܢܘܢ ܢܗܘܘܢ ܥܡܝ ܕܢܗܘܘܢ ܚܙܝܢ ܫܘܒܚܐ ܕܝܠܝ ܗܘ ܕܝܗܒܬ ܠܝ ܕܐܚܒܬܢܝ ܡܢ ܩܕܡ ܬܪܡܝܬܗ ܕܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 25, 'ܐܒܝ ܟܐܢܐ ܘܥܠܡܐ ܠܐ ܝܕܥܟ ܐܢܐ ܕܝܢ ܝܕܥܬܟ ܘܗܢܘܢ ܝܕܥܘ ܕܐܢܬ ܫܕܪܬܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 26, 'ܘܐܘܕܥܬ ܐܢܘܢ ܫܡܟ ܘܡܘܕܥ ܐܢܐ ܕܚܘܒܐ ܗܘ ܕܐܚܒܬܢܝ ܢܗܘܐ ܒܗܘܢ ܘܐܢܐ ܐܗܘܐ ܒܗܘܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 1, 'ܗܠܝܢ ܐܡܪ ܝܫܘܥ ܘܢܦܩ ܥܡ ܬܠܡܝܕܘܗܝ ܠܥܒܪܐ ܕܪܓܠܬܐ ܕܩܕܪܘܢ ܐܬܪ ܕܐܝܬ ܗܘܬ ܓܢܬܐ ܐܝܟܐ ܕܥܠ ܗܘ ܘܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 2, 'ܝܕܥ ܗܘܐ ܕܝܢ ܐܦ ܝܗܘܕܐ ܡܫܠܡܢܐ ܠܕܘܟܬܐ ܗܝ ܡܛܠ ܕܤܓܝ ܙܒܢܐ ܟܢܫ ܗܘܐ ܬܡܢ ܝܫܘܥ ܥܡ ܬܠܡܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 3, 'ܗܘ ܗܟܝܠ ܝܗܘܕܐ ܕܒܪ ܐܤܦܝܪ ܘܡܢ ܠܘܬ ܪܒܝ ܟܗܢܐ ܘܦܪܝܫܐ ܕܒܪ ܕܚܫܐ ܘܐܬܐ ܠܬܡܢ ܥܡ ܢܦܛܪܐ ܘܠܡܦܝܕܐ ܘܙܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 4, 'ܝܫܘܥ ܕܝܢ ܕܝܕܥ ܗܘܐ ܟܠ ܡܕܡ ܕܐܬܐ ܥܠܘܗܝ ܢܦܩ ܘܐܡܪ ܠܗܘܢ ܠܡܢ ܒܥܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 5, 'ܐܡܪܝܢ ܠܗ ܠܝܫܘܥ ܢܨܪܝܐ ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܢܐ ܐܢܐ ܩܐܡ ܗܘܐ ܕܝܢ ܐܦ ܝܗܘܕܐ ܡܫܠܡܢܐ ܥܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 6, 'ܘܟܕ ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܕܐܢܐ ܐܢܐ ܐܙܠܘ ܠܒܤܬܪܗܘܢ ܘܢܦܠܘ ܥܠ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 7, 'ܬܘܒ ܫܐܠ ܐܢܘܢ ܝܫܘܥ ܠܡܢ ܒܥܝܢ ܐܢܬܘܢ ܗܢܘܢ ܕܝܢ ܐܡܪܘ ܠܝܫܘܥ ܢܨܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 8, 'ܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܡܪܬ ܠܟܘܢ ܕܐܢܐ ܐܢܐ ܘܐܢ ܠܝ ܒܥܝܢ ܐܢܬܘܢ ܫܒܘܩܘ ܠܗܠܝܢ ܐܙܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 9, 'ܕܬܫܠܡ ܡܠܬܐ ܕܐܡܪ ܕܐܝܠܝܢ ܕܝܗܒܬ ܠܝ ܠܐ ܐܘܒܕܬ ܡܢܗܘܢ ܐܦܠܐ ܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 10, 'ܫܡܥܘܢ ܕܝܢ ܟܐܦܐ ܐܝܬ ܗܘܐ ܥܠܘܗܝ ܤܦܤܪܐ ܘܫܡܛܗ ܘܡܚܝܗܝ ܠܥܒܕܗ ܕܪܒ ܟܗܢܐ ܘܫܩܠܗ ܐܕܢܗ ܕܝܡܝܢܐ ܫܡܗ ܕܝܢ ܕܥܒܕܐ ܡܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 11, 'ܘܐܡܪ ܝܫܘܥ ܠܟܐܦܐ ܤܝܡ ܤܦܤܪܐ ܒܚܠܬܗ ܟܤܐ ܕܝܗܒ ܠܝ ܐܒܝ ܠܐ ܐܫܬܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 12, 'ܗܝܕܝܢ ܐܤܦܝܪ ܘܟܠܝܪܟܐ ܘܕܚܫܐ ܕܝܗܘܕܝܐ ܐܚܕܘܗܝ ܠܝܫܘܥ ܘܐܤܪܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 13, 'ܘܐܝܬܝܘܗܝ ܠܘܬ ܚܢܢ ܠܘܩܕܡ ܡܛܠ ܕܚܡܘܗܝ ܗܘܐ ܕܩܝܦܐ ܗܘ ܕܐܝܬܘܗܝ ܗܘܐ ܪܒ ܟܗܢܐ ܕܫܢܬܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 14, 'ܐܝܬܘܗܝ ܗܘܐ ܕܝܢ ܩܝܦܐ ܗܘ ܕܡܠܟ ܠܝܗܘܕܝܐ ܕܦܩܚ ܕܚܕ ܓܒܪܐ ܢܡܘܬ ܚܠܦ ܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 15, 'ܫܡܥܘܢ ܕܝܢ ܟܐܦܐ ܘܚܕ ܡܢ ܬܠܡܝܕܐ ܐܚܪܢܐ ܐܬܝܢ ܗܘܘ ܒܬܪܗ ܕܝܫܘܥ ܠܗܘ ܕܝܢ ܬܠܡܝܕܐ ܝܕܥ ܗܘܐ ܠܗ ܪܒ ܟܗܢܐ ܘܥܠ ܥܡ ܝܫܘܥ ܠܕܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 16, 'ܫܡܥܘܢ ܕܝܢ ܩܐܡ ܗܘܐ ܠܒܪ ܠܘܬ ܬܪܥܐ ܘܢܦܩ ܗܘ ܬܠܡܝܕܐ ܐܚܪܢܐ ܕܝܕܥ ܗܘܐ ܠܗ ܪܒ ܟܗܢܐ ܘܐܡܪ ܠܢܛܪܬ ܬܪܥܐ ܘܐܥܠܗ ܠܫܡܥܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 17, 'ܐܡܪܬ ܕܝܢ ܥܠܝܡܬܐ ܢܛܪܬ ܬܪܥܐ ܠܫܡܥܘܢ ܠܡܐ ܐܦ ܐܢܬ ܡܢ ܬܠܡܝܕܘܗܝ ܐܢܬ ܕܗܢܐ ܓܒܪܐ ܐܡܪ ܠܗ ܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 18, 'ܘܩܝܡܝܢ ܗܘܘ ܥܒܕܐ ܘܕܚܫܐ ܘܤܝܡܝܢ ܗܘܘ ܢܘܪܐ ܕܢܫܚܢܘܢ ܡܛܠ ܕܩܪܝܫ ܗܘܐ ܩܐܡ ܗܘܐ ܕܝܢ ܐܦ ܫܡܥܘܢ ܥܡܗܘܢ ܘܫܚܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 19, 'ܪܒ ܟܗܢܐ ܕܝܢ ܫܐܠܗ ܠܝܫܘܥ ܥܠ ܬܠܡܝܕܘܗܝ ܘܥܠ ܝܘܠܦܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 20, 'ܘܐܡܪ ܠܗ ܝܫܘܥ ܐܢܐ ܥܝܢ ܒܓܠܐ ܡܠܠܬ ܥܡ ܥܡܐ ܘܒܟܠܙܒܢ ܐܠܦܬ ܒܟܢܘܫܬܐ ܘܒܗܝܟܠܐ ܐܝܟܐ ܕܟܠܗܘܢ ܝܗܘܕܝܐ ܡܬܟܢܫܝܢ ܘܡܕܡ ܒܛܘܫܝܐ ܠܐ ܡܠܠܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 21, 'ܡܢܐ ܡܫܐܠ ܐܢܬ ܠܝ ܫܐܠ ܠܗܢܘܢ ܕܫܡܥܘ ܡܢܐ ܡܠܠܬ ܥܡܗܘܢ ܗܐ ܗܢܘܢ ܝܕܥܝܢ ܟܠ ܡܕܡ ܕܐܡܪܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 22, 'ܘܟܕ ܗܠܝܢ ܐܡܪ ܚܕ ܡܢ ܕܚܫܐ ܕܩܐܡ ܗܘܐ ܡܚܝܗܝ ܥܠ ܦܟܗ ܠܝܫܘܥ ܘܐܡܪ ܠܗ ܗܟܢܐ ܝܗܒ ܐܢܬ ܦܬܓܡܐ ܠܪܒ ܟܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 23, 'ܥܢܐ ܝܫܘܥ ܘܐܡܪ ܠܗ ܐܢ ܒܝܫܐܝܬ ܡܠܠܬ ܐܤܗܕ ܥܠ ܒܝܫܬܐ ܘܐܢ ܕܝܢ ܫܦܝܪ ܠܡܢܐ ܡܚܝܬܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 24, 'ܚܢܢ ܕܝܢ ܫܕܪ ܠܝܫܘܥ ܟܕ ܐܤܝܪ ܠܘܬ ܩܝܦܐ ܪܒ ܟܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 25, 'ܘܫܡܥܘܢ ܟܐܦܐ ܩܐܡ ܗܘܐ ܘܫܚܢ ܘܐܡܪܝܢ ܠܗ ܠܡܐ ܐܦ ܐܢܬ ܚܕ ܡܢ ܬܠܡܝܕܘܗܝ ܐܢܬ ܘܗܘ ܟܦܪ ܘܐܡܪ ܠܐ ܗܘܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 26, 'ܐܡܪ ܠܗ ܚܕ ܡܢ ܥܒܕܐ ܕܪܒ ܟܗܢܐ ܐܚܝܢܗ ܕܗܘ ܕܦܤܩ ܗܘܐ ܫܡܥܘܢ ܐܕܢܗ ܠܐ ܐܢܐ ܚܙܝܬܟ ܥܡܗ ܒܓܢܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 27, 'ܘܬܘܒ ܟܦܪ ܫܡܥܘܢ ܘܒܗ ܒܫܥܬܐ ܩܪܐ ܬܪܢܓܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 28, 'ܐܝܬܝܘܗܝ ܕܝܢ ܠܝܫܘܥ ܡܢ ܠܘܬ ܩܝܦܐ ܠܦܪܛܘܪܝܢ ܘܐܝܬܘܗܝ ܗܘܐ ܨܦܪܐ ܘܗܢܘܢ ܠܐ ܥܠܘ ܠܦܪܛܘܪܝܢ ܕܠܐ ܢܬܛܘܫܘܢ ܥܕ ܐܟܠܝܢ ܦܨܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 29, 'ܢܦܩ ܕܝܢ ܦܝܠܛܘܤ ܠܒܪ ܠܘܬܗܘܢ ܘܐܡܪ ܠܗܘܢ ܡܢܐ ܡܐܟܠ ܩܪܨܐ ܐܝܬ ܠܟܘܢ ܥܠ ܓܒܪܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 30, 'ܥܢܘ ܘܐܡܪܝܢ ܠܗ ܐܠܘ ܠܐ ܥܒܕ ܒܝܫܬܐ ܗܘܐ ܐܦܠܐ ܠܟ ܡܫܠܡܝܢ ܗܘܝܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 31, 'ܐܡܪ ܠܗܘܢ ܦܝܠܛܘܤ ܕܘܒܪܘܗܝ ܐܢܬܘܢ ܘܕܘܢܘܗܝ ܐܝܟ ܢܡܘܤܟܘܢ ܐܡܪܝܢ ܠܗ ܝܗܘܕܝܐ ܠܐ ܫܠܝܛ ܠܢ ܠܡܩܛܠ ܠܐܢܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 32, 'ܕܬܫܠܡ ܡܠܬܐ ܕܐܡܪ ܝܫܘܥ ܟܕ ܡܘܕܥ ܒܐܝܢܐ ܡܘܬܐ ܥܬܝܕ ܕܢܡܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 33, 'ܥܠ ܕܝܢ ܦܝܠܛܘܤ ܠܦܪܛܘܪܝܢ ܘܩܪܐ ܠܝܫܘܥ ܘܐܡܪ ܠܗ ܐܢܬ ܗܘ ܡܠܟܗܘܢ ܕܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 34, 'ܐܡܪ ܠܗ ܝܫܘܥ ܡܢ ܢܦܫܟ ܐܡܪܬ ܗܕܐ ܐܘ ܐܚܪܢܐ ܐܡܪܘ ܠܟ ܥܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 35, 'ܐܡܪ ܠܗ ܦܝܠܛܘܤ ܠܡܐ ܐܢܐ ܝܗܘܕܝܐ ܐܢܐ ܒܢܝ ܥܡܟ ܗܘ ܘܪܒܝ ܟܗܢܐ ܐܫܠܡܘܟ ܠܝ ܡܢܐ ܥܒܕܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 36, 'ܐܡܪ ܠܗ ܝܫܘܥ ܡܠܟܘܬܝ ܕܝܠܝ ܠܐ ܗܘܬ ܡܢ ܗܢܐ ܥܠܡܐ ܐܠܘ ܡܢ ܥܠܡܐ ܗܘܬ ܗܢܐ ܡܠܟܘܬܝ ܡܬܟܬܫܝܢ ܗܘܘ ܡܫܡܫܢܝ ܕܠܐ ܐܫܬܠܡ ܠܝܗܘܕܝܐ ܗܫܐ ܕܝܢ ܡܠܟܘܬܝ ܕܝܠܝ ܠܐ ܗܘܬ ܡܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 37, 'ܐܡܪ ܠܗ ܦܝܠܛܘܤ ܡܕܝܢ ܡܠܟܐ ܐܢܬ ܐܡܪ ܠܗ ܝܫܘܥ ܐܢܬ ܐܡܪܬ ܕܡܠܟܐ ܐܢܐ ܐܢܐ ܠܗܕܐ ܝܠܝܕ ܐܢܐ ܘܠܗܕܐ ܐܬܝܬ ܠܥܠܡܐ ܕܐܤܗܕ ܥܠ ܫܪܪܐ ܟܠ ܡܢ ܕܐܝܬܘܗܝ ܡܢ ܫܪܪܐ ܫܡܥ ܩܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 38, 'ܐܡܪ ܠܗ ܦܝܠܛܘܤ ܡܢܘ ܫܪܪܐ ܘܟܕ ܐܡܪ ܗܕܐ ܢܦܩ ܠܗ ܬܘܒ ܠܘܬ ܝܗܘܕܝܐ ܘܐܡܪ ܠܗܘܢ ܐܢܐ ܐܦܠܐ ܚܕܐ ܥܠܬܐ ܡܫܟܚ ܐܢܐ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 39, 'ܥܝܕܐ ܕܝܢ ܐܝܬ ܠܟܘܢ ܕܚܕ ܐܫܪܐ ܠܟܘܢ ܒܦܨܚܐ ܨܒܝܢ ܐܢܬܘܢ ܗܟܝܠ ܐܫܪܐ ܠܟܘܢ ܠܗܢܐ ܡܠܟܐ ܕܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 40, 'ܘܩܥܘ ܟܠܗܘܢ ܘܐܡܪܝܢ ܠܐ ܠܗܢܐ ܐܠܐ ܠܒܪ ܐܒܐ ܐܝܬܘܗܝ ܗܘܐ ܕܝܢ ܗܢܐ ܒܪ ܐܒܐ ܓܝܤܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 1, 'ܗܝܕܝܢ ܦܝܠܛܘܤ ܢܓܕܗ ܠܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 2, 'ܘܐܤܛܪܛܝܘܛܐ ܓܕܠܘ ܟܠܝܠܐ ܡܢ ܟܘܒܐ ܘܤܡܘ ܠܗ ܒܪܫܗ ܘܟܤܝܘܗܝ ܢܚܬܐ ܕܐܪܓܘܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 3, 'ܘܐܡܪܝܢ ܗܘܘ ܫܠܡ ܠܟ ܡܠܟܐ ܕܝܗܘܕܝܐ ܘܡܚܝܢ ܗܘܘ ܠܗ ܥܠ ܦܟܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 4, 'ܘܢܦܩ ܦܝܠܛܘܤ ܬܘܒ ܠܒܪ ܘܐܡܪ ܠܗܘܢ ܗܐ ܡܦܩ ܐܢܐ ܠܗ ܠܟܘܢ ܠܒܪ ܕܬܕܥܘܢ ܕܠܐ ܡܫܟܚ ܐܢܐ ܒܬܪܗ ܐܦܠܐ ܚܕܐ ܥܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 5, 'ܘܢܦܩ ܝܫܘܥ ܠܒܪ ܟܕ ܐܝܬ ܥܠܘܗܝ ܟܠܝܠܐ ܕܟܘܒܐ ܘܢܚܬܐ ܕܐܪܓܘܢܐ ܘܐܡܪ ܠܗܘܢ ܦܝܠܛܘܤ ܗܐ ܓܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 6, 'ܟܕ ܕܝܢ ܚܙܐܘܗܝ ܪܒܝ ܟܗܢܐ ܘܕܚܫܐ ܩܥܘ ܘܐܡܪܝܢ ܨܠܘܒܝܗܝ ܨܠܘܒܝܗܝ ܐܡܪ ܠܗܘܢ ܦܝܠܛܘܤ ܕܒܪܘ ܐܢܬܘܢ ܘܙܘܩܦܘܗܝ ܐܢܐ ܓܝܪ ܠܐ ܡܫܟܚ ܐܢܐ ܒܗ ܥܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 7, 'ܐܡܪܝܢ ܠܗ ܝܗܘܕܝܐ ܠܢ ܢܡܘܤܐ ܐܝܬ ܠܢ ܘܐܝܟ ܕܒܢܡܘܤܢ ܚܝܒ ܗܘ ܡܘܬܐ ܕܥܒܕ ܢܦܫܗ ܒܪܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 8, 'ܟܕ ܫܡܥ ܕܝܢ ܦܝܠܛܘܤ ܗܕܐ ܡܠܬܐ ܝܬܝܪܐܝܬ ܕܚܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 9, 'ܘܥܠ ܬܘܒ ܠܦܪܛܘܪܝܢ ܘܐܡܪ ܠܝܫܘܥ ܐܝܡܟܐ ܐܢܬ ܝܫܘܥ ܕܝܢ ܦܬܓܡܐ ܠܐ ܝܗܒ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 10, 'ܐܡܪ ܠܗ ܦܝܠܛܘܤ ܥܡܝ ܠܐ ܡܡܠܠ ܐܢܬ ܠܐ ܝܕܥ ܐܢܬ ܕܫܠܝܛ ܐܢܐ ܕܐܫܪܝܟ ܘܫܠܝܛ ܐܢܐ ܕܐܙܩܦܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 11, 'ܐܡܪ ܠܗ ܝܫܘܥ ܠܝܬ ܗܘܐ ܠܟ ܥܠܝ ܫܘܠܛܢܐ ܐܦ ܠܐ ܚܕ ܐܠܘ ܠܐ ܝܗܝܒ ܗܘܐ ܠܟ ܡܢ ܠܥܠ ܡܛܠ ܗܢܐ ܗܘ ܡܢ ܕܐܫܠܡܢܝ ܠܟ ܪܒܐ ܗܝ ܚܛܝܬܗ ܡܢ ܕܝܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 12, 'ܘܡܛܠ ܗܕܐ ܨܒܐ ܗܘܐ ܦܝܠܛܘܤ ܕܢܫܪܝܘܗܝ ܝܗܘܕܝܐ ܕܝܢ ܩܥܝܢ ܗܘܘ ܕܐܢ ܠܗܢܐ ܫܪܐ ܐܢܬ ܠܐ ܗܘܝܬ ܪܚܡܗ ܕܩܤܪ ܟܠ ܡܢ ܓܝܪ ܕܢܦܫܗ ܡܠܟܐ ܥܒܕ ܤܩܘܒܠܐ ܗܘ ܕܩܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 13, 'ܟܕ ܫܡܥ ܕܝܢ ܦܝܠܛܘܤ ܗܕܐ ܡܠܬܐ ܐܦܩܗ ܠܝܫܘܥ ܠܒܪ ܘܝܬܒ ܥܠ ܒܝܡ ܒܕܘܟܬܐ ܕܡܬܩܪܝܐ ܪܨܝܦܬܐ ܕܟܐܦܐ ܥܒܪܐܝܬ ܕܝܢ ܡܬܐܡܪܐ ܓܦܝܦܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 14, 'ܘܥܪܘܒܬܐ ܗܘܬ ܕܦܨܚܐ ܘܐܝܬ ܗܘܝ ܐܝܟ ܫܥܐ ܫܬ ܘܐܡܪ ܠܝܗܘܕܝܐ ܗܐ ܡܠܟܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 15, 'ܗܢܘܢ ܕܝܢ ܩܥܝܢ ܗܘܘ ܫܩܘܠܝܗܝ ܫܩܘܠܝܗܝ ܨܠܘܒܝܗܝ ܨܠܘܒܝܗܝ ܐܡܪ ܠܗܘܢ ܦܝܠܛܘܤ ܠܡܠܟܟܘܢ ܐܙܩܘܦ ܐܡܪܝܢ ܪܒܝ ܟܗܢܐ ܠܝܬ ܠܢ ܡܠܟܐ ܐܠܐ ܐܢ ܩܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 16, 'ܗܝܕܝܢ ܐܫܠܡܗ ܠܗܘܢ ܕܢܙܩܦܘܢܝܗܝ ܘܕܒܪܘܗܝ ܠܝܫܘܥ ܘܐܦܩܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 17, 'ܟܕ ܫܩܝܠ ܙܩܝܦܗ ܠܕܘܟܬܐ ܕܡܬܩܪܝܐ ܩܪܩܦܬܐ ܥܒܪܐܝܬ ܕܝܢ ܡܬܐܡܪܐ ܓܓܘܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 18, 'ܐܬܪ ܕܙܩܦܘܗܝ ܘܥܡܗ ܬܪܝܢ ܐܚܪܢܝܢ ܚܕ ܡܟܐ ܘܚܕ ܡܟܐ ܘܠܝܫܘܥ ܒܡܨܥܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 19, 'ܘܟܬܒ ܐܦ ܠܘܚܐ ܦܝܠܛܘܤ ܘܤܡ ܥܠ ܙܩܝܦܗ ܟܬܝܒ ܗܘܐ ܕܝܢ ܗܟܢܐ ܗܢܐ ܝܫܘܥ ܢܨܪܝܐ ܡܠܟܐ ܕܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 20, 'ܘܠܗܢܐ ܕܦܐ ܤܓܝܐܐ ܡܢ ܝܗܘܕܝܐ ܩܪܐܘܗܝ ܡܛܠ ܕܩܪܝܒܐ ܗܘܬ ܠܡܕܝܢܬܐ ܕܘܟܬܐ ܕܐܙܕܩܦ ܒܗ ܝܫܘܥ ܘܟܬܝܒܐ ܗܘܐ ܥܒܪܐܝܬ ܘܝܘܢܐܝܬ ܘܪܗܘܡܐܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 21, 'ܘܐܡܪܘ ܪܒܝ ܟܗܢܐ ܠܦܝܠܛܘܤ ܠܐ ܬܟܬܘܒ ܕܡܠܟܐ ܗܘ ܕܝܗܘܕܝܐ ܐܠܐ ܕܗܘ ܐܡܪ ܕܡܠܟܐ ܐܢܐ ܕܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 22, 'ܐܡܪ ܦܝܠܛܘܤ ܡܕܡ ܕܟܬܒܬ ܟܬܒܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 23, 'ܐܤܛܪܛܝܘܛܐ ܕܝܢ ܟܕ ܙܩܦܘܗܝ ܠܝܫܘܥ ܫܩܠܘ ܢܚܬܘܗܝ ܘܥܒܕܘ ܠܐܪܒܥ ܡܢܘܢ ܡܢܬܐ ܠܚܕ ܡܢ ܐܤܛܪܛܝܘܛܐ ܟܘܬܝܢܗ ܕܝܢ ܐܝܬܝܗ ܗܘܬ ܕܠܐ ܚܝܛܐ ܡܢ ܠܥܠ ܙܩܝܪܬܐ ܟܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 24, 'ܘܐܡܪܘ ܚܕ ܠܚܕ ܠܐ ܢܤܕܩܝܗ ܐܠܐ ܢܦܤ ܥܠܝܗ ܡܦܤ ܕܡܢܘ ܬܗܘܐ ܘܫܠܡ ܟܬܒܐ ܕܐܡܪ ܕܦܠܓܘ ܢܚܬܝ ܒܝܢܬܗܘܢ ܘܥܠ ܠܒܘܫܝ ܐܪܡܝܘ ܦܤܐ ܗܠܝܢ ܥܒܕܘ ܐܤܛܪܛܝܘܛܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 25, 'ܩܝܡܢ ܗܘܝ ܕܝܢ ܠܘܬ ܙܩܝܦܗ ܕܝܫܘܥ ܐܡܗ ܘܚܬܗ ܕܐܡܗ ܘܡܪܝܡ ܗܝ ܕܩܠܝܘܦܐ ܘܡܪܝܡ ܡܓܕܠܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 26, 'ܝܫܘܥ ܕܝܢ ܚܙܐ ܠܐܡܗ ܘܠܬܠܡܝܕܐ ܗܘ ܕܪܚܡ ܗܘܐ ܕܩܐܡ ܘܐܡܪ ܠܐܡܗ ܐܢܬܬܐ ܗܐ ܒܪܟܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 27, 'ܘܐܡܪ ܠܬܠܡܝܕܐ ܗܘ ܗܐ ܐܡܟ ܘܡܢ ܗܝ ܫܥܬܐ ܕܒܪܗ ܬܠܡܝܕܐ ܗܘ ܠܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 28, 'ܒܬܪ ܗܠܝܢ ܝܕܥ ܝܫܘܥ ܕܟܠܡܕܡ ܐܫܬܠܡ ܘܕܢܬܡܠܐ ܟܬܒܐ ܐܡܪ ܨܗܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 29, 'ܘܡܐܢܐ ܤܝܡ ܗܘܐ ܕܡܠܐ ܚܠܐ ܗܢܘܢ ܕܝܢ ܡܠܘ ܐܤܦܘܓܐ ܡܢ ܚܠܐ ܘܤܡܘ ܥܠ ܙܘܦܐ ܘܩܪܒܘ ܠܘܬ ܦܘܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 30, 'ܟܕ ܕܝܢ ܫܩܠ ܗܘ ܚܠܐ ܝܫܘܥ ܐܡܪ ܗܐ ܡܫܠܡ ܘܐܪܟܢ ܪܫܗ ܘܐܫܠܡ ܪܘܚܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 31, 'ܝܗܘܕܝܐ ܕܝܢ ܡܛܠ ܕܥܪܘܒܬܐ ܗܘܬ ܐܡܪܝܢ ܠܐ ܢܒܘܬܘܢ ܦܓܪܐ ܗܠܝܢ ܥܠ ܙܩܝܦܝܗܘܢ ܡܛܠ ܕܫܒܬܐ ܢܓܗܐ ܝܘܡܐ ܗܘܐ ܓܝܪ ܪܒܐ ܝܘܡܐ ܕܫܒܬܐ ܗܝ ܘܒܥܘ ܡܢ ܦܝܠܛܘܤ ܕܢܬܒܪܘܢ ܫܩܝܗܘܢ ܕܗܢܘܢ ܙܩܝܦܐ ܘܢܚܬܘܢ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 32, 'ܘܐܬܘ ܐܤܛܪܛܝܘܛܐ ܘܬܒܪܘ ܫܩܘܗܝ ܕܩܕܡܝܐ ܘܕܗܘ ܐܚܪܢܐ ܕܐܙܕܩܦ ܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 33, 'ܘܟܕ ܐܬܘ ܠܘܬ ܝܫܘܥ ܚܙܘ ܕܡܝܬ ܠܗ ܡܢ ܟܕܘ ܘܠܐ ܬܒܪܘ ܫܩܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 34, 'ܐܠܐ ܚܕ ܡܢ ܐܤܛܪܛܝܘܛܐ ܡܚܝܗܝ ܒܕܦܢܗ ܒܠܘܟܝܬܐ ܘܡܚܕܐ ܢܦܩ ܕܡܐ ܘܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 35, 'ܘܡܢ ܕܚܙܐ ܐܤܗܕ ܘܫܪܝܪܐ ܗܝ ܤܗܕܘܬܗ ܘܗܘ ܝܕܥ ܕܫܪܪܐ ܐܡܪ ܕܐܦ ܐܢܬܘܢ ܬܗܝܡܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 36, 'ܗܠܝܢ ܓܝܪ ܗܘܝ ܕܢܬܡܠܐ ܟܬܒܐ ܕܐܡܪ ܕܓܪܡܐ ܠܐ ܢܬܬܒܪ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 37, 'ܘܬܘܒ ܟܬܒܐ ܐܚܪܢܐ ܕܐܡܪ ܕܢܚܘܪܘܢ ܒܡܢ ܕܕܩܪܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 38, 'ܒܬܪ ܗܠܝܢ ܝܘܤܦ ܗܘ ܕܡܢ ܪܡܬܐ ܒܥܐ ܡܢ ܦܝܠܛܘܤ ܡܛܠ ܕܬܠܡܝܕܐ ܗܘܐ ܕܝܫܘܥ ܘܡܛܫܐ ܗܘܐ ܡܢ ܕܚܠܬܐ ܕܝܗܘܕܝܐ ܕܢܫܩܘܠ ܦܓܪܗ ܕܝܫܘܥ ܘܐܦܤ ܦܝܠܛܘܤ ܘܐܬܐ ܘܫܩܠ ܦܓܪܗ ܕܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 39, 'ܘܐܬܐ ܐܦ ܢܝܩܕܡܘܤ ܗܘ ܕܐܬܐ ܗܘܐ ܡܢ ܩܕܝܡ ܠܘܬ ܝܫܘܥ ܒܠܠܝܐ ܘܐܝܬܝ ܥܡܗ ܚܘܢܛܬܐ ܕܡܘܪܐ ܘܕܥܠܘܝ ܐܝܟ ܡܐܐ ܠܝܛܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 40, 'ܘܫܩܠܘܗܝ ܠܦܓܪܗ ܕܝܫܘܥ ܘܟܪܟܘܗܝ ܒܟܬܢܐ ܘܒܒܤܡܐ ܐܝܟܢܐ ܕܐܝܬ ܥܝܕܐ ܠܝܗܘܕܝܐ ܕܢܩܒܪܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 41, 'ܐܝܬ ܗܘܬ ܕܝܢ ܒܗܝ ܕܘܟܬܐ ܕܐܙܕܩܦ ܒܗ ܝܫܘܥ ܓܢܬܐ ܘܒܗ ܒܓܢܬܐ ܒܝܬ ܩܒܘܪܐ ܚܕܬܐ ܕܐܢܫ ܥܕܟܝܠ ܠܐ ܐܬܬܤܝܡ ܗܘܐ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 42, 'ܘܤܡܘܗܝ ܬܡܢ ܠܝܫܘܥ ܡܛܠ ܕܫܒܬܐ ܥܐܠܐ ܗܘܬ ܘܡܛܠ ܕܩܪܝܒ ܗܘܐ ܩܒܪܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 1, 'ܒܚܕ ܒܫܒܐ ܕܝܢ ܐܬܬ ܡܪܝܡ ܡܓܕܠܝܬܐ ܒܨܦܪܐ ܥܕ ܚܫܘܟ ܠܒܝܬ ܩܒܘܪܐ ܘܚܙܬ ܠܟܐܦܐ ܕܫܩܝܠܐ ܡܢ ܩܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 2, 'ܘܪܗܛܬ ܐܬܬ ܠܘܬ ܫܡܥܘܢ ܟܐܦܐ ܘܠܘܬ ܗܘ ܬܠܡܝܕܐ ܐܚܪܢܐ ܕܪܚܡ ܗܘܐ ܝܫܘܥ ܘܐܡܪܐ ܠܗܘܢ ܕܫܩܠܘܗܝ ܠܡܪܢ ܡܢ ܗܘ ܒܝܬ ܩܒܘܪܐ ܘܠܐ ܝܕܥܐ ܐܢܐ ܐܝܟܐ ܤܡܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 3, 'ܘܢܦܩ ܫܡܥܘܢ ܘܗܘ ܬܠܡܝܕܐ ܐܚܪܢܐ ܘܐܬܝܢ ܗܘܘ ܠܒܝܬ ܩܒܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 4, 'ܘܪܗܛܝܢ ܗܘܘ ܬܪܝܗܘܢ ܐܟܚܕܐ ܗܘ ܕܝܢ ܬܠܡܝܕܐ ܪܗܛ ܩܕܡܗ ܠܫܡܥܘܢ ܘܐܬܐ ܩܕܡܝܐ ܠܒܝܬ ܩܒܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 5, 'ܘܐܕܝܩ ܚܙܐ ܟܬܢܐ ܟܕ ܤܝܡܝܢ ܡܥܠ ܕܝܢ ܠܐ ܥܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 6, 'ܐܬܐ ܕܝܢ ܫܡܥܘܢ ܒܬܪܗ ܘܥܠ ܠܒܝܬ ܩܒܘܪܐ ܘܚܙܐ ܟܬܢܐ ܟܕ ܤܝܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 7, 'ܘܤܘܕܪܐ ܗܘ ܕܚܙܝܩ ܗܘܐ ܒܪܫܗ ܠܐ ܥܡ ܟܬܢܐ ܐܠܐ ܟܕ ܟܪܝܟ ܘܤܝܡ ܠܤܛܪ ܒܚܕܐ ܕܘܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 8, 'ܗܝܕܝܢ ܥܠ ܐܦ ܗܘ ܬܠܡܝܕܐ ܕܐܬܐ ܩܕܡܝܐ ܠܒܝܬ ܩܒܘܪܐ ܘܚܙܐ ܘܗܝܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 9, 'ܠܐ ܓܝܪ ܥܕܟܝܠ ܝܕܥܝܢ ܗܘܘ ܡܢ ܟܬܒܐ ܕܥܬܝܕ ܗܘܐ ܠܡܩܡ ܡܢ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 10, 'ܘܐܙܠܘ ܗܢܘܢ ܬܠܡܝܕܐ ܬܘܒ ܠܕܘܟܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 11, 'ܡܪܝܡ ܕܝܢ ܩܝܡܐ ܗܘܬ ܠܘܬ ܩܒܪܐ ܘܒܟܝܐ ܘܟܕ ܒܟܝܐ ܐܕܝܩܬ ܒܩܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 12, 'ܘܚܙܬ ܬܪܝܢ ܡܠܐܟܐ ܒܚܘܪܐ ܕܝܬܒܝܢ ܚܕ ܡܢ ܐܤܕܘܗܝ ܘܚܕ ܡܢ ܪܓܠܘܗܝ ܐܝܟܐ ܕܤܝܡ ܗܘܐ ܦܓܪܗ ܕܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 13, 'ܘܐܡܪܝܢ ܠܗ ܐܢܬܬܐ ܡܢܐ ܒܟܝܐ ܐܢܬܝ ܐܡܪܐ ܠܗܘܢ ܕܫܩܠܘܗܝ ܠܡܪܝ ܘܠܐ ܝܕܥܐ ܐܢܐ ܐܝܟܐ ܤܡܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 14, 'ܗܕܐ ܐܡܪܬ ܘܐܬܦܢܝܬ ܠܒܤܬܪܗ ܘܚܙܬ ܠܝܫܘܥ ܕܩܐܡ ܘܠܐ ܝܕܥܐ ܗܘܬ ܕܝܫܘܥ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 15, 'ܐܡܪ ܠܗ ܝܫܘܥ ܐܢܬܬܐ ܡܢܐ ܒܟܝܐ ܐܢܬܝ ܘܠܡܢ ܒܥܝܐ ܐܢܬܝ ܗܝ ܕܝܢ ܤܒܪܬ ܕܓܢܢܐ ܗܘ ܘܐܡܪܐ ܠܗ ܡܪܝ ܐܢ ܐܢܬ ܫܩܠܬܝܗܝ ܐܡܪ ܠܝ ܐܝܟܐ ܤܡܬܝܗܝ ܐܙܠ ܐܫܩܠܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 16, 'ܐܡܪ ܠܗ ܝܫܘܥ ܡܪܝܡ ܘܐܬܦܢܝܬ ܘܐܡܪܐ ܠܗ ܥܒܪܐܝܬ ܪܒܘܠܝ ܕܡܬܐܡܪ ܡܠܦܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 17, 'ܐܡܪ ܠܗ ܝܫܘܥ ܠܐ ܬܬܩܪܒܝܢ ܠܝ ܠܐ ܓܝܪ ܥܕܟܝܠ ܤܠܩܬ ܠܘܬ ܐܒܝ ܙܠܝ ܕܝܢ ܠܘܬ ܐܚܝ ܘܐܡܪܝ ܠܗܘܢ ܤܠܩ ܐܢܐ ܠܘܬ ܐܒܝ ܘܐܒܘܟܘܢ ܘܐܠܗܝ ܘܐܠܗܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 18, 'ܗܝܕܝܢ ܐܬܬ ܡܪܝܡ ܡܓܕܠܝܬܐ ܘܤܒܪܬ ܠܬܠܡܝܕܐ ܕܚܙܬ ܠܡܪܢ ܘܕܗܠܝܢ ܐܡܪ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 19, 'ܟܕ ܗܘܐ ܕܝܢ ܪܡܫܐ ܕܝܘܡܐ ܗܘ ܕܚܕ ܒܫܒܐ ܘܬܪܥܐ ܐܚܝܕܝܢ ܗܘܘ ܕܐܝܟܐ ܕܐܝܬܝܗܘܢ ܗܘܘ ܬܠܡܝܕܐ ܡܛܠ ܕܚܠܬܐ ܕܝܗܘܕܝܐ ܐܬܐ ܝܫܘܥ ܩܡ ܒܝܢܬܗܘܢ ܘܐܡܪ ܠܗܘܢ ܫܠܡܐ ܥܡܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 20, 'ܗܕܐ ܐܡܪ ܘܚܘܝ ܐܢܘܢ ܐܝܕܘܗܝ ܘܤܛܪܗ ܘܚܕܝܘ ܬܠܡܝܕܐ ܕܚܙܘ ܠܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 21, 'ܐܡܪ ܠܗܘܢ ܕܝܢ ܬܘܒ ܝܫܘܥ ܫܠܡܐ ܥܡܟܘܢ ܐܝܟܢܐ ܕܫܕܪܢܝ ܐܒܝ ܐܦ ܐܢܐ ܡܫܕܪ ܐܢܐ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 22, 'ܘܟܕ ܐܡܪ ܗܠܝܢ ܢܦܚ ܒܗܘܢ ܘܐܡܪ ܠܗܘܢ ܩܒܠܘ ܪܘܚܐ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 23, 'ܐܢ ܬܫܒܩܘܢ ܚܛܗܐ ܠܐܢܫ ܢܫܬܒܩܘܢ ܠܗ ܘܐܢ ܬܐܚܕܘܢ ܕܐܢܫ ܐܚܝܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 24, 'ܬܐܘܡܐ ܕܝܢ ܚܕ ܡܢ ܬܪܥܤܪܬܐ ܗܘ ܕܡܬܐܡܪ ܬܐܡܐ ܠܐ ܗܘܐ ܬܡܢ ܗܘܐ ܥܡܗܘܢ ܟܕ ܐܬܐ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 25, 'ܘܐܡܪܝܢ ܠܗ ܬܠܡܝܕܐ ܚܙܝܢ ܠܡܪܢ ܗܘ ܕܝܢ ܐܡܪ ܠܗܘܢ ܐܠܐ ܚܙܐ ܐܢܐ ܒܐܝܕܘܗܝ ܕܘܟܝܬܐ ܕܨܨܐ ܘܪܡܐ ܐܢܐ ܒܗܝܢ ܨܒܥܬܝ ܘܡܘܫܛ ܐܢܐ ܐܝܕܝ ܒܕܦܢܗ ܠܐ ܡܗܝܡܢ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 26, 'ܘܒܬܪ ܬܡܢܝܐ ܝܘܡܝܢ ܬܘܒ ܠܓܘ ܗܘܘ ܬܠܡܝܕܐ ܘܬܐܘܡܐ ܥܡܗܘܢ ܘܐܬܐ ܝܫܘܥ ܟܕ ܐܚܝܕܝܢ ܬܪܥܐ ܩܡ ܒܡܨܥܬܐ ܘܐܡܪ ܠܗܘܢ ܫܠܡܐ ܥܡܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 27, 'ܘܐܡܪ ܠܬܐܘܡܐ ܐܝܬܐ ܨܒܥܟ ܠܗܪܟܐ ܘܚܙܝ ܐܝܕܝ ܘܐܝܬܐ ܐܝܕܟ ܘܐܘܫܛ ܒܓܒܝ ܘܠܐ ܬܗܘܐ ܠܐ ܡܗܝܡܢܐ ܐܠܐ ܡܗܝܡܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 28, 'ܘܥܢܐ ܬܐܘܡܐ ܘܐܡܪ ܠܗ ܡܪܝ ܘܐܠܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 29, 'ܐܡܪ ܠܗ ܝܫܘܥ ܗܫܐ ܕܚܙܝܬܢܝ ܗܝܡܢܬ ܛܘܒܝܗܘܢ ܠܐܝܠܝܢ ܕܠܐ ܚܙܐܘܢܝ ܘܗܝܡܢܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 30, 'ܤܓܝܐܬܐ ܕܝܢ ܐܬܘܬܐ ܐܚܪܢܝܬܐ ܥܒܕ ܝܫܘܥ ܩܕܡ ܬܠܡܝܕܘܗܝ ܐܝܠܝܢ ܕܠܐ ܟܬܝܒܢ ܒܟܬܒܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 31, 'ܐܦ ܗܠܝܢ ܕܝܢ ܕܟܬܝܒܢ ܕܬܗܝܡܢܘܢ ܕܝܫܘܥ ܗܘ ܡܫܝܚܐ ܒܪܗ ܕܐܠܗܐ ܘܡܐ ܕܗܝܡܢܬܘܢ ܢܗܘܘܢ ܠܟܘܢ ܒܫܡܗ ܚܝܐ ܕܠܥܠܡ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 1, 'ܒܬܪ ܗܠܝܢ ܚܘܝ ܬܘܒ ܢܦܫܗ ܝܫܘܥ ܠܬܠܡܝܕܘܗܝ ܥܠ ܝܡܐ ܕܛܝܒܪܝܘܤ ܚܘܝ ܕܝܢ ܗܟܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 2, 'ܐܝܬܝܗܘܢ ܗܘܘ ܐܟܚܕܐ ܫܡܥܘܢ ܟܐܦܐ ܘܬܐܘܡܐ ܕܡܬܐܡܪ ܬܐܡܐ ܘܢܬܢܝܐܝܠ ܗܘ ܕܡܢ ܩܛܢܐ ܕܓܠܝܠܐ ܘܒܢܝ ܙܒܕܝ ܘܬܪܝܢ ܐܚܪܢܝܢ ܡܢ ܬܠܡܝܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 3, 'ܐܡܪ ܠܗܘܢ ܫܡܥܘܢ ܟܐܦܐ ܐܙܠ ܐܢܐ ܐܨܘܕ ܢܘܢܐ ܐܡܪܝܢ ܠܗ ܐܦ ܚܢܢ ܐܬܝܢܢ ܥܡܟ ܘܢܦܩܘ ܘܤܠܩܘ ܠܤܦܝܢܬܐ ܘܒܗܘ ܠܠܝܐ ܡܕܡ ܠܐ ܨܕܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 4, 'ܟܕ ܕܝܢ ܗܘܐ ܨܦܪܐ ܩܡ ܝܫܘܥ ܥܠ ܝܕ ܝܡܐ ܘܠܐ ܝܕܥܘ ܬܠܡܝܕܐ ܕܝܫܘܥ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 5, 'ܘܐܡܪ ܠܗܘܢ ܝܫܘܥ ܛܠܝܐ ܠܡܐ ܐܝܬ ܠܟܘܢ ܡܕܡ ܠܡܠܥܤ ܐܡܪܝܢ ܠܗ ܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 6, 'ܐܡܪ ܠܗܘܢ ܐܪܡܘ ܡܨܝܕܬܟܘܢ ܡܢ ܓܒܐ ܕܝܡܝܢܐ ܕܤܦܝܢܬܐ ܘܡܫܟܚܝܢ ܐܢܬܘܢ ܘܐܪܡܝܘ ܘܠܐ ܐܫܟܚܘ ܠܡܓܕܗ ܠܡܨܝܕܬܐ ܡܢ ܤܘܓܐܐ ܕܢܘܢܐ ܕܐܚܕܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 7, 'ܘܐܡܪ ܬܠܡܝܕܐ ܗܘ ܕܪܚܡ ܗܘܐ ܠܗ ܝܫܘܥ ܠܟܐܦܐ ܗܢܐ ܡܪܢ ܗܘ ܫܡܥܘܢ ܕܝܢ ܟܕ ܫܡܥ ܕܡܪܢ ܗܘ ܢܤܒ ܟܘܬܝܢܗ ܡܚܐ ܒܚܨܘܗܝ ܡܛܠ ܕܥܪܛܠܝܐ ܗܘܐ ܘܫܕܐ ܢܦܫܗ ܒܝܡܐ ܕܢܐܬܐ ܠܘܬ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 8, 'ܐܚܪܢܐ ܕܝܢ ܬܠܡܝܕܐ ܒܤܦܝܢܬܐ ܐܬܘ ܠܐ ܓܝܪ ܪܚܝܩܝܢ ܗܘܘ ܤܓܝ ܡܢ ܐܪܥܐ ܐܠܐ ܐܝܟ ܡܐܬܝܢ ܐܡܝܢ ܘܢܓܕܝܢ ܗܘܘ ܠܗ ܠܡܨܝܕܬܐ ܗܝ ܕܢܘܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 9, 'ܟܕ ܕܝܢ ܤܠܩܘ ܠܐܪܥܐ ܚܙܘ ܓܘܡܪܐ ܟܕ ܤܝܡܢ ܘܢܘܢܐ ܟܕ ܤܝܡ ܥܠܝܗܝܢ ܘܠܚܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 10, 'ܘܐܡܪ ܠܗܘܢ ܝܫܘܥ ܐܝܬܘ ܡܢ ܗܢܘܢ ܢܘܢܐ ܕܨܕܬܘܢ ܗܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 11, 'ܘܤܠܩ ܫܡܥܘܢ ܟܐܦܐ ܘܢܓܕܗ ܠܡܨܝܕܬܐ ܠܐܪܥܐ ܟܕ ܡܠܝܐ ܢܘܢܐ ܪܘܪܒܐ ܡܐܐ ܘܚܡܫܝܢ ܘܬܠܬܐ ܘܒܗܢܐ ܟܠܗ ܝܘܩܪܐ ܠܐ ܐܨܛܪܝܬ ܡܨܝܕܬܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 12, 'ܘܐܡܪ ܠܗܘܢ ܝܫܘܥ ܬܘ ܐܫܬܪܘ ܐܢܫ ܕܝܢ ܡܢ ܬܠܡܝܕܐ ܠܐ ܡܡܪܚ ܗܘܐ ܕܢܫܐܠܝܘܗܝ ܕܡܢܘ ܕܝܕܥܝܢ ܗܘܘ ܕܡܪܢ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 13, 'ܩܪܒ ܕܝܢ ܝܫܘܥ ܘܫܩܠ ܠܚܡܐ ܘܢܘܢܐ ܘܝܗܒ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 14, 'ܗܕܐ ܕܬܠܬ ܙܒܢܝܢ ܐܬܚܙܝ ܝܫܘܥ ܠܬܠܡܝܕܘܗܝ ܟܕ ܩܡ ܡܢ ܒܝܬ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 15, 'ܟܕ ܕܝܢ ܐܫܬܪܝܘ ܐܡܪ ܝܫܘܥ ܠܫܡܥܘܢ ܟܐܦܐ ܫܡܥܘܢ ܒܪ ܝܘܢܐ ܪܚܡ ܐܢܬ ܠܝ ܝܬܝܪ ܡܢ ܗܠܝܢ ܐܡܪ ܠܗ ܐܝܢ ܡܪܝ ܐܢܬ ܝܕܥ ܐܢܬ ܕܪܚܡ ܐܢܐ ܠܟ ܐܡܪ ܠܗ ܪܥܝ ܠܝ ܐܡܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 16, 'ܐܡܪ ܠܗ ܬܘܒ ܕܬܪܬܝܢ ܙܒܢܝܢ ܫܡܥܘܢ ܒܪ ܝܘܢܐ ܪܚܡ ܐܢܬ ܠܝ ܐܡܪ ܠܗ ܐܝܢ ܡܪܝ ܐܢܬ ܝܕܥ ܐܢܬ ܕܪܚܡ ܐܢܐ ܠܟ ܐܡܪ ܠܗ ܝܫܘܥ ܪܥܝ ܠܝ ܥܪܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 17, 'ܐܡܪ ܠܗ ܕܬܠܬ ܙܒܢܝܢ ܫܡܥܘܢ ܒܪ ܝܘܢܐ ܪܚܡ ܐܢܬ ܠܝ ܘܟܪܝܬ ܠܗ ܠܟܐܦܐ ܕܐܡܪ ܠܗ ܕܬܠܬ ܙܒܢܝܢ ܕܪܚܡ ܐܢܬ ܠܝ ܘܐܡܪ ܠܗ ܡܪܝ ܟܠ ܡܕܡ ܐܢܬ ܚܟܡ ܐܢܬ ܐܢܬ ܝܕܥ ܐܢܬ ܕܪܚܡ ܐܢܐ ܠܟ ܐܡܪ ܠܗ ܝܫܘܥ ܪܥܝ ܠܝ ܢܩܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 18, 'ܐܡܝܢ ܐܡܝܢ ܐܡܪ ܐܢܐ ܠܟ ܕܟܕ ܛܠܐ ܗܘܝܬ ܐܢܬ ܠܢܦܫܟ ܐܤܪ ܗܘܝܬ ܚܨܝܟ ܘܡܗܠܟ ܗܘܝܬ ܠܐܝܟܐ ܕܨܒܐ ܐܢܬ ܡܐ ܕܝܢ ܕܤܐܒܬ ܬܦܫܘܛ ܐܝܕܝܟ ܘܐܚܪܝܢ ܢܐܤܘܪ ܠܟ ܚܨܝܟ ܘܢܘܒܠܟ ܠܐܝܟܐ ܕܠܐ ܨܒܐ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 19, 'ܗܕܐ ܕܝܢ ܐܡܪ ܕܢܚܘܐ ܒܐܝܢܐ ܡܘܬܐ ܥܬܝܕ ܕܢܫܒܚ ܠܐܠܗܐ ܘܟܕ ܐܡܪ ܗܠܝܢ ܐܡܪ ܠܗ ܬܐ ܒܬܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 20, 'ܘܐܬܦܢܝ ܫܡܥܘܢ ܟܐܦܐ ܘܚܙܐ ܠܬܠܡܝܕܐ ܗܘ ܕܪܚܡ ܗܘܐ ܝܫܘܥ ܕܐܬܐ ܒܬܪܗ ܗܘ ܕܢܦܠ ܗܘܐ ܒܚܫܡܝܬܐ ܥܠ ܚܕܝܗ ܕܝܫܘܥ ܘܐܡܪ ܡܪܝ ܡܢܘ ܡܫܠܡ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 21, 'ܠܗܢܐ ܟܕ ܚܙܐ ܟܐܦܐ ܐܡܪ ܠܝܫܘܥ ܡܪܝ ܘܗܢܐ ܡܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 22, 'ܐܡܪ ܠܗ ܝܫܘܥ ܐܢ ܨܒܐ ܐܢܐ ܕܢܩܘܐ ܗܢܐ ܥܕܡܐ ܕܐܬܐ ܐܢܐ ܠܟ ܡܐ ܠܟ ܐܢܬ ܬܐ ܒܬܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 23, 'ܘܢܦܩܬ ܗܕܐ ܡܠܬܐ ܒܝܬ ܐܚܐ ܕܗܘ ܬܠܡܝܕܐ ܠܐ ܡܐܬ ܝܫܘܥ ܕܝܢ ܠܐ ܗܘܐ ܕܠܐ ܡܐܬ ܐܡܪ ܐܠܐ ܕܐܢ ܨܒܐ ܐܢܐ ܕܢܩܘܐ ܗܢܐ ܥܕܡܐ ܕܐܬܐ ܐܢܐ ܠܟ ܡܐ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 24, 'ܗܢܘ ܬܠܡܝܕܐ ܕܐܤܗܕ ܥܠ ܗܠܝܢ ܟܠܗܝܢ ܘܐܦ ܟܬܒ ܐܢܝܢ ܘܝܕܥܝܢ ܚܢܢ ܕܫܪܝܪܐ ܗܝ ܤܗܕܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 25, 'ܐܝܬ ܕܝܢ ܐܦ ܐܚܪܢܝܬܐ ܤܓܝܐܬܐ ܕܥܒܕ ܝܫܘܥ ܐܝܠܝܢ ܕܐܠܘ ܚܕܐ ܚܕܐ ܡܬܟܬܒܢ ܗܘܝ ܐܦ ܠܐ ܗܘ ܥܠܡܐ ܐܝܟ ܕܤܒܪ ܐܢܐ ܤܦܩ ܗܘܐ ܠܟܬܒܐ ܕܡܬܟܬܒܝܢ ܗܘܘ ܀ ܀ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 1, 'ܟܬܒܐ ܩܕܡܝܐ ܟܬܒܬ ܐܘ ܬܐܘܦܝܠܐ ܥܠ ܟܠܗܝܢ ܐܝܠܝܢ ܕܫܪܝ ܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܠܡܥܒܕ ܘܠܡܠܦܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 2, 'ܥܕܡܐ ܠܝܘܡܐ ܗܘ ܕܒܗ ܐܤܬܠܩ ܡܢ ܒܬܪ ܕܦܩܕ ܗܘܐ ܐܢܘܢ ܠܫܠܝܚܐ ܐܝܠܝܢ ܕܓܒܐ ܒܪܘܚܐ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 3, 'ܗܢܘܢ ܕܐܦ ܚܘܝ ܠܗܘܢ ܢܦܫܗ ܟܕ ܚܝ ܡܢ ܒܬܪ ܕܚܫ ܒܐܬܘܬܐ ܤܓܝܐܬܐ ܒܝܘܡܝܢ ܐܪܒܥܝܢ ܟܕ ܡܬܚܙܐ ܗܘܐ ܠܗܘܢ ܘܐܡܪ ܥܠ ܡܠܟܘܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 4, 'ܘܟܕ ܐܟܠ ܥܡܗܘܢ ܠܚܡܐ ܦܩܕ ܐܢܘܢ ܕܡܢ ܐܘܪܫܠܡ ܠܐ ܢܦܪܩܘܢ ܐܠܐ ܕܢܩܘܘܢ ܠܫܘܘܕܝܗ ܕܐܒܐ ܗܘ ܕܫܡܥܬܘܢ ܡܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 5, 'ܕܝܘܚܢܢ ܐܥܡܕ ܒܡܝܐ ܘܐܢܬܘܢ ܬܥܡܕܘܢ ܒܪܘܚܐ ܕܩܘܕܫܐ ܠܐ ܒܬܪ ܝܘܡܬܐ ܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 6, 'ܗܢܘܢ ܕܝܢ ܟܕ ܟܢܝܫܝܢ ܫܐܠܘܗܝ ܘܐܡܪܝܢ ܠܗ ܡܪܢ ܐܢ ܒܗܢܐ ܙܒܢܐ ܡܦܢܐ ܐܢܬ ܡܠܟܘܬܐ ܠܐܝܤܪܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 7, 'ܐܡܪ ܠܗܘܢ ܗܘ ܠܐ ܗܘܬ ܕܝܠܟܘܢ ܗܕܐ ܠܡܕܥ ܙܒܢܐ ܐܘ ܙܒܢܐ ܐܝܠܝܢ ܕܐܒܐ ܤܡ ܐܢܘܢ ܒܫܘܠܛܢܐ ܕܢܦܫܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 8, 'ܐܠܐ ܟܕ ܬܐܬܐ ܪܘܚܐ ܕܩܘܕܫܐ ܥܠܝܟܘܢ ܬܩܒܠܘܢ ܚܝܠܐ ܘܬܗܘܘܢ ܠܝ ܤܗܕܐ ܒܐܘܪܫܠܡ ܘܒܟܠܗ ܝܗܘܕ ܘܐܦ ܒܝܬ ܫܡܪܝܐ ܘܥܕܡܐ ܠܤܘܦܝܗ ܕܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 9, 'ܘܟܕ ܗܠܝܢ ܐܡܪ ܟܕ ܚܙܝܢ ܠܗ ܐܤܬܠܩ ܘܥܢܢܐ ܩܒܠܬܗ ܘܐܬܟܤܝ ܡܢ ܥܝܢܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 10, 'ܘܟܕ ܚܝܪܝܢ ܗܘܘ ܒܫܡܝܐ ܟܕ ܗܘ ܐܙܠ ܗܘܐ ܐܫܬܟܚܘ ܬܪܝܢ ܓܒܪܝܢ ܩܝܡܝܢ ܠܘܬܗܘܢ ܒܠܒܘܫܐ ܚܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 11, 'ܘܐܡܪܝܢ ܠܗܘܢ ܓܒܪܐ ܓܠܝܠܝܐ ܡܢܐ ܩܝܡܝܢ ܐܢܬܘܢ ܘܚܝܪܝܢ ܒܫܡܝܐ ܗܢܐ ܝܫܘܥ ܕܐܤܬܠܩ ܡܢܟܘܢ ܠܫܡܝܐ ܗܟܢܐ ܢܐܬܐ ܐܝܟ ܡܐ ܕܚܙܝܬܘܢܝܗܝ ܕܤܠܩ ܠܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 12, 'ܘܡܢ ܒܬܪܟܢ ܗܦܟܘ ܠܗܘܢ ܠܐܘܪܫܠܡ ܡܢ ܛܘܪܐ ܕܡܬܩܪܐ ܕܒܝܬ ܙܝܬܐ ܕܐܝܬܘܗܝ ܥܠ ܓܢܒ ܐܘܪܫܠܡ ܘܦܪܝܩ ܡܢܗ ܐܝܟ ܫܒܥܐ ܐܤܛܕܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 13, 'ܘܡܢ ܒܬܪ ܕܥܠܘ ܤܠܩܘ ܠܗܘܢ ܠܥܠܝܬܐ ܗܝ ܕܗܘܝܢ ܗܘܘ ܒܗ ܦܛܪܘܤ ܘܝܘܚܢܢ ܘܝܥܩܘܒ ܘܐܢܕܪܐܘܤ ܘܦܝܠܝܦܘܤ ܘܬܐܘܡܐ ܘܡܬܝ ܘܒܪ ܬܘܠܡܝ ܘܝܥܩܘܒ ܒܪ ܚܠܦܝ ܘܫܡܥܘܢ ܛܢܢܐ ܘܝܗܘܕܐ ܒܪ ܝܥܩܘܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 14, 'ܗܠܝܢ ܟܠܗܘܢ ܐܟܚܕܐ ܐܡܝܢܝܢ ܗܘܘ ܒܨܠܘܬܐ ܒܚܕܐ ܢܦܫ ܥܡ ܢܫܐ ܘܥܡ ܡܪܝܡ ܐܡܗ ܕܝܫܘܥ ܘܥܡ ܐܚܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 15, 'ܘܒܗܘܢ ܒܝܘܡܬܐ ܗܢܘܢ ܩܡ ܫܡܥܘܢ ܟܐܦܐ ܡܨܥܬ ܬܠܡܝܕܐ ܐܝܬ ܗܘܐ ܕܝܢ ܬܡܢ ܟܢܫܐ ܕܐܢܫܐ ܐܝܟ ܡܐܐ ܘܥܤܪܝܢ ܘܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 16, 'ܓܒܪܐ ܐܚܝܢ ܙܕܩ ܗܘܐ ܕܢܬܡܠܐ ܟܬܒܐ ܐܝܢܐ ܕܩܕܡ ܐܡܪ ܪܘܚܐ ܕܩܘܕܫܐ ܒܦܘܡܗ ܕܕܘܝܕ ܥܠ ܝܗܘܕܐ ܗܘ ܕܗܘܐ ܡܕܒܪܢܐ ܠܗܢܘܢ ܕܐܚܕܘ ܠܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 17, 'ܡܛܠ ܕܡܢܐ ܗܘܐ ܥܡܢ ܘܐܝܬ ܗܘܐ ܠܗ ܦܤܐ ܒܬܫܡܫܬܐ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 18, 'ܗܢܘ ܕܩܢܐ ܠܗ ܩܪܝܬܐ ܡܢ ܐܓܪܐ ܕܚܛܝܬܐ ܘܢܦܠ ܥܠ ܐܦܘܗܝ ܥܠ ܐܪܥܐ ܘܐܬܦܪܬ ܡܢ ܡܨܥܬܗ ܘܐܬܐܫܕ ܟܠܗ ܓܘܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 19, 'ܘܗܝ ܗܕܐ ܐܬܝܕܥܬ ܠܟܠܗܘܢ ܕܥܡܪܝܢ ܒܐܘܪܫܠܡ ܘܗܟܢܐ ܐܬܩܪܝܬ ܩܪܝܬܐ ܗܝ ܒܠܫܢܗ ܕܐܬܪܐ ܚܩܠ ܕܡܐ ܕܐܝܬܘܗܝ ܬܘܪܓܡܗ ܩܘܪܝܬ ܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 20, 'ܟܬܝܒ ܓܝܪ ܒܤܦܪܐ ܕܡܙܡܘܪܐ ܕܕܝܪܗ ܬܗܘܐ ܚܪܒܐ ܘܥܡܘܪ ܠܐ ܢܗܘܐ ܒܗ ܘܬܫܡܫܬܗ ܢܤܒ ܐܚܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 21, 'ܘܠܝܐ ܗܝ ܗܟܝܠ ܠܚܕ ܡܢ ܗܠܝܢ ܓܒܪܐ ܕܗܘܘ ܥܡܢ ܒܗܢܐ ܙܒܢܐ ܟܠܗ ܕܒܗ ܥܠ ܘܢܦܩ ܥܠܝܢ ܡܪܢ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 22, 'ܕܐܩܦ ܡܢ ܡܥܡܘܕܝܬܗ ܕܝܘܚܢܢ ܥܕܡܐ ܠܝܘܡܐ ܕܐܤܬܠܩ ܡܢ ܠܘܬܢ ܕܗܘ ܢܗܘܐ ܥܡܢ ܤܗܕܐ ܕܩܝܡܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 23, 'ܘܐܩܝܡܘ ܬܪܝܢ ܠܝܘܤܦ ܕܡܬܩܪܐ ܒܪܫܒܐ ܕܐܫܬܡܝ ܝܘܤܛܘܤ ܘܠܡܬܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 24, 'ܘܟܕ ܨܠܝܘ ܐܡܪܘ ܐܢܬ ܡܪܝܐ ܝܕܥ ܕܒܠܒܘܬܐ ܕܟܠ ܚܘܐ ܚܕ ܐܝܢܐ ܕܓܒܐ ܐܢܬ ܡܢ ܗܠܝܢ ܬܪܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 25, 'ܕܗܘ ܢܩܒܠ ܦܤܐ ܕܬܫܡܫܬܐ ܘܫܠܝܚܘܬܐ ܕܡܢܗ ܦܪܩ ܝܗܘܕܐ ܕܢܐܙܠ ܠܗ ܠܐܬܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 26, 'ܘܐܪܡܝܘ ܦܨܐ ܘܤܠܩܬ ܠܡܬܝܐ ܘܐܬܡܢܝ ܥܡ ܚܕܥܤܪ ܫܠܝܚܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 1, 'ܘܟܕ ܐܬܡܠܝܘ ܝܘܡܬܐ ܕܦܢܛܩܘܤܛܐ ܟܕ ܟܢܝܫܝܢ ܗܘܘ ܟܠܗܘܢ ܐܟܚܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 2, 'ܗܘܐ ܡܢ ܫܠܝܐ ܡܢ ܫܡܝܐ ܩܠܐ ܐܝܟ ܪܘܚܐ ܥܙܝܙܬܐ ܘܐܬܡܠܝ ܗܘܐ ܡܢܗ ܟܠܗ ܒܝܬܐ ܗܘ ܕܒܗ ܝܬܒܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 3, 'ܘܐܬܚܙܝܘ ܠܗܘܢ ܠܫܢܐ ܕܡܬܦܠܓܝܢ ܗܘܘ ܐܝܟ ܢܘܪܐ ܘܝܬܒܘ ܥܠ ܚܕ ܚܕ ܡܢܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 4, 'ܘܐܬܡܠܝܘ ܟܠܗܘܢ ܒܪܘܚܐ ܕܩܘܕܫܐ ܘܐܩܦܘ ܗܘܘ ܠܡܡܠܠܘ ܒܠܫܢ ܠܫܢ ܐܝܟ ܡܐ ܕܪܘܚܐ ܝܗܒ ܗܘܐ ܠܗܘܢ ܠܡܡܠܠܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 5, 'ܐܝܬ ܗܘܘ ܕܝܢ ܓܒܪܐ ܕܥܡܪܝܢ ܒܐܘܪܫܠܡ ܕܕܚܠܝܢ ܡܢ ܐܠܗܐ ܝܗܘܕܝܐ ܡܢ ܟܠ ܥܡܡܐ ܕܬܚܝܬ ܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 6, 'ܘܟܕ ܗܘܐ ܩܠܐ ܗܘ ܟܢܫ ܟܠܗ ܥܡܐ ܘܐܫܬܓܫ ܡܛܠ ܕܫܡܥ ܗܘܐ ܐܢܫ ܐܢܫ ܡܢܗܘܢ ܕܡܡܠܠܝܢ ܗܘܘ ܒܠܫܢܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 7, 'ܬܗܝܪܝܢ ܗܘܘ ܕܝܢ ܟܠܗܘܢ ܘܡܬܕܡܪܝܢ ܟܕ ܐܡܪܝܢ ܗܘܘ ܚܕ ܠܚܕ ܗܠܝܢ ܟܠܗܘܢ ܕܡܡܠܠܝܢ ܠܐ ܗܐ ܓܠܝܠܝܐ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 8, 'ܐܝܟܢܐ ܚܢܢ ܫܡܥܝܢ ܚܢܢ ܐܢܫ ܐܢܫ ܠܫܢܗ ܕܒܗ ܝܠܝܕܝܢ ܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 9, 'ܦܪܬܘܝܐ ܘܡܕܝܐ ܘܐܠܢܝܐ ܘܐܝܠܝܢ ܕܥܡܪܝܢ ܒܝܬ ܢܗܪܝܢ ܝܗܘܕܝܐ ܘܩܦܘܕܩܝܐ ܘܕܡܢ ܐܬܪܐ ܕܦܢܛܘܤ ܘܕܐܤܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 10, 'ܘܕܡܢ ܐܬܪܐ ܕܦܪܘܓܝܐ ܘܕܦܡܦܘܠܝܐ ܘܕܡܨܪܝܢ ܘܕܐܬܪܘܬܐ ܕܠܘܒܐ ܕܩܪܝܒܝܢ ܠܩܘܪܝܢܐ ܘܐܝܠܝܢ ܕܐܬܘ ܡܢ ܪܗܘܡܐ ܝܗܘܕܝܐ ܘܓܝܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 11, 'ܘܕܡܢ ܩܪܛܐ ܘܥܪܒܝܐ ܗܐ ܫܡܥܝܢ ܚܢܢ ܡܢܗܘܢ ܕܡܡܠܠܝܢ ܒܠܫܢܝܢ ܕܝܠܢ ܬܕܡܪܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 12, 'ܡܬܕܡܪܝܢ ܗܘܘ ܕܝܢ ܟܠܗܘܢ ܘܬܘܝܪܝܢ ܟܕ ܐܡܪܝܢ ܚܕ ܠܚܕ ܕܡܢܐ ܗܝ ܗܕܐ ܨܒܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 13, 'ܐܚܪܢܐ ܕܝܢ ܡܡܝܩܝܢ ܗܘܘ ܒܗܘܢ ܟܕ ܐܡܪܝܢ ܗܠܝܢ ܡܐܪܝܬܐ ܐܫܬܝܘ ܘܪܘܝܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 14, 'ܒܬܪܟܢ ܩܡ ܫܡܥܘܢ ܟܐܦܐ ܥܡ ܚܕܥܤܪ ܫܠܝܚܝܢ ܘܐܪܝܡ ܩܠܗ ܘܐܡܪ ܠܗܘܢ ܓܒܪܐ ܝܗܘܕܝܐ ܘܟܠܗܘܢ ܕܥܡܪܝܢ ܒܐܘܪܫܠܡ ܗܕܐ ܬܬܝܕܥ ܠܟܘܢ ܘܨܘܬܘ ܡܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 15, 'ܠܐ ܓܝܪ ܐܝܟܢܐ ܕܐܢܬܘܢ ܤܒܪܝܢ ܗܠܝܢ ܪܘܝܢ ܕܗܐ ܥܕܡܐ ܠܗܫܐ ܬܠܬ ܐܢܝܢ ܫܥܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 16, 'ܐܠܐ ܗܕܐ ܗܝ ܕܐܡܝܪܐ ܒܝܘܐܝܠ ܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 17, 'ܢܗܘܐ ܒܝܘܡܬܐ ܐܚܪܝܐ ܐܡܪ ܐܠܗܐ ܐܫܘܕ ܪܘܚܝ ܥܠ ܟܠ ܒܤܪ ܘܢܬܢܒܘܢ ܒܢܝܟܘܢ ܘܒܢܬܟܘܢ ܘܓܕܘܕܝܟܘܢ ܚܙܘܢܐ ܢܚܙܘܢ ܘܩܫܝܫܝܟܘܢ ܚܠܡܐ ܢܚܠܡܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 18, 'ܘܥܠ ܥܒܕܝ ܘܥܠ ܐܡܗܬܝ ܐܫܘܕ ܪܘܚܝ ܒܝܘܡܬܐ ܗܢܘܢ ܘܢܬܢܒܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 19, 'ܘܐܬܠ ܐܬܘܬܐ ܒܫܡܝܐ ܘܓܒܪܘܬܐ ܥܠ ܐܪܥܐ ܕܡܐ ܘܢܘܪܐ ܘܥܛܪܐ ܕܬܢܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 20, 'ܫܡܫܐ ܢܬܚܠܦ ܒܥܡܛܢܐ ܘܤܗܪܐ ܒܕܡܐ ܥܕܠܐ ܢܐܬܐ ܝܘܡܗ ܕܡܪܝܐ ܪܒܐ ܘܕܚܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 21, 'ܘܢܗܘܐ ܟܠ ܕܢܩܪܐ ܫܡܗ ܕܡܪܝܐ ܢܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 22, 'ܓܒܪܐ ܒܢܝ ܐܝܤܪܝܠ ܫܡܥܘ ܡܠܐ ܗܠܝܢ ܝܫܘܥ ܢܨܪܝܐ ܓܒܪܐ ܕܡܢ ܐܠܗܐ ܐܬܚܙܝ ܠܘܬܟܘܢ ܒܚܝܠܐ ܘܒܐܬܘܬܐ ܘܒܓܒܪܘܬܐ ܐܝܠܝܢ ܕܐܠܗܐ ܥܒܕ ܒܝܢܬܟܘܢ ܒܐܝܕܗ ܐܝܟ ܕܐܢܬܘܢ ܝܕܥܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 23, 'ܠܗܢܐ ܕܦܪܝܫ ܗܘܐ ܠܗ ܠܗܕܐ ܒܡܩܕܡܘܬ ܝܕܥܬܗ ܘܒܨܒܝܢܗ ܕܐܠܗܐ ܐܫܠܡܬܘܢܝܗܝ ܒܐܝܕܝ ܪܫܝܥܐ ܘܙܩܦܬܘܢ ܘܩܛܠܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 24, 'ܐܠܗܐ ܕܝܢ ܐܩܝܡܗ ܘܫܪܐ ܚܒܠܝܗ ܕܫܝܘܠ ܡܛܠ ܕܠܐ ܡܫܟܚܐ ܗܘܬ ܕܢܬܬܚܕ ܒܗ ܒܫܝܘܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 25, 'ܕܘܝܕ ܓܝܪ ܐܡܪ ܥܠܘܗܝ ܡܩܕܡ ܗܘܝܬ ܚܙܐ ܠܡܪܝ ܒܟܠܙܒܢ ܕܥܠ ܝܡܝܢܝ ܗܘ ܕܠܐ ܐܙܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 26, 'ܡܛܠ ܗܢܐ ܐܬܒܤܡ ܠܒܝ ܘܪܘܙܬ ܬܫܒܘܚܬܝ ܘܐܦ ܦܓܪܝ ܢܓܢ ܥܠ ܤܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 27, 'ܡܛܠ ܕܠܐ ܫܒܩ ܐܢܬ ܠܢܦܫܝ ܒܫܝܘܠ ܘܠܐ ܝܗܒ ܐܢܬ ܠܚܤܝܟ ܕܢܚܙܐ ܚܒܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 28, 'ܓܠܝܬ ܠܝ ܐܘܪܚܐ ܕܚܝܐ ܬܡܠܝܢܝ ܒܤܝܡܘܬܐ ܥܡ ܦܪܨܘܦܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 29, 'ܓܒܪܐ ܐܚܝܢ ܡܦܤ ܠܡܐܡܪ ܥܝܢ ܒܓܠܐ ܠܘܬܟܘܢ ܥܠ ܪܝܫ ܐܒܗܬܐ ܕܘܝܕ ܕܡܝܬ ܘܐܦ ܐܬܩܒܪ ܘܒܝܬ ܩܒܘܪܗ ܐܝܬܘܗܝ ܠܘܬܢ ܥܕܡܐ ܠܝܘܡܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 30, 'ܢܒܝܐ ܗܘܐ ܓܝܪ ܘܝܕܥ ܗܘܐ ܕܡܘܡܬܐ ܝܡܐ ܠܗ ܐܠܗܐ ܕܡܢ ܦܐܪܐ ܕܟܪܤܟ ܐܘܬܒ ܥܠ ܟܘܪܤܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 31, 'ܘܩܕܡ ܚܙܐ ܘܡܠܠ ܥܠ ܩܝܡܬܗ ܕܡܫܝܚܐ ܕܠܐ ܐܫܬܒܩ ܒܫܝܘܠ ܐܦܠܐ ܦܓܪܗ ܚܙܐ ܚܒܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 32, 'ܠܗܢܐ ܝܫܘܥ ܐܩܝܡ ܐܠܗܐ ܘܚܢܢ ܟܠܢ ܤܗܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 33, 'ܘܗܘܝܘ ܕܒܝܡܝܢܗ ܕܐܠܗܐ ܐܬܬܪܝܡ ܘܢܤܒ ܡܢ ܐܒܐ ܫܘܘܕܝܐ ܕܥܠ ܪܘܚܐ ܕܩܘܕܫܐ ܘܐܫܕ ܡܘܗܒܬܐ ܗܕܐ ܕܗܐ ܚܙܝܢ ܐܢܬܘܢ ܘܫܡܥܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 34, 'ܠܐ ܗܘܐ ܓܝܪ ܕܘܝܕ ܤܠܩ ܠܫܡܝܐ ܡܛܠ ܕܗܘ ܐܡܪ ܕܐܡܪ ܡܪܝܐ ܠܡܪܝ ܬܒ ܠܟ ܡܢ ܝܡܝܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 35, 'ܥܕܡܐ ܕܐܤܝܡ ܒܥܠܕܒܒܝܟ ܟܘܒܫܐ ܠܪܓܠܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 36, 'ܫܪܝܪܐܝܬ ܗܟܝܠ ܢܕܥ ܟܠܗ ܒܝܬ ܐܝܤܪܝܠ ܕܡܪܝܐ ܘܡܫܝܚܐ ܥܒܕܗ ܐܠܗܐ ܠܗܢܐ ܝܫܘܥ ܕܐܢܬܘܢ ܙܩܦܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 37, 'ܘܟܕ ܫܡܥܘ ܗܠܝܢ ܐܬܓܢܚܘ ܒܠܒܗܘܢ ܘܐܡܪܘ ܠܫܡܥܘܢ ܘܠܫܪܟܐ ܕܫܠܝܚܐ ܡܢܐ ܢܥܒܕ ܐܚܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 38, 'ܐܡܪ ܠܗܘܢ ܫܡܥܘܢ ܬܘܒܘ ܘܥܡܕܘ ܐܢܫ ܐܢܫ ܡܢܟܘܢ ܒܫܡܗ ܕܡܪܝܐ ܝܫܘܥ ܠܫܘܒܩܢ ܚܛܗܐ ܕܬܩܒܠܘܢ ܡܘܗܒܬܐ ܕܪܘܚܐ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 39, 'ܠܟܘܢ ܓܝܪ ܗܘܐ ܫܘܘܕܝܐ ܘܠܒܢܝܟܘܢ ܘܠܟܠܗܘܢ ܐܝܠܝܢ ܕܪܚܝܩܝܢ ܐܝܠܝܢ ܕܗܘ ܐܠܗܐ ܢܩܪܐ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 40, 'ܘܒܡܠܐ ܐܚܪܢܝܬܐ ܤܓܝܐܬܐ ܡܤܗܕ ܗܘܐ ܠܗܘܢ ܘܒܥܐ ܗܘܐ ܡܢܗܘܢ ܟܕ ܐܡܪ ܚܝܘ ܡܢ ܫܪܒܬܐ ܗܕܐ ܡܥܩܡܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 41, 'ܘܐܢܫܝܢ ܡܢܗܘܢ ܥܬܝܕܐܝܬ ܩܒܠܘ ܡܠܬܗ ܘܗܝܡܢܘ ܘܥܡܕܘ ܘܐܬܬܘܤܦܘ ܒܗܘ ܝܘܡܐ ܐܝܟ ܬܠܬܐ ܐܠܦܝܢ ܢܦܫܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 42, 'ܘܐܡܝܢܝܢ ܗܘܘ ܒܝܘܠܦܢܐ ܕܫܠܝܚܐ ܘܡܫܬܘܬܦܝܢ ܗܘܘ ܒܨܠܘܬܐ ܘܒܩܨܝܐ ܕܐܘܟܪܤܛܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 43, 'ܘܗܘܝܐ ܗܘܬ ܕܚܠܬܐ ܠܟܠ ܢܦܫ ܘܐܬܘܬܐ ܤܓܝܐܬܐ ܘܓܒܪܘܬܐ ܗܘܝܢ ܗܘܝ ܒܝܕ ܫܠܝܚܐ ܒܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 44, 'ܘܟܠܗܘܢ ܐܝܠܝܢ ܕܗܝܡܢܘ ܗܘܘ ܐܟܚܕܐ ܗܘܘ ܘܟܠܡܕܡ ܕܐܝܬ ܗܘܐ ܠܗܘܢ ܕܓܘܐ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 45, 'ܘܐܝܠܝܢ ܕܐܝܬ ܗܘܐ ܠܗܘܢ ܩܢܝܢܐ ܡܙܒܢܝܢ ܗܘܘ ܠܗ ܘܡܦܠܓܝܢ ܗܘܘ ܠܐܢܫ ܐܢܫ ܐܝܟ ܡܕܡ ܕܤܢܝܩ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 46, 'ܘܟܠܝܘܡ ܐܡܝܢܝܢ ܗܘܘ ܒܗܝܟܠܐ ܒܚܕܐ ܢܦܫ ܘܒܒܝܬܐ ܩܨܝܢ ܗܘܘ ܦܪܝܤܬܐ ܘܡܩܒܠܝܢ ܗܘܘ ܤܝܒܪܬܐ ܟܕ ܪܘܙܝܢ ܘܒܒܪܝܪܘܬܐ ܕܠܒܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 47, 'ܡܫܒܚܝܢ ܗܘܘ ܠܐܠܗܐ ܟܕ ܝܗܝܒܝܢ ܒܪܚܡܐ ܩܕܡ ܟܠܗ ܥܡܐ ܘܡܪܢ ܡܘܤܦ ܗܘܐ ܟܠܝܘܡ ܠܐܝܠܝܢ ܕܚܐܝܢ ܒܥܕܬܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 1, 'ܘܗܘܐ ܕܟܕ ܤܠܩܝܢ ܫܡܥܘܢ ܟܐܦܐ ܘܝܘܚܢܢ ܐܟܚܕܐ ܠܗܝܟܠܐ ܒܥܕܢܐ ܕܨܠܘܬܐ ܕܬܫܥ ܫܥܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 2, 'ܘܗܐ ܓܒܪܐ ܚܕ ܚܓܝܪܐ ܕܡܢ ܟܪܤ ܐܡܗ ܫܩܝܠܝܢ ܗܘܘ ܐܢܫܐ ܐܝܠܝܢ ܕܡܥܕܝܢ ܗܘܘ ܡܝܬܝܢ ܘܤܝܡܝܢ ܠܗ ܒܬܪܥܐ ܕܗܝܟܠܐ ܕܡܬܩܪܐ ܫܦܝܪܐ ܕܢܗܘܐ ܫܐܠ ܙܕܩܬܐ ܡܢ ܗܢܘܢ ܕܥܐܠܝܢ ܠܗܝܟܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 3, 'ܗܢܐ ܟܕ ܚܙܐ ܠܫܡܥܘܢ ܘܠܝܘܚܢܢ ܕܥܐܠܝܢ ܠܗܝܟܠܐ ܒܥܐ ܗܘܐ ܡܢܗܘܢ ܕܢܬܠܘܢ ܠܗ ܙܕܩܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 4, 'ܘܚܪܘ ܒܗ ܫܡܥܘܢ ܘܝܘܚܢܢ ܘܐܡܪܘ ܠܗ ܚܘܪ ܒܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 5, 'ܗܘ ܕܝܢ ܚܪ ܒܗܘܢ ܟܕ ܤܒܪ ܗܘܐ ܠܡܤܒ ܡܢܗܘܢ ܡܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 6, 'ܐܡܪ ܠܗ ܫܡܥܘܢ ܕܗܒܐ ܘܤܐܡܐ ܠܝܬ ܠܝ ܐܠܐ ܡܕܡ ܕܐܝܬ ܠܝ ܝܗܒ ܐܢܐ ܠܟ ܒܫܡܗ ܕܝܫܘܥ ܡܫܝܚܐ ܢܨܪܝܐ ܩܘܡ ܗܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 7, 'ܘܐܚܕܗ ܒܐܝܕܗ ܕܝܡܝܢܐ ܘܐܩܝܡܗ ܘܒܗ ܒܫܥܬܐ ܫܪ ܪܓܠܘܗܝ ܘܥܩܒܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 8, 'ܘܫܘܪ ܩܡ ܘܗܠܟ ܘܥܠ ܥܡܗܘܢ ܠܗܝܟܠܐ ܟܕ ܡܗܠܟ ܘܡܫܘܪ ܘܡܫܒܚ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 9, 'ܘܚܙܐܘܗܝ ܟܠܗ ܥܡܐ ܟܕ ܡܗܠܟ ܘܡܫܒܚ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 10, 'ܘܐܫܬܘܕܥܘ ܕܗܘܝܘ ܗܘ ܚܕܘܪܐ ܕܝܬܒ ܗܘܐ ܟܠܝܘܡ ܘܫܐܠ ܙܕܩܬܐ ܥܠ ܬܪܥܐ ܕܡܬܩܪܐ ܫܦܝܪܐ ܘܐܬܡܠܝܘ ܬܡܗܐ ܘܕܘܡܪܐ ܥܠ ܡܕܡ ܕܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 11, 'ܘܟܕ ܐܚܝܕ ܗܘܐ ܠܫܡܥܘܢ ܘܠܝܘܚܢܢ ܪܗܛ ܟܠܗ ܥܡܐ ܟܕ ܬܗܝܪ ܠܘܬܗܘܢ ܠܐܤܛܘܐ ܕܡܬܩܪܐ ܕܫܠܝܡܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 12, 'ܘܟܕ ܚܙܐ ܫܡܥܘܢ ܥܢܐ ܘܐܡܪ ܠܗܘܢ ܓܒܪܐ ܒܢܝ ܐܝܤܪܝܠ ܡܢܐ ܡܬܕܡܪܝܢ ܐܢܬܘܢ ܒܗܢܐ ܐܘ ܒܢ ܡܢܐ ܚܝܪܝܢ ܐܢܬܘܢ ܐܝܟ ܗܘ ܕܒܚܝܠܐ ܕܝܠܢ ܐܘ ܒܫܘܠܛܢܢ ܥܒܕܢ ܗܕܐ ܕܢܗܠܟ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 13, 'ܐܠܗܗ ܗܘ ܕܐܒܪܗܡ ܘܕܐܝܤܚܩ ܘܕܝܥܩܘܒ ܐܠܗܐ ܕܐܒܗܬܢ ܫܒܚ ܠܒܪܗ ܝܫܘܥ ܗܘ ܕܐܢܬܘܢ ܐܫܠܡܬܘܢ ܘܟܦܪܬܘܢ ܒܗ ܩܕܡ ܐܦܘܗܝ ܕܦܝܠܛܘܤ ܟܕ ܗܘ ܙܕܩ ܗܘܐ ܕܢܫܪܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 14, 'ܐܢܬܘܢ ܕܝܢ ܒܩܕܝܫܐ ܘܙܕܝܩܐ ܟܦܪܬܘܢ ܘܫܐܠܬܘܢ ܠܟܘܢ ܠܓܒܪܐ ܩܛܘܠܐ ܕܢܬܝܗܒ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 15, 'ܘܠܗܘ ܪܫܐ ܕܚܝܐ ܩܛܠܬܘܢ ܕܠܗ ܐܩܝܡ ܐܠܗܐ ܡܢ ܒܝܬ ܡܝܬܐ ܘܚܢܢ ܟܠܢ ܤܗܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 16, 'ܘܒܗܝܡܢܘܬܐ ܕܫܡܗ ܠܗܢܐ ܕܚܙܝܢ ܐܢܬܘܢ ܘܝܕܥܝܢ ܐܢܬܘܢ ܗܘ ܐܫܪ ܘܐܤܝ ܘܗܝܡܢܘܬܐ ܕܒܗ ܝܗܒܬ ܠܗ ܗܕܐ ܚܠܝܡܘܬܐ ܩܕܡ ܟܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 17, 'ܒܪܡ ܗܫܐ ܐܚܝ ܝܕܥ ܐܢܐ ܕܒܛܘܥܝܝ ܥܒܕܬܘܢ ܗܕܐ ܐܝܟ ܕܥܒܕܘ ܪܫܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 18, 'ܘܐܠܗܐ ܐܝܟ ܡܕܡ ܕܩܕܡ ܐܟܪܙ ܒܦܘܡ ܟܠܗܘܢ ܢܒܝܐ ܕܢܚܫ ܡܫܝܚܗ ܡܠܝ ܗܟܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 19, 'ܬܘܒܘ ܗܟܝܠ ܘܐܬܦܢܘ ܐܝܟܢܐ ܕܢܬܥܛܘܢ ܚܛܗܝܟܘܢ ܘܢܐܬܘܢ ܠܟܘܢ ܙܒܢܐ ܕܢܝܚܬܐ ܡܢ ܩܕܡ ܦܪܨܘܦܗ ܕܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 20, 'ܘܢܫܕܪ ܠܟܘܢ ܠܐܝܢܐ ܕܡܛܝܒ ܗܘܐ ܠܟܘܢ ܠܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 21, 'ܕܠܗ ܘܠܐ ܠܫܡܝܐ ܕܢܩܒܠܘܢ ܥܕܡܐ ܠܡܘܠܝܐ ܕܙܒܢܐ ܕܟܠܗܝܢ ܐܝܠܝܢ ܕܡܠܠ ܐܠܗܐ ܒܦܘܡܐ ܕܢܒܝܘܗܝ ܩܕܝܫܐ ܕܡܢ ܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 22, 'ܡܘܫܐ ܓܝܪ ܐܡܪ ܕܢܒܝܐ ܢܩܝܡ ܠܟܘܢ ܡܪܝܐ ܡܢ ܐܚܝܟܘܢ ܐܟܘܬܝ ܠܗ ܫܡܥܘ ܒܟܠ ܡܐ ܕܢܡܠܠ ܥܡܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 23, 'ܘܬܗܘܐ ܟܠ ܢܦܫܐ ܐܝܕܐ ܕܠܐ ܬܫܡܥ ܠܢܒܝܐ ܗܘ ܬܐܒܕ ܢܦܫܐ ܗܝ ܡܢ ܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 24, 'ܘܢܒܝܐ ܟܠܗܘܢ ܡܢ ܫܡܘܐܝܠ ܘܐܝܠܝܢ ܕܡܢ ܒܬܪܗ ܗܘܘ ܡܠܠܘ ܘܐܟܪܙܘ ܥܠ ܝܘܡܬܐ ܗܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 25, 'ܐܢܬܘܢ ܐܢܘܢ ܒܢܝܗܘܢ ܕܢܒܝܐ ܘܕܕܝܬܩܐ ܐܝܕܐ ܕܤܡ ܐܠܗܐ ܠܐܒܗܬܢ ܟܕ ܐܡܪ ܠܐܒܪܗܡ ܕܒܙܪܥܟ ܢܬܒܪܟܢ ܟܠܗܝܢ ܫܪܒܬܐ ܕܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 26, 'ܠܟܘܢ ܡܢ ܩܕܝܡ ܐܩܝܡ ܘܫܕܪ ܐܠܗܐ ܠܒܪܗ ܟܕ ܡܒܪܟ ܠܟܘܢ ܐܢ ܬܬܦܢܘܢ ܘܬܬܘܒܘܢ ܡܢ ܒܝܫܬܟܘܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 1, 'ܘܟܕ ܗܠܝܢ ܡܠܐ ܡܡܠܠܝܢ ܗܘܘ ܠܥܡܐ ܩܡܘ ܥܠܝܗܘܢ ܟܗܢܐ ܘܙܕܘܩܝܐ ܘܐܪܟܘܢܐ ܕܗܝܟܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 2, 'ܟܕ ܡܬܚܡܬܝܢ ܗܘܘ ܥܠܝܗܘܢ ܕܡܠܦܝܢ ܠܥܡܐ ܘܡܟܪܙܝܢ ܒܡܫܝܚܐ ܥܠ ܩܝܡܬܐ ܕܡܢ ܒܝܬ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 3, 'ܘܐܪܡܝܘ ܥܠܝܗܘܢ ܐܝܕܝܐ ܘܢܛܪܘ ܐܢܘܢ ܠܝܘܡܐ ܐܚܪܢܐ ܡܛܠ ܕܩܪܒ ܗܘܐ ܠܗ ܪܡܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 4, 'ܘܤܓܝܐܐ ܕܫܡܥܘ ܗܘܘ ܡܠܬܐ ܗܝܡܢܘ ܗܘܘ ܘܐܝܬܝܗܘܢ ܗܘܘ ܒܡܢܝܢܐ ܐܝܟ ܚܡܫܐ ܐܠܦܝܢ ܓܒܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 5, 'ܘܠܝܘܡܐ ܐܚܪܢܐ ܐܬܟܢܫܘ ܐܪܟܘܢܐ ܘܩܫܝܫܐ ܘܤܦܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 6, 'ܘܐܦ ܚܢܢ ܪܒ ܟܗܢܐ ܘܩܝܦܐ ܘܝܘܚܢܢ ܘܐܠܟܤܢܕܪܘܤ ܘܐܝܠܝܢ ܕܐܝܬܝܗܘܢ ܗܘܘ ܡܢ ܫܪܒܬܐ ܕܪܒܝ ܟܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 7, 'ܘܟܕ ܐܩܝܡܘ ܐܢܘܢ ܒܡܨܥܬܐ ܡܫܐܠܝܢ ܗܘܘ ܠܗܘܢ ܕܒܐܝܢܐ ܚܝܠ ܐܘ ܒܐܝܢܐ ܫܡ ܥܒܕܬܘܢ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 8, 'ܗܝܕܝܢ ܫܡܥܘܢ ܟܐܦܐ ܐܬܡܠܝ ܪܘܚܐ ܕܩܘܕܫܐ ܘܐܡܪ ܠܗܘܢ ܐܪܟܘܢܘܗܝ ܕܥܡܐ ܘܩܫܝܫܐ ܕܒܝܬ ܐܝܤܪܝܠ ܫܡܥܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 9, 'ܐܢ ܚܢܢ ܝܘܡܢܐ ܡܬܕܝܢܝܢ ܚܢܢ ܡܢܟܘܢ ܥܠ ܫܦܝܪܬܐ ܕܗܘܬ ܠܒܪܢܫܐ ܟܪܝܗܐ ܕܒܡܢܐ ܗܢܐ ܐܬܐܤܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 10, 'ܗܕܐ ܬܬܝܕܥ ܠܟܘܢ ܘܠܟܠܗ ܥܡܐ ܕܐܝܤܪܝܠ ܕܒܫܡܗ ܕܝܫܘܥ ܡܫܝܚܐ ܢܨܪܝܐ ܗܘ ܕܐܢܬܘܢ ܙܩܦܬܘܢܝܗܝ ܗܘ ܕܐܩܝܡ ܐܠܗܐ ܡܢ ܒܝܬ ܡܝܬܐ ܒܗ ܒܗܘ ܗܐ ܩܐܡ ܗܢܐ ܩܕܡܝܟܘܢ ܟܕ ܚܠܝܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 11, 'ܗܢܘ ܟܐܦܐ ܕܐܤܠܝܬܘܢ ܐܢܬܘܢ ܒܢܝܐ ܘܗܘ ܗܘܐ ܠܪܝܫ ܩܪܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 12, 'ܘܠܝܬ ܒܐܢܫ ܐܚܪܝܢ ܦܘܪܩܢܐ ܠܐ ܓܝܪ ܐܝܬ ܫܡܐ ܐܚܪܢܐ ܬܚܝܬ ܫܡܝܐ ܕܐܬܝܗܒ ܠܒܢܝܢܫܐ ܕܒܗ ܘܠܐ ܠܡܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 13, 'ܘܟܕ ܫܡܥܘ ܡܠܬܗ ܕܫܡܥܘܢ ܘܕܝܘܚܢܢ ܕܥܝܢ ܒܓܠܐ ܐܡܪܘܗ ܐܤܬܟܠܘ ܕܠܐ ܝܕܥܝܢ ܤܦܪܐ ܘܗܕܝܘܛܐ ܐܢܘܢ ܘܬܗܪܘ ܒܗܘܢ ܘܐܫܬܘܕܥܘ ܐܢܘܢ ܕܥܡ ܝܫܘܥ ܡܬܗܦܟܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 14, 'ܘܚܙܝܢ ܗܘܘ ܕܩܐܡ ܗܘܐ ܥܡܗܘܢ ܚܓܝܪܐ ܗܘ ܕܐܬܐܤܝ ܘܠܐ ܡܫܟܚܝܢ ܗܘܘ ܡܕܡ ܠܡܐܡܪ ܠܘܩܒܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 15, 'ܗܝܕܝܢ ܦܩܕܘ ܕܢܦܩܘܢ ܐܢܘܢ ܡܢ ܟܢܫܗܘܢ ܘܐܡܪܝܢ ܗܘܘ ܚܕ ܠܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 16, 'ܡܢܐ ܢܥܒܕ ܠܗܘܢ ܠܓܒܪܐ ܗܠܝܢ ܗܐ ܓܝܪ ܐܬܐ ܓܠܝܬܐ ܕܗܘܬ ܒܐܝܕܝܗܘܢ ܠܟܠܗܘܢ ܥܡܘܪܝܗ ܕܐܘܪܫܠܡ ܐܬܝܕܥܬ ܘܠܐ ܡܫܟܚܝܢܢ ܕܢܟܦܘܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 17, 'ܐܠܐ ܕܠܐ ܝܬܝܪܐܝܬ ܢܦܘܩ ܒܥܡܐ ܛܒܐ ܗܢܐ ܢܬܠܚܡ ܠܗܘܢ ܕܬܘܒ ܠܐ ܢܡܠܠܘܢ ܒܫܡܐ ܗܢܐ ܠܐܢܫ ܡܢ ܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 18, 'ܘܩܪܘ ܐܢܘܢ ܘܦܩܕܘ ܐܢܘܢ ܕܠܓܡܪ ܠܐ ܢܡܠܠܘܢ ܘܠܐ ܢܠܦܘܢ ܒܫܡ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 19, 'ܥܢܘ ܫܡܥܘܢ ܟܐܦܐ ܘܝܘܚܢܢ ܘܐܡܪܘ ܠܗܘܢ ܐܢ ܟܐܢܐ ܩܕܡ ܐܠܗܐ ܕܠܟܘܢ ܢܫܡܥ ܝܬܝܪ ܡܢ ܐܠܗܐ ܕܘܢܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 20, 'ܠܐ ܓܝܪ ܡܫܟܚܝܢ ܚܢܢ ܕܡܐ ܕܚܙܝܢ ܘܫܡܥܢ ܕܠܐ ܢܡܠܠܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 21, 'ܘܐܬܠܚܡܘ ܠܗܘܢ ܘܫܪܘ ܐܢܘܢ ܠܐ ܓܝܪ ܐܫܟܚܘ ܠܗܘܢ ܥܠܬܐ ܕܢܤܝܡܘܢ ܒܪܫܗܘܢ ܡܛܠ ܥܡܐ ܟܠܢܫ ܓܝܪ ܡܫܒܚ ܗܘܐ ܠܐܠܗܐ ܥܠ ܡܕܡ ܕܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 22, 'ܝܬܝܪ ܓܝܪ ܡܢ ܒܪ ܐܪܒܥܝܢ ܫܢܝܢ ܐܝܬܘܗܝ ܗܘܐ ܓܒܪܐ ܗܘ ܕܗܘܬ ܒܗ ܗܕܐ ܐܬܐ ܕܐܤܝܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 23, 'ܘܟܕ ܐܫܬܪܝܘ ܐܬܘ ܠܘܬ ܐܚܝܗܘܢ ܘܐܫܬܥܝܘ ܠܗܘܢ ܟܠ ܡܐ ܕܐܡܪܘ ܟܗܢܐ ܘܩܫܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 24, 'ܘܗܢܘܢ ܟܕ ܫܡܥܘ ܐܟܚܕ ܐܪܝܡܘ ܩܠܗܘܢ ܠܘܬ ܐܠܗܐ ܘܐܡܪܘ ܡܪܝܐ ܐܢܬ ܗܘ ܐܠܗܐ ܕܥܒܕܬ ܫܡܝܐ ܘܐܪܥܐ ܘܝܡܡܐ ܘܟܠ ܕܐܝܬ ܒܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 25, 'ܘܐܢܬ ܗܘ ܕܡܠܠܬ ܒܝܕ ܪܘܚܐ ܕܩܘܕܫܐ ܒܦܘܡ ܕܘܝܕ ܥܒܕܟ ܠܡܢܐ ܪܓܫܘ ܥܡܡܐ ܘܐܡܘܬܐ ܪܢܝ ܤܪܝܩܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 26, 'ܩܡܘ ܡܠܟܐ ܕܐܪܥܐ ܘܫܠܝܛܢܐ ܘܐܬܡܠܟܘ ܐܟܚܕܐ ܥܠ ܡܪܝܐ ܘܥܠ ܡܫܝܚܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 27, 'ܐܬܟܢܫܘ ܓܝܪ ܫܪܝܪܐܝܬ ܒܡܕܝܢܬܐ ܗܕܐ ܥܠ ܩܕܝܫܐ ܒܪܟ ܝܫܘܥ ܐܝܢܐ ܕܐܢܬ ܡܫܚܬ ܗܪܘܕܤ ܘܦܝܠܛܘܤ ܥܡ ܥܡܡܐ ܘܟܢܫܐ ܕܐܝܤܪܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 28, 'ܠܡܥܒܕ ܟܠ ܡܐ ܕܐܝܕܟ ܘܨܒܝܢܟ ܩܕܡ ܪܫܡ ܕܢܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 29, 'ܘܐܦ ܗܫܐ ܡܪܝܐ ܚܘܪ ܘܚܙܝ ܠܠܘܚܡܝܗܘܢ ܘܗܒ ܠܥܒܕܝܟ ܕܥܝܢ ܒܓܠܐ ܢܗܘܘܢ ܡܟܪܙܝܢ ܡܠܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 30, 'ܟܕ ܐܝܕܟ ܡܘܫܛ ܐܢܬ ܠܐܤܘܬܐ ܘܠܓܒܪܘܬܐ ܘܠܐܬܘܬܐ ܕܢܗܘܝܢ ܒܫܡܗ ܕܒܪܟ ܩܕܝܫܐ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 31, 'ܘܟܕ ܒܥܘ ܘܐܬܟܫܦܘ ܐܬܬܙܝܥ ܐܬܪܐ ܕܒܗ ܟܢܝܫܝܢ ܗܘܘ ܘܐܬܡܠܝܘ ܟܠܗܘܢ ܒܪܘܚܐ ܕܩܘܕܫܐ ܘܡܡܠܠܝܢ ܗܘܘ ܥܝܢ ܒܓܠܐ ܡܠܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 32, 'ܐܝܬ ܗܘܐ ܕܝܢ ܠܟܢܫܐ ܕܐܢܫܐ ܐܝܠܝܢ ܕܗܝܡܢܘ ܗܘܘ ܚܕܐ ܢܦܫ ܘܚܕ ܪܥܝܢ ܘܠܐ ܐܢܫ ܡܢܗܘܢ ܐܡܪ ܗܘܐ ܥܠ ܢܟܤܐ ܕܩܢܐ ܗܘܐ ܕܕܝܠܗ ܐܢܘܢ ܐܠܐ ܟܠ ܡܕܡ ܕܐܝܬ ܗܘܐ ܠܗܘܢ ܕܓܘܐ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 33, 'ܘܒܚܝܠܐ ܪܒܐ ܡܤܗܕܝܢ ܗܘܘ ܗܢܘܢ ܫܠܝܚܐ ܥܠ ܩܝܡܬܗ ܕܝܫܘܥ ܡܫܝܚܐ ܘܛܝܒܘܬܐ ܪܒܬܐ ܐܝܬ ܗܘܬ ܥܡ ܟܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 34, 'ܘܐܢܫ ܠܝܬ ܗܘܐ ܒܗܘܢ ܕܨܪܝܟ ܐܝܠܝܢ ܓܝܪ ܕܩܢܝܢ ܗܘܘ ܩܘܪܝܐ ܘܒܬܐ ܡܙܒܢܝܢ ܗܘܘ ܘܡܝܬܝܢ ܕܡܝܐ ܕܡܕܡ ܕܡܙܕܒܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 35, 'ܘܤܝܡܝܢ ܠܘܬ ܪܓܠܝܗܘܢ ܕܫܠܝܚܐ ܘܡܬܝܗܒ ܗܘܐ ܠܐܢܫ ܐܢܫ ܐܝܟ ܡܕܡ ܕܤܢܝܩ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 36, 'ܝܘܤܦ ܕܝܢ ܗܘ ܕܐܬܟܢܝ ܒܪܢܒܐ ܡܢ ܫܠܝܚܐ ܕܡܬܬܪܓܡ ܒܪܐ ܕܒܘܝܐܐ ܠܘܝܐ ܡܢ ܐܬܪܐ ܕܩܘܦܪܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 37, 'ܐܝܬ ܗܘܐ ܠܗ ܩܪܝܬܐ ܘܙܒܢܗ ܘܐܝܬܝ ܕܡܝܗ ܘܤܡ ܩܕܡ ܪܓܠܝܗܘܢ ܕܫܠܝܚܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 1, 'ܘܓܒܪܐ ܚܕ ܕܫܡܗ ܗܘܐ ܚܢܢܝܐ ܥܡ ܐܢܬܬܗ ܕܫܡܗ ܗܘܐ ܫܦܝܪܐ ܙܒܢ ܗܘܐ ܩܪܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 2, 'ܘܫܩܠ ܡܢ ܛܝܡܝܗ ܘܛܫܝ ܟܕ ܪܓܝܫܐ ܗܘܬ ܒܗ ܐܢܬܬܗ ܘܐܝܬܝ ܡܢܗ ܡܢ ܟܤܦܐ ܘܤܡ ܩܕܡ ܪܓܠܝܗܘܢ ܕܫܠܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 3, 'ܘܐܡܪ ܠܗ ܫܡܥܘܢ ܚܢܢܝܐ ܡܢܘ ܕܗܟܢܐ ܡܠܐ ܤܛܢܐ ܠܒܟ ܕܬܕܓܠ ܒܪܘܚܐ ܕܩܘܕܫܐ ܘܬܛܫܐ ܡܢ ܟܤܦܐ ܕܕܡܝܗ ܕܩܪܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 4, 'ܠܐ ܗܘܐ ܕܝܠܟ ܗܘܬ ܥܕܠܐ ܬܙܕܒܢ ܘܡܢ ܕܐܙܕܒܢܬ ܬܘܒ ܐܢܬ ܫܠܝܛ ܗܘܝܬ ܥܠ ܕܡܝܗ ܠܡܢܐ ܤܡܬ ܒܠܒܟ ܕܬܥܒܕ ܨܒܘܬܐ ܗܕܐ ܠܐ ܕܓܠܬ ܒܒܢܝ ܐܢܫܐ ܐܠܐ ܒܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 5, 'ܘܟܕ ܫܡܥ ܚܢܢܝܐ ܗܠܝܢ ܡܠܐ ܢܦܠ ܘܡܝܬ ܘܗܘܬ ܕܚܠܬܐ ܪܒܬܐ ܒܟܠܗܘܢ ܗܠܝܢ ܕܫܡܥܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 6, 'ܘܩܡܘ ܐܝܠܝܢ ܕܥܠܝܡܝܢ ܒܗܘܢ ܘܟܢܫܘܗܝ ܘܐܦܩܘ ܩܒܪܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 7, 'ܘܡܢ ܒܬܪ ܕܗܘܝ ܬܠܬ ܫܥܝܢ ܐܦ ܐܢܬܬܗ ܥܠܬ ܟܕ ܠܐ ܝܕܥܐ ܗܘܬ ܡܢܐ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 8, 'ܐܡܪ ܠܗ ܫܡܥܘܢ ܐܡܪ ܠܝ ܐܢ ܒܗܠܝܢ ܕܡܝܐ ܙܒܢܬܘܢ ܩܪܝܬܐ ܗܝ ܕܝܢ ܐܡܪܬ ܐܝܢ ܒܗܠܝܢ ܕܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 9, 'ܐܡܪ ܠܗ ܫܡܥܘܢ ܡܛܠ ܕܐܫܬܘܝܬܘܢ ܠܡܢܤܝܘ ܪܘܚܗ ܕܡܪܝܐ ܗܐ ܪܓܠܝܗܘܢ ܕܩܒܘܪܘܗܝ ܕܒܥܠܟܝ ܒܬܪܥܐ ܘܗܢܘܢ ܢܦܩܘܢܟܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 10, 'ܘܒܗ ܒܫܥܬܐ ܢܦܠܬ ܩܕܡ ܪܓܠܝܗܘܢ ܘܡܝܬܬ ܘܥܠܘ ܥܠܝܡܐ ܗܢܘܢ ܘܐܫܟܚܘܗ ܟܕ ܡܝܬܐ ܘܩܦܤܘ ܐܘܒܠܘ ܩܒܪܘܗ ܥܠ ܓܢܒ ܒܥܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 11, 'ܘܗܘܬ ܕܚܠܬܐ ܪܒܬܐ ܒܟܠܗ ܥܕܬܐ ܘܒܟܠܗܘܢ ܗܢܘܢ ܕܫܡܥܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 12, 'ܘܗܘܝܢ ܗܘܝ ܒܝܕ ܫܠܝܚܐ ܐܬܘܬܐ ܘܓܒܪܘܬܐ ܤܓܝܐܬܐ ܒܥܡܐ ܘܟܠܗܘܢ ܟܢܝܫܝܢ ܗܘܘ ܐܟܚܕܐ ܒܐܤܛܘܐ ܕܫܠܝܡܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 13, 'ܘܡܢ ܐܢܫܐ ܐܚܪܢܐ ܐܢܫ ܠܐ ܡܡܪܚ ܗܘܐ ܕܢܬܩܪܒ ܠܘܬܗܘܢ ܐܠܐ ܡܘܪܒ ܗܘܐ ܠܗܘܢ ܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 14, 'ܘܝܬܝܪ ܡܬܬܘܤܦܢ ܗܘܘ ܐܝܠܝܢ ܕܡܗܝܡܢܝܢ ܗܘܘ ܒܡܪܝܐ ܟܢܫܐ ܕܓܒܪܐ ܘܕܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 15, 'ܐܝܟܢܐ ܕܒܫܘܩܐ ܡܦܩܝܢ ܗܘܘ ܠܟܪܝܗܐ ܟܕ ܪܡܝܢ ܗܘܘ ܒܥܪܤܬܐ ܕܐܡܬܝ ܕܢܗܘܐ ܐܬܐ ܫܡܥܘܢ ܐܦܢ ܛܠܢܝܬܗ ܬܓܢ ܥܠܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 16, 'ܐܬܝܢ ܗܘܘ ܕܝܢ ܤܓܝܐܐ ܠܘܬܗܘܢ ܡܢ ܡܕܝܢܬܐ ܐܚܪܢܝܬܐ ܕܚܕܪܝ ܐܘܪܫܠܡ ܟܕ ܡܝܬܝܢ ܗܘܘ ܟܪܝܗܐ ܘܐܝܠܝܢ ܕܗܘܝܢ ܗܘܝ ܠܗܘܢ ܪܘܚܐ ܛܢܦܬܐ ܘܡܬܚܠܡܝܢ ܗܘܘ ܟܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 17, 'ܘܐܬܡܠܝ ܗܘܐ ܚܤܡܐ ܪܒ ܟܗܢܐ ܘܟܠܗܘܢ ܕܥܡܗ ܕܐܝܬܝܗܘܢ ܗܘܘ ܡܢ ܝܘܠܦܢܐ ܕܙܕܘܩܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 18, 'ܘܐܪܡܝܘ ܐܝܕܝܐ ܥܠ ܫܠܝܚܐ ܘܐܚܕܘ ܐܤܪܘ ܐܢܘܢ ܒܝܬ ܐܤܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 19, 'ܗܝܕܝܢ ܒܠܠܝܐ ܡܠܐܟܐ ܕܡܪܝܐ ܦܬܚ ܬܪܥܐ ܕܒܝܬ ܐܤܝܪܐ ܘܐܦܩ ܐܢܘܢ ܘܐܡܪ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 20, 'ܙܠܘ ܩܘܡܘ ܒܗܝܟܠܐ ܘܡܠܠܘ ܠܥܡܐ ܟܠܗܝܢ ܡܠܐ ܗܠܝܢ ܕܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 21, 'ܘܢܦܩܘ ܥܕܢ ܫܦܪܐ ܘܥܠܘ ܠܗܝܟܠܐ ܘܡܠܦܝܢ ܗܘܘ ܪܒ ܟܗܢܐ ܕܝܢ ܘܐܝܠܝܢ ܕܥܡܗ ܩܪܘ ܠܚܒܪܝܗܘܢ ܘܠܩܫܝܫܐ ܕܐܝܤܪܝܠ ܘܫܕܪܘ ܠܒܝܬ ܐܤܝܪܐ ܕܢܝܬܘܢ ܐܢܘܢ ܠܫܠܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 22, 'ܘܟܕ ܐܙܠܘ ܐܝܠܝܢ ܕܐܫܬܕܪܘ ܡܢܗܘܢ ܠܐ ܐܫܟܚܘ ܐܢܘܢ ܒܝܬ ܐܤܝܪܐ ܘܗܦܟܘ ܐܬܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 23, 'ܐܡܪܝܢ ܐܫܟܚܢ ܒܝܬ ܐܤܝܪܐ ܕܐܚܝܕ ܙܗܝܪܐܝܬ ܘܐܦ ܠܢܛܘܪܐ ܕܩܝܡܝܢ ܥܠ ܬܪܥܐ ܘܦܬܚܢ ܘܐܢܫ ܠܐ ܐܫܟܚܢ ܬܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 24, 'ܘܟܕ ܫܡܥܘ ܗܠܝܢ ܡܠܐ ܪܒܝ ܟܗܢܐ ܘܐܪܟܘܢܐ ܕܗܝܟܠܐ ܬܘܝܪܝܢ ܗܘܘ ܥܠܝܗܘܢ ܘܡܬܚܫܒܝܢ ܗܘܘ ܕܡܢܐ ܗܝ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 25, 'ܘܐܬܐ ܐܢܫ ܐܘܕܥ ܐܢܘܢ ܕܗܢܘܢ ܓܒܪܐ ܕܚܒܫܬܘܢ ܒܝܬ ܐܤܝܪܐ ܗܐ ܩܝܡܝܢ ܒܗܝܟܠܐ ܘܡܠܦܝܢ ܠܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 26, 'ܗܝܕܝܢ ܐܙܠܘ ܐܪܟܘܢܐ ܥܡ ܕܚܫܐ ܕܢܝܬܘܢ ܐܢܘܢ ܠܐ ܒܩܛܝܪܐ ܕܚܠܝܢ ܗܘܘ ܓܝܪ ܕܠܡܐ ܢܪܓܘܡ ܐܢܘܢ ܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 27, 'ܘܟܕ ܐܝܬܝܘ ܐܢܘܢ ܐܩܝܡܘ ܐܢܘܢ ܩܕܡ ܟܠܗ ܟܢܫܐ ܘܐܩܦ ܗܘܐ ܪܒ ܟܗܢܐ ܠܡܐܡܪ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 28, 'ܠܐ ܗܘܐ ܡܦܩܕ ܦܩܕܢ ܗܘܝܢ ܠܟܘܢ ܕܠܐܢܫ ܠܐ ܬܠܦܘܢ ܒܫܡܐ ܗܢܐ ܐܢܬܘܢ ܕܝܢ ܗܐ ܡܠܝܬܘܢܗ ܠܐܘܪܫܠܡ ܡܢ ܝܘܠܦܢܟܘܢ ܘܨܒܝܢ ܐܢܬܘܢ ܕܬܝܬܘܢ ܥܠܝܢ ܕܡܗ ܕܓܒܪܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 29, 'ܥܢܐ ܫܡܥܘܢ ܥܡ ܫܠܝܚܐ ܘܐܡܪ ܠܗܘܢ ܠܐܠܗܐ ܘܠܐ ܠܡܬܛܦܤܘ ܝܬܝܪ ܡܢ ܕܠܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 30, 'ܐܠܗܐ ܕܐܒܗܬܢ ܐܩܝܡ ܠܝܫܘܥ ܐܝܢܐ ܕܐܢܬܘܢ ܩܛܠܬܘܢ ܟܕ ܬܠܝܬܘܢܝܗܝ ܥܠ ܩܝܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 31, 'ܠܗ ܠܗܢܐ ܐܩܝܡ ܐܠܗܐ ܪܫܐ ܘܡܚܝܢܐ ܘܐܪܝܡܗ ܒܝܡܝܢܗ ܐܝܟ ܕܢܬܠ ܬܝܒܘܬܐ ܘܫܘܒܩܢ ܚܛܗܐ ܠܐܝܤܪܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 32, 'ܘܚܢܢ ܤܗܕܐ ܚܢܢ ܕܡܠܐ ܗܠܝܢ ܘܪܘܚܐ ܕܩܘܕܫܐ ܗܘ ܕܝܗܒ ܐܠܗܐ ܠܐܝܠܝܢ ܕܡܗܝܡܢܝܢ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 33, 'ܘܟܕ ܫܡܥܘ ܗܘܘ ܗܠܝܢ ܡܠܐ ܡܬܓܘܙܠܝܢ ܗܘܘ ܒܪܘܓܙܐ ܘܡܬܚܫܒܝܢ ܗܘܘ ܠܡܩܛܠ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 34, 'ܘܩܡ ܗܘܐ ܚܕ ܡܢ ܦܪܝܫܐ ܕܫܡܗ ܗܘܐ ܓܡܠܝܐܝܠ ܡܠܦ ܢܡܘܤܐ ܘܡܝܩܪ ܡܢ ܟܠܗ ܥܡܐ ܘܦܩܕ ܕܢܦܩܘܢ ܐܢܘܢ ܠܫܠܝܚܐ ܠܒܪ ܥܕܢܐ ܙܥܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 35, 'ܘܐܡܪ ܠܗܘܢ ܓܒܪܐ ܒܢܝ ܐܝܤܪܝܠ ܐܙܕܗܪܘ ܒܢܦܫܟܘܢ ܘܚܙܘ ܡܢܐ ܘܠܐ ܠܟܘܢ ܠܡܥܒܕ ܥܠ ܗܠܝܢ ܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 36, 'ܡܢ ܩܕܡ ܓܝܪ ܗܢܐ ܙܒܢܐ ܩܡ ܗܘܐ ܬܘܕܐ ܘܐܡܪ ܥܠ ܢܦܫܗ ܕܡܕܡ ܗܘ ܪܒ ܘܐܙܠܘ ܒܬܪܗ ܐܝܟ ܐܪܒܥܡܐܐ ܓ ܒܪܝܢ ܘܗܘ ܐܬܩܛܠ ܘܐܝܠܝܢ ܕܐܙܠܝܢ ܗܘܘ ܒܬܪܗ ܐܬܒܕܪܘ ܘܗܘܘ ܐܝܟ ܠܐ ܡܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 37, 'ܘܩܡ ܡܢ ܒܬܪܗ ܝܗܘܕܐ ܓܠܝܠܝܐ ܒܝܘܡܬܐ ܕܡܬܟܬܒܝܢ ܗܘܘ ܐܢܫܐ ܒܟܤܦ ܪܫܐ ܘܐܤܛܝ ܥܡܐ ܤܓܝܐܐ ܒܬܪܗ ܘܗܘ ܡܝܬ ܘܟܠܗܘܢ ܐܝܠܝܢ ܕܐܙܠܝܢ ܗܘܘ ܒܬܪܗ ܐܬܒܕܪܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 38, 'ܘܗܫܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܦܪܘܩܘ ܠܟܘܢ ܡܢ ܗܠܝܢ ܐܢܫܐ ܘܫܒܘܩܘ ܠܗܘܢ ܕܐܢ ܗܘ ܕܡܢ ܒܢܝܢܫܐ ܐܝܬܝܗ ܗܕܐ ܡܚܫܒܬܐ ܘܗܢܐ ܥܒܕܐ ܡܫܬܪܝܢ ܘܥܒܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 39, 'ܐܢ ܕܝܢ ܡܢ ܐܠܗܐ ܗܘ ܠܐ ܡܛܝܐ ܒܐܝܕܝܟܘܢ ܕܬܒܛܠܘܢܝܗܝ ܕܠܡܐ ܬܫܬܟܚܘܢ ܠܟܘܢ ܕܠܘܩܒܠ ܐܠܗܐ ܩܝܡܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 40, 'ܘܐܬܛܦܝܤܘ ܠܗ ܘܩܪܘ ܐܢܘܢ ܠܫܠܝܚܐ ܘܢܓܕܘ ܐܢܘܢ ܘܦܩܕܘ ܐܢܘܢ ܕܠܐ ܢܗܘܘܢ ܡܡܠܠܝܢ ܒܫܡܐ ܕܝܫܘܥ ܘܫܪܘ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 41, 'ܘܢܦܩܘ ܡܢ ܩܕܡܝܗܘܢ ܟܕ ܚܕܝܢ ܕܫܘܘ ܗܘܘ ܡܛܠ ܫܡܐ ܕܢܨܛܥܪܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 42, 'ܘܠܐ ܫܠܝܢ ܗܘܘ ܟܠܝܘܡ ܠܡܠܦܘ ܒܗܝܟܠܐ ܘܒܒܝܬܐ ܘܠܡܤܒܪܘ ܥܠ ܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 1, 'ܘܒܗܘܢ ܒܝܘܡܬܐ ܗܢܘܢ ܟܕ ܤܓܝܘ ܬܠܡܝܕܐ ܪܛܢܘ ܗܘܘ ܝܘܢܝܐ ܬܠܡܝܕܐ ܥܠ ܥܒܪܝܐ ܕܡܬܒܤܝܢ ܗܘܝ ܐܪܡܠܬܗܘܢ ܒܬܫܡܫܬܐ ܕܟܠܝܘܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 2, 'ܘܩܪܘ ܬܪܥܤܪ ܫܠܝܚܐ ܠܟܠܗ ܟܢܫܐ ܕܬܠܡܝܕܐ ܘܐܡܪܘ ܠܗܘܢ ܠܐ ܫܦܝܪ ܕܢܫܒܘܩ ܡܠܬܐ ܕܐܠܗܐ ܘܢܫܡܫ ܦܬܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 3, 'ܒܨܘ ܗܟܝܠ ܐܚܝ ܘܓܒܘ ܫܒܥܐ ܓܒܪܝܢ ܡܢܟܘܢ ܕܐܝܬ ܥܠܝܗܘܢ ܤܗܕܘܬܐ ܘܡܠܝܢ ܪܘܚܗ ܕܡܪܝܐ ܘܚܟܡܬܐ ܘܢܩܝܡ ܐܢܘܢ ܥܠ ܗܕܐ ܨܒܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 4, 'ܘܚܢܢ ܢܗܘܐ ܐܡܝܢܝܢ ܒܨܠܘܬܐ ܘܒܬܫܡܫܬܐ ܕܡܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 5, 'ܘܫܦܪܬ ܗܕܐ ܡܠܬܐ ܩܕܡ ܟܠܗ ܥܡܐ ܘܓܒܘ ܠܐܤܛܦܢܘܤ ܓܒܪܐ ܕܡܠܐ ܗܘܐ ܗܝܡܢܘܬܐ ܘܪܘܚܐ ܕܩܘܕܫܐ ܘܠܦܝܠܝܦܘܤ ܘܠܦܪܟܪܘܤ ܘܠܢܝܩܢܘܪ ܘܠܛܝܡܘܢ ܘܠܦܪܡܢܐ ܘܠܢܝܩܠܐܘܤ ܓܝܘܪܐ ܐܢܛܝܘܟܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 6, 'ܗܠܝܢ ܩܡܘ ܩܕܡܝܗܘܢ ܕܫܠܝܚܐ ܘܟܕ ܨܠܝܘ ܤܡܘ ܥܠܝܗܘܢ ܐܝܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 7, 'ܘܡܠܬܗ ܕܐܠܗܐ ܪܒܝܐ ܗܘܬ ܘܤܓܐ ܗܘܐ ܡܢܝܢܐ ܕܬܠܡܝܕܐ ܒܐܘܪܫܠܡ ܛܒ ܘܥܡܐ ܤܓܝܐܐ ܡܢ ܝܗܘܕܝܐ ܡܫܬܡܥ ܗܘܐ ܠܗܝܡܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 8, 'ܐܤܛܦܢܘܤ ܕܝܢ ܡܠܐ ܗܘܐ ܛܝܒܘܬܐ ܘܚܝܠܐ ܘܥܒܕ ܗܘܐ ܐܬܘܬܐ ܘܬܕܡܪܬܐ ܒܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 9, 'ܘܩܡܘ ܗܘܘ ܐܢܫܐ ܡܢ ܟܢܘܫܬܐ ܕܡܬܩܪܝܐ ܕܠܝܒܪܛܝܢܘ ܘܩܘܪܝܢܝܐ ܘܐܠܟܤܢܕܪܝܐ ܘܕܡܢ ܩܝܠܝܩܝܐ ܘܡܢ ܐܤܝܐ ܘܕܪܫܝܢ ܗܘܘ ܥܡ ܐܤܛܦܢܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 10, 'ܘܠܐ ܡܫܟܚܝܢ ܗܘܘ ܠܡܩܡ ܠܘܩܒܠ ܚܟܡܬܐ ܘܪܘܚܐ ܕܡܡܠܠܐ ܗܘܬ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 11, 'ܗܝܕܝܢ ܫܕܪܘ ܠܓܒܪܐ ܘܐܠܦܘ ܐܢܘܢ ܕܢܐܡܪܘܢ ܕܚܢܢ ܫܡܥܢܝܗܝ ܕܐܡܪ ܡܠܐ ܕܓܘܕܦܐ ܥܠ ܡܘܫܐ ܘܥܠ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 12, 'ܘܫܓܫܘ ܠܥܡܐ ܘܠܩܫܝܫܐ ܘܠܤܦܪܐ ܘܐܬܘ ܘܩܡܘ ܥܠܘܗܝ ܘܚܛܦܘ ܐܝܬܝܘܗܝ ܠܡܨܥܬ ܟܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 13, 'ܘܐܩܝܡܘ ܤܗܕܐ ܕܓܠܐ ܕܐܡܪܝܢ ܗܢܐ ܓܒܪܐ ܠܐ ܫܠܐ ܠܡܡܠܠܘ ܡܠܐ ܠܘܩܒܠ ܢܡܘܤܐ ܘܥܠ ܐܬܪܐ ܗܢܐ ܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 14, 'ܚܢܢ ܓܝܪ ܫܡܥܢܝܗܝ ܕܐܡܪ ܕܝܫܘܥ ܗܢܐ ܢܨܪܝܐ ܗܘ ܢܫܪܝܘܗܝ ܠܐܬܪܐ ܗܢܐ ܘܢܚܠܦ ܥܝܕܐ ܕܐܫܠܡ ܠܟܘܢ ܡܘܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 15, 'ܘܚܪܘ ܒܗ ܟܠܗܘܢ ܗܢܘܢ ܕܝܬܒܝܢ ܗܘܘ ܒܟܢܘܫܬܐ ܘܚܙܘ ܦܪܨܘܦܗ ܐܝܟ ܦܪܨܘܦܐ ܕܡܠܐܟܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 1, 'ܘܫܐܠܗ ܪܒ ܟܗܢܐ ܕܐܢ ܗܘ ܕܗܠܝܢ ܗܟܢܐ ܐܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 2, 'ܗܘ ܕܝܢ ܐܡܪ ܓܒܪܐ ܐܚܝܢ ܘܐܒܗܬܢ ܫܡܥܘ ܐܠܗܐ ܕܬܫܒܘܚܬܐ ܐܬܚܙܝ ܠܐܒܘܢ ܐܒܪܗܡ ܟܕ ܐܝܬܘܗܝ ܗܘܐ ܒܝܬ ܢܗܪܝܢ ܥܕ ܠܐ ܢܐܬܐ ܢܥܡܪ ܒܚܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 3, 'ܘܐܡܪ ܗܘܐ ܠܗ ܕܦܘܩ ܡܢ ܐܪܥܟ ܘܡܢ ܠܘܬ ܒܢܝ ܛܘܗܡܟ ܘܬܐ ܠܐܪܥܐ ܐܝܕܐ ܕܐܚܘܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 4, 'ܘܗܝܕܝܢ ܢܦܩ ܐܒܪܗܡ ܡܢ ܐܪܥܐ ܕܟܠܕܝܐ ܘܐܬܐ ܥܡܪ ܒܚܪܢ ܘܡܢ ܬܡܢ ܟܕ ܡܝܬ ܐܒܘܗܝ ܫܢܝܗ ܐܠܗܐ ܠܐܪܥܐ ܗܕܐ ܕܒܗ ܥܡܪܝܢ ܐܢܬܘܢ ܝܘܡܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 5, 'ܘܠܐ ܝܗܒ ܠܗ ܝܪܬܘܬܐ ܒܗ ܐܦ ܠܐ ܕܘܪܟܬܐ ܕܪܓܠܐ ܘܐܫܬܘܕܝ ܗܘܐ ܕܢܬܠܝܗ ܠܗ ܐܝܟ ܕܠܡܐܪܬܗ ܠܗ ܘܠܙܪܥܗ ܟܕ ܠܝܬ ܗܘܐ ܠܗ ܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 6, 'ܘܡܠܠ ܗܘܐ ܥܡܗ ܐܠܗܐ ܟܕ ܐܡܪ ܠܗ ܕܢܗܘܐ ܙܪܥܟ ܬܘܬܒܐ ܒܐܪܥܐ ܢܘܟܪܝܬܐ ܘܢܫܥܒܕܘܢܝܗܝ ܘܢܒܐܫܘܢ ܠܗ ܐܪܒܥܡܐܐ ܫܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 7, 'ܘܠܥܡܐ ܕܢܦܠܚܘܢ ܥܒܕܘܬܐ ܐܕܘܢܝܘܗܝ ܐܢܐ ܐܡܪ ܐܠܗܐ ܘܡܢ ܒܬܪ ܗܠܝܢ ܢܦܩܘܢ ܘܢܦܠܚܘܢ ܠܝ ܒܐܬܪܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 8, 'ܘܝܗܒ ܠܗ ܕܝܬܩܐ ܕܓܙܘܪܬܐ ܘܗܝܕܝܢ ܐܘܠܕ ܠܐܝܤܚܩ ܘܓܙܪܗ ܒܝܘܡܐ ܬܡܝܢܝܐ ܘܐܝܤܚܩ ܐܘܠܕ ܠܝܥܩܘܒ ܘܝܥܩܘܒ ܐܘܠܕ ܠܬܪܥܤܪ ܐܒܗܬܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 9, 'ܘܗܢܘܢ ܐܒܗܬܢ ܛܢܘ ܒܝܘܤܦ ܘܙܒܢܘܗܝ ܠܡܨܪܝܢ ܘܐܠܗܐ ܥܡܗ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 10, 'ܘܦܪܩܗ ܗܘܐ ܡܢ ܟܠܗܘܢ ܐܘܠܨܢܘܗܝ ܘܝܗܒ ܠܗ ܛܝܒܘܬܐ ܘܚܟܡܬܐ ܩܕܡ ܦܪܥܘܢ ܡܠܟܐ ܕܡܨܪܝܢ ܘܐܩܝܡܗ ܪܫܐ ܥܠ ܡܨܪܝܢ ܘܥܠ ܒܝܬܗ ܟܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 11, 'ܘܗܘܐ ܟܦܢܐ ܘܐܘܠܨܢܐ ܪܒܐ ܒܟܠܗ ܡܨܪܝܢ ܘܒܐܪܥܐ ܕܟܢܥܢ ܘܠܝܬ ܗܘܐ ܠܗܘܢ ܠܡܤܒܥ ܠܐܒܗܬܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 12, 'ܘܟܕ ܫܡܥ ܝܥܩܘܒ ܕܐܝܬ ܥܒܘܪܐ ܒܡܨܪܝܢ ܫܕܪ ܗܘܐ ܠܐܒܗܬܢ ܠܘܩܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 13, 'ܘܟܕ ܐܙܠܘ ܕܬܪܬܝܢ ܙܒܢܝܢ ܐܘܕܥ ܝܘܤܦ ܢܦܫܗ ܠܐܚܘܗܝ ܘܐܬܝܕܥ ܠܦܪܥܘܢ ܛܘܗܡܗ ܕܝܘܤܦ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 14, 'ܘܫܕܪ ܗܘܐ ܝܘܤܦ ܘܐܝܬܝܗ ܠܐܒܘܗܝ ܝܥܩܘܒ ܘܠܟܠܗ ܛܘܗܡܗ ܘܗܘܝܢ ܗܘܘ ܒܡܢܝܢܐ ܫܒܥܝܢ ܘܚܡܫ ܢܦܫܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 15, 'ܘܢܚܬ ܝܥܩܘܒ ܠܡܨܪܝܢ ܘܡܝܬ ܬܡܢ ܗܘ ܘܐܒܗܬܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 16, 'ܘܐܫܬܢܝ ܠܫܟܝܡ ܘܐܬܬܤܝܡ ܒܩܒܪܐ ܕܙܒܢ ܗܘܐ ܐܒܪܗܡ ܒܟܤܦܐ ܡܢ ܒܢܝ ܚܡܘܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 17, 'ܘܟܕ ܡܛܝ ܗܘܐ ܙܒܢܐ ܕܡܕܡ ܕܐܫܬܘܕܝ ܗܘܐ ܒܡܘܡܬܐ ܐܠܗܐ ܠܐܒܪܗܡ ܤܓܝ ܗܘܐ ܥܡܐ ܘܬܩܦ ܒܡܨܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 18, 'ܥܕܡܐ ܕܩܡ ܡܠܟܐ ܐܚܪܢܐ ܥܠ ܡܨܪܝܢ ܐܝܢܐ ܕܠܐ ܝܕܥ ܗܘܐ ܠܗ ܠܝܘܤܦ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 19, 'ܘܐܨܛܢܥ ܥܠ ܛܘܗܡܢ ܘܐܒܐܫ ܠܐܒܗܬܢ ܘܦܩܕ ܕܢܗܘܘܢ ܡܫܬܕܝܢ ܝܠܘܕܝܗܘܢ ܕܠܐ ܢܚܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 20, 'ܒܗ ܒܙܒܢܐ ܗܘ ܐܬܝܠܕ ܡܘܫܐ ܘܪܚܝܡ ܗܘܐ ܠܐܠܗܐ ܘܐܬܪܒܝ ܝܪܚܐ ܬܠܬܐ ܒܝܬ ܐܒܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 21, 'ܘܟܕ ܐܫܬܕܝ ܡܢ ܐܡܗ ܐܫܟܚܬܗ ܒܪܬ ܦܪܥܘܢ ܘܪܒܝܬܗ ܠܗ ܠܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 22, 'ܘܐܬܪܕܝ ܡܘܫܐ ܒܟܠܗ ܚܟܡܬܐ ܕܡܨܪܝܐ ܘܥܬܝܕ ܗܘܐ ܒܡܠܘܗܝ ܘܐܦ ܒܥܒܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 23, 'ܘܟܕ ܗܘܐ ܒܪ ܐܪܒܥܝܢ ܫܢܝܢ ܤܠܩ ܗܘܐ ܥܠ ܠܒܗ ܕܢܤܥܘܪ ܠܐܚܘܗܝ ܒܢܝ ܐܝܤܪܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 24, 'ܘܚܙܐ ܠܚܕ ܡܢ ܒܢܝ ܫܪܒܬܗ ܕܡܬܕܒܪ ܒܩܛܝܪܐ ܘܬܒܥܗ ܘܥܒܕ ܠܗ ܕܝܢܐ ܘܩܛܠܗ ܠܡܨܪܝܐ ܗܘ ܕܡܤܟܠ ܗܘܐ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 25, 'ܘܤܒܪ ܕܡܤܬܟܠܝܢ ܐܚܘܗܝ ܒܢܝ ܐܝܤܪܝܠ ܕܐܠܗܐ ܒܐܝܕܗ ܝܗܒ ܠܗܘܢ ܦܘܪܩܢܐ ܘܠܐ ܐܤܬܟܠܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 26, 'ܘܠܝܘܡܐ ܐܚܪܢܐ ܐܬܚܙܝ ܠܗܘܢ ܟܕ ܢܨܝܢ ܗܢܘܢ ܚܕ ܥܡ ܚܕ ܘܡܦܝܤ ܗܘܐ ܠܗܘܢ ܕܢܫܬܝܢܘܢ ܟܕ ܐܡܪ ܓܒܪܐ ܐܚܐ ܐܢܬܘܢ ܡܛܠ ܡܢܐ ܡܤܟܠܝܢ ܐܢܬܘܢ ܚܕ ܒܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 27, 'ܗܘ ܕܝܢ ܕܡܤܟܠ ܗܘܐ ܒܚܒܪܗ ܕܚܩܗ ܡܢ ܠܘܬܗ ܘܐܡܪ ܠܗ ܡܢܘ ܐܩܝܡܟ ܥܠܝܢ ܪܫܐ ܘܕܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 28, 'ܕܠܡܐ ܠܡܩܛܠܢܝ ܒܥܐ ܐܢܬ ܐܝܟ ܕܩܛܠܬ ܐܬܡܠܝ ܠܡܨܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 29, 'ܘܥܪܩ ܡܘܫܐ ܒܡܠܬܐ ܗܕܐ ܘܗܘܐ ܬܘܬܒܐ ܒܐܪܥܐ ܕܡܕܝܢ ܘܗܘܘ ܠܗ ܬܪܝܢ ܒܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 30, 'ܘܟܕ ܡܠܝ ܠܗ ܬܡܢ ܐܪܒܥܝܢ ܫܢܝܢ ܐܬܚܙܝ ܠܗ ܒܡܕܒܪܐ ܕܛܘܪ ܤܝܢܝ ܡܠܐܟܗ ܕܡܪܝܐ ܒܢܘܪܐ ܕܝܩܕܐ ܒܤܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 31, 'ܘܟܕ ܚܙܐ ܡܘܫܐ ܐܬܕܡܪ ܒܚܙܘܐ ܘܟܕ ܐܬܩܪܒ ܕܢܚܙܐ ܐܡܪ ܠܗ ܡܪܝܐ ܒܩܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 32, 'ܐܢܐ ܐܢܐ ܐܠܗܐ ܕܐܒܗܝܟ ܐܠܗܗ ܕܐܒܪܗܡ ܘܕܐܝܤܚܩ ܘܕܝܥܩܘܒ ܘܟܕ ܪܬܝܬ ܗܘܐ ܡܘܫܐ ܠܐ ܡܡܪܚ ܗܘܐ ܕܢܚܘܪ ܒܚܙܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 33, 'ܘܐܡܪ ܠܗ ܡܪܝܐ ܫܪܝ ܡܤܢܝܟ ܡܢ ܪܓܠܝܟ ܐܪܥܐ ܓܝܪ ܕܩܐܡ ܐܢܬ ܒܗ ܩܕܝܫܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 34, 'ܡܚܙܐ ܚܙܝܬ ܐܘܠܨܢܗ ܕܥܡܝ ܕܒܡܨܪܝܢ ܘܬܢܚܬܗ ܫܡܥܬ ܘܢܚܬܬ ܕܐܦܪܘܩ ܐܢܘܢ ܘܗܫܐ ܬܐ ܐܫܕܪܟ ܠܡܨܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 35, 'ܠܗܢܐ ܡܘܫܐ ܕܟܦܪܘ ܒܗ ܟܕ ܐܡܪܝܢ ܕܡܢܘ ܐܩܝܡܟ ܥܠܝܢ ܪܫܐ ܘܕܝܢܐ ܠܗ ܠܗܢܐ ܐܠܗܐ ܪܫܐ ܘܦܪܘܩܐ ܫܕܪ ܠܗܘܢ ܒܐܝܕܝ ܡܠܐܟܐ ܗܘ ܕܐܬܚܙܝ ܠܗ ܒܤܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 36, 'ܗܢܘ ܕܐܦܩ ܐܢܘܢ ܟܕ ܥܒܕ ܐܬܘܬܐ ܘܬܕܡܪܬܐ ܘܓܒܪܘܬܐ ܒܐܪܥܐ ܕܡܨܪܝܢ ܘܒܝܡܐ ܕܤܘܦ ܘܒܡܕܒܪܐ ܫܢܝܢ ܐܪܒܥܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 37, 'ܗܢܘ ܡܘܫܐ ܗܘ ܕܐܡܪ ܠܒܢܝ ܐܝܤܪܝܠ ܕܢܒܝܐ ܢܩܝܡ ܠܟܘܢ ܡܪܝܐ ܐܠܗܐ ܡܢ ܐܚܝܟܘܢ ܐܟܘܬܝ ܠܗ ܬܫܡܥܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 38, 'ܗܢܘ ܕܗܘܐ ܒܟܢܘܫܬܐ ܒܡܕܒܪܐ ܥܡ ܡܠܐܟܐ ܗܘ ܕܡܠܠ ܗܘܐ ܥܡܗ ܘܥܡ ܐܒܗܬܢ ܒܛܘܪܐ ܕܤܝܢܝ ܘܗܘܝܘ ܕܩܒܠ ܡܠܐ ܚܝܬܐ ܕܠܢ ܢܬܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 39, 'ܘܠܐ ܨܒܘ ܠܡܬܕܢܝܘ ܠܗ ܐܒܗܬܢ ܐܠܐ ܫܒܩܘܗܝ ܘܒܠܒܘܬܗܘܢ ܗܦܟܘ ܠܗܘܢ ܠܡܨܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 40, 'ܟܕ ܐܡܪܝܢ ܠܐܗܪܘܢ ܥܒܕ ܠܢ ܐܠܗܐ ܕܢܐܙܠܘܢ ܩܕܡܝܢ ܡܛܠ ܕܗܢܐ ܡܘܫܐ ܕܐܦܩܢ ܡܢ ܐܪܥܐ ܕܡܨܪܝܢ ܠܐ ܝܕܥܝܢ ܚܢܢ ܡܢܐ ܗܘܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 41, 'ܘܥܒܕܘ ܠܗܘܢ ܥܓܠܐ ܒܝܘܡܬܐ ܗܢܘܢ ܘܕܒܚܘ ܕܒܚܐ ܠܦܬܟܪܐ ܘܡܬܒܤܡܝܢ ܗܘܘ ܒܥܒܕ ܐܝܕܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 42, 'ܘܗܦܟ ܐܠܗܐ ܘܐܫܠܡ ܐܢܘܢ ܕܢܗܘܘܢ ܦܠܚܝܢ ܠܚܝܠܘܬܐ ܕܫܡܝܐ ܐܝܟ ܕܟܬܝܒ ܒܟܬܒܐ ܕܢܒܝܐ ܠܡܐ ܐܪܒܥܝܢ ܫܢܝܢ ܒܡܕܒܪܐ ܢܟܤܬܐ ܐܘ ܕܒܚܬܐ ܩܪܒܬܘܢ ܠܝ ܒܢܝ ܐܝܤܪܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 43, 'ܐܠܐ ܫܩܠܬܘܢ ܡܫܟܢܗ ܕܡܠܟܘܡ ܘܟܘܟܒܗ ܕܐܠܗܐ ܕܪܦܢ ܕܡܘܬܐ ܕܥܒܕܬܘܢ ܕܬܗܘܘܢ ܤܓܕܝܢ ܠܗܝܢ ܐܫܢܝܟܘܢ ܠܗܠ ܡܢ ܒܒܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 44, 'ܗܐ ܡܫܟܢܐ ܕܤܗܕܘܬܐ ܕܐܒܗܬܢ ܒܡܕܒܪܐ ܐܝܬܘܗܝ ܗܘܐ ܐܝܟ ܡܐ ܕܦܩܕ ܗܘ ܕܡܠܠ ܥܡ ܡܘܫܐ ܠܡܥܒܕܗ ܒܕܡܘܬܐ ܕܚܘܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 45, 'ܘܠܗ ܠܗܢܐ ܡܫܟܢܐ ܐܦ ܡܥܠܘ ܐܥܠܘܗܝ ܐܒܗܬܢ ܥܡ ܝܫܘܥ ܠܐܪܥܐ ܕܝܗܒ ܠܗܘܢ ܐܠܗܐ ܝܘܪܬܢܐ ܡܢ ܥܡܡܐ ܗܢܘܢ ܕܫܕܐ ܡܢ ܩܕܡܝܗܘܢ ܘܐܬܝܒܠ ܥܕܡܐ ܠܝܘܡܘܗܝ ܕܕܘܝܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 46, 'ܗܘ ܕܐܫܟܚ ܪܚܡܐ ܩܕܡܘܗܝ ܕܐܠܗܐ ܘܫܐܠ ܕܢܫܟܚ ܡܫܟܢܐ ܠܐܠܗܗ ܕܝܥܩܘܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 47, 'ܫܠܝܡܘܢ ܕܝܢ ܒܢܐ ܠܗ ܒܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 48, 'ܘܡܪܝܡܐ ܠܐ ܫܪܐ ܒܥܒܕ ܐܝܕܝܐ ܐܝܟ ܕܐܡܪ ܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 49, 'ܕܫܡܝܐ ܟܘܪܤܝ ܘܐܪܥܐ ܟܘܒܫܐ ܕܬܚܝܬ ܪܓܠܝ ܐܝܢܘ ܒܝܬܐ ܕܬܒܢܘܢ ܠܝ ܐܡܪ ܡܪܝܐ ܐܘ ܐܝܢܘ ܐܬܪܐ ܕܢܝܚܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 50, 'ܠܐ ܗܐ ܐܝܕܐ ܕܝܠܝ ܥܒܕܬ ܗܠܝܢ ܟܠܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 51, 'ܐܘ ܩܫܝܝ ܩܕܠܐ ܘܕܠܐ ܓܙܝܪܝܢ ܒܠܒܗܘܢ ܘܒܡܫܡܥܬܗܘܢ ܐܢܬܘܢ ܒܟܠܙܒܢ ܠܘܩܒܠ ܪܘܚܐ ܕܩܘܕܫܐ ܩܝܡܝܢ ܐܢܬܘܢ ܐܝܟ ܐܒܗܝܟܘܢ ܐܦ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 52, 'ܠܐܝܢܐ ܓܝܪ ܡܢ ܢܒܝܐ ܕܠܐ ܪܕܦܘ ܘܩܛܠܘ ܐܒܗܝܟܘܢ ܠܐܝܠܝܢ ܕܩܕܡܘ ܒܕܩܘ ܥܠ ܡܐܬܝܬܗ ܕܙܕܝܩܐ ܗܘ ܕܐܢܬܘܢ ܐܫܠܡܬܘܢ ܘܩܛܠܬܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 53, 'ܘܩܒܠܬܘܢ ܢܡܘܤܐ ܒܝܕ ܦܘܩܕܢܐ ܕܡܠܐܟܐ ܘܠܐ ܢܛܪܬܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 54, 'ܘܟܕ ܫܡܥܘ ܗܘܘ ܗܠܝܢ ܐܬܡܠܝܘ ܚܡܬܐ ܒܢܦܫܗܘܢ ܘܡܚܪܩܝܢ ܗܘܘ ܫܢܝܗܘܢ ܥܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 55, 'ܘܗܘ ܟܕ ܡܠܐ ܗܘܐ ܗܝܡܢܘܬܐ ܘܪܘܚܐ ܕܩܘܕܫܐ ܚܪ ܒܫܡܝܐ ܘܚܙܐ ܬܫܒܘܚܬܐ ܕܐܠܗܐ ܘܠܝܫܘܥ ܟܕ ܩܐܡ ܡܢ ܝܡܝܢܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 56, 'ܘܐܡܪ ܗܐ ܚܙܐ ܐܢܐ ܫܡܝܐ ܟܕ ܦܬܝܚܝܢ ܘܠܒܪܗ ܕܐܢܫܐ ܟܕ ܩܐܡ ܡܢ ܝܡܝܢܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 57, 'ܘܩܥܘ ܒܩܠܐ ܪܡܐ ܘܤܟܪܘ ܐܕܢܝܗܘܢ ܘܓܙܡܘ ܥܠܘܗܝ ܟܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 58, 'ܘܐܚܕܘ ܐܦܩܘܗܝ ܠܒܪ ܡܢ ܡܕܝܢܬܐ ܘܪܓܡܝܢ ܗܘܘ ܠܗ ܘܐܝܠܝܢ ܕܐܤܗܕܘ ܥܠܘܗܝ ܤܡܘ ܢܚܬܝܗܘܢ ܠܘܬ ܪܓܠܘܗܝ ܕܥܠܝܡܐ ܚܕ ܕܡܬܩܪܐ ܫܐܘܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 59, 'ܘܪܓܡܝܢ ܗܘܘ ܠܗ ܠܐܤܛܦܢܘܤ ܟܕ ܡܨܠܐ ܘܐܡܪ ܡܪܢ ܝܫܘܥ ܩܒܠ ܪܘܚܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 60, 'ܘܟܕ ܤܡ ܒܘܪܟܐ ܩܥܐ ܒܩܠܐ ܪܡܐ ܘܐܡܪ ܡܪܢ ܠܐ ܬܩܝܡ ܠܗܘܢ ܗܕܐ ܚܛܝܬܐ ܘܟܕ ܗܕܐ ܐܡܪ ܫܟܒ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 1, 'ܫܐܘܠ ܕܝܢ ܨܒܐ ܗܘܐ ܘܡܫܘܬܦ ܗܘܐ ܒܩܛܠܗ ܘܗܘܐ ܗܘܐ ܒܗܘ ܝܘܡܐ ܪܕܘܦܝܐ ܪܒܐ ܠܥܕܬܐ ܕܒܐܘܪܫܠܡ ܘܐܬܒܕܪܘ ܟܠܗܘܢ ܒܩܘܪܝܐ ܕܝܗܘܕ ܘܐܦ ܒܝܬ ܫܡܪܝܐ ܠܒܪ ܡܢ ܫܠܝܚܐ ܒܠܚܘܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 2, 'ܘܩܦܤܘ ܩܒܪܘܗܝ ܠܐܤܛܦܢܘܤ ܓܒܪܐ ܡܗܝܡܢܐ ܘܐܬܐܒܠܘ ܥܠܘܗܝ ܪܘܪܒܐܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 3, 'ܫܐܘܠ ܕܝܢ ܪܕܦ ܗܘܐ ܠܥܕܬܗ ܕܐܠܗܐ ܟܕ ܥܐܠ ܗܘܐ ܠܒܬܐ ܘܡܓܪܓܪ ܠܓܒܪܐ ܘܠܢܫܐ ܘܡܫܠܡܝܢ ܗܘܐ ܠܒܝܬ ܐܤܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 4, 'ܘܗܢܘܢ ܕܐܬܒܕܪܘ ܡܬܟܪܟܝܢ ܗܘܘ ܘܡܟܪܙܝܢ ܡܠܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 5, 'ܦܝܠܝܦܘܤ ܕܝܢ ܢܚܬ ܠܗ ܠܡܕܝܢܬܐ ܕܫܡܪܝܐ ܘܡܟܪܙ ܗܘܐ ܠܗܘܢ ܥܠ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 6, 'ܘܟܕ ܫܡܥܝܢ ܗܘܘ ܡܠܬܗ ܒܢܝܢܫܐ ܕܬܡܢ ܨܝܬܝܢ ܗܘܘ ܠܗ ܘܡܬܛܦܝܤܝܢ ܗܘܘ ܠܟܠ ܕܐܡܪ ܗܘܐ ܕܚܙܝܢ ܗܘܘ ܐܬܘܬܐ ܕܥܒܕ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 7, 'ܤܓܝܐܐ ܓܝܪ ܕܐܚܝܕܢ ܗܘܝ ܠܗܘܢ ܪܘܚܐ ܛܢܦܬܐ ܩܥܝܢ ܗܘܘ ܒܩܠܐ ܪܡܐ ܘܢܦܩܢ ܗܘܝ ܡܢܗܘܢ ܘܐܚܪܢܐ ܡܫܪܝܐ ܘܡܚܓܪܐ ܐܬܐܤܝܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 8, 'ܘܚܕܘܬܐ ܪܒܬܐ ܗܘܬ ܒܗܝ ܡܕܝܢܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 9, 'ܐܝܬ ܗܘܐ ܕܝܢ ܬܡܢ ܓܒܪܐ ܚܕ ܕܫܡܗ ܤܝܡܘܢ ܕܥܡܝܪ ܗܘܐ ܠܗ ܒܗ ܒܡܕܝܢܬܐ ܙܒܢܐ ܤܓܝܐܐ ܘܒܚܪܫܘܗܝ ܡܛܥܐ ܗܘܐ ܠܥܡܐ ܕܫܡܪܝܐ ܟܕ ܡܘܪܒ ܗܘܐ ܢܦܫܗ ܘܐܡܪ ܕܐܢܐ ܐܢܐ ܪܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 10, 'ܘܨܠܝܢ ܗܘܘ ܠܘܬܗ ܟܠܗܘܢ ܪܘܪܒܐ ܘܕܩܕܩܐ ܘܐܡܪܝܢ ܗܘܘ ܗܢܘ ܚܝܠܗ ܪܒܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 11, 'ܘܡܬܛܦܝܤܝܢ ܗܘܘ ܠܗ ܟܠܗܘܢ ܡܛܠ ܕܙܒܢܐ ܤܓܝܐܐ ܒܚܪܫܘܗܝ ܐܬܡܗ ܗܘܐ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 12, 'ܟܕ ܕܝܢ ܗܝܡܢܘ ܠܦܝܠܝܦܘܤ ܕܡܤܒܪ ܗܘܐ ܡܠܟܘܬܐ ܕܐܠܗܐ ܒܫܡܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܥܡܕܝܢ ܗܘܘ ܓܒܪܐ ܘܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 13, 'ܘܐܦ ܗܘ ܤܝܡܘܢ ܗܝܡܢ ܗܘܐ ܘܥܡܕ ܘܢܩܝܦ ܗܘܐ ܠܗ ܠܦܝܠܝܦܘܤ ܘܟܕ ܚܙܐ ܗܘܐ ܐܬܘܬܐ ܘܚܝܠܐ ܪܘܪܒܐ ܕܗܘܝܢ ܗܘܘ ܒܐܝܕܗ ܬܡܗ ܗܘܐ ܘܡܬܕܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 14, 'ܘܟܕ ܫܡܥܘ ܫܠܝܚܐ ܕܒܐܘܪܫܠܡ ܕܩܒܠܘ ܥܡܐ ܕܫܡܪܝܐ ܡܠܬܐ ܕܐܠܗܐ ܫܕܪܘ ܠܘܬܗܘܢ ܠܫܡܥܘܢ ܟܐܦܐ ܘܠܝܘܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 15, 'ܘܢܚܬܘ ܘܨܠܝܘ ܥܠܝܗܘܢ ܐܝܟ ܕܢܩܒܠܘܢ ܪܘܚܐ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 16, 'ܠܝܬ ܗܘܐ ܓܝܪ ܥܠ ܚܕ ܡܢܗܘܢ ܥܕܟܝܠ ܒܠܚܘܕ ܕܝܢ ܥܡܕܝܢ ܗܘܘ ܒܫܡܗ ܕܡܪܢ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 17, 'ܗܝܕܝܢ ܤܝܡܝܢ ܗܘܘ ܥܠܝܗܘܢ ܐܝܕܐ ܘܡܩܒܠܝܢ ܗܘܘ ܪܘܚܐ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 18, 'ܘܟܕ ܚܙܐ ܤܝܡܘܢ ܕܒܤܝܡ ܐܝܕܐ ܕܫܠܝܚܐ ܡܬܝܗܒܐ ܪܘܚܐ ܕܩܘܕܫܐ ܩܪܒ ܠܗܘܢ ܟܤܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 19, 'ܟܕ ܐܡܪ ܗܒܘ ܐܦ ܠܝ ܫܘܠܛܢܐ ܗܢܐ ܕܐܝܢܐ ܕܐܤܝܡ ܥܠܘܗܝ ܐܝܕܐ ܢܗܘܐ ܡܩܒܠ ܪܘܚܐ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 20, 'ܐܡܪ ܠܗ ܫܡܥܘܢ ܟܐܦܐ ܟܤܦܟ ܥܡܟ ܢܐܙܠ ܠܐܒܕܢܐ ܡܛܠ ܕܤܒܪܬ ܕܡܘܗܒܬܗ ܕܐܠܗܐ ܒܩܢܝܢ ܥܠܡܐ ܡܬܩܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 21, 'ܠܝܬ ܠܟ ܡܢܬܐ ܐܦܠܐ ܦܤܐ ܒܗܝܡܢܘܬܐ ܗܕܐ ܡܛܠ ܕܠܒܟ ܠܐ ܗܘܐ ܬܪܝܨ ܩܕܡ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 22, 'ܒܪܡ ܬܘܒ ܡܢ ܒܝܫܘܬܟ ܗܕܐ ܘܒܥܝ ܡܢ ܐܠܗܐ ܕܠܡܐ ܢܫܬܒܩ ܠܟ ܢܟܠܐ ܕܠܒܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 23, 'ܒܟܒܕܐ ܓܝܪ ܡܪܝܪܬܐ ܘܒܩܛܪܐ ܕܥܘܠܐ ܚܙܐ ܐܢܐ ܕܐܝܬܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 24, 'ܥܢܐ ܤܝܡܘܢ ܘܐܡܪ ܒܥܘ ܐܢܬܘܢ ܚܠܦܝ ܡܢ ܐܠܗܐ ܕܠܐ ܢܐܬܐ ܥܠܝ ܡܕܡ ܡܢ ܗܠܝܢ ܕܐܡܪܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 25, 'ܫܡܥܘܢ ܕܝܢ ܘܝܘܚܢܢ ܟܕ ܤܗܕܘ ܐܢܘܢ ܘܐܠܦܘ ܡܠܬܐ ܕܐܠܗܐ ܗܦܟܘ ܠܗܘܢ ܠܐܘܪܫܠܡ ܘܒܩܘܪܝܐ ܤܓܝܐܬܐ ܕܫܡܪܝܐ ܤܒܪܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 26, 'ܘܡܠܠ ܡܠܐܟܐ ܕܡܪܝܐ ܥܡ ܦܝܠܝܦܘܤ ܘܐܡܪ ܠܗ ܩܘܡ ܙܠ ܠܬܝܡܢܐ ܒܐܘܪܚܐ ܡܕܒܪܝܬܐ ܕܢܚܬܐ ܡܢ ܐܘܪܫܠܡ ܠܓܙܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 27, 'ܘܩܡ ܐܙܠ ܘܐܪܥܗ ܡܗܝܡܢܐ ܚܕ ܕܐܬܐ ܗܘܐ ܡܢ ܟܘܫ ܫܠܝܛܐ ܕܩܢܕܩ ܡܠܟܬܐ ܕܟܘܫܝܐ ܘܗܘ ܫܠܝܛ ܗܘܐ ܥܠ ܟܠܗ ܓܙܗ ܘܐܬܐ ܗܘܐ ܕܢܤܓܘܕ ܒܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 28, 'ܘܟܕ ܗܦܟ ܕܢܐܙܠ ܝܬܒ ܗܘܐ ܥܠ ܡܪܟܒܬܐ ܘܩܪܐ ܗܘܐ ܒܐܫܥܝܐ ܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 29, 'ܘܐܡܪܬ ܪܘܚܐ ܠܦܝܠܝܦܘܤ ܐܬܩܪܒ ܘܩܦ ܠܡܪܟܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 30, 'ܘܟܕ ܐܬܩܪܒ ܫܡܥ ܕܩܪܐ ܒܐܫܥܝܐ ܢܒܝܐ ܘܐܡܪ ܠܗ ܕܐܢ ܡܤܬܟܠ ܐܢܬ ܡܢܐ ܩܪܐ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 31, 'ܘܗܘ ܐܡܪ ܐܝܟܢܐ ܡܫܟܚ ܐܢܐ ܕܐܤܬܟܠ ܐܠܐ ܐܢ ܐܢܫ ܢܪܬܝܢܝ ܘܒܥܐ ܡܢܗ ܡܢ ܦܝܠܝܦܘܤ ܕܢܤܩ ܘܢܬܒ ܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 32, 'ܦܤܘܩܐ ܕܝܢ ܕܟܬܒܐ ܕܩܪܐ ܗܘܐ ܒܗ ܐܝܬܘܗܝ ܗܘܐ ܗܢܐ ܐܝܟ ܐܡܪܐ ܠܢܟܤܬܐ ܐܬܕܒܪ ܘܐܝܟ ܢܩܝܐ ܩܕܡ ܓܙܘܙܐ ܫܬܝܩ ܗܘܐ ܘܗܟܢܐ ܠܐ ܦܬܚ ܦܘܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 33, 'ܒܡܘܟܟܗ ܡܢ ܚܒܘܫܝܐ ܘܡܢ ܕܝܢܐ ܐܬܕܒܪ ܘܕܪܗ ܡܢܘ ܢܫܬܥܐ ܕܡܫܬܩܠܝܢ ܚܝܘܗܝ ܡܢ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 34, 'ܘܐܡܪ ܗܘ ܡܗܝܡܢܐ ܠܦܝܠܝܦܘܤ ܒܥܐ ܐܢܐ ܡܢܟ ܥܠ ܡܢܘ ܐܡܪܗ ܗܕܐ ܢܒܝܐ ܥܠ ܢܦܫܗ ܐܘ ܥܠ ܐܢܫ ܐܚܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 35, 'ܗܝܕܝܢ ܦܝܠܝܦܘܤ ܦܬܚ ܦܘܡܗ ܘܫܪܝ ܡܢܗ ܡܢ ܗܢܐ ܟܬܒܐ ܡܤܒܪ ܠܗ ܥܠ ܡܪܢ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 36, 'ܘܟܕ ܗܢܘܢ ܐܙܠܝܢ ܒܐܘܪܚܐ ܡܛܝܘ ܗܘܘ ܠܕܘܟܬܐ ܚܕܐ ܕܐܝܬ ܒܗ ܡܝܐ ܘܐܡܪ ܗܘ ܡܗܝܡܢܐ ܗܐ ܡܝܐ ܡܢܐ ܗܝ ܟܠܝܬܐ ܕܐܥܡܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 37, 'ܘܐܡܪ ܦܝܠܝܦܘܤ ܐܢ ܡܗܝܡܢ ܐܢܬ ܡܢ ܟܠܗ ܠܒܐ ܫܠܝܛ ܘܥܢܐ ܘܐܡܪ ܐܢܐ ܡܗܝܡܢ ܐܢܐ ܕܝܫܘܥ ܡܫܝܚܐ ܒܪܗ ܕܐܠܗܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 38, 'ܘܦܩܕ ܗܘܐ ܕܬܩܘܡ ܡܪܟܒܬܐ ܘܢܚܬܘ ܬܪܝܗܘܢ ܠܡܝܐ ܘܐܥܡܕܗ ܦܝܠܝܦܘܤ ܠܡܗܝܡܢܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 39, 'ܘܟܕ ܤܠܩܘ ܡܢ ܡܝܐ ܪܘܚܗ ܕܡܪܝܐ ܚܛܦܬ ܠܦܝܠܝܦܘܤ ܘܬܘܒ ܠܐ ܚܙܝܗܝ ܡܗܝܡܢܐ ܗܘ ܐܠܐ ܐܙܠ ܗܘܐ ܒܐܘܪܚܗ ܟܕ ܚܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 40, 'ܦܝܠܝܦܘܤ ܕܝܢ ܐܫܬܟܚ ܒܐܙܘܛܘܤ ܘܡܢ ܬܡܢ ܡܬܟܪܟ ܗܘܐ ܘܡܤܒܪ ܒܡܕܝܢܬܐ ܟܠܗܝܢ ܥܕܡܐ ܕܐܬܐ ܠܩܤܪܝܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 1, 'ܫܐܘܠ ܕܝܢ ܥܕܟܝܠ ܡܠܐ ܗܘܐ ܠܘܚܡܐ ܘܚܡܬܐ ܕܩܛܠܐ ܥܠ ܬܠܡܝܕܘܗܝ ܕܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 2, 'ܘܫܐܠ ܠܗ ܐܓܪܬܐ ܡܢ ܪܒ ܟܗܢܐ ܕܢܬܠ ܠܗ ܠܕܪܡܤܘܩ ܠܟܢܘܫܬܐ ܕܐܢ ܗܘ ܕܢܫܟܚ ܕܪܕܝܢ ܒܗܕܐ ܐܘܪܚܐ ܓܒܪܐ ܐܘ ܢܫܐ ܢܐܤܘܪ ܢܝܬܐ ܐܢܘܢ ܠܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 3, 'ܘܟܕ ܐܙܠ ܗܘܐ ܘܫܪܝ ܡܡܛܐ ܠܕܪܡܤܘܩ ܡܢ ܬܚܝܬ ܫܠܝܐ ܐܙܠܓ ܗܘܐ ܥܠܘܗܝ ܢܘܗܪܐ ܡܢ ܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 4, 'ܘܢܦܠ ܥܠ ܐܪܥܐ ܘܫܡܥ ܩܠܐ ܕܐܡܪ ܠܗ ܫܐܘܠ ܫܐܘܠ ܡܢܐ ܪܕܦ ܐܢܬ ܠܝ ܩܫܐ ܗܘ ܠܟ ܠܡܒܥܛܘ ܠܥܘܩܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 5, 'ܥܢܐ ܗܘ ܘܐܡܪ ܡܢ ܐܢܬ ܡܪܝ ܘܡܪܢ ܐܡܪ ܐܢܐ ܐܢܐ ܝܫܘܥ ܢܨܪܝܐ ܗܘ ܕܐܢܬ ܪܕܦ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 6, 'ܐܠܐ ܩܘܡ ܥܘܠ ܠܡܕܝܢܬܐ ܘܬܡܢ ܢܬܡܠܠ ܥܡܟ ܥܠ ܡܐ ܕܘܠܐ ܠܟ ܠܡܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 7, 'ܘܓܒܪܐ ܕܐܙܠܝܢ ܗܘܘ ܥܡܗ ܒܐܘܪܚܐ ܩܝܡܝܢ ܗܘܘ ܟܕ ܬܡܝܗܝܢ ܡܛܠ ܕܩܠܐ ܒܠܚܘܕ ܫܡܥܝܢ ܗܘܘ ܐܢܫ ܕܝܢ ܠܐ ܡܬܚܙܐ ܗܘܐ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 8, 'ܘܩܡ ܫܐܘܠ ܡܢ ܐܪܥܐ ܘܠܐ ܡܬܚܙܐ ܗܘܐ ܠܗ ܡܕܡ ܟܕ ܥܝܢܘܗܝ ܦܬܝܚܢ ܗܘܝ ܘܟܕ ܐܚܝܕܝܢ ܒܐܝܕܘܗܝ ܐܥܠܘܗܝ ܠܕܪܡܤܘܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 9, 'ܘܠܐ ܡܬܚܙܐ ܗܘܐ ܠܗ ܬܠܬܐ ܝܘܡܝܢ ܘܠܐ ܐܟܠ ܘܠܐ ܐܫܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 10, 'ܐܝܬ ܗܘܐ ܕܝܢ ܒܗ ܒܕܪܡܤܘܩ ܬܠܡܝܕܐ ܚܕ ܕܫܡܗ ܗܘܐ ܚܢܢܝܐ ܘܡܪܝܐ ܐܡܪ ܠܗ ܒܚܙܘܐ ܚܢܢܝܐ ܘܐܡܪ ܗܐ ܐܢܐ ܡܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 11, 'ܘܡܪܢ ܐܡܪ ܠܗ ܩܘܡ ܙܠ ܠܫܘܩܐ ܕܡܬܩܪܐ ܬܪܝܨܐ ܘܒܥܝ ܒܒܝܬܐ ܕܝܗܘܕܐ ܠܫܐܘܠ ܕܐܝܬܘܗܝ ܡܢ ܛܪܤܘܤ ܡܕܝܢܬܐ ܗܐ ܓܝܪ ܟܕ ܗܘ ܡܨܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 12, 'ܚܙܐ ܒܚܙܘܐ ܠܓܒܪܐ ܕܫܡܗ ܚܢܢܝܐ ܕܥܠ ܘܤܡ ܥܠܘܗܝ ܐܝܕܐ ܐܝܟ ܕܢܬܦܬܚܢ ܥܝܢܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 13, 'ܘܐܡܪ ܚܢܢܝܐ ܡܪܝ ܫܡܥܬ ܡܢ ܤܓܝܐܐ ܥܠ ܓܒܪܐ ܗܢܐ ܕܟܡܐ ܒܝܫܬܐ ܐܤܒܠ ܠܩܕܝܫܝܟ ܒܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 14, 'ܘܗܐ ܐܦ ܗܪܟܐ ܐܝܬ ܠܗ ܫܘܠܛܢܐ ܡܢ ܪܒܝ ܟܗܢܐ ܕܢܐܤܘܪ ܠܟܠܗܘܢ ܐܝܠܝܢ ܕܩܪܝܢ ܫܡܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 15, 'ܘܐܡܪ ܠܗ ܡܪܝܐ ܩܘܡ ܙܠ ܡܛܠ ܕܡܐܢܐ ܗܘ ܠܝ ܓܒܝܐ ܕܢܫܩܘܠ ܫܡܝ ܒܥܡܡܐ ܘܒܡܠܟܐ ܘܒܝܬ ܒܢܝ ܐܝܤܪܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 16, 'ܐܢܐ ܓܝܪ ܐܚܘܝܘܗܝ ܟܡܐ ܥܬܝܕ ܠܡܚܫ ܡܛܠ ܫܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 17, 'ܗܝܕܝܢ ܚܢܢܝܐ ܐܙܠ ܠܒܝܬܐ ܠܘܬܗ ܘܤܡ ܥܠܘܗܝ ܐܝܕܐ ܘܐܡܪ ܠܗ ܫܐܘܠ ܐܚܝ ܡܪܢ ܝܫܘܥ ܫܕܪܢܝ ܗܘ ܕܐܬܚܙܝ ܠܟ ܒܐܘܪܚܐ ܟܕ ܐܬܐ ܐܢܬ ܐܝܟ ܕܢܬܦܬܚܢ ܥܝܢܝܟ ܘܬܬܡܠܐ ܪܘܚܐ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 18, 'ܘܒܪ ܫܥܬܗ ܢܦܠ ܡܢ ܥܝܢܘܗܝ ܡܕܡ ܕܕܡܐ ܠܩܠܦܐ ܘܐܬܦܬܚ ܥܝܢܘܗܝ ܘܩܡ ܥܡܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 19, 'ܘܩܒܠ ܤܝܒܪܬܐ ܘܐܬܚܝܠ ܘܗܘܐ ܝܘܡܬܐ ܠܘܬ ܬܠܡܝܕܐ ܗܢܘܢ ܕܐܝܬ ܗܘܘ ܒܕܪܡܤܘܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 20, 'ܘܒܪ ܫܥܬܗ ܡܟܪܙ ܗܘܐ ܒܟܢܘܫܬܐ ܕܝܗܘܕܝܐ ܥܠ ܝܫܘܥ ܕܗܘܝܘ ܒܪܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 21, 'ܘܬܡܝܗܝܢ ܗܘܘ ܟܠܗܘܢ ܐܝܠܝܢ ܕܫܡܥܝܢ ܗܘܘ ܠܗ ܘܐܡܪܝܢ ܗܘܘ ܠܐ ܗܘܐ ܗܢܐ ܗܘ ܕܪܕܦ ܗܘܐ ܠܟܠܗܘܢ ܐܝܠܝܢ ܕܩܪܝܢ ܗܘܘ ܠܫܡܐ ܗܢܐ ܒܐܘܪܫܠܡ ܐܦ ܠܗܪܟܐ ܥܠܝܗ ܥܠ ܗܕܐ ܡܫܕܪ ܗܘܐ ܐܝܟ ܕܢܐܤܘܪ ܢܘܒܠ ܐܢܘܢ ܠܪܒܝ ܟܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 22, 'ܫܐܘܠ ܕܝܢ ܝܬܝܪ ܡܬܚܝܠ ܗܘܐ ܘܡܙܝܥ ܗܘܐ ܠܗܘܢ ܠܝܗܘܕܝܐ ܗܢܘܢ ܕܥܡܪܝܢ ܗܘܘ ܒܕܪܡܤܘܩ ܟܕ ܡܚܘܐ ܗܘܐ ܕܗܢܘ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 23, 'ܘܟܕ ܤܓܝܘ ܠܗ ܬܡܢ ܝܘܡܬܐ ܥܒܕܘ ܥܠܘܗܝ ܢܟܠܐ ܝܗܘܕܝܐ ܕܢܩܛܠܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 24, 'ܐܬܒܕܩ ܠܗ ܕܝܢ ܠܫܐܘܠ ܐܦܪܤܢܐ ܕܒܥܝܢ ܗܘܘ ܠܡܥܒܕ ܠܗ ܘܢܛܪܝܢ ܗܘܘ ܬܪܥܐ ܕܡܕܝܢܬܐ ܐܝܡܡܐ ܘܠܠܝܐ ܕܢܩܛܠܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 25, 'ܗܝܕܝܢ ܤܡܘܗܝ ܬܠܡܝܕܐ ܒܐܤܦܪܝܕܐ ܘܫܒܘܗܝ ܡܢ ܫܘܪܐ ܒܠܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 26, 'ܘܐܙܠ ܠܗ ܠܐܘܪܫܠܡ ܘܨܒܐ ܗܘܐ ܠܡܬܢܩܦܘ ܠܬܠܡܝܕܐ ܘܟܠܗܘܢ ܕܚܠܝܢ ܗܘܘ ܡܢܗ ܘܠܐ ܡܗܝܡܢܝܢ ܗܘܘ ܕܬܠܡܝܕܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 27, 'ܒܪܢܒܐ ܕܝܢ ܐܚܕܗ ܘܐܝܬܝܗ ܠܘܬ ܫܠܝܚܐ ܘܐܫܬܥܝ ܠܗܘܢ ܐܝܟܢܐ ܒܐܘܪܚܐ ܚܙܐ ܠܡܪܝܐ ܘܕܐܝܟܢܐ ܡܠܠ ܥܡܗ ܘܐܝܟܢܐ ܒܕܪܡܤܘܩ ܥܝܢ ܒܓܠܐ ܡܠܠ ܒܫܡܗ ܕܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 28, 'ܘܥܐܠ ܗܘܐ ܥܡܗܘܢ ܘܢܦܩ ܒܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 29, 'ܘܡܡܠܠ ܗܘܐ ܒܫܡܗ ܕܝܫܘܥ ܥܝܢ ܒܓܠܐ ܘܕܪܫ ܗܘܐ ܥܡ ܝܗܘܕܝܐ ܐܝܠܝܢ ܕܝܕܥܝܢ ܗܘܘ ܝܘܢܐܝܬ ܗܢܘܢ ܕܝܢ ܨܒܝܢ ܗܘܘ ܠܡܩܛܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 30, 'ܘܟܕ ܝܕܥܘ ܐܚܐ ܐܝܬܝܘܗܝ ܒܠܠܝܐ ܠܩܤܪܝܐ ܘܡܢ ܬܡܢ ܫܕܪܘܗܝ ܠܛܪܤܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 31, 'ܒܪܡ ܕܝܢ ܥܕܬܐ ܕܒܟܠܗ ܝܗܘܕ ܘܒܓܠܝܠܐ ܘܒܫܡܪܝܢ ܐܝܬ ܗܘܐ ܒܗ ܫܠܡܐ ܟܕ ܡܬܒܢܝܐ ܗܘܬ ܘܪܕܝܐ ܒܕܚܠܬ ܐܠܗܐ ܘܒܒܘܝܐܐ ܕܪܘܚܐ ܕܩܘܕܫܐ ܤܓܝܐ ܗܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 32, 'ܘܗܘܐ ܕܟܕ ܡܬܟܪܟ ܗܘܐ ܫܡܥܘܢ ܒܡܕܝܢܬܐ ܢܚܬ ܐܦ ܠܘܬ ܩܕܝܫܐ ܕܥܡܪܝܢ ܒܠܘܕ ܡܕܝܢܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 33, 'ܘܐܫܟܚ ܓܒܪܐ ܚܕ ܕܫܡܗ ܐܢܝܤ ܕܪܡܐ ܗܘܐ ܒܥܪܤܐ ܘܡܫܪܝ ܫܢܝܢ ܬܡܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 34, 'ܘܐܡܪ ܠܗ ܫܡܥܘܢ ܐܢܝܐ ܡܐܤܐ ܠܟ ܝܫܘܥ ܡܫܝܚܐ ܩܘܡ ܘܫܘܐ ܥܪܤܟ ܘܒܪ ܫܥܬܗ ܩܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 35, 'ܘܚܙܐܘܗܝ ܟܠܗܘܢ ܕܥܡܪܝܢ ܒܠܘܕ ܘܒܤܪܘܢܐ ܘܐܬܦܢܝܘ ܠܘܬ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 36, 'ܐܝܬ ܗܘܐ ܕܝܢ ܬܠܡܝܕܬܐ ܚܕܐ ܒܝܘܦܐ ܡܕܝܢܬܐ ܕܫܡܗ ܗܘܐ ܛܒܝܬܐ ܗܕܐ ܥܬܝܪܐ ܗܘܬ ܒܥܒܕܐ ܛܒܐ ܘܒܙܕܩܬܐ ܕܥܒܕܐ ܗܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 37, 'ܐܬܟܪܗܬ ܕܝܢ ܒܗܘܢ ܒܗܢܘܢ ܝܘܡܬܐ ܘܡܝܬܬ ܘܐܤܚܝܘܗ ܘܤܡܘܗ ܒܥܠܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 38, 'ܘܫܡܥܘ ܗܘܘ ܬܠܡܝܕܐ ܕܫܡܥܘܢ ܒܠܘܕ ܗܘ ܡܕܝܢܬܐ ܗܝ ܕܐܝܬܝܗ ܥܠ ܓܢܒ ܝܘܦܐ ܘܫܕܪܘ ܗܘܘ ܠܘܬܗ ܓܒܪܐ ܬܪܝܢ ܕܢܒܥܘܢ ܡܢܗ ܕܠܐ ܬܡܐܢ ܠܗ ܕܢܐܬܐ ܨܐܕܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 39, 'ܘܩܡ ܫܡܥܘܢ ܐܙܠ ܥܡܗܘܢ ܘܟܕ ܐܬܐ ܐܤܩܘܗܝ ܠܥܠܝܬܐ ܘܟܢܫ ܩܡ ܠܗܝܢ ܚܕܪܘܗܝ ܟܠܗܝܢ ܐܪܡܠܬܐ ܟܕ ܒܟܝܢ ܘܡܚܘܝܢ ܠܗ ܟܘܬܝܢܝܬܐ ܘܡܪܛܘܛܐ ܗܠܝܢ ܕܝܗܒܐ ܗܘܬ ܠܗܝܢ ܛܒܝܬܐ ܟܕ ܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 40, 'ܫܡܥܘܢ ܕܝܢ ܐܦܩ ܠܟܠܗܘܢ ܐܢܫܐ ܠܒܪ ܘܩܥܕ ܥܠ ܒܘܪܟܘܗܝ ܘܨܠܝ ܘܐܬܦܢܝ ܠܘܬ ܫܠܕܐ ܘܐܡܪ ܛܒܝܬܐ ܩܘܡܝ ܗܝ ܕܝܢ ܦܬܚܬ ܥܝܢܝܗ ܘܟܕ ܚܙܬܗ ܠܫܡܥܘܢ ܝܬܒܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 41, 'ܘܐܘܫܛ ܠܗ ܐܝܕܗ ܘܐܩܝܡܗ ܘܩܪܐ ܠܩܕܝܫܐ ܘܠܐܪܡܠܬܐ ܘܝܗܒܗ ܠܗܘܢ ܟܕ ܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 42, 'ܘܐܬܝܕܥܬ ܗܕܐ ܒܟܠܗ ܡܕܝܢܬܐ ܘܤܓܝܐܐ ܗܝܡܢܘ ܒܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 43, 'ܗܘܐ ܕܝܢ ܒܗ ܒܝܘܦܐ ܝܘܡܬܐ ܠܐ ܙܥܘܪܝܢ ܟܕ ܫܪܐ ܗܘܐ ܒܝܬ ܫܡܥܘܢ ܒܘܪܤܝܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 1, 'ܒܩܤܪܝܐ ܕܝܢ ܐܝܬ ܗܘܐ ܓܒܪܐ ܚܕ ܩܢܛܪܘܢܐ ܕܫܡܗ ܗܘܐ ܩܘܪܢܠܝܘܤ ܡܢ ܤܦܝܪܐ ܗܝ ܕܡܬܩܪܝܐ ܐܝܛܠܝܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 2, 'ܘܙܕܝܩ ܗܘܐ ܘܕܚܠ ܗܘܐ ܡܢ ܐܠܗܐ ܗܘ ܘܒܝܬܗ ܟܠܗ ܘܥܒܕ ܗܘܐ ܙܕܩܬܐ ܤܓܝܐܬܐ ܒܥܡܐ ܘܒܟܠܙܒܢ ܒܥܐ ܗܘܐ ܡܢ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 3, 'ܗܢܐ ܚܙܐ ܡܠܐܟܐ ܕܐܠܗܐ ܒܚܙܘܐ ܓܠܝܐܝܬ ܠܐܦܝ ܬܫܥ ܫܥܝܢ ܒܐܝܡܡܐ ܕܥܠ ܠܘܬܗ ܘܐܡܪ ܠܗ ܩܘܪܢܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 4, 'ܘܗܘ ܚܪ ܒܗ ܘܕܚܠ ܘܐܡܪ ܡܢܐ ܡܪܝ ܘܐܡܪ ܠܗ ܡܠܐܟܐ ܨܠܘܬܟ ܘܙܕܩܬܟ ܤܠܩ ܠܕܘܟܪܢܐ ܩܕܡ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 5, 'ܘܗܫܐ ܫܕܪ ܓܒܪܐ ܠܝܘܦܐ ܡܕܝܢܬܐ ܘܐܝܬܐ ܠܫܡܥܘܢ ܕܡܬܩܪܐ ܟܐܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 6, 'ܗܐ ܫܪܐ ܒܒܝܬܗ ܕܫܡܥܘܢ ܒܘܪܤܝܐ ܗܘ ܕܐܝܬܘܗܝ ܥܠ ܝܕ ܝܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 7, 'ܘܟܕ ܐܙܠ ܠܗ ܡܠܐܟܐ ܕܡܠܠ ܥܡܗ ܩܪܐ ܬܪܝܢ ܡܢ ܒܢܝ ܒܝܬܗ ܘܦܠܚܐ ܚܕ ܕܕܚܠ ܠܐܠܗܐ ܐܝܢܐ ܕܡܬܕܢܐ ܗܘܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 8, 'ܘܐܫܬܥܝ ܠܗܘܢ ܟܠܡܕܡ ܕܚܙܐ ܘܫܕܪ ܐܢܘܢ ܠܝܘܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 9, 'ܘܠܝܘܡܐ ܐܚܪܢܐ ܟܕ ܗܢܘܢ ܪܕܝܢ ܒܐܘܪܚܐ ܘܩܪܝܒܝܢ ܠܡܕܝܢܬܐ ܤܠܩ ܫܡܥܘܢ ܠܐܓܪܐ ܕܢܨܠܐ ܒܫܬ ܫܥܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 10, 'ܘܟܦܢ ܘܨܒܐ ܗܘܐ ܕܢܠܥܤ ܘܟܕ ܗܢܘܢ ܡܬܩܢܝܢ ܠܗ ܢܦܠ ܥܠܘܗܝ ܬܡܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 11, 'ܘܚܙܐ ܫܡܝܐ ܟܕ ܦܬܝܚܝܢ ܘܡܐܢܐ ܚܕ ܟܕ ܐܤܝܪ ܒܐܪܒܥ ܩܪܢܢ ܘܕܡܐ ܗܘܐ ܠܟܬܢܐ ܪܒܐ ܘܫܐܒ ܗܘܐ ܡܢ ܫܡܝܐ ܥܠ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 12, 'ܘܐܝܬ ܗܘܐ ܒܗ ܟܠܗܝܢ ܚܝܘܬܐ ܕܐܪܒܥܬ ܪܓܠܐ ܘܪܚܫܐ ܕܐܪܥܐ ܘܦܪܚܬܐ ܕܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 13, 'ܘܩܠܐ ܐܬܐ ܠܗ ܕܐܡܪ ܫܡܥܘܢ ܩܘܡ ܟܘܤ ܘܐܟܘܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 14, 'ܘܫܡܥܘܢ ܐܡܪ ܚܤ ܡܪܝ ܕܡܡܬܘܡ ܠܐ ܐܟܠܬ ܟܠ ܕܡܤܝܒ ܘܛܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 15, 'ܘܬܘܒ ܕܬܪܬܝܢ ܙܒܢܝܢ ܩܠܐ ܗܘܐ ܠܘܬܗ ܐܝܠܝܢ ܕܐܠܗܐ ܕܟܝ ܐܢܬ ܠܐ ܬܤܝܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 16, 'ܗܕܐ ܕܝܢ ܗܘܬ ܬܠܬ ܙܒܢܝܢ ܘܐܬܥܠܝ ܠܗ ܡܐܢܐ ܠܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 17, 'ܘܟܕ ܡܬܕܡܪ ܫܡܥܘܢ ܒܢܦܫܗ ܕܡܢܘ ܚܙܘܐ ܕܚܙܐ ܡܛܝܘ ܓܒܪܐ ܗܢܘܢ ܕܐܫܬܕܪܘ ܗܘܘ ܡܢ ܩܘܪܢܠܝܘܤ ܘܫܐܠܘ ܥܠ ܒܝܬܐ ܕܫܪܐ ܒܗ ܫܡܥܘܢ ܘܐܬܘ ܘܩܡܘ ܥܠ ܬܪܥܐ ܕܕܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 18, 'ܘܩܪܝܢ ܗܘܘ ܬܡܢ ܘܡܫܐܠܝܢ ܕܐܢ ܫܡܥܘܢ ܕܡܬܩܪܐ ܟܐܦܐ ܗܪܟܐ ܫܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 19, 'ܘܟܕ ܫܡܥܘܢ ܪܢܐ ܗܘܐ ܒܚܙܘܐ ܐܡܪ ܠܗ ܪܘܚܐ ܗܐ ܓܒܪܐ ܬܠܬܐ ܒܥܝܢ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 20, 'ܩܘܡ ܚܘܬ ܘܙܠ ܥܡܗܘܢ ܟܕ ܠܐ ܡܬܦܠܓ ܪܥܝܢܟ ܡܛܠ ܕܐܢܐ ܗܘ ܫܕܪܬ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 21, 'ܗܝܕܝܢ ܢܚܬ ܫܡܥܘܢ ܠܘܬ ܓܒܪܐ ܗܢܘܢ ܘܐܡܪ ܠܗܘܢ ܐܢܐ ܗܘ ܕܒܥܝܢ ܐܢܬܘܢ ܐܝܕܐ ܗܝ ܥܠܬܐ ܕܡܛܠܬܗ ܐܬܝܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 22, 'ܐܡܪܝܢ ܠܗ ܓܒܪܐ ܚܕ ܕܫܡܗ ܩܘܪܢܠܝܘܤ ܩܢܛܪܘܢܐ ܟܐܢܐ ܕܕܚܠ ܡܢ ܐܠܗܐ ܘܡܤܗܕ ܥܠܘܗܝ ܟܠܗ ܥܡܐ ܕܝܗܘܕܝܐ ܐܬܐܡܪ ܠܗ ܒܚܙܘܐ ܡܢ ܡܠܐܟܐ ܩܕܝܫܐ ܕܢܫܕܪ ܢܥܠܟ ܠܒܝܬܗ ܘܢܫܡܥ ܡܠܬܐ ܡܢܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 23, 'ܘܐܥܠ ܐܢܘܢ ܫܡܥܘܢ ܘܩܒܠ ܐܢܘܢ ܟܪ ܕܫܪܐ ܗܘܐ ܘܩܡ ܠܒܬܪܗ ܕܝܘܡܐ ܘܢܦܩ ܐܙܠ ܥܡܗܘܢ ܘܐܙܠܘ ܥܡܗ ܐܢܫ ܐܢܫ ܡܢ ܐܚܐ ܕܝܘܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 24, 'ܘܠܝܘܡܐ ܐܚܪܢܐ ܥܠ ܠܩܤܪܝܐ ܩܘܪܢܠܝܘܤ ܕܝܢ ܡܩܘܐ ܗܘܐ ܠܗܘܢ ܟܕ ܡܟܢܫܝܢ ܠܗ ܟܠܗܘܢ ܒܢܝ ܛܘܗܡܗ ܘܐܦ ܪܚܡܐ ܚܒܝܒܐ ܕܐܝܬ ܗܘܘ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 25, 'ܘܟܕ ܥܐܠ ܫܡܥܘܢ ܐܪܥܗ ܩܘܪܢܠܝܘܤ ܘܢܦܠ ܤܓܕ ܠܪܓܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 26, 'ܘܫܡܥܘܢ ܐܩܝܡܗ ܘܐܡܪ ܠܗ ܩܘܡ ܠܟ ܘܐܦ ܐܢܐ ܒܪܢܫܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 27, 'ܘܟܕ ܡܡܠܠ ܥܡܗ ܥܠ ܘܐܫܟܚ ܤܓܝܐܐ ܕܐܬܘ ܗܘܘ ܠܬܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 28, 'ܘܐܡܪ ܠܗܘܢ ܐܢܬܘܢ ܝܕܥܝܢ ܐܢܬܘܢ ܕܠܐ ܡܦܤ ܠܓܒܪܐ ܝܗܘܕܝܐ ܕܢܩܦ ܠܐܢܫܐ ܢܘܟܪܝܐ ܕܠܐ ܗܘܐ ܒܪ ܫܪܒܬܗ ܘܠܝ ܐܠܗܐ ܚܘܝܢܝ ܕܠܐ ܐܡܪ ܥܠ ܐܢܫ ܕܛܡܐ ܐܘ ܡܤܝܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 29, 'ܡܛܠ ܗܢܐ ܥܬܝܕܐܝܬ ܐܬܝܬ ܟܕ ܫܕܪܬܘܢ ܒܬܪܝ ܒܪܡ ܡܫܐܠ ܐܢܐ ܠܟܘܢ ܡܛܠ ܡܢܐ ܫܕܪܬܘܢ ܒܬܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 30, 'ܐܡܪ ܠܗ ܩܘܪܢܠܝܘܤ ܐܪܒܥܐ ܝܘܡܝܢ ܐܝܬ ܥܕܡܐ ܠܗܫܐ ܗܐ ܡܢ ܕܨܐܡ ܐܢܐ ܘܒܬܫܥ ܫܥܝܢ ܟܕ ܡܨܠܐ ܐܢܐ ܒܒܝܬܝ ܩܡ ܓܒܪܐ ܚܕ ܩܕܡܝ ܟܕ ܠܒܝܫ ܚܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 31, 'ܘܐܡܪ ܠܝ ܩܘܪܢܠܝܐ ܐܫܬܡܥܬ ܨܠܘܬܟ ܘܠܙܕܩܬܟ ܕܘܟܪܢܐ ܗܘܐ ܩܕܡ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 32, 'ܒܪܡ ܫܕܪ ܠܝܘܦܐ ܡܕܝܢܬܐ ܘܐܝܬܐ ܠܫܡܥܘܢ ܕܡܬܩܪܐ ܟܐܦܐ ܗܐ ܫܪܐ ܒܒܝܬܗ ܕܫܡܥܘܢ ܒܘܪܤܝܐ ܕܥܠ ܝܕ ܝܡܐ ܘܗܘ ܢܐܬܐ ܢܡܠܠ ܥܡܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 33, 'ܘܒܪ ܫܥܬܗ ܫܕܪܬ ܠܘܬܟ ܘܐܢܬ ܫܦܝܪ ܥܒܕܬ ܕܐܬܝܬ ܘܗܐ ܚܢܢ ܟܠܢ ܩܕܡܝܟ ܘܨܒܝܢܢ ܕܢܫܡܥ ܟܠ ܡܐ ܕܐܬܦܩܕ ܠܟ ܡܢ ܠܘܬ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 34, 'ܦܬܚ ܕܝܢ ܫܡܥܘܢ ܦܘܡܗ ܘܐܡܪ ܒܫܪܪܐ ܐܕܪܟܬ ܕܐܠܗܐ ܠܐ ܗܘܐ ܢܤܒ ܒܐܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 35, 'ܐܠܐ ܒܟܠܗܘܢ ܥܡܡܐ ܐܝܢܐ ܕܕܚܠ ܡܢܗ ܘܦܠܚ ܟܐܢܘܬܐ ܡܩܒܠ ܗܘ ܠܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 36, 'ܡܠܬܐ ܓܝܪ ܕܫܕܪ ܠܒܢܝ ܐܝܤܪܝܠ ܘܤܒܪ ܐܢܘܢ ܫܠܡܐ ܘܫܝܢܐ ܒܝܕ ܝܫܘܥ ܡܫܝܚܐ ܗܢܘ ܡܪܝܐ ܕܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 37, 'ܘܐܦ ܐܢܬܘܢ ܝܕܥܝܢ ܐܢܬܘܢ ܒܡܠܬܐ ܕܗܘܬ ܒܟܠܗ ܝܗܘܕ ܕܐܩܦܬ ܡܢ ܓܠܝܠܐ ܒܬܪ ܡܥܡܘܕܝܬܐ ܕܐܟܪܙ ܝܘܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 38, 'ܥܠ ܝܫܘܥ ܕܡܢ ܢܨܪܬ ܕܐܠܗܐ ܡܫܚܗ ܒܪܘܚܐ ܕܩܘܕܫܐ ܘܒܚܝܠܐ ܘܗܘܝܘ ܕܡܬܟܪܟ ܗܘܐ ܘܡܐܤܐ ܠܗܢܘܢ ܕܐܬܢܟܝܘ ܡܢ ܒܝܫܐ ܡܛܠ ܕܐܠܗܐ ܗܘܐ ܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 39, 'ܘܚܢܢ ܤܗܕܘܗܝ ܥܠ ܟܠ ܡܐ ܕܥܒܕ ܒܟܠܗ ܐܪܥܐ ܕܝܗܘܕ ܘܕܐܘܪܫܠܡ ܠܗ ܠܗܢܐ ܬܠܐܘܗܝ ܝܗܘܕܝܐ ܥܠ ܩܝܤܐ ܘܩܛܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 40, 'ܘܠܗ ܐܩܝܡ ܐܠܗܐ ܠܬܠܬܐ ܝܘܡܝܢ ܘܝܗܒܗ ܕܢܬܚܙܐ ܥܝܢ ܒܓܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 41, 'ܠܐ ܕܝܢ ܠܟܠܗ ܥܡܐ ܐܠܐ ܠܢ ܐܝܠܝܢ ܕܡܢ ܐܠܗܐ ܐܬܓܒܝܢ ܕܢܗܘܐ ܠܗ ܤܗܕܐ ܕܐܟܠܢ ܥܡܗ ܘܐܫܬܝܢ ܡܢ ܒܬܪ ܩܝܡܬܗ ܕܡܢ ܒܝܬ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 42, 'ܘܦܩܕܢ ܕܢܟܪܙ ܘܢܤܗܕ ܠܥܡܐ ܕܗܢܘ ܕܐܬܦܪܫ ܡܢ ܐܠܗܐ ܕܝܢܐ ܕܚܝܐ ܘܕܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 43, 'ܘܥܠܘܗܝ ܐܤܗܕܘ ܟܠܗܘܢ ܢܒܝܐ ܕܟܠܡܢ ܕܡܗܝܡܢ ܒܫܡܗ ܢܩܒܠ ܫܘܒܩܢ ܚܛܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 44, 'ܘܟܕ ܗܘ ܫܡܥܘܢ ܡܡܠܠ ܗܘܐ ܗܠܝܢ ܡܠܐ ܐܓܢܬ ܪܘܚܐ ܕܩܘܕܫܐ ܥܠ ܟܠܗܘܢ ܕܫܡܥܝܢ ܗܘܘ ܡܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 45, 'ܘܬܡܗܘ ܘܬܗܪܘ ܐܚܐ ܓܙܝܪܐ ܗܢܘܢ ܕܐܬܘ ܥܡܗ ܕܐܦ ܥܠ ܥܡܡܐ ܡܘܗܒܬܐ ܕܪܘܚܐ ܕܩܘܕܫܐ ܐܫܬܦܥܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 46, 'ܫܡܥܝܢ ܗܘܘ ܠܗܘܢ ܓܝܪ ܟܕ ܡܡܠܠܝܢ ܒܠܫܢ ܠܫܢ ܘܡܘܪܒܝܢ ܗܘܘ ܠܐܠܗܐ ܘܐܡܪ ܗܘܐ ܫܡܥܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 47, 'ܕܠܡܐ ܡܝܐ ܡܫܟܚ ܐܢܫ ܟܠܐ ܕܠܐ ܢܥܡܕܘܢ ܗܢܘܢ ܕܗܐ ܩܒܠܘ ܪܘܚܐ ܕܩܘܕܫܐ ܐܝܟ ܡܐ ܕܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 48, 'ܗܝܕܝܢ ܦܩܕ ܠܗܘܢ ܕܢܥܡܕܘܢ ܒܫܡܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܘܒܥܘ ܡܢܗ ܐܝܟ ܕܢܩܘܐ ܠܘܬܗܘܢ ܝܘܡܬܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 1, 'ܘܐܫܬܡܥܬ ܗܘܬ ܠܫܠܝܚܐ ܘܠܐܚܐ ܕܒܝܗܘܕ ܕܐܦ ܥܡܡܐ ܩܒܠܘ ܡܠܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 2, 'ܘܟܕ ܤܠܩ ܫܡܥܘܢ ܠܐܘܪܫܠܡ ܕܝܢܝܢ ܗܘܘ ܥܡܗ ܗܢܘܢ ܕܡܢ ܓܙܘܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 3, 'ܟܕ ܐܡܪܝܢ ܕܠܘܬ ܐܢܫܐ ܥܘܪܠܐ ܥܠ ܘܠܥܤ ܥܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 4, 'ܘܐܩܦ ܗܘܐ ܫܡܥܘܢ ܒܬܪ ܒܬܪ ܠܡܐܡܪ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 5, 'ܕܟܕ ܡܨܠܐ ܗܘܝܬ ܒܝܘܦܐ ܚܙܝܬ ܒܚܙܘܐ ܕܢܚܬ ܗܘܐ ܡܐܢܐ ܚܕ ܐܝܢܐ ܕܕܡܐ ܗܘܐ ܠܟܬܢܐ ܘܐܤܝܪ ܗܘܐ ܒܐܪܒܥ ܩܪܢܬܗ ܘܫܐܒ ܗܘܐ ܡܢ ܫܡܝܐ ܘܐܬܐ ܥܕܡܐ ܠܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 6, 'ܘܚܪܬ ܒܗ ܘܚܙܐ ܗܘܝܬ ܕܐܝܬ ܒܗ ܚܝܘܬܐ ܕܐܪܒܥܬ ܪܓܠܝܗܝܢ ܘܪܚܫܐ ܕܐܪܥܐ ܘܐܦ ܦܪܚܬܐ ܕܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 7, 'ܘܫܡܥܬ ܗܘܝܬ ܩܠܐ ܕܐܡܪ ܗܘܐ ܠܝ ܫܡܥܘܢ ܩܘܡ ܟܘܤ ܘܐܟܘܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 8, 'ܘܐܡܪܬ ܚܤ ܡܪܝ ܕܡܡܬܘܡ ܠܐ ܥܠ ܠܦܘܡܝ ܕܛܡܐ ܘܕܡܤܝܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 9, 'ܘܬܘܒ ܩܠܐ ܐܡܪ ܠܝ ܡܢ ܫܡܝܐ ܕܡܕܡ ܕܐܠܗܐ ܕܟܝ ܐܢܬ ܠܐ ܬܤܝܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 10, 'ܗܕܐ ܗܘܬ ܬܠܬ ܙܒܢܝܢ ܘܐܤܬܠܩ ܠܗ ܟܠܡܕܡ ܠܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 11, 'ܘܒܗ ܒܫܥܬܐ ܬܠܬܐ ܓܒܪܝܢ ܕܐܫܬܕܪܘ ܠܘܬܝ ܡܢ ܩܘܪܢܠܝܘܤ ܡܢ ܩܤܪܝܐ ܐܬܘ ܘܩܡܘ ܥܠ ܬܪܥܐ ܕܕܪܬܐ ܕܫܪܐ ܗܘܝܬ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 12, 'ܘܐܡܪ ܠܝ ܪܘܚܐ ܕܙܠ ܥܡܗܘܢ ܕܠܐ ܦܘܠܓܐ ܘܐܬܘ ܥܡܝ ܐܦ ܗܠܝܢ ܫܬܐ ܐܚܝܢ ܘܥܠܢ ܠܒܝܬܗ ܕܓܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 13, 'ܘܐܫܬܥܝ ܠܢ ܐܝܟܢܐ ܚܙܐ ܒܒܝܬܗ ܡܠܐܟܐ ܕܩܡ ܘܐܡܪ ܠܗ ܕܫܕܪ ܠܝܘܦܐ ܡܕܝܢܬܐ ܘܐܝܬܐ ܠܫܡܥܘܢ ܕܡܬܩܪܐ ܟܐܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 14, 'ܘܗܘ ܢܡܠܠ ܥܡܟ ܡܠܐ ܕܒܗܝܢ ܬܚܐ ܐܢܬ ܘܟܠܗ ܒܝܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 15, 'ܘܟܕ ܐܩܦܬ ܗܘܝܬ ܬܡܢ ܠܡܡܠܠܘ ܐܓܢܬ ܪܘܚܐ ܕܩܘܕܫܐ ܥܠܝܗܘܢ ܐܝܟ ܡܐ ܕܥܠܝܢ ܡܢ ܩܕܝܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 16, 'ܘܐܬܕܟܪܬ ܡܠܬܗ ܕܡܪܢ ܕܐܡܪ ܗܘܐ ܕܝܘܚܢܢ ܐܥܡܕ ܒܡܝܐ ܐܢܬܘܢ ܕܝܢ ܬܥܡܕܘܢ ܒܪܘܚܐ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 17, 'ܐܢ ܗܟܝܠ ܐܠܗܐ ܫܘܝܐܝܬ ܝܗܒܗ ܡܘܗܒܬܐ ܠܥܡܡܐ ܐܝܠܝܢ ܕܗܝܡܢܘ ܒܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܐܝܟ ܕܐܦ ܠܢ ܐܢܐ ܡܢ ܗܘܝܬ ܕܐܤܦܩ ܗܘܝܬ ܕܐܟܠܐ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 18, 'ܘܟܕ ܗܠܝܢ ܡܠܐ ܫܡܥܘ ܫܠܝܘ ܠܗܘܢ ܘܫܒܚܘ ܠܐܠܗܐ ܘܐܡܪܝܢ ܗܘܘ ܕܟܒܪ ܐܦ ܠܥܡܡܐ ܐܠܗܐ ܝܗܒ ܬܝܒܘܬܐ ܠܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 19, 'ܗܢܘܢ ܕܝܢ ܕܐܬܒܕܪܘ ܗܘܘ ܡܢ ܐܘܠܨܢܐ ܕܗܘܐ ܗܘܐ ܥܠ ܐܤܛܦܢܘܤ ܡܛܝܘ ܗܘܘ ܥܕܡܐ ܠܦܘܢܝܩܐ ܘܐܦ ܠܐܬܪܐ ܕܩܘܦܪܘܤ ܘܠܐܢܛܝܟܝܐ ܟܕ ܥܡ ܐܢܫ ܠܐ ܡܡܠܠܝܢ ܗܘܘ ܡܠܬܐ ܐܠܐ ܒܠܚܘܕ ܥܡ ܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 20, 'ܐܝܬ ܗܘܘ ܕܝܢ ܐܢܫܐ ܡܢܗܘܢ ܡܢ ܩܘܦܪܘܤ ܘܡܢ ܩܘܪܝܢܐ ܗܠܝܢ ܥܠܘ ܗܘܘ ܠܐܢܛܝܘܟܝ ܘܡܡܠܠܝܢ ܗܘܘ ܥܡ ܝܘܢܝܐ ܘܡܤܒܪܝܢ ܗܘܘ ܥܠ ܡܪܢ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 21, 'ܘܐܝܬ ܗܘܬ ܥܡܗܘܢ ܐܝܕܗ ܕܡܪܝܐ ܘܤܓܝܐܐ ܗܝܡܢܘ ܘܐܬܦܢܝܘ ܠܘܬ ܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 22, 'ܘܐܫܬܡܥܬ ܗܘܬ ܗܝ ܗܕܐ ܠܐܕܢܝܗܘܢ ܕܒܢܝ ܥܕܬܐ ܕܒܐܘܪܫܠܡ ܘܫܕܪܘ ܠܒܪܢܒܐ ܠܐܢܛܝܘܟܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 23, 'ܘܟܕ ܐܬܐ ܠܬܡܢ ܘܚܙܐ ܛܝܒܘܬܗ ܕܐܠܗܐ ܚܕܝ ܘܒܥܐ ܗܘܐ ܡܢܗܘܢ ܕܒܟܠܗ ܠܒܗܘܢ ܢܗܘܘܢ ܢܩܝܦܝܢ ܠܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 24, 'ܡܛܠ ܕܓܒܪܐ ܗܘܐ ܛܒܐ ܘܡܫܡܠܝ ܗܘܐ ܒܪܘܚܐ ܕܩܘܕܫܐ ܘܒܗܝܡܢܘܬܐ ܘܐܬܬܘܤܦ ܗܘܐ ܥܡܐ ܤܓܝܐܐ ܠܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 25, 'ܘܗܘ ܢܦܩ ܗܘܐ ܠܛܪܤܘܤ ܠܡܒܥܐ ܠܫܐܘܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 26, 'ܘܟܕ ܐܫܟܚܗ ܐܝܬܝܗ ܥܡܗ ܠܐܢܛܝܟܝܐ ܘܫܢܬܐ ܟܠܗ ܐܟܚܕܐ ܟܢܝܫܝܢ ܗܘܘ ܒܥܕܬܐ ܘܐܠܦܘ ܥܡܐ ܤܓܝܐܐ ܡܢ ܗܝܕܝܢ ܩܕܡܝܬ ܐܬܩܪܝܘ ܒܐܢܛܝܘܟܝ ܬܠܡܝܕܐ ܟܪܤܛܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 27, 'ܘܒܝܘܡܬܐ ܗܢܘܢ ܐܬܘ ܡܢ ܐܘܪܫܠܡ ܠܬܡܢ ܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 28, 'ܘܩܡ ܚܕ ܡܢܗܘܢ ܕܫܡܗ ܗܘܐ ܐܓܒܘܤ ܘܐܘܕܥ ܐܢܘܢ ܒܪܘܚ ܕܟܦܢܐ ܪܒܐ ܗܘܐ ܒܟܠܗ ܐܪܥܐ ܘܗܘܐ ܟܦܢܐ ܗܢܐ ܒܝܘܡܝ ܩܠܘܕܝܘܤ ܩܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 29, 'ܒܪܡ ܕܝܢ ܬܠܡܝܕܐ ܐܝܟ ܡܐ ܕܐܝܬ ܗܘܐ ܠܐܢܫ ܐܢܫ ܡܢܗܘܢ ܦܪܫܘ ܕܢܫܕܪܘܢ ܠܬܫܡܫܬܐ ܕܐܚܐ ܐܝܠܝܢ ܕܥܡܪܝܢ ܒܝܗܘܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 30, 'ܘܫܕܪܘ ܒܝܕ ܒܪܢܒܐ ܘܫܐܘܠ ܠܩܫܝܫܐ ܕܬܡܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 1, 'ܒܗܘ ܕܝܢ ܙܒܢܐ ܐܪܡܝ ܗܘܐ ܐܝܕܝܐ ܥܠ ܐܢܫܝܢ ܕܒܥܕܬܐ ܐܝܟ ܕܢܒܐܫ ܠܗܘܢ ܗܪܘܕܤ ܡܠܟܐ ܗܘ ܕܡܬܟܢܐ ܗܘܐ ܐܓܪܦܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 2, 'ܘܩܛܠ ܒܤܝܦܐ ܠܝܥܩܘܒ ܐܚܘܗܝ ܕܝܘܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 3, 'ܘܟܕ ܚܙܐ ܕܫܦܪܬ ܗܕܐ ܠܝܗܘܕܝܐ ܐܘܤܦ ܗܘܐ ܠܡܐܚܕ ܐܦ ܠܫܡܥܘܢ ܟܐܦܐ ܘܐܝܬܝܗܘܢ ܗܘܘ ܝܘܡܬܐ ܕܦܛܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 4, 'ܘܐܚܕܗ ܘܐܪܡܝܗ ܒܝܬ ܐܤܝܪܐ ܘܐܫܠܡ ܠܗ ܫܬܬܥܤܪ ܤܛܪܛܘܛܝܢ ܕܢܛܪܘܢܗ ܕܡܢ ܒܬܪ ܦܨܚܐ ܢܫܠܡܝܘܗܝ ܠܥܡܐ ܕܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 5, 'ܘܟܕ ܗܘ ܫܡܥܘܢ ܡܬܢܛܪ ܗܘܐ ܒܝܬ ܐܤܝܪܐ ܨܠܘܬܐ ܐܡܝܢܬܐ ܡܬܩܪܒܐ ܗܘܬ ܡܢ ܥܕܬܐ ܚܠܦܘܗܝ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 6, 'ܘܒܗ ܒܗܘ ܠܠܝܐ ܕܠܨܦܪܐ ܥܬܝܕ ܗܘܐ ܕܢܫܠܡܝܘܗܝ ܟܕ ܕܡܟ ܗܘܐ ܫܡܥܘܢ ܒܝܬ ܬܪܝܢ ܐܤܛܪܛܝܘܛܝܢ ܘܐܤܝܪ ܗܘܐ ܒܬܪܬܝܢ ܫܫܠܢ ܘܐܚܪܢܐ ܢܛܪܝܢ ܗܘܘ ܬܪܥܐ ܕܒܝܬ ܐܤܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 7, 'ܡܠܐܟܐ ܕܡܪܝܐ ܩܡ ܠܥܠ ܡܢܗ ܘܢܘܗܪܐ ܐܙܠܓ ܒܟܠܗ ܒܝܬܐ ܘܕܩܪܗ ܒܓܒܗ ܘܐܩܝܡܗ ܘܐܡܪ ܠܗ ܩܘܡ ܥܓܠ ܘܢܦܠ ܫܫܠܬܐ ܡܢ ܐܝܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 8, 'ܘܐܡܪ ܠܗ ܡܠܐܟܐ ܐܤܘܪ ܚܨܝܟ ܘܤܐܢ ܛܠܪܝܟ ܘܥܒܕ ܗܟܢܐ ܘܬܘܒ ܐܡܪ ܠܗ ܐܬܥܛܦ ܬܟܤܝܬܟ ܘܬܐ ܒܬܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 9, 'ܘܢܦܩ ܘܐܙܠ ܗܘܐ ܒܬܪܗ ܟܕ ܠܐ ܝܕܥ ܗܘܐ ܕܫܪܝܪܐ ܗܘܬ ܗܝ ܕܗܘܝܐ ܗܘܬ ܒܝܕ ܡܠܐܟܐ ܤܒܪ ܗܘܐ ܓܝܪ ܕܚܙܘܐ ܚܙܐ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 10, 'ܘܟܕ ܥܒܪܘ ܡܛܪܬܐ ܩܕܡܝܬܐ ܘܕܬܪܬܝܢ ܐܬܘ ܥܕܡܐ ܠܬܪܥܐ ܕܦܪܙܠܐ ܘܐܬܦܬܚ ܠܗܘܢ ܡܢ ܨܒܘܬ ܢܦܫܗ ܘܟܕ ܢܦܩܘ ܘܥܒܪܘ ܫܘܩܐ ܚܕ ܦܪܩ ܡܢ ܠܘܬܗ ܡܠܐܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 11, 'ܗܝܕܝܢ ܐܫܬܘܕܥ ܫܡܥܘܢ ܘܐܡܪ ܗܫܐ ܝܕܥܬ ܒܩܘܫܬܐ ܕܡܪܝܐ ܫܕܪ ܡܠܐܟܗ ܘܦܠܛܢܝ ܡܢ ܐܝܕܗ ܕܗܪܘܕܤ ܡܠܟܐ ܘܡܢ ܡܕܡ ܕܡܬܚܫܒܝܢ ܗܘܘ ܥܠܝ ܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 12, 'ܘܟܕ ܐܤܬܟܠ ܐܬܐ ܠܗ ܠܒܝܬܐ ܕܡܪܝܡ ܐܡܗ ܕܝܘܚܢܢ ܗܘ ܕܐܬܟܢܝ ܡܪܩܘܤ ܡܛܠ ܕܐܚܐ ܤܓܝܐܐ ܬܡܢ ܟܢܝܫܝܢ ܗܘܘ ܘܡܨܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 13, 'ܘܢܩܫ ܒܬܪܥܐ ܕܕܪܬܐ ܘܢܦܩܬ ܕܬܥܢܝܘܗܝ ܛܠܝܬܐ ܕܫܡܗ ܪܘܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 14, 'ܘܐܫܬܘܕܥܬ ܩܠܗ ܕܫܡܥܘܢ ܘܒܚܕܘܬܗ ܠܐ ܦܬܚܬ ܠܗ ܬܪܥܐ ܐܠܐ ܗܦܟܬ ܒܪܗܛܐ ܘܐܡܪܐ ܠܗܘܢ ܫܡܥܘܢ ܗܐ ܩܐܡ ܥܠ ܬܪܥܐ ܕܕܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 15, 'ܐܡܪܝܢ ܠܗ ܡܙܥ ܙܥܬܝ ܠܟܝ ܘܗܝ ܡܬܚܪܝܐ ܗܘܬ ܕܗܕܐ ܗܟܢܐ ܗܝ ܐܡܪܝܢ ܠܗ ܟܒܪ ܡܠܐܟܗ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 16, 'ܘܫܡܥܘܢ ܢܩܫ ܗܘܐ ܒܬܪܥܐ ܘܢܦܩܘ ܚܙܐܘܗܝ ܘܬܡܗܘ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 17, 'ܘܡܢܝܦ ܗܘܐ ܠܗܘܢ ܐܝܕܗ ܐܝܟ ܕܢܫܬܩܘܢ ܠܗܘܢ ܘܥܠ ܘܐܫܬܥܝ ܠܗܘܢ ܐܝܟܢܐ ܡܪܝܐ ܐܦܩܗ ܡܢ ܒܝܬ ܐܤܝܪܐ ܘܐܡܪ ܠܗܘܢ ܐܫܬܥܘ ܗܠܝܢ ܠܝܥܩܘܒ ܘܠܐܚܝܢ ܘܢܦܩ ܐܙܠ ܠܗ ܠܐܬܪ ܐܚܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 18, 'ܘܟܕ ܗܘܐ ܨܦܪܐ ܗܘܐ ܗܘܐ ܪܘܒܐ ܤܓܝܐܐ ܒܝܬ ܐܤܛܪܛܝܘܛܐ ܥܠ ܫܡܥܘܢ ܕܡܢܐ ܗܘܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 19, 'ܗܪܘܕܤ ܕܝܢ ܟܕ ܒܥܝܗܝ ܘܠܐ ܐܫܟܚܗ ܕܢ ܐܢܘܢ ܠܢܛܘܪܐ ܘܦܩܕ ܕܢܡܘܬܘܢ ܘܢܦܩ ܠܗ ܡܢ ܝܗܘܕ ܘܐܝܬܘܗܝ ܗܘܐ ܒܩܤܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 20, 'ܘܡܛܠ ܕܪܓܝܙ ܗܘܐ ܥܠ ܨܘܪܝܐ ܘܥܠ ܨܝܕܢܝܐ ܐܬܟܢܫܘ ܘܐܬܘ ܠܘܬܗ ܐܟܚܕܐ ܘܐܦܝܤܘ ܠܒܠܤܛܘܤ ܩܝܛܘܢܩܢܗ ܕܡܠܟܐ ܘܫܐܠܘ ܡܢܗ ܕܢܗܘܐ ܠܗܘܢ ܫܝܢܐ ܡܛܠ ܕܦܘܪܢܤܐ ܕܐܬܪܗܘܢ ܡܢ ܡܠܟܘܬܗ ܗܘܐ ܕܗܪܘܕܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 21, 'ܒܝܘܡܐ ܕܝܢ ܝܕܝܥܐ ܠܒܫ ܗܘܐ ܗܪܘܕܤ ܠܒܘܫܐ ܕܡܠܟܘܬܐ ܘܝܬܒ ܥܠ ܒܝܡ ܘܡܡܠܠ ܗܘܐ ܥܡ ܟܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 22, 'ܥܡܐ ܕܝܢ ܟܠܗ ܩܥܐ ܗܘܐ ܘܐܡܪ ܗܠܝܢ ܒܢܬ ܩܠܐ ܕܐܠܗܐ ܐܢܝܢ ܘܠܐ ܗܘܝ ܕܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 23, 'ܘܚܠܦ ܕܠܐ ܝܗܒ ܬܫܒܘܚܬܐ ܠܐܠܗܐ ܒܗ ܒܫܥܬܐ ܡܚܝܗܝ ܡܠܐܟܗ ܕܡܪܝܐ ܘܪܦܬ ܒܬܘܠܥܐ ܘܡܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 24, 'ܘܤܒܪܬܐ ܕܐܠܗܐ ܡܬܟܪܙܐ ܗܘܬ ܘܪܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 25, 'ܒܪܢܒܐ ܕܝܢ ܘܫܐܘܠ ܦܢܘ ܡܢ ܐܘܪܫܠܡ ܠܐܢܛܝܘܟܝ ܡܢ ܒܬܪ ܕܫܠܡܘ ܬܫܡܫܬܗܘܢ ܘܕܒܪܘ ܥܡܗܘܢ ܠܝܘܚܢܢ ܗܘ ܕܐܬܟܢܝ ܡܪܩܘܤ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 1, 'ܐܝܬ ܗܘܐ ܕܝܢ ܒܥܕܬܐ ܕܐܢܛܝܟܝܐ ܢܒܝܐ ܘܡܠܦܢܐ ܒܪܢܒܐ ܘܫܡܥܘܢ ܕܡܬܩܪܐ ܢܝܓܪ ܘܠܘܩܝܤ ܕܡܢ ܩܘܪܝܢܐ ܡܕܝܢܬܐ ܘܡܢܐܝܠ ܒܪ ܡܪܒܝܢܘܗܝ ܕܗܪܘܕܤ ܛܛܪܪܟܐ ܘܫܐܘܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 2, 'ܘܟܕ ܗܢܘܢ ܨܝܡܝܢ ܗܘܘ ܘܡܬܟܫܦܝܢ ܠܐܠܗܐ ܐܡܪܬ ܠܗܘܢ ܪܘܚܐ ܕܩܘܕܫܐ ܦܪܘܫܘ ܠܝ ܠܫܐܘܠ ܘܠܒܪܢܒܐ ܠܥܒܕܐ ܐܝܢܐ ܕܐܢܐ ܩܪܝܬ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 3, 'ܘܒܬܪ ܕܨܡܘ ܘܨܠܝܘ ܤܡܘ ܥܠܝܗܘܢ ܐܝܕܐ ܘܫܕܪܘ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 4, 'ܘܗܢܘܢ ܟܕ ܐܫܬܠܚܘ ܡܢ ܪܘܚܐ ܕܩܘܕܫܐ ܢܚܬܘ ܠܗܘܢ ܠܤܠܘܩܝܐ ܘܡܢ ܬܡܢ ܪܕܘ ܒܝܡܐ ܥܕܡܐ ܠܩܘܦܪܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 5, 'ܘܟܕ ܥܠܘ ܠܤܠܡܢܐ ܡܕܝܢܬܐ ܡܤܒܪܝܢ ܗܘܘ ܡܠܬܗ ܕܡܪܢ ܒܟܢܘܫܬܐ ܕܝܗܘܕܝܐ ܘܝܘܚܢܢ ܡܫܡܫ ܗܘܐ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 6, 'ܘܟܕ ܐܬܟܪܟܘܗ ܠܟܠܗ ܓܙܪܬܐ ܥܕܡܐ ܠܦܦܘܤ ܡܕܝܢܬܐ ܐܫܟܚܘ ܓܒܪܐ ܚܕ ܚܪܫܐ ܝܗܘܕܝܐ ܕܐܝܬܘܗܝ ܗܘܐ ܢܒܝܐ ܕܓܠܐ ܕܫܡܗ ܗܘܐ ܒܪܫܘܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 7, 'ܗܢܐ ܕܒܝܩ ܗܘܐ ܠܓܒܪܐ ܚܟܝܡܐ ܕܐܝܬܘܗܝ ܗܘܐ ܐܢܬܘܦܛܘܤ ܘܡܬܩܪܐ ܗܘܐ ܤܪܓܝܘܤ ܦܘܠܘܤ ܘܩܪܐ ܗܘܐ ܐܢܬܘܦܛܘܤ ܠܫܐܘܠ ܘܠܒܪܢܒܐ ܘܒܥܐ ܗܘܐ ܕܢܫܡܥ ܡܢܗܘܢ ܡܠܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 8, 'ܩܐܡ ܗܘܐ ܕܝܢ ܠܘܩܒܠܗܘܢ ܗܘ ܗܢܐ ܚܪܫܐ ܒܪܫܘܡܐ ܕܡܬܬܪܓܡ ܫܡܗ ܐܠܘܡܤ ܡܛܠ ܕܨܒܐ ܗܘܐ ܕܢܥܩܡܝܘܗܝ ܠܐܢܬܘܦܛܘܤ ܡܢ ܗܝܡܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 9, 'ܫܐܘܠ ܕܝܢ ܗܘ ܕܐܬܩܪܝ ܦܘܠܘܤ ܐܬܡܠܝ ܗܘܐ ܒܪܘܚܐ ܕܩܘܕܫܐ ܘܚܪ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 10, 'ܘܐܡܪ ܐܘ ܕܡܠܐ ܟܠ ܢܟܠܝܢ ܘܟܠܗܝܢ ܒܝܫܬܐ ܒܪܗ ܕܐܟܠ ܩܪܨܐ ܘܒܥܠܕܒܒܐ ܕܟܠܗ ܟܐܢܘܬܐ ܠܐ ܫܠܐ ܐܢܬ ܠܡܥܩܡܘ ܐܘܪܚܬܗ ܬܪܝܨܬܐ ܕܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 11, 'ܘܗܫܐ ܐܝܕܗ ܕܡܪܝܐ ܥܠܝܟ ܘܬܗܘܐ ܤܡܐ ܘܠܐ ܬܚܙܐ ܫܡܫܐ ܥܕܡܐ ܠܙܒܢܐ ܘܒܗ ܒܫܥܬܐ ܢܦܠ ܥܠܘܗܝ ܥܡܛܢܐ ܘܚܫܘܟܐ ܘܡܬܟܪܟ ܗܘܐ ܘܒܥܐ ܕܡܢܘ ܢܐܚܘܕ ܒܐܝܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 12, 'ܘܟܕ ܚܙܐ ܐܢܬܘܦܛܘܤ ܡܕܡ ܕܗܘܐ ܐܬܕܡܪ ܘܗܝܡܢ ܒܝܘܠܦܢܗ ܕܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 13, 'ܦܘܠܘܤ ܕܝܢ ܘܒܪܢܒܐ ܪܕܘ ܒܝܡܐ ܡܢ ܦܦܘܤ ܡܕܝܢܬܐ ܘܐܬܘ ܠܗܘܢ ܠܦܪܓܐ ܡܕܝܢܬܐ ܕܦܡܦܘܠܝܐ ܘܦܪܫ ܡܢܗܘܢ ܝܘܚܢܢ ܘܐܙܠ ܠܗ ܠܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 14, 'ܗܢܘܢ ܕܝܢ ܢܦܩܘ ܡܢ ܦܪܓܐ ܘܐܬܘ ܠܐܢܛܝܟܝܐ ܡܕܝܢܬܐ ܕܦܝܤܝܕܝܐ ܘܥܠܘ ܠܟܢܘܫܬܐ ܘܝܬܒܘ ܒܝܘܡܐ ܘܫܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 15, 'ܘܒܬܪ ܕܐܬܩܪܝ ܢܡܘܤܐ ܘܢܒܝܐ ܫܠܚܘ ܠܗܘܢ ܩܫܝܫܐ ܕܟܢܘܫܬܐ ܘܐܡܪܘ ܓܒܪܐ ܐܚܝܢ ܐܢ ܗܘ ܕܐܝܬ ܠܟܘܢ ܡܠܬܐ ܕܒܘܝܐܐ ܡܠܠܘ ܥܡ ܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 16, 'ܘܩܡ ܦܘܠܘܤ ܘܐܢܝܦ ܐܝܕܗ ܘܐܡܪ ܓܒܪܐ ܒܢܝ ܐܝܤܪܝܠ ܘܐܝܠܝܢ ܕܕܚܠܝܢ ܡܢ ܐܠܗܐ ܫܡܥܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 17, 'ܐܠܗܗ ܕܥܡܐ ܗܢܐ ܓܒܐ ܠܐܒܗܬܢ ܘܐܪܝܡ ܘܐܘܪܒ ܐܢܘܢ ܟܕ ܗܘܘ ܬܘܬܒܐ ܒܐܪܥܐ ܕܡܨܪܝܢ ܘܒܕܪܥܐ ܪܡܐ ܐܦܩ ܐܢܘܢ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 18, 'ܘܬܪܤܝ ܐܢܘܢ ܒܡܕܒܪܐ ܐܪܒܥܝܢ ܫܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 19, 'ܘܗܓܡ ܫܒܥܐ ܥܡܡܝܢ ܒܐܪܥܐ ܕܟܢܥܢ ܘܝܗܒ ܠܗܘܢ ܐܪܥܗܘܢ ܠܝܘܪܬܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 20, 'ܘܐܪܒܥ ܡܐܐ ܘܚܡܫܝܢ ܫܢܝܢ ܝܗܒ ܠܗܘܢ ܕܝܢܐ ܥܕܡܐ ܠܫܡܘܐܝܠ ܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 21, 'ܘܗܝܕܝܢ ܫܐܠܘ ܠܗܘܢ ܡܠܟܐ ܘܝܗܒ ܠܗܘܢ ܐܠܗܐ ܠܫܐܘܠ ܒܪ ܩܝܫ ܓܒܪܐ ܡܢ ܫܪܒܬܐ ܕܒܢܝܡܝܢ ܫܢܝܢ ܐܪܒܥܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 22, 'ܘܢܤܒܗ ܘܐܩܝܡ ܠܗܘܢ ܠܕܘܝܕ ܡܠܟܐ ܘܐܤܗܕ ܥܠܘܗܝ ܘܐܡܪ ܕܐܫܟܚܬ ܠܕܘܝܕ ܒܪܗ ܕܐܝܫܝ ܓܒܪܐ ܐܝܟ ܠܒܝ ܗܘ ܢܥܒܕ ܟܠܗܘܢ ܨܒܝܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 23, 'ܡܢ ܙܪܥܗ ܕܗܢܐ ܐܩܝܡ ܐܠܗܐ ܠܐܝܤܪܝܠ ܐܝܟ ܡܐ ܕܐܫܬܘܕܝ ܠܝܫܘܥ ܦܪܘܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 24, 'ܘܫܕܪ ܠܝܘܚܢܢ ܕܢܟܪܙ ܩܕܡ ܡܐܬܝܬܗ ܡܥܡܘܕܝܬܐ ܕܬܝܒܘܬܐ ܠܟܠܗ ܥܡܐ ܕܐܝܤܪܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 25, 'ܘܟܕ ܡܫܠܡ ܗܘܐ ܝܘܚܢܢ ܬܫܡܫܬܗ ܐܡܪ ܗܘܐ ܡܢܘ ܤܒܪܝܢ ܐܢܬܘܢ ܕܐܝܬܝ ܠܐ ܗܘܝܬ ܐܢܐ ܐܠܐ ܗܐ ܐܬܐ ܒܬܪܝ ܗܘ ܕܠܐ ܫܘܐ ܐܢܐ ܕܐܫܪܐ ܥܪܩܐ ܕܡܤܢܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 26, 'ܓܒܪܐ ܐܚܝܢ ܒܢܝ ܫܪܒܬܗ ܕܐܒܪܗܡ ܘܐܝܠܝܢ ܕܕܚܠܝܢ ܥܡܟܘܢ ܠܐܠܗܐ ܠܟܘܢ ܗܘ ܐܫܬܕܪܬ ܡܠܬܐ ܕܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 27, 'ܗܠܝܢ ܓܝܪ ܥܡܘܪܐ ܕܐܘܪܫܠܡ ܘܪܫܢܝܗܘܢ ܠܐ ܐܪܓܫܘ ܒܗ ܐܦܠܐ ܒܟܬܒܐ ܕܢܒܝܐ ܕܡܬܩܪܝܢ ܒܟܠ ܫܒܐ ܐܠܐ ܕܢܘܗܝ ܘܫܠܡܘ ܗܠܝܢ ܕܟܬܝܒܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 28, 'ܘܟܕ ܠܐ ܐܫܟܚܘ ܥܠܬܐ ܕܡܘܬܐ ܡܕܡ ܫܐܠܘ ܡܢ ܦܝܠܛܘܤ ܕܢܩܛܠܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 29, 'ܘܟܕ ܫܠܡܘ ܟܠܡܕܡ ܕܟܬܝܒ ܗܘܐ ܥܠܘܗܝ ܐܚܬܘܗܝ ܡܢ ܨܠܝܒܐ ܘܤܡܘܗܝ ܒܝܬ ܩܒܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 30, 'ܐܠܗܐ ܕܝܢ ܐܩܝܡܗ ܡܢ ܒܝܬ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 31, 'ܘܐܬܚܙܝ ܝܘܡܬܐ ܤܓܝܐܐ ܠܐܝܠܝܢ ܕܤܠܩܘ ܥܡܗ ܡܢ ܓܠܝܠܐ ܠܐܘܪܫܠܡ ܘܗܢܘܢ ܐܢܘܢ ܗܫܐ ܤܗܕܘܗܝ ܠܘܬ ܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 32, 'ܘܐܦ ܚܢܢ ܗܐ ܡܤܒܪܝܢܢ ܠܟܘܢ ܕܫܘܘܕܝܐ ܗܘ ܕܗܘܐ ܠܘܬ ܐܒܗܬܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 33, 'ܗܐ ܫܡܠܝܗ ܐܠܗܐ ܠܢ ܠܒܢܝܗܘܢ ܕܐܩܝܡ ܠܝܫܘܥ ܐܝܟ ܕܟܬܝܒ ܒܡܙܡܘܪܐ ܕܬܪܝܢ ܕܒܪܝ ܐܢܬ ܐܢܐ ܝܘܡܢܐ ܝܠܕܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 34, 'ܘܗܟܢܐ ܐܩܝܡܗ ܐܠܗܐ ܡܢ ܒܝܬ ܡܝܬܐ ܕܬܘܒ ܠܐ ܢܗܦܘܟ ܢܚܙܐ ܚܒܠܐ ܐܝܟ ܕܐܡܪ ܕܐܬܠ ܠܟܘܢ ܛܝܒܘܬܗ ܕܕܘܝܕ ܡܗܝܡܢܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 35, 'ܘܬܘܒ ܐܡܪ ܒܕܘܟܬܐ ܐܚܪܬܐ ܕܠܐ ܝܗܒܬ ܠܚܤܝܟ ܕܢܚܙܐ ܚܒܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 36, 'ܕܘܝܕ ܓܝܪ ܒܫܪܒܬܗ ܫܡܫ ܨܒܝܢܗ ܕܐܠܗܐ ܘܫܟܒ ܘܐܬܬܘܤܦ ܥܠ ܐܒܗܘܗܝ ܘܚܙܐ ܚܒܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 37, 'ܗܢܐ ܕܝܢ ܕܐܩܝܡ ܐܠܗܐ ܠܐ ܚܙܐ ܚܒܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 38, 'ܕܥܘ ܗܟܝܠ ܐܚܝ ܕܒܗ ܒܗܢܐ ܡܬܟܪܙ ܠܟܘܢ ܫܘܒܩܢ ܚܛܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 39, 'ܘܡܢ ܟܠ ܕܠܐ ܐܫܟܚܬܘܢ ܒܢܡܘܤܐ ܕܡܘܫܐ ܕܬܙܕܕܩܘܢ ܒܗܢܐ ܟܠ ܕܡܗܝܡܢ ܡܙܕܕܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 40, 'ܐܙܕܗܪܘ ܗܟܝܠ ܕܠܡܐ ܢܐܬܐ ܥܠܝܟܘܢ ܡܕܡ ܕܟܬܝܒ ܒܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 41, 'ܕܚܙܘ ܡܒܤܪܢܐ ܘܬܬܡܗܘܢ ܘܬܬܚܒܠܘܢ ܕܥܒܕܐ ܥܒܕ ܐܢܐ ܒܝܘܡܝܟܘܢ ܐܝܢܐ ܕܠܐ ܬܗܝܡܢܘܢ ܐܢ ܐܢܫ ܡܫܬܥܐ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 42, 'ܘܟܕ ܢܦܩܝܢ ܗܘܘ ܡܢ ܨܐܕܝܗܘܢ ܒܥܘ ܡܢܗܘܢ ܕܠܫܒܬܐ ܐܚܪܬܐ ܢܡܠܠܘܢ ܥܡܗܘܢ ܡܠܐ ܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 43, 'ܘܡܢ ܕܐܫܬܪܝܬ ܟܢܘܫܬܐ ܤܓܝܐܐ ܝܗܘܕܝܐ ܐܙܠܘ ܒܬܪܗܘܢ ܘܐܦ ܓܝܘܪܐ ܕܕܚܠܝܢ ܗܘܘ ܡܢ ܐܠܗܐ ܘܗܢܘܢ ܡܡܠܠܝܢ ܗܘܘ ܘܡܦܝܤܝܢ ܠܗܘܢ ܕܢܗܘܘܢ ܢܩܝܦܝܢ ܠܛܝܒܘܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 44, 'ܘܠܫܒܬܐ ܐܚܪܬܐ ܟܢܫܬ ܟܠܗ ܡܕܝܢܬܐ ܠܡܫܡܥ ܡܠܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 45, 'ܘܟܕ ܚܙܘ ܝܗܘܕܝܐ ܟܢܫܐ ܤܓܝܐܐ ܐܬܡܠܝܘ ܚܤܡܐ ܘܩܝܡܝܢ ܗܘܘ ܠܘܩܒܠ ܡܠܐ ܕܐܡܪ ܗܘܐ ܦܘܠܘܤ ܘܡܓܕܦܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 46, 'ܐܡܪ ܕܝܢ ܦܘܠܘܤ ܘܒܪܢܒܐ ܥܝܢ ܒܓܠܐ ܠܟܘܢ ܗܘ ܘܠܐ ܗܘܐ ܩܕܡܝܬ ܕܬܬܐܡܪ ܡܠܬܗ ܕܐܠܗܐ ܐܠܐ ܡܛܠ ܕܕܚܝܢ ܐܢܬܘܢ ܠܗ ܡܢܟܘܢ ܘܦܤܩܬܘܢ ܥܠ ܢܦܫܟܘܢ ܕܠܐ ܫܘܝܢ ܐܢܬܘܢ ܠܚܝܐ ܕܠܥܠܡ ܗܐ ܡܬܦܢܝܢܢ ܠܢ ܠܘܬ ܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 47, 'ܗܟܢܐ ܓܝܪ ܦܩܕܢ ܡܪܢ ܐܝܟ ܕܟܬܝܒ ܕܤܡܬܟ ܢܘܗܪܐ ܠܥܡܡܐ ܕܬܗܘܐ ܠܚܝܐ ܥܕܡܐ ܠܤܘܦܝܗ ܕܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 48, 'ܘܟܕ ܫܡܥܝܢ ܗܘܘ ܥܡܡܐ ܚܕܝܢ ܗܘܘ ܘܡܫܒܚܝܢ ܠܐܠܗܐ ܘܗܝܡܢܘ ܐܝܠܝܢ ܕܤܝܡܝܢ ܗܘܘ ܠܚܝܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 49, 'ܘܡܠܬܗ ܕܡܪܝܐ ܡܬܡܠܠܐ ܗܘܬ ܒܟܠܗ ܗܘ ܐܬܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 50, 'ܝܗܘܕܝܐ ܕܝܢ ܓܪܓܘ ܠܪܫܐ ܕܡܕܝܢܬܐ ܘܠܢܫܐ ܥܬܝܪܬܐ ܐܝܠܝܢ ܕܕܚܠܢ ܗܘܝ ܥܡܗܘܢ ܠܐܠܗܐ ܘܐܩܝܡܘ ܪܕܘܦܝܐ ܥܠ ܦܘܠܘܤ ܘܥܠ ܒܪܢܒܐ ܘܐܦܩܘ ܐܢܘܢ ܡܢ ܬܚܘܡܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 51, 'ܘܟܕ ܢܦܩܘ ܢܦܨܘ ܥܠܝܗܘܢ ܚܠܐ ܕܪܓܠܝܗܘܢ ܘܐܬܘ ܠܗܘܢ ܠܐܝܩܢܘܢ ܡܕܝܢܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 52, 'ܘܬܠܡܝܕܐ ܡܬܡܠܝܢ ܗܘܘ ܚܕܘܬܐ ܘܪܘܚܐ ܕܩܘܕܫܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 1, 'ܘܐܬܘ ܘܥܠܘ ܠܗܘܢ ܠܟܢܘܫܬܐ ܕܝܗܘܕܝܐ ܘܗܟܢܐ ܡܠܠܘ ܥܡܗܘܢ ܐܝܟ ܕܢܗܝܡܢܘܢ ܤܓܝܐܐ ܡܢ ܝܗܘܕܝܐ ܘܡܢ ܝܘܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 2, 'ܝܗܘܕܝܐ ܕܝܢ ܐܝܠܝܢ ܕܠܐ ܡܬܛܦܝܤܝܢ ܗܘܘ ܓܪܓܘ ܠܥܡܡܐ ܕܢܒܐܫܘܢ ܠܗܘܢ ܠܐܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 3, 'ܘܗܢܘܢ ܙܒܢܐ ܤܓܝܐܐ ܗܘܘ ܬܡܢ ܘܥܝܢ ܒܓܠܐ ܡܡܠܠܝܢ ܗܘܘ ܥܠ ܡܪܝܐ ܘܗܘ ܡܤܗܕ ܗܘܐ ܥܠ ܡܠܬܐ ܕܛܝܒܘܬܗ ܒܐܬܘܬܐ ܘܒܬܕܡܪܬܐ ܕܥܒܕ ܗܘܐ ܒܐܝܕܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 4, 'ܘܟܠܗ ܟܢܫܐ ܕܡܕܝܢܬܐ ܦܠܝܓ ܗܘܐ ܡܢܗܘܢ ܐܝܬ ܗܘܘ ܥܡ ܝܗܘܕܝܐ ܘܡܢܗܘܢ ܢܩܝܦܝܢ ܗܘܘ ܠܫܠܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 5, 'ܗܘܐ ܕܝܢ ܥܠܝܗܘܢ ܓܙܡܐ ܡܢ ܥܡܡܐ ܘܡܢ ܝܗܘܕܝܐ ܘܪܫܢܝܗܘܢ ܕܢܨܥܪܘܢ ܐܢܘܢ ܘܢܪܓܡܘܢ ܐܢܘܢ ܒܟܐܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 6, 'ܘܟܕ ܝܕܥܘ ܫܢܝܘ ܘܐܬܓܘܤܘ ܒܡܕܝܢܬܐ ܕܠܘܩܢܝܐ ܠܘܤܛܪܐ ܘܕܪܒܐ ܘܩܘܪܝܐ ܕܚܕܪܝܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 7, 'ܘܬܡܢ ܡܤܒܪܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 8, 'ܘܓܒܪܐ ܚܕ ܝܬܒ ܗܘܐ ܒܠܘܤܛܪܐ ܡܕܝܢܬܐ ܕܤܓܝܦ ܗܘܐ ܒܪܓܠܘܗܝ ܚܓܝܪܐ ܕܡܢ ܟܪܤ ܐܡܗ ܕܡܡܬܘܡ ܠܐ ܗܠܟ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 9, 'ܗܢܐ ܫܡܥ ܠܦܘܠܘܤ ܕܡܡܠܠ ܘܟܕ ܚܙܝܗܝ ܦܘܠܘܤ ܘܐܫܬܘܕܥ ܕܐܝܬ ܒܗ ܗܝܡܢܘܬܐ ܕܢܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 10, 'ܐܡܪ ܠܗ ܒܩܠܐ ܪܡܐ ܠܟ ܐܡܪ ܐܢܐ ܒܫܡܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܩܘܡ ܥܠ ܪܓܠܝܟ ܘܫܘܪ ܩܡ ܘܗܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 11, 'ܘܟܢܫܐ ܕܥܡܐ ܟܕ ܚܙܘ ܡܕܡ ܕܥܒܕ ܦܘܠܘܤ ܐܪܝܡܘ ܩܠܗܘܢ ܒܠܫܢܗ ܕܐܬܪܐ ܘܐܡܪܝܢ ܐܠܗܐ ܐܬܕܡܝܘ ܒܒܢܝ ܐܢܫܐ ܘܢܚܬܘ ܠܘܬܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 12, 'ܘܫܡܝܘ ܗܘܘ ܠܒܪܢܒܐ ܡܪܐ ܐܠܗܐ ܘܠܦܘܠܘܤ ܗܪܡܝܤ ܡܛܠ ܕܗܘ ܡܫܪܐ ܗܘܐ ܒܡܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 13, 'ܘܟܘܡܪܐ ܕܡܪܐ ܐܠܗܐ ܗܘ ܕܐܝܬ ܗܘܐ ܠܒܪ ܡܢ ܡܕܝܢܬܐ ܐܝܬܝ ܬܘܪܐ ܘܟܠܝܠܐ ܠܬܪܥܐ ܕܕܪܬܐ ܐܬܪ ܕܫܪܝܢ ܗܘܘ ܘܨܒܐ ܗܘܐ ܕܢܕܒܚ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 14, 'ܒܪܢܒܐ ܕܝܢ ܘܦܘܠܘܤ ܟܕ ܫܡܥܘ ܤܕܩܘ ܢܚܬܝܗܘܢ ܘܫܘܪܘ ܘܢܦܩܘ ܠܗܘܢ ܠܘܬ ܐܟܠܘܤ ܘܩܥܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 15, 'ܘܐܡܪܝܢ ܓܒܪܐ ܡܢܐ ܥܒܕܝܢ ܐܢܬܘܢ ܐܦ ܚܢܢ ܒܢܝܢܫܐ ܚܢܢ ܚܫܘܫܐ ܐܟܘܬܟܘܢ ܕܡܤܒܪܝܢܢ ܠܟܘܢ ܕܡܢ ܗܠܝܢ ܒܛܠܬܐ ܬܬܦܢܘܢ ܠܘܬ ܐܠܗܐ ܚܝܐ ܗܘ ܕܥܒܕ ܫܡܝܐ ܘܐܪܥܐ ܘܝܡܡܐ ܘܟܠ ܕܐܝܬ ܒܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 16, 'ܗܘ ܕܒܕܪܐ ܩܕܡܝܐ ܫܒܩ ܗܘܐ ܐܢܘܢ ܠܟܠܗܘܢ ܥܡܡܐ ܕܢܐܙܠܘܢ ܒܐܘܪܚܬܐ ܕܢܦܫܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 17, 'ܟܕ ܠܐ ܫܒܩ ܢܦܫܗ ܕܠܐ ܤܗܕܘ ܒܕܥܒܕ ܗܘܐ ܠܗܘܢ ܛܒܬܐ ܡܢ ܫܡܝܐ ܘܡܚܬ ܗܘܐ ܠܗܘܢ ܡܛܪܐ ܘܡܪܒܐ ܗܘܐ ܦܐܪܐ ܒܙܒܢܝܗܘܢ ܘܡܠܐ ܗܘܐ ܬܘܪܤܝܐ ܘܒܤܝܡܘܬܐ ܠܒܘܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 18, 'ܘܟܕ ܗܠܝܢ ܐܡܪܝܢ ܗܘܘ ܡܢ ܡܚܤܢ ܟܠܘ ܠܥܡܐ ܕܐܢܫ ܠܐ ܢܕܒܚ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 19, 'ܐܬܘ ܕܝܢ ܠܬܡܢ ܝܗܘܕܝܐ ܡܢ ܐܝܩܢܘܢ ܘܡܢ ܐܢܛܝܟܝܐ ܘܫܓܫܘ ܥܠܝܗܘܢ ܠܥܡܐ ܘܪܓܡܘܗܝ ܠܦܘܠܘܤ ܘܓܪܘܗܝ ܠܒܪ ܡܢ ܡܕܝܢܬܐ ܡܛܠ ܕܤܒܪܝܢ ܗܘܘ ܕܡܝܬ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 20, 'ܘܟܢܫܘ ܥܠܘܗܝ ܬܠܡܝܕܐ ܘܩܡ ܥܠ ܠܡܕܝܢܬܐ ܘܠܝܘܡܐ ܐܚܪܢܐ ܢܦܩ ܠܗ ܡܢ ܬܡܢ ܥܡ ܒܪܢܒܐ ܘܐܬܘ ܠܕܪܒܐ ܡܕܝܢܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 21, 'ܘܟܕ ܡܤܒܪܝܢ ܗܘܘ ܠܒܢܝ ܡܕܝܢܬܐ ܗܝ ܬܠܡܕܘ ܗܘܘ ܠܤܓܝܐܐ ܘܗܦܟܘ ܐܬܘ ܠܗܘܢ ܠܠܘܤܛܪܐ ܡܕܝܢܬܐ ܘܠܐܝܩܢܘܢ ܘܠܐܢܛܝܟܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 22, 'ܟܕ ܡܫܪܪܝܢ ܗܘܘ ܢܦܫܬܗܘܢ ܕܬܠܡܝܕܐ ܘܒܥܝܢ ܗܘܘ ܡܢܗܘܢ ܕܢܩܘܘܢ ܒܗܝܡܢܘܬܐ ܘܐܡܪܝܢ ܗܘܘ ܠܗܘܢ ܕܒܐܘܠܨܢܐ ܤܓܝܐܐ ܘܠܐ ܠܡܥܠ ܠܡܠܟܘܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 23, 'ܘܐܩܝܡܘ ܠܗܘܢ ܒܟܠ ܥܕܬܐ ܩܫܝܫܐ ܟܕ ܨܝܡܝܢ ܗܘܘ ܥܡܗܘܢ ܘܡܨܠܝܢ ܘܡܓܥܠܝܢ ܗܘܘ ܠܗܘܢ ܠܡܪܢ ܗܘ ܕܗܝܡܢܘ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 24, 'ܘܟܕ ܐܬܟܪܟܘ ܒܐܬܪܐ ܕܦܝܤܝܕܝܐ ܐܬܘ ܠܗܘܢ ܠܦܡܦܘܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 25, 'ܘܟܕ ܡܠܠܘ ܒܦܪܓܐ ܡܕܝܢܬܐ ܡܠܬܐ ܕܡܪܝܐ ܢܚܬܘ ܠܗܘܢ ܠܐܝܛܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 26, 'ܘܡܢ ܬܡܢ ܪܕܘ ܒܝܡܐ ܘܐܬܘ ܠܗܘܢ ܠܐܢܛܝܟܝܐ ܡܛܠ ܕܡܢ ܬܡܢ ܡܓܥܠܝܢ ܗܘܘ ܠܛܝܒܘܬܗ ܕܡܪܝܐ ܠܥܒܕܐ ܗܘ ܕܫܠܡܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 27, 'ܘܟܕ ܟܢܫܘ ܟܠܗ ܥܕܬܐ ܡܫܬܥܝܢ ܗܘܘ ܟܠܡܕܡ ܕܥܒܕ ܥܡܗܘܢ ܐܠܗܐ ܘܕܦܬܚ ܬܪܥܐ ܕܗܝܡܢܘܬܐ ܠܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 28, 'ܘܙܒܢܐ ܤܓܝܐܐ ܗܘܘ ܬܡܢ ܠܘܬ ܬܠܡܝܕܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 1, 'ܢܚܬܘ ܗܘܘ ܕܝܢ ܐܢܫܐ ܡܢ ܝܗܘܕ ܘܡܠܦܝܢ ܗܘܘ ܠܗܘܢ ܠܐܚܐ ܕܐܢ ܗܘ ܕܠܐ ܓܙܪܝܢ ܐܢܬܘܢ ܒܥܝܕܐ ܕܢܡܘܤܐ ܠܐ ܡܫܟܚܝܢ ܐܢܬܘܢ ܠܡܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 2, 'ܘܗܘܐ ܫܓܘܫܝܐ ܤܓܝܐܐ ܘܒܥܬܐ ܠܦܘܠܘܤ ܘܠܒܪܢܒܐ ܥܡܗܘܢ ܘܗܘܬ ܕܢܤܩܘܢ ܦܘܠܘܤ ܘܒܪܢܒܐ ܘܐܚܪܢܐ ܥܡܗܘܢ ܠܘܬ ܫܠܝܚܐ ܘܩܫܝܫܐ ܕܒܐܘܪܫܠܡ ܡܛܠ ܗܕܐ ܒܥܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 3, 'ܘܠܘܝܬ ܫܕܪܬ ܐܢܘܢ ܥܕܬܐ ܘܪܕܝܢ ܗܘܘ ܒܟܠܗ ܦܘܢܝܩܐ ܘܐܦ ܒܝܬ ܫܡܪܝܐ ܟܕ ܡܫܬܥܝܢ ܗܘܘ ܥܠ ܦܘܢܝܐ ܕܥܡܡܐ ܘܥܒܕܝܢ ܗܘܘ ܚܕܘܬܐ ܪܒܬܐ ܠܟܠܗܘܢ ܐܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 4, 'ܘܟܕ ܐܬܘ ܠܐܘܪܫܠܡ ܐܬܩܒܠܘ ܡܢ ܥܕܬܐ ܘܡܢ ܫܠܝܚܐ ܘܡܢ ܩܫܝܫܐ ܘܐܫܬܥܝܘ ܠܗܘܢ ܟܠ ܡܐ ܕܥܒܕ ܥܡܗܘܢ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 5, 'ܩܡܘ ܗܘܘ ܕܝܢ ܐܢܫܐ ܐܝܠܝܢ ܕܗܝܡܢܘ ܗܘܘ ܡܢ ܝܘܠܦܢܐ ܕܦܪܝܫܐ ܘܐܡܪܝܢ ܕܘܠܐ ܗܘ ܠܟܘܢ ܠܡܓܙܪ ܐܢܘܢ ܘܬܦܩܕܘܢ ܐܢܘܢ ܕܢܛܪܘܢ ܢܡܘܤܐ ܕܡܘܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 6, 'ܐܬܟܢܫܘ ܕܝܢ ܫܠܝܚܐ ܘܩܫܝܫܐ ܕܢܚܙܘܢ ܥܠ ܡܠܬܐ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 7, 'ܘܟܕ ܗܘܬ ܒܥܬܐ ܤܓܝܐܬܐ ܩܡ ܫܡܥܘܢ ܘܐܡܪ ܠܗܘܢ ܓܒܪܐ ܐܚܝܢ ܐܢܬܘܢ ܝܕܥܝܢ ܐܢܬܘܢ ܕܡܢ ܝܘܡܬܐ ܩܕܡܝܐ ܡܢ ܦܘܡܝ ܕܝܠܝ ܓܒܐ ܐܠܗܐ ܕܢܫܡܥܘܢ ܥܡܡܐ ܡܠܬܐ ܕܤܒܪܬܐ ܘܢܗܝܡܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 8, 'ܘܐܠܗܐ ܕܝܕܥ ܕܒܠܒܘܬܐ ܐܤܗܕ ܥܠܝܗܘܢ ܘܝܗܒ ܠܗܘܢ ܪܘܚܐ ܕܩܘܕܫܐ ܐܝܟ ܕܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 9, 'ܘܡܕܡ ܠܐ ܦܪܫ ܒܝܢܝܢ ܘܠܗܘܢ ܡܛܠ ܕܕܟܝ ܒܗܝܡܢܘܬܐ ܠܒܘܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 10, 'ܘܗܫܐ ܐܢܬܘܢ ܡܢܐ ܡܢܤܝܢ ܐܢܬܘܢ ܠܐܠܗܐ ܐܝܟ ܕܬܤܝܡܘܢ ܢܝܪܐ ܥܠ ܨܘܪܝܗܘܢ ܕܬܠܡܝܕܐ ܐܝܢܐ ܕܐܦܠܐ ܐܒܗܬܢ ܐܦܠܐ ܚܢܢ ܐܫܟܚܢ ܠܡܛܥܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 11, 'ܐܠܐ ܒܛܝܒܘܬܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܡܗܝܡܢܝܢܢ ܕܢܚܐ ܐܟܘܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 12, 'ܘܫܬܩܘ ܟܠܗ ܟܢܫܐ ܘܫܡܥܝܢ ܗܘܘ ܠܦܘܠܘܤ ܘܠܒܪܢܒܐ ܕܡܫܬܥܝܢ ܗܘܘ ܟܠ ܡܐ ܕܥܒܕ ܐܠܗܐ ܒܐܝܕܝܗܘܢ ܐܬܘܬܐ ܘܓܒܪܘܬܐ ܒܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 13, 'ܘܒܬܪ ܕܫܬܩܘ ܩܡ ܝܥܩܘܒ ܘܐܡܪ ܓܒܪܐ ܐܚܝܢ ܫܘܡܥܘܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 14, 'ܫܡܥܘܢ ܐܫܬܥܝ ܠܟܘܢ ܐܝܟܢܐ ܫܪܝ ܐܠܗܐ ܠܡܓܒܐ ܡܢ ܥܡܡܐ ܥܡܐ ܠܫܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 15, 'ܘܠܗܕܐ ܫܠܡܢ ܡܠܝܗܘܢ ܕܢܒܝܐ ܐܝܟ ܡܐ ܕܟܬܝܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 16, 'ܕܡܢ ܒܬܪ ܗܠܝܢ ܐܗܦܘܟ ܘܐܩܝܡ ܡܫܟܢܗ ܕܕܘܝܕ ܐܝܢܐ ܕܢܦܠ ܘܐܒܢܐ ܡܕܡ ܕܢܦܠ ܡܢܗ ܘܐܩܝܡܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 17, 'ܐܝܟ ܕܢܒܥܘܢ ܫܪܟܗܘܢ ܕܒܢܝܢܫܐ ܠܡܪܝܐ ܘܟܠܗܘܢ ܥܡܡܐ ܐܝܠܝܢ ܕܐܬܩܪܝ ܫܡܝ ܥܠܝܗܘܢ ܐܡܪ ܡܪܝܐ ܕܥܒܕ ܗܠܝܢ ܟܠܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 18, 'ܝܕܝܥܝܢ ܡܢ ܥܠܡ ܥܒܕܘܗܝ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 19, 'ܡܛܠ ܗܕܐ ܐܢܐ ܐܡܪ ܐܢܐ ܕܠܐ ܢܗܘܘܢ ܫܚܩܝܢ ܠܐܝܠܝܢ ܕܡܢ ܥܡܡܐ ܡܬܦܢܝܢ ܠܘܬ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 20, 'ܐܠܐ ܢܫܬܠܚ ܠܗܘܢ ܕܢܗܘܘܢ ܦܪܝܩܝܢ ܡܢ ܛܡܐܘܬܐ ܕܕܒܝܚܐ ܘܡܢ ܙܢܝܘܬܐ ܘܡܢ ܕܚܢܝܩܐ ܘܡܢ ܕܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 21, 'ܡܘܫܐ ܓܝܪ ܡܢ ܕܪܐ ܩܕܡܝܐ ܒܟܠ ܡܕܝܢܐ ܐܝܬ ܗܘܐ ܠܗ ܟܪܘܙܐ ܒܟܢܘܫܬܐ ܕܒܟܠ ܫܒܝܢ ܩܪܝܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 22, 'ܗܝܕܝܢ ܫܠܝܚܐ ܘܩܫܝܫܐ ܥܡ ܟܠܗ ܥܕܬܐ ܓܒܘ ܓܒܪܐ ܡܢܗܘܢ ܘܫܕܪܘ ܠܐܢܛܝܘܟܝ ܥܡ ܦܘܠܘܤ ܘܒܪܢܒܐ ܠܝܗܘܕܐ ܕܡܬܩܪܐ ܒܪ ܫܒܐ ܘܠܫܝܠܐ ܓܒܪܐ ܕܪܫܐ ܗܘܘ ܒܗܘܢ ܒܐܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 23, 'ܘܟܬܒܘ ܐܓܪܬܐ ܒܐܝܕܝܗܘܢ ܗܟܢܐ ܫܠܝܚܐ ܘܩܫܝܫܐ ܘܐܚܐ ܠܐܝܠܝܢ ܕܐܝܬ ܒܐܢܛܝܘܟܝ ܘܒܤܘܪܝܐ ܘܒܩܝܠܝܩܝܐ ܐܚܐ ܕܡܢ ܥܡܡܐ ܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 24, 'ܫܡܝܥ ܠܢ ܕܐܢܫܝܢ ܡܢܢ ܢܦܩܘ ܘܕܠܚܟܘܢ ܒܡܠܐ ܘܐܗܦܟܘ ܢܦܫܬܟܘܢ ܟܕ ܐܡܪܝܢ ܕܬܗܘܘܢ ܓܙܪܝܢ ܘܢܛܪܝܢ ܢܡܘܤܐ ܐܝܠܝܢ ܕܚܢܢ ܠܐ ܦܩܕܢ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 25, 'ܡܛܠ ܗܢܐ ܐܬܚܫܒܢ ܟܠܢ ܟܕ ܟܢܝܫܝܢܢ ܘܓܒܝܢ ܓܒܪܐ ܘܫܕܪܢ ܠܘܬܟܘܢ ܥܡ ܦܘܠܘܤ ܘܒܪܢܒܐ ܚܒܝܒܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 26, 'ܐܢܫܐ ܕܐܫܠܡܘ ܢܦܫܬܗܘܢ ܚܠܦ ܫܡܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 27, 'ܘܫܕܪܢ ܥܡܗܘܢ ܠܝܗܘܕܐ ܘܠܫܝܠܐ ܕܗܢܘܢ ܒܡܠܬܐ ܢܐܡܪܘܢ ܠܟܘܢ ܗܢܝܢ ܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 28, 'ܗܘܐ ܓܝܪ ܨܒܝܢܐ ܠܪܘܚܐ ܕܩܘܕܫܐ ܘܐܦ ܠܢ ܕܠܐ ܢܬܬܤܝܡ ܥܠܝܟܘܢ ܝܘܩܪܐ ܝܬܝܪܐ ܠܒܪ ܡܢ ܗܠܝܢ ܕܐܠܨܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 29, 'ܕܬܬܪܚܩܘܢ ܡܢ ܕܕܒܝܚܐ ܘܡܢ ܕܡܐ ܘܡܢ ܚܢܝܩܐ ܘܡܢ ܙܢܝܘܬܐ ܕܟܕ ܬܛܪܘܢ ܢܦܫܟܘܢ ܡܢ ܗܠܝܢ ܫܦܝܪ ܬܗܘܘܢ ܗܘܘ ܫܪܝܪܝܢ ܒܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 30, 'ܗܢܘܢ ܕܝܢ ܕܐܫܬܠܚܘ ܐܬܘ ܠܐܢܛܝܟܝܐ ܘܟܢܫܘ ܟܠܗ ܥܡܐ ܘܝܗܒܘ ܐܓܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 31, 'ܘܟܕ ܩܪܘ ܚܕܝܘ ܘܐܬܒܝܐܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 32, 'ܘܒܡܠܬܐ ܥܬܝܪܬܐ ܚܝܠܘ ܠܐܚܐ ܘܩܝܡܘ ܐܢܘܢ ܕܒܝܬ ܝܗܘܕܐ ܘܫܝܠܐ ܡܛܠ ܕܐܦ ܗܢܘܢ ܢܒܝܐ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 33, 'ܘܟܕ ܗܘܘ ܬܡܢ ܙܒܢܐ ܫܪܘ ܐܢܘܢ ܐܚܐ ܒܫܠܡܐ ܠܘܬ ܫܠܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 34, 'ܒܪܡ ܗܘܐ ܨܒܝܢܗ ܕܫܝܠܐ ܕܢܩܘܐ ܬܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 35, 'ܦܘܠܘܤ ܕܝܢ ܘܒܪܢܒܐ ܩܘܝܘ ܗܘܘ ܒܐܢܛܝܘܟܝ ܘܡܠܦܝܢ ܗܘܘ ܘܡܤܒܪܝܢ ܥܡ ܐܚܪܢܐ ܤܓܝܐܐ ܡܠܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 36, 'ܘܒܬܪ ܝܘܡܬܐ ܐܡܪ ܦܘܠܘܤ ܠܒܪܢܒܐ ܢܬܦܢܐ ܘܢܤܥܘܪ ܠܐܚܐ ܕܒܟܠ ܡܕܝܢܐ ܕܐܟܪܙܢ ܒܗ ܡܠܬܐ ܕܐܠܗܐ ܘܢܚܙܐ ܡܢܐ ܥܒܝܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 37, 'ܒܪܢܒܐ ܕܝܢ ܨܒܐ ܗܘܐ ܕܢܕܒܪ ܠܝܘܚܢܢ ܗܘ ܕܐܬܟܢܝ ܡܪܩܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 38, 'ܦܘܠܘܤ ܕܝܢ ܠܐ ܨܒܐ ܗܘܐ ܕܢܕܒܪܗ ܥܡܗܘܢ ܡܛܠ ܕܫܒܩ ܗܘܐ ܐܢܘܢ ܟܕ ܗܢܘܢ ܒܦܡܦܘܠܝܐ ܘܠܐ ܐܙܠ ܥܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 39, 'ܡܛܠ ܗܢܐ ܚܪܝܢܐ ܦܪܫܘ ܚܕ ܡܢ ܚܕ ܘܒܪܢܒܐ ܕܒܪ ܠܡܪܩܘܤ ܘܪܕܘ ܒܝܡܐ ܘܐܙܠܘ ܠܗܘܢ ܠܩܘܦܪܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 40, 'ܦܘܠܘܤ ܕܝܢ ܓܒܐ ܠܗ ܠܫܝܠܐ ܘܢܦܩ ܟܕ ܡܓܥܠ ܡܢ ܐܚܐ ܠܛܝܒܘܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 41, 'ܘܪܕܐ ܗܘܐ ܒܤܘܪܝܐ ܘܒܩܝܠܝܩܝܐ ܘܡܩܝܡ ܥܕܬܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 1, 'ܘܡܛܝ ܗܘܐ ܠܕܪܒܐ ܡܕܝܢܬܐ ܘܠܠܘܤܛܪܐ ܐܝܬ ܗܘܐ ܕܝܢ ܬܡܢ ܬܠܡܝܕܐ ܚܕ ܕܫܡܗ ܛܝܡܬܐܘܤ ܒܪܗ ܕܝܗܘܕܝܬܐ ܚܕܐ ܡܗܝܡܢܬܐ ܘܐܒܘܗܝ ܐܪܡܝܐ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 2, 'ܘܟܠܗܘܢ ܬܠܡܝܕܐ ܕܡܢ ܠܘܤܛܪܐ ܘܡܢ ܐܝܩܢܘܢ ܡܤܗܕܝܢ ܗܘܘ ܥܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 3, 'ܠܗܢܐ ܨܒܐ ܗܘܐ ܦܘܠܘܤ ܕܢܕܒܪܝܘܗܝ ܥܡܗ ܘܢܤܒ ܓܙܪܗ ܡܛܠ ܝܗܘܕܝܐ ܕܐܝܬ ܗܘܘ ܒܐܬܪܐ ܝܕܥܝܢ ܗܘܘ ܓܝܪ ܟܠܗܘܢ ܠܐܒܘܗܝ ܕܐܪܡܝܐ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 4, 'ܘܟܕ ܐܙܠܝܢ ܗܘܘ ܒܡܕܝܢܬܐ ܡܟܪܙܝܢ ܗܘܘ ܘܡܠܦܝܢ ܠܗܘܢ ܕܢܗܘܘܢ ܢܛܪܝܢ ܦܘܩܕܢܐ ܐܝܠܝܢ ܕܟܬܒܘ ܫܠܝܚܐ ܘܩܫܝܫܐ ܕܒܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 5, 'ܒܪܡ ܥܕܬܐ ܡܬܩܝܡܢ ܗܘܝ ܒܗܝܡܢܘܬܐ ܘܤܓܝܢ ܗܘܝ ܒܡܢܝܢܐ ܟܠܝܘܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 6, 'ܗܠܟܘ ܕܝܢ ܒܦܪܘܓܝܐ ܘܒܓܠܛܝܐ ܐܬܪܘܬܐ ܘܟܠܬ ܐܢܘܢ ܪܘܚܐ ܕܩܘܕܫܐ ܕܠܐ ܢܡܠܠܘܢ ܡܠܬܐ ܕܐܠܗܐ ܒܐܤܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 7, 'ܘܟܕ ܐܬܘ ܠܡܘܤܝܐ ܐܬܪܐ ܨܒܝܢ ܗܘܘ ܕܢܐܙܠܘܢ ܡܢ ܬܡܢ ܠܒܝܬܘܢܝܐ ܘܠܐ ܐܦܤܬ ܠܗܘܢ ܪܘܚܗ ܕܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 8, 'ܘܟܕ ܢܦܩܘ ܡܢ ܡܘܤܝܐ ܢܚܬܘ ܠܗܘܢ ܠܛܪܘܐܤ ܐܬܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 9, 'ܘܒܚܙܘܐ ܕܠܠܝܐ ܐܬܚܙܝ ܠܦܘܠܘܤ ܐܝܟ ܓܒܪܐ ܚܕ ܡܩܕܘܢܝܐ ܕܩܐܡ ܘܒܥܐ ܡܢܗ ܟܕ ܐܡܪ ܕܬܐ ܠܡܩܕܘܢܝܐ ܘܥܕܪܝܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 10, 'ܟܕ ܕܝܢ ܚܙܐ ܦܘܠܘܤ ܗܢܐ ܚܙܘܐ ܡܚܕܐ ܨܒܝܢ ܠܡܦܩ ܠܡܩܕܘܢܝܐ ܡܛܠ ܕܐܤܬܟܠܢ ܕܡܪܢ ܩܪܢ ܕܢܤܒܪ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 11, 'ܘܪܕܝܢ ܡܢ ܛܪܘܐܤ ܘܬܪܨܢ ܠܤܡܬܪܩܐ ܘܡܢ ܬܡܢ ܠܝܘܡܐ ܐܚܪܢܐ ܐܬܝܢ ܠܢܐܦܘܠܝܤ ܡܕܝܢܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 12, 'ܘܡܢ ܬܡܢ ܠܦܝܠܝܦܘܤ ܕܗܝ ܗܝ ܪܫܐ ܕܡܩܕܘܢܝܐ ܘܐܝܬܝܗ ܩܘܠܘܢܝܐ ܗܘܝܢ ܕܝܢ ܒܗ ܒܗܕܐ ܡܕܝܢܬܐ ܝܘܡܬܐ ܝܕܝܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 13, 'ܘܢܦܩܢ ܒܝܘܡܐ ܕܫܒܬܐ ܠܒܪ ܡܢ ܬܪܥܐ ܕܡܕܝܢܬܐ ܥܠ ܝܕ ܢܗܪܐ ܡܛܠ ܕܬܡܢ ܡܬܚܙܐ ܗܘܐ ܒܝܬ ܨܠܘܬܐ ܘܟܕ ܝܬܒܢ ܡܡܠܠܝܢ ܗܘܝܢ ܥܡ ܢܫܐ ܕܟܢܝܫܢ ܗܘܝ ܬܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 14, 'ܘܐܢܬܬܐ ܚܕܐ ܡܙܒܢܬ ܐܪܓܘܢܐ ܕܕܚܠܐ ܗܘܬ ܡܢ ܐܠܗܐ ܫܡܗ ܗܘܐ ܠܘܕܝܐ ܡܢ ܬܐܘܛܝܪܐ ܡܕܝܢܬܐ ܕܗܕܐ ܦܬܚ ܠܒܗ ܡܪܢ ܘܫܡܥܐ ܗܘܬ ܡܕܡ ܕܐܡܪ ܦܘܠܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 15, 'ܘܥܡܕܬ ܗܘܬ ܗܝ ܘܒܢܝ ܒܝܬܗ ܘܒܥܝܐ ܗܘܬ ܡܢܢ ܘܐܡܪܐ ܕܐܢ ܗܘ ܕܫܪܝܪܐܝܬ ܬܟܝܠܝܢ ܐܢܬܘܢ ܕܗܝܡܢܬ ܒܡܪܢ ܬܘ ܫܪܘ ܠܟܘܢ ܒܒܝܬܝ ܘܤܓܝ ܐܠܨܬܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 16, 'ܘܗܘܐ ܕܟܕ ܐܙܠܝܢܢ ܠܒܝܬ ܨܠܘܬܐ ܦܓܥܬ ܒܢ ܥܠܝܡܬܐ ܚܕܐ ܕܐܝܬ ܗܘܐ ܠܗ ܪܘܚܐ ܕܩܨܡܐ ܘܥܒܕܐ ܗܘܬ ܠܡܪܝܗ ܬܐܓܘܪܬܐ ܤܓܝܐܬܐ ܒܩܨܡܐ ܕܩܨܡܐ ܗܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 17, 'ܘܐܬܝܐ ܗܘܬ ܒܬܪ ܦܘܠܘܤ ܘܒܬܪܢ ܘܩܥܝܐ ܗܘܬ ܘܐܡܪܐ ܗܠܝܢ ܐܢܫܐ ܥܒܕܘܗܝ ܐܢܘܢ ܕܐܠܗܐ ܡܪܝܡܐ ܘܡܤܒܪܝܢ ܠܟܘܢ ܐܘܪܚܐ ܕܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 18, 'ܘܗܟܢܐ ܥܒܕܐ ܗܘܬ ܝܘܡܬܐ ܤܓܝܐܐ ܘܐܬܬܦܝܪ ܦܘܠܘܤ ܘܐܡܪ ܠܗ ܠܪܘܚܐ ܗܝ ܦܩܕ ܐܢܐ ܠܟܝ ܒܫܡܗ ܕܝܫܘܥ ܡܫܝܚܐ ܕܬܦܩܝܢ ܡܢܗ ܘܒܗ ܒܫܥܬܐ ܢܦܩܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 19, 'ܘܟܕ ܚܙܘ ܡܪܝܗ ܕܢܦܩ ܠܗ ܡܢܗ ܤܒܪܐ ܕܬܐܓܘܪܬܗܘܢ ܐܚܕܘܗܝ ܠܦܘܠܘܤ ܘܠܫܝܠܐ ܘܢܓܕܘ ܐܝܬܝܘ ܐܢܘܢ ܠܫܘܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 20, 'ܘܩܪܒܘ ܐܢܘܢ ܠܐܤܛܪܛܓܐ ܘܠܪܫܐ ܕܡܕܝܢܬܐ ܘܐܡܪܝܢ ܗܘܘ ܕܗܠܝܢ ܐܢܫܐ ܡܕܠܚܝܢ ܠܗ ܠܡܕܝܢܬܢ ܡܛܠ ܕܝܗܘܕܝܐ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 21, 'ܘܡܟܪܙܝܢ ܠܢ ܥܝܕܐ ܐܝܠܝܢ ܕܠܐ ܡܦܤ ܠܢ ܠܡܩܒܠܘ ܘܠܡܥܒܕ ܡܛܠ ܕܪܗܘܡܝܐ ܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 22, 'ܘܟܢܫܐ ܤܓܝܐܐ ܐܬܟܢܫܘ ܥܠܝܗܘܢ ܗܝܕܝܢ ܐܤܛܪܛܓܐ ܤܕܩܘ ܢܚܬܝܗܘܢ ܘܦܩܕܘ ܕܢܢܓܕܘܢ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 23, 'ܘܟܕ ܢܓܕܘ ܐܢܘܢ ܤܓܝ ܐܪܡܝܘ ܐܢܘܢ ܒܝܬ ܐܤܝܪܐ ܘܦܩܕܘ ܠܢܛܪ ܒܝܬ ܐܤܝܪܐ ܕܢܛܪ ܐܢܘܢ ܙܗܝܪܐܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 24, 'ܗܘ ܕܝܢ ܟܕ ܩܒܠ ܗܢܐ ܦܘܩܕܢܐ ܐܥܠ ܚܒܫ ܐܢܘܢ ܒܒܝܬܐ ܓܘܝܐ ܕܒܝܬ ܐܤܝܪܐ ܘܐܤܪ ܪܓܠܝܗܘܢ ܒܤܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 25, 'ܘܒܦܠܓܗ ܕܠܠܝܐ ܦܘܠܘܤ ܘܫܝܠܐ ܡܨܠܝܢ ܗܘܘ ܘܡܫܒܚܝܢ ܗܘܘ ܠܐܠܗܐ ܘܫܡܥܝܢ ܗܘܘ ܠܗܘܢ ܐܤܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 26, 'ܘܡܢ ܫܠܝܐ ܙܘܥܐ ܪܒܐ ܗܘܐ ܘܐܬܬܙܝܥ ܫܬܐܤܐ ܕܒܝܬ ܐܤܝܪܐ ܘܐܬܦܬܚܘ ܡܚܕܐ ܬܪܥܐ ܟܠܗܘܢ ܘܐܤܘܪܝܗܘܢ ܕܟܠܗܘܢ ܐܫܬܪܝܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 27, 'ܘܟܕ ܐܬܬܥܝܪ ܢܛܪ ܒܝܬ ܐܤܝܪܐ ܘܚܙܐ ܕܦܬܝܚܝܢ ܬܪܥܐ ܕܒܝܬ ܐܤܝܪܐ ܢܤܒ ܤܦܤܪܐ ܘܒܥܐ ܕܢܩܛܘܠ ܢܦܫܗ ܡܛܠ ܕܤܒܪ ܗܘܐ ܕܥܪܩܘ ܠܗܘܢ ܐܤܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 28, 'ܘܩܪܝܗܝ ܦܘܠܘܤ ܒܩܠܐ ܪܡܐ ܘܐܡܪ ܠܗ ܠܐ ܬܥܒܕ ܠܢܦܫܟ ܡܕܡ ܕܒܝܫ ܡܛܠ ܕܟܠܢ ܗܪܟܐ ܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 29, 'ܘܐܢܗܪ ܠܗ ܫܪܓܐ ܘܫܘܪ ܘܥܠ ܟܕ ܪܐܬ ܘܢܦܠ ܥܠ ܪܓܠܝܗܘܢ ܕܦܘܠܘܤ ܘܕܫܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 30, 'ܘܐܦܩ ܐܢܘܢ ܠܒܪ ܘܐܡܪ ܗܘܐ ܠܗܘܢ ܡܪܝ ܡܢܐ ܘܠܐ ܠܝ ܠܡܥܒܕ ܐܝܟ ܕܐܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 31, 'ܘܗܢܘܢ ܐܡܪܝܢ ܗܘܘ ܠܗ ܗܝܡܢ ܒܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܘܬܚܐ ܐܢܬ ܘܒܝܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 32, 'ܘܡܠܠܘ ܥܡܗ ܡܠܬܐ ܕܡܪܝܐ ܘܥܡ ܟܠܗܘܢ ܒܢܝ ܒܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 33, 'ܘܒܗ ܒܫܥܬܐ ܒܗ ܒܠܠܝܐ ܕܒܪ ܐܤܚܝ ܐܢܘܢ ܡܢ ܢܓܕܗܘܢ ܘܒܪ ܫܥܬܗ ܥܡܕ ܗܘ ܘܒܢܝ ܒܝܬܗ ܟܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 34, 'ܘܕܒܪ ܐܤܩ ܐܢܘܢ ܠܒܝܬܗ ܘܤܡ ܠܗܘܢ ܦܬܘܪܐ ܘܪܘܙ ܗܘܐ ܗܘ ܘܒܢܝ ܒܝܬܗ ܒܗܝܡܢܘܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 35, 'ܘܟܕ ܗܘܐ ܨܦܪܐ ܫܕܪܘ ܐܤܛܪܛܓܐ ܠܫܩܠܝ ܫܒܛܐ ܕܢܐܡܪܘܢ ܠܪܒ ܐܤܝܪܐ ܫܪܝ ܠܗܠܝܢ ܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 36, 'ܘܟܕ ܫܡܥ ܪܒ ܒܝܬ ܐܤܝܪܐ ܥܠ ܐܡܪ ܠܗ ܗܝ ܗܕܐ ܡܠܬܐ ܠܦܘܠܘܤ ܕܫܕܪܘ ܐܤܛܪܛܓܐ ܐܝܟ ܕܬܫܬܪܘܢ ܘܗܫܐ ܦܘܩܘ ܙܠܘ ܒܫܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 37, 'ܐܡܪ ܠܗ ܦܘܠܘܤ ܕܠܐ ܤܟܠܘ ܢܓܕܘܢ ܠܥܝܢ ܥܠܡܐ ܟܠܗ ܠܐܢܫܐ ܪܗܘܡܝܐ ܘܐܪܡܝܘܢ ܒܝܬ ܐܤܝܪܐ ܘܗܫܐ ܡܛܫܝܐܝܬ ܡܦܩܝܢ ܠܢ ܠܐ ܓܝܪ ܐܠܐ ܗܢܘܢ ܢܐܬܘܢ ܢܦܩܘܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 38, 'ܘܐܙܠܘ ܫܩܠܝ ܫܒܛܐ ܘܐܡܪܘ ܠܐܤܛܪܛܓܐ ܡܠܐ ܗܠܝܢ ܕܐܬܐܡܪ ܠܗܘܢ ܘܟܕ ܫܡܥܘ ܕܪܗܘܡܝܐ ܐܢܘܢ ܕܚܠܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 39, 'ܘܐܬܘ ܠܘܬܗܘܢ ܘܒܥܘ ܡܢܗܘܢ ܕܢܦܩܘܢ ܘܢܫܢܘܢ ܠܗܘܢ ܡܢ ܡܕܝܢܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 40, 'ܘܟܕ ܢܦܩܘ ܡܢ ܒܝܬ ܐܤܝܪܐ ܥܠܘ ܠܘܬ ܠܘܕܝܐ ܘܚܙܘ ܬܡܢ ܠܐܚܐ ܘܒܝܐܘ ܐܢܘܢ ܘܢܦܩܘ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 1, 'ܘܥܒܪܘ ܥܠ ܐܡܦܝܦܘܠܝܤ ܘܐܦܠܘܢܝܐ ܡܕܝܢܬܐ ܘܐܬܘ ܠܬܤܠܘܢܝܩܐ ܐܝܟܐ ܕܐܝܬ ܗܘܐ ܟܢܘܫܬܐ ܕܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 2, 'ܘܥܠ ܦܘܠܘܤ ܐܝܟܢܐ ܕܡܥܕ ܗܘܐ ܠܘܬܗܘܢ ܘܫܒܐ ܬܠܬ ܡܠܠ ܥܡܗܘܢ ܡܢ ܟܬܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 3, 'ܟܕ ܡܦܫܩ ܗܘܐ ܘܡܚܘܐ ܕܥܬܝܕ ܗܘܐ ܡܫܝܚܐ ܕܢܚܫ ܘܕܢܩܘܡ ܡܢ ܒܝܬ ܡܝܬܐ ܘܗܘܝܘ ܝܫܘܥ ܡܫܝܚܐ ܗܢܐ ܕܡܤܒܪ ܐܢܐ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 4, 'ܘܐܢܫܝܢ ܡܢܗܘܢ ܗܝܡܢܘ ܘܢܩܦܘ ܠܦܘܠܘܤ ܘܠܫܝܠܐ ܘܤܓܝܐܐ ܡܢ ܝܘܢܝܐ ܐܝܠܝܢ ܕܕܚܠܝܢ ܗܘܘ ܡܢ ܐܠܗܐ ܘܐܦ ܢܫܐ ܝܕܝܥܬܐ ܠܐ ܙܥܘܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 5, 'ܘܚܤܡܘ ܗܘܘ ܝܗܘܕܝܐ ܘܐܩܦܘ ܠܗܘܢ ܐܢܫܐ ܒܝܫܐ ܡܢ ܫܘܩܐ ܕܡܕܝܢܬܐ ܘܥܒܕܘ ܐܟܠܘܤ ܤܓܝܐܐ ܘܕܠܚܘ ܗܘܘ ܠܡܕܝܢܬܐ ܘܐܬܘ ܘܩܡܘ ܠܗܘܢ ܥܠ ܒܝܬܗ ܕܐܝܤܘܢ ܘܒܥܝܢ ܗܘܘ ܕܢܦܩܘܢ ܐܢܘܢ ܡܢ ܬܡܢ ܘܢܫܠܡܘܢ ܐܢܘܢ ܠܐܟܠܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 6, 'ܘܟܕ ܠܐ ܐܫܟܚܘ ܐܢܘܢ ܬܡܢ ܓܪܘܗܝ ܗܘܘ ܠܐܝܤܘܢ ܘܠܐܚܐ ܕܐܝܬ ܗܘܘ ܬܡܢ ܘܐܝܬܝܘ ܐܢܘܢ ܠܘܬ ܪܫܐ ܕܡܕܝܢܬܐ ܟܕ ܩܥܝܢ ܗܘܘ ܕܗܠܝܢ ܐܢܘܢ ܕܠܟܠܗ ܐܪܥܐ ܕܠܚܘ ܘܗܐ ܬܘܒ ܠܗܪܟܐ ܐܬܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 7, 'ܘܡܩܒܠܢܗܘܢ ܗܢܘ ܐܝܤܘܢ ܘܟܠܗܘܢ ܗܠܝܢ ܠܘܩܒܠ ܦܘܩܕܢܘܗܝ ܕܩܤܪ ܩܝܡܝܢ ܟܕ ܐܡܪܝܢ ܕܐܝܬ ܡܠܟܐ ܐܚܪܢܐ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 8, 'ܐܬܕܠܚܘ ܕܝܢ ܪܫܐ ܕܡܕܝܢܬܐ ܘܟܠܗ ܥܡܐ ܟܕ ܫܡܥܘ ܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 9, 'ܘܢܤܒܘ ܥܪܒܐ ܡܢ ܐܝܤܘܢ ܘܐܦ ܡܢ ܐܚܐ ܘܗܝܕܝܢ ܫܪܘ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 10, 'ܐܚܐ ܕܝܢ ܒܪ ܫܥܬܗ ܒܗ ܒܠܠܝܐ ܫܪܘ ܠܦܘܠܘܤ ܘܠܫܝܠܐ ܠܒܪܘܐܐ ܡܕܝܢܬܐ ܘܟܕ ܐܬܘ ܠܬܡܢ ܥܐܠܝܢ ܗܘܘ ܠܟܢܘܫܬܐ ܕܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 11, 'ܚܐܪܝܝܢ ܗܘܘ ܓܝܪ ܗܢܘܢ ܝܗܘܕܝܐ ܕܬܡܢ ܡܢ ܝܗܘܕܝܐ ܗܢܘܢ ܕܐܝܬ ܗܘܘ ܒܬܤܠܘܢܝܩܐ ܘܫܡܥܝܢ ܗܘܘ ܡܢܗܘܢ ܡܠܬܐ ܟܠܝܘܡ ܚܕܝܐܝܬ ܟܕ ܡܦܪܫܝܢ ܗܘܘ ܡܢ ܟܬܒܐ ܕܐܢ ܗܠܝܢ ܗܟܢܐ ܐܝܬܝܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 12, 'ܘܤܓܝܐܐ ܡܢܗܘܢ ܗܝܡܢܘ ܘܗܟܢܐ ܐܦ ܡܢ ܝܘܢܝܐ ܓܒܪܐ ܤܓܝܐܐ ܘܢܫܐ ܝܕܝܥܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 13, 'ܘܟܕ ܝܕܥܘ ܗܢܘܢ ܝܗܘܕܝܐ ܕܡܢ ܬܤܠܘܢܝܩܐ ܕܡܠܬܗ ܕܐܠܗܐ ܐܬܟܪܙܬ ܡܢ ܦܘܠܘܤ ܒܒܪܘܐܐ ܡܕܝܢܬܐ ܐܬܘ ܐܦ ܠܬܡܢ ܘܠܐ ܫܠܝܘ ܠܡܙܥܘ ܘܠܡܕܠܚܘ ܠܐܢܫܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 14, 'ܘܠܦܘܠܘܤ ܫܪܐܘܗܝ ܐܚܐ ܕܢܚܘܬ ܠܗ ܠܝܡܐ ܘܩܘܝ ܗܘܐ ܒܗ ܒܡܕܝܢܬܐ ܗܝ ܫܝܠܐ ܘܛܝܡܬܐܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 15, 'ܘܗܢܘܢ ܕܐܬܠܘܝܘ ܠܗ ܠܦܘܠܘܤ ܐܬܘ ܥܡܗ ܥܕܡܐ ܠܐܬܢܘܤ ܡܕܝܢܬܐ ܘܟܕ ܢܦܩܝܢ ܡܢ ܨܐܕܘܗܝ ܩܒܠܘ ܡܢܗ ܐܓܪܬܐ ܠܘܬ ܫܝܠܐ ܘܛܝܡܬܐܘܤ ܕܒܥܓܠ ܢܐܙܠܘܢ ܠܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 16, 'ܗܘ ܕܝܢ ܦܘܠܘܤ ܟܕ ܡܩܘܐ ܗܘܐ ܒܐܬܢܘܤ ܡܬܡܪܡܪ ܗܘܐ ܒܪܘܚܗ ܟܕ ܚܙܐ ܗܘܐ ܕܡܕܝܢܬܐ ܟܠܗ ܡܠܝܐ ܦܬܟܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 17, 'ܘܡܡܠܠ ܗܘܐ ܒܟܢܘܫܬܐ ܥܡ ܝܗܘܕܝܐ ܘܥܡ ܐܝܠܝܢ ܕܕܚܠܝܢ ܡܢ ܐܠܗܐ ܘܒܫܘܩܐ ܥܡ ܐܝܠܝܢ ܕܡܤܬܩܒܠܝܢ ܗܘܘ ܟܠܝܘܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 18, 'ܘܐܦ ܦܝܠܤܘܦܐ ܕܡܢ ܝܘܠܦܢܗ ܕܐܦܝܩܘܪܤ ܘܐܚܪܢܐ ܕܡܬܩܪܝܢ ܤܛܘܐܝܩܘ ܕܪܫܝܢ ܗܘܘ ܥܡܗ ܘܐܢܫ ܐܢܫ ܡܢܗܘܢ ܐܡܪܝܢ ܗܘܘ ܡܢܐ ܨܒܐ ܗܢܐ ܡܠܩܛ ܡܠܐ ܘܐܚܪܢܐ ܐܡܪܝܢ ܗܘܘ ܕܐܠܗܐ ܢܘܟܪܝܐ ܡܟܪܙ ܡܛܠ ܕܠܝܫܘܥ ܘܠܩܝܡܬܗ ܡܟܪܙ ܗܘܐ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 19, 'ܘܐܚܕܘܗܝ ܘܐܝܬܝܘܗܝ ܠܒܝܬ ܕܝܢܐ ܕܡܬܩܪܐ ܐܪܝܘܤ ܦܓܘܤ ܟܕ ܐܡܪܝܢ ܠܗ ܡܫܟܚܝܢܢ ܠܡܕܥ ܡܢܘ ܗܢܐ ܝܘܠܦܢܐ ܚܕܬܐ ܕܡܟܪܙ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 20, 'ܡܠܐ ܓܝܪ ܢܘܟܪܝܬܐ ܙܪܥ ܐܢܬ ܒܡܫܡܥܬܢ ܘܨܒܝܢܢ ܠܡܕܥ ܡܢܐ ܐܢܝܢ ܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 21, 'ܐܬܢܝܐ ܕܝܢ ܟܠܗܘܢ ܘܐܝܠܝܢ ܕܐܬܝܢ ܠܬܡܢ ܢܘܟܪܝܐ ܥܠ ܡܕܡ ܐܚܪܝܢ ܠܐ ܒܛܝܠ ܠܗܘܢ ܐܠܐ ܠܡܐܡܪ ܘܠܡܫܡܥ ܡܕܡ ܚܕܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 22, 'ܘܟܕ ܩܡ ܦܘܠܘܤ ܒܐܪܝܘܤ ܦܓܘܤ ܐܡܪ ܓܒܪܐ ܐܬܢܝܐ ܚܙܐ ܐܢܐ ܠܟܘܢ ܕܒܟܠܗܝܢ ܝܬܝܪܝܢ ܐܢܬܘܢ ܒܕܚܠܬ ܫܐܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 23, 'ܟܕ ܓܝܪ ܡܬܟܪܟ ܗܘܝܬ ܘܚܙܐ ܗܘܝܬ ܒܝܬ ܕܚܠܬܟܘܢ ܐܫܟܚܬ ܥܠܬܐ ܚܕܐ ܕܟܬܝܒ ܗܘܐ ܥܠܝܗ ܕܐܠܗܐ ܓܢܝܙܐ ܗܘ ܗܟܝܠ ܕܟܕ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܕܚܠܝܢ ܐܢܬܘܢ ܠܗ ܠܗ ܠܗܢܐ ܐܢܐ ܡܤܒܪ ܐܢܐ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 24, 'ܐܠܗܐ ܓܝܪ ܕܥܒܕ ܥܠܡܐ ܘܟܠ ܡܐ ܕܐܝܬ ܒܗ ܘܗܘܝܘ ܡܪܐ ܕܫܡܝܐ ܘܕܐܪܥܐ ܒܗܝܟܠܐ ܕܥܒܕ ܐܝܕܝܐ ܠܐ ܫܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 25, 'ܘܠܐ ܡܫܬܡܫ ܡܢ ܐܝܕܝ ܒܢܝܢܫܐ ܘܥܠ ܡܕܡ ܠܐ ܤܢܝܩ ܡܛܠ ܕܗܘ ܝܗܒ ܠܟܠܢܫ ܚܝܐ ܘܢܦܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 26, 'ܘܡܢ ܚܕ ܕܡ ܥܒܕ ܥܠܡܐ ܟܠܗ ܕܒܢܝܢܫܐ ܕܢܗܘܘܢ ܥܡܪܝܢ ܥܠ ܐܦܝ ܐܪܥܐ ܟܠܗ ܘܦܪܫ ܙܒܢܐ ܒܦܘܩܕܢܗ ܘܤܡ ܬܚܘܡܐ ܕܥܘܡܪܐ ܕܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 27, 'ܕܢܗܘܘܢ ܒܥܝܢ ܠܐܠܗܐ ܘܡܥܩܒܝܢ ܘܡܢ ܒܪܝܬܗ ܡܫܟܚܝܢ ܠܗ ܡܛܠ ܕܐܦ ܠܐ ܗܘܐ ܪܚܝܩ ܡܢ ܟܠ ܡܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 28, 'ܒܗ ܗܘ ܓܝܪ ܚܝܝܢܢ ܘܡܬܬܙܝܥܝܢܢ ܘܐܝܬܝܢ ܐܝܟ ܕܐܦ ܐܢܫܐ ܡܢ ܚܟܝܡܐ ܕܠܘܬܟܘܢ ܐܡܪܘ ܕܡܢܗ ܗܘ ܛܘܗܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 29, 'ܐܢܫܐ ܗܟܝܠ ܕܛܘܗܡܢ ܡܢ ܐܠܗܐ ܗܘ ܠܐ ܚܝܒܝܢܢ ܠܡܤܒܪ ܕܠܕܗܒܐ ܐܘ ܠܤܐܡܐ ܐܘ ܠܟܐܦܐ ܕܓܠܝܦܐ ܒܐܘܡܢܘܬܐ ܘܒܝܕܥܬܐ ܕܒܪܢܫܐ ܕܡܝܐ ܐܠܗܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 30, 'ܙܒܢܐ ܓܝܪ ܕܛܥܝܘܬܐ ܐܥܒܪ ܐܠܗܐ ܘܒܙܒܢܐ ܗܢܐ ܡܦܩܕ ܠܟܠܗܘܢ ܒܢܝܢܫܐ ܕܟܠ ܐܢܫ ܒܟܠ ܕܘܟܐ ܢܬܘܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 31, 'ܡܛܠ ܕܐܩܝܡ ܝܘܡܐ ܕܒܗ ܥܬܝܕ ܕܢܕܘܢ ܐܪܥܐ ܟܠܗ ܒܟܐܢܘܬܐ ܒܝܕ ܓܒܪܐ ܐܝܢܐ ܕܦܪܫ ܘܐܦܢܝ ܠܟܠ ܐܢܫ ܠܗܝܡܢܘܬܗ ܒܕܐܩܝܡܗ ܡܢ ܒܝܬ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 32, 'ܘܟܕ ܫܡܥܘ ܩܝܡܬܐ ܕܡܢ ܒܝܬ ܡܝܬܐ ܡܢܗܘܢ ܡܡܝܩܝܢ ܗܘܘ ܘܡܢܗܘܢ ܐܡܪܝܢ ܗܘܘ ܒܙܒܢ ܐܚܪܝܢ ܫܡܥܝܢܢ ܠܟ ܥܠ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 33, 'ܘܗܟܢܐ ܢܦܩ ܦܘܠܘܤ ܡܢ ܒܝܢܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 34, 'ܘܐܢܫܝܢ ܡܢܗܘܢ ܢܩܦܘܗܝ ܘܗܝܡܢܘ ܚܕ ܕܝܢ ܡܢܗܘܢ ܐܝܬܘܗܝ ܗܘܐ ܕܝܢܘܤܝܘܤ ܡܢ ܕܝܢܐ ܕܐܪܝܘܤ ܦܓܘܤ ܘܐܢܬܬܐ ܚܕܐ ܕܫܡܗ ܗܘܐ ܕܡܪܝܤ ܘܐܚܪܢܐ ܥܡܗܘܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 1, 'ܘܟܕ ܢܦܩ ܦܘܠܘܤ ܡܢ ܐܬܢܘܤ ܐܬܐ ܠܗ ܠܩܘܪܢܬܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 2, 'ܘܐܫܟܚ ܬܡܢ ܓܒܪܐ ܚܕ ܝܗܘܕܝܐ ܕܫܡܗ ܗܘܐ ܐܩܠܘܤ ܕܐܝܬܘܗܝ ܗܘܐ ܡܢ ܦܢܛܘܤ ܐܬܪܐ ܕܒܗ ܒܗܘ ܙܒܢܐ ܐܬܐ ܗܘܐ ܡܢ ܐܬܪܐ ܕܐܝܛܠܝܐ ܗܘ ܘܦܪܝܤܩܠܐ ܐܢܬܬܗ ܡܛܠ ܕܦܩܕ ܗܘܐ ܩܠܘܕܝܘܤ ܩܤܪ ܕܢܦܩܘܢ ܟܠܗܘܢ ܝܗܘܕܝܐ ܡܢ ܪܗܘܡܐ ܘܐܬܩܪܒ ܠܘܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 3, 'ܘܡܛܠ ܕܒܪ ܐܘܡܢܘܬܗܘܢ ܗܘܐ ܫܪܐ ܠܗ ܠܘܬܗܘܢ ܘܦܠܚ ܗܘܐ ܥܡܗܘܢ ܒܐܘܡܢܘܬܗܘܢ ܕܝܢ ܠܘܠܪܐ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 4, 'ܘܡܡܠܠ ܗܘܐ ܒܟܢܘܫܬܐ ܒܟܠ ܫܒܐ ܘܡܦܝܤ ܗܘܐ ܠܝܗܘܕܝܐ ܘܠܚܢܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 5, 'ܘܟܕ ܐܬܘ ܗܘܘ ܡܢ ܡܩܕܘܢܝܐ ܫܝܠܐ ܘܛܝܡܬܐܘܤ ܐܠܝܨ ܗܘܐ ܒܡܠܬܐ ܗܘ ܦܘܠܘܤ ܡܛܠ ܕܩܝܡܝܢ ܗܘܘ ܠܩܘܒܠܗ ܝܗܘܕܝܐ ܘܡܓܕܦܝܢ ܗܘܘ ܟܕ ܡܤܗܕ ܗܘܐ ܠܗܘܢ ܕܝܫܘܥ ܗܘܝܘ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 6, 'ܘܢܦܨ ܡܐܢܘܗܝ ܘܐܡܪ ܠܗܘܢ ܡܢ ܗܫܐ ܐܢܐ ܕܟܐ ܐܢܐ ܐܙܠ ܐܢܐ ܠܝ ܠܘܬ ܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 7, 'ܘܢܦܩ ܡܢ ܬܡܢ ܘܥܠ ܠܒܝܬܗ ܕܓܒܪܐ ܕܫܡܗ ܛܛܘܤ ܐܝܢܐ ܕܕܚܠ ܗܘܐ ܡܢ ܐܠܗܐ ܘܒܝܬܗ ܢܩܝܦ ܗܘܐ ܠܟܢܘܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 8, 'ܘܟܪܝܤܦܘܤ ܪܒ ܟܢܘܫܬܐ ܗܝܡܢ ܗܘܐ ܒܡܪܢ ܗܘ ܘܒܢܝ ܒܝܬܗ ܟܠܗܘܢ ܘܤܓܝܐܐ ܩܘܪܢܬܝܐ ܫܡܥܝܢ ܗܘܘ ܘܡܗܝܡܢܝܢ ܒܐܠܗܐ ܘܥܡܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 9, 'ܘܐܡܪ ܡܪܝܐ ܒܚܙܘܐ ܠܦܘܠܘܤ ܠܐ ܬܕܚܠ ܐܠܐ ܡܠܠ ܘܠܐ ܬܫܬܘܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 10, 'ܡܛܠ ܕܐܢܐ ܥܡܟ ܐܢܐ ܘܐܢܫ ܠܐ ܡܫܟܚ ܠܡܗܪܘܬܟ ܘܥܡܐ ܤܓܝܐܐ ܐܝܬ ܠܝ ܒܡܕܝܢܬܐ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 11, 'ܝܬܒ ܗܘܐ ܕܝܢ ܫܢܬܐ ܚܕܐ ܘܝܪܚܐ ܫܬܐ ܒܩܘܪܢܬܘܤ ܘܡܠܦ ܗܘܐ ܠܗܘܢ ܡܠܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 12, 'ܘܟܕ ܐܝܬܘܗܝ ܗܘܐ ܓܐܠܝܘܢ ܐܢܬܘܦܛܘܤ ܕܐܟܐܝܐ ܐܬܟܢܫܘ ܐܟܚܕܐ ܝܗܘܕܝܐ ܥܠ ܦܘܠܘܤ ܘܐܝܬܝܘܗܝ ܩܕܡ ܒܝܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 13, 'ܟܕ ܐܡܪܝܢ ܕܗܢܐ ܠܒܪ ܡܢ ܢܡܘܤܐ ܡܦܝܤ ܠܒܢܝܢܫܐ ܕܢܗܘܘܢ ܕܚܠܝܢ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 14, 'ܘܟܕ ܒܥܐ ܗܘܐ ܦܘܠܘܤ ܕܢܦܬܚ ܦܘܡܗ ܘܢܡܠܠ ܐܡܪ ܓܐܠܝܘܢ ܠܝܗܘܕܝܐ ܐܠܘ ܥܠ ܡܕܡ ܕܒܝܫ ܐܘ ܕܢܟܝܠ ܐܘ ܕܤܢܐ ܡܩܛܪܓܝܢ ܗܘܝܬܘܢ ܐܘ ܝܗܘܕܝܐ ܒܘܠܝܬܐ ܡܩܒܠ ܗܘܝܬ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 15, 'ܐܢ ܕܝܢ ܙܛܡܐ ܐܢܘܢ ܥܠ ܡܠܬܐ ܘܥܠ ܫܡܗܐ ܘܥܠ ܢܡܘܤܐ ܕܝܠܟܘܢ ܐܢܬܘܢ ܝܕܥܝܢ ܐܢܬܘܢ ܒܝܢܬܟܘܢ ܐܢܐ ܓܝܪ ܠܐ ܨܒܐ ܐܢܐ ܕܐܗܘܐ ܕܝܢܐ ܕܗܠܝܢ ܨܒܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 16, 'ܘܛܪܕ ܐܢܘܢ ܡܢ ܒܝܡ ܕܝܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 17, 'ܘܐܚܕܘ ܗܘܘ ܟܠܗܘܢ ܚܢܦܐ ܠܤܘܤܬܢܝܤ ܩܫܝܫܐ ܕܟܢܘܫܬܐ ܘܡܚܝܢ ܗܘܘ ܠܗ ܩܕܡ ܒܝܡ ܘܓܐܠܝܘܢ ܡܗܡܐ ܗܘܐ ܒܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 18, 'ܘܟܕ ܗܘܐ ܦܘܠܘܤ ܬܡܢ ܝܘܡܬܐ ܤܓܝܐܐ ܝܗܒ ܫܠܡܐ ܠܐܚܐ ܘܪܕܐ ܒܝܡܐ ܕܢܐܙܠ ܠܤܘܪܝܐ ܘܐܬܘ ܥܡܗ ܦܪܝܤܩܠܐ ܘܐܩܠܘܤ ܟܕ ܤܦܪ ܪܫܗ ܒܩܢܟܪܐܤ ܡܛܠ ܕܢܕܪܐ ܢܕܝܪ ܗܘܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 19, 'ܘܡܛܝܘ ܠܐܦܤܘܤ ܘܥܠ ܦܘܠܘܤ ܠܟܢܘܫܬܐ ܘܡܡܠܠ ܗܘܐ ܥܡ ܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 20, 'ܘܒܥܝܢ ܗܘܘ ܡܢܗ ܕܢܓܪ ܠܘܬܗܘܢ ܘܠܐ ܐܬܛܦܝܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 21, 'ܟܕ ܐܡܪ ܕܘܠܐ ܠܝ ܐܡܝܢܐܝܬ ܕܥܐܕܐ ܕܐܬܐ ܒܐܘܪܫܠܡ ܐܥܒܕܝܘܗܝ ܘܐܢ ܐܠܗܐ ܢܨܒܐ ܐܦܢܐ ܬܘܒ ܠܘܬܟܘܢ ܘܠܐܩܠܘܤ ܘܠܦܪܝܤܩܠܐ ܫܒܩ ܐܢܘܢ ܒܐܦܤܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 22, 'ܘܗܘ ܪܕܐ ܒܝܡܐ ܘܐܬܐ ܠܩܤܪܝܐ ܘܤܠܩ ܘܫܐܠ ܒܫܠܡܐ ܕܒܢܝ ܥܕܬܐ ܘܐܙܠ ܠܗ ܠܐܢܛܝܘܟܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 23, 'ܘܟܕ ܗܘܐ ܬܡܢ ܝܘܡܬܐ ܝܕܝܥܐ ܢܦܩ ܘܐܬܟܪܟ ܒܬܪ ܒܬܪ ܒܐܬܪܐ ܕܓܠܛܝܐ ܘܕܦܪܘܓܝܐ ܟܕ ܡܩܝܡ ܗܘܐ ܠܟܠܗܘܢ ܬܠܡܝܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 24, 'ܘܓܒܪܐ ܚܕ ܕܫܡܗ ܗܘܐ ܐܦܠܘ ܝܗܘܕܝܐ ܕܐܝܬܘܗܝ ܗܘܐ ܛܘܗܡܗ ܡܢ ܐܠܟܤܢܕܪܝܐ ܘܪܕܐ ܗܘܐ ܒܡܠܬܐ ܘܡܕܩ ܗܘܐ ܒܟܬܒܐ ܐܬܐ ܠܐܦܤܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 25, 'ܗܢܐ ܡܬܠܡܕ ܗܘܐ ܠܐܘܪܚܗ ܕܡܪܝܐ ܘܪܬܚ ܗܘܐ ܒܪܘܚ ܘܡܡܠܠ ܗܘܐ ܘܡܠܦ ܡܠܝܐܝܬ ܥܠ ܝܫܘܥ ܟܕ ܡܕܡ ܠܐ ܝܕܥ ܗܘܐ ܐܠܐ ܐܢ ܡܥܡܘܕܝܬܐ ܕܝܘܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 26, 'ܘܫܪܝ ܥܝܢ ܒܓܠܐ ܡܡܠܠ ܒܟܢܘܫܬܐ ܘܟܕ ܫܡܥܘܗܝ ܐܩܠܘܤ ܘܦܪܝܤܩܠܐ ܐܝܬܝܘܗܝ ܠܒܝܬܗܘܢ ܘܡܠܝܐܝܬ ܚܘܝܘܗܝ ܐܘܪܚܗ ܕܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 27, 'ܘܟܕ ܨܒܐ ܕܢܐܙܠ ܠܐܟܐܝܐ ܚܦܛܘܗܝ ܐܚܐ ܘܟܬܒܘ ܠܬܠܡܝܕܐ ܕܢܩܒܠܘܢܝܗܝ ܘܟܕ ܐܙܠ ܥܕܪ ܤܓܝ ܒܝܕ ܛܝܒܘܬܐ ܠܟܠܗܘܢ ܡܗܝܡܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 28, 'ܬܩܝܦܐܝܬ ܓܝܪ ܕܪܫ ܗܘܐ ܠܘܩܒܠ ܝܗܘܕܝܐ ܩܕܡ ܟܢܫܐ ܟܕ ܡܚܘܐ ܗܘܐ ܡܢ ܟܬܒܐ ܥܠ ܝܫܘܥ ܕܡܫܝܚܐ ܗܘ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 1, 'ܘܟܕ ܐܝܬܘܗܝ ܗܘܐ ܐܦܠܘ ܒܩܘܪܢܬܘܤ ܐܬܟܪܟ ܦܘܠܘܤ ܒܐܬܪܘܬܐ ܥܠܝܐ ܘܐܬܐ ܠܐܦܤܘܤ ܘܡܫܐܠ ܗܘܐ ܠܬܠܡܝܕܐ ܐܝܠܝܢ ܕܐܫܟܚ ܬܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 2, 'ܕܐܢ ܩܒܠܬܘܢ ܪܘܚܐ ܕܩܘܕܫܐ ܡܢ ܕܗܝܡܢܬܘܢ ܥܢܘ ܘܐܡܪܝܢ ܠܗ ܘܐܦܠܐ ܐܢ ܐܝܬ ܪܘܚܐ ܕܩܘܕܫܐ ܫܡܝܥ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 3, 'ܐܡܪ ܠܗܘܢ ܘܒܡܢܐ ܥܡܕܬܘܢ ܐܡܪܝܢ ܒܡܥܡܘܕܝܬܗ ܕܝܘܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 4, 'ܐܡܪ ܠܗܘܢ ܦܘܠܘܤ ܝܘܚܢܢ ܐܥܡܕ ܡܥܡܘܕܝܬܐ ܕܬܝܒܘܬܐ ܠܥܡܐ ܟܕ ܐܡܪ ܗܘܐ ܕܢܗܝܡܢܘܢ ܒܐܝܢܐ ܕܐܬܐ ܒܬܪܗ ܕܐܝܬܘܗܝ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 5, 'ܘܟܕ ܗܠܝܢ ܫܡܥܘ ܥܡܕܘ ܒܫܡܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 6, 'ܘܤܡ ܥܠܝܗܘܢ ܐܝܕܐ ܦܘܠܘܤ ܘܐܬܐ ܪܘܚܐ ܕܩܘܕܫܐ ܥܠܝܗܘܢ ܘܡܡܠܠܝܢ ܗܘܘ ܒܠܫܢ ܠܫܢ ܘܡܬܢܒܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 7, 'ܗܘܝܢ ܗܘܘ ܕܝܢ ܟܠܗܘܢ ܐܢܫܐ ܬܪܥܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 8, 'ܘܥܠ ܗܘܐ ܦܘܠܘܤ ܠܟܢܘܫܬܐ ܘܡܡܠܠ ܗܘܐ ܥܝܢ ܒܓܠܐ ܝܪܚܐ ܬܠܬܐ ܘܡܦܝܤ ܗܘܐ ܥܠ ܡܠܟܘܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 9, 'ܘܐܢܫܝܢ ܡܢܗܘܢ ܡܬܩܫܝܢ ܗܘܘ ܘܡܬܚܪܝܢ ܘܡܨܚܝܢ ܠܐܘܪܚܐ ܕܐܠܗܐ ܩܕܡ ܟܢܫܐ ܕܥܡܡܐ ܗܝܕܝܢ ܐܪܚܩ ܦܘܠܘܤ ܘܦܪܫ ܡܢܗܘܢ ܠܬܠܡܝܕܐ ܘܟܠܝܘܡ ܡܡܠܠ ܗܘܐ ܥܡܗܘܢ ܒܐܤܟܘܠܐ ܕܓܒܪܐ ܕܫܡܗ ܛܘܪܢܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 10, 'ܘܗܕܐ ܗܘܬ ܫܢܝܢ ܬܪܬܝܢ ܥܕܡܐ ܕܫܡܥܘ ܡܠܬܐ ܕܡܪܝܐ ܟܠܗܘܢ ܕܥܡܪܝܢ ܒܐܤܝܐ ܝܗܘܕܝܐ ܘܐܪܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 11, 'ܘܚܝܠܐ ܪܘܪܒܐ ܥܒܕ ܗܘܐ ܐܠܗܐ ܒܐܝܕܗ ܕܦܘܠܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 12, 'ܗܟܢܐ ܕܐܦ ܡܢ ܢܚܬܐ ܕܥܠ ܓܘܫܡܗ ܤܘܕܪܐ ܐܘ ܪܘܩܥܐ ܡܝܬܝܢ ܗܘܘ ܘܤܝܡܝܢ ܥܠ ܟܪܝܗܐ ܘܦܪܩܝܢ ܗܘܘ ܡܢܗܘܢ ܟܘܪܗܢܐ ܘܐܦ ܫܐܕܐ ܢܦܩܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 13, 'ܨܒܘ ܗܘܘ ܕܝܢ ܐܦ ܐܢܫܐ ܝܗܘܕܝܐ ܐܝܠܝܢ ܕܡܬܟܪܟܝܢ ܗܘܘ ܘܡܘܡܝܢ ܥܠ ܫܐܕܐ ܕܢܘܡܘܢ ܒܫܡܗ ܕܡܪܢ ܝܫܘܥ ܥܠ ܐܝܠܝܢ ܕܐܝܬ ܗܘܝ ܠܗܘܢ ܪܘܚܐ ܛܢܦܬܐ ܟܕ ܐܡܪܝܢ ܗܘܘ ܡܘܡܝܢܢ ܠܟܘܢ ܒܫܡܗ ܕܝܫܘܥ ܐܝܢܐ ܕܡܟܪܙ ܦܘܠܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 14, 'ܐܝܬ ܗܘܐ ܕܝܢ ܫܒܥܐ ܒܢܘܗܝ ܕܓܒܪܐ ܚܕ ܝܗܘܕܝܐ ܪܒ ܟܗܢܐ ܕܫܡܗ ܗܘܐ ܤܩܘܐ ܕܥܒܕܝܢ ܗܘܘ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 15, 'ܘܥܢܐ ܫܐܕܐ ܗܘ ܒܝܫܐ ܘܐܡܪ ܠܗܘܢ ܠܝܫܘܥ ܡܫܬܘܕܥ ܐܢܐ ܘܠܦܘܠܘܤ ܝܕܥ ܐܢܐ ܐܢܬܘܢ ܕܝܢ ܡܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 16, 'ܘܫܘܪ ܥܠܝܗܘܢ ܓܒܪܐ ܗܘ ܕܐܝܬ ܗܘܐ ܒܗ ܪܘܚܐ ܒܝܫܬܐ ܘܐܬܚܝܠ ܥܠܝܗܘܢ ܘܫܦܠ ܐܢܘܢ ܘܟܕ ܫܠܝܚܝܢ ܘܦܥܝܥܝܢ ܥܪܩܘ ܡܢ ܒܝܬܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 17, 'ܘܗܕܐ ܐܬܝܕܥܬ ܗܘܬ ܠܟܠܗܘܢ ܝܗܘܕܝܐ ܘܐܪܡܝܐ ܕܥܡܪܝܢ ܒܐܦܤܘܤ ܘܢܦܠܬ ܕܚܠܬܐ ܥܠ ܟܠܗܘܢ ܘܡܬܪܡܪܡ ܗܘܐ ܫܡܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 18, 'ܘܤܓܝܐܐ ܡܢ ܐܝܠܝܢ ܕܗܝܡܢܘ ܐܬܝܢ ܗܘܘ ܘܡܫܬܥܝܢ ܤܟܠܘܬܗܘܢ ܘܡܘܕܝܢ ܒܡܕܡ ܕܥܒܕܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 19, 'ܤܓܝܐܐ ܕܝܢ ܐܦ ܚܪܫܐ ܟܢܫܘ ܟܬܒܝܗܘܢ ܘܐܝܬܝܘ ܐܘܩܕܘ ܐܢܘܢ ܩܕܡ ܟܠܢܫ ܘܚܫܒܘ ܕܡܝܗܘܢ ܘܤܠܩ ܟܤܦܐ ܪܒܘܬܐ ܚܡܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 20, 'ܘܗܟܢܐ ܒܚܝܠܐ ܪܒܐ ܬܩܦܐ ܗܘܬ ܘܤܓܝܐ ܗܝܡܢܘܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 21, 'ܟܕ ܕܝܢ ܐܫܬܠܡ ܗܠܝܢ ܤܡ ܦܘܠܘܤ ܒܪܥܝܢܗ ܕܢܬܟܪܟ ܒܟܠܗ ܡܩܕܘܢܝܐ ܘܒܐܟܐܝܐ ܘܢܐܙܠ ܠܐܘܪܫܠܡ ܘܐܡܪ ܕܡܐ ܕܐܙܠܬ ܠܬܡܢ ܘܠܐ ܠܝ ܕܐܦ ܪܗܘܡܝ ܐܚܙܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 22, 'ܘܫܕܪ ܗܘܐ ܬܪܝܢ ܐܢܫܝܢ ܡܢ ܗܢܘܢ ܕܡܫܡܫܝܢ ܗܘܘ ܠܗ ܠܡܩܕܘܢܝܐ ܠܛܝܡܬܐܘܤ ܘܠܐܪܤܛܘܤ ܗܘ ܕܝܢ ܩܘܝ ܙܒܢܐ ܒܐܤܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 23, 'ܗܘܐ ܗܘܐ ܕܝܢ ܒܗܘ ܙܒܢܐ ܫܓܘܫܝܐ ܤܓܝܐܐ ܥܠ ܐܘܪܚܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 24, 'ܐܝܬ ܗܘܐ ܕܝܢ ܬܡܢ ܥܒܕ ܤܐܡܐ ܚܕ ܕܫܡܗ ܗܘܐ ܕܡܛܪܝܘܤ ܕܥܒܕ ܗܘܐ ܢܘܤܐ ܕܤܐܡܐ ܠܐܪܛܡܝܤ ܘܡܘܬܪ ܗܘܐ ܠܒܢܝ ܐܘܡܢܘܬܗ ܝܘܬܪܢܐ ܪܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 25, 'ܗܢܐ ܟܢܫ ܐܢܘܢ ܠܒܢܝ ܐܘܡܢܘܬܗ ܟܠܗܘܢ ܘܠܐܝܠܝܢ ܕܦܠܚܝܢ ܥܡܗܘܢ ܘܐܡܪ ܠܗܘܢ ܓܒܪܐ ܝܕܥܝܢ ܐܢܬܘܢ ܕܬܐܓܘܪܬܢ ܟܠܗ ܡܢ ܗܢܐ ܗܝ ܦܘܠܚܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 26, 'ܘܐܦ ܐܢܬܘܢ ܫܡܥܝܢ ܐܢܬܘܢ ܘܚܙܝܢ ܐܢܬܘܢ ܕܠܐ ܗܘܐ ܒܠܚܘܕ ܠܒܢܝ ܐܦܤܘܤ ܐܠܐ ܐܦ ܠܤܘܓܐܐ ܕܟܠܗ ܐܤܝܐ ܐܦܝܤ ܗܢܐ ܦܘܠܘܤ ܘܐܗܦܟ ܟܕ ܐܡܪ ܕܠܘ ܐܠܗܐ ܐܢܘܢ ܗܢܘܢ ܕܒܐܝܕܝ ܒܢܝܢܫܐ ܡܬܥܒܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 27, 'ܘܠܘ ܗܕܐ ܨܒܘܬܐ ܒܠܚܘܕ ܡܬܦܪܤܝܐ ܘܒܛܠܐ ܐܠܐ ܐܦ ܗܘ ܗܝܟܠܐ ܕܐܪܛܡܝܤ ܐܠܗܬܐ ܪܒܬܐ ܡܬܚܫܒ ܠܗ ܐܝܟ ܠܐ ܡܕܡ ܘܐܦ ܗܝ ܐܠܗܬܐ ܕܟܠܗ ܐܤܝܐ ܘܟܠܗܘܢ ܥܡܡܐ ܤܓܕܝܢ ܠܗ ܡܬܫܝܛܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 28, 'ܘܟܕ ܫܡܥܘ ܗܠܝܢ ܐܬܡܠܝܘ ܚܡܬܐ ܘܩܥܝܢ ܗܘܘ ܘܐܡܪܝܢ ܪܒܐ ܗܝ ܐܪܛܡܝܤ ܕܐܦܤܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 29, 'ܘܐܫܬܓܫܬ ܟܠܗ ܡܕܝܢܬܐ ܘܪܗܛܘ ܐܟܚܕܐ ܘܐܙܠܘ ܠܬܐܛܪܘܢ ܘܚܛܦܘ ܐܘܒܠܘ ܥܡܗܘܢ ܠܓܐܝܘܤ ܘܠܐܪܤܛܪܟܘܤ ܓܒܪܐ ܡܩܕܘܢܝܐ ܒܢܝ ܠܘܝܬܗ ܕܦܘܠܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 30, 'ܘܦܘܠܘܤ ܨܒܐ ܗܘܐ ܕܢܥܘܠ ܠܬܐܛܪܘܢ ܘܟܠܐܘܗܝ ܬܠܡܝܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 31, 'ܘܐܦ ܪܫܐ ܕܐܤܝܐ ܡܛܠ ܕܪܚܡܘܗܝ ܗܘܘ ܫܕܪܘ ܒܥܘ ܡܢܗ ܕܠܐ ܢܬܠ ܢܦܫܗ ܕܢܥܘܠ ܠܬܐܛܪܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 32, 'ܟܢܫܐ ܕܝܢ ܕܐܝܬ ܗܘܘ ܒܬܐܛܪܘܢ ܛܒ ܫܓܝܫܝܢ ܗܘܘ ܘܐܚܪܢܐ ܐܚܪܢܝܬܐ ܩܥܝܢ ܗܘܘ ܤܓܝܐܐ ܓܝܪ ܡܢܗܘܢ ܠܐ ܝܕܥܝܢ ܗܘܘ ܡܛܠ ܡܢܐ ܐܬܟܢܫܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 33, 'ܥܡܐ ܕܝܢ ܕܝܗܘܕܝܐ ܕܐܝܬ ܗܘܘ ܬܡܢ ܐܩܝܡܘ ܡܢܗܘܢ ܠܓܒܪܐ ܝܗܘܕܝܐ ܕܫܡܗ ܐܠܟܤܢܕܪܘܤ ܘܟܕ ܩܡ ܐܢܝܦ ܐܝܕܗ ܘܨܒܐ ܗܘܐ ܕܢܦܘܩ ܪܘܚܐ ܠܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 34, 'ܘܟܕ ܝܕܥܘ ܕܝܗܘܕܝܐ ܗܘ ܩܥܘ ܟܠܗܘܢ ܒܚܕ ܩܠܐ ܐܝܟ ܫܥܝܢ ܬܪܬܝܢ ܕܪܒܐ ܗܝ ܐܪܛܡܝܤ ܕܐܦܤܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 35, 'ܘܫܠܝ ܐܢܘܢ ܪܫܐ ܕܡܕܝܢܬܐ ܟܕ ܐܡܪ ܓܒܪܐ ܐܦܤܝܐ ܡܢܘ ܓܝܪ ܡܢ ܒܢܝܢܫܐ ܕܠܐ ܝܕܥ ܠܡܕܝܢܬܐ ܕܐܦܤܝܐ ܕܟܘܡܪܬܐ ܗܝ ܕܐܪܛܡܝܤ ܪܒܬܐ ܘܠܨܠܡܗ ܕܡܢ ܫܡܝܐ ܢܚܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 36, 'ܡܛܠ ܗܟܝܠ ܕܠܘܩܒܠ ܗܕܐ ܐܢܫ ܠܐ ܡܫܟܚ ܠܡܐܡܪ ܘܠܐ ܠܟܘܢ ܕܬܗܘܘܢ ܫܠܝܢ ܘܠܐ ܬܥܒܕܘܢ ܡܕܡ ܒܤܘܪܗܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 37, 'ܐܝܬܝܬܘܢ ܓܝܪ ܠܓܒܪܐ ܗܠܝܢ ܟܕ ܠܐ ܗܝܟܠܐ ܚܠܨܘ ܘܠܐ ܨܚܝܘ ܠܐܠܗܬܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 38, 'ܐܢ ܕܝܢ ܗܢܐ ܕܡܛܪܝܘܤ ܘܒܢܝ ܐܘܡܢܘܬܗ ܐܝܬ ܠܗܘܢ ܕܝܢܐ ܥܡ ܐܢܫ ܗܐ ܐܢܬܘܦܛܘܤ ܒܡܕܝܢܬܐ ܐܘܡܢܐ ܐܢܘܢ ܢܩܪܒܘܢ ܘܢܕܘܢܘܢ ܚܕ ܥܡ ܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 39, 'ܘܐܢ ܗܘ ܕܡܕܡ ܐܚܪܝܢ ܒܥܝܢ ܐܢܬܘܢ ܒܕܘܟܬܐ ܕܝܗܝܒܐ ܡܢ ܢܡܘܤܐ ܠܟܢܘܫܝܐ ܡܫܬܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 40, 'ܡܛܠ ܕܐܦ ܗܫܐ ܒܩܢܕܝܢܘܤ ܩܝܡܝܢܢ ܕܢܬܪܫܐ ܐܝܟ ܫܓܘܫܐ ܕܠܐ ܡܫܟܚܝܢܢ ܕܢܦܘܩ ܪܘܚܐ ܥܠ ܟܢܫܐ ܕܝܘܡܐ ܗܢܐ ܕܐܬܟܢܫܢ ܒܛܠܐܝܬ ܘܐܫܬܓܫܢ ܕܠܐ ܥܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 41, 'ܘܟܕ ܗܠܝܢ ܐܡܪ ܫܪܐ ܠܟܢܫܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 1, 'ܘܒܬܪ ܕܫܠܝ ܫܓܘܫܝܐ ܩܪܐ ܦܘܠܘܤ ܠܬܠܡܝܕܐ ܘܒܝܐ ܐܢܘܢ ܘܢܫܩ ܐܢܘܢ ܘܢܦܩ ܐܙܠ ܠܗ ܠܡܩܕܘܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 2, 'ܘܟܕ ܐܬܟܪܟ ܐܢܘܢ ܠܐܬܪܘܬܐ ܗܠܝܢ ܘܒܝܐ ܐܢܘܢ ܒܡܠܐ ܤܓܝܐܬܐ ܐܬܐ ܠܗ ܠܗܠܤ ܐܬܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 3, 'ܘܗܘܐ ܬܡܢ ܬܠܬܐ ܝܪܚܝܢ ܥܒܕܘ ܕܝܢ ܥܠܘܗܝ ܢܟܠܐ ܝܗܘܕܝܐ ܟܕ ܥܬܝܕ ܗܘܐ ܠܡܐܙܠ ܠܤܘܪܝܐ ܘܐܬܚܫܒ ܕܢܗܦܘܟ ܠܗ ܠܡܩܕܘܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 4, 'ܘܢܦܩܘ ܥܡܗ ܥܕܡܐ ܠܐܤܝܐ ܤܘܦܛܪܘܤ ܕܡܢ ܒܪܘܐܐ ܡܕܝܢܬܐ ܘܐܪܤܛܪܟܘܤ ܘܤܩܘܢܕܘܤ ܕܡܢ ܬܤܠܘܢܝܩܐ ܘܓܐܝܘܤ ܕܡܢ ܕܪܒܐ ܡܕܝܢܬܐ ܘܛܝܡܬܐܘܤ ܕܡܢ ܠܘܤܛܪܐ ܘܡܢ ܐܤܝܐ ܛܘܟܝܩܘܤ ܘܛܪܘܦܝܡܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 5, 'ܗܠܝܢ ܐܙܠܘ ܩܕܡܝܢ ܘܩܘܝܘ ܠܢ ܒܛܪܘܐܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 6, 'ܚܢܢ ܕܝܢ ܢܦܩܢ ܡܢ ܦܝܠܝܦܘܤ ܡܕܝܢܬܐ ܕܡܩܕܘܢܝܐ ܒܬܪ ܝܘܡܬܐ ܕܦܛܝܪܐ ܘܪܕܝܢ ܒܝܡܐ ܘܐܬܝܢ ܠܛܪܘܐܤ ܠܝܘܡܬܐ ܚܡܫܐ ܘܗܘܝܢ ܬܡܢ ܝܘܡܬܐ ܫܒܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 7, 'ܘܒܝܘܡܐ ܕܚܕ ܒܫܒܐ ܟܕ ܟܢܝܫܝܢܢ ܕܢܩܨܐ ܐܘܟܪܤܛܝܐ ܡܡܠܠ ܗܘܐ ܥܡܗܘܢ ܦܘܠܘܤ ܡܛܠ ܕܠܝܘܡܐ ܐܚܪܢܐ ܥܬܝܕ ܗܘܐ ܕܢܦܘܩ ܠܗ ܘܐܓܪ ܗܘܐ ܠܡܡܠܠܘ ܥܕܡܐ ܠܦܠܓܗ ܕܠܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 8, 'ܘܐܝܬ ܗܘܐ ܬܡܢ ܠܡܦܐܕܐ ܕܢܘܪܐ ܤܓܝܐܐ ܒܥܠܝܬܐ ܗܝ ܕܟܢܝܫܝܢ ܗܘܝܢ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 9, 'ܘܝܬܒ ܗܘܐ ܥܠܝܡܐ ܚܕ ܕܫܡܗ ܗܘܐ ܐܘܛܟܘܤ ܒܟܘܬܐ ܘܫܡܥ ܘܛܒܥ ܒܫܢܬܐ ܝܩܝܪܬܐ ܟܕ ܐܓܪ ܗܘܐ ܒܡܠܬܐ ܦܘܠܘܤ ܘܒܫܢܬܗ ܢܦܠ ܗܘܐ ܡܢ ܬܠܬܐ ܡܕܝܪܝܢ ܘܐܫܬܩܠ ܟܕ ܡܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 10, 'ܘܢܚܬ ܦܘܠܘܤ ܢܦܠ ܠܥܠ ܡܢܗ ܘܥܦܩܗ ܘܐܡܪ ܠܐ ܬܬܙܝܥܘܢ ܡܛܠ ܕܢܦܫܗ ܒܗ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 11, 'ܟܕ ܤܠܩ ܕܝܢ ܩܨܐ ܠܚܡܐ ܘܛܥܡ ܘܗܘܐ ܡܡܠܠ ܥܕܡܐ ܕܤܠܩ ܨܦܪܐ ܘܗܝܕܝܢ ܢܦܩ ܕܢܐܙܠ ܒܝܒܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 12, 'ܘܕܒܪܘܗܝ ܠܥܠܝܡܐ ܟܕ ܚܝ ܘܚܕܝܘ ܒܗ ܪܘܪܒܐܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 13, 'ܚܢܢ ܕܝܢ ܢܚܬܢ ܠܐܠܦܐ ܘܪܕܝܢ ܠܘܥܕܐ ܕܬܤܘܤ ܡܛܠ ܕܡܢ ܬܡܢ ܥܬܝܕܝܢ ܗܘܝܢ ܕܢܩܒܠܝܘܗܝ ܠܦܘܠܘܤ ܗܟܢܐ ܓܝܪ ܦܩܕ ܗܘܐ ܠܢ ܟܕ ܐܙܠ ܗܘܐ ܗܘ ܒܝܒܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 14, 'ܟܕ ܕܝܢ ܩܒܠܢܝܗܝ ܡܢ ܬܤܘܤ ܫܩܠܢܝܗܝ ܒܐܠܦܐ ܘܐܬܝܢ ܠܡܝܛܘܠܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 15, 'ܘܡܢ ܬܡܢ ܠܝܘܡܐ ܐܚܪܢܐ ܪܕܝܢ ܠܘܩܒܠ ܟܝܘܤ ܓܙܪܬܐ ܘܬܘܒ ܠܝܘܡܐ ܐܚܪܢܐ ܐܬܝܢ ܠܤܡܘܤ ܘܩܘܝܢ ܒܛܪܘܓܠܝܘܢ ܘܠܝܘܡܐ ܐܚܪܢܐ ܐܬܝܢ ܠܡܝܠܝܛܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 16, 'ܦܤܝܩ ܗܘܐ ܠܗ ܓܝܪ ܠܦܘܠܘܤ ܕܢܥܒܪܝܗ ܠܐܦܤܘܤ ܕܠܡܐ ܢܫܬܘܚܪ ܠܗ ܬܡܢ ܡܛܠ ܕܡܤܪܗܒ ܗܘܐ ܕܐܢ ܡܫܟܚܐ ܝܘܡܐ ܕܦܢܛܩܘܤܛܐ ܒܐܘܪܫܠܡ ܢܥܒܕܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 17, 'ܘܡܢܗ ܡܢ ܡܝܠܝܛܘܤ ܫܕܪ ܐܝܬܝ ܠܩܫܝܫܐ ܕܥܕܬܐ ܕܐܦܤܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 18, 'ܘܟܕ ܐܬܘ ܠܘܬܗ ܐܡܪ ܠܗܘܢ ܐܢܬܘܢ ܝܕܥܝܢ ܐܢܬܘܢ ܕܡܢ ܝܘܡܐ ܩܕܡܝܐ ܕܥܠܬ ܠܐܤܝܐ ܐܝܟܢܐ ܗܘܝܬ ܥܡܟܘܢ ܟܠܗ ܙܒܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 19, 'ܟܕ ܦܠܚ ܐܢܐ ܠܐܠܗܐ ܒܡܟܝܟܘܬܐ ܤܓܝܐܬܐ ܘܒܕܡܥܐ ܘܒܢܤܝܘܢܐ ܐܝܠܝܢ ܕܥܕܝܢ ܗܘܘ ܥܠܝ ܒܢܟܠܝܗܘܢ ܕܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 20, 'ܘܠܐ ܒܤܝܬ ܒܡܕܡ ܕܦܩܚ ܗܘܐ ܠܢܦܫܬܟܘܢ ܕܐܟܪܙ ܠܟܘܢ ܘܐܠܦ ܒܫܘܩܐ ܘܒܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 21, 'ܟܕ ܡܤܗܕ ܗܘܝܬ ܠܝܗܘܕܝܐ ܘܠܐܪܡܝܐ ܥܠ ܬܝܒܘܬܐ ܕܠܘܬ ܐܠܗܐ ܘܗܝܡܢܘܬܐ ܕܒܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 22, 'ܘܗܫܐ ܐܢܐ ܐܤܝܪ ܐܢܐ ܒܪܘܚܐ ܘܐܙܠ ܐܢܐ ܠܐܘܪܫܠܡ ܘܠܐ ܝܕܥ ܐܢܐ ܡܢܐ ܐܪܥ ܠܝ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 23, 'ܒܪܡ ܪܘܚܐ ܕܩܘܕܫܐ ܒܟܠ ܡܕܝܢܐ ܡܤܗܕ ܠܝ ܘܐܡܪ ܕܐܤܘܪܐ ܘܐܘܠܨܢܐ ܥܬܝܕܝܢ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 24, 'ܐܠܐ ܠܝ ܠܐ ܚܫܝܒܐ ܢܦܫܝ ܡܕܡ ܐܝܟ ܕܐܫܠܡ ܪܗܛܝ ܘܬܫܡܫܬܐ ܕܩܒܠܬ ܡܢ ܡܪܢ ܝܫܘܥ ܕܐܤܗܕ ܥܠ ܤܒܪܬܐ ܕܛܝܒܘܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 25, 'ܘܗܫܐ ܐܢܐ ܝܕܥ ܐܢܐ ܕܬܘܒ ܦܪܨܘܦܝ ܠܐ ܚܙܝܢ ܐܢܬܘܢ ܟܠܟܘܢ ܐܝܠܝܢ ܕܐܬܟܪܟܬ ܐܟܪܙܬ ܠܟܘܢ ܡܠܟܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 26, 'ܘܡܛܠ ܗܢܐ ܡܤܗܕ ܐܢܐ ܠܟܘܢ ܝܘܡܐ ܕܝܘܡܢܐ ܕܕܟܐ ܐܢܐ ܡܢ ܕܡܐ ܕܟܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 27, 'ܠܐ ܓܝܪ ܐܫܬܐܠܬ ܕܐܘܕܥܟܘܢ ܟܠܗ ܨܒܝܢܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 28, 'ܐܙܕܗܪܘ ܗܟܝܠ ܒܢܦܫܟܘܢ ܘܒܟܠܗ ܡܪܥܝܬܐ ܗܝ ܕܐܩܝܡܟܘܢ ܒܗ ܪܘܚܐ ܕܩܘܕܫܐ ܐܦܤܩܘܦܐ ܕܬܪܥܘܢ ܠܥܕܬܗ ܕܐܠܗܐ ܗܝ ܕܩܢܗ ܒܕܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 29, 'ܐܢܐ ܝܕܥ ܐܢܐ ܕܡܢ ܒܬܪ ܕܐܙܠ ܐܢܐ ܢܥܠܘܢ ܥܡܟܘܢ ܕܐܒܐ ܬܩܝܦܐ ܕܠܐ ܚܝܤܝܢ ܥܠ ܡܪܥܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 30, 'ܘܐܦ ܡܢܟܘܢ ܕܝܠܟܘܢ ܢܩܘܡܘܢ ܓܒܪܐ ܡܡܠܠܝ ܡܥܩܡܬܐ ܐܝܟ ܕܢܗܦܟܘܢ ܠܬܠܡܝܕܐ ܕܢܐܙܠܘܢ ܒܬܪܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 31, 'ܡܛܠ ܗܢܐ ܗܘܝܬܘܢ ܥܝܪܝܢ ܘܥܗܝܕܝܢ ܕܫܢܝܐ ܬܠܬ ܠܐ ܫܠܝܬ ܒܠܠܝܐ ܘܒܐܝܡܡܐ ܟܕ ܒܕܡܥܐ ܡܪܬܐ ܐܢܐ ܠܐܢܫ ܐܢܫ ܡܢܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 32, 'ܘܗܫܐ ܡܓܥܠ ܐܢܐ ܠܟܘܢ ܠܐܠܗܐ ܘܠܡܠܬܐ ܕܛܝܒܘܬܗ ܕܗܝ ܡܫܟܚܐ ܒܢܝܐ ܠܟܘܢ ܘܝܗܒܐ ܠܟܘܢ ܝܘܪܬܢܐ ܥܡ ܟܠܗܘܢ ܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 33, 'ܟܤܦܐ ܐܘ ܕܗܒܐ ܐܘ ܢܚܬܐ ܠܐ ܪܓܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 34, 'ܘܐܢܬܘܢ ܝܕܥܝܢ ܐܢܬܘܢ ܕܠܤܢܝܩܘܬܝ ܘܠܐܝܠܝܢ ܕܐܝܬ ܥܡܝ ܫܡܫ ܗܠܝܢ ܐܝܕܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 35, 'ܘܟܠܡܕܡ ܚܘܝܬܟܘܢ ܕܗܟܢܐ ܘܠܐ ܠܡܠܐܐ ܘܠܡܐܨܦ ܕܐܝܠܝܢ ܕܟܪܝܗܝܢ ܘܠܡܥܗܕܘ ܡܠܘܗܝ ܕܡܪܢ ܝܫܘܥ ܡܛܠ ܕܗܘ ܐܡܪ ܕܛܘܒܘܗܝ ܠܐܝܢܐ ܕܝܗܒ ܝܬܝܪ ܡܢ ܐܝܢܐ ܕܢܤܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 36, 'ܘܟܕ ܗܠܝܢ ܐܡܪ ܩܥܕ ܥܠ ܒܘܪܟܘܗܝ ܘܨܠܝ ܘܟܠܗܘܢ ܐܢܫܐ ܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 37, 'ܘܗܘܬ ܒܟܬܐ ܪܒܬܐ ܒܟܠܗܘܢ ܘܥܦܩܘܗܝ ܘܡܢܫܩܝܢ ܗܘܘ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 38, 'ܝܬܝܪܐܝܬ ܕܝܢ ܡܫܬܢܩܝܢ ܗܘܘ ܥܠ ܗܝ ܡܠܬܐ ܕܐܡܪ ܕܠܐ ܬܘܒ ܥܬܝܕܝܢ ܕܢܚܙܘܢ ܦܪܨܘܦܗ ܘܠܘܝܘܗܝ ܥܕܡܐ ܠܐܠܦܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 1, 'ܘܦܪܫܢ ܡܢܗܘܢ ܘܪܕܝܢ ܬܪܝܨܐܝܬ ܠܩܘ ܓܙܪܬܐ ܘܠܝܘܡܐ ܐܚܪܢܐ ܐܬܝܢ ܠܪܘܕܘܤ ܘܡܢ ܬܡܢ ܠܦܐܛܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 2, 'ܘܐܫܟܚܢ ܬܡܢ ܐܠܦܐ ܕܐܙܠܐ ܠܦܘܢܝܩܐ ܘܤܠܩܢ ܠܗ ܘܪܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 3, 'ܘܡܛܝܢ ܥܕܡܐ ܠܘܬ ܩܘܦܪܘܤ ܓܙܪܬܐ ܘܫܒܩܢܗ ܠܤܡܠܐ ܘܐܬܝܢ ܠܤܘܪܝܐ ܘܡܢ ܬܡܢ ܡܛܝܢ ܠܨܘܪ ܬܡܢ ܓܝܪ ܐܝܬ ܗܘܐ ܠܗ ܠܐܠܦܐ ܠܡܢܚܘ ܛܥܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 4, 'ܘܟܕ ܐܫܟܚܢ ܬܡܢ ܬܠܡܝܕܐ ܩܘܝܢ ܠܘܬܗܘܢ ܝܘܡܬܐ ܫܒܥܐ ܘܗܠܝܢ ܐܡܪܝܢ ܗܘܘ ܟܠܝܘܡ ܠܦܘܠܘܤ ܒܪܘܚ ܕܠܐ ܢܐܙܠ ܠܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 5, 'ܘܡܢ ܒܬܪ ܗܠܝܢ ܝܘܡܬܐ ܢܦܩܢ ܕܢܐܙܠ ܒܐܘܪܚܐ ܘܡܠܘܝܢ ܗܘܘ ܠܢ ܟܠܗܘܢ ܗܢܘܢ ܘܢܫܝܗܘܢ ܘܒܢܝܗܘܢ ܥܕܡܐ ܠܒܪ ܡܢ ܡܕܝܢܬܐ ܘܩܥܕܘ ܥܠ ܒܘܪܟܝܗܘܢ ܥܠ ܝܕ ܝܡܐ ܘܨܠܝܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 6, 'ܘܢܫܩܢ ܠܚܕܕܐ ܘܤܠܩܢ ܠܐܠܦܐ ܘܗܦܟܘ ܗܢܘܢ ܠܒܬܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 7, 'ܚܢܢ ܕܝܢ ܪܕܝܢ ܡܢ ܨܘܪ ܘܐܬܝܢ ܠܥܟܘ ܡܕܝܢܬܐ ܘܝܗܒܢ ܫܠܡܐ ܠܐܚܐ ܕܬܡܢ ܘܫܪܝܢ ܨܐܕܝܗܘܢ ܝܘܡܐ ܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 8, 'ܘܠܝܘܡܐ ܐܚܪܢܐ ܢܦܩܢ ܘܐܬܝܢ ܠܩܤܪܝܐ ܘܥܠܢ ܫܪܝܢ ܒܒܝܬܗ ܕܦܝܠܝܦܘܤ ܡܤܒܪܢܐ ܐܝܢܐ ܕܐܝܬܘܗܝ ܗܘܐ ܡܢ ܫܒܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 9, 'ܘܐܝܬ ܗܘܝ ܠܗ ܒܢܬܐ ܒܬܘܠܬܐ ܐܪܒܥ ܕܡܬܢܒܝܢ ܗܘܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 10, 'ܘܟܕ ܐܝܬܝܢ ܬܡܢ ܝܘܡܬܐ ܤܓܝܐܐ ܢܚܬ ܗܘܐ ܡܢ ܝܗܘܕ ܢܒܝܐ ܚܕ ܕܫܡܗ ܗܘܐ ܐܓܒܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 11, 'ܘܥܠ ܠܘܬܢ ܘܫܩܠ ܥܪܩܬܐ ܕܚܨܘܗܝ ܕܦܘܠܘܤ ܘܐܤܪ ܪܓܠܐ ܕܢܦܫܗ ܘܐܝܕܘܗܝ ܘܐܡܪ ܗܟܢܐ ܐܡܪ ܪܘܚܐ ܕܩܘܕܫܐ ܕܓܒܪܐ ܡܪܗ ܕܥܪܩܬܐ ܗܕܐ ܗܟܢܐ ܢܐܤܪܘܢܗ ܝܗܘܕܝܐ ܒܐܘܪܫܠܡ ܘܢܫܠܡܘܢܗ ܒܐܝܕܝ ܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 12, 'ܘܟܕ ܗܠܝܢ ܡܠܐ ܫܡܥܢ ܒܥܝܢ ܡܢܗ ܚܢܢ ܘܒܢܝ ܐܬܪܐ ܕܠܐ ܢܐܙܠ ܠܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 13, 'ܗܝܕܝܢ ܥܢܐ ܘܐܡܪ ܦܘܠܘܤ ܡܢܐ ܥܒܕܝܢ ܐܢܬܘܢ ܕܒܟܝܢ ܐܢܬܘܢ ܘܫܚܩܝܢ ܐܢܬܘܢ ܠܗ ܠܠܒܝ ܐܢܐ ܓܝܪ ܠܐ ܗܘܐ ܕܐܬܐܤܪ ܒܠܚܘܕ ܡܛܝܒ ܐܢܐ ܐܠܐ ܐܦ ܕܐܡܘܬ ܒܐܘܪܫܠܡ ܚܠܦ ܫܡܗ ܕܡܪܢ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 14, 'ܘܟܕ ܠܐ ܐܬܛܦܝܤ ܠܢ ܒܗܠܢ ܠܢ ܘܐܡܪܢ ܕܨܒܝܢܗ ܕܡܪܢ ܢܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 15, 'ܘܒܬܪ ܝܘܡܬܐ ܗܠܝܢ ܐܬܛܝܒܢ ܘܤܠܩܢ ܠܢ ܠܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 16, 'ܘܐܬܘ ܗܘܘ ܥܡܢ ܐܢܫܐ ܬܠܡܝܕܐ ܡܢ ܩܤܪܝܐ ܟܕ ܕܒܝܪܝܢ ܥܡܗܘܢ ܐܚܐ ܚܕ ܡܢ ܬܠܡܝܕܐ ܩܕܡܝܐ ܕܫܡܗ ܗܘܐ ܡܢܤܘܢ ܘܐܝܬܘܗܝ ܗܘܐ ܡܢ ܩܘܦܪܘܤ ܕܢܩܒܠܢ ܒܒܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 17, 'ܘܟܕ ܐܬܝܢ ܠܐܘܪܫܠܡ ܩܒܠܘܢ ܐܚܐ ܚܕܝܐܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 18, 'ܘܠܝܘܡܐ ܐܚܪܢܐ ܥܠܢ ܥܡ ܦܘܠܘܤ ܠܘܬ ܝܥܩܘܒ ܟܕ ܐܝܬ ܗܘܐ ܠܘܬܗ ܟܠܗܘܢ ܩܫܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 19, 'ܘܝܗܒܢ ܠܗܘܢ ܫܠܡܐ ܘܡܫܬܥܐ ܗܘܐ ܠܗܘܢ ܦܘܠܘܤ ܒܬܪ ܒܬܪ ܟܠ ܡܐ ܕܥܒܕ ܐܠܗܐ ܒܥܡܡܐ ܒܬܫܡܫܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 20, 'ܘܟܕ ܫܡܥܘ ܫܒܚܘ ܠܐܠܗܐ ܘܐܡܪܘ ܠܗ ܚܙܐ ܐܢܬ ܐܚܘܢ ܟܡܐ ܪܒܘܢ ܐܝܬ ܒܝܗܘܕ ܐܝܠܝܢ ܕܗܝܡܢܘ ܘܟܠܗܘܢ ܗܠܝܢ ܛܢܢܐ ܐܢܘܢ ܕܢܡܘܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 21, 'ܐܬܐܡܪ ܠܗܘܢ ܕܝܢ ܥܠܝܟ ܕܡܠܦ ܐܢܬ ܕܢܦܪܩܘܢ ܡܢ ܡܘܫܐ ܟܠܗܘܢ ܝܗܘܕܝܐ ܕܒܥܡܡܐ ܟܕ ܐܡܪ ܐܢܬ ܕܠܐ ܢܗܘܘܢ ܓܙܪܝܢ ܒܢܝܗܘܢ ܘܠܐ ܒܥܝܕܐ ܕܢܡܘܤܐ ܢܗܘܘܢ ܡܗܠܟܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 22, 'ܡܛܠ ܗܟܝܠ ܕܡܫܬܡܥܐ ܗܝ ܠܗܘܢ ܕܐܬܝܬ ܠܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 23, 'ܥܒܕ ܡܕܡ ܕܐܡܪܝܢܢ ܠܟ ܐܝܬ ܠܢ ܓܒܪܐ ܐܪܒܥܐ ܕܢܕܝܪ ܠܗܘܢ ܕܢܬܕܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 24, 'ܕܒܪ ܐܢܘܢ ܘܙܠ ܐܬܕܟܐ ܥܡܗܘܢ ܘܐܦܩ ܥܠܝܗܘܢ ܢܦܩܬܐ ܐܝܟ ܕܢܓܪܥܘܢ ܪܫܝܗܘܢ ܘܡܬܝܕܥܐ ܠܟܠܢܫ ܕܡܕܡ ܕܐܬܐܡܪ ܥܠܝܟ ܕܓܠ ܗܘ ܘܐܢܬ ܠܢܡܘܤܐ ܫܠܡ ܐܢܬ ܘܢܛܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 25, 'ܥܠ ܐܝܠܝܢ ܕܝܢ ܕܗܝܡܢܘ ܡܢ ܥܡܡܐ ܚܢܢ ܟܬܒܢ ܕܢܗܘܘܢ ܢܛܪܝܢ ܢܦܫܗܘܢ ܡܢ ܕܕܒܝܚܐ ܘܡܢ ܙܢܝܘܬܐ ܘܡܢ ܚܢܝܩܐ ܘܡܢ ܕܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 26, 'ܗܝܕܝܢ ܦܘܠܘܤ ܕܒܪ ܐܢܘܢ ܠܓܒܪܐ ܗܠܝܢ ܠܝܘܡܐ ܐܚܪܢܐ ܘܐܬܕܟܝ ܥܡܗܘܢ ܘܥܠ ܐܙܠ ܠܗܝܟܠܐ ܟܕ ܡܘܕܥ ܠܗܘܢ ܡܘܠܝܐ ܕܝܘܡܬܐ ܕܬܕܟܝܬܐ ܥܕܡܐ ܕܐܬܩܪܒ ܩܘܪܒܢܐ ܕܐܢܫ ܐܢܫ ܡܢܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 27, 'ܘܟܕ ܡܛܝ ܝܘܡܐ ܕܫܒܥܐ ܝܗܘܕܝܐ ܕܡܢ ܐܤܝܐ ܚܙܐܘܗܝ ܒܗܝܟܠܐ ܘܓܪܝܘ ܥܠܘܗܝ ܥܡܐ ܟܠܗ ܘܐܪܡܝܘ ܥܠܘܗܝ ܐܝܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 28, 'ܟܕ ܡܒܓܢܝܢ ܘܐܡܪܝܢ ܓܒܪܐ ܒܢܝ ܐܝܤܪܝܠ ܥܕܪܘ ܗܢܐ ܗܘ ܓܒܪܐ ܕܠܘܩܒܠ ܥܡܐ ܕܝܠܢ ܡܠܦ ܒܟܠ ܕܘܟܐ ܘܠܘܩܒܠ ܢܡܘܤܐ ܘܠܘܩܒܠ ܐܬܪܐ ܗܢܐ ܘܐܦ ܠܐܪܡܝܐ ܐܥܠ ܠܗܝܟܠܐ ܘܤܝܒܗ ܠܐܬܪܐ ܗܢܐ ܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 29, 'ܩܕܡܘ ܗܘܘ ܓܝܪ ܚܙܘ ܥܡܗ ܠܛܪܘܦܝܡܤ ܐܦܤܝܐ ܒܡܕܝܢܬܐ ܘܤܒܪܝܢ ܗܘܘ ܕܥܡ ܦܘܠܘܤ ܥܠ ܠܗܝܟܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 30, 'ܘܐܫܬܓܫܬ ܡܕܝܢܬܐ ܘܐܬܟܢܫܘ ܟܠܗ ܥܡܐ ܘܐܚܕܘܗܝ ܠܦܘܠܘܤ ܘܓܪܘܗܝ ܠܒܪ ܡܢ ܗܝܟܠܐ ܘܒܪ ܫܥܬܗ ܐܬܬܚܕܘ ܬܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 31, 'ܘܟܕ ܒܥܐ ܗܘܐ ܟܢܫܐ ܠܡܩܛܠܗ ܐܫܬܡܥܬ ܠܟܠܝܪܟܐ ܕܐܤܦܝܪ ܕܟܠܗ ܡܕܝܢܬܐ ܐܬܬܙܝܥܬ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 32, 'ܘܒܪ ܫܥܬܗ ܕܒܪ ܠܩܢܛܪܘܢܐ ܘܠܐܤܛܪܛܝܘܛܐ ܤܓܝܐܐ ܘܪܗܛܘ ܥܠܝܗܘܢ ܘܟܕ ܚܙܘ ܠܟܠܝܪܟܐ ܘܠܐܤܛܪܛܝܘܛܐ ܒܗܠܘ ܡܢ ܕܡܚܝܢ ܗܘܘ ܠܗ ܠܦܘܠܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 33, 'ܘܩܪܒ ܠܘܬܗ ܟܠܝܪܟܐ ܘܐܚܕܗ ܘܦܩܕ ܕܢܐܤܪܘܢܗ ܒܬܪܬܝܢ ܫܫܠܢ ܘܡܫܐܠ ܗܘܐ ܥܠܘܗܝ ܕܡܢܘ ܘܡܢܐ ܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 34, 'ܘܩܥܝܢ ܗܘܘ ܥܠܘܗܝ ܐܢܫܐ ܡܢ ܐܟܠܘܤ ܡܕܡ ܡܕܡ ܘܡܛܠ ܩܥܬܗܘܢ ܠܐ ܡܫܟܚ ܗܘܐ ܠܡܕܥ ܐܝܕܐ ܗܝ ܫܪܝܪܬܐ ܘܦܩܕ ܗܘܐ ܕܢܘܒܠܘܢܗ ܠܡܫܪܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 35, 'ܘܟܕ ܡܛܝ ܦܘܠܘܤ ܠܕܪܓܐ ܛܥܢܘܗܝ ܐܤܛܪܛܝܘܛܐ ܡܛܠ ܩܛܝܪܐ ܕܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 36, 'ܐܬܐ ܗܘܐ ܓܝܪ ܒܬܪܗ ܥܡܐ ܤܓܝܐܐ ܘܩܥܝܢ ܗܘܘ ܘܐܡܪܝܢ ܫܩܘܠܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 37, 'ܘܟܕ ܡܛܝ ܠܡܥܠ ܠܡܫܪܝܬܐ ܗܘ ܦܘܠܘܤ ܐܡܪ ܠܟܠܝܪܟܐ ܐܢ ܡܦܤ ܐܢܬ ܠܝ ܐܡܠܠ ܥܡܟ ܗܘ ܕܝܢ ܐܡܪ ܠܗ ܝܘܢܐܝܬ ܝܕܥ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 38, 'ܠܐ ܗܘܝܬ ܐܢܬ ܗܘ ܡܨܪܝܐ ܕܩܕܡ ܝܘܡܬܐ ܗܠܝܢ ܐܙܝܥܬ ܘܐܦܩܬ ܠܡܕܒܪܐ ܐܪܒܥܐ ܐܠܦܝܢ ܓܒܪܐ ܥܒܕܝ ܒܝܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 39, 'ܐܡܪ ܠܗ ܦܘܠܘܤ ܐܢܐ ܓܒܪܐ ܐܢܐ ܝܗܘܕܝܐ ܡܢ ܛܪܤܘܤ ܕܩܝܠܝܩܝܐ ܡܕܝܢܬܐ ܝܕܝܥܬܐ ܕܒܗ ܝܠܝܕ ܐܢܐ ܒܥܐ ܐܢܐ ܡܢܟ ܐܦܤ ܠܝ ܠܡܡܠܠܘ ܠܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 40, 'ܘܟܕ ܐܦܤ ܠܗ ܩܡ ܦܘܠܘܤ ܥܠ ܕܪܓܐ ܘܐܙܝܥ ܗܘܐ ܠܗܘܢ ܐܝܕܗ ܘܟܕ ܒܗܠܘ ܡܠܠ ܥܡܗܘܢ ܥܒܪܐܝܬ ܘܐܡܪ ܠܗܘܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 1, 'ܐܚܐ ܘܐܒܗܬܐ ܫܡܥܘ ܡܦܩ ܒܪܘܚ ܕܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 2, 'ܘܟܕ ܫܡܥܘ ܕܥܒܪܐܝܬ ܡܡܠܠ ܗܘܐ ܥܡܗܘܢ ܝܬܝܪܐܝܬ ܒܗܠܘ ܘܐܡܪ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 3, 'ܐܢܐ ܓܒܪܐ ܐܢܐ ܝܗܘܕܝܐ ܘܝܠܝܕ ܐܢܐ ܒܛܪܤܘܤ ܕܩܝܠܝܩܝܐ ܐܬܪܒܝܬ ܕܝܢ ܒܗܕܐ ܡܕܝܢܬܐ ܥܠ ܓܢܒ ܪܓܠܘܗܝ ܕܓܡܠܝܐܝܠ ܘܐܬܪܕܝܬ ܓܡܝܪܐܝܬ ܒܢܡܘܤܐ ܕܐܒܗܬܢ ܘܐܝܬܝ ܗܘܝܬ ܛܢܢܐ ܕܐܠܗܐ ܐܝܟ ܡܐ ܕܐܦ ܐܢܬܘܢ ܟܠܟܘܢ ܐܝܬܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 4, 'ܘܠܗܕܐ ܐܘܪܚܐ ܪܕܦܬ ܥܕܡܐ ܠܡܘܬܐ ܟܕ ܐܤܪ ܗܘܝܬ ܘܡܫܠܡ ܗܘܝܬ ܠܒܝܬ ܐܤܝܪܐ ܓܒܪܐ ܘܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 5, 'ܐܝܟ ܡܐ ܕܤܗܕ ܥܠܝ ܪܒ ܟܗܢܐ ܘܟܠܗܘܢ ܩܫܝܫܐ ܕܡܢܗܘܢ ܩܒܠܬ ܐܓܪܬܐ ܕܐܙܠ ܠܘܬ ܐܚܐ ܕܒܕܪܡܤܘܩ ܕܐܦ ܠܗܢܘܢ ܕܐܝܬ ܗܘܘ ܬܡܢ ܐܝܬܐ ܐܢܘܢ ܠܐܘܪܫܠܡ ܟܕ ܐܤܝܪܝܢ ܘܢܩܒܠܘܢ ܡܤܡ ܒܪܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 6, 'ܘܟܕ ܐܙܠ ܗܘܝܬ ܘܫܪܝܬ ܡܡܛܐ ܐܢܐ ܠܕܪܡܤܘܩ ܒܦܠܓܗ ܕܝܘܡܐ ܡܢ ܬܚܝܬ ܫܠܝܐ ܡܢ ܫܡܝܐ ܐܙܠܓ ܥܠܝ ܢܘܗܪܐ ܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 7, 'ܘܢܦܠܬ ܥܠ ܐܪܥܐ ܘܫܡܥܬ ܩܠܐ ܕܐܡܪ ܗܘܐ ܠܝ ܫܐܘܠ ܫܐܘܠ ܡܢܐ ܪܕܦ ܐܢܬ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 8, 'ܐܢܐ ܕܝܢ ܥܢܝܬ ܘܐܡܪܬ ܡܢ ܐܢܬ ܡܪܝ ܘܗܘ ܐܡܪ ܠܝ ܐܢܐ ܗܘ ܝܫܘܥ ܢܨܪܝܐ ܕܐܢܬ ܪܕܦ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 9, 'ܘܐܢܫܐ ܕܐܝܬ ܗܘܘ ܥܡܝ ܚܙܘ ܢܘܗܪܐ ܩܠܐ ܕܝܢ ܠܐ ܫܡܥܘ ܕܗܘ ܕܡܡܠܠ ܗܘܐ ܥܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 10, 'ܘܐܡܪܬ ܡܢܐ ܐܥܒܕ ܡܪܝ ܘܡܪܢ ܐܡܪ ܠܝ ܩܘܡ ܙܠ ܠܕܪܡܤܘܩ ܘܬܡܢ ܢܬܡܠܠ ܥܡܟ ܥܠ ܟܠ ܡܕܡ ܕܡܬܦܩܕ ܠܟ ܕܬܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 11, 'ܘܟܕ ܠܐ ܡܬܚܙܐ ܗܘܐ ܠܝ ܡܛܠ ܬܫܒܘܚܬܗ ܕܢܘܗܪܐ ܗܘ ܐܚܕܘܢܝ ܒܐܝܕܝ ܗܢܘܢ ܕܥܡܝ ܗܘܘ ܘܥܠܬ ܠܕܪܡܤܘܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 12, 'ܘܓܒܪܐ ܚܕ ܚܢܢܝܐ ܟܐܢܐ ܒܢܡܘܤܐ ܐܝܟ ܕܤܗܕܝܢ ܗܘܘ ܥܠܘܗܝ ܟܠܗܘܢ ܝܗܘܕܝܐ ܕܬܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 13, 'ܐܬܐ ܠܘܬܝ ܘܐܡܪ ܠܝ ܫܐܘܠ ܐܚܝ ܦܬܚ ܥܝܢܝܟ ܘܒܗ ܒܫܥܬܐ ܐܬܦܬܚ ܥܝܢܝ ܘܚܪܬ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 14, 'ܘܐܡܪ ܠܝ ܐܠܗܐ ܕܐܒܗܬܢ ܐܩܝܡܟ ܠܡܕܥ ܨܒܝܢܗ ܘܬܚܙܐ ܠܙܕܝܩܐ ܘܬܫܡܥ ܩܠܐ ܡܢ ܦܘܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 15, 'ܘܬܗܘܐ ܠܗ ܤܗܕܐ ܠܘܬ ܟܠܗܘܢ ܒܢܝܢܫܐ ܥܠ ܟܘܠ ܡܐ ܕܚܙܝܬ ܘܫܡܥܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 16, 'ܘܗܫܐ ܡܢܐ ܡܫܬܘܚܪ ܐܢܬ ܩܘܡ ܥܡܕ ܘܐܬܕܟܐ ܡܢ ܚܛܗܝܟ ܟܕ ܩܪܐ ܐܢܬ ܫܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 17, 'ܘܗܦܟܬ ܐܬܝܬ ܠܟܐ ܠܐܘܪܫܠܡ ܘܨܠܝܬ ܒܗܝܟܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 18, 'ܘܚܙܝܬܗ ܒܚܙܘܐ ܟܕ ܐܡܪ ܠܝ ܐܤܬܪܗܒ ܘܦܘܩ ܠܟ ܡܢ ܐܘܪܫܠܡ ܡܛܠ ܕܠܐ ܡܩܒܠܝܢ ܤܗܕܘܬܟ ܕܥܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 19, 'ܘܐܢܐ ܐܡܪܬ ܡܪܝ ܐܦ ܗܢܘܢ ܝܕܥܝܢ ܕܐܢܐ ܡܫܠܡ ܗܘܝܬ ܠܒܝܬ ܐܤܝܪܐ ܘܡܚܐ ܗܘܝܬ ܒܟܠ ܟܢܘܫܢ ܠܐܝܠܝܢ ܕܡܗܝܡܢܝܢ ܗܘܘ ܒܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 20, 'ܘܟܕ ܡܬܐܫܕ ܗܘܐ ܕܡܗ ܕܐܤܛܦܢܤ ܤܗܕܟ ܘܐܦ ܐܢܐ ܥܡܗܘܢ ܩܐܡ ܗܘܝܬ ܘܫܠܡ ܗܘܝܬ ܠܨܒܝܢܗܘܢ ܕܩܛܘܠܘܗܝ ܘܢܛܪ ܗܘܝܬ ܡܐܢܐ ܕܐܝܠܝܢ ܕܪܓܡܝܢ ܗܘܘ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 21, 'ܘܐܡܪ ܠܝ ܙܠ ܕܐܢܐ ܡܫܕܪ ܐܢܐ ܠܟ ܠܪܘܚܩܐ ܠܡܟܪܙܘ ܠܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 22, 'ܘܟܕ ܫܡܥܘܗܝ ܠܦܘܠܘܤ ܥܕܡܐ ܠܗܕܐ ܡܠܬܐ ܐܪܝܡܘ ܩܠܗܘܢ ܘܩܥܘ ܢܫܬܩܠ ܡܢ ܐܪܥܐ ܐܝܢܐ ܕܗܟܢܐ ܗܘ ܠܐ ܓܝܪ ܘܠܐ ܠܗ ܠܡܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 23, 'ܘܟܕ ܡܒܓܢܝܢ ܗܘܘ ܘܡܫܕܝܢ ܗܘܘ ܡܐܢܝܗܘܢ ܘܡܤܩܝܢ ܗܘܘ ܚܠܐ ܠܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 24, 'ܦܩܕ ܟܠܝܪܟܐ ܕܢܥܠܘܢܗ ܠܡܫܪܝܬܐ ܘܦܩܕ ܕܒܢܓܕܐ ܢܫܬܐܠ ܐܝܟ ܕܢܕܥ ܡܛܠ ܐܝܕܐ ܥܠܬܐ ܩܥܝܢ ܗܘܘ ܥܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 25, 'ܘܟܕ ܡܬܚܘܗܝ ܒܥܪܩܐ ܐܡܪ ܗܘ ܦܘܠܘܤ ܠܩܢܛܪܘܢܐ ܕܩܐܡ ܗܘܐ ܥܠܘܗܝ ܡܦܤ ܠܟܘܢ ܕܠܓܒܪܐ ܪܗܘܡܝܐ ܕܠܐ ܡܚܝܒ ܕܬܢܓܕܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 26, 'ܘܟܕ ܫܡܥ ܩܢܛܪܘܢܐ ܩܪܒ ܠܘܬ ܟܠܝܪܟܐ ܘܐܡܪ ܠܗ ܡܢܐ ܥܒܕ ܐܢܬ ܗܢܐ ܓܝܪ ܓܒܪܐ ܪܗܘܡܝܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 27, 'ܘܩܪܒ ܠܘܬܗ ܟܠܝܪܟܐ ܘܐܡܪ ܠܗ ܐܡܪ ܠܝ ܐܢܬ ܪܗܘܡܝܐ ܐܢܬ ܘܐܡܪ ܠܗ ܐܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 28, 'ܘܥܢܐ ܟܠܝܪܟܐ ܘܐܡܪ ܐܢܐ ܒܟܤܦܐ ܤܓܝܐܐ ܩܢܝܬܗ ܠܪܗܘܡܝܘܬܐ ܐܡܪ ܠܗ ܦܘܠܘܤ ܐܢܐ ܕܝܢ ܐܦ ܒܗ ܐܬܝܠܕܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 29, 'ܘܡܚܕܐ ܦܪܩܘ ܠܗܘܢ ܡܢܗ ܗܢܘܢ ܕܒܥܝܢ ܗܘܘ ܠܡܢܓܕܘܬܗ ܘܕܚܠ ܟܠܝܪܟܐ ܟܕ ܝܠܦ ܕܪܗܘܡܝܐ ܗܘ ܥܠ ܕܦܟܪܗ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 30, 'ܘܠܝܘܡܐ ܐܚܪܢܐ ܨܒܐ ܗܘܐ ܠܡܕܥ ܫܪܝܪܐܝܬ ܕܡܢܐ ܗܝ ܩܛܓܪܢܘܬܐ ܕܡܝܬܝܢ ܗܘܘ ܥܠܘܗܝ ܝܗܘܕܝܐ ܘܫܪܝܗܝ ܘܦܩܕ ܕܢܐܬܘܢ ܪܒܝ ܟܗܢܐ ܘܟܠܗ ܟܢܫܐ ܕܪܫܝܗܘܢ ܘܕܒܪ ܠܦܘܠܘܤ ܘܐܚܬ ܐܩܝܡܗ ܒܝܢܬܗܘܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 1, 'ܘܟܕ ܚܪ ܦܘܠܘܤ ܒܟܢܫܗܘܢ ܐܡܪ ܓܒܪܐ ܐܚܝ ܐܢܐ ܒܟܠ ܬܐܪܬܐ ܛܒܬܐ ܐܬܕܒܪܬ ܩܕܡ ܐܠܗܐ ܥܕܡܐ ܠܝܘܡܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 2, 'ܘܚܢܢܝܐ ܟܗܢܐ ܦܩܕ ܠܗܢܘܢ ܕܩܝܡܝܢ ܥܠ ܓܒܗ ܕܢܡܚܘܢܗ ܠܦܘܠܘܤ ܥܠ ܦܘܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 3, 'ܘܦܘܠܘܤ ܐܡܪ ܠܗ ܥܬܝܕ ܗܘ ܐܠܗܐ ܕܢܡܚܝܟ ܐܤܬܐ ܡܚܘܪܬܐ ܘܐܢܬ ܝܬܒ ܐܢܬ ܕܐܢ ܐܢܬ ܠܝ ܐܝܟ ܕܒܢܡܘܤܐ ܟܕ ܥܒܪ ܐܢܬ ܥܠ ܢܡܘܤܐ ܘܦܩܕ ܐܢܬ ܕܢܡܚܘܢܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 4, 'ܘܐܝܠܝܢ ܕܩܝܡܝܢ ܗܘܘ ܬܡܢ ܐܡܪܝܢ ܠܗ ܠܟܗܢܐ ܕܐܠܗܐ ܡܨܚܐ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 5, 'ܐܡܪ ܠܗܘܢ ܦܘܠܘܤ ܠܐ ܝܕܥ ܗܘܝܬ ܐܚܝ ܕܟܗܢܐ ܗܘ ܟܬܝܒ ܗܘ ܓܝܪ ܕܠܪܫܐ ܕܥܡܟ ܠܐ ܬܠܘܛ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 6, 'ܘܟܕ ܝܕܥ ܦܘܠܘܤ ܕܡܢܗ ܕܥܡܐ ܐܝܬܘܗܝ ܕܙܕܘܩܝܐ ܘܡܢܗ ܕܦܪܝܫܐ ܩܥܐ ܗܘܐ ܒܟܢܫܐ ܓܒܪܐ ܐܚܝ ܐܢܐ ܦܪܝܫܐ ܐܢܐ ܒܪ ܦܪܝܫܐ ܘܥܠ ܤܒܪܐ ܕܩܝܡܬܐ ܕܡܝܬܐ ܡܬܕܝܢ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 7, 'ܘܟܕ ܗܕܐ ܐܡܪ ܢܦܠܘ ܚܕ ܒܚܕ ܦܪܝܫܐ ܘܙܕܘܩܝܐ ܘܐܬܦܠܓ ܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 8, 'ܙܕܘܩܝܐ ܓܝܪ ܐܡܪܝܢ ܕܠܝܬ ܩܝܡܬܐ ܘܠܐ ܡܠܐܟܐ ܘܠܐ ܪܘܚܐ ܦܪܝܫܐ ܕܝܢ ܡܘܕܝܢ ܒܟܠܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 9, 'ܘܗܘܐ ܩܠܐ ܪܒܐ ܘܩܡܘ ܐܢܫܐ ܤܦܪܐ ܡܢ ܓܒܐ ܕܦܪܝܫܐ ܘܢܨܝܢ ܗܘܘ ܥܡܗܘܢ ܘܐܡܪܝܢ ܠܐ ܡܫܟܚܝܢܢ ܡܕܡ ܕܒܝܫ ܒܗܢܐ ܓܒܪܐ ܐܢ ܕܝܢ ܪܘܚܐ ܐܘ ܡܠܐܟܐ ܡܠܠ ܥܡܗ ܡܢܐ ܐܝܬ ܒܗ ܒܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 10, 'ܘܟܕ ܗܘܐ ܫܓܘܫܝܐ ܪܒܐ ܒܝܢܬܗܘܢ ܕܚܠ ܗܘܐ ܟܠܝܪܟܐ ܕܠܡܐ ܢܦܫܚܘܢܗ ܠܦܘܠܘܤ ܘܫܠܚ ܠܪܗܘܡܝܐ ܕܢܐܬܘܢ ܢܚܛܦܘܢܗ ܡܢ ܡܨܥܬܗܘܢ ܘܢܥܠܘܢܗ ܠܡܫܪܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 11, 'ܘܟܕ ܗܘܐ ܠܠܝܐ ܐܬܚܙܝ ܠܗ ܡܪܢ ܠܦܘܠܘܤ ܘܐܡܪ ܠܗ ܐܬܚܝܠ ܡܛܠ ܕܐܝܟ ܕܐܤܗܕܬ ܥܠܝ ܒܐܘܪܫܠܡ ܗܟܢܐ ܥܬܝܕ ܐܢܬ ܕܐܦ ܒܪܗܘܡܐ ܬܤܗܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 12, 'ܘܟܕ ܗܘܐ ܨܦܪܐ ܐܬܟܢܫܘ ܗܘܘ ܐܢܫܝܢ ܡܢ ܝܗܘܕܝܐ ܘܐܚܪܡܘ ܥܠܝܗܘܢ ܕܠܐ ܢܐܟܠܘܢ ܘܠܐ ܢܫܬܘܢ ܥܕܡܐ ܕܢܩܛܠܘܢܗ ܠܦܘܠܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 13, 'ܗܘܝܢ ܗܘܘ ܕܝܢ ܗܢܘܢ ܕܐܩܝܡܘ ܒܡܘܡܬܐ ܗܢܐ ܩܝܡܐ ܝܬܝܪ ܡܢ ܐܪܒܥܝܢ ܓܒܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 14, 'ܘܐܬܩܪܒܘ ܠܘܬ ܟܗܢܐ ܘܠܘܬ ܩܫܝܫܐ ܘܐܡܪܝܢ ܗܘܘ ܕܚܪܡܐ ܐܚܪܡܢ ܥܠܝܢ ܕܡܕܡ ܠܐ ܢܛܥܡ ܥܕܡܐ ܕܢܩܛܘܠ ܠܦܘܠܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 15, 'ܘܗܫܐ ܒܥܘ ܐܢܬܘܢ ܘܪܫܐ ܕܟܢܘܫܬܐ ܡܢ ܟܠܝܪܟܐ ܕܢܝܬܝܘܗܝ ܠܘܬܟܘܢ ܐܝܟ ܒܥܝܢ ܐܢܬܘܢ ܕܬܒܨܘܢ ܫܪܝܪܐܝܬ ܤܘܥܪܢܗ ܘܚܢܢ ܡܛܝܒܝܢܢ ܕܢܩܛܠܝܘܗܝ ܥܕܠܐ ܢܡܛܐ ܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 16, 'ܘܫܡܥ ܗܘܐ ܒܪ ܚܬܗ ܕܦܘܠܘܤ ܐܦܪܤܢܐ ܗܢܐ ܘܥܠ ܠܡܫܪܝܬܐ ܘܒܕܩ ܠܦܘܠܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 17, 'ܘܫܕܪ ܦܘܠܘܤ ܩܪܐ ܠܚܕ ܡܢ ܩܢܛܪܘܢܐ ܘܐܡܪ ܠܗ ܐܘܒܠ ܠܥܠܝܡܐ ܗܢܐ ܠܘܬ ܟܠܝܪܟܐ ܐܝܬ ܠܗ ܓܝܪ ܡܕܡ ܕܢܐܡܪ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 18, 'ܘܕܒܪܗ ܩܢܛܪܘܢܐ ܠܥܠܝܡܐ ܘܐܥܠܗ ܠܘܬ ܟܠܝܪܟܐ ܘܐܡܪ ܦܘܠܘܤ ܐܤܝܪܐ ܩܪܢܝ ܘܒܥܐ ܡܢܝ ܕܐܝܬܐ ܗܢܐ ܥܠܝܡܐ ܠܘܬܟ ܕܐܝܬ ܠܗ ܡܕܡ ܕܢܐܡܪ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 19, 'ܘܐܚܕܗ ܒܐܝܕܗ ܟܠܝܪܟܐ ܠܥܠܝܡܐ ܘܢܓܕܗ ܠܚܕ ܓܒܐ ܘܡܫܐܠ ܗܘܐ ܠܗ ܕܡܢܐ ܐܝܬ ܠܟ ܕܬܐܡܪ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 20, 'ܘܐܡܪ ܠܗ ܥܠܝܡܐ ܝܗܘܕܝܐ ܐܬܚܫܒܘ ܕܢܒܥܘܢ ܡܢܟ ܕܬܚܬ ܠܦܘܠܘܤ ܡܚܪ ܠܟܢܫܗܘܢ ܐܝܟ ܨܒܝܢ ܡܕܡ ܝܬܝܪ ܕܢܐܠܦܘܢ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 21, 'ܐܢܬ ܗܟܝܠ ܠܐ ܬܬܛܦܝܤ ܠܗܘܢ ܗܐ ܓܝܪ ܝܬܝܪ ܡܢ ܐܪܒܥܝܢ ܓܒܪܝܢ ܡܢܗܘܢ ܢܛܪܝܢ ܠܗ ܒܟܡܐܢܐ ܘܐܚܪܡܘ ܥܠ ܢܦܫܗܘܢ ܕܠܐ ܢܐܟܠܘܢ ܘܠܐ ܢܫܬܘܢ ܥܕܡܐ ܕܢܩܛܠܘܢܗ ܘܗܐ ܡܛܝܒܝܢ ܘܡܩܘܝܢ ܠܫܘܘܕܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 22, 'ܘܫܪܝܗܝ ܟܠܝܪܟܐ ܠܥܠܝܡܐ ܟܕ ܦܩܕܗ ܕܐܢܫ ܠܐ ܢܕܥ ܕܗܠܝܢ ܒܕܩܬ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 23, 'ܘܩܪܐ ܠܬܪܝܢ ܩܢܛܪܘܢܝܢ ܘܐܡܪ ܠܗܘܢ ܙܠܘ ܥܬܕܘ ܪܗܘܡܝܐ ܡܐܬܝܢ ܕܢܐܙܠܘܢ ܠܩܤܪܝܐ ܘܦܪܫܐ ܫܒܥܝܢ ܘܫܕܝܝ ܒܝܡܝܢܐ ܡܐܬܝܢ ܕܢܦܩܘܢ ܡܢ ܬܠܬ ܫܥܝܢ ܒܠܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 24, 'ܛܝܒܘ ܕܝܢ ܐܦ ܒܥܝܪܐ ܐܝܟ ܕܢܪܟܒܘܢ ܠܦܘܠܘܤ ܘܢܦܠܛܘܢܗ ܠܘܬ ܦܝܠܟܤ ܗܓܡܘܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 25, 'ܘܟܬܒ ܐܓܪܬܐ ܝܗܒ ܠܗܘܢ ܕܐܝܬ ܒܗ ܗܟܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 26, 'ܠܘܤܝܘܤ ܠܦܝܠܟܤ ܗܓܡܘܢܐ ܢܨܝܚܐ ܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 27, 'ܠܓܒܪܐ ܗܢܐ ܐܚܕܘ ܝܗܘܕܝܐ ܐܝܟ ܕܢܩܛܠܘܢܗ ܘܩܡܬ ܐܢܐ ܥܡ ܪܗܘܡܝܐ ܘܦܪܩܬܗ ܟܕ ܝܠܦܬ ܕܪܗܘܡܝܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 28, 'ܘܟܕ ܒܥܐ ܗܘܝܬ ܠܡܕܥ ܥܠܬܐ ܕܡܛܠܬܗ ܪܫܝܢ ܗܘܘ ܠܗ ܐܚܬܬܗ ܠܟܢܫܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 29, 'ܘܐܫܟܚܬ ܕܥܠ ܙܛܡܐ ܕܢܡܘܤܗܘܢ ܪܫܝܢ ܗܘܘ ܠܗ ܘܥܠܬܐ ܕܫܘܝܐ ܠܐܤܘܪܐ ܐܘ ܠܡܘܬܐ ܠܝܬ ܗܘܐ ܠܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 30, 'ܘܟܕ ܐܬܒܕܩ ܠܝ ܢܟܠܐ ܒܟܡܐܢܐ ܕܥܒܕܘ ܥܠܘܗܝ ܝܗܘܕܝܐ ܡܚܕܐ ܫܕܪܬܗ ܠܘܬܟ ܘܦܩܕܬ ܠܩܛܓܪܢܘܗܝ ܕܢܐܬܘܢ ܘܢܐܡܪܘܢ ܥܡܗ ܩܕܡܝܟ ܗܘܝ ܚܠܝܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 31, 'ܗܝܕܝܢ ܪܗܘܡܝܐ ܐܝܟ ܕܐܬܦܩܕܘ ܕܒܪܘܗܝ ܠܦܘܠܘܤ ܒܠܠܝܐ ܘܐܝܬܝܘܗܝ ܠܐܢܛܝܦܛܪܤ ܡܕܝܢܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 32, 'ܘܠܝܘܡܐ ܐܚܪܢܐ ܫܪܘ ܦܪܫܐ ܠܪܓܠܐ ܚܒܪܝܗܘܢ ܕܢܗܦܟܘܢ ܠܡܫܪܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 33, 'ܘܐܝܬܝܘܗܝ ܠܩܤܪܝܐ ܘܝܗܒܘ ܐܓܪܬܐ ܠܗܓܡܘܢܐ ܘܐܩܝܡܘܗܝ ܩܕܡܘܗܝ ܠܦܘܠܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 34, 'ܘܟܕ ܩܪܐ ܐܓܪܬܐ ܡܫܐܠ ܗܘܐ ܠܗ ܕܡܢ ܐܝܕܐ ܗܘܦܪܟܝܐ ܐܝܬܘܗܝ ܘܟܕ ܝܠܦ ܕܡܢ ܩܝܠܝܩܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 35, 'ܐܡܪ ܠܗ ܫܡܥ ܐܢܐ ܠܟ ܡܐ ܕܐܬܘ ܩܛܓܪܢܝܟ ܘܦܩܕ ܕܢܛܪܘܢܗ ܒܦܪܛܘܪܝܢ ܕܗܪܘܕܤ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 1, 'ܘܡܢ ܒܬܪ ܝܘܡܬܐ ܚܡܫܐ ܢܚܬ ܚܢܢܝܐ ܪܒ ܟܗܢܐ ܥܡ ܩܫܝܫܐ ܘܥܡ ܛܪܛܠܘܤ ܪܗܛܪܐ ܘܐܘܕܥܘ ܠܗܓܡܘܢܐ ܥܠ ܦܘܠܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 2, 'ܘܟܕ ܐܬܩܪܝ ܐܩܦ ܗܘܐ ܛܪܛܠܘܤ ܡܩܛܪܓ ܠܗ ܘܐܡܪ ܒܤܘܓܐܐ ܕܫܝܢܐ ܥܡܪܝܢܢ ܡܛܠܬܟ ܘܬܩܢܬܐ ܤܓܝܐܬܐ ܗܘܝ ܠܥܡܐ ܗܢܐ ܒܫܩܠ ܛܥܢܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 3, 'ܘܟܠܢ ܒܟܠ ܕܘܟ ܡܩܒܠܝܢܢ ܛܝܒܘܬܟ ܢܨܝܚܐ ܦܝܠܟܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 4, 'ܕܠܐ ܕܝܢ ܢܠܐܝܟ ܒܤܓܝܐܬܐ ܒܥܐ ܐܢܐ ܡܢܟ ܕܬܫܡܥ ܠܡܟܝܟܘܬܢ ܒܦܤܝܩܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 5, 'ܐܫܟܚܢ ܓܝܪ ܠܓܒܪܐ ܗܢܐ ܕܐܝܬܘܗܝ ܡܫܚܛܢܐ ܘܡܥܝܪ ܫܓܘܫܝܐ ܠܟܠܗܘܢ ܝܗܘܕܝܐ ܕܒܟܠܗ ܐܪܥܐ ܪܫܐ ܗܘ ܓܝܪ ܕܝܘܠܦܢܐ ܕܢܨܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 6, 'ܘܠܗܝܟܠܢ ܨܒܐ ܠܡܤܝܒܘ ܘܟܕ ܐܚܕܢܝܗܝ ܒܥܝܢ ܕܢܕܘܢܝܘܗܝ ܐܝܟ ܕܒܢܡܘܤܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 7, 'ܐܬܐ ܕܝܢ ܠܘܤܝܘܤ ܟܠܝܪܟܐ ܘܒܩܛܝܪܐ ܤܓܝܐܐ ܡܢ ܐܝܕܝܢ ܐܥܕܝܗ ܘܠܟ ܫܕܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 8, 'ܘܦܩܕ ܠܩܛܪܓܢܘܗܝ ܕܢܐܬܘܢ ܠܘܬܟ ܘܡܫܟܚ ܐܢܬ ܟܕ ܡܫܐܠ ܐܢܬ ܠܗ ܕܬܐܠܦ ܡܢܗ ܥܠ ܟܠܗܝܢ ܗܠܝܢ ܕܡܩܛܪܓܝܢܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 9, 'ܐܪܝܒܘ ܕܝܢ ܥܠܘܗܝ ܐܦ ܗܢܘܢ ܝܗܘܕܝܐ ܟܕ ܐܡܪܝܢ ܕܗܠܝܢ ܗܟܢܐ ܐܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 10, 'ܘܪܡܙ ܗܓܡܘܢܐ ܠܦܘܠܘܤ ܕܢܡܠܠ ܘܥܢܐ ܦܘܠܘܤ ܘܐܡܪ ܡܢ ܫܢܝܐ ܤܓܝܐܬܐ ܝܕܥ ܐܢܐ ܕܐܝܬܝܟ ܕܝܢܐ ܕܥܡܐ ܗܢܐ ܘܡܛܠ ܗܢܐ ܚܕܝܐܝܬ ܢܦܩ ܐܢܐ ܪܘܚܐ ܥܠ ܐܦܝ ܢܦܫܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 11, 'ܟܕ ܡܫܟܚ ܐܢܬ ܠܡܕܥ ܕܠܝܬ ܠܝ ܝܬܝܪ ܡܢ ܬܪܥܤܪ ܝܘܡܝܢ ܕܤܠܩܬ ܠܐܘܪܫܠܡ ܠܡܤܓܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 12, 'ܘܠܐ ܐܫܟܚܘܢܝ ܕܡܡܠܠ ܐܢܐ ܥܡ ܐܢܫ ܒܗܝܟܠܐ ܘܐܦܠܐ ܟܢܫܐ ܕܟܢܫ ܐܢܐ ܠܐ ܒܟܢܘܫܬܗܘܢ ܘܠܐ ܒܡܕܝܢܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 13, 'ܘܠܐ ܕܢܚܘܘܢ ܡܛܝܐ ܒܐܝܕܝܗܘܢ ܩܕܡܝܟ ܥܠ ܡܕܡ ܕܗܫܐ ܡܩܛܪܓܝܢ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 14, 'ܒܪܡ ܕܝܢ ܗܕܐ ܡܘܕܐ ܐܢܐ ܕܒܗ ܒܗܢܐ ܝܘܠܦܢܐ ܕܐܡܪܝܢ ܒܗ ܦܠܚ ܐܢܐ ܠܐܠܗܐ ܕܐܒܗܝ ܟܕ ܡܗܝܡܢ ܐܢܐ ܠܟܠܗܝܢ ܕܟܬܝܒܢ ܒܢܡܘܤܐ ܘܒܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 15, 'ܘܟܕ ܐܝܬ ܠܝ ܤܒܪܐ ܥܠ ܐܠܗܐ ܐܝܢܐ ܕܐܦ ܗܢܘܢ ܗܠܝܢ ܡܤܒܪܝܢ ܕܥܬܝܕܐ ܕܬܗܘܐ ܩܝܡܬܐ ܕܡܢ ܒܝܬ ܡܝܬܐ ܕܟܐܢܐ ܘܕܥܘܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 16, 'ܡܛܠ ܗܢܐ ܐܦ ܥܡܠ ܐܢܐ ܕܬܐܪܬܐ ܕܟܝܬܐ ܬܗܘܐ ܠܝ ܩܕܡ ܐܠܗܐ ܘܩܕܡ ܒܢܝ ܐܢܫܐ ܐܡܝܢܐܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 17, 'ܠܫܢܝܢ ܕܝܢ ܤܓܝܐܢ ܐܬܝܬ ܠܘܬ ܒܢܝ ܥܡܐ ܕܝܠܝ ܕܐܬܠ ܙܕܩܬܐ ܘܐܩܪܒ ܩܘܪܒܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 18, 'ܘܐܫܟܚܘܢܝ ܗܠܝܢ ܒܗܝܟܠܐ ܟܕ ܡܕܟܝ ܐܢܐ ܠܐ ܥܡ ܟܢܫܐ ܐܦܠܐ ܒܫܓܘܫܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 19, 'ܐܠܐ ܐܢ ܕܫܓܫܘ ܐܢܫܝܢ ܝܗܘܕܝܐ ܕܐܬܘ ܡܢ ܐܤܝܐ ܐܝܠܝܢ ܕܘܠܐ ܗܘܐ ܕܢܩܘܡܘܢ ܥܡܝ ܩܕܡܝܟ ܘܢܩܛܪܓܘܢ ܡܕܡ ܕܐܝܬ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 20, 'ܐܘ ܗܢܘܢ ܗܠܝܢ ܢܐܡܪܘܢ ܡܢܐ ܐܫܟܚܘ ܒܝ ܤܟܠܘܬܐ ܟܕ ܩܡܬ ܩܕܡ ܟܢܫܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 21, 'ܐܠܐ ܐܢ ܗܕܐ ܚܕܐ ܡܠܬܐ ܕܩܥܝܬ ܟܕ ܩܐܡ ܐܢܐ ܒܝܢܬܗܘܢ ܕܥܠ ܩܝܡܬܐ ܕܡܝܬܐ ܡܬܕܝܢ ܐܢܐ ܝܘܡܢܐ ܩܕܡܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 22, 'ܦܝܠܟܤ ܕܝܢ ܡܛܠ ܕܝܕܥ ܗܘܐ ܠܗ ܠܐܘܪܚܐ ܗܕܐ ܡܠܝܐܝܬ ܬܗܝ ܐܢܘܢ ܟܕ ܐܡܪ ܕܡܐ ܕܐܬܐ ܟܠܝܪܟܐ ܫܡܥ ܐܢܐ ܒܝܢܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 23, 'ܘܦܩܕ ܠܩܢܛܪܘܢܐ ܕܢܛܪܝܘܗܝ ܠܦܘܠܘܤ ܒܢܝܚܐ ܘܕܠܐ ܐܢܫ ܡܢ ܝܕܘܥܘܗܝ ܢܬܟܠܐ ܕܢܗܘܐ ܡܫܡܫ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 24, 'ܘܡܢ ܒܬܪ ܩܠܝܠ ܝܘܡܬܐ ܫܕܪ ܦܝܠܟܤ ܘܕܘܪܤܠܐ ܐܢܬܬܗ ܕܐܝܬܝܗ ܗܘܬ ܝܗܘܕܝܬܐ ܘܩܪܘ ܠܗ ܠܦܘܠܘܤ ܘܫܡܥܘ ܡܢܗ ܥܠ ܗܝܡܢܘܬܐ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 25, 'ܘܟܕ ܡܡܠܠ ܥܡܗܘܢ ܥܠ ܙܕܝܩܘܬܐ ܘܥܠ ܩܕܝܫܘܬܐ ܘܥܠ ܕܝܢܐ ܕܥܬܝܕ ܐܬܡܠܝ ܕܚܠܬܐ ܦܝܠܟܤ ܘܐܡܪ ܕܗܫܐ ܙܠ ܘܐܡܬܝ ܕܢܗܘܐ ܠܝ ܐܬܪܐ ܐܫܕܪ ܒܬܪܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 26, 'ܤܒܪ ܗܘܐ ܓܝܪ ܕܫܘܚܕܐ ܡܬܝܗܒ ܗܘܐ ܠܗ ܡܢ ܦܘܠܘܤ ܡܛܠ ܗܕܐ ܐܦ ܐܡܝܢܐܝܬ ܡܫܕܪ ܗܘܐ ܡܝܬܐ ܠܗ ܘܡܡܠܠ ܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 27, 'ܘܟܕ ܡܠܝ ܠܗ ܬܪܬܝܢ ܫܢܝܢ ܐܚܪܢܐ ܗܓܡܘܢܐ ܐܬܐ ܗܘܐ ܠܕܘܟܬܗ ܕܡܬܩܪܐ ܗܘܐ ܦܪܩܝܘܤ ܦܗܤܛܘܤ ܦܝܠܟܤ ܕܝܢ ܐܝܟ ܕܢܥܒܕ ܛܝܒܘܬܐ ܒܝܗܘܕܝܐ ܫܒܩܗ ܠܦܘܠܘܤ ܟܕ ܐܤܝܪ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 1, 'ܘܟܕ ܐܬܐ ܦܗܤܛܘܤ ܠܩܤܪܝܐ ܒܬܪ ܬܠܬܐ ܝܘܡܝܢ ܤܠܩ ܠܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 2, 'ܘܐܘܕܥܘܗܝ ܪܒܝ ܟܗܢܐ ܘܪܫܐ ܕܝܗܘܕܝܐ ܥܠ ܦܘܠܘܤ ܘܒܥܝܢ ܗܘܘ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 3, 'ܟܕ ܫܐܠܝܢ ܠܗ ܗܕܐ ܛܝܒܘܬܐ ܕܢܫܕܪ ܢܝܬܝܘܗܝ ܠܐܘܪܫܠܡ ܟܕ ܥܒܕܝܢ ܗܘܘ ܟܡܐܢܐ ܒܐܘܪܚܐ ܕܢܩܛܠܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 4, 'ܘܦܗܤܛܘܤ ܦܢܝ ܦܬܓܡܐ ܕܦܘܠܘܤ ܡܬܢܛܪ ܒܩܤܪܝܐ ܘܐܢܐ ܡܤܪܗܒ ܐܢܐ ܕܐܚܙܘܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 5, 'ܐܝܠܝܢ ܗܟܝܠ ܕܐܝܬ ܒܟܘܢ ܕܡܛܝܐ ܒܐܝܕܝܗܘܢ ܢܚܬܘܢ ܥܡܢ ܘܥܠ ܟܠ ܤܟܠܘ ܕܐܝܬ ܒܗ ܒܓܒܪܐ ܢܩܛܪܓܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 6, 'ܘܟܕ ܗܘܐ ܬܡܢ ܝܘܡܬܐ ܬܡܢܝܐ ܐܘ ܥܤܪܐ ܢܚܬ ܠܗ ܠܩܤܪܝܐ ܘܠܝܘܡܐ ܐܚܪܢܐ ܝܬܒ ܥܠ ܒܝܡ ܘܦܩܕ ܕܢܝܬܘܢ ܠܦܘܠܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 7, 'ܘܟܕ ܐܬܐ ܚܕܪܘܗܝ ܝܗܘܕܝܐ ܕܢܚܬܘ ܡܢ ܐܘܪܫܠܡ ܘܪܫܐ ܤܓܝܐܐ ܘܩܫܝܐ ܡܝܬܝܢ ܗܘܘ ܒܬܪܗ ܐܝܠܝܢ ܕܠܐ ܡܫܟܚܝܢ ܗܘܘ ܕܢܚܘܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 8, 'ܘܟܕ ܦܘܠܘܤ ܢܦܩ ܗܘܐ ܪܘܚܐ ܕܠܐ ܐܤܟܠ ܡܕܡ ܠܐ ܒܢܡܘܤܐ ܕܝܗܘܕܝܐ ܘܠܐ ܒܗܝܟܠܐ ܘܠܐ ܒܩܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 9, 'ܦܗܤܛܘܤ ܕܝܢ ܡܛܠ ܕܨܒܐ ܗܘܐ ܕܢܪܫܐ ܛܝܒܘܬܐ ܒܝܗܘܕܝܐ ܐܡܪ ܠܗ ܠܦܘܠܘܤ ܨܒܐ ܐܢܬ ܕܬܤܩ ܠܐܘܪܫܠܡ ܘܬܡܢ ܥܠ ܗܠܝܢ ܬܬܕܝܢ ܩܕܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 10, 'ܥܢܐ ܦܘܠܘܤ ܘܐܡܪ ܥܠ ܒܝܡ ܕܩܤܪ ܩܐܡ ܐܢܐ ܗܪܟܐ ܗܘ ܙܕܩ ܠܝ ܠܡܬܕܢܘ ܠܐ ܡܕܡ ܚܛܝܬ ܠܝܗܘܕܝܐ ܐܝܟ ܕܐܦ ܐܢܬ ܝܕܥ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 11, 'ܘܐܢ ܤܟܠܘܬܐ ܥܒܝܕܐ ܠܝ ܐܘ ܡܕܡ ܕܫܘܐ ܠܡܘܬܐ ܠܐ ܡܫܬܐܠ ܐܢܐ ܡܢ ܡܘܬܐ ܐܢ ܕܝܢ ܠܝܬ ܡܕܡ ܨܐܕܝ ܡܢ ܗܠܝܢ ܕܡܩܛܪܓܝܢ ܠܝ ܠܐ ܐܢܫ ܝܗܒ ܠܝ ܠܗܘܢ ܡܘܗܒܬ ܒܓܢܘܗܝ ܕܩܤܪ ܩܪܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 12, 'ܗܝܕܝܢ ܦܗܤܛܘܤ ܡܠܠ ܥܡ ܒܢܝ ܡܠܟܗ ܘܐܡܪ ܒܓܢ ܩܤܪ ܩܪܝܬ ܠܘܬ ܩܤܪ ܐܙܠ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 13, 'ܘܟܕ ܗܘܘ ܝܘܡܬܐ ܢܚܬ ܐܓܪܦܘܤ ܡܠܟܐ ܘܒܪܢܝܩܐ ܠܩܤܪܝܐ ܕܢܫܐܠܘܢ ܫܠܡܗ ܕܦܗܤܛܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 14, 'ܘܟܕ ܗܘܘ ܠܘܬܗ ܝܘܡܬܐ ܐܫܬܥܝ ܦܗܤܛܘܤ ܠܡܠܟܐ ܕܝܢܗ ܕܦܘܠܘܤ ܟܕ ܐܡܪ ܓܒܪܐ ܚܕ ܐܤܝܪܐ ܐܫܬܒܩ ܡܢ ܐܝܕܝ ܦܝܠܟܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 15, 'ܘܟܕ ܗܘܝܬ ܒܐܘܪܫܠܡ ܐܘܕܥܘ ܠܝ ܥܠܘܗܝ ܪܒܝ ܟܗܢܐ ܘܩܫܝܫܐ ܕܝܗܘܕܝܐ ܘܒܥܘ ܕܐܥܒܕ ܠܗܘܢ ܕܝܢܐ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 16, 'ܘܐܡܪܬ ܠܗܘܢ ܕܠܝܬ ܥܝܕܐ ܠܪܗܘܡܝܐ ܕܢܬܠܘܢ ܒܪܢܫܐ ܡܘܗܒܬ ܠܩܛܠܐ ܥܕܡܐ ܕܢܐܬܐ ܒܥܠ ܕܝܢܗ ܘܢܟܤܝܘܗܝ ܒܐܦܘܗܝ ܘܢܬܝܗܒ ܠܗ ܐܬܪܐ ܠܡܦܩ ܒܪܘܚܐ ܥܠ ܡܐ ܕܡܬܪܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 17, 'ܘܟܕ ܐܬܝܬ ܠܟܐ ܕܠܐ ܬܘܗܝܐ ܠܝܘܡܐ ܐܚܪܢܐ ܝܬܒܬ ܥܠ ܒܝܡ ܘܦܩܕܬ ܕܢܝܬܘܢܝܗܝ ܠܝ ܠܓܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 18, 'ܘܩܡܘ ܥܡܗ ܩܛܪܓܢܘܗܝ ܘܠܐ ܐܫܟܚܘ ܡܕܡ ܪܫܝܢܐ ܒܝܫܐ ܕܢܚܘܘܢ ܥܠܘܗܝ ܐܝܟ ܡܐ ܕܤܒܪ ܗܘܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 19, 'ܙܛܡܐ ܕܝܢ ܡܕܡ ܡܕܡ ܥܠ ܕܚܠܬܗܘܢ ܐܝܬ ܗܘܐ ܠܗܘܢ ܠܘܬܗ ܘܥܠ ܝܫܘܥ ܐܢܫ ܕܡܝܬ ܗܘ ܕܐܡܪ ܗܘܐ ܦܘܠܘܤ ܕܚܝ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 20, 'ܘܡܛܠ ܕܠܐ ܩܐܡ ܗܘܝܬ ܐܢܐ ܥܠ ܒܥܬܐ ܕܗܠܝܢ ܐܡܪܬ ܗܘܝܬ ܠܦܘܠܘܤ ܕܐܢ ܒܥܐ ܐܢܬ ܕܬܐܙܠ ܠܐܘܪܫܠܡ ܘܬܡܢ ܬܬܕܝܢ ܥܠ ܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 21, 'ܗܘ ܕܝܢ ܒܥܐ ܕܢܬܢܛܪ ܠܕܝܢܗ ܕܩܤܪ ܘܦܩܕܬ ܕܢܬܢܛܪ ܥܕܡܐ ܕܐܫܕܪܝܘܗܝ ܠܘܬ ܩܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 22, 'ܘܐܡܪ ܐܓܪܦܘܤ ܨܒܐ ܗܘܝܬ ܕܐܫܡܥܝܘܗܝ ܠܓܒܪܐ ܗܢܐ ܘܦܗܤܛܘܤ ܐܡܪ ܕܠܡܚܪ ܫܡܥ ܐܢܬ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 23, 'ܘܠܝܘܡܐ ܐܚܪܢܐ ܐܬܐ ܐܓܪܦܘܤ ܘܒܪܢܝܩܐ ܒܙܘܚܐ ܤܓܝܐܐ ܘܥܠ ܠܒܝܬ ܕܝܢܐ ܥܡ ܟܠܝܪܟܐ ܘܪܫܐ ܕܡܕܝܢܬܐ ܘܦܩܕ ܦܗܤܛܘܤ ܘܐܬܐ ܦܘܠܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 24, 'ܘܐܡܪ ܦܗܤܛܘܤ ܐܓܪܦܘܤ ܡܠܟܐ ܘܟܠܟܘܢ ܓܒܪܐ ܕܐܝܬܝܟܘܢ ܥܡܢ ܥܠ ܗܢܐ ܓܒܪܐ ܕܚܙܝܢ ܐܢܬܘܢ ܟܠܗ ܥܡܐ ܕܝܗܘܕܝܐ ܩܒܠܢܝ ܒܐܘܪܫܠܡ ܘܗܪܟܐ ܟܕ ܩܥܝܢ ܕܠܐ ܬܘܒ ܘܠܐ ܠܗܢܐ ܕܢܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 25, 'ܐܢܐ ܕܝܢ ܐܕܪܟܬ ܕܡܕܡ ܕܫܘܐ ܠܡܘܬܐ ܠܐ ܤܥܝܪ ܠܗ ܘܡܛܠ ܕܗܘ ܒܥܐ ܕܢܬܢܛܪ ܠܕܝܢܗ ܕܩܤܪ ܦܩܕܬ ܕܢܫܬܕܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 26, 'ܘܠܐ ܝܕܥ ܐܢܐ ܡܢܐ ܐܟܬܘܒ ܥܠܘܗܝ ܠܩܤܪ ܡܛܠ ܗܢܐ ܨܒܝܬ ܠܡܝܬܝܘܬܗ ܩܕܡܝܟܘܢ ܘܝܬܝܪܐܝܬ ܩܕܡܝܟ ܡܠܟܐ ܐܓܪܦܐ ܕܡܐ ܕܐܫܬܐܠ ܕܝܢܗ ܐܫܟܚ ܡܢܐ ܐܟܬܘܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 27, 'ܠܐ ܓܝܪ ܘܠܐ ܕܟܕ ܡܫܕܪܝܢܢ ܓܒܪܐ ܐܤܝܪܐ ܕܠܐ ܢܟܬܘܒ ܤܟܠܘܬܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 1, 'ܘܐܡܪ ܐܓܪܦܘܤ ܠܦܘܠܘܤ ܡܦܤ ܠܟ ܠܡܡܠܠܘ ܥܠ ܐܦܝ ܢܦܫܟ ܗܝܕܝܢ ܦܘܠܘܤ ܦܫܛ ܐܝܕܗ ܘܢܦܩ ܗܘܐ ܪܘܚܐ ܘܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 2, 'ܥܠ ܟܠ ܡܐ ܕܡܬܪܫܐ ܐܢܐ ܡܢ ܝܗܘܕܝܐ ܡܠܟܐ ܐܓܪܦܐ ܤܒܪ ܐܢܐ ܥܠ ܢܦܫܝ ܕܛܘܒܢܐ ܐܢܐ ܕܩܕܡܝܟ ܝܘܡܢܐ ܢܦܩ ܐܢܐ ܪܘܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 3, 'ܝܬܝܪܐܝܬ ܕܝܕܥ ܐܢܐ ܕܡܦܤ ܐܢܬ ܒܟܠܗܘܢ ܙܛܡܐ ܘܢܡܘܤܐ ܕܝܗܘܕܝܐ ܡܛܠ ܗܢܐ ܒܥܐ ܐܢܐ ܡܢܟ ܕܒܡܓܪܬ ܪܘܚܐ ܬܫܡܥܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 4, 'ܝܕܥܝܢ ܐܢܘܢ ܓܝܪ ܐܦ ܗܢܘܢ ܝܗܘܕܝܐ ܐܢ ܨܒܝܢ ܕܢܤܗܕܘܢ ܕܘܒܪܝ ܕܡܢ ܛܠܝܘܬܝ ܕܗܘܘ ܠܝ ܡܢ ܫܘܪܝܐ ܒܥܡܝ ܘܒܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 5, 'ܡܛܠ ܕܡܢ ܢܘܓܪܐ ܡܦܤܝܢ ܒܝ ܘܝܕܥܝܢ ܕܒܝܘܠܦܢܐ ܪܫܝܐ ܕܦܪܝܫܐ ܚܝܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 6, 'ܘܗܫܐ ܥܠ ܤܒܪܐ ܕܫܘܘܕܝܐ ܕܗܘܐ ܗܘܐ ܠܐܒܗܬܢ ܡܢ ܐܠܗܐ ܩܐܡ ܐܢܐ ܘܡܬܕܝܢ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 7, 'ܕܥܠ ܗܢܐ ܤܒܪܐ ܬܪܬܥܤܪܐ ܫܪܒܢ ܡܤܒܪܢ ܕܢܡܢܥܢ ܒܨܠܘܬܐ ܚܦܝܛܬܐ ܕܐܝܡܡܐ ܘܕܠܠܝܐ ܘܥܠܘܗܝ ܥܠ ܗܢܐ ܤܒܪܐ ܡܬܪܫܐ ܐܢܐ ܡܢ ܐܝܕܝ ܝܗܘܕܝܐ ܡܠܟܐ ܐܓܪܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 8, 'ܡܢܐ ܕܝܢܝܢ ܐܢܬܘܢ ܠܐ ܘܠܐ ܕܢܗܝܡܢ ܕܡܩܝܡ ܐܠܗܐ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 9, 'ܐܢܐ ܓܝܪ ܡܢ ܩܕܝܡ ܤܡܬ ܒܪܥܝܢܝ ܕܤܩܘܒܠܐ ܤܓܝܐܐ ܐܤܥܘܪ ܠܘܩܒܠ ܫܡܗ ܕܝܫܘܥ ܢܨܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 10, 'ܗܕܐ ܕܥܒܕܬ ܐܦ ܒܐܘܪܫܠܡ ܘܩܕܝܫܐ ܤܓܝܐܐ ܐܪܡܝܬ ܒܝܬ ܐܤܝܪܐ ܒܫܘܠܛܢܐ ܕܩܒܠܬ ܡܢ ܪܒܝ ܟܗܢܐ ܘܟܕ ܡܬܩܛܠܝܢ ܗܘܘ ܡܢܗܘܢ ܐܫܬܘܬܦܬ ܠܐܝܠܝܢ ܕܚܝܒܘ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 11, 'ܘܒܟܠ ܟܢܘܫܐ ܡܫܬܢܕ ܗܘܝܬ ܒܗܘܢ ܟܕ ܐܠܨ ܗܘܝܬ ܕܢܗܘܘܢ ܡܓܕܦܝܢ ܒܫܡܗ ܕܝܫܘܥ ܘܒܪܘܓܙܐ ܤܓܝܐܐ ܕܡܠܐ ܗܘܝܬ ܥܠܝܗܘܢ ܐܦ ܠܡܕܝܢܬܐ ܐܚܪܢܝܬܐ ܢܦܩ ܗܘܝܬ ܠܡܪܕܦ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 12, 'ܘܟܕ ܐܙܠ ܗܘܝܬ ܡܛܠ ܗܕܐ ܠܕܪܡܤܘܩ ܒܫܘܠܛܢܐ ܘܒܡܦܤܢܘܬܐ ܕܪܒܝ ܟܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 13, 'ܒܦܠܓܗ ܕܝܘܡܐ ܒܐܘܪܚܐ ܚܙܝܬ ܡܢ ܫܡܝܐ ܐܘ ܡܠܟܐ ܕܐܙܠܓ ܥܠܝ ܘܥܠ ܟܠ ܕܥܡܝ ܗܘܘ ܢܘܗܪܐ ܕܡܝܬܪ ܡܢ ܕܫܡܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 14, 'ܘܢܦܠܢ ܟܠܢ ܥܠ ܐܪܥܐ ܘܫܡܥܬ ܩܠܐ ܟܕ ܐܡܪ ܠܝ ܥܒܪܐܝܬ ܫܐܘܠ ܫܐܘܠ ܡܢܐ ܪܕܦ ܐܢܬ ܠܝ ܩܫܐ ܗܘ ܠܟ ܠܡܒܥܛܘ ܠܥܘܩܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 15, 'ܘܐܢܐ ܐܡܪܬ ܡܢ ܐܢܬ ܡܪܝ ܘܡܪܢ ܐܡܪ ܠܝ ܐܢܐ ܗܘ ܝܫܘܥ ܢܨܪܝܐ ܕܐܢܬ ܪܕܦ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 16, 'ܘܐܡܪ ܠܝ ܩܘܡ ܥܠ ܪܓܠܝܟ ܡܛܠ ܗܕܐ ܓܝܪ ܐܬܚܙܝܬ ܠܟ ܕܐܩܝܡܟ ܡܫܡܫܢܐ ܘܤܗܕܐ ܕܡܕܡ ܕܚܙܝܬܢܝ ܘܕܥܬܝܕ ܐܢܬ ܕܬܚܙܝܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 17, 'ܘܐܦܨܝܟ ܡܢ ܥܡܐ ܕܝܗܘܕܝܐ ܘܡܢ ܥܡܡܐ ܐܚܪܢܐ ܕܠܘܬܗܘܢ ܡܫܕܪ ܐܢܐ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 18, 'ܕܬܦܬܚ ܥܝܢܝܗܘܢ ܐܝܟ ܕܢܗܦܟܘܢ ܡܢ ܚܫܘܟܐ ܠܢܗܝܪܐ ܘܡܢ ܫܘܠܛܢܗ ܕܤܛܢܐ ܠܘܬ ܐܠܗܐ ܘܢܩܒܠܘܢ ܫܘܒܩܢ ܚܛܗܐ ܘܦܤܐ ܥܡ ܩܕܝܫܐ ܒܗܝܡܢܘܬܐ ܕܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 19, 'ܡܛܠ ܗܕܐ ܡܠܟܐ ܐܓܪܦܐ ܠܐ ܩܡܬ ܒܚܪܝܢܐ ܠܘܩܒܠ ܚܙܘܐ ܫܡܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 20, 'ܐܠܐ ܐܟܪܙܬ ܡܢ ܠܩܘܕܡܝܢ ܠܗܢܘܢ ܕܒܕܪܡܤܘܩ ܘܠܗܢܘܢ ܕܒܐܘܪܫܠܡ ܘܕܒܟܠܗܝܢ ܩܘܪܝܐ ܕܝܗܘܕ ܘܐܦ ܠܥܡܡܐ ܐܟܪܙܬ ܕܢܬܘܒܘܢ ܘܢܬܦܢܘܢ ܠܘܬ ܐܠܗܐ ܘܢܥܒܕܘܢ ܥܒܕܐ ܕܫܘܝܢ ܠܬܝܒܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 21, 'ܘܥܠ ܐܦܝ ܗܠܝܢ ܐܚܕܘܢܝ ܝܗܘܕܝܐ ܒܗܝܟܠܐ ܘܨܒܝܢ ܗܘܘ ܠܡܩܛܠܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 22, 'ܥܕܪܢܝ ܕܝܢ ܐܠܗܐ ܥܕܡܐ ܠܝܘܡܐ ܗܢܐ ܘܗܐ ܩܐܡ ܐܢܐ ܘܡܤܗܕ ܐܢܐ ܠܙܥܘܪܐ ܘܠܪܒܐ ܟܕ ܡܕܡ ܠܒܪ ܡܢ ܡܘܫܐ ܘܢܒܝܐ ܠܐ ܐܡܪ ܐܢܐ ܐܠܐ ܐܝܠܝܢ ܕܗܢܘܢ ܐܡܪܘ ܕܥܬܝܕܢ ܕܢܗܘܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 23, 'ܕܢܚܫ ܡܫܝܚܐ ܘܢܗܘܐ ܪܫܝܬܐ ܕܩܝܡܬܐ ܕܡܢ ܒܝܬ ܡܝܬܐ ܘܕܥܬܝܕ ܕܢܟܪܙ ܢܘܗܪܐ ܠܥܡܐ ܘܠܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 24, 'ܘܟܕ ܗܟܢܐ ܢܦܩ ܗܘܐ ܦܘܠܘܤ ܪܘܚܐ ܩܥܐ ܦܗܤܛܘܤ ܒܩܠܐ ܪܡܐ ܫܢܝܬ ܠܟ ܦܘܠܐ ܤܦܪܐ ܤܓܝܐܐ ܥܒܕܟ ܕܬܫܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 25, 'ܐܡܪ ܦܘܠܘܤ ܠܐ ܫܢܝܬ ܢܨܝܚܐ ܦܗܤܛܘܤ ܐܠܐ ܡܠܝ ܫܪܪܐ ܘܬܩܢܘܬܐ ܡܡܠܠ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 26, 'ܘܐܦ ܡܠܟܐ ܐܓܪܦܘܤ ܝܬܝܪܐܝܬ ܝܕܥ ܥܠܝܗܝܢ ܕܗܠܝܢ ܘܡܛܠ ܗܢܐ ܥܝܢ ܒܓܠܐ ܡܡܠܠ ܐܢܐ ܩܕܡܘܗܝ ܡܛܠ ܕܚܕܐ ܡܢ ܗܠܝܢ ܡܠܐ ܠܐ ܤܒܪ ܐܢܐ ܕܛܥܝܢ ܠܗ ܕܠܐ ܗܘܐ ܒܛܘܫܝܐ ܤܥܝܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 27, 'ܡܗܝܡܢ ܐܢܬ ܡܠܟܐ ܐܓܪܦܐ ܠܢܒܝܐ ܝܕܥ ܐܢܐ ܕܡܗܝܡܢ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 28, 'ܐܡܪ ܠܗ ܐܓܪܦܘܤ ܒܩܠܝܠ ܡܕܡ ܡܦܝܤ ܐܢܬ ܠܝ ܕܐܗܘܐ ܟܪܤܛܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 29, 'ܘܦܘܠܘܤ ܐܡܪ ܒܥܐ ܗܘܝܬ ܡܢ ܐܠܗܐ ܒܩܠܝܠ ܘܒܤܓܝ ܠܐ ܗܘܐ ܠܟ ܒܠܚܘܕ ܐܠܐ ܐܦ ܠܟܠܗܘܢ ܐܝܠܝܢ ܕܫܡܥܝܢ ܠܝ ܝܘܡܢܐ ܕܢܗܘܘܢ ܐܟܘܬܝ ܠܒܪ ܡܢ ܐܤܘܪܐ ܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 30, 'ܘܩܡ ܠܗ ܡܠܟܐ ܘܗܓܡܘܢܐ ܘܒܪܢܝܩܐ ܘܐܝܠܝܢ ܕܝܬܒܝܢ ܗܘܘ ܥܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 31, 'ܘܟܕ ܦܪܩܘ ܡܢ ܬܡܢ ܡܡܠܠܝܢ ܗܘܘ ܚܕ ܥܡ ܚܕ ܘܐܡܪܝܢ ܕܡܕܡ ܕܫܘܐ ܠܡܘܬܐ ܐܘ ܠܐܤܘܪܐ ܠܐ ܥܒܕ ܓܒܪܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 32, 'ܘܐܡܪ ܐܓܪܦܘܤ ܠܦܗܤܛܘܤ ܡܫܟܚ ܗܘܐ ܓܒܪܐ ܗܢܐ ܕܢܫܬܪܐ ܐܠܘ ܒܓܢ ܩܤܪ ܠܐ ܩܪܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 1, 'ܘܦܩܕ ܥܠܘܗܝ ܦܗܤܛܤ ܕܢܫܬܕܪ ܠܘܬ ܩܤܪ ܠܐܝܛܠܝܐ ܘܐܫܠܡܗ ܠܦܘܠܘܤ ܘܠܐܤܝܪܐ ܐܚܪܢܐ ܥܡܗ ܠܓܒܪܐ ܚܕ ܩܢܛܪܘܢܐ ܡܢ ܐܤܦܝܪ ܤܒܤܛܐ ܕܫܡܗ ܗܘܐ ܝܘܠܝܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 2, 'ܘܟܕ ܗܘܬ ܕܢܪܕܐ ܢܚܬܢ ܠܐܠܦܐ ܕܐܝܬܝܗ ܗܘܬ ܡܢ ܐܕܪܡܢܛܘܤ ܡܕܝܢܬܐ ܘܐܙܠܐ ܗܘܬ ܠܐܬܪܐ ܕܐܤܝܐ ܘܥܠ ܗܘܐ ܥܡܢ ܠܐܠܦܐ ܐܪܤܛܪܟܘܤ ܡܩܕܘܢܝܐ ܕܡܢ ܬܤܠܘܢܝܩܐ ܡܕܝܢܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 3, 'ܘܠܝܘܡܐ ܐܚܪܢܐ ܡܛܝܢ ܠܨܝܕܢ ܘܐܬܚܫܚ ܩܢܛܪܘܢܐ ܒܡܪܚܡܢܘܬܐ ܠܘܬ ܦܘܠܘܤ ܘܐܦܤ ܠܗ ܕܢܐܙܠ ܠܘܬ ܪܚܡܘܗܝ ܘܢܬܬܢܝܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 4, 'ܘܡܢ ܬܡܢ ܪܕܝܢ ܘܡܛܠ ܕܪܘܚܐ ܤܩܘܒܠܢ ܗܘܝ ܐܬܟܪܟܢ ܥܠ ܩܘܦܪܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 5, 'ܘܥܒܪܢ ܝܡܐ ܕܩܝܠܝܩܝܐ ܘܕܦܡܦܘܠܝܐ ܘܡܛܝܢ ܠܡܘܪܐ ܡܕܝܢܬܐ ܕܠܘܩܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 6, 'ܘܐܫܟܚ ܬܡܢ ܩܢܛܪܘܢܐ ܐܠܦܐ ܡܢ ܐܠܟܤܢܕܪܝܐ ܕܐܙܠܐ ܗܘܬ ܠܐܝܛܠܝܐ ܘܐܘܬܒܢ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 7, 'ܘܡܛܠ ܕܝܩܝܪܐܝܬ ܪܕܝܐ ܗܘܬ ܠܝܘܡܬܐ ܤܓܝܐܐ ܠܡܚܤܢ ܡܛܝܢ ܠܘܩܒܠ ܩܢܝܕܘܤ ܓܙܪܬܐ ܘܡܛܠ ܕܠܐ ܫܒܩܐ ܗܘܬ ܪܘܚܐ ܕܢܐܙܠ ܬܪܝܨܐܝܬ ܐܬܟܪܟܢ ܥܠ ܩܪܛܐ ܠܘܩܒܠ ܤܠܡܘܢܐ ܡܕܝܢܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 8, 'ܘܠܡܚܤܢ ܟܕ ܪܕܝܢܢ ܚܕܪܝܗ ܡܛܝܢ ܠܕܘܟܬܐ ܕܡܬܩܪܝܐ ܠܡܐܢܐ ܫܦܝܪܐ ܘܩܪܝܒܐ ܗܘܬ ܠܗ ܡܕܝܢܬܐ ܕܫܡܗ ܠܐܤܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 9, 'ܘܗܘܝܢ ܬܡܢ ܙܒܢܐ ܤܓܝܐܐ ܥܕܡܐ ܕܥܒܪ ܐܦ ܝܘܡܐ ܕܨܘܡܐ ܕܝܗܘܕܝܐ ܘܗܘܐ ܠܗ ܩܢܛܐ ܕܢܪܕܐ ܐܢܫ ܒܝܡܐ ܘܡܠܟ ܗܘܐ ܠܗܘܢ ܦܘܠܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 10, 'ܘܐܡܪ ܓܒܪܐ ܚܙܐ ܐܢܐ ܕܒܐܘܠܨܢܐ ܘܒܚܘܤܪܢܐ ܤܓܝܐܐ ܗܘܝܐ ܡܪܕܝܬܢ ܠܘ ܒܠܚܘܕ ܕܡܘܒܠܗ ܕܐܠܦܢ ܐܠܐ ܐܦ ܕܢܦܫܬܢ ܕܝܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 11, 'ܩܢܛܪܘܢܐ ܕܝܢ ܠܩܘܒܪܢܛܐ ܘܠܡܪܗ ܕܐܠܦܐ ܫܡܥ ܗܘܐ ܝܬܝܪ ܡܢ ܡܠܘܗܝ ܕܦܘܠܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 12, 'ܘܡܛܠ ܕܠܐ ܥܗܢ ܗܘܐ ܗܘ ܠܡܐܢܐ ܠܡܤܬܝܘ ܒܗ ܤܬܘܐ ܤܓܝܐܐ ܡܢܢ ܨܒܝܢ ܗܘܘ ܕܢܪܕܐ ܡܢ ܬܡܢ ܘܐܢܗܘ ܕܡܫܟܚܝܢ ܕܢܡܢܥܘܢ ܘܢܤܬܘܢ ܒܠܡܐܢܐ ܚܕ ܕܐܝܬܘܗܝ ܗܘܐ ܒܩܪܛܐ ܘܡܬܩܪܐ ܗܘܐ ܦܘܢܟܤ ܘܚܐܪ ܗܘܐ ܠܬܝܡܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 13, 'ܘܟܕ ܢܫܒܬ ܪܘܚܐ ܕܬܝܡܢܐ ܘܤܒܪܘ ܕܡܡܛܝܢ ܐܝܟ ܨܒܝܢܗܘܢ ܪܕܝܢ ܗܘܘ ܚܕܪܝ ܩܪܛܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 14, 'ܘܡܢ ܒܬܪ ܩܠܝܠ ܢܦܩ ܥܠܝܢ ܡܫܒܐ ܕܥܠܥܠܐ ܕܡܬܩܪܐ ܛܘܦܢܝܩܘܤ ܐܘܪܩܠܝܕܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 15, 'ܘܐܬܚܛܦܬ ܐܠܦܐ ܘܠܐ ܐܫܟܚܬ ܕܬܩܘܡ ܠܘܩܒܠ ܪܘܚܐ ܘܝܗܒܢ ܠܐܝܕܐ ܕܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 16, 'ܘܟܕ ܥܒܪܢ ܓܙܪܬܐ ܚܕܐ ܕܡܬܩܪܝܐ ܩܘܕܐ ܠܡܚܤܢ ܐܫܟܚܢ ܐܚܕܢ ܠܩܪܩܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 17, 'ܘܟܕ ܫܩܠܢܗ ܡܚܝܨܝܢ ܗܘܝܢ ܘܡܬܩܢܝܢ ܠܗ ܠܐܠܦܐ ܘܡܛܠ ܕܕܚܠܝܢ ܗܘܝܢ ܕܕܠܡܐ ܢܦܠ ܒܡܚܬܬܗ ܕܝܡܐ ܐܚܬܢܝܗܝ ܠܐܪܡܢܘܢ ܘܗܟܘܬ ܪܕܝܢ ܗܘܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 18, 'ܘܟܕ ܩܡ ܠܗ ܥܠܝܢ ܟܝܡܘܢܐ ܩܫܝܐ ܠܝܘܡܐ ܐܚܪܢܐ ܫܕܝܢ ܡܐܢܝܢ ܒܝܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 19, 'ܘܠܝܘܡܐ ܕܬܠܬܐ ܡܐܢܐ ܕܝܠܗ ܕܐܠܦܐ ܒܐܝܕܝܢ ܫܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 20, 'ܘܟܕ ܐܚܕ ܠܗ ܤܬܘܐ ܝܘܡܬܐ ܝܬܝܪܐ ܘܠܐ ܫܡܫܐ ܡܬܚܙܐ ܗܘܐ ܘܠܐ ܤܗܪܐ ܘܠܐ ܟܘܟܒܐ ܤܒܪܐ ܕܚܝܝܢ ܟܠ ܟܠܗ ܐܬܦܤܩ ܗܘܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 21, 'ܘܟܕ ܐܢܫ ܡܕܡ ܠܐ ܡܤܬܝܒܪ ܗܘܐ ܗܝܕܝܢ ܩܡ ܦܘܠܘܤ ܒܝܢܬܗܘܢ ܘܐܡܪ ܐܠܘ ܐܬܛܦܝܤܬܘܢ ܠܝ ܓܒܪܐ ܠܐ ܪܕܝܢ ܗܘܝܢ ܡܢ ܩܪܛܐ ܘܡܬܚܤܟܝܢ ܗܘܝܢ ܡܢ ܚܘܤܪܢܐ ܘܡܢ ܐܘܠܨܢܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 22, 'ܘܗܫܐ ܡܠܟ ܐܢܐ ܕܬܗܘܘܢ ܕܠܐ ܥܩܐ ܢܦܫܐ ܓܝܪ ܡܢܟܘܢ ܚܕܐ ܠܐ ܐܒܕܐ ܐܠܐ ܐܢ ܐܠܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 23, 'ܐܬܚܙܝ ܠܝ ܓܝܪ ܒܠܠܝܐ ܗܢܐ ܡܠܐܟܗ ܕܐܠܗܐ ܗܘ ܕܕܝܠܗ ܐܢܐ ܘܠܗ ܦܠܚ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 24, 'ܘܐܡܪ ܠܝ ܠܐ ܬܕܚܠ ܦܘܠܐ ܥܬܝܕ ܗܘ ܠܟ ܠܡܩܡ ܩܕܡ ܩܤܪ ܘܗܐ ܝܗܒ ܠܟ ܐܠܗܐ ܡܘܗܒܬܐ ܠܟܠ ܕܪܕܝܢ ܥܡܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 25, 'ܡܛܠ ܗܢܐ ܐܬܠܒܒܘ ܓܒܪܐ ܡܗܝܡܢ ܐܢܐ ܓܝܪ ܒܐܠܗܐ ܕܗܟܢܐ ܗܘܐ ܐܝܟ ܡܐ ܕܐܬܡܠܠ ܥܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 26, 'ܒܪܡ ܠܓܙܪܬܐ ܚܕܐ ܐܝܬ ܠܢ ܕܢܫܬܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 27, 'ܘܡܢ ܒܬܪ ܐܪܒܬܥܤܪ ܝܘܡܝܢ ܕܛܥܝܢ ܘܐܬܛܪܦܢ ܒܗܕܪܝܘܤ ܝܡܐ ܒܦܠܓܗ ܕܠܠܝܐ ܤܒܪܘ ܡܠܚܐ ܕܠܐܪܥܐ ܡܬܩܪܒܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 28, 'ܘܐܪܡܝܘ ܐܘܩܝܢܤ ܘܐܫܟܚܘ ܩܘܡܝܢ ܥܤܪܝܢ ܘܬܘܒ ܩܠܝܠ ܪܕܘ ܘܐܫܟܚܘ ܩܘܡܝܢ ܚܡܫܥܤܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 29, 'ܘܟܕ ܕܚܝܠܝܢ ܗܘܝܢ ܕܠܡܐ ܢܫܬܟܚ ܠܢ ܒܕܘܟܝܬܐ ܕܐܝܬ ܒܗܝܢ ܫܘܥܐ ܐܪܡܝܘ ܡܢ ܚܪܬܗ ܕܐܠܦܐ ܐܘܩܝܢܤ ܐܪܒܥ ܘܡܨܠܝܢ ܗܘܘ ܕܢܗܘܐ ܝܘܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 30, 'ܡܠܚܐ ܕܝܢ ܒܥܘ ܠܡܥܪܩ ܡܢܗ ܡܢ ܐܠܦܐ ܘܐܚܬܘ ܡܢܗ ܠܩܪܩܘܪܐ ܠܝܡܐ ܒܥܠܬܐ ܕܢܐܙܠܘܢ ܒܗ ܘܢܐܤܪܘܢܗ ܠܐܠܦܐ ܒܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 31, 'ܘܟܕ ܚܙܐ ܦܘܠܘܤ ܐܡܪ ܠܩܢܛܪܘܢܐ ܘܠܐܤܛܪܛܝܘܛܐ ܕܐܢ ܗܠܝܢ ܒܐܠܦܐ ܠܐ ܡܟܬܪܝܢ ܐܢܬܘܢ ܠܐ ܡܫܟܚܝܢ ܐܢܬܘܢ ܕܬܚܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 32, 'ܗܝܕܝܢ ܦܤܩܘ ܐܤܛܪܛܝܘܛܐ ܠܚܒܠܝܗ ܕܩܪܩܘܪܐ ܡܢ ܐܠܦܐ ܘܫܒܩܘܗ ܛܥܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 33, 'ܗܘ ܕܝܢ ܦܘܠܘܤ ܥܕܡܐ ܕܗܘܐ ܨܦܪܐ ܡܦܝܤ ܗܘܐ ܠܟܠܗܘܢ ܕܢܩܒܠܘܢ ܤܝܒܪܬܐ ܟܕ ܐܡܪ ܠܗܘܢ ܝܘܡܢܐ ܗܐ ܐܪܒܬܥܤܪ ܝܘܡܝܢ ܡܢ ܩܢܛܐ ܡܕܡ ܠܐ ܛܥܝܡ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 34, 'ܡܛܠ ܗܕܐ ܒܥܐ ܐܢܐ ܡܢܟܘܢ ܕܬܩܒܠܘܢ ܡܐܟܘܠܬܐ ܠܩܘܝܡܐ ܕܚܝܝܟܘܢ ܡܢܬܐ ܓܝܪ ܡܢ ܪܫܐ ܕܚܕ ܡܢܟܘܢ ܠܐ ܐܒܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 35, 'ܘܟܕ ܗܠܝܢ ܐܡܪ ܢܤܒ ܠܚܡܐ ܘܫܒܚ ܠܐܠܗܐ ܩܕܡ ܟܠܗܘܢ ܘܩܨܐ ܘܐܩܦ ܠܡܐܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 36, 'ܘܐܬܒܝܐܘ ܟܠܗܘܢ ܘܩܒܠܘ ܬܘܪܤܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 37, 'ܐܝܬܝܢ ܗܘܝܢ ܕܝܢ ܒܐܠܦܐ ܡܐܬܝܢ ܘܫܒܥܝܢ ܘܫܬ ܢܦܫܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 38, 'ܘܟܕ ܤܒܥܘ ܡܐܟܘܠܬܐ ܐܩܠܘ ܡܢ ܐܠܦܐ ܘܫܩܠܘ ܚܛܐ ܘܫܕܘ ܒܝܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 39, 'ܘܟܕ ܗܘܐ ܝܘܡܐ ܤܦܢܐ ܐܝܕܐ ܗܝ ܐܪܥܐ ܠܐ ܐܫܬܘܕܥܘ ܐܠܐ ܚܪܘ ܥܠ ܓܢܒ ܝܒܫܐ ܟܢܦܐ ܚܕܐ ܕܝܡܐ ܐܝܟܐ ܕܪܢܝܢ ܗܘܘ ܕܐܢ ܡܫܟܚܐ ܢܕܚܘܢܗ ܠܐܠܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 40, 'ܘܦܤܩܘ ܐܘܩܝܢܤ ܡܢ ܐܠܦܐ ܘܐܪܦܝܘ ܐܢܝܢ ܒܝܡܐ ܘܫܪܘ ܪܟܒܐ ܕܤܘܟܢܐ ܘܬܠܘ ܐܪܡܢܘܢ ܙܥܘܪܐ ܠܪܘܚܐ ܕܢܫܒܐ ܘܪܕܝܢ ܗܘܘ ܠܐܦܝ ܝܒܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 41, 'ܘܓܫܬ ܐܠܦܐ ܒܕܘܟܬܐ ܕܪܡܐ ܒܝܢܬ ܬܪܝܢ ܥܘܡܩܝܢ ܕܝܡܐ ܘܐܬܚܪܝܬ ܒܗ ܘܩܡ ܥܠܝܗ ܓܒܗ ܩܕܡܝܐ ܘܠܐ ܡܬܬܙܝܥ ܗܘܐ ܓܒܗ ܕܝܢ ܐܚܪܝܐ ܐܫܬܪܝ ܡܢ ܩܛܝܪܐ ܕܓܠܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 42, 'ܘܨܒܘ ܗܘܘ ܐܤܛܪܛܝܘܛܐ ܕܢܩܛܠܘܢ ܐܢܘܢ ܠܐܤܝܪܐ ܕܠܐ ܢܪܡܘܢ ܤܚܘܐ ܘܢܥܪܩܘܢ ܠܗܘܢ ܡܢܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 43, 'ܘܩܢܛܪܘܢܐ ܟܠܐ ܐܢܘܢ ܡܢ ܗܕܐ ܡܛܠ ܕܨܒܐ ܗܘܐ ܕܢܚܐ ܠܦܘܠܘܤ ܘܠܐܝܠܝܢ ܕܡܫܟܚܝܢ ܗܘܘ ܠܡܪܡܝܘ ܤܚܘܐ ܦܩܕ ܠܗܘܢ ܕܒܩܕܡܝܐ ܢܤܚܘܢ ܘܢܥܒܪܘܢ ܠܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 44, 'ܘܠܫܪܟܐ ܥܠ ܕܦܐ ܘܥܠ ܩܝܤܐ ܐܚܪܢܐ ܕܐܠܦܐ ܐܥܒܪܘ ܐܢܘܢ ܘܗܟܢܐ ܟܠܗܘܢ ܐܫܬܘܙܒܘ ܠܐܪܥܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 1, 'ܘܡܢ ܒܬܪܟܢ ܝܠܦܢ ܕܡܠܝܛܐ ܡܬܩܪܝܐ ܗܝ ܓܙܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 2, 'ܘܒܪܒܪܝܐ ܕܥܡܪܝܢ ܗܘܘ ܒܗ ܪܚܡܐ ܤܓܝܐܐ ܚܘܝܘ ܠܘܬܢ ܘܐܘܚܕܘ ܢܘܪܐ ܘܩܪܐܘܢ ܠܟܠܢ ܕܢܫܚܢ ܡܛܠ ܡܛܪܐ ܤܓܝܐܐ ܘܩܘܪܫܐ ܕܐܝܬ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 3, 'ܘܦܘܠܘܤ ܫܩܠ ܤܘܓܐܐ ܕܚܒܘܒܐ ܘܤܡ ܥܠ ܢܘܪܐ ܘܢܦܩܬ ܡܢܗܘܢ ܐܟܕܢܐ ܡܢ ܪܬܚܐ ܕܢܘܪܐ ܘܢܟܬܬ ܒܐܝܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 4, 'ܘܟܕ ܚܙܐܘܗ ܒܪܒܪܝܐ ܕܬܠܝܐ ܒܐܝܕܗ ܐܡܪܝܢ ܗܘܘ ܟܒܪ ܗܢܐ ܓܒܪܐ ܩܛܘܠܐ ܗܘ ܕܟܕ ܐܫܬܘܙܒ ܡܢ ܝܡܐ ܟܐܢܘܬܐ ܠܐ ܫܒܩܬܗ ܕܢܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 5, 'ܗܘ ܕܝܢ ܦܘܠܘܤ ܐܢܝܦ ܐܝܕܗ ܘܫܕܗ ܠܐܟܕܢܐ ܒܢܘܪܐ ܘܡܕܡ ܕܤܢܐ ܠܐ ܗܘܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 6, 'ܤܒܪܝܢ ܗܘܘ ܕܝܢ ܒܪܒܪܝܐ ܕܒܪ ܫܥܬܗ ܡܬܡܤܐ ܘܢܦܠ ܟܕ ܡܝܬ ܥܠ ܐܪܥܐ ܘܟܕ ܥܕܢܐ ܤܓܝܐܐ ܤܟܝܘ ܘܚܙܘ ܕܡܕܡ ܕܤܢܐ ܠܐ ܗܘܝܗܝ ܫܚܠܦܘ ܡܠܝܗܘܢ ܘܐܡܪܘ ܕܐܠܗܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 7, 'ܐܝܬ ܗܘܐ ܕܝܢ ܩܘܪܝܐ ܒܗ ܒܗܘ ܐܬܪܐ ܠܓܒܪܐ ܚܕ ܕܫܡܗ ܗܘܐ ܦܘܦܠܝܘܤ ܕܗܘ ܗܘܐ ܪܫܗ ܕܓܙܪܬܐ ܘܩܒܠܢ ܒܒܝܬܗ ܬܠܬܐ ܝܘܡܝܢ ܚܕܝܐܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 8, 'ܐܒܘܗܝ ܕܝܢ ܕܦܘܦܠܝܘܤ ܒܐܫܬܐ ܘܒܟܐܒ ܡܥܝܐ ܟܪܝܗ ܗܘܐ ܘܥܠ ܠܘܬܗ ܦܘܠܘܤ ܘܨܠܝ ܘܤܡ ܥܠܘܗܝ ܐܝܕܗ ܘܐܚܠܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 9, 'ܘܟܕ ܗܘܬ ܗܕܐ ܐܦ ܫܪܟܐ ܐܝܠܝܢ ܕܐܝܬ ܗܘܘ ܒܗ ܒܓܙܪܬܐ ܟܪܝܗܐ ܩܪܒܝܢ ܗܘܘ ܠܘܬܗ ܘܡܬܐܤܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 10, 'ܘܐܝܩܪܐ ܪܘܪܒܐ ܝܩܪܘܢ ܘܟܕ ܢܦܩܝܢ ܗܘܝܢ ܡܢ ܬܡܢ ܙܘܕܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 11, 'ܢܦܩܢ ܕܝܢ ܒܬܪ ܝܪܚܐ ܬܠܬܐ ܘܪܕܝܢ ܒܐܠܦܐ ܐܠܟܤܢܕܪܝܬܐ ܕܐܤܬܝܬ ܗܘܬ ܒܗ ܒܓܙܪܬܐ ܘܐܝܬ ܗܘܬ ܥܠܝܗ ܐܬܐ ܕܬܐܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 12, 'ܘܐܬܝܢ ܠܤܪܩܘܤܐ ܡܕܝܢܬܐ ܘܩܘܝܢ ܬܡܢ ܝܘܡܬܐ ܬܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 13, 'ܘܡܢ ܬܡܢ ܐܬܟܪܟܢ ܘܡܢܥܢ ܠܪܓܝܘܢ ܡܕܝܢܬܐ ܘܒܬܪ ܝܘܡܐ ܚܕ ܢܫܒܬ ܠܢ ܪܘܚܐ ܕܬܝܡܢܐ ܘܠܬܪܝܢ ܝܘܡܝܢ ܐܬܝܢ ܠܦܘܛܝܐܠܤ ܡܕܝܢܬܐ ܕܐܝܛܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 14, 'ܘܐܫܟܚܢ ܬܡܢ ܐܚܐ ܘܒܥܘ ܡܢܢ ܘܗܘܝܢ ܠܘܬܗܘܢ ܝܘܡܬܐ ܫܒܥܐ ܘܗܝܕܝܢ ܐܙܠܢ ܠܪܗܘܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 15, 'ܘܟܕ ܫܡܥܘ ܐܚܐ ܕܬܡܢ ܢܦܩܘ ܠܐܘܪܥܢ ܥܕܡܐ ܠܫܘܩܐ ܕܡܬܩܪܐ ܐܦܝܘܤ ܦܘܪܘܤ ܘܥܕܡܐ ܠܬܠܬ ܚܢܘܢ ܘܟܕ ܚܙܐ ܐܢܘܢ ܦܘܠܘܤ ܐܘܕܝ ܠܐܠܗܐ ܘܐܬܚܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 16, 'ܘܥܠܢ ܠܪܗܘܡܐ ܘܐܦܤ ܩܢܛܪܘܢܐ ܠܦܘܠܘܤ ܕܢܫܪܐ ܐܝܟܐ ܕܨܒܐ ܥܡ ܐܤܛܪܛܝܘܛܐ ܗܘ ܕܢܛܪ ܗܘܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 17, 'ܘܡܢ ܒܬܪ ܬܠܬܐ ܝܘܡܝܢ ܫܕܪ ܦܘܠܘܤ ܩܪܐ ܠܪܫܢܝܗܘܢ ܕܝܗܘܕܝܐ ܘܟܕ ܐܬܟܢܫܘ ܐܡܪ ܠܗܘܢ ܓܒܪܐ ܐܚܝ ܐܢܐ ܟܕ ܒܡܕܡ ܠܐ ܩܡܬ ܠܘܩܒܠ ܥܡܐ ܘܢܡܘܤܐ ܕܐܒܗܝ ܒܐܤܘܪܐ ܐܫܬܠܡܬ ܡܢ ܐܘܪܫܠܡ ܒܐܝܕܐ ܕܪܗܘܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 18, 'ܘܗܢܘܢ ܟܕ ܫܐܠܘܢܝ ܨܒܘ ܕܢܫܪܘܢܢܝ ܡܛܠ ܕܠܐ ܐܫܟܚܘ ܒܬܪܝ ܪܫܝܢܐ ܡܕܡ ܕܫܘܐ ܠܡܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 19, 'ܘܟܕ ܩܝܡܝܢ ܗܘܘ ܠܘܩܒܠܝ ܝܗܘܕܝܐ ܐܬܐܠܨܬ ܕܐܩܥܐ ܒܓܢ ܩܤܪ ܠܐ ܐܝܟ ܐܝܬ ܠܝ ܕܐܩܛܪܓ ܒܡܕܡ ܠܒܢܝ ܥܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 20, 'ܡܛܠ ܗܢܐ ܒܥܝܬ ܡܢܟܘܢ ܕܬܐܬܘܢ ܘܐܚܙܝܟܘܢ ܘܐܫܬܥܐ ܠܟܘܢ ܗܠܝܢ ܡܛܠ ܤܒܪܐ ܓܝܪ ܕܐܝܤܪܝܠ ܐܤܝܪ ܐܢܐ ܒܫܫܠܬܐ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 21, 'ܐܡܪܝܢ ܠܗ ܗܢܘܢ ܚܢܢ ܐܓܪܬܐ ܥܠܝܟ ܠܐ ܩܒܠܢ ܡܢ ܝܗܘܕ ܘܠܐ ܐܢܫ ܡܢ ܐܚܐ ܕܐܬܘ ܡܢ ܐܘܪܫܠܡ ܐܡܪܘ ܠܢ ܡܕܡ ܕܒܝܫ ܥܠܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 22, 'ܨܒܝܢܢ ܕܝܢ ܕܢܫܡܥ ܡܢܟ ܡܕܡ ܕܡܬܪܥܐ ܐܢܬ ܡܛܠ ܕܝܘܠܦܢܐ ܗܢܐ ܝܕܥܝܢܢ ܕܥܠ ܐܢܫ ܠܐ ܡܩܒܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 23, 'ܘܐܩܝܡܘ ܠܗ ܝܘܡܐ ܘܟܢܫܘ ܘܐܬܘ ܠܘܬܗ ܤܓܝܐܐ ܟܪ ܕܫܪܐ ܗܘܐ ܘܓܠܐ ܠܗܘܢ ܥܠ ܡܠܟܘܬܐ ܕܐܠܗܐ ܟܕ ܡܤܗܕ ܘܡܦܝܤ ܠܗܘܢ ܥܠ ܝܫܘܥ ܡܢ ܢܡܘܤܐ ܕܡܘܫܐ ܘܡܢ ܢܒܝܐ ܡܢ ܨܦܪܐ ܘܥܕܡܐ ܠܪܡܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 24, 'ܘܐܢܫܝܢ ܡܢܗܘܢ ܡܬܛܦܝܤܝܢ ܗܘܘ ܠܡܠܘܗܝ ܘܐܚܪܢܐ ܠܐ ܡܬܛܦܝܤܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 25, 'ܘܐܫܬܪܝܘ ܡܢ ܠܘܬܗ ܟܕ ܠܐ ܫܠܡܝܢ ܠܚܕܕܐ ܘܐܡܪ ܠܗܘܢ ܗܘ ܦܘܠܘܤ ܡܠܬܐ ܗܕܐ ܫܦܝܪ ܡܠܠ ܪܘܚܐ ܕܩܘܕܫܐ ܒܦܘܡ ܐܫܥܝܐ ܢܒܝܐ ܠܘܩܒܠ ܐܒܗܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 26, 'ܟܕ ܐܡܪ ܕܙܠ ܠܘܬ ܥܡܐ ܗܢܐ ܘܐܡܪ ܠܗܘܢ ܕܫܡܥܐ ܬܫܡܥܘܢ ܘܠܐ ܬܤܬܟܠܘܢ ܘܬܚܙܘܢ ܘܠܐ ܬܒܚܪܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 27, 'ܐܬܥܒܝ ܠܗ ܓܝܪ ܠܒܗ ܕܥܡܐ ܗܢܐ ܘܡܫܡܥܬܗܘܢ ܐܘܩܪܘ ܘܥܝܢܝܗܘܢ ܥܡܨܘ ܕܠܐ ܢܚܙܘܢ ܒܥܝܢܝܗܘܢ ܘܢܫܡܥܘܢ ܒܐܕܢܝܗܘܢ ܘܢܤܬܟܠܘܢ ܒܠܒܗܘܢ ܘܢܬܘܒܘܢ ܠܘܬܝ ܘܐܫܒܘܩ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 28, 'ܬܬܝܕܥ ܠܟܘܢ ܗܟܝܠ ܗܕܐ ܕܠܥܡܡܐ ܗܘ ܐܫܬܕܪ ܗܢܐ ܦܘܪܩܢܐ ܕܐܠܗܐ ܗܢܘܢ ܓܝܪ ܐܦ ܫܡܥܝܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 29, 'ܘܟܕ ܗܠܝܢ ܐܡܪ ܢܦܩܘ ܝܗܘܕܝܐ ܘܤܓܝ ܕܪܫܝܢ ܗܘܘ ܒܝܢܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 30, 'ܘܐܓܪ ܠܗ ܦܘܠܘܤ ܡܢ ܕܝܠܗ ܒܝܬܐ ܘܗܘܐ ܒܗ ܬܪܬܝܢ ܫܢܝܢ ܘܡܩܒܠ ܗܘܐ ܬܡܢ ܠܟܠܗܘܢ ܐܝܠܝܢ ܕܐܬܝܢ ܗܘܘ ܠܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 31, 'ܘܡܟܪܙ ܗܘܐ ܥܠ ܡܠܟܘܬܗ ܕܐܠܗܐ ܘܡܠܦ ܗܘܐ ܥܝܢ ܒܓܠܐ ܥܠ ܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܕܠܐ ܟܠܝܢ ܀ ܀ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 1, 'ܦܘܠܘܤ ܥܒܕܐ ܕܝܫܘܥ ܡܫܝܚܐ ܩܪܝܐ ܘܫܠܝܚܐ ܕܐܬܦܪܫ ܠܐܘܢܓܠܝܘܢ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 2, 'ܕܡܢ ܩܕܝܡ ܡܠܟ ܒܝܕ ܢܒܝܘܗܝ ܒܟܬܒܐ ܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 3, 'ܥܠ ܒܪܗ ܗܘ ܕܐܬܝܠܕ ܒܒܤܪ ܡܢ ܙܪܥܐ ܕܒܝܬ ܕܘܝܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 4, 'ܘܐܬܝܕܥ ܒܪܐ ܕܐܠܗܐ ܒܚܝܠ ܘܒܪܘܚ ܩܕܘܫ ܕܩܡ ܡܢ ܒܝܬ ܡܝܬܐ ܝܫܘܥ ܡܫܝܚܐ ܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 5, 'ܕܒܗ ܢܤܒܢ ܛܝܒܘܬܐ ܘܫܠܝܚܘܬܐ ܒܟܠܗܘܢ ܥܡܡܐ ܐܝܟ ܕܢܫܬܡܥܘܢ ܠܗܝܡܢܘܬܐ ܕܫܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 6, 'ܕܐܦ ܐܢܬܘܢ ܡܢܗܘܢ ܐܢܬܘܢ ܩܪܝܐ ܒܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 7, 'ܠܟܠܗܘܢ ܕܒܪܗܘܡܝ ܚܒܝܒܘܗܝ ܕܐܠܗܐ ܩܪܝܐ ܘܩܕܝܫܐ ܫܠܡܐ ܘܛܝܒܘܬܐ ܥܡܟܘܢ ܡܢ ܐܠܗܐ ܐܒܘܢ ܘܡܢ ܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 8, 'ܠܘܩܕܡ ܡܘܕܐ ܐܢܐ ܠܐܠܗܝ ܒܝܫܘܥ ܡܫܝܚܐ ܥܠ ܟܠܟܘܢ ܕܗܝܡܢܘܬܟܘܢ ܐܫܬܡܥܬ ܒܟܠܗ ܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 9, 'ܤܗܕ ܗܘ ܠܝ ܓܝܪ ܐܠܗܐ ܕܠܗ ܡܫܡܫ ܐܢܐ ܒܪܘܚ ܒܐܘܢܓܠܝܘܢ ܕܒܪܗ ܕܕܠܐ ܫܠܘܐ ܒܟܠܙܒܢ ܡܬܕܟܪ ܐܢܐ ܠܟܘܢ ܒܨܠܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 10, 'ܘܡܬܚܢܢ ܐܢܐ ܕܐܢ ܡܢ ܟܕܘ ܬܬܦܬܚ ܠܝ ܐܘܪܚܐ ܒܨܒܝܢܗ ܕܐܠܗܐ ܕܐܬܐ ܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 11, 'ܡܛܠ ܕܛܒ ܤܘܐ ܐܢܐ ܕܐܚܙܟܘܢ ܘܐܬܠ ܠܟܘܢ ܡܘܗܒܬܐ ܕܪܘܚ ܕܒܗ ܬܫܬܪܪܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 12, 'ܘܐܟܚܕܐ ܢܬܒܝܐ ܒܗܝܡܢܘܬܐ ܕܝܠܟܘܢ ܘܕܝܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 13, 'ܨܒܐ ܐܢܐ ܕܝܢ ܐܚܝ ܕܬܕܥܘܢ ܕܙܒܢܝܢ ܤܓܝܐܢ ܨܒܝܬ ܕܐܬܐ ܠܘܬܟܘܢ ܘܐܬܟܠܝܬ ܥܕܡܐ ܠܗܫܐ ܕܐܦ ܒܟܘܢ ܢܗܘܐ ܠܝ ܐܕܫܐ ܐܝܟ ܕܒܫܪܟܐ ܕܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 14, 'ܝܘܢܝܐ ܘܒܪܒܪܝܐ ܚܟܝܡܐ ܘܤܟܠܐ ܕܠܟܠܢܫ ܚܝܒ ܐܢܐ ܕܐܟܪܙ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 15, 'ܘܗܟܢܐ ܡܬܚܦܛ ܐܢܐ ܕܐܦ ܠܟܘܢ ܕܒܪܗܘܡܝ ܐܤܒܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 16, 'ܠܐ ܓܝܪ ܒܗܬ ܐܢܐ ܒܗ ܒܐܘܢܓܠܝܘܢ ܡܛܠ ܕܚܝܠܐ ܗܘ ܕܐܠܗܐ ܠܚܝܐ ܕܟܠ ܕܡܗܝܡܢܝܢ ܒܗ ܐܢ ܡܢ ܝܗܘܕܝܐ ܠܘܩܕܡ ܘܐܢ ܡܢ ܐܪܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 17, 'ܟܐܢܘܬܗ ܓܝܪ ܕܐܠܗܐ ܒܗ ܡܬܓܠܝܐ ܡܢ ܗܝܡܢܘܬܐ ܠܗܝܡܢܘܬܐ ܐܝܟ ܕܟܬܝܒ ܕܟܐܢܐ ܡܢ ܗܝܡܢܘܬܐ ܢܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 18, 'ܡܬܓܠܐ ܗܘ ܓܝܪ ܪܘܓܙܗ ܕܐܠܗܐ ܡܢ ܫܡܝܐ ܥܠ ܟܠܗ ܥܘܠܗܘܢ ܘܪܘܫܥܗܘܢ ܕܒܢܝܢܫܐ ܗܢܘܢ ܕܩܘܫܬܐ ܒܥܘܠܐ ܐܚܝܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 19, 'ܡܛܠ ܕܝܕܝܥܘܬܗ ܕܐܠܗܐ ܓܠܝܐ ܗܝ ܒܗܘܢ ܐܠܗܐ ܓܝܪ ܓܠܗ ܒܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 20, 'ܟܤܝܬܗ ܓܝܪ ܕܐܠܗܐ ܡܢ ܬܪܡܝܬܗ ܕܥܠܡܐ ܠܒܪܝܬܗ ܒܤܘܟܠܐ ܡܬܚܙܝܢ ܘܚܝܠܗ ܘܐܠܗܘܬܗ ܕܠܥܠܡ ܕܢܗܘܘܢ ܕܠܐ ܡܦܩ ܒܪܘܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 21, 'ܡܛܠ ܕܝܕܥܘ ܠܐܠܗܐ ܘܠܐ ܐܝܟ ܕܠܐܠܗܐ ܫܒܚܘܗܝ ܘܐܘܕܝܘ ܠܗ ܐܠܐ ܐܤܬܪܩܘ ܒܡܚܫܒܬܗܘܢ ܘܐܬܚܫܟ ܠܒܗܘܢ ܕܠܐ ܡܤܬܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 22, 'ܘܟܕ ܤܒܪܝܢ ܒܢܦܫܗܘܢ ܕܚܟܝܡܝܢ ܐܢܘܢ ܫܛܘ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 23, 'ܘܚܠܦܘ ܬܫܒܘܚܬܗ ܕܐܠܗܐ ܕܠܐ ܡܬܚܒܠ ܒܕܡܘܬܐ ܕܨܠܡܐ ܕܒܪܢܫܐ ܕܡܬܚܒܠ ܘܒܕܡܘܬܐ ܕܦܪܚܬܐ ܘܕܐܪܒܥܬ ܪܓܠܝܗ ܘܕܪܚܫܐ ܕܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 24, 'ܡܛܠ ܗܢܐ ܐܫܠܡ ܐܢܘܢ ܐܠܗܐ ܠܪܓܝܓܬܐ ܛܡܐܬܐ ܕܠܒܗܘܢ ܕܢܨܥܪܘܢ ܦܓܪܝܗܘܢ ܒܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 25, 'ܘܚܠܦܘ ܫܪܪܗ ܕܐܠܗܐ ܒܟܕܒܘܬܐ ܘܕܚܠܘ ܘܫܡܫܘ ܠܒܪܝܬܐ ܛܒ ܡܢ ܕܠܒܪܘܝܗܝܢ ܕܠܗ ܬܫܒܚܢ ܘܒܘܪܟܢ ܠܥܠܡ ܥܠܡܝܢ ܐܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 26, 'ܡܛܠ ܗܢܐ ܐܫܠܡ ܐܢܘܢ ܐܠܗܐ ܠܟܐܒܐ ܕܨܥܪܐ ܢܩܒܬܗܘܢ ܓܝܪ ܚܠܦ ܚܫܚܬܐ ܕܟܝܢܗܝܢ ܘܒܡܕܡ ܕܠܐ ܡܟܢ ܐܬܚܫܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 27, 'ܘܬܘܒ ܐܦ ܕܟܪܝܗܘܢ ܗܟܢܐ ܫܒܩܘ ܚܫܚܬܐ ܕܟܝܢܐ ܕܢܩܒܬܐ ܘܐܫܬܪܚܘ ܒܪܓܬܐ ܚܕ ܥܠ ܚܕ ܘܕܟܪܐ ܥܠ ܕܟܪܐ ܒܗܬܬܐ ܥܒܕܘ ܘܦܘܪܥܢܐ ܕܙܕܩ ܗܘܐ ܠܛܥܝܘܬܗܘܢ ܒܩܢܘܡܗܘܢ ܩܒܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 28, 'ܘܐܝܟ ܕܠܐ ܕܢܘ ܒܢܦܫܗܘܢ ܕܢܕܥܘܢ ܠܐܠܗܐ ܐܫܠܡ ܐܢܘܢ ܐܠܗܐ ܠܡܕܥܐ ܕܤܪܝܩܘܬܐ ܕܢܗܘܘܢ ܥܒܕܝܢ ܡܕܡ ܕܠܐ ܘܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 29, 'ܟܕ ܡܠܝܢ ܟܠ ܥܘܠܘܬܐ ܘܙܢܝܘܬܐ ܘܡܪܝܪܘܬܐ ܘܒܝܫܘܬܐ ܘܥܠܘܒܘܬܐ ܘܚܤܡܐ ܘܩܛܠܐ ܘܚܪܝܢܐ ܘܢܟܠܐ ܘܡܚܫܒܬܐ ܒܝܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 30, 'ܘܪܛܢܐ ܘܡܐܟܠܩܪܨܐ ܘܤܢܝܐܝܢ ܠܐܠܗܐ ܡܨܥܪܢܐ ܚܬܝܪܐ ܫܒܗܪܢܐ ܡܫܟܚܝ ܒܝܫܬܐ ܚܤܝܪܝ ܪܥܝܢܐ ܕܠܐܒܗܝܗܘܢ ܠܐ ܡܬܛܦܝܤܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 31, 'ܘܕܩܝܡܐ ܠܝܬ ܠܗܘܢ ܘܠܐ ܚܘܒܐ ܘܠܐ ܫܝܢܐ ܘܠܐ ܪܚܡܐ ܐܝܬ ܒܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 32, 'ܐܝܠܝܢ ܕܟܕ ܝܕܥܝܢ ܕܝܢܗ ܕܐܠܗܐ ܕܠܐܝܠܝܢ ܕܐܝܟ ܗܠܝܢ ܤܥܪܝܢ ܠܡܘܬܐ ܡܚܝܒ ܠܐ ܗܘܐ ܒܠܚܘܕ ܥܒܕܝܢ ܠܗܝܢ ܐܠܐ ܐܦ ܡܫܬܘܬܦܝܢ ܠܐܝܠܝܢ ܕܗܠܝܢ ܥܒܕܝܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 1, 'ܡܛܠ ܗܢܐ ܠܝܬ ܠܟ ܡܦܩ ܒܪܘܚܐ ܐܘ ܒܪܢܫܐ ܕܐܢ ܚܒܪܗ ܒܗܘ ܓܝܪ ܕܕܐܢ ܐܢܬ ܚܒܪܟ ܢܦܫܟ ܗܘ ܡܚܝܒ ܐܢܬ ܐܦ ܐܢܬ ܓܝܪ ܕܕܐܢ ܐܢܬ ܒܗܝܢ ܗܘ ܡܬܗܦܟ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 2, 'ܘܝܕܥܝܢܢ ܕܐܝܬܘܗܝ ܕܝܢܗ ܕܐܠܗܐ ܒܩܘܫܬܐ ܥܠ ܐܝܠܝܢ ܕܒܗܠܝܢ ܡܬܗܦܟܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 3, 'ܡܢܐ ܕܝܢ ܡܬܚܫܒ ܐܢܬ ܐܘ ܒܪܢܫܐ ܕܕܐܢ ܐܢܬ ܠܐܝܠܝܢ ܕܒܗܠܝܢ ܡܬܗܦܟܝܢ ܟܕ ܐܦ ܐܢܬ ܒܗܝܢ ܡܬܗܦܟ ܐܢܬ ܕܐܢܬ ܬܥܪܘܩ ܡܢ ܕܝܢܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 4, 'ܐܘ ܥܠ ܥܘܬܪܐ ܕܒܤܝܡܘܬܗ ܘܥܠ ܡܓܪܬ ܪܘܚܗ ܘܥܠ ܐܬܪܐ ܕܝܗܒ ܠܟ ܡܡܪܚ ܐܢܬ ܘܠܐ ܝܕܥ ܐܢܬ ܕܒܤܝܡܘܬܗ ܕܐܠܗܐ ܠܬܝܒܘܬܐ ܗܘ ܡܝܬܝܐ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 5, 'ܐܠܐ ܡܛܠ ܩܫܝܘܬ ܠܒܟ ܕܠܐ ܬܐܒ ܤܐܡ ܐܢܬ ܠܟ ܤܝܡܬܐ ܕܪܘܓܙܐ ܠܝܘܡܐ ܕܪܘܓܙܐ ܘܠܓܠܝܢܐ ܕܕܝܢܐ ܟܐܢܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 6, 'ܗܘ ܕܦܪܥ ܠܟܠܢܫ ܐܝܟ ܥܒܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 7, 'ܠܐܝܠܝܢ ܕܒܡܤܝܒܪܢܘܬܐ ܕܥܒܕܐ ܛܒܐ ܬܫܒܘܚܬܐ ܘܐܝܩܪܐ ܘܠܐ ܡܬܚܒܠܢܘܬܐ ܒܥܝܢ ܝܗܒ ܠܗܘܢ ܚܝܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 8, 'ܐܝܠܝܢ ܕܝܢ ܕܥܨܝܢ ܘܠܐ ܡܬܛܦܝܤܝܢ ܠܫܪܪܐ ܐܠܐ ܠܥܘܠܐ ܡܬܛܦܝܤܝܢ ܢܦܪܘܥ ܐܢܘܢ ܪܘܓܙܐ ܘܚܡܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 9, 'ܘܐܘܠܨܢܐ ܘܛܘܪܦܐ ܠܟܠ ܒܪܢܫ ܕܦܠܚ ܒܝܫܬܐ ܠܝܗܘܕܝܐ ܠܘܩܕܡ ܘܠܐܪܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 10, 'ܬܫܒܘܚܬܐ ܕܝܢ ܘܐܝܩܪܐ ܘܫܠܡܐ ܠܟܠ ܕܦܠܚ ܛܒܬܐ ܠܝܗܘܕܝܐ ܠܘܩܕܡ ܘܠܐܪܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 11, 'ܠܐ ܓܝܪ ܐܝܬ ܡܤܒ ܒܐܦܐ ܠܘܬ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 12, 'ܐܝܠܝܢ ܓܝܪ ܕܕܠܐ ܢܡܘܤܐ ܚܛܘ ܐܦ ܕܠܐ ܢܡܘܤܐ ܢܐܒܕܘܢ ܘܐܝܠܝܢ ܕܒܢܡܘܤܐ ܚܛܘ ܡܢ ܢܡܘܤܐ ܢܬܕܝܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 13, 'ܠܐ ܗܘܐ ܓܝܪ ܫܡܘܥܘܗܝ ܕܢܡܘܤܐ ܟܐܢܝܢ ܩܕܡ ܐܠܗܐ ܐܠܐ ܥܒܘܕܘܗܝ ܕܢܡܘܤܐ ܡܙܕܕܩܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 14, 'ܐܢ ܓܝܪ ܥܡܡܐ ܕܢܡܘܤܐ ܠܝܬ ܠܗܘܢ ܡܢ ܟܝܢܗܘܢ ܢܥܒܕܘܢ ܕܢܡܘܤܐ ܗܢܘܢ ܕܟܕ ܢܡܘܤܐ ܠܝܬ ܗܘܐ ܠܗܘܢ ܠܢܦܫܗܘܢ ܗܘܘ ܢܡܘܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 15, 'ܘܗܢܘܢ ܡܚܘܝܢ ܥܒܕܗ ܕܢܡܘܤܐ ܟܕ ܟܬܝܒ ܥܠ ܠܒܗܘܢ ܘܡܤܗܕܐ ܥܠܝܗܘܢ ܬܐܪܬܗܘܢ ܟܕ ܡܚܫܒܬܗܘܢ ܡܟܘܢܢ ܐܘ ܢܦܩܢ ܪܘܚܐ ܠܚܕܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 16, 'ܒܝܘܡܐ ܕܕܐܢ ܐܠܗܐ ܟܤܝܬܐ ܕܒܢܝܢܫܐ ܐܝܟ ܐܘܢܓܠܝܘܢ ܕܝܠܝ ܒܝܕ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 17, 'ܐܢ ܐܢܬ ܕܝܢ ܕܝܗܘܕܝܐ ܡܬܩܪܐ ܐܢܬ ܘܡܬܬܢܝܚ ܐܢܬ ܥܠ ܢܡܘܤܐ ܘܡܫܬܒܗܪ ܐܢܬ ܒܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 18, 'ܕܝܕܥ ܐܢܬ ܨܒܝܢܗ ܘܦܪܫ ܐܢܬ ܘܠܝܬܐ ܕܝܠܝܦ ܐܢܬ ܡܢ ܢܡܘܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 19, 'ܘܐܬܬܟܠܬ ܥܠ ܢܦܫܟ ܕܡܕܒܪܢܐ ܐܢܬ ܕܥܘܝܪܐ ܘܢܘܗܪܐ ܕܐܝܠܝܢ ܕܐܝܬܝܗܘܢ ܒܚܫܘܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 20, 'ܘܪܕܘܝܐ ܕܚܤܝܪܝ ܪܥܝܢܐ ܘܡܠܦܢܐ ܕܛܠܝܐ ܘܐܝܬ ܠܟ ܕܘܡܝܐ ܕܝܕܥܬܐ ܘܕܫܪܪܐ ܒܢܡܘܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 21, 'ܐܢܬ ܗܟܝܠ ܕܡܠܦ ܐܢܬ ܠܐܚܪܢܐ ܠܢܦܫܟ ܠܐ ܡܠܦ ܐܢܬ ܘܕܡܟܪܙ ܐܢܬ ܕܠܐ ܢܓܢܒܘܢ ܐܢܬ ܓܢܒ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 22, 'ܘܕܐܡܪ ܐܢܬ ܕܠܐ ܢܓܘܪܘܢ ܐܢܬ ܓܐܪ ܐܢܬ ܘܐܢܬ ܕܫܐܛ ܐܢܬ ܦܬܟܪܐ ܡܚܠܨ ܐܢܬ ܒܝܬ ܡܩܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 23, 'ܘܐܢܬ ܕܡܫܬܒܗܪ ܐܢܬ ܒܢܡܘܤܐ ܒܗܘ ܕܥܒܪ ܐܢܬ ܥܠ ܢܡܘܤܐ ܠܐܠܗܐ ܗܘ ܡܨܥܪ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 24, 'ܫܡܗ ܓܝܪ ܕܐܠܗܐ ܡܛܠܬܟܘܢ ܗܘ ܡܬܓܕܦ ܒܝܬ ܥܡܡܐ ܐܝܟ ܕܟܬܝܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 25, 'ܓܙܘܪܬܐ ܓܝܪ ܡܗܢܝܐ ܐܢ ܢܡܘܤܐ ܬܓܡܘܪ ܐܢ ܬܥܒܪ ܠܟ ܕܝܢ ܡܢ ܢܡܘܤܐ ܓܙܘܪܬܟ ܗܘܬ ܠܗ ܥܘܪܠܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 26, 'ܐܢ ܗܘ ܕܝܢ ܕܥܘܪܠܘܬܐ ܬܛܪ ܦܘܩܕܢܗ ܕܢܡܘܤܐ ܠܐ ܗܐ ܥܘܪܠܘܬܐ ܡܬܚܫܒܐ ܠܗ ܓܙܘܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 27, 'ܘܬܕܘܢ ܥܘܪܠܘܬܐ ܕܡܢ ܟܝܢܗ ܓܡܪܐ ܢܡܘܤܐ ܠܟ ܕܒܟܬܒܐ ܘܒܓܙܘܪܬܐ ܥܒܪ ܐܢܬ ܥܠ ܢܡܘܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 28, 'ܠܐ ܗܘܐ ܓܝܪ ܡܢ ܕܒܓܠܝܐ ܗܘ ܗܘ ܝܗܘܕܝܐ ܐܦܠܐ ܐܝܕܐ ܕܡܬܚܙܝܐ ܒܒܤܪܐ ܓܙܘܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 29, 'ܐܠܐ ܗܘ ܗܘ ܝܗܘܕܝܐ ܐܝܢܐ ܕܒܟܤܝܐ ܗܘ ܘܓܙܘܪܬܐ ܐܝܕܐ ܕܕܠܒܐ ܗܝ ܒܪܘܚ ܘܠܐ ܒܟܬܒܐ ܐܝܕܐ ܕܬܫܒܘܚܬܗ ܠܐ ܗܘܬ ܡܢ ܒܢܝ ܐܢܫܐ ܐܠܐ ܡܢ ܐܠܗܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 1, 'ܡܢܐ ܗܝ ܗܟܝܠ ܝܬܝܪܘܬܗ ܕܝܗܘܕܝܐ ܐܘ ܡܢܐ ܝܘܬܪܢܗ ܕܓܙܘܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 2, 'ܤܓܝ ܒܟܠ ܡܕܡ ܠܘܩܕܡ ܕܐܬܗܝܡܢ ܡܠܘܗܝ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 3, 'ܐܢ ܡܢܗܘܢ ܓܝܪ ܠܐ ܗܝܡܢܘ ܕܠܡܐ ܒܕܠܐ ܗܝܡܢܘ ܗܝܡܢܘܬܗ ܕܐܠܗܐ ܒܛܠܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 4, 'ܚܤ ܐܝܬܘܗܝ ܓܝܪ ܐܠܗܐ ܫܪܝܪܐ ܘܟܠ ܒܪܢܫ ܕܓܠ ܐܝܟܢܐ ܕܟܬܝܒ ܕܬܗܘܐ ܟܐܝܢ ܒܡܠܝܟ ܘܬܙܟܐ ܟܕ ܕܝܢܝܢ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 5, 'ܐܢ ܕܝܢ ܥܘܠܢ ܟܐܢܘܬܗ ܕܐܠܗܐ ܡܩܝܡ ܡܢܐ ܢܐܡܪ ܠܡܐ ܥܘܠ ܗܘ ܐܠܗܐ ܕܡܝܬܐ ܪܘܓܙܗ ܐܝܟ ܒܪܢܫܐ ܗܘ ܡܡܠܠ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 6, 'ܚܤ ܘܐܢ ܠܐ ܐܝܟܢܐ ܢܕܘܢ ܐܠܗܐ ܠܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 7, 'ܐܢ ܓܝܪ ܫܪܪܗ ܕܐܠܗܐ ܐܬܝܬܪ ܒܕܓܠܘܬܝ ܠܬܫܒܘܚܬܗ ܕܝܠܗ ܠܡܢܐ ܗܟܝܠ ܐܢܐ ܐܝܟ ܚܛܝܐ ܡܬܬܕܝܢ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 8, 'ܐܘ ܕܠܡܐ ܐܝܟ ܕܡܓܕܦܝܢ ܥܠܝܢ ܘܐܡܪܝܢ ܕܐܡܪܝܢܢ ܕܢܥܒܕ ܒܝܫܬܐ ܕܢܐܬܝܢ ܛܒܬܐ ܗܢܘܢ ܕܕܝܢܗܘܢ ܢܛܝܪ ܗܘ ܠܟܐܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 9, 'ܡܢܐ ܗܟܝܠ ܐܚܝܕܝܢܢ ܝܬܝܪܐ ܕܩܕܡܢ ܦܤܩܢ ܥܠ ܝܗܘܕܝܐ ܘܥܠ ܐܪܡܝܐ ܕܬܚܝܬ ܚܛܝܬܐ ܐܢܘܢ ܟܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 10, 'ܐܝܟ ܕܟܬܝܒ ܕܠܝܬ ܟܐܢܐ ܐܦܠܐ ܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 11, 'ܘܠܐ ܕܡܤܬܟܠ ܘܠܐ ܕܒܥܐ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 12, 'ܟܠܗܘܢ ܤܛܘ ܐܟܚܕܐ ܘܐܤܬܠܝܘ ܘܠܝܬ ܕܥܒܕ ܛܒܬܐ ܐܦܠܐ ܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 13, 'ܩܒܪܐ ܦܬܝܚܐ ܓܓܪܬܗܘܢ ܘܠܫܢܝܗܘܢ ܢܟܘܠܬܢܝܢ ܘܚܡܬܐ ܕܐܤܦܤ ܬܚܝܬ ܤܦܘܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 14, 'ܦܘܡܗܘܢ ܡܠܐ ܠܘܛܬܐ ܘܡܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 15, 'ܘܪܓܠܝܗܘܢ ܩܠܝܠܢ ܠܡܐܫܕ ܕܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 16, 'ܫܚܩܐ ܘܕܘܘܢܐ ܒܐܘܪܚܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 17, 'ܘܐܘܪܚܐ ܕܫܠܡܐ ܠܐ ܝܕܥܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 18, 'ܘܕܚܠܬܗ ܕܐܠܗܐ ܠܝܬ ܩܕܡ ܥܝܢܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 19, 'ܝܕܥܝܢܢ ܕܝܢ ܕܡܕܡ ܕܐܡܪ ܢܡܘܤܐ ܠܐܝܠܝܢ ܕܒܢܡܘܤܐ ܐܢܘܢ ܐܡܪ ܕܟܠ ܦܘܡ ܢܤܬܟܪ ܘܥܠܡܐ ܟܠܗ ܢܬܚܝܒ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 20, 'ܡܛܠ ܕܡܢ ܥܒܕܘܗܝ ܕܢܡܘܤܐ ܠܐ ܡܙܕܕܩ ܟܠ ܒܤܪ ܩܕܡܘܗܝ ܡܢ ܢܡܘܤܐ ܓܝܪ ܐܬܝܕܥܬ ܚܛܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 21, 'ܗܫܐ ܕܝܢ ܕܠܐ ܢܡܘܤܐ ܟܐܢܘܬܗ ܕܐܠܗܐ ܐܬܓܠܝܬ ܘܡܤܗܕ ܥܠܝܗ ܗܘ ܢܡܘܤܐ ܘܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 22, 'ܟܐܢܘܬܗ ܕܝܢ ܕܐܠܗܐ ܒܝܕ ܗܝܡܢܘܬܐ ܗܝ ܕܝܫܘܥ ܡܫܝܚܐ ܠܟܠܢܫ ܐܦ ܥܠ ܟܠܢܫ ܕܡܗܝܡܢ ܒܗ ܠܝܬ ܓܝܪ ܦܘܪܫܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 23, 'ܡܛܠ ܕܟܠܗܘܢ ܚܛܘ ܘܚܤܝܪܝܢ ܡܢ ܬܫܒܘܚܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 24, 'ܘܡܙܕܕܩܝܢ ܒܛܝܒܘܬܐ ܡܓܢ ܘܒܦܘܪܩܢܐ ܕܐܝܬܘܗܝ ܒܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 25, 'ܗܢܐ ܕܩܕܡ ܤܡܗ ܐܠܗܐ ܚܘܤܝܐ ܒܗܝܡܢܘܬܐ ܕܕܡܗ ܡܛܠ ܚܛܗܝܢ ܕܡܢ ܩܕܝܡ ܚܛܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 26, 'ܒܐܬܪܐ ܕܝܗܒ ܠܢ ܐܠܗܐ ܒܡܓܪܬ ܪܘܚܗ ܠܬܚܘܝܬܐ ܕܟܐܢܘܬܗ ܕܒܙܒܢܐ ܗܢܐ ܕܗܘ ܢܗܘܐ ܟܐܢܐ ܘܢܙܕܩ ܒܟܐܢܘܬܐ ܠܡܢ ܕܒܗܝܡܢܘܬܐ ܗܘ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 27, 'ܐܝܟܘ ܗܟܝܠ ܫܘܒܗܪܐ ܐܬܒܛܠ ܠܗ ܒܐܝܢܐ ܢܡܘܤܐ ܕܥܒܕܐ ܠܐ ܐܠܐ ܒܢܡܘܤܐ ܕܗܝܡܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 28, 'ܡܬܪܥܝܢܢ ܗܟܝܠ ܕܒܗܝܡܢܘܬܐ ܗܘ ܡܙܕܕܩ ܒܪܢܫܐ ܘܠܐ ܒܥܒܕܐ ܕܢܡܘܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 29, 'ܠܡܐ ܓܝܪ ܐܠܗܐ ܕܝܗܘܕܝܐ ܗܘ ܒܠܚܘܕ ܘܕܥܡܡܐ ܠܐ ܐܝܢ ܐܦ ܕܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 30, 'ܡܛܠ ܕܚܕ ܗܘ ܐܠܗܐ ܕܡܙܕܩ ܓܙܘܪܬܐ ܒܗܝܡܢܘܬܐ ܐܦ ܥܘܪܠܘܬܐ ܒܗ ܒܗܝܡܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 31, 'ܠܡܐ ܗܟܝܠ ܢܡܘܤܐ ܗܘ ܡܒܛܠܝܢܢ ܒܗܝܡܢܘܬܐ ܚܤ ܐܠܐ ܢܡܘܤܐ ܗܘ ܡܩܝܡܝܢܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 1, 'ܡܢܐ ܗܟܝܠ ܐܡܪܝܢܢ ܥܠ ܐܒܪܗܡ ܪܫܐ ܕܐܒܗܬܐ ܕܐܫܟܚ ܒܒܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 2, 'ܐܠܘ ܓܝܪ ܐܒܪܗܡ ܡܢ ܥܒܕܐ ܐܙܕܕܩ ܗܘܐ ܐܝܬ ܗܘܐ ܠܗ ܫܘܒܗܪܐ ܐܠܐ ܠܐ ܠܘܬ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 3, 'ܡܢܐ ܓܝܪ ܐܡܪ ܟܬܒܐ ܕܗܝܡܢ ܐܒܪܗܡ ܠܐܠܗܐ ܘܐܬܚܫܒܬ ܠܗ ܠܙܕܝܩܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 4, 'ܠܡܢ ܕܦܠܚ ܕܝܢ ܠܐ ܡܬܚܫܒ ܠܗ ܐܓܪܗ ܐܝܟ ܕܒܛܝܒܘ ܐܠܐ ܐܝܟ ܡܢ ܕܡܬܬܚܝܒ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 5, 'ܠܗܘ ܕܝܢ ܕܠܐ ܦܠܚ ܐܠܐ ܗܝܡܢ ܒܠܚܘܕ ܒܡܢ ܕܡܙܕܩ ܠܚܛܝܐ ܡܬܚܫܒܐ ܠܗ ܗܝܡܢܘܬܗ ܠܟܐܢܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 6, 'ܐܝܟܢܐ ܕܐܦ ܕܘܝܕ ܐܡܪ ܥܠ ܛܘܒܗ ܕܓܒܪܐ ܐܝܢܐ ܕܐܠܗܐ ܚܫܒ ܠܗ ܙܕܝܩܘܬܐ ܕܠܐ ܥܒܕܐ ܟܕ ܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 7, 'ܕܛܘܒܝܗܘܢ ܠܐܝܠܝܢ ܕܐܫܬܒܩ ܠܗܘܢ ܥܘܠܗܘܢ ܘܐܬܟܤܝܘ ܚܛܗܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 8, 'ܘܛܘܒܘܗܝ ܠܓܒܪܐ ܕܠܐ ܢܚܫܘܒ ܠܗ ܐܠܗܐ ܚܛܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 9, 'ܗܢܐ ܗܟܝܠ ܛܘܒܐ ܥܠ ܓܙܘܪܬܐ ܗܘ ܐܘ ܥܠ ܥܘܪܠܘܬܐ ܐܡܪܝܢܢ ܓܝܪ ܕܐܬܚܫܒܬ ܠܐܒܪܗܡ ܗܝܡܢܘܬܗ ܠܟܐܢܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 10, 'ܐܝܟܢܐ ܗܟܝܠ ܐܬܚܫܒܬ ܠܗ ܒܓܙܘܪܬܐ ܐܘ ܒܥܘܪܠܘܬܐ ܠܐ ܗܘܐ ܒܓܙܘܪܬܐ ܐܠܐ ܒܥܘܪܠܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 11, 'ܐܬܐ ܗܘ ܓܝܪ ܫܩܠܗ ܠܓܙܘܪܬܐ ܘܚܬܡܐ ܕܟܐܢܘܬܐ ܕܗܝܡܢܘܬܗ ܕܒܥܘܪܠܘܬܐ ܕܢܗܘܐ ܐܒܐ ܠܟܠܗܘܢ ܐܝܠܝܢ ܕܡܗܝܡܢܝܢ ܡܢ ܥܘܪܠܘܬܐ ܕܬܬܚܫܒ ܐܦ ܠܗܘܢ ܠܟܐܢܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 12, 'ܘܐܒܐ ܠܓܙܘܪܬܐ ܠܐ ܗܘܐ ܠܐܝܠܝܢ ܕܡܢ ܓܙܘܪܬܐ ܐܢܘܢ ܒܠܚܘܕ ܐܠܐ ܐܦ ܠܐܝܠܝܢ ܕܫܠܡܝܢ ܠܥܩܒܬܐ ܕܗܝܡܢܘܬܐ ܕܥܘܪܠܘܬܐ ܕܐܒܘܢ ܐܒܪܗܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 13, 'ܠܐ ܗܘܐ ܓܝܪ ܒܢܡܘܤܐ ܗܘܐ ܡܘܠܟܢܐ ܠܐܒܪܗܡ ܘܠܙܪܥܗ ܕܢܗܘܐ ܝܪܬܐ ܠܥܠܡܐ ܐܠܐ ܒܟܐܢܘܬܐ ܕܗܝܡܢܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 14, 'ܐܠܘ ܓܝܪ ܗܠܝܢ ܕܡܢ ܢܡܘܤܐ ܗܘܘ ܝܪܬܐ ܤܪܝܩܐ ܗܘܬ ܗܝܡܢܘܬܐ ܘܡܒܛܠ ܗܘܐ ܡܘܠܟܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 15, 'ܢܡܘܤܐ ܓܝܪ ܡܥܒܕܢܐ ܗܘ ܕܪܘܓܙܐ ܟܪ ܕܠܝܬ ܓܝܪ ܢܡܘܤܐ ܐܦܠܐ ܥܒܪ ܢܡܘܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 16, 'ܡܛܠ ܗܢܐ ܒܗܝܡܢܘܬܐ ܕܒܛܝܒܘܬܐ ܢܙܕܕܩ ܘܢܗܘܐ ܫܪܝܪ ܡܘܠܟܢܐ ܠܟܠܗ ܙܪܥܗ ܠܐ ܠܐܝܢܐ ܕܡܢ ܢܡܘܤܐ ܗܘ ܒܠܚܘܕ ܐܠܐ ܐܦ ܠܐܝܢܐ ܕܡܢ ܗܝܡܢܘܬܐ ܗܘ ܕܐܒܪܗܡ ܕܐܝܬܘܗܝ ܐܒܐ ܕܟܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 17, 'ܐܝܟܢܐ ܕܟܬܝܒ ܕܤܡܬܟ ܐܒܐ ܠܤܘܓܐܐ ܕܥܡܡܐ ܩܕܡ ܐܠܗܐ ܗܘ ܕܗܝܡܢܬ ܒܗ ܕܡܚܐ ܡܝܬܐ ܘܩܪܐ ܠܐܝܠܝܢ ܕܠܐ ܐܝܬܝܗܘܢ ܐܝܟ ܐܝܬܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 18, 'ܘܕܠܐ ܤܒܪܐ ܠܤܒܪܐ ܗܝܡܢ ܕܢܗܘܐ ܐܒܐ ܠܤܘܓܐܐ ܕܥܡܡܐ ܐܝܟ ܕܟܬܝܒ ܕܗܟܢܐ ܢܗܘܐ ܙܪܥܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 19, 'ܘܠܐ ܐܬܟܪܗ ܒܗܝܡܢܘܬܗ ܟܕ ܡܬܒܩܐ ܒܦܓܪܗ ܡܝܬܐ ܕܗܘܐ ܒܪ ܡܐܐ ܫܢܝܢ ܘܒܡܪܒܥܐ ܡܝܬܐ ܕܤܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 20, 'ܘܒܡܘܠܟܢܐ ܕܐܠܗܐ ܠܐ ܐܬܦܠܓ ܐܝܟ ܚܤܝܪ ܗܝܡܢܘܬܐ ܐܠܐ ܐܬܚܝܠ ܒܗܝܡܢܘܬܐ ܘܝܗܒ ܬܫܒܘܚܬܐ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 21, 'ܘܐܫܪ ܕܡܕܡ ܕܡܠܟ ܠܗ ܐܠܗܐ ܡܫܟܚ ܠܡܓܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 22, 'ܡܛܠ ܗܢܐ ܐܬܚܫܒܬ ܠܗ ܠܟܐܢܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 23, 'ܘܠܐ ܗܘܐ ܡܛܠܬܗ ܒܠܚܘܕ ܐܬܟܬܒܬ ܗܕܐ ܕܐܬܚܫܒܬ ܗܝܡܢܘܬܗ ܠܟܐܢܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 24, 'ܐܠܐ ܐܦ ܡܛܠܬܢ ܕܐܦܠܢ ܥܬܝܕ ܗܘ ܕܢܚܫܘܒ ܐܝܠܝܢ ܕܗܝܡܢܢ ܒܡܢ ܕܐܩܝܡ ܠܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܡܢ ܒܝܬ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 25, 'ܕܗܘ ܐܫܬܠܡ ܡܛܠ ܚܛܗܝܢ ܘܩܡ ܡܛܠ ܕܢܙܕܩܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 1, 'ܡܛܠ ܕܐܙܕܕܩܢ ܗܟܝܠ ܒܗܝܡܢܘܬܐ ܢܗܘܐ ܠܢ ܫܠܡܐ ܠܘܬ ܐܠܗܐ ܒܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 2, 'ܕܒܗ ܐܬܩܪܒܢ ܒܗܝܡܢܘܬܐ ܠܛܝܒܘܬܐ ܗܕܐ ܕܒܗ ܩܝܡܝܢܢ ܘܡܫܬܒܗܪܝܢܢ ܒܤܒܪܐ ܕܬܫܒܘܚܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 3, 'ܘܠܐ ܗܟܢܐ ܒܠܚܘܕ ܐܠܐ ܐܦ ܒܐܘܠܨܢܝܢ ܡܫܬܒܗܪܝܢܢ ܕܝܕܥܝܢܢ ܕܐܘܠܨܢܐ ܡܤܝܒܪܢܘܬܐ ܓܡܪ ܒܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 4, 'ܘܡܤܝܒܪܢܘܬܐ ܒܘܩܝܐ ܘܒܘܩܝܐ ܤܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 5, 'ܤܒܪܐ ܕܝܢ ܠܐ ܡܒܗܬ ܡܛܠ ܕܚܘܒܗ ܕܐܠܗܐ ܡܫܦܥ ܥܠ ܠܒܘܬܢ ܒܪܘܚܐ ܕܩܘܕܫܐ ܕܐܬܝܗܒܬ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 6, 'ܐܢ ܕܝܢ ܡܫܝܚܐ ܡܛܠ ܟܪܝܗܘܬܢ ܒܙܒܢܐ ܗܢܐ ܚܠܦ ܪܫܝܥܐ ܡܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 7, 'ܠܡܚܤܢ ܓܝܪ ܐܢܫ ܚܠܦ ܪܫܝܥܐ ܡܐܬ ܚܠܦ ܛܒܐ ܓܝܪ ܛܟ ܡܡܪܚ ܐܢܫ ܠܡܡܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 8, 'ܗܪܟܐ ܡܚܘܐ ܐܠܗܐ ܚܘܒܗ ܕܠܘܬܢ ܕܐܢ ܟܕ ܚܛܝܐ ܐܝܬܝܢ ܗܘܝܢ ܡܫܝܚܐ ܚܠܦܝܢ ܡܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 9, 'ܟܡܐ ܗܟܝܠ ܝܬܝܪܐܝܬ ܢܙܕܕܩ ܗܫܐ ܒܕܡܗ ܘܒܗ ܢܬܦܨܐ ܡܢ ܪܘܓܙܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 10, 'ܐܢ ܓܝܪ ܟܕ ܐܝܬܝܢ ܒܥܠܕܒܒܐ ܐܬܪܥܝ ܥܡܢ ܐܠܗܐ ܒܡܘܬܐ ܕܒܪܗ ܟܡܐ ܗܟܝܠ ܝܬܝܪܐܝܬ ܒܬܪܥܘܬܗ ܢܚܐ ܒܚܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 11, 'ܘܠܐ ܗܟܢܐ ܒܠܚܘܕ ܐܠܐ ܐܦ ܢܫܬܒܗܪ ܒܐܠܗܐ ܒܝܕ ܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܕܒܗ ܗܘ ܗܫܐ ܩܒܠܢ ܬܪܥܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 12, 'ܐܝܟܢܐ ܓܝܪ ܕܒܝܕ ܚܕ ܒܪܢܫܐ ܥܠܬ ܚܛܝܬܐ ܠܥܠܡܐ ܘܒܝܕ ܚܛܝܬܐ ܡܘܬܐ ܘܗܟܢܐ ܒܟܠܗܘܢ ܒܢܝ ܐܢܫܐ ܥܒܪ ܡܘܬܐ ܒܗܝ ܕܟܠܗܘܢ ܚܛܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 13, 'ܥܕܡܐ ܓܝܪ ܠܢܡܘܤܐ ܚܛܝܬܐ ܟܕ ܐܝܬܝܗ ܗܘܬ ܒܥܠܡܐ ܠܐ ܚܫܝܒܐ ܗܘܬ ܚܛܝܬܐ ܡܛܠ ܕܠܝܬ ܗܘܐ ܢܡܘܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 14, 'ܐܠܐ ܐܡܠܟ ܡܘܬܐ ܡܢ ܐܕܡ ܘܥܕܡܐ ܠܡܘܫܐ ܐܦ ܥܠ ܐܝܠܝܢ ܕܠܐ ܚܛܘ ܒܕܡܘܬܐ ܕܥܒܪ ܢܡܘܤܗ ܕܐܕܡ ܗܘ ܕܐܝܬܘܗܝ ܕܡܘܬܐ ܕܗܘ ܕܥܬܝܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 15, 'ܐܠܐ ܠܐ ܗܘܐ ܐܝܟ ܫܘܪܥܬܐ ܗܟܢܐ ܡܘܗܒܬܐ ܐܢ ܓܝܪ ܡܛܠ ܫܘܪܥܬܗ ܕܚܕ ܤܓܝܐܐ ܡܝܬܘ ܟܡܐ ܗܟܝܠ ܝܬܝܪܐܝܬ ܛܝܒܘܬܗ ܕܐܠܗܐ ܘܡܘܗܒܬܗ ܡܛܠ ܚܕ ܒܪܢܫܐ ܝܫܘܥ ܡܫܝܚܐ ܒܤܓܝܐܐ ܬܬܝܬܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 16, 'ܘܠܐ ܐܝܟ ܤܟܠܘܬܐ ܕܚܕ ܗܟܢܐ ܡܘܗܒܬܐ ܕܝܢܐ ܓܝܪ ܕܗܘܐ ܡܢ ܚܕ ܠܚܘܝܒܐ ܗܘܐ ܡܘܗܒܬܐ ܕܝܢ ܡܢ ܚܛܗܐ ܤܓܝܐܐ ܗܘܬ ܠܟܐܢܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 17, 'ܐܢ ܓܝܪ ܡܛܠ ܤܟܠܘܬܐ ܕܚܕ ܐܡܠܟ ܡܘܬܐ ܝܬܝܪܐܝܬ ܐܝܠܝܢ ܕܢܤܒܘ ܤܘܓܐܐ ܕܛܝܒܘܬܐ ܘܕܡܘܗܒܬܐ ܘܕܟܐܢܘܬܐ ܒܚܝܐ ܢܡܠܟܘܢ ܒܝܕ ܚܕ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 18, 'ܐܟܙܢܐ ܗܟܝܠ ܕܡܛܠ ܤܟܠܘܬܐ ܕܚܕ ܗܘܐ ܚܘܝܒܐ ܠܟܠܗܘܢ ܒܢܝܢܫܐ ܗܟܢܐ ܡܛܠ ܟܐܢܘܬܐ ܕܚܕ ܬܗܘܐ ܙܟܘܬܐ ܠܚܝܐ ܠܟܠܗܘܢ ܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 19, 'ܐܝܟܢܐ ܓܝܪ ܕܡܛܠ ܠܐ ܡܫܬܡܥܢܘܬܗ ܕܚܕ ܒܪܢܫܐ ܚܛܝܐ ܤܓܝܐܐ ܗܘܘ ܗܟܢܐ ܐܦ ܡܛܠ ܡܫܬܡܥܢܘܬܗ ܕܚܕ ܤܓܝܐܐ ܟܐܢܐ ܗܘܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 20, 'ܡܥܠܢܐ ܕܝܢ ܕܗܘܐ ܠܢܡܘܤܐ ܕܬܤܓܐ ܚܛܝܬܐ ܘܟܪ ܕܤܓܝܬ ܚܛܝܬܐ ܬܡܢ ܐܬܝܬܪܬ ܛܝܒܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 21, 'ܕܐܝܟ ܕܐܡܠܟܬ ܚܛܝܬܐ ܒܡܘܬܐ ܗܟܢܐ ܬܡܠܟ ܛܝܒܘܬܐ ܒܟܐܢܘܬܐ ܠܚܝܐ ܕܠܥܠܡ ܒܝܕ ܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 1, 'ܡܢܐ ܗܟܝܠ ܢܐܡܪ ܢܩܘܐ ܒܗ ܒܚܛܝܬܐ ܕܛܝܒܘܬܐ ܬܬܝܬܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 2, 'ܚܤ ܐܝܠܝܢ ܓܝܪ ܕܡܝܬܢ ܠܚܛܝܬܐ ܐܝܟܢܐ ܢܚܐ ܒܗ ܬܘܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 3, 'ܐܘ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܕܐܝܠܝܢ ܕܥܡܕܢ ܒܝܫܘܥ ܡܫܝܚܐ ܒܡܘܬܗ ܗܘ ܥܡܕܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 4, 'ܐܬܩܒܪܢ ܥܡܗ ܒܡܥܡܘܕܝܬܐ ܠܡܘܬܐ ܕܐܝܟܢܐ ܕܩܡ ܝܫܘܥ ܡܫܝܚܐ ܡܢ ܒܝܬ ܡܝܬܐ ܒܬܫܒܘܚܬܐ ܕܐܒܘܗܝ ܗܟܢܐ ܐܦ ܚܢܢ ܒܚܝܐ ܚܕܬܐ ܢܗܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 5, 'ܐܢ ܓܝܪ ܐܟܚܕ ܐܬܢܨܒܢ ܥܡܗ ܒܕܡܘܬܐ ܕܡܘܬܗ ܗܟܢܐ ܐܦ ܒܩܝܡܬܗ ܢܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 6, 'ܝܕܥܝܢܢ ܓܝܪ ܕܒܪܢܫܢ ܥܬܝܩܐ ܐܙܕܩܦ ܥܡܗ ܕܢܬܒܛܠ ܦܓܪܐ ܕܚܛܝܬܐ ܕܬܘܒ ܠܐ ܢܫܡܫ ܠܚܛܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 7, 'ܐܝܢܐ ܕܡܝܬ ܓܝܪ ܐܬܚܪܪ ܠܗ ܡܢ ܚܛܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 8, 'ܐܢ ܗܟܝܠ ܡܝܬܢ ܥܡ ܡܫܝܚܐ ܢܗܝܡܢ ܕܥܡܗ ܥܡ ܡܫܝܚܐ ܢܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 9, 'ܝܕܥܝܢܢ ܓܝܪ ܕܡܫܝܚܐ ܩܡ ܡܢ ܒܝܬ ܡܝܬܐ ܘܬܘܒ ܠܐ ܡܐܬ ܘܡܘܬܐ ܠܐ ܡܫܬܠܛ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 10, 'ܕܡܝܬ ܓܝܪ ܠܚܛܝܬܐ ܗܘ ܡܝܬ ܚܕܐ ܙܒܢ ܘܕܚܝ ܚܝ ܗܘ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 11, 'ܗܟܢܐ ܐܦ ܐܢܬܘܢ ܚܫܘܒܘ ܢܦܫܟܘܢ ܕܡܝܬܐ ܐܢܬܘܢ ܠܚܛܝܬܐ ܘܚܝܐ ܐܢܬܘܢ ܠܐܠܗܐ ܒܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 12, 'ܠܐ ܗܟܝܠ ܬܡܠܟ ܚܛܝܬܐ ܒܦܓܪܟܘܢ ܡܝܬܐ ܐܝܟ ܕܬܫܬܡܥܘܢ ܠܪܓܝܓܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 13, 'ܘܐܦ ܠܐ ܬܛܝܒܘܢ ܗܕܡܝܟܘܢ ܙܝܢܐ ܕܥܘܠܐ ܠܚܛܝܬܐ ܐܠܐ ܛܝܒܘ ܢܦܫܟܘܢ ܠܐܠܗܐ ܐܝܟ ܐܢܫܐ ܕܡܢ ܡܝܬܐ ܚܝܝܬܘܢ ܘܗܕܡܝܟܘܢ ܙܝܢܐ ܢܗܘܘܢ ܠܟܐܢܘܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 14, 'ܘܚܛܝܬܐ ܠܐ ܡܫܬܠܛܐ ܥܠܝܟܘܢ ܠܐ ܓܝܪ ܐܝܬܝܟܘܢ ܬܚܝܬ ܢܡܘܤܐ ܐܠܐ ܬܚܝܬ ܛܝܒܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 15, 'ܡܢܐ ܗܟܝܠ ܢܚܛܐ ܕܠܐ ܗܘܝܢ ܬܚܝܬ ܢܡܘܤܐ ܐܠܐ ܬܚܝܬ ܛܝܒܘܬܐ ܚܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 16, 'ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܕܠܡܢ ܕܡܛܝܒܝܢ ܐܢܬܘܢ ܢܦܫܟܘܢ ܕܬܫܬܡܥܘܢ ܠܗ ܠܥܒܕܘܬܐ ܕܝܠܗ ܐܢܬܘܢ ܥܒܕܐ ܕܗܘ ܕܡܫܬܡܥܝܢ ܐܢܬܘܢ ܠܗ ܐܢ ܠܚܛܝܬܐ ܘܐܢ ܠܡܫܡܥ ܐܕܢܐ ܕܟܐܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 17, 'ܛܝܒܘ ܕܝܢ ܠܐܠܗܐ ܕܥܒܕܐ ܗܘܝܬܘܢ ܕܚܛܝܬܐ ܘܐܫܬܡܥܬܘܢ ܡܢ ܠܒܐ ܠܕܡܘܬܐ ܕܝܘܠܦܢܐ ܕܐܫܬܠܡܬܘܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 18, 'ܘܟܕ ܐܬܚܪܪܬܘܢ ܡܢ ܚܛܝܬܐ ܐܫܬܥܒܕܬܘܢ ܠܟܐܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 19, 'ܐܝܟ ܕܒܝܬ ܒܢܝܢܫܐ ܐܡܪ ܐܢܐ ܡܛܠ ܟܪܝܗܘܬܐ ܕܒܤܪܟܘܢ ܕܐܝܟܢܐ ܕܛܝܒܬܘܢ ܗܕܡܝܟܘܢ ܠܥܒܕܘܬܐ ܕܛܢܦܘܬܐ ܘܕܥܘܠܐ ܗܟܢܐ ܐܦ ܗܫܐ ܛܝܒܘ ܗܕܡܝܟܘܢ ܠܥܒܕܘܬܐ ܕܟܐܢܘܬܐ ܘܕܩܕܝܫܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 20, 'ܟܕ ܥܒܕܐ ܗܘܝܬܘܢ ܓܝܪ ܕܚܛܝܬܐ ܡܚܪܪܐ ܗܘܝܬܘܢ ܠܟܐܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 21, 'ܘܡܢܐ ܐܕܫܐ ܐܝܬ ܗܘܐ ܠܟܘܢ ܗܝܕܝܢ ܐܝܢܐ ܕܝܘܡܢܐ ܒܗܬܝܢ ܐܢܬܘܢ ܒܗ ܚܪܬܗ ܓܝܪ ܐܝܬܝܗ ܡܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 22, 'ܘܗܫܐ ܕܐܬܚܪܪܬܘܢ ܡܢ ܚܛܝܬܐ ܘܗܘܝܬܘܢ ܥܒܕܐ ܠܐܠܗܐ ܐܝܬ ܠܟܘܢ ܦܐܪܐ ܩܕܝܫܐ ܕܚܪܬܗܘܢ ܚܝܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 23, 'ܬܐܓܘܪܬܐ ܕܝܢ ܕܚܛܝܬܐ ܡܘܬܐ ܗܘ ܘܡܘܗܒܬܗ ܕܐܠܗܐ ܚܝܐ ܕܠܥܠܡ ܒܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 1, 'ܐܘ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܐܚܝ ܠܝܕܥܝ ܢܡܘܤܐ ܓܝܪ ܡܡܠܠ ܐܢܐ ܕܢܡܘܤܐ ܫܠܝܛ ܗܘ ܥܠ ܓܒܪܐ ܟܡܐ ܕܚܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 2, 'ܐܝܟ ܐܢܬܬܐ ܕܐܤܝܪܐ ܗܝ ܒܒܥܠܗ ܟܡܐ ܕܚܝ ܒܢܡܘܤܐ ܐܢ ܕܝܢ ܡܝܬ ܒܥܠܗ ܐܬܚܪܪܬ ܡܢ ܢܡܘܤܐ ܕܒܥܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 3, 'ܐܢ ܕܝܢ ܟܕ ܚܝ ܒܥܠܗ ܬܩܦ ܠܓܒܪ ܐܚܪܝܢ ܗܘܬ ܠܗ ܓܝܪܬܐ ܐܢ ܕܝܢ ܢܡܘܬ ܒܥܠܗ ܐܬܚܪܪܬ ܡܢ ܢܡܘܤܐ ܘܠܐ ܐܝܬܝܗ ܓܝܪܬܐ ܐܢ ܬܗܘܐ ܠܓܒܪܐ ܐܚܪܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 4, 'ܘܗܫܐ ܐܚܝ ܐܦ ܐܢܬܘܢ ܡܝܬܬܘܢ ܠܢܡܘܤܐ ܒܦܓܪܗ ܕܡܫܝܚܐ ܕܬܗܘܘܢ ܠܐܚܪܝܢ ܐܝܢܐ ܕܩܡ ܡܢ ܒܝܬ ܡܝܬܐ ܕܬܬܠܘܢ ܦܐܪܐ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 5, 'ܟܕ ܒܒܤܪܐ ܗܘܝܢ ܓܝܪ ܟܐܒܐ ܕܚܛܗܐ ܕܒܢܡܘܤܐ ܡܬܚܦܛܝܢ ܗܘܘ ܒܗܕܡܝܢ ܕܦܐܪܐ ܢܬܠ ܠܡܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 6, 'ܗܫܐ ܕܝܢ ܐܬܒܛܠܢ ܡܢ ܢܡܘܤܐ ܘܡܝܬܢ ܠܗܘ ܕܐܚܝܕ ܗܘܐ ܠܢ ܕܢܫܡܫ ܡܟܝܠ ܒܚܕܬܘܬܐ ܕܪܘܚܐ ܘܠܐ ܒܥܬܝܩܘܬ ܟܬܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 7, 'ܡܢܐ ܗܟܝܠ ܐܡܪܝܢܢ ܢܡܘܤܐ ܚܛܝܬܐ ܗܘ ܚܤ ܐܠܐ ܠܚܛܝܬܐ ܠܐ ܝܠܦܬ ܐܠܐ ܒܝܕ ܢܡܘܤܐ ܠܐ ܓܝܪ ܪܓܬܐ ܝܕܥ ܗܘܝܬ ܐܠܘ ܠܐ ܢܡܘܤܐ ܕܐܡܪ ܕܠܐ ܬܪܓ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 8, 'ܘܒܗܢܐ ܦܘܩܕܢܐ ܐܫܟܚܬ ܠܗ ܚܛܝܬܐ ܥܠܬܐ ܘܓܡܪܬ ܒܝ ܟܠ ܪܓܐ ܒܠܥܕ ܢܡܘܤܐ ܓܝܪ ܚܛܝܬܐ ܡܝܬܐ ܗܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 9, 'ܐܢܐ ܕܝܢ ܚܝ ܗܘܝܬ ܕܠܐ ܢܡܘܤܐ ܡܢ ܩܕܝܡ ܟܕ ܐܬܐ ܕܝܢ ܦܘܩܕܢܐ ܚܛܝܬܐ ܚܝܬ ܘܐܢܐ ܡܝܬܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 10, 'ܘܐܫܬܟܚ ܠܝ ܦܘܩܕܢܐ ܗܘ ܕܚܝܐ ܠܡܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 11, 'ܚܛܝܬܐ ܓܝܪ ܒܥܠܬܐ ܕܐܫܟܚܬ ܠܗ ܒܝܕ ܦܘܩܕܢܐ ܐܛܥܝܬܢܝ ܘܒܗ ܩܛܠܬܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 12, 'ܢܡܘܤܐ ܡܕܝܢ ܩܕܝܫ ܗܘ ܘܦܘܩܕܢܐ ܩܕܝܫ ܗܘ ܘܟܐܝܢ ܘܛܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 13, 'ܛܒܬܐ ܗܟܝܠ ܠܝ ܠܡܘܬܐ ܗܘ ܗܘܬ ܚܤ ܐܠܐ ܚܛܝܬܐ ܕܬܬܚܙܐ ܕܚܛܝܬܐ ܗܝ ܕܒܛܒܬܐ ܓܡܪܬ ܒܝ ܡܘܬܐ ܕܝܬܝܪܐܝܬ ܬܬܚܝܒ ܚܛܝܬܐ ܒܦܘܩܕܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 14, 'ܝܕܥܝܢܢ ܓܝܪ ܕܢܡܘܤܐ ܕܪܘܚ ܗܘ ܐܢܐ ܕܝܢ ܕܒܤܪ ܐܢܐ ܘܡܙܒܢ ܐܢܐ ܠܚܛܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 15, 'ܡܕܡ ܕܤܥܪ ܐܢܐ ܓܝܪ ܠܐ ܝܕܥ ܐܢܐ ܘܠܐ ܗܘܐ ܡܕܡ ܕܨܒܐ ܐܢܐ ܥܒܕ ܐܢܐ ܐܠܐ ܡܕܡ ܕܤܢܐ ܐܢܐ ܗܘ ܗܘ ܥܒܕ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 16, 'ܘܐܢ ܡܕܡ ܕܠܐ ܨܒܐ ܐܢܐ ܥܒܕ ܐܢܐ ܤܗܕ ܐܢܐ ܥܠ ܢܡܘܤܐ ܕܫܦܝܪ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 17, 'ܗܫܐ ܕܝܢ ܠܐ ܗܘܐ ܐܢܐ ܤܥܪ ܐܢܐ ܗܕܐ ܐܠܐ ܚܛܝܬܐ ܕܥܡܪܐ ܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 18, 'ܝܕܥ ܐܢܐ ܗܘ ܓܝܪ ܕܠܐ ܥܡܪܐ ܒܝ ܗܢܘ ܕܝܢ ܒܒܤܪܝ ܛܒܬܐ ܕܐܨܒܐ ܓܝܪ ܒܛܒܬܐ ܦܫܝܩ ܠܝ ܕܐܤܥܪܝܗ ܕܝܢ ܠܐ ܡܫܟܚ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 19, 'ܠܐ ܗܘܐ ܓܝܪ ܠܛܒܬܐ ܕܨܒܐ ܐܢܐ ܕܐܥܒܕ ܥܒܕ ܐܢܐ ܐܠܐ ܒܝܫܬܐ ܕܠܐ ܨܒܐ ܐܢܐ ܕܐܥܒܕ ܠܗ ܥܒܕ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 20, 'ܘܐܢ ܡܕܡ ܕܠܐ ܨܒܐ ܐܢܐ ܥܒܕ ܐܢܐ ܠܐ ܗܘܐ ܐܢܐ ܥܒܕ ܐܢܐ ܐܠܐ ܚܛܝܬܐ ܕܥܡܪܐ ܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 21, 'ܡܫܟܚ ܐܢܐ ܗܟܝܠ ܠܢܡܘܤܐ ܕܫܠܡ ܠܪܥܝܢܝ ܗܘ ܕܨܒܐ ܕܢܥܒܕ ܛܒܬܐ ܡܛܠ ܕܒܝܫܬܐ ܩܪܝܒܐ ܗܝ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 22, 'ܚܕܐ ܐܢܐ ܓܝܪ ܒܢܡܘܤܗ ܕܐܠܗܐ ܒܒܪܢܫܐ ܕܠܓܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 23, 'ܚܙܐ ܐܢܐ ܕܝܢ ܢܡܘܤܐ ܐܚܪܢܐ ܒܗܕܡܝ ܕܡܩܪܒ ܠܘܩܒܠ ܢܡܘܤܐ ܕܪܥܝܢܝ ܘܫܒܐ ܠܝ ܠܢܡܘܤܐ ܕܚܛܝܬܐ ܕܐܝܬ ܒܗܕܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 24, 'ܕܘܝܐ ܐܢܐ ܒܪܢܫܐ ܡܢܘ ܢܦܨܝܢܝ ܡܢ ܦܓܪܐ ܗܢܐ ܕܡܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 25, 'ܡܘܕܐ ܐܢܐ ܠܐܠܗܐ ܒܝܕ ܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܗܫܐ ܗܟܝܠ ܐܢܐ ܒܪܥܝܢܝ ܥܒܕܐ ܐܢܐ ܕܢܡܘܤܐ ܕܐܠܗܐ ܒܒܤܪܝ ܕܝܢ ܐܝܬܝ ܥܒܕܐ ܕܢܡܘܤܐ ܕܚܛܝܬܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 1, 'ܡܟܝܠ ܠܝܬ ܚܝܒܘܬܐ ܠܐܝܠܝܢ ܕܠܐ ܡܗܠܟܝܢ ܒܒܤܪ ܒܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 2, 'ܢܡܘܤܐ ܓܝܪ ܕܪܘܚܐ ܕܚܝܐ ܕܒܝܫܘܥ ܡܫܝܚܐ ܚܪܪܟ ܡܢ ܢܡܘܤܐ ܕܚܛܝܬܐ ܘܕܡܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 3, 'ܡܛܠ ܕܡܚܝܠ ܗܘܐ ܓܝܪ ܢܡܘܤܐ ܒܝܕ ܟܪܝܗܘܬܗ ܕܒܤܪܐ ܫܕܪ ܐܠܗܐ ܠܒܪܗ ܒܕܡܘܬܐ ܕܒܤܪܐ ܕܚܛܝܬܐ ܡܛܠܬܗ ܕܚܛܝܬܐ ܕܢܚܝܒܝܗ ܠܚܛܝܬܐ ܒܒܤܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 4, 'ܕܟܐܢܘܬܐ ܕܢܡܘܤܐ ܒܢ ܬܬܡܠܐ ܕܠܐ ܗܘܐ ܒܒܤܪ ܡܗܠܟܝܢܢ ܐܠܐ ܒܪܘܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 5, 'ܐܝܠܝܢ ܓܝܪ ܕܒܒܤܪ ܐܢܘܢ ܕܒܤܪ ܗܘ ܡܬܪܥܝܢ ܘܐܝܠܝܢ ܕܕܪܘܚ ܐܢܘܢ ܕܪܘܚ ܗܘ ܡܬܪܥܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 6, 'ܬܪܥܝܬܐ ܓܝܪ ܕܒܤܪܐ ܡܘܬܐ ܗܘ ܘܬܪܥܝܬܐ ܕܪܘܚܐ ܚܝܐ ܘܫܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 7, 'ܡܛܠ ܕܬܪܥܝܬܐ ܕܒܤܪܐ ܒܥܠܕܒܒܘܬܐ ܗܝ ܠܘܬ ܐܠܗܐ ܠܢܡܘܤܐ ܓܝܪ ܕܐܠܗܐ ܠܐ ܡܫܬܥܒܕܐ ܡܛܠ ܕܠܐ ܡܫܟܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 8, 'ܘܐܝܠܝܢ ܕܒܒܤܪ ܐܢܘܢ ܠܡܫܦܪ ܠܐܠܗܐ ܠܐ ܡܫܟܚܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 9, 'ܐܢܬܘܢ ܕܝܢ ܠܐ ܗܘܝܬܘܢ ܒܒܤܪ ܐܠܐ ܒܪܘܚ ܐܢ ܫܪܝܪܐܝܬ ܪܘܚܗ ܕܐܠܗܐ ܥܡܪܐ ܒܟܘܢ ܐܢ ܕܝܢ ܐܢܫ ܠܝܬ ܒܗ ܪܘܚܗ ܕܡܫܝܚܐ ܗܢܐ ܠܐ ܗܘܐ ܕܝܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 10, 'ܘܐܢ ܕܝܢ ܡܫܝܚܐ ܒܟܘܢ ܦܓܪܐ ܡܝܬ ܗܘ ܡܛܠ ܚܛܝܬܐ ܪܘܚܐ ܕܝܢ ܚܝܐ ܗܝ ܡܛܠ ܟܐܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 11, 'ܘܐܢ ܪܘܚܗ ܕܗܘ ܡܢ ܕܐܩܝܡ ܠܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܡܢ ܒܝܬ ܡܝܬܐ ܥܡܪܐ ܒܟܘܢ ܗܘ ܡܢ ܕܐܩܝܡܗ ܠܝܫܘܥ ܡܫܝܚܐ ܡܢ ܒܝܬ ܡܝܬܐ ܐܦ ܠܦܓܪܝܟܘܢ ܡܝܬܐ ܢܚܐ ܡܛܠ ܪܘܚܗ ܕܥܡܪܐ ܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 12, 'ܗܫܐ ܐܚܝ ܚܝܒܝܢܢ ܠܘ ܠܒܤܪܐ ܕܒܒܤܪ ܢܗܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 13, 'ܐܢ ܓܝܪ ܒܒܤܪ ܚܐܝܢ ܐܢܬܘܢ ܥܬܝܕܝܢ ܐܢܬܘܢ ܗܘ ܠܡܡܬ ܘܐܢ ܒܪܘܚ ܗܘܦܟܝ ܦܓܪܐ ܡܡܝܬܝܢ ܐܢܬܘܢ ܚܐܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 14, 'ܐܝܠܝܢ ܓܝܪ ܕܒܪܘܚܐ ܕܐܠܗܐ ܡܬܕܒܪܝܢ ܗܠܝܢ ܒܢܝܐ ܐܢܘܢ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 15, 'ܠܐ ܓܝܪ ܢܤܒܬܘܢ ܪܘܚܐ ܕܥܒܕܘܬܐ ܬܘܒ ܠܕܚܠܬܐ ܐܠܐ ܢܤܒܬܘܢ ܪܘܚܐ ܕܤܝܡܬ ܒܢܝܐ ܕܒܗ ܩܪܝܢܢ ܐܒܐ ܐܒܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 16, 'ܘܗܝ ܪܘܚܐ ܡܤܗܕܐ ܠܪܘܚܢ ܕܐܝܬܝܢ ܒܢܝܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 17, 'ܘܐܢ ܒܢܝܐ ܐܦ ܝܪܬܐ ܝܪܬܐ ܕܐܠܗܐ ܘܒܢܝ ܝܪܬܘܬܗ ܕܝܫܘܥ ܡܫܝܚܐ ܕܐܢ ܢܚܫ ܥܡܗ ܐܦ ܥܡܗ ܢܫܬܒܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 18, 'ܡܬܪܥܐ ܐܢܐ ܓܝܪ ܕܠܐ ܫܘܝܢ ܚܫܘܗܝ ܕܙܒܢܐ ܗܢܐ ܠܬܫܒܘܚܬܐ ܐܝܕܐ ܕܥܬܝܕܐ ܕܬܬܓܠܐ ܒܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 19, 'ܟܠܗ ܓܝܪ ܒܪܝܬܐ ܡܤܒܪܐ ܘܡܤܟܝܐ ܠܓܠܝܢܐ ܕܒܢܘܗܝ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 20, 'ܒܪܝܬܐ ܓܝܪ ܐܫܬܥܒܕܬ ܠܤܪܝܩܘܬܐ ܠܐ ܒܨܒܝܢܗ ܐܠܐ ܡܛܠ ܡܢ ܕܫܥܒܕܗ ܥܠ ܤܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 21, 'ܕܐܦ ܗܝ ܒܪܝܬܐ ܬܬܚܪܪ ܡܢ ܥܒܕܘܬܐ ܕܚܒܠܐ ܒܚܪܘܪܐ ܕܬܫܒܘܚܬܐ ܕܒܢܝܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 22, 'ܝܕܥܝܢܢ ܓܝܪ ܕܟܠܗܝܢ ܒܪܝܬܐ ܡܬܬܢܚܢ ܘܡܚܒܠܢ ܥܕܡܐ ܠܝܘܡܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 23, 'ܘܠܐ ܒܠܚܘܕ ܗܢܝܢ ܐܠܐ ܐܦ ܚܢܢ ܕܐܝܬ ܒܢ ܪܫܝܬܐ ܕܪܘܚܐ ܡܬܬܢܚܝܢܢ ܒܢܦܫܢ ܘܡܤܟܝܢܢ ܠܤܝܡܬ ܒܢܝܐ ܘܠܦܘܪܩܢܐ ܕܦܓܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 24, 'ܡܛܠ ܕܒܤܒܪܐ ܗܘ ܚܝܝܢ ܤܒܪܐ ܕܝܢ ܕܡܬܚܙܐ ܠܐ ܗܘܐ ܤܒܪܐ ܐܢ ܓܝܪ ܚܙܝܢܢ ܠܗ ܡܢܐ ܡܤܟܝܢܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 25, 'ܐܢ ܕܝܢ ܠܡܕܡ ܕܠܐ ܡܬܚܙܐ ܡܤܒܪܝܢܢ ܒܡܤܝܒܪܢܘܬܐ ܡܩܘܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 26, 'ܗܟܢܐ ܐܦ ܗܝ ܪܘܚܐ ܡܥܕܪܐ ܠܟܪܝܗܘܬܢ ܡܢܐ ܓܝܪ ܢܨܠܐ ܐܝܟ ܡܐ ܕܘܠܐ ܠܐ ܝܕܥܝܢܢ ܐܠܐ ܗܝ ܪܘܚܐ ܡܨܠܝܐ ܚܠܦܝܢ ܒܬܢܚܬܐ ܕܠܐ ܡܬܡܠܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 27, 'ܕܡܐܫ ܕܝܢ ܠܒܘܬܐ ܗܘ ܝܕܥ ܡܢܐ ܗܝ ܬܪܥܝܬܐ ܕܪܘܚܐ ܕܐܝܟ ܨܒܝܢܗ ܗܘ ܕܐܠܗܐ ܡܨܠܝܐ ܚܠܦ ܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 28, 'ܝܕܥܝܢܢ ܕܝܢ ܕܐܝܠܝܢ ܕܡܚܒܝܢ ܠܐܠܗܐ ܒܟܠ ܡܕܡ ܡܥܕܪ ܠܗܘܢ ܠܛܒܬܐ ܠܐܝܠܝܢ ܕܩܕܡ ܤܡ ܕܢܗܘܘܢ ܩܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 29, 'ܘܡܢ ܠܘܩܕܡ ܝܕܥ ܐܢܘܢ ܘܪܫܡ ܐܢܘܢ ܒܕܡܘܬܐ ܕܨܘܪܬܐ ܕܒܪܗ ܕܗܘ ܢܗܘܐ ܒܘܟܪܐ ܕܐܚܐ ܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 30, 'ܠܐܝܠܝܢ ܕܝܢ ܕܩܕܡ ܪܫܡ ܠܗܘܢ ܩܪܐ ܘܠܐܝܠܝܢ ܕܩܪܐ ܠܗܘܢ ܙܕܩ ܘܠܐܝܠܝܢ ܕܙܕܩ ܠܗܘܢ ܫܒܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 31, 'ܡܢܐ ܗܟܝܠ ܢܐܡܪ ܥܠ ܗܠܝܢ ܐܢ ܐܠܗܐ ܚܠܦܝܢ ܡܢܘ ܕܠܩܘܒܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 32, 'ܘܐܢ ܥܠ ܒܪܗ ܠܐ ܚܤ ܐܠܐ ܚܠܦ ܟܠܢ ܐܫܠܡܗ ܐܝܟܢܐ ܠܐ ܟܠ ܡܕܡ ܥܡܗ ܢܬܠ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 33, 'ܡܢܘ ܢܩܒܘܠ ܥܠ ܓܒܝܐ ܕܐܠܗܐ ܐܠܗܐ ܡܙܕܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 34, 'ܡܢܘ ܡܚܝܒ ܡܫܝܚܐ ܡܝܬ ܘܩܡ ܘܐܝܬܘܗܝ ܥܠ ܝܡܝܢܐ ܕܐܠܗܐ ܘܒܥܐ ܚܠܦܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 35, 'ܡܢܘ ܢܦܪܫܢܝ ܡܢ ܚܘܒܗ ܕܡܫܝܚܐ ܐܘܠܨܢܐ ܐܘ ܚܒܘܫܝܐ ܐܘ ܪܕܘܦܝܐ ܐܘ ܟܦܢܐ ܐܘ ܥܪܛܠܝܘܬܐ ܐܘ ܩܢܕܝܢܘܤ ܐܘ ܤܝܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 36, 'ܐܝܟ ܕܟܬܝܒ ܕܡܛܠܬܟ ܟܠܝܘܡ ܡܝܬܝܢܢ ܘܐܬܚܫܒܢ ܐܝܟ ܐܡܪܐ ܠܢܟܤܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 37, 'ܐܠܐ ܒܗܠܝܢ ܟܠܗܝܢ ܙܟܝܝܢܢ ܒܝܕ ܡܢ ܕܐܚܒܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 38, 'ܡܦܤ ܐܢܐ ܓܝܪ ܕܠܐ ܡܘܬܐ ܘܠܐ ܚܝܐ ܘܠܐ ܡܠܐܟܐ ܘܠܐ ܫܘܠܛܢܐ ܘܠܐ ܚܝܠܐ ܘܠܐ ܕܩܝܡܢ ܘܠܐ ܕܥܬܝܕܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 39, 'ܘܠܐ ܪܘܡܐ ܘܠܐ ܥܘܡܩܐ ܐܦܠܐ ܒܪܝܬܐ ܐܚܪܬܐ ܬܫܟܚ ܬܦܪܫܢܝ ܡܢ ܚܘܒܗ ܕܐܠܗܐ ܕܒܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 1, 'ܩܘܫܬܐ ܐܡܪ ܐܢܐ ܒܡܫܝܚܐ ܘܠܐ ܡܕܓܠ ܐܢܐ ܘܪܥܝܢܝ ܡܤܗܕ ܥܠܝ ܒܪܘܚܐ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 2, 'ܕܟܪܝܘܬܐ ܗܝ ܠܝ ܪܒܬܐ ܘܟܐܒܐ ܕܡܢ ܠܒܝ ܠܐ ܫܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 3, 'ܡܨܠܐ ܗܘܝܬ ܓܝܪ ܕܐܢܐ ܩܢܘܡܝ ܚܪܡܐ ܐܗܘܐ ܡܢ ܡܫܝܚܐ ܚܠܦ ܐܚܝ ܘܐܚܝܢܝ ܕܒܒܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 4, 'ܕܐܝܬܝܗܘܢ ܒܢܝ ܐܝܤܪܝܠ ܕܕܝܠܗܘܢ ܗܘܬ ܤܝܡܬ ܒܢܝܐ ܘܬܫܒܘܚܬܐ ܘܩܝܡܐ ܘܢܡܘܤܐ ܘܬܫܡܫܬܐ ܕܒܗ ܘܡܘܠܟܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 5, 'ܘܐܒܗܬܐ ܘܡܢܗܘܢ ܐܬܚܙܝ ܡܫܝܚܐ ܒܒܤܪ ܕܐܝܬܘܗܝ ܐܠܗܐ ܕܥܠ ܟܠ ܕܠܗ ܬܫܒܚܢ ܘܒܘܪܟܢ ܠܥܠܡ ܥܠܡܝܢ ܐܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 6, 'ܠܐ ܗܘܐ ܕܝܢ ܡܦܠ ܢܦܠܬ ܡܠܬܗ ܕܐܠܗܐ ܠܐ ܗܘܐ ܓܝܪ ܟܠܗܘܢ ܕܡܢ ܐܝܤܪܝܠ ܐܝܬܝܗܘܢ ܐܝܤܪܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 7, 'ܐܦܠܐ ܡܛܠ ܕܡܢ ܙܪܥܗ ܐܢܘܢ ܕܐܒܪܗܡ ܟܠܗܘܢ ܒܢܝܐ ܡܛܠ ܕܐܬܐܡܪ ܕܒܐܝܤܚܩ ܢܬܩܪܐ ܠܟ ܙܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 8, 'ܗܢܘ ܕܝܢ ܠܐ ܗܘܐ ܒܢܝܐ ܕܒܤܪܐ ܐܝܬܝܗܘܢ ܒܢܝܐ ܕܐܠܗܐ ܐܠܐ ܒܢܝܐ ܕܡܘܠܟܢܐ ܡܬܚܫܒܝܢ ܠܙܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 9, 'ܕܡܘܠܟܢܐ ܓܝܪ ܐܝܬܝܗ ܡܠܬܐ ܗܕܐ ܕܒܙܒܢܐ ܗܢܐ ܐܬܐ ܘܢܗܘܐ ܒܪܐ ܠܤܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 10, 'ܘܠܘ ܗܕܐ ܒܠܚܘܕ ܐܠܐ ܐܦ ܪܦܩܐ ܟܕ ܥܡ ܚܕ ܐܒܘܢ ܐܝܤܚܩ ܐܝܬ ܗܘܐ ܠܗ ܫܘܬܦܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 11, 'ܥܕܠܐ ܢܬܝܠܕܘܢ ܒܢܝܗ ܘܠܐ ܢܤܥܪܘܢ ܛܒܬܐ ܐܘ ܒܝܫܬܐ ܩܕܡܬ ܐܬܝܕܥܬ ܓܒܝܘܬܗ ܕܐܠܗܐ ܕܗܝ ܬܩܘܐ ܠܐ ܒܥܒܕܐ ܐܠܐ ܒܝܕ ܡܢ ܕܩܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 12, 'ܐܬܐܡܪ ܓܝܪ ܕܩܫܝܫܐ ܢܗܘܐ ܥܒܕܐ ܠܙܥܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 13, 'ܐܝܟ ܕܟܬܝܒ ܕܠܝܥܩܘܒ ܪܚܡܬ ܘܠܥܤܘ ܤܢܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 14, 'ܡܢܐ ܗܟܝܠ ܢܐܡܪ ܕܠܡܐ ܥܘܠܐ ܐܝܬ ܠܘܬ ܐܠܗܐ ܚܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 15, 'ܗܐ ܐܦ ܠܡܘܫܐ ܐܡܪ ܐܪܚܡ ܥܠ ܐܝܢܐ ܕܡܪܚܡ ܐܢܐ ܘܐܚܘܢ ܠܐܝܢܐ ܕܚܐܢ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 16, 'ܠܐ ܗܟܝܠ ܒܐܝܕܝ ܡܢ ܕܨܒܐ ܘܠܐ ܒܐܝܕܝ ܡܢ ܕܪܗܛ ܐܠܐ ܒܐܝܕܝ ܐܠܗܐ ܡܪܚܡܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 17, 'ܐܡܪ ܓܝܪ ܒܟܬܒܐ ܠܦܪܥܘܢ ܕܠܗ ܠܗܕܐ ܐܩܝܡܬܟ ܕܐܚܘܐ ܒܟ ܚܝܠܝ ܘܕܢܬܟܪܙ ܫܡܝ ܒܐܪܥܐ ܟܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 18, 'ܡܕܝܢ ܥܠ ܡܢ ܕܨܒܐ ܗܘ ܡܪܚܡ ܘܥܠ ܡܢ ܕܨܒܐ ܡܩܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 19, 'ܘܟܒܪ ܬܐܡܪ ܕܠܡܢܐ ܪܫܐ ܡܢܘ ܓܝܪ ܕܢܩܘܡ ܠܘܩܒܠ ܨܒܝܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 20, 'ܐܢܬ ܗܟܝܠ ܡܢ ܐܢܬ ܐܘ ܒܪܢܫܐ ܕܦܬܓܡܐ ܠܐܠܗܐ ܝܗܒ ܐܢܬ ܕܠܡܐ ܐܡܪܐ ܓܒܝܠܬܐ ܠܡܢ ܕܓܒܠܗ ܕܠܡܢܐ ܗܟܢܐ ܓܒܠܬܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 21, 'ܐܘ ܠܐ ܫܠܝܛ ܦܚܪܐ ܥܠ ܛܝܢܗ ܕܡܢܗ ܡܢ ܓܒܝܠܬܐ ܢܥܒܕ ܡܐܢܐ ܚܕ ܠܐܝܩܪܐ ܘܚܕ ܠܨܥܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 22, 'ܐܢ ܕܝܢ ܨܒܐ ܐܠܗܐ ܕܢܚܘܐ ܪܘܓܙܗ ܘܢܘܕܥ ܚܝܠܗ ܐܝܬܝ ܒܤܘܓܐܐ ܕܡܓܪܬ ܪܘܚܗ ܪܘܓܙܐ ܥܠ ܡܐܢܐ ܕܪܘܓܙܐ ܕܓܡܝܪܝܢ ܠܐܒܕܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 23, 'ܘܐܫܦܥ ܪܚܡܘܗܝ ܥܠ ܡܐܢܐ ܕܪܚܡܐ ܕܡܛܝܒܝܢ ܗܘܘ ܠܐܠܗܐ ܠܬܫܒܘܚܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 24, 'ܕܐܝܬܝܢ ܚܢܢ ܩܪܝܐ ܠܐ ܗܘܐ ܒܠܚܘܕ ܡܢ ܝܗܘܕܝܐ ܐܠܐ ܐܦ ܡܢ ܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 25, 'ܐܝܟܢܐ ܕܐܦ ܒܗܘܫܥ ܐܡܪ ܕܐܩܪܐ ܠܐܝܠܝܢ ܕܠܐ ܗܘܘ ܥܡܝ ܥܡܐ ܕܝܠܝ ܘܠܠܐ ܐܬܪܚܡܬ ܐܬܪܚܡܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 26, 'ܢܗܘܐ ܓܝܪ ܒܕܘܟܬܐ ܟܪ ܕܡܬܩܪܝܢ ܗܘܘ ܠܐ ܥܡܝ ܬܡܢ ܢܬܩܪܘܢ ܒܢܝܐ ܠܐܠܗܐ ܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 27, 'ܐܫܥܝܐ ܕܝܢ ܐܟܪܙ ܥܠ ܒܢܝ ܐܝܤܪܝܠ ܕܐܢ ܢܗܘܐ ܡܢܝܢܐ ܕܒܢܝ ܐܝܤܪܝܠ ܐܝܟ ܚܠܐ ܕܒܝܡܐ ܫܪܟܢܐ ܕܡܢܗܘܢ ܢܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 28, 'ܡܠܬܐ ܓܪܡ ܘܦܤܩ ܘܢܥܒܕܝܗ ܡܪܝܐ ܥܠ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 29, 'ܘܐܝܟ ܡܕܡ ܕܩܕܡ ܐܡܪ ܗܘ ܐܫܥܝܐ ܕܐܠܘ ܠܐ ܡܪܝܐ ܨܒܐܘܬ ܐܘܬܪ ܠܢ ܤܪܝܕܐ ܐܝܟ ܤܕܘܡ ܗܘܝܢ ܗܘܝܢ ܘܠܥܡܘܪܐ ܡܬܕܡܝܢ ܗܘܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 30, 'ܡܢܐ ܗܟܝܠ ܢܐܡܪ ܕܥܡܡܐ ܕܠܐ ܪܗܛܝܢ ܗܘܘ ܒܬܪ ܟܐܢܘܬܐ ܐܕܪܟܘ ܟܐܢܘܬܐ ܟܐܢܘܬܐ ܕܝܢ ܐܝܕܐ ܕܡܢ ܗܝܡܢܘܬܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 31, 'ܐܝܤܪܝܠ ܕܝܢ ܕܪܗܛ ܗܘܐ ܒܬܪ ܢܡܘܤܐ ܕܟܐܢܘܬܐ ܠܢܡܘܤܐ ܕܟܐܢܘܬܐ ܠܐ ܐܕܪܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 32, 'ܡܛܠ ܡܢܐ ܡܛܠ ܕܠܐ ܗܘܐ ܡܢ ܗܝܡܢܘܬܐ ܐܠܐ ܡܢ ܥܒܕܘܗܝ ܕܢܡܘܤܐ ܐܬܬܩܠܘ ܓܝܪ ܒܟܐܦܐ ܕܬܘܩܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 33, 'ܐܝܟ ܕܟܬܝܒ ܕܗܐ ܤܐܡ ܐܢܐ ܒܨܗܝܘܢ ܟܐܦܐ ܕܬܘܩܠܬܐ ܘܟܐܦܐ ܕܡܟܫܘܠܐ ܘܡܢ ܕܒܗ ܢܗܝܡܢ ܠܐ ܢܒܗܬ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 1, 'ܐܚܝ ܨܒܝܢܐ ܕܠܒܝ ܘܒܥܘܬܝ ܕܠܘܬ ܐܠܗܐ ܚܠܦܝܗܘܢ ܕܢܚܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 2, 'ܡܤܗܕ ܐܢܐ ܓܝܪ ܥܠܝܗܘܢ ܕܛܢܢܐ ܕܐܠܗܐ ܐܝܬ ܒܗܘܢ ܐܠܐ ܠܘ ܒܝܕܥܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 3, 'ܟܐܢܘܬܗ ܓܝܪ ܕܐܠܗܐ ܠܐ ܝܕܥܘ ܐܠܐ ܒܥܘ ܕܟܐܢܘܬܐ ܕܢܦܫܗܘܢ ܢܩܝܡܘܢ ܘܡܛܠ ܗܢܐ ܠܟܐܢܘܬܗ ܕܐܠܗܐ ܠܐ ܐܫܬܥܒܕܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 4, 'ܤܟܗ ܓܝܪ ܕܢܡܘܤܐ ܡܫܝܚܐ ܗܘ ܠܟܐܢܘܬܐ ܠܟܠ ܕܡܗܝܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 5, 'ܡܘܫܐ ܓܝܪ ܗܟܢܐ ܟܬܒ ܟܐܢܘܬܐ ܕܒܢܡܘܤܐ ܕܡܢ ܕܢܥܒܕ ܗܠܝܢ ܢܚܐ ܒܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 6, 'ܟܐܢܘܬܐ ܕܝܢ ܕܒܗܝܡܢܘܬܐ ܗܟܢܐ ܐܡܪܐ ܕܠܐ ܬܐܡܪ ܒܠܒܟ ܕܡܢܘ ܤܠܩ ܠܫܡܝܐ ܘܐܚܬ ܠܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 7, 'ܘܡܢܘ ܢܚܬ ܠܬܗܘܡܐ ܕܫܝܘܠ ܘܐܤܩ ܠܡܫܝܚܐ ܡܢ ܒܝܬ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 8, 'ܐܠܐ ܡܢܐ ܐܡܪ ܩܪܝܒ ܗܘ ܠܟ ܦܬܓܡܐ ܠܦܘܡܟ ܘܠܠܒܟ ܗܕܐ ܗܝ ܡܠܬܐ ܕܗܝܡܢܘܬܐ ܕܡܟܪܙܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 9, 'ܘܐܢ ܬܘܕܐ ܒܦܘܡܟ ܒܡܪܢ ܝܫܘܥ ܘܬܗܝܡܢ ܒܠܒܟ ܕܐܠܗܐ ܐܩܝܡܗ ܡܢ ܒܝܬ ܡܝܬܐ ܬܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 10, 'ܠܒܐ ܓܝܪ ܕܡܗܝܡܢ ܒܗ ܡܙܕܕܩ ܘܦܘܡܐ ܕܡܘܕܐ ܒܗ ܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 11, 'ܐܡܪ ܓܝܪ ܟܬܒܐ ܕܟܠ ܕܡܗܝܡܢ ܒܗ ܠܐ ܢܒܗܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 12, 'ܘܒܗܕܐ ܠܐ ܦܪܫ ܠܐ ܠܝܗܘܕܝܐ ܘܠܐ ܠܐܪܡܝܐ ܚܕ ܗܘ ܓܝܪ ܡܪܝܐ ܕܟܠܗܘܢ ܕܥܬܝܪ ܒܟܠ ܕܩܪܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 13, 'ܟܠ ܓܝܪ ܕܢܩܪܐ ܫܡܗ ܕܡܪܝܐ ܢܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 14, 'ܐܝܟܢܐ ܗܟܝܠ ܢܩܪܘܢ ܠܐܝܢܐ ܕܠܐ ܗܝܡܢܘ ܒܗ ܐܘ ܐܝܟܢܐ ܢܗܝܡܢܘܢ ܠܗܘ ܕܠܐ ܫܡܥܘܗܝ ܐܘ ܐܝܟܢܐ ܢܫܡܥܘܢ ܕܠܐ ܡܟܪܙܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 15, 'ܐܘ ܐܝܟܢܐ ܢܟܪܙܘܢ ܐܢ ܠܐ ܢܫܬܠܚܘܢ ܐܝܟ ܕܟܬܝܒ ܕܡܐ ܝܐܝܢ ܪܓܠܝܗܘܢ ܕܡܤܒܪܝ ܫܠܡܐ ܘܕܡܤܒܪܝ ܛܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 16, 'ܐܠܐ ܠܐ ܗܘܐ ܟܠܗܘܢ ܐܫܬܡܥܘ ܠܤܒܪܬܐ ܕܐܘܢܓܠܝܘܢ ܐܫܥܝܐ ܓܝܪ ܐܡܪ ܡܪܝ ܡܢܘ ܗܝܡܢ ܠܒܪܬ ܩܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 17, 'ܡܟܝܠ ܗܝܡܢܘܬܐ ܡܢ ܡܫܡܥ ܐܕܢܐ ܗܝ ܘܡܫܡܥ ܐܕܢܐ ܡܢ ܡܠܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 18, 'ܐܠܐ ܐܡܪ ܐܢܐ ܕܠܡܐ ܠܐ ܫܡܥܘ ܘܗܐ ܒܟܠܗ ܐܪܥܐ ܢܦܩܬ ܒܪܬ ܩܠܗܘܢ ܘܒܤܘܦܝܗ ܕܬܒܝܠ ܡܠܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 19, 'ܐܠܐ ܐܡܪ ܐܢܐ ܕܠܡܐ ܠܐ ܝܕܥ ܐܝܤܪܝܠ ܩܕܡܝܐ ܡܘܫܐ ܗܟܢܐ ܐܡܪ ܐܛܢܟܘܢ ܒܥܡ ܕܠܐ ܥܡ ܘܒܥܡܐ ܕܠܐ ܡܬܛܦܝܤ ܐܪܓܙܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 20, 'ܐܫܥܝܐ ܕܝܢ ܐܡܪܚ ܘܐܡܪ ܕܐܬܚܙܝܬ ܠܐܝܠܝܢ ܕܠܐ ܒܥܐܘܢܝ ܘܐܫܬܟܚܬ ܠܐܝܠܝܢ ܕܥܠܝ ܠܐ ܫܐܠܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 21, 'ܠܐܝܤܪܝܠ ܕܝܢ ܐܡܪ ܕܦܫܛܬ ܐܝܕܝ ܝܘܡܐ ܟܠܗ ܠܘܬ ܥܡܐ ܕܡܬܚܪܐ ܘܠܐ ܡܬܛܦܝܤ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 1, 'ܐܠܐ ܐܡܪ ܐܢܐ ܕܠܡܐ ܕܚܩܗ ܐܠܗܐ ܠܥܡܗ ܚܤ ܐܦ ܐܢܐ ܓܝܪ ܡܢ ܐܝܤܪܝܠ ܐܢܐ ܡܢ ܙܪܥܗ ܕܐܒܪܗܡ ܡܢ ܫܪܒܬܐ ܕܒܢܝܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 2, 'ܠܐ ܕܚܩ ܐܠܗܐ ܠܥܡܗ ܐܝܢܐ ܕܡܢ ܩܕܝܡ ܝܕܝܥ ܗܘܐ ܠܗ ܐܘ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܒܟܬܒܐ ܕܐܠܝܐ ܡܢܐ ܐܡܪ ܟܕ ܩܒܠ ܗܘܐ ܠܐܠܗܐ ܥܠ ܐܝܤܪܝܠ ܘܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 3, 'ܡܪܝ ܠܢܒܝܝܟ ܩܛܠܘ ܘܠܡܕܒܚܝܟ ܤܚܦܘ ܘܐܢܐ ܗܘ ܒܠܚܘܕܝ ܐܫܬܚܪܬ ܘܒܥܝܢ ܠܢܦܫܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 4, 'ܘܐܬܐܡܪ ܠܗ ܒܓܠܝܢܐ ܕܗܐ ܫܒܩܬ ܠܢܦܫܝ ܫܒܥܐ ܐܠܦܝܢ ܓܒܪܝܢ ܐܝܠܝܢ ܕܥܠ ܒܘܪܟܝܗܘܢ ܠܐ ܒܪܟܘ ܘܠܐ ܤܓܕܘ ܠܒܥܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 5, 'ܗܟܢܐ ܐܦ ܒܗܢܐ ܙܒܢܐ ܫܪܟܢܐ ܗܘ ܐܫܬܚܪ ܒܓܒܝܬܐ ܕܛܝܒܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 6, 'ܐܢ ܕܝܢ ܒܛܝܒܘܬܐ ܠܐ ܗܘܐ ܡܢ ܥܒܕܐ ܘܐܢ ܠܐ ܛܝܒܘܬܐ ܠܐ ܐܝܬܝܗ ܛܝܒܘܬܐ ܐܢ ܕܝܢ ܒܥܒܕܐ ܠܐ ܗܘܐ ܡܢ ܛܝܒܘܬܐ ܘܐܢ ܠܐ ܥܒܕܐ ܠܐ ܐܝܬܘܗܝ ܥܒܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 7, 'ܡܢܐ ܗܟܝܠ ܗܝ ܕܒܥܐ ܗܘܐ ܐܝܤܪܝܠ ܠܐ ܐܫܟܚ ܓܒܝܬܐ ܕܝܢ ܐܫܟܚܬ ܫܪܟܗܘܢ ܕܝܢ ܐܬܥܘܪܘ ܒܠܒܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 8, 'ܐܝܟܢܐ ܕܟܬܝܒ ܕܝܗܒ ܠܗܘܢ ܐܠܗܐ ܪܘܚܐ ܡܕܥܪܢܝܬܐ ܘܥܝܢܐ ܕܠܐ ܢܒܚܪܘܢ ܒܗܝܢ ܘܐܕܢܐ ܕܠܐ ܢܫܡܥܘܢ ܥܕܡܐ ܠܝܘܡܐ ܕܝܘܡܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 9, 'ܘܕܘܝܕ ܬܘܒ ܐܡܪ ܢܗܘܐ ܦܬܘܪܗܘܢ ܩܕܡܝܗܘܢ ܠܦܚܐ ܘܦܘܪܥܢܗܘܢ ܠܬܘܩܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 10, 'ܢܚܫܟܢ ܥܝܢܝܗܘܢ ܕܠܐ ܢܚܙܘܢ ܘܚܨܗܘܢ ܒܟܠܙܒܢ ܢܗܘܐ ܟܦܝܦ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 11, 'ܐܡܪ ܐܢܐ ܕܝܢ ܕܠܡܐ ܐܬܬܩܠܘ ܐܝܟ ܕܢܦܠܘܢ ܚܤ ܐܠܐ ܒܬܘܩܠܬܗܘܢ ܕܝܠܗܘܢ ܗܘܘ ܚܝܐ ܠܥܡܡܐ ܠܛܢܢܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 12, 'ܘܐܢ ܬܘܩܠܬܗܘܢ ܗܘܬ ܥܘܬܪܐ ܠܥܠܡܐ ܘܚܝܒܘܬܗܘܢ ܥܘܬܪܐ ܠܥܡܡܐ ܟܡܐ ܗܟܝܠ ܫܘܡܠܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 13, 'ܠܟܘܢ ܕܝܢ ܐܡܪ ܐܢܐ ܠܥܡܡܐ ܐܢܐ ܕܐܝܬܝ ܫܠܝܚܐ ܕܥܡܡܐ ܠܬܫܡܫܬܝ ܡܫܒܚ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 14, 'ܕܠܡܐ ܐܛܢ ܠܒܢܝ ܒܤܪܝ ܘܐܚܐ ܐܢܫܝܢ ܡܢܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 15, 'ܐܢ ܓܝܪ ܡܤܬܠܝܢܘܬܗܘܢ ܬܪܥܘܬܐ ܠܥܠܡܐ ܗܘܬ ܟܡܐ ܗܟܝܠ ܦܘܢܝܗܘܢ ܐܠܐ ܚܝܐ ܕܡܢ ܒܝܬ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 16, 'ܘܐܢ ܕܝܢ ܪܫܝܬܐ ܩܕܝܫܐ ܐܦ ܓܒܝܠܬܐ ܘܐܢ ܥܩܪܐ ܩܕܝܫ ܗܘ ܐܦ ܤܘܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 17, 'ܘܐܢ ܡܢ ܤܘܟܐ ܐܬܦܫܚ ܘܐܢܬ ܕܙܝܬܐ ܐܢܬ ܕܒܪܐ ܐܬܛܥܡܬ ܒܕܘܟܝܬܗܝܢ ܘܗܘܝܬ ܫܘܬܦܐ ܠܥܩܪܗ ܘܠܫܘܡܢܗ ܕܙܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 18, 'ܠܐ ܬܫܬܒܗܪ ܥܠ ܤܘܟܐ ܐܢ ܕܝܢ ܡܫܬܒܗܪ ܐܢܬ ܠܐ ܗܘܐ ܐܢܬ ܫܩܝܠ ܠܗ ܠܥܩܪܐ ܐܠܐ ܗܘ ܥܩܪܐ ܫܩܝܠ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 19, 'ܘܟܒܪ ܬܐܡܪ ܕܤܘܟܐ ܕܐܬܦܫܚ ܕܐܢܐ ܒܕܘܟܝܬܗܝܢ ܐܬܛܥܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 20, 'ܫܦܝܪ ܗܠܝܢ ܡܛܠ ܕܠܐ ܗܝܡܢ ܐܬܦܫܚ ܐܢܬ ܕܝܢ ܒܗܝܡܢܘܬܐ ܩܡܬ ܠܐ ܬܬܪܝܡ ܒܪܥܝܢܟ ܐܠܐ ܕܚܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 21, 'ܐܢ ܐܠܗܐ ܓܝܪ ܥܠ ܤܘܟܐ ܕܡܢ ܟܝܢܗܝܢ ܠܐ ܚܤ ܕܠܡܐ ܐܦܠܐ ܥܠܝܟ ܢܚܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 22, 'ܚܙܝ ܗܟܝܠ ܒܤܝܡܘܬܗ ܘܩܫܝܘܬܗ ܕܐܠܗܐ ܥܠ ܐܝܠܝܢ ܕܢܦܠܘ ܩܫܝܘܬܐ ܥܠܝܟ ܕܝܢ ܒܤܝܡܘܬܐ ܐܢ ܬܩܘܐ ܒܗ ܒܒܤܝܡܘܬܐ ܘܐܢ ܠܐ ܐܦ ܐܢܬ ܬܬܦܫܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 23, 'ܘܗܢܘܢ ܐܢ ܠܐ ܢܩܘܘܢ ܒܚܤܝܪܘܬ ܗܝܡܢܘܬܗܘܢ ܐܦ ܗܢܘܢ ܢܬܛܥܡܘܢ ܡܫܟܚ ܓܝܪ ܐܠܗܐ ܕܬܘܒ ܢܛܥܡ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 24, 'ܐܢ ܓܝܪ ܐܢܬ ܕܡܢ ܙܝܬܐ ܐܢܬ ܕܒܪܐ ܗܘ ܕܒܟܝܢܟ ܐܬܦܫܚܬ ܘܕܠܐ ܒܟܝܢܟ ܐܬܛܥܡܬ ܒܙܝܬܐ ܛܒܐ ܟܡܐ ܗܟܝܠ ܗܢܘܢ ܐܢ ܢܬܛܥܡܘܢ ܒܙܝܬܐ ܕܟܝܢܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 25, 'ܨܒܐ ܐܢܐ ܓܝܪ ܕܬܕܥܘܢ ܐܚܝ ܐܪܙܐ ܗܢܐ ܕܠܐ ܬܗܘܘܢ ܚܟܝܡܝܢ ܒܪܥܝܢ ܢܦܫܟܘܢ ܕܥܘܝܪܘܬ ܠܒܐ ܡܢ ܐܬܪ ܩܠܝܠ ܗܘܬ ܠܐܝܤܪܝܠ ܥܕܡܐ ܕܢܥܘܠ ܡܘܠܝܐ ܕܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 26, 'ܘܗܝܕܝܢ ܟܠܗ ܐܝܤܪܝܠ ܢܚܐ ܐܝܟܢܐ ܕܟܬܝܒ ܕܢܐܬܐ ܡܢ ܨܗܝܘܢ ܦܪܘܩܐ ܘܢܗܦܟ ܥܘܠܐ ܡܢ ܝܥܩܘܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 27, 'ܘܗܝܕܝܢ ܬܗܘܐ ܠܗܘܢ ܕܝܬܩܐ ܗܝ ܕܡܢ ܠܘܬܝ ܡܐ ܕܫܒܩܬ ܠܗܘܢ ܚܛܗܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 28, 'ܒܐܘܢܓܠܝܘܢ ܕܝܢ ܒܥܠܕܒܒܐ ܐܢܘܢ ܡܛܠܬܟܘܢ ܘܒܓܒܝܘܬܐ ܚܒܝܒܝܢ ܐܢܘܢ ܡܛܠ ܐܒܗܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 29, 'ܠܐ ܓܝܪ ܗܦܟܐ ܐܠܗܐ ܒܡܘܗܒܬܗ ܘܒܩܪܝܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 30, 'ܐܝܟܢܐ ܓܝܪ ܕܐܦ ܐܢܬܘܢ ܠܐ ܡܬܛܦܝܤܝܢ ܗܘܝܬܘܢ ܠܐܠܗܐ ܡܢ ܩܕܝܡ ܘܗܫܐ ܐܬܚܢܢܬܘܢ ܡܛܠ ܠܐ ܡܬܛܦܝܤܢܘܬܗܘܢ ܕܗܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 31, 'ܗܟܢܐ ܐܦ ܗܠܝܢ ܠܐ ܐܬܛܦܝܤܘ ܗܫܐ ܠܪܚܡܐ ܕܥܠܝܟܘܢ ܕܐܦ ܥܠܝܗܘܢ ܢܗܘܘܢ ܪܚܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 32, 'ܚܒܫ ܓܝܪ ܐܠܗܐ ܠܟܠܢܫ ܒܠܐ ܡܬܛܦܝܤܢܘܬܐ ܕܥܠ ܟܠ ܐܢܫ ܢܪܚܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 33, 'ܐܘ ܥܘܡܩܐ ܕܥܘܬܪܐ ܘܚܟܡܬܐ ܘܡܕܥܐ ܕܐܠܗܐ ܕܐܢܫ ܠܐ ܡܫ ܕܝܢܘܗܝ ܘܐܘܪܚܬܗ ܠܐ ܡܬܥܩܒܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 34, 'ܡܢܘ ܓܝܪ ܝܕܥ ܪܥܝܢܗ ܕܡܪܝܐ ܐܘ ܡܢܘ ܗܘܐ ܠܗ ܒܥܠ ܡܠܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 35, 'ܘܡܢܘ ܩܕܡ ܝܗܒ ܠܗ ܘܟܢ ܢܤܒ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 36, 'ܡܛܠ ܕܟܠ ܡܢܗ ܘܟܠ ܒܗ ܘܟܠ ܒܐܝܕܗ ܕܠܗ ܬܫܒܚܢ ܘܒܘܪܟܢ ܠܥܠܡ ܥܠܡܝܢ ܐܡܝܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 1, 'ܒܥܢܐ ܗܟܝܠ ܡܢܟܘܢ ܐܚܝ ܒܪܚܡܘܗܝ ܕܐܠܗܐ ܕܬܩܝܡܘܢ ܦܓܪܝܟܘܢ ܕܒܚܬܐ ܚܝܬܐ ܘܩܕܝܫܬܐ ܘܡܩܒܠܬܐ ܠܐܠܗܐ ܒܬܫܡܫܬܐ ܡܠܝܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 2, 'ܘܠܐ ܬܬܕܡܘܢ ܠܥܠܡܐ ܗܢܐ ܐܠܐ ܐܫܬܚܠܦܘ ܒܚܘܕܬܐ ܕܪܥܝܢܝܟܘܢ ܘܗܘܝܬܘܢ ܦܪܫܝܢ ܐܝܢܐ ܗܘ ܨܒܝܢܐ ܕܐܠܗܐ ܛܒܐ ܘܡܩܒܠܐ ܘܓܡܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 3, 'ܐܡܪ ܐܢܐ ܕܝܢ ܒܛܝܒܘܬܐ ܕܐܬܝܗܒܬ ܠܝ ܠܟܠܟܘܢ ܕܠܐ ܬܗܘܘܢ ܡܬܪܥܝܢ ܠܒܪ ܡܢ ܡܐ ܕܘܠܐ ܕܬܬܪܥܘܢ ܐܠܐ ܬܗܘܘܢ ܡܬܪܥܝܢ ܒܢܟܦܘܬܐ ܟܠܢܫ ܐܝܟ ܕܦܠܓ ܠܗ ܐܠܗܐ ܗܝܡܢܘܬܐ ܒܡܫܘܚܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 4, 'ܐܝܟܢܐ ܓܝܪ ܕܒܚܕ ܦܓܪܐ ܗܕܡܐ ܤܓܝܐܐ ܐܝܬ ܠܢ ܘܟܠܗܘܢ ܗܕܡܐ ܠܐ ܗܘܐ ܚܕ ܤܘܥܪܢܐ ܐܝܬ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 5, 'ܗܟܢܐ ܐܦ ܚܢܢ ܕܤܓܝܐܐ ܚܢܢ ܚܕ ܚܢܢ ܦܓܪ ܒܡܫܝܚܐ ܚܕ ܚܕ ܡܢܢ ܕܝܢ ܗܕܡܐ ܚܢܢ ܕܚܕܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 6, 'ܐܠܐ ܐܝܬ ܠܢ ܡܘܗܒܬܐ ܡܫܚܠܦܬܐ ܐܝܟ ܛܝܒܘܬܐ ܕܐܬܝܗܒܬ ܠܢ ܐܝܬ ܕܢܒܝܘܬܐ ܐܝܟ ܡܫܘܚܬܐ ܕܗܝܡܢܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 7, 'ܘܐܝܬ ܕܬܫܡܫܬܐ ܐܝܬ ܠܗ ܒܬܫܡܫܬܗ ܘܐܝܬ ܕܡܠܦܢܐ ܗܘ ܒܝܘܠܦܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 8, 'ܘܐܝܬ ܕܡܒܝܐܢܐ ܗܘ ܒܒܘܝܐܗ ܘܕܝܗܒ ܒܦܫܝܛܘܬܐ ܘܕܩܐܡ ܒܪܫܐ ܒܚܦܝܛܘܬܐ ܘܕܡܪܚܡ ܒܦܨܝܚܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 9, 'ܘܠܐ ܢܗܘܐ ܢܟܝܠ ܚܘܒܟܘܢ ܐܠܐ ܗܘܝܬܘܢ ܤܢܝܢ ܠܒܝܫܬܐ ܘܡܬܢܩܦܝܢ ܠܛܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 10, 'ܗܘܝܬܘܢ ܪܚܡܝܢ ܠܐܚܝܟܘܢ ܘܡܚܒܝܢ ܚܕ ܠܚܕ ܗܘܝܬܘܢ ܡܩܕܡܝܢ ܡܝܩܪܝܢ ܚܕ ܠܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 11, 'ܗܘܝܬܘܢ ܚܦܝܛܝܢ ܘܠܐ ܚܒܢܢܝܢ ܗܘܝܬܘܢ ܪܬܚܝܢ ܒܪܘܚ ܗܘܝܬܘܢ ܦܠܚܝܢ ܠܡܪܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 12, 'ܗܘܝܬܘܢ ܚܕܝܢ ܒܤܒܪܟܘܢ ܗܘܝܬܘܢ ܡܤܝܒܪܝܢ ܐܘܠܨܢܝܟܘܢ ܗܘܝܬܘܢ ܐܡܝܢܝܢ ܒܨܠܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 13, 'ܗܘܝܬܘܢ ܡܫܬܘܬܦܝܢ ܠܤܢܝܩܘܬܐ ܕܩܕܝܫܐ ܗܘܝܬܘܢ ܪܚܡܝܢ ܐܟܤܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 14, 'ܒܪܟܘ ܠܪܕܘܦܝܟܘܢ ܒܪܟܘ ܘܠܐ ܬܠܘܛܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 15, 'ܚܕܘ ܥܡ ܕܚܕܝܢ ܘܒܟܘ ܥܡ ܕܒܟܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 16, 'ܘܡܕܡ ܕܡܬܪܥܝܢ ܐܢܬܘܢ ܥܠ ܢܦܫܟܘܢ ܐܦ ܥܠ ܐܚܝܟܘܢ ܘܠܐ ܬܬܪܥܘܢ ܪܥܝܢܐ ܪܡܐ ܐܠܐ ܩܦܘ ܠܐܝܠܝܢ ܕܡܟܝܟܝܢ ܘܠܐ ܗܘܝܬܘܢ ܚܟܝܡܝܢ ܒܪܥܝܢ ܢܦܫܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 17, 'ܘܠܐ ܬܦܪܥܘܢ ܠܐܢܫ ܒܝܫܬܐ ܚܠܦ ܒܝܫܬܐ ܐܠܐ ܢܬܒܛܠ ܠܟܘܢ ܕܬܥܒܕܘܢ ܛܒܬܐ ܩܕܡ ܒܢܝܢܫܐ ܟܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 18, 'ܘܐܢ ܡܫܟܚܐ ܐܝܟ ܕܡܢ ܠܘܬܟܘܢ ܥܡ ܟܠܒܪܢܫ ܫܠܡܐ ܥܒܕܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 19, 'ܘܠܐ ܗܘܝܬܘܢ ܬܒܥܝܢ ܢܦܫܟܘܢ ܚܒܝܒܝ ܐܠܐ ܗܒܘ ܐܬܪܐ ܠܪܘܓܙܐ ܟܬܝܒ ܗܘ ܓܝܪ ܕܐܢ ܠܐ ܬܥܒܕ ܕܝܢܐ ܠܢܦܫܟ ܐܢܐ ܐܥܒܕ ܕܝܢܟ ܐܡܪ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 20, 'ܘܐܢ ܟܦܢ ܒܥܠܕܒܒܟ ܐܘܟܠܝܗܝ ܘܐܢ ܨܗܐ ܐܫܩܝܗܝ ܘܐܢ ܗܠܝܢ ܬܥܒܕ ܠܗ ܓܘܡܪܐ ܕܢܘܪܐ ܬܩܒܪ ܥܠ ܩܪܩܦܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 21, 'ܠܐ ܬܙܟܝܟܘܢ ܒܝܫܬܐ ܐܠܐ ܙܟܐܘܗ ܠܒܝܫܬܐ ܒܛܒܬܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 1, 'ܟܠ ܢܦܫ ܠܫܘܠܛܢܐ ܕܪܒܘܬܐ ܬܫܬܥܒܕ ܠܝܬ ܓܝܪ ܫܘܠܛܢܐ ܕܠܐ ܗܘܐ ܡܢ ܐܠܗܐ ܗܘ ܘܐܝܠܝܢ ܫܘܠܛܢܐ ܕܐܝܬܝܗܘܢ ܡܢ ܐܠܗܐ ܗܘ ܦܩܝܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 2, 'ܡܢ ܕܩܐܡ ܗܟܝܠ ܠܘܩܒܠ ܫܘܠܛܢܐ ܠܘܩܒܠ ܦܘܩܕܢܐ ܕܐܠܗܐ ܩܐܡ ܘܗܠܝܢ ܕܩܝܡܝܢ ܠܘܩܒܠܗܘܢ ܕܝܢܐ ܢܤܒܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 3, 'ܕܝܢܐ ܓܝܪ ܠܐ ܗܘܘ ܕܚܠܬܐ ܠܥܒܕܐ ܛܒܐ ܐܠܐ ܠܒܝܫܐ ܨܒܐ ܐܢܬ ܗܟܝܠ ܕܠܐ ܬܕܚܠ ܡܢ ܫܘܠܛܢܐ ܥܒܕ ܛܒܬܐ ܘܬܫܒܘܚܬܐ ܬܗܘܐ ܠܟ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 4, 'ܡܫܡܫܢܐ ܗܘ ܓܝܪ ܕܐܠܗܐ ܐܠܐ ܠܟ ܠܛܒܬܐ ܘܐܢ ܒܝܫܬܐ ܥܒܕ ܐܢܬ ܕܚܠ ܠܐ ܗܘܐ ܓܝܪ ܤܪܝܩܐܝܬ ܐܤܝܪ ܠܤܦܤܪܐ ܡܫܡܫܢܐ ܗܘ ܓܝܪ ܕܐܠܗܐ ܘܬܒܘܥܐ ܕܪܘܓܙܐ ܠܐܝܠܝܢ ܕܥܒܕܝܢ ܒܝܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 5, 'ܘܡܛܠ ܗܢܐ ܐܠܨܐ ܠܢ ܕܢܫܬܥܒܕ ܠܐ ܡܛܠ ܪܘܓܙܐ ܒܠܚܘܕ ܐܠܐ ܐܦ ܡܛܠ ܬܐܪܬܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 6, 'ܡܛܠ ܗܢܐ ܐܦ ܟܤܦ ܪܫܐ ܝܗܒܝܢ ܐܢܬܘܢ ܡܫܡܫܢܐ ܐܢܘܢ ܓܝܪ ܕܐܠܗܐ ܕܥܠܝܗܝܢ ܕܗܠܝܢ ܩܝܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 7, 'ܦܪܘܥܘ ܗܟܝܠ ܠܟܠܢܫ ܐܝܟ ܕܡܬܚܝܒ ܠܗ ܠܡܢ ܕܟܤܦ ܪܫܐ ܟܤܦ ܪܫܐ ܘܠܡܢ ܕܡܟܤܐ ܡܟܤܐ ܘܠܡܢ ܕܕܚܠܬܐ ܕܚܠܬܐ ܘܠܡܢ ܕܐܝܩܪܐ ܐܝܩܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 8, 'ܘܠܐܢܫ ܡܕܡ ܠܐ ܬܚܘܒܘܢ ܐܠܐ ܚܕ ܠܚܕ ܠܡܚܒܘ ܡܢ ܕܡܚܒ ܓܝܪ ܚܒܪܗ ܢܡܘܤܐ ܡܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 9, 'ܘܐܦ ܗܝ ܓܝܪ ܕܐܡܪ ܕܠܐ ܬܓܘܪ ܘܠܐ ܬܩܛܘܠ ܘܠܐ ܬܓܢܘܒ ܘܠܐ ܬܪܓ ܘܐܢ ܐܝܬ ܦܘܩܕܢܐ ܐܚܪܢܐ ܒܗܕܐ ܡܠܬܐ ܡܫܬܠܡ ܕܬܪܚܡ ܠܩܪܝܒܟ ܐܝܟ ܢܦܫܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 10, 'ܚܘܒܐ ܠܩܪܝܒܗ ܒܝܫܬܐ ܠܐ ܤܥܪ ܡܛܠ ܕܚܘܒܐ ܡܘܠܝܗ ܗܘ ܕܢܡܘܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 11, 'ܘܐܦ ܗܕܐ ܕܥܘ ܕܙܒܢܐ ܗܘ ܘܫܥܬܐ ܗܝ ܡܟܝܠ ܕܢܬܥܝܪ ܡܢ ܫܢܬܢ ܗܫܐ ܗܘ ܓܝܪ ܐܬܩܪܒܘ ܠܢ ܚܝܝܢ ܝܬܝܪ ܡܢ ܕܟܕ ܗܝܡܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 12, 'ܠܠܝܐ ܡܟܝܠ ܥܒܪ ܘܐܝܡܡܐ ܩܪܒ ܢܢܝܚ ܡܢܢ ܗܟܝܠ ܥܒܕܐ ܕܚܫܘܟܐ ܘܢܠܒܫ ܙܝܢܗ ܕܢܘܗܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 13, 'ܘܐܝܟ ܕܒܐܝܡܡܐ ܒܐܤܟܡܐ ܢܗܠܟ ܠܐ ܒܙܡܪܐ ܘܠܐ ܒܪܘܝܘܬܐ ܘܠܐ ܒܡܕܡܟܐ ܛܢܦܐ ܘܠܐ ܒܚܤܡܐ ܘܒܚܪܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 14, 'ܐܠܐ ܠܘܒܫܘܗܝ ܠܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܘܠܐ ܬܐܨܦܘܢ ܕܒܤܪܟܘܢ ܠܪܓܝܓܬܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 1, 'ܠܐܝܢܐ ܕܝܢ ܕܟܪܝܗ ܒܗܝܡܢܘܬܐ ܗܒܘ ܠܗ ܐܝܕܐ ܘܠܐ ܬܗܘܘܢ ܡܬܦܠܓܝܢ ܒܡܚܫܒܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 2, 'ܐܝܬ ܓܝܪ ܕܡܗܝܡܢ ܕܟܠܡܕܡ ܢܐܟܘܠ ܘܕܟܪܝܗ ܝܪܩܐ ܗܘ ܐܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 3, 'ܗܘ ܕܝܢ ܕܐܟܠ ܠܗܘ ܡܢ ܕܠܐ ܐܟܠ ܠܐ ܢܫܘܛ ܘܗܘ ܡܢ ܕܠܐ ܐܟܠ ܠܗܘ ܡܢ ܕܐܟܠ ܠܐ ܢܕܘܢ ܐܠܗܐ ܓܝܪ ܩܪܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 4, 'ܐܢܬ ܡܢ ܐܢܬ ܕܕܐܢ ܐܢܬ ܠܥܒܕܐ ܕܠܐ ܕܝܠܟ ܕܐܢ ܩܐܡ ܠܡܪܗ ܩܐܡ ܘܐܢ ܢܦܠ ܠܡܪܗ ܢܦܠ ܡܩܡ ܗܘ ܕܝܢ ܩܐܡ ܡܛܐ ܓܝܪ ܒܐܝܕܝ ܡܪܗ ܕܢܩܝܡܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 5, 'ܐܝܬ ܕܕܐܢ ܝܘܡܐ ܡܢ ܝܘܡܐ ܘܐܝܬ ܕܕܐܢ ܟܠܗܘܢ ܝܘܡܬܐ ܟܠܢܫ ܕܝܢ ܒܡܕܥܐ ܕܢܦܫܗ ܢܫܬܪܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 6, 'ܡܢ ܕܡܬܪܥܐ ܕܝܘܡܐ ܠܡܪܗ ܡܬܪܥܐ ܘܟܠ ܕܠܐ ܡܬܪܥܐ ܕܝܘܡܐ ܠܡܪܗ ܠܐ ܡܬܪܥܐ ܘܕܐܟܠ ܠܡܪܗ ܐܟܠ ܘܠܐܠܗܐ ܡܘܕܐ ܘܕܠܐ ܐܟܠ ܠܡܪܗ ܠܐ ܐܟܠ ܘܡܘܕܐ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 7, 'ܠܝܬ ܓܝܪ ܐܢܫ ܡܢܢ ܕܠܢܦܫܗ ܚܝ ܘܠܝܬ ܐܢܫ ܕܠܢܦܫܗ ܡܐܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 8, 'ܡܛܠ ܕܐܢ ܚܐܝܢܢ ܠܡܪܢ ܚܐܝܢܢ ܘܐܢ ܡܝܬܝܢܢ ܠܡܪܢ ܗܘ ܡܝܬܝܢܢ ܘܐܢ ܚܝܝܢܢ ܗܟܝܠ ܘܐܢ ܡܝܬܝܢܢ ܕܡܪܢ ܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 9, 'ܡܛܠ ܗܢܐ ܐܦ ܡܫܝܚܐ ܡܝܬ ܘܚܝܐ ܘܩܡ ܕܗܘ ܢܗܘܐ ܡܪܝܐ ܠܡܝܬܐ ܘܠܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 10, 'ܐܢܬ ܕܝܢ ܡܢܐ ܕܐܢ ܐܢܬ ܠܐܚܘܟ ܐܘ ܐܦ ܐܢܬ ܠܡܢܐ ܫܐܛ ܐܢܬ ܠܐܚܘܟ ܟܠܢ ܓܝܪ ܥܬܝܕܝܢܢ ܠܡܩܡ ܩܕܡ ܒܝܡ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 11, 'ܐܝܟ ܕܟܬܝܒ ܕܚܝ ܐܢܐ ܐܡܪ ܡܪܝܐ ܕܠܝ ܬܟܘܦ ܟܠ ܒܪܘܟ ܘܠܝ ܢܘܕܐ ܟܠ ܠܫܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 12, 'ܡܕܝܢ ܟܠ ܐܢܫ ܡܢܢ ܦܬܓܡܐ ܚܠܦ ܢܦܫܗ ܝܗܒ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 13, 'ܠܐ ܡܟܝܠ ܢܕܘܢ ܚܕ ܠܚܕ ܐܠܐ ܗܕܐ ܕܘܢܘ ܝܬܝܪܐܝܬ ܕܬܘܩܠܬܐ ܠܐܚܘܟ ܠܐ ܬܤܝܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 14, 'ܝܕܥ ܐܢܐ ܓܝܪ ܘܡܦܤ ܐܢܐ ܒܡܪܝܐ ܝܫܘܥ ܕܡܕܡ ܕܡܤܝܒ ܡܢ ܠܘܬܗ ܠܝܬ ܐܠܐ ܠܐܝܢܐ ܕܪܢܐ ܥܠ ܡܕܡ ܕܛܡܐ ܠܗܘ ܗܘ ܒܠܚܘܕ ܛܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 15, 'ܐܢ ܕܝܢ ܡܛܠ ܡܐܟܘܠܬܐ ܡܥܝܩ ܐܢܬ ܠܐܚܘܟ ܠܐ ܗܘܐ ܒܚܘܒܐ ܡܗܠܟ ܐܢܬ ܠܐ ܬܘܒܕ ܒܡܐܟܘܠܬܟ ܠܗܘ ܕܡܛܠܬܗ ܡܝܬ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 16, 'ܘܠܐ ܬܬܓܕܦ ܛܒܬܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 17, 'ܡܠܟܘܬܗ ܓܝܪ ܕܐܠܗܐ ܠܐ ܗܘܬ ܡܐܟܠܐ ܘܡܫܬܝܐ ܐܠܐ ܟܐܢܘܬܐ ܘܫܠܡܐ ܘܚܕܘܬܐ ܒܪܘܚܐ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 18, 'ܡܢ ܕܒܗܠܝܢ ܓܝܪ ܡܫܡܫ ܠܡܫܝܚܐ ܫܦܪ ܠܐܠܗܐ ܘܩܕܡ ܒܢܝܢܫܐ ܒܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 19, 'ܗܫܐ ܒܬܪ ܫܠܡܐ ܢܪܗܛ ܘܒܬܪ ܒܢܝܢܐ ܚܕ ܕܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 20, 'ܘܠܐ ܡܛܠ ܡܐܟܘܠܬܐ ܢܫܪܐ ܥܒܕܐ ܕܐܠܗܐ ܟܠܡܕܡ ܓܝܪ ܕܟܐ ܗܘ ܐܠܐ ܒܝܫ ܗܘ ܠܒܪܢܫܐ ܕܒܬܘܩܠܬܐ ܐܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 21, 'ܫܦܝܪ ܗܘ ܕܠܐ ܢܐܟܘܠ ܒܤܪܐ ܘܠܐ ܢܫܬܐ ܚܡܪܐ ܘܠܐ ܡܕܡ ܕܡܬܬܩܠ ܒܗ ܐܚܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 22, 'ܐܢܬ ܕܐܝܬ ܒܟ ܗܝܡܢܘܬܐ ܒܢܦܫܟ ܐܚܘܕܝܗ ܩܕܡ ܐܠܗܐ ܛܘܒܘܗܝ ܠܡܢ ܕܠܐ ܕܢ ܢܦܫܗ ܒܡܕܡ ܕܦܪܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 23, 'ܐܝܢܐ ܓܝܪ ܕܡܬܦܠܓ ܘܐܟܠ ܐܬܚܝܒ ܠܗ ܡܛܠ ܕܠܘ ܒܗܝܡܢܘܬܐ ܟܠ ܡܕܡ ܓܝܪ ܕܠܐ ܗܘܐ ܡܢ ܗܝܡܢܘܬܐ ܚܛܝܬܐ ܗܘ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 1, 'ܚܝܒܝܢܢ ܗܟܝܠ ܚܢܢ ܚܝܠܬܢܐ ܕܟܘܪܗܢܐ ܕܡܚܝܠܐ ܢܫܩܘܠ ܘܠܐ ܠܢܦܫܢ ܢܫܦܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 2, 'ܐܠܐ ܐܢܫ ܡܢܢ ܠܩܪܝܒܗ ܢܫܦܪ ܒܛܒܬܐ ܐܝܟ ܕܠܒܢܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 3, 'ܡܛܠ ܕܐܦ ܡܫܝܚܐ ܠܐ ܗܘܐ ܠܢܦܫܗ ܫܦܪ ܐܠܐ ܐܝܟ ܕܟܬܝܒ ܕܚܤܕܐ ܕܡܚܤܕܢܝܟ ܢܦܠ ܥܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 4, 'ܟܠ ܡܕܡ ܓܝܪ ܕܡܢ ܩܕܝܡ ܐܬܟܬܒ ܠܝܘܠܦܢܐ ܗܘ ܕܝܠܢ ܐܬܟܬܒ ܕܒܡܤܝܒܪܢܘܬܐ ܘܒܒܘܝܐܐ ܕܟܬܒܐ ܤܒܪܐ ܢܗܘܐ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 5, 'ܐܠܗܐ ܕܝܢ ܕܡܤܝܒܪܢܘܬܐ ܘܕܒܘܝܐܐ ܢܬܠ ܠܟܘܢ ܕܫܘܝܘܬܐ ܬܬܚܫܒܘܢ ܚܕ ܥܠ ܚܕ ܒܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 6, 'ܕܒܚܕ ܪܥܝܢ ܘܒܚܕ ܦܘܡ ܬܫܒܚܘܢ ܠܐܠܗܐ ܐܒܘܗܝ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 7, 'ܡܛܠ ܗܕܐ ܗܘܘ ܡܩܪܒܝܢ ܘܛܥܢܝܢ ܠܚܕܕܐ ܐܝܟܢܐ ܕܐܦ ܡܫܝܚܐ ܩܪܒܟܘܢ ܠܬܫܒܘܚܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 8, 'ܐܡܪ ܐܢܐ ܕܝܢ ܕܝܫܘܥ ܡܫܝܚܐ ܫܡܫ ܓܙܘܪܬܐ ܚܠܦ ܫܪܪܗ ܕܐܠܗܐ ܐܝܟܢܐ ܕܢܫܪ ܡܘܠܟܢܐ ܕܐܒܗܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 9, 'ܘܥܡܡܐ ܢܫܒܚܘܢ ܠܐܠܗܐ ܚܠܦ ܪܚܡܐ ܕܗܘܘ ܥܠܝܗܘܢ ܐܝܟ ܕܟܬܝܒ ܕܐܘܕܐ ܠܟ ܒܥܡܡܐ ܘܠܫܡܟ ܐܙܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 10, 'ܘܬܘܒ ܐܡܪ ܐܬܒܤܡܘ ܥܡܡܐ ܥܡ ܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 11, 'ܘܬܘܒ ܐܡܪ ܫܒܚܘ ܠܡܪܝܐ ܟܠܟܘܢ ܥܡܡܐ ܫܒܚܝܗܝ ܟܠܗܝܢ ܐܡܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 12, 'ܘܬܘܒ ܐܫܥܝܐ ܐܡܪ ܕܢܗܘܐ ܥܩܪܐ ܠܐܝܫܝ ܘܡܢ ܕܢܩܘܡ ܢܗܘܐ ܪܫܐ ܠܥܡܡܐ ܘܥܠܘܗܝ ܢܤܒܪܘܢ ܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 13, 'ܐܠܗܐ ܕܝܢ ܕܤܒܪܐ ܢܡܠܝܟܘܢ ܟܠܗ ܚܕܘܬܐ ܘܫܠܡܐ ܒܗܝܡܢܘܬܐ ܕܬܬܝܬܪܘܢ ܒܤܒܪܗ ܒܚܝܠܐ ܕܪܘܚܐ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 14, 'ܡܦܤ ܐܢܐ ܕܝܢ ܐܦ ܐܢܐ ܥܠܝܟܘܢ ܐܚܝ ܕܐܦ ܐܢܬܘܢ ܡܠܝܢ ܐܢܬܘܢ ܗܘ ܛܒܬܐ ܘܡܫܡܠܝܬܘܢ ܒܟܠܗ ܝܕܥܬܐ ܘܡܫܟܚܝܢ ܐܢܬܘܢ ܐܦ ܠܐܚܪܢܐ ܠܡܪܬܝܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 15, 'ܩܠܝܠ ܕܝܢ ܡܪܚܐܝܬ ܟܬܒܬ ܠܟܘܢ ܐܚܝ ܐܝܟܢܐ ܕܐܥܗܕܟܘܢ ܒܛܝܒܘܬܐ ܕܐܬܝܗܒܬ ܠܝ ܡܢ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 16, 'ܕܐܗܘܐ ܡܫܡܫܢܐ ܠܝܫܘܥ ܡܫܝܚܐ ܒܥܡܡܐ ܘܐܦܠܘܚ ܠܐܘܢܓܠܝܘܢ ܕܐܠܗܐ ܕܢܗܘܐ ܩܘܪܒܢܐ ܕܥܡܡܐ ܡܩܒܠ ܘܡܩܕܫ ܒܪܘܚܐ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 17, 'ܐܝܬ ܠܝ ܗܟܝܠ ܫܘܒܗܪܐ ܒܝܫܘܥ ܡܫܝܚܐ ܠܘܬ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 18, 'ܠܐ ܓܝܪ ܡܡܪܚ ܐܢܐ ܕܐܡܪ ܡܕܡ ܕܠܐ ܤܥܪ ܒܐܝܕܝ ܡܫܝܚܐ ܠܡܫܡܥܐ ܕܥܡܡܐ ܒܡܠܬܐ ܘܒܥܒܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 19, 'ܒܚܝܠܐ ܕܐܬܘܬܐ ܘܕܬܕܡܪܬܐ ܘܒܚܝܠܐ ܕܪܘܚܗ ܕܐܠܗܐ ܐܝܟܢܐ ܕܡܢ ܐܘܪܫܠܡ ܐܬܟܪܟ ܥܕܡܐ ܠܐܠܘܪܝܩܘܢ ܘܐܡܠܐ ܤܒܪܬܗ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 20, 'ܟܕ ܡܬܚܦܛ ܐܢܐ ܐܤܒܪ ܠܐ ܟܪ ܕܐܬܩܪܝ ܫܡܗ ܕܡܫܝܚܐ ܕܠܐ ܐܒܢܐ ܥܠ ܫܬܐܤܬܐ ܢܘܟܪܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 21, 'ܐܠܐ ܐܝܟܢܐ ܕܟܬܝܒ ܕܐܝܠܝܢ ܕܠܐ ܐܬܐܡܪ ܠܗܘܢ ܥܠܘܗܝ ܢܚܙܘܢܗ ܘܐܝܠܝܢ ܕܠܐ ܫܡܥܘ ܢܬܛܦܝܤܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 22, 'ܡܛܠ ܗܢܐ ܐܬܬܟܤܬ ܙܒܢܝܢ ܤܓܝܐܢ ܕܐܬܐ ܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 23, 'ܗܫܐ ܕܝܢ ܡܛܠ ܕܕܘܟܐ ܠܝܬ ܠܝ ܒܗܠܝܢ ܐܬܪܘܬܐ ܘܤܘܐ ܗܘܝܬ ܡܢ ܩܕܡ ܫܢܝܐ ܤܓܝܐܬܐ ܕܐܬܐ ܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 24, 'ܡܐ ܕܐܙܠ ܐܢܐ ܠܐܤܦܢܝܐ ܡܤܒܪ ܐܢܐ ܕܐܬܐ ܘܐܚܙܝܟܘܢ ܘܐܢܬܘܢ ܬܠܘܘܢܢܝ ܠܬܡܢ ܡܐ ܕܩܠܝܠ ܡܢ ܤܓܝ ܐܬܒܤܡܬ ܒܚܙܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 25, 'ܗܫܐ ܕܝܢ ܐܙܠ ܐܢܐ ܠܐܘܪܫܠܡ ܕܐܫܡܫ ܠܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 26, 'ܨܒܘ ܓܝܪ ܗܠܝܢ ܕܒܡܩܕܘܢܝܐ ܘܒܐܟܐܝܐ ܕܫܘܬܦܘܬܐ ܬܗܘܐ ܠܗܘܢ ܥܡ ܡܤܟܢܐ ܩܕܝܫܐ ܕܐܝܬ ܒܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 27, 'ܨܒܘ ܡܛܠ ܕܐܦ ܚܝܒܝܢ ܠܗܘܢ ܐܢ ܓܝܪ ܒܕܪܘܚ ܐܫܬܘܬܦܘ ܥܡܗܘܢ ܥܡܡܐ ܚܝܒܝܢ ܐܢܘܢ ܕܐܦ ܒܕܒܤܪ ܢܫܡܫܘܢ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 28, 'ܗܕܐ ܗܟܝܠ ܡܐ ܕܓܡܪܬ ܘܚܬܡܬ ܠܗܘܢ ܐܕܫܐ ܗܢܐ ܥܒܪ ܐܢܐ ܥܠܝܟܘܢ ܠܐܤܦܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 29, 'ܝܕܥ ܐܢܐ ܕܝܢ ܕܐܡܬܝ ܕܐܬܐ ܐܢܐ ܠܘܬܟܘܢ ܒܡܘܠܝܐ ܗܘ ܕܒܘܪܟܬܐ ܕܐܘܢܓܠܝܘܢ ܕܡܫܝܚܐ ܐܬܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 30, 'ܒܥܐ ܐܢܐ ܕܝܢ ܡܢܟܘܢ ܐܚܝ ܒܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܘܒܚܘܒܐ ܕܪܘܚܐ ܕܬܥܡܠܘܢ ܥܡܝ ܒܨܠܘܬܐ ܕܚܠܦܝ ܠܘܬ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 31, 'ܕܐܬܦܨܐ ܡܢ ܐܝܠܝܢ ܕܠܐ ܡܬܛܦܝܤܝܢ ܕܒܝܗܘܕ ܘܬܫܡܫܬܐ ܕܡܘܒܠ ܐܢܐ ܠܩܕܝܫܐ ܕܒܐܘܪܫܠܡ ܬܬܩܒܠ ܫܦܝܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 32, 'ܘܐܬܐ ܠܘܬܟܘܢ ܒܚܕܘܬܐ ܒܨܒܝܢܗ ܕܐܠܗܐ ܘܐܬܬܢܝܚ ܥܡܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 33, 'ܐܠܗܐ ܕܝܢ ܕܫܠܡܐ ܢܗܘܐ ܥܡ ܟܠܟܘܢ ܐܡܝܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 1, 'ܡܓܥܠ ܐܢܐ ܠܟܘܢ ܕܝܢ ܠܦܘܒܐ ܚܬܢ ܕܐܝܬܝܗ ܡܫܡܫܢܝܬܐ ܕܥܕܬܐ ܕܩܢܟܪܐܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 2, 'ܕܬܩܒܠܘܢܗ ܒܡܪܢ ܐܝܟ ܕܙܕܩ ܠܩܕܝܫܐ ܘܒܟܠ ܨܒܘ ܕܒܥܝܐ ܡܢܟܘܢ ܬܩܘܡܘܢ ܠܗ ܡܛܠ ܕܐܦ ܗܝ ܩܝܘܡܬܐ ܗܘܬ ܠܤܓܝܐܐ ܐܦ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 3, 'ܫܐܠܘ ܒܫܠܡܐ ܕܦܪܝܤܩܠܐ ܘܕܐܩܠܤ ܦܠܚܐ ܕܥܡܝ ܒܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 4, 'ܕܗܢܘܢ ܗܠܝܢ ܚܠܦ ܢܦܫܝ ܨܘܪܝܗܘܢ ܝܗܒܘ ܘܠܐ ܗܘܐ ܐܢܐ ܒܠܚܘܕܝ ܡܘܕܐ ܐܢܐ ܠܗܘܢ ܐܠܐ ܐܦ ܟܠܗܝܢ ܥܕܬܐ ܕܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 5, 'ܘܗܒܘ ܫܠܡܐ ܠܥܕܬܐ ܕܐܝܬ ܒܒܝܬܗܘܢ ܫܐܠܘ ܒܫܠܡܐ ܕܐܦܢܛܘܤ ܚܒܝܒܝ ܐܝܢܐ ܕܐܝܬܘܗܝ ܪܫܝܬܐ ܕܐܟܐܝܐ ܒܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 6, 'ܫܐܠܘ ܒܫܠܡܐ ܕܡܪܝܐ ܐܝܕܐ ܕܤܓܝ ܠܐܝܬ ܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 7, 'ܫܐܠܘ ܒܫܠܡܐ ܕܐܢܕܪܘܢܝܩܘܤ ܘܕܝܘܢܝܐ ܐܚܝܢܝ ܕܗܘܘ ܫܒܝܐ ܥܡܝ ܘܝܕܝܥܝܢ ܐܢܘܢ ܒܫܠܝܚܐ ܘܒܡܫܝܚܐ ܩܕܡܝ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 8, 'ܫܐܠܘ ܒܫܠܡܐ ܕܐܡܦܠܝܘܤ ܚܒܝܒܝ ܒܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 9, 'ܫܐܠܘ ܒܫܠܡܐ ܕܐܘܪܒܢܘܤ ܦܠܚܐ ܕܥܡܢ ܒܡܫܝܚܐ ܘܕܐܤܛܟܘܤ ܚܒܝܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 10, 'ܫܐܠܘ ܒܫܠܡܐ ܕܐܦܠܐ ܓܒܝܐ ܒܡܪܢ ܫܐܠܘ ܒܫܠܡܐ ܕܒܢܝ ܒܝܬܗ ܕܐܪܤܛܒܘܠܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 11, 'ܫܐܠܘ ܒܫܠܡܐ ܕܗܪܘܕܝܘܢ ܐܚܝܢܝ ܫܐܠܘ ܒܫܠܡܐ ܕܒܢܝ ܒܝܬܗ ܕܢܪܩܤܘܤ ܐܝܠܝܢ ܕܐܝܬܝܗܘܢ ܒܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 12, 'ܫܐܠܘ ܒܫܠܡܐ ܕܛܪܘܦܢܐ ܘܕܛܪܘܦܤܐ ܕܠܐܝܝܢ ܒܡܪܢ ܫܐܠܘ ܒܫܠܡܐ ܕܦܪܤܤ ܚܒܝܒܬܝ ܐܝܕܐ ܕܤܓܝ ܠܐܝܬ ܒܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 13, 'ܫܐܠܘ ܒܫܠܡܐ ܕܪܘܦܤ ܓܒܝܐ ܒܡܪܢ ܘܕܐܡܗ ܕܝܠܗ ܘܕܝܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 14, 'ܫܐܠܘ ܒܫܠܡܐ ܕܐܤܘܢܩܪܛܘܤ ܘܕܦܠܓܘܢ ܘܕܗܪܡܐ ܘܕܦܛܪܒܐ ܘܕܗܪܡܐ ܘܕܐܚܐ ܕܥܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 15, 'ܫܐܠܘ ܒܫܠܡܐ ܕܦܝܠܠܓܘܤ ܘܕܝܘܠܝܐ ܘܕܢܐܪܘܤ ܘܕܚܬܗ ܘܕܐܠܘܡܦܐ ܘܕܟܠܗܘܢ ܩܕܝܫܐ ܕܥܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 16, 'ܫܐܠܘ ܒܫܠܡܐ ܚܕ ܕܚܕ ܒܢܘܫܩܬܐ ܩܕܝܫܬܐ ܫܐܠܢ ܒܫܠܡܟܘܢ ܟܠܗܝܢ ܥܕܬܗ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 17, 'ܒܥܢܐ ܕܝܢ ܡܢܟܘܢ ܐܚܝ ܕܬܙܕܗܪܘܢ ܡܢ ܐܝܠܝܢ ܕܦܠܓܘܬܐ ܘܡܟܫܘܠܐ ܥܒܕܝܢ ܠܒܪ ܡܢ ܝܘܠܦܢܐ ܕܐܢܬܘܢ ܝܠܦܬܘܢ ܕܬܬܪܚܩܘܢ ܠܟܘܢ ܡܢܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 18, 'ܐܝܠܝܢ ܓܝܪ ܕܗܟܢܐ ܐܢܘܢ ܠܐ ܡܫܡܫܝܢ ܠܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܐܠܐ ܠܟܪܤܗܘܢ ܘܒܡܠܐ ܒܤܝܡܬܐ ܘܒܒܘܪܟܬܐ ܡܛܥܝܢ ܠܒܘܬܐ ܕܦܫܝܛܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 19, 'ܡܫܬܡܥܢܘܬܟܘܢ ܕܝܢ ܕܝܠܟܘܢ ܠܟܠ ܒܪܢܫ ܐܬܝܕܥܬ ܚܕܐ ܐܢܐ ܗܟܝܠ ܒܟܘܢ ܘܨܒܐ ܐܢܐ ܕܬܗܘܘܢ ܚܟܝܡܝܢ ܠܛܒܬܐ ܘܬܡܝܡܝܢ ܠܒܝܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 20, 'ܐܠܗܐ ܕܝܢ ܕܫܠܡܐ ܢܫܚܩܝܘܗܝ ܒܥܓܠ ܠܤܛܢܐ ܬܚܝܬ ܪܓܠܝܟܘܢ ܛܝܒܘܬܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܬܗܘܐ ܥܡܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 21, 'ܫܐܠܝܢ ܒܫܠܡܟܘܢ ܛܝܡܬܐܘܤ ܦܠܚܐ ܕܥܡܝ ܘܠܘܩܝܘܤ ܘܐܝܤܘܢ ܘܤܘܤܝܦܛܪܘܤ ܐܚܝܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 22, 'ܫܐܠ ܐܢܐ ܒܫܠܡܟܘܢ ܐܢܐ ܛܪܛܝܘܤ ܕܟܬܒܬ ܐܓܪܬܐ ܒܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 23, 'ܫܐܠ ܒܫܠܡܟܘܢ ܓܐܝܘܤ ܡܩܒܠܢܝ ܘܕܟܠܗ ܥܕܬܐ ܫܐܠ ܒܫܠܡܟܘܢ ܐܪܤܛܘܤ ܪܒܝܬܐ ܕܡܕܝܢܬܐ ܘܩܘܐܪܛܘܤ ܐܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 24, 'ܠܐܠܗܐ ܕܝܢ ܗܘ ܕܡܫܟܚ ܕܢܫܪܪܟܘܢ ܒܐܘܢܓܠܝܘܢ ܕܝܠܝ ܗܘ ܕܐܬܟܪܙ ܥܠ ܝܫܘܥ ܡܫܝܚܐ ܒܓܠܝܢܐ ܕܐܪܙܐ ܕܡܢ ܙܒܢܝ ܥܠܡܐ ܡܟܤܝ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 25, 'ܐܬܓܠܝ ܕܝܢ ܒܙܒܢܐ ܗܢܐ ܒܝܕ ܟܬܒܐ ܕܢܒܝܐ ܘܒܦܘܩܕܢܗ ܕܐܠܗܐ ܕܠܥܠܡ ܐܬܝܕܥ ܠܟܠܗܘܢ ܥܡܡܐ ܠܡܫܡܥܐ ܕܗܝܡܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 26, 'ܕܗܘܝܘ ܚܟܝܡܐ ܒܠܚܘܕܘܗܝ ܫܘܒܚܐ ܒܝܕ ܝܫܘܥ ܡܫܝܚܐ ܠܥܠܡܥܠܡܝܢ ܐܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 27, 'ܛܝܒܘܬܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܥܡ ܟܠܟܘܢ ܐܡܝܢ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 1, 'ܦܘܠܘܤ ܩܪܝܐ ܘܫܠܝܚܐ ܕܝܫܘܥ ܡܫܝܚܐ ܒܨܒܝܢܗ ܕܐܠܗܐ ܘܤܘܤܬܢܤ ܐܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 2, 'ܠܥܕܬܐ ܕܐܠܗܐ ܕܒܩܘܪܢܬܘܤ ܩܪܝܐ ܘܩܕܝܫܐ ܕܒܝܫܘܥ ܡܫܝܚܐ ܡܩܕܫܝܢ ܘܠܟܠܗܘܢ ܐܝܠܝܢ ܕܩܪܝܢ ܫܡܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܒܟܠ ܐܬܪ ܕܝܠܗܘܢ ܘܕܝܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 3, 'ܛܝܒܘܬܐ ܥܡܟܘܢ ܘܫܠܡܐ ܡܢ ܐܠܗܐ ܐܒܘܢ ܘܡܢ ܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 4, 'ܡܘܕܐ ܐܢܐ ܠܐܠܗܝ ܒܟܠܙܒܢ ܚܠܦܝܟܘܢ ܥܠ ܛܝܒܘܬܐ ܕܐܠܗܐ ܕܐܬܝܗܒܬ ܠܟܘܢ ܒܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 5, 'ܕܒܟܠ ܡܕܡ ܥܬܪܬܘܢ ܒܗ ܒܟܠ ܡܠܐ ܘܒܟܠ ܝܕܥܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 6, 'ܐܝܟ ܕܤܗܕܘܬܗ ܕܡܫܝܚܐ ܐܫܬܪܪܬ ܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 7, 'ܕܠܐ ܐܬܒܨܪܬܘܢ ܒܚܕܐ ܡܢ ܡܘܗܒܬܗ ܐܠܐ ܡܤܟܝܢ ܐܢܬܘܢ ܠܓܠܝܢܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 8, 'ܕܗܘ ܢܫܪܪܟܘܢ ܥܕܡܐ ܠܐܚܪܝܬܐ ܕܕܠܐ ܪܫܝܢ ܬܗܘܘܢ ܒܝܘܡܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 9, 'ܡܗܝܡܢ ܗܘ ܐܠܗܐ ܕܒܐܝܕܗ ܐܬܩܪܝܬܘܢ ܠܫܘܬܦܘܬܐ ܕܒܪܗ ܝܫܘܥ ܡܫܝܚܐ ܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 10, 'ܒܥܐ ܐܢܐ ܕܝܢ ܡܢܟܘܢ ܐܚܝ ܒܫܡܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܕܬܗܘܐ ܚܕܐ ܡܠܬܐ ܠܟܠܟܘܢ ܘܠܐ ܢܗܘܝܢ ܒܟܘܢ ܦܠܓܘܬܐ ܐܠܐ ܬܗܘܘܢ ܓܡܝܪܝܢ ܒܚܕܐ ܬܪܥܝܬܐ ܘܒܚܕ ܪܥܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 11, 'ܫܠܚܘ ܠܝ ܓܝܪ ܥܠܝܟܘܢ ܐܚܝ ܡܢ ܒܝܬ ܟܠܐܐ ܕܚܪܝܢܐ ܐܝܬ ܒܝܢܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 12, 'ܗܕܐ ܕܝܢ ܐܡܪ ܐܢܐ ܕܐܝܬ ܡܢܟܘܢ ܕܐܡܪ ܐܢܐ ܕܦܘܠܘܤ ܐܢܐ ܘܐܝܬ ܕܐܡܪ ܐܢܐ ܕܐܦܠܘ ܐܢܐ ܘܐܝܬ ܕܐܡܪ ܐܢܐ ܕܟܐܦܐ ܐܢܐ ܘܐܝܬ ܕܐܡܪ ܐܢܐ ܕܡܫܝܚܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 13, 'ܕܠܡܐ ܐܬܦܠܓ ܠܗ ܡܫܝܚܐ ܐܘ ܠܡܐ ܦܘܠܘܤ ܐܙܕܩܦ ܥܠ ܐܦܝܟܘܢ ܐܘ ܒܫܡܗ ܕܦܘܠܘܤ ܥܡܕܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 14, 'ܡܘܕܐ ܐܢܐ ܠܐܠܗܝ ܕܠܐܢܫ ܡܢܟܘܢ ܠܐ ܐܥܡܕܬ ܐܠܐ ܠܟܪܝܤܦܘܤ ܘܠܓܐܝܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 15, 'ܕܠܐ ܐܢܫ ܢܐܡܪ ܕܒܫܡܝ ܐܥܡܕܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 16, 'ܐܥܡܕܬ ܕܝܢ ܐܦ ܠܒܝܬܗ ܕܐܤܛܦܢܐ ܬܘܒ ܕܝܢ ܠܐ ܝܕܥ ܐܢܐ ܐܢ ܠܐܢܫ ܐܚܪܝܢ ܐܥܡܕܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 17, 'ܠܐ ܓܝܪ ܫܕܪܢܝ ܡܫܝܚܐ ܠܡܥܡܕܘ ܐܠܐ ܠܡܤܒܪܘ ܠܐ ܒܚܟܡܬ ܡܠܐ ܕܠܐ ܢܤܬܪܩ ܙܩܝܦܗ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 18, 'ܡܠܬܐ ܓܝܪ ܕܙܩܝܦܐ ܠܐܒܝܕܐ ܫܛܝܘܬܐ ܗܝ ܠܢ ܕܝܢ ܠܐܝܠܝܢ ܕܚܝܝܢܢ ܚܝܠܐ ܗܝ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 19, 'ܟܬܝܒ ܓܝܪ ܕܐܘܒܕ ܚܟܡܬܐ ܕܚܟܝܡܐ ܘܐܓܠܘܙ ܬܪܥܝܬܐ ܕܤܟܘܠܬܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 20, 'ܐܝܟܐ ܗܘ ܚܟܝܡܐ ܐܘ ܐܝܟܐ ܗܘ ܤܦܪܐ ܐܘ ܐܝܟܐ ܗܘ ܕܪܘܫܐ ܕܥܠܡܐ ܗܢܐ ܠܐ ܗܐ ܐܫܛܝܗ ܐܠܗܐ ܚܟܡܬܗ ܕܥܠܡܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 21, 'ܡܛܠ ܓܝܪ ܕܒܚܟܡܬܗ ܕܐܠܗܐ ܠܐ ܝܕܥ ܥܠܡܐ ܒܚܟܡܬܐ ܠܐܠܗܐ ܨܒܐ ܐܠܗܐ ܕܒܫܛܝܘܬܐ ܕܟܪܘܙܘܬܐ ܢܚܐ ܠܐܝܠܝܢ ܕܡܗܝܡܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 22, 'ܡܛܠ ܕܝܗܘܕܝܐ ܐܬܘܬܐ ܫܐܠܝܢ ܘܐܪܡܝܐ ܚܟܡܬܐ ܒܥܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 23, 'ܚܢܢ ܕܝܢ ܡܟܪܙܝܢܢ ܡܫܝܚܐ ܟܕ ܙܩܝܦ ܬܘܩܠܬܐ ܠܝܗܘܕܝܐ ܘܠܐܪܡܝܐ ܫܛܝܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 24, 'ܠܗܢܘܢ ܕܝܢ ܕܩܪܝܢ ܝܗܘܕܝܐ ܘܐܪܡܝܐ ܡܫܝܚܐ ܚܝܠܐ ܕܐܠܗܐ ܘܚܟܡܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 25, 'ܡܛܠ ܕܫܛܝܘܬܗ ܕܐܠܗܐ ܚܟܝܡܐ ܗܝ ܡܢ ܒܢܝܢܫܐ ܘܟܪܝܗܘܬܗ ܕܐܠܗܐ ܚܝܠܬܢܝܐ ܗܝ ܡܢ ܒܢܝ ܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 26, 'ܚܙܘ ܓܝܪ ܐܦ ܩܪܝܬܟܘܢ ܐܚܝ ܕܠܐ ܤܓܝܐܝܢ ܒܟܘܢ ܚܟܝܡܐ ܒܒܤܪ ܘܠܐ ܤܓܝܐܝܢ ܒܟܘܢ ܚܝܠܬܢܐ ܘܠܐ ܤܓܝܐܝܢ ܒܟܘܢ ܒܢܝ ܛܘܗܡܐ ܪܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 27, 'ܐܠܐ ܓܒܐ ܐܠܗܐ ܠܤܟܠܘܗܝ ܕܥܠܡܐ ܕܢܒܗܬ ܠܚܟܝܡܐ ܘܓܒܐ ܟܪܝܗܘܗܝ ܕܥܠܡܐ ܕܢܒܗܬ ܠܚܝܠܬܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 28, 'ܘܓܒܐ ܠܐܝܠܝܢ ܕܒܨܝܪ ܛܘܗܡܗܘܢ ܒܥܠܡܐ ܘܠܡܤܠܝܐ ܘܠܐܝܠܝܢ ܕܠܝܬ ܐܢܘܢ ܕܢܒܛܠ ܠܐܝܠܝܢ ܕܐܝܬܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 29, 'ܕܠܐ ܢܫܬܒܗܪ ܟܠ ܒܤܪ ܩܕܡܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 30, 'ܐܦ ܐܢܬܘܢ ܕܝܢ ܡܢܗ ܐܢܬܘܢ ܒܝܫܘܥ ܡܫܝܚܐ ܗܘ ܕܗܘܐ ܠܢ ܚܟܡܬܐ ܡܢ ܐܠܗܐ ܘܙܕܝܩܘܬܐ ܘܩܕܝܫܘܬܐ ܘܦܘܪܩܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 31, 'ܐܝܟ ܕܟܬܝܒ ܕܡܢ ܕܡܫܬܒܗܪ ܒܡܪܝܐ ܢܫܬܒܗܪ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 1, 'ܘܐܢܐ ܐܚܝ ܟܕ ܐܬܝܬ ܠܘܬܟܘܢ ܠܐ ܒܡܡܠܠܐ ܪܘܪܒܐ ܐܦܠܐ ܒܚܟܡܬܐ ܤܒܪܬܟܘܢ ܐܪܙܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 2, 'ܘܠܐ ܕܢܬ ܢܦܫܝ ܒܝܢܬܟܘܢ ܐܝܟ ܕܡܕܡ ܝܕܥ ܐܢܐ ܐܠܐ ܐܢ ܠܝܫܘܥ ܡܫܝܚܐ ܐܦ ܠܗ ܟܕ ܙܩܝܦ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 3, 'ܘܐܢܐ ܒܕܚܠܬܐ ܤܓܝܐܬܐ ܘܒܪܬܝܬܐ ܗܘܝܬ ܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 4, 'ܘܡܠܬܝ ܘܟܪܘܙܘܬܝ ܠܐ ܗܘܬ ܒܡܦܝܤܢܘܬܐ ܕܡܠܐ ܕܚܟܡܬܐ ܐܠܐ ܒܬܚܘܝܬܐ ܕܪܘܚܐ ܘܕܚܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 5, 'ܕܠܐ ܬܗܘܐ ܗܝܡܢܘܬܟܘܢ ܒܚܟܡܬܐ ܕܒܢܝܢܫܐ ܐܠܐ ܒܚܝܠܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 6, 'ܚܟܡܬܐ ܕܝܢ ܡܡܠܠܝܢܢ ܒܓܡܝܪܐ ܚܟܡܬܐ ܠܐ ܕܗܢܐ ܥܠܡܐ ܐܦܠܐ ܕܫܠܝܛܢܘܗܝ ܕܥܠܡܐ ܗܢܐ ܕܡܬܒܛܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 7, 'ܐܠܐ ܡܡܠܠܝܢܢ ܚܟܡܬܐ ܕܐܠܗܐ ܒܐܪܙ ܗܝ ܕܡܟܤܝܐ ܗܘܬ ܘܩܕܡ ܗܘܐ ܦܪܫܗ ܐܠܗܐ ܡܢ ܩܕܡ ܥܠܡܐ ܠܫܘܒܚܐ ܕܝܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 8, 'ܗܝ ܕܚܕ ܡܢ ܫܠܝܛܢܐ ܕܥܠܡܐ ܗܢܐ ܠܐ ܝܕܥܗ ܐܠܘ ܓܝܪ ܝܕܥܘܗ ܠܘ ܠܡܪܗ ܕܬܫܒܘܚܬܐ ܙܩܦܝܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 9, 'ܐܠܐ ܐܝܟ ܕܟܬܝܒ ܕܥܝܢܐ ܠܐ ܚܙܬ ܘܐܕܢܐ ܠܐ ܫܡܥܬ ܘܥܠ ܠܒܐ ܕܒܪܢܫܐ ܠܐ ܤܠܩ ܡܕܡ ܕܛܝܒ ܐܠܗܐ ܠܐܝܠܝܢ ܕܪܚܡܝܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 10, 'ܠܢ ܕܝܢ ܓܠܐ ܐܠܗܐ ܒܪܘܚܗ ܪܘܚܐ ܓܝܪ ܟܠܡܕܡ ܒܨܝܐ ܐܦ ܥܘܡܩܘܗܝ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 11, 'ܐܝܢܐ ܗܘ ܓܝܪ ܒܪܢܫܐ ܕܝܕܥ ܕܒܒܪܢܫܐ ܐܠܐ ܐܢ ܪܘܚܐ ܕܒܪ ܕܒܗ ܗܟܢܐ ܐܦ ܕܒܐܠܗܐ ܐܢܫ ܠܐ ܝܕܥ ܐܠܐ ܐܢ ܪܘܚܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 12, 'ܚܢܢ ܕܝܢ ܠܐ ܗܘܐ ܪܘܚܐ ܕܥܠܡܐ ܢܤܒܢ ܐܠܐ ܪܘܚܐ ܕܡܢ ܐܠܗܐ ܕܢܕܥ ܡܘܗܒܬܐ ܕܡܢ ܐܠܗܐ ܐܬܝܗܒ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 13, 'ܐܝܠܝܢ ܕܐܦ ܡܡܠܠܝܢܢ ܠܐ ܗܘܐ ܒܝܘܠܦܢܐ ܕܡܠܐ ܕܚܟܡܬܐ ܕܒܢܝܢܫܐ ܐܠܐ ܒܝܘܠܦܢܐ ܕܪܘܚܐ ܘܠܪܘܚܢܐ ܪܘܚܢܝܬܐ ܡܦܚܡܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 14, 'ܒܪܢܫܐ ܓܝܪ ܕܒܢܦܫ ܗܘ ܠܐ ܡܩܒܠ ܪܘܚܢܝܬܐ ܫܛܝܘܬܐ ܐܢܝܢ ܓܝܪ ܠܗ ܘܠܐ ܡܫܟܚ ܕܢܕܥ ܕܒܪܘܚ ܡܬܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 15, 'ܪܘܚܢܐ ܕܝܢ ܟܠ ܡܕܡ ܕܐܢ ܘܗܘ ܡܢ ܐܢܫ ܠܐ ܡܬܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 16, 'ܡܢܘ ܓܝܪ ܝܕܥ ܪܥܝܢܗ ܕܡܪܝܐ ܕܢܠܦܝܘܗܝ ܠܢ ܕܝܢ ܪܥܝܢܐ ܕܡܫܝܚܐ ܐܝܬ ܠܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 1, 'ܘܐܢܐ ܐܚܝ ܠܐ ܐܫܟܚܬ ܕܐܡܠܠ ܥܡܟܘܢ ܐܝܟ ܕܥܡ ܪܘܚܢܐ ܐܠܐ ܐܝܟ ܕܥܡ ܦܓܪܢܐ ܘܐܝܟ ܕܠܝܠܘܕܐ ܒܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 2, 'ܚܠܒܐ ܐܫܩܝܬܟܘܢ ܘܠܐ ܝܗܒܬ ܠܟܘܢ ܡܐܟܘܠܬܐ ܠܐ ܓܝܪ ܥܕܟܝܠ ܡܫܟܚܝܢ ܗܘܝܬܘܢ ܐܠܐ ܐܦܠܐ ܗܫܐ ܡܫܟܚܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 3, 'ܥܕܟܝܠ ܓܝܪ ܒܒܤܪ ܐܢܬܘܢ ܐܝܟܐ ܓܝܪ ܕܐܝܬ ܒܟܘܢ ܚܤܡܐ ܘܚܪܝܢܐ ܘܦܠܓܘܬܐ ܠܐ ܗܐ ܦܓܪܢܐ ܐܢܬܘܢ ܘܒܒܤܪ ܡܗܠܟܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 4, 'ܡܐ ܕܐܡܪ ܓܝܪ ܐܢܫ ܐܢܫ ܡܢܟܘܢ ܐܢܐ ܕܦܘܠܘܤ ܐܢܐ ܘܐܚܪܢܐ ܐܡܪ ܐܢܐ ܕܐܦܠܘ ܐܢܐ ܠܐ ܗܐ ܦܓܪܢܐ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 5, 'ܡܢܘ ܓܝܪ ܦܘܠܘܤ ܐܘ ܡܢܘ ܐܦܠܘ ܐܠܐ ܡܫܡܫܢܐ ܕܒܐܝܕܝܗܘܢ ܗܝܡܢܬܘܢ ܘܐܢܫ ܐܢܫ ܐܝܟ ܕܝܗܒ ܠܗ ܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 6, 'ܐܢܐ ܢܨܒܬ ܘܐܦܠܘ ܐܫܩܝ ܐܠܐ ܐܠܗܐ ܪܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 7, 'ܠܐ ܗܟܝܠ ܗܘ ܕܢܨܒ ܐܝܬܘܗܝ ܡܕܡ ܘܠܐ ܗܘ ܕܡܫܩܐ ܐܠܐ ܐܠܗܐ ܕܡܪܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 8, 'ܡܢ ܕܢܨܒ ܕܝܢ ܘܡܢ ܕܡܫܩܐ ܚܕ ܐܢܘܢ ܘܐܢܫ ܐܝܟ ܥܡܠܗ ܐܓܪܗ ܡܩܒܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 9, 'ܥܡ ܐܠܗܐ ܓܝܪ ܦܠܚܝܢܢ ܘܦܘܠܚܢܐ ܕܐܠܗܐ ܘܒܢܝܢܐ ܕܐܠܗܐ ܐܢܬܘܢ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 10, 'ܘܐܝܟ ܛܝܒܘܬܐ ܕܐܠܗܐ ܕܐܬܝܗܒܬ ܠܝ ܤܡܬ ܫܬܐܤܬܐ ܐܝܟ ܐܪܕܟܠܐ ܚܟܝܡܐ ܐܚܪܢܐ ܕܝܢ ܥܠܝܗ ܒܢܐ ܟܠܢܫ ܕܝܢ ܢܚܙܐ ܐܝܟܢ ܒܢܐ ܥܠܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 11, 'ܫܬܐܤܬܐ ܓܝܪ ܐܚܪܬܐ ܤܛܪ ܡܢ ܗܕܐ ܕܤܝܡܐ ܐܢܫ ܠܐ ܡܫܟܚ ܠܡܤܡ ܕܐܝܬܝܗ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 12, 'ܘܐܢ ܐܢܫ ܕܝܢ ܒܢܐ ܥܠ ܗܕܐ ܫܬܐܤܬܐ ܕܗܒܐ ܐܘ ܤܐܡܐ ܐܘ ܟܐܦܐ ܝܩܝܪܬܐ ܐܘ ܩܝܤܐ ܐܘ ܥܡܝܪܐ ܐܘ ܚܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 13, 'ܥܒܕܐ ܕܟܠܢܫ ܡܬܓܠܐ ܝܘܡܐ ܓܝܪ ܗܘ ܓܠܐ ܠܗ ܡܛܠ ܕܒܢܘܪܐ ܡܬܓܠܐ ܘܥܒܕܗ ܕܟܠܢܫ ܐܝܟܢܐ ܐܝܬܘܗܝ ܢܘܪܐ ܬܦܪܫܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 14, 'ܘܐܝܢܐ ܕܢܩܘܐ ܥܒܕܗ ܗܘ ܕܒܢܐ ܐܓܪܗ ܢܩܒܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 15, 'ܘܐܝܢܐ ܕܥܒܕܗ ܢܐܩܕ ܢܚܤܪ ܗܘ ܕܝܢ ܢܫܬܘܙܒ ܗܟܢܐ ܕܝܢ ܐܝܟ ܕܡܢ ܢܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 16, 'ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܕܗܝܟܠܗ ܐܢܬܘܢ ܕܐܠܗܐ ܘܪܘܚܗ ܕܐܠܗܐ ܥܡܪܐ ܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 17, 'ܘܡܢ ܕܡܚܒܠ ܗܝܟܠܗ ܕܐܠܗܐ ܡܚܒܠ ܠܗ ܐܠܗܐ ܗܝܟܠܗ ܓܝܪ ܕܐܠܗܐ ܩܕܝܫ ܗܘ ܕܐܢܬܘܢ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 18, 'ܐܢܫ ܠܐ ܢܛܥܐ ܢܦܫܗ ܡܢ ܕܤܒܪ ܒܟܘܢ ܕܚܟܝܡ ܗܘ ܒܥܠܡܐ ܗܢܐ ܢܗܘܐ ܠܗ ܤܟܠܐ ܕܢܗܘܐ ܚܟܝܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 19, 'ܚܟܡܬܗ ܓܝܪ ܕܥܠܡܐ ܗܢܐ ܠܠܘܬܐ ܗܝ ܠܘܬ ܐܠܗܐ ܟܬܝܒ ܗܘ ܓܝܪ ܕܐܚܕ ܚܟܝܡܐ ܒܚܪܥܘܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 20, 'ܘܬܘܒ ܡܪܝܐ ܝܕܥ ܡܚܫܒܬܗܘܢ ܕܚܟܝܡܐ ܕܤܪܝܩܢ ܐܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 21, 'ܡܛܠ ܗܢܐ ܠܐ ܐܢܫ ܢܫܬܒܗܪ ܒܒܢܝܢܫܐ ܟܠܡܕܡ ܓܝܪ ܕܝܠܟܘܢ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 22, 'ܐܢ ܦܘܠܘܤ ܘܐܢ ܐܦܠܘ ܘܐܢ ܟܐܦܐ ܘܐܢ ܥܠܡܐ ܘܐܢ ܚܝܐ ܘܐܢ ܡܘܬܐ ܘܐܢ ܕܩܝܡܢ ܘܐܢ ܕܥܬܝܕܢ ܟܠ ܡܕܡ ܕܝܠܟܘܢ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 23, 'ܘܐܢܬܘܢ ܕܡܫܝܚܐ ܘܡܫܝܚܐ ܕܐܠܗܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 1, 'ܗܟܢܐ ܗܘܝܢ ܚܫܝܒܝܢ ܠܟܘܢ ܐܝܟ ܡܫܡܫܢܐ ܕܡܫܝܚܐ ܘܪܒܝ ܒܬܐ ܕܐܪܙܘܗܝ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 2, 'ܗܪܟܐ ܡܟܝܠ ܡܬܒܥܐ ܒܪܒܝ ܒܬܐ ܕܐܢܫ ܟܕ ܡܗܝܡܢ ܢܫܬܟܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 3, 'ܠܝ ܕܝܢ ܗܕܐ ܒܨܝܪܘܬܐ ܗܝ ܠܝ ܕܡܢܟܘܢ ܐܬܕܝܢ ܐܘ ܡܢ ܟܠ ܒܪ ܐܢܫ ܐܠܐ ܐܦܠܐ ܐܢܐ ܢܦܫܝ ܕܐܢ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 4, 'ܠܐ ܓܝܪ ܡܕܡ ܒܢܦܫܝ ܚܫܝܫ ܐܢܐ ܐܠܐ ܠܘ ܒܗܕܐ ܐܙܕܕܩܬ ܕܝܢܝ ܓܝܪ ܡܪܝܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 5, 'ܡܛܠ ܗܢܐ ܠܐ ܡܢ ܩܕܡ ܙܒܢܐ ܬܗܘܘܢ ܕܝܢܝܢ ܥܕܡܐ ܕܢܐܬܐ ܡܪܝܐ ܗܘ ܕܡܢܗܪ ܟܤܝܬܗ ܕܚܫܘܟܐ ܘܓܠܐ ܡܚܫܒܬܗܘܢ ܕܠܒܘܬܐ ܘܗܝܕܝܢ ܢܗܘܐ ܫܘܒܚܐ ܠܐܢܫ ܐܢܫ ܡܢ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 6, 'ܗܠܝܢ ܕܝܢ ܐܚܝ ܡܛܠܬܟܘܢ ܗܘ ܤܡܬ ܥܠ ܦܪܨܘܦܐ ܕܝܠܝ ܘܕܐܦܠܘ ܕܒܢ ܬܐܠܦܘܢ ܕܠܐ ܬܬܪܥܘܢ ܝܬܝܪ ܡܢ ܡܐ ܕܟܬܝܒ ܘܐܢܫ ܥܠ ܚܒܪܗ ܠܐ ܢܬܪܝܡ ܡܛܠ ܐܢܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 7, 'ܡܢܘ ܓܝܪ ܒܨܟ ܐܘ ܡܢܐ ܐܝܬ ܠܟ ܕܠܐ ܢܤܒܬ ܘܐܢ ܢܤܒܬ ܠܡܢܐ ܡܫܬܒܗܪ ܐܢܬ ܐܝܟ ܗܘ ܕܠܐ ܢܤܒܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 8, 'ܡܢ ܟܕܘ ܤܒܥܬܘܢ ܠܟܘܢ ܘܥܬܪܬܘܢ ܘܒܠܥܕܝܢ ܐܡܠܟܬܘܢ ܐܫܬܘܦ ܕܝܢ ܐܡܠܟܬܘܢ ܕܐܦ ܚܢܢ ܢܡܠܟ ܥܡܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 9, 'ܤܒܪ ܐܢܐ ܓܝܪ ܕܠܢ ܠܫܠܝܚܐ ܐܚܪܝܐ ܗܘ ܤܡܢ ܐܠܗܐ ܐܝܟ ܕܠܡܘܬܐ ܕܗܘܝܢ ܬܐܛܪܘܢ ܠܥܠܡܐ ܘܠܡܠܐܟܐ ܘܠܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 10, 'ܚܢܢ ܫܛܝܐ ܡܛܠ ܡܫܝܚܐ ܐܢܬܘܢ ܕܝܢ ܚܟܝܡܐ ܒܡܫܝܚܐ ܚܢܢ ܟܪܝܗܐ ܘܐܢܬܘܢ ܚܝܠܬܢܐ ܐܢܬܘܢ ܡܫܬܒܚܝܢ ܘܚܢܢ ܡܨܛܥܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 11, 'ܥܕܡܐ ܠܗܕܐ ܫܥܬܐ ܟܦܢܝܢܢ ܘܨܗܝܢܢ ܘܥܪܛܠܝܝܢܢ ܘܡܬܩܦܚܝܢܢ ܘܒܝܬ ܩܝܡܐ ܠܝܬ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 12, 'ܘܠܐܝܢܢ ܟܕ ܦܠܚܝܢܢ ܒܐܝܕܝܢ ܡܨܥܪܝܢ ܠܢ ܘܡܒܪܟܝܢܢ ܪܕܦܝܢ ܠܢ ܘܡܤܝܒܪܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 13, 'ܡܨܚܝܢ ܠܢ ܘܒܥܝܢܢ ܡܢܗܘܢ ܐܝܟ ܢܦܬܐ ܕܥܠܡܐ ܗܘܝܢ ܘܟܘܦܪܐ ܕܟܠܢܫ ܥܕܡܐ ܠܗܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 14, 'ܠܐ ܗܘܐ ܐܝܟ ܕܐܒܗܬܟܘܢ ܟܬܒ ܐܢܐ ܗܠܝܢ ܐܠܐ ܐܝܟ ܕܠܒܢܝܐ ܚܒܝܒܐ ܡܪܬܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 15, 'ܐܢ ܓܝܪ ܪܒܘ ܬܪܐܐ ܢܗܘܘܢ ܠܟܘܢ ܒܡܫܝܚܐ ܐܠܐ ܠܐ ܤܓܝܐܐ ܐܒܗܐ ܒܝܫܘܥ ܓܝܪ ܡܫܝܚܐ ܐܢܐ ܗܘ ܐܘܠܕܬܟܘܢ ܒܤܒܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 16, 'ܒܥܐ ܐܢܐ ܗܟܝܠ ܡܢܟܘܢ ܕܒܝ ܬܬܕܡܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 17, 'ܡܛܠ ܗܢܐ ܫܕܪܬ ܠܘܬܟܘܢ ܠܛܝܡܬܐܘܤ ܕܐܝܬܘܗܝ ܒܪܝ ܚܒܝܒܐ ܘܡܗܝܡܢܐ ܒܡܪܝܐ ܕܗܘ ܢܥܗܕܟܘܢ ܐܘܪܚܬܝ ܕܒܡܫܝܚܐ ܐܝܟ ܡܕܡ ܕܡܠܦ ܐܢܐ ܒܟܠܗܝܢ ܥܕܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 18, 'ܐܝܟ ܗܘ ܕܝܢ ܕܠܐ ܐܬܐ ܐܢܐ ܠܘܬܟܘܢ ܐܬܚܬܪܘ ܐܢܫܐ ܡܢܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 19, 'ܐܠܐ ܐܢ ܡܪܝܐ ܨܒܐ ܒܥܓܠ ܐܬܐ ܐܢܐ ܠܘܬܟܘܢ ܘܐܕܥ ܠܐ ܡܠܬܗܘܢ ܕܗܠܝܢ ܕܡܪܝܡܝܢ ܢܦܫܗܘܢ ܐܠܐ ܚܝܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 20, 'ܡܠܟܘܬܗ ܓܝܪ ܕܐܠܗܐ ܠܐ ܗܘܬ ܒܡܠܬܐ ܐܠܐ ܒܚܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 21, 'ܐܝܟܢܐ ܨܒܝܢ ܐܢܬܘܢ ܒܚܘܛܪܐ ܐܬܐ ܠܘܬܟܘܢ ܐܘ ܒܚܘܒܐ ܘܒܪܘܚܐ ܡܟܝܟܬܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 1, 'ܤܟܐ ܡܫܬܡܥܐ ܒܝܢܬܟܘܢ ܙܢܝܘܬܐ ܘܕܐܝܟ ܗܕܐ ܙܢܝܘܬܐ ܐܝܕܐ ܕܐܦܠܐ ܒܝܬ ܚܢܦܐ ܡܫܬܡܗܐ ܥܕܡܐ ܕܢܤܒ ܒܪܐ ܐܢܬܬ ܐܒܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 2, 'ܘܐܢܬܘܢ ܚܬܝܪܝܢ ܐܢܬܘܢ ܘܠܐ ܕܝܢ ܝܬܝܪܐܝܬ ܝܬܒܬܘܢ ܒܐܒܠܐ ܕܢܫܬܩܠ ܡܢ ܒܝܢܬܟܘܢ ܗܘ ܡܢ ܕܗܢܐ ܤܘܥܪܢܐ ܤܥܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 3, 'ܐܢܐ ܓܝܪ ܟܕ ܪܚܝܩ ܐܢܐ ܡܢܟܘܢ ܒܦܓܪ ܘܩܪܝܒ ܐܢܐ ܠܟܘܢ ܒܪܘܚ ܡܢ ܟܕܘ ܕܢܬ ܐܝܟ ܩܪܝܒܐ ܠܗܘ ܡܢ ܕܗܕܐ ܤܥܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 4, 'ܕܒܫܡܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܬܬܟܢܫܘܢ ܟܠܟܘܢ ܘܐܢܐ ܥܡܟܘܢ ܒܪܘܚ ܥܡ ܚܝܠܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 5, 'ܘܬܫܠܡܘܢܗ ܠܗܢܐ ܠܤܛܢܐ ܠܐܒܕܢܐ ܕܦܓܪܗ ܕܒܪܘܚ ܢܚܐ ܒܝܘܡܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 6, 'ܠܐ ܫܦܝܪ ܫܘܒܗܪܟܘܢ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܕܚܡܝܪܐ ܩܠܝܠ ܟܠܗ ܓܒܝܠܬܐ ܡܚܡܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 7, 'ܕܟܘ ܡܢܟܘܢ ܚܡܝܪܐ ܥܬܝܩܐ ܕܬܗܘܘܢ ܓܒܝܠܬܐ ܚܕܬܐ ܐܝܟܢܐ ܕܐܝܬܝܟܘܢ ܦܛܝܪܐ ܦܨܚܐ ܓܝܪ ܕܝܠܢ ܡܫܝܚܐ ܗܘ ܕܐܬܢܟܤ ܚܠܦܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 8, 'ܡܛܠ ܗܢܐ ܢܥܒܕ ܥܕܥܕܐ ܠܐ ܒܚܡܝܪܐ ܥܬܝܩܐ ܘܠܐ ܒܚܡܝܪܐ ܕܒܝܫܘܬܐ ܘܕܡܪܝܪܘܬܐ ܐܠܐ ܒܚܡܝܪܐ ܕܕܟܝܘܬܐ ܘܕܩܕܝܫܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 9, 'ܟܬܒܬ ܠܟܘܢ ܒܐܓܪܬܐ ܕܠܐ ܬܬܚܠܛܘܢ ܥܡ ܙܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 10, 'ܠܐ ܕܝܢ ܐܡܪ ܐܢܐ ܥܠ ܙܢܝܐ ܕܒܥܠܡܐ ܗܢܐ ܐܘ ܥܠ ܥܠܘܒܐ ܐܘ ܥܠ ܚܛܘܦܐ ܐܘ ܥܠ ܦܠܚܝ ܦܬܟܪܐ ܘܐܢ ܠܐ ܚܝܒܝܢ ܗܘܝܬܘܢ ܐܦ ܡܢ ܥܠܡܐ ܠܡܦܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 11, 'ܗܕܐ ܕܝܢ ܕܟܬܒܬ ܠܟܘܢ ܕܠܐ ܬܬܚܠܛܘܢ ܐܢ ܐܝܬ ܕܡܬܩܪܐ ܐܚܐ ܘܐܝܬܘܗܝ ܙܢܝܐ ܐܘ ܥܠܘܒܐ ܐܘ ܦܠܚ ܦܬܟܪܐ ܐܘ ܡܨܥܪܢ ܐܘ ܪܘܝ ܐܘ ܚܛܘܦ ܥܡ ܐܝܢܐ ܕܗܟܢܐ ܗܘ ܐܦܠܐ ܠܚܡܐ ܠܡܐܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 12, 'ܡܐ ܠܝ ܓܝܪ ܠܡܕܢ ܠܒܪܝܐ ܐܢܬܘܢ ܠܕܠܓܘ ܕܘܢܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 13, 'ܠܒܪܝܐ ܕܝܢ ܐܠܗܐ ܕܐܢ ܘܫܘܩܠܘܗܝ ܠܒܝܫܐ ܡܢ ܒܝܢܬܟܘܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 1, 'ܡܡܪܚ ܐܢܫ ܡܢܟܘܢ ܟܕ ܐܝܬ ܠܗ ܕܝܢܐ ܥܡ ܐܚܘܗܝ ܕܢܕܘܢ ܩܕܡ ܥܘܠܐ ܘܠܐ ܩܕܡ ܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 2, 'ܐܘ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܕܩܕܝܫܐ ܠܥܠܡܐ ܢܕܘܢܘܢ ܘܐܢ ܥܠܡܐ ܒܟܘܢ ܡܬܕܝܢ ܠܐ ܫܘܝܢ ܐܢܬܘܢ ܠܡܕܢ ܕܝܢܐ ܕܩܕܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 3, 'ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܕܠܡܠܐܟܐ ܕܝܢܝܢܢ ܚܕ ܟܡܐ ܐܝܠܝܢ ܕܕܥܠܡܐ ܐܢܝܢ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 4, 'ܐܠܐ ܐܢ ܐܝܬ ܠܟܘܢ ܕܝܢܐ ܥܠ ܕܥܠܡܐ ܠܐܝܠܝܢ ܕܒܤܝܢ ܒܥܕܬܐ ܐܘܬܒܘ ܠܟܘܢ ܒܕܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 5, 'ܠܟܘܐܪܐ ܗܘ ܕܝܢ ܐܡܪ ܐܢܐ ܠܟܘܢ ܗܟܢܐ ܠܝܬ ܒܟܘܢ ܐܦܠܐ ܚܕ ܚܟܝܡܐ ܕܢܫܟܚ ܢܫܘܐ ܒܝܬ ܐܚܐ ܠܐܚܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 6, 'ܐܠܐ ܐܚܐ ܥܡ ܐܚܘܗܝ ܡܬܕܝܢ ܘܬܘܒ ܩܕܡ ܐܝܠܝܢ ܕܠܐ ܡܗܝܡܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 7, 'ܡܢ ܟܕܘ ܗܟܝܠ ܩܢܘܡܟܘܢ ܚܒܬܘܢ ܠܟܘܢ ܕܕܝܢܐ ܚܕ ܥܡ ܚܕ ܐܝܬ ܠܟܘܢ ܡܛܠ ܡܢܐ ܓܝܪ ܠܐ ܡܬܥܠܒܝܢ ܐܢܬܘܢ ܘܡܛܠ ܡܢܐ ܠܐ ܡܬܓܠܙܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 8, 'ܐܠܐ ܐܢܬܘܢ ܥܠܒܝܢ ܐܢܬܘܢ ܘܓܠܙܝܢ ܐܢܬܘܢ ܐܦ ܠܐܚܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 9, 'ܐܘ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܕܥܘܠܐ ܡܠܟܘܬܗ ܕܐܠܗܐ ܠܐ ܝܪܬܝܢ ܠܐ ܬܛܥܘܢ ܠܐ ܙܢܝܐ ܘܠܐ ܦܠܚܝ ܦܬܟܪܐ ܘܠܐ ܓܝܪܐ ܘܠܐ ܡܚܒܠܐ ܘܠܐ ܫܟܒܝ ܥܡ ܕܟܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 10, 'ܘܠܐ ܥܠܘܒܐ ܘܠܐ ܓܢܒܐ ܘܠܐ ܪܘܝܐ ܘܠܐ ܡܨܥܪܢܐ ܘܠܐ ܚܛܘܦܐ ܗܠܝܢ ܡܠܟܘܬܗ ܕܐܠܗܐ ܠܐ ܝܪܬܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 11, 'ܘܗܠܝܢ ܐܝܬ ܗܘܝ ܒܐܢܫ ܐܢܫ ܡܢܟܘܢ ܐܠܐ ܤܚܝܬܘܢ ܘܐܬܩܕܫܬܘܢ ܘܐܙܕܕܩܬܘܢ ܒܫܡܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܘܒܪܘܚܗ ܕܐܠܗܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 12, 'ܟܠ ܫܠܝܛ ܠܝ ܐܠܐ ܠܐ ܟܠ ܦܩܚ ܠܝ ܟܠ ܫܠܝܛ ܠܝ ܐܠܐ ܥܠܝ ܐܢܫ ܠܐ ܢܫܬܠܛ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 13, 'ܡܐܟܘܠܬܐ ܕܟܪܤܐ ܘܟܪܤܐ ܕܡܐܟܘܠܬܐ ܐܠܗܐ ܕܝܢ ܠܬܪܬܝܗܝܢ ܡܒܛܠ ܦܓܪܐ ܕܝܢ ܠܐ ܠܙܢܝܘܬܐ ܐܠܐ ܠܡܪܢ ܘܡܪܢ ܠܦܓܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 14, 'ܐܠܗܐ ܕܝܢ ܘܠܡܪܢ ܐܩܝܡ ܘܠܢ ܡܩܝܡ ܒܚܝܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 15, 'ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܕܦܓܪܝܟܘܢ ܗܕܡܐ ܐܢܘܢ ܕܡܫܝܚܐ ܢܤܒ ܗܕܡܐ ܕܡܫܝܚܐ ܢܥܒܕܝܘܗܝ ܗܕܡܐ ܕܙܢܝܬܐ ܚܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 16, 'ܐܘ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܕܡܢ ܕܢܩܦ ܠܙܢܝܬܐ ܚܕ ܗܘ ܦܓܪ ܐܡܝܪ ܓܝܪ ܕܢܗܘܘܢ ܬܪܝܗܘܢ ܚܕ ܦܓܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 17, 'ܡܢ ܕܢܩܦ ܕܝܢ ܠܡܪܢ ܗܘܐ ܥܡܗ ܚܕܐ ܪܘܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 18, 'ܥܪܘܩܘ ܡܢ ܙܢܝܘܬܐ ܟܠ ܚܛܝܬܐ ܕܢܥܒܕ ܒܪܢܫܐ ܠܒܪ ܡܢ ܦܓܪܗ ܗܝ ܡܢ ܕܡܙܢܐ ܕܝܢ ܒܦܓܪܗ ܗܘ ܚܛܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 19, 'ܐܘ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܕܦܓܪܟܘܢ ܗܝܟܠܐ ܗܘ ܕܪܘܚܐ ܕܩܘܕܫܐ ܕܥܡܪܐ ܒܟܘܢ ܗܝ ܕܩܒܠܬܘܢ ܡܢ ܐܠܗܐ ܘܠܐ ܗܘܝܬܘܢ ܕܢܦܫܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 20, 'ܐܙܕܒܢܬܘܢ ܓܝܪ ܒܕܡܝܐ ܗܘܝܬܘܢ ܗܟܝܠ ܡܫܒܚܝܢ ܠܐܠܗܐ ܒܦܓܪܟܘܢ ܘܒܪܘܚܟܘܢ ܗܢܘܢ ܕܐܝܬܝܗܘܢ ܕܐܠܗܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 1, 'ܥܠ ܐܝܠܝܢ ܕܟܬܒܬܘܢ ܠܝ ܕܝܢ ܫܦܝܪ ܗܘ ܠܓܒܪܐ ܕܠܐܢܬܬܐ ܠܐ ܢܬܩܪܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 2, 'ܐܠܐ ܡܛܠ ܙܢܝܘܬܐ ܐܢܫ ܐܢܬܬܗ ܢܐܚܘܕ ܘܐܢܬܬܐ ܠܒܥܠܗ ܬܐܚܘܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 3, 'ܓܒܪܐ ܠܐܢܬܬܗ ܚܘܒܐ ܕܡܬܬܚܝܒ ܢܦܪܘܥ ܗܟܢܐ ܐܦ ܗܝ ܐܢܬܬܐ ܠܒܥܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 4, 'ܐܢܬܬܐ ܠܐ ܫܠܝܛܐ ܥܠ ܦܓܪܗ ܐܠܐ ܒܥܠܗ ܗܟܢܐ ܐܦ ܓܒܪܐ ܠܐ ܫܠܝܛ ܥܠ ܦܓܪܗ ܐܠܐ ܐܢܬܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 5, 'ܠܐ ܗܟܝܠ ܬܓܠܙܘܢ ܚܕ ܠܚܕ ܐܠܐ ܐܡܬܝ ܕܬܪܝܟܘܢ ܬܫܬܘܘܢ ܒܙܒܢ ܕܬܬܥܢܘܢ ܠܨܘܡܐ ܘܠܨܠܘܬܐ ܘܬܘܒ ܠܗ ܠܨܒܘܬܐ ܬܬܦܢܘܢ ܕܠܐ ܢܢܤܝܟܘܢ ܤܛܢܐ ܡܛܠ ܪܓܬܐ ܕܦܓܪܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 6, 'ܗܕܐ ܕܝܢ ܐܡܪ ܐܢܐ ܐܝܟ ܕܠܡܚܝܠܐ ܠܘ ܡܢ ܦܘܩܕܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 7, 'ܐܢܐ ܓܝܪ ܨܒܐ ܐܢܐ ܕܟܠܗܘܢ ܒܢܝܢܫܐ ܐܟܘܬܝ ܢܗܘܘܢ ܒܕܟܝܘܬܐ ܐܠܐ ܟܠܢܫ ܡܘܗܒܬܐ ܝܗܝܒܐ ܠܗ ܡܢ ܐܠܗܐ ܐܝܬ ܕܗܟܢܐ ܘܐܝܬ ܕܗܟܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 8, 'ܐܡܪ ܐܢܐ ܕܝܢ ܠܐܝܠܝܢ ܕܠܝܬ ܠܗܘܢ ܢܫܐ ܘܠܐܪܡܠܬܐ ܕܦܩܚ ܠܗܘܢ ܐܢ ܢܩܘܘܢ ܐܟܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 9, 'ܐܢ ܠܐ ܕܝܢ ܡܤܝܒܪܝܢ ܢܙܕܘܓܘܢ ܦܩܚ ܓܝܪ ܠܡܤܒ ܐܢܬܬܐ ܛܒ ܡܢ ܕܠܡܐܩܕ ܒܪܓܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 10, 'ܠܐܝܠܝܢ ܕܝܢ ܕܐܝܬ ܠܗܘܢ ܢܫܐ ܡܦܩܕ ܐܢܐ ܠܐ ܐܢܐ ܐܠܐ ܡܪܝ ܕܐܢܬܬܐ ܡܢ ܒܥܠܗ ܠܐ ܬܦܪܘܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 11, 'ܘܐܢ ܬܦܪܘܫ ܬܩܘܐ ܕܠܐ ܓܒܪܐ ܐܘ ܠܒܥܠܗ ܬܬܪܥܐ ܘܓܒܪܐ ܠܐܢܬܬܗ ܠܐ ܢܫܒܘܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 12, 'ܠܫܪܟܐ ܕܝܢ ܐܡܪ ܐܢܐ ܐܢܐ ܠܐ ܡܪܝ ܐܢ ܐܝܬ ܐܚܐ ܕܐܝܬ ܠܗ ܐܢܬܬܐ ܕܠܐ ܡܗܝܡܢܐ ܘܗܝ ܨܒܝܐ ܕܬܥܡܪ ܥܡܗ ܠܐ ܢܫܒܩܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 13, 'ܘܐܢܬܬܐ ܐܝܕܐ ܕܐܝܬ ܠܗ ܒܥܠܐ ܕܠܐ ܡܗܝܡܢ ܘܗܘ ܨܒܐ ܕܢܥܡܪ ܥܡܗ ܠܐ ܬܫܒܘܩ ܒܥܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 14, 'ܡܩܕܫ ܗܘ ܓܝܪ ܓܒܪܐ ܐܝܢܐ ܕܠܐ ܡܗܝܡܢ ܒܐܢܬܬܐ ܕܡܗܝܡܢܐ ܘܡܩܕܫܐ ܗܝ ܐܢܬܬܐ ܐܝܕܐ ܕܠܐ ܡܗܝܡܢܐ ܒܓܒܪܐ ܕܡܗܝܡܢ ܘܐܢ ܠܐ ܒܢܝܗܘܢ ܛܡܐܝܢ ܐܢܘܢ ܗܫܐ ܕܝܢ ܕܟܝܢ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 15, 'ܐܢ ܕܝܢ ܗܘ ܕܠܐ ܡܗܝܡܢ ܦܪܫ ܢܦܪܘܫ ܠܐ ܡܫܥܒܕ ܐܚܐ ܐܘ ܚܬܐ ܒܗܠܝܢ ܠܫܠܡܐ ܗܘ ܩܪܢ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 16, 'ܡܢܐ ܓܝܪ ܝܕܥܐ ܐܢܬܝ ܐܢܬܬܐ ܐܢ ܠܒܥܠܟܝ ܬܚܝܢ ܐܘ ܐܢܬ ܓܒܪܐ ܝܕܥ ܐܢܬ ܐܢ ܠܐܢܬܬܟ ܬܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 17, 'ܐܠܐ ܐܢܫ ܐܢܫ ܐܝܟ ܕܦܠܓ ܠܗ ܡܪܝܐ ܘܐܢܫ ܐܝܟ ܕܩܪܝܗܝ ܐܠܗܐ ܗܟܢܐ ܢܗܠܟ ܘܐܦ ܠܟܠܗܝܢ ܥܕܬܐ ܗܟܢܐ ܡܦܩܕ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 18, 'ܐܢ ܟܕ ܓܙܝܪ ܐܢܫ ܐܬܩܪܝ ܠܐ ܢܗܦܘܟ ܠܗ ܠܥܘܪܠܘܬܐ ܘܐܢ ܒܥܘܪܠܘܬܐ ܐܬܩܪܝ ܠܐ ܢܓܙܘܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 19, 'ܓܙܘܪܬܐ ܓܝܪ ܠܐ ܗܘܬ ܡܕܡ ܐܦ ܠܐ ܥܘܪܠܘܬܐ ܐܠܐ ܢܛܘܪܬܐ ܕܦܘܩܕܢܘܗܝ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 20, 'ܟܠܢܫ ܒܩܪܝܢܐ ܕܐܬܩܪܝ ܒܗ ܢܩܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 21, 'ܐܢ ܥܒܕܐ ܐܬܩܪܝܬ ܠܐ ܢܬܒܛܠ ܠܟ ܐܠܐ ܐܦܢ ܡܫܟܚ ܐܢܬ ܠܡܬܚܪܪܘ ܓܒܝ ܠܟ ܕܬܦܠܘܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 22, 'ܐܝܢܐ ܓܝܪ ܕܥܒܕܐ ܐܬܩܪܝ ܒܡܪܢ ܡܚܪܪܐ ܗܘ ܕܐܠܗܐ ܗܟܢܐ ܐܦ ܐܝܢܐ ܕܒܪ ܚܐܪܐ ܐܬܩܪܝ ܥܒܕܐ ܗܘ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 23, 'ܒܕܡܝܐ ܐܙܕܒܢܬܘܢ ܠܐ ܬܗܘܘܢ ܥܒܕܐ ܕܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 24, 'ܟܠܢܫ ܒܡܕܡ ܕܐܬܩܪܝ ܐܚܝ ܒܗ ܢܩܘܐ ܠܘܬ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 25, 'ܥܠ ܒܬܘܠܘܬܐ ܕܝܢ ܦܘܩܕܢܐ ܡܢ ܐܠܗܐ ܠܐ ܐܚܝܕ ܐܢܐ ܡܠܟܐ ܕܝܢ ܝܗܒ ܐܢܐ ܐܝܟ ܓܒܪܐ ܕܐܬܚܢܢܬ ܡܢ ܐܠܗܐ ܕܐܗܘܐ ܡܗܝܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 26, 'ܘܤܒܪ ܐܢܐ ܕܗܕܐ ܫܦܝܪܐ ܡܛܠ ܐܢܢܩܐ ܕܙܒܢܐ ܕܦܩܚ ܠܗ ܠܒܪܢܫܐ ܕܗܟܢܐ ܢܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 27, 'ܐܤܝܪ ܐܢܬ ܒܐܢܬܬܐ ܠܐ ܬܒܥܐ ܫܪܝܐ ܫܪܐ ܐܢܬ ܡܢ ܐܢܬܬܐ ܠܐ ܬܒܥܐ ܐܢܬܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 28, 'ܘܐܢ ܬܤܒ ܐܢܬܬܐ ܠܐ ܚܛܐ ܐܢܬ ܘܐܢ ܒܬܘܠܬܐ ܬܗܘܐ ܠܓܒܪܐ ܠܐ ܚܛܝܐ ܐܘܠܨܢܐ ܕܝܢ ܒܦܓܪ ܗܘܐ ܠܐܝܠܝܢ ܕܗܟܢܐ ܐܢܘܢ ܐܢܐ ܕܝܢ ܥܠܝܟܘܢ ܚܐܤ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 29, 'ܘܗܕܐ ܐܡܪ ܐܢܐ ܐܚܝ ܕܙܒܢܐ ܡܟܝܠ ܐܙܕܠܗܙ ܠܗ ܕܐܝܠܝܢ ܕܐܝܬ ܠܗܘܢ ܢܫܐ ܢܗܘܘܢ ܐܝܟ ܕܠܝܬ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 30, 'ܘܐܝܠܝܢ ܕܒܟܝܢ ܐܝܟ ܠܐ ܒܟܝܢ ܘܐܝܠܝܢ ܕܚܕܝܢ ܐܝܟ ܠܐ ܚܕܝܢ ܘܐܝܠܝܢ ܕܙܒܢܝܢ ܐܝܟ ܠܐ ܡܩܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 31, 'ܘܐܝܠܝܢ ܕܡܬܚܫܚܝܢ ܒܥܠܡܐ ܗܢܐ ܠܐ ܠܒܪ ܡܢ ܙܕܩܐ ܕܚܫܚܬܐ ܥܒܪ ܠܗ ܓܝܪ ܐܤܟܡܗ ܕܥܠܡܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 32, 'ܡܛܠ ܗܢܐ ܨܒܐ ܐܢܐ ܕܕܠܐ ܨܦܬܐ ܬܗܘܘܢ ܐܝܢܐ ܓܝܪ ܕܠܝܬ ܠܗ ܐܢܬܬܐ ܪܢܐ ܒܕܡܪܗ ܕܐܝܟܢܐ ܢܫܦܪ ܠܡܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 33, 'ܘܐܝܢܐ ܕܐܝܬ ܠܗ ܐܢܬܬܐ ܝܨܦ ܕܥܠܡܐ ܕܐܝܟܢܐ ܢܫܦܪ ܠܐܢܬܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 34, 'ܦܘܪܫܢܐ ܕܝܢ ܐܝܬ ܐܦ ܒܝܢܬ ܐܢܬܬܐ ܠܒܬܘܠܬܐ ܐܝܕܐ ܕܠܓܒܪܐ ܠܐ ܗܘܬ ܪܢܝܐ ܒܡܪܗ ܕܬܗܘܐ ܩܕܝܫܐ ܒܦܓܪܗ ܘܒܪܘܚܗ ܘܐܝܕܐ ܕܐܝܬ ܠܗ ܒܥܠܐ ܪܢܝܐ ܕܥܠܡܐ ܕܐܝܟܢܐ ܬܫܦܪ ܠܒܥܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 35, 'ܗܕܐ ܕܝܢ ܠܥܘܕܪܢܟܘܢ ܗܘ ܕܝܠܟܘܢ ܐܡܪ ܐܢܐ ܠܐ ܗܘܐ ܡܚܢܘܩܝܬܐ ܪܡܐ ܐܢܐ ܠܟܘܢ ܐܠܐ ܕܬܗܘܘܢ ܐܡܝܢܝܢ ܠܘܬ ܡܪܟܘܢ ܒܐܤܟܡܐ ܫܦܝܪܐ ܟܕ ܠܐ ܪܢܝܢ ܐܢܬܘܢ ܒܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 36, 'ܐܢ ܐܢܫ ܕܝܢ ܤܒܪ ܕܡܬܒܙܚ ܒܒܬܘܠܬܗ ܕܥܒܪ ܙܒܢܗ ܘܠܐ ܝܗܒܗ ܠܓܒܪܐ ܘܘܠܝܐ ܕܢܬܠܝܗ ܐܝܟ ܕܨܒܐ ܢܥܒܕ ܠܐ ܚܛܐ ܬܙܕܘܓ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 37, 'ܐܝܢܐ ܕܝܢ ܕܫܪܝܪܐܝܬ ܦܤܩ ܒܪܥܝܢܗ ܘܠܐ ܐܠܨܐ ܠܗ ܨܒܘܬܐ ܘܫܠܝܛ ܥܠ ܨܒܝܢܗ ܘܗܟܢܐ ܕܢ ܒܠܒܗ ܕܢܛܪ ܒܬܘܠܬܗ ܫܦܝܪ ܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 38, 'ܘܐܝܢܐ ܗܟܝܠ ܕܝܗܒ ܒܬܘܠܬܗ ܫܦܝܪ ܥܒܕ ܘܐܝܢܐ ܕܠܐ ܝܗܒ ܒܬܘܠܬܗ ܝܬܝܪܐܝܬ ܫܦܝܪ ܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 39, 'ܐܢܬܬܐ ܟܡܐ ܕܚܝ ܒܥܠܗ ܐܤܝܪܐ ܗܝ ܒܢܡܘܤܐ ܐܢ ܕܝܢ ܢܕܡܟ ܒܥܠܗ ܡܚܪܪܐ ܗܝ ܕܬܗܘܐ ܠܡܢ ܕܨܒܝܐ ܒܠܚܘܕ ܒܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 40, 'ܛܘܒܝܗ ܕܝܢ ܐܢ ܗܟܢܐ ܬܩܘܐ ܐܝܟ ܪܥܝܢܝ ܕܝܠܝ ܤܒܪ ܐܢܐ ܕܝܢ ܐܦ ܐܢܐ ܕܪܘܚܐ ܕܐܠܗܐ ܐܝܬ ܒܝ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 1, 'ܥܠ ܕܒܚܐ ܕܝܢ ܕܦܬܟܪܐ ܝܕܥܝܢܢ ܕܒܟܠܢ ܐܝܬ ܝܕܥܬܐ ܘܝܕܥܬܐ ܡܚܬܪܐ ܚܘܒܐ ܕܝܢ ܒܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 2, 'ܐܢ ܐܢܫ ܕܝܢ ܤܒܪ ܕܝܕܥ ܡܕܡ ܠܐ ܥܕܟܝܠ ܡܕܡ ܝܕܥ ܐܝܟ ܡܐ ܕܘܠܐ ܠܗ ܠܡܕܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 3, 'ܐܢ ܐܢܫ ܕܝܢ ܡܚܒ ܠܐܠܗܐ ܗܢܐ ܐܬܝܕܥ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 4, 'ܥܠ ܡܐܟܘܠܬܐ ܗܟܝܠ ܕܕܒܚܐ ܕܦܬܟܪܐ ܝܕܥܝܢܢ ܕܠܐ ܗܘܐ ܡܕܡ ܗܘ ܦܬܟܪܐ ܒܥܠܡܐ ܘܕܠܝܬ ܐܠܗ ܐܚܪܝܢ ܐܠܐ ܐܢ ܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 5, 'ܐܦܢ ܓܝܪ ܐܝܬ ܕܡܬܩܪܝܢ ܐܠܗܐ ܐܘ ܒܫܡܝܐ ܐܘ ܒܐܪܥܐ ܐܝܟܢܐ ܕܐܝܬ ܐܠܗܐ ܤܓܝܐܐ ܘܡܪܘܬܐ ܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 6, 'ܐܠܐ ܠܢ ܕܝܠܢ ܚܕ ܗܘ ܐܠܗܐ ܐܒܐ ܕܟܠ ܡܢܗ ܘܚܢܢ ܒܗ ܘܚܕ ܡܪܝܐ ܝܫܘܥ ܡܫܝܚܐ ܕܟܠ ܒܐܝܕܗ ܘܐܦ ܚܢܢ ܒܐܝܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 7, 'ܐܠܐ ܠܐ ܗܘܐ ܒܟܠܢܫ ܝܕܥܬܐ ܐܝܬ ܓܝܪ ܐܢܫܐ ܕܒܬܐܪܬܗܘܢ ܥܕܡܐ ܠܗܫܐ ܕܥܠ ܦܬܟܪܐ ܐܝܟ ܕܕܒܝܚܐ ܐܟܠܝܢ ܘܡܛܠ ܕܟܪܝܗܐ ܬܐܪܬܗܘܢ ܡܬܛܘܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 8, 'ܡܐܟܘܠܬܐ ܕܝܢ ܠܐ ܡܩܪܒܐ ܠܢ ܠܐܠܗܐ ܠܐ ܓܝܪ ܐܢ ܐܟܠܝܢܢ ܡܬܝܬܪܝܢܢ ܘܠܐ ܐܢ ܠܐ ܢܐܟܘܠ ܡܬܒܨܪܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 9, 'ܚܙܘ ܕܝܢ ܕܠܡܐ ܫܘܠܛܢܟܘܢ ܗܢܐ ܢܗܘܐ ܬܘܩܠܬܐ ܠܟܪܝܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 10, 'ܐܢ ܐܢܫ ܓܝܪ ܢܚܙܝܟ ܠܟ ܕܐܝܬ ܒܟ ܝܕܥܬܐ ܕܤܡܝܟ ܐܢܬ ܒܝܬ ܦܬܟܪܐ ܠܐ ܗܐ ܬܐܪܬܗ ܡܛܠ ܕܟܪܝܗ ܗܘ ܡܫܬܪܪܐ ܠܡܐܟܠ ܕܕܒܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 11, 'ܘܐܒܕ ܠܗ ܒܝܕܥܬܟ ܕܝܠܟ ܗܘ ܐܝܢܐ ܕܡܪܥ ܕܡܛܠܬܗ ܡܝܬ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 12, 'ܘܐܢ ܗܟܢܐ ܡܤܟܠܝܢ ܐܢܬܘܢ ܒܐܚܝܟܘܢ ܘܡܩܦܚܝܢ ܐܢܬܘܢ ܬܐܪܬܗܘܢ ܡܪܥܬܐ ܒܡܫܝܚܐ ܗܘ ܡܤܟܠܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 13, 'ܡܛܠ ܗܢܐ ܐܢ ܡܐܟܘܠܬܐ ܡܟܫܠܐ ܠܐܚܝ ܠܥܠܡ ܒܤܪܐ ܠܐ ܐܟܘܠ ܕܠܐ ܐܟܫܠ ܠܐܚܝ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 1, 'ܠܡܐ ܠܐ ܗܘܝܬ ܒܪ ܚܐܪܐ ܐܘ ܠܐ ܗܘܝܬ ܫܠܝܚܐ ܐܘ ܠܝܫܘܥ ܡܫܝܚܐ ܡܪܢ ܠܐ ܚܙܝܬ ܐܘ ܠܐ ܗܘܝܬܘܢ ܥܒܕܝ ܒܡܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 2, 'ܘܐܢ ܠܐܚܪܢܐ ܠܐ ܗܘܝܬ ܫܠܝܚܐ ܐܠܐ ܠܟܘܢ ܐܝܬܝ ܘܚܬܡܐ ܕܫܠܝܚܘܬܝ ܐܢܬܘܢ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 3, 'ܘܡܦܩ ܒܪܘܚܝ ܠܐܝܠܝܢ ܕܕܝܢܝܢ ܠܝ ܗܢܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 4, 'ܠܡܐ ܠܐ ܫܠܝܛ ܠܢ ܠܡܐܟܠ ܘܠܡܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 5, 'ܘܠܡܐ ܠܐ ܫܠܝܛ ܠܢ ܚܬܐ ܐܢܬܬܐ ܠܡܟܪܟܘ ܥܡܢ ܐܝܟ ܫܪܟܐ ܕܫܠܝܚܐ ܘܐܝܟ ܐܚܘܗܝ ܕܡܪܢ ܘܐܝܟ ܟܐܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 6, 'ܐܘ ܐܢܐ ܒܠܚܘܕ ܘܒܪܢܒܐ ܠܝܬ ܠܢ ܫܘܠܛܢܐ ܕܠܐ ܢܦܠܘܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 7, 'ܡܢܘ ܕܡܦܠܚ ܒܦܠܚܘܬܐ ܒܢܦܩܬܐ ܕܢܦܫܗ ܐܘ ܡܢܘ ܕܢܨܒ ܟܪܡܐ ܘܡܢ ܦܐܪܘܗܝ ܠܐ ܐܟܠ ܐܘ ܡܢܘ ܕܪܥܐ ܥܢܐ ܘܡܢ ܚܠܒܐ ܕܡܪܥܝܬܗ ܠܐ ܐܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 8, 'ܕܠܡܐ ܐܝܟ ܒܪܢܫܐ ܗܠܝܢ ܐܡܪ ܐܢܐ ܗܐ ܐܦ ܢܡܘܤܐ ܗܠܝܢ ܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 9, 'ܟܬܝܒ ܓܝܪ ܒܗ ܒܢܡܘܤܐ ܕܡܘܫܐ ܕܠܐ ܬܒܠܘܡ ܬܘܪܐ ܕܡܕܪܟ ܠܡܐ ܥܠ ܬܘܪܐ ܒܛܝܠ ܠܗ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 10, 'ܐܠܐ ܝܕܝܥܐ ܕܡܛܠܬܢ ܗܘ ܐܡܪ ܘܡܛܠܬܢ ܓܝܪ ܐܬܟܬܒܬ ܡܛܠ ܕܥܠ ܤܒܪܐ ܗܘ ܘܠܐ ܠܗ ܠܟܪܘܒܐ ܕܢܟܪܘܒ ܘܐܝܢܐ ܕܡܕܪܟ ܥܠ ܤܒܪܐ ܕܥܠܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 11, 'ܐܢ ܚܢܢ ܕܪܘܚܐ ܙܪܥܢ ܒܟܘܢ ܪܒܐ ܗܝ ܐܢ ܚܢܢ ܡܢܟܘܢ ܕܦܓܪܐ ܢܚܨܘܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 12, 'ܘܐܢ ܠܐܚܪܢܐ ܐܝܬ ܠܗܘܢ ܫܘܠܛܢܐ ܥܠܝܟܘܢ ܠܐ ܠܢ ܘܠܐ ܝܬܝܪܐܝܬ ܐܠܐ ܠܐ ܐܬܚܫܚܢ ܒܫܘܠܛܢܐ ܗܢܐ ܐܠܐ ܟܠܡܕܡ ܡܤܝܒܪܝܢܢ ܕܒܡܕܡ ܠܐ ܢܬܟܤ ܤܒܪܬܗ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 13, 'ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܕܐܝܠܝܢ ܕܒܝܬ ܩܘܕܫܐ ܦܠܚܝܢ ܡܢ ܒܝܬ ܩܘܕܫܐ ܡܤܬܝܒܪܝܢ ܘܐܝܠܝܢ ܕܠܡܕܒܚܐ ܦܠܚܝܢ ܥܡ ܡܕܒܚܐ ܦܠܓܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 14, 'ܗܟܢܐ ܐܦ ܡܪܢ ܦܩܕ ܕܐܝܠܝܢ ܕܤܒܪܬܗ ܡܟܪܙܝܢ ܡܢ ܤܒܪܬܗ ܢܚܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 15, 'ܐܢܐ ܕܝܢ ܠܐ ܐܬܚܫܚܬ ܒܚܕܐ ܡܢ ܗܠܝܢ ܘܠܘ ܡܛܠ ܗܕܐ ܟܬܒܬ ܕܗܟܢܐ ܢܗܘܐ ܠܝ ܦܩܚ ܠܝ ܓܝܪ ܕܡܡܬ ܐܡܘܬ ܘܠܐ ܕܐܢܫ ܫܘܒܗܪܝ ܢܤܪܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 16, 'ܐܦ ܕܡܤܒܪ ܐܢܐ ܓܝܪ ܠܝܬ ܠܝ ܫܘܒܗܪܐ ܩܛܝܪܐ ܓܝܪ ܤܝܡ ܥܠܝ ܘܝ ܠܝ ܕܝܢ ܐܠܐ ܐܤܒܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 17, 'ܐܢ ܓܝܪ ܒܨܒܝܢܝ ܗܕܐ ܤܥܪ ܐܢܐ ܐܓܪܐ ܐܝܬ ܠܝ ܐܢ ܕܝܢ ܕܠܐ ܒܨܒܝܢܝ ܪܒܬ ܒܝܬܘܬܐ ܗܘ ܡܗܝܡܢ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 18, 'ܐܝܢܐ ܗܘ ܗܟܝܠ ܐܓܪܝ ܕܟܕ ܡܤܒܪ ܐܢܐ ܕܠܐ ܢܦܩܬܐ ܐܥܒܕܝܗ ܠܤܒܪܬܗ ܕܡܫܝܚܐ ܘܠܐ ܐܬܚܫܚ ܒܫܘܠܛܢܐ ܕܝܗܒ ܠܝ ܒܐܘܢܓܠܝܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 19, 'ܟܕ ܡܚܪܪ ܐܢܐ ܓܝܪ ܡܢ ܟܠܗܝܢ ܠܟܠܢܫ ܫܥܒܕܬ ܢܦܫܝ ܕܠܤܓܝܐܐ ܐܬܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 20, 'ܘܗܘܝܬ ܥܡ ܝܗܘܕܝܐ ܐܝܟ ܝܗܘܕܝܐ ܕܠܝܗܘܕܝܐ ܐܬܪ ܘܥܡ ܕܬܚܝܬ ܢܡܘܤܐ ܐܢܘܢ ܗܘܝܬ ܐܝܟ ܕܬܚܝܬ ܢܡܘܤܐ ܕܠܐܝܠܝܢ ܕܬܚܝܬ ܢܡܘܤܐ ܐܢܘܢ ܐܬܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 21, 'ܘܠܐܝܠܝܢ ܕܢܡܘܤܐ ܠܝܬ ܠܗܘܢ ܗܘܝܬ ܐܝܟ ܕܠܐ ܢܡܘܤ ܟܕ ܠܐ ܐܝܬܝ ܠܐܠܗܐ ܕܠܐ ܢܡܘܤ ܐܠܐ ܒܢܡܘܤܗ ܕܡܫܝܚܐ ܕܐܦ ܠܗܢܘܢ ܕܕܠܐ ܢܡܘܤ ܐܢܘܢ ܐܬܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 22, 'ܗܘܝܬ ܥܡ ܟܪܝܗܐ ܐܝܟ ܟܪܝܗܐ ܕܠܟܪܝܗܐ ܐܬܪ ܠܟܠܢܫ ܟܠ ܗܘܝܬ ܕܠܟܠܢܫ ܐܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 23, 'ܗܕܐ ܕܝܢ ܥܒܕ ܐܢܐ ܡܛܠ ܕܐܗܘܐ ܫܘܬܦܐ ܠܤܒܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 24, 'ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܕܐܝܠܝܢ ܕܒܐܤܛܕܝܘܢ ܪܗܛܝܢ ܟܠܗܘܢ ܗܘ ܪܗܛܝܢ ܐܠܐ ܚܕ ܗܘ ܢܤܒ ܠܗ ܙܟܘܬܐ ܗܟܢܐ ܗܪܛܘ ܐܝܟ ܕܬܕܪܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 25, 'ܟܠ ܐܢܫ ܕܝܢ ܕܐܓܘܢܐ ܥܒܕ ܡܢ ܟܠ ܡܕܡ ܐܚܕ ܪܥܝܢܗ ܘܗܠܝܢ ܪܗܛܝܢ ܕܢܤܒܘܢ ܟܠܝܠܐ ܕܡܬܚܒܠ ܚܢܢ ܕܝܢ ܕܠܐ ܡܬܚܒܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 26, 'ܐܢܐ ܗܟܝܠ ܗܟܢܐ ܪܗܛ ܐܢܐ ܠܐ ܗܘܐ ܐܝܟ ܕܥܠ ܡܕܡ ܕܠܐ ܝܕܝܥ ܘܗܟܢܐ ܡܬܟܬܫ ܐܢܐ ܠܐ ܐܝܟ ܗܘ ܕܠܐܐܪ ܟܬܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 27, 'ܐܠܐ ܦܓܪܝ ܗܘ ܟܒܫ ܐܢܐ ܘܡܫܥܒܕ ܐܢܐ ܕܕܠܡܐ ܐܢܐ ܕܠܐܚܪܢܐ ܐܟܪܙܬ ܐܢܐ ܩܢܘܡܝ ܐܤܬܠܐ ܠܝ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 1, 'ܨܒܐ ܐܢܐ ܕܝܢ ܕܬܕܥܘܢ ܐܚܝ ܕܐܒܗܝܢ ܟܠܗܘܢ ܬܚܝܬ ܥܢܢܐ ܗܘܘ ܘܟܠܗܘܢ ܒܝܡܐ ܥܒܪܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 2, 'ܘܟܠܗܘܢ ܒܝܕ ܡܘܫܐ ܥܡܕܘ ܒܥܢܢܐ ܘܒܝܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 3, 'ܘܟܠܗܘܢ ܚܕܐ ܡܐܟܘܠܬܐ ܕܪܘܚܐ ܐܟܠܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 4, 'ܘܟܠܗܘܢ ܚܕ ܡܫܬܝܐ ܕܪܘܚܐ ܐܫܬܝܘ ܫܬܝܢ ܗܘܘ ܓܝܪ ܡܢ ܟܐܦܐ ܕܪܘܚܐ ܕܐܬܝܐ ܗܘܬ ܥܡܗܘܢ ܟܐܦܐ ܕܝܢ ܗܝ ܗܘ ܗܘܐ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 5, 'ܐܠܐ ܠܐ ܗܘܐ ܒܤܘܓܐܗܘܢ ܐܨܛܒܝ ܐܠܗܐ ܢܦܠܘ ܓܝܪ ܒܡܕܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 6, 'ܗܠܝܢ ܕܝܢ ܛܘܦܤܐ ܠܢ ܗܘ ܗܘܘ ܕܠܐ ܗܘܝܢ ܪܓܝܢ ܒܝܫܬܐ ܐܝܟܢܐ ܕܗܢܘܢ ܪܓܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 7, 'ܘܐܦܠܐ ܢܗܘܐ ܦܠܚܝ ܦܬܟܪܐ ܐܝܟܢܐ ܕܐܦ ܡܢܗܘܢ ܦܠܚܘ ܐܝܟ ܕܟܬܝܒ ܕܝܬܒ ܥܡܐ ܠܡܐܟܠ ܘܠܡܫܬܐ ܘܩܡܘ ܠܡܫܬܥܝܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 8, 'ܘܐܦܠܐ ܢܙܢܐ ܐܝܟܢܐ ܕܡܢܗܘܢ ܙܢܝܘ ܘܢܦܠܘ ܒܚܕ ܝܘܡܐ ܥܤܪܝܢ ܘܬܠܬܐ ܐܠܦܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 9, 'ܘܠܐ ܢܢܤܐ ܠܡܫܝܚܐ ܐܝܟܢܐ ܕܡܢܗܘܢ ܢܤܝܘ ܘܐܘܒܕܘ ܐܢܘܢ ܚܘܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 10, 'ܘܐܦܠܐ ܬܪܛܢܘܢ ܐܝܟܢܐ ܕܡܢܗܘܢ ܪܛܢܘ ܘܐܒܕܘ ܒܐܝܕܝ ܡܚܒܠܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 11, 'ܗܠܝܢ ܕܝܢ ܟܠܗܝܢ ܕܓܕܫ ܠܗܘܢ ܠܛܘܦܤܢ ܗܘܝ ܘܐܬܟܬܒ ܡܛܠ ܡܪܕܘܬܐ ܕܝܠܢ ܕܚܪܬܗܘܢ ܕܥܠܡܐ ܥܠܝܢ ܡܛܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 12, 'ܡܟܝܠ ܡܢ ܕܤܒܪ ܕܩܡ ܢܙܕܗܪ ܕܠܐ ܢܦܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 13, 'ܢܤܝܘܢܐ ܠܐ ܡܛܝܟܘܢ ܐܠܐ ܕܒܢܝܢܫܐ ܡܗܝܡܢ ܗܘ ܕܝܢ ܐܠܗܐ ܕܠܐ ܢܪܦܝܟܘܢ ܕܬܬܢܤܘܢ ܝܬܝܪ ܡܢ ܡܕܡ ܕܡܨܝܢ ܐܢܬܘܢ ܐܠܐ ܢܥܒܕ ܠܢܤܝܘܢܟܘܢ ܡܦܩܢܐ ܐܝܟܢܐ ܕܬܫܟܚܘܢ ܠܡܤܝܒܪܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 14, 'ܡܛܠ ܗܢܐ ܚܒܝܒܝ ܥܪܘܩܘ ܡܢ ܦܘܠܚܢܐ ܕܦܬܟܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 15, 'ܐܝܟ ܕܠܚܟܝܡܐ ܐܡܪ ܐܢܐ ܕܘܢܘ ܐܢܬܘܢ ܡܕܡ ܕܐܡܪ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 16, 'ܟܤܐ ܗܘ ܕܬܘܕܝܬܐ ܕܡܒܪܟܝܢܢ ܠܐ ܗܘܐ ܫܘܬܦܘܬܐ ܐܝܬܘܗܝ ܕܕܡܗ ܕܡܫܝܚܐ ܘܠܚܡܐ ܗܘ ܕܩܨܝܢܢ ܠܐ ܗܘܐ ܫܘܬܦܘܬܐ ܐܝܬܘܗܝ ܕܦܓܪܗ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 17, 'ܐܝܟܢܐ ܕܚܕ ܗܘ ܠܚܡܐ ܗܘ ܗܟܢܐ ܟܠܢ ܚܕ ܚܢܢ ܦܓܪ ܟܠܢ ܓܝܪ ܡܢ ܗܘ ܗܘ ܚܕ ܠܚܡܐ ܢܤܒܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 18, 'ܚܙܘ ܠܐܝܤܪܝܠ ܕܒܒܤܪ ܠܐ ܗܘܐ ܐܝܠܝܢ ܕܐܟܠܝܢ ܕܒܚܐ ܗܘܝܢ ܫܘܬܦܐ ܠܡܕܒܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 19, 'ܡܢܐ ܗܟܝܠ ܐܡܪ ܐܢܐ ܕܦܬܟܪܐ ܡܕܡ ܐܝܬܘܗܝ ܐܘ ܕܒܚܐ ܕܦܬܟܪܐ ܡܕܡ ܗܘ ܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 20, 'ܐܠܐ ܗܘ ܡܕܡ ܕܕܒܚܝܢ ܚܢܦܐ ܠܫܐܕܐ ܗܘ ܕܒܚܝܢ ܘܠܐ ܠܐܠܗܐ ܠܐ ܕܝܢ ܨܒܐ ܐܢܐ ܕܬܗܘܘܢ ܫܘܬܦܐ ܠܫܐܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 21, 'ܠܐ ܡܫܟܚܝܢ ܐܢܬܘܢ ܕܬܫܬܘܢ ܟܤܐ ܕܡܪܢ ܘܟܤܐ ܕܫܐܕܐ ܘܠܐ ܡܫܟܚܝܢ ܐܢܬܘܢ ܕܬܫܬܘܬܦܘܢ ܒܦܬܘܪܐ ܕܡܪܢ ܘܒܦܬܘܪܐ ܕܫܐܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 22, 'ܐܘ ܕܠܡܐ ܡܛܢܘ ܡܛܢܝܢܢ ܠܡܪܢ ܕܠܡܐ ܚܤܝܢܝܢܢ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 23, 'ܟܠ ܡܕܡ ܫܠܝܛ ܠܝ ܐܠܐ ܠܐ ܟܠ ܡܕܡ ܦܩܚ ܟܠ ܡܕܡ ܫܠܝܛ ܠܝ ܐܠܐ ܠܐ ܟܠ ܡܕܡ ܡܒܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 24, 'ܠܐ ܐܢܫ ܕܢܦܫܗ ܢܗܘܐ ܒܥܐ ܐܠܐ ܟܠܢܫ ܐܦ ܕܚܒܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 25, 'ܟܠܡܕܡ ܕܡܙܕܒܢ ܒܡܩܠܘܢ ܗܘܝܬܘܢ ܐܟܠܝܢ ܕܠܐ ܥܘܩܒܐ ܡܛܠ ܬܐܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 26, 'ܕܡܪܝܐ ܗܝ ܓܝܪ ܐܪܥܐ ܒܡܠܐܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 27, 'ܐܢ ܐܢܫ ܕܝܢ ܡܢ ܚܢܦܐ ܩܪܐ ܠܟܘܢ ܘܨܒܝܢ ܐܢܬܘܢ ܠܡܐܙܠ ܟܠܡܕܡ ܕܡܬܤܝܡ ܩܕܡܝܟܘܢ ܐܟܘܠܘ ܕܠܐ ܥܘܩܒܐ ܡܛܠ ܬܐܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 28, 'ܐܢ ܐܢܫ ܕܝܢ ܢܐܡܪ ܠܟܘܢ ܕܗܢܐ ܕܕܒܝܚܐ ܗܘ ܠܐ ܬܐܟܠܘܢ ܡܛܠ ܗܘ ܕܐܡܪ ܠܟܘܢ ܘܡܛܠ ܬܐܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 29, 'ܬܐܪܬܐ ܕܝܢ ܐܡܪ ܐܢܐ ܠܐ ܗܘܐ ܕܝܠܟܘܢ ܐܠܐ ܕܗܘ ܕܐܡܪ ܠܡܢܐ ܓܝܪ ܚܐܪܘܬܝ ܡܬܕܝܢܐ ܡܢ ܬܐܪܬܐ ܕܐܚܪܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 30, 'ܐܢ ܐܢܐ ܒܛܝܒܘܬܐ ܡܬܚܫܚ ܐܢܐ ܡܢܐ ܡܬܓܕܦ ܐܢܐ ܥܠ ܡܕܡ ܕܐܢܐ ܡܘܕܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 31, 'ܐܢ ܐܟܠܝܢ ܐܢܬܘܢ ܗܟܝܠ ܘܐܢ ܫܬܝܢ ܐܢܬܘܢ ܘܐܢ ܡܕܡ ܥܒܕܝܢ ܐܢܬܘܢ ܟܠܡܕܡ ܠܬܫܒܘܚܬܐ ܕܐܠܗܐ ܗܘܝܬܘܢ ܥܒܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 32, 'ܕܠܐ ܬܘܩܠܐ ܗܘܘ ܠܝܗܘܕܝܐ ܘܠܐܪܡܝܐ ܘܠܥܕܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 33, 'ܐܝܟ ܕܐܦ ܐܢܐ ܒܟܠ ܡܕܡ ܠܟܠܢܫ ܫܦܪ ܐܢܐ ܘܠܐ ܒܥܐ ܐܢܐ ܡܕܡ ܕܠܝ ܦܩܚ ܐܠܐ ܡܕܡ ܕܠܤܓܝܐܐ ܦܩܚ ܕܢܚܘܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 1, 'ܐܬܕܡܘ ܒܝ ܐܝܟܢܐ ܕܐܦ ܐܢܐ ܒܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 2, 'ܡܫܒܚ ܐܢܐ ܠܟܘܢ ܕܝܢ ܐܚܝ ܕܒܟܠ ܡܕܡ ܡܥܗܕ ܐܢܬܘܢ ܠܝ ܘܐܝܟܢܐ ܕܐܫܠܡܬ ܠܟܘܢ ܦܘܩܕܢܐ ܐܚܝܕܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 3, 'ܨܒܐ ܐܢܐ ܕܝܢ ܕܬܕܥܘܢ ܕܟܠ ܓܒܪ ܪܫܗ ܡܫܝܚܐ ܗܘ ܘܪܫܗ ܕܐܢܬܬܐ ܓܒܪܐ ܗܘ ܘܪܫܗ ܕܡܫܝܚܐ ܐܠܗܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 4, 'ܟܠ ܓܒܪܐ ܕܡܨܠܐ ܐܘ ܡܬܢܒܐ ܟܕ ܡܟܤܝ ܪܫܗ ܡܒܗܬ ܪܫܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 5, 'ܘܟܠ ܐܢܬܬܐ ܕܡܨܠܝܐ ܐܘ ܡܬܢܒܝܐ ܟܕ ܓܠܐ ܪܫܗ ܡܒܗܬܐ ܪܫܗ ܫܘܝܐ ܗܝ ܓܝܪ ܥܡ ܗܝ ܕܓܪܝܥ ܪܫܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 6, 'ܐܢ ܓܝܪ ܠܐ ܡܬܟܤܝܐ ܐܢܬܬܐ ܐܦ ܬܤܬܦܪ ܐܢ ܕܝܢ ܡܫܟܪ ܗܘ ܠܐܢܬܬܐ ܠܡܤܬܦܪܘ ܐܘ ܠܡܓܪܥ ܬܬܟܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 7, 'ܓܒܪܐ ܓܝܪ ܠܐ ܚܝܒ ܕܢܟܤܐ ܪܫܗ ܡܛܠ ܕܕܡܘܬܐ ܗܘ ܘܬܫܒܘܚܬܐ ܕܐܠܗܐ ܐܢܬܬܐ ܕܝܢ ܬܫܒܘܚܬܐ ܗܝ ܕܓܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 8, 'ܠܐ ܓܝܪ ܐܝܬܘܗܝ ܓܒܪܐ ܡܢ ܐܢܬܬܐ ܐܠܐ ܐܢܬܬܐ ܡܢ ܓܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 9, 'ܘܐܦܠܐ ܓܝܪ ܓܒܪܐ ܐܬܒܪܝ ܡܛܠ ܐܢܬܬܐ ܐܠܐ ܐܢܬܬܐ ܡܛܠ ܓܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 10, 'ܡܛܠ ܗܢܐ ܚܝܒܐ ܐܢܬܬܐ ܕܫܘܠܛܢܐ ܢܗܘܐ ܥܠ ܪܫܗ ܡܛܠ ܡܠܐܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 11, 'ܒܪܡ ܕܝܢ ܠܐ ܓܒܪܐ ܠܒܪ ܡܢ ܐܢܬܬܐ ܐܦܠܐ ܐܢܬܬܐ ܠܒܪ ܡܢ ܓܒܪܐ ܒܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 12, 'ܐܝܟܢܐ ܓܝܪ ܕܐܢܬܬܐ ܡܢ ܓܒܪܐ ܗܟܢܐ ܐܦ ܓܒܪܐ ܒܝܕ ܐܢܬܬܐ ܟܠܡܕܡ ܕܝܢ ܡܢ ܐܠܗܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 13, 'ܕܘܢܘ ܒܝܢܝܟܘܢ ܠܢܦܫܟܘܢ ܝܐܐ ܠܐܢܬܬܐ ܕܟܕ ܓܠܐ ܪܫܗ ܬܨܠܐ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 14, 'ܐܦܠܐ ܗܘ ܟܝܢܐ ܡܠܦ ܠܟܘܢ ܕܓܒܪܐ ܡܐ ܕܩܐܡ ܤܥܪܗ ܨܥܪܐ ܗܘ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 15, 'ܘܐܢܬܬܐ ܡܐ ܕܡܪܒܝ ܤܥܪܗ ܬܫܒܘܚܬܐ ܗܝ ܠܗ ܡܛܠ ܕܤܥܪܗ ܚܠܦ ܬܟܤܝܬܐ ܗܘ ܐܬܝܗܒ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 16, 'ܐܢ ܐܢܫ ܕܝܢ ܡܬܚܪܐ ܥܠ ܗܠܝܢ ܠܢ ܠܝܬ ܥܝܕܐ ܐܝܟ ܗܢܐ ܘܠܐ ܠܥܕܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 17, 'ܗܕܐ ܕܝܢ ܕܡܦܩܕ ܐܢܐ ܠܐ ܐܝܟ ܡܫܒܚ ܐܢܐ ܠܟܘܢ ܡܛܠ ܕܠܐ ܗܘܐ ܠܩܕܡܝܟܘܢ ܐܬܝܬܘܢ ܐܠܐ ܠܒܨܝܪܘܬܐ ܗܘ ܢܚܬܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 18, 'ܠܘܩܕܡ ܓܝܪ ܡܐ ܕܡܬܟܢܫܝܢ ܐܢܬܘܢ ܒܥܕܬܐ ܦܠܓܘܬܐ ܫܡܥ ܐܢܐ ܕܐܝܬ ܒܝܢܬܟܘܢ ܘܡܕܡ ܡܕܡ ܡܗܝܡܢ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 19, 'ܥܬܝܕܝܢ ܐܢܘܢ ܓܝܪ ܐܦ ܚܪܝܢܐ ܕܢܗܘܘܢ ܒܝܢܬܟܘܢ ܕܐܝܠܝܢ ܕܒܩܝܢ ܒܟܘܢ ܢܬܝܕܥܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 20, 'ܡܐ ܗܟܝܠ ܕܡܬܟܢܫܝܢ ܐܢܬܘܢ ܠܘ ܐܝܟ ܕܙܕܩ ܠܝܘܡܗ ܕܡܪܢ ܐܟܠܝܢ ܐܢܬܘܢ ܘܫܬܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 21, 'ܐܠܐ ܐܢܫ ܐܢܫ ܚܫܡܝܬܗ ܩܕܡ ܐܟܠ ܠܗ ܘܗܘܐ ܚܕ ܟܦܢ ܘܚܕ ܪܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 22, 'ܕܠܡܐ ܒܬܐ ܠܝܬ ܠܟܘܢ ܕܬܐܟܠܘܢ ܘܬܫܬܘܢ ܐܘ ܒܥܕܬܗ ܕܐܠܗܐ ܒܤܝܢ ܐܢܬܘܢ ܘܡܒܗܬܝܢ ܐܢܬܘܢ ܠܐܝܠܝܢ ܕܠܝܬ ܠܗܘܢ ܡܢܐ ܐܡܪ ܠܟܘܢ ܐܫܒܚܟܘܢ ܒܗܕܐ ܠܐ ܡܫܒܚ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 23, 'ܐܢܐ ܓܝܪ ܩܒܠܬ ܡܢ ܡܪܢ ܗܘ ܡܕܡ ܕܐܫܠܡܬ ܠܟܘܢ ܕܡܪܢ ܝܫܘܥ ܒܗܘ ܠܠܝܐ ܕܡܫܬܠܡ ܗܘܐ ܢܤܒ ܗܘܐ ܠܚܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 24, 'ܘܒܪܟ ܘܩܨܐ ܘܐܡܪ ܤܒܘ ܐܟܘܠܘ ܗܢܘ ܦܓܪܝ ܕܥܠ ܐܦܝܟܘܢ ܡܬܩܨܐ ܗܟܢܐ ܗܘܝܬܘܢ ܥܒܕܝܢ ܠܕܘܟܪܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 25, 'ܗܟܢܐ ܡܢ ܒܬܪ ܕܐܚܫܡܘ ܐܦ ܟܤܐ ܝܗܒ ܘܐܡܪ ܗܢܐ ܟܤܐ ܐܝܬܘܗܝ ܕܝܬܩܐ ܚܕܬܐ ܒܕܡܝ ܗܟܢܐ ܗܘܝܬܘܢ ܥܒܕܝܢ ܟܠ ܐܡܬܝ ܕܫܬܝܢ ܐܢܬܘܢ ܠܕܘܟܪܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 26, 'ܟܠ ܐܡܬܝ ܓܝܪ ܕܐܟܠܝܢ ܐܢܬܘܢ ܠܚܡܐ ܗܢܐ ܘܫܬܝܢ ܐܢܬܘܢ ܟܤܐ ܗܢܐ ܡܘܬܗ ܗܘ ܕܡܪܢ ܡܥܗܕܝܢ ܐܢܬܘܢ ܥܕܡܐ ܠܡܐܬܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 27, 'ܐܝܢܐ ܗܟܝܠ ܕܐܟܠ ܡܢ ܠܚܡܗ ܕܡܪܝܐ ܘܫܬܐ ܡܢ ܟܤܗ ܘܠܐ ܫܘܐ ܠܗ ܡܚܝܒ ܗܘ ܠܕܡܗ ܕܡܪܝܐ ܘܠܦܓܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 28, 'ܡܛܠ ܗܢܐ ܢܗܘܐ ܐܢܫ ܒܩܐ ܢܦܫܗ ܘܗܝܕܝܢ ܐܟܠ ܡܢ ܠܚܡܐ ܗܢܐ ܘܫܬܐ ܡܢ ܟܤܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 29, 'ܡܢ ܕܐܟܠ ܓܝܪ ܘܫܬܐ ܡܢܗ ܟܕ ܠܐ ܫܘܐ ܚܘܝܒܐ ܗܘ ܠܢܦܫܗ ܐܟܠ ܘܫܬܐ ܕܠܐ ܦܪܫ ܦܓܪܗ ܕܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 30, 'ܡܛܠ ܗܢܐ ܤܓܝܐܝܢ ܒܟܘܢ ܡܪܥܐ ܘܟܪܝܗܐ ܘܤܓܝܐܐ ܕܕܡܟܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 31, 'ܐܠܘ ܓܝܪ ܢܦܫܢ ܕܝܢܝܢ ܗܘܝܢ ܠܐ ܡܬܕܝܢܝܢ ܗܘܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 32, 'ܟܕ ܡܬܕܝܢܝܢܢ ܕܝܢ ܡܢ ܡܪܢ ܡܬܪܕܝܘ ܡܬܪܕܝܢܢ ܕܠܐ ܥܡ ܥܠܡܐ ܢܬܚܝܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 33, 'ܡܟܝܠ ܐܚܝ ܐܡܬܝ ܕܡܬܟܢܫܝܢ ܐܢܬܘܢ ܠܡܐܟܠ ܗܘܝܬܘܢ ܡܩܘܝܢ ܚܕ ܠܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 34, 'ܡܢ ܕܝܢ ܕܟܦܢ ܒܒܝܬܗ ܢܠܥܤ ܕܠܐ ܠܚܝܒܘܬܐ ܬܗܘܘܢ ܡܬܟܢܫܝܢ ܥܠ ܫܪܟܐ ܕܝܢ ܡܐ ܕܐܬܝܬ ܐܦܩܕܟܘܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 1, 'ܥܠ ܪܘܚܢܝܬܐ ܕܝܢ ܐܚܝ ܨܒܐ ܐܢܐ ܕܬܕܥܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 2, 'ܕܚܢܦܐ ܗܘܝܬܘܢ ܘܠܦܬܟܪܐ ܐܝܠܝܢ ܕܠܝܬ ܠܗܘܢ ܩܠܐ ܕܠܐ ܦܘܪܫܢ ܡܬܕܒܪܝܢ ܗܘܝܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 3, 'ܡܛܠ ܗܢܐ ܡܘܕܥ ܐܢܐ ܠܟܘܢ ܕܠܝܬ ܐܢܫ ܕܒܪܘܚܐ ܕܐܠܗܐ ܡܡܠܠ ܘܐܡܪ ܕܚܪܡ ܗܘ ܝܫܘܥ ܘܐܦܠܐ ܐܢܫ ܡܫܟܚ ܠܡܐܡܪ ܕܡܪܝܐ ܗܘ ܝܫܘܥ ܐܠܐ ܐܢ ܒܪܘܚܐ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 4, 'ܦܘܠܓܐ ܕܝܢ ܕܡܘܗܒܬܐ ܐܝܬܝܗܘܢ ܐܠܐ ܚܕܐ ܗܝ ܪܘܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 5, 'ܘܦܘܠܓܐ ܕܬܫܡܫܬܐ ܐܝܬܝܗܘܢ ܐܠܐ ܚܕ ܗܘ ܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 6, 'ܘܦܘܠܓܐ ܕܚܝܠܘܬܐ ܐܝܬ ܐܠܐ ܚܕ ܗܘ ܐܠܗܐ ܕܡܥܒܕ ܟܠ ܒܟܠܢܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 7, 'ܠܐܢܫ ܐܢܫ ܕܝܢ ܡܬܝܗܒ ܠܗ ܓܠܝܢܐ ܕܪܘܚܐ ܐܝܟ ܕܥܕܪܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 8, 'ܐܝܬ ܕܝܗܝܒܐ ܠܗ ܒܪܘܚܐ ܡܠܬܐ ܕܚܟܡܬܐ ܠܐܚܪܢܐ ܕܝܢ ܡܠܬܐ ܕܝܕܥܬܐ ܒܗ ܒܪܘܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 9, 'ܠܐܚܪܢܐ ܗܝܡܢܘܬܐ ܒܗ ܒܪܘܚܐ ܠܐܚܪܢܐ ܡܘܗܒܬܐ ܕܐܤܝܘܬܐ ܒܗ ܒܪܘܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 10, 'ܠܐܚܪܢܐ ܕܝܢ ܚܝܠܐ ܠܐܚܪܢܐ ܕܝܢ ܢܒܝܘܬܐ ܠܐܚܪܢܐ ܕܝܢ ܦܪܘܫܘܬܐ ܕܪܘܚܐ ܠܐܚܪܢܐ ܕܝܢ ܙܢܝܐ ܕܠܫܢܐ ܠܐܚܪܢܐ ܕܝܢ ܦܘܫܩܐ ܕܠܫܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 11, 'ܟܠܗܝܢ ܕܝܢ ܗܠܝܢ ܚܕܐ ܗܝ ܪܘܚܐ ܤܥܪܐ ܘܡܦܠܓܐ ܠܟܠܢܫ ܐܝܟ ܕܗܝ ܨܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 12, 'ܐܟܙܢܐ ܓܝܪ ܕܦܓܪܐ ܚܕ ܗܘ ܘܐܝܬ ܒܗ ܗܕܡܐ ܤܓܝܐܐ ܟܠܗܘܢ ܕܝܢ ܗܕܡܐ ܕܦܓܪܐ ܟܕ ܤܓܝܐܐ ܐܝܬܝܗܘܢ ܚܕ ܐܢܘܢ ܦܓܪ ܗܟܢܐ ܐܦ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 13, 'ܐܦ ܚܢܢ ܓܝܪ ܟܠܢ ܒܚܕܐ ܪܘܚ ܠܚܕ ܦܓܪ ܥܡܕܢ ܐܢ ܝܗܘܕܝܐ ܘܐܢ ܐܪܡܝܐ ܘܐܢ ܥܒܕܐ ܘܐܢ ܒܢܝ ܚܐܪܐ ܘܟܠܢ ܚܕܐ ܪܘܚܐ ܐܫܬܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 14, 'ܐܦ ܦܓܪܐ ܓܝܪ ܠܐ ܗܘܐ ܚܕ ܗܕܡ ܐܠܐ ܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 15, 'ܐܢ ܬܐܡܪ ܓܝܪ ܪܓܠܐ ܕܡܛܠ ܕܠܐ ܗܘܝܬ ܐܝܕܐ ܠܐ ܗܘܝܬ ܡܢܗ ܡܢ ܦܓܪܐ ܠܘ ܡܛܠ ܗܕܐ ܠܝܬܝܗ ܡܢܗ ܡܢ ܦܓܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 16, 'ܘܐܢ ܬܐܡܪ ܐܕܢܐ ܥܠ ܕܠܐ ܗܘܝܬ ܥܝܢܐ ܠܐ ܗܘܝܬ ܡܢܗ ܡܢ ܦܓܪܐ ܠܘ ܡܛܠ ܗܕܐ ܠܝܬܝܗ ܡܢܗ ܡܢ ܦܓܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 17, 'ܐܠܘ ܓܝܪ ܟܠܗ ܦܓܪܐ ܥܝܢܐ ܗܘܐ ܐܝܟܐ ܗܘܬ ܡܫܡܥܬܐ ܘܐܠܘ ܟܠܗ ܡܫܡܥܬܐ ܗܘܐ ܐܝܟܢܐ ܡܪܝܚ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 18, 'ܗܫܐ ܕܝܢ ܐܠܗܐ ܤܡ ܟܠ ܚܕ ܚܕ ܡܢ ܗܕܡܐ ܒܦܓܪܐ ܐܝܟܢܐ ܕܗܘ ܨܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 19, 'ܐܠܘ ܕܝܢ ܟܠܗܘܢ ܚܕ ܗܘܘ ܗܕܡܐ ܐܝܟܐ ܗܘܐ ܦܓܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 20, 'ܗܫܐ ܕܝܢ ܗܕܡܐ ܐܝܬܝܗܘܢ ܤܓܝܐܐ ܚܕ ܗܘ ܕܝܢ ܦܓܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 21, 'ܠܐ ܡܫܟܚܐ ܥܝܢܐ ܕܬܐܡܪ ܠܐܝܕܐ ܕܠܐ ܡܬܒܥܝܐ ܐܢܬܝ ܠܝ ܐܦܠܐ ܪܫܐ ܡܫܟܚ ܕܢܐܡܪ ܠܪܓܠܐ ܕܠܐ ܡܬܒܥܝܢ ܐܢܬܝܢ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 22, 'ܐܠܐ ܝܬܝܪܐܝܬ ܐܝܠܝܢ ܗܕܡܐ ܕܡܤܬܒܪܝܢ ܕܡܚܝܠܝܢ ܥܠܝܗܘܢ ܗܘ ܤܘܢܩܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 23, 'ܘܐܝܠܝܢ ܕܤܒܪܝܢܢ ܕܡܨܥܪܝܢ ܐܢܘܢ ܒܦܓܪܐ ܠܗܠܝܢ ܐܝܩܪܐ ܝܬܝܪܐ ܡܤܓܝܢܢ ܘܐܝܠܝܢ ܕܕܢܘܟܦܐ ܐܢܘܢ ܐܤܟܡܐ ܝܬܝܪܐ ܥܒܕܝܢܢ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 24, 'ܐܝܠܝܢ ܕܝܢ ܗܕܡܐ ܕܐܝܬ ܒܢ ܕܡܝܩܪܝܢ ܠܐ ܡܬܒܥܐ ܠܗܘܢ ܐܝܩܪܐ ܐܠܗܐ ܓܝܪ ܡܙܓܗ ܠܦܓܪܐ ܘܝܗܒ ܐܝܩܪܐ ܝܬܝܪܐ ܠܗܕܡܐ ܐܝܢܐ ܕܙܥܘܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 25, 'ܕܠܐ ܬܗܘܐ ܦܠܓܘܬܐ ܒܦܓܪܐ ܐܠܐ ܟܠܗܘܢ ܗܕܡܐ ܫܘܝܐܝܬ ܚܕ ܥܠ ܚܕ ܢܗܘܘܢ ܝܨܦܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 26, 'ܕܐܡܬܝ ܕܚܕ ܗܕܡ ܢܗܘܐ ܟܐܒ ܟܠܗܘܢ ܢܗܘܘܢ ܚܫܝܢ ܘܐܢ ܡܫܬܒܚ ܚܕ ܗܕܡ ܟܠܗܘܢ ܗܕܡܐ ܢܗܘܘܢ ܡܫܬܒܚܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 27, 'ܐܢܬܘܢ ܕܝܢ ܦܓܪܗ ܐܢܬܘܢ ܕܡܫܝܚܐ ܘܗܕܡܐ ܒܕܘܟܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 28, 'ܤܡ ܓܝܪ ܐܠܗܐ ܒܥܕܬܗ ܠܘܩܕܡ ܫܠܝܚܐ ܒܬܪܗܘܢ ܢܒܝܐ ܒܬܪܗܘܢ ܡܠܦܢܐ ܒܬܪܗܘܢ ܥܒܕܝ ܚܝܠܐ ܒܬܪܗܘܢ ܡܘܗܒܬܐ ܕܐܤܝܘܬܐ ܘܡܥܕܪܢܐ ܘܡܕܒܪܢܐ ܘܙܢܝܐ ܕܠܫܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 29, 'ܕܠܡܐ ܟܠܗܘܢ ܫܠܝܚܐ ܕܠܡܐ ܟܠܗܘܢ ܢܒܝܐ ܠܡܐ ܟܠܗܘܢ ܡܠܦܢܐ ܠܡܐ ܟܠܗܘܢ ܤܥܪܝ ܚܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 30, 'ܠܡܐ ܠܟܠܗܘܢ ܐܝܬ ܠܗܘܢ ܡܘܗܒܬܐ ܕܐܤܝܘܬܐ ܠܡܐ ܟܠܗܘܢ ܒܠܫܢܐ ܡܡܠܠܝܢ ܐܘ ܕܠܡܐ ܟܠܗܘܢ ܡܦܫܩܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 31, 'ܐܢ ܕܝܢ ܛܢܝܢ ܐܢܬܘܢ ܒܡܘܗܒܬܐ ܪܘܪܒܬܐ ܐܢܐ ܬܘܒ ܐܚܘܝܟܘܢ ܐܘܪܚܐ ܕܡܝܬܪܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 1, 'ܐܢ ܒܟܠ ܠܫܢ ܕܒܢܝܢܫܐ ܐܡܠܠ ܘܒܕܡܠܐܟܐ ܘܚܘܒܐ ܠܐ ܢܗܘܐ ܒܝ ܗܘܝܬ ܠܝ ܢܚܫܐ ܕܙܐܡ ܐܘ ܨܨܠܐ ܕܝܗܒ ܩܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 2, 'ܘܐܢ ܬܗܘܐ ܒܝ ܢܒܝܘܬܐ ܘܐܕܥ ܐܪܙܐ ܟܠܗܘܢ ܘܟܠܗ ܝܕܥܬܐ ܘܐܢ ܬܗܘܐ ܒܝ ܟܠܗ ܗܝܡܢܘܬܐ ܐܝܟܢܐ ܕܛܘܪܐ ܐܫܢܐ ܘܚܘܒܐ ܠܝܬ ܒܝ ܠܐ ܗܘܝܬ ܡܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 3, 'ܘܐܢ ܐܘܟܠ ܟܠ ܕܐܝܬ ܠܝ ܠܡܤܟܢܐ ܘܐܢ ܐܫܠܡ ܦܓܪܝ ܕܢܐܩܕ ܘܚܘܒܐ ܠܐ ܢܗܘܐ ܒܝ ܡܕܡ ܠܐ ܝܬܪ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 4, 'ܚܘܒܐ ܢܓܝܪܐ ܗܝ ܪܘܚܗ ܘܒܤܝܡ ܚܘܒܐ ܠܐ ܚܤܡ ܚܘܒܐ ܠܐ ܡܫܬܓܫ ܘܠܐ ܡܬܚܬܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 5, 'ܘܠܐ ܤܥܪ ܕܒܗܬܬܐ ܘܠܐ ܒܥܐ ܕܝܠܗ ܘܠܐ ܡܬܬܦܝܪ ܘܠܐ ܡܬܪܥܐ ܕܒܝܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 6, 'ܠܐ ܚܕܐ ܒܥܘܠܐ ܐܠܐ ܚܕܐ ܒܩܘܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 7, 'ܟܠ ܡܕܡ ܡܤܝܒܪ ܟܠܡܕܡ ܡܗܝܡܢ ܟܠ ܡܤܒܪ ܟܠ ܤܒܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 8, 'ܚܘܒܐ ܡܡܬܘܡ ܠܐ ܢܦܠ ܢܒܝܘܬܐ ܓܝܪ ܢܬܒܛܠܢ ܘܠܫܢܐ ܢܫܬܬܩܘܢ ܘܝܕܥܬܐ ܬܬܒܛܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 9, 'ܩܠܝܠ ܗܘ ܓܝܪ ܡܢ ܤܓܝ ܝܕܥܝܢܢ ܘܩܠܝܠ ܡܢ ܤܓܝ ܡܬܢܒܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 10, 'ܐܡܬܝ ܕܝܢ ܕܬܐܬܐ ܓܡܝܪܘܬܐ ܗܝܕܝܢ ܢܬܒܛܠ ܗܘ ܡܕܡ ܕܩܠܝܠ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 11, 'ܟܕ ܝܠܘܕܐ ܗܘܝܬ ܐܝܟ ܝܠܘܕܐ ܡܡܠܠ ܗܘܝܬ ܘܐܝܟ ܝܠܘܕܐ ܡܬܪܥܐ ܗܘܝܬ ܘܐܝܟ ܝܠܘܕܐ ܡܬܚܫܒ ܗܘܝܬ ܟܕ ܗܘܝܬ ܕܝܢ ܓܒܪܐ ܒܛܠܬ ܗܠܝܢ ܕܛܠܝܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 12, 'ܗܫܐ ܐܝܟ ܕܒܡܚܙܝܬܐ ܚܙܝܢܢ ܒܦܠܐܬܐ ܗܝܕܝܢ ܕܝܢ ܐܦܝܢ ܠܘܩܒܠ ܐܦܝܢ ܗܫܐ ܝܕܥ ܐܢܐ ܩܠܝܠ ܡܢ ܤܓܝ ܗܝܕܝܢ ܕܝܢ ܐܕܥ ܐܝܟ ܡܐ ܕܐܬܝܕܥܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 13, 'ܗܠܝܢ ܐܢܝܢ ܓܝܪ ܬܠܬ ܕܡܟܬܪܢ ܗܝܡܢܘܬܐ ܘܤܒܪܐ ܘܚܘܒܐ ܕܪܒ ܕܝܢ ܡܢܗܝܢ ܚܘܒܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 1, 'ܗܪܛܘ ܒܬܪ ܚܘܒܐ ܘܛܢܘ ܒܡܘܗܒܬܐ ܕܪܘܚܐ ܝܬܝܪܐܝܬ ܕܝܢ ܕܬܬܢܒܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 2, 'ܡܢ ܕܡܡܠܠ ܓܝܪ ܒܠܫܢܐ ܠܐ ܗܘܐ ܠܒܢܝܢܫܐ ܡܡܠܠ ܐܠܐ ܠܐܠܗܐ ܠܐ ܓܝܪ ܐܢܫ ܫܡܥ ܡܕܡ ܕܡܡܠܠ ܐܠܐ ܒܪܘܚ ܐܪܙܐ ܡܡܠܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 3, 'ܐܝܢܐ ܕܝܢ ܕܡܬܢܒܐ ܠܒܢܝܢܫܐ ܡܡܠܠ ܒܢܝܢܐ ܘܠܘܒܒܐ ܘܒܘܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 4, 'ܕܡܡܠܠ ܒܠܫܢܐ ܢܦܫܗ ܗܘ ܒܢܐ ܘܕܡܬܢܒܐ ܥܕܬܐ ܒܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 5, 'ܨܒܐ ܐܢܐ ܕܝܢ ܕܟܠܟܘܢ ܬܡܠܠܘܢ ܒܠܫܢܐ ܝܬܝܪܐܝܬ ܕܝܢ ܕܬܬܢܒܘܢ ܪܒ ܗܘ ܓܝܪ ܐܝܢܐ ܕܡܬܢܒܐ ܡܢ ܗܘ ܕܡܡܠܠ ܒܠܫܢܐ ܐܢ ܠܐ ܡܦܫܩ ܐܢ ܕܝܢ ܡܦܫܩ ܥܕܬܐ ܒܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 6, 'ܘܗܫܐ ܐܚܝ ܐܢ ܐܬܐ ܠܘܬܟܘܢ ܘܐܡܠܠ ܥܡܟܘܢ ܒܠܫܢܐ ܡܢܐ ܡܘܬܪ ܐܢܐ ܠܟܘܢ ܐܠܐ ܐܢ ܐܡܠܠ ܥܡܟܘܢ ܐܘ ܒܓܠܝܢܐ ܐܘ ܒܝܕܥܬܐ ܐܘ ܒܢܒܝܘܬܐ ܐܘ ܒܝܘܠܦܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 7, 'ܐܦ ܨܒܘܬܐ ܓܝܪ ܐܝܠܝܢ ܕܠܝܬ ܒܗܝܢ ܢܦܫܐ ܘܝܗܒܢ ܩܠܐ ܐܢ ܐܒܘܒܐ ܘܐܢ ܩܝܬܪܐ ܐܢ ܦܘܪܫܢܐ ܠܐ ܥܒܕܢ ܒܝܬ ܩܝܢܬܐ ܠܚܒܪܬܗ ܐܝܟܢܐ ܡܬܝܕܥ ܡܕܡ ܕܡܙܕܡܪ ܐܘ ܡܕܡ ܕܡܬܢܩܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 8, 'ܘܐܢ ܩܪܢܐ ܬܩܪܐ ܩܠܐ ܕܠܐ ܦܪܝܫ ܡܢܘ ܢܬܛܝܒ ܠܩܪܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 9, 'ܗܟܢܐ ܐܦ ܐܢܬܘܢ ܐܢ ܬܐܡܪܘܢ ܡܠܬܐ ܒܠܫܢܐ ܘܠܐ ܬܗܘܐ ܡܦܫܩܐ ܐܝܟܢܐ ܡܬܝܕܥ ܡܕܡ ܕܐܡܪܝܢ ܐܢܬܘܢ ܗܘܝܬܘܢ ܠܟܘܢ ܐܝܟ ܗܘ ܕܥܡ ܐܐܪ ܡܡܠܠܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 10, 'ܗܐ ܓܝܪ ܓܢܤܐ ܕܠܫܢܐ ܤܓܝܐܐ ܐܝܬ ܒܥܠܡܐ ܘܠܝܬ ܚܕ ܡܢܗܘܢ ܕܠܐ ܩܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 11, 'ܘܐܢ ܠܐ ܝܕܥ ܐܢܐ ܚܝܠܗ ܕܩܠܐ ܗܘܐ ܐܢܐ ܠܝ ܒܪܒܪܝܐ ܠܗܘ ܕܡܡܠܠ ܘܐܦ ܗܘ ܕܡܡܠܠ ܗܘܐ ܠܗ ܠܝ ܒܪܒܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 12, 'ܗܟܢܐ ܐܦ ܐܢܬܘܢ ܡܛܠ ܕܛܢܢܐ ܐܢܬܘܢ ܕܡܘܗܒܬܐ ܕܪܘܚܐ ܠܒܢܝܢܐ ܕܥܕܬܐ ܒܥܘ ܕܬܬܝܬܪܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 13, 'ܘܗܘ ܕܡܡܠܠ ܒܠܫܢܐ ܢܨܠܐ ܕܢܦܫܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 14, 'ܐܢ ܗܘ ܓܝܪ ܕܗܘܝܬ ܡܨܠܐ ܒܠܫܢܐ ܪܘܚܝ ܗܘ ܡܨܠܝܐ ܡܕܥܝ ܕܝܢ ܕܠܐ ܦܐܪܝܢ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 15, 'ܡܢܐ ܗܟܝܠ ܐܥܒܕ ܐܨܠܐ ܒܪܘܚܝ ܘܐܨܠܐ ܐܦ ܒܡܕܥܝ ܘܐܙܡܪ ܒܪܘܚܝ ܘܐܙܡܪ ܐܦ ܒܡܕܥܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 16, 'ܘܐܢ ܠܐ ܐܢ ܡܒܪܟ ܐܢܬ ܒܪܘܚ ܗܘ ܕܡܡܠܐ ܕܘܟܬܗ ܕܗܕܝܘܛܐ ܐܝܟܢܐ ܢܐܡܪ ܐܡܝܢ ܥܠ ܬܘܕܝܬܟ ܕܝܠܟ ܡܛܠ ܕܡܢܐ ܐܡܪ ܐܢܬ ܠܐ ܝܕܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 17, 'ܐܢܬ ܓܝܪ ܫܦܝܪ ܡܒܪܟ ܐܢܬ ܐܠܐ ܚܒܪܟ ܠܐ ܡܬܒܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 18, 'ܡܘܕܐ ܐܢܐ ܠܐܠܗܐ ܕܝܬܝܪ ܡܢ ܟܠܟܘܢ ܡܡܠܠ ܐܢܐ ܒܠܫܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 19, 'ܐܠܐ ܒܥܕܬܐ ܨܒܐ ܐܢܐ ܕܚܡܫ ܡܠܝܢ ܒܡܕܥܝ ܐܡܠܠ ܕܐܦ ܐܚܪܢܐ ܐܠܦ ܝܬܝܪ ܡܢ ܪܒܘ ܡܠܝܢ ܒܠܫܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 20, 'ܐܚܝ ܠܐ ܗܘܝܬܘܢ ܛܠܝܢ ܒܪܥܝܢܝܟܘܢ ܐܠܐ ܠܒܝܫܬܐ ܗܘܘ ܝܠܘܕܐ ܘܒܪܥܝܢܝܟܘܢ ܗܘܘ ܓܡܝܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 21, 'ܒܢܡܘܤܐ ܟܬܝܒ ܕܒܡܡܠܠܐ ܢܘܟܪܝܐ ܘܒܠܫܢܐ ܐܚܪܢܐ ܐܡܠܠ ܥܡܗ ܥܡ ܥܡܐ ܗܢܐ ܐܦܠܐ ܗܟܢܐ ܢܫܡܥܘܢܢܝ ܐܡܪ ܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 22, 'ܡܕܝܢ ܠܫܢܐ ܠܐܬܐ ܗܘ ܤܝܡܝܢ ܠܐ ܠܡܗܝܡܢܐ ܐܠܐ ܠܐܝܠܝܢ ܕܠܐ ܡܗܝܡܢܝܢ ܢܒܝܘܬܐ ܕܝܢ ܠܐ ܗܘܐ ܠܐܝܠܝܢ ܕܠܐ ܡܗܝܡܢܝܢ ܐܠܐ ܠܐܝܠܝܢ ܕܡܗܝܡܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 23, 'ܐܢ ܗܘ ܗܟܝܠ ܕܬܬܟܢܫ ܟܠܗ ܥܕܬܐ ܘܟܠܗܘܢ ܒܠܫܢܐ ܢܡܠܠܘܢ ܘܢܥܠܘܢ ܗܕܝܘܛܐ ܐܘ ܐܝܠܝܢ ܕܠܐ ܡܗܝܡܢܝܢ ܠܐ ܐܡܪܝܢ ܕܗܠܝܢ ܫܢܘ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 24, 'ܘܐܢ ܟܠܟܘܢ ܬܗܘܘܢ ܡܬܢܒܝܢ ܘܢܥܘܠ ܠܘܬܟܘܢ ܗܕܝܘܛܐ ܐܘ ܡܢ ܕܠܐ ܡܗܝܡܢ ܡܬܒܨܐ ܡܢ ܟܠܟܘܢ ܘܡܬܟܘܢ ܡܢ ܟܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 25, 'ܘܟܤܝܬܐ ܕܠܒܗ ܡܬܓܠܝܢ ܘܗܝܕܝܢ ܢܦܠ ܥܠ ܐܦܘܗܝ ܘܢܤܓܘܕ ܠܐܠܗܐ ܘܢܐܡܪ ܫܪܝܪܐܝܬ ܐܠܗܐ ܐܝܬ ܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 26, 'ܐܡܪ ܐܢܐ ܗܟܝܠ ܐܚܝ ܕܐܡܬܝ ܕܡܬܟܢܫܝܢ ܐܢܬܘܢ ܠܐܝܢܐ ܡܢܟܘܢ ܕܐܝܬ ܠܗ ܡܙܡܘܪܐ ܢܐܡܪ ܘܐܝܢܐ ܕܐܝܬ ܠܗ ܝܘܠܦܢܐ ܘܐܝܢܐ ܕܐܝܬ ܠܗ ܓܠܝܢܐ ܘܐܝܢܐ ܕܐܝܬ ܠܗ ܠܫܢܐ ܘܐܝܢܐ ܕܐܝܬ ܠܗ ܦܘܫܩܐ ܟܠܗܝܢ ܠܒܢܝܢܐ ܢܗܘܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 27, 'ܘܐܢ ܒܠܫܢܐ ܐܢܫ ܢܡܠܠ ܬܪܝܢ ܢܡܠܠܘܢ ܘܟܕ ܤܓܝ ܬܠܬܐ ܘܚܕ ܚܕ ܢܡܠܠܘܢ ܘܚܕ ܢܦܫܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 28, 'ܘܐܢ ܠܝܬ ܕܡܦܫܩ ܢܫܬܘܩ ܠܗ ܒܥܕܬܐ ܗܘ ܕܡܡܠܠ ܒܠܫܢܐ ܘܒܝܢܘܗܝ ܠܢܦܫܗ ܘܠܐܠܗܐ ܢܡܠܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 29, 'ܢܒܝܐ ܕܝܢ ܬܪܝܢ ܐܘ ܬܠܬܐ ܢܡܠܠܘܢ ܘܫܪܟܐ ܢܦܪܫܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 30, 'ܘܐܢ ܠܐܚܪܢܐ ܢܬܓܠܐ ܟܕ ܝܬܒ ܩܕܡܝܐ ܢܫܬܘܩ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 31, 'ܡܫܟܚܝܢ ܐܢܬܘܢ ܓܝܪ ܟܠܟܘܢ ܕܚܕ ܚܕ ܬܬܢܒܘܢ ܕܟܠܢܫ ܢܐܠܦ ܘܟܠܢܫ ܢܬܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 32, 'ܪܘܚܐ ܓܝܪ ܕܢܒܝܐ ܠܢܒܝܐ ܡܫܬܥܒܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 33, 'ܡܛܠ ܕܐܠܗܐ ܠܐ ܗܘܐ ܕܫܓܘܫܝܐ ܐܠܐ ܕܫܠܡܐ ܐܝܟ ܕܒܟܠܗܝܢ ܥܕܬܐ ܕܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 34, 'ܢܫܝܟܘܢ ܒܥܕܬܐ ܢܗܘܝܢ ܫܬܝܩܢ ܠܐ ܓܝܪ ܡܦܤ ܠܗܝܢ ܕܢܡܠܠܢ ܐܠܐ ܕܢܫܬܥܒܕܢ ܐܝܟܢܐ ܕܐܦ ܢܡܘܤܐ ܐܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 35, 'ܘܐܢ ܡܕܡ ܨܒܝܢ ܕܢܐܠܦܢ ܒܒܬܝܗܝܢ ܢܫܐܠܢ ܠܒܥܠܝܗܝܢ ܒܗܬܬܐ ܗܝ ܓܝܪ ܕܢܫܐ ܒܥܕܬܐ ܢܡܠܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 36, 'ܐܘ ܕܠܡܐ ܡܢܟܘܢ ܗܘ ܢܦܩܬ ܡܠܬܗ ܕܐܠܗܐ ܐܘ ܠܘܬܟܘܢ ܗܘ ܒܠܚܘܕ ܡܛܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 37, 'ܐܢ ܐܢܫ ܕܝܢ ܡܢܟܘܢ ܤܒܪ ܕܢܒܝܐ ܗܘ ܐܘ ܕܪܘܚܐ ܗܘ ܢܕܥ ܗܠܝܢ ܕܟܬܒ ܐܢܐ ܠܟܘܢ ܕܦܘܩܕܢܐ ܐܢܘܢ ܕܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 38, 'ܐܢ ܐܢܫ ܕܝܢ ܠܐ ܝܕܥ ܠܐ ܢܕܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 39, 'ܛܢܘ ܗܟܝܠ ܐܚܝ ܠܡܬܢܒܝܘ ܘܠܡܡܠܠܘ ܒܠܫܢܐ ܠܐ ܬܟܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 40, 'ܟܠܡܕܡ ܕܝܢ ܒܐܤܟܡܐ ܘܒܛܟܤܐ ܢܗܘܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 1, 'ܡܘܕܥ ܐܢܐ ܠܟܘܢ ܕܝܢ ܐܚܝ ܐܘܢܓܠܝܘܢ ܕܤܒܪܬܟܘܢ ܘܩܒܠܬܘܢܝܗܝ ܘܩܡܬܘܢ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 2, 'ܘܒܗ ܚܐܝܢ ܐܢܬܘܢ ܒܐܝܕܐ ܡܠܬܐ ܤܒܪܬܟܘܢ ܐܢ ܥܗܕܝܢ ܐܢܬܘܢ ܐܢ ܠܐ ܗܘܐ ܤܪܝܩܐܝܬ ܗܝܡܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 3, 'ܐܫܠܡܬ ܠܟܘܢ ܓܝܪ ܡܢ ܠܘܩܕܡ ܐܝܟ ܡܕܡ ܕܩܒܠܬ ܕܡܫܝܚܐ ܡܝܬ ܥܠ ܐܦܝ ܚܛܗܝܢ ܐܝܟܢܐ ܕܟܬܝܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 4, 'ܘܕܐܬܩܒܪ ܘܩܡ ܠܬܠܬܐ ܝܘܡܝܢ ܐܝܟ ܕܟܬܝܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 5, 'ܘܐܬܚܙܝ ܠܟܐܦܐ ܘܒܬܪܗ ܠܬܪܥܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 6, 'ܘܒܬܪܟܢ ܐܬܚܙܝ ܠܝܬܝܪ ܡܢ ܚܡܫܡܐܐ ܐܚܝܢ ܐܟܚܕܐ ܕܤܓܝܐܐ ܡܢܗܘܢ ܩܝܡܝܢ ܐܢܘܢ ܥܕܡܐ ܠܗܫܐ ܘܡܢܗܘܢ ܕܡܟܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 7, 'ܘܡܢ ܒܬܪ ܗܠܝܢ ܐܬܚܙܝ ܠܝܥܩܘܒ ܘܒܬܪܗ ܠܫܠܝܚܐ ܟܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 8, 'ܠܚܪܬܐ ܕܝܢ ܕܟܠܗܘܢ ܐܝܟ ܕܠܝܚܛܐ ܐܬܚܙܝ ܐܦ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 9, 'ܐܢܐ ܐܢܐ ܓܝܪ ܙܥܘܪܗܘܢ ܕܫܠܝܚܐ ܘܠܐ ܫܘܐ ܐܢܐ ܕܐܬܩܪܐ ܫܠܝܚܐ ܡܛܠ ܕܪܕܦܬ ܥܕܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 10, 'ܒܛܝܒܘܬܗ ܕܝܢ ܕܐܠܗܐ ܐܝܬܝ ܡܕܡ ܕܐܝܬܝ ܘܛܝܒܘܬܗ ܕܒܝ ܠܐ ܗܘܬ ܤܪܝܩܐ ܐܠܐ ܝܬܝܪ ܡܢ ܟܠܗܘܢ ܠܐܝܬ ܠܐ ܐܢܐ ܐܠܐ ܛܝܒܘܬܗ ܕܥܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 11, 'ܐܢ ܐܢܐ ܗܟܝܠ ܘܐܢ ܗܢܘܢ ܗܟܢܐ ܡܟܪܙܝܢܢ ܘܗܟܢܐ ܗܝܡܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 12, 'ܐܢ ܕܝܢ ܡܫܝܚܐ ܡܬܟܪܙ ܕܩܡ ܡܢ ܒܝܬ ܡܝܬܐ ܐܝܟܢܐ ܐܝܬ ܒܟܘܢ ܐܢܫܐ ܕܐܡܪܝܢ ܠܝܬ ܚܝܬ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 13, 'ܘܐܢ ܚܝܬ ܡܝܬܐ ܠܝܬ ܐܦܠܐ ܡܫܝܚܐ ܩܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 14, 'ܘܐܢ ܡܫܝܚܐ ܠܐ ܩܡ ܤܪܝܩܐ ܗܝ ܟܪܘܙܘܬܢ ܤܪܝܩܐ ܐܦ ܗܝܡܢܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 15, 'ܡܫܬܟܚܝܢ ܚܢܢ ܕܝܢ ܐܦ ܤܗܕܐ ܕܓܠܐ ܕܐܠܗܐ ܕܐܤܗܕܢ ܥܠ ܐܠܗܐ ܕܐܩܝܡ ܠܡܫܝܚܐ ܟܕ ܠܐ ܐܩܝܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 16, 'ܐܢ ܡܝܬܐ ܓܝܪ ܠܐ ܩܝܡܝܢ ܐܦܠܐ ܡܫܝܚܐ ܩܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 17, 'ܘܐܢ ܡܫܝܚܐ ܠܐ ܩܡ ܒܛܠܐ ܗܝ ܗܝܡܢܘܬܟܘܢ ܘܥܕܟܝܠ ܒܚܛܗܝܟܘܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 18, 'ܘܟܒܪ ܐܦ ܐܝܠܝܢ ܕܕܡܟܘ ܒܡܫܝܚܐ ܐܒܕܘ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 19, 'ܘܐܢ ܒܗܠܝܢ ܗܘ ܚܝܐ ܒܠܚܘܕ ܡܤܒܪܝܢܢ ܒܡܫܝܚܐ ܕܘܝܢܢ ܗܘ ܡܢ ܟܠܗܘܢ ܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 20, 'ܗܫܐ ܕܝܢ ܡܫܝܚܐ ܩܡ ܡܢ ܒܝܬ ܡܝܬܐ ܘܗܘܐ ܪܫܝܬܐ ܕܕܡܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 21, 'ܘܐܝܟܢܐ ܕܒܝܕ ܒܪܢܫܐ ܗܘܐ ܡܘܬܐ ܗܟܢܐ ܐܦ ܒܝܕ ܒܪܢܫܐ ܗܘܝܐ ܚܝܬ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 22, 'ܐܝܟܢܐ ܓܝܪ ܕܒܐܕܡ ܟܠܗܘܢ ܒܢܝܢܫܐ ܡܝܬܝܢ ܗܟܢܐ ܐܦ ܒܡܫܝܚܐ ܟܠܗܘܢ ܚܐܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 23, 'ܐܢܫ ܐܢܫ ܒܛܟܤܗ ܪܫܝܬܐ ܗܘܐ ܡܫܝܚܐ ܒܬܪܟܢ ܐܝܠܝܢ ܕܕܡܫܝܚܐ ܐܢܘܢ ܒܡܐܬܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 24, 'ܘܗܝܕܝܢ ܬܗܘܐ ܚܪܬܐ ܡܐ ܕܡܫܠܡ ܡܠܟܘܬܐ ܠܐܠܗܐ ܐܒܐ ܡܐ ܕܒܛܠ ܟܠ ܪܝܫ ܘܟܠ ܫܘܠܛܢ ܘܟܠ ܚܝܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 25, 'ܥܬܝܕ ܗܘ ܓܝܪ ܕܢܡܠܟ ܥܕܡܐ ܕܢܤܝܡ ܒܥܠܕܒܒܘܗܝ ܟܠܗܘܢ ܬܚܝܬ ܪܓܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 26, 'ܘܐܚܪܝܐ ܒܥܠܕܒܒܐ ܡܬܒܛܠ ܡܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 27, 'ܟܠ ܓܝܪ ܫܥܒܕ ܬܚܝܬ ܪܓܠܘܗܝ ܡܐ ܕܐܡܪ ܕܝܢ ܕܟܠܡܕܡ ܡܫܬܥܒܕ ܠܗ ܝܕܝܥܐ ܕܤܛܪ ܡܢ ܗܘ ܕܫܥܒܕ ܠܗ ܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 28, 'ܘܡܐ ܕܐܫܬܥܒܕ ܠܗ ܟܠ ܗܝܕܝܢ ܗܘ ܒܪܐ ܢܫܬܥܒܕ ܠܗܘ ܕܫܥܒܕ ܠܗ ܟܠ ܕܢܗܘܐ ܐܠܗܐ ܟܠ ܒܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 29, 'ܘܐܠܐ ܡܢܐ ܢܥܒܕܘܢ ܗܢܘܢ ܕܥܡܕܝܢ ܚܠܦ ܡܝܬܐ ܐܢ ܡܝܬܐ ܠܐ ܩܝܡܝܢ ܡܢܐ ܥܡܕܝܢ ܚܠܦ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 30, 'ܘܠܡܢܐ ܐܦ ܚܢܢ ܒܟܠ ܫܥܐ ܒܩܢܕܝܢܘܤ ܩܝܡܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 31, 'ܝܡܐ ܐܢܐ ܒܫܘܒܗܪܟܘܢ ܐܚܝ ܕܐܝܬ ܠܝ ܒܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܕܟܠܝܘܡ ܡܐܬ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 32, 'ܐܢ ܐܝܟ ܕܒܝܬ ܒܢܝܢܫܐ ܐܫܬܕܝܬ ܠܚܝܘܬܐ ܒܐܦܤܘܤ ܡܢܐ ܐܬܗܢܝܬ ܐܢ ܡܝܬܐ ܠܐ ܩܝܡܝܢ ܢܐܟܘܠ ܘܢܫܬܐ ܡܚܪ ܓܝܪ ܡܝܬܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 33, 'ܠܐ ܬܛܥܘܢ ܡܚܒܠܢ ܪܥܝܢܐ ܒܤܝܡܐ ܫܘܥܝܬܐ ܒܝܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 34, 'ܐܥܝܪܘ ܠܒܟܘܢ ܙܕܝܩܐܝܬ ܘܠܐ ܬܚܛܘܢ ܐܝܬ ܓܝܪ ܐܢܫܐ ܕܝܕܥܬܐ ܕܐܠܗܐ ܠܝܬ ܒܗܘܢ ܠܒܗܬܬܟܘܢ ܗܘ ܐܡܪ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 35, 'ܢܐܡܪ ܐܢܫ ܡܢܟܘܢ ܕܐܝܟܢܐ ܩܝܡܝܢ ܡܝܬܐ ܘܒܐܝܢܐ ܦܓܪܐ ܐܬܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 36, 'ܤܟܠܐ ܙܪܥܐ ܕܙܪܥ ܐܢܬ ܐܢ ܠܐ ܡܐܬ ܠܐ ܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 37, 'ܘܗܘ ܡܕܡ ܕܙܪܥ ܐܢܬ ܠܐ ܗܘܐ ܗܘ ܦܓܪܐ ܕܥܬܝܕ ܠܡܗܘܐ ܙܪܥ ܐܢܬ ܐܠܐ ܦܪܕܬܐ ܥܪܛܠܝܬܐ ܕܚܛܐ ܐܘ ܕܤܥܪܐ ܐܘ ܕܫܪܟܐ ܕܙܪܥܘܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 38, 'ܐܠܗܐ ܕܝܢ ܝܗܒ ܠܗ ܦܓܪܐ ܐܝܟܢܐ ܕܨܒܐ ܘܠܚܕ ܚܕ ܡܢ ܙܪܥܘܢܐ ܦܓܪܐ ܕܟܝܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 39, 'ܠܐ ܗܘܐ ܕܝܢ ܟܠ ܦܓܪ ܫܘܐ ܐܚܪܝܢ ܗܘ ܓܝܪ ܦܓܪܐ ܕܒܪ ܐܢܫܐ ܘܐܚܪܝܢ ܕܒܥܝܪܐ ܘܐܚܪܝܢ ܕܦܪܚܬܐ ܘܐܚܪܝܢ ܕܢܘܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 40, 'ܘܐܝܬ ܦܓܪܐ ܫܡܝܢܐ ܘܐܝܬ ܦܓܪܐ ܐܪܥܢܝܐ ܐܠܐ ܐܚܪܝܢ ܗܘ ܫܘܒܚܐ ܕܫܡܝܢܐ ܘܐܚܪܝܢ ܕܐܪܥܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 41, 'ܘܐܚܪܝܢ ܗܘ ܫܘܒܚܐ ܕܫܡܫܐ ܘܐܚܪܝܢ ܫܘܒܚܐ ܕܤܗܪܐ ܘܐܚܪܝܢ ܫܘܒܚܐ ܕܟܘܟܒܐ ܘܟܘܟܒܐ ܡܢ ܟܘܟܒܐ ܡܝܬܪ ܗܘ ܒܫܘܒܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 42, 'ܗܟܢܐ ܐܦ ܚܝܬ ܡܝܬܐ ܡܙܕܪܥܝܢ ܒܚܒܠܐ ܩܝܡܝܢ ܕܠܐ ܚܒܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 43, 'ܡܙܕܪܥܝܢ ܒܨܥܪܐ ܩܝܡܝܢ ܒܫܘܒܚܐ ܡܙܕܪܥܝܢ ܒܟܪܝܗܘܬܐ ܩܝܡܝܢ ܒܚܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 44, 'ܡܙܕܪܥܝܢ ܦܓܪܐ ܢܦܫܢܝܐ ܩܐܡ ܦܓܪܐ ܪܘܚܢܝܐ ܐܝܬ ܓܝܪ ܦܓܪܐ ܕܢܦܫ ܘܐܝܬ ܦܓܪܐ ܕܪܘܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 45, 'ܗܟܢܐ ܐܦ ܟܬܝܒ ܗܘܐ ܐܕܡ ܒܪܢܫܐ ܩܕܡܝܐ ܠܢܦܫ ܚܝܐ ܘܐܕܡ ܐܚܪܝܐ ܠܪܘܚܐ ܡܚܝܢܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 46, 'ܐܠܐ ܠܐ ܗܘܐ ܠܘܩܕܡ ܪܘܚܢܝܐ ܐܠܐ ܢܦܫܢܝܐ ܘܗܝܕܝܢ ܪܘܚܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 47, 'ܒܪܢܫܐ ܩܕܡܝܐ ܥܦܪܢܐ ܕܡܢ ܐܪܥܐ ܒܪܢܫܐ ܕܬܪܝܢ ܡܪܝܐ ܡܢ ܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 48, 'ܐܝܟܢܐ ܕܐܝܬܘܗܝ ܗܘ ܥܦܪܢܐ ܗܟܢܐ ܐܦ ܥܦܪܢܐ ܘܐܝܟܢܐ ܕܐܝܬܘܗܝ ܗܘ ܕܡܢ ܫܡܝܐ ܗܟܢܐ ܐܦ ܫܡܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 49, 'ܘܐܝܟ ܕܠܒܫܢ ܕܡܘܬܐ ܕܗܘ ܕܡܢ ܥܦܪܐ ܗܟܢܐ ܢܠܒܫ ܕܡܘܬܐ ܕܗܘ ܕܡܢ ܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 50, 'ܗܕܐ ܕܝܢ ܐܡܪ ܐܢܐ ܐܚܝ ܕܒܤܪܐ ܘܕܡܐ ܡܠܟܘܬܐ ܕܫܡܝܐ ܠܡܐܪܬ ܠܐ ܡܫܟܚܝܢ ܘܠܐ ܚܒܠܐ ܝܪܬ ܠܐ ܡܬܚܒܠܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 51, 'ܗܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܐܪܙܐ ܠܘ ܟܠܢ ܢܕܡܟ ܟܠܢ ܕܝܢ ܢܬܚܠܦ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 52, 'ܚܪܝܦܐܝܬ ܐܝܟ ܪܦܦ ܥܝܢܐ ܒܩܪܢܐ ܐܚܪܝܬܐ ܟܕ ܬܩܪܐ ܘܢܩܘܡܘܢ ܡܝܬܐ ܕܠܐ ܚܒܠܐ ܘܚܢܢ ܢܬܚܠܦ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 53, 'ܥܬܝܕ ܗܘ ܓܝܪ ܗܢܐ ܕܡܬܚܒܠ ܕܢܠܒܫ ܠܐ ܡܬܚܒܠܢܘܬܐ ܘܗܢܐ ܕܡܐܬ ܕܢܠܒܫ ܠܐ ܡܝܘܬܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 54, 'ܡܐ ܕܠܒܫ ܕܝܢ ܗܢܐ ܕܡܬܚܒܠ ܠܐ ܡܬܚܒܠܢܘܬܐ ܘܗܢܐ ܕܡܐܬ ܠܐ ܡܝܘܬܘܬܐ ܗܝܕܝܢ ܬܗܘܐ ܡܠܬܐ ܗܝ ܕܟܬܝܒܐ ܕܐܬܒܠܥ ܡܘܬܐ ܒܙܟܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 55, 'ܐܝܟܘ ܥܘܩܤܟ ܡܘܬܐ ܐܘ ܐܝܟܐ ܗܝ ܙܟܘܬܟܝ ܫܝܘܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 56, 'ܥܘܩܤܗ ܕܝܢ ܕܡܘܬܐ ܚܛܝܬܐ ܗܝ ܘܚܝܠܐ ܕܚܛܝܬܐ ܢܡܘܤܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 57, 'ܛܝܒܘ ܕܝܢ ܠܐܠܗܐ ܕܝܗܒ ܠܢ ܙܟܘܬܐ ܒܝܕ ܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 58, 'ܡܟܝܠ ܐܚܝ ܘܚܒܝܒܝ ܗܘܘ ܡܫܪܪܝܢ ܘܠܐ ܬܗܘܘܢ ܡܬܙܝܥܝܢ ܐܠܐ ܗܘܘ ܡܬܝܬܪܝܢ ܒܟܠܙܒܢ ܒܥܒܕܗ ܕܡܪܝܐ ܟܕ ܝܕܥܝܢ ܐܢܬܘܢ ܕܥܡܠܟܘܢ ܠܐ ܗܘܐ ܤܪܝܩ ܒܡܪܝܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 1, 'ܥܠ ܡܕܡ ܕܝܢ ܕܡܬܟܢܫ ܠܩܕܝܫܐ ܐܝܟܢܐ ܕܦܩܕܬ ܠܥܕܬܐ ܕܓܠܛܝܐ ܗܟܢܐ ܐܦ ܐܢܬܘܢ ܥܒܕܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 2, 'ܒܟܠ ܚܕ ܒܫܒܐ ܐܢܫ ܐܢܫ ܡܢܟܘܢ ܒܒܝܬܗ ܢܗܘܐ ܤܐܡ ܘܢܛܪ ܗܘ ܡܕܡ ܕܡܛܐ ܒܐܝܕܘܗܝ ܕܠܐ ܡܐ ܕܐܬܝܬ ܗܝܕܝܢ ܢܗܘܝܢ ܓܒܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 3, 'ܘܡܐ ܕܐܬܝܬ ܠܐܝܠܝܢ ܕܓܒܝܢ ܐܢܬܘܢ ܠܗܘܢ ܐܫܕܪ ܒܐܓܪܬܐ ܕܢܘܒܠܘܢ ܛܝܒܘܬܟܘܢ ܠܐܘܪܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 4, 'ܐܢ ܕܝܢ ܫܘܐ ܗܘ ܤܘܥܪܢܐ ܕܐܦ ܐܢܐ ܐܙܠ ܥܡܝ ܢܐܙܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 5, 'ܐܬܐ ܐܢܐ ܕܝܢ ܠܘܬܟܘܢ ܡܐ ܕܥܒܪܬ ܡܢ ܡܩܕܘܢܝܐ ܥܒܪ ܐܢܐ ܠܗ ܓܝܪ ܠܡܩܕܘܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 6, 'ܘܟܒܪ ܐܦ ܠܘܬܟܘܢ ܐܩܘܐ ܐܘ ܐܤܬܐ ܠܘܬܟܘܢ ܕܐܢܬܘܢ ܬܠܘܘܢܢܝ ܠܐܬܪ ܕܐܙܠ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 7, 'ܠܐ ܓܝܪ ܨܒܐ ܐܢܐ ܕܗܫܐ ܐܝܟ ܥܒܪ ܐܘܪܚܐ ܐܚܙܝܟܘܢ ܡܤܒܪ ܐܢܐ ܓܝܪ ܕܐܘܚܪ ܙܒܢܐ ܠܘܬܟܘܢ ܐܢ ܡܪܝ ܡܦܤ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 8, 'ܡܩܘܐ ܐܢܐ ܕܝܢ ܒܐܦܤܘܤ ܥܕܡܐ ܠܦܢܛܩܘܤܛܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 9, 'ܬܪܥܐ ܓܝܪ ܪܒܐ ܐܬܦܬܚ ܠܝ ܕܡܠܐ ܤܘܥܪܢܐ ܘܤܩܘܒܠܐ ܤܓܝܐܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 10, 'ܐܢ ܕܝܢ ܢܐܬܐ ܨܐܕܝܟܘܢ ܛܝܡܬܐܘܤ ܚܙܘ ܕܕܠܐ ܕܚܠܐ ܢܗܘܐ ܠܘܬܟܘܢ ܥܒܕܐ ܓܝܪ ܕܡܪܝܐ ܦܠܚ ܐܟܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 11, 'ܠܡܐ ܗܟܝܠ ܐܢܫ ܢܫܘܛܝܘܗܝ ܐܠܐ ܠܘܐܘܗܝ ܒܫܠܡܐ ܕܢܐܬܐ ܠܘܬܝ ܡܩܘܐ ܐܢܐ ܠܗ ܓܝܪ ܥܡ ܐܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 12, 'ܡܢ ܐܦܠܘ ܕܝܢ ܐܚܝ ܤܓܝ ܒܥܝܬ ܡܢܗ ܕܢܐܬܐ ܠܘܬܟܘܢ ܥܡ ܐܚܐ ܘܟܒܪ ܠܐ ܗܘܐ ܨܒܝܢܐ ܕܢܐܬܐ ܠܘܬܟܘܢ ܐܡܬܝ ܕܝܢ ܕܢܗܘܐ ܠܗ ܐܬܪܐ ܐܬܐ ܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 13, 'ܐܬܬܥܝܪܘ ܘܩܘܡܘ ܒܗܝܡܢܘܬܐ ܐܬܓܒܪܘ ܐܬܚܤܢܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 14, 'ܘܟܠܗܝܢ ܨܒܘܬܟܘܢ ܒܚܘܒܐ ܢܗܘܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 15, 'ܒܥܐ ܐܢܐ ܕܝܢ ܡܢܟܘܢ ܐܚܝ ܥܠ ܒܝܬܐ ܕܐܤܛܦܢܐ ܡܛܠ ܕܝܕܥܝܢ ܐܢܬܘܢ ܕܗܢܘܢ ܐܢܘܢ ܪܫܝܬܐ ܕܐܟܐܝܐ ܘܤܡܘ ܢܦܫܗܘܢ ܠܬܫܡܫܬܐ ܕܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 16, 'ܕܐܦ ܐܢܬܘܢ ܬܗܘܘܢ ܡܫܬܡܥܝܢ ܠܐܝܠܝܢ ܕܗܟܢܐ ܐܢܘܢ ܘܠܟܠܢܫ ܕܠܐܐ ܥܡܢ ܘܡܥܕܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 17, 'ܚܕܐ ܐܢܐ ܕܝܢ ܒܡܐܬܝܬܗ ܕܐܤܛܦܢܐ ܘܕܦܪܛܘܢܛܘܤ ܘܕܐܟܐܝܩܘܤ ܕܡܕܡ ܕܒܨܪܬܘܢ ܠܘܬܝ ܗܢܘܢ ܡܠܝܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 18, 'ܐܢܝܚܘ ܓܝܪ ܪܘܚܝ ܕܝܠܝ ܘܕܝܠܟܘܢ ܗܘܝܬܘܢ ܗܟܝܠ ܡܫܬܘܕܥܝܢ ܠܐܝܠܝܢ ܕܗܟܢܐ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 19, 'ܫܐܠܢ ܒܫܠܡܟܘܢ ܥܕܬܐ ܟܠܗܝܢ ܕܒܐܤܝܐ ܫܐܠܝܢ ܒܫܠܡܟܘܢ ܤܓܝ ܒܡܪܢ ܐܩܠܘܤ ܘܦܪܝܤܩܠܐ ܥܡ ܥܕܬܐ ܕܒܒܝܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 20, 'ܫܐܠܝܢ ܒܫܠܡܟܘܢ ܐܚܝܢ ܟܠܗܘܢ ܫܐܠܘ ܒܫܠܡܐ ܚܕ ܕܚܕ ܒܢܘܫܩܬܐ ܩܕܝܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 21, 'ܫܠܡܐ ܒܟܬܒܬ ܐܝܕܐ ܕܝܠܝ ܕܦܘܠܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 22, 'ܡܢ ܕܠܐ ܪܚܡ ܠܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܢܗܘܐ ܚܪܡ ܡܪܢ ܐܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 23, 'ܛܝܒܘܬܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܥܡܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 24, 'ܘܚܘܒܝ ܥܡ ܟܠܟܘܢ ܒܡܫܝܚܐ ܝܫܘܥ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 1, 'ܦܘܠܘܤ ܫܠܝܚܐ ܕܝܫܘܥ ܡܫܝܚܐ ܒܨܒܝܢܗ ܕܐܠܗܐ ܘܛܝܡܬܐܘܤ ܐܚܐ ܠܥܕܬܐ ܕܐܠܗܐ ܕܐܝܬ ܒܩܘܪܢܬܘܤ ܘܠܟܠܗܘܢ ܩܕܝܫܐ ܕܐܝܬ ܒܐܟܐܝܐ ܟܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 2, 'ܛܝܒܘܬܐ ܥܡܟܘܢ ܘܫܠܡܐ ܡܢ ܐܠܗܐ ܐܒܘܢ ܘܡܢ ܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 3, 'ܡܒܪܟ ܗܘ ܐܠܗܐ ܐܒܘܗܝ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܐܒܐ ܕܪܚܡܐ ܘܐܠܗܐ ܕܟܠ ܒܘܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 4, 'ܗܘ ܕܡܒܝܐ ܠܢ ܒܟܠܗܘܢ ܐܘܠܨܢܝܢ ܕܐܦ ܚܢܢ ܢܫܟܚ ܢܒܝܐ ܠܐܝܠܝܢ ܕܒܟܠ ܐܘܠܨܢܝܢ ܐܢܘܢ ܒܗܘ ܒܘܝܐܐ ܕܚܢܢ ܡܬܒܝܐܝܢܢ ܡܢ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 5, 'ܐܝܟܢܐ ܓܝܪ ܕܡܬܝܬܪܝܢ ܒܢ ܚܫܘܗܝ ܕܡܫܝܚܐ ܗܟܢܐ ܒܝܕ ܡܫܝܚܐ ܡܬܝܬܪ ܐܦ ܒܘܝܐܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 6, 'ܐܦܢ ܕܝܢ ܡܬܐܠܨܝܢܢ ܥܠ ܐܦܝ ܒܘܝܐܟܘܢ ܗܘ ܘܥܠ ܐܦܝ ܚܝܝܟܘܢ ܡܬܐܠܨܝܢܢ ܘܐܢ ܡܬܒܝܐܝܢܢ ܡܛܠ ܕܐܢܬܘܢ ܬܬܒܝܐܘܢ ܘܬܗܘܐ ܒܟܘܢ ܚܦܝܛܘܬܐ ܕܬܤܝܒܪܘܢ ܐܢܘܢ ܠܚܫܐ ܗܢܘܢ ܕܐܦ ܚܢܢ ܚܫܝܢܢ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 7, 'ܘܤܒܪܢ ܕܥܠܝܟܘܢ ܫܪܝܪ ܗܘ ܝܕܥܝܢܢ ܓܝܪ ܕܐܢ ܫܘܬܦܝܢ ܐܢܬܘܢ ܒܚܫܐ ܫܘܬܦܝܢ ܐܢܬܘܢ ܐܦ ܒܒܘܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 8, 'ܨܒܝܢܢ ܕܝܢ ܕܬܕܥܘܢ ܐܚܝܢ ܥܠ ܐܘܠܨܢܐ ܕܗܘܐ ܠܢ ܒܐܤܝܐ ܕܪܘܪܒܐܝܬ ܐܬܐܠܨܢ ܝܬܝܪ ܡܢ ܚܝܠܢ ܥܕܡܐ ܕܩܪܝܒܝܢ ܗܘܘ ܚܝܝܢ ܠܡܬܛܠܩܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 9, 'ܘܥܠ ܢܦܫܢ ܦܤܩܢ ܡܘܬܐ ܕܠܐ ܢܗܘܐ ܠܢ ܬܘܟܠܢܐ ܥܠ ܢܦܫܢ ܐܠܐ ܥܠ ܐܠܗܐ ܗܘ ܕܡܩܝܡ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 10, 'ܗܘ ܕܡܢ ܡܘܬܐ ܚܤܝܢܐ ܦܪܩܢ ܘܬܘܒ ܡܤܒܪܝܢܢ ܕܦܪܩ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 11, 'ܒܡܥܕܪܢܘܬܐ ܕܒܥܘܬܟܘܢ ܕܥܠ ܐܦܝܢ ܕܬܗܘܐ ܡܘܗܒܬܗ ܕܠܘܬܢ ܛܝܒܘܬܐ ܕܥܒܝܕܐ ܒܐܦܝ ܤܓܝܐܐ ܘܤܓܝܐܐ ܢܘܕܘܢ ܠܗ ܥܠ ܐܦܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 12, 'ܫܘܒܗܪܢ ܓܝܪ ܗܢܘ ܤܗܕܘܬܐ ܕܪܥܝܢܢ ܕܒܦܫܝܛܘܬܐ ܘܒܕܟܝܘܬܐ ܘܒܛܝܒܘܬܐ ܕܐܠܗܐ ܐܬܗܦܟܢ ܒܥܠܡܐ ܘܠܐ ܒܚܟܡܬܐ ܕܦܓܪܐ ܘܝܬܝܪܐܝܬ ܠܘܬܟܘܢ ܕܝܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 13, 'ܠܐ ܗܘܐ ܐܚܪܢܝܢ ܟܬܒܝܢܢ ܠܟܘܢ ܐܠܐ ܐܝܠܝܢ ܕܝܕܥܝܢ ܐܢܬܘܢ ܐܦ ܡܫܬܘܕܥܝܢ ܐܢܬܘܢ ܬܟܝܠ ܐܢܐ ܕܝܢ ܕܥܕܡܐ ܠܐܚܪܝܬܐ ܬܫܬܘܕܥܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 14, 'ܐܝܟܢܐ ܕܐܦ ܐܫܬܘܕܥܬܘܢ ܩܠܝܠ ܡܢ ܤܓܝ ܕܫܘܒܗܪܟܘܢ ܚܢܢ ܐܝܟ ܕܐܦ ܐܢܬܘܢ ܕܝܠܢ ܒܝܘܡܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 15, 'ܘܒܗܢܐ ܬܘܟܠܢܐ ܨܒܐ ܗܘܝܬ ܡܢ ܩܕܝܡ ܕܐܬܐ ܠܘܬܟܘܢ ܕܐܥܝܦܐܝܬ ܬܩܒܠܘܢ ܛܝܒܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 16, 'ܘܐܥܒܪ ܥܠܝܟܘܢ ܠܡܩܕܘܢܝܐ ܘܬܘܒ ܡܢ ܡܩܕܘܢܝܐ ܠܘܬܟܘܢ ܐܬܐ ܘܐܢܬܘܢ ܬܠܘܘܢܢܝ ܠܝܗܘܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 17, 'ܗܕܐ ܗܟܝܠ ܕܐܬܪܥܝܬ ܠܡܐ ܐܝܟ ܡܤܪܗܒܐ ܐܬܪܥܝܬ ܐܘ ܕܠܡܐ ܕܒܤܪ ܐܢܝܢ ܐܝܠܝܢ ܕܡܬܪܥܐ ܐܢܐ ܡܛܠ ܕܘܠܐ ܗܘܐ ܕܢܗܘܐ ܒܗܝܢ ܐܝܢ ܐܝܢ ܘܠܐ ܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 18, 'ܡܗܝܡܢ ܗܘ ܐܠܗܐ ܕܠܐ ܗܘܬ ܡܠܬܢ ܕܠܘܬܟܘܢ ܐܝܢ ܘܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 19, 'ܒܪܗ ܓܝܪ ܕܐܠܗܐ ܝܫܘܥ ܡܫܝܚܐ ܗܘ ܕܒܐܝܕܢ ܐܬܟܪܙ ܠܟܘܢ ܒܝ ܘܒܤܠܘܢܘܤ ܘܒܛܝܡܬܐܘܤ ܠܐ ܗܘܐ ܐܝܢ ܘܠܐ ܐܠܐ ܐܝܢ ܗܘܐ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 20, 'ܟܠܗܘܢ ܓܝܪ ܡܘܠܟܢܘܗܝ ܕܐܠܗܐ ܒܗ ܗܘ ܒܡܫܝܚܐ ܐܝܢ ܗܘܘ ܡܛܠ ܗܢܐ ܒܐܝܕܗ ܝܗܒܝܢܢ ܐܡܝܢ ܠܬܫܒܘܚܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 21, 'ܐܠܗܐ ܗܘ ܕܝܢ ܡܫܪܪ ܠܢ ܥܡܟܘܢ ܒܡܫܝܚܐ ܗܘ ܕܗܘ ܡܫܚܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 22, 'ܘܚܬܡܢ ܘܝܗܒ ܪܗܒܘܢܐ ܕܪܘܚܗ ܒܠܒܘܬܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 23, 'ܐܢܐ ܕܝܢ ܠܐܠܗܐ ܡܤܗܕ ܐܢܐ ܥܠ ܢܦܫܝ ܕܡܛܠ ܕܚܐܤ ܐܢܐ ܥܠܝܟܘܢ ܠܐ ܐܬܝܬ ܠܩܘܪܢܬܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 24, 'ܠܐ ܗܘܐ ܡܛܠ ܕܡܪܝ ܗܝܡܢܘܬܟܘܢ ܚܢܢ ܐܠܐ ܡܥܕܪܢܐ ܚܢܢ ܕܚܕܘܬܟܘܢ ܒܗܝܡܢܘܬܐ ܗܘ ܓܝܪ ܩܝܡܝܢ ܐܢܬܘܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 1, 'ܕܢܬ ܕܝܢ ܗܕܐ ܒܢܦܫܝ ܕܠܐ ܬܘܒ ܒܟܪܝܘܬܐ ܐܬܐ ܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 2, 'ܐܢ ܐܢܐ ܓܝܪ ܡܟܪܐ ܐܢܐ ܠܟܘܢ ܡܢܘ ܢܚܕܝܢܝ ܐܠܐ ܗܘ ܕܐܢܐ ܐܟܪܝܬ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 3, 'ܘܕܟܬܒܬ ܠܟܘܢ ܗܝ ܗܕܐ ܕܠܐ ܟܕ ܐܬܐ ܢܟܪܘܢ ܠܝ ܗܢܘܢ ܐܝܠܝܢ ܕܘܠܐ ܗܘܐ ܠܗܘܢ ܕܗܢܘܢ ܢܚܕܘܢܢܝ ܬܟܝܠ ܐܢܐ ܕܝܢ ܥܠ ܟܠܟܘܢ ܕܚܕܘܬܝ ܕܟܠܟܘܢ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 4, 'ܘܡܢ ܐܘܠܨܢܐ ܪܒܐ ܘܡܢ ܐܢܘܤܝܐ ܕܠܒܐ ܟܬܒܬ ܠܟܘܢ ܗܠܝܢ ܒܕܡܥܐ ܤܓܝܐܬܐ ܠܐ ܡܛܠ ܕܬܟܪܐ ܠܟܘܢ ܐܠܐ ܡܛܠ ܕܬܕܥܘܢ ܚܘܒܐ ܝܬܝܪܐ ܕܐܝܬ ܠܝ ܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 5, 'ܐܢ ܕܝܢ ܐܢܫ ܐܟܪܝ ܠܐ ܗܘܐ ܠܝ ܐܟܪܝ ܐܠܐ ܒܨܝܪܐ ܩܠܝܠ ܠܟܠܟܘܢ ܕܠܐ ܬܐܩܪ ܡܠܬܐ ܥܠܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 6, 'ܟܕܘ ܠܗ ܕܝܢ ܠܗܢܐ ܟܐܬܐ ܕܡܢ ܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 7, 'ܘܡܟܝܠ ܐܚܪܢܝܐܝܬ ܘܠܐ ܕܬܫܒܩܘܢ ܠܗ ܘܬܒܝܐܘܢܗ ܕܠܡܐ ܒܟܪܝܘܬܐ ܝܬܝܪܬܐ ܢܬܒܠܥ ܠܗ ܗܘ ܡܢ ܕܗܟܢܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 8, 'ܡܛܠ ܗܢܐ ܒܥܐ ܐܢܐ ܡܢܟܘܢ ܕܬܫܪܪܘܢ ܒܗ ܚܘܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 9, 'ܡܛܠ ܗܢܐ ܓܝܪ ܟܬܒܬ ܐܦ ܕܐܕܥ ܒܢܤܝܢܐ ܐܢ ܒܟܠܡܕܡ ܡܫܬܡܥܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 10, 'ܠܡܢ ܕܐܢܬܘܢ ܕܝܢ ܫܒܩܝܢ ܐܢܬܘܢ ܐܦ ܐܢܐ ܐܦ ܐܢܐ ܓܝܪ ܡܕܡ ܕܫܒܩܬ ܠܡܢ ܕܫܒܩܬ ܡܛܠܬܟܘܢ ܗܘ ܫܒܩܬ ܒܦܪܨܘܦܗ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 11, 'ܕܠܐ ܢܥܠܒܢ ܤܛܢܐ ܝܕܥܝܢܢ ܓܝܪ ܡܚܫܒܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 12, 'ܟܕ ܐܬܝܬ ܕܝܢ ܠܛܪܘܐܤ ܒܤܒܪܬܗ ܕܡܫܝܚܐ ܘܐܬܦܬܚ ܠܝ ܬܪܥܐ ܒܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 13, 'ܠܐ ܗܘܐ ܠܝ ܢܝܚܐ ܒܪܘܚܝ ܕܠܐ ܐܫܟܚܬ ܠܛܛܘܤ ܐܚܝ ܐܠܐ ܫܪܝܬ ܐܢܘܢ ܘܢܦܩܬ ܠܝ ܠܡܩܕܘܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 14, 'ܛܝܒܘ ܕܝܢ ܠܐܠܗܐ ܕܒܟܠ ܙܒܢ ܚܙܬܐ ܥܒܕ ܠܢ ܒܡܫܝܚܐ ܘܓܠܐ ܒܢ ܪܝܚܐ ܕܝܕܥܬܗ ܒܟܠ ܐܬܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 15, 'ܪܝܚܐ ܚܢܢ ܓܝܪ ܒܤܝܡܐ ܒܡܫܝܚܐ ܠܐܠܗܐ ܒܐܝܠܝܢ ܕܚܐܝܢ ܘܒܐܝܠܝܢ ܕܐܒܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 16, 'ܠܐܝܠܝܢ ܕܪܝܚܐ ܕܡܘܬܐ ܠܡܘܬܐ ܘܠܐܝܠܝܢ ܕܪܝܚܐ ܕܚܝܐ ܠܚܝܐ ܘܠܗܠܝܢ ܡܢܘ ܢܫܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 17, 'ܠܐ ܗܘܝܢ ܓܝܪ ܐܝܟ ܫܪܟܐ ܕܡܡܙܓܝܢ ܡܠܘܗܝ ܕܐܠܗܐ ܐܠܐ ܐܝܟ ܕܒܫܪܪܐ ܘܐܝܟ ܕܡܢ ܐܠܗܐ ܩܕܡ ܐܠܗܐ ܒܡܫܝܚܐ ܡܡܠܠܝܢܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 1, 'ܡܫܪܝܢܢ ܠܢ ܬܘܒ ܡܢ ܕܪܝܫ ܕܢܚܘܝܟܘܢ ܡܢܐ ܚܢܢ ܐܘ ܕܠܡܐ ܤܢܝܩܝܢܢ ܐܝܟ ܐܚܪܢܐ ܕܐܓܪܬܐ ܕܦܘܩܕܐ ܢܬܟܬܒܢ ܠܟܘܢ ܥܠܝܢ ܐܘ ܕܐܢܬܘܢ ܬܟܬܒܘܢ ܬܦܩܕܘܢ ܥܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 2, 'ܐܓܪܬܢ ܕܝܢ ܕܝܠܢ ܐܢܬܘܢ ܐܢܘܢ ܕܟܬܝܒܐ ܒܠܒܢ ܘܝܕܝܥܐ ܘܡܬܩܪܝܐ ܡܢ ܟܠܢܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 3, 'ܝܕܥܝܬܘܢ ܓܝܪ ܕܐܓܪܬܗ ܐܢܬܘܢ ܕܡܫܝܚܐ ܕܐܫܬܡܫܬ ܡܢܢ ܕܟܬܝܒܐ ܠܐ ܒܕܝܘܬܐ ܐܠܐ ܒܪܘܚܐ ܕܐܠܗܐ ܚܝܐ ܠܐ ܒܠܘܚܐ ܕܟܐܦܐ ܐܠܐ ܒܠܘܚܐ ܕܠܒܐ ܕܒܤܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 4, 'ܬܘܟܠܢܐ ܕܝܢ ܗܟܢܐ ܐܝܬ ܠܢ ܒܡܫܝܚܐ ܠܘܬ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 5, 'ܠܘ ܕܚܢܢ ܤܦܩܝܢܢ ܕܢܬܪܥܐ ܡܕܡ ܐܝܟ ܕܡܢ ܢܦܫܢ ܐܠܐ ܚܝܠܢ ܡܢ ܐܠܗܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 6, 'ܗܘ ܕܐܫܘܝܢ ܕܢܗܘܐ ܡܫܡܫܢܐ ܕܕܝܬܩܐ ܚܕܬܐ ܠܐ ܒܟܬܒܐ ܐܠܐ ܒܪܘܚܐ ܟܬܒܐ ܓܝܪ ܩܛܠ ܪܘܚܐ ܕܝܢ ܡܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 7, 'ܐܢ ܕܝܢ ܬܫܡܫܬܐ ܕܡܘܬܐ ܒܟܬܒܐ ܐܬܪܫܡܬ ܒܟܐܦܐ ܘܗܘܬ ܒܬܫܒܘܚܬܐ ܐܝܟܢܐ ܕܠܐ ܢܫܟܚܘܢ ܒܢܝ ܐܝܤܪܝܠ ܠܡܚܪ ܒܦܪܨܘܦܗ ܕܡܘܫܐ ܡܛܠ ܫܘܒܚܐ ܕܦܪܨܘܦܗ ܗܘ ܕܐܬܒܛܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 8, 'ܐܝܟܢܐ ܗܟܝܠ ܬܫܡܫܬܐ ܕܪܘܚܐ ܠܐ ܝܬܝܪܐܝܬ ܬܗܘܐ ܒܫܘܒܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 9, 'ܐܢ ܓܝܪ ܠܬܫܡܫܬܐ ܕܚܘܝܒܐ ܗܘܐ ܫܘܒܚܐ ܚܕ ܟܡܐ ܬܬܝܬܪ ܬܫܡܫܬܐ ܕܙܕܝܩܘܬܐ ܒܫܘܒܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 10, 'ܐܝܟ ܗܘ ܓܝܪ ܕܐܦܠܐ ܡܫܒܚܐ ܗܝ ܕܐܫܬܒܚܬ ܒܦܘܚܡܐ ܕܗܢܐ ܫܘܒܚܐ ܡܝܬܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 11, 'ܐܢ ܓܝܪ ܡܕܡ ܕܐܬܒܛܠ ܒܫܘܒܚܐ ܗܘܐ ܝܬܝܪܐܝܬ ܐܝܢܐ ܕܡܩܘܐ ܒܫܘܒܚܐ ܢܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 12, 'ܡܛܠ ܕܐܝܬ ܠܢ ܗܟܝܠ ܗܢܐ ܤܒܪܐ ܝܬܝܪܐܝܬ ܒܓܠܐ ܥܝܢ ܡܬܕܒܪܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 13, 'ܘܠܐ ܐܝܟ ܡܘܫܐ ܕܪܡܐ ܗܘܐ ܬܚܦܝܬܐ ܥܠ ܐܦܘܗܝ ܕܠܐ ܢܚܘܪܘܢ ܒܢܝ ܐܝܤܪܝܠ ܒܫܘܠܡܗ ܕܗܘ ܕܡܬܒܛܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 14, 'ܐܠܐ ܐܬܥܘܪܘ ܒܡܕܥܝܗܘܢ ܥܕܡܐ ܓܝܪ ܠܝܘܡܢܐ ܐܡܬܝ ܕܕܝܬܩܐ ܥܬܝܩܬܐ ܡܬܩܪܝܐ ܗܝ ܗܝ ܬܚܦܝܬܐ ܩܝܡܐ ܥܠܝܗܘܢ ܘܠܐ ܡܬܓܠܝܐ ܕܒܡܫܝܚܐ ܗܘ ܡܬܒܛܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 15, 'ܘܥܕܡܐ ܠܝܘܡܢܐ ܐܡܬܝ ܕܡܬܩܪܐ ܡܘܫܐ ܬܚܦܝܬܐ ܥܠ ܠܒܗܘܢ ܪܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 16, 'ܘܐܡܬܝ ܕܐܢܫ ܡܢܗܘܢ ܢܬܦܢܐ ܠܘܬ ܡܪܝܐ ܡܫܬܩܠܐ ܡܢܗ ܬܚܦܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 17, 'ܡܪܝܐ ܕܝܢ ܗܘܝܘ ܪܘܚܐ ܘܐܬܪ ܕܪܘܚܗ ܕܡܪܝܐ ܚܐܪܘܬܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 18, 'ܚܢܢ ܕܝܢ ܟܠܢ ܒܐܦܐ ܓܠܝܬܐ ܬܫܒܘܚܬܗ ܕܡܪܝܐ ܐܝܟ ܕܒܡܚܙܝܬܐ ܚܙܝܢܢ ܘܠܗ ܠܕܡܘܬܐ ܡܫܬܚܠܦܝܢܢ ܡܢ ܫܘܒܚܐ ܠܫܘܒܚܐ ܐܝܟ ܕܡܢ ܡܪܝܐ ܪܘܚܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 1, 'ܡܛܠ ܗܢܐ ܠܐ ܡܐܢܐ ܠܢ ܒܬܫܡܫܬܐ ܗܕܐ ܕܐܚܝܕܝܢܢ ܐܝܟ ܪܚܡܐ ܕܗܘܘ ܥܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 2, 'ܐܠܐ ܐܤܠܝܢܢ ܟܤܝܬܗ ܕܒܗܬܬܐ ܘܠܐ ܡܗܠܟܝܢܢ ܒܚܪܥܘܬܐ ܘܠܐ ܢܟܠܝܢܢ ܡܠܬܗ ܕܐܠܗܐ ܐܠܐ ܒܓܠܝܘܬܐ ܕܫܪܪܐ ܡܚܘܝܢܢ ܢܦܫܢ ܠܟܠܗܘܢ ܪܥܝܢܐ ܕܒܢܝܢܫܐ ܩܕܡ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 3, 'ܘܐܢ ܕܝܢ ܡܟܤܝ ܗܘ ܐܘܢܓܠܝܘܢ ܕܝܠܢ ܠܐܝܠܝܢ ܕܐܒܕܝܢ ܗܘ ܡܟܤܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 4, 'ܐܝܠܝܢ ܕܐܠܗܗ ܕܥܠܡܐ ܗܢܐ ܥܘܪ ܡܕܥܝܗܘܢ ܥܠ ܕܠܐ ܡܗܝܡܢܝܢ ܕܠܐ ܢܕܢܚ ܠܗܘܢ ܢܘܗܪܗ ܕܐܘܢܓܠܝܘܢ ܕܫܘܒܚܗ ܕܡܫܝܚܐ ܕܗܘܝܘ ܕܡܘܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 5, 'ܠܐ ܗܘܐ ܓܝܪ ܢܦܫܢ ܡܟܪܙܝܢܢ ܐܠܐ ܠܡܫܝܚܐ ܝܫܘܥ ܡܪܢ ܠܢܦܫܢ ܕܝܢ ܕܥܒܕܝܟܘܢ ܚܢܢ ܡܛܠ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 6, 'ܡܛܠ ܕܐܠܗܐ ܗܘ ܕܐܡܪ ܕܡܢ ܚܫܘܟܐ ܢܘܗܪܐ ܢܕܢܚ ܗܘ ܕܢܚ ܒܠܒܘܬܢ ܕܢܬܢܗܪ ܒܝܕܥܬܐ ܕܫܘܒܚܗ ܕܐܠܗܐ ܒܦܪܨܘܦܗ ܕܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 7, 'ܐܝܬ ܠܢ ܕܝܢ ܤܝܡܬܐ ܗܕܐ ܒܡܐܢܐ ܕܚܨܦܐ ܕܪܒܘܬܐ ܕܚܝܠܐ ܡܢ ܐܠܗܐ ܬܗܘܐ ܘܠܐ ܡܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 8, 'ܒܟܠܡܕܡ ܓܝܪ ܡܬܐܠܨܝܢܢ ܐܠܐ ܠܐ ܡܬܚܢܩܝܢܢ ܡܬܛܪܦܝܢܢ ܐܠܐ ܠܐ ܚܝܒܝܢ ܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 9, 'ܡܬܪܕܦܝܢܢ ܐܠܐ ܠܐ ܡܫܬܒܩܝܢܢ ܡܤܬܚܦܝܢܢ ܐܠܐ ܠܐ ܐܒܕܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 10, 'ܒܟܠܙܒܢ ܡܝܬܘܬܗ ܕܝܫܘܥ ܒܦܓܪܝܢ ܫܩܝܠܝܢܢ ܕܐܦ ܚܝܘܗܝ ܕܝܫܘܥ ܒܦܓܪܝܢ ܢܬܓܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 11, 'ܐܢ ܚܢܢ ܓܝܪ ܚܝܐ ܠܡܘܬܐ ܡܫܬܠܡܝܢܢ ܡܛܠ ܝܫܘܥ ܗܟܢܐ ܐܦ ܚܝܘܗܝ ܕܝܫܘܥ ܢܬܓܠܘܢ ܒܦܓܪܢ ܗܢܐ ܕܡܐܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 12, 'ܗܫܐ ܡܘܬܐ ܒܢ ܡܬܚܦܛ ܘܚܝܐ ܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 13, 'ܐܦ ܚܢܢ ܗܟܝܠ ܕܚܕܐ ܗܝ ܪܘܚܐ ܕܗܝܡܢܘܬܐ ܐܝܬ ܠܢ ܐܝܟ ܕܟܬܝܒ ܕܗܝܡܢܬ ܡܛܠ ܗܢܐ ܐܦ ܡܠܠܬ ܡܗܝܡܢܝܢܢ ܡܛܠ ܗܢܐ ܐܦ ܡܡܠܠܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 14, 'ܘܝܕܥܝܢܢ ܕܗܘ ܡܢ ܕܐܩܝܡ ܠܡܪܢ ܝܫܘܥ ܐܦ ܠܢ ܒܝܕ ܝܫܘܥ ܢܩܝܡ ܘܢܩܪܒܢ ܥܡܟܘܢ ܠܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 15, 'ܟܠܡܕܡ ܓܝܪ ܡܛܠܬܟܘܢ ܗܘ ܕܟܕ ܡܬܝܬܪܐ ܛܝܒܘܬܐ ܒܝܕ ܤܓܝܐܐ ܬܤܓܐ ܬܘܕܝܬܐ ܠܫܘܒܚܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 16, 'ܡܛܠ ܗܢܐ ܠܐ ܡܐܢܐ ܠܢ ܐܦ ܐܢ ܒܪܢܫܢ ܓܝܪ ܒܪܝܐ ܡܬܚܒܠ ܐܠܐ ܕܡܢ ܠܓܘ ܡܬܚܕܬ ܝܘܡ ܡܢ ܝܘܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 17, 'ܐܘܠܨܢܗ ܓܝܪ ܕܙܒܢܐ ܗܢܐ ܟܕ ܛܒ ܙܥܘܪ ܘܩܠܝܠ ܫܘܒܚܐ ܪܒܐ ܕܠܐ ܤܟܐ ܠܥܠܡ ܥܠܡܝܢ ܡܛܝܒ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 18, 'ܕܠܐ ܚܕܝܢܢ ܒܗܠܝܢ ܕܡܬܚܙܝܢ ܐܠܐ ܒܗܠܝܢ ܕܠܐ ܡܬܚܙܝܢ ܕܡܬܚܙܝܢ ܓܝܪ ܕܙܒܢܐ ܐܢܝܢ ܕܠܐ ܡܬܚܙܝܢ ܕܝܢ ܕܠܥܠܡ ܐܢܝܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 1, 'ܝܕܥܝܢܢ ܓܝܪ ܕܐܢ ܒܝܬܢ ܕܒܐܪܥܐ ܗܢܐ ܕܦܓܪܐ ܢܫܬܪܐ ܐܠܐ ܐܝܬ ܠܢ ܒܢܝܢܐ ܕܡܢ ܐܠܗܐ ܒܝܬܐ ܕܠܐ ܒܥܒܕ ܐܝܕܝܐ ܒܫܡܝܐ ܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 2, 'ܐܦ ܥܠ ܗܕܐ ܓܝܪ ܡܬܬܢܚܝܢܢ ܘܤܘܝܢܢ ܕܢܠܒܫ ܒܝܬܢ ܕܡܢ ܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 3, 'ܐܠܐ ܐܦ ܡܐ ܕܠܒܫܢ ܢܫܬܟܚ ܠܢ ܥܪܛܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 4, 'ܟܕ ܐܝܬܝܢ ܓܝܪ ܗܫܐ ܒܗܢܐ ܒܝܬܐ ܡܬܬܢܚܝܢܢ ܡܢ ܝܘܩܪܗ ܘܠܐ ܨܒܝܢܢ ܠܡܫܠܚܗ ܐܠܐ ܕܢܠܒܫ ܠܥܠ ܡܢܗ ܕܬܬܒܠܥ ܡܝܬܘܬܗ ܒܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 5, 'ܘܕܡܥܬܕ ܠܢ ܠܗ ܠܗܕܐ ܐܠܗܐ ܗܘ ܗܘ ܕܝܗܒ ܠܢ ܪܗܒܘܢܐ ܕܪܘܚܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 6, 'ܡܛܠ ܕܝܕܥܝܢܢ ܗܟܝܠ ܘܡܦܤܝܢܢ ܕܟܡܐ ܕܒܦܓܪܐ ܫܪܝܢܢ ܥܢܝܕܝܢܢ ܡܢ ܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 7, 'ܒܗܝܡܢܘܬܐ ܗܘ ܓܝܪ ܡܗܠܟܝܢܢ ܘܠܐ ܒܚܙܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 8, 'ܡܛܠ ܗܢܐ ܬܟܝܠܝܢܢ ܘܡܤܘܚܝܢܢ ܕܢܥܢܕ ܡܢ ܦܓܪܐ ܘܢܗܘܐ ܠܘܬ ܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 9, 'ܘܡܬܚܦܛܝܢܢ ܕܐܢ ܥܢܘܕܐ ܚܢܢ ܘܐܢ ܥܡܘܪܐ ܠܗ ܗܘܝܢ ܫܦܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 10, 'ܟܠܢ ܓܝܪ ܥܬܝܕܝܢܢ ܠܡܩܡ ܩܕܡ ܒܝܡ ܕܡܫܝܚܐ ܕܢܬܦܪܥ ܐܢܫ ܐܢܫ ܒܦܓܪܗ ܡܕܡ ܕܥܒܝܕ ܠܗ ܐܢ ܕܛܒ ܘܐܢ ܕܒܝܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 11, 'ܡܛܠ ܕܝܕܥܝܢܢ ܗܟܝܠ ܕܚܠܬܗ ܕܡܪܢ ܠܒܢܝܢܫܐ ܗܘ ܡܦܝܤܝܢܢ ܠܐܠܗܐ ܕܝܢ ܓܠܝܢܢ ܤܒܪ ܐܢܐ ܕܝܢ ܕܐܦ ܠܡܕܥܝܟܘܢ ܕܝܠܟܘܢ ܓܠܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 12, 'ܠܐ ܗܘܐ ܬܘܒ ܢܦܫܢ ܡܫܒܚܝܢܢ ܠܟܘܢ ܐܠܐ ܥܠܬܐ ܗܘ ܝܗܒܝܢܢ ܠܟܘܢ ܕܗܘܝܬܘܢ ܡܫܬܒܗܪܝܢ ܒܢ ܠܘܬ ܗܢܘܢ ܕܒܐܦܐ ܗܘ ܡܫܬܒܗܪܝܢ ܘܠܐ ܒܠܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 13, 'ܐܢ ܓܝܪ ܫܛܝܢܢ ܠܐܠܗܐ ܘܐܢ ܬܩܢܝܢܢ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 14, 'ܚܘܒܗ ܓܝܪ ܕܡܫܝܚܐ ܐܠܨ ܠܢ ܕܪܢܝܢܢ ܗܕܐ ܕܚܕ ܚܠܦ ܟܠܢܫ ܡܝܬ ܡܕܝܢ ܟܠܢܫ ܡܝܬ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 15, 'ܘܚܠܦ ܟܠܢܫ ܗܘ ܡܝܬ ܕܐܝܠܝܢ ܕܚܝܝܢ ܠܐ ܠܢܦܫܗܘܢ ܢܚܘܢ ܐܠܐ ܠܗܘ ܐܝܢܐ ܕܥܠ ܐܦܝܗܘܢ ܡܝܬ ܘܩܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 16, 'ܘܡܟܝܠ ܚܢܢ ܠܐܢܫ ܒܦܓܪ ܠܐ ܝܕܥܝܢܢ ܘܐܢ ܝܕܥܢ ܒܦܓܪ ܠܡܫܝܚܐ ܐܠܐ ܡܢ ܗܫܐ ܠܐ ܝܕܥܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 17, 'ܟܠ ܡܢ ܕܒܡܫܝܚܐ ܗܘ ܗܟܝܠ ܒܪܝܬܐ ܗܘ ܚܕܬܐ ܥܬܝܩܬܐ ܥܒܪ ܠܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 18, 'ܘܟܠ ܡܕܡ ܗܘܐ ܚܕܬܐ ܡܢ ܐܠܗܐ ܗܘ ܕܪܥܝܢ ܠܗ ܒܡܫܝܚܐ ܘܝܗܒ ܠܢ ܬܫܡܫܬܐ ܕܬܪܥܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 19, 'ܐܠܗܐ ܗܘܐ ܓܝܪ ܒܡܫܝܚܐ ܗܘ ܕܪܥܝ ܠܥܠܡܐ ܥܡ ܪܒܘܬܗ ܘܠܐ ܚܫܒ ܠܗܘܢ ܚܛܗܝܗܘܢ ܘܤܡ ܒܢ ܕܝܠܢ ܡܠܬܐ ܕܬܪܥܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 20, 'ܐܝܙܓܕܐ ܚܢܢ ܗܟܝܠ ܚܠܦ ܡܫܝܚܐ ܘܐܝܟ ܗܘ ܕܐܠܗܐ ܒܥܐ ܡܢܟܘܢ ܒܐܝܕܢ ܚܠܦ ܡܫܝܚܐ ܗܟܝܠ ܒܥܝܢܢ ܐܬܪܥܘ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 21, 'ܗܘ ܓܝܪ ܕܠܐ ܝܕܥ ܗܘܐ ܚܛܝܬܐ ܡܛܠܬܟܘܢ ܚܛܝܬܐ ܥܒܕܗ ܕܚܢܢ ܢܗܘܐ ܒܗ ܙܕܝܩܘܬܐ ܕܐܠܗܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 1, 'ܘܐܝܟ ܡܥܕܪܢܐ ܒܥܝܢܢ ܡܢܟܘܢ ܕܠܐ ܬܤܬܪܩ ܒܟܘܢ ܛܝܒܘܬܐ ܕܐܠܗܐ ܕܩܒܠܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 2, 'ܐܡܪ ܓܝܪ ܕܒܙܒܢܐ ܡܩܒܠܐ ܥܢܝܬܟ ܘܒܝܘܡܐ ܕܚܝܐ ܥܕܪܬܟ ܗܐ ܗܫܐ ܙܒܢܐ ܡܩܒܠܐ ܘܗܐ ܗܫܐ ܝܘܡܐ ܕܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 3, 'ܠܡܐ ܒܡܕܡ ܬܬܠܘܢ ܠܐܢܫ ܥܠܬܐ ܕܬܘܩܠܬܐ ܕܠܐ ܢܗܘܐ ܡܘܡܐ ܒܬܫܡܫܬܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 4, 'ܐܠܐ ܒܟܠ ܡܕܡ ܢܚܘܐ ܢܦܫܢ ܕܡܫܡܫܢܐ ܚܢܢ ܕܐܠܗܐ ܒܡܤܝܒܪܢܘܬܐ ܤܓܝܐܬܐ ܒܐܘܠܨܢܐ ܒܐܢܢܩܤ ܒܚܒܘܫܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 5, 'ܒܢܓܕܐ ܒܐܤܘܪܐ ܒܫܓܘܫܝܐ ܒܠܐܘܬܐ ܒܫܗܪܐ ܒܨܘܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 6, 'ܒܕܟܝܘܬܐ ܒܝܕܥܬܐ ܒܢܓܝܪܘܬ ܪܘܚܐ ܒܒܤܝܡܘܬܐ ܒܪܘܚܐ ܕܩܘܕܫܐ ܒܚܘܒܐ ܕܠܐ ܢܟܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 7, 'ܒܡܠܬܐ ܕܩܘܫܬܐ ܒܚܝܠܐ ܕܐܠܗܐ ܒܙܝܢܐ ܕܙܕܝܩܘܬܐ ܕܒܝܡܝܢܐ ܘܒܤܡܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 8, 'ܒܫܘܒܚܐ ܘܒܨܥܪܐ ܒܩܘܠܤܐ ܘܒܓܘܢܝܐ ܐܝܟ ܡܛܥܝܢܐ ܘܫܪܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 9, 'ܐܝܟ ܠܐ ܝܕܝܥܐ ܘܝܕܝܥܝܢܢ ܐܝܟ ܡܝܬܝܢܢ ܘܗܐ ܚܝܝܢ ܚܢܢ ܐܝܟ ܕܡܬܪܕܝܢܢ ܘܠܐ ܡܝܬܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 10, 'ܐܝܟ ܕܟܪܝܐ ܠܢ ܘܒܟܠܙܒܢ ܚܕܝܢܢ ܐܝܟ ܡܤܟܢܐ ܘܠܤܓܝܐܐ ܡܥܬܪܝܢܢ ܐܝܟ ܕܡܕܡ ܠܝܬ ܠܢ ܘܟܠ ܡܕܡ ܐܚܝܕܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 11, 'ܦܘܡܢ ܦܬܝܚ ܗܘ ܠܘܬܟܘܢ ܩܘܪܢܬܝܐ ܘܠܒܢ ܪܘܝܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 12, 'ܠܐ ܐܠܝܨܝܬܘܢ ܒܢ ܐܠܝܨܝܬܘܢ ܕܝܢ ܒܪܚܡܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 13, 'ܐܝܟ ܕܠܒܢܝܐ ܕܝܢ ܐܡܪ ܐܢܐ ܦܘܪܥܘܢܝ ܚܘܒܠܝ ܕܠܘܬܟܘܢ ܘܪܘܚܘ ܚܘܒܟܘܢ ܠܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 14, 'ܘܠܐ ܬܗܘܘܢ ܒܢܝ ܙܘܓܐ ܠܐܝܠܝܢ ܕܠܐ ܡܗܝܡܢܝܢ ܐܝܕܐ ܓܝܪ ܫܘܬܦܘܬܐ ܐܝܬ ܠܙܕܝܩܘܬܐ ܥܡ ܥܘܠܐ ܐܘ ܐܝܢܐ ܚܘܠܛܢܐ ܐܝܬ ܠܢܗܝܪܐ ܥܡ ܚܫܘܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 15, 'ܐܘ ܐܝܕܐ ܫܠܡܘܬܐ ܐܝܬ ܠܡܫܝܚܐ ܥܡ ܤܛܢܐ ܐܘ ܐܝܕܐ ܡܢܬܐ ܐܝܬ ܠܕܡܗܝܡܢ ܥܡ ܕܠܐ ܡܗܝܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 16, 'ܐܝܕܐ ܕܝܢ ܐܘܝܘܬܐ ܐܝܬ ܠܗܝܟܠܗ ܕܐܠܗܐ ܥܡ ܕܫܐܕܐ ܐܢܬܘܢ ܕܝܢ ܗܝܟܠܗ ܐܢܬܘܢ ܕܐܠܗܐ ܚܝܐ ܐܝܟܢܐ ܕܐܡܝܪ ܕܐܥܡܪ ܒܗܘܢ ܘܐܗܠܟ ܒܗܘܢ ܘܐܗܘܐ ܐܠܗܗܘܢ ܘܗܢܘܢ ܢܗܘܘܢ ܠܝ ܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 17, 'ܡܛܠ ܗܢܐ ܦܘܩܘ ܡܢ ܒܝܢܬܗܘܢ ܘܐܬܦܪܫܘ ܡܢܗܘܢ ܐܡܪ ܡܪܝܐ ܘܠܛܡܐܐ ܠܐ ܬܬܩܪܒܘܢ ܘܐܢܐ ܐܩܒܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 18, 'ܘܐܗܘܐ ܠܟܘܢ ܠܐܒܐ ܘܐܢܬܘܢ ܬܗܘܘܢ ܠܝ ܠܒܢܝܐ ܘܠܒܢܬܐ ܐܡܪ ܡܪܝܐ ܕܟܠ ܐܚܝܕ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 1, 'ܡܛܠ ܕܐܝܬ ܠܢ ܗܟܝܠ ܗܠܝܢ ܡܘܠܟܢܐ ܚܒܝܒܝ ܢܕܟܐ ܢܦܫܢ ܡܢ ܟܠܗ ܛܡܐܘܬܐ ܕܒܤܪܐ ܘܕܪܘܚܐ ܘܢܦܠܘܚ ܩܕܝܫܘܬܐ ܒܕܚܠܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 2, 'ܤܝܒܪܘܢ ܐܚܝܢ ܒܐܢܫ ܠܐ ܐܥܘܠܢ ܠܐܢܫ ܠܐ ܚܒܠܢ ܠܐܢܫ ܠܐ ܥܠܒܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 3, 'ܠܐ ܗܘܐ ܠܡܚܝܒܘܬܟܘܢ ܐܡܪ ܐܢܐ ܩܕܡܬ ܓܝܪ ܐܡܪܬ ܕܒܠܒܢ ܐܢܬܘܢ ܠܡܡܬ ܐܟܚܕܐ ܘܠܡܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 4, 'ܦܪܗܤܝܐ ܤܓܝܐܬܐ ܐܝܬ ܠܝ ܠܘܬܟܘܢ ܘܤܓܝ ܐܝܬ ܠܝ ܒܟܘܢ ܫܘܒܗܪܐ ܘܡܠܐ ܐܢܐ ܒܘܝܐܐ ܘܤܘܓܐܐ ܡܬܝܬܪܐ ܒܝ ܚܕܘܬܐ ܒܟܠܗܘܢ ܐܘܠܨܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 5, 'ܐܦ ܡܢ ܕܐܬܝܢ ܓܝܪ ܠܡܩܕܘܢܝܐ ܐܦܠܐ ܚܕ ܢܝܚ ܗܘܐ ܠܦܓܪܢ ܐܠܐ ܒܟܠ ܡܕܡ ܐܬܐܠܨܢ ܡܢ ܠܒܪ ܩܪܒܐ ܘܡܢ ܠܓܘ ܕܚܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 6, 'ܐܠܐ ܐܠܗܐ ܗܘ ܕܡܒܝܐ ܠܡܟܝܟܐ ܒܝܐܢ ܒܡܐܬܝܬܗ ܕܛܛܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 7, 'ܘܠܐ ܒܠܚܘܕ ܒܡܐܬܝܬܗ ܐܠܐ ܐܦ ܒܢܝܚܗ ܗܘ ܕܐܬܢܝܚ ܒܟܘܢ ܤܒܪܢ ܓܝܪ ܥܠ ܚܘܒܟܘܢ ܕܠܘܬܢ ܘܥܠ ܐܒܠܟܘܢ ܘܛܢܢܟܘܢ ܕܥܠ ܐܦܝܢ ܘܟܕ ܫܡܥܬ ܚܕܘܬܐ ܤܓܝܐܬܐ ܗܘܬ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 8, 'ܕܐܦܢ ܐܟܪܝܬ ܠܟܘܢ ܒܐܓܪܬܐ ܠܐ ܬܘܝܐ ܠܝ ܢܦܫܝ ܐܦܢ ܬܘܝܐ ܗܘܬ ܚܙܐ ܐܢܐ ܓܝܪ ܕܗܝ ܐܓܪܬܐ ܐܦܢ ܕܫܥܬܐ ܐܟܪܝܬ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 9, 'ܐܠܐ ܚܕܘܬܐ ܤܓܝܐܬܐ ܥܒܕܬ ܠܝ ܠܐ ܥܠ ܕܟܪܝܬ ܠܟܘܢ ܐܠܐ ܡܛܠ ܕܟܪܝܘܬܟܘܢ ܠܬܝܒܘܬܐ ܐܝܬܝܬܟܘܢ ܟܪܝܬ ܠܟܘܢ ܓܝܪ ܒܕܐܠܗܐ ܐܝܟܢܐ ܕܒܡܕܡ ܠܐ ܬܚܤܪܘܢ ܡܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 10, 'ܟܪܝܘܬܐ ܓܝܪ ܕܡܛܠ ܐܠܗܐ ܬܘܬ ܢܦܫܐ ܥܒܕܐ ܕܠܐ ܗܦܟܐ ܘܡܦܢܝܐ ܠܚܝܐ ܟܪܝܘܬܐ ܕܝܢ ܕܥܠܡܐ ܡܘܬܐ ܗܘ ܥܒܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 11, 'ܗܐ ܓܝܪ ܗܝ ܗܕܐ ܕܐܬܬܥܝܩܬܘܢ ܡܛܠ ܐܠܗܐ ܟܡܐ ܐܥܒܕܬ ܒܟܘܢ ܚܦܝܛܘܬܐ ܘܡܦܩ ܒܪܘܚܐ ܘܪܘܓܙܐ ܘܕܚܠܬܐ ܘܚܘܒܐ ܘܛܢܢܐ ܘܬܒܥܬܐ ܘܒܟܠܡܕܡ ܚܘܝܬܘܢ ܢܦܫܟܘܢ ܕܕܟܝܢ ܐܢܬܘܢ ܒܗ ܒܨܒܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 12, 'ܬܗܘܐ ܕܝܢ ܗܕܐ ܕܟܬܒܬ ܠܟܘܢ ܕܠܐ ܗܘܐ ܡܛܠ ܡܤܟܠܢܐ ܐܦ ܠܐ ܡܛܠ ܗܘ ܡܢ ܕܡܤܟܠ ܒܗ ܐܠܐ ܡܛܠ ܕܬܬܝܕܥ ܩܕܡ ܐܠܗܐ ܚܦܝܛܘܬܟܘܢ ܕܡܛܠܬܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 13, 'ܡܛܠ ܗܢܐ ܐܬܒܝܐܢ ܘܥܡ ܒܘܝܐܢ ܝܬܝܪܐܝܬ ܚܕܝܢ ܒܚܕܘܬܗ ܕܛܛܘܤ ܕܐܬܢܝܚܬ ܪܘܚܗ ܥܡ ܟܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 14, 'ܕܒܡܕܡ ܕܐܫܬܒܗܪܬ ܠܗ ܥܠ ܐܦܝܟܘܢ ܠܐ ܒܗܬܬ ܐܠܐ ܐܝܟ ܕܟܠ ܡܕܡ ܩܘܫܬܐ ܡܠܠܢ ܥܡܟܘܢ ܗܟܢܐ ܐܦ ܫܘܒܗܪܢ ܕܠܘܬ ܛܛܘܤ ܒܩܘܫܬܐ ܐܫܬܟܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 15, 'ܐܦ ܪܚܡܘܗܝ ܝܬܝܪܐܝܬ ܤܓܝܘ ܥܠܝܟܘܢ ܟܕ ܡܬܕܟܪ ܡܫܬܡܥܢܘܬܐ ܕܟܠܟܘܢ ܕܒܕܚܠܬܐ ܘܒܪܬܝܬܐ ܩܒܠܬܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 16, 'ܚܕܐ ܐܢܐ ܕܒܟܠ ܡܕܡ ܬܟܝܠ ܐܢܐ ܥܠܝܟܘܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 1, 'ܡܘܕܥܝܢܢ ܠܟܘܢ ܕܝܢ ܐܚܝܢ ܛܝܒܘܬܗ ܕܐܠܗܐ ܕܐܬܝܗܒܬ ܒܥܕܬܐ ܕܡܩܕܘܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 2, 'ܕܒܒܘܩܝܐ ܤܓܝܐܐ ܕܐܘܠܨܢܗܘܢ ܝܬܝܪܘܬܐ ܗܘܬ ܠܚܕܘܬܗܘܢ ܘܥܘܡܩܐ ܕܡܤܟܢܘܬܗܘܢ ܐܬܝܬܪ ܒܥܘܬܪܐ ܕܦܫܝܛܘܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 3, 'ܤܗܕ ܐܢܐ ܓܝܪ ܕܐܝܟ ܚܝܠܗܘܢ ܘܝܬܝܪ ܡܢ ܚܝܠܗܘܢ ܒܨܒܝܢ ܢܦܫܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 4, 'ܒܥܘ ܡܢܢ ܒܒܥܘܬܐ ܤܓܝܐܬܐ ܕܢܫܬܘܬܦܘܢ ܒܛܝܒܘܬܐ ܕܬܫܡܫܬܐ ܕܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 5, 'ܘܠܐ ܐܝܟ ܡܐ ܕܤܒܪܝܢ ܗܘܝܢ ܐܠܐ ܢܦܫܗܘܢ ܐܫܠܡܘ ܠܘܩܕܡ ܠܡܪܢ ܘܐܦ ܠܢ ܒܨܒܝܢܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 6, 'ܕܚܢܢ ܢܒܥܐ ܡܢ ܛܛܘܤ ܕܐܝܟ ܕܫܪܝ ܗܟܢܐ ܢܫܠܡ ܒܟܘܢ ܐܦ ܛܝܒܘܬܐ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 7, 'ܐܠܐ ܐܝܟܢܐ ܕܒܟܠܡܕܡ ܡܬܝܬܪܝܢ ܐܢܬܘܢ ܒܗܝܡܢܘܬܐ ܘܒܡܠܬܐ ܘܒܝܕܥܬܐ ܘܒܟܠ ܚܦܝܛܘ ܘܒܚܘܒܢ ܕܠܘܬܟܘܢ ܗܟܢܐ ܐܦ ܒܗܕܐ ܛܝܒܘܬܐ ܬܬܝܬܪܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 8, 'ܠܐ ܗܘܐ ܡܦܩܕ ܦܩܕ ܐܢܐ ܠܟܘܢ ܐܠܐ ܒܚܦܝܛܘܬܐ ܕܚܒܪܝܟܘܢ ܫܪܪܐ ܕܚܘܒܟܘܢ ܡܢܤܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 9, 'ܝܕܥܝܢ ܐܢܬܘܢ ܓܝܪ ܛܝܒܘܬܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܕܡܛܠܬܟܘܢ ܐܬܡܤܟܢ ܟܕ ܐܝܬܘܗܝ ܥܬܝܪܐ ܕܐܢܬܘܢ ܒܡܤܟܢܘܬܗ ܬܥܬܪܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 10, 'ܡܡܠܟ ܕܝܢ ܡܠܟ ܐܢܐ ܠܟܘܢ ܗܕܐ ܕܡܥܕܪܐ ܠܟܘܢ ܡܛܠ ܕܡܢ ܐܫܬܩܕܝ ܫܪܝܬܘܢ ܠܘ ܠܡܨܒܐ ܒܠܚܘܕ ܐܠܐ ܐܦ ܠܡܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 11, 'ܗܫܐ ܕܝܢ ܫܠܡܘ ܒܥܒܕܐ ܗܘ ܡܕܡ ܕܨܒܝܬܘܢ ܕܐܝܟܢܐ ܕܗܘܐ ܠܟܘܢ ܤܘܘܚܐ ܠܡܨܒܐ ܗܟܢܐ ܒܥܒܕܐ ܬܫܠܡܘܢ ܡܢ ܡܐ ܕܐܝܬ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 12, 'ܐܢ ܓܝܪ ܨܒܝܢܐ ܐܝܬܘܗܝ ܐܝܟ ܡܐ ܕܐܝܬ ܠܗ ܗܟܘܬ ܡܬܩܒܠ ܠܐ ܗܘܐ ܐܝܟ ܡܐ ܕܠܝܬ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 13, 'ܠܐ ܗܘܐ ܓܝܪ ܕܠܐܚܪܢܐ ܬܗܘܐ ܪܘܚܬܐ ܘܠܟܘܢ ܐܘܠܨܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 14, 'ܐܠܐ ܒܫܘܝܘܬܐ ܗܘܘ ܒܙܒܢܐ ܗܢܐ ܕܝܬܝܪܘܬܟܘܢ ܕܝܠܟܘܢ ܬܗܘܐ ܠܚܤܝܪܘܬܗܘܢ ܕܗܠܝܢ ܕܐܦ ܝܬܝܪܘܬܗܘܢ ܕܗܢܘܢ ܬܗܘܐ ܠܚܤܝܪܘܬܟܘܢ ܕܝܠܟܘܢ ܕܬܗܘܐ ܫܘܝܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 15, 'ܐܝܟ ܕܟܬܝܒ ܕܗܘ ܕܐܤܓܝ ܫܩܠ ܠܐ ܐܬܝܬܪ ܠܗ ܘܗܘ ܕܩܠܝܠ ܫܩܠ ܠܐ ܐܬܒܨܪ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 16, 'ܛܝܒܘ ܕܝܢ ܠܐܠܗܐ ܕܝܗܒܗ ܚܦܝܛܘܬܐ ܗܕܐ ܚܠܦܝܟܘܢ ܒܠܒܗ ܕܛܛܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 17, 'ܒܥܘܬܢ ܓܝܪ ܩܒܠ ܘܡܛܠ ܕܛܒ ܒܛܝܠ ܠܗ ܡܢ ܨܒܝܢܗ ܢܦܩ ܨܐܕܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 18, 'ܫܕܪܢ ܕܝܢ ܥܡܗ ܠܐܚܘܢ ܐܝܢܐ ܕܬܫܒܘܚܬܗ ܒܐܘܢܓܠܝܘܢ ܒܟܠܗܝܢ ܥܕܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 19, 'ܗܟܢܐ ܕܐܦ ܡܓܒܐ ܓܒܐ ܡܢ ܥܕܬܐ ܕܢܦܘܩ ܥܡܢ ܒܛܝܒܘܬܐ ܗܕܐ ܕܡܫܬܡܫܐ ܡܢܢ ܠܫܘܒܚܗ ܕܝܠܗ ܕܐܠܗܐ ܘܠܠܘܒܒܢ ܕܝܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 20, 'ܩܢܝܛܝܢܢ ܕܝܢ ܒܗܕܐ ܕܠܐ ܐܢܫ ܢܤܝܡ ܒܢ ܡܘܡܐ ܒܗܕܐ ܪܒܘܬܐ ܕܡܫܬܡܫܐ ܡܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 21, 'ܝܨܝܦܝܢܢ ܓܝܪ ܕܫܦܝܪܬܐ ܠܐ ܩܕܡ ܐܠܗܐ ܒܠܚܘܕ ܐܠܐ ܐܦ ܩܕܡ ܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 22, 'ܫܕܪܢ ܕܝܢ ܥܡܗܘܢ ܐܦ ܠܐܚܘܢ ܐܝܢܐ ܕܠܢ ܒܩܐ ܒܟܠܙܒܢ ܒܤܓܝܐܬܐ ܕܚܦܝܛܐ ܗܘ ܗܫܐ ܕܝܢ ܝܬܝܪܐܝܬ ܚܦܝܛ ܒܬܘܟܠܢܐ ܤܓܝܐܐ ܕܥܠܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 23, 'ܐܢ ܗܟܝܠ ܛܛܘܤ ܫܘܬܦܐ ܗܘ ܕܝܠܝ ܘܡܥܕܪܢܐ ܒܟܘܢ ܘܐܢ ܐܚܝܢ ܐܚܪܢܐ ܫܠܝܚܐ ܐܢܘܢ ܕܥܕܬܐ ܕܫܘܒܚܗ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 24, 'ܡܟܝܠ ܬܚܘܝܬܐ ܕܚܘܒܟܘܢ ܘܫܘܒܗܪܢ ܕܒܟܘܢ ܒܗܘܢ ܚܘܘ ܒܦܪܨܘܦ ܥܕܬܐ ܟܠܗܝܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 1, 'ܥܠ ܬܫܡܫܬܐ ܕܝܢ ܕܩܕܝܫܐ ܝܬܝܪܬܐ ܥܒܕ ܐܢܐ ܐܢ ܟܬܒ ܐܢܐ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 2, 'ܝܕܥ ܐܢܐ ܓܝܪ ܛܘܝܒܗ ܕܪܥܝܢܟܘܢ ܘܡܛܠ ܗܢܐ ܐܫܬܒܗܪܬ ܒܟܘܢ ܠܡܩܕܘܢܝܐ ܕܐܟܐܝܐ ܥܬܝܕܐ ܗܝ ܡܢ ܐܫܬܩܕܝ ܘܛܢܢܐ ܕܝܠܟܘܢ ܠܤܓܝܐܐ ܓܪܓ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 3, 'ܫܕܪܬ ܕܝܢ ܠܐܚܐ ܕܠܐ ܢܤܬܪܩ ܫܘܒܗܪܢ ܕܐܫܬܒܗܪܢ ܒܟܘܢ ܥܠ ܗܕܐ ܨܒܘܬܐ ܕܐܝܟܢܐ ܕܐܡܪܬ ܬܗܘܘܢ ܡܛܝܒܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 4, 'ܕܠܡܐ ܢܐܬܘܢ ܥܡܝ ܡܩܕܘܢܝܐ ܘܢܫܟܚܘܢܟܘܢ ܟܕ ܠܐ ܥܬܝܕܝܢ ܐܢܬܘܢ ܘܢܒܗܬ ܚܢܢ ܕܠܐ ܢܐܡܪ ܕܐܢܬܘܢ ܬܒܗܬܘܢ ܒܫܘܒܗܪܐ ܗܘ ܕܐܫܬܒܗܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 5, 'ܡܛܠ ܗܢܐ ܐܬܒܛܠ ܠܝ ܕܐܒܥܐ ܡܢ ܐܚܝ ܗܠܝܢ ܕܢܐܬܘܢ ܩܕܡܝ ܠܘܬܟܘܢ ܘܢܥܬܕܘܢ ܒܘܪܟܬܐ ܗܝ ܕܡܢ ܩܕܝܡ ܐܫܬܡܥܬܘܢ ܕܬܗܘܐ ܡܛܝܒܐ ܗܟܢܐ ܐܝܟ ܒܘܪܟܬܐ ܠܐ ܗܘܐ ܐܝܟ ܝܥܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 6, 'ܗܕܐ ܕܝܢ ܡܢ ܕܙܪܥ ܒܚܘܤܢܐ ܐܦ ܒܚܘܤܢܐ ܚܨܕ ܘܡܢ ܕܙܪܥ ܒܒܘܪܟܬܐ ܒܒܘܪܟܬܐ ܢܚܨܘܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 7, 'ܟܠ ܐܢܫ ܐܝܟ ܕܐܝܬ ܒܪܥܝܢܗ ܠܐ ܐܝܟ ܕܡܢ ܟܪܝܘܬܐ ܐܘ ܐܝܟ ܕܡܢ ܩܛܝܪܐ ܠܝܗܘܒܐ ܗܘ ܓܝܪ ܚܕܝܐ ܪܚܡ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 8, 'ܡܛܝܐ ܗܝ ܕܝܢ ܒܐܝܕܝ ܐܠܗܐ ܟܠ ܛܝܒܘ ܕܢܝܬܪ ܒܟܘܢ ܕܒܟܠܙܒܢ ܒܟܠܡܕܡ ܗܘ ܡܐ ܕܤܦܩ ܠܟܘܢ ܢܗܘܐ ܠܟܘܢ ܘܬܬܝܬܪܘܢ ܒܟܠ ܥܒܕ ܛܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 9, 'ܐܝܟ ܕܟܬܝܒ ܕܒܕܪ ܘܝܗܒ ܠܡܤܟܢܐ ܘܙܕܝܩܘܬܗ ܩܝܡܐ ܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 10, 'ܗܘ ܕܝܢ ܕܝܗܒ ܙܪܥܐ ܠܙܪܘܥܐ ܘܠܚܡܐ ܠܡܐܟܘܠܬܐ ܗܘ ܢܬܠ ܘܢܤܓܐ ܙܪܥܟܘܢ ܘܢܪܒܐ ܦܐܪܐ ܕܙܕܝܩܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 11, 'ܕܒܟܠ ܡܕܡ ܬܥܬܪܘܢ ܒܟܠܗ ܦܫܝܛܘܬܐ ܕܗܝ ܓܡܪܐ ܒܐܝܕܢ ܬܘܕܝܬܐ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 12, 'ܡܛܠ ܕܦܘܠܚܢܐ ܕܬܫܡܫܬܐ ܗܕܐ ܠܐ ܗܘܐ ܒܠܚܘܕ ܚܤܝܪܘܬܐ ܕܩܕܝܫܐ ܡܡܠܐ ܐܠܐ ܐܦ ܡܝܬܪ ܒܬܘܕܝܬܐ ܤܓܝܐܬܐ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 13, 'ܡܛܠ ܒܘܩܝܐ ܓܝܪ ܕܬܫܡܫܬܐ ܗܕܐ ܠܐܠܗܐ ܡܫܒܚܝܢ ܕܐܫܬܥܒܕܬܘܢ ܠܬܘܕܝܬܐ ܕܤܒܪܬܗ ܕܡܫܝܚܐ ܘܐܫܬܘܬܦܬܘܢ ܒܦܫܝܛܘܬܟܘܢ ܥܡܗܘܢ ܘܥܡ ܟܠܢܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 14, 'ܘܨܠܘܬܐ ܡܩܪܒܝܢ ܚܠܦܝܟܘܢ ܒܚܘܒܐ ܤܓܝܐܐ ܡܛܠ ܤܘܓܐܐ ܕܛܝܒܘܬܗ ܕܐܠܗܐ ܕܥܠܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 15, 'ܛܝܒܘ ܕܝܢ ܠܐܠܗܐ ܥܠ ܡܘܗܒܬܗ ܕܠܐ ܡܬܡܠܠܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 1, 'ܐܢܐ ܕܝܢ ܦܘܠܘܤ ܒܥܐ ܐܢܐ ܡܢܟܘܢ ܒܢܝܚܘܬܗ ܘܒܡܟܝܟܘܬܗ ܕܡܫܝܚܐ ܕܐܦܢ ܒܐܦܝܢ ܡܟܝܟ ܐܢܐ ܠܘܬܟܘܢ ܐܠܐ ܟܕ ܪܚܝܩ ܐܢܐ ܬܟܝܠ ܐܢܐ ܥܠܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 2, 'ܒܥܐ ܐܢܐ ܕܝܢ ܡܢܟܘܢ ܕܠܐ ܡܐ ܕܐܬܝܬ ܐܬܐܠܨ ܒܬܘܟܠܢܐ ܕܐܝܬ ܠܝ ܕܐܫܝܚ ܐܝܟ ܕܡܬܪܥܐ ܐܢܐ ܥܠ ܐܢܫܐ ܐܝܠܝܢ ܕܚܫܒܝܢ ܠܢ ܕܐܝܟ ܕܒܒܤܪ ܡܗܠܟܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 3, 'ܐܦܢ ܒܒܤܪ ܓܝܪ ܡܗܠܟܝܢ ܚܢܢ ܐܠܐ ܠܐ ܗܘܐ ܕܒܤܪܐ ܦܠܚܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 4, 'ܙܝܢܐ ܓܝܪ ܕܦܠܚܘܬܢ ܠܐ ܗܘܐ ܕܒܤܪܐ ܐܠܐ ܕܚܝܠܐ ܕܐܠܗܐ ܘܒܗ ܟܒܫܝܢܢ ܚܤܢܐ ܡܪܝܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 5, 'ܘܤܬܪܝܢܢ ܡܚܫܒܬܐ ܘܟܠ ܪܘܡܐ ܕܡܬܬܪܝܡ ܠܘܩܒܠ ܝܕܥܬܗ ܕܐܠܗܐ ܘܫܒܝܢܢ ܟܠ ܬܪܥܝܢ ܠܡܫܡܥܬܗ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 6, 'ܘܡܛܝܒܝܢܢ ܠܡܥܒܕ ܬܒܥܬܐ ܡܢ ܐܝܠܝܢ ܕܠܐ ܡܫܬܡܥܝܢ ܡܐ ܕܐܬܡܠܝܬ ܡܫܬܡܥܢܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 7, 'ܒܦܪܨܘܦܐ ܚܝܪܝܢ ܐܢܬܘܢ ܐܢ ܐܢܫ ܬܟܝܠ ܥܠ ܢܦܫܗ ܕܕܡܫܝܚܐ ܗܘ ܗܢܐ ܢܕܥ ܡܢ ܢܦܫܗ ܕܐܝܟ ܕܗܘ ܕܡܫܝܚܐ ܗܟܢܐ ܐܦ ܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 8, 'ܐܢ ܓܝܪ ܐܦ ܡܕܡ ܝܬܝܪ ܐܫܬܒܗܪ ܥܠ ܫܘܠܛܢܐ ܕܝܗܒ ܠܝ ܡܪܢ ܠܐ ܒܗܬ ܐܢܐ ܡܛܠ ܕܠܒܢܝܢܐ ܗܘ ܕܝܠܟܘܢ ܝܗܒ ܠܢ ܘܠܐ ܠܤܘܚܦܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 9, 'ܡܗܡܐ ܐܢܐ ܕܝܢ ܕܠܐ ܐܤܬܒܪ ܐܝܟ ܗܘ ܕܡܕܚܠܘ ܡܕܚܠ ܐܢܐ ܠܟܘܢ ܒܐܓܪܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 10, 'ܡܛܠ ܕܐܝܬ ܐܢܫܐ ܕܐܡܪܝܢ ܕܐܓܪܬܐ ܝܩܝܪܢ ܘܚܤܝܢܢ ܡܐܬܝܗ ܕܝܢ ܕܓܘܫܡܐ ܟܪܝܗ ܘܡܠܬܗ ܫܝܛܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 11, 'ܐܠܐ ܗܕܐ ܢܬܪܥܐ ܡܢ ܕܗܟܢܐ ܐܡܪ ܕܐܝܟܢܐ ܕܐܝܬܝܢ ܒܡܠܬܐ ܕܐܓܪܬܢ ܟܕ ܪܚܝܩܝܢܢ ܗܟܢܐ ܐܝܬܝܢ ܐܦ ܡܐ ܕܩܪܝܒܝܢܢ ܒܥܒܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 12, 'ܠܐ ܓܝܪ ܡܡܪܚܝܢܢ ܕܢܚܫܘܒ ܐܘ ܕܢܦܚܡ ܢܦܫܢ ܥܡ ܗܢܘܢ ܕܡܫܒܗܪܝܢ ܢܦܫܗܘܢ ܐܠܐ ܡܛܠ ܕܗܢܘܢ ܒܗܘܢ ܠܗܘܢ ܡܦܚܡܝܢ ܠܐ ܡܤܬܟܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 13, 'ܚܢܢ ܕܝܢ ܠܐ ܡܫܬܒܗܪܝܢܢ ܝܬܝܪ ܡܢ ܡܫܘܚܬܢ ܐܠܐ ܒܡܫܘܚܬܐ ܕܬܚܘܡܐ ܕܦܠܓ ܠܢ ܐܠܗܐ ܕܢܡܛܐ ܐܦ ܥܕܡܐ ܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 14, 'ܠܐ ܗܘܐ ܓܝܪ ܐܝܟ ܕܠܐ ܡܬܡܛܝܢܢ ܠܘܬܟܘܢ ܡܬܚܝܢܢ ܢܦܫܢ ܥܕܡܐ ܓܝܪ ܠܘܬܟܘܢ ܡܛܝܢ ܒܤܒܪܬܗ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 15, 'ܘܠܐ ܡܫܬܒܗܪܝܢܢ ܠܒܪ ܡܢ ܡܫܘܚܬܢ ܒܥܡܠܐ ܕܐܚܪܢܐ ܐܠܐ ܐܝܬ ܠܢ ܤܒܪܐ ܕܟܕ ܪܒܝܐ ܗܝܡܢܘܬܟܘܢ ܒܟܘܢ ܢܬܪܘܪܒ ܐܝܟ ܡܫܘܚܬܢ ܘܢܬܝܬܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 16, 'ܐܦ ܠܗܠ ܡܢܟܘܢ ܠܡܤܒܪܘ ܠܐ ܒܡܫܘܚܬܐ ܕܐܚܪܢܐ ܒܐܝܠܝܢ ܕܡܬܩܢܢ ܢܫܬܒܗܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 17, 'ܗܘ ܕܝܢ ܕܡܫܬܒܗܪ ܒܡܪܝܐ ܢܫܬܒܗܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 18, 'ܠܐ ܗܘܐ ܓܝܪ ܡܢ ܕܢܦܫܗ ܡܫܒܚ ܗܘ ܗܘ ܒܩܐ ܐܠܐ ܡܢ ܕܡܪܝܐ ܢܫܒܚܝܘܗܝ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 1, 'ܐܫܬܘܦ ܕܝܢ ܡܤܝܒܪܝܢ ܗܘܝܬܘܢ ܠܝ ܩܠܝܠ ܕܐܡܠܠ ܦܟܝܗܐܝܬ ܐܠܐ ܐܦ ܡܤܝܒܪܝܢ ܐܢܬܘܢ ܗܘ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 2, 'ܛܐܢ ܐܢܐ ܓܝܪ ܒܟܘܢ ܒܛܢܢܐ ܕܐܠܗܐ ܡܟܪܬܟܘܢ ܓܝܪ ܠܓܒܪܐ ܚܕ ܒܬܘܠܬܐ ܕܟܝܬܐ ܕܐܩܪܒ ܠܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 3, 'ܕܚܠ ܐܢܐ ܕܝܢ ܕܠܡܐ ܐܝܟ ܕܐܛܥܝ ܚܘܝܐ ܠܚܘܐ ܒܢܟܝܠܘܬܗ ܗܟܢܐ ܢܬܚܒܠܘܢ ܪܥܝܢܝܟܘܢ ܡܢ ܦܫܝܛܘܬܐ ܕܠܘܬ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 4, 'ܐܢ ܓܝܪ ܗܘ ܕܐܬܐ ܠܘܬܟܘܢ ܐܚܪܢܐ ܝܫܘܥ ܐܟܪܙ ܠܟܘܢ ܐܝܢܐ ܕܚܢܢ ܠܐ ܐܟܪܙܢ ܐܘ ܪܘܚܐ ܐܚܪܬܐ ܢܤܒܬܘܢ ܐܝܕܐ ܕܠܐ ܢܤܒܬܘܢ ܐܘ ܤܒܪܬܐ ܐܚܪܬܐ ܐܝܕܐ ܕܠܐ ܩܒܠܬܘܢ ܫܦܝܪ ܡܬܛܦܝܤܝܢ ܗܘܝܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 5, 'ܪܢܐ ܐܢܐ ܓܝܪ ܕܡܕܡ ܠܐ ܒܨܪܬ ܡܢ ܫܠܝܚܐ ܐܝܠܝܢ ܕܛܒ ܡܝܬܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 6, 'ܐܦܢ ܓܝܪ ܒܘܪܐ ܐܢܐ ܒܡܠܬܝ ܐܠܐ ܠܐ ܒܝܕܥܬܝ ܐܠܐ ܒܟܠܡܕܡ ܐܬܓܠܝܢ ܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 7, 'ܐܘ ܕܠܡܐ ܡܤܟܠܘ ܐܤܟܠܬ ܕܡܟܟܬ ܢܦܫܝ ܕܐܢܬܘܢ ܬܬܪܝܡܘܢ ܘܡܓܢ ܐܟܪܙܬ ܠܟܘܢ ܤܒܪܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 8, 'ܘܥܕܬܐ ܐܚܪܢܝܬܐ ܚܠܨܬ ܘܢܤܒܬ ܢܦܩܬܐ ܠܬܫܡܫܬܐ ܕܝܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 9, 'ܘܟܕ ܐܬܝܬ ܨܐܕܝܟܘܢ ܘܚܤܪ ܠܝ ܠܐ ܝܩܪܬ ܥܠ ܐܢܫ ܡܢܟܘܢ ܤܢܝܩܘܬܝ ܓܝܪ ܡܠܝܘ ܐܚܐ ܕܐܬܘ ܡܢ ܡܩܕܘܢܝܐ ܘܒܟܠ ܡܕܡ ܢܛܪܬ ܢܦܫܝ ܘܢܛܪ ܐܢܐ ܕܠܐ ܐܩܪ ܥܠܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 10, 'ܐܝܬܘܗܝ ܒܝ ܫܪܪܗ ܕܡܫܝܚܐ ܕܗܢܐ ܫܘܒܗܪܐ ܠܐ ܢܬܒܛܠ ܒܝ ܒܐܬܪܘܬܐ ܕܐܟܐܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 11, 'ܠܡܢܐ ܡܛܠ ܕܠܐ ܡܚܒ ܐܢܐ ܠܟܘܢ ܐܠܗܐ ܗܘ ܝܕܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 12, 'ܐܠܐ ܗܕܐ ܕܥܒܕ ܐܢܐ ܐܦ ܐܥܒܕ ܕܐܦܤܘܩ ܗܘ ܥܠܬܗܘܢ ܕܐܝܠܝܢ ܕܒܥܝܢ ܥܠܬܐ ܕܒܗܘ ܡܕܡ ܕܡܫܬܒܗܪܝܢ ܢܫܬܟܚܘܢ ܐܟܘܬܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 13, 'ܐܝܬܝܗܘܢ ܓܝܪ ܗܠܝܢ ܫܠܝܚܐ ܕܓܠܐ ܘܦܥܠܐ ܢܟܝܠܐ ܘܡܕܡܝܢ ܢܦܫܗܘܢ ܠܫܠܝܚܐ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 14, 'ܘܠܝܬ ܠܡܬܕܡܪܘ ܒܗܕܐ ܐܢ ܗܘ ܓܝܪ ܕܤܛܢܐ ܡܬܕܡܐ ܒܡܠܐܟܐ ܕܢܘܗܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 15, 'ܠܐ ܗܘܬ ܪܒܐ ܨܒܘܬܐ ܐܢ ܐܦ ܡܫܡܫܢܘܗܝ ܡܬܕܡܝܢ ܒܡܫܡܫܢܐ ܕܙܕܝܩܘܬܐ ܗܢܘܢ ܕܚܪܬܗܘܢ ܬܗܘܐ ܐܝܟ ܥܒܕܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 16, 'ܬܘܒ ܕܝܢ ܐܡܪ ܐܢܐ ܠܡܐ ܐܢܫ ܢܤܒܪ ܥܠܝ ܐܝܟ ܕܤܟܠܐ ܐܢܐ ܘܐܢ ܠܐ ܐܦܢ ܐܝܟ ܤܟܠܐ ܩܒܠܘܢܝ ܕܐܦ ܐܢܐ ܩܠܝܠ ܐܫܬܒܗܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 17, 'ܡܕܡ ܕܡܡܠܠ ܐܢܐ ܠܐ ܗܘܐ ܒܡܪܢ ܡܡܠܠ ܐܢܐ ܐܠܐ ܐܝܟ ܕܒܦܟܝܗܘܬܐ ܒܗܕܐ ܕܘܟܬܐ ܕܫܘܒܗܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 18, 'ܡܛܠ ܕܤܓܝܐܐ ܡܫܬܒܗܪܝܢ ܒܒܤܪܐ ܐܦ ܐܢܐ ܐܫܬܒܗܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 19, 'ܡܢܚܝܢ ܐܢܬܘܢ ܗܘ ܓܝܪ ܠܡܫܬܡܥܘ ܠܚܤܝܪܝ ܪܥܝܢܐ ܟܕ ܐܢܬܘܢ ܚܟܝܡܐ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 20, 'ܘܡܬܕܢܝܢ ܐܢܬܘܢ ܠܡܢ ܕܡܫܥܒܕ ܠܟܘܢ ܘܠܡܢ ܕܐܟܠ ܠܟܘܢ ܘܠܡܢ ܕܢܤܒ ܡܢܟܘܢ ܘܠܡܢ ܕܡܬܪܝܡ ܥܠܝܟܘܢ ܘܠܡܢ ܕܡܚܐ ܠܟܘܢ ܥܠ ܐܦܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 21, 'ܐܝܟ ܕܒܨܥܪܐ ܐܡܪ ܐܢܐ ܐܝܟ ܗܘ ܕܚܢܢ ܡܚܝܠܝܢܢ ܗܘ ܒܚܤܝܪܘܬ ܪܥܝܢܐ ܐܡܪ ܐܢܐ ܕܒܟܠܡܕܡ ܕܡܡܪܚ ܐܢܫ ܐܦ ܐܢܐ ܡܡܪܚ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 22, 'ܐܢ ܥܒܪܝܐ ܐܢܘܢ ܐܦ ܐܢܐ ܐܢ ܐܝܤܪܠܝܐ ܐܢܘܢ ܐܦ ܐܢܐ ܐܢ ܙܪܥܗ ܐܢܘܢ ܕܐܒܪܗܡ ܐܦ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 23, 'ܐܢ ܡܫܡܫܢܐ ܐܢܘܢ ܕܡܫܝܚܐ ܒܚܤܝܪܘܬ ܪܥܝܢܐ ܐܡܪ ܐܢܐ ܕܝܬܝܪ ܡܢܗܘܢ ܐܢܐ ܒܠܐܘܬܐ ܝܬܝܪ ܡܢܗܘܢ ܒܡܚܘܬܐ ܝܬܝܪ ܡܢܗܘܢ ܒܐܤܘܪܐ ܝܬܝܪ ܡܢܗܘܢ ܒܡܘܬܐ ܙܒܢܝܢ ܤܓܝܐܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 24, 'ܡܢ ܝܗܘܕܝܐ ܚܡܫ ܙܒܢܝܢ ܐܪܒܥܝܢ ܐܪܒܥܝܢ ܚܤܝܪ ܚܕܐ ܒܠܥܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 25, 'ܬܠܬ ܙܒܢܝܢ ܒܫܒܛܐ ܐܬܢܓܕܬ ܚܕܐ ܙܒܢ ܐܬܪܓܡܬ ܬܠܬ ܙܒܢܝܢ ܗܘܝܬ ܢܘܘܓܐ ܐܝܡܡܐ ܘܠܠܝܐ ܕܠܐ ܤܦܝܢܬܐ ܒܝܡܐ ܗܘܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 26, 'ܒܐܘܪܚܬܐ ܤܓܝܐܬܐ ܒܩܢܕܝܢܘܤ ܕܢܗܪܘܬܐ ܒܩܢܕܝܢܘܤ ܕܓܝܤܐ ܒܩܢܕܝܢܘܤ ܕܡܢ ܛܘܗܡܝ ܒܩܢܕܝܢܘܤ ܕܡܢ ܥܡܡܐ ܒܩܢܕܝܢܘܤ ܗܘܝܬ ܒܡܕܝܢܬܐ ܒܩܢܕܝܢܘܤ ܗܘܝܬ ܒܚܘܪܒܐ ܒܩܢܕܝܢܘܤ ܒܝܡܐ ܒܩܢܕܝܢܘܤ ܕܡܢ ܐܚܐ ܕܓܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 27, 'ܒܥܡܠܐ ܘܒܠܐܘܬܐ ܒܫܗܪܐ ܤܓܝܐܐ ܒܟܦܢܐ ܘܒܨܗܝܐ ܒܨܘܡܐ ܤܓܝܐܐ ܒܥܪܝܐ ܘܒܥܪܛܠܝܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 28, 'ܤܛܪ ܡܢ ܝܬܝܪܬܐ ܘܟܢܘܫܝܐ ܕܥܠܝ ܕܟܠܝܘܡ ܘܨܦܬܝ ܕܥܠ ܐܦܝ ܟܠܗܝܢ ܥܕܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 29, 'ܡܢܘ ܡܬܟܪܗ ܘܠܐ ܐܢܐ ܡܬܟܪܗ ܐܢܐ ܡܢܘ ܡܬܟܫܠ ܘܠܐ ܐܢܐ ܝܩܕ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 30, 'ܐܢ ܠܡܫܬܒܗܪܘ ܘܠܐ ܒܟܘܪܗܢܝ ܐܫܬܒܗܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 31, 'ܝܕܥ ܗܘ ܐܠܗܐ ܐܒܘܗܝ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܡܒܪܟܐ ܕܠܥܠܡ ܥܠܡܝܢ ܕܠܐ ܡܟܕܒ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 32, 'ܒܕܪܡܤܘܩ ܪܒ ܚܝܠܐ ܕܐܪܛܘܤ ܡܠܟܐ ܢܛܪ ܗܘܐ ܡܕܝܢܬܐ ܕܕܪܡܘܤܩܝܐ ܠܡܐܚܕܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 33, 'ܘܡܢ ܟܘܬܐ ܒܤܪܝܓܬܐ ܫܒܘܢܝ ܡܢ ܫܘܪܐ ܘܐܬܦܠܛܬ ܡܢ ܐܝܕܘܗܝ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 1, 'ܠܡܫܬܒܗܪܘ ܘܠܐ ܐܠܐ ܠܐ ܦܩܚ ܐܬܐ ܐܢܐ ܠܝ ܓܝܪ ܠܚܙܘܢܐ ܘܠܓܠܝܢܘܗܝ ܕܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 2, 'ܝܕܥܢܐ ܓܒܪܐ ܒܡܫܝܚܐ ܡܢ ܩܕܡ ܐܪܒܥܤܪܐ ܫܢܝܢ ܐܢ ܒܦܓܪ ܕܝܢ ܘܐܢ ܕܠܐ ܦܓܪ ܠܐ ܝܕܥ ܐܢܐ ܐܠܗܐ ܗܘ ܝܕܥ ܕܐܬܚܛܦ ܗܘ ܗܢܐ ܥܕܡܐ ܫܡܝܐ ܕܬܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 3, 'ܘܝܕܥ ܐܢܐ ܠܗ ܠܗܢܐ ܒܪܢܫܐ ܐܢ ܒܦܓܪ ܕܝܢ ܘܐܢ ܕܠܐ ܦܓܪ ܠܐ ܝܕܥ ܐܢܐ ܐܠܗܐ ܗܘ ܝܕܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 4, 'ܕܐܬܚܛܦ ܠܦܪܕܝܤܐ ܘܫܡܥ ܡܠܐ ܕܠܐ ܡܬܡܠܠܢ ܐܝܠܝܢ ܕܠܐ ܫܠܝܛ ܠܒܪܢܫܐ ܠܡܡܠܠܘ ܐܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 5, 'ܥܠ ܗܢܐ ܐܫܬܒܗܪ ܥܠ ܢܦܫܝ ܕܝܢ ܠܐ ܐܫܬܒܗܪ ܐܠܐ ܐܢ ܒܟܘܪܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 6, 'ܐܢ ܐܨܒܐ ܓܝܪ ܕܐܫܬܒܗܪ ܠܐ ܗܘܐ ܐܢܐ ܫܛܝܐ ܫܪܪܐ ܗܘ ܓܝܪ ܐܡܪ ܐܢܐ ܚܐܤܢܐ ܕܝܢ ܕܠܡܐ ܐܢܫ ܢܬܪܥܐ ܥܠܝ ܝܬܝܪ ܡܢ ܡܐ ܕܚܙܐ ܠܝ ܘܕܫܡܥ ܡܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 7, 'ܘܕܠܐ ܐܬܪܝܡ ܒܝܬܝܪܘܬܐ ܕܓܠܝܢܐ ܐܫܬܠܡ ܠܝ ܫܦܝܐ ܠܒܤܪܝ ܡܠܐܟܗ ܕܤܛܢܐ ܕܢܗܘܐ ܡܩܦܚ ܠܝ ܕܠܐ ܐܬܪܝܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 8, 'ܘܥܠ ܗܢܐ ܬܠܬ ܙܒܢܝܢ ܒܥܝܬ ܡܢ ܡܪܝ ܕܢܦܪܩ ܡܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 9, 'ܘܐܡܪ ܠܝ ܤܦܩܐ ܠܟ ܛܝܒܘܬܝ ܚܝܠܝ ܓܝܪ ܒܟܪܝܗܘܬܐ ܗܘ ܡܬܓܡܪ ܚܕܝܐܝܬ ܗܟܝܠ ܐܫܬܒܗܪ ܒܟܘܪܗܢܝ ܕܢܓܢ ܥܠܝ ܚܝܠܗ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 10, 'ܡܛܠ ܗܢܐ ܨܒܐ ܐܢܐ ܒܟܘܪܗܢܐ ܒܨܥܪܐ ܒܐܘܠܨܢܐ ܒܪܕܘܦܝܐ ܒܚܒܘܫܝܐ ܕܥܠ ܐܦܝ ܡܫܝܚܐ ܐܡܬܝ ܕܟܪܝܗ ܐܢܐ ܓܝܪ ܗܝܕܝܢ ܗܘ ܚܝܠܬܢ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 11, 'ܗܐ ܗܘܝܬ ܚܤܝܪ ܪܥܝܢܐ ܒܫܘܒܗܪܝ ܕܐܢܬܘܢ ܐܠܨܬܘܢܢܝ ܚܝܒܝܢ ܗܘܝܬܘܢ ܓܝܪ ܕܐܢܬܘܢ ܬܤܗܕܘܢ ܥܠܝ ܡܛܠ ܕܠܐ ܒܨܪܬ ܡܕܡ ܡܢ ܫܠܝܚܐ ܐܝܠܝܢ ܕܛܒ ܡܝܬܪܝܢ ܘܐܦܢ ܠܐ ܗܘܝܬ ܡܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 12, 'ܐܬܘܬܐ ܕܫܠܝܚܐ ܥܒܕܬ ܒܝܢܬܟܘܢ ܒܟܠ ܡܤܝܒܪܢܘ ܘܒܓܒܪܘܬܐ ܘܒܬܕܡܪܬܐ ܘܒܚܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 13, 'ܒܡܢܐ ܓܝܪ ܐܬܒܨܪܬܘܢ ܡܢ ܥܕܬܐ ܐܚܪܢܝܬܐ ܐܠܐ ܐܢ ܒܗܕܐ ܕܐܢܐ ܠܐ ܝܩܪܬ ܥܠܝܟܘܢ ܫܒܘܩܘ ܠܝ ܗܕܐ ܤܟܠܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 14, 'ܗܐ ܗܕܐ ܕܬܠܬ ܗܝ ܙܒܢܝܢ ܕܡܛܝܒ ܐܢܐ ܕܐܬܐ ܠܘܬܟܘܢ ܘܠܐ ܐܩܪ ܥܠܝܟܘܢ ܡܛܠ ܕܠܐ ܒܥܐ ܐܢܐ ܕܝܠܟܘܢ ܐܠܐ ܠܟܘܢ ܠܐ ܓܝܪ ܚܝܒܝܢ ܒܢܝܐ ܠܡܤܡ ܤܝܡܬܐ ܠܐܒܗܐ ܐܠܐ ܐܒܗܐ ܠܒܢܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 15, 'ܐܢܐ ܕܝܢ ܚܕܝܐܝܬ ܘܢܦܩܬܐ ܐܦܩ ܘܐܦ ܩܢܘܡܝ ܐܬܠ ܥܠ ܐܦܝ ܢܦܫܬܟܘܢ ܐܦܢ ܟܕ ܝܬܝܪܐܝܬ ܡܚܒ ܐܢܐ ܠܟܘܢ ܐܢܬܘܢ ܒܨܝܪܐܝܬ ܡܚܒܝܢ ܐܢܬܘܢ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 16, 'ܘܟܒܪ ܐܢܐ ܠܐ ܝܩܪܬ ܥܠܝܟܘܢ ܐܠܐ ܐܝܟ ܓܒܪܐ ܚܪܥܐ ܒܢܟܠܐ ܓܢܒܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 17, 'ܠܡܐ ܒܝܕ ܐܢܫ ܐܚܪܝܢ ܕܫܕܪܬ ܠܘܬܟܘܢ ܐܬܝܥܢܬ ܥܠܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 18, 'ܡܢ ܛܛܘܤ ܒܥܝܬ ܘܫܕܪܬ ܥܡܗ ܠܐܚܐ ܠܡܐ ܡܕܡ ܐܬܝܥܢ ܥܠܝܟܘܢ ܛܛܘܤ ܠܘ ܒܚܕܐ ܪܘܚܐ ܗܠܟܢ ܘܒܗܝܢ ܒܥܩܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 19, 'ܠܡܐ ܬܘܒ ܤܒܪܝܢ ܐܢܬܘܢ ܕܠܟܘܢ ܢܦܩܝܢܢ ܪܘܚܐ ܩܕܡ ܐܠܗܐ ܒܡܫܝܚܐ ܡܡܠܠܝܢܢ ܘܟܠܗܝܢ ܚܒܝܒܝ ܡܛܠ ܒܢܝܢܐ ܗܘ ܕܝܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 20, 'ܕܚܠ ܐܢܐ ܓܝܪ ܕܠܡܐ ܐܬܐ ܠܘܬܟܘܢ ܘܠܐ ܐܫܟܚܟܘܢ ܐܝܟ ܕܨܒܐ ܐܢܐ ܘܐܫܬܟܚ ܠܟܘܢ ܐܦ ܐܢܐ ܐܝܟ ܡܐ ܕܠܐ ܨܒܝܬܘܢ ܕܠܡܐ ܚܪܝܢܐ ܘܚܤܡܐ ܘܚܡܬܐ ܘܥܨܝܢܐ ܘܡܐܟܠ ܩܪܨܐ ܘܪܛܢܐ ܘܚܬܝܪܘܬܐ ܘܫܓܘܫܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 21, 'ܕܠܡܐ ܟܕ ܐܬܐ ܠܘܬܟܘܢ ܢܡܟܟܢܝ ܐܠܗܝ ܘܐܬܐܒܠ ܥܠ ܤܓܝܐܐ ܐܝܠܝܢ ܕܚܛܘ ܘܠܐ ܬܒܘ ܡܢ ܛܢܦܘܬܐ ܘܡܢ ܙܢܝܘܬܐ ܘܡܢ ܦܚܙܘܬܐ ܕܥܒܕܘ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 1, 'ܗܕܐ ܕܬܠܬ ܗܝ ܙܒܢܝܢ ܕܐܬܐ ܐܢܐ ܠܘܬܟܘܢ ܕܥܠ ܦܘܡ ܬܪܝܢ ܘܬܠܬܐ ܤܗܕܝܢ ܬܩܘܡ ܟܠ ܡܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 2, 'ܡܢ ܩܕܝܡ ܐܡܪܬ ܗܘܝܬ ܠܟܘܢ ܘܬܘܒ ܡܩܕܡ ܐܢܐ ܐܡܪ ܐܢܐ ܠܟܘܢ ܐܝܟ ܕܐܦ ܬܪܬܝܢ ܙܒܢܝܢ ܕܗܘܝܬ ܠܘܬܟܘܢ ܐܡܪܬ ܠܟܘܢ ܘܐܦ ܗܫܐ ܟܕ ܪܚܝܩ ܐܢܐ ܟܬܒܢܐ ܠܗܠܝܢ ܕܚܛܘ ܘܠܫܪܟܐ ܕܐܚܪܢܐ ܕܐܢ ܐܬܐ ܬܘܒ ܠܐ ܐܚܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 3, 'ܡܛܠ ܕܒܘܩܝܐ ܒܥܝܬܘܢ ܕܡܫܝܚܐ ܕܡܡܠܠ ܒܝ ܗܘ ܕܠܐ ܗܘܐ ܡܚܝܠ ܒܟܘܢ ܐܠܐ ܚܝܠܬܢ ܗܘ ܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 4, 'ܐܦܢ ܐܙܕܩܦ ܓܝܪ ܒܡܚܝܠܘܬܐ ܐܠܐ ܚܝ ܗܘ ܒܚܝܠܐ ܕܐܠܗܐ ܘܐܦ ܚܢܢ ܡܚܝܠܝܢܢ ܥܡܗ ܐܠܐ ܚܝܝܢ ܚܢܢ ܥܡܗ ܒܚܝܠܐ ܕܐܠܗܐ ܕܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 5, 'ܢܦܫܟܘܢ ܒܩܘ ܐܢ ܒܗ ܒܗܝܡܢܘܬܐ ܩܝܡܝܢ ܐܢܬܘܢ ܢܦܫܟܘܢ ܐܤܘ ܐܘ ܠܐ ܡܫܬܘܕܥܝܬܘܢ ܕܝܫܘܥ ܡܫܝܚܐ ܒܟܘܢ ܗܘ ܘܐܢ ܠܐ ܡܤܠܝܐ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 6, 'ܡܤܒܪ ܐܢܐ ܕܝܢ ܕܬܕܥܘܢ ܕܚܢܢ ܠܐ ܗܘܝܢ ܡܤܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 7, 'ܒܥܐ ܐܢܐ ܕܝܢ ܡܢ ܐܠܗܐ ܕܠܐ ܢܗܘܐ ܒܟܘܢ ܡܕܡ ܕܒܝܫ ܐܝܟ ܕܒܘܩܝܢ ܕܝܠܢ ܢܬܚܙܐ ܐܠܐ ܕܐܢܬܘܢ ܬܗܘܘܢ ܤܥܪܝܢ ܛܒܬܐ ܘܚܢܢ ܢܗܘܐ ܐܝܟ ܡܤܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 8, 'ܠܐ ܓܝܪ ܡܫܟܚܝܢܢ ܕܢܥܒܕ ܡܕܡ ܠܘܩܒܠ ܩܘܫܬܐ ܐܠܐ ܚܠܦ ܩܘܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 9, 'ܚܕܝܢܢ ܕܝܢ ܡܐ ܕܚܢܢ ܟܪܝܗܝܢ ܘܐܢܬܘܢ ܚܝܠܬܢܝܢ ܗܕܐ ܓܝܪ ܐܦ ܡܨܠܝܢܢ ܕܐܢܬܘܢ ܬܬܓܡܪܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 10, 'ܡܛܠ ܗܢܐ ܟܕ ܪܚܝܩ ܐܢܐ ܗܠܝܢ ܟܬܒ ܐܢܐ ܕܠܐ ܡܐ ܕܐܬܝܬ ܩܫܝܐܝܬ ܐܤܥܘܪ ܐܝܟ ܫܘܠܛܢܐ ܕܝܗܒ ܠܝ ܡܪܝ ܠܒܢܝܢܟܘܢ ܘܠܐ ܠܤܘܚܦܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 11, 'ܡܟܝܠ ܐܚܝ ܚܕܘ ܘܐܬܓܡܪܘ ܘܐܬܒܝܐܘ ܘܐܘܝܘܬܐ ܘܫܝܢܐ ܢܗܘܐ ܒܟܘܢ ܘܐܠܗܐ ܕܚܘܒܐ ܘܕܫܠܡܘܬܐ ܢܗܘܐ ܥܡܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 12, 'ܫܐܠܘ ܒܫܠܡܐ ܚܕ ܕܚܕ ܒܢܘܫܩܬܐ ܩܕܝܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 13, 'ܫܐܠܝܢ ܒܫܠܡܟܘܢ ܟܠܗܘܢ ܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 14, 'ܫܠܡܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܘܚܘܒܗ ܕܐܠܗܐ ܘܫܘܬܦܘܬܐ ܕܪܘܚܐ ܕܩܘܕܫܐ ܥܡ ܟܠܟܘܢ ܐܡܝܢ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 1, 'ܦܘܠܘܤ ܫܠܝܚܐ ܠܐ ܡܢ ܒܢܝܢܫܐ ܘܠܐ ܒܝܕ ܒܪܢܫܐ ܐܠܐ ܒܝܕ ܝܫܘܥ ܡܫܝܚܐ ܘܐܠܗܐ ܐܒܘܗܝ ܗܘ ܕܐܩܝܡܗ ܡܢ ܒܝܬ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 2, 'ܘܟܠܗܘܢ ܐܚܐ ܕܥܡܝ ܠܥܕܬܐ ܕܐܝܬ ܒܓܠܛܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 3, 'ܛܝܒܘܬܐ ܥܡܟܘܢ ܘܫܠܡܐ ܡܢ ܐܠܗܐ ܐܒܐ ܘܡܢ ܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 4, 'ܗܘ ܕܝܗܒ ܢܦܫܗ ܚܠܦ ܚܛܗܝܢ ܕܢܦܨܝܢ ܡܢ ܥܠܡܐ ܗܢܐ ܒܝܫܐ ܐܝܟ ܨܒܝܢܗ ܕܐܠܗܐ ܐܒܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 5, 'ܕܠܗ ܫܘܒܚܐ ܠܥܠܡ ܥܠܡܝܢ ܐܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 6, 'ܡܬܕܡܪ ܐܢܐ ܐܝܟܢܐ ܒܥܓܠ ܡܬܗܦܟܝܢ ܐܢܬܘܢ ܡܢ ܡܫܝܚܐ ܗܘ ܕܩܪܟܘܢ ܒܛܝܒܘܬܗ ܠܐܚܪܬܐ ܤܒܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 7, 'ܐܝܕܐ ܕܠܐ ܐܝܬܝܗ ܐܠܐ ܐܢܫܐ ܗܘ ܐܝܬ ܕܕܠܚܝܢ ܠܟܘܢ ܘܨܒܝܢ ܕܢܫܚܠܦܘܢ ܤܒܪܬܗ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 8, 'ܐܦ ܐܢ ܚܢܢ ܕܝܢ ܐܘ ܡܠܐܟܐ ܡܢ ܫܡܝܐ ܢܤܒܪܟܘܢ ܠܒܪ ܡܢ ܡܐ ܕܤܒܪܢܟܘܢ ܢܗܘܐ ܚܪܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 9, 'ܐܝܟܢܐ ܕܡܢ ܠܘܩܕܡ ܐܡܪܬ ܘܗܫܐ ܬܘܒ ܐܡܪ ܐܢܐ ܕܐܢ ܐܢܫ ܡܤܒܪ ܠܟܘܢ ܠܒܪ ܡܢ ܡܐ ܕܩܒܠܬܘܢ ܢܗܘܐ ܚܪܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 10, 'ܗܫܐ ܓܝܪ ܠܒܢܝܢܫܐ ܗܘ ܡܦܝܤ ܐܢܐ ܐܘ ܠܐܠܗܐ ܐܘ ܠܒܢܝܢܫܐ ܒܥܐ ܐܢܐ ܕܐܫܦܪ ܐܠܘ ܓܝܪ ܥܕܡܐ ܠܗܫܐ ܠܒܢܝܢܫܐ ܫܦܪ ܗܘܝܬ ܥܒܕܐ ܕܡܫܝܚܐ ܠܐ ܗܘܐ ܗܘܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 11, 'ܡܘܕܥ ܐܢܐ ܠܟܘܢ ܕܝܢ ܐܚܝ ܕܤܒܪܬܐ ܕܐܤܬܒܪܬ ܡܢܝ ܠܐ ܗܘܬ ܡܢ ܒܪܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 12, 'ܐܦܠܐ ܓܝܪ ܐܢܐ ܡܢ ܒܪܢܫܐ ܩܒܠܬܗ ܘܝܠܦܬܗ ܐܠܐ ܒܓܠܝܢܐ ܕܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 13, 'ܫܡܥܬܘܢ ܓܝܪ ܗܘܦܟܝ ܕܡܢ ܩܕܝܡ ܕܒܝܗܘܕܝܘܬܐ ܕܝܬܝܪܐܝܬ ܪܕܦ ܗܘܝܬ ܥܕܬܗ ܕܐܠܗܐ ܘܚܪܒ ܗܘܝܬ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 14, 'ܘܡܬܝܬܪ ܗܘܝܬ ܒܝܗܘܕܝܘܬܐ ܛܒ ܡܢ ܤܓܝܐܐ ܒܢܝ ܫܢܝ ܕܐܝܬ ܗܘܐ ܒܛܘܗܡܝ ܘܝܬܝܪܐܝܬ ܛܐܢ ܗܘܝܬ ܒܝܘܠܦܢܐ ܕܐܒܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 15, 'ܟܕ ܨܒܐ ܕܝܢ ܗܘ ܡܢ ܕܦܪܫܢܝ ܡܢ ܟܪܤ ܐܡܝ ܘܩܪܢܝ ܒܛܝܒܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 16, 'ܕܢܓܠܐ ܒܪܗ ܒܝ ܕܐܤܒܪܝܘܗܝ ܒܥܡܡܐ ܒܪ ܫܥܬܗ ܠܐ ܓܠܝܬ ܠܒܤܪܐ ܘܠܕܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 17, 'ܘܠܐ ܐܙܠܬ ܠܐܘܪܫܠܡ ܠܘܬ ܫܠܝܚܐ ܕܡܢ ܩܕܡܝ ܐܠܐ ܐܙܠܬ ܠܐܪܒܝܐ ܘܬܘܒ ܗܦܟܬ ܠܕܪܡܤܘܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 18, 'ܘܡܢ ܒܬܪ ܬܠܬ ܫܢܝܢ ܐܙܠܬ ܠܐܘܪܫܠܡ ܕܐܚܙܐ ܠܟܐܦܐ ܘܩܘܝܬ ܠܘܬܗ ܝܘܡܬܐ ܚܡܫܬܥܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 19, 'ܠܐܚܪܝܢ ܕܝܢ ܡܢ ܫܠܝܚܐ ܠܐ ܚܙܝܬ ܐܠܐ ܐܢ ܠܝܥܩܘܒ ܐܚܘܗܝ ܕܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 20, 'ܗܠܝܢ ܕܝܢ ܕܟܬܒ ܐܢܐ ܠܟܘܢ ܗܐ ܩܕܡ ܐܠܗܐ ܕܠܐ ܡܟܕܒ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 21, 'ܡܢ ܒܬܪ ܗܠܝܢ ܐܬܝܬ ܠܐܬܪܘܬܐ ܕܤܘܪܝܐ ܘܕܩܝܠܝܩܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 22, 'ܘܠܐ ܝܕܥܢ ܗܘܝ ܠܝ ܒܐܦܝܢ ܥܕܬܐ ܕܒܝܗܘܕ ܗܠܝܢ ܕܒܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 23, 'ܐܠܐ ܗܕܐ ܒܠܚܘܕ ܫܡܥܝܢ ܗܘܘ ܕܗܘ ܕܡܢ ܩܕܝܡ ܪܕܦ ܗܘܐ ܠܢ ܗܫܐ ܗܐ ܡܤܒܪ ܗܝܡܢܘܬܐ ܗܝ ܕܡܢ ܩܕܡ ܙܒܢܐ ܡܤܚܦ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 24, 'ܘܡܫܒܚܝܢ ܗܘܘ ܒܝ ܠܐܠܗܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 1, 'ܬܘܒ ܕܝܢ ܡܢ ܒܬܪ ܐܪܒܥܤܪܐ ܫܢܝܢ ܤܠܩܬ ܠܐܘܪܫܠܡ ܥܡ ܒܪܢܒܐ ܘܕܒܪܬ ܥܡܝ ܠܛܛܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 2, 'ܤܠܩܬ ܕܝܢ ܒܓܠܝܢܐ ܘܓܠܝܬ ܠܗܘܢ ܤܒܪܬܐ ܕܡܟܪܙ ܐܢܐ ܒܥܡܡܐ ܘܚܘܝܬܗ ܠܐܝܠܝܢ ܕܡܤܬܒܪܝܢ ܗܘܘ ܕܡܕܡ ܐܝܬܝܗܘܢ ܒܝܢܝ ܘܠܗܘܢ ܕܡ ܤܪܝܩܐܝܬ ܪܗܛܬ ܐܘ ܪܗܛ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 3, 'ܐܦ ܛܛܘܤ ܕܥܡܝ ܕܐܪܡܝܐ ܗܘܐ ܠܐ ܐܬܐܢܤ ܕܢܓܙܘܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 4, 'ܡܛܠ ܕܝܢ ܐܚܐ ܕܓܠܐ ܕܥܠܘ ܥܠܝܢ ܕܢܓܫܘܢ ܚܐܪܘܬܐ ܕܐܝܬ ܠܢ ܒܝܫܘܥ ܡܫܝܚܐ ܐܝܟܢܐ ܕܢܫܥܒܕܘܢܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 5, 'ܐܦܠܐ ܡܠܐ ܫܥܐ ܐܬܪܡܝܢ ܠܫܘܥܒܕܗܘܢ ܕܫܪܪܗ ܕܤܒܪܬܐ ܢܩܘܐ ܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 6, 'ܗܢܘܢ ܕܝܢ ܕܡܤܬܒܪܝܢ ܕܐܝܬܝܗܘܢ ܡܕܡ ܡܢ ܕܝܢ ܐܝܬܝܗܘܢ ܠܐ ܒܛܝܠ ܠܝ ܐܠܗܐ ܓܝܪ ܒܐܦܝ ܒܢܝܢܫܐ ܠܐ ܢܤܒ ܗܢܘܢ ܕܝܢ ܗܠܝܢ ܡܕܡ ܠܐ ܐܘܤܦܘ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 7, 'ܐܠܐ ܐܚܪܢܝܐܝܬ ܚܙܘ ܓܝܪ ܕܐܬܗܝܡܢܬ ܤܒܪܬܐ ܕܥܘܪܠܘܬܐ ܐܝܟ ܕܐܬܗܝܡܢ ܟܐܦܐ ܒܓܙܘܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 8, 'ܗܘ ܓܝܪ ܕܚܦܛ ܠܟܐܦܐ ܒܫܠܝܚܘܬܐ ܕܓܙܘܪܬܐ ܚܦܛ ܐܦ ܠܝ ܒܫܠܝܚܘܬܐ ܕܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 9, 'ܘܟܕ ܝܕܥܘ ܛܝܒܘܬܐ ܕܐܬܝܗܒܬ ܠܝ ܝܥܩܘܒ ܘܟܐܦܐ ܘܝܘܚܢܢ ܗܢܘܢ ܕܡܤܬܒܪܝܢ ܗܘܘ ܕܐܝܬܝܗܘܢ ܥܡܘܕܐ ܝܡܝܢܐ ܕܫܘܬܦܘܬܐ ܝܗܒܘ ܠܝ ܘܠܒܪܢܒܐ ܕܚܢܢ ܒܥܡܡܐ ܘܗܢܘܢ ܒܓܙܘܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 10, 'ܒܠܚܘܕ ܕܠܡܤܟܢܐ ܗܘܝܢ ܥܗܕܝܢܢ ܘܐܬܒܛܠ ܠܝ ܕܗܝ ܗܕܐ ܐܥܒܕܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 11, 'ܟܕ ܐܬܐ ܕܝܢ ܟܐܦܐ ܠܐܢܛܝܘܟܝ ܒܐܦܘܗܝ ܐܟܤܬܗ ܡܛܠ ܕܡܬܬܩܠܝܢ ܗܘܘ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 12, 'ܕܥܕܠܐ ܢܐܬܘܢ ܐܢܫܐ ܡܢ ܠܘܬ ܝܥܩܘܒ ܥܡ ܥܡܡܐ ܐܟܠ ܗܘܐ ܟܕ ܐܬܘ ܕܝܢ ܢܓܕ ܗܘܐ ܢܦܫܗ ܘܦܪܫ ܡܛܠ ܕܕܚܠ ܗܘܐ ܡܢ ܗܢܘܢ ܕܡܢ ܓܙܘܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 13, 'ܘܐܬܪܡܝܘ ܗܘܘ ܥܡܗ ܠܗܕܐ ܐܦ ܫܪܟܐ ܕܝܗܘܕܝܐ ܗܟܢܐ ܕܐܦ ܒܪܢܒܐ ܐܬܕܒܪ ܗܘܐ ܠܡܤܒ ܒܐܦܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 14, 'ܘܟܕ ܚܙܝܬ ܕܠܐ ܐܙܠܝܢ ܬܪܝܨܐܝܬ ܒܫܪܪܗ ܕܐܘܢܓܠܝܘܢ ܐܡܪܬ ܠܟܐܦܐ ܠܥܝܢ ܟܠܗܘܢ ܐܢ ܐܢܬ ܕܝܗܘܕܝܐ ܐܢܬ ܐܪܡܐܝܬ ܚܝܐ ܐܢܬ ܘܠܐ ܝܗܘܕܐܝܬ ܐܝܟܢܐ ܐܠܨ ܐܢܬ ܠܥܡܡܐ ܕܝܗܘܕܐܝܬ ܢܚܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 15, 'ܚܢܢ ܓܝܪ ܕܡܢ ܟܝܢܢ ܝܗܘܕܝܐ ܚܢܢ ܘܠܐ ܗܘܝܢ ܡܢ ܥܡܡܐ ܚܛܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 16, 'ܡܛܠ ܕܝܕܥܝܢܢ ܕܠܐ ܡܙܕܕܩ ܒܪܢܫܐ ܡܢ ܥܒܕܐ ܕܢܡܘܤܐ ܐܠܐ ܒܗܝܡܢܘܬܐ ܕܝܫܘܥ ܡܫܝܚܐ ܐܦ ܚܢܢ ܒܗ ܒܝܫܘܥ ܡܫܝܚܐ ܗܝܡܢܢ ܕܡܢ ܗܝܡܢܘܬܗ ܕܡܫܝܚܐ ܢܙܕܕܩ ܘܠܐ ܡܢ ܥܒܕܐ ܕܢܡܘܤܐ ܡܛܠ ܕܡܢ ܥܒܕܐ ܕܢܡܘܤܐ ܠܐ ܡܙܕܕܩ ܟܠ ܒܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 17, 'ܐܢ ܕܝܢ ܟܕ ܒܥܝܢܢ ܕܢܙܕܕܩ ܒܡܫܝܚܐ ܐܫܬܟܚܢ ܠܢ ܐܦ ܚܢܢ ܚܛܝܐ ܡܕܝܢ ܝܫܘܥ ܡܫܝܚܐ ܡܫܡܫܢܐ ܗܘ ܕܚܛܝܬܐ ܚܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 18, 'ܐܢ ܓܝܪ ܠܐܝܠܝܢ ܕܤܬܪܬ ܬܘܒ ܠܗܝܢ ܒܢܐ ܐܢܐ ܚܘܝܬ ܥܠ ܢܦܫܝ ܕܥܒܪ ܥܠ ܦܘܩܕܢܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 19, 'ܐܢܐ ܓܝܪ ܒܢܡܘܤܐ ܠܢܡܘܤܐ ܡܝܬܬ ܕܠܐܠܗܐ ܐܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 20, 'ܘܥܡ ܡܫܝܚܐ ܙܩܝܦ ܐܢܐ ܘܡܟܝܠ ܠܐ ܗܘܐ ܐܢܐ ܚܝ ܐܢܐ ܐܠܐ ܚܝ ܒܝ ܡܫܝܚܐ ܘܗܢܐ ܕܗܫܐ ܚܝ ܐܢܐ ܒܒܤܪ ܒܗܝܡܢܘܬܐ ܗܘ ܕܒܪܗ ܕܐܠܗܐ ܚܝ ܐܢܐ ܗܘ ܕܐܚܒܢ ܘܝܗܒ ܢܦܫܗ ܚܠܦܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 21, 'ܠܐ ܛܠܡ ܐܢܐ ܛܝܒܘܬܗ ܕܐܠܗܐ ܐܢ ܓܝܪ ܒܝܕ ܢܡܘܤܐ ܗܝ ܙܕܝܩܘܬܐ ܡܫܝܚܐ ܡܓܢ ܡܝܬ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 1, 'ܐܘ ܚܤܝܪܝ ܪܥܝܢܐ ܓܠܛܝܐ ܡܢܘ ܚܤܡ ܒܟܘܢ ܕܗܐ ܐܝܟ ܗܘ ܕܡܨܪ ܨܝܪ ܗܘܐ ܩܕܡ ܥܝܢܝܟܘܢ ܝܫܘܥ ܡܫܝܚܐ ܟܕ ܨܠܝܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 2, 'ܗܕܐ ܒܠܚܘܕ ܨܒܐ ܐܢܐ ܕܐܕܥ ܡܢܟܘܢ ܡܢ ܥܒܕܐ ܕܢܡܘܤܐ ܢܤܒܬܘܢ ܪܘܚܐ ܐܘ ܡܢ ܫܡܥܐ ܕܗܝܡܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 3, 'ܗܟܢܐ ܤܟܠܝܢ ܐܢܬܘܢ ܕܫܪܝܬܘܢ ܒܪܘܚܐ ܘܗܫܐ ܒܒܤܪ ܡܫܠܡܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 4, 'ܗܠܝܢ ܟܠܗܝܢ ܐܝܩܐ ܤܝܒܪܬܘܢ ܘܐܫܬܘܦ ܕܝܢ ܐܝܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 5, 'ܗܘ ܗܟܝܠ ܕܝܗܒ ܒܟܘܢ ܪܘܚܐ ܘܤܥܪ ܒܟܘܢ ܚܝܠܐ ܡܢ ܥܒܕܐ ܕܢܡܘܤܐ ܐܘ ܡܢ ܫܡܥܐ ܕܗܝܡܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 6, 'ܐܝܟܢܐ ܕܗܝܡܢ ܐܒܪܗܡ ܠܐܠܗܐ ܘܐܬܚܫܒܬ ܠܗ ܠܙܕܝܩܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 7, 'ܕܥܘ ܗܟܝܠ ܕܐܝܠܝܢ ܕܡܢ ܗܝܡܢܘܬܐ ܐܢܘܢ ܗܢܘܢ ܐܢܘܢ ܒܢܝܐ ܕܐܒܪܗܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 8, 'ܡܛܠ ܓܝܪ ܕܩܕܡ ܝܕܥ ܐܠܗܐ ܕܒܗܝܡܢܘܬܐ ܗܘ ܡܙܕܕܩܝܢ ܥܡܡܐ ܩܕܡ ܤܒܪ ܠܐܒܪܗܡ ܐܝܟ ܕܐܡܪ ܟܬܒܐ ܩܕܝܫܐ ܕܒܟ ܢܬܒܪܟܘܢ ܟܠܗܘܢ ܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 9, 'ܡܕܝܢ ܡܗܝܡܢܐ ܗܘ ܡܬܒܪܟܝܢ ܒܐܒܪܗܡ ܡܗܝܡܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 10, 'ܐܝܠܝܢ ܓܝܪ ܕܡܢ ܥܒܕܐ ܐܢܘܢ ܕܢܡܘܤܐ ܬܚܝܬ ܠܘܛܬܐ ܐܢܘܢ ܟܬܝܒ ܓܝܪ ܕܠܝܛ ܗܘ ܟܠܡܢ ܕܠܐ ܢܥܒܕ ܟܠ ܕܟܬܝܒ ܒܢܡܘܤܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 11, 'ܕܠܐ ܕܝܢ ܡܙܕܕܩ ܐܢܫ ܒܢܡܘܤܐ ܠܘܬ ܐܠܗܐ ܗܕܐ ܓܠܝܐ ܗܝ ܡܛܠ ܕܟܬܝܒ ܕܙܕܝܩܐ ܒܗܝܡܢܘܬܐ ܢܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 12, 'ܢܡܘܤܐ ܕܝܢ ܠܐ ܗܘܐ ܡܢ ܗܝܡܢܘܬܐ ܐܠܐ ܡܢ ܕܢܥܒܕ ܐܝܠܝܢ ܕܟܬܝܒܢ ܒܗ ܢܚܐ ܒܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 13, 'ܠܢ ܕܝܢ ܡܫܝܚܐ ܙܒܢܢ ܡܢ ܠܘܛܬܐ ܕܢܡܘܤܐ ܘܗܘ ܗܘܐ ܚܠܦܝܢ ܠܘܛܬܐ ܟܬܝܒ ܓܝܪ ܕܠܝܛ ܗܘ ܟܠܡܢ ܕܡܬܬܠܐ ܒܩܝܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 14, 'ܕܒܥܡܡܐ ܬܗܘܐ ܒܘܪܟܬܗ ܕܐܒܪܗܡ ܒܝܫܘܥ ܡܫܝܚܐ ܘܚܢܢ ܢܤܒ ܫܘܘܕܝܐ ܕܪܘܚܐ ܒܗܝܡܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 15, 'ܐܚܝ ܐܝܟ ܕܒܝܬ ܒܢܝܢܫܐ ܐܡܪ ܐܢܐ ܕܕܝܬܩܐ ܕܒܪܢܫܐ ܕܐܫܬܪܪܬ ܐܢܫ ܠܐ ܡܤܠܐ ܐܘ ܡܫܚܠܦ ܒܗ ܡܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 16, 'ܠܐܒܪܗܡ ܕܝܢ ܐܬܡܠܟ ܡܘܠܟܢܐ ܘܠܙܪܥܗ ܘܠܐ ܐܡܪ ܠܗ ܕܠܙܪܥܝܟ ܐܝܟ ܕܠܤܓܝܐܐ ܐܠܐ ܠܙܪܥܟ ܐܝܟ ܕܠܚܕ ܗܘ ܕܐܝܬܘܗܝ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 17, 'ܗܕܐ ܕܝܢ ܐܡܪ ܐܢܐ ܕܕܝܬܩܐ ܕܡܢ ܩܕܝܡ ܐܫܬܪܪܬ ܡܢ ܐܠܗܐ ܒܡܫܝܚܐ ܢܡܘܤܐ ܗܘ ܕܗܘܐ ܡܢ ܒܬܪ ܐܪܒܥ ܡܐܐ ܘܬܠܬܝܢ ܫܢܝܢ ܠܐ ܡܫܟܚ ܕܢܤܠܝܗ ܘܢܒܛܠ ܡܘܠܟܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 18, 'ܐܢ ܕܝܢ ܡܢ ܢܡܘܤܐ ܗܝ ܝܪܬܘܬܐ ܡܟܝܠ ܠܐ ܗܘܬ ܡܢ ܡܘܠܟܢܐ ܠܐܒܪܗܡ ܕܝܢ ܒܡܘܠܟܢܐ ܗܘ ܝܗܒ ܠܗ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 19, 'ܡܢܐ ܗܟܝܠ ܢܡܘܤܐ ܡܛܠ ܡܤܛܝܢܘܬܐ ܐܬܬܘܤܦ ܥܕܡܐ ܕܢܐܬܐ ܙܪܥܐ ܗܘ ܕܠܗ ܗܘܐ ܫܘܘܕܝܐ ܘܐܬܝܗܒ ܗܘ ܢܡܘܤܐ ܒܝܕ ܡܠܐܟܐ ܒܐܝܕܐ ܕܡܨܥܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 20, 'ܡܨܥܝܐ ܕܝܢ ܠܐ ܗܘܐ ܕܚܕ ܐܠܗܐ ܕܝܢ ܚܕ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 21, 'ܢܡܘܤܐ ܗܟܝܠ ܤܩܘܒܠܐ ܗܘ ܕܡܘܠܟܢܐ ܕܐܠܗܐ ܚܤ ܐܠܘ ܓܝܪ ܐܬܝܗܒ ܗܘܐ ܢܡܘܤܐ ܐܝܢܐ ܕܡܫܟܚ ܗܘܐ ܠܡܚܝܘ ܫܪܝܪܐܝܬ ܡܢ ܢܡܘܤܐ ܗܘܝܐ ܗܘܬ ܙܕܝܩܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 22, 'ܐܠܐ ܚܒܫ ܟܬܒܐ ܟܠܡܕܡ ܬܚܝܬ ܚܛܝܬܐ ܕܡܘܠܟܢܐ ܒܗܝܡܢܘܬܐ ܕܝܫܘܥ ܡܫܝܚܐ ܢܬܝܗܒ ܠܐܝܠܝܢ ܕܡܗܝܡܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 23, 'ܥܕܠܐ ܕܝܢ ܬܐܬܐ ܗܝܡܢܘܬܐ ܢܡܘܤܐ ܢܛܪ ܗܘܐ ܠܢ ܟܕ ܚܒܝܫܝܢܢ ܠܗܝܡܢܘܬܐ ܕܥܬܝܕܐ ܗܘܬ ܠܡܬܓܠܝܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 24, 'ܢܡܘܤܐ ܗܟܝܠ ܬܪܐܐ ܗܘܐ ܠܢ ܠܘܬ ܡܫܝܚܐ ܕܡܢ ܗܝܡܢܘܬܐ ܢܙܕܕܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 25, 'ܟܕ ܐܬܬ ܕܝܢ ܗܝܡܢܘܬܐ ܠܐ ܗܘܝܢ ܬܚܝܬ ܬܪܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 26, 'ܟܠܟܘܢ ܓܝܪ ܒܢܝܐ ܐܢܬܘܢ ܕܐܠܗܐ ܒܗܝܡܢܘܬܐ ܕܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 27, 'ܐܝܠܝܢ ܓܝܪ ܕܒܡܫܝܚܐ ܥܡܕܬܘܢ ܠܡܫܝܚܐ ܠܒܫܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 28, 'ܠܝܬ ܝܗܘܕܝܐ ܘܠܐ ܐܪܡܝܐ ܠܝܬ ܥܒܕܐ ܘܠܐ ܒܪ ܚܐܪܐ ܠܝܬ ܕܟܪܐ ܘܠܐ ܢܩܒܬܐ ܟܠܟܘܢ ܓܝܪ ܚܕ ܐܢܬܘܢ ܒܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 29, 'ܘܐܢ ܕܡܫܝܚܐ ܐܢܬܘܢ ܡܟܝܠ ܙܪܥܗ ܐܢܬܘܢ ܕܐܒܪܗܡ ܘܝܪܬܐ ܒܡܘܠܟܢܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 1, 'ܐܡܪ ܐܢܐ ܕܝܢ ܕܟܡܐ ܙܒܢܐ ܕܝܪܬܐ ܛܠܐ ܠܐ ܦܪܝܫ ܡܢ ܥܒܕܐ ܟܕ ܡܪܐ ܗܘ ܕܟܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 2, 'ܐܠܐ ܬܚܝܬ ܐܦܛܪܘܦܐ ܐܝܬܘܗܝ ܘܪܒܝ ܒܬܐ ܥܕܡܐ ܠܙܒܢܐ ܕܤܡ ܐܒܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 3, 'ܗܟܢܐ ܐܦ ܚܢܢ ܟܕ ܝܠܘܕܐ ܗܘܝܢ ܬܚܝܬ ܐܤܛܘܟܤܘܗܝ ܕܥܠܡܐ ܡܫܥܒܕܝܢ ܗܘܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 4, 'ܟܕ ܡܛܐ ܕܝܢ ܫܘܠܡܗ ܕܙܒܢܐ ܫܕܪ ܐܠܗܐ ܠܒܪܗ ܘܗܘܐ ܡܢ ܐܢܬܬܐ ܘܗܘܐ ܬܚܝܬ ܢܡܘܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 5, 'ܕܠܐܝܠܝܢ ܕܬܚܝܬ ܢܡܘܤܐ ܐܢܘܢ ܢܙܒܢ ܘܢܩܒܠ ܤܝܡܬ ܒܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 6, 'ܘܕܐܝܬܝܟܘܢ ܕܝܢ ܒܢܝܐ ܫܕܪ ܐܠܗܐ ܪܘܚܐ ܕܒܪܗ ܠܠܒܘܬܟܘܢ ܗܝ ܕܩܪܝܐ ܐܒܐ ܐܒܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 7, 'ܡܟܝܠ ܠܐ ܗܘܝܬܘܢ ܥܒܕܐ ܐܠܐ ܒܢܝܐ ܘܐܢ ܒܢܝܐ ܐܦ ܝܪܬܐ ܕܐܠܗܐ ܒܝܕ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 8, 'ܗܝܕܝܢ ܓܝܪ ܟܕ ܠܐ ܝܕܥܝܢ ܗܘܝܬܘܢ ܠܐܠܗܐ ܦܠܚܬܘܢ ܠܗܢܘܢ ܕܡܢ ܟܝܢܗܘܢ ܠܐ ܗܘܘ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 9, 'ܗܫܐ ܕܝܢ ܕܝܕܥܬܘܢ ܠܐܠܗܐ ܘܝܬܝܪܐܝܬ ܕܐܬܝܕܥܬܘܢ ܡܢ ܐܠܗܐ ܬܘܒ ܗܦܟܬܘܢ ܠܟܘܢ ܥܠ ܗܢܘܢ ܐܤܛܘܟܤܐ ܡܪܥܐ ܘܡܤܟܢܐ ܘܡܢ ܕܪܝܫ ܨܒܝܬܘܢ ܠܡܫܬܥܒܕܘ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 10, 'ܝܘܡܐ ܘܝܪܚܐ ܘܙܒܢܐ ܘܫܢܝܐ ܢܛܪܝܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 11, 'ܕܚܠ ܐܢܐ ܕܠܡܐ ܤܪܝܩܐܝܬ ܠܐܝܬ ܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 12, 'ܗܘܘ ܐܟܘܬܝ ܡܛܠ ܕܐܦ ܐܢܐ ܐܟܘܬܟܘܢ ܗܘܝܬ ܐܚܝ ܒܥܐ ܐܢܐ ܡܢܟܘܢ ܠܐ ܡܕܡ ܐܤܟܠܬܘܢ ܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 13, 'ܝܕܥܝܢ ܐܢܬܘܢ ܓܝܪ ܕܒܟܪܝܗܘܬ ܒܤܪܝ ܤܒܪܬܟܘܢ ܗܘܝܬ ܡܢ ܩܕܝܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 14, 'ܘܢܤܝܘܢܐ ܕܒܤܪܝ ܠܐ ܫܛܬܘܢ ܘܠܐ ܢܕܬܘܢ ܐܠܐ ܐܝܟ ܕܠܡܠܐܟܐ ܕܐܠܗܐ ܩܒܠܬܘܢܢܝ ܘܐܝܟ ܕܠܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 15, 'ܐܝܟܘ ܗܟܝܠ ܛܘܒܟܘܢ ܤܗܕ ܐܢܐ ܓܝܪ ܥܠܝܟܘܢ ܕܐܠܘ ܡܫܟܚܐ ܗܘܬ ܥܝܢܝܟܘܢ ܚܨܝܢ ܗܘܝܬܘܢ ܘܝܗܒܝܢ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 16, 'ܕܠܡܐ ܒܥܠܕܒܒܐ ܗܘܝܬ ܠܟܘܢ ܕܐܟܪܙܬ ܠܟܘܢ ܫܪܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 17, 'ܚܤܡܝܢ ܒܟܘܢ ܠܐ ܗܘܐ ܠܫܦܝܪܬܐ ܐܠܐ ܠܡܚܒܫܟܘܢ ܗܘ ܨܒܝܢ ܕܐܢܬܘܢ ܬܗܘܘܢ ܚܤܡܝܢ ܒܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 18, 'ܫܦܝܪ ܗܘ ܕܝܢ ܕܬܬܚܤܡܘܢ ܒܫܦܝܪܬܐ ܒܟܠܙܒܢ ܘܠܐ ܐܡܬܝ ܕܠܘܬܟܘܢ ܐܢܐ ܒܠܚܘܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 19, 'ܒܢܝ ܐܝܠܝܢ ܕܡܢ ܕܪܝܫ ܡܚܒܠ ܐܢܐ ܥܕܡܐ ܕܢܬܬܨܝܪ ܒܟܘܢ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 20, 'ܨܒܐ ܗܘܝܬ ܕܝܢ ܕܐܗܘܐ ܠܘܬܟܘܢ ܗܫܐ ܘܐܫܚܠܦ ܒܪܬ ܩܠܝ ܡܛܠ ܕܬܡܝܗ ܐܢܐ ܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 21, 'ܐܡܪܘ ܠܝ ܐܢܬܘܢ ܐܝܠܝܢ ܕܨܒܝܢ ܕܢܗܘܘܢ ܬܚܝܬ ܢܡܘܤܐ ܠܗ ܠܢܡܘܤܐ ܠܐ ܫܡܥܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 22, 'ܟܬܝܒ ܓܝܪ ܕܠܐܒܪܗܡ ܬܪܝܢ ܒܢܝܢ ܗܘܘ ܠܗ ܚܕ ܡܢ ܐܡܬܐ ܘܚܕ ܡܢ ܚܐܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 23, 'ܐܠܐ ܗܘ ܕܡܢ ܐܡܬܐ ܒܒܤܪ ܐܬܝܠܕ ܗܘ ܕܝܢ ܕܡܢ ܚܐܪܬܐ ܒܡܘܠܟܢܐ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 24, 'ܗܠܝܢ ܕܝܢ ܐܝܬܝܗܝܢ ܦܠܐܬܐ ܕܬܪܬܝܢ ܕܝܬܩܤ ܚܕܐ ܕܡܢ ܛܘܪ ܤܝܢܝ ܝܠܕܐ ܠܥܒܕܘܬܐ ܕܐܝܬܝܗ ܗܓܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 25, 'ܗܓܪ ܓܝܪ ܛܘܪܐ ܗܘ ܕܤܝܢܝ ܕܒܐܪܒܝܐ ܘܫܠܡܐ ܠܗܕܐ ܐܘܪܫܠܡ ܘܦܠܚܐ ܥܒܕܘܬܐ ܗܝ ܘܒܢܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 26, 'ܗܝ ܕܝܢ ܐܘܪܫܠܡ ܥܠܝܬܐ ܚܐܪܬܐ ܗܝ ܕܐܝܬܝܗ ܐܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 27, 'ܟܬܝܒ ܓܝܪ ܕܐܬܒܤܡܝ ܥܩܪܬܐ ܗܝ ܕܠܐ ܝܠܕܐ ܘܐܬܦܨܚܝ ܘܓܥܝ ܗܝ ܕܠܐ ܡܚܒܠܐ ܡܛܠ ܕܤܓܝܘ ܒܢܝܗ ܕܨܕܝܬܐ ܝܬܝܪ ܡܢ ܒܢܝܗ ܕܒܥܝܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 28, 'ܚܢܢ ܕܝܢ ܐܚܝ ܐܝܟ ܐܝܤܚܩ ܒܢܝ ܡܘܠܟܢܐ ܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 29, 'ܘܐܝܟ ܕܗܝܕܝܢ ܗܘ ܕܝܠܝܕ ܗܘܐ ܒܒܤܪܐ ܪܕܦ ܗܘܐ ܠܗܘ ܕܒܪܘܚܐ ܗܟܢܐ ܐܦ ܗܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 30, 'ܐܠܐ ܡܢܐ ܐܡܪ ܟܬܒܐ ܐܦܩܝܗ ܠܐܡܬܐ ܘܠܒܪܗ ܡܛܠ ܕܠܐ ܢܐܪܬ ܒܪܗ ܕܐܡܬܐ ܥܡ ܒܪܗ ܕܚܐܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 31, 'ܚܢܢ ܗܟܝܠ ܐܚܝ ܠܐ ܗܘܝܢ ܒܢܝ ܐܡܬܐ ܐܠܐ ܒܢܝ ܚܐܪܬܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 1, 'ܩܘܡܘ ܗܟܝܠ ܒܚܐܪܘܬܐ ܗܝ ܕܡܫܝܚܐ ܚܪܪܢ ܘܠܐ ܬܬܟܕܢܘܢ ܬܘܒ ܒܢܝܪܐ ܕܥܒܕܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 2, 'ܗܐ ܐܢܐ ܦܘܠܘܤ ܐܡܪ ܐܢܐ ܠܟܘܢ ܕܐܢ ܬܬܓܙܪܘܢ ܡܫܝܚܐ ܡܕܡ ܠܐ ܡܗܢܐ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 3, 'ܡܤܗܕ ܐܢܐ ܕܝܢ ܬܘܒ ܠܟܠ ܒܪܢܫ ܕܡܬܓܙܪ ܕܚܝܒ ܗܘ ܕܟܠܗ ܢܡܘܤܐ ܢܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 4, 'ܐܬܒܛܠܬܘܢ ܠܟܘܢ ܡܢ ܡܫܝܚܐ ܐܝܠܝܢ ܕܒܢܡܘܤܐ ܡܙܕܕܩܝܬܘܢ ܘܡܢ ܛܝܒܘܬܐ ܢܦܠܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 5, 'ܚܢܢ ܓܝܪ ܒܪܘܚܐ ܕܡܢ ܗܝܡܢܘܬܐ ܠܤܒܪܐ ܕܙܕܝܩܘܬܐ ܡܩܘܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 6, 'ܒܡܫܝܚܐ ܓܝܪ ܝܫܘܥ ܠܐ ܓܙܘܪܬܐ ܡܕܡ ܐܝܬܝܗ ܘܠܐ ܥܘܪܠܘܬܐ ܐܠܐ ܗܝܡܢܘܬܐ ܕܡܬܓܡܪܐ ܒܚܘܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 7, 'ܫܦܝܪ ܪܗܛܝܢ ܗܘܝܬܘܢ ܡܢܘ ܕܘܕܟܘܢ ܕܠܫܪܪܐ ܠܐ ܬܬܛܦܝܤܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 8, 'ܦܝܤܟܘܢ ܠܐ ܗܘܐ ܡܢ ܗܘ ܕܩܪܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 9, 'ܚܡܝܪܐ ܩܠܝܠ ܟܠܗ ܓܒܝܠܬܐ ܡܚܡܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 10, 'ܐܢܐ ܬܟܝܠ ܐܢܐ ܥܠܝܟܘܢ ܒܡܪܢ ܕܡܕܡ ܐܚܪܝܢ ܠܐ ܡܬܪܥܝܬܘܢ ܘܐܝܢܐ ܕܕܠܚ ܠܟܘܢ ܗܘ ܢܤܝܒܪܝܘܗܝ ܠܕܝܢܐ ܡܢ ܕܐܝܬܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 11, 'ܐܢܐ ܕܝܢ ܐܚܝ ܐܠܘ ܥܕܟܝܠ ܓܙܘܪܬܐ ܡܟܪܙ ܗܘܝܬ ܠܡܢܐ ܡܬܪܕܦ ܗܘܝܬ ܕܠܡܐ ܐܬܒܛܠ ܠܗ ܟܫܠܗ ܕܙܩܝܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 12, 'ܐܫܬܘܦ ܕܝܢ ܐܦ ܡܦܤܩ ܢܦܤܩܘܢ ܗܢܘܢ ܕܕܠܚܝܢ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 13, 'ܐܢܬܘܢ ܕܝܢ ܠܚܐܪܘܬܐ ܗܘ ܐܬܩܪܝܬܘܢ ܐܚܝ ܒܠܚܘܕ ܠܐ ܬܗܘܐ ܚܐܪܘܬܟܘܢ ܠܥܠܬ ܒܤܪܐ ܐܠܐ ܒܚܘܒܐ ܗܘܝܬܘܢ ܡܫܬܥܒܕܝܢ ܚܕ ܠܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 14, 'ܟܠܗ ܓܝܪ ܢܡܘܤܐ ܒܚܕܐ ܡܠܬܐ ܡܬܡܠܐ ܒܗܝ ܕܬܚܒ ܠܩܪܝܒܟ ܐܝܟ ܢܦܫܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 15, 'ܐܢ ܕܝܢ ܚܕ ܠܚܕ ܡܢܟܬܝܬܘܢ ܘܐܟܠܝܬܘܢ ܚܙܘ ܕܠܡܐ ܚܕ ܡܢ ܚܕ ܬܤܘܦܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 16, 'ܐܡܪ ܐܢܐ ܕܝܢ ܕܒܪܘܚܐ ܗܘܝܬܘܢ ܡܗܠܟܝܢ ܘܪܓܬܐ ܕܒܤܪܐ ܠܐ ܡܢ ܡܬܘܡ ܬܥܒܕܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 17, 'ܒܤܪܐ ܓܝܪ ܪܐܓ ܡܕܡ ܕܢܟܐ ܠܪܘܚܐ ܘܪܘܚܐ ܪܓܐ ܡܕܡ ܕܢܟܐ ܠܒܤܪܐ ܘܬܪܝܗܘܢ ܤܩܘܒܠܐ ܐܢܘܢ ܚܕ ܕܚܕ ܕܠܐ ܡܕܡ ܕܨܒܝܬܘܢ ܬܗܘܘܢ ܥܒܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 18, 'ܐܢ ܕܝܢ ܒܪܘܚܐ ܡܬܕܒܪܝܢ ܐܢܬܘܢ ܠܐ ܗܘܝܬܘܢ ܬܚܝܬ ܢܡܘܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 19, 'ܝܕܝܥܝܢ ܐܢܘܢ ܓܝܪ ܥܒܕܘܗܝ ܕܒܤܪܐ ܕܐܝܬܝܗܘܢ ܙܢܝܘܬܐ ܛܢܦܘܬܐ ܨܚܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 20, 'ܦܘܠܚܢܐ ܕܦܬܟܪܐ ܚܪܫܘܬܐ ܒܥܠܕܒܒܘܬܐ ܚܪܝܢܐ ܛܢܢܐ ܚܡܬܐ ܥܨܝܢܐ ܦܠܓܘܬܐ ܤܕܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 21, 'ܚܤܡܐ ܩܛܠܐ ܪܘܝܘܬܐ ܙܡܪܐ ܘܟܠ ܕܠܗܠܝܢ ܕܡܝܢ ܘܐܝܠܝܢ ܕܗܠܝܢ ܤܥܪܝܢ ܐܝܟ ܕܡܢ ܠܘܩܕܡ ܐܡܪܬ ܠܟܘܢ ܐܦ ܗܫܐ ܐܡܪ ܐܢܐ ܕܡܠܟܘܬܗ ܕܐܠܗܐ ܠܐ ܝܪܬܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 22, 'ܦܐܪܐ ܕܝܢ ܕܪܘܚܐ ܐܝܬܝܗܘܢ ܚܘܒܐ ܚܕܘܬܐ ܫܠܡܐ ܡܓܪܬ ܪܘܚܐ ܒܤܝܡܘܬܐ ܛܒܘܬܐ ܗܝܡܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 23, 'ܡܟܝܟܘܬܐ ܡܤܝܒܪܢܘܬܐ ܥܠ ܗܠܝܢ ܢܡܘܤܐ ܠܐ ܤܝܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 24, 'ܐܝܠܝܢ ܕܝܢ ܕܕܡܫܝܚܐ ܐܢܘܢ ܒܤܪܗܘܢ ܙܩܦܘ ܥܡ ܟܠܗܘܢ ܟܐܒܘܗܝ ܘܪܓܝܓܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 25, 'ܢܚܐ ܗܟܝܠ ܒܪܘܚܐ ܘܠܪܘܚܐ ܢܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 26, 'ܘܠܐ ܢܗܘܐ ܤܪܝܩܝ ܫܘܒܚܐ ܕܡܩܠܝܢ ܚܕ ܥܠ ܚܕ ܘܚܤܡܝܢ ܚܕ ܒܚܕ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 1, 'ܐܚܝ ܐܢ ܐܢܫ ܡܢܟܘܢ ܢܬܩܕܡ ܒܤܟܠܘܬܐ ܐܢܬܘܢ ܐܝܠܝܢ ܕܒܪܘܚ ܐܢܬܘܢ ܐܬܩܢܘܗܝ ܒܪܘܚܐ ܡܟܝܟܬܐ ܘܗܘܝܬܘܢ ܙܗܝܪܝܢ ܕܕܠܡܐ ܐܦ ܐܢܬܘܢ ܬܬܢܤܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 2, 'ܘܛܥܢܘ ܝܘܩܪܐ ܕܚܕܕܐ ܕܗܟܢܐ ܡܡܠܝܬܘܢ ܢܡܘܤܗ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 3, 'ܐܢ ܓܝܪ ܐܢܫ ܤܒܪ ܕܐܝܬܘܗܝ ܡܕܡ ܟܕ ܠܐ ܐܝܬܘܗܝ ܢܦܫܗ ܡܛܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 4, 'ܐܠܐ ܐܢܫ ܥܒܕܗ ܢܗܘܐ ܒܩܐ ܘܗܝܕܝܢ ܒܝܢܘܗܝ ܠܢܦܫܗ ܢܗܘܐ ܫܘܒܗܪܗ ܘܠܐ ܒܐܚܪܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 5, 'ܟܠܢܫ ܓܝܪ ܡܘܒܠܐ ܕܢܦܫܗ ܢܫܩܘܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 6, 'ܢܫܬܘܬܦ ܕܝܢ ܗܘ ܕܫܡܥ ܡܠܬܐ ܠܗܘ ܡܢ ܕܡܫܡܥ ܠܗ ܒܟܠܗܝܢ ܛܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 7, 'ܠܐ ܬܛܥܘܢ ܐܠܗܐ ܠܐ ܡܬܒܙܚ ܡܕܡ ܕܙܪܥ ܓܝܪ ܒܪ ܐܢܫܐ ܗܘ ܗܘ ܚܨܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 8, 'ܡܢ ܕܒܒܤܪ ܙܪܥ ܡܢ ܒܤܪܐ ܚܒܠܐ ܗܘ ܚܨܕ ܘܡܢ ܕܒܪܘܚ ܙܪܥ ܡܢ ܪܘܚܐ ܚܝܐ ܕܠܥܠܡ ܢܚܨܘܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 9, 'ܘܟܕ ܥܒܕܝܢܢ ܕܛܒ ܠܐ ܗܘܬ ܡܐܢܐ ܠܢ ܢܗܘܐ ܓܝܪ ܙܒܢܐ ܕܢܚܨܘܕ ܘܠܐ ܬܡܐܢ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 10, 'ܗܫܐ ܗܟܝܠ ܥܕ ܙܒܢܐ ܐܝܬ ܠܢ ܢܦܠܘܚ ܛܒܬܐ ܠܘܬ ܟܠܢܫ ܝܬܝܪܐܝܬ ܠܘܬ ܒܢܝ ܒܝܬܐ ܕܗܝܡܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 11, 'ܚܙܘ ܐܝܠܝܢ ܟܬܝܒܢ ܟܬܒܬ ܠܟܘܢ ܒܐܝܕܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 12, 'ܐܝܠܝܢ ܕܨܒܝܢ ܕܢܫܬܒܗܪܘܢ ܒܒܤܪܐ ܗܢܘܢ ܐܠܨܝܢ ܠܟܘܢ ܕܬܬܓܙܪܘܢ ܕܒܠܚܘܕ ܒܙܩܝܦܗ ܕܡܫܝܚܐ ܠܐ ܢܬܪܕܦܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 13, 'ܐܦܠܐ ܓܝܪ ܗܢܘܢ ܗܠܝܢ ܕܓܙܪܝܢ ܢܛܪܝܢ ܢܡܘܤܐ ܐܠܐ ܨܒܝܢ ܕܬܬܓܙܪܘܢ ܕܒܒܤܪܟܘܢ ܕܝܠܟܘܢ ܢܫܬܒܗܪܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 14, 'ܠܝ ܕܝܢ ܠܐ ܢܗܘܐ ܠܝ ܕܐܫܬܒܗܪ ܐܠܐ ܒܙܩܝܦܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܕܒܗ ܥܠܡܐ ܙܩܝܦ ܠܝ ܘܐܢܐ ܙܩܝܦ ܐܢܐ ܠܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 15, 'ܠܐ ܓܝܪ ܓܙܘܪܬܐ ܐܝܬܝܗ ܡܕܡ ܘܠܐ ܥܘܪܠܘܬܐ ܐܠܐ ܒܪܝܬܐ ܚܕܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 16, 'ܘܐܝܠܝܢ ܕܠܗܢܐ ܫܒܝܠܐ ܫܠܡܝܢ ܫܠܡܐ ܢܗܘܐ ܥܠܝܗܘܢ ܘܪܚܡܐ ܘܥܠ ܐܝܤܪܝܠ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 17, 'ܡܟܝܠ ܠܝ ܐܢܫ ܥܡܠܐ ܠܐ ܢܪܡܐ ܐܢܐ ܓܝܪ ܟܘܬܡܬܗ ܕܡܪܢ ܝܫܘܥ ܒܦܓܪܝ ܫܩܝܠ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 18, 'ܛܝܒܘܬܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܥܡ ܪܘܚܟܘܢ ܐܚܝ ܐܡܝܢ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 1, 'ܦܘܠܘܤ ܫܠܝܚܐ ܕܝܫܘܥ ܡܫܝܚܐ ܒܨܒܝܢܗ ܕܐܠܗܐ ܠܐܝܠܝܢ ܕܐܝܬܝܗܘܢ ܒܐܦܤܘܤ ܩܕܝܫܐ ܘܡܗܝܡܢܐ ܒܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 2, 'ܫܠܡܐ ܥܡܟܘܢ ܘܛܝܒܘܬܐ ܡܢ ܐܠܗܐ ܐܒܘܢ ܘܡܢ ܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 3, 'ܡܒܪܟ ܗܘ ܐܠܗܐ ܐܒܘܗܝ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܗܘ ܕܒܪܟܢ ܒܟܠ ܒܘܪܟܢ ܕܪܘܚ ܒܫܡܝܐ ܒܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 4, 'ܐܝܟܢܐ ܕܩܕܡ ܓܒܢ ܒܗ ܡܢ ܩܕܡ ܬܪܡܝܬܗ ܕܥܠܡܐ ܕܢܗܘܐ ܩܕܝܫܐ ܘܕܠܐ ܡܘܡ ܩܕܡܘܗܝ ܘܒܚܘܒܐ ܩܕܡ ܪܫܡܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 5, 'ܘܤܡܢ ܠܒܢܝܐ ܒܝܫܘܥ ܡܫܝܚܐ ܐܝܟ ܕܫܦܪ ܠܨܒܝܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 6, 'ܕܢܫܬܒܚ ܫܘܒܚܐ ܕܛܝܒܘܬܗ ܗܘ ܕܐܫܦܥ ܥܠܝܢ ܒܝܕ ܚܒܝܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 7, 'ܕܒܗ ܐܝܬ ܠܢ ܦܘܪܩܢܐ ܘܒܕܡܗ ܫܘܒܩܢܐ ܕܚܛܗܐ ܐܝܟ ܥܘܬܪܐ ܕܛܝܒܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 8, 'ܗܝ ܕܐܬܝܬܪܬ ܒܢ ܒܟܠ ܚܟܡܐ ܘܒܟܠ ܤܘܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 9, 'ܘܐܘܕܥܢ ܐܪܙܐ ܕܨܒܝܢܗ ܗܘ ܕܩܕܡ ܗܘܐ ܤܡ ܕܢܤܥܘܪ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 10, 'ܠܡܕܒܪܢܘܬܐ ܕܡܘܠܝܗܘܢ ܕܙܒܢܐ ܕܟܠܡܕܡ ܡܢ ܕܪܝܫ ܢܬܚܕܬ ܒܡܫܝܚܐ ܕܒܫܡܝܐ ܘܒܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 11, 'ܘܒܗ ܚܢܢ ܐܬܓܒܝܢ ܐܝܟ ܕܩܕܡ ܪܫܡܢ ܘܨܒܐ ܗܘ ܕܟܠ ܤܥܪ ܐܝܟ ܬܪܥܝܬܐ ܕܨܒܝܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 12, 'ܕܢܗܘܐ ܚܢܢ ܐܝܠܝܢ ܕܩܕܡܢ ܤܒܪܢ ܒܡܫܝܚܐ ܠܗܕܪܐ ܕܬܫܒܘܚܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 13, 'ܕܐܦ ܐܢܬܘܢ ܒܗ ܫܡܥܬܘܢ ܡܠܬܐ ܕܩܘܫܬܐ ܕܐܝܬܝܗ ܤܒܪܬܐ ܕܚܝܝܟܘܢ ܘܒܗ ܗܝܡܢܬܘܢ ܘܐܬܚܬܡܬܘܢ ܒܪܘܚܐ ܕܩܘܕܫܐ ܕܡܠܝܟܐ ܗܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 14, 'ܗܝ ܕܐܝܬܝܗ ܪܗܒܘܢܐ ܕܝܪܬܘܬܢ ܠܦܘܪܩܢܐ ܕܐܝܠܝܢ ܕܚܐܝܢ ܘܠܫܘܒܚܐ ܕܐܝܩܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 15, 'ܡܛܠ ܗܢܐ ܐܦ ܐܢܐ ܗܐ ܡܢ ܕܫܡܥܬ ܗܝܡܢܘܬܟܘܢ ܕܒܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܘܚܘܒܟܘܢ ܕܠܘܬ ܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 16, 'ܠܐ ܡܫܬܠܐ ܐܢܐ ܠܡܘܕܝܘ ܥܠ ܐܦܝܟܘܢ ܘܠܡܥܗܕܟܘܢ ܒܨܠܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 17, 'ܕܐܠܗܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܐܒܐ ܕܬܫܒܘܚܬܐ ܢܬܠ ܠܟܘܢ ܪܘܚܐ ܕܚܟܡܬܐ ܘܕܓܠܝܢܐ ܒܝܕܥܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 18, 'ܘܢܢܗܪܢ ܥܝܢܐ ܕܠܒܘܬܟܘܢ ܕܬܕܥܘܢ ܡܢܘ ܤܒܪܐ ܕܩܪܝܢܗ ܘܡܢܘ ܥܘܬܪܐ ܕܫܘܒܚܐ ܕܝܪܬܘܬܗ ܒܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 19, 'ܘܡܢܐ ܗܝ ܝܬܝܪܘܬܐ ܕܪܒܘܬܐ ܕܚܝܠܗ ܒܢ ܒܐܝܠܝܢ ܕܡܗܝܡܢܝܢܢ ܐܝܟ ܡܥܒܕܢܘܬܐ ܕܬܘܩܦܐ ܕܚܝܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 20, 'ܕܥܒܕ ܒܡܫܝܚܐ ܘܐܩܝܡܗ ܡܢ ܒܝܬ ܡܝܬܐ ܘܐܘܬܒܗ ܡܢ ܝܡܝܢܗ ܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 21, 'ܠܥܠ ܡܢ ܟܠܗܝܢ ܐܪܟܘܤ ܘܫܘܠܛܢܐ ܘܚܝܠܐ ܘܡܪܘܬܐ ܘܠܥܠ ܡܢ ܟܠ ܫܡ ܕܡܫܬܡܗ ܠܐ ܒܠܚܘܕ ܒܥܠܡܐ ܗܢܐ ܐܠܐ ܐܦ ܒܕܥܬܝܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 22, 'ܘܫܥܒܕ ܟܠܡܕܡ ܬܚܝܬ ܪܓܠܘܗܝ ܘܠܗ ܕܐܝܬܘܗܝ ܠܥܠ ܡܢ ܟܠ ܝܗܒܗ ܪܫܐ ܠܥܕܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 23, 'ܕܐܝܬܝܗ ܓܘܫܡܗ ܘܫܘܡܠܝܐ ܕܗܘ ܕܟܠ ܒܟܠ ܡܫܡܠܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 1, 'ܘܐܦ ܠܟܘܢ ܕܡܝܬܝܢ ܗܘܝܬܘܢ ܒܚܛܗܝܟܘܢ ܘܒܤܟܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 2, 'ܒܗܠܝܢ ܕܡܢ ܩܕܝܡ ܗܠܟܬܘܢ ܗܘܝܬܘܢ ܒܗܝܢ ܐܝܟ ܥܠܡܝܘܬܗ ܕܥܠܡܐ ܗܢܐ ܘܐܝܟ ܨܒܝܢܗ ܕܪܝܫ ܫܘܠܛܢܐ ܕܐܐܪ ܘܕܪܘܚܐ ܗܕܐ ܕܡܬܚܦܛܐ ܒܒܢܝܐ ܕܠܐ ܡܬܛܦܝܤܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 3, 'ܒܗܢܘܢ ܥܒܕܐ ܕܐܦ ܚܢܢ ܐܬܗܦܟܢ ܒܗܘܢ ܡܢ ܩܕܝܡ ܒܪܓܝܓܬܐ ܕܒܤܪܢ ܘܥܒܕܝܢ ܗܘܝܢ ܨܒܝܢܐ ܕܒܤܪܢ ܘܕܬܪܥܝܬܢ ܘܒܢܝܐ ܗܘܝܢ ܕܪܘܓܙܐ ܡܠܝܐܝܬ ܐܝܟ ܫܪܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 4, 'ܐܠܗܐ ܕܝܢ ܕܥܬܝܪ ܒܪܚܡܘܗܝ ܡܛܠ ܚܘܒܗ ܤܓܝܐܐ ܕܐܚܒܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 5, 'ܟܕ ܡܝܬܝܢ ܗܘܝܢ ܒܚܛܗܝܢ ܐܚܝܢ ܥܡ ܡܫܝܚܐ ܘܒܛܝܒܘܬܗ ܦܪܩܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 6, 'ܘܐܩܝܡܢ ܥܡܗ ܘܐܘܬܒܢ ܥܡܗ ܒܫܡܝܐ ܒܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 7, 'ܕܢܚܘܐ ܠܥܠܡܐ ܕܐܬܝܢ ܪܒܘܬܐ ܕܥܘܬܪܐ ܕܛܝܒܘܬܗ ܘܒܤܝܡܘܬܗ ܕܗܘܬ ܥܠܝܢ ܒܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 8, 'ܒܛܝܒܘܬܗ ܗܘ ܓܝܪ ܐܬܦܪܩܢ ܒܗܝܡܢܘܬܐ ܘܗܕܐ ܠܐ ܗܘܬ ܡܢܟܘܢ ܐܠܐ ܡܘܗܒܬܐ ܗܝ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 9, 'ܠܐ ܡܢ ܥܒܕܐ ܕܠܐ ܐܢܫ ܢܫܬܒܗܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 10, 'ܒܪܝܬܐ ܚܢܢ ܓܝܪ ܕܝܠܗ ܕܐܬܒܪܝܢ ܒܝܫܘܥ ܡܫܝܚܐ ܠܥܒܕܐ ܛܒܐ ܗܢܘܢ ܕܡܢ ܩܕܝܡ ܛܝܒ ܐܠܗܐ ܕܒܗܘܢ ܢܗܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 11, 'ܡܛܠ ܗܕܐ ܗܘܝܬܘܢ ܥܗܕܝܢ ܕܐܢܬܘܢ ܥܡܡܐ ܡܢ ܩܕܝܡ ܕܒܤܪ ܗܘܝܬܘܢ ܘܡܬܩܪܝܢ ܗܘܝܬܘܢ ܥܘܪܠܘܬܐ ܡܢ ܗܝ ܕܡܬܩܪܝܐ ܓܙܘܪܬܐ ܘܐܝܬܝܗ ܥܒܕ ܐܝܕܝܐ ܒܒܤܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 12, 'ܘܐܝܬܝܟܘܢ ܗܘܝܬܘܢ ܒܗܘ ܙܒܢܐ ܕܠܐ ܡܫܝܚܐ ܘܢܘܟܪܝܝܢ ܗܘܝܬܘܢ ܡܢ ܕܘܒܪܐ ܕܐܝܤܪܝܠ ܘܐܟܤܢܝܐ ܗܘ ܠܕܝܬܩܐ ܕܡܘܠܟܢܐ ܘܕܠܐ ܤܒܪ ܗܘܝܬܘܢ ܘܕܠܐ ܐܠܗ ܒܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 13, 'ܗܫܐ ܕܝܢ ܒܝܫܘܥ ܡܫܝܚܐ ܐܢܬܘܢ ܕܡܢ ܩܕܝܡ ܪܚܝܩܝܢ ܗܘܝܬܘܢ ܩܪܝܒܐ ܗܘܝܬܘܢ ܒܕܡܗ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 14, 'ܗܘܝܘ ܓܝܪ ܫܝܢܢ ܗܘ ܕܥܒܕ ܬܪܬܝܗܝܢ ܚܕܐ ܘܫܪܐ ܤܝܓܐ ܕܩܐܡ ܗܘܐ ܒܡܨܥܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 15, 'ܘܒܥܠܕܒܒܘܬܐ ܒܒܤܪܗ ܘܢܡܘܤܐ ܕܦܘܩܕܐ ܒܦܘܩܕܢܘܗܝ ܒܛܠ ܕܠܬܪܝܗܘܢ ܢܒܪܐ ܒܩܢܘܡܗ ܠܚܕ ܒܪܢܫܐ ܚܕܬܐ ܘܥܒܕ ܫܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 16, 'ܘܪܥܝ ܠܬܪܝܗܘܢ ܒܚܕ ܦܓܪ ܥܡ ܐܠܗܐ ܘܒܙܩܝܦܗ ܩܛܠ ܒܥܠܕܒܒܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 17, 'ܘܐܬܐ ܤܒܪ ܫܠܡܐ ܠܟܘܢ ܠܪܚܝܩܐ ܘܠܩܪܝܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 18, 'ܡܛܠ ܕܒܗ ܗܘ ܗܘܐ ܠܢ ܩܘܪܒܐ ܠܬܪܝܢ ܒܚܕܐ ܪܘܚ ܠܘܬ ܐܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 19, 'ܡܟܝܠ ܠܐ ܗܘܝܬܘܢ ܐܟܤܢܝܐ ܘܠܐ ܬܘܬܒܐ ܐܠܐ ܒܢܝ ܡܕܝܢܬܐ ܕܩܕܝܫܐ ܘܒܢܝ ܒܝܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 20, 'ܘܐܬܒܢܝܬܘܢ ܥܠ ܫܬܐܤܬܐ ܕܫܠܝܚܐ ܘܕܢܒܝܐ ܘܗܘ ܗܘܐ ܪܝܫ ܩܪܢܐ ܕܒܢܝܢܐ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 21, 'ܘܒܗ ܡܬܪܟܒ ܟܠܗ ܒܢܝܢܐ ܘܪܒܐ ܠܗܝܟܠܐ ܩܕܝܫܐ ܒܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 22, 'ܟܕ ܐܦ ܐܢܬܘܢ ܒܗ ܡܬܒܢܝܢ ܐܢܬܘܢ ܠܡܥܡܪܐ ܕܐܠܗܐ ܒܪܘܚ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 1, 'ܡܛܠ ܗܢܐ ܐܢܐ ܦܘܠܘܤ ܐܤܝܪܐ ܐܢܐ ܕܝܫܘܥ ܡܫܝܚܐ ܥܠ ܐܦܝܟܘܢ ܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 2, 'ܐܢ ܫܡܥܬܘܢ ܡܕܒܪܢܘܬܐ ܕܛܝܒܘܬܗ ܕܐܠܗܐ ܕܐܬܝܗܒܬ ܠܝ ܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 3, 'ܕܒܓܠܝܢܐ ܐܬܝܕܥ ܠܝ ܐܪܙܐ ܐܝܟܢܐ ܕܟܬܒܬ ܠܟܘܢ ܒܙܥܘܪܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 4, 'ܐܝܟ ܡܐ ܕܡܫܟܚܝܬܘܢ ܟܕ ܩܪܝܢ ܐܢܬܘܢ ܠܡܤܬܟܠܘ ܝܕܥܬܝ ܕܒܐܪܙܗ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 5, 'ܗܘ ܕܒܕܪܐ ܐܚܪܢܐ ܠܐ ܐܬܝܕܥ ܠܒܢܝܢܫܐ ܐܝܟ ܕܗܫܐ ܐܬܓܠܝ ܠܫܠܝܚܘܗܝ ܩܕܝܫܐ ܘܠܢܒܝܘܗܝ ܒܪܘܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 6, 'ܕܢܗܘܘܢ ܥܡܡܐ ܒܢܝ ܝܪܬܘܬܗ ܘܫܘܬܦܐ ܕܦܓܪܗ ܘܕܡܘܠܟܢܐ ܕܐܬܝܗܒ ܒܗ ܒܝܕ ܐܘܢܓܠܝܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 7, 'ܗܘ ܕܐܢܐ ܗܘܝܬ ܡܫܡܫܢܗ ܐܝܟ ܡܘܗܒܬܐ ܕܛܝܒܘܬܗ ܕܐܠܗܐ ܕܐܬܝܗܒܬ ܠܝ ܡܢ ܡܥܒܕܢܘܬܐ ܕܚܝܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 8, 'ܠܝ ܕܙܥܘܪܐ ܐܢܐ ܕܟܠܗܘܢ ܩܕܝܫܐ ܐܬܝܗܒܬ ܛܝܒܘܬܐ ܗܕܐ ܕܐܤܒܪ ܒܥܡܡܐ ܥܘܬܪܗ ܕܡܫܝܚܐ ܗܘ ܕܠܐ ܡܬܥܩܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 9, 'ܘܐܢܗܪ ܠܟܠܢܫ ܐܝܕܐ ܗܝ ܡܕܒܪܢܘܬܐ ܕܐܪܙܐ ܗܘ ܕܟܤܐ ܗܘܐ ܡܢ ܥܠܡܐ ܒܐܠܗܐ ܕܟܠ ܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 10, 'ܕܒܝܕ ܥܕܬܐ ܬܬܝܕܥ ܚܟܡܬܗ ܕܐܠܗܐ ܡܠܝܬ ܦܘܪܫܢܐ ܠܐܪܟܘܤ ܘܠܫܘܠܛܢܐ ܕܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 11, 'ܗܝ ܕܥܬܕ ܗܘܐ ܡܢ ܥܠܡܐ ܘܥܒܕܗ ܒܝܫܘܥ ܡܫܝܚܐ ܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 12, 'ܗܘ ܕܒܗ ܐܝܬ ܠܢ ܦܪܗܤܝܐ ܘܩܪܝܒܘܬܐ ܒܬܘܟܠܢܐ ܕܗܝܡܢܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 13, 'ܡܛܠ ܗܢܐ ܫܐܠܢܐ ܕܠܐ ܬܡܐܢ ܠܝ ܒܐܘܠܨܢܝ ܕܥܠ ܐܦܝܟܘܢ ܕܗܕܐ ܗܝ ܬܫܒܘܚܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 14, 'ܘܟܐܦܢܐ ܒܘܪܟܝ ܠܘܬ ܐܒܘܗܝ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 15, 'ܗܘ ܕܡܢܗ ܡܫܬܡܗܐ ܟܠ ܐܒܗܘܬܐ ܕܒܫܡܝܐ ܘܒܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 16, 'ܕܢܬܠ ܠܟܘܢ ܐܝܟ ܥܘܬܪܐ ܕܬܫܒܘܚܬܗ ܕܒܚܝܠܐ ܬܫܬܪܪܘܢ ܒܪܘܚܗ ܕܒܒܪܢܫܟܘܢ ܕܠܓܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 17, 'ܢܥܡܪ ܡܫܝܚܐ ܒܗܝܡܢܘܬܐ ܘܒܠܒܘܬܟܘܢ ܒܚܘܒܐ ܟܕ ܢܗܘܐ ܫܪܝܪ ܥܩܪܟܘܢ ܘܫܬܐܤܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 18, 'ܕܬܫܟܚܘܢ ܠܡܕܪܟܘ ܥܡ ܟܠܗܘܢ ܩܕܝܫܐ ܡܢܘ ܪܘܡܐ ܘܥܘܡܩܐ ܘܐܘܪܟܐ ܘܦܬܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 19, 'ܘܬܕܥܘܢ ܪܒܘܬܐ ܕܝܕܥܬܐ ܕܚܘܒܗ ܕܡܫܝܚܐ ܘܬܬܡܠܘܢ ܒܟܠܗ ܡܘܠܝܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 20, 'ܠܗܘ ܕܝܢ ܕܡܨܐ ܒܚܝܠܐ ܝܬܝܪ ܡܢ ܟܠ ܠܡܥܒܕ ܠܢ ܘܝܬܝܪ ܡܢ ܡܐ ܕܫܐܠܝܢܢ ܘܪܢܝܢܢ ܐܝܟ ܚܝܠܗ ܕܡܤܬܥܪ ܒܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 21, 'ܠܗ ܬܫܒܘܚܬܐ ܒܥܕܬܗ ܒܝܫܘܥ ܡܫܝܚܐ ܒܟܠܗܘܢ ܕܪܐ ܕܥܠܡܝ ܥܠܡܝܢ ܐܡܝܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 1, 'ܒܥܐ ܐܢܐ ܗܟܝܠ ܡܢܟܘܢ ܐܢܐ ܐܤܝܪܐ ܒܡܪܢ ܕܬܗܠܟܘܢ ܐܝܟܢܐ ܕܝܐܐ ܠܩܪܝܢܐ ܕܐܬܩܪܝܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 2, 'ܒܟܠܗ ܡܟܝܟܘܬ ܪܥܝܢܐ ܘܢܝܚܘܬܐ ܘܢܓܝܪܘܬ ܪܘܚܐ ܘܗܘܝܬܘܢ ܡܤܝܒܪܝܢ ܚܕ ܠܚܕ ܒܚܘܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 3, 'ܘܗܘܝܬܘܢ ܚܦܝܛܝܢ ܠܡܛܪ ܐܘܝܘܬܐ ܕܪܘܚܐ ܒܚܙܩܐ ܕܫܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 4, 'ܕܬܗܘܘܢ ܒܚܕ ܦܓܪܐ ܘܒܚܕܐ ܪܘܚܐ ܐܝܟ ܡܐ ܕܐܬܩܪܝܬܘܢ ܒܚܕ ܤܒܪܐ ܕܩܪܝܢܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 5, 'ܚܕ ܗܘ ܓܝܪ ܡܪܝܐ ܘܚܕܐ ܗܝܡܢܘܬܐ ܘܚܕܐ ܡܥܡܘܕܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 6, 'ܘܚܕ ܐܠܗܐ ܐܒܐ ܕܟܠ ܘܥܠ ܟܠ ܘܒܝܕ ܟܠ ܘܒܟܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 7, 'ܠܚܕ ܚܕ ܡܢܢ ܕܝܢ ܐܬܝܗܒܬ ܛܝܒܘܬܐ ܐܝܟ ܡܫܘܚܬܐ ܕܡܘܗܒܬܗ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 8, 'ܡܛܠ ܗܢܐ ܐܡܝܪ ܕܤܠܩ ܠܡܪܘܡܐ ܘܫܒܐ ܫܒܝܬܐ ܘܝܗܒ ܡܘܗܒܬܐ ܠܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 9, 'ܕܤܠܩ ܕܝܢ ܡܢܐ ܗܝ ܐܠܐ ܐܢ ܕܐܦ ܢܚܬ ܠܘܩܕܡ ܠܬܚܬܝܬܗ ܕܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 10, 'ܗܘ ܕܢܚܬ ܗܘܝܘ ܗܘ ܕܐܦ ܤܠܩ ܠܥܠ ܡܢ ܟܠܗܘܢ ܫܡܝܐ ܕܢܫܠܡ ܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 11, 'ܘܗܘ ܝܗܒ ܐܝܬ ܕܫܠܝܚܐ ܘܐܝܬ ܕܢܒܝܐ ܘܐܝܬ ܕܡܤܒܪܢܐ ܘܐܝܬ ܕܪܥܘܬܐ ܘܐܝܬ ܕܡܠܦܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 12, 'ܠܓܡܪܐ ܕܩܕܝܫܐ ܠܥܒܕܐ ܕܬܫܡܫܬܐ ܠܒܢܝܢܐ ܕܦܓܪܐ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 13, 'ܥܕܡܐ ܕܟܠܢ ܢܗܘܐ ܚܕ ܡܕܡ ܒܗܝܡܢܘܬܐ ܘܒܝܕܥܬܐ ܕܒܪܗ ܕܐܠܗܐ ܘܚܕ ܓܒܪܐ ܓܡܝܪܐ ܒܡܫܘܚܬܐ ܕܩܘܡܬܐ ܕܫܘܡܠܝܗ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 14, 'ܘܠܐ ܢܗܘܐ ܝܠܘܕܐ ܕܡܙܕܥܙܥܝܢ ܘܡܫܬܢܝܢ ܠܟܠ ܪܘܚ ܕܝܘܠܦܢܐ ܢܟܝܠܐ ܕܒܢܝܢܫܐ ܗܢܘܢ ܕܒܚܪܥܘܬܗܘܢ ܡܨܛܢܥܝܢ ܕܢܛܥܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 15, 'ܐܠܐ ܗܘܝܢ ܫܪܝܪܝܢ ܒܚܘܒܢ ܕܟܘܠ ܡܕܡ ܕܝܠܢ ܢܪܒܐ ܒܡܫܝܚܐ ܕܗܘܝܘ ܪܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 16, 'ܘܡܢܗ ܟܠܗ ܦܓܪܐ ܡܬܪܟܒ ܘܡܬܩܛܪ ܒܟܠ ܫܪܝܢ ܐܝܟ ܡܘܗܒܬܐ ܕܡܬܝܗܒܐ ܒܡܫܘܚܬܐ ܠܟܠ ܗܕܡ ܠܬܪܒܝܬܐ ܕܝܠܗ ܕܦܓܪܐ ܕܒܚܘܒܐ ܢܫܬܠܡ ܒܢܝܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 17, 'ܗܕܐ ܕܝܢ ܐܡܪ ܐܢܐ ܘܡܤܗܕ ܐܢܐ ܒܡܪܝܐ ܕܡܢ ܗܫܐ ܠܐ ܬܗܘܘܢ ܡܗܠܟܝܢ ܐܝܟ ܫܪܟܐ ܕܥܡܡܐ ܕܡܗܠܟܝܢ ܒܤܪܝܩܘܬ ܪܥܝܢܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 18, 'ܘܚܫܘܟܝܢ ܒܡܕܥܝܗܘܢ ܘܢܘܟܪܝܝܢ ܐܢܘܢ ܡܢ ܚܝܘܗܝ ܕܐܠܗܐ ܡܛܠ ܕܠܝܬ ܒܗܘܢ ܝܕܥܬܐ ܘܡܛܠ ܥܘܝܪܘܬ ܠܒܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 19, 'ܗܢܘܢ ܕܦܤܩܘ ܤܒܪܗܘܢ ܘܐܫܠܡܘ ܢܦܫܗܘܢ ܠܦܚܙܘܬܐ ܘܠܦܘܠܚܢܐ ܕܟܠܗ ܛܢܦܘܬܐ ܒܝܥܢܘܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 20, 'ܐܢܬܘܢ ܕܝܢ ܠܐ ܗܘܐ ܗܟܢܐ ܝܠܦܬܘܢܝܗܝ ܠܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 21, 'ܐܢ ܫܪܝܪܐܝܬ ܫܡܥܬܘܢܝܗܝ ܘܒܗ ܝܠܦܬܘܢ ܐܝܟ ܡܐ ܕܐܝܬܘܗܝ ܩܘܫܬܐ ܒܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 22, 'ܐܠܐ ܕܬܢܝܚܘܢ ܡܢܟܘܢ ܗܘܦܟܝܟܘܢ ܩܕܡܝܐ ܠܒܪܢܫܐ ܗܘ ܥܬܝܩܐ ܕܡܬܚܒܠ ܒܪܓܝܓܬܐ ܕܛܘܥܝܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 23, 'ܘܬܬܚܕܬܘܢ ܒܪܘܚܐ ܕܡܕܥܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 24, 'ܘܬܠܒܫܘܢ ܠܒܪܢܫܐ ܚܕܬܐ ܗܘ ܕܒܐܠܗܐ ܐܬܒܪܝ ܒܙܕܝܩܘܬܐ ܘܒܚܤܝܘܬܐ ܕܩܘܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 25, 'ܡܛܠ ܗܢܐ ܐܢܝܚܘ ܡܢܟܘܢ ܟܕܒܘܬܐ ܘܡܠܠܘ ܩܘܫܬܐ ܐܢܫ ܥܡ ܩܪܝܒܗ ܗܕܡܐ ܚܢܢ ܓܝܪ ܚܕ ܕܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 26, 'ܪܓܙܘ ܘܠܐ ܬܚܛܘܢ ܘܫܡܫܐ ܥܠ ܪܘܓܙܟܘܢ ܠܐ ܢܥܪܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 27, 'ܘܠܐ ܬܬܠܘܢ ܐܬܪܐ ܠܐܟܠ ܩܪܨܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 28, 'ܘܐܝܢܐ ܕܓܢܒ ܗܘܐ ܡܟܝܠ ܠܐ ܢܓܢܘܒ ܐܠܐ ܢܠܐܐ ܒܐܝܕܘܗܝ ܘܢܦܠܘܚ ܛܒܬܐ ܕܢܗܘܐ ܠܗ ܠܡܬܠ ܠܡܢ ܕܤܢܝܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 29, 'ܟܠ ܡܠܐ ܤܢܝܐ ܡܢ ܦܘܡܟܘܢ ܠܐ ܬܦܘܩ ܐܠܐ ܐܝܕܐ ܕܫܦܝܪܐ ܘܚܫܚܐ ܠܒܢܝܢܐ ܕܬܬܠ ܛܝܒܘܬܐ ܠܐܝܠܝܢ ܕܫܡܥܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 30, 'ܘܠܐ ܬܗܘܘܢ ܡܥܝܩܝܢ ܠܪܘܚܐ ܩܕܝܫܬܐ ܕܐܠܗܐ ܕܐܬܚܬܡܬܘܢ ܒܗ ܠܝܘܡܐ ܕܦܘܪܩܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 31, 'ܟܠܗ ܡܪܝܪܘܬܐ ܘܚܡܬܐ ܘܪܘܓܙܐ ܘܪܘܒܐ ܘܓܘܕܦܐ ܢܫܬܩܠܢ ܡܢܟܘܢ ܥܡ ܟܠܗ ܒܝܫܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 32, 'ܘܗܘܝܬܘܢ ܒܤܝܡܝܢ ܚܕ ܠܘܬ ܚܕ ܘܡܪܚܡܢܝܢ ܘܗܘܝܬܘܢ ܫܒܩܝܢ ܚܕ ܠܚܕ ܐܝܟܢܐ ܕܐܠܗܐ ܒܡܫܝܚܐ ܫܒܩ ܠܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 1, 'ܗܘܝܬܘܢ ܗܟܝܠ ܡܬܕܡܝܢ ܒܐܠܗܐ ܐܝܟ ܒܢܝܐ ܚܒܝܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 2, 'ܘܗܠܟܘ ܒܚܘܒܐ ܐܝܟ ܕܐܦ ܡܫܝܚܐ ܐܚܒܢ ܘܐܫܠܡ ܢܦܫܗ ܥܠ ܐܦܝܢ ܩܘܪܒܢܐ ܘܕܒܚܐ ܠܐܠܗܐ ܠܪܝܚܐ ܒܤܝܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 3, 'ܙܢܝܘܬܐ ܕܝܢ ܘܟܠܗ ܛܢܦܘܬܐ ܘܥܠܘܒܘܬܐ ܐܦܠܐ ܡܫܬܡܗܘ ܬܫܬܡܗ ܒܝܢܬܟܘܢ ܐܝܟ ܕܝܐܐ ܠܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 4, 'ܘܠܐ ܨܘܚܝܬܐ ܘܠܐ ܡܠܐ ܕܫܛܝܘܬܐ ܐܘ ܕܒܙܚܐ ܐܘ ܕܫܥܝܐ ܗܠܝܢ ܕܠܐ ܡܬܒܥܝܢ ܐܠܐ ܚܠܦ ܗܠܝܢ ܬܘܕܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 5, 'ܗܕܐ ܕܝܢ ܗܘܝܬܘܢ ܝܕܥܝܢ ܕܟܠܢܫ ܕܐܝܬܘܗܝ ܙܢܝܐ ܐܘ ܛܢܦܐ ܐܘ ܥܠܘܒܐ ܕܐܝܬܘܗܝ ܦܠܚ ܦܬܟܪܐ ܠܝܬ ܠܗ ܝܪܬܘܬܐ ܒܡܠܟܘܬܗ ܕܡܫܝܚܐ ܘܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 6, 'ܕܠܡܐ ܐܢܫ ܢܛܥܝܟܘܢ ܒܡܠܐ ܤܪܝܩܬܐ ܡܛܠ ܗܠܝܢ ܗܘ ܓܝܪ ܐܬܐ ܪܘܓܙܗ ܕܐܠܗܐ ܥܠ ܒܢܝܐ ܕܠܐ ܡܬܛܦܝܤܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 7, 'ܠܐ ܗܟܝܠ ܬܗܘܘܢ ܠܗܘܢ ܫܘܬܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 8, 'ܐܝܬܝܟܘܢ ܗܘܝܬܘܢ ܓܝܪ ܡܢ ܠܘܩܕܡ ܚܫܘܟܐ ܗܫܐ ܕܝܢ ܢܘܗܪܐ ܐܢܬܘܢ ܒܡܪܢ ܐܝܟ ܒܢܝ ܢܘܗܪܐ ܗܟܝܠ ܗܟܢܐ ܗܠܟܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 9, 'ܦܐܪܘܗܝ ܓܝܪ ܕܢܘܗܪܐ ܒܟܠܗ ܐܢܘܢ ܛܒܘܬܐ ܘܙܕܝܩܘܬܐ ܘܩܘܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 10, 'ܘܗܘܝܬܘܢ ܦܪܫܝܢ ܡܢܐ ܫܦܝܪ ܩܕܡ ܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 11, 'ܘܠܐ ܗܘܝܬܘܢ ܡܫܬܘܬܦܝܢ ܠܘܬ ܥܒܕܐ ܕܚܫܘܟܐ ܕܦܐܪܐ ܠܝܬ ܒܗܘܢ ܐܠܐ ܗܘܝܬܘܢ ܡܟܘܢܝܢ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 12, 'ܡܕܡ ܓܝܪ ܕܒܛܘܫܝܝ ܥܒܕܝܢ ܢܕܝܕ ܗܘ ܐܦ ܠܡܡܠܠܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 13, 'ܟܠ ܡܕܡ ܓܝܪ ܡܬܟܘܢ ܡܢ ܢܘܗܪܐ ܘܡܬܓܠܐ ܘܟܠܡܕܡ ܕܓܠܐ ܢܘܗܪܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 14, 'ܡܛܠ ܗܢܐ ܐܡܝܪ ܕܐܬܬܥܝܪ ܕܡܟܐ ܘܩܘܡ ܡܢ ܒܝܬ ܡܝܬܐ ܘܢܢܗܪ ܠܟ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 15, 'ܚܙܘ ܗܟܝܠ ܐܝܟܢܐ ܬܗܠܟܘܢ ܙܗܝܐܝܬ ܠܐ ܐܝܟ ܤܟܠܐ ܐܠܐ ܐܝܟ ܚܟܝܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 16, 'ܕܙܒܢܝܢ ܩܐܪܤܗܘܢ ܡܛܠ ܕܝܘܡܬܐ ܒܝܫܐ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 17, 'ܡܛܠ ܗܢܐ ܠܐ ܬܗܘܘܢ ܚܤܝܪܝ ܪܥܝܢܐ ܐܠܐ ܐܤܬܟܠܘ ܡܢܘ ܨܒܝܢܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 18, 'ܘܠܐ ܗܘܝܬܘܢ ܪܘܝܢ ܒܚܡܪܐ ܕܒܗ ܐܝܬ ܐܤܘܛܘܬܐ ܐܠܐ ܐܬܡܠܘ ܒܪܘܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 19, 'ܘܡܠܠܘ ܥܡ ܢܦܫܟܘܢ ܒܡܙܡܘܪܐ ܘܒܬܫܒܚܬܐ ܘܒܙܡܝܪܬܐ ܕܪܘܚܐ ܗܘܝܬܘܢ ܙܡܪܝܢ ܒܠܒܘܬܟܘܢ ܠܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 20, 'ܘܗܘܝܬܘܢ ܡܘܕܝܢ ܒܟܠܙܒܢ ܥܠ ܐܦܝ ܟܠܢܫ ܒܫܡܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܠܐܠܗܐ ܐܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 21, 'ܘܗܘܝܬܘܢ ܡܫܬܥܒܕܝܢ ܚܕ ܠܚܕ ܒܚܘܒܗ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 22, 'ܢܫܐ ܗܘܝܬܝܢ ܡܫܬܥܒܕܢ ܠܒܥܠܝܟܝܢ ܐܝܟ ܕܠܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 23, 'ܡܛܠ ܕܓܒܪܐ ܪܫܗ ܗܘ ܕܐܢܬܬܐ ܐܝܟܢܐ ܕܐܦ ܡܫܝܚܐ ܪܫܐ ܗܘ ܕܥܕܬܐ ܘܗܘܝܘ ܡܚܝܢܐ ܕܦܓܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 24, 'ܐܠܐ ܐܝܟܢܐ ܕܥܕܬܐ ܡܫܬܥܒܕܐ ܠܡܫܝܚܐ ܗܟܢܐ ܐܦ ܢܫܐ ܠܒܥܠܝܗܝܢ ܒܟܠܡܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 25, 'ܓܒܪܐ ܐܚܒܘ ܢܫܝܟܘܢ ܐܝܟ ܕܐܦ ܡܫܝܚܐ ܐܚܒ ܠܥܕܬܗ ܘܢܦܫܗ ܐܫܠܡ ܥܠ ܐܦܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 26, 'ܕܢܩܕܫܝܗ ܘܢܕܟܝܗ ܒܤܚܝܐ ܕܡܝܐ ܘܒܡܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 27, 'ܘܢܩܝܡܝܗ ܥܕܬܐ ܠܢܦܫܗ ܟܕ ܡܫܒܚܐ ܘܠܝܬ ܒܗ ܛܘܠܫܐ ܘܠܐ ܩܡܛܐ ܘܠܐ ܡܕܡ ܕܠܗܠܝܢ ܕܡܐ ܐܠܐ ܬܗܘܐ ܩܕܝܫܐ ܕܠܐ ܡܘܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 28, 'ܗܟܢܐ ܘܠܐ ܠܓܒܪܐ ܕܢܚܒܘܢ ܢܫܝܗܘܢ ܐܝܟ ܕܠܦܓܪܝܗܘܢ ܡܢ ܕܠܐܢܬܬܗ ܓܝܪ ܡܚܒ ܢܦܫܗ ܗܘ ܡܚܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 29, 'ܠܐ ܓܝܪ ܐܢܫ ܡܡܬܘܡ ܤܢܐ ܦܓܪܗ ܐܠܐ ܡܬܪܤܐ ܠܗ ܘܝܨܦ ܕܝܠܗ ܐܝܟܢܐ ܕܐܦ ܡܫܝܚܐ ܕܥܕܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 30, 'ܡܛܠ ܕܗܕܡܐ ܚܢܢ ܕܦܓܪܗ ܘܡܢ ܒܤܪܗ ܚܢܢ ܘܡܢ ܓܪܡܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 31, 'ܡܛܠ ܗܢܐ ܢܫܒܘܩ ܓܒܪܐ ܠܐܒܘܗܝ ܘܠܐܡܗ ܘܢܩܦ ܠܐܢܬܬܗ ܘܢܗܘܘܢ ܬܪܝܗܘܢ ܚܕ ܒܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 32, 'ܗܢܐ ܐܪܙܐ ܪܒ ܗܘ ܐܢܐ ܕܝܢ ܐܡܪ ܐܢܐ ܥܠ ܡܫܝܚܐ ܘܥܠ ܥܕܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 33, 'ܒܪܡ ܐܦ ܐܢܬܘܢ ܟܠ ܚܕ ܚܕ ܡܢܟܘܢ ܗܟܢܐ ܢܪܚܡ ܐܢܬܬܗ ܐܝܟ ܕܠܢܦܫܗ ܐܢܬܬܐ ܕܝܢ ܬܗܘܐ ܕܚܠܐ ܡܢ ܒܥܠܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 1, 'ܒܢܝܐ ܐܫܬܡܥܘ ܠܐܒܗܝܟܘܢ ܒܡܪܢ ܗܕܐ ܓܝܪ ܟܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 2, 'ܘܗܢܘ ܦܘܩܕܢܐ ܩܕܡܝܐ ܕܡܠܝܟ ܝܩܪ ܠܐܒܘܟ ܘܠܐܡܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 3, 'ܕܢܗܘܐ ܠܟ ܕܫܦܝܪ ܘܢܐܪܟܘܢ ܚܝܝܟ ܥܠ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 4, 'ܐܒܗܐ ܠܐ ܬܪܓܙܘܢ ܒܢܝܟܘܢ ܐܠܐ ܪܒܘ ܐܢܘܢ ܒܡܪܕܘܬܐ ܘܒܝܘܠܦܢܐ ܕܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 5, 'ܥܒܕܐ ܐܫܬܡܥܘ ܠܡܪܝܟܘܢ ܕܒܒܤܪ ܒܕܚܠܬܐ ܘܒܪܬܝܬܐ ܘܒܦܫܝܛܘܬ ܠܒܐ ܐܝܟ ܕܠܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 6, 'ܠܐ ܒܡܚܙܐ ܥܝܢܐ ܐܝܟ ܗܘ ܕܠܒܢܝܢܫܐ ܫܦܪܝܢ ܐܢܬܘܢ ܐܠܐ ܐܝܟ ܥܒܕܐ ܕܡܫܝܚܐ ܕܥܒܕܝܢ ܨܒܝܢܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 7, 'ܘܫܡܫܘ ܐܢܘܢ ܡܢ ܟܠܗ ܢܦܫܟܘܢ ܒܚܘܒܐ ܐܝܟ ܕܠܡܪܢ ܘܠܐ ܐܝܟ ܕܠܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 8, 'ܟܕ ܝܕܥܝܬܘܢ ܕܗܘ ܡܕܡ ܕܥܒܕ ܐܢܫ ܕܫܦܝܪ ܗܘ ܡܬܦܪܥ ܡܢ ܡܪܢ ܐܢ ܥܒܕܐ ܗܘ ܘܐܢ ܒܪ ܚܐܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 9, 'ܐܦ ܐܢܬܘܢ ܡܪܝܐ ܗܟܢܐ ܗܘܝܬܘܢ ܥܒܕܝܢ ܠܥܒܕܝܟܘܢ ܗܘܝܬܘܢ ܫܒܩܝܢ ܠܗܘܢ ܤܟܠܘܬܐ ܡܛܠ ܕܝܕܥܝܢ ܐܢܬܘܢ ܕܐܦ ܡܪܟܘܢ ܕܝܠܟܘܢ ܐܝܬܘܗܝ ܒܫܡܝܐ ܘܡܤܒ ܒܐܦܐ ܠܝܬ ܠܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 10, 'ܡܟܝܠ ܐܚܝ ܐܬܚܝܠܘ ܒܡܪܢ ܘܒܬܘܩܦܐ ܕܚܝܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 11, 'ܘܠܒܫܘ ܟܠܗ ܙܝܢܐ ܕܐܠܗܐ ܐܝܟܢܐ ܕܬܫܟܚܘܢ ܠܡܩܡ ܠܘܩܒܠ ܨܢܥܬܗ ܕܐܟܠ ܩܪܨܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 12, 'ܡܛܠ ܕܬܟܬܘܫܟܘܢ ܠܐ ܗܘܐ ܥܡ ܒܤܪܐ ܘܕܡܐ ܐܠܐ ܥܡ ܐܪܟܘܤ ܘܥܡ ܫܠܝܛܢܐ ܘܥܡ ܐܚܝܕܝ ܥܠܡܐ ܕܗܢܐ ܚܫܘܟܐ ܘܥܡ ܪܘܚܐ ܒܝܫܬܐ ܕܬܚܝܬ ܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 13, 'ܡܛܠ ܗܢܐ ܠܒܫܘ ܟܠܗ ܙܝܢܐ ܕܐܠܗܐ ܕܬܫܟܚܘܢ ܬܐܪܥܘܢ ܒܝܫܐ ܘܟܕ ܡܥܬܕܝܢ ܐܢܬܘܢ ܒܟܠܡܕܡ ܬܩܘܡܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 14, 'ܩܘܡܘ ܗܟܝܠ ܘܚܙܘܩܘ ܚܨܝܟܘܢ ܒܩܘܫܬܐ ܘܠܒܫܘ ܫܪܝܢܐ ܕܙܕܝܩܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 15, 'ܘܤܐܢܘ ܒܪܓܠܝܟܘܢ ܛܘܝܒܗ ܕܐܘܢܓܠܝܘܢ ܕܫܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 16, 'ܘܥܡ ܗܠܝܢ ܤܒܘ ܠܟܘܢ ܤܟܪܐ ܕܗܝܡܢܘܬܐ ܕܒܗ ܬܬܡܨܘܢ ܚܝܠܐ ܠܡܕܥܟܘ ܟܠܗܘܢ ܓܐܪܘܗܝ ܝܩܕܐ ܕܒܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 17, 'ܘܤܝܡܘ ܤܢܘܪܬܐ ܕܦܘܪܩܢܐ ܘܐܚܘܕܘ ܤܝܦܐ ܕܪܘܚܐ ܕܐܝܬܘܗܝ ܡܠܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 18, 'ܘܒܟܠ ܨܠܘܢ ܘܒܟܠ ܒܥܘܢ ܨܠܘ ܒܟܠܙܒܢ ܒܪܘܚ ܘܒܗ ܒܨܠܘܬܐ ܗܘܝܬܘܢ ܫܗܪܝܢ ܒܟܠܥܕܢ ܟܕ ܡܨܠܝܬܘܢ ܐܡܝܢܐܝܬ ܘܡܬܟܫܦܝܢ ܥܠ ܐܦܝ ܟܠܗܘܢ ܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 19, 'ܐܦ ܥܠܝ ܕܬܬܝܗܒ ܠܝ ܡܠܬܐ ܒܡܦܬܚ ܦܘܡܝ ܕܥܝܢ ܒܓܠܐ ܐܟܪܙ ܐܪܙܐ ܕܤܒܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 20, 'ܗܘ ܕܐܢܐ ܐܝܙܓܕܗ ܒܫܫܠܬܐ ܕܒܦܪܗܤܝܐ ܐܡܠܠܝܘܗܝ ܐܝܟ ܕܘܠܐ ܠܝ ܠܡܡܠܠܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 21, 'ܕܬܕܥܘܢ ܕܝܢ ܐܦ ܐܢܬܘܢ ܡܕܡ ܕܠܘܬܝ ܘܡܕܡ ܕܤܥܪ ܐܢܐ ܗܐ ܡܘܕܥ ܠܟܘܢ ܛܘܟܝܩܘܤ ܐܚܐ ܚܒܝܒܐ ܘܡܫܡܫܢܐ ܡܗܝܡܢܐ ܒܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 22, 'ܕܠܗ ܫܕܪܬ ܠܘܬܟܘܢ ܥܠܝܗ ܥܠ ܗܕܐ ܕܬܕܥܘܢ ܡܐ ܕܠܘܬܝ ܘܢܒܝܐ ܠܒܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 23, 'ܫܠܡܐ ܥܡ ܐܚܝܢ ܘܚܘܒܐ ܥܡ ܗܝܡܢܘܬܐ ܡܢ ܐܠܗܐ ܐܒܐ ܘܡܢ ܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 24, 'ܛܝܒܘܬܐ ܥܡ ܟܠܗܘܢ ܐܝܠܝܢ ܕܡܚܒܝܢ ܠܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܕܠܐ ܚܒܠ ܐܡܝܢ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 1, 'ܦܘܠܘܤ ܘܛܝܡܬܐܘܤ ܥܒܕܐ ܕܝܫܘܥ ܡܫܝܚܐ ܠܟܠܗܘܢ ܩܕܝܫܐ ܕܒܝܫܘܥ ܡܫܝܚܐ ܕܐܝܬ ܒܦܝܠܝܦܘܤ ܥܡ ܩܫܝܫܐ ܘܡܫܡܫܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 2, 'ܛܝܒܘܬܐ ܥܡܟܘܢ ܘܫܠܡܐ ܡܢ ܐܠܗܐ ܐܒܘܢ ܘܡܢ ܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 3, 'ܡܘܕܐ ܐܢܐ ܠܐܠܗܝ ܥܠ ܥܘܗܕܢܟܘܢ ܐܡܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 4, 'ܕܒܟܠ ܒܥܘܬܝ ܕܥܠ ܐܦܝܟܘܢ ܘܟܕ ܚܕܐ ܐܢܐ ܡܬܟܫܦ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 5, 'ܥܠ ܫܘܬܦܘܬܟܘܢ ܕܒܐܘܢܓܠܝܘܢ ܡܢ ܝܘܡܐ ܩܕܡܝܐ ܘܥܕܡܐ ܠܗܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 6, 'ܡܛܠ ܕܬܟܝܠ ܐܢܐ ܥܠ ܗܕܐ ܕܗܘ ܡܢ ܕܫܪܝ ܒܟܘܢ ܥܒܕܐ ܛܒܐ ܗܘ ܢܫܠܡ ܥܕܡܐ ܠܝܘܡܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 7, 'ܗܟܢܐ ܓܝܪ ܙܕܩ ܠܝ ܠܡܬܪܥܝܘ ܥܠ ܟܠܟܘܢ ܡܛܠ ܕܒܠܒܝ ܤܝܡܝܢ ܐܢܬܘܢ ܘܒܐܤܘܪܝ ܘܒܡܦܩ ܒܪܘܚܝ ܕܥܠ ܫܪܪܗ ܕܐܘܢܓܠܝܘܢ ܕܫܘܬܦܝ ܐܢܬܘܢ ܒܛܝܒܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 8, 'ܤܗܕ ܗܘ ܠܝ ܓܝܪ ܐܠܗܐ ܕܐܝܟܢܐ ܡܚܒ ܐܢܐ ܠܟܘܢ ܒܪܚܡܘܗܝ ܕܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 9, 'ܘܗܕܐ ܡܨܠܐ ܐܢܐ ܕܬܘܒ ܢܤܓܐ ܘܢܬܝܬܪ ܚܘܒܟܘܢ ܒܝܕܥܬܐ ܘܒܟܠ ܤܘܟܠ ܕܪܘܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 10, 'ܕܬܗܘܘܢ ܦܪܫܝܢ ܐܝܠܝܢ ܕܥܗܢܢ ܘܬܗܘܘܢ ܕܟܝܢ ܕܠܐ ܬܘܩܠܐ ܒܝܘܡܗ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 11, 'ܘܡܠܝܢ ܦܐܪܐ ܕܙܕܝܩܘܬܐ ܕܒܝܫܘܥ ܡܫܝܚܐ ܠܫܘܒܚܐ ܘܠܐܝܩܪܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 12, 'ܨܒܐ ܐܢܐ ܕܝܢ ܕܬܕܥܘܢ ܐܚܝ ܕܤܘܥܪܢܝ ܕܝܠܝ ܝܬܝܪܐܝܬ ܐܬܐ ܠܩܕܡܘܗܝ ܒܐܘܢܓܠܝܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 13, 'ܗܟܢܐ ܕܐܦ ܐܤܘܪܝ ܐܬܓܠܝܘ ܒܡܫܝܚܐ ܒܦܪܛܘܪܝܢ ܟܠܗ ܘܠܫܪܟܐ ܕܟܠܢܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 14, 'ܘܤܘܓܐܐ ܕܐܚܐ ܕܒܡܪܢ ܐܬܬܟܠܘ ܥܠ ܐܤܘܪܝ ܘܐܫܝܚܘ ܝܬܝܪܐܝܬ ܕܠܐ ܕܚܠܐ ܠܡܡܠܠܘ ܡܠܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 15, 'ܘܐܢܫ ܐܢܫ ܡܢ ܚܤܡܐ ܘܚܪܝܢܐ ܐܢܫ ܐܢܫ ܕܝܢ ܒܨܒܝܢܐ ܛܒܐ ܘܒܚܘܒܐ ܠܡܫܝܚܐ ܡܟܪܙܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 16, 'ܡܛܠ ܕܝܕܥܝܢ ܕܠܡܦܩ ܒܪܘܚܐ ܕܐܘܢܓܠܝܘܢ ܤܝܡ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 17, 'ܗܢܘܢ ܕܝܢ ܕܒܚܪܝܢܐ ܠܡܫܝܚܐ ܡܟܪܙܝܢ ܠܐ ܗܘܐ ܕܟܝܐܝܬ ܐܠܐ ܕܤܒܪܝܢ ܕܡܘܤܦܝܢ ܐܘܠܨܢܐ ܠܐܤܘܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 18, 'ܘܒܗܕܐ ܚܕܝܬ ܘܚܕܐ ܐܢܐ ܕܒܟܠ ܦܪܘܤ ܐܢ ܒܥܠܬܐ ܘܐܢ ܒܩܘܫܬܐ ܡܫܝܚܐ ܢܬܟܪܙ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 19, 'ܝܕܥ ܐܢܐ ܓܝܪ ܕܗܠܝܢ ܠܚܝܐ ܡܫܬܟܚܢ ܠܝ ܒܒܥܘܬܟܘܢ ܘܒܡܘܗܒܬܐ ܕܪܘܚܐ ܕܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 20, 'ܐܝܟܢܐ ܕܡܤܒܪ ܐܢܐ ܘܡܤܟܐ ܐܢܐ ܕܒܡܕܡ ܠܐ ܐܒܗܬ ܐܠܐ ܒܓܠܝܘܬ ܐܦܐ ܐܝܟ ܕܒܟܠܙܒܢ ܐܦ ܗܫܐ ܢܬܪܘܪܒ ܡܫܝܚܐ ܒܦܓܪܝ ܐܢ ܒܚܝܐ ܘܐܢ ܒܡܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 21, 'ܚܝܝ ܓܝܪ ܕܝܠܝ ܡܫܝܚܐ ܗܘ ܘܐܢ ܐܡܘܬ ܝܘܬܪܢܐ ܗܘ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 22, 'ܐܢ ܕܝܢ ܐܦ ܒܗܠܝܢ ܚܝܐ ܕܒܤܪܐ ܦܐܪܐ ܐܝܬ ܠܝ ܒܥܒܕܝ ܠܐ ܝܕܥ ܐܢܐ ܡܢܐ ܐܓܒܐ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 23, 'ܐܠܨܢ ܠܝ ܓܝܪ ܬܪܬܝܗܝܢ ܠܡܦܛܪ ܪܓܝܓ ܐܢܐ ܕܥܡ ܡܫܝܚܐ ܐܗܘܐ ܘܗܕܐ ܛܒ ܦܩܚܐ ܗܘܬ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 24, 'ܐܠܐ ܐܦ ܕܐܩܘܐ ܒܦܓܪܝ ܐܠܨܐ ܠܝ ܨܒܘܬܐ ܡܛܠܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 25, 'ܘܗܕܐ ܬܟܝܠܐܝܬ ܝܕܥ ܐܢܐ ܕܦܐܫ ܐܢܐ ܘܡܩܘܐ ܐܢܐ ܠܚܕܘܬܐ ܕܝܠܟܘܢ ܘܠܬܪܒܝܬܐ ܕܗܝܡܢܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 26, 'ܕܟܕ ܐܬܐ ܬܘܒ ܠܘܬܟܘܢ ܢܬܝܬܪ ܒܝ ܫܘܒܗܪܟܘܢ ܕܒܝܫܘܥ ܡܫܝܚܐ ܒܠܚܘܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 27, 'ܐܝܟܢܐ ܕܝܐܐ ܠܤܒܪܬܗ ܕܡܫܝܚܐ ܗܘܝܬܘܢ ܡܬܕܒܪܝܢ ܕܐܢ ܐܬܐ ܐܚܙܝܟܘܢ ܘܐܢ ܦܪܝܩ ܐܢܐ ܐܫܡܥ ܥܠܝܟܘܢ ܕܩܝܡܝܢ ܐܢܬܘܢ ܒܚܕܐ ܪܘܚ ܘܒܚܕܐ ܢܦܫ ܘܡܬܢܨܚܝܬܘܢ ܐܟܚܕ ܒܗܝܡܢܘܬܐ ܕܐܘܢܓܠܝܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 28, 'ܘܒܡܕܡ ܠܐ ܡܬܬܙܝܥܝܢ ܐܢܬܘܢ ܡܢ ܗܢܘܢ ܕܩܝܡܝܢ ܠܩܘܒܠܢ ܠܬܚܘܝܬܐ ܕܐܒܕܢܗܘܢ ܘܠܚܝܐ ܕܝܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 29, 'ܘܗܕܐ ܡܢ ܐܠܗܐ ܗܘ ܐܬܝܗܒܬ ܠܟܘܢ ܕܠܐ ܒܠܚܘܕ ܡܗܝܡܢܘ ܬܗܝܡܢܘܢ ܒܗ ܒܡܫܝܚܐ ܐܠܐ ܐܦ ܕܥܠ ܐܦܘܗܝ ܬܚܫܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 30, 'ܘܬܤܝܒܪܘܢ ܐܓܘܢܐ ܐܝܟ ܗܘ ܕܚܙܝܬܘܢ ܒܝ ܘܗܫܐ ܫܡܥܝܬܘܢ ܥܠܝ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 1, 'ܐܢ ܐܝܬ ܠܟܘܢ ܗܟܝܠ ܒܘܝܐܐ ܒܡܫܝܚܐ ܘܐܢ ܡܡܠܐ ܒܠܒܐ ܒܚܘܒܐ ܘܐܢ ܫܘܬܦܘܬܐ ܕܪܘܚܐ ܘܐܢ ܪܘܚܦܐ ܘܪܚܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 2, 'ܫܠܡܘ ܚܕܘܬܝ ܕܚܕ ܪܥܝܢܐ ܢܗܘܐ ܠܟܘܢ ܘܚܕ ܚܘܒܐ ܘܚܕܐ ܢܦܫ ܘܚܕܐ ܬܪܥܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 3, 'ܘܡܕܡ ܒܚܪܝܢܐ ܐܘ ܒܬܫܒܘܚܬܐ ܤܪܝܩܬܐ ܠܐ ܬܥܒܕܘܢ ܐܠܐ ܒܡܟܝܟܘܬ ܪܥܝܢܐ ܟܠܢܫ ܠܚܒܪܗ ܐܝܟ ܕܡܝܬܪ ܡܢܗ ܢܚܫܘܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 4, 'ܘܠܐ ܐܢܫ ܕܢܦܫܗ ܢܐܨܦ ܐܠܐ ܟܠܢܫ ܐܦ ܕܚܒܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 5, 'ܘܗܕܐ ܐܬܪܥܘ ܒܢܦܫܟܘܢ ܗܝ ܕܐܦ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 6, 'ܗܘ ܕܟܕ ܐܝܬܘܗܝ ܒܕܡܘܬܐ ܕܐܠܗܐ ܠܐ ܗܘܐ ܚܛܘܦܝܐ ܚܫܒܗ ܗܕܐ ܕܐܝܬܘܗܝ ܦܚܡܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 7, 'ܐܠܐ ܢܦܫܗ ܤܪܩ ܘܕܡܘܬܐ ܕܥܒܕܐ ܢܤܒ ܘܗܘܐ ܒܕܡܘܬܐ ܕܒܢܝܢܫܐ ܘܒܐܤܟܡܐ ܐܫܬܟܚ ܐܝܟ ܒܪܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 8, 'ܘܡܟܟ ܢܦܫܗ ܘܐܫܬܡܥ ܥܕܡܐ ܠܡܘܬܐ ܡܘܬܐ ܕܝܢ ܕܙܩܝܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 9, 'ܡܛܠ ܗܢܐ ܐܦ ܐܠܗܐ ܐܤܓܝ ܪܡܪܡܗ ܘܝܗܒ ܠܗ ܫܡܐ ܕܡܝܬܪ ܡܢ ܟܠ ܫܡܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 10, 'ܕܒܫܡܗ ܕܝܫܘܥ ܟܠ ܒܪܘܟ ܬܟܘܦ ܕܒܫܡܝܐ ܘܒܐܪܥܐ ܘܕܠܬܚܬ ܡܢ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 11, 'ܘܟܠ ܠܫܢ ܢܘܕܐ ܕܡܪܝܐ ܗܘ ܝܫܘܥ ܡܫܝܚܐ ܠܫܘܒܚܐ ܕܐܠܗܐ ܐܒܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 12, 'ܡܟܝܠ ܚܒܝܒܝ ܐܝܟܢܐ ܕܒܟܠܙܒܢ ܐܫܬܡܥܬܘܢ ܠܐ ܟܕ ܩܪܝܒ ܐܢܐ ܠܟܘܢ ܒܠܚܘܕ ܐܠܐ ܗܫܐ ܕܪܚܝܩ ܐܢܐ ܡܢܟܘܢ ܝܬܝܪܐܝܬ ܒܕܚܠܬܐ ܘܒܪܬܝܬܐ ܦܠܘܚܘ ܦܘܠܚܢܐ ܕܚܝܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 13, 'ܐܠܗܐ ܗܘ ܓܝܪ ܡܚܦܛ ܒܟܘܢ ܐܦ ܠܡܨܒܐ ܐܦ ܠܡܤܥܪ ܗܘ ܡܕܡ ܕܨܒܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 14, 'ܟܠܡܕܡ ܗܘܝܬܘܢ ܥܒܕܝܢ ܕܠܐ ܪܛܢܐ ܘܕܠܐ ܦܘܠܓ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 15, 'ܕܬܗܘܘܢ ܬܡܝܡܐ ܘܕܠܐ ܡܘܡ ܐܝܟ ܒܢܝܐ ܕܟܝܐ ܕܐܠܗܐ ܕܥܡܪܝܢ ܒܕܪܐ ܥܤܩܐ ܘܡܥܩܡܐ ܘܐܬܚܙܘ ܒܝܢܬܗܘܢ ܐܝܟ ܢܗܝܪܐ ܒܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 16, 'ܕܐܝܬܝܟܘܢ ܠܗܘܢ ܒܕܘܟܬ ܚܝܐ ܠܫܘܒܗܪܝ ܕܝܠܝ ܒܝܘܡܗ ܕܡܫܝܚܐ ܕܠܐ ܗܘܐ ܐܝܩܐ ܪܗܛܬ ܘܠܐ ܤܪܝܩܐܝܬ ܥܡܠܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 17, 'ܐܠܐ ܐܦܢ ܡܬܢܩܐ ܐܢܐ ܥܠ ܕܒܚܬܐ ܘܬܫܡܫܬܐ ܕܗܝܡܢܘܬܟܘܢ ܚܕܐ ܐܢܐ ܘܪܘܙ ܐܢܐ ܥܡ ܟܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 18, 'ܗܟܢܐ ܐܦ ܐܢܬܘܢ ܚܕܘ ܘܪܘܙܘ ܥܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 19, 'ܡܤܒܪ ܐܢܐ ܕܝܢ ܒܡܪܢ ܝܫܘܥ ܕܐܫܕܪ ܠܘܬܟܘܢ ܠܛܝܡܬܐܘܤ ܒܥܓܠ ܕܐܦ ܠܝ ܢܗܘܐ ܠܝ ܢܝܚܐ ܟܕ ܐܠܦ ܡܛܠܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 20, 'ܠܝܬ ܠܝ ܓܝܪ ܐܚܪܝܢ ܗܪܟܐ ܕܐܝܟ ܢܦܫܝ ܗܘ ܕܐܟܝܦܐܝܬ ܝܨܦ ܕܝܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 21, 'ܟܠܗܘܢ ܓܝܪ ܕܢܦܫܗܘܢ ܗܘ ܒܥܝܢ ܘܠܐ ܕܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 22, 'ܒܘܩܝܗ ܕܝܢ ܕܗܢܐ ܝܕܥܝܬܘܢ ܕܐܝܟ ܒܪܐ ܥܡ ܐܒܘܗܝ ܗܟܢܐ ܦܠܚ ܥܡܝ ܒܤܒܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 23, 'ܠܗܢܐ ܡܤܒܪ ܐܢܐ ܕܐܫܕܪ ܠܘܬܟܘܢ ܒܥܓܠ ܡܐ ܕܚܙܝܬ ܡܐ ܕܠܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 24, 'ܘܬܟܝܠ ܐܢܐ ܥܠ ܡܪܝ ܕܐܦ ܐܢܐ ܒܥܓܠ ܐܬܐ ܐܢܐ ܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 25, 'ܗܫܐ ܕܝܢ ܐܠܨܬܢܝ ܨܒܘܬܐ ܕܐܫܕܪ ܠܘܬܟܘܢ ܠܐܦܦܪܘܕܝܛܤ ܐܚܐ ܕܐܝܬܘܗܝ ܡܥܕܪܢܐ ܘܦܠܚܐ ܕܥܡܝ ܕܝܠܟܘܢ ܕܝܢ ܫܠܝܚܐ ܘܡܫܡܫܢܐ ܕܚܫܚܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 26, 'ܡܛܠ ܕܤܘܐ ܗܘܐ ܠܡܚܙܐ ܠܟܠܟܘܢ ܘܡܥܩ ܗܘܐ ܕܝܕܥ ܕܫܡܥܬܘܢ ܕܐܬܟܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 27, 'ܐܦ ܐܬܟܪܗ ܓܝܪ ܥܕܡܐ ܠܡܘܬܐ ܐܠܐ ܐܠܗܐ ܪܚܡ ܥܠܘܗܝ ܠܐ ܗܘܐ ܕܝܢ ܥܠܘܗܝ ܒܠܚܘܕ ܐܠܐ ܐܦ ܥܠܝ ܕܠܐ ܬܗܘܐ ܠܝ ܥܩܐ ܥܠ ܥܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 28, 'ܚܦܝܛܐܝܬ ܗܟܝܠ ܫܕܪܬܗ ܠܘܬܟܘܢ ܕܟܕ ܬܚܙܘܢܝܗܝ ܬܘܒ ܬܚܕܘܢ ܘܠܝ ܩܠܝܠ ܢܗܘܐ ܢܦܐܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 29, 'ܩܒܠܘܗܝ ܗܟܝܠ ܒܡܪܝܐ ܒܟܠ ܚܕܘܐ ܘܠܐܝܠܝܢ ܕܗܟܢܐ ܐܢܘܢ ܒܐܝܩܪܐ ܐܚܘܕܘ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 30, 'ܡܛܠ ܥܒܕܐ ܓܝܪ ܕܡܫܝܚܐ ܥܕܡܐ ܠܡܘܬܐ ܡܛܝ ܘܒܤܪ ܥܠ ܢܦܫܗ ܕܢܡܠܐ ܡܕܡ ܕܒܨܪܬܘܢ ܗܘܝܬܘܢ ܒܬܫܡܫܬܐ ܕܠܘܬܝ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 1, 'ܡܟܝܠ ܐܚܝ ܚܕܘ ܒܡܪܢ ܗܢܝܢ ܟܕ ܗܢܝܢ ܕܐܟܬܘܒ ܠܟܘܢ ܠܐ ܡܐܢܐ ܠܝ ܡܛܠ ܕܠܟܘܢ ܡܙܗܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 2, 'ܐܙܕܗܪܘ ܡܢ ܟܠܒܐ ܐܙܕܗܪܘ ܡܢ ܦܥܠܐ ܒܝܫܐ ܐܙܕܗܪܘ ܡܢ ܦܤܩ ܒܤܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 3, 'ܓܙܘܪܬܐ ܓܝܪ ܚܢܢ ܐܝܬܝܢ ܐܝܠܝܢ ܕܦܠܚܝܢܢ ܠܐܠܗܐ ܒܪܘܚܐ ܘܡܫܬܒܗܪܝܢܢ ܒܝܫܘܥ ܡܫܝܚܐ ܘܠܐ ܬܟܝܠܝܢܢ ܥܠ ܒܤܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 4, 'ܟܕ ܠܝ ܐܝܬ ܗܘܐ ܠܝ ܬܘܟܠܢܐ ܐܦ ܥܠ ܒܤܪܐ ܐܢ ܓܝܪ ܐܢܫ ܤܒܪ ܕܬܘܟܠܢܗ ܒܒܤܪܐ ܗܘ ܝܬܝܪ ܡܢܗ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 5, 'ܓܙܝܪܐ ܒܪ ܬܡܢܝܐ ܝܘܡܝܢ ܡܢ ܛܘܗܡܐ ܕܐܝܤܪܝܠ ܡܢ ܫܒܛܐ ܕܒܢܝܡܝܢ ܥܒܪܝܐ ܒܪ ܥܒܪܝܐ ܒܢܡܘܤܐ ܦܪܝܫܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 6, 'ܒܛܢܢܐ ܪܕܘܦܐ ܕܥܕܬܐ ܘܒܙܕܝܩܘܬܐ ܕܢܡܘܤܐ ܕܠܐ ܪܫܝܢ ܗܘܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 7, 'ܐܠܐ ܗܠܝܢ ܕܝܘܬܪܢܐ ܗܘܝ ܠܝ ܚܘܤܪܢܐ ܚܫܒܬ ܐܢܝܢ ܡܛܠ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 8, 'ܐܦ ܚܫܒ ܐܢܐ ܠܗܝܢ ܟܠܗܝܢ ܚܘܤܪܢܐ ܡܛܠ ܪܒܘܬܐ ܕܝܕܥܬܗ ܕܝܫܘܥ ܡܫܝܚܐ ܡܪܝ ܗܘ ܕܡܛܠܬܗ ܟܠܡܕܡ ܚܤܪܬ ܘܐܝܟ ܙܒܠܐ ܚܫܒܬ ܕܠܡܫܝܚܐ ܐܬܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 9, 'ܘܐܫܬܟܚ ܒܗ ܟܕ ܠܝܬ ܠܝ ܙܕܝܩܘܬܐ ܕܢܦܫܝ ܗܝ ܕܡܢ ܢܡܘܤܐ ܐܠܐ ܗܝ ܕܡܢ ܗܝܡܢܘܬܐ ܕܡܫܝܚܐ ܕܐܝܬܝܗ ܙܕܝܩܘܬܐ ܕܡܢ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 10, 'ܕܒܗ ܐܫܬܘܕܥ ܠܝܫܘܥ ܘܠܚܝܠܐ ܕܩܝܡܬܗ ܘܐܫܬܘܬܦ ܒܚܫܘܗܝ ܘܐܬܕܡܐ ܒܡܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 11, 'ܕܠܡܐ ܐܫܟܚ ܐܡܛܐ ܠܩܝܡܬܐ ܕܡܢ ܒܝܬ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 12, 'ܠܐ ܗܘܐ ܡܢ ܟܕܘ ܢܤܒܬ ܐܘ ܡܢ ܟܕܘ ܐܬܓܡܪܬ ܐܠܐ ܪܗܛ ܐܢܐ ܕܠܡܐ ܐܕܪܟ ܠܡܕܡ ܕܡܛܠܬܗ ܐܕܪܟܢܝ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 13, 'ܐܚܝ ܐܢܐ ܥܠ ܢܦܫܝ ܠܐ ܪܢܐ ܐܢܐ ܕܐܕܪܟܬ ܚܕܐ ܕܝܢ ܝܕܥ ܐܢܐ ܕܕܒܤܬܪܝ ܛܥܐ ܐܢܐ ܘܠܩܕܡܝ ܡܫܬܘܫܛ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 14, 'ܘܪܗܛ ܐܢܐ ܠܘܩܒܠ ܢܝܫܐ ܕܐܤܒ ܙܟܘܬܐ ܕܩܪܝܢܐ ܕܠܥܠ ܕܐܠܗܐ ܒܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 15, 'ܐܝܠܝܢ ܕܓܡܝܪܝܢ ܗܟܝܠ ܗܠܝܢ ܢܬܪܥܘܢ ܘܐܢ ܡܕܡ ܐܚܪܢܝܐܝܬ ܡܬܪܥܝܢ ܐܢܬܘܢ ܐܦ ܗܕܐ ܐܠܗܐ ܢܓܠܐ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 16, 'ܒܪܡ ܠܗܕܐ ܕܡܛܝܢ ܒܚܕ ܫܒܝܠܐ ܢܫܠܡ ܘܒܚܕܐ ܐܘܝܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 17, 'ܐܬܕܡܘ ܒܝ ܐܚܝ ܘܗܘܝܬܘܢ ܡܬܒܩܝܢ ܒܐܝܠܝܢ ܕܗܟܢܐ ܡܗܠܟܝܢ ܐܝܟ ܕܡܘܬܐ ܕܒܢ ܚܙܝܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 18, 'ܐܝܬ ܓܝܪ ܤܓܝܐܐ ܕܐܚܪܢܝܐܝܬ ܡܗܠܟܝܢ ܗܢܘܢ ܕܙܒܢܝܢ ܤܓܝܐܢ ܐܡܪܬ ܠܟܘܢ ܥܠܝܗܘܢ ܗܫܐ ܕܝܢ ܟܕ ܒܟܐ ܐܢܐ ܐܡܪ ܐܢܐ ܗܢܘܢ ܕܒܥܠܕܒܒܐ ܐܢܘܢ ܕܙܩܝܦܗ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 19, 'ܕܚܪܬܗܘܢ ܐܒܕܢܐ ܗܝ ܗܢܘܢ ܕܐܠܗܗܘܢ ܟܪܤܗܘܢ ܘܬܫܒܘܚܬܗܘܢ ܒܗܬܬܗܘܢ ܗܠܝܢ ܕܬܪܥܝܬܗܘܢ ܒܐܪܥܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 20, 'ܕܝܠܢ ܕܝܢ ܦܘܠܚܢܢ ܒܫܡܝܐ ܗܘ ܘܡܢ ܬܡܢ ܡܤܟܝܢܢ ܠܡܚܝܢܐ ܠܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 21, 'ܕܗܘ ܢܚܠܦ ܦܓܪܐ ܕܡܘܟܟܢ ܕܢܗܘܐ ܒܕܡܘܬܐ ܕܦܓܪܐ ܕܫܘܒܚܗ ܐܝܟ ܚܝܠܗ ܪܒܐ ܗܘ ܕܒܗ ܟܠ ܐܫܬܥܒܕ ܠܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 1, 'ܡܟܝܠ ܐܚܝ ܚܒܝܒܐ ܘܪܚܝܡܐ ܘܚܕܘܬܝ ܘܟܠܝܠܝ ܗܟܢܐ ܩܘܡܘ ܒܡܪܢ ܚܒܝܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 2, 'ܡܢ ܐܘܗܕܝܐ ܒܥܐ ܐܢܐ ܘܡܢ ܤܘܢܛܝܟܐ ܕܚܕ ܪܥܝܢܐ ܢܗܘܐ ܠܗܝܢ ܒܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 3, 'ܐܦ ܡܢܟ ܒܥܐ ܐܢܐ ܒܪ ܙܘܓܝ ܫܪܝܪܐ ܕܬܗܘܐ ܡܥܕܪ ܠܗܝܢ ܕܗܢܝܢ ܠܐܝ ܥܡܝ ܒܐܘܢܓܠܝܘܢ ܥܡ ܩܠܡܝܤ ܘܥܡ ܫܪܟܐ ܕܡܥܕܪܢܝ ܗܢܘܢ ܕܫܡܗܝܗܘܢ ܟܬܝܒܝܢ ܒܟܬܒܐ ܕܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 4, 'ܚܕܘ ܒܡܪܢ ܒܟܠܙܒܢ ܘܬܘܒ ܐܡܪ ܐܢܐ ܚܕܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 5, 'ܘܡܟܝܟܘܬܟܘܢ ܬܬܝܕܥ ܠܘܬ ܟܠܢܫ ܡܪܢ ܩܪܝܒ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 6, 'ܕܡܕܡ ܠܐ ܬܐܨܦܘܢ ܐܠܐ ܒܟܠܙܒܢ ܒܨܠܘܬܐ ܘܒܒܥܘܬܐ ܘܒܬܘܕܝܬܐ ܫܐܠܬܟܘܢ ܢܬܝܕܥܢ ܩܕܡ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 7, 'ܘܫܠܡܗ ܕܐܠܗܐ ܗܘ ܕܪܒ ܡܢ ܟܠ ܡܕܥ ܢܢܛܪ ܠܒܘܬܟܘܢ ܘܡܕܥܝܟܘܢ ܒܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 8, 'ܡܟܝܠ ܐܚܝ ܐܝܠܝܢ ܕܫܪܝܪܢ ܘܐܝܠܝܢ ܕܢܟܦܢ ܘܐܝܠܝܢ ܕܟܐܢܢ ܘܐܝܠܝܢ ܕܕܟܝܢ ܘܐܝܠܝܢ ܕܪܚܝܡܢ ܘܐܝܠܝܢ ܕܫܒܝܚܢ ܘܐܝܠܝܢ ܥܒܕܐ ܕܫܘܒܚܐ ܘܕܩܘܠܤܐ ܗܠܝܢ ܐܬܪܥܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 9, 'ܗܠܝܢ ܕܝܠܦܬܘܢ ܘܩܒܠܬܘܢ ܘܫܡܥܬܘܢ ܘܚܙܝܬܘܢ ܒܝ ܗܠܝܢ ܤܥܘܪܘ ܘܐܠܗܐ ܕܫܠܡܐ ܢܗܘܐ ܥܡܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 10, 'ܪܘܪܒܐܝܬ ܕܝܢ ܚܕܝܬ ܒܡܪܢ ܕܐܩܦܬܘܢ ܠܡܐܨܦ ܕܝܠܝ ܐܝܟܢܐ ܕܐܦ ܝܨܦܝܢ ܗܘܝܬܘܢ ܐܠܐ ܠܐ ܤܦܝܩܝܢ ܗܘܝܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 11, 'ܠܐ ܗܘܐ ܕܝܢ ܡܛܠ ܕܚܤܪ ܠܝ ܐܡܪ ܐܢܐ ܐܢܐ ܓܝܪ ܝܠܦܬ ܕܢܗܘܐ ܤܦܩ ܠܝ ܗܘ ܡܕܡ ܕܐܝܬ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 12, 'ܝܕܥ ܐܢܐ ܕܐܬܡܟܟ ܝܕܥ ܐܢܐ ܗܘ ܐܦ ܕܐܬܝܬܪ ܒܟܠ ܘܒܟܠ ܡܕܡ ܡܕܪܫ ܐܢܐ ܐܦ ܒܤܒܥܐ ܐܦ ܒܟܦܢܐ ܒܡܫܪܬܚܘܬܐ ܘܒܚܤܝܪܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 13, 'ܕܟܠ ܡܕܡ ܡܨܐ ܐܢܐ ܚܝܠܐ ܒܡܫܝܚܐ ܕܡܚܝܠ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 14, 'ܒܪܡ ܫܦܝܪ ܥܒܕܬܘܢ ܕܐܫܬܘܬܦܬܘܢ ܠܐܘܠܨܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 15, 'ܝܕܥܝܢ ܐܢܬܘܢ ܕܝܢ ܐܦ ܐܢܬܘܢ ܦܝܠܝܦܤܝܐ ܕܒܫܘܪܝܐ ܕܤܒܪܬܐ ܟܕ ܢܦܩܬ ܡܢ ܡܩܕܘܢܝܐ ܐܦܠܐ ܚܕܐ ܡܢ ܥܕܬܐ ܐܫܬܘܬܦ ܠܝ ܒܚܘܫܒܢ ܡܤܒܐ ܘܡܬܠܐ ܐܠܐ ܐܢܬܘܢ ܒܠܚܘܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 16, 'ܕܐܦ ܠܬܤܠܘܢܝܩܐ ܚܕܐ ܙܒܢ ܘܬܪܬܝܢ ܚܫܚܬܝ ܫܕܪܬܘܢ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 17, 'ܠܘ ܕܡܘܗܒܬܐ ܒܥܐ ܐܢܐ ܐܠܐ ܒܥܐ ܐܢܐ ܕܦܐܪܐ ܢܤܓܘܢ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 18, 'ܟܠܡܕܡ ܩܒܠܬ ܘܝܬܝܪ ܠܝ ܘܡܠܐ ܐܢܐ ܘܢܤܒܬ ܟܠ ܕܫܕܪܬܘܢ ܠܝ ܒܝܕ ܐܦܦܪܘܕܝܛܤ ܪܝܚܐ ܒܤܝܡܐ ܘܕܒܚܐ ܡܩܒܠܐ ܕܫܦܪ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 19, 'ܘܐܠܗܝ ܢܡܠܐ ܟܠܗ ܤܢܝܩܘܬܟܘܢ ܐܝܟ ܥܘܬܪܗ ܒܫܘܒܚܐ ܕܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 20, 'ܠܐܠܗܐ ܕܝܢ ܐܒܘܢ ܫܘܒܚܐ ܘܐܝܩܪܐ ܠܥܠܡ ܥܠܡܝܢ ܐܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 21, 'ܫܐܠܘ ܒܫܠܡܐ ܕܟܠܗܘܢ ܩܕܝܫܐ ܕܒܝܫܘܥ ܡܫܝܚܐ ܫܐܠܝܢ ܒܫܠܡܟܘܢ ܐܚܐ ܕܥܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 22, 'ܫܐܠܝܢ ܒܫܠܡܟܘܢ ܟܠܗܘܢ ܩܕܝܫܐ ܝܬܝܪܐܝܬ ܗܠܝܢ ܕܡܢ ܒܝܬܗ ܕܩܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 23, 'ܛܝܒܘܬܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܥܡ ܟܠܟܘܢ ܐܡܝܢ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 1, 'ܦܘܠܘܤ ܫܠܝܚܐ ܕܝܫܘܥ ܡܫܝܚܐ ܒܨܒܝܢܗ ܕܐܠܗܐ ܘܛܝܡܬܐܘܤ ܐܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 2, 'ܠܐܝܠܝܢ ܕܐܝܬ ܒܩܘܠܤܘܤ ܐܚܐ ܩܕܝܫܐ ܘܡܗܝܡܢܐ ܒܝܫܘܥ ܡܫܝܚܐ ܫܠܡܐ ܥܡܟܘܢ ܘܛܝܒܘܬܐ ܡܢ ܐܠܗܐ ܐܒܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 3, 'ܡܘܕܝܢܢ ܠܐܠܗܐ ܐܒܘܗܝ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܒܟܠܙܒܢ ܘܡܨܠܝܢܢ ܥܠܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 4, 'ܗܐ ܡܢ ܕܫܡܥܢ ܗܝܡܢܘܬܟܘܢ ܕܒܝܫܘܥ ܡܫܝܚܐ ܘܚܘܒܟܘܢ ܕܠܘܬ ܟܠܗܘܢ ܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 5, 'ܡܛܠ ܤܒܪܐ ܗܘ ܕܢܛܝܪ ܠܟܘܢ ܒܫܡܝܐ ܗܘ ܕܡܢ ܩܕܝܡ ܫܡܥܬܘܢ ܒܡܠܬܐ ܕܩܘܫܬܐ ܕܤܒܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 6, 'ܗܝ ܕܐܬܟܪܙܬ ܠܟܘܢ ܐܝܟ ܕܐܦ ܠܟܠܗ ܥܠܡܐ ܘܪܒܝܐ ܘܝܗܒܐ ܦܐܪܐ ܐܝܟܢܐ ܕܐܦ ܒܟܘܢ ܡܢ ܝܘܡܐ ܕܫܡܥܬܘܢ ܘܐܫܬܘܕܥܬܘܢ ܛܝܒܘܬܗ ܕܐܠܗܐ ܒܩܘܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 7, 'ܐܝܟ ܡܐ ܕܝܠܦܬܘܢ ܡܢ ܐܦܦܪܐ ܟܢܬܢ ܚܒܝܒܐ ܕܗܘ ܐܝܬܘܗܝ ܚܠܦܝܟܘܢ ܡܫܡܫܢܐ ܡܗܝܡܢܐ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 8, 'ܘܗܘ ܐܘܕܥܢ ܚܘܒܟܘܢ ܕܒܪܘܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 9, 'ܡܛܠܗܢܐ ܐܦ ܚܢܢ ܡܢ ܝܘܡܐ ܕܫܡܥܢ ܠܐ ܫܠܝܢܢ ܠܡܨܠܝܘ ܥܠܝܟܘܢ ܘܠܡܫܐܠ ܕܬܬܡܠܘܢ ܝܕܥܬܐ ܕܨܒܝܢܗ ܕܐܠܗܐ ܒܟܠ ܚܟܡܐ ܘܒܟܠ ܤܘܟܠ ܕܪܘܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 10, 'ܕܬܗܠܟܘܢ ܐܝܟ ܕܙܕܩ ܘܬܫܦܪܘܢ ܠܐܠܗܐ ܒܟܠ ܥܒܕܝܢ ܛܒܝܢ ܘܬܬܠܘܢ ܦܐܪܐ ܘܬܪܒܘܢ ܒܝܕܥܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 11, 'ܘܒܟܠ ܚܝܠ ܬܬܚܝܠܘܢ ܐܝܟ ܪܒܘܬܐ ܕܫܘܒܚܗ ܒܟܠ ܡܤܝܒܪܢܘ ܘܒܡܓܪܬ ܪܘܚ ܘܒܚܕܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 12, 'ܬܘܕܘܢ ܠܐܠܗܐ ܐܒܐ ܕܐܫܘܝܢ ܠܡܢܬܐ ܕܝܪܬܘܬܐ ܕܩܕܝܫܐ ܒܢܘܗܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 13, 'ܘܦܪܩܢ ܡܢ ܫܘܠܛܢܗ ܕܚܫܘܟܐ ܘܐܝܬܝܢ ܠܡܠܟܘܬܐ ܕܒܪܗ ܚܒܝܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 14, 'ܗܘ ܕܒܗ ܐܝܬ ܠܢ ܦܘܪܩܢܐ ܘܫܘܒܩܢܐ ܕܚܛܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 15, 'ܗܘ ܕܗܘܝܘ ܕܡܘܬܐ ܕܐܠܗܐ ܕܠܐ ܡܬܚܙܐ ܘܒܘܟܪܐ ܕܟܠܗܝܢ ܒܪܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 16, 'ܘܒܗ ܐܬܒܪܝ ܟܠܡܕܡ ܕܒܫܡܝܐ ܘܒܐܪܥܐ ܟܠ ܕܡܬܚܙܐ ܘܟܠ ܕܠܐ ܡܬܚܙܐ ܐܢ ܡܘܬܒܐ ܘܐܢ ܡܪܘܬܐ ܘܐܢ ܐܪܟܘܤ ܘܐܢ ܫܘܠܛܢܐ ܟܠܡܕܡ ܒܐܝܕܗ ܘܒܗ ܐܬܒܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 17, 'ܘܗܘܝܘ ܕܡܢ ܩܕܡ ܟܠ ܘܟܠܡܕܡ ܒܗ ܩܐܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 18, 'ܘܗܘܝܘ ܪܫܐ ܕܦܓܪܐ ܕܥܕܬܐ ܕܐܝܬܘܗܝ ܪܫܐ ܘܒܘܟܪܐ ܕܡܢ ܒܝܬ ܡܝܬܐ ܕܢܗܘܐ ܩܕܡܝ ܒܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 19, 'ܕܒܗ ܗܘ ܨܒܐ ܟܠܗ ܡܘܠܝܐ ܠܡܥܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 20, 'ܘܒܐܝܕܗ ܠܡܪܥܝܘ ܠܗ ܟܠܡܕܡ ܘܫܝܢ ܒܕܡܐ ܕܙܩܝܦܗ ܒܐܝܕܘܗܝ ܐܢ ܕܒܐܪܥܐ ܘܐܢ ܕܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 21, 'ܐܦ ܠܟܘܢ ܕܡܢ ܩܕܝܡ ܢܘܟܪܝܐ ܗܘܝܬܘܢ ܘܒܥܠܕܒܒܐ ܒܪܥܝܢܝܟܘܢ ܡܛܠ ܥܒܕܝܟܘܢ ܒܝܫܐ ܫܝܢܟܘܢ ܗܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 22, 'ܒܦܓܪܐ ܕܒܤܪܗ ܘܒܡܘܬܗ ܕܢܩܝܡܟܘܢ ܩܕܡܘܗܝ ܩܕܝܫܝܢ ܕܠܐ ܡܘܡ ܘܕܠܐ ܪܫܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 23, 'ܐܢ ܬܩܘܘܢ ܒܗܝܡܢܘܬܟܘܢ ܟܕ ܫܪܝܪܐ ܫܬܐܤܬܟܘܢ ܘܠܐ ܡܬܬܙܝܥܝܬܘܢ ܡܢ ܤܒܪܐ ܕܐܘܢܓܠܝܘܢ ܗܘ ܕܫܡܥܬܘܢ ܕܐܬܟܪܙ ܒܟܠܗ ܒܪܝܬܐ ܕܬܚܝܬ ܫܡܝܐ ܗܘ ܕܐܢܐ ܦܘܠܘܤ ܗܘܝܬ ܡܫܡܫܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 24, 'ܘܚܕܐ ܐܢܐ ܒܚܫܐ ܕܥܠ ܐܦܝܟܘܢ ܘܡܡܠܐ ܐܢܐ ܚܤܝܪܘܬܐ ܕܐܘܠܨܢܘܗܝ ܕܡܫܝܚܐ ܒܒܤܪܝ ܚܠܦ ܦܓܪܗ ܕܐܝܬܘܗܝ ܥܕܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 25, 'ܗܝ ܕܐܢܐ ܗܘܝܬ ܡܫܡܫܢܗ ܐܝܟ ܡܕܒܪܢܘܬܐ ܕܐܠܗܐ ܕܐܬܝܗܒܬ ܠܝ ܒܟܘܢ ܕܐܫܡܠܐ ܡܠܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 26, 'ܐܪܙܐ ܗܘ ܕܡܟܤܝ ܗܘܐ ܡܢ ܥܠܡܐ ܘܡܢ ܕܪܐ ܗܫܐ ܕܝܢ ܐܬܓܠܝ ܠܩܕܝܫܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 27, 'ܠܐܝܠܝܢ ܕܨܒܐ ܐܠܗܐ ܕܢܘܕܥ ܡܢܘ ܥܘܬܪܐ ܕܫܘܒܚܗ ܕܐܪܙܐ ܗܢܐ ܒܥܡܡܐ ܕܗܘܝܘ ܡܫܝܚܐ ܕܒܟܘܢ ܤܒܪܐ ܕܫܘܒܚܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 28, 'ܗܘ ܕܚܢܢ ܡܟܪܙܝܢܢ ܘܡܠܦܝܢܢ ܘܡܤܟܠܝܢܢ ܠܟܠ ܒܪܢܫ ܒܟܠ ܚܟܡܐ ܕܢܩܝܡ ܠܟܠ ܒܪܢܫ ܟܕ ܓܡܝܪ ܒܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 29, 'ܒܗܕܐ ܗܘ ܓܝܪ ܐܦ ܥܡܠ ܐܢܐ ܘܡܬܟܬܫ ܐܢܐ ܒܡܥܕܪܢܘܬܐ ܕܚܝܠܐ ܕܡܬܝܗܒ ܠܝ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 1, 'ܨܒܐ ܐܢܐ ܕܝܢ ܕܬܕܥܘܢ ܐܝܢܐ ܐܓܘܢܐ ܐܝܬ ܠܝ ܚܠܦܝܟܘܢ ܘܚܠܦ ܗܢܘܢ ܕܒܠܕܝܩܝܐ ܘܚܠܦ ܫܪܟܐ ܐܝܠܝܢ ܕܦܪܨܘܦܝ ܠܐ ܚܙܘ ܒܒܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 2, 'ܕܢܬܒܝܐܘܢ ܠܒܘܬܗܘܢ ܘܢܬܩܪܒܘܢ ܒܚܘܒܐ ܠܟܠܗ ܥܘܬܪܐ ܕܦܝܤܐ ܘܠܤܘܟܠܐ ܕܝܕܥܬܗ ܕܐܪܙܐ ܕܐܠܗܐ ܐܒܐ ܘܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 3, 'ܗܘ ܕܒܗ ܟܤܝܢ ܟܠܗܝܢ ܤܝܡܬܐ ܕܚܟܡܬܐ ܘܕܝܕܥܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 4, 'ܗܕܐ ܕܝܢ ܐܡܪ ܐܢܐ ܕܠܐ ܐܢܫ ܢܗܘܐ ܡܛܥܐ ܠܟܘܢ ܒܦܝܤܐ ܕܡܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 5, 'ܐܦܢ ܒܒܤܪ ܓܝܪ ܦܪܝܩ ܐܢܐ ܡܢܟܘܢ ܐܠܐ ܒܪܘܚ ܥܡܟܘܢ ܐܢܐ ܘܚܕܐ ܐܢܐ ܕܚܙܐ ܐܢܐ ܡܛܟܤܘܬܟܘܢ ܘܫܪܝܪܘܬܐ ܕܗܝܡܢܘܬܟܘܢ ܕܒܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 6, 'ܐܝܟܢܐ ܗܟܝܠ ܕܩܒܠܬܘܢ ܠܝܫܘܥ ܡܫܝܚܐ ܡܪܢ ܒܗ ܗܠܟܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 7, 'ܟܕ ܡܫܪܪܝܢ ܥܩܪܝܟܘܢ ܘܡܬܒܢܝܢ ܐܢܬܘܢ ܒܗ ܘܡܬܩܝܡܝܢ ܐܢܬܘܢ ܒܗܝܡܢܘܬܐ ܗܝ ܕܝܠܦܬܘܢ ܕܒܗ ܬܬܝܬܪܘܢ ܒܬܘܕܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 8, 'ܐܙܕܗܪܘ ܕܠܡܐ ܐܢܫ ܢܚܠܨܟܘܢ ܒܦܝܠܤܦܘܬܐ ܘܒܛܥܝܘܬܐ ܤܪܝܩܬܐ ܐܝܟ ܝܘܠܦܢܐ ܕܒܢܝܢܫܐ ܘܐܝܟ ܐܤܛܘܟܤܘܗܝ ܕܥܠܡܐ ܘܠܐ ܐܝܟ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 9, 'ܕܒܗ ܥܡܪ ܟܠܗ ܡܘܠܝܐ ܕܐܠܗܘܬܐ ܓܘܫܡܢܐܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 10, 'ܘܒܗ ܐܦ ܐܢܬܘܢ ܡܫܬܡܠܝܬܘܢ ܕܗܘ ܐܝܬܘܗܝ ܪܫܐ ܕܟܠܗܝܢ ܐܪܟܘܤ ܘܫܘܠܛܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 11, 'ܘܒܗ ܐܬܓܙܪܬܘܢ ܓܙܘܪܬܐ ܕܠܐ ܒܐܝܕܝܢ ܒܫܠܚ ܒܤܪܐ ܕܚܛܗܐ ܒܓܙܘܪܬܗ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 12, 'ܘܐܬܩܒܪܬܘܢ ܥܡܗ ܒܡܥܡܘܕܝܬܐ ܘܒܗ ܩܡܬܘܢ ܥܡܗ ܕܗܝܡܢܬܘܢ ܒܚܝܠܐ ܕܐܠܗܐ ܕܐܩܝܡܗ ܡܢ ܒܝܬ ܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 13, 'ܘܠܟܘܢ ܕܡܝܬܝܢ ܗܘܝܬܘܢ ܒܚܛܗܝܟܘܢ ܘܒܥܘܪܠܘܬ ܒܤܪܟܘܢ ܐܚܝܟܘܢ ܥܡܗ ܘܫܒܩ ܠܢ ܟܠܗܘܢ ܚܛܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 14, 'ܘܥܛܐ ܒܦܘܩܕܢܘܗܝ ܫܛܪ ܚܘܒܝܢ ܗܘ ܕܐܝܬܘܗܝ ܗܘܐ ܤܩܘܒܠܢ ܘܫܩܠܗ ܡܢ ܡܨܥܬܐ ܘܩܒܥܗ ܒܙܩܝܦܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 15, 'ܘܒܫܠܚ ܦܓܪܗ ܦܪܤܝ ܠܐܪܟܘܤ ܘܠܫܠܝܛܢܐ ܘܐܒܗܬ ܐܢܘܢ ܓܠܝܐܝܬ ܒܩܢܘܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 16, 'ܠܐ ܗܟܝܠ ܐܢܫ ܢܕܘܕܟܘܢ ܒܡܐܟܠܐ ܘܒܡܫܬܝܐ ܐܘ ܒܦܘܠܓܐ ܕܥܐܕܐ ܘܕܪܝܫ ܝܪܚܐ ܘܕܫܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 17, 'ܗܠܝܢ ܕܐܝܬܝܗܝܢ ܛܠܢܝܬܐ ܕܐܝܠܝܢ ܕܥܬܝܕܢ ܦܓܪܐ ܕܝܢ ܡܫܝܚܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 18, 'ܘܠܡܐ ܐܢܫ ܢܨܒܐ ܒܡܟܝܟܘܬ ܪܥܝܢܐ ܠܡܚܝܒܘܬܟܘܢ ܕܬܫܬܥܒܕܘܢ ܠܦܘܠܚܢܐ ܕܡܠܐܟܐ ܒܕܤܥܐ ܥܠ ܡܕܡ ܕܠܐ ܚܙܐ ܘܤܪܝܩܐܝܬ ܡܬܚܬܪ ܒܪܥܝܢܐ ܕܒܤܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 19, 'ܘܠܐ ܐܚܕ ܪܫܐ ܕܡܢܗ ܟܠܗ ܦܓܪܐ ܡܬܪܟܒ ܘܡܬܩܝܡ ܒܫܪܝܢܐ ܘܒܗܕܡܐ ܘܪܒܐ ܬܪܒܝܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 20, 'ܐܢ ܓܝܪ ܡܝܬܬܘܢ ܥܡ ܡܫܝܚܐ ܡܢ ܐܤܛܘܟܤܘܗܝ ܕܥܠܡܐ ܠܡܢܐ ܐܝܟ ܗܘ ܕܚܝܝܢ ܐܢܬܘܢ ܒܥܠܡܐ ܡܬܕܝܢܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 21, 'ܠܐ ܠܡ ܬܩܪܘܒ ܘܠܐ ܬܛܥܡ ܘܠܐ ܬܩܦ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 22, 'ܕܐܝܬܝܗܝܢ ܗܠܝܢ ܚܫܚܬܐ ܕܡܬܚܒܠܐ ܘܦܘܩܕܐ ܐܢܝܢ ܘܝܘܠܦܢܐ ܕܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 23, 'ܘܡܬܚܙܝܢ ܕܐܝܬ ܒܗܝܢ ܡܠܬܐ ܕܚܟܡܬܐ ܒܦܪܨܘܦ ܡܟܝܟܘܬܐ ܘܕܚܠܬ ܐܠܗܐ ܘܕܠܐ ܚܝܤܝܢ ܥܠ ܦܓܪܐ ܠܘ ܒܡܕܡ ܕܡܝܩܪ ܐܠܐ ܒܐܝܠܝܢ ܕܚܫܚܬܐ ܐܢܝܢ ܕܒܤܪܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 1, 'ܐܢ ܗܟܝܠ ܩܡܬܘܢ ܥܡ ܡܫܝܚܐ ܕܠܥܠ ܒܥܘ ܐܬܪ ܕܡܫܝܚܐ ܝܬܒ ܥܠ ܝܡܝܢܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 2, 'ܕܠܥܠ ܐܬܪܥܘ ܘܠܐ ܕܒܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 3, 'ܡܝܬܬܘܢ ܠܟܘܢ ܓܝܪ ܘܚܝܝܟܘܢ ܟܤܝܢ ܥܡ ܡܫܝܚܐ ܒܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 4, 'ܘܐܡܬܝ ܕܡܫܝܚܐ ܡܬܓܠܐ ܕܗܘܝܘ ܚܝܝܢ ܗܝܕܝܢ ܐܦ ܐܢܬܘܢ ܬܬܓܠܘܢ ܥܡܗ ܒܫܘܒܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 5, 'ܐܡܝܬܘ ܗܟܝܠ ܗܕܡܝܟܘܢ ܕܒܐܪܥܐ ܙܢܝܘܬܐ ܘܛܢܦܘܬܐ ܘܟܐܒܐ ܘܪܓܬܐ ܒܝܫܬܐ ܘܥܠܘܒܘܬܐ ܕܗܝ ܗܝ ܕܚܠܬ ܦܬܟܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 6, 'ܡܛܠ ܗܠܝܢ ܓܝܪ ܐܬܐ ܪܘܓܙܗ ܕܐܠܗܐ ܥܠ ܒܢܝܗ ܕܠܐ ܡܬܛܦܝܤܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 7, 'ܘܒܗܠܝܢ ܐܦ ܐܢܬܘܢ ܗܠܟܬܘܢ ܡܢ ܩܕܝܡ ܟܕ ܡܬܗܦܟܝܢ ܗܘܝܬܘܢ ܒܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 8, 'ܗܫܐ ܕܝܢ ܐܢܝܚܘ ܡܢܟܘܢ ܟܠܗܝܢ ܗܠܝܢ ܪܘܓܙܐ ܚܡܬܐ ܒܝܫܘܬܐ ܓܘܕܦܐ ܡܡܠܠܐ ܛܢܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 9, 'ܘܠܐ ܬܗܘܘܢ ܡܕܓܠܝܢ ܚܕ ܒܚܕ ܐܠܐ ܫܘܠܚܘܗܝ ܠܒܪܢܫܐ ܥܬܝܩܐ ܥܡ ܟܠܗܘܢ ܗܘܦܟܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 10, 'ܘܠܒܫܘ ܚܕܬܐ ܕܡܬܚܕܬ ܒܝܕܥܬܐ ܒܕܡܘܬܐ ܕܒܪܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 11, 'ܟܪ ܕܠܝܬ ܝܗܘܕܝܐ ܘܐܪܡܝܐ ܘܠܐ ܓܙܘܪܬܐ ܘܥܘܪܠܘܬܐ ܘܠܐ ܝܘܢܝܐ ܘܒܪܒܪܝܐ ܘܠܐ ܥܒܕܐ ܘܒܪܚܐܪܐ ܐܠܐ ܟܠ ܘܒܟܠܢܫ ܡܫܝܚܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 12, 'ܠܒܫܘ ܗܟܝܠ ܐܝܟ ܓܒܝܐ ܕܐܠܗܐ ܩܕܝܫܐ ܘܚܒܝܒܐ ܪܚܡܐ ܘܪܘܚܦܐ ܘܒܤܝܡܘܬܐ ܘܡܟܝܟܘܬ ܪܥܝܢܐ ܘܢܝܚܘܬܐ ܘܢܓܝܪܘܬ ܪܘܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 13, 'ܘܗܘܝܬܘܢ ܡܤܝܒܪܝܢ ܚܕ ܠܚܕ ܘܫܒܩܝܢ ܚܕ ܠܚܕ ܘܐܢ ܐܝܬ ܠܐܢܫ ܥܠ ܚܒܪܗ ܪܘܥܡܐ ܐܝܟܢܐ ܕܡܫܝܚܐ ܫܒܩ ܠܟܘܢ ܗܟܢܐ ܐܦ ܐܢܬܘܢ ܫܒܘܩܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 14, 'ܘܥܡ ܗܠܝܢ ܟܠܗܝܢ ܚܘܒܐ ܕܗܘܝܘ ܚܙܩܐ ܕܓܡܝܪܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 15, 'ܘܫܠܡܗ ܕܡܫܝܚܐ ܢܕܒܪ ܠܒܘܬܟܘܢ ܕܠܗ ܐܬܩܪܝܬܘܢ ܒܚܕ ܦܓܪ ܘܗܘܝܬܘܢ ܡܘܕܝܢ ܠܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 16, 'ܕܡܠܬܗ ܬܥܡܪ ܒܟܘܢ ܥܬܝܪܐܝܬ ܒܟܠ ܚܟܡܐ ܘܗܘܝܬܘܢ ܡܠܦܝܢ ܘܪܕܝܢ ܢܦܫܟܘܢ ܒܡܙܡܘܪܐ ܘܒܬܫܒܚܬܐ ܘܒܙܡܝܪܬܐ ܕܪܘܚܐ ܘܒܛܝܒܘܬܐ ܗܘܝܬܘܢ ܙܡܪܝܢ ܒܠܒܘܬܟܘܢ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 17, 'ܘܟܠ ܡܕܡ ܕܤܥܪܝܢ ܐܢܬܘܢ ܒܡܠܬܐ ܘܒܥܒܕܐ ܒܫܡܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܗܘܝܬܘܢ ܥܒܕܝܢ ܘܗܘܝܬܘܢ ܡܘܕܝܢ ܒܐܝܕܗ ܠܐܠܗܐ ܐܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 18, 'ܢܫܐ ܐܫܬܥܒܕܝܢ ܠܒܥܠܝܟܝܢ ܐܝܟ ܕܙܕܩ ܒܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 19, 'ܓܒܪܐ ܐܚܒܘ ܢܫܝܟܘܢ ܘܠܐ ܬܗܘܘܢ ܡܪܝܪܝܢ ܥܠܝܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 20, 'ܒܢܝܐ ܐܫܬܡܥܘ ܠܐܒܗܝܟܘܢ ܒܟܠܡܕܡ ܗܟܢܐ ܓܝܪ ܫܦܝܪ ܩܕܡ ܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 21, 'ܐܒܗܐ ܠܐ ܬܪܓܙܘܢ ܒܢܝܟܘܢ ܕܠܐ ܢܬܬܥܝܩܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 22, 'ܥܒܕܐ ܐܫܬܡܥܘ ܒܟܠܡܕܡ ܠܡܪܝܟܘܢ ܕܦܓܪܐ ܠܐ ܒܡܚܙܐ ܥܝܢܐ ܐܝܟ ܗܢܘܢ ܕܫܦܪܝܢ ܠܒܢܝܢܫܐ ܐܠܐ ܒܠܒܐ ܦܫܝܛܐ ܘܒܕܚܠܬܗ ܕܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 23, 'ܘܟܠ ܕܥܒܕܝܢ ܐܢܬܘܢ ܡܢ ܟܠܗ ܢܦܫܟܘܢ ܥܒܕܘ ܐܝܟ ܕܠܡܪܢ ܘܠܐ ܐܝܟ ܕܠܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 24, 'ܘܕܥܘ ܕܡܢ ܡܪܢ ܡܩܒܠܝܬܘܢ ܦܘܪܥܢܐ ܒܝܪܬܘܬܐ ܠܡܪܝܐ ܓܝܪ ܡܫܝܚܐ ܦܠܚܝܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 25, 'ܡܤܟܠܢܐ ܕܝܢ ܡܬܦܪܥ ܐܝܟ ܡܕܡ ܕܐܤܟܠ ܘܠܝܬ ܡܤܒ ܒܐܦܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 1, 'ܡܪܝܐ ܥܒܕܘ ܫܘܝܘܬܐ ܘܟܐܢܘܬܐ ܠܘܬ ܥܒܕܝܟܘܢ ܘܗܘܝܬܘܢ ܝܕܥܝܢ ܕܐܦ ܠܟܘܢ ܐܝܬ ܗܘ ܡܪܐ ܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 2, 'ܒܨܠܘܬܐ ܐܬܐܡܢܘ ܘܗܘܝܬܘܢ ܥܝܪܝܢ ܐܢܬܘܢ ܒܗ ܘܡܘܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 3, 'ܘܡܨܠܝܢ ܐܦ ܥܠܝܢ ܕܐܠܗܐ ܢܦܬܚ ܠܢ ܬܪܥܐ ܕܡܠܬܐ ܠܡܡܠܠܘ ܐܪܙܐ ܕܡܫܝܚܐ ܕܡܛܠܬܗ ܐܤܝܪ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 4, 'ܕܐܓܠܝܘܗܝ ܘܐܡܠܠܝܘܗܝ ܐܝܟܢܐ ܕܘܠܐ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 5, 'ܒܚܟܡܬܐ ܗܠܟܘ ܠܘܬ ܒܪܝܐ ܘܙܒܢܘ ܩܐܪܤܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 6, 'ܘܡܠܬܟܘܢ ܒܟܠܙܒܢ ܒܛܝܒܘܬܐ ܐܝܟ ܕܒܡܠܚܐ ܬܗܘܐ ܡܡܕܟܐ ܘܗܘܝܬܘܢ ܝܕܥܝܢ ܠܐܢܫ ܐܢܫ ܐܝܟܢܐ ܘܠܐ ܠܟܘܢ ܠܡܬܒܘ ܦܬܓܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 7, 'ܡܕܡ ܕܠܘܬܝ ܕܝܢ ܢܘܕܥܟܘܢ ܛܘܟܝܩܘܤ ܐܚܐ ܚܒܝܒܐ ܘܡܫܡܫܢܐ ܡܗܝܡܢܐ ܘܟܢܬܢ ܒܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 8, 'ܗܢܐ ܕܫܕܪܬ ܠܘܬܟܘܢ ܥܠܝܗ ܥܠ ܗܕܐ ܕܢܕܥ ܡܐ ܕܠܘܬܟܘܢ ܘܢܒܝܐ ܠܒܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 9, 'ܥܡ ܐܢܤܝܡܘܤ ܐܚܐ ܡܗܝܡܢܐ ܘܚܒܝܒܐ ܗܘ ܕܐܝܬܘܗܝ ܡܢܟܘܢ ܗܢܘܢ ܢܘܕܥܘܢܟܘܢ ܡܕܡ ܕܠܘܬܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 10, 'ܫܐܠ ܒܫܠܡܟܘܢ ܐܪܤܛܪܟܘܤ ܫܒܝܐ ܕܥܡܝ ܘܡܪܩܘܤ ܒܪ ܕܕܗ ܕܒܪܢܒܐ ܗܘ ܕܐܬܦܩܕܬܘܢ ܥܠܘܗܝ ܕܐܢ ܐܬܐ ܠܘܬܟܘܢ ܬܩܒܠܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 11, 'ܘܝܫܘܥ ܗܘ ܕܡܬܩܪܐ ܝܘܤܛܘܤ ܗܠܝܢ ܕܐܝܬܝܗܘܢ ܡܢ ܓܙܘܪܬܐ ܘܗܢܘܢ ܒܠܚܘܕ ܥܕܪܘܢܝ ܒܡܠܟܘܬܗ ܕܐܠܗܐ ܘܗܢܘܢ ܗܘܘ ܠܝ ܒܘܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 12, 'ܫܐܠ ܒܫܠܡܟܘܢ ܐܦܦܪܐ ܗܘ ܕܡܢܟܘܢ ܥܒܕܐ ܕܡܫܝܚܐ ܟܕ ܒܟܠܙܒܢ ܥܡܠ ܚܠܦܝܟܘܢ ܒܨܠܘܬܐ ܕܬܩܘܡܘܢ ܓܡܝܪܐ ܘܡܫܡܠܝܐ ܒܟܠܗ ܨܒܝܢܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 13, 'ܤܗܕ ܐܢܐ ܓܝܪ ܥܠܘܗܝ ܕܛܢܢܐ ܤܓܝܐܐ ܐܝܬ ܠܗ ܚܠܦܝܟܘܢ ܘܚܠܦ ܗܢܘܢ ܕܒܠܕܝܩܝܐ ܘܕܒܐܝܪܦܘܠܝܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 14, 'ܫܐܠ ܒܫܠܡܟܘܢ ܠܘܩܐ ܐܤܝܐ ܚܒܝܒܢ ܘܕܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 15, 'ܫܐܠܘ ܒܫܠܡܐ ܕܐܚܐ ܕܒܠܕܝܩܝܐ ܘܕܢܘܡܦܐ ܘܕܥܕܬܐ ܕܒܒܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 16, 'ܘܡܐ ܕܐܬܩܪܝܬ ܐܓܪܬܐ ܗܕܐ ܠܘܬܟܘܢ ܥܒܕܘ ܕܐܦ ܒܥܕܬܐ ܕܠܕܝܩܝܐ ܬܬܩܪܐ ܘܗܝ ܕܐܬܟܬܒܬ ܡܢ ܠܕܝܩܝܐ ܩܪܐܘܗ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 17, 'ܘܐܡܪܘ ܠܐܪܟܝܦܘܤ ܕܐܙܕܗܪ ܒܬܫܡܫܬܐ ܗܝ ܕܩܒܠܬ ܒܡܪܢ ܕܬܗܘܐ ܡܫܡܠܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 18, 'ܫܠܡܐ ܗܢܐ ܒܐܝܕܐ ܕܝܠܝ ܕܦܘܠܘܤ ܗܘܝܬܘܢ ܥܗܕܝܢ ܠܐܤܘܪܝ ܛܝܒܘܬܐ ܥܡܟܘܢ ܐܡܝܢ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 1, 'ܦܘܠܘܤ ܘܤܠܘܢܘܤ ܘܛܝܡܬܐܤ ܠܥܕܬܐ ܕܬܤܠܘܢܝܩܝܐ ܕܒܐܠܗܐ ܐܒܐ ܘܒܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܛܝܒܘܬܐ ܥܡܟܘܢ ܘܫܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 2, 'ܡܘܕܝܢܢ ܠܐܠܗܐ ܒܟܠܙܒܢ ܥܠ ܟܠܟܘܢ ܘܡܬܕܟܪܝܢܢ ܠܟܘܢ ܒܨܠܘܬܢ ܐܡܝܢܐܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 3, 'ܘܥܗܕܝܢܢ ܩܕܡ ܐܠܗܐ ܐܒܐ ܥܒܕܐ ܕܗܝܡܢܘܬܟܘܢ ܘܥܡܠܐ ܕܚܘܒܟܘܢ ܘܡܤܝܒܪܢܘܬܐ ܕܤܒܪܟܘܢ ܕܒܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 4, 'ܝܕܥܝܢܢ ܓܝܪ ܓܒܝܘܬܟܘܢ ܐܚܝ ܚܒܝܒܘܗܝ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 5, 'ܡܛܠ ܕܡܤܒܪܢܘܬܢ ܠܐ ܗܘܐ ܒܡܠܐ ܒܠܚܘܕ ܗܘܬ ܠܘܬܟܘܢ ܐܠܐ ܐܦ ܒܚܝܠܐ ܘܒܪܘܚܐ ܕܩܘܕܫܐ ܘܒܦܝܤܐ ܫܪܝܪܐ ܐܦ ܐܢܬܘܢ ܝܕܥܝܢ ܐܢܬܘܢ ܐܝܟܢܐ ܗܘܝܢ ܒܝܢܬܟܘܢ ܡܛܠܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 6, 'ܘܐܢܬܘܢ ܒܢ ܐܬܕܡܝܬܘܢ ܘܒܡܪܢ ܕܩܒܠܬܘܢ ܡܠܬܐ ܒܐܘܠܨܢܐ ܪܒܐ ܘܒܚܕܘܬܐ ܕܪܘܚܐ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 7, 'ܘܗܘܝܬܘܢ ܕܡܘܬܐ ܠܟܠܗܘܢ ܡܗܝܡܢܐ ܕܐܝܬ ܒܡܩܕܘܢܝܐ ܘܒܐܟܐܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 8, 'ܡܢܟܘܢ ܓܝܪ ܐܫܬܡܥܬ ܡܠܬܗ ܕܡܪܢ ܠܐ ܒܠܚܘܕ ܒܡܩܕܘܢܝܐ ܘܒܐܟܐܝܐ ܐܠܐ ܒܟܠ ܐܬܪ ܗܝܡܢܘܬܟܘܢ ܕܒܐܠܗܐ ܐܫܬܡܥܬ ܐܝܟܢܐ ܕܠܐ ܢܤܬܢܩ ܕܢܐܡܪ ܥܠܝܟܘܢ ܡܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 9, 'ܗܢܘܢ ܓܝܪ ܡܫܬܥܝܢ ܐܝܢܐ ܡܥܠܢܐ ܗܘܐ ܠܢ ܠܘܬܟܘܢ ܘܐܝܟܢܐ ܐܬܦܢܝܬܘܢ ܠܘܬ ܐܠܗܐ ܡܢ ܕܚܠܬ ܦܬܟܪܐ ܕܬܦܠܚܘܢ ܠܐܠܗܐ ܚܝܐ ܘܫܪܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 10, 'ܟܕ ܡܤܟܝܬܘܢ ܠܒܪܗ ܡܢ ܫܡܝܐ ܠܝܫܘܥ ܗܘ ܕܐܩܝܡ ܡܢ ܒܝܬ ܡܝܬܐ ܕܗܘ ܡܦܨܐ ܠܢ ܡܢ ܪܘܓܙܐ ܕܐܬܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 1, 'ܘܐܢܬܘܢ ܝܕܥܝܢ ܐܢܬܘܢ ܐܚܝ ܕܡܥܠܢܢ ܕܠܘܬܟܘܢ ܠܐ ܗܘܐ ܤܪܝܩܐܝܬ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 2, 'ܐܠܐ ܠܘܩܕܡ ܚܫܢܢ ܘܐܨܛܥܪܢܢ ܐܝܟ ܕܝܕܥܝܬܘܢ ܒܦܝܠܝܦܘܤ ܘܗܝܕܝܢ ܒܐܓܘܢܐ ܪܒܐ ܡܠܠܢ ܥܡܟܘܢ ܒܦܪܗܤܝܐ ܕܐܠܗܢ ܤܒܪܬܗ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 3, 'ܒܘܝܐܢ ܓܝܪ ܠܐ ܗܘܐ ܡܢ ܛܥܝܘܬܐ ܐܦܠܐ ܡܢ ܛܢܦܘܬܐ ܐܦܠܐ ܒܢܟܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 4, 'ܐܠܐ ܐܝܟ ܡܐ ܕܒܩܝܢܢ ܠܐܠܗܐ ܕܬܬܗܝܡܢ ܤܒܪܬܗ ܗܟܢܐ ܡܡܠܠܝܢܢ ܠܐ ܐܝܟ ܕܠܒܢܝ ܐܢܫܐ ܢܫܦܪ ܐܠܐ ܠܐܠܗܐ ܗܘ ܕܒܩܐ ܠܒܘܬܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 5, 'ܐܦܠܐ ܓܝܪ ܡܡܬܘܡ ܐܬܚܫܚܢ ܒܡܡܠܠܐ ܫܕܠܐ ܐܝܟ ܕܝܕܥܝܢ ܐܢܬܘܢ ܘܠܐ ܒܥܠܬܐ ܕܝܥܢܘܬܐ ܐܠܗܐ ܤܗܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 6, 'ܘܠܐ ܒܥܝܢ ܫܘܒܚܐ ܡܢ ܒܢܝܢܫܐ ܠܐ ܡܢܟܘܢ ܘܠܐ ܡܢ ܐܚܪܢܐ ܟܕ ܡܫܟܚܝܢ ܗܘܝܢ ܝܩܝܪܐ ܠܡܗܘܐ ܐܝܟ ܫܠܝܚܐ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 7, 'ܐܠܐ ܗܘܝܢ ܒܝܢܬܟܘܢ ܡܟܝܟܐ ܘܐܝܟ ܡܪܒܝܢܝܬܐ ܕܡܚܒܒܐ ܒܢܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 8, 'ܗܟܢܐ ܐܦ ܚܢܢ ܡܚܒܒܝܢܢ ܘܡܤܘܚܝܢܢ ܠܡܬܠ ܠܟܘܢ ܠܐ ܒܠܚܘܕ ܤܒܪܬܗ ܕܐܠܗܐ ܐܠܐ ܐܦ ܢܦܫܢ ܡܛܠ ܕܚܒܝܒܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 9, 'ܥܗܕܝܢ ܐܢܬܘܢ ܓܝܪ ܐܚܝܢ ܕܠܐܝܢ ܗܘܝܢ ܘܥܡܠܝܢ ܒܥܒܕ ܐܝܕܝܢ ܒܠܠܝܐ ܘܒܐܝܡܡܐ ܕܥܠ ܚܕ ܡܢܟܘܢ ܠܐ ܢܐܩܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 10, 'ܐܢܬܘܢ ܤܗܕܝܢ ܘܐܠܗܐ ܐܝܟܢܐ ܐܟܪܙܢ ܠܟܘܢ ܤܒܪܬܗ ܕܐܠܗܐ ܕܟܝܐܝܬ ܘܟܐܢܐܝܬ ܘܕܠܐ ܪܫܝܢ ܗܘܝܢ ܠܘܬ ܟܠܗܘܢ ܡܗܝܡܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 11, 'ܐܝܟܢܐ ܕܝܕܥܝܢ ܐܢܬܘܢ ܕܡܢ ܚܕ ܚܕ ܡܢܟܘܢ ܐܝܟ ܐܒܐ ܡܢ ܒܢܘܗܝ ܒܥܝܢ ܗܘܝܢ ܘܡܠܝܢ ܗܘܝܢ ܒܠܒܟܘܢ ܘܡܤܗܕܝܢܢ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 12, 'ܕܬܗܠܟܘܢ ܐܝܟ ܕܝܐܐ ܠܐܠܗܐ ܗܘ ܕܩܪܟܘܢ ܠܡܠܟܘܬܗ ܘܠܫܘܒܚܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 13, 'ܡܛܠ ܗܢܐ ܐܦ ܚܢܢ ܡܘܕܝܢܢ ܐܡܝܢܐܝܬ ܠܐܠܗܐ ܕܡܠܬܐ ܕܐܠܗܐ ܕܩܒܠܬܘܢ ܡܢܢ ܠܐ ܗܘܐ ܐܝܟ ܡܠܬܐ ܕܒܢܝܢܫܐ ܩܒܠܬܘܢܗ ܐܠܐ ܐܝܟܢܐ ܕܐܝܬܝܗ ܫܪܝܪܐܝܬ ܡܠܬܐ ܕܐܠܗܐ ܘܗܝ ܒܥܒܕܐ ܡܤܬܥܪܐ ܒܟܘܢ ܒܐܝܠܝܢ ܕܡܗܝܡܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 14, 'ܐܢܬܘܢ ܕܝܢ ܐܚܝ ܐܬܕܡܝܬܘܢ ܠܥܕܬܐ ܕܐܠܗܐ ܕܒܝܗܘܕ ܗܠܝܢ ܕܒܝܫܘܥ ܡܫܝܚܐ ܕܗܟܢ ܤܝܒܪܬܘܢ ܐܦ ܐܢܬܘܢ ܡܢ ܒܢܝ ܫܪܒܬܟܘܢ ܐܝܟܢܐ ܕܐܦ ܗܢܘܢ ܡܢ ܝܗܘܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 15, 'ܗܢܘܢ ܕܠܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܩܛܠܘ ܘܠܢܒܝܐ ܕܡܢܗܘܢ ܘܠܢ ܪܕܦܘ ܘܠܐܠܗܐ ܠܐ ܫܦܪܝܢ ܘܥܒܝܕܝܢ ܤܩܘܒܠܐ ܠܟܠܗܘܢ ܒܢܝ ܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 16, 'ܕܟܠܝܢ ܠܢ ܕܢܡܠܠ ܥܡ ܥܡܡܐ ܕܢܚܘܢ ܠܡܫܠܡܘ ܚܛܗܝܗܘܢ ܒܟܠܙܒܢ ܡܛܝ ܕܝܢ ܥܠܝܗܘܢ ܪܘܓܙܐ ܥܕܡܐ ܠܚܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 17, 'ܚܢܢ ܕܝܢ ܐܚܝܢ ܗܘܝܢ ܝܬܡܐ ܡܢܟܘܢ ܙܒܢܐ ܕܫܥܬܐ ܒܐܦܝܢ ܘܠܐ ܒܠܒܢ ܘܝܬܝܪܐܝܬ ܐܬܚܦܛܢ ܠܡܚܙܐ ܐܦܝܟܘܢ ܒܚܘܒܐ ܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 18, 'ܘܨܒܝܢ ܕܢܐܬܐ ܠܘܬܟܘܢ ܐܢܐ ܦܘܠܘܤ ܚܕܐ ܙܒܢ ܘܬܪܬܝܢ ܘܥܘܟܢܝ ܤܛܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 19, 'ܐܝܢܘ ܓܝܪ ܤܒܪܢ ܘܚܕܘܬܢ ܘܟܠܝܠܐ ܕܫܘܒܗܪܢ ܐܠܐ ܐܢ ܐܢܬܘܢ ܩܕܡ ܡܪܢ ܝܫܘܥ ܒܡܐܬܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 20, 'ܐܢܬܘܢ ܐܢܘܢ ܓܝܪ ܬܫܒܘܚܬܢ ܘܚܕܘܬܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 1, 'ܘܡܛܠ ܕܠܐ ܚܡܤܢܢ ܨܒܝܢ ܠܡܦܫ ܒܐܬܢܘܤ ܒܠܚܘܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 2, 'ܘܢܫܕܪ ܠܘܬܟܘܢ ܠܛܝܡܬܐܘܤ ܐܚܘܢ ܘܡܫܡܫܢܐ ܕܐܠܗܐ ܘܡܥܕܪܢܢ ܒܤܒܪܬܗ ܕܡܫܝܚܐ ܕܢܫܪܪܟܘܢ ܘܢܒܥܐ ܡܢܟܘܢ ܥܠ ܗܝܡܢܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 3, 'ܕܠܐ ܐܢܫ ܡܢܟܘܢ ܬܬܩܛܥ ܠܗ ܒܐܘܠܨܢܐ ܗܠܝܢ ܐܢܬܘܢ ܓܝܪ ܝܕܥܝܢ ܐܢܬܘܢ ܕܠܗܕܐ ܗܘ ܤܝܡܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 4, 'ܐܦ ܟܕ ܠܘܬܟܘܢ ܗܘܝܢ ܓܝܪ ܩܕܡܢ ܐܡܪܢ ܠܟܘܢ ܕܥܬܝܕܝܢܢ ܠܡܬܐܠܨܘ ܐܝܟܢܐ ܕܝܕܥܝܢ ܐܢܬܘܢ ܕܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 5, 'ܡܛܠ ܗܢܐ ܐܦ ܐܢܐ ܠܐ ܚܡܤܢܬ ܥܕܡܐ ܕܫܕܪܬ ܕܐܕܥ ܗܝܡܢܘܬܟܘܢ ܕܕܠܡܐ ܢܢܤܝܟܘܢ ܡܢܤܝܢܐ ܘܢܗܘܐ ܥܡܠܢ ܤܪܝܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 6, 'ܗܫܐ ܕܝܢ ܡܢ ܕܐܬܐ ܠܘܬܢ ܛܝܡܬܐܘܤ ܡܢ ܨܐܕܝܟܘܢ ܘܤܒܪܢ ܥܠ ܗܝܡܢܘܬܟܘܢ ܘܥܠ ܚܘܒܟܘܢ ܘܕܐܝܬ ܠܘܬܟܘܢ ܥܘܗܕܢܢ ܛܒܐ ܒܟܠ ܥܕܢ ܘܤܘܝܢ ܐܢܬܘܢ ܠܡܚܙܝܢ ܐܝܟܢܐ ܕܐܦ ܚܢܢ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 7, 'ܡܛܠ ܗܢܐ ܐܬܒܝܐܢ ܒܟܘܢ ܐܚܝܢ ܥܠ ܟܠܗܝܢ ܥܩܬܢ ܘܐܘܠܨܢܝܢ ܡܛܠ ܗܝܡܢܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 8, 'ܘܗܫܐ ܗܘ ܚܝܝܢܢ ܐܢ ܐܢܬܘܢ ܬܬܩܝܡܘܢ ܒܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 9, 'ܐܝܕܐ ܓܝܪ ܬܘܕܝܬܐ ܡܫܟܚܝܢܢ ܠܡܦܪܥ ܚܠܦܝܟܘܢ ܠܐܠܗܐ ܥܠ ܟܠ ܚܕܘܬܐ ܕܚܕܝܢܢ ܡܛܠܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 10, 'ܐܠܐ ܕܩܕܡ ܐܠܗܐ ܝܬܝܪܐܝܬ ܢܬܟܫܦ ܒܠܠܝܐ ܘܒܐܝܡܡܐ ܕܢܚܙܐ ܐܦܝܟܘܢ ܘܢܓܡܘܪ ܡܐ ܕܚܤܝܪܐ ܗܝܡܢܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 11, 'ܗܘ ܕܝܢ ܐܠܗܐ ܐܒܘܢ ܘܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܢܬܪܘܨ ܐܘܪܚܢ ܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 12, 'ܘܢܤܓܐ ܘܢܝܬܪ ܚܘܒܟܘܢ ܕܚܕ ܠܘܬ ܚܕ ܘܕܠܘܬ ܟܠܢܫ ܐܝܟܢܐ ܕܚܢܢ ܡܚܒܝܢܢ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 13, 'ܘܢܩܝܡ ܠܒܘܬܟܘܢ ܕܠܐ ܪܫܝܢ ܒܩܕܝܫܘܬܐ ܩܕܡ ܐܠܗܐ ܐܒܘܢ ܒܡܐܬܝܬܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܕܥܡ ܟܠܗܘܢ ܩܕܝܫܘܗܝ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 1, 'ܡܟܝܠ ܐܚܝ ܒܥܝܢܢ ܡܢܟܘܢ ܘܡܬܟܫܦܝܢܢ ܠܟܘܢ ܒܡܪܢ ܝܫܘܥ ܕܐܝܟ ܕܩܒܠܬܘܢ ܡܢܢ ܐܝܟܢ ܘܠܐ ܠܟܘܢ ܕܬܗܠܟܘܢ ܘܬܫܦܪܘܢ ܠܐܠܗܐ ܕܝܬܝܪܐܝܬ ܬܘܤܦܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 2, 'ܝܕܥܝܢ ܐܢܬܘܢ ܓܝܪ ܐܝܠܝܢ ܦܘܩܕܢܐ ܝܗܒܢ ܠܟܘܢ ܒܡܪܢ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 3, 'ܗܢܘ ܓܝܪ ܨܒܝܢܗ ܕܐܠܗܐ ܩܕܝܫܘܬܟܘܢ ܘܕܬܗܘܘܢ ܦܪܝܩܝܢ ܡܢ ܟܠ ܙܢܝܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 4, 'ܘܢܗܘܐ ܝܕܥ ܐܢܫ ܐܢܫ ܡܢܟܘܢ ܠܡܩܢܐ ܡܐܢܗ ܒܩܕܝܫܘܬܐ ܘܒܐܝܩܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 5, 'ܘܠܐ ܒܚܫܐ ܕܪܓܬܐ ܐܝܟ ܫܪܟܐ ܕܥܡܡܐ ܗܢܘܢ ܕܠܐ ܝܕܥܝܢ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 6, 'ܘܠܐ ܬܗܘܘܢ ܡܫܝܚܝܢ ܠܡܥܒܪ ܘܠܡܥܠܒ ܐܢܫ ܠܐܚܘܗܝ ܒܗܕܐ ܨܒܘܬܐ ܡܛܠ ܕܡܪܢ ܗܘ ܬܒܘܥܐ ܥܠ ܗܠܝܢ ܟܠܗܝܢ ܐܝܟܢܐ ܕܐܦ ܡܢ ܩܕܡ ܙܒܢܐ ܐܡܪܢ ܠܟܘܢ ܘܤܗܕܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 7, 'ܠܐ ܓܝܪ ܩܪܟܘܢ ܐܠܗܐ ܠܛܢܦܘܬܐ ܐܠܐ ܠܩܕܝܫܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 8, 'ܡܟܝܠ ܡܢ ܕܛܠܡ ܠܐ ܗܘܐ ܠܒܪܢܫܐ ܛܠܡ ܐܠܐ ܠܐܠܗܐ ܗܘ ܕܝܗܒ ܒܟܘܢ ܪܘܚܗ ܩܕܝܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 9, 'ܥܠ ܚܘܒܐ ܕܝܢ ܕܐܚܐ ܠܐ ܤܢܝܩܝܬܘܢ ܠܡܟܬܒ ܠܟܘܢ ܐܢܬܘܢ ܓܝܪ ܩܢܘܡܟܘܢ ܡܠܦܐ ܐܢܬܘܢ ܕܐܠܗܐ ܕܬܚܒܘܢ ܚܕ ܠܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 10, 'ܐܦ ܥܒܕܝܢ ܐܢܬܘܢ ܗܟܢܐ ܠܟܠܗܘܢ ܐܚܐ ܕܒܟܠܗ ܡܩܕܘܢܝܐ ܒܥܐ ܐܢܐ ܕܝܢ ܡܢܟܘܢ ܐܚܝ ܕܬܬܝܬܪܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 11, 'ܘܬܬܚܦܛܘܢ ܕܬܗܘܘܢ ܫܠܝܢ ܘܥܢܝܢ ܒܤܘܥܪܢܝܟܘܢ ܘܗܘܝܬܘܢ ܦܠܚܝܢ ܒܐܝܕܝܟܘܢ ܐܝܟܢܐ ܕܦܩܕܢܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 12, 'ܕܬܗܘܘܢ ܡܗܠܟܝܢ ܒܐܤܟܡܐ ܠܘܬ ܒܪܝܐ ܘܥܠ ܐܢܫ ܠܐ ܬܤܬܢܩܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 13, 'ܨܒܐ ܐܢܐ ܕܝܢ ܕܬܕܥܘܢ ܐܚܝ ܕܥܠ ܐܝܠܝܢ ܕܕܡܟܝܢ ܠܐ ܬܗܘܐ ܟܪܝܐ ܠܟܘܢ ܐܝܟ ܫܪܟܐ ܕܐܢܫܐ ܕܤܒܪܐ ܠܝܬ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 14, 'ܐܢ ܓܝܪ ܡܗܝܡܢܝܢܢ ܕܝܫܘܥ ܡܝܬ ܘܩܡ ܗܟܢܐ ܐܦ ܐܠܗܐ ܠܐܝܠܝܢ ܕܕܡܟܘ ܒܝܫܘܥ ܡܝܬܐ ܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 15, 'ܗܕܐ ܕܝܢ ܐܡܪܝܢܢ ܠܟܘܢ ܒܡܠܬܗ ܕܡܪܢ ܕܚܢܢ ܐܝܠܝܢ ܕܡܫܬܚܪܝܢܢ ܒܡܐܬܝܬܗ ܕܡܪܢ ܕܚܝܝܢܢ ܠܐ ܢܕܪܟ ܠܐܝܠܝܢ ܕܕܡܟܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 16, 'ܡܛܠ ܕܗܘ ܡܪܢ ܒܦܘܩܕܢܐ ܘܒܩܠܐ ܕܪܝܫ ܡܠܐܟܐ ܘܒܩܪܢܐ ܕܐܠܗܐ ܢܚܬ ܡܢ ܫܡܝܐ ܘܡܝܬܐ ܕܒܡܫܝܚܐ ܢܩܘܡܘܢ ܠܘܩܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 17, 'ܘܗܝܕܝܢ ܚܢܢ ܐܝܠܝܢ ܕܡܫܬܚܪܝܢܢ ܕܚܝܝܢܢ ܢܬܚܛܦ ܥܡܗܘܢ ܐܟܚܕܐ ܒܥܢܢܐ ܠܐܘܪܥܗ ܕܡܪܢ ܒܐܐܪ ܘܗܟܢܐ ܒܟܠܙܒܢ ܥܡ ܡܪܢ ܢܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 18, 'ܗܘܘ ܗܟܝܠ ܡܒܝܐܝܢ ܚܕ ܠܚܕ ܒܡܠܐ ܗܠܝܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 1, 'ܥܠ ܙܒܢܐ ܕܝܢ ܘܥܕܢܐ ܐܚܝ ܠܐ ܤܢܝܩܝܬܘܢ ܠܡܟܬܒ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 2, 'ܐܢܬܘܢ ܓܝܪ ܫܪܝܪܐܝܬ ܝܕܥܝܢ ܐܢܬܘܢ ܕܝܘܡܗ ܕܡܪܢ ܐܝܟ ܓܢܒܐ ܒܠܠܝܐ ܗܟܢܐ ܐܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 3, 'ܟܕ ܢܐܡܪܘܢ ܕܫܠܡܐ ܗܘ ܘܫܝܢܐ ܘܗܝܕܝܢ ܡܢܫܠܝܐ ܢܩܘܡ ܥܠܝܗܘܢ ܐܒܕܢܐ ܐܝܟ ܚܒܠܐ ܥܠ ܒܛܢܬܐ ܘܠܐ ܢܡܕܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 4, 'ܐܢܬܘܢ ܕܝܢ ܐܚܝ ܠܐ ܗܘܝܬܘܢ ܒܚܫܘܟܐ ܕܗܘ ܝܘܡܐ ܐܝܟ ܓܢܒܐ ܢܕܪܟܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 5, 'ܟܠܟܘܢ ܓܝܪ ܒܢܝ ܢܘܗܪܐ ܐܢܬܘܢ ܘܒܢܝ ܐܝܡܡܐ ܘܠܐ ܗܘܝܬܘܢ ܒܢܝ ܠܠܝܐ ܘܠܐ ܒܢܝ ܚܫܘܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 6, 'ܠܐ ܢܕܡܟ ܗܟܝܠ ܐܝܟ ܫܪܟܐ ܐܠܐ ܗܘܝܢ ܥܝܪܝܢ ܘܡܗܘܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 7, 'ܐܝܠܝܢ ܓܝܪ ܕܕܡܟܝܢ ܒܠܠܝܐ ܗܘ ܕܡܟܝܢ ܘܐܝܠܝܢ ܕܪܘܝܢ ܒܠܠܝܐ ܗܘ ܪܘܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 8, 'ܚܢܢ ܕܝܢ ܕܒܢܝ ܐܝܡܡܐ ܚܢܢ ܗܘܝܢ ܥܝܪܝܢ ܒܪܥܝܢܢ ܘܠܒܝܫܝܢ ܫܪܝܢܐ ܕܗܝܡܢܘܬܐ ܘܕܚܘܒܐ ܘܢܤܝܡ ܤܢܘܪܬܐ ܕܤܒܪܐ ܕܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 9, 'ܡܛܠ ܕܠܐ ܤܡܢ ܐܠܗܐ ܠܪܘܓܙܐ ܐܠܐ ܠܩܢܝܢܐ ܕܚܝܐ ܒܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 10, 'ܗܘ ܕܡܝܬ ܥܠ ܐܦܝܢ ܕܐܢ ܥܝܪܝܢܢ ܘܐܢ ܕܡܟܝܢܢ ܐܟܚܕܐ ܥܡܗ ܢܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 11, 'ܡܛܠ ܗܢܐ ܒܝܐܘ ܚܕ ܠܚܕ ܘܒܢܘ ܚܕ ܠܚܕ ܐܝܟܢܐ ܕܐܦ ܥܒܕܝܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 12, 'ܒܥܝܢܢ ܕܝܢ ܡܢܟܘܢ ܐܚܝ ܕܗܘܝܬܘܢ ܝܕܥܝܢ ܠܐܝܠܝܢ ܕܠܐܝܢ ܒܟܘܢ ܘܩܝܡܝܢ ܒܐܦܝܟܘܢ ܒܡܪܢ ܘܡܠܦܝܢ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 13, 'ܕܗܘܘ ܚܫܝܒܝܢ ܠܟܘܢ ܒܚܘܒܐ ܝܬܝܪܐ ܘܡܛܠ ܥܒܕܗܘܢ ܐܫܬܝܢܘ ܥܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 14, 'ܒܥܝܢܢ ܕܝܢ ܡܢܟܘܢ ܐܚܝ ܪܕܘ ܠܡܤܟܠܢܐ ܘܠܒܒܘ ܠܙܥܘܪܝ ܢܦܫܐ ܘܤܒܘ ܛܥܢܐ ܕܡܚܝܠܐ ܘܐܓܪܘ ܪܘܚܟܘܢ ܠܘܬ ܟܠܢܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 15, 'ܘܐܙܕܗܪܘ ܕܠܡܐ ܐܢܫ ܡܢܟܘܢ ܒܝܫܬܐ ܚܠܦ ܒܝܫܬܐ ܢܦܪܘܥ ܐܠܐ ܒܟܠܙܒܢ ܗܪܛܘ ܒܬܪ ܛܒܬܐ ܠܘܬ ܚܕܕܐ ܘܠܘܬ ܟܠܢܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 16, 'ܗܘܘ ܚܕܝܢ ܒܟܠܙܒܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 17, 'ܘܡܨܠܝܢ ܕܠܐ ܫܠܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 18, 'ܘܒܟܠ ܡܕܡ ܗܘܘ ܡܘܕܝܢ ܗܢܘ ܓܝܪ ܨܒܝܢܗ ܕܐܠܗܐ ܒܝܫܘܥ ܡܫܝܚܐ ܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 19, 'ܪܘܚܐ ܠܐ ܬܕܥܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 20, 'ܢܒܝܘܬܐ ܠܐ ܬܤܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 21, 'ܟܠܡܕܡ ܒܩܘ ܘܕܫܦܝܪ ܐܚܘܕܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 22, 'ܘܡܢ ܟܠ ܨܒܘ ܒܝܫܐ ܥܪܘܩܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 23, 'ܗܘ ܕܝܢ ܐܠܗܐ ܕܫܠܡܐ ܢܩܕܫܟܘܢ ܓܡܝܪܐܝܬ ܠܟܠܟܘܢ ܘܟܠܗ ܪܘܚܟܘܢ ܘܢܦܫܟܘܢ ܘܦܓܪܟܘܢ ܢܢܛܪ ܕܠܐ ܪܫܝܢ ܠܡܐܬܝܬܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 24, 'ܡܗܝܡܢ ܗܘ ܡܢ ܕܩܪܟܘܢ ܕܗܘ ܢܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 25, 'ܐܚܝ ܨܠܘ ܥܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 26, 'ܫܐܠܘ ܒܫܠܡܐ ܕܐܚܝܢ ܟܠܗܘܢ ܒܢܘܫܩܬܐ ܩܕܝܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 27, 'ܡܘܡܐ ܐܢܐ ܠܟܘܢ ܒܡܪܢ ܕܬܬܩܪܐ ܐܓܪܬܐ ܗܕܐ ܠܟܠܗܘܢ ܐܚܐ ܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 28, 'ܛܝܒܘܬܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܥܡܟܘܢ ܐܡܝܢ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 1, 'ܦܘܠܘܤ ܘܤܠܘܢܘܤ ܘܛܝܡܬܐܘܤ ܠܥܕܬܐ ܕܬܤܠܘܢܝܩܝܐ ܕܒܐܠܗܐ ܐܒܘܢ ܘܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 2, 'ܛܝܒܘܬܐ ܥܡܟܘܢ ܘܫܠܡܐ ܡܢ ܐܠܗܐ ܐܒܘܢ ܘܡܢ ܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 3, 'ܠܡܘܕܝܘ ܠܐܠܗܐ ܒܟܠܙܒܢ ܚܝܒܝܢܢ ܥܠܝܟܘܢ ܐܚܝ ܐܝܟ ܡܐ ܕܘܠܐ ܕܝܬܝܪܐܝܬ ܪܒܝܐ ܗܝܡܢܘܬܟܘܢ ܘܤܓܐ ܚܘܒܐ ܕܟܠܟܘܢ ܕܟܠܢܫ ܠܘܬ ܚܒܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 4, 'ܐܝܟ ܕܐܦ ܚܢܢ ܒܟܘܢ ܢܗܘܐ ܡܫܬܒܗܪܝܢ ܚܢܢ ܒܥܕܬܐ ܕܐܠܗܐ ܥܠ ܗܝܡܢܘܬܟܘܢ ܘܥܠ ܡܤܝܒܪܢܘܬܟܘܢ ܕܒܟܠܗ ܪܕܝܦܘܬܟܘܢ ܘܐܘܠܨܢܝܟܘܢ ܕܡܤܝܒܪܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 5, 'ܠܬܚܘܝܬܐ ܕܕܝܢܐ ܟܐܢܐ ܕܐܠܗܐ ܕܬܫܘܘܢ ܠܡܠܟܘܬܗ ܗܝ ܕܥܠ ܐܦܝܗ ܚܫܝܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 6, 'ܘܐܢ ܟܐܢܐ ܩܕܡ ܐܠܗܐ ܕܢܦܪܘܥ ܐܘܠܨܢܐ ܠܐܠܘܨܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 7, 'ܘܠܟܘܢ ܕܡܬܐܠܨܝܬܘܢ ܢܚܐ ܥܡܢ ܒܓܠܝܢܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܕܡܢ ܫܡܝܐ ܥܡ ܚܝܠܐ ܕܡܠܐܟܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 8, 'ܡܐ ܕܥܒܕ ܬܒܥܬܐ ܒܓܘܙܠܐ ܕܢܘܪܐ ܡܢ ܗܢܘܢ ܕܠܐ ܝܕܥܘ ܠܐܠܗܐ ܘܡܢ ܗܢܘܢ ܕܠܐ ܐܫܬܘܕܥܘ ܠܤܒܪܬܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 9, 'ܕܗܢܘܢ ܒܕܝܢܐ ܢܬܦܪܥܘܢ ܐܒܕܢܐ ܕܠܥܠܡ ܡܢ ܦܪܨܘܦܗ ܕܡܪܢ ܘܡܢ ܫܘܒܚܐ ܕܚܝܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 10, 'ܡܐ ܕܐܬܐ ܕܢܫܬܒܚ ܒܩܕܝܫܘܗܝ ܘܢܚܘܐ ܬܕܡܪܬܗ ܒܡܗܝܡܢܘܗܝ ܕܬܬܗܝܡܢ ܤܗܕܘܬܢ ܕܥܠܝܟܘܢ ܒܗܘ ܝܘܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 11, 'ܡܛܠܗܢܐ ܒܟܠܙܒܢ ܡܨܠܝܢܢ ܥܠܝܟܘܢ ܕܢܫܘܝܟܘܢ ܐܠܗܐ ܠܩܪܝܢܟܘܢ ܘܢܡܠܝܟܘܢ ܟܠܗ ܨܒܝܢܐ ܕܛܒܬܐ ܘܥܒܕܐ ܕܗܝܡܢܘܬܐ ܒܚܝܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 12, 'ܕܢܫܬܒܚ ܒܟܘܢ ܫܡܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܐܦ ܐܢܬܘܢ ܒܗ ܐܝܟ ܛܝܒܘܬܗ ܕܐܠܗܢ ܘܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 1, 'ܒܥܝܢܢ ܕܝܢ ܡܢܟܘܢ ܐܚܝ ܥܠ ܡܐܬܝܬܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܘܥܠ ܟܢܘܫܝܐ ܕܝܠܢ ܕܠܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 2, 'ܕܠܐ ܥܓܠ ܬܬܙܝܥܘܢ ܒܪܥܝܢܝܟܘܢ ܘܠܐ ܬܬܘܗܘܢ ܠܐ ܡܢ ܡܠܬܐ ܘܠܐ ܡܢ ܪܘܚܐ ܘܠܐ ܡܢ ܐܓܪܬܐ ܕܐܝܟ ܕܡܢ ܠܘܬܢ ܗܝ ܕܗܐ ܠܡ ܡܛܝ ܝܘܡܗ ܕܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 3, 'ܠܡܐ ܐܢܫ ܢܛܥܝܟܘܢ ܒܚܕ ܡܢ ܐܤܟܡܝܢ ܡܛܠ ܕܐܢ ܠܐ ܬܐܬܐ ܠܘܩܕܡ ܡܪܘܕܘܬܐ ܘܢܬܓܠܐ ܒܪܢܫܐ ܕܚܛܝܬܐ ܒܪܗ ܕܐܒܕܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 4, 'ܗܘ ܕܠܩܘܒܠܐ ܗܘ ܘܡܫܬܥܠܐ ܥܠ ܟܠ ܡܢ ܕܡܬܩܪܐ ܐܠܗ ܘܕܚܠܐ ܐܝܟܢܐ ܕܐܦ ܒܗܝܟܠܐ ܕܐܠܗܐ ܐܝܟ ܐܠܗܐ ܢܬܒ ܘܢܚܘܐ ܥܠ ܢܦܫܗ ܐܝܟ ܕܐܠܗܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 5, 'ܠܐ ܥܗܕܝܬܘܢ ܕܟܕ ܠܘܬܟܘܢ ܗܘܝܬ ܗܠܝܢ ܐܡܪܬ ܗܘܝܬ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 6, 'ܘܗܫܐ ܝܕܥܝܢ ܐܢܬܘܢ ܡܐ ܕܐܚܝܕ ܕܢܬܓܠܐ ܗܘ ܒܙܒܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 7, 'ܐܪܙܐ ܓܝܪ ܕܥܘܠܐ ܡܢ ܟܕܘ ܫܪܝ ܠܡܬܚܦܛܘ ܒܠܚܘܕ ܐܢ ܗܘ ܡܐ ܕܗܫܐ ܐܚܝܕ ܢܫܬܩܠ ܡܢ ܡܨܥܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 8, 'ܘܗܝܕܝܢ ܢܬܓܠܐ ܥܘܠܐ ܗܘ ܕܡܪܢ ܝܫܘܥ ܢܤܝܦܝܘܗܝ ܒܪܘܚ ܦܘܡܗ ܘܢܒܛܠܝܘܗܝ ܒܓܠܝܢܐ ܕܡܐܬܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 9, 'ܡܐܬܝܬܗ ܓܝܪ ܕܗܘ ܒܡܥܒܕܢܘܬܐ ܗܝ ܕܤܛܢܐ ܒܟܠ ܚܝܠ ܘܐܬܘܬܐ ܘܬܕܡܪܬܐ ܕܓܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 10, 'ܘܒܟܠܗ ܛܘܥܝܝ ܕܥܘܠܐ ܕܗܘܝܐ ܒܐܒܝܕܐ ܥܠ ܕܠܐ ܩܒܠܘ ܚܘܒܐ ܕܩܘܫܬܐ ܕܒܗ ܢܚܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 11, 'ܡܛܠܗܢܐ ܢܫܕܪ ܠܗܘܢ ܐܠܗܐ ܡܥܒܕܢܘܬܐ ܕܛܘܥܝܝ ܕܢܗܝܡܢܘܢ ܠܫܘܩܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 12, 'ܘܢܬܕܝܢܘܢ ܟܠܗܘܢ ܐܝܠܝܢ ܕܠܐ ܗܝܡܢܘ ܒܩܘܫܬܐ ܐܠܐ ܐܨܛܒܝܘ ܒܥܘܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 13, 'ܚܢܢ ܕܝܢ ܚܝܒܝܢܢ ܠܡܘܕܝܘ ܠܐܠܗܐ ܒܟܠܙܒܢ ܥܠ ܐܦܝܟܘܢ ܐܚܝܢ ܚܒܝܒܘܗܝ ܕܡܪܢ ܕܓܒܟܘܢ ܐܠܗܐ ܡܢ ܪܫܝܬܐ ܠܚܝܐ ܒܩܕܝܫܘܬܐ ܕܪܘܚܐ ܘܒܗܝܡܢܘܬܐ ܕܫܪܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 14, 'ܠܗܠܝܢ ܗܘ ܓܝܪ ܩܪܟܘܢ ܒܡܤܒܪܢܘܬܐ ܕܝܠܢ ܕܬܗܘܘܢ ܬܫܒܘܚܬܐ ܠܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 15, 'ܡܟܝܠ ܐܚܝ ܐܬܩܝܡܘ ܘܚܡܤܢܘ ܒܦܘܩܕܢܐ ܕܝܠܦܬܘܢ ܐܢ ܒܡܠܬܐ ܘܐܢ ܒܐܓܪܬܐ ܕܝܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 16, 'ܗܘ ܕܝܢ ܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܘܐܠܗܐ ܐܒܘܢ ܗܘ ܕܐܚܒܢ ܘܝܗܒ ܠܢ ܒܘܝܐܐ ܕܠܥܠܡ ܘܤܒܪܐ ܛܒܐ ܒܛܝܒܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 17, 'ܢܒܝܐ ܠܒܘܬܟܘܢ ܘܢܩܝܡ ܒܟܠ ܡܠܐ ܘܒܟܠ ܥܒܕ ܛܒ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 1, 'ܡܢ ܗܫܐ ܐܚܝܢ ܨܠܘ ܥܠܝܢ ܕܡܠܬܗ ܕܡܪܢ ܬܗܘܐ ܪܗܛܐ ܘܡܫܬܒܚܐ ܒܟܠ ܕܘܟܐ ܐܝܟ ܕܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 2, 'ܘܕܢܬܦܨܐ ܡܢ ܒܢܝܢܫܐ ܒܝܫܐ ܘܥܢܬܐ ܠܘ ܓܝܪ ܕܟܠܢܫ ܗܝ ܗܝܡܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 3, 'ܡܗܝܡܢ ܗܘ ܕܝܢ ܡܪܝܐ ܕܗܘ ܢܢܛܪܟܘܢ ܘܢܫܘܙܒܟܘܢ ܡܢ ܒܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 4, 'ܬܟܝܠܝܢܢ ܕܝܢ ܥܠܝܟܘܢ ܒܡܪܢ ܕܡܕܡ ܕܡܦܩܕܝܢܢ ܠܟܘܢ ܥܒܕܬܘܢ ܐܦ ܥܒܕܝܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 5, 'ܘܡܪܢ ܢܬܪܘܨ ܠܒܘܬܟܘܢ ܠܚܘܒܗ ܕܐܠܗܐ ܘܠܡܤܒܪܢܘܬܗ ܕܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 6, 'ܡܦܩܕܝܢܢ ܠܟܘܢ ܕܝܢ ܐܚܝ ܒܫܡܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܕܗܘܝܬܘܢ ܦܪܝܩܝܢ ܡܢ ܟܠ ܐܚܐ ܐܝܢܐ ܕܒܝܫ ܒܝܫ ܡܗܠܟ ܘܠܐ ܐܝܟ ܦܘܩܕܢܐ ܕܩܒܠܘ ܡܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 7, 'ܐܢܬܘܢ ܓܝܪ ܝܕܥܝܢ ܐܢܬܘܢ ܐܝܟܢܐ ܘܠܐ ܠܡܬܕܡܝܘ ܒܢ ܕܠܐ ܗܠܟܢ ܒܝܫ ܒܝܫ ܒܝܢܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 8, 'ܐܦܠܐ ܠܚܡܐ ܕܡܓܢ ܐܟܠܢ ܡܢ ܐܢܫ ܡܢܟܘܢ ܐܠܐ ܒܥܡܠܐ ܘܒܠܐܘܬܐ ܒܠܠܝܐ ܘܒܐܝܡܡܐ ܦܠܚܝܢ ܗܘܝܢ ܕܥܠ ܐܢܫ ܡܢܟܘܢ ܠܐ ܢܐܩܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 9, 'ܠܐ ܗܘܐ ܡܛܠ ܕܠܐ ܫܠܝܛ ܠܢ ܐܠܐ ܕܒܢܦܫܢ ܢܬܠ ܠܟܘܢ ܛܘܦܤܐ ܕܒܢ ܬܬܕܡܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 10, 'ܐܦ ܟܕ ܠܘܬܟܘܢ ܗܘܝܢ ܓܝܪ ܗܕܐ ܡܦܩܕܝܢ ܗܘܝܢ ܠܟܘܢ ܕܟܠ ܕܠܐ ܨܒܐ ܕܢܦܠܘܚ ܐܦܠܐ ܢܠܥܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 11, 'ܫܡܥܝܢܢ ܓܝܪ ܕܐܝܬ ܒܟܘܢ ܐܢܫܐ ܕܒܝܫ ܒܝܫ ܡܗܠܟܝܢ ܘܡܕܡ ܠܐ ܦܠܚܝܢ ܐܠܐ ܐܢ ܤܪܝܩܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 12, 'ܠܗܘܢ ܕܝܢ ܠܗܠܝܢ ܡܦܩܕܝܢܢ ܘܒܥܝܢܢ ܡܢܗܘܢ ܒܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܕܒܫܠܝܐ ܢܗܘܘܢ ܦܠܚܝܢ ܘܐܟܠܝܢ ܠܚܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 13, 'ܐܢܬܘܢ ܕܝܢ ܐܚܝ ܠܐ ܬܡܐܢ ܠܟܘܢ ܠܡܥܒܕ ܕܫܦܝܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 14, 'ܘܐܢ ܐܢܫ ܠܐ ܡܫܬܡܥ ܠܡܠܝܢ ܗܠܝܢ ܕܒܐܓܪܬܐ ܢܬܦܪܫ ܠܟܘܢ ܗܢܐ ܘܠܐ ܗܘܝܬܘܢ ܡܬܚܠܛܝܢ ܥܡܗ ܕܢܒܗܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 15, 'ܘܠܐ ܐܝܟ ܒܥܠܕܒܒܐ ܬܐܚܕܘܢܗ ܐܠܐ ܗܘܝܬܘܢ ܡܪܬܝܢ ܠܗ ܐܝܟ ܕܠܐܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 16, 'ܗܘ ܕܝܢ ܡܪܗ ܕܫܠܡܐ ܢܬܠ ܠܟܘܢ ܫܠܡܐ ܒܟܠܙܒܢ ܒܟܠܡܕܡ ܡܪܢ ܥܡ ܟܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 17, 'ܫܠܡܐ ܒܟܬܒܬ ܐܝܕܝ ܐܢܐ ܦܘܠܘܤ ܟܬܒܬ ܕܐܝܬܘܗܝ ܐܬܐ ܕܒܟܠܗܝܢ ܐܓܪܬܝ ܗܟܢܐ ܟܬܒ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 18, 'ܛܝܒܘܬܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܥܡ ܟܠܟܘܢ ܐܡܝܢ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 1, 'ܦܘܠܘܤ ܫܠܝܚܐ ܕܝܫܘܥ ܡܫܝܚܐ ܒܦܘܩܕܢܐ ܕܐܠܗܐ ܡܚܝܢܢ ܘܕܡܫܝܚܐ ܝܫܘܥ ܤܒܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 2, 'ܠܛܝܡܬܐܘܤ ܒܪܐ ܫܪܝܪܐ ܒܗܝܡܢܘܬܐ ܛܝܒܘܬܐ ܘܪܚܡܐ ܘܫܠܡܐ ܡܢ ܐܠܗܐ ܐܒܘܢ ܘܡܫܝܚܐ ܝܫܘܥ ܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 3, 'ܒܥܝܬ ܗܘܝܬ ܡܢܟ ܟܕ ܐܙܠ ܐܢܐ ܠܡܩܕܘܢܝܐ ܕܬܩܘܐ ܒܐܦܤܘܤ ܘܬܦܩܕ ܠܐܢܫ ܐܢܫ ܕܠܐ ܢܠܦܘܢ ܝܘܠܦܢܐ ܡܫܚܠܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 4, 'ܘܠܐ ܢܬܪܡܘܢ ܠܫܘܥܝܬܐ ܘܠܬܫܥܝܬܐ ܕܫܪܒܬܐ ܕܤܟܐ ܠܝܬ ܠܗܘܢ ܗܠܝܢ ܕܚܪܝܢܐ ܗܘ ܥܒܕܢ ܝܬܝܪܐܝܬ ܘܠܐ ܒܢܝܢܐ ܒܗܝܡܢܘܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 5, 'ܤܟܗ ܕܝܢ ܕܦܘܩܕܢܐ ܐܝܬܘܗܝ ܚܘܒܐ ܕܡܢ ܠܒܐ ܕܟܝܐ ܘܡܢ ܬܐܪܬܐ ܛܒܬܐ ܘܡܢ ܗܝܡܢܘܬܐ ܫܪܝܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 6, 'ܘܡܢܗܝܢ ܡܢ ܗܠܝܢ ܛܥܘ ܐܢܫܐ ܘܤܛܘ ܠܡܠܐ ܤܪܝܩܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 7, 'ܒܕܒܥܘ ܠܡܗܘܐ ܡܠܦܢܐ ܕܢܡܘܤܐ ܟܕ ܠܐ ܡܤܬܟܠܝܢ ܡܕܡ ܕܡܡܠܠܝܢ ܘܠܐ ܡܕܡ ܕܥܠܘܗܝ ܡܬܚܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 8, 'ܝܕܥܝܢܢ ܕܝܢ ܕܢܡܘܤܐ ܫܦܝܪ ܗܘ ܐܢ ܐܢܫ ܐܟܘܬܗ ܕܢܡܘܤܐ ܢܬܕܒܪ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 9, 'ܟܕ ܝܕܥ ܕܠܟܐܢܐ ܢܡܘܤܐ ܠܐ ܤܝܡ ܐܠܐ ܠܥܘܠܐ ܘܠܡܪܘܕܐ ܘܠܪܫܝܥܐ ܘܠܚܛܝܐ ܘܠܥܢܬܐ ܘܠܐܝܠܝܢ ܕܠܐ ܕܟܝܢ ܘܠܐܝܠܝܢ ܕܡܚܝܢ ܠܐܒܗܝܗܘܢ ܘܠܐܝܠܝܢ ܕܡܚܝܢ ܠܐܡܗܬܗܘܢ ܘܠܩܛܘܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 10, 'ܘܠܙܢܝܐ ܘܠܫܟܒܝ ܥܡ ܕܟܪܐ ܘܠܓܢܒܝ ܒܢܝ ܚܐܪܐ ܘܠܕܓܠܐ ܘܠܥܒܪܝ ܥܠ ܡܘܡܬܐ ܘܠܟܠܡܕܡ ܕܐܝܬܘܗܝ ܤܩܘܒܠܐ ܠܝܘܠܦܢܐ ܚܠܝܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 11, 'ܕܐܘܢܓܠܝܘܢ ܕܬܫܒܘܚܬܗ ܕܐܠܗܐ ܡܒܪܟܐ ܗܘ ܕܐܢܐ ܐܬܗܝܡܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 12, 'ܘܡܘܕܐ ܐܢܐ ܠܗ ܕܚܝܠܢܝ ܠܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܕܚܫܒܢܝ ܡܗܝܡܢܐ ܘܤܡܢܝ ܠܬܫܡܫܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 13, 'ܠܝ ܕܡܢ ܩܕܝܡ ܡܓܕܦܢ ܗܘܝܬ ܘܪܕܘܦ ܘܡܨܥܪܢ ܐܠܐ ܐܬܚܢܢܬ ܡܛܠ ܕܟܕ ܠܐ ܝܕܥ ܗܘܝܬ ܥܒܕܬ ܕܠܐ ܗܝܡܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 14, 'ܤܓܝܬ ܒܝ ܕܝܢ ܛܝܒܘܬܗ ܕܡܪܢ ܘܗܝܡܢܘܬܐ ܘܚܘܒܐ ܕܒܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 15, 'ܡܗܝܡܢܐ ܗܝ ܡܠܬܐ ܘܫܘܝܐ ܗܝ ܠܡܩܒܠܘܬܗ ܕܝܫܘܥ ܡܫܝܚܐ ܐܬܐ ܠܥܠܡܐ ܠܡܚܝܘ ܠܚܛܝܐ ܕܩܕܡܝܗܘܢ ܐܢܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 16, 'ܐܠܐ ܡܛܠ ܗܢܐ ܪܚܡ ܥܠܝ ܕܒܝ ܩܕܡܐ ܢܚܘܐ ܝܫܘܥ ܡܫܝܚܐ ܟܠܗ ܡܓܪܬ ܪܘܚܗ ܠܬܚܘܝܬܐ ܕܐܝܠܝܢ ܕܥܬܝܕܝܢ ܕܢܗܝܡܢܘܢ ܒܗ ܠܚܝܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 17, 'ܠܡܠܟܐ ܕܝܢ ܕܥܠܡܐ ܠܗܘ ܕܠܐ ܡܬܚܒܠ ܘܠܐ ܡܬܚܙܐ ܕܗܘܝܘ ܚܕ ܐܠܗܐ ܐܝܩܪܐ ܘܬܫܒܘܚܬܐ ܠܥܠܡ ܥܠܡܝܢ ܐܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 18, 'ܗܢܐ ܦܘܩܕܢܐ ܡܓܥܠ ܐܢܐ ܠܟ ܒܪܝ ܛܝܡܬܐܘܤ ܐܝܟ ܢܒܝܘܬܐ ܩܕܡܝܬܐ ܕܗܘܝ ܥܠܝܟ ܕܬܦܠܘܚ ܒܗܝܢ ܦܠܚܘܬܐ ܗܕܐ ܫܦܝܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 19, 'ܒܗܝܡܢܘܬܐ ܘܒܬܐܪܬܐ ܛܒܬܐ ܐܝܠܝܢ ܓܝܪ ܕܠܗܕܐ ܕܚܩܘ ܡܢܗܘܢ ܡܢ ܗܝܡܢܘܬܐ ܐܤܬܪܩܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 20, 'ܐܝܟ ܗܘܡܢܐܘܤ ܘܐܠܟܤܢܕܪܘܤ ܗܠܝܢ ܕܐܫܠܡܬ ܠܤܛܢܐ ܕܢܬܪܕܘܢ ܕܠܐ ܢܗܘܘܢ ܡܓܕܦܝܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 1, 'ܒܥܐ ܐܢܐ ܗܟܝܠ ܡܢܟ ܕܡܢ ܩܕܡ ܟܠܡܕܡ ܒܥܘܬܐ ܬܗܘܐ ܡܩܪܒ ܠܐܠܗܐ ܘܨܠܘܬܐ ܘܬܚܢܢܬܐ ܘܬܘܕܝܬܐ ܚܠܦ ܟܠܗܘܢ ܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 2, 'ܚܠܦ ܡܠܟܐ ܘܪܘܪܒܢܐ ܕܥܘܡܪܐ ܫܠܝܐ ܘܢܝܚܐ ܢܥܡܪ ܒܟܠܗ ܕܚܠܬ ܐܠܗܐ ܘܕܟܝܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 3, 'ܗܕܐ ܓܝܪ ܫܦܝܪܐ ܘܡܩܒܠܐ ܩܕܡ ܐܠܗܐ ܡܚܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 4, 'ܗܘ ܕܨܒܐ ܕܟܠܗܘܢ ܒܢܝܢܫܐ ܢܚܘܢ ܘܢܬܦܢܘܢ ܠܝܕܥܬܐ ܕܫܪܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 5, 'ܚܕ ܗܘ ܓܝܪ ܐܠܗܐ ܘܚܕ ܗܘ ܡܨܥܝܐ ܕܐܠܗܐ ܘܕܒܢܝܢܫܐ ܒܪܢܫܐ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 6, 'ܗܘ ܕܝܗܒ ܢܦܫܗ ܦܘܪܩܢܐ ܚܠܦ ܟܠ ܐܢܫ ܤܗܕܘܬܐ ܕܐܬܬ ܒܙܒܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 7, 'ܗܝ ܕܐܢܐ ܐܬܤܝܡܬ ܟܪܘܙܗ ܘܫܠܝܚܗ ܩܘܫܬܐ ܐܡܪ ܐܢܐ ܘܠܐ ܡܕܓܠ ܐܢܐ ܕܗܘܝܬ ܡܠܦܢܐ ܕܥܡܡܐ ܒܗܝܡܢܘܬܐ ܕܫܪܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 8, 'ܨܒܐ ܐܢܐ ܗܟܝܠ ܕܗܘܘ ܡܨܠܝܢ ܓܒܪܐ ܒܟܠ ܕܘܟܐ ܟܕ ܡܪܝܡܝܢ ܐܝܕܝܗܘܢ ܕܟܝܐܝܬ ܕܠܐ ܪܘܓܙܐ ܘܕܠܐ ܡܚܫܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 9, 'ܗܟܢܐ ܐܦ ܢܫܐ ܒܐܤܟܡܐ ܢܟܦܐ ܕܠܒܘܫܐ ܒܬܚܡܨܬܐ ܘܒܢܟܦܘܬܐ ܢܗܘܐ ܬܨܒܝܬܗܝܢ ܠܐ ܒܓܕܘܠܐ ܘܒܕܗܒܐ ܐܘ ܒܡܪܓܢܝܬܐ ܐܘ ܒܢܚܬܐ ܫܦܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 10, 'ܐܠܐ ܒܥܒܕܐ ܛܒܐ ܐܝܟܢܐ ܕܝܐܐ ܠܢܫܐ ܕܡܫܬܘܕܝܢ ܕܚܠܬ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 11, 'ܐܢܬܬܐ ܒܫܠܝܐ ܗܘܬ ܝܠܦܐ ܒܟܠ ܫܘܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 12, 'ܠܐܢܬܬܐ ܓܝܪ ܠܡܠܦܘ ܠܐ ܡܦܤ ܐܢܐ ܘܠܐ ܠܡܡܪܚܘ ܥܠ ܓܒܪܐ ܐܠܐ ܬܗܘܐ ܒܫܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 13, 'ܐܕܡ ܓܝܪ ܐܬܓܒܠ ܠܘܩܕܡ ܘܗܝܕܝܢ ܚܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 14, 'ܘܐܕܡ ܠܐ ܛܥܐ ܐܢܬܬܐ ܕܝܢ ܛܥܬ ܘܥܒܪܬ ܥܠ ܦܘܩܕܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 15, 'ܚܝܐ ܕܝܢ ܒܝܕ ܝܠܕܝܗ ܐܢ ܢܩܘܘܢ ܒܗܝܡܢܘܬܐ ܘܒܚܘܒܐ ܘܒܩܕܝܫܘܬܐ ܘܒܢܟܦܘܬܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 1, 'ܡܗܝܡܢܐ ܗܝ ܡܠܬܐ ܕܐܢ ܐܢܫ ܪܐܓ ܩܫܝܫܘܬܐ ܥܒܕܐ ܛܒܐ ܪܐܓ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 2, 'ܘܠܐ ܕܝܢ ܕܢܗܘܐ ܩܫܝܫܐ ܐܝܢܐ ܕܡܘܡܐ ܠܐ ܡܫܬܟܚ ܒܗ ܘܗܘܐ ܒܥܠܐ ܕܚܕܐ ܐܢܬܬܐ ܐܝܢܐ ܕܥܝܪ ܪܥܝܢܗ ܘܢܟܦ ܘܡܛܟܤ ܘܪܚܡ ܐܟܤܢܝܐ ܘܡܠܦܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 3, 'ܘܠܐ ܥܒܪ ܥܠ ܚܡܪܐ ܘܠܐ ܪܗܛܐ ܐܝܕܗ ܠܡܡܚܐ ܐܠܐ ܢܗܘܐ ܡܟܝܟ ܘܠܐ ܢܨܝ ܘܠܐ ܪܚܡ ܟܤܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 4, 'ܘܡܕܒܪ ܒܝܬܗ ܫܦܝܪ ܘܐܚܝܕ ܒܢܘܗܝ ܒܫܘܥܒܕܐ ܒܟܠܗ ܕܟܝܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 5, 'ܐܢ ܓܝܪ ܒܝܬܐ ܕܢܦܫܗ ܠܐ ܝܕܥ ܕܢܕܒܪ ܫܦܝܪ ܐܝܟܢܐ ܡܫܟܚ ܥܕܬܗ ܕܐܠܗܐ ܕܢܕܒܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 6, 'ܘܠܐ ܢܗܘܐ ܛܠܐ ܬܘܠܡܕܗ ܕܠܐ ܢܬܪܝܡ ܘܢܦܠ ܒܕܝܢܗ ܕܤܛܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 7, 'ܘܠܐ ܕܝܢ ܕܐܦ ܤܗܕܘܬܐ ܫܦܝܪܬܐ ܐܝܬ ܠܗ ܡܢ ܒܪܝܐ ܕܠܐ ܢܦܠ ܒܚܤܕܐ ܘܒܦܚܐ ܕܤܛܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 8, 'ܘܐܦ ܡܫܡܫܢܐ ܗܟܢܐ ܢܗܘܘܢ ܕܟܝܢ ܘܠܐ ܢܡܠܠܘܢ ܬܪܬܝܢ ܘܠܐ ܢܗܘܘܢ ܨܠܝܢ ܠܚܡܪܐ ܤܓܝܐܐ ܘܠܐ ܢܪܚܡܘܢ ܝܘܬܪܢܐ ܛܢܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 9, 'ܐܠܐ ܢܐܚܕܘܢ ܐܪܙܐ ܕܗܝܡܢܘܬܐ ܒܬܐܪܬܐ ܕܟܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 10, 'ܘܗܢܘܢ ܗܠܝܢ ܢܬܒܩܘܢ ܠܘܩܕܡ ܘܗܝܕܝܢ ܢܫܡܫܘܢ ܟܕ ܐܝܬܝܗܘܢ ܕܠܐ ܪܫܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 11, 'ܗܟܢܐ ܐܦ ܢܫܐ ܢܗܘܝܢ ܢܟܦܢ ܘܢܗܘܐ ܥܝܪ ܪܥܝܢܗܝܢ ܘܢܗܘܝܢ ܡܗܝܡܢܢ ܒܟܠ ܡܕܡ ܘܠܐ ܢܗܘܝܢ ܐܟܠܢ ܩܪܨܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 12, 'ܡܫܡܫܢܐ ܢܗܘܘܢ ܐܝܢܐ ܕܚܕܐ ܐܢܬܬܐ ܗܘܬ ܠܗ ܘܕܒܪ ܒܢܘܗܝ ܘܒܝܬܗ ܫܦܝܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 13, 'ܐܝܠܝܢ ܓܝܪ ܕܫܡܫܘ ܫܦܝܪ ܕܪܓܐ ܛܒܐ ܩܢܝܢ ܠܢܦܫܗܘܢ ܘܡܓܠܐ ܐܦܐ ܤܓܝܐܐ ܒܗܝܡܢܘܬܐ ܕܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 14, 'ܗܠܝܢ ܟܬܒ ܐܢܐ ܠܟ ܟܕ ܡܤܒܪ ܐܢܐ ܕܒܥܓܠ ܐܬܐ ܐܢܐ ܠܘܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 15, 'ܘܐܢ ܗܘ ܕܝܢ ܕܡܘܚܪ ܐܢܐ ܕܬܕܥ ܐܝܟܢܐ ܘܠܐ ܠܡܬܗܦܟܘ ܒܒܝܬܗ ܕܐܠܗܐ ܕܐܝܬܝܗ ܥܕܬܐ ܕܐܠܗܐ ܚܝܐ ܥܡܘܕܐ ܘܫܬܐܤܬܐ ܕܫܪܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 16, 'ܘܫܪܝܪܐܝܬ ܪܒ ܗܘ ܐܪܙܐ ܗܢܐ ܕܟܐܢܘܬܐ ܕܐܬܓܠܝ ܒܒܤܪ ܘܐܙܕܕܩ ܒܪܘܚ ܘܐܬܚܙܝ ܠܡܠܐܟܐ ܘܐܬܟܪܙ ܒܝܬ ܥܡܡܐ ܘܐܬܗܝܡܢ ܒܥܠܡܐ ܘܐܤܬܠܩ ܒܫܘܒܚܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 1, 'ܪܘܚܐ ܕܝܢ ܦܫܝܩܐܝܬ ܐܡܪܐ ܕܒܙܒܢܐ ܐܚܪܝܐ ܢܦܪܩܘܢ ܐܢܫ ܐܢܫ ܡܢ ܗܝܡܢܘܬܐ ܘܢܐܙܠܘܢ ܒܬܪ ܪܘܚܐ ܛܥܝܬܐ ܘܒܬܪ ܝܘܠܦܢܐ ܕܫܐܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 2, 'ܗܠܝܢ ܕܒܐܤܟܡܐ ܕܓܠܐ ܡܛܥܝܢ ܘܡܡܠܠܝܢ ܟܕܒܘܬܐ ܘܟܘܝܢ ܒܬܐܪܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 3, 'ܘܟܠܝܢ ܠܡܙܕܘܓܘ ܘܡܦܪܩܝܢ ܡܢ ܡܐܟܠܬܐ ܐܝܠܝܢ ܕܐܠܗܐ ܒܪܐ ܐܢܝܢ ܠܚܘܫܚܐ ܘܠܬܘܕܝܬܐ ܠܐܝܠܝܢ ܕܡܗܝܡܢܝܢ ܘܝܕܥܝܢ ܫܪܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 4, 'ܡܛܠ ܕܟܠ ܕܒܪܐ ܠܐܠܗܐ ܫܦܝܪ ܗܘ ܘܠܝܬ ܡܕܡ ܕܡܤܠܝ ܐܢ ܒܬܘܕܝܬܐ ܢܬܢܤܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 5, 'ܡܬܩܕܫ ܓܝܪ ܒܡܠܬܐ ܕܐܠܗܐ ܘܒܨܠܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 6, 'ܗܠܝܢ ܐܢ ܬܗܘܐ ܡܠܦ ܠܐܚܝܟ ܡܫܡܫܢܐ ܛܒܐ ܬܗܘܐ ܕܝܫܘܥ ܡܫܝܚܐ ܟܕ ܡܬܪܒܐ ܐܢܬ ܒܡܠܐ ܕܗܝܡܢܘܬܐ ܘܒܝܘܠܦܢܐ ܛܒܐ ܕܝܠܝܦ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 7, 'ܡܢ ܫܘܥܝܬܐ ܕܝܢ ܦܟܝܗܬܐ ܕܤܒܬܐ ܐܫܬܐܠ ܘܕܪܫ ܢܦܫܟ ܒܟܐܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 8, 'ܕܘܪܫܐ ܓܝܪ ܕܦܓܪܐ ܙܒܢܐ ܗܘ ܙܥܘܪܐ ܡܘܬܪ ܟܐܢܘܬܐ ܕܝܢ ܒܟܠ ܡܕܡ ܡܘܬܪܐ ܘܐܝܬ ܠܗ ܫܘܘܕܝܐ ܕܚܝܐ ܕܙܒܢܐ ܗܢܐ ܘܕܥܬܝܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 9, 'ܡܗܝܡܢܐ ܗܝ ܡܠܬܐ ܘܫܘܝܐ ܗܝ ܠܡܩܒܠܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 10, 'ܡܛܠ ܗܕܐ ܗܘ ܓܝܪ ܠܐܝܢܢ ܘܡܬܚܤܕܝܢܢ ܕܡܤܒܪܝܢܢ ܒܐܠܗܐ ܚܝܐ ܕܐܝܬܘܗܝ ܡܚܝܢܐ ܕܒܢܝܢܫܐ ܟܠܗܘܢ ܝܬܝܪܐܝܬ ܕܡܗܝܡܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 11, 'ܗܠܝܢ ܐܠܦ ܘܦܩܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 12, 'ܘܠܐ ܐܢܫ ܢܒܤܐ ܥܠ ܛܠܝܘܬܟ ܐܠܐ ܗܘܝ ܕܡܘܬܐ ܠܡܗܝܡܢܐ ܒܡܠܬܐ ܘܒܗܘܦܟܐ ܘܒܚܘܒܐ ܘܒܗܝܡܢܘܬܐ ܘܒܕܟܝܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 13, 'ܥܕ ܐܬܐ ܐܢܐ ܐܬܚܦܛ ܒܩܪܝܢܐ ܘܒܒܥܘܬܐ ܘܒܝܘܠܦܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 14, 'ܘܠܐ ܬܒܤܐ ܒܡܘܗܒܬܐ ܕܐܝܬ ܒܟ ܗܝ ܕܐܬܝܗܒܬ ܠܟ ܒܢܒܝܘܬܐ ܘܒܤܝܡ ܐܝܕܐ ܕܩܫܝܫܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 15, 'ܒܗܠܝܢ ܐܬܗܓܐ ܘܒܗܝܢ ܗܘܝ ܕܬܬܝܕܥ ܠܟܠܢܫ ܕܠܩܕܡܝܟ ܐܬܐ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 16, 'ܘܐܙܕܗܪ ܒܢܦܫܟ ܘܒܝܘܠܦܢܟ ܘܚܡܤܢ ܒܗܘܢ ܟܕ ܗܠܝܢ ܓܝܪ ܬܥܒܕ ܢܦܫܟ ܬܚܐ ܘܠܐܝܠܝܢ ܕܫܡܥܝܢ ܠܟ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 1, 'ܒܩܫܝܫܐ ܠܐ ܬܓܥܘܪ ܐܠܐ ܐܦܝܤܝܗܝ ܐܝܟ ܕܠܐܒܐ ܘܠܐܝܠܝܢ ܕܛܠܝܢ ܐܝܟ ܕܠܐܚܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 2, 'ܘܠܩܫܝܫܬܐ ܐܝܟ ܐܡܗܬܐ ܘܠܐܝܠܝܢ ܕܛܠܝܢ ܐܝܟ ܕܠܐܚܘܬܟ ܒܟܠܗ ܕܟܝܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 3, 'ܠܐܪܡܠܬܐ ܝܩܪ ܠܐܝܠܝܢ ܕܐܪܡܠܬܐ ܐܢܝܢ ܒܫܪܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 4, 'ܘܐܢ ܐܝܬ ܐܪܡܠܬܐ ܕܐܝܬ ܠܗ ܒܢܝܐ ܐܘ ܒܢܝ ܒܢܝܐ ܢܐܠܦܘܢ ܠܘܩܕܡ ܕܒܒܢܝ ܒܝܬܗܘܢ ܢܙܕܕܩܘܢ ܘܢܦܪܥܘܢ ܚܘܒܠܐ ܠܐܒܗܝܗܘܢ ܗܕܐ ܓܝܪ ܡܩܒܠܐ ܩܕܡ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 5, 'ܐܝܕܐ ܕܝܢ ܕܫܪܝܪܐܝܬ ܐܪܡܠܬܐ ܗܝ ܘܡܫܘܚܕܬܐ ܗܕܐ ܤܒܪܗ ܥܠ ܐܠܗܐ ܗܘ ܘܐܡܝܢܐ ܒܨܠܘܬܐ ܘܒܒܥܘܬܐ ܒܠܠܝܐ ܘܒܐܝܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 6, 'ܐܝܕܐ ܕܝܢ ܕܦܠܚܐ ܐܤܛܪܢܝܐ ܡܝܬܐ ܗܝ ܟܕ ܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 7, 'ܗܠܝܢ ܗܘܝܬ ܡܦܩܕ ܠܗܝܢ ܕܕܠܐ ܪܫܝܢ ܢܗܘܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 8, 'ܐܢ ܐܢܫ ܓܝܪ ܕܐܝܠܝܢ ܕܕܝܠܗ ܐܢܘܢ ܘܝܬܝܪܐܝܬ ܕܐܝܠܝܢ ܕܐܝܬܝܗܘܢ ܒܢܝ ܒܝܬܐ ܕܗܝܡܢܘܬܐ ܠܐ ܝܨܦ ܗܢܐ ܟܦܪ ܠܗ ܒܗܝܡܢܘܬܐ ܘܒܝܫ ܗܘ ܡܢ ܐܝܠܝܢ ܕܠܐ ܡܗܝܡܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 9, 'ܗܘܝܬ ܗܟܝܠ ܓܒܐ ܐܪܡܠܬܐ ܐܝܕܐ ܕܠܐ ܒܨܝܪܐ ܡܢ ܫܬܝܢ ܫܢܝܢ ܐܝܕܐ ܕܠܚܕ ܗܘ ܓܒܪܐ ܗܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 10, 'ܘܐܝܬ ܠܗ ܤܗܕܘܬܐ ܕܥܒܕܐ ܫܦܝܪܐ ܐܢ ܪܒܝܬ ܒܢܝܐ ܐܢ ܩܒܠܬ ܐܟܤܢܝܐ ܐܢ ܐܫܝܓܬ ܪܓܠܝܗܘܢ ܕܩܕܝܫܐ ܐܢ ܐܪܘܚܬ ܠܐܠܝܨܐ ܐܢ ܗܠܟܬ ܒܟܠ ܥܒܕ ܛܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 11, 'ܡܢ ܐܪܡܠܬܐ ܕܝܢ ܐܝܠܝܢ ܕܛܠܝܢ ܐܫܬܐܠ ܡܨܛܪܝܢ ܠܗܝܢ ܓܝܪ ܥܠ ܡܫܝܚܐ ܘܒܥܝܢ ܠܡܗܘܐ ܠܓܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 12, 'ܘܕܝܢܗܝܢ ܩܝܡ ܗܘ ܕܛܠܡ ܗܝܡܢܘܬܗܝܢ ܩܕܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 13, 'ܝܠܦܢ ܕܝܢ ܐܦ ܚܒܢܢܘܬܐ ܟܕ ܡܬܟܪܟܢ ܒܝܬ ܒܬܐ ܘܠܐ ܒܠܚܘܕ ܚܒܢܢܘܬܐ ܐܠܐ ܐܦ ܕܢܤܓܝܢ ܡܡܠܠܐ ܘܢܦܪܩܢ ܤܪܝܩܬܐ ܘܢܡܠܠܢ ܡܕܡ ܕܠܐ ܘܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 14, 'ܨܒܐ ܐܢܐ ܗܟܝܠ ܕܐܝܠܝܢ ܕܛܠܝܢ ܢܙܕܘܓܢ ܘܢܐܠܕܢ ܒܢܝܐ ܘܢܕܒܪܢ ܒܬܝܗܝܢ ܘܠܐ ܢܬܠܢ ܠܒܥܠܕܒܒܐ ܐܦܠܐ ܚܕܐ ܥܠܬܐ ܕܨܘܚܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 15, 'ܥܠ ܕܗܫܐ ܓܝܪ ܫܪܝܘ ܐܢܫ ܐܢܫ ܠܡܤܛܐ ܒܬܪ ܤܛܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 16, 'ܐܢ ܐܢܫ ܡܗܝܡܢܐ ܐܘ ܡܗܝܡܢܬܐ ܐܝܬ ܠܗܘܢ ܐܪܡܠܬܐ ܢܬܪܤܘܢ ܐܢܝܢ ܘܠܐ ܢܐܩܪܢ ܥܠ ܥܕܬܐ ܕܠܐܝܠܝܢ ܕܐܪܡܠܬܐ ܐܢܝܢ ܕܫܪܪܐ ܬܤܦܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 17, 'ܩܫܝܫܐ ܐܝܠܝܢ ܕܫܦܝܪ ܡܬܕܒܪܝܢ ܠܐܝܩܪܐ ܥܦܝܦܐ ܢܫܘܘܢ ܝܬܝܪܐܝܬ ܐܝܠܝܢ ܕܠܐܝܢ ܒܡܠܬܐ ܘܒܝܘܠܦܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 18, 'ܐܡܪ ܓܝܪ ܟܬܒܐ ܕܠܐ ܬܒܠܘܡ ܬܘܪܐ ܒܕܪܟܬܐ ܘܫܘܐ ܗܘ ܦܥܠܐ ܐܓܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 19, 'ܥܠ ܩܫܝܫܐ ܩܛܓܪܢܘܬܐ ܠܐ ܬܩܒܠ ܐܠܐ ܥܠ ܦܘܡ ܬܪܝܢ ܘܬܠܬܐ ܤܗܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 20, 'ܠܐܝܠܝܢ ܕܚܛܝܢ ܩܕܡ ܟܠܢܫ ܟܘܢ ܕܐܦ ܫܪܟܐ ܕܐܢܫܐ ܢܕܚܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 21, 'ܡܤܗܕ ܐܢܐ ܠܟ ܩܕܡ ܐܠܗܐ ܘܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܘܡܠܐܟܘܗܝ ܓܒܝܐ ܕܗܠܝܢ ܬܛܪ ܘܠܐ ܢܬܩܕܡ ܪܥܝܢܟ ܠܡܕܡ ܘܠܐ ܬܤܥܘܪ ܡܕܡ ܒܡܤܒ ܒܐܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 22, 'ܐܝܕܐ ܒܥܓܠ ܥܠ ܐܢܫ ܠܐ ܬܤܝܡ ܘܠܐ ܬܫܬܘܬܦ ܒܚܛܗܐ ܢܘܟܪܝܐ ܢܦܫܟ ܛܪ ܒܕܟܝܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 23, 'ܘܡܟܝܠ ܡܝܐ ܠܐ ܬܫܬܐ ܐܠܐ ܚܡܪܐ ܩܠܝܠ ܗܘܝܬ ܫܬܐ ܡܛܠ ܐܤܛܘܡܟܟ ܘܡܛܠ ܟܘܪܗܢܝܟ ܐܡܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 24, 'ܐܝܬ ܒܢܝܢܫܐ ܕܚܛܗܝܗܘܢ ܝܕܝܥܝܢ ܐܢܘܢ ܘܡܩܕܡܝܢ ܠܗܘܢ ܠܒܝܬ ܕܝܢܐ ܘܐܝܬ ܕܒܬܪܗܘܢ ܐܙܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 25, 'ܗܟܢܐ ܐܦ ܥܒܕܐ ܛܒܐ ܝܕܝܥܝܢ ܐܢܘܢ ܘܐܝܠܝܢ ܕܐܚܪܢܝܐܝܬ ܐܢܘܢ ܠܡܛܫܐ ܠܐ ܡܫܟܚܝܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 1, 'ܐܝܠܝܢ ܕܐܝܬܝܗܘܢ ܬܚܝܬ ܢܝܪܐ ܕܥܒܕܘܬܐ ܠܡܪܝܗܘܢ ܒܟܠ ܐܝܩܪ ܢܐܚܕܘܢ ܕܠܐ ܢܗܘܐ ܫܡܗ ܕܐܠܗܐ ܘܝܘܠܦܢܗ ܡܬܓܕܦ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 2, 'ܐܝܠܝܢ ܕܝܢ ܕܐܝܬ ܠܗܘܢ ܡܪܝܐ ܡܗܝܡܢܐ ܠܐ ܢܒܤܘܢ ܒܗܘܢ ܥܠ ܕܐܚܝܗܘܢ ܐܢܘܢ ܐܠܐ ܝܬܝܪܐܝܬ ܢܫܡܫܘܢ ܐܢܘܢ ܥܠ ܕܡܗܝܡܢܐ ܐܢܘܢ ܘܚܒܝܒܐ ܗܠܝܢ ܕܡܬܬܢܝܚܝܢ ܒܬܫܡܫܬܗܘܢ ܗܠܝܢ ܐܠܦ ܘܒܥܝ ܡܢܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 3, 'ܐܢ ܕܝܢ ܐܝܬ ܐܢܫ ܕܡܠܦ ܝܘܠܦܢܐ ܐܚܪܢܐ ܘܠܐ ܡܬܩܪܒ ܠܡܠܐ ܚܠܝܡܬܐ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܘܠܝܘܠܦܢܐ ܕܕܚܠܬ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 4, 'ܗܢܐ ܡܬܪܝܡ ܟܕ ܡܕܡ ܠܐ ܝܕܥ ܐܠܐ ܟܪܝܗ ܒܕܪܫܐ ܘܒܒܥܬܐ ܕܡܠܐ ܕܡܢܗܝܢ ܗܘܐ ܚܤܡܐ ܘܚܪܝܢܐ ܘܓܘܕܦܐ ܘܡܤܡ ܒܪܥܝܢܐ ܒܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 5, 'ܘܫܚܩܐ ܕܒܢܝܢܫܐ ܐܝܠܝܢ ܕܡܚܒܠ ܪܥܝܢܗܘܢ ܘܓܠܝܙܝܢ ܡܢ ܩܘܫܬܐ ܘܤܒܪܝܢ ܕܬܓܘܪܬܐ ܗܝ ܕܚܠܬ ܐܠܗܐ ܐܢܬ ܕܝܢ ܐܬܪܚܩ ܡܢ ܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 6, 'ܬܐܓܘܪܬܢ ܓܝܪ ܕܝܠܢ ܪܒܐ ܗܝ ܕܐܝܬܝܗ ܕܚܠܬ ܐܠܗܐ ܒܚܫܚܬܐ ܕܡܤܬܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 7, 'ܡܕܡ ܓܝܪ ܠܐ ܐܥܠܢ ܠܥܠܡܐ ܘܝܕܝܥܐ ܕܐܦܠܐ ܕܢܦܩ ܡܢܗ ܡܫܟܚܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 8, 'ܡܛܠ ܗܢܐ ܤܦܩܐ ܠܢ ܡܐܟܘܠܬܐ ܘܬܟܤܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 9, 'ܐܝܠܝܢ ܕܝܢ ܕܨܒܝܢ ܠܡܥܬܪ ܢܦܠܝܢ ܒܢܤܝܘܢܐ ܘܒܦܚܐ ܘܒܪܓܝܓܬܐ ܤܓܝܐܬܐ ܕܤܟܠܢ ܘܡܤܓܦܢ ܘܡܛܒܥܢ ܠܒܢܝܢܫܐ ܒܚܒܠܐ ܘܒܐܒܕܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 10, 'ܥܩܪܐ ܕܝܢ ܕܟܘܠܗܝܢ ܒܝܫܬܐ ܐܝܬܝܗ ܪܚܡܬ ܟܤܦܐ ܘܐܝܬ ܐܢܫܐ ܕܐܬܪܓܪܓܘ ܠܗ ܘܡܢ ܗܝܡܢܘܬܐ ܛܥܘ ܘܢܦܫܗܘܢ ܐܥܠܘ ܠܕܐܘܢܐ ܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 11, 'ܐܢܬ ܕܝܢ ܐܘ ܒܪܢܫܐ ܕܐܠܗܐ ܡܢ ܗܠܝܢ ܥܪܘܩ ܘܗܪܛ ܒܬܪ ܙܕܝܩܘܬܐ ܘܒܬܪ ܟܐܢܘܬܐ ܘܒܬܪ ܗܝܡܢܘܬܐ ܘܒܬܪ ܚܘܒܐ ܘܒܬܪ ܡܤܝܒܪܢܘܬܐ ܘܒܬܪ ܡܟܝܟܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 12, 'ܘܐܬܟܬܫ ܒܐܓܘܢܐ ܛܒܐ ܕܗܝܡܢܘܬܐ ܘܐܕܪܟ ܚܝܐ ܕܠܥܠܡ ܕܠܗܘܢ ܐܬܩܪܝܬ ܘܐܘܕܝܬ ܬܘܕܝܬܐ ܛܒܬܐ ܩܕܡ ܤܗܕܐ ܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 13, 'ܡܤܗܕ ܐܢܐ ܠܟ ܩܕܡ ܐܠܗܐ ܗܘ ܕܡܚܐ ܟܠ ܘܝܫܘܥ ܡܫܝܚܐ ܗܘ ܕܐܤܗܕ ܩܕܡ ܦܢܛܝܘܤ ܦܝܠܛܘܤ ܤܗܕܘܬܐ ܫܦܝܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 14, 'ܕܬܛܪܝܘܗܝ ܦܘܩܕܢܐ ܕܠܐ ܛܘܠܫܐ ܘܕܠܐ ܡܘܡܐ ܥܕܡܐ ܠܓܠܝܢܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 15, 'ܗܘ ܕܥܬܝܕ ܒܙܒܢܗ ܢܚܘܝܘܗܝ ܐܠܗܐ ܡܒܪܟܐ ܘܚܝܠܬܢܐ ܒܠܚܘܕܘܗܝ ܡܠܟܐ ܕܡܠܟܐ ܘܡܪܐ ܕܡܪܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 16, 'ܗܘ ܕܗܘ ܒܠܚܘܕܘܗܝ ܠܐ ܡܬܚܒܠ ܘܥܡܪ ܒܢܘܗܪܐ ܕܐܢܫ ܠܐ ܡܫܟܚ ܕܢܬܩܪܒ ܠܗ ܘܐܢܫ ܡܢ ܒܢܝܢܫܐ ܠܐ ܚܙܝܗܝ ܘܐܦܠܐ ܡܨܐ ܠܡܚܙܝܗ ܗܘ ܕܠܗ ܐܝܩܪܐ ܘܫܘܠܛܢܐ ܠܥܠܡ ܥܠܡܝܢ ܐܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 17, 'ܠܥܬܝܪܐ ܕܥܠܡܐ ܗܢܐ ܦܩܕ ܕܠܐ ܢܬܪܝܡܘܢ ܒܪܥܝܢܝܗܘܢ ܘܠܐ ܢܬܬܟܠܘܢ ܥܠ ܥܘܬܪܐ ܕܠܝܬ ܥܠܘܗܝ ܬܘܟܠܢܐ ܐܠܐ ܥܠ ܐܠܗܐ ܚܝܐ ܗܘ ܕܝܗܒ ܠܢ ܟܠ ܥܬܝܪܐܝܬ ܠܢܝܚܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 18, 'ܘܢܥܒܕܘܢ ܥܒܕܐ ܛܒܐ ܘܢܥܬܪܘܢ ܒܤܘܥܪܢܐ ܫܦܝܪܐ ܘܢܗܘܘܢ ܕܠܝܠܝܢ ܠܡܬܠ ܘܠܡܫܬܘܬܦܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 19, 'ܘܢܤܝܡܘܢ ܠܢܦܫܗܘܢ ܫܬܐܤܬܐ ܛܒܬܐ ܠܡܕܡ ܕܥܬܝܕ ܕܢܕܪܟܘܢ ܚܝܐ ܫܪܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 20, 'ܐܘ ܛܝܡܬܐܐ ܐܙܕܗܪ ܒܡܕܡ ܕܐܬܓܥܠ ܠܟ ܘܥܪܘܩ ܡܢ ܒܢܬ ܩܠܐ ܤܪܝܩܬܐ ܘܡܢ ܗܦܟܬܐ ܕܝܕܥܬܐ ܕܓܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 21, 'ܐܝܠܝܢ ܓܝܪ ܕܡܫܬܘܕܝܢ ܠܗ ܛܥܘ ܠܗܘܢ ܡܢ ܗܝܡܢܘܬܐ ܛܝܒܘܬܐ ܥܡܟ ܐܡܝܢ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 1, 'ܦܘܠܘܤ ܫܠܝܚܐ ܕܝܫܘܥ ܡܫܝܚܐ ܒܨܒܝܢܗ ܕܐܠܗܐ ܘܒܡܘܠܟܢܐ ܕܚܝܐ ܕܒܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 2, 'ܠܛܝܡܬܐܘܤ ܒܪܐ ܚܒܝܒܐ ܛܝܒܘܬܐ ܘܪܚܡܐ ܘܫܠܡܐ ܡܢ ܐܠܗܐ ܐܒܐ ܘܡܢ ܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 3, 'ܡܘܕܐ ܐܢܐ ܠܐܠܗܐ ܗܘ ܕܠܗ ܡܫܡܫ ܐܢܐ ܡܢ ܐܒܗܬܝ ܒܬܐܪܬܐ ܕܟܝܬܐ ܕܐܡܝܢܐܝܬ ܡܬܕܟܪ ܐܢܐ ܠܟ ܒܨܠܘܬܝ ܕܠܠܝܐ ܘܕܐܝܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 4, 'ܘܤܘܐ ܐܢܐ ܠܡܚܙܝܟ ܘܡܬܕܟܪ ܐܢܐ ܕܡܥܝܟ ܕܐܬܡܠܐ ܚܕܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 5, 'ܒܥܘܗܕܢܐ ܕܗܘܐ ܠܝ ܒܗܝܡܢܘܬܟ ܫܪܝܪܬܐ ܗܝ ܕܫܪܬ ܠܘܩܕܡ ܒܐܡܐ ܕܐܡܟ ܠܘܐܝܤ ܘܒܐܡܟ ܐܘܢܝܩܐ ܡܦܤ ܐܢܐ ܕܝܢ ܕܐܦ ܒܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 6, 'ܡܛܠ ܗܢܐ ܡܥܗܕ ܐܢܐ ܠܟ ܕܬܥܝܪ ܡܘܗܒܬܐ ܕܐܠܗܐ ܗܝ ܕܐܝܬ ܒܟ ܒܤܝܡ ܐܝܕܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 7, 'ܠܐ ܓܝܪ ܝܗܒ ܠܢ ܐܠܗܐ ܪܘܚܐ ܕܕܚܠܬܐ ܐܠܐ ܕܚܝܠܐ ܘܕܚܘܒܐ ܘܕܡܪܬܝܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 8, 'ܠܐ ܗܟܝܠ ܬܒܗܬ ܒܤܗܕܘܬܗ ܕܡܪܢ ܐܦܠܐ ܒܝ ܒܐܤܝܪܗ ܐܠܐ ܫܩܘܠ ܒܝܫܬܐ ܥܡ ܐܘܢܓܠܝܘܢ ܒܚܝܠܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 9, 'ܗܘ ܕܐܚܝܢ ܘܩܪܢ ܒܩܪܝܢܐ ܩܕܝܫܐ ܠܘ ܐܝܟ ܥܒܕܝܢ ܐܠܐ ܐܝܟ ܨܒܝܢܗ ܘܛܝܒܘܬܗ ܗܝ ܕܐܬܝܗܒܬ ܠܢ ܒܝܫܘܥ ܡܫܝܚܐ ܡܢ ܩܕܡ ܙܒܢܐ ܕܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 10, 'ܘܐܬܓܠܝܬ ܗܫܐ ܒܓܠܝܢܗ ܕܡܚܝܢܢ ܝܫܘܥ ܡܫܝܚܐ ܕܒܛܠ ܠܡܘܬܐ ܘܚܘܝ ܚܝܐ ܘܠܐ ܡܬܚܒܠܢܘܬܐ ܒܐܘܢܓܠܝܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 11, 'ܗܘ ܕܐܬܬܤܝܡܬ ܒܗ ܐܢܐ ܟܪܘܙܐ ܘܫܠܝܚܐ ܘܡܠܦܢܐ ܕܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 12, 'ܡܛܠ ܗܕܐ ܡܤܝܒܪ ܐܢܐ ܗܠܝܢ ܘܠܐ ܒܗܬ ܐܢܐ ܝܕܥ ܐܢܐ ܓܝܪ ܒܡܢ ܗܝܡܢܬ ܘܡܦܤ ܐܢܐ ܕܡܛܝܐ ܒܐܝܕܘܗܝ ܓܘܥܠܢܝ ܠܡܛܪ ܠܝ ܠܝܘܡܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 13, 'ܢܗܘܝܢ ܠܟ ܚܘܪܐ ܡܠܐ ܚܠܝܡܬܐ ܕܫܡܥܬ ܡܢܝ ܒܗܝܡܢܘܬܐ ܘܒܚܘܒܐ ܕܒܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 14, 'ܓܘܥܠܢܐ ܛܒܐ ܛܪ ܒܪܘܚܐ ܕܩܘܕܫܐ ܗܝ ܕܥܡܪܬ ܒܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 15, 'ܝܕܥ ܐܢܬ ܗܕܐ ܕܗܦܟܘ ܡܢܝ ܟܠܗܘܢ ܗܠܝܢ ܕܒܐܤܝܐ ܕܐܝܬܝܗܘܢ ܡܢܗܘܢ ܦܘܓܠܘܤ ܘܗܪܡܓܢܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 16, 'ܢܬܠ ܡܪܢ ܪܚܡܐ ܠܒܝܬܗ ܕܐܢܤܝܦܘܪܘܤ ܕܙܒܢܝܢ ܤܓܝܐܢ ܐܢܝܚܢܝ ܘܒܫܫܠܬܐ ܕܐܤܘܪܝ ܠܐ ܒܗܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 17, 'ܐܠܐ ܐܦ ܟܕ ܐܬܐ ܠܪܗܘܡܝ ܒܚܦܝܛܘܬܐ ܒܥܢܝ ܘܐܫܟܚܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 18, 'ܢܬܠ ܠܗ ܡܪܢ ܕܢܫܟܚ ܪܚܡܐ ܠܘܬ ܡܪܢ ܒܝܘܡܐ ܗܘ ܘܟܡܐ ܕܫܡܫܢܝ ܒܐܦܤܘܤ ܝܬܝܪܐܝܬ ܝܕܥ ܐܢܬ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 1, 'ܐܢܬ ܗܟܝܠ ܒܪܝ ܐܬܚܝܠ ܒܛܝܒܘܬܐ ܕܒܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 2, 'ܘܐܝܠܝܢ ܕܫܡܥܬ ܡܢܝ ܒܝܕ ܤܗܕܐ ܤܓܝܐܐ ܗܢܝܢ ܐܓܥܠ ܠܐܢܫܐ ܡܗܝܡܢܐ ܐܝܠܝܢ ܕܡܛܝܐ ܒܐܝܕܝܗܘܢ ܐܦ ܠܐܚܪܢܐ ܠܡܠܦܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 3, 'ܘܤܝܒܪ ܒܝܫܬܐ ܐܝܟ ܦܠܚܐ ܛܒܐ ܕܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 4, 'ܠܐ ܐܢܫ ܦܠܚ ܘܡܬܦܟܪ ܒܨܒܘܬܐ ܕܥܠܡܐ ܕܢܫܦܪ ܠܗܘ ܡܢ ܕܓܒܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 5, 'ܘܐܢ ܡܬܟܬܫ ܐܢܫ ܠܐ ܡܬܟܠܠ ܐܢ ܒܢܡܘܤܗ ܠܐ ܡܬܟܬܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 6, 'ܠܐܟܪܐ ܕܠܐܐ ܘܠܐ ܠܗ ܕܩܕܡܐ ܡܢ ܦܐܪܘܗܝ ܢܤܬܝܒܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 7, 'ܐܤܬܟܠ ܡܕܡ ܕܐܡܪ ܐܢܐ ܢܬܠ ܠܟ ܡܪܢ ܚܟܡܬܐ ܒܟܠ ܡܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 8, 'ܐܬܕܟܪ ܠܝܫܘܥ ܡܫܝܚܐ ܕܩܡ ܡܢ ܒܝܬ ܡܝܬܐ ܗܘ ܕܐܝܬܘܗܝ ܡܢ ܙܪܥܐ ܕܕܘܝܕ ܐܝܟ ܐܘܢܓܠܝܘܢ ܕܝܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 9, 'ܕܒܗ ܤܒܠ ܐܢܐ ܒܝܫܬܐ ܥܕܡܐ ܠܐܤܘܪܐ ܐܝܟ ܥܒܕ ܒܝܫܬܐ ܐܠܐ ܡܠܬܗ ܕܐܠܗܐ ܠܐ ܐܤܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 10, 'ܡܛܠ ܗܢܐ ܟܠܡܕܡ ܡܤܝܒܪ ܐܢܐ ܡܛܠ ܓܒܝܐ ܕܐܦ ܗܢܘܢ ܢܫܟܚܘܢ ܚܝܐ ܕܒܝܫܘܥ ܡܫܝܚܐ ܥܡ ܫܘܒܚܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 11, 'ܡܗܝܡܢܐ ܗܝ ܡܠܬܐ ܐܢ ܓܝܪ ܡܝܬܢ ܥܡܗ ܐܦ ܥܡܗ ܢܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 12, 'ܘܐܢ ܢܤܝܒܪ ܐܦ ܢܡܠܟ ܥܡܗ ܐܢ ܕܝܢ ܢܟܦܘܪ ܒܗ ܐܦ ܗܘ ܢܟܦܘܪ ܒܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 13, 'ܘܐܢ ܠܐ ܢܗܝܡܢ ܒܗ ܗܘ ܒܗܝܡܢܘܬܗ ܡܩܘܐ ܕܢܟܦܘܪ ܓܝܪ ܒܢܦܫܗ ܠܐ ܡܫܟܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 14, 'ܗܠܝܢ ܗܘܝܬ ܡܥܗܕ ܠܗܘܢ ܘܡܤܗܕ ܩܕܡ ܡܪܢ ܕܠܐ ܢܗܘܘܢ ܡܬܚܪܝܢ ܒܡܠܐ ܕܠܐ ܝܘܬܪܢ ܠܤܘܚܦܐ ܕܐܝܠܝܢ ܕܫܡܥܝܢ ܠܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 15, 'ܘܢܬܒܛܠ ܠܟ ܕܬܩܝܡ ܢܦܫܟ ܓܡܝܪܐܝܬ ܩܕܡ ܐܠܗܐ ܦܠܚܐ ܕܠܐ ܒܗܬܬܐ ܕܡܟܪܙ ܬܪܝܨܐܝܬ ܡܠܬܐ ܕܫܪܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 16, 'ܘܡܢ ܡܠܐ ܤܪܝܩܬܐ ܕܠܝܬ ܒܗܝܢ ܚܫܚܘ ܐܫܬܐܠ ܝܬܝܪܐܝܬ ܓܝܪ ܢܘܤܦܘܢ ܥܠ ܪܘܫܥܗܘܢ ܗܢܘܢ ܕܥܢܝܢ ܒܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 17, 'ܘܡܠܬܗܘܢ ܐܝܟ ܚܠܕܝܬܐ ܢܘܡܐ ܬܐܚܘܕ ܒܤܓܝܐܐ ܐܝܬܘܗܝ ܕܝܢ ܚܕ ܡܢܗܘܢ ܗܘܡܢܐܘܤ ܘܐܚܪܢܐ ܦܝܠܛܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 18, 'ܗܠܝܢ ܕܛܥܘ ܡܢ ܫܪܪܐ ܟܕ ܐܡܪܝܢ ܕܩܝܡܬܐ ܕܡܝܬܐ ܗܘܬ ܠܗ ܘܗܝܡܢܘܬܐ ܕܐܢܫ ܐܢܫ ܡܗܦܟܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 19, 'ܫܬܐܤܬܐ ܕܝܢ ܫܪܝܪܬܐ ܕܐܠܗܐ ܩܝܡܐ ܗܝ ܘܐܝܬ ܠܗ ܚܬܡܐ ܗܢܐ ܘܝܕܥ ܡܪܝܐ ܠܐܝܠܝܢ ܕܕܝܠܗ ܐܢܘܢ ܘܢܦܪܘܩ ܡܢ ܥܘܠܐ ܟܠ ܕܩܪܐ ܠܫܡܗ ܕܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 20, 'ܒܒܝܬܐ ܕܝܢ ܪܒܐ ܠܐ ܗܘܐ ܡܐܢܐ ܕܕܗܒܐ ܒܠܚܘܕ ܐܘ ܕܤܐܡܐ ܐܝܬ ܒܗ ܐܠܐ ܐܦ ܕܩܝܤܐ ܐܦ ܕܦܚܪܐ ܡܢܗܘܢ ܠܐܝܩܪܐ ܘܡܢܗܘܢ ܠܨܥܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 21, 'ܐܢ ܐܢܫ ܗܟܝܠ ܢܕܟܐ ܢܦܫܗ ܡܢ ܗܠܝܢ ܗܘܐ ܡܐܢܐ ܕܟܝܐ ܠܐܝܩܪܐ ܕܥܗܢ ܠܚܘܫܚܐ ܕܡܪܗ ܘܡܛܝܒ ܠܟܠ ܥܒܕ ܛܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 22, 'ܡܢ ܟܠܗܝܢ ܪܓܝܓܬܐ ܕܛܠܝܘܬܐ ܥܪܘܩ ܘܗܪܛ ܒܬܪ ܟܐܢܘܬܐ ܘܗܝܡܢܘܬܐ ܘܚܘܒܐ ܘܫܠܡܐ ܥܡ ܐܝܠܝܢ ܕܩܪܝܢ ܠܡܪܢ ܒܠܒܐ ܕܟܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 23, 'ܚܪܝܢܐ ܤܟܠܐ ܐܝܠܝܢ ܕܕܠܐ ܡܪܕܘ ܐܢܘܢ ܐܫܬܐܠ ܡܢܗܘܢ ܝܕܥ ܐܢܬ ܓܝܪ ܕܬܟܬܘܫܐ ܡܘܠܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 24, 'ܥܒܕܗ ܕܝܢ ܕܡܪܢ ܠܐ ܚܝܒ ܠܡܬܟܬܫܘ ܐܠܐ ܕܢܗܘܐ ܡܟܝܟ ܠܘܬ ܟܠܢܫ ܘܡܠܦܢ ܘܢܓܝܪܐ ܪܘܚܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 25, 'ܕܢܗܘܐ ܪܕܐ ܠܐܝܠܝܢ ܕܡܬܚܪܝܢ ܠܩܘܒܠܗ ܒܡܟܝܟܘܬܐ ܕܠܡܐ ܢܬܠ ܠܗܘܢ ܐܠܗܐ ܬܝܒܘܬܐ ܘܢܕܥܘܢ ܫܪܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 26, 'ܘܢܥܗܕܘܢ ܢܦܫܗܘܢ ܘܢܦܪܩܘܢ ܡܢ ܦܚܗ ܕܤܛܢܐ ܕܒܗ ܐܬܬܨܝܕܘ ܠܨܒܝܢܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 1, 'ܗܕܐ ܕܝܢ ܗܘܝܬ ܝܕܥ ܕܒܝܘܡܬܐ ܐܚܪܝܐ ܢܐܬܘܢ ܙܒܢܐ ܩܫܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 2, 'ܘܢܗܘܘܢ ܒܢܝܢܫܐ ܪܚܡܝ ܢܦܫܗܘܢ ܘܪܚܡܝ ܟܤܦܐ ܫܒܗܪܢܐ ܪܡܐ ܡܓܕܦܢܐ ܕܠܐܢܫܝܗܘܢ ܠܐ ܡܬܛܦܝܤܝܢ ܟܦܪܝ ܒܛܝܒܘܬܐ ܪܫܝܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 3, 'ܐܟܠܝ ܩܪܨܐ ܡܫܥܒܕܝ ܠܪܓܬܐ ܒܥܪܝܪܝܐ ܤܢܝܝ ܛܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 4, 'ܡܫܠܡܢܐ ܡܤܪܗܒܐ ܚܬܝܪܐ ܪܚܡܝ ܪܓܝܓܬܐ ܛܒ ܡܢ ܚܘܒܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 5, 'ܕܐܝܬ ܠܗܘܢ ܐܤܟܡܐ ܕܕܚܠܬ ܐܠܗܐ ܘܡܢ ܚܝܠܗ ܪܚܝܩܝܢ ܐܝܠܝܢ ܕܗܟܢܐ ܐܢܘܢ ܕܚܘܩ ܐܢܘܢ ܡܢܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 6, 'ܡܢܗܘܢ ܐܢܘܢ ܓܝܪ ܗܠܝܢ ܕܡܚܠܕܝܢ ܒܝܬ ܒܬܐ ܘܫܒܝܢ ܢܫܐ ܕܛܡܝܪܢ ܒܚܛܗܐ ܘܡܬܕܒܪܢ ܠܪܓܝܓܬܐ ܡܫܚܠܦܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 7, 'ܕܒܟܠܙܒܢ ܝܠܦܢ ܘܡܡܬܘܡ ܠܝܕܥܬܐ ܕܫܪܪܐ ܠܡܐܬܐ ܠܐ ܡܫܟܚܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 8, 'ܐܝܟܢܐ ܕܝܢ ܕܝܢܤ ܘܝܡܒܪܝܤ ܩܡܘ ܠܘܩܒܠ ܡܘܫܐ ܗܟܢܐ ܐܦ ܗܢܘܢ ܩܝܡܝܢ ܠܘܩܒܠ ܫܪܪܐ ܐܢܫܐ ܕܡܚܒܠ ܪܥܝܢܗܘܢ ܘܤܠܝܢ ܡܢ ܗܝܡܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 9, 'ܐܠܐ ܠܐ ܢܐܬܘܢ ܠܩܕܡܝܗܘܢ ܫܛܝܘܬܗܘܢ ܓܝܪ ܡܬܝܕܥܐ ܗܝ ܠܟܠܢܫ ܐܝܟܢܐ ܕܐܦ ܕܗܢܘܢ ܐܬܝܕܥܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 10, 'ܐܢܬ ܕܝܢ ܐܬܝܬ ܒܬܪ ܝܘܠܦܢܝ ܘܒܬܪ ܕܘܒܪܝ ܘܒܬܪ ܨܒܝܢܝ ܘܒܬܪ ܗܝܡܢܘܬܝ ܘܒܬܪ ܢܓܝܪܘܬ ܪܘܚܝ ܘܒܬܪ ܚܘܒܝ ܘܒܬܪ ܡܤܝܒܪܢܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 11, 'ܘܒܬܪ ܪܕܝܦܘܬܝ ܘܒܬܪ ܚܫܝ ܘܝܕܥ ܐܢܬ ܐܝܠܝܢ ܤܝܒܪܬ ܒܐܢܛܝܟܝܐ ܘܒܐܝܩܢܘܢ ܘܒܠܘܤܛܪܐ ܐܝܕܐ ܪܕܝܦܘܬܐ ܤܝܒܪܬ ܘܡܢ ܟܠܗܝܢ ܦܨܝܢܝ ܡܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 12, 'ܘܟܠܗܘܢ ܕܝܢ ܐܝܠܝܢ ܕܨܒܝܢ ܒܕܚܠܬ ܐܠܗܐ ܕܢܚܘܢ ܒܝܫܘܥ ܡܫܝܚܐ ܡܬܪܕܦܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 13, 'ܒܢܝܢܫܐ ܕܝܢ ܒܝܫܐ ܘܡܛܥܝܢܐ ܢܘܤܦܘܢ ܥܠ ܒܝܫܘܬܗܘܢ ܟܕ ܛܥܝܢ ܘܡܛܥܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 14, 'ܐܢܬ ܕܝܢ ܩܘܐ ܒܐܝܠܝܢ ܕܝܠܦܬ ܘܐܫܬܪܪܬ ܝܕܥ ܐܢܬ ܓܝܪ ܡܢ ܡܢܘ ܝܠܦܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 15, 'ܘܕܡܢ ܛܠܝܘܬܟ ܤܦܪܐ ܩܕܝܫܐ ܝܠܝܦ ܐܢܬ ܕܡܫܟܚܝܢ ܕܢܚܟܡܘܢܟ ܠܚܝܐ ܒܗܝܡܢܘܬܐ ܕܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 16, 'ܟܠ ܟܬܒ ܕܒܪܘܚܐ ܐܬܟܬܒ ܡܘܬܪܢܐ ܗܘ ܠܝܘܠܦܢܐ ܘܠܟܘܘܢܐ ܘܠܬܘܪܨܐ ܘܠܡܪܕܘܬܐ ܕܒܟܐܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 17, 'ܕܢܗܘܐ ܓܡܝܪ ܒܪܢܫܐ ܕܐܠܗܐ ܘܠܟܠ ܥܒܕ ܛܒ ܡܫܠܡܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 1, 'ܡܤܗܕ ܐܢܐ ܠܟ ܩܕܡ ܐܠܗܐ ܘܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܗܘ ܕܥܬܝܕ ܠܡܕܢ ܚܝܐ ܘܡܝܬܐ ܒܓܠܝܢܐ ܕܡܠܟܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 2, 'ܐܟܪܙ ܡܠܬܐ ܘܩܘܡ ܒܚܦܝܛܘܬܐ ܒܙܒܢܐ ܘܕܠܐ ܙܒܢܐ ܐܟܤ ܘܟܘܢ ܒܟܠܗ ܡܓܪܬ ܪܘܚܐ ܘܝܘܠܦܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 3, 'ܢܗܘܐ ܓܝܪ ܙܒܢܐ ܕܠܝܘܠܦܢܐ ܚܠܝܡܐ ܠܐ ܢܫܬܡܥܘܢ ܐܠܐ ܐܝܟ ܪܓܝܓܬܗܘܢ ܢܤܓܘܢ ܠܢܦܫܗܘܢ ܡܠܦܢܐ ܒܚܘܬܚܬܐ ܕܡܫܡܥܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 4, 'ܘܡܢ ܫܪܪܐ ܢܗܦܟܘܢ ܐܕܢܗܘܢ ܠܫܘܥܝܬܐ ܕܝܢ ܢܤܛܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 5, 'ܐܢܬ ܕܝܢ ܗܘܝܬ ܥܝܪ ܒܟܠܡܕܡ ܘܤܝܒܪ ܒܝܫܬܐ ܘܥܒܕܐ ܥܒܕ ܕܡܤܒܪܢܐ ܘܬܫܡܫܬܟ ܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 6, 'ܐܢܐ ܓܝܪ ܡܟܝܠ ܡܬܢܩܐ ܐܢܐ ܘܙܒܢܐ ܕܐܫܬܪܐ ܡܛܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 7, 'ܐܓܘܢܐ ܫܦܝܪܐ ܐܬܟܬܫܬ ܘܪܗܛܝ ܫܠܡܬ ܘܗܝܡܢܘܬܝ ܢܛܪܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 8, 'ܘܡܢ ܗܫܐ ܢܛܝܪ ܠܝ ܟܠܝܠܐ ܕܟܐܢܘܬܐ ܕܢܦܪܥܝܘܗܝ ܠܝ ܡܪܝ ܒܝܘܡܐ ܗܘ ܕܗܘܝܘ ܕܝܢܐ ܟܐܢܐ ܠܐ ܕܝܢ ܒܠܚܘܕ ܠܝ ܐܠܐ ܐܦ ܠܐܝܠܝܢ ܕܐܚܒܘ ܠܓܠܝܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 9, 'ܢܬܒܛܠ ܠܟ ܕܬܐܬܐ ܠܘܬܝ ܒܥܓܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 10, 'ܕܡܐ ܓܝܪ ܫܒܩܢܝ ܘܐܚܒ ܥܠܡܐ ܗܢܐ ܘܐܙܠ ܠܗ ܠܬܤܠܘܢܝܩܐ ܩܪܤܩܘܤ ܠܓܠܛܝܐ ܛܛܘܤ ܠܕܠܡܛܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 11, 'ܠܘܩܐ ܗܘ ܒܠܚܘܕܘܗܝ ܥܡܝ ܠܡܪܩܘܤ ܕܒܪ ܘܐܝܬܝܗܝ ܥܡܟ ܥܗܢ ܠܝ ܓܝܪ ܠܬܫܡܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 12, 'ܠܛܘܟܝܩܘܤ ܕܝܢ ܫܕܪܬ ܠܐܦܤܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 13, 'ܒܝܬ ܟܬܒܐ ܕܝܢ ܕܫܒܩܬ ܒܛܪܘܐܘܤ ܠܘܬ ܩܪܦܘܤ ܡܐ ܕܐܬܐ ܐܢܬ ܐܝܬܝܗܝ ܘܟܬܒܐ ܝܬܝܪܐܝܬ ܟܪܟܐ ܕܡܓܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 14, 'ܐܠܟܤܢܕܪܤ ܩܝܢܝܐ ܒܝܫܬܐ ܤܓܝܐܬܐ ܚܘܝܢܝ ܦܪܥ ܠܗ ܡܪܢ ܐܝܟ ܥܒܕܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 15, 'ܐܦ ܐܢܬ ܕܝܢ ܐܙܕܗܪ ܡܢܗ ܛܒ ܓܝܪ ܙܩܝܦ ܠܘܩܒܠ ܡܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 16, 'ܒܡܦܩ ܒܪܘܚܝ ܩܕܡܝܐ ܠܐ ܐܢܫ ܗܘܐ ܥܡܝ ܐܠܐ ܟܠܗܘܢ ܫܒܩܘܢܝ ܠܐ ܬܬܚܫܒ ܠܗܘܢ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 17, 'ܡܪܝ ܕܝܢ ܩܡ ܠܝ ܘܚܝܠܢܝ ܕܒܝ ܟܪܘܙܘܬܐ ܬܫܬܠܡ ܘܢܫܡܥܘܢ ܥܡܡܐ ܟܠܗܘܢ ܕܐܬܦܨܝܬ ܡܢ ܦܘܡܐ ܕܐܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 18, 'ܘܢܦܨܝܢܝ ܡܪܝ ܡܢ ܟܠ ܥܒܕ ܒܝܫ ܘܢܚܝܢܝ ܒܡܠܟܘܬܗ ܕܒܫܡܝܐ ܕܠܗ ܫܘܒܚܐ ܠܥܠܡ ܥܠܡܝܢ ܐܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 19, 'ܗܒ ܫܠܡܐ ܠܦܪܝܤܩܠܐ ܘܠܐܩܠܘܤ ܘܠܒܝܬܐ ܕܐܢܤܝܦܘܪܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 20, 'ܐܪܤܛܘܤ ܦܫ ܠܗ ܒܩܘܪܢܬܘܤ ܛܪܘܦܝܡܘܤ ܕܝܢ ܫܒܩܬܗ ܟܕ ܟܪܝܗ ܒܡܝܠܝܛܘܤ ܡܕܝܢܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 21, 'ܢܬܒܛܠ ܠܟ ܕܩܕܡ ܤܬܘܐ ܬܐܬܐ ܫܐܠ ܒܫܠܡܟ ܐܘܒܘܠܘܤ ܘܦܘܕܤ ܘܠܝܢܘܤ ܘܩܠܘܕܝܐ ܘܐܚܐ ܟܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 22, 'ܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܥܡ ܪܘܚܟ ܛܝܒܘܬܐ ܥܡܟ ܐܡܝܢ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 1, 'ܦܘܠܘܤ ܥܒܕܗ ܕܐܠܗܐ ܘܫܠܝܚܗ ܕܝܫܘܥ ܡܫܝܚܐ ܒܗܝܡܢܘܬܐ ܕܓܒܝܐ ܕܐܠܗܐ ܘܝܕܥܬܐ ܕܫܪܪܐ ܕܒܕܚܠܬ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 2, 'ܥܠ ܤܒܪܐ ܕܚܝܐ ܕܠܥܠܡ ܕܡܠܟ ܐܠܗܐ ܫܪܝܪܐ ܩܕܡ ܙܒܢܘܗܝ ܕܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 3, 'ܘܓܠܐ ܡܠܬܗ ܒܙܒܢܗ ܒܝܕ ܟܪܘܙܘܬܢ ܗܝ ܕܐܬܗܝܡܢܬ ܐܢܐ ܒܦܘܩܕܢܐ ܕܐܠܗܐ ܡܚܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 4, 'ܠܛܛܘܤ ܒܪܐ ܫܪܝܪܐ ܒܗܝܡܢܘܬܐ ܕܓܘܐ ܛܝܒܘܬܐ ܘܫܠܡܐ ܡܢ ܐܠܗܐ ܐܒܐ ܘܡܢ ܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܡܚܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 5, 'ܡܛܠܗܢܐ ܗܘ ܫܒܩܬܟ ܗܘܝܬ ܒܩܪܛܐ ܕܐܝܠܝܢ ܕܚܤܝܪܢ ܬܬܩܢ ܘܬܩܝܡ ܩܫܝܫܐ ܒܡܕܝܢܐ ܡܕܝܢܐ ܐܝܟܢܐ ܕܦܩܕܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 6, 'ܐܝܢܐ ܕܕܠܐ ܪܫܝܢ ܗܘ ܘܗܘܐ ܒܥܠܐ ܕܚܕܐ ܐܢܬܬܐ ܘܐܝܬ ܠܗ ܒܢܝܐ ܡܗܝܡܢܐ ܕܠܐ ܡܨܚܝܢ ܘܠܐ ܐܝܬܝܗܘܢ ܕܠܐ ܫܘܥܒܕ ܒܐܤܘܛܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 7, 'ܚܝܒ ܗܘ ܓܝܪ ܩܫܝܫܐ ܕܢܗܘܐ ܕܠܐ ܪܫܝܢ ܐܝܟ ܪܒܝܬܐ ܕܐܠܗܐ ܘܠܐ ܢܗܘܐ ܡܬܕܒܪ ܒܪܥܝܢ ܢܦܫܗ ܘܠܐ ܢܗܘܐ ܚܡܬܢ ܘܠܐ ܢܗܘܐ ܥܒܪ ܥܠ ܚܡܪܐ ܘܠܐ ܬܗܘܐ ܪܗܛܐ ܐܝܕܗ ܠܡܡܚܐ ܘܠܐ ܢܗܘܐ ܪܚܡ ܝܘܬܪܢܐ ܛܢܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 8, 'ܐܠܐ ܢܗܘܐ ܪܚܡ ܐܟܤܢܝܐ ܘܢܗܘܐ ܪܚܡ ܛܒܬܐ ܘܢܗܘܐ ܢܟܦ ܘܢܗܘܐ ܟܐܝܢ ܘܢܗܘܐ ܚܤܝܐ ܘܠܒܝܟ ܢܦܫܗ ܡܢ ܪܓܝܓܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 9, 'ܘܒܛܝܠ ܠܗ ܥܠ ܝܘܠܦܢܐ ܕܡܠܬܐ ܕܗܝܡܢܘܬܐ ܕܢܫܟܚ ܐܦ ܠܡܒܝܐܘ ܒܝܘܠܦܢܗ ܚܠܝܡܐ ܘܠܡܟܤܘ ܠܐܝܠܝܢ ܕܡܬܚܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 10, 'ܐܝܬ ܓܝܪ ܤܓܝܐܐ ܕܠܐ ܡܫܬܥܒܕܝܢ ܘܤܪܝܩܢ ܡܠܝܗܘܢ ܘܡܛܥܝܢ ܪܥܝܢܐ ܕܒܢܝܢܫܐ ܝܬܝܪܐܝܬ ܐܝܠܝܢ ܕܡܢ ܓܙܘܪܬܐ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 11, 'ܗܢܘܢ ܕܘܠܐ ܠܡܤܟܪܘ ܦܘܡܗܘܢ ܒܬܐ ܓܝܪ ܤܓܝܐܐ ܡܚܒܠܝܢ ܘܡܠܦܝܢ ܡܕܡ ܕܠܐ ܘܠܐ ܡܛܠ ܝܘܬܪܢܐ ܛܢܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 12, 'ܐܡܪ ܐܢܫ ܡܢܗܘܢ ܢܒܝܐ ܕܝܠܗܘܢ ܕܒܢܝ ܩܪܛܐ ܒܟܠܙܒܢ ܕܓܠܝܢ ܐܢܘܢ ܚܝܘܬܐ ܒܝܫܬܐ ܘܟܪܤܬܐ ܒܛܝܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 13, 'ܘܫܪܝܪܐܝܬ ܐܝܬܝܗ ܤܗܕܘܬܐ ܗܕܐ ܡܛܠ ܗܢܐ ܩܫܝܐܝܬ ܗܘܝܬ ܡܟܤ ܠܗܘܢ ܕܢܗܘܘܢ ܚܠܝܡܝܢ ܒܗܝܡܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 14, 'ܘܠܐ ܢܬܪܡܘܢ ܠܫܘܥܝܬܐ ܕܝܗܘܕܝܐ ܘܠܦܘܩܕܐ ܕܒܢܝܢܫܐ ܕܤܢܝܢ ܫܪܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 15, 'ܟܠܡܕܡ ܓܝܪ ܕܟܐ ܗܘ ܠܕܟܝܐ ܠܐܝܠܝܢ ܕܝܢ ܕܡܤܝܒܝܢ ܘܠܐ ܡܗܝܡܢܝܢ ܡܕܡ ܕܕܟܐ ܠܝܬ ܠܗܘܢ ܐܠܐ ܡܤܝܒ ܗܘ ܪܥܝܢܗܘܢ ܘܬܐܪܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 16, 'ܘܡܘܕܝܢ ܕܝܕܥܝܢ ܠܐܠܗܐ ܒܥܒܕܝܗܘܢ ܕܝܢ ܟܦܪܝܢ ܒܗ ܘܐܝܬܝܗܘܢ ܤܢܝܐܐ ܘܕܠܐ ܦܝܤ ܘܡܤܠܝܐ ܠܟܠ ܥܒܕ ܛܒ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 1, 'ܐܢܬ ܕܝܢ ܡܠܠ ܡܕܡ ܕܝܐܐ ܠܝܘܠܦܢܐ ܚܠܝܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 2, 'ܘܐܠܦ ܕܢܗܘܘܢ ܩܫܝܫܐ ܥܝܪܝܢ ܒܪܥܝܢܝܗܘܢ ܘܢܗܘܘܢ ܢܟܦܝܢ ܘܢܗܘܘܢ ܕܟܝܢ ܘܢܗܘܘܢ ܚܠܝܡܝܢ ܒܗܝܡܢܘܬܐ ܘܒܚܘܒܐ ܘܒܡܤܝܒܪܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 3, 'ܘܐܦ ܩܫܝܫܬܐ ܗܟܢܐ ܕܢܗܘܝܢ ܒܐܤܟܡܐ ܕܝܐܐ ܠܕܚܠܬ ܐܠܗܐ ܘܠܐ ܢܗܘܝܢ ܐܟܠܢ ܩܪܨܐ ܘܠܐ ܢܗܘܝܢ ܡܫܥܒܕܢ ܠܚܡܪܐ ܤܓܝܐܐ ܘܢܗܘܝܢ ܡܠܦܢ ܫܦܝܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 4, 'ܘܡܢܟܦܢ ܠܐܝܠܝܢ ܕܛܠܝܢ ܕܢܪܚܡܢ ܒܥܠܝܗܝܢ ܘܒܢܝܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 5, 'ܘܢܗܘܝܢ ܢܟܦܢ ܘܩܕܝܫܢ ܘܢܗܘܝܢ ܝܨܦܢ ܫܦܝܪ ܕܒܬܝܗܝܢ ܘܡܫܬܥܒܕܢ ܠܒܥܠܝܗܝܢ ܕܠܐ ܐܢܫ ܢܓܕܦ ܥܠ ܡܠܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 6, 'ܘܡܢ ܐܝܠܝܢ ܕܛܠܝܢ ܗܟܢܐ ܒܥܝ ܕܢܗܘܘܢ ܢܟܦܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 7, 'ܒܟܠܡܕܡ ܕܝܢ ܕܡܘܬܐ ܒܢܦܫܟ ܚܘܐ ܒܟܠ ܥܒܕܐ ܛܒܐ ܘܒܡܠܦܢܘܬܟ ܬܗܘܐ ܠܟ ܡܠܬܐ ܚܠܝܡܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 8, 'ܕܢܟܦܐ ܘܠܐ ܡܚܒܠܐ ܘܠܐ ܐܢܫ ܒܤܪ ܠܗ ܕܐܝܢܐ ܕܩܐܡ ܠܩܘܒܠܢ ܢܒܗܬ ܟܕ ܠܐ ܢܫܟܚ ܕܢܐܡܪ ܥܠܝܢ ܡܕܡ ܕܤܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 9, 'ܥܒܕܐ ܠܡܪܝܗܘܢ ܢܫܬܥܒܕܘܢ ܒܟܠܡܕܡ ܘܢܫܬܦܪܘܢ ܘܠܐ ܗܘܘ ܡܥܨܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 10, 'ܘܠܐ ܗܘܘ ܓܢܒܝܢ ܐܠܐ ܢܚܘܘܢ ܫܪܪܗܘܢ ܛܒܐ ܒܟܠܡܕܡ ܕܢܨܒܬܘܢ ܒܟܠܡܕܡ ܝܘܠܦܢܗ ܕܐܠܗܐ ܡܚܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 11, 'ܐܬܓܠܝܬ ܓܝܪ ܛܝܒܘܬܗ ܕܐܠܗܐ ܡܚܝܬ ܟܠ ܠܒܢܝܢܫܐ ܟܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 12, 'ܘܪܕܝܐ ܠܢ ܕܢܟܦܘܪ ܒܪܘܫܥܐ ܘܒܪܓܝܓܬܐ ܕܥܠܡܐ ܘܢܚܐ ܒܥܠܡܐ ܗܢܐ ܒܢܟܦܘܬܐ ܘܒܟܐܢܘܬܐ ܘܒܕܚܠܬ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 13, 'ܟܕ ܡܤܟܝܢܢ ܠܤܒܪܐ ܒܪܝܟܐ ܘܠܓܠܝܢܐ ܕܬܫܒܘܚܬܗ ܕܐܠܗܐ ܪܒܐ ܘܡܚܝܢܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 14, 'ܕܗܘ ܝܗܒ ܢܦܫܗ ܚܠܦܝܢ ܕܢܦܪܩܢ ܡܢ ܟܠ ܥܘܠܐ ܘܢܕܟܝܢ ܠܢܦܫܗ ܥܡܐ ܚܕܬܐ ܕܚܤܡ ܒܥܒܕܐ ܛܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 15, 'ܗܠܝܢ ܡܠܠ ܘܒܥܝ ܘܟܘܢ ܒܟܠ ܦܘܩܕܢ ܘܠܐ ܐܢܫ ܢܒܤܐ ܒܟ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 1, 'ܘܗܘܝܬ ܡܥܗܕ ܠܗܘܢ ܕܠܪܫܐ ܘܠܫܠܝܛܢܐ ܢܫܬܡܥܘܢ ܘܢܫܬܥܒܕܘܢ ܘܕܢܗܘܘܢ ܥܬܝܕܝܢ ܠܟܠ ܥܒܕ ܛܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 2, 'ܘܥܠ ܐܢܫ ܠܐ ܢܓܕܦܘܢ ܘܠܐ ܢܗܘܘܢ ܡܬܟܬܫܝܢ ܐܠܐ ܢܗܘܘܢ ܡܟܝܟܝܢ ܘܒܟܠܡܕܡ ܢܚܘܘܢ ܒܤܝܡܘܬܗܘܢ ܠܘܬ ܟܠܗܘܢ ܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 3, 'ܐܦ ܚܢܢ ܓܝܪ ܡܢ ܩܕܝܡ ܕܠܐ ܪܥܝܢܐ ܗܘܝܢ ܘܕܠܐ ܦܝܤܐ ܘܛܥܝܢ ܗܘܝܢ ܘܡܫܥܒܕܝܢ ܗܘܝܢ ܠܐܪܓܝܓܬܐ ܡܫܚܠܦܬܐ ܘܒܒܝܫܘܬܐ ܘܒܚܤܡܐ ܡܬܗܦܟܝܢ ܗܘܝܢ ܘܤܢܝܐܝܝܢ ܗܘܝܢ ܐܦ ܚܕ ܠܚܕ ܤܢܝܢ ܗܘܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 4, 'ܟܕ ܕܝܢ ܐܬܓܠܝܬ ܒܤܝܡܘܬܗ ܘܡܪܚܡܢܘܬܗ ܕܐܠܗܐ ܡܚܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 5, 'ܠܐ ܒܥܒܕܐ ܕܙܕܝܩܘܬܐ ܕܥܒܕܢ ܐܠܐ ܒܪܚܡܘܗܝ ܕܝܠܗ ܐܚܝܢ ܒܤܚܬܐ ܕܡܘܠܕܐ ܕܡܢ ܕܪܝܫ ܘܒܚܘܕܬܐ ܕܪܘܚܐ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 6, 'ܗܝ ܕܐܫܕ ܥܠܝܢ ܥܬܝܪܐܝܬ ܒܝܕ ܝܫܘܥ ܡܫܝܚܐ ܡܚܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 7, 'ܕܒܛܝܒܘܬܗ ܢܙܕܕܩ ܘܢܗܘܐ ܝܪܬܐ ܒܤܒܪܐ ܒܚܝܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 8, 'ܡܗܝܡܢܐ ܗܝ ܡܠܬܐ ܘܒܗܠܝܢ ܨܒܐ ܐܢܐ ܕܐܦ ܐܢܬ ܗܘܝܬ ܡܫܪܪ ܠܗܘܢ ܕܢܬܒܛܠ ܠܗܘܢ ܠܡܦܠܚ ܥܒܕܐ ܛܒܐ ܐܝܠܝܢ ܕܗܝܡܢܘ ܒܐܠܗܐ ܗܠܝܢ ܐܢܝܢ ܛܒܢ ܘܡܘܬܪܢ ܠܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 9, 'ܡܢ ܒܥܬܐ ܕܝܢ ܤܟܠܬܐ ܘܡܢ ܬܫܥܝܬܐ ܕܫܪܒܬܐ ܘܡܢ ܚܪܝܢܐ ܘܡܢ ܬܟܬܘܫܐ ܕܤܦܪܐ ܐܫܬܐܠ ܝܘܬܪܢ ܓܝܪ ܠܝܬ ܒܗܝܢ ܘܤܪܝܩܢ ܐܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 10, 'ܡܢ ܓܒܪܐ ܗܪܤܝܘܛܐ ܡܢ ܒܬܪ ܚܕܐ ܙܒܢ ܘܬܪܬܝܢ ܕܡܪܬܐ ܐܢܬ ܠܗ ܐܫܬܐܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 11, 'ܘܗܘܝܬ ܝܕܥ ܕܡܢ ܕܗܟܢܐ ܗܘ ܡܥܩܡ ܗܘ ܘܚܛܐ ܘܗܘ ܚܝܒ ܢܦܫܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 12, 'ܡܐ ܕܫܕܪܬ ܠܘܬܟ ܠܐܪܛܡܐ ܐܘ ܠܛܘܟܝܩܘܤ ܢܬܒܛܠ ܠܟ ܕܬܐܬܐ ܠܘܬܝ ܠܢܝܩܦܘܠܝܤ ܬܡܢ ܓܝܪ ܤܡܬ ܒܪܥܝܢܝ ܕܐܤܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 13, 'ܥܠ ܙܢܐ ܕܝܢ ܤܦܪܐ ܘܥܠ ܐܦܠܘ ܢܬܒܛܠ ܠܟ ܕܬܠܘܐ ܐܢܘܢ ܫܦܝܪ ܕܡܕܡ ܠܐ ܢܚܤܪ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 14, 'ܘܢܐܠܦܘܢ ܐܦ ܐܝܠܝܢ ܕܕܝܠܢ ܐܢܘܢ ܕܢܗܘܘܢ ܦܠܚܝܢ ܥܒܕܐ ܛܒܐ ܒܨܒܘܬܐ ܕܐܠܨܢ ܕܠܐ ܢܗܘܘܢ ܕܠܐ ܦܐܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 15, 'ܫܐܠܝܢ ܒܫܠܡܟ ܟܠܗܘܢ ܐܝܠܝܢ ܕܥܡܝ ܐܢܘܢ ܫܐܠ ܒܫܠܡܐ ܕܟܠܗܘܢ ܐܝܠܝܢ ܕܪܚܡܝܢ ܠܢ ܒܗܝܡܢܘܬܐ ܛܝܒܘܬܐ ܥܡ ܟܠܟܘܢ ܐܡܝܢ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 1, 'ܦܘܠܘܤ ܐܤܝܪܗ ܕܝܫܘܥ ܡܫܝܚܐ ܘܛܝܡܬܐܘܤ ܐܚܐ ܠܦܝܠܡܘܢ ܚܒܝܒܐ ܘܦܠܚܐ ܕܥܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 2, 'ܘܠܐܦܝܐ ܚܒܝܒܬܢ ܘܠܐܪܟܝܦܘܤ ܦܠܚܐ ܕܥܡܢ ܘܠܥܕܬܐ ܕܒܒܝܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 3, 'ܛܝܒܘܬܐ ܥܡܟܘܢ ܘܫܠܡܐ ܡܢ ܐܠܗܐ ܐܒܘܢ ܘܡܢ ܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 4, 'ܡܘܕܐ ܐܢܐ ܠܐܠܗܝ ܒܟܠܙܒܢ ܘܡܬܕܟܪ ܐܢܐ ܠܟ ܒܨܠܘܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 5, 'ܗܐ ܡܢ ܕܫܡܥܬ ܗܝܡܢܘܬܟ ܘܚܘܒܐ ܕܐܝܬ ܠܟ ܠܘܬ ܡܪܢ ܝܫܘܥ ܘܠܘܬ ܟܠܗܘܢ ܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 6, 'ܕܬܗܘܐ ܫܘܬܦܘܬܐ ܕܗܝܡܢܘܬܟ ܝܗܒܐ ܦܐܪܐ ܒܥܒܕܐ ܘܒܝܕܥܬܐ ܕܟܠ ܛܒܢ ܕܐܝܬ ܠܟܘܢ ܒܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 7, 'ܚܕܘܬܐ ܓܝܪ ܤܓܝܐܬܐ ܐܝܬ ܠܢ ܘܒܘܝܐܐ ܕܒܝܕ ܚܘܒܟ ܐܬܬܢܝܚܘ ܪܚܡܐ ܕܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 8, 'ܡܛܠ ܗܕܐ ܦܪܗܤܝܐ ܤܓܝܐܬܐ ܐܝܬ ܠܝ ܒܡܫܝܚܐ ܕܐܦܩܘܕ ܠܟ ܐܝܠܝܢ ܕܙܕܩܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 9, 'ܡܛܠ ܚܘܒܐ ܕܝܢ ܡܒܥܐ ܗܘ ܒܥܐ ܐܢܐ ܡܢܟ ܐܢܐ ܦܘܠܘܤ ܕܐܝܬܝ ܤܒܐ ܐܝܟ ܕܝܕܥ ܐܢܬ ܗܫܐ ܕܝܢ ܐܦ ܐܤܝܪܐ ܕܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 10, 'ܘܒܥܐ ܐܢܐ ܡܢܟ ܥܠ ܒܪܝ ܐܝܢܐ ܕܝܠܕܬ ܒܐܤܘܪܝ ܐܢܤܝܡܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 11, 'ܗܘ ܕܒܙܒܢ ܠܝܬ ܗܘܐ ܠܟ ܒܗ ܚܫܚܘ ܗܫܐ ܕܝܢ ܐܦ ܠܟ ܐܦ ܠܝ ܛܒ ܚܫܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 12, 'ܘܫܕܪܬܗ ܠܟ ܐܢܬ ܕܝܢ ܐܝܟ ܕܠܝܠܕܐ ܕܝܠܝ ܗܟܢܐ ܩܒܠܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 13, 'ܨܒܐ ܗܘܝܬ ܓܝܪ ܕܠܘܬܝ ܐܚܕܝܘܗܝ ܕܢܗܘܐ ܡܫܡܫ ܠܝ ܚܠܦܝܟ ܒܐܤܘܪܐ ܕܐܘܢܓܠܝܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 14, 'ܒܠܥܕ ܡܠܟܟ ܕܝܢ ܠܐ ܨܒܝܬ ܡܕܡ ܠܡܥܒܕ ܕܠܐ ܐܝܟ ܕܒܩܛܝܪܐ ܬܗܘܐ ܛܒܬܟ ܐܠܐ ܒܨܒܝܢܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 15, 'ܟܒܪ ܕܝܢ ܐܦ ܡܛܠ ܗܕܐ ܫܢܝ ܕܫܥܬܐ ܕܠܥܠܡ ܬܐܚܕܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 16, 'ܠܐ ܡܟܝܠ ܐܝܟ ܥܒܕܐ ܐܠܐ ܝܬܝܪ ܡܢ ܥܒܕܐ ܐܚܐ ܚܒܝܒܐ ܕܝܠܝ ܚܕ ܟܡܐ ܕܝܠܟ ܘܒܒܤܪ ܘܒܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 17, 'ܐܢ ܗܟܝܠ ܐܝܬܝܟ ܠܝ ܫܘܬܦܐ ܩܒܠܝܗܝ ܐܝܟ ܕܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 18, 'ܘܐܢ ܡܕܡ ܚܤܪܟ ܐܘ ܚܝܒ ܗܕܐ ܥܠܝ ܚܫܘܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 19, 'ܐܢܐ ܦܘܠܘܤ ܟܬܒܬ ܒܐܝܕܝ ܐܢܐ ܦܪܥ ܐܢܐ ܕܠܐ ܐܡܪ ܠܟ ܕܐܦ ܢܦܫܟ ܚܝܒ ܐܢܬ ܠܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 20, 'ܐܝܢ ܐܚܝ ܐܢܐ ܐܬܬܢܝܚ ܒܟ ܒܡܪܢ ܐܢܝܚ ܪܚܡܝ ܒܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 21, 'ܡܛܠ ܕܬܟܝܠ ܐܢܐ ܕܫܡܥ ܐܢܬ ܠܝ ܟܬܒܬ ܠܟ ܘܝܕܥ ܐܢܐ ܕܝܬܝܪ ܡܢ ܡܐ ܕܐܡܪ ܐܢܐ ܥܒܕ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 22, 'ܒܚܕܐ ܕܝܢ ܐܦ ܛܝܒ ܠܝ ܒܝܬ ܡܫܪܝܐ ܡܤܒܪ ܐܢܐ ܓܝܪ ܕܒܨܠܘܬܟܘܢ ܡܬܝܗܒ ܐܢܐ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 23, 'ܫܐܠ ܒܫܠܡܟ ܐܦܦܪܐ ܫܒܝܐ ܕܥܡܝ ܒܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 24, 'ܘܡܪܩܘܤ ܘܐܪܤܛܪܟܘܤ ܘܕܡܐ ܘܠܘܩܐ ܡܥܕܪܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 25, 'ܛܝܒܘܬܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܥܡ ܪܘܚܟܘܢ ܐܡܝܢ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 1, 'ܒܟܠ ܡܢܘܢ ܘܒܟܠ ܕܡܘܢ ܡܠܠ ܐܠܗܐ ܥܡ ܐܒܗܝܢ ܒܢܒܝܐ ܡܢ ܩܕܝܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 2, 'ܘܒܗܠܝܢ ܝܘܡܬܐ ܐܚܪܝܐ ܡܠܠ ܥܡܢ ܒܒܪܗ ܕܠܗ ܤܡ ܝܪܬܐ ܕܟܠܡܕܡ ܘܒܗ ܥܒܕ ܠܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 3, 'ܕܗܘܝܘ ܨܡܚܐ ܕܫܘܒܚܗ ܘܨܠܡܐ ܕܐܝܬܘܬܗ ܘܐܚܝܕ ܟܠ ܒܚܝܠܐ ܕܡܠܬܗ ܘܗܘ ܒܩܢܘܡܗ ܥܒܕ ܕܘܟܝܐ ܕܚܛܗܝܢ ܘܝܬܒ ܥܠ ܝܡܝܢܐ ܕܪܒܘܬܐ ܒܡܪܘܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 4, 'ܘܗܢܐ ܟܠܗ ܝܪܒ ܡܢ ܡܠܐܟܐ ܐܝܟ ܡܐ ܕܡܝܬܪ ܫܡܐ ܕܝܪܬ ܡܢ ܕܝܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 5, 'ܠܐܝܢܐ ܓܝܪ ܡܢ ܡܠܐܟܐ ܡܡܬܘܡ ܐܡܪ ܐܠܗܐ ܕܒܪܝ ܐܢܬ ܐܢܐ ܝܘܡܢܐ ܝܠܕܬܟ ܘܬܘܒ ܕܐܢܐ ܐܗܘܐ ܠܗ ܠܐܒܐ ܘܗܘ ܢܗܘܐ ܠܝ ܠܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 6, 'ܬܘܒ ܕܝܢ ܐܡܬܝ ܕܡܥܠ ܒܘܟܪܐ ܠܥܠܡܐ ܐܡܪ ܕܠܗ ܢܤܓܕܘܢ ܟܠܗܘܢ ܡܠܐܟܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 7, 'ܥܠ ܡܠܐܟܐ ܕܝܢ ܗܟܢܐ ܐܡܪ ܕܥܒܕ ܡܠܐܟܘܗܝ ܪܘܚ ܘܡܫܡܫܢܘܗܝ ܢܘܪ ܝܩܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 8, 'ܥܠ ܒܪܐ ܕܝܢ ܐܡܪ ܕܟܘܪܤܝܟ ܕܝܠܟ ܐܠܗܐ ܠܥܠܡ ܥܠܡܝܢ ܫܒܛܐ ܦܫܝܛܐ ܫܒܛܐ ܕܡܠܟܘܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 9, 'ܪܚܡܬ ܟܐܢܘܬܐ ܘܤܢܝܬ ܥܘܠܐ ܡܛܠ ܗܢܐ ܡܫܚܟ ܐܠܗܐ ܐܠܗܟ ܡܫܚܐ ܕܚܕܘܬܐ ܝܬܝܪ ܡܢ ܚܒܪܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 10, 'ܘܬܘܒ ܐܢܬ ܡܢ ܒܪܝܫܝܬ ܤܡܬ ܫܬܐܤܝܗ ܕܐܪܥܐ ܘܫܡܝܐ ܥܒܕ ܐܝܕܝܟ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 11, 'ܗܢܘܢ ܢܥܒܪܘܢ ܘܐܢܬ ܩܝܡ ܐܢܬ ܘܟܠܗܘܢ ܐܝܟ ܢܚܬܐ ܢܒܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 12, 'ܘܐܝܟ ܬܟܤܝܬܐ ܬܥܘܦ ܐܢܘܢ ܗܢܘܢ ܢܬܚܠܦܘܢ ܘܐܢܬ ܐܝܟ ܕܐܝܬܝܟ ܐܢܬ ܘܫܢܝܟ ܠܐ ܢܓܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 13, 'ܠܡܢ ܕܝܢ ܡܢ ܡܠܐܟܐ ܡܡܬܘܡ ܐܡܪ ܕܬܒ ܡܢ ܝܡܝܢܝ ܥܕܡܐ ܕܐܤܝܡ ܒܥܠܕܒܒܝܟ ܟܘܒܫܐ ܬܚܝܬ ܪܓܠܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 14, 'ܠܐ ܗܐ ܟܠܗܘܢ ܐܝܬܝܗܘܢ ܪܘܚܐ ܕܬܫܡܫܬܐ ܕܡܫܬܕܪܝܢ ܒܬܫܡܫܬܐ ܡܛܠ ܐܝܠܝܢ ܕܥܬܝܕܝܢ ܠܡܐܪܬ ܚܝܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 1, 'ܡܛܠ ܗܢܐ ܚܝܒܝܢܢ ܕܝܬܝܪܐܝܬ ܢܗܘܐ ܙܗܝܪܝܢ ܒܡܕܡ ܕܫܡܥܢ ܕܠܐ ܢܦܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 2, 'ܐܢ ܓܝܪ ܡܠܬܐ ܕܐܬܡܠܠܬ ܒܝܕ ܡܠܐܟܐ ܐܫܬܪܪܬ ܘܟܠ ܕܫܡܥܗ ܘܥܒܪ ܥܠܝܗ ܩܒܠ ܦܘܪܥܢܐ ܒܟܐܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 3, 'ܐܝܟܢܐ ܚܢܢ ܢܥܪܘܩ ܐܢ ܢܒܤܐ ܥܠ ܐܝܠܝܢ ܕܗܢܘܢ ܐܢܘܢ ܚܝܝܢ ܗܢܘܢ ܕܫܪܝܘ ܡܢ ܡܪܢ ܠܡܬܡܠܠܘ ܘܡܢ ܐܝܠܝܢ ܕܡܢܗ ܫܡܥܘ ܒܢ ܐܫܬܪܪܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 4, 'ܟܕ ܤܗܕ ܥܠܝܗܘܢ ܐܠܗܐ ܒܐܬܘܬܐ ܘܒܬܕܡܪܬܐ ܘܒܚܝܠܐ ܡܫܚܠܦܐ ܘܒܦܘܠܓܐ ܕܪܘܚܐ ܕܩܘܕܫܐ ܕܐܬܝܗܒܘ ܐܝܟ ܨܒܝܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 5, 'ܠܐ ܗܘܐ ܓܝܪ ܠܡܠܐܟܐ ܫܥܒܕ ܥܠܡܐ ܕܥܬܝܕ ܕܥܠܘܗܝ ܡܡܠܠܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 6, 'ܐܠܐ ܐܝܟ ܕܡܤܗܕ ܟܬܒܐ ܘܐܡܪ ܡܢܘ ܓܒܪܐ ܕܥܗܕܬܝܗܝ ܘܒܪܗ ܕܐܢܫܐ ܕܤܥܪܬܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 7, 'ܐܡܟܬܝܗܝ ܩܠܝܠ ܡܢ ܡܠܐܟܐ ܬܫܒܘܚܬܐ ܘܐܝܩܪܐ ܤܡܬ ܒܪܝܫܗ ܘܐܫܠܛܬܝܗܝ ܒܥܒܕܐ ܕܐܝܕܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 8, 'ܘܟܠ ܡܕܡ ܫܥܒܕܬ ܬܚܝܬ ܪܓܠܘܗܝ ܒܗܝ ܕܝܢ ܕܟܠ ܡܕܡ ܫܥܒܕ ܠܗ ܠܐ ܫܒܩ ܡܕܡ ܕܠܐ ܡܫܥܒܕ ܗܫܐ ܕܝܢ ܠܐ ܥܕܟܝܠ ܚܙܝܢܢ ܕܟܠ ܡܕܡ ܡܫܥܒܕ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 9, 'ܗܘ ܕܝܢ ܕܡܟ ܩܠܝܠ ܡܢ ܡܠܐܟܐ ܚܙܝܢܢ ܕܗܘܝܘ ܝܫܘܥ ܡܛܠ ܚܫܐ ܕܡܘܬܗ ܘܬܫܒܘܚܬܐ ܘܐܝܩܪܐ ܤܝܡ ܒܪܝܫܗ ܗܘ ܓܝܪ ܒܛܝܒܘܬܗ ܐܠܗܐ ܚܠܦ ܟܠܢܫ ܛܥܡ ܡܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 10, 'ܝܐܐ ܗܘܐ ܓܝܪ ܠܗܘ ܕܟܠ ܒܐܝܕܗ ܘܟܠ ܡܛܠܬܗ ܘܒܢܝܐ ܤܓܝܐܐ ܐܥܠ ܠܬܫܒܘܚܬܐ ܕܠܪܝܫܐ ܕܚܝܝܗܘܢ ܒܚܫܗ ܢܓܡܪܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 11, 'ܗܘ ܓܝܪ ܕܩܕܫ ܘܗܢܘܢ ܕܐܬܩܕܫܘ ܡܢ ܚܕ ܐܢܘܢ ܟܠܗܘܢ ܡܛܠ ܗܢܐ ܠܐ ܒܗܬ ܕܢܩܪܐ ܐܢܘܢ ܐܚܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 12, 'ܟܕ ܐܡܪ ܐܤܒܪ ܫܡܟ ܠܐܚܝ ܘܒܓܘܗ ܕܥܕܬܐ ܐܫܒܚܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 13, 'ܘܬܘܒ ܕܐܢܐ ܐܗܘܐ ܬܟܝܠ ܥܠܘܗܝ ܘܬܘܒ ܗܐ ܐܢܐ ܘܒܢܝܐ ܕܝܗܒ ܠܝ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 14, 'ܡܛܠ ܓܝܪ ܕܒܢܝܐ ܐܫܬܘܬܦܘ ܒܒܤܪܐ ܘܕܡܐ ܐܦ ܗܘ ܒܗ ܒܕܡܘܬܐ ܐܫܬܘܬܦ ܒܗܝܢ ܒܗܠܝܢ ܕܒܡܘܬܗ ܢܒܛܠ ܠܡܢ ܕܐܚܝܕ ܫܘܠܛܢܐ ܕܡܘܬܐ ܕܐܝܬܘܗܝ ܤܛܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 15, 'ܘܢܫܪܐ ܠܗܢܘܢ ܐܝܠܝܢ ܕܒܕܚܠܬܐ ܕܡܘܬܐ ܒܟܠܗܘܢ ܚܝܝܗܘܢ ܡܫܥܒܕܝܢ ܗܘܘ ܠܥܒܕܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 16, 'ܠܐ ܗܘܐ ܓܝܪ ܥܠ ܡܠܐܟܐ ܡܫܠܛ ܗܘܐ ܡܘܬܐ ܐܠܐ ܥܠ ܙܪܥܗ ܕܐܒܪܗܡ ܡܫܠܛ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 17, 'ܡܛܠ ܗܢܐ ܙܕܩ ܗܘܐ ܕܒܟܠ ܡܕܡ ܢܬܕܡܐ ܠܐܚܘܗܝ ܕܢܗܘܐ ܡܪܚܡܢܐ ܘܪܒ ܟܘܡܪܐ ܡܗܝܡܢܐ ܒܕܐܠܗܐ ܘܢܗܘܐ ܡܚܤܐ ܥܠ ܚܛܗܘܗܝ ܕܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 18, 'ܒܗܝ ܓܝܪ ܕܗܘ ܚܫ ܘܐܬܢܤܝ ܡܫܟܚ ܕܢܥܕܪ ܠܐܝܠܝܢ ܕܡܬܢܤܝܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 1, 'ܡܟܝܠ ܐܚܝ ܩܕܝܫܐ ܕܐܬܩܪܝܬܘܢ ܒܩܪܝܢܐ ܕܡܢ ܫܡܝܐ ܚܙܐܘܗܝ ܠܗܢܐ ܫܠܝܚܐ ܘܪܒ ܟܘܡܪܐ ܕܬܘܕܝܬܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 2, 'ܕܡܗܝܡܢ ܠܡܢ ܕܥܒܕܗ ܐܝܟ ܡܘܫܐ ܒܟܠܗ ܒܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 3, 'ܤܓܝܐܐ ܗܝ ܓܝܪ ܬܫܒܘܚܬܗ ܕܗܢܐ ܛܒ ܡܢ ܕܡܘܫܐ ܐܝܟܢܐ ܕܤܓܝ ܐܝܩܪܐ ܕܒܢܝܗ ܕܒܝܬܐ ܛܒ ܡܢ ܒܢܝܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 4, 'ܟܠ ܒܝܬܐ ܓܝܪ ܡܢ ܐܢܫ ܗܘ ܡܬܒܢܐ ܗܘ ܕܝܢ ܕܒܢܐ ܟܠ ܐܠܗܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 5, 'ܘܡܘܫܐ ܐܝܟ ܥܒܕܐ ܐܬܗܝܡܢ ܒܒܝܬܐ ܟܠܗ ܠܤܗܕܘܬܐ ܕܐܝܠܝܢ ܕܥܬܝܕܢ ܗܘܝ ܠܡܬܡܠܠܘ ܒܐܝܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 6, 'ܡܫܝܚܐ ܕܝܢ ܐܝܟ ܒܪܐ ܥܠ ܒܝܬܗ ܘܐܝܬܘܗܝ ܒܝܬܗ ܚܢܢ ܐܢ ܥܕܡܐ ܠܚܪܬܐ ܢܐܚܘܕ ܓܠܝܘܬ ܐܦܐ ܘܫܘܒܗܪܐ ܕܤܒܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 7, 'ܡܛܠ ܕܐܡܪܬ ܪܘܚܐ ܕܩܘܕܫܐ ܕܝܘܡܢܐ ܐܢ ܒܩܠܗ ܬܫܡܥܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 8, 'ܠܐ ܬܩܫܘܢ ܠܒܘܬܟܘܢ ܠܡܪܓܙܘܬܗ ܐܝܟ ܡܡܪܡܪܢܐ ܘܐܝܟ ܝܘܡܐ ܕܢܤܝܘܢܐ ܕܒܡܕܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 9, 'ܕܢܤܝܘܢܝ ܐܒܗܝܟܘܢ ܘܒܩܘ ܚܙܘ ܥܒܕܝ ܐܪܒܥܝܢ ܫܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 10, 'ܡܛܠ ܗܢܐ ܡܐܢܬ ܠܝ ܒܕܪܐ ܗܘ ܘܐܡܪܬ ܕܥܡܐ ܗܘ ܕܛܥܐ ܠܒܗܘܢ ܘܗܢܘܢ ܠܐ ܝܕܥܘ ܐܘܪܚܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 11, 'ܘܐܝܟ ܕܝܡܝܬ ܒܪܘܓܙܝ ܕܠܐ ܢܥܠܘܢ ܠܢܝܚܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 12, 'ܐܙܕܗܪܘ ܗܟܝܠ ܐܚܝ ܕܠܡܐ ܢܗܘܐ ܒܐܢܫ ܡܢܟܘܢ ܠܒܐ ܒܝܫܐ ܕܠܐ ܡܗܝܡܢ ܘܬܦܪܩܘܢ ܡܢ ܐܠܗܐ ܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 13, 'ܐܠܐ ܒܥܘ ܡܢ ܢܦܫܟܘܢ ܟܠܗܘܢ ܝܘܡܬܐ ܥܕܡܐ ܠܝܘܡܐ ܕܡܬܩܪܐ ܝܘܡܢܐ ܕܠܐ ܢܬܩܫܐ ܐܢܫ ܡܢܟܘܢ ܒܛܥܝܘܬܐ ܕܚܛܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 14, 'ܐܬܚܠܛܢ ܓܝܪ ܥܡ ܡܫܝܚܐ ܐܢ ܡܢ ܪܝܫܝܬܐ ܘܥܕܡܐ ܠܐܚܪܝܬܐ ܒܗ ܒܩܝܡܐ ܗܢܐ ܫܪܝܪܐ ܢܚܡܤܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 15, 'ܐܝܟܢܐ ܕܐܡܝܪ ܕܝܘܡܢܐ ܐܢ ܒܪܬ ܩܠܗ ܬܫܡܥܘܢ ܠܐ ܬܩܫܘܢ ܠܒܘܬܟܘܢ ܠܡܪܓܙܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 16, 'ܡܢ ܐܢܘܢ ܓܝܪ ܗܢܘܢ ܕܫܡܥܘ ܘܐܪܓܙܘܗܝ ܠܐ ܟܠܗܘܢ ܗܠܝܢ ܕܢܦܩܘ ܡܢ ܡܨܪܝܢ ܒܝܕ ܡܘܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 17, 'ܘܒܐܝܠܝܢ ܡܐܢܬ ܠܗ ܐܪܒܥܝܢ ܫܢܝܢ ܐܠܐ ܒܗܢܘܢ ܕܚܛܘ ܘܓܪܡܝܗܘܢ ܢܦܠܘ ܒܡܕܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 18, 'ܘܥܠ ܐܝܠܝܢ ܝܡܐ ܕܠܐ ܢܥܠܘܢ ܠܢܝܚܬܗ ܐܠܐ ܥܠ ܗܢܘܢ ܕܠܐ ܐܬܛܦܝܤܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 19, 'ܘܚܙܝܢܢ ܕܠܐ ܐܫܟܚܘ ܠܡܥܠ ܡܛܠ ܕܠܐ ܗܝܡܢܘ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 1, 'ܢܕܚܠ ܗܟܝܠ ܕܠܡܐ ܟܕ ܩܝܡ ܡܘܠܟܢܐ ܕܡܥܠܬܐ ܕܠܢܝܚܬܗ ܢܫܬܟܚ ܐܢܫ ܡܢܟܘܢ ܕܦܐܫ ܡܢ ܕܠܡܥܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 2, 'ܐܦ ܚܢܢ ܓܝܪ ܐܤܬܒܪܢ ܐܝܟ ܕܐܦ ܗܢܘܢ ܐܠܐ ܠܐ ܐܘܬܪܬ ܐܢܘܢ ܠܗܢܘܢ ܡܠܬܐ ܕܫܡܥܘ ܡܛܠ ܕܠܐ ܡܡܙܓܐ ܗܘܬ ܒܗܝܡܢܘܬܐ ܠܗܢܘܢ ܕܫܡܥܘܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 3, 'ܥܐܠܝܢܢ ܕܝܢ ܠܢܝܚܬܐ ܚܢܢ ܐܝܠܝܢ ܕܗܝܡܢܢ ܐܝܟܢܐ ܕܝܢ ܐܡܪ ܐܝܟ ܕܝܡܝܬ ܒܪܘܓܙܝ ܕܠܐ ܢܥܠܘܢ ܠܢܝܚܬܝ ܕܗܐ ܥܒܕܘܗܝ ܕܐܠܗܐ ܡܢ ܫܘܪܝܗ ܕܥܠܡܐ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 4, 'ܐܝܟ ܕܐܡܪ ܥܠ ܫܒܬܐ ܕܐܬܢܝܚ ܐܠܗܐ ܒܝܘܡܐ ܫܒܝܥܝܐ ܡܢ ܥܒܕܘܗܝ ܟܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 5, 'ܘܗܪܟܐ ܬܘܒ ܐܡܪ ܕܠܐ ܢܥܠܘܢ ܠܢܝܚܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 6, 'ܡܛܠ ܗܟܝܠ ܕܐܝܬ ܗܘܐ ܐܬܪܐ ܕܐܢܫ ܐܢܫ ܢܥܘܠ ܠܗ ܘܗܢܘܢ ܕܐܤܬܒܪܘ ܩܕܡܝܐ ܠܐ ܥܠܘ ܒܕܠܐ ܐܬܛܦܝܤܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 7, 'ܬܘܒ ܝܘܡܐ ܐܚܪܢܐ ܤܐܡ ܡܢ ܒܬܪ ܙܒܢܐ ܤܓܝܐܐ ܐܝܟܢܐ ܕܡܢ ܠܥܠ ܟܬܝܒ ܕܐܡܪ ܕܘܝܕ ܕܝܘܡܢܐ ܐܢ ܒܩܠܗ ܬܫܡܥܘܢ ܠܐ ܬܩܫܘܢ ܠܒܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 8, 'ܐܠܘ ܓܝܪ ܝܫܘܥ ܒܪ ܢܘܢ ܐܢܝܚ ܗܘܐ ܐܢܘܢ ܠܐ ܐܡܪ ܗܘܐ ܡܢ ܒܬܪܟܢ ܥܠ ܝܘܡܐ ܐܚܪܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 9, 'ܡܕܝܢ ܩܝܡ ܗܘ ܠܡܫܒܬܘ ܠܥܡܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 10, 'ܐܝܢܐ ܓܝܪ ܕܥܠ ܠܢܝܚܬܗ ܐܬܬܢܝܚ ܐܦ ܗܘ ܡܢ ܥܒܕܘܗܝ ܐܝܟ ܕܐܠܗܐ ܡܢ ܕܝܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 11, 'ܢܬܚܦܛ ܗܟܝܠ ܕܢܥܘܠ ܠܗܝ ܢܝܚܬܐ ܕܠܐ ܢܦܠ ܒܕܡܘܬܐ ܕܗܢܘܢ ܕܠܐ ܐܬܛܦܝܤܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 12, 'ܚܝܐ ܗܝ ܓܝܪ ܡܠܬܗ ܕܐܠܗܐ ܘܟܠ ܤܥܪܐ ܘܚܪܝܦܐ ܛܒ ܡܢ ܤܦܤܪܐ ܕܬܪܝܢ ܦܘܡܝܗ ܘܥܐܠܐ ܥܕܡܐ ܠܦܘܪܫܢܐ ܕܢܦܫܐ ܘܕܪܘܚܐ ܘܕܫܪܝܬܐ ܘܕܡܘܚܐ ܘܕܓܪܡܐ ܘܕܝܢܐ ܡܚܫܒܬܐ ܘܬܪܥܝܬܐ ܕܠܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 13, 'ܘܠܝܬ ܒܪܝܬܐ ܕܛܫܝܐ ܡܢ ܩܕܡܝܗ ܐܠܐ ܟܠ ܡܕܡ ܥܪܛܠ ܘܓܠܐ ܩܕܡ ܥܝܢܘܗܝ ܕܠܗ ܝܗܒܝܢܢ ܦܬܓܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 14, 'ܡܛܠ ܕܐܝܬ ܠܢ ܗܟܝܠ ܪܒ ܟܘܡܪܐ ܪܒܐ ܝܫܘܥ ܡܫܝܚܐ ܒܪܗ ܕܐܠܗܐ ܕܤܠܩ ܠܫܡܝܐ ܢܚܡܤܢ ܒܬܘܕܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 15, 'ܠܐ ܓܝܪ ܐܝܬ ܠܢ ܪܒܟܘܡܪܐ ܕܠܐ ܡܨܐ ܕܢܚܫ ܥܡ ܟܪܝܗܘܬܢ ܐܠܐ ܕܡܢܤܝ ܒܟܠܡܕܡ ܐܟܘܬܢ ܤܛܪ ܡܢ ܚܛܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 16, 'ܢܬܩܪܒ ܗܟܝܠ ܒܓܠܐ ܥܝܢ ܠܟܘܪܤܝܐ ܕܛܝܒܘܬܗ ܕܢܤܒ ܪܚܡܐ ܘܢܫܟܚ ܛܝܒܘܬܐ ܠܥܘܕܪܢܐ ܒܙܒܢܐ ܕܐܘܠܨܢܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 1, 'ܟܠ ܪܒ ܟܘܡܪܐ ܓܝܪ ܕܡܢ ܒܢܝܢܫܐ ܗܘܐ ܚܠܦ ܒܢܝܢܫܐ ܩܐܡ ܥܠ ܐܝܠܝܢ ܕܕܐܠܗܐ ܐܢܝܢ ܕܢܩܪܒ ܩܘܪܒܢܐ ܘܕܒܚܐ ܚܠܦ ܚܛܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 2, 'ܐܝܢܐ ܕܡܫܟܚ ܕܢܡܟ ܢܦܫܗ ܘܢܚܫ ܥܡ ܐܝܠܝܢ ܕܠܐ ܝܕܥܝܢ ܘܛܥܝܢ ܡܛܠ ܕܐܦ ܗܘ ܟܪܝܗܘܬܐ ܠܒܝܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 3, 'ܘܡܛܠܬܗ ܚܝܒ ܗܘ ܕܐܝܟܢܐ ܕܚܠܦ ܥܡܐ ܗܟܢܐ ܐܦ ܚܠܦ ܢܦܫܗ ܢܩܪܒ ܥܠ ܚܛܗܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 4, 'ܘܠܐ ܗܘܐ ܠܢܦܫܗ ܐܢܫ ܢܤܒ ܐܝܩܪܐ ܐܠܐ ܐܝܢܐ ܕܡܬܩܪܐ ܡܢ ܐܠܗܐ ܐܝܟܢܐ ܕܐܗܪܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 5, 'ܗܟܢܐ ܐܦ ܡܫܝܚܐ ܠܐ ܗܘܐ ܢܦܫܗ ܫܒܚ ܕܢܗܘܐ ܪܒ ܟܘܡܪܐ ܐܠܐ ܗܘ ܕܐܡܪ ܠܗ ܕܒܪܝ ܐܢܬ ܐܢܐ ܝܘܡܢܐ ܝܠܕܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 6, 'ܐܝܟ ܕܐܦ ܒܕܘܟܬܐ ܐܚܪܬܐ ܐܡܪ ܕܐܢܬ ܗܘ ܟܘܡܪܐ ܠܥܠܡ ܒܕܡܘܬܗ ܕܡܠܟܝܙܕܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 7, 'ܐܦ ܟܕ ܒܤܪܐ ܠܒܝܫ ܗܘܐ ܒܥܘܬܐ ܘܬܟܫܦܬܐ ܒܓܥܬܐ ܚܝܠܬܢܝܬܐ ܘܒܕܡܥܐ ܩܪܒ ܗܘܐ ܠܡܢ ܕܡܫܟܚ ܗܘܐ ܡܢ ܡܘܬܐ ܕܢܚܝܘܗܝ ܘܐܫܬܡܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 8, 'ܘܟܕ ܛܒ ܒܪܐ ܐܝܬܘܗܝ ܡܢ ܕܚܠܬܐ ܘܚܫܐ ܕܤܒܠ ܝܠܦܗ ܠܡܫܬܡܥܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 9, 'ܘܗܟܢܐ ܐܬܓܡܪ ܘܗܘܐ ܠܟܠܗܘܢ ܐܝܠܝܢ ܕܡܫܬܡܥܝܢ ܠܗ ܥܠܬܐ ܕܚܝܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 10, 'ܘܐܫܬܡܗ ܡܢ ܐܠܗܐ ܪܒ ܟܘܡܪܐ ܒܕܡܘܬܗ ܕܡܠܟܝܙܕܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 11, 'ܥܠܘܗܝ ܕܝܢ ܥܠ ܗܢܐ ܡܠܟܝܙܕܩ ܤܓܝܐܐ ܗܝ ܠܢ ܡܠܬܐ ܠܡܐܡܪܗ ܘܥܤܩܐ ܠܡܦܫܩܘܬܗ ܡܛܠ ܕܗܘܝܬܘܢ ܠܟܘܢ ܟܪܝܗܐ ܒܡܫܡܥܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 12, 'ܚܝܒܝܢ ܗܘܝܬܘܢ ܓܝܪ ܡܠܦܢܐ ܠܡܗܘܐ ܡܛܠ ܕܙܒܢܐ ܠܟܘܢ ܒܝܘܠܦܢܐ ܗܫܐ ܕܝܢ ܬܘܒ ܤܢܝܩܝܢ ܐܢܬܘܢ ܕܬܐܠܦܘܢ ܐܝܠܝܢ ܐܢܝܢ ܟܬܝܒܬܐ ܩܕܡܝܬܐ ܕܪܝܫ ܡܠܘܗܝ ܕܐܠܗܐ ܘܗܘܝܬܘܢ ܠܟܘܢ ܤܢܝܩܐ ܥܠ ܚܠܒܐ ܘܠܐ ܥܠ ܡܐܟܘܠܬܐ ܫܪܝܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 13, 'ܟܠܢܫ ܕܝܢ ܕܡܐܟܘܠܬܗ ܚܠܒܐ ܗܘ ܠܐ ܡܦܤ ܒܡܠܬܐ ܕܟܐܢܘܬܐ ܡܛܠ ܕܫܒܪܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 14, 'ܕܓܡܝܪܐ ܗܝ ܕܝܢ ܡܐܟܘܠܬܐ ܫܪܝܪܬܐ ܐܝܠܝܢ ܕܡܛܠ ܕܡܕܪܫܝܢ ܐܬܢܦܩܘ ܪܓܫܝܗܘܢ ܠܡܦܪܫ ܛܒܬܐ ܘܒܝܫܬܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 1, 'ܡܛܠ ܗܢܐ ܢܫܒܘܩ ܫܘܪܝܐ ܕܡܠܬܗ ܕܡܫܝܚܐ ܘܢܐܬܐ ܠܓܡܝܪܘܬܐ ܐܘ ܠܡܐ ܬܘܒ ܫܬܐܤܬܐ ܐܚܪܬܐ ܡܪܡܝܬܘܢ ܠܬܝܒܘܬܐ ܡܢ ܥܒܕܐ ܡܝܬܐ ܘܠܗܝܡܢܘܬܐ ܕܒܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 2, 'ܘܠܝܘܠܦܢܐ ܕܡܥܡܘܕܝܬܐ ܘܕܤܝܡ ܐܝܕܐ ܘܠܩܝܡܬܐ ܕܡܢ ܒܝܬ ܡܝܬܐ ܘܠܕܝܢܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 3, 'ܐܢ ܡܪܝܐ ܡܦܤ ܢܥܒܕ ܗܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 4, 'ܐܠܐ ܠܐ ܡܫܟܚܝܢ ܗܢܘܢ ܕܚܕܐ ܙܒܢ ܠܡܥܡܘܕܝܬܐ ܢܚܬܘ ܘܛܥܡܘ ܡܘܗܒܬܐ ܕܡܢ ܫܡܝܐ ܘܢܤܒܘ ܪܘܚܐ ܕܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 5, 'ܘܛܥܡܘ ܡܠܬܐ ܛܒܬܐ ܕܐܠܗܐ ܘܚܝܠܐ ܕܥܠܡܐ ܕܥܬܝܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 6, 'ܕܬܘܒ ܢܚܛܘܢ ܕܡܢ ܕܪܝܫ ܢܬܚܕܬܘܢ ܠܬܝܒܘܬܐ ܘܡܢ ܕܪܝܫ ܢܙܩܦܘܢ ܠܒܪܗ ܕܐܠܗܐ ܘܢܨܥܪܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 7, 'ܐܪܥܐ ܓܝܪ ܕܐܫܬܝܬ ܡܛܪܐ ܕܐܬܐ ܠܗ ܙܒܢܝܢ ܤܓܝܐܢ ܘܐܘܥܝܬ ܥܤܒܐ ܕܚܫܚ ܠܗܢܘܢ ܕܡܛܠܬܗܘܢ ܡܬܦܠܚܐ ܡܩܒܠܐ ܒܘܪܟܬܐ ܡܢ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 8, 'ܐܢ ܗܘ ܕܝܢ ܕܬܦܩ ܩܘܪܛܒܐ ܘܕܪܕܪܐ ܗܘܝܐ ܠܗ ܡܤܠܝܬܐ ܘܠܐ ܪܚܝܩܐ ܡܢ ܠܘܛܬܐ ܐܠܐ ܚܪܬܗ ܝܩܕܢܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 9, 'ܡܦܝܤܝܢܢ ܕܝܢ ܥܠܝܟܘܢ ܐܚܝ ܐܝܠܝܢ ܕܫܦܝܪܢ ܘܩܪܝܒܢ ܠܚܝܐ ܐܦܢ ܗܟܢܐ ܡܡܠܠܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 10, 'ܠܐ ܗܘܐ ܓܝܪ ܥܘܠ ܐܠܗܐ ܕܢܛܥܐ ܥܒܕܝܟܘܢ ܘܚܘܒܟܘܢ ܗܘ ܕܚܘܝܬܘܢ ܒܫܡܗ ܕܫܡܫܬܘܢ ܠܩܕܝܫܐ ܘܡܫܡܫܝܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 11, 'ܨܒܝܢܢ ܕܝܢ ܕܐܢܫ ܐܢܫ ܡܢܟܘܢ ܗܝ ܗܕܐ ܚܦܝܛܘܬܐ ܢܚܘܐ ܠܫܘܡܠܝܐ ܕܤܒܪܟܘܢ ܥܕܡܐ ܠܚܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 12, 'ܘܕܠܐ ܬܬܩܛܥ ܠܟܘܢ ܐܠܐ ܕܬܗܘܘܢ ܡܡܪܝܢܐ ܠܗܢܘܢ ܕܒܗܝܡܢܘܬܐ ܘܒܢܓܝܪܘܬ ܪܘܚܐ ܗܘܘ ܝܪܬܐ ܕܡܘܠܟܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 13, 'ܠܐܒܪܗܡ ܓܝܪ ܟܕ ܡܠܟ ܠܗ ܐܠܗܐ ܡܛܠ ܕܠܝܬ ܗܘܐ ܠܗ ܕܪܒ ܡܢܗ ܕܢܐܡܐ ܒܗ ܝܡܐ ܒܢܦܫܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 14, 'ܘܐܡܪ ܕܡܒܪܟܘ ܐܒܪܟܟ ܘܡܤܓܝܘ ܐܤܓܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 15, 'ܘܗܟܢܐ ܐܓܪ ܪܘܚܗ ܘܩܒܠ ܡܘܠܟܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 16, 'ܒܢܝܢܫܐ ܓܝܪ ܒܕܪܒ ܡܢܗܘܢ ܝܡܝܢ ܘܥܠ ܟܠ ܚܪܝܢ ܕܗܘܐ ܒܝܢܬܗܘܢ ܫܘܠܡܐ ܫܪܝܪܐ ܒܡܘܡܬܐ ܗܘܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 17, 'ܡܛܠ ܗܢܐ ܝܬܝܪܐܝܬ ܨܒܐ ܐܠܗܐ ܕܢܚܘܐ ܠܝܪܬܐ ܕܡܘܠܟܢܐ ܕܫܘܘܕܝܗ ܠܐ ܡܫܬܚܠܦ ܘܚܒܫܗ ܒܡܘܡܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 18, 'ܕܒܬܪܬܝܢ ܨܒܘܢ ܕܠܐ ܡܫܬܚܠܦܢ ܕܠܐ ܡܫܟܚ ܐܠܗܐ ܕܢܕܓܠ ܒܗܝܢ ܒܘܝܐܐ ܪܒܐ ܢܗܘܐ ܠܢ ܕܐܬܓܘܤܢ ܒܗ ܘܢܐܚܘܕ ܤܒܪܐ ܕܡܠܝܟ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 19, 'ܗܘ ܕܐܝܬܘܗܝ ܠܢ ܐܝܟ ܐܘܩܝܢܐ ܕܠܒܝܟ ܒܢܦܫܢ ܕܠܐ ܬܬܙܝܥ ܘܥܐܠ ܠܓܘ ܡܢ ܐܦܝ ܬܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 20, 'ܟܪ ܕܩܕV ܥܠ ܚܠܦܝܢ ܝܫܘܥ ܘܗܘܐ ܟܘܡܪܐ ܠܥܠV ܒܕܡܘܬܗ ܕܡܠܟܝܙܕܩ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 1, 'ܗܢܐ ܕܝܢ ܡܠܟܝܙܕܩ ܐܝܬܘܗܝ ܡܠܟ ܫܠܝV ܟܘܡܪܐ ܕܐܠܗܐ ܡܪܝܡܐ ܘܗܘ ܐܪܥܗ ܠܐܒܪܗV ܟܕ ܗܦܟ ܡܢ ܚܪܒܐ ܕܡܠܟܐ ܘܒܪܟܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 2, 'ܘܠܗ ܦܪܫ ܐܒܪܗV ܡܥܤܪܐ ܡܢ ܟܠܡܕV ܕܐܝܬ ܗܘܐ ܥܡܗ ܡܬܦܫܩ ܕܝܢ ܫܡܗ ܡܠܟܐ ܕܟܐܢܘܬܐ ܘܬܘܒ ܡܠܟ ܫܠܝV ܕܐܝܬܘܗܝ ܡܠܟܐ ܕܫܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 3, 'ܕܠܐ ܐܒܘܗܝ ܘܠܐ ܐܡܗ ܐܬܟܬܒܘ ܒܫܪܒܬܐ ܘܠܐ ܪܝܫܝܬܐ ܕܝܘܡܘܗܝ ܘܠܐ ܫܘܠܡܐ ܕܚܝܘܗܝ ܐܠܐ ܒܕܡܘܬܐ ܕܒܪܗ ܕܐܠܗܐ ܡܩܘܝܐ ܟܘܡܪܘܬܗ ܠܥܠV ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 4, 'ܚܙܘ ܕܝܢ ܟܡܐ ܪܒ ܗܢܐ ܕܐܒܪܗV ܪܝܫ ܐܒܗܬܐ ܠܗ ܝܗܒ ܡܥܤܪܐ ܕܪܫܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 5, 'ܐܝܠܝܢ ܓܝܪ ܡܢ ܒܢܝ ܠܘܝ ܕܡܩܒܠܝܢ ܗܘܘ ܟܘܡܪܘܬܐ ܦܘܩܕܢܐ ܐܝܬ ܗܘܐ ܠܗܘܢ ܕܢܡܘܤܐ ܕܢܤܒܘܢ ܡܥܤܪܐ ܡܢ ܥܡܐ ܗܢܘܢ ܡܢ ܐܚܝܗܘܢ ܟܕ ܐܦ ܗܢܘܢ ܡܢ ܚܨܗ ܕܐܒܪܗV ܢܦܩܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 6, 'ܗܢܐ ܕܝܢ ܕܠܐ ܟܬܝܒ ܒܫܪܒܬܗܘܢ ܡܥܤܪܐ ܫܩܠ ܡܢ ܐܒܪܗV ܘܒܪܟܗ ܠܗܘ ܕܩܒܠ ܡܘܠܟܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 7, 'ܕܠܐ ܚܪܝܢܐ ܕܝܢ ܗܘ ܕܒܨܝܪ ܡܬܒܪܟ ܡܢ ܗܘ ܕܡܝܬܪ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 8, 'ܘܗܪܟܐ ܒܢܝܢܫܐ ܕܡܝܬܝܢ ܢܤܒܝܢ ܡܥܤܪܐ ܠܗܠ ܕܝܢ ܗܘ ܕܐܤܗܕ ܥܠܘܗܝ ܟܬܒܐ ܕܚܝ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 9, 'ܘܐܝܟ ܐܢܫ ܢܐܡܪ ܒܝܕ ܐܒܪܗV ܐܦ ܠܘܝ ܗܘ ܕܡܥܤܪܐ ܢܤܒ ܗܘܐ ܐܦ ܗܘ ܐܬܥܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 10, 'ܥܕܟܝܠ ܓܝܪ ܒܚܨܗ ܗܘܐ ܕܐܒܘܗܝ ܟܕ ܐܪܥܗ ܠܡܠܟܝܙܕܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 11, 'ܐܠܘ ܗܟܝܠ ܓܡܝܪܘܬܐ ܒܝܕ ܟܘܡܪܘܬܐ ܕܠܘܝܐ ܐܝܬܝܗ ܗܘܬ ܕܒܗ ܤܝV ܢܡܘܤܐ ܠܥܡܐ ܠܡܢܐ ܡܬܒܥܐ ܗܘܐ ܟܘܡܪܐ ܐܚܪܢܐ ܕܢܩܘV ܒܕܡܘܬܗ ܕܡܠܟܝܙܕܩ ܐܡܪ ܕܝܢ ܕܒܕܡܘܬܗ ܕܐܗܪܘܢ ܢܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 12, 'ܐܠܐ ܐܝܟܢܐ ܕܗܘܐ ܫܘܚܠܦܐ ܒܟܘܡܪܘܬܐ ܗܟܢܐ ܗܘܐ ܫܘܚܠܦܐ ܐܦ ܒܢܡܘܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 13, 'ܗܘ ܓܝܪ ܕܐܬܐܡܪ ܥܠܘܗܝ ܗܠܝܢ ܡܢ ܫܪܒܬܐ ܗܘ ܐܚܪܬܐ ܐܬܝܠܕ ܕܐܢܫ ܡܡܬܘV ܡܢܗ ܠܐ ܫܡܫ ܒܡܕܒܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 14, 'ܓܠܝܐ ܗܝ ܓܝܪ ܕܡܢ ܝܗܘܕܐ ܕܢܚ ܡܪܢ ܡܢ ܫܪܒܬܐ ܕܠܐ ܐܡܪ ܥܠܝܗ ܡܘܫܐ ܡܕV ܥܠ ܟܘܡܪܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 15, 'ܘܝܬܝܪܐܝܬ ܬܘܒ ܝܕܝܥܐ ܒܗܝ ܕܐܡܪ ܕܒܕܡܘܬܗ ܕܡܠܟܝܙܕܩ ܩܐV ܟܘܡܪܐ ܐܚܪܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 16, 'ܗܘ ܕܠܐ ܗܘܐ ܒܢܡܘܤܐ ܕܦܘܩܕܢܐ ܦܓܪܢܝܐ ܗܘܐ ܐܠܐ ܒܚܝܠܐ ܕܚܝܐ ܕܠܐ ܡܫܬܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 17, 'ܡܤܗܕ ܓܝܪ ܥܠܘܗܝ ܕܐܢܬ ܗܘ ܟܘܡܪܐ ܠܥܠV ܒܕܡܘܬܗ ܕܡܠܟܝܙܕܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 18, 'ܫܘܚܠܦܐ ܕܝܢ ܕܗܘܐ ܠܦܘܩܕܢܐ ܩܕܡܝܐ ܡܛܠ ܡܚܝܠܘܬܗ ܘܕܝܘܬܪܢ ܠܝܬ ܗܘܐ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 19, 'ܡܕV ܓܝܪ ܠܐ ܓܡܪ ܢܡܘܤܐ ܥܠ ܕܝܢ ܚܠܦܘܗܝ ܤܒܪܐ ܕܡܝܬܪ ܡܢܗ ܕܒܗ ܡܬܩܪܒܝܢܢ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 20, 'ܘܫܪܪܗ ܠܢ ܒܡܘܡܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 21, 'ܗܢܘܢ ܓܝܪ ܕܠܐ ܡܘܡܬܐ ܗܘܘ ܟܘܡܪܐ ܗܢܐ ܕܝܢ ܒܡܘܡܬܐ ܐܝܟ ܕܐܡܪ ܠܗ ܒܝܕ ܕܘܝܕ ܕܝܡܐ ܡܪܝܐ ܘܠܐ ܢܕܓܠ ܕܐܢܬ ܗܘ ܟܘܡܪܐ ܠܥܠV ܒܕܡܘܬܗ ܕܡܠܟܝܙܕܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 22, 'ܗܢܐ ܟܠܗ ܡܝܬܪܐ ܕܝܬܩܐ ܗܕܐ ܕܗܘܐ ܒܗ ܥܪܒܐ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 23, 'ܘܗܢܘܢ ܗܘܘ ܟܘܡܪܐ ܤܓܝܐܐ ܡܛܠ ܕܡܝܬܝܢ ܗܘܘ ܘܠܐ ܡܫܬܒܩܝܢ ܗܘܘ ܕܢܩܘܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 24, 'ܗܢܐ ܕܝܢ ܡܛܠ ܕܠܥܠV ܩܝV ܠܐ ܥܒܪܐ ܟܘܡܪܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 25, 'ܘܡܫܟܚ ܠܡܚܝܘ ܠܥܠV ܠܐܝܠܝܢ ܕܡܬܩܪܒܝܢ ܒܐܝܕܗ ܠܐܠܗܐ ܚܝ ܗܘ ܓܝܪ ܒܟܠܙܒܢ ܘܡܤܩ ܨܠܘܬܐ ܚܠܦܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 26, 'ܕܐܝܟ ܗܢܐ ܓܝܪ ܟܘܡܪܐ ܐܦ ܙܕܩ ܗܘܐ ܠܢ ܕܟܝܐ ܕܠܐ ܒܝܫܘ ܘܕܠܐ ܛܘܠܫܐ ܕܦܪܝܩ ܡܢ ܚܛܗܐ ܘܡܪܝV ܠܥܠ ܡܢ ܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 27, 'ܘܠܝܬ ܠܗ ܐܘܠܨܢܐ ܟܠܝܘV ܐܝܟ ܪܒܝ ܟܘܡܪܐ ܕܠܘܩܕV ܚܠܦ ܚܛܗܘܗܝ ܢܩܪܒ ܕܒܚܐ ܘܗܝܕܝܢ ܚܠܦ ܥܡܐ ܗܕܐ ܓܝܪ ܥܒܕܗ ܚܕܐ ܙܒܢ ܒܢܦܫܗ ܕܩܪܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 28, 'ܢܡܘܤܐ ܓܝܪ ܒܢܝܢܫܐ ܗܘ ܟܪܝܗܐ ܡܩܝV ܟܘܡܪܐ ܡܠܬܐ ܕܝܢ ܕܡܘܡܬܐ ܕܗܘܬ ܒܬܪ ܢܡܘܤܐ ܒܪܐ ܓܡܝܪܐ ܠܥܠV ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 1, 'ܪܝܫܐ ܕܝܢ ܕܟܠܗܝܢ ܐܝܬ ܠܢ ܪܒ ܟܘܡܪܐ ܐܝܢܐ ܕܝܬܒ ܡܢ ܝܡܝܢܐ ܕܟܘܪܤܝܐ ܕܪܒܘܬܐ ܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 2, 'ܘܗܘܐ ܡܫܡܫܢܐ ܕܒܝܬ ܩܘܕܫܐ ܘܕܡܫܟܢܐ ܕܫܪܪܐ ܗܘ ܕܩܒܥ ܐܠܗܐ ܘܠܐ ܒܪܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 3, 'ܟܠ ܪܒ ܟܘܡܪܐ ܓܝܪ ܕܩܐV ܕܢܩܪܒ ܩܘܪܒܢܐ ܘܕܒܚܐ ܡܛܠ ܗܢܐ ܙܕܩܐ ܗܘܬ ܐܦ ܠܗܢܐ ܕܢܗܘܐ ܐܝܬ ܠܗ ܡܕV ܕܢܩܪܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 4, 'ܘܐܠܘ ܒܐܪܥܐ ܗܘܐ ܐܦܠܐ ܟܘܡܪܐ ܗܘܐ ܗܘܐ ܡܛܠ ܕܐܝܬܝܗܘܢ ܗܘܘ ܟܘܡܪܐ ܕܡܩܪܒܝܢ ܗܘܘ ܩܘܪܒܢܐ ܐܝܟ ܕܒܢܡܘܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 5, 'ܗܢܘܢ ܕܡܫܡܫܝܢ ܠܕܡܘܬܐ ܘܠܛܠܢܝܬܐ ܕܗܠܝܢ ܕܒܫܡܝܐ ܐܝܟ ܕܐܬܐܡܪ ܠܡܘܫܐ ܟܕ ܥܒܕ ܗܘܐ ܡܫܟܢܐ ܕܚܙܝ ܘܥܒܕ ܟܠܡܕV ܒܕܡܘܬܐ ܗܝ ܕܐܬܚܙܝܬ ܠܟ ܒܛܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 6, 'ܗܫܐ ܕܝܢ ܬܫܡܫܬܐ ܕܡܝܬܪܐ ܡܢ ܗܝ ܩܒܠ ܝܫܘܥ ܡܫܝܚܐ ܐܟܡܐ ܕܡܝܬܪܐ ܐܦ ܕܝܬܝܩܐ ܗܝ ܕܥܒܝܕ ܒܗ ܡܨܥܝܐ ܘܒܫܘܘܕܝܐ ܕܡܝܬܪܝܢ ܡܢ ܕܗܝ ܐܬܝܗܒܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 7, 'ܐܠܘ ܓܝܪ ܐܝܬܝܗ ܗܘܬ ܩܕܡܝܬܐ ܕܠܐ ܪܫܝܢ ܠܝܬ ܗܘܐ ܐܬܪܐ ܠܗܕܐ ܕܬܪܬܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 8, 'ܪܫܐ ܠܗܘܢ ܓܝܪ ܘܐܡܪ ܕܗܐ ܝܘܡܬܐ ܐܬܝܢ ܐܡܪ ܡܪܝܐ ܘܐܓܡܘܪ ܥܠ ܒܝܬܐ ܕܒܝܬ ܐܝܤܪܝܠ ܘܥܠ ܒܝܬܐ ܕܒܝܬ ܝܗܘܕܐ ܕܝܬܩܐ ܚܕܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 9, 'ܠܐ ܐܝܟ ܗܝ ܕܝܬܩܐ ܕܝܗܒܬ ܠܐܒܗܝܗܘܢ ܒܝܘܡܐ ܕܐܚܕܬ ܒܐܝܕܗܘܢ ܘܐܦܩܬ ܐܢܘܢ ܡܢ ܐܪܥܐ ܕܡܨܪܝܢ ܡܛܠ ܕܗܢܘܢ ܠܐ ܩܘܝܘ ܒܕܝܬܩܐ ܕܝܠܝ ܐܦ ܐܢܐ ܒܤܝܬ ܒܗܘܢ ܐܡܪ ܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 10, 'ܗܕܐ ܕܝܢ ܕܝܬܩܐ ܕܐܬܠ ܠܒܝܬܐ ܕܒܝܬ ܐܝܤܪܝܠ ܒܬܪ ܝܘܡܬܐ ܗܢܘܢ ܐܡܪ ܡܪܝܐ ܐܬܠܝܘܗܝ ܠܢܡܘܤܝ ܒܡܕܥܝܗܘܢ ܘܥܠ ܠܒܘܬܗܘܢ ܐܟܬܒܝܘܗܝ ܘܐܗܘܐ ܠܗܘܢ ܐܢܐ ܐܠܗܐ ܘܗܢܘܢ ܢܗܘܘܢ ܠܝ ܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 11, 'ܘܠܐ ܢܠܦ ܐܢܫ ܠܒܪ ܡܕܝܢܬܗ ܐܦܠܐ ܠܐܚܘܗܝ ܘܢܐܡܪ ܕܕܥ ܠܡܪܝܐ ܡܛܠ ܕܟܠܗܘܢ ܢܕܥܘܢܢܝ ܡܢ ܙܥܘܪܗܘܢ ܘܥܕܡܐ ܠܩܫܝܫܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 12, 'ܘܐܚܤܐ ܐܢܘܢ ܡܢ ܥܘܠܗܘܢ ܘܚܛܗܝܗܘܢ ܬܘܒ ܠܐ ܐܬܕܟܪ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 13, 'ܒܗܝ ܕܐܡܪ ܚܕܬܐ ܠܩܕܡܝܬܐ ܐܥܬܩܗ ܘܐܝܢܐ ܕܥܬܩ ܘܤܐܒ ܩܪܝܒ ܗܘ ܠܚܒܠܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 1, 'ܒܩܕܡܝܬܐ ܕܝܢ ܐܝܬ ܗܘܐ ܒܗ ܦܘܩܕܐ ܕܬܫܡܫܬܐ ܘܒܝܬ ܩܘܕܫܐ ܥܠܡܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 2, 'ܡܫܟܢܐ ܓܝܪ ܩܕܡܝܐ ܕܐܬܥܒܕ ܐܝܬ ܗܘܐ ܒܗ ܡܢܪܬܐ ܘܦܬܘܪܐ ܘܠܚV ܐܦܐ ܘܡܬܩܪܐ ܗܘܐ ܒܝܬ ܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 3, 'ܡܫܟܢܐ ܕܝܢ ܓܘܝܐ ܕܠܓܘ ܡܢ ܐܦܝ ܬܪܥܐ ܕܬܪܝܢ ܡܬܩܪܐ ܗܘܐ ܩܕܘܫ ܩܘܕܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 4, 'ܘܐܝܬ ܗܘܐ ܒܗ ܒܝܬ ܒܤܡܐ ܕܕܗܒܐ ܘܩܒܘܬܐ ܕܕܝܬܩܐ ܕܩܪܝܡܐ ܟܠܗ ܒܕܗܒܐ ܘܐܝܬ ܒܗ ܩܤܛܐ ܕܕܗܒܐ ܗܝ ܕܐܝܬ ܗܘܐ ܒܗ ܡܢܢܐ ܘܫܒܛܐ ܕܐܗܪܘܢ ܗܘ ܕܐܦܪܥ ܘܠܘܚܐ ܕܕܝܬܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 5, 'ܘܠܥܠ ܡܢܗ ܟܪܘܒܐ ܕܫܘܒܚܐ ܕܡܛܠܝܢ ܥܠ ܚܘܤܝܐ ܠܐ ܗܘܐ ܕܝܢ ܙܒܢܐ ܗܘ ܕܢܐܡܪ ܥܠ ܚܕܐ ܚܕܐ ܡܢ ܗܠܝܢ ܕܗܟܢܐ ܡܬܩܢܢ ܗܘܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 6, 'ܘܠܡܫܟܢܐ ܒܪܝܐ ܒܟܠܙܒܢ ܥܐܠܝܢ ܗܘܘ ܟܘܡܪܐ ܘܡܫܠܡܝܢ ܗܘܘ ܬܫܡܫܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 7, 'ܠܡܫܟܢܐ ܕܝܢ ܕܠܓܘ ܡܢܗ ܚܕܐ ܗܘ ܒܫܢܬܐ ܒܠܚܘܕܘܗܝ ܥܐܠ ܗܘܐ ܪܒܟܘܡܪܐ ܒܕܡܐ ܗܘ ܕܡܩܪܒ ܗܘܐ ܚܠܦ ܢܦܫܗ ܘܚܠܦ ܤܟܠܘܬܗ ܕܥܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 8, 'ܒܗܕܐ ܕܝܢ ܡܘܕܥܐ ܗܘܬ ܪܘܚܐ ܕܩܘܕܫܐ ܕܠܐ ܐܬܓܠܝܬ ܥܕܟܝܠ ܐܘܪܚܐ ܕܩܕܝܫܐ ܟܡܐ ܙܒܢܐ ܕܐܝܬ ܗܘܐ ܩܝܡܐ ܠܡܫܟܢܐ ܩܕܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 9, 'ܘܐܝܬܘܗܝ ܗܘܐ ܡܬܠܐ ܗܢܐ ܠܙܒܢܐ ܗܘ ܕܒܗ ܩܘܪܒܢܐ ܘܕܒܚܐ ܡܬܩܪܒܝܢ ܗܘܘ ܐܝܠܝܢ ܕܠܐ ܡܫܟܚܝܢ ܗܘܘ ܠܡܓܡܪ ܬܐܪܬܗ ܕܡܢ ܕܡܩܪܒ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 10, 'ܐܠܐ ܒܡܐܟܠܐ ܘܒܡܫܬܝܐ ܒܠܚܘܕ ܘܒܡܥܡܘܕܝܬܐ ܕܙܢܝܢ ܙܢܝܢ ܕܐܝܬܝܗܝܢ ܦܘܩܕܐ ܕܒܤܪܐ ܕܤܝܡܝܢ ܥܕܡܐ ܠܙܒܢܐ ܕܬܘܪܨܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 11, 'ܡܫܝܚܐ ܕܝܢ ܕܐܬܐ ܗܘܐ ܪܒܟܘܡܪܐ ܕܛܒܬܐ ܕܤܥܪ ܘܥܠ ܠܡܫܟܢܐ ܪܒܐ ܘܡܫܠܡܢܐ ܕܠܐ ܥܒܝܕ ܒܐܝܕܝܐ ܘܠܐ ܗܘܐ ܡܢ ܗܠܝܢ ܒܪܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 12, 'ܘܠܐ ܥܠ ܒܕܡܐ ܕܨܦܪܝܐ ܘܕܥܓܠܐ ܐܠܐ ܒܕܡܐ ܕܢܦܫܗ ܥܠ ܚܕܐ ܙܒܢ ܠܒܝܬ ܡܩܕܫܐ ܘܐܫܟܚ ܦܘܪܩܢܐ ܕܠܥܠV ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 13, 'ܐܢ ܓܝܪ ܕܡܐ ܕܨܦܪܝܐ ܘܕܥܓܠܐ ܘܩܛܡܐ ܕܥܓܠܬܐ ܡܬܪܤܤ ܗܘܐ ܥܠ ܐܝܠܝܢ ܕܡܬܛܡܐܝܢ ܗܘܘ ܘܡܩܕܫ ܠܗܘܢ ܠܕܘܟܝܐ ܕܒܤܪܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 14, 'ܟܡܐ ܗܟܝܠ ܝܬܝܪܐܝܬ ܕܡܗ ܕܡܫܝܚܐ ܕܒܪܘܚܐ ܕܠܥܠV ܢܦܫܗ ܩܪܒ ܕܠܐ ܡܘV ܠܐܠܗܐ ܢܕܟܐ ܬܐܪܬܢ ܡܢ ܥܒܕܐ ܡܝܬܐ ܕܢܫܡܫ ܠܐܠܗܐ ܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 15, 'ܡܛܠ ܗܢܐ ܗܘ ܗܘܐ ܡܨܥܝܐ ܕܕܝܬܩܐ ܚܕܬܐ ܕܒܡܘܬܗ ܗܘ ܗܘܐ ܦܘܪܩܢܐ ܠܐܝܠܝܢ ܕܥܒܪܘ ܥܠ ܕܝܬܩܐ ܩܕܡܝܬܐ ܕܢܤܒܘܢ ܡܘܠܟܢܐ ܐܝܠܝܢ ܕܐܬܩܪܝܘ ܠܝܪܬܘܬܐ ܕܠܥܠV ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 16, 'ܐܝܟܐ ܓܝܪ ܕܐܝܬ ܕܝܬܩܐ ܡܘܬܐ ܗܘ ܡܚܘܝܐ ܕܗܘ ܕܥܒܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 17, 'ܥܠ ܡܝܬܐ ܕܝܢ ܒܠܚܘܕ ܡܫܬܪܪܐ ܡܛܠ ܕܟܡܐ ܕܚܝ ܗܘ ܕܥܒܕܗ ܠܝܬ ܒܗ ܚܫܚܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 18, 'ܡܛܠ ܗܢܐ ܐܦܠܐ ܩܕܡܝܬܐ ܕܠܐ ܕܡܐ ܐܫܬܪܪܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 19, 'ܟܕ ܐܬܦܩܕ ܓܝܪ ܟܠܗ ܦܘܩܕܢܐ ܡܢ ܡܘܫܐ ܠܥܡܐ ܟܠܗ ܒܢܡܘܤܐ ܢܤܒ ܗܘܐ ܡܘܫܐ ܕܡܐ ܕܥܓܠܬܐ ܘܡܝܐ ܒܥܡܪܐ ܕܙܚܘܪܝܬܐ ܘܙܘܦܐ ܘܪܤ ܥܠ ܤܦܪܐ ܘܥܠ ܥܡܐ ܟܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 20, 'ܘܐܡܪ ܠܗܘܢ ܗܢܐ ܗܘ ܕܡܐ ܕܕܝܬܩܐ ܗܝ ܕܐܬܦܩܕܬ ܠܟܘܢ ܡܢ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 21, 'ܐܦ ܥܠ ܡܫܟܢܐ ܘܥܠ ܟܠܗܘܢ ܡܐܢܐ ܕܬܫܡܫܬܐ ܡܢܗ ܡܢ ܕܡܐ ܪܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 22, 'ܡܛܠ ܕܟܠܡܕV ܒܕܡܐ ܗܘ ܡܬܕܟܐ ܒܢܡܘܤܐ ܘܕܠܐ ܫܘܦܥ ܕܡܐ ܠܝܬ ܫܘܒܩܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 23, 'ܐܢܢܩܐ ܗܝ ܓܝܪ ܕܗܠܝܢ ܕܕܡܘܬܐ ܐܢܝܢ ܕܫܡܝܢܝܬܐ ܒܗܠܝܢ ܡܬܕܟܝܢ ܗܠܝܢ ܕܝܢ ܫܡܝܢܝܬܐ ܒܕܒܚܐ ܕܡܝܬܪܝܢ ܡܢ ܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 24, 'ܠܐ ܗܘܐ ܓܝܪ ܠܒܝܬ ܡܩܕܫܐ ܕܥܒܝܕ ܒܐܝܕܝܐ ܥܠ ܡܫܝܚܐ ܕܐܝܬܘܗܝ ܕܡܘܬܐ ܕܗܘ ܫܪܝܪܐ ܐܠܐ ܠܗ ܠܫܡܝܐ ܥܠ ܕܢܬܚܙܐ ܩܕV ܦܪܨܘܦܗ ܕܐܠܗܐ ܚܠܦܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 25, 'ܐܦܠܐ ܕܢܩܪܒ ܢܦܫܗ ܙܒܢܬܐ ܤܓܝܐܬܐ ܐܝܟ ܕܥܒܕ ܗܘܐ ܪܒ ܟܘܡܪܐ ܘܥܐܠ ܒܟܠ ܫܢܐ ܠܒܝܬ ܡܩܕܫܐ ܒܕܡܐ ܕܠܐ ܕܝܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 26, 'ܘܐܢ ܠܐ ܚܝܒ ܗܘܐ ܕܙܒܢܬܐ ܤܓܝܐܬܐ ܢܚܫ ܡܢ ܫܘܪܝܗ ܕܥܠܡܐ ܗܫܐ ܕܝܢ ܒܚܪܬܗ ܕܥܠܡܐ ܚܕܐ ܗܘ ܙܒܢ ܩܪܒ ܢܦܫܗ ܒܕܒܝܚܘܬܗ ܕܢܒܛܠܝܗ ܠܚܛܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 27, 'ܘܐܝܟܢܐ ܕܤܝV ܠܒܢܝܢܫܐ ܕܚܕܐ ܙܒܢ ܢܡܘܬܘܢ ܘܡܢ ܒܬܪ ܡܘܬܗܘܢ ܕܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 28, 'ܗܟܢܐ ܐܦ ܡܫܝܚܐ ܚܕܐ ܙܒܢ ܐܬܩܪܒ ܘܒܩܢܘܡܗ ܕܒܚ ܚܛܗܐ ܕܤܓܝܐܐ ܕܬܪܬܝܢ ܕܝܢ ܙܒܢܝܢ ܕܠܐ ܚܛܗܝܢ ܡܬܚܙܐ ܠܚܝܝܗܘܢ ܕܐܝܠܝܢ ܕܡܤܟܝܢ ܠܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 1, 'ܢܡܘܤܐ ܓܝܪ ܛܠܢܝܬܐ ܐܝܬ ܗܘܐ ܒܗ ܕܛܒܬܐ ܕܥܬܝܕܢ ܠܐ ܗܘܐ ܩܢܘܡܐ ܕܝܠܗܝܢ ܕܨܒܘܬܐ ܡܛܠ ܗܢܐ ܟܕ ܒܟܠ ܫܢܐ ܗܢܘܢ ܟܕ ܗܢܘܢ ܕܒܚܐ ܡܬܩܪܒܝܢ ܗܘܘ ܠܐ ܡܡܬܘV ܐܫܟܚܘ ܕܢܓܡܪܘܢ ܠܐܝܠܝܢ ܕܡܩܪܒܝܢ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 2, 'ܐܠܘ ܓܝܪ ܓܡܪܝܢ ܗܘܘ ܟܒܪ ܕܝܢ ܐܬܬܢܝܚܘ ܡܢ ܩܘܪܒܢܝܗܘܢ ܡܛܠ ܕܠܐ ܡܟܝܠ ܛܪܝܐ ܗܘܬ ܠܗܘܢ ܬܐܪܬܗܘܢ ܒܚܛܗܐ ܠܐܝܠܝܢ ܕܚܕܐ ܙܒܢ ܐܬܕܟܝܘ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 3, 'ܐܠܐ ܒܗܘܢ ܒܕܒܚܐ ܡܕܟܪܝܢ ܚܛܗܝܗܘܢ ܒܟܠ ܫܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 4, 'ܠܐ ܓܝܪ ܡܫܟܚ ܕܡܐ ܕܬܘܪܐ ܘܕܨܦܪܝܐ ܠܡܕܟܝܘ ܚܛܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 5, 'ܡܛܠ ܗܢܐ ܟܕ ܥܐܠ ܠܥܠܡܐ ܐܡܪ ܒܕܒܚܐ ܘܩܘܪܒܢܐ ܠܐ ܨܒܝܬ ܦܓܪܐ ܕܝܢ ܐܠܒܫܬܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 6, 'ܘܝܩܕܐ ܫܠܡܐ ܕܚܠܦ ܚܛܗܐ ܠܐ ܫܐܠܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 7, 'ܗܝܕܝܢ ܐܡܪܬ ܕܗܐ ܐܢܐ ܐܬܐ ܐܢܐ ܕܒܪܝܫ ܟܬܒܐ ܟܬܝܒ ܥܠܝ ܕܐܥܒܕ ܨܒܝܢܟ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 8, 'ܡܢ ܠܥܠ ܐܡܪ ܕܕܒܚܐ ܘܩܘܪܒܢܐ ܘܝܩܕܐ ܫܠܡܐ ܕܚܠܦ ܚܛܗܐ ܠܐ ܨܒܝܬ ܗܢܘܢ ܕܡܬܩܪܒܝܢ ܗܘܘ ܒܢܡܘܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 9, 'ܘܒܬܪܗ ܐܡܪ ܕܗܐ ܐܬܐ ܐܢܐ ܕܐܥܒܕ ܨܒܝܢܟ ܐܠܗܐ ܒܗܕܐ ܒܛܠ ܠܩܕܡܝܬܐ ܕܢܩܝܡ ܠܕܬܪܬܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 10, 'ܒܗܢܐ ܓܝܪ ܨܒܝܢܗ ܐܬܩܕܫܢ ܒܩܘܪܒܢܐ ܕܦܓܪܗ ܕܝܫܘܥ ܡܫܝܚܐ ܕܚܕܐ ܙܒܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 11, 'ܟܠ ܪܒ ܟܘܡܪܐ ܓܝܪ ܕܩܐܡ ܗܘܐ ܘܡܫܡܫ ܟܠܝܘܡ ܗܢܘܢ ܒܗܢܘܢ ܕܒܚܐ ܡܩܪܒ ܗܘܐ ܐܝܠܝܢ ܕܡܢ ܡܬܘܡ ܠܐ ܡܫܟܚܝܢ ܗܘܘ ܠܡܕܟܝܘ ܚܛܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 12, 'ܗܢܐ ܕܝܢ ܚܕ ܕܒܚܐ ܩܪܒ ܚܠܦ ܚܛܗܐ ܘܝܬܒ ܥܠ ܝܡܝܢܐ ܕܐܠܗܐ ܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 13, 'ܘܡܩܘܐ ܡܟܝܠ ܥܕܡܐ ܕܢܬܬܤܝܡܘܢ ܒܥܠܕܒܒܘܗܝ ܟܘܒܫܐ ܬܚܝܬ ܪܓܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 14, 'ܒܚܕ ܓܝܪ ܩܘܪܒܢܐ ܓܡܪ ܠܐܝܠܝܢ ܕܡܬܩܕܫܝܢ ܒܗ ܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 15, 'ܤܗܕܐ ܠܢ ܕܝܢ ܐܦ ܪܘܚܐ ܕܩܘܕܫܐ ܕܐܡܪܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 16, 'ܗܕܐ ܗܝ ܕܝܬܩܐ ܕܐܬܠ ܠܗܘܢ ܡܢ ܒܬܪ ܝܘܡܬܐ ܗܢܘܢ ܐܡܪ ܡܪܝܐ ܐܬܠܝܘܗܝ ܠܢܡܘܤܝ ܒܡܕܥܝܗܘܢ ܘܥܠ ܠܒܘܬܗܘܢ ܐܟܬܒܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 17, 'ܘܥܘܠܗܘܢ ܘܚܛܗܝܗܘܢ ܠܐ ܐܬܕܟܪ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 18, 'ܐܝܟܐ ܕܝܢ ܕܐܝܬ ܫܘܒܩܢܐ ܕܚܛܗܐ ܠܐ ܡܬܒܥܐ ܩܘܪܒܢܐ ܕܚܠܦ ܚܛܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 19, 'ܐܝܬ ܠܢ ܗܟܝܠ ܐܚܝ ܓܠܝܘܬ ܐܦܐ ܒܡܥܠܢܐ ܕܒܝܬ ܩܘܕܫܐ ܒܕܡܗ ܕܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 20, 'ܘܐܘܪܚܐ ܕܚܝܐ ܕܚܕܬ ܠܢ ܗܫܐ ܒܐܦܝ ܬܪܥܐ ܕܐܝܬܘܗܝ ܒܤܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 21, 'ܘܐܝܬ ܠܢ ܟܘܡܪܐ ܪܒܐ ܥܠ ܒܝܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 22, 'ܢܩܪܘܒ ܗܟܝܠ ܒܠܒܐ ܫܪܝܪܐ ܘܒܬܘܟܠܢܐ ܕܗܝܡܢܘܬܐ ܟܕ ܪܤܝܤܝܢ ܠܒܘܬܢ ܘܕܟܝܢ ܡܢ ܬܐܪܬܐ ܒܝܫܬܐ ܘܡܤܚܝ ܦܓܪܢ ܒܡܝܐ ܕܟܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 23, 'ܘܢܚܡܤܢ ܒܬܘܕܝܬܐ ܕܤܒܪܢ ܘܠܐ ܢܨܛܠܐ ܡܗܝܡܢ ܗܘ ܓܝܪ ܡܢ ܕܡܠܟ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 24, 'ܘܢܚܘܪ ܚܕ ܒܚܕ ܒܓܘܪܓܐ ܕܚܘܒܐ ܘܕܥܒܕܐ ܛܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 25, 'ܘܠܐ ܢܗܘܐ ܫܒܩܝܢ ܟܢܘܫܝܢ ܐܝܟ ܕܐܝܬ ܥܝܕܐ ܠܐܢܫ ܐܢܫ ܐܠܐ ܒܥܘ ܚܕ ܡܢ ܚܕ ܝܬܝܪܐܝܬ ܟܡܐ ܕܚܙܝܬܘܢ ܕܩܪܒ ܝܘܡܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 26, 'ܐܢ ܓܝܪ ܒܨܒܝܢܗ ܢܚܛܐ ܐܢܫ ܡܢ ܒܬܪ ܕܩܒܠ ܝܕܥܬܐ ܕܫܪܪܐ ܠܝܬ ܡܟܝܠ ܕܒܚܬܐ ܕܬܬܩܪܒ ܚܠܦ ܚܛܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 27, 'ܐܠܐ ܥܬܝܕ ܗܘ ܕܝܢܐ ܕܚܝܠܐ ܘܛܢܢܐ ܕܢܘܪܐ ܕܐܟܠ ܠܒܥܠܕܒܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 28, 'ܐܢ ܓܝܪ ܐܝܢܐ ܕܥܒܪ ܥܠ ܢܡܘܤܐ ܕܡܘܫܐ ܥܠ ܦܘܡ ܬܪܝܢ ܘܬܠܬܐ ܤܗܕܝܢ ܕܠܐ ܪܚܡܝܢ ܡܐܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 29, 'ܟܡܐ ܤܒܪܝܢ ܐܢܬܘܢ ܝܬܝܪܐܝܬ ܡܤܡ ܒܪܫܐ ܢܩܒܠ ܐܝܢܐ ܕܕܫܗ ܠܒܪܗ ܕܐܠܗܐ ܘܚܫܒ ܕܡܐ ܕܕܝܬܩܐ ܕܝܠܗ ܐܝܟ ܕܟܠܢܫ ܕܒܗ ܐܬܩܕܫ ܘܨܥܪ ܠܪܘܚܐ ܕܛܝܒܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 30, 'ܝܕܥܝܢܢ ܠܗܘ ܕܐܡܪ ܕܕܝܠܝ ܗܝ ܬܒܥܬܐ ܘܐܢܐ ܐܦܪܘܥ ܘܬܘܒ ܕܢܕܘܢ ܡܪܝܐ ܠܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 31, 'ܕܚܠܬܐ ܗܝ ܪܒܬܐ ܠܡܦܠ ܒܐܝܕܘܗܝ ܕܐܠܗܐ ܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 32, 'ܐܬܕܟܪܘ ܗܟܝܠ ܠܝܘܡܬܐ ܩܕܡܝܐ ܗܢܘܢ ܕܒܗܘܢ ܩܒܠܬܘܢ ܡܥܡܘܕܝܬܐ ܘܐܓܘܢܐ ܪܒܐ ܤܝܒܪܬܘܢ ܕܚܫܐ ܒܚܤܕܐ ܘܒܐܘܠܨܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 33, 'ܘܕܗܘܝܬܘܢ ܚܙܘܢܐ ܘܐܦ ܐܫܬܘܬܦܬܘܢ ܠܐܢܫܐ ܕܗܠܝܢ ܤܝܒܪܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 34, 'ܘܟܐܒ ܠܟܘܢ ܥܠ ܐܝܠܝܢ ܕܐܤܝܪܝܢ ܘܚܛܘܦܝܐ ܕܢܟܤܝܟܘܢ ܒܚܕܘܬܐ ܤܝܒܪܬܘܢ ܡܛܠ ܕܝܕܥܝܢ ܐܢܬܘܢ ܕܐܝܬ ܠܟܘܢ ܩܢܝܢܐ ܒܫܡܝܐ ܕܡܝܬܪ ܗܘ ܘܠܐ ܥܒܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 35, 'ܠܐ ܗܟܝܠ ܬܘܒܕܘܢ ܓܠܝܘܬ ܐܦܐ ܕܐܝܬ ܠܟܘܢ ܕܥܬܝܕ ܠܗ ܐܓܪܐ ܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 36, 'ܡܤܝܒܪܢܘܬܐ ܗܘ ܓܝܪ ܡܬܒܥܝܐ ܠܟܘܢ ܕܬܥܒܕܘܢ ܨܒܝܢܐ ܕܐܠܗܐ ܘܬܤܒܘܢ ܡܘܠܟܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 37, 'ܡܛܠ ܕܩܠܝܠ ܗܘ ܙܒܢܐ ܘܛܒ ܙܥܘܪ ܕܢܐܬܐ ܗܘ ܕܐܬܐ ܘܠܐ ܢܘܚܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 38, 'ܟܐܢܐ ܕܝܢ ܡܢ ܗܝܡܢܘܬܐ ܕܝܠܝ ܢܚܐ ܘܐܢ ܡܬܩܛܥܐ ܠܗ ܠܐ ܨܒܝܐ ܒܗ ܢܦܫܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 39, 'ܚܢܢ ܕܝܢ ܠܐ ܗܘܝܢ ܕܩܘܛܥܐ ܕܡܘܒܠܐ ܠܐܒܕܢܐ ܐܠܐ ܕܗܝܡܢܘܬܐ ܕܡܩܢܝܐ ܠܢ ܢܦܫܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 1, 'ܐܝܬܝܗ ܕܝܢ ܗܝܡܢܘܬܐ ܦܝܤܐ ܥܠ ܐܝܠܝܢ ܕܐܝܬܝܗܝܢ ܒܤܒܪܐ ܐܝܟ ܗܘ ܕܗܘܝ ܠܗܝܢ ܒܤܘܥܪܢܐ ܘܓܠܝܢܐ ܕܐܝܠܝܢ ܕܠܐ ܡܬܚܙܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 2, 'ܘܒܗܕܐ ܗܘܬ ܤܗܕܘܬܐ ܥܠ ܩܫܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 3, 'ܒܗܝܡܢܘܬܐ ܓܝܪ ܡܤܬܟܠܝܢܢ ܕܐܬܬܩܢܘ ܥܠܡܐ ܒܡܠܬܐ ܕܐܠܗܐ ܘܗܠܝܢ ܕܡܬܚܙܝܢ ܗܘܝ ܡܢ ܐܝܠܝܢ ܕܠܐ ܡܬܚܙܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 4, 'ܒܗܝܡܢܘܬܐ ܩܪܒ ܗܒܝܠ ܕܒܚܬܐ ܕܡܝܬܪܐ ܛܒ ܡܢ ܕܩܐܝܢ ܠܐܠܗܐ ܘܡܛܠܬܗ ܗܘܬ ܥܠܘܗܝ ܤܗܕܘܬܐ ܕܟܐܢܐ ܗܘ ܘܤܗܕ ܥܠ ܩܘܪܒܢܗ ܐܠܗܐ ܘܡܛܠܬܗ ܐܦ ܟܕ ܡܝܝܬ ܡܡܠܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 5, 'ܒܗܝܡܢܘܬܐ ܐܫܬܢܝ ܚܢܘܟ ܘܡܘܬܐ ܠܐ ܛܥܡ ܘܠܐ ܐܫܬܟܚ ܡܛܠ ܕܫܢܝܗ ܐܠܗܐ ܡܢ ܩܕܡ ܕܢܫܢܝܘܗܝ ܓܝܪ ܗܘܬ ܥܠܘܗܝ ܤܗܕܘܬܐ ܕܫܦܪ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 6, 'ܕܠܐ ܗܝܡܢܘܬܐ ܕܝܢ ܠܐ ܐܢܫ ܡܫܟܚ ܕܢܫܦܪ ܠܐܠܗܐ ܚܝܒ ܗܘ ܓܝܪ ܡܢ ܕܡܬܩܪܒ ܠܘܬ ܐܠܗܐ ܕܢܗܝܡܢ ܕܐܝܬܘܗܝ ܘܠܐܝܠܝܢ ܕܒܥܝܢ ܠܗ ܗܘܐ ܦܪܘܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 7, 'ܒܗܝܡܢܘܬܐ ܢܘܚ ܟܕ ܐܬܡܠܠ ܥܡܗ ܥܠ ܐܝܠܝܢ ܕܠܐ ܡܬܚܙܝܢ ܗܘܝ ܕܚܠ ܘܥܒܕ ܠܗ ܩܒܘܬܐ ܠܚܝܐ ܕܒܢܝ ܒܝܬܗ ܕܒܗ ܚܝܒܗ ܠܥܠܡܐ ܘܗܘܐ ܝܪܬܐ ܕܟܐܢܘܬܐ ܕܒܗܝܡܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 8, 'ܒܗܝܡܢܘܬܐ ܐܒܪܗܡ ܟܕ ܐܬܩܪܝ ܐܫܬܡܥ ܕܢܦܘܩ ܠܐܬܪܐ ܗܘ ܕܥܬܝܕ ܗܘܐ ܕܢܤܒ ܠܝܪܬܘܬܐ ܘܢܦܩ ܟܕ ܠܐ ܝܕܥ ܗܘܐ ܠܐܝܟܐ ܐܙܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 9, 'ܒܗܝܡܢܘܬܐ ܗܘܐ ܬܘܬܒܐ ܒܐܪܥܐ ܗܝ ܕܐܬܡܠܟܬ ܠܗ ܐܝܟ ܕܒܢܘܟܪܝܬܐ ܘܒܡܫܟܢܐ ܥܡܪ ܥܡ ܐܝܤܚܩ ܘܝܥܩܘܒ ܒܢܝ ܝܪܬܘܬܐ ܕܝܠܗ ܕܡܘܠܟܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 10, 'ܡܤܟܐ ܗܘܐ ܓܝܪ ܠܡܕܝܢܬܐ ܕܫܬܐܤܬܐ ܐܝܬ ܠܗ ܕܐܘܡܢܗ ܘܥܒܘܕܗ ܐܠܗܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 11, 'ܒܗܝܡܢܘܬܐ ܐܦ ܤܪܐ ܕܥܩܪܬܐ ܗܘܬ ܢܤܒܬ ܚܝܠܐ ܕܬܩܒܠ ܙܪܥܐ ܘܕܠܐ ܒܙܒܢܐ ܕܫܢܝܗ ܝܠܕܬ ܥܠ ܕܐܫܪܬ ܕܡܗܝܡܢ ܗܘ ܗܘ ܕܡܠܟ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 12, 'ܡܛܠ ܗܢܐ ܡܢ ܚܕ ܕܒܛܠ ܒܤܝܒܘܬܐ ܐܬܝܠܕܘ ܤܓܝܐܐ ܐܝܟ ܟܘܟܒܐ ܕܒܫܡܝܐ ܘܐܝܟ ܚܠܐ ܕܥܠ ܤܦܬܗ ܕܝܡܐ ܕܡܢܝܢ ܠܝܬ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 13, 'ܒܗܝܡܢܘܬܐ ܡܝܬܘ ܗܠܝܢ ܟܠܗܘܢ ܘܠܐ ܢܤܒܘ ܡܘܠܟܢܗܘܢ ܐܠܐ ܡܢ ܪܘܚܩܐ ܚܙܐܘܗܝ ܘܚܕܝܘ ܒܗ ܘܐܘܕܝܘ ܕܐܟܤܢܝܐ ܐܢܘܢ ܘܬܘܬܒܐ ܒܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 14, 'ܐܝܠܝܢ ܕܝܢ ܕܗܠܝܢ ܐܡܪܝܢ ܡܚܘܝܢ ܕܠܡܕܝܢܬܗܘܢ ܒܥܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 15, 'ܘܐܠܘ ܠܡܕܝܢܬܐ ܗܝ ܕܢܦܩܘ ܡܢܗ ܒܥܝܢ ܗܘܘ ܐܝܬ ܗܘܐ ܠܗܘܢ ܙܒܢܐ ܕܬܘܒ ܢܗܦܟܘܢ ܢܐܙܠܘܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 16, 'ܗܫܐ ܕܝܢ ܝܕܝܥܐ ܕܠܕܛܒܐ ܡܢܗ ܪܓܝܢ ܗܘܘ ܠܗܝ ܕܐܝܬܝܗ ܒܫܡܝܐ ܡܛܠ ܗܢܐ ܠܐ ܢܟܦ ܐܠܗܐ ܕܐܠܗܗܘܢ ܢܬܩܪܐ ܛܝܒ ܠܗܘܢ ܓܝܪ ܡܕܝܢܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 17, 'ܒܗܝܡܢܘܬܐ ܩܪܒ ܐܒܪܗܡ ܠܐܝܤܚܩ ܒܢܤܝܘܢܗ ܘܠܝܚܝܕܗ ܐܤܩ ܠܡܕܒܚܐ ܠܗܘ ܕܩܒܠ ܗܘܐ ܒܡܘܠܟܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 18, 'ܐܬܐܡܪ ܗܘܐ ܠܗ ܓܝܪ ܕܒܐܝܤܚܩ ܢܬܩܪܐ ܠܟ ܙܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 19, 'ܘܐܬܪܥܝ ܗܘܐ ܒܢܦܫܗ ܕܡܛܝܐ ܒܐܝܕܘܗܝ ܕܐܠܗܐ ܐܦ ܡܢ ܡܝܬܐ ܠܡܩܡܘ ܘܡܛܠ ܗܢܐ ܒܡܬܠܐ ܐܬܝܗܒ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 20, 'ܒܗܝܡܢܘܬܐ ܕܡܕܡ ܕܥܬܝܕ ܗܘܐ ܒܪܟ ܐܝܤܚܩ ܠܝܥܩܘܒ ܘܠܥܤܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 21, 'ܒܗܝܡܢܘܬܐ ܟܕ ܡܐܬ ܝܥܩܘܒ ܒܪܟ ܠܟܠ ܚܕ ܚܕ ܡܢ ܒܢܘܗܝ ܕܝܘܤܦ ܘܤܓܕ ܥܠ ܪܝܫ ܚܘܛܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 22, 'ܒܗܝܡܢܘܬܐ ܝܘܤܦ ܟܕ ܡܐܬ ܥܗܕ ܠܡܦܩܬܐ ܕܒܢܝ ܐܝܤܪܝܠ ܘܦܩܕ ܥܠ ܓܪܡܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 23, 'ܒܗܝܡܢܘܬܐ ܐܒܗܘܗܝ ܕܡܘܫܐ ܛܫܝܘܗܝ ܟܕ ܐܬܝܠܕ ܝܪܚܐ ܬܠܬܐ ܕܚܙܘ ܕܫܦܝܪ ܗܘܐ ܛܠܝܐ ܘܠܐ ܕܚܠܘ ܡܢ ܦܘܩܕܢܐ ܕܡܠܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 24, 'ܒܗܝܡܢܘܬܐ ܡܘܫܐ ܟܕ ܗܘܐ ܓܒܪܐ ܟܦܪ ܕܠܐ ܢܬܩܪܐ ܒܪܐ ܠܒܪܬܗ ܕܦܪܥܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 25, 'ܘܓܒܐ ܠܗ ܕܒܐܘܠܨܢܐ ܥܡ ܥܡܗ ܕܐܠܗܐ ܢܗܘܐ ܘܠܐ ܕܙܒܢ ܙܥܘܪ ܢܬܒܤܡ ܒܚܛܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 26, 'ܘܐܬܪܥܝ ܕܡܝܬܪ ܗܘ ܥܘܬܪܐ ܕܚܤܕܗ ܕܡܫܝܚܐ ܛܒ ܡܢ ܤܝܡܬܗ ܕܡܨܪܝܢ ܚܐܪ ܗܘܐ ܓܝܪ ܒܦܘܪܥܢ ܐܓܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 27, 'ܒܗܝܡܢܘܬܐ ܫܒܩܗ ܠܡܨܪܝܢ ܘܠܐ ܕܚܠ ܡܢ ܚܡܬܗ ܕܡܠܟܐ ܘܤܝܒܪ ܐܝܟ ܗܘ ܕܚܙܐ ܗܘܐ ܠܐܠܗܐ ܕܠܐ ܡܬܚܙܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 28, 'ܒܗܝܡܢܘܬܐ ܥܒܕ ܦܨܚܐ ܘܪܤܤ ܕܡܐ ܕܠܐ ܢܬܩܪܒ ܠܗܘܢ ܗܘ ܕܡܚܒܠ ܗܘܐ ܒܘܟܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 29, 'ܒܗܝܡܢܘܬܐ ܥܒܪܘ ܝܡܐ ܕܤܘܦ ܐܝܟ ܕܥܠ ܐܪܥܐ ܝܒܝܫܬܐ ܘܒܗ ܐܬܒܠܥܘ ܡܨܪܝܐ ܟܕ ܐܡܪܚܘ ܥܠܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 30, 'ܒܗܝܡܢܘܬܐ ܫܘܪܝܗ ܕܐܝܪܝܚܘ ܢܦܠܘ ܡܢ ܕܐܬܟܪܟܘ ܫܒܥܐ ܝܘܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 31, 'ܒܗܝܡܢܘܬܐ ܪܚܒ ܙܢܝܬܐ ܠܐ ܐܒܕܬ ܥܡ ܗܢܘܢ ܕܠܐ ܐܫܬܡܥܘ ܕܩܒܠܬ ܠܓܫܘܫܐ ܒܫܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 32, 'ܘܡܢܐ ܬܘܒ ܐܡܪ ܙܥܘܪ ܗܘ ܠܝ ܓܝܪ ܙܒܢܐ ܕܐܫܬܥܐ ܥܠ ܓܕܥܘܢ ܘܥܠ ܒܪܩ ܘܥܠ ܫܡܫܘܢ ܘܥܠ ܢܦܬܚ ܘܥܠ ܕܘܝܕ ܘܥܠ ܫܡܘܐܝܠ ܘܥܠ ܫܪܟܐ ܕܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 33, 'ܐܝܠܝܢ ܕܒܗܝܡܢܘܬܐ ܙܟܘ ܠܡܠܟܘܬܐ ܘܦܠܚܘ ܟܐܢܘܬܐ ܘܩܒܠܘ ܡܘܠܟܢܐ ܘܤܟܪܘ ܦܘܡܐ ܕܐܪܝܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 34, 'ܘܕܥܟܘ ܚܝܠܐ ܕܢܘܪܐ ܘܐܬܦܨܝܘ ܡܢ ܦܘܡܐ ܕܤܝܦܐ ܘܐܬܚܝܠܘ ܡܢ ܟܘܪܗܢܐ ܘܗܘܘ ܚܝܠܬܢܐ ܒܩܪܒܐ ܘܤܚܦܘ ܡܫܪܝܬܐ ܕܒܥܠܕܒܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 35, 'ܘܝܗܒܘ ܠܢܫܐ ܒܢܝܗܝܢ ܡܢ ܩܝܡܬܐ ܕܡܝܬܐ ܘܐܚܪܢܐ ܒܫܢܕܐ ܡܝܬܘ ܘܠܐ ܤܟܝܘ ܠܡܬܦܨܝܘ ܕܩܝܡܬܐ ܡܝܬܪܬܐ ܬܗܘܐ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 36, 'ܐܚܪܢܐ ܕܝܢ ܠܒܙܚܐ ܘܠܢܓܕܐ ܥܠܘ ܐܚܪܢܐ ܠܐܤܘܪܐ ܘܠܚܒܘܫܝܐ ܐܫܬܠܡܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 37, 'ܐܚܪܢܐ ܐܬܪܓܡܘ ܐܚܪܢܐ ܐܬܢܤܪܘ ܐܚܪܢܐ ܒܦܘܡܐ ܕܤܝܦܐ ܡܝܬܘ ܐܚܪܢܐ ܐܬܟܪܟܘ ܟܕ ܠܒܝܫܝܢ ܡܫܟܐ ܕܐܡܪܐ ܘܕܥܙܐ ܘܤܢܝܩܝܢ ܘܐܠܝܨܝܢ ܘܡܛܪܦܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 38, 'ܐܢܫܐ ܕܠܐ ܫܘܐ ܗܘܐ ܠܗܘܢ ܥܠܡܐ ܘܗܘܘ ܐܝܟ ܛܥܝܐ ܒܚܘܪܒܐ ܘܒܛܘܪܐ ܘܒܡܥܪܐ ܘܒܦܥܪܝܗ ܕܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 39, 'ܘܗܠܝܢ ܟܠܗܘܢ ܕܗܘܬ ܥܠܝܗܘܢ ܤܗܕܘܬܐ ܒܗܝܡܢܘܬܗܘܢ ܠܐ ܩܒܠܘ ܡܘܠܟܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 40, 'ܡܛܠ ܕܐܠܗܐ ܩܕܡ ܚܪ ܒܥܘܕܪܢܢ ܕܝܠܢ ܕܠܐ ܒܠܥܕܝܢ ܢܬܓܡܪܘܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 1, 'ܡܛܠ ܗܢܐ ܐܦ ܚܢܢ ܕܐܝܬ ܠܢ ܗܠܝܢ ܟܠܗܘܢ ܤܗܕܐ ܕܐܝܟ ܥܢܢܐ ܚܕܝܪܝܢ ܠܢ ܢܫܕܐ ܡܢܢ ܟܠ ܝܘܩܪܝܢ ܐܦ ܚܛܝܬܐ ܕܒܟܠܙܒܢ ܡܛܝܒܐ ܗܝ ܠܢ ܘܒܡܤܝܒܪܢܘܬܐ ܢܪܗܛܝܘܗܝ ܠܐܓܘܢܐ ܗܢܐ ܕܤܝܡ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 2, 'ܘܢܚܘܪ ܒܝܫܘܥ ܕܗܘ ܗܘܐ ܪܝܫܐ ܘܓܡܘܪܐ ܠܗܝܡܢܘܬܢ ܕܚܠܦ ܚܕܘܬܐ ܕܐܝܬ ܗܘܐ ܠܗ ܤܝܒܪ ܨܠܝܒܐ ܘܥܠ ܒܗܬܬܐ ܐܡܤܪ ܘܥܠ ܝܡܝܢܐ ܕܟܘܪܤܝܗ ܕܐܠܗܐ ܝܬܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 3, 'ܚܙܘ ܗܟܝܠ ܟܡܐ ܤܝܒܪ ܡܢ ܚܛܝܐ ܗܢܘܢ ܕܗܢܘܢ ܗܘܘ ܤܩܘܒܠܐ ܠܢܦܫܗܘܢ ܕܠܐ ܬܡܐܢ ܠܟܘܢ ܘܠܐ ܬܬܪܦܐ ܢܦܫܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 4, 'ܠܐ ܥܕܟܝܠ ܡܛܝܬܘܢ ܥܕܡܐ ܠܕܡܐ ܒܐܓܘܢܐ ܕܠܘܩܒܠ ܚܛܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 5, 'ܘܛܥܝܬܘܢܝܗܝ ܠܝܘܠܦܢܐ ܐܝܢܐ ܕܐܝܟ ܕܠܒܢܝܐ ܐܡܪ ܠܟܘܢ ܒܪܝ ܠܐ ܬܗܡܐ ܡܢ ܡܪܕܘܬܗ ܕܡܪܝܐ ܘܠܐ ܬܪܦܐ ܢܦܫܟ ܐܡܬܝ ܕܡܢܗ ܡܬܟܘܢ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 6, 'ܠܡܢ ܕܪܚܡ ܓܝܪ ܡܪܝܐ ܪܕܐ ܠܗ ܘܡܢܓܕ ܠܒܢܝܐ ܐܝܠܝܢ ܕܗܘ ܨܒܐ ܒܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 7, 'ܤܝܒܪܘ ܗܟܝܠ ܡܪܕܘܬܐ ܡܛܠ ܕܐܝܟ ܕܠܘܬ ܒܢܝܐ ܤܥܪ ܨܐܕܝܟܘܢ ܐܠܗܐ ܐܝܢܘ ܓܝܪ ܒܪܐ ܕܠܐ ܪܕܐ ܠܗ ܐܒܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 8, 'ܘܐܢ ܕܠܐ ܡܪܕܘܬܐ ܐܢܬܘܢ ܗܝ ܕܒܗ ܡܬܪܕܐ ܟܠܢܫ ܗܘܝܬܘܢ ܠܟܘܢ ܢܘܟܪܝܐ ܘܠܐ ܒܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 9, 'ܘܐܢ ܐܒܗܝܢ ܕܒܤܪܐ ܪܕܝܢ ܗܘܘ ܠܢ ܘܒܗܬܝܢ ܗܘܝܢ ܡܢܗܘܢ ܟܡܐ ܗܟܝܠ ܚܝܒܝܢܢ ܕܢܫܬܥܒܕ ܠܐܒܘܗܝܢ ܕܪܘܚܬܐ ܘܢܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 10, 'ܗܢܘܢ ܓܝܪ ܠܙܒܢ ܗܘ ܙܥܘܪ ܐܝܟ ܕܨܒܝܢ ܗܘܘ ܪܕܝܢ ܗܘܘ ܠܢ ܐܠܗܐ ܕܝܢ ܠܥܘܕܪܢܢ ܕܢܫܬܘܬܦ ܠܩܕܝܫܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 11, 'ܟܠ ܡܪܕܘܬܐ ܕܝܢ ܒܙܒܢܗ ܠܐ ܡܤܬܒܪܐ ܕܕܚܕܘܬܐ ܗܝ ܐܠܐ ܕܟܪܝܘܬܐ ܠܚܪܬܐ ܕܝܢ ܦܐܪܐ ܕܫܠܡܐ ܘܕܙܕܝܩܘܬܐ ܝܗܒܐ ܠܐܝܠܝܢ ܕܒܗ ܐܬܕܪܫܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 12, 'ܡܛܠ ܗܢܐ ܐܝܕܝܟܘܢ ܡܫܪܝܬܐ ܘܒܘܪܟܝܟܘܢ ܪܥܠܬܐ ܫܪܪܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 13, 'ܘܫܒܝܠܐ ܬܪܝܨܐ ܥܒܕܘ ܠܪܓܠܝܟܘܢ ܕܗܕܡܐ ܕܚܓܝܪ ܠܐ ܢܛܥܫ ܐܠܐ ܢܬܐܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 14, 'ܗܪܛܘ ܒܬܪ ܫܠܡܐ ܥܡ ܟܠ ܐܢܫ ܘܒܬܪ ܩܕܝܫܘܬܐ ܕܒܠܥܕܝܗ ܐܢܫ ܠܡܪܢ ܠܐ ܚܙܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 15, 'ܘܗܘܝܬܘܢ ܙܗܝܪܝܢ ܕܠܡܐ ܐܢܫ ܢܫܬܟܚ ܒܟܘܢ ܕܚܤܝܪ ܡܢ ܛܝܒܘܬܐ ܕܐܠܗܐ ܐܘ ܕܠܡܐ ܥܩܪܐ ܕܡܪܪܐ ܢܦܩ ܥܘܦܝܐ ܘܢܗܪܟܘܢ ܘܒܗ ܤܓܝܐܐ ܢܤܬܝܒܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 16, 'ܐܘ ܠܡܐ ܐܢܫ ܢܫܬܟܚ ܒܟܘܢ ܕܙܢܝ ܘܪܦܐ ܐܝܟ ܥܤܘ ܕܒܚܕܐ ܡܐܟܘܠܬܐ ܙܒܢ ܒܘܟܪܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 17, 'ܝܕܥܝܢ ܐܢܬܘܢ ܓܝܪ ܕܐܦ ܡܢ ܒܬܪܟܢ ܨܒܐ ܗܘܐ ܕܢܐܪܬ ܒܘܪܟܬܐ ܘܐܤܬܠܝ ܐܬܪܐ ܓܝܪ ܠܬܝܒܘܬܐ ܠܐ ܐܫܟܚ ܟܕ ܒܕܡܥܐ ܒܥܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 18, 'ܠܐ ܓܝܪ ܐܬܩܪܒܬܘܢ ܠܢܘܪܐ ܕܝܩܕܐ ܘܡܬܓܫܐ ܐܦܠܐ ܠܚܫܘܟܐ ܘܠܥܪܦܠܐ ܘܠܥܪܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 19, 'ܘܠܐ ܠܩܠܐ ܕܩܪܢܐ ܘܠܩܠܐ ܕܡܠܐ ܗܘ ܕܗܢܘܢ ܕܫܡܥܘܗܝ ܐܫܬܐܠܘ ܕܠܐ ܢܬܬܘܤܦ ܢܬܡܠܠ ܥܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 20, 'ܠܐ ܓܝܪ ܡܫܟܚܝܢ ܗܘܘ ܠܡܤܝܒܪܘ ܡܕܡ ܕܐܬܦܩܕܘ ܕܐܦܢ ܚܝܘܬܐ ܬܬܩܪܒ ܠܘܬ ܛܘܪܐ ܬܬܪܓܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 21, 'ܘܗܟܢܐ ܕܚܝܠ ܗܘܐ ܚܙܘܐ ܕܡܘܫܐ ܐܡܪ ܕܕܚܝܠ ܐܢܐ ܘܪܬܝܬ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 22, 'ܐܢܬܘܢ ܕܝܢ ܐܬܩܪܒܬܘܢ ܠܛܘܪܐ ܕܨܗܝܘܢ ܘܠܡܕܝܢܬܐ ܕܐܠܗܐ ܚܝܐ ܠܐܘܪܫܠܡ ܕܒܫܡܝܐ ܘܠܟܢܫܐ ܕܪܒܘܬܐ ܕܡܠܐܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 23, 'ܘܠܥܕܬܐ ܕܒܘܟܪܐ ܕܡܬܟܬܒܝܢ ܒܫܡܝܐ ܘܠܐܠܗܐ ܕܝܢܐ ܕܟܠ ܘܠܪܘܚܬܐ ܕܟܐܢܐ ܕܐܬܓܡܪܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 24, 'ܘܠܝܫܘܥ ܡܨܥܝܐ ܕܕܝܬܩܐ ܚܕܬܐ ܘܠܪܤܤ ܕܡܗ ܕܡܡܠܠ ܛܒ ܡܢ ܗܘ ܕܗܒܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 25, 'ܐܙܕܗܪܘ ܗܟܝܠ ܕܠܡܐ ܬܫܬܐܠܘܢ ܡܢ ܡܢ ܕܡܠܠ ܥܡܟܘܢ ܐܢ ܓܝܪ ܗܢܘܢ ܠܐ ܐܬܦܨܝܘ ܕܐܫܬܐܠܘ ܡܢ ܕܡܠܠ ܥܡܗܘܢ ܒܐܪܥܐ ܚܕ ܟܡܐ ܚܢܢ ܐܢ ܢܫܬܐܠ ܡܢ ܡܢ ܕܡܠܠ ܥܡܢ ܡܢ ܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 26, 'ܐܝܢܐ ܕܩܠܗ ܐܪܥܐ ܐܙܝܥ ܗܫܐ ܕܝܢ ܡܠܟ ܘܐܡܪ ܕܬܘܒ ܚܕܐ ܙܒܢ ܐܢܐ ܐܙܝܥ ܠܐ ܒܠܚܘܕ ܐܪܥܐ ܐܠܐ ܐܦ ܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 27, 'ܗܕܐ ܕܝܢ ܕܐܡܪ ܚܕܐ ܙܒܢ ܡܚܘܝܐ ܫܘܚܠܦܐ ܕܗܢܘܢ ܕܡܬܙܝܥܝܢ ܡܛܠ ܕܥܒܝܕܐ ܐܢܘܢ ܕܢܩܘܘܢ ܗܢܘܢ ܕܠܐ ܡܬܙܝܥܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 28, 'ܡܛܠ ܗܟܝܠ ܕܩܒܠܢ ܡܠܟܘܬܐ ܕܠܐ ܡܬܙܝܥܐ ܢܐܚܘܕ ܛܝܒܘܬܐ ܕܒܗ ܢܫܡܫ ܘܢܫܦܪ ܠܐܠܗܐ ܒܬܚܡܨܬܐ ܘܒܕܚܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 29, 'ܐܠܗܢ ܓܝܪ ܢܘܪܐ ܗܘ ܐܟܠܬܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 1, 'ܚܘܒܐ ܕܐܚܐ ܢܟܬܪ ܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 2, 'ܘܪܚܡܬܐ ܕܐܟܤܢܝܐ ܠܐ ܬܛܥܘܢ ܒܗܕܐ ܓܝܪ ܫܘܘ ܐܢܫܐ ܕܟܕ ܠܐ ܪܓܝܫܝܢ ܢܩܒܠܘܢ ܡܠܐܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 3, 'ܥܗܕܘ ܠܐܝܠܝܢ ܕܐܤܝܪܝܢ ܐܝܟ ܗܘ ܕܥܡܗܘܢ ܐܤܝܪܝܢ ܐܢܬܘܢ ܐܬܕܟܪܘ ܠܐܝܠܝܢ ܕܐܠܝܨܝܢ ܐܝܟ ܐܢܫܐ ܕܒܤܪܐ ܠܒܝܫܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 4, 'ܡܝܩܪ ܗܘ ܙܘܘܓܐ ܒܟܠ ܘܥܪܤܗܘܢ ܕܟܝܐ ܗܝ ܠܙܢܝܐ ܕܝܢ ܘܠܓܝܪܐ ܕܐܢ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 5, 'ܠܐ ܗܘܐ ܪܚܡ ܟܤܦܐ ܪܥܝܢܟܘܢ ܐܠܐ ܢܤܦܩ ܠܟܘܢ ܡܕܡ ܕܐܝܬ ܠܟܘܢ ܗܘ ܓܝܪ ܡܪܝܐ ܐܡܪ ܕܠܐ ܐܫܒܩܟ ܘܠܐ ܐܪܦܐ ܒܟ ܐܝܕܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 6, 'ܘܐܝܬ ܠܢ ܕܢܐܡܪ ܬܟܝܠܐܝܬ ܡܪܝ ܡܥܕܪܢܝ ܠܐ ܐܕܚܠ ܡܢܐ ܥܒܕ ܠܝ ܒܪܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 7, 'ܗܘܝܬܘܢ ܥܗܕܝܢ ܠܡܕܒܪܢܝܟܘܢ ܐܝܠܝܢ ܕܡܠܠܘ ܥܡܟܘܢ ܡܠܬܐ ܕܐܠܗܐ ܐܬܒܩܘ ܒܫܘܠܡܐ ܕܕܘܒܪܝܗܘܢ ܘܡܪܘ ܒܗܝܡܢܘܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 8, 'ܝܫܘܥ ܡܫܝܚܐ ܐܬܡܠܝ ܘܝܘܡܢܐ ܗܘܝܘ ܘܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 9, 'ܠܝܘܠܦܢܐ ܢܘܟܪܝܐ ܘܡܫܚܠܦܐ ܠܐ ܬܬܕܒܪܘܢ ܫܦܝܪ ܗܘ ܓܝܪ ܕܒܛܝܒܘܬܐ ܢܫܪܪ ܠܒܘܬܢ ܘܠܐ ܒܡܐܟܠܬܐ ܡܛܠ ܕܠܐ ܐܬܥܕܪܘ ܐܝܠܝܢ ܕܗܠܟܘ ܒܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 10, 'ܐܝܬ ܠܢ ܕܝܢ ܡܕܒܚܐ ܕܠܐ ܫܠܝܛ ܠܡܐܟܠ ܡܢܗ ܠܗܢܘܢ ܕܒܡܫܟܢܐ ܡܫܡܫܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 11, 'ܚܝܘܬܐ ܓܝܪ ܗܠܝܢ ܕܡܥܠ ܗܘܐ ܕܡܗܝܢ ܪܒ ܟܘܡܪܐ ܠܒܝܬ ܡܩܕܫܐ ܚܠܦ ܚܛܗܐ ܒܤܪܗܝܢ ܝܩܕ ܗܘܐ ܠܒܪ ܡܢ ܡܫܪܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 12, 'ܡܛܠ ܗܢܐ ܐܦ ܝܫܘܥ ܕܢܩܕܫ ܠܥܡܗ ܒܕܡܗ ܠܒܪ ܡܢ ܡܕܝܢܬܐ ܚܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 13, 'ܘܐܦ ܚܢܢ ܗܟܝܠ ܢܦܘܩ ܠܘܬܗ ܠܒܪ ܡܢ ܡܫܪܝܬܐ ܟܕ ܫܩܝܠܝܢܢ ܚܤܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 14, 'ܠܝܬ ܠܢ ܓܝܪ ܡܕܝܢܬܐ ܕܡܩܘܝܐ ܗܪܟܐ ܐܠܐ ܠܐܝܕܐ ܕܥܬܝܕܐ ܡܤܟܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 15, 'ܘܒܐܝܕܗ ܢܤܩ ܕܒܚܐ ܕܬܫܒܘܚܬܐ ܒܟܠܙܒܢ ܠܐܠܗܐ ܕܐܝܬܝܗ ܦܐܪܐ ܕܤܦܘܬܐ ܕܡܘܕܝܢ ܠܫܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 16, 'ܘܠܐ ܬܛܥܘܢ ܡܪܚܡܢܘܬܐ ܘܫܘܬܦܘܬܐ ܕܡܤܟܢܐ ܒܗܠܝܢ ܓܝܪ ܕܒܚܐ ܫܦܪ ܐܢܫ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 17, 'ܐܬܛܦܝܤܘ ܠܡܕܒܪܢܝܟܘܢ ܘܐܫܬܡܥܘ ܠܗܘܢ ܗܢܘܢ ܓܝܪ ܫܗܪܝܢ ܚܠܦ ܢܦܫܬܟܘܢ ܐܝܟ ܐܢܫܐ ܕܝܗܒܝܢ ܚܘܫܒܢܟܘܢ ܕܒܚܕܘܬܐ ܢܗܘܘܢ ܥܒܕܝܢ ܗܕܐ ܘܠܐ ܒܬܢܚܬܐ ܡܛܠ ܕܠܐ ܦܩܚܐ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 18, 'ܨܠܘ ܥܠܝܢ ܬܟܝܠܝܢܢ ܓܝܪ ܕܬܐܪܬܐ ܛܒܬܐ ܐܝܬ ܠܢ ܕܒܟܠܡܕܡ ܨܒܝܢܢ ܕܫܦܝܪ ܢܬܕܒܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 19, 'ܝܬܝܪܐܝܬ ܒܥܐ ܐܢܐ ܡܢܟܘܢ ܕܬܥܒܕܘܢ ܗܕܐ ܕܒܥܓܠ ܐܬܦܢܐ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 20, 'ܐܠܗܐ ܕܝܢ ܕܫܠܡܐ ܗܘ ܕܐܤܩ ܡܢ ܒܝܬ ܡܝܬܐ ܠܪܥܝܐ ܪܒܐ ܕܡܪܥܝܬܐ ܒܕܡܐ ܕܕܝܬܩܐ ܕܠܥܠܡ ܕܐܝܬܘܗܝ ܝܫܘܥ ܡܫܝܚܐ ܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 21, 'ܗܘ ܢܓܡܘܪܟܘܢ ܒܟܠ ܥܒܕ ܛܒ ܕܬܥܒܕܘܢ ܨܒܝܢܗ ܘܗܘ ܢܤܥܘܪ ܒܢ ܡܕܡ ܕܫܦܝܪ ܩܕܡܘܗܝ ܒܝܕ ܝܫܘܥ ܡܫܝܚܐ ܕܠܗ ܫܘܒܚܐ ܠܥܠܡ ܥܠܡܝܢ ܐܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 22, 'ܒܥܐ ܐܢܐ ܕܝܢ ܡܢܟܘܢ ܐܚܝ ܕܬܓܪܘܢ ܪܘܚܟܘܢ ܒܡܠܬܐ ܕܒܘܝܐܐ ܡܛܠ ܕܒܙܥܘܪܝܬܐ ܗܘ ܟܬܒܬ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 23, 'ܕܥܘ ܕܝܢ ܠܐܚܘܢ ܛܝܡܬܐܘܤ ܕܐܫܬܪܝ ܘܐܢ ܒܥܓܠ ܢܐܬܐ ܥܡܗ ܐܚܙܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 24, 'ܫܐܠܘ ܒܫܠܡܐ ܕܟܠܗܘܢ ܡܕܒܪܢܝܟܘܢ ܘܕܟܠܗܘܢ ܩܕܝܫܐ ܫܐܠܝܢ ܒܫܠܡܟܘܢ ܟܠܗܘܢ ܕܡܢ ܐܝܛܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 25, 'ܛܝܒܘܬܐ ܥܡ ܟܠܟܘܢ ܐܡܝܢ ܀ ܀ ܫܥܡܐܐ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 1, 'ܝܥܩܘܒ ܥܒܕܗ ܕܐܠܗܐ ܘܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܠܬܪܬܥܤܪܐ ܫܪܒܢ ܕܙܪܝܥܢ ܒܥܡܡܐ ܫܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 2, 'ܟܠ ܚܕܘܐ ܬܗܘܐ ܠܟܘܢ ܐܚܝ ܟܕ ܬܥܠܘܢ ܠܢܤܝܘܢܐ ܡܫܚܠܦܐ ܘܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 3, 'ܝܕܥܝܢ ܐܢܬܘܢ ܓܝܪ ܕܒܘܩܝܐ ܕܗܝܡܢܘܬܐ ܡܩܢܐ ܠܟܘܢ ܡܤܝܒܪܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 4, 'ܠܗ ܕܝܢ ܠܡܤܝܒܪܢܘܬܐ ܢܗܘܐ ܠܗ ܥܒܕܐ ܡܫܡܠܝܐ ܕܬܗܘܘܢ ܓܡܝܪܝܢ ܘܡܫܠܡܢܝܢ ܘܒܡܕܡ ܠܐ ܗܘܝܬܘܢ ܚܤܝܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 5, 'ܐܢ ܐܢܫ ܕܝܢ ܡܢܟܘܢ ܚܤܝܪ ܡܢ ܚܟܡܬܐ ܢܫܐܠ ܡܢ ܐܠܗܐ ܕܝܗܒ ܠܟܠ ܦܫܝܛܐܝܬ ܘܠܐ ܡܚܤܕ ܘܡܬܝܗܒܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 6, 'ܢܫܐܠ ܕܝܢ ܒܗܝܡܢܘܬܐ ܟܕ ܠܐ ܡܬܦܠܓ ܗܘ ܓܝܪ ܕܡܬܦܠܓ ܕܡܐ ܠܓܠܠܐ ܕܝܡܐ ܕܫܓܫܐ ܠܗܘܢ ܪܘܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 7, 'ܘܠܐ ܢܤܒܪ ܗܘ ܒܪܢܫܐ ܕܢܤܒ ܡܕܡ ܡܢ ܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 8, 'ܐܝܢܐ ܕܦܠܝܓ ܒܪܥܝܢܗ ܘܫܓܝܫ ܒܟܠܗܝܢ ܐܘܪܚܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 9, 'ܢܫܬܒܗܪ ܕܝܢ ܐܚܐ ܡܟܝܟܐ ܒܪܘܡܪܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 10, 'ܘܥܬܝܪܐ ܒܡܘܟܟܗ ܡܛܠ ܕܐܝܟ ܗܒܒܐ ܕܥܤܒܐ ܗܟܢܐ ܥܒܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 11, 'ܕܢܚ ܓܝܪ ܫܡܫܐ ܒܚܘܡܗ ܘܡܘܒܫ ܠܗ ܠܥܤܒܐ ܘܗܒܒܗ ܢܦܠ ܘܫܘܦܪܐ ܕܚܙܘܗ ܐܒܕ ܗܟܢܐ ܐܦ ܥܬܝܪܐ ܚܡܐ ܒܗܘܦܟܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 12, 'ܛܘܒܘܗܝ ܠܓܒܪܐ ܕܡܤܝܒܪ ܢܤܝܘܢܐ ܕܡܐ ܕܐܬܒܚܪ ܢܤܒ ܟܠܝܠܐ ܕܚܝܐ ܗܘ ܕܡܠܟ ܐܠܗܐ ܠܐܝܠܝܢ ܕܪܚܡܝܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 13, 'ܠܐ ܢܐܡܪ ܐܢܫ ܟܕ ܡܬܢܤܐ ܕܡܢ ܐܠܗܐ ܡܬܢܤܐ ܐܢܐ ܐܠܗܐ ܓܝܪ ܠܐ ܡܢܤܝ ܒܒܝܫܬܐ ܘܗܘ ܠܐܢܫ ܠܐ ܡܢܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 14, 'ܐܠܐ ܐܢܫ ܐܢܫ ܡܢ ܪܓܬܗ ܗܘ ܡܬܢܤܐ ܘܡܬܪܓܪܓ ܘܡܬܢܓܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 15, 'ܘܗܕܐ ܪܓܬܐ ܒܛܢܐ ܘܝܠܕܐ ܚܛܝܬܐ ܚܛܝܬܐ ܕܝܢ ܡܐ ܕܐܬܓܡܪܬ ܝܠܕܐ ܡܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 16, 'ܠܐ ܬܛܥܘܢ ܐܚܝ ܚܒܝܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 17, 'ܟܠ ܡܘܗܒܬܐ ܛܒܬܐ ܘܡܫܡܠܝܬܐ ܡܢ ܠܥܠ ܢܚܬܐ ܡܢ ܐܒܐ ܕܢܗܝܪܐ ܗܘ ܕܠܝܬ ܠܘܬܗ ܫܘܚܠܦܐ ܡܕܡ ܐܦܠܐ ܛܠܢܝܬܐ ܕܫܘܓܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 18, 'ܗܘܝܘ ܨܒܐ ܘܝܠܕܢ ܒܡܠܬܐ ܕܩܘܫܬܐ ܕܢܗܘܐ ܪܫܝܬܐ ܕܒܪܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 19, 'ܘܐܢܬܘܢ ܐܚܝ ܚܒܝܒܐ ܟܠܢܫ ܡܢܟܘܢ ܢܗܘܐ ܡܤܪܗܒ ܠܡܫܡܥ ܘܡܘܚܪ ܠܡܡܠܠܘ ܘܡܘܚܪ ܠܡܪܓܙ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 20, 'ܪܘܓܙܗ ܓܝܪ ܕܓܒܪܐ ܙܕܝܩܘܬܐ ܕܐܠܗܐ ܠܐ ܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 21, 'ܡܛܠ ܗܢܐ ܐܪܚܩܘ ܡܢܟܘܢ ܟܠܗ ܛܢܦܘܬܐ ܘܤܘܓܐܐ ܕܒܝܫܘܬܐ ܘܒܡܟܝܟܘܬܐ ܩܒܠܘ ܡܠܬܐ ܕܢܨܝܒܐ ܒܟܝܢܢ ܕܗܝ ܡܫܟܚܐ ܕܬܚܐ ܐܢܝܢ ܢܦܫܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 22, 'ܗܘܘ ܕܝܢ ܥܒܘܕܐ ܕܡܠܬܐ ܘܠܐ ܫܡܘܥܐ ܒܠܚܘܕ ܘܠܐ ܬܛܥܘܢ ܢܦܫܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 23, 'ܐܢ ܐܢܫ ܓܝܪ ܢܗܘܐ ܫܡܘܥܗ ܕܡܠܬܐ ܘܠܐ ܥܒܘܕܗ ܗܢܐ ܕܡܐ ܠܗܘ ܕܚܙܐ ܐܦܘܗܝ ܒܡܚܙܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 24, 'ܚܙܐ ܓܝܪ ܢܦܫܗ ܘܥܒܪ ܘܛܥܐ ܕܐܝܟܢܐ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 25, 'ܟܠ ܕܝܢ ܕܚܪ ܒܢܡܘܤܐ ܡܫܠܡܢܐ ܕܚܐܪܘܬܐ ܘܩܘܝ ܒܗ ܠܐ ܗܘܐ ܫܡܘܥܐ ܕܫܡܥܐ ܕܡܬܛܥܐ ܐܠܐ ܥܒܘܕܐ ܕܥܒܕܐ ܘܗܢܐ ܛܘܒܢܐ ܢܗܘܐ ܒܥܒܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 26, 'ܘܐܢ ܐܢܫ ܤܒܪ ܕܡܫܡܫ ܠܐܠܗܐ ܘܠܐ ܐܚܕ ܠܫܢܗ ܐܠܐ ܡܛܥܐ ܠܗ ܠܒܗ ܕܗܢܐ ܤܪܝܩܐ ܗܝ ܬܫܡܫܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 27, 'ܬܫܡܫܬܐ ܓܝܪ ܕܟܝܬܐ ܘܩܕܝܫܬܐ ܩܕܡ ܐܠܗܐ ܐܒܐ ܗܕܐ ܗܝ ܠܡܤܥܪ ܝܬܡܐ ܘܐܪܡܠܬܐ ܒܐܘܠܨܢܝܗܘܢ ܘܠܡܛܪ ܐܢܫ ܢܦܫܗ ܡܢ ܥܠܡܐ ܕܠܐ ܛܘܠܫܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 1, 'ܐܚܝ ܠܐ ܒܡܤܒ ܒܐܦܐ ܬܗܘܘܢ ܐܚܝܕܝܢ ܠܗܝܡܢܘܬܐ ܕܬܫܒܘܚܬܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 2, 'ܐܢ ܓܝܪ ܢܥܘܠ ܠܟܢܘܫܬܟܘܢ ܐܢܫ ܕܥܙܩܬܗ ܕܕܗܒܐ ܐܘ ܕܡܐܢܘܗܝ ܫܦܝܪܐ ܘܢܥܘܠ ܡܤܟܢܐ ܒܡܐܢܐ ܨܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 3, 'ܘܬܚܘܪܘܢ ܒܗܘ ܕܠܒܝܫ ܡܐܢܐ ܫܦܝܪܐ ܘܬܐܡܪܘܢ ܠܗ ܐܢܬ ܗܪܟܐ ܬܒ ܫܦܝܪ ܘܠܡܤܟܢܐ ܬܐܡܪܘܢ ܠܗ ܐܢܬ ܩܘܡ ܠܗܠ ܐܘ ܬܒ ܠܟ ܗܪܟܐ ܩܕܡ ܟܘܒܫܐ ܕܪܓܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 4, 'ܠܐ ܗܐ ܐܬܦܠܓܬܘܢ ܠܟܘܢ ܒܢܦܫܟܘܢ ܘܗܘܝܬܘܢ ܡܦܪܫܢܐ ܕܡܚܫܒܬܐ ܒܝܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 5, 'ܫܡܥܘ ܐܚܝ ܚܒܝܒܐ ܠܐ ܗܘܐ ܠܡܤܟܢܐ ܕܥܠܡܐ ܥܬܝܪܐ ܕܝܢ ܒܗܝܡܢܘܬܐ ܓܒܐ ܐܠܗܐ ܕܢܗܘܘܢ ܝܪܬܐ ܒܡܠܟܘܬܐ ܗܝ ܕܡܠܟ ܐܠܗܐ ܠܐܝܠܝܢ ܕܪܚܡܝܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 6, 'ܐܢܬܘܢ ܕܝܢ ܫܛܬܘܢܝܗܝ ܠܡܤܟܢܐ ܠܐ ܗܐ ܥܬܝܪܐ ܡܫܬܥܠܝܢ ܥܠܝܟܘܢ ܘܗܢܘܢ ܢܓܕܝܢ ܠܟܘܢ ܠܒܝܬ ܕܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 7, 'ܠܐ ܗܐ ܗܢܘܢ ܡܓܕܦܝܢ ܥܠ ܫܡܐ ܛܒܐ ܕܐܬܩܪܝ ܥܠܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 8, 'ܘܐܢ ܢܡܘܤܐ ܕܐܠܗܐ ܒܗܕܐ ܡܫܠܡܝܬܘܢ ܐܝܟ ܕܟܬܝܒ ܕܬܪܚܡ ܠܩܪܝܒܟ ܐܝܟ ܢܦܫܟ ܫܦܝܪ ܥܒܕܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 9, 'ܐܢ ܕܝܢ ܒܐܦܐ ܢܤܒܝܢ ܐܢܬܘܢ ܚܛܝܬܐ ܥܒܕܝܢ ܐܢܬܘܢ ܘܡܬܟܘܢܝܢ ܐܢܬܘܢ ܡܢ ܢܡܘܤܐ ܐܝܟ ܥܒܪܝ ܥܠ ܢܡܘܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 10, 'ܐܝܢܐ ܓܝܪ ܕܟܠܗ ܢܡܘܤܐ ܢܛܪ ܘܒܚܕܐ ܫܪܥ ܠܟܠܗ ܢܡܘܤܐ ܐܬܚܝܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 11, 'ܗܘ ܓܝܪ ܕܐܡܪ ܕܠܐ ܬܓܘܪ ܗܘ ܐܡܪ ܕܠܐ ܬܩܛܘܠ ܐܢ ܕܝܢ ܠܐ ܓܐܪ ܐܢܬ ܐܠܐ ܩܛܠ ܐܢܬ ܗܘܝܬ ܠܟ ܥܒܪ ܥܠ ܢܡܘܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 12, 'ܗܟܢܐ ܗܘܝܬܘܢ ܡܡܠܠܝܢ ܘܗܟܢܐ ܗܘܝܬܘܢ ܤܥܪܝܢ ܐܝܟ ܐܢܫܐ ܕܒܢܡܘܤܐ ܕܚܐܪܘܬܐ ܥܬܝܕܝܢ ܐܢܬܘܢ ܠܡܬܕܢܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 13, 'ܕܝܢܐ ܓܝܪ ܗܘܐ ܕܠܐ ܪܚܡܐ ܥܠ ܗܘ ܕܠܐ ܥܒܕ ܪܚܡܐ ܡܫܬܥܠܝܢ ܐܢܬܘܢ ܒܪܚܡܐ ܥܠ ܕܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 14, 'ܡܢܐ ܗܢܝܢܐ ܐܚܝ ܐܢ ܐܢܫ ܐܡܪ ܕܐܝܬ ܠܝ ܗܝܡܢܘܬܐ ܘܥܒܕܐ ܠܝܬ ܠܗ ܕܠܡܐ ܡܫܟܚܐ ܗܝܡܢܘܬܗ ܕܬܚܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 15, 'ܘܐܢ ܐܚܐ ܐܘ ܚܬܐ ܢܗܘܘܢ ܥܪܛܠܝܝܢ ܘܚܤܝܪܝܢ ܤܝܒܪܬܐ ܕܝܘܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 16, 'ܘܢܐܡܪ ܠܗܘܢ ܐܢܫ ܡܢܟܘܢ ܙܠܘ ܒܫܠܡܐ ܫܚܢܘ ܘܤܒܥܘ ܘܠܐ ܬܬܠܘܢ ܠܗܘܢ ܤܢܝܩܘܬܗ ܕܦܓܪܐ ܡܢܐ ܗܢܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 17, 'ܗܟܢܐ ܐܦ ܗܝܡܢܘܬܐ ܕܠܐ ܥܒܕܐ ܡܝܬܐ ܗܝ ܒܠܚܘܕܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 18, 'ܐܡܪ ܓܝܪ ܐܢܫ ܠܟ ܐܝܬ ܠܟ ܗܝܡܢܘܬܐ ܘܠܝ ܐܝܬ ܠܝ ܥܒܕܐ ܚܘܢܝ ܗܝܡܢܘܬܟ ܕܠܐ ܥܒܕܐ ܘܐܢܐ ܡܚܘܐ ܐܢܐ ܠܟ ܗܝܡܢܘܬܝ ܡܢ ܥܒܕܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 19, 'ܡܗܝܡܢ ܐܢܬ ܕܚܕ ܗܘ ܐܠܗܐ ܫܦܝܪ ܥܒܕ ܐܢܬ ܐܦ ܫܐܕܐ ܡܗܝܡܢܝܢ ܘܪܥܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 20, 'ܨܒܐ ܐܢܬ ܕܝܢ ܕܬܕܥ ܐܘ ܒܪܢܫܐ ܚܠܫܐ ܕܗܝܡܢܘܬܐ ܕܠܐ ܥܒܕܐ ܡܝܬܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 21, 'ܐܒܘܢ ܐܒܪܗܡ ܠܐ ܗܘܐ ܡܢ ܥܒܕܐ ܐܙܕܕܩ ܕܐܤܩ ܠܐܝܤܚܩ ܒܪܗ ܥܠ ܡܕܒܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 22, 'ܚܙܐ ܐܢܬ ܕܗܝܡܢܘܬܗ ܤܝܥܬ ܠܥܒܕܘܗܝ ܘܡܢ ܥܒܕܐ ܗܝܡܢܘܬܗ ܐܬܓܡܪܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 23, 'ܘܫܠܡ ܟܬܒܐ ܕܐܡܪ ܕܗܝܡܢ ܐܒܪܗܡ ܒܐܠܗܐ ܘܐܬܚܫܒܬ ܠܗ ܠܙܕܝܩܘ ܘܪܚܡܐ ܕܐܠܗܐ ܐܬܩܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 24, 'ܚܙܐ ܐܢܬ ܕܡܢ ܥܒܕܐ ܡܙܕܕܩ ܒܪܢܫܐ ܘܠܐ ܗܘܐ ܡܢ ܗܝܡܢܘܬܐ ܒܠܚܘܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 25, 'ܗܟܢܐ ܐܦ ܪܚܒ ܙܢܝܬܐ ܠܐ ܗܘܐ ܡܢ ܥܒܕܐ ܐܙܕܕܩܬ ܕܩܒܠܬ ܠܓܫܘܫܐ ܘܒܐܘܪܚܐ ܐܚܪܬܐ ܐܦܩܬ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 26, 'ܐܝܟܢܐ ܕܦܓܪܐ ܕܠܐ ܪܘܚܐ ܡܝܬ ܗܘ ܗܟܢܐ ܐܦ ܗܝܡܢܘܬܐ ܕܠܐ ܥܒܕܐ ܡܝܬܐ ܗܝ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 1, 'ܠܐ ܤܓܝܐܐ ܡܠܦܢܐ ܢܗܘܘܢ ܒܟܘܢ ܐܚܝ ܐܠܐ ܗܘܝܬܘܢ ܝܕܥܝܢ ܕܕܝܢܐ ܝܬܝܪܐ ܚܝܒܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 2, 'ܤܓܝܐܬܐ ܓܝܪ ܡܫܬܪܥܝܢܢ ܟܠܢ ܟܠ ܕܒܡܠܬܐ ܠܐ ܫܪܥ ܗܢܐ ܐܝܬܘܗܝ ܓܒܪܐ ܓܡܝܪܐ ܕܡܫܟܚ ܢܫܥܒܕ ܐܦ ܟܠܗ ܦܓܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 3, 'ܗܐ ܓܝܪ ܦܓܘܕܐ ܒܦܘܡܐ ܕܪܟܫܐ ܪܡܝܢܢ ܐܝܟ ܕܢܫܬܥܒܕܘܢ ܠܢ ܘܟܠܗ ܓܘܫܡܗܘܢ ܡܗܦܟܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 4, 'ܐܦ ܐܠܦܐ ܥܫܝܢܬܐ ܟܕ ܕܒܝܪܢ ܠܗܝܢ ܪܘܚܐ ܩܫܝܬܐ ܡܢ ܩܝܤܐ ܙܥܘܪܐ ܡܬܢܬܦܢ ܠܐܬܪ ܕܚܐܪ ܨܒܝܢܗ ܕܗܘ ܕܡܕܒܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 5, 'ܗܟܢܐ ܐܦ ܠܫܢܐ ܗܕܡܐ ܗܘ ܙܥܘܪܐ ܘܡܫܬܥܠܐ ܐܦ ܢܘܪܐ ܙܥܘܪܬܐ ܥܒܐ ܤܓܝܐܐ ܡܘܩܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 6, 'ܘܠܫܢܐ ܢܘܪܐ ܗܘ ܘܥܠܡܐ ܕܚܛܝܬܐ ܐܝܟ ܥܒܐ ܗܘ ܘܗܘ ܠܫܢܐ ܟܕ ܐܝܬܘܗܝ ܒܗܕܡܝܢ ܡܟܬܡ ܠܗ ܠܟܠܗ ܦܓܪܢ ܘܡܘܩܕ ܝܘܒܠܐ ܕܫܪܒܬܢ ܕܪܗܛܝܢ ܐܝܟ ܓܝܓܠܐ ܘܝܩܕ ܐܦ ܗܘ ܒܢܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 7, 'ܟܠܗܘܢ ܓܝܪ ܟܝܢܐ ܕܚܝܘܬܐ ܘܕܦܪܚܬܐ ܘܪܚܫܐ ܕܝܡܐ ܘܕܝܒܫܐ ܡܫܬܥܒܕܝܢ ܠܟܝܢܐ ܕܐܢܫܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 8, 'ܠܫܢܐ ܕܝܢ ܐܢܫ ܠܐ ܐܫܟܚ ܕܢܟܒܫܝܘܗܝ ܒܝܫܬܐ ܗܕܐ ܕܠܐ ܡܬܬܟܤܐ ܡܠܐ ܗܘ ܤܡܐ ܕܡܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 9, 'ܒܗ ܡܒܪܟܝܢܢ ܠܡܪܝܐ ܘܐܒܐ ܘܒܗ ܠܝܛܝܢܢ ܠܒܢܝܢܫܐ ܕܒܕܡܘܬܐ ܕܐܠܗܐ ܥܒܝܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 10, 'ܘܡܢܗ ܡܢ ܦܘܡܐ ܢܦܩܢ ܒܘܪܟܬܐ ܘܠܘܛܬܐ ܠܐ ܘܠܐ ܐܚܝ ܕܗܠܝܢ ܗܟܢܐ ܢܤܬܥܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 11, 'ܕܠܡܐ ܡܫܟܚܐ ܕܡܢ ܚܕ ܡܒܘܥܐ ܢܦܩܘܢ ܡܝܐ ܚܠܝܐ ܘܡܪܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 12, 'ܐܘ ܕܠܡܐ ܡܫܟܚܐ ܬܬܐ ܐܚܝ ܕܙܝܬܐ ܬܥܒܕ ܐܘ ܓܦܬܐ ܬܐܢܐ ܗܟܢܐ ܐܦ ܠܐ ܡܝܐ ܡܠܝܚܐ ܡܫܟܚܝܢ ܕܢܬܥܒܕܘܢ ܚܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 13, 'ܡܢܘ ܡܢܟܘܢ ܕܚܟܝܡ ܘܪܕܐ ܢܚܘܐ ܥܒܕܘܗܝ ܒܗܘܦܟܐ ܫܦܝܪܐ ܒܚܟܡܬܐ ܡܟܝܟܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 14, 'ܐܢ ܕܝܢ ܚܤܡܐ ܡܪܝܪܐ ܐܝܬ ܒܟܘܢ ܐܘ ܚܪܝܢܐ ܒܠܒܝܟܘܢ ܠܐ ܬܬܚܬܪܘܢ ܥܠ ܩܘܫܬܐ ܘܬܕܓܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 15, 'ܡܛܠ ܕܗܕܐ ܚܟܡܬܐ ܡܢ ܠܥܠ ܠܐ ܢܚܬܬ ܐܠܐ ܐܝܬܝܗ ܐܪܥܢܝܬܐ ܡܢ ܚܘܫܒܐ ܕܢܦܫܐ ܘܡܢ ܫܐܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 16, 'ܐܝܟܐ ܓܝܪ ܕܐܝܬ ܚܤܡܐ ܘܚܪܝܢܐ ܬܡܢ ܐܦ ܕܠܘܚܝܐ ܘܟܠܡܕܡ ܕܒܝܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 17, 'ܚܟܡܬܐ ܕܝܢ ܕܡܢ ܠܥܠ ܕܟܝܐ ܗܝ ܘܡܠܝܐ ܫܠܡܐ ܘܡܟܝܟܐ ܘܡܫܬܡܥܢܝܐ ܘܡܠܝܐ ܪܚܡܐ ܘܦܐܪܐ ܛܒܐ ܘܕܠܐ ܦܠܓܘܬܐ ܗܝ ܘܒܐܦܐ ܠܐ ܢܤܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 18, 'ܦܐܪܐ ܕܝܢ ܕܙܕܝܩܘܬܐ ܒܫܝܢܐ ܡܙܕܪܥܝܢ ܠܐܝܠܝܢ ܕܥܒܕܝܢ ܫܠܡܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 1, 'ܡܢ ܐܝܟܐ ܐܝܬ ܒܟܘܢ ܩܪܒܐ ܘܡܨܘܬܐ ܠܐ ܗܘܐ ܡܢ ܪܓܝܓܬܐ ܕܡܩܪܒܢ ܒܗܕܡܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 2, 'ܡܬܪܓܪܓܝܢ ܐܢܬܘܢ ܘܠܝܬ ܠܟܘܢ ܘܩܛܠܝܢ ܐܢܬܘܢ ܘܛܢܝܢ ܐܢܬܘܢ ܘܠܐ ܐܬܝܐ ܒܐܝܕܝܟܘܢ ܘܢܨܝܢ ܐܢܬܘܢ ܘܩܪܒܐ ܥܒܕܝܢ ܐܢܬܘܢ ܘܠܝܬ ܠܟܘܢ ܡܛܠ ܕܠܐ ܫܐܠܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 3, 'ܫܐܠܝܢ ܐܢܬܘܢ ܘܠܐ ܢܤܒܝܢ ܐܢܬܘܢ ܡܛܠ ܕܒܝܫܐܝܬ ܫܐܠܝܢ ܐܢܬܘܢ ܐܝܟ ܕܬܬܪܤܘܢ ܪܓܝܓܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 4, 'ܓܝܪܐ ܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܕܪܚܡܬܗ ܕܥܠܡܐ ܗܢܐ ܒܥܠܕܒܒܘܬܐ ܗܝ ܕܐܠܗܐ ܐܝܢܐ ܗܟܝܠ ܕܨܒܐ ܕܢܗܘܐ ܪܚܡܐ ܕܥܠܡܐ ܗܢܐ ܒܥܠܕܒܒܐ ܗܘܐ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 5, 'ܐܘ ܕܠܡܐ ܤܪܝܩܐܝܬ ܤܒܪܝܢ ܐܢܬܘܢ ܕܐܡܪ ܟܬܒܐ ܕܒܛܢܢܐ ܪܓܐ ܪܘܚܐ ܕܥܡܪܐ ܒܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 6, 'ܛܝܒܘܬܐ ܕܝܢ ܝܬܝܪܬܐ ܝܗܒ ܠܢ ܡܪܢ ܡܛܠ ܗܕܐ ܐܡܪ ܕܐܠܗܐ ܡܡܟܟ ܠܪܡܐ ܘܠܡܟܝܟܐ ܝܗܒ ܛܝܒܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 7, 'ܐܫܬܥܒܕܘ ܗܟܝܠ ܠܐܠܗܐ ܘܩܘܡܘ ܠܘܩܒܠ ܤܛܢܐ ܘܥܪܩ ܡܢܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 8, 'ܘܩܪܘܒܘ ܠܘܬ ܐܠܗܐ ܘܢܬܩܪܒ ܠܟܘܢ ܕܟܘ ܐܝܕܝܟܘܢ ܚܛܝܐ ܩܕܫܘ ܠܒܘܬܟܘܢ ܦܠܝܓܝ ܢܦܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 9, 'ܐܬܡܟܟܘ ܘܐܬܐܒܠܘ ܘܓܘܚܟܟܘܢ ܠܐܒܠܐ ܢܬܗܦܟ ܘܚܕܘܬܟܘܢ ܠܥܩܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 10, 'ܐܬܡܟܟܘ ܩܕܡ ܡܪܝܐ ܘܢܪܡܪܡܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 11, 'ܠܐ ܬܗܘܘܢ ܡܡܠܠܝܢ ܥܠ ܚܕܕܐ ܐܚܝ ܗܘ ܓܝܪ ܕܡܡܠܠ ܥܠ ܐܚܘܗܝ ܐܘ ܕܐܢ ܠܐܚܘܗܝ ܡܡܠܠ ܥܠ ܢܡܘܤܐ ܘܕܐܢ ܠܢܡܘܤܐ ܘܐܢ ܠܢܡܘܤܐ ܕܐܢ ܐܢܬ ܠܐ ܗܘܝܬ ܥܒܘܕܗ ܕܢܡܘܤܐ ܐܠܐ ܕܝܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 12, 'ܚܕ ܗܘ ܤܐܡ ܢܡܘܤܐ ܘܕܝܢܐ ܕܗܘ ܡܫܟܚ ܕܢܚܐ ܘܢܘܒܕ ܐܢܬ ܕܝܢ ܡܢ ܐܢܬ ܕܕܐܢ ܐܢܬ ܠܗ ܠܩܪܝܒܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 13, 'ܡܢܐ ܕܝܢ ܢܐܡܪ ܐܦ ܥܠ ܐܝܠܝܢ ܕܐܡܪܝܢ ܕܝܘܡܢܐ ܐܘ ܡܚܪ ܐܙܠܝܢܢ ܠܡܕܝܢܬܐ ܐܝܕܐ ܕܗܝ ܘܥܒܕܝܢܢ ܬܡܢ ܫܢܬܐ ܚܕܐ ܘܡܬܬܓܪܝܢܢ ܘܝܬܪܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 14, 'ܘܠܐ ܝܕܥܝܢ ܡܢܐ ܗܘܐ ܡܚܪ ܡܢܐ ܐܢܘܢ ܓܝܪ ܚܝܝܢ ܐܠܐ ܐܢ ܠܗܓܐ ܕܩܠܝܠ ܡܬܚܙܐ ܘܛܠܩ ܘܡܘܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 15, 'ܚܠܦ ܕܢܐܡܪܘܢ ܕܐܢ ܡܪܝܐ ܢܨܒܐ ܘܢܚܐ ܥܒܕܝܢܢ ܗܕܐ ܐܘ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 16, 'ܡܫܬܒܗܪܝܢ ܒܚܬܝܪܘܬܗܘܢ ܟܠ ܫܘܒܗܪܐ ܕܐܝܟ ܗܢܐ ܒܝܫܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 17, 'ܘܐܝܢܐ ܕܝܕܥ ܛܒܬܐ ܘܠܐ ܥܒܕ ܠܗ ܚܛܗܐ ܗܘܐ ܠܗ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 1, 'ܐܘ ܥܬܝܪܐ ܐܝܠܠܘ ܘܒܟܘ ܥܠ ܕܘܘܢܐ ܕܐܬܝܢ ܥܠܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 2, 'ܥܘܬܪܟܘܢ ܓܝܪ ܐܬܚܒܠ ܘܤܪܝ ܘܡܐܢܝܟܘܢ ܐܬܐܟܠܘ ܡܢ ܤܤܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 3, 'ܘܕܗܒܟܘܢ ܘܤܐܡܟܘܢ ܐܫܚܬ ܠܗ ܘܫܘܚܬܗܘܢ ܗܘܝܐ ܠܤܗܕܘܬܐ ܥܠܝܟܘܢ ܘܗܝ ܥܬܝܕܐ ܕܬܐܟܘܠ ܒܤܪܟܘܢ ܢܘܪܐ ܟܢܫܬܘܢ ܠܟܘܢ ܠܝܘܡܬܐ ܐܚܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 4, 'ܗܐ ܐܓܪܐ ܕܦܥܠܐ ܕܚܨܕܘ ܐܪܥܬܟܘܢ ܗܘ ܕܛܠܡܬܘܢ ܩܥܐ ܘܓܥܬܐ ܕܚܨܘܕܐ ܠܐܕܢܘܗܝ ܕܡܪܝܐ ܨܒܐܘܬ ܥܠܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 5, 'ܒܤܡܬܘܢ ܓܝܪ ܥܠ ܐܪܥܐ ܘܐܬܠܥܒܬܘܢ ܘܬܪܤܝܬܘܢ ܦܓܪܝܟܘܢ ܐܝܟ ܕܠܝܘܡܐ ܕܢܟܤܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 6, 'ܚܝܒܬܘܢ ܘܩܛܠܬܘܢ ܠܙܕܝܩܐ ܘܠܐ ܩܡ ܠܘܩܒܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 7, 'ܐܢܬܘܢ ܕܝܢ ܐܚܝ ܐܓܪܘ ܪܘܚܟܘܢ ܥܕܡܐ ܠܡܐܬܝܬܗ ܕܡܪܝܐ ܐܝܟ ܐܟܪܐ ܕܡܤܟܐ ܠܦܐܪܐ ܝܩܝܪܐ ܕܐܪܥܗ ܘܡܓܪ ܪܘܚܗ ܥܠܝܗܘܢ ܥܕܡܐ ܕܢܤܒ ܡܛܪܐ ܒܟܝܪܝܐ ܘܠܩܝܫܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 8, 'ܗܟܢܐ ܐܦ ܐܢܬܘܢ ܐܓܪܘ ܪܘܚܟܘܢ ܘܠܒܘܬܟܘܢ ܫܪܪܘ ܩܪܒܬ ܠܗ ܓܝܪ ܡܐܬܝܬܗ ܕܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 9, 'ܠܐ ܬܬܢܚܘܢ ܚܕ ܥܠ ܚܕ ܐܚܝ ܕܠܐ ܬܬܕܝܢܘܢ ܗܐ ܓܝܪ ܕܝܢܐ ܩܕܡ ܬܪܥܐ ܩܐܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 10, 'ܕܡܘܬܐ ܠܢܒܝܐ ܤܒܘ ܠܟܘܢ ܐܚܝ ܠܡܓܪܬ ܪܘܚܐ ܕܐܘܠܨܢܝܟܘܢ ܗܢܘܢ ܕܡܠܠܘ ܒܫܡܗ ܕܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 11, 'ܗܐ ܓܝܪ ܝܗܒܝܢܢ ܛܘܒܐ ܠܐܝܠܝܢ ܕܤܝܒܪܘ ܫܡܥܬܘܢ ܡܤܝܒܪܢܘܬܗ ܕܐܝܘܒ ܘܚܪܬܐ ܕܥܒܕ ܠܗ ܡܪܝܐ ܚܙܝܬܘܢ ܡܛܠ ܕܡܪܚܡܢ ܗܘ ܡܪܝܐ ܘܡܪܚܦܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 12, 'ܩܕܡ ܟܠ ܡܕܡ ܕܝܢ ܐܚܝ ܠܐ ܗܘܝܬܘܢ ܝܡܝܢ ܠܐ ܒܫܡܝܐ ܘܠܐ ܒܐܪܥܐ ܐܦܠܐ ܒܡܘܡܬܐ ܐܚܪܬܐ ܐܠܐ ܬܗܘܐ ܡܠܬܟܘܢ ܐܝܢ ܐܝܢ ܘܠܐ ܠܐ ܕܠܐ ܬܬܚܝܒܘܢ ܬܚܝܬ ܕܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 13, 'ܘܐܢ ܐܢܫ ܡܢܟܘܢ ܢܗܘܐ ܒܐܘܠܨܢܐ ܢܗܘܐ ܡܨܠܐ ܘܐܢ ܚܕܐ ܢܗܘܐ ܡܙܡܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 14, 'ܘܐܢ ܟܪܝܗ ܢܩܪܐ ܠܩܫܝܫܐ ܕܥܕܬܐ ܘܢܨܠܘܢ ܥܠܘܗܝ ܘܢܡܫܚܘܢܗ ܡܫܚܐ ܒܫܡܗ ܕܡܪܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 15, 'ܘܨܠܘܬܐ ܕܗܝܡܢܘܬܐ ܡܚܠܡܐ ܠܗ ܠܗܘ ܕܟܪܝܗ ܘܡܩܝܡ ܠܗ ܡܪܢ ܘܐܢ ܚܛܗܐ ܥܒܝܕܝܢ ܠܗ ܡܫܬܒܩܝܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 16, 'ܗܘܝܬܘܢ ܕܝܢ ܡܘܕܝܢ ܤܟܠܘܬܟܘܢ ܚܕ ܠܚܕ ܘܗܘܝܬܘܢ ܡܨܠܝܢ ܚܕ ܥܠ ܚܕ ܕܬܬܐܤܘܢ ܪܒ ܗܘ ܓܝܪ ܚܝܠܗ ܕܨܠܘܬܐ ܐܝܕܐ ܕܙܕܝܩܐ ܡܨܠܐ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 17, 'ܐܦ ܐܠܝܐ ܒܪܢܫܐ ܗܘܐ ܚܫܘܫܐ ܐܟܘܬܢ ܘܨܠܝ ܕܠܐ ܢܚܘܬ ܡܛܪܐ ܥܠ ܐܪܥܐ ܘܠܐ ܢܚܬ ܬܠܬ ܫܢܝܢ ܘܫܬܐ ܝܪܚܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 18, 'ܘܬܘܒ ܨܠܝ ܘܫܡܝܐ ܝܗܒܘ ܡܛܪܐ ܘܐܪܥܐ ܝܗܒܬ ܦܐܪܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 19, 'ܐܚܝ ܐܢ ܐܢܫ ܡܢܟܘܢ ܢܛܥܐ ܡܢ ܐܘܪܚܐ ܕܩܘܫܬܐ ܘܢܦܢܝܘܗܝ ܐܢܫ ܡܢ ܛܥܝܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 20, 'ܢܕܥ ܕܗܘ ܕܡܗܦܟ ܠܚܛܝܐ ܡܢ ܛܥܝܘܬܐ ܕܐܘܪܚܗ ܡܚܐ ܢܦܫܗ ܡܢ ܡܘܬܐ ܘܥܛܐ ܤܘܓܐܐ ܕܚܛܗܘܗܝ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 1, 'ܦܛܪܘܤ ܫܠܝܚܐ ܕܝܫܘܥ ܡܫܝܚܐ ܠܓܒܝܐ ܘܬܘܬܒܐ ܕܙܪܝܥܝܢ ܒܦܢܛܘܤ ܘܒܓܠܛܝܐ ܘܒܩܦܘܕܩܝܐ ܘܒܐܤܝܐ ܘܒܒܝܬܘܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 2, 'ܐܝܠܝܢ ܕܐܬܓܒܝܘ ܒܡܩܕܡܘܬ ܝܕܥܬܗ ܕܐܠܗܐ ܐܒܐ ܒܩܕܝܫܘܬܐ ܕܪܘܚܐ ܕܢܗܘܘܢ ܠܡܫܡܥܬܐ ܘܠܪܤܤ ܕܡܗ ܕܝܫܘܥ ܡܫܝܚܐ ܛܝܒܘܬܐ ܘܫܠܡܐ ܢܤܓܐ ܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 3, 'ܡܒܪܟ ܗܘ ܐܠܗܐ ܐܒܘܗܝ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܗܘ ܕܒܚܢܢܗ ܤܓܝܐܐ ܐܘܠܕܢ ܡܢ ܕܪܝܫ ܒܩܝܡܬܗ ܕܝܫܘܥ ܡܫܝܚܐ ܠܤܒܪܐ ܕܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 4, 'ܘܠܝܪܬܘܬܐ ܕܠܐ ܡܬܚܒܠܐ ܘܠܐ ܡܬܛܢܦܐ ܘܠܐ ܚܡܝܐ ܗܝ ܕܡܛܝܒܐ ܠܟܘܢ ܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 5, 'ܟܕ ܢܛܝܪܝܢ ܐܢܬܘܢ ܒܚܝܠܐ ܕܐܠܗܐ ܘܒܗܝܡܢܘܬܐ ܠܚܝܐ ܕܡܛܝܒܝܢ ܕܢܬܓܠܘܢ ܠܙܒܢܐ ܐܚܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 6, 'ܕܒܗܘܢ ܬܚܕܘܢ ܠܥܠܡ ܐܦܢ ܒܗܢܐ ܙܒܢܐ ܩܠܝܠ ܡܬܬܥܝܩܝܢ ܐܢܬܘܢ ܒܢܤܝܘܢܐ ܡܫܚܠܦܐ ܕܥܕܝܢ ܥܠܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 7, 'ܐܝܟܢܐ ܕܒܘܚܪܢܐ ܕܗܝܡܢܘܬܟܘܢ ܢܬܚܙܐ ܕܡܝܬܪ ܡܢ ܕܗܒܐ ܤܢܝܢܐ ܕܐܬܒܩܝ ܒܢܘܪܐ ܠܬܫܒܘܚܬܐ ܘܠܐܝܩܪܐ ܘܠܩܘܠܤܐ ܒܓܠܝܢܗ ܕܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 8, 'ܗܘ ܕܠܐ ܚܙܝܬܘܢܝܗܝ ܘܡܚܒܝܢ ܐܢܬܘܢ ܠܗ ܘܒܗܝܡܢܘܬܗ ܪܘܙܝܢ ܐܢܬܘܢ ܒܚܕܘܬܐ ܡܫܒܚܬܐ ܕܠܐ ܡܬܡܠܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 9, 'ܕܬܩܒܠܘܢ ܦܘܪܥܢܐ ܕܗܝܡܢܘܬܟܘܢ ܚܝܐ ܕܢܦܫܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 10, 'ܗܢܘܢ ܚܝܐ ܕܥܩܒܘ ܗܘܘ ܥܠܝܗܘܢ ܢܒܝܐ ܟܕ ܐܬܢܒܝܘ ܥܠ ܛܝܒܘܬܐ ܕܥܬܝܕܐ ܗܘܬ ܕܬܬܝܗܒ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 11, 'ܘܒܨܘ ܕܒܐܝܢܐ ܙܒܢܐ ܡܚܘܝܐ ܘܡܤܗܕܐ ܪܘܚܗ ܕܡܫܝܚܐ ܕܥܡܪܐ ܒܗܘܢ ܕܥܬܝܕܝܢ ܚܫܘܗܝ ܕܡܫܝܚܐ ܘܬܫܒܘܚܬܗ ܕܡܢ ܒܬܪܟܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 12, 'ܘܐܬܓܠܝ ܠܗܘܢ ܟܠ ܕܒܨܝܢ ܗܘܘ ܡܛܠ ܕܠܘ ܠܢܦܫܗܘܢ ܒܥܝܢ ܗܘܘ ܐܠܐ ܠܢ ܕܝܠܢ ܡܬܢܒܝܢ ܗܘܘ ܐܝܠܝܢ ܕܗܫܐ ܐܬܓܠܝ ܠܟܘܢ ܒܝܕ ܐܝܠܝܢ ܕܤܒܪܢܟܘܢ ܒܪܘܚܐ ܕܩܘܕܫܐ ܕܐܫܬܕܪ ܡܢ ܫܡܝܐ ܕܒܗܝܢ ܒܗܠܝܢ ܡܬܪܓܪܓܝܢ ܐܦ ܡܠܐܟܐ ܕܢܕܝܩܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 13, 'ܡܛܠ ܗܢܐ ܚܙܘܩܘ ܚܨܐ ܕܬܪܥܝܬܟܘܢ ܘܐܬܬܥܝܪܘ ܓܡܝܪܐܝܬ ܘܤܒܪܘ ܥܠ ܚܕܘܬܐ ܕܐܬܝܐ ܠܟܘܢ ܒܓܠܝܢܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 14, 'ܐܝܟ ܒܢܝܐ ܡܫܬܡܥܢܐ ܘܠܐ ܬܫܬܘܬܦܘܢ ܬܘܒ ܠܪܓܝܓܬܟܘܢ ܩܕܡܝܬܐ ܐܝܠܝܢ ܕܪܓܝܢ ܗܘܝܬܘܢ ܕܠܐ ܒܝܕܥܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 15, 'ܐܠܐ ܗܘܘ ܩܕܝܫܝܢ ܒܟܠܗܘܢ ܗܘܦܟܝܟܘܢ ܐܝܟ ܕܩܕܝܫ ܗܘ ܡܢ ܕܩܪܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 16, 'ܡܛܠ ܕܟܬܝܒ ܕܗܘܝܬܘܢ ܩܕܝܫܝܢ ܐܝܟܢܐ ܕܐܦ ܐܢܐ ܩܕܝܫ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 17, 'ܘܐܢ ܗܘ ܕܐܒܐ ܩܪܝܢ ܐܢܬܘܢ ܗܘ ܕܠܝܬ ܩܕܡܘܗܝ ܡܤܒ ܒܐܦܐ ܘܕܐܢ ܠܟܠܢܫ ܐܝܟ ܥܒܕܘܗܝ ܒܕܚܠܬܐ ܐܬܕܒܪܘ ܒܙܒܢܐ ܗܢܐ ܕܬܘܬܒܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 18, 'ܟܕ ܝܕܥܝܢ ܐܢܬܘܢ ܕܠܐ ܒܟܤܦܐ ܕܒܠܐ ܘܠܐ ܒܕܗܒܐ ܐܬܦܪܩܬܘܢ ܡܢ ܥܒܕܝܟܘܢ ܤܪܝܩܐ ܗܢܘܢ ܕܩܒܠܬܘܢ ܡܢ ܐܒܗܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 19, 'ܐܠܐ ܒܕܡܐ ܝܩܝܪܐ ܕܐܡܪܐ ܕܡܘܡܐ ܘܛܘܠܫܐ ܠܝܬ ܒܗ ܕܐܝܬܘܗܝ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 20, 'ܗܘ ܕܡܩܕܡ ܗܘܐ ܦܪܝܫ ܠܗܕܐ ܡܢ ܩܕܡ ܬܪܡܝܬܗ ܕܥܠܡܐ ܘܐܬܓܠܝ ܒܐܚܪܝܬܗܘܢ ܕܙܒܢܐ ܡܛܠܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 21, 'ܐܝܠܝܢ ܕܒܐܝܕܗ ܗܝܡܢܬܘܢ ܒܐܠܗܐ ܗܘ ܕܐܩܝܡܗ ܡܢ ܒܝܬ ܡܝܬܐ ܘܝܗܒ ܠܗ ܫܘܒܚܐ ܕܗܝܡܢܘܬܟܘܢ ܘܤܒܪܟܘܢ ܢܗܘܐ ܥܠ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 22, 'ܟܕ ܢܗܘܝܢ ܩܕܝܫܢ ܢܦܫܬܟܘܢ ܒܡܫܬܡܥܢܘܬܗ ܕܫܪܪܐ ܘܢܗܘܝܢ ܡܠܝܢ ܚܘܒܐ ܕܠܐ ܡܤܒ ܒܐܦܐ ܕܡܢ ܠܒܐ ܕܟܝܐ ܘܓܡܝܪܐ ܬܗܘܘܢ ܡܚܒܝܢ ܚܕ ܠܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 23, 'ܐܝܟ ܐܢܫܐ ܕܡܢ ܕܪܝܫ ܐܬܝܠܕܬܘܢ ܠܐ ܡܢ ܙܪܥܐ ܕܒܠܐ ܐܠܐ ܡܢ ܐܝܢܐ ܕܠܐ ܒܠܐ ܒܡܠܬܐ ܚܝܬܐ ܕܐܠܗܐ ܕܩܝܡܐ ܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 24, 'ܡܛܠ ܕܟܠ ܒܤܪ ܥܡܝܪܐ ܘܟܠܗ ܝܐܝܘܬܗ ܐܝܟ ܥܘܦܝܐ ܕܚܩܠܐ ܝܒܫ ܥܡܝܪܐ ܘܚܡܐ ܥܘܦܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 25, 'ܘܡܠܬܗ ܕܐܠܗܢ ܩܝܡܐ ܠܥܠܡܝܢ ܘܗܕܐ ܗܝ ܡܠܬܐ ܗܝ ܕܐܤܬܒܪܬܘܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 1, 'ܐܢܝܚܘ ܗܟܝܠ ܡܢܟܘܢ ܟܠܗ ܒܝܫܘܬܐ ܘܟܠܗ ܢܟܠܐ ܘܡܤܒ ܒܐܦܐ ܘܚܤܡܐ ܘܡܐܟܠ ܩܪܨܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 2, 'ܘܗܘܘ ܐܝܟ ܝܠܘܕܐ ܫܒܪܐ ܘܐܬܪܓܪܓܘ ܠܗ ܠܡܠܬܐ ܐܝܟ ܕܠܚܠܒܐ ܢܩܕܐ ܘܪܘܚܢܐ ܕܒܗ ܬܬܪܒܘܢ ܠܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 3, 'ܐܢ ܛܥܡܬܘܢ ܘܚܙܝܬܘܢ ܕܛܒ ܗܘ ܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 4, 'ܗܘ ܕܠܗ ܡܬܩܪܒܝܢ ܐܢܬܘܢ ܕܐܝܬܘܗܝ ܟܐܦܐ ܚܝܐ ܕܐܤܠܝܘܗܝ ܒܢܝ ܐܢܫܐ ܘܓܒܐ ܘܡܝܩܪ ܠܘܬ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 5, 'ܘܐܦ ܐܢܬܘܢ ܐܝܟ ܟܐܦܐ ܚܝܬܐ ܐܬܒܢܘ ܘܗܘܘ ܗܝܟܠܐ ܪܘܚܢܐ ܘܟܗܢܐ ܩܕܝܫܐ ܠܡܤܩܘ ܕܒܚܐ ܪܘܚܢܐ ܕܡܩܒܠܝܢ ܩܕܡ ܐܠܗܐ ܒܝܕ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 6, 'ܐܡܝܪ ܗܘ ܓܝܪ ܒܟܬܒܐ ܕܗܐ ܤܐܡ ܐܢܐ ܒܨܗܝܘܢ ܟܐܦܐ ܒܚܝܪܬܐ ܘܝܩܝܪܬܐ ܒܪܝܫ ܙܘܝܬܐ ܘܡܢ ܕܡܗܝܡܢ ܒܗ ܠܐ ܢܒܗܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 7, 'ܠܟܘܢ ܗܟܝܠ ܐܬܝܗܒ ܗܢܐ ܐܝܩܪܐ ܠܐܝܠܝܢ ܕܡܗܝܡܢܝܢ ܠܗܢܘܢ ܕܝܢ ܕܠܐ ܡܬܛܦܝܤܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 8, 'ܟܐܦܐ ܗܘ ܕܬܘܩܠܬܐ ܘܐܒܢܐ ܕܟܫܠܐ ܘܡܬܬܩܠܝܢ ܒܗ ܒܕܠܐ ܡܬܛܦܝܤܝܢ ܠܡܠܬܐ ܕܠܗܕܐ ܤܝܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 9, 'ܐܢܬܘܢ ܕܝܢ ܫܪܒܬܐ ܐܢܬܘܢ ܓܒܝܬܐ ܕܡܟܗܢܐ ܠܡܠܟܘܬܐ ܥܡܐ ܩܕܝܫܐ ܟܢܫܐ ܦܪܝܩܐ ܬܤܒܪܘܢ ܬܫܒܚܬܗ ܕܗܘ ܕܩܪܟܘܢ ܡܢ ܚܫܘܟܐ ܠܢܘܗܪܗ ܡܝܬܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 10, 'ܐܝܠܝܢ ܕܡܢ ܩܕܝܡ ܠܐ ܚܫܝܒܝܢ ܗܘܝܬܘܢ ܥܡܐ ܗܫܐ ܕܝܢ ܥܡܐ ܕܐܠܗܐ ܐܦܠܐ ܪܚܡܐ ܐܝܬ ܗܘܘ ܥܠܝܟܘܢ ܗܫܐ ܕܝܢ ܐܫܬܦܥܘ ܥܠܝܟܘܢ ܪܚܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 11, 'ܚܒܝܒܝ ܒܥܐ ܐܢܐ ܡܢܟܘܢ ܐܝܟ ܥܪܨܐ ܘܐܝܟ ܬܘܬܒܐ ܐܬܦܪܩܘ ܡܢ ܟܠܗܝܢ ܪܓܝܓܬܗ ܕܦܓܪܐ ܗܠܝܢ ܕܥܒܕܢ ܩܪܒܐ ܠܘܩܒܠ ܢܦܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 12, 'ܘܢܗܘܘܢ ܫܦܝܪܝܢ ܗܘܦܟܝܟܘܢ ܩܕܡ ܟܠܗܘܢ ܒܢܝ ܐܢܫܐ ܐܝܠܝܢ ܕܡܡܠܠܝܢ ܥܠܝܟܘܢ ܡܠܐ ܒܝܫܬܐ ܢܚܙܘܢ ܥܒܕܝܟܘܢ ܫܦܝܪܐ ܘܢܫܒܚܘܢ ܠܐܠܗܐ ܒܝܘܡܐ ܕܒܘܚܪܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 13, 'ܘܗܘܝܬܘܢ ܡܫܬܥܒܕܝܢ ܠܟܠܗܘܢ ܒܢܝ ܐܢܫܐ ܡܛܠ ܐܠܗܐ ܠܡܠܟܐ ܡܛܠ ܫܘܠܛܢܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 14, 'ܘܠܕܝܢܐ ܡܛܠ ܕܡܢܗ ܡܫܬܕܪܝܢ ܠܬܒܥܬܐ ܕܡܤܟܠܢܐ ܘܠܬܫܒܘܚܬܐ ܕܥܒܕܝ ܛܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 15, 'ܕܗܟܢܐ ܗܘ ܨܒܝܢܐ ܕܐܠܗܐ ܕܒܥܒܕܝܟܘܢ ܫܦܝܪܐ ܬܤܟܪܘܢ ܦܘܡܐ ܕܤܟܠܐ ܗܢܘܢ ܕܠܐ ܝܕܥܝܢ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 16, 'ܐܝܟ ܒܢܝ ܚܐܪܐ ܘܠܐ ܐܝܟ ܐܢܫܐ ܕܥܒܝܕܐ ܠܗܘܢ ܚܐܪܘܬܗܘܢ ܬܚܦܝܬܐ ܠܒܝܫܘܬܗܘܢ ܐܠܐ ܐܝܟ ܥܒܕܘܗܝ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 17, 'ܠܟܠܢܫ ܝܩܪܘ ܠܐܚܝܟܘܢ ܐܚܒܘ ܘܡܢ ܐܠܗܐ ܕܚܠܘ ܘܠܡܠܟܐ ܝܩܪܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 18, 'ܘܐܝܠܝܢ ܥܒܕܐ ܕܐܝܬ ܒܟܘܢ ܐܫܬܥܒܕܘ ܠܡܪܝܟܘܢ ܒܕܚܠܬܐ ܠܐ ܒܠܚܘܕ ܠܛܒܐ ܘܠܡܟܝܟܐ ܐܠܐ ܐܦ ܠܩܫܝܐ ܘܠܥܤܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 19, 'ܠܗܠܝܢ ܓܝܪ ܐܝܬ ܠܗܘܢ ܛܝܒܘܬܐ ܩܕܡ ܐܠܗܐ ܠܐܝܠܝܢ ܕܡܛܠ ܬܐܪܬܐ ܫܦܝܪܬܐ ܡܤܝܒܪܝܢ ܥܩܬܐ ܕܐܬܝܢ ܥܠܝܗܘܢ ܒܥܘܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 20, 'ܐܝܠܝܢ ܕܝܢ ܕܡܛܠ ܤܟܠܘܬܗܘܢ ܡܤܝܒܪܝܢ ܐܘܠܨܢܐ ܐܝܕܐ ܬܫܒܘܚܬܐ ܗܘܝܐ ܠܗܘܢ ܐܠܐ ܡܐ ܕܥܒܕܝܢ ܐܢܬܘܢ ܕܫܦܝܪ ܘܐܠܨܝܢ ܠܟܘܢ ܘܡܤܝܒܪܝܢ ܐܢܬܘܢ ܗܝܕܝܢ ܝܪܒܐ ܬܫܒܘܚܬܟܘܢ ܠܘܬ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 21, 'ܠܗܕܐ ܓܝܪ ܐܬܩܪܝܬܘܢ ܕܐܦ ܡܫܝܚܐ ܡܝܬ ܚܠܦܝܢ ܘܫܒܩ ܠܢ ܗܢܐ ܛܘܦܤܐ ܕܐܢܬܘܢ ܒܥܩܒܬܗ ܬܗܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 22, 'ܗܘ ܕܠܐ ܥܒܕ ܚܛܝܬܐ ܐܦܠܐ ܢܟܠܐ ܐܫܬܟܚ ܒܦܘܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 23, 'ܗܘ ܕܡܨܛܚܐ ܗܘܐ ܘܠܐ ܡܨܚܐ ܘܚܐܫ ܗܘܐ ܘܠܐ ܡܬܠܚܡ ܐܠܐ ܡܫܠܡ ܗܘܐ ܕܝܢܗ ܠܕܝܢܐ ܕܟܐܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 24, 'ܘܫܩܠ ܚܛܗܝܢ ܟܠܗܘܢ ܘܐܤܩ ܐܢܘܢ ܒܦܓܪܗ ܠܨܠܝܒܐ ܕܟܕ ܡܝܬܝܢܢ ܠܚܛܝܬܐ ܒܙܕܝܩܘܬܐ ܕܝܠܗ ܢܚܐ ܒܫܘܡܬܗ ܓܝܪ ܐܬܐܤܝܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 25, 'ܕܛܥܝܢ ܗܘܝܬܘܢ ܐܝܟ ܥܪܒܐ ܘܐܬܦܢܝܬܘܢ ܗܫܐ ܠܘܬ ܪܥܝܐ ܘܤܥܘܪܐ ܕܢܦܫܬܟܘܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 1, 'ܗܟܢܐ ܐܦ ܐܢܬܝܢ ܢܫܐ ܐܫܬܥܒܕܝܢ ܠܒܥܠܝܟܝܢ ܕܐܝܠܝܢ ܕܠܐ ܡܬܛܦܝܤܝܢ ܠܡܠܬܐ ܒܕܘܒܪܝܟܝܢ ܫܦܝܪܐ ܕܠܐ ܥܡܠܐ ܬܩܢܝܢ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 2, 'ܟܕ ܚܙܝܢ ܕܒܕܚܠܬܐ ܘܒܢܟܦܘܬܐ ܡܬܕܒܪܢ ܐܢܬܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 3, 'ܘܠܐ ܬܨܛܒܬܢ ܒܨܒܬܐ ܒܪܝܐ ܕܓܕܘܠܐ ܕܤܥܪܝܟܝܢ ܐܘ ܕܚܫܠܬܐ ܕܕܗܒܐ ܐܘ ܕܠܒܘܫܐ ܡܝܬܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 4, 'ܐܠܐ ܐܨܛܒܬܝܢ ܒܒܪܢܫܐ ܟܤܝܐ ܕܠܒܐ ܒܪܘܚܐ ܡܟܝܟܬܐ ܕܠܐ ܡܬܚܒܠܐ ܨܒܬܐ ܕܡܝܬܪ ܩܕܡ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 5, 'ܗܟܢܐ ܓܝܪ ܐܦ ܡܢ ܩܕܝܡ ܢܫܐ ܩܕܝܫܬܐ ܐܝܠܝܢ ܕܡܤܒܪܢ ܗܘܝ ܒܐܠܗܐ ܡܨܒܬܢ ܗܘܝ ܢܦܫܬܗܝܢ ܘܡܫܬܥܒܕܢ ܗܘܝ ܠܒܥܠܝܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 6, 'ܐܝܟܢܐ ܕܤܪܐ ܡܫܬܥܒܕܐ ܗܘܬ ܠܐܒܪܗܡ ܘܩܪܝܐ ܗܘܬ ܠܗ ܡܪܝ ܗܝ ܕܗܘܝܢ ܐܢܬܝܢ ܠܗ ܒܢܬܐ ܒܥܒܕܐ ܛܒܐ ܟܕ ܠܐ ܡܬܬܙܝܥܢ ܐܢܬܝܢ ܡܢ ܟܠ ܕܚܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 7, 'ܘܐܢܬܘܢ ܓܒܪܐ ܗܟܢܐ ܥܡܪܘ ܥܡ ܢܫܝܟܘܢ ܒܝܕܥܬܐ ܘܐܝܟ ܕܠܡܐܢܐ ܡܚܝܠܐ ܒܐܝܩܪܐ ܐܚܘܕܘ ܐܢܝܢ ܡܛܠ ܕܐܦ ܗܢܝܢ ܥܡܟܘܢ ܝܪܬܢ ܡܘܗܒܬܐ ܕܚܝܐ ܕܠܥܠܡ ܕܠܐ ܬܗܘܘܢ ܡܬܬܩܠܝܢ ܒܨܠܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 8, 'ܫܘܠܡܐ ܕܝܢ ܕܬܗܘܘܢ ܟܠܟܘܢ ܒܐܘܝܘܬܐ ܘܗܘܝܬܘܢ ܚܫܝܢ ܥܡ ܐܝܠܝܢ ܕܚܫܝܢ ܘܪܚܡܝܢ ܚܕ ܠܚܕ ܘܗܘܝܬܘܢ ܪܚܡܬܢܝܢ ܘܡܟܝܟܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 9, 'ܘܠܐܢܫ ܒܝܫܬܐ ܚܠܦ ܒܝܫܬܐ ܠܐ ܬܦܪܥܘܢ ܘܐܦܠܐ ܨܘܚܝܬܐ ܚܠܦ ܨܘܚܝܬܐ ܐܠܐ ܕܠܩܘܒܠܐ ܕܗܠܝܢ ܗܘܝܬܘܢ ܡܒܪܟܝܢ ܠܗܕܐ ܓܝܪ ܐܬܩܪܝܬܘܢ ܕܒܘܪܟܬܐ ܬܐܪܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 10, 'ܡܢ ܕܨܒܐ ܗܟܝܠ ܚܝܐ ܘܪܚܡ ܝܘܡܬܐ ܛܒܐ ܠܡܚܙܐ ܢܛܪ ܠܫܢܗ ܡܢ ܒܝܫܬܐ ܘܤܦܘܬܗ ܠܐ ܢܡܠܠܢ ܢܟܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 11, 'ܢܥܒܪ ܡܢ ܒܝܫܬܐ ܘܢܥܒܕ ܛܒܬܐ ܘܢܒܥܐ ܫܠܡܐ ܘܢܪܗܛ ܒܬܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 12, 'ܡܛܠ ܕܥܝܢܘܗܝ ܕܡܪܝܐ ܥܠ ܙܕܝܩܐ ܘܐܕܢܘܗܝ ܠܡܫܡܥ ܐܢܘܢ ܘܐܦܘܗܝ ܕܡܪܝܐ ܥܠ ܒܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 13, 'ܘܡܢܘ ܕܢܥܒܕ ܠܟܘܢ ܒܝܫܬܐ ܐܢ ܬܗܘܘܢ ܛܢܢܐ ܕܛܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 14, 'ܘܐܢ ܗܘ ܕܬܚܫܘܢ ܥܠ ܐܦܝ ܟܐܢܘܬܐ ܛܘܒܝܟܘܢ ܘܠܐ ܬܕܚܠܘܢ ܡܢ ܐܝܠܝܢ ܕܡܕܚܠܝܢ ܠܟܘܢ ܘܠܐ ܬܫܬܓܫܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 15, 'ܐܠܐ ܩܕܫܘ ܒܠܒܘܬܟܘܢ ܠܡܪܝܐ ܡܫܝܚܐ ܘܗܘܝܬܘܢ ܡܛܝܒܝܢ ܠܡܦܩ ܒܪܘܚܐ ܠܟܠ ܕܬܒܥ ܠܟܘܢ ܡܠܬܐ ܥܠ ܤܒܪܐ ܕܗܝܡܢܘܬܟܘܢ ܒܡܟܝܟܘܬܐ ܘܒܕܚܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 16, 'ܟܕ ܐܝܬ ܠܟܘܢ ܬܐܪܬܐ ܛܒܬܐ ܐܝܟܢܐ ܕܗܢܘܢ ܕܡܡܠܠܝܢ ܥܠܝܟܘܢ ܐܝܟ ܕܥܠ ܐܢܫܐ ܒܝܫܐ ܢܒܗܬܘܢ ܐܝܟ ܐܢܫܐ ܕܛܠܡܝܢ ܠܕܘܒܪܝܟܘܢ ܫܦܝܪܐ ܕܒܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 17, 'ܥܕܪܐ ܗܝ ܠܟܘܢ ܓܝܪ ܕܟܕ ܥܒܕܝܢ ܐܢܬܘܢ ܥܒܕܐ ܛܒܐ ܬܤܒܠܘܢ ܒܝܫܬܐ ܐܢ ܗܟܢܐ ܗܘ ܨܒܝܢܐ ܕܐܠܗܐ ܘܠܐ ܟܕ ܥܒܕܝܢ ܐܢܬܘܢ ܒܝܫܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 18, 'ܡܛܠ ܕܐܦ ܡܫܝܚܐ ܚܕܐ ܙܒܢ ܡܝܬ ܚܠܦ ܚܛܗܝܢ ܙܕܝܩܐ ܚܠܦ ܚܛܝܐ ܕܢܩܪܒܟܘܢ ܠܐܠܗܐ ܘܡܝܬ ܒܦܓܪ ܘܚܝܐ ܒܪܘܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 19, 'ܘܐܟܪܙ ܠܢܦܫܬܐ ܐܝܠܝܢ ܕܐܚܝܕܢ ܗܘܝ ܒܫܝܘܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 20, 'ܗܠܝܢ ܕܡܢ ܩܕܝܡ ܠܐ ܐܬܛܦܝܤ ܗܘܝ ܒܝܘܡܬܗ ܕܢܘܚ ܟܕ ܡܓܪܬ ܪܘܚܗ ܕܐܠܗܐ ܦܩܕܬ ܕܬܗܘܐ ܩܒܘܬܐ ܥܠ ܤܒܪܐ ܕܬܝܒܘܬܗܘܢ ܘܬܡܢܐ ܒܠܚܘܕ ܢܦܫܢ ܥܠܝܢ ܠܗ ܘܚܝܝ ܒܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 21, 'ܕܐܦ ܐܢܬܘܢ ܒܗ ܒܗܘ ܛܘܦܤܐ ܚܝܝܢ ܐܢܬܘܢ ܒܡܥܡܘܕܝܬܐ ܠܘ ܟܕ ܦܓܪܐ ܡܫܝܓܝܢ ܐܢܬܘܢ ܡܢ ܨܐܬܐ ܐܠܐ ܟܕ ܡܘܕܝܢ ܐܢܬܘܢ ܒܐܠܗܐ ܒܬܐܪܬܐ ܕܟܝܬܐ ܘܒܩܝܡܬܗ ܕܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 22, 'ܗܘ ܕܐܬܥܠܝ ܠܫܡܝܐ ܘܐܝܬܘܗܝ ܥܠ ܝܡܝܢܐ ܕܐܠܗܐ ܘܐܫܬܥܒܕܘ ܠܗ ܡܠܐܟܐ ܘܫܠܝܛܢܐ ܘܚܝܠܘܬܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 1, 'ܐܢ ܡܫܝܚܐ ܗܟܝܠ ܚܫ ܚܠܦܝܟܘܢ ܒܒܤܪ ܘܐܦ ܐܢܬܘܢ ܒܗ ܒܗܢܐ ܪܥܝܢܐ ܐܙܕܝܢܘ ܟܠܡܢ ܕܡܐܬ ܓܝܪ ܒܦܓܪܗ ܫܠܝ ܠܗ ܡܢ ܟܠܗܘܢ ܚܛܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 2, 'ܕܠܐ ܡܟܝܠ ܠܪܓܝܓܬܐ ܕܒܢܝܢܫܐ ܢܚܐ ܟܡܐ ܙܒܢܐ ܕܐܝܬܘܗܝ ܒܦܓܪܐ ܐܠܐ ܠܨܒܝܢܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 3, 'ܤܦܩ ܓܝܪ ܙܒܢܐ ܗܘ ܕܥܒܪ ܕܦܠܚܬܘܢ ܒܗ ܨܒܝܢܐ ܕܚܢܦܐ ܒܐܤܘܛܘܬܐ ܘܒܪܘܝܘܬܐ ܘܒܨܚܢܘܬܐ ܘܒܙܡܪܐ ܘܒܦܘܠܚܢܐ ܕܫܐܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 4, 'ܘܗܐ ܗܫܐ ܡܬܕܡܪܝܢ ܘܡܓܕܦܝܢ ܥܠܝܟܘܢ ܒܕܠܐ ܡܫܬܪܚܝܬܘܢ ܥܡܗܘܢ ܒܗܝ ܐܤܘܛܘܬܐ ܩܕܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 5, 'ܗܢܘܢ ܕܝܗܒܝܢ ܦܬܓܡܐ ܠܐܠܗܐ ܗܘ ܕܥܬܝܕ ܠܡܕܢ ܡܝܬܐ ܘܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 6, 'ܡܛܠ ܗܢܐ ܓܝܪ ܐܤܬܒܪ ܐܦ ܠܡܝܬܐ ܕܢܬܕܝܢܘܢ ܐܝܟ ܒܢܝܢܫܐ ܒܒܤܪ ܘܢܚܘܢ ܒܐܠܗܐ ܒܪܘܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 7, 'ܡܛܝܬ ܠܗ ܕܝܢ ܚܪܬܐ ܕܟܠ ܡܛܠ ܗܢܐ ܐܬܢܟܦܘ ܘܐܬܬܥܝܪܘ ܠܨܠܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 8, 'ܘܩܕܡ ܟܠ ܡܕܡ ܚܘܒܐ ܚܪܝܦܐ ܠܘܬ ܚܕܕܐ ܢܗܘܐ ܠܟܘܢ ܚܘܒܐ ܓܝܪ ܡܚܦܐ ܤܘܓܐܐ ܕܚܛܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 9, 'ܘܗܘܝܬܘܢ ܪܚܡܝܢ ܐܟܤܢܝܐ ܕܠܐ ܪܛܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 10, 'ܘܟܠܢܫ ܡܢܟܘܢ ܡܘܗܒܬܐ ܕܩܒܠ ܡܢ ܐܠܗܐ ܢܫܡܫ ܒܗ ܠܚܒܪܘܗܝ ܐܝܟ ܪܒܝ ܒܬܐ ܛܒܐ ܕܛܝܒܘܬܐ ܡܦܪܫܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 11, 'ܟܠܡܢ ܕܡܡܠܠ ܐܝܟ ܡܠܬܐ ܕܐܠܗܐ ܢܡܠܠ ܘܟܠܡܢ ܕܡܫܡܫ ܐܝܟ ܕܡܢ ܚܝܠܐ ܗܘ ܕܐܠܗܐ ܝܗܒ ܠܗ ܕܒܟܠ ܕܥܒܕܝܢ ܐܢܬܘܢ ܢܫܬܒܚ ܐܠܗܐ ܒܝܕ ܝܫܘܥ ܡܫܝܚܐ ܗܘ ܕܕܝܠܗ ܗܝ ܬܫܒܘܚܬܐ ܘܐܝܩܪܐ ܠܥܠܡ ܥܠܡܝܢ ܐܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 12, 'ܚܒܝܒܝ ܠܐ ܬܬܕܡܪܘܢ ܒܢܤܝܘܢܐ ܕܗܘܝܢ ܠܟܘܢ ܐܝܟ ܕܡܕܡ ܢܘܟܪܝ ܓܕܫ ܠܟܘܢ ܡܛܠ ܕܠܒܘܚܪܢܟܘܢ ܗܘ ܗܘܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 13, 'ܐܠܐ ܚܕܘ ܕܡܫܬܘܬܦܝܢ ܐܢܬܘܢ ܠܚܫܘܗܝ ܕܡܫܝܚܐ ܕܗܟܢܐ ܐܦ ܒܓܠܝܢܐ ܕܬܫܒܘܚܬܗ ܬܚܕܘܢ ܘܬܪܘܙܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 14, 'ܘܐܢ ܡܬܚܤܕܝܢ ܐܢܬܘܢ ܥܠ ܐܦܝ ܫܡܗ ܕܡܫܝܚܐ ܛܘܒܝܟܘܢ ܕܪܘܚܐ ܡܫܒܚܬܐ ܕܐܠܗܐ ܡܬܬܢܝܚܐ ܥܠܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 15, 'ܒܠܚܘܕ ܠܐ ܐܢܫ ܡܢܟܘܢ ܐܝܟ ܩܛܘܠܐ ܐܘ ܐܝܟ ܓܢܒܐ ܐܘ ܐܝܟ ܥܒܕ ܒܝܫܬܐ ܢܗܘܐ ܚܐܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 16, 'ܐܢ ܕܝܢ ܚܐܫ ܐܝܟ ܟܪܤܛܝܢܐ ܠܐ ܢܒܗܬ ܐܠܐ ܢܫܒܚ ܠܐܠܗܐ ܒܗ ܒܗܢܐ ܫܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 17, 'ܡܛܠ ܕܙܒܢܐ ܗܘ ܕܢܫܪܐ ܕܝܢܐ ܡܢ ܒܝܬܗ ܕܐܠܗܐ ܐܢ ܕܝܢ ܡܢܢ ܡܫܪܐ ܐܝܕܐ ܗܝ ܚܪܬܐ ܕܐܝܠܝܢ ܕܠܐ ܡܬܛܦܝܤܝܢ ܠܤܒܪܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 18, 'ܘܐܢ ܙܕܝܩܐ ܠܡܚܤܢ ܚܝܐ ܪܫܝܥܐ ܘܚܛܝܐ ܐܝܟܐ ܡܫܬܟܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 19, 'ܡܛܠ ܗܢܐ ܐܝܠܝܢ ܕܚܫܝܢ ܐܝܟ ܨܒܝܢܗ ܕܐܠܗܐ ܢܓܥܠܘܢ ܠܗ ܢܦܫܬܗܘܢ ܒܥܒܕܐ ܫܦܝܪܐ ܐܝܟ ܕܠܒܪܘܝܐ ܡܗܝܡܢܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 1, 'ܒܥܐ ܐܢܐ ܕܝܢ ܡܢ ܩܫܝܫܐ ܕܐܝܬ ܒܟܘܢ ܐܢܐ ܩܫܝܫܐ ܚܒܪܟܘܢ ܘܤܗܕܐ ܕܚܫܘܗܝ ܕܡܫܝܚܐ ܘܫܘܬܦܐ ܕܫܘܒܚܗ ܗܘ ܕܥܬܝܕ ܕܢܬܓܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 2, 'ܪܥܘ ܡܪܥܝܬܐ ܕܐܠܗܐ ܕܡܫܠܡܐ ܠܟܘܢ ܘܤܥܘܪܘ ܪܘܚܢܐܝܬ ܠܐ ܒܩܛܝܪܐ ܐܠܐ ܒܨܒܝܢܐ ܠܐ ܒܝܘܬܪܢܐ ܛܢܦܐ ܐܠܐ ܡܢ ܟܠܗ ܠܒܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 3, 'ܠܐ ܐܝܟ ܡܪܝܐ ܕܡܪܥܝܬܐ ܐܠܐ ܐܝܟ ܕܬܗܘܘܢ ܠܗܘܢ ܕܡܘܬܐ ܫܦܝܪܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 4, 'ܕܟܕ ܢܬܓܠܐ ܪܒ ܪܥܘܬܐ ܬܩܒܠܘܢ ܡܢܗ ܟܠܝܠܐ ܕܫܘܒܚܐ ܕܠܐ ܚܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 5, 'ܘܐܢܬܘܢ ܥܠܝܡܐ ܐܫܬܥܒܕܘ ܠܩܫܝܫܝܟܘܢ ܘܐܬܥܛܦܘ ܚܝܨܐܝܬ ܡܟܝܟܘܬ ܪܥܝܢܐ ܠܘܬ ܚܕܕܐ ܡܛܠ ܕܐܠܗܐ ܤܩܘܒܠܐ ܗܘ ܕܐܝܠܝܢ ܕܡܬܬܪܝܡܝܢ ܘܠܡܟܝܟܐ ܝܗܒ ܛܝܒܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 6, 'ܐܬܡܟܟܘ ܗܟܝܠ ܬܚܝܬ ܐܝܕܗ ܬܩܝܦܬܐ ܕܐܠܗܐ ܕܗܝ ܬܪܝܡܟܘܢ ܒܙܒܢܐ ܕܙܕܩ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 7, 'ܘܨܦܬܟܘܢ ܟܠܗ ܫܕܘ ܥܠ ܐܠܗܐ ܕܠܗ ܒܛܝܠ ܥܠܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 8, 'ܐܬܬܥܝܪܘ ܘܥܗܕܘ ܡܛܠ ܕܒܥܠܕܒܒܟܘܢ ܤܛܢܐ ܐܝܟ ܐܪܝܐ ܢܗܡ ܘܡܗܠܟ ܘܒܥܐ ܕܠܡܢܘ ܢܒܠܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 9, 'ܩܘܡܘ ܗܟܝܠ ܠܩܘܒܠܗ ܟܕ ܡܫܪܪܝܢ ܐܢܬܘܢ ܒܗܝܡܢܘܬܐ ܘܕܥܘ ܕܐܦ ܥܠ ܐܚܝܟܘܢ ܕܒܥܠܡܐ ܗܢܘܢ ܗܠܝܢ ܚܫܐ ܥܪܨܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 10, 'ܐܠܗܐ ܕܝܢ ܕܛܝܒܘܬܐ ܗܘ ܕܩܪܢ ܠܫܘܒܚܗ ܕܠܥܠܡ ܒܝܕ ܝܫܘܥ ܡܫܝܚܐ ܗܘ ܕܝܗܒ ܠܢ ܕܟܕ ܢܤܝܒܪ ܗܠܝܢ ܐܘܠܨܢܐ ܙܥܘܪܐ ܕܢܬܚܝܠ ܘܢܫܬܪܪ ܘܢܬܩܝܡ ܒܗ ܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 11, 'ܕܠܗ ܫܘܒܚܐ ܘܐܘܚܕܢܐ ܘܐܝܩܪܐ ܠܥܠܡ ܥܠܡܝܢ ܐܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 12, 'ܗܠܝܢ ܙܥܘܪܝܬܐ ܐܝܟ ܕܤܒܪ ܐܢܐ ܟܬܒܬ ܠܟܘܢ ܒܝܕ ܤܠܘܢܘܤ ܐܚܐ ܡܗܝܡܢܐ ܘܡܦܤ ܐܢܐ ܘܤܗܕ ܐܢܐ ܕܗܕܐ ܗܝ ܛܝܒܘܬܐ ܫܪܝܪܬܐ ܕܐܠܗܐ ܗܕܐ ܕܩܝܡܝܢ ܐܢܬܘܢ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 13, 'ܫܐܠܐ ܫܠܡܟܘܢ ܥܕܬܐ ܓܒܝܬܐ ܕܒܒܒܠ ܘܡܪܩܘܤ ܒܪܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 14, 'ܫܐܠܘ ܒܫܠܡܐ ܚܕ ܕܚܕ ܒܢܘܫܩܬܐ ܩܕܝܫܬܐ ܫܠܡܐ ܥܡ ܟܠܗܘܢ ܐܝܠܝܢ ܕܒܡܫܝܚܐ ܐܢܘܢ ܐܡܝܢ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 1, 'ܫܡܥܘܢ ܦܛܪܘܤ ܥܒܕܐ ܘܫܠܝܚܐ ܕܝܫܘܥ ܡܫܝܚܐ ܠܐܝܠܝܢ ܕܠܗܝܡܢܘܬܐ ܫܘܝܬ ܒܐܝܩܪܐ ܥܡܢ ܐܫܬܘܝܘ ܒܙܕܝܩܘܬܐ ܕܡܪܢ ܘܦܪܘܩܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 2, 'ܛܝܒܘܬܐ ܘܫܠܡܐ ܢܤܓܐ ܠܟܘܢ ܒܫܘܘܕܥܐ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 3, 'ܐܝܟ ܡܢ ܕܟܠܗܝܢ ܐܝܠܝܢ ܕܐܝܬܝܗܝܢ ܕܚܝܠܐ ܐܠܗܝܐ ܠܘܬ ܚܝܐ ܘܕܚܠܬ ܐܠܗܐ ܝܗܒ ܒܝܕ ܫܘܘܕܥܐ ܕܗܘ ܕܩܪܐ ܠܢ ܒܬܫܒܘܚܬܐ ܕܝܠܗ ܘܡܝܬܪܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 4, 'ܕܒܐܝܕܝܗܝܢ ܫܘܘܕܝܐ ܪܘܪܒܐ ܘܝܩܝܪܐ ܠܟܘܢ ܝܗܒ ܕܒܝܕ ܗܠܝܢ ܬܗܘܘܢ ܫܘܬܦܐ ܕܟܝܢܐ ܐܠܗܝܐ ܟܕ ܥܪܩܝܢ ܐܢܬܘܢ ܡܢ ܚܒܠܐ ܕܪܓܝܓܬܐ ܕܒܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 5, 'ܘܗܝ ܕܝܢ ܗܕܐ ܟܕ ܫܩܠܛܥܢܐ ܟܠܗ ܡܥܠܝܢ ܐܢܬܘܢ ܐܘܤܦܘ ܥܠ ܗܝܡܢܘܬܟܘܢ ܡܝܬܪܘܬܐ ܥܠ ܕܝܢ ܡܝܬܪܘܬܐ ܝܕܥܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 6, 'ܥܠ ܕܝܢ ܝܕܥܬܐ ܡܚܡܤܢܢܘܬܐ ܥܠ ܕܝܢ ܡܚܡܤܢܢܘܬܐ ܡܤܝܒܪܢܘܬܐ ܥܠ ܕܝܢ ܡܤܝܒܪܢܘܬܐ ܕܚܠܬ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 7, 'ܥܠ ܕܝܢ ܕܚܠܬ ܐܠܗܐ ܪܚܡܬ ܐܚܘܬܐ ܥܠ ܕܝܢ ܪܚܡܬ ܐܚܘܬܐ ܚܘܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 8, 'ܗܠܝܢ ܓܝܪ ܟܕ ܫܟܝܚܢ ܠܟܘܢ ܘܝܬܝܪܢ ܠܐ ܗܘܐ ܒܛܝܠܐ ܐܦܠܐ ܕܠܐ ܦܐܪܐ ܡܩܝܡܢ ܠܟܘܢ ܒܫܘܘܕܥܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 9, 'ܗܘ ܓܝܪ ܕܠܐ ܫܟܝܚܢ ܠܗ ܗܠܝܢ ܤܡܝܐ ܐܝܬܘܗܝ ܕܠܐ ܚܙܐ ܕܛܥܐ ܕܘܟܝܐ ܕܚܛܗܘܗܝ ܩܕܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 10, 'ܘܥܠ ܗܝ ܝܬܝܪܐܝܬ ܐܚܝ ܝܨܦܘ ܕܒܝܕ ܥܒܕܝܟܘܢ ܛܒܐ ܩܪܝܬܟܘܢ ܘܓܒܝܬܟܘܢ ܡܫܪܪܬܐ ܬܥܒܕܘܢ ܟܕ ܓܝܪ ܗܠܝܢ ܥܒܕܝܢ ܐܢܬܘܢ ܠܐ ܡܬܘܡ ܡܫܬܪܥܝܢ ܐܢܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 11, 'ܗܟܢܐ ܓܝܪ ܥܬܝܪܐܝܬ ܡܬܝܗܒܐ ܠܟܘܢ ܡܥܠܬܐ ܕܡܠܟܘܬܐ ܕܠܥܠܡ ܕܡܪܢ ܘܦܪܘܩܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 12, 'ܘܥܠ ܗܕܐ ܠܐ ܡܡܐܢ ܐܢܐ ܡܢ ܕܡܥܗܕ ܐܢܐ ܠܟܘܢ ܐܡܝܢܐܝܬ ܥܠ ܗܠܝܢ ܟܕ ܛܒ ܐܦ ܝܕܥܝܢ ܐܢܬܘܢ ܘܤܡܝܟܝܢ ܐܢܬܘܢ ܥܠ ܫܪܪܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 13, 'ܕܟܐܢܐ ܕܝܢ ܐܤܬܒܪܬ ܠܝ ܕܟܡܐ ܕܐܝܬܝ ܒܦܓܪܐ ܗܢܐ ܐܥܝܪܟܘܢ ܒܥܘܗܕܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 14, 'ܟܕ ܝܕܥ ܐܢܐ ܕܥܘܢܕܢܐ ܕܦܓܪܝ ܒܥܓܠ ܗܘܐ ܐܝܟܢܐ ܕܐܦ ܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܐܘܕܥܢܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 15, 'ܝܨܦ ܐܢܐ ܕܝܢ ܕܐܦ ܐܡܝܢܐܝܬ ܗܘܐ ܐܝܬ ܠܟܘܢ ܕܐܦ ܡܢ ܒܬܪ ܡܦܩܢܐ ܕܝܠܝ ܥܘܗܕܢܐ ܕܗܠܝܢ ܬܗܘܘܢ ܥܒܕܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 16, 'ܠܐ ܗܘܐ ܓܝܪ ܟܕ ܒܬܪ ܡܬܠܐ ܕܥܒܝܕܝܢ ܒܐܘܡܢܘܬܐ ܐܙܠܝܢܢ ܐܘܕܥܢܟܘܢ ܚܝܠܗ ܘܡܐܬܝܬܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܐܠܐ ܟܕ ܚܙܝܐ ܗܘܝܢ ܕܪܒܘܬܐ ܕܝܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 17, 'ܟܕ ܓܝܪ ܢܤܒ ܡܢ ܐܠܗܐ ܐܒܐ ܐܝܩܪܐ ܘܬܫܒܘܚܬܐ ܟܕ ܩܠܐ ܐܬܐ ܠܗ ܕܐܝܟ ܗܢܐ ܡܢ ܬܫܒܘܚܬܐ ܦܐܝܬ ܒܪܒܘܬܗ ܕܗܢܘ ܒܪܝ ܗܘ ܚܒܝܒܐ ܗܘ ܕܒܗ ܐܨܛܒܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 18, 'ܐܦ ܚܢܢ ܠܗ ܠܗܢܐ ܩܠܐ ܫܡܥܢܢ ܡܢ ܫܡܝܐ ܕܐܬܐ ܠܗ ܟܕ ܐܝܬܝܢ ܗܘܝܢ ܥܡܗ ܒܛܘܪܐ ܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 19, 'ܘܐܝܬ ܠܢ ܕܫܪܝܪܐ ܐܦ ܡܠܬܐ ܕܢܒܝܘܬܐ ܗܝ ܕܫܦܝܪ ܥܒܕܝܢ ܐܢܬܘܢ ܟܕ ܒܗ ܚܝܪܝܢ ܐܢܬܘܢ ܐܝܟ ܕܒܫܪܓܐ ܕܡܢܗܪ ܒܐܬܪܐ ܥܡܘܛܐ ܥܕܡܐ ܕܐܝܡܡܐ ܢܢܗܪ ܘܫܡܫܐ ܢܕܢܚ ܒܠܒܘܬܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 20, 'ܟܕ ܗܕܐ ܠܘܩܕܡ ܝܕܥܝܢ ܐܢܬܘܢ ܕܟܠ ܢܒܝܘܬܐ ܫܪܝܐ ܕܟܬܒܐ ܕܝܠܗ ܠܐ ܗܘܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 21, 'ܠܐ ܗܘܐ ܓܝܪ ܒܨܒܝܢܐ ܕܒܪܢܫܐ ܐܬܬ ܡܢ ܡܬܘܡ ܢܒܝܘܬܐ ܐܠܐ ܟܕ ܡܢ ܪܘܚܐ ܩܕܝܫܐ ܡܬܢܓܕܝܢ ܡܠܠܘ ܩܕܝܫܐ ܒܢܝܢܫܐ ܕܐܠܗܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 1, 'ܗܘܘ ܕܝܢ ܐܦ ܢܒܝܐ ܕܓܠܐ ܒܥܡܐ ܐܝܟ ܕܐܦ ܒܟܘܢ ܢܗܘܘܢ ܡܠܦܢܐ ܕܓܠܐ ܐܝܠܝܢ ܕܡܥܠܝܢ ܗܪܤܝܤ ܕܐܒܕܢܐ ܘܒܡܪܐ ܕܙܒܢ ܐܢܘܢ ܟܦܪܝܢ ܟܕ ܡܝܬܝܢ ܥܠ ܢܦܫܗܘܢ ܐܒܕܢܐ ܡܤܪܗܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 2, 'ܘܤܓܝܐܐ ܢܐܙܠܘܢ ܒܬܪ ܛܢܦܘܬܐ ܕܝܠܗܘܢ ܗܠܝܢ ܕܡܛܠܬܗܘܢ ܐܘܪܚܐ ܕܫܪܪܐ ܬܬܓܕܦ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 3, 'ܘܒܥܠܘܒܘܬܐ ܘܒܡܠܐ ܕܒܕܝܐ ܢܬܬܓܪܘܢ ܒܟܘܢ ܗܠܝܢ ܕܕܝܢܗܘܢ ܡܢ ܩܕܝܡ ܠܐ ܒܛܠ ܘܐܒܕܢܗܘܢ ܠܐ ܢܐܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 4, 'ܐܢ ܓܝܪ ܐܠܗܐ ܥܠ ܡܠܐܟܐ ܕܚܛܘ ܠܐ ܚܤ ܐܠܐ ܒܫܫܠܬܐ ܕܥܡܛܢܐ ܥܓܢ ܐܢܘܢ ܒܬܚܬܝܬܐ ܘܐܫܠܡ ܐܢܘܢ ܕܢܬܢܛܪܘܢ ܠܕܝܢܐ ܕܫܘܢܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 5, 'ܘܥܠ ܥܠܡܐ ܩܕܡܝܐ ܠܐ ܚܤ ܐܠܐ ܠܢܘܚ ܕܬܡܢܝܐ ܟܪܘܙܐ ܕܟܐܢܘܬܐ ܢܛܪ ܟܕ ܛܘܦܢܐ ܥܠ ܥܠܡܐ ܕܪܫܝܥܐ ܐܝܬܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 6, 'ܘܟܕ ܠܡܕܝܢܬܐ ܕܤܕܘܡ ܘܕܥܡܘܪܐ ܐܘܩܕ ܘܒܗܦܘܟܝܐ ܚܝܒ ܐܢܝܢ ܟܕ ܬܚܘܝܬܐ ܠܪܫܝܥܐ ܕܥܬܝܕܝܢ ܕܢܗܘܘܢ ܤܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 7, 'ܐܦ ܠܠܘܛ ܙܕܝܩܐ ܕܡܬܩܦܚ ܗܘܐ ܡܢ ܗܘܦܟܐ ܕܒܛܢܦܘܬܐ ܕܗܠܝܢ ܕܕܠܐ ܢܡܘܤܐ ܐܝܬܝܗܘܢ ܗܘܘ ܦܨܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 8, 'ܒܚܙܬܐ ܓܝܪ ܘܒܫܡܥܐ ܟܕ ܥܡܪ ܗܘܐ ܟܐܢܐ ܗܘ ܒܝܢܬܗܘܢ ܝܘܡ ܡܢ ܝܘܡ ܠܢܦܫܗ ܙܕܝܩܬܐ ܒܥܒܕܐ ܕܠܐ ܢܡܘܤ ܡܫܢܩ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 9, 'ܝܕܥ ܗܘ ܡܪܝܐ ܕܢܦܪܘܩ ܡܢ ܐܘܠܨܢܐ ܠܐܝܠܝܢ ܕܕܚܠܝܢ ܠܗ ܠܥܘܠܐ ܕܝܢ ܠܝܘܡܐ ܕܕܝܢܐ ܟܕ ܡܫܬܢܩܝܢ ܢܛܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 10, 'ܝܬܝܪܐܝܬ ܕܝܢ ܠܐܝܠܝܢ ܕܒܬܪ ܒܤܪܐ ܒܪܓܬܐ ܕܛܡܐܘܬܐ ܐܙܠܝܢ ܘܥܠ ܡܪܘܬܐ ܡܒܤܪܝܢ ܡܪܚܐ ܘܡܫܩܠܐ ܕܡܢ ܬܫܒܘܚܬܐ ܠܐ ܙܝܥܝܢ ܟܕ ܡܓܕܦܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 11, 'ܐܝܟܐ ܕܡܠܐܟܐ ܕܒܚܝܠܐ ܘܒܥܘܫܢܐ ܪܘܪܒܝܢ ܡܢܗܘܢ ܠܐ ܡܝܬܝܢ ܥܠܝܗܘܢ ܡܢ ܡܪܝܐ ܕܝܢܐ ܕܓܘܕܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 12, 'ܗܠܝܢ ܕܝܢ ܕܐܝܟ ܚܝܘܬܐ ܚܪܫܬܐ ܗܘܘ ܒܟܝܢܐ ܠܚܪܒܐ ܘܠܚܒܠܐ ܟܕ ܒܐܝܠܝܢ ܕܠܐ ܝܕܥܝܢ ܡܓܕܦܝܢ ܒܚܒܠܐ ܕܝܠܗܘܢ ܢܬܚܒܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 13, 'ܟܕ ܐܝܠܝܢ ܕܗܘܐ ܒܗܘܢ ܥܘܠܐ ܐܓܪܐ ܕܥܘܠܐ ܗܢܝܘܬܐ ܚܫܝܒ ܠܗܘܢ ܒܘܤܡܐ ܕܗܘܐ ܒܐܝܡܡܐ ܡܟܬܡܐ ܘܡܠܝܝ ܡܘܡܐ ܕܟܕ ܡܬܒܤܡܝܢ ܒܢܝܚܬܗܘܢ ܡܬܦܢܩܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 14, 'ܟܕ ܥܝܢܐ ܐܝܬ ܠܗܘܢ ܕܡܠܝܢ ܓܘܪܐ ܘܚܛܗܐ ܕܠܐ ܡܘܦܝܢ ܟܕ ܡܫܕܠܝܢ ܠܢܦܫܬܐ ܕܠܐ ܤܡܝܟܢ ܘܠܒܐ ܕܡܕܪܫ ܒܥܠܘܒܘܬܐ ܐܝܬ ܠܗܘܢ ܒܢܝܐ ܕܠܘܛܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 15, 'ܕܟܕ ܫܒܩܘ ܐܘܪܚܐ ܬܪܝܨܬܐ ܫܓܘ ܘܐܙܠܘ ܒܐܘܪܚܐ ܕܒܠܥܡ ܒܪ ܒܥܘܪ ܗܘ ܕܐܓܪܐ ܕܥܘܠܐ ܐܚܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 16, 'ܡܟܤܢܘܬܐ ܕܝܢ ܕܗܘܬ ܠܗ ܠܡܬܥܒܪܢܘܬܗ ܐܬܢܐ ܕܠܐ ܩܠܐ ܕܒܩܠܐ ܕܒܢܝܢܫܐ ܡܠܠܬ ܟܠܬ ܠܫܛܝܘܬܗ ܕܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 17, 'ܗܠܝܢ ܐܢܘܢ ܡܥܝܢܐ ܕܠܐ ܡܝܐ ܥܢܢܐ ܕܡܢ ܥܠܥܠܐ ܡܬܪܕܦܢ ܗܠܝܢ ܕܥܡܛܢܐ ܕܚܫܘܟܐ ܢܛܝܪ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 18, 'ܟܕ ܓܝܪ ܓܘܢܚܐ ܕܤܪܝܩܘܬܐ ܡܡܠܠܝܢ ܡܫܕܠܝܢ ܒܪܓܝܓܬܐ ܛܢܦܬܐ ܕܒܤܪܐ ܠܐܝܠܝܢ ܕܒܡܠܐ ܩܠܝܠ ܥܪܩܝܢ ܡܢ ܐܝܠܝܢ ܕܒܛܥܝܘܬܐ ܡܬܗܦܟܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 19, 'ܘܚܐܪܘܬܐ ܠܗܘܢ ܡܫܬܘܕܝܢ ܟܕ ܗܢܘܢ ܥܒܕܐ ܐܝܬܝܗܘܢ ܕܚܒܠܐ ܠܗܘ ܓܝܪ ܡܕܡ ܕܐܢܫ ܙܟܐ ܠܗ ܠܗܢܐ ܐܦ ܡܫܥܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 20, 'ܐܢ ܓܝܪ ܟܕ ܥܪܩܘ ܡܢ ܛܢܦܘܬܗ ܕܥܠܡܐ ܒܫܘܘܕܥܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܘܦܪܘܩܢ ܒܗܝܢ ܒܗܠܝܢ ܟܕ ܡܬܥܪܙܠܝܢ ܬܘܒ ܡܙܕܟܝܢ ܗܘܬ ܠܗ ܚܪܬܗܘܢ ܕܒܝܫܐ ܡܢ ܩܕܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 21, 'ܦܩܚ ܗܘܐ ܠܗܘܢ ܓܝܪ ܕܠܐ ܢܫܬܘܕܥܘܢ ܠܐܘܪܚܐ ܕܙܕܝܩܘܬܐ ܐܘ ܕܟܕ ܐܫܬܘܕܥܘ ܠܒܬܪܐ ܢܗܦܟܘܢ ܡܢ ܦܘܩܕܢܐ ܩܕܝܫܐ ܕܐܫܬܠܡ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 22, 'ܓܕܫ ܠܗܘܢ ܕܝܢ ܗܠܝܢ ܕܡܬܠܐ ܫܪܝܪܐ ܕܟܠܒܐ ܕܗܦܟ ܥܠ ܬܝܘܒܗ ܘܚܙܝܪܬܐ ܕܤܚܬ ܒܥܘܪܓܠܐ ܕܤܝܢܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 1, 'ܗܕܐ ܡܢ ܟܕܘ ܚܒܝܒܝ ܐܓܪܬܐ ܕܬܪܬܝܢ ܟܬܒ ܐܢܐ ܠܟܘܢ ܗܠܝܢ ܕܒܗܝܢ ܡܥܝܪ ܐܢܐ ܒܥܘܗܕܢܐ ܠܪܥܝܢܟܘܢ ܫܦܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 2, 'ܕܬܬܥܗܕܘܢ ܠܡܠܐ ܕܩܕܡ ܐܬܐܡܪ ܡܢ ܢܒܝܐ ܩܕܝܫܐ ܘܠܦܘܩܕܢܗ ܕܡܪܢ ܘܦܪܘܩܢ ܕܒܝܕ ܫܠܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 3, 'ܟܕ ܗܕܐ ܠܘܩܕܡ ܝܕܥܝܢ ܐܢܬܘܢ ܕܢܐܬܘܢ ܒܚܪܬܐ ܕܝܘܡܬܐ ܡܒܙܚܢܐ ܕܡܒܙܚܝܢ ܟܕ ܐܝܟ ܪܓܝܓܬܐ ܕܝܠܗܘܢ ܡܗܠܟܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 4, 'ܘܐܡܪܝܢ ܕܐܝܟܐ ܐܝܬܘܗܝ ܡܘܠܟܢܐ ܕܡܐܬܝܬܗ ܡܢ ܟܕ ܓܝܪ ܐܒܗܬܢ ܫܟܒܘ ܟܠܡܕܡ ܗܟܢܐ ܡܟܬܪ ܡܢ ܫܘܪܝܗ ܕܒܪܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 5, 'ܛܥܝܐ ܠܗܘܢ ܓܝܪ ܗܕܐ ܟܕ ܨܒܝܢ ܕܫܡܝܐ ܐܝܬܝܗܘܢ ܗܘܘ ܡܢ ܩܕܝܡ ܘܐܪܥܐ ܡܢ ܡܝܐ ܘܒܝܕ ܡܝܐ ܩܡܬ ܒܡܠܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 6, 'ܗܠܝܢ ܕܒܐܝܕܝܗܘܢ ܥܠܡܐ ܕܗܝܕܝܢ ܛܦ ܒܡܝܐ ܘܐܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 7, 'ܫܡܝܐ ܕܝܢ ܕܗܫܐ ܘܐܪܥܐ ܒܡܠܬܐ ܕܝܠܗ ܐܤܝܢܝܢ ܟܕ ܠܢܘܪܐ ܡܬܢܛܪܝܢ ܠܝܘܡܐ ܕܕܝܢܐ ܘܕܐܒܕܢܐ ܕܒܢܝܢܫܐ ܪܫܝܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 8, 'ܗܕܐ ܕܝܢ ܚܕܐ ܠܐ ܬܛܥܝܟܘܢ ܚܒܝܒܝ ܕܚܕ ܝܘܡܐ ܠܡܪܝܐ ܐܝܟ ܐܠܦ ܫܢܝܢ ܐܝܬܘܗܝ ܘܐܠܦ ܫܢܝܢ ܐܝܟ ܝܘܡܐ ܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 9, 'ܠܐ ܡܫܬܘܚܪ ܡܪܝܐ ܒܡܘܠܟܢܘܗܝ ܐܝܟ ܕܐܢܫܝܢ ܫܘܘܚܪܐ ܡܤܒܪܝܢ ܐܠܐ ܡܓܪ ܪܘܚܗ ܡܛܠܬܟܘܢ ܒܕܠܐ ܨܒܐ ܕܐܢܫ ܢܐܒܕ ܐܠܐ ܟܠܢܫ ܠܬܝܒܘܬܐ ܢܐܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 10, 'ܐܬܐ ܕܝܢ ܝܘܡܗ ܕܡܪܝܐ ܐܝܟ ܓܢܒܐ ܗܘ ܕܒܗ ܫܡܝܐ ܡܢ ܫܠܝ ܥܒܪܝܢ ܐܤܛܘܟܤܐ ܕܝܢ ܟܕ ܝܩܕܝܢ ܢܫܬܪܘܢ ܘܐܪܥܐ ܘܥܒܕܐ ܕܒܗ ܬܫܬܟܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 11, 'ܟܕ ܗܟܝܠ ܗܠܝܢ ܟܠܗܘܢ ܡܫܬܪܝܢ ܕܐܝܟ ܐܝܠܝܢ ܙܕܩ ܠܟܘܢ ܕܬܗܘܘܢ ܒܗܘܦܟܝܟܘܢ ܩܕܝܫܐ ܘܒܕܚܠܬ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 12, 'ܟܕ ܡܤܟܝܢ ܐܢܬܘܢ ܘܤܘܚܝܢ ܐܢܬܘܢ ܠܡܐܬܝܬܐ ܕܝܘܡܗ ܕܐܠܗܐ ܗܘ ܕܒܗ ܫܡܝܐ ܟܕ ܡܬܒܚܪܝܢ ܒܢܘܪܐ ܢܫܬܪܘܢ ܘܐܤܛܘܟܤܐ ܟܕ ܝܩܕܝܢ ܢܫܘܚܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 13, 'ܠܫܡܝܐ ܕܝܢ ܚܕܬܐ ܘܐܪܥܐ ܚܕܬܐ ܐܝܟ ܡܘܠܟܢܐ ܕܝܠܗ ܡܤܟܝܢܢ ܗܠܝܢ ܕܒܗܘܢ ܙܕܝܩܘܬܐ ܥܡܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 14, 'ܡܛܠ ܗܢܐ ܚܒܝܒܝ ܟܕ ܠܗܠܝܢ ܡܤܟܝܢ ܐܢܬܘܢ ܝܨܦܘ ܕܕܠܐ ܟܘܬܡܐ ܘܕܠܐ ܡܘܡܐ ܠܗ ܬܫܬܟܚܘܢ ܒܫܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 15, 'ܘܠܡܓܪܬ ܪܘܚܗ ܕܡܪܝܐ ܦܘܪܩܢܐ ܬܚܫܒܘܢ ܐܝܟܢܐ ܕܐܦ ܐܚܘܢ ܚܒܝܒܐ ܦܘܠܘܤ ܐܝܟ ܚܟܡܬܐ ܕܐܬܝܗܒܬ ܠܗ ܟܬܒ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 16, 'ܐܝܟܢܐ ܕܒܟܠܗܝܢ ܐܓܪܬܗ ܡܠܠ ܒܗܝܢ ܥܠ ܗܠܝܢ ܐܝܠܝܢ ܕܐܝܬ ܒܗܝܢ ܡܕܡ ܕܥܤܝܩ ܠܤܘܟܠܐ ܗܠܝܢ ܕܐܝܠܝܢ ܕܕܠܐ ܝܘܠܦܢܐ ܐܢܘܢ ܘܠܐ ܤܡܝܟܝܢ ܡܥܩܡܝܢ ܠܗܝܢ ܐܝܟ ܕܐܦ ܠܗܠܝܢ ܟܬܒܐ ܕܫܪܟܐ ܠܘܬ ܐܒܕܢܐ ܕܝܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 17, 'ܐܢܬܘܢ ܗܟܝܠ ܚܒܝܒܝ ܟܕ ܩܕܡܝܢ ܐܢܬܘܢ ܝܕܥܝܢ ܛܪܘ ܢܦܫܟܘܢ ܕܕܠܡܐ ܟܕ ܐܙܠܝܢ ܐܢܬܘܢ ܒܬܪ ܛܥܝܘܬܐ ܕܐܝܠܝܢ ܕܕܠܐ ܢܡܘܤ ܐܢܘܢ ܬܦܠܘܢ ܡܢ ܤܡܟܐ ܕܝܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 18, 'ܗܘܝܬܘܢ ܕܝܢ ܡܬܪܒܝܢ ܒܛܝܒܘܬܐ ܘܒܝܕܥܬܐ ܕܡܪܢ ܘܦܪܘܩܢ ܝܫܘܥ ܡܫܝܚܐ ܘܕܐܠܗܐ ܐܒܐ ܕܠܗ ܬܫܒܘܚܬܐ ܐܦ ܗܫܐ ܘܒܟܠܙܒܢ ܘܠܝܘܡܝ ܥܠܡܐ ܐܡܝܢ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 1, 'ܡܤܒܪܝܢܢ ܠܟܘܢ ܠܗܘ ܕܐܝܬܘܗܝ ܗܘܐ ܡܢ ܒܪܫܝܬ ܗܘ ܕܫܡܥܢ ܘܚܙܝܢܝܗܝ ܒܥܝܢܝܢ ܚܙܝܢ ܘܓܫܢ ܒܐܝܕܝܢ ܗܘ ܕܐܝܬܘܗܝ ܡܠܬܐ ܕܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 2, 'ܘܚܝܐ ܐܬܓܠܝܘ ܘܚܙܝܢ ܘܤܗܕܝܢܢ ܘܡܟܪܙܝܢܢ ܠܟܘܢ ܚܝܐ ܕܠܥܠܡ ܗܢܘܢ ܕܐܝܬܝܗܘܢ ܗܘܘ ܠܘܬ ܐܒܐ ܘܐܬܓܠܝܘ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 3, 'ܘܡܕܡ ܕܚܙܝܢ ܘܫܡܥܢ ܡܘܕܥܝܢܢ ܐܦ ܠܟܘܢ ܕܬܗܘܐ ܠܟܘܢ ܫܘܬܦܘܬܐ ܥܡܢ ܘܫܘܬܦܘܬܢ ܕܝܢ ܕܝܠܢ ܐܝܬܝܗ ܥܡ ܐܒܐ ܘܥܡ ܒܪܗ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 4, 'ܘܗܠܝܢ ܟܬܒܝܢܢ ܠܟܘܢ ܕܚܕܘܬܢ ܕܒܟܘܢ ܬܗܘܐ ܡܫܡܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 5, 'ܘܗܕܐ ܗܝ ܤܒܪܬܐ ܕܫܡܥܢ ܡܢܗ ܘܡܤܒܪܝܢܢ ܠܟܘܢ ܕܐܠܗܐ ܢܘܗܪܐ ܗܘ ܘܚܫܘܟܐ ܟܠ ܟܠܗ ܠܝܬ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 6, 'ܘܐܢ ܐܡܪܝܢܢ ܕܫܘܬܦܘܬܐ ܐܝܬ ܠܢ ܥܡܗ ܘܡܗܠܟܝܢܢ ܒܚܫܘܟܐ ܕܓܠܐ ܚܢܢ ܘܠܘ ܒܫܪܪܐ ܪܕܝܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 7, 'ܐܢ ܕܝܢ ܒܢܘܗܪܐ ܡܗܠܟܝܢܢ ܐܝܟ ܕܗܘ ܐܝܬܘܗܝ ܒܢܘܗܪܐ ܐܝܬ ܠܢ ܫܘܬܦܘܬܐ ܥܡ ܚܕܕܐ ܘܕܡܐ ܕܝܫܘܥ ܒܪܗ ܡܕܟܐ ܠܢ ܡܢ ܟܠܗܘܢ ܚܛܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 8, 'ܘܐܢ ܢܐܡܪ ܕܠܝܬ ܠܢ ܚܛܝܬܐ ܢܦܫܢ ܡܛܥܝܢܢ ܘܫܪܪܐ ܠܝܬ ܒܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 9, 'ܐܢ ܕܝܢ ܡܘܕܝܢܢ ܒܚܛܗܝܢ ܡܗܝܡܢ ܗܘ ܘܙܕܝܩ ܕܢܫܒܘܩ ܠܢ ܚܛܗܝܢ ܘܢܕܟܝܢ ܡܢ ܟܠܗ ܥܘܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 10, 'ܘܐܢ ܐܡܪܝܢܢ ܕܠܐ ܚܛܝܢ ܥܒܕܝܢܢ ܠܗ ܕܓܠܐ ܘܡܠܬܗ ܠܝܬ ܠܘܬܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 1, 'ܒܢܝ ܗܠܝܢ ܟܬܒ ܐܢܐ ܠܟܘܢ ܕܠܐ ܬܚܛܘܢ ܘܐܢ ܐܢܫ ܢܚܛܐ ܐܝܬ ܠܢ ܦܪܩܠܛܐ ܠܘܬ ܐܒܐ ܝܫܘܥ ܡܫܝܚܐ ܙܕܝܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 2, 'ܗܘܝܘ ܓܝܪ ܚܘܤܝܐ ܕܥܠ ܐܦܝ ܚܛܗܝܢ ܘܠܘ ܚܠܦܝܢ ܕܝܠܢ ܒܠܚܘܕ ܐܠܐ ܐܦ ܚܠܦ ܟܠܗ ܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 3, 'ܘܒܗܕܐ ܡܪܓܫܝܢܢ ܕܝܕܥܢܝܗܝ ܐܢ ܢܛܪܝܢܢ ܦܘܩܕܢܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 4, 'ܗܘ ܓܝܪ ܕܐܡܪ ܕܝܕܥܬܗ ܘܦܘܩܕܢܘܗܝ ܠܐ ܢܛܪ ܕܓܠܐ ܗܘ ܘܫܪܪܐ ܠܝܬ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 5, 'ܗܘ ܕܝܢ ܕܢܛܪ ܡܠܬܗ ܒܗܢܐ ܡܫܠܡ ܫܪܝܪܐܝܬ ܚܘܒܗ ܕܐܠܗܐ ܒܗܕܐ ܓܝܪ ܝܕܥܝܢܢ ܕܒܗ ܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 6, 'ܗܘ ܕܐܡܪ ܕܒܗ ܐܝܬܝ ܘܠܐ ܠܗ ܕܐܝܟ ܗܠܟܬܗ ܕܝܠܗ ܢܗܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 7, 'ܚܒܝܒܝ ܠܘ ܦܘܩܕܢܐ ܚܕܬܐ ܟܬܒ ܐܢܐ ܠܟܘܢ ܐܠܐ ܦܘܩܕܢܐ ܥܬܝܩܐ ܗܘ ܕܐܝܬ ܗܘܐ ܠܟܘܢ ܡܢ ܒܪܫܝܬ ܦܘܩܕܢܐ ܕܝܢ ܥܬܝܩܐ ܐܝܬܘܗܝ ܡܠܬܐ ܗܝ ܕܫܡܥܬܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 8, 'ܬܘܒ ܦܘܩܕܢܐ ܚܕܬܐ ܟܬܒ ܐܢܐ ܠܟܘܢ ܗܘ ܕܐܝܬܘܗܝ ܫܪܝܪܐ ܒܗ ܘܒܟܘܢ ܕܚܫܘܟܐ ܥܒܪ ܠܗ ܘܢܘܗܪܐ ܫܪܝܪܐ ܫܪܝ ܡܬܚܙܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 9, 'ܡܢ ܕܐܡܪ ܗܟܝܠ ܕܐܝܬܘܗܝ ܒܢܘܗܪܐ ܘܤܢܐ ܠܐܚܘܗܝ ܒܚܫܘܟܐ ܐܝܬܘܗܝ ܥܕܡܐ ܠܗܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 10, 'ܗܘ ܕܝܢ ܕܡܚܒ ܠܐܚܘܗܝ ܒܢܘܗܪܐ ܡܟܬܪ ܘܟܫܠܐ ܠܝܬ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 11, 'ܗܘ ܕܝܢ ܕܤܢܐ ܠܐܚܘܗܝ ܒܚܫܘܟܐ ܐܝܬܘܗܝ ܘܒܚܫܘܟܐ ܡܗܠܟ ܘܠܐ ܝܕܥ ܠܐܝܟܐ ܐܙܠ ܡܛܠ ܕܚܫܘܟܐ ܤܡܝ ܐܢܝܢ ܥܝܢܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 12, 'ܟܬܒ ܐܢܐ ܠܟܘܢ ܒܢܝܐ ܕܐܫܬܒܩܘ ܠܟܘܢ ܚܛܗܝܟܘܢ ܡܛܠ ܫܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 13, 'ܟܬܒ ܐܢܐ ܠܟܘܢ ܐܒܗܐ ܕܝܕܥܬܘܢܝܗܝ ܠܗܘ ܕܐܝܬܘܗܝ ܗܘܐ ܡܢ ܒܪܫܝܬ ܟܬܒ ܐܢܐ ܠܟܘܢ ܥܠܝܡܐ ܕܙܟܝܬܘܢܝܗܝ ܠܒܝܫܐ ܟܬܒܬ ܠܟܘܢ ܛܠܝܐ ܕܝܕܥܬܘܢ ܠܐܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 14, 'ܟܬܒܬ ܠܟܘܢ ܐܒܗܐ ܕܝܕܥܬܘܢ ܠܗܘ ܕܡܢ ܒܪܫܝܬ ܟܬܒܬ ܠܟܘܢ ܥܠܝܡܐ ܕܚܝܠܬܢܐ ܐܢܬܘܢ ܘܡܠܬܗ ܕܐܠܗܐ ܫܪܝܐ ܒܟܘܢ ܘܙܟܝܬܘܢܝܗܝ ܠܒܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 15, 'ܠܐ ܬܪܚܡܘܢ ܠܥܠܡܐ ܘܠܐ ܠܡܕܡ ܕܐܝܬ ܒܗ ܡܢ ܓܝܪ ܕܪܚܡ ܠܥܠܡܐ ܚܘܒܗ ܕܐܒܐ ܠܝܬ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 16, 'ܟܠ ܡܕܡ ܓܝܪ ܕܐܝܬ ܒܗ ܒܥܠܡܐ ܪܓܬܐ ܗܘ ܕܦܓܪܐ ܘܪܓܬܐ ܕܥܝܢܐ ܘܫܘܒܗܪܐ ܕܥܠܡܐ ܗܠܝܢ ܕܠܐ ܗܘܝ ܡܢ ܐܒܐ ܐܠܐ ܡܢܗ ܐܢܝܢ ܡܢ ܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 17, 'ܘܥܒܪ ܥܠܡܐ ܗܘ ܘܪܓܬܗ ܗܘ ܕܝܢ ܕܥܒܕ ܨܒܝܢܗ ܕܐܠܗܐ ܡܩܘܐ ܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 18, 'ܒܢܝ ܙܒܢܐ ܗܘ ܐܚܪܝܐ ܘܐܝܟ ܡܕܡ ܕܫܡܥܬܘܢ ܕܐܬܐ ܡܫܝܚܐ ܕܓܠܐ ܘܗܫܐ ܗܘܘ ܠܗܘܢ ܤܓܝܐܐ ܡܫܝܚܐ ܕܓܠܐ ܘܡܢ ܗܕܐ ܝܕܥܝܢܢ ܕܙܒܢܐ ܗܘ ܐܚܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 19, 'ܡܢܢ ܢܦܩܘ ܐܠܐ ܠܘ ܡܢܢ ܗܘܘ ܐܠܘ ܓܝܪ ܡܢܢ ܗܘܘ ܠܘܬܢ ܡܟܬܪܝܢ ܗܘܘ ܐܠܐ ܢܦܩܘ ܡܢܢ ܕܬܬܝܕܥ ܕܠܘ ܡܢܢ ܗܘܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 20, 'ܘܐܢܬܘܢ ܡܫܝܚܘܬܐ ܐܝܬ ܠܟܘܢ ܡܢ ܩܕܝܫܐ ܘܦܪܫܝܢ ܐܢܬܘܢ ܗܘ ܠܟܠ ܐܢܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 21, 'ܠܐ ܟܬܒܬ ܠܟܘܢ ܕܠܐ ܝܕܥܝܢ ܐܢܬܘܢ ܠܗ ܠܫܪܪܐ ܐܠܐ ܕܝܕܥܝܢ ܐܢܬܘܢ ܠܗ ܘܕܟܠܗ ܕܓܠܘܬܐ ܠܐ ܗܘܬ ܡܢܗ ܡܢ ܫܪܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 22, 'ܡܢܘ ܕܓܠܐ ܐܠܐ ܐܢ ܐܝܢܐ ܕܟܦܪ ܕܝܫܘܥ ܠܐ ܗܘܐ ܡܫܝܚܐ ܗܢܐ ܗܘ ܡܫܝܚܐ ܕܓܠܐ ܗܘ ܕܟܦܪ ܒܐܒܐ ܟܦܪ ܐܦ ܒܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 23, 'ܘܗܘ ܕܟܦܪ ܒܒܪܐ ܐܦܠܐ ܒܐܒܐ ܡܗܝܡܢ ܡܢ ܕܡܘܕܐ ܒܒܪܐ ܐܦ ܒܐܒܐ ܡܘܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 24, 'ܘܐܢܬܘܢ ܡܕܡ ܕܫܡܥܬܘܢ ܡܢ ܩܕܝܡ ܢܩܘܐ ܠܘܬܟܘܢ ܐܢ ܓܝܪ ܡܩܘܐ ܠܘܬܟܘܢ ܡܕܡ ܕܫܡܥܬܘܢ ܡܢ ܩܕܝܡ ܐܦ ܐܢܬܘܢ ܡܩܘܝܢ ܐܢܬܘܢ ܒܐܒܐ ܘܒܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 25, 'ܘܗܢܘ ܫܘܘܕܝܐ ܕܐܫܬܘܕܝ ܠܢ ܚܝܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 26, 'ܗܠܝܢ ܕܝܢ ܟܬܒܬ ܠܟܘܢ ܡܛܠ ܐܝܠܝܢ ܕܡܛܥܝܢ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 27, 'ܘܐܦ ܐܢܬܘܢ ܐܢ ܬܩܘܐ ܠܘܬܟܘܢ ܡܫܝܚܘܬܐ ܕܩܒܠܬܘܢ ܡܢܗ ܠܐ ܤܢܝܩܝܢ ܐܢܬܘܢ ܕܐܢܫ ܢܠܦܟܘܢ ܐܠܐ ܐܝܟ ܡܫܝܚܘܬܐ ܕܐܝܬܝܗ ܡܢ ܐܠܗܐ ܗܝ ܡܠܦܐ ܠܟܘܢ ܥܠ ܟܠܡܕܡ ܘܫܪܝܪܐ ܗܝ ܘܠܝܬ ܒܗ ܕܓܠܘܬܐ ܘܐܝܟ ܕܐܠܦܟܘܢ ܩܘܘ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 28, 'ܘܗܫܐ ܒܢܝ ܩܘܘ ܒܗ ܕܡܐ ܕܡܬܓܠܐ ܠܐ ܢܒܗܬ ܡܢܗ ܐܠܐ ܬܗܘܐ ܠܢ ܓܠܝܘܬ ܐܦܐ ܒܡܐܬܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 29, 'ܐܢ ܝܕܥܬܘܢ ܕܙܕܝܩ ܗܘ ܕܥܘ ܕܐܦ ܟܠ ܕܥܒܕ ܙܕܝܩܘܬܐ ܡܢܗ ܐܝܬܘܗܝ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 1, 'ܘܚܙܘ ܕܟܡܐ ܤܓܝ ܚܘܒܗ ܕܐܒܐ ܠܘܬܢ ܕܒܢܝܐ ܩܪܢ ܐܦ ܥܒܕܢ ܡܛܠ ܗܢܐ ܥܠܡܐ ܠܐ ܝܕܥ ܠܢ ܡܛܠ ܕܐܦܠܐ ܠܗ ܝܕܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 2, 'ܚܒܝܒܝ ܗܫܐ ܒܢܘܗܝ ܚܢܢ ܕܐܠܗܐ ܘܠܐ ܐܬܓܠܝ ܥܕܡܐ ܠܗܫܐ ܡܢܐ ܥܬܝܕܝܢܢ ܕܢܗܘܐ ܝܕܥܝܢܢ ܕܝܢ ܕܡܐ ܕܐܬܓܠܝ ܒܕܡܘܬܗ ܗܘܝܢܢ ܘܚܙܝܢܢ ܠܗ ܐܝܟ ܡܕܡ ܕܐܝܬܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 3, 'ܘܟܠ ܐܝܢܐ ܕܐܝܬ ܠܗ ܥܠܘܗܝ ܗܢܐ ܤܒܪܐ ܡܕܟܐ ܢܦܫܗ ܐܝܟ ܕܗܘ ܕܟܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 4, 'ܡܢ ܕܝܢ ܕܥܒܕ ܚܛܝܬܐ ܥܘܠܐ ܤܥܪ ܚܛܝܬܐ ܓܝܪ ܟܠܗ ܥܘܠܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 5, 'ܘܝܕܥܝܢ ܐܢܬܘܢ ܕܗܘ ܕܐܬܓܠܝ ܕܢܤܒ ܚܛܗܝܢ ܘܚܛܝܬܐ ܒܗ ܠܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 6, 'ܘܟܠ ܕܒܗ ܡܩܘܐ ܠܐ ܚܛܐ ܘܟܠ ܕܚܛܐ ܠܐ ܚܙܝܗܝ ܘܠܐ ܝܕܥܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 7, 'ܒܢܝ ܠܐ ܐܢܫ ܢܛܥܝܟܘܢ ܗܘ ܕܥܒܕ ܙܕܝܩܘܬܐ ܙܕܝܩܐ ܗܘ ܐܝܟ ܕܐܦ ܗܘ ܡܫܝܚܐ ܐܝܬܘܗܝ ܙܕܝܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 8, 'ܗܘ ܕܤܥܪ ܚܛܝܬܐ ܡܢ ܤܛܢܐ ܗܘ ܡܛܠ ܕܡܢ ܪܫܝܬܐ ܗܘ ܤܛܢܐ ܚܛܝܐ ܗܘ ܘܡܛܠ ܗܢܐ ܐܬܚܙܝ ܒܪܗ ܕܐܠܗܐ ܕܢܫܪܐ ܥܒܕܘܗܝ ܕܤܛܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 9, 'ܟܠ ܕܐܬܝܠܕ ܡܢ ܐܠܗܐ ܚܛܝܬܐ ܠܐ ܥܒܕ ܡܛܠ ܕܙܪܥܗ ܒܗ ܐܝܬܘܗܝ ܘܠܐ ܡܫܟܚ ܠܡܚܛܐ ܕܡܢ ܐܠܗܐ ܐܬܝܠܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 10, 'ܒܗܕܐ ܡܬܦܪܫܝܢ ܒܢܘܗܝ ܕܐܠܗܐ ܡܢ ܒܢܘܗܝ ܕܤܛܢܐ ܟܠ ܕܠܐ ܥܒܕ ܙܕܝܩܘܬܐ ܘܠܐ ܡܚܒ ܠܐܚܘܗܝ ܠܝܬܘܗܝ ܡܢ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 11, 'ܕܗܢܘ ܦܘܩܕܢܐ ܕܫܡܥܬܘܢ ܡܢ ܩܕܝܡ ܕܬܚܒܘܢ ܚܕ ܠܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 12, 'ܠܘ ܐܝܟ ܩܐܝܢ ܗܘ ܕܐܝܬܘܗܝ ܗܘܐ ܡܢ ܒܝܫܐ ܘܩܛܠ ܠܐܚܘܗܝ ܘܡܛܠ ܡܢܐ ܩܛܠܗ ܐܠܐ ܡܛܠ ܕܒܝܫܝܢ ܗܘܘ ܥܒܕܘܗܝ ܘܕܐܚܘܗܝ ܙܕܝܩܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 13, 'ܘܠܐ ܬܬܕܡܪܘܢ ܐܚܝ ܐܢ ܤܢܐ ܠܟܘܢ ܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 14, 'ܚܢܢ ܝܕܥܝܢܢ ܕܫܢܝܢܢ ܡܢ ܡܘܬܐ ܠܚܝܐ ܒܗܕܐ ܕܡܚܒܝܢܢ ܠܐܚܝܢ ܗܘ ܕܠܐ ܡܚܒ ܠܐܚܘܗܝ ܡܩܘܐ ܒܡܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 15, 'ܟܠ ܓܝܪ ܕܤܢܐ ܠܐܚܘܗܝ ܩܛܠ ܐܢܫܐ ܗܘ ܘܝܕܥܝܢ ܐܢܬܘܢ ܕܟܠ ܕܩܛܠ ܐܢܫܐ ܠܐ ܡܫܟܚܝܢ ܡܩܘܝܢ ܒܗ ܚܝܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 16, 'ܒܗܕܐ ܝܕܥܝܢܢ ܚܘܒܗ ܕܠܘܬܢ ܕܗܘ ܝܗܒ ܢܦܫܗ ܚܠܦܝܢ ܘܐܦ ܚܢܢ ܙܕܩ ܠܢ ܕܥܠ ܐܦܝ ܐܚܝܢ ܢܬܠ ܢܦܫܬܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 17, 'ܘܐܝܢܐ ܕܐܝܬ ܠܗ ܩܢܝܢܐ ܕܥܠܡܐ ܘܢܚܙܐ ܠܐܚܘܗܝ ܕܤܢܝܩ ܘܢܐܚܘܕ ܪܚܡܘܗܝ ܡܢܗ ܐܝܟܢܐ ܐܝܬ ܒܗ ܚܘܒܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 18, 'ܒܢܝ ܠܐ ܢܚܒ ܚܕ ܠܚܕ ܒܡܠܐ ܘܒܠܫܢܐ ܐܠܐ ܒܥܒܕܐ ܘܒܫܪܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 19, 'ܘܒܗܕܐ ܡܫܬܘܕܥܝܢܢ ܕܡܢ ܫܪܪܐ ܐܝܬܝܢ ܘܩܕܡ ܕܢܐܬܐ ܗܘ ܡܦܝܤܝܢܢ ܠܒܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 20, 'ܕܐܢ ܗܘ ܠܒܢ ܒܤܪ ܠܢ ܟܡܐ ܐܠܗܐ ܕܪܒ ܡܢ ܠܒܢ ܘܝܕܥ ܟܠܡܕܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 21, 'ܚܒܝܒܝ ܐܢ ܠܒܢ ܠܐ ܒܤܪ ܠܢ ܓܠܝܢ ܐܢܝܢ ܐܦܝܢ ܩܕܡ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 22, 'ܘܟܠܡܕܡ ܕܫܐܠܝܢܢ ܢܤܒܝܢܢ ܡܢܗ ܡܛܠ ܕܢܛܪܝܢܢ ܦܘܩܕܢܘܗܝ ܘܫܦܝܪܬܐ ܤܥܪܝܢܢ ܩܕܡܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 23, 'ܘܗܢܐ ܗܘ ܦܘܩܕܢܗ ܕܢܗܝܡܢ ܒܫܡܐ ܕܒܪܗ ܝܫܘܥ ܡܫܝܚܐ ܘܢܚܒ ܚܕ ܠܚܕ ܐܝܟ ܕܦܩܕܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 24, 'ܘܐܝܢܐ ܕܢܛܪ ܦܘܩܕܢܘܗܝ ܒܗ ܡܬܢܛܪ ܘܗܘ ܫܪܐ ܒܗ ܘܒܗܕܐ ܡܤܬܟܠܝܢܢ ܕܫܪܐ ܒܢ ܡܢ ܪܘܚܗ ܗܝ ܕܝܗܒ ܠܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 1, 'ܚܒܝܒܝ ܠܐ ܠܟܠ ܪܘܚܝܢ ܬܗܝܡܢܘܢ ܐܠܐ ܗܘܝܬܘܢ ܦܪܫܝܢ ܪܘܚܐ ܐܢ ܡܢ ܐܠܗܐ ܐܝܬܝܗܝܢ ܡܛܠ ܕܤܓܝܐܐ ܢܒܝܐ ܕܓܠܐ ܢܦܩܘ ܒܗ ܒܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 2, 'ܒܗܕܐ ܡܬܝܕܥܐ ܪܘܚܗ ܕܐܠܗܐ ܟܠ ܪܘܚܐ ܕܡܘܕܝܢ ܕܝܫܘܥ ܡܫܝܚܐ ܐܬܐ ܒܒܤܪ ܡܢ ܐܠܗܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 3, 'ܘܟܠ ܪܘܚܐ ܕܠܐ ܡܘܕܝܐ ܕܝܫܘܥ ܐܬܐ ܒܒܤܪ ܠܝܬܝܗ ܡܢ ܐܠܗܐ ܐܠܐ ܗܕܐ ܡܢ ܡܫܝܚܐ ܗܝ ܕܓܠܐ ܗܘ ܕܫܡܥܬܘܢ ܕܐܬܐ ܘܗܫܐ ܒܥܠܡܐ ܐܝܬܘܗܝ ܡܢ ܟܕܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 4, 'ܐܢܬܘܢ ܕܝܢ ܡܢ ܐܠܗܐ ܐܝܬܝܟܘܢ ܒܢܝܐ ܘܙܟܝܬܘܢ ܐܢܘܢ ܡܛܠ ܕܪܒ ܗܘ ܕܒܟܘܢ ܡܢ ܗܘ ܕܒܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 5, 'ܘܗܠܝܢ ܡܢ ܥܠܡܐ ܐܢܘܢ ܡܛܠ ܗܢܐ ܡܢ ܥܠܡܐ ܡܡܠܠܝܢ ܘܥܠܡܐ ܠܗܘܢ ܫܡܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 6, 'ܚܢܢ ܕܝܢ ܡܢ ܐܠܗܐ ܚܢܢ ܘܗܘ ܕܝܕܥ ܠܐܠܗܐ ܫܡܥ ܠܢ ܘܗܘ ܕܠܐ ܐܝܬܘܗܝ ܡܢ ܐܠܗܐ ܠܐ ܫܡܥ ܠܢ ܒܗ ܒܗܕܐ ܡܤܬܟܠܝܢܢ ܠܪܘܚܐ ܕܫܪܪܐ ܘܠܪܘܚܐ ܕܡܛܥܝܢܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 7, 'ܚܒܝܒܝ ܢܚܒ ܚܕ ܠܚܕ ܡܛܠ ܕܚܘܒܐ ܡܢ ܐܠܗܐ ܗܘ ܘܟܠ ܡܢ ܕܡܚܒ ܡܢ ܐܠܗܐ ܝܠܝܕ ܘܝܕܥ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 8, 'ܡܛܠ ܕܐܠܗܐ ܚܘܒܐ ܗܘ ܘܟܠ ܕܠܐ ܡܚܒ ܠܐ ܝܕܥ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 9, 'ܒܗܕܐ ܐܬܝܕܥ ܚܘܒܗ ܕܐܠܗܐ ܕܠܘܬܢ ܕܠܒܪܗ ܝܚܝܕܝܐ ܫܕܪ ܐܠܗܐ ܠܥܠܡܐ ܕܢܚܐ ܒܐܝܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 10, 'ܒܗܢܐ ܐܝܬܘܗܝ ܚܘܒܐ ܠܐ ܗܘܐ ܕܚܢܢ ܐܚܒܢ ܠܐܠܗܐ ܐܠܐ ܗܘ ܐܚܒܢ ܘܫܕܪ ܠܒܪܗ ܚܘܤܝܐ ܥܠ ܐܦܝ ܚܛܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 11, 'ܚܒܝܒܝ ܐܢ ܗܟܢܐ ܐܚܒܢ ܐܠܗܐ ܐܦ ܚܢܢ ܚܝܒܝܢܢ ܚܕ ܠܚܕ ܠܡܚܒܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 12, 'ܠܐܠܗܐ ܡܡܬܘܡ ܐܢܫ ܠܐ ܚܙܝܗܝ ܐܢ ܕܝܢ ܢܚܒ ܚܕ ܠܚܕ ܐܠܗܐ ܒܢ ܡܩܘܐ ܘܚܘܒܗ ܡܫܬܡܠܐ ܒܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 13, 'ܘܒܗܕܐ ܝܕܥܝܢܢ ܕܒܗ ܡܩܘܝܢܢ ܘܗܘ ܡܩܘܐ ܒܢ ܕܡܢ ܪܘܚܗ ܝܗܒ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 14, 'ܘܚܢܢ ܚܙܝܢ ܘܡܤܗܕܝܢܢ ܕܐܒܐ ܫܕܪ ܠܒܪܗ ܦܪܘܩܐ ܠܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 15, 'ܟܠ ܕܡܘܕܐ ܒܝܫܘܥ ܕܗܘܝܘ ܒܪܗ ܕܐܠܗܐ ܐܠܗܐ ܒܗ ܡܩܘܐ ܘܗܘ ܡܩܘܐ ܒܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 16, 'ܘܚܢܢ ܗܝܡܢܢ ܘܝܕܥܢ ܚܘܒܐ ܕܐܝܬ ܠܗ ܠܐܠܗܐ ܨܐܕܝܢ ܐܠܗܐ ܓܝܪ ܚܘܒܐ ܗܘ ܘܟܠ ܕܡܩܘܐ ܒܚܘܒܐ ܒܐܠܗܐ ܡܩܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 17, 'ܘܒܗܕܐ ܡܫܬܠܡ ܚܘܒܗ ܥܡܢ ܕܓܠܝܘܬ ܐܦܐ ܬܗܘܐ ܠܢ ܒܝܘܡܐ ܕܕܝܢܐ ܡܛܠ ܕܐܝܟ ܕܗܘܐ ܗܘ ܗܟܢܐ ܐܦ ܚܢܢ ܐܝܬܝܢ ܒܗܢܐ ܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 18, 'ܕܚܠܬܐ ܒܚܘܒܐ ܠܝܬ ܐܠܐ ܚܘܒܐ ܡܫܡܠܝܐ ܠܒܪ ܫܕܐ ܠܗ ܠܕܚܠܬܐ ܡܛܠ ܕܕܚܠܬܐ ܒܩܢܛܐ ܐܝܬܝܗ ܗܘ ܕܝܢ ܕܕܚܠ ܠܐ ܡܫܡܠܝ ܒܚܘܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 19, 'ܚܢܢ ܗܟܝܠ ܢܚܒ ܠܐܠܗܐ ܡܛܠ ܕܗܘ ܩܕܡܝܬ ܐܚܒܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 20, 'ܐܢ ܕܝܢ ܐܢܫ ܢܐܡܪ ܕܡܚܒ ܐܢܐ ܠܐܠܗܐ ܘܠܐܚܘܗܝ ܤܢܐ ܕܓܠܐ ܗܘ ܗܘ ܓܝܪ ܕܠܐܚܘܗܝ ܕܡܬܚܙܐ ܠܐ ܡܚܒ ܠܐܠܗܐ ܗܘ ܕܠܐ ܡܬܚܙܐ ܐܝܟܢܐ ܡܫܟܚ ܕܢܚܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 21, 'ܘܗܢܐ ܦܘܩܕܢܐ ܩܒܠܢ ܡܢܗ ܕܟܠ ܕܡܚܒ ܠܐܠܗܐ ܢܚܒ ܐܦ ܠܐܚܘܗܝ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 1, 'ܟܠ ܕܡܗܝܡܢ ܕܝܫܘܥ ܐܝܬܘܗܝ ܡܫܝܚܐ ܡܢ ܐܠܗܐ ܝܠܝܕ ܘܟܠ ܕܡܚܒ ܠܝܠܘܕܐ ܡܚܒ ܐܦ ܠܗܘ ܕܐܬܝܠܕ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 2, 'ܘܒܗܕܐ ܝܕܥܝܢܢ ܕܡܚܒܝܢܢ ܠܒܢܘܗܝ ܕܐܠܗܐ ܡܐ ܕܠܐܠܗܐ ܡܚܒܝܢܢ ܘܥܒܕܝܢܢ ܦܘܩܕܢܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 3, 'ܗܢܐ ܗܘ ܓܝܪ ܚܘܒܗ ܕܐܠܗܐ ܕܢܛܪ ܦܘܩܕܢܘܗܝ ܘܦܘܩܕܢܘܗܝ ܠܐ ܝܩܝܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 4, 'ܡܛܠ ܕܟܠ ܕܝܠܝܕ ܡܢ ܐܠܗܐ ܙܟܐ ܠܗ ܠܥܠܡܐ ܘܗܕܐ ܗܝ ܙܟܘܬܐ ܕܙܟܬܗ ܠܥܠܡܐ ܗܝܡܢܘܬܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 5, 'ܡܢܘ ܓܝܪ ܕܙܟܐ ܠܗ ܠܥܠܡܐ ܐܠܐ ܗܘ ܕܡܗܝܡܢ ܕܝܫܘܥ ܒܪܗ ܗܘ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 6, 'ܗܢܘ ܕܐܬܐ ܒܝܕ ܡܝܐ ܘܕܡܐ ܝܫܘܥ ܡܫܝܚܐ ܠܐ ܗܘܐ ܒܡܝܐ ܒܠܚܘܕ ܐܠܐ ܒܡܝܐ ܘܕܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 7, 'ܘܪܘܚܐ ܡܤܗܕܐ ܕܗܝ ܪܘܚܐ ܐܝܬܝܗ ܫܪܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 8, 'ܘܐܝܬܝܗܘܢ ܬܠܬܐ ܤܗܕܝܢ ܪܘܚܐ ܘܡܝܐ ܘܕܡܐ ܘܬܠܬܝܗܘܢ ܒܚܕ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 9, 'ܐܢ ܤܗܕܘܬܐ ܕܒܢܝܢܫܐ ܡܩܒܠܝܢܢ ܚܕ ܟܡܐ ܤܗܕܘܬܗ ܕܐܠܗܐ ܕܪܒܐ ܗܝ ܘܗܕܐ ܗܝ ܤܗܕܘܬܗ ܕܐܠܗܐ ܕܐܤܗܕ ܥܠ ܒܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 10, 'ܟܠܡܢ ܕܡܗܝܡܢ ܒܒܪܗ ܕܐܠܗܐ ܐܝܬ ܠܗ ܗܕܐ ܤܗܕܘܬܐ ܒܢܦܫܗ ܟܠ ܕܠܐ ܡܗܝܡܢ ܠܐܠܗܐ ܕܓܠܐ ܥܒܕܗ ܒܕܠܐ ܗܝܡܢ ܠܤܗܕܘܬܐ ܕܐܤܗܕ ܐܠܗܐ ܥܠ ܒܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 11, 'ܘܗܕܐ ܗܝ ܤܗܕܘܬܐ ܕܚܝܐ ܕܠܥܠܡ ܝܗܒ ܠܢ ܐܠܗܐ ܘܗܢܘܢ ܚܝܐ ܒܒܪܗ ܐܝܬܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 12, 'ܟܠ ܕܐܚܝܕ ܠܒܪܐ ܐܚܝܕ ܐܦ ܠܚܝܐ ܘܟܠ ܕܠܐ ܐܚܝܕ ܠܒܪܗ ܕܐܠܗܐ ܠܝܬ ܠܗ ܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 13, 'ܗܠܝܢ ܟܬܒܬ ܠܟܘܢ ܕܬܕܥܘܢ ܕܚܝܐ ܕܠܥܠܡ ܐܝܬ ܠܟܘܢ ܠܐܝܠܝܢ ܕܗܝܡܢܬܘܢ ܒܫܡܗ ܕܒܪܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 14, 'ܘܗܕܐ ܦܪܗܤܝܐ ܐܝܬ ܠܢ ܠܘܬܗ ܕܟܠ ܕܫܐܠܝܢܢ ܠܗ ܐܝܟ ܨܒܝܢܗ ܫܡܥ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 15, 'ܘܐܢ ܡܦܤܝܢܢ ܕܫܡܥ ܠܢ ܥܠ ܡܕܡ ܕܫܐܠܝܢܢ ܡܢܗ ܬܟܝܠܝܢܢ ܕܩܒܠܢ ܡܢ ܟܕܘ ܫܐܠܬܢ ܕܫܐܠܢ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 16, 'ܐܢ ܐܢܫ ܢܚܙܐ ܠܐܚܘܗܝ ܕܚܛܐ ܚܛܗܐ ܕܠܐ ܡܚܝܒ ܠܡܘܬܐ ܢܫܐܠ ܘܡܬܝܗܒܝܢ ܠܗ ܚܝܐ ܠܐܝܠܝܢ ܕܠܐ ܗܘܐ ܐܝܟ ܕܠܡܘܬܐ ܚܛܝܢ ܐܝܬ ܓܝܪ ܚܛܗܐ ܕܡܘܬܐ ܠܐ ܗܘܐ ܥܠ ܗܢܐ ܐܡܪ ܐܢܐ ܕܢܒܥܐ ܐܢܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 17, 'ܟܠ ܥܘܠܐ ܓܝܪ ܚܛܝܬܐ ܗܘ ܘܐܝܬ ܚܛܗܐ ܕܠܐ ܗܘܐ ܕܡܘܬܐ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 18, 'ܘܝܕܥܝܢܢ ܕܟܠ ܕܝܠܝܕ ܡܢ ܐܠܗܐ ܠܐ ܚܛܐ ܗܘ ܓܝܪ ܕܝܠܝܕ ܡܢ ܐܠܗܐ ܢܛܪ ܢܦܫܗ ܘܒܝܫܐ ܠܐ ܡܬܩܪܒ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 19, 'ܝܕܥܝܢܢ ܕܡܢ ܐܠܗܐ ܚܢܢ ܘܥܠܡܐ ܟܠܗ ܒܒܝܫܐ ܗܘ ܤܝܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 20, 'ܘܝܕܥܝܢܢ ܕܒܪܗ ܕܐܠܗܐ ܐܬܐ ܘܝܗܒ ܠܢ ܡܕܥܐ ܕܢܕܥ ܠܫܪܝܪܐ ܘܢܗܘܐ ܒܗ ܒܫܪܝܪܐ ܒܒܪܗ ܝܫܘܥ ܡܫܝܚܐ ܗܢܐ ܗܘ ܐܠܗܐ ܫܪܝܪܐ ܘܚܝܐ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 21, 'ܒܢܝ ܛܪܘ ܢܦܫܟܘܢ ܡܢ ܕܚܠܬ ܦܬܟܪܐ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 1, 'ܩܫܝܫܐ ܠܓܒܝܬܐ ܩܘܪܝܐ ܘܠܒܢܝܗ ܐܝܠܝܢ ܕܐܢܐ ܡܚܒ ܐܢܐ ܒܫܪܪܐ ܠܐ ܗܘܐ ܕܝܢ ܐܢܐ ܒܠܚܘܕܝ ܐܠܐ ܟܠܗܘܢ ܐܝܠܝܢ ܕܝܕܥܘܗܝ ܠܫܪܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 2, 'ܡܛܠ ܫܪܪܐ ܐܝܢܐ ܕܡܩܘܐ ܒܢ ܘܥܡܢ ܐܝܬܘܗܝ ܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 3, 'ܬܗܘܐ ܥܡܢ ܛܝܒܘܬܐ ܘܪܚܡܐ ܘܫܠܡܐ ܡܢ ܐܠܗܐ ܐܒܐ ܘܡܢ ܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܒܪܗ ܕܐܒܐ ܒܫܪܪܐ ܘܒܚܘܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 4, 'ܚܕܝܬ ܤܓܝ ܕܐܫܟܚܬ ܡܢ ܒܢܝܟܝ ܕܡܗܠܟܝܢ ܒܫܪܪܐ ܐܝܟܢܐ ܕܦܘܩܕܢܐ ܢܤܒܢܢ ܡܢ ܐܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 5, 'ܘܗܫܐ ܡܦܝܤ ܐܢܐ ܠܟܝ ܩܘܪܝܐ ܠܐ ܗܘܐ ܐܝܟ ܦܘܩܕܢܐ ܚܕܬܐ ܟܬܒ ܐܢܐ ܠܟܝ ܐܠܐ ܗܘ ܕܐܝܬ ܗܘܐ ܠܢ ܡܢ ܫܘܪܝܐ ܕܢܚܒ ܚܕ ܠܚܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 6, 'ܘܗܢܐ ܐܝܬܘܗܝ ܚܘܒܐ ܕܢܗܠܟ ܐܝܟ ܦܘܩܕܢܘܗܝ ܗܢܐ ܦܘܩܕܢܐ ܐܝܬܘܗܝ ܐܝܟ ܡܐ ܕܫܡܥܬܘܢ ܡܢ ܫܘܪܝܐ ܕܒܗ ܗܘܝܬܘܢ ܡܗܠܟܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 7, 'ܡܛܠ ܕܤܓܝܐܐ ܡܛܥܝܢܐ ܢܦܩܘ ܒܗ ܒܥܠܡܐ ܐܝܠܝܢ ܕܠܐ ܡܘܕܝܢ ܕܝܫܘܥ ܡܫܝܚܐ ܐܬܐ ܒܒܤܪ ܗܢܐ ܐܝܬܘܗܝ ܡܛܥܝܢܐ ܘܐܢܛܝܟܪܝܤܛܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 8, 'ܐܙܕܗܪܘ ܒܢܦܫܟܘܢ ܕܠܐ ܬܘܒܕܘܢ ܡܕܡ ܕܦܠܚܬܘܢ ܐܠܐ ܐܓܪܐ ܡܫܠܡܢܐ ܬܬܦܪܥܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 9, 'ܟܠ ܐܝܢܐ ܕܥܒܪ ܘܠܐ ܡܩܘܐ ܒܝܘܠܦܢܗ ܕܡܫܝܚܐ ܐܠܗܐ ܠܝܬ ܒܗ ܗܘ ܕܡܩܘܐ ܒܝܘܠܦܢܗ ܗܢܐ ܘܠܐܒܐ ܘܠܒܪܐ ܐܝܬ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 10, 'ܐܢ ܐܢܫ ܐܬܐ ܠܘܬܟܘܢ ܘܗܢܐ ܝܘܠܦܢܐ ܠܐ ܡܝܬܐ ܠܐ ܬܩܒܠܘܢܝܗܝ ܒܒܝܬܐ ܘܚܕܝ ܠܟ ܠܐ ܬܐܡܪܘܢ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 11, 'ܗܘ ܓܝܪ ܕܐܡܪ ܠܗ ܚܕܝ ܠܟ ܡܫܘܬܦ ܗܘ ܠܥܒܕܘܗܝ ܒܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 12, 'ܟܕ ܤܓܝܐܬܐ ܐܝܬ ܗܘܐ ܠܝ ܠܡܟܬܒ ܠܟܘܢ ܠܐ ܒܥܝܬ ܕܒܝܕ ܟܪܛܝܤܐ ܘܕܝܘܬܐ ܐܠܐ ܡܤܒܪ ܐܢܐ ܕܐܬܐ ܠܘܬܟܘܢ ܘܦܘܡܐ ܠܘܬ ܦܘܡܐ ܢܡܠܠ ܕܚܕܘܬܐ ܕܝܠܢ ܬܗܘܐ ܡܫܡܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 13, 'ܫܐܠܝܢ ܫܠܡܟܝ ܒܢܝܐ ܕܚܬܟܝ ܓܒܝܬܐ ܐܡܝܢ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 1, 'ܩܫܝܫܐ ܠܓܐܝܘܤ ܚܒܝܒ ܠܗܘ ܕܐܢܐ ܡܚܒ ܐܢܐ ܒܫܪܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 2, 'ܚܒܝܒܢ ܒܟܠܡܕܡ ܡܨܠܐ ܐܢܐ ܥܠܝܟ ܕܬܨܠܚ ܘܬܗܘܐ ܚܠܝܡ ܐܝܟ ܡܐ ܕܡܨܠܚܐ ܢܦܫܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 3, 'ܚܕܝܬ ܓܝܪ ܤܓܝ ܕܐܬܝܢ ܐܚܐ ܘܤܗܕܝܢ ܥܠ ܫܪܪܟ ܐܝܟ ܡܐ ܕܐܢܬ ܒܫܪܪܐ ܡܗܠܟ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 4, 'ܕܪܒܐ ܡܢ ܗܕܐ ܚܕܘܬܐ ܠܝܬ ܠܝ ܕܐܫܡܥ ܕܒܢܝܐ ܕܝܠܝ ܒܫܪܪܐ ܡܗܠܟܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 5, 'ܚܒܝܒܢ ܒܗܝܡܢܘܬܐ ܥܒܕ ܐܢܬ ܗܘ ܡܐ ܕܤܥܪ ܐܢܬ ܠܘܬ ܐܚܐ ܘܝܬܝܪܐܝܬ ܕܐܝܬܝܗܘܢ ܐܟܤܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 6, 'ܐܝܠܝܢ ܕܐܤܗܕܘ ܥܠ ܚܘܒܟ ܩܕܡ ܟܠܗ ܥܕܬܐ ܠܗܢܘܢ ܫܦܝܪ ܥܒܕ ܐܢܬ ܕܡܙܘܕ ܐܢܬ ܠܗܘܢ ܐܝܟ ܡܐ ܕܦܐܐ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 7, 'ܚܠܦ ܓܝܪ ܫܡܗ ܢܦܩܘ ܟܕ ܡܕܡ ܠܐ ܢܤܒܘ ܡܢ ܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 8, 'ܚܢܢ ܗܟܝܠ ܚܝܒܝܢܢ ܠܡܩܒܠܘ ܠܕܐܝܟ ܗܠܝܢ ܕܡܥܕܪܢܐ ܢܗܘܐ ܠܫܪܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 9, 'ܒܥܐ ܗܘܝܬ ܐܟܬܘܒ ܠܥܕܬܐ ܐܠܐ ܗܘ ܕܪܚܡ ܕܢܗܘܐ ܩܕܡܝܐ ܕܝܠܗܘܢ ܕܝܘܛܪܦܝܤ ܠܐ ܡܩܒܠ ܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 10, 'ܡܛܠ ܗܢܐ ܐܢ ܗܘ ܕܐܬܐ ܐܥܗܕ ܥܒܕܘܗܝ ܗܢܘܢ ܕܥܒܕ ܕܒܡܠܐ ܒܝܫܬܐ ܤܬܪ ܠܢ ܘܟܕ ܠܐ ܤܦܩ ܠܗ ܗܠܝܢ ܠܐ ܗܘ ܡܩܒܠ ܠܐܚܐ ܘܠܐܝܠܝܢ ܕܡܩܒܠܝܢ ܟܠܐ ܘܡܦܩ ܡܢ ܥܕܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 11, 'ܚܒܝܒܢ ܠܐ ܬܬܕܡܐ ܒܒܝܫܬܐ ܐܠܐ ܒܛܒܬܐ ܗܘ ܕܥܒܕ ܛܒܬܐ ܡܢ ܐܠܗܐ ܐܝܬܘܗܝ ܗܘ ܕܥܒܕ ܒܝܫܬܐ ܠܐ ܚܙܝܗܝ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 12, 'ܥܠ ܕܡܝܛܪܝܘܤ ܐܝܬ ܗܘ ܤܗܕܘܬܐ ܡܢ ܟܠܢܫ ܘܡܢܗ ܕܥܕܬܐ ܘܡܢܗ ܕܫܪܪܐ ܐܦ ܚܢܢ ܕܝܢ ܤܗܕܝܢܢ ܘܝܕܥ ܐܢܬ ܕܤܗܕܘܬܐ ܕܝܠܢ ܫܪܝܪܐ ܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 13, 'ܤܓܝܐܬܐ ܐܝܬ ܗܘܐ ܠܝ ܠܡܟܬܒ ܠܟ ܐܠܐ ܠܐ ܒܥܐ ܐܢܐ ܕܒܝܕ ܕܝܘܬܐ ܘܩܢܝܐ ܐܟܬܘܒ ܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 14, 'ܡܤܒܪ ܐܢܐ ܕܝܢ ܕܒܥܓܠ ܐܚܙܝܟ ܘܦܘܡܐ ܠܘܬ ܦܘܡܐ ܢܡܠܠܥ ܫܠܡܐ ܢܗܘܐ ܥܡܟ ܫܐܠܝܢ ܫܠܡܟ ܪܚܡܐ ܫܐܠ ܫܠܡܐ ܕܪܚܡܐ ܕܟܠܢܫ ܒܫܡܗ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 1, 'ܝܗܘܕܐ ܥܒܕܐ ܕܝܫܘܥ ܡܫܝܚܐ ܐܚܐ ܕܝܢ ܕܝܥܩܘܒ ܠܥܡܡܐ ܩܪܝܐ ܕܒܐܠܗܐ ܐܒܐ ܪܚܝܡܝܢ ܘܒܝܫܘܥ ܡܫܝܚܐ ܢܛܝܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 2, 'ܪܚܡܐ ܘܫܠܡܐ ܘܚܘܒܐ ܢܤܓܐ ܠܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 3, 'ܚܒܝܒܝ ܟܕ ܟܠܗ ܝܨܝܦܘܬܐ ܥܒܕ ܐܢܐ ܠܡܟܬܒ ܠܟܘܢ ܥܠ ܚܝܐ ܕܝܠܢ ܕܓܘܐ ܐܢܢܩܐ ܐܝܬ ܠܝ ܠܡܟܬܒ ܠܟܘܢ ܟܕ ܡܦܝܤ ܐܢܐ ܕܐܓܘܢܐ ܬܥܒܕܘܢ ܚܠܦ ܗܝܡܢܘܬܐ ܐܝܕܐ ܕܚܕܐ ܙܒܢ ܐܫܬܠܡܬ ܠܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 4, 'ܩܢܘ ܓܝܪ ܐܢܫܝܢ ܡܥܠܢܘܬܐ ܐܝܠܝܢ ܕܡܢ ܫܘܪܝܐ ܩܕܡܘ ܐܬܟܬܒܘ ܒܚܘܝܒܐ ܗܢܐ ܐܢܫܐ ܪܫܝܥܐ ܕܠܛܝܒܘܬܗ ܕܐܠܗܢ ܡܗܦܟܝܢ ܠܛܢܦܘܬܐ ܘܒܗܘ ܕܐܝܬܘܗܝ ܒܠܚܘܕܘܗܝ ܡܪܐ ܐܠܗܐ ܘܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܟܦܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 5, 'ܠܡܥܗܕܘܬܟܘܢ ܕܝܢ ܨܒܐ ܐܢܐ ܟܕ ܝܕܥܝܢ ܐܢܬܘܢ ܟܠܗܝܢ ܕܐܠܗܐ ܟܕ ܚܕܐ ܙܒܢ ܠܥܡܐ ܡܢ ܡܨܪܝܢ ܦܪܩ ܗܝ ܕܬܪܬܝܢ ܠܐܝܠܝܢ ܕܠܐ ܗܝܡܢܘ ܐܘܒܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 6, 'ܘܠܡܠܐܟܐ ܐܝܠܝܢ ܕܠܐ ܢܛܪܘ ܪܝܫܢܘܬܗܘܢ ܐܠܐ ܫܒܩܘ ܥܘܡܪܐ ܕܝܠܗܘܢ ܠܕܝܢܐ ܕܝܘܡܐ ܪܒܐ ܒܐܤܘܪܐ ܠܐ ܝܕܝܥܐ ܬܚܝܬ ܥܡܛܢܐ ܢܛܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 7, 'ܐܝܟܢܐ ܕܤܕܘܡ ܘܥܡܘܪܐ ܘܡܕܝܢܬܐ ܕܚܕܪܝܗܝܢ ܕܒܗ ܒܕܡܘܬܐ ܕܗܠܝܢ ܙܢܝ ܘܐܙܠ ܒܬܪ ܒܤܪܐ ܐܚܪܢܐ ܤܝܡܢ ܬܚܝܬ ܬܚܘܝܬܐ ܕܢܘܪܐ ܕܠܥܠܡ ܟܕ ܡܚܝܒܢ ܠܕܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 8, 'ܒܗ ܒܕܡܘܬܐ ܐܦ ܗܠܝܢ ܕܒܚܠܡܐ ܡܫܬܪܓܪܓܝܢ ܠܒܤܪܐ ܡܢ ܡܛܢܦܝܢ ܠܡܪܘܬܐ ܕܝܢ ܛܠܡܝܢ ܘܠܬܫܒܘܚܬܐ ܡܓܕܦܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 9, 'ܡܝܟܐܝܠ ܕܝܢ ܪܝܫ ܡܠܐܟܐ ܗܘ ܕܥܡ ܐܟܠܩܪܨܐ ܟܕ ܕܐܢ ܡܡܠܠ ܗܘܐ ܡܛܠ ܦܓܪܗ ܕܡܘܫܐ ܠܐ ܐܡܪܚ ܕܢܝܬܐ ܥܠܘܗܝ ܕܝܢܐ ܕܓܘܕܦܐ ܐܠܐ ܐܡܪ ܕܢܓܥܘܪ ܒܟ ܡܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 10, 'ܗܠܝܢ ܕܝܢ ܒܐܝܠܝܢ ܕܠܐ ܝܕܥܝܢ ܡܓܕܦܝܢ ܒܐܝܠܝܢ ܕܝܢ ܕܟܝܢܐܝܬ ܐܝܟ ܚܝܘܬܐ ܚܪܫܬܐ ܡܦܤܝܢ ܒܗܝܢ ܡܬܚܒܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 11, 'ܘܝ ܠܗܘܢ ܕܒܐܘܪܚܗ ܕܩܐܝܢ ܐܙܠܘ ܘܒܬܪ ܛܥܝܘܬܗ ܕܒܠܥܡ ܒܐܓܪܐ ܐܫܬܪܚܘ ܘܒܥܨܝܝܘܬܗ ܕܩܘܪܚ ܐܒܕܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 12, 'ܗܠܝܢ ܐܝܬܝܗܘܢ ܐܝܠܝܢ ܕܒܢܝܚܬܗܘܢ ܟܕ ܡܟܬܡܝܢ ܡܬܦܪܦܥܝܢ ܟܕ ܕܠܐ ܕܚܠܬܐ ܢܦܫܗܘܢ ܪܥܝܢ ܥܢܢܐ ܕܠܐ ܡܛܪܐ ܕܡܢ ܪܘܚܐ ܦܗܝܢ ܐܝܠܢܐ ܕܐܘܦܝ ܐܒܗܘܢ ܕܐܝܬܝܗܘܢ ܕܠܐ ܦܐܪܐ ܕܡܝܬܘ ܬܢܝܢܘܬ ܘܤܠܩܘ ܡܢ ܥܩܪܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 13, 'ܓܠܠܐ ܥܙܝܙܐ ܕܝܡܐ ܕܒܝܕ ܪܘܥܬܗܘܢ ܡܚܘܝܢ ܒܗܬܬܗܘܢ ܟܘܟܒܐ ܡܛܥܝܢܐ ܠܗܠܝܢ ܕܥܡܛܢܐ ܕܚܫܘܟܐ ܠܥܠܡ ܠܗܘܢ ܢܛܝܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 14, 'ܐܬܢܒܝ ܕܝܢ ܐܦ ܠܗܠܝܢ ܗܘ ܕܐܝܬܘܗܝ ܕܫܒܥܐ ܡܢ ܐܕܡ ܚܢܘܟ ܟܕ ܐܡܪ ܕܗܐ ܡܪܝܐ ܐܬܐ ܒܪܒܘܬܐ ܕܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 15, 'ܕܢܥܒܕ ܕܝܢܐ ܥܠ ܟܠ ܘܠܡܟܤܘ ܠܟܠ ܢܦܫܬܐ ܡܛܠ ܟܠܗܘܢ ܥܒܕܐ ܐܝܠܝܢ ܕܐܪܫܥܘ ܘܡܛܠ ܟܠܗܝܢ ܡܠܐ ܩܫܝܬܐ ܐܝܠܝܢ ܕܡܠܠܘ ܚܛܝܐ ܪܫܝܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 16, 'ܗܠܝܢ ܐܢܘܢ ܕܡܪܛܢܝܢ ܘܥܕܠܝܢ ܒܟܠ ܨܒܘܢ ܟܕ ܐܝܟ ܪܓܝܓܬܐ ܕܝܠܗܘܢ ܡܗܠܟܝܢ ܘܦܘܡܗܘܢ ܡܡܠܠ ܓܢܝܚܬܐ ܘܡܫܒܚܝܢ ܠܦܪܨܘܦܐ ܡܛܠ ܝܘܬܪܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 17, 'ܐܢܬܘܢ ܕܝܢ ܚܒܝܒܝ ܐܬܕܟܪܘ ܠܡܠܐ ܐܝܠܝܢ ܕܩܕܡ ܐܬܐܡܪ ܡܢ ܫܠܝܚܘܗܝ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 18, 'ܕܐܡܪܝܢ ܗܘܘ ܠܟܘܢ ܕܒܚܪܬܐ ܕܙܒܢܐ ܢܗܘܘܢ ܐܝܠܝܢ ܕܡܒܙܚܝܢ ܕܐܝܟ ܪܓܝܓܬܐ ܕܝܠܗܘܢ ܐܙܠܝܢ ܒܬܪ ܪܘܫܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 19, 'ܗܠܝܢ ܐܝܬܝܗܘܢ ܐܝܠܝܢ ܕܡܦܪܫܝܢ ܢܦܫܢܝܐ ܕܪܘܚܐ ܠܝܬ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 20, 'ܐܢܬܘܢ ܕܝܢ ܚܒܝܒܝ ܒܗܝܡܢܘܬܐ ܕܝܠܟܘܢ ܩܕܝܫܬܐ ܐܬܒܢܘ ܡܢ ܕܪܝܫ ܒܪܘܚܐ ܩܕܝܫܐ ܟܕ ܡܨܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 21, 'ܢܦܫܢ ܕܝܢ ܒܚܘܒܐ ܕܐܠܗܐ ܢܛܪ ܟܕ ܡܤܟܝܢܢ ܠܚܢܢܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܠܚܝܐ ܕܝܠܢ ܕܠܥܠܡ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 22, 'ܘܠܡܢܗܘܢ ܡܢ ܡܢ ܢܘܪܐ ܚܛܘܦܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 23, 'ܟܕ ܕܝܢ ܡܬܬܘܝܢ ܐܬܪܚܡܘ ܥܠܝܗܘܢ ܒܕܚܠܬܐ ܟܕ ܤܢܝܢ ܐܢܬܘܢ ܐܦ ܠܟܘܬܝܢܐ ܕܡܢ ܒܤܪܐ ܕܡܟܬܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 24, 'ܠܗܘ ܕܝܢ ܕܡܫܟܚ ܕܢܢܛܪ ܠܢ ܕܠܐ ܫܘܪܥܬܐ ܘܕܠܐ ܟܘܬܡܐ ܘܢܩܝܡ ܕܠܐ ܡܘܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 25, 'ܒܠܚܘܕܘܗܝ ܐܠܗܐ ܦܪܘܩܢ ܒܝܕ ܝܫܘܥ ܡܫܝܚܐ ܡܪܢ ܩܕܡ ܬܫܒܘܚܬܗ ܒܚܕܘܬܐ ܠܗ ܫܘܒܚܐ ܘܐܘܚܕܢܐ ܘܐܝܩܪܐ ܘܪܒܘܬܐ ܐܦ ܗܫܐ ܘܒܟܠܗܘܢ ܥܠܡܐ ܐܡܝܢ ܀   ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 1, 'ܓܠܝܢܐ ܕܝܫܘܥ ܡܫܝܚܐ ܕܝܗܒ ܠܗ ܐܠܗܐ ܠܡܚܘܝܘ ܠܥܒܕܘܗܝ ܡܐ ܕܝܗܝܒ ܠܡܗܘܐ ܒܥܓܠ ܘܫܘܕܥ ܟܕ ܫܠܚ ܒܝܕ ܡܠܐܟܗ ܠܥܒܕܗ ܝܘܚܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 2, 'ܗܘ ܕܐܤܗܕ ܠܡܠܬܐ ܕܐܠܗܐ ܘܠܤܗܕܘܬܗ ܕܝܫܘܥ ܡܫܝܚܐ ܟܠ ܡܐ ܕܚܙܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 3, 'ܛܘܒܘܗܝ ܠܡܢ ܕܩܪܐ ܘܠܐܝܠܝܢ ܕܫܡܥܝܢ ܡܠܐ ܕܢܒܝܘܬܐ ܗܕܐ ܘܢܛܪܝܢ ܐܝܠܝܢ ܕܟܬܝܒܢ ܒܗ ܙܒܢܐ ܓܝܪ ܩܪܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 4, 'ܝܘܚܢܢ ܠܫܒܥ ܥܕܬܐ ܕܒܐܤܝܐ ܛܝܒܘܬܐ ܠܟܘܢ ܘܫܠܡܐ ܡܢ ܗܘ ܕܐܝܬܘܗܝ ܘܐܝܬܘܗܝ ܗܘܐ ܘܐܬܐ ܘܡܢ ܫܒܥ ܪܘܚܐ ܐܝܠܝܢ ܕܩܕܡ ܟܘܪܤܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 5, 'ܘܡܢ ܝܫܘܥ ܡܫܝܚܐ ܤܗܕܐ ܡܗܝܡܢܐ ܒܘܟܪܐ ܕܡܝܬܐ ܘܪܫܐ ܕܡܠܟܐ ܕܐܪܥܐ ܗܘ ܕܡܚܒ ܠܢ ܘܫܪܐ ܠܢ ܡܢ ܚܛܗܝܢ ܒܕܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 6, 'ܘܥܒܕ ܠܢ ܡܠܟܘܬܐ ܟܗܢܝܬܐ ܠܐܠܗܐ ܘܐܒܘܗܝ ܘܠܗ ܬܫܒܘܚܬܐ ܘܐܘܚܕܢܐ ܠܥܠܡ ܥܠܡܝܢ ܐܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 7, 'ܗܐ ܐܬܐ ܥܡ ܥܢܢܐ ܘܢܚܙܝܢܝܗܝ ܟܠ ܥܝܢܐ ܘܐܦ ܐܝܠܝܢ ܕܕܩܪܘܗܝ ܘܢܪܩܕܢ ܥܠܘܗܝ ܟܠ ܫܪܒܬܐ ܕܐܪܥܐ ܐܝܢ ܘܐܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 8, 'ܐܢܐ ܐܠܦ ܘܬܘ ܐܡܪ ܡܪܝܐ ܐܠܗܐ ܗܘ ܕܐܝܬܘܗܝ ܘܐܝܬܘܗܝ ܗܘܐ ܘܐܬܐ ܗܘ ܕܐܚܝܕ ܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 9, 'ܐܢܐ ܝܘܚܢܢ ܗܘ ܐܚܘܟܘܢ ܘܒܪ ܫܘܬܦܟܘܢ ܒܐܘܠܨܢܐ ܘܒܡܤܝܒܪܢܘܬܐ ܕܒܝܫܘܥ ܗܘܝܬ ܒܓܙܪܬܐ ܕܡܬܩܪܝܐ ܦܛܡܘܤ ܡܛܠ ܡܠܬܐ ܕܐܠܗܐ ܘܡܛܠ ܤܗܕܘܬܐ ܕܝܫܘܥ ܡܫܝܚܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 10, 'ܘܗܘܝܬ ܒܪܘܚ ܒܝܘܡܐ ܕܚܕܒܫܒܐ ܘܫܡܥܬ ܡܢ ܒܤܬܪܝ ܩܠܐ ܪܒܐ ܐܝܟ ܫܝܦܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 11, 'ܕܐܡܪ ܐܝܠܝܢ ܕܚܙܝܬ ܟܬܘܒ ܒܟܬܒܐ ܘܫܕܪ ܠܫܒܥ ܥܕܬܐ ܠܐܦܤܘܤ ܘܠܙܡܘܪܢܐ ܘܠܦܪܓܡܘܤ ܘܠܬܐܘܛܝܪܐ ܘܠܤܪܕܝܤ ܘܠܦܝܠܕܠܦܝܐ ܘܠܠܕܝܩܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 12, 'ܘܗܦܟܬ ܠܡܕܥ ܩܠܐ ܐܝܢܐ ܕܡܠܠ ܥܡܝ ܘܟܕ ܥܛܦܬ ܚܙܝܬ ܫܒܥ ܡܢܪܢ ܕܕܗܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 13, 'ܘܒܡܨܥܬܐ ܕܡܢܪܬܐ ܐܝܟ ܕܡܘܬܐ ܕܒܪܢܫܐ ܘܠܒܝܫ ܐܦܘܕܐ ܘܐܤܝܪ ܨܝܕ ܬܕܘܗܝ ܐܤܪܐ ܕܕܗܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 14, 'ܪܫܗ ܕܝܢ ܘܤܥܪܗ ܚܘܪ ܐܝܟ ܥܡܪܐ ܘܐܝܟ ܬܠܓܐ ܘܥܝܢܘܗܝ ܐܝܟ ܫܠܗܒܝܬܐ ܕܢܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 15, 'ܘܪܓܠܘܗܝ ܒܕܡܘܬܐ ܕܢܚܫܐ ܠܒܢܝܐ ܕܡܚܡ ܒܐܬܘܢܐ ܘܩܠܗ ܐܝܟ ܩܠܐ ܕܡܝܐ ܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 16, 'ܘܐܝܬ ܠܗ ܒܐܝܕܗ ܕܝܡܝܢܐ ܫܒܥܐ ܟܘܟܒܝܢ ܘܡܢ ܦܘܡܗ ܢܦܩܐ ܪܘܡܚܐ ܚܪܝܦܬܐ ܘܚܙܬܗ ܐܝܟ ܫܡܫܐ ܡܚܘܝܐ ܒܚܝܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 17, 'ܘܟܕ ܚܙܝܬܗ ܢܦܠܬ ܥܠ ܪܓܠܘܗܝ ܐܝܟ ܡܝܬܐ ܘܤܡ ܥܠܝ ܐܝܕܗ ܕܝܡܝܢܐ ܠܡܐܡܪ ܠܐ ܬܕܚܠ ܕܐܢܐ ܐܝܬܝ ܩܕܡܝܐ ܘܐܚܪܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 18, 'ܘܕܚܝ ܘܕܡܝܬܐ ܗܘܝܬ ܘܗܐ ܚܝܐ ܐܝܬܝ ܠܥܠܡ ܥܠܡܝܢ ܐܡܝܢ ܘܐܝܬ ܠܝ ܩܠܝܕܐ ܕܡܘܬܐ ܘܕܫܝܘܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 19, 'ܟܬܘܒ ܗܟܝܠ ܡܐ ܕܚܙܝܬ ܘܐܝܠܝܢ ܕܐܝܬܝܗܝܢ ܘܥܬܝܕܢ ܠܡܗܘܐ ܒܬܪ ܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 20, 'ܐܪܙܐ ܕܫܒܥܐ ܟܘܟܒܝܢ ܐܝܠܝܢ ܕܚܙܝܬ ܥܠ ܝܡܝܢܝ ܘܫܒܥ ܡܢܪܬܐ ܫܒܥܐ ܟܘܟܒܝܢ ܡܠܐܟܐ ܕܫܒܥ ܥܕܬܐ ܐܝܬܝܗܘܢ ܘܡܢܪܬܐ ܫܒܥ ܕܕܗܒܐ ܐܝܠܝܢ ܕܚܙܝܬ ܫܒܥ ܐܢܝܢ ܥܕܬܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 1, 'ܘܠܡܠܐܟܐ ܕܒܥܕܬܐ ܕܐܦܤܘܤ ܟܬܘܒ ܗܟܢܐ ܐܡܪ ܗܘ ܕܐܚܝܕ ܫܒܥܐ ܟܘܟܒܝܢ ܒܐܝܕܗ ܗܘ ܕܡܗܠܟ ܒܝܢܬ ܡܢܪܬܐ ܕܕܗܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 2, 'ܝܕܥ ܐܢܐ ܥܒܕܝܟ ܘܥܡܠܟ ܘܡܤܝܒܪܢܘܬܟ ܘܕܠܐ ܡܨܝܬ ܠܡܛܥܢ ܠܒܝܫܐ ܘܢܤܝܬ ܠܐܝܠܝܢ ܕܐܡܪܝܢ ܢܦܫܗܘܢ ܕܫܠܝܚܐ ܐܝܬܝܗܘܢ ܘܠܝܬܝܗܘܢ ܘܐܫܟܚܬ ܐܢܘܢ ܕܓܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 3, 'ܘܡܤܝܒܪܢܘܬܐ ܐܝܬ ܠܟ ܘܛܥܢܬ ܡܛܠ ܫܡܝ ܘܠܐ ܠܐܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 4, 'ܐܠܐ ܐܝܬ ܠܝ ܥܠܝܟ ܕܚܘܒܟ ܩܕܡܝܐ ܫܒܩܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 5, 'ܐܬܕܟܪ ܡܢ ܐܝܟܐ ܢܦܩܬ ܘܥܒܕ ܥܒܕܐ ܩܕܡܝܐ ܘܐܢܕܝܢ ܠܐ ܐܬܐ ܐܢܐ ܥܠܝܟ ܘܡܙܝܥ ܐܢܐ ܡܢܪܬܟ ܐܠܐ ܬܬܘܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 6, 'ܐܠܐ ܗܕܐ ܐܝܬ ܠܟ ܕܤܢܝܬ ܥܒܕܐ ܕܢܐܩܘܠܝܛܐ ܐܝܠܝܢ ܕܐܢܐ ܤܢܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 7, 'ܗܘ ܕܐܝܬ ܠܗ ܐܕܢܐ ܢܫܡܥ ܡܢܐ ܪܘܚܐ ܡܡܠܠ ܠܥܕܬܐ ܘܠܕܙܟܐ ܐܬܠ ܡܢ ܩܝܤܐ ܕܚܝܐ ܠܡܐܟܠ ܗܘ ܕܐܝܬܘܗܝ ܒܦܪܕܝܤܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 8, 'ܘܠܡܠܐܟܐ ܕܥܕܬܐ ܕܙܡܘܪܢܐ ܟܬܘܒ ܗܟܢܐ ܐܡܪ ܩܕܡܝܐ ܘܐܚܪܝܐ ܗܘ ܕܗܘܐ ܡܝܬܐ ܘܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 9, 'ܝܕܥ ܐܢܐ ܐܘܠܨܢܟ ܘܡܤܟܢܘܬܟ ܐܠܐ ܥܬܝܪܐ ܐܢܬ ܘܠܓܘܕܦܐ ܕܡܢ ܐܝܠܝܢ ܕܐܡܪܝܢ ܢܦܫܗܘܢ ܝܗܘܕܝܐ ܝܗܘܕܝܐ ܟܕ ܠܐ ܐܝܬܝܗܘܢ ܐܠܐ ܟܢܘܫܬܐ ܕܤܛܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 10, 'ܒܡܕܡ ܠܐ ܬܕܚܠ ܡܢ ܐܝܠܝܢ ܕܥܬܝܕ ܐܢܬ ܠܡܚܫ ܗܐ ܥܬܝܕ ܐܟܠܩܪܨܐ ܕܢܪܡܐ ܡܢܟܘܢ ܒܒܝܬ ܚܒܘܫܝܐ ܕܬܬܢܤܘܢ ܘܢܗܘܐ ܠܟܘܢ ܐܘܠܨܢܐ ܝܘܡܝܢ ܥܤܪܐ ܗܘܘ ܡܗܝܡܢܐ ܥܕܡܐ ܠܡܘܬܐ ܘܐܬܠ ܠܟܘܢ ܟܠܝܠܐ ܕܚܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 11, 'ܕܐܝܬ ܠܗ ܐܕܢܐ ܢܫܡܥ ܡܢܐ ܪܘܚܐ ܡܡܠܠ ܠܥܕܬܐ ܕܐܝܢܐ ܕܙܟܐ ܠܐ ܢܗܪ ܡܢ ܡܘܬܐ ܬܢܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 12, 'ܘܠܡܠܐܟܐ ܕܒܥܕܬܐ ܕܦܪܓܡܐ ܟܬܘܒ ܗܟܢܐ ܐܡܪ ܗܘ ܕܐܝܬ ܠܗ ܚܪܒܐ ܚܪܝܦܬܐ ܕܬܪܝܢ ܦܘܡܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 13, 'ܝܕܥ ܐܢܐ ܐܝܟܐ ܥܡܪܬ ܐܬܪ ܕܟܘܪܤܝܗ ܕܤܛܢܐ ܘܐܚܝܕ ܐܢܬ ܒܫܡܝ ܘܒܗܝܡܢܘܬܝ ܠܐ ܟܦܪܬ ܘܒܝܘܡܬܐ ܐܬܚܪܝܬ ܘܤܗܕܐ ܕܝܠܝ ܡܗܝܡܢܐ ܡܛܠ ܕܟܠ ܤܗܕܐ ܕܝܠܝ ܡܗܝܡܢܐ ܐܝܢܐ ܕܡܢܟܘܢ ܐܬܩܛܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 14, 'ܐܠܐ ܐܝܬ ܠܝ ܥܠܝܟ ܙܥܘܪܝܬܐ ܕܐܝܬ ܠܟ ܬܡܢ ܕܐܚܝܕܝܢ ܡܠܦܢܘܬܐ ܕܒܠܥܡ ܗܘ ܕܐܠܦ ܠܒܠܩ ܕܢܪܡܐ ܟܫܠܐ ܩܕܡ ܒܢܝ ܐܝܤܪܝܠ ܠܡܐܟܠ ܕܒܚܝ ܦܬܟܪܐ ܘܠܡܙܢܝܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 15, 'ܗܟܢܐ ܐܝܬ ܐܦ ܠܟ ܕܐܚܝܕܝܢ ܝܘܠܦܢܐ ܕܢܐܩܘܠܝܛܐ ܗܟܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 16, 'ܬܘܒ ܗܟܝܠ ܘܐܢܕܝܢ ܠܐ ܐܬܐ ܐܢܐ ܥܠܝܟ ܡܚܕܐ ܘܐܩܪܒ ܥܡܗܘܢ ܒܚܪܒܐ ܕܦܘܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 17, 'ܘܕܐܝܬ ܠܗ ܐܕܢܐ ܢܫܡܥ ܡܢܐ ܪܘܚܐ ܡܡܠܠ ܠܥܕܬܐ ܕܠܕܙܟܐ ܐܬܠ ܡܢ ܡܢܢܐ ܗܘ ܕܡܛܫܝ ܘܐܬܠ ܠܗ ܚܘܫܒܢܐ ܚܘܪܐ ܘܥܠ ܚܘܫܒܢܐ ܫܡܐ ܚܕܬܐ ܕܟܬܒܐ ܕܠܐ ܐܢܫ ܝܕܥ ܐܠܐ ܗܘ ܕܢܤܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 18, 'ܘܠܡܠܐܟܐ ܕܒܥܕܬܐ ܕܒܬܐܘܛܝܪܐ ܟܬܘܒ ܗܟܢܐ ܐܡܪ ܒܪܗ ܕܐܠܗܐ ܗܘ ܕܐܝܬ ܠܗ ܥܝܢܐ ܐܝܟ ܫܠܗܒܝܬܐ ܕܢܘܪܐ ܘܪܓܠܘܗܝ ܐܝܟ ܢܚܫܐ ܠܒܢܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 19, 'ܝܕܥ ܐܢܐ ܥܒܕܝܟ ܘܚܘܒܟ ܘܗܝܡܢܘܬܟ ܘܬܫܡܫܬܟ ܘܡܤܝܒܪܢܘܬܟ ܘܥܒܕܝܟ ܐܚܪܝܐ ܤܓܝܐܐ ܐܢܘܢ ܡܢ ܩܕܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 20, 'ܐܠܐ ܐܝܬ ܠܝ ܥܠܝܟ ܤܓܝ ܕܫܒܩܬ ܠܐܢܬܬܟ ܐܝܙܒܠ ܗܝ ܕܐܡܪܐ ܥܠ ܢܦܫܗ ܕܢܒܝܬܐ ܗܝ ܘܡܠܦܐ ܘܡܛܥܝܐ ܠܥܒܕܝ ܠܡܙܢܝܘ ܘܡܐܟܠ ܕܒܚܝ ܦܬܟܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 21, 'ܘܝܗܒܬ ܠܗ ܙܒܢܐ ܠܬܝܒܘܬܐ ܘܠܐ ܨܒܝܐ ܠܡܬܒ ܡܢ ܙܢܝܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 22, 'ܗܐ ܪܡܐ ܐܢܐ ܠܗ ܒܥܪܤܐ ܘܠܐܝܠܝܢ ܕܓܝܪܝܢ ܥܡܗ ܒܐܘܠܨܢܐ ܪܒܐ ܐܠܐ ܢܬܬܘܘܢ ܡܢ ܥܒܕܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 23, 'ܘܠܒܢܝܗ ܐܩܛܘܠ ܒܡܘܬܐ ܘܝܕܥܢ ܟܠܗܝܢ ܥܕܬܐ ܕܐܢܐ ܐܢܐ ܒܨܐ ܟܘܠܝܬܐ ܘܠܒܐ ܘܐܬܠ ܠܟܘܢ ܠܟܠܢܫ ܐܝܟ ܥܒܕܝܟܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 24, 'ܠܟܘܢ ܐܡܪ ܐܢܐ ܠܫܪܟܐ ܕܒܬܐܘܛܝܪܐ ܟܠܗܘܢ ܐܝܠܝܢ ܕܠܝܬ ܠܗܘܢ ܝܘܠܦܢܐ ܗܢܐ ܐܝܠܝܢ ܕܠܐ ܝܕܥܘ ܥܡܝܩܬܗ ܕܤܛܢܐ ܐܝܟ ܕܐܡܪܝܢ ܠܐ ܐܪܡܐ ܥܠܝܟܘܢ ܝܘܩܪܐ ܐܚܪܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 25, 'ܗܘ ܗܟܝܠ ܕܐܝܬ ܠܟܘܢ ܐܚܘܕܘ ܥܕܡܐ ܕܐܬܐ ܐܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 26, 'ܘܕܙܟܐ ܘܢܛܪ ܥܒܕܝ ܐܬܠ ܠܗ ܫܘܠܛܢܐ ܥܠ ܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 27, 'ܠܡܪܥܐ ܐܢܘܢ ܒܫܒܛܐ ܕܦܪܙܠܐ ܘܐܝܟ ܡܐܢܝ ܦܚܪܐ ܬܫܚܩܘܢ ܗܟܢܐ ܓܝܪ ܘܐܢܐ ܢܤܒܬ ܡܢ ܐܒܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 28, 'ܘܐܬܠ ܠܗ ܠܟܘܟܒ ܨܦܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 29, 'ܕܐܝܬ ܠܗ ܐܕܢܐ ܢܫܡܥ ܡܢܐ ܪܘܚܐ ܡܡܠܠ ܠܥܕܬܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 1, 'ܘܠܡܠܐܟܐ ܕܒܥܕܬܐ ܕܤܪܕܝܤ ܟܬܘܒ ܗܟܢܐ ܐܡܪ ܗܘ ܕܐܝܬ ܠܗ ܫܒܥ ܪܘܚܝܢ ܕܐܠܗܐ ܘܫܒܥܐ ܟܘܟܒܐ ܝܕܥ ܐܢܐ ܥܒܕܝܟ ܘܫܡܐ ܕܐܝܬ ܠܟ ܘܕܚܝܐ ܐܢܬ ܘܕܡܝܬܐ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 2, 'ܘܗܘܝ ܥܝܪܐ ܘܩܝܡ ܕܫܪܟܐ ܕܐܝܠܝܢ ܕܥܬܝܕ ܗܘܝܬ ܠܡܡܬ ܠܐ ܓܝܪ ܐܫܟܚܬܟ ܕܡܫܡܠܝܢ ܥܒܕܝܟ ܩܕܡ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 3, 'ܐܬܕܟܪ ܐܝܟܢ ܫܡܥܬ ܘܢܤܒܬ ܐܙܕܗܪ ܘܬܘܒ ܘܐܢܕܝܢ ܠܐ ܬܬܥܝܪ ܐܬܐ ܐܢܐ ܥܠܝܟ ܐܝܟ ܓܢܒܐ ܘܠܐ ܬܕܥ ܐܝܕܐ ܫܥܬܐ ܐܬܐ ܥܠܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 4, 'ܐܠܐ ܐܝܬ ܠܝ ܩܠܝܠ ܫܡܗܐ ܒܤܪܕܝܤ ܐܝܠܝܢ ܕܠܐ ܛܘܫܘ ܡܐܢܝܗܘܢ ܘܡܗܠܟܝܢ ܩܕܡܝ ܒܚܘܪܐ ܘܫܘܝܢ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 5, 'ܕܙܟܐ ܗܟܢܐ ܡܬܥܛܦ ܡܐܢܐ ܚܘܪܐ ܘܠܐ ܐܠܚܐ ܫܡܗ ܡܢ ܤܦܪܐ ܕܚܝܐ ܘܐܘܕܐ ܒܫܡܗ ܩܕܡ ܐܒܝ ܘܩܕܡ ܡܠܐܟܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 6, 'ܐܝܢܐ ܕܐܝܬ ܠܗ ܐܕܢܐ ܢܫܡܥ ܡܢܐ ܪܘܚܐ ܡܡܠܠ ܠܥܕܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 7, 'ܘܠܡܠܐܟܐ ܕܥܕܬܐ ܕܦܝܠܕܠܦܝܐ ܟܬܘܒ ܗܟܢܐ ܐܡܪ ܩܕܝܫܐ ܫܪܝܪܐ ܗܘ ܕܐܝܬ ܠܗ ܩܠܝܕܐ ܕܕܘܝܕ ܐܝܢܐ ܕܦܬܚ ܘܠܝܬ ܕܐܚܕ ܘܐܚܕ ܘܠܝܬ ܕܦܬܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 8, 'ܝܕܥ ܐܢܐ ܥܒܕܝܟ ܘܗܐ ܝܗܒܬ ܩܕܡܝܟ ܬܪܥܐ ܦܬܝܚܐ ܐܝܢܐ ܕܠܐ ܐܢܫ ܡܨܐ ܠܡܐܚܕܗ ܡܛܠ ܕܩܠܝܠ ܚܝܠܐ ܐܝܬ ܠܟ ܘܡܠܬܝ ܢܛܪܬ ܘܒܫܡܝ ܠܐ ܟܦܪܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 9, 'ܘܗܐ ܝܗܒ ܐܢܐ ܡܢ ܟܢܘܫܬܐ ܕܤܛܢܐ ܡܢ ܐܝܠܝܢ ܕܐܡܪܝܢ ܥܠ ܢܦܫܗܘܢ ܕܝܗܘܕܝܐ ܐܢܘܢ ܘܠܐ ܐܝܬܝܗܘܢ ܐܠܐ ܡܕܓܠܝܢ ܗܐ ܐܥܒܕ ܠܗܘܢ ܕܢܐܬܘܢ ܘܢܤܓܕܘܢ ܩܕܡ ܪܓܠܝܟ ܘܢܕܥܘܢ ܕܐܢܐ ܐܚܒܬܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 10, 'ܥܠ ܕܢܛܪܬ ܡܠܬܐ ܕܡܤܝܒܪܢܘܬܝ ܘܐܢܐ ܐܛܪܟ ܡܢ ܢܤܝܘܢܐ ܕܥܬܝܕ ܕܢܐܬܐ ܥܠ ܟܠܗ ܬܐܒܝܠ ܕܢܢܤܐ ܠܥܡܘܪܝܗ ܕܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 11, 'ܐܬܐ ܐܢܐ ܡܚܕܐ ܐܚܘܕ ܗܘ ܡܐ ܕܐܝܬ ܠܟ ܕܠܐ ܐܢܫ ܢܤܒ ܟܠܝܠܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 12, 'ܘܕܙܟܐ ܐܥܒܕܗ ܥܡܘܕܐ ܒܗܝܟܠܐ ܕܐܠܗܐ ܘܠܒܪ ܠܐ ܢܦܘܩ ܬܘܒ ܘܐܟܬܘܒ ܥܠܘܗܝ ܫܡܐ ܕܐܠܗܝ ܘܫܡܐ ܕܡܕܝܢܬܐ ܚܕܬܐ ܐܘܪܫܠܡ ܐܝܕܐ ܕܢܚܬܐ ܡܢ ܐܠܗܝ ܘܫܡܐ ܕܝܠܝ ܚܕܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 13, 'ܘܕܐܝܬ ܠܗ ܐܕܢܐ ܢܫܡܥ ܡܢܐ ܪܘܚܐ ܡܡܠܠ ܠܥܕܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 14, 'ܘܠܡܠܐܟܐ ܕܥܕܬܐ ܕܠܐܝܕܝܩܝܐ ܟܬܘܒ ܗܟܢܐ ܐܡܪ ܐܡܝܢ ܤܗܕܐ ܡܗܝܡܢܐ ܘܫܪܝܪܐ ܘܪܫܝܬܐ ܕܒܪܝܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 15, 'ܝܕܥ ܐܢܐ ܥܒܕܝܟ ܠܐ ܩܪܝܪܐ ܐܢܬ ܘܠܐ ܚܡܝܡܐ ܕܘܠܐ ܗܘܐ ܕܐܘ ܩܪܝܪܐ ܬܗܘܐ ܐܘ ܚܡܝܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 16, 'ܘܐܝܬܝܟ ܦܫܘܪܐ ܘܠܐ ܩܪܝܪܐ ܘܠܐ ܚܡܝܡܐ ܥܬܝܕ ܐܢܐ ܠܡܬܒܘܬܟ ܡܢ ܦܘܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 17, 'ܡܛܠ ܕܐܡܪܬ ܕܥܬܝܪܐ ܐܢܬ ܘܥܬܪܬ ܘܥܠ ܡܕܡ ܠܐ ܤܢܝܩ ܐܢܐ ܘܠܐ ܝܕܥ ܐܢܬ ܕܐܢܬ ܗܘ ܡܚܝܠܐ ܘܕܘܝܐ ܘܡܤܟܢܐ ܘܥܪܛܠܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 18, 'ܡܠܟ ܐܢܐ ܠܟ ܕܬܙܒܢ ܡܢܝ ܕܗܒܐ ܕܒܚܝܪ ܡܢ ܢܘܪܐ ܕܬܥܬܪ ܘܡܐܢܐ ܚܘܪܐ ܠܡܬܥܛܦܘ ܘܠܐ ܬܬܓܠܐ ܒܗܬܬܐ ܕܥܪܛܠܝܘܬܟ ܘܫܝܦܐ ܟܚܘܠ ܕܬܚܙܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 19, 'ܐܢܐ ܠܐܝܠܝܢ ܕܪܚܡ ܐܢܐ ܡܟܤ ܐܢܐ ܘܪܕܐ ܐܢܐ ܛܢ ܗܟܝܠ ܘܬܘܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 20, 'ܗܐ ܩܡܬ ܥܠ ܬܪܥܐ ܘܐܩܘܫ ܐܢ ܐܢܫ ܫܡܥ ܒܩܠܝ ܘܢܦܬܚ ܬܪܥܐ ܘܐܥܘܠ ܘܐܚܫܡ ܥܡܗ ܘܗܘ ܥܡܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 21, 'ܘܕܙܟܐ ܐܬܠ ܠܗ ܠܡܬܒ ܥܡܝ ܥܠ ܟܘܪܤܝܐ ܕܝܠܝ ܐܝܟܢܐ ܕܐܢܐ ܙܟܝܬ ܘܝܬܒܬ ܥܡ ܐܒܝ ܥܠ ܟܘܪܤܝܐ ܕܝܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 22, 'ܡܢ ܕܐܝܬ ܠܗ ܐܕܢܐ ܢܫܡܥ ܡܢܐ ܪܘܚܐ ܡܡܠܠ ܠܥܕܬܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 1, 'ܡܢ ܒܬܪ ܗܠܝܢ ܚܙܝܬ ܘܗܐ ܬܪܥܐ ܦܬܝܚܐ ܒܫܡܝܐ ܘܩܠܐ ܗܘ ܕܫܡܥܬ ܐܝܟ ܫܝܦܘܪܐ ܡܠܠ ܥܡܝ ܠܡܐܡܪ ܤܩ ܠܗܪܟܐ ܘܐܚܘܝܟ ܡܐ ܕܝܗܝܒ ܠܡܗܘܐ ܒܬܪ ܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 2, 'ܘܡܚܕܐ ܗܘܝܬ ܒܪܘܚ ܘܗܐ ܟܘܪܤܝܐ ܤܝܡ ܒܫܡܝܐ ܘܥܠ ܟܘܪܤܝܐ ܝܬܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 3, 'ܘܕܝܬܒ ܐܝܟ ܕܡܘܬܐ ܕܚܙܘܐ ܕܟܐܦܐ ܕܝܫܦܗ ܘܕܤܪܕܘܢ ܘܩܫܬܐ ܕܥܢܢܐ ܕܚܕܪܘܗܝ ܕܟܘܪܤܝܐ ܕܡܘܬ ܚܙܘܐ ܕܙܡܪܓܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 4, 'ܘܚܕܪ ܟܘܪܤܝܐ ܟܘܪܤܘܬܐ ܥܤܪܝܢ ܘܐܪܒܥܐ ܘܥܠܝܗܘܢ ܕܝܢ ܕܟܘܪܤܘܬܐ ܥܤܪܝܢ ܘܐܪܒܥܐ ܩܫܝܫܝܢ ܕܝܬܒܝܢ ܕܥܛܝܦܝܢ ܡܐܢܐ ܚܘܪܐ ܘܥܠ ܩܪܩܦܬܗܘܢ ܟܠܝܠܐ ܕܕܗܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 5, 'ܘܡܢ ܟܘܪܤܘܬܐ ܢܦܩܝܢ ܪܥܡܐ ܘܒܪܩܐ ܘܩܠܐ ܘܫܒܥܐ ܢܗܝܪܐ ܕܝܩܕܝܢ ܩܕܡܘܗܝ ܕܟܘܪܤܝܐ ܐܝܠܝܢ ܕܐܝܬܝܗܘܢ ܫܒܥ ܪܘܚܝܢ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 6, 'ܘܩܕܡ ܟܘܪܤܝܐ ܝܡܐ ܕܙܓܘܓܝܬܐ ܐܝܟ ܕܘܡܝܐ ܕܓܠܝܕܐ ܘܒܡܨܥܬ ܟܘܪܤܝܐ ܘܚܕܪܘܗܝ ܕܟܘܪܤܝܐ ܐܪܒܥ ܚܝܘܢ ܕܡܠܝܢ ܥܝܢܐ ܡܢ ܩܕܡܝܗܝܢ ܘܡܢ ܒܤܬܪܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 7, 'ܚܝܘܬܐ ܩܕܡܝܬܐ ܕܡܝܐ ܠܐܪܝܐ ܘܚܝܘܬܐ ܕܬܪܬܝܢ ܕܡܘܬܐ ܕܥܓܠܐ ܘܚܝܘܬܐ ܕܬܠܬ ܐܝܬ ܠܗ ܐܦܐ ܐܝܟ ܕܒܪܢܫܐ ܘܚܝܘܬܐ ܕܐܪܒܥ ܕܡܘܬܐ ܕܢܫܪܐ ܕܦܪܚ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 8, 'ܐܪܒܥܬܝܗܝܢ ܚܝܘܬܐ ܟܠܚܕܐ ܡܢܗܝܢ ܩܝܡܐ ܘܐܝܬ ܠܗ ܡܢ ܛܦܪܝܗ ܘܠܥܠ ܫܬܐ ܓܦܝܢ ܚܘܕܪܢܐܝܬ ܘܡܢ ܠܓܘ ܡܠܝܢ ܥܝܢܐ ܘܫܠܝܐ ܠܝܬ ܠܗܝܢ ܐܝܡܡܐ ܘܠܠܝܐ ܠܡܐܡܪ ܩܕܝܫ ܩܕܝܫ ܩܕܝܫ ܡܪܝܐ ܐܠܗܐ ܐܚܝܕ ܟܠ ܗܘ ܕܐܝܬܘܗܝ ܗܘܐ ܘܐܝܬܘܗܝ ܘܐܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 9, 'ܘܡܐ ܕܝܗܒ ܐܪܒܥܬܝܗܝܢ ܚܝܘܬܐ ܬܫܒܘܚܬܐ ܘܐܝܩܪܐ ܘܩܘܒܠ ܛܝܒܘܬܐ ܠܕܝܬܒ ܥܠ ܟܘܪܤܝܐ ܘܠܕܚܝ ܠܥܠܡ ܥܠܡܝܢ ܐܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 10, 'ܢܦܠܘܢ ܥܤܪܝܢ ܘܐܪܒܥܐ ܩܫܝܫܝܢ ܩܕܡ ܡܢ ܕܝܬܒ ܥܠ ܟܘܪܤܝܐ ܘܢܤܓܕܘܢ ܠܥܠܡ ܥܠܡܝܢ ܐܡܝܢ ܠܡܢ ܕܚܝ ܘܢܪܡܘܢ ܟܠܝܠܝܗܘܢ ܩܕܡ ܟܘܪܤܝܐ ܟܕ ܐܡܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 11, 'ܕܫܘܝܬ ܗܘ ܡܪܢ ܘܐܠܗܢ ܠܡܤܒ ܬܫܒܘܚܬܐ ܘܐܝܩܪܐ ܘܚܝܠܐ ܡܛܠ ܕܐܢܬ ܒܪܝܬ ܟܠ ܘܒܝܕ ܨܒܝܢܟ ܗܘܝ ܘܐܬܒܪܝ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 1, 'ܘܚܙܝܬ ܥܠ ܝܡܝܢܗ ܕܗܘ ܕܝܬܒ ܥܠ ܟܘܪܤܝܐ ܟܬܒܐ ܕܪܫܝܡ ܡܢ ܠܓܘ ܘܡܢ ܠܒܪ ܘܛܒܝܥ ܛܒܥܐ ܫܒܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 2, 'ܘܚܙܝܬ ܐܚܪܢܐ ܡܠܐܟܐ ܚܝܠܬܢܐ ܕܡܟܪܙ ܒܩܠܐ ܪܡܐ ܡܢ ܫܘܐ ܠܡܦܬܚ ܟܬܒܐ ܘܠܡܫܪܐ ܛܒܥܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 3, 'ܘܠܝܬ ܕܐܬܡܨܝ ܒܫܡܝܐ ܘܠܐ ܒܐܪܥܐ ܘܠܐ ܕܠܬܚܬ ܡܢ ܐܪܥܐ ܠܡܦܬܚ ܠܟܬܒܐ ܘܠܡܫܪܐ ܛܒܥܘܗܝ ܘܠܡܚܙܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 4, 'ܘܒܟܐ ܗܘܝܬ ܤܓܝ ܡܛܠ ܕܠܝܬ ܕܐܫܬܟܚ ܕܫܘܐ ܠܡܦܬܚ ܠܟܬܒܐ ܘܠܡܫܪܐ ܛܒܥܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 5, 'ܘܚܕ ܡܢ ܩܫܝܫܐ ܐܡܪ ܠܝ ܠܐ ܬܒܟܐ ܗܐ ܙܟܐ ܐܪܝܐ ܡܢ ܫܒܛܐ ܕܝܗܘܕܐ ܥܩܪܐ ܕܕܘܝܕ ܢܦܬܚ ܟܬܒܐ ܘܛܒܥܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 6, 'ܘܚܙܝܬ ܒܡܨܥܬ ܟܘܪܤܝܐ ܘܕܐܪܒܥ ܚܝܘܢ ܘܕܩܫܝܫܐ ܐܡܪܐ ܕܩܐܡ ܐܝܟ ܢܟܝܤܐ ܘܐܝܬ ܠܗ ܩܪܢܬܐ ܫܒܥ ܘܥܝܢܐ ܫܒܥ ܐܝܠܝܢ ܕܐܝܬܝܗܝܢ ܫܒܥ ܪܘܚܐ ܕܐܠܗܐ ܕܡܫܬܕܪܢ ܠܟܠܗ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 7, 'ܘܐܬܐ ܘܢܤܒ ܟܬܒܐ ܡܢ ܐܝܕܗ ܕܗܘ ܕܝܬܒ ܥܠ ܟܘܪܤܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 8, 'ܘܟܕ ܫܩܠܗ ܠܟܬܒܐ ܐܪܒܥ ܚܝܘܢ ܘܥܤܪܝܢ ܘܐܪܒܥܐ ܩܫܝܫܝܢ ܢܦܠܘ ܩܕܡܘܗܝ ܕܐܡܪܐ ܟܕ ܐܝܬ ܠܟܠܚܕ ܚܕ ܡܢܗܘܢ ܩܝܬܪܐ ܘܙܒܘܪܐ ܕܕܗܒܐ ܕܡܠܝܐ ܒܤܡܐ ܐܝܠܝܢ ܕܐܝܬܝܗܝܢ ܨܠܘܬܐ ܕܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 9, 'ܕܡܫܒܚܝܢ ܬܫܒܘܚܬܐ ܚܕܬܐ ܘܐܡܪܝܢ ܫܘܝܬ ܗܘ ܠܡܤܒܝܘܗܝ ܠܟܬܒܐ ܘܠܡܫܪܐ ܛܒܥܘܗܝ ܥܠ ܕܐܬܢܟܤܬ ܘܙܒܢܬܢ ܒܕܡܟ ܠܐܠܗܐ ܡܢ ܟܠ ܫܪܒܬܐ ܘܥܡܡܐ ܘܐܡܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 10, 'ܘܥܒܕܬ ܐܢܘܢ ܠܐܠܗܢ ܡܠܟܘܬܐ ܘܟܗܢܐ ܘܡܠܟܐ ܘܢܡܠܟܘܢ ܥܠ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 11, 'ܘܚܙܝܬ ܘܫܡܥܬ ܐܝܟ ܩܠܐ ܕܡܠܐܟܐ ܤܓܝܐܐ ܚܕܪܝ ܟܘܪܤܝܐ ܘܕܚܝܘܬܐ ܘܕܩܫܝܫܐ ܘܐܝܬܘܗܝ ܗܘܐ ܡܢܝܢܗܘܢ ܪܒܘ ܪܒܘܢ ܘܐܠܦ ܐܠܦܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 12, 'ܘܐܡܪܝܢ ܒܩܠܐ ܪܡܐ ܫܘܝܬ ܗܘ ܐܡܪܐ ܢܟܝܤܐ ܠܡܤܒ ܚܝܠܐ ܘܥܘܬܪܐ ܘܚܟܡܬܐ ܘܥܘܫܢܐ ܘܐܝܩܪܐ ܘܬܫܒܘܚܬܐ ܘܒܘܪܟܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 13, 'ܘܟܠ ܒܪܝܬܐ ܕܒܫܡܝܐ ܘܒܐܪܥܐ ܘܕܠܬܚܬ ܡܢ ܐܪܥܐ ܘܕܒܝܡܐ ܐܝܬܝܗ ܘܟܠ ܕܒܗܘܢ ܘܫܡܥܬ ܕܐܡܪܝܢ ܠܕܝܬܒ ܥܠ ܟܘܪܤܝܐ ܘܠܐܡܪܐ ܕܒܘܪܟܬܐ ܘܐܝܩܪܐ ܘܬܫܒܘܚܬܐ ܘܐܘܚܕܢܐ ܠܥܠܡ ܥܠܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 14, 'ܘܐܪܒܥ ܚܝܘܢ ܕܐܡܪܢ ܐܡܝܢ ܘܩܫܝܫܐ ܢܦܠܘ ܘܤܓܕܘ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 1, 'ܘܚܙܝܬ ܟܕ ܦܬܚ ܐܡܪܐ ܚܕ ܡܢ ܫܒܥܐ ܛܒܥܝܢ ܘܫܡܥܬ ܠܚܕܐ ܡܢ ܐܪܒܥ ܚܝܘܢ ܕܐܡܪܐ ܐܝܟ ܩܠܐ ܕܪܥܡܐ ܬܐ ܘܚܙܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 2, 'ܘܫܡܥܬ ܘܚܙܝܬ ܘܗܐ ܤܘܤܝܐ ܚܘܪܐ ܘܕܝܬܒ ܥܠܘܗܝ ܐܝܬ ܠܗ ܩܫܬܐ ܘܐܬܝܗܒ ܠܗ ܟܠܝܠܐ ܘܢܦܩ ܙܟܝ ܘܙܟܐ ܘܕܢܙܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 3, 'ܘܟܕ ܦܬܚ ܛܒܥܐ ܕܬܪܝܢ ܫܡܥܬ ܠܚܝܘܬܐ ܕܬܪܬܝܢ ܕܐܡܪܐ ܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 4, 'ܘܢܦܩ ܤܘܤܝܐ ܤܘܡܩܐ ܘܠܕܝܬܒ ܥܠܘܗܝ ܐܬܝܗܒ ܠܗ ܠܡܤܒ ܫܠܡܐ ܡܢ ܐܪܥܐ ܕܠܚܕܕܐ ܢܢܟܤܘܢ ܘܐܬܝܗܒܬ ܠܗ ܚܪܒܐ ܪܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 5, 'ܘܟܕ ܐܬܦܬܚ ܛܒܥܐ ܕܬܠܬܐ ܫܡܥܬ ܠܚܝܘܬܐ ܕܬܠܬ ܕܐܡܪܐ ܬܐ ܘܗܐ ܤܘܤܝܐ ܐܘܟܡܐ ܘܕܝܬܒ ܥܠܘܗܝ ܐܝܬ ܡܐܤܬܐ ܒܐܝܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 6, 'ܘܫܡܥܬ ܩܠܐ ܡܢ ܒܝܬ ܚܝܘܬܐ ܕܐܡܪ ܩܒܐ ܕܚܛܐ ܒܕܝܢܪܐ ܘܬܠܬܐ ܩܒܝܢ ܕܤܥܪܐ ܒܕܝܢܪܐ ܘܠܚܡܪܐ ܘܠܡܫܚܐ ܠܐ ܬܗܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 7, 'ܘܟܕ ܦܬܚ ܛܒܥܐ ܕܐܪܒܥܐ ܫܡܥܬ ܩܠܐ ܕܚܝܘܬܐ ܕܐܡܪܐ ܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 8, 'ܘܚܙܝܬ ܤܘܤܝܐ ܝܘܪܩܐ ܘܫܡܗ ܕܗܘ ܕܝܬܒ ܥܠܘܗܝ ܡܘܬܐ ܘܫܝܘܠ ܢܩܝܦܐ ܠܗ ܘܐܬܝܗܒ ܠܗ ܫܘܠܛܢܐ ܥܠ ܪܘܒܥܗ ܕܐܪܥܐ ܕܢܩܛܠ ܒܚܪܒܐ ܘܒܟܦܢܐ ܘܒܡܘܬܐ ܘܒܚܝܘܬܐ ܕܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 9, 'ܘܟܕ ܦܬܚ ܠܛܒܥܐ ܕܚܡܫܐ ܚܙܝܬ ܠܬܚܬ ܡܢ ܡܕܒܚܐ ܠܢܦܫܬܐ ܕܐܬܩܛܠ ܡܛܠ ܡܠܬܐ ܕܐܠܗܐ ܘܡܛܠ ܤܗܕܘܬܐ ܕܝܫܘܥ ܗܝ ܕܐܝܬ ܗܘܐ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 10, 'ܘܩܥܘ ܒܩܠܐ ܪܒܐ ܘܐܡܪܝܢ ܥܕܡܐ ܠܐܡܬܝ ܡܪܝܐ ܩܕܝܫܐ ܘܫܪܝܪܐ ܠܐ ܕܝܢܬ ܘܬܒܥܬ ܕܡܢ ܡܢ ܥܡܘܪܝܗ ܕܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 11, 'ܘܐܬܝܗܒܬ ܠܟܠܚܕ ܚܕ ܡܢܗܘܢ ܐܤܛܠܐ ܚܘܪܬܐ ܘܐܬܐܡܪ ܕܢܬܬܢܝܚܘܢ ܥܕ ܥܕܢ ܙܒܢ ܙܥܘܪ ܥܕܡܐ ܕܡܫܬܡܠܝܢ ܐܦ ܟܢܘܬܗܘܢ ܘܐܚܝܗܘܢ ܐܝܠܝܢ ܕܥܬܝܕܝܢ ܠܡܬܩܛܠܘ ܐܝܟ ܕܐܦ ܗܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 12, 'ܘܚܙܝܬ ܟܕ ܦܬܚ ܛܒܥܐ ܕܫܬܐ ܘܢܘܕܐ ܪܒܐ ܗܘܐ ܘܫܡܫܐ ܐܝܟ ܤܩܐ ܕܤܥܪܐ ܐܘܟܡ ܗܘܐ ܘܤܗܪܐ ܟܠܗ ܗܘܐ ܠܗ ܐܝܟ ܕܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 13, 'ܘܟܘܟܒܐ ܕܫܡܝܐ ܢܦܠܘ ܥܠ ܐܪܥܐ ܐܝܟ ܬܬܐ ܕܫܕܝܐ ܦܩܘܥܝܗ ܡܢ ܪܘܚܐ ܥܫܝܢܬܐ ܡܐ ܕܡܬܬܙܝܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 14, 'ܘܫܡܝܐ ܐܬܦܪܫ ܘܐܝܟ ܟܬܒܐ ܐܬܟܪܟܘ ܘܟܠ ܛܘܪ ܘܟܠ ܓܙܪܬܐ ܡܢ ܕܘܟܬܗܘܢ ܐܬܬܙܝܥܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 15, 'ܘܡܠܟܐ ܕܐܪܥܐ ܘܪܘܪܒܢܐ ܘܪܝܫܝ ܐܠܦܐ ܘܥܬܝܪܐ ܘܚܝܠܘܬܐ ܘܟܠ ܥܒܕܐ ܘܒܢܝ ܚܐܪܐ ܛܫܝܘ ܢܦܫܗܘܢ ܒܡܥܪܐ ܘܒܫܘܥܐ ܕܛܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 16, 'ܘܐܡܪܝܢ ܠܛܘܪܐ ܘܫܘܥܐ ܕܦܠܘ ܥܠܝܢ ܘܛܫܘ ܠܢ ܡܢ ܩܕܡ ܐܦܘܗܝ ܕܐܡܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 17, 'ܡܛܠ ܕܐܬܐ ܝܘܡܐ ܪܒܐ ܕܪܘܓܙܗܘܢ ܘܡܢܘ ܡܫܟܚ ܠܡܩܡ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 1, 'ܘܡܢ ܒܬܪ ܗܕܐ ܚܙܝܬ ܐܪܒܥܐ ܡܠܐܟܝܢ ܩܝܡܝܢ ܥܠ ܐܪܒܥ ܙܘܝܬܗ ܕܐܪܥܐ ܘܐܚܝܕܝܢ ܠܐܪܒܥܬ ܪܘܚܐ ܕܠܐ ܢܫܒ ܪܘܚܐ ܥܠ ܐܪܥܐ ܘܠܐ ܥܠ ܝܡܐ ܘܠܐ ܥܠ ܟܠ ܐܝܠܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 2, 'ܘܚܙܝܬ ܐܚܪܢܐ ܡܠܐܟܐ ܕܤܠܩ ܡܢ ܡܕܢܚܝ ܫܡܫܐ ܘܐܝܬ ܠܗ ܚܬܡܐ ܕܐܠܗܐ ܚܝܐ ܘܩܥܐ ܒܩܠܐ ܪܡܐ ܠܐܪܒܥܐ ܡܠܐܟܐ ܗܢܘܢ ܕܐܬܝܗܒ ܠܗܘܢ ܕܢܗܪܘܢ ܠܐܪܥܐ ܘܠܝܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 3, 'ܘܐܡܪ ܠܐ ܬܗܪܘܢ ܠܐܪܥܐ ܘܠܐ ܠܝܡܐ ܘܐܦܠܐ ܠܐܝܠܢܐ ܥܕܡܐ ܕܢܚܬܘܡ ܠܥܒܕܘܗܝ ܕܐܠܗܐ ܒܝܬ ܥܝܢܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 4, 'ܘܫܡܥܬ ܡܢܝܢܐ ܕܚܬܝܡܐ ܡܐܐ ܘܐܪܒܥܝܢ ܘܐܪܒܥܐ ܐܠܦܝܢ ܡܢ ܟܠ ܫܪܒܢ ܕܐܝܤܪܐܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 5, 'ܡܢ ܫܪܒܬܗ ܕܝܗܘܕܐ ܬܪܥܤܪ ܐܠܦܝܢ ܡܢ ܫܪܒܬܗ ܕܪܘܒܝܠ ܬܪܥܤܪ ܐܠܦܝܢ ܡܢ ܫܪܒܬܗ ܕܓܕ ܬܪܥܤܪ ܐܠܦܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 6, 'ܡܢ ܫܪܒܬܗ ܕܐܫܝܪ ܬܪܥܤܪ ܐܠܦܝܢ ܡܢ ܫܪܒܬܗ ܕܢܦܬܠܝ ܬܪܥܤܪ ܐܠܦܝܢ ܡܢ ܫܪܒܬܗ ܕܡܢܫܐ ܬܪܥܤܪ ܐܠܦܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 7, 'ܡܢ ܫܪܒܬܗ ܕܫܡܥܘܢ ܬܪܥܤܪ ܐܠܦܝܢ ܡܢ ܫܪܒܬܗ ܕܐܝܤܟܪ ܬܪܥܤܪ ܐܠܦܝܢ ܡܢ ܫܪܒܬܗ ܕܠܘܝ ܬܪܥܤܪ ܐܠܦܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 8, 'ܡܢ ܫܪܒܬܗ ܕܙܒܘܠܘܢ ܬܪܥܤܪ ܐܠܦܝܢ ܡܢ ܫܪܒܬܗ ܕܝܘܤܦ ܬܪܥܤܪ ܐܠܦܝܢ ܡܢ ܫܪܒܬܗ ܕܒܢܝܡܝܢ ܬܪܥܤܪ ܐܠܦܝܢ ܚܬܝܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 9, 'ܒܬܪܟܢ ܚܙܝܬ ܟܢܫܐ ܤܓܝܐܐ ܐܝܢܐ ܕܠܡܢܝܢܗ ܠܝܬ ܕܡܨܐ ܗܘܐ ܡܢ ܟܠ ܥܡ ܘܫܪܒܐ ܘܐܡܘܢ ܘܠܫܢܝܢ ܕܩܝܡܝܢ ܩܕܡ ܟܘܪܤܝܐ ܘܩܕܡܘܗܝ ܕܐܡܪܐ ܘܡܥܛܦܝܢ ܐܤܛܠܐ ܚܘܪܬܐ ܘܒܐܝܕܝܗܘܢ ܕܩܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 10, 'ܘܩܥܝܢ ܒܩܠܐ ܪܒܐ ܘܐܡܪܝܢ ܦܘܪܩܢܐ ܠܐܠܗܢ ܘܠܕܝܬܒ ܥܠ ܟܘܪܤܝܐ ܘܠܐܡܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 11, 'ܘܟܠܗܘܢ ܡܠܐܟܐ ܩܝܡܝܢ ܗܘܘ ܚܕܪܘܗܝ ܕܟܘܪܤܝܐ ܘܕܩܫܝܫܐ ܘܕܐܪܒܥ ܚܝܘܢ ܘܢܦܠܘ ܩܕܡ ܟܘܪܤܝܐ ܥܠ ܐܦܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 12, 'ܟܕ ܐܡܪܝܢ ܐܡܝܢ ܬܫܒܘܚܬܐ ܘܒܘܪܟܬܐ ܘܚܟܡܬܐ ܘܩܘܒܠ ܛܝܒܘܬܐ ܘܐܝܩܪܐ ܘܚܝܠܐ ܘܥܘܫܢܐ ܠܐܠܗܢ ܠܥܠܡ ܥܠܡܝܢ ܐܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 13, 'ܘܥܢܐ ܚܕ ܡܢ ܩܫܝܫܐ ܘܐܡܪ ܠܝ ܗܠܝܢ ܕܥܛܝܦܝܢ ܐܤܛܠܐ ܚܘܪܬܐ ܡܢ ܐܢܘܢ ܘܡܢ ܐܝܟܐ ܐܬܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 14, 'ܘܐܡܪܬ ܠܗ ܡܪܝ ܐܢܬ ܝܕܥ ܐܢܬ ܘܐܡܪ ܠܝ ܗܠܝܢ ܐܢܘܢ ܐܝܠܝܢ ܕܐܬܘ ܡܢ ܐܘܠܨܢܐ ܪܒܐ ܘܚܠܠܘ ܐܤܛܠܝܗܘܢ ܘܚܘܪܘ ܐܢܝܢ ܒܕܡܐ ܕܐܡܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 15, 'ܡܛܠ ܗܢܐ ܐܝܬܝܗܘܢ ܩܕܡ ܟܘܪܤܝܐ ܕܐܠܗܐ ܘܡܫܡܫܝܢ ܠܗ ܐܝܡܡܐ ܘܠܠܝܐ ܒܗܝܟܠܗ ܘܕܝܬܒ ܥܠ ܟܘܪܤܝܐ ܢܓܢ ܥܠܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 16, 'ܠܐ ܢܟܦܢܘܢ ܘܠܐ ܢܨܗܘܢ ܘܫܡܫܐ ܥܠܝܗܘܢ ܠܐ ܢܦܠ ܘܠܐ ܟܠ ܫܘܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 17, 'ܡܛܠ ܕܐܡܪܐ ܕܒܡܨܥܬ ܟܘܪܤܝܐ ܢܪܥܐ ܐܢܘܢ ܘܢܫܒܠ ܐܢܘܢ ܨܝܕ ܚܝܐ ܘܨܝܕ ܥܝܢܬܐ ܕܡܝܐ ܘܢܠܚܐ ܟܠ ܕܡܥܐ ܡܢ ܥܝܢܝܗܘܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 1, 'ܘܟܕ ܦܬܚ ܛܒܥܐ ܕܫܒܥܐ ܗܘܐ ܫܬܩܐ ܒܫܡܝܐ ܐܝܟ ܦܠܓܘܬ ܫܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 2, 'ܘܚܙܝܬ ܠܫܒܥܐ ܡܠܐܟܝܢ ܐܝܠܝܢ ܕܩܕܡ ܐܠܗܐ ܩܝܡܝܢ ܗܘܘ ܕܐܬܝܗܒܘ ܠܗܘܢ ܫܒܥܐ ܫܝܦܘܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 3, 'ܘܐܚܪܢܐ ܡܠܐܟܐ ܐܬܐ ܘܩܡ ܥܠ ܡܕܒܚܐ ܘܐܝܬ ܠܗ ܦܝܪܡܐ ܕܕܗܒܐ ܘܐܬܝܗܒ ܠܗ ܒܤܡܐ ܤܓܝܐܐ ܕܢܬܠ ܒܨܠܘܬܐ ܕܟܠܗܘܢ ܩܕܝܫܐ ܥܠ ܡܕܒܚܐ ܕܩܕܡ ܟܘܪܤܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 4, 'ܘܤܠܩ ܥܛܪܐ ܕܒܤܡܐ ܒܨܠܘܬܐ ܕܩܕܝܫܐ ܡܢ ܝܕ ܡܠܐܟܐ ܩܕܡ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 5, 'ܘܢܤܒ ܡܠܐܟܐ ܠܦܝܪܡܐ ܘܡܠܝܗܝ ܡܢ ܢܘܪܐ ܕܥܠ ܡܕܒܚܐ ܘܐܪܡܝ ܥܠ ܐܪܥܐ ܘܗܘܘ ܪܥܡܐ ܘܩܠܐ ܘܒܪܩܐ ܘܢܘܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 6, 'ܘܫܒܥܐ ܡܠܐܟܝܢ ܕܥܠܝܗܘܢ ܫܒܥܐ ܫܝܦܘܪܝܢ ܛܝܒܘ ܢܦܫܗܘܢ ܠܡܙܥܩܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 7, 'ܘܗܘ ܩܕܡܝܐ ܐܙܥܩ ܘܗܘܐ ܒܪܕܐ ܘܢܘܪܐ ܕܦܬܝܟܝܢ ܒܡܝܐ ܘܐܬܪܡܝܘ ܥܠ ܐܪܥܐ ܘܬܘܠܬܗ ܕܐܪܥܐ ܝܩܕ ܘܬܘܠܬܐ ܕܐܝܠܢܐ ܝܩܕ ܘܟܠ ܥܤܒܐ ܕܐܪܥܐ ܝܩܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 8, 'ܘܕܬܪܝܢ ܙܥܩ ܘܗܘܐ ܐܝܟ ܛܘܪܐ ܪܒܐ ܕܝܩܕ ܢܦܠ ܒܝܡܐ ܘܗܘܐ ܬܘܠܬܗ ܕܝܡܐ ܕܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 9, 'ܘܡܝܬ ܬܘܠܬܐ ܕܟܠ ܒܪܝܬܐ ܕܒܝܡܐ ܕܐܝܬ ܒܗ ܢܦܫܐ ܘܬܘܠܬܐ ܕܐܠܦܐ ܐܬܚܒܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 10, 'ܘܕܬܠܬܐ ܙܥܩ ܘܢܦܠ ܡܢ ܫܡܝܐ ܟܘܟܒܐ ܪܒܐ ܕܝܩܕ ܐܝܟ ܫܠܗܒܝܬܐ ܘܢܦܠ ܥܠ ܬܘܠܬܐ ܕܢܗܪܘܬܐ ܘܥܠ ܥܝܢܬܐ ܕܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 11, 'ܘܫܡܗ ܕܟܘܟܒܐ ܡܬܐܡܪ ܐܦܤܝܬܢܐ ܘܗܘܐ ܬܘܠܬܗܘܢ ܕܡܝܐ ܐܝܟ ܐܦܤܢܬܝܢ ܘܤܘܓܐܐ ܕܒܢܝܢܫܐ ܡܝܬܘ ܡܛܠ ܕܐܬܡܪܡܪܘ ܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 12, 'ܘܕܐܪܒܥܐ ܙܥܩ ܘܒܠܥ ܬܘܠܬܗ ܕܫܡܫܐ ܘܬܘܠܬܗ ܕܤܗܪܐ ܘܬܘܠܬܐ ܕܟܘܟܒܐ ܘܚܫܟܘ ܬܘܠܬܗܘܢ ܘܝܘܡܐ ܠܐ ܚܘܝ ܬܘܠܬܗ ܘܠܠܝܐ ܗܟܘܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 13, 'ܘܫܡܥܬ ܠܢܫܪܐ ܚܕ ܕܦܪܚ ܒܫܡܝܐ ܕܐܡܪ ܘܝ ܘܝ ܘܝ ܠܥܡܘܪܝܗ ܕܐܪܥܐ ܡܢ ܩܠܐ ܕܫܝܦܘܪܐ ܕܬܠܬܐ ܡܠܐܟܝܢ ܕܥܬܝܕܝܢ ܠܡܙܥܩܘ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 1, 'ܘܕܚܡܫܐ ܙܥܩ ܘܚܙܝܬ ܟܘܟܒܐ ܕܢܦܠ ܡܢ ܫܡܝܐ ܥܠ ܐܪܥܐ ܘܐܬܝܗܒ ܠܗ ܩܠܝܕܐ ܕܒܐܪܘܗܝ ܕܬܗܘܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 2, 'ܘܤܠܩ ܬܢܢܐ ܡܢ ܒܐܪܐ ܐܝܟ ܬܢܢܐ ܕܐܬܘܢܐ ܪܒܐ ܕܡܫܬܓܪ ܘܚܫܟ ܫܡܫܐ ܘܐܐܪ ܡܢ ܬܢܢܐ ܕܒܐܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 3, 'ܘܡܢ ܬܢܢܐ ܢܦܩܘ ܩܡܨܐ ܥܠ ܐܪܥܐ ܘܐܬܝܗܒ ܠܗܘܢ ܫܘܠܛܢܐ ܕܐܝܬ ܠܥܩܪܒܐ ܕܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 4, 'ܘܐܬܐܡܪ ܠܗܘܢ ܕܠܐ ܢܗܪܘܢ ܠܥܤܒܗ ܕܐܪܥܐ ܘܠܟܠ ܝܘܪܩ ܐܦܠܐ ܠܐܝܠܢܐ ܐܠܐ ܐܢ ܠܒܢܝܢܫܐ ܐܝܠܝܢ ܕܠܝܬ ܠܗܘܢ ܚܬܡܐ ܕܐܠܗܐ ܒܝܬ ܥܝܢܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 5, 'ܘܐܬܝܗܒ ܠܗܘܢ ܕܠܐ ܢܩܛܠܘܢ ܐܢܘܢ ܐܠܐ ܢܫܬܢܩܘܢ ܝܪܚܐ ܚܡܫܐ ܘܬܫܢܝܩܗܘܢ ܐܝܟ ܬܫܢܝܩܐ ܕܥܩܪܒܐ ܡܐ ܕܢܦܠܐ ܥܠ ܐܢܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 6, 'ܘܒܝܘܡܬܐ ܗܢܘܢ ܢܒܥܘܢ ܒܢܝܢܫܐ ܠܡܘܬܐ ܘܠܐ ܢܫܟܚܘܢܝܗܝ ܘܢܬܪܓܪܓܘܢ ܠܡܡܬ ܘܢܥܪܘܩ ܡܘܬܐ ܡܢܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 7, 'ܘܕܡܘܬܐ ܕܩܡܨܐ ܐܝܟ ܕܡܘܬܐ ܕܪܟܫܐ ܕܡܛܝܒܝܢ ܠܩܪܒܐ ܘܥܠ ܪܫܝܗܘܢ ܐܝܟ ܟܠܝܠܐ ܕܕܡܘܬܐ ܕܕܗܒܐ ܘܐܦܝܗܘܢ ܐܝܟ ܐܦܐ ܕܐܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 8, 'ܘܤܥܪܐ ܐܝܬ ܠܗܘܢ ܐܝܟ ܤܥܪܐ ܕܢܫܐ ܘܫܢܝܗܘܢ ܐܝܟ ܕܐܪܝܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 9, 'ܘܐܝܬ ܗܘܐ ܠܗܘܢ ܫܪܝܢܐ ܐܝܟ ܫܪܝܢܐ ܕܦܪܙܠܐ ܘܩܠܐ ܕܓܦܝܗܘܢ ܐܝܟ ܩܠܐ ܕܡܪܟܒܬܐ ܕܪܟܫܐ ܤܓܝܐܐ ܕܪܗܛܝܢ ܠܩܪܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 10, 'ܘܐܝܬ ܠܗܘܢ ܕܘܢܒܝܬܐ ܐܝܟ ܕܡܘܬܐ ܕܥܩܪܒܐ ܘܥܘܩܤܐ ܕܝܢ ܒܕܘܢܒܝܬܗܘܢ ܘܫܘܠܛܢܗܘܢ ܠܡܗܪܘ ܠܒܢܝܢܫܐ ܝܪܚܐ ܚܡܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 11, 'ܘܐܝܬ ܥܠܝܗܘܢ ܡܠܟܐ ܡܠܐܟܗ ܕܬܗܘܡܐ ܕܫܡܗ ܥܒܪܐܝܬ ܥܒܕܘ ܘܐܪܡܐܝܬ ܫܡܐ ܠܗ ܐܝܬ ܫܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 12, 'ܘܝ ܚܕ ܐܙܠ ܗܐ ܬܘܒ ܐܬܝܢ ܬܪܝܢ ܘܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 13, 'ܒܬܪ ܗܠܝܢ ܡܠܐܟܐ ܕܫܬܐ ܙܥܩ ܘܫܡܥܬ ܩܠܐ ܚܕ ܡܢ ܐܪܒܥ ܩܪܢܬܗ ܕܡܕܒܚܐ ܕܕܗܒܐ ܕܩܕܡ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 14, 'ܕܐܡܪ ܠܡܠܐܟܐ ܫܬܝܬܝܐ ܕܐܝܬ ܠܗ ܫܝܦܘܪܐ ܫܪܝ ܠܐܪܒܥܐ ܡܠܐܟܐ ܕܐܤܝܪܝܢ ܥܠ ܢܗܪܐ ܪܒܐ ܦܪܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 15, 'ܘܐܫܬܪܝܘ ܐܪܒܥܐ ܡܠܐܟܝܢ ܐܝܠܝܢ ܕܡܛܝܒܝܢ ܠܫܥܬܐ ܘܠܝܘܡܐ ܘܠܝܪܚܐ ܘܠܫܢܬܐ ܕܢܩܛܠܘܢ ܬܘܠܬܐ ܕܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 16, 'ܘܡܢܝܢܐ ܕܚܝܠܘܬܐ ܕܦܪܫܐ ܬܪܬܝܢ ܪܒܘ ܪܒܘܢ ܫܡܥܬ ܡܢܝܢܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 17, 'ܘܗܟܢܐ ܚܙܝܬ ܪܟܫܐ ܒܚܙܘܐ ܘܠܕܝܬܒܝܢ ܥܠܝܗܘܢ ܐܝܬ ܫܪܝܢܐ ܕܢܘܪܐ ܘܩܪܟܕܢܐ ܕܟܒܪܝܬܐ ܘܩܪܩܦܬܐ ܕܪܟܫܗܘܢ ܐܝܟ ܩܪܩܦܬܐ ܕܐܪܝܘܬܐ ܘܡܢ ܦܘܡܗܘܢ ܢܦܩܐ ܢܘܪܐ ܘܟܒܪܝܬܐ ܘܬܢܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 18, 'ܘܡܢ ܗܠܝܢ ܬܠܬ ܡܚܘܢ ܐܬܩܛܠܘ ܬܘܠܬܐ ܕܒܢܝܢܫܐ ܘܡܢ ܢܘܪܐ ܘܡܢ ܟܒܪܝܬܐ ܘܡܢ ܬܢܢܐ ܕܢܦܩ ܡܢ ܦܘܡܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 19, 'ܡܛܠ ܕܫܘܠܛܢܐ ܕܪܟܫܐ ܒܦܘܡܗܘܢ ܘܐܦ ܒܕܘܢܒܝܬܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 20, 'ܘܫܪܟܐ ܕܒܢܝܢܫܐ ܕܠܐ ܐܬܩܛܠܘ ܒܡܚܘܬܐ ܗܠܝܢ ܘܠܐ ܬܒܘ ܡܢ ܥܒܕ ܐܝܕܝܗܘܢ ܕܠܐ ܢܤܓܕܘܢ ܠܕܝܘܐ ܘܠܦܬܟܪܐ ܕܕܗܒܐ ܘܕܤܐܡܐ ܘܕܢܚܫܐ ܘܕܩܝܤܐ ܘܕܟܐܦܐ ܐܝܠܝܢ ܕܠܐ ܠܡܚܙܐ ܘܠܐ ܠܡܫܡܥ ܡܨܝܢ ܐܘ ܠܡܗܠܟܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 21, 'ܘܠܐ ܬܒܘ ܡܢ ܩܛܠܝܗܘܢ ܘܡܢ ܚܪܫܝܗܘܢ ܘܡܢ ܙܢܝܘܬܗܘܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 1, 'ܘܚܙܝܬ ܐܚܪܢܐ ܡܠܐܟܐ ܕܢܚܬ ܡܢ ܫܡܝܐ ܘܡܥܛܦ ܥܢܢܐ ܘܩܫܬܐ ܕܫܡܝܐ ܥܠ ܪܫܗ ܘܚܙܘܗ ܐܝܟ ܫܡܫܐ ܘܪܓܠܘܗܝ ܐܝܟ ܥܡܘܕܐ ܕܢܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 2, 'ܘܐܝܬ ܠܗ ܒܐܝܕܗ ܟܬܒܘܢܐ ܦܬܝܚܐ ܘܤܡ ܪܓܠܗ ܕܝܡܝܢܐ ܥܠ ܝܡܐ ܕܤܡܠܐ ܕܝܢ ܥܠ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 3, 'ܘܩܥܐ ܒܩܠܐ ܪܡܐ ܐܝܟ ܐܪܝܐ ܕܓܤܪ ܘܟܕ ܩܥܐ ܡܠܠܘ ܫܒܥܐ ܪܥܡܝܢ ܒܩܠܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 4, 'ܘܟܕ ܡܠܠܘ ܫܒܥܐ ܪܥܡܝܢ ܡܛܝܒ ܗܘܝܬ ܠܡܟܬܒ ܘܫܡܥܬ ܩܠܐ ܡܢ ܫܡܝܐ ܕܫܒܥܐ ܕܐܡܪ ܚܬܘܡ ܗܘ ܡܐ ܕܡܠܠܘ ܫܒܥܐ ܪܥܡܝܢ ܘܠܐ ܬܟܬܒܝܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 5, 'ܘܡܠܐܟܐ ܗܘ ܕܚܙܝܬ ܕܩܐܡ ܥܠ ܝܡܐ ܘܥܠ ܝܒܫܐ ܕܐܪܝܡ ܐܝܕܗ ܠܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 6, 'ܘܝܡܐ ܒܗܘ ܕܚܝ ܠܥܠܡ ܥܠܡܝܢ ܗܘ ܕܒܪܗ ܠܫܡܝܐ ܘܕܒܗ ܘܠܐܪܥܐ ܘܕܒܗ ܕܬܘܒ ܙܒܢܐ ܠܐ ܢܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 7, 'ܐܠܐ ܒܝܘܡܬܐ ܕܡܠܐܟܐ ܕܫܒܥܐ ܡܐ ܕܥܬܝܕ ܠܡܙܥܩ ܘܐܫܬܠܡ ܐܪܙܗ ܕܐܠܗܐ ܗܘ ܕܤܒܪ ܠܥܒܕܘܗܝ ܢܒܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 8, 'ܘܩܠܐ ܫܡܥܬ ܡܢ ܫܡܝܐ ܬܘܒ ܕܡܡܠܠ ܥܡܝ ܘܐܡܪ ܙܠ ܤܒ ܠܟܬܒܘܢܐ ܕܒܐܝܕܗ ܕܡܠܐܟܐ ܕܩܐܡ ܥܠ ܐܪܥܐ ܘܥܠ ܝܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 9, 'ܘܐܙܠܬ ܠܘܬ ܡܠܐܟܐ ܟܕ ܐܡܪ ܐܢܐ ܠܗ ܠܡܬܠ ܠܝ ܠܟܬܒܘܢܐ ܘܐܡܪ ܠܝ ܤܒ ܘܐܟܘܠܝܗܝ ܘܢܡܪ ܠܟ ܟܪܤܟ ܐܠܐ ܒܦܘܡܟ ܢܗܘܐ ܐܝܟ ܕܒܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 10, 'ܘܢܤܒܬ ܠܟܬܒܘܢܐ ܡܢ ܐܝܕܗ ܕܡܠܐܟܐ ܘܐܟܠܬܗ ܘܐܝܬ ܗܘܐ ܒܦܘܡܝ ܐܝܟ ܕܒܫܐ ܚܠܝܐ ܘܟܕ ܐܟܠܬܗ ܡܪܬ ܟܪܤܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 11, 'ܘܐܡܪ ܠܝ ܝܗܝܒ ܠܟ ܬܘܒ ܙܒܢܐ ܠܡܬܢܒܝܘ ܥܠ ܥܡܡܐ ܘܐܡܘܬܐ ܘܠܫܢܐ ܘܡܠܟܐ ܤܓܝܐܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 1, 'ܘܐܬܝܗܒ ܠܝ ܩܢܝܐ ܕܡܘܬܐ ܕܫܒܛܐ ܘܩܐܡ ܗܘܐ ܡܠܐܟܐ ܘܐܡܪ ܩܘܡ ܘܡܫܘܚ ܠܗܝܟܠܐ ܕܐܠܗܐ ܘܠܡܕܒܚܐ ܘܠܐܝܠܝܢ ܕܤܓܕܝܢ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 2, 'ܘܠܕܪܬܐ ܕܠܓܘ ܡܢ ܗܝܟܠܐ ܐܦܩ ܡܢ ܠܒܪ ܘܠܐ ܬܡܫܚܝܗ ܡܛܠ ܕܐܬܝܗܒܬ ܠܥܡܡܐ ܘܠܡܕܝܢܬܐ ܩܕܝܫܬܐ ܢܕܘܫܘܢ ܝܪܚܐ ܐܪܒܥܝܢ ܘܬܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 3, 'ܘܐܬܠ ܠܬܪܝܢ ܤܗܕܝ ܠܡܬܢܒܝܘ ܝܘܡܝܢ ܐܠܦ ܘܡܐܬܝܢ ܘܫܬܝܢ ܟܕ ܥܛܝܦܝܢ ܤܩܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 4, 'ܗܠܝܢ ܐܢܘܢ ܬܪܝܢ ܙܝܬܝܢ ܘܬܪܬܝܢ ܡܢܪܢ ܕܩܕܡ ܡܪܐ ܕܟܠܗ ܐܪܥܐ ܩܝܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 5, 'ܘܡܢ ܕܒܥܐ ܕܢܗܪ ܐܢܘܢ ܢܦܩܐ ܢܘܪܐ ܡܢ ܦܘܡܗܘܢ ܘܐܟܠܐ ܠܒܥܠܕܒܒܝܗܘܢ ܘܠܐܝܢܐ ܕܨܒܐ ܕܢܗܪ ܐܢܘܢ ܗܟܢ ܝܗܝܒ ܠܗܘܢ ܠܡܬܩܛܠܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 6, 'ܘܗܠܝܢ ܐܝܬ ܠܗܘܢ ܫܘܠܛܢܐ ܕܢܐܚܕܘܢ ܠܫܡܝܐ ܕܠܐ ܢܚܘܬ ܡܛܪܐ ܒܝܘܡܬܐ ܕܢܒܝܘܬܗܘܢ ܘܐܝܬ ܠܗܘܢ ܫܘܠܛܢܐ ܕܢܗܦܟܘܢ ܡܝܐ ܠܕܡܐ ܘܕܢܡܚܘܢ ܠܐܪܥܐ ܒܟܠ ܡܚܘܢ ܟܡܐ ܕܢܨܒܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 7, 'ܘܡܐ ܕܫܡܠܝܘ ܤܗܕܘܬܗܘܢ ܚܝܘܬܐ ܕܤܠܩܐ ܡܢ ܝܡܐ ܬܥܒܕ ܥܡܗܘܢ ܩܪܒܐ ܘܬܙܟܐ ܐܢܘܢ ܘܬܩܛܘܠ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 8, 'ܘܫܠܕܝܗܘܢ ܥܠ ܫܘܩܐ ܕܡܕܝܢܬܐ ܪܒܬܐ ܐܝܕܐ ܕܡܬܩܪܝܐ ܪܘܚܢܐܝܬ ܤܕܘܡ ܘܡܨܪܝܢ ܐܝܟܐ ܕܡܪܗܘܢ ܐܨܛܠܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 9, 'ܘܚܙܝܢ ܡܢ ܐܡܘܬܐ ܘܫܪܒܬܐ ܘܠܫܢܐ ܘܥܡܡܐ ܠܫܠܕܝܗܘܢ ܬܠܬܐ ܝܘܡܝܢ ܘܦܠܓܗ ܘܠܫܠܕܝܗܘܢ ܠܐ ܢܫܒܩܘܢ ܠܡܬܬܤܡܘ ܒܩܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 10, 'ܘܥܡܘܪܝܗ ܕܐܪܥܐ ܢܚܕܘܢ ܥܠܝܗܘܢ ܘܢܬܦܨܚܘܢ ܘܡܘܗܒܬܐ ܢܫܕܪܘܢ ܠܚܕܕܐ ܡܛܠ ܬܪܝܢ ܢܒܝܝܢ ܕܫܢܩܘ ܠܥܡܘܪܝܗ ܕܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 11, 'ܘܡܢ ܒܬܪ ܬܠܬܐ ܝܘܡܝܢ ܘܦܠܓܗ ܪܘܚܐ ܚܝܬܐ ܡܢ ܐܠܗܐ ܥܠܬ ܒܗܘܢ ܘܩܡܘ ܥܠ ܪܓܠܝܗܘܢ ܘܪܘܚܐ ܕܚܝܐ ܢܦܠܬ ܥܠܝܗܘܢ ܘܕܚܠܬܐ ܪܒܬܐ ܗܘܬ ܥܠ ܐܝܠܝܢ ܕܚܙܝܢ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 12, 'ܘܫܡܥܘ ܩܠܐ ܪܒܐ ܡܢ ܫܡܝܐ ܕܐܡܪ ܠܗܘܢ ܤܩܘ ܠܟܐ ܘܤܠܩܘ ܠܫܡܝܐ ܒܥܢܢܐ ܘܡܨܕܝܢ ܒܗܘܢ ܒܥܠܕܒܒܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 13, 'ܘܒܫܥܬܐ ܗܝ ܗܘܐ ܙܘܥܐ ܪܒܐ ܘܚܕ ܡܢ ܥܤܪܐ ܕܡܕܝܢܬܐ ܢܦܠܘ ܘܐܬܩܛܠܘ ܒܙܘܥܐ ܫܡܗܐ ܕܓܒܪܐ ܐܠܦܐ ܫܒܥܐ ܘܕܫܪܟܐ ܗܘܘ ܒܕܚܠܬܐ ܘܝܗܒܘܢ ܬܫܒܘܚܬܐ ܠܐܠܗܐ ܕܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 14, 'ܗܐ ܬܪܝܢ ܘܝ ܐܙܠܘ ܘܗܐ ܘܝ ܕܬܠܬܐ ܐܬܐ ܡܚܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 15, 'ܘܡܠܐܟܐ ܕܫܒܥܐ ܙܥܩ ܘܗܘܘ ܩܠܐ ܪܘܪܒܐ ܒܫܡܝܐ ܕܐܡܪܝܢ ܗܘܬ ܡܠܟܘܬܗ ܕܥܠܡܐ ܕܐܠܗܢ ܘܕܡܫܝܚܗ ܘܐܡܠܟ ܠܥܠܡ ܥܠܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 16, 'ܘܥܤܪܝܢ ܘܐܪܒܥܐ ܩܫܝܫܐ ܐܝܠܝܢ ܕܩܕܡ ܐܠܗܐ ܝܬܒܝܢ ܥܠ ܟܘܪܤܘܬܗܘܢ ܢܦܠܘ ܥܠ ܐܦܝܗܘܢ ܘܤܓܕܘ ܠܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 17, 'ܠܡܐܡܪ ܡܘܕܝܢܢ ܠܟ ܡܪܝܐ ܐܠܗܐ ܐܚܝܕ ܟܠ ܕܐܝܬܘܗܝ ܘܐܝܬܘܗܝ ܗܘܐ ܕܢܤܒܬ ܒܚܝܠܟ ܪܒܐ ܘܐܡܠܟܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 18, 'ܘܥܡܡܐ ܪܓܙܘ ܘܐܬܐ ܪܘܓܙܟ ܘܙܒܢܐ ܕܡܝܬܐ ܕܢܬܕܝܢܘܢ ܘܬܬܠ ܐܓܪܐ ܠܥܒܕܝܟ ܢܒܝܐ ܘܠܩܕܝܫܐ ܘܠܕܚܠܝ ܫܡܟ ܠܙܥܘܪܐ ܥܡ ܪܘܪܒܐ ܘܬܚܒܠ ܠܐܝܠܝܢ ܕܚܒܠܘ ܠܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 19, 'ܘܐܬܦܬܚ ܗܝܟܠܐ ܒܫܡܝܐ ܘܐܬܚܙܝܬ ܩܝܒܘܬܐ ܕܕܝܬܩܐ ܕܝܠܗ ܒܗܝܟܠܐ ܘܗܘܘ ܒܪܩܐ ܘܪܥܡܐ ܘܩܠܐ ܘܢܘܕܐ ܘܒܪܕܐ ܪܒܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 1, 'ܘܐܬܐ ܪܒܬܐ ܐܬܚܙܝܬ ܒܫܡܝܐ ܐܢܬܬܐ ܕܥܛܝܦܐ ܫܡܫܐ ܘܤܗܪܐ ܬܚܝܬ ܪܓܠܝܗ ܘܟܠܝܠܐ ܕܟܘܟܒܐ ܬܪܥܤܪ ܥܠ ܪܫܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 2, 'ܘܒܛܢܐ ܘܩܥܝܐ ܘܡܚܒܠܢ ܐܦ ܡܫܬܢܩܐ ܕܬܐܠܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 3, 'ܘܐܬܚܙܝܬ ܐܬܐ ܐܚܪܬܐ ܒܫܡܝܐ ܘܗܐ ܬܢܝܢܐ ܪܒܐ ܕܢܘܪܐ ܕܐܝܬ ܠܗ ܫܒܥܐ ܪܫܝܢ ܘܥܤܪ ܩܪܢܬܐ ܘܥܠ ܪܫܘܗܝ ܫܒܥܐ ܬܐܓܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 4, 'ܘܕܘܢܒܗ ܓܪܫܐ ܠܬܘܠܬܐ ܕܟܘܟܒܐ ܕܒܫܡܝܐ ܘܐܪܡܝ ܐܢܘܢ ܥܠ ܐܪܥܐ ܘܬܢܝܢܐ ܩܐܡ ܗܘܐ ܩܕܡ ܐܢܬܬܐ ܕܥܬܝܕܐ ܕܬܐܠܕ ܕܡܐ ܕܝܠܕܬ ܢܐܟܠܝܘܗܝ ܠܒܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 5, 'ܘܝܠܕܬ ܒܪܐ ܕܟܪܐ ܗܘ ܕܥܬܝܕ ܠܡܪܥܐ ܠܟܠܗܘܢ ܥܡܡܐ ܒܫܒܛܐ ܕܦܪܙܠܐ ܘܐܬܚܛܦ ܒܪܗ ܠܘܬ ܐܠܗܐ ܘܠܘܬ ܟܘܪܤܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 6, 'ܘܐܢܬܬܐ ܥܪܩܬ ܠܚܘܪܒܐ ܐܬܪ ܕܐܝܬ ܗܘܐ ܠܗ ܬܡܢ ܕܘܟܬܐ ܕܡܛܝܒܐ ܡܢ ܐܠܗܐ ܕܢܬܪܤܘܢܗ ܝܘܡܝܢ ܐܠܦ ܘܡܐܬܝܢ ܘܫܬܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 7, 'ܘܗܘܐ ܩܪܒܐ ܒܫܡܝܐ ܡܝܟܐܝܠ ܘܡܠܐܟܘܗܝ ܡܩܪܒܝܢ ܥܡ ܬܢܝܢܐ ܘܬܢܝܢܐ ܘܡܠܐܟܘܗܝ ܐܩܪܒܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 8, 'ܘܠܐ ܐܬܡܨܝܘ ܘܠܐ ܐܬܪܐ ܐܫܬܟܚ ܠܗܘܢ ܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 9, 'ܘܐܬܪܡܝ ܬܢܝܢܐ ܪܒܐ ܗܘ ܚܘܝܐ ܪܫܐ ܗܘ ܕܡܬܩܪܐ ܐܟܠܩܪܨܐ ܘܤܛܢܐ ܗܘ ܕܐܛܥܝ ܠܟܠܗ ܐܪܥܐ ܘܐܬܪܡܝ ܥܠ ܐܪܥܐ ܘܡܠܐܟܘܗܝ ܥܡܗ ܐܬܪܡܝܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 10, 'ܘܫܡܥܬ ܩܠܐ ܪܒܐ ܡܢ ܫܡܝܐ ܕܐܡܪ ܗܐ ܗܘܐ ܫܘܘܙܒܐ ܘܚܝܠܐ ܘܡܠܟܘܬܐ ܕܐܠܗܢ ܕܐܬܪܡܝ ܡܤܘܪܐ ܕܐܚܝܢ ܗܘ ܕܡܤܪ ܗܘܐ ܠܗܘܢ ܠܠܝܐ ܘܐܝܡܡܐ ܩܕܡ ܐܠܗܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 11, 'ܘܗܢܘܢ ܙܟܘ ܒܕܡܐ ܕܐܡܪܐ ܘܒܝܕ ܡܠܬܐ ܕܤܗܕܘܬܗ ܘܠܐ ܐܚܒܘ ܢܦܫܗܘܢ ܥܕܡܐ ܠܡܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 12, 'ܡܛܠܗܢܐ ܫܡܝܐ ܐܬܦܨܚܘ ܘܐܝܠܝܢ ܕܒܗܘܢ ܫܪܝܢ ܘܝ ܠܐܪܥܐ ܘܠܝܡܐ ܥܠ ܕܢܚܬ ܐܟܠܩܪܨܐ ܠܘܬܗܘܢ ܕܐܝܬ ܠܗ ܚܡܬܐ ܪܒܬܐ ܟܕ ܝܕܥ ܕܩܠܝܠ ܙܒܢܐ ܐܝܬ ܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 13, 'ܘܟܕ ܚܙܐ ܬܢܝܢܐ ܕܐܬܪܡܝ ܥܠ ܐܪܥܐ ܪܕܦ ܠܐܢܬܬܐ ܐܝܕܐ ܕܝܠܕܬ ܕܟܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 14, 'ܘܐܬܝܗܒ ܠܐܢܬܬܐ ܬܪܝܢ ܓܦܝܢ ܕܢܫܪܐ ܪܒܐ ܕܬܦܪܚܝ ܠܚܘܪܒܐ ܠܕܘܟܬܗ ܠܡܬܬܪܤܝܘ ܬܡܢ ܥܕܢ ܥܕܢܝܢ ܘܦܠܓܘܬ ܥܕܢ ܡܢ ܩܕܡ ܐܦܘܗܝ ܕܚܘܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 15, 'ܘܐܪܡܝ ܚܘܝܐ ܡܢ ܦܘܡܗ ܒܬܪ ܐܢܬܬܐ ܡܝܐ ܐܝܟ ܢܗܪܐ ܕܫܩܝܠܬ ܡܝܐ ܢܥܒܕܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 16, 'ܘܥܕܪܬ ܐܪܥܐ ܠܐܢܬܬܐ ܘܦܬܚܬ ܐܪܥܐ ܦܘܡܗ ܘܒܠܥܬܗ ܠܢܗܪܐ ܗܘ ܕܐܪܡܝ ܬܢܝܢܐ ܡܢ ܦܘܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 17, 'ܘܪܓܙ ܬܢܝܢܐ ܥܠ ܐܢܬܬܐ ܘܐܙܠ ܠܡܥܒܕ ܩܪܒܐ ܥܡ ܫܪܟܐ ܕܙܪܥܗ ܗܠܝܢ ܕܢܛܪܝܢ ܦܘܩܕܢܘܗܝ ܕܐܠܗܐ ܘܐܝܬ ܠܗܘܢ ܤܗܕܘܬܗ ܕܝܫܘܥ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 1, 'ܘܩܡܬ ܥܠ ܚܠܐ ܕܝܡܐ ܘܚܙܝܬ ܕܤܠܩܐ ܚܝܘܬܐ ܡܢ ܝܡܐ ܕܐܝܬ ܠܗ ܥܤܪ ܩܪܢܢ ܘܫܒܥ ܩܪܩܦܢ ܘܥܠ ܩܪܢܬܗ ܥܤܪܐ ܬܐܓܝܢ ܘܥܠ ܩܪܩܦܬܗ ܫܡܐ ܕܓܘܕܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 2, 'ܘܚܝܘܬܐ ܗܝ ܕܚܙܝܬ ܕܡܘܬܐ ܗܘܬ ܕܢܡܪܐ ܘܪܓܠܝܗ ܐܝܟ ܕܕܒܐ ܘܦܘܡܗ ܐܝܟ ܕܐܪܝܘܬܐ ܘܝܗܒ ܠܗ ܬܢܝܢܐ ܚܝܠܗ ܘܟܘܪܤܝܗ ܘܫܘܠܛܢܐ ܪܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 3, 'ܘܚܕܐ ܡܢ ܩܪܩܦܬܗ ܐܝܟ ܦܥܝܥܬܐ ܠܡܘܬܐ ܘܡܚܘܬܐ ܕܡܘܬܗ ܐܬܐܤܝܬ ܘܐܬܕܡܪܬ ܟܠܗ ܐܪܥܐ ܒܬܪ ܚܝܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 4, 'ܘܤܓܕܘ ܠܬܢܝܢܐ ܕܝܗܒ ܫܘܠܛܢܐ ܠܚܝܘܬܐ ܘܤܓܕܘ ܠܚܝܘܬܐ ܠܡܐܡܪ ܡܢܘ ܕܕܡܐ ܠܚܝܘܬܐ ܗܕܐ ܘܡܢܘ ܡܫܟܚ ܠܡܩܪܒܘ ܥܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 5, 'ܘܐܬܝܗܒ ܠܗ ܦܘܡܐ ܕܡܡܠܠ ܪܘܪܒܬܐ ܘܓܘܕܦܐ ܘܐܬܝܗܒ ܠܗ ܫܘܠܛܢܐ ܠܡܥܒܕ ܝܪܚܐ ܐܪܒܥܝܢ ܘܬܪܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 6, 'ܘܦܬܚܬ ܦܘܡܗ ܠܡܓܕܦܘ ܩܕܡ ܐܠܗܐ ܕܬܓܕܦܝ ܒܫܡܐ ܘܒܡܫܪܝܐ ܕܐܝܠܝܢ ܕܫܪܝܢ ܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 7, 'ܘܐܬܝܗܒ ܠܗ ܠܡܥܒܕ ܩܪܒܐ ܥܡ ܩܕܝܫܐ ܘܠܡܙܟܐ ܐܢܘܢ ܘܐܬܝܗܒ ܠܗ ܫܘܠܛܢܐ ܥܠ ܟܠܗܝܢ ܫܪܒܬܐ ܘܐܡܘܬܐ ܘܠܫܢܐ ܘܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 8, 'ܘܢܤܓܕܘܢ ܠܗ ܟܠܗܘܢ ܥܡܘܪܝܗ ܕܐܪܥܐ ܗܢܘܢ ܐܝܠܝܢ ܕܠܐ ܟܬܝܒܝܢ ܒܟܬܒܐ ܕܚܝܐ ܗܘ ܕܐܡܪܐ ܩܛܝܠܐ ܩܕܡ ܬܪܡܝܬܗ ܕܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 9, 'ܡܢ ܕܐܝܬ ܠܗ ܐܕܢܐ ܢܫܡܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 10, 'ܡܢ ܕܒܫܒܝܐ ܡܘܒܠ ܒܫܒܝܐ ܐܙܠ ܘܐܝܢܐ ܕܒܚܪܒܐ ܩܛܠ ܒܚܪܒܐ ܢܬܩܛܠ ܗܪܟܐ ܗܝ ܗܝܡܢܘܬܐ ܘܡܤܝܒܪܢܘܬܐ ܕܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 11, 'ܘܚܙܝܬ ܚܝܘܬܐ ܐܚܪܬܐ ܕܤܠܩܐ ܡܢ ܐܪܥܐ ܘܐܝܬ ܗܘܐ ܠܗ ܬܪܬܝܢ ܩܪܢܢ ܘܕܡܝܐ ܠܐܡܪܐ ܘܡܡܠܠܐ ܗܘܬ ܐܝܟ ܬܢܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 12, 'ܘܫܘܠܛܢܐ ܕܚܝܘܬܐ ܩܕܡܝܬܐ ܟܠܗ ܬܥܒܕ ܩܕܡܘܗܝ ܘܬܥܒܕ ܠܐܪܥܐ ܘܠܕܥܡܪܝܢ ܒܗ ܘܢܤܓܕܘܢ ܠܚܝܘܬܐ ܩܕܡܝܬܐ ܗܝ ܕܐܬܚܠܡܬ ܡܚܘܬܐ ܕܡܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 13, 'ܘܬܥܒܕ ܐܬܘܬܐ ܪܘܪܒܬܐ ܐܝܟܢܐ ܕܢܘܪܐ ܬܥܒܕ ܠܡܚܬ ܡܢ ܫܡܝܐ ܥܠ ܐܪܥܐ ܩܕܡ ܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 14, 'ܘܬܛܥܐ ܠܕܥܡܪܝܢ ܥܠ ܐܪܥܐ ܒܝܕ ܐܬܘܬܐ ܕܐܬܝܗܒ ܠܗ ܠܡܥܒܕ ܩܕܡ ܚܝܘܬܐ ܠܡܐܡܪ ܠܕܥܡܪܝܢ ܥܠ ܐܪܥܐ ܠܡܥܒܕ ܨܠܡܐ ܠܚܝܘܬܐ ܐܝܕܐ ܕܐܝܬ ܠܗ ܡܚܘܬܐ ܕܚܪܒܐ ܘܚܝܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 15, 'ܘܐܬܝܗܒ ܠܗ ܕܬܬܠ ܪܘܚܐ ܠܨܠܡܐ ܕܚܝܘܬܐ ܘܬܥܒܕ ܕܟܠ ܕܠܐ ܢܤܓܕܘܢ ܠܗ ܠܨܠܡܐ ܕܚܝܘܬܐ ܢܬܩܛܠܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 16, 'ܘܬܥܒܕ ܠܟܠܗܘܢ ܙܥܘܪܐ ܘܪܘܪܒܐ ܥܬܝܪܐ ܘܡܤܟܢܐ ܡܪܝܐ ܘܥܒܕܐ ܕܢܬܝܗܒ ܠܗܘܢ ܪܘܫܡܐ ܥܠ ܐܝܕܝܗܘܢ ܕܝܡܝܢܐ ܐܘ ܥܠ ܒܝܬ ܥܝܢܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 17, 'ܕܠܐ ܐܢܫ ܢܙܒܢ ܐܘ ܢܙܒܢ ܬܘܒ ܐܠܐ ܐܝܢܐ ܕܐܝܬ ܥܠܘܗܝ ܪܘܫܡܐ ܕܫܡܐ ܕܚܝܘܬܐ ܐܘ ܡܢܝܢܐ ܕܫܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 18, 'ܗܪܟܐ ܐܝܬܝܗ ܚܟܡܬܐ ܘܕܐܝܬ ܒܗ ܗܘܢܐ ܢܚܫܒܝܘܗܝ ܠܡܢܝܢܐ ܕܚܝܘܬܐ ܡܢܝܢܐ ܗܘ ܓܝܪ ܕܒܪܢܫܐ ܫܬܡܐܐ ܘܫܬܝܢ ܘܫܬ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 1, 'ܘܚܙܝܬ ܘܗܐ ܐܡܪܐ ܩܐܡ ܥܠ ܛܘܪܐ ܕܨܗܝܘܢ ܘܥܡܗ ܡܐܐ ܘܐܪܒܥܝܢ ܘܐܪܒܥܐ ܐܠܦܝܢ ܕܐܝܬ ܥܠܝܗܘܢ ܫܡܗ ܘܫܡܐ ܕܐܒܘܗܝ ܟܬܝܒ ܥܠ ܒܝܬ ܥܝܢܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 2, 'ܘܫܡܥܬ ܩܠܐ ܡܢ ܫܡܝܐ ܐܝܟ ܩܠܐ ܕܡܝܐ ܤܓܝܐܐ ܘܐܝܟ ܩܠܐ ܕܪܥܡܐ ܪܒܐ ܩܠܐ ܐܝܢܐ ܕܫܡܥܬ ܐܝܟ ܩܝܬܪܘܕܐ ܕܢܩܫ ܒܩܝܬܪܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 3, 'ܘܡܫܒܚܝܢ ܐܝܟ ܬܫܒܘܚܬܐ ܚܕܬܐ ܩܕܡ ܟܘܪܤܝܐ ܘܩܕܡ ܐܪܒܥ ܚܝܘܢ ܘܩܕܡ ܩܫܝܫܐ ܘܠܐ ܐܢܫ ܐܬܡܨܝ ܠܡܐܠܦܗ ܠܬܫܒܘܚܬܐ ܐܠܐ ܐܢ ܡܐܐ ܘܐܪܒܥܝܢ ܘܐܪܒܥܐ ܐܠܦܝܢ ܙܒܝܢܝ ܡܢ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 4, 'ܗܠܝܢ ܐܢܘܢ ܐܝܠܝܢ ܕܥܡ ܢܫܐ ܠܐ ܐܬܛܘܫܘ ܒܬܘܠܐ ܓܝܪ ܐܝܬܝܗܘܢ ܗܠܝܢ ܕܢܩܦܘܗܝ ܠܐܡܪܐ ܟܠ ܟܪ ܕܢܐܙܠ ܗܠܝܢ ܐܙܕܒܢܘ ܡܢ ܐܢܫܐ ܪܫܝܬܐ ܠܐܠܗܐ ܘܠܐܡܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 5, 'ܕܒܦܘܡܗܘܢ ܠܐ ܐܫܬܟܚܬ ܕܓܠܘܬܐ ܕܠܐ ܡܘܡ ܓܝܪ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 6, 'ܘܚܙܝܬ ܐܚܪܢܐ ܡܠܐܟܐ ܕܦܪܚ ܡܨܥܬ ܫܡܝܐ ܘܐܝܬ ܠܗ ܥܠܘܗܝ ܤܒܪܬܐ ܕܠܥܠܡ ܠܡܤܒܪܘ ܥܠ ܝܬܒܝ ܐܪܥܐ ܘܥܠ ܟܠ ܥܡ ܘܐܡܘܢ ܘܫܪܒܢ ܘܠܫܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 7, 'ܠܡܐܡܪ ܒܩܠܐ ܪܒܐ ܕܚܠܘ ܡܢ ܐܠܗܐ ܘܗܒܘ ܠܗ ܬܫܒܘܚܬܐ ܡܛܠ ܕܐܬܬ ܫܥܬܐ ܕܕܝܢܗ ܘܤܓܘܕܘ ܠܕܥܒܕ ܫܡܝܐ ܘܐܪܥܐ ܘܝܡܐ ܘܥܝܢܬܐ ܕܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 8, 'ܘܐܚܪܢܐ ܕܬܪܝܢ ܢܩܝܦ ܗܘܐ ܠܗ ܘܐܡܪ ܢܦܠܬ ܢܦܠܬ ܒܒܝܠ ܪܒܬܐ ܐܝܕܐ ܕܡܢ ܚܡܬܐ ܕܙܢܝܘܬܗ ܐܫܩܝܬ ܠܟܠܗܘܢ ܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 9, 'ܘܐܚܪܢܐ ܡܠܐܟܐ ܕܬܠܬܐ ܢܩܦ ܠܗܘܢ ܠܡܐܡܪ ܒܩܠܐ ܪܒܐ ܐܝܢܐ ܕܤܓܕ ܠܚܝܘܬܐ ܘܠܨܠܡܗ ܘܫܩܠ ܪܘܫܡܗ ܒܝܬ ܥܝܢܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 10, 'ܐܦ ܗܘ ܢܫܬܐ ܡܢ ܚܡܪܐ ܕܚܡܬܗ ܕܡܪܝܐ ܕܡܙܝܓ ܕܠܐ ܚܠܛܐ ܒܟܤܐ ܕܪܘܓܙܗ ܘܢܫܬܢܩ ܒܢܘܪܐ ܘܟܒܪܝܬܐ ܩܕܡ ܡܠܐܟܐ ܩܕܝܫܐ ܘܩܕܡ ܐܡܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 11, 'ܘܬܢܢܐ ܕܬܫܢܝܩܗܘܢ ܠܥܠܡ ܥܠܡܝܢ ܢܤܩ ܘܠܝܬ ܠܗܘܢ ܢܦܐܫܐ ܐܝܡܡܐ ܘܠܠܝܐ ܐܝܠܝܢ ܕܤܓܕܝܢ ܠܚܝܘܬܐ ܘܠܨܠܡܗ ܘܠܡܢ ܕܫܩܠ ܪܘܫܡܐ ܕܫܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 12, 'ܗܪܟܐ ܐܝܬܝܗ ܡܤܝܒܪܢܘܬܐ ܕܩܕܝܫܐ ܐܝܠܝܢ ܕܢܛܪܘ ܦܘܩܕܢܘܗܝ ܕܐܠܗܐ ܘܗܝܡܢܘܬܗ ܕܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 13, 'ܘܫܡܥܬ ܩܠܐ ܡܢ ܫܡܝܐ ܕܐܡܪ ܟܬܘܒ ܛܘܒܝܗܘܢ ܠܡܝܬܐ ܐܝܠܝܢ ܕܥܢܕܘ ܒܡܪܢ ܡܢ ܗܫܐ ܐܝܢ ܐܡܪ ܪܘܚܐ ܡܛܠ ܕܢܬܬܢܝܚܘܢ ܡܢ ܥܡܠܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 14, 'ܘܗܐ ܥܢܢܐ ܚܘܪܬܐ ܘܥܠ ܥܢܢܐ ܝܬܒ ܕܡܘܬܐ ܕܒܪܢܫܐ ܘܐܝܬ ܠܗ ܥܠ ܪܫܗ ܟܠܝܠܐ ܕܕܗܒܐ ܘܥܠ ܐܝܕܗ ܡܓܠܬܐ ܚܪܝܦܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 15, 'ܘܐܚܪܢܐ ܡܠܐܟܐ ܢܦܩ ܡܢ ܗܝܟܠܐ ܘܩܥܐ ܒܩܠܐ ܪܒܐ ܠܕܝܬܒ ܥܠ ܥܢܢܐ ܫܕܪ ܡܓܠܬܟ ܘܚܨܘܕ ܡܛܠ ܕܐܬܬ ܫܥܬܐ ܠܡܚܨܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 16, 'ܘܐܪܡܝ ܗܘ ܕܝܬܒ ܥܠ ܥܢܢܐ ܡܓܠܬܗ ܥܠ ܐܪܥܐ ܘܐܬܚܨܕܬ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 17, 'ܘܐܚܪܢܐ ܡܠܐܟܐ ܢܦܩ ܡܢ ܗܝܟܠܐ ܕܒܫܡܝܐ ܘܥܠܘܗܝ ܐܝܬ ܡܓܠܬܐ ܚܪܝܦܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 18, 'ܘܐܚܪܢܐ ܡܠܐܟܐ ܢܦܩ ܡܢ ܡܕܒܚܐ ܕܐܝܬ ܠܗ ܫܘܠܛܢܐ ܥܠ ܢܘܪܐ ܘܩܥܐ ܒܩܠܐ ܪܒܐ ܠܕܐܝܬ ܠܗ ܡܓܠܬܐ ܚܪܝܦܬܐ ܫܕܪ ܐܢܬ ܡܓܠܬܟ ܚܪܝܦܬܐ ܘܩܛܘܦ ܠܤܓܘܠܐ ܕܟܪܡܗ ܕܐܪܥܐ ܡܛܠ ܕܪܒܝ ܥܢܒܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 19, 'ܘܐܪܡܝ ܡܠܐܟܐ ܡܓܠܬܗ ܥܠ ܐܪܥܐ ܘܩܛܦ ܠܟܪܡܗ ܕܐܪܥܐ ܘܐܪܡܝ ܒܡܥܨܪܬܐ ܪܒܬܐ ܕܚܡܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 20, 'ܘܐܬܬܕܝܫܬ ܡܥܨܪܬܐ ܠܒܪ ܡܢ ܡܕܝܢܬܐ ܘܢܦܩ ܕܡܐ ܡܢ ܡܥܨܪܬܐ ܥܕܡܐ ܠܦܓܘܕܐ ܕܪܟܫܐ ܥܠ ܐܠܦ ܘܡܐܬܝܢ ܐܤܛܕܘܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 1, 'ܘܚܙܝܬ ܐܚܪܬܐ ܐܬܐ ܒܫܡܝܐ ܪܒܬܐ ܘܬܡܝܗܬܐ ܡܠܐܟܐ ܕܐܝܬ ܥܠܝܗܘܢ ܡܚܘܬܐ ܫܒܥ ܐܚܪܝܬܐ ܕܒܗܝܢ ܐܫܬܡܠܝܬ ܚܡܬܗ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 2, 'ܘܚܙܝܬ ܐܝܟ ܝܡܐ ܕܙܓܘܓܝܬܐ ܕܦܬܝܟܐ ܒܢܘܪܐ ܘܠܕܙܟܘ ܡܢ ܚܝܘܬܐ ܘܡܢ ܨܠܡܗ ܘܡܢ ܡܢܝܢܐ ܕܫܡܗ ܕܩܝܡܝܢ ܠܥܠ ܡܢ ܝܡܐ ܕܙܓܘܓܝܬܐ ܘܐܝܬ ܥܠܝܗܘܢ ܩܝܬܪܘܗܝ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 3, 'ܘܡܫܒܚܝܢ ܬܫܒܘܚܬܐ ܕܡܘܫܐ ܥܒܕܗ ܕܐܠܗܐ ܘܬܫܒܘܚܬܐ ܕܐܡܪܐ ܘܐܡܪܝܢ ܪܘܪܒܝܢ ܘܬܡܝܗܝܢ ܥܒܕܝܟ ܡܪܝܐ ܐܠܗܐ ܐܚܝܕ ܟܠ ܟܐܢܝܢ ܘܫܪܝܪܝܢ ܥܒܕܝܟ ܡܠܟܐ ܕܥܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 4, 'ܡܢ ܠܐ ܢܕܚܠ ܠܟ ܡܪܝܐ ܘܢܫܒܚ ܠܫܡܟ ܡܛܠ ܕܐܢܬ ܗܘ ܒܠܚܘܕ ܚܤܝܐ ܡܛܠ ܕܟܠܗܘܢ ܥܡܡܐ ܢܐܬܘܢ ܘܢܤܓܕܘܢ ܩܕܡܝܟ ܡܛܠ ܕܬܪܝܨ ܐܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 5, 'ܘܡܢ ܒܬܪ ܗܠܝܢ ܚܙܝܬ ܘܐܬܦܬܚ ܗܝܟܠܐ ܕܡܫܟܢܐ ܕܤܗܕܘܬܐ ܒܫܡܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 6, 'ܘܢܦܩܘ ܫܒܥܐ ܡܠܐܟܝܢ ܡܢ ܗܝܟܠܐ ܗܢܘܢ ܕܐܝܬ ܥܠܝܗܘܢ ܫܒܥ ܡܚܘܢ ܟܕ ܠܒܝܫܝܢ ܟܬܢܐ ܕܟܝܐ ܘܢܗܝܪܐ ܘܐܤܝܪܝܢ ܥܠ ܚܕܝܝܗܘܢ ܐܤܪܐ ܕܕܗܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 7, 'ܘܚܕܐ ܡܢ ܐܪܒܥ ܚܝܘܬܐ ܝܗܒܬ ܠܫܒܥܐ ܡܠܐܟܝܢ ܫܒܥ ܙܒܘܪܝܢ ܕܡܠܝܢ ܚܡܬܗ ܕܐܠܗܐ ܕܐܝܬܘܗܝ ܚܝܐ ܠܥܠܡ ܥܠܡܝܢ ܐܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 8, 'ܘܐܬܡܠܝ ܗܝܟܠܐ ܡܢ ܬܢܢܐ ܕܬܫܒܘܚܬܗ ܕܐܠܗܐ ܘܡܢ ܚܝܠܗ ܘܠܝܬ ܕܡܨܐ ܗܘܐ ܠܡܥܠ ܠܗܝܟܠܐ ܥܕܡܐ ܕܢܫܬܡܠܝܢ ܫܒܥ ܡܚܘܢ ܕܫܒܥܐ ܡܠܐܟܝܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 1, 'ܘܫܡܥܬ ܩܠܐ ܪܒܐ ܡܢ ܗܝܟܠܐ ܕܐܡܪ ܠܫܒܥܐ ܡܠܐܟܝܢ ܙܠܘ ܘܐܫܘܕܘ ܫܒܥ ܙܒܘܪܝܢ ܕܚܡܬܗ ܕܐܠܗܐ ܥܠ ܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 2, 'ܘܐܙܠ ܩܕܡܝܐ ܘܐܫܕ ܙܒܘܪܗ ܥܠ ܐܪܥܐ ܘܗܘܐ ܫܘܚܢܐ ܒܝܫܐ ܘܟܐܒܢܐ ܥܠ ܐܢܫܐ ܕܐܝܬ ܠܗܘܢ ܪܘܫܡܐ ܕܚܝܘܬܐ ܘܐܝܠܝܢ ܕܤܓܕܝܢ ܠܨܠܡܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 3, 'ܘܡܠܐܟܐ ܕܬܪܝܢ ܐܫܕ ܙܒܘܪܗ ܒܝܡܐ ܘܗܘܐ ܝܡܐ ܐܝܟ ܡܝܬܐ ܘܟܠ ܢܦܫܐ ܚܝܬܐ ܡܝܬܬ ܒܝܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 4, 'ܘܡܠܐܟܐ ܕܬܠܬܐ ܐܫܕ ܙܒܘܪܗ ܒܢܗܪܘܬܐ ܘܒܥܝܢܬܐ ܕܡܝܐ ܘܗܘܘ ܕܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 5, 'ܘܫܡܥܬ ܠܡܠܐܟܐ ܕܡܝܐ ܕܐܡܪ ܙܕܝܩ ܐܢܬ ܗܘ ܕܐܝܬܘܗܝ ܘܐܝܬܘܗܝ ܗܘܐ ܘܚܤܝܐ ܕܗܠܝܢ ܕܢܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 6, 'ܡܛܠ ܕܕܡܐ ܕܢܒܝܐ ܘܕܩܕܝܫܐ ܐܫܕܘ ܘܕܡܐ ܝܗܒܬ ܠܗܘܢ ܠܡܫܬܐ ܫܘܝܢ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 7, 'ܘܫܡܥܬ ܠܡܕܒܚܐ ܕܐܡܪ ܐܝܢ ܡܪܝܐ ܐܠܗܐ ܐܚܝܕ ܟܠ ܫܪܝܪܝܢ ܘܙܕܝܩܝܢ ܕܝܢܝܟ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 8, 'ܘܡܠܐܟܐ ܕܐܪܒܥܐ ܐܫܕ ܙܒܘܪܗ ܥܠ ܫܡܫܐ ܘܐܬܝܗܒ ܠܗ ܕܢܚܡ ܠܒܢܝܢܫܐ ܒܢܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 9, 'ܘܐܬܚܡܡܘ ܒܢܝܢܫܐ ܒܚܘܡܐ ܪܒܐ ܘܓܕܦܘ ܠܫܡܐ ܕܐܠܗܐ ܕܐܝܬ ܠܗ ܫܘܠܛܢܐ ܥܠ ܡܚܘܬܐ ܗܠܝܢ ܘܠܐ ܬܒܘ ܠܡܬܠ ܠܗ ܬܫܒܘܚܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 10, 'ܘܡܠܐܟܐ ܕܚܡܫܐ ܐܫܕ ܙܒܘܪܗ ܥܠ ܟܘܪܤܝܗ ܕܚܝܘܬܐ ܘܗܘܬ ܡܠܟܘܬܗ ܚܫܘܟܬܐ ܘܡܠܥܤܝܢ ܗܘܘ ܠܫܢܝܗܘܢ ܡܢ ܟܐܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 11, 'ܘܓܕܦܘ ܠܫܡܐ ܕܐܠܗܐ ܕܫܡܝܐ ܡܢ ܟܐܒܝܗܘܢ ܘܡܢ ܫܘܚܢܝܗܘܢ ܘܠܐ ܬܒܘ ܡܢ ܥܒܕܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 12, 'ܘܡܠܐܟܐ ܕܫܬܐ ܐܫܕ ܙܒܘܪܗ ܥܠ ܢܗܪܐ ܪܒܐ ܦܪܬ ܘܝܒܫܘ ܡܘܗܝ ܕܬܬܛܝܒ ܐܘܪܚܐ ܕܡܠܟܐ ܡܢ ܡܕܢܚܝ ܫܡܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 13, 'ܘܚܙܝܬ ܡܢ ܦܘܡܗ ܕܬܢܝܢܐ ܘܡܢ ܦܘܡܗ ܕܚܝܘܬܐ ܘܡܢ ܦܘܡܗ ܕܢܒܝܐ ܕܓܠܐ ܪܘܚܐ ܬܠܬ ܠܐ ܕܟܝܬܐ ܐܝܟ ܐܘܪܕܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 14, 'ܐܝܬܝܗܝܢ ܓܝܪ ܪܘܚܐ ܕܫܐܕܐ ܐܝܠܝܢ ܕܥܒܕܢ ܐܬܘܬܐ ܕܐܙܠܢ ܥܠ ܡܠܟܐ ܕܬܐܒܝܠ ܠܡܟܢܫܘ ܐܢܘܢ ܠܩܪܒܐ ܕܝܘܡܐ ܗܘ ܪܒܐ ܕܐܠܗܐ ܐܚܝܕ ܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 15, 'ܗܐ ܐܬܐ ܐܝܟ ܓܢܒܐ ܛܘܒܘܗܝ ܠܗܘ ܕܥܝܪ ܘܢܛܪ ܡܐܢܘܗܝ ܕܠܐ ܥܪܛܠ ܢܗܠܟ ܘܢܚܙܘܢ ܒܗܬܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 16, 'ܘܢܟܢܫ ܐܢܘܢ ܠܐܬܪܐ ܕܡܬܩܪܐ ܥܒܪܐܝܬ ܡܓܕܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 17, 'ܘܡܠܐܟܐ ܕܫܒܥܐ ܐܫܕ ܙܒܘܪܗ ܒܐܐܪ ܘܢܦܩ ܩܠܐ ܪܒܐ ܡܢ ܗܝܟܠܐ ܡܢ ܩܕܡ ܟܘܪܤܝܐ ܕܐܡܪ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 18, 'ܘܗܘܘ ܒܪܩܐ ܘܪܥܡܐ ܘܢܘܕܐ ܗܘܐ ܪܒܐ ܕܐܟܘܬܗ ܠܐ ܗܘܐ ܡܢ ܕܗܘܘ ܒܢܝܢܫܐ ܥܠ ܐܪܥܐ ܕܐܝܟ ܗܢܐ ܙܘܥܐ ܗܟܢܐ ܪܒ ܗܘܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 19, 'ܘܗܘܬ ܡܕܝܢܬܐ ܪܒܬܐ ܠܬܠܬ ܡܢܘܢ ܘܡܕܝܢܬܐ ܕܥܡܡܐ ܢܦܠܝ ܘܒܒܝܠ ܪܒܬܐ ܐܬܕܟܪܬ ܩܕܡ ܐܠܗܐ ܠܡܬܠ ܠܗ ܟܤܐ ܕܚܡܪܐ ܕܚܡܬܗ ܘܕܪܘܓܙܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 20, 'ܘܟܠ ܓܙܪܬܐ ܥܪܩܬ ܘܛܘܪܐ ܠܐ ܐܫܬܟܚܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 21, 'ܘܒܪܕܐ ܪܒܐ ܐܝܟ ܟܟܪܐ ܢܚܬ ܡܢ ܫܡܝܐ ܥܠ ܒܢܝܢܫܐ ܘܓܕܦܘ ܒܢܝܢܫܐ ܠܐܠܗܐ ܥܠ ܡܚܘܬܐ ܕܒܪܕܐ ܡܛܠ ܕܪܒܐ ܗܝ ܡܚܘܬܗ ܛܒ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 1, 'ܘܐܬܐ ܚܕ ܡܢ ܫܒܥܐ ܡܠܐܟܐ ܕܐܝܬ ܥܠܝܗܘܢ ܫܒܥ ܙܒܘܪܝܢ ܘܡܠܠ ܥܡܝ ܠܡܐܡܪ ܬܐ ܒܬܪܝ ܐܚܘܝܟ ܕܝܢܐ ܕܙܢܝܬܐ ܕܝܬܒܐ ܥܠ ܡܝܐ ܤܓܝܐܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 2, 'ܕܥܡܗ ܙܢܝܘ ܡܠܟܝܗ ܕܐܪܥܐ ܘܪܘܝܘ ܟܠܗܘܢ ܥܡܘܪܝܗ ܕܐܪܥܐ ܡܢ ܚܡܪܐ ܕܙܢܝܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 3, 'ܘܐܦܩܢܝ ܠܚܘܪܒܐ ܒܪܘܚ ܘܚܙܝܬ ܐܢܬܬܐ ܕܝܬܒܐ ܥܠ ܚܝܘܬܐ ܤܘܡܩܬܐ ܕܡܠܝܐ ܫܡܗܐ ܕܓܘܕܦܐ ܕܐܝܬ ܠܗ ܪܫܐ ܫܒܥܐ ܩܪܢܬܐ ܕܝܢ ܥܤܪ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 4, 'ܘܐܢܬܬܐ ܐܝܬ ܗܘܐ ܕܡܥܛܦܐ ܐܪܓܘܢܐ ܘܙܚܘܪܝܬܐ ܕܡܕܗܒܢ ܒܕܗܒܐ ܘܟܐܦܐ ܛܒܬܐ ܘܡܪܓܢܝܬܐ ܘܐܝܬ ܠܗ ܟܤܐ ܕܕܗܒܐ ܥܠ ܐܝܕܗ ܘܡܠܐ ܛܡܐܘܬܐ ܘܤܘܝܒܐ ܕܙܢܝܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 5, 'ܘܥܠ ܒܝܬ ܥܝܢܝܗ ܟܬܝܒ ܐܪܙܐ ܒܒܝܠ ܪܒܬܐ ܐܡܐ ܕܙܢܝܬܐ ܘܕܤܘܝܒܝܗ ܕܐܪܥܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 6, 'ܘܚܙܝܬ ܐܢܬܬܐ ܕܪܘܝܐ ܡܢ ܕܡܐ ܕܩܕܝܫܐ ܘܡܢ ܕܡܐ ܕܤܗܕܘܗܝ ܕܝܫܘܥ ܘܐܬܕܡܪܬ ܕܘܡܪܐ ܪܒܐ ܟܕ ܚܙܝܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 7, 'ܘܐܡܪ ܠܝ ܡܠܐܟܐ ܠܡܢܐ ܐܬܕܡܪܬ ܐܢܐ ܐܡܪ ܐܢܐ ܠܟ ܪܐܙܐ ܕܐܢܬܬܐ ܘܕܚܝܘܬܐ ܕܛܥܝܢܐ ܠܗ ܕܐܝܬ ܠܗ ܫܒܥܐ ܪܫܝܢ ܘܥܤܪ ܩܪܢܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 8, 'ܚܝܘܬܐ ܕܚܙܝܬ ܐܝܬܝܗ ܗܘܬ ܘܠܝܬܝܗ ܥܬܝܕܐ ܕܬܤܩ ܡܢ ܝܡܐ ܘܠܐܒܕܢܐ ܐܙܠܐ ܘܢܬܕܡܪܘܢ ܥܡܪܝ ܥܠ ܐܪܥܐ ܗܢܘܢ ܕܠܐ ܟܬܝܒܝܢ ܫܡܗܝܗܘܢ ܒܤܦܪܐ ܕܚܝܐ ܡܢ ܬܪܡܝܬܗ ܕܥܠܡܐ ܕܚܙܝܢ ܚܝܘܬܐ ܕܐܝܬܝܗ ܗܘܬ ܘܠܝܬܝܗ ܘܩܪܒܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 9, 'ܗܪܟܐ ܗܘܢܐ ܠܕܐܝܬ ܠܗ ܚܟܡܬܐ ܫܒܥܐ ܪܫܝܢ ܫܒܥܐ ܐܢܘܢ ܛܘܪܝܢ ܐܝܟܐ ܕܝܬܒܐ ܐܢܬܬܐ ܥܠܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 10, 'ܘܡܠܟܐ ܫܒܥܐ ܐܢܘܢ ܚܡܫܐ ܢܦܠܘ ܘܚܕ ܐܝܬܘܗܝ ܗܘ ܐܚܪܢܐ ܠܐ ܥܕܟܝܠ ܐܬܐ ܘܡܐ ܕܐܬܐ ܩܠܝܠ ܝܗܝܒ ܠܗ ܠܡܟܬܪܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 11, 'ܘܬܢܝܢܐ ܘܚܝܘܬܐ ܗܝ ܕܐܝܬܝܗ ܘܠܝܬܝܗ ܘܗܝ ܕܬܡܢܝܐ ܘܡܢ ܫܒܥܐ ܗܝ ܘܠܐܒܕܢܐ ܐܙܠܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 12, 'ܘܥܤܪ ܩܪܢܢ ܕܚܙܝܬ ܥܤܪܐ ܡܠܟܝܢ ܐܢܘܢ ܐܝܠܝܢ ܕܡܠܟܘܬܐ ܠܐ ܥܕܟܝܠ ܢܤܒܘ ܐܠܐ ܫܘܠܛܢܐ ܐܝܟ ܡܠܟܐ ܚܕܐ ܫܥܬܐ ܫܩܠܝܢ ܥܡ ܚܝܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 13, 'ܗܠܝܢ ܚܕ ܨܒܝܢܐ ܐܝܬ ܠܗܘܢ ܘܚܝܠܐ ܘܫܘܠܛܢܐ ܕܝܠܗܘܢ ܠܚܝܘܬܐ ܝܗܒܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 14, 'ܗܠܝܢ ܥܡ ܐܡܪܐ ܢܩܪܒܘܢ ܘܐܡܪܐ ܢܙܟܐ ܐܢܘܢ ܡܛܠ ܕܡܪܐ ܗܘ ܕܡܪܘܬܐ ܘܡܠܟ ܡܠܟܐ ܘܕܥܡܗ ܩܪܝܐ ܘܓܒܝܐ ܘܡܗܝܡܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 15, 'ܘܐܡܪ ܠܝ ܡܝܐ ܕܚܙܝܬ ܕܥܠܝܗܘܢ ܝܬܒܐ ܙܢܝܬܐ ܥܡܡܐ ܘܟܢܫܐ ܘܐܡܘܬܐ ܘܠܫܢܐ ܐܝܬܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 16, 'ܘܥܤܪ ܩܪܢܬܐ ܕܚܙܝܬ ܠܚܝܘܬܐ ܗܠܝܢ ܢܤܢܝܢ ܠܙܢܝܬܐ ܘܚܪܒܬܐ ܘܥܪܛܠܝܬܐ ܢܥܒܕܘܢܗ ܘܒܤܪܗ ܢܐܟܠܘܢ ܘܢܘܩܕܘܢܗ ܒܢܘܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 17, 'ܐܠܗܐ ܓܝܪ ܝܗܒ ܒܠܒܘܬܗܘܢ ܕܢܥܒܕܘܢ ܨܒܝܢܗ ܘܢܥܒܕܘܢ ܨܒܝܢܗܘܢ ܚܕ ܘܢܬܠܘܢ ܡܠܟܘܬܗܘܢ ܠܚܝܘܬܐ ܗܝ ܥܕܡܐ ܕܢܫܬܡܠܝܢ ܡܠܘܗܝ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 18, 'ܘܐܢܬܬܐ ܐܝܕܐ ܕܚܙܝܬ ܡܕܝܢܬܐ ܪܒܬܐ ܐܝܕܐ ܕܐܝܬ ܠܗ ܡܠܟܘܬܐ ܥܠ ܡܠܟܝܗ ܕܐܪܥܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 1, 'ܘܡܢ ܒܬܪ ܗܠܝܢ ܚܙܝܬ ܐܚܪܢܐ ܡܠܐܟܐ ܕܢܚܬ ܡܢ ܫܡܝܐ ܕܐܝܬ ܠܗ ܫܘܠܛܢܐ ܪܒܐ ܘܐܪܥܐ ܢܗܪܬ ܡܢ ܬܫܒܘܚܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 2, 'ܘܩܥܐ ܒܩܠܐ ܪܒܐ ܢܦܠܬ ܢܦܠܬ ܒܒܝܠ ܪܒܬܐ ܘܗܘܬ ܡܥܡܪܐ ܠܫܐܕܐ ܘܢܛܘܪܬܐ ܠܟܠ ܪܘܚܐ ܠܐ ܕܟܝܬܐ ܘܤܢܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 3, 'ܡܛܠ ܕܡܢ ܚܡܪܐ ܕܙܢܝܘܬܗ ܡܙܓܬ ܠܟܠܗܘܢ ܥܡܡܐ ܘܡܠܟܝܗ ܕܐܪܥܐ ܥܡܗ ܙܢܝܘ ܘܬܓܪܐ ܕܐܪܥܐ ܡܢ ܚܝܠܐ ܕܫܢܝܗ ܥܬܪܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 4, 'ܘܫܡܥܬ ܐܚܪܢܐ ܩܠܐ ܡܢ ܫܡܝܐ ܕܐܡܪ ܦܘܩܘ ܡܢ ܓܘܗ ܥܡܝ ܕܠܐ ܬܫܬܘܬܦܘܢ ܒܚܛܗܝܗ ܕܠܡܐ ܬܤܒܘܢ ܡܢ ܡܚܘܬܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 5, 'ܡܛܠ ܕܕܒܩܘ ܒܗ ܚܛܗܐ ܥܕܡܐ ܠܫܡܝܐ ܘܐܬܕܟܪ ܐܠܗܐ ܥܘܠܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 6, 'ܦܘܪܥܘܗ ܐܝܟܢܐ ܕܐܦ ܗܝ ܦܪܥܬ ܘܥܘܦܘ ܠܗ ܐܥܦܐ ܥܠ ܥܒܕܝܗ ܒܟܤܐ ܗܘ ܕܡܙܓܬ ܡܙܘܓܘ ܠܗ ܐܥܦܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 7, 'ܥܠ ܡܕܡ ܕܫܒܚܬ ܢܦܫܗ ܘܐܫܬܥܠܝܬ ܕܐܝܟ ܗܟܢ ܫܘܢܩܐ ܘܐܒܠܐ ܡܛܠ ܕܒܠܒܗ ܐܡܪܐ ܕܝܬܒܐ ܐܢܐ ܡܠܟܬܐ ܘܐܪܡܠܬܐ ܠܝܬܝ ܘܐܒܠܐ ܠܐ ܐܚܙܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 8, 'ܡܛܠܗܢܐ ܒܚܕ ܝܘܡܐ ܢܐܬܝܢ ܥܠܝܗ ܡܚܘܬܐ ܡܘܬܐ ܘܐܒܠܐ ܘܟܦܢܐ ܘܒܢܘܪܐ ܬܐܩܕ ܡܛܠ ܕܚܝܠܬܢ ܡܪܝܐ ܕܕܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 9, 'ܘܢܒܟܘܢܗ ܘܢܪܩܕܘܢ ܥܠܝܗ ܡܠܟܝܗ ܕܐܪܥܐ ܗܢܘܢ ܕܙܢܝܘ ܥܡܗ ܘܐܫܬܥܠܝܘ ܡܐ ܕܚܙܝܢ ܬܢܢܐ ܕܝܩܕܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 10, 'ܟܕ ܩܝܡܝܢ ܡܢ ܩܒܘܠ ܡܢ ܕܚܠܬܐ ܕܬܫܢܝܩܗ ܘܢܐܡܪܘܢ ܘܝ ܘܝ ܘܝ ܡܕܝܢܬܐ ܪܒܬܐ ܒܒܝܠ ܡܕܝܢܬܐ ܥܫܝܢܬܐ ܡܛܠ ܕܒܚܕܐ ܫܥܐ ܐܬܐ ܕܝܢܟܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 11, 'ܘܬܓܪܐ ܕܐܪܥܐ ܢܒܟܘܢ ܘܢܬܐܒܠܘܢ ܥܠܝܗ ܘܡܘܒܠܗܘܢ ܠܝܬ ܕܙܒܢ ܬܘܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 12, 'ܡܘܒܠܐ ܕܕܗܒܐ ܘܕܤܐܡܐ ܘܕܟܐܦܐ ܝܩܝܪܬܐ ܘܕܡܪܓܢܝܬܐ ܘܕܒܘܨܐ ܘܕܐܪܓܘܢܐ ܘܫܐܪܝܐ ܕܙܚܘܪܝܬܐ ܘܟܠ ܩܝܤ ܕܒܤܡܐ ܘܟܠ ܡܐܢ ܕܫܢܐ ܘܟܠ ܡܐܢ ܕܩܝܤܐ ܝܩܝܪܐ ܘܢܚܫܐ ܘܦܪܙܠܐ ܘܫܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 13, 'ܘܩܘܢܝܡܘܢ ܘܒܤܡܐ ܘܡܘܪܘܢ ܘܠܒܘܢܬܐ ܘܚܡܪܐ ܘܡܫܚܐ ܘܤܡܝܕܐ ܘܥܪܒܐ ܘܪܟܫܐ ܘܡܪܟܒܬܐ ܘܦܓܪܐ ܘܢܦܫܬܐ ܕܒܢܝܢܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 14, 'ܘܐܒܟܝ ܪܓܬܐ ܕܢܦܫܟܝ ܐܙܠ ܡܢܟܝ ܘܟܠ ܕܫܡܝܢ ܘܫܒܝܚ ܐܙܠ ܡܢܟܝ ܘܠܐ ܬܘܒ ܬܚܙܝܢ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 15, 'ܘܠܐ ܢܫܟܚܘܢ ܐܢܘܢ ܬܓܪܐ ܕܗܠܝܢ ܕܥܬܪܘ ܡܢܗ ܡܢ ܩܒܘܠ ܢܩܘܡܘܢ ܡܢ ܕܚܠܬܐ ܕܫܘܢܩܗ ܟܕ ܒܟܝܢ ܘܐܒܝܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 16, 'ܘܐܡܪܝܢ ܘܝ ܘܝ ܡܕܝܢܬܐ ܪܒܬܐ ܕܡܥܛܦܐ ܒܘܨܐ ܘܐܪܓܘܢܐ ܘܙܚܘܪܝܬܐ ܕܡܕܗܒܢ ܒܕܗܒܐ ܘܟܐܦܐ ܝܩܝܪܬܐ ܘܡܪܓܢܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 17, 'ܡܛܠ ܕܒܚܕܐ ܫܥܐ ܐܤܬܪܩ ܥܘܬܪܐ ܕܐܝܟ ܗܢܐ ܘܟܠ ܡܕܒܪܝ ܐܠܦܐ ܘܟܠ ܐܙܠܝ ܒܐܠܦܐ ܠܕܘܟܝܬܐ ܘܐܠܦܪܐ ܘܟܠ ܕܒܝܡܐ ܦܠܚܝܢ ܡܢ ܪܘܚܩܐ ܩܡܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 18, 'ܘܒܟܐܘܗ ܟܕ ܚܙܝܢ ܬܢܢܐ ܕܝܩܕܢܗ ܘܐܡܪܝܢ ܡܢ ܗܝ ܕܕܡܝܐ ܠܡܕܝܢܬܐ ܪܒܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 19, 'ܘܐܪܡܝܘ ܥܦܪܐ ܥܠ ܪܝܫܝܗܘܢ ܘܩܥܘ ܟܕ ܒܟܝܢ ܘܐܒܝܠܝܢ ܘܐܡܪܝܢ ܘܝ ܘܝ ܡܕܝܢܬܐ ܪܒܬܐ ܐܝܕܐ ܕܒܗ ܥܬܪܘ ܐܝܠܝܢ ܕܐܝܬ ܠܗܘܢ ܐܠܦܐ ܒܝܡܐ ܡܢ ܐܝܩܪܗ ܕܒܚܕܐ ܫܥܐ ܚܪܒܬ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 20, 'ܐܬܦܨܚܘ ܥܠܝܗ ܫܡܝܐ ܘܩܕܝܫܐ ܘܫܠܝܚܐ ܘܢܒܝܐ ܡܛܠ ܕܕܢ ܐܠܗܐ ܕܝܢܟܘܢ ܡܢܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 21, 'ܘܫܩܠ ܚܕ ܡܢ ܡܠܐܟܐ ܚܝܠܬܢܐ ܟܐܦܐ ܪܒܬܐ ܐܝܟ ܪܚܝܐ ܘܐܪܡܝ ܒܝܡܐ ܘܐܡܪ ܗܟܢܐ ܒܚܐܦܐ ܬܫܬܕܐ ܒܒܝܠ ܡܕܝܢܬܐ ܪܒܬܐ ܘܠܐ ܬܫܟܚ ܬܘܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 22, 'ܘܩܠܐ ܕܩܝܬܪܐ ܘܕܫܝܦܘܪܐ ܘܕܙܢܝ ܙܡܪܐ ܘܕܡܙܥܘܩܐ ܠܐ ܢܫܬܡܥ ܒܟܝ ܬܘܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 23, 'ܘܢܘܗܪܐ ܕܫܪܓܐ ܠܐ ܢܬܚܙܐ ܠܟܝ ܬܘܒ ܘܩܠܐ ܕܚܬܢܐ ܘܩܠܐ ܕܟܠܬܐ ܠܐ ܢܫܬܡܥ ܒܟܝ ܬܘܒ ܡܛܠ ܕܬܓܪܝܟܝ ܐܝܬ ܗܘܘ ܪܘܪܒܢܝܗ ܕܐܪܥܐ ܡܛܠ ܕܒܚܪܫܝܟܝ ܐܛܥܝܬܝ ܠܟܠܗܘܢ ܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 24, 'ܘܒܗ ܐܫܬܟܚ ܕܡܐ ܕܢܒܝܐ ܘܩܕܝܫܐ ܕܩܛܝܠܝܢ ܥܠ ܐܪܥܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 1, 'ܘܡܢ ܒܬܪ ܗܠܝܢ ܫܡܥܬ ܩܠܐ ܪܒܐ ܕܟܢܫܐ ܤܓܝܐܐ ܒܫܡܝܐ ܕܐܡܪܝܢ ܗܠܠܘܝܐ ܦܘܪܩܢܐ ܘܬܫܒܘܚܬܐ ܘܚܝܠܐ ܠܐܠܗܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 2, 'ܡܛܠ ܕܫܪܝܪܝܢ ܘܟܐܢܝܢ ܕܝܢܘܗܝ ܡܛܠ ܕܕܢ ܠܙܢܝܬܐ ܪܒܬܐ ܐܝܕܐ ܕܚܒܠܬ ܠܐܪܥܐ ܒܙܢܝܘܬܗ ܘܬܒܥ ܕܡܐ ܕܥܒܕܘܗܝ ܡܢ ܐܝܕܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 3, 'ܕܬܪܬܝܢ ܐܡܪܘ ܗܠܠܘܝܐ ܘܬܢܢܗ ܤܠܩ ܠܥܠܡ ܥܠܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 4, 'ܘܢܦܠܘ ܥܤܪܝܢ ܘܐܪܒܥܐ ܩܫܝܫܝܢ ܘܐܪܒܥ ܚܝܘܢ ܘܤܓܕܘ ܠܐܠܗܢ ܕܝܬܒ ܥܠ ܟܘܪܤܝܐ ܘܐܡܪܝܢ ܐܡܝܢ ܗܠܠܘܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 5, 'ܘܩܠܐ ܡܢ ܟܘܪܤܝܐ ܕܐܡܪ ܫܒܚܘ ܠܐܠܗܢ ܟܠܗܘܢ ܥܒܕܘܗܝ ܘܕܚܠܝ ܫܡܗ ܟܠܗܘܢ ܙܥܘܪܐ ܥܡ ܪܘܪܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 6, 'ܘܫܡܥܬ ܩܠܐ ܐܝܟ ܕܟܢܫܐ ܤܓܝܐܐ ܘܐܝܟ ܩܠܐ ܕܡܝܐ ܤܓܝܐܐ ܘܐܝܟ ܩܠܐ ܕܪܥܡܐ ܚܝܠܬܢܐ ܕܐܡܪܝܢ ܗܠܠܘܝܐ ܡܛܠ ܕܐܡܠܟ ܡܪܝܐ ܐܠܗܐ ܐܚܝܕ ܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 7, 'ܚܕܝܢܢ ܘܡܬܦܨܚܝܢܢ ܢܬܠ ܠܗ ܬܫܒܘܚܬܐ ܡܛܠ ܕܐܬܬ ܡܫܬܘܬܗ ܕܐܡܪܐ ܘܐܢܬܬܗ ܛܝܒܬ ܢܦܫܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 8, 'ܘܐܬܝܗܒ ܠܗ ܕܬܬܥܛܦ ܒܘܨܐ ܕܟܝܐ ܘܢܗܝܪܐ ܒܘܨܐ ܓܝܪ ܬܪܝܨܬܐ ܐܢܝܢ ܕܩܕܝܫܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 9, 'ܘܐܡܪܘ ܠܝ ܟܬܘܒ ܛܘܒܝܗܘܢ ܠܐܝܠܝܢ ܕܠܚܫܡܝܬܐ ܕܡܫܬܘܬܗ ܕܐܡܪܐ ܐܝܬܝܗܘܢ ܩܪܝܐ ܘܐܡܪ ܠܝ ܗܠܝܢ ܡܠܐ ܕܫܪܝܪܢ ܕܐܠܗܐ ܐܝܬܝܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 10, 'ܘܢܦܠܬ ܩܕܡ ܪܓܠܘܗܝ ܘܤܓܕܬ ܠܗ ܘܐܡܪ ܠܝ ܠܐ ܟܢܬܟ ܐܝܬܝ ܘܕܐܚܝܟ ܗܠܝܢ ܕܐܝܬ ܠܗܘܢ ܤܗܕܘܬܐ ܕܝܫܘܥ ܠܐܠܗܐ ܤܓܘܕ ܝܬܝܪܐܝܬ ܤܗܕܘܬܐ ܓܝܪ ܕܝܫܘܥ ܐܝܬܝܗ ܪܘܚܐ ܕܢܒܝܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 11, 'ܘܚܙܝܬ ܫܡܝܐ ܕܦܬܝܚ ܘܗܐ ܤܘܤܝܐ ܚܘܪܐ ܘܕܝܬܒ ܥܠܘܗܝ ܡܬܩܪܐ ܡܗܝܡܢܐ ܘܫܪܝܪܐ ܘܒܟܐܢܘܬܐ ܕܐܢ ܘܡܩܪܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 12, 'ܥܝܢܘܗܝ ܕܝܢ ܐܝܟ ܫܠܗܒܝܬܐ ܕܢܘܪܐ ܘܥܠ ܪܝܫܗ ܬܐܓܐ ܤܓܝܐܐ ܘܐܝܬ ܠܗ ܫܡܐ ܟܬܝܒܐ ܐܝܢܐ ܕܠܐ ܝܕܥ ܐܠܐ ܐܢ ܗܘ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 13, 'ܘܡܥܛܦ ܡܐܢܐ ܕܙܠܝܥ ܒܕܡܐ ܘܡܬܩܪܐ ܫܡܗ ܡܠܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 14, 'ܘܚܝܠܘܬܐ ܕܫܡܝܐ ܢܩܝܦܝܢ ܗܘܘ ܠܗ ܥܠ ܪܟܫܐ ܚܘܪܐ ܘܠܒܝܫܝܢ ܒܘܨܐ ܚܘܪܐ ܘܕܟܝܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 15, 'ܘܡܢ ܦܘܡܗܘܢ ܢܦܩܐ ܚܪܒܐ ܚܪܝܦܬܐ ܕܒܗ ܢܩܛܠܘܢ ܠܥܡܡܐ ܘܗܘ ܢܪܥܐ ܐܢܘܢ ܒܫܒܛܐ ܕܦܪܙܠܐ ܘܗܘ ܕܐܫ ܡܥܨܪܬܐ ܕܪܘܓܙܗ ܕܐܠܗܐ ܐܚܝܕ ܟܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 16, 'ܘܐܝܬ ܠܗ ܥܠ ܡܐܢܘܗܝ ܥܠ ܥܛܡܬܗ ܫܡܐ ܟܬܝܒܐ ܡܠܟܐ ܕܡܠܟܐ ܘܡܪܐ ܕܡܪܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 17, 'ܘܚܙܝܬ ܐܚܪܢܐ ܡܠܐܟܐ ܕܩܐܡ ܒܫܡܫܐ ܘܩܥܐ ܒܩܠܐ ܪܡܐ ܘܐܡܪ ܠܦܪܚܬܐ ܕܦܪܚܐ ܡܨܥܬ ܫܡܝܐ ܐܬܟܢܫܘ ܠܚܫܡܝܬܐ ܪܒܬܐ ܕܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 18, 'ܕܬܐܟܠܘܢ ܒܤܪܐ ܕܡܠܟܐ ܘܒܤܪܐ ܕܪܫܝ ܐܠܦܐ ܘܒܤܪܐ ܕܥܫܝܢܐ ܘܒܤܪܐ ܕܪܟܫܐ ܘܕܐܝܠܝܢ ܕܝܬܒܝܢ ܥܠܝܗܘܢ ܘܒܤܪܐ ܕܚܐܪܐ ܘܕܥܒܕܐ ܘܕܙܥܘܪܐ ܘܕܪܘܪܒܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 19, 'ܘܚܙܝܬ ܠܚܝܘܬܐ ܘܠܚܝܠܘܬܗ ܘܡܠܟܐ ܕܐܪܥܐ ܘܠܦܠܚܝܗܘܢ ܕܡܟܢܫܝܢ ܠܡܥܒܕ ܩܪܒܐ ܥܡ ܗܘ ܕܝܬܒ ܥܠ ܤܘܤܝܐ ܘܥܡ ܦܠܚܘܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 20, 'ܘܐܬܬܨܝܕܬ ܚܝܘܬܐ ܘܢܒܝܐ ܕܓܠܐ ܥܡܗ ܗܘ ܕܥܒܕ ܐܬܘܬܐ ܩܕܡܝܗ ܕܒܗܝܢ ܐܛܥܝ ܠܐܝܠܝܢ ܕܢܤܒܘ ܪܘܫܡܐ ܕܚܝܘܬܐ ܘܠܐܝܠܝܢ ܕܤܓܕܘ ܠܨܠܡܗ ܘܢܚܬܘ ܬܪܝܗܘܢ ܘܐܬܪܡܝܘ ܒܝܡܬܐ ܕܢܘܪܐ ܕܝܩܕܐ ܘܕܟܒܪܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 21, 'ܘܕܫܪܟܐ ܕܝܢ ܐܬܩܛܠܘ ܒܚܪܒܗ ܕܗܘ ܕܝܬܒ ܥܠ ܤܘܤܝܐ ܒܐܝܕܐ ܕܢܦܩܐ ܡܢ ܦܘܡܗ ܘܟܠܗ ܛܝܪܐ ܤܒܥܬ ܡܢ ܒܤܪܗܘܢ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 1, 'ܘܚܙܝܬ ܐܚܪܢܐ ܡܠܐܟܐ ܕܢܚܬ ܡܢ ܫܡܝܐ ܕܐܝܬ ܥܠܘܗܝ ܩܠܝܕܐ ܕܬܗܘܡܐ ܘܫܝܫܠܬܐ ܪܒܬܐ ܒܐܝܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 2, 'ܘܠܒܟܗ ܠܬܢܝܢܐ ܚܘܝܐ ܩܕܡܝܐ ܗܘ ܕܐܝܬܘܗܝ ܐܟܠܩܪܨܐ ܘܤܛܢܐ ܘܐܤܪܗ ܐܠܦ ܫܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 3, 'ܘܐܪܡܝܗ ܒܬܗܘܡܐ ܘܐܚܕ ܘܛܒܥ ܠܥܠ ܡܢܗ ܕܠܐ ܬܘܒ ܢܛܥܐ ܠܟܠܗܘܢ ܥܡܡܐ ܒܬܪ ܗܠܝܢ ܝܗܝܒ ܠܡܫܪܝܗ ܩܠܝܠ ܙܒܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 4, 'ܘܚܙܝܬ ܡܘܬܒܐ ܘܝܬܒܘ ܥܠܝܗܘܢ ܘܕܝܢܐ ܐܬܝܗܒ ܠܗܘܢ ܘܢܦܫܬܐ ܗܠܝܢ ܕܐܬܦܤܩ ܡܛܠ ܤܗܕܘܬܐ ܕܝܫܘܥ ܘܡܛܠ ܡܠܬܐ ܕܐܠܗܐ ܘܕܐܝܠܝܢ ܕܠܐ ܤܓܕܘ ܠܚܝܘܬܐ ܘܠܐ ܠܨܠܡܗ ܘܠܐ ܢܤܒܘ ܪܘܫܡܐ ܥܠ ܒܝܬ ܥܝܢܝܗܘܢ ܐܘ ܥܠ ܐܝܕܝܗܘܢ ܕܚܝܘ ܘܐܡܠܟܘ ܥܡ ܡܫܝܚܐ ܐܠܦ ܫܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 5, 'ܘܗܕܐ ܗܝ ܩܝܡܬܐ ܩܕܡܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 6, 'ܛܘܒܢܐ ܗܘ ܘܩܕܝܫܐ ܡܢ ܕܐܝܬ ܠܗ ܡܢܬܐ ܒܩܝܡܬܐ ܩܕܡܝܬܐ ܘܥܠ ܗܠܝܢ ܠܝܬ ܫܘܠܛܢܐ ܠܡܘܬܐ ܬܢܝܢܐ ܐܠܐ ܢܗܘܘܢ ܟܗܢܐ ܕܐܠܗܐ ܘܕܡܫܝܚܐ ܘܢܡܠܟܘܢ ܥܡܗ ܐܠܦ ܫܢܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 7, 'ܘܡܐ ܕܐܫܬܠܡ ܐܠܦ ܫܢܝܢ ܢܫܬܪܐ ܤܛܢܐ ܡܢ ܚܒܘܫܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 8, 'ܘܢܦܘܩ ܠܡܛܥܝܘ ܠܟܠܗܘܢ ܥܡܡܐ ܒܐܪܒܥ ܙܘܝܬܗ ܕܐܪܥܐ ܠܓܘܓ ܘܠܡܓܘܓ ܘܠܡܟܢܫܘ ܐܢܘܢ ܠܩܪܒܐ ܐܝܠܝܢ ܕܡܢܝܢܗܘܢ ܐܝܟ ܚܠܐ ܕܝܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 9, 'ܘܤܠܩܘ ܥܠ ܦܬܝܗ ܕܐܪܥܐ ܘܚܕܪܘܗ ܠܡܕܝܢܬܐ ܕܡܫܪܝܬܐ ܕܩܕܝܫܐ ܘܠܡܕܝܢܬܐ ܚܒܝܒܬܐ ܘܢܚܬܬ ܢܘܪܐ ܡܢ ܫܡܝܐ ܡܢ ܐܠܗܐ ܘܐܟܠܬ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 10, 'ܘܐܟܠܩܪܨܐ ܡܛܥܝܢܗܘܢ ܐܬܪܡܝ ܒܝܡܬܐ ܕܢܘܪܐ ܘܟܒܪܝܬܐ ܐܝܟܐ ܕܚܝܘܬܐ ܘܢܒܝܐ ܕܓܠܐ ܘܢܫܬܢܩܘܢ ܐܝܡܡܐ ܘܠܠܝܐ ܠܥܠܡ ܥܠܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 11, 'ܘܚܙܝܬ ܟܘܪܤܝܐ ܪܒܐ ܚܘܪܐ ܘܠܕܝܬܒ ܠܥܠ ܡܢܗ ܗܘ ܕܡܢ ܩܕܡ ܐܦܘܗܝ ܥܪܩܬ ܐܪܥܐ ܘܫܡܝܐ ܘܐܬܪ ܠܐ ܐܫܬܟܚ ܠܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 12, 'ܘܚܙܝܬ ܠܡܝܬܐ ܪܘܪܒܐ ܘܙܥܘܪܐ ܕܩܡܘ ܩܕܡ ܟܘܪܤܝܐ ܘܤܦܪܐ ܐܬܦܬܚܘ ܘܐܚܪܢܐ ܤܦܪܐ ܐܬܦܬܚ ܕܐܝܬܘܗܝ ܕܕܝܢܐ ܘܐܬܕܝܢܘ ܡܝܬܐ ܡܢ ܐܝܠܝܢ ܕܟܬܝܒܢ ܒܤܦܪܐ ܐܝܟ ܥܒܕܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 13, 'ܘܝܗܒ ܝܡܐ ܡܝܬܐ ܕܒܗ ܘܡܘܬܐ ܘܫܝܘܠ ܝܗܒܘ ܡܝܬܐ ܕܨܐܝܕܝܗܘܢ ܘܐܬܕܝܢ ܚܕ ܚܕ ܡܢܗܘܢ ܐܝܟ ܥܒܕܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 14, 'ܘܡܘܬܐ ܘܫܝܘܠ ܐܬܪܡܝܘ ܒܝܡܬܐ ܕܢܘܪܐ ܗܢܐ ܕܐܝܬܘܗܝ ܡܘܬܐ ܬܢܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 15, 'ܘܐܝܢܐ ܕܠܐ ܐܫܬܟܚ ܕܪܫܝܡ ܒܟܬܒܐ ܕܚܝܐ ܐܬܪܡܝ ܒܝܡܬܐ ܕܢܘܪܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 1, 'ܘܚܙܝܬ ܫܡܝܐ ܚܕܬܬܐ ܘܐܪܥܐ ܚܕܬܐ ܫܡܝܐ ܓܝܪ ܩܕܡܝܬܐ ܘܐܪܥܐ ܩܕܡܝܬܐ ܐܙܠܘ ܘܝܡܐ ܠܝܬܘܗܝ ܬܘܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 2, 'ܘܠܡܕܝܢܬܐ ܩܕܝܫܬܐ ܐܘܪܫܠܡ ܚܕܬܐ ܚܙܝܬܗ ܕܢܚܬܐ ܡܢ ܫܡܝܐ ܡܢ ܨܝܕ ܐܠܗܐ ܕܡܛܝܒܐ ܐܝܟ ܟܠܬܐ ܡܨܒܬܬܐ ܠܒܥܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 3, 'ܘܫܡܥܬ ܩܠܐ ܪܒܐ ܡܢ ܫܡܝܐ ܕܐܡܪ ܗܐ ܡܫܪܝܐ ܕܐܠܗܐ ܥܡ ܒܢܝܢܫܐ ܘܫܪܐ ܥܡܗܘܢ ܘܗܢܘܢ ܥܡܐ ܕܝܠܗ ܢܗܘܘܢ ܘܗܘ ܐܠܗܐ ܥܡܗܘܢ ܘܢܗܘܐ ܠܗܘܢ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 4, 'ܘܗܘ ܢܠܚܐ ܟܠ ܕܡܥܐ ܡܢ ܥܝܢܝܗܘܢ ܘܡܘܬܐ ܠܐ ܢܗܘܐ ܡܟܝܠ ܘܠܐ ܐܒܠܐ ܘܠܐ ܪܘܒܐ ܘܠܐ ܟܐܒܐ ܬܘܒ ܢܗܘܐ ܥܠ ܐܦܝܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 5, 'ܘܐܙܠܬ ܘܐܡܪ ܠܝ ܕܝܬܒ ܥܠ ܟܘܪܤܝܐ ܗܐ ܚܕܬܐ ܥܒܕ ܐܢܐ ܟܠ ܘܐܡܪ ܠܝ ܟܬܘܒ ܗܠܝܢ ܡܠܐ ܡܗܝܡܢܬܐ ܘܫܪܝܪܬܐ ܐܝܬܝܗܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 6, 'ܘܐܡܪ ܠܝ ܗܘܝ ܐܢܐ ܐܠܦ ܘܐܢܐ ܬܘ ܪܝܫܝܬܐ ܘܫܘܠܡܐ ܠܕܨܗܐ ܐܢܐ ܐܬܠ ܡܢ ܥܝܢܐ ܕܡܝܐ ܚܝܐ ܡܓܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 7, 'ܘܕܙܟܐ ܗܘ ܢܐܪܬ ܗܠܝܢ ܘܐܗܘܐ ܠܗ ܐܠܗܐ ܘܢܗܘܐ ܠܝ ܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 8, 'ܠܩܢܘܛܬܢܐ ܕܝܢ ܘܠܐ ܡܗܝܡܢܐ ܘܥܘܠܐ ܘܡܤܝܒܐ ܘܩܛܘܠܐ ܘܚܪܫܐ ܘܙܢܝܐ ܘܦܠܚܝ ܦܬܟܪܐ ܘܟܠܗܘܢ ܕܓܠܐ ܡܢܬܗܘܢ ܒܝܡܬܐ ܝܩܕܬܐ ܕܢܘܪܐ ܘܟܒܪܝܬܐ ܐܝܕܐ ܕܐܝܬܝܗ ܡܘܬܐ ܬܢܝܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 9, 'ܘܐܬܐ ܚܕ ܡܢ ܫܒܥܐ ܡܠܐܟܝܢ ܐܝܠܝܢ ܕܐܝܬ ܥܠܝܗܘܢ ܫܒܥ ܙܒܘܪܝܢ ܕܡܠܝܢ ܫܒܥ ܡܚܘܬܐ ܐܚܪܝܬܐ ܘܡܠܠ ܥܡܝ ܠܡܐܡܪ ܬܐ ܐܚܘܝܟ ܠܟܠܬܐ ܐܢܬܬܗ ܕܐܡܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 10, 'ܘܐܘܒܠܢܝ ܒܪܘܚ ܠܛܘܪܐ ܪܒܐ ܘܪܡܐ ܘܚܘܝܢܝ ܠܡܕܝܢܬܐ ܩܕܝܫܬܐ ܐܘܪܫܠܡ ܕܢܚܬܐ ܡܢ ܫܡܝܐ ܡܢ ܨܝܕ ܐܠܗܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 11, 'ܘܐܝܬ ܠܗ ܬܫܒܘܚܬܐ ܕܐܠܗܐ ܘܢܘܗܪܗ ܐܝܟ ܕܡܘܬܐ ܕܟܐܦܐ ܝܩܝܪܬܐ ܐܝܟ ܝܫܦܗ ܐܝܟ ܕܘܡܝܐ ܕܩܪܘܤܛܠܘܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 12, 'ܘܐܝܬ ܠܗ ܫܘܪܐ ܪܒܐ ܘܪܡܐ ܘܐܝܬ ܠܗ ܬܪܥܐ ܬܪܥܤܪ ܘܥܠ ܬܪܥܐ ܡܠܐܟܐ ܬܪܥܤܪ ܘܫܡܗܝܗܘܢ ܟܬܝܒܐ ܐܝܠܝܢ ܕܐܝܬܝܗܘܢ ܫܡܗܐ ܕܬܪܥܤܪ ܫܒܛܐ ܕܐܝܤܪܝܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 13, 'ܡܢ ܡܕܢܚܐ ܬܪܥܐ ܬܠܬܐ ܘܡܢ ܓܪܒܝܐ ܬܪܥܐ ܬܠܬܐ ܘܡܢ ܬܝܡܢܐ ܬܪܥܐ ܬܠܬܐ ܘܡܢ ܡܥܪܒܐ ܬܪܥܐ ܬܠܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 14, 'ܘܫܘܪܐ ܕܡܕܝܢܬܐ ܐܝܬ ܠܗ ܫܬܐܤܐ ܬܪܬܥܤܪܐ ܘܥܠܝܗܝܢ ܬܪܥܤܪ ܫܡܗܐ ܕܫܠܝܚܘܗܝ ܕܒܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 15, 'ܘܗܘ ܕܡܡܠܠ ܗܘܐ ܥܡܝ ܐܝܬ ܗܘܐ ܥܠܘܗܝ ܩܢܝܐ ܕܡܫܘܚܬܐ ܕܕܗܒܐ ܠܡܡܫܚܗ ܠܡܕܝܢܬܐ ܘܠܫܘܪܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 16, 'ܘܡܕܝܢܬܐ ܡܪܒܥܐܝܬ ܤܝܡܐ ܘܐܘܪܟܗ ܐܝܟ ܦܬܝܗ ܘܡܫܚܗ ܠܡܕܝܢܬܐ ܒܩܢܝܐ ܥܠ ܬܪܥܤܪ ܐܠܦܝܢ ܐܤܛܕܘܬܐ ܐܘܪܟܗ ܘܦܬܝܗ ܘܪܘܡܗ ܫܘܝܢ ܐܢܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 17, 'ܘܡܫܚܗ ܠܫܘܪܗ ܡܐܐ ܘܐܪܒܥܝܢ ܘܐܪܒܥ ܐܡܝܢ ܒܡܫܘܚܬܐ ܕܐܢܫܐ ܐܝܕܐ ܕܐܝܬܝܗ ܕܡܠܐܟܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 18, 'ܘܕܘܡܤܐ ܕܫܘܪܗ ܝܫܦܗ ܘܡܕܝܢܬܐ ܕܕܗܒܐ ܕܟܝܐ ܒܕܡܘܬܐ ܕܙܓܘܓܝܬܐ ܕܟܝܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 19, 'ܘܫܬܐܤܐ ܕܫܘܪܐ ܕܡܕܝܢܬܐ ܒܟܐܦܐ ܝܩܝܪܬܐ ܡܨܒܬܢ ܘܫܬܐܤܬܐ ܩܕܡܝܬܐ ܝܫܦܗ ܘܕܬܪܬܝܢ ܤܦܝܠܐ ܘܕܬܠܬ ܩܪܟܕܢܐ ܘܕܐܪܒܥ ܙܡܪܓܕܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 20, 'ܘܕܚܡܫ ܤܪܕܘܢ ܘܛܦܪܐ ܘܕܫܬ ܤܪܕܘܢ ܘܕܫܒܥ ܟܐܦ ܕܗܒܐ ܘܕܬܡܢܐ ܒܪܘܠܐ ܘܕܬܫܥ ܛܘܦܢܕܝܘܢ ܘܕܥܤܪ ܟܪܘܤܦܪܤܐ ܕܚܕܥܤܪܐ ܝܘܟܢܬܘܤ ܕܬܪܬܥܤܪܐ ܐܡܘܬܤܤ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 21, 'ܘܬܪܥܤܪ ܬܪܥܐ ܘܬܪܬܥܤܪܐ ܡܪܓܢܝܬܐ ܚܕܐ ܠܚܕܐ ܘܟܠܚܕ ܡܢ ܬܪܥܐ ܐܝܬ ܗܘܐ ܡܢ ܚܕܐ ܡܪܓܢܝܬܐ ܘܫܘܩܐ ܕܝܢ ܕܡܕܝܢܬܐ ܕܕܗܒܐ ܕܟܝܐ ܐܝܟ ܙܓܘܓܝܬܐ ܐܝܬ ܒܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 22, 'ܘܗܝܟܠܐ ܠܐ ܚܙܝܬ ܒܗ ܡܪܝܐ ܓܝܪ ܐܠܗܐ ܐܚܝܕ ܟܠ ܗܘ ܐܝܬܘܗܝ ܗܝܟܠܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 23, 'ܘܠܐܡܪܐ ܘܠܡܕܝܢܬܐ ܠܐ ܡܬܒܥܐ ܫܡܫܐ ܘܠܐ ܤܗܪܐ ܕܢܢܗܪܘܢ ܠܗ ܬܫܒܘܚܬܗ ܓܝܪ ܕܐܠܗܐ ܐܢܗܪܬܗ ܘܫܪܓܗ ܐܝܬܘܗܝ ܐܡܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 24, 'ܘܡܗܠܟܝܢ ܥܡܡܐ ܒܢܘܗܪܗ ܘܡܠܟܐ ܕܐܪܥܐ ܡܝܬܝܢ ܠܗ ܬܫܒܘܚܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 25, 'ܘܬܪܥܝܗ ܠܐ ܢܬܬܚܕܘܢ ܒܐܝܡܡܐ ܠܠܝܐ ܓܝܪ ܠܐ ܢܗܘܐ ܬܡܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 26, 'ܘܢܝܬܘܢ ܠܗ ܬܫܒܘܚܬܐ ܘܐܝܩܪܐ ܕܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 27, 'ܘܠܐ ܢܗܘܐ ܬܡܢ ܟܠ ܛܡܐ ܘܕܥܒܕ ܡܤܝܒܘܬܐ ܘܕܓܠܘܬܐ ܐܠܐ ܐܢ ܐܝܠܝܢ ܕܟܬܝܒܢ ܒܟܬܒܗ ܕܐܡܪܐ ܀  ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 1, 'ܘܚܘܝܢܝ ܢܗܪܐ ܕܡܝܐ ܚܝܐ ܕܟܝܐ ܐܦ ܢܗܝܪܐ ܐܝܟ ܓܠܝܕܐ ܘܢܦܩ ܡܢ ܟܘܪܤܝܗ ܕܐܠܗܐ ܘܕܐܡܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 2, 'ܘܡܨܥܬ ܫܘܩܝܗ ܡܟܐ ܘܡܟܐ ܥܠ ܢܗܪܐ ܩܝܤܐ ܕܚܝܐ ܕܥܒܕ ܦܐܪܐ ܬܪܥܤܪ ܘܒܟܠ ܝܪܚ ܝܗܒ ܦܐܪܘܗܝ ܘܛܪܦܘܗܝ ܠܐܤܝܘܬܐ ܕܥܡܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 3, 'ܘܟܠ ܚܪܡܐ ܠܐ ܢܗܘܐ ܬܡܢ ܘܟܘܪܤܝܗ ܕܐܠܗܐ ܘܕܐܡܪܐ ܒܗ ܢܗܘܐ ܘܥܒܕܘܗܝ ܢܫܡܫܘܢܝܗܝ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 4, 'ܘܢܚܙܘܢ ܐܦܘܗܝ ܘܫܡܗ ܥܠ ܒܝܬ ܥܝܢܝܗܘܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 5, 'ܘܠܠܝܐ ܠܐ ܢܗܘܐ ܬܡܢ ܘܠܐ ܢܬܒܥܐ ܠܗܘܢ ܢܘܗܪܐ ܘܫܪܓܐ ܘܢܘܗܪܗ ܕܫܡܫܐ ܡܛܠ ܕܡܪܝܐ ܐܠܗܐ ܡܢܗܪ ܠܗܘܢ ܘܡܠܟܗܘܢ ܠܥܠܡ ܥܠܡܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 6, 'ܘܐܡܪ ܠܝ ܗܠܝܢ ܡܠܐ ܡܗܝܡܢܢ ܘܫܪܝܪܢ ܘܡܪܝܐ ܐܠܗܐ ܕܪܘܚܬܐ ܕܢܒܝܐ ܩܕܝܫܐ ܫܕܪ ܠܡܠܐܟܗ ܠܡܚܘܝܘ ܠܥܒܕܘܗܝ ܡܐ ܕܝܗܝܒ ܠܡܗܘܐ ܒܥܓܠ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 7, 'ܘܗܐ ܐܬܐ ܐܢܐ ܒܥܓܠ ܛܘܒܘܗܝ ܠܡܢ ܕܢܛܪ ܡܠܐ ܕܢܒܝܘܬܐ ܕܟܬܒܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 8, 'ܐܢܐ ܝܘܚܢܢ ܕܚܙܐ ܘܫܡܥ ܗܠܝܢ ܘܟܕ ܚܙܝܬ ܘܫܡܥܬ ܢܦܠܬ ܠܡܤܓܕ ܩܕܡ ܪܓܠܘܗܝ ܕܡܠܐܟܐ ܕܡܚܘܐ ܠܝ ܗܠܝܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 9, 'ܘܐܡܪ ܠܝ ܚܙܝ ܠܐ ܟܢܬܟ ܐܝܬܝ ܘܕܐܚܝܟ ܢܒܝܐ ܘܕܐܝܠܝܢ ܕܢܛܪܝܢ ܗܠܝܢ ܡܠܐ ܕܟܬܒܐ ܗܢܐ ܠܐܠܗܐ ܤܓܘܕ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 10, 'ܘܐܡܪ ܠܝ ܠܐ ܬܚܬܘܡ ܡܠܐ ܕܢܒܝܘܬܐ ܕܟܬܒܐ ܗܢܐ ܙܒܢܐ ܓܝܪ ܩܪܒ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 11, 'ܘܕܡܥܘܠ ܬܘܒ ܢܥܘܠ ܘܕܨܥ ܬܘܒ ܢܨܛܥܨܥ ܘܙܕܝܩܐ ܬܘܒ ܢܥܒܕ ܙܕܝܩܘܬܐ ܘܩܕܝܫܐ ܬܘܒ ܢܬܩܕܫ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 12, 'ܗܐ ܐܬܐ ܐܢܐ ܡܚܕܐ ܘܐܓܪܝ ܥܡܝ ܘܐܬܠ ܠܟܠܢܫ ܐܝܟ ܥܒܕܗ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 13, 'ܐܢܐ ܐܠܦ ܘܐܢܐ ܬܘ ܩܕܡܝܐ ܘܐܚܪܝܐ ܘܫܘܪܝܐ ܘܫܘܠܡܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 14, 'ܛܘܒܝܗܘܢ ܠܕܥܒܕܝܢ ܦܘܩܕܢܘܗܝ ܢܗܘܐ ܫܘܠܛܢܗܘܢ ܥܠ ܩܝܤܐ ܕܚܝܐ ܘܒܬܪܥܐ ܢܥܠܘܢ ܠܡܕܝܢܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 15, 'ܘܙܢܝܐ ܘܩܛܘܠܐ ܘܦܠܚܝ ܦܬܟܪܐ ܠܒܪ ܘܛܡܐܐ ܘܚܪܫܐ ܘܟܠ ܚܙܝܝ ܘܥܒܕܝ ܕܓܠܘܬܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 16, 'ܐܢܐ ܝܫܘܥ ܫܕܪܬ ܠܡܠܐܟܝ ܕܢܤܗܕ ܒܟܘܢ ܗܠܝܢ ܩܕܡ ܥܕܬܐ ܐܢܐ ܐܢܐ ܥܩܪܐ ܘܫܪܒܬܗ ܕܕܘܝܕ ܘܥܡܗ ܘܟܘܟܒ ܨܦܪܐ ܢܗܝܪܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 17, 'ܘܪܘܚܐ ܘܟܠܬܐ ܐܡܪܝܢ ܬܐ ܘܕܫܡܥ ܢܐܡܪ ܬܐ ܘܕܨܗܐ ܢܐܬܐ ܘܢܤܒ ܡܝܐ ܚܝܐ ܡܓܢ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 18, 'ܡܤܗܕ ܐܢܐ ܠܟܠ ܕܫܡܥ ܡܠܬܐ ܕܢܒܝܘܬܐ ܕܟܬܒܐ ܗܢܐ ܕܐܝܢܐ ܕܢܤܝܡ ܥܠܝܗܝܢ ܢܤܝܡ ܥܠܘܗܝ ܐܠܗܐ ܡܚܘܬܐ ܕܟܬܝܒܢ ܒܟܬܒܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 19, 'ܘܐܝܢܐ ܕܡܒܨܪ ܡܢ ܡܠܐ ܕܟܬܒܐ ܕܢܒܝܘܬܐ ܗܕܐ ܢܒܨܪ ܐܠܗܐ ܡܢܬܗ ܡܢ ܩܝܤܐ ܕܚܝܐ ܘܡܢ ܡܕܝܢܬܐ ܩܕܝܫܬܐ ܐܝܠܝܢ ܕܟܬܝܒܢ ܒܟܬܒܐ ܗܢܐ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 20, 'ܐܡܪ ܟܕ ܡܤܗܕ ܗܠܝܢ ܐܝܢ ܐܬܐ ܐܢܐ ܒܥܓܠ ܬܐ ܡܪܝܐ ܝܫܘܥ ܀ ');
INSERT INTO `Peshitta_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 21, 'ܛܝܒܘܬܗ ܕܡܪܢ ܝܫܘܥ ܡܫܝܚܐ ܥܡ ܟܠܗܘܢ ܩܕܝܫܘܗܝ ܐܡܝܢ ܀܀   ');
