INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 1, 'בראשית ברא אלהים את השמים ואת הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 2, 'והארץ היתה תהו ובהו וחשך על פני תהום ורוח אלהים מרחפת על פני המים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 3, 'ויאמר אלהים יהי אור ויהי אור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 4, 'וירא אלהים את האור כי טוב ויבדל אלהים בין האור ובין החשך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 5, 'ויקרא אלהים לאור יום ולחשך קרא לילה ויהי ערב ויהי בקר יום אחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 6, 'ויאמר אלהים יהי רקיע בתוך המים ויהי מבדיל בין מים למים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 7, 'ויעש אלהים את הרקיע ויבדל בין המים אשר מתחת לרקיע ובין המים אשר מעל לרקיע ויהי כן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 8, 'ויקרא אלהים לרקיע שמים ויהי ערב ויהי בקר יום שני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 9, 'ויאמר אלהים יקוו המים מתחת השמים אל מקום אחד ותראה היבשה ויהי כן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 10, 'ויקרא אלהים ליבשה ארץ ולמקוה המים קרא ימים וירא אלהים כי טוב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 11, 'ויאמר אלהים תדשא הארץ דשא עשב מזריע זרע ועץ פרי עשה פרי למינו אשר זרעו בו על הארץ ויהי כן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 12, 'ותוצא הארץ דשא עשב מזריע זרע למינהו ועץ עשה פרי אשר זרעו בו למינהו וירא אלהים כי טוב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 13, 'ויהי ערב ויהי בקר יום שלישי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 14, 'ויאמר אלהים יהי מאורות ברקיע השמים להאיר על הארץ ולהבדיל בין היום ובין הלילה והיו לאתות ולמועדים ולימים ושנים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 15, 'והיו למאורות ברקיע השמים להאיר על הארץ ויהי כן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 16, 'ויעש אלהים את שני המאורות הגדלים את המאור הגדול לממשלת היום ואת המאור הקטן לממשלת הלילה ואת הכוכבים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 17, 'ויתן אתם אלהים ברקיע השמים להאיר על הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 18, 'ולמשל ביום ובלילה ולהבדיל בין האור ובין החשך וירא אלהים כי טוב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 19, 'ויהי ערב ויהי בקר יום רביעי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 20, 'ויאמר אלהים ישרצו המים שרץ נפש חיה ועוף יעפף על הארץ על פני רקיע השמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 21, 'ויברא אלהים את התנינים הגדלים ואת כל נפש החיה הרמשת אשר שרצו המים למיניהם ואת כל עוף כנף למינהו וירא אלהים כי טוב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 22, 'ויברך אתם אלהים לאמר פרו ורבו ומלאו את המים בימים והעוף ירבה בארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 23, 'ויהי ערב ויהי בקר יום חמישי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 24, 'ויאמר אלהים תוצא הארץ נפש חיה למינה בהמה ורמש וחית הארץ למינה ויהי כן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 25, 'ויעש אלהים את חית הארץ למינה ואת הבהמה למינה ואת כל רמש האדמה למינהו וירא אלהים כי טוב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 26, 'ויאמר אלהים נעשה אדם בצלמנו וכדמותנו וירדו בדגת הים ובעוף השמים ובבהמה ובכל הארץ ובכל הרמש הרמש על הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 27, 'ויברא אלהים את האדם בצלמו בצלם אלהים ברא אתו זכר ונקבה ברא אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 28, 'ויברך אתם אלהים ויאמר להם אלהים פרו ורבו ומלאו את הארץ וכבשוה ורדו בדגת הים ובעוף השמים ובכל החיה הרמשת על הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 29, 'ויאמר אלהים הנה נתתי לכם את כל עשב זרע זרע אשר על פני כל הארץ ואת כל עץ אשר בו פרי עץ זרע זרע לכם יהיה לאכלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 30, 'ולכל חית הארץ ולכל עוף השמים ולכל הרמש על הארץ אשר בו נפש חיה את כל ירק עשב לאכלה ויהי כן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 1, 31, 'וירא אלהים את כל אשר עשה והנה טוב מאד ויהי ערב ויהי בקר יום הששי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 1, 'ויכלו השמים והארץ וכל צבאם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 2, 'ויכל אלהים ביום הששי מלאכתו אשר עשה וישבת ביום השביעי מכל מלאכתו אשר עשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 3, 'ויברך אלהים את יום השביעי ויקדש אתו כי בו שבת מכל מלאכתו אשר ברא אלהים לעשות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 4, 'אלה תולדת השמים והארץ בהבראם ביום עשות יהוה אלהים שמימ וארץ . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 5, 'וכל שיח השדה טרם יהיה בארץ וכל עשב השדה טרם יצמח כי לא המטיר יהוה אלהים על הארץ ואדם אין לעבד את האדמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 6, 'ואד יעלה מן הארץ והשקה את כל פני האדמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 7, 'ויצר יהוה אלהים את האדם עפר מן האדמה ויפח באפיו נשמת חיים ויהי אדם לנפש חיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 8, 'ויטע יהוה אלהים גן בעדן מקדם וישם שם את האדם אשר יצר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 9, 'ויצמח יהוה אלהים מן האדמה את כל עץ נחמד למראה וטוב למאכל ועץ החיים בתוך הגן ועץ הדעת טוב ורע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 10, 'ונהר יצא מעדן להשקות את הגן ומשם יפרד והיה לארבעה ראשים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 11, 'שם האחד פישון הוא הסבב את כל ארץ החוילה אשר שם הזהב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 12, 'וזהב הארץ ההיא טוב מאד שם הבדלח ואבן השהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 13, 'ושם הנהר השני גיחון הוא הסבב את כל ארץ כוש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 14, 'ושם הנהר השלישי חדקל הוא ההלך קדמת אשור והנהר הרביעי הוא פרת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 15, 'ויקח יהוה אלהים את האדם וינחהו בגן עדן לעבדה ולשמרה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 16, 'ויצו יהוה אלהים על האדם לאמר מכל עץ הגן אכל תאכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 17, 'ומעץ הדעת טוב ורע לא תאכל ממנו כי ביום אכלך ממנו מות תמות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 18, 'ויאמר יהוה אלהים לא טוב היות האדם לבדו אעשה לו עזר כנגדו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 19, 'ויצר יהוה אלהים עוד מן האדמה את כל חית השדה ואת כל עוף השמים ויבא אל האדם לראות מה יקרא לו וכל אשר יקרא לו האדם נפש חיה הוא שמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 20, 'ויקרא האדם שמות לכל הבהמה ולעוף השמים ולכל חית השדה ולאדם לא מצא עזר כנגדו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 21, 'ויפל יהוה אלהים תרדמה על האדם ויישן ויקח אחת מצלעתיו ויסגר בשר תחתנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 22, 'ויבן יהוה אלהים את הצלע אשר לקח מן האדם לאשה ויביאה אל האדם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 23, 'ויאמר האדם זאת הפעם עצם מעצמי ובשר מבשרי לזאת יקרא אשה כי מאישה לקחה זאת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 24, 'על כן יעזב איש את אביו ואת אמו ודבק באשתו והיה משניהמ לבשר אחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 2, 25, 'ויהיו שניהם ערמים אדם ואשתו ולא יתבששו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 1, 'והנחש היה ערום מכל חית השדה אשר עשה יהוה אלהים ויאמר אל האשה אף כי אמר אלהים לא תאכלו מכל עץ הגן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 2, 'ותאמר האשה אל הנחש מפרי עץ הגן נאכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 3, 'ומפרי העץ הזה אשר בתוך הגן אמר אלהים לא תאכלו ממנו ולא תגעו בו פן תמתון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 4, 'ויאמר הנחש אל האשה לא מות תמתון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 5, 'כי ידע אלהים כי ביום אכלכם ממנו ונפקחו עיניכם והייתם כאלהים ידעי טוב ורע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 6, 'ותרא האשה כי טוב העץ למאכל וכי תאוה הוא לעינים ונחמד העץ להשכל ותקח מפריו ותאכל ותתן גם לאישה עמה ויאכלו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 7, 'ותפקחנה עיני שניהם וידעו כי ערמם הם ויתפרו עלי תאנה ויעשו להם חגרות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 8, 'וישמעו את קול יהוה אלהים מתהלך בגן לרוח היום ויתחבא אדם ואשתו מפני יהוה אלהים בתוך עץ הגן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 9, 'ויקרא יהוה אלהים אל האדם ויאמר לו איד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 10, 'ויאמר את קולך שמעתי בגן ואירא כי ערום אנכי ואחבא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 11, 'ויאמר מי הגיד לך כי ערום אתה המן העץ אשר צויתיך לבלתי אכל ממנו אכלת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 12, 'ויאמר האדם האשה אשר נתתה עמדי היא נתנה לי מן העץ ואכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 13, 'ויאמר יהוה אלהים לאשה מה זאת עשיתי ותאמר האשה הנחש השיאני ואכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 14, 'ויאמר יהוה אלהים אל הנחש כי עשית זאת ארור אתה מכל הבהמה ומכל חית השדה על גחנך תלך ועפר תאכל כל ימי חייך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 15, 'ואיבה אשית בינך ובין האשה ובין זרעך ובין זרעה הוא ישופך ראש ואתה תשופנו עקב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 16, 'ואל האשה אמר הרבה ארבה עצבונך והריונך בעצבון תלדי בנים ואל אישך תשוקתך והוא ימשל בך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 17, 'ולאדם אמר כי שמעת לקול אשתך ותאכל מן העץ אשר צויתיך לאמר לא תאכל ממנו ארורה האדמה בעבורך בעצבון תאכלנה כל ימי חייך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 18, 'וקוץ ודרדר תצמח לך ואכלת את עשב השדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 19, 'בזעת אפיך תאכל לחם עד שובך אל האדמה כי ממנה לקחת כי עפר אתה ואל עפרך תשוב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 20, 'ויקרא האדם שם אשתו חוה כי היא היתה אם כל חי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 21, 'ויעש יהוה אלהים לאדם ולאשתו כיתנת עור וילבשם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 22, 'ויאמר יהוה אלהים הן האדם היה כאחד ממנו לדעת טוב ורע ועתה פן ישלח ידו ולקח גם מעץ החיים ואכל וחי לעולם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 23, 'וישלחהו יהוה אלהים מגן עדן לעבד את האדמה אשר לקח משם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 3, 24, 'ויגרש את האדם וישכן מקדם לגן עדן את הכרובים ואת להט החרב המתהפכת לשמר את דרך עץ החיים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 1, 'והאדם ידע את חוה אשתו ותהר ותלד את קין ותאמר קניתי איש את יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 2, 'ותסף ללדת את אחיו את הבל ויהי הבל רעה צאן וקין היה עבד אדמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 3, 'ויהי מקץ ימים ויבא קין מפרי האדמה מנחה ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 4, 'והבל הביא גם הוא מבכרות צאנו ומחלביהן וישע יהוה אל הבל ואל מנחתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 5, 'ואל קין ואל מנחתו לא שעה ויחר לקין מאד ויפלו פניו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 6, 'ויאמר יהוה אל קין למה חרה לך ולמה נפלו פניך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 7, 'הלוא אם תטיב שאת ואם לא תטיב לפתח חטאת רבץ ואליך תשוקתו ואתה תמשל בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 8, 'ויאמר קין אל הבל אחיו נלכה השדה ויהי בהיותם בשדה ויקם קין אל הבל אחיו ויהרגהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 9, 'ויאמר יהוה אל קין איה הבל אחיך ויאמר לא ידעתי השמר אחי אנכי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 10, 'ויאמר מה עשית קול דם אחיך צעק אלי מן האדמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 11, 'ועתה ארור אתה מן האדמה אשר פצתה את פיה לקחת את דם אחיך מידך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 12, 'כי תעבד את האדמה לא תוסף תת כחה לך נע ונד תהיה בארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 13, 'ויאמר קין אל יהוה גדול עוני מנשא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 14, 'הן גרשת אתי היום מעל פני האדמה ומפניך אסתר והייתי נע ונד בארץ והיה כל מצאי יהרגני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 15, 'ויאמר לו יהוה לכן כל הרג קין שבעתים יקם וישם יהוה לקין אות לבלתי הכות אתו כל מצאו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 16, 'ויצא קין מלפני יהוה וישב בארץ נד קדמת עדן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 17, 'וידע קין את אשתו ותהר ותלד את חנוך ויהי בנה עיר ויקרא את שם העיר כשם בנו חנוך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 18, 'ויולד לחנוך את עירד ועירד ילד את מחיאל ומחיאל ילד את מתושאל ומתושאל ילד את למך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 19, 'ויקח לו למך שתי נשים שם האחת עדה ושם השנית צלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 20, 'ותלד עדה את יבל הוא היה אבי ישב אהל ומקנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 21, 'ושם אחיו יובל הוא היה אבי כל תפש כנר ועגב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 22, 'וצלה גם היא ילדה את תובל קין לטש כל חרש נחשת וברזל ואחות תובל קין נעמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 23, 'ויאמר למך לנשיו עדה וצלה שמען קולי נשי למך האזינה אמרתי כי איש הרגתי לפצעי וילד לחברתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 24, 'כי שבעתים יקם קין ולמך שבעים ושבעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 25, 'וידע אדם עוד את אשתו ותלד בן ויקרא את שמו שת כי שת לי אלהים זרע אחר תחת הבל כי הרגו קין ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 4, 26, 'ולשת גם הוא ילד בן ויקרא את שמו אנוש אז החל לקרא בשם יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 1, 'זה ספר תולדת אדם ביום ברא אלהים אדם בדמות אלהים עשה אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 2, 'זכר ונקבה בראם ויברך אתם ויקרא את שמם אדם ביום הבראם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 3, 'ויחי אדם שלשים ומאת שנה ויולד בדמותו כצלמו ויקרא את שמו שת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 4, 'ויהיו ימי אדם אחרי הולידו את שת שמנה מאות שנה ויולד בנים ובנות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 5, 'ויהיו כל ימי אדם אשר חי תשע מאות שנה ושלשים שנה וימת ');
