INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 7, 'ויחי שת אחרי הולידו את אנוש שבע שנים ושמנה מאות שנה ויולד בנים ובנות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 8, 'ויהיו כל ימי שת שתים עשרה שנה ותשע מאות שנה וימת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 9, 'ויחי אנוש תשעים שנה ויולד את קינן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 10, 'ויחי אנוש אחרי הולידו את קינן חמש עשרה שנה ושמנה מאות שנה ויולד בנים ובנות ');
