INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 20, 'ויהיו כל ימי ירד שבע וארבעימ שנה ושמנה מאות שנה וימת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 21, 'ויחי חנוך חמש וששים שנה ויולד את מתושלח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 22, 'ויתהלך חנוך את האלהים אחרי הולידו את מתושלח שלש מאות שנה ויולד בנים ובנות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 23, 'ויהיו כל ימי חנוך חמש וששים שנה ושלש מאות שנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 24, 'ויתהלך חנוך את האלהים ואיננו כי לקח אתו אלהים ');
