INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 26, 'ויחי מתושלח אחרי הולידו את למך שלש וחמשים שנה ושש מאות שנה ויולד בנים ובנות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 27, 'ויהיו כל ימי מתושלח עשרים . שנה ושבע מאות שנה וימת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 28, 'ויחי למך שלש וחמשים שנה . . ויולד בן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 29, 'ויקרא את שמו נח לאמר זה ינחמנו ממעשינו ומעצבון ידינו מן האדמה אשר אררה יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 30, 'ויחי למך אחרי הולידו את נח שש מאות שנה . . . ויולד בנים ובנות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 31, 'ויהיו כל ימי למך שלש וחמשים שנה ושש מאות שנה וימת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 5, 32, 'ויהי נח בן חמש מאות שנה ויולד . את שם את חם ואת יפת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 1, 'ויהי כי החל האדם לרב על פני האדמה ובנות ילדו להם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 2, 'ויראו בני האלהים את בנות האדם כי טובת הנה ויקחו להם נשים מכל אשר בחרו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 3, 'ויאמר יהוה לא ידון רוחי באדם לעולם בשגם הוא בשר והיו ימיו מאה ועשרים שנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 4, 'והנפלים היו בארץ בימים ההם וגם אחרי כן אשר יבאו בני האלהים אל בנות האדם ויולידו להם הם הגברים אשר מעולם אנשי השם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 5, 'וירא יהוה כי רבה רעת האדם בארץ וכל יצר מחשבות לבו רק רע כל היום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 6, 'וינחם יהוה כי עשה את האדם בארץ ויתעצב אל לבו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 7, 'ויאמר יהוה אמחה את האדם אשר בראתי מעל פני האדמה מאדם עד בהמה עד רמש עד עוף השמים כי נחמתי כי עשיתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 8, 'ונח מצא חן בעיני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 9, 'אלה תולדת נח נח איש צדיק ותמים היה בדרתיו את האלהים התהלך נח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 10, 'ויולד נח שלשה בנים את שם את חם ואת יפת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 11, 'ותשחת הארץ לפני האלהים ותמלא הארץ חמס ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 12, 'וירא אלהים את הארץ והנה נשחתה כי השחית כל בשר את דרכו על הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 13, 'ויאמר אלהים לנח קץ כל בשר בא לפני כי מלאה הארץ חמס מפניהם והנני משחיתם את הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 14, 'עשה לך תבת עצי גפר קנים תעשה את התבה וכפרת אתה מבית ומחוץ בכפר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 15, 'וזה אשר תעשה אתה שלש מאות אמה ארך התבה והמשים אמה רחבה ושלשים אמה קומתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 16, 'צהר תעשה לתבה ואל אמה תכלנה מלמעלה ופתח התבה בצדה תשים תחתים שנים ושלישים תעשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 17, 'ואני הנני מביא את המבול מים על הארץ להשחית כל בשר אשר בו רוח חיים מתחת השמים כל אשר בארץ יגוע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 18, 'והקמתי את בריתי אתך ובאת אל התבה אתה ובניך ואשתך ונשי בניך אתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 19, 'ומכל החיה ומכל הבשר שנים מכל תביא אל התבה להחיות אתך זכר ונקבה והיה מן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 20, 'העוף למינהו ומן הבהמה למינה ומכל אשר רמש על האדמה למיניהם שנים מכל יבאו אליך להחיות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 21, 'ואתה קח לך מכל מאכל אשר יאכל ואספת אליך והיה לך ולהם לאכלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 6, 22, 'ויעש נח ככל אשר צוה אתו אלהים כן עשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 1, 'ויאמר אלהים אל נח בא אתה וכל ביתך אל התבה כי אתך ראיתי צדיק לפני בדור הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 2, 'מכל הבהמה הטהרה תקח לך שבעה שבעה זכר ונקבה ומן הבהמה אשר לא טהרה היא שנים שנים זכר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 3, 'ונקבה וגם מעוף השמים המהור שבעה שבעה זכר ונקבה להחיות זרע על פני כל הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 4, 'כי לימים עוד שבעה אנכי ממטיר על הארץ ארבעים יום וארבעים לילה ומחיתי את כל היקום אשר עשיתי מעל פני האדמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 5, 'ויעש נח ככל אשר צוהו יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 6, 'ונח בן שש מאות שנה והמבול היה מים על הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 7, 'ויבא נח ובניו ואשתו ונשי בניו אתו אל התבה מפני מי המבול ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 8, 'מן הבהמה הטהרה ומן הבהמה אשר איננה טהרה ומן העוף ומכל אשר רמש על האדמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 9, 'שנים שנים באו אל נח אל התבה זכר ונקבה כאשר צוה יהוה את נח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 10, 'ויהי לשבעת הימים ומי המבול היו על הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 11, 'בשנת השש מאות שנה לחיי נח בחדש השני בשבעה עשר יום לחדש ביום הזה נבקעו כל מעינות תהום רבה וארבות השמים נפתחו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 12, 'ויהי הגשם על הארץ ארבעים יום וארבעים לילה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 13, 'בעצם היום הזה בא נח שם חם ויפת בני נח ואשת נח ושלשת נשי בניו אתם אל התבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 14, 'הם וכל החיה למינה וכל הבהמה למינה וכל הרמש הרמש על הארץ למינהו וכל העוף למינהו כל צפור כל כנף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 15, 'ויבאו אל נח אל התבה שנים שנים מכל בשר אשר בו רוח חיים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 16, 'והבאים זכר ונקבה זכר ונקבה מכל בשר באו כאשר צוה אתו אלהים ויסגר יהוה בעדו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 17, 'ויהי המבול ארבעים יום על הארץ וירבו המים וישאו את התבה ותרם מעל הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 18, 'ויגברו המים וירבו מאד על הארץ ותלך התבה על פני המים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 19, 'והמים גברו מאד מאד על הארץ ויכסו כל ההרים הגבהים אשר תחת כל השמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 20, 'חמש עשרה אמה מלמעלה גברו המים ויכסו ההרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 21, 'ויגוע כל בשר הרמש על הארץ בעוף ובבהמה ובחיה ובכל השרץ השרץ על הארץ וכל האדם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 22, 'כל אשר נשמת רוח חיים באפיו מכל אשר בחרבה מתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 23, 'וימח את כל היקום אשר על פני האדמה מאדם עד בהמה עד רמש עד עוף השמים וימחו מן הארץ וישאר אך נח ואשר אתו בתבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 7, 24, 'ויגברו המים על הארץ חמשים ומאת יום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 1, 'ויזכר אלהים את נח ואת כל החיה ואת כל הבהמה אשר אתו בתבה ויעבר אלהים רוח על הארץ וישכו המים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 2, 'ויסכרו מעינות תהום וארבות השמים ויכל הגשם מן השמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 3, 'וישבו המים מעל הארץ הלכו ושבו ויחסרו המים מקץ חמשים ומאת יום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 4, 'ותנח התבה בחדש השביעי בשבעה עשר יום לחדש על הרי הררט ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 5, 'והמים היו הלכו וחסרו עד חדש העשירי בעשירי באחד לחדש נראו ראשי ההרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 6, 'ויהי מקץ ארבעים יום ויפתח נח את חלון התבה אשר עשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 7, 'וישלח את הערב ויצא יצא ושב עד יבשת המים מעל הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 8, 'וישלח את היונה מאתו לראות הקלו המים מעל פני האדמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 9, 'ולא מצאה היונה מנוח לכף רגלה ותשב אליו אל התבה כי מים על פני כל הארץ וישלח את ידו ויקחה ויבא אתה אליו אל התבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 10, 'ויחל עוד שבעת ימים אחרים ויסף שלח את היונה מן התבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 11, 'ותבא אליו היונה לעת ערב והנה עלי זית טרף בפיה וידע נח כי קלו המים מעל הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 12, 'ויחל עוד שבעת ימים אחרים וישלח את היונה ולא יספה שובה אליו עוד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 13, 'ויהי באחת ושש מאות שנה בראישון באחד לחדש חרבו המים מעל הארץ ויסר נח את מכסה התבה וירא והנה חרבו פני האדמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 14, 'ובחדש השני בשבעה ועשרים יום לחדש יבשה הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 15, 'וידבר אלהים אל נח לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 16, 'צא מן התבה אתה ואשתך ובניך ונשי בניך אתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 17, 'וכל החיה אשר אתך מכל בשר בעוף ובבהמה ובכל הרמש הרמש על הארץ הוצא אתך ושרצו בארץ ופרו ורבו על הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 18, 'ויצא נח ובניו ואשתו ונשי בניו אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 19, 'וכל החיה וכל העוף וכל הרמש . . . הרמש על הארץ למשפחותיהם יצאו מן התבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 20, 'ויבן נח מזבח ליהוה ויקח מכל הבהמה הטהרה ומכל העוף הטהור ויעל עלות במזבח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 21, 'וירח יהוה את ריח הניחח ויאמר יהוה אל לבו לא אוסף עוד לקלל . את האדמה בעבור האדם כי יצר לב האדם רע מנעריו ולא אוסף עוד להכות את כל חי כאשר עשיתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 8, 22, 'עד כל ימי הארץ זרע וקציר קר וחם קיץ וחרף יומם ולילה לא ישבתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 1, 'ויברך אלהים את נח ואת בניו ויאמר להם פרו ורבו ומלאו את הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 2, 'ומראכם וחתתכם יהיה על כל חית הארץ ועל כל עוף השמים בכל אשר תרמש האדמה ובכל דגי הים בידכם נתתיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 3, 'כל רמש אשר הוא חי לכם יהיה לאכלה כירק עשב נתתי לכם את הכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 4, 'אך בשר בנפשו דמו לא תאכלו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 5, 'ואך את דמכם לנפשתיכם אדרש מיד כל חי אדרשנו מיד האדם מיד איש ואחיו אדרש את נפש האדם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 6, 'שפך דם האדם באדם דמו ישפך כי בצלם אלהים עשה את האדם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 7, 'ואתם פרו ורבו ושרצו בארץ ורבו בה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 8, 'ויאמר אלהים אל נח ואל בניו אתו לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 9, 'ואני הנני מקים את בריתי אתכם ואת זרעכם אחריכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 10, 'ואת כל נפש החיה אשר אתכם בעוף ובבהמה ובכל חית הארץ אתכם מכל יצאי התבה לכל חית הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 11, 'והקמתי את בריתי אתכם ולא יכרת עוד כל בשר . ממי המבול ולא יהיה עוד המבול להשחית הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 12, 'ויאמר אלהים זאת אות הברית אשר אני נתן ביני ובינכם ובין כל נפש החיה אשר אתכם לדרת עולם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 13, 'את קשתי נתתי בענן והיתה לאות ברית ביני ובין הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 14, 'והיה בענני ענן על הארץ ונראתה הקשת בענן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 15, 'וזכרתי את בריתי אשר ביני ובינכם ובין כל נפש החיה אשר אתכם בכל בשר ולא יהיה עוד המים למבול להשחית כל בשר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 16, 'והיתה הקשת בענן וראיתה לאזכרה ברית עולם בין אלהים ובין כל נפש החיה בכל בשר אשר על הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 17, 'ויאמר אלהים אל נח זאת אות הברית אשר הקמתי ביני ובין כל בשר אשר על הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 18, 'ויהיו בני נח היצאים מן התבה שם חם ויפת וחם הוא אבי כנען ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 19, 'שלשה אלה בני נח מאלה נפצה כל הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 20, 'ויחל נח איש האדמה ויטע כרם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 21, 'וישת מן היין וישכר ויתגל בתוך אהלו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 22, 'וירא חם אבי כנען את ערות אביו ויגד לשני אחיו בחוץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 23, 'ויקח שם ויפת את השמלה וישמו על שכם שניהם וילכו אחרנית ויכסו את ערות אביהם ופניהם אחרנית וערות אביהם לא ראו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 24, 'ויקץ נח מיינו וידע את אשר עשה לו בנו הקטן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 25, 'ויאמר ארור כנען עבד עבדים יהיה לאחיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 26, 'ויאמר ברוך יהוה אלהי שם ויהי כנען עבד למו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 27, 'יפת אלהים ליפת וישכן באהלי שם ויהי כנען עבד למו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 28, 'ויחי נח אחרי המבול שלש מאות שנה וחמשים שנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 9, 29, 'ויהיו כל ימי נח תשע מאות שנה וחמשים שנה וימת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 1, 'ואלה תולדת בני נח שם חם ויפת ויולדו להם בנים אחרי המבול ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 2, 'בני יפת גמר ומגוג מדי ויון תובל ומשך ותירס ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 3, 'ובני גמר אשכנז ריפת ותגרמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 4, 'ובני יון אליש ותרשיש כתים ודודנים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 5, 'מאלה נפרדו איי הגוים בארצותם איש ללשנו למשפחותם בגויהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 6, 'ובני חם כוש ומצרים פוט וכנען ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 7, 'ובני כוש סבא וחוילה סבתה ורעמה וסבתכה ובני רעמה שבא ודדן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 8, 'וכוש הוליד את נמרוד הוא החל להיות גבור בארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 9, 'הוא היה גבור ציד לפני יהוה על כן יאמר כנמרוד גבור ציד לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 10, 'ותהי ראשית ממלכתו בבל וארך ואכד וכלנה בארץ שנער ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 11, 'מן הארץ ההיא יצא אשור ויבן את נינוה ואת רחבות עיר ואת כלח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 12, 'ואת רסן בין נינוה ובין כלח היא העיר הגדלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 13, 'ומצרים ילד את לדים ואת ענמים ואת להבים ואת נפתחים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 14, 'ואת פתרסים ואת כסלחים אשר יצאו משם פלשתים ואת כפתרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 15, 'וכנען ילד את צידון בכורו ואת חת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 16, 'ואת היבוסי ואת האמרי ואת הגרגשי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 17, 'ואת החוי ואת הערוקי ואת הסיני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 18, 'ואת הארודי ואת הצמרי ואת החמתי ואחר נפצה משפחת הכנעני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 19, 'ויהי גבול הכנעני מנהר מצרים . עד הנהר הגדול פרת ועד הים האחרון . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 20, 'אלה בני חם למשפחתם ללשנותם בארצותם לגויהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 21, 'ולשם ילד גם הוא אבי כל בני עבר אחי יפת הגדול ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 22, 'בני שם עילם ואשור וארפכשד ולד וארם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 23, 'ובני ארם עוץ וחויל גתר ומשא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 24, 'וארפכשד ילד את שלח ושלח ילד את עבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 25, 'לעבר ילדו שני בנים שם האחד פלג כי בימיו נפלגה הארץ ושם אחיו יקטן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 26, 'ויקטן ילד את אלמודד ואת שלף ואת חצרמות ואת ירח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 27, 'ואת הדורם ואת איזל ואת דקלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 28, 'ואת עיבל ואת אבימאל ואת שבא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 29, 'ואת אפר ואת חוילה ואת יובב כל אלה בני יקטן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 30, 'ויהי מושבם ממשא באכה ספרה הר הקדם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 31, 'אלה בני שם למשפחותם ללשנותם בארצותם לגויהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 10, 32, 'אלה משפחות בני נח לתולדתם בגויהם מאלה נפרדו איי הגוים בארץ אחרי המבול ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 1, 'ויהי כל הארץ שפה אחת ודברים אחדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 2, 'ויהי בנסעם מקדם וימצאו בקעה בארץ שנער וישבו שם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 3, 'ויאמרו איש אל רעהו הבה נלבנה לבנים ונשרפה לשרפה ותהי להם הלבנה לאבן והחמר היה להם לחמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 4, 'ויאמרו הבה נבנה לנו עיר ומגדל וראשו בשמים ונעשה לנו שם פן נפוץ על פני כל הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 5, 'וירד יהוה לראות את העיר ואת המגדל אשר בנו בני האדם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 6, 'ויאמר יהוה הן עם אחד ושפה אחת לכלם וזה החלם לעשות ועתה לא יבצר מהם כל אשר יזמנו לעשות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 7, 'הבה נרדה ונבלה שם שפתים אשר לא ישמע איש את שפת רעהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 8, 'ויפץ יהוה אתם משם על פני כל הארץ ויחדלו לבנות את העיר ואת המגדל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 9, 'על כן קרא שמה בבל כי שם בלל יהוה את שפת כל הארץ ומשם הפיצם יהוה על פני כל הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 10, 'אלה תולדת שם שם בן מאת שנה ויולד את ארפכשד שנתים אחרי המבול ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 11, 'ויחי שם אחרי הולידו את ארפכשד חמש מאות שנה ויולד בנים ובנות ויהיו כל ימי שם שש מאות שנה וימת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 12, 'וארפכשד חי חמש ושלשים ומאת שנה ויולד את שלח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 13, 'ויחי ארפכשד אחרי הולידו את שלח שלש שנים ושלש מאות שנה ויולד בנים ובנות ויהיו כל ימי ארפכשד שמנה ושלשים שנה וארבע מאות שנה וימת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 14, 'ויחי שלח שלשים שנה ומאת שנה ויולד את עבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 15, 'ויחי שלח אחרי הולידו את עבר שלש שנים ושלש מאות שנה ויולד בנים ובנות ויחיו כל ימי שלח שלש ושלשים שנה וארבע מאות שנה וימת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 16, 'ויחי עבר ארבע ושלשים ומאת שנה ויולד את פלג ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 17, 'ויחי עבר אחרי הולידו את פלג שבעים שנה ומאתים . שנה ויולד בנים ובנות ויהיו כל ימי עבר ארבע שנימ וארבע מאות שנה וימת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 18, 'ויחי פלנ שלשימ שנה ומאת שנה ויולד את רעו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 19, 'ויחי פלג אחרי הולידו את רעו תשע שנים ומאת שנה ויולד בנים ובנות ויהיו כל ימי פלג תשע ושלשים ומאתים שנה וימת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 20, 'ויחי רעו שתים ושלשים ומאת שנה ויולד את שרוג ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 21, 'ויחי רעו אחרי הולידו את שרוג שבע שנים ומאת שנה ויולד בנים ובנות ויהיו כל ימי רעו תשע ושלשים ומאתים שנה וימת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 22, 'ויחי שרוג שלשים שנה ומאת שנה ויולד את נחור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 23, 'ויחי שרוג אחרי הולידו את נחור מאת שנה ויולד בנים ובנות ויהיו כל ימי שרוג שלשים שנה ומאתים שנה וימת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 24, 'ויחי נחור תשע ושבעים שנה ויולד את תרח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 25, 'ויחי נחור אחרי הולידו את תרח תשע . שנים וששים שנה ויולד בנים ובנות ויהיו כל ימי נחור שמנה וארבעים שנה ומאת שנה וימת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 26, 'ויחי תרח שבעים שנה ויולד את אברם את נחור ואת הרן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 27, 'אלה תולדת תרח תרח הוליד את אברם את נחור ואת הרן והרן הוליד את לוט ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 28, 'וימת הרן על פני תרח אביו בארץ מולדתו באור כשדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 29, 'ויקח אברם ונחור להם נשים שם אשת אברם שרי ושם אשת נחור מלכה בת הרן אבי מלכה ואבי יסכה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 30, 'ותהי שרי עקרה אין לה ילד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 31, 'ויקח תרח את אברם בנו ואת לוט בן הרן בן בנו ואת שרי ואת מלכה כלותו אשת אברם ונחור בניו ויוצא אתם מאור כשדים ללכת ארצה כנען ויבאו עד חרן וישבו שם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 11, 32, 'ויהיו ימי תרח חמש שנים וארבעים ומאת שנה וימת תרח בחרן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 1, 'ויאמר יהוה אל אברם לך לך מארצך וממולדתך ומבית אביך אל הארץ אשר אראך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 2, 'ואעשך לגוי גדול ואברכך ואגדלה שמך והוי ברכה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 3, 'ואברך מברכיך ומקלליך אאר ונברכו בך כל משפחות האדמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 4, 'וילך אברם כאשר דבר אליו יהוה וילך אתו לוט ואברם בן חמש שנים ושבעים שנה בצאתו מחרן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 5, 'ויקח אברם את שרי אשתו ואת לוט בן אחיו ואת כל רכושם אשר רכשו ואת הנפש אשר עשו בחרן ויצאו ללכת ארצה כנען ויבאו ארצה כנען ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 6, 'ויעבר אברם בארץ עד מקום שכם עד אלון מורא והכנעני אז בארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 7, 'וירא יהוה אל אברם ויאמר לו לזרעך אתן את הארץ הזאת ויבן שם מזבח ליהוה הנראה אליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 8, 'ויעתק משם ההרה מקדם לבית אל ויט אהלו בית אל מים והעי מקדם ויבן שם מזבח ליהוה ויקרא בשם יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 9, 'ויסע אברם הלך ונסע הנגבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 10, 'ויהי רעב בארץ וירד אברם מצרימה לגור שם כי כבד הרעב בארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 11, 'ויהי כאשר הקריב לבוא מצרימה ויאמר אל שרי אשתו הנה נא ידעתי כי אשה יפת מראה אתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 12, 'והיה כי יראו אתך המצרים ואמרו אשתו זאת והרגו אתי ואתיך יחיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 13, 'אמרי נא אחתי אתי למען ייטב לי בעבורך וחיתה נפשי בגללך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 14, 'ויהי כבוא אברם מצרימה ויראו המצרים את האשה כי יפה היא מאד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 15, 'ויראו אתה שרי פרעה ויהללו אתה אל פרעה ותקח האשה בית פרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 16, 'ולאברם הייטב בעבורה ויהי לו צאן ובקר מקנה כבד מאד . ועבדים ושפחות וחמרים ואתנות וגמלים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 17, 'וינגע יהוה את פרעה נגעים גדלים ואת ביתו על דבר שרי אשת אברם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 18, 'ויקרא פרעה לאברם ויאמר מה זאת עשית לי למה לא הגדת לי כי אשתך הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 19, 'ולמה אמרת אחתי היא ואקח אתה לי לאשה ועתה הנה אשתך קח ולך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 12, 20, 'ויצו עליו פרעה אנשים וישלחו אתו ואת אשתו ואת כל אשר לו ולוט עמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 1, 'ויעל אברם ממצרים הוא ואשתו וכל אשר לו ולוט עמו הנגבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 2, 'ואברם כבד מאד במקנה בכסף ובזהב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 3, 'וילך למסעיו מנגב ועד בית אל עד המקום אשר היה שם אהלו בתחלה בין בית אל ובין העי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 4, 'אל מקום המזבח אשר עשה שם בראישונה ויקרא שם אברם בשם יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 5, 'וגם ללוט ההלך את אברם היה צאן ובקר ואהלים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 6, 'ולא נשאה אתם הארץ לשבת יחדו כי היה רכושם רב ולא יכלו לשבת יחדו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 7, 'ויהי ריב בין רעי מקנה אברם ובין רעי מקנה לוט והכנעני והפרזי אז ישבים בארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 8, 'ויאמר אברם אל לוט אל נא תהיה מריבה ביני ובינך ובין רעי ובין רעיך כי אנשים אחים אנחנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 9, 'הלוא כל הארץ לפניך הפרד נא מעלי אם השמאלה והימינה ואם הימינה והשמאלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 10, 'וישא לוט את עיניו וירא את כל ככר הירדן כי כלו משקה לפני שחת יהוה את סדם ואת עמרה כגן יהוה כארץ מצרים באכה צערה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 11, 'ויבחר לו לוט את כל ככר הירדן ויסע לוט מקדם ויפרדו איש מעל אחיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 12, 'אברם ישב בארץ כנען ולוט ישב בערי הככר ויאהל עד סדם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 13, 'ואנשי סדם רעים וחטאים ליהוה מאד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 14, 'ויהוה אמר אל אברם אחר הפרד לוט מעמו שא נא עיניך וראה מן המקום אשר אתה שם צפונה ונגבה וקדמה וימה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 15, 'כי את כל הארץ אשר אתה ראה לך אתננה ולזרעך עד עולם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 16, 'ושמתי את זרעך כעפר הארץ אשר אם יוכל איש למנות את עפר הארץ גם זרעך ימנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 17, 'קום התהלך בארץ לארכה ולרחבה כי לך אתננה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 13, 18, 'ויאהל אברם ויבא וישב באלוני ממרא אשר בחברון ויבן שם מזבח ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 1, 'ויהי בימי אמרפל מלך שנער ואריוך מלך אלסר כדר לעמר מלך עילם ותדעל מלך גוים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 2, 'עשו מלחמה את ברע מלך סדם ואת ברשע מלך עמרה ושנאב מלך אדמה ושמאבר מלך צביים ומלך בלע היא צער ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 3, 'כל אלה חברו אל עמק השדים הוא ים המלח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 4, 'שתים עשרה שנה עבדו את כדר לעמר ובשלש עשרה שנה מרדו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 5, 'ובארבע עשרה שנה בא כדר לעמר והמלכים אשר אתו ויכו את הרפאים בעשתרות קרנים ואת הזוזים בהם ואת האימים בשוה קריתים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 6, 'ואת החרי בהררי שעיר עד איל פראן אשר על המדבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 7, 'וישבו ויבאו אל עין משפט היא קדש ויכו את כל שדה העמלקי וגם את האמרי הישב בחצצון תמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 8, 'ויצא מלך סדם ומלך עמרה ומלך אדמה ומלך צבאים ומלך בלע היא צער ויערכו אתם מלחמה בעמק השדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 9, 'את כדר לעמר מלך עילם ותדעל מלך גוים ואמרפל מלך שנער ואריוך מלך אלסר ארבעה מלכים את החמשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 10, 'ועמק השדים בארות בארות חמר וינסו מלך סדם ומלך עמרה ויפלו שמ והנשארים ההרה נסו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 11, 'ויקחו את כל רכוש סדם ועמרה ואת כל אכלם וילכו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 12, 'ויקחו את לוט ואת רכשו בן אחי אברם וילך והוא ישב בסדם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 13, 'ויבא הפליט ויגד לאברם העברי והוא שכן באלוני ממרא האמרי אחי אשכול ואחי ענרם והם בעלי ברית אברם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 14, 'וישמע אברם כי נשבה אחיו וירק את חניכיו ילידי ביתו שמנה עשר ושלש מאות וירדף עד דן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 15, 'ויחלק עליהם לילה הוא ועבדיו ויכם וירדפם עד חובה אשר משמאל לדמשק ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 16, 'וישב את כל הרכוש וגם את לוט אחיו ורכושו השיב וגם את הנשים ואת העם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 17, 'ויצא מלך סדם לקראתו אחרי שובו מהכות את כדר לעמר ואת המלכים אשר אתו אל עמק השוה הוא עמק המלך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 18, 'ומלכי צדק מלך שלם הוציא לחם ויין והוא כהן לאל עליון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 19, 'ויברך את אברם ויאמר ברוך אברם לאל עליון קנה שמים וארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 20, 'וברוך אל עליון אשר מגן צריך בידך ויתן לו מעשר מכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 21, 'ויאמר מלך סדם אל אברם תנה לי הנפש והרכוש קח לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 22, 'ויאמר אברם אל מלך סדם הרמתי את ידי אל האלהים אל עליון קנה שמים וארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 23, 'אם מחוט ועד שרוך נעל אם אקח מכל אשר לך ולא תאמר אני העשרתי את אברם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 14, 24, 'בלעדי רק אשר אכלו הנערים וחלק האנשים אשר הלכו אתי ענרם אשכל וממרא הם יקחו חלקם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 1, 'אחר הדברים האלה היה דבר יהוה אל אברם במחזה לאמר אל תירא אברם אנכי מגן לך שכרך ארבה מאד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 2, 'ויאמר אברם אדני יהוה מה תתן לי ואנכי הלך עררי ובן משק ביתי הוא דמשק אליעזר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 3, 'ויאמר אברם הן לי לא נתתה זרע והנה בן ביתי יירש אתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 4, 'והנה דבר יהוה אליו לאמר לא יירשך זה כי אם אשר יצא ממעיך הוא יירשך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 5, 'ויוצא אתו החוצה ויאמר הבט נא השמימ וספר הכוכבים אם תוכל לספר אתם ויאמר לו כה יהיה זרעך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 6, 'והאמן ביהוה ויחשבה לו צדקה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 7, 'ויאמר אליו אני יהוה אשר הוצאתיך מאור כשדים לתת לך את הארץ הזאת לרשתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 8, 'ויאמר אדני יהוה במה אדע כי אירשנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 9, 'ויאמר אליו קח לי עגלה משלשת ועז משלשת ואיל משלש ותר וגוזל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 10, 'ויקח לו את כל אלה ויבתר אתם בתור ויתן איש את בתרו לקראת רעהו ואת הצפרים לא בתר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 11, 'וירד העיט על הפגרים וישב אתם אברם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 12, 'ויהי השמש לבוא ותרדמה נפלה על אברם והנה אימה חשכה גדלה נפלת עליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 13, 'ויאמר לאברם ידע תדע כי גר יהיה זרעך בארץ לא להם ועבדום וענו אתם ארבע מאות שנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 14, 'וגם את הגוי אשר יעבדו דן אנכי ואחרי כן יצאו ברכוש גדול ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 15, 'ואתה תבוא אל אבתיך בשלום תקבר בשיבה טובה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 16, 'ודור הרביעי ישוב הנה כי לא שלם עון האמרי עד הנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 17, 'ויהי השמש בא ועלטה היה והנה תנור עשן ולפיד אש אשר עבר בין הגזרים האלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 18, 'ביום ההוא כרת יהוה את אברם ברית לאמר לזרעך נתתי את הארץ הזאת מנהר מצרים עד הנהר הגדול נהר פרת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 19, 'את הקיני ואת הקנזי ואת הקדמוני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 20, 'ואת החתי ואת הפרזי ואת הרפאים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 15, 21, 'ואת האמרי ואת הכנעני ואת הגרגשי ואת החוי ואת היבוסי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 1, 'ושרי אשת אברם לא ילדה לו ולה שפחה מצרית ושמה הגר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 2, 'ותאמר שרי אל אברם הנה נא עצרני יהוה מלדת בא נא אל שפחתי אולי אבנה ממנה וישמע אברם לקול שרי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 3, 'ותקח שרי אשת אברם את הגר המצרית שפחתה מקץ עשר שנים לשבת אברם בארץ כנען ותתן אתה לאברם אישה לו לאשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 4, 'ויבא אל הגר ותהר ותרא כי הרתה ותקל גברתה בעיניה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 5, 'ותאמר שרי אל אברם חמסי עליך אנכי נתתי שפחתי בחיקך ותרא כי הרתה ואקל בעיניה ישפט יהוה ביני ובינך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 6, 'ויאמר אברם אל שרי הנה שפחתיך בידך עשי לה הטוב בעיניך ותענה שרי ותברח מפניה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 7, 'וימצאה מלאך יהוה על עין המים במדבר על העין בדרך שור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 8, 'ויאמר הגר שפחת שרי אי מזה באתי ואנה תלכי ותאמר מפני שרי גברתי אנכי ברחת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 9, 'ויאמר לה מלאך יהוה שובי אל גברתיך והתעני תחת ידה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 10, 'ויאמר לה מלאך יהוה הרבה ארבה את זרעך ולא יספר מרב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 11, 'ויאמר לה מלאך יהוה הנך הרה וילדתי בן וקראתי את שמו ישמעאל כי שמע יהוה אל עניך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 12, 'והוא יהיה פרה אדם ידו בכל ויד כל בו ועל פני כל אחיו ישכן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 13, 'ותקרא שם יהוה הדבר אליה אתה אל ראה כי אמרה הגם הלם ראיתי אחרי ראה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 14, 'על כן קרא לבאר באר לחי ראי הנה בין קדש ובין ברד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 15, 'ותלד הגר לאברם בן ויקרא אברם את שם בנו אשר ילדה הגר ישמעאל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 16, 16, 'ואברם בן שמנים שנה ושש שנים בלדת הגר את ישמעאל לאברם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 1, 'ויהי אברם בן תשעים . ותשע שנים וירא יהוה אל אברם ויאמר אליו אני אל שדי התהלך לפני והוי תמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 2, 'ואתנה בריתי ביני ובינך וארבה אתך במאד מאד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 3, 'ויפל אברם על פניו וידבר אתו אלהים לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 4, 'אני הנה בריתי אתך והיית לאב המון גוים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 5, 'ולא יקרא עוד . שמך אברם והיה שמך אברהם כי אב המון גוים נתתיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 6, 'והפריתי אתך במאד מאד ונתתיך לגוים ומלכים ממך יצאו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 7, 'והקמתי את בריתי ביני ובינך ובין זרעך אחריך לדרתם לברית עולם להיות לך לאלהים ולזרעך אחריך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 8, 'ונתתי לך ולזרעך אחריך את ארץ מגריך את כל ארץ כנען לאחזת עולם והייתי להם לאלהים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 9, 'ויאמר אלהים אל אברהם ואתה את בריתי תשמר אתה וזרעך אחריך לדרתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 10, 'זאת בריתי אשר תשמרו ביני ובינכם ובין זרעך אחריך המול לכם כל זכר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 11, 'ונמלתם את בשר ערלתכם והיתה לאות ברית ביני ובינכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 12, 'ובן שמנת ימים ימול לכם כל זכר לדרתיכם יליד בית ומקנת כסף מכל בן נכר אשר לא מזרעך הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 13, 'המול ימול ילידי ביתך ומקנת כספך והיתה בריתי בבשרכם לברית עולם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 14, 'וערל זכר אשר לא ימול את בשר ערלתו ביום השמיני ונכרתה הנפש ההיא מעמיה את בריתי הפר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 15, 'ויאמר אלהים אל אברהם שרי אשתך לא תקרא את שמה שרי כי שרה שמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 16, 'וברכתי אתה וגם נתתי ממנה לך בן וברכתיו והיתה לגוים ומלכי עמים ממנה יהיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 17, 'ויפל אברהם על פניו ויצחק ויאמר בלבו הלבן מאת שנה יוליד ואם שרה הבת תשעים שנה תלד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 18, 'ויאמר אברהם אל האלהים לוי ישמעאל יחיה לפניך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 19, 'ויאמר אלהים אבל הנה שרה אשתך ילדת לך בן וקראת את שמו יצחק והקמתי את בריתי אתו לברית עולם ולזרעו אחריו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 20, 'ולישמעאל שמעתיך הנה ברכתי אתו והפריתי אתו והרביתי אתו במאד מאד שנים עשר נשיאים יוליד ונתתיו לגוי גדול ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 21, 'ואת בריתי אקים את יצחק אשר תלד לך שרה למועד הזה בשנה האחרת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 22, 'ויכל לדבר אתו ויעל אלהים מעל אברהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 23, 'ויקח אברהם את ישמעאל בנו ואת כל ילידי ביתו ואת כל מקנת כספו כל זכר באנשי בית אברהם וימל את בשר ערלתם בעצם היום הזה כאשר דבר אתו אלהים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 24, 'ואברהם בן תשעים ותשע שנים בהמלו את בשר ערלתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 25, 'וישמעאל בנו בן שלש עשרה שנה בהמלו את בשר ערלתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 26, 'בעצם היום הזה נמל אברהם וישמעאל בנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 17, 27, 'וכל אנשי ביתו יליד בית ומקנת כסף מאת בן נכר נמלו אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 1, 'וירא אליו יהוה באלוני ממרא והוא ישב פתח האהל כחם היום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 2, 'וישא עיניו וירא והנה שלשה אנשים נצבים עליו וירא וירץ לקראתם מפתח האהל וישתחוי ארצה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 3, 'ויאמר אדני אם נא מצאתי חן בעיניכם אל נא תעברו מעל עבדכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 4, 'יקח נא מעט מים ורחצו רגליכם והשעינו תחת העץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 5, 'ואקח פת לחם וסעדו לבבכם ואחר תעברו כי על כן עברתם על עבדכם ויאמרו כן תעשה כאשר דברת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 6, 'וימהר אברהם האהלה אל שרה ויאמר מהרי שלש סאים קמח סלת לושי ועשי עגות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 7, 'ואל הבקר רץ אברהם ויקח בן בקר רך וטוב ויתן אל הנער וימהר לעשותו . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 8, 'ויקח חמאה וחלב ואת בן הבקר אשר עשה ויתן לפניהם והוא עמד עליהם תחת העץ ויאכלו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 9, 'ויאמרו אליו איה שרה אשתך ויאמר הנה באהל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 10, 'ויאמר שוב אשוב אליך כעת חיה והנה בן לשרה אשתך ושרה שמעת פתח האהל והיא אחריו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 11, 'ואברהם ושרה זקנים באים בימים חדל להיות לשרה ארח כנשים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 12, 'ותצחק שרה בקרבה לאמר אחרי בלתי היתה לי עדנה ואדני זקן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 13, 'ויאמר יהוה אל אברהם למה זה צחקה שרה לאמר האף אמנם אלד ואני זקנתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 14, 'היפלא מיהוה דבר למועד אשוב אליך כעת חיה ולשרה בן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 15, 'ותכחש שרה לאמר לא צחקתי כי יראה ויאמר לה כי צחקתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 16, 'ויקמו משם האנשים וישקפו על פני סדם ואברהם הלך עמם לשלחם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 17, 'ויהוה אמר המכסה אני מאברהם את אשר אני עשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 18, 'ואברהם היו יהיה לגוי גדול ועצום ונברכו בו כל גויי הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 19, 'כי ידעתי למען אשר יצוה אברהם את בניו ואת ביתו אחריו ושמרו דרך יהוה לעשות צדקה ומשפט למען הביא יהוה על אברהם את אשר דבר עליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 20, 'ויאמר יהוה צעקת סדם ועמרה כי רבה וחטאתם כי כבדה מאד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 21, 'ארדה נא ואראה הכצעקתה הבאה אלי עשו כלה ואם לא אדעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 22, 'ויפנו משם האנשים וילכו סדמה ואברהם עודנו עמד לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 23, 'ויגש אברהם ויאמר האף תספה צדיק עם רשע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 24, 'אולי יש חמשים צדיקם בתוך העיר האף תספה ולא תשא למקום למען חמשים הצדקים אשר בקרבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 25, 'חלילה לך מעשת . כדבר הזה להמית צדיק עם רשע והיה כצדיק כרשע חלילה לך השפט כל הארץ לא יעשה משפט ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 26, 'ויאמר יהוה אם אמצא בסדם חמשים צדיקם בתוך העיר ונשאתי לכל המקום בעבורם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 27, 'ויען אברהם ויאמר הנה נא הואלתי לדבר אל אדני ואנכי עפר ואפר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 28, 'אולי יחסרון חמשים הצדיקם חמשה התשחית בחמשה את כל העיר ויאמר לא אשחית אם אמצא שם ארבעים וחמשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 29, 'ויסף עוד לדבר אליו ויאמר אליו אולי ימצאון שם ארבעים ויאמר לא אשחית בעבור הארבעים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 30, 'ויאמר אל נא יחר לאדני ואדברה אולי ימצאון שם שלשים ויאמר לא אשחית אם אמצא שם שלשים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 31, 'ויאמר הנה נא הואלתי לדבר אל אדני אולי ימצאון שם עשרים ויאמר לא אשחית בעבור העשרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 32, 'ויאמר אל נא יחר לאדני ואדברה אך הפעם אולי ימצאון שם עשרה ויאמר לא אשחית בעבור העשרה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 18, 33, 'וילך יהוה כאשר כלה לדבר אל אברהם ואברהם שב למקומו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 1, 'ויבאו שני המלאכים סדמה בערב ולוט ישב בשער סדם וירא לוט ויקם לקראתם וישתחוי אפים ארצה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 2, 'ויאמר הנה נא אדני סורו נא אל בית עבדכם ולינו ורחצו רגליכם והשכמתם והלכתם לדרככם ויאמרו לא כי ברחב נלין ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 3, 'ויפצר בם מאד ויסרו אליו ויבאו אל ביתו ויעש להם משתה ומצות אפה ויאכלו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 4, 'טרם ישכבו ואנשי העיר אנשי סדם נסבו על הבית מנער ועד זקן כל העם מקצה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 5, 'ויקראו אל לוט ויאמרו אליו איה האנשים אשר באו אליך הלילה הוצאם אלינו ונדע אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 6, 'ויצא אליהם לוט הפתח והדלת סגרו אחריו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 7, 'ויאמר אל נא אחי תריעו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 8, 'הנה נא לי שתי בנות אשר לא ידעו איש אוציא נא אתהן אליכם ועשו להן כטוב בעיניכם רק לאנשים האלה אל תעשו דבר כי על כן באו בצל קורתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 9, 'ויאמרו גשה הלאה ויאמרו האחד בא לגור וישפט גם שפט עתה נריע לך מהם ויפצרו באיש בלוט מאד ויגשו לשבר הדלת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 10, 'וישלחו האנשים את ידם ויביאו את לוט אליהם הביתה ואת הדלת סגרו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 11, 'ואת האנשים אשר פתח הבית הכו בסנורים מקטן ועד גדול וילאו למצא הפתח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 12, 'ויאמרו המלאכים אל לוט עוד מי לך פה חתן בניך ובנתיך וכל אשר לך בעיר הוציא מן המקום הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 13, 'כי משחתים אנחנו את המקום הזה כי גדלה צעקתם את פני יהוה וישלחנו יהוה להשחיתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 14, 'ויצא לוט וידבר אל חתניו לקחי בנתיו ויאמר קומו צאו מן המקום הזה כי משחית יהוה את העיר ויהי כמצחק בעיני חתניו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 15, 'וכמו השחר עלה ויאיצו המלאכים בלוט לאמר קום קח את אשתך ואת שתי בנתיך הנמצאות פן תספה בעון העיר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 16, 'ויתמהמה ויחזקו האנשים בידו וביד אשתו וביד שתי בנתיו בחמלת יהוה עליו ויוצאהו וינחהו מחוץ לעיר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 17, 'ויהי כהוציאם אתם החוצה ויאמר המלט על נפשך אל תביט אחריך ואל תעמד בכל הככר ההרה המלט פן תספה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 18, 'ויאמר לוט אליהם אל נא אדני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 19, 'הנה נא מצא עבדך חן בעיניך ותגדל חסדך אשר עשית עמדי להחיות את נפשי ואנכי לא אוכל להמלט ההרה פן תדבקני הרעה ומתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 20, 'הנה נא העיר הזאת קרובה לנוס שמה והיא מצער אמלט נא שמה הלוא מצער הוא ותחי נפשי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 21, 'ויאמר אליו הנה נשאתי פניך גם לדבר הזה לבלתי הפכי את העיר אשר דברת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 22, 'מהר המלט שמה כי לא אוכל לעשות דבר עד באך שמה על כן קרא שם העיר צער ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 23, 'השמש יצא על הארץ ולוט בא צערה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 24, 'ויהוה המטיר על סדם ועל עמרה גפרית ואש מאת יהוה מן השמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 25, 'ויהפך את הערים האלה ואת כל הככר ואת כל ישבי הערים וצמח האדמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 26, 'ותבט אשתו מאחריו ותהי נצב מלח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 27, 'וישכם אברהם בבקר אל המקום אשר עמד שם את פני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 28, 'וישקף על פני סדם ועמרה ועל פני כל הארץ הככר וירא והנה עלה קיטור הארץ כקיטור הכבשן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 29, 'ויהי בהשחית אלהים את ערי הככר ויזכר אלהים את אברהם וישלח את לוט מתוך האפכה בהפכו את הערים אשר ישב בהן לוט ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 30, 'ויעל לוט מצער וישב בהר ושתי בנתיו עמו כי ירא לשבת בצער וישב במערה הוא ושתי בנתיו עמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 31, 'ותאמר הבכירה אל הצעירה אבינו זקן ואיש אין בארץ לבוא עלינו כדרך כל הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 32, 'לכי נשקה את אבינו יין ונשכבה עמו ונחיה מאבינו זרע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 33, 'ותשקינה את אביהן יין בלילה ההוא ותבא הבכירה ותשכב את אביה ולא ידע בשכבה ובקומה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 34, 'ויהי ממחרת ותאמר הבכירה אל הצעירה הנה שכבתי אמש את אבי נשקנו יין גם הלילה ובאי שכבי עמו ונחיה מאבינו זרע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 35, 'ותשקינה גם בלילה ההוא את אביהן יין ותקם הצעירה ותשכב עמו ולא ידע בשכבה ובקומה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 36, 'ותהרנה שתי בנות לוט מאביהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 37, 'ותלד הבכירה בן ותקרא את שמו מואב הוא אבי מואב עד היום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 19, 38, 'והצעירה גם היא ילדה בן ותקרא את שמו בן עמי הוא אבי בני עמון עד היום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 1, 'ויסע משם אברהם ארץ הנגב וישב בין קדש ובין שור ויגר בגרר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 2, 'ויאמר אברהם אל שרה אשתו אחתי היא וישלח אבימלך מלך גרר ויקח את שרה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 3, 'ויבא אלהים אל אבימלך בחלום הלילה ויאמר לו הנך מת על אדות האשה אשר לקחת והיא בעילת בעל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 4, 'ואבימלך לא קרב אליה ויאמר אדני הגוי גם צדיק תהרג ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 5, 'הלוא הוא אמר לי אחתי היא והיא גם . אמרה אחי הוא בתם לבבי ובנקיון כפי עשיתי זאת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 6, 'ויאמר אליו האלהים בחלום גם אנכי ידעתי כי בתם לבבך עשית זאת ואחשך גם אנכי אתך מחטאה לי על כן לא נתתיך לנגע אליה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 7, 'ועתה השב את אשת האיש כי נביא הוא ויתפלל בעדך וחיה ואם אינך משיב דע כי מות תמות אתה וכל אשר לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 8, 'וישכם אבימלך בבקר ויקרא לכל עבדיו וידבר את כל הדברים האלה באזניהם וייראו כל האנשים מאד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 9, 'ויקרא אבימלך לאברהם ויאמר לו מה עשית לנו ומה חטאתי לך כי הבאת עלי ועל ממלכתי חטאה גדלה מעשים אשר לא יעשון עשית עמדי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 10, 'ויאמר אבימלך אל אברהם מה ראית כי עשית את הדבר הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 11, 'ויאמר אברהם כי יראתי כי אמרתי רק אין יראת אלהים במקום הזה והרגוני על דבר אשתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 12, 'וגם אמנם אחתי היא בת אבי . אך לא בת אמי ותהי לי לאשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 13, 'ויהי כאשר התעה אתי אלהים מבית אבי ומארץ מולדתי ואמר לה זה חסדך אשר תעשין עמדי אל כל המקום אשר נבוא שמה אמרי לי אחי הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 14, 'ויקח אבימלך אלף כסף וצאן ובקר ועבדים ושפחות ויתן לאברהם וישב לו את שרה אשתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 15, 'ויאמר אבימלך הנה ארצי לפניך בטוב בעיניך שב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 16, 'ולשרה אמר הנה נתתי אלף כסף לאחיך הנה הוא לך כסות עינים לכל אשר אתך ואת הכל ונוכחת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 17, 'ויתפלל אברהם אל האלהים וירפא אלהים את אבימלך ואת אשתו ואמהתיו וילדו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 20, 18, 'כי עצר עצר אלהים בעד כל רחם לבית אבימלך על דבר שרה אשת אברהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 1, 'ויהוה פקד את שרה כאשר אמר ויעש יהוה לשרה כאשר דבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 2, 'ותהר ותלד שרה לאברהם בן לזקניו למועד אשר דבר אתו אלהים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 3, 'ויקרא אברהם את שם בנו הנולד לו אשר ילדה לו שרה יצחק ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 4, 'וימל אברהם את יצחק בנו בן שמנת ימים כאשר צוה אתו אלהים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 5, 'ואברהם בן מאת שנה בהולד לו את יצחק בנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 6, 'ותאמר שרה צחק עשה לי אלהים כל השמע יצחק לי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 7, 'ותאמר מי מלל לאברהם היניקה בנים שרה כי ילדתי לו בן לזקניו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 8, 'ויגדל הילד ויגמל ויעש אברהם משתה גדול ביום הגמל את יצחק בנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 9, 'ותרא שרה את בן הגר המצרית אשר ילדה לאברהם מצחק ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 10, 'ותאמר לאברהם גרש את האמה הזאת ואת בנה כי לא יירש בן האמה הזאת עם בני עם יצחק ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 11, 'וירע הדבר מאד בעיני אברהם על אודת בנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 12, 'ויאמר אלהים אל אברהם אל ירע בעיניך על הנער ועל אמתך כל אשר תאמר אליך שרה שמע בקולה כי ביצחק יקרא לך זרע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 13, 'וגם את בן האמה הזאת לגוי גדול אשימנו כי זרעך הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 14, 'וישכם אברהם בבקר ויקח לחם וחמת מים ויתן אל הגר שם על שכמה ואת הילד וישלחה ותלך ותתע במדבר באר שבע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 15, 'ויכלו המים מן החמת ותשלך את הילד תחת אחד השחים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 16, 'ותלך ותשב לה מנגד הרחק כמטחוי קשת כי אמרה אל אראה במות הילד ותשב מנגד ותשא את קולה ותבך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 17, 'וישמע אלהים את קול הנער ויקרא מלאך אלהים אל הגר מן השמים ויאמר לה מה לך הגר אל תיראי כי שמע אלהים את קול הנער באשר הוא שם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 18, 'קומי שאי את הנער והחזקי את ידך בו כי לגוי גדול אשימנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 19, 'ויפקח אלהים את עיניה ותרא באר מים ותלך ותמלא את החמת מים ותשק את הנער ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 20, 'ויהי אלהים את הנער ויגדל וישב במדבר ויהי רבי קשת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 21, 'וישב במדבר פראן ותקח לו אמו אשה מארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 22, 'ויהי בעת ההיא ויאמר אבימלך ופיכל שר צבאו אל אברהם לאמר אלהים עמך בכל אשר אתה עשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 23, 'ועתה השבע לי באלהים הנה אם תשקר לי ולניני ולנגדי וכחסד אשר עשיתי עמך תעשה עמדי ועם הארץ אשר גרת בה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 24, 'ויאמר אברהם אנכי אשבע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 25, 'ויוכיח אברהם את אבימלך על אדות באר המים אשר גזלו עבדי אבימלך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 26, 'ויאמר אבימלך לא ידעתי מי עשה את הדבר הזה וגם אתה לא הגדת לי וגם אנכי לא שמעתי בלתי היום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 27, 'ויקח אברהם צאן ובקר ויתן לאבימלך ויכרתו שניהם ברית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 28, 'ויצב אברהם . שבע כבשות צאן לבדהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 29, 'ויאמר אבימלך אל אברהם מה הנה שבע הכבשות האלה אשר הצבת לבדהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 30, 'ויאמר אברהם כי את שבע הכבשות תקח מידי בעבור תהיה לי לעדה כי חפרתי את הבאר הזאת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 31, 'על כן קרא למקום ההוא באר שבע כי שם נשבעו שניהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 32, 'ויכרתו ברית בבאר שבע ויקם אבימלך ופיכל שר צבאו וישבו אל ארץ פלשתים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 33, 'ויטע אברהם אשל בבאר שבע ויקרא שם בשם יהוה אל עולם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 21, 34, 'ויגר אברהם בארץ פלשתים ימים רבים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 1, 'ויהי אחר הדברים האלה והאלהים נסה את אברהם ויאמר אליו אברהם ויאמר הנני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 2, 'ויאמר קח נא את בנך את יחידך אשר אהבת את יצחק ולך לך אל ארץ המוראה והעלהו שם עלה על אחד ההרים אשר אמר אליך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 3, 'וישכם אברהם בבקר ויחבש את חמרו ויקח את שני נעריו אתו ואת יצחק בנו ויבקע עצי העלה ויקם וילך אל המקום אשר אמר לו האלהים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 4, 'ביום השלישי וישא אברהם את עיניו וירא את המקום מרחק ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 5, 'ויאמר אברהם אל נעריו שבו לכם פה עם החמור ואני והנער נלכה עד כה ונשתחוי ונשוב אליכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 6, 'ויקח אברהם את עצי העלה וישם על יצחק בנו ויקח בידו את האש ואת המאכלת וילכו שניהם יחדו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 7, 'ויאמר יצחק אל אברהם אביו ויאמר אבי ויאמר הנני בני ויאמר הנה האש והעצים ואיה השה לעלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 8, 'ויאמר אברהם אלהים יראה לו שה לעלה בני וילכו שניהם יחדו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 9, 'ויבאו אל המקום אשר אמר לו האלהים ויבן שם אברהם את המזבח ויערך את העצים ויעקד את יצחק בנו וישם אתו על המזבח ממעל לעצים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 10, 'וישלח אברהם את ידו ויקח את המאכלת לשחט את בנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 11, 'ויקרא אליו מלאך יהוה מן השמים ויאמר אברהם אברהם ויאמר הנני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 12, 'ויאמר אל תשלח ידך על הנער ואל תעשה לו מאומה כי עתה ידעתי כי ירא אלהים אתה ולא חשכת את בנך את יחידך ממני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 13, 'וישא אברהם את עיניו וירא והנה איל אחר נאחז בסבך בקרניו וילך אברהם ויקח את האיל ויעלהו עלה תחת בנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 14, 'ויקרא אברהם את שם המקום ההוא יהוה יראה אשר יאמר היום בהר יהוה יראה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 15, 'ויקרא מלאך יהוה אל אברהם שנית מן השמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 16, 'ויאמר בי נשבעתי נאם יהוה כי יען אשר עשית את הדבר הזה ולא חשכת את בנך את יחידך ממני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 17, 'כי ברוך אברכך והרבה ארבה את זרעך ככוכבי השמים וכחול אשר על שפת הים ויירש זרעך את שער איביו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 18, 'והתברכו בזרעך כל גוי הארץ עקב אשר שמעת בקולי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 19, 'וישב אברהם אל נעריו ויקמו וילכו יחדו אל באר שבע וישב אברהם בבאר שבע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 20, 'ויהי אחר הדברים האלה ויגד לאברהם לאמר הנה ילדה מלכה גם היא בנים לנחור אחיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 21, 'את עוץ בכרו ואת בוז אחיו ואת קמואל אבי ארם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 22, 'ואת כשד ואת חזו ואת פילדש ואת ידלף ואת בתואל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 23, 'ובתואל הוליד את רבקה שמנה אלה ילדה מלכה לנחור אחי אברהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 22, 24, 'ופילגשו ושמה רומה ותלד גם היא את טבח ואת גחם ואת תחש ואת מעכה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 1, 'ויהיו חיי שרה מאת שנה ועשרים שנה ושבע שנים שני חיי שרה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 2, 'ותמת שרה בקרית הארבע אל עמק היא חברון בארץ כנען ויבא אברהם לספד לשרה ולבכותה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 3, 'ויקם אברהם מעל פני מתו וידבר אל בני חת לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 4, 'גר ותושב אנכי עמכם תנו לי אחזת קבר עמכם ואקברה את מתי מלפני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 5, 'ויענו בני חת את אברהם לאמר לא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 6, 'שמענו אדני נשיא אלהים אתה בתוכנו במבחר קברינו קבר את מתך איש ממנו את קברו לא יכלה ממך מקבר מתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 7, 'ויקם אברהם וישתחוי לעם הארץ לבני חת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 8, 'וידבר אתם לאמר אם יש את נפשכם לקבר את מתי מלפני שמעוני ופגעו לי בעפרון בן צהר החתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 9, 'ויתן לי את מערת המכפלה אשר לו אשר בקצה שדהו בכסף מלא יתננה לי בתוככם לאחזת קבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 10, 'ועפרון ישב בתוך בני חת ויען עפרון החתי את אברהם באזני בני חת לכל באי שער עירו לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 11, 'לא אדני שמעני השדה נתתי לך והמערה אשר בו לך נתתיה לעיני בני עמי נתתיה לך קבר מתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 12, 'וישתחוי אברהם לפני עם הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 13, 'וידבר אל עפרון באזני עם הארץ לאמר אך אם אתה לי שמעני נתתי כסף השדה קח ממני ואקברה את מתי שם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 14, 'ויען עפרון את אברהם לאמר לא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 15, 'אדני שמעני ארץ ארבע מאות שקל כסף ביני ובינך מה הוא ואת מתך קבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 16, 'וישמע אברהם אל עפרון וישקל אברהם לעפרן את הכסף אשר דבר באזני בני חת ארבע מאות שקל כסף עבר לסחר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 17, 'ויקם שדה עפרון אשר במכפלה אשר על פני ממרא השדה והמערה אשר בו וכל העץ אשר בשדה אשר בכל גבולו סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 18, 'לאברהם למקנה לעיני בני חת בכל באי שער עירו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 19, 'ואחרי כן קבר אברהם את שרה אשתו אל מערת שדה המכפלה על פני ממרא היא חברון בארץ כנען ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 23, 20, 'ויקם השדה והמערה אשר בו לאברהם לאחזת קבר מאת בני חת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 1, 'ואברהם זקן בא בימים ויהוה ברך את אברהם בכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 2, 'ויאמר אברהם אל עבדו זקן ביתו המשל בכל אשר לו שים נא ידך תחת ירכי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 3, 'ואשביעך ביהוה אלהי השמים ואלהי הארץ אשר לא תקח אשה לבני מבנות הכנעני אשר אנכי ישב בקרבו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 4, 'כי אל ארצי ואל מולדתי תלך ולקחת אשה לבני ליצחק ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 5, 'ויאמר אליו העבד אולי לא תאבה האשה ללכת אחרי אל הארץ הזאת ההשב אשיב את בנך אל הארץ אשר יצאת משם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 6, 'ויאמר אליו אברהם השמר לך פן תשיב את בני שמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 7, 'יהוה אלהי השמים אשר לקחני מבית אבי ומארץ מולדתי ואשר דבר לי ואשר נשבע לי לאמר לזרעך אתן את הארץ הזאת הוא ישלח מלאכו לפניך ולקחת אשה לבני משם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 8, 'ואם לא תאבה האשה ללכת אחריך ונקית משבעתי זאת רק את בני לא תשיב שמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 9, 'וישם העבד את ידו תחת ירך אברהם אדניו וישבע לו על הדבר הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 10, 'ויקח העבד עשרה גמלים מגמלי אדניו וילך וכל טוב אדניו בידו ויקם וילך אל ארם נהרים אל עיר נחור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 11, 'ויברך הגמלים מחוץ לעיר על באר המים לעת ערב לעת צאת השאבות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 12, 'ויאמר יהוה אלהי אדני אברהם הקרא נא לפני היום ועשה חסד עם אדני אברהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 13, 'הנה אנכי נצב על עין המים ובנות אנשי העיר יצאת לשאב מים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 14, 'והיה הנערה אשר אמר אליה הטי נא כדך ואשתה ואמרה שתה וגם גמליך אשקה אתה הוכחת לעבדך ליצחק ובה אדע כי עשית חסד עם אדני אברהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 15, 'ויהי הוא טרם כלה לדבר אל לבו והנה רבקה יצאת אשר ילדה לבתואל בן מלכה אשת נחור אחי אברהם וכדה על שכמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 16, 'והנערה טובת מראה מאד בתולה ואיש לא ידעה ותרד העין ותמלא כדה ותעל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 17, 'וירץ העבד לקראתה ויאמר הגמיני נא מעט מים מכדך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 18, 'ותאמר שתה אדני ותמהר ותורד כדה על ידה ותשקהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 19, 'ותכל להשקתו ותאמר גם לגמליך אשאב עד אם כלו לשתות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 20, 'ותמהר ותורד כדה על השקות ותרץ עוד אל הבאר לשאב ותשאב לכל גמליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 21, 'והאיש משתה לה ומחריש לדעת ההצליח יהוה דרכו אם לא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 22, 'ויהי כאשר כלו הגמלים לשתות ויקח האיש נזם זהב בקע משקלו וישם על אפה ושני צמידים על ידיה עשרה זהב משקלם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 23, 'ויאמר בת מי אתי הגידי נא לי היש בבית אביך מקום לנו ללין ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 24, 'ותאמר אליו בת בתואל אנכי בן מלכה אשר ילדה לנחור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 25, 'ותאמר אליו גם תבן גם מספה רב עמנו וגם מקום ללין ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 26, 'ויקד האיש וישתחוי ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 27, 'ויאמר ברוך יהוה אלהי אדני אברהם אשר לא עזב חסדו ואמתו מעם אדני אברהם אנכי בדרך נחני יהוה בית אחי אדני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 28, 'ותרץ הנערה ותגד לבית אמה כדברים האלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 29, 'ולרבקה אח ושמו לבן וירץ לבן אל האיש החוצה אל העין ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 30, 'ויהי כראותו את הנזם ואת הצמדים על ידי אחתו וכשמעו את דברי רבקה אחתו לאמר כה דבר אלי האיש ויבא אל האיש והנה עמד על הגמלים על העין ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 31, 'ויאמר בא ברוך יהוה למה תעמד בחוץ ואנכי פניתי הבית ומקום לגמלים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 32, 'ויבא האיש הביתה ויפתח הגמלים ויתן תבן ומספה לגמלים ומים לרחץ רגליו ורגלי האנשים אשר אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 33, 'ויושם לפניו לאכל ויאמר לא אכל עד אם דברתי דברי ויאמרו דבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 34, 'ויאמר עבד אברהם אנכי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 35, 'ויהוה ברך את אדני מאד ויגדל ויתן לו צאן ובקר וכסף וזהב ועבדים ושפחות וגמלים וחמרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 36, 'ותלד שרה אשת אדני בן לאדני אחרי זקנתו ויתן לו את כל אשר לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 37, 'וישבעני אדני לאמר לא תקח אשה לבני מבנות הכנעני אשר אנכי ישב בארצו כי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 38, 'אם . אל בית אבי תלך ואל משפחתי ולקחת אשה לבני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 39, 'ואמר אל אדני אולי לא תלך האשה אחרי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 40, 'ויאמר אלי יהוה אשר התהלכתי לפניו ישלח מלאכו אתך והצליח דרכך ולקחת אשה לבני ממשפחתי ומבית אבי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 41, 'אז תנקה מאלתי כי תבוא אל משפחתי ואם לא יתנו לך והיית נקיא מאלתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 42, 'ואבא היום אל העין ואמר יהוה אלהי אדני אברהם אם ישך נא מצליח דרכי אשר אני הלך עליה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 43, 'הנה אנכי נצב על עין המים והיה העלמה היצאת לשאב ואמרתי אליה השקני נא מעט מים מכדך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 44, 'ואמרה אלי גם אתה שתה וגם לגמליך אשאב היא האשה אשר הוכיח יהוה לבן אדני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 45, 'אני טרם אכלה לדבר אל לבי והנה רבקה יצאת וכדה על שכמה ותרד העין ותשאב ואמר אליה השקני נא מעט מים מכדך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 46, 'ותמהר ותורד כדה מעליה ותאמר שתה וגם גמליך אשקה ואשתה וגם הגמלים השקתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 47, 'ואשאל אתה ואמר בת מי אתי ותאמר בת בתואל בן נחור אשר ילדה לו מלכה ואשים הנזם על אפה והצמדים על ידיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 48, 'ואקד ואשתחוה ליהוה ואברך את יהוה אלהי אדני אברהם אשר הנחני בדרך אמת לקחת את בת אחי אדני לבנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 49, 'ועתה אם ישכם עשים חסד ואמת את אדני הגידו לי ואם לא הגידו לי ואפנה אל הימין או אל השמאל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 50, 'ויען לבן ובתואל ויאמרו מיהוה יצא הדבר לא נוכל דבר אליך רע . וטוב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 51, 'הנה רבקה לפניך קח ולך ותהי אשה לבן אדניך כאשר דבר יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 52, 'ויהי כאשר שמע עבד אברהם את דבריהם וישתחוי ארצה ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 53, 'ויוצא העבד כלי כסף וכלי זהב ובגדים ויתן לרבקה ומגדנות נתן לאחיה ולאמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 54, 'ויאכלו וישתו הוא והאנשים אשר עמו וילינו ויקמו בבקר ויאמר שלחוני לאדני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 55, 'ויאמרו אחיה ואמה תשב הנערה אתנו ימים או חדש ואחר תלך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 56, 'ויאמר אליהם אל תאחרו אתי ויהוה הצליח את דרכי שלחוני ואלכה אל אדני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 57, 'ויאמרו נקרא לנערה ונשאל את פיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 58, 'ויקראו לרבקה ויאמרו לה התלכי עם האיש הזה ותאמר אלך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 59, 'וישלחו את רבקה אחתם ואת מינקתה ואת עבד אברהם ואת אנשיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 60, 'ויברכו את רבקה ויאמרו לה אחתנו אתי הוי לאלפי רבבה ויירש זרעך את שער איביו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 61, 'ותקם רבקה ונערתיה ותרכבנה על הגמלים ותלכנה אחרי האיש ויקח העבד את רבקה וילך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 62, 'ויצחק בא במדבר באר לחי ראי והוא ישב בארץ הנגב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 63, 'ויצא יצחק לשוח בשדה לפנות ערב וישא עיניו וירא והנה הגמלים באים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 64, 'ותשא רבקה את עיניה ותרא את יצחק ותפל מעל הגמל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 65, 'ותאמר אל העבד מי האיש הלז ההלך בשדה לקראתנו ויאמר העבד הוא אדני ותקח הצעיף ותתכס ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 66, 'ויספר העבד ליצחק את כל הדברים אשר עשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 24, 67, 'ויבאה יצחק האהלה שרה אמו ויקח את רבקה ותהי לו לאשה ויאהבה וינחם יצחק אחרי אמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 1, 'ויסף אברהם ויקח אשה ושמה קטורה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 2, 'ותלד לו את זמרון ואת יקשן ואת מדן ואת מדין ואת ישבק ואת שוח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 3, 'ויקשן ילד את שבא ואת דדן ובני דדן היו אשורם ולטשים ולאמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 4, 'ובני מדין עיפה ועפר וחנוך ואבידע ואלדעה כל אלה בני קטורה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 5, 'ויתן אברהם את כל אשר לו ליצחק בנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 6, 'ולבני הפלגשים אשר לאברהם נתן אברהם מתנות וישלחם מעל יצחק בנו בעודנו חי קדמה אל ארץ קדםה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 7, 'ואלה ימי שני חיי אברהם אשר חי מאת שנה ושבעים שנה וחמש שנים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 8, 'ויגוע וימת אברהם בשיבה טובה זקן ושבע ימים ויאסף אל עמיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 9, 'ויקברו אתו יצחק וישמעאל בניו אל מערת המכפלה אל שדה עפרון בן צהר החתי אשר על פני ממרא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 10, 'השדה אשר קנה אברהם מאת בני חת שם קבור אברהם ושרה אשתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 11, 'ויהי אחרי מות אברהם ויברך אלהים את יצחק בנו וישב יצחק עם באר לחי ראי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 12, 'ואלה תולדת ישמעאל בן אברהם אשר ילדה הגר המצרית שפחת שרה לאברהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 13, 'ואלה שמות בני ישמעאל בשמותם לתולדתם בכור ישמעאל נבאות וקדר ואדבאל ומבשם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 14, 'ומשמע ודומה ומשא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 15, 'חדד ותימה יטור נפיש וקדמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 16, 'אלה הם בני ישמעאל ואלה שמותם בחצרותם ובטירתם שנים עשר נשיאם לאמתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 17, 'ואלה שני חיי ישמעאל מאת שנה ושלשים שנה ושבע שנים ויגוע וימת ויאסף אל עמיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 18, 'וישכנו מחוילה עד שור אשר על פני מצרים באכה אשורה על פני כל אחיו נפל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 19, 'ואלה תולדת יצחק בן אברהם אברהם הוליד את יצחק ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 20, 'ויהי יצחק בן ארבעים שנה בקחתו את רבקה בת בתואל הארמי מפדן ארם אחות לבן הארמי לו לאשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 21, 'ויעתר יצחק ליהוה לנכח אשתו כי עקרה היא ויעתר לו יהוה ותהר רבקה אשתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 22, 'ויתרצצו הבנים בקרבה ותאמר אם כן למה זה אנכי ותלך לדרש את יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 23, 'ויאמר יהוה לה שני גוים בבטנך ושני לאמים ממעיך יפרדו ולאם מלאם יאמץ ורב יעבד צעיר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 24, 'וימלאו ימיה ללדת והנה תאמים בבטנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 25, 'ויצא הראישון אדמני כלו כאדרת שער ויקראו שמו עשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 26, 'ואחרי כן יצא אחיו וידו אחזת בעקב עשו ויקראו שמו יעקב ויצחק בן ששים שנה בלדת אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 27, 'ויגדלו הנערים ויהי עשו איש ידע ציד איש שדה ויעקב איש תם ישב אהלים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 28, 'ויאהב יצחק את עשו כי צידו בפיו ורבקה אהבת את יעקב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 29, 'ויזד יעקב נזיד ויבא עשו מן השדה והוא עיף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 30, 'ויאמר עשו אל יעקב הלעטני נא מן האדם האדם הזה כי עיף אנכי על כן קרא שמו אדום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 31, 'ויאמר יעקב מכרה כיום את בכרתך לי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 32, 'ויאמר עשו הנה אנכי הלך למות ולמה זה לי בכרה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 33, 'ויאמר יעקב השבע לי כיום וישבע לו וימכר את בכרתו ליעקב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 25, 34, 'ויעקב נתן לעשו לחם ונזיד עד שים ויאכל וישת ויקם וילך ויבז עשו את הבכרה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 1, 'ויהי רעב בארץ מלבד הרעב הראישון אשר היה בימי אברהם וילך יצחק אל אבימלך מלך פלשתים גררה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 2, 'וירא אליו יהוה ויאמר אל תרד מצרימה שכן בארץ אשר אמר אליך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 3, 'גור בארץ הזאת ואהיה עמך ואברכך כי לך ולזרעך אתן את כל הארצות האלה והקמתי את השבעה אשר נשבעתי לאברהם אביך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 4, 'והרביתי את זרעך ככוכבי השמים ונתתי לזרעך את כל הארצות האלה והתברכו בזרעך כל גוי הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 5, 'עקב אשר שמע אברהם אביך בקולי וישמר משמרתי מצותי חקתי ותורתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 6, 'וישב יצחק בגרר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 7, 'וישאלו אנשי המקום על אשתו ויאמר אחתי היא כי ירא לאמר אשתי היא פן יהרגוני אנשי המקום על רבקה כי טובת מראה היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 8, 'ויהי כאשר ארכו לו שם הימים וישקף אבימלך מלך פלשתים בעד החלון וירא והנה יצחק מצחק את רבקה אשתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 9, 'ויקרא אבימלך ליצחק ויאמר אך הנה אשתך היא ואיך אמרת אחתי היא ויאמר אליו יצחק כי אמרתי פן אמות עליה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 10, 'ויאמר אבימלך מה זאת עשית לנו כמעט שכב אחד העם את אשתך והבאת עלינו אשם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 11, 'ויצו אבימלך את כל עמו לאמר הנגע באיש הזה ובאשתו מות יומת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 12, 'ויזרע יצחק בארץ ההיא וימצא בשנה ההיא מאה שערים ויברכהו יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 13, 'ויגדל האיש וילך הלך וגדל עד כי גדל מאד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 14, 'ויהי לו מקנה צאן ומקנה בקר ועבדה רבה ויקנאו אתו פלשתים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 15, 'וכל הבארות אשר חפרו עבדי אביו בימי אברהם אביו סתמום פלשתים וימלאום עפר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 16, 'ויאמר אבימלך אל יצחק לך מעמנו כי עצמת ממנו מאד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 17, 'וילך משם יצחק ויחן בנחל גרר וישב שם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 18, 'וישב יצחק ויחפר את בארות המים אשר חפרו עבדי אברהם אביו ויסתמום פלשתים אחרי מות אברהם ויקרא להן שמות כשמות אשר קרא להן אביו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 19, 'ויחפרו עבדי יצחק בנחל וימצאו שם באר מים חיים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 20, 'ויריבו רעי גרר עם רעי יצחק לאמר לנו המים ויקרא שם הבאר עשק כי התעשקו עמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 21, 'ויחפרו באר אחרת ויריבו גם עליה ויקרא שמה שטנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 22, 'ויעתק משם ויחפרו באר אחרת ולא רבו עליה ויקרא שמה רחבות ויאמר כי עתה הרחיב יהוה לנו ופרינו בארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 23, 'ויעל משם באר שבע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 24, 'וירא אליו יהוה בלילה ההוא ויאמר אנכי אלהי אברהם אביך אל תירא כי אתך אנכי וברכתיך והרביתי את זרעך בעבור אברהם עבדי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 25, 'ויבן שם מזבח ויקרא בשם יהוה ויט שם אהלו ויכרו שם עבדי יצחק באר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 26, 'ואבימלך הלך אליו מגרר ואחזת מרעהו ופיכל שר צבאו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 27, 'ויאמר אליהם יצחק מדוע באתם אלי ואתם שנאתם אתי ותשלחוני מאתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 28, 'ויאמרו ראו ראינו כי היה יהוה עמך ונאמר תהיה נא אלה בינתנו ביננו ובינך ונכרתה ברית עמך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 29, 'אם תעשה עמנו רע כאשר לא נגענוך וכאשר עשינו עמך רק טוב ונשלחך בשלום עתה אתה ברוך יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 30, 'ויעש להם משתה ויאכלו וישתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 31, 'וישכמו בבקר וישבעו איש לרעהו וישלחם יצחק וילכו מאתו בשלום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 32, 'ויהי ביום ההוא ויבאו עבדי יצחק ויגדו לו על אדות הבאר אשר חפרו ויאמרו לו מצאנו מים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 33, 'ויקרא אתה שבעה על כן שם העיר באר שבע עד היום הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 34, 'ויהי עשו בן ארבעים שנה ויקח אשה את יהודית בת בארי החתי ואת בשמת בת אילון החוי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 26, 35, 'ותהיינה מרת רוח ליצחק ולרבקה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 1, 'ויהי כי זקן יצחק ותכהין עיניו מראות ויקרא את עשו בנו הגדול ויאמר אליו בני ויאמר אליו הנני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 2, 'ויאמר הנה נא זקנתי לא ידעתי יום מותי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 3, 'ועתה שא נא כליך תליתך וקשתך וצא השדה וצוד לי ציד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 4, 'ועשה לי מטעמים כאשר אהבתי והבאת לי ואכל בעבור תברכך נפשי בטרם אמות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 5, 'ורבקה שמעת בדבר יצחק אל עשו בנו וילך עשו השדה לצוד ציד להביא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 6, 'ורבקה אמרה אל יעקב בנה לאמר הנה שמעתי את אביך מדבר אל עשו אחיך לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 7, 'הבא לי ציד ועשה לי מטעמים ואכלה ואברכך לפני יהוה לפני מותי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 8, 'ועתה בני שמע בקולי לאשר אני מצוה אתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 9, 'לך נא אל הצאן וקח לי משם שני גדיי עזים טובים ואעשה אתם מטעמים לאביך כאשר אהב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 10, 'והבאת לאביך ואכל בעבור אשר יברכך לפני מותו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 11, 'ויאמר יעקב אל רבקה אמו הנה עשו אחי איש שעיר ואנכי איש חלק ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 12, 'אולי ימשני אבי והייתי בעיניו כמתעתע והבאתי עלי קללה ולא ברכה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 13, 'ותאמר לו אמו עלי קללתך בני אך שמע בקולי ולך קח לי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 14, 'וילך ויקח ויבא לאמו ותעש אמו מטעמים כאשר אהב אביו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 15, 'ותקח רבקה את בגדי עשו בנה הגדול החמדות אשר אתה בבית ותלבש את יעקב בנה הקטן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 16, 'ואת עורת גדיי העזים הלבישה על ידיו ועל חלקת צוארו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 17, 'ותתן את המטעמים ואת הלחם אשר עשתה ביד יעקב בנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 18, 'ויבא אל אביו ויאמר אבי ויאמר הנני מי אתה בני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 19, 'ויאמר יעקב אל אביו אנכי עשו בכרך עשיתי כאשר דברת אלי קום נא שבה ואכלה מצידי בעבור תברכני נפשך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 20, 'ויאמר יצחק אל בנו מה זה מהרת למצא בני ויאמר כי הקרא יהוה אלהיך לפני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 21, 'ויאמר יצחק אל יעקב גשה נא ואמשך בני האתה זה בני עשו אם לא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 22, 'ויגש יעקב אל יצחק אביו וימשהו ויאמר הקול קול יעקב והידים ידי עשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 23, 'ולא הכירו כי היו ידיו כידי עשו אחיו שעירות ויברכהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 24, 'ויאמר האתה זה בני עשו ויאמר אני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 25, 'ויאמר הגישה לי ואכל מציד בני למען תברכך נפשי ויגש לו ויאכל ויבא לו יין וישת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 26, 'ויאמר אליו יצחק אביו גשה נא ושקה לי בני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 27, 'ויגש וישק לו וירח את ריח בגדיו ויברכהו ויאמר ראו ריח בני כריח השדה מלא אשר ברכהו יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 28, 'ויתן לך האלהים מטל השמים ומשמני הארץ ורב דגן ותירש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 29, 'יעבדוך עמים וישתחוו לך לאמים הוי גביר לאחיך וישתחוו לך בני אמך ארריך ארור ומברכיך ברוך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 30, 'ויהי כאשר כלה יצחק לברך את יעקב ויהי אך יצא יצא יעקב מאת פני יצחק אביו ועשו אחיו בא מצידו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 31, 'ויעש גם הוא מטעמים ויבא לאביו ויאמר לאביו יקום אבי ויאכל מציד בנו בעבור תברכני נפשך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 32, 'ויאמר אליו יצחק אביו מי אתה ויאמר אני בנך בכרך עשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 33, 'ויחרד יצחק חרדה גדלה עד מאד ויאמר מי אפוא . הצד ציד ויבא לי ואכל מכל בטרם תבוא ואברכהו וגם ברוך יהיה ויהי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 34, 'כשמע עשו את דברי אביו ויצעק צעקה גדלה ומרה עד מאד ויאמר לאביו ברכני גם אני אבי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 35, 'ויאמר בא אחיך במרמה ויקח את ברכתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 36, 'ויאמר הכו קרא שמו יעקב ויעקבני זה פעמים את בכרתי לקח והנה עתה לקח ברכתי ויאמר הלא אצלת לי ברכה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 37, 'ויען יצחק ויאמר לעשו הן גביר שמתיו לך ואת כל אחיו נתתי לו לעבדים ודגן ותירש סמכתיו ולך אפוא מה אעשה בני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 38, 'ויאמר עשו אל אביו הברכה אחת היא לך אבי ברכני גם אני אבי וישא עשו קולו ויבך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 39, 'ויען יצחק אביו ויאמר אליו הן משמני הארץ יהיה מושבך ומטל השמים מעל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 40, 'ועל חרבך תחיה ואת אחיך תעבד והיה כאשר תאדר ופרקת עולו מעל צוארך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 41, 'וישטם עשו את יעקב על הברכה אשר ברכו אביו ויאמר עשו בלבו יקרבו ימי אבל אבי ואהרגה את יעקב אחי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 42, 'ויגד לרבקה את דברי עשו בנה הגדול ותשלח ותקרא ליעקב בנה הקטן ותאמר אליו הנה עשו אחיך מתנחם לך להרגך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 43, 'ועתה בני שמע בקולי וקום ברח לך אל לבן אחי חרנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 44, 'וישבת עמו ימים אחדים עד אשר תשוב חמת אחיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 45, 'עד שוב אף אחיך ממך ושכח את אשר עשית לו ושלחתי ולקחתיך משם למה אשכל גם שניכם יום אחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 27, 46, 'ותאמר רבקה אל יצחק קצתי בחיי מפני בנות חת אם לקח יעקב אשה מבנות חת כאלה מבנות הארץ למה לי חיים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 1, 'ויקרא יצחק אל יעקב ויברך אתו ויצוהו ויאמר לו לא תקח אשה מבנות כנען ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 2, 'קום לך פדנה ארם בית בתואל אבי אמך וקח לך משם אשה מבנות לבן אחי אמך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 3, 'ואל שדי יברך אתך ויפרך וירבך והיית לקהל עמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 4, 'ויתן לך את ברכת אברהם אביך לך ולזרעך אתך לרשת את ארץ מגריך אשר נתן יהוה לאברהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 5, 'וישלח יצחק את יעקב וילך פדנה ארם אל לבן בן בתואל הארמי אחי רבקה אם יעקב ועשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 6, 'וירא עשו כי ברך יצחק את יעקב ושלח אתו פדנה ארם לקחת לו משם אשה בברכו אתו ויצו עליו לאמר לא תקח אשה מבנות כנען ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 7, 'וישמע יעקב אל אביו ואל אמו וילך פדנה ארם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 8, 'וירא עשו כי רעות בנות כנען בעיני יצחק אביו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 9, 'וילך עשו . . ויקח את מחלת בת ישמעאל בן אברהם אחות נבאות על נשיו לו לאשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 10, 'ויצא יעקב מבאר שבע ללכת חרנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 11, 'ויפגע במקום וילן שם כי בא השמש ויקח מאבני המקום וישם מראישתו וישכב במקום ההוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 12, 'ויחלם והנה סלם מצב ארצה וראשו מגיע השמים והנה מלאכי אלהים עלים וירדים בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 13, 'והנה יהוה נצב עליו ויאמר אני יהוה אלהי אברהם אביך ואלהי יצחק הארץ אשר אתה שכב עליה לך אתננה ולזרעך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 14, 'והיה זרעך כעפר הארץ ופרצת ימה וקדמה וצפונה ונגבה ונברכו בך כל משפחות האדמה ובזרעך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 15, 'והנה אנכי עמך ושמרתך בכל אשר תלך והשבתך אל האדמה הזאת כי לא אעזבך עד אשר אם עשיתי את אשר דברתי לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 16, 'ויקץ יעקב משנתו ויאמר אכן יש יהוה במקום הזה ואנכי לא ידעתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 17, 'ויירא ויאמר מה נורא המקום הזה אין זה כי אם בית אלהים וזה שער השמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 18, 'וישכם יעקב בבקר ויקח את האבן אשר שם מראישתו וישם אתה מצבה ויצק שמן על ראשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 19, 'ויקרא את שם המקום ההוא בית אל ואולם לוזה שם העיר לראישנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 20, 'וידר יעקב נדר לאמר אם יהיה אלהים עמדי ושמרני בדרך הזה אשר אני הלך ונתן לי לחם לאכל ובגד ללבש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 21, 'ושבתי בשלום אל בית אבי והיה יהוה לי לאלהים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 28, 22, 'והאבן הזאת אשר שמתי מצבה יהיה בית אלהים וכל אשר תתן לי עשר אעשרנו לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 1, 'וישא יעקב רגליו וילך ארץ בני קדם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 2, 'וירא והנה באר בשדה והנה שם שלשה עדרי צאן רבצים עליה כי מן הבאר ההיא ישקו העדרים ואבן גדלה על פי הבאר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 3, 'ונאספו שם כל הרעים וגללו את האבן מעל פי הבאר והשקו את הצאן והשיבו את האבן על פי הבאר למקומה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 4, 'ויאמר להם יעקב אחי מאין אתם ויאמרו מחרן אנחנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 5, 'ויאמר להם הידעתם את לבן בן נחור ויאמרו ידענו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 6, 'ויאמר להם השלום לו ויאמרו שלום והנה רחל בתו באה עם הצאן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 7, 'ויאמר להם הן עוד היום גדול לא עת האסף המקנה השקו הצאן ולכו רעו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 8, 'ויאמרו לא נוכל עד אשר יאספו כל הרעים וגללו את האבן מעל פי הבאר והשקינו הצאן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 9, 'עודנו מדבר עמם ורחל באה עם הצאן אשר לאביה כי רעה היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 10, 'ויהי כאשר ראה יעקב את רחל בת לבן אחי אמו ואת צאן לבן אחי אמו ויגש יעקב ויגל את האבן מעל פי הבאר וישק את צאן לבן אחי אמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 11, 'וישק יעקב לרחל וישא את קולו ויבך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 12, 'ויגד יעקב לרחל כי אחי אביה הוא וכי בן רבקה הוא ותרץ ותגד לאביה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 13, 'ויהי כשמע לבן את שמע יעקב בן אחתו וירץ לקראתו ויחבק לו וינשק לו ויבאהו אל ביתו ויספר ללבן את כל הדברים האלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 14, 'ויאמר לו לבן אך עצמי ובשרי אתה וישב עמו חדש ימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 15, 'ויאמר לבן ליעקב הכי אחי אתה ועבדתני חנם הגידה לי מה משכרתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 16, 'וללבן שתי בנות שם הגדלה לאה ושם הקטנה רחל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 17, 'ועיני לאה רכות ורחל היתה יפת תאר ויפת מראה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 18, 'ויאהב יעקב את רחל ויאמר אעבדך שבע שנים ברחל בתך הקטנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 19, 'ויאמר לבן טוב תתי אתה לך מתתי אתה לאיש אחר שב עמדי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 20, 'ויעבד יעקב ברחל שבע שנים ויהיו בעיניו כימים אחדים באהבתו אתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 21, 'ויאמר יעקב אל לבן הבה את אשתי כי מלאו ימי ואבא אליה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 22, 'ויאסף לבן את כל אנשי המקום ויעש משתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 23, 'ויהי בערב ויקח את לאה בתו ויבא אתה אליו ויבא אליה יעקב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 24, 'ויתן לבן . את זלפה שפחתו ללאה בתו לשפחה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 25, 'ויהי בבקר והנה היא לאה ויאמר אל לבן מה זאת עשית לי הלוא ברחל עבדתי עמך ולמה רמיתני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 26, 'ויאמר לבן לא יעשה כן במקומנו לתת הצעירה לפני הבכירה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 27, 'מלא שבע זאת ואתן לך גם את זאת בעבדה אשר תעבד עמדי עוד שבע שנים אחרות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 28, 'ויעש יעקב כן וימלא שבע זאת ויתן לו את רחל בתו לו לאשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 29, 'ויתן לבן לרחל בתו את בלהה שפחתו לה לשפחה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 30, 'ויבא גם אל רחל ויאהב גם את רחל מלאה ויעבד עמו עוד שבע שנים אחרות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 31, 'וירא יהוה כי שנואה לאה ויפתח את רחמה ורחל עקרה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 32, 'ותהר לאה ותלד בן ותקרא את שמו ראובן כי אמרה כי ראה יהוה בעניי כי עתה יאהבני אישי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 33, 'ותהר עוד ותלד בן ותאמר כי שמע יהוה כי שנואה אנכי ויתן לי גם את זה ותקרא את שמו שמעון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 34, 'ותהר עוד ותלד בן ותאמר עתה הפעם ילוה אישי אלי כי ילדתי לו שלשה בנים על כן קראה שמו לוי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 29, 35, 'ותהר עוד ותלד בן ותאמר הפעם אודה את יהוה על כן קראה שמו יהודה ותעמד מלדת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 1, 'ותרא רחל כי לא ילדה ליעקב ותקנא רחל באחתה ותאמר אל יעקב הבה לי בנים ואם אין מתה אנכי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 2, 'ויחר אף יעקב ברחל ויאמר התחת אלהים אנכי אשר מנע ממך פרי בטן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 3, 'ותאמר הנה אמתי בלהה בא אליה ותלד על ברכי ואבנה גם אנכי ממנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 4, 'ותתן לו את בלהה שפחתה לאשה ויבא אליה יעקב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 5, 'ותהר בלהה ותלד ליעקב בן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 6, 'ותאמר רחל דנני אלהים וגם שמע בקולי ויתן לי בן על כן קראה שמו דן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 7, 'ותהר עוד ותלד בלהה שפחת רחל בן שני ליעקב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 8, 'ותאמר רחל נפתלי אלהים נפתלתי עם אחתי וגם יכלתי ותקרא את שמו נפתלי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 9, 'ותרא לאה כי עמדה מלדת ותקח את זלפה שפחתה ותתן אתה ליעקב לאשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 10, 'ותלד זלפה שפחת לאה ליעקב בן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 11, 'ותאמר לאה בגד ותקרא את שמו גד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 12, 'ותלד זלפה שפחת לאה בן שני ליעקב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 13, 'ותאמר לאה באשרי כי אשרוני בנות ותקרא את שמו אשר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 14, 'וילך ראובן בימי קציר חטים וימצא דודאים בשדה ויבא אתם אל לאה אמו ותאמר רחל אל לאה תני נא לי מדודאי בנך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 15, 'ותאמר לה המעט קחתך את אישי ולקחתי גם את דודאי בני ותאמר רחל לכן ישכב עמך הלילה תחת דודאי בנך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 16, 'ויבא יעקב מן השדה בערב ותצא לאה לקראתו ותאמר אלי תבוא כי שכר שכרתך בדודאי בני וישכב עמה בלילה ההוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 17, 'וישמע אלהים אל לאה ותהר ותלד ליעקב בן חמישי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 18, 'ותאמר לאה נתן אלהים שכרי אשר נתתי שפחתי לאישי ותקרא את שמו יששכר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 19, 'ותהר עוד לאה ותלד בן ששי ליעקב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 20, 'ותאמר לאה זבדני אלהים אתי זבד טוב הפעם יזבלני אישי כי ילדתי לו ששה בנים ותקרא את שמו זבולן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 21, 'ואחר ילדה בת ותקרא את שמה דינה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 22, 'ויזכר אלהים את רחל וישמע אליה אלהים ויפתח את רחמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 23, 'ותהר ותלד בן ותאמר אסף אלהים את חרפתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 24, 'ותקרא את שמו יוסף לאמר יסף יהוה לי בן אחר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 25, 'ויהי כאשר ילדה רחל את יוסף ויאמר יעקב אל לבן שלחני ואלכה אל מקומי ואל ארצי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 26, 'תנה את נשי ואת ילדי אשר עבדתי אתך בהן ואלכה כי אתה ידעת את עבדתי אשר עבדתיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 27, 'ויאמר אליו לבן אם נא מצאתי חן בעיניך נחשתי ויברכני יהוה בגללך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 28, 'ויאמר נקבה שכרך עלי ואתנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 29, 'ויאמר אליו אתה ידעת את אשר עבדתיך ואת אשר היה מקניך אתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 30, 'כי מעט אשר היה לך לפני ויפרץ לרב ויברך יהוה אתך לרגלי ועתה מתי אעשה גם אנכי לביתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 31, 'ויאמר מה אתן לך ויאמר יעקב לא תתן לי מאומה אם תעשה לי את הדבר הזה אשובה ארעה צאנך אשמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 32, 'אעבר בכל צאנך היום הסר משם כל שה נקוד וטלוא וכל שה חום בכשבים וטלוא ונקוד בעזים והיה שכרי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 33, 'וענתה בי צדקתי ביום מחר כי יבוא על שכרי לפניך כל אשר איננו נקוד וטלוא בעזים וחום בכשבים גנוב הוא אתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 34, 'ויאמר לבן והן לא יהיה כדבריך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 35, 'ויסר ביום ההוא את התישים העקדים והטלאים ואת כל העזים הנקדות והטלאת כל אשר לבן בו וכל חום בכשבים ויתן ביד בניו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 36, 'וישם דרך שלשת ימים בינם ובין יעקב ויעקב רעה את צאן לבן הנותרת ויאמר מלאך אלהימ אל יעקב בחלום ויאמר יעקב ויאמר הנני ויאמר שא נא עיניך וראה את כל העתודים העלים על הצאן עקדים נקדים וברדים כי ראיתי את אשר לבן עשה לך אנכי האל בית אל אשר משחת שם מצבה ואשר נדרת לי שם נדר ועתה קום צא מן הארץ הזאת ושוב אל ארץ אביך ואיטיב עמך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 37, 'ויקח לו יעקב מקל לבנה לח ולוז וערמון ויפצל בהם פצלות לבנות מחשף הלבן אשר על המקלות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 38, 'ויצג את המקלות אשר פצל ברהטים בהשקות המים אשר תבאנה הצאן לשתות לנכח הצאן ויחמנה בבאנה לשתות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 39, 'ויחמנה הצאן על המקלות ותלדנה הצאן עקדים נקדים וטלאים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 40, 'והכשבים הפריד יעקב ויתן פני הצאן איל עקוד וכל חום בצאן לבן וישת לו עדרים לבדו ולא שתם על צאן לבן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 41, 'ויהי בכל יחם הצאן המקשרות ישים יעקב את המקלות לעיני הצאן ברהטים ליחמנה במקלות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 42, 'ובהעטיף הצאן לא ישים והיו העטופים ללבן והקשרים ליעקב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 30, 43, 'ויפרץ האיש מאד מאד ויהי לו צאן רבות ושפחות ועבדים וגמלים וחמרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 1, 'וישמע את דברי בני לבן לאמר לקח יעקב את כל אשר לאבינו ומאשר לאבינו עשה את כל הכבוד הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 2, 'וירא יעקב את פני לבן והנה אינם עמו כתמול שלשום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 3, 'ויאמר יהוה אל יעקב שוב אל ארץ אבתיך ואל מולדתך ואהיה עמך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 4, 'וישלח יעקב ויקרא לרחל וללאה השדה אל צאנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 5, 'ויאמר להן ראה אנכי את פני אביכן כי אינם אלי כתמול שלשום ואלהי אבי היה עמדי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 6, 'ואתן ידעתן כי בכל כחי עבדתי את אביכן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 7, 'ואביכן התל בי ויחלף את משכרתי עשרת מנים ולא נתנו יהוה להריע עמדי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 8, 'אם כה יאמר נקדים יהיה שכרך וילדו כל הצאן נקדים ואם כה יאמר עקדים יהיה שכרך וילדו כל הצאן עקדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 9, 'ויצל יהוה את מקנה אביכן ויתן לי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 10, 'ויהי בעת יחם הצאן ואשא עיני ואראה בחלום והנה העתודים העלים על הצאן עקדים נקדים וברדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 11, 'ויאמר אלי מלאך האלהים בחלום יעקב ואמר הנני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 12, 'ויאמר שא נא עיניך וראה את כל העתודים העלים על הצאן עקדים נקדים וברדים כי ראיתי את כל אשר לבן עשה לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 13, 'אנכי האל בית אל אשר משחת שם מצבה ואשר נדרת לי שם נדר ועתה קום צא מן הארץ הזאת ושוב אל ארץ מולדתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 14, 'ותען רחל ולאה ותאמרנה לו העוד לנו חלק ונחלה בבית אבינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 15, 'הלוא כנכריות נחשבנו לו כי מכרנו ויאכל גם אכל את כספנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 16, 'כי כל העשר אשר הציל יהוה מאבינו לנו הוא ולבנינו ועתה כל אשר אמר אליך אלהים עשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 17, 'ויקם יעקב וישא את נשיו ואת בניו על הגמלים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 18, 'וינהג את כל מקנהו ואת כל רכשו אשר רכש מקנה קנינו אשר רכש בפדן ארם לבוא אל יצחק אביו ארצה כנען ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 19, 'ולבן הלך לגז את צאנו ותגנב רחל את התרפים אשר לאביה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 20, 'ויגנב יעקב את לב לבן הארמי עד בלתי הגיד לו כי ברח הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 21, 'ויברח הוא וכל אשר לו ויקם ויעבר את הנהר וישם את פניו הר הגלעד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 22, 'ויגד ללבן ביום השלישי כי ברח יעקב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 23, 'ויקח את אחיו עמו וירדף אחריו דרך שבעת ימים וידבק אתו בהר הגלעד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 24, 'ויבא אלהים אל לבן הארמי בחלום הלילה ויאמר לו השמר לך פן תדבר עם יעקב מטוב עד רע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 25, 'וישג לבן את יעקב ויעקב תקע את אהלו בהר ולבן תקע את אחיו בהר הגלעד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 26, 'ויאמר לבן ליעקב מה עשית ותגנב את לבבי ותנהג את בנותי כשביות חרב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 27, 'למה נחבת לברח ותגנב אתי ולא הגדת לי ואשלחך בשמחה ובשרים בתף ובכנר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 28, 'ולא נטשתני לנשק לבני ולבנותי עתה הסכלת עשות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 29, 'יש לאל ידי לעשות עמך רע ואלהי אביך אמש אמר אלי לאמר השמר לך מדבר עם יעקב מטוב עד רע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 30, 'ועתה הלך הלכת כי נכסף נכספת לבית אביך למה גנבת את אלהי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 31, 'ויען יעקב ויאמר ללבן כי יראתי כי אמרתי פן תגזל את בנתיך מעמי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 32, 'עם אשר תמצא את אלהיך לא יחיה נגד אחינו הכר לך מה עמדי וקח לך ולא ידע יעקב כי רחל גנבתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 33, 'ויבא לבן ויחפש באהל יעקב ובאהל לאה ובאהל שתי השפחות ולא מצא ויצא מאהל לאה ויבא באהל רחל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 34, 'ורחל לקחה את התרפים ותשמם בכר הגמל ותשב עליהם וימשש לבן את כל האהל ולא מצא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 35, 'ותאמר אל אביה אל יחר בעיני אדני כי לוא אוכל לקום מפניך כי דרך הנשים לי ויחפש ולא מצא את התרפים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 36, 'ויחר ליעקב וירב בלבן ויען יעקב ויאמר ללבן מה פשעי ומה חטאתי כי דלקת אחרי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 37, 'וכי מששת את כל כלי מה מצאת מכל כלי ביתך שים כה נגד אחי ואחיך ויוכיחו בין שנינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 38, 'זה עשרים שנה אנכי עמך רחליך ועזיך לא שכלו אילי צאנך לא אכלתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 39, 'טרפה לא הבאתי אליך אנכי אחטנה מידי . גנובת יום וגנובת לילה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 40, 'הייתי ביום אכלני חרב וקרח בלילה ותדד שנתי מעיני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 41, 'זה לי עשרים שנה בביתך עבדתיך ארבע עשרה שנה בשתי בנתיך ושש שנים בצאנך ותחלף את משכרתי עשרת מנים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 42, 'לו לא אלהי אבי אלהי אברהם ופחד יצחק היה לי כי עתה ריקם שלחתני את עניי ואת יגע כפי ראה אלהים ויוכיח אמש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 43, 'ויען לבן ויאמר אל יעקב הבנות בנותי והבנים בני והצאן צאני וכל אשר אתה ראה לי הוא ולבנותי מה אעשה לאלה היום או לבניהם אשר ילדו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 44, 'ועתה לכה נכרתה ברית אני ואתה והיה לעד ביני ובינך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 45, 'ויקח יעקב אבן וירמה מצבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 46, 'ויאמר יעקב לאחיו לקטו אבנים ויקחו אבנים ויעשו גל ויאכלו שם על הגל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 47, 'ויקרא לו לבן יגר שהדותה ויעקב קרא לו גלעד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 48, 'ויאמר לבן הגל הזה עד ביני ובינך היום על כן קרא שמו גלעד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 49, 'והמצבה אשר אמר יצף יהוה ביני ובינך כי נסתר איש מרעהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 50, 'אם תענה את בנותי ואם תקח נשים על בנותי אין איש עמנו ראה אלהים עד ביני ובינך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 51, 'ויאמר לבן ליעקב הנה הגל הזה והנה המצבה אשר ירית ביני ובינך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 52, 'עד הגל הזה ועדה המצבה אם אני לא אעבר אליך את הגל הזה ואם אתה לא תעבר אלי את הגל הזה ואת המצבה הזאת לרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 53, 'אלהי אברהם ואלהי נחור ישפט ביננו אלהי אברהם וישבע יעקב בפחד אביו יצחק ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 31, 54, 'ויזבח יעקב זבח בהר ויקרא לאחיו לאכל לחם ויאכלו לחם וילינו בהר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 1, 'וישכם לבן בבקר וינשק לבניו ולבנתיו ויברך אתם וילך וישב לבן למקומו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 2, 'ויעקב הלך לדרכו ויפגעו בו מלאכי אלהים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 3, 'ויאמר יעקב כאשר ראם מחנה אלהים זה ויקרא שם המקום ההוא מחנים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 4, 'וישלח יעקב מלאכים לפניו אל עשו אחיו ארץ שעיר שדה אדום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 5, 'ויצו אתם לאמר כה תאמרון לאדני לעשו כה אמר עבדך יעקב עם לבן גרתי ואחר עד עתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 6, 'ויהי לי שור וחמור וצאן ועבד ושפחה ואשלחה להגיד לאדני למצא חן בעיניך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 7, 'וישבו המלאכים אל יעקב לאמר באנו אל אחיך אל עשו וגם הלך לקראתך וארבע מאות איש עמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 8, 'ויירא יעקב מאד ויצר לו ויחץ את העם אשר אתו ואת הצאן ואת הבקר והגמלים לשני מחנות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 9, 'ויאמר אם יבוא עשו אל המחנה האחד והכהו והיה המחנה הנשאר לפלטה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 10, 'ויאמר יעקב אלהי אבי אברהם ואלהי אבי יצחק יהוה האמר אלי שוב ארצך ואל מולדתך ואיטיב עמך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 11, 'קטנתי מכל החסדים ומכל האמת אשר עשית את עבדך כי במקלי עברתי את הירדן הזה ועתה הייתי לשני מחנות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 12, 'הצילני נא מיד אחי מיד עשו כי ירא אנכי אתו פן יבוא והכני האם על הבנים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 13, 'ואתה אמרת היטב איטיב עמך ושמתי את זרעך כחול הים אשר לא יספר מרב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 14, 'וילן שם בלילה ההוא ויקח מן הבא בידו מנחה לעשו אחיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 15, 'עזים מאתים ותישים עשרים רחלים מאתים ואילים עשרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 16, 'גמלים מינקות ובניהם שלשים פרות ארבעים ופרים עשרה אתנות עשרים ועירים עשרה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 17, 'ויתן ביד עבדיו עדר עדר לבדו ויאמר אל עבדיו עברו לפני ורוח תשימו בין עדר ובין עדר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 18, 'ויצו את הראישון לאמר כי יפגשך עשו אחי ושאלך לאמר למי אתה ואנה תלך ולמי אלה לפניך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 19, 'ואמרת לעבדך ליעקב מנחה היא שלוחה לאדני לעשו והנה גם הוא אחרינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 20, 'ויצו גם את השני וגם את השלישי וגם את כל ההלכים אחרי העדרים לאמר כדבר הזה תדברון אל עשו במצאכם אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 21, 'ואמרתם גם הנה עבדך יעקב בא אחרינו כי אמר אכפרה פניו במנחה ההלכת לפני ואחרי כן אראה פניו אולי ישא פני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 22, 'ותעבר המנחה על פניו והוא לן בלילה ההוא במחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 23, 'ויקם בלילה ההוא ויקח את שתי נשיו ואת שתי שפחתיו ואת אחד עשר ילדיו ויעבר את מעבר היבק ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 24, 'ויקחם ויעברם את הנחל ויעבר את כל אשר לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 25, 'ויותר יעקב לבדו ויאבק איש עמו עד עלות השחר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 26, 'וירא כי לא יכל לו ויגע בכף ירכו ותקע כף ירך יעקב בהאבקו עמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 27, 'ויאמר שלחני כי עלה השחר ויאמר לא אשלחך כי אם ברכתני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 28, 'ויאמר אליו מה שמך ויאמר יעקב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 29, 'ויאמר לא יעקב יאמר עוד שמך כי אם ישראל כי שרית עם אלהים ועם אנשים ותוכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 30, 'וישאל יעקב ויאמר הגידה נא שמך ויאמר למה זה תשאל לשמי ויברך אתו שם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 31, 'ויקרא יעקב שם המקום פנואל כי ראיתי אלהים פנים אל פנים ותנצל נפשי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 32, 'ויזרח לו השמש כאשר עבר את פנואל והוא צולע על ירכו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 32, 33, 'על כן לא יאכלו בני ישראל את גיד הנשא אשר על כף הירך עד היום הזה כי נגע בכף ירך יעקב בגיד הנשא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 1, 'וישא יעקב עיניו וירא והנה עשו בא ועמו ארבע מאות איש ויחץ את הילדים על לאה ועל רחל ועל שתי השפחות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 2, 'וישם את השפחות ואת ילדיהן ראישונה ואת לאה וילדיה אחרנים ואת רחל ואת יוסף אחרנים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 3, 'והוא עבר לפניהם וישתחוי ארצה שבע פעמים עד גשתו עד אחיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 4, 'וירץ עשו לקראתו ויחבקהו ויפל על צוארו וישקהו ויבכו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 5, 'וישא . עיניו וירא את הנשים ואת הילדים ויאמר מי אלה לך ויאמר הילדים אשר חנן אלהים את עבדך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 6, 'ותגשנה השפחות הנה וילדיהן ותשתחוינה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 7, 'ותגש גם לאה וילדיה וישתחוו ואחר נגש יוסף ורחל וישתחוו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 8, 'ויאמר מי לך כל המחנה הזה אשר פגשתי ויאמר למצא חן בעיני אדני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 9, 'ויאמר עשו יש לי רב אחי ויהי לך אשר לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 10, 'ויאמר יעקב אל נא אם נא מצאתי חן בעיניך ולקחת מנחתי מידי כי על כן ראיתי פניך כראותי פני אלהים ותרצני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 11, 'קח נא את ברכתי אשר הבאתי לך כי חנני אלהים וכי יש לי כל ויפצר בו ויקח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 12, 'ויאמר נסעה ונלכה ואלכה לנגדך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 13, 'ויאמר אליו אדני ידע כי הילדים רכים והצאן והבקר אעלת עלי ודפקתם יום אחד ומתו כל הצאן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 14, 'יעבר נא אדני לפני עבדו ואני אתנהלה לאטי לרגל המלאכה אשר לפני ולרגל הילדים עד אשר אבוא אל אדני שעירה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 15, 'ויאמר עשו אציגה נא עמך מן העם אשר אתי ויאמר למה זה אמצא חן בעיני אדני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 16, 'וישב ביום ההוא עשו לדרכו שעירה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 17, 'ויעקב נסע סכותה ויבן לו בית ולמקנהו עשה סכות על כן קרא שם המקום סכות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 18, 'ויבא יעקב שלום עיר שכם אשר בארץ כנען בבאו מפדן ארם ויחן את פני העיר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 19, 'ויקן את חלקת השדה אשר נטה שם אהלו מיד בני חמור אבי שכם במאה קשיטה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 33, 20, 'ויצב שם מזבח ויקרא לו אל אלהי ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 1, 'ותצא דינה בת לאה אשר ילדה ליעקב לראות בבנות הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 2, 'וירא אתה שכם בן חמור החוי נשיא הארץ ויקח אתה וישכב אתה ויענה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 3, 'ותדבק נפשו בדינה בת יעקב ויאהב את הנערה וידבר אל לב הנערה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 4, 'ויאמר שכם אל . אביו לאמר קח לי את הילדה הזאת לאשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 5, 'ויעקב שמע כי טמא את דינה בתו ובניו היו את מקנהו בשדה והחריש יעקב עד באם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 6, 'ויצא חמור אבי שכם אל יעקב לדבר אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 7, 'ובני יעקב באו מן השדה כשמעם ויתעצבו האנשים ויחר להם מאד כי נבלה עשה בישראל לשכב את בת יעקב וכן לא יעשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 8, 'וידבר חמור אתם לאמר שכם בני חשקה נפשו בבתכם תנו נא אתה לו לאשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 9, 'והתחתנו אתנו בנתיכם תתנו לנו ואת בנתינו תקחו לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 10, 'ואתנו תשבו והארץ תהיה לפניכם שבו סחרוה ואחזו בה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 11, 'ויאמר שכם אל אביה ואל אחיה אמצא חן בעיניכם ואשר תאמרו אלי אתן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 12, 'הרבו עלי מהר מאד ומתנה ואתן כאשר תאמרו אלי ותנו לי את הנערה לאשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 13, 'ויענו בני יעקב את שכם ואת חמור אביו במרמה וידברו אשר טמאו את דינה אחתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 14, 'ויאמרו אליהם לא נוכל לעשות את הדבר הזה לתת את אחתנו לאיש אשר לו ערלה כי חרפה היא לנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 15, 'אך בזאת נאות לכם אם תהיו כמונו להמול לכם כל זכר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 16, 'ונתנו את בנתינו לכם ואת בנתיכם נקח לנו וישבנו אתכם והיינו כעם אחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 17, 'ואם לא תשמעו אלינו להמול ולקחנו את בתנו והלכנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 18, 'וייטבו דבריהם בעיני חמור ובעיני שכם בנו . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 19, 'ולא אחר הנער לעשות הדבר כי חפץ בבת יעקב והוא נכבד מכל בית אביו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 20, 'ויבא חמור ושכם בנו אל שער עירם וידברו אל אנשי עירם לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 21, 'האנשים האלה שלמים הם אתנו ישבו בארץ ויסחרו אתה והארץ הנה רחבות ידים לפניהם את בנותם נקח לנו לנשים ואת בנתינו נתן להם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 22, 'אך בזאת יאותו לנו האנשים לשבת אתנו להיות לעם אחד בהמול לנו כל זכר כאשר הם נמלים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 23, 'מקניהם וקנינם וכל בהמתם הלוא לנו הם אך נאות להם וישבו אתנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 24, 'וישמעו אל חמור ואל שכם בנו כל יצאי שער עירו וימלו כל זכר כל יצאי שער עירו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 25, 'ויהי ביום השלישי בהיותם כאבים ויקחו שני בני יעקב שמעון ולוי אחי דינה איש חרבו ויבאו על העיר בטח ויהרגו כל זכר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 26, 'ואת חמור ואת שכם בנו הרגו לפי חרב ויקחו את דינה מבית שכם ויצאו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 27, 'ובני יעקב באו על החללים ויבזו העיר אשר טמאו אחתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 28, 'ואת צאנם ואת בקרם ואת חמריהם ואת אשר בעיר ואת אשר בשדה לקחו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 29, 'ואת כל חילם ואת . טפם ואת נשיהם שבו ויבזו את כל אשר בבית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 30, 'ויאמר יעקב אל שמעון ואל לוי עכרתם אתי להבאשני ביושב הארץ בכנעני ובפרזי ואני מתי מספר ונאספו עלי והכני ונשמדתי אני וביתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 34, 31, 'ויאמרו הכ זונה יעשו את אחותנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 1, 'ויאמר אלהים אל יעקב קום עלה בית אל ושב שם ועשה שם מזבח לאל הנראה אליך בברחך מפני עשו אחיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 2, 'ויאמר יעקב אל ביתו ואל כל אשר עמו הסירו את אלהי הנכר אשר בתוככם והטהרו והחליפו שמלתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 3, 'ונקומה ונעלה בית אל ואעשה שם מזבח לאל הענה אתי ביום צרתי ויהי עמדי בדרך אשר הלכתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 4, 'ויתנו אל יעקב את כל אלהי הנכר אשר בידם ואת הנזמים אשר באזניהם ויטמן אתם יעקב תחת האילה אשר עם שכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 5, 'ויסעו ויהי חתת אלהים על הערים אשר סביבתיהם ולא רדפו אחרי בני יעקב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 6, 'ויבא יעקב לוזה אשר בארץ כנען הוא בית אל הוא וכל העם אשר עמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 7, 'ויבן שם מזבח ויקרא למקום אל בית אל כי שם נגלה אליו האלהים בברחו מפני אחיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 8, 'ותמת דבורה מינקת רבקה ותקבר מתחת לבית אל תחת האלון ויקרא שמו אלון בכית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 9, 'וירא אלהים אל יעקב עוד בבאו מפדן ארם ויברך אתו אלהים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 10, 'ויאמר לו אלהים שמך יעקב לא יקרא עוד שמך יעקב כי אם ישראל יהיה שמך ויקרא את שמו ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 11, 'ויאמר לו אלהים אני אל שדי פרה ורבה גוי וקהל גוים יהיה ממך ומלכים מחלציך יצאו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 12, 'ואת הארץ אשר נתתי לאברהם וליצחק לך אתננה ולזרעך אחריך אתן את הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 13, 'ויעל מעליו אלהים במקום אשר דבר אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 14, 'ויצב יעקב מצבה במקום אשר דבר אתו מצבת אבן ויסך עליה נסך ויצק עליה שמן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 15, 'ויקרא יעקב את שם המקום אשר דבר אתו שם אלהים בית אל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 16, 'ויסעו מבית אל ויהי עוד כברת הארץ לבוא אפרתה ותלד רחל ותקש בלדתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 17, 'ויהי בהקשותה בלדתה ותאמר לה המילדת אל תיראי כי גם זה לך בן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 18, 'ויהי בצאת נפשה כי מתה ותקרא את שמו בן אוני ואביו קרא לו בנימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 19, 'ותמת רחל ותקבר בדרך אפרתה היא בית לחם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 20, 'ויצב יעקב מצבה על קברתה היא מצבת קברת רחל עד היום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 21, 'ויסע ישראל ויט אהלה מהלאה למגדל עדר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 22, 'ויהי בשכן ישראל בארץ ההיא וילך ראובן וישכב את בלהה פילגש אביו וישמע ישראל ויהיו בני יעקב שנים עשר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 23, 'בני לאה בכור יעקב ראובן ושמעון ולוי ויהודה ויששכר וזבולן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 24, 'ובני רחל יוסף ובנימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 25, 'ובני בלהה שפחת רחל דן ונפתלי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 26, 'ובני זלפה שפחת לאה גד ואשר אלה בני יעקב אשר ילדו לו בפדן ארם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 27, 'ויבא יעקב אל יצחק אביו ממרא קרית הארבע היא חברון אשר גר שם אברהם ויצחק ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 28, 'ויהיו ימי יצחק מאת שנה ושמנים שנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 35, 29, 'ויגוע יצחק וימת ויאסף אל עמיו זקן ושבע ימים ויקברו אתו עשו ויעקב בניו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 1, 'ואלה תולדת עשו הוא אדום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 2, 'עשו לקח את נשיו מבנות כנען את עדה בת אילון החתי ואת אהליבמה בת ענה בן צבעון החוי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 3, 'ואת מחלת בת ישמעאל אחות נבאות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 4, 'ותלד עדה לעשו את אליפז ומחלת ילדה את רעואל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 5, 'ואהליבמה ילדה את יעוש ואת יעלם ואת קרח אלה בני עשו אשר ילדו לו בארץ כנען ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 6, 'ויקח עשו את נשיו ואת בניו ואת בנתיו ואת כל נפשות ביתו ואת מקנהו ואת כל בהמתו ואת כל קנינו אשר רכש בארץ כנען וילך מארץ כנען מפני יעקב אחיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 7, 'כי היה רכושם רב משבת יחדו ולא יכלה ארץ מגריהם לשאת אתם מפני מקניהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 8, 'וישב עשו בהר שעיר עשו הוא אדום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 9, 'ואלה תולדת עשו אבי אדום בהר שעיר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 10, 'ואלה שמות בני עשו אליפז בן עדה אשת עשו רעואל בן מחלת אשת עשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 11, 'ויהיו בני אליפז תימן ואמר וצפו וגעתם וקנז ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 12, 'ותמנע היתה פילגש לאליפז בן עשו ותלד לאליפז את עמלק אלה בני עדה אשת עשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 13, 'ואלה בני רעואל נחת וזרח שמה ומזה אלה היו בני מחלת אשת עשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 14, 'ואלה היו בני אהליבמה בת ענה בן צבעון אשת עשו ותלד לעשו את יעוש ואת יעלם ואת קרח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 15, 'אלה אלופי בני עשו בני אליפז בכור עשו אלוף תימן אלוף אמר אלוף צפו אלוף קנז ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 16, '. . אלוף געתם אלוף עמלק אלה אלופי אליפז בארץ אדום אלה בני עדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 17, 'ואלה בני רעואל בן עשו אלוף נחת אלוף זרח אלוף שמה אלוף מזה אלה אלופי רעואל בארץ אדום אלה בני מחלת אשת עשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 18, 'ואלה בני אהליבמה אשת עשו אלוף יעוש אלוף יעלם אלוף קרח אלה אלופי אהליבמה בת ענה אשת עשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 19, 'אלה בני עשו ואלה אלופיהם עשו הוא אדום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 20, 'ואלה בני שעיר החרי ישבי הארץ לוטן ושובל וצבעון וענה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 21, 'ודישון ואצר ודישן אלה אלופי החרי בני שעיר בארץ אדום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 22, 'ויהיו בני לוטן חרי והימם ואחות לוטן תמנע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 23, 'ואלה בני שובל עלון ומנחת ועיבל ושפו ואונם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 24, 'ואלה בני צבעון איה וענה הוא ענה אשר מצא את האימים במדבר ברעתו את החמרים לצבעון אביו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 25, 'ואלה בני ענה דישון ואהליבמה בת ענה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 26, 'ואלה בני דישון חמדן ואשבן יתרן וכרן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 27, 'ואלה בני אצר בלהן וזוען ועקן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 28, 'ואלה בני דישן עוץ וארן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 29, 'אלה אלופי החרי אלוף לוטן אלוף שובל אלוף צבעון אלוף ענה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 30, 'אלוף דישון אלוף אצר אלוף דישן אלה אלופי החרי לאלופיהם בארץ שעיר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 31, 'ואלה המלכים אשר מלכו בארץ אדום לפני מלך מלך לבני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 32, 'וימלך באדום בלע בן בעור ושם עירו דנהבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 33, 'וימת בלע וימלך תחתיו יובב בן זרח מבצרה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 34, 'וימת יובב וימלך תחתיו חשם מארץ התימני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 35, 'וימת חשם וימלך תחתיו הדד בן בדד המכה את המדיני בשדה מואב ושם עירו עוית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 36, 'וימת הדד וימלך תחתיו שמלה ממשרקה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 37, 'וימת שמלה וימלך תחתיו שאול מרחבות הנהר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 38, 'וימת שאול וימלך תחתיו בעל חנן בן עכבור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 39, 'וימת בעל חנן . . וימלך תחתיו הדר ושם עירו פעו ושם אשתו מהטבאל בת מטרד בת מי זהב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 40, 'ואלה שמות אלופי עשו למשפחותם למקומתם בשמותם אלוף תמנע אלוף עלוה אלוף יתת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 41, 'אלוף אהליבמה אלוף אלה אלוף פינן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 42, 'אלוף קנז אלוף תימן אלוף מבצר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 36, 43, 'אלוף מגדאל אלוף עירם אלה אלופי אדום למשפחתם בארץ אחזתם הוא עשו אבי אדום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 1, 'וישב יעקב בארץ מגרי אביו בארץ כנען ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 2, 'אלה תולדת יעקב יוסף בן שבע עשרה שנה היה רעה את אחיו בצאן והוא נער את בני בלהה ואת בני זלפה נשי אביו ויבא יוסף את דבתם רעה אל אביהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 3, 'וישראל אהב את יוסף מכל בניו כי בן זקנים הוא לו ויעש לו כיתנת פסים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 4, 'ויראו אחיו כי אתו אהב אביהם מכל בניו וישנאו אתו ולא יכלו דברו לשלום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 5, 'ויחלם יוסף חלום ויגד לאחיו ויוספו עוד שנא אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 6, 'ויאמר אליהם שמעו נא החלום הזה אשר חלמתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 7, 'והנה אנחנו מאלמים אלמים בתוך השדה והנה קמה אלמתי וגם נצבה והנה תסבנה אלמתיכם ותשתחוינה לאלמתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 8, 'ויאמרו לו אחיו המלך תמלך עלינו אם משל תמשל בנו ויוספו עוד שנא אתו על חלמיו ועל דבריו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 9, 'ויחלם עוד חלום אחר ויספר אתו לאחיו ויאמר הנה חלמתי חלום עוד והנה השמש והירח ואחד עשר כוכבים משתחוים לי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 10, 'ויספר לאביו . ולאחיו . ויגער בו אביו ויאמר לו מה החלום הזה אשר חלמת הבא נבוא אני ואמך ואחיך להשתחות לך ארצה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 11, 'ויקנאו בו אחיו ואביו שמר את הדבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 12, 'וילכו אחיו לרעות את צאן אביהם בשכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 13, 'ויאמר ישראל אל יוסף הלוא אחיך רעים בשכם לך ואשלחך אליהם ויאמר לו הנני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 14, 'ויאמר לו לך נא וראה את שלום אחיך ואת שלום הצאן והשבני דבר וישלחהו מעמק חברון ויבא שכמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 15, 'וימצאהו האיש והנה תעה בשדה וישאלהו האיש לאמר מה תבקש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 16, 'ויאמר את אחי אנכי מבקש הגידה נא לי איפה הם רעים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 17, 'ויאמר האיש נסעו מזה כי שמעתם אמרים נלכה דתינה וילך יוסף אחרי אחיו וימצאם בדתין ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 18, 'ויראו אתו מרחק בטרם יקרב אליהם ויתנכלו אתו להמיתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 19, 'ויאמרו איש אל אחיו הנה בעל החלמות הלז בא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 20, 'ועתה לכו ונהרגהו ונשלכהו באחד הברות ואמרנו חיה רעה אכלתו ונראה מה יהיה חלמתיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 21, 'וישמע ראובן ויצלו מידם ויאמר לא נכנו נפש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 22, 'ויאמר אליהם ראובן אל תשפכו דם השליכו אתו אל הבור הזה אשר במדבר ויד אל תשלחו בו למען הציל אתו מידם להשיבו אל אביו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 23, 'ויהי כאשר בא יוסף אל אחיו ויפשטו את יוסף את כיתנתו את כיתנת הפסים אשר עליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 24, 'ויקחו וישלכו אתו הבור והבור רק אין בו מים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 25, 'וישבו לאכל לחם וישאו עיניהם ויראו והנה ארחת ישמעאלים באה מגלעד וגמליהם נשאים נכאת וצרי ולוט הלכים להוריד מצרימה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 26, 'ויאמר יהודה אל אחיו מה בצע כי נהרג את אחינו וכסינו את דמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 27, 'לכו ונמכרנו לישמעאלים וידנו אל תהיה בו כי אחינו ובשרנו הוא וישמעו אחיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 28, 'ויעברו אנשים מדינים סחרים וימשכו ויעלו את יוסף מן הבור וימכרו את יוסף לישמעאלים בעשרים כסף ויביאו את יוסף מצרימה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 29, 'וישב ראובן אל הבור והנה אין יוסף בבור ויקרע את בגדיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 30, 'וישב אל אחיו ויאמר הילד איננו ואני אנה אני בא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 31, 'ויקחו את כיתנת יוסף וישחטו שעיר עזים ויטבלו את הכיתנת בדם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 32, 'וישלחו את כיתנת הפסים ויביאוה אל אביהם ויאמרו זאת מצאנו הכר נא הכיתנת בנך היא אם לא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 33, 'ויכרה ויאמר כיתנת בני היא חיה רעה אכלתו טרף טרף יוסף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 34, 'ויקרע יעקב שמלתיו וישם שק במתניו ויתאבל על בנו ימים רבים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 35, 'ויקמו כל בניו וכל בנתיו לנחמו וימאן להתנחם ויאמר כי ארד על בני אבל שאלה ויבך אתו אביו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 37, 36, 'והמדינים מכרו את יוסף מצרימה לפוטיפר סריס פרעה שר הטבחים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 1, 'ויהי בעת ההיא וירד יהודה מאת אחיו ויט עד איש עדלמי ושמו חירה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 2, 'וירא שם יהודה בת איש כנעני ושמו שוע ויקחה ויבא אליה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 3, 'ותהר ותלד בן ותקרא את שמו ער ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 4, 'ותהר עוד ותלד בן ותקרא את שמו אונן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 5, 'ותסף עוד ותלד בן ותקרא את שמו שלה ויהי בכזבה בלדתה אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 6, 'ויקח יהודה אשה לער בכרו ושמה תמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 7, 'ויהי ער בכור יהודה רע בעיני יהוה וימתהו יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 8, 'ויאמר יהודה לאונן בא אל אשת אחיך ויבם אתה והקם זרע לאחיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 9, 'וידע אונן כי לא לו יהיה הזרע והיה אם בא אל אשת אחיו ושחת ארצה לבלתי נתן זרע לאחיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 10, 'וירע בעיני יהוה את אשר עשה וימת גם אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 11, 'ויאמר יהודה לתמר כלתו שבי אלמנה בית אביך עד יגדל שלה בני כי אמר פן ימות גם הוא כאחיו ותלך תמר ותשב בית אביה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 12, 'וירבו הימים ותמת בת שוע אשת יהודה וינחם יהודה ויעל . לגזזי צאנו הוא וחירה רעהו העדלמי תמנתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 13, 'ויגד לתמר כלתו לאמר הנה חמיך עלה תמנתה לגז צאנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 14, 'ותסר בגדי אלמנותה מעליה ותתכס בצעיף ותתעלף ותשב בפתח עינים אשר על דרך תמנתה כי ראתה כי גדל שלה והיא לא נתנה לו לאשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 15, 'ויראה יהודה ויחשבה לזונה כי כסתה פניה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 16, 'ויט אליה אל הדרך ויאמר הבה נא אבוא אליך כי לא ידע כי כלתו היא ותאמר מה תתן לי כי תבוא אלי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 17, 'ויאמר אנכי אשלח גדי עזים מן הצאן ותאמר אם תתן ערבון עד שלחך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 18, 'ויאמר מה הערבון אשר אתן לך ותאמר חתימך ופתילך ומטך אשר בידך ויתן לה ויבא אליה ותהר לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 19, 'ותקם ותלך ותסר צעיפה מעליה ותלבש בגדי אלמנותה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 20, 'וישלח יהודה את גדי העזים ביד רעהו העדלמי לקחת הערבון מיד האשה ולא מצאה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 21, 'וישאל את אנשי המקום לאמר איה הקדשה ההיא בעינים על הדרך ויאמרו לא היתה בזה קדשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 22, 'וישב אל יהודה ויאמר לא מצאתיה וגם אנשי המקום אמרו לא היתה בזה קדשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 23, 'ויאמר יהודה תקח לה פן נהיה לבוז הנה שלחתי את הגדי הזה ואתה לא מצאתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 24, 'ויהי כמשלשת חדשים ויגד ליהודה לאמר זנתה תמר כלתך וגם הנה הרה לזנים ויאמר יהודה הוציאוה ותשרף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 25, 'היא מוצאת והיא שלחה אל חמיה לאמר לאיש אשר אלה לו אנכי הרה ותאמר הכר נא למי החתים והפתיל והמטה האלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 26, 'ויכר יהודה ויאמר צדקה ממני כי על כן לא נתתיה לשלה בני ולא יסף עוד לדעתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 27, 'ויהי בעת לדתה והנה תאמים בבטנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 28, 'ויהי בלדתה ויתן יד ותקח המילדת ותקשר על ידו שני לאמר זה יצא ראישון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 29, 'ויהי כמשיב ידו והנה יצא אחיו ותאמר מה פרצת עלינו פרץ ותקרא את שמו פרץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 38, 30, 'ואחרי כן יצא אחיו אשר על ידו השני ותקרא שמו זרח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 1, 'ויוסף הורד מצרימה ויקנהו פוטיפר סריס פרעה שר הטבחים איש מצרי מיד הישמעאלים אשר הורידהו שמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 2, 'ויהי יהוה את יוסף ויהי איש מצליח ויהי בית אדניו המצרי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 3, 'וירא אדניו כי יהוה אתו וכל אשר הוא עשה יהוה מצליח בידו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 4, 'וימצא יוסף חן בעיני אדניו וישרת אתו ויפקדהו על ביתו וכל אשר יש לו נתן בידו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 5, 'ויהי מאז הפקיד אתו בביתו ועל כל אשר יש לו ויברך יהוה את בית המצרי בגלל יוסף ויהי ברכת יהוה בכל אשר יש לו בבית ובשדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 6, 'ויעזב כל אשר לו ביד יוסף ולא ידע אתו מאומה כי אם הלחם אשר הוא אכל ויהי יוסף יפה תאר ויפה מראה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 7, 'ויהי אחר הדברים האלה ותשא אשת אדניו את עיניה אל יוסף ותאמר שכבה עמי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 8, 'וימאן ויאמר אל אשת אדניו הן אדני לא ידע אתי מאומה בביתו וכל אשר יש לו נתן בידי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 9, 'איננו גדול בבית הזה ממני ולא חשך ממני מאומה כי אם אתך באשר אתי אשתו ואיך אעשה הרעה הגדלה הזאת וחטאתי לאלהים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 10, 'ויהי כדברה אל יוסף יום ויום ולא שמע אליה לשכב אצלה להיות עמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 11, 'ויהי כיום הזה ויבא יוסף הביתה לעשות מלאכתו בבית ואין איש מאנשי הבית שם בבית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 12, 'ותתפשהו בבגדיו לאמר שכבה עמי ויעזב בגדיו בידה וינס ויצא החוצה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 13, 'ויהי כראותה כי עזב בגדיו בידה וינס ויצא החוצה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 14, 'ותקרא לאנשי ביתה ותאמר להם לאמר ראו הביא לנו איש עברי לצחק בנו בא אלי לשכב עמי ואקרא בקול גדול ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 15, 'ויהי כשמעו כי הרמי קולי ואקרא ויעזב בגדיו בידי וינס ויצא החוצה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 16, 'ותנח בגדיו אצלה עד בא אדניו אל ביתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 17, 'ותדבר אליו כדברים האלה לאמר בא אלי העבד העברי אשר הבאת לנו לצחק בי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 18, 'ויהי כהרמי קולי ואקרא ויעזב בגדיו אצלי וינס החוצה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 19, 'ויהי כשמע אדניו את דברי אשתו אשר דברה אליו לאמר כדברים האלה עשה לי עבדך ויחר אפו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 20, 'ויקח אדני יוסף אתו ויתנהו אל בית הסהר מקום אשר אסורי המלך אסורים ויהי שם בבית הסהר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 21, 'ויהי יהוה את יוסף ויט אליו חסד ויתן חנו בעיני שר בית הסהר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 22, 'ויתן שר בית הסהר ביד יוסף את כל האסורם אשר בבית הסהר ואת כל אשר עשים שם הוא היה עשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 39, 23, 'אין שר בית הסהר ראה את כל מאומה בידו באשר יהוה אתו וכל אשר הוא עשה יהוה מצליח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 1, 'ויהי אחר הדברים האלה חטאו משקה מלך מצרים והאפה לאדניהם למלך מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 2, 'ויקצף פרעה על שני סריסיו על שר המשקים ועל שר האפים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 3, 'ויתן אתם במשמר בית שר הטבחים אל בית הסהר מקום אשר יוסף אסור שם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 4, 'ויפקד שר הטבחים את יוסף אתם וישרת אתם ויהיו ימים במשמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 5, 'ויחלמו חלום שניהם איש חלמו בלילה אחד איש כפתרון חלמו המשקה והאפה אשר למלך מצרים אשר אסורים בבית הסהר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 6, 'ויבא אליהם יוסף בבקר וירא אתם והנם זעפים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 7, 'וישאל את סריסי פרעה אשר אתו במשמר בית אדניו לאמר מדוע פניכם רעים היום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 8, 'ויאמרו אליו חלום חלמנו ופתר אין אתו ויאמר אליהם יוסף הלוא לאלהים פתרנים ספרו נא לי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 9, 'ויספר שר המשקים את חלמו ליוסף ויאמר לו בחלמי והנה גפן לפני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 10, 'ובגפן שלשה שרגים והיא כפרחת עלתה נצה הבשילו אשכלתיה ענבים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 11, 'וכוס פרעה בידי ואקח את הענבים ואשחט אתם אל כוס פרעה ואתן את הכוס על כף פרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 12, 'ויאמר לו יוסף זה פתרנו שלשת השרגים שלשת ימים הם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 13, 'בעוד שלשת ימים ישא פרעה את ראשך והשיבך על כנך ונתת כוס פרעה בידו כמשפט הראישון אשר היית משקהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 14, 'כי אם זכרתני אתך כאשר ייטב לך ועשית נא עמדי חסד והזכרתני אל פרעה והוצאתני מן הבית הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 15, 'כי גנוב נגנבתי מארץ העברים וגם פה לא עשיתי מאומה כי שמו אתי בבור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 16, 'וירא שר האפים כי טוב פתר ויאמר אל יוסף אף אני בחלמי והנה שלשה סלי חרי על ראשי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 17, 'ובסל העליון מכל מאכל פרעה מעשה אפה והעוף אכל אתם מעל הסל מעל ראשי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 18, 'ויען יוסף ויאמר זה פתרנו שלשת הסלים שלשת ימים הם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 19, 'בעוד שלשת ימים ישא פרעה את ראשך מעליך ותלא אתך על העץ ואכל העוף את בשרך מעליך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 20, 'ויהי ביום השלישי יום הולדת את פרעה ויעש משתה לכל עבדיו וישא את ראש שר המשקים ואת ראש שר האפים בתוך עבדיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 21, 'וישב את שר המשקים על משקהו ויתן את הכוס על כף פרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 22, 'ואת שר האפים תלא כאשר פתר להם יוסף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 40, 23, 'ולא זכר שר המשקים את יוסף וישכחהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 1, 'ויהי מקץ שנתים ימים ופרעה חלם והנה עמד על היאר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 2, 'והנה מן היאר עלות שבע פרות יפות מראה ובריאות בשר ותרעינה באחו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 3, 'והנה שבע פרות אחרות עלות אחריהן מן היאר רעות מראה ודקות בשר ותעמדנה אצל הפרות על שפת היאר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 4, 'ותאכלנה הפרות רעות המראה ודקות הבשר את שבע הפרות יפות המראה והבריאות ויקץ פרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 5, 'ויישן ויחלם שנית והנה שבע שבלים עלות בקנה אחד בריאות וטובת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 6, 'והנה שבע שבלים דקות ושדיפת קדים צמחות אחריהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 7, 'ותבלענה השבלים הדקות . את שבע השבלים הבריאות והמליאת ויקץ פרעה והנה חלום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 8, 'ויהי בבקר ותפעם רוחו וישלח ויקרא את כל חרטמי מצרים ואת כל חכמיה ויספר פרעה להם את חלמיו ואין פתר אתם לפרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 9, 'וידבר שר המשקים אל פרעה לאמר את חטאי אני מזכיר היום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 10, 'פרעה קצף על עבדיו ויתן אתם במשמר בית שר הטבחים אתי ואת שר האפים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 11, 'ונחלם חלום בלילה אחד אני והוא איש כפתרון חלמו חלמנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 12, 'ושם אתנו נער עברי עבד לשר הטבחים ונספר לו ויפתר לנו את חלמתינו איש כחלמו פתר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 13, 'ויהי כאשר פתר לנו כן היה אתי השיב על כני ואתו תלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 14, 'וישלח פרעה ויקרא את יוסף וירצהו מן הבור ויגלח ויחלף שמלתיו ויבא אל פרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 15, 'ויאמר פרעה אל יוסף חלום חלמתי ופתר אין אתו ואני שמעתי עליך לאמר התשמע חלום לפתר אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 16, 'ויען יוסף את פרעה לאמר בלעדי אלהים לא יענה את שלום פרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 17, 'וידבר פרעה את יוסף בחלמי והנני עמד על שפת היאר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 18, 'והנה מן היאר עלות שבע פרות בריאות בשר ויפת תאר ותרעינה באחו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 19, 'והנה שבע פרות אחרות עלות אחריהן דלות ורעות תאר מאד ורקות בשר לא ראיתי כהן בכל ארץ מצרים לרע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 20, 'ותאכלנה הפרות הרקות והרעות את שבע הפרות הראישנות והבריאות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 21, 'ותבאנה אל קרבהן ולא נודע כי באו אל קרבהן ומראהן רע כאשר בתחלה ואקץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 22, 'ואראה בחלמי והנה שבע שבלים עלות בקנה אחד מליאות וטובות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 23, 'והנה שבע שבלים צנימות דקות ושדיפת קדים צמחות אחריהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 24, 'ותבלענה . השבלים הדקות את שבע השבלים הטובות ואמר אל החרטמים ואין מגיד לי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 25, 'ויאמר יוסף אל פרעה חלום פרעה אחד הוא את אשר האלהים עשה אגיד לפרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 26, 'שבע הפרות הטובות שבע שנים הנה ושבע השבלים הטובות שבע שנים הנה חלום אחד הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 27, 'ושבע הפרות הרקות והרעות העלות אחריהן שבע שנים הנה ושבע השבלים הרקות ושדיפת הקדים יהיו שבע שני רעב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 28, 'הוא הדבר אשר דברתי אל פרעה את אשר האלהים עשה הראה את פרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 29, 'הנה שבע שנים באות שבע גדול בכל ארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 30, 'וקמו שבע שני רעב אחריהן ונשכח כל השבע בארץ מצרים וכלה הרעב את הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 31, 'ולא יודע השבע בארץ מפני הרעב ההוא ואחרי כן כי כבד הוא מאד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 32, 'ועלה שנית החלום אל פרעה פעמים כי נכון הדבר מעם האלהים וממהר האלהים לעשותו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 33, 'ועתה יראה לו פרעה איש נבון וחכם וישתהו על ארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 34, 'ויעש פרעה ויפקד פקדים על הארץ ויחמש את ארץ מצרים בשבע שני השבע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 35, 'ויקבץ את כל האכל שבע השנים הטובות הבאות האלה ויצברו בר תחת יד פרעה אכל בערים ישמרו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 36, 'והיה האכל לפקדון לארץ לשבע שני הרעב אשר תהיינה בארץ מצרים ולא תכרת הארץ ברעב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 37, 'וייטב הדבר בעיני פרעה ובעיני כל עבדיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 38, 'ויאמר פרעה אל עבדיו הנמצא כזה איש אשר רוח אלהים בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 39, 'ויאמר פרעה אל יוסף אחרי הודיע אלהים אתך את כל זאת אין נבון וחכם כמוך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 40, 'אתה תהיה על ביתי ועל פיך ישק כל עמי רק הכסא אגדל ממך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 41, 'ויאמר פרעה אל יוסף ראה נתתי אתך על כל ארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 42, 'ויסר פרעה את טבעתו מעל ידו ויתן אתה על יד יוסף וילבש אתו בגדי שש וישם רביד זהב על צוארו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 43, 'וירכב אתו במרכבת המשנה אשר לו ויקרא לפניו אברך ונתן אתו על כל ארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 44, 'ויאמר פרעה אל יוסף אני פרעה ובלעדיך לא ירים איש את ידו ואת רגלו בכל ארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 45, 'ויקרא פרעה שם יוסף צפנתי פענח ויתן לו את אסנת בת פוטיפרע . כהן אן לאשה ויצא יוסף על ארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 46, 'ויוסף בן שלשים שנה בעמדו לפני פרעה מלך מצרים ויצא יוסף מלפני פרעה ויעבר בכל ארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 47, 'ותעש הארץ בשבע שני השבע לקמצים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 48, 'ויקבץ את כל האכל שבע השנים אשר היה השבע בארץ מצרים ויתן אכל בערים אכל שדה העיר אשר סביבתיה נתן בתוכה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 49, 'ויצבר יוסף בר כחול הים הרבה מאד עד כי חדל לספר כי אין מספר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 50, 'וליוסף ילדו שני בנים בטרם תבוא שנת הרעב אשר ילדה לו אסנת בת פוטיפרע . כהן אן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 51, 'ויקרא יוסף . שם הבכור מנשה כי נשני אלהים את . עמלי ואת כל בית אבי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 52, 'ואת שם השני קרא אפרים כי הפרני אלהים בארץ עניי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 53, 'ותכלנה שבע שני השבע אשר היו בארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 54, 'ותחלנה שבע שני הרעב לבוא כאשר אמר יוסף ויהי הרעב בכל הארצות ובכל ארץ מצרים היה לחם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 55, 'ותרעב כל ארץ מצרים ויצעק העם אל פרעה ללחם ויאמר פרעה לכל מצרים לכו אל יוסף ואשר יאמר לכם תעשון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 56, 'והרעב היה על פני כל הארץ ויפתח יוסף את כל אשר בהם בר וישבר למצרים ויחזק הרעב בארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 41, 57, 'וכל הארצות באו מצרימה לשבר אל יוסף כי חזק הרעב בכל הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 1, 'וירא יעקב כי יש שבר במצרים ויאמר יעקב אל בניו למה תתיראו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 2, 'ויאמר הנה שמעתי כי יש שבר במצרים רדו שמה ושברו לנו משם ונחיה ולא נמות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 3, 'וירדו אחי יוסף עשרה לשבר בר ממצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 4, 'ואת בנימים אחי יוסף לא שלח יעקב את אחיו כי אמר פן יקראנו אסון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 5, 'ויבאו בני ישראל לשבר בתוך הבאים כי היה הרעב בארץ כנען ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 6, 'ויוסף הוא השליט על הארץ והוא המשביר לכל עם הארץ ויבאו אחי יוסף וישתחוו לו אפים ארצה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 7, 'וירא יוסף את אחיו ויכרם ויתנכר אליהם וידבר אתם קשות ויאמר אליהם מאין באתם ויאמרו מארץ כנען לשבר אכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 8, 'ויכר יוסף את אחיו והם לא הכרהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 9, 'ויזכר יוסף את החלמות אשר חלם להם ויאמר אליהם מרגלים אתם לראות את ערות הארץ באתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 10, 'ויאמרו אליו לא אדני ועבדיך באו לשבר אכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 11, 'כלנו בני איש אחד אנחנו כנים אנחנו לא היו עבדיך מרגלים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 12, 'ויאמר אליהם לא כי ערות הארץ באתם לראות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 13, 'ויאמרו שנים עשר עבדיך אחים אנחנו בני איש אחד בארץ כנען והנה הקטן את אבינו היום והאחד איננו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 14, 'ויאמר אליהם יוסף הוא אשר דברתי אליכם לאמר מרגלים אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 15, 'בזאת תבחנו חי פרעה אם תצאו מזה כי אם בבוא אחיכם הקטן הנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 16, 'שלחו מכם אחד ויקח את אחיכם ואתם האסרו ויבחנו דבריכם האמת אתכם ואם לא חי פרעה כי מרגלים אתם ויאמרו לא יוכל הנער לעזב את אביו ועזב את אביו ומת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 17, 'ויאסף אתם אל משמר שלשת ימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 18, 'ויאמר אליהם יוסף ביום השלישי זאת עשו וחיו את האלהים אני ירא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 19, 'אם כנים אתם אחיכם האחד יאסר בבית משמרכם ואתם לכו והביאו את שבר רעבון בתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 20, 'ואת אחיכם הקטן תביאון אלי ויאמנו דבריכם ולא תמותון ויעשו כן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 21, 'ויאמרו איש אל אחיו אבל אשמים אנחנו על אחינו אשר ראינו בצרת נפשו בהתחננו אלינו ולא שמענו על כן באה עלינו כל הצרה הזאת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 22, 'ויען ראובן אתם לאמר הלוא דברתי אליכם לאמר אל תחטאו בילד ולא שמעתם וגם דמו הנה נדרש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 23, 'והם לא ידעו כי שמע יוסף כי המליץ בינתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 24, 'ויסב מעליהם ויבך וישב אליהם וידבר אליהם ויקח מאתם את שמעון ויאסר אתו לעיניהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 25, 'ויצו יוסף וימלאו את כליהם בר ולהשיב את כספיהם איש אל שקו ולתת להם צדה לדרך ויעש להם כן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 26, 'וישאו את שברם על חמריהם וילכו משם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 27, 'ויפתח האחד את שקו לתת מספה לחמרו במלון וירא את כספו והנה הוא בפי אמתחתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 28, 'ויאמר אל אחיו הושב כספי וגם הנה הוא באמתחתי ויצא לבם ויחרדו איש על אחיו לאמר מה זאת עשה אלהים לנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 29, 'ויבאו אל יעקב אביהם ארצה כנען ויגדו לו את כל הקרות אתם לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 30, 'דבר האיש אדני הארץ אתנו קשות ויתן אתנו כמרגלים את הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 31, 'ונאמר אליו כנים אנחנו לא היינו מרגלים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 32, 'שנים עשר אחים אנחנו בני אבינו והאחד איננו והקטן היום את אבינו בארץ כנען ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 33, 'ויאמר אלינו האיש אדני הארץ בזאת אדע כי כנים אתם אחיכם האחד הניחו אתי ואת רעבון בתיכם קחו ולכו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 34, 'והביאו את אחיכם הקטן אלי ואדעה כי לא מרגלים אתם כי כנים אתם את אחיכם אתן לכם ואת הארץ תסחרו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 35, 'ויהיו הם מריקים שקיהם והנה איש צרור כספו בשקו ויראו את צררות כספיהם הם ואביהם וייראו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 36, 'ויאמר אליהם יעקב אביהם אתי שכלתם יוסף איננו ושמעון איננו ואת בנימים תקחו עלי היו כלנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 37, 'ויאמר ראובן אל אביו לאמר את שני בני תמית אם לא אביאנו אליך תנה אתו על ידי ואני אשיבנו אליך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 42, 38, 'ויאמר לא ירד בני עמכם כי אחיו מת והוא לבדו נשאר וקראהו אסון בדרך אשר תלכו בה והורדתם את שיבתי ביגון שאלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 1, 'והרעב כבד בארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 2, 'ויהי כאשר כלו לאכל את השבר אשר הביאו ממצרים ויאמר אליהם אביהם שובו שברו לנו מעט אכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 3, 'ויאמר אליו יהודה לאמר העד העד בנו האיש לאמר לא תראו פני בלתי אחיכם אתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 4, 'אם ישך משלח את אחינו אתנו נרדה ונשברה לך אכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 5, 'ואם אינך משלח לא נרד כי האיש אמר אלינו לא תראו פני בלתי אחיכם אתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 6, 'ויאמר ישראל למה הרעתם לי להגיד לאיש העוד לכם אח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 7, 'ויאמרו שאל שאל האיש לנו ולמולדתנו לאמר העוד אביכם חי היש לכם אח ונגד לו על פי הדברים האלה הידוע נדע כי יאמר הורידו את אחיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 8, 'ויאמר יהודה אל ישראל אביו שלח הנער אתי ונקומה ונלכה ונחיה ולא נמות גם אנחנו גם אתה גם טפנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 9, 'אנכי אערבנו מידי תבקשנו אם לא הבאתיו אליך והצגתיו לפניך וחטאתי לך כל הימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 10, 'כי לו לא התמהמנו כי עתה שבנו זה פעמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 11, 'ויאמר אליהם ישראל אביהם אם כן אפוא זאת עשו קחו מזמרת הארץ בכליכם והורידו לאיש מנחה מעט צרי ומעט דבש נכאת ולוט בטנים ושקדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 12, 'וכסף משנה קחו בידכם ואת הכסף המושב בפי אמתחתיכם תשיבו בידכם אולי משגה היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 13, 'ואת אחיכם קחו וקומו ושובו אל האיש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 14, 'ואל שדי יתן לכם רחמים לפני האיש ושלח לכם את אחיכם האחר ואת בנימים ואני כאשר שכלתי שכלתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 15, 'ויקחו האנשים את המנחה הזאת ומשנה כסף לקחו בידם ואת בנימים ויקמו וירדו מצרימה ויעמדו לפני יוסף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 16, 'וירא יוסף אתם ואת בנימים ויאמר לאשר על ביתו הבא את האנשים הביתה וטבח טבח והכן כי אתי יאכלו האנשים בצהרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 17, 'ויעש האיש כאשר אמר יוסף ויבא האיש את האנשים ביתה יוסף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 18, 'וייראו האנשים כי הובאו ביתה יוסף ויאמרו על דבר הכסף המושב באמתחתינו בתחלה אנחנו מובאים להתגלל עלינו ולהתנפל עלינו לקחת אתנו לעבדים ואת חמרינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 19, 'ויגשו אל האיש אשר על בית יוסף וידברו אליו פתח הבית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 20, 'ויאמרו בי אדני ירד ירדנו בתחלה לשבר אכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 21, 'ויהי כי באנו אל המלון ונפתח את אמתחתינו והנה כסף איש בפי אמתחתו כספנו במשקלו ונשיב אתו בידנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 22, 'וכסף אחר לקחנו בידנו לשבר אכל לא ידענו מי שם כספנו באמתחתינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 23, 'ויאמר שלום לכם אל תיראו אלהיכם ואלהי אבתיכם נתן לכם מטמון באמתחתיכם כספכם בא אלי ויוצא אליהם את שמעון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 24, 'ויבא האיש את האנשים ביתה יוסף ויתן מים וירחצו רגליהם ויתן מספה לחמריהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 25, 'ויכינו את המנחה עד בא יוסף בצהרים כי שמעו כי שם יאכלו לחם ויבא יוסף הביתה ויבאו לו את המנחה אשר בידם הביתה וישתחוו לו ארצה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 26, '');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 27, 'וישאל להם לשלום ויאמר השלום אביכם הזקן אשר אמרתם עודנו חי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 28, 'ויאמרו שלום לעבדך לאבינו עודנו חי ויאמר ברוך האיש ההוא לאלהים ויקדו וישתחוו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 29, 'וישא עיניו וירא את בנימים אחיו בן אמו ויאמר הזה אחיכם הקטן אשר אמרתם אלי ויאמר אלהים יחנך בני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 30, 'וימהר יוסף כי נכמרו רחמיו על אחיו ויבקש לבכות ויבא החדרה ויבך שם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 31, 'וירחץ פניו ויצא ויתאפק ויאמר שימו לחם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 32, 'וישמו לו לבדו ולהם לבדם ולמצרים האכלים אתו לבדם כי לא יוכלון המצרים לאכל את העברים לחם כי תועבה היא למצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 33, 'וישבו לפניו הבכור כבכורתו והצעיר כצעירתו ויתמהו האנשים איש על רעהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 43, 34, 'וישא משאת מאת פניו אליהם ותרב משאת בנימים ממשאת כלם חמש ידות וישתו וישכרו עמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 1, 'ויצו את אשר על ביתו לאמר מלא את אמתחת האנשים אכל כאשר יוכלון שאת ושים כסף איש בפי אמתחתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 2, 'ואת גביעי גביע הכסף תשים בפי אמתחת הקטן ואת כסף שברו ויעש כדבר יוסף אשר דבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 3, 'הבקר אור והאנשים שלחו הם וחמריהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 4, 'הם יצאו את העיר לא הרחיקו ויוסף אמר לאשר על ביתו קום רדף אחרי האנשים והשגתם ואמרת אליהם למה שלמתם רעה תחת טובה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 5, 'הלוא זה אשר ישתה אדני בו והוא נחש ינחש בו הרעתם אשר עשיתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 6, 'וישגם וידבר אליהם את הדברים האלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 7, 'ויאמרו . למה ידבר אדני כדברים האלה חלילה לעבדיך מעשות כדבר הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 8, 'הן הכסף אשר מצאנו בפי אמתחתינו השבנו אליך מארץ כנען ואיך נגנב מבית אדניך כסף או זהב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 9, 'אשר ימצא אתו מעבדיך יומת וגם אנחנו נהיה לאדני לעבדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 10, 'ויאמר גם עתה כדבריכם כן הוא אשר ימצא אתו יהיה לי עבד ואתם תהיו נקוים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 11, 'וימהרו ויורדו איש . אמתחתו ארצה ויפתחו איש אמתחתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 12, 'ויחפש בגדול החל ובקטן כלה וימצא הגביע באמתחת בנימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 13, 'ויקרעו שמלתם ויעמסו איש על חמרו וישבו העירה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 14, 'ויבא יהודה ואחיו ביתה יוסף והוא עודנו שם ויפלו לפניו ארצה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 15, 'ויאמר להם יוסף מה המעשה הזה אשר עשיתם הלוא ידעתם כי נחש ינחש איש אשר כמוני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 16, 'ויאמר יהודה מה נאמר לאדני ומה נדבר ומה נצטדק והאלהים מצא את עון עבדיך הננו עבדים לאדני גם אנחנו גם אשר נמצא הגביע בידו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 17, 'ויאמר חלילה לי מעשות זאת האיש אשר נמצא הגביע בידו הוא יהוה לי עבד ואתם עלו לשלום אל אביכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 18, 'ויגש אליו יהודה ויאמר בי אדני ידבר נא עבדך דבר באזני אדני ואל יחר אפך בעבדך כי כמוך כפרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 19, 'אדני שאל את עבדיו לאמר היש לכם אב או אח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 20, 'ונאמר אל אדני יש לנו אב זקן וילד זקנים קטן אחיו מת ויותר הוא לבדו לאמו ואביו אהבו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 21, 'ותאמר אל עבדיך הורידהו אלי ואשימה עיני עליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 22, 'ונאמר אל אדני לא יוכל הנער לעזב את אביו ועזב את אביו ומת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 23, 'ותאמר אל עבדיך אם לא ירד אחיכם הקטן אתכם לא תוסיפון לראות פני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 24, 'ויהי כי עלינו אל עבדך אבינו ונגיד לו את דברי אדני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 25, 'ויאמר אבינו שובו שברו לנו מעט אכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 26, 'ונאמר לא נוכל לרדת אם יש אחינו הקטן אתנו וירדנו כי לא נוכל לראות את פני האיש ואחינו הקטן איננו אתנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 27, 'ויאמר עבדך אבי אלינו אתם ידעתם כי שנים ילדה לי אשתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 28, 'ויצא האחד מאתי ואמר אך טרף טרף ולא ראיתיו עד הנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 29, 'ולקחתם גם את זה מעם פני וקראהו אסון והורדתם את שיבתי ברעה שאלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 30, 'ועתה כבאי אל עבדך אבי והנער איננו אתי ונפשו קשורה בנפשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 31, 'והיה כראותו כי אין הנער אתנו ומת והורידו עבדיך את שיבת עבדך אבינו ביגון שאלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 32, 'כי עבדך ערב את הנער מעם אביו לאמר אם לא אביאנו אליך וחטאתי לאבי כל הימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 33, 'ועתה ישב נא עבדך תחת הנער עבד לאדני והנער יעלה עם אחיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 44, 34, 'כי איך אעלה אל אבי והנער איננו אתי פן אראה ברעה אשר תמצא את אבי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 1, 'ולא יכל יוסף להתאפק לכל הנצבים עליו ויקרא והוציאו כל איש מעליו ולא עמד איש אתו בהתודע יוסף אל אחיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 2, 'ויתן את קולו בבכי וישמעו מצרים וישמעו בית פרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 3, 'ויאמר יוסף אל אחיו אני יוסף העוד אבי חי ולא יכלו אחיו לענות אתו כי נבהלו מפניו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 4, 'ויאמר יוסף אל אחיו גשו נא אלי ויגשו ויאמר אני יוסף אחיכם אשר מכרתם אתי מצרימה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 5, 'ועתה אל תעצבו ואל יחר בעיניכם כי מכרתם אתי הנה כי למחיה שלחני אלהים לפניכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 6, 'כי זה שנתים רעב בקרב הארץ ועוד חמש שנים אשר אין חריש וקציר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 7, 'וישלחני אלהים לפניכם לשים לכם שארות בארץ ולהחיות לכם פלטה גדלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 8, 'ועתה לא אתם שלחתם אתי הנה כי האלהים וישמני לאב לפרעה ולאדון לכל ביתו ומשל בכל ארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 9, 'מהרו ועלו אל אבי ואמרתם אליו כה אמר בנך יוסף שמני אלהים לאדון לכל מצרים רדה אלי אל תעמד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 10, 'וישבת בארץ גשן והיית קרוב אלי אתה ובניך ובני בניך צאנך ובקרך וכל אשר לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 11, 'וכלכלתי אתך שם כי עוד חמש שנים רעב פן תורש אתה וביתך וכל אשר לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 12, 'והנה עיניכם ראות ועיני אחי בנימים כי פיי המדבר אליכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 13, 'והגדתם לאבי את כל כבודי במצרים ואת כל אשר ראיתם ומהרתם והורדתם את אבי הנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 14, 'ויפל על צואר . בנימים אחיו ויבך . ובנימים בכה על צוארו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 15, 'וינשק לכל אחיו ויבך עליהם ואחרי כן דברו אחיו אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 16, 'והקול נשמע בית פרעה לאמר באו אחי יוסף וייטב בעיני פרעה ובעיני עבדיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 17, 'ויאמר פרעה אל יוסף אמר אל אחיך זאת עשו טענו את בעירכם ולכו ובאו ארצה כנען ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 18, 'וקחו את אביכם ואת בתיכם ובאו אלי ואתן לכם את טוב ארץ מצרים ואכלו את חלב הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 19, 'ואתה צויתי זאת עשו קחו לכם מארץ מצרים עגלות לטפכם ולנשיכם ונשאתם את אביכם ובאתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 20, 'ועיניכם אל תחוס על כליכם כי טוב כל ארץ מצרים לכם הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 21, 'ויעשו כן בני ישראל ויתן להם יוסף עגלות על פי פרעה ויתן להם צדה לדרך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 22, 'לכלם נתן לאיש חליפת שמלות ולבנימים נתן שלש מאות כסף וחמש חליפת שמלות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 23, 'ולאביו שלח כזאת עשרה חמרים נשאים מטוב ארץ מצרים ועשר אתנות נשאות בר ולחם ומזון לאביו לדרך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 24, 'וישלח את אחיו וילכו ויאמר אלהים אל תתרגזו בדרך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 25, 'ויעלו ממצרים ויבאו ארצה כנען אל יעקב אביהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 26, 'ויגדו לו לאמר עוד יוסף חי וכי הוא משל בכל ארץ מצרים ויפג לבו כי לא האמן להם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 27, 'וידברו אליו את כל דברי יוסף אשר דבר אליהם וירא את העגלות אשר שלח יוסף לשאת אתו ותחי רוח יעקב אביהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 45, 28, 'ויאמר ישראל רב עוד יוסף בני חי אלכה ואראנו בטרם אמות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 1, 'ויסע ישראל וכל אשר לו ויבא בארה שבע ויזבח זבחים לאלהי אביו יצחק ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 2, 'ויאמר אלהים לישראל במראת הלילה ויאמר יעקב יעקב ויאמר הנני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 3, 'ויאמר אנכי האל אלהי אביך אל תירא מרדת מצרימה כי לגוי גדול אשימך שם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 4, 'אנכי ארד עמך מצרימה ואנכי אעלך גם עלה ויוסף ישית ידו על עיניך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 5, 'ויקם יעקב מבאר שבע וישאו בני ישראל את יעקב אביהם ואת טפם ואת נשיהם בעגלות אשר שלח פרעה לשאת אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 6, 'ויקחו את מקניהם ואת רכושם אשר רכשו בארץ כנען ויבאו מצרימה יעקב וכל זרעו אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 7, 'בניו ובני בניו אתו בנתיו ובנות בניו וכל זרעו הביא אתו מצרימה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 8, 'ואלה שמות בני ישראל הבאים מצרימה יעקב ובניו בכור יעקב ראובן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 9, 'ובני ראובן חנוך ופלוא חצרון וכרמי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 10, 'ובני שמעון ימואל וימין ואהד ויכין וצהר ושאול בן הכנענית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 11, 'ובני לוי גרשון קהת ומררי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 12, 'ובני יהודה ער ואונן ושלה ופרץ וזרח וימת ער ואונן בארץ כנען ויהיו בני פרץ חצרון וחמואל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 13, 'ובני יששכר תולע ופואה ישוב ושמרון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 14, 'ובני זבולן סרד ואלון ויחלאל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 15, 'אלה בני לאה אשר ילדה ליעקב בפדן ארם ואת דינה בתו כל נפש בניו ובנתיו שלשים ושלש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 16, 'ובני גד צפון וחגי ושוני ואצבעון ערי וארודי וארולי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 17, 'ובני אשר ימנה וישוה וישוי ובריעה ושרח אחתם ובני בריעה חבר ומלכיאל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 18, 'אלה בני זלפה אשר נתן לבן ללאה בתו ותלד את אלה ליעקב שש עשרה נפש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 19, 'בני רחל אשת יעקב יוסף ובנימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 20, 'ויולד ליוסף בארץ מצרים אשר ילדה לו אסנת בת פוטיפרע . כהן אן את מנשה ואת אפרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 21, 'ובני בנימים בלע ובכר ואשבל גרה ונעמן אחים וראש מפים וחפים וארד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 22, 'אלה בני רחל אשר ילדה ליעקב כל נפש ארבע עשרה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 23, 'ובני דן חשים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 24, 'ובני נפתלי יחצאל וגוני יצר ושלום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 25, 'אלה בני בלהה אשר נתן לבן לרחל בתו ותלד את אלה ליעקב כל נפש שבע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 26, 'כל הנפש הבאה ליעקב מצרימה יצאי ירכו מלבד נשי בני יעקב כל נפש ששים ושש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 27, 'ובני יוסף אשר ילדו לו במצרים נפש שנים כל הנפש לבית יעקב הבאה מצרימה שבעים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 28, 'ואת יהודה שלח לפניו אל יוסף להראות לפניו גשנה ויבא ארץ גשן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 29, 'ויאסר יוסף מרכבתו ויעל לקראת ישראל אביו גשנה וירא אליו ויפל על צוארו ויבך על צוארו עוד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 30, 'ויאמר ישראל אל יוסף אמותה הפעם אחרי ראיתי . פניך כי עודך חי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 31, 'ויאמר יוסף אל אחיו ואל בית אביו אעלה ואגידה לפרעה ואמר אליו אחי ובית אבי אשר בארץ כנען באו אלי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 32, 'והאנשים רעי צאן כי אנשי מקנה היו וצאנם ובקרם וכל אשר להם הביאו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 33, 'והיה כי יקרא לכם פרעה ואמר מה מעשיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 46, 34, 'ואמרתם אנשי מקנה היו עבדיך מנערינו ועד עתה גם אנחנו גם אבתינו בעבור תשבו בארץ גשן כי תועבת מצרים כל רעי צאן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 1, 'ויבא יוסף ויגד לפרעה ויאמר אליו אבי ואחי וצאנם ובקרם וכל אשר להם באו מארץ כנען והנם בארץ גשן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 2, 'ומקצה אחיו לקח עמו חמשה אנשים ויצגם לפני פרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 3, 'ויאמר פרעה אל אחי יוסף מה מעשיכם ויאמרו אל פרעה רעי צאן עבדיך גם אנחנו גם אבתינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 4, 'ויאמרו אל פרעה לגור בארץ באנו כי אין מרעה לצאן אשר לעבדיך כי כבד הרעב בארץ כנען ועתה ישבו נא עבדיך בארץ גשן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 5, 'ויאמר פרעה אל יוסף לאמר אביך ואחיך באו אליך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 6, 'ארץ מצרים לפניך היא במיטב הארץ הושב את אביך ואת אחיך ישבו בארץ גשן ואם ידעת היש בם אנשי חיל ושמתם שרי מקנה על אשר לי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 7, 'ויבא יוסף את יעקב אביו ויעמדהו לפני פרעה ויברך יעקב את פרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 8, 'ויאמר פרעה אל יעקב כמה ימי שני חייך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 9, 'ויאמר יעקב אל פרעה ימי שני מגרי שלשים ומאת שנה מעט ורעים היו ימי שני חיי ולא השיגו את ימי שני חיי אבתי בימי מגריהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 10, 'ויברך יעקב את פרעה ויצא מלפני פרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 11, 'ויושב יוסף את אביו ואת אחיו ויתן להם אחזה בארץ מצרים במיטב הארץ בארץ רעמסס כאשר צוה פרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 12, 'ויכלכל יוסף את אביו ואת אחיו ואת כל בית אביו לחם לפי הטף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 13, 'ולחם אין בכל הארץ כי כבד הרעב מאד ותלא ארץ מצרים וארץ כנען מפני הרעב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 14, 'וילקט יוסף את כל הכסף הנמצא בארץ מצרים ובארץ כנען בשבר אשר הם שברים ויבא יוסף את הכסף ביתה פרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 15, 'ויתם הכסף מארץ מצרים ומארץ כנען ויבאו כל מצרים אל יוסף לאמר הבה לנו לחם ולמה נמות נגדך כי אפס הכסף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 16, 'ויאמר יוסף הבו מקניכם ואתן לכם לחם במקניכם אם אפס הכסף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 17, 'ויבאו את מקניהם אל יוסף ויתן להם יוסף לחם בסוסים ובמקנה הצאן ובמקנה הבקר ובחמרים וינהלם בלחם בכל מקניהם בשנה ההיא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 18, 'ותתם השנה ההיא ויבאו אליו בשנה השנית ויאמרו לו לא נכחד מאדני כי אם תם הכסף ומקנה הבהמה אל אדני לא נשאר לפני אדני בלתי אם גויתנו ואדמתנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 19, 'למה נמות לעיניך גם אנחנו גם אדמתנו קנה אתנו ואת אדמתנו בלחם ונהיה אנחנו ואדמתנו עבדים לפרעה ותן זרע ונחיה ולא נמות והאדמה לא תשם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 20, 'ויקן יוסף את כל אדמת מצרים לפרעה כי מכרו מצרים איש שדהו כי חזק עליהם הרעב ותהי הארץ לפרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 21, 'ואת העם העביר אתו לעבדים מקצה גבול מצרים עד קצהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 22, 'רק אדמת הכהנים לא קנה כי חק לכהנים מאת פרעה ואכלו את חקם אשר נתן להם פרעה על כן לא מכרו את אדמתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 23, 'ויאמר יוסף אל העם הן קניתי אתכם היום ואת אדמתכם לפרעה הא לכם זרע וזרעתם את האדמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 24, 'והיה בתבואתה ונתתם חמישת לפרעה וארבע הידות יהיה לכם לזרע השדה ולאכלכם ולאשר בבתיכם ולאכל לטפכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 25, 'ויאמרו החיתנו נמצא חן בעיני אדני והיינו עבדים לפרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 26, 'וישם אתה יוסף לחק עד היום הזה על אדמת מצרים לפרעה לחמש רק אדמת הכהנים לבדה לא היתה לפרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 27, 'וישב ישראל בארץ מצרים בארץ גשן ויאחזו בה ויפרו וירבו מאד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 28, 'ויחי יעקב בארץ מצרים שבע עשרה שנה ויהיו ימי יעקב שני חייו שבע שנים וארבעים ומאת שנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 29, 'ויקרבו ימי ישראל למות ויקרא לבנו ליוסף ויאמר לו אם נא מצאתי חן בעיניך שים נא ידך תחת ירכי ועשית עמדי חסד ואמת אל נא תקברני במצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 30, 'ושכבתי עם אבתי ונשאתני ממצרים וקברתני בקברתם ויאמר אנכי אעשה כדבריך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 47, 31, 'ויאמר השבע לי וישבע לו וישתחוי ישראל על ראש המטה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 1, 'ויהי אחר הדברים האלה ויאמר ליוסף הנה אביך חלה ויקח את שני בניו עמו את מנשה ואת אפרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 2, 'ויגד ליעקב ויאמר הנה בנך יוסף בא אליך ויתחזק ישראל וישב על המטה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 3, 'ויאמר יעקב אל יוסף אל שדי נראה אלי בלוזה בארץ כנען ויברך אתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 4, 'ויאמר אלי הנני מפריך והרביתך ונתתיך לקהל עמים ונתתי את הארץ הזאת לזרעך אחריך אחזת עולם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 5, 'ועתה שני בניך הנולדים לך בארץ מצרים עד באי אליך מצרימה לי הם אפרים ומנשה כראובן וכשמעון יהיו לי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 6, 'ומולדתך אשר הולדת אחריהם לך יהיו על שם אחיהם יקראו בנחלתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 7, 'ואני בבאי מפדן ארם מתה עלי רחל אמך בארץ כנען בדרך בעוד כברת הארץ לבוא אפרתה ואקברה שם בדרך אפרתה היא בית לחם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 8, 'וירא ישראל את בני יוסף ויאמר מי אלה לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 9, 'ויאמר יוסף אל אביו בני הם אשר נתן לי אלהים בזה ויאמר קחם נא אלי ואברכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 10, 'ועיני ישראל כבדה מזקנה לא יכל לראות ויגש אתם אליו וישק להם ויחבק להם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 11, 'ויאמר ישראל אל יוסף ראה פניך לא פללתי והנה הראה אתי אלהים גם את זרעך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 12, 'ויוצא יוסף אתם מעם ברכיו וישתחוו לאפיו ארצה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 13, 'ויקח יוסף את שניהם את אפרים בימינו משמאל ישראל ואת מנשה בשמאלו מימין ישראל ויגש אליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 14, 'וישלח ישראל את יד ימינו וישתה על ראש אפרים והוא הצעיר ואת שמאלו על ראש מנשה שכל את ידיו כי מנשה הבכור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 15, 'ויברך את יוסף ויאמר האלהים אשר התהלכו אבתי לפניו אברהם ויצחק האלהים הרעה אתי מעדני עד היום הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 16, 'המלך הגאל אתי מכל רע יברך את הנערים האלה ויקרא בהם שמי ושם אבתי אברהם ויצחק וידגו לרב בקרב הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 17, 'וירא יוסף כי ישית אביו את יד ימינו על ראש אפרים וירע בעיניו ויתמך יד אביו להסיר אתה מעל ראש אפרים על ראש מנשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 18, 'ויאמר יוסף אל אביו לא כן אבי כי זה הבכור שים ימינך על ראשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 19, 'וימאן אביו ויאמר ידעתי בני ידעתי גם הוא יהיה לעם וגם הוא יגדל ואולם אחיו הקטן יגדל ממנו וזרעו יהיה מלא הגוים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 20, 'ויברכם ביום ההוא לאמר בך יברך ישראל לאמר ישימך אלהים כאפרים וכמנשה וישם את אפרים לפני מנשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 21, 'ויאמר ישראל אל יוסף הנה אנכי מת והיה אלהים עמכם והשיב אתכם אל ארץ אבתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 48, 22, 'ואני נתתי לך שכם אחד על אחיך אשר לקחתי מיד האמרי בחרבי ובקשתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 1, 'ויקרא יעקב אל בניו ויאמר האספו ואגידה לכם את אשר יקרא אתכם באחרית הימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 2, 'הקבצו ושמעו בני יעקב ושמעו אל ישראל אביכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 3, 'ראובן בכורי אתה כחי וראשית אוני יתר שאת ויתר עז ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 4, 'פחזת כמים אל תותר כי עלית משכבי אביך אז חללת יצועי עלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 5, 'שמעון ולוי אחים כלו חמס מכרתיהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 6, 'בסודם אל תבוא נפשי ובקהלם אל יחר כבודי כי באפם הרגו איש וברצנם עקרו שור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 7, 'אדיר אפם כי עז ועברתם כי קשתה אחלקם ביעקב ואפיצם בישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 8, 'יהודה אתה יודוך אחיך ידיך בערף איביך ישתחוו לך בני אביך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 9, 'גר אריה יהודה מטרף בני עלית כרע רבץ כאריה וכלביה מי יקימנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 10, 'לא יסור שבט מיהודה ומחקק מבין דגליו עד כי יבוא שילה ולו יקהתו עמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 11, 'אסורי לגפן עירו ולשרקה בני אתנו כבס ביין לבושו ובדם ענבים כסותה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 12, 'חכלילו עינים מיין ולבן שנים מחלב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 13, 'זבולן לחוף ימים ישכן והוא לחוף אניות וירכתו על צידן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 14, 'יששכר חמר גרים רבץ בין המשפתים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 15, 'וירא מנוחה כי טובה ואת הארץ כי נעמה ויט שכמו לסבל ויהי למס עבד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 16, 'דן ידין עמו כאחד שבטי ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 17, 'יהיה דן נחש עלי דרך שפפון עלי ארח הנשך עקבי סוס ויפל רכבו אחור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 18, 'לישועתך קויתי יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 19, 'גד גדוד יגדנו והוא יגד עקב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 20, 'מאשר שמן לחמו והוא יתן מעדני מלך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 21, 'נפתלי אילה שלחה הנתן אמרי שפר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 22, 'בן פרת יוסף בן פרת עלי עין בני צעירי עלי שור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 23, 'וימררהו ויריבהו וישטמהו בעלי חצים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 24, 'ותשב באיתן קשתו ויפזו זרועי ידיו מידי אביר יעקב משם רעה אבן ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 25, 'מאל אביך ויעזרך ואל שדי ויברכך ברכת שמים ממעל ברכת תהום רבצת תחת ברכת שדים ורחם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 26, 'ברכת אביך ואמך גברו על ברכת הרי עד תאות גבעת עולם תהיינה לראש יוסף ולקדקד נזיר אחיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 27, 'בנימים זאב יטרף בבקר יאכל עדי ולערב יחלק שלל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 28, 'כל אלה שבטי ישראל שנים עשר וזאת אשר דבר להם אביהם ויברך אתם איש אשר כברכתו ברך אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 29, 'ויצו אתם ויאמר אליהם אני נאסף אל עמי קברו אתי אל אבתי אל המערה אשר בשדה עפרון החתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 30, 'במערה אשר בשדה המכפלה אשר על פני ממרא בארץ כנען אשר קנה אברהם את השדה מאת עפרון החתי לאחזת קבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 31, 'שמ קברו את אברהם ואת שרה אשתו ושם קברו את יצחק ואת רבקה אשתו ושם קברתי את לאה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 32, 'מקנה השדה והמערה אשר בו מאת בני חת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 49, 33, 'ויכל יעקב לצות את בניו ויאסף רגליו אל המטה ויגוע ויאסף אל עמיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 1, 'ויפל יוסף על פני אביו ויבך עליו וישק לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 2, 'ויצו יוסף את עבדיו את הרפאים לחנט את אביו ויחנטו הרפאים את ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 3, 'וימלאו לו ארבעים יום כי כן ימלאו ימי החנטים ויבכו אתו מצרים שבעים יום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 4, 'ויעברו ימי בכיתו וידבר יוסף אל בית פרעה לאמר אם נא מצאתי חן בעיניכם דברו נא באזני פרעה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 5, 'אבי השבעני לפני מותו לאמר הנה אנכי מת בקברי אשר כרתי לי בארץ כנען שם תקברני ועתה אעלה נא ואקברה את אבי כאשר השבעני ואשובה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 6, 'ויאמר פרעה עלה וקבר את אביך כאשר השבעך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 7, 'ויעל יוסף לקבר את אביו ויעלו אתו כל עבדי פרעה זקני ביתו וכל זקני ארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 8, 'וכל בית יוסף אחיו ובית אביו רק טפם וצאנם ובקרם עזבו בארץ גשן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 9, 'ויעל עמו גם רכב גם פרשים ויהי המחנה כבד מאד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 10, 'ויבאו עד גרן האטד אשר בעבר הירדן ויספדו שם מספד גדול וכבד מאד ויעש לאביו אבל שבעת ימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 11, 'וירא יושב הארץ הכנעני את האבל בגרן האטד ויאמרו אבל כבד זה למצרים על כן קרא שמו אבל מצרים אשר בעבר הירדן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 12, 'ויעשו לו בניו כן כאשר צום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 13, 'וישאו אתו בניו ארצה כנען ויקברו אתו במערת שדה המכפלה אשר קנה אברהם את השדה לאחזת קבר מאת עפרון החתי על פני ממרא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 14, 'וישב יוסף מצרימה הוא ואחיו וכל העלים אתו לקבר את אביו אחרי קברו את אביו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 15, 'ויראו אחי יוסף כי מת אביהם ויאמרו לא ישטמנו יוסף השב ישיב לנו את כל הרעה אשר גמלנו אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 16, 'ויצוו על יוסף לאמר אביך צוה לפני מותו לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 17, 'כה תאמרון ליוסף אנה שא נא פשע אחיך וחטאתם כי רעה גמלוך ועתה שא נא לפשע עבדי אלהי אביך ויבך יוסף בדברם אליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 18, 'וילכו גם אחיו ויפלו לפניו ויאמרו הננו לך לעבדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 19, 'ויאמר אליהם יוסף אל תיראו . התחת אלהים אני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 20, 'ואתם חשבתם עלי רעה והאלהים חשבה לטובה למען עשות כיום הזה להחיות עם רב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 21, 'ועתה אל תיראו אנכי אכלכל אתכם ואת טפכם וינחם אתם וידבר אל לבם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 22, 'וישב יוסף במצרים הוא ובית אביו ויחי יוסף מאה ועשר שנים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 23, 'וירא יוסף לאפרים בנים שלשים גם בני מכיר בן מנשה ילדו . בימי יוסף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 24, 'ויאמר יוסף אל אחיו אנכי מת ואלהים פקד יפקד אתכם והעלה אתכם מן הארץ הזאת אל הארץ אשר נשבע לאברהם ליצחק וליעקב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 25, 'וישבע יוסף את בני ישראל לאמר פקד יפקד אלהים אתכם והעליתם את עצמתי מזה אתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (1, 50, 26, 'וימת יוסף בן מאה ועשר שנים ויחנטו אתו ויושם בארן במצרים  ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 1, 1, 'ואלה שמות בני ישראל הבאים מצרימה את יעקב . איש וביתו באו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 1, 2, 'ראובן ושמעון ולוי ויהודה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 1, 3, 'ויששכר וזבולן ובנימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 1, 4, 'דן ונפתלי גד ואשר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 1, 5, 'ויהיו כל נפש יצאי ירך יעקב שבעים . נפש ויוסף היה במצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 1, 6, 'וימת יוסף וכל אחיו וכל הדור ההוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 1, 7, 'ובני ישראל פרו וישרצו וירבו ויעצמו במאד מאד ותמלא הארץ אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 1, 8, 'ויקם מלך חדש על מצרים אשר לא ידע את יוסף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 1, 9, 'ויאמר אל עמו הן עם בני ישראל רב ועצום ממנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 1, 10, 'הבה נתחכם לו פן ירבה והיה כי תקראנו מלחמה ונוסף גם הוא על שנאינו ונלחם בנו ועלה מן הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 1, 11, 'וישמו עליו שרי מסים למען ענותם בסבלתם ויבנו ערי מסכנת לפרעה את פיתון ואת רעמסס ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 1, 12, 'וכאשר יענו אתו כן יפרה וכן יפרץ . . ויקצו . מפני בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 1, 13, 'ויעבדו מצרים את בני ישראל בפרך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 1, 14, 'וימררו את חייהם בעבדה קשה בחמר ובלבנים ובכל עבדה בשדה את כל עבדתם אשר עבדו בהם בפרך . . . . . . . . . . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 1, 15, 'ויאמר מלך מצרים למילדות העבריות אשר שם האחת שפרה ושם השנית פועה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 1, 16, 'ויאמר בילדכן את העבריות וראיתן על האבנים אם בן הוא והמתן אתו ואם בת היא וחיתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 1, 17, 'ותיראנה המילדות את האלהים ולא עשו כאשר דבר אליהן מלך מצרים ותחיינה את הילדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 1, 18, 'ויקרא פרעה . למילדות . ויאמר להן מדוע עשיתן את הדבר הזה ותחיינה את הילדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 1, 19, 'ותאמרנה המילדת אל פרעה כי לא כנשים המצריות העבריות כי חיות הנה בטרם תבוא אליהן המילדת וילדו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 1, 20, 'וייטב אלהים למילדות וירבו העם ויעצמו מאד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 1, 21, 'ויהי כי יראו המילדות את האלהים ויעש להם בתים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 1, 22, 'ויצו פרעה לכל עמו לאמר כל הבן הילד לעברים היאר תשליכון וכל הבת תחיון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 1, 'וילך איש מבית לוי ויקח את בת לוי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 2, 'ותהר האשה ותלד בן ותרא אתו כי טוב הוא ותצפנהו שלשה ירחים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 3, 'ולא יכלה עוד הצפנהו ותקח לו אמו תבת גמא ותחמרה בחמר ובזפת ותשם בה את הילד . . . ותשם . בסף על שפת היאר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 4, 'ותתיצב אחתו מרחק לדעה מה יעשה לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 5, 'ותרד בת פרעה לרחץ על היאר ונערתיה הלכת על יד היאר ותרא את התבה בתוך הסף ותשלח את אמתה ותקחה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 6, 'ותפתחה ותרא את הילד והנה נער בכה ותחמל עליו בת פרעה ותאמר מילדי העברים זה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 7, 'ותאמר אחתו אל בת פרעה האלך וקראתי לך אשה מינקת מן העבריות ותינק לך את הילד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 8, 'ותאמר לה בת פרעה לכי ותלך העלמה ותקרא את אם הילד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 9, 'ותאמר לה בת פרעה הלכי את הילד הזה והינקהו לי ואני אתן את שכרך ותקח האשה את הילד ותניקהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 10, 'ויגדל הנער ותבאהו אל בת פרעה ויהי לה לבן ותקרא את שמו משה ותאמר כי מן המים משיתיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 11, 'ויהי בימים . ההם ויגדל משה ויצא אל אחיו וירא בסבלתם וירא איש מצרי מכה איש עברי מאחיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 12, 'ויפן כה וכה וירא כי אין איש ויך את המצרי ויטמנהו בחול ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 13, 'ויצא ביום השני . והנה שני אנשים עברים נצים ויאמר לרשע למה תכה . רעך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 14, 'ויאמר . מי שמך לאיש שר ולשפט עלינו הלהרגני אתה אמר כאשר הרגת את המצרי ויירא משה . ויאמר אכן נודע הדבר . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 15, 'וישמע פרעה את הדבר הזה ויבקש להרג את משה ויברח משה מפני פרעה וישב בארץ מדין וישב על הבאר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 16, 'ולכהן מדין שבע בנות . . ותבאנה ותדלנה ותמלאנה את הרהטים להשקות את צאן אביהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 17, 'ויבאו הרעים ויגרשום ויקם משה ויושען וישק את צאנם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 18, 'ותבאנה אל רעואל אביהן ויאמר מדוע מהרתן בא היום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 19, 'ותאמרנה איש מצרי הצילנו מיד הרעים וגם דלה דלא לנו וישק את הצאן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 20, 'ויאמר אל בנתיו ואיה למה זה עזבתן את האיש קראן לו ויאכל לחם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 21, 'ויאל משה לשבת את האיש ויתן את צפורה בתו למשה לאשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 22, 'ותלד בן ויקרא את שמו גרשם כי אמר גר הייתי בארץ נכריה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 23, 'ויהי בימים הרבים ההם וימת מלך מצרים ויאנחו בני ישראל מן העבדה ויצעקו ותעל שועתם אל האלהים מן העבדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 24, 'וישמע אלהים את נקאתם ויזכר אלהים את בריתו את אברהם ואת יצחק ואת יעקב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 2, 25, 'וירא אלהים את בני ישראל וידע אלהים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 3, 1, 'ומשה היה רעה את צאן יתרו חתנו כהן מדין וינהג את הצאן אחר המדבר ויבא אל הר האלהים חורבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 3, 2, 'וירא אליו מלאך יהוה בלהבת אש מתוך הסנה וירא והנה הסנה בער באש והסנה איננו אכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 3, 3, 'ויאמר משה אסור נא ואראה את המראה הגדול הזה מדוע לא יבער הסנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 3, 4, 'וירא אלהים כי סר לראות ויקרא אליו אלהים מתוך הסנה ויאמר משה משה ויאמר הנני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 3, 5, 'ויאמר אל תקרב הלם של נעליך מעל רגליך כי המקום אשר אתה עמד עליו אדמת קדש היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 3, 6, 'ויאמר אנכי אלהי אבתיך אלהי אברהם ואלהי יצחק ואלהי יעקב ויסתר משה פניו כי ירא מהביט אל האלהים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 3, 7, 'ויאמר יהוה ראו ראיתי את עני עמי אשר במצרים ואת צעקתם שמעתי מפני נגשיו כי ידעתי את מכאבו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 3, 8, 'וארדה להצילו מיד מצרים ולהעלותו מן הארץ ההיא אל ארץ טובה ורחבה אל ארץ זבת חלב ודבש אל מקום הכנעני החתי והאמרי והפרזי והגרגשי והחוי והיבוסי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 3, 9, 'ועתה הנה צעקת בני ישראל באה אלי וגם ראיתי את הלחץ אשר מצרים לחצים אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 3, 10, 'ועתה לך ואשלחך אל פרעה והוצאת את עמי בני ישראל ממצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 3, 11, 'ויאמר משה אל האלהים מי אנכי כי אלך אל פרעה וכי אוציא את בני ישראל ממצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 3, 12, 'ויאמר כי אהיה עמך וזה לך האות כי אנכי שלחתיך בהוציאך את העם ממצרים תעבדון את האלהים על ההר הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 3, 13, 'ויאמר משה אל האלהים הנה אנכי בא אל בני ישראל ואמרתי להם אלהי אבתיכם שלחני אליכם ואמרו לי מה שמו מה אמר אליהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 3, 14, 'ויאמר אלהים אל משה אהיה אשר אהיה ויאמר כה תאמר אל בני ישראל אהיה שלחני אליכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 3, 15, 'ויאמר עוד אלהים אל משה כה תאמר אל בני ישראל יהוה אלהי אבתיכם אלהי אברהם ואלהי יצחק ואלהי יעקב שלחני אליכם זה שמי לעולם וזה זכרי לדר ודר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 3, 16, 'לך ואספת את זקני בני ישראל ואמרת אליהם יהוה אלהי אבתיכם נראה אלי אלהי אברהם . ויצחק . ויעקב לאמר פקד פקדתי אתכם ואת העשוי לכם במצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 3, 17, 'ואמרה אעלה אתכם מעני מצרים אל ארץ הכנעני החתי והאמרי והפרזי והגרגשי והחוי והיבוסי . אל ארץ זבת חלב ודבש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 3, 18, 'ושמעו לקולך ובאת אתה וזקני . ישראל אל מלך מצרים ואמרתם אליו יהוה אלהי העברים נקרא עלינו . נלכה נא דרך שלשת ימים במדבר ונזבחה ליהוה אלהינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 3, 19, 'ואני ידעתי כי לא יתן אתכם מלך מצרים להלך הלוא . ביד חזקה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 3, 20, 'ושלחתי את ידי והכיתי את מצרים בכל נפלאתי אשר אעשה בקרבו ואחרי כן ישלח אתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 3, 21, 'ונתתי את חן העם הזה בעיני מצרים והיה כי תלכון לא תלכון ריקם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 3, 22, 'ושאל איש מאת רעהו ואשה מאת רעותה משכנתה ומגרת ביתה כלי כסף וכלי זהב ושמלות ושמתם על בניכם ועל בנתיכם ונצלתם את מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 1, 'ויען משה ויאמר והן לא יאמנו לי ולא ישמעו בקולי כי יאמרו לא נראה אליך יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 2, 'ויאמר אליו יהוה מה זה בידך ויאמר מטה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 3, 'ויאמר השליכהו . ארצה וישלכהו ארצה ויהי לנחש וינס משה מפניו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 4, 'ויאמר יהוה אל משה שלח ידך ואחז בזנבו וישלח ידו ויחזק בו ויהי למטה בכפו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 5, 'למען יאמנו כי נראה אליך יהוה אלהי אבתם אלהי אברהם ואלהי יצחק ואלהי יעקב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 6, 'ויאמר יהוה לו עוד הבא נא ידך בחיקך ויבא ידו בחיקו ויוצאה מחיקו והנה ידו מצרעת כשלג ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 7, 'ויאמר השב ידך אל חיקך וישב ידו אל חיקו ויוציאה מחיקו והנה שבה כבשרו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 8, 'והיה אם לא יאמנו לך ולא ישמעו לקול האות הראישון והאמנו לקול האות האחרון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 9, 'והיה אם לא יאמנו גם לשני האתות האלה ולא ישמעו לקולך ולקחת ממימי היאר ושפכת היבשה והיו המים אשר תקח מן היאר יהיו לדם ביבשת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 10, 'ויאמר משה אל יהוה בי אדני לא איש דברים אנכי גם מתמול גם משלשום גם מאז דברך אל עבדך כי כבד פה וכבד לשון אנכי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 11, 'ויאמר יהוה אליו מי שם פה לאדם או מי ישים אלם או חרש או פקח או עור הלא אנכי יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 12, 'ועתה לך ואנכי אהיה עם פיך והוריתך אשר תדבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 13, 'ויאמר בי אדני שלח נא ביד תשלח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 14, 'ויחר אף יהוה במשה ויאמר הלוא אהרן אחיך הלוי ידעתי כי דבר ידבר הוא וגם הנה הוא יצא לקראתך וראך ושמח בלבבו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 15, 'ודברת אליו ושמת את הדברים בפיו ואנכי אהיה עם פיך ועם פיו והוריתי אתכם את אשר תעשון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 16, 'ודבר הוא לך אל העם והיה הוא יהיה לך לפה ואתה תהיה לו לאלהים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 17, 'ואת המטה הזה תקח בידך אשר תעשה בו את האתות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 18, 'וילך משה וישב אל יתרו חתנו ויאמר לו אלכה נא ואשובה אל אחי אשר במצרים ואראה העודם חיים ויאמר יתרו למשה לך לשלום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 19, 'ויאמר יהוה אל משה במדין לך שוב מצריםה כי מתו כל האנשים המבקשים את נפשך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 20, 'ויקח משה את אשתו ואת בניו וירכבם על החמור וישב ארץ מצרים ויקח משה את מטה האלהים בידו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 21, 'ויאמר יהוה אל משה בלכתך לשוב מצרימה ראה את כל המפתים אשר שמתי בידך ועשיתם לפני פרעה ואני אחזק את לבו ולא ישלח את העם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 22, 'ואמרת אל פרעה כה אמר יהוה בני בכרי ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 23, 'ואמר אליך שלח את בני ויעבדני ותמאן לשלחו הנה אנכי הרג את בנך בכרך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 24, 'ויהי בדרך במלון ויפגשהו יהוה ויבקש המיתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 25, 'ותקח צפורה צר ותכרת את ערלת בנה ותגע לרגליו ותאמר כי חתן דמים אתה לי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 26, 'וירף ממנה אז אמרה חתן דמים למלות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 27, 'ויאמר יהוה אל אהרן לך לקראת משה המדברה וילך ויפגשהו בהר האלהים וישק לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 28, 'ויגד משה לאהרן את כל דברי יהוה אשר שלחו ואת כל האתות אשר צוהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 29, 'וילכו משה ואהרן ויאספו את כל זקני בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 30, 'וידבר אהרן את כל הדברים אשר דבר יהוה אל משה ויעש האתות לעיני העם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 4, 31, 'ויאמנו העם וישמעו כי פקד יהוה את בני ישראל וכי ראה את ענים ויקדו וישתחוו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 1, 'ואחר באו משה ואהרן ויאמרו אל פרעה כה אמר יהוה אלהי ישראל שלח את עמי ויחגו לי במדבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 2, 'ויאמר פרעה מי יהוה אשר אשמע בקולו לשלח את ישראל לא ידעתי את יהוה וגם את ישראל לא אשלח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 3, 'ויאמרו אלהי העברים נקרא עלינו נלכה נא המדברה דרך שלשת ימים ונזבחה ליהוה אלהינו פן יפגענו בדבר או בחרב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 4, 'ויאמר אליהם מלך מצרים למה משה ואהרן תפרידו את העם ממעשיו לכו לסבלתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 5, 'ויאמר פרעה הן רבים עתה מעם הארץ והשבתם אתם מסבלתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 6, 'ויצו פרעה ביום ההוא את הנגשים בעם ואת שטריו לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 7, 'לא תוסיפון לתת תבן לעם ללבן לבנים כתמול שלשום הם ילכו ויקששו להם תבן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 8, 'ואת מתכנת הלבנים אשר הם עשים תמול שלשום תשימו עליהם לא תגרעו ממנו . כי נרפים הם על כן הם צעקים לאמר נלכה נזבחה לאלהינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 9, 'תכבד העבדה על האנשים וישעו בה ולא ישעו בדברי שקר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 10, 'ויצאו נגשי העם ושטריו וידברו אל העם לאמר כה אמר פרעה אינני נתן לכם תבן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 11, 'אתם לכו קחו לכם תבן מאשר תמצאו כי אין נגרע מעבדתכם דבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 12, 'ויפץ העם בכל ארץ מצרים לקשש קש לתבן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 13, 'והנגשים אצוים . בעם . לאמר כלו מעשיכם דבר יום ביומו כאשר בהיות התבן נתן לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 14, 'ויכו שטרי בני ישראל אשר שמו עליהם נגשי פרעה לאמר מדוע לא כליתם חקכם ללבן כתמול שלשום גם תמול גם היום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 15, 'ויבאו שטרי בני ישראל ויצעקו אל פרעה לאמר למה תעשה כה לעבדיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 16, 'תבן אין נתן לעבדיך ולבנים אמרים לנו עשו והנה עבדיך מכים וחטאת עמך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 17, 'ויאמר נרפים אתם נרפים על כן אתם אמרים נלכה נזבחה ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 18, 'ועתה לכו עבדו ותבן לא ינתן לכם ותכן הלבנים תתנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 19, 'ויראו שטרי בני ישראל אתם ברע לאמר לא יגרע מלבניכם דבר יום ביומו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 20, 'ויפגעו את משה ואת אהרן נצבים לקראתם בצאתם מאת פני פרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 21, 'ויאמרו אליהם יראה יהוה עליכם וישפט אשר הבאשתם את ריחנו בעיני פרעה ובעיני עבדיו לתת חרב בידו להרגנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 22, 'וישב משה אל יהוה ויאמר אדני למה הרעת לעם הזה ולמה זה שלחתני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 5, 23, 'ומאז באתי אל פרעה לדבר בשמך הרע לעם הזה והצל לא הצלת את עמך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 1, 'ויאמר יהוה אל משה אתה תראה את אשר אעשה לפרעה כי ביד חזקה ישלחם וביד חזקה יגרשם מארצו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 2, 'וידבר יהוה אל משה ויאמר אליו אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 3, 'ואראה אל אברהם ואל יצחק ואל יעקב באל שדי ושמי יהוה לא נודעתי להם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 4, 'וגם הקמתי את בריתי אתם לתת להם את ארץ כנען את ארץ מגריהם אשר גרו בה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 5, 'וגם אני שמעתי את נקאת בני ישראל אשר מצרים מעבדים אתם ואזכרה את בריתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 6, 'לכן אמר לבני ישראל אני יהוה והוצאתי אתכם מתחת סבלות מצרים והצלתי אתכם מעבדתם וגאלתי אתכם בזרוע נטויה ובמשפטים גדלים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 7, 'ולקחתי אתכם לי לעם והייתי לכם לאלהים וידעתם כי אני יהוה אלהיכם המוצא אתכם מתחת סבלת מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 8, 'והבאתי אתכם אל הארץ אשר נשאתי את ידי לתת אתה לאברהם ליצחק וליעקב ונתתי אתה לכם מורשה אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 9, 'וידבר משה כן אל בני ישראל ולא שמעו אל משה מקצר רוח ומעבדה קשה ויאמרו אל משה חדל נא ממנו ונעבדה את מצרים כי טוב לנו עבד את מצרים ממותנו במדבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 10, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 11, 'בא דבר אל פרעה מלך מצרים וישלח את בני ישראל מארצו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 12, 'וידבר משה לפני יהוה לאמר הן בני ישראל לא שמעו אלי ואיך ישמעני פרעה ואני ערל שפתים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 13, 'וידבר יהוה אל משה ואל אהרן ויצום אל בני ישראל ואל פרעה מלך מצרים להוציא את בני ישראל מארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 14, 'ואלה ראשי בית אבתם בני ראובן בכור ישראל חנוך ופלוא חצרון וכרמי אלה משפחות ראובן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 15, 'ובני שמעון ימואל וימין ואהד ויכין וצהר ושאול בן הכנענית אלה משפחות שמעון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 16, 'ואלה שמות בני לוי לתולדתם גרשון קהת ומררי ושני חיי לוי שבע ושלשים ומאת שנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 17, 'ובני גרשון לבני ושמעי למשפחותם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 18, 'ובני קהת עמרם ויצהר חברון ועזיאל ושני חיי קהת שלש ושלשים ומאת שנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 19, 'ובני מררי מחלי ומושי אלה משפחות הלוי לתולדתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 20, 'ויקח עמרם את יוכבד דדתו לו לאשה ותלד לו את אהרן ואת משה ואת מרים אחתם ושני חיי עמרם שש ושלשים ומאת שנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 21, 'ובני יצהר קרח ונפג וזכרי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 22, 'ובני עזיאל מישאל ואליצפן וסתרי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 23, 'ויקח אהרן את אלישבע בת עמינדב אחות נחשון לו לאשה ותלד לו את נדב ואת אביהוא את אלעזר ואת איתמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 24, 'ובני קרח אסור ואלקנה ואביסף אלה משפחות הקרחי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 25, 'ואלעזר בן אהרן לקח לו מבנות פוטיאל לו לאשה ותלד לו את פינחס אלה ראשי אבות הלוי למשפחותם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 26, 'הוא אהרן ומשה אשר אמר יהוה להם הוציאו את בני ישראל מארץ מצרים על צבאתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 27, 'הם המדברים אל פרעה מלך מצרים להוציא את בני ישראל מארץ מצרים הוא משה ואהרן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 28, 'ויהי ביום דבר יהוה אל משה בארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 29, 'וידבר יהוה אל משה לאמר אני יהוה דבר אל פרעה מלך מצרים את כל אשר אני דבר אליך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 6, 30, 'ויאמר משה לפני יהוה הן אני ערל שפתים ואיך ישמעני . פרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 1, 'ויאמר יהוה אל משה ראה נתתיך אלהים לפרעה ואהרן אחיך יהיה נביאך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 2, 'אתה תדבר את כל אשר אצוך ואהרן אחיך ידבר אל פרעה ושלח את בני ישראל מארצו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 3, 'ואני אקשה את לב פרעה והרביתי את אתתי ואת מופתי בארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 4, 'ולא ישמע אליכם פרעה ונתתי את ידי במצרים והוצאתי את צבאתי את עמי בני ישראל מארץ מצרים במשפטים גדלים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 5, 'וידעו כל מצרים כי אני יהוה בנטתי את ידי על מצרים והוצאתי את עמי בני ישראל מתוכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 6, 'ויעש משה ואהרן כאשר צוה יהוה אתם כן עשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 7, 'ומשה בן שמנים שנה ואהרן בן שלש ושמנים שנה בדברם אל פרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 8, 'וידבר יהוה אל משה ואל אהרן לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 9, 'כי ידבר אליכם פרעה לאמר תנו לכם אות או מופת ואמרת אל אהרן קח את מטך והשלך לפני פרעה ויהי לתנין ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 10, 'ויבא משה ואהרן לפני פרעה ויעשו כן כאשר צוה יהוה וישלך אהרן את מטהו לפני פרעה ולפני עבדיו ויהי לתנין ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 11, 'ויקרא גם פרעה לחכמים ולמכשפים ויעשו גם הם חרטמי מצרים בלהטיהם כן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 12, 'וישלכו איש מטהו ויהיו לתנינים ויבלע מטה אהרן את מטותם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 13, 'ויחזק לב פרעה ולא שמע אליהם כאשר דבר יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 14, 'וידבר יהוה אל משה כבד לב פרעה מאן לשלח את ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 15, 'העם לך אל פרעה בבקר הוא יצא המים ונצבת לקראתו על שפת היאר והמטה אשר נהפך לנחש תקח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 16, 'בידך ואמרת אליו יהוה אלהי העברים שלחני אליך לאמר שלח את עמי ויעבדני במדבר והנה לא שמעת עד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 17, 'כה כה אמר יהוה בזאת תדע כי אני יהוה הנה אנכי מכה במטה אשר בידי על המים אשר ביאר ונהפכו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 18, 'לדם והדגה אשר ביאר תמות ובאש היאר ונלאו מצרים לשתות מים מן היאר וילך משה ואהרן אל פרעה ויאמרו אליו יהוה אלהי העברים שלחנו אליך לאמר שלח את עמי ויעבדני במדבר והנה לא שמעת עד כה כה אמר יהוה בזאת תדע כי אני יהוה הנה אנכי מכה במטה אשר בידי על המים אשר ביאר ונהפכו לדם והדגה אשר ביאר תמות ובאש היאר ונלאו מצרים לשתות מים מן היאר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 19, 'ויאמר יהוה אל משה אמר אל אהרן קח את מטך ונטה את ידך על מימי מצרים על נהרותם ועל יאריהם ועל אגמיהם ועל כל מקוה מימיהם ויהיו דם ויהי הדם בכל ארץ מצרים ובעצים ובאבנים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 20, 'ויעשו כן משה ואהרן כאשר צוה יהוה וירם במטהו ויך את המים אשר ביאר לעיני פרעה ולעיני עבדיו ויהפכו כל המים אשר ביאר לדם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 21, 'והדגה אשר ביאר מתה ויבאש היאר ולא יכלו מצרים לשתות מים מן היאר ויהי הדם בכל ארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 22, 'ויעשו כן חרטמי מצרים בלהטיהם ויחזק לב פרעה ולא שמע אליהם כאשר דבר יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 23, 'ויפן פרעה ויבא אל ביתו ולא שת לבו גם לזאת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 24, 'ויחפרו כל מצרים סביבת היאר מים לשתות כי לא יכלו לשתות ממימי היאר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 25, 'וימלאו שבעת ימים אחרי הכות יהוה את היאר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 26, 'ויאמר יהוה אל משה בא אל פרעה ודברת אליו כה אמר יהוה שלח את עמי ויעבדני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 27, 'ואם מאן אתה לשלח הנה אנכי נגף את כל גבולך בצפרדעים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 28, 'ושרץ היאר צפרדעים ועלו ובאו בביתך ובחדרי משכביך ועל מטתיך ובבתי עבדיך ובעמך ובתנוריך ובמשארתיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 7, 29, 'ובך ובעמך ובכל עבדיך יעלו הצפרדעים ויבא משה ואהרן אל פרעה וידברו אליו כה אמר יהוה שלח את עמי ויעבדני ואם מאן אתה לשלח הנה אנכי נגף את כל גבולך בצפרדעים ושרץ היאר צפרדעים ועלו ובאו בבתיך ובחדרי משכביך ועל מטתיך ובבתי עבדיך ובעמך ובתנוריך ובמשארתיך ובד ובעמך ובכל עבדיך יעלו הצפרדעים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 1, 'ויאמר יהוה אל משה אמר אל אהרן נטה את ידך במטך על הנהרות ועל היארים ועל האגמים והעל את הצפרדעים על ארץ מצרים ויאמר משה אל אהרן נטה את ידך במטך ותעל הצפרדע על ארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 2, 'ויט אהרן את ידו על מימי מצרים ותעל הצפרדע ותכס את ארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 3, 'ויעשו כן חרטמי מצרים בלהטיהם ויעלו . הצפרדעים על ארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 4, 'ויקרא פרעה למשה ולאהרן ויאמר העתירו אל יהוה ויסר הצפרדעים ממני ומעמי ואשלח את העם ויזבחו ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 5, 'ויאמר משה אל פרעה התפאר עלי למתי אעתיר לך ולעבדיך ולעמך להכרית הצפרדעים ממך ומבתיך ומעבדיך ומעמך רק ביאר תשארנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 6, 'ויאמר למחר ויאמר כדבריך למען תדע כי אין כיהוה אלהינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 7, 'וסרו הצפרדעים ממך ומבתיך ומעבדיך ומעמך רק ביאר תשארנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 8, 'ויצא משה ואהרן מעם פרעה ויצעק משה אל יהוה על דבר הצפרדעים אשר שם לפרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 9, 'ויעש יהוה כדבר משה וימתו הצפרדעים מן הבתים ומן החצרות ומן השדות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 10, 'ויצברו אתם חמרים חמרים ותבאש הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 11, 'וירא פרעה כי היתה הרוחה ויכבד את לבו ולא שמע אליהם כאשר דבר יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 12, 'ויאמר יהוה אל משה אמר אל אהרן נטה את ידך במטך והך את עפר הארץ ויהי לכנים בכל ארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 13, 'ויעשו כן ויט אהרן את ידו במטהו ויך את עפר הארץ ותהי הכנים באדם ובבהמה כל עפר הארץ היה כנים בכל ארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 14, 'ויעשו כן החרטמים בלהטיהם להוציא את הכנים ולא יכלו ותהי הכנים באדם ובבהמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 15, 'ויאמרו החרטמים אל פרעה אצבע אלהים היא ויחזק לב פרעה ולא שמע אליהם כאשר דבר יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 16, 'ויאמר יהוה אל משה השכם בבקר והתיצב לפני פרעה הנה הוא יצא המים ואמרת אליו כה אמר יהוה שלח את עמי ויעבדני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 17, 'כי אם אינך משלח את עמי הנני משלח בך ובעבדיך ובעמך ובבתיך את הערב ומלאו בתי מצרים את הערב וגם האדמה אשר הם עליה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 18, 'והפליתי ביום ההוא את ארץ גשן אשר עמי עמד עליה לבלתי היות שם ערב למען תדע כי אני יהוה בקרב הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 19, 'ושמתי פדות בין עמי ובין עמך למחר יהיה האות הזה ויבא משה ואהרן אל פרעה ויאמרו אליו כה אמר יהוה שלח את עמי ויעבדני כי אם אינך משלח את עמי הנני משלח בך ובעבדיך ובעמך ובבתיך את הערב ומלאו בתי מצרים את הערב וגם האדמה אשר הם עליה והפליתי ביום ההוא את ארץ גשן אשר עמי עמד עליה לבלתי היות שם ערב למען תדע כי אני יהוה בקרב הארץ ושמתי פדות בין עמי ובין עמך למחר יהיה האות הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 20, 'ויעש יהוה כן ויבא ערב כבד מאד ביתה פרעה ובית עבדיו בכל ארץ מצרים ותשחת הארץ מפני הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 21, 'ויקרא פרעה למשה . ולאהרן ויאמר לכו זבחו לאלהיכם בארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 22, 'ויאמר משה לא נכון לעשות כן כי תועבת מצרים נזבח ליהוה אלהינו הן נזבח את תועבת מצרים לעיניהם ולא יסקלנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 23, 'דרך שלשת ימים נלך במדבר ונזבח ליהוה אלהינו כאשר יאמר אלינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 24, 'ויאמר פרעה אנכי אשלח אתכם וזבחתם ליהוה אלהיכם במדבר רק הרחק לא תרחיקו ללכת העתירו בעדי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 25, 'ויאמר משה הנה אנכי יצא מעמך והעתרתי אל יהוה וסר הערב מפרעה ומעבדיו ומעמו מחר רק אל יסף פרעה התל לבלתי שלח את העם לזבח ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 26, 'ויצא משה מעם פרעה ויעתר אל יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 27, 'ויעש יהוה כדבר משה ויסר הערב מפרעה ומעבדיו ומעמו לא נשאר אחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 8, 28, 'ויכבד פרעה את לבו גם בפעם הזאת ולא שלח את העם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 1, 'ויאמר יהוה אל משה בא אל פרעה ואמרת אליו כה אמר יהוה אלהי העברים שלח את עמי ויעבדני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 2, 'כי אם מאן אתה לשלח ועודך מחזק בם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 3, 'הנה יד יהוה היה במקניך אשר בשדה בסוסים ובחמרים ובגמלים בבקר ובצאן דבר כבד מאד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 4, 'והפלא יהוה בין מקנה ישראל ובין מקנה מצרים ולא ימות מכל לבני ישראל דבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 5, 'וישם יהוה מועד לאמר מחר יעשה יהוה את הדבר הזה בארץ ויבא משה ואהרן אל פרעה ויאמרו אליו כה אמר יהוה אלהי העברים שלח את עמי ויעבדני כי אם מאן אתה לשלח ועודך מחזק בם הנה יד יהוה היה במקניך אשר בשדה בסוסים ובחמרים ובגמלים בבקר ובצאן דבר כבד מאד והפלא יהוה בין מקנה ישראל ובין מקנה מצרים ולא ימות מכל לבני ישראל דבר מחר יעשה יהוה את הדבר הזה בארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 6, 'ויעש יהוה את הדבר הזה ממחרת וימת כל מקנה מצרים וממקנה בני ישראל לא מת אחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 7, 'וישלח פרעה והנה לא מת ממקנה בני ישראל עד אחד ויכבד לב פרעה ולא שלח את העם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 8, 'ויאמר יהוה אל משה ואל אהרן . קחו לכם מלוא חפניכם פיח כבשן וזרקו משה השמים לעיני פרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 9, 'והיה לאבק על כל ארץ מצרים והיה על האדם ועל הבהמה לשחין פרח אבעבעות בכל ארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 10, 'ויקחו את פיח הכבשן ויעמדו לפני פרעה ויזרק אתו משה השמים ויהי שחין אבעבעות פרח באדם ובבהמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 11, 'ולא יכלו החרטמים לעמד לפני משה מפני השחין כי היה השחין בחרטמים ובכל מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 12, 'ויחזק יהוה את לב פרעה ולא שמע אליהם כאשר דבר יהוה אל משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 13, 'ויאמר יהוה אל משה השכם בבקר והתיצב לפני פרעה ואמרת אליו כה אמר יהוה אלהי העברים שלח את עמי ויעבדני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 14, 'כי בפעם הזאת אני שלח את כל מגפתי על לבך ובעבדיך ובעמך בעבור תדע כי אין כמוני בכל הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 15, 'כי עתה שלחתי את ידי ואכה אתך ואת עמך בדבר ותכחד מן הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 16, 'ואולם בעבור זאת העמדתיך בעבור הראתיך את כחי ולמען ספר שמי בכל הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 17, 'עודך מסתולל בעמי לבלתי שלחם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 18, 'הנני ממטיר כעת מחר ברד כבד מאד אשר לא היה כמהו במצרים למיום . היסדה ועד עתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 19, 'ועתה שלח העז את מקניך ואת כל אשר לך בשדה כל האדם והבהמה אשר ימצא בשדה ולא יאסף הביתה וירד עליהם הברד ומתו ויבא משה ואהרן אל פרעה ויאמרו אליו כה אמר יהוה אלהי העברים שלח את עמי ויעבדני כי בפעם הזאת אני שלח את כל מגפתי על לבך ובעבדיך ובעמך בעבור תדע כי אין כמוני בכל הארץ כי עתה שלחתי את ידי ואכה אתך ואת עמך בדבר ותכחד מן הארץ ואולם בעבור זאת העמדתיך בעבור הראתיך את כחי ולמען ספר שמי בכל הארץ עודך מסתולל בעמי לבלתי שלחם הנני ממטיר כעת מחר ברד כבד מאד אשר לא היה כמהו במצרים למיום היסדה ועד עתה ועתה שלח חעז את מקניך ואת כל אשר לך בשדה כל האדם והבהמה אשר ימצא בשדה ולא יאסף הביתה וירד עליהם הברד ומתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 20, 'הירא את דבר יהוה מעבדי פרעה הניס את עבדיו ואת מקנהו אל הבתים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 21, 'ואשר לא שם לבו על דבר יהוה ויעזב את עבדיו ואת מקנהו בשדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 22, 'ויאמר יהוה אל משה נטה את ידך על השמים ויהי ברד בכל ארץ מצרים על האדם ועל הבהמה ועל כל עשב השדה בארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 23, 'ויט משה את מטהו על השמים ויהוה נתן קולות וברד ותהלך אש ארצה וימטר יהוה ברד על ארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 24, 'ויהי הברד ואש מתלקחת בתוך הברד כבד מאד אשר לא היה כמהו . . במצרים מאז היתה לגוי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 25, 'ויך הברד . בארץ מצרים את כל אשר בשדה מאדם עד בהמה ואת כל עשב השדה הכה הברד ואת כל עץ השדה שבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 26, 'רק בארץ גשן אשר שם בני ישראל לא היה ברד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 27, 'וישלח פרעה ויקרא למשה ולאהרן ויאמר אליהם חטאתי הפעם יהוה הצדיק ואני ועמי הרשעים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 28, 'העתירו אל יהוה ורב מהיות קולות אלהים וברד . ואשלח אתכם ולא תוסיפון לעמד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 29, 'ויאמר אליו משה כצאתי את העיר אפרש . כפי אל יהוה והקולות יחדלון והברד לא יהיה עוד למען תדע כי ליהוה הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 30, 'ואתה ועבדיך ידעתי כי טרם תיראון מפני אדני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 31, 'והפשתה והשערה נכו כי השערה אביב והפשתה גבעל . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 32, 'והחטה והכסמת לא נכו כי אפלות הנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 33, 'ויצא משה מעם פרעה את העיר ויפרש כפיו אל יהוה ויחדלו הקולות והברד והמטר לא נתך ארצה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 34, 'וירא פרעה כי חדל הברד והמטר והקולות ויסף לחטא ויכבד לבו הוא ועבדיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 9, 35, 'ויחזק לב פרעה ולא שלח את בני ישראל כאשר דבר יהוה ביד משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 1, 'ויאמר יהוה אל משה בא אל פרעה כי אני הכבדתי את לבו ואת לב עבדיו למען שתי אתתי אלה בקרבו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 2, 'ולמען תספר באזני בנך ובן בנך את אשר התעללתי במצרים ואת אתותי אשר שמתי בם וידעתם כי אני יהוה אלהיכם ואמרת אל פרעה כה אמר יהוה אלהי העברים עד מתי מאנת לענות מפני שלח את עמי ויעבדני כי אם מאן אתה לשלח את עמי הנני מביא מחר ארבה בגבולך וכסה את עין הארץ ולא יכל לראות את הארץ ואכל את יתר הפלטה הנשארת לכם מן הברד ואכל את כל עשב הארץ ואת כל פרי העץ הצמח לכם מן השדה ומלאו בתיך ובתי כל עבדיך ובתי כל מצרים אשר לא ראו אבתיך ואבת אבתיך מיום היותם על האדמה עד היום הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 3, 'ויבא משה ואהרן אל פרעה ויאמרו אליו כה אמר יהוה אלהי העברים עד מתי מאנת לענות מפני שלח את עמי ויעבדני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 4, 'כי אם מאן אתה לשלח את עמי הנני מביא מחר ארבה בגבולך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 5, 'וכסה את עין הארץ ולא יכל לראות את הארץ ואכל את יתר הפלטה הנשארת לכם מן הברד ואכל את כל עשב הארץ ואת כל פרי העץ הצמח לכם מן השדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 6, 'ומלאו בתיך ובתי כל עבדיך ובתי כל מצרים אשר לא ראו אבתיך ואבת אבתיך מיום היותם על האדמה עד היום הזה ויפן ויצא מעם פרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 7, 'ויאמרו עבדי פרעה אליו עד מתי יהיה זה לנו למוקש שלח את האנשים ויעבדו את יהוה אלהיהם הטרם תדע כי אבדה מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 8, 'וישב את משה ואת אהרן אל פרעה ויאמר אליהם לכו עבדו את יהוה אלהיכם מי ומי ההלכים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 9, 'ויאמר משה בנערינו ובזקנינו נלך בבנינו ובבנתינו בצאננו ובבקרנו נלך כי חג יהוה לנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 10, 'ויאמר אליהם יהיה כן יהוה עמכם כאשר אשלח אתכם ואת טפכם ראו כי רעה נגד פניכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 11, 'לכן . לכו נא הגברים ועבדו את יהוה כי אתה אתם מבקשים ויגרשו אתם מאת פני פרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 12, 'ויאמר יהוה אל משה נטה את ידך על ארץ מצרים בארבה ויעל על ארץ מצרים ויאכל את כל עשב הארץ ואת כל פרי העץ אשר השאיר הברד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 13, 'ויט משה את ידו על ארץ מצרים ויהוה נהג רוח קדים בארץ כל היום ההוא וכל הלילה הבקר היה ורוח הקדים נשאה את הארבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 14, 'ויעל הארבה על כל ארץ מצרים וינח בכל גבול מצרים כבד מאד לפניו לא היה כן ארבה כמהו ואחריו לא יהיה כן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 15, 'ויכס את עין כל הארץ ותחשך הארץ ויאכל את כל עשב הארץ ואת כל פרי העץ אשר הותיר הברד ולא נותר כל ירק בעץ ובעשב השדה בכל ארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 16, 'וימהר פרעה לקרא למשה ולאהרן ויאמר חטאתי ליהוה אלהיכם ולכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 17, 'ועתה שאו נא חטאתי אך הפעם העתירו אל יהוה אלהיכם ויסר מעלי רק את המות הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 18, 'ויצא מעם פרעה ויעתר אל יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 19, 'ויהפך יהוה רוח ים חזק מאד וישא את הארבה ויתקעהו ים סוף לא נשאר ארבה אחד בכל גבול מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 20, 'ויחזק יהוה את לב פרעה ולא שלח את בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 21, 'ויאמר יהוה אל משה נטה את ידך על השמים ויהי חשך על ארץ מצרים וימש החשך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 22, 'ויט משה את ידו על השמים ויהי חשך אפלה בכל ארץ מצרים שלשת ימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 23, 'לא ראו איש את אחיו ולא קמו איש מתחתיו שלשת ימים ולכל בני ישראל היה אור במושבתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 24, 'ויקרא פרעה למשה ולאהרן ויאמר לכו עבדו את יהוה רק צאנכם ובקרכם יצג גם טפכם ילך עמכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 25, 'ויאמר משה גם אתה תתן בידנו זבחים ועלות ועשינו ליהוה אלהינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 26, 'וגם מקנינו ילך עמנו לא תשאר פרסה כי ממנו נקח לעבד את יהוה אלהינו ואנחנו לא נדע מה נעבד את יהוה עד באנו שמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 27, 'ויחזק יהוה את לב פרעה ולא אבה לשלחם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 28, 'ויאמר לו פרעה לך לך מעלי השמר לך אל תוסף ראות פני כי ביום ראותך פני תמות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 10, 29, 'ויאמר משה כן דברת לא אוסף עוד ראות פניך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 11, 1, 'ויאמר יהוה אל משה עוד נגע אחד אביא על פרעה ועל מצרים ואחרי כן ישלח אתכם מזה כשלחו כלה גרש יגרש אתכם מזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 11, 2, 'דברו נא באזני העם וישאלו איש מאת רעהו ואשה מאת רעותה כלי כסף וכלי זהב ושמלות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 11, 3, 'ונתתי . את חן העם הזה בעיני מצרים והשאלום וכחצית הלילה אני יצא בתוך ארץ מצרים ומת כל בכור בארץ מצרים מבכור פרעה הישב על כסאו ועד בכור השפחה אשר אחר הרחים ועד בכור כל בהמה והיתה צעקה גדלה במצרים אשר כמוה לא נהיתה וכמוה לא תסף ולכל בני ישראל לא יחרץ כלב לשנו למאיש ועד בהמה למען תדע אשר יפלא יהוה בין מצרים ובין ישראל וגם האיש משה גדל מאד . בארץ מצרים בעיני עבדי פרעה ובעיני העם ויאמר משה אל פרעה כה אמר יהוה בני בכרי ישראל ואמר אליך שלח את בני ויעבדני ותמאן לשלחו הנה יהוה הרג את בנך בכרך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 11, 4, 'ויאמר משה . . כה אמר יהוה כחצית הלילה אני יצא בתוך ארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 11, 5, 'ומת כל בכור בארץ מצרים מבכור פרעה הישב על כסאו ועד בכור השפחה אשר אחר הרחים ועד בכור כל בהמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 11, 6, 'והיתה צעקה גדלה . . במצרים אשר כמוה לא נהיתה וכמוה לא תסף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 11, 7, 'ולכל בני ישראל לא יחרץ כלב לשנו למאיש ועד בהמה למען תדע אשר יפלא יהוה בין מצרים ובין ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 11, 8, 'וירדו כל עבדיך אלה אלי וישתחוו לי לאמר צא אתה וכל העם אשר ברגליך ואחרי כן אצא ויצא מעם פרעה בחרי אף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 11, 9, 'ויאמר יהוה אל משה לא ישמע אליכם פרעה למען רבות מופתי בארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 11, 10, 'ומשה ואהרן עשו את כל המפתים האלה לפני פרעה ויחזק יהוה את לב פרעה ולא שלח את בני ישראל מארצו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 1, 'ויאמר יהוה אל משה ואל אהרן בארץ מצרים לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 2, 'החדש הזה לכם ראש החדשים ראישון הוא לכם לחדשי השנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 3, 'דברו נא אל כל עדת בני ישראל לאמר בעשור לחדש הזה ויקחו להם איש שה לבית אבות שה לבית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 4, 'ואם ימעט הבית מהיות משה ולקח הוא ושכינו הקרוב אל ביתו במכסות נפשות איש לפי אכלו תכסו על השה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 5, 'שה תמים זכר בן שנה יהיה לכם מן הכשבים ומן העזים תקחו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 6, 'והיה לכם למשמרת עד ארבעה עשר יום לחדש הזה ושחטו אתו כל קהל עדת בני ישראל בין הערבים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 7, 'ולקחו מן הדם ונתנו על שתי המזוזת ועל המשקף על הבתים אשר יאכלו אתו בהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 8, 'ואכלו את הבשר בלילה הזה צלי אש ומצות על מררים יאכלהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 9, 'אל תאכלו ממנו נא ובשל מבשל במים כי אם צלי אש ראשו על כרעיו ועל קרבו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 10, 'ולא תותירו ממנו עד בקר והנותר ממנו עד בקר באש תשרפו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 11, 'וככה תאכלו אתו מתניכם חגרים נעליכם ברגליכם ומקליכם בידיכם ואכלתם אתו בחפזון פסח הוא ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 12, 'ועברתי בארץ מצרים בלילה הזה והכיתי כל בכור בארץ מצרים מאדם עד בהמה ובכל אלהי מצרים אעשה שפטים אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 13, 'והיה הדם לכם לאות על הבתים אשר אתם שם וראיתי את הדם ופסחתי עליכם ולא יהיה בכם נגף למשחית בהכותי בארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 14, 'והיה היום הזה לכם לזכרון וחגתם אתו חג ליהוה לדרתיכם חקת עולם תחגהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 15, 'שבעת ימים מצות תאכלו אך ביום הראישון תשביתו שאר מבתיכם כי כל אכל חמץ ונכרתה הנפש ההיא מישראל מיום הראישון ועד יום השביעי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 16, 'וביום הראישון מקרא קדש וביום השביעי מקרא קדש יהיה לכם כל מלאכה לא יעשה בהם אך אשר יאכל לכל נפש הוא לבדו יעשה לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 17, 'ושמרתם את המצות כי בעצם היום הזה הוצאתי את צבאתיכם מארץ מצרים ושמרתם ועשיתם את היום הזה לדרתיכם חקת עולם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 18, 'בראישון בארבעה עשר יום לחדש בערב תאכלו מצות עד יום אחד ועשרים לחדש בערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 19, 'שבעת ימים שאר לא ימצא בבתיכם כי כל אכל מחמצת ונכרתה הנפש ההוא מעדת ישראל בגר ובאזרח הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 20, 'כל מחמצת לא תאכלו בכל מושבתיכם תאכלו מצות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 21, 'ויקרא משה לכל זקני ישראל ויאמר אליהם משכו קחו לכם צאן למשפחתיכם ושחטו הפסח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 22, 'ולקחתם אגדת אזוב וטבלתם בדם אשר בסף והגעתם על המשקף ועל שתי המזוזת מן הדם אשר בסף ואתם אל תצאו איש מפתח ביתו עד בקר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 23, 'ועבר יהוה לנגף את מצרים וראה את הדם על המשקף ועל שתי המזוזת ופסח יהוה על הפתח ולא יתן המשחית לבוא אל בתיכם לנגף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 24, 'ושמרתם את הדבר הזה לחק לך ולבניך עד עולם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 25, 'והיה כי תבאו אל הארץ אשר יתן יהוה לכם כאשר דבר ושמרתם את העבדה הזאת בחדש הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 26, 'והיה כי יאמרו אליכם בניכם מה העבדה הזאת לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 27, 'ואמרתם זבח פסח הוא ליהוה אשר פסח על בתי בני ישראל במצרים בנגפו את מצרים ואת בתינו הציל ויקדו העם וישתחוו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 28, 'וילכו ויעשו בני ישראל כאשר צוה יהוה את משה ואהרן כן עשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 29, 'ויהי בחצי הלילה ויהוה הכה כל בכור בארץ מצרים מבכור פרעה הישב על כסאו ועד בכור השבי אשר בבית הבור וכל בכור בהמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 30, 'ויקם פרעה לילה הוא וכל עבדיו וכל מצרים ותהי צעקה גדלה במצרים כי אין בית אשר אין שם מת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 31, 'ויקרא למשה ולאהרן לילה ויאמר קומו צאו מתוך עמי גם אתם גם בני ישראל ולכו עבדו את יהוה כדבריכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 32, 'גם צאנכם גם בקרכם קחו כאשר דברתם ולכו וברכתם גם אתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 33, 'ותחזק מצרים על העם למהר לשלחם מן הארץ כי אמרו כלנו מתים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 34, 'וישא העם את בצקו טרם יחמץ משארתם צררות בשמלתם על שכמם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 35, 'ובני ישראל עשו כדבר משה וישאלו ממצרים כלי כסף וכלי זהב ושמלות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 36, 'ויהוה נתן את חן העם בעיני מצרים וישאלום וינצלו את מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 37, 'ויסעו בני ישראל מרעמסס סכותה כשש מאות אלף רגלי הגברים לבד מטף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 38, 'וגם ערב רב עלה אתם וצאן ובקר מקנה כבד מאד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 39, 'ויאפו את הבצק אשר הוציאו ממצרים עגות מצות כי לא חמץ כי גרשום מצרים ולא יכלו להתמהמה וגם צדה לא עשו להם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 40, 'ומושב בני ישראל ואבתם אשר ישבו בארץ כנען ובארץ מצרים שלשים שנה וארבע מאות שנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 41, 'ויהי מקץ שלשים שנה וארבע מאות שנה ויהי בעצם היום הזה יצאו כל צבאות יהוה מארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 42, 'לילה שמרים הוא ליהוה להוציאם מארץ מצרים הוא הלילה הזה ליהוה שמרים לכל בני ישראל לדרתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 43, 'ויאמר יהוה אל משה ואל אהרן זאת חקת הפסח כל בן נכר לא יאכל בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 44, 'וכל עבד איש מקנת כספו ומלת אתו אז יאכל בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 45, 'תושב ושכיר לא יאכל בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 46, 'בבית אחד יאכל לא תוציאו מן הבית מן הבשר החוצה ועצם לא תשברו בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 47, 'כל עדת ישראל יעשו אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 48, 'וכי יגור אתכם גר ועשה פסח ליהוה המול לו כל זכר ואז יקרב לעשותו והיה כאזרח הארץ וכל ערל לא יאכל בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 49, 'תורה אחת יהיה לאזרח ולגר הגר בתוככם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 50, 'ויעשו כל בני ישראל כאשר צוה יהוה את משה ואת אהרן כן עשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 12, 51, 'ויהי בעצם היום הזה הוציא יהוה את בני ישראל מארץ מצרים על צבאתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 13, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 13, 2, 'קדש לי כל בכור פטר כל רחם בבני ישראל באדם ובבהמה לי הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 13, 3, 'ויאמר משה אל העם זכרו את היום הזה אשר יצאתם בו מארץ מצרים מבית עבדים כי בחזק יד הוציא יהוה אתכם מזה ולא יאכל חמץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 13, 4, 'היום ואתם יצאים בחדש האביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 13, 5, 'והיה כי יביאך יהוה אלהיך אל ארץ הכנעני החתי והאמרי והפרזי והגרגשי והחוי והיבוסי . אשר נשבע לאבתיך לתת לך ארץ זבת חלב ודבש ועבדת את העבדה הזאת בחדש הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 13, 6, 'ששת ימים תאכל מצות וביום השביעי חג ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 13, 7, 'מצות יאכל את שבעת הימים ולא יראה לך חמץ ולא יראה לך שאר בכל גבולך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 13, 8, 'והגדת לבנך ביום ההוא לאמר בעבור זה עשה יהוה לי בצאתי ממצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 13, 9, 'והיו לך לאות על ידיך ולזכרון בין עיניך למען תהיה תורת יהוה בפיך כי ביד חזקה הוציאך יהוה ממצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 13, 10, 'ושמרת את החקה הזאת למועדה מימים ימימה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 13, 11, 'והיה כי יביאך יהוה אלהיך אל ארץ הכנעני כאשר נשבע לך ולאבתיך ונתנה לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 13, 12, 'והעברת כל פטר רחם ליהוה וכל פטר שגר בהמה אשר יהיו לך הזכרים ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 13, 13, 'וכל פטר חמור תפדה בשה ואם לא תפדנו וערפתו וכל בכור אדם בבניך תפדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 13, 14, 'והיה כי ישאלך בנך מחר לאמר מה זאת ואמרת אליו בחזק יד הוציאנו יהוה ממצרים מבית עבדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 13, 15, 'ויהי כי הקשה פרעה לשלחנו ויהרג יהוה כל בכור בארץ מצרים מבכור אדם עד בכור בהמה על כן אני זבח ליהוה כל פטר רחם הזכרים וכל בכור אדם בבני אפדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 13, 16, 'והיו לך לאות על ידיך ולטטפות בין עיניך כי בחזק יד הוציאך יהוה ממצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 13, 17, 'ויהי בשלח פרעה את העם ולא נחם אלהים דרך ארץ פלשתים כי קרוב הוא כי אמר אלהים פן ינחם העם בראותם מלחמה ושבו מצרימה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 13, 18, 'ויסב אלהים את העם דרך מדבר ים סוף וחמשים עלו בני ישראל מארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 13, 19, 'ויקח משה את עצמת יוסף עמו כי השבע השביע יוסף את בני ישראל לאמר פקד יפקד אלהים אתכם והעליתם את עצמתי מזה אתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 13, 20, 'ויסעו מסכות ויחנו באתם אשר בקצה המדבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 13, 21, 'ויהוה הלך לפניהם יומם בעמוד ענן לנחתם דרך ולילה בעמוד אש להאיר להם ללכת יומם ולילה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 13, 22, 'לא ימוש עמוד ענן יומם ועמוד אש לילה לפני העם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 2, 'דבר אל בני ישראל וישבו ויחנו לפני פי החירת בין מגדל ובין הים לפני בעל צפון נכחו תחנו על הים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 3, 'ואמר פרעה . לבני ישראל נבכים הם בארץ סגר עליהם המדבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 4, 'וחזקתי את לב פרעה ורדף אחריהם ואכבדה בפרעה ובכל חילו וידעו מצרים כי אני יהוה ויעשו כן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 5, 'ויגד למלך מצרים כי ברח העם ויהפך לב פרעה ועבדיו על העם ויאמרו מה זאת עשינו כי שלחנו את ישראל מעבדנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 6, 'ויאסר את רכבו ואת עמו לקח עמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 7, 'ויקח שש מאות רכב בחור וכל רכב מצרים ושלשים על כלו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 8, 'ויחזק יהוה את לב פרעה מלך מצרים וירדף אחרי בני ישראל ובני ישראל יצאים ביד רמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 9, 'וירדפו מצרים אחריהם וישגו אתם חנים על הים כל סוס רכב פרעה ופרשיו וחילו על פי החירת לפני בעל צפון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 10, 'ופרעה הקריב וישאו בני ישראל את עיניהם ויראו והנה מצרים נסעים אחריהם וייראו מאד ויצעקו בני ישראל אל יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 11, 'ויאמרו אל משה המבלי אין קברים במצרים לקחתנו למות במדבר מה זאת עשית לנו להוציאנו ממצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 12, 'הלא זה הדבר אשר דברנו אליך במצרים לאמר חדל נא ממנו ונעבדה את מצרים כי טוב לנו עבד את מצרים ממתנו במדבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 13, 'ויאמר משה אל העם אל תיראו התיצבו וראו את ישועת יהוה אשר יעשה לכם היום כי כאשר ראיתם את מצרים היום לא תוספון לראתם עוד עד עולם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 14, 'יהוה ילחם לכם ואתם תחרישון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 15, 'ויאמר יהוה אל משה מה תצעק אלי דבר אל בני ישראל ויסעו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 16, 'ואתה הרם את מטך ונטה את ידך על הים ובקעהו ויבאו בני ישראל בתוך הים ביבשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 17, 'ואני הנני מחזק את לב מצרים ויבאו אחריהם ואכבדה בפרעה ובכל חילו ברכבו ובפרשיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 18, 'וידעו כל מצרים כי אני יהוה בהכבדי בפרעה ובכל חילו ברכבו ובפרשיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 19, 'ויסע מלאך האלהים ההלך לפני מחנה ישראל וילך מאחריהם ויסע עמוד הענן מפניהם ויעמד מאחריהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 20, 'ויבא בין מחנה מצרים ובין מחנה ישראל ויהי הענן והחשך ויאר את הלילה ולא קרב זה אל זה כל הלילה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 21, 'ויט משה את ידו על הים ויולך יהוה את הים ברוח קדים עזה כל הלילה וישם את הים לחרבה ויבקעו המים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 22, 'ויבאו בני ישראל בתוך הים ביבשה והמים להם חומה מימינם ומשמאלם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 23, 'וירדפו מצרים ויבאו אחריהם כל סוס פרעה רכבו ופרשיו אל תוך הים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 24, 'ויהי באשמרת הבקר וישקף יהוה על מחנה מצרים בעמוד אש וענן ויהם את מחנה מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 25, 'ויאסר את אפן מרכבתיו וינהגהו בכבדות ויאמרו מצרים אנוסה מפני ישראל כי יהוה הנלחם להם במצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 26, 'ויאמר יהוה אל משה נטה את ידך על הים וישבו המים על מצרים על רכבו ועל פרשיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 27, 'ויט משה את ידו על הים וישב הים לפנות הבקר לאיתנו ומצרים נסעים לקראתו וינער יהוה את מצרים תוך הים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 28, 'וישבו המים ויכסו את הרכב ואת הפרשים לכל חיל פרעה הבאים אחריהם בים לא נשאר בהם עד אחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 29, 'ובני ישראל הלכו ביבשה בתוך הים והמים להם חומה מימינם ומשמאלם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 30, 'ויושע יהוה ביום ההוא את ישראל מיד מצרים וירא ישראל את מצרים מת על שפת הים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 14, 31, 'וירא ישראל את היד הגדלה אשר עשה יהוה במצרים וייראו העם את יהוה ויאמנו ביהוה ובמשה עבדו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 1, 'אז ישר משה ובני ישראל את השירה הזאת ליהוה ויאמרו לאמר אשרו ליהוה כי גוי גאה סוס ורכבו רמה בים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 2, 'עזי וזמרת . ויהי לי לישועה זה אלי ואנוהו אלהי אבי וארממנהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 3, 'יהוה גבור במלחמה יהוה שמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 4, 'מרכבת פרעה וחילו ירא בים ומבחר שלשיו טבעו בים סוף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 5, 'תהומת יכסמו ירדו במצלות כמו אבן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 6, 'ימינך יהוה נאדרי בכח ימינך יהוה תרעץ אויב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 7, 'וברב גאנך תהרס קמיך תשלח חרונך יאכלמו כקש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 8, 'וברוח אפיך נערמו מים נצבו כמו נד נזלים קפאו תהומת בלב ים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 9, 'אמר אויב ארדף אשיג אחלק שלל תמלאמו נפשי אריק חרבי תורשמו ידי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 10, 'נשבת ברוחך כסמו ים צללו כעופרת במים אדירים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 11, 'מי כמוך באלים יהוה מי כמוך נאדרי בקדש נורא תהלת עשה פלאה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 12, 'נטית ימינך תבלעמו הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 13, 'נחית בחסדך עם זה גאלת נהלת בעזך אל נוה קדשך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 14, 'שמעו עמים וירגזו חיל אחז ישבי פלשת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 15, 'אז נבהלו אלופי אדום אילי מואב יאחזמו רעד נמגו כל ישבי כנען ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 16, 'תפל עליהם אימה ופחד בגדול זרועך ידמו כאבן עד יעבר עמך יהוה עד יעבר עם זה קנית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 17, 'תביאמו ותטעמו בהר נחלתך מכון לשבתך פעלת יהוה מקדש יהוה כוננו ידך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 18, 'יהוה ימלך עולם ועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 19, 'כי בא סוס פרעה ברכבו ובפרשיו בים וישב יהוה עליהם את מי הים ובני ישראל הלכו ביבשה בתוך הים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 20, 'ותקח מרים הנביאה אחות אהרן את התף בידה ותצאנה כל הנשים אחריה בתפים ובמחלות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 21, 'ותען להם מרים שירו ליהוה כי גוי גאה סוס ורכבו רמה בים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 22, 'ויסע משה את ישראל מים סוף ויוצאהו אל מדבר שור וילכו שלשת ימים במדבר ולא מצאו מים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 23, 'ויבאו מרתה ולא יכלו לשתות מים ממרה כי מרים הם על כן קרא שמה מרה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 24, 'וילן העם על משה לאמר מה נשתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 25, 'ויצעק משה אל יהוה ויראהו יהוה עץ וישלך אל המים וימתקו המים שם שם לו חק ומשפט ושם נסהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 26, 'ויאמר אם שמע תשמע לקול יהוה אלהיך והישר בעיניו תעשה והאזנת למצותיו ושמרת כל חקיו כל המחלה אשר שמתי במצרים לא אשים עליך כי אני יהוה רפאך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 15, 27, 'ויבאו אילים ובאילים שתים עשרה עינות מים ושבעים תמרים ויחנו שם על המים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 1, 'ויסעו מאילים ויבאו כל עדת בני ישראל אל מדבר סין אשר בין אילים ובין סיני בחמשה עשר יום לחדש השני לצאתם מארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 2, 'וילנו כל עדת בני ישראל על משה ועל אהרן במדבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 3, 'ויאמרו אליהם בני ישראל מי יתן מותנו ביד יהוה בארץ מצרים בשבתנו על סיר הבשר באכלנו לחם לשבע כי הוצאתם אתנו אל המדבר הזה להמית את כל הקהל הזה ברעב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 4, 'ויאמר יהוה אל משה הנני ממטיר לכם לחם מן השמים ויצא העם ולקטו דבר יום ביומו למען אנסנו הילכו בתורתי אם לא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 5, 'והיה ביום הששי והכינו את אשר יביאו והיה משנה על אשר ילקטו יום ויום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 6, 'ויאמר משה ואהרן אל כל בני ישראל ערב וידעתם כי יהוה הוציא אתכם מארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 7, 'ובקר וראיתם את כבוד יהוה בשמעו את תלנתיכם על יהוה ואנחנו מה כי תלנו עלינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 8, 'ויאמר משה בתת יהוה לכם בערב בשר לאכל ולחם בבקר לשבע בשמע יהוה את תלנתיכם אשר אתם מלנים עליו ואנחנו מה לא עלינו תלנתיכם כי על יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 9, 'ויאמר משה אל אהרן אמר אל כל עדת בני ישראל קרבו לפני יהוה כי שמע את תלנתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 10, 'ויהי כדבר אהרן אל כל עדת בני ישראל ויפנו אל המדבר והנה כבוד יהוה נראה בענן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 11, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 12, 'שמעתי את תלנות בני ישראל דבר אליהם לאמר בין הערבים תאכלו בשר ובבקר תשבעו לחם וידעתם כי אני יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 13, 'ויהי בערב ותעל השלוי ותכס את המחנה ובבקר היתה שכבת הטל סביב למחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 14, 'ותעל שכבת הטל והנה על פני המדבר דק מחספס דק ככפר על הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 15, 'ויראו בני ישראל ויאמרו איש אל אחיו מן הוא כי לא ידעו מה הוא ויאמר משה אליהם הוא הלחם אשר נתן יהוה לכם לאכלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 16, 'זה הדבר אשר צוה יהוה לקטו ממנו איש לפי אכלו עמר לגלגלת מספר נפשתיכם איש לאשר באהלו תקחו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 17, 'ויעשו כן בני ישראל וילקטו המרבה והממעיט ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 18, 'וימדו בעמר ולא העדיף המרבה והממעיט לא החסיר איש לפי אכלו לקטו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 19, 'ויאמר משה אליהם איש אל יותר ממנו עד בקר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 20, 'ולא שמעו אל משה ויותירו אנשים ממנו עד בקר וירם תולעים ויבאש ויקצף עליהם משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 21, 'וילקטו אתו בבקר בבקר איש לפי אכלו וחםה השמש ונמס ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 22, 'ויהי ביום הששי לקטו לחם משנה שני העמר לאחד ויבאו כל נשיאי העדה ויגדו למשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 23, 'ויאמר אליהם הוא אשר דבר יהוה שבתון שבת קדש ליהוה מחר את אשר תאפו אפו ואת אשר תבשלו בשלו ואת כל העדף הניחו לכם למשמרת עד בקר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 24, 'ויניחו אתו עד הבקר כאשר צוה משה ולא הבאיש ורמה לא היתה בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 25, 'ויאמר משה אכלהו היום כי שבת היום ליהוה היום לא תמצאו בשדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 26, 'ששת ימים תלקטהו וביום השביעי שבת לא יהיה בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 27, 'ויהי ביום השביעי יצאו מן העם ללקט ולא מצאו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 28, 'ויאמר יהוה אל משה עד אנה מאנתם לשמר מצותי ותורתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 29, 'ראו כי יהוה נתן לכם את השבת על כן הוא נתן לכם ביום הששי לחם יומים שבו איש תחתיו אל יצא איש ממקומו ביום השבת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 30, 'וישבת העם ביום השביעי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 31, 'ויקראו בית ישראל את שמו מן והוא כזרע גד לבן וטעמו כצפחית בדבש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 32, 'ויאמר משה זה הדבר אשר צוה יהוה מלאו העמר ממנו למשמרת לדרתיכם למען יראו את הלחם אשר האכלתי אתכם במדבר בהוצאי אתכם מארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 33, 'ויאמר משה אל אהרן קח צנצנת אחת ותן שמ מלוא העמר מן והניח אתו לפני יהוה למשמרת לדרתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 34, 'כאשר צוה יהוה את משה וינחהו אהרן לפני העדות למשמרת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 35, 'ובני ישראל אכלו את המן ארבעים שנה עד באם אל ארץ נושבת את המן אכלו עד באם אל קצה ארץ כנען ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 16, 36, 'והעמר עשרית האיפה הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 17, 1, 'ויסעו כל עדת בני ישראל ממדבר סין למסעיהם על פי יהוה ויחנו ברפדים ואין מים לשתות העם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 17, 2, 'וירב העם עם משה ויאמרו תנה לנו מים ונשתה ויאמר להם משה מה תריבון עמדי ומה תנסו את יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 17, 3, 'ויצמא שם העם למים וילן העם על משה ויאמרו למה . העליתנו ממצרים להמית אתי ואת בני ואת מקני בצמא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 17, 4, 'ויצעק משה אל יהוה לאמר מה אעשה לעם הזה עוד מעט וסקלוני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 17, 5, 'ויאמר יהוה אל משה עבר לפני העם וקח אתך מזקני ישראל ומטך אשר הכית בו את היאר תקח בידך והלכת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 17, 6, 'הנני עמד לפניך שם על הצור בחורב והכית בצור ויצא ממנו מים ושתה העם ויעש כן משה לעיני זקני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 17, 7, 'ויקרא שם המקום מסה ומריבה על ריב בני ישראל ועל נסותם את יהוה לאמר היש יהוה בקרבנו אם אין ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 17, 8, 'ויבא עמלק וילחם עם ישראל ברפדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 17, 9, 'ויאמר משה אל יהושע בחר לנו אנשים וצא הלחם בעמלק מחר אנכי נצב על ראש הגבעה ומטה האלהים בידי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 17, 10, 'ויעש יהושע כאשר אמר לו משה להלחם בעמלק ומשה ואהרן וחור עלו אל ראש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 17, 11, 'הגבעה והיה כאשר ירים משה ידיו וגבר ישראל וכאשר יניח ידיו וגבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 17, 12, 'עמלק וידי משה כבדים ויקחו אבן וישמו תחתיו וישב עליה ואהרן וחור תמכו בידיו מזה אחד ומזה אחד ויהיו ידיו אמונה עד בא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 17, 13, 'השמש ויחלש יהושע את עמלק ואת עמו ויכם לפי חרב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 17, 14, 'ויאמר יהוה אל משה כתב זאת זכרון בספר ושים באזני יהושע כי מחה אמחה את זכר עמלק מתחת השמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 17, 15, 'ויבן משה מזבח ויקרא שמו יהוה נסי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 17, 16, 'ויאמר כי יד על כסא . מלחמה ליהוה בעמלק . מדר ודר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 1, 'וישמע יתרו כהן מדין חתן משה את כל אשר עשה אלהים למשה ולישראל עמו כי הוציא יהוה את ישראל ממצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 2, 'ויקח יתרו חתן משה את צפורה אשת משה אחר שלחיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 3, 'ואת שני בניה אשר שם האחד גרשם כי אמר גר הייתי בארץ נכריה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 4, 'ושם האחד אליעזר כי אלהי אבי בעזרי ויצלני מחרב פרעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 5, 'ויבא יתרו חתן משה ובניו ואשתו אל משה אל המדבר אשר הוא חנה שם הר האלהים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 6, 'ויאמר למשה . הנה חתנך יתרו בא אליך ואשתך ושני בניה עמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 7, 'ויצא משה לקראת חתנו וישתחוי למשה וישק לו וישאלו איש לרעהו לשלום ויבאהו האהלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 8, 'ויספר משה לחתנו את כל אשר עשה יהוה לפרעה ולמצרים על אדות ישראל את כל התלאה אשר מצאתם בדרך ויצלם יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 9, 'ויחד יתרו על כל הטובה אשר עשה יהוה לישראל אשר הצילו מיד מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 10, 'ויאמר יתרו ברוך יהוה אשר הציל אתכם מיד מצרים ומיד פרעה אשר הציל את העם מתחת יד מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 11, 'עתה ידעתי כי גדול יהוה מכל האלהים כי בדבר אשר זדו עליהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 12, 'ויקח יתרו חתן משה עלה וזבחים לאלהים ויבא אהרן . ומזקני ישראל לאכל לחם עם חתן משה לפני האלהים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 13, 'ויהי ממחרת וישב משה לשפט את העם ויעמד העם על משה מן הבקר ועד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 14, 'וירא חתן משה את כל אשר הוא עשה לעם ויאמר מה הדבר הזה אשר אתה עשה לעם מדוע אתה ישב לבדך וכל העם נצב עליך מן הבקר ועד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 15, 'ויאמר משה לחתנו כי יבא אלי העם לדרש אלהים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 16, 'כי יהיה להם דבר באו אלי ושפטתי בין איש ובין רעהו והודעתיו את חקי האלהים ואת תורתיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 17, 'ויאמר חתן משה אליו לא טוב הדבר אשר אתה עשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 18, 'נבל תבל גם אתה גם העם הזה אשר עמך כי כבד ממך הדבר לא תוכל עשותו לבדך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 19, 'עתה שמע בקולי אעיצך ויהי אלהים עמך הוי אתה לעם מול האלהים והבאת אתה את הדברים אל האלהים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 20, 'והזהרת אתם את החקים ואת התורת והודעת להם את הדרך אשר ילכו בה ואת המעשה אשר יעשון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 21, 'ואתה תחזה לך מכל העם אנשי חיל יראי אלהים אנשי אמת שנאי בצע ושמת עליהם שרי אלפים ושרי מאות שרי חמשים ושרי עשרות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 22, 'ושפטו את העם בכל עת והיה כל הדבר הגדול יביאון אליך וכל הדבר הקטן ישפטו הם והקל מעליך ונשאו אתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 23, 'אם את הדבר הזה תעשה וצוך אלהים ויכלת עמד וגם כל העם הזה אל מקומו יבוא בשלום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 24, 'וישמע משה לקול חתנו ויעש כל אשר אמר ויאמר משה אל העם לא אוכל אנכי לבדי שאת אתכם יהוה אלהיכם הרבה אתכם והנכם היום ככוכבי השמים לרב יהוה אלהי אבתיכם יסף עליכם ככם אלף פעמים ויברך אתכם כאשר דבר לכם איכה אשא לבדי טרחכם משאכם וריבכם הבו לכם אנשים חכמים ונבונים וידעים לשבטיכם ואשימם בראשיכם ויענו ויאמרו טוב הדבר אשר דברת לעשות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 25, 'ויקח את ראשי שבטיהם אנשים חכמים וידעים ויתן אתם ראשים עליהם . שרי אלפים ושרי מאות שרי חמשים ושרי עשרות ושטרים לשבטיהם ויצו את שפטיהם לאמר שמעו בין אחיכם ושפטתם צדק בין איש ובין אחיו ובין גרו לא תכירו פנים במשפט כקטן כגדול תשמעון לא תגורו מפני איש כי המשפט לאלהים הוא והדבר אשר יקשה מכם תקריבון אלי ושמעתיו ויצו אתם את כל הדברים אשר יעשון . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 26, 'וישפטו את העם בכל עת את הדבר הגדול יביאון אל משה וכל הדבר הקטן ישפטו הם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 18, 27, 'וישלח משה את חתנו וילך לו אל ארצו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 1, 'בחדש השלישי לצאת בני ישראל מארץ מצרים ביום הזה באו מדבר סיני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 2, 'ויסעו מרפדים ויבאו מדבר סיני ויחנו במדבר ויחן שם ישראל נגד ההר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 3, 'ומשה עלה אל האלהים ויקרא אליו יהוה מן ההר לאמר כה תאמר לבית יעקב ותגיד לבני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 4, 'אתם ראיתם את אשר עשיתי למצרים ואשא אתכם על כנפי נשרים ואביא אתכם אלי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 5, 'ועתה אם שמע תשמעו בקולי ושמרתם את בריתי והייתם לי סגלה מכל העמים כי לי כל הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 6, 'ואתם תהיו לי ממלכת כהנים וגוי קדש אלה הדברים אשר תדבר אל בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 7, 'ויבא משה ויקרא לזקני העם וישם לפניהם את כל הדברים האלה אשר צוהו יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 8, 'ויענו כל העם יחדו ויאמרו כל אשר דבר יהוה נעשה וישב משה את דברי העם אל יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 9, 'ויאמר יהוה אל משה הנה אנכי בא אליך בעבי הענן בעבור ישמע העם בדברי עמך וגם בך יאמנו לעולם ויגד משה את דברי העם אל יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 10, 'ויאמר יהוה אל משה לך אל העם וקדשתם היום ומחר וכבסו שמלתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 11, 'והיו נכנים ליום השלישי כי ביום השלישי ירד יהוה לעיני כל העם על הר סיני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 12, 'והגבלת את ההר סביב ואל העם תאמר השמרו לכם עלות בהר ונגע בקצהו כל הנגע בהר מות יומת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 13, 'לא תגע בו יד כי סקל יסקל או ירא יראה אם בהמה אם איש לא יחיה במשך היובל הם יעלו בהר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 14, 'וירד משה מן ההר אל העם ויקדש את העם ויכבסו שמלתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 15, 'ויאמר אל העם היו נכנים לשלשת הימים אל תגשו אל אשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 16, 'ויהי ביום השלישי בהיות הבקר ויהי קולות וברקים וענן כבד על ההר וקול השופר חזק מאד ויחרד כל העם אשר במחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 17, 'ויוצא משה את העם לקראת האלהים מן המחנה ויתיצבו בתחתית ההר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 18, 'והר סיני עשן כלו מפני אשר ירד יהוה עליו באש ויעל עשנו כעשן הכבשן ויחרד כל ההר מאד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 19, 'ויהי קול השופר הלך וחזק מאד משה ידבר והאלהים יעננו בקול ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 20, 'וירד יהוה על הר סיני אל ראש ההר ויקרא יהוה אל משה אל ראש ההר ויעל משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 21, 'ויאמר יהוה אל משה רד העד בעם פן יהרסו אל יהוה לראות ונפל ממנו רב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 22, 'וגם הכהנים הנגשים אל יהוה יתקדשו פן יפרץ בם יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 23, 'ויאמר משה אל יהוה לא יוכל העם לעלות אל הר סיני כי אתה העדת בנו לאמר הגבל את ההר וקדשתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 24, 'ויאמר אליו יהוה לך רד ועלית אתה ואהרן עמך והכהנים והעם אל יהרסו לעלות אל יהוה פן יפרץ בם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 19, 25, 'וירד משה מן ההר אל העם ויאמר אליהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 1, 'וידבר אלהים את כל הדברים האלה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 2, 'אנכי יהוה אלהיך אשר הוצאתיך מארץ מצרים מבית עבדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 3, 'לא יהיה לך אלהים אחרים על פני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 4, 'לא תעשה לך פסל וכל תמונה אשר בשמים ממעל ואשר בארץ מתחת ואשר במים מתחת לארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 5, 'לא תשתחוה להם ולא תעבדם כי אנכי יהוה אלהיך אל קנא פקד עון אבות על בנים על שלישים ועל רבעים לשנאי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 6, 'ועשה חסד לאלפים לאהבי ולשמרי מצותי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 7, 'לא תשא את שם יהוה אלהיך לשוא כי לא ינקה יהוה את אשר ישא את שמו לשוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 8, 'שמור את יום השבת לקדשהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 9, 'ששת ימים תעבד ועשית כל מלאכתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 10, 'ויום השביעי שבת ליהוה אלהיך לא תעשה כל מלאכה אתה ובנך ובתך עבדך ואמתך בהמתך וגרך אשר בשעריך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 11, 'כי ששת ימים עשה יהוה את השמים ואת הארץ את הים ואת כל אשר בם וינח ביום השביעי על כן ברך יהוה את יום השבת ויקדשהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 12, 'כבד את אביך ואת אמך למען יארכון ימיך על האדמה אשר יהוה אלהיך נתן לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 13, 'לא תרצח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 14, 'לא תנאף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 15, 'לא תגנב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 16, 'לא תענה ברעך עד שקר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 17, 'לא תחמד בית רעך ולא תחמד אשת רעך שדהו עבדו ואמתו שורו וחמרו וכל אשר לרעך והיה כי יביאך יהוה אלהיך אל ארץ הכנעני אשר אתה בא שמה לרשתה והקמת לך אבנים גדלות ושדת אתם בשיד וכתבת על האבנים את כל דברי התורה הזאת והיה בעברכם את הירדן תקימו את האבנים האלה אשר אנכי מצוה אתכם היום בהר גריזים ובנית שם מזבח ליהוה אלהיך מזבח אבנים לא תניף עליהם ברזל אבנים שלמות תבנה את מזבח יהוה אלהיך והעלית עליו עלות ליהוה אלהיך וזבחת שלמים ואכלת שם ושמחת לפני יהוה אלהיך ההר ההוא בעבר הירדן אחרי דרך מבוא השמש בארץ הכנעני הישב בערבה מול הגלגל אצל אלון מורא מול שכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 18, 'וכל העם שמע את הקולות ואת קול השופר וראים את הלפדים ואת ההר עשן ויראו כל העם וינעו ויעמדו מרחק ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 19, '. . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . . ויאמרו אל משה הן הראנו יהוה אלהינו את כבדו ואת גדלו ואת קולו שמענו מתוך האש היום הזה ראינו כי ידבר אלהים את האדם וחי ועתה למה נמות כי תאכלנו האש הגדלה הזאת אם יספים אנחנו לשמע את קול יהוה אלהינו עוד ומתנו כי מי כל בשר אשר שמע קול אלהים חיים מדבר מתוך האש כמונו ויחי קרב אתה ושמע את כל אשר יאמר יהוה אלהינו ואתה תדבר אלינו את כל אשר ידבר יהוה אלהינו אליך ושמענו ועשינו ואל ידבר עמנו האלהים פן נמות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 20, 'ויאמר משה אל העם אל תיראו כי לבעבור נסות אתכם בא האלהים ובעבור תהיה יראתו על פניכם לבלתי תחטאו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 21, 'ויעמד העם מרחק ומשה נגש אל הערפל אשר שם האלהים וידבר יהוה אל משה לאמר שמעתי את קול דברי העם הזה אשר דברו אליך הטיבו כל אשר דברו מי יתן והיה לבבם זה להם ליראה אתי ולשמר את מצותי כל הימים למען ייטב להם ולבניהם לעלם נביא אקים להם מקרב אחיהם כמוך ונתתי דברי בפיו ודבר אליהם את כל אשר אצונו והיה האיש אשר לא ישמע אל דבריו אשר ידבר בשמי אנכי אדרש מעמו אך הנביא אשר יזיד לדבר בשמי את אשר לא צויתיו לדבר ואשר ידבר בשם אלהים אחרים ומת הנביא ההוא וכי תאמר בלבבך איך נודע את הדבר אשר לא דברו יהוה אשר ידבר הנביא בשם יהוה לא יהיה הדבר ולא יבוא הוא הדבר אשר לא דברו יהוה בזידון דברו הנביא לא תגור ממנו לך אמר להם שובו לכם לאהליכם ואתה פה עמד עמדי ואדברה אליך את כל המצוה החקים והמשפטים אשר תלמדם ועשו בארץ אשר אנכי נתן להם לרשתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 22, 'וידבר יהוה אל משה לאמר דבר אל בני ישראל אתם ראיתם כי מן השמים דברתי עמכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 23, 'לא תעשו אתי אלהי כסף ואלהי זהב לא תעשו לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 24, 'מזבח אדמה תעשה לי וזבחת עליו את עלתיך ואת שלמיך . מצאנך . ומבקרך . במקום אשר אזכרתי את שמי שמה אבוא אליך וברכתיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 25, 'ואם מזבח אבנים תעשה לי לא תבנה אתהן גזית כי חרבך הנפת עליו ותחללהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 20, 26, 'ולא תעלה במעלת על מזבחי אשר לא תגלה ערותך אליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 1, 'אלה המשפטים אשר תשים לפניהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 2, 'כי תקנה עבד עברי שש שנים יעבדך ובשבעית יצא לחפשי חנם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 3, 'אם בגפיו יבוא בגפיו יצא אם בעל אשה הוא ויצאה אשתו עמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 4, 'אם אדניו יתן לו אשה וילדה לו בנים או בנות האשה וילדיה תהיה לאדניו והוא יצא בגפיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 5, 'אם אמר יאמר העבד אהבתי את אדני את אשתי ואת בני לא אצא חפשי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 6, 'והגישו אדניו אל האלהים והגישו אל הדלת או אל המזוזה ורצע אדניו את אזנו במרצע ועבדו לעלם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 7, 'וכי ימכר איש את בתו לאמה לא תצא כצאת העבדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 8, 'אם רעה היא בעיני אדניה אשר לא העדה והפדה לעם נכרי לא ימשל למכרה בבגדו בה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 9, 'ואם לבנו יעדנה כמשפט הבנות יעשה לה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 10, 'ואם אחרת יקח לו שארה כסותה וענתה לא יגרע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 11, 'ואם שלש אלה לא יעשה לה ויצאה חנם אין כסף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 12, 'מכה איש ומת מות יומת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 13, 'ואשר לא צדה והאלהים אנה לידו ושמתי לך מקום אשר ינוס שמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 14, 'וכי יזד איש על רעהו להרגו בערמה מעם מזבחי תקחנו למות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 15, 'ומכה אביו ואמו מות יומת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 16, 'וגנב איש ומכרו ונמצא בידו מות יומת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 17, 'ומקלל אביו ואמו מות יומת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 18, 'וכי יריבון אנשים והכו איש את רעהו . . . ולא ימות ונפל למשכב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 19, 'אם יקום והתהלך בחוץ על משענתו ונקה המכה רק שבתו יתן ורפא ירפא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 20, 'וכי יכה איש את עבדו או את אמתו . ומת תחת ידו מות יומת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 21, 'אך אם יום או יומים יעמד לא יומת כי כספו הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 22, 'וכי ינצו אנשים ונגפו אשה הרה ויצא ולדה ולא יהיה אסון ענש יענש כאשר ישית עליו בעל האשה ונתן בפללים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 23, 'ואם אסון יהיה ונתתה נפש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 24, 'תחת נפש עין תחת עין שן תחת שן יד תחת יד רגל תחת רגל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 25, 'מכוה תחת מכוה פצע תחת פצע חבורה תחת חבורה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 26, 'וכי יכה איש את עין עבדו או את עין אמתו ושחתה לחפשי ישלחנו תחת עינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 27, 'ואם שן עבדו או שן אמתו יפל לחפשי ישלחנו תחת שנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 28, 'וכי יכה שור או כל בהמה את איש או את אשה ומת סקל יסקל הבהמה ולא יאכל את בשרו ובעל הבהמה נקיא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 29, 'ואם בהמה מכה היא מתמול שלשום והועד בבעליו ולא ישמרנו והמית איש או אשה הבהמה תסקל וגם בעליו יומת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 30, 'אם כפר יושת עליו ונתן פדיון נפשו ככל אשר יושת עליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 31, 'או בן יכה או בת . כמשפט הזה יעשה לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 32, 'אם עבד תכה הבהמה או אמה כסף שלשים שקלים יתן לאדניו והבהמה תסקל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 33, 'וכי יפתח איש בור או כי יכרה איש בור ולא יכסנו ונפל שם שור או חמור וא כל בהמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 34, 'בעל הבור ישלם כסף ישיב לבעליו והמת יהיה לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 35, 'וכי יגף שור איש או כל בהמתו את שור רעהו או את כל בהמתו ומת ומכרו את . החי וחצו את כספו וגם את המת יחצון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 36, 'או נודע כי בהמה מכה היא מתמול שלשום ולא ישמרנו בעליו שלם ישלם בהמה תחת בהמה והמת יהיה לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 21, 37, 'וכי יגנב איש שור או שה וטבחו או מכרו חמשה בקר ישלם תחת השור וארבע צאן תחת השה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 1, 'אם במחתרת ימצא הגנב והכהו ומת אין לו דם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 2, 'אם זרח השמש עליו דם לו שלם ישלם אם אין לו . ונמכר בגנבתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 3, 'אם המצא תמצא בידו הגנבה משור עד חמור עד שה עד כל בהמה חיים אחד שנים ישלם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 4, 'וכי יבער איש שדה או כרם ושלח את בעירו ובער בשדה אחר שלם ישלם משדהו כתבואתה ואם כל השדה יבעה מיטב שדהו ומיטב כרמו ישלם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 5, 'כי תצא אש ומצאה קוצים ונאכל גדיש או הקמה או השדה שלם ישלם המבער את הבערה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 6, 'וכי יתן איש אל רעהו כסף או כלים לשמר ונגנב מבית האיש אם ימצא הגנב ושלם אחד שנים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 7, 'ואם לא ימצא הגנב ונקרב בעל הבית אל האלהים אם לא שלח ידו במלאכת רעהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 8, 'על כל דבר פשע על שור על חמור על שה על שמלה על כל האבדה אשר יאמר כי הוא זה עד יהוה יבוא דבר שניהם אשר ירשיענו האלהים ישלם אחד שנים לרעהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 9, 'וכי יתן איש אל רעהו חמור או שור או שה או כל בהמה לשמר ומת או נשבר או נשבה אין ראה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 10, 'שבועת יהוה תהיה בין שניהם אם לא שלח ידו במלאכת רעהו ולקח בעליו ולא ישלם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 11, 'אם גנב יגנב מעמו ושלם לבעליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 12, 'אם טרף יטרף יביא עד הטרפה ולא ישלם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 13, 'וכי ישאל איש מעם רעהו ונשבר או מת בעליו אין עמו שלם ישלם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 14, 'אם בעליו עמו לא ישלם אם שכיר הוא ובא בשכרו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 15, 'וכי יפתה איש בתולה אשר לא ארשה ושכב עמה מהר ימהרנה לו לאשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 16, 'ואם מאן ימאן אביה לתתה לו כסף ישקל כמהר הבתולת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 17, 'מכשפה לא תחיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 18, 'כל שכב עם בהמה מות יומת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 19, 'זבח לאלהים אחרים יחרם . . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 20, 'וגר לא תונו ולא תלחצו כי גרים הייתם בארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 21, 'כל אלמנה ויתום לא תענו כי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 22, 'אם ענה תענו אתו כי אם צעק יצעק אלי שמע אשמע צעקתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 23, 'וחרה אפי והרגתי אתכם בחרב והיו נשיכם אלמנות ובניכם יתמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 24, 'אם כסף תלוה את עמי את עני עמך לא תהיה לו כנשא לא תשימנו עליו נשך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 25, 'אם חבל תחבל שמלת רעך עד בא השמש תשיבנה לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 26, 'כי היא כסותו לבדה היא שמלתו לעורו במה ישכב והיה כי יצעק אלי ושמעתי כי חנון אנכי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 27, 'אלהים לא תקלל ונשיא בעמך לא תאר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 28, 'מליתך ודמעך לא תאחר בכור בניך תתן לי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 29, 'כן תעשה לשורך ולצאנך שבעת ימים יהיה עם אמו וביום השמיני תתנו לי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 22, 30, 'ואנשי קדש תהיו לי ובשר בשדה טרפה לא תאכלו השלך תשליכו אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 1, 'לא תשא שמע שוא אל תשית ידך עם רשע להיות עד חמס ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 2, 'לא תהיה אחרי רבים לרעות ולא תענה על ריב לנטות אחרי רבים להטות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 3, 'ודל לא תהדר בריבו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 4, 'כי תפגע שור איבך או חמרו או כל בהמתו תעה השב תשיבנו לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 5, 'כי תראה חמור שנאך רבץ תחת משאו וחדלת מעזב לו עזב תעזב עמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 6, 'לא תטה משפט אביונך בריבו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 7, 'מדבר שקר תרחק ונקיא וצדיק אל תהרג כי לא הצדיק רשע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 8, 'ושחד אל תקח כי השחד יעור עיני פקחים ויסלף דברי צדקים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 9, 'וגר לא תלחצו ואתם ידעתם את נפש הגר כי גרים הייתם בארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 10, 'ושש שנים תזרע את ארצך ואספת את תבואתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 11, 'והשבעית תשמטנה ונטשתה ואכלו אביוני עמך ויתרם תאכל חית השדה כן תעשה לכרמך ולזיתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 12, 'ששת ימים תעשה מעשיך וביום השביעי תשבת למען ינוח עבדך ואמתך כמוך וכל בהמתך והגר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 13, 'וכל אשר אמרתי אליכם תשמרו ושם אלהים אחרים לא תזכר ולא ישמעו על פיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 14, 'שלש רגלים תחג לי בשנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 15, 'את חג המצות תשמר שבעת ימים תאכל מצות כאשר צויתיך למועד חדש האביב כי בו יצאת ממצרים ולא יראו פני ריקם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 16, 'וחג הקציר בכורי מעשיך אשר תזרע בשדה וחג האסף בצאת השנה באספך את מעשיך מן השדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 17, 'שלש פעמים בשנה יראה כל זכורך את פני ארון יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 18, 'לא תזבח על חמץ דם זבחי ולא ילין חלב חגי עד בקר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 19, 'ראשית בכורי אדמתך תביא ביתה יהוה אלהיך לא תבשל גדי בחלב אמו כי עשה זאת כזבח שכח ועברה היא לאלהי יעקב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 20, 'הנה אנכי שלח מלאכי לפניך לשמרך בדרך ולהביאך אל המקום אשר הכנתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 21, 'השמר מפניו ושמע בקולו אל תמרי בו כי לא ישא לפשעכם כי שמי בקרבו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 22, 'כי אם שמע תשמעו בקולי ועשיתם את כל אשר אדבר ואיבתי את איביך וצרתי את צרריך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 23, 'כי ילך מלאכי לפניך והביאך אל הכנעני והאמרי והחתי והגרגשי והפרזי והחוי והיבוסי והכחדתיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 24, 'לא תשתחוה לאלהיהם ולא תעבדם ולא תעשה כמעשיהם כי הרס תהרסם ושבר תשבר מצבתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 25, 'ועבדתם את יהוה אלהיכם וברך את לחמך ואת מימיך והסרתי מחלה מקרבך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 26, 'ולא תהיה משכלה ועקרה בארצך את מספר ימיך אמלא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 27, 'את אימתי אשלח לפניך והמתי את כל העם אשר תבוא בהן ונתתי את כל איביך אליך ערף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 28, 'ושלחתי את הצרעה לפניך וגרשה את הכנעני ואת האמרי ואת החתי ואת הגרגשי ואת הפרזי ואת החוי ואת היבוסי מלפניך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 29, 'לא אגרשנו מפניך בשנה אחת פן תהיה הארץ שממה ורבה עליך חית השדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 30, 'מעט מעט אגרשנו מפניך עד אשר תפרה ונחלת את הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 31, 'ושתי את גבולך מים סוף ועד ים פלשתים וממדבר עד הנהר כי אתן בידכם את ישבי הארץ וגרשתם מפניך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 32, 'לא תכרת להם ולאלהיהם ברית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 23, 33, 'לא ישבו בארצך פן יחטיאו אתך לי כי תעבד את אלהיהם כי יהיו לך למוקש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 24, 1, 'ואל משה אמר עלה אל יהוה אתה ואהרן נדב ואביהוא אלעזר ואיתמר ושבעים מזקני ישראל והשתחויתם מרחק ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 24, 2, 'ונגש משה לבדו אל יהוה והם לא יגשו והעם לא יעלה עמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 24, 3, 'ויבא משה ויספר לעם את כל דברי יהוה ואת כל המשפטים ויענו כל העם קול אחד ויאמרו כל הדברים אשר דבר יהוה נעשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 24, 4, 'ויכתב משה את כל דברי יהוה וישכם בבקר ויבן מזבח תחת ההר ושתים עשרה אבנים לשנים עשר שבטי ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 24, 5, 'וישלח את נערי בני ישראל ויעלו עלות ויזבחו זבחים שלמים ליהוה פרים בני בקר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 24, 6, 'ויקח משה חצי הדם וישם באגנות וחצי הדם זרק על המזבח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 24, 7, 'ויקח ספר הברית ויקרא באזני העם ויאמרו כל אשר דבר יהוה נשמע ונעשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 24, 8, 'ויקח משה את הדם ויזרקהו על העם ויאמר הנה דם הברית אשר כרת יהוה עמכם על כל הדברים האלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 24, 9, 'ויעל משה ואהרן נדב ואביהוא אלעזר ואיתמר ושבעים מזקני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 24, 10, 'ויראו את אלהי ישראל ותחת רגליו כמעשה לבנת הספיר וכעצם השמים לטהור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 24, 11, 'ואל אצלי בני ישראל לא שלח ידו ויחזו את האלהים ויאכלו וישתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 24, 12, 'ויאמר יהוה אל משה עלה אלי ההרה והוי שם ואתן לך את לוחת האבנים התורה והמצוה אשר כתבתי להורתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 24, 13, 'ויקם משה ויהושע משרתו ויעל משה אל הר האלהים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 24, 14, 'ואל הזקנים אמר שבו לנו בזה עד אשר נשוב אליכם הנה אהרן וחור עמכם מי בעל דברים יגש אליהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 24, 15, 'ויעל משה אל ההר ויכס הענן את ההר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 24, 16, 'וישכן כבוד יהוה על הר סיני ויכסהו הענן ששת ימים ויקרא אל משה ביום השביעי מתוך הענן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 24, 17, 'ומראה כבוד יהוה כאש אכלת בראש ההר לעיני בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 24, 18, 'ויבא משה בתוך הענן ויעל אל ההר ויהי משה בהר ארבעים יום וארבעים לילה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 2, 'דבר אל בני ישראל ויקחו לי תרומה מאת כל איש אשר ידבנו לבו תקחו את תרומתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 3, 'וזאת התרומה אשר תקחו מאתם זהב כסף ונחשת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 4, 'ותכלת וארגמן ותולעת שני ושש ועזים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 5, 'ועורת אילים מאדמים ועורת תחשים ועצי שטים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 6, 'ושמן למאור ובשמים לשמן המשחה ולקטרת הסמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 7, 'ואבני שהם ואבני מלאים לאפוד ולחשן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 8, 'ועשו לי מקדש ושכנתי בתוככם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 9, 'ככל אשר אני מראה אתך בהר את תבנית המשכן ואת תבנית כל כליו וכן תעשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 10, 'ועשית ארון עצי שטים אמתים וחצי ארכו ואמה וחצי רחבו ואמה וחצי קומתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 11, 'וצפית אתו זהב טהור מבית ומחוץ תצפנו ועשית לו זר זהב סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 12, 'ויצקת לו ארבע טבעות זהב ונתתה על ארבע פעמתיו ושתי טבעות על צלעו האחת ושתי טבעות על צלעו השנית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 13, 'ועשית בדי עצי שטים וצפית אתם זהב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 14, 'והבאת את הבדים בטבעות על צלעות הארון לשאת את הארון בהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 15, 'בטבעות הארון יהיו הבדים לא יסורו ממנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 16, 'ונתתה אל הארון את העדות אשר אתן אליך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 17, 'ועשית כפרת זהב טהור אמתים וחצי ארכו ואמה וחצי רחבו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 18, 'ועשית שנים כרובים זהב מקשה תעשה אתם משני קצות הכפרת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 19, 'יעשו כרוב אחד מקצה מזה וכרוב אחד מקצה מזה מן הכפרת תעשה את הכרובים על שני קצתיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 20, 'והיו הכרובים פרשי כנפים למעלה סככים בכנפיהם על הכפרת ופניהם אחד אל אחד אל הכפרת יהיו פני הכרובים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 21, 'ונתתה את הכפרת על הארון מלמעלה ואל הארון תתן את העדות . . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 22, 'ונועדתי לך שם ודברתי אתך מעל הכפרת מבין שני הכרובים אשר על ארון העדות את כל אשר אצוה אתך אל בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 23, 'ועשית שלחן עצי שטים אמתים ארכו ואמה רחבו ואמה וחצי קומתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 24, 'וצפית אתו זהב טהור ועשית לו זר זהב סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 25, 'ועשית לו מסגרת טפח סביב ועשית זר זהב למסגרתו סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 26, 'ועשית לו ארבע טבעות זהב ונתתה את הטבעות על ארבע הפאות אשר לארבע רגליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 27, 'לעמת המסגרת תהיינה הטבעות בתים לבדים לשאת את השלחן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 28, 'ועשית את הבדים עצי שטים וצפית אתם זהב ונשאו בהם את השלחן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 29, 'ועשית את קערתיו וכפתיו ומנקיתיו וקשתיו אשר יסכו בהם זהב טהור תעשה אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 30, 'ונתתה על השלחן לחם פנים לפני תמיד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 31, 'ועשית מנורת זהב טהור מקשה תעשה את המנורה ירכיה קניה גביעיה כפתריה ופרחיה ממנה יהיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 32, 'וששה קנים יצאים מצדיה שלשה קני מנורה מצדה האחד ושלשה קני מנורה מצדה השני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 33, 'שלשה גבעים משקדים בקנה האחד כפתר ופרח ושלשה גבעים משקדים בקנה האחד כפתר ופרח כן תעשה לששת הקנים היצאים מן המנורה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 34, 'ובמנורה ארבעה גבעים משקדים כפתריה ופרחיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 35, 'וכפתר תחת שני הקנים ממנה וכפתר תחת שני הקנים ממנה וכפתר תחת שני הקנים ממנה לששת הקנים היוצאים מן המנורה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 36, 'כפתריהם וקנותם ממנה יהיו כלה מקשה אחד זהב טהור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 37, 'ועשית את נרתיה שבעה והעלית את נרתיה והאירו אל עבר פניה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 38, 'ומלקחיה ומחתיתיה זהב טהור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 39, 'ככר זהב טהור תעשה אתה ואת כל הכלים האלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 25, 40, 'וראה ועשה בתבניתם אשר אתה מראה בהר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 1, 'ואת המשכן תעשה עשר יריעות שש משזר תכלת וארגמן ותולעת שני כרובים מעשה חשב תעשה אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 2, 'ארך היריעה האחת שמנה ועשרים באמה ורחב ארבע באמה היריעה האחת מדה אחת לכל היריעות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 3, 'וחמש היריעות תהיינה חברות אחת אל אחת וחמש היריעות תהיינה חברות אחת אל אחת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 4, 'ועשית ללאות תכלת על שפת היריעה האחת בקצה במחברת וכן תעשה בשפת היריעה הקצונה במחברת השנית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 5, 'חמשים ללאות תעשה ביריעה האחת וחמשים ללאות תעשה בקצה היריעה אשר במחברת השנית מקבלות הללאות אחת אל אחת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 6, 'ועשית חמשים קרסי זהב וחברת את היריעות אחת אל אחת בקרסים והיה המשכן אחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 7, 'ועשית יריעות עזים לאהל על המשכן עשתי עשרה יריעות היריעהת אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 8, 'ארך היריעה האחת שלשים באמה וארבע אמות רחב היריעה האחת מדה אחת לעשתי עשרה היריעות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 9, 'וחברת את חמש היריעות לבד ואת שש היריעות לבד וכפלת את היריעה הששית אל מול פני האהל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 10, 'ועשית ללאות חמשים על שפת היריעה האחת הקצונה במחברת וחמשים ללאות תעשה על שפת היריעה החברת השנית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 11, 'ועשית קרסי נחשת חמשים והבאת את הקרסים בללאות וחברת את האהל והיה אחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 12, 'וסרח העדף ביריעת האהל חצי היריעה העדפת תסרח אל אחרי המשכן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 13, 'והאמה מזה והאמה מזה בעדף בארך יריעת האהל יהיה סרוח אל צדי המשכן מזה ומזה לכסותו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 14, 'ועשית מכסה לאהל עורת אילים מאדמים ומכסה עורת תחשים מלמעלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 15, 'ועשית את הקרשים למשכן עצי שטים עמדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 16, 'עשר אמות ארך הקרש האחד אמה וחצי האמה רחב הקרש האחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 17, 'שתי ידות לקרש האחד משלבות אחת אל אחת כן תעשה לכל קרשי המשכן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 18, 'ועשית את הקרשים למשכן עשרים קרשים לפאת נגבה תימנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 19, 'וארבעים אדני כסף תעשה תחת עשרים הקרשים שני אדנים תחת הקרש האחד לשתי ידתיו ושני אדנים תחת הקרש האחד לשתי ידתיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 20, 'ולצלע המשכן השנית לפאת צפונה תעשה עשרים קרשים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 21, 'וארבעים אדניהם כסף שני אדנים תחת הקרש האחד ושני אדנים תחת הקרש האחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 22, 'ולירכתי המשכן ימה תעשה ששה קרשים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 23, 'ושני קרשים תעשה למקצעות המשכן בירכתים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 24, 'והיו תאמים מלמטה יחדו יהיו תאמים אל ראשו אל הטבעת האחת כן יהיה לשניהם לשני המקצעות יהיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 25, 'והיו שמנה קרשים ואדניהם כסף ששה עשר אדנים שני אדנים . . . שני אדנים . לקרש האחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 26, 'ועשית בריחי עצי שטים חמשה לקרשי צלע המשכן האחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 27, 'וחמשה בריחים לקרשי צלע המשכן השנית וחמשה בריחים לקרשי צלע המשכן לירכתים ימה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 28, 'והבריח התיכון בתוך הקרשים מבריח מן הקצה אל הקצה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 29, 'ואת הקרשים תצפה זהב ואת טבעתיהם תעשה זהב בתים לבריחים וצפית את הבריחים זהב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 30, 'והקמת את המשכן כמשפטו אשר הראת בהר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 31, 'ועשית פרכת תכלת וארגמן ותולעת שני ושש משזר מעשה חשב יעשו אתה כרובים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 32, 'ונתתה אתה על ארבעה עמודי שטים מצפים זהב וויהם זהב על ארבעה אדני כסף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 33, 'ונתתה את הפרכת תחת הקרסים והבאת שמה מבית לפרכת את ארון העדות והבדילה הפרכת לכם בין הקדש ובין קדש הקדשים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 34, 'ונתתה את הכפרת על ארון העדות בקדש הקדשים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 35, 'ושמת את השלחן מחוץ לפרכת ואת המנורה נכח השלחן על ירך המשכן תימנה ואת השלחן תתן על צלע צפונה ועשת מזבח מקטר קטרת עצי שטים תעשה אתו אמה ארכו ואמה רחבו רבוע יהיה ואמתים קומתו ממנו קרנתיו וצפית אתו זהב טהור את גגו ואת קירתיו סביב ואת קרנתיו ועשית לו זר זהב סביב ושתי טבעות זהב תעשה לו מתחת לזרו על שתי צלעתיו תעשה על שני צדיו והיו לבתים לבדים לשאת אתו בהם ועשית את הבדים עצי שטים וצפית אתם זהב ונתתה אתו לפני הפרכת אשר על ארון העדות אשר אועד לך שמה והקטיר עליו אהרן קטרת סמים בבקר בבקר בהטיבו את הנרות יקטירנו ובהעלות אהרן את הנרות בין הערבים יקטירנה קטרת תמיד לפני יהוה לדרתיכם לא תעלו עליו קטרת זרה ועלה ומנחה ונסך לא תסכו עליו וכפר אהרן על קרנתיו אחת בשנה מדם חטאת הכפרים אחת בשנה יכפר עליו לדרתיכם קדש קדשים הוא ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 36, 'ועשית מסך לפתח האהל תכלת וארגמן ותולעת שני ושש משזר מעשה רקם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 26, 37, 'ועשית למסך חמשה עמודי שטים וצפית אתם זהב טהור וויהם זהב ויצקת להם חמשה אדני נחשת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 27, 1, 'ועשית . מזבח עצי שטים חמש אמות ארך וחמש אמות רחב רבוע יהיה המזבח ושלש אמות קומתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 27, 2, 'ועשית קרנתיו על ארבע פנתיו ממנו תהיינה קרנתיו וצפית אתו נחשת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 27, 3, 'ועשית סירתיו לדשנו ויעיו ומזרקתיו ומזלגתיו ומחתיתיו לכל כליו תעשה נחשת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 27, 4, 'ועשית לו מכבר מעשה רשת נחשת ועשית על הרשת ארבע טבעות נחשת על ארבע קצתיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 27, 5, 'ונתתה אתו תחת כרכב המזבח מלמטה והיתה הרשת עד חצי המזבח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 27, 6, 'ועשית בדים למזבח בדי עצי שטים וצפית אתם נחשת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 27, 7, 'והבאת את הבדים בטבעות והיו הבדים על שתי צלעות המזבח בשאת אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 27, 8, 'נבוב לוחת תעשה אתו כאשר הראה אתך בהר וכן יעשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 27, 9, 'ועשית את חצר המשכן לפאת נגבה תימנה קלעים לחצר שש משזר מאה באתה ארך לפאה האחת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 27, 10, 'ועמדיו עשרים ואדניהם עשרים נחשת וויהם . וחשקיהם כסף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 27, 11, 'וכן לפאת צפונה בארך קלעים מאה באמה עמודיהם עשרים ואדניהם עשרים נחשת וויהם . וחשקיהם לסף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 27, 12, 'ורחב החצר לפאת יםה קלעים חמשים אמה עמדיהם עשרה ואדניהם עשרה נחשת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 27, 13, 'ורחב החצר לפאת קדמה מזרחה חמשים אמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 27, 14, 'חמש עשרה אמה קלעים לכתף עמדיהם שלשה ואדניהם שלשה נחשת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 27, 15, 'ולפאת השנית חמש עשרה אמה קלעים עמודיהם שלשה ואדניהם שלשה נחשת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 27, 16, 'ולשער החצר מסך עשרים אמה תכלת וארגמן ותולעת שני ושש משזר מעשה רקם עמודיהם ארבעה ואדניהם ארבעה נחשת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 27, 17, 'כל עמודי החצר סביב מחשקים כסף וויהם כסף ואדניהם נחשת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 27, 18, 'ארך החצר מאה באמה ורחב חמשים באמה וקומה חמש אמות שש משזר ואדניהם נחשת ועשית את ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 27, 19, 'כל כלי המשכן בכל עבדתו ובכל יתדתיו וכל יתדות החצר נחשת ועשית בגדי תכלת וארגמן ותולעת שני לשדת בהם בקדש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 27, 20, 'ואתה תצוה את בני ישראל ויקחו אליך שמן זית זך כתית למאור להעלות נר תמיד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 27, 21, 'באהל מועד מחוץ לפרכת אשר על העדות יערך אתו אהרן ובניו מערב עד בקר לפני יהוה חקת עולם לדרתיכם מאת בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 1, 'ואתה הקריב אליך את אהרן אחיך ואת בניו אתו מתוך בני ישראל לכהן לי אהרן נדב ואביהוא אלעזר ואיתמר בני אהרן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 2, 'ועשית בגדי קדש לאהרן אחיך לכבוד ולתפארת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 3, 'ואתה תדבר אל כל חכם לב אשר מלאתיו רוח חכמה ועשו את בגדי אהרן לקדשו לכהן לי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 4, 'ואלה הבגדים אשר יעשון חשן ואפוד ומעיל כיתנת תשבץ מצנפת ואבניט ועשו בגדי קדש לאהרן אחיך ולבניו לכהן לי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 5, 'והם יקחו את הזהב ואת התכלת ואת הארגמן ואת התולעת השני ואת השש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 6, 'ועשו את האפוד זהב תכלת וארגמן ותולעת שני ושש משזר מעשה חשב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 7, 'שתי כתפות חברות יהיה לו על שני קצתיו יחבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 8, 'וחשב אפדתו אשר עליו כמעשהו ממנו יהיה זהב תכלת וארגמן ותולעת שני ושש משזר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 9, 'ולקחת את שתי אבני השהם ופתחת עליהן את שמות בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 10, 'ששה משמתם על האבן האחת ואת שמות הששה הנותרים על האבן השנית כתולדתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 11, 'מעשה חרש אבן פתוחי חתם תפתח את שתי האבנים על שמות בני ישראל מוסבת משבצות זהב תעשה אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 12, 'ושמת את שתי האבנים על כתפות האפוד אבני זכרון הנה לבני ישראל ונשא אהרן את שמותם לפני יהוה על שתי כתפתיו לזכרון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 13, 'ועשית משבצות זהב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 14, 'ושתי שרשרות זהב טהור מגבלות תעשה אתם מעשה עבות ונתתה את שרשרות העבתות על המשבצות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 15, 'ועשית חשן משפט מעשה חשב כמעשה האפוד תעשנו זהב תכלת וארגמן ותולעת שני ושש משזר תעשה אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 16, 'רבוע יהיה כפול זרת ארכו וזרת רחבו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 17, 'ומלאת בו מלאת אבן ארבעה טורים . טור אדם פטדה וברקת הטור האחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 18, 'והטור השני נפך ספיר ויהלם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 19, 'והטור השלישי לשם שבו ואחלמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 20, 'והטור הרביעי תרשיש שהם וישפה וישפה מוסבת משבצות זהב יהיו במלאתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 21, 'והאבנים תהיינה על שמות בני ישראל שתים עשרה על שמותם פתוחי חתם איש על שמו תהיינה לשנים עשר שבט ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 22, 'ועשית על החשן שרשרות גבלות מעשה עבות זהב טהור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 23, 'ועשית על החשן שתי משבצות זהב ושתי טבעות זהב ונתתה את . הטבעות על . קצות החשן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 24, 'ונתתה את שתי עבתות הזהב על שתי הטבעות על קצות החשן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 25, 'ואת שתי הקצות שתי העבתות תתן על שתי המשבצות ונתתה על כתפות האפוד אל מול פניו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 26, 'ועשית שתי טבעות זהב ושמת אתם על שני קצות החשן על שפתו אשר על עבר האפוד ביתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 27, 'ועשית שתי טבעות זהב ונתתה אתם על שתי כתפות האפוד מלמטה ממול פניו לעמת מחברתו ממעל לחשב האפוד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 28, 'וירכסו את החשן מטבעתו אל טבעת האפוד בפתיל תכלת להיות על חשב האפוד ולא יזח החשן מעל האפוד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 29, 'ונשא אהרן את שמות בני ישראל בחשן המשפט על לבו בבאו אל הקדש לזכרון לפני יהוה תמידה ועשית את הארים ואת התמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 30, 'ונתתה על חשן המשפט את הארים ואת התמים והיו על לב אהרן בבאו לפני יהוה ונשא אהרן את משפט בני ישראל על לבו לפני יהוה תמידה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 31, 'ועשית את מעיל האפוד כליל תכלת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 32, 'והיה פי ראשו בתוכו שפה יהיה לפיו סביב מעשה ארג כפי תחרא יהיה לו לא יקרע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 33, 'ועשית על שוליו רמוני תכלת וארגמן ותולעת שני ושש משזר על שוליו סביב ופעמוני זהב בתוכם סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 34, 'פעמון זהב ורמון פעמון זהב ורמון על שולי המעיל סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 35, 'והיה על אהרן לשרת ונשמע קולו בבאו אל הקדש לפני יהוה ובצאתו ולא ימות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 36, 'ועשית ציץ זהב טהור ופתחת עליו פתוחי חתם קדש ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 37, 'ושמת אתו על פתיל תכלת והיה על המצנפת אל מול פני המצנפת יהיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 38, 'והיה על מצח אהרן ונשא אהרן את עון הקדשים אשר יקדישו בני ישראל לכל מתנות קדשיהם והיה על מצחו תמיד לרצון להם לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 39, 'ושבצת את הכתנת שש ועשית מצנפת שש ואבניט תעשה מעשה רקם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 40, 'ולבני אהרן תעשה כיתנת ועשית להם אבנטים ומגבעות תעשה להם לכבוד ולתפארת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 41, 'והלבשת אתם את אהרן אחיך ואת בניו אתו ומשחת אתם ומלאת את ידם וקדשת אתם וכהנו לי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 42, 'ועשה להם מכנסי בד לכסות בשר ערוה ממתנים ועד ירכים יהיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 28, 43, 'והיו על אהרן ועל בניו בבאם אל אהל מועד או בגשתם אל המזבח לשרת בקדש ולא ישאו עון ומתו חקת עולם לו ולזרעו אחריו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 1, 'וזה הדבר אשר תעשה להם לקדש אתם לכהן לי לקח פר אחד בן בקר ואילים שנים תמימם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 2, 'ולחם מצות וחלות מצות בללות בשמן ורקיקי מצות . . סלת חטים תעשה אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 3, 'ונתתה אתם על סל אחד והקרבת אתם בסל ואת הפר ואת שני האילים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 4, 'ואת אהרן ואת בניו תקריב אל פתח אהל מועד ורחצת אתם במים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 5, 'ולקחת את הבגדים והלבשת את אהרן את הכיתנת וחגרת אתו אבניט והלבשת אתו את המעיל ונתתה עליו את האפוד . . ואת החשן ואפדת לו בחשב האפוד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 6, 'ושמת המצנפת על ראשו ונתתה את נזר הקדש על המצנפת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 7, 'ולקחת את שמן המשחה ויצקת על ראשו ומשחת אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 8, 'ואת בניו תקריב והלבשתם כיתנת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 9, 'וחגרת אתם אבנטים אהרן ובניו וחבשת להם מגבעות והיתה להם כהנה לחקת עולם ומלאת יד אהרן ויד בניו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 10, 'והקרבת את הפר לפני יהוה פתח אהל מועד וסמך אהרן ובניו את ידיהם על ראש הפר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 11, 'ושחטת את הפר לפני יהוה פתח אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 12, 'ולקחת מדם הפר ונתתה על קרנות המזבח באצבעך ואת כל הדם תשפך אל יסוד המזבח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 13, 'ולקחת את כל החלב המכסה את הקרב ואת יותרת . הכבד ואת שתי הכליות ואת החלב אשר עליהן והקטרת המזבחה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 14, 'ואת בשר הפר ואת עורו ואת פרשו תשרף באש מחוץ למחנה חטאת היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 15, 'ואת האיל האחד תקח וסמך אהרן ובניו את ידיהם על ראש האיל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 16, 'ושחטת את האיל ולקחת את דמו וזרקת על המזבח סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 17, 'ואת האיל תנתח לנתחיו ורחצת קרבו וכרעיו ונתתה על נתחיו ועל ראשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 18, 'והקטרת את כל האיל המזבחה עלה הוא ליהוה ריח ניחח אשה הוא ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 19, 'ולקחת את האיל השני וסמך אהרן ובניו את ידיהם על ראש האיל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 20, 'ושחטת את האיל ולקחת מדמו ונתתה על תנך אזן אהרן ועל תנך אזן בניו הימנית ועל בהון ידם הימנית ועל בהון רגלם הימנית וזרקת את הדם על המזבח סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 21, '. . . . . . . . . . . . . . . . . . . . . . . . . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 22, 'ולקחת מן האיל החלב את האליה ואת החלב המכסה את הקרב ואת יותרת הכבד ואת שתי הכליות ואת החלב אשר עליהן ואת שוק הימין כי איל מלאים הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 23, 'וככר לחם אחת וחלת לחם שמן אחת ורקיק אחד מסל המצות אשר לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 24, 'ושמת הכל על כפי אהרן ועל כפי בניו והנפת אתם תנופה לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 25, 'ולקחת אתם מידם והקטרת אתם המזבחה על העלה לריח ניחח . . אשה הוא ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 26, 'ולקחת את החזה מאיל המלאים מאשר לאהרן והנפת אתו תנופה לפני יהוה והיה לך למנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 27, 'וקדשת את חזה התנופה ואת שוק התרומה אשר הונף ואשר הורם מאיל המלאים מאשר לאהרן ומאשר לבניו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 28, 'והיה לאהרן ולבניו לחק עולם מאת בני ישראל כי תרומה הוא ותרומה יהיה מאת בני ישראל מזבחי שלמיהם תרומתם ליהוה ולקחת משמן המשחה ומן הדם אשר על המזבח והזית על אהרן ועל בגדיו ועל בניו ועל בגדי בניו אתו וקדשתו ואת בגדיו ואת בניו ואת בגדי בניו אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 29, 'ובגדי הקדש אשר לאהרן יהיו לבניו אחריו למשחה בם ולמלא בם את ידם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 30, 'שבעת ימים ילבשם הכהן תחתיו מבניו אשר יבוא אל אהל מועד לשרת בקדש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 31, 'ואת איל המלאים תקח ובשלת את בשרו במקום קדוש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 32, 'ואכל אהרן ובניו את בשר האיל ואת הלחם אשר בסל פתח אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 33, 'יאכלו אתם אשר כפר בם למלא בם את ידם לקדש אתם וזר לא יאכל כי קדש הם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 34, 'ואם יותר מבשר המלאים ומן הלחם עד הבקר ושרפת את הנותר באש לא יאכל כי קדש הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 35, 'ועשית לאהרן ולבניו ככה ככל אשר צויתי אתך שבעת ימים תמלא את ידם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 36, 'ופר חטאת תעשה ליום על הכפרים וחטאת על המזבח בכפרך עליו ומשחת אתו לקדשו ');
