INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 38, 'וזה אשר תעשה על המזבח כבשים בני שנה שנים ליום תמיד עלת תמיד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 39, 'את הכבש האחד תעשה בבקר ואת הכבש השני תעשה בין הערבים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 40, 'ועשרון סלת בלול בשמן כתית רבעית ההין ונסך רבעית ההין יין לכבש האחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 41, 'ואת הכבש השני תעשה בין הערבים כמנחת הבקר וכנסכו תעשה לו ריח ניחח אשה ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 29, 42, 'עלת תמיד לדרתיכם פתח אהל מועד לפני יהוה אשר אועד לך שמה לדבר אליך שם ');
