INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 30, 32, 'על בשר אדם לא יוסך ובתכנתו לא תעשו כמהו קדש הוא וקדש יהיה לכם ');
