INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 30, 35, 'ועשית אתו קטרת רקח מעשה רקח ממלחת טהור קדש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 30, 36, 'ושחקת ממנו הדק ונתתה ממנו לפני העדות באהל מועד אשר אועד לך שמה קדש קדשים תהיה לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 30, 37, 'והקטרת אשר תעשה במתכנתה לא תעשו לכם קדש תהיה לכם ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 30, 38, 'איש אשר יעשה כמוה להריח בה ונכרת מעמיו ');
