INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 31, 14, 'ושמרתם את השבת כי קדש . היא לכם מחלליה מות יומת כי כל העשה בה מלאכה ונכרתה הנפש ההיא מקרב עמיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 31, 15, 'ששת ימים יעשה מלאכה וביום השביעי שבת שבתון קדש ליהוה כל העשה מלאכה ביום השבת מות יומת ');
