INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 31, 17, 'ביני ובין בני ישראל אות היא לעלם כי ששת ימים עשה יהוה את השמים ואת הארץ וביום השביעי שבת וינפש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 31, 18, 'ויתן אל משה ככלתו לדבר אתו בהר סיני את שני לוחת העדות לוחת אבנים כתבים באצבע אלהים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 1, 'וירא העם כי בשש משה לרדת מן ההר ויקהל העם על אהרן ויאמרו אליו קום עשה לנו אלהים אשר ילכו לפנינו כי זה משה האיש אשר העלנו מארץ מצרים לא ידענו מה היה לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 2, 'ויאמר אליהם אהרן פרקו את נזמי הזהב אשר באזני נשיכם בניכם ובנתיכם והביאו אלי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 3, 'ויתפרקו כל העם את נזמי הזהב אשר באזניהם ויביאו אל אהרן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 4, 'ויקח מידם ויצר אתו בחרט ויעשהו עגל מסכה ויאמרו אלה אלהיך ישראל אשר העלוך מארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 5, 'וירא אהרן ויבן מזבח לפניו ויקרא אהרן ויאמר חג ליהוה מחר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 6, 'וישכמו ממחרת ויעלו עלות ויגשו שלמים וישב העם לאכל ושתו ויקמו לצחק ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 7, 'וידבר יהוה אל משה לאמר לך רד כי שחת עמך אשר העלית מארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 8, 'סרו מהר מן הדרך אשר צויתם עשו להם עגל מסכה וישתחוו לו ויזבחו לו ויאמרו אלה אלהיך ישראל אשר העלוך מארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 9, 'ויאמר יהוה אל משה ראיתי את העם הזה והנה עם קשה ערף הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 10, 'ועתה הניחה לי ויחר אפי בם ואכלם ואעשה אתך לגוי גדול ובאהרן התאנף יהוה מאד להשמידו ויתפלל משה בעד אהרן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 11, 'ויחל משה את פני יהוה אלהיו ויאמר למה יהוה יחר אפך בעמך אשר הוצאת . ממצרים בכח גדול ובזרוע נטויה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 12, 'למה יאמרו מצרים לאמר ברעה הוציאם להרג אתם בהרים ולכלותם מעל פני האדמה שוב מחרון אפך והנחם על הרעה לעמך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 13, 'זכר לאברהם ליצחק וליעקב עבדיך אשר נשבעת להם בך ותדבר אליהם הרבה ארבה את זרעכם ככוכבי השמים וכל הארץ הזאת אשר אמרתי אתן לזרעכם ונחלוה לעלם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 14, 'וינחם יהוה על הרעה אשר דבר לעשות לעמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 15, 'ויפן וירד משה מן ההר ושני לוחת העדות בידו לוחת כתבים משני עבריהם מזה ומזה הם כתבים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 16, 'והלוחת מעשה אלהים הם והמכתב מכתב אלהים הוא חרות על הלוחת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 17, 'וישמע יהושע את קול העם ברעה ויאמר אל משה קול מלחמה במחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 18, 'ויאמר אין קול ענות גברה ואין קול ענות חלושה קול ענות אנכי שמע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 19, 'ויהי כאשר קרב אל המחנה וירא את העגל ואת המחלות ויחר אף משה וישלך מידיו את הלוחות וישבר אתם בתחתית ההר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 20, 'ויקח את העגל אשר עשו וישרפהו באש ויטחנהו עד אשר דק ויזר על פני המים וישק את בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 21, 'ויאמר משה אל אהרן מה עשה לך העם הזה כי הבאת עליו חטאה גדלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 22, 'ויאמר אהרן אל יחר אף אדני אתה ידעת את העם כי פרוע הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 23, 'ויאמרו לי עשה לנו אלהים אשר ילכו לפנינו כי זה משה האיש אשר העלנו מארץ מצרים לא ידענו מה היה לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 24, 'ואמר להם למי זהב התפרקו ויתנו לי ואשליכהו באש ויצא העגל הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 25, 'וירא משה את העם כי פרוע הוא כי פרעו אהרן לשמצו בקומיהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 26, 'ויעמד משה בשער המחנה ויאמר מי ליהוה אלי ויאספו אליו כל בני לוי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 27, 'ויאמר להם כה אמר יהוה אלהי ישראל שימו איש חרבו על ירכו ועברו ושובו משער לשער במחנה והרגו איש את אחיו ואיש את רעהו ואיש את קרבו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 28, 'ויעשו בני לוי כדבר משה ויפל מן העם ביום ההוא כשלשת אלפי איש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 29, 'ויאמר משה מלאו ידיכם היום ליהוה כי איש בבנו ובאחיו ולתת עליכם היום ברכה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 30, 'ויהי ממחרת ויאמר משה אל העם אתם חטאתם חטאה גדלה ועתה אעלה אל יהוה אולי אכפר בעד חטאתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 31, 'וישב משה אל יהוה ויאמר הנה חטא העם הזה חטאה גדלה ויעשו להם אלהי זהב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 32, 'ועתה אם תשא חטאתם שא ואם אין מחני . מספרך אשר כתבת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 33, 'ויאמר יהוה אל משה מי אשר חטא לי אמחנו מספרי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 34, 'ועתה לך נחה את העם על אשר דברתי לך והנה מלאכי ילך לפניך וביום פקודי ופקדתי עליהם חטאתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 32, 35, 'ויגף יהוה את העם על אשר עשו את העגל אשר עשה אהרן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 1, 'וידבר יהוה אל משה לאמר לך עלה מזה אתה והעם אשר העלית מארץ מצרים אל הארץ אשר נשבעתי לאברהם ליצחק וליעקב לאמר לזרעך אתננה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 2, 'ושלחתי לפניך מלאך וגרשתי את הכנעני והאמרי והחתי והגרגשי והפרזי והחוי והיבוסי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 3, 'אל ארץ זבת חלב ודבש כי לא אעלה בקרבך כי עם קשה ערף אתה פן אכלך בדרך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 4, 'וישמע העם את הדבר הרע הזה ויתאבלו ולא שתו איש עדיו עליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 5, 'ויאמר יהוה אל משה אמר אל בני ישראל אתם עם קשה ערף רגע אחד אעלה בקרבך וכליתך ועתה הוריד עדיך מעליך ואדעה מה אעשה לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 6, 'ויתנצלו בני ישראל את עדים מהר חורב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 7, 'ומשה יקח את האהל ונטה לו מחוץ למחנה הרחק מן המחנה וקרא לו אהל מועד והיו כל מבקשי יהוה יצאו אל אהל מועד אשר מחוץ למחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 8, 'והיה כצאת משה אל האהל יקומו כל העם ונצבו איש פתח אהלו והביטו אחרי משה עד באו האהלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 9, 'והיה כבוא משה האהל ירד עמוד הענן ועמד פתח האהל ודבר עם משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 10, 'וראו כל העם את עמוד הענן עמד פתח האהל וקםו כל העם והשתחוו איש פתח אהלו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 11, 'ודבר יהוה אל משה פנים אל פנים כאשר ידבר איש אל רעהו ושב אל המחנה ומשרתו יהושע בן נון נער לא ימוש מתוך האהל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 12, 'ויאמר משה אל יהוה ראה אתה אמר אלי העל את העם הזה ואתה לא הודעתני את אשר תשלח עמי ואתה אמרת ידעתיך בשם וגם מצאת חן בעיני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 13, 'ועתה אם נא מצאתי חן בעיניך הודעני נא את דרכיך ואדעך למען אמצא חן בעיניך וראה כי עמך הגוי הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 14, 'ויאמר פני ילכו והנחתי לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 15, 'ויאמר אליו אם אין פניך הלכים אל תעלנו מזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 16, 'ובמה יודע אפו כי מצאתי חן בעיניך אני ועמך הלוא בלכתך עמנו ונפלנו אני ועמך מכל העם אשר על פני האדמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 17, 'ויאמר יהוה אל משה גם את הדבר הזה אשר דברת אעשה כי מצאת חן בעיני ואדעך בשם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 18, 'ויאמר הראני נא את כבודך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 19, 'ויאמר אני אעביר כל טובי על פניך וקראתי בשם יהוה לפניך וחנתי את אשר אחן ורחמתי את אשר ארחם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 20, 'ויאמר לא תוכל לראות . פני כי לא יראני האדם וחי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 21, 'ויאמר יהוה הנה מקום אתי ונצבת על הצור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 22, 'והיה בעבר כבודי ושמתיך בנקירות הצור ושכתי כפי עליך עד עברי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 33, 23, 'והסרתי . כפי וראית את אחרי ופני לא יראו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 1, 'ויאמר יהוה אל משה פסל לך שני לוחת אבנים כראישונים וכתבתי על הלוחות את הדברים אשר היו על הלוחות הראישונים אשר שברת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 2, 'והוי נכון לבקר ועלית בבקר אל הר סיני ונצבת לי שם על ראש ההר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 3, 'ואיש לא יעלה עמך וגם איש אל יראה בכל ההר וגם הצאן ובקר אל ירעו אל מול ההר ההוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 4, 'ויפסל משה שני לוחת אבנים כראישונים וישכם . בבקר ויעל אל הר סיני כאשר צוה יהוה אתו ויקח בידו שני לוחת אבנים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 5, 'וירד יהוה בענן ויתיצב עמו שם ויקרא בשם יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 6, 'ויעבר יהוה על פניו ויקרא יהוה יהוה אל רחום וחנון ארך אפים ורב חסד ואמת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 7, 'נצר חסד לאלפים נשא עון ופשע וחטאה ונקה לו ינקה פקד עון אבות על בנים ועל בני בנים על שלשים ועל רבעים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 8, 'וימהר משה ויקד ארצה וישתחוי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 9, 'ויאמר אם נא מצאתי חן בעיניך אדני ילך נא אדני בקרבנו כי עם קשה ערף הוא וסלחת לעוננו ולחטאתנו ונחלתנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 10, 'ויאמר . . . הנה אנכי כרת ברית נגד כל עמך אעשה נפלאות אשר לא נבראו בכל הארץ ובכל הגוים וראה כל העם אשר אתה בקרבו את מעשה יהוה כי נורא הוא אשר אני עשה עמך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 11, 'שמור לך את אשר אנכי מצוך היום הנני גרש מפניך את הכנעני והאמרי והחתי והגרגשי והפרזי והחוי והיבוסי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 12, 'השמר לך פן תכרת ברית ליושב הארץ אשר אתה בא עליו פן יהיה למוקש בקרבך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 13, 'כי את מזבחתיהם תתצון ואת מצבתיהם תשברון . ואשריהם תכרתון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 14, 'כי לא תשתחוה לאל אחר כי יהוה קנא שמו אל קנא הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 15, 'פן תכרת ברית ליושב הארץ וזנו אחרי אלהיהם וזבחו לאלהיהם וקרא לך ואכלת מזבחו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 16, 'ולקחת מבנתיו לבניך וזנו בנתיו אחרי אלהיהן והזנו את בניך אחרי אלהיהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 17, 'אלהי מסכה לא תעשה לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 18, 'את חג המצות תשמר שבעת ימים תאכל מצות כאשר צויתך למועד חדש האביב כי בו . יצאת ממצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 19, 'כל פטר רחם לי וכל מקניך תזכר פטר שור ושה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 20, 'ופטר חמור תפדה בשה ואם לא תפדנו וערפתו וכל בכור אדם בבניך תפדה ולא יראו פני ריקם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 21, 'ששת ימים תעבד וביום השביעי תשבת בחריש ובקציר תשבת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 22, 'וחג שבעות תעשה לך בכורי קציר חטים וחג האסף תקופת השנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 23, 'שלש פעמים בשנה יראה כל זכורך את פני האדון יהוה אלהי ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 24, 'כי אוריש גוים רבים מפניך והרחבתי את גבולך ולא יחמד איש את ארצך בעלותך לראות את פני יהוה אלהיך שלש פעמים בשנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 25, 'לא תשחט על חמץ דם זבחי ולא ילין לבקר זבח חג הפסח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 26, 'ראשית בכורי אדמתך תביא ביתה יהוה אלהיך לא תבשל גדי בחלב אמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 27, 'ויאמר יהוה אל משה כתב לך את הדברים האלה כי על פי הדברים האלה כרתי אתך ברית ואת ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 28, 'ויהי שם לפני יהוה ארבעים יום וארבעים לילה לחם לא אכל ומים לא שתה ויכתב על הלוחת את דברי הברית עשרת הדברים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 29, 'ויהי ברדת משה מהר סיני ושני לוחת העדות בידו . ברדתו מן ההר ומשה לא ידע כי קרן עור פניו בדברו אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 30, 'וירא אהרן וכל בני ישראל את משה והנה קרן עור פניו וייראו מגשת אליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 31, 'ויקרא אליהם משה וישבו אליו אהרן וכל הנשיאים בעדה וידבר משה אליהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 32, 'ואחרי כן נגשו אליו כל בני ישראל ויצום את כל אשר דבר יהוה אתו בהר סיני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 33, 'ויכל משה מדבר אתם ויתן על פניו מסוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 34, 'ובבא משה לפני יהוה לדבר אתו יסיר את המסוה עד צאתו ויצא ודבר אל בני ישראל את בל אשר יצוהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 34, 35, 'וראו בני ישראל את פני משה כי קרן עור פני משה והשיב . את המסוה על פניו עד באו לדבר אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 1, 'ויקהל משה את כל עדת בני ישראל ויאמר אליהם אלה הדברים אשר צוה יהוה לעשות אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 2, 'ששת ימים יעשה מלאכה וביום השביעי יהיה לכם קדש שבת שבתון ליהוה כל העשה בו מלאכה יומת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 3, 'לא תבעירו אש בכל מושבתיכם ביום השבת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 4, 'ויאמר משה אל כל עדת בני ישראל לאמר זה הדבר אשר צוה יהוה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 5, 'קחו מאתכם תרומה ליהוה כל נדיב לבו יביא את תרומת יהוה זהב כסף ונחשת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 6, 'תכלת וארגמן ותולעת שני ושש ועזים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 7, 'ועורת אילם מאדמים ועורת תחשים ועצי שטים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 8, 'ושמן למאור ובשמים לשמן המשחה ולקטרת הסמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 9, 'ואבני שהם ואבני מלאים לאפוד ולחשן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 10, 'וכל חכם לב בכם יבוא ועשה את כל אשר צוה יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 11, 'את המשכן ואת אהלו ואת מכסהו ואת קרסיו ואת קרשיו ואת בריחיו ואת עמדיו ואת אדניו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 12, 'את הארון ואת בדיו ואת הכפרת ואת פרכת המסך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 13, 'את השלחן ואת בדיו ואת כל כליו ואת לחם הפנים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 14, 'את מנורת המאור ואת כל כליה . . ואת שמן המאור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 15, 'את מזבח הקטרת ואת בדיו ואת שמן המשחה ואת קטרת הסמים ואת מסך הפתח לפתח המשכן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 16, 'את מזבח העלה ואת מכבר הנחשת אשר לו ואת בדיו ואת כל כליו את הכיור ואת כנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 17, 'את קלעי החצר ואת עמודיה ואת אדניה ואת מסך שער החצר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 18, 'את יתדות המשכן ואת יתדות החצר ואת מיתריהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 19, 'את בגדי השרד לשרת בקדש ואת בגדי הקדש לאהרן הכהן ואת בגדי בניו לכהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 20, 'ויצאו כל עדת בני ישראל מלפני משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 21, 'ויביאו כל איש אשר נשאו לבו וכל איש אשר נדבה רוחו אתו הביאו את תרומת יהוה למלאכת אהל מועד ולכל עבדתו ולבגדי הקדש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 22, 'ויביאו האנשים על הנשים כל נדיב לב הביא חח ונזם טבעת עגיל וכומז כל כלי זהב וכל . אשר הניף תנופת זהב ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 23, 'וכל איש אשר נמצא אתו תכלת וארגמן ותולעת שני ושש ועזים ועורת אילים מאדמים ועורת תחשים הביאו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 24, 'וכל מרים תרומת כסף ונחשת הביאו את תרומת יהוה וכל אשר נמצא אתו עצי שטים . למלאכת העבדה הביאו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 25, 'וכל אשה חכמת לב בידה טוה ויביאו מטוה את התכלת ואת הארגמן ואת התולעת השני ואת השש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 26, 'וכל הנשים אשר נשא לבהן אתהן בחכמה טוו את העזים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 27, 'והנשיאים הביאו את אבני השהם ואת אבני המלאים לאפוד ולחשן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 28, 'ואת הבשםים ואת שמן המאור ולשמן המשחה ולקטרת הסמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 29, 'וכל איש ואשה אשר נדב אתם לבם להביא לכל המלאכה אשר צוה יהוה לעשות ביד משה הביאו בני ישראל נדבה ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 30, 'ויאמר משה . לבני ישראל ראו קרא יהוה בשם בצלאל בן אורי בן חור למטה יהודה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 31, 'וימלא אתו רוח אלהים בחכמה ובתבונה ובדעת ובכל מלאכה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 32, 'ולחשב מחשבות לעשות בזהב ובכסף ובנחשת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 33, 'ובחרשת אבן למלאת ובחרשת עץ לעשות בכל מלאכת מחשבות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 34, 'ולהורות נתן בלבו הוא ואהליאב בן אחיסמך למטה דן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 35, 35, 'מלא אתם חכמת לב לעשות כל מלאכת חרש וחשב ורקם בתכלת ובארגמן ובתולעת השני ובשש וארג עשו כל המלאכה וחשבי מחשבות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 1, 'ועשה בצלאל ואהליאב וכל איש חכם לב אשר נתן יהוה בהם חכמה ותבונה . לדעת לעשות את כל מלאכת עבדת הקדש לכל אשר צוה יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 2, 'ויקרא משה אל בצלאל ואל אהליאב ואל כל איש חכם לב אשר נתן יהוה חכמה בלבו כל אשר נשאו לבו לקרב אל המלאכה לעשות אתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 3, 'ויקחו מלפני משה את כל התרומה אשר הביאו בני ישראל למלאכת עבדת הקדש לעשות אתה והם הביאו אליו עוד נדבה בבקר בבקר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 4, 'ויבאו כל החכמים העשים את כל מלאכת הקדש איש איש ממלאכתו אשר הם עשים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 5, 'וידברו אל משה לאמר מרבים העם להביא מדי העבדה למלאכה אשר צוה יהוה לעשות אתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 6, 'ויצו משה ויעברו קול במחנה לאמר איש ואשה אל יעשה עוד מלאכה לתרומת הקדש ויכל העם מהביא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 7, 'והמלאכה היתה דים לכל המלאכה לעשות אתה והותירו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 8, 'ויעשו כל חכמי לב בעשי המלאכה את המשכן עשו עשר יריעות שש משזר תכלת וארגמן ותולעת שני כרובים מעשה חשב עשה אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 9, 'ארך היריעה האחת שמנה ועשרים באמה ורחב ארבע באמה היריעה האחת מדה אחת לכל היריעות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 10, 'ויחבר את חמש היריעות אחת אל אחת וחמש היריעות חבר אחת אל אחת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 11, 'ויעש ללאות תכלת על שפת היריעה האחת בקצה במחברת וכן עשה בשפת היריעה הקצונה במחברת השנית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 12, 'חמשים ללאות עשה ביריעה האחת וחמשים ללאות עשה בקצה היריעה אשר במחברת השנית מקבלות הללאות אחת אל אחת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 13, 'ויעש חמשים קרסי זהב ויחבר את היריעות אחת אל אחת בקרסים ויהי המשכן אחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 14, 'ויעש יריעות עזים לאהל על המשכן עשתי עשרה יריעות עשה אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 15, 'ארך היריעה האחת שלשים באמה וארבע אמות רחב היריעה האחת מדה אחת לעשתי עשרה היריעות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 16, 'ויחבר את חמש היריעות לבד ואת שש היריעות לבד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 17, 'ויעש ללאות חמשים על שפת היריעה הקצונה במחברת וחמשים ללאות עשה על שפת היריעה החברת השנית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 18, 'ויעש קרסי נחשת חמשים לחבר את האהל להיות אחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 19, 'ויעש מכסה לאהל עורת אילים מאדמים ומכסה עורת תחשים מלמעלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 20, 'ויעש את הקרשים למשכן עצי שטים עמדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 21, 'עשר אמות ארך הקרש האחד אמה וחצי האמה רחב הקרש האחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 22, 'שתי ידות לקרש האחד משלבות אחת אל אחת כן עשה לכל קרשי המשכן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 23, 'ויעש את הקרשים למשכן עשרים קרשים לפאת נגבה תימנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 24, 'וארבעים אדני כסף עשה תחת עשרים הקרשים שני אדנים תחת הקרש האחד לשתי ידתיו ושני אדנים תחת הקרש האחד לשתי ידתיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 25, 'ולצלע המשכן השנית לפאת צפונה עשה עשרים קרשים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 26, 'וארבעים אדניהם כסף שני אדנים תחת הקרש האחד ושני אדנים תחת הקרש האחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 27, 'ולירכתי המשכן ימה עשה ששה קרשים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 28, 'ושני קרשים עשה למקצעות המשכן בירכתים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 29, 'והיו תאמים מלמטה יחדו היו תאמים אל ראשו אל הטבעת האחת כן עשה לשניהם לשני המקצעות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 30, 'והיו שמנה קרשים ואדניהם כסף ששה עשר אדנים שני אדנים שני אדנים תחת הקרש האחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 31, 'ויעשו בריחי עצי שטים חמשה לקרשי צלע המשכן האחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 32, 'וחמשה בריחים לקרשי צלע המשכן השנית וחמשה בריחים לקרשי צלע המשכן לירכתים ימה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 33, 'ויעש את הבריח התיכון לברח בתוך הקרשים מן הקצה אל הקצה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 34, 'ואת הקרשים צפה זהב ואת טבעתיהם עשה זהב בתים לבריחים ויצף את הבריחים זהב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 35, 'ויעש . פרכת תכלת וארגמן ותולעת שני ושש משזר מעשה חשב עשה אתה כרזבים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 36, 'ויעש לה ארבעה עמודי שטים ויצפם זהב וויהם זהב ויצק להם ארבעה אדני כסף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 37, 'ויעש מסך לפתח האהל תכלת וארגמן ותולעת שני ושש משזר מעשה רקם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 36, 38, 'את עמודיו חמשה ואת וויהם וצפה ראשיהם וחשקיהם זהב ואדניהם חמשה נחשת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 1, 'ויעש בצלאל את הארון עצי שטים אמתים וחצי ארכו ואמה וחצי רחבו ואמה וחצי קומתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 2, 'ויצפהו זהב טהור מבית ומחוץ ויעש לו זר זהב סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 3, 'ויצק לו ארבע טבעות זהב על ארבע פעמתיו ושתי טבעות על צלעו האחת ושתי טבעות על צלעו השנית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 4, 'ויעש בדי עצי שטים ויצף אתם זהב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 5, 'ויבא את הבדים בטבעות על צלעות הארון לשאת את הארון בהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 6, 'ויעש כפרת זהב טהור אמתים וחצי ארכו ואמה וחצי רחבו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 7, 'ויעש שני כרובים זהב מקשה עשה אתם משני קצות הכפרת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 8, 'כרוב אחד מקצה מזה וכרוב אחד מקצה מזה מן הכפרת עשה את הכרובים משני קצתיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 9, 'והיו הכרובים פרשי כנפים למעלה סככים בכנפיהם על הכפרת ופניהם אחד אל אחד אל הכפרת היו פני הכרובים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 10, 'ויעש את השלחן עצי שטים אמתים ארכו ואמה רחבו ואמה וחצי קומתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 11, 'ויצף אתו זהב טהור ויעש לו זר זהב סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 12, 'ויעש לו מסגרת טפח סביב ויעש זר זהב למסגרתו סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 13, 'ויצק . ארבע טבעות זהב ויתן את הטבעות על ארבע הפאות אשר . לארבע רגליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 14, 'לעמת המסגרת היו הטבעות בתים לבדים לשאת את השלחן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 15, 'ויעש את הבדים עצי שטים ויצף אתם זהב לשאת את השלחן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 16, 'ויעש את הכלים אשר על השלחן את קערתיו ואת כפתיו ואת מנקיתיו ואת הקשות אשר יוסך בהם זהב טהור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 17, 'ויעש את המנורה זהב טהור מקשה עשה את המנורה ירכיה קניה גביעיה כפתריה ופרחיה ממנה היו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 18, 'וששה קנים יצאים מצדיה שלשה קני מנורה מצדה האחד ושלשה קני מנורה מצדה השני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 19, 'שלשה גבעים משקדים בקנה האחד כפתר ופרח ושלשה גבעים משקדים בקנה האחד כפתר ופרח כן לששת הקנים היצאים מן המנורה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 20, 'ובמנורה ארבעה גבעים משקדים כפתריה ופרחיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 21, 'וכפתר תחת שני הקנים ממנה וכפתר תחת שני הקנים ממנה וכפתר תחת שני הקנים ממנה לששת הקנים היצאים ממנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 22, 'כפתריהם וקנותם ממנה היו כלה מקשה אחד זהב טהור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 23, 'ויעש את נרתיה שבעה ומלקחיה ומחתיתיה זהב טהור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 24, 'ככר זהב טהור עשה אתה ואת כל כליה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 25, 'ויעש את מזבח הקטרת עצי שטים אמה ארכו ואמה רחבו רבוע ואמתים קומתו ממנו היו קרנתיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 26, 'ויצף אתו זהב טהור את גגו ואת קירתיו סביב ואת קרנתיו ויעש לו זר זהב סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 27, 'ושתי טבעות זהב עשה לו מתחת לזרו על שתי צלעתיו על שני צדיו לבתים לבדים לשאת אתו בהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 28, 'ויעש את הבדים עצי שטים ויצף אתם זהב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 37, 29, 'ויעש את שמן המשחה קדש ואת קטרת הסמים טהור מעשה רקח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 1, 'ויעש את מזבח העלה עצי שטים חמש אמות ארך וחמש אמות רחב . ושלש אמות קומתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 2, 'ויעש קרנתיו על ארבע פנתיו ממנו היו קרנתיו ויצף אתו נחשת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 3, 'ויעש את כל כלי המזבח את הסירות ואת היעים ואת המזרקות ואת המזלגות ואת המחתות כל כליו עשה נחשת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 4, 'ויעש למזבח מכבר מעשה רשת נחשת תחת כרכבו מלמטה עד חציו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 5, 'ויצק ארבע טבעות בארבע הקצות למכבר הנחשת בתים לבדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 6, 'ויעש את הבדים עצי שטים ויצף אתם נחשת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 7, 'ויבא את הבדים בטבעות על צלעות המזבח לשאת אתו בהם נבוב לוחת עשה אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 8, 'ויעש את הכיור נחשת ואת כנו נחשת במראת הצבאת אשר צבאו פתח אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 9, 'ויעש את החצר לפאת נגבה תימנה קלעי החצר שש משזר מאה באמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 10, 'עמודיהם עשרים ואדניהם עשרים . ווי העמודים וחשקיהם כסף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 11, 'ולפאת צפונה מאה באמה עמודיהם עשרים ואדניהם עשרים נחשת ווי העמודים וחשקיהם כסף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 12, 'ולפאת ימה קלעים חמשים אמה עמודיהם עשרה ואדניהם עשרה ווי העמודים וחשקיהם כסף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 13, 'ולפאת קדמה מזרחה חמשים אמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 14, 'קלעים חמש עשרה אמה אל הכתף עמודיהם שלשה ואדניהם שלשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 15, 'ולכתף השנית מזה ומזה לשער החצר קלעים חמש עשרה אמה עמודיהם שלשה ואדניהם שלשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 16, 'כל קלעי החצר סביב שש משזר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 17, 'והאדנים לעמודים נחשת וויהם . . כסף וצפוי ראשיהם כסף והם מחשקים כסף לכל עמודי החצר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 18, 'ומסך שער החצר מעשה רקם תכלת וארגמן ותולעת שני ושש משזר ועשרים אמה ארך וקומה ברחב חמש אמות לעמת קלעי החצר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 19, 'ועמודיהם ארבעה ואדניהם ארבעה נחשת וויהם כסף וצפוי ראשיהם וחשקיהם כסף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 20, 'וכל יתדות למשכן ולחצר סביב נחשת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 21, 'אלה פקדי המשכן משכן העדות אשר פקד על פי משה עבדת הלוים ביד איתמר בן אהרן הכהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 22, 'ובצלאל בן אורי בן חור למטה יהודה עשה את כל אשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 23, 'ואתו אהליאב בן אחיסמך למטה דן חרש וחשב ורקם בתכלת ובארגמן ובתולעת השני ובשש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 24, 'כל זהב העשוי למלאכה בכל מלאכת הקדש ויהי זהב התנופה תשע ועשרים ככר ושבע מאות ושלשים שקל בשקל הקדש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 25, 'וכסף פקדי העדה מאת ככר ואלף ושבע מאות חמשה ושבעים שקל . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 26, 'בקע לגלגלת מחצית השקל בשקל הקדש לכל העבר על הפקדים מבן עשרים שנה ומעלה לשש מאות אלף ושלשת אלפים וחמש מאות וחמשים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 27, 'ויהי מאת ככר הכסף לצקת את אדני הקדש ואת אדני הפרכת מאת האדנים למאת הככר ככר לאדן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 28, 'ואת האלף ושבע המאות חמשה ושבעים עשה ווים לעמודים וצפה ראשיהם וחשק אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 29, 'ונחשת התנופה שבעים ככר ואלפים וארבע מאות שקל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 30, 'ויעש בה את אדני פתח אהל מועד ואת מזבח הנחשת ואת מכבר הנחשת אשר למזבח ואת כל כלי המזבח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 38, 31, 'ואת אדני החצר סביב ואת אדני שער החצר ואת כל יתדות המשכן ואת כל יתדות החצר סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 1, 'ומן התכלת והארגמן ותולעת השני עשו בגדי שרד לשרת בקדש ויעשו את בגדי הקדש אשר לאהרן כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 2, 'ויעשו את האפוד זהב תכלת וארגמן ותולעת שני ושש משזר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 3, 'וירקעו את פחי הזהב וקצצו פתלים לעשות בתוך התכלת ובתוך הארגמן ובתוך התולעת השני ובתוך השש מעשה חשב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 4, 'כתפות עשה לו חברות על שני קצתיו חבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 5, 'וחשב אפדתו אשר עליו ממנו הוא כמעשהו זהב תכלת וארגמן ותולעת שני ושש משזר כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 6, 'ויעשו את אבני השהם מוסבת משבצות זהב מפתחות פתוחי חותם על שמות בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 7, 'וישמו אתם על כתפות האפוד אבני זכרון לבני ישראל כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 8, 'ויעשו את החשן מעשה חשב כמעשה האפוד זהב תכלת וארגמן ותולעת שני ושש משזר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 9, 'רבוע היה כפול עשה את החשן זרת ארכו וזרת רחבו . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 10, 'וימלאו בו ארבעה טורי אבן טור אדם פטדה וברקת הטור האחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 11, 'והטור השני נפך ספיר ויהלם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 12, 'והטור השלישי לשם שבו ואחלמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 13, 'והטור הרביעי תרשיש שהם וישפה מוסבת משבצות זהב במלאתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 14, 'והאבנים על שמות בני ישראל הנה שתים עשרה על שמותם פתוחי חותם איש על שמו לשנים עשר שבט ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 15, 'ויעשו על החשן שרשרות גבלות מעשה עבות זהב טהור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 16, 'ויעשו שתי משבצות זהב ושתי טבעות זהב ויתנו את שתי הטבעות על שני קצות החשן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 17, 'ויתנו את שתי עבתות הזהב על שתי הטבעות על קצות החשן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 18, 'ואת שתי הקצות שתי העבתות נתנו על שתי המשבצות ויתנם על כתפות האפוד אל מול פניו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 19, 'ויעשו שתי טבעות זהב וישמו על שני קצות החשן על שפתו אשר על עבר האפוד ביתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 20, 'ויעשו שתי טבעות זהב ויתנם על שתי כתפות האפוד מלמטה ממול פניו לעמת מחברתו ממעל לחשב האפוד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 21, 'וירכסו את החשן מטבעתו אל טבעת האפוד . . . . . בפתיל תכלת להיות על חשב האפוד . ולא יזח החשן מעל האפוד . . . . . . . . . . כאשר צוה יהוה את משה ויעשו את הארים ואת התמים כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 22, 'ויעשו את המעיל . מעשה ארג כליל תכלת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 23, 'ופי המעיל בתוכו כפי תחרא שפה לפיו סביב לא יקרע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 24, 'ויעשו על שולי המעיל רמוני תכלת וארגמן ותולעת שני ושש משזר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 25, 'ויעשו פעמוני זהב טהור ויתנו את הפעמונים . . על שולי המעיל סביב בתוך הרמונים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 26, 'פעמון זהב ורמון פעמון זהב ורמון על שולי המעיל סביב לשרת כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 27, 'ויעשו את הכיתנת שש מעשה ארג לאהרן ולבניו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 28, 'ואת המצנפת שש ואת פארי המגבעות שש ואת מכנסי הבד שש משזר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 29, 'ואת האבניט שש משזר תכלת וארגמן ותולעת שני מעשה רקם כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 30, 'ויעשו את ציץ נזר הקדש זהב טהור ויכתבו עליו מכתב פתוחי חותם קדש ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 31, 'ויתנו עליו פתיל תכלת לתת על המצנפת מלמעלה כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 32, 'ותכל כל עבדת המשכן אהל מועד ויעשו בני ישראל . כאשר צוה יהוה את משה כן עשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 33, 'ויביאו את המשכן אל משה את האהל ואת כל כליו קרסיו קרשיו בריחיו עמדיו ואדניהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 34, 'ואת מכסה עורת האילים המאדמים ואת מכסה עורת התחשים ואת פרכת המסך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 35, 'את ארון העדות ואת בדיו ואת הכפרת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 36, 'את השלחן ואת כל כליו ואת לחם הפנים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 37, 'את המנורה הטהרה ואת נרתיה נרת המערכה ואת כל כליה ואת שמן המאור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 38, 'את מזבח הזהב ואת שמן המשחה ואת קטרת הסמים ואת מסך פתח האהל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 39, 'את מזבח הנחשת ואת מכבר הנחשת אשר לו ואת בדיו ואת כל כליו את הכיור ואת כנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 40, 'את קלעי החצר ואת עמודיה ואת אדניה ואת המסך לשער החצר את מיתריה ואת יתדתיה ואת כל כלי עבדת המשכן לאהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 41, 'את בגדי השרד לשרת בקדש ואת בגדי הקדש לאהרן הכהן ואת בגדי בניו לכהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 42, 'ככל אשר צוה יהוה את משה כן עשו בני ישראל את כל העבדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 39, 43, 'וירא משה את כל המלאכה והנה עשו אתה כאשר צוה יהוה כן עשו ויברך אתם משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 2, 'ביום החדש הראישון באחד לחדש תקים את המשכן אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 3, 'ושמת שם את ארון העדות וסכת על הארון את הכפרת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 4, 'והבאת את השלחן וערכת את ערכו והבאת את המנורה והעלית את נרתיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 5, 'ונתתה את מזבח הזהב לקטרת לפני ארון העדות ושמת את מסך הפתח למשכן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 6, 'ונתתה את מזבח העלה לפני פתח המשכן אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 7, 'ונתתה את הכיור בין אהל מועד ובין המזבח ונתתה שם מים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 8, 'ושמת את החצר סביב ונתתה את מסך שער החצר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 9, 'ולקחת את שמן המשחה ומשחת את המשכן ואת כל אשר בו וקדשת אתו ואת כל כליו והיה קדש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 10, 'ומשחת את מזבח העלה ואת כל כליו וקדשת את המזבח והיה המזבח קדש קדשים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 11, 'ומשחת את הכיור ואת כנו וקדשת אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 12, 'והקרבת את אהרן ואת בניו . אל פתח אהל מועד ורחצת אתם במים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 13, 'והלבשת את אהרן את בגדי הקדש ומשחת אתו וקדשת אתו וכהן לי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 14, 'ואת בניו תקריב והלבשתם . . כיתנות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 15, 'ומשחת אתם כאשר משחת את אביהם וכהנו לי והיתה להיות להם משחתם לכהנת עולם לדרתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 16, 'ויעש משה ככל אשר צוה יהוה אתו כן עשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 17, 'ויהי בחדש הראישון בשנה השנית לצאתם ממצרים באחד לחדש הוקם . המשכן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 18, 'ויקם משה את המשכן ויתן את אדניו וישם את . . קרשיו ויתן את בריחיו ויקם את עמדיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 19, 'ויפרש את האהל על המשכן וישם את מכסה האהל עליו מלמעלה כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 20, 'ויקח ויתן את העדות אל הארון וישם את הבדים על הארון ויתן את הכפרת על הארון מלמעלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 21, 'ויבא את הארון אל המשכן וישם את פרכת המסך ויסך על ארון העדות כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 22, 'וישם את השלחן . באהל מועד על ירך המשכן צפונה מחוץ לפרכת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 23, 'ויערך עליו ערך לחם לפני יהוה כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 24, 'וישם את המנורה . באהל מועד נכח השלחן על ירך המשכן נגבה . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 25, 'ויעל את הנרות לפני יהוה כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 26, 'וישם את מזבח הזהב באהל מועד לפני הפרכת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 27, 'ויקטר עליו קטרת סמים לפני יהוה כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 28, 'וישם את מסך הפתח למשכן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 29, 'ואת מזבח העלה שם לפני פתח המשכן אהל מועד ויעל עליו את העלה ואת המנחה כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 30, 'וישם את הכיור בין אהל מועד ובין המזבח ויתן שם מים לרחצה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 31, 'וירחץ ממנו משה ואהרן ובניו את ידיהם ואת רגליהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 32, 'בבאם אל אהל מועד ובקרבתם אל המזבח ירחצו כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 33, 'ויקם את החצר סביב למשכן ולמזבח ויתן את מסך שער החצר ויכל משה את כל המלאכה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 34, 'ויכס הענן את אהל מועד וכבוד יהוה מלא את המשכן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 35, 'ולא יכל משה לבוא אל אהל מועד כי שכן עליו הענן וכבוד יהוה מלא את המשכן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 36, 'ובהעלות הענן מעל המשכן יסעו בני ישראל בכל מסעיהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 37, 'ואם לא יעלה הענן ולא יסעו עד יום העלותו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (2, 40, 38, 'כי ענן יהוה על המשכן יומם ואש תהיה בו לילה לעיני כל בית ישראל בכל מסעיהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 1, 1, 'ויקרא אל משה וידבר יהוה אליו מאהל מועד לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 1, 2, 'דבר אל בני ישראל ואמרת אליהם אדם כי יקריב מכם קרבן ליהוה מן הבהמה מן הבקר ומן הצאן תקריבו את קרבניכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 1, 3, 'אם עלה קרבנו מן הבקר זכר תמים יקריבנו אל פתח אהל מועד יקריב אתו לרצונו לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 1, 4, 'וסמך את ידו על ראש העלה ונרצה לו לכפר עליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 1, 5, 'ושחט את בן הבקר לפני יהוה והקריבו בני אהרן הכהנים את הדם וזרקו את הדם על המזבח סביב אשר פתח אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 1, 6, 'והפשיטו את העלה ונתחו אתה לנתחיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 1, 7, 'ונתנו בני אהרן הכהנים אש על המזבח וערכו עצים על האש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 1, 8, 'וערכו בני אהרן הכהנים את הנתחים ואת הראש ואת הפדר על העצים אשר על האש אשר על המזבח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 1, 9, 'וקרבו וכרעיו ירחצו במים והקטיר הכהן . הכל המזבחה עלה הוא אשה ריח ניחח ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 1, 10, 'ואם מן הצאן עלה קרבנו ליהוה מן הכשבים או מן העזים . זכר תמים יקריבנו אל פתח אהל מועד יקריב אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 1, 11, 'ושחט אתו על ירך המזבח צפונה לפני יהוה וזרקו בני אהרן הכהנים את דמו על המזבח סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 1, 12, 'ונתחו אתו לנתחיו ואת ראשו ואת פדרו יערך הכהן אתם על העצים אשר על האש אשר על המזבח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 1, 13, 'והקרב והכרעים ירחץ במים והקריב הכהן את הכל והקטיר המזבחה עלה הוא אשה ריח ניחח ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 1, 14, 'ואם מן העוף עלה קרבנו ליהוה והקריב מן התרים או . מבני היונה את קרבנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 1, 15, 'והקריבו הכהן אל המזבח ומלק את ראשו והקטיר המזבחה ונמצא דמו אל קיר המזבח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 1, 16, 'והסיר את מרתו בנצתו והשליך אתו אצל המזבח קדמה אל מקום הדשן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 1, 17, 'ושסע אתו בכנפיו ולא יבדיל והקטיר אתו הכהן המזבחה על העצים אשר על האש עלה הוא אשה ריח ניחח ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 2, 1, 'ונפש כי תקריב קרבן מנחה ליהוה סלת יהיה קרבנו ויצק עליה שמן ונתן עליה לבונה מנחה היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 2, 2, 'והביאה אל בני אהרן הכהנים וקמץ ממנה מלוא קמצו מסלתה ומשמנה על כל לבונתה והקטיר הכהן את אזכרתה המזבחה אשה ריח ניחח ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 2, 3, 'והנותרת מן המנחה לאהרן ולבניו קדש קדשים מאשי יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 2, 4, 'וכי תקריב קרבן מנחה מאפה תנור סלת חלות מצות בללות בשמן ורקיקי מצות משחים בשמן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 2, 5, 'ואם מנחה על מחבת קרבנך סלת בלולה בשמן מצה תהיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 2, 6, 'פתת אתה פתים ויצקת עליה שמן מנחה היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 2, 7, 'ואם מנחה מרחשת קרבנך סלת בשמן תעשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 2, 8, 'והבאת את המנחה אשר יעשה מאלה ליהוה והקריבה אל הכהן והגישה אל המזבח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 2, 9, 'והרים הכהן מן המנחה את אזכרתה והקטיר המזבחה אשה ריח ניחח ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 2, 10, 'והנותרת מן המנחה לאהרן ולבניו קדש קדשים מאשי יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 2, 11, 'כל המנחה אשר תקריבו ליהוה לא תעשה חמץ כי כל שאר וכל דבש לא תקריבו ממנו אשה ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 2, 12, 'קרבן ראשית תקריבו אתם ליהוה ואל המזבח לא יעלו לריח ניחח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 2, 13, 'וכל קרבן מנחתך במלח תמלח ולא תשבית מלח ברית אלהיך מעל מנחתך על כל קרבנך תקריב מלח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 2, 14, 'ואם תקריב מנחת בכרים ליהוה אביב קלי באש גרש כרמל תקריב את מנחת בכריך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 2, 15, 'ונתתה עליה שמן ושמת עליה לבונה מנחה היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 2, 16, 'והקטיר הכהן את אזכרתה מגרשה ומשמנה על כל לבונתה אשה ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 3, 1, 'ואם זבח שלמים קרבנו . אם מן הבקר הוא מקריב אם זכר אם נקבה תמים יקריבנו לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 3, 2, 'וסמך את ידו על ראש קרבנו ושחטו פתח אהל מועד וזרקו בני אהרן הכהנים את הדם על המזבח סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 3, 3, 'והקריב מזבח השלמים אשה ליהוה את החלב המכסה את הקרב ואת כל החלב אשר על הקרב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 3, 4, 'ואת שתי הכליות ואת החלב אשר עליהן אשר על הכסלים ואת היותרת על הכבד על הכליות יסירנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 3, 5, 'והקטירו אתו בני אהרן המזבחה על העלה אשר על העצים אשר על האש אשר על המזבח אשה ריח ניחח ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 3, 6, 'ואם מן הצאן קרבנו לזבח השלמים ליהוה זכר או נקבה תמים יקריבנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 3, 7, 'אם כשב הוא מקריב את קרבנו והקריב אתו לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 3, 8, 'וסמך את ידו על ראש קרבנו ושחט אתו לפני אהל מועד וזרקו בני אהרן את הדם על המזבח סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 3, 9, 'והקריב מזבח השלמים אשה ליהוה חלבו האליה תמימה לעמת העצה יסירנה את החלב המכסה את הקרב ואת כל החלב אשר על הקרב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 3, 10, 'ואת שתי הכליות ואת החלב אשר עליהן אשר על הכסלים ואת היותרת על הכבד על הכליות יסירנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 3, 11, 'והקטיר הכהן המזבחה לחם אשה ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 3, 12, 'ואם עז קרבנו והקריבו לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 3, 13, 'וסמך את ידו על ראשו ושחט אתו לפני אהל מועד וזרקו בני אהרן הכהנים את דמו על המזבח סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 3, 14, 'והקריב ממנו קרבנו אשה ליהוה את החלב המכסה את הקרב ואת כל החלב אשר על הקרב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 3, 15, 'ואת שתי הכליות ואת החלב אשר עליהן אשר על הכסלים ואת היותרת על הכבד על הכליות יסירנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 3, 16, 'והקטירם הכהן המזבחה לחם אשה לריח ניחח ליהוה כל חלב ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 3, 17, 'חקת עולם לדרתיכם בכל מושבתיכם כל חלב וכל דם לא תאכלו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 2, 'דבר אל בני ישראל לאמר נפש כי תחטא בשגגה מכל מצות יהוה אשר לא תעשינה ועשה מאחת מהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 3, 'אם הכהן המשיח יחטא לאשמת העם והקריב על חטאתו אשר חטא פר בן בקר תמים ליהוה לחטאת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 4, 'והביא את הפר אל פתח אהל מועד לפני יהוה וסמך את ידו על ראש הפר ושחט את הפר לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 5, 'ולקח הכהן המשיח אשר מלא את ידו מדם הפר והביא אתו אל אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 6, 'וטבל הכהן את אצבעו בדם והזה מן הדם שבע פעמים באצבעו לפני יהוה את פני פרכת הקדש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 7, 'ונתן הכהן מן הדם על קרנות מזבח קטרת הסמים לפני יהוה אשר באהל מועד ואת כל הדם . ישפך אל יסוד מזבח העלה אשר פתח אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 8, 'ואת כל חלב פר החטאת ירים ממנו את החלב המכסה את הקרב ואת כל החלב אשר על הקרב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 9, 'ואת שתי הכליות ואת החלב אשר עליהן אשר על הכסלים ואת היותרת על הכבד על הכליות יסירנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 10, 'כאשר יורם משור זבח השלמים והקטירם הכהן על מזבח העלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 11, 'ואת עור הפר ואת כל בשרו על ראשו ועל כרעיו וקרבו ופרשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 12, 'והוציאו את כל הפר אל מחוץ למחנה אל מקום טהור על שפך הדשן ושרפו אתו על העצים באש על שפך הדשן ישרף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 13, 'ואם כל עדת ישראל ישגו ונעלם דבר מעיני הקהל ועשו אחת מכל מצות יהוה אשר לא תעשינה ואשמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 14, 'ונודע החטאת אשר חטאו עליה והקריבו הקהל פר בן בקר תמים לחטאת והביאו אתו לפני אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 15, 'וסמכו זקני העדה את ידיהם על ראש הפר לפני יהוה ושחט את הפר לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 16, 'והביא הכהן המשיח מדם הפר אל אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 17, 'וטבל הכהן את אצבעו מן הדם והזה מן הדם שבע פעמים לפני יהוה את פני פרכת הקדש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 18, 'ומן הדם יתן הכהן על קרנות מזבח קטרת הסמים אשר לפני יהוה אשר באהל מועד ואת כל הדם ישפך אל יסוד מזבח העלה אשר פתח אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 19, 'ואת כל חלבו ירים ממנו והקטיר המזבחה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 20, 'ועשה לפר כאשר עשה לפר החטאת כן יעשה לו וכפר עליהם הכהן ונסלח להם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 21, 'והוציא את הפר אל מחוץ למחנה ושרף אתו כאשר שרף את הפר הראישון חטאת הקהל היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 22, 'אשר נשיא יחטא ועשה אחת מכל מצות יהוה אלהיו אשר לא תעשינה בשגגה ואשם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 23, 'או הודע אליו חטאתו אשר חטא בה והביא את קרבנו . . שעיר עזים זכר תמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 24, 'וסמך את ידו על ראש השעיר ושחט אתו במקום אשר ישחטו את העלה לפני יהוה חטאת היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 25, 'ולקח הכהן מדם החטאת באצבעו ונתן על קרנות מזבח העלה ואת דמו ישפך אל יסוד מזבח העלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 26, 'ואת כל חלבו יקטיר המזבחה כחלב זבח השלמים וכפר עליו הכהן מחטאתו ונסלח לו ואם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 27, 'נפש אחת תחטא בשגגה מעם הארץ בעשותה אחת מכל מצות יהוה אשר לא תעשינה ואשם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 28, 'או הודע אליו חטאתו אשר חטא עליה והביא את קרבנו שעירת עזים נקבה תמימה על חטאתו אשר חטא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 29, 'וסמך את ידו על ראש החטאת ושחט את החטאת במקום אשר ישחטו את העלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 30, 'ולקח הכהן מדמה באצבעו ונתן על קרנות מזבח העלה ואת כל דמה ישפך אל יסוד מזבח העלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 31, 'ואת כל חלבה יסיר כאשר יסיר החלב מעל זבח השלמים והקטיר הכהן המזבחה לריח ניחח ליהוה וכפר עליו הכהן ונסלח לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 32, 'ואם כשבה יביא קרבנו לחטאת נקבה תמימה יביאנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 33, 'וסמך את ידו על ראש החטאת ושחט אתה לחטאת במקום אשר ישחטו את העלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 34, 'ולקח הכהן מדם החטאת באצבעו ונתן על קרנות מזבח העלה ואת כל דמה ישפך אל יסוד מזבח העלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 4, 35, 'ואת כל חלבה יסיר כאשר יסיר חלב הכשב מזבח השלמים והקטיר הכהן . המזבחה על אשי יהוה וכפר עליו הכהן על חטאתו אשר חטא ונסלח לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 1, 'ונפש כי תחטא ושמעה קול אלה והוא עד או ראה או ידע אם לא יגיד ונשא עונו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 2, 'או נפש כי תגע בכל דבר טמא או בנבלת חיה טמאה או בנבלת בהמה טמאה או בנבלת שרץ טמא ונעלם ממנו והוא טמא ואשם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 3, 'או כי יגע בטמאת אדם לכל טמאתו אשר יטמא בה ונעלם ממנו והוא ידע ואשם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 4, 'או נפש כי תשבע לבטא בשפתים להריע או להטיב לכל אשר יבטא האדם בשבעה ונעלם ממנו והוא ידע ואשם לאחת מאלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 5, 'והיה כי יחטא לאחת מאלה והתודה את אשר חטא עליה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 6, 'והביא את אשמו ליהוה על חטאתו אשר חטא נקבה מן הצאן כשבה או שעירת עזים לחטאת וכפר עליו הכהן על חטאתו אשר חטא ונסלח לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 7, 'ואם לא תשיג ידו די שה והביא על אשמו אשר חטא שתי תרים או שני בני יונה ליהוה אחד לחטאת ואחד לעלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 8, 'והביא אתם אל הכהן והקריב את אשר לחטאת ראישון ומלק הכהן את ראשו ממול ערפו ולא יבדיל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 9, 'והזה מדם החטאת אל קיר המזבח והנשאר בדם ימצא אל יסוד המזבח חטאת היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 10, 'ואת השני יעשה עלה כמשפט וכפר עליו הכהן מחטאתו אשר חטא ונסלח לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 11, 'ואם לא תשיג ידו לשתי תרים או לשני בני יונה והביא את קרבנו אשר חטא עשירת האיפה סלת לחטאת לא יצק עליה שמן ולא יתן עליה לבונה כי חטאת היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 12, 'והביאה אל הכהן וקמץ . ממנה מלוא קמצו את אזכרתה והקטיר המזבחה על אשי יהוה חטאת היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 13, 'וכפר עליו הכהן על חטאתו אשר חטא מאחת מאלה ונסלח לו והיתה לכהן כמנחה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 14, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 15, 'נפש כי תמעל מעל וחטאה בשגגה מקדשי יהוה והביא את אשמו ליהוה איל תמים מן הצאן בערכך כסף שקלים בשקל הקדש לאשם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 16, 'ואת אשר חטא מן הקדש ישלם ואת חמישתו יסף עליו ונתן אתו לכהן והכהן יכפר עליו באיל האשם ונסלח לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 17, 'ואם נפש אחת תחטא ועשתה אחת מכל מצות יהוה אשר לא תעשינה ולא ידע ואשם ונשא עונו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 18, 'והביא איל תמים מן הצאן בערכך לאשם אל הכהן וכפר עליו הכהן על שגגתו אשר שגג והוא לא ידע ונסלח לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 19, 'אשם הוא אשם אשם ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 20, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 21, 'נפש כי תחטא ומעלה מעל ביהוה וכחש בעמיתו בפקדון או בתשומת יד או בגזל או עשק את עמיתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 22, 'או מצא אבדה וכחש בה ונשבע על שקר על אחת מכל אשר יעשה האדם לחטא בהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 23, 'והיה כי יחטא ואשם והשיב את הגזל אשר גזל או את העשק אשר עשק או את הפקדון אשר הפקד אתו או את האבדה אשר מצא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 24, 'או מכל דבר אשר ישבע עליו לשקר ושלם אתו בראשו וחמישתו יסף עליו לאשר הוא לו יתננו ביום אשמתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 25, 'ואת אשמו יביא ליהוה איל תמים מן הצאן בערכך לאשם . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 5, 26, 'וכפר עליו הכהן לפני יהוה ונסלח לו על אחת מכל אשר יעשה לאשמה בה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 2, 'צוה את אהרן ואת בניו לאמר זאת תורת העלה היא העלה על המוקדה על המזבח כל הלילה עד הבקר ואש המזבח תוקד בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 3, 'ולבש הכהן מדי בד ומכנסי בד ילבש על בשרו והרים את הדשן אשר תאכל האש את העלה על המזבח ושמו אצל המזבח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 4, 'ופשט את בגדיו ולבש בגדים אחרים והוציא את הדשן אל מחוץ למחנה אל מקום טהור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 5, 'והאש על המזבח תוקד בו לא תכבה ובער עליה הכהן עצים בבקר בבקר וערך עליה העלה והקטיר עליה חלבי שלמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 6, 'אש תמיד תוקד על המזבח לא תכבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 7, 'וזאת תורת המנחה הקריבו אתה בני אהרן לפני יהוה אל פני המזבח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 8, 'והרים ממנה בקמצו מסלת המנחה ומשמנה ואת כל הלבונה אשר על המנחה והקטיר המזבחה אשה ריח ניחח אזכרתה ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 9, 'והנותרת ממנה יאכל אהרן ובניו מצות תאכל במקום קדוש בחצר אהל מועד יאכלוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 10, 'לא תאפה חמץ חלקם נתתי אתה מאשי יהוה קדש קדשים היא כחטאת וכאשם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 11, 'כל זכר בבני אהרן יאכלנה חק עולם לדרתיכם מאשי יהוה כל אשר יגע בהם יקדש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 12, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 13, 'זה קרבן אהרן ובניו אשר יקריבו ליהוה ביום המשח אתו עשירת האיפה סלת למנחה תמיד מחציתה בבקר ומחציתה בין הערבים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 14, 'על המחבת בשמן תעשה מרבכת תביאנה תופני מנחת פתים תקריב ריח ניחח ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 15, 'הכהן המשיח תחתיו מבניו יעשה אתה חק עולם ליהוה כליל תקטיר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 16, 'וכל מנחת כהן כליל תהיה לא תאכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 17, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 18, 'דבר אל אהרן ואל בניו לאמר זאת תורת החטאת במקום אשר תשחט העלה תשחט החטאת לפני יהוה קדש קדשים היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 19, 'הכהן המחטא אתה יאכלנה במקום קדוש תאכל בחצר אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 20, 'כל אשר יגע בבשרה יקדש ואשר יזה מדמה על הבגד אשר יזה עליו יכבס במקום קדוש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 21, 'וכלי חרש אשר תבשל בו ישבר ואם בכלי נחשת בשלה ומרק ושטף במים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 22, 'כל זכר בכהנים יאכל אתה קדש קדשים היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 6, 23, 'וכל חטאת אשר יבוא מדמה אל אהל מועד לכפר בקדש לא תאכל באש תשרף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 1, 'וזאת תורת האשם קדש קדשים הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 2, 'במקום אשר ישחטו את העלה ישחטו את האשם ואת דמו יזרק על המזבח סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 3, 'ואת כל חלבו יקריב ממנו את האליה ואת החלב המכסה את הקרב ואת כל החלב אשר על הקרב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 4, 'ואת שתי הכליות ואת החלב אשר עליהן אשר על הכסלים ואת היותרת על הכבד על הכליות יסירנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 5, 'והקטיר אתם הכהן המזבחה אשה ליהוה אשם הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 6, 'כל זכר בכהנים יאכלנו במקום קדוש יאכל קדש קדשים הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 7, 'כחטאת כאשם תורה אחת להם הכהן אשר יכפר בו לו יהיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 8, 'והכהן המקריב את עלת איש עור העלה אשר יקריבו לכהן לו יהיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 9, 'וכל מנחה אשר תאפה בתנור וכל נעשה במרחשת ועל המחבת לכהן המקריב אתה לו תהיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 10, 'וכל מנחה בלולה בשמן וחרבה לכל בני אהרן תהיה איש כאחיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 11, 'וזאת תורת זבח השלמים אשר יקריבו ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 12, 'אם על תודה יקריבנו והקריב על זבח התודה חלות מצות בללות בשמן ורקיקי מצות משחים בשמן וסלת מרבכת חלות בללות בשמן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 13, 'על חלת לחם חמץ יקריב קרבנו על זבח תודת שלמיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 14, 'והקריב ממנו אחד מכל קרבן תרומה ליהוה לכהן הזרק את דם השלמים לו יהיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 15, 'ובשר זבח תודת שלמיו ביום קרבנו יאכל לא יניח ממנו עד בקר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 16, 'ואם נדר או נדבה זבח קרבנו ביום הקריבו את זבחו יאכל וממחרת והנותר ממנו יאכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 17, 'והנותר מבשר הזבח ביום השלישי באש ישרף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 18, 'ואם אכל יאכל מבשר זבח שלמיו ביום השלישי לא ירצה המקריב אתו לא יחשב לו פגול יהיה והנפש האכלת ממנו עונה תשא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 19, 'והבשר אשר יגע בכל טמא לא יאכל באש ישרף הבשר כל טהור יאכל בשר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 20, 'והנפש אשר תאכל בשר מזבח השלמים אשר ליהוה וטמאתו עליו ונכרתה הנפש ההיא מעמיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 21, 'ונפש כי תגע בכל טמא בטמאת אדם או בבהמה טמאה או בכל שרץ טמא ואכל מבשר זבח השלמים אשר ליהוה ונכרתה הנפש ההיא מעמיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 22, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 23, 'דבר אל בני ישראל לאמר כל חלב שור וכשב ועז לא תאכלו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 24, 'וחלב נבלה וחלב טרפה יעשה לכל מלאכה ואכל לא תאכלהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 25, 'כי כל אכל חלב מן הבהמה אשר יקריב ממנה אשה ליהוה ונכרתה הנפש האכלת מעמיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 26, 'וכל דם לא תאכלו בכל מושבתיכם לעוף ולבהמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 27, 'כל הנפש אשר תאכל כל דם ונכרתה הנפש ההיא מעמיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 28, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 29, '. ואל בני ישראל תדבר המקריב את זבח שלמיו ליהוה יביא את קרבנו ליהוה מזבח שלמיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 30, 'ידיו תביאנה את אשי יהוה את החלב על החזה יביאנו את החזה להניף אתו תנופה לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 31, 'והקטיר הכהן את החלב המזבחה והיה החזה לאהרן ולבניו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 32, 'ואת שוק הימין תתנו תרומה לכהן מזבחי שלמיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 33, 'המקריב את דם השלמים ואת החלב מבני אהרן לו תהיה שוק הימין למנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 34, 'כי את חזה התנופה ואת שוק התרומה לקחתי מאת בני ישראל מזבחי שלמיהם ואתן אתם לאהרן הכהן ולבניו לחק עולם מאת בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 35, 'זאת משחת אהרן ומשחת בניו מאשי יהוה ביום הקריב אתם לכהן ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 36, 'אשר צוה יהוה לתת להם ביום משחו אתם מאת בני ישראל חק עולם לדרתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 37, 'זאת התורה לעלה ולמנחה ולחטאת ולאשם ולמלאים ולזבח השלמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 7, 38, 'אשר צוה יהוה את משה בהר סיני ביום צותו את בני ישראל להקריב את קרבניהם ליהוה במדבר סיני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 2, 'קח את אהרן ואת בניו אתו ואת הבגדים ואת שמן המשחה ואת פר החטאת ואת שני האילים ואת סל המצות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 3, 'ואת כל העדה הקהל אל פתח אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 4, 'ויעש משה כאשר צוה יהוה אתו ותקהל העדה אל פתח אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 5, 'ויאמר משה אל העדה זה הדבר אשר צוה יהוה לעשות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 6, 'ויקרב משה את אהרן ואת בניו וירחץ אתם במים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 7, 'ויתן עליו את הכיתנת ויחגר אתו באבניט וילבש אתו את המעיל ויתן עליו את האפוד ויחגר אתו בחשב האפוד ויאפד לו בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 8, 'וישם עליו את החשן ויתן על החשן את הארים ואת התמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 9, 'וישם את המצנפת על ראשו ויתן על המצנפת אל מול פניו את ציץ הזהב נזר הקדש כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 10, 'ויקח משה את שמן המשחה וימשח את המשכן ואת כל אשר בו ויקדש אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 11, 'ויז ממנו על המזבח שבע פעמים וימשח את המזבח ואת כל כליו ואת הכיור ואת כנו לקדשם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 12, 'ויצק משמן המשחה על ראש אהרן וימשח אתו לקדשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 13, 'ויקריב משה את בני אהרן וילבשם כתנות ויחגר אתם אבנטים ויחבש להם מגבעות כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 14, 'ויגש את פר החטאת ויסמך אהרן ובניו את ידיהם על ראש פר החטאת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 15, 'וישחט ויקח משה את הדם ויתן על קרנות המזבח סביב באצבעו ויחטא את המזבח ואת הדם יצק אל יסוד המזבח ויקדשהו לכפר עליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 16, 'ויקח את כל החלב אשר על הקרב ואת יותרת הכבד ואת שתי הכליות ואת חלביהן ויקטר משה המזבחה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 17, 'ואת הפר ואת עורו ואת בשרו ואת פרשו שרף באש מחוץ למחנה כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 18, 'וינש את איל העלה ויסמך אהרן ובניו את ידיהם על ראש האיל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 19, 'וישחט ויזרק משה את הדם על המזבח סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 20, 'ואת האיל נתח לנתחיו ויקטר משה את הראש ואת הנתחים ואת הפדר ואת הקרב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 21, '. והכרעים רחץ במים ויקטר משה את כל האיל המזבחה עלה הוא לריח ניחח אשה הוא ליהוה כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 22, 'ויקריב את האיל השני איל המלאים ויסמך אהרן ובניו את ידיהם על ראש האיל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 23, 'וישחט ויקח משה מדמו ויתן על תנך אזן אהרן הימנית ועל בהון ידו הימנית ועל בהון רגלו הימנית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 24, 'ויקרב את בני אהרן ויתן משה מן הדם על תנך אזנם הימנית ועל בהון ידם הימנית ועל בהון רגלם הימנית ויזרק משה את הדם על המזבח סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 25, 'ויקח את החלב ואת האליה ואת כל החלב אשר על הקרב ואת יותרת הכבד ואת שתי הכליות ואת חלביהן ואת שוק הימין ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 26, 'ומסל המצות אשר לפני יהוה לקח חלת מצה אחת וחלת לחם שמן אחת ורקיק אחד וישם על החלבים ועל שוק הימין ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 27, 'ויתן את הכל על כפי אהרן ועל כפי בניו וינף אתם תנופה לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 28, 'ויקח משה אתם מעל כפיהם ויקטר המזבחה על העלה מלאים הם לריח ניחח אשה . ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 29, 'ויקח משה את החזה וינפהו תנופה לפני יהוה מאיל המלאים למשה היה למנה כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 30, 'ויקח משה משמן המשחה ומן הדם אשר על המזבח ויז על אהרן ועל בגדיו ועל בניו ועל בגדי בניו אתו ויקדש את אהרן ואת בגדיו ואת בניו ואת בגדי בניו אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 31, 'ויאמר משה אל אהרן ואל בניו בשלו את הבשר פתח אהל מועד במקום הקדש ושם תאכלו אתו ואת הלחם אשר בסל המלאים כאשר צויתי לאמר אהרן ובניו יאכלהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 32, 'והנותר בבשר ובלחם באש תשרפו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 33, 'ומפתח אהל מועד לא תצאו שבעת ימים עד יום מלאת ימי מלאיכם כי שבעת ימים ימלא את ידיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 34, 'כאשר עשה ביום הזה צוה יהוה לעשות לכפר עליכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 35, 'ופתח אהל מועד תשבו יומם ולילה שבעת ימים ושמרתם את משמרת יהוה ולא תמותו כי כן צויתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 8, 36, 'ויעש אהרן ובניו את כל הדברים אשר צוה יהוה ביד משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 1, 'ויהי ביום השמיני קרא משה לאהרן ולבניו ולזקני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 2, 'ויאמר אל אהרן קח לך עגל בן בקר לחטאת ואיל לעלה תמימם והקריב לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 3, 'ואל זקני ישראל תדבר לאמר קחו שעיר עזים לחטאת ועגל וכבש בני שנה תמימם לעלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 4, 'ושור ואיל לשלמים לזבח לפני יהוה ומנחה בלולה בשמן כי היום יהוה נראה אליכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 5, 'ויקחו את אשר צוה משה אל פני אהל מועד ויקרבו כל העדה ויעמדו לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 6, 'ויאמר משה זה הדבר אשר צוה יהוה תעשון וירא אליכם כבוד יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 7, 'ויאמר משה אל אהרן קרב אל המזבח ועשה את חטאתך ואת עלתך וכפר בעדך ובעד העם ועשה את קרבן העם וכפר בעדם כאשר צוה יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 8, 'ויקרב אהרן אל המזבח וישחט את עגל החטאת אשר לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 9, 'ויקרבו בני אהרן את הדם אליו ויטבל אצבעו בדם ויתן על קרנות המזבח ואת הדם יצק אל יסוד המזבח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 10, 'ואת החלב ואת הכליות ואת היותרת מן הכבד מן החטאת הקטיר המזבחה כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 11, 'ואת הבשר ואת העור שרף באש מחוץ למחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 12, 'וישחט את העלה וימציאו בני אהרן אליו את הדם ויזרקהו על המזבח סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 13, 'ואת העלה המציאו אליו לנתחיה ואת הראש ויקטר . המזבחה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 14, 'וירחץ את הקרב ואת הכרעים ויקטר על העלה המזבחה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 15, 'ויקרב את קרבן העם ויקח את שעיר החטאת אשר לעם וישחטהו ויחטאהו כראישון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 16, 'ויקריב את העלה ויעשה כמשפט ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 17, 'ויקריב את המנחה וימלא כפיו ממנה ויקטר על המזבח מלבד עלת הבקר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 18, 'וישחט את השור ואת האיל זבח השלמים אשר לעם וימציאו בני אהרן את הדם אליו ויזרקהו על המזבח סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 19, 'ואת החלבים מן השור ומן האיל האליה והמכסה והכליות ויותרת הכבד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 20, 'וישם את החלבים על החזות ויקטר החלבים המזבחה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 21, 'ואת החזות ואת שוק הימין הניף אהרן תנופה לפני יהוה כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 22, 'וישא אהרן את ידו על העם ויברכם וירד מעשות החטאת והעלה והשלמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 23, 'ויבא משה ואהרן אל אהל מועד ויצאו ויברכו את העם וירא כבוד יהוה אל כל העם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 9, 24, 'ותצא אש מלפני יהוה ותאכל על המזבח את העלה ואת החלבים ויראו כל העם וירנו ויפלו על פניהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 10, 1, 'ויקחו . בני אהרן נדב ואביהוא איש מחתתו ויתנו בהן אש וישמו עליהן קטרת ויקרבו לפני יהוה אש זרה אשר לא צוה . אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 10, 2, 'ותצא אש מלפני יהוה ותאכל אתם וימתו לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 10, 3, 'ויאמר משה אל אהרן הוא אשר דבר יהוה לאמר בקרבי אקדש ועל פני כל העם אכבד וידם אהרן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 10, 4, 'ויקרא משה אל מישאל ואל אליצפן בני עזיאל דד אהרן ויאמר אליהם קרבו שאו את אחיכם מאת פני הקדש אל מחוץ למחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 10, 5, 'ויקרבו וישאו בכיתנתם אל מחוץ למחנה כאשר דבר משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 10, 6, 'ויאמר משה אל אהרן ואל אלעזר ואל איתמר בניו ראשיכם לא תפרעו ובגדיכם לא תפרמו ולא תמתו ועל כל העדה יקצף ואחיכם כל בית ישראל יבכו את השרפה אשר שרף יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 10, 7, 'ומפתח אהל מועד לא תצאו פן תמתו כי שמן משחת יהוה עליכם ויעשו כדבר משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 10, 8, 'וידבר יהוה אל אהרן לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 10, 9, 'יין ושכר אל תשתה אתה ובניך אתך בבאכם אל אהל מועד ולא תמתו חקת עולם לדרתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 10, 10, 'ולהבדיל בין הקדש ובין החל ובין הטמא ובין הטהור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 10, 11, 'ולהורת את בני ישראל את כל החקים אשר דבר יהוה אליהם ביד משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 10, 12, 'וידבר משה אל אהרן ואל אלעזר ואל איתמר בניו הנותרים קחו את המנחה הנותרת מאשי יהוה ואכלוה מצות אצל המזבח כי קדש קדשים היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 10, 13, 'ואכלתם אתה במקום קדוש כי חקך וחק בניך היא מאשי יהוה כי כן צויתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 10, 14, 'ואת חזה התנופה ואת שוק התרומה תאכלו במקום טהור אתה ובניך ובנתיך אתך כי חקך וחק בניך נתנו מזבחי שלמי בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 10, 15, 'שוק התרומה וחזה התנופה על אשי החלבים יביאו להניף תנופה לפני יהוה והיה לך ולבניך ולבנתיך אתך לחק עולם כאשר צוה יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 10, 16, 'ואת שעיר החטאת דרש דרש משה והנה שרף ויקצף על אלעזר ועל איתמר בני אהרן הנותרים לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 10, 17, 'מדוע לא אכלתם את החטאת במקום הקדש כי קדש קדשים היא ואתה נתן לכם לשאת את עון העדה לכפר עליהם לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 10, 18, 'הן לא הובא את דמה אל הקדש פנימה אכל תאכלו אתה בקדש כאשר צויתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 10, 19, 'וידבר אהרן אל משה הן היום הקריבו את חטאתם ואת עלתם לפני יהוה ותקראנה אתי כאלה ואכלתי חטאת היום הייטב בעיני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 10, 20, 'וישמע משה וייטב בעיניו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 1, 'וידבר יהוה אל משה ואל אהרן לאמר אליהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 2, 'דברו אל בני ישראל לאמר זאת החיה אשר תאכלו מכל הבהמה אשר על הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 3, 'כל מפריסת פרסה ושסעת שסע שתי פרסות מעלת גרה בבהמה אתה תאכלו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 4, 'אך את זה לא תאכלו ממעלי הגרה וממפרסי הפרסה את הגמל כי מעלה גרה הוא ופרסה איננו מפריס טמא הוא לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 5, 'ואת השפן כי מעלה גרה הוא ופרסה לא הפריס טמא הוא לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 6, 'ואת הארנבת כי מעלת גרה היא ופרסה לא הפריסה טמאה היא לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 7, 'ואת החזיר כי מפריס פרסה הוא ושסע שסע פרסה והוא גרה לא יגור טמא הוא לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 8, 'מבשרם לא תאכלו ובנבלתם לא תגעו טמאים הם לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 9, 'ואת זה תאכלו מכל אשר במים כל אשר לו סנפיר וקשקשת במים בימים ובנחלים אתם תאכלו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 10, 'וכל אשר אין לו סנפיר וקשקשת במים בימים ובנחלים מכל שרץ המים ומכל נפש החיה אשר במים שקץ הם לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 11, 'ושקץ יהיו לכם מבשרם לא תאכלו ואת נבלתם תשקצו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 12, 'וכל אשר אין לו סנפיר וקשקשת במים שקץ הוא לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 13, 'ואת אלה תשקצו מן העוף לא תאכלו שקץ הם את הנשר ואת הפרס ואת העזניה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 14, 'ואת הדאה ואת האיה למינה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 15, 'ואת כל ערב למינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 16, 'ואת בת היענה ואת התחמס ואת השחף למינו ואת הנץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 17, 'ואת הכוס ואת השלך ואת הינשוף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 18, 'ואת התנשמת ואת הקאת ואת הרחמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 19, 'ואת החסידה והאנפה למינה ואת הדגיפת ואת העטלף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 20, 'וכל שרץ העוף ההלך על ארבע שקץ הוא לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 21, 'אך את זה תאכלו מכל שרץ העוף ההלך על ארבע אשר לא כרעים ממעל לרגליו לנתר בהם על הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 22, 'את אלה מהם תאכלו את הארבה למינהו ואת הסלעם למינהו ואת החרגל למינה ואת החגב למינהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 23, 'וכל שרץ העוף אשר לו ארבע רגלים שקץ הוא לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 24, 'ולאלה תטמאו כל הנגע בנבלתם יטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 25, 'וכל הנשא מנבלתם יכבס בגדיו ורחץ במים וטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 26, 'לכל הבהמה אשר היא מפריסת פרסה ושסעה איננה שסע . וגרה איננה מעלה טמאים הם לכם כל הנגע בהם יטמא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 27, 'וכל ההלך על כפיו בכל החיה ההלכת על ארבע טמאים הם לכם כל הנגע בנבלתם יטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 28, 'והנשא . מנבלתם יכבס בגדיו וטמא עד הערב טמאים הם לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 29, 'וזה לכם הטמא בשרץ השרץ על הארץ החלד והעכבור והצב למינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 30, 'והאנקה והכח והלטאה והחמט והתנשמת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 31, 'אלה הטמאים לכם מכל השרץ כל הנגע בהם במותם יטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 32, 'וכל אשר יפל עליו מהם במותם יטמא מכל כלי עץ או בגד או עור או שק כל כלי אשר יעשה מלאכה בהם במים יבוא וטמא עד הערב וטהר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 33, 'וכל כלי חרש אשר יפל מהם אל תוכו כל אשר בתוכו יטמא ואתו תשברו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 34, 'מכל האכל אשר יאכל אשר יבוא עליו מים יטמא וכל משקה אשר ישתה בכל כלי יטמא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 35, 'וכל אשר יפל מנבלתם עליו יטמא תנור וכרים יתצו טמאים הם וטמאים יהיו לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 36, 'אך מעין מים ובור מקוה מים יהיה טהור ונגע בנבלתם יטמא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 37, 'וכי יפל מנבלתם על . זרע זרע אשר יזרע טהור הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 38, 'וכי יתן מים על הזרע ונפל מנבלתם עליו טמא הוא לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 39, 'וכי ימות מן הבהמה אשר היא לכם לאכלה הנגע בנבלתה יטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 40, 'והאכל מנבלתה יכבס בגדיו וטמא עד הערב והנשא את נבלתה יכבס בגדיו וטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 41, 'וכל השרץ השרץ על הארץ שקץ הוא לא יאכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 42, 'כל ההלך על גחון וכל ההלך על ארבע עד כל מרבה רגלים לכל השרץ השרץ על הארץ לא תאכלום כי שקץ הם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 43, 'אל תשקצו את נפשתיכם בכל השרץ השרץ ולא תטמאו בהם ונטמאתם בם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 44, 'כי אני יהוה אלהיכם והתקדשתם והייתם קדשים כי קדוש אני ולא תטמאו את נפשתיכם בכל השרץ הרמש על הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 45, 'כי אני יהוה אלהיכם המעלה אתכם מארץ מצרים להיות לכם לאלהים והייתם קדשים כי קדוש אני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 46, 'זאת תורת הבהמה והעוף וכל נפש החיה הרמשת במים לכל הנפש השרצת על הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 11, 47, 'להבדיל בין הטמא ובין הטהור ובין החיה הנאכלת ובין החיה אשר לא תאכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 12, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 12, 2, 'דבר אל בני ישראל לאמר אשה כי תזרע וילדה זכר וטמאה שבעת ימים כימי נדת דותה תטמא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 12, 3, 'וביום השמיני ימול את בשר ערלתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 12, 4, 'ושלשים יום ושלשת ימים תשב בדם טהרה בכל קדש לא תגע ואל המקדש לא תבוא עד מלאת ימי טהרה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 12, 5, 'ואם נקבה תלד וטמאה שבעים כנדתה וששים יום וששת ימים תשב על דמי טהרה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 12, 6, 'ובמלאת ימי טהרה לבן או לבת תביא כבש בן שנתו לעלה ובן יונה או תר לחטאת אל פתח אהל מועד אל הכהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 12, 7, 'והקריבו לפני יהוה וכפר עליה הכהן וטהרה ממקור דמיה זאת תורת הילדת לזכר או לנקבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 12, 8, 'ואם לא תמצא ידה די שה ולקחה שתי תרים או שני בני יונה אחד לחטאת ואחד לעלה וכפר עליה הכהן וטהרה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 1, 'וידבר יהוה אל משה ואל אהרן לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 2, 'אדם כי יהיה בעור בשרו שאת או ספחת או בהרת והיה בעור בשרו לנגע צרעת והובא אל אהרן הכהן או אל אחד מבניו הכהנים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 3, 'וראה הכהן את הנגע בעור הבשר ושער בנגע הפך לבן ומראה הנגע עמק מעור בשרו נגע צרעת הוא וראה הכהן וטמא אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 4, 'ואם בהרת לבנה היא בעור בשרו ועמק אין מראה מן העור ושערה לא הפך לבן והסגיר הכהן את הנגע שבעת ימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 5, 'וראה הכהן ביום השביעי והנה הנגע עמד בעיניו לא פשה הנגע בעור והסגירו הכהן שבעת ימים שנית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 6, 'וראה הכהן . ביום השביעי שנית והנה כהה הנגע לא פשה הנגע בעור וטהרו הכהן מספחת היא וכבס בגדיו וטהר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 7, 'ואם פשה תפשה המספחת בעור אחרי הראתו אל הכהן לטהרתו ונראה שנית אל הכהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 8, 'וראה הכהן והנה פשתה המספחת בעור וטמאו הכהן צרעת היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 9, 'ונגע צרעת כי תהיה באדם והובא אל הכהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 10, 'וראה הכהן והנה שאת לבנה בעור והיא הפכה שער לבן ומחית בשר חי בשאת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 11, 'צרעת נושנת היא בעור בשרו וטמאו הכהן לא יסגרנו כי טמא הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 12, 'ואם פרח תפרח הצרעת בעור וכסתה הצרעת את כל עור הנגע מראשו ועד רגליו לכל מראה עיני הכהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 13, 'וראה הכהן והנה כסתה הצרעת את כל בשרו וטהר את הנגע כלו הפך לבן טהור הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 14, 'וביום הראות בו בשר חי יטמא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 15, 'וראה הכהן את הבשר החי וטמאו הבשר החי טמא הוא צרעת היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 16, 'או כי ישוב הבשר החי ונהפך ללבן ובא אל הכהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 17, 'וראה הכהן והנה נהפך הנגע ללבן וטהר הכהן את הנגע טהור הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 18, 'ובשר כי יהיה בו . שחין ונרפא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 19, 'והיה במקום השחין שאת לבנה או בהרת לבנה אדמדמת ונראה אל הכהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 20, 'וראה הכהן והנה מראהו שפל מן העור ושערה הפך לבן וטמאו הכהן נגע צרעת היא בשחין פרחה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 21, 'ואם יראה הכהן והנה אין בה שער לבן ושפלה איננה מן העור והיא כהה והסגירו הכהן שבעת ימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 22, 'ואם פשה תפשה בעור וטמא הכהן אתו נגע הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 23, 'ואם תחתיה תעמד הבהרת לא פשתה צרבת השחין היא וטהרו הכהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 24, 'או בשר כי יהיה בעורו מכות אש והיתה מחית המכוה בהרת לבנה אדמדמת או לבנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 25, 'וראה אתה הכהן והנה הפך שער לבן בבהרת ומראה עמק מן העור צרעת היא במכוה פרחה וטמא אתו הכהן נגע צרעת היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 26, 'ואם יראה הכהן והנה אין בבהרת שער לבן ושפלה איננה מן העור והיא כהה והסגירו הכהן שבעת ימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 27, 'וראה הכהן ביום השביעי אם פשה תפשה בעור וטמא אתו הכהן נגע צרעת היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 28, 'ואם תחתיה תעמד הבהרת לא פשתה בעור והיא כהה שאת המכוה היא וטהרו הכהן כי צרבת המכוה היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 29, 'ואיש או אשה כי יהיה בו נגע בראש או בזקן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 30, 'וראה הכהן את הנגע והנה מראהו עמק מן העור ובו שער צהב דק וטמא הכהן אתו נתק הוא צרעת הראש או הזקן היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 31, 'וכי יראה הכהן את נגע הנתק והנה אין מראהו עמק מן העור ושער שחר אין בו והסגיר הכהן את נגע הנתק שבעת ימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 32, 'וראה הכהן את הנתק ביום השביעי והנה לא פשה הנתק ולא היה בו שער צהב ומראה הנתק אין עמק מן העור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 33, 'והתגלח ואת הנתק לא יגלח והסגיר הכהן את הנתק שבעת ימים שנית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 34, 'וראה הכהן את הנתק ביום השביעי והנה לא פשה הנתק בעור ומראהו איננו עמק מן העור וטהרו . הכהן וכבס בגדיו וטהר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 35, 'ואם פשה יפשה הנתק בעור אחרי טהרתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 36, 'וראה הכהן והנה פשה הנתק בעור לא יבקר הכהן לשער הצהב טמא הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 37, 'ואם בעיניו עמד הנתק ושער שחר צמח בו נרפא הנתק טהור הוא וטהרו הכהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 38, 'ואיש או אשה כי יהיה בעור בשרם בהרת בהרת לבנות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 39, 'וראה הכהן והנה בעור בשרם בהרת כהות לבנות בהק הוא פרח בעור טהור הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 40, 'ואיש כי ימרט ראשו קרח הוא טהור הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 41, 'ואם מפאת פניו ימרט ראשו גבח הוא טהור הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 42, 'וכי יהיה בקרחתו או בגבחתו נגע לבן אדמדם צרעת פרחת היא בקרחתו או בגבחתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 43, 'וראה אתה הכהן והנה שאת הנגע לבנה אדמדמת בקרחתו או בגבחתו כמראה צרעת עור בשר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 44, 'איש צרוע הוא טמא הוא טמא יטמאנו הכהן בראשו נגעו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 45, 'והצרוע אשר בו הנגע בגדיו יהיו פרומים וראשו יהיה פרוע ועל שפם יעטא וטמא טמא יקרא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 46, 'כל ימי אשר הנגע בו יטמא טמא הוא בדד ישב מחוץ למחנה מושבו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 47, 'והבגד כי יהיה בו נגע צרעת בבגד צמר או בבגד פשתים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 48, 'או בשתי או בערב לפשתים ולצמר או בעור או בכל מלאכת עור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 49, 'והיה הנגע ירקרק או אדמדם בבגד או בעור או בשתי או בערב או בכל כלי עור נגע צרעת הוא והראה את הכהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 50, 'וראה הכהן את הנגע והסגיר את הנגע שבעת ימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 51, 'וראה את הנגע ביום השביעי כי פשה הנגע בבגד או בשתי או בערב או בעור לכל אשר יעשה העור למלאכה צרעת ממראת הנגע טמא הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 52, 'ושרף את הבגד או את השתי או את הערב בצמר או בפשתים או את כל כלי העור אשר יהיה בו הנגע כי צרעת ממראת היא באש תשרף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 53, 'ואם יראה הכהן והנה לא פשה הנגע בבגד או בשתי או בערב או בכל כלי עור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 54, 'וצוה הכהן וכבסו את אשר בו הנגע והסגירו שבעת ימים שנית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 55, 'וראה הכהן אחרי הכבס את הנגע והנה לא הפך הנגע את עיניו והנגע לא פשה טמא הוא באש תשרפנו פחתת היא בקרחתו או בגבחתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 56, 'ואם ראה הכהן והנה כהה הנגע אחרי הכבסו אתו וקרעו אתו מן הבגד או מן העור או מן השתי או מן הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 57, 'ואם תראה עוד בבגד או בשתי או בערב או בכל כלי עור פרחת היא באש תשרפנו את אשר בו הנגע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 58, 'והבגד או השתי או הערב או כל כלי העור אשר תכבס וסר מהם הנגע וכבס שנית וטהר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 13, 59, 'זאת תורת נגע הצרעת בגד הצמר או הפשתים או השתי או הערב או כל כלי העור לטהרו או לטמאו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 2, 'זאת תהיה תורת המצרע ביום טהרתו והובא אל הכהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 3, 'ויצא הכהן אל מחוץ למחנה וראה הכהן והנה נרפא נגע הצרעת מן הצרוע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 4, 'וצוה הכהן ולקחו למטהר שתי צפרים חיות טהרות ועץ ארז ושני תולעת ואזוב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 5, 'וצוה הכהן ושחטו את הצפור האחת אל כלי חרש על מים חיים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 6, 'ואת הצפר החיה יקח אתה ואת עץ הארז ואת שני התולעת ואת האזוב וטבל אתם ואת הצפור החיה בדם הצפור השחוטה על מים חיים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 7, 'והזה על המטהר מן הצרעת שבע פעמים וטהרו ושלח את הצפור החיה על פני השדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 8, 'וכבס המטהר את בגדיו וגלח את כל שערו ורחץ במים וטהר ואחר יבוא אל המחנה וישב מחוץ לאהלו שבעת ימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 9, 'והיה ביום השביעי יגלח את כל שערו את ראשו ואת זקנו ואת גבת עיניו ואת כל שערו יגלח וכבס את בגדיו ורחץ את בשרו במים וטהר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 10, 'וביום השמיני יקח שני כבשים תמימם בני שנה וכבשה אחת בת שנתה תמימה ושלשה עשרנים סלת מנחה בלולה בשמן ולג שמן אחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 11, 'והעמיד הכהן המטהר את האיש המטהר ואתם לפני יהוה פתח אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 12, 'ולקח הכהן את הכבש האחד והקריב אתו לאשם ואת לג השמן והניף אתם תנופה לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 13, 'ושחט את הכבש במקום אשר ישחטו את החטאת ואת העלה במקום הקדש כי כחטאת כאשם הוא לכהן קדש קדשים הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 14, 'ולקח הכהן מדם האשם ונתן . על תנך אזן המטהר הימנית ועל בהון ידו הימנית ועל בהון רגלו הימנית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 15, 'ולקח הכהן מלג השמן ויצק על כף הכהן השמאלית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 16, 'וטבל הכהן את אצבעו הימנית מן השמן אשר על כפו השמאלית והזה מן השמן . שבע פעמים לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 17, 'ומיתר השמן אשר על כפו יתן הכהן על תנך אזן המטהר הימנית ועל בהון ידו הימנית ועל בהון רגלו הימנית על דם האשם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 18, 'והנותר בשמן אשר על כף הכהן יתן על ראש המטהר וכפר עליו הכהן לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 19, 'ועשה הכהן את החטאת וכפר על המטהר מטמאתו ואחר ישחט את העלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 20, 'והעלה הכהן את העלה ואת המנחה המזבחה לפני יהוה וכפר עליו הכהן וטהר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 21, 'ואם דל הוא ואין ידו משגת ולקח כבש אחד אשם לתנופה לכפר עליו ועשרון סלת אחד בלול בשמן למנחה ולג שמן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 22, 'ושתי תרים או שני בני יונה אשר תשיג ידו והיה אחד חטאת ואחד עלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 23, 'והביא אתם ביום השמיני לטהרתו אל הכהן אל פתח אהל מועד לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 24, 'ולקח הכהן את כבש האשם ואת לג השמן והניף אתם . תנופה לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 25, 'ושחט את כבש האשם ולקח הכהן מדם האשם ונתן על תנך אזן המטהר הימנית ועל בהון ידו הימנית ועל בהון רגלו הימנית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 26, 'ומן השמן יצק הכהן על כף הכהן השמאלית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 27, 'והזה הכהן באצבעו הימנית מן השמן אשר על כפו השמאלית שבע פעמים לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 28, 'ונתן הכהן מן השמן אשר על כפו על תנך אזן המטהר הימנית ועל בהון ידו הימנית ועל בהון רגלו הימנית על מקום דם האשם ');
