INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 30, 'ועשה את האחד מן התרים או . מבני היונה מאשר תשיג ידו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 31, 'את אשר תשיג ידו את האחד חטאת ואת האחד עלה על המנחה וכפר . על המטהר לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 32, 'זאת תורת אשר בו נגע צרעת אשר לא תשיג ידו בטהרתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 33, 'וידבר יהוה אל משה ואל אהרן לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 34, 'כי תבאו אל ארץ כנען אשר אני נתן לכם לאחזה ונתתי נגע צרעת בבית ארץ אחזתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 35, 'ובא אשר לו הבית והגיד לכהן לאמר כנגע נראה לי בבית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 36, 'וצוה הכהן ופנו את הבית בטרם יבוא הכהן לראות את הנגע ולא יטמא כל אשר בבית ואחרי כן יבוא הכהן לראות את הבית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 37, 'וראה הכהן את הנגע והנה הנגע בקירות הבית שקעררות ירקרקות או אדמדמות ומראהן שפל מן הקיר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 38, 'ויצא הכהן מן הבית אל פתח הבית והסגיר את הבית שבעת ימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 39, 'ושב הכהן ביום השביעי וראה והנה פשה הנגע בקירות הבית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 40, 'וצוה הכהן וחלצו את האבנים אשר בהן הנגע והשליכו אתהן אל מחוץ לעיר אל מקום טמא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 41, 'ואת הבית יקצעו מבית סביב ושפכו את העפר אשר הקיצו אל מחוץ לעיר אל מקום טמא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 42, 'ולקחו אבנים אחרות והביאו אל תחת האבנים ועפר אחר יקחו וטחו את הבית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 43, 'ואם ישוב הנגע ופרח בבית אחרי חלצו את האבנים ואחרי הקצות את הבית ואחרי הטח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 44, 'ובא הכהן וראה והנה פרה הנגע בבית צרעת ממראת . בבית טמא הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 45, 'ונתצו את הבית את אבניו ואת עציו ואת כל עפר הבית והוציאו אל מחוץ לעיר אל מקום טמא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 46, 'והבא אל הבית כל ימי הסגיר אתו יטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 47, 'והשכב בבית יכבס . בגדיו והאכל בבית יכבס . בגדיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 48, 'ואם בא יבוא הכהן וראה והנה לא פרח הנגע בבית אחרי הטח את הבית וטהר הכהן את הבית כי נרפא הנגע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 49, 'ולקחו לחטא את הבית שתי צפרים ועץ ארז ושני תולעת ואזוב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 50, 'ושחט את הצפור האחת אל כלי חרש על מים חיים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 51, 'ולקח את עץ הארז ואת שני התולעת ואת האזוב ואת הצפור החיה וטבל אתם בדם הצפור השחוטה ובמים החיים והזה על הבית שבע פעמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 52, 'וחטא את הבית בדם הצפור ובמים החיים ובצפור החיה ובעץ הארז ובשני התולעת ובאזוב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 53, 'ושלח את הצפור החיה אל מחוץ לעיר על פני השדה וכפר על הבית וטהר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 54, 'זאת התורה לכל נגע הצרעת ולנתק ');
