INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 56, 'ולשאת ולספחת ולבהרת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 14, 57, 'להורות ביום הטמא וביום הטהור זאת תורת הצרעת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 1, 'וידבר יהוה אל משה ואל אהרן לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 2, 'דברו אל בני ישראל ואמרתם אליהם איש איש כי יהיה זב מבשרו זובו טמא הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 3, 'וזאת תהיה טמאתו בזובו רר בשרו את זובו או החתים בשרו מזובו טמא הוא כל ימי זב בשרו או החתים בשרו מזובו טמאתו היא . . . . . . . . . . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 4, 'כל המשכב אשר ישכב עליו הזב יטמא וכל הכלי אשר ישב עליו יטמא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 5, 'ואיש אשר יגע במשכבו יכבס בגדיו ורחץ במים וטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 6, 'והישב על הכלי אשר ישב עליו הזב יכבס בגדיו ורחץ במים וטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 7, 'והנגע בבשר הזב יכבס בגדיו ורחץ במים וטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 8, 'וכי ירק הזב בטהור יכבס בגדיו ורחץ במים וטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 9, 'וכל המרכב אשר ירכב עליו הזב יטמא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 10, 'וכל הנגע בכל אשר יהיה תחתיו יטמא עד הערב והנשא אתם יכבס בגדיו ורחץ במים וטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 11, 'וכל אשר יגע בו הזב וידו לא שטף במים יכבס בגדיו ורחץ במים וטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 12, 'וכלי חרש אשר יגע בו הזב ישבר וכל כלי עץ ישטף במים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 13, 'וכי יטהר הזב מזובו וספר לו שבעת ימים לטהרתו וכבס בגדיו ורחץ את בשרו במים חיים וטהר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 14, 'וביום השמיני יקח לו שתי תרים או שני בני יונה ובא לפני יהוה אל פתח אהל מועד ונתנם אל הכהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 15, 'ועשה אתם הכהן אחד חטאת ואחד עלה וכפר עליו הכהן לפני יהוה מזובו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 16, 'ואיש כי תצא ממנו שכבת זרע ורחץ במים את כל בשרו וטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 17, 'וכל בגד וכל עור אשר יהיה עליו שכבת זרע וכבס במים וטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 18, 'ואשה אשר ישכב אישה אתה שכבת זרע ורחצו במים וטמאו עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 19, 'ואשה כי תהיה זבה דם יהיה זובה בבשרה שבעת ימים תהיה בנדתה כל הנגע בה יטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 20, 'וכל אשר תשכב עליו בנדתה יטמא וכל אשר תשב עליו יטמא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 21, 'וכל הנגע במשכבה יכבס בגדיו ורחץ במים וטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 22, 'וכל הנגע בכל הכלי אשר תשב עליו יכבס בגדיו ורחץ במים וטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 23, 'ואם על המשכבה היא או על הכלי אשר היא ישבת עליו בנגעו בו יטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 24, 'ואם שכב ישכב אישה אתה ותהי נדתה עליו יטמא שבעת ימים וכל המשכב אשר ישכב עליו יטמא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 25, 'ואשה כי יזוב זוב דמה ימים רבים בלא עת נדתה או כי תזוב על נדתה כל ימי זוב טמאתה כימי נדתה תהיה טמאה היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 26, 'כל המשכב אשר תשכב עליו כל ימי זובה כמשכב נדתה יהיה לה וכל הכלי אשר תשב עליו טמא יהיה כטמאת נדתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 27, 'וכל הנגע בם יטמא וכבס בגדיו ורחץ במים וטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 28, 'ואם טהרה מזובה וספרה לה שבעת ימים ואחר תטהר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 29, 'וביום השמיני תקח לה שתי תרים או שני בני יונה והביאה אתם אל הכהן אל פתח אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 30, 'ועשה הכהן את האחד חטאת ואת האחד עלה וכפר עליה הכהן לפני יהוה מזוב טמאתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 31, 'והזהרתם את בני ישראל מטמאתם ולא ימותו בטמאתם בטמאם את משכני אשר בתוכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 32, 'זאת תורת הזב ואשר תצא ממנו שכבת זרע לטמאה בה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 15, 33, 'והדוה בנדתה והזב את זובו לזכר ולנקבה ולאיש אשר ישכב עם טמאה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 1, 'וידבר יהוה אל משה אחרי מות שני בני אהרן בקרבתם לפני יהוה וימתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 2, 'ויאמר יהוה אל משה דבר אל אהרן אחיך ואל יבוא בכל עת אל הקדש מבית לפרכת אל פני הכפרת אשר על הארון ולא ימות כי בענן אראה על הכפרת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 3, 'בזאת יבוא אהרן אל הקדש בפר בן בקר לחטאת ואיל לעלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 4, 'וכיתנת בד קדש ילבש ומכנסי בד יהיו על בשרו ובאבניט בד יחגר ובמצנפת בד יצנף בגדי קדש הם ורחץ במים את בשרו ולבשם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 5, 'ומאת עדת בני ישראל יקח שני שעירי עזים לחטאת ואיל אחד לעלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 6, 'והקריב אהרן את פר החטאת אשר לו וכפר בעדו ובעד ביתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 7, 'ולקח את שני השעירים והעמיד אתם לפני יהוה פתח אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 8, 'ונתן אהרן על שני השעירים גורלות גורל אחד ליהוה וגורל אחד לעזאזל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 9, 'והקריב אהרן את השעיר אשר עלה עליו הגורל ליהוה ועשהו חטאת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 10, 'והשעיר אשר עלה עליו הגורל לעזאזל יעמד חי לפני יהוה לכפר עליו לשלח אתו לעזאזל המדברה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 11, 'והקריב אהרן את פר החטאת אשר לו וכפר בעדו ובעד ביתו ושחט את פר החטאת אשר לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 12, 'ולקח מלוא המחתה גחלי אש מעל המזבח מלפני יהוה ומלוא חפניו קטרת סמים דקה והביא מבית לפרכת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 13, 'ונתן את הקטרת על האש לפני יהוה וכסה ענן הקטרת את הכפרת אשר על העדות ולא ימות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 14, 'ולקח מדם הפר והזה באצבעו אל פני הכפרת קדמה ולפני הכפרת יזה שבע פעמים מן הדם באצבעו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 15, 'ושחט את שעיר החטאת אשר לעם והביא את דמו אל מבית לפרכת ועשה את דמו כאשר עשה לדם הפר והזה אתו על הכפרת ולפני הכפרת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 16, 'וכפר על הקדש מטמאת בני ישראל ומפשעיהם לכל חטאתם וכן יעשה לאהל מועד השכן אתם בתוך טמאתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 17, 'וכל אדם לא יהיה באהל מועד בבאו לכפר בקדש עד צאתו וכפר בעדו ובעד ביתו ובעד כל קהל ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 18, 'ויצא אל המזבח אשר לפני יהוה וכפר עליו ולקח מדם הפר ומדם השעיר ונתן על קרנות המזבח סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 19, 'והזה עליו מן הדם באצבעו שבע פעמים וטהרו וקדשו מטמאת בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 20, 'וכלה מכפר את הקדש ואת אהל מועד ואת המזבח והקריב את השעיר החי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 21, 'וסמך אהרן את שתי ידיו על ראש השעיר החי והתודה עליו את כל עונות בני ישראל ואת כל פשעיהם לכל חטאתם ונתן אתם על ראש השעיר ושלח ביד איש עתי המדברה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 22, 'ונשא השעיר עליו את כל עונתם אל ארץ גזרה ושלח את השעיר במדבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 23, 'ובא אהרן אל אהל מועד ופשט את בגדי הבד אשר לבש בבאו אל הקדש והניחם שם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 24, 'ורחץ את בשרו במים במקום קדוש ולבש . בגדיו ויצא ועשה את עלתו ואת עלת העם וכפר בעדו ובעד העם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 25, 'ואת חלב החטאת יקטיר המזבחה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 26, 'והמשלח את השעיר לעזאזל יכבס בגדיו ורחץ את בשרו במים ואחרי כן יבוא אל המחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 27, 'ואת פר החטאת ואת שעיר החטאת אשר הובא את דמם לכפר בקדש יוצא אל מחוץ למחנה ושרף באש את עורתם ואת בשרם ואת פרשם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 28, 'והשרף אתם יכבס בגדיו ורחץ את בשרו במים ואחרי כן יבוא אל המחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 29, 'והיתה לכם לחקת עולם בחדש השביעי בעשור לחדש תענו את נפשתיכם וכל מלאכה לא תעשו האזרח והגר הגר בתוככם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 30, 'כי ביום הזה יכפר עליכם לטהר אתכם מכל חטאתיכם לפני יהוה תטהרו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 31, 'שבת שבתון הוא לכם ועניתם את נפשתיכם חקת עולם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 32, 'יכפר הכהן אשר ימשח אתו ואשר ימלא את ידו לכהן תחת אביו ולבש את בגדי הבד בגדי הקדש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 33, 'וכפר את מקדש הקדש ואת אהל מועד ואת המזבח יכפר על הכהנים ועל כל עם הקהל יכפר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 16, 34, 'והיתה זאת לכם לחקת עולם לכפר על בני ישראל מכל חטאתם אחת בשנה ויעש כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 17, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 17, 2, 'דבר אל אהרן ואל בניו ואל כל בני ישראל ואמרת אליהם זה הדבר אשר צוה יהוה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 17, 3, 'איש איש מבית ישראל . . . אשר ישחט שור או כשב או עז במחנה או אשר ישחט מחוץ למחנה . . . . . . . . . . . . . . . . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 17, 4, 'ואל פתח אהל מועד לא הביאו לעשות אתו עלה או שלמים ליהוה לרצונכם לריח ניחח וישחטהו בחוץ ואל פתח אהל מועד לא הביאו להקריבו קרבן ליהוה לפני משכן יהוה דם יחשב לאיש ההוא דם שפך ונכרת האיש ההוא מקרב עמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 17, 5, 'למען אשר יביאו בני ישראל את זבחיהם אשר הם זבחים על פני השדה והביאום ליהוה אל פתח אהל מועד אל הכהן וזבחו זבחי שלמים ליהוה אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 17, 6, 'וזרק הכהן את הדם על מזבח יהוה אשר פתח אהל מועד והקטיר החלב לריח ניחח ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 17, 7, 'ולא יזבחו עוד את זבחיהם לשערים אשר הם זנים אחריהם חקת עולם תהיה זאת להם לדרתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 17, 8, 'ואליהם תאמר איש איש מבית ישראל ומן הגר אשר יגור בתוכם אשר יעשה עלה או זבח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 17, 9, 'ואל פתח אהל מועד לא יביאנו לעשות אתו ליהוה ונכרת האיש ההוא מעמיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 17, 10, 'ואיש איש מבית ישראל ומן הגר אשר יגור בתוכם אשר יאכל כל דם ונתתי את פני בנפש האכלת את הדם והכרתי אתה מקרב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 17, 11, 'עמה כי נפש הבשר בדם הוא ואני נתתיו לכם על המזבח לכפר על נפשתיכם כי הדם הוא בנפש יכפר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 17, 12, 'על כן אמרתי לבני ישראל כל נפש מכם לא תאכל דם והגר הגר בתוככם לא יאכל דם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 17, 13, 'ואיש איש מבית ישראל ומן הגר הגר בתוכם אשר יצוד ציד חיה או עוף אשר יאכל ושפך את דמו וכסהו בעפר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 17, 14, 'כי נפש כל בשר דמו בנפשו הוא ואמר לבני ישראל דם כל בשר לא תאכלו כי נפש כל בשר דמו הוא כל אכלו יכרת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 17, 15, 'וכל הנפש אשר תאכל נבלה וטרפה באזרח ובגר וכבס בגדיו ורחץ במים וטמא עד הערב . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 17, 16, 'ואם לא יכבס ובשרו לא ירחץ ונשא עונו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 2, 'דבר אל בני ישראל ואמרת אליהם אני יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 3, 'כמעשה ארץ מצרים אשר ישבתם בה לא תעשו וכמעשה ארץ כנען אשר אני מביא אתכם שמה לא תעשו ובחקתיהם לא תלכו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 4, 'את משפטי תעשו ואת חקתי תשמרו ללכת בהם אני יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 5, 'ושמרתם את חקתי ואת משפטי אשר יעשה אתם האדם וחיה בהם אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 6, 'איש איש אל כל שאר בשרו לא תקרבו לגלות ערוה אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 7, 'ערות אביך וערות אמך לא תגלה אמך היא לא תגלה ערותה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 8, 'ערות אשת אביך לא תגלה ערות אביך היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 9, 'ערות אחתך בת אביך או בת אמך מולדת בית או מולדת חוץ לא תגלה ערותה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 10, 'ערות בת בנך או בת בתך לא תגלה ערותן כי ערותך הנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 11, 'ערות בת אשת אביך מולדת אביך אחתך היא לא תגלה ערותה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 12, 'ערות אחות אביך לא תגלה שאר אביך היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 13, 'ערות אחות אמך לא תגלה . שאר אמך היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 14, 'ערות אחי אביך לא תגלה ואל אשתו לא תקרב דדתך היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 15, 'ערות כלתך לא תגלה אשת בנך היא לא תגלה ערותה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 16, 'ערות אשת אחיך לא תגלה ערות אחיך היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 17, 'ערות אשה ובתה לא תגלה את בת בנה ואת בת בתה לא תקח לגלות ערותה שארה הנה זמה היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 18, 'ואשה על אחתה לא תקח לצרר לגלות ערותה עליה בחייה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 19, 'ואל אשה בנדת טמאתה לא תקרב לגלות ערותה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 20, 'ואל אשת עמיתך לא תתן שכבתך לזרע לטמאה בה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 21, 'ומזרעך לא תתן להעביר למלך ולא תחלל את שם אלהיך אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 22, 'ואת זכר לא תשכב משכבי אשה תועבה היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 23, 'ובכל בהמה לא תתן שכבתך לטמאה בה ואשה לא תעמד לפני בהמה לרבעה תבל היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 24, 'אל תטמאו בכל אלה כי בכל אלה נטמאו הגוים אשר אני משלח מפניכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 25, 'ותטמא הארץ ואפקד עונה עליה ותקיא הארץ את ישביה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 26, 'ושמרתם . את חקתי ואת משפטי ולא תעשו מכל התועבת האלה האזרח והגר הגר בתוככם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 27, 'כי את כל התועבת האלה עשו . . . . . . . . אנשי הארץ אשר לפניכם ותטמא הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 28, 'ולא תקיא הארץ אתכם בטמאכם אתה כאשר קאה את הגוי אשר לפניכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 29, 'כי כל אשר יעשה מכל התועבת האלה ונכרתו הנפשות העשות מקרב עמם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 18, 30, 'ושמרתם את משמרתי לבלתי עשות מחקות התועבת אשר נעשו לפניכם ולא תטמאו בהן . אני יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 2, 'דבר אל כל עדת בני ישראל ואמרת אליהם קדשים תהיו כי קדוש אני יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 3, 'איש אמו ואביו תיראו ואת שבתתי תשמרו אני יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 4, 'אל תפנו אל האלילים ואלהי מסכה לא תעשו לכם אני יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 5, 'וכי תזבחו זבח שלמים ליהוה לרצונכם תזבחהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 6, 'ביום זבחכם יאכל וממחרת והנותר עד יום השלישי באש ישרף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 7, 'ואם אכל יאכל ביום השלישי פגול הוא לא ירצה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 8, 'ואכלו עונו ישא כי את קדש יהוה חלל ונכרתה הנפש ההיא מעמיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 9, 'ובקצרכם את קציר ארצכם לא תכלה פאת שדך לקצר ולקט קצירך לא תלקט ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 10, 'וכרמך לא תעלל ופרט כרמך לא תלקט לעני ולגר תעזב אתם אני יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 11, 'לא תגנבו ולא תכחשו ולא תשקרו איש בעמיתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 12, 'ולא תשבעו בשמי לשקר וחללת את שם אלהיך אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 13, 'לא תעשק את רעך ולא תגזל ולא תלין פעלת שכיר אתך עד בקר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 14, 'לא תקלל חרש ולפני עור לא תתן מכשול ויראת מאלהיך אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 15, 'לא תעשה עול במשפט לא תשא פני דל ולא תהדר פני גדול בצדק תשפט את עמיתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 16, 'לא תלך רכיל בעמך לא תעמד על דם רעך אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 17, 'לא תשנא את אחיך בלבבך הוכח תוכיח את עמיתך ולא תשא עליו חטא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 18, 'לא תקום ולא תטר את בני עמך ואהבת לרעך כמוך אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 19, 'את חקתי תשמרו בהמתך לא תרביע כלאים ושדך לא תזרע כלאים ובגד כלאים שעטניז לא יעלה עליך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 20, 'ואיש כי ישכב את אשה שכבת זרע והיא שפחה נחרפת לאיש והפדה לא נפדתה או חפשה לא נתן לה בקרת תהיה לו לא יומת כי לא חפשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 21, 'והביא את אשמו ליהוה אל פתח אהל מועד איל אשם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 22, 'וכפר עליו הכהן באיל האשם לפני יהוה על חטאתו אשר חטא ונסלח לו מחטאתו אשר חטא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 23, 'וכי תבאו אל הארץ ונטעתם כל עץ מאכל וערלתם את ערלתו את פריו שלש שנים יהיה לכם ערלים לא יאכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 24, 'ובשנה הרביעית יהיו כל פריו קדש חללים ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 25, 'ובשנה החמשית תאכלו את פריו להסיף לכם תבואתו אני יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 26, 'לא תאכלו על הדם ולא תנחשו ולא תעננו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 27, 'ולא תקיפו את פאת ראשכם ולא תשחיתו את פאת זקנכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 28, 'ושרטה לנפש לא תתנו בבשרכם וכתבת קעקע לא תתנו בכם אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 29, 'אל תחלל את בתך להזנותה ולא תזנה הארץ ומלאה הארץ זמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 30, 'את שבתתי תשמרו ומקדשי תיראו אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 31, 'אל תפנו אל האבות ואל הידענים אל תבקשו לטמאה בהם אני יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 32, 'מפני שיבה תקום והדרת פני זקן ויראת מאלהיך אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 33, 'וכי יגור אתכם גר בארצכם לא תונו אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 34, 'כאזרח מכם יהיה לכם הגר הגר אתכם ואהבת לו כמוך כי גרים הייתם בארץ מצרים אני יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 35, 'לא תעשו עול במשפט במדה במשקל ובמשורה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 36, 'מאזני צדק אבני צדק איפת צדק והין צדק יהיה לכם אני יהוה אלהיכם אשר הוצאתי אתכם מארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 19, 37, 'ושמרתם את כל חקתי ואת כל משפטי ועשיתם אתם אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 2, 'ואל בני ישראל תדבר איש איש מבית ישראל ומן הגר הגר בישראל אשר יתן מזרעו למלך מות יומת עם הארץ ירגמהו באבן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 3, 'ואני אתן את פני באיש ההוא והכרתי אתו מקרב עמו כי מזרעו נתן למלך למען טמא את מקדשי וחלל את שם קדשי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 4, 'ואם העלם יעלמו עם הארץ את עיניהם מן האיש ההוא בתתו מזרעו למלך לבלתי המית אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 5, 'ושמתי אני את פני באיש ההוא ובמשפחתו והכרתי אתו ואת כל הזנים אחריו לזנות אחרי המלך מקרב עמם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 6, 'והנפש אשר תפנה אל האבות ואל הידענים לזנות אחריהם ונתתי את פני בנפש ההיא והכרתי אתה מקרב עמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 7, '. והייתם קדשים כי אני יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 8, 'ושמרתם את כל חקתי ועשיתם אתם אני יהוה מקדשכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 9, 'כי איש איש אשר יקלל את אביו ואת אמו מות יומת אביו ואמו קלל דמו בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 10, 'ואיש אשר ינאף את אשת איש אשר ינאף את אשת רעהו מות יומת הנאף והנאפת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 11, 'ואיש אשר ישכב את אשת אביו ערות אביו גלה מות יומתו שניהם דמיהם בם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 12, 'ואיש אשר ישכב את כלתו מות יומתו שניהם תבל עשו דמיהם בם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 13, 'ואיש אשר ישכב את זכר משכבי אשה תועבה עשו מות יומתו שניהם דמיהם בם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 14, 'ואיש אשר יקח את אשה ואת אמה זמה היא באש ישרפו אתו ואתהן ולא תהיה זמה בתוככם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 15, 'ואיש אשר יתן שכבתו בבהמה מות יומת ואת הבהמה תהרגו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 16, 'ואשה אשר תקרב אל כל בהמה לרבעה אתה והרגת את האשה ואת הבהמה מות יומתו דמיהם בם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 17, 'ואיש אשר יקח את אחתו בת אביו או בת אמו וראה את ערותה והיא תראה את ערותו חסד הוא ונכרתו לעיני בני עמם ערות אחתו גלה עונו ישא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 18, 'ואיש אשר ישכב את אשה דוה וגלה את ערותה את מקור הערוה והיא גלתה את מקור דמיה ונכרתו שניהם מקרב עמם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 19, 'ערות אחות אביך ואחות אמך לא תגלה כי את שאר הערוה עונם ישאו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 20, 'ואיש אשר ישכב את דדתו את ערות דדו גלה חטאם ישאו עררים יומתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 21, 'ואיש אשר יקח את אשת אחיו נדה היא ערות אחיו גלה עררים יהיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 22, 'ושמרתם את כל חקתי ואת כל משפטי ועשיתם אתם ולא תקיא הארץ אתכם אשר אני מביא אתכם שמה לשבת בה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 23, 'ולא תלכו בחקות הגוים אשר אני משלח מפניכם כי את כל אלה עשו ואקץ בם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 24, 'ואמר לכם אתם תירשו את אדמתם ואני אתננה לכם לרשת אתה ארץ זבת חלב ודבש אני יהוה אלהיכם אשר הבדלתי אתכם מן העמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 25, 'והבדלתם בין הבהמה הטהרה לטמאה ובין העוף הטמא לטהור ולא תשקצו את נפשתיכם בבהמה ובעוף ובכל אשר תרמש האדמה אשר הבדלתי לכם לטמאה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 26, 'והייתם לי קדשים כי . אני יהוה ואבדיל אתכם מן העמים להיות לי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 20, 27, 'ואיש או אשה אשר יהיה בהם אוב או ידעני מות יומתו באבנים תרגמום . דמיהם בם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 1, 'ויאמר יהוה אל משה אמר אל הכהנים בני אהרן ואמרת אליהם לנפש לא יטמא בעמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 2, 'כי אם לשארו הקרוב אליו לאביו ולאמו לבנו ולבתו לאחיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 3, 'ולאחתו הבתולה הקרובה אליו אשר לא היתה לאיש לה יטמא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 4, 'לא יטמא בעל בעמו להחלו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 5, 'ולא יקרחו קרחה בראשם ופאת זקנם לא יגלחו ובבשרם לא ישרטו שרטה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 6, 'קדשים יהיו לאלהיהם ולא יחללו את שם אלהיהם כי את אשי יהוה לחם אלהיהם הם מקרבים והיו קדשים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 7, 'אשה זונה וחללה לא יקחו ואשה גרושה מאישה לא יקחו כי קדוש הוא לאלהיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 8, 'וקדשתו כי את לחם אלהיך הוא מקריב קדוש יהיה לך כי קדוש אני יהוה מקדשם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 9, 'ובת איש כהן כי תחל לזנות את . אביה היא מחללת באש תשרף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 10, 'והכהן הגדול מאחיו אשר יוצק על ראשו שמן המשחה ומלא . ידו ללבש את הבגדים את ראשו לא יפרע ובגדיו לא יפרם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 11, 'ועל כל נפשות מת לא יבוא לאביו ולאמו לא יטמא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 12, 'ומן המקדש לא יצא ולא יחלל את מקדש אלהיו כי נזר שמן משחת אלהיו עליו אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 13, 'והוא אשה בבתליה יקח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 14, 'אלמנה וגרושה וחללה וזונה את אלה לא יקח כי אם בתולה מעמו יקח אשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 15, 'ולא יחלל זרעו בעמו כי אני יהוה מקדשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 16, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 17, 'דבר אל אהרן לאמר איש מזרעך לדרתם אשר יהיה בו מום לא יקרב להגיש לחם אלהיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 18, 'כי כל איש אשר יהיה בו מום לא יקרב איש עור או פסח או חרום או שרוע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 19, 'או איש אשר יהיה בו שבר רגל או שבר יד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 20, 'או גבן או דק או תבלל בעיניו או גרב או ילפת או מרוח אשך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 21, 'כל איש אשר בו מום מזרע אהרן הכהן לא יגיש . את אשי יהוה מום בו לא יגש להקריב את לחם אלהיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 22, '. . מקדשי הקדשים ומן הקדשים יאכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 23, 'אך אל הפרכת לא יבוא ואל המזבח לא יגש כי מום בו ולא יחלל את מקדשי כי אני יהוה מקדשם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 21, 24, 'וידבר משה אל אהרן ואל בניו ואל כל בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 2, 'דבר אל אהרן ואל בניו וינזרו מקדשי בני ישראל ולא יחללו את שם קדשי אשר הם מקדשים לי אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 3, 'אמר אליהם לדרתיכם כל איש אשר יקרב מכל זרעכם אל הקדשים אשר יקדישו בני ישראל ליהוה וטמאתו עליו ונכרתה הנפש ההיא מלפני אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 4, 'איש איש מזרע אהרן והוא צרוע או זב בקדשים לא יאכל עד אשר יטהר והנגע בכל טמא נפש או איש אשר תצא ממנו שכבת זרע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 5, 'או איש אשר יגע בכל שרץ טמא אשר יטמא לו או באדם אשר יטמא לו לכל טמאתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 6, 'נפש אשר תגע בו וטמא עד הערב ולא יאכל מן הקדשים כי אם רחץ בשרו במים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 7, 'ובא השמש וטהר ואחר יאכל מן הקדשים כי לחמו הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 8, 'נבלה וטרפה לא יאכלו לטמאה בה אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 9, 'ושמרו את משמרתי ולא ישאו עליו חטא ומתו בו כי יחללהו אני יהוה מקדשם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 10, 'וכל זר לא יאכל קדש תושב כהן ושכיר לא יאכל קדש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 11, 'וכהן כי יקנה נפש קנין כספו הוא יאכל בו וילדי ביתו הם יאכלו בלחמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 12, 'ובת כהן כי תהיה לאיש זר היא בתרומת הקדשים לא תאכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 13, 'ובת כהן כי תהיה אלמנה וגרושה וזרע אין לה ושבה אל בית אביה כנעריה מלחם אביה תאכל וכל זר לא יאכל בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 14, 'ואיש כי יאכל קדש בשגגה ויסף חמישתו עליו ונתן לכהן את הקדש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 15, 'ולא יחללו את קדשי בני ישראל . אשר ירימו ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 16, 'והשיאו אתם עון אשמה באכלם את קדשיהם כי אני יהוה מקדשם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 17, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 18, 'דבר אל אהרן ואל בניו ואל כל בני ישראל ואמרת אליהם איש איש מבית ישראל ומן הגר הגר בישראל אשר יקריב קרבנו לכל נדריהם . ולכל נדבתם אשר יקריבו ליהוה לעלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 19, 'לרצונכם תמים זכר בבקר בכשבים ובעזים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 20, 'כל אשר בו מום לא תקריבו כי לא לרצון יהיה לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 21, 'ואיש כי יקריב זבח שלמים ליהוה לפלא נדר או נדבה בבקר או . בצאן תמים יהיה לרצון וכל מום לא יהיה בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 22, 'עורת או שבור או חרוץ או יבלת או גרב או ילפת . . . לא תקריבו אלה ליהוה ואשה לא תתנו מהם על המזבח ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 23, 'ושור ושה שרוע וקלוט נדבה תעשו אתו ולנדר לא ירצה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 24, 'מעוך וכתות ונתוק וכרות לא תקריבו . ליהוה ובארצכם לא תעשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 25, 'ומיד בן נכר לא תקריבו את לחם אלהיכם מכל אלה כי משחתים בהם מום בם לא ירצו לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 26, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 27, 'שור או כשב או עז כי יולד והיה שבעת ימים תחת אמו ומיום השמיני והלאה ירצה לקרבן אשה ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 28, 'ושור . ושה אתו ואת בנו לא תשחטו ביום אחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 29, 'וכי תזבחו זבח תודה ליהוה לרצונכם תזבחהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 30, 'ביום ההוא יאכל לא תותירו ממנו עד בקר אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 31, 'ושמרתם את מצותי ועשיתם אתם . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 32, 'ולא תחללו את שם קדשי ונקדשתי בתוך בני ישראל אני יהוה מקדשם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 22, 33, 'המוצא אתכם מארץ מצרים להיות לכם לאלהים אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 2, 'דבר אל בני ישראל ואמרת אליהם מועדי יהוה אשר תקראו אתם מקראי קדש אלה הם מועדי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 3, 'ששת ימים יעשה מלאכה וביום השביעי שבת שבתון מקרא קדש כל מלאכה לא תעשו שבת היא ליהוה בכל מושבתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 4, 'ואלה מועדי יהוה מקראי קדש אשר תקראו אתם במועדם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 5, 'בחדש הראישון בארבעה עשר יום לחדש בין הערבים פסח ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 6, 'ובחמשה עשר יום לחדש הזה חג המצות ליהוה שבעת ימים מצות תאכלו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 7, 'ביום הראישון מקרא קדש יהיה לכם כל מלאכת עבדה לא תעשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 8, 'והקרבתם אשה ליהוה שבעת ימים וביום השביעי מקרא קדש כל מלאכת עבדה לא תעשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 9, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 10, 'דבר אל בני ישראל ואמרת אליהם כי תבאו אל הארץ אשר אני נתן לכם וקצרתם את קצירה והבאתם את העמר ראשית קצירכם אל הכהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 11, 'והניף את העמר לפני יהוה לרצונכם ממחרת השבת יניפנו הכהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 12, 'ועשיתם ביום הנפכם את העמר כבש תמים בן שנתו לעלה ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 13, 'ומנחתו שני עשרנים סלת בלולה בשמן אשה ליהוה ריח ניחח ונסכו יין רבעית ההין ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 14, 'ולחם וקלי וכרמל לא תאכלו עד עצם היום הזה עד הבאכם את קרבן אלהיכם חקת עולם לדרתיכם בכל מושבתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 15, 'וספרתם לכם ממחרת השבת מיום הבאכם את עמר התנופה שבע שבתות תמימות תהיינה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 16, 'עד ממחרת השבת השביעת תספרו חמשים יום והקרבתם מנחה חדשה ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 17, 'ממושבתיכם תביאו לחם תנופה שתים חלות שני עשרנים סלת תהיינה חמץ תאפינה בכרים ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 18, 'והקרבתם על הלחם שבעה כבשים תמימם בני שנה ופר בן בקר אחד ואילים שנים תמימם יהיו עלה ליהוה ומנחתם ונסכיהם אשה ריח ניחח ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 19, 'ועשיתם שעיר עזים אחד לחטאת ושני כבשים בני שנה לזבח השלמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 20, 'והניף הכהן אתם על לחם הבכרים תנופה לפני יהוה על שני הכבשים קדש יהיו ליהוה לכהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 21, 'וקראתם בעצם היום הזה מקרא קדש יהיה לכם כל מלאכת עבדה לא תעשו חקת עולם בכל מושבתיכם לדרתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 22, 'ובקצירכם את קציר ארצכם לא תכלה פאת שדך לקצר ולקט קצירך לא תלקט לעני ולגר תעזב אתם אני יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 23, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 24, 'דבר אל בני ישראל לאמר בחדש השביעי באחד לחדש יהיה לכם שבתון זכרון תרועה מקרא קדש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 25, 'כל מלאכת עבדה לא תעשו והקרבתם אשה ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 26, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 27, 'אך בעשור לחדש השביעי הזה יום כפרים הוא מקרא קדש יהיה לכם ועניתם את נפשתיכם והקרבתם אשה ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 28, 'וכל מלאכה לא תעשו בעצם היום הזה כי יום כפרים הוא לכפר עליכם לפני יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 29, 'כי כל הנפש אשר לא תענה בעצם היום הזה ונכרתה מעמיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 30, 'וכל הנפש אשר תעשה כל מלאכה בעצם היום הזה והאבדתי את הנפש ההיא מקרב עמיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 31, 'וכל מלאכה לא תעשו חקת עולם לדרתיכם בכל מושבתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 32, 'שבת שבתון הוא לכם ועניתם את נפשתיכם בתשעה לחדש בערב מערב עד ערב תשביתו שבתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 33, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 34, 'דבר אל בני ישראל לאמר בחמשה עשר יום לחדש השביעי הזה חג הסכות שבעת ימים ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 35, 'ביום הראישון מקרא קדש כל מלאכת עבדה לא תעשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 36, 'שבעת ימים תקריבו אשה ליהוה ביום השמיני מקרא קדש יהיה לכם והקרבתם אשה ליהוה עצרת היא וכל מלאכת עבדה לא תעשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 37, 'אלה מועדי יהוה אשר תקראו אתם מקראי קדש להקריב אשה ליהוה עלה ומנחה זבח ונסכים דבר יום ביומו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 38, 'מלבד שבתות יהוה ומלבד כל מתנתיכם ומלבד כל נדריכם ומלבד כל נדבתיכם אשר תתנו ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 39, 'אך בחמשה עשר יום לחדש השביעי באספכם את תבואת הארץ תחגו את חג יהוה שבעת ימים ביום הראישון שבתון וביום השמיני שבתון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 40, 'ולקחתם לכם ביום הראישון פרי עץ הדר כפות תמרים וענפי עץ עבות וערבי נחל ושמחתם לפני יהוה אלהיכם שבעת ימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 41, 'וחגתם אתו חג ליהוה שבעת ימים בשנה חקת עולם לדרתיכם בחדש השביעי תחגו אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 42, 'בסכות תשבו שבעת ימים כל האזרח בישראל ישב בסכות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 43, 'למען ידעו דרתיכם כי בסכות הושבתי את בני ישראל בהוצאי אתם מארץ מצרים אני יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 23, 44, 'וידבר משה את מועדי יהוה אל בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 2, 'צוי את בני ישראל ויקחו אליך שמן זית זך כתית למאור להעלות נר תמיד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 3, 'מחוץ לפרכת העדות באהל מועד יערך אתו אהרן ובניו מערב עד בקר לפני יהוה תמיד חקת עולם לדרתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 4, 'על המנורה הטהרה יערך את הנרות לפני יהוה עד בקר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 5, 'ולקחת סלת ואפית אתה שתים עשרה חלות שני עשרנים יהיה החלה האחת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 6, 'ושמת אתם שתי מערכות שש המערכת על השלחן הטהור לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 7, 'ונתתה על המערכת לבונה זכה והיתה ללחם לאזכרה אשה ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 8, 'ביום השבת ביום השבת יערכנו לפני יהוה תמיד מאת בני ישראל ברית עולם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 9, 'והיתה לאהרן ולבניו ואכלוה במקום קדוש כי קדש קדשים היא לו מאשי יהוה חק עולם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 10, 'ויצא בן אשה ישראלית והוא בן איש מצרי בתוך בני ישראל וינצו במחנה בן הישראלית ואיש ישראלי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 11, 'ויקב בן האשה הישראלית את . השם ויקלל ויביאו אתו אל משה ושם אמו שלומית בת דברי למטה דן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 12, 'וינחהו . במשמר לפרש להם על פי יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 13, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 14, 'הוציא את המקלל אל מחוץ למחנה וסמכו כל השמעים את ידיהם על ראשו ורגמו אתו כל העדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 15, 'ואל בני ישראל תדבר לאמר איש איש כי יקלל אלהיו ונשא חטאו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 16, 'ונקב שם יהוה מות יומת רגם ירגמו בו כל העדה כגר כאזרח בנקבו השם יומת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 17, 'ואיש כי יכה כל נפש אדם מות יומת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 18, 'ומכה נפש בהמה ישלמנה נפש תחת נפש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 19, 'ואיש כי יתן מום בעמיתו כאשר עשה כן יעשה לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 20, 'שבר תחת שבר עין תחת עין שן תחת שן כאשר יתן מום באדם כן ינתן בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 21, 'ומכה בהמה ישלמנה ומכה אדם יומת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 22, 'משפט אחד יהיה לכם כגר כאזרח יהיה כי אני יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 24, 23, 'וידבר משה אל בני ישראל ויוציאו את המקלל אל מחוץ למחנה וירגמו אתו אבן ובני ישראל עשו כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 1, 'וידבר יהוה אל משה בהר סיני לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 2, 'דבר אל בני ישראל ואמרת אליהם כי תבאו אל הארץ אשר אני נתן לכם ושבתה הארץ שבת ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 3, 'שש שנים תזרע שדך ושש שנים תזמר כרמך ואספת את תבואתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 4, 'ובשנה השביעת שבת שבתון יהיה לארץ שבת ליהוה שדך לא תזרע וכרמך לא תזמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 5, 'ואת ספחי קצירך לא תקצר ואת ענבי נזירך לא תבצר שנת שבתון יהיה לארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 6, 'והיתה שבת הארץ לכם לאכלה לך ולעבדיך ולאמהתיך ולשכיריך ולתושביך הגרים עמך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 7, 'ולבהמתך ולחיה אשר בארצך תהיה כל תבואתה לאכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 8, 'וספרת לך שבע שבתות שנים שבע שנים שבע פעמים והיו לך ימי שבע שבתות השנים תשע וארבעים שנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 9, 'והעברת שופר תרועה בחדש השביעי בעשור לחדש ביום הכפרים תעבירו שופר בכל ארצכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 10, 'וקדשתם את שנת החמשים שנה וקראתם דרור בארץ לכל ישביה יובל הוא תהיה לכם ושבתם איש אל אחזתו ואיש אל משפחתו תשבו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 11, 'יובל הוא שנת החמשים שנה תהיה לכם לא תזרעו ולא תקצרו את ספחיה ולא תבצרו את נזריה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 12, 'כי יובל הוא קדש תהיה לכם מן השדה תאכלו את תבואתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 13, 'בשנת היובל הזאת תשבו איש אל אחזתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 14, 'וכי תמכר ממכר לעמיתך או קנה מיד עמיתך אל תונו איש את אחיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 15, 'במספר שנים אחר היובל תקנה מאת עמיתך במספר שני תבואת ימכר לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 16, 'לפי רב השנים תרבה מקנתו ולפי מעט השנים תמעיט מקנתו כי מספר תבואת הוא מכר לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 17, 'ולא תונו איש את עמיתו ויראת מאלהיך כי אני יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 18, 'ועשיתם את חקתי ואת משפטי תשמרו ועשיתם אתם וישבתם על הארץ לבטח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 19, 'ונתנה הארץ פריה ואכלתם לשבע וישבתם לבטח עליה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 20, 'וכי תאמרו מה נאכל בשנה השביעית הן לא נזרע ולא נאסף את תבואתנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 21, 'וצויתי את ברכתי לכם בשנה הששית ועשתה את תבואתה לשלש השנים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 22, 'וזרעתם את השנה השמינית ואכלתם מן התבואתה ישן עד השנה התשעית עד בא תבואתה תאכלו ישן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 23, 'והארץ לא תמכר לצמיתת כי לי הארץ כי גרים ותושבים אתם עמדי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 24, 'ובכל ארץ אחזתכם גאלה תתנו לארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 25, 'וכי ימך אחיך ומכר מאחזתו ובא גאלו הקרוב אליו וגאל את ממכר אחיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 26, 'ואיש כי לא יהיה לו גאל והשיגה ידו ומצא כדי גאלתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 27, 'וחשב את שני ממכרו והשיב את העדף לאיש אשר מכר לו ושב לאחזתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 28, 'ואם לא מצא ידו די השיב לו והיה ממכרו ביד הקנה אתו עד שנת היובל ויצא ביובל ושב לאחזתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 29, 'ואיש כי ימכר בית מושב עיר חומה והיתה גאלתו עד תם שנת ממכרו ימים תהיה גאלתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 30, 'ואם לא יגאל עד מלאת לו שנה תמימה וקם הבית אשר בעיר אשר לו חומה לצמיתת לקנה אתו לדרתיו לא יצא ביובל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 31, 'ובתי החצרים אשר אין להם חומה סביב על שדה הארץ יחשבו גאלה תהיה לו וביובל יצא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 32, 'וערי הלוים בתי ערי אחזתם גאלת עולם תהיה ללוים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 33, 'ואשר יגאל מן הלוים ויצא ממכר בית ועיר אחזתו ביובל כי בתי ערי הלוים היא אחזתם בתוך בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 34, 'ושדה מגרש עריהם לא ימכרו כי אחזת עולם היא להם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 35, 'וכי ימך אחיך ומטה ידו עמך וחזקת בו גר ותושב וחי אחיך עמך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 36, 'אל תקח מאתו נשך ותרבית ויראת מאלהיך וחי אחיך עמך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 37, 'את כספך לא תתן לו בנשך ובתרבית לא תתן אכלך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 38, 'אני יהוה אלהיכם אשר הוצאתי אתכם מארץ מצרים לתת לכם את ארץ כנען להיות לכם לאלהים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 39, 'וכי ימך אחיך עמך ונמכר לך לא תעבד בו עבדת עבד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 40, 'כשכיר כתושב יהיה עמך עד שנת היובל יעבד עמך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 41, 'ויצא מעמך הוא ובניו עמו ושב אל משפחתו ואל אחזת אבתיו ישוב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 42, 'כי עבדי הם אשר הוצאתי אתם מארץ מצרים לא ימכרו ממכרת עבד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 43, 'לא תרדה בו בפרך ויראת מאלהיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 44, 'ועבדיך ואמהתיך אשר יהיו לך מאת הגוים אשר סביבתיכם מהם תקנהו עבד ואמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 45, 'וגם מבני התושבים הגרים עמכם מהם תקנהו וממשפחתם אשר עמכם אשר הולדו בארצכם והיו לכם לאחזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 46, 'והתנחלתם אתם לבניכם אחריכם לרשת אחזה לעלם בהם תעבדו ובאחיכם בני ישראל איש באחיו לא תרדה בו בפרך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 47, 'וכי תשיג יד גר ותושב עמך ומך אחיך עמו ונמכר לגר ולתושב עמך או לעקר משפחת גר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 48, 'אחרי נמכר גאלה תהיה לו אחד מאחיו יגאלנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 49, 'או דדו או בן דדו יגאלנו או משאר בשרו ממשפחתו יגאלנו או השיגה ידו ונגאל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 50, 'וחשב עם קנהו משנת המכרו לו עד שנת היובל והיה כסף ממכרו במספר שנים כימי שכיר יהיה עמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 51, 'אם עוד רבות בשנים לפיהן ישיב גאלתו מכסף מקנתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 52, 'ואם מעט נשאר בשנים עד שנת היובל וחשב לו כפי שניו ישיב את גאלתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 53, 'כשכיר שנה בשנה יהיה עמו לא ירדנו בפרך לעיניך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 54, 'ואם לא יגאל באלה ויצא בשנת היובל הוא ובניו עמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 25, 55, 'כי לי בני ישראל עבדים עבדי הם אשר הוצאתי אתם מארץ מצרים אני יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 1, 'לא תעשו לכם אלילים ופסל ומצבה לא תקימו לכם ואבן משכית לא תתנו בארצכם להשתחות עליה כי אני יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 2, 'את שבתתי תשמרו ומקדשי תיראו אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 3, 'אם בחקתי תלכו ואת מצותי תשמרו ועשיתם אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 4, 'ונתתי גשמיכם בעתם ונתנה הארץ יבולה ועץ השדה יתן פריו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 5, 'והשיג לכם דיש את בציר ובציר ישיג את הזרע ואכלתם לחמכם לשבע וישבתם לבטח בארצכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 6, 'ונתתי שלום בארץ ושכבתם ואין מחריד והשבתי חיה רעה מן הארץ וחרב לא תעבר בארצכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 7, 'ורדפתם את איביכם ונפלו לפניכם לחרב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 8, 'ורדפו מכם חמשה מאה ומאה מכם רבבה ירדפו ונפלו איביכם לפניכם לחרב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 9, 'ופניתי אליכם והפריתי אתכם והרביתי אתכם והקמתי את בריתי אתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 10, 'ואכלתם ישן נושן וישן מפני חדש תוציאו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 11, 'ונתתי משכני בתוככם ולא תגעל נפשי אתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 12, 'והתהלכתי בתוככם והייתי לכם לאלהים ואתם תהיו לי לעם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 13, 'אני יהוה אלהיכם אשר הוצאתי אתכם מארץ מצרים מהיות להם עבדים ואשברה מטות עולכם ואולך אתכם קוממית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 14, 'ואם לא תשמעו לי ולא תעשו את כל המצות האלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 15, 'ואם בחקתי תמאסו ואם את משפטי תגעל נפשכם לבלתי עשות את כל מצותי להפרכם את בריתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 16, 'אף אני אעשה זאת לכם והפקדתי עליכם בחלה את השחפת ואת הקדחת מכלות עינים ומדיבת נפש וזרעתם לריק זרעכם ואכלהו איביכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 17, 'ונתתי פני בכם ונגפתם לפני איביכם ורדו בכם שנאיכם ונסתם ואין רדף אתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 18, 'ואם עד אלה לא תשמעו לי ויספתי ליסרה אתכם שבע על חטאתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 19, 'ושברתי את גאון עזכם ונתתי את שמיכם כברזל ואת ארצכם כנחשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 20, 'ותם לריק כחכם ולא תתן ארצכם את יבולה ועץ השדה לא יתן פריו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 21, 'ואם תלכו עמי קרי ולא תאבו לשמע לי ויספתי עליכם מכה שבע כחטאתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 22, 'ושלחתי בכם את חית השדה ושכלה אתכם והכריתה את בהמתכם והמעיטה אתכם ונשמו דרכיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 23, 'ואם באלה לא תוסרו לי והלכתם עמי קרי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 24, 'והלכתי גם אני עמכם . בקרי והכיתי אתכם גם אני שבע על חטאתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 25, 'והבאתי עליכם חרב נקמת נקם ברית ונאספתם אל עריכם ושלחתי דבר בתוככם ונתתם ביד אויב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 26, 'בשברי לכם מטה לחם ואפו עשר נשים לחמכם בתנור אחד והשיבו לחמכם במשקל ואכלתם ולא תשבעו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 27, 'ואם בזאת לא תשמעו לי והלכתם עמי בקרי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 28, 'והלכתי עמכם בחמת קרי ויסרתי אתכם אף אני שבע על חטאתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 29, 'ואכלתם בשר בניכם ובשר בנתיכם תאכלו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 30, 'והשמדתי את במתיכם והכרתי את חמניכם ונתתי את פגריכם על פגרי גלליכם וגעלה נפשי אתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 31, 'ונתתי את עריכם חרבה והשמתי את מקדשכם ולא אריח בריח ניחחכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 32, 'והשמתי אני את הארץ ושממו עליה איביכם הישבים בה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 33, 'ואתכם אזרה בגוים והרקתי אחריכם חרב והיתה ארצכם שממה ועריכם יהיו חרבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 34, 'אז תרצה הארץ את שבתתיה כל ימי אשמה ואתם בארץ איביכם אז תשבת הארץ והרצתה את שבתתיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 35, 'כל ימי אשמה תשבת את אשר לא שבתה בשבתתיכם בשבתכם עליה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 36, 'והנשארים בכם והבאתי מרך בלבבם בארצות איביהם ורדף אתם קול עלי נדף ונסו מנסת חרב ונפלו ואין רדף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 37, 'וכשלו איש באחיו כמפני חרב ורדף אין ולא תהיה לכם תקומה לפני איביכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 38, 'ואבדתם בגוים ואכלה אתכם ארץ איביכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 39, 'והנשארים בכם ימקו בעונם בארצות איביהם ואף בעונות אבתם אתם ימקו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 40, 'והתודו את עונם ואת עון אבתם במעלם אשר מעלו בי ואף אשר הלכו עמי בקרי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 41, 'אף אני אלך עמם בקרי והבאתי אתם בארץ איביהם או אז יכנע לבבם הערל ואז ירצו את עונם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 42, 'וזכרתי את בריתי יעקב ואף את בריתי יצחק ואף את בריתי אברהם אזכר והארץ אזכר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 43, 'והארץ תעזב מהם והרצתה את שבתתיה באשמה מהם והם ירצו את עונם יען ביען במשפטי מאסו ואת חקתי געלה נפשם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 44, 'ואף גם זאת בהיותם בארצות איביהם לא מאסתים ולא געלתים לכלתם להפר בריתי אתם כי אני יהוה אלהיהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 45, 'וזכרתי להם ברית ראישונים אשר הוצאתי אתם מארץ מצרים לעיני הגוים להיות להם לאלהים אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 26, 46, 'אלה החקים והמשפטים והתורות אשר נתן יהוה בינו ובין בני ישראל בהר סיני ביד משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 2, 'דבר אל בני ישראל ואמרת אליהם איש כי יפלא נדר בערכך נפשות ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 3, 'והיה ערכך הזכר מבן עשרים שנה ועד בן ששים שנה והיה ערכך חמשים שקל כסף בשקל הקדש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 4, 'ואם נקבה היא והיה ערכך שלשים שקל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 5, 'ואם מבן חמש שנים ועד בן עשרים שנה והיה ערכך הזכר עשרים שקלים ולנקבה עשרת שקלים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 6, 'ואם מבן חדש ועד בן חמש שנים והיה ערכך הזכר חמשה שקלים כסף ולנקבה ערכך שלשת שקלים כסף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 7, 'ואם מבן ששים שנה ומעלה אם זכר והיה ערכך חמשה עשר שקל ולנקבה עשרה שקלים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 8, 'ואם מך הוא מערכך והעמידו לפני הכהן והעריך אתו הכהן על פי אשר תשיג יד הנדר יעריכנו הכהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 9, 'ואם בהמה אשר יקריב ממנה קרבן ליהוה כל אשר יתן ממנו ליהוה יהיה קדש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 10, 'לא יחליפנו ולא ימיר אתו טוב ברע או רע בטוב ואם המר ימיר בהמה בבהמה והיה הוא ותמירתו יהיה קדש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 11, 'ואם כל בהמה טמאה אשר לא יקריב ממנה קרבן ליהוה והעמיד את הבהמה לפני הכהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 12, 'והעריך הכהן אתו בין טוב ובין רע כערכך הכהן כן יהיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 13, 'ואם גאל יגאלנו ויסף חמישתו על ערכך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 14, 'ואיש כי יקדש את ביתו קדש ליהוה והעריכו הכהן בין טוב ובין רע כאשר יעריך אתו הכהן כן יקום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 15, 'ואם המקדש יגאל את ביתו ויסף חמישת כסף ערכך עליו והיה לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 16, 'ואם משדה אחזתו יקדש איש ליהוה והיה ערכך לפי זרעו זרע חמר שערים בחמשים שקל כסף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 17, 'ואם משנת היובל יקדש שדהו כערכך יקום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 18, 'ואם אחר היובל יקדיש שדהו וחשב לו הכהן את הכסף על פי השנים הנותרת עד שנת היובל ונגרע מערכך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 19, 'ואם גאל יגאל את השדה המקדש אתו ויסף חמישת כסף ערכך עליו וקם לו ואם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 20, 'לא יגאל את השדה ואם מכר את השדה לאיש אחר לא יגאל עוד והיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 21, 'השדה בצאתו ביובל קדש ליהוה כשדה החרם לכהן תהיה אחזתו ואם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 22, 'את שדה מקנתו אשר לא משדה אחזתו יקדיש ליהוה וחשב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 23, 'לו הכהן את מכסות הערכך עד שנת היובל ונתן את הערכך ביום ההוא קדש ליהוה בשנת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 24, 'היובל ישוב השדה לאשר קנהו מאתו לאשר לו אחזת הארץ וכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 25, 'ערכך יהיה בשקל הקדש עשרים גרה . השקל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 26, 'אך כל בכור אשר יבכר ליהוה בבהמה לא יקדיש איש אתו אם שור אם שה ליהוה הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 27, 'ואם בבהמה הטמאה ופדה בערכך ויסף חמישתו עליו ואם לא יגאל ונמכר בערכך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 28, 'אך כל חרם אשר יחרם איש ליהוה מכל אשר לו מאדם ובהמה ומשדה אחזתו לא ימכר ולא יגאל כל חרם קדש קדשים הוא ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 29, 'כל חרם אשר יחרם מן האדם לא יפדה מות יומת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 30, 'וכל מעשר הארץ מזרע הארץ ומפרי העץ ליהוה הוא קדש ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 31, 'ואם גאל יגאל איש ממעשרו חמשיתו יסף עליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 32, 'וכל מעשר בקר וצאן כל אשר יעבר תחת השבט העשירי יהיה קדש ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 33, 'לא יבקר בין טוב לרע ולא ימירנו ואם המר ימירנו והיה הוא ותמירתו יהיה קדש לא יגאל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (3, 27, 34, 'אלה המצות אשר צוה יהוה את משה אל בני ישראל בהר סיני  ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 1, 'וידבר יהוה אל משה במדבר סיני באהל מועד באחד לחדש השני בשנה השנית לצאתם מארץ מצרים לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 2, 'שאו את ראש . עדת בני ישראל למשפחתם לבית אבתם במספר שמות כל זכר לגלגלתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 3, 'מבן עשרים שנה ומעלה כל יצא צבא בישראל תפקד אתם לצבאתם אתה ואהרן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 4, 'ואתכם יהיו איש איש למטה איש ראש לבית אבתיו הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 5, 'ואלה שמות האנשים אשר יעמדו אתכם לראובן אליצור בן שדיאור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 6, 'לשמעון שלמיאל בן צורישדי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 7, 'ליהודה נחשון בן עמינדב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 8, 'ליששכר נתנאל בן צוער ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 9, 'לזבולן אליאב בן חילן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 10, 'לבני יוסף לאפרים אלישמע בן עמיהוד למנשה גמליאל בן פדהצור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 11, 'לבנימים אבידן בן גדעני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 12, 'לדן אחיעזר בן עמישדי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 13, 'לאשר פגעיאל בן עכרן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 14, 'לגד אליסף בן דעואל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 15, 'לנפתלי אחירע בן עינן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 16, 'אלה קריאי העדה נשיאי מטות אבתם ראשי אלפי ישראל הם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 17, 'ויקח משה ואהרן את האנשים האלה אשר נקבו בשמות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 18, 'ואת כל העדה הקהילו באחד לחדש השני ויתילדו על משפחותם לבית אבתם במספר שמות מבן עשרים שנה ומעלה לגלגלתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 19, 'כאשר צוה יהוה את משה ויפקדם במדבר סיני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 20, 'ויהיו בני ראובן בכור ישראל תולדתם למשפחותם לבית אבתם במספר שמות כל זכר לגלגלתם מבן עשרים שנה ומעלה כל יצא צבא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 21, 'פקדיהם למטה ראובן ששה וארבעים אלף וחמש מאות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 22, 'לבני שמעון תולדתם למשפחותם לבית אבתם פקדיהם במספר שמות לגלגלתם כל זכר מבן עשרים שנה ומעלה כל יצא צבא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 23, 'פקדיהם למטה שמעון תשעה וחמשים אלף ושלש מאות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 24, 'לבני גד תולדתם למשפחותם לבית אבתם במספר שמות מבן עשרים שנה ומעלה כל יצא צבא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 25, 'פקדיהם למטה גד חמשה וארבעים אלף ושש מאות וחמשים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 26, 'לבני יהודה תולדתם למשפחותם לבית אבתם במספר שמות מבן עשרים שנה ומעלה כל יצא צבא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 1, 27, 'פקדיהם למטה יהודה ארבעה ושבעים אלף ושש מאות ');
