INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 3, 35, 'ונשיא בית אב למשפחת מררי צוריאל בן אביחיל על ירך המשכן יחנו צפונה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 3, 36, 'ופקדת משמרת בני מררי קרשי המשכן ובריחיו ועמדיו ואדניו וכל כליו וכל עבדתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 3, 37, 'ועמודי החצר סביב ואדניהם ויתדתם ומיתריהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 3, 38, 'והחנים את פני המשכן קדמה לפני אהל מועד מזרחה משה אהרן ובניו שמרי משמרת הקדש למשמרת בני ישראל והזר הקרב יומת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 3, 39, 'כל פקדי הלוים אשר פקד משה . על פי יהוה למשפחותם כל זכר מבן חדש ומעלה שנים ועשרים אלף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 3, 40, 'ויאמר יהוה אל משה פקד כל בכור זכר לבני ישראל מבן חדש ומעלה ושא את מספר שמותם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 3, 41, 'ולקחת את הלוים לי אני יהוה תחת כל בכור בבני ישראל ואת בהמת הלוים תחת כל בכור בבהמת בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 3, 42, 'ויפקד משה כאשר צוה יהוה אתו את כל בכור בבני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 3, 43, 'ויהיו כל בכור זכר במספר שמות מבן חדש ומעלה לפקדיהם שנים ועשרים אלף שלשה ושבעים ומאתים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 3, 44, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 3, 45, 'קח את הלוים תחת כל בכור בבני ישראל ואת בהמת הלוים תחת בהמתם והיו לי הלוים אני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 3, 46, 'ואת פדויי השלשה והשבעים והמאתים העדפים על הלוים מבכורי בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 3, 47, 'ולקחת חמשת חמשת שקלים לגלגלת בשקל הקדש תקח עשרים גרה השקל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 3, 48, 'ונתתה הכסף לאהרן ולבניו פדויי העדפים בהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 3, 49, 'ויקח משה את כסף הפדוים מאת העדפים על פדויי הלוים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 3, 50, 'מאת בכורי בני ישראל לקח את הכסף חמשה וששים ושלש מאות ואלף בשקל הקדש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 3, 51, 'ויתן משה את כסף הפדוים לאהרן ולבניו על פי יהוה כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 1, 'וידבר יהוה אל משה ואל אהרן לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 2, 'נשא את ראש בני קהת מתוך בני לוי למשפחתם לבית אבתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 3, 'מבן שלשים שנה ומעלה ועד בן חמשים שנה כל הבא לצבא לעשות מלאכה באהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 4, 'זאת עבדת בני קהת באהל מועד קדש הקדשים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 5, 'ובא אהרן ובניו בנסע המחנה והורידו את פרכת המסך וכסו בה את ארון העדות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 6, 'ונתנו עליו כסוי עור תחש ופרשו עליו בגד כליל תכלת מלמעלה ושמו בדיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 7, 'ועל שלחן הפנים יפרשו בגד תכלת ונתנו עליו את הקערת ואת הכפות ואת המנקיות ואת קשות הנסך ולחם התמיד עליו יהיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 8, 'ופרשו עליהם כסוי בגד תולעת שני וכסו אתו במכסה עור תחש ושמו . בדיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 9, 'ולקחו בגד תכלת וכסו את מנורת המאור ואת נרתיה ואת מלקחיה ואת מחתיתיה ואת כל כלי שמנה אשר ישרתו לה בהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 10, 'ונתנו אתה ואת כל כליה אל מכסה עור תחש ונתנו על המוט ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 11, 'ועל מזבח הזהב יפרשו בגד תכלת וכסו אתו במכסה עור תחש ושמו את בדיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 12, 'ולקחו את כל כלי השרת אשר ישרתו בהם בקדש ונתנו אל בגד תכלת וכסו אתם במכסה עור תחש ונתנו על המוט ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 13, 'ודשנו את המזבח ופרשו עליו בגד ארגמן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 14, 'ונתנו עליו את כל כליו אשר ישרתו עליו בהם את המחתות את המזלגות ואת היעים ואת המזרקות כל כלי המזבח ופרשו עליו כסוי עור תחש ושמו בדיו ולקחו בגד ארגמן וכסו את הכיור ואת כנו ונתנו אתם אל מכסה עור תחש ונתנו על המוט ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 15, 'וכלה אהרן ובניו לכסות את הקדש ואת כל כלי הקדש בנסע המחנה ואחרי כן יבאו בני קהת לשאת ולא יגעו אל הקדש ומתו אלה משאי בני קהת באהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 16, 'ופקדת אלעזר בן אהרן הכהן שמן המאור וקטרת הסמים ומנחת התמיד ושמן המשחה פקדת כל המשכן וכל אשר בו בקדש ובכליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 17, 'וידבר יהוה אל משה ואל אהרן לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 18, 'אל תכריתו את שבט משפחת הקהתי מתוך הלוים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 19, 'זאת עשו להם וחיו ולא ימותו בגשתם את קדש הקדשים אהרן ובניו יבאו ושמו אתם איש . על עבדתו ועל משאו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 20, 'ולא יבאו לראות כבלע את הקדש ומתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 21, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 22, 'נשא את ראש בני גרשון גם הם לבית אבתם למשפחותם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 23, 'מבן שלשים שנה ומעלה ועד בן חמשים שנה תפקד אתם כל הבא לצבא צבא לעבד עבדה באהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 24, 'זאת עבדת משפחת הגרשוני לעבד ולמשא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 25, 'ונשאו את יריעות המשכן ואת אהל מועד מכסהו ומכסה התחש אשר עליו מלמעלה ואת מסך פתח אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 26, 'ואת קלעי החצר ואת מסך פתח שער החצר אשר על המשכן ועל המזבח סביב ואת מיתריהם ואת כל כלי עבדתם ואת כל אשר יעשה להם ועבדו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 27, 'על פי אהרן ובניו תהיה כל עבדת בני הגרשוני לכל משאם ולכל עבדתם ופקדתם עליהם במשמרת את כל משאם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 28, 'זאת עבדת משפחת בני הגרשוני באהל מועד ומשמרתם ביד איתמר בן אהרן הכהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 29, 'בני מררי למשפחותם לבית אבתם תפקד אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 30, 'מבן שלשים שנה ומעלה ועד בן חמשים שנה תפקדם כל הבא לצבא לעבד את עבדת אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 31, 'וזאת משמרת משאם לכל עבדתם באהל מועד קרשי המשכן ובריחיו ועמודיו ואדניו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 32, 'ועמדי החצר סביב ואדניהם ויתדתם ומיתריהם לכל כליהם ולכל עבדתם ובשמות תפקדו את כל כלי משמרת משאם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 33, 'זאת עבדת משפחת בני מררי לכל עבדתם באהל מועד ביד איתמר בן אהרן הכהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 34, 'ויפקד משה ואהרן ונשיאי העדה את בני הקהתי למשפחתם לבית אבתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 35, 'מבן שלשים שנה ומעלה ועד בן חמשים שנה כל הבא לצבא לעבדה באהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 36, 'ויהיו פקדיהם למשפחתם אלפים ושבע מאות וחמשים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 37, 'אלה פקדי משפחת הקהתי כל העבד באהל מועד אשר פקד משה ואהרן על פי יהוה ביד משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 38, 'ופקדי בני גרשון למשפחתם לבית אבתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 39, 'מבן שלשים שנה ומעלה ועד בן חמשים שנה כל הבא לצבא לעבדה באהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 40, 'ויהיו פקדיהם למשפחותם לבית אבתם אלפים ושש מאות ושלשים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 41, 'אלה פקדי משפחת בני גרשון כל העבד באהל מועד אשר פקד משה ואהרן על פי יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 42, 'ופקדי משפחת בני מררי למשפחותם לבית אבתם ');
