INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 44, 'ויהיו פקדיהם למשפחותם שלשת אלפים ומאתים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 45, 'אלה פקדי משפחת בני מררי אשר פקד משה ואהרן על פי יהוה ביד משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 46, 'כל הפקדים אשר פקד משה ואהרן ונשיאי ישראל את הלוים למשפחותם לבית אבתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 47, 'מבן שלשים שנה ומעלה ועד בן חמשים שנה כל הבא לעבד עבדת עבדה ועבדת משא באהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 48, 'ויהיו פקדיהם שמנת אלפים וחמש מאות ושמנים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 4, 49, 'על פי יהוה פקד אתם ביד משה איש איש על עבדתו ועל משאו ופקדיו כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 2, 'צוה את בני ישראל וישלחו מן המחנה כל צרוע וכל זב וכל טמא לנפש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 3, 'מזכר עד נקבה תשלחו אל מחוץ למחנה תשלחום ולא יטמאו את מחניהם אשר אני שכן בתוכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 4, 'ויעשו כן בני ישראל וישלחו אתם אל מחוץ למחנה כאשר דבר יהוה אל משה כן עשו בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 5, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 6, 'דבר אל בני ישראל לאמר איש או אשה כי יעשו מכל חטאת האדם למעל מעל ביהוה ואשמה הנפש ההיא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 7, 'והתודו את חטאתם אשר עשו והשיב את אשמו בראשו וחמישתו יסף עליו ונתן לאשר אשם לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 8, 'ואם אין לאיש גאל להשיב האשם אליו האשם המושב ליהוה לכהן מלבד איל הכפרים אשר יכפר בו עליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 9, 'וכל תרומה לכל קדשי בני ישראל אשר יקריבו לכהן לו יהיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 10, 'ואיש את קדשיו לו יהיה ואיש אשר יתן לכהן לו יהיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 11, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 12, 'דבר אל בני ישראל ואמרת אליהם איש איש כי תשטה אשתו ומעלה בו מעל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 13, 'ושכב איש אתה שכבת זרע ונעלם מעיני אישה ונסתרה והיא נטמאה ועד אין בה והיא לא נתפשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 14, 'ועבר עליו רוח קנאה וקנא את אשתו והיא נטמאה או עבר עליו רוח קנאה וקנא את אשתו והיא לא נטמאה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 15, 'והביא האיש את אשתו אל הכהן והביא את קרבנה עליה עשירת האיפה קמח שערים לא יצק עליה שמן ולא יתן עליה לבונה כי מנחת קנאת היא מנחת זכרון מזכרת עון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 16, 'והקריב אתה הכהן והעמדה לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 17, 'ולקח הכהן מים קדשים בכלי חרש ומן העפר אשר יהיה בקרקע המשכן יקח הכהן ונתן אל המים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 18, 'והעמיד הכהן את האשה לפני יהוה ופרע את ראש האשה ונתן על כפיה את מנחת הזכרון מנחת קנאת היא וביד הכהן יהיו מי המרים המאררים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 19, 'והשביע אתה הכהן ואמר אל האשה אם לא שכב איש אתך ואם לא שטית טמאה תחת אישך הנקי ממי המרים המאררים האלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 20, 'ואת כי שטית תחת אישך וכי נטמאת ויתן איש בך את שכבתו מבלעדי אישך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 21, 'והשביע הכהן את האשה בשבעת האלה ואמר הכהן לאשה יתן יהוה אתך לאלה ולשבעה בתוך עמך בתת יהוה את ירכך נפלת ואת בטנך צבא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 22, 'ובאו המים המאררים האלה במעיך לצבת בטן ולנפל ירך ואמרה האשה אמן אמן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 23, 'וכתב את האלות האלה הכהן בספר ומחה אל מי המרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 24, 'והשקה את האשה את מי המרים המאררים ובאו בה המים המאררים למרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 25, 'ולקח הכהן מיד האשה את מנחת הקנאת והניף את המנחה לפני יהוה והקריב אתה אל המזבח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 26, 'והרים הכהן מן המנחה את אזכרתה והקטיר המזבחה ואחר ישקה את האשה את המים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 27, 'והשקה את המים והיה אם נטמאה ותמעל מעל באישה ובאו בה המים המאררים למרים וצבתה בטנה ונפלה ירכה והיתה האשה לאלה בקרב עמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 28, 'ואם לא נטמאה האשה וטהרה היא ונקתה ונזרעה זרע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 29, 'זאת תורת הקנאת אשר תשטה אשה תחת אישה ונטמאה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 30, 'או איש אשר תעבר עליו רוח קנאה וקנא את אשתו והעמיד את האשה לפני יהוה ועשה לה הכהן את כל התורה הזאת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 5, 31, 'ונקה האיש מעון והאשה ההיא תשא את עונה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 2, 'דבר אל בני ישראל ואמרת אליהם איש או אשה כי יפלא לנדר נדר נזיר להזיר ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 3, 'מיין ושכר יזיר חמץ יין וחמץ שכר לא ישתה וכל משארת ענבים לא ישתה וענבים לחים ויבשים לא יאכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 4, 'כל ימי נזרו מכל אשר יעשה מגפן היין מחרצנים ועד זג לא יאכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 5, 'כל ימי נדר נזרו תער לא יעבר על ראשו עד מלאת הימים אשר יזיר ליהוה קדוש יהיה גדל פרע שער ראשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 6, 'כל ימי הזירו ליהוה על נפש מת לא יבוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 7, 'לאביו ולאמו לאחיו ולאחתו לא יטמא להם במותם כי נזר אלהיו על ראשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 8, 'כל ימי נזרו קדוש הוא ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 9, 'וכי ימות מת עליו בפתע פתאם וטמא ראש נזרו וגלח ראשו ביום טהרתו ביום השביעי יגלחנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 10, 'וביום השמיני יביא שתי תרים או שני בני יונה אל הכהן אל פתח אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 11, 'ועשה הכהן אחד לחטאת ואחד לעלה וכפר עליו מאשר חטא על הנפש וקדש את ראשו ביום ההוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 12, 'והזיר ליהוה את ימי נזרו והביא כבש בן שנתו לאשם והימים הראישונים יפלו כי טמא נזרו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 13, 'וזאת תורת הנזיר ביום מלאת ימי נזרו יביא אתו אל פתח אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 14, 'והקריב את קרבנו ליהוה כבש בן שנתו תמים אחד לעלה וכבשה אחת בת שנתה תמימה לחטאת ואיל אחד תמים לשלמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 15, 'וסל מצות סלת חלות בלולת בשמן ורקיקי מצות משחים בשמן ומנחתם ונסכיהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 16, 'והקריב הכהן לפני יהוה ועשה את חטאתו ואת עלתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 17, 'ואת האיל יעשה זבח שלמים ליהוה על סל המצות ועשה הכהן את מנחתו ואת נסכו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 18, 'וגלח הנזיר פתח אהל מועד את ראש נזרו ולקח את שער ראש נזרו ונתן על האש אשר תחת זבח השלמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 19, 'ולקח הכהן את הזרוע בשלה מן האיל וחלת מצה אחת מן הסל ורקיק מצה אחד ונתן על כפי הנזיר אחרי התגלחו את נזרו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 20, 'והניף אתם הכהן תנופה לפני יהוה קדש הוא לכהן יהיה על חזה התנופה ועל שוק התרומה ואחר ישתה הנזיר יין ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 21, 'זאת תורת הנזיר אשר ידר קרבנו ליהוה על נזרו מלבד אשר תשיג ידו כפי נדרו אשר ידר כן יעשה על תורת נזרו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 22, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 23, 'דבר אל אהרן ואל בניו לאמר כה תברכו את בני ישראל אמר להם ');
