INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 25, 'יאיר יהוה פניו אליך ויחנך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 26, 'ישא יהוה פניו אליך וישם לך שלום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 6, 27, 'ושמו את שמי על בני ישראל ואני אברכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 7, 1, 'ויהי ביום כלות משה להקים את המשכן וימשח אתו ויקדש אתו ואת כל כליו ואת המזבח ואת כל כליו וימשחם ויקדש אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 7, 2, 'ויקרבו נשיאי ישראל ראשי בית אבתם הם נשיאי המטות הם העמדים על הפקדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 7, 3, 'ויביאו את קרבנם לפני יהוה שש עגלות צב ושנים עשר בקר עגלה על שני הנשיאים ושור לאחד ויקרבו אתם לפני המשכן ');
