INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 7, 84, 'זאת חנכת המזבח ביום המשח אתו מאת נשיאי ישראל קערת כסף שתים עשרה מזרקי כסף שנים עשר כפות זהב שתים עשרה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 7, 85, 'שלשים ומאה הקערה האחת כסף ושבעים המזרק האחד כל כסף הכלים אלפים וארבע מאות בשקל הקדש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 7, 86, 'כפות זהב שתים עשרה מלאות קטרת עשרה עשרה הכף בשקל הקדש כל זהב הכפות עשרים ומאה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 7, 87, 'כל הבקר לעלה שנים עשר פרים אילים שנים עשר כבשים בני שנה שנים עשר ומנחתם ושעירי עזים שנים עשר לחטאת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 7, 88, 'וכל בקר זבח השלמים עשרים וארבעה פרים אילים ששים עתודים ששים כבשים בני שנה ששים זאת חנכת המזבח אחרי המשח אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 7, 89, 'ובבא משה אל אהל מועד לדבר אתו וישמע את הקול מדבר אליו מעל הכפרת אשר על ארון העדות מבין שני הכרבים וידבר אליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 2, 'דבר אל אהרן ואמרת אליו בהעלותך את הנרות אל מול פני המנורה יאירו שבעת הנרות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 3, 'ויעש כן אהרן אל מול פני המנורה העלה נרתיה כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 4, 'וזה מעשה המנורה מקשה זהב עד ירכיה ועד פרחיה מקשה היא כמראה אשר הראה יהוה את משה כן עשה את המנורה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 5, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 6, 'קח את הלוים מתוך בני ישראל וטהרת אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 7, 'וכה תעשה להם לטהרם הזה עליהם מי חטאת והעבירו תער על כל בשרם וכבסו בגדיהם והטהרו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 8, 'ולקחו פר בן בקר ומנחתו סלת בלולה בשמן ופר שני בן בקר תקח לחטאת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 9, 'והקרבת את הלוים לפני אהל מועד והקהלת את כל עדת בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 10, 'והקרבת את הלוים לפני יהוה וסמכו בני ישראל את ידיהם על הלוים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 11, 'והניף אהרן את הלוים תנופה לפני יהוה מאת בני ישראל והיו לעבד את עבדת יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 12, 'והלוים יסמכו את ידיהם על ראש הפרים ועשה את האחד חטאת ואת האחד עלה ליהוה לכפר על הלוים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 13, 'והעמדת את הלוים לפני אהרן ולפני בניו והנפת אתם תנופה ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 14, 'והבדלת את הלוים מתוך בני ישראל והיו לי הלוים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 15, 'ואחרי כן יבאו הלוים לעבד את עבדת אהל מועד וטהרת אתם והנפת אתם תנופה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 16, 'כי נתנים נתנים הם לי מתוך בני ישראל תחת כל בכור פטר רחם . בבני ישראל לקחתי אתם לי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 17, 'כי לי כל בכור בבני ישראל באדם ובבהמה ביום הכיתי כל בכור בארץ מצרים הקדשתי אתם לי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 18, 'ואקח את הלוים תחת כל בכור בבני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 19, 'ואתנה את הלוים נתנים לאהרן ולבניו מתוך בני ישראל לעבד את עבדת בני ישראל באהל מועד ולכפר על בני ישראל ולא יהיה בבני ישראל נגף בגשת בני ישראל אל הקדש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 20, 'ויעש משה ואהרן וכל עדת בני ישראל ללוים ככל אשר צוה יהוה את משה ללוים כן עשו להם בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 21, 'ויתחטאו הלוים ויכבסו בגדיהם וינף אהרן אתם תנופה לפני יהוה ויכפר עליהם אהרן לטהרם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 22, 'ואחרי כן באו הלוים לעבד את עבדתם באהל מועד לפני אהרן ולפני בניו כאשר צוה יהוה את משה על הלוים כן עשו להם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 23, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 24, 'זאת אשר ללוים מבן חמש ועשרים שנה ומעלה יבוא לצבא צבא בעבדת אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 25, 'ומבן חמשים שנה ישוב מצבא העבדה ולא יעבד עוד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 8, 26, 'ושרת את אחיו באהל מועד לשמר משמרת ועבדה לא יעבד ככה תעשה ללוים במשמרתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 1, 'וידבר יהוה אל משה במדבר סיני בשנה השנית לצאתם מארץ מצרים בחדש הראישון לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 2, 'ויעשו בני ישראל את הפסח במועדיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 3, 'בארבעה עשר יום בחדש הזה בין הערבים יעשו אתו במועדיו ככל חקתיו וככל משפטיו תעשו אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 4, 'וידבר משה אל בני ישראל לעשות הפסח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 5, 'ויעשו את הפסח בראישון בארבעה עשר יום לחדש בין הערבים במדבר סיני ככל אשר צוה יהוה את משה כן עשו בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 6, 'ויהיו אנשים אשר היו טמאים לנפש אדם ולא יכלו לעשות הפסח ביום ההוא ויקרבו לפני משה ולפני אהרן ביום ההוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 7, 'ויאמרו האנשים ההם אליו אנחנו טמאים לנפש אדם למה נגרע לבלתי הקריב את קרבן יהוה במועדיו בתוך בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 8, 'ויאמר אליהם משה עמדו ואשמעה מה יצוה יהוה לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 9, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 10, 'דבר אל בני ישראל לאמר איש איש כי יהיה טמא לנפש או בדרך רחוקה לכם או לדרתיכם ועשה פסח ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 11, 'בחדש השני בארבעה עשר יום בין הערבים יעשו אתו על מצות ומררים יאכלהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 12, 'לא ישאירו ממנו עד בקר ועצם לא ישברו בו ככל חקת הפסח יעשו אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 13, 'ואיש אשר הוא טהור ובדרך לא היה וחדל לעשות הפסח ונכרתה הנפש ההיא מעמיה כי קרבן יהוה לא הקריב במועדיו חטאו ישא האיש ההוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 14, 'וכי יגור אתכם גר ועשה פסח ליהוה כחקת הפסח וכמשפטיו כן יעשה חקה אחת יהיה לכם ולגר ולאזרח הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 15, 'וביום הוקם את המשכן כסה הענן את המשכן לאהל העדות ובערב יהיה על המשכן כמראה אש עד בקר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 16, 'כן יהיה תמיד הענן יכסנו ומראה אש לילה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 17, 'ולפי העלות הענן מעל האהל ואחרי כן יסעו בני ישראל ובמקום אשר ישכן שם הענן שם יחנו בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 18, 'על פי יהוה יסעו בני ישראל ועל פי יהוה יחנו כל ימי אשר ישכן הענן על המשכן יחנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 19, 'ובהאריך הענן על המשכן ימים רבים ושמרו בני ישראל את משמרת יהוה ולא יסעו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 20, 'ויש אשר יהיה הענן ימים במספר על המשכן על פי יהוה יחנו ועל פי יהוה יסעו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 21, 'ויש אשר יהיה הענן מערב עד בקר ונעלה הענן בבקר ונסעו או יומם ולילה ונעלה הענן ונסעו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 22, 'או יומים או חדש או ימים בהאריך הענן על המשכן לשכן עליו יחנו בני ישראל ולא יסעו ובהעלותו יסעו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 9, 23, 'על פי יהוה יחנו ועל פי יהוה יסעו את משמרת יהוה שמרו על פי יהוה ביד משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 2, 'עשה לך שתי חצצרות כסף מקשה תעשה אתם והיו לך למקרא העדה ולמסע את המחנות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 3, 'ותקעו בהן ונועדו אליך כל העדה אל פתח אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 4, 'ואם באחת יתקעו ונועדו אליך הנשיאים ראשי אלפי ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 5, 'ותקעתם תרועה ונסעו המחנות החנים קדמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 6, 'ותקעתם תרועה שנית ונסעו המחנות החנים צפונה תרועה יתקעו למסעיהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 7, 'ובהקהל את הקהל תתקעו ולא תריעו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 8, 'ובני אהרן הכהנים יתקעו בחצצרות והיו לכם לחקת עולם לדרתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 9, 'וכי תבאו מלחמה בארצכם על הצר הצרר אתכם והרעתם בחצצרות ונזכרתם לפני יהוה אלהיכם ונושעתם מאיביכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 10, 'וביום שמחתיכם ובמועדיכם ובראשי חדשיכם ותקעתם בחצצרות על עלתיכם ועל זבחי שלמיכם והיו לכם לזכרון לפני יהוה אלהיכם אני יהוה אלהיכם וידבר יהוה אל משה לאמר רב לכם שבת בהר הזה פנו וסעו לכם ובאו הר האמרי ואל כל שכניו בערבה בהר ובשפלה בנגב ובחוף הים ארץ הכנעני והלבנון עד הנהר הגדול נהר פרת ראו נתתי לפניכם את הארץ באו ורשו את הארץ אשר נשבעתי לאבתיכם לאברהם ליצחק וליעקב לתת לזרעם אחריהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 11, 'ויהי בשנה השנית בחדש השני בעשרים בחדש נעלה הענן מעל משכן העדות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 12, 'ויסעו בני ישראל למסעיהם ממדבר סיני וישכן הענן במדבר פראן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 13, 'ויסעו בראישונה על פי יהוה ביד משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 14, 'ויסע דגל מחנה בני יהודה בראישונה לצבאתם ועל צבאו נחשון בן עמינדב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 15, 'ועל צבא מטה בני יששכר נתנאל בן צוער ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 16, 'ועל צבא מטה בני זבולן אליאב בן חילן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 17, 'והורד המשכן ונסעו בני גרשון ובני מררי נשאי המשכן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 18, 'ונסע דגל מחנה בני ראובן לצבאתם ועל צבאו אליצור בן שדיאור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 19, 'ועל צבא מטה בני שמעון שלמיאל בן צורישדי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 20, 'ועל צבא מטה בני גד אליסף בן דעואל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 21, 'ונסעו הקהתים נשאי המקדש והקימו את המשכן עד באם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 22, 'ונסע דגל מחנה בני אפרים לצבאתם ועל צבאו אלישמע בן עמיהוד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 23, 'ועל צבא מטה בני מנשה גמליאל בן פדהצור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 24, 'ועל צבא מטה בני בנימים אבידן בן גדעוני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 25, 'ונסע דגל מחנה בני דן מאסף לכל המחנות לצבאתם ועל צבאו אחיעזר בן עמישדי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 26, 'ועל צבא מטה בני אשר פגעיאל בן עכרן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 27, 'ועל צבא מטה בני נפתלי אחירע בן עינן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 28, 'אלה מסעי בני ישראל לצבאתם ויסעו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 29, 'ויאמר משה לחובב בן רעואל המדיני חתן משה נסעים אנחנו אל המקום אשר אמר יהוה אתו אתן לכם לך אתנו והטבנו לך כי יהוה דבר טוב על ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 30, 'ויאמר אליו לא אלך כי אם אל ארצי ואל מולדתי אלך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 31, 'ויאמר אל נא תעזב אתנו כי על כן ידעת חנתינו במדבר והיית לנו לעינים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 32, 'והיה כי תלך עמנו והיה הטוב ההוא אשר ייטיב יהוה עמנו והיטבנו לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 33, 'ויסעו מהר יהוה דרך שלשת ימים וארון ברית יהוה נסע לפניהם דרך שלשת ימים לתור להם מנוחה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 34, 'וענן יהוה עליהם יומם בנסעם מן המחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 35, 'ויהי בנסע הארון ויאמר משה קומה יהוה ויפצו איביך וינסו משנאיך מפניך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 10, 36, 'ובמנוחה יאמר שובה יהוה רבבות אלפי ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 1, 'ויהי העם כמתאננים רע באזני יהוה וישמע יהוה ויחר אפו ותבער בם אש יהוה ותאכל בקצה המחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 2, 'ויצעק העם אל משה ויתפלל משה אל יהוה ותשקע האש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 3, 'ויקרא שם המקום ההוא תבערה כי בערה בם אש יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 4, 'והאספסף אשר בקרבו התאוו תאוה וישבו ויבכו גם בני ישראל ויאמרו מי יאכלנו בשר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 5, 'זכרנו את הדגה אשר נאכל במצרים חנם את הקשואים ואת האבטחים ואת החציר ואת הבצלים ואת השומים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 6, 'ועתה נפשנו יבשה אין כל בלתי אל המן עינינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 7, 'והמן כזרע גד הוא ועינו כעין הבדלח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 8, 'שטו העם ולקטו וטחנו ברחים או דכו במדוכה ובשלו בפרור ועשו אתו עגות והיה טעמו כטעם לשד השמן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 9, 'וברדת הטל על המחנה לילה ירד המן עליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 10, 'וישמע משה את העם בכה למשפחתיו איש לפתח אהלו ויחר אף יהוה מאד ובעיני משה רע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 11, 'ויאמר משה אל יהוה למה הרעת לעבדך ולמה לא מצאתי חן בעיניך לשים את משא כל העם הזה עלי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 12, 'האנכי הריתי את כל העם הזה אם אנכי ילדתיו כי תאמר אלי שאהו בחיקך כאשר ישא האמן את הינק אל האדמה אשר נשבעת לאבתיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 13, 'מאין לי בשר לתת לכל העם הזה כי יבכו עלי לאמר תנה לנו בשר ונאכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 14, 'לא אוכל אנכי לבדי לשאת את כל העם הזה כי כבד ממני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 15, 'ואם ככה אתה עשה לי הרגני נא הרג ואם מצאתי חן בעיניך ואל אראה ברעתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 16, 'ויאמר יהוה אל משה אספה לי שבעים איש מזקני ישראל אשר ידעת כי הם זקני העם ושטריו ולקחת אתם אל אהל מועד והתיצבו שם עמך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 17, 'וירדתי ודברתי עמך שם והצלתי מן הרוח אשר עליך ושמתי עליהם ונשאו אתך במשא העם ולא תשא אתה לבדך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 18, 'ואל העם תאמר התקדשו למחר ואכלתם בשר כי בכיתם באזני יהוה לאמר מי יאכלנו בשר כי טוב לנו במצרים ונתן יהוה לכם בשר ואכלתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 19, 'לא יום אחד תאכלון ולא יומים ולא חמשה ימים ולא עשרה ימים ולא עשרים יום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 20, 'עד חדש ימים עד אשר יצא מאפכם והיה לכם לזרה יען כי מאסתם את יהוה אשר בקרבכם ותבכו לפניו לאמר למה זה יצאנו ממצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 21, 'ויאמר משה שש מאות אלף רגלי העם אשר אנכי בקרבו ואתה אמרת בשר אתן להם ואכלו חדש ימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 22, 'הצאן ובקר ישחט להם ומצא להם אם את כל דגי הים יאסף להם ומצא להם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 23, 'ויאמר יהוה אל משה היד יהוה תקצר עתה תראה היקרך דברי אם לא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 24, 'ויצא משה וידבר אל העם את דברי יהוה ויאסף שבעים איש מזקני העם ויעמד אתם סביבת האהל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 25, 'וירד יהוה בענן וידבר אליו ויצל מן הרוח אשר עליו ויתן על שבעים איש הזקנים ויהי כנוח עליהם הרוח ויתנבאו ולא יאספו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 26, 'וישארו שני אנשים במחנה שם האחד אלדד ושם השני מידד ותנח עליהם הרוח והם בכתבים ולא יצאו האהלה ויתנבאו במחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 27, 'וירץ הנער ויגד למשה ויאמר אלדד ומודד מתנבאים במחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 28, 'ויען יהושע בן נון משרת משה מבחריו ויאמר אדני משה כלאם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 29, 'ויאמר לו משה המקנא אתה לי ומי יתן כל עם יהוה נביאים כי יתן יהוה את רוחו עליהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 30, 'ויאסף משה אל המחנה הוא וזקני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 31, 'ורוח נסע מאת יהוה ויגז שלוי מן הים ויטש אל המחנה כדרך יום כה וכדרך יום כה סביבת המחנה וכאמתים על פני הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 32, 'ויקם . העם כל היום ההוא וכל הלילה וכל היום המחרת ויאספו את השלוי הממעיט אסף עשרה חמרים וישטחו להם שחוט סביבת המחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 33, 'הבשר עודנו בין שניהם טרם יכרת ואף יהוה חרה בעם ויך יהוה בעם מכה רבה מאד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 34, 'ויקרא . שם המקום ההוא קברות התאוה כי שם קברו את העם המתאוים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 11, 35, 'מקברות התאוה נסעו העם החצרות ויהיו בחצרות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 12, 1, 'ותדבר מרים ואהרן במשה על אדות האשה הכשית אשר לקח כי אשה כשית לקח . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 12, 2, 'ויאמרו הרק אך במשה דבר יהוה הלוא גם בנו דבר וישמע יהוה . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 12, 3, 'והאיש משה ענו מאד מכל האדם אשר על פני האדמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 12, 4, 'ויאמר יהוה פתאם אל משה ואל אהרן ואל מרים צאו שלשתכם אל אהל מועד ויצאו שלשתם . . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 12, 5, 'וירד יהוה בעמוד ענן ויעמד פתח האהל . ויקרא אהרן ומרים ויצאו שניהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 12, 6, 'ויאמר . . שמעו נא דברי אם יהיה נביאכם יהוה במראה אליו התודע ובחלום אדבר בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 12, 7, 'לא כן עבדי משה בכל ביתי נאמן הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 12, 8, 'פה אל פה אדבר בו בממראה ולא בחידת ותמונת יהוה יביט ומדוע לא יראתם לדבר בעבדי במשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 12, 9, 'ויחר אף יהוה בם וילך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 12, 10, 'והענן סר מעל האהל והנה מרים מצרעת כשלג ויפן אהרן אל מרים והנה מצרעת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 12, 11, 'ויאמר אהרן אל משה בי אדני אל נא תשית עלינו חטאת אשר נואלנו ואשר חטאנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 12, 12, 'אל נא תהיה כמת אשר בצאתו מרחם אמו ויאכל חצי בשרו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 12, 13, 'ויצעק משה אל יהוה לאמר אל נא רפא נא לה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 12, 14, 'ויאמר יהוה אל משה ואביה ירק ירק בפניה הלא תכלם שבעת ימים תסגר שבעת ימים מחוץ למחנה ואחר תאסף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 12, 15, 'ותסגר מרים מחוץ למחנה שבעת ימים והעם לא נסעו עד האספה מרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 12, 16, 'ואחר נסעו העם מחצרות ויחנו במדבר פראן ויאמר משה לבני ישראל באתם עד הר האמרי אשר יהוה אלהינו נתן לנו ראה נתן יהוה אלהיך לפניך את הארץ עלה רש כאשר דבר יהוה אלהי אבתיך לך אל תירא ואל תחת ויקרבו אל משה ויאמרו נשלחה אנשים לפנינו ויחפדו לנו את הארץ וישיבו אתנו דבר את הדרך אשר נעלה בה ואת הערים אשר נבוא עליהן וייטב בעיני משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 2, 'שלח לך אנשים ויתרו את ארץ כנען אשר אני נתן לבני ישראל איש אחד איש אחד למטה אבתיו תשלח כל נשיא בהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 3, 'וישלח אתם משה ממדבר פראן על פי יהוה כלם אנשים ראשי בני ישראל הם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 4, 'ואלה שמותם למטה ראובן שמוע בן זכור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 5, 'למטה שמעון שפט בן חורי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 6, 'למטה יהודה כלב בן יפנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 7, 'למטה יששכר יגאל בן יוסף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 8, 'למטה אפרים יהושע בן נון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 9, 'למטה בנימים פלטי בן רפוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 10, 'למטה זבולן גדיאל בן סודי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 11, 'למטה יוסף למטה מנשה גדי בן סוסי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 12, 'למטה דן עמיאל בן גמלי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 13, 'למטה אשר סתור בן מיכאל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 14, 'למטה נפתלי נחבי בן ופסי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 15, 'למטה גד גואל בן מיכי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 16, 'אלה שמות האנשים אשר שלח משה לתור את הארץ . ויקרא משה ליהושע בן נון יהושע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 17, 'וישלח אתם . לתור את ארץ כנען ויאמר אליהם עלו זה בנגב ועליתם את ההר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 18, 'וראיתם את הארץ . מה היא ואת העם הישב עליה החזק הוא . הרפא . המעט הוא אם רב . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 19, 'ומה הארץ אשר הוא ישב בה הטובה היא אם רעה . ומה הערים אשר הוא ישב בהן המבחנים אך מבצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 20, 'ומה הארץ השמנה היא אם רזה . היש בה עץ אם אין והתחזקתם ולקחתם . מפרי הארץ והימים ימי בכרות ענבים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 21, 'וילכו ויבאו ויתרו את הארץ ממדבר צן עד רחב לבוא חמתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 22, 'ויעלו בנגב ויבאו עד חברון ושם אחימן ששי ותלמי ילדי ענק וחברון שבע שנים נבנתה לפני צען מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 23, 'ויבאו עד נחל אשכול . . ויכרתו משם זמרה ואשכל ענבים אחד . וישאו במוט בשנים ומן הרמונים ומן התאנים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 24, 'למקום ההוא קראו נחל אשכל על אדות האשכל אשר כרתו משם בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 25, 'וישבו מתור את הארץ מקץ ארבעים יום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 26, 'וילכו ויבאו אל משה ואל אהרן ואל כל עדת בני ישראל אל מדבר פראן קדשה וישיבו אתם דבר ואת כל העדה ויראום את פרי הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 27, 'ויספרו לו ויאמרו באנו אל הארץ אשר שלחתנו וגם זבת חלב ודבש היא וזה פריה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 28, 'אפס כי עז העם הישב בארץ והערים בצרות וגדלות מאד וגם ילדי ענק ראינו שם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 29, 'עמלק ישב בארץ הנגב והחתי והחוי והיבוסי והאמרי ישב בהר והכנעני ישב על הים ועל יד הירדן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 30, 'ויהס כלב את העם על משה ויאמר לו עלה נעלה וירשנו אתה כי יכל נכל לה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 31, 'והאנשים אשר עלו עמו אמרו לא נוכל לעלות אל העם כי חזק הוא ממנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 32, 'ויוציאו דבת הארץ אשר תרו אתה אל בני ישראל לאמר הארץ אשר עברנו בה לתור אתה ארץ אכלת ישביה היא וכל העם אשר ראינו בתוכה אנשי מדות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 13, 33, 'ושם ראינו את הנפלים בני ענק מן הנפלים ונהי בעינינו כחגבים וכן היינו בעיניהם וירגנו בני ישראל באהליהם ויאמרו בשנאת יהוה אתנו הוציאנו מארץ מצרים לתת אתנו ביד האמרי להשמידנו אנה אנחנו עלים ואחינו המיסו את לבבנו לאמר עם גדול ורב ממנו וערים גדלות ובצרות בשמים וגם בני ענקים ראינו שם ויאמר משה לבני ישראל לא תערצון ולא תיראון מהם יהוה אלהיכם ההלך לפניכם הוא ילחם לכם ככל אשר עשה אתכם במצרים לעיניכם ובמדבר אשר ראית אשר נשאך יהוה אלהיך כאשר ישא איש את בנו בכל הדרך אשר הלכתם עד באכם עד המקום הזה ובדבר הזה אינכם מאמנים ביהוה אלהיכם ההלך לפניכם בדרך לתור לכם מקום להחנתכם באש לילה להראתכם בדרך אשר תלכו בה וענן יומם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 1, 'ותשא כל העדה ויתנו את קולם ויבך העם בלילה ההוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 2, 'וילנו על משה ועל אהרן כל בני ישראל ויאמרו אליהם כל העדה לו מתנו בארץ מצרים או במדבר הזה לו מתנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 3, 'ולמה יהוה מביא אתנו אל הארץ הזאת לנפל בחרב נשינו וטפנו יהיו לבז הלוא טוב לנו שוב מצרימה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 4, 'ויאמרו איש אל אחיו נתן ראש ונשוב מצרימה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 5, 'ויפל משה ואהרן על פניהם לפני כל קהל עדת בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 6, 'ויהושע בן נון וכלב בן יפנה מן התרים את הארץ קרעו בגדיהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 7, 'ויאמרו אל כל עדת בני ישראל לאמר הארץ אשר עברנו בה לתור אתה טובה הארץ מאד מאד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 8, 'אם חפץ בנו יהוה והביא אתנו אל הארץ הזאת ונתנה לנו ארץ אשר היא זבת חלב ודבש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 9, 'אך ביהוה אל תמרדו ואתם אל תיראו את עם הארץ כי לחמנו הם סר צלם מעליהם ויהוה אתנו אל תיראם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 10, 'ויאמרו כל העדה לרגם אתם באבנים וכבוד יהוה נראה באהל מועד אל כל בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 11, 'ויאמר יהוה אל משה עד אנה ינאצני העם הזה ועד אנה לא יאמנו בי בכל האתות אשר עשיתי בקרבו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 12, 'אכנו בדבר ואורשנו ואעשה אתך ואת בית אביך לגוי גדול ועצום ממנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 13, 'ויאמר משה אל יהוה ושמעו מצרים כי העלית בכחך את העם הזה מקרבו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 14, 'ואמרו אל יושב הארץ הזאת שמעו כי אתה יהוה בקרב העם הזה אשר עין בעין נראה אתה יהוה ועננך עמד עליהם ובעמוד ענן אתה הלך לפניהם יומם ובעמוד אש לילה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 15, 'והמת את העם הזה כאיש אחד ואמרו הגוים אשר שמעו את שמעך לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 16, 'מבלתי יכלת יהוה להביא את העם הזה אל הארץ אשר נשבע להם וישחטם במדבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 17, 'ועתה יגדל נא כח אדני כאשר דברת לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 18, 'יהוה ארך אפים ורב חסד ואמת נשא עון ופשע וחטאה ונקה לו ינקה פקד עון אבות על בנים על שלשים ועל רבעים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 19, 'סלח נא לעון העם הזה כגדל חסדך וכאשר נשאת לעם הזה ממצרים ועד הנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 20, 'ויאמר יהוה סלחתי כדבריך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 21, 'ואולם חי אני וימלא כבוד יהוה את כל הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 22, 'כי כל האנשים הראים את כבודי ואת אתתי אשר עשיתי במצרים ובמדבר וינסו אתי זה עשר פעמים ולא שמעו בקולי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 23, 'אם יראו את הארץ אשר נשבעתי לאבתם לתת להם וכל מנאצי לא יראוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 24, 'ועבדי כלב עקב היתה רוח אחרת עמו וימלא אחרי והבאתיו אל הארץ אשר בא שמה וזרעו יירשנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 25, 'והעמלקי והכנעני ישב בעמק מחר פנו וסעו לכם המדברה דרך ים סוף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 26, 'וידבר יהוה אל משה ואל אהרן לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 27, 'עד מתי לעדה הרעה הזאת אשר הם מלנים עלי את תלנות בני ישראל אשר הם מלנים עלי שמעתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 28, 'אמר אליהם חי אני נאם יהוה אם לא כאשר דברתם באזני כן אעשה לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 29, 'במדבר הזה יפלו פגריכם וכל פקדיכם לכל מספרכם מבן עשרים שנה ומעלה אשר הלנתם עלי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 30, 'אם אתם תבאו אל הארץ אשר נשאתי את ידי לשכן אתכם בה כי אם כלב בן יפנה ויהושע בן נון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 31, 'וטפכם אשר אמרתם לבז יהיה והבאתי אתם וידעו את הארץ אשר מאסתם בה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 32, 'ופגריכם אתם יפלו במדבר הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 33, 'ובניכם יהיו רעים במדבר ארבעים שנה ונשאו את זנתיכם עד תם פגריכם במדבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 34, 'במספר הימים אשר תרתם את הארץ ארבעים יום יום לשנה יום לשנה תשאו את עונתיכם ארבעים שנה וידעתם את תנואתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 35, 'אני יהוה דברתי אם לא זאת אעשה לכל העדה הרעה הזאת הנועדים עלי במדבר הזה יתמו ושם ימותו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 36, 'והאנשים אשר שלח משה לתור את הארץ וישבו וילנו עליו את כל העדה להוציא דבה על הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 37, 'וימתו האנשים מוצאי דבת הארץ רעה במגפה לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 38, 'ויהושע בן נון וכלב בן יפנה חיו מן האנשים ההם ההלכים לתור את הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 39, 'וידבר משה את הדברים האלה אל כל בני ישראל ויתאבלו העם מאד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 40, 'וישכמו בבקר ויעלו אל ראש ההר לאמר הננו ועלינו אל המקום אשר אמר יהוה כי חטאנו ויאמר יהוה אל משה אמר להם לא תעלו ולא תלחמו כי אינני בקרבכם ולא תנגפו לפני איביכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 41, 'ויאמר משה למה זה אתם עברים את פי יהוה והיא לא תצליח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 42, 'אל תעלו כי אין יהוה בקרבכם ולא תנגפו לפני איביכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 43, 'כי העמלקי והכנעני שם לפניכם ונפלתם בחרב כי על כן שבתם מאחרי יהוה ולא יהיה יהוה עמכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 44, 'ויעפלו לעלות אל ראש ההר וארון ברית יהוה ומשה לא משו מקרב המחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 14, 45, 'וירד העמלקי והכנעני הישב בהר ההוא לקראתם וירדפו אתם כאשר תעשינה הדברים ויכום ויכתום עד חרמה וישבו אל המחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 2, 'דבר אל בני ישראל ואמרת אליהם כי תבאו אל ארץ מושבתיכם אשר אני נתן לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 3, 'ועשיתם אשה ליהוה עלה או זבח לפלא נדר או נדבה או במועדיכם לעשות ריח ניחח ליהוה מן הבקר או מן הצאן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 4, 'והקריב המקריב את קרבנו ליהוה מנחה סלת עשרון בלול ברבעית ההין שמן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 5, 'ויין לנסך רבעית ההין תעשה על העלה או לזבח לכבש האחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 6, 'או לאיל תעשה מנחה סלת שני עשרנים בלול בשמן שלשית ההין ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 7, 'ויין לנסך שלשית ההין תקריב ריח ניחח ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 8, 'וכי תעשה בן בקר עלה או זבח לפלא נדר או שלמים ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 9, 'והקריב על בן הבקר מנחה סלת שלשה עשרנים בלול בשמן חצי ההין ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 10, 'ויין תקריב לנסך חצי ההין אשה ריח ניחח ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 11, 'ככה יעשה לשור האחד או לאיל האחד או לשה בכבשים או בעזים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 12, 'כמספרם אשר תעשו ככה תעשו לאחד במספרם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 13, 'כל האזרח יעשה ככה את אלה להקריב אשה ריח ניחח ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 14, 'וכי יגור אתכם גר או אשר בתוככם לדרתיכם ועשה אשה ריח ניחח ליהוה כאשר תעשו כן יעשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 15, 'הקהל חקה אחת לכם ולגר הגר חקת עולם לדרתיכם ככם כגר יהיה לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 16, 'תורה אחת ומשפט אחד יהיה לכם ולגר הגר אתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 17, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 18, 'דבר אל בני ישראל ואמרת אליהם בבאכם אל הארץ אשר אני מביא אתכם שמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 19, 'והיה באכלכם מלחם הארץ תרימו תרומה ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 20, 'ראשית ערסתיכם חלה תרימו תרומה כתרומת גרן כן תרימו אתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 21, 'מראשית ערסתיכם תתנו ליהוה תרומה לדרתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 22, 'וכי תשגו ולא תעשו את כל המצות האלה אשר דבר יהוה אל משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 23, 'את כל אשר צוה יהוה אליכם ביד משה למן היום אשר צוה יהוה והלאה לדרתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 24, 'והיה אם מעיני העדה נעשתה לשגגה ועשו כל העדה פר בן בקר אחד לעלה לריח ניחח ליהוה ומנחתו ונסכו כמשפט ושעיר עזים אחד לחטאת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 25, 'וכפר הכהן על כל עדת בני ישראל ונסלח להם כי שגגה היא והם הביאו את קרבנם אשה ליהוה וחטאתם לפני יהוה על שגגתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 26, 'ונסלח לכל עדת בני ישראל ולגר הגר בתוכם כי לכל העם בשגגה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 27, 'ואם נפש אחת תחטא בשגגה והקריבה עז בת שנתה לחטאת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 28, 'וכפר הכהן על הנפש השגגת בחטאה בשגגה לפני יהוה לכפר עליו ונסלח לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 29, 'האזרח בבני ישראל ולגר הגר בתוכם תורה אחת יהיה לכם לעשה בשגגה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 30, 'והנפש אשר תעשה ביד רמה מן האזרח ומן הגר את יהוה הוא מגדף ונכרתה הנפש ההיא מקרב עמיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 31, 'כי דבר יהוה בזה ואת מצותיו הפר הכרת תכרת הנפש ההיא עונה בה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 32, 'ויהיו בני ישראל במדבר וימצאו איש מקשש עצים ביום השבת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 33, 'ויקריבו אתו המצאים אתו מקשש עצים אל משה ואל אהרן ואל כל העדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 34, 'ויניחו אתו במשמר כי לא פרש מה יעשה לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 35, 'ויאמר יהוה אל משה מות יומת האיש רגמו אתו באבנים כל העדה מחוץ למחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 36, 'ויוציאו אתו כל העדה אל מחוץ למחנה וירגמו אתו באבנים וימת כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 37, 'ויאמר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 38, 'דבר אל בני ישראל ואמרת אליהם ועשו להם ציציות על כנפי בגדיהם לדרתם ונתנו על ציצת הכנף פתיל תכלת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 39, 'והיו לכם לציציות וראיתם אתם וזכרתם את כל מצות יהוה ועשיתם אתם ולא תתרו אחרי לבבכם ואחרי עיניכם אשר אתם זנים אחריהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 40, 'למען תזכרו ועשיתם את כל מצותי והייתם קדשים לאלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 15, 41, 'אני יהוה אלהיכם אשר הוצאתי אתכם מארץ מצרים להיות לכם לאלהים אני יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 1, 'ויקח קרח בן יצהר בן קהת בן לוי ודתן ואבירם בני אליאב ואון בן פלת בן ראובן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 2, 'ויקמו לפני משה ואנשים מבני ישראל חמשים ומאתים נשיאי עדה קריאי מועד אנשי שם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 3, 'ויקהלו על משה ועל אהרן ויאמרו אליהם רב לכם כי כל העדה כלם קדשים ובתוכם יהוה ומדוע תתנשאו על קהל יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 4, 'וישמע משה ויפל על פניו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 5, 'וידבר אל קרח ואל כל עדתו לאמר בקר ויודע יהוה את אשר לו ואת הקדוש יקריב אליו ואת אשר יבחר בו יקריב אליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 6, 'זאת עשו קחו לכם מחתות קרח וכל עדתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 7, 'ותנו בהן אש ושימו עליהן קטרת לפני יהוה מחר והיה האיש אשר יבחר יהוה הוא הקדוש רב לכם בני לוי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 8, 'ויאמר משה אל קרח . . שמעו נא בני לוי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 9, 'המעט מכם כי הבדיל אלהי ישראל אתכם מעדת ישראל להקריב אתכם אליו לעבד את עבדת משכן יהוה ולעמד לפני העדה לשרתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 10, 'ויקריב אתך ואת כל אחיך בני לוי אתך ובקשתם גם כהנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 11, 'לכן אתה וכל עדתך הנועדים על יהוה ואהרן מה הוא כי תלנו עליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 12, 'וישלח משה לקרא לדתן ולאבירם בני אליאב ויאמרו לא נעלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 13, 'המעט כי העליתנו מארץ זבת חלב ודבש להמיתנו במדבר כי תשתרר עלינו גם השתרר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 14, 'אף לא אל ארץ זבת חלב ודבש הבאתנו ותתן לנו נחלת שדה וכרם העיני האנשים ההם תנקר לא נעלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 15, 'ויחר למשה מאד ויאמר אל יהוה אל תפן אל מנחתם לא חמור אחד מהם נשאתי ולא הרעתי את אחד מהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 16, 'ויאמר משה אל קרח אתה וכל עדתך היו לפני יהוה אתה והם ואהרן מחר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 17, 'וקחו איש מחתתו ונתתם עליהן קטרת והקרבתם לפני יהוה איש מחתתו חמשים ומאתים מחתת ואתה ואהרן איש מחתתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 18, 'ויקחו איש מחתתו ויתנו בהן אש וישמו עליהן קטרת ויעמדו פתח אהל מועד ומשה ואהרן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 19, 'ויקהל עליהם קרח את כל העדה אל פתח אהל מועד וירא כבוד יהוה אל כל העדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 20, 'וידבר יהוה אל משה ואל אהרן לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 21, 'הבדלו מתוך העדה הזאת ואכלה אתם כרגע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 22, 'ויפלו על פניהם ויאמרו אל אלהי הרוחות לכל הבשר האיש האחד יחטא ועל כל העדה תקצף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 23, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 24, 'דבר אל העדה לאמר העלו מסביב למשכן קרח דתן ואבירם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 25, 'ויקם משה וילך אל דתן ואבירם וילכו אחריו זקני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 26, 'וידבר אל העדה לאמר סורו נא מעל אהלי האנשים הרשעים האלה ואל תגעו בכל אשר להם פן תספו בכל חטאתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 27, 'ויעלו מעל משכן קרח דתן ואבירם מסביב ודתן ואבירם יצאו נצבים פתח אהליהם ונשיהם ובניהם וטפם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 28, 'ויאמר משה בזאת תדעון כי יהוה שלחני לעשות את כל המעשים האלה כי לא מלבי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 29, 'אם כמות כל האדם ימתון אלה ופקדת כל האדם יפקד עליהם לא יהוה שלחני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 30, 'ואם בריה יברא יהוה ופצתה האדמה את פיה ובלעה אתם ואת כל אשר להם וירדו חיים שאלה וידעתם כי נאצו האנשים האלה את יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 31, 'ויהי ככלתו לדבר את כל הדברים האלה ותבקע האדמה אשר תחתיהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 32, 'ותפתח הארץ את פיה ותבלע אתם ואת בתיהם ואת כל האדם אשר לקרח ואת כל הרכוש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 33, 'וירדו הם וכל אשר להם חיים שאלה ותכס עליהם הארץ ויאבדו מתוך הקהל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 34, 'וכל ישראל אשר סביבתיהם נסו לקולם כי אמרו פן תבלענו הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 16, 35, 'ואש יצאה מאת יהוה ותאכל את חמשים ומאתים איש מקרבי הקטרת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 2, 'אמר אל אלעזר בן אהרן הכהן וירם את המחתות מבין השרפה ואת האש זרה הלאה כי קדשו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 3, 'את מחתות החטאים האלה בנפשתם ועשו אתם רקועי פחים צפוי למזבח כי הקריבם לפני יהוה ויקדשו והיו לאות לבני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 4, 'ויקח אלעזר בן אהרן הכהן את מחתות הנחשת אשר הקריבו השרפים וירקעם צפוי למזבח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 5, 'זכרון לבני ישראל למען אשר לא יקרב איש זר אשר לא מזרע אהרן הוא להקטיר קטרת לפני יהוה ולא יהיה כקרח וכעדתו כאשר דבר יהוה ביד משה לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 6, 'וילנו כל עדת בני ישראל ממחרת על משה ועל אהרן לאמר אתם המתם את עם יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 7, 'ויהי בהקהל העדה על משה ועל אהרן ויפנו אל אהל מועד והנה כסהו הענן וירא כבוד יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 8, 'ויבא משה ואהרן אל פני אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 9, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 10, 'הרמו מתוך העדה הזאת ואכלה אתם כרגע ויפלו על פניהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 11, 'ויאמר משה אל אהרן קח את המחתה ותן עליה אש מעל המזבח ושים קטרת והלך מהר אל העדה וכפר עליהם כי יצא הקצף מלפני יהוה החל הנגף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 12, 'ויקח אהרן כאשר דבר משה וירץ אל תוך הקהל והנה החל הנגף בעם ויתן את הקטרת ויכפר על העם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 13, 'ויעמד בין המתים ובין החיים ותעצר המגפה . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 14, 'ויהיו המתים במגפה ארבעה עשר אלף ושבע מאות מלבד המתים על דבר קרח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 15, 'וישב אהרן אל משה אל פתח אהל מועד והמגפה נעצרה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 16, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 17, 'דבר אל בני ישראל וקח מאתם מטה מטה לבית אב מאת כל נשיאיהם לבית אבתם שנים עשר מטות איש את שמו תכתב על מטהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 18, 'ואת שם אהרן תכתב על מטה לוי כי מטה אחד לראש בית אבתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 19, 'והנחתם באהל מועד לפני העדות אשר אועד לך שמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 20, 'והיה האיש אשר אבחר בו מטהו יפרח והשכתי מעלי את תלנות בני ישראל אשר הם מלנים עליכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 21, 'וידבר משה אל בני ישראל ויתנו אליו כל נשיאיהם מטה לנשיא אחד מטה לנשיא אחד לבית אבתם שנים עשר מטות ומטה אהרן בתוך מטותם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 22, 'וינח משה את המטות לפני יהוה באהל העדות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 23, 'ויהי ממחרת ויבא משה אל אהל העדות והנה פרח מטה אהרן לבית לוי ויוצא פרח ויצץ ציץ ויגמל שקדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 24, 'ויוצא משה את כל המטות מלפני יהוה אל כל בני ישראל ויראו ויקחו איש מטהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 25, 'ויאמר יהוה אל משה השב את מטה אהרן לפני העדות למשמרת לאות לבני מרי ותכל תלנותם מעלי ולא ימותו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 26, 'ויעש משה כאשר צוה יהוה אתו כן עשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 27, 'ויאמרו בני ישראל אל משה לאמר הן גוענו אבדנו כלנו אבדנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 17, 28, 'כל הקרב הקרב אל משכן יהוה יומת האם תמנו לגוע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 1, 'ויאמר יהוה אל אהרן אתה ובניך ובית אביך אתך תשאו את עון המקדש ואתה ובניך אתך תשאו את עון כהנתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 2, 'וגם את אחיך מטה לוי שבט אביך הקרב אתך וילוו עליך וישרתוך ואתה ובניך אתך לפני אהל העדות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 3, 'ושמרו משמרתך ומשמרת כל האהל אך אל כלי הקדש ואל המזבח לא יקרבו ולא ימותו גם הם גם אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 4, 'ונלוו עליך ושמרו את משמרת אהל מועד לכל עבדת האהל וזר לא יקרב אליכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 5, 'ושמרתם את משמרת הקדש ואת משמרת המזבח ולא יהיה עוד קצף על בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 6, 'ואני הנה לקחתי את אחיכם הלוים מתוך בני ישראל לכם מתנה נתנים ליהוה לעבד את עבדת אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 7, 'ואתה ובניך אתך תשמרו את כהנתכם לכל דבר המזבח ולמבית לפרכת ועבדתם עבדת מתנה אתן את כהנתכם והזר הקרב יומת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 8, 'וידבר יהוה אל אהרן ואני הנה נתתי לך את משמרת תרומתי לכל קדשי בני ישראל לך נתתים למשחה ולבניך לחק עולם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 9, 'זה יהיה לך מקדשי הקדשים מן האש כל קרבנם לכל מנחתם ולכל חטאתם ולכל אשמם אשר יאשמו לי קדש קדשים לך הוא ולבניך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 10, 'בקדש הקדשים תאכלנו כל זכר יאכל אתו קדש יהיה לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 11, 'וזה לך תרומת מתנם לכל תנופת בני ישראל לך נתתים ולבניך ולבנתיך אתך לחק עולם כל טהור בביתך יאכל אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 12, 'כל חלב יצהר וכל חלב תירש ודגן ראשיתם אשר יתנו ליהוה לך נתתים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 13, 'בכורי כל אשר בארצם אשר יביאו ליהוה לך יהיה כל טהור בביתך יאכלנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 14, 'כל חרם בישראל לך יהיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 15, 'כל פטר רחם לכל בשר אשר יקריבו ליהוה באדם ובבהמה יהיה לך אך פדה תפדה את בכור האדם ואת בכור הבהמה הטמאה תפדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 16, 'ופדיו מבן חדש תפדה בערכך כסף חמשת שקלים בשקל הקדש עשרים גרה הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 17, 'אך בכור שור או בכור כשב או בכור עז לא תפדה קדש הם את דמם תזרק על המזבח ואת חלבם תקטיר אשה ריח ניחח ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 18, 'ובשרם יהיה לך כחזה התנופה וכשוק הימין לך יהיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 19, 'כל תרומת הקדשים אשר ירימו בני ישראל ליהוה נתתי לך ולבניך ולבנתיך אתך לחק עולם ברית מלח עולם הוא לפני יהוה לך ולזרעך אתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 20, 'ויאמר יהוה אל אהרן בארצם לא תנחל וחלק לא יהיה לך בתוכם אני חלקך ונחלתך בתוך בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 21, 'ולבני לוי הנה נתתי כל מעשר בישראל לנחלה חלף עבדתם אשר הם עבדים את עבדת אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 22, 'ולא יקרבו עוד בני ישראל אל אהל מועד לשאת חטא למות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 23, 'ועבד הלוי הוא את עבדת אהל מועד והם ישאו עונם חקת עולם לדרתיכם ובתוך בני ישראל לא ינחלו נחלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 24, 'כי את מעשר בני ישראל אשר ירימו ליהוה תרומה נתתי ללוים לנחלה על כן אמרתי להם בתוך בני ישראל לא ינחלו נחלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 25, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 26, 'ואל הלוים תדבר ואמרת אליהם . כי תקחו מאת בני ישראל את המעשר אשר נתתי לכם מאתם בנחלתיכם והרמתם ממנו את תרומת יהוה מעשר מן המעשר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 27, 'ונחשב לכם תרומתיכם כדגן מן הגרן וכמלאה מן היקב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 28, 'כן תרימו גם אתם את תרומת יהוה מכל מעשרתיכם אשר תקחו מאת בני ישראל ונתתם ממנו את תרומת יהוה לאהרן הכהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 29, 'מכל מתנתיכם תרימו את כל תרומת יהוה מכל חלבו את מקדשו ממנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 30, 'ואמרת אליהם בהרמכם את חלבו ממנו ונחשב ללוים . כתבואת . גרן וכתבואת . יקב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 31, 'ואכלתם אתו בכל מקום אתם ובתיכם כי שכר הוא לכם חלף עבדתכם באהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 18, 32, 'ולא תשאו עליו חטא בהרמכם את חלבו ממנו ואת קדשי בני ישראל לא תחללו ולא תמותו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 19, 1, 'וידבר יהוה אל משה ואל אהרן לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 19, 2, 'זאת חקת התורה אשר צוה יהוה לאמר דבר אל בני ישראל ויקחו אליך פרה אדמה תמימה אשר אין בה מום ואשר לא עלה עליה עול ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 19, 3, 'ונתתם אתה אל אלעזר הכהן והוציא אתה אל מחוץ למחנה ושחט אתה לפניו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 19, 4, 'ולקח אלעזר הכהן מדמה באצבעו והזה אל נכח פני אהל מועד מדמה שבע פעמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 19, 5, 'ושרף את הפרה לעיניו את עורה ואת בשרה ואת דמה על פרשה ישרף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 19, 6, 'ולקח הכהן עץ ארז ואזוב ושני תולעת והשליך אל תוך שרפת הפרה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 19, 7, 'וכבס בגדיו הכהן ורחץ את בשרו במים ואחר יבוא אל המחנה וטמא הכהן עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 19, 8, 'והשרף אתה יכבס בגדיו במים ורחץ את בשרו במים וטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 19, 9, 'ואסף איש טהור את אפר הפרה והניח מחוץ למחנה במקום טהור והיתה לעדת בני ישראל למשמרת למי נדה חטאת היא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 19, 10, 'וכבס האסף את אפר הפרה את בגדיו וטמא עד הערב והיתה לבני ישראל ולגר הגר בתוכם לחקת עולם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 19, 11, 'הנגע במת לכל נפש אדם יטמא שבעת ימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 19, 12, 'הוא יתחטא בו ביום השלישי וביום השביעי וטהר ואם לא יתחטא ביום השלישי וביום השביעי לא יטהר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 19, 13, 'כל הנגע במת בנפש אדם אשר ימות ולא יתחטא את משכן יהוה טמא ונכרתה הנפש ההיא מישראל כי מי נדה לא זרק עליו טמא יהיה עוד טמאתו בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 19, 14, 'וזאת התורה אדם כי ימות באהל כל הבא אל האהל וכל אשר באהל יטמא שבעת ימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 19, 15, 'וכל כלי פתוח אשר אין צמיד פתיל עליו טמא הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 19, 16, 'וכל אשר יגע על פני השדה בחלל חרב או במת או בעצם אדם או בקבר יטמא שבעת ימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 19, 17, 'ולקחו לטמא מעפר שרפת החטאת ונתנו עליו מים חיים אל כלי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 19, 18, 'ולקח אזוב וטבל במים איש טהור והזה על האהל ועל . הכלים ועל הנפשות אשר היו שם ועל הנגע בעצם או בחלל או במת או בקבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 19, 19, 'והזה הטהור על הטמא ביום השלישי וביום השביעי וחטאו ביום השביעי וכבס בגדיו ורחץ במים וטהר בערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 19, 20, 'והאיש אשר יטמא ולא יתחטא ונכרתה הנפש ההיא מתוך הקהל כי את מקדש יהוה טמא מי נדה לא זרק עליו טמא הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 19, 21, 'והיתה להם לחקת עולם ומזה מי הנדה יכבס בגדיו והנגע במי הנדה יטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 19, 22, 'וכל אשר יגע בו הטמא יטמא והנפש הנגעת תטמא עד הערב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 1, 'ויבאו בני ישראל כל העדה מדבר צן בחדש הראישון וישב העם בקדש ותמת שם מרים ותקבר שם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 2, 'ולא היה מים לעדה ויקהלו על משה ועל אהרן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 3, 'וירב העם עם משה ויאמרו לאמר ולוי גוענו בגוע אחינו לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 4, 'ולמה הבאתם את קהל יהוה אל המדבר הזה למות שם אנחנו ובעירנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 5, 'ולמה העליתנו ממצרים להביא אתנו אל המקום הרע הזה לא מקום זרע ותאנה וגפן ורמון ומים אין לשתות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 6, 'ויבא משה ואהרן מפני הקהל אל פתח אהל מועד ויפלו על פניהם וירא כבוד יהוה אליהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 7, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 8, 'קח את המטה והקהל את העדה אתה ואהרן אחיך ודברתם אל הסלע לעיניהם ונתן מימיו והוצאת להם מים מן הסלע והשקית את העדה ואת בעירם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 9, 'ויקח משה את המטה מלפני יהוה כאשר צוהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 10, 'ויקהלו משה ואהרן את הקהל אל פני הסלע ויאמר להם שמעו נא המרים המן הסלע הזה נוצא לכם מים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 11, 'וירם משה את ידו ויך את הסלע במטהו פעמים ויצאו מים רבים ותשת העדה ובעירם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 12, 'ויאמר יהוה אל משה ואל אהרן יען לא האמנתם בי להקדישני לעיני בני ישראל לכן לא תביאו את הקהל הזה אל הארץ אשר נתתי להם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 13, 'הם מי מריבה אשר רבו בני ישראל את יהוה ויקדש בם ויאמר משה אדני יהוה אתה החלת להראות את עבדך את גדלך ואת ידך החזקה אשר מי אל בשמים ובארץ אשר יעשה כמעשיך וכגבורתך אעברה נא ואראה את הארץ הטובה . אשר בעבר הירדן ההר הטוב הזה והלבנון ויאמר יהוה אל משה רב לך אל תוסף דבר אלי עוד בדבר הזה עלה אל ראש הפסגה ושא עיניך ימה וצפונה ותימנה ומזרחה וראה בעיניך כי לא תעבר את הירדן הזה וצוי את יהושע בן נון וחזקהו ואמצהו כי הוא יעבר לפני העם הזה והוא ינחל אתם את הארץ אשר תראה וידבר יהוה אל משה לאמר רב לכם סוב את ההר הזה פנו לכם צפונה ואת העם צוי לאמר אתם עברים בגבול אחיכם בני עשו הישבים בשעיר וייראו מכם ונשמרתם מאד אל תתגרו בם כי לא אתן לכם מארצם ירשה עד מדרך כף רגל כי ירשה לעשו נתתי את הר שעיר אכל תשברו מאתם בכםף ואכלתם וגם מים תכירו מאתם בכסף ושתיתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 14, 'וישלח משה מלאכים מקדש אל מלך אדום כה אמר אחיך ישראל אתה ידעת את כל התלאה אשר מצאתנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 15, 'וירדו אבתינו מצרימה ונשב במצרים ימים רבים ויריעו לנו המצרים ולאבתינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 16, 'ונצעק אל יהוה וישמע קולנו וישלח מלאך ויוציאנו ממצרים והנה אנחנו בקדש עיר קצה גבולך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 17, 'נעברה נא בארצך לא נעבר בשדה ובכרם ולא נשתה מי בור דרך המלך נלך לא נסור ימין ושמאל עד אשר נעבר גבולך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 18, 'ויאמר אליו אדום לא תעבר בי פן בחרב אצא לקראתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 19, 'ויאמרו אליו בני ישראל במסלה נעלה ואם ממימיך נשתה אני ומקני ונתתי מכרם רק אין דבר ברגלי אעבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 20, 'ויאמר לא תעבר . . . . . ויצא אדום לקראתו בעם כבד וביד חזקה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 21, 'וימאן אדום נתן את ישראל עבר בגבולו ויט ישראל מעליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 22, 'ויסעו מקדש ויבאו בני ישראל כל העדה הר ההר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 23, 'ויאמר יהוה אל משה ואל אהרן בהר ההר על גבול ארץ אדום לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 24, 'יאסף אהרן אל עמו כי לא יבוא אל הארץ אשר נתתי לבני ישראל על אשר מריתם את פי למי מריבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 25, 'קח את אהרן ואת אלעזר בנו והעל אתם הר ההר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 26, 'והפשטת את אהרן את בגדיו והלבשתם את אלעזר בנו ואהרן יאסף וימת שם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 27, 'ויעש משה כאשר צוה יהוה ויעלהו אל הר ההר לעיני כל העדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 28, 'ויפשט משה את אהרן את בגדיו וילבש אתם את אלעזר בנו וימת אהרן שם בראש ההר וירד משה ואלעזר מן ההר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 20, 29, 'ויראו כל העדה כי גוע אהרן ויבכו את אהרן שלשים יום כל בית ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 1, 'וישמע הכנעני מלך ערד ישב הנגב כי בא ישראל דרך האתרים וילחם בישראל וישב ממנו שבי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 2, 'וידר ישראל נדר ליהוה ויאמר אם נתן תתן את העם הזה בידי והחרמתי את עריהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 3, 'וישמע יהוה בקול ישראל ויתן את הכנעני בידו ויחרם אתם ואת עריהם ויקרא שם המקום חרמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 4, 'ויסעו מהר ההר דרך ים סוף לסבב את ארץ אדום ותקצר נפש העם בדרך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 5, 'וידבר העם באלהים ובמשה למה הוצאתנו ממצרים למות במדבר כי אין לחם ואין מים ונפשנו קצה בלחם הקלקל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 6, 'וישלח יהוה בעם את הנחשים השרפים וינשכו את העם יומת עם רב מישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 7, 'ויבא העם אל משה ויאמרו חטאנו כי דברנו ביהוה ובך התפלל אל יהוה ויסר מעלינו את הנחש ויתפלל משה בעד העם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 8, 'ויאמר יהוה אל משה עשה לך שרוף ושים אתו על הנס והיה כל הנשך וראה אתו וחי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 9, 'ויעש משה נחש נחשת וישמהו על הנס והיה אם נשך הנחש את האיש והביט אל נחש הנחשת וחי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 10, 'ויסעו בני ישראל ויחנו באבות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 11, 'ויסעו מאבות ויחנו בעיי העברים במדבר אשר על פני מואב מזרח השמש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 12, 'ויאמר יהוה אל משה אל תצור את מואב ואל תתגר בם . כי לא אתן לך מארצו ירשה כי לבני לוט נתתי את ער ירשה משם נסעו ויחנו בנחל זרד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 13, 'וידבר יהוה אל משה לאמר אתה עבר היום את גבול מואב את ער וקרבת מול בני עמון אל תצורם ואל תתגר בם כי לא אתן מארץ בני עמון לך ירשה כי לבני לוט נתתיה ירשה ויסעו מנחל זרד ויחנו בעבר ארנן אשר על המדבר היוצא מגבול האמרי כי ארנן גבול מואב בין מואב ובין האמרי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 14, 'על כן יאמר בספר מלחמות יהוה את והב בסופה ואת הנחלים ארנן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 15, 'אשר הנחלם ואשר נטה לשבת עיר ונשען לגבול מואב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 16, 'ומשם בארה היא הבאר אשר אמר יהוה אל משה אספה לי את העם ואתן להם מים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 17, 'אז ישר ישראל את השירה הזאת עלי באר ענו לה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 18, 'באר חפרוה שרים כרוה נדיבי העם במחקק ובמשענתם ממדבר מתנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 19, 'וממתנה נחלאל ומנחלאל במות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 20, 'ומבמות הגיא אשר בשדה מואב ראש הפסגה הנשקפה על פני הישמון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 21, 'ויאמר יהוה אל משה קומו סעו ועברו את נחל ארנן ראה נתתי בידך את סיחון מלך חשבון האמרי ואת ארצו החל רש . . . והתגר בו מלחמה היום הזה החל תת פחדך ויראתך על פני העמים תחת כל השמים אשר ישמעו את שמעך ורגזו וחלו מפניך וישלח ישראל מלאכים אל סיחון מלך האמרי דברי שלום לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 22, 'אעברה בארצך בדרך המלך אלך לא אסור ימין ושמאל לא אטה בשדה ובכרם אכל בכסף תשברני ואכלתי ומים בכסף תתן לי ושתיתי רק אעברה ברגלי כאשר עשו לי בני עשו הישבים בשעיר והמואבים הישבים בער . . . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 23, 'ולא נתן סיחון את ישראל עבר בגבולו ויאמר יהוה אל משה ראה החלתי תת לפניך את סיחון ואת ארצו החל רש לרשת את ארצו ויאסף סיחון את כל עמו ויצא לקראת ישראל המדברה ויבא יחצה וילחם בישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 24, 'ויכהו ישראל ואת בניו ואת כל עמו לפי חרב ויירשו את ארצו מארנן עד יבק עד בני עמון כי עז גבול בני עמון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 25, 'ויקח ישראל את כל הערים האלה וישב ישראל בכל ערי האמרי בחשבון ובכל בנתיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 26, 'כי חשבון עיר סיחון מלך האמרי היא והוא הנלחם במלך מואב הראישון ויקח את כל ארצו מידו עד ארנן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 27, 'על כן יאמרו המשלים באי חשבון תבנה ותכונן עיר סיחון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 28, 'כי אש יצאה מחשבון להבה מקרית סיחון אכלה ער מואב בעלי במות ארנן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 29, 'אוי לך מואב אבדת עם כמוש נתן בניו פלטים ובנתיו בשבי למלך האמרי סיחון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 30, 'ונרם אבדה חשבון עד דיבון ונשים עד הנפח אש על מידבא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 31, 'וישב ישראל בערי האמרי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 32, 'וישלח משה לרגל את יעזר וילכדו בנתיה ויורשו את האמרי אשר שם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 33, 'ויפנו ויעלו דרך הבשן ויצא עוג מלך הבשן לקראתם הוא וכל עמו למלחמה אדרעי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 34, 'ויאמר יהוה אל משה אל תירא אתו כי בידך נתתי אתו ואת כל עמו ואת ארצו ועשית לו כאשר עשית לסיחון מלך האמרי אשר ישב בחשבון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 21, 35, 'ויכו אתו ואת . . כל עמו עד בלתי השאיר לו שריד ויירשו את ארצו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 1, 'ויסעו בני ישראל ויחנו בערבת מואב מעבר לירדן יריחו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 2, 'וירא בלק בן צפור את כל אשר עשה ישראל לאמרי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 3, 'ויגר מואב מפני העם מאד כי רב הוא ויקץ מואב מפני בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 4, 'ויאמר מואב אל זקני מדין עתה ילחך הקהל הזה את כל סביבתינו כלחך השור את ירק השדה ובלק בן צפור מלך למואב בעת ההוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 5, 'וישלח מלאכים אל בלעם בן בעור פתרה אשר על הנהר ארץ בני עמון לקרא לו לאמר הן עם יצא ממצרים והנה כסה את עין הארץ והוא ישב ממולי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 6, 'ועתה לכה נא ארה לי את העם הזה כי עצום הוא ממני אולי אוכל נכה בו ואגרשנו מן הארץ כי ידעתי את אשר תברך מברך ואת אשר תאר ויאר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 7, 'וילכו זקני מואב וזקני מדין וקסמיהם בידם ויבאו אל בלעם וידברו אליו . דברי בלק ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 8, 'ויאמר אליהם לינו פה הלילה והשבתי אתכם דבר כאשר ידבר יהוה אלי וישבו שרי מואב עם בלעם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 9, 'ויבא אלהים אל בלעם ויאמר . מה האנשים האלה עמך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 10, 'ויאמר בלעם אל האלהים בלק בן צפור מלך מואב שלח אלי . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 11, 'הן עם יצא ממצרים ויכס את עין הארץ . . . ועתה לכה קבה לי אתו אולי אוכל להלחם בו וגרשתיו . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 12, 'ויאמר אלהים אל בלעם לא תלך עמהם . ולא תאר את העם כי ברוך הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 13, 'ויקם בלעם בבקר ויאמר אל שרי בלק לכו אל ארצכם כי מאן יהוה לתתי להלך עמכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 14, 'ויקמו שרי מואב ויבאו אל בלק ויאמרו . מאן בלעם הלך עמנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 15, 'ויסף עוד בלק שלח שרים רבים ונכבדים מאלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 16, 'ויבאו אל בלעם ויאמרו לו כה אמר בלק בן צפור . . אל נא תמנע מהלך אלי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 17, 'כי כבד אכבדך מאד וכל אשר תאמר אלי אעשה . ולכה נא קבה לי את העם הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 18, 'ויען בלעם ויאמר אל עבדי בלק אם יתן לי בלק מלוא ביתו כסף וזהב לא אוכל לעבר את פי יהוה אלהי לעשות קטנה או גדלה . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 19, 'ועתה שבו נא בזה גם אתם הלילה ואדעה מה יסף יהוה דבר עמי . . . . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 20, 'ויבא מלאך אלהים אל בלעם לילה ויאמר לו אם לקרא לך באו האנשים . קום לך אתם ואך את הדבר אשר אדבר אליך אתו תעשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 21, 'ויקם בלעם בבקר ויחבש את אתנו וילך עם שרי מואב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 22, 'ויחר אף יהוה כי הלך הוא ויתיצב מלאך יהוה בדרך לשטן לו והוא רכב על אתנו ושני נעריו עמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 23, 'ותרא האתון את מלאך יהוה נצב בדרך וחרבו שלופה בידו ותט האתון מן הדרך ותלך בשדה ויך בלעם את האתון להטותה הדרך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 24, 'ויעמד מלאך יהוה במשעל הכרמים גדר מזה וגדר מזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 25, 'ותרא האתון את מלאך יהוה ותלחץ אל הקיר ותלחץ את רגל בלעם אל הקיר ויסף להכותה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 26, 'ויסף מלאך יהוה עבר ויעמד במקום צר אשר אין דרך לנטות ימין ושמאל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 27, 'ותרא האתון את מלאך יהוה ותרבץ תחת בלעם ויחר אף בלעם ויך את האתון במקל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 28, 'ויפתח יהוה את פי האתון ותאמר אל בלעם מה עשיתי לך כי הכיתני זה שלש רגלים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 29, 'ויאמר בלעם לאתון כי התעללתי בי לו יש חרב בידי כי עתה הרגתיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 30, 'ותאמר האתון אל בלעם הלא אנכי אתנך אשר רכבת עלי מעודך עד היום הזה הסכן הסכנתי לעשות לך כה ויאמר לא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 31, 'ויגל יהוה את עיני בלעם וירא את מלאך יהוה נצב בדרך וחרבו שלופה בידו ויקד וישתחוי לאפיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 32, 'ויאמר אליו מלאך יהוה על מה הכית את אתנך זה שלוש רגלים הנה אנכי יצאתי לשטנך . כי הרע דרכך לנגדי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 33, 'ותראני האתון ותט מלפני זה שלש רגלים אולי נטתה מפני כי עתה גם אתך הכיתי ואתה החייתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 34, 'ויאמר בלעם אל מלאך יהוה חטאתי כי לא ידעתי כי אתה נצב לקראתי בדרך ועתה אם רע בעיניך אשובה לי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 35, 'ויאמר מלאך יהוה אל בלעם לך עם האנשים ואפס את הדבר אשר אדבר אליך אתו תשמר תדבר וילך בלעם עם שרי בלק ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 36, 'וישמע בלק כי בא בלעם ויצא לקראתו אל עיר מואב אשר על גבול ארנן אשר בקצה הגבול ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 37, 'ויאמר בלק אל בלעם הלא שלח שלחתי אליך לקרא לך למה לא הלכת אלי האמנם לא אוכל כבדך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 38, 'ויאמר בלעם אל בלק הנה באתי אליך עתה היכל אוכל דבר מאומה הדבר אשר ישים אלהים בפיי אתו אשמר לדבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 39, 'וילך בלעם עם בלק ויבאהו קרית חצות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 40, 'ויזבח בלק בקר וצאן וישלח לבלעם ולשרים אשר אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 22, 41, 'ויהי בבקר ויקח בלק את בלעם ויעלהו במות בעל וירא משם קצה העם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 1, 'ויאמר בלעם אל בלק עשה לי בזה שבעה מזבחות והכן לי בזה שבעה פרים ושבעה אילים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 2, 'ויעש בלק כאשר דבר בלעם ויעל בלק ובלעם פר ואיל במזבח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 3, 'ויאמר בלעם אל בלק התיצב על עלתיך ואלכה . אולי יקרא אלהים לקראתי ודבר מה יראני והגדתי לך וילך . . . . . . . . . שפי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 4, 'וימצא מלאך אלהים את בלעם ויאמר אליו את שבעת המזבחת ערכתי ואעלה פר ואיל במזבח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 5, 'וישם מלאך יהוה דבר בפי בלעם ויאמר שוב אל בלק וכה תדבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 6, 'וישב אליו והנה נצב על עלתיו הוא וכל שרי מואב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 7, 'וישא משלו ויאמר . מארם ינחני בלק מלך מואב מהררי קדם לכה ארה לי יעקב ולכה זעמה ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 8, 'מה אקב לא קבה אל ומה אזעם לא זעם יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 9, 'כי מראש צורים אראנו ומגבעות אשורנו הן עם לבדד ישכן ובגוים לא יתחשב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 10, 'מי מנה עפר יעקב ומי ספר . מרבעת ישראל תמות נפשי מות ישרים ותהי אחריתי כמהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 11, 'ויאמר בלק אל בלעם מה עשית לי לקב איבי לקחתיך והנה ברכת ברוך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 12, 'ויען ויאמר הלוא את אשר ישים יהוה בפיי אתו אשמר לדבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 13, 'ויאמר אליו בלק לך נא אתי אל מקום אחר . תראנו משם אפס קצהו תראה וכלו לא תראה וקבנו לי משם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 14, 'ויקחהו שדה צפים אל ראש הפסגה ויבן . שבעה מזבחות ויעל פר ואיל במזבח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 15, 'ויאמר . אל בלק התיצב . על עלתיך ואנכי אקרא כה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 16, 'ויקרא מלאך יהוה אל בלעם וישם דבר בפיו ויאמר שוב אל בלק וכה תדבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 17, 'ויבא אליו והנה נצב על עלתיו ושרי מואב אתו ויאמר אליו בלק מה דבר יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 18, 'וישא משלו ויאמר קומה בלק ושמעה האזינה עדי בנו צפור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 19, 'לא איש אל ויכזב ובן אדם ויתנחם ההוא אמר ולא יעשה דבר ולא יקימנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 20, 'הן לברך לקחתי אברך ולא אשיבנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 21, 'לא אביט עון ביעקב ולא ראה עמל בישראל יהוה אלהיו עמו ותרועת מלך בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 22, 'אל מוציאם ממצרים כתעפת ראם לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 23, 'כי לא נחש ביעקב ולא קסם בישראל כעת יאמר ליעקב ולישראל מה פעל אל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 24, 'הן עם כלביה יקום וכאריה יתנשא לא ישכב עד יאכל טרף ודם חללים ישתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 25, 'ויאמר בלק אל בלעם גם קב לא תקבנו וגם ברוך לא תברכנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 26, 'ויען בלעם ויאמר אל בלק הלא דברתי אליך לאמר כל הדבר אשר ידבר האלהים אתו אעשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 27, 'ויאמר בלק אל בלעם לך נא אקחך אל מקום אחר אולי יישר בעיני האלהים וקבתו לי משם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 28, 'ויקח בלק את בלעם ראש הפעור הנשקף על פני הישמון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 29, 'ויאמר בלעם אל בלק בנה לי בזה שבעה מזבחות והכן לי בזה שבעה פרים ושבעה אילים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 23, 30, 'ויעש בלק כאשר דבר בלעם ויעל פר ואיל אל המזבח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 1, 'וירא בלעם כי טוב בעיני יהוה לברך את ישראל ולא הלך כפעם בפעם לקראת הנחשים . . . וישת אל המדבר פניו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 2, 'וישא בלעם את עיניו וירא את ישראל שכן לשבטיו ותהי עליו רוח אלהים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 3, 'וישא משלו ויאמר נאם בלעם בנו בעור ונאם הגבר שתם העין ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 4, '. . . . אשר מחזה שדי יחזה נפל וגלוי עין ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 5, 'מה טבו אהליך יעקב ומשכנתיך ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 6, 'כנחלים נטוים כגנות עלי נהר כאהלים נטה יהוה כארזים עלי מים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 7, 'יזל מים מדליו וזרעו במים רבים וירם מגוג מלכו ותנשא מלכותו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 8, 'אל נחהו ממצרים כתעפת ראם לו יאכל גוים צריו ועצמתיהם יגרם וחציו ימחץ . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 9, 'כרע שכב כאריה וכלביה מי יקימנו מברכיך ברוך וארריך ארור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 10, 'ויחר אף בלק אל בלעם ויספק את כפיו ויאמר בלק אל בלעם לקב איבי קראתי לך והנה ברכת ברוך זה שלש רגלים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 11, 'ועתה ברח לך אל מקומך אמרתי כבד אכבדך והנה מנעך יהוה מכבוד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 12, 'ויאמר בלעם אל בלק הלא גם אל מלאכיך אשר שלחת אלי דברתי לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 13, 'אם יתן לי בלק מלוא ביתו כסף וזהב לא אוכל לעבר את פי יהוה לעשות טובה או רעה מלבי אשר ידבר יהוה אלי אתו אדברה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 14, 'ועתה הנני הלך לעמי לך ואעיצך את אשר יעשה העם הזה לעמך באחרית הימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 15, 'וישא משלו ויאמר נאם בלעם בנו בעור ונאם הגבר שתם העין ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 16, 'נאם שמע אמרי אל וידע דעת עליון מחזה שדי יחזה נפל וגלוי עינים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 17, 'אראנו ולא עתה אשורנו ולא קרוב דרך כוכב מיעקב וקם שבט מישראל ומחץ פאתי מואב וקרקר כל בני שת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 18, 'והיה אדום ירשה והיה ירשה עשו איביו וישראל עשה חיל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 19, 'וירד מיעקב והאביד שריד מעיר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 20, 'וירא את עמלק וישא משלו ויאמר ראשית גוים עמלק ואחריתו עד יאבד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 21, 'וירא את הקיני וישא משלו ויאמר איתן מושבך ושים בסלע קנך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 22, 'כי אם יהיה לבער קין עד . מאשור תשבך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 23, 'וישא משלו ויאמר אוי מי יחיה משמו אל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 24, 'יוציאם מיד כתים וענו אשור ויענו עבר וגם הוא עד יאבד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 24, 25, 'ויקם בלעם וילך וישב למקומו וגם בלק הלך לדרכו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 25, 1, 'וישב ישראל בשטים ויחל העם לזנות אל בנות מואב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 25, 2, 'ותקראנה לעם לזבחי אלהיהן ויאכל העם וישתחוו לאלהיהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 25, 3, 'ויצמד מבני ישראל לבעל פעור ויחר אף יהוה בישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 25, 4, 'ויאמר יהוה אל משה . . . אמר ויהרגו את האנשים הנצמדים לבעל פעור וישב חרון אף יהוה מישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 25, 5, 'ויאמר משה אל שפטי ישראל הרגו איש את אנשיו הנצמדים לבעל פעור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 25, 6, 'והנה איש מבני ישראל בא ויקרב אל אחיו את המדינית לעיני משה ולעיני כל עדת בני ישראל והם בכים פתח אהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 25, 7, 'וירא פינחס בן אלעזר בן אהרן הכהן ויקם מתוך העדה ויקח רמח בידו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 25, 8, 'ויבא אחרי איש ישראל אל הקבה וידקר את שניהם את איש ישראל ואת האשה אל קבתה ותעצר המגפה מעל בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 25, 9, 'ויהיו המתים במגפה ארבעה ועשרים אלף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 25, 10, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 25, 11, 'פינחס בן אלעזר בן אהרן הכהן השיב את חמתי מעל בני ישראל בקנאו את קנאתי בתוכם ולא כליתי את בני ישראל בקנאתי ');
