INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 25, 13, 'והיתה לו ולזרעו אחריו ברית כהנת עולם תחת אשר קנא לאלהיו ויכפר על בני ישראל ');
