INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 26, 57, 'ואלה פקדי הלוים למשפחותם לגרשון משפחת הגרשוני לקהת משפחת הקהתי למררי משפחת המררי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 26, 58, 'אלה משפחות בני לוי משפחת הלבני ומשפחת החברוני ומשפחת המחלי ומשפחת הקרחי ומשפחת המושי וקהת הוליד את עמרם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 26, 59, 'ושם אשת עמרם יוכבד בת לוי אשר ילדה אתה ללוי במצרים ותלד לעמרם את אהרן ואת משה ואת מרים אחתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 26, 60, 'ויולד לאהרן את נדב ואת אביהוא את אלעזר ואת איתמר ');
