INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 26, 62, 'ויהיו פקדיהם שלשה ועשרים אלף כל זכר מבן חדש ומעלה כי לא התפקדו בתוך בני ישראל כי לא נתן להם נחלה בתוך בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 26, 63, 'אלה פקדי משה ואלעזר הכהן אשר פקדו את בני ישראל בערבת מואב על ירדן יריחו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 26, 64, 'ובאלה לא היה איש מפקדי משה ואהרן הכהן אשר פקדו את בני ישראל במדבר סיני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 26, 65, 'כי אמר יהוה להם מות ימותו במדבר ולא נותר מהם איש כי אם כלב בן יפנה ויהושע בן נון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 27, 1, 'ותקרבנה בנות צלפחד בן חפר בן גלעד בן מכיר בן מנשה למשפחת מנשה בן יוסף ואלה שמות בנתיו מחלה ונעה חגלה מלכה ותרצה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 27, 2, 'ותעמדנה לפני משה ולפני אלעזר הכהן ולפני הנשיאים וכל העדה פתח אהל מועד לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 27, 3, 'אבינו מת במדבר והוא לא היה בתוך העדה הנועדים על יהוה בעדת קרח כי בחטאו מת אבינו ובנים לא היו לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 27, 4, 'למה יגרע שם אבינו מתוך משפחתו כי אין לו בן תנו לנו אחזת נחלה בתוך אחי אבינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 27, 5, 'ויקרב משה את משפטן לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 27, 6, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 27, 7, 'כן בנות צלפחד דברות נתן תתן להן אחזת נחלה בתוך אחי אביהן והעברת את נחלת אביהן להן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 27, 8, 'ואל בני ישראל תדבר לאמר איש כי ימות ובן אין לו ונתתם את נחלתו לבתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 27, 9, 'ואם אין לו בת ונתתם את נחלתו לאחיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 27, 10, 'ואם אין לו אחים ונתתם את נחלתו לאחי אביו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 27, 11, 'ואם אין אחים לאביו ונתתם את נחלתו לשארו הקרוב אליו ממשפחתו ויירש אתו והיתה לבני ישראל לחקת משפט כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 27, 12, 'וידבר יהוה אל משה לאמר עלה אל הר העברים הזה וראה את הארץ אשר נתתי לבני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 27, 13, 'וראית אתה ונאספת אל עמך גם אתה כאשר נאסף אהרן אחיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 27, 14, 'אשר מריתם את פי במדבר צן במריבת העדה להקדשני במים לעיניהם הם מי מריבת קדש מדבר צן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 27, 15, 'וידבר משה אל יהוה לאמר ');
