INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 30, 11, 'ואם בית אישה נדרה או אסרה אסר על נפשה בשבעה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 30, 12, 'ושמע אישה והחריש לה לא הניא אתה וקמו כל נדריה וכל אסריה אשר אסרה על נפשה יקומו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 30, 13, 'ואם הפר יפיר אתם אישה ביום שמעו כל מוצא שפתיה לנדריה ולאסר נפשה לא יקומו אישה הפרם ויהוה יסלח לה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 30, 14, 'כל נדר וכל שבעת אסר לענות נפש אישה יקימנו ואישה יפירנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 30, 15, 'ואם החריש יחריש לה אישה מיום אל יום והקים את כל נדריה או את כל אסריה אשר עליה הקים אתם כי החריש לה ביום שמעו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 30, 16, 'ואם הפר יפיר אתם אחרי . שמעו ונשא את עונו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 30, 17, 'אלה החקים אשר צוה יהוה את משה בין איש לאשתו ובין אב לבתו בנעריה בבית אביה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 1, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 2, 'נקם . נקמת בני ישראל מאת המדינים ואחר תאסף אל עמך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 3, 'וידבר משה אל העם . . לאמר החלצו מאתכם אנשים לצבא ויהיו על מדין לתת נקמת יהוה במדין ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 4, 'אלף למטה אלף למטה לכל מטות . ישראל תשלחו לצבא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 5, 'וימסרו מאלפי ישראל אלף למטה . . . . שנים עשר אלף חלוצי צבא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 6, 'וישלח אתם משה אלף למטה . . לצבא אתם ואת פינחס בן אלעזר הכהן לצבא וכלי הקדש וחצצרת התרועה בידו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 7, 'ויצבאו על מדין כאשר צוה יהוה את משה ויהרגו כל זכר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 8, 'ואת מלכי מדין הרגו על חלליהם את אוי ואת רקם ואת צור ואת חור ואת רבע חמשת מלכי מדין ואת בלעם בן בעור הרגו בחרב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 9, 'וישבו בני ישראל את נשי מדין ואת טפם ואת כל בהמתם ואת כל מקניהם ואת כל חילם בזזו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 10, 'ואת כל עריהם במושבתם ואת כל טירתם שרפו באש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 11, 'ויקחו את כל השלל ואת כל המלקח באדם ובבהמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 12, 'ויבאו אל משה ואל אלעזר הכהן ואל כל עדת בני ישראל את השבי ואת המלקח ואת השלל אל המחנה אל ערבת מואב אשר על ירדן יריחו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 13, 'ויצא משה ואלעזר הכהן וכל נשיאי העדה לקראתם אל מחוץ למחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 14, 'ויקצף משה על פקדי החיל שרי האלפים ושרי המאות הבאים מצבא המלחמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 15, 'ויאמר אליהם משה למה החייתם כל נקבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 16, 'הנה הנה היו לבני ישראל בדבר בלעם למסר מעל ביהוה על דבר פעור ותהי המגפה בעדת יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 17, 'ועתה הרגו כל זכר בטף וכל אשה ידעת איש למשכב זכר הרגו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 18, 'וכל הטף בנשים אשר לא ידעו משכב זכר החיו לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 19, 'ואתם חנו לכם מחוץ למחנה שבעת ימים כל הרג נפש וכל הנגע בחלל תתחטאו ביום השלישי וביום השביעי אתם ושביתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 20, 'וכל בגד וכל כלי עור וכל מעשה עזים וכל כלי עץ תתחטאו ויאמר משה אל אלעזר הכהן אמר אל אנשי הצבא הבאים למלחמה זאת חקת התורה אשר צוה יהוה אך את הזהב ואת הכסף ואת הנחשת ואת הברזל ואת הבדיל ואת העופרת כל דבר אשר יבוא באש תעבירו באש וטהר אך במי נדה יתחטא וכל אשר לא יבוא באש תעבירו במים וכבסתם בגדיכם ביום השביעי וטהרתם ואחר תבאו אל המחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 21, 'ויאמר אלעזר הכהן אל אנשי הצבא הבאים למלחמה זאת חקת התורה אשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 22, 'אך את הזהב ואת הכסף את הנחשת ואת הברזל ואת הבדיל ואת העופרת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 23, 'כל דבר אשר יבוא באש תעבירו באש וטהר אך במי נדה יתחטא וכל אשר לא יבוא באש תעבירו במים . . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 24, 'וכבסתם בגדיכם ביום השביעי וטהרתם ואחר תבאו אל המחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 25, 'ויאמר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 26, 'שא את ראש המלקח השבי באדם ובבהמה אתה ואלעזר הכהן וראשי אבות העדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 27, 'וחצית את המלקח בין תפשי המלחמה היוצאים לצבא ובין כל העדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 28, 'והרמת מכס ליהוה מאת אנשי המלחמה היצאים לצבא אחת נפש מחמש מאות מן האדם ומן הבקר ומן החמרים ומן הצאן ומכל הבהמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 29, 'ממחציתם תקח ונתתה לאלעזר הכהן את תרומת יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 30, 'וממחצית בני ישראל תקח אחד אחז מן החמשים מן האדם ומן הבקר ומן החמרים ומן הצאן . . . ומכל הבהמה ונתתה אתם ללוים שמרי משמרת משכן יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 31, 'ויעש משה ואלעזר הכהן כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 32, 'ויהי המלקח יתר הבז אשר בזזו עם הצבא צאן שש מאות אלף ושבעים אלף וחמשת אלפים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 33, 'ובקר שנים ושבעים אלף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 34, 'וחמרים אחד וששים אלף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 35, 'ונפש אדם מן הנשים אשר לא ידעו משכב זכר כל נפש שנים ושלשים אלף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 36, 'ותהי המחצה חלק היוצאים בצבא מספר צאן שלש מאות אלף ושלשים אלף ושבעת אלפים וחמש מאות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 37, 'ויהי המכס ליהוה מן הצאן שש מאות חמשה ושבעים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 38, 'ובקר ששה ושלשים אלף ומכסם ליהוה שנים ושבעים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 39, 'וחמרים שלשים אלף וחמש מאות ומכסם ליהוה אחד וששים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 40, 'ונפש אדם ששה עשר אלף ומכסם ליהוה שנים ושלשים נפש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 41, 'ויתן משה את מכס תרומת יהוה לאלעזר הכהן כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 42, 'וממחצית בני ישראל אשר חצה משה מן האנשים הצבאים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 43, 'ותהי מחצית העדה מן הצאן שלש מאות אלף ושלשים אלף ושבעת אלפים וחמש מאות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 44, 'ובקר ששה ושלשים אלף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 45, 'וחמרים שלשים אלף וחמש מאות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 46, 'ונפש אדם ששה עשר אלף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 47, 'ויקח משה ממחצית בני ישראל את אחז אחד מן החמשים מן האדם ומן הבהמה ויתן אתם אל הלוים שמרי משמרת משכן יהוה כאשר צוה יהוה את ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 48, 'משה ויקרבו אל משה הפקדים אשר לאלפי הצבא שרי האלפים ושרי המאות . . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 49, 'ויאמרו אל משה עבדיך נשאו את ראש אנשי המלחמה אשר בידנו ולא נפקד ממנו איש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 50, 'ונקריב את קרבן יהוה איש אשר מצא כלי זהב אצעדה וצמיד טבעת עגיל וכומז לכפר על נפשתינו לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 51, 'ויקח משה ואלעזר הכהן את הזהב מאתם כל כלי מעשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 52, 'ויהי . זהב התרומה אשר הרימו ליהוה ששה עשר אלף ושבע מאות וחמשים שקל מאת שרי האלפים ומאת שרי המאות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 53, 'אנשי הצבא בזזו איש לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 31, 54, 'ויקח משה ואלעזר הכהן את הזהב מאת שרי האלפים והמאות ויבאו אתו אל אהל מועד זכרון לבני ישראל לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 1, 'ומקנה רב היה לבני ראובן ולבני גד ולחצי שבט המנשה עצום מאד ויראו את ארץ יעזר ואת ארץ גלעד והנה המקום מקום מקנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 2, 'ויבאו בני גד ובני ראובן וחצי שבט המנשה ויאמרו אל משה ואל אלעזר הכהן ואל נשיאי העדה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 3, 'עטרות ודיבון ויעזר ונמרה וחשבון ואלעלה ושבמה ונבו ובעון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 4, 'הארץ אשר הכה יהוה לפני עדת ישראל ארץ מקנה היא ולעבדיך מקנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 5, 'ויאמרו אם מצאנו חן בעיניך יתן את הארץ הזאת לעבדיך לאחזה ואל תעברנו את הירדן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 6, 'ויאמר משה לבני ראובן ולבני גד ולחצי שבט המנשה האחיכם יבאו למלחמה ואתם תשבו פה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 7, 'ולמה תניאון את לב בני ישראל מעבר אל הארץ אשר נתן להם יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 8, 'כה עשו אבתיכם בשלחי אתם מקדש ברנע לראות את הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 9, 'ויעלו עד נחל אשכול ויראו את הארץ ויניאו את לב בני ישראל לבלתי בא אל הארץ אשר נתן להם יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 10, 'ויחר אף יהוה ביום ההוא וישבע לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 11, 'אם יראו האנשים העלים ממצרים מבן עשרים שנה ומעלה את האדמה אשר נשבעתי לאברהם ליצחק וליעקב כי לא מלאו אחרי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 12, 'בלתי כלב בן יפנה הקנזי ויהושע בן נון כי מלאו אחרי יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 13, 'ויחר אף יהוה בישראל וינעם במדבר ארבעים שנה עד תם כל הדור העשה הרע בעיני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 14, 'והנה קמתם תחת אבתיכם תרבות אנשים חטאים לספות עוד על חרון אף יהוה על ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 15, 'כי תשובון מאחריו ויסף עוד להניחו במדבר ושחתם לכל העם הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 16, 'ויגשו אליו ויאמרו גדרות צאן נבנה למקנינו פה וערים לטפנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 17, 'ואנחנו נחלץ חשים לפני בני ישראל עד אשר אם הביאנם אל מקומם וישב טפנו בערי המבצר מפני ישבי הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 18, 'לא נשוב אל בתינו עד התנחלו בני ישראל איש את נחלתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 19, 'כי לא ננחל אתם מעבר לירדן והלאה כי באה נחלתנו אלינו מעבר לירדן מזרחה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 20, 'ויאמר אליהם משה אם תעשון את הדבר הזה אם תחלצו לפני יהוה למלחמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 21, 'ועבר לכם כל חלוץ את הירדן לפני יהוה עד הורישו את איביו מפניו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 22, 'ונכבשה הארץ לפני יהוה ואחר תשובון והייתם נקוים מיהוה ומישראל והיתה הארץ הזאת לכם לאחזה לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 23, 'ואם לא תעשון כן הנה חטאתם ליהוה ודעו חטאתיכם אשר תמצא אתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 24, 'בנו לכם ערים לטפכם וגדרות לצאנכם והיוצא מפיכם תעשון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 25, 'ויאמרו בני . . ראובן ובני גד וחצי שבט המנשה אל משה לאמר עבדיך יעשו כאשר אדני מצוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 26, 'טפנו ונשינו ומקנינו וכל בהמתנו יהיו שם בערי הגלעד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 27, 'ועבדיך יעברו כל חלוץ צבא לפני יהוה למלחמה כאשר אדני דבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 28, 'ויצו להם משה את אלעזר . . הכהן ואת יהושע בן נון ואת ראשי אבות המטות לבני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 29, 'ויאמר משה אליהם אם יעברו בני ראובן ובני גד וחצי שבט המנשה אתכם את הירדן כל חלוץ למלחמה לפני יהוה ונכבשה הארץ לפניכם ונתתם להם את ארץ הגלעד לאחזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 30, 'ואם לא יעברו חלוצים אתכם . . . . . . . . . ונאחזו בתוככם . . בארץ כנען . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 31, 'ויענו בני ראובן ובני גד וחצי שבט המנשה לאמר את אשר דבר יהוה אל עבדיך כן נעשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 32, 'אנחנו נעבר חלוצים לפני יהוה ארץ כנען ואתנו אחזת נחלתנו מעבר לירדן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 33, 'ויתן להם משה לבני ראובן ולבני גד ולחצי שבט המנשה בן יוסף את ממלכת סיחון מלך האמרי ואת ממלכת עוג מלך הבשן ארץ לעריה בגבולת ערי הארץ סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 34, 'ויבנו בני גד את דיבון ואת עטרות ואת ערער ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 35, 'ואת עטרות שפים ואת יעזר ויגבהה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 36, 'ואת בית נמרה ואת בית הרן ערי מבצר וגדרות צאן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 37, 'ובני ראובן בנו את חשבון ואת אלעלה ואת קריתים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 38, 'ואת נבו ואת בעל מעון מוסבת שם ואת שבמה ויקראו בשמות את שמות הערים אשר בנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 39, 'וילכו בני מכיר בן מנשה . . גלעדה וילכדוה ויורישו את האמרי אשר בה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 40, 'ויתן משה את הגלעד למכיר בן מנשה וישב בה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 41, 'ויאיר בן מנשה הלך וילכד את חותים ויקרא אתהן חות יאיר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 32, 42, 'ונבח הלך וילכד את קנת ואת בנתיה ויקרא לה נבח בשמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 33, 1, 'אלה מסעי בני ישראל אשר יצאו מארץ מצרים לצבאתם ביד משה ואהרן ');
