INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 33, 3, 'ויסעו מרעמסס בחדש הראישון בחמשה עשר יום לחדש הראישון ממחרת הפסח יצאו בני ישראל ביד רמה לעיני כל מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 33, 4, 'ומצרים מקברים את אשר הכה יהוה בהם כל בכור ובאלהיהם עשה יהוה שפטים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 33, 5, 'ויסעו בני ישראל מרעמסס ויחנו בסכות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 33, 6, 'ויסעו מסכות ויחנו באתם אשר בקצה המדבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 33, 7, 'ויסעו מאתם וישבו על פי החירת אשר על פני בעל צפון ויחנו לפני מגדל ');
