INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 33, 9, 'ויסעו ממרה ויבאו אילים ובאילים שתים עשרה עינות מים ושבעים תמרים ויחנו שם ');
