INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 33, 52, 'והורשתם את כל ישבי הארץ מפניכם ואבדתם את כל משכיתם ואת כל צלמי מסכותם תאבדו ואת כל במתם תשמידו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 33, 53, 'והורשתם את הארץ וישבתם בה כי לכם נתתי את הארץ לרשת אתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 33, 54, 'והתנחלתם את הארץ בגורל למשפחתיכם לרב תרבה את נחלתו ולמעט תמעיט את נחלתו אל אשר יצא לו שם הגורל לו יהיה למטות אבתיכם תתנחלו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 33, 55, 'ואם לא תורישו את ישבי הארץ מפניכם והיה אשר תותירו מהם לשכים בעיניכם ולצנים בצדיכם וצררו אתכם על הארץ אשר אתם ישבים בה ');
