INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 34, 3, 'והיה לכם פאת נגבה ממדבר צן על ידי אדום והיה לכם גבול נגבה מקצה ים המלח קדמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 34, 4, 'ונסב לכם הגבול מנגב למעלה עקרבים ועבר צנה והיה תוציאתו מנגב לקדש ברנע ויצא חצר אדר ועבר עצמונה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 34, 5, 'ונסב הגבול מעצמונה נחל מצרים והיה תוצאתו הים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 34, 6, 'וגבול ימה יהיה לכם הים הגדול יגבל זה יהיה לכם גבול ימה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 34, 7, 'וזה יהיה לכם גבול צפונה מן הים הגדול תתאו לכם הר ההר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 34, 8, 'ומהר ההר תתאו לבוא חמתה והיה תוצאתו הגבול צדדה ');
