INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 34, 10, 'והתאויתם לכם לגבול קדמה מחצר עינן שפמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 34, 11, 'וירד הגבול משפמה הרבלה מקדם לעין וירד הגבול ומחה אל כתף ים כנרת קדמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 34, 12, 'וירד הגבול הירדנה והיה תוצאתו ים המלח זאת תהיה לכם הארץ לגבלתיה סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 34, 13, 'ויצו משה את בני ישראל לאמר זאת הארץ אשר תתנחלו אתה בגורל אשר צוה יהוה לתת לתשעת המטות וחצי המטה ');
