INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 34, 28, 'ולמטה בני נפתלי נשיא פדהאל בן עמיהוד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 34, 29, 'אלה אשר צוה יהוה לנחל את בני ישראל בארץ כנען ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 1, 'וידבר יהוה אל משה בערבת מואב על ירדן יריחו לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 2, 'צוי את בני ישראל ונתנו ללוים מנחלת אחזתם ערים לשבת ומגרש לערים סביבתיהם תתנו ללוים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 3, 'והיו הערים . להם לשבת ומגרשיהן יהיו לבהמתם ולרכושם ולכל חיתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 4, 'ומגרשי הערים אשר תתנו ללוים מקיר העיר החוצה אלף אמה סביב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 5, 'ומדתם מחוץ לעיר את פאת קדמה אלפים באמה ואת פאת נגבה אלפים באמה ואת פאת ימה אלפים באמה ואת פאת צפונה אלפים באמה והעיר בתוך זה יהיה לכם מגרשי הערים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 6, 'ואת הערים אשר תתנו ללוים את שש ערי המקלט אשר תתנו לנוס שמה הרצח ועליהן תתנו ארבעים ושתים עיר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 7, 'כל הערים אשר תתנו ללוים ארבעים ושמנה עיר אתהן ואת מגרשיהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 8, 'והערים אשר תתנו מאחזת בני ישראל מאת הרב תרבו ומאת המעט תמעיטו איש כפי נחלתו אשר ינחל יתן מעריו ללוים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 9, 'וידבר יהוה אל משה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 10, 'דבר אל בני ישראל ואמרת אליהם כי אתם עברים את הירדן אל ארץ כנען ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 11, 'והקריתם לכם ערים ערי מקלט תהיינה לכם ונס שמה הרצח מכה נפש בשגגה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 12, 'והיו לכם הערים למקלט מגאל ולא ימות הרצח עד עמדו לפני העדה למשפט ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 13, 'והערים אשר תתנו שש ערי מקלט תהיינה לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 14, 'את שלש הערים תתנו מעבר לירדן ואת שלש הערים תתנו בארץ כנען ערי מקלט תהיינה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 15, 'לבני ישראל ולגר ולתושב בתוכם תהיינה שש הערים האלה למקלט לנוס שמה כל מכה נפש בשגגה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 16, 'ואם בכלי ברזל הכהו וימת רצח הוא מות יומת הרצח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 17, 'ואם באבן יד אשר ימות בה הכהו וימת רצח הוא מות יומת הרצח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 18, 'ואם בכלי עץ יד אשר ימות בו הכהו וימת . . . רצח הוא מות יומת הרצח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 19, 'גאל הדם הוא ימית את הרצח בפגעו בו הוא ימיתנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 20, 'ואם בשנאה יהדפנו או השליך עליו . . בצדיה וימת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 21, 'או באיבה הכהו בידו וימת מות יומת המכה רצח הוא . . . גאל הדם ימית את הרצח בפגעו בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 22, 'ואם בפתע בלא איבה הדיפו או השליך עליו כל כלי בלא צדיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 23, 'או בכל אבן אשר ימות בה בלא ראות ויפל עליו וימת והוא לא אויב לו ולא מבקש רעתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 24, 'ושפטו העדה בין המכה ובין גאל הדם על המשפטים האלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 25, 'והצילו העדה את המכה מיד גאל הדם והשיבו אתו העדה אל עיר מקלטו אשר נס שמה וישב בה עד מות הכהן הגדול אשר משח אתו בשמן הקדש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 26, 'ואם יצא יצא הרצח את גבול עיר מקלטו אשר ינוס שמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 27, 'ומצא אתו גאל הדם . . מחוץ לגבול עיר מקלטו ורצח גאל הדם את הרצח אין לו דם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 28, 'כי בעיר מקלטו ישב עד מות הכהן הגדול ואחרי מות הכהן הגדול ישוב הרצח אל ארץ אחזתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 29, 'והיו אלה לכם לחקת משפט לדרתיכם בכל מושבתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 30, 'כל מכה נפש לפי עדים ירצח את הרצח ועד אחד לא יענה בנפש למות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 31, 'ולא תקחו כפר לנפש רצח אשר הוא רשע למות כי מות יומת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 32, 'ולא תקחו כפר לנוס אל עיר מקלטו לשוב לשבת בארץ עד מות הכהן הגדול ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 33, 'ולא תחניפו את הארץ אשר אתם ישבים בה כי הדם הוא יחניף את הארץ ולארץ לא יכפר לדם אשר שפך בה כי אם בדם שפכו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 35, 34, 'ולא תטמאו את הארץ אשר אתם ישבים בה אשר אני שכן בתוכה כי אני יהוה שכן בתוך בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 36, 1, 'ויקרבו ראשי האבות למשפחת בני גלעד בן מכיר בן מנשה ממשפחת בני יוסף וידברו לפני משה ולפני . . . הנשיאים ראשי האבות לבני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 36, 2, 'ויאמרו את אדני צוה יהוה לתת את הארץ בנחלה בגורל לבני ישראל ואדני צוה ביהוה לתת את נחלת צלפחד אחינו לבנתיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 36, 3, 'והיו לאחד מבני שבטי בני ישראל לנשים ונגרעה נחלתן מנחלת אבתינו ונוספה על נחלת המטה אשר תהיינה להם ומגורל נחלתנו יגרע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 36, 4, 'ואם יהיה היובל לבני ישראל ונוספה נחלתן על נחלת המטה אשר תהיינה להם ומנחלת מטה אבתינו יגרע נחלתן . . . . . . . . . . . . . . . . . . . . . . . . . . . . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 36, 5, 'ויצו משה את בני ישראל על פי יהוה לאמר כן מטה בני יוסף דברים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 36, 6, 'זה הדבר אשר צוה יהוה לבנות צלפחד לאמר לטוב בעיניהם . תהיינה לנשים אך למשפחת מטה אביהן תהיינה לנשים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 36, 7, 'ולא תסב נחלה לבני ישראל ממטה אל מטה כי איש בנחלת מטה אבתיו ידבקו בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 36, 8, 'וכל בת ירשת נחלה ממטות בני ישראל לאחד ממשפחת מטה אביה תהיה לאשה למען יירשו בני ישראל איש את נחלת אבתיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 36, 9, 'ולא תסב נחלה ממטה אל מטה אחר כי איש בנחלתו ידבקו מטות בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 36, 10, 'כאשר צוה יהוה את משה כן עשו בנות צלפחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 36, 11, 'ותהיינה מחלה ונעה חגלה מלכה ותרצה בנות צלפחד לבני דדיהן לנשים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 36, 12, 'ממשפחת בני מנשה בן יוסף היו לנשים ותהי נחלתן על מטה משפחת אביהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (4, 36, 13, 'אלה המצות והמשפטים אשר צוה יהוה ביד משה אל בני ישראל בערבת מואב על ירדן יריחו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 1, 'אלה הדברים אשר דבר משה אל כל ישראל בעבר הירדן במדבר בערבה מול סוף בין פראן ובין תפל ולבן וחצרות ודי זהב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 2, 'אחד עשר יום מחורב דרך הר שעיר עד קדש ברנע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 3, 'ויהי בארבעים שנה בעשתי עשר חדש באחד לחדש דבר משה אל בני ישראל ככל אשר צוה יהוה אתו אליהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 4, 'אחרי הכתו את סיחון מלך האמרי אשר ישב בחשבון ואת עוג מלך הבשן אשר ישב בעשתרות באדרעי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 5, 'בעבר הירדן בארץ מואב הואל משה באר את התורה הזאת לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 6, 'יהוה אלהינו דבר אלינו בחורב לאמר רב לכם שבת בהר הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 7, 'פנו וסעו לכם ובאו הר האמרי ואל כל שכניו בערבה בהר ובשפלה בנגב ובחוף הים ארץ הכנעני והלבנון עד הנהר הגדול נהר פרת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 8, 'ראו נתתי לפניכם את הארץ באו ורשו את הארץ אשר נשבעתי . לאבתיכם לאברהם ליצחק וליעקב לתת . לזרעם אחריהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 9, 'ואמר אליכם בעת ההיא לאמר לא אוכל לבדי שאת אתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 10, 'יהוה אלהיכם הרבה אתכם והנכם היום ככוכבי השמים לרב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 11, 'יהוה אלהי אבתיכם יסף עליכם ככם אלף פעמים ויברך אתכם כאשר דבר לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 12, 'איכה אשא לבדי טרחכם משאכם וריבכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 13, 'הבו לכם אנשים חכמים ונבונים וידעים לשבטיכם ואשימם בראשיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 14, 'ותענו אתי ותאמרו טוב הדבר אשר דברת לעשות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 15, 'ואקח את ראשי שבטיכם אנשים חכמים וידעים ואתן אתם ראשים עליכם שרי אלפים ושרי מאות שרי חמשים ושרי עשרות ושטרים לשבטיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 16, 'ואצוה את שפטיכם בעת ההיא לאמר שמעו בין אחיכם ושפטתם צדק בין איש ובין אחיו ובין גרו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 17, 'לא תכירו פנים במשפט כקטן כגדול תשמעון לא תגורו מפני איש כי המשפט לאלהים הוא והדבר אשר יקשה מכם תקריבון אלי ושמעתיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 18, 'ואצוה אתכם בעת ההיא את כל הדברים אשר תעשון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 19, 'ונסעה מחורב ונלכה את כל המדבר הגדול והנורא ההוא אשר ראיתם דרך הר האמרי כאשר צוה יהוה אלהינו אתנו ונבא עד קדש ברנע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 20, 'ואמר אליכם באתם עד הר האמרי אשר יהוה אלהינו נתן לנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 21, 'ראה נתן יהוה אלהיך לפניך את הארץ עלה רש כאשר דבר יהוה אלהי אבתיך לך אל תירא ואל תחת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 22, 'ותקרבון אלי כלכם ותאמרו נשלחה אנשים לפנינו ויחפרו לנו את הארץ וישבו אתנו דבר את הדרך אשר נעלה בה ואת הערים אשר נבוא עליהן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 23, 'וייטב בעיני הדבר ואקח מכם שנים עשר אנשים איש אחד לשבט ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 24, 'ויפנו ויעלו ההרה ויבאו עד נחל אשכול וירגלו אתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 25, 'ויקחו בידם מפרי הארץ ויורדו אלינו וישבו אתנו דבר ויאמרו טובה הארץ אשר יהוה אלהינו נתן לנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 26, 'ולא אביתם לעלות ותמרו את פי יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 27, 'ותרגנו באהליכם ותאמרו בשנאת יהוה אתנו הוציאנו מארץ מצרים לתת אתנו ביד האמרי להשמידנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 28, 'אנה אנחנו עלים ואחינו המיסו את לבבנו לאמר עם גדול ורב ממנו וערים גדלות ובצרות בשמים וגם בני ענקים ראינו שם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 29, 'ואמר אליכם לא תערצון ולא תיראון מהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 30, 'יהוה אלהיכם ההלך לפניכם הוא ילחם לכם ככל אשר עשה אתכם במצרים לעיניכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 31, 'ובמדבר אשר ראית אשר נשאך יהוה אלהיך כאשר ישא איש את בנו בכל הדרך אשר הלכתם עד באכם עד המקום הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 32, 'ובדבר הזה אינכם מאמינם ביהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 33, 'ההלך לפניכם בדרך לתור לכם מקום לחנותכם באש לילה להראתכם בדרך אשר תלכו בה ובענן יומם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 34, 'וישמע יהוה את קול דבריכם ויקצף וישבע לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 35, 'אם יראה איש באנשים האלה הדור הרע הזה את הארץ הטובה אשר נשבעתי . לאבתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 36, 'זולתי כלב בן יפנה הוא יראנה ולו אתן את הארץ אשר דרך בה ולבניו יען כי מלא אחרי יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 37, 'גם בי התאנף יהוה בגללכם לאמר גם אתה לא תבוא שמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 38, 'יהושע בן נון העמד לפניך הוא יבוא שמה אתו החזק כי הוא ינחלנה את ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 39, 'וטפכם אשר אמרתם לבז יהיה ובניכם . . . . . . הם יבאו שמה ולהם אתננה והם יירשוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 40, 'ואתם פנו וסעו לכם המדברה דרך ים סוף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 41, 'ותענו ותאמרו אלי חטאנו ליהוה אלהינו אנחנו נעלה ונלחמנו ככל אשר צונו יהוה אלהינו ותחגרו איש את כלי מלחמתו ותהינו לעלות ההרה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 42, 'ויאמר יהוה אלי אמר להם לא תעלו ולא תלחמו כי אינני בקרבכם ולא תנגפו לפני איביכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 43, 'ואדברה אליכם ולא שמעתם ותמרו את פי יהוה אלהיכם ותזידו ותעלו ההרה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 44, 'ויצא העמלקי והכנעני הישב בהר ההוא לקראתכם וירדפו אתכם כאשר תעשינה הדברים ויכתו אתכם בשעיר עד חרמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 45, 'ותשבו ותבכו לפני יהוה ולא שמע יהוה בקולכם ולא האזין אליכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 1, 46, 'ותשבו בקדש ימים רבים כימים אשר ישבתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 1, 'ונפנה ונסעה המדברה דרך ים סוף כאשר דבר יהוה אלי ונסב את הר שעיר ימים רבים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 2, 'ויאמר יהוה אלי לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 3, 'רב לכם סוב את ההר הזה פנו לכם צפונה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 4, 'ואת העם צוי לאמר אתם עברים בגבול אחיכם בני עשו הישבים בשעיר וייראו מכם ונשמרתם מאד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 5, 'אל תתגרו בם כי לא אתן לכם מארצם ירשה עד מדרך כף רגל כי ירשה לעשו נתתי את הר שעיר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 6, 'אכל תשברו מאתם בכסף ואכלתם וגם מים תכירו מאתם בכסף ושתיתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 7, 'כי יהוה אלהיך ברכך בכל מעשה ידיך ידע לכתך את המדבר הגדול הזה זה ארבעים שנה יהוה אלהיך עמך לא חסרת דבר ואשלחה מלאכים אל מלך אדום לאמר אעברה בארצך לא אטה בשדה ובכרם ולא נשתה מי בור דרך המלך נלך לא נסור ימין ושמאל עד אשר נעבר נבולך ויאמר לא תעבר בי פן בחרב אצא לקראתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 8, 'ונעברה מאת אחינו בני עשו הישבים בשעיר מדרך הערבה מאילת ומעציון גבר ונפנה ונעברה דרך מדבר מואב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 9, 'ויאמר יהוה אלי אל תצור את מואב ואל תתגר בו . כי לא אתן לך מארצו ירשה כי לבני לוט נתתי את ער ירשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 10, 'האמים לפנים ישבו בה עם גדול ורב ורם כענקים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 11, '. . . . . והמואבים יקראו להם אימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 12, 'ובשעיר ישבו החרי לפנים ובני עשו יירשם וישמדם יהוה מפניהם ויירשם וישבו תחתיהם כאשר עשה ישראל לארץ ירשתו אשר נתן יהוה להם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 13, 'ועתה קומו סעו ועברו לכם את נחל זרד ונעברה את נחל זרד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 14, 'והימים אשר הלכנו מקדש ברנע עד אשר עברנו את נחל זרד שלשים ושמנה שנה עד תם כל הדור אנשי המלחמה מקרב המחנה כאשר נשבע יהוה להם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 15, 'וגם יד יהוה היתה בם להמם מקרב המחנה עד תמם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 16, 'ויהי כאשר תמו כל אנשי המלחמה למות מקרב העם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 17, 'ויאמר יהוה אלי לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 18, 'אתה עבר היום את גבול מואב את ער ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 19, 'וקרבת מול בני עמון אל תצרם ואל תתגר בם כי לא אתן מארץ בני עמון לך ירשה כי לבני לוט נתתיה ירשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 20, 'ארץ רפאים תחשב אף היא רפאים ישבו בה לפנים והעמונים יקראו להם זמזמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 21, 'עם גדול ורב ורם כענקים וישמדם יהוה מפניהם ויירשום וישבו תחתיהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 22, 'כאשר עשה לבני עשו הישבים בשעיר אשר השמיד את החרי מפניהם ויירשם וישבו תחתם עד היום הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 23, 'והעוים הישבים בחצרים עד עזה כפתרים היצאים מכפתר השמידם וישבו תחתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 24, 'קומו סעו ועברו את נחל ארנן ראה נתתי בידך את סיחון מלך חשבון האמרי ואת ארצו החל רש והתגר בו מלחמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 25, 'היום הזה החל תת פחדך ויראתך על פני העמים תחת כל השמים אשר ישמעו את שמעך ורגזו וחלו מפניך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 26, 'ואשלחה מלאכים ממדבר קדמות אל סיחון מלך חשבון דברי שלום לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 27, 'אעברה בארצך בדרך בדרך אלך לא אסור ימין ושמאל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 28, 'אכל בכסף תשברני ואכלתי ומים בכסף תתן לי ושתיתי רק אעברה ברגלי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 29, 'כאשר עשו לי בני עשו הישבים בשעיר והמואבים הישבים בער עד אשר אעברה את הירדן אל הארץ אשר יהוה אלהינו נתן לנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 30, 'ולא אבה סיחון מלך חשבון העברנו בו כי הקשה יהוה אלהיך את רוחו ואמץ את לבבו למען תתו בידך כיום הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 31, 'ויאמר יהוה אלי ראה החלתי תת לפניך את סיחון מלך חשבון האמרי ואת ארצו החל רש לרשת את ארצו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 32, 'ויצא סיחון לקראתנו הוא וכל עמו למלחמה יהצה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 33, 'ויתנהו יהוה אלהינו לפנינו ונכה אתו ואת בניו ואת כל עמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 34, 'ונלכדה את כל עריו בעת ההיא ונחרמה את כל עריו מתם הנשים והטף לא השארנו שריד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 35, 'רק הבהמה בזזנו לנו ושלל הערים אשר לכדנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 36, 'מערער אשר על שפת נחל ארנן והעיר אשר בנחל ועד הגלעד לא היתה קריה אשר שגבה ממנו את הכל נתן יהוה אלהינו בידנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 2, 37, 'רק אל ארץ בני עמון לא קרבת כל יד נחל היבק וערי ההר וכל אשר צונו יהוה אלהינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 1, 'ונפנה ונעלה דרך הבשן ויצא עוג מלך הבשן לקראתנו הוא וכל עמו למלחמה אדרעי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 2, 'ויאמר יהוה אלי אל תירא אתו כי בידך נתתי אתו ואת כל עמו ואת ארצו ועשית לו כאשר עשית לסיחון מלך האמרי אשר ישב בחשבון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 3, 'ויתן יהוה אלהינו בידנו גם את עוג מלך הבשן ואת כל עמו ונכנו עד בלתי השאיר לו שריד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 4, 'ונלכדה את כל עריו בעת ההיא לא היתה קריה אשר לא לקחנו מאתם ששים עיר כל חבל הארגב ממלכת עוג בבשן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 5, 'כל אלה ערים בצרות חומה גבהה דלתים ובריח לבד מערי הפרזי הרבה מאד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 6, 'ונחרימה אתם כאשר עשינו לסיחון מלך חשבון החרים כל עיר מתם הנשים והטף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 7, 'וכל הבהמה ושלל הערים בזונו לנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 8, 'ונקח בעת ההיא את הארץ מיד שני מלכי האמרי אשר בעבר הירדן מנחל ארנן ועד הר חרמון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 9, 'צידנים יקראו לחרמון שרין והאמרי יקראו לו שניר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 10, 'כל ערי המישור וכל הגלעד וכל הבשן עד סלכה ואדרעי ערי ממלכת עוג בבשן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 11, 'כי רק עוג מלך הבשן נשאר מיתר הרפאים הנה ערשו ערש ברזל הלוא היא ברבת בני עמון תשע אמות ארכה וארבע אמות רחבה באמת איש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 12, 'ואת הארץ הזאת ירשנו בעת ההיא מערער אשר על שפת נחל ארנן וחצי הר הגלעד ועריו נתתי לראובני ולגדי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 13, 'ויתר הגלעד וכל הבשן ממלכת עוג נתתי לחצי שבט המנשה כל חבל הארגב לכל הבשן ההוא יקרא ארץ רפאים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 14, 'ויאיר בן מנשה לקח את כל חבל הארגב עד גבול הגשורי והמכעתי ויקרא אתם על שמו את הבשן חות יאיר עד היום הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 15, 'ולמכיר נתתי את הגלעד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 16, 'ולראובני ולגדי נתתי מן הגלעד ועד נחל ארנן תוך הנחל וגבול עד יבק הנחל גבול בני עמון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 17, 'והערבה והירדן וגבול מכנרת ועד ים הערבה ים המלח תחת אשדת הפסגה מזרחה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 18, 'ואצוה אתכם בעת ההיא לאמר יהיה אלהיכם נתן לכם את הארץ הזאת לרשתה חלוצים תעברו לפני אחיכם בני ישראל כל בני חיל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 19, 'רק טפכם ונשיכם ומקניכם ידעתי כי מקנה רב לכם ישבו בעריכם אשר נתתי לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 20, 'עד אשר יניח יהוה . . לאחיכם ככם ויירשו גם הם את הארץ אשר יהוה אלהיכם נתן להם בעבר הירדן ושבתם איש לירשתו אשר נתתי לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 21, 'ואת יהושע צויתי בעת ההיא לאמר עיניך הראות את כל אשר עשה יהוה . לשני המלכים האלה כן יעשה יהוה לכל הממלכות אשר אתה עבר שמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 22, 'לא תיראם כי יהוה אלהיכם הוא הנלחם לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 23, 'ואתחנן אל יהוה בעת ההיא לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 24, 'אדני יהוה אתה החלת להראות את עבדך את גדלך ואת ידך החזקה אשר מי אל בשמים ובארץ אשר יעשה כמעשיך וכגבורתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 25, 'אעברה נא ואראה את הארץ הטובה אשר בעבר הירדן ההר הטוב הזה והלבנון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 26, 'ויתעבר יהוה בי למענכם ולא שמע אלי ויאמר יהוה אלי רב לך אל תוסף דבר אלי עוד בדבר הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 27, 'עלה אל ראש הפסגה ושא עיניך ימה וצפונה ותימנה ומזרחה וראה בעיניך כי לא תעבר את הירדן הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 28, 'וצוי את יהושע וחזקהו ואמצהו כי הוא יעבר לפני העם הזה והוא ינחל אתם את הארץ אשר תראה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 3, 29, 'ונשב בגיא מול בית פעור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 1, 'ועתא ישראל שמע אל החקים ואל המשפטים אשר אנכי מלמד אתכם לעשות למען תחיון ובאתם וירשתם את הארץ אשר יהוה אלהי אבתיכם נתן לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 2, 'לא תוספו על הדבר אשר אנכי מצוה אתכם היום ולא תגרעו ממנו לשמר את מצות יהוה אלהיכם אשר אוכי מצוה אתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 3, 'עיניכם הראות את אשר עשה יהוה בבעל פעור כי כל האיש אשר הלך אחרי בעל פעור השמידו יהוה אלהיך מקרבך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 4, 'ואתם הדבקים ביהוה אלהיכם חיים כלכם היום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 5, 'ראו למדתי אתכם חקים ומשפטים כאשר צוני יהוה אלהי לעשות כן בקרב הארץ אשר אתם באים שמה לרשתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 6, 'ושמרתם ועשיתם כי היא חכמתכם ובינתכם לעיני העמים אשר ישמעו את כל החקים האלה ואמרו רק עם חכם ונבון הגוי הגדול הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 7, 'כי מי גוי גדול אשר לו אלהים קרובים אליו כיהוה אלהינו בכל קראנו אליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 8, 'ומי גוי גדול אשר לו חקים ומשפטים צדקים ככל התורה הזאת אשר אנכי נתן לפניכם היום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 9, 'רק השמר לך ושמר נפשך מאד פן תשכח את הדברים אשר ראו עיניך ופן יסורו מלבבך כל ימי חייך והודעתם לבניך ולבני בניך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 10, 'יום אשר עמדת לפני יהוה אלהיך בחורב באמר יהוה אלי הקהל לי את העם ואשמעם את דברי אשר ילמדון ליראה אתי כל הימים אשר הם חיים על האדמה ואת בניהם ילמדון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 11, 'ותקרבון ותעמדון תחת ההר וההר בער באש עד לבב השמים חשך ענן וערפל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 12, 'וידבר יהוה אליכם מתוך האש קול דברים אתם שמעים ותמונה אינכם ראים זולתי קול ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 13, 'ויגד לכם את בריתו אשר צוה אתכם לעשות עשרת הדברים ויכתבם על שני לחות אבנים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 14, 'ואתי צוה יהוה בעת ההיא ללמד אתכם חקים ומשפטים לעשותכם אתם בארץ אשר אתם עברים . שמה לרשתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 15, 'ונשמרתם מאד לנפשתיכם כי לא ראיתם כל תמונה ביום דבר יהוה אלהיכם בחורב מתוך האש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 16, 'פן תשחתון ועשיתם לכם פסל תמונת כל סמל תבנית זכר או נקבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 17, 'תבנית כל בהמה אשר בארץ תבנית כל צפור כנף אשר תעף בשמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 18, 'תבנית כל רמש באדמה תבנית כל דגה אשר במים מתחת לארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 19, 'ופן תשא עיניך השמים וראית את השמש ואת הירח ואת הכוכבים כל צבא השמים ונדחת והשתחוית להם ועבדתם אשר חלק יהוה אלהיך אתם לכל העמים תחת כל השמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 20, 'ואתכם לקח יהוה ויוצא אתכם מכור הברזל ממצרים להיות לו לעם נחלה כיום הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 21, 'ויהוה התאנף בי על דבריכם . לבלתי עברי את הירדן ולבלתי בא אל הארץ הטובה אשר יהוה אלהיך נתן לך נחלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 22, 'כי אנכי מת בארץ הזאת אינני עבר את הירדן ואתם עברים וירשתם את הארץ הטובה הזאת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 23, 'השמרו לכם פן תשכחו את ברית יהוה אלהיכם אשר כרת עמכם ועשיתם לכם פסל תמונת כל אשר צוך יהוה אלהיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 24, 'כי יהוה אלהיך אש אכלה הוא אל קנא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 25, 'כי תולידו בנים ובני בנים ונושנתם בארץ והשחתם ועשיתם פסל תמונת כל ועשיתם הרע בעיני יהוה אלהיכם להכעיסו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 26, 'העדתי בכם היום את השמים ואת הארץ כי אבד תאבדון מהר מעל הארץ אשר אתם עברים את הירדן שמה לרשתה לא תאריכון ימים עליה כי השמד תשמדון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 27, 'והפיץ יהוה אתכם בעמים ונשארתם מתי מספר בגוים אשר ינהג יהוה אתכם שמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 28, 'ועבדתם שם אלהים מעשה ידי אדם עץ ואבן אשר לא יראון ולא ישמעון ולא יאכלון ולא יריחון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 29, 'ובקשת משם את יהוה אלהיך ומצאתו כי תדרשנו בכל לבבך ובכל נפשך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 30, 'בצר לך ומצאוך כל הדברים האלה באחרית הימים ושבת עד יהוה אלהיך ושמעת בקולו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 31, 'כי אל רחום יהוה אלהיך לא ירפך ולא ישחיתך ולא ישכח את ברית אבתיך אשר נשבע להם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 32, 'כי שאל נא לימים הראישונים אשר היו לפניך למן היום אשר ברא אלהים אדם על הארץ ולמקצה השמים ועד קצה השמים הן היה כדבר הגדול הזה או הנשמע כמהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 33, 'השמע עם קול אלהים חיים מדבר מתוך האש כאשר שמעת אתה ויחי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 34, 'או הנסה אלהים לבוא לקחת לו גוי מקרב גוי במסות באתות ובמפתים ובמלחמה וביד חזקה ובזרוע נטויה ובמראים גדלים ככל אשר עשה לכם יהוה אלהיכם במצרים לעיניכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 35, 'אתה הראת לדעת כי יהוה הוא האלהים אין עוד מלבדו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 36, 'מן השמים השמעך את קולו ליסרך ועל הארץ הראך את אשו הגדלה ודבריו שמעת מתוך האש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 37, 'ותחת כי אהב את אבתיך ויבחר בזרעם אחריהם ויוצאך בפניו ובכחו הגדול ממצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 38, 'להוריש גוים גדלים ועצמים ממך מפניך להביאך לתת לך את ארצם נחלה כיום הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 39, 'וידעת היום והשבת אל לבבך כי יהוה הוא האלהים בשמים ממעל ועל הארץ מתחת אין עוד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 40, 'ושמרת את חקיו ואת מצותיו אשר אנכי מצוך היום אשר ייטב לך ולבניך אחריך ולמען תאריך ימים על האדמה אשר יהוה אלהיך נתן לך כל הימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 41, 'אז יבדיל משה שלש ערים בעבר הירדן מזרחה השמש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 42, 'לנס שמה הרצח אשר ירצח את רעהו בבלי דעת והוא לא שנא לו מתמול שלשום ונס אל אחת מן הערים האלה וחי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 43, 'את בצר במדבר בארץ המישור לראובני ואת רמות בגלעד לגדי ואת גולן בבשן למנשי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 44, 'וזאת התורה אשר שם משה לפני בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 45, 'אלה העדות החקים והמשפטים אשר דבר משה אל בני ישראל בצאתם ממצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 46, 'בעבר הירדן בגיא מול בית פעור בארץ סיחון מלך האמרי אשר ישב בחשבון אשר הכה משה ובני ישראל בצאתם ממצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 47, 'ויירשו את ארצו ואת ארץ עוג מלך הבשן שני מלכי האמרי אשר בעבר הירדן מזרח השמש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 48, 'מערער אשר על שפת נחל ארנן ועד הר שיאון הוא חרמון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 4, 49, 'וכל הערבה עבר הירדן מזרחה ועד ים הערבה ים המלח תחת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 1, 'אשדת הפסגה ויקרא משה אל כל ישראל ויאמר אליהם שמע ישראל את החקים ואת המשפטים אשר אנכי דבר באזניכם היום ולמדתם אתם ושמרתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 2, 'לעשותם יהוה אלהינו כרת עמנו ברית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 3, 'בחורב לא את אבתינו כרת יהוה את הברית הזאת כי אתנו אנחנו אלה פה היום כלנו חיים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 4, 'פנים בפנים דבר יהוה עמכם בהר מתוך האש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 5, 'ואנכי עמד בין יהוה ובינכם בעת ההיא להגיד לכם את דברי יהוה . כי יראתם מפני האש ולא עליתם בהר לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 6, 'אנכי יהוה אלהיך אשר הוצאתיך מארץ מצרים מבית עבדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 7, 'לא יהיה לך אלהים אחרים על פני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 8, 'לא תעשה לך פסל וכל תמונה אשר בשמים ממעל ואשר בארץ מתחת ואשר במים מתחת לארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 9, 'לא תשתחוה להם ולא תעבדם . כי אנכי יהוה אלהיך אל קנא פקד עון אבות על בנים על שלישים ועל רבעים לשנאי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 10, 'ועשה חסד לאלפים לאהבי ולשמרי מצותי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 11, 'לא תשא את שם יהוה אלהיך לשוא כי לא ינקה יהוה את אשר ישא את שמו לשוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 12, 'שמור את יום השבת לקדשהו כאשר צוך יהוה אלהיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 13, 'ששת ימים תעבד ועשית כל מלאכתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 14, 'ויום השביעי שבת ליהוה אלהיך לא תעשה בו כל מלאכה אתה ובנך ובתך עבדך ואמתך שורך וחמרך וכל בהמתך וגרך אשר בשעריך למען ינוח עבדך ואמתך כמוך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 15, 'וזכרת כי עבד היית בארץ מצרים ויוצאך יהוה אלהיך משם ביד חזקה ובזרע נטויה על כן צוך יהוה אלהיך לעשות את יום השבת . . . . . . . . . . . . . . . . . . . . . . . . . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 16, 'כבד את אביך ואת אמך כאשר צוך יהוה אלהיך למען יארכון ימיך ולמען ייטב לך על האדמה אשר יהוה אלהיך נתן לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 17, 'לא תרצח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 18, 'לא תנאף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 19, 'לא תגנב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 20, 'לא תענה ברעך עד שוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 21, 'לא תחמד בית רעך ולא תחמד אשת רעך שדהו עבדו ואמתו שורו וחמרו וכל אשר לרעך . והיה כי יביאך יהוה אלהיך אל ארץ הכנעני אשר אתה בא שמה לרשתה והקמת לך אבנים גדלות ושדת אתם בשיד וכתבת על האבנים את כל דברי התורה הזאת והיה בעברכם את הירדן תקימו את האבנים האלה אשר אנכי מצוה אתכם היום בהר גריזים ובנית שם מזבח ליהוה אלהיך מזבח אבנים לא תניף עליהם ברזל אבנים שלמות תבנה את מזבח יהוה אלהיך והעלית עליו עלות ליהוה אלהיך וזבחת שלמים ואכלת שם ושמחת לפני יהוה אלהיך ההר ההוא בעבר הירדן אחרי דרך מבוא השמש בארץ הכנעני הישב בערבה מול הגלגל אצל אלון מורא מול שכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 22, 'את הדברים האלה דבר יהוה אל כל קהלכם בהר מתוך האש חשך ענן וערפל קול גדול ולא יסף ויכתבם על שני לוחת אבנים ויתנם אלי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 23, 'ויהי כשמעכם את הקול מתוך החשך וההר בער באש ותקרבון אלי כל ראשי שבטיכם וזקניכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 24, 'ותאמרו הן הראנו יהוה אלהינו את כבודו ואת גדלו ואת קולו שמענו מתוך האש היום הזה ראינו כי ידבר אלהים את האדם ויחי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 25, 'ועתה למה נמות כי תאכלנו האש הגדלה הזאת אם יספים אנחנו לשמע את קול יהוה אלהינו עוד ומתנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 26, 'כי מי כל בשר אשר שמע קול אלהים חיים מדבר מתוך האש כמונו ויחי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 27, 'קרב אתה ושמע את כל אשר יאמר יהוה אלהינו . ואתה תדבר אלינו את כל אשר ידבר יהוה אלהינו אליך ושמענו ועשינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 28, 'וישמע יהוה את קול דבריכם בדברכם אלי ויאמר יהוה אלי שמעתי את קול דברי העם הזה אשר דברו אליך היטיבו כל אשר דברו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 29, 'מי יתן והיה לבבם זה להם ליראה אתי ולשמר את . מצותי כל הימים למען ייטב להם ולבניהם לעלם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 30, 'לך אמר להם שובו לכם לאהליכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 31, 'ואתה פה עמד עמדי ואדברה אליך את כל המצוה החקים והמשפטים אשר תלמדם ועשו בארץ אשר אנכי נתן להם לרשתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 32, 'ושמרתם לעשות כאשר צוה יהוה אלהיכם אתכם לא תסורו ימין ושמאל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 5, 33, 'בכל הדרך אשר צוה יהוה אלהיכם אתכם תלכון למען תחיון וטוב לכם והארכתם ימים בארץ אשר תירשון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 1, 'וזאת המצוה החקים והמשפטים אשר צוה יהוה אלהיכם ללמד אתכם לעשות בארץ אשר אתם עברים שמה לרשתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 2, 'למען תירא את יהוה אלהיך לשמר את כל חקיו ומצותיו אשר אנכי מצוך היום אתה ובנך ובן בנך כל ימי חייך ולמען יארכון ימיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 3, 'ושמעת ישראל ושמרת לעשות אשר ייטב לך ואשר תרבה מאד כאשר דבר יהוה אלהי אבתיך לך ארץ זבת חלב ודבש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 4, 'שמע ישראל יהוה אלהינו יהוה אחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 5, 'ואהבת את יהוה אלהיך בכל לבבך ובכל נפשך ובכל מאדך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 6, 'והיו הדברים האלה אשר אנכי מצוך היום על לבבך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 7, 'ושננתם לבניך ודברת בם בשבתך בבית בלכתך בדרך בשכבך ובקומך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 8, 'וקשרתם לאות על ידיך והיו לטטפות בין עיניך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 9, 'וכתבתם על מזזות בתיך ובשעריך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 10, 'והיה כי יביאך יהוה אלהיך אל הארץ אשר נשבע לאבתיך לאברהם ליצחק וליעקב לתת לך ערים גדלות וטובות אשר לא בנית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 11, 'בתים מלאים כל טוב אשר לא מלאת ברות חצבים אשר לא חצבת כרמים וזיתים אשר לא נטעת ואכלת ושבעת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 12, 'השמר לך פן תשכח את יהוה אלהיך אשר הוציאך מארץ מצרים מבית עבדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 13, 'את יהוה אלהיך תירא ואתו תעבד ובשמו תשבע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 14, 'לא תלכו אחרי אלהים אחרים מאלהי העמים אשר סביבתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 15, 'כי אל קנא יהוה אלהיך בקרבך פן יחר אף יהוה אלהיך בך והשמידך מעל פני האדמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 16, 'לא תנסו את יהוה אלהיכם כאשר נסיתם במסה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 17, 'שמר תשמרון את מצות יהוה אלהיכם ועדתיו וחקיו אשר צוך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 18, 'ועשית הישר והטוב בעיני יהוה אלהיך למען ייטב לך ובאת וירשת את הארץ הטובה אשר נשבע יהוה לאבתיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 19, 'להדיף את כל איביך מפניך כאשר דבר יהוה והיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 20, 'כי ישאלך בנך מחר לאמר מה העדת החקים והמשפטים אשר צוה יהוה אלהינו אתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 21, 'ואמרת לבנך עבדים היינו לפרעה במצרים ויוציאנו יהוה ממצרים ביד חזקה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 22, 'ויתן יהוה אתות ומופתים גדלים ורעים במצרים בפרעה ובכל ביתו לעינינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 23, 'ואתנו הוציא משם למען הביא אתנו לתת לנו את הארץ אשר נשבע לאבתינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 24, 'ויצונו יהוה לעשות את כל החקים האלה ליראה את יהוה אלהינו לטוב לנו כל הימים ולהחיתנו כיום הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 6, 25, 'וצדקה תהיה לנו כי נשמר לעשות את כל המצוה הזאת לפני יהוה אלהינו כאשר צונו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 1, 'כי יביאך יהוה אלהיך אל הארץ אשר אתה בא שמה לרשתה ונשל גוים רבים מפניך החתי והגרגשי והאמרי והכנעני והפרזי והחוי והיבוסי שבעה גוים רבים ועצמים ממך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 2, 'ונתנם יהוה אלהיך לפניך והכיתם החרים תחרים אתם לא תכרת להם ברית ולא תחנם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 3, 'ולא תתחתן בם בתך לא תתן לבנו ובתו לא תקח לבנך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 4, 'כי יסיר את בנך מאחרי ועבד אלהים אחרים וחרה אף יהוה . בכם והשמידך מהר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 5, 'כי אם כה תעשה להם מזבחתיהם תתצון ומצבתיהם תשברון ואשריהם תגדעון ופסליהם תשרפון באש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 6, 'כי עם קדש אתה ליהוה אלהיך ובך בחר יהוה אלהיך להיות לו לעם סגלה מכל העמים אשר על פני האדמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 7, 'לא מרבכם מכל העמים חשק יהוה בכם ויבחר בכם כי אתם המעט מכל העמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 8, 'כי מאהבת יהוה אתכם ומשמרו את השבעה אשר נשבע לאבתיכם הוציא יהוה אתכם ביד חזקה ויפדך מבית עבדים מיד פרעה מלך מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 9, 'וידעת כי יהוה אלהיך הוא האלהים האל הנאמן שמר הברית והחסד לאהביו ולשמרי מצותיו לאלף דר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 10, 'ומשלם לשנאיו על פניו להאבידו לא יאחר לשנאיו על פניו ישלם לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 11, 'ושמרת את המצוה את החקים ואת המשפטים אשר אנכי מצוך היום לעשותם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 12, 'והיה עקב תשמעון את המשפטים האלה ושמרתם ועשיתם אתם ושמר יהוה אלהיך לך את הברית ואת החסד אשר נשבע לאבתיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 13, 'ואהבך וברכך והרבך וברך פרי בטנך ופרי אדמתך דגנך תירשך ויצהרך שגר אלפיך ועשתרות צאנך על האדמה אשר נשבע לאבתיך לתת לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 14, 'ברוך תהיה מכל העמים לא יהיה בך עקר ועקרה ובבהמתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 15, 'והסיר יהוה ממך כל חלי וכל מדוי מצרים הרעים אשר . . ידעת לא ישימם בך ונתנם בכל שנאיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 16, 'ואכלת את כל העמים אשר יהוה אלהיך נתן לך לא תחוס עינך עליהם ולא תעבד את אלהיהם כי מוקש הוא לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 17, 'כי תאמר בלבבך רבים הגוים האלה ממני איך אוכל להורישם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 18, 'לא תירא מהם זכר תזכר את אשר עשה יהוה אלהיך לפרעה ולכל מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 19, 'המסות הגדלות . אשר ראו עיניך האתות והמפתים והיד החזקה והזרוע הנטויה אשר הוציאך יהוה אלהיך כן יעשה יהוה אלהיך לכל העמים אשר אתה ירא מפניהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 20, 'וגם את הצרעה ישלח יהוה אלהיך בם עד אבד הנשארים והנסתרים מפניך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 21, 'לא תערץ מפניהם כי יהוה אלהיך בקרבך אל גדול ונורא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 22, 'ונשל יהוה אלהיך את הגוים האלה מלפניך מעט מעט לא תוכל כלותם מהר פן תרבה עליך חית השדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 23, 'ונתנם יהוה אלהיך לפניך והמם מהמה גדלה עד השמידם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 24, 'ונתן מלכיהם בידך ואבדת את שמם מתחת השמים לא יתיצב איש לפניך עד השמדך אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 25, 'פסלי אלהיהם תשרפון באש לא תחמד כסף וזהב עליהם ולקחת לך פן תוקש בו כי תועבת יהוה אלהיך הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 7, 26, 'ולא תביא תועבה אל ביתך והיית חרם כמהו שקץ תשקצנו ותעב תתעבנו כי חרם הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 8, 1, 'כל המצוה אשר אנכי מצוך היום תשמרון לעשות למען תחיון ורביתם ובאתם וירשתם את הארץ אשר נשבע יהוה לאבתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 8, 2, 'וזכרת את כל הדרך אשר הוליכך יהוה אלהיך זה ארבעים שנה במדבר למען ענותך לנסותך לדעת . אשר בלבבך התשמר מצותיו אם לא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 8, 3, 'ויענך וירעבך ויאכלך את המן אשר לא ידעת ולא ידעו אבתיך למען הודיעך כי לא על הלחם לבדו יחיה האדם כי על כל מוצא פי יהוה יחיה האדם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 8, 4, 'שמלתך לא בלתה מעליך . ורגליך לא בצקה זה ארבעים שנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 8, 5, 'וידעת עם לבבך כי כאשר ייסר איש את בנו . יהוה אלהיך מיסרך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 8, 6, 'ושמרת את מצות יהוה אלהיך ללכת . בדרכיו וליראה אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 8, 7, 'כי יהוה אלהיך מביאך אל ארץ טובה ורחבה ארץ נחלי מים עינות תהומת יצאים בבקעה ובהר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 8, 8, 'ארץ חטה ושערה גפן תאנה ורמון ארץ זית שמן ודבש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 8, 9, 'ארץ אשר לא במסכנת תאכל בה לחם לא תחסר כל . בה ארץ אשר אבניה ברזל ומהרריה תחצב נחשת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 8, 10, 'ואכלת ושבעת וברכת את יהוה אלהיך על הארץ הטובה אשר נתן לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 8, 11, 'השמר לך פן תשכח את יהוה אלהיך לבלתי שמר מצותיו ומשפטיו וחקתיו אשר אנכי מצוך היום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 8, 12, 'פן תאכל ושבעת ובתים טובים תבנה וישבת . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 8, 13, 'ובקרך וצאנך ירבון וכסף וזהב ירבה לך וכל אשר לך ירבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 8, 14, 'ורם לבבך ושכחת את יהוה אלהיך המוציאך מארץ מצרים מבית עבדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 8, 15, 'המוליכך במדבר הגדול והנורא נחש שרוף עקרב וצמאון אשר אין מים המוצא לך מים מצור החלמיש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 8, 16, 'המאכילך מן במדבר אשר לא ידעו אבתיך ולמען ענותך ולמען נסותך להיטבך באחריתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 8, 17, 'ואמרת בלבבך כחי ועצם ידי עשה לי את החיל הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 8, 18, 'וזכרת את יהוה אלהיך כי הוא הנתן לך כח לעשות חיל ולמען הקים את בריתו אשר נשבע לאבתיך לאברהם ליצחק וליעקב כיום הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 8, 19, 'והיה אם שכח תשכח את יהוה אלהיך והלכת אחרי אלהים אחרים ועבדתם והשתחוית להם העדתי בכם היום . . . . כי אבד תאבדון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 8, 20, 'כגוים אשר יהוה מאבד מפניכם כן תאבדון עקב לא תשמעון בקול יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 1, 'שמע ישראל אתה עבר היום את הירדן לבוא לרשת גוים גדלים ועצמים ממך ערים גדלות ובצרות בשמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 2, 'עם גדול ורם בני ענקים אשר אתה ידעת ואתה שמעת מי יתיצב לפני בני ענק ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 3, 'וידעת היום כי יהוה אלהיך הוא העבר לפניך אש אכלה הוא ישמדם והוא יכנעם לפניך והורשתם ואבדתם מהר כאשר דבר יהוה לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 4, 'אל תאמר בלבבך בהדיף יהוה אלהיך אתם מפניך לאמר בצדקתי הביאני יהוה לרשת את הארץ הזאת וברשעת הגוים האלה יהוה מורשם מפניך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 5, 'לא בצדקתך ובישר לבבך אתה בא לרשת את ארצם כי ברשעת הגוים האלה יהוה . מורשם מפניך ולמען הקים את הדבר אשר נשבע . לאבתיך לאברהם ליצחק וליעקב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 6, 'וידעת כי לא בצדקתך יהוה אלהיך נתן לך את הארץ הטובה הזאת לרשתה כי עם קשה ערף אתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 7, 'זכר ואל תשכח את אשר הקצפת את יהוה אלהיך במדבר למן היום אשר יצאתם מארץ מצרים עד באכם עד המקום הזה ממרים הייתם עם יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 8, 'ובחורב הקצפתם את יהוה ויתאנף יהוה בכם להשמיד אתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 9, 'בעלתי ההרה לקחת לוחת האבנים לוחת הברית אשר כרת יהוה עמכם ואשב בהר ארבעים יום וארבעים לילה לחם לא אכלתי ומים לא שתיתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 10, 'ויתן יהוה אלי את שני לוחת האבנים כתבים באצבע אלהים ועליהם ככל הדברים אשר דבר יהוה אליכם בהר מתוך האש ביום הקהל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 11, 'ויהי מקץ ארבעים יום וארבעים לילה נתן יהוה אלי את שני לוחת . . הברית ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 12, 'ויאמר יהוה אלי קום רד מהר מזה כי שחת עמך אשר הוצאת ממצרים סרו מהר מן הדרך אשר צויתם עשו להם עגל מסכה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 13, 'ויאמר יהוה אלי לאמר ראיתי את העם הזה והנה עם קשה ערף הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 14, 'הרף ממני ואשמדם ואמחה את שמם מתחת השמים ואעשה אתך לגוי עצום ורב ממנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 15, 'ואפנה וארדה מן ההר וההר בער באש ושני לוחת הברית על שתי ידי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 16, 'ואראה והנה חטאתם ליהוה אלהיכם עשיתם לכם עגל מסכה סרתם מהר מן הדרך אשר צוה יהוה אתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 17, 'ואתפשה בשני הלוחת ואשלכם מעל שתי ידי ואשברם לעיניכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 18, 'ואתנפלה לפני יהוה כראישונה ארבעים יום וארבעים לילה לחם לא אכלתי ומים לא שתיתי על כל חטאתיכם אשר חטאתם לעשות הרע בעיני יהוה להכעיסו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 19, 'כי יגרתי מפני האף והחמה אשר קצף יהוה עליכם להשמיד אתכם וישמע יהוה אלי גם בפעם ההוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 20, 'ובאהרן התאנף יהוה מאד להשמידו ואתפללה גם בעד אהרן בעת ההיא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 21, 'ואת חטאתיכם אשר עשיתם את העגל לקחתי ואשרפה אתו באש ואכתה אתו טחון הייטב עד אשר דק לעפר ואשליכה את עפרו אל הנחל הירד מן ההר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 22, 'ובתבערה ובמסה ובקברות התאוה מקצפים הייתם את יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 23, 'ובשלח יהוה אתכם מקדש ברנע לאמר עלו ורשו את הארץ אשר נתתי לכם ותמרו את פי יהוה אלהיכם ולא האמנתם לו ולא שמעתם בקולו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 24, 'ממרים הייתם עם יהוה מיום דעתי אתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 25, 'ואתנפלה לפני יהוה את ארבעים היום . וארבעים הלילה אשר התנפלתי כי אמר יהוה להשמיד אתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 26, 'ואתפללה אל יהוה ואמרה אדני יהוה אל תשחית עמך ונחלתך אשר פדית בגדלך אשר הוצאת ממצרים בידך החזקה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 27, 'זכר לעבדיך לאברהם ליצחק וליעקב אל תפן אל קשי העם הזה ואל רשעו ואל חטאתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 28, 'פן יאמרו עם הארץ אשר הוצאתנו משם מבלתי יכלת יהוה להביא אתם אל הארץ אשר דבר להם ומשנאתו אתם הוציאם להמתם במדבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 9, 29, 'והם עמך ונחלתך אשר הוצאת ממצרים בכחך הגדול ובזרועך הנטויה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 10, 1, 'בעת ההיא אמר יהוה אלי פסל לך שני לוחת אבנים כראישונים ועלה אלי ההרה ועשית לך ארון עץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 10, 2, 'ואכתבה על הלוחת את הדברים אשר . היו על הלוחת הראישונים אשר שברת ושמתם בארון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 10, 3, 'ואעשה ארון עצי שטים ואפסלה שני לוחת אבנים כראישונים ואעלה ההרה ושני הלוחת בידי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 10, 4, 'ויכתב על הלוחת כמכתב הראישון את עשרת הדברים אשר דבר יהוה אליכם בהר מתוך האש ביום הקהל ויתנם יהוה אלי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 10, 5, 'ואפנה וארדה מן ההר ואשימה את הלוחת בארון אשר עשיתי ויהיו שם כאשר צוני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 10, 6, 'ובני ישראל נסעו ממסרות ויחנו בבני יעקן . . . . . . . . . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 10, 7, 'משם נסעו ויחנו הגדגדה משם נסעו ויחנו ביטבתה ארץ נחלי מים משם נסעו ויחנו בעברנה משם נסעו ויחנו בעציון גבר משם נסעו ויחנו במדבר צן היא קדש משם נסעו ויחנו בהר ההר וימת שם אהרן ויקבר שם ויכהן אלעזר בנו תחתיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 10, 8, 'בעת ההיא הבדיל יהוה את שבט הלוי לשאת את ארון ברית יהוה לעמד לפני יהוה לשרתו ולברך בשמו עד היום הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 10, 9, 'על כן לא היה ללוי חלק ונחלה עם אחיו יהוה הוא נחלתו כאשר דבר יהוה אלהיך לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 10, 10, 'ואנכי עמדתי בהר כימים הראישונים ארבעים יום וארבעים לילה וישמע יהוה אלי גם בפעם ההוא ולא אבה יהוה השחיתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 10, 11, 'ויאמר יהוה אלי קום לך למסע לפני העם הזה ויבאו ויירשו את הארץ אשר נשבעתי לאבתם לתת להם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 10, 12, 'ועתה ישראל מה יהוה אלהיך שאל מעמך כי אם ליראה את יהוה אלהיך ללכת בכל דרכיו ולאהבה אתו ולעבד את יהוה אלהיך בכל לבבך ובכל נפשך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 10, 13, 'ולשמר את מצות יהוה אלהיך ואת חקתיו אשר אנכי מצוך היום לטוב לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 10, 14, 'הן ליהוה אלהיך השמים ושמי השמים הארץ וכל אשר בה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 10, 15, 'רק באבתיך חשק יהוה לאהבה אתם ויבחר בזרעם אחריהם בכם מכל העמים כיום הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 10, 16, 'ומלתם את ערלת לבבכם וערפכם לא תקשו עוד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 10, 17, 'כי יהוה אלהיכם הוא אלהי האלהים ואדון האדונים האל הגדול והגבור והנורא אשר לא ישא פנים ולא יקח שחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 10, 18, 'עשה משפט יתום ואלמנה ואהב גר לתת לו לחם ושמלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 10, 19, 'ואהבתם את הגר כי גרים הייתם בארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 10, 20, 'את יהוה אלהיך תירא ואתו תעבד ובו תדבק ובשמו תשבע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 10, 21, 'הוא תהלתך והוא אלהיך אשר עשה אתך את הגדלות ואת הנוראות האלה אשר ראו עיניך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 10, 22, 'בשבעים נפש ירדו אבתיך מצרימה ועתה שמך יהוה אלהיך ככוכבי השמים לרב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 1, 'ואהבת את יהוה אלהיך ושמרת משמרתו חקתיו ומצותיו ומשפטיו כל הימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 2, 'וידעתם היום כי לא את בניכם אשר לא ידעו ואשר לא ראו את מוסר יהוה אלהיכם את גדלו ואת ידו החזקה ואת זרועו הנטויה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 3, 'את אתתיו ואת מעשיו אשר עשה בתוך מצרים לפרעה . . ולכל ארצו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 4, 'ואשר עשה לחיל מצרים לסוסיו ולרכבו אשר הציף את מי ים סוף על פניהם ברדפם אחריכם ויאבדם יהוה עד היום הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 5, 'ואשר עשה לכם במדבר עד באכם עד המקום הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 6, 'ואשר עשה לדתן ולאבירם בני אליאב בן ראובן אשר פצתה הארץ את פיה ותבלעם ואת כל האדם אשר לקרח בתיהם ואת אהליהם ואת כל . . . היקום אשר ברגליהם בקרב כל ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 7, 'כי עיניכם הראות את כל מעשה יהוה הגדול אשר . עשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 8, 'ושמרתם את כל המצוה . . אשר אנכי מצוה אתכם היום למען תחזקו . ובאתם וירשתם את הארץ אשר אתם באים . . שמה לרשתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 9, 'ולמען תאריכון ימים על האדמה אשר נשבע יהוה לאבתיכם לתת . לזרעם . ארץ זבת חלב ודבש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 10, 'כי הארץ אשר אתם באים שמה לרשתה לא כארץ מצרים היא אשר יצאתם משם אשר תזרע את זרעך והשקית ברגליך כגן הירק ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 11, 'והארץ אשר אתם עברים שמה לרשתה ארץ הרים ובקעות למטר השמים תשתה מים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 12, 'ארץ אשר יהוה אלהיך דרש אתה תמיד עיני יהוה אלהיך בה מראשית השנה ועד אחרית השנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 13, 'והיה אם שמע תשמעו אל מצותי אשר אנכי מצוה אתכם היום לאהבה את יהוה אלהיכם לעבדו בכל לבבכם ובכל נפשכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 14, 'ונתן מטר ארצך בעתו יורא ומלקוש ואספת דגנך תירשך ויצהרך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 15, 'ונתן עשב בשדך לבהמתך ואכלת ושבעת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 16, 'השמרו לכם פן יפתה לבבכם וסרתם ועבדתם אלהים אחרים והשתחויתם להם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 17, 'וחרה אף יהוה בכם ועצר את השמים ולא יהיה מטר והאדמה לא תתן את יבולה ואבדתם מהר מעל הארץ הטובה אשר יהוה נתן לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 18, 'ושמתם את דברי אלה על לבבכם ועל נפשכם וקשרתם אתם לאות על ידיכם והיו לטטפות בין עיניכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 19, 'ולמדתם אתם את בניכם לדבר בם בשבתך בבית בלכתך בדרך בשכבך ובקומך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 20, 'וכתבתם על מזוזת בתיך ובשעריך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 21, 'למען ירבון ימיכם וימי בניכם על האדמה אשר נשבע יהוה לאבתיכם לתת להם כימי השמים על הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 22, 'כי אם שמר תשמרון את כל המצוה הזאת אשר אנכי מצוה אתכם היום לעשותה לאהבה את יהוה אלהיכם ללכת בכל דרכיו ולדבקה בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 23, 'והוריש יהוה את כל הגוים האלה מלפניך וירשתם גוים גדלים ועצמים ממך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 24, 'כל המקום אשר תדרך כף רגלכם בו לכם יהיה מן המדבר והלבנון ומן הנהר נהר פרת ועד הים האחרון יהיה גבולכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 25, 'לא יתיצב איש בפניכם פחדכם ומראכם יתן יהוה אלהיכם על פני כל הארץ אשר תדרכו בה כאשר דבר לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 26, 'ראה אנכי נתן לפניכם היום ברכה וקללה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 27, 'את הברכה אשר תשמעון אל מצות יהוה אלהיכם אשר אנכי מצוה אתכם היום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 28, 'והקללה אם לא תשמעון אל מצות יהוה אלהיכם וסרתם מן הדרך אשר אנכי מצוה אתכם היום ללכת אחרי אלהים אחרים אשר לא ידעתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 29, 'והיה כי יביאך יהוה אלהיך אל הארץ אשר אתה בא שמה לרשתה ונתתה את הברכה על הר גריזים ואת הקללה על הר עיבל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 30, 'הלוא הם בעבר הירדן אחרי דרך מבוא השמש בארץ הכנעני הישב בערבה מול הגלגל אצל אלוני מורא מול שכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 31, 'כי אתם עברים את הירדן לבוא לרשת את הארץ אשר יהוה אלהיכם נתן לכם וירשתם אתה וישבתם בה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 11, 32, 'ושמרתם לעשות את כל החקים ואת כל המשפטים אשר אנכי נתן לפניכם היום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 1, 'אלה החקים והמשפטים אשר תשמרו לעשות בארץ אשר נתן יהוה אלהי אבתיך לך לרשתה כל הימים אשר אתם חיים על האדמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 2, 'אבד תאבדון את כל המקומת אשר עבדו שם הגוים אשר אתם ירשים אתם את אלהיהם על ההרים הרמים ועל הגבעות ותחת כל עץ רענן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 3, 'ונתצתם את מזבחתיהם ושברתם את מצבתיהם ואשריהם תשרפון באש ופסלי אלהיהם תגדעון ואבדתם את שמם מן המקום ההוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 4, 'לא תעשו כן ליהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 5, 'כי אם אל המקום אשר בחר יהוה אלהיכם מכל שבטיכם לשים את שמו שם לשכנו תדרשו ובאתם שמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 6, 'והבאתם שמה עלתיכם וזבחיכם ואת מעשרתיכם ואת תרומתיכם . ונדריכם ונדבתיכם ובכרת בקרכם וצאנכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 7, 'ואכלתם שם לפני יהוה אלהיכם ושמחתם בכל משלח ידיכם אתם ובתיכם אשר ברכך יהוה אלהיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 8, 'לא תעשו ככל אשר אנחנו עשים פה היום איש כל הישר בעיניו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 9, 'כי לא באתם עד עתה אל המנוחה ואל הנחלה אשר יהוה אלהיכם נתן לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 10, 'ועברתם את הירדן וישבתם בארץ אשר יהוה אלהיכם מנחיל אתכם והניח לכם מכל איביכם מסביב וישבתם בטח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 11, 'והיה המקום אשר בחר יהוה אלהיכם בו לשכן את שמו שם שמה תביאו את כל אשר אנכי מצוה אתכם עלתיכם וזבחיכם ומעשרתיכם ותרמתימם . ונדבתיכם וכל מבחר נדריכם אשר תדרו ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 12, 'ושמחתם לפני יהוה אלהיכם אתם ובניכם ובנתיכם עבדיכם ואמהתיכם והלוי אשר בשעריכם כי אין לו חלק ונחלה אתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 13, 'השמר לך פן תעלה עלתיך בכל מקום אשר תראה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 14, 'כי אם במקום אשר בחר יהוה באחד שבטיך שם תעלה עלתיך ושם תעשה את כל אשר אנכי מצוך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 15, 'רק בכל אות נפשך תזבח ואכלת בשר כברכת יהוה אלהיך אשר נתן לך בכל שעריך הטמא והטהור יאכלנו כצבי וכאיל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 16, 'רק הדם לא תאכל על הארץ תשפכנו כמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 17, 'לא תוכל לאכל בשעריך מעשר דגנך תירשך ויצהרך ובכורת בקרך וצאנך וכל נדריך אשר תדר ונדבתיך ותרומת ידיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 18, 'כי אם לפני יהוה אלהיך תאכלנו במקום אשר בחר יהוה אלהיך בו אתה ובנך ובתך עבדך ואמתך והלוי אשר בשעריך ושמחת לפני יהוה אלהיך בכל משלח ידיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 19, 'השמר לך פן תעזב את הלוי כל ימיך על אדמתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 20, 'כי ירחיב יהוה אלהיך את גבולך כאשר דבר לך ואמרת אכלה בשר כי תאוה נפשך לאכל בשר בכל אות נפשך תאכל בשר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 21, 'כי ירחק ממך המקום אשר בחר יהוה אלהיך לשכן את שמו שם וזבחת מבקרך ומצאנך אשר נתן יהוה לך כאשר צויתיך ואכלת בשעריך בכל אות נפשך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 22, 'אך כאשר יאכל את הצבי ואת האיל כן תאכלנו הטמא בך והטהור יאכלנו יחדו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 23, 'רק חזק לבלתי אכל הדם כי הדם הוא הנפש ולא תאכל הנפש עם הבשר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 24, 'לא תאכלנו על הארץ תשפכנו כמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 25, 'לא תאכלנו למען ייטב לך ולבניך אחריך כי תעשה הישר בעיני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 26, 'רק קדשיך אשר יהיו לך ונדריך תשא ובאת אל המקום אשר בחר יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 27, 'ועשית עלתיך הבשר והדם על מזבח יהוה אלהיך ודם זבחיך ישפך על מזבח יהוה אלהיך והבשר תאכל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 28, 'שמור ושמעת ועשית את כל הדברים האלה אשר אנכי מצוך היום למען ייטב לך ולבניך אחריך עד עולם כי תעשה הישר והטוב בעיני יהוה אלהיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 29, 'כי יכרית יהוה אלהיך את הגוים אשר אתה בא שמה לרשת אתם מפניך וירשת אתם וישבת בארצם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 30, 'השמר לך פן תנקש אחריהם אחרי השמדם מפניך ופן תדרש לאלהיהם לאמר איך יעבדו הגוים האלה את אלהיהם ואעשה כן גם אני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 12, 31, 'לא תעשה כן ליהוה אלהיך כי את כל תועבת יהוה אשר שנא עשו לאלהיהם כי גם את בניהם ואת בנתיהם ישרפו באש לאלהיהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 13, 1, 'את כל הדבר אשר אנכי מצוך היום אתו תשמרו לעשות לא תוסיפו עליו ולא תגרעו ממנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 13, 2, 'כי יקום בקרבך נביא או חלם חלום ונתן אליך אות או מופת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 13, 3, 'ובא האות והמופת אשר דבר אליך לאמר נלכה אחרי אלהים אחרים אשר לא ידעתם ונעבדם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 13, 4, 'לא תשמע אל דברי הנביא ההוא או אל החלם החלום ההוא כי מנסה יהוה אלהיכם אתכם לדעת הישכם אהבים את יהוה אלהיכם בכל לבבכם ובכל נפשכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 13, 5, 'אחרי יהוה אלהיכם תלכון ואתו תיראון ואת מצותיו תשמרון ובקולו תשמעון ואתו תעבדו ובו תדבקו . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 13, 6, 'והנביא ההוא או חלם החלום ההוא יומת כי דבר סרה על יהוה אלהיך המוציאך . מארץ מצרים והפדך מבית עבדים להדיחך מן הדרך אשר צוך יהוה אלהיך ללכת בה ובערת הרע מקרבך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 13, 7, 'כי יסיתך אחיך בן אביך או בן אמך או בנך . או בתך או אשת חיקך או רעך אשר כנפשך בסתר לאמר נלכה ונעבדה אלהים אחרים אשר לא ידעת אתה ואבתיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 13, 8, 'מאלהי העמים אשר סביבתיכם הקרובים אליך או הרחקים ממך מקצה הארץ ועד קצה הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 13, 9, 'לא תאבה אליו ולא תשמע אליו ולא תחוס עינך עליו ולא תחמל ולא תכסה עליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 13, 10, 'כי הרג תהרגנו ידך תהיה בו בראישונה להמיתו ויד כל העם באחרנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 13, 11, 'וסקלתו באבנים ומת כי בקש להדיחך מעל יהוה אלהיך המוציאך מארץ מצרים מבית עבדים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 13, 12, 'וכל ישראל ישמעו וייראו ולא יוספו עוד לעשות כדבר הרע הזה בקרבך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 13, 13, 'כי תשמע באחת עריך אשר יהוה אלהיך נתן לך לשבת שם לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 13, 14, 'יצאו אנשים בני בליעל מקרבך וידיחו את ישבי עירם לאמר נלכה ונעבדה אלהים אחרים אשר לא ידעתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 13, 15, 'ודרשת וחקרת ושאלת הייטב והנה אמת נכון הדבר נעשתה התועבה הזאת בקרבך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 13, 16, 'הכה תכה את ישבי העיר ההיא לפי חרב החרים אתה ואת כל אשר בה ואת בהמתה לפי חרב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 13, 17, 'ואת כל שללה תקבץ אל תוך רחבה ושרפת באש את העיר ואת כל שללה כליל ליהוה אלהיך והיתה תל עולם לא תבנה עוד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 13, 18, 'ולא ידבק בידך מאומה מן החרם למען ישוב יהוה מחרון אפו ונתן לך רחמים ורחמך והרבך כאשר נשבע לאבתיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 13, 19, 'כי תשמע בקול יהוה אלהיך לשמר את כל מצותיו אשר אנכי מצוך היום לעשות הישר והטוב בעיני יהוה אלהיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 1, 'בנים אתם ליהוה אלהיכם לא תתגדדו ולא תשימו קרחה בין עיניכם למת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 2, 'כי עם קדש אהה ליהוה אלהיך ובך בחר יהוה אלהיך להיות לו לעם סגלה מכל העמים אשר על פני האדמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 3, 'לא תאכלו כל תועבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 4, 'וזאת הבהמה אשר תאכלו שור ושה כשבים ושה עזים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 5, 'איל וצבי ויחמור אקו ודישון תאי וזמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 6, 'כל בהמה מפריסת פרסה ושסעת שסע שתי פרסות מעלת גרה בבהמה אתה תאכלו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 7, 'אך את זה לא תאכלו ממעלי הגרה וממפרסי הפרסה . את הגמל ואת הארנבת ואת השפן כי מעלי גרה הם ופרסה לא הפריסו טמאים הם לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 8, 'ואת החזיר כי מפריס פרסה הוא ושסע שסע פרסה והוא גרה לא יגור טמא הוא לכם מבשרם לא תאכלו ובנבלתם לא תגעו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 9, 'ואת זה תאכלו מכל אשר במים כל אשר לו סנפיר וקשקשת תאכלו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 10, 'וכל אשר אין לו סנפיר וקשקשת לא תאכלו טמא הוא לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 11, 'כל צפור טהרה תאכלו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 12, 'וזה אשר לא תאכלו מהם את הנשר ואת הפרס ואת העזניה ואת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 13, 'הדאה ואת האיה . למינה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 14, 'ואת כל ערב למינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 15, 'ואת בת היענה ואת התחמס ואת השחף למינו ואת הנץ . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 16, 'ואת הכוס ואת שלך ואת הינשף ואת התנשמת ואת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 17, 'הקאת ואת הרחמה . . ואת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 18, 'החסידה והאנפה למינה ואת הדגפת ואת העטלף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 19, 'וכל שרץ העוף טמא הוא לכם לא תאכלו מהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 20, 'כל עוף טהור תאכלו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 21, 'לא תאכלו כל נבלה לגר אשר בשעריך תתננה ואכלה או מכרה לנכרי כי עם קדש אתה ליהוה אלהיך לא תבשל גדי בחלב אמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 22, 'עשר תעשר את כל תבואת זרעך יוצא השדה שנה שנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 23, 'ואכלתו לפני יהוה אלהיך במקום אשר בחר יהוה אלהיך לשכן את שמו שם מעשר דגנך תירשך ויצהרך ובכורת בקרך וצאנך למען תלמד ליראה את יהוה אלהיך כל הימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 24, 'וכי ירבה ממך הדרך כי לא תוכל שאתו כי ירחק ממך המקום אשר בחר יהוה אלהיך לשכן את שמו שם כי יברכך יהוה אלהיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 25, 'ונתתה בכסף וצרת הכסף בידך והלכת אל המקום אשר בחר יהוה אלהיך בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 26, 'ונתתה הכסף בכל אשר תאוה נפשך בבקר ובצאן ביין ובשכר ובכל אשר תשאלך נפשך ואכלת שם לפני יהוה אלהיך ושמחת אתה וביתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 27, 'והלוי אשר בשעריך לא תעזבנו כי אין לו חלק ונחלה עמך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 28, 'מקץ שלש שנים תוצא את כל מעשר תבואתך בשנה ההיא והנחתו בשעריך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 14, 29, 'ובא הלוי כי אין לו חלק ונחלה עמך והגר והיתום והאלמנה אשר בשעריך ואכלו ושבעו למען יברכך יהוה אלהיך בכל מעשה ידיך אשר תעשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 1, 'מקץ שבע שנים תעשה שמטה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 2, 'וזה דבר השמטה שמט כל בעל משא ידו אשר ישא ברעהו לא יגש את רעהו את אחיו כי קרא שמטה ליהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 3, 'את הנכרי תגש ואשר יהיה לך את אחיך תשמט ידך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 4, 'אפס כי לא יהיה בך אביון כי ברוך יברכך יהוה אלהיך בארץ אשר יהוה אלהיך נתן לך נחלה לרשתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 5, 'רק אם שמע תשמע בקול יהוה אלהיך לשמר ולעשות את כל המצוה הזאת אשר אנכי מצוך היום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 6, 'כי יהוה אלהיך ברכך כאשר דבר לך והעבטת גוים רבים ואתה לא תעבט ומשלת בגוים רבים ובך לא ימשלו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 7, 'כי יהיה בך אביון מאחד אחיך באחת שעריך בארץ אשר יהוה אלהיך נתן לך לא תאמץ את לבבך ולא תקפץ את ידך מאחיך האביון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 8, 'כי פתח תפתח . ידך לו העבט תעביטנו די מחסרו אשר יחסר לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 9, 'השמר לך פן יהיה דבר עם לבבך בליעל לאמר קרובה שנת השבע שנת השמטה ורעה עינך באחיך האביון ולא תתן לו וקרא עליך אל יהוה והיה בך חטא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 10, 'נתן תתן לו ולא ירע בלבבך בתתך לו כי בגלל הדבר הזה יברכך יהוה אלהיך בכל מעשיך ובכל משלח ידיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 11, 'כי לא יחדל האביון מקרב הארץ על כן אנכי מצוך לאמר פתח תפתח . ידך לאחיך לעניך ולאביונך בארצך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 12, 'כי ימכר לך אחיך העברי או העבריה ועבדך שש שנים ובשנה השבעית תשלחנו חפשי מעמך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 13, 'וכי תשלחנו חפשי מעמך לא תשלחנו ריקם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 14, 'העניק תעניק לו מצאנך ומגרנך ומיקבך כאשר ברכך יהוה . אלהיך תתן לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 15, 'וזכרת כי עבד היית בארץ מצרים ויפדך יהוה אלהיך על כן אנכי מצוך . את הדבר הזה היום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 16, 'והיה כי יאמר אליך לא אצא מעמך כי אהבך ואת ביתך כי טוב לו עמך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 17, 'ולקחת את המרצע ונתת באזנו ובדלת והיה לך עבד עולם ואף לאמתך תעשה כן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 18, 'ולא יקשה בעיניך בשלחך אתו חפשי מעמך כי משנה שכר שכיר עבדך שש שנים וברכך יהוה אלהיך בכל אשר תעשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 19, 'כל הבכור אשר יולד בבקרך ובצאנך הזכר תקדיש ליהוה אלהיך לא תעבד בבכור שורך ולא תגז בכור צאנך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 20, 'לפני יהוה אלהיך תאכלנו שנה בשנה במקום אשר בחר יהוה אתה וביתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 21, 'וכי יהיה בו מום פסח או עור או כל מום רע לא תזבחנו ליהוה אלהיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 22, 'בשעריך תאכלנו הטמא והטהור יחדו כצבי וכאיל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 15, 23, 'רק את דמו לא תאכל על הארץ תשפכנו כמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 16, 1, 'שמור את חדש האביב ועשית פסח ליהוה אלהיך כי בחדש האביב הוציאך יהוה אלהיך ממצרים לילה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 16, 2, 'וזבחת פסח ליהוה אלהיך צאן ובקר במקום אשר בחר יהוה אלהיך לשכן את שמו שם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 16, 3, 'לא תאכלו עליו חמץ שבעת ימים תאכלו עליו מצות לחם עני כי בחפזון יצאת מארץ מצרים למען תזכר את יום צאתך מארץ מצרים כל ימי חייך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 16, 4, 'ולא יראה לך שאר בכל גבולך שבעת ימים ולא ילין מן הבשר אשר תזבח בין הערבים ביום הראישון לבקר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 16, 5, 'ולא תוכל לזבח את הפסח באחת שעריך אשר יהוה אלהיך נתן לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 16, 6, 'כי אם . במקום אשר בחר יהוה אלהיך . לשכן את שמו שם שם תזבח את הפסח בערב כבוא השמש מועד . צאתך ממצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 16, 7, 'ובשלת ואכלת במקום אשר בחר יהוה אלהיך בו ופנית בבקר והלכת לאהליך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 16, 8, 'ששת ימים תאכל מצות וביום השביעי חג ליהוה אלהיך לא תעשה . כל מלאכת עבדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 16, 9, 'שבעה שבעות תספר לך מהחלך חרמש בקמה תחל לספר שבעה שבעות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 16, 10, 'ועשית חג שבעות ליהוה אלהיך מסת נדבת ידיך אשר תתן כאשר ברכך יהוה אלהיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 16, 11, 'ושמחת לפני יהוה אלהיך אתה ובנך ובתך עבדך ואמתך והלוי אשר בשעריך והגר והיתום והאלמנה אשר בקרבך במקום אשר בחר יהוה אלהיך לשכן את שמו שם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 16, 12, 'וזכרת כי עבד היית בארץ מצרים ושמרת ועשית את החקים האלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 16, 13, 'חג הסכות תעשה לך שבעת ימים באספך מגרנך ומיקבך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 16, 14, 'ושמחת בחגך אתה ובנך ובתך עבדך ואמתך והלוי והגר והיתום והאלמנה אשר בשעריך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 16, 15, 'שבעת ימים תחג ליהוה אלהיך במקום אשר בחר יהוה אלהיך כי יברכך יהוה אלהיך בכל תבואתך ובכל מעשה ידיך והיית אך שמח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 16, 16, 'שלש פעמים בשנה יראה כל זכורך את פני יהוה אלהיך במקום אשר בחר בו בחג המצות ובחג השבעות ובחג הסכות ולא יראו את פני יהוה ריקם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 16, 17, 'איש כמתנת ידו כברכת יהוה אלהיך אשר נתן לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 16, 18, 'שפטים ושטרים תתן לך בכל שעריך אשר יהוה אלהיך נתן לך לשבטיך ושפטו את העם משפט צדק ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 16, 19, 'לא תטה משפט ולא תכיר פנים ולא תקח שחד כי השחד יעור עיני חכמים ויסלף דברי צדקים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 16, 20, 'צדק צדק תרדף למען תחיה וירשת את הארץ אשר יהוה אלהיך נתן לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 16, 21, 'לא תטע לך אשרה כל עץ אצל מזבח יהוה אלהיך אשר תעשה לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 16, 22, 'ולא תקים לך מצבה אשר שנאה יהוה אלהיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 17, 1, 'לא תזבח ליהוה אלהיך שור ושה אשר יהיה בו מום כל דבר רע כי תועבת יהוה אלהיך הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 17, 2, 'כי ימצא בקרבך באחת שעריך אשר יהוה אלהיך נתן לך איש או אשה אשר יעשה את הרע בעיני יהוה אלהיך לעבר בריתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 17, 3, 'וילך ויעבד אלהים אחרים וישתחוי להם ולשמש או לירח או לכל צבא השמים אשר לא צויתיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 17, 4, 'והגידו לך ושמעת ודרשת הייטב והנה אמת נכון הדבר נעשתה התועבה הזאת בישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 17, 5, 'והוצאת את האיש ההוא או את האשה ההיא אשר עשו את הדבר הרע הזה אל שעריך את האיש או את האשה וסקלתם באבנים ומתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 17, 6, 'על פי שני עדים או על פי שלשה עדים יומת המת לא יומת על פי עד אחד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 17, 7, 'יד העדים תהיה בו בראישונה להמיתו ויד כל העם באחרנה ובערת הרע מקרבך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 17, 8, 'כי יפלא ממך דבר למשפט בין דם לדם ובין דין לדין ובין נגע לנגע דברי ריבות בשעריך וקמת ועלית אל המקום אשר בחר יהוה אלהיך . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 17, 9, 'ובאת אל הכהנים הלוים ואל השפט אשר יהיה בימים ההם ודרשו והגידו לך את דבר המשפט ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 17, 10, 'ועשית על פי הדבר אשר יגידו לך מן המקום ההוא אשר בחר יהוה אלהיך ושמרת לעשות ככל אשר יורוך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 17, 11, 'על פי התורה אשר יורוך ועל פי המשפט אשר יאמרו לך תעשה ולא תסור מן הדבר אשר יגידו לך ימין ושמאל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 17, 12, 'והאיש אשר יעשה בזדון לבלתי שמע אל הכהן העמד לשרת שם את יהוה אלהיך או אל השפט ומת האיש ההוא ובערת הרע מישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 17, 13, 'וכל העם ישמעו וייראו ולא יזידו עוד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 17, 14, 'כי תבוא אל הארץ אשר יהוה אלהיך נתן לך וירשתה וישבת בה ואמרת אשימה עלי מלך ככל הגוים אשר סביבתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 17, 15, 'שים תשים עליך מלך אשר יבחר יהוה אלהיך בו מקרב אחיך תשים עליך מלך לא תוכל לתת עליך איש נכרי אשר לא אחיך הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 17, 16, 'רק לא ירבה לו סוסים ולא ישיב את העם מצרימה למען הרבות סוס ויהוה אמר לכם לא תוסיפון לשוב בדרך הזה עוד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 17, 17, 'ולא ירבה לו נשים ולא יסור לבבו וכסף וזהב לא ירבה לו מאד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 17, 18, 'והיה כשבתו על כסא ממלכתו וכתב לו את משנה התורה הזאת על ספר מלפני הכהנים הלוים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 17, 19, 'והיתה עמו וקרא בה כל ימי חייו למען ילמד ליראה את יהוה אלהיו לשמר את כל דברי התורה הזאת ואת החקים האלה לעשותם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 17, 20, 'לבלתי רם לבבו מאחיו ולבלתי סר מן המצוה ימין ושמאל למען יאריך ימים על כסא ממלכתו הוא ובניו בקרב ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 18, 1, 'לא יהיה לכהנים הלוים כל שבט לוי חלק ונחלה עם ישראל אשי יהוה ונחלתו יאכלון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 18, 2, 'ונחלה לא יהיה לו בקרב אחיו יהוה הוא נחלתו כאשר דבר לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 18, 3, 'וזה יהיה משפט הכהנים מאת העם מאת זבחי הזבח אם שור אם שה ונתן לכהן הזרוע והלחים והקבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 18, 4, 'ראשית דגנך תירשך ויצהרך וראשית גז צאנך תתן לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 18, 5, 'כי בו בחר יהוה אלהיך מכל שבטיך לעמד לפני יהוה אלהיך ולשרתו ולברך בשמו . הוא ובניו כל הימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 18, 6, 'וכי יבוא הלוי מאחד שעריך מכל ישראל אשר הוא גר שם ובא בכל אות נפשו אל המקום אשר בחר יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 18, 7, 'ושרת בשם יהוה אלהיו ככל אחיו הלוים העמדים שם לפני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 18, 8, 'חלק כחלק יאכלון לבד ממכרו ועל האבות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 18, 9, 'כי אתה בא אל הארץ אשר יהוה אלהיך נתן לך לא תלמד לעשות כתועבת הגוים ההם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 18, 10, 'לא ימצא בך מעביר בנו ובתו באש קסם קסמים מענן מנחש מכשף ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 18, 11, 'חבר חבר שאל אוב וידעני ודרש אל המתים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 18, 12, 'כי תועבת יהוה אלהיך כל עשה אלה ובגלל התועבת האלה יהוה אלהיך מורשם . מפניך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 18, 13, 'תמים תהיה עם יהוה אלהיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 18, 14, 'כי הגוים האלה אשר אתם ירשים אתם אל המעננים ואל הקסמים ישמעו ואתה לא כן נתן לך יהוה אלהיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 18, 15, 'נביא מקרב אחיך כמוני יקים לך יהוה אלהיך אליו תשמעון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 18, 16, 'ככל אשר שאלת מעם יהוה אלהיך בחורב ביום הקהל לאמר לא אוסף לשמע את קול יהוה אלהי ואת אשו הגדלה הזאת לא אראה עוד ולא אמות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 18, 17, 'ויאמר יהוה אלי הטיבו אשר דברו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 18, 18, 'נביא אקים להם מקרב אחיהם כמוך ונתתי דברי בפיו ודבר אליהם את כל אשר אצונו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 18, 19, 'והיה האיש אשר לא ישמע אל דבריו אשר ידבר בשמי אנכי אדרש מעמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 18, 20, 'אך הנביא אשר יזיד לדבר דבר בשמי את אשר לא צויתיו לדבר ואשר ידבר בשם אלהים אחרים ומת הנביא ההוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 18, 21, 'וכי תאמר בלבבך איך נודע את הדבר אשר לא דברו יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 18, 22, 'אשר ידבר הנביא בשם יהוה לא יהיה הדבר ולא יבוא הוא הדבר אשר לא דברו יהוה בזדון דברו הנביא לא תגור ממנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 19, 1, 'כי יכרית יהוה אלהיך את הגוים אשר יהוה אלהיך נתן לך את ארצם וירשתם וישבת בעריהם ובבתיהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 19, 2, 'שלש ערים תבדיל לך בתוך ארצך אשר יהוה אלהיך נתן לך לרשתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 19, 3, 'תכן לך הדרך ושלשת את גבול ארצך אשר ינחילך יהוה אלהיך והיה לנוס שמה כל רצח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 19, 4, 'וזה דבר הרצח אשר ינוס שמה וחי אשר יכה את רעהו בבלי דעת והוא לא שנא לו מתמול שלשום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 19, 5, 'ואשר יבוא את רעהו ביער לחטב עצים ונדח ידו בגרזן לכרת העץ ונשל הברזל מן העץ ומצא את רעהו ומת הוא ינוס אל אחת מן הערים האלה וחי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 19, 6, 'פן ירדף גאל הדם אחרי הרצח כי יחם לבבו והשיגו כי ירבה הדרך והכהו נפש ולו אין משפט מות כי לא שנא הוא לו מתמול שלשום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 19, 7, 'על כן אנכי מצוך לאמר שלש ערים תבדל לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 19, 8, 'ואם ירחיב יהוה אלהיך את גבולך כאשר נשבע לאבתיך ונתן לך את כל הארץ אשר דבר לתת לאבתיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 19, 9, 'כי תשמר את כל המצוה הזאת לעשותה אשר אנכי מצוך היום לאהבה את יהוה אלהיך וללכת בדרכיו כל הימים ויספת לך עוד שלש ערים על השלש האלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 19, 10, 'ולא ישפך דם נקיא בקרב ארצך אשר יהוה אלהיך נתן לך נחלה והיה עליך דמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 19, 11, 'כי יהיה איש שנא לרעהו וארב לו וקם עליו והכהו נפש ומת ונס אל אחת . הערים האלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 19, 12, 'ושלחו זקני עירו ולקחו אתו משם ונתנו אתו ביד גאל הדם והומת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 19, 13, 'לא תחוס עינך עליו ובערת הדם הנקיא מישראל וטוב לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 19, 14, 'לא תסיג גבול רעך אשר גבלו ראישונים בנחלתך אשר תנחל בארץ אשר יהוה אלהיך נתן לך לרשתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 19, 15, 'לא יקום עד אחד באיש לכל עון ולכל חטא בכל חטא אשר יחטא על פי שני עדים או על פי שלשה עדים יקום דבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 19, 16, 'וכי יקום עד חמס באיש לענות בו סרה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 19, 17, 'ועמדו שני האנשים אשר להם הריב לפני יהוה לפני הכהנים והשפטים אשר יהיו בימים ההם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 19, 18, 'ודרשו השפטים הייטב והנה עד שקר העד שקר ענה באחיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 19, 19, 'ועשיתם לו כאשר זמם לעשות לאחיו ובערת הרע מקרבך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 19, 20, 'והנשארים ישמעו וייראו ולא יוסיפו עוד לעשות כדבר הרע הזה בקרבך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 19, 21, 'לא תחוס עינך נפש בנפש עין בעין שן בשן יד ביד רגל ברגל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 20, 1, 'כי תצא למלחמה על איביך וראית סוס ורכב ועם רב ממך לא תירא מהם כי יהוה אלהיך עמך המעלך מארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 20, 2, 'והיה כקרבכם אל המלחמה ונגש הכהן ודבר אל העם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 20, 3, 'ואמר אליהם שמע ישראל אתם קרבים היום למלחמה על איביכם אל ירך לבבכם אל תיראו ואל תערצו ואל תחפזו מפניהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 20, 4, 'כי יהוה אלהיכם ההלך עמכם להלחם לכם עם איביכם ולהושיע אתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 20, 5, 'ודברו השטרים אל העם לאמר מי האיש אשר בנה בית חדש ולא חנכו ילך וישוב לביתו פן ימות במלחמה ואיש אחר יחנכנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 20, 6, 'ומי האיש אשר נטע כרם ולא חללו ילך וישוב לביתו פן ימות במלחמה ואיש אחר יחללנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 20, 7, 'ומי האיש אשר ארש אשה ולא לקחה ילך וישוב לביתו פן ימות במלחמה ואיש אחר יקחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 20, 8, 'ויוסיפו השטרים לדבר אל העם ואמרו מי האיש הירא ורך הלבב ילך וישוב לביתו ולא ימיס את לבב אחיו כלבבו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 20, 9, 'והיה ככלות השטרים לדבר אל העם ופקדו שרי צבאות בראש העם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 20, 10, 'כי תקרב על עיר להלחם עליה וקראת אליה לשלום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 20, 11, 'והיה אם שלום תענך ופתחה לך והיה כל העם הנמצא בה יהיו לך למס ועבדוך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 20, 12, 'ואם לא תשלים עמך ועשתה עמך מלחמה וצרת עליה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 20, 13, 'ונתנה יהוה אלהיך בידך והכית את כל זכורה לפי חרב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 20, 14, 'רק הנשים והטף והבהמה וכל אשר יהיה בעיר כל שללה תבז לך ואכלת את שלל איביך אשר נתן יהוה אלהיך לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 20, 15, 'כן תעשה לכל הערים הרחקת ממך מאד אשר לא מערי הגוים האלה הנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 20, 16, 'רק מערי העמים האלה אשר יהוה אלהיך נתן לך נחלה לא תחיה כל נשמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 20, 17, 'כי החרם תחרימם הכנעני והאמרי והחתי והגרגשי והפרזי והחוי והיבוסי כאשר צוך יהוה אלהיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 20, 18, 'למען אשר לא ילמדו אתכם לעשות ככל תועבתם אשר עשו לאלהיהם וחטאתם ליהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 20, 19, 'כי תצור על עיר ימים רבים להלחם עליה לתפשה לא תשחית את עצה לנדח עליו גרזן כי ממנו תאכל ואתו לא תכרית כי האדם עץ השדה לבוא מפניך במצור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 20, 20, 'רק עץ אשר תדע כי לא עץ מאכל הוא אתו תשחית וכרת ובנית מצור על העיר אשר היא עשתה עמך מלחמה עד רדתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 1, 'כי ימצא חלל באדמה אשר יהוה אלהיך נתן לך לרשתה נפל בשדה לא נודע מי הכהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 2, 'ויצאו זקניך ושטריך ומדדו אל הערים אשר סביבת החלל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 3, 'והיה העיר הקרובה אל החלל ולקחו זקני העיר ההיא עגלת בקר אשר לא עבד בה ואשר לא משכה בעל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 4, 'והורידו זקני העיר ההיא את העגלה אל נחל איתן אשר לא יעבד בו ולא יזרע וערפו שם את העגלה בנחל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 5, 'ונגשו הכהנים בני לוי כי בם בחר יהוה אלהיך לשרת ולברך בשם יהוה ועל פיהם יהיה כל ריב וכל נגע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 6, 'וכל זקני העיר ההיא הקרובים אל החלל ירחצו את ידיהם על העגלה הערופה בנחל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 7, 'וענו ואמרו ידינו לא שפכו את הדם הזה ועינינו לא ראו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 8, 'כפר לעמך ישראל אשר פדית יהוה ואל תתן דם נקיא בקרב עמך ישראל ונכפר להם הדם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 9, 'ואתה תבער הדם הנקיא מקרבך כי תעשה הישר בעיני יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 10, 'כי תצא למלחמה על איביך ונתנו יהוה אלהיך בידך ושבית שביו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 11, 'וראית בשביו אשה יפת תאר וחשקת בה ולקחת לך לאשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 12, 'והבאתה אל תוך ביתך וגלחה את ראשה ועשתה את צפרניה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 13, 'והסירה את שמלת שביה מעליה וישבה בביתך ובכתה את אביה ואת אמה ירח ימים ואחר כן תבוא אליה ובעלתה והיתה לך לאשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 14, 'והיה אם לא חפצת בה ושלחתה לנפשה ומכר לא תמכרנה בכסף לא תתעמר בה תחת אשר עניתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 15, 'כי תהיינה לאיש שתי נשים אחת אהובה ואחת שנואה וילדו לו בנים האהובה והשנואה והיה הבן הבכור לשנואה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 16, 'והיה ביום הנחילו את בניו את אשר יהיה לו לא יוכל לבכר את בן האהובה על פני בן השנואה הבכור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 17, 'כי את הבכור בן השנואה יכיר לתת לו פי שנים בכל אשר ימצא לו כי הוא ראשית אונו ולו משפט הבכרה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 18, 'כי יהיה לאיש בן סורר ומורה איננו שמע בקול אביו ובקול אמו ויסרו אתו ולא ישמע אליהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 19, 'ותפשו בו אביו ואמו והוציאו אתו אל זקני עירו אל שער מקומו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 20, 'ואמרו אל אנשי עירו בננו זה סורר ומורה איננו שמע בקולנו זולל וסובא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 21, 'ורגמהו כל אנשי עירו באבנים ומת ובערת הרע מקרבך וכל ישראל ישמעו וייראו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 22, 'כי יהיה באיש חטא משפט מות והומת ותלית אתו על העץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 21, 23, 'לא תלין נבלתו על העץ כי קבר תקברנו ביום ההוא כי קללת אלהים תלאי ולא תטמא את אדמתך אשר יהוה אלהיך נתן לך נחלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 1, 'לא תראה את שור אחיך או את שיו או את כל בהמתו נדחים והתעלמת מהם השב תשיבם לאחיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 2, 'ואם לא קרוב אחיך אליך ולא ידעתו ואספתו אל תוך ביתך והיה עמך עד דרש אחיך אתו מעמך והשבתו לו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 3, 'כן תעשה לחמרו וכן תעשה לשמלתו וכן תעשה לכל אבדת אחיך אשר תאבד ממנו ומצאתה לא תוכל להתעלם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 4, 'לא תראה את חמור אחיך או את שורו או את כל בהמתו נפלים בדרך והתעלמת מהם הקם תקים עמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 5, 'לא יהיה כלי גבר על אשה ולא ילבש גבר שמלת אשה כי הועבת יהוה אלהיך כל עשה אלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 6, 'כי יקרא קן צפור לפניך בדרך בכל עץ או על הארץ אפרחים או בצים והאם רבצת על האפרחים או על הבצים לא תקח האם על הבנים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 7, 'שלח תשלח את האם ואת הבנים תקח לך למען ייטב לך והארכת ימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 8, 'כי תבנה בית חדש ועשית מעקה לגגך ולא תשים דמים בביתך כי יפל הנפל ממנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 9, 'לא תזרע כרמך כלאים פן תקדיש המלאה הזרע אשר תזרע ותבואת הכרם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 10, 'לא תחריש בשור ובחמור יחדו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 11, 'לא תלבש שעטניז צמר ופשתים יחדו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 12, 'גדלים תעשה לך על ארבע כנפות כסותך אשר תכסה בה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 13, 'כי יקח איש אשה ובא אליה ושנאה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 14, 'ושם לה עלילת דברים והוציא עליה שם רע ואמר את האשה הזאת לקחתי ואקרבה אליה ולא מצאתי לה בתולים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 15, 'ולקח אבי הנערה ואמה והוציאו את בתולי הנערה אל זקני העיר השערה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 16, 'ואמר אבי הנערה אל הזקנים את בתי נתתי לאיש הזה לאשה וישנאה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 17, 'והנה הוא לה שם עלילת דברים לאמר לא מצאתי לבתך בתולים ואלה בתולי בתי ופרש השמלה לפני זקני העיר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 18, 'ולקחו זקני העיר ההיא את האיש ההוא ויסרו אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 19, 'וענשו אתו מאה כסף ונתנו לאבי הנערה כי הוציא שם רע על בתולת ישראל ולו תהיה לאשה לא יוכל שלחה כל ימיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 20, 'ואם אמת היה הדבר . לא נמצאו בתולים לנערה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 21, 'והוציאו את הנערה אל פתח בית אביה וסקלוה אנשי עירה באבנים ומתה כי עשתה נבלה בישראל להזנות את בית אביה ובערת הרע מקרבך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 22, 'כי ימצא איש שכב עם אשה בעלת בעל ומתו גם שניהם האיש השכב עם האשה והאשה ובערת הרע מישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 23, 'כי יהיה נערה בתולה מארשה לאיש ומצאה איש בעיר ושכב עמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 24, 'והוצאתם את שניהם אל שער העיר ההיא וסקלתם אתם באבנים ומתו את הנערה על דבר אשר לא צעקה בעיר ואת האיש על דבר אשר ענה את אשת רעהו ובערת הרע מקרבך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 25, 'ואם בשדה ימצא האיש את הנערה המארשה והחזיק בה האיש ושכב עמה ומת האיש אשר שכב עמה לבדו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 26, 'ולנערה לא תעשו דבר אין לנערה חטא מות כי כאשר יקום איש על רעהו ורצחו נפש כן הדבר הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 27, 'כי בשדה מצאה צעקה הנערה המארשה ואין מושיע לה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 28, 'כי ימצא איש נערה בתולה אשר לא ארשה ותפשה ושכב עמה ונמצאו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 22, 29, 'ונתן האיש השכב עמה לאבי הנערה חמשים כסף ולו תהיה לאשה תחת אשר ענה לא יוכל שלחה כל ימיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 1, 'לא יקח איש את אשת אביו ולא יגלה כנף אביו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 2, 'לא יבוא פצע דכה וכרות שפכת בקהל יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 3, 'לא יבוא ממזר בקהל יהוה גם דור עשירי לא יבוא לו בקהל יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 4, 'לא יבוא עמוני ומואבי בקהל יהוה גם דור עשירי לא יבוא להם בקהל יהוה עד עולם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 5, 'על דבר אשר לא קדמו אתכם בלחם ובמים בדרך בצאתכם ממצרים ואשר שכר עליך את בלעם בן בעור פתרה ארם נהרים לקללך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 6, 'ולא אבה יהוה אלהיך לשמע אל בלעם ויהפך יהוה אלהיך לך את הקללה לברכה כי אהבך יהוה אלהיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 7, 'לא תדרש שלמם וטובתם כל ימיך לעלם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 8, 'לא תתעב אדמי כי אחיך הוא ולא תתעב מצרי כי גר היית בארצו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 9, 'בנים אשר יולדו להם דור שלישי יבוא להם בקהל יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 10, 'כי תצא מחנה על איביך ונשמרת מכל דבר רע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 11, 'כי יהיה בך איש אשר לא יהיה טהור מקרי לילה ויצא . מחוץ למחנה לא יבוא אל תוך המחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 12, '. כי אם רחץ בשרו במים ובא השמש ואחרי כן יבוא אל . המחנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 13, 'ויד יהיה לך מחוץ למחנה ויצאת שמה החוצה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 14, 'ויתד תהיה לך על אזנך והיה בשבתך החוצה וחפרת בה ושבת וכסית את צאתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 15, 'כי יהוה אלהיך מתהלך בקרב מחנך להצילך ולתת איביך לפניך והיה מחנך קדוש ולא יראה בך ערות דבר ושב מאחריך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 16, 'לא תסגיר עבד אל אדניו אשר ינצל אליך מעם אדניו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 17, 'עמך ישב בקרבך במקום אשר יבחר באחת שעריך בטוב לו לא תוננו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 18, 'לא תחיה קדשה מבנות ישראל ולא יחיה קדש מבני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 19, 'לא תביא אתנן זונה ומחיר כלב בית יהוה אלהיך לכל נדר כי תועבת יהוה אלהיך גם שניהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 20, 'לא תשיך לאחיך נשך כסף נשך אכל נשך כל דבר אשר ישך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 21, 'לנכרי תשיך ולאחיך לא תשיך למען יברכך יהוה אלהיך בכל משלח ידיך על הארץ אשר אתה בא שמה לרשתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 22, 'כי תדר נדר ליהוה אלהיך לא תאחר לשלמו כי דרש ידרשנו יהוה אלהיך מעמך והיה בך חטא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 23, 'וכי תחדל לנדר לא יהיה בך הטא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 24, 'מוצא שפתיך תשמר ועשית כאשר נדרת ליהוה . נדבה אשר דברת בפיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 25, 'כי תבוא בכרם רעך ואכלת ענבים כנפשך שבעך ואל כליך לא תתן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 23, 26, 'כי תבוא בקמת רעך וקטפת מלילת בידך וחרמש לא תניף על קמת רעך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 24, 1, 'כי יקח איש אשה ובא אליה ובעלה והיה אם לא תמצא חן בעיניו כי מצא בה ערות דבר וכתב לה ספר כריתת ונתן בידה ושלחה מביתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 24, 2, 'ויצאה מביתו והלכה והיתה לאיש אחר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 24, 3, 'ושנאה האיש האחרון וכתב לה ספר כריתת ונתן בידה ושלחה מביתו או כי ימות בעלה האחרון אשר לקחה לו לאשה . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 24, 4, 'לא יוכל בעלה הראישון אשר שלחה לשוב לקחתה להיות לו לאשה אחרי אשר הטמאה כי תועבה היא לפני יהוה ולא תחטיאו את הארץ אשר יהוה אלהיך נתן לך נחלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 24, 5, 'כי יקח איש אשה חדשה לא יצא בצבא ולא יעבר עליו לכל דבר נקיא יהיה לביתו שנה אחת ושמח את אשתו אשר לקח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 24, 6, 'לא יחבל רחים ורכב כי נפש הוא חבל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 24, 7, 'כי ימצא איש גנב נפש מאחיו מבני ישראל והתעמר בו ומכרו ומת הגנב ההוא ובערת הרע מקרבך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 24, 8, 'השמר בנגע הצרעת לשמר מאד לעשות ככל התורה אשר יורו אתכם הכהנים הלוים כאשר צויתם תשמרו לעשות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 24, 9, 'זכר את אשר עשה יהוה אלהיך למרים בדרך בצאתכם ממצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 24, 10, 'כי תשא ברעך משאת מאומה לא תבוא אל ביתו לעבט עבוטו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 24, 11, 'בחוץ תעמד והאיש אשר אתה נשא בו הוא יוצא אליך את העבוט החוצה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 24, 12, 'ואם איש עני הוא לא תשכב בעבוטו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 24, 13, 'השב תשיב לו את העבוט כבוא השמש ושכב בשלמתו וברכך ולך תהיה צדקה לפני יהוה אלהיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 24, 14, 'לא תעשק שכיר עני ואביון מאחיך או מגרך אשר בארצך בשעריך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 24, 15, 'ביומו תתן שכרו ולא תבוא עליו השמש כי עני הוא ואליו הוא נשא את נפשו ולא יקרא עליך אל יהוה והיה בך חטא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 24, 16, 'לא יומתו אבות על בנים ובנים לא יומתו על האבות איש בחטאו יומת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 24, 17, 'לא תטה משפט גר יתום ולא תחבל בגד אלמנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 24, 18, 'וזכרת כי עבד היית במצרים ויפדך יהוה אלהיך . על כן אנכי מצוך לעשות את הדבר הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 24, 19, 'כי תקצר קצירך בשדך ושכחת עמר בשדה לא תשוב לקחתו לגר ליתום ולאלמנה יהיה למען יברכך יהוה אלהיך בכל מעשה ידיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 24, 20, 'כי תחבט זיתך לא תפאר אחריך לגר ליתום ולאלמנה יהיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 24, 21, 'כי תבצר כרמך לא תעלל אחריך לגר ליתום ולאלמנה יהיה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 24, 22, 'וזכרת כי עבד היית בארץ מצרים על כן אנכי מצוך . את הדבר הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 25, 1, 'כי יהיה ריב בין אנשים ונגשו אל המשפט ושפטום והצדיקו את הצדיק והרשיעו את הרשע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 25, 2, 'והיה אם בן הכות הרשע והפילו השפט והכהו לפניו כדי רשעתו במספר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 25, 3, 'ארבעים יכנו לא יסף פן יסף להכתו על אלה מכה רבה ונקלה אחיך לעיניך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 25, 4, 'לא תחסם שור בדישו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 25, 5, 'כי ישבו אחים יחדו ומת אחד מהם ובן אין לו לא תהיה אשת המת החוצה לאיש זר יבמה יבוא אליה ולקחה לו לאשה ויבמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 25, 6, 'והיה הבן הבכור אשר תלד יקום על שם אחיו המת ולא ימחה שמו מישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 25, 7, 'ואם לא יחפץ האיש לקחת את יבמתו ועלתה יבמתו השערה אל הזקנים ואמרה מאן יבמי להקים לאחיו שם בישראל לא אבה יבמי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 25, 8, 'וקראו לו זקני עירו ודברו אליו ועמד ואמר לא חפצתי לקחתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 25, 9, 'ונגשה יבמתו אליו לעיני הזקנים וחלצה נעלו מעל רגלו וירקה בפניו וענתה ואמרה ככה יעשה לאיש אשר לא יבנה את בית אחיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 25, 10, 'ונקרא שמו בישראל בית חלוץ הנעל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 25, 11, 'כי ינצו אנשים יחדו איש ואחיו וקרבה אשת אחד להציל את אישה מיד מכהו ושלחה את ידה והחזיקה בבשרו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 25, 12, 'וקצת את כפה לא תחוס עינך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 25, 13, 'לא יהיה לך בכיסך אבן ואבן גדלה וקטנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 25, 14, 'לא יהיה לך בביתך איפה ואיפה גדלה וקטנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 25, 15, 'אבן שלמה וצדק יהיה לך איפה שלמה וצדק יהיה לך למען יארכון ימיך על האדמה אשר יהוה אלהיך נתן לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 25, 16, 'כי תועבת יהוה אלהיך כל עשה אלה כל עשה עול ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 25, 17, 'זכר את אשר עשה לך עמלק בדרך בצאתכם ממצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 25, 18, 'אשר קרך בדרך ויזנב בך כל הנחשלים אחריך ואתה עיף ויגע ולא ירא אלהים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 25, 19, 'והיה בהניח יהוה אלהיך לך מכל איביך מסביב בארץ אשר יהוה אלהיך נתן לך נחלה לרשתה תמחה את זכר עמלק מתחת השמים לא תשכח ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 26, 1, 'והיה כי תבוא אל הארץ אשר יהוה אלהיך נתן לך נחלה וירשתה וישבת בה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 26, 2, 'ולקחת מראשית . פרי האדמה אשר תביא מארצך אשר יהוה אלהיך נתן לך ושמת בטנא והלכת אל המקום אשר בחר יהוה אלהיך לשכן את שמו שם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 26, 3, 'ובאת אל הכהן אשר יהיה בימים ההם ואמרת אליו הגדתי היום ליהוה . אלהיך כי באתי אל הארץ אשר נשבע יהוה לאבתינו לתת לנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 26, 4, 'ולקח הכהן . הטנא מידך והניחו לפני מזבח יהוה אלהיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 26, 5, 'וענית ואמרת לפני יהוה אלהיך ארמי אבד אבי וירד מצרימה ויגר שם במתי מעט ויהי שם לגוי גדול עצום ורב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 26, 6, 'ויריעו אתנו המצרים ויעננו ויתנו עלינו עבדה קשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 26, 7, 'ונצעק אל יהוה אלהי אבתינו וישמע יהוה את קולנו וירא את ענינו ואת עמלנו ואת לחצנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 26, 8, 'ויוציאנו יהוה ממצרים ביד חזקה ובזרוע נטויה ובמראה גדול ובאתות ובמפתים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 26, 9, 'ויביאנו אל המקום הזה ויתן לנו את הארץ הזאת ארץ זבת חלב ודבש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 26, 10, 'ועתה הנה הבאתי את ראשית פרי האדמה אשר נתתה לי יהוה והנחתו לפני יהוה אלהיך והשתחוית לפני יהוה אלהיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 26, 11, 'ושמחת בכל הטוב אשר נתן לך יהוה אלהיך ולביתך אתה והלוי והגר אשר בקרבך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 26, 12, 'כי תכלה לעשר את כל מעשר תבואתך בשנה השלישית שנת המעשר ונתתו ללוי לגר ליתום ולאלמנה ואכלו בשעריך ושבעו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 26, 13, 'ואמרת לפני יהוה אלהיך בערתי הקדש מן הבית וגם נתתיו ללוי לגר ליתום ולאלמנה ככל מצותיך אשר צויתני לא עברתי ממצותיך ולא שכחתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 26, 14, 'לא אכלתי באני ממנו ולא בערתי ממנו בטמא ולא נתתי ממנו למת שמעתי בקול יהוה אלהי עשיתי ככל אשר צויתני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 26, 15, 'השקף ממעון קדשך מן השמים וברך את עמך את ישראל ואת האדמה אשר נתתה לנו כאשר נשבעת לאבתינו ארץ זבת חלב ודבש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 26, 16, 'היום הזה יהוה אלהיך מצוך לעשות את החקים האלה ואת המשפטים ושמרת ועשית אתם בכל לבבך ובכל נפשך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 26, 17, 'את יהוה האמרת היום להיות לך לאלהים וללכת בדרכיו ולשמר חקיו ומצותיו . ולשמע בקולו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 26, 18, 'ויהוה האמרך היום להיות לו לעם סגלה כאשר דבר לך ולשמר . כל מצותיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 26, 19, 'ולתתך עליון על כל הגוים אשר עשה לתהלה לשם ולתפארת ולהיותך עם קדש ליהוה אלהיך כאשר דבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 1, 'ויצו משה וזקני ישראל את העם לאמר שמרו את כל המצוה . אשר אנכי מצוה אתכם היום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 2, 'והיה ביום אשר תעברו את הירדן אל הארץ אשר יהוה אלהיך נתן לך והקמת לך אבנים גדלות ושדת אתם בשיד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 3, 'וכתבת עליהן את כל דברי התורה הזאת בעברך למען אשר תבוא אל הארץ אשר יהוה אלהיך נתן לך ארץ זבת חלב ודבש כאשר דבר יהוה אלהי אבתיך לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 4, 'והיה בעברכם את הירדן תקימו את האבנים האלה אשר אנכי מצוה אתכם היום בהר גריזים ושדת אתם בשיד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 5, 'ובנית שם מזבח ליהוה אלהיך מזבח אבנים לא תניף עליהם ברזל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 6, 'אבנים שלמות תבנה את מזבח יהוה אלהיך והעלית עליו עלות ליהוה אלהיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 7, 'וזבחת שלמים ואכלת שם ושמחת לפני יהוה אלהיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 8, 'וכתבת על האבנים את כל דברי התורה הזאת באר הייטב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 9, 'וידבר משה והכהנים הלוים אל כל ישראל לאמר הסכת ושמע ישראל היום הזה נהית לעם קדש ליהוה אלהיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 10, 'ושמעת בקול יהוה אלהיך ועשית את מצותיו ואת חקתיו אשר אנכי מצוך היום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 11, 'ויצו משה את העם ביום ההוא לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 12, 'אלה יעמדו לברך את העם על הר גריזים בעברכם את הירדן שמעון ולוי יהודה ויששכר יוסף ובנימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 13, 'ואלה יעמדו על הקללה בהר עיבל ראובן גד ואשר זבולן דן ונפתלי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 14, 'וענו הלוים ואמרו אל כל איש ישראל קול רם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 15, 'ארור האיש אשר יעשה פסל ומסכה תועבת יהוה מעשה ידי חרש ושם בסתר וענו כל העם ואמרו אמן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 16, 'ארור מקלה אביו ואמו ואמרו כל העם אמן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 17, 'ארור מסיג גבול רעהו ואמרו כל העם אמן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 18, 'ארור משגה עור בדרך ואמרו כל העם אמן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 19, 'ארור מטה משפט גר יתום ואלמנה ואמרו כל העם אמן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 20, 'ארור שכב עם אשת אביו כי גלה כנף אביו ואמרו כל העם אמן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 21, 'ארור שכב עם כל בהמה ואמרו כל העם אמן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 22, 'ארור שכב עם אחתו בת אביו או בת אמו ואמרו כל העם אמן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 23, 'ארור שכב עם חתנתו ואמרו כל העם אמן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 24, 'ארור מכה רעהו בסתר ואמרו כל העם אמן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 25, 'ארור לקח שחד להכות נפש דם נקיא ואמרו כל העם אמן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 27, 26, 'ארור אשר לא יקים את כל דברי התורה הזאת לעשותם . ואמרו כל העם אמן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 1, 'והיה אם שמע תשמע בקול יהוה אלהיך לשמר ולעשות את כל מצותיו אשר אנכי מצוך היום ונתנך יהוה אלהיך עליון על כל גוי הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 2, 'ובאו עליך כל הברכות האלה והשיגוך כי תשמע בקול יהוה אלהיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 3, 'ברוך אתה בעיר וברוך אתה בשדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 4, 'ברוך פרי בטנך ופרי אדמתך ופרי בהמתך שגר אלפיך ועשתרות צאנך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 5, 'ברוך טנאך ומשארתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 6, 'ברוך אתה בבאך וברוך אתה בצאתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 7, 'יתן יהוה את איביך הקמים עליך נגפים לפניך בדרך אחד יצאו אליך ובשבעה דרכים ינוסו לפניך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 8, 'יצוה יהוה אתך את הברכה באסימך ובכל משלח ידיך וברכך בארץ אשר יהוה אלהיך נתן לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 9, 'יקימך יהוה לו לעם קדש כאשר נשבע לך כי תשמר את מצות יהוה אלהיך והלכת בדרכיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 10, 'וראו כל עמי הארץ כי שם יהוה נקרא עליך וייראו ממך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 11, 'והותירך יהוה לטוב בפרי בטנך ובפרי בהמתך ובפרי אדמתך על האדמה אשר נשבע יהוה לאבתיך לתת לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 12, 'יפתח יהוה לך את אוצרו הטוב את השמים לתת את מטר ארצך בעתו ולברך את כל מעשה ידיך והלוית גוים רבים ואתה לא תלוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 13, 'ונתנך יהוה לראש ולא לזנב והיית רק למעלה ולא תהיה למטה כי תשמע אל מצות יהוה אלהיך אשר אנכי מצוך היום לשמר ולעשות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 14, 'ולא תסור מכל הדברים אשר אנכי מצוך . היום ימין ושמאל ללכת אחרי אלהים אחרים לעבדם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 15, 'והיה אם לא תשמע בקול יהוה אלהיך לשמר . . . מצותיו וחקתיו אשר אנכי מצוך היום ובאו עליך כל הקללות האלה והשיגוך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 16, 'ארור אתה בעיר וארור אתה בשדה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 17, 'ארור טנאך ומשארתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 18, 'ארור פרי בטנך ופרי אדמתך ופרי בהמתך שגר אלפיך ועשתרות צאנך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 19, 'ארור אתה בבאך וארור אתה בצאתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 20, 'ישלח יהוה בך את המרה ואת המהמה ואת המגערת בכל משלח ידיך אשר תעשה עד השמידך ועד אבידך מהר מפני רע מעלליך אשר עזבתני ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 21, 'ידבק יהוה בך את הדבר עד כלותו אתך מעל האדמה אשר אתה בא שמה לרשתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 22, 'יכך יהוה בשחפת ובקדחת ובדלקת ובחרחר ובחרב ובשדפון ובירקון ירדפוך עד אבידוך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 23, 'והיו שמיך אשר על ראשך נחשת והארץ אשר תחתיך ברזל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 24, 'יתן יהוה את מטר ארצך אבק ועפר מן השמים ירד עליך עד השמידך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 25, 'יתנך יהוה נגף לפני איביך בדרך אחד תצא אליו ובשבעה דרכים תנוס לפניו והיית לזועה לכל ממלכת הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 26, 'והיתה נבלתך למאכל . לעוף השמים ולבהמת הארץ ואין מחריד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 27, 'יכך יהוה בשחין מצרים ובעפלים ובגרב ובחרס אשר לא תוכל להרפא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 28, 'יכך יהוה בשגעון ובעורון ובתמהון לבב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 29, 'והיית ממשש בצהרים כאשר ימשש העור באפלה ולא תצליח . דרכך והיית אק עשוק וגזול כל הימים ואין מושיע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 30, 'אשה תארש ואיש אחר ישכב עמה בית תבנה ולא תשב בו כרם תטע ולא תחללנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 31, 'שורך טבוח לעיניך ולא תאכל ממנו חמרך גזול מלפניך ולא ישוב לך צאנך נתנות לאיביך ואין לך מושיע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 32, 'בניך ובנתיך נתנים לעם אחר ועיניך ראות וכלות אליהם כל היום ואין לאל ידך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 33, 'פרי אדמתך וכל יגעך יאכל עם אשר לא ידעת והיית רק עשוק ורצוץ כל הימים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 34, 'והיית משגע ממראה עיניך אשר תראה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 35, 'יכך יהוה בשחין רע על הברכים ועל השקים אשר לא תוכל להרפא מכף רגלך ועד קדקדך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 36, 'יולך יהוה אתך ואת מלכך אשר תקים עליך אל גוי אשר לא ידעת אתה ואבתיך ועבדת שם אלהים אחרים עץ ואבן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 37, 'והיית לשם ולמשל ולשנינה בכל העמים אשר ינהגך יהוה שמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 38, 'זרע רב תוצא השדה ומעט תאסף כי יחסלנו הארבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 39, 'כרמים תטע ועבדת ויין לא תשתה ולא תאגר כי תאכלנו התולעת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 40, 'זיתים יהיו לך בכל גבולך ושמן לא תסך כי ישל זיתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 41, 'בנים ובנות תוליד ולא יהיו לך כי ילכו בשבי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 42, 'כל עצך ופרי אדמתך יוריש הצלצל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 43, 'הגר אשר בקרבך יעלה עליך מעלה מעלה ואתה תרד מטה מטה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 44, 'הוא ילוך ואתה לא תלונו הוא יהיה לראש ואתה תהיה לזנב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 45, 'ובאו עליך כל הקללות האלה ורדפוך והשיגוך עד השמידוך כי לא שמעת בקול יהוה אלהיך לשמר מצותיו וחקתיו אשר צוך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 46, 'והיו בך לאות ולמופת ובזרעך עד עולם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 47, 'תחת אשר לא עבדת את יהוה אלהיך בשמחה ובטוב לבב מרב כל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 48, 'ועבדת את איביך אשר ישלחנו יהוה בך ברעב ובצמא ובערום ובחסר כל ונתן על ברזל על צוארך עד השמידו אתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 49, 'ישא יהוה עליך גוי מרחק מקצה הארץ כאשר יראה הנשר גוי אשר לא תשמע לשנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 50, 'גוי עז פנים אשר לא ישא פנים לזקן ונער לא יחן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 51, 'ואכל פרי בהמתך ופרי אדמתך עד השמידך אשר לא ישאיר לך דגן ותירש ויצהר שגר אלפיך ועשתרות צאנך עד האבידו אתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 52, 'והצר לך בכל שעריך עד רדת חומתך הגבהות והבצרות אשר אתה בטח בהן בכל ארצך והצר לך בכל שעריך בכל ארצך אשר נתן יהוה אלהיך לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 53, 'ואכלת פרי בטנך בשר בניך ובנתיך אשר נתן לך . . במצור ובמצוק אשר יציקו . איביך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 54, 'האיש הרך בך והענג מאד תרע עינו באחיו ובאשת חיקו וביתר בניו אשר יותיר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 55, 'מתת לאחד מהם מבשר בניו אשר יאכל מבלי השאיר לו כל במצור ובמצוק אשר יציקו לך איביך בכל שעריך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 56, 'הרכה בך והענגה אשר לא נסתה כף רגלה הצינה על הארץ מהתענג ומרך תרע עינה באיש חיקה בבנה ובבתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 57, 'ובשליתה היצאת מבין רגליה ובבניה אשר תלד כי תאכלם בחסר כל בסתר במצור ובמצוק אשר יציקו לך איביך בשעריך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 58, 'אם לא תשמר לעשות את כל דברי התורה הזאת הכתובים בספר הזה ליראה את השם הנכבד והנורא הזה את יהוה אלהיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 59, 'והפלא יהוה אלהיך את מכותך ואת מכות זרעך מכות גדלות ונאמנות וחלים רעים ונאמנים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 60, 'והשיב בך את כל מדוי מצרים אשר יגרת מפניהם ודבק בך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 61, 'גם כל חלי וכל מכה אשר לא כתוב בספר התורה הזה יעלם יהוה עליך עד השמידך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 62, 'ונשארתם במתי מעט תחת אשר הייתם ככוכבי השמים לרב כי לא שמעתם בקול יהיה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 63, 'והיה כאשר שש יהוה עליכם להטיב אתכם ולהרבות אתכם כן ישש יהוה עליכם להאביד אתכם ולהשמיד אתכם ונסחתם מעל האדמה אשר אתה בא שמה לרשתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 64, 'והפיצך יהוה בכל העמים מקצה הארץ ועד קצה הארץ ועבדת שם אלהים אחרים אשר לא ידעת אתה ואבתיך עץ ואבן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 65, 'ובגוים ההם לא תרגיע ולא יהיה מנוח לכף רגלך ונתן יהוה לך שם לב רגז כליון עינים ודיבון נפש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 66, 'והיו חייך תלאים לך מנגד ופחדת לילה ויומם ולא תאמן בחייך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 67, 'בבקר תאמר מי יתן ערב ובערב תאמר מי יתן בקר מפחד לבבך אשר תפחד וממראה עיניך אשר תראה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 68, 'והשיבך יהוה מצרימה באניות בדרך אשר אמרתי לכם לא תוסיפון עוד לראותה והתמכרתם שם לאיביכם לעבדים ולשפחות ואין קנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 28, 69, 'ואלה דברי הברית אשר צוה יהוה את משה לכרת את בני ישראל בארץ מואב מלבד הברית אשר כרת אתם בחורב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 1, 'ויקרא משה אל כל ישראל ויאמר אליהם אתם ראיתם את כל אשר עשה יהוה לעיניכם בארץ מצרים לפרעה ולכל עבדיו ולכל ארצו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 2, 'המסות הגדלות אשר ראו עיניך האתות והמפתים הגדלים ההם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 3, 'ולא נתן יהוה לכם לב לדעת ועינים לראות ואזנים לשמע עד היום הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 4, 'ואולך אתכם ארבעים שנה במדבר לא בלו שמלתיכם מעליכם ונעליכם לא בלו מעל רגליכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 5, 'לחם לא אכלתם ויין ושכר לא שתיתם למען תדעו כי אני יהוה אלהיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 6, 'ותבאו אל המקום הזה ויצא סיחון מלך חשבון ועוג מלך הבשן לקראתנו למלחמה ונכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 7, 'ונקח את ארצם ונתנה נחלה לראובני ולגדי ולחצי שבט המנשי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 8, 'ושמרתם את דברי הברית הזאת ועשיתם אתם למען תשכלו את כל אשר תעשון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 9, 'אתם נצבים היום כלכם לפני יהוה אלהיכם ראשיכם שבטיכם זקניכם ושטריכם כל איש ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 10, 'טפכם ונשיכם וגרך אשר בקרב מחנך מחטב עציך ועד שאב מימיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 11, 'לעברך בברית יהוה אלהיך ובאלתו אשר יהוה אלהיך כרת עמך היום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 12, 'למען הקים אתך היום לו לעם והוא יהיה לך לאלהים כאשר דבר לך וכאשר נשבע לאבתיך לאברהם ליצחק וליעקב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 13, 'ולא אתכם לבדכם אנכי כרת את הברית הזאת ואת האלה הזאת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 14, 'כי את אשר ישנו פה עמנו עמד היום לפני יהוה אלהינו ואת אשר איננו פה עמנו היום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 15, 'כי אתם ידעתם את אשר ישבנו בארץ מצרים ואת אשר עברנו בקרב הגוים אשר עברתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 16, 'ותראו את שקציהם ואת גלליהם עץ ואבן כסף וזהב אשר עמהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 17, 'פן יש בכם איש או אשה או משפחה או שבט אשר לבבו פנה היום מעם יהוה אלהינו ללכת לעבד את אלהי הגוים ההם פן יש בכם שרש פרה ראש ולענה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 18, 'והיה בשמעו את דברי האלה הזאת והתברך בלבבו לאמר שלום יהיה לי כי בשרירת לבי אלך למען ספות הרוה את הצמאה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 19, 'לא יאבה יהוה לסלח לו כי אז יחר אף יהוה וקנאתו באיש ההוא ורבצו בו כל האלה הכתובה בספר הזה ומחה יהוה את שמו מתחת השמים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 20, 'והבדילו יהוה לרעה מכל שבטי ישראל ככל אלות הברית הכתובים בספר התורה הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 21, 'ואמר הדור האחרון בניכם אשר יקומו מאחריכם והנכרי אשר יבוא מארץ רחוקה וראה את מכות הארץ ההיא ואת תחלאיה אשר חלה יהוה בה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 22, 'גפרית ומלח שרפה כל ארצה לא תזריע ולא תצמיח ולא יעלה בה כל עשב כמהפכת סדם ועמרה אדמה וצבאים אשר הפך יהוה באפו ובחמתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 23, 'ואמרו כל הגוים על מה עשה יהוה ככה לארץ הזאת ומה חרי האף הגדול הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 24, 'ואמרו על אשר עזבו את ברית יהוה אלהי אבתם אשר כרת עמם בהוצאי אתם מארץ מצרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 25, 'וילכו ויעבדו אלהים אחרים וישתחוו להם אלהים אשר לא ידעום ולא חלק להם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 26, 'ויחר אף יהוה בארץ ההיא להביא עליה את כל הקללה הכתובה בספר הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 27, 'ויתשם יהוה מעל אדמתם באף ובחמה ובקצף גדול וישלכם אל ארץ אחרת כיום הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 29, 28, 'הנסתרות ליהוה אלהינו והנגלאות לנו ולבנינו עד עולם לעשות את כל דברי התורה הזאת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 30, 1, 'והיה כי יבאו עליך כל הדברים האלה הברכה והקללה אשר נתתי לפניך והשבת אל לבבך בכל הגוים אשר ידיחך יהוה אלהיך שמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 30, 2, 'ושבת עד יהוה אלהיך ושמעת בקולו ככל אשר אנכי מצוך היום אתה ובניך בכל לבבך ובכל נפשך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 30, 3, 'ושב יהוה אלהיך את שובתך ורחמך ושב וקבצך מכל העמים אשר הפיצך יהוה אלהיך שמה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 30, 4, 'אם יהוה נדחך בקצה השמים משם יקבצך יהוה אלהיך ומשם יקחך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 30, 5, 'והביאך יהוה אלהיך אל הארץ אשר ירשו אבתיך וירשתה . והרבך מאבתיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 30, 6, 'ומל יהוה אלהיך את לבבך ואת לבב זרעך לאהבה את יהוה אלהיך בכל לבבך ובכל נפשך למען חייך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 30, 7, 'ונתן יהוה אלהיך את כל האלות האלה על איביך ועל שנאיך אשר רדפוך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 30, 8, 'ואתה תשוב ושמעת בקול יהוה אלהיך ועשית את כל מצותיו אשר אנכי מצוך היום ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 30, 9, 'והותירך יהוה אלהיך בכל מעשה ידיך בפרי בטנך ובפרי אדמתך ובפרי בהמתך לטובה כי ישוב יהוה לשש עליך לטוב כאשר שש על אבתיך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 30, 10, 'כי תשמע בקול יהוה אלהיך לשמר מצותיו וחקתיו הכתובה בספר התורה הזה כי השוב אל יהוה אלהיך בכל לבבך ובכל נפשך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 30, 11, 'כי המצוה הזאת אשר אנכי מצוך היום לא נפלאת היא ממך ולא רחקה היא . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 30, 12, 'לא בשמים היא לאמר מי יעלה לנו השמים ויקחה לנו וישמענו אתה ונעשנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 30, 13, 'ולא מעבר לים היא לאמר מי יעבר לנו אל עבר הים ויקחה לנו וישמענו אתה ונעשנה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 30, 14, 'כי קרוב אליך הדבר מאד בפיך ובלבבך . לעשתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 30, 15, 'ראה נתתי לפניך . את החיים ואת הטוב ואת המות ואת הרע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 30, 16, 'אשר אנכי מצוך היום לאהבה את יהוה אלהיך ללכת בדרכיו ולשמר חקיו ומצותיו ומשפטיו וחיית ורבית וברכך יהוה אלהיך בארץ אשר אתה בא שמה לרשתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 30, 17, 'ואם יפנה לבבך ולא תשמע ונדחת והשתחוית לאלהים אחרים ועבדתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 30, 18, 'הגדתי לכם היום כי אבד תאבדון לא תאריכון ימים על האדמה אשר אתם עברים את הירדן לבוא שמה לרשתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 30, 19, 'העדתי בכם היום את השמים ואת הארץ החיים והמות נתתי לפניך הברכה והקללה ובחרת בחיים למען תחיה אתה וזרעך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 30, 20, 'לאהבה את יהוה אלהיך לשמע בקולו ולדבקה בו כי הוא חייך וארך ימיך לשבת על האדמה אשר נשבע יהוה לאבתיך לאברהם ליצחק וליעקב לתת לכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 1, 'וילך משה וידבר את הדברים האלה אל כל ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 2, 'ויאמר אליהם בן מאה ועשרים שנה אנכי היום לא אוכל עוד לצאת ולבוא ויהוה אמר אלי לא תעבר את הירדן הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 3, 'יהוה אלהיך הוא העבר לפניך הוא ישמיד את הגוים האלה מלפניך וירשתם ויהושע הוא העבר לפניך כאשר דבר יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 4, 'ועשה יהוה להם כאשר עשה לסיחון ולעוג מלכי האמרי ולארצם אשר השמיד אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 5, 'ונתנם יהוה לפניכם ועשיתם להם ככל המצוה אשר צויתי אתכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 6, 'חזקו ואמצו אל תיראו ואל תערצו מפניהם כי יהוה אלהיך הוא ההלך עמך לא ירפך ולא יעזבך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 7, 'ויקרא משה ליהושע ויאמר אליו לעיני כל ישראל חזק ואמץ כי אתה תביא את העם הזה אל הארץ אשר נשבע יהוה לאבתם לתת להם ואתה תנחלנה אתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 8, 'ויהוה הוא ההלך לפניך הוא יהיה עמך לא ירפך ולא יעזבך לא תירא ולא תחת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 9, 'ויכתב משה את התורה הזאת . . ויתנה אל הכהנים בני לוי הנשאים את ארון ברית יהוה ואל כל זקני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 10, 'ויצו משה אתם . . לאמר מקץ שבע שנים במועד שנת השמטה בחג הסכות ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 11, 'בבוא כל ישראל להראות את פני יהוה אלהיך במקום אשר בחר יקרא את התורה הזאת נגד כל ישראל באזניהם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 12, 'הקהל את העם האנשים והנשים והטף וגרך אשר בשעריך למען ישמעו ולמען ילמדו וייראו את יהוה אלהיהם ושמרו לעשות את כל דברי התורה הזאת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 13, 'ובניהם אשר לא ידעו ישמעו ולמדו ליראה את יהוה אלהיהם כל הימים אשר הם חיים על האדמה אשר אתם עברים את הירדן שמה לרשתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 14, 'ויאמר יהוה אל משה הן קרבו ימיך למות קרא את יהושע והתיצבו באהל מועד ואצונו וילך משה ויהושע ויתיצבו באהל מועד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 15, 'וירא יהוה באהל בעמוד ענן ויעמד עמוד הענן על פתח האהל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 16, 'ויאמר יהוה אל משה הנך שכב עם אבתיך וקם העם הזה וזנה אחרי אלהי נכר הארץ אשר הוא בא שמה בקרבו ועזבוני והפרו את בריתי אשר כרתי אתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 17, 'וחרה אפי בו ביום ההוא ועזבתם והסתרתי פני מהם והיו לאכלה ומצאהו רעות רבות וצרות ואמר ביום ההוא הלא על כי אין . אלהי בקרבי מצאני הרעות האלה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 18, 'ואנכי הסתר אסתיר פני מהם ביום ההוא על כל הרעה אשר עשו כי פנו אל אלהים אחרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 19, 'ועתה כתבו לכם את . השירה הזאת ולמדה את בני ישראל ושימה בפיהם למען תהיה לי השירה הזאת לעד בבני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 20, 'כי אביאנו אל האדמה אשר נשבעתי לאבתיו לתת להם זבת חלב ודבש ואכל ושבע ודשן ופנה אל אלהים אחרים ועבדום ונאצוני והפרו את בריתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 21, 'והיה כי תמצאנה אתו רעות רבות וצרות וענתה השירה הזאת לפניו לעד כי לא תשכח מפי זרעו כי ידעתי את יצרו אשר הוא עשה היום בטרם אביאנו אל האדמה אשר נשבעתי לאבתיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 22, 'ויכתב משה את השירה הזאת ביום ההוא וילמדה את בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 23, 'ויצו את יהושע בן נון ויאמר חזק ואמץ כי אתה תביא את בני ישראל אל הארץ אשר נשבעתי להם ואנכי אהיה עמך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 24, 'ויהי ככלות משה לכתב את דברי התורה הזאת על ספר עד תמם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 25, 'ויצו משה את הלוים נשאי ארון ברית יהוה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 26, 'לקחו את ספר התורה הזה ושמתם אתו מצד ארון ברית יהוה אלהיכם והיה שם בך לעד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 27, 'כי אנכי ידעתי את מריך ואת ערפך הקשה הן בעודני חי עמכם היום ממרים הייתם עם יהוה ואף כי אחרי מותי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 28, 'הקהלו אלי את כל זקני שבטיכם . . ושטריכם ואדברה באזניהם את הדברים האלה ואעידה בם את השמים ואת הארץ ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 29, 'כי ידעתי אחרי מותי כי השחת תשחתון וסרתם מן הדרך אשר צויתי אתכם וקרתה אתכם הרעה באחרית הימים כי תעשו את הרע בעיני יהוה להכעיסו במעשה ידיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 31, 30, 'וידבר משה באזני כל קהל ישראל את דברי השירה הזאת עד תמם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 1, 'האזינו השמים ואדברה ותשמע הארץ אמרי פיי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 2, 'יערף כמטר לקחי ותזל כטל אמרתי כשעירים עלי דשא וכרביבים עלי עשב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 3, 'כי בשם יהוה אקרא והבו גדל לאלהינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 4, 'הצור תמים פעלו כי כל דרכיו משפט אל אמונה ואין עול צדיק וישר הוא ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 5, 'שחתו לו לא בני מום דור עקש ופתלתל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 6, 'הליהוה תגמלו זאת עם נבל ולא חכם הלוא הוא אביך קנך הוא עשך ויכוננך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 7, 'זכרו יומת עולם בינו שנת דר ודר שאל אביך ויגידך זקניך ויאמרו לך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 8, 'בהנחל עליון גוים בהפרידו בני אדם יצב גבלות עמים למספר בני . ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 9, 'כי חלק יהוה עמו יעקב חבל נחלתו ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 10, 'יאמצהו בארץ המדבר ובתהללות . ישמנהו יסבבנהו ויבננהו ויצרנהו כאישן עינו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 11, 'כנשר יער קנו ועל גוזליו ירחף יפרש כנפיו ויקחהו ישאהו על אברתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 12, 'יהוה בדד ינחנו ואין עמו אל נכר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 13, 'ירכבהו על במתי הארץ יאכלהו תנופת שדי יינקהו דבש מסלע ושמן מחלמש צור ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 14, 'חמת בקר וחלב צאן עם חמת כרים ואילים בני בשן ועתודים עם חלב כליות חטה ודם ענב תשתה חמר יאכל יעקב וישבע ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 15, 'ישמן ישרון ויבעט שמנת עבית כשית ויטש אלה עשהו וינבלו צור ישועתו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 16, 'יקנאהו בזרים ובתועבת יכעסהו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 17, 'יזבחו לשדים לא אלה אלהים לא ידעום חדשים מקרב באו לא שערום אבתיכם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 18, 'צור ילדך תשא ותשכח אל מהללך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 19, 'וירא יהוה וינאץ מכעס בניו ובנתיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 20, 'ויאמר אסתיר פני מהם ואראה מה אחריתם כי דור הפכות הם בנים לא האמן בם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 21, 'הם קנאוני בלא אל כעסוני באבליהם ואני אקניאם בלא עם בגוי נבל אכעיסם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 22, 'כי אש קדחה באפי ותוקד עד שאל תחתית תאכל ארץ ויבולה תלהט מוסדי הרים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 23, 'אספה עליהם רעות וחצי אכלה בם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 24, 'מזה רעב לחמו רשף קטף מררים ושן בהמות אשלח בם עם חמת זחלי עפר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 25, 'מחוץ תשכל חרב ומחדרים אימה גם בחור גם בתולה וינק עם איש שיבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 26, 'אמרתי אפיהם אשבית מאנוש זכרם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 27, 'לו לא כעס איבי אגור פן ינכרו צרינו פן יאמרו ידנו רמה ולא יהוה פעל כל זאת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 28, 'כי גוי אבד עצותם . ואין בהם תבונה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 29, 'ולא חכמו וישכלו זאת ויבינו לאחריתם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 30, 'איך ירדף אחד אלף ושנים יניסו רבבה אם לא . צורם מכרם ויהוה הסגירם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 31, 'כי לא כצורנו צורם ואיבינו פללים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 32, 'כי מגפן סדם גפנם ומשדמות עמרה ענביהם ענבי ראש ואשכלי מררות למו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 33, 'חמת תנינים יינם וראש פתנים אך זרי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 34, 'הלוא הוא כנוס עמדי חתום באוצרתי ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 35, 'ליום נקם ושלם לעת תמוט רגלם כי קרוב יום אידם וחש עתידת למו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 36, 'כי ידין יהוה עמו ועל עבדיו יתנחם כי יראה כי אזלת יד ואפס עצור ועזוב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 37, 'ואמרו . איה אלהימו . צור . חסיו בו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 38, 'אשר חלב זבחיהם יאכלו וישתו יין נסכם יקומו ויעזרוכם יהיו עליכם סתרה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 39, 'ראו עתה כי אני אני הוא ואין אלהים עמדי אני אמית ואחיה מחצתי ואנכי ארפא ואין מידי מציל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 40, 'כי אשא אל השמים ידי ואמרתי חי אנכי לעלם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 41, 'אם שנתי ברק חרבי ותאחז במשפט ידי אשיב נקם לצרי ולמשנאי אשלם ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 42, 'אשכיר חצי מדם וחרבי תאכל בשר מדם חלל ושביה מראש פרעת אויב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 43, 'הרנינו גוים עמו . . . . כי דם עבדיו יקם ונקם ישיב לצריו . . וכפר אדמת עמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 44, 'ויבא משה וידבר את כל דברי השירה הזאת באזני העם הוא ויהושע בן נון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 45, 'ויכל משה מדבר את . הדברים האלה אל כל ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 46, 'ויאמר אליהם שימו לבבכם על כל הדברים אשר אנכי מעיד בכם היום אשר תצום את בניכם לשמר ולעשות את כל דברי התורה הזאת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 47, 'כי לא דבר רק הוא מכם כי הוא חייכם ובדבר הזה תאריכון ימים על האדמה אשר אתם עברים את הירדן שמה לרשתה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 48, 'וידבר יהוה אל משה בעצם היום הזה לאמר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 49, 'עלה אל הר העברים הזה הר נבא אשר בארץ מואב אשר על פני יריחו וראה את ארץ כנען אשר אני נתן לבני ישראל לאחזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 50, 'ומות בהר אשר אתה עלה שמה והאסף אל עמך כאשר מת אהרן אחיך בהר ההר ויאסף אל עמו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 51, 'על אשר מעלתם בי בתוך בני ישראל במי מריבת קדש מדבר צן על אשר לא קדשתם אתי בתוך בני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 32, 52, 'כי מנגד תראה את הארץ ושמה לא תבוא אל הארץ אשר אני נתן לבני ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 1, 'וזאת הברכה אשר ברך משה איש האלהים את בני ישראל לפני מותו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 2, 'ויאמר יהוה מסיני בא וזרח משעיר למו הופיע מהר פראן ואתו מרבבות קדש מימנו אשדת למו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 3, 'אף חובב עמים וכל קדשיו בידך והם תכו לרגליך ושאו מדברתך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 4, 'תורה צוה לנו משה מורשה קהלת יעקב ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 5, 'ויהי בישרון מלך בהתאסף ראשי העם יחדו שבטי ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 6, 'יחי ראובן ואל ימות ויהי מאתו מספר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 7, 'וזאת ליהודה ויאמר שמע יהוה קול יהודה ואל עמו תביאנו ידו רב לו ועזר מצריו תהיה . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 8, 'וללוי אמר תמיך ואריך לאיש חסידך אשר נסיתהו במסה ותריבהו על מי מריבה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 9, 'האמר לאביו ולאמו לא ראיתי ואת אחיו לא הכר ואת בנו לא ידע כי שמרו אמרתך ובריתך ינצרו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 10, 'יורו משפטיך ליעקב ותורתך לישראל ישימו קטרה באפך וכליל על מזבחך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 11, 'ברך יהוה חילו ופעל ידו תרצה מחץ מתני קמיו ומשנאיו מן יקימנו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 12, 'ולבנימים אמר יד יד יהוה ישכן לבטח . חפף עליו כל היום ובין כתפתיו שכן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 13, 'וליוסף אמר מברכת יהוה ארצו ממגד שמים מטל ומתהום רבצת תחת ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 14, 'וממגד תבואת שמש וממגד גרושי ירחים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 15, 'ומראש הרי קדם וממגד גבעת עולם . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 16, 'וממגד ארץ ומלואה ורצון שכן סנה תבואתה לראש יוסף ולקדקד נזיר אחיו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 17, 'בכור שור הדר לו וקרני ראמי קרניו בהם עמים ינגח יחדו אפסי הארץ הם רבבות אפרים והם אלפי מנשה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 18, 'ולזבולן אמר שמח זבולן בצאתך ויששכר באהליך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 19, 'עמים הרי יקראו ושם יזבחו זבחי צדק כי שפע ימים יינק ושפוני טמוני חול ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 20, 'ולגד אמר ברוך מרחיב גד כלביה שכן טרף זרוע וגם קדקד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 21, 'וירא ראשית לו כי שם חלקת מחקק ספון ויתא ראשי העם צדקת יהוה עשה ומשפטיו עם ישראל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 22, 'ולדן אמר דן גר אריה ויזנק מן הבשן ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 23, 'ולנפתלי אמר נפתלי שבע ורצון ומלא ברכת יהוה ימה ודרום יירש ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 24, 'ולאשר אמר ברוך מבנים אשר ויהי רצוי אחיו וטבל בשמן רגליו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 25, 'ברזל ונחשת מנעליך וכימיך רביך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 26, 'אין כאל ישרון רכב שמים בעזרך ובגאתו שחקים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 27, 'מעונה אלהי קדם ומתחת זרועת עולם ויגרש מפניך אויב ויאמר השמד ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 28, 'וישכן ישראל בטח בדד עין יעקב על ארץ דגן ותירש ואף שמיך יערפו טל ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 33, 29, 'אשרך ישראל מי כמוך העם הנושע ביהוה מגן עזרך ואשר חרב גאתך ויכחשו איביך לך ואתה על במתם תדרך ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 34, 1, 'ויעל משה מערבת מואב אל הר נבא ראש הפסגה אשר על פני יריחו ויראהו יהוה את כל הארץ מנהר מצרים עד הנהר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 34, 2, 'הגדול נהר פרת . . . . . . . . ועד הים האחרון ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 34, 3, '. . . . . . . . . . ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 34, 4, 'ויאמר יהוה אליו זאת הארץ אשר נשבעתי לאבתיך לאברהם ליצחק וליעקב לאמר לזרעך אתננה הראתיך בעיניך ושמה לא תעבר ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 34, 5, 'וימת שם משה עבד יהוה בארץ מואב על פי יהוה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 34, 6, 'ויקבר אתו בגיא בארץ מואב מול בית פעור ולא ידע איש את קברתו עד היום הזה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 34, 7, 'ומשה בן מאה ועשרים שנה במותו לא כהתה עינו ולא נס לחה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 34, 8, 'ויבכו בני ישראל את משה בערבת מואב שלשים יום ויתמו ימי בכי אבל משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 34, 9, 'ויהושע בן נון מלא רוח חכמה כי סמך משה את ידו עליו וישמעו אליו בני ישראל ויעשו כאשר צוה יהוה את משה ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 34, 10, 'ולא קם עוד נביא בישראל כמשה אשר ידעו יהוה פנים אל פנים ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 34, 11, 'לכל האתות והמפתים אשר שלחו יהוה לעשות בארץ מצרים לפרעה ולכל עבדיו ולכל ארצו ');
INSERT INTO `SP_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (5, 34, 12, 'ולכל היד החזקה ולכל המראה הגדול אשר עשה משה לעיני כל ישראל   ');
